<script>
let kanda1 = false
let kanda2 = false
let kanda3 = false
let kanda4 = false
let kanda5 = false
let kanda6 = false
let kanda7 = false
</script>



<div class="kanda-2">
	<h4>Kāṇḍa 2</h4>
		{#if kanda2}
    <ul id="R_2.001.001">
        <li id="R_2.001.001ab">kasya cit tv atha kālasya rājā daśarathaḥ sutam</li>
        <li id="R_2.001.001cd">bharataṃ kekayīputram abravīd raghunandanaḥ</li>
    </ul>
    <ul id="R_2.001.002">
        <li id="R_2.001.002ab">ayaṃ kekayarājasya putro vasati putraka</li>
        <li id="R_2.001.002cd">tvāṃ netum āgato vīra yudhājin mātulas tava</li>
    </ul>
    <ul id="R_2.001.003">
        <li id="R_2.001.003ab">śrutvā daśarathasyaitad bharataḥ kekayīsutaḥ</li>
        <li id="R_2.001.003cd">gamanāyābhicakrāma śatrughnasahitas tadā</li>
    </ul>
    <ul id="R_2.001.004">
        <li id="R_2.001.004ab">āpṛcchya pitaraṃ śūro rāmaṃ cākliṣṭakāriṇam</li>
        <li id="R_2.001.004cd">mātṝṃś cāpi naraśreṣṭhaḥ śatrughnasahito yayau</li>
    </ul>
    <ul id="R_2.001.005">
        <li id="R_2.001.005ab">yudhājit prāpya bharataṃ saśatrughnaṃ praharṣitaḥ</li>
        <li id="R_2.001.005cd">svapuraṃ prāviśad vīraḥ pitā tasya tutoṣa ha</li>
    </ul>
    <ul id="R_2.001.006">
        <li id="R_2.001.006ab">sa tatra nyavasad bhrātrā saha satkārasatkṛtaḥ</li>
        <li id="R_2.001.006cd">mātulenāśvapatinā putrasnehena lālitaḥ</li>
    </ul>
    <ul id="R_2.001.007">
        <li id="R_2.001.007ab">tatrāpi nivasantau tau tarpyamāṇau ca kāmataḥ</li>
        <li id="R_2.001.007cd">bhrātarau smaratāṃ vīrau vṛddhaṃ daśarathaṃ nṛpam</li>
    </ul>
    <ul id="R_2.001.008">
        <li id="R_2.001.008ab">rājāpi tau mahātejāḥ sasmāra proṣitau sutau</li>
        <li id="R_2.001.008cd">ubhau bharataśatrughnau mahendravaruṇopamau</li>
    </ul>
    <ul id="R_2.001.009">
        <li id="R_2.001.009ab">sarva eva tu tasyeṣṭāś catvāraḥ puruṣarṣabhāḥ</li>
        <li id="R_2.001.009cd">svaśarīrād vinirvṛttāś catvāra iva bāhavaḥ</li>
    </ul>
    <ul id="R_2.001.010">
        <li id="R_2.001.010ab">teṣām api mahātejā rāmo ratikaraḥ pituḥ</li>
        <li id="R_2.001.010cd">svayambhūr iva bhūtānāṃ babhūva guṇavattaraḥ</li>
    </ul>
    <ul id="R_2.001.011">
        <li id="R_2.001.011ab">gate ca bharate rāmo lakṣmaṇaś ca mahābalaḥ</li>
        <li id="R_2.001.011cd">pitaraṃ devasaṃkāśaṃ pūjayām āsatus tadā</li>
    </ul>
    <ul id="R_2.001.012">
        <li id="R_2.001.012ab">pitur ājñāṃ puraskṛtya paurakāryāṇi sarvaśaḥ</li>
        <li id="R_2.001.012cd">cakāra rāmo dharmātmā priyāṇi ca hitāni ca</li>
    </ul>
    <ul id="R_2.001.013">
        <li id="R_2.001.013ab">mātṛbhyo mātṛkāryāṇi kṛtvā paramayantritaḥ</li>
        <li id="R_2.001.013cd">gurūṇāṃ gurukāryāṇi kāle kāle 'nvavaikṣata</li>
    </ul>
    <ul id="R_2.001.014">
        <li id="R_2.001.014ab">evaṃ daśarathaḥ prīto brāhmaṇā naigamās tathā</li>
        <li id="R_2.001.014cd">rāmasya śīlavṛttena sarve viṣayavāsinaḥ</li>
    </ul>
    <ul id="R_2.001.015">
        <li id="R_2.001.015ab">sa hi nityaṃ praśāntātmā mṛdupūrvaṃ ca bhāṣate</li>
        <li id="R_2.001.015cd">ucyamāno 'pi paruṣaṃ nottaraṃ pratipadyate</li>
    </ul>
    <ul id="R_2.001.016">
        <li id="R_2.001.016ab">kathaṃ cid upakāreṇa kṛtenaikena tuṣyati</li>
        <li id="R_2.001.016cd">na smaraty apakārāṇāṃ śatam apy ātmavattayā</li>
    </ul>
    <ul id="R_2.001.017">
        <li id="R_2.001.017ab">śīlavṛddhair jñānavṛddhair vayovṛddhaiś ca sajjanaiḥ</li>
        <li id="R_2.001.017cd">kathayann āsta vai nityam astrayogyāntareṣv api</li>
    </ul>
    <ul id="R_2.001.018">
        <li id="R_2.001.018ab">kalyāṇābhijanaḥ sādhur adīnaḥ satyavāg ṛjuḥ</li>
        <li id="R_2.001.018cd">vṛddhair abhivinītaś ca dvijair dharmārthadarśibhiḥ</li>
    </ul>
    <ul id="R_2.001.019">
        <li id="R_2.001.019ab">dharmārthakāmatattvajñaḥ smṛtimān pratibhāvanān</li>
        <li id="R_2.001.019cd">laukike samayācare kṛtakalpo viśāradaḥ</li>
    </ul>
    <ul id="R_2.001.020">
        <li id="R_2.001.020ab">śāstrajñaś ca kṛtajñaś ca puruṣāntarakovidaḥ</li>
        <li id="R_2.001.020cd">yaḥ pragrahānugrahayor yathānyāyaṃ vicakṣaṇaḥ</li>
    </ul>
    <ul id="R_2.001.021">
        <li id="R_2.001.021ab">āyakarmaṇy upāyajñaḥ saṃdṛṣṭavyayakarmavit</li>
        <li id="R_2.001.021cd">śraiṣṭhyaṃ śāstrasamūheṣu prāpto vyāmiśrakeṣv api</li>
    </ul>
    <ul id="R_2.001.022">
        <li id="R_2.001.022ab">arthadharmau ca saṃgṛhya sukhatantro na cālasaḥ</li>
        <li id="R_2.001.022cd">vaihārikāṇāṃ śilpānāṃ vijñātārthavibhāgavit</li>
    </ul>
    <ul id="R_2.001.023">
        <li id="R_2.001.023ab">ārohe vinaye caiva yukto vāraṇavājinām</li>
        <li id="R_2.001.023cd">dhanurvedavidāṃ śreṣṭho loke 'tirathasaṃmataḥ</li>
    </ul>
    <ul id="R_2.001.024">
        <li id="R_2.001.024ab">abhiyātā prahartā ca senānayaviśāradaḥ</li>
        <li id="R_2.001.024cd">apradhṛṣyaś ca saṃgrāme kruddhair api surāsuraiḥ</li>
    </ul>
    <ul id="R_2.001.025">
        <li id="R_2.001.025ab">anasūyo jitakrodho na dṛpto na ca matsarī</li>
        <li id="R_2.001.025cd">na cāvamantā bhūtānāṃ na ca kālavaśānugaḥ</li>
    </ul>
    <ul id="R_2.001.026">
        <li id="R_2.001.026ab">evaṃ śreṣṭhair guṇair yuktaḥ prajānāṃ pārthivātmajaḥ</li>
        <li id="R_2.001.026cd">saṃmatas triṣu lokeṣu vasudhāyāḥ kṣamāguṇaiḥ</li>
        <li id="R_2.001.026ef">buddhyā bṛhaspates tulyo vīryeṇāpi śacīpateḥ</li>
    </ul>
    <ul id="R_2.001.027">
        <li id="R_2.001.027ab">tathā sarvaprajākāntaiḥ prītisaṃjananaiḥ pituḥ</li>
        <li id="R_2.001.027cd">guṇair viruruce rāmo dīptaḥ sūrya ivāṃśubhiḥ</li>
    </ul>
    <ul id="R_2.001.028">
        <li id="R_2.001.028ab">tam evaṃvṛttasaṃpannam apradhṛṣya parākramam</li>
        <li id="R_2.001.028cd">lokapālopamaṃ nātham akāmayata medinī</li>
    </ul>
    <ul id="R_2.001.029">
        <li id="R_2.001.029ab">etais tu bahubhir yuktaṃ guṇair anupamaiḥ sutam</li>
        <li id="R_2.001.029cd">dṛṣṭvā daśaratho rājā cakre cintāṃ paraṃtapaḥ</li>
    </ul>
    <ul id="R_2.001.030">
        <li id="R_2.001.030ab">eṣā hy asya parā prītir hṛdi saṃparivartate</li>
        <li id="R_2.001.030cd">kadā nāma sutaṃ drakṣyāmy abhiṣiktam ahaṃ priyam</li>
    </ul>
    <ul id="R_2.001.031">
        <li id="R_2.001.031ab">vṛddhikāmo hi lokasya sarvabhūtānukampanaḥ</li>
        <li id="R_2.001.031cd">mattaḥ priyataro loke parjanya iva vṛṣṭimān</li>
    </ul>
    <ul id="R_2.001.032">
        <li id="R_2.001.032ab">yamaśakrasamo vīrye bṛhaspatisamo matau</li>
        <li id="R_2.001.032cd">mahīdharasamo dhṛtyāṃ mattaś ca guṇavattaraḥ</li>
    </ul>
    <ul id="R_2.001.033">
        <li id="R_2.001.033ab">mahīm aham imāṃ kṛtsnām adhitiṣṭhantam ātmajam</li>
        <li id="R_2.001.033cd">anena vayasā dṛṣṭvā yathā svargam avāpnuyām</li>
    </ul>
    <ul id="R_2.001.034">
        <li id="R_2.001.034ab">taṃ samīkṣya mahārājo yuktaṃ samuditair guṇaiḥ</li>
        <li id="R_2.001.034cd">niścitya sacivaiḥ sārdhaṃ yuvarājam amanyata</li>
    </ul>
    <ul id="R_2.001.035">
        <li id="R_2.001.035ab">nānānagaravāstavyān pṛthagjānapadān api</li>
        <li id="R_2.001.035cd">samānināya medinyāḥ pradhānān pṛthivīpatiḥ</li>
    </ul>
    <ul id="R_2.001.036">
        <li id="R_2.001.036ab">atha rājavitīrṇeṣu vividheṣv āsaneṣu ca</li>
        <li id="R_2.001.036cd">rājānam evābhimukhā niṣedur niyatā nṛpāḥ</li>
    </ul>
    <ul id="R_2.001.037">
        <li id="R_2.001.037ab">
            <seg type="pāda" n="a">sa labdhamānair vinayānvitair nṛpaiḥ</seg>
            <seg type="pāda" n="b">purālayair jānapadaiś ca mānavaiḥ</seg>
        </li>
        <li id="R_2.001.037cd">
            <seg type="pāda" n="c">upopaviṣṭair nṛpatir vṛto babhau</seg>
            <seg type="pāda" n="d">sahasracakṣur bhagavān ivāmaraiḥ</seg>
        </li>
    </ul>
    <ul id="R_2.002.001">
        <li id="R_2.002.001ab">tataḥ pariṣadaṃ sarvām āmantrya vasudhādhipaḥ</li>
        <li id="R_2.002.001cd">hitam uddharṣaṇaṃ cedam uvācāpratimaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.002.002">
        <li id="R_2.002.002ab">dundubhisvanakalpena gambhīreṇānunādinā</li>
        <li id="R_2.002.002cd">svareṇa mahatā rājā jīgmūta iva nādayan</li>
    </ul>
    <ul id="R_2.002.003">
        <li id="R_2.002.003ab">so 'ham ikṣvākubhiḥ pūrvair narendraiḥ paripālitam</li>
        <li id="R_2.002.003cd">śreyasā yoktukāmo 'smi sukhārham akhilaṃ jagat</li>
    </ul>
    <ul id="R_2.002.004">
        <li id="R_2.002.004ab">mayāpy ācaritaṃ pūrvaiḥ panthānam anugacchatā</li>
        <li id="R_2.002.004cd">prajā nityam atandreṇa yathāśakty abhirakṣatā</li>
    </ul>
    <ul id="R_2.002.005">
        <li id="R_2.002.005ab">idaṃ śarīraṃ kṛtsnasya lokasya caratā hitam</li>
        <li id="R_2.002.005cd">pāṇḍur asyātapatrasyac chāyāyāṃ jaritaṃ mayā</li>
    </ul>
    <ul id="R_2.002.006">
        <li id="R_2.002.006ab">prāpya varṣasahasrāṇi bahūny āyūṃṣi jīvitaḥ</li>
        <li id="R_2.002.006cd">jīrṇasyāsya śarīrasya viśrāntim abhirocaye</li>
    </ul>
    <ul id="R_2.002.007">
        <li id="R_2.002.007ab">rājaprabhāvajuṣṭāṃ hi durvahām ajitendriyaiḥ</li>
        <li id="R_2.002.007cd">pariśrānto 'smi lokasya gurvīṃ dharmadhuraṃ vahan</li>
    </ul>
    <ul id="R_2.002.008">
        <li id="R_2.002.008ab">so 'haṃ viśramam icchāmi putraṃ kṛtvā prajāhite</li>
        <li id="R_2.002.008cd">saṃnikṛṣṭān imān sarvān anumānya dvijarṣabhān</li>
    </ul>
    <ul id="R_2.002.009">
        <li id="R_2.002.009ab">anujāto hi me sarvair guṇair jyeṣṭho mamātmajaḥ</li>
        <li id="R_2.002.009cd">puraṃdarasamo vīrye rāmaḥ parapuraṃjayaḥ</li>
    </ul>
    <ul id="R_2.002.010">
        <li id="R_2.002.010ab">taṃ candram iva puṣyeṇa yuktaṃ dharmabhṛtāṃ varam</li>
        <li id="R_2.002.010cd">yauvarājyena yoktāsmi prītaḥ puruṣapuṃgavam</li>
    </ul>
    <ul id="R_2.002.011">
        <li id="R_2.002.011ab">anurūpaḥ sa vo nātho lakṣmīvāṃl lakṣmaṇāgrajaḥ</li>
        <li id="R_2.002.011cd">trailokyam api nāthena yena syān nāthavattaram</li>
    </ul>
    <ul id="R_2.002.012">
        <li id="R_2.002.012ab">anena śreyasā sadyaḥ saṃyojyāham imāṃ mahīm</li>
        <li id="R_2.002.012cd">gatakleśo bhaviṣyāmi sute tasmin niveśya vai</li>
    </ul>
    <ul id="R_2.002.013">
        <li id="R_2.002.013ab">iti bruvantaṃ muditāḥ pratyanandan nṛpā nṛpam</li>
        <li id="R_2.002.013cd">vṛṣṭimantaṃ mahāmeghaṃ nardantam iva barhiṇaḥ</li>
    </ul>
    <ul id="R_2.002.014">
        <li id="R_2.002.014ab">tasya dharmārthaviduṣo bhāvam ājñāya sarvaśaḥ</li>
        <li id="R_2.002.014cd">ūcuś ca manasā jñātvā vṛddhaṃ daśarathaṃ nṛpam</li>
    </ul>
    <ul id="R_2.002.015">
        <li id="R_2.002.015ab">anekavarṣasāhasro vṛddhas tvam asi pārthiva</li>
        <li id="R_2.002.015cd">sa rāmaṃ yuvarājānam abhiṣiñcasva pārthivam</li>
    </ul>
    <ul id="R_2.002.016">
        <li id="R_2.002.016ab">iti tad vacanaṃ śrutvā rājā teṣāṃ manaḥpriyam</li>
        <li id="R_2.002.016cd">ajānann iva jijñāsur idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.002.017">
        <li id="R_2.002.017ab">kathaṃ nu mayi dharmeṇa pṛthivīm anuśāsati</li>
        <li id="R_2.002.017cd">bhavanto draṣṭum icchanti yuvarājaṃ mamātmajam</li>
    </ul>
    <ul id="R_2.002.018">
        <li id="R_2.002.018ab">te tam ūcur mahātmānaṃ paurajānapadaiḥ saha</li>
        <li id="R_2.002.018cd">bahavo nṛpa kalyāṇā guṇāḥ putrasya santi te</li>
    </ul>
    <ul id="R_2.002.019">
        <li id="R_2.002.019ab">divyair guṇaiḥ śakrasamo rāmaḥ satyaparākramaḥ</li>
        <li id="R_2.002.019cd">ikṣvākubhyo hi sarvebhyo 'py atirakto viśāmpate</li>
    </ul>
    <ul id="R_2.002.020">
        <li id="R_2.002.020ab">rāmaḥ satpuruṣo loke satyadharmaparāyaṇaḥ</li>
        <li id="R_2.002.020cd">dharmajñaḥ satyasaṃdhaś ca śīlavān anasūyakaḥ</li>
    </ul>
    <ul id="R_2.002.021">
        <li id="R_2.002.021ab">kṣāntaḥ sāntvayitā ślakṣṇaḥ kṛtajño vijitendriyaḥ</li>
        <li id="R_2.002.021cd">mṛduś ca sthiracittaś ca sadā bhavyo 'nasūyakaḥ</li>
    </ul>
    <ul id="R_2.002.022">
        <li id="R_2.002.022ab">priyavādī ca bhūtānāṃ satyavādī ca rāghavaḥ</li>
        <li id="R_2.002.022cd">bahuśrutānāṃ vṛddhānāṃ brāhmaṇānām upāsitā</li>
    </ul>
    <ul id="R_2.002.023">
        <li id="R_2.002.023ab">tenāsyehātulā kīrtir yaśas tejaś ca vardhate</li>
        <li id="R_2.002.023cd">devāsuramanuṣyāṇāṃ sarvāstreṣu viśāradaḥ</li>
    </ul>
    <ul id="R_2.002.024">
        <li id="R_2.002.024ab">yadā vrajati saṃgrāmaṃ grāmārthe nagarasya vā</li>
        <li id="R_2.002.024cd">gatvā saumitrisahito nāvijitya nivartate</li>
    </ul>
    <ul id="R_2.002.025">
        <li id="R_2.002.025ab">saṃgrāmāt punar āgamya kuñjareṇa rathena vā</li>
        <li id="R_2.002.025cd">paurān svajanavan nityaṃ kuśalaṃ paripṛcchati</li>
    </ul>
    <ul id="R_2.002.026">
        <li id="R_2.002.026ab">putreṣv agniṣu dāreṣu preṣyaśiṣyagaṇeṣu ca</li>
        <li id="R_2.002.026cd">nikhilenānupūrvyā ca pitā putrān ivaurasān</li>
    </ul>
    <ul id="R_2.002.027">
        <li id="R_2.002.027ab">śuśrūṣante ca vaḥ śiṣyāḥ kaccit karmasu daṃśitāḥ</li>
        <li id="R_2.002.027cd">iti naḥ puruṣavyāghraḥ sadā rāmo 'bhibhāṣate</li>
    </ul>
    <ul id="R_2.002.028">
        <li id="R_2.002.028ab">vyasaneṣu manuṣyāṇāṃ bhṛśaṃ bhavati duḥkhitaḥ</li>
        <li id="R_2.002.028cd">utsaveṣu ca sarveṣu piteva parituṣyati</li>
    </ul>
    <ul id="R_2.002.029">
        <li id="R_2.002.029ab">satyavādī maheṣvāso vṛddhasevī jitendriyaḥ</li>
        <li id="R_2.002.029cd">vatsaḥ śreyasi jātas te diṣṭyāsau tava rāghavaḥ</li>
        <li id="R_2.002.029ef">diṣṭyā putraguṇair yukto mārīca iva kaśyapaḥ</li>
    </ul>
    <ul id="R_2.002.030">
        <li id="R_2.002.030ab">balam ārogyam āyuś ca rāmasya viditātmanaḥ</li>
        <li id="R_2.002.030cd">āśaṃsate janaḥ sarvo rāṣṭre puravare tathā</li>
    </ul>
    <ul id="R_2.002.031">
        <li id="R_2.002.031ab">abhyantaraś ca bāhyaś ca paurajānapado janaḥ</li>
        <li id="R_2.002.031cd">striyo vṛddhās taruṇyaś ca sāyaṃprātaḥ samāhitāḥ</li>
    </ul>
    <ul id="R_2.002.032">
        <li id="R_2.002.032ab">sarvān devān namasyanti rāmasyārthe yaśasvinaḥ</li>
        <li id="R_2.002.032cd">teṣām āyācitaṃ deva tvatprasādāt samṛdhyatām</li>
    </ul>
    <ul id="R_2.002.033">
        <li id="R_2.002.033ab">rāmam indīvaraśyāmaṃ sarvaśatrunibarhaṇam</li>
        <li id="R_2.002.033cd">paśyāmo yauvarājyasthaṃ tava rājottamātmajam</li>
    </ul>
    <ul id="R_2.002.034">
        <li id="R_2.002.034ab">
            <seg type="pāda" n="a">taṃ devadevopamam ātmajaṃ te</seg>
            <seg type="pāda" n="b">sarvasya lokasya hite niviṣṭam</seg>
        </li>
        <li id="R_2.002.034cd">
            <seg type="pāda" n="c">hitāya naḥ kṣipram udārajuṣṭaṃ</seg>
            <seg type="pāda" n="d">mudābhiṣektuṃ varada tvam arhasi</seg>
        </li>
    </ul>
    <ul id="R_2.003.001">
        <li id="R_2.003.001ab">teṣām ajñalipadmāni pragṛhītāni sarvaśaḥ</li>
        <li id="R_2.003.001cd">pratigṛhyābravīd rājā tebhyaḥ priyahitaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.003.002">
        <li id="R_2.003.002ab">aho 'smi paramaprītaḥ prabhāvaś cātulo mama</li>
        <li id="R_2.003.002cd">yan me jyeṣṭhaṃ priyaṃ putraṃ yauvarājyastham icchatha</li>
    </ul>
    <ul id="R_2.003.003">
        <li id="R_2.003.003ab">iti pratyarcya tān rājā brāhmaṇān idam abravīt</li>
        <li id="R_2.003.003cd">vasiṣṭhaṃ vāmadevaṃ ca teṣām evopaśṛṇvatām</li>
    </ul>
    <ul id="R_2.003.004">
        <li id="R_2.003.004ab">caitraḥ śrīmān ayaṃ māsaḥ puṇyaḥ puṣpitakānanaḥ</li>
        <li id="R_2.003.004cd">yauvarājyāya rāmasya sarvam evopakalpyatām</li>
    </ul>
    <ul id="R_2.003.005">
        <li id="R_2.003.005ab">kṛtam ity eva cābrūtām abhigamya jagatpatim</li>
        <li id="R_2.003.005cd">yathoktavacanaṃ prītau harṣayuktau dvijarṣabhau</li>
    </ul>
    <ul id="R_2.003.006">
        <li id="R_2.003.006ab">tataḥ sumantraṃ dyutimān rājā vacanam abravīt</li>
        <li id="R_2.003.006cd">rāmaḥ kṛtātmā bhavatā śīghram ānīyatām iti</li>
    </ul>
    <ul id="R_2.003.007">
        <li id="R_2.003.007ab">sa tatheti pratijñāya sumantro rājaśāsanāt</li>
        <li id="R_2.003.007cd">rāmaṃ tatrānayāṃ cakre rathena rathināṃ varam</li>
    </ul>
    <ul id="R_2.003.008">
        <li id="R_2.003.008ab">atha tatra samāsīnās tadā daśarathaṃ nṛpam</li>
        <li id="R_2.003.008cd">prācyodīcyāḥ pratīcyāś ca dākṣiṇātyāś ca bhūmipāḥ</li>
    </ul>
    <ul id="R_2.003.009">
        <li id="R_2.003.009ab">mlecchāś cāryāś ca ye cānye vanaśailāntavāsinaḥ</li>
        <li id="R_2.003.009cd">upāsāṃ cakrire sarve taṃ devā iva vāsavam</li>
    </ul>
    <ul id="R_2.003.010">
        <li id="R_2.003.010ab">teṣāṃ madhye sa rājarṣir marutām iva vāsavaḥ</li>
        <li id="R_2.003.010cd">prāsādastho rathagataṃ dadarśāyāntam ātmajam</li>
    </ul>
    <ul id="R_2.003.011">
        <li id="R_2.003.011ab">gandharvarājapratimaṃ loke vikhyātapauruṣam</li>
        <li id="R_2.003.011cd">dīrghabāhuṃ mahāsattvaṃ mattamātaṅgagāminam</li>
    </ul>
    <ul id="R_2.003.012">
        <li id="R_2.003.012ab">candrakāntānanaṃ rāmam atīva priyadarśanam</li>
        <li id="R_2.003.012cd">rūpaudāryaguṇaiḥ puṃsāṃ dṛṣṭicittāpahāriṇam</li>
    </ul>
    <ul id="R_2.003.013">
        <li id="R_2.003.013ab">gharmābhitaptāḥ parjanyaṃ hlādayantam iva prajāḥ</li>
        <li id="R_2.003.013cd">na tatarpa samāyāntaṃ paśyamāno narādhipaḥ</li>
    </ul>
    <ul id="R_2.003.014">
        <li id="R_2.003.014ab">avatārya sumantras taṃ rāghavaṃ syandanottamāt</li>
        <li id="R_2.003.014cd">pituḥ samīpaṃ gacchantaṃ prāñjaliḥ pṛṣṭhato 'nvagāt</li>
    </ul>
    <ul id="R_2.003.015">
        <li id="R_2.003.015ab">sa taṃ kailāsaśṛṅgābhaṃ prāsādaṃ narapuṃgavaḥ</li>
        <li id="R_2.003.015cd">āruroha nṛpaṃ draṣṭuṃ saha sūtena rāghavaḥ</li>
    </ul>
    <ul id="R_2.003.016">
        <li id="R_2.003.016ab">sa prāñjalir abhipretya praṇataḥ pitur antike</li>
        <li id="R_2.003.016cd">nāma svaṃ śrāvayan rāmo vavande caraṇau pituḥ</li>
    </ul>
    <ul id="R_2.003.017">
        <li id="R_2.003.017ab">taṃ dṛṣṭvā praṇataṃ pārśve kṛtāñjalipuṭaṃ nṛpaḥ</li>
        <li id="R_2.003.017cd">gṛhyāñjalau samākṛṣya sasvaje priyam ātmajam</li>
    </ul>
    <ul id="R_2.003.018">
        <li id="R_2.003.018ab">tasmai cābhyudyataṃ śrīmān maṇikāñcanabhūṣitam</li>
        <li id="R_2.003.018cd">dideśa rājā ruciraṃ rāmāya paramāsanam</li>
    </ul>
    <ul id="R_2.003.019">
        <li id="R_2.003.019ab">tad āsanavaraṃ prāpya vyadīpayata rāghavaḥ</li>
        <li id="R_2.003.019cd">svayeva prabhayā merum udaye vimalo raviḥ</li>
    </ul>
    <ul id="R_2.003.020">
        <li id="R_2.003.020ab">tena vibhrājitā tatra sā sabhābhivyarocata</li>
        <li id="R_2.003.020cd">vimalagrahanakṣatrā śāradī dyaur ivendunā</li>
    </ul>
    <ul id="R_2.003.021">
        <li id="R_2.003.021ab">taṃ paśyamāno nṛpatis tutoṣa priyam ātmajam</li>
        <li id="R_2.003.021cd">alaṃkṛtam ivātmānam ādarśatalasaṃsthitam</li>
    </ul>
    <ul id="R_2.003.022">
        <li id="R_2.003.022ab">sa taṃ sasmitam ābhāṣya putraṃ putravatāṃ varaḥ</li>
        <li id="R_2.003.022cd">uvācedaṃ vaco rājā devendram iva kaśyapaḥ</li>
    </ul>
    <ul id="R_2.003.023">
        <li id="R_2.003.023ab">jyeṣṭhāyām asi me patnyāṃ sadṛśyāṃ sadṛśaḥ sutaḥ</li>
        <li id="R_2.003.023cd">utpannas tvaṃ guṇaśreṣṭho mama rāmātmajaḥ priyaḥ</li>
    </ul>
    <ul id="R_2.003.024">
        <li id="R_2.003.024ab">tvayā yataḥ prajāś cemāḥ svaguṇair anurañjitāḥ</li>
        <li id="R_2.003.024cd">tasmāt tvaṃ puṣyayogena yauvarājyam avāpnuhi</li>
    </ul>
    <ul id="R_2.003.025">
        <li id="R_2.003.025ab">kāmatas tvaṃ prakṛtyaiva vinīto guṇavān asi</li>
        <li id="R_2.003.025cd">guṇavaty api tu snehāt putra vakṣyāmi te hitam</li>
    </ul>
    <ul id="R_2.003.026">
        <li id="R_2.003.026ab">bhūyo vinayam āsthāya bhava nityaṃ jitendriyaḥ</li>
        <li id="R_2.003.026cd">kāmakrodhasamutthāni tyajethā vyasanāni ca</li>
    </ul>
    <ul id="R_2.003.027">
        <li id="R_2.003.027ab">parokṣayā vartamāno vṛttyā pratyakṣayā tathā</li>
        <li id="R_2.003.027cd">amātyaprabhṛtīḥ sarvāḥ prakṛtīś cānurañjaya</li>
    </ul>
    <ul id="R_2.003.028">
        <li id="R_2.003.028ab">tuṣṭānuraktaprakṛtir yaḥ pālayati medinīm</li>
        <li id="R_2.003.028cd">tasya nandanti mitrāṇi labdhvāmṛtam ivāmarāḥ</li>
        <li id="R_2.003.028ef">tasmāt putra tvam ātmānaṃ niyamyaiva samācara</li>
    </ul>
    <ul id="R_2.003.029">
        <li id="R_2.003.029ab">tac chrutvā suhṛdas tasya rāmasya priyakāriṇaḥ</li>
        <li id="R_2.003.029cd">tvaritāḥ śīghram abhyetya kausalyāyai nyavedayan</li>
    </ul>
    <ul id="R_2.003.030">
        <li id="R_2.003.030ab">sā hiraṇyaṃ ca gāś caiva ratnāni vividhāni ca</li>
        <li id="R_2.003.030cd">vyādideśa priyākhyebhyaḥ kausalyā pramadottamā</li>
    </ul>
    <ul id="R_2.003.031">
        <li id="R_2.003.031ab">athābhivādya rājānaṃ ratham āruhya rāghavaḥ</li>
        <li id="R_2.003.031cd">yayau svaṃ dyutimad veśma janaughaiḥ pratipūjitaḥ</li>
    </ul>
    <ul id="R_2.003.032">
        <li id="R_2.003.032ab">
            <seg type="pāda" n="a">te cāpi paurā nṛpater vacas tac</seg>
            <seg type="pāda" n="b">chrutvā tadā lābham iveṣṭam āpya</seg>
        </li>
        <li id="R_2.003.032cd">
            <seg type="pāda" n="c">narendram āmantya gṛhāṇi gatvā</seg>
            <seg type="pāda" n="d">devān samānarcur atīva hṛṣṭāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.004.001">
        <li id="R_2.004.001ab">gateṣv atha nṛpo bhūyaḥ paureṣu saha mantribhiḥ</li>
        <li id="R_2.004.001cd">mantrayitvā tataś cakre niścayajñaḥ sa niścayam</li>
    </ul>
    <ul id="R_2.004.002">
        <li id="R_2.004.002ab">śva eva puṣyo bhavitā śvo 'bhiṣecyeta me sutaḥ</li>
        <li id="R_2.004.002cd">rāmo rājīvatāmrākṣo yauvarājya iti prabhuḥ</li>
    </ul>
    <ul id="R_2.004.003">
        <li id="R_2.004.003ab">athāntargṛham āviśya rājā daśarathas tadā</li>
        <li id="R_2.004.003cd">sūtam ājñāpayām āsa rāmaṃ punar ihānaya</li>
    </ul>
    <ul id="R_2.004.004">
        <li id="R_2.004.004ab">pratigṛhya sa tadvākyaṃ sūtaḥ punar upāyayau</li>
        <li id="R_2.004.004cd">rāmasya bhavanaṃ śīghraṃ rāmam ānayituṃ punaḥ</li>
    </ul>
    <ul id="R_2.004.005">
        <li id="R_2.004.005ab">dvāḥsthair āveditaṃ tasya rāmāyāgamanaṃ punaḥ</li>
        <li id="R_2.004.005cd">śrutvaiva cāpi rāmas taṃ prāptaṃ śaṅkānvito 'bhavat</li>
    </ul>
    <ul id="R_2.004.006">
        <li id="R_2.004.006ab">praveśya cainaṃ tvaritaṃ rāmo vacanam abravīt</li>
        <li id="R_2.004.006cd">yad āgamanakṛtyaṃ te bhūyas tad brūhy aśeṣataḥ</li>
    </ul>
    <ul id="R_2.004.007">
        <li id="R_2.004.007ab">tam uvāca tataḥ sūto rājā tvāṃ draṣṭum icchati</li>
        <li id="R_2.004.007cd">śrutvā pramāṇam atra tvaṃ gamanāyetarāya vā</li>
    </ul>
    <ul id="R_2.004.008">
        <li id="R_2.004.008ab">iti sūtavacaḥ śrutvā rāmo 'tha tvarayānvitaḥ</li>
        <li id="R_2.004.008cd">prayayau rājabhavanaṃ punar draṣṭuṃ nareśvaram</li>
    </ul>
    <ul id="R_2.004.009">
        <li id="R_2.004.009ab">taṃ śrutvā samanuprāptaṃ rāmaṃ daśaratho nṛpaḥ</li>
        <li id="R_2.004.009cd">praveśayām āsa gṛhaṃ vivikṣuḥ priyam uttamam</li>
    </ul>
    <ul id="R_2.004.010">
        <li id="R_2.004.010ab">praviśann eva ca śrīmān rāghavo bhavanaṃ pituḥ</li>
        <li id="R_2.004.010cd">dadarśa pitaraṃ dūrāt praṇipatya kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_2.004.011">
        <li id="R_2.004.011ab">praṇamantaṃ samutthāpya taṃ pariṣvajya bhūmipaḥ</li>
        <li id="R_2.004.011cd">pradiśya cāsmai ruciram āsanaṃ punar abravīt</li>
    </ul>
    <ul id="R_2.004.012">
        <li id="R_2.004.012ab">rāma vṛddho 'smi dīrghāyur bhuktā bhogā mayepsitāḥ</li>
        <li id="R_2.004.012cd">annavadbhiḥ kratuśatais tatheṣṭaṃ bhūridakṣiṇaiḥ</li>
    </ul>
    <ul id="R_2.004.013">
        <li id="R_2.004.013ab">jātam iṣṭam apatyaṃ me tvam adyānupamaṃ bhuvi</li>
        <li id="R_2.004.013cd">dattam iṣṭam adhītaṃ ca mayā puruṣasattama</li>
    </ul>
    <ul id="R_2.004.014">
        <li id="R_2.004.014ab">anubhūtāni ceṣṭāni mayā vīra sukhāni ca</li>
        <li id="R_2.004.014cd">devarṣi pitṛviprāṇām anṛṇo 'smi tathātmanaḥ</li>
    </ul>
    <ul id="R_2.004.015">
        <li id="R_2.004.015ab">na kiṃ cin mama kartavyaṃ tavānyatrābhiṣecanāt</li>
        <li id="R_2.004.015cd">ato yat tvām ahaṃ brūyāṃ tan me tvaṃ kartum arhasi</li>
    </ul>
    <ul id="R_2.004.016">
        <li id="R_2.004.016ab">adya prakṛtayaḥ sarvās tvām icchanti narādhipam</li>
        <li id="R_2.004.016cd">atas tvāṃ yuvarājānam abhiṣekṣyāmi putraka</li>
    </ul>
    <ul id="R_2.004.017">
        <li id="R_2.004.017ab">api cādyāśubhān rāma svapnān paśyāmi dāruṇān</li>
        <li id="R_2.004.017cd">sanirghātā maholkāś ca patantīha mahāsvanāḥ</li>
    </ul>
    <ul id="R_2.004.018">
        <li id="R_2.004.018ab">avaṣṭabdhaṃ ca me rāma nakṣatraṃ dāruṇair grahaiḥ</li>
        <li id="R_2.004.018cd">āvedayanti daivajñāḥ sūryāṅgārakarāhubhiḥ</li>
    </ul>
    <ul id="R_2.004.019">
        <li id="R_2.004.019ab">prāyeṇa hi nimittānām īdṛśānāṃ samudbhave</li>
        <li id="R_2.004.019cd">rājā vā mṛtyum āpnoti ghorāṃ vāpadam ṛcchati</li>
    </ul>
    <ul id="R_2.004.020">
        <li id="R_2.004.020ab">tad yāvad eva me ceto na vimuhyati rāghava</li>
        <li id="R_2.004.020cd">tāvad evābhiṣiñcasva calā hi prāṇināṃ matiḥ</li>
    </ul>
    <ul id="R_2.004.021">
        <li id="R_2.004.021ab">adya candro 'bhyupagataḥ puṣyāt pūrvaṃ punar vasum</li>
        <li id="R_2.004.021cd">śvaḥ puṣya yogaṃ niyataṃ vakṣyante daivacintakāḥ</li>
    </ul>
    <ul id="R_2.004.022">
        <li id="R_2.004.022ab">tatra puṣye 'bhiṣiñcasva manas tvarayatīva mām</li>
        <li id="R_2.004.022cd">śvas tvāham abhiṣekṣyāmi yauvarājye paraṃtapa</li>
    </ul>
    <ul id="R_2.004.023">
        <li id="R_2.004.023ab">tasmāt tvayādya vratinā niśeyaṃ niyatātmanā</li>
        <li id="R_2.004.023cd">saha vadhvopavastavyā darbhaprastaraśāyinā</li>
    </ul>
    <ul id="R_2.004.024">
        <li id="R_2.004.024ab">suhṛdaś cāpramattās tvāṃ rakṣantv adya samantataḥ</li>
        <li id="R_2.004.024cd">bhavanti bahuvighnāni kāryāṇy evaṃvidhāni hi</li>
    </ul>
    <ul id="R_2.004.025">
        <li id="R_2.004.025ab">viproṣitaś ca bharato yāvad eva purād itaḥ</li>
        <li id="R_2.004.025cd">tāvad evābhiṣekas te prāptakālo mato mama</li>
    </ul>
    <ul id="R_2.004.026">
        <li id="R_2.004.026ab">kāmaṃ khalu satāṃ vṛtte bhrātā te bharataḥ sthitaḥ</li>
        <li id="R_2.004.026cd">jyeṣṭhānuvartī dharmātmā sānukrośo jitendriyaḥ</li>
    </ul>
    <ul id="R_2.004.027">
        <li id="R_2.004.027ab">kiṃ tu cittaṃ manuṣyāṇām anityam iti me matiḥ</li>
        <li id="R_2.004.027cd">satāṃ ca dharmanityānāṃ kṛtaśobhi ca rāghava</li>
    </ul>
    <ul id="R_2.004.028">
        <li id="R_2.004.028ab">ity uktaḥ so 'bhyanujñātaḥ śvobhāviny abhiṣecane</li>
        <li id="R_2.004.028cd">vrajeti rāmaḥ pitaram abhivādyābhyayād gṛham</li>
    </ul>
    <ul id="R_2.004.029">
        <li id="R_2.004.029ab">praviśya cātmano veśma rājñoddiṣṭe 'bhiṣecane</li>
        <li id="R_2.004.029cd">tasmin kṣaṇe vinirgatya mātur antaḥpuraṃ yayau</li>
    </ul>
    <ul id="R_2.004.030">
        <li id="R_2.004.030ab">tatra tāṃ pravaṇām eva mātaraṃ kṣaumavāsinīm</li>
        <li id="R_2.004.030cd">vāgyatāṃ devatāgāre dadarśa yācatīṃ śriyam</li>
    </ul>
    <ul id="R_2.004.031">
        <li id="R_2.004.031ab">prāg eva cāgatā tatra sumitrā lakṣmaṇas tathā</li>
        <li id="R_2.004.031cd">sītā cānāyitā śrutvā priyaṃ rāmābhiṣecanam</li>
    </ul>
    <ul id="R_2.004.032">
        <li id="R_2.004.032ab">tasmin kāle hi kausalyā tasthāv āmīlitekṣaṇā</li>
        <li id="R_2.004.032cd">sumitrayānvāsyamānā sītayā lakṣmaṇena ca</li>
    </ul>
    <ul id="R_2.004.033">
        <li id="R_2.004.033ab">śrutvā puṣyeṇa putrasya yauvarājyābhiṣecanam</li>
        <li id="R_2.004.033cd">prāṇāyāmena puruṣaṃ dhyāyamānā janārdanam</li>
    </ul>
    <ul id="R_2.004.034">
        <li id="R_2.004.034ab">tathā saniyamām eva so 'bhigamyābhivādya ca</li>
        <li id="R_2.004.034cd">uvāca vacanaṃ rāmo harṣayaṃs tām idaṃ tadā</li>
    </ul>
    <ul id="R_2.004.035">
        <li id="R_2.004.035ab">amba pitrā niyukto 'smi prajāpālanakarmaṇi</li>
        <li id="R_2.004.035cd">bhavitā śvo 'bhiṣeko me yathā me śāsanaṃ pituḥ</li>
    </ul>
    <ul id="R_2.004.036">
        <li id="R_2.004.036ab">sītayāpy upavastavyā rajanīyaṃ mayā saha</li>
        <li id="R_2.004.036cd">evam ṛtvigupādhyāyaiḥ saha mām uktavān pitā</li>
    </ul>
    <ul id="R_2.004.037">
        <li id="R_2.004.037ab">yāni yāny atra yogyāni śvobhāviny abhiṣecane</li>
        <li id="R_2.004.037cd">tāni me maṅgalāny adya vaidehyāś caiva kāraya</li>
    </ul>
    <ul id="R_2.004.038">
        <li id="R_2.004.038ab">etac chrutvā tu kausalyā cirakālābhikāṅkṣitam</li>
        <li id="R_2.004.038cd">harṣabāṣpakalaṃ vākyam idaṃ rāmam abhāṣata</li>
    </ul>
    <ul id="R_2.004.039">
        <li id="R_2.004.039ab">vatsa rāma ciraṃ jīva hatās te paripanthinaḥ</li>
        <li id="R_2.004.039cd">jñātīn me tvaṃ śriyā yuktaḥ sumitrāyāś ca nandaya</li>
    </ul>
    <ul id="R_2.004.040">
        <li id="R_2.004.040ab">kalyāṇe bata nakṣatre mayi jāto 'si putraka</li>
        <li id="R_2.004.040cd">yena tvayā daśaratho guṇair ārādhitaḥ pitā</li>
    </ul>
    <ul id="R_2.004.041">
        <li id="R_2.004.041ab">amoghaṃ bata me kṣāntaṃ puruṣe puṣkarekṣaṇe</li>
        <li id="R_2.004.041cd">yeyam ikṣvākurājyaśrīḥ putra tvāṃ saṃśrayiṣyati</li>
    </ul>
    <ul id="R_2.004.042">
        <li id="R_2.004.042ab">ity evam ukto mātredaṃ rāmo bhāratam abravīt</li>
        <li id="R_2.004.042cd">prāñjaliṃ prahvam āsīnam abhivīkṣya smayann iva</li>
    </ul>
    <ul id="R_2.004.043">
        <li id="R_2.004.043ab">lakṣmaṇemāṃ mayā sārdhaṃ praśādhi tvaṃ vasuṃdharām</li>
        <li id="R_2.004.043cd">dvitīyaṃ me 'ntarātmānaṃ tvām iyaṃ śrīr upasthitā</li>
    </ul>
    <ul id="R_2.004.044">
        <li id="R_2.004.044ab">saumitre bhuṅkṣva bhogāṃs tvam iṣṭān rājyaphalāni ca</li>
        <li id="R_2.004.044cd">jīvitaṃ ca hi rājyaṃ ca tvadartham abhikāmaye</li>
    </ul>
    <ul id="R_2.004.045">
        <li id="R_2.004.045ab">ity uktvā lakṣmaṇaṃ rāmo mātarāv abhivādya ca</li>
        <li id="R_2.004.045cd">abhyanujñāpya sītāṃ ca jagāma svaṃ niveśanam</li>
    </ul>
    <ul id="R_2.005.001">
        <li id="R_2.005.001ab">saṃdiśya rāmaṃ nṛpatiḥ śvobhāviny abhiṣecane</li>
        <li id="R_2.005.001cd">purohitaṃ samāhūya vasiṣṭham idam abravīt</li>
    </ul>
    <ul id="R_2.005.002">
        <li id="R_2.005.002ab">gacchopavāsaṃ kākutsthaṃ kārayādya tapodhana</li>
        <li id="R_2.005.002cd">śrīyaśorājyalābhāya vadhvā saha yatavratam</li>
    </ul>
    <ul id="R_2.005.003">
        <li id="R_2.005.003ab">tatheti ca sa rājānam uktvā vedavidāṃ varaḥ</li>
        <li id="R_2.005.003cd">svayaṃ vasiṣṭho bhagavān yayau rāmaniveśanam</li>
    </ul>
    <ul id="R_2.005.004">
        <li id="R_2.005.004ab">sa rāmabhavanaṃ prāpya pāṇḍurābhraghanaprabham</li>
        <li id="R_2.005.004cd">tisraḥ kakṣyā rathenaiva viveśa munisattamaḥ</li>
    </ul>
    <ul id="R_2.005.005">
        <li id="R_2.005.005ab">tam āgatam ṛṣiṃ rāmas tvarann iva sasaṃbhramaḥ</li>
        <li id="R_2.005.005cd">mānayiṣyan sa mānārhaṃ niścakrāma niveśanāt</li>
    </ul>
    <ul id="R_2.005.006">
        <li id="R_2.005.006ab">abhyetya tvaramāṇaś ca rathābhyāśaṃ manīṣiṇaḥ</li>
        <li id="R_2.005.006cd">tato 'vatārayām āsa parigṛhya rathāt svayam</li>
    </ul>
    <ul id="R_2.005.007">
        <li id="R_2.005.007ab">sa cainaṃ praśritaṃ dṛṣṭvā saṃbhāṣyābhiprasādya ca</li>
        <li id="R_2.005.007cd">priyārhaṃ harṣayan rāmam ity uvāca purohitaḥ</li>
    </ul>
    <ul id="R_2.005.008">
        <li id="R_2.005.008ab">prasannas te pitā rāma yauvarājyam avāpsyasi</li>
        <li id="R_2.005.008cd">upavāsaṃ bhavān adya karotu saha sītayā</li>
    </ul>
    <ul id="R_2.005.009">
        <li id="R_2.005.009ab">prātas tvām abhiṣektā hi yauvarājye narādhipaḥ</li>
        <li id="R_2.005.009cd">pitā daśarathaḥ prītyā yayātiṃ nahuṣo yathā</li>
    </ul>
    <ul id="R_2.005.010">
        <li id="R_2.005.010ab">ity uktvā sa tadā rāmam upavāsaṃ yatavratam</li>
        <li id="R_2.005.010cd">mantravat kārayām āsa vaidehyā sahitaṃ muniḥ</li>
    </ul>
    <ul id="R_2.005.011">
        <li id="R_2.005.011ab">tato yathāvad rāmeṇa sa rājño gurur arcitaḥ</li>
        <li id="R_2.005.011cd">abhyanujñāpya kākutsthaṃ yayau rāmaniveśanāt</li>
    </ul>
    <ul id="R_2.005.012">
        <li id="R_2.005.012ab">suhṛdbhis tatra rāmo 'pi tān anujñāpya sarvaśaḥ</li>
        <li id="R_2.005.012cd">sabhājito viveśātha tān anujñāpya sarvaśaḥ</li>
    </ul>
    <ul id="R_2.005.013">
        <li id="R_2.005.013ab">hṛṣṭanārī narayutaṃ rāmaveśma tadā babhau</li>
        <li id="R_2.005.013cd">yathā mattadvijagaṇaṃ praphullanalinaṃ saraḥ</li>
    </ul>
    <ul id="R_2.005.014">
        <li id="R_2.005.014ab">sa rājabhavanaprakhyāt tasmād rāmaniveśanāt</li>
        <li id="R_2.005.014cd">nirgatya dadṛśe mārgaṃ vasiṣṭho janasaṃvṛtam</li>
    </ul>
    <ul id="R_2.005.015">
        <li id="R_2.005.015ab">vṛndavṛndair ayodhyāyāṃ rājamārgāḥ samantataḥ</li>
        <li id="R_2.005.015cd">babhūvur abhisaṃbādhāḥ kutūhalajanair vṛtāḥ</li>
    </ul>
    <ul id="R_2.005.016">
        <li id="R_2.005.016ab">janavṛndormisaṃgharṣaharṣasvanavatas tadā</li>
        <li id="R_2.005.016cd">babhūva rājamārgasya sāgarasyeva nisvanaḥ</li>
    </ul>
    <ul id="R_2.005.017">
        <li id="R_2.005.017ab">siktasaṃmṛṣṭarathyā hi tad ahar vanamālinī</li>
        <li id="R_2.005.017cd">āsīd ayodhyā nagarī samucchritagṛhadhvajā</li>
    </ul>
    <ul id="R_2.005.018">
        <li id="R_2.005.018ab">tadā hy ayodhyā nilayaḥ sastrībālābalo janaḥ</li>
        <li id="R_2.005.018cd">rāmābhiṣekam ākāṅkṣann ākāṅkṣann udayaṃ raveḥ</li>
    </ul>
    <ul id="R_2.005.019">
        <li id="R_2.005.019ab">prajālaṃkārabhūtaṃ ca janasyānandavardhanam</li>
        <li id="R_2.005.019cd">utsuko 'bhūj jano draṣṭuṃ tam ayodhyā mahotsavam</li>
    </ul>
    <ul id="R_2.005.020">
        <li id="R_2.005.020ab">evaṃ taṃ janasaṃbādhaṃ rājamārgaṃ purohitaḥ</li>
        <li id="R_2.005.020cd">vyūhann iva janaughaṃ taṃ śanai rāja kulaṃ yayau</li>
    </ul>
    <ul id="R_2.005.021">
        <li id="R_2.005.021ab">sitābhraśikharaprakhyaṃ prāsadam adhiruhya saḥ</li>
        <li id="R_2.005.021cd">samiyāya narendreṇa śakreṇeva bṛhaspatiḥ</li>
    </ul>
    <ul id="R_2.005.022">
        <li id="R_2.005.022ab">tam āgatam abhiprekṣya hitvā rājāsanaṃ nṛpaḥ</li>
        <li id="R_2.005.022cd">papraccha sa ca tasmai tat kṛtam ity abhyavedayat</li>
    </ul>
    <ul id="R_2.005.023">
        <li id="R_2.005.023ab">guruṇā tv abhyanujñāto manujaughaṃ visṛjya tam</li>
        <li id="R_2.005.023cd">viveśāntaḥpuraṃ rājā siṃho giriguhām iva</li>
    </ul>
    <ul id="R_2.005.024">
        <li id="R_2.005.024ab">
            <seg type="pāda" n="a">tad agryaveṣapramadājanākulaṃ</seg>
            <seg type="pāda" n="b">mahendraveśmapratimaṃ niveśanam</seg>
        </li>
        <li id="R_2.005.024cd">
            <seg type="pāda" n="c">vyadīpayaṃś cāru viveśa pārthivaḥ</seg>
            <seg type="pāda" n="d">śaśīva tārāgaṇasaṃkulaṃ nabhaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.006.001">
        <li id="R_2.006.001ab">gate purohite rāmaḥ snāto niyatamānasaḥ</li>
        <li id="R_2.006.001cd">saha patnyā viśālākṣyā nārāyaṇam upāgamat</li>
    </ul>
    <ul id="R_2.006.002">
        <li id="R_2.006.002ab">pragṛhya śirasā pātrīṃ haviṣo vidhivat tadā</li>
        <li id="R_2.006.002cd">mahate daivatāyājyaṃ juhāva jvalite 'nale</li>
    </ul>
    <ul id="R_2.006.003">
        <li id="R_2.006.003ab">śeṣaṃ ca haviṣas tasya prāśyāśāsyātmanaḥ priyam</li>
        <li id="R_2.006.003cd">dhyāyan nārāyaṇaṃ devaṃ svāstīrṇe kuśasaṃstare</li>
    </ul>
    <ul id="R_2.006.004">
        <li id="R_2.006.004ab">vāgyataḥ saha vaidehyā bhūtvā niyatamānasaḥ</li>
        <li id="R_2.006.004cd">śrīmaty āyatane viṣṇoḥ śiśye naravarātmajaḥ</li>
    </ul>
    <ul id="R_2.006.005">
        <li id="R_2.006.005ab">ekayāmāvaśiṣṭāyāṃ rātryāṃ prativibudhya saḥ</li>
        <li id="R_2.006.005cd">alaṃkāravidhiṃ kṛtsnaṃ kārayām āsa veśmanaḥ</li>
    </ul>
    <ul id="R_2.006.006">
        <li id="R_2.006.006ab">tatra śṛṇvan sukhā vācaḥ sūtamāgadhabandinām</li>
        <li id="R_2.006.006cd">pūrvāṃ saṃdhyām upāsīno jajāpa yatamānasaḥ</li>
    </ul>
    <ul id="R_2.006.007">
        <li id="R_2.006.007ab">tuṣṭāva praṇataś caiva śirasā madhusūdanam</li>
        <li id="R_2.006.007cd">vimalakṣaumasaṃvīto vācayām āsa ca dvijān</li>
    </ul>
    <ul id="R_2.006.008">
        <li id="R_2.006.008ab">teṣāṃ puṇyāhaghoṣo 'tha gambhīramadhuras tadā</li>
        <li id="R_2.006.008cd">ayodhyāṃ pūrayām āsa tūryaghoṣānunāditaḥ</li>
    </ul>
    <ul id="R_2.006.009">
        <li id="R_2.006.009ab">kṛtopavāsaṃ tu tadā vaidehyā saha rāghavam</li>
        <li id="R_2.006.009cd">ayodhyā nilayaḥ śrutvā sarvaḥ pramudito janaḥ</li>
    </ul>
    <ul id="R_2.006.010">
        <li id="R_2.006.010ab">tataḥ paurajanaḥ sarvaḥ śrutvā rāmābhiṣecanam</li>
        <li id="R_2.006.010cd">prabhātāṃ rajanīṃ dṛṣṭvā cakre śobhāṃ parāṃ punaḥ</li>
    </ul>
    <ul id="R_2.006.011">
        <li id="R_2.006.011ab">sitābhraśikharābheṣu devatāyataneṣu ca</li>
        <li id="R_2.006.011cd">catuṣpatheṣu rathyāsu caityeṣv aṭṭālakeṣu ca</li>
    </ul>
    <ul id="R_2.006.012">
        <li id="R_2.006.012ab">nānāpaṇyasamṛddheṣu vaṇijām āpaṇeṣu ca</li>
        <li id="R_2.006.012cd">kuṭumbināṃ samṛddheṣu śrīmatsu bhavaneṣu ca</li>
    </ul>
    <ul id="R_2.006.013">
        <li id="R_2.006.013ab">sabhāsu caiva sarvāsu vṛkṣeṣv ālakṣiteṣu ca</li>
        <li id="R_2.006.013cd">dhvajāḥ samucchritāś citrāḥ patākāś cābhavaṃs tadā</li>
    </ul>
    <ul id="R_2.006.014">
        <li id="R_2.006.014ab">naṭanartakasaṃghānāṃ gāyakānāṃ ca gāyatām</li>
        <li id="R_2.006.014cd">manaḥkarṇasukhā vācaḥ śuśruvuś ca tatas tataḥ</li>
    </ul>
    <ul id="R_2.006.015">
        <li id="R_2.006.015ab">rāmābhiṣekayuktāś ca kathāś cakrur mitho janāḥ</li>
        <li id="R_2.006.015cd">rāmābhiṣeke saṃprāpte catvareṣu gṛheṣu ca</li>
    </ul>
    <ul id="R_2.006.016">
        <li id="R_2.006.016ab">bālā api krīḍamānā gṛhadvāreṣu saṃghaśaḥ</li>
        <li id="R_2.006.016cd">rāmābhiṣekasaṃyuktāś cakrur eva mithaḥ kathāḥ</li>
    </ul>
    <ul id="R_2.006.017">
        <li id="R_2.006.017ab">kṛtapuṣpopahāraś ca dhūpagandhādhivāsitaḥ</li>
        <li id="R_2.006.017cd">rājamārgaḥ kṛtaḥ śrīmān paurai rāmābhiṣecane</li>
    </ul>
    <ul id="R_2.006.018">
        <li id="R_2.006.018ab">prakāśīkaraṇārthaṃ ca niśāgamanaśaṅkayā</li>
        <li id="R_2.006.018cd">dīpavṛkṣāṃs tathā cakrur anu rathyāsu sarvaśaḥ</li>
    </ul>
    <ul id="R_2.006.019">
        <li id="R_2.006.019ab">alaṃkāraṃ purasyaivaṃ kṛtvā tat puravāsinaḥ</li>
        <li id="R_2.006.019cd">ākāṅkṣamāṇā rāmasya yauvarājyābhiṣecanam</li>
    </ul>
    <ul id="R_2.006.020">
        <li id="R_2.006.020ab">sametya saṃghaśaḥ sarve catvareṣu sabhāsu ca</li>
        <li id="R_2.006.020cd">kathayanto mithas tatra praśaśaṃsur janādhipam</li>
    </ul>
    <ul id="R_2.006.021">
        <li id="R_2.006.021ab">aho mahātmā rājāyam ikṣvākukulanandanaḥ</li>
        <li id="R_2.006.021cd">jñātvā yo vṛddham ātmānaṃ rāmaṃ rājye 'bhiṣekṣyati</li>
    </ul>
    <ul id="R_2.006.022">
        <li id="R_2.006.022ab">sarve hy anugṛhītāḥ sma yan no rāmo mahīpatiḥ</li>
        <li id="R_2.006.022cd">cirāya bhavitā goptā dṛṣṭalokaparāvaraḥ</li>
    </ul>
    <ul id="R_2.006.023">
        <li id="R_2.006.023ab">anuddhatamanā vidvān dharmātmā bhrātṛvatsalaḥ</li>
        <li id="R_2.006.023cd">yathā ca bhrātṛṣu snigdhas tathāsmāsv api rāghavaḥ</li>
    </ul>
    <ul id="R_2.006.024">
        <li id="R_2.006.024ab">ciraṃ jīvatu dharmātmā rājā daśaratho 'naghaḥ</li>
        <li id="R_2.006.024cd">yatprasādenābhiṣiktaṃ rāmaṃ drakṣyāmahe vayam</li>
    </ul>
    <ul id="R_2.006.025">
        <li id="R_2.006.025ab">evaṃvidhaṃ kathayatāṃ paurāṇāṃ śuśruvus tadā</li>
        <li id="R_2.006.025cd">digbhyo 'pi śrutavṛttāntāḥ prāptā jānapadā janāḥ</li>
    </ul>
    <ul id="R_2.006.026">
        <li id="R_2.006.026ab">te tu digbhyaḥ purīṃ prāptā draṣṭuṃ rāmābhiṣecanam</li>
        <li id="R_2.006.026cd">rāmasya pūrayām āsuḥ purīṃ jānapadā janāḥ</li>
    </ul>
    <ul id="R_2.006.027">
        <li id="R_2.006.027ab">janaughais tair visarpadbhiḥ śuśruve tatra niḥsvanaḥ</li>
        <li id="R_2.006.027cd">parvasūdīrṇavegasya sāgarasyeva niḥsvanaḥ</li>
    </ul>
    <ul id="R_2.006.028">
        <li id="R_2.006.028ab">
            <seg type="pāda" n="a">tatas tad indrakṣayasaṃnibhaṃ puraṃ</seg>
            <seg type="pāda" n="b">didṛkṣubhir jānapadair upāgataiḥ</seg>
        </li>
        <li id="R_2.006.028cd">
            <seg type="pāda" n="c">samantataḥ sasvanam ākulaṃ babhau</seg>
            <seg type="pāda" n="d">samudrayādobhir ivārṇavodakam</seg>
        </li>
    </ul>
    <ul id="R_2.007.001">
        <li id="R_2.007.001ab">jñātidāsī yato jātā kaikeyyās tu sahoṣitā</li>
        <li id="R_2.007.001cd">prāsādaṃ candrasaṃkāśam āruroha yadṛcchayā</li>
    </ul>
    <ul id="R_2.007.002">
        <li id="R_2.007.002ab">siktarājapathāṃ kṛtsnāṃ prakīrṇakamalotpalām</li>
        <li id="R_2.007.002cd">ayodhyāṃ mantharā tasmāt prāsādād anvavaikṣata</li>
    </ul>
    <ul id="R_2.007.003">
        <li id="R_2.007.003ab">patākābhir varārhābhir dhvajaiś ca samalaṃkṛtām</li>
        <li id="R_2.007.003cd">siktāṃ candanatoyaiś ca śiraḥsnātajanair vṛtām</li>
    </ul>
    <ul id="R_2.007.004">
        <li id="R_2.007.004ab">avidūre sthitāṃ dṛṣṭvā dhātrīṃ papraccha mantharā</li>
        <li id="R_2.007.004cd">uttamenābhisaṃyuktā harṣeṇārthaparā satī</li>
    </ul>
    <ul id="R_2.007.005">
        <li id="R_2.007.005ab">rāmamātā dhanaṃ kiṃ nu janebhyaḥ saṃprayacchati</li>
        <li id="R_2.007.005cd">atimātraṃ praharṣo 'yaṃ kiṃ janasya ca śaṃsa me</li>
        <li id="R_2.007.005ef">kārayiṣyati kiṃ vāpi saṃprahṛṣṭo mahīpatiḥ</li>
    </ul>
    <ul id="R_2.007.006">
        <li id="R_2.007.006ab">vidīryamāṇā harṣeṇa dhātrī paramayā mudā</li>
        <li id="R_2.007.006cd">ācacakṣe 'tha kubjāyai bhūyasīṃ rāghave śriyam</li>
    </ul>
    <ul id="R_2.007.007">
        <li id="R_2.007.007ab">śvaḥ puṣyeṇa jitakrodhaṃ yauvarājyena rāghavam</li>
        <li id="R_2.007.007cd">rājā daśaratho rāmam abhiṣecayitānagham</li>
    </ul>
    <ul id="R_2.007.008">
        <li id="R_2.007.008ab">dhātryās tu vacanaṃ śrutvā kubjā kṣipram amarṣitā</li>
        <li id="R_2.007.008cd">kailāsa śikharākārāt prāsādād avarohata</li>
    </ul>
    <ul id="R_2.007.009">
        <li id="R_2.007.009ab">sā dahyamānā kopena mantharā pāpadarśinī</li>
        <li id="R_2.007.009cd">śayānām etya kaikeyīm idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.007.010">
        <li id="R_2.007.010ab">uttiṣṭha mūḍhe kiṃ śeṣe bhayaṃ tvām abhivartate</li>
        <li id="R_2.007.010cd">upaplutamahaughena kim ātmānaṃ na budhyase</li>
    </ul>
    <ul id="R_2.007.011">
        <li id="R_2.007.011ab">aniṣṭe subhagākāre saubhāgyena vikatthase</li>
        <li id="R_2.007.011cd">calaṃ hi tava saubhāgyaṃ nadyaḥ srota ivoṣṇage</li>
    </ul>
    <ul id="R_2.007.012">
        <li id="R_2.007.012ab">evam uktā tu kaikeyī ruṣṭayā paruṣaṃ vacaḥ</li>
        <li id="R_2.007.012cd">kubjayā pāpadarśinyā viṣādam agamat param</li>
    </ul>
    <ul id="R_2.007.013">
        <li id="R_2.007.013ab">kaikeyī tv abravīt kubjāṃ kaccit kṣemaṃ na manthare</li>
        <li id="R_2.007.013cd">viṣaṇṇavadanāṃ hi tvāṃ lakṣaye bhṛśaduḥkhitām</li>
    </ul>
    <ul id="R_2.007.014">
        <li id="R_2.007.014ab">mantharā tu vacaḥ śrutvā kaikeyyā madhurākṣaram</li>
        <li id="R_2.007.014cd">uvāca krodhasaṃyuktā vākyaṃ vākyaviśāradā</li>
    </ul>
    <ul id="R_2.007.015">
        <li id="R_2.007.015ab">sā viṣaṇṇatarā bhūtvā kubjā tasyā hitaiṣiṇī</li>
        <li id="R_2.007.015cd">viṣādayantī provāca bhedayantī ca rāghavam</li>
    </ul>
    <ul id="R_2.007.016">
        <li id="R_2.007.016ab">akṣemaṃ sumahad devi pravṛttaṃ tvadvināśanam</li>
        <li id="R_2.007.016cd">rāmaṃ daśaratho rājā yauvarājye 'bhiṣekṣyati</li>
    </ul>
    <ul id="R_2.007.017">
        <li id="R_2.007.017ab">sāsmy agādhe bhaye magnā duḥkhaśokasamanvitā</li>
        <li id="R_2.007.017cd">dahyamānānaleneva tvaddhitārtham ihāgatā</li>
    </ul>
    <ul id="R_2.007.018">
        <li id="R_2.007.018ab">tava duḥkhena kaikeyi mama duḥkhaṃ mahad bhavet</li>
        <li id="R_2.007.018cd">tvadvṛddhau mama vṛddhiś ca bhaved atra na saṃśayaḥ</li>
    </ul>
    <ul id="R_2.007.019">
        <li id="R_2.007.019ab">narādhipakule jātā mahiṣī tvaṃ mahīpateḥ</li>
        <li id="R_2.007.019cd">ugratvaṃ rājadharmāṇāṃ kathaṃ devi na budhyase</li>
    </ul>
    <ul id="R_2.007.020">
        <li id="R_2.007.020ab">dharmavādī śaṭho bhartā ślakṣṇavādī ca dāruṇaḥ</li>
        <li id="R_2.007.020cd">śuddhabhāve na jānīṣe tenaivam atisaṃdhitā</li>
    </ul>
    <ul id="R_2.007.021">
        <li id="R_2.007.021ab">upasthitaṃ prayuñjānas tvayi sāntvam anarthakam</li>
        <li id="R_2.007.021cd">arthenaivādya te bhartā kausalyāṃ yojayiṣyati</li>
    </ul>
    <ul id="R_2.007.022">
        <li id="R_2.007.022ab">apavāhya sa duṣṭātmā bharataṃ tava bandhuṣu</li>
        <li id="R_2.007.022cd">kālyaṃ sthāpayitā rāmaṃ rājye nihatakaṇṭake</li>
    </ul>
    <ul id="R_2.007.023">
        <li id="R_2.007.023ab">śatruḥ patipravādena mātreva hitakāmyayā</li>
        <li id="R_2.007.023cd">āśīviṣa ivāṅkena bāle paridhṛtas tvayā</li>
    </ul>
    <ul id="R_2.007.024">
        <li id="R_2.007.024ab">yathā hi kuryāt sarpo vā śatrur vā pratyupekṣitaḥ</li>
        <li id="R_2.007.024cd">rājñā daśarathenādya saputrā tvaṃ tathā kṛtā</li>
    </ul>
    <ul id="R_2.007.025">
        <li id="R_2.007.025ab">pāpenānṛtasāntvena bāle nityaṃ sukhocite</li>
        <li id="R_2.007.025cd">rāmaṃ sthāpayatā rājye sānubandhā hatā hy asi</li>
    </ul>
    <ul id="R_2.007.026">
        <li id="R_2.007.026ab">sā prāptakālaṃ kaikeyi kṣipraṃ kuru hitaṃ tava</li>
        <li id="R_2.007.026cd">trāyasva putram ātmānaṃ māṃ ca vismayadarśane</li>
    </ul>
    <ul id="R_2.007.027">
        <li id="R_2.007.027ab">mantharāyā vacaḥ śrutvā śayanāt sā śubhānanā</li>
        <li id="R_2.007.027cd">ekam ābharaṇaṃ tasyai kubjāyai pradadau śubham</li>
    </ul>
    <ul id="R_2.007.028">
        <li id="R_2.007.028ab">dattvā tv ābharaṇaṃ tasyai kubjāyai pramadottamā</li>
        <li id="R_2.007.028cd">kaikeyī mantharāṃ hṛṣṭā punar evābravīd idam</li>
    </ul>
    <ul id="R_2.007.029">
        <li id="R_2.007.029ab">idaṃ tu manthare mahyam ākhyāsi paramaṃ priyam</li>
        <li id="R_2.007.029cd">etan me priyam ākhyātuḥ kiṃ vā bhūyaḥ karomi te</li>
    </ul>
    <ul id="R_2.007.030">
        <li id="R_2.007.030ab">rāme vā bharate vāhaṃ viśeṣaṃ nopalakṣaye</li>
        <li id="R_2.007.030cd">tasmāt tuṣṭāsmi yad rājā rāmaṃ rājye 'bhiṣekṣyati</li>
    </ul>
    <ul id="R_2.007.031">
        <li id="R_2.007.031ab">
            <seg type="pāda" n="a">na me paraṃ kiṃ cid itas tvayā punaḥ</seg>
            <seg type="pāda" n="b">priyaṃ priyārhe suvacaṃ vaco varam</seg>
        </li>
        <li id="R_2.007.031cd">
            <seg type="pāda" n="c">tathā hy avocas tvam ataḥ priyottaraṃ</seg>
            <seg type="pāda" n="d">varaṃ paraṃ te pradadāmi taṃ vṛṇu</seg>
        </li>
    </ul>
    <ul id="R_2.008.001">
        <li id="R_2.008.001ab">mantharā tv abhyasūyyainām utsṛjyābharaṇaṃ ca tat</li>
        <li id="R_2.008.001cd">uvācedaṃ tato vākyaṃ kopaduḥkhasamanvitā</li>
    </ul>
    <ul id="R_2.008.002">
        <li id="R_2.008.002ab">harṣaṃ kim idam asthāne kṛtavaty asi bāliśe</li>
        <li id="R_2.008.002cd">śokasāgaramadhyastham ātmānaṃ nāvabudhyase</li>
    </ul>
    <ul id="R_2.008.003">
        <li id="R_2.008.003ab">subhagā khalu kausalyā yasyāḥ putro 'bhiṣekṣyate</li>
        <li id="R_2.008.003cd">yauvarājyena mahatā śvaḥ puṣyeṇa dvijottamaiḥ</li>
    </ul>
    <ul id="R_2.008.004">
        <li id="R_2.008.004ab">prāptāṃ sumahatīṃ prītiṃ pratītāṃ tāṃ hatadviṣam</li>
        <li id="R_2.008.004cd">upasthāsyasi kausalyāṃ dāsīva tvaṃ kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_2.008.005">
        <li id="R_2.008.005ab">hṛṣṭāḥ khalu bhaviṣyanti rāmasya paramāḥ striyaḥ</li>
        <li id="R_2.008.005cd">aprahṛṣṭā bhaviṣyanti snuṣās te bharatakṣaye</li>
    </ul>
    <ul id="R_2.008.006">
        <li id="R_2.008.006ab">tāṃ dṛṣṭvā paramaprītāṃ bruvantīṃ mantharāṃ tataḥ</li>
        <li id="R_2.008.006cd">rāmasyaiva guṇān devī kaikeyī praśaśaṃsa ha</li>
    </ul>
    <ul id="R_2.008.007">
        <li id="R_2.008.007ab">dharmajño gurubhir dāntaḥ kṛtajñaḥ satyavāk śuciḥ</li>
        <li id="R_2.008.007cd">rāmo rājñaḥ suto jyeṣṭho yauvarājyam ato 'rhati</li>
    </ul>
    <ul id="R_2.008.008">
        <li id="R_2.008.008ab">bhrātṝn bhṛtyāṃś ca dīrghāyuḥ pitṛvat pālayiṣyati</li>
        <li id="R_2.008.008cd">saṃtapyase kathaṃ kubje śrutvā rāmābhiṣecanam</li>
    </ul>
    <ul id="R_2.008.009">
        <li id="R_2.008.009ab">bharataś cāpi rāmasya dhruvaṃ varṣaśatāt param</li>
        <li id="R_2.008.009cd">pitṛpaitāmahaṃ rājyam avāpsyati nararṣabhaḥ</li>
    </ul>
    <ul id="R_2.008.010">
        <li id="R_2.008.010ab">sā tvam abhyudaye prāpte vartamāne ca manthare</li>
        <li id="R_2.008.010cd">bhaviṣyati ca kalyāṇe kimarthaṃ paritapyase</li>
        <li id="R_2.008.010ef">kausalyāto 'tiriktaṃ ca sa tu śuśrūṣate hi mām</li>
    </ul>
    <ul id="R_2.008.011">
        <li id="R_2.008.011ab">kaikeyyā vacanaṃ śrutvā mantharā bhṛśaduḥkhitā</li>
        <li id="R_2.008.011cd">dīrgham uṣṇaṃ viniḥśvasya kaikeyīm idam abravīt</li>
    </ul>
    <ul id="R_2.008.012">
        <li id="R_2.008.012ab">anarthadarśinī maurkhyān nātmānam avabudhyase</li>
        <li id="R_2.008.012cd">śokavyasanavistīrṇe majjantī duḥkhasāgare</li>
    </ul>
    <ul id="R_2.008.013">
        <li id="R_2.008.013ab">bhavitā rāghavo rājā rāghavasya ca yaḥ sutaḥ</li>
        <li id="R_2.008.013cd">rājavaṃśāt tu bharataḥ kaikeyi parihāsyate</li>
    </ul>
    <ul id="R_2.008.014">
        <li id="R_2.008.014ab">na hi rājñaḥ sutāḥ sarve rājye tiṣṭhanti bhāmini</li>
        <li id="R_2.008.014cd">sthāpyamāneṣu sarveṣu sumahān anayo bhavet</li>
    </ul>
    <ul id="R_2.008.015">
        <li id="R_2.008.015ab">tasmāj jyeṣṭhe hi kaikeyi rājyatantrāṇi pārthivāḥ</li>
        <li id="R_2.008.015cd">sthāpayanty anavadyāṅgi guṇavatsv itareṣv api</li>
    </ul>
    <ul id="R_2.008.016">
        <li id="R_2.008.016ab">asāv atyantanirbhagnas tava putro bhaviṣyati</li>
        <li id="R_2.008.016cd">anāthavat sukhebhyaś ca rājavaṃśāc ca vatsale</li>
    </ul>
    <ul id="R_2.008.017">
        <li id="R_2.008.017ab">sāhaṃ tvadarthe saṃprāptā tvaṃ tu māṃ nāvabudhyase</li>
        <li id="R_2.008.017cd">sapatnivṛddhau yā me tvaṃ pradeyaṃ dātum icchasi</li>
    </ul>
    <ul id="R_2.008.018">
        <li id="R_2.008.018ab">dhruvaṃ tu bharataṃ rāmaḥ prāpya rājyam akaṇṭakam</li>
        <li id="R_2.008.018cd">deśāntaraṃ nāyayitā lokāntaram athāpi vā</li>
    </ul>
    <ul id="R_2.008.019">
        <li id="R_2.008.019ab">bāla eva hi mātulyaṃ bharato nāyitas tvayā</li>
        <li id="R_2.008.019cd">saṃnikarṣāc ca sauhārdaṃ jāyate sthāvareṣv api</li>
    </ul>
    <ul id="R_2.008.020">
        <li id="R_2.008.020ab">goptā hi rāmaṃ saumitrir lakṣmaṇaṃ cāpi rāghavaḥ</li>
        <li id="R_2.008.020cd">aśvinor iva saubhrātraṃ tayor lokeṣu viśrutam</li>
    </ul>
    <ul id="R_2.008.021">
        <li id="R_2.008.021ab">tasmān na lakṣmaṇe rāmaḥ pāpaṃ kiṃ cit kariṣyati</li>
        <li id="R_2.008.021cd">rāmas tu bharate pāpaṃ kuryād iti na saṃśayaḥ</li>
    </ul>
    <ul id="R_2.008.022">
        <li id="R_2.008.022ab">tasmād rājagṛhād eva vanaṃ gacchatu te sutaḥ</li>
        <li id="R_2.008.022cd">etad dhi rocate mahyaṃ bhṛśaṃ cāpi hitaṃ tava</li>
    </ul>
    <ul id="R_2.008.023">
        <li id="R_2.008.023ab">evaṃ te jñātipakṣasya śreyaś caiva bhaviṣyati</li>
        <li id="R_2.008.023cd">yadi ced bharato dharmāt pitryaṃ rājyam avāpsyati</li>
    </ul>
    <ul id="R_2.008.024">
        <li id="R_2.008.024ab">sa te sukhocito bālo rāmasya sahajo ripuḥ</li>
        <li id="R_2.008.024cd">samṛddhārthasya naṣṭārtho jīviṣyati kathaṃ vaśe</li>
    </ul>
    <ul id="R_2.008.025">
        <li id="R_2.008.025ab">abhidrutam ivāraṇye siṃhena gajayūthapam</li>
        <li id="R_2.008.025cd">pracchādyamānaṃ rāmeṇa bharataṃ trātum arhasi</li>
    </ul>
    <ul id="R_2.008.026">
        <li id="R_2.008.026ab">darpān nirākṛtā pūrvaṃ tvayā saubhāgyavattayā</li>
        <li id="R_2.008.026cd">rāmamātā sapatnī te kathaṃ vairaṃ na yātayet</li>
    </ul>
    <ul id="R_2.008.027">
        <li id="R_2.008.027ab">
            <seg type="pāda" n="a">yadā hi rāmaḥ pṛthivīm avāpsyati</seg>
            <seg type="pāda" n="b">dhruvaṃ pranaṣṭo bharato bhaviṣyati</seg>
        </li>
        <li id="R_2.008.027cd">
            <seg type="pāda" n="c">ato hi saṃcintaya rājyam ātmaje</seg>
            <seg type="pāda" n="d">parasya cādyaiva vivāsakāraṇam</seg>
        </li>
    </ul>
    <ul id="R_2.009.001">
        <li id="R_2.009.001ab">evam uktā tu kaikeyī krodhena jvalitānanā</li>
        <li id="R_2.009.001cd">dīrgham uṣṇaṃ viniḥśvasya mantharām idam abravīt</li>
    </ul>
    <ul id="R_2.009.002">
        <li id="R_2.009.002ab">adya rāmam itaḥ kṣipraṃ vanaṃ prasthāpayāmy aham</li>
        <li id="R_2.009.002cd">yauvarājyena bharataṃ kṣipram evābhiṣecaye</li>
    </ul>
    <ul id="R_2.009.003">
        <li id="R_2.009.003ab">idaṃ tv idānīṃ saṃpaśya kenopāyena manthare</li>
        <li id="R_2.009.003cd">bharataḥ prāpnuyād rājyaṃ na tu rāmaḥ kathaṃ cana</li>
    </ul>
    <ul id="R_2.009.004">
        <li id="R_2.009.004ab">evam uktā tayā devyā mantharā pāpadarśinī</li>
        <li id="R_2.009.004cd">rāmārtham upahiṃsantī kaikeyīm idam abravīt</li>
    </ul>
    <ul id="R_2.009.005">
        <li id="R_2.009.005ab">hantedānīṃ pravakṣyāmi kaikeyi śrūyatāṃ ca me</li>
        <li id="R_2.009.005cd">yathā te bharato rājyaṃ putraḥ prāpsyati kevalam</li>
    </ul>
    <ul id="R_2.009.006">
        <li id="R_2.009.006ab">śrutvaivaṃ vacanaṃ tasyā mantharāyās tu kaikayī</li>
        <li id="R_2.009.006cd">kiṃ cid utthāya śayanāt svāstīrṇād idam abravīt</li>
    </ul>
    <ul id="R_2.009.007">
        <li id="R_2.009.007ab">kathaya tvaṃ mamopāyaṃ kenopāyena manthare</li>
        <li id="R_2.009.007cd">bharataḥ prāpnuyād rājyaṃ na tu rāmaḥ kathaṃ cana</li>
    </ul>
    <ul id="R_2.009.008">
        <li id="R_2.009.008ab">evam uktā tayā devyā mantharā pāpadarśinī</li>
        <li id="R_2.009.008cd">rāmārtham upahiṃsantī kubjā vacanam abravīt</li>
    </ul>
    <ul id="R_2.009.009">
        <li id="R_2.009.009ab">tava devāsure yuddhe saha rājarṣibhiḥ patiḥ</li>
        <li id="R_2.009.009cd">agacchat tvām upādāya devarājasya sāhyakṛt</li>
    </ul>
    <ul id="R_2.009.010">
        <li id="R_2.009.010ab">diśam āsthāya kaikeyi dakṣiṇāṃ daṇḍakān prati</li>
        <li id="R_2.009.010cd">vaijayantam iti khyātaṃ puraṃ yatra timidhvajaḥ</li>
    </ul>
    <ul id="R_2.009.011">
        <li id="R_2.009.011ab">sa śambara iti khyātaḥ śatamāyo mahāsuraḥ</li>
        <li id="R_2.009.011cd">dadau śakrasya saṃgrāmaṃ devasaṃghair anirjitaḥ</li>
    </ul>
    <ul id="R_2.009.012">
        <li id="R_2.009.012ab">tasmin mahati saṃgrāme rājā daśarathas tadā</li>
        <li id="R_2.009.012cd">apavāhya tvayā devi saṃgrāmān naṣṭacetanaḥ</li>
    </ul>
    <ul id="R_2.009.013">
        <li id="R_2.009.013ab">tatrāpi vikṣataḥ śastraiḥ patis te rakṣitas tvayā</li>
        <li id="R_2.009.013cd">tuṣṭena tena dattau te dvau varau śubhadarśane</li>
    </ul>
    <ul id="R_2.009.014">
        <li id="R_2.009.014ab">sa tvayoktaḥ patir devi yadeccheyaṃ tadā varau</li>
        <li id="R_2.009.014cd">gṛhṇīyām iti tat tena tathety uktaṃ mahātmanā</li>
        <li id="R_2.009.014ef">anabhijñā hy ahaṃ devi tvayaiva kathitaṃ purā</li>
    </ul>
    <ul id="R_2.009.015">
        <li id="R_2.009.015ab">tau varau yāca bhartāraṃ bharatasyābhiṣecanam</li>
        <li id="R_2.009.015cd">pravrājanaṃ ca rāmasya tvaṃ varṣāṇi caturdaśa</li>
    </ul>
    <ul id="R_2.009.016">
        <li id="R_2.009.016ab">krodhāgāraṃ praviśyādya kruddhevāśvapateḥ sute</li>
        <li id="R_2.009.016cd">śeṣvānantarhitāyāṃ tvaṃ bhūmau malinavāsinī</li>
        <li id="R_2.009.016ef">mā smainaṃ pratyudīkṣethā mā cainam abhibhāṣathāḥ</li>
    </ul>
    <ul id="R_2.009.017">
        <li id="R_2.009.017ab">dayitā tvaṃ sadā bhartur atra me nāsti saṃśayaḥ</li>
        <li id="R_2.009.017cd">tvatkṛte ca mahārājo viśed api hutāśanam</li>
    </ul>
    <ul id="R_2.009.018">
        <li id="R_2.009.018ab">na tvāṃ krodhayituṃ śakto na kruddhāṃ pratyudīkṣitum</li>
        <li id="R_2.009.018cd">tava priyārthaṃ rājā hi prāṇān api parityajet</li>
    </ul>
    <ul id="R_2.009.019">
        <li id="R_2.009.019ab">na hy atikramituṃ śaktas tava vākyaṃ mahīpatiḥ</li>
        <li id="R_2.009.019cd">mandasvabhāve budhyasva saubhāgyabalam ātmanaḥ</li>
    </ul>
    <ul id="R_2.009.020">
        <li id="R_2.009.020ab">maṇimuktāsuvarṇāni ratnāni vividhāni ca</li>
        <li id="R_2.009.020cd">dadyād daśaratho rājā mā sma teṣu manaḥ kṛthāḥ</li>
    </ul>
    <ul id="R_2.009.021">
        <li id="R_2.009.021ab">yau tau devāsure yuddhe varau daśaratho 'dadāt</li>
        <li id="R_2.009.021cd">tau smāraya mahābhāge so 'rtho mā tvām atikramet</li>
    </ul>
    <ul id="R_2.009.022">
        <li id="R_2.009.022ab">yadā tu te varaṃ dadyāt svayam utthāpya rāghavaḥ</li>
        <li id="R_2.009.022cd">vyavasthāpya mahārājaṃ tvam imaṃ vṛṇuyā varam</li>
    </ul>
    <ul id="R_2.009.023">
        <li id="R_2.009.023ab">rāmaṃ pravrājayāraṇye nava varṣāṇi pañca ca</li>
        <li id="R_2.009.023cd">bharataḥ kriyatāṃ rājā pṛthivyāṃ pārthivarṣabhaḥ</li>
    </ul>
    <ul id="R_2.009.024">
        <li id="R_2.009.024ab">evaṃ pravrājitaś caiva rāmo 'rāmo bhaviṣyati</li>
        <li id="R_2.009.024cd">bharataś ca hatāmitras tava rājā bhaviṣyati</li>
    </ul>
    <ul id="R_2.009.025">
        <li id="R_2.009.025ab">yena kālena rāmaś ca vanāt pratyāgamiṣyati</li>
        <li id="R_2.009.025cd">tena kālena putras te kṛtamūlo bhaviṣyati</li>
        <li id="R_2.009.025ef">saṃgṛhītamanuṣyaś ca suhṛdbhiḥ sārdham ātmavān</li>
    </ul>
    <ul id="R_2.009.026">
        <li id="R_2.009.026ab">prāptakālaṃ tu te manye rājānaṃ vītasādhvasā</li>
        <li id="R_2.009.026cd">rāmābhiṣekasaṃkalpān nigṛhya vinivartaya</li>
    </ul>
    <ul id="R_2.009.027">
        <li id="R_2.009.027ab">anartham artharūpeṇa grāhitā sā tatas tayā</li>
        <li id="R_2.009.027cd">hṛṣṭā pratītā kaikeyī mantharām idam abravīt</li>
    </ul>
    <ul id="R_2.009.028">
        <li id="R_2.009.028ab">kubje tvāṃ nābhijānāmi śreṣṭhāṃ śreṣṭhābhidhāyinīm</li>
        <li id="R_2.009.028cd">pṛthivyām asi kubjānām uttamā buddhiniścaye</li>
    </ul>
    <ul id="R_2.009.029">
        <li id="R_2.009.029ab">tvam eva tu mamārtheṣu nityayuktā hitaiṣiṇī</li>
        <li id="R_2.009.029cd">nāhaṃ samavabudhyeyaṃ kubje rājñaś cikīrṣitam</li>
    </ul>
    <ul id="R_2.009.030">
        <li id="R_2.009.030ab">santi duḥsaṃsthitāḥ kubjā vakrāḥ paramapāpikāḥ</li>
        <li id="R_2.009.030cd">tvaṃ padmam iva vātena saṃnatā priyadarśanā</li>
    </ul>
    <ul id="R_2.009.031">
        <li id="R_2.009.031ab">uras te 'bhiniviṣṭaṃ vai yāvat skandhāt samunnatam</li>
        <li id="R_2.009.031cd">adhastāc codaraṃ śāntaṃ sunābham iva lajjitam</li>
    </ul>
    <ul id="R_2.009.032">
        <li id="R_2.009.032ab">jaghanaṃ tava nirghuṣṭaṃ raśanādāmaśobhitam</li>
        <li id="R_2.009.032cd">jaṅghe bhṛśam upanyaste pādau cāpy āyatāv ubhau</li>
    </ul>
    <ul id="R_2.009.033">
        <li id="R_2.009.033ab">tvam āyatābhyāṃ sakthibhyāṃ manthare kṣaumavāsini</li>
        <li id="R_2.009.033cd">agrato mama gacchantī rājahaṃsīva rājase</li>
    </ul>
    <ul id="R_2.009.034">
        <li id="R_2.009.034ab">tavedaṃ sthagu yad dīrghaṃ rathaghoṇam ivāyatam</li>
        <li id="R_2.009.034cd">matayaḥ kṣatravidyāś ca māyāś cātra vasanti te</li>
    </ul>
    <ul id="R_2.009.035">
        <li id="R_2.009.035ab">atra te pratimokṣyāmi mālāṃ kubje hiraṇmayīm</li>
        <li id="R_2.009.035cd">abhiṣikte ca bharate rāghave ca vanaṃ gate</li>
    </ul>
    <ul id="R_2.009.036">
        <li id="R_2.009.036ab">jātyena ca suvarṇena suniṣṭaptena sundari</li>
        <li id="R_2.009.036cd">labdhārthā ca pratītā ca lepayiṣyāmi te sthagu</li>
    </ul>
    <ul id="R_2.009.037">
        <li id="R_2.009.037ab">mukhe ca tilakaṃ citraṃ jātarūpamayaṃ śubham</li>
        <li id="R_2.009.037cd">kārayiṣyāmi te kubje śubhāny ābharaṇāni ca</li>
    </ul>
    <ul id="R_2.009.038">
        <li id="R_2.009.038ab">paridhāya śubhe vastre devadeva cariṣyasi</li>
        <li id="R_2.009.038cd">candram āhvayamānena mukhenāpratimānanā</li>
        <li id="R_2.009.038ef">gamiṣyasi gatiṃ mukhyāṃ garvayantī dviṣajjanam</li>
    </ul>
    <ul id="R_2.009.039">
        <li id="R_2.009.039ab">tavāpi kubjāḥ kubjāyāḥ sarvābharaṇabhūṣitāḥ</li>
        <li id="R_2.009.039cd">pādau paricariṣyanti yathaiva tvaṃ sadā mama</li>
    </ul>
    <ul id="R_2.009.040">
        <li id="R_2.009.040ab">iti praśasyamānā sā kaikeyīm idam abravīt</li>
        <li id="R_2.009.040cd">śayānāṃ śayane śubhre vedyām agniśikhām iva</li>
    </ul>
    <ul id="R_2.009.041">
        <li id="R_2.009.041ab">gatodake setubandho na kalyāṇi vidhīyate</li>
        <li id="R_2.009.041cd">uttiṣṭha kuru kalyāṇaṃ rājānam anudarśaya</li>
    </ul>
    <ul id="R_2.009.042">
        <li id="R_2.009.042ab">tathā protsāhitā devī gatvā mantharayā saha</li>
        <li id="R_2.009.042cd">krodhāgāraṃ viśālākṣī saubhāgyamadagarvitā</li>
    </ul>
    <ul id="R_2.009.043">
        <li id="R_2.009.043ab">anekaśatasāhasraṃ muktāhāraṃ varāṅganā</li>
        <li id="R_2.009.043cd">avamucya varārhāṇi śubhāny ābharaṇāni ca</li>
    </ul>
    <ul id="R_2.009.044">
        <li id="R_2.009.044ab">tato hemopamā tatra kubjā vākyaṃ vaśaṃ gatā</li>
        <li id="R_2.009.044cd">saṃviśya bhūmau kaikeyī mantharām idam abravīt</li>
    </ul>
    <ul id="R_2.009.045">
        <li id="R_2.009.045ab">iha vā māṃ mṛtāṃ kubje nṛpāyāvedayiṣyasi</li>
        <li id="R_2.009.045cd">vanaṃ tu rāghave prāpte bharataḥ prāpsyati kṣitim</li>
    </ul>
    <ul id="R_2.009.046">
        <li id="R_2.009.046ab">
            <seg type="pāda" n="a">athaitad uktvā vacanaṃ sudāruṇaṃ</seg>
            <seg type="pāda" n="b">nidhāya sarvābharaṇāni bhāminī</seg>
        </li>
        <li id="R_2.009.046cd">
            <seg type="pāda" n="c">asaṃvṛtām āstaraṇena medinīṃ</seg>
            <seg type="pāda" n="d">tadādhiśiśye patiteva kinnarī</seg>
        </li>
    </ul>
    <ul id="R_2.009.047">
        <li id="R_2.009.047ab">
            <seg type="pāda" n="a">udīrṇasaṃrambhatamovṛtānanā</seg>
            <seg type="pāda" n="b">tathāvamuktottamamālyabhūṣaṇā</seg>
        </li>
        <li id="R_2.009.047cd">
            <seg type="pāda" n="c">narendrapatnī vimanā babhūva sā</seg>
            <seg type="pāda" n="d">tamovṛtā dyaur iva magnatārakā</seg>
        </li>
    </ul>
    <ul id="R_2.010.001">
        <li id="R_2.010.001ab">ājñāpya tu mahārājo rāghavasyābhiṣecanam</li>
        <li id="R_2.010.001cd">priyārhāṃ priyam ākhyātuṃ viveśāntaḥpuraṃ vaśī</li>
    </ul>
    <ul id="R_2.010.002">
        <li id="R_2.010.002ab">tāṃ tatra patitāṃ bhūmau śayānām atathocitām</li>
        <li id="R_2.010.002cd">pratapta iva duḥkhena so 'paśyaj jagatīpatiḥ</li>
    </ul>
    <ul id="R_2.010.003">
        <li id="R_2.010.003ab">sa vṛddhas taruṇīṃ bhāryāṃ prāṇebhyo 'pi garīyasīm</li>
        <li id="R_2.010.003cd">apāpaḥ pāpasaṃkalpāṃ dadarśa dharaṇītale</li>
    </ul>
    <ul id="R_2.010.004">
        <li id="R_2.010.004ab">kareṇum iva digdhena viddhāṃ mṛgayuṇā vane</li>
        <li id="R_2.010.004cd">mahāgaja ivāraṇye snehāt parimamarśa tām</li>
    </ul>
    <ul id="R_2.010.005">
        <li id="R_2.010.005ab">parimṛśya ca pāṇibhyām abhisaṃtrastacetanaḥ</li>
        <li id="R_2.010.005cd">kāmī kamalapatrākṣīm uvāca vanitām idam</li>
    </ul>
    <ul id="R_2.010.006">
        <li id="R_2.010.006ab">na te 'ham abhijānāmi krodham ātmani saṃśritam</li>
        <li id="R_2.010.006cd">devi kenābhiyuktāsi kena vāsi vimānitā</li>
    </ul>
    <ul id="R_2.010.007">
        <li id="R_2.010.007ab">yad idaṃ mama duḥkhāya śeṣe kalyāṇi pāṃsuṣu</li>
        <li id="R_2.010.007cd">bhūmau śeṣe kimarthaṃ tvaṃ mayi kalyāṇa cetasi</li>
        <li id="R_2.010.007ef">bhūtopahatacitteva mama cittapramāthinī</li>
    </ul>
    <ul id="R_2.010.008">
        <li id="R_2.010.008ab">santi me kuśalā vaidyā abhituṣṭāś ca sarvaśaḥ</li>
        <li id="R_2.010.008cd">sukhitāṃ tvāṃ kariṣyanti vyādhim ācakṣva bhāmini</li>
    </ul>
    <ul id="R_2.010.009">
        <li id="R_2.010.009ab">kasya vā te priyaṃ kāryaṃ kena vā vipriyaṃ kṛtam</li>
        <li id="R_2.010.009cd">kaḥ priyaṃ labhatām adya ko vā sumahad apriyam</li>
    </ul>
    <ul id="R_2.010.010">
        <li id="R_2.010.010ab">avadhyo vadhyatāṃ ko vā vadhyaḥ ko vā vimucyatām</li>
        <li id="R_2.010.010cd">daridraḥ ko bhavatv āḍhyo dravyavān vāpy akiṃcanaḥ</li>
    </ul>
    <ul id="R_2.010.011">
        <li id="R_2.010.011ab">ahaṃ caiva madīyāś ca sarve tava vaśānugāḥ</li>
        <li id="R_2.010.011cd">na te kaṃ cid abhiprāyaṃ vyāhantum aham utsahe</li>
    </ul>
    <ul id="R_2.010.012">
        <li id="R_2.010.012ab">ātmano jīvitenāpi brūhi yan manasecchasi</li>
        <li id="R_2.010.012cd">yāvad āvartate cakraṃ tāvatī me vasuṃdharā</li>
    </ul>
    <ul id="R_2.010.013">
        <li id="R_2.010.013ab">tathoktā sā samāśvastā vaktukāmā tad apriyam</li>
        <li id="R_2.010.013cd">paripīḍayituṃ bhūyo bhartāram upacakrame</li>
    </ul>
    <ul id="R_2.010.014">
        <li id="R_2.010.014ab">nāsmi viprakṛtā deva kena cin na vimānitā</li>
        <li id="R_2.010.014cd">abhiprāyas tu me kaś cit tam icchāmi tvayā kṛtam</li>
    </ul>
    <ul id="R_2.010.015">
        <li id="R_2.010.015ab">pratijñāṃ pratijānīṣva yadi tvaṃ kartum icchasi</li>
        <li id="R_2.010.015cd">atha tad vyāhariṣyāmi yad abhiprārthitaṃ mayā</li>
    </ul>
    <ul id="R_2.010.016">
        <li id="R_2.010.016ab">evam uktas tayā rājā priyayā strīvaśaṃ gataḥ</li>
        <li id="R_2.010.016cd">tām uvāca mahātejāḥ kaikeyīm īṣadutsmitaḥ</li>
    </ul>
    <ul id="R_2.010.017">
        <li id="R_2.010.017ab">avalipte na jānāsi tvattaḥ priyataro mama</li>
        <li id="R_2.010.017cd">manujo manujavyāghrād rāmād anyo na vidyate</li>
    </ul>
    <ul id="R_2.010.018">
        <li id="R_2.010.018ab">bhadre hṛdayam apy etad anumṛśśyoddharasva me</li>
        <li id="R_2.010.018cd">etat samīkṣya kaikeyi brūhi yat sādhu manyase</li>
    </ul>
    <ul id="R_2.010.019">
        <li id="R_2.010.019ab">balam ātmani paśyantī na māṃ śaṅkitum arhasi</li>
        <li id="R_2.010.019cd">kariṣyāmi tava prītiṃ sukṛtenāpi te śape</li>
    </ul>
    <ul id="R_2.010.020">
        <li id="R_2.010.020ab">tena vākyena saṃhṛṣṭā tam abhiprāyam ātmanaḥ</li>
        <li id="R_2.010.020cd">vyājahāra mahāghoram abhyāgatam ivāntakam</li>
    </ul>
    <ul id="R_2.010.021">
        <li id="R_2.010.021ab">yathākrameṇa śapasi varaṃ mama dadāsi ca</li>
        <li id="R_2.010.021cd">tac chṛṇvantu trayastriṃśad devāḥ sendrapurogamāḥ</li>
    </ul>
    <ul id="R_2.010.022">
        <li id="R_2.010.022ab">candrādityau nabhaś caiva grahā rātryahanī diśaḥ</li>
        <li id="R_2.010.022cd">jagac ca pṛthivī caiva sagandharvā sarākṣasā</li>
    </ul>
    <ul id="R_2.010.023">
        <li id="R_2.010.023ab">niśācarāṇi bhūtāni gṛheṣu gṛhadevatāḥ</li>
        <li id="R_2.010.023cd">yāni cānyāni bhūtāni jānīyur bhāṣitaṃ tava</li>
    </ul>
    <ul id="R_2.010.024">
        <li id="R_2.010.024ab">satyasaṃdho mahātejā dharmajñaḥ susamāhitaḥ</li>
        <li id="R_2.010.024cd">varaṃ mama dadāty eṣa tan me śṛṇvantu devatāḥ</li>
    </ul>
    <ul id="R_2.010.025">
        <li id="R_2.010.025ab">iti devī maheṣvāsaṃ parigṛhyābhiśasya ca</li>
        <li id="R_2.010.025cd">tataḥ param uvācedaṃ varadaṃ kāmamohitam</li>
    </ul>
    <ul id="R_2.010.026">
        <li id="R_2.010.026ab">varau yau me tvayā deva tadā dattau mahīpate</li>
        <li id="R_2.010.026cd">tau tāvad aham adyaiva vakṣyāmi śṛṇu me vacaḥ</li>
    </ul>
    <ul id="R_2.010.027">
        <li id="R_2.010.027ab">abhiṣeka samārambho rāghavasyopakalpitaḥ</li>
        <li id="R_2.010.027cd">anenaivābhiṣekeṇa bharato me 'bhiṣicyatām</li>
    </ul>
    <ul id="R_2.010.028">
        <li id="R_2.010.028ab">nava pañca ca varṣāṇi daṇḍakāraṇyam āśritaḥ</li>
        <li id="R_2.010.028cd">cīrājinajaṭādhārī rāmo bhavatu tāpasaḥ</li>
    </ul>
    <ul id="R_2.010.029">
        <li id="R_2.010.029ab">bharato bhajatām adya yauvarājyam akaṇṭakam</li>
        <li id="R_2.010.029cd">adya caiva hi paśyeyaṃ prayāntaṃ rāghavaṃ vane</li>
    </ul>
    <ul id="R_2.010.030">
        <li id="R_2.010.030ab">tataḥ śrutvā mahārājaḥ kaikeyyā dāruṇaṃ vacaḥ</li>
        <li id="R_2.010.030cd">vyathito viklavaś caiva vyāghrīṃ dṛṣṭvā yathā mṛgaḥ</li>
    </ul>
    <ul id="R_2.010.031">
        <li id="R_2.010.031ab">asaṃvṛtāyām āsīno jagatyāṃ dīrgham ucchvasan</li>
        <li id="R_2.010.031cd">aho dhig iti sāmarṣo vācam uktvā narādhipaḥ</li>
        <li id="R_2.010.031ef">moham āpedivān bhūyaḥ śokopahatacetanaḥ</li>
    </ul>
    <ul id="R_2.010.032">
        <li id="R_2.010.032ab">cireṇa tu nṛpaḥ saṃjñāṃ pratilabhya suduḥkhitaḥ</li>
        <li id="R_2.010.032cd">kaikeyīm abravīt kruddhaḥ pradahann iva cakṣuṣā</li>
    </ul>
    <ul id="R_2.010.033">
        <li id="R_2.010.033ab">nṛśaṃse duṣṭacāritre kulasyāsya vināśini</li>
        <li id="R_2.010.033cd">kiṃ kṛtaṃ tava rāmeṇa pāpe pāpaṃ mayāpi vā</li>
    </ul>
    <ul id="R_2.010.034">
        <li id="R_2.010.034ab">sadā te jananī tulyāṃ vṛttiṃ vahati rāghavaḥ</li>
        <li id="R_2.010.034cd">tasyaiva tvam anarthāya kiṃnimittam ihodyatā</li>
    </ul>
    <ul id="R_2.010.035">
        <li id="R_2.010.035ab">tvaṃ mayātmavināśāya bhavanaṃ svaṃ praveśitā</li>
        <li id="R_2.010.035cd">avijñānān nṛpasutā vyālī tīkṣṇaviṣā yathā</li>
    </ul>
    <ul id="R_2.010.036">
        <li id="R_2.010.036ab">jīvaloko yadā sarvo rāmasyeha guṇastavam</li>
        <li id="R_2.010.036cd">aparādhaṃ kam uddiśya tyakṣyāmīṣṭam ahaṃ sutam</li>
    </ul>
    <ul id="R_2.010.037">
        <li id="R_2.010.037ab">kausalyāṃ vā sumitrāṃ vā tyajeyam api vā śriyam</li>
        <li id="R_2.010.037cd">jīvitaṃ vātmano rāmaṃ na tv eva pitṛvatsalam</li>
    </ul>
    <ul id="R_2.010.038">
        <li id="R_2.010.038ab">parā bhavati me prītir dṛṣṭvā tanayam agrajam</li>
        <li id="R_2.010.038cd">apaśyatas tu me rāmaṃ naṣṭā bhavati cetanā</li>
    </ul>
    <ul id="R_2.010.039">
        <li id="R_2.010.039ab">tiṣṭhel loko vinā sūryaṃ sasyaṃ vā salilaṃ vinā</li>
        <li id="R_2.010.039cd">na tu rāmaṃ vinā dehe tiṣṭhet tu mama jīvitam</li>
    </ul>
    <ul id="R_2.010.040">
        <li id="R_2.010.040ab">tad alaṃ tyajyatām eṣa niścayaḥ pāpaniścaye</li>
        <li id="R_2.010.040cd">api te caraṇau mūrdhnā spṛśāmy eṣa prasīda me</li>
    </ul>
    <ul id="R_2.010.041">
        <li id="R_2.010.041ab">
            <seg type="pāda" n="a">sa bhūmipālo vilapann anāthavat</seg>
            <seg type="pāda" n="b">striyā gṛhīto dṛhaye 'timātratā</seg>
        </li>
        <li id="R_2.010.041cd">
            <seg type="pāda" n="c">papāta devyāś caraṇau prasāritāv</seg>
            <seg type="pāda" n="d">ubhāv asaṃspṛśya yathāturas tathā</seg>
        </li>
    </ul>
    <ul id="R_2.011.001">
        <li id="R_2.011.001ab">atadarhaṃ mahārājaṃ śayānam atathocitam</li>
        <li id="R_2.011.001cd">yayātim iva puṇyānte devalokāt paricyutam</li>
    </ul>
    <ul id="R_2.011.002">
        <li id="R_2.011.002ab">anartharūpā siddhārthā abhītā bhayadarśinī</li>
        <li id="R_2.011.002cd">punar ākārayām āsa tam eva varam aṅganā</li>
    </ul>
    <ul id="R_2.011.003">
        <li id="R_2.011.003ab">tvaṃ katthase mahārāja satyavādī dṛḍhavrataḥ</li>
        <li id="R_2.011.003cd">mama cemaṃ varaṃ kasmād vidhārayitum icchasi</li>
    </ul>
    <ul id="R_2.011.004">
        <li id="R_2.011.004ab">evam uktas tu kaikeyyā rājā daśarathas tadā</li>
        <li id="R_2.011.004cd">pratyuvāca tataḥ kruddho muhūrtaṃ vihvalann iva</li>
    </ul>
    <ul id="R_2.011.005">
        <li id="R_2.011.005ab">mṛte mayi gate rāme vanaṃ manujapuṃgave</li>
        <li id="R_2.011.005cd">hantānārye mamāmitre rāmaḥ pravrājito vanam</li>
    </ul>
    <ul id="R_2.011.006">
        <li id="R_2.011.006ab">yadi satyaṃ bravīmy etat tad asatyaṃ bhaviṣyati</li>
        <li id="R_2.011.006cd">akīrtir atulā loke dhruvaṃ paribhavaś ca me</li>
    </ul>
    <ul id="R_2.011.007">
        <li id="R_2.011.007ab">tathā vilapatas tasya paribhramitacetasaḥ</li>
        <li id="R_2.011.007cd">astam abhyagamat sūryo rajanī cābhyavartata</li>
    </ul>
    <ul id="R_2.011.008">
        <li id="R_2.011.008ab">sa triyāmā tathārtasya candramaṇḍalamaṇḍitā</li>
        <li id="R_2.011.008cd">rājño vilapamānasya na vyabhāsata śarvarī</li>
    </ul>
    <ul id="R_2.011.009">
        <li id="R_2.011.009ab">tathaivoṣṇaṃ viniḥśvasya vṛddho daśaratho nṛpaḥ</li>
        <li id="R_2.011.009cd">vilalāpārtavad duḥkhaṃ gaganāsaktalocanaḥ</li>
    </ul>
    <ul id="R_2.011.010">
        <li id="R_2.011.010ab">na prabhātaṃ tvayecchāmi mayāyaṃ racito 'ñjaliḥ</li>
        <li id="R_2.011.010cd">atha vā gamyatāṃ śīghraṃ nāham icchāmi nirghṛṇām</li>
        <li id="R_2.011.010ef">nṛśaṃsāṃ kaikeyīṃ draṣṭuṃ yatkṛte vyasanaṃ mahat</li>
    </ul>
    <ul id="R_2.011.011">
        <li id="R_2.011.011ab">evam uktvā tato rājā kaikeyīṃ saṃyatāñjaliḥ</li>
        <li id="R_2.011.011cd">prasādayām āsa punaḥ kaikeyīṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.011.012">
        <li id="R_2.011.012ab">sādhuvṛttasya dīnasya tvadgatasya gatāyuṣaḥ</li>
        <li id="R_2.011.012cd">prasādaḥ kriyatāṃ devi bhadre rājño viśeṣataḥ</li>
    </ul>
    <ul id="R_2.011.013">
        <li id="R_2.011.013ab">śūnyena khalu suśroṇi mayedaṃ samudāhṛtam</li>
        <li id="R_2.011.013cd">kuru sādhu prasādaṃ me bāle sahṛdayā hy asi</li>
    </ul>
    <ul id="R_2.011.014">
        <li id="R_2.011.014ab">
            <seg type="pāda" n="a">viśuddhabhāvasya hi duṣṭabhāvā</seg>
            <seg type="pāda" n="b">tāmrekṣaṇasyāśrukalasya rājñaḥ</seg>
        </li>
        <li id="R_2.011.014cd">
            <seg type="pāda" n="c">śrutvā vicitraṃ karuṇaṃ vilāpaṃ</seg>
            <seg type="pāda" n="d">bhartur nṛśaṃsā na cakāra vākyam</seg>
        </li>
    </ul>
    <ul id="R_2.011.015">
        <li id="R_2.011.015ab">
            <seg type="pāda" n="a">tataḥ sa rājā punar eva mūrchitaḥ</seg>
            <seg type="pāda" n="b">priyām atuṣṭāṃ pratikūlabhāṣiṇīm</seg>
        </li>
        <li id="R_2.011.015cd">
            <seg type="pāda" n="c">samīkṣya putrasya vivāsanaṃ prati</seg>
            <seg type="pāda" n="d">kṣitau visaṃjño nipapāta duḥkhitaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.012.001">
        <li id="R_2.012.001ab">putraśokārditaṃ pāpā visaṃjñaṃ patitaṃ bhuvi</li>
        <li id="R_2.012.001cd">viveṣṭamānam udīkṣya saikṣvākam idam abravīt</li>
    </ul>
    <ul id="R_2.012.002">
        <li id="R_2.012.002ab">pāpaṃ kṛtveva kim idaṃ mama saṃśrutya saṃśravam</li>
        <li id="R_2.012.002cd">śeṣe kṣititale sannaḥ sthityāṃ sthātuṃ tvam arhasi</li>
    </ul>
    <ul id="R_2.012.003">
        <li id="R_2.012.003ab">āhuḥ satyaṃ hi paramaṃ dharmaṃ dharmavido janāḥ</li>
        <li id="R_2.012.003cd">satyam āśritya hi mayā tvaṃ ca dharmaṃ pracoditaḥ</li>
    </ul>
    <ul id="R_2.012.004">
        <li id="R_2.012.004ab">saṃśrutya śaibyaḥ śyenāya svāṃ tanuṃ jagatīpatiḥ</li>
        <li id="R_2.012.004cd">pradāya pakṣiṇo rājañ jagāma gatim uttamām</li>
    </ul>
    <ul id="R_2.012.005">
        <li id="R_2.012.005ab">tatha hy alarkas tejasvī brāhmaṇe vedapārage</li>
        <li id="R_2.012.005cd">yācamāne svake netre uddhṛtyāvimanā dadau</li>
    </ul>
    <ul id="R_2.012.006">
        <li id="R_2.012.006ab">saritāṃ tu patiḥ svalpāṃ maryādāṃ satyam anvitaḥ</li>
        <li id="R_2.012.006cd">satyānurodhāt samaye velāṃ khāṃ nātivartate</li>
    </ul>
    <ul id="R_2.012.007">
        <li id="R_2.012.007ab">samayaṃ ca mamāryemaṃ yadi tvaṃ na kariṣyasi</li>
        <li id="R_2.012.007cd">agratas te parityaktā parityakṣyāmi jīvitam</li>
    </ul>
    <ul id="R_2.012.008">
        <li id="R_2.012.008ab">evaṃ pracodito rājā kaikeyyā nirviśaṅkayā</li>
        <li id="R_2.012.008cd">nāśakat pāśam unmoktuṃ balir indrakṛtaṃ yathā</li>
    </ul>
    <ul id="R_2.012.009">
        <li id="R_2.012.009ab">udbhrāntahṛdayaś cāpi vivarṇavanado 'bhavat</li>
        <li id="R_2.012.009cd">sa dhuryo vai parispandan yugacakrāntaraṃ yathā</li>
    </ul>
    <ul id="R_2.012.010">
        <li id="R_2.012.010ab">vihvalābhyāṃ ca netrābhyām apaśyann iva bhūmipaḥ</li>
        <li id="R_2.012.010cd">kṛcchrād dhairyeṇa saṃstabhya kaikeyīm idam abravīt</li>
    </ul>
    <ul id="R_2.012.011">
        <li id="R_2.012.011ab">yas te mantrakṛtaḥ pāṇir agnau pāpe mayā dhṛtaḥ</li>
        <li id="R_2.012.011cd">taṃ tyajāmi svajaṃ caiva tava putraṃ saha tvayā</li>
    </ul>
    <ul id="R_2.012.012">
        <li id="R_2.012.012ab">tataḥ pāpasamācārā kaikeyī pārthivaṃ punaḥ</li>
        <li id="R_2.012.012cd">uvāca paruṣaṃ vākyaṃ vākyajñā roṣamūrchitā</li>
    </ul>
    <ul id="R_2.012.013">
        <li id="R_2.012.013ab">kim idaṃ bhāṣase rājan vākyaṃ gararujopamam</li>
        <li id="R_2.012.013cd">ānāyayitum akliṣṭaṃ putraṃ rāmam ihārhasi</li>
    </ul>
    <ul id="R_2.012.014">
        <li id="R_2.012.014ab">sthāpya rājye mama sutaṃ kṛtvā rāmaṃ vanecaram</li>
        <li id="R_2.012.014cd">niḥsapatnāṃ ca māṃ kṛtvā kṛtakṛtyo bhaviṣyasi</li>
    </ul>
    <ul id="R_2.012.015">
        <li id="R_2.012.015ab">sa nunna iva tīkṣeṇa pratodena hayottamaḥ</li>
        <li id="R_2.012.015cd">rājā pradocito 'bhīkṣṇaṃ kaikeyīm idam abravīt</li>
    </ul>
    <ul id="R_2.012.016">
        <li id="R_2.012.016ab">dharmabandhena baddho 'smi naṣṭā ca mama cetanā</li>
        <li id="R_2.012.016cd">jyeṣṭhaṃ putraṃ priyaṃ rāmaṃ draṣṭum icchāmi dhārmikam</li>
    </ul>
    <ul id="R_2.012.017">
        <li id="R_2.012.017ab">iti rājño vacaḥ śrutvā kaikeyī tadanantaram</li>
        <li id="R_2.012.017cd">svayam evābravīt sūtaṃ gaccha tvaṃ rāmam ānaya</li>
    </ul>
    <ul id="R_2.012.018">
        <li id="R_2.012.018ab">tataḥ sa rājā taṃ sūtaṃ sannaharṣaḥ sutaṃ prati</li>
        <li id="R_2.012.018cd">śokāraktekṣaṇaḥ śrīmān udvīkṣyovāca dhārmikaḥ</li>
    </ul>
    <ul id="R_2.012.019">
        <li id="R_2.012.019ab">sumantraḥ karuṇaṃ śrutvā dṛṣṭvā dīnaṃ ca pārthivam</li>
        <li id="R_2.012.019cd">pragṛhītāñjaliḥ kiṃ cit tasmād deśād apākraman</li>
    </ul>
    <ul id="R_2.012.020">
        <li id="R_2.012.020ab">yadā vaktuṃ svayaṃ dainyān na śaśāka mahīpatiḥ</li>
        <li id="R_2.012.020cd">tadā sumantraṃ mantrajñā kaikeyī pratyuvāca ha</li>
    </ul>
    <ul id="R_2.012.021">
        <li id="R_2.012.021ab">sumantra rāmaṃ drakṣyāmi śīghram ānaya sundaram</li>
        <li id="R_2.012.021cd">sa manyamānaḥ kalyāṇaṃ hṛdayena nananda ca</li>
    </ul>
    <ul id="R_2.012.022">
        <li id="R_2.012.022ab">sumantraś cintayām āsa tvaritaṃ coditas tayā</li>
        <li id="R_2.012.022cd">vyaktaṃ rāmo 'bhiṣekārtham ihāyāsyati dharmavit</li>
    </ul>
    <ul id="R_2.012.023">
        <li id="R_2.012.023ab">iti sūto matiṃ kṛtvā harṣeṇa mahatā punaḥ</li>
        <li id="R_2.012.023cd">nirjagāma mahātejā rāghavasya didṛkṣayā</li>
    </ul>
    <ul id="R_2.012.024">
        <li id="R_2.012.024ab">
            <seg type="pāda" n="a">tataḥ purastāt sahasā vinirgato</seg>
            <seg type="pāda" n="b">mahīpatīn dvāragatān vilokayan</seg>
        </li>
        <li id="R_2.012.024cd">
            <seg type="pāda" n="c">dadarśa paurān vividhān mahādhanān</seg>
            <seg type="pāda" n="d">upasthitān dvāram upetya viṣṭhitān</seg>
        </li>
    </ul>
    <ul id="R_2.013.001">
        <li id="R_2.013.001ab">te tu tāṃ rajanīm uṣya brāhmaṇā vedapāragāḥ</li>
        <li id="R_2.013.001cd">upatasthur upasthānaṃ saharājapurohitāḥ</li>
    </ul>
    <ul id="R_2.013.002">
        <li id="R_2.013.002ab">amātyā balamukhyāś ca mukhyā ye nigamasya ca</li>
        <li id="R_2.013.002cd">rāghavasyābhiṣekārthe prīyamāṇās tu saṃgatāḥ</li>
    </ul>
    <ul id="R_2.013.003">
        <li id="R_2.013.003ab">udite vimale sūrye puṣye cābhyāgate 'hani</li>
        <li id="R_2.013.003cd">abhiṣekāya rāmasya dvijendrair upakalpitam</li>
    </ul>
    <ul id="R_2.013.004">
        <li id="R_2.013.004ab">kāñcanā jalakumbhāś ca bhadrapīṭhaṃ svalaṃkṛtam</li>
        <li id="R_2.013.004cd">rāmaś ca samyagāstīrṇo bhāsvarā vyāghracarmaṇā</li>
    </ul>
    <ul id="R_2.013.005">
        <li id="R_2.013.005ab">gaṅgāyamunayoḥ puṇyāt saṃgamād āhṛtaṃ jalam</li>
        <li id="R_2.013.005cd">yāś cānyāḥ saritaḥ puṇyā hradāḥ kūpāḥ sarāṃsi ca</li>
    </ul>
    <ul id="R_2.013.006">
        <li id="R_2.013.006ab">prāgvāhāś cordhvavāhāś ca tiryagvāhāḥ samāhitāḥ</li>
        <li id="R_2.013.006cd">tābhyaś caivāhṛtaṃ toyaṃ samudrebhyaś ca sarvaśaḥ</li>
    </ul>
    <ul id="R_2.013.007">
        <li id="R_2.013.007ab">kṣaudraṃ dadhi ghṛtaṃ lājā darbhāḥ sumanasaḥ payaḥ</li>
        <li id="R_2.013.007cd">salājāḥ kṣīribhiś channā ghaṭāḥ kāñcanarājatāḥ</li>
        <li id="R_2.013.007ef">padmotpalayutā bhānti pūrṇāḥ paramavāriṇā</li>
    </ul>
    <ul id="R_2.013.008">
        <li id="R_2.013.008ab">candrāṃśuvikacaprakhyaṃ pāṇḍuraṃ ratnabhūṣitam</li>
        <li id="R_2.013.008cd">sajjaṃ tiṣṭhati rāmasya vālavyajanam uttamam</li>
    </ul>
    <ul id="R_2.013.009">
        <li id="R_2.013.009ab">candramaṇḍalasaṃkāśam ātapatraṃ ca pāṇḍuram</li>
        <li id="R_2.013.009cd">sajjaṃ dyutikaraṃ śrīmad abhiṣekapuraskṛtam</li>
    </ul>
    <ul id="R_2.013.010">
        <li id="R_2.013.010ab">pāṇḍuraś ca vṛṣaḥ sajjaḥ pāṇḍurāśvaś ca susthitaḥ</li>
        <li id="R_2.013.010cd">prasrutaś ca gajaḥ śrīmān aupavāhyaḥ pratīkṣate</li>
    </ul>
    <ul id="R_2.013.011">
        <li id="R_2.013.011ab">aṣṭau kanyāś ca maṅgalyāḥ sarvābharaṇabhūṣitāḥ</li>
        <li id="R_2.013.011cd">vāditrāṇi ca sarvāṇi bandinaś ca tathāpare</li>
    </ul>
    <ul id="R_2.013.012">
        <li id="R_2.013.012ab">ikṣvākūṇāṃ yathā rājye saṃbhriyetābhiṣecanam</li>
        <li id="R_2.013.012cd">tathā jātīyām ādāya rājaputrābhiṣecanam</li>
    </ul>
    <ul id="R_2.013.013">
        <li id="R_2.013.013ab">te rājavacanāt tatra samavetā mahīpatim</li>
        <li id="R_2.013.013cd">apaśyanto 'bruvan ko nu rājño naḥ prativedayet</li>
    </ul>
    <ul id="R_2.013.014">
        <li id="R_2.013.014ab">na paśyāmaś ca rājānam uditaś ca divākaraḥ</li>
        <li id="R_2.013.014cd">yauvarājyābhiṣekaś ca sajjo rāmasya dhīmataḥ</li>
    </ul>
    <ul id="R_2.013.015">
        <li id="R_2.013.015ab">iti teṣu bruvāṇeṣu sārvabhaumān mahīpatīn</li>
        <li id="R_2.013.015cd">abravīt tān idaṃ sarvān sumantro rājasatkṛtaḥ</li>
    </ul>
    <ul id="R_2.013.016">
        <li id="R_2.013.016ab">ayaṃ pṛcchāmi vacanāt sukham āyuṣmatām aham</li>
        <li id="R_2.013.016cd">rājñaḥ saṃpratibuddhasya yac cāgamanakāraṇam</li>
    </ul>
    <ul id="R_2.013.017">
        <li id="R_2.013.017ab">ity uktvāntaḥpuradvāram ājagāma purāṇavit</li>
        <li id="R_2.013.017cd">āśīrbhir guṇayuktābhir abhituṣṭāva rāghavam</li>
    </ul>
    <ul id="R_2.013.018">
        <li id="R_2.013.018ab">gatā bhagavatī rātrirahaḥ śivam upasthitam</li>
        <li id="R_2.013.018cd">budhyasva nṛpaśārdūla kuru kāryam anantaram</li>
    </ul>
    <ul id="R_2.013.019">
        <li id="R_2.013.019ab">brāhmaṇā balamukhyāś ca naigamāś cāgatā nṛpa</li>
        <li id="R_2.013.019cd">darśanaṃ pratikāṅkṣante pratibudhyasva rāghava</li>
    </ul>
    <ul id="R_2.013.020">
        <li id="R_2.013.020ab">stuvantaṃ taṃ tadā sūtaṃ sumantraṃ mantrakovidam</li>
        <li id="R_2.013.020cd">pratibudhya tato rājā idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.013.021">
        <li id="R_2.013.021ab">na caiva saṃprasuto 'ham ānayed āśu rāghavam</li>
        <li id="R_2.013.021cd">iti rājā daśarathaḥ sūtaṃ tatrānvaśāt punaḥ</li>
    </ul>
    <ul id="R_2.013.022">
        <li id="R_2.013.022ab">sa rājavacanaṃ śrutvā śirasā pratipūjya tam</li>
        <li id="R_2.013.022cd">nirjagāma nṛpāvāsān manyamānaḥ priyaṃ mahat</li>
    </ul>
    <ul id="R_2.013.023">
        <li id="R_2.013.023ab">prapanno rājamārgaṃ ca patākā dhvajaśobhitam</li>
        <li id="R_2.013.023cd">sa sūtas tatra śuśrāva rāmādhikaraṇāḥ kathāḥ</li>
    </ul>
    <ul id="R_2.013.024">
        <li id="R_2.013.024ab">tato dadarśa ruciraṃ kailāsasadṛśaprabham</li>
        <li id="R_2.013.024cd">rāmaveśma sumantras tu śakraveśmasamaprabham</li>
    </ul>
    <ul id="R_2.013.025">
        <li id="R_2.013.025ab">mahākapāṭapihitaṃ vitardiśataśobhitam</li>
        <li id="R_2.013.025cd">kāñcanapratimaikāgraṃ maṇividrumatoraṇam</li>
    </ul>
    <ul id="R_2.013.026">
        <li id="R_2.013.026ab">śāradābhraghanaprakhyaṃ dīptaṃ meruguhopamam</li>
        <li id="R_2.013.026cd">dāmabhir varamālyānāṃ sumahadbhir alaṃkṛtam</li>
    </ul>
    <ul id="R_2.013.027">
        <li id="R_2.013.027ab">
            <seg type="pāda" n="a">sa vājiyuktena rathena sārathir</seg>
            <seg type="pāda" n="b">narākulaṃ rājakulaṃ vilokayan</seg>
        </li>
        <li id="R_2.013.027cd">
            <seg type="pāda" n="c">tataḥ samāsādya mahādhanaṃ mahat</seg>
            <seg type="pāda" n="d">prahṛṣṭaromā sa babhūva sārathiḥ</seg>
        </li>
    </ul>
    <ul id="R_2.013.028">
        <li id="R_2.013.028ab">
            <seg type="pāda" n="a">tad adrikūṭācalameghasaṃnibhaṃ</seg>
            <seg type="pāda" n="b">mahāvimānottamaveśmasaṃghavat</seg>
        </li>
        <li id="R_2.013.028cd">
            <seg type="pāda" n="c">avāryamāṇaḥ praviveśa sārathiḥ</seg>
            <seg type="pāda" n="d">prabhūtaratnaṃ makaro yathārṇavam</seg>
        </li>
    </ul>
    <ul id="R_2.014.001">
        <li id="R_2.014.001ab">sa tad antaḥpuradvāraṃ samatītya janākulam</li>
        <li id="R_2.014.001cd">praviviktāṃ tataḥ kakṣyām āsasāda purāṇavit</li>
    </ul>
    <ul id="R_2.014.002">
        <li id="R_2.014.002ab">prāsakārmukabibhradbhir yuvabhir mṛṣṭakuṇḍalaiḥ</li>
        <li id="R_2.014.002cd">apramādibhir ekāgraiḥ svanuraktair adhiṣṭhitām</li>
    </ul>
    <ul id="R_2.014.003">
        <li id="R_2.014.003ab">tatra kāṣāyiṇo vṛddhān vetrapāṇīn svalaṃkṛtān</li>
        <li id="R_2.014.003cd">dadarśa viṣṭhitān dvāri stryadhyakṣān susamāhitān</li>
    </ul>
    <ul id="R_2.014.004">
        <li id="R_2.014.004ab">te samīkṣya samāyāntaṃ rāmapriyacikīrṣavaḥ</li>
        <li id="R_2.014.004cd">sahabhāryāya rāmāya kṣipram evācacakṣire</li>
    </ul>
    <ul id="R_2.014.005">
        <li id="R_2.014.005ab">prativeditam ājñāya sūtam abhyantaraṃ pituḥ</li>
        <li id="R_2.014.005cd">tatraivānāyayām āsa rāghavaḥ priyakāmyayā</li>
    </ul>
    <ul id="R_2.014.006">
        <li id="R_2.014.006ab">taṃ vaiśravaṇasaṃkāśam upaviṣṭaṃ svalaṃkṛtam</li>
        <li id="R_2.014.006cd">dādarśa sūtaḥ paryaṅke sauvaṇo sottaracchade</li>
    </ul>
    <ul id="R_2.014.007">
        <li id="R_2.014.007ab">varāharudhirābheṇa śucinā ca sugandhinā</li>
        <li id="R_2.014.007cd">anuliptaṃ parārdhyena candanena paraṃtapam</li>
    </ul>
    <ul id="R_2.014.008">
        <li id="R_2.014.008ab">sthitayā pārśvataś cāpi vālavyajanahastayā</li>
        <li id="R_2.014.008cd">upetaṃ sītayā bhūyaś citrayā śaśinaṃ yathā</li>
    </ul>
    <ul id="R_2.014.009">
        <li id="R_2.014.009ab">taṃ tapantam ivādityam upapannaṃ svatejasā</li>
        <li id="R_2.014.009cd">vavande varadaṃ bandī niyamajño vinītavat</li>
    </ul>
    <ul id="R_2.014.010">
        <li id="R_2.014.010ab">prāñjalis tu sukhaṃ pṛṣṭvā vihāraśayanāsane</li>
        <li id="R_2.014.010cd">rājaputram uvācedaṃ sumantro rājasatkṛtaḥ</li>
    </ul>
    <ul id="R_2.014.011">
        <li id="R_2.014.011ab">kausalyā suprabhā deva pitā tvaṃ draṣṭum icchati</li>
        <li id="R_2.014.011cd">mahiṣyā saha kaikeyyā gamyatāṃ tatra māciram</li>
    </ul>
    <ul id="R_2.014.012">
        <li id="R_2.014.012ab">evam uktas tu saṃhṛṣṭo narasiṃho mahādyutiḥ</li>
        <li id="R_2.014.012cd">tataḥ saṃmānayām āsa sītām idam uvāca ha</li>
    </ul>
    <ul id="R_2.014.013">
        <li id="R_2.014.013ab">devi devaś ca devī ca samāgamya madantare</li>
        <li id="R_2.014.013cd">mantreyete dhruvaṃ kiṃ cid abhiṣecanasaṃhitam</li>
    </ul>
    <ul id="R_2.014.014">
        <li id="R_2.014.014ab">lakṣayitvā hy abhiprāyaṃ priyakāmā sudakṣiṇā</li>
        <li id="R_2.014.014cd">saṃcodayati rājānaṃ madarthaṃ madirekṣaṇā</li>
    </ul>
    <ul id="R_2.014.015">
        <li id="R_2.014.015ab">yādṛśī pariṣat tatra tādṛśo dūta āgataḥ</li>
        <li id="R_2.014.015cd">dhruvam adyaiva māṃ rājā yauvarājye 'bhiṣekṣyati</li>
    </ul>
    <ul id="R_2.014.016">
        <li id="R_2.014.016ab">hanta śīghram ito gatvā drakṣyāmi ca mahīpatiḥ</li>
        <li id="R_2.014.016cd">saha tvaṃ parivāreṇa sukham āssva ramasya ca</li>
    </ul>
    <ul id="R_2.014.017">
        <li id="R_2.014.017ab">patisaṃmānitā sītā bhartāram asitekṣaṇā</li>
        <li id="R_2.014.017cd">ādvāram anuvavrāja maṅgalāny abhidadhyuṣī</li>
    </ul>
    <ul id="R_2.014.018">
        <li id="R_2.014.018ab">sa sarvān arthino dṛṣṭvā sametya pratinandya ca</li>
        <li id="R_2.014.018cd">tataḥ pāvakasaṃkāśam āruroha rathottamam</li>
    </ul>
    <ul id="R_2.014.019">
        <li id="R_2.014.019ab">muṣṇantam iva cakṣūṃṣi prabhayā hemavarcasaṃ</li>
        <li id="R_2.014.019cd">kareṇuśiśukalpaiś ca yuktaṃ paramavājibhiḥ</li>
    </ul>
    <ul id="R_2.014.020">
        <li id="R_2.014.020ab">hariyuktaṃ sahasrākṣo ratham indra ivāśugam</li>
        <li id="R_2.014.020cd">prayayau tūrṇam āsthāya rāghavo jvalitaḥ śriyā</li>
    </ul>
    <ul id="R_2.014.021">
        <li id="R_2.014.021ab">sa parjanya ivākāśe svanavān abhinādayan</li>
        <li id="R_2.014.021cd">niketān niryayau śrīmān mahābhrād iva candramāḥ</li>
    </ul>
    <ul id="R_2.014.022">
        <li id="R_2.014.022ab">chatracāmarapāṇis tu lakṣmaṇo rāghavānujaḥ</li>
        <li id="R_2.014.022cd">jugopa bhrātaraṃ bhrātā ratham āsthāya pṛṣṭhataḥ</li>
    </ul>
    <ul id="R_2.014.023">
        <li id="R_2.014.023ab">tato halahalāśabdas tumulaḥ samajāyata</li>
        <li id="R_2.014.023cd">tasya niṣkramamāṇasya janaughasya samantataḥ</li>
    </ul>
    <ul id="R_2.014.024">
        <li id="R_2.014.024ab">
            <seg type="pāda" n="a">sa rāghavas tatra kathāpralāpaṃ</seg>
            <seg type="pāda" n="b">śuśrāva lokasya samāgatasya</seg>
        </li>
        <li id="R_2.014.024cd">
            <seg type="pāda" n="c">ātmādhikārā vividhāś ca vācaḥ</seg>
            <seg type="pāda" n="d">prahṛṣṭarūpasya pure janasya</seg>
        </li>
    </ul>
    <ul id="R_2.014.025">
        <li id="R_2.014.025ab">
            <seg type="pāda" n="a">eṣa śriyaṃ gacchati rāghavo 'dya</seg>
            <seg type="pāda" n="b">rājaprasādād vipulāṃ gamiṣyan</seg>
        </li>
        <li id="R_2.014.025cd">
            <seg type="pāda" n="c">ete vayaṃ sarvasamṛddhakāmā</seg>
            <seg type="pāda" n="d">yeṣām ayaṃ no bhavitā praśāstā</seg>
        </li>
        <li id="R_2.014.025ef">
            <seg type="pāda" n="e">lābho janasyāsya yad eṣa sarvaṃ</seg>
            <seg type="pāda" n="f">prapatsyate rāṣṭram idaṃ cirāya</seg>
        </li>
    </ul>
    <ul id="R_2.014.026">
        <li id="R_2.014.026ab">
            <seg type="pāda" n="a">sa ghoṣavadbhiś ca hayaiḥ sanāgaiḥ</seg>
            <seg type="pāda" n="b">puraḥsaraiḥ svastikasūtamāgadhaiḥ</seg>
        </li>
        <li id="R_2.014.026cd">
            <seg type="pāda" n="c">mahīyamānaḥ pravaraiś ca vādakair</seg>
            <seg type="pāda" n="d">abhiṣṭuto vaiśravaṇo yathā yayau</seg>
        </li>
    </ul>
    <ul id="R_2.014.027">
        <li id="R_2.014.027ab">
            <seg type="pāda" n="a">kareṇumātaṅgarathāśvasaṃkulaṃ</seg>
            <seg type="pāda" n="b">mahājanaughaiḥ paripūrṇacatvaram</seg>
        </li>
        <li id="R_2.014.027cd">
            <seg type="pāda" n="c">prabhūtaratnaṃ bahupaṇyasaṃcayaṃ</seg>
            <seg type="pāda" n="d">dadarśa rāmo ruciraṃ mahāpatham</seg>
        </li>
    </ul>
    <ul id="R_2.015.001">
        <li id="R_2.015.001ab">sa rāmo ratham āsthāya saṃprahṛṣṭasuhṛjjanaḥ</li>
        <li id="R_2.015.001cd">apaśyan nagaraṃ śrīmān nānājanasamākulam</li>
    </ul>
    <ul id="R_2.015.002">
        <li id="R_2.015.002ab">sa gṛhair abhrasaṃkāśaiḥ pāṇḍurair upaśobhitam</li>
        <li id="R_2.015.002cd">rājamārgaṃ yayau rāmo madhyenāgarudhūpitam</li>
    </ul>
    <ul id="R_2.015.003">
        <li id="R_2.015.003ab">śobhamānam asaṃbādhaṃ taṃ rājapatham uttamam</li>
        <li id="R_2.015.003cd">saṃvṛtaṃ vividhaiḥ paṇyair bhakṣyair uccāvacair api</li>
    </ul>
    <ul id="R_2.015.004">
        <li id="R_2.015.004ab">āśīrvādān bahūñ śṛṇvan suhṛdbhiḥ samudīritān</li>
        <li id="R_2.015.004cd">yathārhaṃ cāpi saṃpūjya sarvān eva narān yayau</li>
    </ul>
    <ul id="R_2.015.005">
        <li id="R_2.015.005ab">pitāmahair ācaritaṃ tathaiva prapitāmahaiḥ</li>
        <li id="R_2.015.005cd">adyopādāya taṃ mārgam abhiṣikto 'nupālaya</li>
    </ul>
    <ul id="R_2.015.006">
        <li id="R_2.015.006ab">yathā sma lālitāḥ pitrā yathā pūrvaiḥ pitāmahaiḥ</li>
        <li id="R_2.015.006cd">tataḥ sukhataraṃ sarve rāme vatsyāma rājani</li>
    </ul>
    <ul id="R_2.015.007">
        <li id="R_2.015.007ab">alam adya hi bhuktena paramārthair alaṃ ca naḥ</li>
        <li id="R_2.015.007cd">yathā paśyāma niryāntaṃ rāmaṃ rājye pratiṣṭhitam</li>
    </ul>
    <ul id="R_2.015.008">
        <li id="R_2.015.008ab">ato hi na priyataraṃ nānyat kiṃ cid bhaviṣyati</li>
        <li id="R_2.015.008cd">yathābhiṣeko rāmasya rājyenāmitatejasaḥ</li>
    </ul>
    <ul id="R_2.015.009">
        <li id="R_2.015.009ab">etāś cānyāś ca suhṛdām udāsīnaḥ kathāḥ śubhāḥ</li>
        <li id="R_2.015.009cd">ātmasaṃpūjanīḥ śṛṇvan yayau rāmo mahāpatham</li>
    </ul>
    <ul id="R_2.015.010">
        <li id="R_2.015.010ab">na hi tasmān manaḥ kaś cic cakṣuṣī vā narottamāt</li>
        <li id="R_2.015.010cd">naraḥ śaknoty apākraṣṭum atikrānte 'pi rāghave</li>
    </ul>
    <ul id="R_2.015.011">
        <li id="R_2.015.011ab">sarveṣāṃ sa hi dharmātmā varṇānāṃ kurute dayām</li>
        <li id="R_2.015.011cd">caturṇāṃ hi vayaḥsthānāṃ tena te tam anuvratāḥ</li>
    </ul>
    <ul id="R_2.015.012">
        <li id="R_2.015.012ab">sa rājakulam āsādya mahendrabhavanopamam</li>
        <li id="R_2.015.012cd">rājaputraḥ pitur veśma praviveśa śriyā jvalan</li>
    </ul>
    <ul id="R_2.015.013">
        <li id="R_2.015.013ab">sa sarvāḥ samatikramya kakṣyā daśarathātmajaḥ</li>
        <li id="R_2.015.013cd">saṃnivartya janaṃ sarvaṃ śuddhāntaḥpuram abhyagāt</li>
    </ul>
    <ul id="R_2.015.014">
        <li id="R_2.015.014ab">
            <seg type="pāda" n="a">tataḥ praviṣṭe pitur antikaṃ tadā</seg>
            <seg type="pāda" n="b">janaḥ sa sarvo mudito nṛpātmaje</seg>
        </li>
        <li id="R_2.015.014cd">
            <seg type="pāda" n="c">pratīkṣate tasya punaḥ sma nirgamaṃ</seg>
            <seg type="pāda" n="d">yathodayaṃ candramasaḥ saritpatiḥ</seg>
        </li>
    </ul>
    <ul id="R_2.016.001">
        <li id="R_2.016.001ab">sa dadarśāsane rāmo niṣaṇṇaṃ pitaraṃ śubhe</li>
        <li id="R_2.016.001cd">kaikeyīsahitaṃ dīnaṃ mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_2.016.002">
        <li id="R_2.016.002ab">sa pituś caraṇau pūrvam abhivādya vinītavat</li>
        <li id="R_2.016.002cd">tato vavande caraṇau kaikeyyāḥ susamāhitaḥ</li>
    </ul>
    <ul id="R_2.016.003">
        <li id="R_2.016.003ab">rāmety uktvā ca vacanaṃ vāṣpaparyākulekṣaṇaḥ</li>
        <li id="R_2.016.003cd">śaśāka nṛpatir dīno nekṣituṃ nābhibhāṣitum</li>
    </ul>
    <ul id="R_2.016.004">
        <li id="R_2.016.004ab">tad apūrvaṃ narapater dṛṣṭvā rūpaṃ bhayāvaham</li>
        <li id="R_2.016.004cd">rāmo 'pi bhayam āpannaḥ padā spṛṣṭveva pannagam</li>
    </ul>
    <ul id="R_2.016.005">
        <li id="R_2.016.005ab">indriyair aprahṛṣṭais taṃ śokasaṃtāpakarśitam</li>
        <li id="R_2.016.005cd">niḥśvasantaṃ mahārājaṃ vyathitākulacetasaṃ</li>
    </ul>
    <ul id="R_2.016.006">
        <li id="R_2.016.006ab">ūrmi mālinam akṣobhyaṃ kṣubhyantam iva sāgaram</li>
        <li id="R_2.016.006cd">upaplutam ivādityam uktānṛtam ṛṣiṃ yathā</li>
    </ul>
    <ul id="R_2.016.007">
        <li id="R_2.016.007ab">acintyakalpaṃ hi pitus taṃ śokam upadhārayan</li>
        <li id="R_2.016.007cd">babhūva saṃrabdhataraḥ samudra iva parvaṇi</li>
    </ul>
    <ul id="R_2.016.008">
        <li id="R_2.016.008ab">cintayām āsa ca tadā rāmaḥ pitṛhite rataḥ</li>
        <li id="R_2.016.008cd">kiṃsvid adyaiva nṛpatir na māṃ pratyabhinandati</li>
    </ul>
    <ul id="R_2.016.009">
        <li id="R_2.016.009ab">anyadā māṃ pitā dṛṣṭvā kupito 'pi prasīdati</li>
        <li id="R_2.016.009cd">tasya mām adya saṃprekṣya kimāyāsaḥ pravartate</li>
    </ul>
    <ul id="R_2.016.010">
        <li id="R_2.016.010ab">sa dīna iva śokārto viṣaṇṇavadanadyutiḥ</li>
        <li id="R_2.016.010cd">kaikeyīm abhivādyaiva rāmo vacanam abravīt</li>
    </ul>
    <ul id="R_2.016.011">
        <li id="R_2.016.011ab">kaccin mayā nāparāddham ajñānād yena me pitā</li>
        <li id="R_2.016.011cd">kupitas tan mamācakṣva tvaṃ caivainaṃ prasādaya</li>
    </ul>
    <ul id="R_2.016.012">
        <li id="R_2.016.012ab">vivarṇavadano dīno na hi mām abhibhāṣate</li>
        <li id="R_2.016.012cd">śārīro mānaso vāpi kaccid enaṃ na bādhate</li>
        <li id="R_2.016.012ef">saṃtāpo vābhitāpo vā durlabhaṃ hi sadā sukham</li>
    </ul>
    <ul id="R_2.016.013">
        <li id="R_2.016.013ab">kaccin na kiṃ cid bharate kumāre priyadarśane</li>
        <li id="R_2.016.013cd">śatrughne vā mahāsattve mātṝṇāṃ vā mamāśubham</li>
    </ul>
    <ul id="R_2.016.014">
        <li id="R_2.016.014ab">atoṣayan mahārājam akurvan vā pitur vacaḥ</li>
        <li id="R_2.016.014cd">muhūrtam api neccheyaṃ jīvituṃ kupite nṛpe</li>
    </ul>
    <ul id="R_2.016.015">
        <li id="R_2.016.015ab">yatomūlaṃ naraḥ paśyet prādurbhāvam ihātmanaḥ</li>
        <li id="R_2.016.015cd">kathaṃ tasmin na varteta pratyakṣe sati daivate</li>
    </ul>
    <ul id="R_2.016.016">
        <li id="R_2.016.016ab">kaccit te paruṣaṃ kiṃ cid abhimānāt pitā mama</li>
        <li id="R_2.016.016cd">ukto bhavatyā kopena yatrāsya lulitaṃ manaḥ</li>
    </ul>
    <ul id="R_2.016.017">
        <li id="R_2.016.017ab">etad ācakṣva me devi tattvena paripṛcchataḥ</li>
        <li id="R_2.016.017cd">kiṃnimittam apūrvo 'yaṃ vikāro manujādhipe</li>
    </ul>
    <ul id="R_2.016.018">
        <li id="R_2.016.018ab">ahaṃ hi vacanād rājñaḥ pateyam api pāvake</li>
        <li id="R_2.016.018cd">bhakṣayeyaṃ viṣaṃ tīkṣṇaṃ majjeyam api cārṇave</li>
        <li id="R_2.016.018ef">niyukto guruṇā pitrā nṛpeṇa ca hitena ca</li>
    </ul>
    <ul id="R_2.016.019">
        <li id="R_2.016.019ab">tad brūhi vacanaṃ devi rājño yad abhikāṅkṣitam</li>
        <li id="R_2.016.019cd">kariṣye pratijāne ca rāmo dvir nābhibhāṣate</li>
    </ul>
    <ul id="R_2.016.020">
        <li id="R_2.016.020ab">tam ārjavasamāyuktam anāryā satyavādinam</li>
        <li id="R_2.016.020cd">uvāca rāmaṃ kaikeyī vacanaṃ bhṛśadāruṇam</li>
    </ul>
    <ul id="R_2.016.021">
        <li id="R_2.016.021ab">purā devāsure yuddhe pitrā te mama rāghava</li>
        <li id="R_2.016.021cd">rakṣitena varau dattau saśalyena mahāraṇe</li>
    </ul>
    <ul id="R_2.016.022">
        <li id="R_2.016.022ab">tatra me yācito rājā bharatasyābhiṣecanam</li>
        <li id="R_2.016.022cd">gamanaṃ daṇḍakāraṇye tava cādyaiva rāghava</li>
    </ul>
    <ul id="R_2.016.023">
        <li id="R_2.016.023ab">yadi satyapratijñaṃ tvaṃ pitaraṃ kartum icchasi</li>
        <li id="R_2.016.023cd">ātmānaṃ ca narareṣṭha mama vākyam idaṃ śṛṇu</li>
    </ul>
    <ul id="R_2.016.024">
        <li id="R_2.016.024ab">sa nideśe pitus tiṣṭha yathā tena pratiśrutam</li>
        <li id="R_2.016.024cd">tvayāraṇyaṃ praveṣṭavyaṃ nava varṣāṇi pañca ca</li>
    </ul>
    <ul id="R_2.016.025">
        <li id="R_2.016.025ab">sapta sapta ca varṣāṇi daṇḍakāraṇyam āśritaḥ</li>
        <li id="R_2.016.025cd">abhiṣekam imaṃ tyaktvā jaṭācīradharo vasa</li>
    </ul>
    <ul id="R_2.016.026">
        <li id="R_2.016.026ab">bharataḥ kosalapure praśāstu vasudhām imām</li>
        <li id="R_2.016.026cd">nānāratnasamākīrṇāṃ savājirathakuñjarām</li>
    </ul>
    <ul id="R_2.016.027">
        <li id="R_2.016.027ab">tad apriyam amitraghno vacanaṃ maraṇopamam</li>
        <li id="R_2.016.027cd">śrutvā na vivyathe rāmaḥ kaikeyīṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.016.028">
        <li id="R_2.016.028ab">evam astu gamiṣyāmi vanaṃ vastum ahaṃ tv ataḥ</li>
        <li id="R_2.016.028cd">jaṭācīradharo rājñaḥ pratijñām anupālayan</li>
    </ul>
    <ul id="R_2.016.029">
        <li id="R_2.016.029ab">idaṃ tu jñātum icchāmi kimarthaṃ māṃ mahīpatiḥ</li>
        <li id="R_2.016.029cd">nābhinandati durdharṣo yathāpuram ariṃdamaḥ</li>
    </ul>
    <ul id="R_2.016.030">
        <li id="R_2.016.030ab">manyur na ca tvayā kāryo devi brūhi tavāgrataḥ</li>
        <li id="R_2.016.030cd">yāsyāmi bhava suprītā vanaṃ cīrajaṭādharaḥ</li>
    </ul>
    <ul id="R_2.016.031">
        <li id="R_2.016.031ab">hitena guruṇā pitrā kṛtajñena nṛpeṇa ca</li>
        <li id="R_2.016.031cd">niyujyamāno viśrabdhaṃ kiṃ na kuryād ahaṃ priyam</li>
    </ul>
    <ul id="R_2.016.032">
        <li id="R_2.016.032ab">alīkaṃ mānasaṃ tv ekaṃ hṛdayaṃ dahatīva me</li>
        <li id="R_2.016.032cd">svayaṃ yan nāha māṃ rājā bharatasyābhiṣecanam</li>
    </ul>
    <ul id="R_2.016.033">
        <li id="R_2.016.033ab">ahaṃ hi sītāṃ rājyaṃ ca prāṇān iṣṭān dhanāni ca</li>
        <li id="R_2.016.033cd">hṛṣṭo bhrātre svayaṃ dadyāṃ bharatāyāpracoditaḥ</li>
    </ul>
    <ul id="R_2.016.034">
        <li id="R_2.016.034ab">kiṃ punar manujendreṇa svayaṃ pitrā pracoditaḥ</li>
        <li id="R_2.016.034cd">tava ca priyakāmārthaṃ pratijñām anupālayan</li>
    </ul>
    <ul id="R_2.016.035">
        <li id="R_2.016.035ab">tad āśvāsaya hīmaṃ tvaṃ kiṃ nv idaṃ yan mahīpatiḥ</li>
        <li id="R_2.016.035cd">vasudhāsaktanayano mandam aśrūṇi muñcati</li>
    </ul>
    <ul id="R_2.016.036">
        <li id="R_2.016.036ab">gacchantu caivānayituṃ dūtāḥ śīghrajavair hayaiḥ</li>
        <li id="R_2.016.036cd">bharataṃ mātulakulād adyaiva nṛpaśāsanāt</li>
    </ul>
    <ul id="R_2.016.037">
        <li id="R_2.016.037ab">daṇḍakāraṇyam eṣo 'ham ito gacchāmi satvaraḥ</li>
        <li id="R_2.016.037cd">avicārya pitur vākyaṃ samāvastuṃ caturdaśa</li>
    </ul>
    <ul id="R_2.016.038">
        <li id="R_2.016.038ab">sā hṛṣṭā tasya tadvākyaṃ śrutvā rāmasya kaikayī</li>
        <li id="R_2.016.038cd">prasthānaṃ śraddadhānā hi tvarayām āsa rāghavam</li>
    </ul>
    <ul id="R_2.016.039">
        <li id="R_2.016.039ab">evaṃ bhavatu yāsyanti dūtāḥ śīghrajavair hayaiḥ</li>
        <li id="R_2.016.039cd">bharataṃ mātulakulād upāvartayituṃ narāḥ</li>
    </ul>
    <ul id="R_2.016.040">
        <li id="R_2.016.040ab">tava tv ahaṃ kṣamaṃ manye notsukasya vilambanam</li>
        <li id="R_2.016.040cd">rāma tasmād itaḥ śīghraṃ vanaṃ tvaṃ gantum arhasi</li>
    </ul>
    <ul id="R_2.016.041">
        <li id="R_2.016.041ab">vrīḍānvitaḥ svayaṃ yac ca nṛpas tvāṃ nābhibhāṣate</li>
        <li id="R_2.016.041cd">naitat kiṃ cin naraśreṣṭha manyur eṣo 'panīyatām</li>
    </ul>
    <ul id="R_2.016.042">
        <li id="R_2.016.042ab">yāvat tvaṃ na vanaṃ yātaḥ purād asmād abhitvaran</li>
        <li id="R_2.016.042cd">pitā tāvan na te rāma snāsyate bhokṣyate 'pi vā</li>
    </ul>
    <ul id="R_2.016.043">
        <li id="R_2.016.043ab">dhik kaṣṭam iti niḥśvasya rājā śokapariplutaḥ</li>
        <li id="R_2.016.043cd">mūrchito nyapatat tasmin paryaṅke hemabhūṣite</li>
    </ul>
    <ul id="R_2.016.044">
        <li id="R_2.016.044ab">rāmo 'py utthāpya rājānaṃ kaikeyyābhipracoditaḥ</li>
        <li id="R_2.016.044cd">kaśayevāhato vājī vanaṃ gantuṃ kṛtatvaraḥ</li>
    </ul>
    <ul id="R_2.016.045">
        <li id="R_2.016.045ab">tad apriyam anāryāyā vacanaṃ dāruṇodaram</li>
        <li id="R_2.016.045cd">śrutvā gatavyatho rāmaḥ kaikeyīṃ vākyam abravīt</li>
    </ul>
    <ul id="R_2.016.046">
        <li id="R_2.016.046ab">nāham arthaparo devi lokam āvastum utsahe</li>
        <li id="R_2.016.046cd">viddhi mām ṛṣibhis tulyaṃ kevalaṃ dharmam āsthitam</li>
    </ul>
    <ul id="R_2.016.047">
        <li id="R_2.016.047ab">yad atrabhavataḥ kiṃ cic chakyaṃ kartuṃ priyaṃ mayā</li>
        <li id="R_2.016.047cd">prāṇān api parityajya sarvathā kṛtam eva tat</li>
    </ul>
    <ul id="R_2.016.048">
        <li id="R_2.016.048ab">na hy ato dharmacaraṇaṃ kiṃ cid asti mahattaram</li>
        <li id="R_2.016.048cd">yathā pitari śuśrūṣā tasya vā vacanakriyā</li>
    </ul>
    <ul id="R_2.016.049">
        <li id="R_2.016.049ab">anukto 'py atrabhavatā bhavatyā vacanād aham</li>
        <li id="R_2.016.049cd">vane vatsyāmi vijane varṣāṇīha caturdaśa</li>
    </ul>
    <ul id="R_2.016.050">
        <li id="R_2.016.050ab">na nūnaṃ mayi kaikeyi kiṃ cid āśaṃsase guṇam</li>
        <li id="R_2.016.050cd">yad rājānam avocas tvaṃ mameśvaratarā satī</li>
    </ul>
    <ul id="R_2.016.051">
        <li id="R_2.016.051ab">yāvan mātaram āpṛcche sītāṃ cānunayāmy aham</li>
        <li id="R_2.016.051cd">tato 'dyaiva gamiṣyāmi daṇḍakānāṃ mahad vanam</li>
    </ul>
    <ul id="R_2.016.052">
        <li id="R_2.016.052ab">bharataḥ pālayed rājyaṃ śuśrūṣec ca pitur yathā</li>
        <li id="R_2.016.052cd">tathā bhavatyā kartavyaṃ sa hi dharmaḥ sanātanaḥ</li>
    </ul>
    <ul id="R_2.016.053">
        <li id="R_2.016.053ab">sa rāmasya vacaḥ śrutvā bhṛśaṃ duḥkhahataḥ pitā</li>
        <li id="R_2.016.053cd">śokād aśaknuvan bāṣpaṃ praruroda mahāsvanam</li>
    </ul>
    <ul id="R_2.016.054">
        <li id="R_2.016.054ab">vanditvā caraṇau rāmo visaṃjñasya pitus tadā</li>
        <li id="R_2.016.054cd">kaikeyyāś cāpy anāryāyā niṣpapāta mahādyutiḥ</li>
    </ul>
    <ul id="R_2.016.055">
        <li id="R_2.016.055ab">sa rāmaḥ pitaraṃ kṛtvā kaikeyīṃ ca pradakṣiṇam</li>
        <li id="R_2.016.055cd">niṣkramyāntaḥpurāt tasmāt svaṃ dadarśa suhṛjjanam</li>
    </ul>
    <ul id="R_2.016.056">
        <li id="R_2.016.056ab">taṃ bāṣpaparipūrṇākṣaḥ pṛṣṭhato 'nujagāma ha</li>
        <li id="R_2.016.056cd">lakṣmaṇaḥ paramakruddhaḥ sumitrānandavardhanaḥ</li>
    </ul>
    <ul id="R_2.016.057">
        <li id="R_2.016.057ab">ābhiṣecanikaṃ bhāṇḍaṃ kṛtvā rāmaḥ pradakṣiṇam</li>
        <li id="R_2.016.057cd">śanair jagāma sāpekṣo dṛṣṭiṃ tatrāvicālayan</li>
    </ul>
    <ul id="R_2.016.058">
        <li id="R_2.016.058ab">na cāsya mahatīṃ lakṣmīṃ rājyanāśo 'pakarṣati</li>
        <li id="R_2.016.058cd">lokakāntasya kāntatvaṃ śītaraśmer iva kṣapā</li>
    </ul>
    <ul id="R_2.016.059">
        <li id="R_2.016.059ab">na vanaṃ gantukāmasya tyajataś ca vasuṃdharām</li>
        <li id="R_2.016.059cd">sarvalokātigasyeva lakṣyate cittavikriyā</li>
    </ul>
    <ul id="R_2.016.060">
        <li id="R_2.016.060ab">dhārayan manasā duḥkham indriyāṇi nigṛhya ca</li>
        <li id="R_2.016.060cd">praviveśātmavān veśma māturapriyaśaṃsivān</li>
    </ul>
    <ul id="R_2.016.061">
        <li id="R_2.016.061ab">
            <seg type="pāda" n="a">praviśya veśmātibhṛśaṃ mudānvitaṃ</seg>
            <seg type="pāda" n="b">samīkṣya tāṃ cārthavipattim āgatām</seg>
        </li>
        <li id="R_2.016.061cd">
            <seg type="pāda" n="c">na caiva rāmo 'tra jagāma vikriyāṃ</seg>
            <seg type="pāda" n="d">suhṛjjanasyātmavipattiśaṅkayā</seg>
        </li>
    </ul>
    <ul id="R_2.017.001">
        <li id="R_2.017.001ab">rāmas tu bhṛśam āyasto niḥśvasann iva kuñjaraḥ</li>
        <li id="R_2.017.001cd">jagāma sahito bhrātrā mātur antaḥpuraṃ vaśī</li>
    </ul>
    <ul id="R_2.017.002">
        <li id="R_2.017.002ab">so 'paśyat puruṣaṃ tatra vṛddhaṃ paramapūjitam</li>
        <li id="R_2.017.002cd">upaviṣṭaṃ gṛhadvāri tiṣṭhataś cāparān bahūn</li>
    </ul>
    <ul id="R_2.017.003">
        <li id="R_2.017.003ab">praviśya prathamāṃ kakṣyāṃ dvitīyāyāṃ dadarśa saḥ</li>
        <li id="R_2.017.003cd">brāhmaṇān vedasaṃpannān vṛddhān rājñābhisatkṛtān</li>
    </ul>
    <ul id="R_2.017.004">
        <li id="R_2.017.004ab">praṇamya rāmas tān vṛddhāṃs tṛtīyāyāṃ dadarśa saḥ</li>
        <li id="R_2.017.004cd">striyo vṛddhāś ca bālāś ca dvārarakṣaṇatatparāḥ</li>
    </ul>
    <ul id="R_2.017.005">
        <li id="R_2.017.005ab">vardhayitvā prahṛṣṭās tāḥ praviśya ca gṛhaṃ striyaḥ</li>
        <li id="R_2.017.005cd">nyavedayanta tvaritā rāma mātuḥ priyaṃ tadā</li>
    </ul>
    <ul id="R_2.017.006">
        <li id="R_2.017.006ab">kausalyāpi tadā devī rātriṃ sthitvā samāhitā</li>
        <li id="R_2.017.006cd">prabhāte tv akarot pūjāṃ viṣṇoḥ putrahitaiṣiṇī</li>
    </ul>
    <ul id="R_2.017.007">
        <li id="R_2.017.007ab">sā kṣaumavasanā hṛṣṭā nityaṃ vrataparāyaṇā</li>
        <li id="R_2.017.007cd">agniṃ juhoti sma tadā mantravat kṛtamaṅgalā</li>
    </ul>
    <ul id="R_2.017.008">
        <li id="R_2.017.008ab">praviśya ca tadā rāmo mātur antaḥpuraṃ śubham</li>
        <li id="R_2.017.008cd">dadarśa mātaraṃ tatra hāvayantīṃ hutāśanam</li>
    </ul>
    <ul id="R_2.017.009">
        <li id="R_2.017.009ab">sā cirasyātmajaṃ dṛṣṭvā mātṛnandanam āgatam</li>
        <li id="R_2.017.009cd">abhicakrāma saṃhṛṣṭā kiśoraṃ vaḍavā yathā</li>
    </ul>
    <ul id="R_2.017.010">
        <li id="R_2.017.010ab">tam uvāca durādharṣaṃ rāghavaṃ sutam ātmanaḥ</li>
        <li id="R_2.017.010cd">kausalyā putravātsalyād idaṃ priyahitaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.017.011">
        <li id="R_2.017.011ab">vṛddhānāṃ dharmaśīlānāṃ rājarṣīṇāṃ mahātmanām</li>
        <li id="R_2.017.011cd">prāpnuhy āyuś ca kīrtiṃ ca dharmaṃ copahitaṃ kule</li>
    </ul>
    <ul id="R_2.017.012">
        <li id="R_2.017.012ab">satyapratijñaṃ pitaraṃ rājānaṃ paśya rāghava</li>
        <li id="R_2.017.012cd">adyaiva hi tvāṃ dharmātmā yauvarājye 'bhiṣekṣyati</li>
    </ul>
    <ul id="R_2.017.013">
        <li id="R_2.017.013ab">mātaraṃ rāghavaḥ kiṃ cit prasāryāñjalim abravīt</li>
        <li id="R_2.017.013cd">sa svabhāvavinītaś ca gauravāc ca tadānataḥ</li>
    </ul>
    <ul id="R_2.017.014">
        <li id="R_2.017.014ab">devi nūnaṃ na jānīṣe mahad bhayam upasthitam</li>
        <li id="R_2.017.014cd">idaṃ tava ca duḥkhāya vaidehyā lakṣmaṇasya ca</li>
    </ul>
    <ul id="R_2.017.015">
        <li id="R_2.017.015ab">caturdaśa hi varṣāṇi vatsyāmi vijane vane</li>
        <li id="R_2.017.015cd">madhumūlaphalair jīvan hitvā munivad āmiṣam</li>
    </ul>
    <ul id="R_2.017.016">
        <li id="R_2.017.016ab">bharatāya mahārājo yauvarājyaṃ prayacchati</li>
        <li id="R_2.017.016cd">māṃ punar daṇḍakāraṇyaṃ vivāsayati tāpasaṃ</li>
    </ul>
    <ul id="R_2.017.017">
        <li id="R_2.017.017ab">tām aduḥkhocitāṃ dṛṣṭvā patitāṃ kadalīm iva</li>
        <li id="R_2.017.017cd">rāmas tūtthāpayām āsa mātaraṃ gatacetasaṃ</li>
    </ul>
    <ul id="R_2.017.018">
        <li id="R_2.017.018ab">upāvṛtyotthitāṃ dīnāṃ vaḍavām iva vāhitām</li>
        <li id="R_2.017.018cd">pāṃśuguṇṭhitasarvāgnīṃ vimamarśa ca pāṇinā</li>
    </ul>
    <ul id="R_2.017.019">
        <li id="R_2.017.019ab">sā rāghavam upāsīnam asukhārtā sukhocitā</li>
        <li id="R_2.017.019cd">uvāca puruṣavyāghram upaśṛṇvati lakṣmaṇe</li>
    </ul>
    <ul id="R_2.017.020">
        <li id="R_2.017.020ab">yadi putra na jāyethā mama śokāya rāghava</li>
        <li id="R_2.017.020cd">na sma duḥkham ato bhūyaḥ paśyeyam aham aprajā</li>
    </ul>
    <ul id="R_2.017.021">
        <li id="R_2.017.021ab">eka eva hi vandhyāyāḥ śoko bhavati mānavaḥ</li>
        <li id="R_2.017.021cd">aprajāsmīti saṃtāpo na hy anyaḥ putra vidyate</li>
    </ul>
    <ul id="R_2.017.022">
        <li id="R_2.017.022ab">na dṛṣṭapūrvaṃ kalyāṇaṃ sukhaṃ vā patipauruṣe</li>
        <li id="R_2.017.022cd">api putre vipaśyeyam iti rāmāsthitaṃ mayā</li>
    </ul>
    <ul id="R_2.017.023">
        <li id="R_2.017.023ab">sā bahūny amanojñāni vākyāni hṛdayacchidām</li>
        <li id="R_2.017.023cd">ahaṃ śroṣye sapatnīnām avarāṇāṃ varā satī</li>
        <li id="R_2.017.023ef">ato duḥkhataraṃ kiṃ nu pramadānāṃ bhaviṣyati</li>
    </ul>
    <ul id="R_2.017.024">
        <li id="R_2.017.024ab">tvayi saṃnihite 'py evam aham āsaṃ nirākṛtā</li>
        <li id="R_2.017.024cd">kiṃ punaḥ proṣite tāta dhruvaṃ maraṇam eva me</li>
    </ul>
    <ul id="R_2.017.025">
        <li id="R_2.017.025ab">yo hi māṃ sevate kaś cid atha vāpy anuvartate</li>
        <li id="R_2.017.025cd">kaikeyyāḥ putram anvīkṣya sa jano nābhibhāṣate</li>
    </ul>
    <ul id="R_2.017.026">
        <li id="R_2.017.026ab">daśa sapta ca varṣāṇi tava jātasya rāghava</li>
        <li id="R_2.017.026cd">atītāni prakāṅkṣantyā mayā duḥkhaparikṣayam</li>
    </ul>
    <ul id="R_2.017.027">
        <li id="R_2.017.027ab">upavāsaiś ca yogaiś ca bahubhiś ca pariśramaiḥ</li>
        <li id="R_2.017.027cd">duḥkhaṃ saṃvardhito moghaṃ tvaṃ hi durgatayā mayā</li>
    </ul>
    <ul id="R_2.017.028">
        <li id="R_2.017.028ab">sthiraṃ tu hṛdayaṃ manye mamedaṃ yan na dīryate</li>
        <li id="R_2.017.028cd">prāvṛṣīva mahānadyāḥ spṛṣṭaṃ kūlaṃ navāmbhasā</li>
    </ul>
    <ul id="R_2.017.029">
        <li id="R_2.017.029ab">
            <seg type="pāda" n="a">mamaiva nūnaṃ maraṇaṃ na vidyate</seg>
            <seg type="pāda" n="b">na cāvakāśo 'sti yamakṣaye mama</seg>
        </li>
        <li id="R_2.017.029cd">
            <seg type="pāda" n="c">yad antako 'dyaiva na māṃ jihīrṣati</seg>
            <seg type="pāda" n="d">prasahya siṃho rudatīṃ mṛgīm iva</seg>
        </li>
    </ul>
    <ul id="R_2.017.030">
        <li id="R_2.017.030ab">
            <seg type="pāda" n="a">sthiraṃ hi nūnaṃ hṛdayaṃ mamāyasaṃ</seg>
            <seg type="pāda" n="b">na bhidyate yad bhuvi nāvadīryate</seg>
        </li>
        <li id="R_2.017.030cd">
            <seg type="pāda" n="c">anena duḥkhena ca deham arpitaṃ</seg>
            <seg type="pāda" n="d">dhruvaṃ hy akāle maraṇaṃ na vidyate</seg>
        </li>
    </ul>
    <ul id="R_2.017.031">
        <li id="R_2.017.031ab">
            <seg type="pāda" n="a">idaṃ tu duḥkhaṃ yad anarthakāni me</seg>
            <seg type="pāda" n="b">vratāni dānāni ca saṃyamāś ca hi</seg>
        </li>
        <li id="R_2.017.031cd">
            <seg type="pāda" n="c">tapaś ca taptaṃ yad apatyakāraṇāt</seg>
            <seg type="pāda" n="d">suniṣphalaṃ bījam ivoptam ūṣare</seg>
        </li>
    </ul>
    <ul id="R_2.017.032">
        <li id="R_2.017.032ab">
            <seg type="pāda" n="a">yadi hy akāle maraṇaṃ svayecchayā</seg>
            <seg type="pāda" n="b">labheta kaś cid guru duḥkha karśitaḥ</seg>
        </li>
        <li id="R_2.017.032cd">
            <seg type="pāda" n="c">gatāham adyaiva pareta saṃsadaṃ</seg>
            <seg type="pāda" n="d">vinā tvayā dhenur ivātmajena vai</seg>
        </li>
    </ul>
    <ul id="R_2.017.033">
        <li id="R_2.017.033ab">
            <seg type="pāda" n="a">bhṛśam asukham amarṣitā tadā</seg>
            <seg type="pāda" n="b">bahu vilalāpa samīkṣya rāghavam</seg>
        </li>
        <li id="R_2.017.033cd">
            <seg type="pāda" n="c">vyasanam upaniśāmya sā mahat</seg>
            <seg type="pāda" n="d">sutam iva baddham avekṣya kiṃnarī</seg>
        </li>
    </ul>
    <ul id="R_2.018.001">
        <li id="R_2.018.001ab">tathā tu vilapantīṃ tāṃ kausalyāṃ rāmamātaram</li>
        <li id="R_2.018.001cd">uvāca lakṣmaṇo dīnas tat kālasadṛśaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.018.002">
        <li id="R_2.018.002ab">na rocate mamāpy etad ārye yad rāghavo vanam</li>
        <li id="R_2.018.002cd">tyaktvā rājyaśriyaṃ gacchet striyā vākyavaśaṃ gataḥ</li>
    </ul>
    <ul id="R_2.018.003">
        <li id="R_2.018.003ab">viparītaś ca vṛddhaś ca viṣayaiś ca pradharṣitaḥ</li>
        <li id="R_2.018.003cd">nṛpaḥ kim iva na brūyāc codyamānaḥ samanmathaḥ</li>
    </ul>
    <ul id="R_2.018.004">
        <li id="R_2.018.004ab">nāsyāparādhaṃ paśyāmi nāpi doṣaṃ tathā vidham</li>
        <li id="R_2.018.004cd">yena nirvāsyate rāṣṭrād vanavāsāya rāghavaḥ</li>
    </ul>
    <ul id="R_2.018.005">
        <li id="R_2.018.005ab">na taṃ paśyāmy ahaṃ loke parokṣam api yo naraḥ</li>
        <li id="R_2.018.005cd">amitro 'pi nirasto 'pi yo 'sya doṣam udāharet</li>
    </ul>
    <ul id="R_2.018.006">
        <li id="R_2.018.006ab">devakalpam ṛjuṃ dāntaṃ ripūṇām api vatsalam</li>
        <li id="R_2.018.006cd">avekṣamāṇaḥ ko dharmaṃ tyajet putram akāraṇāt</li>
    </ul>
    <ul id="R_2.018.007">
        <li id="R_2.018.007ab">tad idaṃ vacanaṃ rājñaḥ punar bālyam upeyuṣaḥ</li>
        <li id="R_2.018.007cd">putraḥ ko hṛdaye kuryād rājavṛttam anusmaran</li>
    </ul>
    <ul id="R_2.018.008">
        <li id="R_2.018.008ab">yāvad eva na jānāti kaś cid artham imaṃ naraḥ</li>
        <li id="R_2.018.008cd">tāvad eva mayā sārdham ātmasthaṃ kuru śāsanam</li>
    </ul>
    <ul id="R_2.018.009">
        <li id="R_2.018.009ab">mayā pārśve sadhanuṣā tava guptasya rāghava</li>
        <li id="R_2.018.009cd">kaḥ samartho 'dhikaṃ kartuṃ kṛtāntasyeva tiṣṭhataḥ</li>
    </ul>
    <ul id="R_2.018.010">
        <li id="R_2.018.010ab">nirmanuṣyām imāṃ sarvām ayodhyāṃ manujarṣabha</li>
        <li id="R_2.018.010cd">kariṣyāmi śarais tīkṣṇair yadi sthāsyati vipriye</li>
    </ul>
    <ul id="R_2.018.011">
        <li id="R_2.018.011ab">bharatasyātha pakṣyo vā yo vāsya hitam icchati</li>
        <li id="R_2.018.011cd">sarvān etān vadhiṣyāmi mṛdur hi paribhūyate</li>
    </ul>
    <ul id="R_2.018.012">
        <li id="R_2.018.012ab">tvayā caiva mayā caiva kṛtvā vairam anuttamam</li>
        <li id="R_2.018.012cd">kasya śaktiḥ śriyaṃ dātuṃ bharatāyāriśāsana</li>
    </ul>
    <ul id="R_2.018.013">
        <li id="R_2.018.013ab">anurakto 'smi bhāvena bhrātaraṃ devi tattvataḥ</li>
        <li id="R_2.018.013cd">satyena dhanuṣā caiva datteneṣṭena te śape</li>
    </ul>
    <ul id="R_2.018.014">
        <li id="R_2.018.014ab">dīptam agnim araṇyaṃ vā yadi rāmaḥ pravekṣyate</li>
        <li id="R_2.018.014cd">praviṣṭaṃ tatra māṃ devi tvaṃ pūrvam avadhāraya</li>
    </ul>
    <ul id="R_2.018.015">
        <li id="R_2.018.015ab">harāmi vīryād duḥkhaṃ te tamaḥ sūrya ivoditaḥ</li>
        <li id="R_2.018.015cd">devī paśyatu me vīryaṃ rāghavaś caiva paśyatu</li>
    </ul>
    <ul id="R_2.018.016">
        <li id="R_2.018.016ab">etat tu vacanaṃ śrutvā lakṣmaṇasya mahātmanaḥ</li>
        <li id="R_2.018.016cd">uvāca rāmaṃ kausalyā rudantī śokalālasā</li>
    </ul>
    <ul id="R_2.018.017">
        <li id="R_2.018.017ab">bhrātus te vadataḥ putra lakṣmaṇasya śrutaṃ tvayā</li>
        <li id="R_2.018.017cd">yad atrānantaraṃ tat tvaṃ kuruṣva yadi rocate</li>
    </ul>
    <ul id="R_2.018.018">
        <li id="R_2.018.018ab">na cādharmyaṃ vacaḥ śrutvā sapatnyā mama bhāṣitam</li>
        <li id="R_2.018.018cd">vihāya śokasaṃtaptāṃ gantum arhasi mām itaḥ</li>
    </ul>
    <ul id="R_2.018.019">
        <li id="R_2.018.019ab">dharmajña yadi dharmiṣṭho dharmaṃ caritum icchasi</li>
        <li id="R_2.018.019cd">śuśrūṣa mām ihasthas tvaṃ cara dharmam anuttamam</li>
    </ul>
    <ul id="R_2.018.020">
        <li id="R_2.018.020ab">śuśrūṣur jananīṃ putra svagṛhe niyato vasan</li>
        <li id="R_2.018.020cd">pareṇa tapasā yuktaḥ kāśyapas tridivaṃ gataḥ</li>
    </ul>
    <ul id="R_2.018.021">
        <li id="R_2.018.021ab">yathaiva rājā pūjyas te gauraveṇa tathā hy aham</li>
        <li id="R_2.018.021cd">tvāṃ nāham anujānāmi na gantavyam ito vanam</li>
    </ul>
    <ul id="R_2.018.022">
        <li id="R_2.018.022ab">tvadviyogān na me kāryaṃ jīvitena sukhena vā</li>
        <li id="R_2.018.022cd">tvayā saha mama śreyas tṛṇānām api bhakṣaṇam</li>
    </ul>
    <ul id="R_2.018.023">
        <li id="R_2.018.023ab">yadi tvaṃ yāsyasi vanaṃ tyaktvā māṃ śokalālasām</li>
        <li id="R_2.018.023cd">ahaṃ prāyam ihāsiṣye na hi śakṣyāmi jīvitum</li>
    </ul>
    <ul id="R_2.018.024">
        <li id="R_2.018.024ab">tatas tvaṃ prāpsyase putra nirayaṃ lokaviśrutam</li>
        <li id="R_2.018.024cd">brahmahatyām ivādharmāt samudraḥ saritāṃ patiḥ</li>
    </ul>
    <ul id="R_2.018.025">
        <li id="R_2.018.025ab">vilapantīṃ tathā dīnāṃ kausalyāṃ jananīṃ tataḥ</li>
        <li id="R_2.018.025cd">uvāca rāmo dharmātmā vacanaṃ dharmasaṃhitam</li>
    </ul>
    <ul id="R_2.018.026">
        <li id="R_2.018.026ab">nāsti śaktiḥ pitur vākyaṃ samatikramituṃ mama</li>
        <li id="R_2.018.026cd">prasādaye tvāṃ śirasā gantum icchāmy ahaṃ vanam</li>
    </ul>
    <ul id="R_2.018.027">
        <li id="R_2.018.027ab">ṛṣiṇā ca pitur vākyaṃ kurvatā vratacāriṇā</li>
        <li id="R_2.018.027cd">gaur hatā jānatā dharmaṃ kaṇḍunāpi vipaścitā</li>
    </ul>
    <ul id="R_2.018.028">
        <li id="R_2.018.028ab">asmākaṃ ca kule pūrvaṃ sagarasyājñayā pituḥ</li>
        <li id="R_2.018.028cd">khanadbhiḥ sāgarair bhūtim avāptaḥ sumahān vadhaḥ</li>
    </ul>
    <ul id="R_2.018.029">
        <li id="R_2.018.029ab">jāmadagnyena rāmeṇa reṇukā jananī svayam</li>
        <li id="R_2.018.029cd">kṛttā paraśunāraṇye pitur vacanakāriṇā</li>
    </ul>
    <ul id="R_2.018.030">
        <li id="R_2.018.030ab">na khalv etan mayaikena kriyate pitṛśāsanam</li>
        <li id="R_2.018.030cd">pūrvair ayam abhipreto gato mārgo 'nugamyate</li>
    </ul>
    <ul id="R_2.018.031">
        <li id="R_2.018.031ab">tad etat tu mayā kāryaṃ kriyate bhuvi nānyathā</li>
        <li id="R_2.018.031cd">pitur hi vacanaṃ kurvan na kaś cin nāma hīyate</li>
    </ul>
    <ul id="R_2.018.032">
        <li id="R_2.018.032ab">tām evam uktvā jananīṃ lakṣmaṇaṃ punar abravīt</li>
        <li id="R_2.018.032cd">tava lakṣmaṇa jānāmi mayi sneham anuttamam</li>
        <li id="R_2.018.032ef">abhiprāyam avijñāya satyasya ca śamasya ca</li>
    </ul>
    <ul id="R_2.018.033">
        <li id="R_2.018.033ab">dharmo hi paramo loke dharme satyaṃ pratiṣṭhitam</li>
        <li id="R_2.018.033cd">dharmasaṃśritam etac ca pitur vacanam uttamam</li>
    </ul>
    <ul id="R_2.018.034">
        <li id="R_2.018.034ab">saṃśrutya ca pitur vākyaṃ mātur vā brāhmaṇasya vā</li>
        <li id="R_2.018.034cd">na kartavyaṃ vṛthā vīra dharmam āśritya tiṣṭhatā</li>
    </ul>
    <ul id="R_2.018.035">
        <li id="R_2.018.035ab">so 'haṃ na śakṣyāmi pitur niyogam ativartitum</li>
        <li id="R_2.018.035cd">pitur hi vacanād vīra kaikeyyāhaṃ pracoditaḥ</li>
    </ul>
    <ul id="R_2.018.036">
        <li id="R_2.018.036ab">tad enāṃ visṛjānāryāṃ kṣatradharmāśritāṃ matim</li>
        <li id="R_2.018.036cd">dharmam āśraya mā taikṣṇyaṃ madbuddhir anugamyatām</li>
    </ul>
    <ul id="R_2.018.037">
        <li id="R_2.018.037ab">tam evam uktvā sauhārdād bhrātaraṃ lakṣmaṇāgrajaḥ</li>
        <li id="R_2.018.037cd">uvāca bhūyaḥ kausalyāṃ prāñjaliḥ śirasānataḥ</li>
    </ul>
    <ul id="R_2.018.038">
        <li id="R_2.018.038ab">anumanyasva māṃ devi gamiṣyantam ito vanam</li>
        <li id="R_2.018.038cd">śāpitāsi mama prāṇaiḥ kuru svastyayanāni me</li>
        <li id="R_2.018.038ef">tīrṇapratijñaś ca vanāt punar eṣyāmy ahaṃ purīm</li>
    </ul>
    <ul id="R_2.018.039">
        <li id="R_2.018.039ab">
            <seg type="pāda" n="a">yaśo hy ahaṃ kevalarājyakāraṇān</seg>
            <seg type="pāda" n="b">na pṛṣṭhataḥ kartum alaṃ mahodayam</seg>
        </li>
        <li id="R_2.018.039cd">
            <seg type="pāda" n="c">adīrghakāle na tu devi jīvite</seg>
            <seg type="pāda" n="d">vṛṇe 'varām adya mahīm adharmataḥ</seg>
        </li>
    </ul>
    <ul id="R_2.018.040">
        <li id="R_2.018.040ab">
            <seg type="pāda" n="a">prasādayan naravṛṣabhaḥ sa mātaraṃ</seg>
            <seg type="pāda" n="b">parākramāj jigamiṣur eva daṇḍakān</seg>
        </li>
        <li id="R_2.018.040cd">
            <seg type="pāda" n="c">athānujaṃ bhṛśam anuśāsya darśanaṃ</seg>
            <seg type="pāda" n="d">cakāra tāṃ hṛdi jananīṃ pradakṣiṇam</seg>
        </li>
    </ul>
    <ul id="R_2.019.001">
        <li id="R_2.019.001ab">atha taṃ vyathayā dīnaṃ saviśeṣam amarṣitam</li>
        <li id="R_2.019.001cd">śvasantam iva nāgendraṃ roṣavisphāritekṣaṇam</li>
    </ul>
    <ul id="R_2.019.002">
        <li id="R_2.019.002ab">āsādya rāmaḥ saumitriṃ suhṛdaṃ bhrātaraṃ priyam</li>
        <li id="R_2.019.002cd">uvācedaṃ sa dhairyeṇa dhārayan sattvam ātmavān</li>
    </ul>
    <ul id="R_2.019.003">
        <li id="R_2.019.003ab">saumitre yo 'bhiṣekārthe mama saṃbhārasaṃbhramaḥ</li>
        <li id="R_2.019.003cd">abhiṣekanivṛttyarthe so 'stu saṃbhārasaṃbhramaḥ</li>
    </ul>
    <ul id="R_2.019.004">
        <li id="R_2.019.004ab">yasyā madabhiṣekārthaṃ mānasaṃ paritapyate</li>
        <li id="R_2.019.004cd">mātā naḥ sā yathā na syāt saviśaṅkā tathā kuru</li>
    </ul>
    <ul id="R_2.019.005">
        <li id="R_2.019.005ab">tasyāḥ śaṅkāmayaṃ duḥkhaṃ muhūrtam api notsahe</li>
        <li id="R_2.019.005cd">manasi pratisaṃjātaṃ saumitre 'ham upekṣitum</li>
    </ul>
    <ul id="R_2.019.006">
        <li id="R_2.019.006ab">na buddhipūrvaṃ nābuddhaṃ smarāmīha kadā cana</li>
        <li id="R_2.019.006cd">mātṝṇāṃ vā pitur vāhaṃ kṛtam alpaṃ ca vipriyam</li>
    </ul>
    <ul id="R_2.019.007">
        <li id="R_2.019.007ab">satyaḥ satyābhisaṃdhaś ca nityaṃ satyaparākramaḥ</li>
        <li id="R_2.019.007cd">paralokabhayād bhīto nirbhayo 'stu pitā mama</li>
    </ul>
    <ul id="R_2.019.008">
        <li id="R_2.019.008ab">tasyāpi hi bhaved asmin karmaṇy apratisaṃhṛte</li>
        <li id="R_2.019.008cd">satyaṃ neti manastāpas tasya tāpas tapec ca mām</li>
    </ul>
    <ul id="R_2.019.009">
        <li id="R_2.019.009ab">abhiṣekavidhānaṃ tu tasmāt saṃhṛtya lakṣmaṇa</li>
        <li id="R_2.019.009cd">anvag evāham icchāmi vanaṃ gantum itaḥ punaḥ</li>
    </ul>
    <ul id="R_2.019.010">
        <li id="R_2.019.010ab">mama pravrājanād adya kṛtakṛtyā nṛpātmajā</li>
        <li id="R_2.019.010cd">sutaṃ bharatam avyagram abhiṣecayitā tataḥ</li>
    </ul>
    <ul id="R_2.019.011">
        <li id="R_2.019.011ab">mayi cīrājinadhare jaṭāmaṇḍaladhāriṇi</li>
        <li id="R_2.019.011cd">gate 'raṇyaṃ ca kaikeyyā bhaviṣyati manaḥsukham</li>
    </ul>
    <ul id="R_2.019.012">
        <li id="R_2.019.012ab">buddhiḥ praṇītā yeneyaṃ manaś ca susamāhitam</li>
        <li id="R_2.019.012cd">tat tu nārhāmi saṃkleṣṭuṃ pravrajiṣyāmi māciram</li>
    </ul>
    <ul id="R_2.019.013">
        <li id="R_2.019.013ab">kṛtāntas tv eva saumitre draṣṭavyo matpravāsane</li>
        <li id="R_2.019.013cd">rājyasya ca vitīrṇasya punar eva nivartane</li>
    </ul>
    <ul id="R_2.019.014">
        <li id="R_2.019.014ab">kaikeyyāḥ pratipattir hi kathaṃ syān mama pīḍane</li>
        <li id="R_2.019.014cd">yadi bhāvo na daivo 'yaṃ kṛtāntavihito bhavet</li>
    </ul>
    <ul id="R_2.019.015">
        <li id="R_2.019.015ab">jānāsi hi yathā saumya na mātṛṣu mamāntaram</li>
        <li id="R_2.019.015cd">bhūtapūrvaṃ viśeṣo vā tasyā mayi sute 'pi vā</li>
    </ul>
    <ul id="R_2.019.016">
        <li id="R_2.019.016ab">so 'bhiṣekanivṛttyarthaiḥ pravāsārthaiś ca durvacaiḥ</li>
        <li id="R_2.019.016cd">ugrair vākyair ahaṃ tasyā nānyad daivāt samarthaye</li>
    </ul>
    <ul id="R_2.019.017">
        <li id="R_2.019.017ab">kathaṃ prakṛtisaṃpannā rājaputrī tathāguṇā</li>
        <li id="R_2.019.017cd">brūyāt sā prākṛteva strī matpīḍāṃ bhartṛsaṃnidhau</li>
    </ul>
    <ul id="R_2.019.018">
        <li id="R_2.019.018ab">yad acintyaṃ tu tad daivaṃ bhūteṣv api na hanyate</li>
        <li id="R_2.019.018cd">vyaktaṃ mayi ca tasyāṃ ca patito hi viparyayaḥ</li>
    </ul>
    <ul id="R_2.019.019">
        <li id="R_2.019.019ab">kaś cid daivena saumitre yoddhum utsahate pumān</li>
        <li id="R_2.019.019cd">yasya na grahaṇaṃ kiṃ cit karmaṇo 'nyatra dṛśyate</li>
    </ul>
    <ul id="R_2.019.020">
        <li id="R_2.019.020ab">sukhaduḥkhe bhayakrodhau lābhālābhau bhavābhavau</li>
        <li id="R_2.019.020cd">yasya kiṃ cit tathābhūtaṃ nanu daivasya karma tat</li>
    </ul>
    <ul id="R_2.019.021">
        <li id="R_2.019.021ab">vyāhate 'py abhiṣeke me paritāpo na vidyate</li>
        <li id="R_2.019.021cd">tasmād aparitāpaḥ saṃs tvam apy anuvidhāya mām</li>
        <li id="R_2.019.021ef">pratisaṃhāraya kṣipram ābhiṣecanikīṃ kriyām</li>
    </ul>
    <ul id="R_2.019.022">
        <li id="R_2.019.022ab">
            <seg type="pāda" n="a">na lakṣmaṇāsmin mama rājyavighne</seg>
            <seg type="pāda" n="b">mātā yavīyasy atiśaṅkanīyā</seg>
        </li>
        <li id="R_2.019.022cd">
            <seg type="pāda" n="c">daivābhipannā hi vadanty aniṣṭaṃ</seg>
            <seg type="pāda" n="d">jānāsi daivaṃ ca tathāprabhāvam</seg>
        </li>
    </ul>
    <ul id="R_2.020.001">
        <li id="R_2.020.001ab">iti bruvati rāme tu lakṣmaṇo 'dhaḥśirā muhuḥ</li>
        <li id="R_2.020.001cd">śrutvā madhyaṃ jagāmeva manasā duḥkhaharṣayoḥ</li>
    </ul>
    <ul id="R_2.020.002">
        <li id="R_2.020.002ab">tadā tu baddhvā bhrukuṭīṃ bhruvor madhye nararṣabha</li>
        <li id="R_2.020.002cd">niśaśvāsa mahāsarpo bilastha iva roṣitaḥ</li>
    </ul>
    <ul id="R_2.020.003">
        <li id="R_2.020.003ab">tasya duṣprativīkṣyaṃ tad bhrukuṭīsahitaṃ tadā</li>
        <li id="R_2.020.003cd">babhau kruddhasya siṃhasya mukhasya sadṛśaṃ mukham</li>
    </ul>
    <ul id="R_2.020.004">
        <li id="R_2.020.004ab">agrahas taṃ vidhunvaṃs tu hastī hastam ivātmanaḥ</li>
        <li id="R_2.020.004cd">tiryag ūrdhvaṃ śarīre ca pātayitvā śirodharām</li>
    </ul>
    <ul id="R_2.020.005">
        <li id="R_2.020.005ab">agrākṣṇā vīkṣamāṇas tu tiryag bhrātaram abravīt</li>
        <li id="R_2.020.005cd">asthāne saṃbhramo yasya jāto vai sumahān ayam</li>
    </ul>
    <ul id="R_2.020.006">
        <li id="R_2.020.006ab">dharmadoṣaprasaṅgena lokasyānatiśaṅkayā</li>
        <li id="R_2.020.006cd">kathaṃ hy etad asaṃbhrāntas tvadvidho vaktum arhati</li>
    </ul>
    <ul id="R_2.020.007">
        <li id="R_2.020.007ab">yathā daivam aśauṇḍīraṃ śauṇḍīraḥ kṣatriyarṣabhaḥ</li>
        <li id="R_2.020.007cd">kiṃ nāma kṛpaṇaṃ daivam aśaktam abhiśaṃsasi</li>
    </ul>
    <ul id="R_2.020.008">
        <li id="R_2.020.008ab">pāpayos te kathaṃ nāma tayoḥ śaṅkā na vidyate</li>
        <li id="R_2.020.008cd">santi dharmopadhāḥ ślakṣṇā dharmātman kiṃ na budhyase</li>
    </ul>
    <ul id="R_2.020.009">
        <li id="R_2.020.009ab">lokavidviṣṭam ārabdhaṃ tvadanyasyābhiṣecanam</li>
        <li id="R_2.020.009cd">yeneyam āgatā dvaidhaṃ tava buddhir mahīpate</li>
        <li id="R_2.020.009ef">sa hi dharmo mama dveṣyaḥ prasaṅgād yasya muhyasi</li>
    </ul>
    <ul id="R_2.020.010">
        <li id="R_2.020.010ab">yady api pratipattis te daivī cāpi tayor matam</li>
        <li id="R_2.020.010cd">tathāpy upekṣaṇīyaṃ te na me tad api rocate</li>
    </ul>
    <ul id="R_2.020.011">
        <li id="R_2.020.011ab">viklavo vīryahīno yaḥ sa daivam anuvartate</li>
        <li id="R_2.020.011cd">vīrāḥ saṃbhāvitātmāno na daivaṃ paryupāsate</li>
    </ul>
    <ul id="R_2.020.012">
        <li id="R_2.020.012ab">daivaṃ puruṣakāreṇa yaḥ samarthaḥ prabādhitum</li>
        <li id="R_2.020.012cd">na daivena vipannārthaḥ puruṣaḥ so 'vasīdati</li>
    </ul>
    <ul id="R_2.020.013">
        <li id="R_2.020.013ab">drakṣyanti tv adya daivasya pauruṣaṃ puruṣasya ca</li>
        <li id="R_2.020.013cd">daivamānuṣayor adya vyaktā vyaktir bhaviṣyati</li>
    </ul>
    <ul id="R_2.020.014">
        <li id="R_2.020.014ab">adya matpauruṣahataṃ daivaṃ drakṣyanti vai janāḥ</li>
        <li id="R_2.020.014cd">yad daivād āhataṃ te 'dya dṛṣṭaṃ rājyābhiṣecanam</li>
    </ul>
    <ul id="R_2.020.015">
        <li id="R_2.020.015ab">atyaṅkuśam ivoddāmaṃ gajaṃ madabaloddhatam</li>
        <li id="R_2.020.015cd">pradhāvitam ahaṃ daivaṃ pauruṣeṇa nivartaye</li>
    </ul>
    <ul id="R_2.020.016">
        <li id="R_2.020.016ab">lokapālāḥ samastās te nādya rāmābhiṣecanam</li>
        <li id="R_2.020.016cd">na ca kṛtsnās trayo lokā vihanyuḥ kiṃ punaḥ pitā</li>
    </ul>
    <ul id="R_2.020.017">
        <li id="R_2.020.017ab">yair vivāsas tavāraṇye mitho rājan samarthitaḥ</li>
        <li id="R_2.020.017cd">araṇye te vivatsyanti caturdaśa samās tathā</li>
    </ul>
    <ul id="R_2.020.018">
        <li id="R_2.020.018ab">ahaṃ tadāśāṃ chetsyāmi pitus tasyāś ca yā tava</li>
        <li id="R_2.020.018cd">abhiṣekavighātena putrarājyāya vartate</li>
    </ul>
    <ul id="R_2.020.019">
        <li id="R_2.020.019ab">madbalena viruddhāya na syād daivabalaṃ tathā</li>
        <li id="R_2.020.019cd">prabhaviṣyati duḥkhāya yathograṃ pauruṣaṃ mama</li>
    </ul>
    <ul id="R_2.020.020">
        <li id="R_2.020.020ab">ūrdhvaṃ varṣasahasrānte prajāpālyam anantaram</li>
        <li id="R_2.020.020cd">āryaputrāḥ kariṣyanti vanavāsaṃ gate tvayi</li>
    </ul>
    <ul id="R_2.020.021">
        <li id="R_2.020.021ab">pūrvarājarṣivṛttyā hi vanavāso vidhīyate</li>
        <li id="R_2.020.021cd">prajā nikṣipya putreṣu putravat paripālane</li>
    </ul>
    <ul id="R_2.020.022">
        <li id="R_2.020.022ab">sa ced rājany anekāgre rājyavibhramaśaṅkayā</li>
        <li id="R_2.020.022cd">naivam icchasi dharmātman rājyaṃ rāma tvam ātmani</li>
    </ul>
    <ul id="R_2.020.023">
        <li id="R_2.020.023ab">pratijāne ca te vīra mā bhūvaṃ vīralokabhāk</li>
        <li id="R_2.020.023cd">rājyaṃ ca tava rakṣeyam ahaṃ veleva sāgaram</li>
    </ul>
    <ul id="R_2.020.024">
        <li id="R_2.020.024ab">maṅgalair abhiṣiñcasva tatra tvaṃ vyāpṛto bhava</li>
        <li id="R_2.020.024cd">aham eko mahīpālān alaṃ vārayituṃ balāt</li>
    </ul>
    <ul id="R_2.020.025">
        <li id="R_2.020.025ab">na śobhārthāv imau bāhū na dhanur bhūṣaṇāya me</li>
        <li id="R_2.020.025cd">nāsirābandhanārthāya na śarāḥ stambhahetavaḥ</li>
    </ul>
    <ul id="R_2.020.026">
        <li id="R_2.020.026ab">amitradamanārthaṃ me sarvam etac catuṣṭayam</li>
        <li id="R_2.020.026cd">na cāhaṃ kāmaye 'tyarthaṃ yaḥ syāc chatrur mato mama</li>
    </ul>
    <ul id="R_2.020.027">
        <li id="R_2.020.027ab">asinā tīkṣṇadhāreṇa vidyuccalitavarcasā</li>
        <li id="R_2.020.027cd">pragṛhītena vai śatruṃ vajriṇaṃ vā na kalpaye</li>
    </ul>
    <ul id="R_2.020.028">
        <li id="R_2.020.028ab">khaḍganiṣpeṣaniṣpiṣṭair gahanā duścarā ca me</li>
        <li id="R_2.020.028cd">hastyaśvanarahastoruśirobhir bhavitā mahī</li>
    </ul>
    <ul id="R_2.020.029">
        <li id="R_2.020.029ab">khaḍgadhārāhatā me 'dya dīpyamānā ivādrayaḥ</li>
        <li id="R_2.020.029cd">patiṣyanti dvipā bhūmau meghā iva savidyutaḥ</li>
    </ul>
    <ul id="R_2.020.030">
        <li id="R_2.020.030ab">baddhagodhāṅgulitrāṇe pragṛhītaśarāsane</li>
        <li id="R_2.020.030cd">kathaṃ puruṣamānī syāt puruṣāṇāṃ mayi sthite</li>
    </ul>
    <ul id="R_2.020.031">
        <li id="R_2.020.031ab">bahubhiś caikam atyasyann ekena ca bahūñ janān</li>
        <li id="R_2.020.031cd">viniyokṣyāmy ahaṃ bāṇān nṛvājigajamarmasu</li>
    </ul>
    <ul id="R_2.020.032">
        <li id="R_2.020.032ab">adya me 'straprabhāvasya prabhāvaḥ prabhaviṣyati</li>
        <li id="R_2.020.032cd">rājñaś cāprabhutāṃ kartuṃ prabhutvaṃ ca tava prabho</li>
    </ul>
    <ul id="R_2.020.033">
        <li id="R_2.020.033ab">adya candanasārasya keyūrāmokṣaṇasya ca</li>
        <li id="R_2.020.033cd">vasūnāṃ ca vimokṣasya suhṛdāṃ pālanasya ca</li>
    </ul>
    <ul id="R_2.020.034">
        <li id="R_2.020.034ab">anurūpāv imau bāhū rāma karma kariṣyataḥ</li>
        <li id="R_2.020.034cd">abhiṣecanavighnasya kartṝṇāṃ te nivāraṇe</li>
    </ul>
    <ul id="R_2.020.035">
        <li id="R_2.020.035ab">
            <seg type="pāda" n="a">bravīhi ko 'dyaiva mayā viyujyatāṃ</seg>
            <seg type="pāda" n="b">tavāsuhṛt prāṇayaśaḥ suhṛjjanaiḥ</seg>
        </li>
        <li id="R_2.020.035cd">
            <seg type="pāda" n="c">yathā taveyaṃ vasudhā vaśe bhavet</seg>
            <seg type="pāda" n="d">tathaiva māṃ śādhi tavāsmi kiṃkaraḥ</seg>
        </li>
    </ul>
    <ul id="R_2.020.036">
        <li id="R_2.020.036ab">
            <seg type="pāda" n="a">vimṛjya bāṣpaṃ parisāntvya cāsakṛt</seg>
            <seg type="pāda" n="b">sa lakṣmaṇaṃ rāghavavaṃśavardhanaḥ</seg>
        </li>
        <li id="R_2.020.036cd">
            <seg type="pāda" n="c">uvāca pitrye vacane vyavasthitaṃ</seg>
            <seg type="pāda" n="d">nibodha mām eṣa hi saumya satpathaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.021.001">
        <li id="R_2.021.001ab">taṃ samīkṣya tv avahitaṃ pitur nirdeśapālane</li>
        <li id="R_2.021.001cd">kausalyā bāṣpasaṃruddhā vaco dharmiṣṭham abravīt</li>
    </ul>
    <ul id="R_2.021.002">
        <li id="R_2.021.002ab">adṛṣṭaduḥkho dharmātmā sarvabhūtapriyaṃvadaḥ</li>
        <li id="R_2.021.002cd">mayi jāto daśarathāt katham uñchena vartayet</li>
    </ul>
    <ul id="R_2.021.003">
        <li id="R_2.021.003ab">yasya bhṛtyāś ca dāsāś ca mṛṣṭāny annāni bhuñjate</li>
        <li id="R_2.021.003cd">kathaṃ sa bhokṣyate nātho vane mūlaphalāny ayam</li>
    </ul>
    <ul id="R_2.021.004">
        <li id="R_2.021.004ab">ka etac chraddadhec chrutvā kasya vā na bhaved bhayam</li>
        <li id="R_2.021.004cd">guṇavān dayito rājño rāghavo yad vivāsyate</li>
    </ul>
    <ul id="R_2.021.005">
        <li id="R_2.021.005ab">tvayā vihīnām iha māṃ śokāgnir atulo mahān</li>
        <li id="R_2.021.005cd">pradhakṣyati yathā kakṣaṃ citrabhānur himātyaye</li>
    </ul>
    <ul id="R_2.021.006">
        <li id="R_2.021.006ab">kathaṃ hi dhenuḥ svaṃ vatsaṃ gacchantaṃ nānugacchati</li>
        <li id="R_2.021.006cd">ahaṃ tvānugamiṣyāmi yatra putra gamiṣyasi</li>
    </ul>
    <ul id="R_2.021.007">
        <li id="R_2.021.007ab">tathā nigaditaṃ mātrā tad vākyaṃ puruṣarṣabhaḥ</li>
        <li id="R_2.021.007cd">śrutvā rāmo 'bravīd vākyaṃ mātaraṃ bhṛśaduḥkhitām</li>
    </ul>
    <ul id="R_2.021.008">
        <li id="R_2.021.008ab">kaikeyyā vañcito rājā mayi cāraṇyam āśrite</li>
        <li id="R_2.021.008cd">bhavatyā ca parityakto na nūnaṃ vartayiṣyati</li>
    </ul>
    <ul id="R_2.021.009">
        <li id="R_2.021.009ab">bhartuḥ kila parityāgo nṛśaṃsaḥ kevalaṃ striyāḥ</li>
        <li id="R_2.021.009cd">sa bhavatyā na kartavyo manasāpi vigarhitaḥ</li>
    </ul>
    <ul id="R_2.021.010">
        <li id="R_2.021.010ab">yāvaj jīvati kākutsthaḥ pitā me jagatīpatiḥ</li>
        <li id="R_2.021.010cd">śuśrūṣā kriyatāṃ tāvat sa hi dharmaḥ sanātanaḥ</li>
    </ul>
    <ul id="R_2.021.011">
        <li id="R_2.021.011ab">evam uktā tu rāmeṇa kausalyā śubha darśanā</li>
        <li id="R_2.021.011cd">tathety uvāca suprītā rāmam akliṣṭakāriṇam</li>
    </ul>
    <ul id="R_2.021.012">
        <li id="R_2.021.012ab">evam uktas tu vacanaṃ rāmo dharmabhṛtāṃ varaḥ</li>
        <li id="R_2.021.012cd">bhūyas tām abravīd vākyaṃ mātaraṃ bhṛśaduḥkhitām</li>
    </ul>
    <ul id="R_2.021.013">
        <li id="R_2.021.013ab">mayā caiva bhavatyā ca kartavyaṃ vacanaṃ pituḥ</li>
        <li id="R_2.021.013cd">rājā bhartā guruḥ śreṣṭhaḥ sarveṣām īśvaraḥ prabhuḥ</li>
    </ul>
    <ul id="R_2.021.014">
        <li id="R_2.021.014ab">imāni tu mahāraṇye vihṛtya nava pañca ca</li>
        <li id="R_2.021.014cd">varṣāṇi paramaprītaḥ sthāsyāmi vacane tava</li>
    </ul>
    <ul id="R_2.021.015">
        <li id="R_2.021.015ab">evam uktā priyaṃ putraṃ bāṣpapūrṇānanā tadā</li>
        <li id="R_2.021.015cd">uvāca paramārtā tu kausalyā putravatsalā</li>
    </ul>
    <ul id="R_2.021.016">
        <li id="R_2.021.016ab">āsāṃ rāma sapatnīnāṃ vastuṃ madhye na me kṣamam</li>
        <li id="R_2.021.016cd">naya mām api kākutstha vanaṃ vanyaṃ mṛgīṃ yathā</li>
        <li id="R_2.021.016ef">yadi te gamane buddhiḥ kṛtā pitur apekṣayā</li>
    </ul>
    <ul id="R_2.021.017">
        <li id="R_2.021.017ab">tāṃ tathā rudatīṃ rāmo rudan vacanam abravīt</li>
        <li id="R_2.021.017cd">jīvantyā hi striyā bhartā daivataṃ prabhur eva ca</li>
        <li id="R_2.021.017ef">bhavatyā mama caivādya rājā prabhavati prabhuḥ</li>
    </ul>
    <ul id="R_2.021.018">
        <li id="R_2.021.018ab">bharataś cāpi dharmātmā sarvabhūtapriyaṃvadaḥ</li>
        <li id="R_2.021.018cd">bhavatīm anuvarteta sa hi dharmarataḥ sadā</li>
    </ul>
    <ul id="R_2.021.019">
        <li id="R_2.021.019ab">yathā mayi tu niṣkrānte putraśokena pārthivaḥ</li>
        <li id="R_2.021.019cd">śramaṃ nāvāpnuyāt kiṃ cid apramattā tathā kuru</li>
    </ul>
    <ul id="R_2.021.020">
        <li id="R_2.021.020ab">vratopavāsaniratā yā nārī paramottamā</li>
        <li id="R_2.021.020cd">bhartāraṃ nānuvarteta sā ca pāpagatir bhavet</li>
    </ul>
    <ul id="R_2.021.021">
        <li id="R_2.021.021ab">śuśrūṣam eva kurvīta bhartuḥ priyahite ratā</li>
        <li id="R_2.021.021cd">eṣa dharmaḥ purā dṛṣṭo loke vede śrutaḥ smṛtaḥ</li>
    </ul>
    <ul id="R_2.021.022">
        <li id="R_2.021.022ab">pūjyās te matkṛte devi brāhmaṇāś caiva suvratāḥ</li>
        <li id="R_2.021.022cd">evaṃ kālaṃ pratīkṣasva mamāgamanakāṅkṣiṇī</li>
    </ul>
    <ul id="R_2.021.023">
        <li id="R_2.021.023ab">prāpsyase paramaṃ kāmaṃ mayi pratyāgate sati</li>
        <li id="R_2.021.023cd">yadi dharmabhṛtāṃ śreṣṭho dhārayiṣyati jīvitam</li>
    </ul>
    <ul id="R_2.021.024">
        <li id="R_2.021.024ab">evam uktā tu rāmeṇa bāṣpaparyākulekṣaṇā</li>
        <li id="R_2.021.024cd">kausalyā putraśokārtā rāmaṃ vacanam abravīt</li>
        <li id="R_2.021.024ef">gaccha putra tvam ekāgro bhadraṃ te 'stu sadā vibho</li>
    </ul>
    <ul id="R_2.021.025">
        <li id="R_2.021.025ab">
            <seg type="pāda" n="a">tathā hi rāmaṃ vanavāsaniścitaṃ</seg>
            <seg type="pāda" n="b">samīkṣya devī parameṇa cetasā</seg>
        </li>
        <li id="R_2.021.025cd">
            <seg type="pāda" n="c">uvāca rāmaṃ śubhalakṣaṇaṃ vaco</seg>
            <seg type="pāda" n="d">babhūva ca svastyayanābhikāṅkṣiṇī</seg>
        </li>
    </ul>
    <ul id="R_2.022.001">
        <li id="R_2.022.001ab">sāpanīya tam āyāsam upaspṛśya jalaṃ śuci</li>
        <li id="R_2.022.001cd">cakāra mātā rāmasya maṅgalāni manasvinī</li>
    </ul>
    <ul id="R_2.022.002">
        <li id="R_2.022.002ab">svasti sādhyāś ca viśve ca marutaś ca maharṣayaḥ</li>
        <li id="R_2.022.002cd">svasti dhātā vidhātā ca svasti pūṣā bhago 'ryamā</li>
    </ul>
    <ul id="R_2.022.003">
        <li id="R_2.022.003ab">ṛtavaś caiva pakṣāś ca māsāḥ saṃvatsarāḥ kṣapāḥ</li>
        <li id="R_2.022.003cd">dināni ca muhūrtāś ca svasti kurvantu te sadā</li>
    </ul>
    <ul id="R_2.022.004">
        <li id="R_2.022.004ab">smṛtir dhṛtiś ca dharmaś ca pāntu tvāṃ putra sarvataḥ</li>
        <li id="R_2.022.004cd">skandaś ca bhagavān devaḥ somaś ca sabṛhaspatiḥ</li>
    </ul>
    <ul id="R_2.022.005">
        <li id="R_2.022.005ab">saptarṣayo nāradaś ca te tvāṃ rakṣantu sarvataḥ</li>
        <li id="R_2.022.005cd">nakṣatrāṇi ca sarvāṇi grahāś ca sahadevatāḥ</li>
        <li id="R_2.022.005ef">mahāvanāni carato muniveṣasya dhīmataḥ</li>
    </ul>
    <ul id="R_2.022.006">
        <li id="R_2.022.006ab">plavagā vṛścikā daṃśā maśakāś caiva kānane</li>
        <li id="R_2.022.006cd">sarīsṛpāś ca kīṭāś ca mā bhūvan gahane tava</li>
    </ul>
    <ul id="R_2.022.007">
        <li id="R_2.022.007ab">mahādvipāś ca siṃhāś ca vyāghrā ṛkṣāś ca daṃṣṭriṇaḥ</li>
        <li id="R_2.022.007cd">mahiṣāḥ śṛṅgiṇo raudrā na te druhyantu putraka</li>
    </ul>
    <ul id="R_2.022.008">
        <li id="R_2.022.008ab">nṛmāṃsabhojanā raudrā ye cānye sattvajātayaḥ</li>
        <li id="R_2.022.008cd">mā ca tvāṃ hiṃsiṣuḥ putra mayā saṃpūjitās tv iha</li>
    </ul>
    <ul id="R_2.022.009">
        <li id="R_2.022.009ab">āgamās te śivāḥ santu sidhyantu ca parākramāḥ</li>
        <li id="R_2.022.009cd">sarvasaṃpattayo rāma svastimān gaccha putraka</li>
    </ul>
    <ul id="R_2.022.010">
        <li id="R_2.022.010ab">svasti te 'stv āntarikṣebhyaḥ pārthivebhyaḥ punaḥ punaḥ</li>
        <li id="R_2.022.010cd">sarvebhyaś caiva devebhyo ye ca te paripanthinaḥ</li>
    </ul>
    <ul id="R_2.022.011">
        <li id="R_2.022.011ab">sarvalokaprabhur brahmā bhūtabhartā tatharṣayaḥ</li>
        <li id="R_2.022.011cd">ye ca śeṣāḥ surās te tvāṃ rakṣantu vanavāsinam</li>
    </ul>
    <ul id="R_2.022.012">
        <li id="R_2.022.012ab">iti mālyaiḥ suragaṇān gandhaiś cāpi yaśasvinī</li>
        <li id="R_2.022.012cd">stutibhiś cānurūpābhir ānarcāyatalocanā</li>
    </ul>
    <ul id="R_2.022.013">
        <li id="R_2.022.013ab">yan maṅgalaṃ sahasrākṣe sarvadevanamaskṛte</li>
        <li id="R_2.022.013cd">vṛtranāśe samabhavat tat te bhavatu maṅgalam</li>
    </ul>
    <ul id="R_2.022.014">
        <li id="R_2.022.014ab">yan maṅgalaṃ suparṇasya vinatākalpayat purā</li>
        <li id="R_2.022.014cd">amṛtaṃ prārthayānasya tat te bhavatu maṅgalam</li>
    </ul>
    <ul id="R_2.022.015">
        <li id="R_2.022.015ab">oṣadhīṃ cāpi siddhārthāṃ viśalyakaraṇīṃ śubhām</li>
        <li id="R_2.022.015cd">cakāra rakṣāṃ kausalyā mantrair abhijajāpa ca</li>
    </ul>
    <ul id="R_2.022.016">
        <li id="R_2.022.016ab">ānamya mūrdhni cāghrāya pariṣvajya yaśasvinī</li>
        <li id="R_2.022.016cd">avadat putra siddhārtho gaccha rāma yathāsukham</li>
    </ul>
    <ul id="R_2.022.017">
        <li id="R_2.022.017ab">arogaṃ sarvasiddhārtham ayodhyāṃ punar āgatam</li>
        <li id="R_2.022.017cd">paśyāmi tvāṃ sukhaṃ vatsa susthitaṃ rājaveśmani</li>
    </ul>
    <ul id="R_2.022.018">
        <li id="R_2.022.018ab">
            <seg type="pāda" n="a">mayārcitā devagaṇāḥ śivādayo</seg>
            <seg type="pāda" n="b">maharṣayo bhūtamahāsuroragāḥ</seg>
        </li>
        <li id="R_2.022.018cd">
            <seg type="pāda" n="c">abhiprayātasya vanaṃ cirāya te</seg>
            <seg type="pāda" n="d">hitāni kāṅkṣantu diśaś ca rāghava</seg>
        </li>
    </ul>
    <ul id="R_2.022.019">
        <li id="R_2.022.019ab">
            <seg type="pāda" n="a">itīva cāśrupratipūrṇalocanā</seg>
            <seg type="pāda" n="b">samāpya ca svastyayanaṃ yathāvidhi</seg>
        </li>
        <li id="R_2.022.019cd">
            <seg type="pāda" n="c">pradakṣiṇaṃ caiva cakāra rāghavaṃ</seg>
            <seg type="pāda" n="d">punaḥ punaś cāpi nipīḍya sasvaje</seg>
        </li>
    </ul>
    <ul id="R_2.022.020">
        <li id="R_2.022.020ab">
            <seg type="pāda" n="a">tathā tu devyā sa kṛtapradakṣiṇo</seg>
            <seg type="pāda" n="b">nipīḍya mātuś caraṇau punaḥ punaḥ</seg>
        </li>
        <li id="R_2.022.020cd">
            <seg type="pāda" n="c">jagāma sītānilayaṃ mahāyaśāḥ</seg>
            <seg type="pāda" n="d">sa rāghavaḥ prajvalitaḥ svayā śriyā</seg>
        </li>
    </ul>
    <ul id="R_2.023.001">
        <li id="R_2.023.001ab">abhivādya tu kausalyāṃ rāmaḥ saṃprasthito vanam</li>
        <li id="R_2.023.001cd">kṛtasvastyayano mātrā dharmiṣṭhe vartmani sthitaḥ</li>
    </ul>
    <ul id="R_2.023.002">
        <li id="R_2.023.002ab">virājayan rājasuto rājamārgaṃ narair vṛtam</li>
        <li id="R_2.023.002cd">hṛdayāny āmamantheva janasya guṇavattayā</li>
    </ul>
    <ul id="R_2.023.003">
        <li id="R_2.023.003ab">vaidehī cāpi tat sarvaṃ na śuśrāva tapasvinī</li>
        <li id="R_2.023.003cd">tad eva hṛdi tasyāś ca yauvarājyābhiṣecanam</li>
    </ul>
    <ul id="R_2.023.004">
        <li id="R_2.023.004ab">devakāryaṃ sma sā kṛtvā kṛtajñā hṛṣṭacetanā</li>
        <li id="R_2.023.004cd">abhijñā rājadharmāṇāṃ rājaputraṃ pratīkṣate</li>
    </ul>
    <ul id="R_2.023.005">
        <li id="R_2.023.005ab">praviveśātha rāmas tu svaveśma suvibhūṣitam</li>
        <li id="R_2.023.005cd">prahṛṣṭajanasaṃpūrṇaṃ hriyā kiṃ cid avāṅmukhaḥ</li>
    </ul>
    <ul id="R_2.023.006">
        <li id="R_2.023.006ab">atha sītā samutpatya vepamānā ca taṃ patim</li>
        <li id="R_2.023.006cd">apaśyac chokasaṃtaptaṃ cintāvyākulilendriyam</li>
    </ul>
    <ul id="R_2.023.007">
        <li id="R_2.023.007ab">vivarṇavadanaṃ dṛṣṭvā taṃ prasvinnam amarṣaṇam</li>
        <li id="R_2.023.007cd">āha duḥkhābhisaṃtaptā kim idānīm idaṃ prabho</li>
    </ul>
    <ul id="R_2.023.008">
        <li id="R_2.023.008ab">adya bārhaspataḥ śrīmān yuktaḥ puṣyo na rāghava</li>
        <li id="R_2.023.008cd">procyate brāhmaṇaiḥ prājñaiḥ kena tvam asi durmanāḥ</li>
    </ul>
    <ul id="R_2.023.009">
        <li id="R_2.023.009ab">na te śataśalākena jalaphenanibhena ca</li>
        <li id="R_2.023.009cd">āvṛtaṃ vadanaṃ valgu chatreṇābhivirājate</li>
    </ul>
    <ul id="R_2.023.010">
        <li id="R_2.023.010ab">vyajanābhyāṃ ca mukhyābhyāṃ śatapatranibhekṣaṇam</li>
        <li id="R_2.023.010cd">candrahaṃsaprakāśābhyāṃ vījyate na tavānanam</li>
    </ul>
    <ul id="R_2.023.011">
        <li id="R_2.023.011ab">vāgmino bandinaś cāpi prahṛṣṭās tvaṃ nararṣabha</li>
        <li id="R_2.023.011cd">stuvanto nādya dṛśyante maṅgalaiḥ sūtamāgadhāḥ</li>
    </ul>
    <ul id="R_2.023.012">
        <li id="R_2.023.012ab">na te kṣaudraṃ ca dadhi ca brāhmaṇā vedapāragāḥ</li>
        <li id="R_2.023.012cd">mūrdhni mūrdhāvasiktasya dadhati sma vidhānataḥ</li>
    </ul>
    <ul id="R_2.023.013">
        <li id="R_2.023.013ab">na tvāṃ prakṛtayaḥ sarvā śreṇīmukhyāś ca bhūṣitāḥ</li>
        <li id="R_2.023.013cd">anuvrajitum icchanti paurajāpapadās tathā</li>
    </ul>
    <ul id="R_2.023.014">
        <li id="R_2.023.014ab">caturbhir vegasaṃpannair hayaiḥ kāñcanabhūṣaṇaiḥ</li>
        <li id="R_2.023.014cd">mukhyaḥ puṣyaratho yuktaḥ kiṃ na gacchati te 'grataḥ</li>
    </ul>
    <ul id="R_2.023.015">
        <li id="R_2.023.015ab">na hastī cāgrataḥ śrīmāṃs tava lakṣaṇapūjitaḥ</li>
        <li id="R_2.023.015cd">prayāṇe lakṣyate vīra kṛṣṇameghagiri prabhaḥ</li>
    </ul>
    <ul id="R_2.023.016">
        <li id="R_2.023.016ab">na ca kāñcanacitraṃ te paśyāmi priyadarśana</li>
        <li id="R_2.023.016cd">bhadrāsanaṃ puraskṛtya yāntaṃ vīrapuraḥsaram</li>
    </ul>
    <ul id="R_2.023.017">
        <li id="R_2.023.017ab">abhiṣeko yadā sajjaḥ kim idānīm idaṃ tava</li>
        <li id="R_2.023.017cd">apūrvo mukhavarṇaś ca na praharṣaś ca lakṣyate</li>
    </ul>
    <ul id="R_2.023.018">
        <li id="R_2.023.018ab">itīva vilapantīṃ tāṃ provāca raghunandanaḥ</li>
        <li id="R_2.023.018cd">sīte tatrabhavāṃs tāta pravrājayati māṃ vanam</li>
    </ul>
    <ul id="R_2.023.019">
        <li id="R_2.023.019ab">kule mahati saṃbhūte dharmajñe dharmacāriṇi</li>
        <li id="R_2.023.019cd">śṛṇu jānaki yenedaṃ krameṇābhyāgataṃ mama</li>
    </ul>
    <ul id="R_2.023.020">
        <li id="R_2.023.020ab">rājñā satyapratijñena pitrā daśarathena me</li>
        <li id="R_2.023.020cd">kaikeyyai prītamanasā purā dattau mahāvarau</li>
    </ul>
    <ul id="R_2.023.021">
        <li id="R_2.023.021ab">tayādya mama sajje 'sminn abhiṣeke nṛpodyate</li>
        <li id="R_2.023.021cd">pracoditaḥ sa samayo dharmeṇa pratinirjitaḥ</li>
    </ul>
    <ul id="R_2.023.022">
        <li id="R_2.023.022ab">caturdaśa hi varṣāṇi vastavyaṃ daṇḍake mayā</li>
        <li id="R_2.023.022cd">pitrā me bharataś cāpi yauvarājye niyojitaḥ</li>
        <li id="R_2.023.022ef">so 'haṃ tvām āgato draṣṭuṃ prasthito vijanaṃ vanam</li>
    </ul>
    <ul id="R_2.023.023">
        <li id="R_2.023.023ab">bharatasya samīpe te nāhaṃ kathyaḥ kadā cana</li>
        <li id="R_2.023.023cd">ṛddhiyuktā hi puruṣā na sahante parastavam</li>
        <li id="R_2.023.023ef">tasmān na te guṇāḥ kathyā bharatasyāgrato mama</li>
    </ul>
    <ul id="R_2.023.024">
        <li id="R_2.023.024ab">nāpi tvaṃ tena bhartavyā viśeṣeṇa kadā cana</li>
        <li id="R_2.023.024cd">anukūlatayā śakyaṃ samīpe tasya vartitum</li>
    </ul>
    <ul id="R_2.023.025">
        <li id="R_2.023.025ab">ahaṃ cāpi pratijñāṃ tāṃ guroḥ samanupālayan</li>
        <li id="R_2.023.025cd">vanam adyaiva yāsyāmi sthirā bhava manasvini</li>
    </ul>
    <ul id="R_2.023.026">
        <li id="R_2.023.026ab">yāte ca mayi kalyāṇi vanaṃ muniniṣevitam</li>
        <li id="R_2.023.026cd">vratopavāsaratayā bhavitavyaṃ tvayānaghe</li>
    </ul>
    <ul id="R_2.023.027">
        <li id="R_2.023.027ab">kālyam utthāya devānāṃ kṛtvā pūjāṃ yathāvidhi</li>
        <li id="R_2.023.027cd">vanditavyo daśarathaḥ pitā mama nareśvaraḥ</li>
    </ul>
    <ul id="R_2.023.028">
        <li id="R_2.023.028ab">mātā ca mama kausalyā vṛddhā saṃtāpakarśitā</li>
        <li id="R_2.023.028cd">dharmam evāgrataḥ kṛtvā tvattaḥ saṃmānam arhati</li>
    </ul>
    <ul id="R_2.023.029">
        <li id="R_2.023.029ab">vanditavyāś ca te nityaṃ yāḥ śeṣā mama mātaraḥ</li>
        <li id="R_2.023.029cd">snehapraṇayasaṃbhogaiḥ samā hi mama mātaraḥ</li>
    </ul>
    <ul id="R_2.023.030">
        <li id="R_2.023.030ab">bhrātṛputrasamau cāpi draṣṭavyau ca viśeṣataḥ</li>
        <li id="R_2.023.030cd">tvayā lakṣmaṇaśatrughnau prāṇaiḥ priyatarau mama</li>
    </ul>
    <ul id="R_2.023.031">
        <li id="R_2.023.031ab">vipriyaṃ na ca kartavyaṃ bharatasya kadā cana</li>
        <li id="R_2.023.031cd">sa hi rājā prabhuś caiva deśasya ca kulasya ca</li>
    </ul>
    <ul id="R_2.023.032">
        <li id="R_2.023.032ab">ārādhitā hi śīlena prayatnaiś copasevitāḥ</li>
        <li id="R_2.023.032cd">rājānaḥ saṃprasīdanti prakupyanti viparyaye</li>
    </ul>
    <ul id="R_2.023.033">
        <li id="R_2.023.033ab">aurasān api putrān hi tyajanty ahitakāriṇaḥ</li>
        <li id="R_2.023.033cd">samarthān saṃpragṛhṇanti janān api narādhipāḥ</li>
    </ul>
    <ul id="R_2.023.034">
        <li id="R_2.023.034ab">
            <seg type="pāda" n="a">ahaṃ gamiṣyāmi mahāvanaṃ priye</seg>
            <seg type="pāda" n="b">tvayā hi vastavyam ihaiva bhāmini</seg>
        </li>
        <li id="R_2.023.034cd">
            <seg type="pāda" n="c">yathā vyalīkaṃ kuruṣe na kasya cit</seg>
            <seg type="pāda" n="d">tathā tvayā kāryam idaṃ vaco mama</seg>
        </li>
    </ul>
    <ul id="R_2.024.001">
        <li id="R_2.024.001ab">evam uktā tu vaidehī priyārhā priyavādinī</li>
        <li id="R_2.024.001cd">praṇayād eva saṃkruddhā bhartāram idam abravīt</li>
    </ul>
    <ul id="R_2.024.002">
        <li id="R_2.024.002ab">āryaputra pitā mātā bhrātā putras tathā snuṣā</li>
        <li id="R_2.024.002cd">svāni puṇyāni bhuñjānāḥ svaṃ svaṃ bhāgyam upāsate</li>
    </ul>
    <ul id="R_2.024.003">
        <li id="R_2.024.003ab">bhartur bhāgyaṃ tu bhāryaikā prāpnoti puruṣarṣabha</li>
        <li id="R_2.024.003cd">ataś caivāham ādiṣṭā vane vastavyam ity api</li>
    </ul>
    <ul id="R_2.024.004">
        <li id="R_2.024.004ab">na pitā nātmajo nātmā na mātā na sakhījanaḥ</li>
        <li id="R_2.024.004cd">iha pretya ca nārīṇāṃ patir eko gatiḥ sadā</li>
    </ul>
    <ul id="R_2.024.005">
        <li id="R_2.024.005ab">yadi tvaṃ prasthito durgaṃ vanam adyaiva rāghava</li>
        <li id="R_2.024.005cd">agratas te gamiṣyāmi mṛdnantī kuśakaṇṭakān</li>
    </ul>
    <ul id="R_2.024.006">
        <li id="R_2.024.006ab">īrṣyā roṣau bahiṣkṛtya bhuktaśeṣam ivodakam</li>
        <li id="R_2.024.006cd">naya māṃ vīra viśrabdhaḥ pāpaṃ mayi na vidyate</li>
    </ul>
    <ul id="R_2.024.007">
        <li id="R_2.024.007ab">prāsādāgrair vimānair vā vaihāyasagatena vā</li>
        <li id="R_2.024.007cd">sarvāvasthāgatā bhartuḥ pādacchāyā viśiṣyate</li>
    </ul>
    <ul id="R_2.024.008">
        <li id="R_2.024.008ab">anuśiṣṭāsmi mātrā ca pitrā ca vividhāśrayam</li>
        <li id="R_2.024.008cd">nāsmi saṃprati vaktavyā vartitavyaṃ yathā mayā</li>
    </ul>
    <ul id="R_2.024.009">
        <li id="R_2.024.009ab">sukhaṃ vane nivatsyāmi yathaiva bhavane pituḥ</li>
        <li id="R_2.024.009cd">acintayantī trīṃl lokāṃś cintayantī pativratam</li>
    </ul>
    <ul id="R_2.024.010">
        <li id="R_2.024.010ab">śuśrūṣamāṇā te nityaṃ niyatā brahmacāriṇī</li>
        <li id="R_2.024.010cd">saha raṃsye tvayā vīra vaneṣu madhugandhiṣu</li>
    </ul>
    <ul id="R_2.024.011">
        <li id="R_2.024.011ab">tvaṃ hi kartuṃ vane śakto rāma saṃparipālanam</li>
        <li id="R_2.024.011cd">anyasya pai janasyeha kiṃ punar mama mānada</li>
    </ul>
    <ul id="R_2.024.012">
        <li id="R_2.024.012ab">phalamūlāśanā nityaṃ bhaviṣyāmi na saṃśayaḥ</li>
        <li id="R_2.024.012cd">na te duḥkhaṃ kariṣyāmi nivasantī saha tvayā</li>
    </ul>
    <ul id="R_2.024.013">
        <li id="R_2.024.013ab">icchāmi saritaḥ śailān palvalāni vanāni ca</li>
        <li id="R_2.024.013cd">draṣṭuṃ sarvatra nirbhītā tvayā nāthena dhīmatā</li>
    </ul>
    <ul id="R_2.024.014">
        <li id="R_2.024.014ab">haṃsakāraṇḍavākīrṇāḥ padminīḥ sādhupuṣpitāḥ</li>
        <li id="R_2.024.014cd">iccheyaṃ sukhinī draṣṭuṃ tvayā vīreṇa saṃgatā</li>
    </ul>
    <ul id="R_2.024.015">
        <li id="R_2.024.015ab">saha tvayā viśālākṣa raṃsye paramanandinī</li>
        <li id="R_2.024.015cd">evaṃ varṣasahasrāṇāṃ śataṃ vāhaṃ tvayā saha</li>
    </ul>
    <ul id="R_2.024.016">
        <li id="R_2.024.016ab">svarge 'pi ca vinā vāso bhavitā yadi rāghava</li>
        <li id="R_2.024.016cd">tvayā mama naravyāghra nāhaṃ tam api rocaye</li>
    </ul>
    <ul id="R_2.024.017">
        <li id="R_2.024.017ab">
            <seg type="pāda" n="a">ahaṃ gamiṣyāmi vanaṃ sudurgamaṃ</seg>
            <seg type="pāda" n="b">mṛgāyutaṃ vānaravāraṇair yutam</seg>
        </li>
        <li id="R_2.024.017cd">
            <seg type="pāda" n="c">vane nivatsyāmi yathā pitur gṛhe</seg>
            <seg type="pāda" n="d">tavaiva pādāv upagṛhya saṃmatā</seg>
        </li>
    </ul>
    <ul id="R_2.024.018">
        <li id="R_2.024.018ab">
            <seg type="pāda" n="a">ananyabhāvām anuraktacetasaṃ</seg>
            <seg type="pāda" n="b">tvayā viyuktāṃ maraṇāya niścitām</seg>
        </li>
        <li id="R_2.024.018cd">
            <seg type="pāda" n="c">nayasva māṃ sādhu kuruṣva yācanāṃ</seg>
            <seg type="pāda" n="d">na te mayāto gurutā bhaviṣyati</seg>
        </li>
    </ul>
    <ul id="R_2.024.019">
        <li id="R_2.024.019ab">
            <seg type="pāda" n="a">tathā bruvāṇām api dharmavatsalo</seg>
            <seg type="pāda" n="b">na ca sma sītāṃ nṛvaro ninīṣati</seg>
        </li>
        <li id="R_2.024.019cd">
            <seg type="pāda" n="c">uvāca caināṃ bahu saṃnivartane</seg>
            <seg type="pāda" n="d">vane nivāsasya ca duḥkhitāṃ prati</seg>
        </li>
    </ul>
    <ul id="R_2.025.001">
        <li id="R_2.025.001ab">sa evaṃ bruvatīṃ sītāṃ dharmajño dharmavatsalaḥ</li>
        <li id="R_2.025.001cd">nivartanārthe dharmātmā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_2.025.002">
        <li id="R_2.025.002ab">sīte mahākulīnāsi dharme ca niratā sadā</li>
        <li id="R_2.025.002cd">ihācara svadharmaṃ tvaṃ mā yathā manasaḥ sukham</li>
    </ul>
    <ul id="R_2.025.003">
        <li id="R_2.025.003ab">sīte yathā tvāṃ vakṣyāmi tathā kāryaṃ tvayābale</li>
        <li id="R_2.025.003cd">vane doṣā hi bahavo vadatas tān nibodha me</li>
    </ul>
    <ul id="R_2.025.004">
        <li id="R_2.025.004ab">sīte vimucyatām eṣā vanavāsakṛtā matiḥ</li>
        <li id="R_2.025.004cd">bahudoṣaṃ hi kāntāraṃ vanam ity abhidhīyate</li>
    </ul>
    <ul id="R_2.025.005">
        <li id="R_2.025.005ab">hitabuddhyā khalu vaco mayaitad abhidhīyate</li>
        <li id="R_2.025.005cd">sadā sukhaṃ na jānāmi duḥkham eva sadā vanam</li>
    </ul>
    <ul id="R_2.025.006">
        <li id="R_2.025.006ab">girinirjharasaṃbhūtā girikandaravāsinām</li>
        <li id="R_2.025.006cd">siṃhānāṃ ninadā duḥkhāḥ śrotuṃ duḥkham ato vanam</li>
    </ul>
    <ul id="R_2.025.007">
        <li id="R_2.025.007ab">supyate parṇaśayyāsu svayaṃ bhagnāsu bhūtale</li>
        <li id="R_2.025.007cd">rātriṣu śramakhinnena tasmād duḥkhataraṃ vanam</li>
    </ul>
    <ul id="R_2.025.008">
        <li id="R_2.025.008ab">upavāsaś ca kartavyā yathāprāṇena maithili</li>
        <li id="R_2.025.008cd">jaṭābhāraś ca kartavyo valkalāmbaradhāriṇā</li>
    </ul>
    <ul id="R_2.025.009">
        <li id="R_2.025.009ab">atīva vātas timiraṃ bubhukṣā cātra nityaśaḥ</li>
        <li id="R_2.025.009cd">bhayāni ca mahānty atra tato duḥkhataraṃ vanam</li>
    </ul>
    <ul id="R_2.025.010">
        <li id="R_2.025.010ab">sarīsṛpāś ca bahavo bahurūpāś ca bhāmini</li>
        <li id="R_2.025.010cd">caranti pṛthivīṃ darpād ato dukhataraṃ vanam</li>
    </ul>
    <ul id="R_2.025.011">
        <li id="R_2.025.011ab">nadīnilayanāḥ sarpā nadīkuṭilagāminaḥ</li>
        <li id="R_2.025.011cd">tiṣṭhanty āvṛtya panthānam ato duḥkhataraṃ vanam</li>
    </ul>
    <ul id="R_2.025.012">
        <li id="R_2.025.012ab">pataṃgā vṛścikāḥ kīṭā daṃśāś ca maśakaiḥ saha</li>
        <li id="R_2.025.012cd">bādhante nityam abale sarvaṃ duḥkham ato vanam</li>
    </ul>
    <ul id="R_2.025.013">
        <li id="R_2.025.013ab">drumāḥ kaṇṭakinaś caiva kuśakāśāś ca bhāmini</li>
        <li id="R_2.025.013cd">vane vyākulaśākhāgrās tena duḥkhataraṃ vanam</li>
    </ul>
    <ul id="R_2.025.014">
        <li id="R_2.025.014ab">tad alaṃ te vanaṃ gatvā kṣamaṃ na hi vanaṃ tava</li>
        <li id="R_2.025.014cd">vimṛśann iha paśyāmi bahudoṣataraṃ vanam</li>
    </ul>
    <ul id="R_2.025.015">
        <li id="R_2.025.015ab">
            <seg type="pāda" n="a">vanaṃ tu netuṃ na kṛtā matis tadā</seg>
            <seg type="pāda" n="b">babhūva rāmeṇa yadā mahātmanā</seg>
        </li>
        <li id="R_2.025.015cd">
            <seg type="pāda" n="c">na tasya sītā vacanaṃ cakāra tat</seg>
            <seg type="pāda" n="d">tato 'bravīd rāmam idaṃ suduḥkhitā</seg>
        </li>
    </ul>
    <ul id="R_2.026.001">
        <li id="R_2.026.001ab">etat tu vacanaṃ śrutvā sītā rāmasya duḥkhitā</li>
        <li id="R_2.026.001cd">prasaktāśrumukhī mandam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.026.002">
        <li id="R_2.026.002ab">ye tvayā kīrtitā doṣā vane vastavyatāṃ prati</li>
        <li id="R_2.026.002cd">guṇān ity eva tān viddhi tava snehapuraskṛtān</li>
    </ul>
    <ul id="R_2.026.003">
        <li id="R_2.026.003ab">tvayā ca saha gantavyaṃ mayā gurujanājñayā</li>
        <li id="R_2.026.003cd">tvadviyogena me rāma tyaktavyam iha jīvitam</li>
    </ul>
    <ul id="R_2.026.004">
        <li id="R_2.026.004ab">na ca māṃ tvatsamīpastham api śaknoti rāghava</li>
        <li id="R_2.026.004cd">surāṇām īśvaraḥ śakraḥ pradharṣayitum ojasā</li>
    </ul>
    <ul id="R_2.026.005">
        <li id="R_2.026.005ab">patihīnā tu yā nārī na sā śakṣyati jīvitum</li>
        <li id="R_2.026.005cd">kāmam evaṃvidhaṃ rāma tvayā mama vidarśitam</li>
    </ul>
    <ul id="R_2.026.006">
        <li id="R_2.026.006ab">atha cāpi mahāprājña brāhmaṇānāṃ mayā śrutam</li>
        <li id="R_2.026.006cd">purā pitṛgṛhe satyaṃ vastavyaṃ kila me vane</li>
    </ul>
    <ul id="R_2.026.007">
        <li id="R_2.026.007ab">lakṣaṇibhyo dvijātibhyaḥ śrutvāhaṃ vacanaṃ gṛhe</li>
        <li id="R_2.026.007cd">vanavāsakṛtotsāhā nityam eva mahābala</li>
    </ul>
    <ul id="R_2.026.008">
        <li id="R_2.026.008ab">ādeśo vanavāsasya prāptavyaḥ sa mayā kila</li>
        <li id="R_2.026.008cd">sā tvayā saha tatrāhaṃ yāsyāmi priya nānyathā</li>
    </ul>
    <ul id="R_2.026.009">
        <li id="R_2.026.009ab">kṛtādeśā bhaviṣyāmi gamiṣyāmi saha tvayā</li>
        <li id="R_2.026.009cd">kālaś cāyaṃ samutpannaḥ satyavāg bhavatu dvijaḥ</li>
    </ul>
    <ul id="R_2.026.010">
        <li id="R_2.026.010ab">vanavāse hi jānāmi duḥkhāni bahudhā kila</li>
        <li id="R_2.026.010cd">prāpyante niyataṃ vīra puruṣair akṛtātmabhiḥ</li>
    </ul>
    <ul id="R_2.026.011">
        <li id="R_2.026.011ab">kanyayā ca pitur gehe vanavāsaḥ śruto mayā</li>
        <li id="R_2.026.011cd">bhikṣiṇyāḥ sādhuvṛttāyā mama mātur ihāgrataḥ</li>
    </ul>
    <ul id="R_2.026.012">
        <li id="R_2.026.012ab">prasāditaś ca vai pūrvaṃ tvaṃ vai bahuvidhaṃ prabho</li>
        <li id="R_2.026.012cd">gamanaṃ vanavāsasya kāṅkṣitaṃ hi saha tvayā</li>
    </ul>
    <ul id="R_2.026.013">
        <li id="R_2.026.013ab">kṛtakṣaṇāhaṃ bhadraṃ te gamanaṃ prati rāghava</li>
        <li id="R_2.026.013cd">vanavāsasya śūrasya caryā hi mama rocate</li>
    </ul>
    <ul id="R_2.026.014">
        <li id="R_2.026.014ab">śuddhātman premabhāvād dhi bhaviṣyāmi vikalmaṣā</li>
        <li id="R_2.026.014cd">bhartāram anugacchantī bhartā hi mama daivatam</li>
    </ul>
    <ul id="R_2.026.015">
        <li id="R_2.026.015ab">pretyabhāve 'pi kalyāṇaḥ saṃgamo me saha tvayā</li>
        <li id="R_2.026.015cd">śrutir hi śrūyate puṇyā brāhmaṇānāṃ yaśasvinām</li>
    </ul>
    <ul id="R_2.026.016">
        <li id="R_2.026.016ab">iha loke ca pitṛbhir yā strī yasya mahāmate</li>
        <li id="R_2.026.016cd">adbhir dattā svadharmeṇa pretyabhāve 'pi tasya sā</li>
    </ul>
    <ul id="R_2.026.017">
        <li id="R_2.026.017ab">evam asmāt svakāṃ nārīṃ suvṛttāṃ hi pativratām</li>
        <li id="R_2.026.017cd">nābhirocayase netuṃ tvaṃ māṃ keneha hetunā</li>
    </ul>
    <ul id="R_2.026.018">
        <li id="R_2.026.018ab">bhaktāṃ pativratāṃ dīnāṃ māṃ samāṃ sukhaduḥkhayoḥ</li>
        <li id="R_2.026.018cd">netum arhasi kākutstha samānasukhaduḥkhinīm</li>
    </ul>
    <ul id="R_2.026.019">
        <li id="R_2.026.019ab">yadi māṃ duḥkhitām evaṃ vanaṃ netuṃ na cecchasi</li>
        <li id="R_2.026.019cd">viṣam agniṃ jalaṃ vāham āsthāsye mṛtyukāraṇāt</li>
    </ul>
    <ul id="R_2.026.020">
        <li id="R_2.026.020ab">evaṃ bahuvidhaṃ taṃ sā yācate gamanaṃ prati</li>
        <li id="R_2.026.020cd">nānumene mahābāhus tāṃ netuṃ vijanaṃ vanam</li>
    </ul>
    <ul id="R_2.026.021">
        <li id="R_2.026.021ab">evam uktā tu sā cintāṃ maithilī samupāgatā</li>
        <li id="R_2.026.021cd">snāpayantīva gām uṣṇair aśrubhir nayanacyutaiḥ</li>
    </ul>
    <ul id="R_2.026.022">
        <li id="R_2.026.022ab">cintayantīṃ tathā tāṃ tu nivartayitum ātmavān</li>
        <li id="R_2.026.022cd">krodhāviṣṭāṃ tu vaidehīṃ kākutstho bahv asāntvayat</li>
    </ul>
    <ul id="R_2.027.001">
        <li id="R_2.027.001ab">sāntvyamānā tu rāmeṇa maithilī janakātmajā</li>
        <li id="R_2.027.001cd">vanavāsanimittāya bhartāram idam abravīt</li>
    </ul>
    <ul id="R_2.027.002">
        <li id="R_2.027.002ab">sā tam uttamasaṃvignā sītā vipulavakṣasaṃ</li>
        <li id="R_2.027.002cd">praṇayāc cābhimānāc ca paricikṣepa rāghavam</li>
    </ul>
    <ul id="R_2.027.003">
        <li id="R_2.027.003ab">kiṃ tvāmanyata vaidehaḥ pitā me mithilādhipaḥ</li>
        <li id="R_2.027.003cd">rāma jāmātaraṃ prāpya striyaṃ puruṣavigraham</li>
    </ul>
    <ul id="R_2.027.004">
        <li id="R_2.027.004ab">anṛtaṃ balaloko 'yam ajñānād yad dhi vakṣyati</li>
        <li id="R_2.027.004cd">tejo nāsti paraṃ rāme tapatīva divākare</li>
    </ul>
    <ul id="R_2.027.005">
        <li id="R_2.027.005ab">kiṃ hi kṛtvā viṣaṇṇas tvaṃ kuto vā bhayam asti te</li>
        <li id="R_2.027.005cd">yat parityaktukāmas tvaṃ mām ananyaparāyaṇām</li>
    </ul>
    <ul id="R_2.027.006">
        <li id="R_2.027.006ab">dyumatsenasutaṃ vīra satyavantam anuvratām</li>
        <li id="R_2.027.006cd">sāvitrīm iva māṃ viddhi tvam ātmavaśavartinīm</li>
    </ul>
    <ul id="R_2.027.007">
        <li id="R_2.027.007ab">na tv ahaṃ manasāpy anyaṃ draṣṭāsmi tvadṛte 'nagha</li>
        <li id="R_2.027.007cd">tvayā rāghava gaccheyaṃ yathānyā kulapāṃsanī</li>
    </ul>
    <ul id="R_2.027.008">
        <li id="R_2.027.008ab">svayaṃ tu bhāryāṃ kaumārīṃ ciram adhyuṣitāṃ satīm</li>
        <li id="R_2.027.008cd">śailūṣa iva māṃ rāma parebhyo dātum icchasi</li>
    </ul>
    <ul id="R_2.027.009">
        <li id="R_2.027.009ab">sa mām anādāya vanaṃ na tvaṃ prasthātum arhasi</li>
        <li id="R_2.027.009cd">tapo vā yadi vāraṇyaṃ svargo vā syāt saha tvayā</li>
    </ul>
    <ul id="R_2.027.010">
        <li id="R_2.027.010ab">na ca me bhavitā tatra kaś cit pathi pariśramaḥ</li>
        <li id="R_2.027.010cd">pṛṣṭhatas tava gacchantyā vihāraśayaneṣv api</li>
    </ul>
    <ul id="R_2.027.011">
        <li id="R_2.027.011ab">kuśakāśaśareṣīkā ye ca kaṇṭakino drumāḥ</li>
        <li id="R_2.027.011cd">tūlājinasamasparśā mārge mama saha tvayā</li>
    </ul>
    <ul id="R_2.027.012">
        <li id="R_2.027.012ab">mahāvāta samuddhūtaṃ yan mām avakariṣyati</li>
        <li id="R_2.027.012cd">rajo ramaṇa tan manye parārdhyam iva candanam</li>
    </ul>
    <ul id="R_2.027.013">
        <li id="R_2.027.013ab">śādvaleṣu yad āsiṣye vanānte vanagoracā</li>
        <li id="R_2.027.013cd">kuthāstaraṇatalpeṣu kiṃ syāt sukhataraṃ tataḥ</li>
    </ul>
    <ul id="R_2.027.014">
        <li id="R_2.027.014ab">patraṃ mūlaṃ phalaṃ yat tvam alpaṃ vā yadi vā bahu</li>
        <li id="R_2.027.014cd">dāsyasi svayam āhṛtya tan me 'mṛtarasopamam</li>
    </ul>
    <ul id="R_2.027.015">
        <li id="R_2.027.015ab">na mātur na pitus tatra smariṣyāmi na veśmanaḥ</li>
        <li id="R_2.027.015cd">ārtavāny upabhuñjānā puṣpāṇi ca phalāni ca</li>
    </ul>
    <ul id="R_2.027.016">
        <li id="R_2.027.016ab">na ca tatra gataḥ kiṃ cid draṣṭum arhasi vipriyam</li>
        <li id="R_2.027.016cd">matkṛte na ca te śoko na bhaviṣyāmi durbharā</li>
    </ul>
    <ul id="R_2.027.017">
        <li id="R_2.027.017ab">yas tvayā saha sa svargo nirayo yas tvayā vinā</li>
        <li id="R_2.027.017cd">iti jānan parāṃ prītiṃ gaccha rāma mayā saha</li>
    </ul>
    <ul id="R_2.027.018">
        <li id="R_2.027.018ab">atha mām evam avyagrāṃ vanaṃ naiva nayiṣyasi</li>
        <li id="R_2.027.018cd">viṣam adyaiva pāsyāmi mā viśaṃ dviṣatāṃ vaśam</li>
    </ul>
    <ul id="R_2.027.019">
        <li id="R_2.027.019ab">paścād api hi duḥkhena mama naivāsti jīvitam</li>
        <li id="R_2.027.019cd">ujjhitāyās tvayā nātha tadaiva maraṇaṃ varam</li>
    </ul>
    <ul id="R_2.027.020">
        <li id="R_2.027.020ab">idaṃ hi sahituṃ śokaṃ muhūrtam api notsahe</li>
        <li id="R_2.027.020cd">kiṃ punar daśavarṣāṇi trīṇi caikaṃ ca duḥkhitā</li>
    </ul>
    <ul id="R_2.027.021">
        <li id="R_2.027.021ab">iti sā śokasaṃtaptā vilapya karuṇaṃ bahu</li>
        <li id="R_2.027.021cd">cukrośa patim āyastā bhṛśam āliṅgya sasvaram</li>
    </ul>
    <ul id="R_2.027.022">
        <li id="R_2.027.022ab">sā viddhā bahubhir vākyair digdhair iva gajāṅganā</li>
        <li id="R_2.027.022cd">cira saṃniyataṃ bāṣpaṃ mumocāgnim ivāraṇiḥ</li>
    </ul>
    <ul id="R_2.027.023">
        <li id="R_2.027.023ab">tasyāḥ sphaṭikasaṃkāśaṃ vāri saṃtāpasaṃbhavam</li>
        <li id="R_2.027.023cd">netrābhyāṃ parisusrāva paṅkajābhyām ivodakam</li>
    </ul>
    <ul id="R_2.027.024">
        <li id="R_2.027.024ab">tāṃ pariṣvajya bāhubhyāṃ visaṃjñām iva duḥkhitām</li>
        <li id="R_2.027.024cd">uvāca vacanaṃ rāmaḥ pariviśvāsayaṃs tadā</li>
    </ul>
    <ul id="R_2.027.025">
        <li id="R_2.027.025ab">na devi tava duḥkhena svargam apy abhirocaye</li>
        <li id="R_2.027.025cd">na hi me 'sti bhayaṃ kiṃ cit svayambhor iva sarvataḥ</li>
    </ul>
    <ul id="R_2.027.026">
        <li id="R_2.027.026ab">tava sarvam abhiprāyam avijñāya śubhānane</li>
        <li id="R_2.027.026cd">vāsaṃ na rocaye 'raṇye śaktimān api rakṣaṇe</li>
    </ul>
    <ul id="R_2.027.027">
        <li id="R_2.027.027ab">yat sṛṣṭāsi mayā sārdhaṃ vanavāsāya maithili</li>
        <li id="R_2.027.027cd">na vihātuṃ mayā śakyā kīrtir ātmavatā yathā</li>
    </ul>
    <ul id="R_2.027.028">
        <li id="R_2.027.028ab">dharmas tu gajanāsoru sadbhir ācaritaḥ purā</li>
        <li id="R_2.027.028cd">taṃ cāham anuvarte 'dya yathā sūryaṃ suvarcalā</li>
    </ul>
    <ul id="R_2.027.029">
        <li id="R_2.027.029ab">eṣa dharmas tu suśroṇi pitur mātuś ca vaśyatā</li>
        <li id="R_2.027.029cd">ataś cājñāṃ vyatikramya nāhaṃ jīvitum utsahe</li>
    </ul>
    <ul id="R_2.027.030">
        <li id="R_2.027.030ab">sa māṃ pitā yathā śāsti satyadharmapathe sthitaḥ</li>
        <li id="R_2.027.030cd">tathā vartitum icchāmi sa hi dharmaḥ sanātanaḥ</li>
        <li id="R_2.027.030ef">anugacchasva māṃ bhīru sahadharmacarī bhava</li>
    </ul>
    <ul id="R_2.027.031">
        <li id="R_2.027.031ab">brāhmaṇebhyaś ca ratnāni bhikṣukebhyaś ca bhojanam</li>
        <li id="R_2.027.031cd">dehi cāśaṃsamānebhyaḥ saṃtvarasva ca māciram</li>
    </ul>
    <ul id="R_2.027.032">
        <li id="R_2.027.032ab">anukūlaṃ tu sā bhartur jñātvā gamanam ātmanaḥ</li>
        <li id="R_2.027.032cd">kṣipraṃ pramuditā devī dātum evopacakrame</li>
    </ul>
    <ul id="R_2.027.033">
        <li id="R_2.027.033ab">
            <seg type="pāda" n="a">tataḥ prahṛṣṭā paripūrṇamānasā</seg>
            <seg type="pāda" n="b">yaśasvinī bhartur avekṣya bhāṣitam</seg>
        </li>
        <li id="R_2.027.033cd">
            <seg type="pāda" n="c">dhanāni ratnāni ca dātum aṅganā</seg>
            <seg type="pāda" n="d">pracakrame dharmabhṛtāṃ manasvinī</seg>
        </li>
    </ul>
    <ul id="R_2.028.001">
        <li id="R_2.028.001ab">tato 'bravīn mahātejā rāmo lakṣmaṇam agrataḥ</li>
        <li id="R_2.028.001cd">sthitaṃ prāggāminaṃ vīraṃ yācamānaṃ kṛtāñjalim</li>
    </ul>
    <ul id="R_2.028.002">
        <li id="R_2.028.002ab">mayādya saha saumitre tvayi gacchati tad vanam</li>
        <li id="R_2.028.002cd">ko bhariṣyati kausalyāṃ sumitrāṃ vā yaśasvinīm</li>
    </ul>
    <ul id="R_2.028.003">
        <li id="R_2.028.003ab">abhivarṣati kāmair yaḥ parjanyaḥ pṛthivīm iva</li>
        <li id="R_2.028.003cd">sa kāmapāśaparyasto mahātejā mahīpatiḥ</li>
    </ul>
    <ul id="R_2.028.004">
        <li id="R_2.028.004ab">sā hi rājyam idaṃ prāpya nṛpasyāśvapateḥ sutā</li>
        <li id="R_2.028.004cd">duḥkhitānāṃ sapatnīnāṃ na kariṣyati śobhanam</li>
    </ul>
    <ul id="R_2.028.005">
        <li id="R_2.028.005ab">evam uktas tu rāmeṇa lakṣmaṇaḥ ślakṣṇayā girā</li>
        <li id="R_2.028.005cd">pratyuvāca tadā rāmaṃ vākyajño vākyakovidam</li>
    </ul>
    <ul id="R_2.028.006">
        <li id="R_2.028.006ab">tavaiva tejasā vīra bharataḥ pūjayiṣyati</li>
        <li id="R_2.028.006cd">kausalyāṃ ca sumitrāṃ ca prayato nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_2.028.007">
        <li id="R_2.028.007ab">kausalyā bibhṛyād āryā sahasram api madvidhān</li>
        <li id="R_2.028.007cd">yasyāḥ sahasraṃ grāmāṇāṃ saṃprāptam upajīvanam</li>
    </ul>
    <ul id="R_2.028.008">
        <li id="R_2.028.008ab">dhanur ādāya saśaraṃ khanitrapiṭakādharaḥ</li>
        <li id="R_2.028.008cd">agratas te gamiṣyāmi panthānam anudarśayan</li>
    </ul>
    <ul id="R_2.028.009">
        <li id="R_2.028.009ab">āhariṣyāmi te nityaṃ mūlāni ca phalāni ca</li>
        <li id="R_2.028.009cd">vanyāni yāni cānyāni svāhārāṇi tapasvinām</li>
    </ul>
    <ul id="R_2.028.010">
        <li id="R_2.028.010ab">bhavāṃs tu saha vaidehyā girisānuṣu raṃsyate</li>
        <li id="R_2.028.010cd">ahaṃ sarvaṃ kariṣyāmi jāgrataḥ svapataś ca te</li>
    </ul>
    <ul id="R_2.028.011">
        <li id="R_2.028.011ab">rāmas tv anena vākyena suprītaḥ pratyuvāca tam</li>
        <li id="R_2.028.011cd">vrajāpṛcchasva saumitre sarvam eva suhṛjjanam</li>
    </ul>
    <ul id="R_2.028.012">
        <li id="R_2.028.012ab">ye ca rājño dadau divye mahātmā varuṇaḥ svayam</li>
        <li id="R_2.028.012cd">janakasya mahāyajñe dhanuṣī raudradarśane</li>
    </ul>
    <ul id="R_2.028.013">
        <li id="R_2.028.013ab">abhedyakavace divye tūṇī cākṣayasāyakau</li>
        <li id="R_2.028.013cd">ādityavimalau cobhau khaḍgau hemapariṣkṛtau</li>
    </ul>
    <ul id="R_2.028.014">
        <li id="R_2.028.014ab">satkṛtya nihitaṃ sarvam etad ācāryasadmani</li>
        <li id="R_2.028.014cd">sa tvam āyudham ādāya kṣipram āvraja lakṣmaṇa</li>
    </ul>
    <ul id="R_2.028.015">
        <li id="R_2.028.015ab">sa suhṛjjanam āmantrya vanavāsāya niścitaḥ</li>
        <li id="R_2.028.015cd">ikṣvākugurum āmantrya jagrāhāyudham uttamam</li>
    </ul>
    <ul id="R_2.028.016">
        <li id="R_2.028.016ab">tad divyaṃ rājaśārdūlaḥ satkṛtaṃ mālyabhūṣitam</li>
        <li id="R_2.028.016cd">rāmāya darśayām āsa saumitriḥ sarvam āyudham</li>
    </ul>
    <ul id="R_2.028.017">
        <li id="R_2.028.017ab">tam uvācātmavān rāmaḥ prītyā lakṣmaṇam āgatam</li>
        <li id="R_2.028.017cd">kāle tvam āgataḥ saumya kāṅkṣite mama lakṣmaṇa</li>
    </ul>
    <ul id="R_2.028.018">
        <li id="R_2.028.018ab">ahaṃ pradātum icchāmi yad idaṃ māmakaṃ dhanam</li>
        <li id="R_2.028.018cd">brāhmaṇebhyas tapasvibhyas tvayā saha paraṃtapa</li>
    </ul>
    <ul id="R_2.028.019">
        <li id="R_2.028.019ab">vasantīha dṛḍhaṃ bhaktyā guruṣu dvijasattamāḥ</li>
        <li id="R_2.028.019cd">teṣām api ca me bhūyaḥ sarveṣāṃ copajīvinām</li>
    </ul>
    <ul id="R_2.028.020">
        <li id="R_2.028.020ab">
            <seg type="pāda" n="a">vasiṣṭhaputraṃ tu suyajñam āryaṃ</seg>
            <seg type="pāda" n="b">tvam ānayāśu pravaraṃ dvijānām</seg>
        </li>
        <li id="R_2.028.020cd">
            <seg type="pāda" n="c">abhiprayāsyāmi vanaṃ samastān</seg>
            <seg type="pāda" n="d">abhyarcya śiṣṭān aparān dvijātīn</seg>
        </li>
    </ul>
    <ul id="R_2.029.001">
        <li id="R_2.029.001ab">tataḥ śāsanam ājñāya bhrātuḥ śubhataraṃ priyam</li>
        <li id="R_2.029.001cd">gatvā sa praviveśāśu suyajñasya niveśanam</li>
    </ul>
    <ul id="R_2.029.002">
        <li id="R_2.029.002ab">taṃ vipram agnyagārasthaṃ vanditvā lakṣmaṇo 'bravīt</li>
        <li id="R_2.029.002cd">sakhe 'bhyāgaccha paśya tvaṃ veśma duṣkarakāriṇaḥ</li>
    </ul>
    <ul id="R_2.029.003">
        <li id="R_2.029.003ab">tataḥ saṃdhyām upāsyāśu gatvā saumitriṇā saha</li>
        <li id="R_2.029.003cd">juṣṭaṃ tat prāviśal lakṣmyā ramyaṃ rāmaniveśanam</li>
    </ul>
    <ul id="R_2.029.004">
        <li id="R_2.029.004ab">tam āgataṃ vedavidaṃ prāñjaliḥ sītayā saha</li>
        <li id="R_2.029.004cd">suyajñam abhicakrāma rāghavo 'gnim ivārcitam</li>
    </ul>
    <ul id="R_2.029.005">
        <li id="R_2.029.005ab">jātarūpamayair mukhyair aṅgadaiḥ kuṇḍalaiḥ śubhaiḥ</li>
        <li id="R_2.029.005cd">sahema sūtrair maṇibhiḥ keyūrair valayair api</li>
    </ul>
    <ul id="R_2.029.006">
        <li id="R_2.029.006ab">anyaiś ca ratnair bahubhiḥ kākutsthaḥ pratyapūjayat</li>
        <li id="R_2.029.006cd">suyajñaṃ sa tadovāca rāmaḥ sītāpracoditaḥ</li>
    </ul>
    <ul id="R_2.029.007">
        <li id="R_2.029.007ab">hāraṃ ca hemasūtraṃ ca bhāryāyai saumya hāraya</li>
        <li id="R_2.029.007cd">raśanāṃ cādhunā sītā dātum icchati te sakhe</li>
    </ul>
    <ul id="R_2.029.008">
        <li id="R_2.029.008ab">paryaṅkam agryāstaraṇaṃ nānāratnavibhūṣitam</li>
        <li id="R_2.029.008cd">tam apīcchati vaidehī pratiṣṭhāpayituṃ tvayi</li>
    </ul>
    <ul id="R_2.029.009">
        <li id="R_2.029.009ab">nāgaḥ śatruṃ jayo nāma mātulo yaṃ dadau mama</li>
        <li id="R_2.029.009cd">taṃ te gajasahasreṇa dadāmi dvijapuṃgava</li>
    </ul>
    <ul id="R_2.029.010">
        <li id="R_2.029.010ab">ity uktaḥ sa hi rāmeṇa suyajñaḥ pratigṛhya tat</li>
        <li id="R_2.029.010cd">rāmalakṣmaṇasītānāṃ prayuyojāśiṣaḥ śivāḥ</li>
    </ul>
    <ul id="R_2.029.011">
        <li id="R_2.029.011ab">atha bhrātaram avyagraṃ priyaṃ rāmaḥ priyaṃvadaḥ</li>
        <li id="R_2.029.011cd">saumitriṃ tam uvācedaṃ brahmeva tridaśeśvaram</li>
    </ul>
    <ul id="R_2.029.012">
        <li id="R_2.029.012ab">agastyaṃ kauśikaṃ caiva tāv ubhau brāhmaṇottamau</li>
        <li id="R_2.029.012cd">arcayāhūya saumitre ratnaiḥ sasyam ivāmbubhiḥ</li>
    </ul>
    <ul id="R_2.029.013">
        <li id="R_2.029.013ab">kausalyāṃ ca ya āśīrbhir bhaktaḥ paryupatiṣṭhati</li>
        <li id="R_2.029.013cd">ācāryas taittirīyāṇām abhirūpaś ca vedavit</li>
    </ul>
    <ul id="R_2.029.014">
        <li id="R_2.029.014ab">tasya yānaṃ ca dāsīś ca saumitre saṃpradāpaya</li>
        <li id="R_2.029.014cd">kauśeyāni ca vastrāṇi yāvat tuṣyati sa dvijaḥ</li>
    </ul>
    <ul id="R_2.029.015">
        <li id="R_2.029.015ab">sūtaś citrarathaś cāryaḥ sacivaḥ suciroṣitaḥ</li>
        <li id="R_2.029.015cd">toṣayainaṃ mahārhaiś ca ratnair vastrair dhanais tathā</li>
    </ul>
    <ul id="R_2.029.016">
        <li id="R_2.029.016ab">śālivāhasahasraṃ ca dve śate bhadrakāṃs tathā</li>
        <li id="R_2.029.016cd">vyañjanārthaṃ ca saumitre gosahasram upākuru</li>
    </ul>
    <ul id="R_2.029.017">
        <li id="R_2.029.017ab">tataḥ sa puruṣavyāghras tad dhanaṃ lakṣmaṇaḥ svayam</li>
        <li id="R_2.029.017cd">yathoktaṃ brāhmaṇendrāṇām adadād dhanado yathā</li>
    </ul>
    <ul id="R_2.029.018">
        <li id="R_2.029.018ab">athābravīd bāṣpakalāṃs tiṣṭhataś copajīvinaḥ</li>
        <li id="R_2.029.018cd">saṃpradāya bahu dravyam ekaikasyopajīvinaḥ</li>
    </ul>
    <ul id="R_2.029.019">
        <li id="R_2.029.019ab">lakṣmaṇasya ca yad veśma gṛhaṃ ca yad idaṃ mama</li>
        <li id="R_2.029.019cd">aśūnyaṃ kāryam ekaikaṃ yāvadāgamanaṃ mama</li>
    </ul>
    <ul id="R_2.029.020">
        <li id="R_2.029.020ab">ity uktvā duḥkhitaṃ sarvaṃ janaṃ tam upajīvinam</li>
        <li id="R_2.029.020cd">uvācedaṃ dhanadhyakṣaṃ dhanam ānīyatām iti</li>
        <li id="R_2.029.020ef">tato 'sya dhanam ājahruḥ sarvam evopajīvinaḥ</li>
    </ul>
    <ul id="R_2.029.021">
        <li id="R_2.029.021ab">tataḥ sa puruṣavyāghras tad dhanaṃ sahalakṣmaṇaḥ</li>
        <li id="R_2.029.021cd">dvijebhyo bālavṛddhebhyaḥ kṛpaṇebhyo 'bhyadāpayat</li>
    </ul>
    <ul id="R_2.029.022">
        <li id="R_2.029.022ab">tatrāsīt piṅgalo gārgyas trijaṭo nāma vai dvijaḥ</li>
        <li id="R_2.029.022cd">ā pañcamāyāḥ kakṣyāyā nainaṃ kaś cid avārayat</li>
    </ul>
    <ul id="R_2.029.023">
        <li id="R_2.029.023ab">sa rājaputram āsādya trijaṭo vākyam abravīt</li>
        <li id="R_2.029.023cd">nirdhano bahuputro 'smi rājaputra mahāyaśaḥ</li>
        <li id="R_2.029.023ef">uñchavṛttir vane nityaṃ pratyavekṣasva mām iti</li>
    </ul>
    <ul id="R_2.029.024">
        <li id="R_2.029.024ab">tam uvāca tato rāmaḥ parihāsasamanvitam</li>
        <li id="R_2.029.024cd">gavāṃ sahasram apy ekaṃ na tu viśrāṇitaṃ mayā</li>
        <li id="R_2.029.024ef">parikṣipasi daṇḍena yāvat tāvad avāpsyasi</li>
    </ul>
    <ul id="R_2.029.025">
        <li id="R_2.029.025ab">sa śāṭīṃ tvaritaḥ kaṭyāṃ saṃbhrāntaḥ pariveṣṭya tām</li>
        <li id="R_2.029.025cd">āvidhya daṇḍaṃ cikṣepa sarvaprāṇena vegitaḥ</li>
    </ul>
    <ul id="R_2.029.026">
        <li id="R_2.029.026ab">uvāca ca tato rāmas taṃ gārgyam abhisāntvayan</li>
        <li id="R_2.029.026cd">manyur na khalu kartavyaḥ parihāso hy ayaṃ mama</li>
    </ul>
    <ul id="R_2.029.027">
        <li id="R_2.029.027ab">
            <seg type="pāda" n="a">tataḥ sabhāryas trijaṭo mahāmunir</seg>
            <seg type="pāda" n="b">gavām anīkaṃ pratigṛhya moditaḥ</seg>
        </li>
        <li id="R_2.029.027cd">
            <seg type="pāda" n="c">yaśobalaprītisukhopabṛṃhiṇīs</seg>
            <seg type="pāda" n="d">tad āśiṣaḥ pratyavadan mahātmanaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.030.001">
        <li id="R_2.030.001ab">dattvā tu saha vaidehyā brāhmaṇebhyo dhanaṃ bahu</li>
        <li id="R_2.030.001cd">jagmatuḥ pitaraṃ draṣṭuṃ sītayā saha rāghavau</li>
    </ul>
    <ul id="R_2.030.002">
        <li id="R_2.030.002ab">tato gṛhīte duṣprekṣye aśobhetāṃ tadāyudhe</li>
        <li id="R_2.030.002cd">mālādāmabhir āsakte sītayā samalaṃkṛte</li>
    </ul>
    <ul id="R_2.030.003">
        <li id="R_2.030.003ab">tataḥ prāsādaharmyāṇi vimānaśikharāṇi ca</li>
        <li id="R_2.030.003cd">adhiruhya janaḥ śrīmān udāsīno vyalokayat</li>
    </ul>
    <ul id="R_2.030.004">
        <li id="R_2.030.004ab">na hi rathyāḥ sma śakyante gantuṃ bahujanākulāḥ</li>
        <li id="R_2.030.004cd">āruhya tasmāt prāsādān dīnāḥ paśyanti rāghavam</li>
    </ul>
    <ul id="R_2.030.005">
        <li id="R_2.030.005ab">padātiṃ varjitacchatraṃ rāmaṃ dṛṣṭvā tadā janāḥ</li>
        <li id="R_2.030.005cd">ūcur bahuvidhā vācaḥ śokopahatacetasaḥ</li>
    </ul>
    <ul id="R_2.030.006">
        <li id="R_2.030.006ab">yaṃ yāntam anuyāti sma caturaṅgabalaṃ mahat</li>
        <li id="R_2.030.006cd">tam ekaṃ sītayā sārdham anuyāti sma lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_2.030.007">
        <li id="R_2.030.007ab">aiśvaryasya rasajñaḥ san kāmināṃ caiva kāmadaḥ</li>
        <li id="R_2.030.007cd">necchaty evānṛtaṃ kartuṃ pitaraṃ dharmagauravāt</li>
    </ul>
    <ul id="R_2.030.008">
        <li id="R_2.030.008ab">yā na śakyā purā draṣṭuṃ bhūtair ākāśagair api</li>
        <li id="R_2.030.008cd">tām adya sītāṃ paśyanti rājamārgagatā janāḥ</li>
    </ul>
    <ul id="R_2.030.009">
        <li id="R_2.030.009ab">aṅgarāgocitāṃ sītāṃ raktacandana sevinīm</li>
        <li id="R_2.030.009cd">varṣam uṣṇaṃ ca śītaṃ ca neṣyaty āśu vivarṇatām</li>
    </ul>
    <ul id="R_2.030.010">
        <li id="R_2.030.010ab">adya nūnaṃ daśarathaḥ sattvam āviśya bhāṣate</li>
        <li id="R_2.030.010cd">na hi rājā priyaṃ putraṃ vivāsayitum arhati</li>
    </ul>
    <ul id="R_2.030.011">
        <li id="R_2.030.011ab">nirguṇasyāpi putrasyā kāthaṃ syād vipravāsanam</li>
        <li id="R_2.030.011cd">kiṃ punar yasya loko 'yaṃ jito vṛttena kevalam</li>
    </ul>
    <ul id="R_2.030.012">
        <li id="R_2.030.012ab">ānṛśaṃsyam anukrośaḥ śrutaṃ śīlaṃ damaḥ śamaḥ</li>
        <li id="R_2.030.012cd">rāghavaṃ śobhayanty ete ṣaḍguṇāḥ puruṣottamam</li>
    </ul>
    <ul id="R_2.030.013">
        <li id="R_2.030.013ab">tasmāt tasyopaghātena prajāḥ paramapīḍitāḥ</li>
        <li id="R_2.030.013cd">audakānīva sattvāni grīṣme salilasaṃkṣayāt</li>
    </ul>
    <ul id="R_2.030.014">
        <li id="R_2.030.014ab">pīḍayā pīḍitaṃ sarvaṃ jagad asya jagatpateḥ</li>
        <li id="R_2.030.014cd">mūlasyevopaghātena vṛkṣaḥ puṣpaphalopagaḥ</li>
    </ul>
    <ul id="R_2.030.015">
        <li id="R_2.030.015ab">te lakṣmaṇa iva kṣipraṃ sapatnyaḥ sahabāndhavāḥ</li>
        <li id="R_2.030.015cd">gacchantam anugacchāmo yena gacchati rāghavaḥ</li>
    </ul>
    <ul id="R_2.030.016">
        <li id="R_2.030.016ab">udyānāni parityajya kṣetrāṇi ca gṛhāṇi ca</li>
        <li id="R_2.030.016cd">ekaduḥkhasukhā rāmam anugacchāma dhārmikam</li>
    </ul>
    <ul id="R_2.030.017">
        <li id="R_2.030.017ab">samuddhṛtanidhānāni paridhvastājirāṇi ca</li>
        <li id="R_2.030.017cd">upāttadhanadhānyāni hṛtasārāṇi sarvaśaḥ</li>
    </ul>
    <ul id="R_2.030.018">
        <li id="R_2.030.018ab">rajasābhyavakīrṇāni parityaktāni daivataiḥ</li>
        <li id="R_2.030.018cd">asmattyaktāni veśmāni kaikeyī pratipadyatām</li>
    </ul>
    <ul id="R_2.030.019">
        <li id="R_2.030.019ab">vanaṃ nagaram evāstu yena gacchati rāghavaḥ</li>
        <li id="R_2.030.019cd">asmābhiś ca parityaktaṃ puraṃ saṃpadyatāṃ vanam</li>
    </ul>
    <ul id="R_2.030.020">
        <li id="R_2.030.020ab">bilāni daṃṣṭriṇaḥ sarve sānūni mṛgapakṣiṇaḥ</li>
        <li id="R_2.030.020cd">asmattyaktaṃ prapadyantāṃ sevyamānaṃ tyajantu ca</li>
    </ul>
    <ul id="R_2.030.021">
        <li id="R_2.030.021ab">ity evaṃ vividhā vāco nānājanasamīritāḥ</li>
        <li id="R_2.030.021cd">śuśrāva rāmaḥ śrutvā ca na vicakre 'sya mānasaṃ</li>
    </ul>
    <ul id="R_2.030.022">
        <li id="R_2.030.022ab">
            <seg type="pāda" n="a">pratīkṣamāṇo 'bhijanaṃ tadārtam</seg>
            <seg type="pāda" n="b">anārtarūpaḥ prahasann ivātha</seg>
        </li>
        <li id="R_2.030.022cd">
            <seg type="pāda" n="c">jagāma rāmaḥ pitaraṃ didṛkṣuḥ</seg>
            <seg type="pāda" n="d">pitur nideśaṃ vidhivac cikīrṣuḥ</seg>
        </li>
    </ul>
    <ul id="R_2.030.023">
        <li id="R_2.030.023ab">
            <seg type="pāda" n="a">tat pūrvam aikṣvākasuto mahātmā</seg>
            <seg type="pāda" n="b">rāmo gamiṣyan vanam ārtarūpam</seg>
        </li>
        <li id="R_2.030.023cd">
            <seg type="pāda" n="c">vyatiṣṭhata prekṣya tadā sumantraṃ</seg>
            <seg type="pāda" n="d">pitur mahātmā pratihāraṇārtham</seg>
        </li>
    </ul>
    <ul id="R_2.030.024">
        <li id="R_2.030.024ab">
            <seg type="pāda" n="a">pitur nideśena tu dharmavatsalo</seg>
            <seg type="pāda" n="b">vanapraveśe kṛtabuddhiniścayaḥ</seg>
        </li>
        <li id="R_2.030.024cd">
            <seg type="pāda" n="c">sa rāghavaḥ prekṣya sumantram abravīn</seg>
            <seg type="pāda" n="d">nivedayasvāgamanaṃ nṛpāya me</seg>
        </li>
    </ul>
    <ul id="R_2.031.001">
        <li id="R_2.031.001ab">sa rāmapreṣitaḥ kṣipraṃ saṃtāpakaluṣendriyaḥ</li>
        <li id="R_2.031.001cd">praviśya nṛpatiṃ sūto niḥśvasantaṃ dadarśa ha</li>
    </ul>
    <ul id="R_2.031.002">
        <li id="R_2.031.002ab">ālokya tu mahāprājñaḥ paramākula cetasaṃ</li>
        <li id="R_2.031.002cd">rāmam evānuśocantaṃ sūtaḥ prāñjalir āsadat</li>
    </ul>
    <ul id="R_2.031.003">
        <li id="R_2.031.003ab">ayaṃ sa puruṣavyāghra dvāri tiṣṭhati te sutaḥ</li>
        <li id="R_2.031.003cd">brāhmaṇebhyo dhanaṃ dattvā sarvaṃ caivopajīvinām</li>
    </ul>
    <ul id="R_2.031.004">
        <li id="R_2.031.004ab">sa tvā paśyatu bhadraṃ te rāmaḥ satyaparākramaḥ</li>
        <li id="R_2.031.004cd">sarvān suhṛda āpṛcchya tvām idānīṃ didṛkṣate</li>
    </ul>
    <ul id="R_2.031.005">
        <li id="R_2.031.005ab">gamiṣyati mahāraṇyaṃ taṃ paśya jagatīpate</li>
        <li id="R_2.031.005cd">vṛtaṃ rājaguṇaiḥ sarvair ādityam iva raśmibhiḥ</li>
    </ul>
    <ul id="R_2.031.006">
        <li id="R_2.031.006ab">sa satyavādī dharmātmā gāmbhīryāt sāgaropamaḥ</li>
        <li id="R_2.031.006cd">ākāśa iva niṣpaṅko narendraḥ pratyuvāca tam</li>
    </ul>
    <ul id="R_2.031.007">
        <li id="R_2.031.007ab">sumantrānaya me dārān ye ke cid iha māmakāḥ</li>
        <li id="R_2.031.007cd">dāraiḥ parivṛtaḥ sarvair draṣṭum icchāmi rāghavam</li>
    </ul>
    <ul id="R_2.031.008">
        <li id="R_2.031.008ab">so 'ntaḥpuram atītyaiva striyas tā vākyam abravīt</li>
        <li id="R_2.031.008cd">āryo hvayati vo rājā gamyatāṃ tatra māciram</li>
    </ul>
    <ul id="R_2.031.009">
        <li id="R_2.031.009ab">evam uktāḥ striyaḥ sarvāḥ sumantreṇa nṛpājñayā</li>
        <li id="R_2.031.009cd">pracakramus tad bhavanaṃ bhartur ājñāya śāsanam</li>
    </ul>
    <ul id="R_2.031.010">
        <li id="R_2.031.010ab">ardhasaptaśatās tās tu pramadās tāmralocanāḥ</li>
        <li id="R_2.031.010cd">kausalyāṃ parivāryātha śanair jagmur dhṛtavratāḥ</li>
    </ul>
    <ul id="R_2.031.011">
        <li id="R_2.031.011ab">āgateṣu ca dāreṣu samavekṣya mahīpatiḥ</li>
        <li id="R_2.031.011cd">uvāca rājā taṃ sūtaṃ sumantrānaya me sutam</li>
    </ul>
    <ul id="R_2.031.012">
        <li id="R_2.031.012ab">sa sūto rāmam ādāya lakṣmaṇaṃ maithilīṃ tadā</li>
        <li id="R_2.031.012cd">jagāmābhimukhas tūrṇaṃ sakāśaṃ jagatīpateḥ</li>
    </ul>
    <ul id="R_2.031.013">
        <li id="R_2.031.013ab">sa rājā putram āyāntaṃ dṛṣṭvā dūrāt kṛtāñjalim</li>
        <li id="R_2.031.013cd">utpapātāsanāt tūrṇam ārtaḥ strījanasaṃvṛtaḥ</li>
    </ul>
    <ul id="R_2.031.014">
        <li id="R_2.031.014ab">so 'bhidudrāva vegena rāmaṃ dṛṣṭvā viśāṃ patiḥ</li>
        <li id="R_2.031.014cd">tam asaṃprāpya duḥkhārtaḥ papāta bhuvi mūrchitaḥ</li>
    </ul>
    <ul id="R_2.031.015">
        <li id="R_2.031.015ab">taṃ rāmo 'bhyapātat kṣipraṃ lakṣmaṇaś ca mahārathaḥ</li>
        <li id="R_2.031.015cd">visaṃjñam iva duḥkhena saśokaṃ nṛpatiṃ tadā</li>
    </ul>
    <ul id="R_2.031.016">
        <li id="R_2.031.016ab">strīsahasraninādaś ca saṃjajñe rājaveśmani</li>
        <li id="R_2.031.016cd">hāhā rāmeti sahasā bhūṣaṇadhvanimūrchitaḥ</li>
    </ul>
    <ul id="R_2.031.017">
        <li id="R_2.031.017ab">taṃ pariṣvajya bāhubhyāṃ tāv ubhau rāmalakṣmaṇau</li>
        <li id="R_2.031.017cd">paryaṅke sītayā sārdhaṃ rudantaḥ samaveśayan</li>
    </ul>
    <ul id="R_2.031.018">
        <li id="R_2.031.018ab">atha rāmo muhūrtena labdhasaṃjñaṃ mahīpatim</li>
        <li id="R_2.031.018cd">uvāca prāñjalir bhūtvā śokārṇavapariplutam</li>
    </ul>
    <ul id="R_2.031.019">
        <li id="R_2.031.019ab">āpṛcche tvāṃ mahārāja sarveṣām īśvaro 'si naḥ</li>
        <li id="R_2.031.019cd">prasthitaṃ daṇḍakāraṇyaṃ paśya tvaṃ kuśalena mām</li>
    </ul>
    <ul id="R_2.031.020">
        <li id="R_2.031.020ab">lakṣmaṇaṃ cānujānīhi sītā cānveti māṃ vanam</li>
        <li id="R_2.031.020cd">kāraṇair bahubhis tathyair vāryamāṇau na cecchataḥ</li>
    </ul>
    <ul id="R_2.031.021">
        <li id="R_2.031.021ab">anujānīhi sarvān naḥ śokam utsṛjya mānada</li>
        <li id="R_2.031.021cd">lakṣmaṇaṃ māṃ ca sītāṃ ca prajāpatir iva prajāḥ</li>
    </ul>
    <ul id="R_2.031.022">
        <li id="R_2.031.022ab">pratīkṣamāṇam avyagram anujñāṃ jagatīpateḥ</li>
        <li id="R_2.031.022cd">uvāca rarjā saṃprekṣya vanavāsāya rāghavam</li>
    </ul>
    <ul id="R_2.031.023">
        <li id="R_2.031.023ab">ahaṃ rāghava kaikeyyā varadānena mohitaḥ</li>
        <li id="R_2.031.023cd">ayodhyāyās tvam evādya bhava rājā nigṛhya mām</li>
    </ul>
    <ul id="R_2.031.024">
        <li id="R_2.031.024ab">evam ukto nṛpatinā rāmo dharmabhṛtāṃ varaḥ</li>
        <li id="R_2.031.024cd">pratyuvācāñjaliṃ kṛtvā pitaraṃ vākyakovidaḥ</li>
    </ul>
    <ul id="R_2.031.025">
        <li id="R_2.031.025ab">bhavān varṣasahasrāya pṛthivyā nṛpate patiḥ</li>
        <li id="R_2.031.025cd">ahaṃ tv araṇye vatsyāmi na me kāryaṃ tvayānṛtam</li>
    </ul>
    <ul id="R_2.031.026">
        <li id="R_2.031.026ab">śreyase vṛddhaye tāta punarāgamanāya ca</li>
        <li id="R_2.031.026cd">gacchasvāriṣṭam avyagraḥ panthānam akutobhayam</li>
    </ul>
    <ul id="R_2.031.027">
        <li id="R_2.031.027ab">adya tv idānīṃ rajanīṃ putra mā gaccha sarvathā</li>
        <li id="R_2.031.027cd">mātaraṃ māṃ ca saṃpaśyan vasemām adya śarvarīm</li>
        <li id="R_2.031.027ef">tarpitaḥ sarvakāmais tvaṃ śvaḥkāle sādhayiṣyasi</li>
    </ul>
    <ul id="R_2.031.028">
        <li id="R_2.031.028ab">atha rāmas tathā śrutvā pitur ārtasya bhāṣitam</li>
        <li id="R_2.031.028cd">lakṣmaṇena saha bhrātrā dīno vacanam abravīt</li>
    </ul>
    <ul id="R_2.031.029">
        <li id="R_2.031.029ab">prāpsyāmi yān adya guṇān ko me śvastān pradāsyati</li>
        <li id="R_2.031.029cd">apakramaṇam evātaḥ sarvakāmair ahaṃ vṛṇe</li>
    </ul>
    <ul id="R_2.031.030">
        <li id="R_2.031.030ab">iyaṃ sarāṣṭrā sajanā dhanadhānyasamākulā</li>
        <li id="R_2.031.030cd">mayā visṛṣṭā vasudhā bharatāya pradīyatām</li>
    </ul>
    <ul id="R_2.031.031">
        <li id="R_2.031.031ab">apagacchatu te duḥkhaṃ mā bhūr bāṣpapariplutaḥ</li>
        <li id="R_2.031.031cd">na hi kṣubhyati durdharṣaḥ samudraḥ saritāṃ patiḥ</li>
    </ul>
    <ul id="R_2.031.032">
        <li id="R_2.031.032ab">naivāhaṃ rājyam icchāmi na sukhaṃ na ca maithilīm</li>
        <li id="R_2.031.032cd">tvām ahaṃ satyam icchāmi nānṛtaṃ puruṣarṣabha</li>
    </ul>
    <ul id="R_2.031.033">
        <li id="R_2.031.033ab">
            <seg type="pāda" n="a">puraṃ ca rāṣṭraṃ ca mahī ca kevalā</seg>
            <seg type="pāda" n="b">mayā nisṛṣṭā bharatāya dīyatām</seg>
        </li>
        <li id="R_2.031.033cd">
            <seg type="pāda" n="c">ahaṃ nideśaṃ bhavato 'nupālayan</seg>
            <seg type="pāda" n="d">vanaṃ gamiṣyāmi cirāya sevitum</seg>
        </li>
    </ul>
    <ul id="R_2.031.034">
        <li id="R_2.031.034ab">
            <seg type="pāda" n="a">mayā nisṛṣṭāṃ bharato mahīm imāṃ</seg>
            <seg type="pāda" n="b">saśailakhaṇḍāṃ sapurāṃ sakānanām</seg>
        </li>
        <li id="R_2.031.034cd">
            <seg type="pāda" n="c">śivāṃ susīmām anuśāstu kevalaṃ</seg>
            <seg type="pāda" n="d">tvayā yad uktaṃ nṛpate yathāstu tat</seg>
        </li>
    </ul>
    <ul id="R_2.031.035">
        <li id="R_2.031.035ab">
            <seg type="pāda" n="a">na me tathā pārthiva dhīyate mano</seg>
            <seg type="pāda" n="b">mahatsu kāmeṣu na cātmanaḥ priye</seg>
        </li>
        <li id="R_2.031.035cd">
            <seg type="pāda" n="c">yathā nideśe tava śiṣṭasaṃmate</seg>
            <seg type="pāda" n="d">vyapaitu duḥkhaṃ tava matkṛte 'nagha</seg>
        </li>
    </ul>
    <ul id="R_2.031.036">
        <li id="R_2.031.036ab">
            <seg type="pāda" n="a">tad adya naivānagha rājyam avyayaṃ</seg>
            <seg type="pāda" n="b">na sarvakāmān na sukhaṃ na maithilīm</seg>
        </li>
        <li id="R_2.031.036cd">
            <seg type="pāda" n="c">na jīvitaṃ tvām anṛtena yojayan</seg>
            <seg type="pāda" n="d">vṛṇīya satyaṃ vratam astu te tathā</seg>
        </li>
    </ul>
    <ul id="R_2.031.037">
        <li id="R_2.031.037ab">
            <seg type="pāda" n="a">phalāni mūlāni ca bhakṣayan vane</seg>
            <seg type="pāda" n="b">girīṃś ca paśyan saritaḥ sarāṃsi ca</seg>
        </li>
        <li id="R_2.031.037cd">
            <seg type="pāda" n="c">vanaṃ praviśyaiva vicitrapādapaṃ</seg>
            <seg type="pāda" n="d">sukhī bhaviṣyāmi tavāstu nirvṛtiḥ</seg>
        </li>
    </ul>
    <ul id="R_2.032.001">
        <li id="R_2.032.001ab">tataḥ sumantram aikṣvākaḥ pīḍito 'tra pratijñayā</li>
        <li id="R_2.032.001cd">sabāṣpam atiniḥśvasya jagādedaṃ punaḥ punaḥ</li>
    </ul>
    <ul id="R_2.032.002">
        <li id="R_2.032.002ab">sūta ratnasusaṃpūrṇā caturvidhabalā camūḥ</li>
        <li id="R_2.032.002cd">rāgavasyānuyātrārthaṃ kṣipraṃ pratividhīyatām</li>
    </ul>
    <ul id="R_2.032.003">
        <li id="R_2.032.003ab">rūpājīvā ca śālinyo vaṇijaś ca mahādhanāḥ</li>
        <li id="R_2.032.003cd">śobhayantu kumārasya vāhinīṃ suprasāritāḥ</li>
    </ul>
    <ul id="R_2.032.004">
        <li id="R_2.032.004ab">ye cainam upajīvanti ramate yaiś ca vīryataḥ</li>
        <li id="R_2.032.004cd">teṣāṃ bahuvidhaṃ dattvā tān apy atra niyojaya</li>
    </ul>
    <ul id="R_2.032.005">
        <li id="R_2.032.005ab">nighnan mṛgān kuñjarāṃś ca pibaṃś cāraṇyakaṃ madhu</li>
        <li id="R_2.032.005cd">nadīś ca vividhāḥ paśyan na rājyaṃ saṃsmariṣyati</li>
    </ul>
    <ul id="R_2.032.006">
        <li id="R_2.032.006ab">dhānyakośaś ca yaḥ kaś cid dhanakośaś ca māmakaḥ</li>
        <li id="R_2.032.006cd">tau rāmam anugacchetāṃ vasantaṃ nirjane vane</li>
    </ul>
    <ul id="R_2.032.007">
        <li id="R_2.032.007ab">yajan puṇyeṣu deśeṣu visṛjaṃś cāptadakṣiṇāḥ</li>
        <li id="R_2.032.007cd">ṛṣibhiś ca samāgamya pravatsyati sukhaṃ vane</li>
    </ul>
    <ul id="R_2.032.008">
        <li id="R_2.032.008ab">bharataś ca mahābāhur ayodhyāṃ pālayiṣyati</li>
        <li id="R_2.032.008cd">sarvakāmaiḥ punaḥ śrīmān rāmaḥ saṃsādhyatām iti</li>
    </ul>
    <ul id="R_2.032.009">
        <li id="R_2.032.009ab">evaṃ bruvati kākutsthe kaikeyyā bhayam āgatam</li>
        <li id="R_2.032.009cd">mukhaṃ cāpy agamāc choṣaṃ svaraś cāpi nyarudhyata</li>
    </ul>
    <ul id="R_2.032.010">
        <li id="R_2.032.010ab">sā viṣaṇṇā ca saṃtrastā kaikeyī vākyam abravīt</li>
        <li id="R_2.032.010cd">rājyaṃ gatajanaṃ sādho pītamaṇḍāṃ surām iva</li>
        <li id="R_2.032.010ef">nirāsvādyatamaṃ śūnyaṃ bharato nābhipatsyate</li>
    </ul>
    <ul id="R_2.032.011">
        <li id="R_2.032.011ab">kaikeyyāṃ muktalajjāyāṃ vadantyām atidāruṇam</li>
        <li id="R_2.032.011cd">rājā daśaratho vākyam uvācāyatalocanām</li>
        <li id="R_2.032.011ef">vahantaṃ kiṃ tudasi māṃ niyujya dhuri māhite</li>
    </ul>
    <ul id="R_2.032.012">
        <li id="R_2.032.012ab">kaikeyī dviguṇaṃ kruddhā rājānam idam abravīt</li>
        <li id="R_2.032.012cd">tavaiva vaṃśe sagaro jyeṣṭhaṃ putram upārudhat</li>
        <li id="R_2.032.012ef">asamañja iti khyātaṃ tathāyaṃ gantum arhati</li>
    </ul>
    <ul id="R_2.032.013">
        <li id="R_2.032.013ab">evam ukto dhig ity eva rājā daśaratho 'bravīt</li>
        <li id="R_2.032.013cd">vrīḍitaś ca janaḥ sarvaḥ sā ca tan nāvabudhyata</li>
    </ul>
    <ul id="R_2.032.014">
        <li id="R_2.032.014ab">tatra vṛddho mahāmātraḥ siddhārtho nāma nāmataḥ</li>
        <li id="R_2.032.014cd">śucir bahumato rājñaḥ kaikeyīm idam abravīt</li>
    </ul>
    <ul id="R_2.032.015">
        <li id="R_2.032.015ab">asamañjo gṛhītvā tu krīḍitaḥ pathi dārakān</li>
        <li id="R_2.032.015cd">sarayvāḥ prakṣipann apsu ramate tena durmatiḥ</li>
    </ul>
    <ul id="R_2.032.016">
        <li id="R_2.032.016ab">taṃ dṛṣṭvā nāgaraḥ sarve kruddhā rājānam abruvan</li>
        <li id="R_2.032.016cd">asamañjaṃ vṛṣīṇvaikam asmān vā rāṣṭravardhana</li>
    </ul>
    <ul id="R_2.032.017">
        <li id="R_2.032.017ab">tān uvāca tato rājā kiṃnimittam idaṃ bhayam</li>
        <li id="R_2.032.017cd">tāś cāpi rājñā saṃpṛṣṭā vākyaṃ prakṛtayo 'bruvan</li>
    </ul>
    <ul id="R_2.032.018">
        <li id="R_2.032.018ab">krīḍitas tv eṣa naḥ putrān bālān udbhrāntacetanaḥ</li>
        <li id="R_2.032.018cd">sarayvāṃ prakṣipan maurkhyād atulāṃ prītim aśnute</li>
    </ul>
    <ul id="R_2.032.019">
        <li id="R_2.032.019ab">sa tāsāṃ vacanaṃ śrutvā prakṛtīnāṃ narādhipa</li>
        <li id="R_2.032.019cd">taṃ tatyājāhitaṃ putraṃ tāsāṃ priyacikīrṣayā</li>
    </ul>
    <ul id="R_2.032.020">
        <li id="R_2.032.020ab">ity evam atyajad rājā sagaro vai sudhārmikaḥ</li>
        <li id="R_2.032.020cd">rāmaḥ kim akarot pāpaṃ yenaivam uparudhyate</li>
    </ul>
    <ul id="R_2.032.021">
        <li id="R_2.032.021ab">śrutvā tu siddhārthavaco rājā śrāntatarasvanaḥ</li>
        <li id="R_2.032.021cd">śokopahatayā vācā kaikeyīm idam abravīt</li>
    </ul>
    <ul id="R_2.032.022">
        <li id="R_2.032.022ab">
            <seg type="pāda" n="a">anuvrajiṣyāmy aham adya rāmaṃ</seg>
            <seg type="pāda" n="b">rājyaṃ parityajya sukhaṃ dhanaṃ ca</seg>
        </li>
        <li id="R_2.032.022cd">
            <seg type="pāda" n="c">sahaiva rājñā bharatena ca tvaṃ</seg>
            <seg type="pāda" n="d">yathā sukhaṃ bhuṅkṣva cirāya rājyam</seg>
        </li>
    </ul>
    <ul id="R_2.033.001">
        <li id="R_2.033.001ab">mahāmātravacaḥ śrutvā rāmo daśarathaṃ tadā</li>
        <li id="R_2.033.001cd">anvabhāṣata vākyaṃ tu vinayajño vinītavat</li>
    </ul>
    <ul id="R_2.033.002">
        <li id="R_2.033.002ab">tyaktabhogasya me rājan vane vanyena jīvataḥ</li>
        <li id="R_2.033.002cd">kiṃ kāryam anuyātreṇa tyaktasaṅgasya sarvataḥ</li>
    </ul>
    <ul id="R_2.033.003">
        <li id="R_2.033.003ab">yo hi dattvā dvipaśreṣṭhaṃ kakṣyāyāṃ kurute manaḥ</li>
        <li id="R_2.033.003cd">rajjusnehena kiṃ tasya tyajataḥ kuñjarottamam</li>
    </ul>
    <ul id="R_2.033.004">
        <li id="R_2.033.004ab">tathā mama satāṃ śreṣṭha kiṃ dhvajinyā jagatpate</li>
        <li id="R_2.033.004cd">sarvāṇy evānujānāmi cīrāṇy evānayantu me</li>
    </ul>
    <ul id="R_2.033.005">
        <li id="R_2.033.005ab">khanitrapiṭake cobhe mamānayata gacchataḥ</li>
        <li id="R_2.033.005cd">caturdaśa vane vāsaṃ varṣāṇi vasato mama</li>
    </ul>
    <ul id="R_2.033.006">
        <li id="R_2.033.006ab">atha cīrāṇi kaikeyī svayam āhṛtya rāghavam</li>
        <li id="R_2.033.006cd">uvāca paridhatsveti janaughe nirapatrapā</li>
    </ul>
    <ul id="R_2.033.007">
        <li id="R_2.033.007ab">sa cīre puruṣavyāghraḥ kaikeyyāḥ pratigṛhya te</li>
        <li id="R_2.033.007cd">sūkṣmavastram avakṣipya munivastrāṇy avasta ha</li>
    </ul>
    <ul id="R_2.033.008">
        <li id="R_2.033.008ab">lakṣmaṇaś cāpi tatraiva vihāya vasane śubhe</li>
        <li id="R_2.033.008cd">tāpasāc chādane caiva jagrāha pitur agrataḥ</li>
    </ul>
    <ul id="R_2.033.009">
        <li id="R_2.033.009ab">athātmaparidhānārthaṃ sītā kauśeyavāsinī</li>
        <li id="R_2.033.009cd">samīkṣya cīraṃ saṃtrastā pṛṣatī vāgurām iva</li>
    </ul>
    <ul id="R_2.033.010">
        <li id="R_2.033.010ab">sā vyapatrapamāṇeva pratigṛhya ca durmanāḥ</li>
        <li id="R_2.033.010cd">gandharvarājapratimaṃ bhartāram idam abravīt</li>
        <li id="R_2.033.010ef">kathaṃ nu cīraṃ badhnanti munayo vanavāsinaḥ</li>
    </ul>
    <ul id="R_2.033.011">
        <li id="R_2.033.011ab">kṛtvā kaṇṭhe ca sā cīram ekam ādāya pāṇinā</li>
        <li id="R_2.033.011cd">tasthau hy akuṣalā tatra vrīḍitā janakātmaja</li>
    </ul>
    <ul id="R_2.033.012">
        <li id="R_2.033.012ab">tasyās tat kṣipram āgamya rāmo dharmabhṛtāṃ varaḥ</li>
        <li id="R_2.033.012cd">cīraṃ babandha sītāyāḥ kauśeyasyopari svayam</li>
    </ul>
    <ul id="R_2.033.013">
        <li id="R_2.033.013ab">tasyāṃ cīraṃ vasānāyāṃ nāthavatyām anāthavat</li>
        <li id="R_2.033.013cd">pracukrośa janaḥ sarvo dhik tvāṃ daśarathaṃ tv iti</li>
    </ul>
    <ul id="R_2.033.014">
        <li id="R_2.033.014ab">sa niḥśvasyoṣṇam aikṣvākas tāṃ bhāryām idam abravīt</li>
        <li id="R_2.033.014cd">kaikeyi kuśacīreṇa na sītā gantum arhati</li>
    </ul>
    <ul id="R_2.033.015">
        <li id="R_2.033.015ab">nanu paryāptam etat te pāpe rāmavivāsanam</li>
        <li id="R_2.033.015cd">kim ebhiḥ kṛpaṇair bhūyaḥ pātakair api te kṛtaiḥ</li>
    </ul>
    <ul id="R_2.033.016">
        <li id="R_2.033.016ab">evaṃ bruvantaṃ pitaraṃ rāmaḥ saṃprasthito vanam</li>
        <li id="R_2.033.016cd">avākśirasam āsīnam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.033.017">
        <li id="R_2.033.017ab">iyaṃ dhārmika kausalyā mama mātā yaśasvinī</li>
        <li id="R_2.033.017cd">vṛddhā cākṣudraśīlā ca na ca tvāṃ devagarhite</li>
    </ul>
    <ul id="R_2.033.018">
        <li id="R_2.033.018ab">mayā vihīnāṃ varada prapannāṃ śokasāgaram</li>
        <li id="R_2.033.018cd">adṛṣṭapūrvavyasanāṃ bhūyaḥ saṃmantum arhasi</li>
    </ul>
    <ul id="R_2.033.019">
        <li id="R_2.033.019ab">
            <seg type="pāda" n="a">imāṃ mahendropamajātagarbhiṇīṃ</seg>
            <seg type="pāda" n="b">tathā vidhātuṃ janamīṃ mamārhasi</seg>
        </li>
        <li id="R_2.033.019cd">
            <seg type="pāda" n="c">yathā vanasthe mayi śokakarśitā</seg>
            <seg type="pāda" n="d">na jīvitaṃ nyasya yamakṣayaṃ vrajet</seg>
        </li>
    </ul>
    <ul id="R_2.034.001">
        <li id="R_2.034.001ab">rāmasya tu vacaḥ śrutvā muniveṣadharaṃ ca tam</li>
        <li id="R_2.034.001cd">samīkṣya saha bhāryābhī rājā vigatacetanaḥ</li>
    </ul>
    <ul id="R_2.034.002">
        <li id="R_2.034.002ab">nainaṃ duḥkhena saṃtaptaḥ pratyavaikṣata rāghavam</li>
        <li id="R_2.034.002cd">na cainam abhisaṃprekṣya pratyabhāṣata durmanāḥ</li>
    </ul>
    <ul id="R_2.034.003">
        <li id="R_2.034.003ab">sa muhūrtam ivāsaṃjño duḥkhitaś ca mahīpatiḥ</li>
        <li id="R_2.034.003cd">vilalāpa mahābāhū rāmam evānucintayan</li>
    </ul>
    <ul id="R_2.034.004">
        <li id="R_2.034.004ab">manye khalu mayā pūrvaṃ vivatsā bahavaḥ kṛtāḥ</li>
        <li id="R_2.034.004cd">prāṇino hiṃsitā vāpi tasmād idam upasthitam</li>
    </ul>
    <ul id="R_2.034.005">
        <li id="R_2.034.005ab">na tv evānāgate kāle dehāc cyavati jīvitam</li>
        <li id="R_2.034.005cd">kaikeyyā kliśyamānasya mṛtyur mama na vidyate</li>
    </ul>
    <ul id="R_2.034.006">
        <li id="R_2.034.006ab">yo 'haṃ pāvakasaṃkāśaṃ paśyāmi purataḥ sthitam</li>
        <li id="R_2.034.006cd">vihāya vasane sūkṣme tāpasācchādam ātmajam</li>
    </ul>
    <ul id="R_2.034.007">
        <li id="R_2.034.007ab">ekasyāḥ khalu kaikeyyāḥ kṛte 'yaṃ kliśyate janaḥ</li>
        <li id="R_2.034.007cd">svārthe prayatamānāyāḥ saṃśritya nikṛtiṃ tv imām</li>
    </ul>
    <ul id="R_2.034.008">
        <li id="R_2.034.008ab">evam uktvā tu vacanaṃ bāṣpeṇa pihitekṣṇaha</li>
        <li id="R_2.034.008cd">rāmeti sakṛd evoktvā vyāhartuṃ na śaśāka ha</li>
    </ul>
    <ul id="R_2.034.009">
        <li id="R_2.034.009ab">saṃjñāṃ tu pratilabhyaiva muhūrtāt sa mahīpatiḥ</li>
        <li id="R_2.034.009cd">netrābhyām aśrupūrṇābhyāṃ sumantram idam abravīt</li>
    </ul>
    <ul id="R_2.034.010">
        <li id="R_2.034.010ab">aupavāhyaṃ rathaṃ yuktvā tvam āyāhi hayottamaiḥ</li>
        <li id="R_2.034.010cd">prāpayainaṃ mahābhāgam ito janapadāt param</li>
    </ul>
    <ul id="R_2.034.011">
        <li id="R_2.034.011ab">evaṃ manye guṇavatāṃ guṇānāṃ phalam ucyate</li>
        <li id="R_2.034.011cd">pitrā mātrā ca yat sādhur vīro nirvāsyate vanam</li>
    </ul>
    <ul id="R_2.034.012">
        <li id="R_2.034.012ab">rājño vacanam ājñāya sumantraḥ śīghravikramaḥ</li>
        <li id="R_2.034.012cd">yojayitvāyayau tatra ratham aśvair alaṃkṛtam</li>
    </ul>
    <ul id="R_2.034.013">
        <li id="R_2.034.013ab">taṃ rathaṃ rājaputrāya sūtaḥ kanakabhūṣitam</li>
        <li id="R_2.034.013cd">ācacakṣe 'ñjaliṃ kṛtvā yuktaṃ paramavājibhiḥ</li>
    </ul>
    <ul id="R_2.034.014">
        <li id="R_2.034.014ab">rājā satvaram āhūya vyāpṛtaṃ vittasaṃcaye</li>
        <li id="R_2.034.014cd">uvāca deśakālajño niścitaṃ sarvataḥ śuci</li>
    </ul>
    <ul id="R_2.034.015">
        <li id="R_2.034.015ab">vāsāṃsi ca mahārhāṇi bhūṣaṇāni varāṇi ca</li>
        <li id="R_2.034.015cd">varṣāṇy etāni saṃkhyāya vaidehyāḥ kṣipram ānaya</li>
    </ul>
    <ul id="R_2.034.016">
        <li id="R_2.034.016ab">narendreṇaivam uktas tu gatvā kośagṛhaṃ tataḥ</li>
        <li id="R_2.034.016cd">prāyacchat sarvam āhṛtya sītāyai kṣipram eva tat</li>
    </ul>
    <ul id="R_2.034.017">
        <li id="R_2.034.017ab">sā sujātā sujātāni vaidehī prasthitā vanam</li>
        <li id="R_2.034.017cd">bhūṣayām āsa gātrāṇi tair vicitrair vibhūṣaṇaiḥ</li>
    </ul>
    <ul id="R_2.034.018">
        <li id="R_2.034.018ab">vyarājayata vaidehī veśma tat suvibhūṣitā</li>
        <li id="R_2.034.018cd">udyato 'ṃśumataḥ kāle khaṃ prabheva vivasvataḥ</li>
    </ul>
    <ul id="R_2.034.019">
        <li id="R_2.034.019ab">tāṃ bhujābhyāṃ pariṣvajya śvaśrūr vacanam abravīt</li>
        <li id="R_2.034.019cd">anācarantīṃ kṛpaṇaṃ mūdhny upāghrāya maithilīm</li>
    </ul>
    <ul id="R_2.034.020">
        <li id="R_2.034.020ab">asatyaḥ sarvaloke 'smin satataṃ satkṛtāḥ priyaiḥ</li>
        <li id="R_2.034.020cd">bhartāraṃ nānumanyante vinipātagataṃ striyaḥ</li>
    </ul>
    <ul id="R_2.034.021">
        <li id="R_2.034.021ab">sa tvayā nāvamantavyaḥ putraḥ pravrājito mama</li>
        <li id="R_2.034.021cd">tava daivatam astv eṣa nirdhanaḥ sadhano 'pi vā</li>
    </ul>
    <ul id="R_2.034.022">
        <li id="R_2.034.022ab">vijñāya vacanaṃ sītā tasyā dharmārthasaṃhitam</li>
        <li id="R_2.034.022cd">kṛtāñjalir uvācedaṃ śvaśrūm abhimukhe sthitā</li>
    </ul>
    <ul id="R_2.034.023">
        <li id="R_2.034.023ab">kariṣye sarvam evāham āryā yad anuśāsti mām</li>
        <li id="R_2.034.023cd">abhijñāsmi yathā bhartur vartitavyaṃ śrutaṃ ca me</li>
    </ul>
    <ul id="R_2.034.024">
        <li id="R_2.034.024ab">na mām asajjanenāryā samānayitum arhati</li>
        <li id="R_2.034.024cd">dharmād vicalituṃ nāham alaṃ candrād iva prabhā</li>
    </ul>
    <ul id="R_2.034.025">
        <li id="R_2.034.025ab">nātantrī vādyate vīṇā nācakro vartate rathaḥ</li>
        <li id="R_2.034.025cd">nāpatiḥ sukham edhate yā syād api śatātmajā</li>
    </ul>
    <ul id="R_2.034.026">
        <li id="R_2.034.026ab">mitaṃ dadāti hi pitā mitaṃ mātā mitaṃ sutaḥ</li>
        <li id="R_2.034.026cd">amitasya hi dātāraṃ bhartāraṃ kā na pūjayet</li>
    </ul>
    <ul id="R_2.034.027">
        <li id="R_2.034.027ab">sāham evaṃgatā śreṣṭhā śrutadharmaparāvarā</li>
        <li id="R_2.034.027cd">ārye kim avamanyeyaṃ strīṇāṃ bhartā hi daivatam</li>
    </ul>
    <ul id="R_2.034.028">
        <li id="R_2.034.028ab">sītāyā vacanaṃ śrutvā kausalyā hṛdayaṃgamam</li>
        <li id="R_2.034.028cd">śuddhasattvā mumocāśru sahasā duḥkhaharṣajam</li>
    </ul>
    <ul id="R_2.034.029">
        <li id="R_2.034.029ab">tāṃ prāñjalir abhikramya mātṛmadhye 'tisatkṛtām</li>
        <li id="R_2.034.029cd">rāmaḥ paramadharmajño mātaraṃ vākyam abravīt</li>
    </ul>
    <ul id="R_2.034.030">
        <li id="R_2.034.030ab">amba mā duḥkhitā bhūs tvaṃ paśya tvaṃ pitaraṃ mama</li>
        <li id="R_2.034.030cd">kṣayo hi vanavāsasya kṣipram eva bhaviṣyati</li>
    </ul>
    <ul id="R_2.034.031">
        <li id="R_2.034.031ab">suptāyās te gamiṣyanti navavarṣāṇi pañca ca</li>
        <li id="R_2.034.031cd">sā samagram iha prāptaṃ māṃ drakṣyasi suhṛdvṛtam</li>
    </ul>
    <ul id="R_2.034.032">
        <li id="R_2.034.032ab">etāvad abhinītārtham uktvā sa jananīṃ vacaḥ</li>
        <li id="R_2.034.032cd">trayaḥ śataśatārdhā hi dadarśāvekṣya mātaraḥ</li>
    </ul>
    <ul id="R_2.034.033">
        <li id="R_2.034.033ab">tāś cāpi sa tathaivārtā mātṝr daśarathātmajaḥ</li>
        <li id="R_2.034.033cd">dharmayuktam idaṃ vākyaṃ nijagāda kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_2.034.034">
        <li id="R_2.034.034ab">saṃvāsāt paruṣaṃ kiṃ cid ajñānād vāpi yat kṛtam</li>
        <li id="R_2.034.034cd">tan me samanujānīta sarvāś cāmantrayāmi vaḥ</li>
    </ul>
    <ul id="R_2.034.035">
        <li id="R_2.034.035ab">jajñe 'tha tāsāṃ saṃnādaḥ krauñcīnām iva niḥsvanaḥ</li>
        <li id="R_2.034.035cd">mānavendrasya bhāryāṇām evaṃ vadati rāghave</li>
    </ul>
    <ul id="R_2.034.036">
        <li id="R_2.034.036ab">
            <seg type="pāda" n="a">murajapaṇavameghaghoṣavad</seg>
            <seg type="pāda" n="b">daśarathaveśma babhūva yat purā</seg>
        </li>
        <li id="R_2.034.036cd">
            <seg type="pāda" n="c">vilapita paridevanākulaṃ</seg>
            <seg type="pāda" n="d">vyasanagataṃ tad abhūt suduḥkhitam</seg>
        </li>
    </ul>
    <ul id="R_2.035.001">
        <li id="R_2.035.001ab">atha rāmaś ca sītā ca lakṣmaṇaś ca kṛtāñjaliḥ</li>
        <li id="R_2.035.001cd">upasaṃgṛhya rājānaṃ cakrur dīnāḥ pradakṣiṇam</li>
    </ul>
    <ul id="R_2.035.002">
        <li id="R_2.035.002ab">taṃ cāpi samanujñāpya dharmajñaḥ sītayā saha</li>
        <li id="R_2.035.002cd">rāghavaḥ śokasaṃmūḍho jananīm abhyavādayat</li>
    </ul>
    <ul id="R_2.035.003">
        <li id="R_2.035.003ab">anvakṣaṃ lakṣmaṇo bhrātuḥ kausalyām abhyavādayat</li>
        <li id="R_2.035.003cd">atha mātuḥ sumitrāyā jagrāha caraṇau punaḥ</li>
    </ul>
    <ul id="R_2.035.004">
        <li id="R_2.035.004ab">taṃ vandamānaṃ rudatī mātā saumitrim abravīt</li>
        <li id="R_2.035.004cd">hitakāmā mahābāhuṃ mūrdhny upāghrāya lakṣmaṇam</li>
    </ul>
    <ul id="R_2.035.005">
        <li id="R_2.035.005ab">sṛṣṭas tvaṃ vanavāsāya svanuraktaḥ suhṛjjane</li>
        <li id="R_2.035.005cd">rāme pramādaṃ mā kārṣīḥ putra bhrātari gacchati</li>
    </ul>
    <ul id="R_2.035.006">
        <li id="R_2.035.006ab">vyasanī vā samṛddho vā gatir eṣa tavānagha</li>
        <li id="R_2.035.006cd">eṣa loke satāṃ dharmo yaj jyeṣṭhavaśago bhavet</li>
    </ul>
    <ul id="R_2.035.007">
        <li id="R_2.035.007ab">idaṃ hi vṛttam ucitaṃ kulasyāsya sanātanam</li>
        <li id="R_2.035.007cd">dānaṃ dīkṣā ca yajñeṣu tanutyāgo mṛdheṣu ca</li>
    </ul>
    <ul id="R_2.035.008">
        <li id="R_2.035.008ab">rāmaṃ daśarathaṃ viddhi māṃ viddhi janakātmajām</li>
        <li id="R_2.035.008cd">ayodhyām aṭavīṃ viddhi gaccha tāta yathāsukham</li>
    </ul>
    <ul id="R_2.035.009">
        <li id="R_2.035.009ab">tataḥ sumantraḥ kākutsthaṃ prāñjalir vākyam abravīt</li>
        <li id="R_2.035.009cd">vinīto vinayajñaś ca mātalir vāsavaṃ yathā</li>
    </ul>
    <ul id="R_2.035.010">
        <li id="R_2.035.010ab">ratham āroha bhadraṃ te rājaputra mahāyaśaḥ</li>
        <li id="R_2.035.010cd">kṣipraṃ tvāṃ prāpayiṣyāmi yatra māṃ rāma vakṣyasi</li>
    </ul>
    <ul id="R_2.035.011">
        <li id="R_2.035.011ab">caturdaśa hi varṣāṇi vastavyāni vane tvayā</li>
        <li id="R_2.035.011cd">tāny upakramitavyāni yāni devyāsi coditaḥ</li>
    </ul>
    <ul id="R_2.035.012">
        <li id="R_2.035.012ab">taṃ rathaṃ sūryasaṃkāśaṃ sītā hṛṣṭena cetasā</li>
        <li id="R_2.035.012cd">āruroha varārohā kṛtvālaṃkāram ātmanaḥ</li>
    </ul>
    <ul id="R_2.035.013">
        <li id="R_2.035.013ab">tathaivāyudhajātāni bhrātṛbhyāṃ kavacāni ca</li>
        <li id="R_2.035.013cd">rathopasthe pratinyasya sacarmakaṭhinaṃ ca tat</li>
    </ul>
    <ul id="R_2.035.014">
        <li id="R_2.035.014ab">sītātṛtīyān ārūḍhān dṛṣṭvā dhṛṣṭam acodayat</li>
        <li id="R_2.035.014cd">sumantraḥ saṃmatān aśvān vāyuvegasamāñ jave</li>
    </ul>
    <ul id="R_2.035.015">
        <li id="R_2.035.015ab">prayāte tu mahāraṇyaṃ cirarātrāya rāghave</li>
        <li id="R_2.035.015cd">babhūva nagare mūrcchā balamūrcchā janasya ca</li>
    </ul>
    <ul id="R_2.035.016">
        <li id="R_2.035.016ab">tat samākulasaṃbhrāntaṃ mattasaṃkupita dvipam</li>
        <li id="R_2.035.016cd">hayaśiñjitanirghoṣaṃ puram āsīn mahāsvanam</li>
    </ul>
    <ul id="R_2.035.017">
        <li id="R_2.035.017ab">tataḥ sabālavṛddhā sā purī paramapīḍitā</li>
        <li id="R_2.035.017cd">rāmam evābhidudrāva gharmārtaḥ salilaṃ yathā</li>
    </ul>
    <ul id="R_2.035.018">
        <li id="R_2.035.018ab">pārśvataḥ pṛṣṭhataś cāpi lambamānās tadunmukhāḥ</li>
        <li id="R_2.035.018cd">bāṣpapūrṇamukhāḥ sarve tam ūcur bhṛśaduḥkhitāḥ</li>
    </ul>
    <ul id="R_2.035.019">
        <li id="R_2.035.019ab">saṃyaccha vājināṃ raśmīn sūta yāhi śanaiḥ śanaiḥ</li>
        <li id="R_2.035.019cd">mukhaṃ drakṣyāmi rāmasya durdarśaṃ no bhaviṣyati</li>
    </ul>
    <ul id="R_2.035.020">
        <li id="R_2.035.020ab">āyasaṃ hṛdayaṃ nūnaṃ rāmamātur asaṃśayam</li>
        <li id="R_2.035.020cd">yad devagarbhapratime vanaṃ yāti na bhidyate</li>
    </ul>
    <ul id="R_2.035.021">
        <li id="R_2.035.021ab">kṛtakṛtyā hi vaidehī chāyevānugatā patim</li>
        <li id="R_2.035.021cd">na jahāti ratā dharme merum arkaprabhā yathā</li>
    </ul>
    <ul id="R_2.035.022">
        <li id="R_2.035.022ab">aho lakṣmaṇa siddhārthaḥ satatāṃ priyavādinam</li>
        <li id="R_2.035.022cd">bhrātaraṃ devasaṃkāśaṃ yas tvaṃ paricariṣyasi</li>
    </ul>
    <ul id="R_2.035.023">
        <li id="R_2.035.023ab">mahaty eṣā hi te siddhir eṣa cābhyudayo mahān</li>
        <li id="R_2.035.023cd">eṣa svargasya mārgaś ca yad enam anugacchasi</li>
        <li id="R_2.035.023ef">evaṃ vadantas te soḍhuṃ na śekur bāṣpam āgatam</li>
    </ul>
    <ul id="R_2.035.024">
        <li id="R_2.035.024ab">atha rājā vṛtaḥ strībhir dīnābhir dīnacetanaḥ</li>
        <li id="R_2.035.024cd">nirjagāma priyaṃ putraṃ drakṣyāmīti bruvan gṛhāt</li>
    </ul>
    <ul id="R_2.035.025">
        <li id="R_2.035.025ab">śuśruve cāgrataḥ strīṇāṃ rudantīnāṃ mahāsvanaḥ</li>
        <li id="R_2.035.025cd">yathā nādaḥ kareṇūnāṃ baddhe mahati kuñjare</li>
    </ul>
    <ul id="R_2.035.026">
        <li id="R_2.035.026ab">pitā ca rājā kākutsthaḥ śrīmān sannas tadā babhau</li>
        <li id="R_2.035.026cd">paripūrṇaḥ śaśī kāle graheṇopapluto yathā</li>
    </ul>
    <ul id="R_2.035.027">
        <li id="R_2.035.027ab">tato halahalāśabdo jajñe rāmasya pṛṣṭhataḥ</li>
        <li id="R_2.035.027cd">narāṇāṃ prekṣya rājānaṃ sīdantaṃ bhṛśaduḥkhitam</li>
    </ul>
    <ul id="R_2.035.028">
        <li id="R_2.035.028ab">hā rāmeti janāḥ ke cid rāmamāteti cāpare</li>
        <li id="R_2.035.028cd">antaḥpuraṃ samṛddhaṃ ca krośantaṃ paryadevayan</li>
    </ul>
    <ul id="R_2.035.029">
        <li id="R_2.035.029ab">anvīkṣamāṇo rāmas tu viṣaṇṇaṃ bhrāntacetasaṃ</li>
        <li id="R_2.035.029cd">rājānaṃ mātaraṃ caiva dadarśānugatau pathi</li>
        <li id="R_2.035.029ef">dharmapāśena saṃkṣiptaḥ prakāśaṃ nābhyudaikṣata</li>
    </ul>
    <ul id="R_2.035.030">
        <li id="R_2.035.030ab">padātinau ca yānārhāv aduḥkhārhau sukhocitau</li>
        <li id="R_2.035.030cd">dṛṣṭvā saṃcodayām āsa śīghraṃ yāhīti sārathim</li>
    </ul>
    <ul id="R_2.035.031">
        <li id="R_2.035.031ab">na hi tat puruṣavyāghro duḥkhadaṃ darśanaṃ pituḥ</li>
        <li id="R_2.035.031cd">mātuś ca sahituṃ śaktas totrārdita iva dvipaḥ</li>
    </ul>
    <ul id="R_2.035.032">
        <li id="R_2.035.032ab">tathā rudantīṃ kausalyāṃ rathaṃ tam anudhāvatīm</li>
        <li id="R_2.035.032cd">krośantīṃ rāma rāmeti hā sīte lakṣmaṇeti ca</li>
        <li id="R_2.035.032ef">asakṛt praikṣata tadā nṛtyantīm iva mātaram</li>
    </ul>
    <ul id="R_2.035.033">
        <li id="R_2.035.033ab">tiṣṭheti rājā cukroṣa yāhi yāhīti rāghavaḥ</li>
        <li id="R_2.035.033cd">sumantrasya babhūvātmā cakrayor iva cāntarā</li>
    </ul>
    <ul id="R_2.035.034">
        <li id="R_2.035.034ab">nāśrauṣam iti rājānam upālabdho 'pi vakṣyasi</li>
        <li id="R_2.035.034cd">ciraṃ duḥkhasya pāpiṣṭham iti rāmas tam abravīt</li>
    </ul>
    <ul id="R_2.035.035">
        <li id="R_2.035.035ab">rāmasya sa vacaḥ kurvann anujñāpya ca taṃ janam</li>
        <li id="R_2.035.035cd">vrajato 'pi hayāñ śīghraṃ codayām āsa sārathiḥ</li>
    </ul>
    <ul id="R_2.035.036">
        <li id="R_2.035.036ab">nyavartata jano rājño rāmaṃ kṛtvā pradakṣiṇam</li>
        <li id="R_2.035.036cd">manasāpy aśruvegaiś ca na nyavartata mānuṣam</li>
    </ul>
    <ul id="R_2.035.037">
        <li id="R_2.035.037ab">yam icchet punar āyāntaṃ nainaṃ dūram anuvrajet</li>
        <li id="R_2.035.037cd">ity amātyā mahārājam ūcur daśarathaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.035.038">
        <li id="R_2.035.038ab">
            <seg type="pāda" n="a">teṣāṃ vacaḥ sarvaguṇopapannaṃ</seg>
            <seg type="pāda" n="b">prasvinnagātraḥ praviṣaṇṇarūpaḥ</seg>
        </li>
        <li id="R_2.035.038cd">
            <seg type="pāda" n="c">niśamya rājā kṛpaṇaḥ sabhāryo</seg>
            <seg type="pāda" n="d">vyavasthitas taṃ sutam īkṣamāṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.036.001">
        <li id="R_2.036.001ab">tasmiṃs tu puruṣavyāghre niṣkrāmati kṛtāñjalau</li>
        <li id="R_2.036.001cd">ārtaśabdo hi saṃjajñe strīṇām antaḥpure mahān</li>
    </ul>
    <ul id="R_2.036.002">
        <li id="R_2.036.002ab">anāthasya janasyāsya durbalasya tapasvinaḥ</li>
        <li id="R_2.036.002cd">yo gatiṃ śaraṇaṃ cāsīt sa nāthaḥ kva nu gacchati</li>
    </ul>
    <ul id="R_2.036.003">
        <li id="R_2.036.003ab">na krudhyaty abhiśasto 'pi krodhanīyāni varjayan</li>
        <li id="R_2.036.003cd">kruddhān prasādayan sarvān samaduḥkhaḥ kva gacchati</li>
    </ul>
    <ul id="R_2.036.004">
        <li id="R_2.036.004ab">kausalyāyāṃ mahātejā yathā mātari vartate</li>
        <li id="R_2.036.004cd">tathā yo vartate 'smāsu mahātmā kva nu gacchati</li>
    </ul>
    <ul id="R_2.036.005">
        <li id="R_2.036.005ab">kaikeyyā kliśyamānena rājñā saṃcodito vanam</li>
        <li id="R_2.036.005cd">paritrātā janasyāsya jagataḥ kva nu gacchati</li>
    </ul>
    <ul id="R_2.036.006">
        <li id="R_2.036.006ab">aho niścetano rājā jīvalokasya saṃpriyam</li>
        <li id="R_2.036.006cd">dharmyaṃ satyavrataṃ rāmaṃ vanavāso pravatsyati</li>
    </ul>
    <ul id="R_2.036.007">
        <li id="R_2.036.007ab">iti sarvā mahiṣyas tā vivatsā iva dhenavaḥ</li>
        <li id="R_2.036.007cd">ruruduś caiva duḥkhārtāḥ sasvaraṃ ca vicukruśuḥ</li>
    </ul>
    <ul id="R_2.036.008">
        <li id="R_2.036.008ab">sa tam antaḥpure ghoram ārtaśabdaṃ mahīpatiḥ</li>
        <li id="R_2.036.008cd">putraśokābhisaṃtaptaḥ śrutvā cāsīt suduḥkhitaḥ</li>
    </ul>
    <ul id="R_2.036.009">
        <li id="R_2.036.009ab">nāgnihotrāṇy ahūyanta sūryaś cāntaradhīyata</li>
        <li id="R_2.036.009cd">vyasṛjan kavalān nāgā gāvo vatsān na pāyayan</li>
    </ul>
    <ul id="R_2.036.010">
        <li id="R_2.036.010ab">triśaṅkur lohitāṅgaś ca bṛhaspatibudhāv api</li>
        <li id="R_2.036.010cd">dāruṇāḥ somam abhyetya grahāḥ sarve vyavasthitāḥ</li>
    </ul>
    <ul id="R_2.036.011">
        <li id="R_2.036.011ab">nakṣatrāṇi gatārcīṃṣi grahāś ca gatatejasaḥ</li>
        <li id="R_2.036.011cd">viśākhāś ca sadhūmāś ca nabhasi pracakāśire</li>
    </ul>
    <ul id="R_2.036.012">
        <li id="R_2.036.012ab">akasmān nāgaraḥ sarvo jano dainyam upāgamat</li>
        <li id="R_2.036.012cd">āhāre vā vihāre vā na kaś cid akaron manaḥ</li>
    </ul>
    <ul id="R_2.036.013">
        <li id="R_2.036.013ab">bāṣpaparyākulamukho rājamārgagato janaḥ</li>
        <li id="R_2.036.013cd">na hṛṣṭo lakṣyate kaś cit sarvaḥ śokaparāyaṇaḥ</li>
    </ul>
    <ul id="R_2.036.014">
        <li id="R_2.036.014ab">na vāti pavanaḥ śīto na śaśī saumyadarśanaḥ</li>
        <li id="R_2.036.014cd">na sūryas tapate lokaṃ sarvaṃ paryākulaṃ jagat</li>
    </ul>
    <ul id="R_2.036.015">
        <li id="R_2.036.015ab">anarthinaḥ sutāḥ strīṇāṃ bhartāro bhrātaras tathā</li>
        <li id="R_2.036.015cd">sarve sarvaṃ parityajya rāmam evānvacintayan</li>
    </ul>
    <ul id="R_2.036.016">
        <li id="R_2.036.016ab">ye tu rāmasya suhṛdaḥ sarve te mūḍhacetasaḥ</li>
        <li id="R_2.036.016cd">śokabhāreṇa cākrāntāḥ śayanaṃ na juhus tadā</li>
    </ul>
    <ul id="R_2.036.017">
        <li id="R_2.036.017ab">
            <seg type="pāda" n="a">tatas tv ayodhyā rahitā mahātmanā</seg>
            <seg type="pāda" n="b">puraṃdareṇeva mahī saparvatā</seg>
        </li>
        <li id="R_2.036.017cd">
            <seg type="pāda" n="c">cacāla ghoraṃ bhayabhārapīḍitā</seg>
            <seg type="pāda" n="d">sanāgayodhāśvagaṇā nanāda ca</seg>
        </li>
    </ul>
    <ul id="R_2.037.001">
        <li id="R_2.037.001ab">yāvat tu niryatas tasya rajorūpam adṛśyata</li>
        <li id="R_2.037.001cd">naivekṣvākuvaras tāvat saṃjahārātmacakṣuṣī</li>
    </ul>
    <ul id="R_2.037.002">
        <li id="R_2.037.002ab">yāvad rājā priyaṃ putraṃ paśyaty atyantadhārmikam</li>
        <li id="R_2.037.002cd">tāvad vyavardhatevāsya dharaṇyāṃ putradarśane</li>
    </ul>
    <ul id="R_2.037.003">
        <li id="R_2.037.003ab">na paśyati rajo 'py asya yadā rāmasya bhūmipaḥ</li>
        <li id="R_2.037.003cd">tadārtaś ca viṣaṇṇaś ca papāta dharaṇītale</li>
    </ul>
    <ul id="R_2.037.004">
        <li id="R_2.037.004ab">tasya dakṣiṇam anvagāt kausalyā bāhum aṅganā</li>
        <li id="R_2.037.004cd">vāmaṃ cāsyānvagāt pārśvaṃ kaikeyī bharatapriyā</li>
    </ul>
    <ul id="R_2.037.005">
        <li id="R_2.037.005ab">tāṃ nayena ca saṃpanno dharmeṇa nivayena ca</li>
        <li id="R_2.037.005cd">uvāca rājā kaikeyīṃ samīkṣya vyathitendriyaḥ</li>
    </ul>
    <ul id="R_2.037.006">
        <li id="R_2.037.006ab">kaikeyi mā mamāṅgāni sprākṣīs tvaṃ duṣṭacāriṇī</li>
        <li id="R_2.037.006cd">na hi tvāṃ draṣṭum icchāmi na bhāryā na ca bāndhavī</li>
    </ul>
    <ul id="R_2.037.007">
        <li id="R_2.037.007ab">ye ca tvām upajīvanti nāhaṃ teṣāṃ na te mama</li>
        <li id="R_2.037.007cd">kevalārthaparāṃ hi tvāṃ tyaktadharmāṃ tyajāmy aham</li>
    </ul>
    <ul id="R_2.037.008">
        <li id="R_2.037.008ab">agṛhṇāṃ yac ca te pāṇim agniṃ paryaṇayaṃ ca yat</li>
        <li id="R_2.037.008cd">anujānāmi tat sarvam asmiṃl loke paratra ca</li>
    </ul>
    <ul id="R_2.037.009">
        <li id="R_2.037.009ab">bharataś cet pratītaḥ syād rājyaṃ prāpyedam avyayam</li>
        <li id="R_2.037.009cd">yan me sa dadyāt pitrarthaṃ mā mā tad dattam āgamat</li>
    </ul>
    <ul id="R_2.037.010">
        <li id="R_2.037.010ab">atha reṇusamudhvastaṃ tam utthāpya narādhipam</li>
        <li id="R_2.037.010cd">nyavartata tadā devī kausalyā śokakarśitā</li>
    </ul>
    <ul id="R_2.037.011">
        <li id="R_2.037.011ab">hatveva brāhmaṇaṃ kāmāt spṛṣṭvāgnim iva pāṇinā</li>
        <li id="R_2.037.011cd">anvatapyata dharmātmā putraṃ saṃcintya tāpasaṃ</li>
    </ul>
    <ul id="R_2.037.012">
        <li id="R_2.037.012ab">nivṛtyaiva nivṛtyaiva sīdato rathavartmasu</li>
        <li id="R_2.037.012cd">rājño nātibabhau rūpaṃ grastasyāṃśumato yathā</li>
    </ul>
    <ul id="R_2.037.013">
        <li id="R_2.037.013ab">vilalāpa ca duḥkhārtaḥ priyaṃ putram anusmaran</li>
        <li id="R_2.037.013cd">nagarāntam anuprāptaṃ buddhvā putram athābravīt</li>
    </ul>
    <ul id="R_2.037.014">
        <li id="R_2.037.014ab">vāhanānāṃ ca mukhyānāṃ vahatāṃ taṃ mamātmajam</li>
        <li id="R_2.037.014cd">padāni pathi dṛśyante sa mahātmā na dṛśyate</li>
    </ul>
    <ul id="R_2.037.015">
        <li id="R_2.037.015ab">sa nūnaṃ kva cid evādya vṛkṣamūlam upāśritaḥ</li>
        <li id="R_2.037.015cd">kāṣṭhaṃ vā yadi vāśmānam upadhāya śayiṣyate</li>
    </ul>
    <ul id="R_2.037.016">
        <li id="R_2.037.016ab">utthāsyati ca medinyāḥ kṛpaṇaḥ pāṃśuguṇṭhitaḥ</li>
        <li id="R_2.037.016cd">viniḥśvasan prasravaṇāt kareṇūnām ivarṣabhaḥ</li>
    </ul>
    <ul id="R_2.037.017">
        <li id="R_2.037.017ab">drakṣyanti nūnaṃ puruṣā dīrghabāhuṃ vanecarāḥ</li>
        <li id="R_2.037.017cd">rāmam utthāya gacchantaṃ lokanātham anāthavat</li>
    </ul>
    <ul id="R_2.037.018">
        <li id="R_2.037.018ab">sakāmā bhava kaikeyi vidhavā rājyam āvasa</li>
        <li id="R_2.037.018cd">na hi taṃ puruṣavyāghraṃ vinā jīvitum utsahe</li>
    </ul>
    <ul id="R_2.037.019">
        <li id="R_2.037.019ab">ity evaṃ vilapan rājā janaughenābhisaṃvṛtaḥ</li>
        <li id="R_2.037.019cd">apasnāta ivāriṣṭaṃ praviveśa purottamam</li>
    </ul>
    <ul id="R_2.037.020">
        <li id="R_2.037.020ab">śūnyacatvaraveśmāntāṃ saṃvṛtāpaṇadevatām</li>
        <li id="R_2.037.020cd">klāntadurbaladuḥkhārtāṃ nātyākīrṇamahāpathām</li>
    </ul>
    <ul id="R_2.037.021">
        <li id="R_2.037.021ab">tām avekṣya purīṃ sarvāṃ rāmam evānucintayan</li>
        <li id="R_2.037.021cd">vilapan prāviśad rājā gṛhaṃ sūrya ivāmbudam</li>
    </ul>
    <ul id="R_2.037.022">
        <li id="R_2.037.022ab">mahāhradam ivākṣobhyaṃ suparṇena hṛtoragam</li>
        <li id="R_2.037.022cd">rāmeṇa rahitaṃ veśma vaidehyā lakṣmaṇena ca</li>
    </ul>
    <ul id="R_2.037.023">
        <li id="R_2.037.023ab">kausalyāyā gṛhaṃ śīghraṃ rāma mātur nayantu mām</li>
        <li id="R_2.037.023cd">iti bruvantaṃ rājānam anayan dvāradarśitaḥ</li>
    </ul>
    <ul id="R_2.037.024">
        <li id="R_2.037.024ab">tatas tatra praviṣṭasya kausalyāyā niveśanam</li>
        <li id="R_2.037.024cd">adhiruhyāpi śayanaṃ babhūva lulitaṃ manaḥ</li>
    </ul>
    <ul id="R_2.037.025">
        <li id="R_2.037.025ab">tac ca dṛṣṭvā mahārājo bhujam udyamya vīryavān</li>
        <li id="R_2.037.025cd">uccaiḥ svareṇa cukrośa hā rāghava jahāsi mām</li>
    </ul>
    <ul id="R_2.037.026">
        <li id="R_2.037.026ab">sukhitā bata taṃ kālaṃ jīviṣyanti narottamāḥ</li>
        <li id="R_2.037.026cd">pariṣvajanto ye rāmaṃ drakṣyanti punar āgatam</li>
    </ul>
    <ul id="R_2.037.027">
        <li id="R_2.037.027ab">na tvāṃ paśyāmi kausalye sādhu māṃ pāṇinā spṛśa</li>
        <li id="R_2.037.027cd">rāmaṃ me 'nugatā dṛṣṭir adyāpi na nivartate</li>
    </ul>
    <ul id="R_2.037.028">
        <li id="R_2.037.028ab">
            <seg type="pāda" n="a">taṃ rāmam evānuvicintayantaṃ</seg>
            <seg type="pāda" n="b">samīkṣya devī śayane narendram</seg>
        </li>
        <li id="R_2.037.028cd">
            <seg type="pāda" n="c">upopaviśyādhikam ārtarūpā</seg>
            <seg type="pāda" n="d">viniḥśvasantī vilalāpa kṛcchraṃ</seg>
        </li>
    </ul>
    <ul id="R_2.038.001">
        <li id="R_2.038.001ab">tataḥ samīkṣya śayane sannaṃ śokena pārthivam</li>
        <li id="R_2.038.001cd">kausalyā putraśokārtā tam uvāca mahīpatim</li>
    </ul>
    <ul id="R_2.038.002">
        <li id="R_2.038.002ab">rāghavo naraśārdūla viṣam uptvā dvijihvavat</li>
        <li id="R_2.038.002cd">vicariṣyati kaikeyī nirmukteva hi pannagī</li>
    </ul>
    <ul id="R_2.038.003">
        <li id="R_2.038.003ab">vivāsya rāmaṃ subhagā labdhakāmā samāhitā</li>
        <li id="R_2.038.003cd">trāsayiṣyati māṃ bhūyo duṣṭāhir iva veśmani</li>
    </ul>
    <ul id="R_2.038.004">
        <li id="R_2.038.004ab">atha sma nagare rāmaś caran bhaikṣaṃ gṛhe vaset</li>
        <li id="R_2.038.004cd">kāmakāro varaṃ dātum api dāsaṃ mamātmajam</li>
    </ul>
    <ul id="R_2.038.005">
        <li id="R_2.038.005ab">pātayitvā tu kaikeyyā rāmaṃ sthānād yatheṣṭataḥ</li>
        <li id="R_2.038.005cd">pradiṣṭo rakṣasāṃ bhāgaḥ parvaṇīvāhitāgninā</li>
    </ul>
    <ul id="R_2.038.006">
        <li id="R_2.038.006ab">gajarājagatir vīro mahābāhur dhanurdharaḥ</li>
        <li id="R_2.038.006cd">vanam āviśate nūnaṃ sabhāryaḥ sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_2.038.007">
        <li id="R_2.038.007ab">vane tv adṛṣṭaduḥkhānāṃ kaikeyyānumate tvayā</li>
        <li id="R_2.038.007cd">tyaktānāṃ vanavāsāya kā nv avasthā bhaviṣyati</li>
    </ul>
    <ul id="R_2.038.008">
        <li id="R_2.038.008ab">te ratnahīnās taruṇāḥ phalakāle vivāsitāḥ</li>
        <li id="R_2.038.008cd">kathaṃ vatsyanti kṛpaṇāḥ phalamūlaiḥ kṛtāśanāḥ</li>
    </ul>
    <ul id="R_2.038.009">
        <li id="R_2.038.009ab">apīdānīṃ sa kālaḥ syān mama śokakṣayaḥ śivaḥ</li>
        <li id="R_2.038.009cd">sabhāryaṃ yat saha bhrātrā paśyeyam iha rāghavam</li>
    </ul>
    <ul id="R_2.038.010">
        <li id="R_2.038.010ab">śrutvaivopasthitau vīrau kadāyodhyā bhaviṣyati</li>
        <li id="R_2.038.010cd">yaśasvinī hṛṣṭajanā sūcchritadhvajamālinī</li>
    </ul>
    <ul id="R_2.038.011">
        <li id="R_2.038.011ab">kadā prekṣya naravyāghrāv araṇyāt punarāgatau</li>
        <li id="R_2.038.011cd">nandiṣyati purī hṛṣṭā samudra iva parvaṇi</li>
    </ul>
    <ul id="R_2.038.012">
        <li id="R_2.038.012ab">kadāyodhyāṃ mahābāhuḥ purīṃ vīraḥ pravekṣyati</li>
        <li id="R_2.038.012cd">puraskṛtya rathe sītāṃ vṛṣabho govadhūm iva</li>
    </ul>
    <ul id="R_2.038.013">
        <li id="R_2.038.013ab">kadā prāṇisahasrāṇi rājamārge mamātmajau</li>
        <li id="R_2.038.013cd">lājair avakariṣyanti praviśantāv ariṃdamau</li>
    </ul>
    <ul id="R_2.038.014">
        <li id="R_2.038.014ab">kadā sumanasaḥ kanyā dvijātīnāṃ phalāni ca</li>
        <li id="R_2.038.014cd">pradiśantyaḥ purīṃ hṛṣṭāḥ kariṣyanti pradakṣiṇam</li>
    </ul>
    <ul id="R_2.038.015">
        <li id="R_2.038.015ab">kadā pariṇato buddhyā vayasā cāmaraprabhaḥ</li>
        <li id="R_2.038.015cd">abhyupaiṣyati dharmajñas trivarṣa iva māṃ lalan</li>
    </ul>
    <ul id="R_2.038.016">
        <li id="R_2.038.016ab">niḥsaṃśayaṃ mayā manye purā vīra kadaryayā</li>
        <li id="R_2.038.016cd">pātu kāmeṣu vatseṣu mātṝṇāṃ śātitāḥ stanāḥ</li>
    </ul>
    <ul id="R_2.038.017">
        <li id="R_2.038.017ab">sāhaṃ gaur iva siṃhena vivatsā vatsalā kṛtā</li>
        <li id="R_2.038.017cd">kaikeyyā puruṣavyāghra bālavatseva gaur balāt</li>
    </ul>
    <ul id="R_2.038.018">
        <li id="R_2.038.018ab">na hi tāvad guṇair juṣṭaṃ sarvaśāstraviśāradam</li>
        <li id="R_2.038.018cd">ekaputrā vinā putram ahaṃ jīvitum utsahe</li>
    </ul>
    <ul id="R_2.038.019">
        <li id="R_2.038.019ab">na hi me jīvite kiṃ cit sāmartham iha kalpyate</li>
        <li id="R_2.038.019cd">apaśyantyāḥ priyaṃ putraṃ mahābāhuṃ mahābalam</li>
    </ul>
    <ul id="R_2.038.020">
        <li id="R_2.038.020ab">
            <seg type="pāda" n="a">ayaṃ hi māṃ dīpayate samutthitas</seg>
            <seg type="pāda" n="b">tanūjaśokaprabhavo hutāśanaḥ</seg>
        </li>
        <li id="R_2.038.020cd">
            <seg type="pāda" n="c">mahīm imāṃ raśmibhir uttamaprabho</seg>
            <seg type="pāda" n="d">yathā nidāghe bhagavān divākaraḥ</seg>
        </li>
    </ul>
    <ul id="R_2.039.001">
        <li id="R_2.039.001ab">vilapantīṃ tathā tāṃ tu kausalyāṃ pramadottamām</li>
        <li id="R_2.039.001cd">idaṃ dharme sthitā dharmyaṃ sumitrā vākyam abravīt</li>
    </ul>
    <ul id="R_2.039.002">
        <li id="R_2.039.002ab">tavārye sadguṇair yuktaḥ putraḥ sa puruṣottamaḥ</li>
        <li id="R_2.039.002cd">kiṃ te vilapitenaivaṃ kṛpaṇaṃ ruditena vā</li>
    </ul>
    <ul id="R_2.039.003">
        <li id="R_2.039.003ab">yas tavārye gataḥ putras tyaktvā rājyaṃ mahābalaḥ</li>
        <li id="R_2.039.003cd">sādhu kurvan mahātmānaṃ pitaraṃ satyavādinām</li>
    </ul>
    <ul id="R_2.039.004">
        <li id="R_2.039.004ab">śiṣṭair ācarite samyak śaśvat pretya phalodaye</li>
        <li id="R_2.039.004cd">rāmo dharme sthitaḥ śreṣṭho na sa śocyaḥ kadā cana</li>
    </ul>
    <ul id="R_2.039.005">
        <li id="R_2.039.005ab">vartate cottamāṃ vṛttiṃ lakṣmaṇo 'smin sadānaghaḥ</li>
        <li id="R_2.039.005cd">dayāvān sarvabhūteṣu lābhas tasya mahātmanaḥ</li>
    </ul>
    <ul id="R_2.039.006">
        <li id="R_2.039.006ab">araṇyavāse yad duḥkhaṃ jānatī vai sukhocitā</li>
        <li id="R_2.039.006cd">anugacchati vaidehī dharmātmānaṃ tavātmajam</li>
    </ul>
    <ul id="R_2.039.007">
        <li id="R_2.039.007ab">kīrtibhūtāṃ patākāṃ yo loke bhrāmayati prabhuḥ</li>
        <li id="R_2.039.007cd">damasatyavrataparaḥ kiṃ na prāptas tavātmajaḥ</li>
    </ul>
    <ul id="R_2.039.008">
        <li id="R_2.039.008ab">vyaktaṃ rāmasya vijñāya śaucaṃ māhātmyam uttamam</li>
        <li id="R_2.039.008cd">na gātram aṃśubhiḥ sūryaḥ saṃtāpayitum arhati</li>
    </ul>
    <ul id="R_2.039.009">
        <li id="R_2.039.009ab">śivaḥ sarveṣu kāleṣu kānanebhyo viniḥsṛtaḥ</li>
        <li id="R_2.039.009cd">rāghavaṃ yuktaśītoṣṇaḥ seviṣyati sukho 'nilaḥ</li>
    </ul>
    <ul id="R_2.039.010">
        <li id="R_2.039.010ab">śayānam anaghaṃ rātrau pitevābhipariṣvajan</li>
        <li id="R_2.039.010cd">raśmibhiḥ saṃspṛśañ śītaiś candramā hlādayiṣyati</li>
    </ul>
    <ul id="R_2.039.011">
        <li id="R_2.039.011ab">dadau cāstrāṇi divyāni yasmai brahmā mahaujase</li>
        <li id="R_2.039.011cd">dānavendraṃ hataṃ dṛṣṭvā timidhvajasutaṃ raṇe</li>
    </ul>
    <ul id="R_2.039.012">
        <li id="R_2.039.012ab">pṛthivyā saha vaidehyā śriyā ca puruṣarṣabhaḥ</li>
        <li id="R_2.039.012cd">kṣipraṃ tisṛbhir etābhiḥ saha rāmo 'bhiṣekṣyate</li>
    </ul>
    <ul id="R_2.039.013">
        <li id="R_2.039.013ab">duḥkhajaṃ visṛjanty asraṃ niṣkrāmantam udīkṣya yam</li>
        <li id="R_2.039.013cd">samutsrakṣyasi netrābhyāṃ kṣipram ānandajaṃ payaḥ</li>
    </ul>
    <ul id="R_2.039.014">
        <li id="R_2.039.014ab">abhivādayamānaṃ taṃ dṛṣṭvā sasuhṛdaṃ sutam</li>
        <li id="R_2.039.014cd">mudāśru mokṣyase kṣipraṃ meghalekeva vārṣikī</li>
    </ul>
    <ul id="R_2.039.015">
        <li id="R_2.039.015ab">putras te varadaḥ kṣipram ayodhyāṃ punar āgataḥ</li>
        <li id="R_2.039.015cd">karābhyāṃ mṛdupīnābhyāṃ caraṇau pīḍayiṣyati</li>
    </ul>
    <ul id="R_2.039.016">
        <li id="R_2.039.016ab">
            <seg type="pāda" n="a">niśamya tal lakṣmaṇamātṛvākyaṃ</seg>
            <seg type="pāda" n="b">rāmasya mātur naradevapatnyāḥ</seg>
        </li>
        <li id="R_2.039.016cd">
            <seg type="pāda" n="c">sadyaḥ śarīre vinanāśa śokaḥ</seg>
            <seg type="pāda" n="d">śaradgato megha ivālpatoyaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.040.001">
        <li id="R_2.040.001ab">anuraktā mahātmānaṃ rāmaṃ satyaparakramam</li>
        <li id="R_2.040.001cd">anujagmuḥ prayāntaṃ taṃ vanavāsāya mānavāḥ</li>
    </ul>
    <ul id="R_2.040.002">
        <li id="R_2.040.002ab">nivartite 'pi ca balāt suhṛdvarge ca rājini</li>
        <li id="R_2.040.002cd">naiva te saṃnyavartanta rāmasyānugatā ratham</li>
    </ul>
    <ul id="R_2.040.003">
        <li id="R_2.040.003ab">ayodhyānilayānāṃ hi puruṣāṇāṃ mahāyaśāḥ</li>
        <li id="R_2.040.003cd">babhūva guṇasaṃpannaḥ pūrṇacandra iva priyaḥ</li>
    </ul>
    <ul id="R_2.040.004">
        <li id="R_2.040.004ab">sa yācyamānaḥ kākutsthaḥ svābhiḥ prakṛtibhis tadā</li>
        <li id="R_2.040.004cd">kurvāṇaḥ pitaraṃ satyaṃ vanam evānvapadyata</li>
    </ul>
    <ul id="R_2.040.005">
        <li id="R_2.040.005ab">avekṣamāṇaḥ sasnehaṃ cakṣuṣā prapibann iva</li>
        <li id="R_2.040.005cd">uvāca rāmaḥ snehena tāḥ prajāḥ svāḥ prajā iva</li>
    </ul>
    <ul id="R_2.040.006">
        <li id="R_2.040.006ab">yā prītir bahumānaś ca mayy ayodhyānivāsinām</li>
        <li id="R_2.040.006cd">matpriyārthaṃ viśeṣeṇa bharate sā niveśyatām</li>
    </ul>
    <ul id="R_2.040.007">
        <li id="R_2.040.007ab">sa hi kalyāṇa cāritraḥ kaikeyyānandavardhanaḥ</li>
        <li id="R_2.040.007cd">kariṣyati yathāvad vaḥ priyāṇi ca hitāni ca</li>
    </ul>
    <ul id="R_2.040.008">
        <li id="R_2.040.008ab">jñānavṛddho vayobālo mṛdur vīryaguṇānvitaḥ</li>
        <li id="R_2.040.008cd">anurūpaḥ sa vo bhartā bhaviṣyati bhayāpahaḥ</li>
    </ul>
    <ul id="R_2.040.009">
        <li id="R_2.040.009ab">sa hi rājaguṇair yukto yuvarājaḥ samīkṣitaḥ</li>
        <li id="R_2.040.009cd">api cāpi mayā śiṣṭaiḥ kāryaṃ vo bhartṛśāsanam</li>
    </ul>
    <ul id="R_2.040.010">
        <li id="R_2.040.010ab">na ca tapyed yathā cāsau vanavāsaṃ gate mayi</li>
        <li id="R_2.040.010cd">mahārājas tathā kāryo mama priyacikīrṣayā</li>
    </ul>
    <ul id="R_2.040.011">
        <li id="R_2.040.011ab">yathā yathā dāśarathir dharmam evāsthito 'bhavat</li>
        <li id="R_2.040.011cd">tathā tathā prakṛtayo rāmaṃ patim akāmayan</li>
    </ul>
    <ul id="R_2.040.012">
        <li id="R_2.040.012ab">bāṣpeṇa pihitaṃ dīnaṃ rāmaḥ saumitriṇā saha</li>
        <li id="R_2.040.012cd">cakarṣeva guṇair baddhvā janaṃ punar ivāsanam</li>
    </ul>
    <ul id="R_2.040.013">
        <li id="R_2.040.013ab">te dvijās trividhaṃ vṛddhā jñānena vayasaujasā</li>
        <li id="R_2.040.013cd">vayaḥprakampaśiraso dūrād ūcur idaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.040.014">
        <li id="R_2.040.014ab">vahanto javanā rāmaṃ bho bho jātyās turaṃgamāḥ</li>
        <li id="R_2.040.014cd">nivartadhvaṃ na gantavyaṃ hitā bhavata bhartari</li>
        <li id="R_2.040.014ef">upavāhyas tu vo bhartā nāpavāhyaḥ purād vanam</li>
    </ul>
    <ul id="R_2.040.015">
        <li id="R_2.040.015ab">evam ārtapralāpāṃs tān vṛddhān pralapato dvijān</li>
        <li id="R_2.040.015cd">avekṣya sahasā rāmo rathād avatatāra ha</li>
    </ul>
    <ul id="R_2.040.016">
        <li id="R_2.040.016ab">padbhyām eva jagāmātha sasītaḥ sahalakṣmaṇaḥ</li>
        <li id="R_2.040.016cd">saṃnikṛṣṭapadanyāso rāmo vanaparāyaṇaḥ</li>
    </ul>
    <ul id="R_2.040.017">
        <li id="R_2.040.017ab">dvijātīṃs tu padātīṃs tān rāmaś cāritravatsalaḥ</li>
        <li id="R_2.040.017cd">na śaśāka ghṛṇācakṣuḥ parimoktuṃ rathena saḥ</li>
    </ul>
    <ul id="R_2.040.018">
        <li id="R_2.040.018ab">gacchantam eva taṃ dṛṣṭvā rāmaṃ saṃbhrāntamānasāḥ</li>
        <li id="R_2.040.018cd">ūcuḥ paramasaṃtaptā rāmaṃ vākyam idaṃ dvijāḥ</li>
    </ul>
    <ul id="R_2.040.019">
        <li id="R_2.040.019ab">brāhmaṇyaṃ kṛtsnam etat tvāṃ brahmaṇyam anugacchati</li>
        <li id="R_2.040.019cd">dvijaskandhādhirūḍhās tvām agnayo 'py anuyānty amī</li>
    </ul>
    <ul id="R_2.040.020">
        <li id="R_2.040.020ab">vājapeyasamutthāni chatrāṇy etāni paśya naḥ</li>
        <li id="R_2.040.020cd">pṛṣṭhato 'nuprayātāni haṃsān iva jalātyaye</li>
    </ul>
    <ul id="R_2.040.021">
        <li id="R_2.040.021ab">anavāptātapatrasya raśmisaṃtāpitasya te</li>
        <li id="R_2.040.021cd">ebhiś chāyāṃ kariṣyāmaḥ svaiś chatrair vājapeyikaiḥ</li>
    </ul>
    <ul id="R_2.040.022">
        <li id="R_2.040.022ab">yā hi naḥ satataṃ buddhir vedamantrānusāriṇī</li>
        <li id="R_2.040.022cd">tvatkṛte sā kṛtā vatsa vanavāsānusāriṇī</li>
    </ul>
    <ul id="R_2.040.023">
        <li id="R_2.040.023ab">hṛdayeṣv avatiṣṭhante vedā ye naḥ paraṃ dhanam</li>
        <li id="R_2.040.023cd">vatsyanty api gṛheṣv eva dārāś cāritrarakṣitāḥ</li>
    </ul>
    <ul id="R_2.040.024">
        <li id="R_2.040.024ab">na punar niścayaḥ kāryas tvadgatau sukṛtā matiḥ</li>
        <li id="R_2.040.024cd">tvayi dharmavyapekṣe tu kiṃ syād dharmam avekṣitum</li>
    </ul>
    <ul id="R_2.040.025">
        <li id="R_2.040.025ab">yācito no nivartasva haṃsaśuklaśiroruhaiḥ</li>
        <li id="R_2.040.025cd">śirobhir nibhṛtācāra mahīpatanapāṃśulaiḥ</li>
    </ul>
    <ul id="R_2.040.026">
        <li id="R_2.040.026ab">bahūnāṃ vitatā yajñā dvijānāṃ ya ihāgatāḥ</li>
        <li id="R_2.040.026cd">teṣāṃ samāptir āyattā tava vatsa nivartane</li>
    </ul>
    <ul id="R_2.040.027">
        <li id="R_2.040.027ab">bhaktimanti hi bhūtāni jaṃgamājaṃgamāni ca</li>
        <li id="R_2.040.027cd">yācamāneṣu teṣu tvaṃ bhaktiṃ bhakteṣu darśaya</li>
    </ul>
    <ul id="R_2.040.028">
        <li id="R_2.040.028ab">anugaṃtum aśaktās tvāṃ mūlair uddhṛtavegibhiḥ</li>
        <li id="R_2.040.028cd">unnatā vāyuvegena vikrośantīva pādapāḥ</li>
    </ul>
    <ul id="R_2.040.029">
        <li id="R_2.040.029ab">niśceṣṭāhārasaṃcārā vṛkṣaikasthānaviṣṭhitāḥ</li>
        <li id="R_2.040.029cd">pakṣiṇo 'pi prayācante sarvabhūtānukampinam</li>
    </ul>
    <ul id="R_2.040.030">
        <li id="R_2.040.030ab">evaṃ vikrośatāṃ teṣāṃ dvijātīnāṃ nivartane</li>
        <li id="R_2.040.030cd">dadṛśe tamasā tatra vārayantīva rāghavam</li>
    </ul>
    <ul id="R_2.041.001">
        <li id="R_2.041.001ab">tatas tu tamasā tīraṃ ramyam āśritya rāghavaḥ</li>
        <li id="R_2.041.001cd">sītām udvīkṣya saumitrim idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.041.002">
        <li id="R_2.041.002ab">iyam adya niśā pūrvā saumitre prasthitā vanam</li>
        <li id="R_2.041.002cd">vanavāsasya bhadraṃ te sa notkaṇṭhitum arhasi</li>
    </ul>
    <ul id="R_2.041.003">
        <li id="R_2.041.003ab">paśya śūnyāny araṇyāni rudantīva samantataḥ</li>
        <li id="R_2.041.003cd">yathānilayam āyadbhir nilīnāni mṛgadvijaiḥ</li>
    </ul>
    <ul id="R_2.041.004">
        <li id="R_2.041.004ab">adyāyodhyā tu nagarī rājadhānī pitur mama</li>
        <li id="R_2.041.004cd">sastrīpuṃsā gatān asmāñ śociṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_2.041.005">
        <li id="R_2.041.005ab">bharataḥ khalu dharmātmā pitaraṃ mātaraṃ ca me</li>
        <li id="R_2.041.005cd">dharmārthakāmasahitair vākyair āśvāsayiṣyati</li>
    </ul>
    <ul id="R_2.041.006">
        <li id="R_2.041.006ab">bharatasyānṛśaṃsatvaṃ saṃcintyāhaṃ punaḥ punaḥ</li>
        <li id="R_2.041.006cd">nānuśocāmi pitaraṃ mātaraṃ cāpi lakṣmaṇa</li>
    </ul>
    <ul id="R_2.041.007">
        <li id="R_2.041.007ab">tvayā kāryaṃ naravyāghra mām anuvrajatā kṛtam</li>
        <li id="R_2.041.007cd">anveṣṭavyā hi vaidehyā rakṣaṇārthe sahāyatā</li>
    </ul>
    <ul id="R_2.041.008">
        <li id="R_2.041.008ab">adbhir eva tu saumitre vatsyāmy adya niśām imām</li>
        <li id="R_2.041.008cd">etad dhi rocate mahyaṃ vanye 'pi vividhe sati</li>
    </ul>
    <ul id="R_2.041.009">
        <li id="R_2.041.009ab">evam uktvā tu saumitraṃ sumantram api rāghavaḥ</li>
        <li id="R_2.041.009cd">apramattas tvam aśveṣu bhava saumyety uvāca ha</li>
    </ul>
    <ul id="R_2.041.010">
        <li id="R_2.041.010ab">so 'śvān sumantraḥ saṃyamya sūrye 'staṃ samupāgate</li>
        <li id="R_2.041.010cd">prabhūtayavasān kṛtvā babhūva pratyanantaraḥ</li>
    </ul>
    <ul id="R_2.041.011">
        <li id="R_2.041.011ab">upāsyatu śivāṃ saṃdhyāṃ dṛṣṭvā rātrim upasthitām</li>
        <li id="R_2.041.011cd">rāmasya śayanaṃ cakre sūtaḥ saumitriṇā saha</li>
    </ul>
    <ul id="R_2.041.012">
        <li id="R_2.041.012ab">tāṃ śayyāṃ tamasātīre vīkṣya vṛkṣadalaiḥ kṛtām</li>
        <li id="R_2.041.012cd">rāmaḥ saumitriṇāṃ sārdhaṃ sabhāryaḥ saṃviveśa ha</li>
    </ul>
    <ul id="R_2.041.013">
        <li id="R_2.041.013ab">sabhāryaṃ saṃprasuptaṃ taṃ bhrātaraṃ vīkṣya lakṣmaṇaḥ</li>
        <li id="R_2.041.013cd">kathayām āsa sūtāya rāmasya vividhān guṇān</li>
    </ul>
    <ul id="R_2.041.014">
        <li id="R_2.041.014ab">jāgrato hy eva tāṃ rātriṃ saumitrer udito raviḥ</li>
        <li id="R_2.041.014cd">sūtasya tamasātīre rāmasya bruvato guṇān</li>
    </ul>
    <ul id="R_2.041.015">
        <li id="R_2.041.015ab">gokulākulatīrāyās tamasāyā vidūrataḥ</li>
        <li id="R_2.041.015cd">avasat tatra tāṃ rātriṃ rāmaḥ prakṛtibhiḥ saha</li>
    </ul>
    <ul id="R_2.041.016">
        <li id="R_2.041.016ab">utthāya tu mahātejāḥ prakṛtīs tā niśāmya ca</li>
        <li id="R_2.041.016cd">abravīd bhrātaraṃ rāmo lakṣmaṇaṃ puṇyalakṣaṇam</li>
    </ul>
    <ul id="R_2.041.017">
        <li id="R_2.041.017ab">asmadvyapekṣān saumitre nirapekṣān gṛheṣv api</li>
        <li id="R_2.041.017cd">vṛkṣamūleṣu saṃsuptān paśya lakṣmaṇa sāmpratam</li>
    </ul>
    <ul id="R_2.041.018">
        <li id="R_2.041.018ab">yathaite niyamaṃ paurāḥ kurvanty asmannivartane</li>
        <li id="R_2.041.018cd">api prāṇān asiṣyanti na tu tyakṣyanti niścayam</li>
    </ul>
    <ul id="R_2.041.019">
        <li id="R_2.041.019ab">yāvad eva tu saṃsuptās tāvad eva vayaṃ laghu</li>
        <li id="R_2.041.019cd">ratham āruhya gacchāmaḥ panthānam akutobhayam</li>
    </ul>
    <ul id="R_2.041.020">
        <li id="R_2.041.020ab">ato bhūyo 'pi nedānīm ikṣvākupuravāsinaḥ</li>
        <li id="R_2.041.020cd">svapeyur anuraktā māṃ vṛkṣamūlāni saṃśritāḥ</li>
    </ul>
    <ul id="R_2.041.021">
        <li id="R_2.041.021ab">paurā hy ātmakṛtād duḥkhād vipramocyā nṛpātmajaiḥ</li>
        <li id="R_2.041.021cd">na tu khalv ātmanā yojyā duḥkhena puravāsinaḥ</li>
    </ul>
    <ul id="R_2.041.022">
        <li id="R_2.041.022ab">abravīl lakṣmaṇo rāmaṃ sākṣād dharmam iva sthitam</li>
        <li id="R_2.041.022cd">rocate me mahāprājña kṣipram āruhyatām iti</li>
    </ul>
    <ul id="R_2.041.023">
        <li id="R_2.041.023ab">sūtas tataḥ saṃtvaritaḥ syandanaṃ tair hayottamaiḥ</li>
        <li id="R_2.041.023cd">yojayitvātha rāmāya prāñjaliḥ pratyavedayat</li>
    </ul>
    <ul id="R_2.041.024">
        <li id="R_2.041.024ab">mohanārthaṃ tu paurāṇāṃ sūtaṃ rāmo 'bravīd vacaḥ</li>
        <li id="R_2.041.024cd">udaṅmukhaḥ prayāhi tvaṃ ratham āsthāya sārathe</li>
    </ul>
    <ul id="R_2.041.025">
        <li id="R_2.041.025ab">muhūrtaṃ tvaritaṃ gatvā nirgataya rathaṃ punaḥ</li>
        <li id="R_2.041.025cd">yathā na vidyuḥ paurā māṃ tathā kuru samāhitaḥ</li>
    </ul>
    <ul id="R_2.041.026">
        <li id="R_2.041.026ab">rāmasya vacanaṃ śrutvā tathā cakre sa sārathiḥ</li>
        <li id="R_2.041.026cd">pratyāgamya ca rāmasya syandanaṃ pratyavedayat</li>
    </ul>
    <ul id="R_2.041.027">
        <li id="R_2.041.027ab">taṃ syandanam adhiṣṭhāya rāghavaḥ saparicchadaḥ</li>
        <li id="R_2.041.027cd">śīghragām ākulāvartāṃ tamasām ataran nadīm</li>
    </ul>
    <ul id="R_2.041.028">
        <li id="R_2.041.028ab">sa saṃtīrya mahābāhuḥ śrīmāñ śivam akaṇṭakam</li>
        <li id="R_2.041.028cd">prāpadyata mahāmārgam abhayaṃ bhayadarśinām</li>
    </ul>
    <ul id="R_2.041.029">
        <li id="R_2.041.029ab">prabhātāyāṃ tu śarvaryāṃ paurās te rāghavo vinā</li>
        <li id="R_2.041.029cd">śokopahataniśceṣṭā babhūvur hatacetasaḥ</li>
    </ul>
    <ul id="R_2.041.030">
        <li id="R_2.041.030ab">śokajāśruparidyūnā vīkṣamāṇās tatas tataḥ</li>
        <li id="R_2.041.030cd">ālokam api rāmasya na paśyanti sma duḥkhitāḥ</li>
    </ul>
    <ul id="R_2.041.031">
        <li id="R_2.041.031ab">tato mārgānusāreṇa gatvā kiṃ cit kṣaṇaṃ punaḥ</li>
        <li id="R_2.041.031cd">mārganāśād viṣādena mahatā samabhiplutaḥ</li>
    </ul>
    <ul id="R_2.041.032">
        <li id="R_2.041.032ab">rathasya mārganāśena nyavartanta manasvinaḥ</li>
        <li id="R_2.041.032cd">kim idaṃ kiṃ kariṣyāmo daivenopahatā iti</li>
    </ul>
    <ul id="R_2.041.033">
        <li id="R_2.041.033ab">tato yathāgatenaiva mārgeṇa klāntacetasaḥ</li>
        <li id="R_2.041.033cd">ayodhyām agaman sarve purīṃ vyathitasajjanām</li>
    </ul>
    <ul id="R_2.042.001">
        <li id="R_2.042.001ab">anugamya nivṛttānāṃ rāmaṃ nagaravāsinām</li>
        <li id="R_2.042.001cd">udgatānīva sattvāni babhūvur amanasvinām</li>
    </ul>
    <ul id="R_2.042.002">
        <li id="R_2.042.002ab">svaṃ svaṃ nilayam āgamya putradāraiḥ samāvṛtāḥ</li>
        <li id="R_2.042.002cd">aśrūṇi mumucuḥ sarve bāṣpeṇa pihitānanāḥ</li>
    </ul>
    <ul id="R_2.042.003">
        <li id="R_2.042.003ab">na cāhṛṣyan na cāmodan vaṇijo na prasārayan</li>
        <li id="R_2.042.003cd">na cāśobhanta paṇyāni nāpacan gṛhamedhinaḥ</li>
    </ul>
    <ul id="R_2.042.004">
        <li id="R_2.042.004ab">naṣṭaṃ dṛṣṭvā nābhyanandan vipulaṃ vā dhanāgamam</li>
        <li id="R_2.042.004cd">putraṃ prathamajaṃ labdhvā jananī nābhyanandata</li>
    </ul>
    <ul id="R_2.042.005">
        <li id="R_2.042.005ab">gṛhe gṛhe rudantyaś ca bhartāraṃ gṛham āgatam</li>
        <li id="R_2.042.005cd">vyagarhayanto duḥkhārtā vāgbhis totrair iva dvipān</li>
    </ul>
    <ul id="R_2.042.006">
        <li id="R_2.042.006ab">kiṃ nu teṣāṃ gṛhaiḥ kāryaṃ kiṃ dāraiḥ kiṃ dhanena vā</li>
        <li id="R_2.042.006cd">putrair vā kiṃ sukhair vāpi ye na paśyanti rāghavam</li>
    </ul>
    <ul id="R_2.042.007">
        <li id="R_2.042.007ab">ekaḥ satpuruṣo loke lakṣmaṇaḥ saha sītayā</li>
        <li id="R_2.042.007cd">yo 'nugacchati kākutsthaṃ rāmaṃ paricaran vane</li>
    </ul>
    <ul id="R_2.042.008">
        <li id="R_2.042.008ab">āpagāḥ kṛtapuṇyās tāḥ padminyaś ca sarāṃsi ca</li>
        <li id="R_2.042.008cd">yeṣu snāsyati kākutstho vigāhya salilaṃ śuci</li>
    </ul>
    <ul id="R_2.042.009">
        <li id="R_2.042.009ab">śobhayiṣyanti kākutstham aṭavyo ramyakānanāḥ</li>
        <li id="R_2.042.009cd">āpagāś ca mahānūpāḥ sānumantaś ca parvatāḥ</li>
    </ul>
    <ul id="R_2.042.010">
        <li id="R_2.042.010ab">kānanaṃ vāpi śailaṃ vā yaṃ rāmo 'bhigamiṣyati</li>
        <li id="R_2.042.010cd">priyātithim iva prāptaṃ nainaṃ śakṣyanty anarcitum</li>
    </ul>
    <ul id="R_2.042.011">
        <li id="R_2.042.011ab">vicitrakusumāpīḍā bahumañjaridhāriṇaḥ</li>
        <li id="R_2.042.011cd">akāle cāpi mukhyāni puṣpāṇi ca phalāni ca</li>
        <li id="R_2.042.011ef">darśayiṣyanty anukrośād girayo rāmam āgatam</li>
    </ul>
    <ul id="R_2.042.012">
        <li id="R_2.042.012ab">vidarśayanto vividhān bhūyaś citrāṃś ca nirjharān</li>
        <li id="R_2.042.012cd">pādapāḥ parvatāgreṣu ramayiṣyanti rāghavam</li>
    </ul>
    <ul id="R_2.042.013">
        <li id="R_2.042.013ab">yatra rāmo bhayaṃ nātra nāsti tatra parābhavaḥ</li>
        <li id="R_2.042.013cd">sa hi śūro mahābāhuḥ putro daśarathasya ca</li>
    </ul>
    <ul id="R_2.042.014">
        <li id="R_2.042.014ab">purā bhavati no dūrād anugacchāma rāghavam</li>
        <li id="R_2.042.014cd">pādacchāyā sukhā bhartus tādṛśasya mahātmanaḥ</li>
        <li id="R_2.042.014ef">sa hi nātho janasyāsya sa gatiḥ sa parāyaṇam</li>
    </ul>
    <ul id="R_2.042.015">
        <li id="R_2.042.015ab">vayaṃ paricariṣyāmaḥ sītāṃ yūyaṃ tu rāghavam</li>
        <li id="R_2.042.015cd">iti paurastriyo bhartṝn duḥkhārtās tat tad abruvan</li>
    </ul>
    <ul id="R_2.042.016">
        <li id="R_2.042.016ab">yuṣmākaṃ rāghavo 'raṇye yogakṣemaṃ vidhāsyati</li>
        <li id="R_2.042.016cd">sītā nārījanasyāsya yogakṣemaṃ kariṣyati</li>
    </ul>
    <ul id="R_2.042.017">
        <li id="R_2.042.017ab">ko nv anenāpratītena sotkaṇṭhitajanena ca</li>
        <li id="R_2.042.017cd">saṃprīyetāmanojñena vāsena hṛtacetasā</li>
    </ul>
    <ul id="R_2.042.018">
        <li id="R_2.042.018ab">kaikeyyā yadi ced rājyaṃ syād adharmyam anāthavat</li>
        <li id="R_2.042.018cd">na hi no jīvitenārthaḥ kutaḥ putraiḥ kuto dhanaiḥ</li>
    </ul>
    <ul id="R_2.042.019">
        <li id="R_2.042.019ab">yayā putraś ca bhartā ca tyaktāv aiśvaryakāraṇāt</li>
        <li id="R_2.042.019cd">kaṃ sā parihared anyaṃ kaikeyī kulapāṃsanī</li>
    </ul>
    <ul id="R_2.042.020">
        <li id="R_2.042.020ab">kaikeyyā na vayaṃ rājye bhṛtakā nivasemahi</li>
        <li id="R_2.042.020cd">jīvantyā jātu jīvantyaḥ putrair api śapāmahe</li>
    </ul>
    <ul id="R_2.042.021">
        <li id="R_2.042.021ab">yā putraṃ pārthivendrasya pravāsayati nirghṛṇā</li>
        <li id="R_2.042.021cd">kas tāṃ prāpya sukhaṃ jīved adharmyāṃ duṣṭacāriṇīm</li>
    </ul>
    <ul id="R_2.042.022">
        <li id="R_2.042.022ab">na hi pravrajite rāme jīviṣyati mahīpatiḥ</li>
        <li id="R_2.042.022cd">mṛte daśarathe vyaktaṃ vilopas tadanantaram</li>
    </ul>
    <ul id="R_2.042.023">
        <li id="R_2.042.023ab">te viṣaṃ pibatāloḍya kṣīṇapuṇyāḥ sudurgatāḥ</li>
        <li id="R_2.042.023cd">rāghavaṃ vānugacchadhvam aśrutiṃ vāpi gacchata</li>
    </ul>
    <ul id="R_2.042.024">
        <li id="R_2.042.024ab">mithyā pravrājito rāmaḥ sabhāryaḥ sahalakṣmaṇaḥ</li>
        <li id="R_2.042.024cd">bharate saṃniṣṛṣṭāḥ smaḥ saunike paśavo yathā</li>
    </ul>
    <ul id="R_2.042.025">
        <li id="R_2.042.025ab">tās tathā vilapantyas tu nagare nāgarastriyaḥ</li>
        <li id="R_2.042.025cd">cukruśur bhṛśasaṃtaptā mṛtyor iva bhayāgame</li>
    </ul>
    <ul id="R_2.042.026">
        <li id="R_2.042.026ab">
            <seg type="pāda" n="a">tathā striyo rāmanimittam āturā</seg>
            <seg type="pāda" n="b">yathā sute bhrātari vā vivāsite</seg>
        </li>
        <li id="R_2.042.026cd">
            <seg type="pāda" n="c">vilapya dīnā rurudur vicetasaḥ</seg>
            <seg type="pāda" n="d">sutair hi tāsām adhiko hi so 'bhavat</seg>
        </li>
    </ul>
    <ul id="R_2.043.001">
        <li id="R_2.043.001ab">rāmo 'pi rātriśeṣeṇa tenaiva mahad antaram</li>
        <li id="R_2.043.001cd">jagāma puruṣavyāghraḥ pitur ājñām anusmaran</li>
    </ul>
    <ul id="R_2.043.002">
        <li id="R_2.043.002ab">tathaiva gacchatas tasya vyapāyād rajanī śivā</li>
        <li id="R_2.043.002cd">upāsya sa śivāṃ saṃdhyāṃ viṣayāntaṃ vyagāhata</li>
    </ul>
    <ul id="R_2.043.003">
        <li id="R_2.043.003ab">grāmān vikṛṣṭasīmāṃs tān puṣpitāni vanāni ca</li>
        <li id="R_2.043.003cd">paśyann atiyayau śīghraṃ śarair iva hayottamaiḥ</li>
    </ul>
    <ul id="R_2.043.004">
        <li id="R_2.043.004ab">śṛṇvan vāco manuṣyāṇāṃ grāmasaṃvāsavāsinām</li>
        <li id="R_2.043.004cd">rājānaṃ dhig daśarathaṃ kāmasya vaśam āgatam</li>
    </ul>
    <ul id="R_2.043.005">
        <li id="R_2.043.005ab">hā nṛśaṃsādya kaikeyī pāpā pāpānubandhinī</li>
        <li id="R_2.043.005cd">tīkṣṇā saṃbhinnamaryādā tīkṣṇe karmaṇi vartate</li>
    </ul>
    <ul id="R_2.043.006">
        <li id="R_2.043.006ab">yā putram īdṛśaṃ rājñaḥ pravāsayati dhārmikam</li>
        <li id="R_2.043.006cd">vana vāse mahāprājñaṃ sānukrośam atandritam</li>
    </ul>
    <ul id="R_2.043.007">
        <li id="R_2.043.007ab">etā vāco manuṣyāṇāṃ grāmasaṃvāsavāsinām</li>
        <li id="R_2.043.007cd">śṛṇvann atiyayau vīraḥ kosalān kosaleśvaraḥ</li>
    </ul>
    <ul id="R_2.043.008">
        <li id="R_2.043.008ab">tato vedaśrutiṃ nāma śivavārivahāṃ nadīm</li>
        <li id="R_2.043.008cd">uttīryābhimukhaḥ prāyād agastyādhyuṣitāṃ diśam</li>
    </ul>
    <ul id="R_2.043.009">
        <li id="R_2.043.009ab">gatvā tu suciraṃ kālaṃ tataḥ śītajalāṃ nadīm</li>
        <li id="R_2.043.009cd">gomatīṃ goyutānūpām atarat sāgaraṃgamām</li>
    </ul>
    <ul id="R_2.043.010">
        <li id="R_2.043.010ab">gomatīṃ cāpy atikramya rāghavaḥ śīghragair hayaiḥ</li>
        <li id="R_2.043.010cd">mayūrahaṃsābhirutāṃ tatāra syandikāṃ nadīm</li>
    </ul>
    <ul id="R_2.043.011">
        <li id="R_2.043.011ab">sa mahīṃ manunā rājñā dattām ikṣvākave purā</li>
        <li id="R_2.043.011cd">sphītāṃ rāṣṭrāvṛtāṃ rāmo vaidehīm anvadarśayat</li>
    </ul>
    <ul id="R_2.043.012">
        <li id="R_2.043.012ab">sūta ity eva cābhāṣya sārathiṃ tam abhīkṣṇaśaḥ</li>
        <li id="R_2.043.012cd">haṃsamattasvaraḥ śrīmān uvāca puruṣarṣabhaḥ</li>
    </ul>
    <ul id="R_2.043.013">
        <li id="R_2.043.013ab">kadāhaṃ punar āgamya sarayvāḥ puṣpite vane</li>
        <li id="R_2.043.013cd">mṛgayāṃ paryāṭaṣyāmi mātrā pitrā ca saṃgataḥ</li>
    </ul>
    <ul id="R_2.043.014">
        <li id="R_2.043.014ab">nātyartham abhikāṅkṣāmi mṛgayāṃ sarayūvane</li>
        <li id="R_2.043.014cd">ratir hy eṣātulā loke rājarṣigaṇasaṃmatā</li>
    </ul>
    <ul id="R_2.043.015">
        <li id="R_2.043.015ab">sa tam adhvānam aikṣvākaḥ sūtaṃ madhurayā girā</li>
        <li id="R_2.043.015cd">taṃ tam artham abhipretya yayauvākyam udīrayan</li>
    </ul>
    <ul id="R_2.044.001">
        <li id="R_2.044.001ab">viśālān kosalān ramyān yātvā lakṣmaṇapūrvajaḥ</li>
        <li id="R_2.044.001cd">āsasāda mahābāhuḥ śṛṅgaverapuraṃ prati</li>
    </ul>
    <ul id="R_2.044.002">
        <li id="R_2.044.002ab">tatra tripathagāṃ divyāṃ śivatoyām aśaivalām</li>
        <li id="R_2.044.002cd">dadarśa rāghavo gaṅgāṃ puṇyām ṛṣinisevitām</li>
    </ul>
    <ul id="R_2.044.003">
        <li id="R_2.044.003ab">haṃsasārasasaṃghuṣṭāṃ cakravākopakūjitām</li>
        <li id="R_2.044.003cd">śiṃśumaraiś ca nakraiś ca bhujaṃgaiś ca niṣevitām</li>
    </ul>
    <ul id="R_2.044.004">
        <li id="R_2.044.004ab">tām ūrmikalilāvartām anvavekṣya mahārathaḥ</li>
        <li id="R_2.044.004cd">sumantram abravīt sūtam ihaivādya vasāmahe</li>
    </ul>
    <ul id="R_2.044.005">
        <li id="R_2.044.005ab">avidūrād ayaṃ nadyā bahupuṣpapravālavān</li>
        <li id="R_2.044.005cd">sumahān iṅgudīvṛkṣo vasāmo 'traiva sārathe</li>
    </ul>
    <ul id="R_2.044.006">
        <li id="R_2.044.006ab">lakṣaṇaś ca sumantraś ca bāḍham ity eva rāghavam</li>
        <li id="R_2.044.006cd">uktvā tam iṅgudīvṛkṣaṃ tadopayayatur hayaiḥ</li>
    </ul>
    <ul id="R_2.044.007">
        <li id="R_2.044.007ab">rāmo 'bhiyāya taṃ ramyaṃ vṛkṣam ikṣvākunandanaḥ</li>
        <li id="R_2.044.007cd">rathād avātarat tasmāt sabhāryaḥ sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_2.044.008">
        <li id="R_2.044.008ab">sumantro 'py avatīryaiva mocayitvā hayottamān</li>
        <li id="R_2.044.008cd">vṛkṣamūlagataṃ rāmam upatasthe kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_2.044.009">
        <li id="R_2.044.009ab">tatra rājā guho nāma rāmasyātmasamaḥ sakhā</li>
        <li id="R_2.044.009cd">niṣādajātyo balavān sthapatiś ceti viśrutaḥ</li>
    </ul>
    <ul id="R_2.044.010">
        <li id="R_2.044.010ab">sa śrutvā puruṣavyāghraṃ rāmaṃ viṣayam āgatam</li>
        <li id="R_2.044.010cd">vṛddhaiḥ parivṛto 'mātyair jñātibhiś cāpy upāgataḥ</li>
    </ul>
    <ul id="R_2.044.011">
        <li id="R_2.044.011ab">tato niṣādādhipatiṃ dṛṣṭvā dūrād avasthitam</li>
        <li id="R_2.044.011cd">saha saumitriṇā rāmaḥ samāgacchad guhena saḥ</li>
    </ul>
    <ul id="R_2.044.012">
        <li id="R_2.044.012ab">tam ārtaḥ saṃpariṣvajya guho rāghavam abravīt</li>
        <li id="R_2.044.012cd">yathāyodhyā tathedaṃ te rāma kiṃ karavāṇi te</li>
    </ul>
    <ul id="R_2.044.013">
        <li id="R_2.044.013ab">tato guṇavadannādyam upādāya pṛthagvidham</li>
        <li id="R_2.044.013cd">arghyaṃ copānayat kṣipraṃ vākyaṃ cedam uvāca ha</li>
    </ul>
    <ul id="R_2.044.014">
        <li id="R_2.044.014ab">svāgataṃ te mahābāho taveyam akhilā mahī</li>
        <li id="R_2.044.014cd">vayaṃ preṣyā bhavān bhartā sādhu rājyaṃ praśādhi naḥ</li>
    </ul>
    <ul id="R_2.044.015">
        <li id="R_2.044.015ab">bhakṣyaṃ bhojyaṃ ca peyaṃ ca lehyaṃ cedam upasthitam</li>
        <li id="R_2.044.015cd">śayanāni ca mukhyāni vājināṃ khādanaṃ ca te</li>
    </ul>
    <ul id="R_2.044.016">
        <li id="R_2.044.016ab">guham eva bruvāṇaṃ taṃ rāghavaḥ pratyuvāca ha</li>
        <li id="R_2.044.016cd">arcitāś caiva hṛṣṭāś ca bhavatā sarvathā vayam</li>
    </ul>
    <ul id="R_2.044.017">
        <li id="R_2.044.017ab">padbhyām abhigamāc caiva snehasaṃdarśanena ca</li>
        <li id="R_2.044.017cd">bhujābhyāṃ sādhuvṛttābhyāṃ pīḍayan vākyam abravīt</li>
    </ul>
    <ul id="R_2.044.018">
        <li id="R_2.044.018ab">diṣṭyā tvāṃ guha paśyāmi arogaṃ saha bāndhavaiḥ</li>
        <li id="R_2.044.018cd">api te kūśalaṃ rāṣṭre mitreṣu ca dhaneṣu ca</li>
    </ul>
    <ul id="R_2.044.019">
        <li id="R_2.044.019ab">yat tv idaṃ bhavatā kiṃ cit prītyā samupakalpitam</li>
        <li id="R_2.044.019cd">sarvaṃ tad anujānāmi na hi varte pratigrahe</li>
    </ul>
    <ul id="R_2.044.020">
        <li id="R_2.044.020ab">kuśacīrājinadharaṃ phalamūlāśanaṃ ca mām</li>
        <li id="R_2.044.020cd">viddhi praṇihitaṃ dharme tāpasaṃ vanagocaram</li>
    </ul>
    <ul id="R_2.044.021">
        <li id="R_2.044.021ab">aśvānāṃ khādanenāham arthī nānyena kena cit</li>
        <li id="R_2.044.021cd">etāvatātrabhavatā bhaviṣyāmi supūjitaḥ</li>
    </ul>
    <ul id="R_2.044.022">
        <li id="R_2.044.022ab">ete hi dayitā rājñaḥ pitur daśarathasya me</li>
        <li id="R_2.044.022cd">etaiḥ suvihitair aśvair bhaviṣyāmy aham arcitaḥ</li>
    </ul>
    <ul id="R_2.044.023">
        <li id="R_2.044.023ab">aśvānāṃ pratipānaṃ ca khādanaṃ caiva so 'nvaśāt</li>
        <li id="R_2.044.023cd">guhas tatraiva puruṣāṃs tvaritaṃ dīyatām iti</li>
    </ul>
    <ul id="R_2.044.024">
        <li id="R_2.044.024ab">tataś cīrottarāsaṅgaḥ saṃdhyām anvāsya paścimām</li>
        <li id="R_2.044.024cd">jalam evādade bhojyaṃ lakṣmaṇenāhṛtaṃ svayam</li>
    </ul>
    <ul id="R_2.044.025">
        <li id="R_2.044.025ab">tasya bhūmau śayānasya pādau prakṣālya lakṣmaṇaḥ</li>
        <li id="R_2.044.025cd">sabhāryasya tato 'bhyetya tasthau vṛkṣam upāśritaḥ</li>
    </ul>
    <ul id="R_2.044.026">
        <li id="R_2.044.026ab">guho 'pi saha sūtena saumitrim anubhāṣayan</li>
        <li id="R_2.044.026cd">anvajāgrat tato rāmam apramatto dhanurdharaḥ</li>
    </ul>
    <ul id="R_2.044.027">
        <li id="R_2.044.027ab">
            <seg type="pāda" n="a">tathā śayānasya tato 'sya dhīmato</seg>
            <seg type="pāda" n="b">yaśasvino dāśarather mahātmanaḥ</seg>
        </li>
        <li id="R_2.044.027cd">
            <seg type="pāda" n="c">adṛṣṭaduḥkhasya sukhocitasya sā</seg>
            <seg type="pāda" n="d">tadā vyatīyāya cireṇa śarvarī</seg>
        </li>
    </ul>
    <ul id="R_2.045.001">
        <li id="R_2.045.001ab">taṃ jāgratam adambhena bhrātur arthāya lakṣmaṇam</li>
        <li id="R_2.045.001cd">guhaḥ saṃtāpasaṃtapto rāghavaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_2.045.002">
        <li id="R_2.045.002ab">iyaṃ tāta sukhā śayyā tvadartham upakalpitā</li>
        <li id="R_2.045.002cd">pratyāśvasihi sādhv asyāṃ rājaputra yathāsukham</li>
    </ul>
    <ul id="R_2.045.003">
        <li id="R_2.045.003ab">ucito 'yaṃ janaḥ sarvaḥ kleśānāṃ tvaṃ sukhocitaḥ</li>
        <li id="R_2.045.003cd">guptyarthaṃ jāgariṣyāmaḥ kākutsthasya vayaṃ niśām</li>
    </ul>
    <ul id="R_2.045.004">
        <li id="R_2.045.004ab">na hi rāmāt priyataro mamāsti bhuvi kaś cana</li>
        <li id="R_2.045.004cd">bravīmy etad ahaṃ satyaṃ satyenaiva ca te śape</li>
    </ul>
    <ul id="R_2.045.005">
        <li id="R_2.045.005ab">asya prasādād āśaṃse loke 'smin sumahad yaśaḥ</li>
        <li id="R_2.045.005cd">dharmāvāptiṃ ca vipulām arthāvāptiṃ ca kevalām</li>
    </ul>
    <ul id="R_2.045.006">
        <li id="R_2.045.006ab">so 'haṃ priyasakhaṃ rāmaṃ śayānaṃ saha sītayā</li>
        <li id="R_2.045.006cd">rakṣiṣyāmi dhanuṣpāṇiḥ sarvato jñātibhiḥ saha</li>
    </ul>
    <ul id="R_2.045.007">
        <li id="R_2.045.007ab">na hi me 'viditaṃ kiṃ cid vane 'smiṃś carataḥ sadā</li>
        <li id="R_2.045.007cd">caturaṅgaṃ hy api balaṃ sumahat prasahemahi</li>
    </ul>
    <ul id="R_2.045.008">
        <li id="R_2.045.008ab">lakṣmaṇas taṃ tadovāca rakṣyamāṇās tvayānagha</li>
        <li id="R_2.045.008cd">nātra bhītā vayaṃ sarve dharmam evānupaśyatā</li>
    </ul>
    <ul id="R_2.045.009">
        <li id="R_2.045.009ab">kathaṃ dāśarathau bhūmau śayāne saha sītayā</li>
        <li id="R_2.045.009cd">śakyā nidrā mayā labdhuṃ jīvitaṃ vā sukhāni vā</li>
    </ul>
    <ul id="R_2.045.010">
        <li id="R_2.045.010ab">yo na devāsuraiḥ sarvaiḥ śakyaḥ prasahituṃ yudhi</li>
        <li id="R_2.045.010cd">taṃ paśya sukhasaṃviṣṭaṃ tṛṇeṣu saha sītayā</li>
    </ul>
    <ul id="R_2.045.011">
        <li id="R_2.045.011ab">yo mantra tapasā labdho vividhaiś ca pariśramaiḥ</li>
        <li id="R_2.045.011cd">eko daśarathasyaiṣa putraḥ sadṛśalakṣaṇaḥ</li>
    </ul>
    <ul id="R_2.045.012">
        <li id="R_2.045.012ab">asmin pravrajito rājā na ciraṃ vartayiṣyati</li>
        <li id="R_2.045.012cd">vidhavā medinī nūnaṃ kṣipram eva bhaviṣyati</li>
    </ul>
    <ul id="R_2.045.013">
        <li id="R_2.045.013ab">vinadya sumahānādaṃ śrameṇoparatāḥ striyaḥ</li>
        <li id="R_2.045.013cd">nirghoṣoparataṃ tāta manye rājaniveśanam</li>
    </ul>
    <ul id="R_2.045.014">
        <li id="R_2.045.014ab">kausalyā caiva rājā ca tathaiva jananī mama</li>
        <li id="R_2.045.014cd">nāśaṃse yadi jīvanti sarve te śarvarīm imām</li>
    </ul>
    <ul id="R_2.045.015">
        <li id="R_2.045.015ab">jīved api hi me mātā śatrughnasyānvavekṣayā</li>
        <li id="R_2.045.015cd">tad duḥkhaṃ yat tu kausalyā vīrasūr vinaśiṣyati</li>
    </ul>
    <ul id="R_2.045.016">
        <li id="R_2.045.016ab">anuraktajanākīrṇā sukhālokapriyāvahā</li>
        <li id="R_2.045.016cd">rājavyasanasaṃsṛṣṭā sā purī vinaśiṣyati</li>
    </ul>
    <ul id="R_2.045.017">
        <li id="R_2.045.017ab">atikrāntam atikrāntam anavāpya manoratham</li>
        <li id="R_2.045.017cd">rājye rāmam anikṣipya pitā me vinaśiṣyati</li>
    </ul>
    <ul id="R_2.045.018">
        <li id="R_2.045.018ab">siddhārthāḥ pitaraṃ vṛttaṃ tasmin kāle hy upasthite</li>
        <li id="R_2.045.018cd">pretakāryeṣu sarveṣu saṃskariṣyanti bhūmipam</li>
    </ul>
    <ul id="R_2.045.019">
        <li id="R_2.045.019ab">ramyacatvarasaṃsthānāṃ suvibhaktamahāpathām</li>
        <li id="R_2.045.019cd">harmyaprāsādasaṃpannāṃ gaṇikāvaraśobhitām</li>
    </ul>
    <ul id="R_2.045.020">
        <li id="R_2.045.020ab">rathāśvagajasaṃbādhāṃ tūryanādavināditām</li>
        <li id="R_2.045.020cd">sarvakalyāṇasaṃpūrṇāṃ hṛṣṭapuṣṭajanākulām</li>
    </ul>
    <ul id="R_2.045.021">
        <li id="R_2.045.021ab">ārāmodyānasaṃpannāṃ samājotsavaśālinīm</li>
        <li id="R_2.045.021cd">sukhitā vicariṣyanti rājadhānīṃ pitur mama</li>
    </ul>
    <ul id="R_2.045.022">
        <li id="R_2.045.022ab">api satyapratijñena sārdhaṃ kuśalinā vayam</li>
        <li id="R_2.045.022cd">nivṛtte vanavāse 'sminn ayodhyāṃ praviśemahi</li>
    </ul>
    <ul id="R_2.045.023">
        <li id="R_2.045.023ab">paridevayamānasya duḥkhārtasya mahātmanaḥ</li>
        <li id="R_2.045.023cd">tiṣṭhato rājaputrasya śarvarī sātyavartata</li>
    </ul>
    <ul id="R_2.045.024">
        <li id="R_2.045.024ab">
            <seg type="pāda" n="a">tathā hi satyaṃ bruvati prajāhite</seg>
            <seg type="pāda" n="b">narendraputre gurusauhṛdād guhaḥ</seg>
        </li>
        <li id="R_2.045.024cd">
            <seg type="pāda" n="c">mumoca bāṣpaṃ vyasanābhipīḍito</seg>
            <seg type="pāda" n="d">jvarāturo nāga iva vyathāturaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.046.001">
        <li id="R_2.046.001ab">prabhātāyāṃ tu śarvaryāṃ pṛthu vṛkṣā mahāyaśāḥ</li>
        <li id="R_2.046.001cd">uvāca rāmaḥ saumitriṃ lakṣmaṇaṃ śubhalakṣaṇam</li>
    </ul>
    <ul id="R_2.046.002">
        <li id="R_2.046.002ab">bhāskarodayakālo 'yaṃ gatā bhagavatī niśā</li>
        <li id="R_2.046.002cd">asau sukṛṣṇo vihagaḥ kokilas tāta kūjati</li>
    </ul>
    <ul id="R_2.046.003">
        <li id="R_2.046.003ab">barhiṇānāṃ ca nirghoṣaḥ śrūyate nadatāṃ vane</li>
        <li id="R_2.046.003cd">tarāma jāhnavīṃ saumya śīghragāṃ sāgaraṃgamām</li>
    </ul>
    <ul id="R_2.046.004">
        <li id="R_2.046.004ab">vijñāya rāmasya vacaḥ saumitrir mitranandanaḥ</li>
        <li id="R_2.046.004cd">guham āmantrya sūtaṃ ca so 'tiṣṭhad bhrātur agrataḥ</li>
    </ul>
    <ul id="R_2.046.005">
        <li id="R_2.046.005ab">tataḥ kalāpān saṃnahya khaḍgau baddhvā ca dhanvinau</li>
        <li id="R_2.046.005cd">jagmatur yena tau gaṅgāṃ sītayā saha rāghavau</li>
    </ul>
    <ul id="R_2.046.006">
        <li id="R_2.046.006ab">rāmam eva tu dharmajñam upagamya vinītavat</li>
        <li id="R_2.046.006cd">kim ahaṃ karavāṇīti sūtaḥ prāñjalir abravīt</li>
    </ul>
    <ul id="R_2.046.007">
        <li id="R_2.046.007ab">nivartasvety uvācainam etāvad dhi kṛtaṃ mama</li>
        <li id="R_2.046.007cd">yānaṃ vihāya padbhyāṃ tu gamiṣyāmo mahāvanam</li>
    </ul>
    <ul id="R_2.046.008">
        <li id="R_2.046.008ab">ātmānaṃ tv abhyanujñātam avekṣyārtaḥ sa sārathiḥ</li>
        <li id="R_2.046.008cd">sumantraḥ puruṣavyāghram aikṣvākam idam abravīt</li>
    </ul>
    <ul id="R_2.046.009">
        <li id="R_2.046.009ab">nātikrāntam idaṃ loke puruṣeṇeha kena cit</li>
        <li id="R_2.046.009cd">tava sabhrātṛbhāryasya vāsaḥ prākṛtavad vane</li>
    </ul>
    <ul id="R_2.046.010">
        <li id="R_2.046.010ab">na manye brahmacarye 'sti svadhīte vā phalodayaḥ</li>
        <li id="R_2.046.010cd">mārdavārjavayor vāpi tvāṃ ced vyasanam āgatam</li>
    </ul>
    <ul id="R_2.046.011">
        <li id="R_2.046.011ab">saha rāghava vaidehyā bhrātrā caiva vane vasan</li>
        <li id="R_2.046.011cd">tvaṃ gatiṃ prāpsyase vīra trīṃl lokāṃs tu jayann iva</li>
    </ul>
    <ul id="R_2.046.012">
        <li id="R_2.046.012ab">vayaṃ khalu hatā rāma ye tayāpy upavañcitāḥ</li>
        <li id="R_2.046.012cd">kaikeyyā vaśam eṣyāmaḥ pāpāyā duḥkhabhāginaḥ</li>
    </ul>
    <ul id="R_2.046.013">
        <li id="R_2.046.013ab">iti bruvann ātma samaṃ sumantraḥ sārathis tadā</li>
        <li id="R_2.046.013cd">dṛṣṭvā dura gataṃ rāmaṃ duḥkhārto rurude ciram</li>
    </ul>
    <ul id="R_2.046.014">
        <li id="R_2.046.014ab">tatas tu vigate bāṣpe sūtaṃ spṛṣṭodakaṃ śucim</li>
        <li id="R_2.046.014cd">rāmas tu madhuraṃ vākyaṃ punaḥ punar uvāca tam</li>
    </ul>
    <ul id="R_2.046.015">
        <li id="R_2.046.015ab">ikṣvākūṇāṃ tvayā tulyaṃ suhṛdaṃ nopalakṣaye</li>
        <li id="R_2.046.015cd">yathā daśaratho rājā māṃ na śocet tathā kuru</li>
    </ul>
    <ul id="R_2.046.016">
        <li id="R_2.046.016ab">śokopahata cetāś ca vṛddhaś ca jagatīpatiḥ</li>
        <li id="R_2.046.016cd">kāma bhārāvasannaś ca tasmād etad bravīmi te</li>
    </ul>
    <ul id="R_2.046.017">
        <li id="R_2.046.017ab">yad yad ājñāpayet kiṃ cit sa mahātmā mahīpatiḥ</li>
        <li id="R_2.046.017cd">kaikeyyāḥ priyakāmārthaṃ kāryaṃ tad avikāṅkṣayā</li>
    </ul>
    <ul id="R_2.046.018">
        <li id="R_2.046.018ab">etadarthaṃ hi rājyāni praśāsati nareśvarāḥ</li>
        <li id="R_2.046.018cd">yad eṣāṃ sarvakṛtyeṣu mano na pratihanyate</li>
    </ul>
    <ul id="R_2.046.019">
        <li id="R_2.046.019ab">tad yathā sa mahārājo nālīkam adhigacchati</li>
        <li id="R_2.046.019cd">na ca tāmyati duḥkhena sumantra kuru tat tathā</li>
    </ul>
    <ul id="R_2.046.020">
        <li id="R_2.046.020ab">adṛṣṭaduḥkhaṃ rājānaṃ vṛddham āryaṃ jitendriyam</li>
        <li id="R_2.046.020cd">brūyās tvam abhivādyaiva mama hetor idaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.046.021">
        <li id="R_2.046.021ab">naivāham anuśocāmi lakṣmaṇo na ca maithilī</li>
        <li id="R_2.046.021cd">ayodhyāyāś cyutāś ceti vane vatsyāmaheti vā</li>
    </ul>
    <ul id="R_2.046.022">
        <li id="R_2.046.022ab">caturdaśasu varṣeṣu nivṛtteṣu punaḥ punaḥ</li>
        <li id="R_2.046.022cd">lakṣmaṇaṃ māṃ ca sītāṃ ca drakṣyasi kṣipram āgatān</li>
    </ul>
    <ul id="R_2.046.023">
        <li id="R_2.046.023ab">evam uktvā tu rājānaṃ mātaraṃ ca sumantra me</li>
        <li id="R_2.046.023cd">anyāś ca devīḥ sahitāḥ kaikeyīṃ ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_2.046.024">
        <li id="R_2.046.024ab">ārogyaṃ brūhi kausalyām atha pādābhivandanam</li>
        <li id="R_2.046.024cd">sītāyā mama cāryasya vacanāl lakṣmaṇasya ca</li>
    </ul>
    <ul id="R_2.046.025">
        <li id="R_2.046.025ab">brūyāś ca hi mahārājaṃ bharataṃ kṣipram ānaya</li>
        <li id="R_2.046.025cd">āgataś cāpi bharataḥ sthāpyo nṛpamate pade</li>
    </ul>
    <ul id="R_2.046.026">
        <li id="R_2.046.026ab">bharataṃ ca pariṣvajya yauvarājye 'bhiṣicya ca</li>
        <li id="R_2.046.026cd">asmatsaṃtāpajaṃ duḥkhaṃ na tvām abhibhaviṣyati</li>
    </ul>
    <ul id="R_2.046.027">
        <li id="R_2.046.027ab">bharataś cāpi vaktavyo yathā rājani vartase</li>
        <li id="R_2.046.027cd">tathā mātṛṣu vartethāḥ sarvāsv evāviśeṣataḥ</li>
    </ul>
    <ul id="R_2.046.028">
        <li id="R_2.046.028ab">yathā ca tava kaikeyī sumitrā cāviśeṣataḥ</li>
        <li id="R_2.046.028cd">tathaiva devī kausalyā mama mātā viśeṣataḥ</li>
    </ul>
    <ul id="R_2.046.029">
        <li id="R_2.046.029ab">nivartyamāno rāmeṇa sumantraḥ śokakarśitaḥ</li>
        <li id="R_2.046.029cd">tat sarvaṃ vacanaṃ śrutvā snehāt kākutstham abravīt</li>
    </ul>
    <ul id="R_2.046.030">
        <li id="R_2.046.030ab">yad ahaṃ nopacāreṇa brūyāṃ snehād aviklavaḥ</li>
        <li id="R_2.046.030cd">bhaktimān iti tat tāvad vākyaṃ tvaṃ kṣantum arhasi</li>
    </ul>
    <ul id="R_2.046.031">
        <li id="R_2.046.031ab">kathaṃ hi tvadvihīno 'haṃ pratiyāsyāmi tāṃ purīm</li>
        <li id="R_2.046.031cd">tava tāta viyogena putraśokākulām iva</li>
    </ul>
    <ul id="R_2.046.032">
        <li id="R_2.046.032ab">sarāmam api tāvan me rathaṃ dṛṣṭvā tadā janaḥ</li>
        <li id="R_2.046.032cd">vinā rāmaṃ rathaṃ dṛṣṭvā vidīryetāpi sā purī</li>
    </ul>
    <ul id="R_2.046.033">
        <li id="R_2.046.033ab">dainyaṃ hi nagarī gacched dṛṣṭvā śūnyam imaṃ ratham</li>
        <li id="R_2.046.033cd">sūtāvaśeṣaṃ svaṃ sainyaṃ hatavīram ivāhave</li>
    </ul>
    <ul id="R_2.046.034">
        <li id="R_2.046.034ab">dūre 'pi nivasantaṃ tvāṃ mānasenāgrataḥ sthitam</li>
        <li id="R_2.046.034cd">cintayantyo 'dya nūnaṃ tvāṃ nirāhārāḥ kṛtāḥ prajāḥ</li>
    </ul>
    <ul id="R_2.046.035">
        <li id="R_2.046.035ab">ārtanādo hi yaḥ paurair muktas tadvipravāsane</li>
        <li id="R_2.046.035cd">rathasthaṃ māṃ niśāmyaiva kuryuḥ śataguṇaṃ tataḥ</li>
    </ul>
    <ul id="R_2.046.036">
        <li id="R_2.046.036ab">ahaṃ kiṃ cāpi vakṣyāmi devīṃ tava suto mayā</li>
        <li id="R_2.046.036cd">nīto 'sau mātulakulaṃ saṃtāpaṃ mā kṛthā iti</li>
    </ul>
    <ul id="R_2.046.037">
        <li id="R_2.046.037ab">asatyam api naivāhaṃ brūyāṃ vacanam īdṛśam</li>
        <li id="R_2.046.037cd">katham apriyam evāhaṃ brūyāṃ satyam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.046.038">
        <li id="R_2.046.038ab">mama tāvan niyogasthās tvadbandhujanavāhinaḥ</li>
        <li id="R_2.046.038cd">kathaṃ rathaṃ tvayā hīnaṃ pravakṣyanti hayottamāḥ</li>
    </ul>
    <ul id="R_2.046.039">
        <li id="R_2.046.039ab">yadi me yācamānasya tyāgam eva kariṣyasi</li>
        <li id="R_2.046.039cd">saratho 'gniṃ pravekṣyāmi tyakta mātra iha tvayā</li>
    </ul>
    <ul id="R_2.046.040">
        <li id="R_2.046.040ab">bhaviṣyanti vane yāni tapovighnakarāṇi te</li>
        <li id="R_2.046.040cd">rathena pratibādhiṣye tāni sattvāni rāghava</li>
    </ul>
    <ul id="R_2.046.041">
        <li id="R_2.046.041ab">tat kṛtena mayā prāptaṃ ratha caryā kṛtaṃ sukham</li>
        <li id="R_2.046.041cd">āśaṃse tvatkṛtenāhaṃ vanavāsakṛtaṃ sukham</li>
    </ul>
    <ul id="R_2.046.042">
        <li id="R_2.046.042ab">prasīdecchāmi te 'raṇye bhavituṃ pratyanantaraḥ</li>
        <li id="R_2.046.042cd">prītyābhihitam icchāmi bhava me patyanantaraḥ</li>
    </ul>
    <ul id="R_2.046.043">
        <li id="R_2.046.043ab">tava śuśrūṣaṇaṃ mūrdhnā kariṣyāmi vane vasan</li>
        <li id="R_2.046.043cd">ayodhyāṃ devalokaṃ vā sarvathā prajahāmy aham</li>
    </ul>
    <ul id="R_2.046.044">
        <li id="R_2.046.044ab">na hi śakyā praveṣṭuṃ sā mayāyodhyā tvayā vinā</li>
        <li id="R_2.046.044cd">rājadhānī mahendrasya yathā duṣkṛtakarmaṇā</li>
    </ul>
    <ul id="R_2.046.045">
        <li id="R_2.046.045ab">ime cāpi hayā vīra yadi te vanavāsinaḥ</li>
        <li id="R_2.046.045cd">paricaryāṃ kariṣyanti prāpsyanti paramāṃ gatim</li>
    </ul>
    <ul id="R_2.046.046">
        <li id="R_2.046.046ab">vanavāse kṣayaṃ prāpte mamaiṣa hi manorathaḥ</li>
        <li id="R_2.046.046cd">yad anena rathenaiva tvāṃ vaheyaṃ purīṃ punaḥ</li>
    </ul>
    <ul id="R_2.046.047">
        <li id="R_2.046.047ab">caturdaśa hi varṣāṇi sahitasya tvayā vane</li>
        <li id="R_2.046.047cd">kṣaṇabhūtāni yāsyanti śataśas tu tato 'nyathā</li>
    </ul>
    <ul id="R_2.046.048">
        <li id="R_2.046.048ab">bhṛtyavatsala tiṣṭhantaṃ bhartṛputragate pathi</li>
        <li id="R_2.046.048cd">bhaktaṃ bhṛtyaṃ sthitaṃ sthityāṃ tvaṃ na māṃ hātum arhasi</li>
    </ul>
    <ul id="R_2.046.049">
        <li id="R_2.046.049ab">evaṃ bahuvidhaṃ dīnaṃ yācamānaṃ punaḥ punaḥ</li>
        <li id="R_2.046.049cd">rāmo bhṛtyānukampī tu sumantram idam abravīt</li>
    </ul>
    <ul id="R_2.046.050">
        <li id="R_2.046.050ab">jānāmi paramāṃ bhaktiṃ mayi te bhartṛvatsala</li>
        <li id="R_2.046.050cd">śṛṇu cāpi yadarthaṃ tvāṃ preṣayāmi purīm itaḥ</li>
    </ul>
    <ul id="R_2.046.051">
        <li id="R_2.046.051ab">nagarīṃ tvāṃ gataṃ dṛṣṭvā jananī me yavīyasī</li>
        <li id="R_2.046.051cd">kaikeyī pratyayaṃ gacched iti rāmo vanaṃ gataḥ</li>
    </ul>
    <ul id="R_2.046.052">
        <li id="R_2.046.052ab">parituṣṭā hi sā devi vanavāsaṃ gate mayi</li>
        <li id="R_2.046.052cd">rājānaṃ nātiśaṅketa mithyāvādīti dhārmikam</li>
    </ul>
    <ul id="R_2.046.053">
        <li id="R_2.046.053ab">eṣa me prathamaḥ kalpo yad ambā me yavīyasī</li>
        <li id="R_2.046.053cd">bharatārakṣitaṃ sphītaṃ putrarājyam avāpnuyāt</li>
    </ul>
    <ul id="R_2.046.054">
        <li id="R_2.046.054ab">mama priyārthaṃ rājñaś ca sarathas tvaṃ purīṃ vraja</li>
        <li id="R_2.046.054cd">saṃdiṣṭaś cāsi yān arthāṃs tāṃs tān brūyās tathātathā</li>
    </ul>
    <ul id="R_2.046.055">
        <li id="R_2.046.055ab">ity uktvā vacanaṃ sūtaṃ sāntvayitvā punaḥ punaḥ</li>
        <li id="R_2.046.055cd">guhaṃ vacanam aklībaṃ rāmo hetumad abravīt</li>
        <li id="R_2.046.055ef">jaṭāḥ kṛtvā gamiṣyāmi nyagrodhakṣīram ānaya</li>
    </ul>
    <ul id="R_2.046.056">
        <li id="R_2.046.056ab">tat kṣīraṃ rājaputrāya guhaḥ kṣipram upāharat</li>
        <li id="R_2.046.056cd">lakṣmaṇasyātmanaś caiva rāmas tenākaroj jaṭāḥ</li>
    </ul>
    <ul id="R_2.046.057">
        <li id="R_2.046.057ab">tau tadā cīravasanau jaṭāmaṇḍaladhāriṇau</li>
        <li id="R_2.046.057cd">aśobhetām ṛṣisamau bhrātarau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_2.046.058">
        <li id="R_2.046.058ab">tato vaikhānasaṃ mārgam āsthitaḥ sahalakṣmaṇaḥ</li>
        <li id="R_2.046.058cd">vratam ādiṣṭavān rāmaḥ sahāyaṃ guham abravīt</li>
    </ul>
    <ul id="R_2.046.059">
        <li id="R_2.046.059ab">apramatto bale kośe durge janapade tathā</li>
        <li id="R_2.046.059cd">bhavethā guha rājyaṃ hi durārakṣatamaṃ matam</li>
    </ul>
    <ul id="R_2.046.060">
        <li id="R_2.046.060ab">tatas taṃ samanujñāya guham ikṣvākunandanaḥ</li>
        <li id="R_2.046.060cd">jagāma tūrṇam avyagraḥ sabhāryaḥ sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_2.046.061">
        <li id="R_2.046.061ab">sa tu dṛṣṭvā nadītīre nāvam ikṣvākunandanaḥ</li>
        <li id="R_2.046.061cd">titīrṣuḥ śīghragāṃ gaṅgām idaṃ lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_2.046.062">
        <li id="R_2.046.062ab">āroha tvaṃ nara vyāghra sthitāṃ nāvam imāṃ śanaiḥ</li>
        <li id="R_2.046.062cd">sītāṃ cāropayānvakṣaṃ parigṛhya manasvinīm</li>
    </ul>
    <ul id="R_2.046.063">
        <li id="R_2.046.063ab">sa bhrātuḥ śāsanaṃ śrutvā sarvam apratikūlayan</li>
        <li id="R_2.046.063cd">āropya maithilīṃ pūrvam ārurohātmavāṃs tataḥ</li>
    </ul>
    <ul id="R_2.046.064">
        <li id="R_2.046.064ab">athāruroha tejasvī svayaṃ lakṣmaṇapūrvajaḥ</li>
        <li id="R_2.046.064cd">tato niṣādādhipatir guho jñātīn acodayat</li>
    </ul>
    <ul id="R_2.046.065">
        <li id="R_2.046.065ab">anujñāya sumantraṃ ca sabalaṃ caiva taṃ guham</li>
        <li id="R_2.046.065cd">āsthāya nāvaṃ rāmas tu codayām āsa nāvikān</li>
    </ul>
    <ul id="R_2.046.066">
        <li id="R_2.046.066ab">tatas taiś coditā sā nauḥ karṇadhārasamāhitā</li>
        <li id="R_2.046.066cd">śubhasphyavegābhihatā śīghraṃ salilam atyagāt</li>
    </ul>
    <ul id="R_2.046.067">
        <li id="R_2.046.067ab">madhyaṃ tu samanuprāpya bhāgīrathyās tv aninditā</li>
        <li id="R_2.046.067cd">vaidehī prāñjalir bhūtvā tāṃ nadīm idam abravīt</li>
    </ul>
    <ul id="R_2.046.068">
        <li id="R_2.046.068ab">putro daśarathasyāyaṃ mahārājasya dhīmataḥ</li>
        <li id="R_2.046.068cd">nideśaṃ pālayatv enaṃ gaṅge tvadabhirakṣitaḥ</li>
    </ul>
    <ul id="R_2.046.069">
        <li id="R_2.046.069ab">caturdaśa hi varṣāṇi samagrāṇy uṣya kānane</li>
        <li id="R_2.046.069cd">bhrātrā saha mayā caiva punaḥ pratyāgamiṣyati</li>
    </ul>
    <ul id="R_2.046.070">
        <li id="R_2.046.070ab">tatas tvāṃ devi subhage kṣemeṇa punar āgatā</li>
        <li id="R_2.046.070cd">yakṣye pramuditā gaṅge sarvakāmasamṛddhaye</li>
    </ul>
    <ul id="R_2.046.071">
        <li id="R_2.046.071ab">tvaṃ hi tripathagā devi brahma lokaṃ samīkṣase</li>
        <li id="R_2.046.071cd">bhāryā codadhirājasya loke 'smin saṃpradṛśyase</li>
    </ul>
    <ul id="R_2.046.072">
        <li id="R_2.046.072ab">sā tvāṃ devi namasyāmi praśaṃsāmi ca śobhane</li>
        <li id="R_2.046.072cd">prāpta rājye naravyāghra śivena punar āgate</li>
    </ul>
    <ul id="R_2.046.073">
        <li id="R_2.046.073ab">gavāṃ śatasahasrāṇi vastrāṇy annaṃ ca peśalam</li>
        <li id="R_2.046.073cd">brāhmaṇebhyaḥ pradāsyāmi tava priyacikīrṣayā</li>
    </ul>
    <ul id="R_2.046.074">
        <li id="R_2.046.074ab">tathā saṃbhāṣamāṇā sā sītā gaṅgām aninditā</li>
        <li id="R_2.046.074cd">dakṣiṇā dakṣiṇaṃ tīraṃ kṣipram evābhyupāgamat</li>
    </ul>
    <ul id="R_2.046.075">
        <li id="R_2.046.075ab">tīraṃ tu samanuprāpya nāvaṃ hitvā nararṣabhaḥ</li>
        <li id="R_2.046.075cd">prātiṣṭhata saha bhrātrā vaidehyā ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_2.046.076">
        <li id="R_2.046.076ab">athābravīn mahābāhuḥ sumitrānandavardhanam</li>
        <li id="R_2.046.076cd">agrato gaccha saumitre sītā tvām anugacchatu</li>
    </ul>
    <ul id="R_2.046.077">
        <li id="R_2.046.077ab">pṛṣṭhato 'haṃ gamiṣyāmi tvāṃ ca sītāṃ ca pālayan</li>
        <li id="R_2.046.077cd">adya duḥkhaṃ tu vaidehī vanavāsasya vetsyati</li>
    </ul>
    <ul id="R_2.046.078">
        <li id="R_2.046.078ab">
            <seg type="pāda" n="a">gataṃ tu gaṅgāparapāram āśu</seg>
            <seg type="pāda" n="b">rāmaṃ sumantraḥ pratataṃ nirīkṣya</seg>
        </li>
        <li id="R_2.046.078cd">
            <seg type="pāda" n="c">adhvaprakarṣād vinivṛttadṛṣṭir</seg>
            <seg type="pāda" n="d">mumoca bāṣpaṃ vyathitas tapasvī</seg>
        </li>
    </ul>
    <ul id="R_2.046.079">
        <li id="R_2.046.079ab">
            <seg type="pāda" n="a">tau tatra hatvā caturo mahāmṛgān</seg>
            <seg type="pāda" n="b">varāham ṛśyaṃ pṛṣataṃ mahārurum</seg>
        </li>
        <li id="R_2.046.079cd">
            <seg type="pāda" n="c">ādāya medhyaṃ tvaritaṃ bubhukṣitau</seg>
            <seg type="pāda" n="d">vāsāya kāle yayatur vanaspatim</seg>
        </li>
    </ul>
    <ul id="R_2.047.001">
        <li id="R_2.047.001ab">sa taṃ vṛkṣaṃ samāsādya saṃdhyām anvāsya paścimām</li>
        <li id="R_2.047.001cd">rāmo ramayatāṃ śreṣṭha iti hovāca lakṣmaṇam</li>
    </ul>
    <ul id="R_2.047.002">
        <li id="R_2.047.002ab">adyeyaṃ prathamā rātrir yātā janapadād bahiḥ</li>
        <li id="R_2.047.002cd">yā sumantreṇa rahitā tāṃ notkaṇṭhitum arhasi</li>
    </ul>
    <ul id="R_2.047.003">
        <li id="R_2.047.003ab">jāgartavyam atandribhyām adya prabhṛti rātriṣu</li>
        <li id="R_2.047.003cd">yogakṣemo hi sītāyā vartate lakṣmaṇāvayoḥ</li>
    </ul>
    <ul id="R_2.047.004">
        <li id="R_2.047.004ab">rātriṃ kathaṃ cid evemāṃ saumitre vartayāmahe</li>
        <li id="R_2.047.004cd">upāvartāmahe bhūmāv āstīrya svayam ārjitaiḥ</li>
    </ul>
    <ul id="R_2.047.005">
        <li id="R_2.047.005ab">sa tu saṃviśya medinyāṃ mahārhaśayanocitaḥ</li>
        <li id="R_2.047.005cd">imāḥ saumitraye rāmo vyājahāra kathāḥ śubhāḥ</li>
    </ul>
    <ul id="R_2.047.006">
        <li id="R_2.047.006ab">dhruvam adya mahārājo duḥkhaṃ svapiti lakṣmaṇa</li>
        <li id="R_2.047.006cd">kṛtakāmā tu kaikeyī tuṣṭā bhavitum arhati</li>
    </ul>
    <ul id="R_2.047.007">
        <li id="R_2.047.007ab">sā hi devī mahārājaṃ kaikeyī rājyakāraṇāt</li>
        <li id="R_2.047.007cd">api na cyāvayet prāṇān dṛṣṭvā bharatam āgatam</li>
    </ul>
    <ul id="R_2.047.008">
        <li id="R_2.047.008ab">anāthaś caiva vṛddhaś ca mayā caiva vinākṛtaḥ</li>
        <li id="R_2.047.008cd">kiṃ kariṣyati kāmātmā kaikeyyā vaśam āgataḥ</li>
    </ul>
    <ul id="R_2.047.009">
        <li id="R_2.047.009ab">idaṃ vyasanam ālokya rājñaś ca mativibhramam</li>
        <li id="R_2.047.009cd">kāma evārdhadharmābhyāṃ garīyān iti me matiḥ</li>
    </ul>
    <ul id="R_2.047.010">
        <li id="R_2.047.010ab">ko hy avidvān api pumān pramadāyāḥ kṛte tyajet</li>
        <li id="R_2.047.010cd">chandānuvartinaṃ putraṃ tāto mām iva lakṣmaṇa</li>
    </ul>
    <ul id="R_2.047.011">
        <li id="R_2.047.011ab">sukhī bata sabhāryaś ca bharataḥ kekayīsutaḥ</li>
        <li id="R_2.047.011cd">muditān kosalān eko yo bhokṣyaty adhirājavat</li>
    </ul>
    <ul id="R_2.047.012">
        <li id="R_2.047.012ab">sa hi sarvasya rājyasya mukham ekaṃ bhaviṣyati</li>
        <li id="R_2.047.012cd">tāte ca vayasātīte mayi cāraṇyam āśrite</li>
    </ul>
    <ul id="R_2.047.013">
        <li id="R_2.047.013ab">arthadharmau parityajya yaḥ kāmam anuvartate</li>
        <li id="R_2.047.013cd">evam āpadyate kṣipraṃ rājā daśaratho yathā</li>
    </ul>
    <ul id="R_2.047.014">
        <li id="R_2.047.014ab">manye daśarathāntāya mama pravrājanāya ca</li>
        <li id="R_2.047.014cd">kaikeyī saumya saṃprāptā rājyāya bharatasya ca</li>
    </ul>
    <ul id="R_2.047.015">
        <li id="R_2.047.015ab">apīdānīṃ na kaikeyī saubhāgyamadamohitā</li>
        <li id="R_2.047.015cd">kausalyāṃ ca sumitrāṃ ca saṃprabādheta matkṛte</li>
    </ul>
    <ul id="R_2.047.016">
        <li id="R_2.047.016ab">mā sma matkāraṇād devī sumitrā duḥkham āvaset</li>
        <li id="R_2.047.016cd">ayodhyām ita eva tvaṃ kāle praviśa lakṣmaṇa</li>
    </ul>
    <ul id="R_2.047.017">
        <li id="R_2.047.017ab">aham eko gamiṣyāmi sītayā saha daṇḍakān</li>
        <li id="R_2.047.017cd">anāthāyā hi nāthas tvaṃ kausalyāyā bhaviṣyasi</li>
    </ul>
    <ul id="R_2.047.018">
        <li id="R_2.047.018ab">kṣudrakarmā hi kaikeyī dveṣād anyāyyam ācaret</li>
        <li id="R_2.047.018cd">paridadyā hi dharmajñe bharate mama mātaram</li>
    </ul>
    <ul id="R_2.047.019">
        <li id="R_2.047.019ab">nūnaṃ jātyantare kasmiṃ striyaḥ putrair viyojitāḥ</li>
        <li id="R_2.047.019cd">jananyā mama saumitre tad apy etad upasthitam</li>
    </ul>
    <ul id="R_2.047.020">
        <li id="R_2.047.020ab">mayā hi cirapuṣṭena duḥkhasaṃvardhitena ca</li>
        <li id="R_2.047.020cd">viprāyujyata kausalyā phalakāle dhig astu mām</li>
    </ul>
    <ul id="R_2.047.021">
        <li id="R_2.047.021ab">mā sma sīmantinī kā cij janayet putram īdṛśam</li>
        <li id="R_2.047.021cd">saumitre yo 'ham ambāyā dadmi śokam anantakam</li>
    </ul>
    <ul id="R_2.047.022">
        <li id="R_2.047.022ab">manye prītiviśiṣṭā sā matto lakṣmaṇasārikā</li>
        <li id="R_2.047.022cd">yasyās tac chrūyate vākyaṃ śuka pādam arer daśa</li>
    </ul>
    <ul id="R_2.047.023">
        <li id="R_2.047.023ab">śocantyāś cālpabhāgyāyā na kiṃ cid upakurvatā</li>
        <li id="R_2.047.023cd">purtreṇa kim aputrāyā mayā kāryam ariṃdama</li>
    </ul>
    <ul id="R_2.047.024">
        <li id="R_2.047.024ab">alpabhāgyā hi me mātā kausalyā rahitā mayā</li>
        <li id="R_2.047.024cd">śete paramaduḥkhārtā patitā śokasāgare</li>
    </ul>
    <ul id="R_2.047.025">
        <li id="R_2.047.025ab">eko hy aham ayodhyāṃ ca pṛthivīṃ cāpi lakṣmaṇa</li>
        <li id="R_2.047.025cd">tareyam iṣubhiḥ kruddho nanu vīryam akāraṇam</li>
    </ul>
    <ul id="R_2.047.026">
        <li id="R_2.047.026ab">adharmabhaya bhītaś ca paralokasya cānagha</li>
        <li id="R_2.047.026cd">tena lakṣmaṇa nādyāham ātmānam abhiṣecaye</li>
    </ul>
    <ul id="R_2.047.027">
        <li id="R_2.047.027ab">etad anyac ca karuṇaṃ vilapya vijane bahu</li>
        <li id="R_2.047.027cd">aśrupūrṇamukho rāmo niśi tūṣṇīm upāviśat</li>
    </ul>
    <ul id="R_2.047.028">
        <li id="R_2.047.028ab">vilapyoparataṃ rāmaṃ gatārciṣam ivānalam</li>
        <li id="R_2.047.028cd">samudram iva nirvegam āśvāsayata lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_2.047.029">
        <li id="R_2.047.029ab">dhruvam adya purī rāma ayodhyā yudhināṃ vara</li>
        <li id="R_2.047.029cd">niṣprabhā tvayi niṣkrānte gatacandreva śarvarī</li>
    </ul>
    <ul id="R_2.047.030">
        <li id="R_2.047.030ab">naitad aupayikaṃ rāma yad idaṃ paritapyase</li>
        <li id="R_2.047.030cd">viṣādayasi sītāṃ ca māṃ caiva puruṣarṣabha</li>
    </ul>
    <ul id="R_2.047.031">
        <li id="R_2.047.031ab">na ca sītā tvayā hīnā na cāham api rāghava</li>
        <li id="R_2.047.031cd">muhūrtam api jīvāvo jalān matsyāv ivoddhṛtau</li>
    </ul>
    <ul id="R_2.047.032">
        <li id="R_2.047.032ab">na hi tātaṃ na śatrughnaṃ na sumitrāṃ paraṃtapa</li>
        <li id="R_2.047.032cd">draṣṭum iccheyam adyāhaṃ svargaṃ vāpi tvayā vinā</li>
    </ul>
    <ul id="R_2.047.033">
        <li id="R_2.047.033ab">
            <seg type="pāda" n="a">sa lakṣmaṇasyottama puṣkalaṃ vaco</seg>
            <seg type="pāda" n="b">niśamya caivaṃ vanavāsam ādarāt</seg>
        </li>
        <li id="R_2.047.033cd">
            <seg type="pāda" n="c">samāḥ samastā vidadhe paraṃtapaḥ</seg>
            <seg type="pāda" n="d">prapadya dharmaṃ sucirāya rāghavaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.048.001">
        <li id="R_2.048.001ab">te tu tasmin mahāvṛkṣa uṣitvā rajanīṃ śivām</li>
        <li id="R_2.048.001cd">vimale 'bhyudite sūrye tasmād deśāt pratasthire</li>
    </ul>
    <ul id="R_2.048.002">
        <li id="R_2.048.002ab">yatra bhāgīrathī gaṅgā yamunām abhivartate</li>
        <li id="R_2.048.002cd">jagmus taṃ deśam uddiśya vigāhya sumahad vanam</li>
    </ul>
    <ul id="R_2.048.003">
        <li id="R_2.048.003ab">te bhūmim āgān vividhān deśāṃś cāpi manoramān</li>
        <li id="R_2.048.003cd">adṛṣṭapūrvān paśyantas tatra tatra yaśasvinaḥ</li>
    </ul>
    <ul id="R_2.048.004">
        <li id="R_2.048.004ab">yathākṣemeṇa gacchan sa paśyaṃś ca vividhān drumān</li>
        <li id="R_2.048.004cd">nivṛttamātre divase rāmaḥ saumitrim abravīt</li>
    </ul>
    <ul id="R_2.048.005">
        <li id="R_2.048.005ab">prayāgam abhitaḥ paśya saumitre dhūmam unnatam</li>
        <li id="R_2.048.005cd">agner bhagavataḥ ketuṃ manye saṃnihito muniḥ</li>
    </ul>
    <ul id="R_2.048.006">
        <li id="R_2.048.006ab">nūnaṃ prāptāḥ sma saṃbhedaṃ gaṅgāyamunayor vayam</li>
        <li id="R_2.048.006cd">tathā hi śrūyate śambdo vāriṇā vārighaṭṭitaḥ</li>
    </ul>
    <ul id="R_2.048.007">
        <li id="R_2.048.007ab">dārūṇi paribhinnāni vanajair upajīvibhiḥ</li>
        <li id="R_2.048.007cd">bharadvājāśrame caite dṛśyante vividhā drumāḥ</li>
    </ul>
    <ul id="R_2.048.008">
        <li id="R_2.048.008ab">dhanvinau tau sukhaṃ gatvā lambamāne divākare</li>
        <li id="R_2.048.008cd">gaṅgāyamunayoḥ saṃdhau prāpatur nilayaṃ muneḥ</li>
    </ul>
    <ul id="R_2.048.009">
        <li id="R_2.048.009ab">rāmas tv āśramam āsādya trāsayan mṛgapakṣiṇaḥ</li>
        <li id="R_2.048.009cd">gatvā muhūrtam adhvānaṃ bharadvājam upāgamat</li>
    </ul>
    <ul id="R_2.048.010">
        <li id="R_2.048.010ab">tatas tv āśramam āsādya muner darśanakāṅkṣiṇau</li>
        <li id="R_2.048.010cd">sītayānugatau vīrau dūrād evāvatasthatuḥ</li>
    </ul>
    <ul id="R_2.048.011">
        <li id="R_2.048.011ab">hutāgnihotraṃ dṛṣṭvaiva mahābhāgaṃ kṛtāñjaliḥ</li>
        <li id="R_2.048.011cd">rāmaḥ saumitriṇā sārdhaṃ sītayā cābhyavādayat</li>
    </ul>
    <ul id="R_2.048.012">
        <li id="R_2.048.012ab">nyavedayata cātmānaṃ tasmai lakṣmaṇapūrvajaḥ</li>
        <li id="R_2.048.012cd">putrau daśarathasyāvāṃ bhagavan rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_2.048.013">
        <li id="R_2.048.013ab">bhāryā mameyaṃ vaidehī kalyāṇī janakātmajā</li>
        <li id="R_2.048.013cd">māṃ cānuyātā vijanaṃ tapovanam aninditā</li>
    </ul>
    <ul id="R_2.048.014">
        <li id="R_2.048.014ab">pitrā pravrājyamānaṃ māṃ saumitrir anujaḥ priyaḥ</li>
        <li id="R_2.048.014cd">ayam anvagamad bhrātā vanam eva dṛḍhavrataḥ</li>
    </ul>
    <ul id="R_2.048.015">
        <li id="R_2.048.015ab">pitrā niyuktā bhagavan praveṣyāmas tapovanam</li>
        <li id="R_2.048.015cd">dharmam evācariṣyāmas tatra mūlaphalāśanāḥ</li>
    </ul>
    <ul id="R_2.048.016">
        <li id="R_2.048.016ab">tasya tad vacanaṃ śrutvā rājaputrasya dhīmataḥ</li>
        <li id="R_2.048.016cd">upānayata dharmātmā gām arghyam udakaṃ tataḥ</li>
    </ul>
    <ul id="R_2.048.017">
        <li id="R_2.048.017ab">mṛgapakṣibhir āsīno munibhiś ca samantataḥ</li>
        <li id="R_2.048.017cd">rāmam āgatam abhyarcya svāgatenāha taṃ muniḥ</li>
    </ul>
    <ul id="R_2.048.018">
        <li id="R_2.048.018ab">pratigṛhya ca tām arcām upaviṣṭaṃ sarāghavam</li>
        <li id="R_2.048.018cd">bharadvājo 'bravīd vākyaṃ dharmayuktam idaṃ tadā</li>
    </ul>
    <ul id="R_2.048.019">
        <li id="R_2.048.019ab">cirasya khalu kākutstha paśyāmi tvām ihāgatam</li>
        <li id="R_2.048.019cd">śrutaṃ tava mayā cedaṃ vivāsanam akāraṇam</li>
    </ul>
    <ul id="R_2.048.020">
        <li id="R_2.048.020ab">avakāśo vivikto 'yaṃ mahānadyoḥ samāgame</li>
        <li id="R_2.048.020cd">puṇyaś ca ramaṇīyaś ca vasatv iha bhagān sukham</li>
    </ul>
    <ul id="R_2.048.021">
        <li id="R_2.048.021ab">evam uktas tu vacanaṃ bharadvājena rāghavaḥ</li>
        <li id="R_2.048.021cd">pratyuvāca śubhaṃ vākyaṃ rāmaḥ sarvahite rataḥ</li>
    </ul>
    <ul id="R_2.048.022">
        <li id="R_2.048.022ab">bhagavann ita āsannaḥ paurajānapado janaḥ</li>
        <li id="R_2.048.022cd">āgamiṣyati vaidehīṃ māṃ cāpi prekṣako janaḥ</li>
        <li id="R_2.048.022ef">anena kāraṇenāham iha vāsaṃ na rocaye</li>
    </ul>
    <ul id="R_2.048.023">
        <li id="R_2.048.023ab">ekānte paśya bhagavann āśramasthānam uttamam</li>
        <li id="R_2.048.023cd">ramate yatra vaidehī sukhārhā janakātmajā</li>
    </ul>
    <ul id="R_2.048.024">
        <li id="R_2.048.024ab">etac chrutvā śubhaṃ vākyaṃ bharadvājo mahāmuniḥ</li>
        <li id="R_2.048.024cd">rāghavasya tato vākyam artha grāhakam abravīt</li>
    </ul>
    <ul id="R_2.048.025">
        <li id="R_2.048.025ab">daśakrośa itas tāta girir yasmin nivatsyasi</li>
        <li id="R_2.048.025cd">maharṣisevitaḥ puṇyaḥ sarvataḥ sukha darśanaḥ</li>
    </ul>
    <ul id="R_2.048.026">
        <li id="R_2.048.026ab">golāṅgūlānucarito vānararkṣaniṣevitaḥ</li>
        <li id="R_2.048.026cd">citrakūṭa iti khyāto gandhamādanasaṃnibhaḥ</li>
    </ul>
    <ul id="R_2.048.027">
        <li id="R_2.048.027ab">yāvatā citra kūṭasya naraḥ śṛṅgāṇy avekṣate</li>
        <li id="R_2.048.027cd">kalyāṇāni samādhatte na pāpe kurute manaḥ</li>
    </ul>
    <ul id="R_2.048.028">
        <li id="R_2.048.028ab">ṛṣayas tatra bahavo vihṛtya śaradāṃ śatam</li>
        <li id="R_2.048.028cd">tapasā divam ārūḍhāḥ kapālaśirasā saha</li>
    </ul>
    <ul id="R_2.048.029">
        <li id="R_2.048.029ab">praviviktam ahaṃ manye taṃ vāsaṃ bhavataḥ sukham</li>
        <li id="R_2.048.029cd">iha vā vanavāsāya vasa rāma mayā saha</li>
    </ul>
    <ul id="R_2.048.030">
        <li id="R_2.048.030ab">sa rāmaṃ sarvakāmais taṃ bharadvājaḥ priyātithim</li>
        <li id="R_2.048.030cd">sabhāryaṃ saha ca bhrātrā pratijagrāha dharmavit</li>
    </ul>
    <ul id="R_2.048.031">
        <li id="R_2.048.031ab">tasya prayāge rāmasya taṃ maharṣim upeyuṣaḥ</li>
        <li id="R_2.048.031cd">prapannā rajanī puṇyā citrāḥ kathayataḥ kathāḥ</li>
    </ul>
    <ul id="R_2.048.032">
        <li id="R_2.048.032ab">prabhātāyāṃ rajanyāṃ tu bharadvājam upāgamat</li>
        <li id="R_2.048.032cd">uvāca naraśārdūlo muniṃ jvalitatejasaṃ</li>
    </ul>
    <ul id="R_2.048.033">
        <li id="R_2.048.033ab">śarvarīṃ bhavanann adya satyaśīla tavāśrame</li>
        <li id="R_2.048.033cd">uṣitāḥ smeha vasatim anujānātu no bhavān</li>
    </ul>
    <ul id="R_2.048.034">
        <li id="R_2.048.034ab">rātryāṃ tu tasyāṃ vyuṣṭāyāṃ bharadvājo 'bravīd idam</li>
        <li id="R_2.048.034cd">madhumūlaphalopetaṃ citrakūṭaṃ vrajeti ha</li>
    </ul>
    <ul id="R_2.048.035">
        <li id="R_2.048.035ab">tatra kuñjarayūthāni mṛgayūthāni cābhitaḥ</li>
        <li id="R_2.048.035cd">vicaranti vanānteṣu tāni drakṣyasi rāghava</li>
    </ul>
    <ul id="R_2.048.036">
        <li id="R_2.048.036ab">
            <seg type="pāda" n="a">prahṛṣṭakoyaṣṭikakokilasvanair</seg>
            <seg type="pāda" n="b">vināditaṃ taṃ vasudhādharaṃ śivam</seg>
        </li>
        <li id="R_2.048.036cd">
            <seg type="pāda" n="c">mṛgaiś ca mattair bahubhiś ca kuñjaraiḥ</seg>
            <seg type="pāda" n="d">suramyam āsādya samāvasāśramam</seg>
        </li>
    </ul>
    <ul id="R_2.049.001">
        <li id="R_2.049.001ab">uṣitvā rajanīṃ tatra rājaputrāv ariṃdamau</li>
        <li id="R_2.049.001cd">maharṣim abhivādyātha jagmatus taṃ giriṃ prati</li>
    </ul>
    <ul id="R_2.049.002">
        <li id="R_2.049.002ab">prasthitāṃś caiva tān prekṣya pitā putrān ivānvagāt</li>
        <li id="R_2.049.002cd">tataḥ pracakrame vaktuṃ vacanaṃ sa mahāmuniḥ</li>
    </ul>
    <ul id="R_2.049.003">
        <li id="R_2.049.003ab">athāsādya tu kālindīṃ śīghrasrotasamāpagām</li>
        <li id="R_2.049.003cd">tatra yūyaṃ plavaṃ kṛtvā taratāṃśumatīṃ nadīm</li>
    </ul>
    <ul id="R_2.049.004">
        <li id="R_2.049.004ab">tato nyagrodham āsādya mahāntaṃ haritacchadam</li>
        <li id="R_2.049.004cd">vivṛddhaṃ bahubhir vṛkṣaiḥ śyāmaṃ siddhopasevitam</li>
    </ul>
    <ul id="R_2.049.005">
        <li id="R_2.049.005ab">krośamātraṃ tato gatvā nīlaṃ drakṣyatha kānanam</li>
        <li id="R_2.049.005cd">palāśabadarīmiśraṃ rāma vaṃśaiś ca yāmunaiḥ</li>
    </ul>
    <ul id="R_2.049.006">
        <li id="R_2.049.006ab">sa panthāś citrakūṭasya gataḥ subahuśo mayā</li>
        <li id="R_2.049.006cd">ramyo mārdavayuktaś ca vanadāvair vivarjitaḥ</li>
        <li id="R_2.049.006ef">iti panthānam āvedya maharṣiḥ sa nyavartata</li>
    </ul>
    <ul id="R_2.049.007">
        <li id="R_2.049.007ab">upāvṛtte munau tasmin rāmo lakṣmaṇam abravīt</li>
        <li id="R_2.049.007cd">kṛtapuṇyāḥ sma saumitre munir yan no 'nukampate</li>
    </ul>
    <ul id="R_2.049.008">
        <li id="R_2.049.008ab">iti tau puruṣavyāghrau mantrayitvā manasvinau</li>
        <li id="R_2.049.008cd">sītām evāgrataḥ kṛtvā kālindīṃ jagmatur nadīm</li>
    </ul>
    <ul id="R_2.049.009">
        <li id="R_2.049.009ab">tau kāṣṭhasaṃghāṭam atho cakratuḥ sumahāplavam</li>
        <li id="R_2.049.009cd">cakāra lakṣmaṇaś chittvā sītāyāḥ sukhamānasaṃ</li>
    </ul>
    <ul id="R_2.049.010">
        <li id="R_2.049.010ab">tatra śriyam ivācintyāṃ rāmo dāśarathiḥ priyām</li>
        <li id="R_2.049.010cd">īṣatsaṃlajjamānāṃ tām adhyāropayata plavam</li>
    </ul>
    <ul id="R_2.049.011">
        <li id="R_2.049.011ab">tataḥ plavenāṃśumatīṃ śīghragām ūrmimālinīm</li>
        <li id="R_2.049.011cd">tīrajair bahubhir vṛkṣaiḥ saṃterur yamunāṃ nadīm</li>
    </ul>
    <ul id="R_2.049.012">
        <li id="R_2.049.012ab">te tīrṇāḥ plavam utsṛjya prasthāya yamunāvanāt</li>
        <li id="R_2.049.012cd">śyāmaṃ nyagrodham āseduḥ śītalaṃ haritacchadam</li>
    </ul>
    <ul id="R_2.049.013">
        <li id="R_2.049.013ab">kausalyāṃ caiva paśyeyaṃ sumitrāṃ ca yaśasvinīm</li>
        <li id="R_2.049.013cd">iti sītāñjaliṃ kṛtvā paryagachad vanaspatim</li>
    </ul>
    <ul id="R_2.049.014">
        <li id="R_2.049.014ab">krośamātraṃ tato gatvā bhrātarau rāmalakṣmaṇau</li>
        <li id="R_2.049.014cd">bahūn medhyān mṛgān hatvā ceratur yamunāvane</li>
    </ul>
    <ul id="R_2.049.015">
        <li id="R_2.049.015ab">
            <seg type="pāda" n="a">vihṛtya te barhiṇapūganādite</seg>
            <seg type="pāda" n="b">śubhe vane vāraṇavānarāyute</seg>
        </li>
        <li id="R_2.049.015cd">
            <seg type="pāda" n="c">samaṃ nadīvapram upetya saṃmataṃ</seg>
            <seg type="pāda" n="d">nivāsam ājagmur adīnadarśanaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.050.001">
        <li id="R_2.050.001ab">atha rātryāṃ vyatītāyām avasuptam anantaram</li>
        <li id="R_2.050.001cd">prabodhayām āsa śanair lakṣmaṇaṃ raghunandanaḥ</li>
    </ul>
    <ul id="R_2.050.002">
        <li id="R_2.050.002ab">saumitre śṛṇu vanyānāṃ valgu vyāharatāṃ svanam</li>
        <li id="R_2.050.002cd">saṃpratiṣṭhāmahe kālaḥ prasthānasya paraṃtapa</li>
    </ul>
    <ul id="R_2.050.003">
        <li id="R_2.050.003ab">sa suptaḥ samaye bhrātrā lakṣmaṇaḥ pratibodhitaḥ</li>
        <li id="R_2.050.003cd">jahau nidrāṃ ca tandrīṃ ca prasaktaṃ ca pathi śramam</li>
    </ul>
    <ul id="R_2.050.004">
        <li id="R_2.050.004ab">tata utthāya te sarve spṛṣṭvā nadyāḥ śivaṃ jalam</li>
        <li id="R_2.050.004cd">panthānam ṛṣiṇoddiṣṭaṃ citrakūṭasya taṃ yayuḥ</li>
    </ul>
    <ul id="R_2.050.005">
        <li id="R_2.050.005ab">tataḥ saṃprasthitaḥ kāle rāmaḥ saumitriṇā saha</li>
        <li id="R_2.050.005cd">sītāṃ kamalapatrākṣīm idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.050.006">
        <li id="R_2.050.006ab">ādīptān iva vaidehi sarvataḥ puṣpitān nagān</li>
        <li id="R_2.050.006cd">svaiḥ puṣpaiḥ kiṃśukān paśya mālinaḥ śiśirātyaye</li>
    </ul>
    <ul id="R_2.050.007">
        <li id="R_2.050.007ab">paśya bhallātakān phullān narair anupasevitān</li>
        <li id="R_2.050.007cd">phalapatrair avanatān nūnaṃ śakṣyāmi jīvitum</li>
    </ul>
    <ul id="R_2.050.008">
        <li id="R_2.050.008ab">paśya droṇapramāṇāni lambamānāni lakṣmaṇa</li>
        <li id="R_2.050.008cd">madhūni madhukārībhiḥ saṃbhṛtāni nage nage</li>
    </ul>
    <ul id="R_2.050.009">
        <li id="R_2.050.009ab">eṣa krośati natyūhas taṃ śikhī pratikūjati</li>
        <li id="R_2.050.009cd">ramaṇīye vanoddeśe puṣpasaṃstarasaṃkaṭe</li>
    </ul>
    <ul id="R_2.050.010">
        <li id="R_2.050.010ab">mātaṃgayūthānusṛtaṃ pakṣisaṃghānunāditam</li>
        <li id="R_2.050.010cd">citrakūṭam imaṃ paśya pravṛddhaśikharaṃ girim</li>
    </ul>
    <ul id="R_2.050.011">
        <li id="R_2.050.011ab">tatas tau pādacāreṇa gacchantau saha sītayā</li>
        <li id="R_2.050.011cd">ramyam āsedatuḥ śailaṃ citrakūṭaṃ manoramam</li>
    </ul>
    <ul id="R_2.050.012">
        <li id="R_2.050.012ab">taṃ tu parvatam āsādya nānāpakṣigaṇāyutam</li>
        <li id="R_2.050.012cd">ayaṃ vāso bhavet tāvad atra saumya ramemahi</li>
    </ul>
    <ul id="R_2.050.013">
        <li id="R_2.050.013ab">lakṣmaṇānaya dārūṇi dṛḍhāni ca varāṇi ca</li>
        <li id="R_2.050.013cd">kuruṣvāvasathaṃ saumya vāse me 'bhirataṃ manaḥ</li>
    </ul>
    <ul id="R_2.050.014">
        <li id="R_2.050.014ab">tasya tad vacanaṃ śrutvā saumitrir vividhān drumān</li>
        <li id="R_2.050.014cd">ājahāra tataś cakre parṇa śālām ariṃ dama</li>
    </ul>
    <ul id="R_2.050.015">
        <li id="R_2.050.015ab">śuśrūṣamāṇam ekāgram idaṃ vacanam abravīt</li>
        <li id="R_2.050.015cd">aiṇeyaṃ māṃsam āhṛtya śālāṃ yakṣyāmahe vayam</li>
    </ul>
    <ul id="R_2.050.016">
        <li id="R_2.050.016ab">sa lakṣmaṇaḥ kṛṣṇamṛgaṃ hatvā medhyaṃ patāpavān</li>
        <li id="R_2.050.016cd">atha cikṣepa saumitriḥ samiddhe jātavedasi</li>
    </ul>
    <ul id="R_2.050.017">
        <li id="R_2.050.017ab">taṃ tu pakvaṃ samājñāya niṣṭaptaṃ chinnaśoṇitam</li>
        <li id="R_2.050.017cd">lakṣmaṇaḥ puruṣavyāghram atha rāghavam abravīt</li>
    </ul>
    <ul id="R_2.050.018">
        <li id="R_2.050.018ab">ayaṃ kṛṣṇaḥ samāptāṅgaḥ śṛtaḥ kṛṣṇa mṛgo yathā</li>
        <li id="R_2.050.018cd">devatā devasaṃkāśa yajasva kuśalo hy asi</li>
    </ul>
    <ul id="R_2.050.019">
        <li id="R_2.050.019ab">rāmaḥ snātvā tu niyato guṇavāñ japyakovidaḥ</li>
        <li id="R_2.050.019cd">pāpasaṃśamanaṃ rāmaś cakāra balim uttamam</li>
    </ul>
    <ul id="R_2.050.020">
        <li id="R_2.050.020ab">
            <seg type="pāda" n="a">tāṃ vṛkṣaparṇac chadanāṃ manojñāṃ</seg>
            <seg type="pāda" n="b">yathāpradeśaṃ sukṛtāṃ nivātām</seg>
        </li>
        <li id="R_2.050.020cd">
            <seg type="pāda" n="c">vāsāya sarve viviśuḥ sametāḥ</seg>
            <seg type="pāda" n="d">sabhāṃ yathā deva gaṇāḥ sudharmām</seg>
        </li>
    </ul>
    <ul id="R_2.050.021">
        <li id="R_2.050.021ab">
            <seg type="pāda" n="a">anekanānāmṛgapakṣisaṃkule</seg>
            <seg type="pāda" n="b">vicitrapuṣpastabalair drumair yute</seg>
        </li>
        <li id="R_2.050.021cd">
            <seg type="pāda" n="c">vanottame vyālamṛgānunādite</seg>
            <seg type="pāda" n="d">tathā vijahruḥ susukhaṃ jitendriyāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.050.022">
        <li id="R_2.050.022ab">
            <seg type="pāda" n="a">suramyam āsādya tu citrakūṭaṃ</seg>
            <seg type="pāda" n="b">nadīṃ ca tāṃ mālyavatīṃ sutīrthām</seg>
        </li>
        <li id="R_2.050.022cd">
            <seg type="pāda" n="c">nananda hṛṣṭo mṛgapakṣijuṣṭāṃ</seg>
            <seg type="pāda" n="d">jahau ca duḥkhaṃ puravipravāsāt</seg>
        </li>
    </ul>
    <ul id="R_2.051.001">
        <li id="R_2.051.001ab">kathayitvā suduḥkhārtaḥ sumantreṇa ciraṃ saha</li>
        <li id="R_2.051.001cd">rāme dakṣiṇa kūlasthe jagāma svagṛhaṃ guhaḥ</li>
    </ul>
    <ul id="R_2.051.002">
        <li id="R_2.051.002ab">anujñātaḥ sumantro 'tha yojayitvā hayottamān</li>
        <li id="R_2.051.002cd">ayodhyām eva nagarīṃ prayayau gāḍhadurmanāḥ</li>
    </ul>
    <ul id="R_2.051.003">
        <li id="R_2.051.003ab">sa vanāni sugandhīni saritaś ca sarāṃsi ca</li>
        <li id="R_2.051.003cd">paśyann atiyayau śīghraṃ grāmāṇi nagarāṇi ca</li>
    </ul>
    <ul id="R_2.051.004">
        <li id="R_2.051.004ab">tataḥ sāyāhnasamaye tṛtīye 'hani sārathiḥ</li>
        <li id="R_2.051.004cd">ayodhyāṃ samanuprāpya nirānandāṃ dadarśa ha</li>
    </ul>
    <ul id="R_2.051.005">
        <li id="R_2.051.005ab">sa śūnyām iva niḥśabdāṃ dṛṣṭvā paramadurmanāḥ</li>
        <li id="R_2.051.005cd">sumantraś cintayām āsa śokavegasamāhataḥ</li>
    </ul>
    <ul id="R_2.051.006">
        <li id="R_2.051.006ab">kaccin na sagajā sāśvā sajanā sajanādhipā</li>
        <li id="R_2.051.006cd">rāmasaṃtāpaduḥkhena dagdhā śokāgninā purī</li>
        <li id="R_2.051.006ef">iti cintāparaḥ sūtas tvaritaḥ praviveśa ha</li>
    </ul>
    <ul id="R_2.051.007">
        <li id="R_2.051.007ab">sumantram abhiyāntaṃ taṃ śataśo 'tha sahasraśaḥ</li>
        <li id="R_2.051.007cd">kva rāma iti pṛcchantaḥ sūtam abhyadravan narāḥ</li>
    </ul>
    <ul id="R_2.051.008">
        <li id="R_2.051.008ab">teṣāṃ śaśaṃsa gaṅgāyām aham āpṛcchya rāghavam</li>
        <li id="R_2.051.008cd">anujñāto nivṛtto 'smi dhārmikeṇa mahātmanā</li>
    </ul>
    <ul id="R_2.051.009">
        <li id="R_2.051.009ab">te tīrṇā iti vijñāya bāṣpapūrṇamukhā janāḥ</li>
        <li id="R_2.051.009cd">aho dhig iti niḥśvasya hā rāmeti ca cukruśuḥ</li>
    </ul>
    <ul id="R_2.051.010">
        <li id="R_2.051.010ab">śuśrāva ca vacas teṣāṃ vṛndaṃ vṛndaṃ ca tiṣṭhatām</li>
        <li id="R_2.051.010cd">hatāḥ sma khalu ye neha paśyāma iti rāghavam</li>
    </ul>
    <ul id="R_2.051.011">
        <li id="R_2.051.011ab">dānayajñavivāheṣu samājeṣu mahatsu ca</li>
        <li id="R_2.051.011cd">na drakṣyāmaḥ punar jātu dhārmikaṃ rāmam antarā</li>
    </ul>
    <ul id="R_2.051.012">
        <li id="R_2.051.012ab">kiṃ samarthaṃ janasyāsya kiṃ priyaṃ kiṃ sukhāvaham</li>
        <li id="R_2.051.012cd">iti rāmeṇa nagaraṃ pitṛvat paripālitam</li>
    </ul>
    <ul id="R_2.051.013">
        <li id="R_2.051.013ab">vātāyanagatānāṃ ca strīṇām anvantarāpaṇam</li>
        <li id="R_2.051.013cd">rāmaśokābhitaptānāṃ śuśrāva paridevanam</li>
    </ul>
    <ul id="R_2.051.014">
        <li id="R_2.051.014ab">sa rājamārgamadhyena sumantraḥ pihitānanaḥ</li>
        <li id="R_2.051.014cd">yatra rājā daśarathas tad evopayayau gṛham</li>
    </ul>
    <ul id="R_2.051.015">
        <li id="R_2.051.015ab">so 'vatīrya rathāc chīghraṃ rājaveśma praviśya ca</li>
        <li id="R_2.051.015cd">kakṣyāḥ saptābhicakrāma mahājanasamākulāḥ</li>
    </ul>
    <ul id="R_2.051.016">
        <li id="R_2.051.016ab">tato daśarathastrīṇāṃ prāsādebhyas tatas tataḥ</li>
        <li id="R_2.051.016cd">rāmaśokābhitaptānāṃ mandaṃ śuśrāva jalpitam</li>
    </ul>
    <ul id="R_2.051.017">
        <li id="R_2.051.017ab">saha rāmeṇa niryāto vinā rāmam ihāgataḥ</li>
        <li id="R_2.051.017cd">sūtaḥ kiṃ nāma kausalyāṃ śocantīṃ prativakṣyati</li>
    </ul>
    <ul id="R_2.051.018">
        <li id="R_2.051.018ab">yathā ca manye durjīvam evaṃ na sukaraṃ dhruvam</li>
        <li id="R_2.051.018cd">ācchidya putre niryāte kausalyā yatra jīvati</li>
    </ul>
    <ul id="R_2.051.019">
        <li id="R_2.051.019ab">satya rūpaṃ tu tadvākyaṃ rājñaḥ strīṇāṃ niśāmayan</li>
        <li id="R_2.051.019cd">pradīptam iva śokena viveśa sahasā gṛham</li>
    </ul>
    <ul id="R_2.051.020">
        <li id="R_2.051.020ab">sa praviśyāṣṭamīṃ kakṣyāṃ rājānaṃ dīnam ātulam</li>
        <li id="R_2.051.020cd">putraśokaparidyūnam apaśyat pāṇḍare gṛhe</li>
    </ul>
    <ul id="R_2.051.021">
        <li id="R_2.051.021ab">abhigamya tam āsīnaṃ narendram abhivādya ca</li>
        <li id="R_2.051.021cd">sumantro rāmavacanaṃ yathoktaṃ pratyavedayat</li>
    </ul>
    <ul id="R_2.051.022">
        <li id="R_2.051.022ab">sa tūṣṇīm eva tac chrutvā rājā vibhrānta cetanaḥ</li>
        <li id="R_2.051.022cd">mūrchito nyapatad bhūmau rāmaśokābhipīḍitaḥ</li>
    </ul>
    <ul id="R_2.051.023">
        <li id="R_2.051.023ab">tato 'ntaḥpuram āviddhaṃ mūrchite pṛthivīpatau</li>
        <li id="R_2.051.023cd">uddhṛtya bāhū cukrośa nṛpatau patite kṣitau</li>
    </ul>
    <ul id="R_2.051.024">
        <li id="R_2.051.024ab">sumitrayā tu sahitā kausalyā patitaṃ patim</li>
        <li id="R_2.051.024cd">utthāpayām āsa tadā vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.051.025">
        <li id="R_2.051.025ab">imaṃ tasya mahābhāga dūtaṃ duṣkarakāriṇaḥ</li>
        <li id="R_2.051.025cd">vanavāsād anuprāptaṃ kasmān na pratibhāṣase</li>
    </ul>
    <ul id="R_2.051.026">
        <li id="R_2.051.026ab">adyemam anayaṃ kṛtvā vyapatrapasi rāghava</li>
        <li id="R_2.051.026cd">uttiṣṭha sukṛtaṃ te 'stu śoke na syāt sahāyatā</li>
    </ul>
    <ul id="R_2.051.027">
        <li id="R_2.051.027ab">deva yasyā bhayād rāmaṃ nānupṛcchasi sārathim</li>
        <li id="R_2.051.027cd">neha tiṣṭhati kaikeyī viśrabdhaṃ pratibhāṣyatām</li>
    </ul>
    <ul id="R_2.051.028">
        <li id="R_2.051.028ab">sā tathoktvā mahārājaṃ kausalyā śokalālasā</li>
        <li id="R_2.051.028cd">dharaṇyāṃ nipapātāśu bāṣpaviplutabhāṣiṇī</li>
    </ul>
    <ul id="R_2.051.029">
        <li id="R_2.051.029ab">evaṃ vilapatīṃ dṛṣṭvā kausalyāṃ patitāṃ bhuvi</li>
        <li id="R_2.051.029cd">patiṃ cāvekṣya tāḥ sarvāḥ sasvaraṃ ruruduḥ striyaḥ</li>
    </ul>
    <ul id="R_2.051.030">
        <li id="R_2.051.030ab">
            <seg type="pāda" n="a">tatas tam antaḥpuranādam utthitaṃ</seg>
            <seg type="pāda" n="b">samīkṣya vṛddhās taruṇāś ca mānavāḥ</seg>
        </li>
        <li id="R_2.051.030cd">
            <seg type="pāda" n="c">striyaś ca sarvā ruruduḥ samantataḥ</seg>
            <seg type="pāda" n="d">puraṃ tadāsīt punar eva saṃkulam</seg>
        </li>
    </ul>
    <ul id="R_2.052.001">
        <li id="R_2.052.001ab">pratyāśvasto yadā rājā mohāt pratyāgataḥ punaḥ</li>
        <li id="R_2.052.001cd">athājuhāva taṃ sūtaṃ rāmavṛttāntakāraṇāt</li>
    </ul>
    <ul id="R_2.052.002">
        <li id="R_2.052.002ab">vṛddhaṃ paramasaṃtaptaṃ navagraham iva dvipam</li>
        <li id="R_2.052.002cd">viniḥśvasantaṃ dhyāyantam asvastham iva kuñjaram</li>
    </ul>
    <ul id="R_2.052.003">
        <li id="R_2.052.003ab">rājā tu rajasā sūtaṃ dhvastāṅgaṃ samupasthitam</li>
        <li id="R_2.052.003cd">aśrupūrṇamukhaṃ dīnam uvāca paramārtavat</li>
    </ul>
    <ul id="R_2.052.004">
        <li id="R_2.052.004ab">kva nu vatsyati dharmātmā vṛkṣamūlam upāśritaḥ</li>
        <li id="R_2.052.004cd">so 'tyantasukhitaḥ sūta kim aśiṣyati rāghavaḥ</li>
        <li id="R_2.052.004ef">bhūmipālātmajo bhūmau śete katham anāthavat</li>
    </ul>
    <ul id="R_2.052.005">
        <li id="R_2.052.005ab">yaṃ yāntam anuyānti sma padāti rathakuñjarāḥ</li>
        <li id="R_2.052.005cd">sa vatsyati kathaṃ rāmo vijanaṃ vanam āśritaḥ</li>
    </ul>
    <ul id="R_2.052.006">
        <li id="R_2.052.006ab">vyālair mṛgair ācaritaṃ kṛṣṇasarpaniṣevitam</li>
        <li id="R_2.052.006cd">kathaṃ kumārau vaidehyā sārdhaṃ vanam upasthitau</li>
    </ul>
    <ul id="R_2.052.007">
        <li id="R_2.052.007ab">sukumāryā tapasvinyā sumantra saha sītayā</li>
        <li id="R_2.052.007cd">rājaputrau kathaṃ pādair avaruhya rathād gatau</li>
    </ul>
    <ul id="R_2.052.008">
        <li id="R_2.052.008ab">siddhārthaḥ khalu sūta tvaṃ yena dṛṣṭau mamātmajau</li>
        <li id="R_2.052.008cd">vanāntaṃ praviśantau tāv aśvināv iva mandaram</li>
    </ul>
    <ul id="R_2.052.009">
        <li id="R_2.052.009ab">kim uvāca vaco rāmaḥ kim uvāca ca lakṣmaṇaḥ</li>
        <li id="R_2.052.009cd">sumantra vanam āsādya kim uvāca ca maithilī</li>
        <li id="R_2.052.009ef">āsitaṃ śayitaṃ bhuktaṃ sūta rāmasya kīrtaya</li>
    </ul>
    <ul id="R_2.052.010">
        <li id="R_2.052.010ab">iti sūto narendreṇa coditaḥ sajjamānayā</li>
        <li id="R_2.052.010cd">uvāca vācā rājānaṃ sabāṣpaparirabdhayā</li>
    </ul>
    <ul id="R_2.052.011">
        <li id="R_2.052.011ab">abravīn māṃ mahārāja dharmam evānupālayan</li>
        <li id="R_2.052.011cd">añjaliṃ rāghavaḥ kṛtvā śirasābhipraṇamya ca</li>
    </ul>
    <ul id="R_2.052.012">
        <li id="R_2.052.012ab">sūta madvacanāt tasya tātasya viditātmanaḥ</li>
        <li id="R_2.052.012cd">śirasā vandanīyasya vandyau pādau mahātmanaḥ</li>
    </ul>
    <ul id="R_2.052.013">
        <li id="R_2.052.013ab">sarvam antaḥpuraṃ vācyaṃ sūta mad vacanāt tvayā</li>
        <li id="R_2.052.013cd">ārogyam aviśeṣeṇa yathārhaṃ cābhivādanam</li>
    </ul>
    <ul id="R_2.052.014">
        <li id="R_2.052.014ab">mātā ca mama kausalyā kuśalaṃ cābhivādanam</li>
        <li id="R_2.052.014cd">devi devasya pādau ca devavat paripālaya</li>
    </ul>
    <ul id="R_2.052.015">
        <li id="R_2.052.015ab">bharataḥ kuśalaṃ vācyo vācyo madvacanena ca</li>
        <li id="R_2.052.015cd">sarvāsv eva yathānyāyaṃ vṛttiṃ vartasva mātṛṣu</li>
    </ul>
    <ul id="R_2.052.016">
        <li id="R_2.052.016ab">vaktavyaś ca mahābāhur ikṣvākukulanandanaḥ</li>
        <li id="R_2.052.016cd">pitaraṃ yauvarājyastho rājyastham anupālaya</li>
    </ul>
    <ul id="R_2.052.017">
        <li id="R_2.052.017ab">ity evaṃ māṃ mahārāja bruvann eva mahāyaśāḥ</li>
        <li id="R_2.052.017cd">rāmo rājīvatāmrākṣo bhṛśam aśrūṇy avartayat</li>
    </ul>
    <ul id="R_2.052.018">
        <li id="R_2.052.018ab">lakṣmaṇas tu susaṃkruddho niḥśvasan vākyam abravīt</li>
        <li id="R_2.052.018cd">kenāyam aparādhena rājaputro vivāsitaḥ</li>
    </ul>
    <ul id="R_2.052.019">
        <li id="R_2.052.019ab">yadi pravrājito rāmo lobhakāraṇakāritam</li>
        <li id="R_2.052.019cd">varadānanimittaṃ vā sarvathā duṣkṛtaṃ kṛtam</li>
        <li id="R_2.052.019ef">rāmasya tu parityāge na hetum upalakṣaye</li>
    </ul>
    <ul id="R_2.052.020">
        <li id="R_2.052.020ab">asamīkṣya samārabdhaṃ viruddhaṃ buddhilāghavāt</li>
        <li id="R_2.052.020cd">janayiṣyati saṃkrośaṃ rāghavasya vivāsanam</li>
    </ul>
    <ul id="R_2.052.021">
        <li id="R_2.052.021ab">ahaṃ tāvan mahārāje pitṛtvaṃ nopalakṣaye</li>
        <li id="R_2.052.021cd">bhrātā bhartā ca bandhuś ca pitā ca mama rāghavaḥ</li>
    </ul>
    <ul id="R_2.052.022">
        <li id="R_2.052.022ab">sarvalokapriyaṃ tyaktvā sarvalokahite ratam</li>
        <li id="R_2.052.022cd">sarvaloko 'nurajyeta kathaṃ tvānena karmaṇā</li>
    </ul>
    <ul id="R_2.052.023">
        <li id="R_2.052.023ab">jānakī tu mahārāja niḥśvasantī tapasvinī</li>
        <li id="R_2.052.023cd">bhūtopahatacitteva viṣṭhitā vṛṣmṛtā sthitā</li>
    </ul>
    <ul id="R_2.052.024">
        <li id="R_2.052.024ab">adṛṣṭapūrvavyasanā rājaputrī yaśasvinī</li>
        <li id="R_2.052.024cd">tena duḥkhena rudatī naiva māṃ kiṃ cid abravīt</li>
    </ul>
    <ul id="R_2.052.025">
        <li id="R_2.052.025ab">udvīkṣamāṇā bhartāraṃ mukhena pariśuṣyatā</li>
        <li id="R_2.052.025cd">mumoca sahasā bāṣpaṃ māṃ prayāntam udīkṣya sā</li>
    </ul>
    <ul id="R_2.052.026">
        <li id="R_2.052.026ab">
            <seg type="pāda" n="a">tathaiva rāmo 'śrumukhaḥ kṛtāñjaliḥ</seg>
            <seg type="pāda" n="b">sthito 'bhaval lakṣmaṇabāhupālitaḥ</seg>
        </li>
        <li id="R_2.052.026cd">
            <seg type="pāda" n="c">tathaiva sītā rudatī tapasvinī</seg>
            <seg type="pāda" n="d">nirīkṣate rājarathaṃ tathaiva mām</seg>
        </li>
    </ul>
    <ul id="R_2.053.001">
        <li id="R_2.053.001ab">mama tv aśvā nivṛttasya na prāvartanta vartmani</li>
        <li id="R_2.053.001cd">uṣṇam aśru vimuñcanto rāme saṃprasthite vanam</li>
    </ul>
    <ul id="R_2.053.002">
        <li id="R_2.053.002ab">ubhābhyāṃ rājaputrābhyām atha kṛtvāham ajñalim</li>
        <li id="R_2.053.002cd">prasthito ratham āsthāya tad duḥkham api dhārayan</li>
    </ul>
    <ul id="R_2.053.003">
        <li id="R_2.053.003ab">guheva sārdhaṃ tatraiva sthito 'smi divasān bahūn</li>
        <li id="R_2.053.003cd">āśayā yadi māṃ rāmaḥ punaḥ śabdāpayed iti</li>
    </ul>
    <ul id="R_2.053.004">
        <li id="R_2.053.004ab">viṣaye te mahārāja rāmavyasanakarśitāḥ</li>
        <li id="R_2.053.004cd">api vṛkṣāḥ parimlānaḥ sapuṣpāṅkurakorakāḥ</li>
    </ul>
    <ul id="R_2.053.005">
        <li id="R_2.053.005ab">na ca sarpanti sattvāni vyālā na prasaranti ca</li>
        <li id="R_2.053.005cd">rāmaśokābhibhūtaṃ tan niṣkūjam abhavad vanam</li>
    </ul>
    <ul id="R_2.053.006">
        <li id="R_2.053.006ab">līnapuṣkarapatrāś ca narendra kaluṣodakāḥ</li>
        <li id="R_2.053.006cd">saṃtaptapadmāḥ padminyo līnamīnavihaṃgamāḥ</li>
    </ul>
    <ul id="R_2.053.007">
        <li id="R_2.053.007ab">jalajāni ca puṣpāṇi mālyāni sthalajāni ca</li>
        <li id="R_2.053.007cd">nādya bhānty alpagandhīni phalāni ca yathā puram</li>
    </ul>
    <ul id="R_2.053.008">
        <li id="R_2.053.008ab">praviśantam ayodhyāṃ māṃ na kaś cid abhinandati</li>
        <li id="R_2.053.008cd">narā rāmam apaśyanto niḥśvasanti muhur muhuḥ</li>
    </ul>
    <ul id="R_2.053.009">
        <li id="R_2.053.009ab">harmyair vimānaiḥ prāsādair avekṣya ratham āgatam</li>
        <li id="R_2.053.009cd">hāhākārakṛtā nāryo rāmādarśanakarśitāḥ</li>
    </ul>
    <ul id="R_2.053.010">
        <li id="R_2.053.010ab">āyatair vimalair netrair aśruvegapariplutaiḥ</li>
        <li id="R_2.053.010cd">anyonyam abhivīkṣante vyaktam ārtatarāḥ striyaḥ</li>
    </ul>
    <ul id="R_2.053.011">
        <li id="R_2.053.011ab">nāmitrāṇāṃ na mitrāṇām udāsīnajanasya ca</li>
        <li id="R_2.053.011cd">aham ārtatayā kaṃ cid viśeṣaṃ nopalakṣaye</li>
    </ul>
    <ul id="R_2.053.012">
        <li id="R_2.053.012ab">aprahṛṣṭamanuṣyā ca dīnanāgaturaṃgamā</li>
        <li id="R_2.053.012cd">ārtasvaraparimlānā viniḥśvasitaniḥsvanā</li>
    </ul>
    <ul id="R_2.053.013">
        <li id="R_2.053.013ab">nirānandā mahārāja rāmapravrājanātulā</li>
        <li id="R_2.053.013cd">kausalyā putra hīneva ayodhyā pratibhāti mā</li>
    </ul>
    <ul id="R_2.053.014">
        <li id="R_2.053.014ab">sūtasya vacanaṃ śrutvā vācā paramadīnayā</li>
        <li id="R_2.053.014cd">bāṣpopahatayā rājā taṃ sūtam idam abravīt</li>
    </ul>
    <ul id="R_2.053.015">
        <li id="R_2.053.015ab">kaikeyyā viniyuktena pāpābhijanabhāvayā</li>
        <li id="R_2.053.015cd">mayā na mantrakuśalair vṛddhaiḥ saha samarthitam</li>
    </ul>
    <ul id="R_2.053.016">
        <li id="R_2.053.016ab">na suhṛdbhir na cāmātyair mantrayitvā na naigamaiḥ</li>
        <li id="R_2.053.016cd">mayāyam arthaḥ saṃmohāt strīhetoḥ sahasā kṛtaḥ</li>
    </ul>
    <ul id="R_2.053.017">
        <li id="R_2.053.017ab">bhavitavyatayā nūnam idaṃ vā vyasanaṃ mahat</li>
        <li id="R_2.053.017cd">kulasyāsya vināśāya prāptaṃ sūta yadṛcchayā</li>
    </ul>
    <ul id="R_2.053.018">
        <li id="R_2.053.018ab">sūta yady asti te kiṃ cin mayāpi sukṛtaṃ kṛtam</li>
        <li id="R_2.053.018cd">tvaṃ prāpayāśu māṃ rāmaṃ prāṇāḥ saṃtvarayanti mām</li>
    </ul>
    <ul id="R_2.053.019">
        <li id="R_2.053.019ab">yad yad yāpi mamaivājñā nivartayatu rāghavam</li>
        <li id="R_2.053.019cd">na śakṣyāmi vinā rāma muhūrtam api jīvitum</li>
    </ul>
    <ul id="R_2.053.020">
        <li id="R_2.053.020ab">atha vāpi mahābāhur gato dūraṃ bhaviṣyati</li>
        <li id="R_2.053.020cd">mām eva ratham āropya śīghraṃ rāmāya darśaya</li>
    </ul>
    <ul id="R_2.053.021">
        <li id="R_2.053.021ab">vṛttadaṃṣṭro maheṣvāsaḥ kvāsau lakṣmaṇapūrvajaḥ</li>
        <li id="R_2.053.021cd">yadi jīvāmi sādhv enaṃ paśyeyaṃ saha sītayā</li>
    </ul>
    <ul id="R_2.053.022">
        <li id="R_2.053.022ab">lohitākṣaṃ mahābāhum āmuktamaṇikuṇḍalam</li>
        <li id="R_2.053.022cd">rāmaṃ yadi na paśyāmi gamiṣyāmi yamakṣayam</li>
    </ul>
    <ul id="R_2.053.023">
        <li id="R_2.053.023ab">ato nu kiṃ duḥkhataraṃ yo 'ham ikṣvākunandanam</li>
        <li id="R_2.053.023cd">imām avasthām āpanno neha paśyāmi rāghavam</li>
    </ul>
    <ul id="R_2.053.024">
        <li id="R_2.053.024ab">hā rāma rāmānuja hā hā vaidehi tapasvinī</li>
        <li id="R_2.053.024cd">na māṃ jānīta duḥkhena mriyamāṇam anāthavat</li>
        <li id="R_2.053.024ef">dustaro jīvatā devi mayāyaṃ śokasāgaraḥ</li>
    </ul>
    <ul id="R_2.053.025">
        <li id="R_2.053.025ab">
            <seg type="pāda" n="a">aśobhanaṃ yo 'ham ihādya rāghavaṃ</seg>
            <seg type="pāda" n="b">didṛkṣamāṇo na labhe salakṣmaṇam</seg>
        </li>
        <li id="R_2.053.025cd">
            <seg type="pāda" n="c">itīva rājā vilapan mahāyaśāḥ</seg>
            <seg type="pāda" n="d">papāta tūrṇaṃ śayane sa mūrchitaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.053.026">
        <li id="R_2.053.026ab">
            <seg type="pāda" n="a">iti vilapati pārthive pranaṣṭe</seg>
            <seg type="pāda" n="b">karuṇataraṃ dviguṇaṃ ca rāmahetoḥ</seg>
        </li>
        <li id="R_2.053.026cd">
            <seg type="pāda" n="c">vacanam anuniśamya tasya devī</seg>
            <seg type="pāda" n="d">bhayam agamat punar eva rāmamātā</seg>
        </li>
    </ul>
    <ul id="R_2.054.001">
        <li id="R_2.054.001ab">tato bhūtopasṛṣṭeva vepamānā punaḥ punaḥ</li>
        <li id="R_2.054.001cd">dharaṇyāṃ gatasattveva kausalyā sūtam abravīt</li>
    </ul>
    <ul id="R_2.054.002">
        <li id="R_2.054.002ab">naya māṃ yatra kākutsthaḥ sītā yatra ca lakṣmaṇaḥ</li>
        <li id="R_2.054.002cd">tān vinā kṣaṇam apy atra jīvituṃ notsahe hy aham</li>
    </ul>
    <ul id="R_2.054.003">
        <li id="R_2.054.003ab">nivartaya rathaṃ śīghraṃ daṇḍakān naya mām api</li>
        <li id="R_2.054.003cd">atha tān nānugacchāmi gamiṣyāmi yamakṣayam</li>
    </ul>
    <ul id="R_2.054.004">
        <li id="R_2.054.004ab">bāṣpavegaupahatayā sa vācā sajjamānayā</li>
        <li id="R_2.054.004cd">idam āśvāsayan devīṃ sūtaḥ prāñjalir abravīt</li>
    </ul>
    <ul id="R_2.054.005">
        <li id="R_2.054.005ab">tyaja śokaṃ ca mohaṃ ca saṃbhramaṃ duḥkhajaṃ tathā</li>
        <li id="R_2.054.005cd">vyavadhūya ca saṃtāpaṃ vane vatsyati rāghavaḥ</li>
    </ul>
    <ul id="R_2.054.006">
        <li id="R_2.054.006ab">lakṣmaṇaś cāpi rāmasya pādau paricaran vane</li>
        <li id="R_2.054.006cd">ārādhayati dharmajñaḥ paralokaṃ jitendriyaḥ</li>
    </ul>
    <ul id="R_2.054.007">
        <li id="R_2.054.007ab">vijane 'pi vane sītā vāsaṃ prāpya gṛheṣv iva</li>
        <li id="R_2.054.007cd">visrambhaṃ labhate 'bhītā rāme saṃnyasta mānasā</li>
    </ul>
    <ul id="R_2.054.008">
        <li id="R_2.054.008ab">nāsyā dainyaṃ kṛtaṃ kiṃ cit susūkṣmam api lakṣaye</li>
        <li id="R_2.054.008cd">uciteva pravāsānāṃ vaidehī pratibhāti mā</li>
    </ul>
    <ul id="R_2.054.009">
        <li id="R_2.054.009ab">nagaropavanaṃ gatvā yathā sma ramate purā</li>
        <li id="R_2.054.009cd">tathaiva ramate sītā nirjaneṣu vaneṣv api</li>
    </ul>
    <ul id="R_2.054.010">
        <li id="R_2.054.010ab">bāleva ramate sītā bālacandranibhānanā</li>
        <li id="R_2.054.010cd">rāmā rāme hy adīnātmā vijane 'pi vane satī</li>
    </ul>
    <ul id="R_2.054.011">
        <li id="R_2.054.011ab">tadgataṃ hṛdayaṃ hy asyās tad adhīnaṃ ca jīvitam</li>
        <li id="R_2.054.011cd">ayodhyāpi bhavet tasyā rāma hīnā tathā vanam</li>
    </ul>
    <ul id="R_2.054.012">
        <li id="R_2.054.012ab">pathi pṛcchati vaidehī grāmāṃś ca nagarāṇi ca</li>
        <li id="R_2.054.012cd">gatiṃ dṛṣṭvā nadīnāṃ ca pādapān vividhān api</li>
    </ul>
    <ul id="R_2.054.013">
        <li id="R_2.054.013ab">adhvanā vāta vegena saṃbhrameṇātapena ca</li>
        <li id="R_2.054.013cd">na hi gacchati vaidehyāś candrāṃśusadṛśī prabhā</li>
    </ul>
    <ul id="R_2.054.014">
        <li id="R_2.054.014ab">sadṛśaṃ śatapatrasya pūrṇacandropamaprabham</li>
        <li id="R_2.054.014cd">vadanaṃ tadvadānyāyā vaidehyā na vikampate</li>
    </ul>
    <ul id="R_2.054.015">
        <li id="R_2.054.015ab">alaktarasaraktābhāv alaktarasavarjitau</li>
        <li id="R_2.054.015cd">adyāpi caraṇau tasyāḥ padmakośasamaprabhau</li>
    </ul>
    <ul id="R_2.054.016">
        <li id="R_2.054.016ab">nūpurodghuṣṭaheleva khelaṃ gacchati bhāminī</li>
        <li id="R_2.054.016cd">idānīm api vaidehī tadrāgā nyastabhūṣaṇā</li>
    </ul>
    <ul id="R_2.054.017">
        <li id="R_2.054.017ab">gajaṃ vā vīkṣya siṃhaṃ vā vyāghraṃ vā vanam āśritā</li>
        <li id="R_2.054.017cd">nāhārayati saṃtrāsaṃ bāhū rāmasya saṃśritā</li>
    </ul>
    <ul id="R_2.054.018">
        <li id="R_2.054.018ab">na śocyās te na cātmā te śocyo nāpi janādhipaḥ</li>
        <li id="R_2.054.018cd">idaṃ hi caritaṃ loke pratiṣṭhāsyati śāśvatam</li>
    </ul>
    <ul id="R_2.054.019">
        <li id="R_2.054.019ab">
            <seg type="pāda" n="a">vidhūya śokaṃ parihṛṣṭamānasā</seg>
            <seg type="pāda" n="b">maharṣiyāte pathi suvyavasthitāḥ</seg>
        </li>
        <li id="R_2.054.019cd">
            <seg type="pāda" n="c">vane ratā vanyaphalāśanāḥ pituḥ</seg>
            <seg type="pāda" n="d">śubhāṃ pratijñāṃ paripālayanti te</seg>
        </li>
    </ul>
    <ul id="R_2.054.020">
        <li id="R_2.054.020ab">
            <seg type="pāda" n="a">tathāpi sūtena suyuktavādinā</seg>
            <seg type="pāda" n="b">nivāryamāṇā sutaśokakarśitā</seg>
        </li>
        <li id="R_2.054.020cd">
            <seg type="pāda" n="c">na caiva devī virarāma kūjitāt</seg>
            <seg type="pāda" n="d">priyeti putreti ca rāghaveti ca</seg>
        </li>
    </ul>
    <ul id="R_2.055.001">
        <li id="R_2.055.001ab">vanaṃ gate dharmapare rāme ramayatāṃ vare</li>
        <li id="R_2.055.001cd">kausalyā rudatī svārtā bhartāram idam abravīt</li>
    </ul>
    <ul id="R_2.055.002">
        <li id="R_2.055.002ab">yady apitriṣu lokeṣu prathitaṃ te mayad yaśaḥ</li>
        <li id="R_2.055.002cd">sānukrośo vadānyaś ca priyavādī ca rāghavaḥ</li>
    </ul>
    <ul id="R_2.055.003">
        <li id="R_2.055.003ab">kathaṃ naravaraśreṣṭha putrau tau saha sītayā</li>
        <li id="R_2.055.003cd">duḥkhitau sukhasaṃvṛddhau vane duḥkhaṃ sahiṣyataḥ</li>
    </ul>
    <ul id="R_2.055.004">
        <li id="R_2.055.004ab">sā nūnaṃ taruṇī śyāmā sukumārī sukhocitā</li>
        <li id="R_2.055.004cd">katham uṣṇaṃ ca śītaṃ ca maithilī prasahiṣyate</li>
    </ul>
    <ul id="R_2.055.005">
        <li id="R_2.055.005ab">bhuktvāśanaṃ viśālākṣī sūpadaṃśānvitaṃ śubham</li>
        <li id="R_2.055.005cd">vanyaṃ naivāram āhāraṃ kathaṃ sītopabhokṣyate</li>
    </ul>
    <ul id="R_2.055.006">
        <li id="R_2.055.006ab">gītavāditranirghoṣaṃ śrutvā śubham aninditā</li>
        <li id="R_2.055.006cd">kathaṃ kravyādasiṃhānāṃ śabdaṃ śroṣyaty aśobhanam</li>
    </ul>
    <ul id="R_2.055.007">
        <li id="R_2.055.007ab">mahendradhvajasaṃkāśaḥ kva nu śete mahābhujaḥ</li>
        <li id="R_2.055.007cd">bhujaṃ parighasaṃkāśam upadhāya mahābalaḥ</li>
    </ul>
    <ul id="R_2.055.008">
        <li id="R_2.055.008ab">padmavarṇaṃ sukeśāntaṃ padmaniḥśvāsam uttamam</li>
        <li id="R_2.055.008cd">kadā drakṣyāmi rāmasya vadanaṃ puṣkarekṣaṇam</li>
    </ul>
    <ul id="R_2.055.009">
        <li id="R_2.055.009ab">vajrasāramayaṃ nūnaṃ hṛdayaṃ me na saṃśayaḥ</li>
        <li id="R_2.055.009cd">apaśyantyā na taṃ yad vai phalatīdaṃ sahasradhā</li>
    </ul>
    <ul id="R_2.055.010">
        <li id="R_2.055.010ab">yadi pañcadaśe varṣe rāghavaḥ punar eṣyati</li>
        <li id="R_2.055.010cd">jahyād rājyaṃ ca kośaṃ ca bharatenopabhokṣyate</li>
    </ul>
    <ul id="R_2.055.011">
        <li id="R_2.055.011ab">evaṃ kanīyasā bhrātrā bhuktaṃ rājyaṃ viśāṃ pate</li>
        <li id="R_2.055.011cd">bhrātā jyeṣṭhā variṣṭhāś ca kimarthaṃ nāvamaṃsyate</li>
    </ul>
    <ul id="R_2.055.012">
        <li id="R_2.055.012ab">na pareṇāhṛtaṃ bhakṣyaṃ vyāghraḥ khāditum icchati</li>
        <li id="R_2.055.012cd">evam eva naravyāghraḥ paralīḍhaṃ na maṃsyate</li>
    </ul>
    <ul id="R_2.055.013">
        <li id="R_2.055.013ab">havir ājyaṃ puroḍāśāḥ kuśā yūpāś ca khādirāḥ</li>
        <li id="R_2.055.013cd">naitāni yātayāmāni kurvanti punar adhvare</li>
    </ul>
    <ul id="R_2.055.014">
        <li id="R_2.055.014ab">tathā hy āttam idaṃ rājyaṃ hṛtasārāṃ surām iva</li>
        <li id="R_2.055.014cd">nābhimantum alaṃ rāmo naṣṭasomam ivādhvaram</li>
    </ul>
    <ul id="R_2.055.015">
        <li id="R_2.055.015ab">naivaṃvidham asatkāraṃ rāghavo marṣayiṣyati</li>
        <li id="R_2.055.015cd">balavān iva śārdūlo bāladher abhimarśanam</li>
    </ul>
    <ul id="R_2.055.016">
        <li id="R_2.055.016ab">sa tādṛśaḥ siṃhabalo vṛṣabhākṣo nararṣabhaḥ</li>
        <li id="R_2.055.016cd">svayam eva hataḥ pitrā jalajenātmajo yathā</li>
    </ul>
    <ul id="R_2.055.017">
        <li id="R_2.055.017ab">dvijāti carito dharmaḥ śāstradṛṣṭaḥ sanātanaḥ</li>
        <li id="R_2.055.017cd">yadi te dharmanirate tvayā putre vivāsite</li>
    </ul>
    <ul id="R_2.055.018">
        <li id="R_2.055.018ab">gatir evāk patir nāryā dvitīyā gatir ātmajaḥ</li>
        <li id="R_2.055.018cd">tṛtīyā jñātayo rājaṃś caturthī neha vidyate</li>
    </ul>
    <ul id="R_2.055.019">
        <li id="R_2.055.019ab">tatra tvaṃ caiva me nāsti rāmaś ca vanam āśritaḥ</li>
        <li id="R_2.055.019cd">na vanaṃ gantum icchāmi sarvathā hi hatā tvayā</li>
    </ul>
    <ul id="R_2.055.020">
        <li id="R_2.055.020ab">
            <seg type="pāda" n="a">hataṃ tvayā rājyam idaṃ sarāṣṭraṃ</seg>
            <seg type="pāda" n="b">hatas tathātmā saha mantribhiś ca</seg>
        </li>
        <li id="R_2.055.020cd">
            <seg type="pāda" n="c">hatā saputrāsmi hatāś ca paurāḥ</seg>
            <seg type="pāda" n="d">sutaś ca bhāryā ca tava prahṛṣṭau</seg>
        </li>
    </ul>
    <ul id="R_2.055.021">
        <li id="R_2.055.021ab">
            <seg type="pāda" n="a">imāṃ giraṃ dāruṇaśabdasaṃśritāṃ</seg>
            <seg type="pāda" n="b">niśamya rājāpi mumoha duḥkhitaḥ</seg>
        </li>
        <li id="R_2.055.021cd">
            <seg type="pāda" n="c">tataḥ sa śokaṃ praviveśa pārthivaḥ</seg>
            <seg type="pāda" n="d">svaduṣkṛtaṃ cāpi punas tadāsmarat</seg>
        </li>
    </ul>
    <ul id="R_2.056.001">
        <li id="R_2.056.001ab">evaṃ tu kruddhayā rājā rāmamātrā saśokayā</li>
        <li id="R_2.056.001cd">śrāvitaḥ paruṣaṃ vākyaṃ cintayām āsa duḥkhitaḥ</li>
    </ul>
    <ul id="R_2.056.002">
        <li id="R_2.056.002ab">tasya cintayamānasya pratyabhāt karma duṣkṛtam</li>
        <li id="R_2.056.002cd">yad anena kṛtaṃ pūrvam ajñānāc chabdavedhinā</li>
    </ul>
    <ul id="R_2.056.003">
        <li id="R_2.056.003ab">amanās tena śokena rāmaśokena ca prabhuḥ</li>
        <li id="R_2.056.003cd">dahyamānas tu śokābhyāṃ kausalyām āha bhūpatiḥ</li>
    </ul>
    <ul id="R_2.056.004">
        <li id="R_2.056.004ab">prasādaye tvāṃ kausalye racito 'yaṃ mayāñjaliḥ</li>
        <li id="R_2.056.004cd">vatsalā cānṛśaṃsā ca tvaṃ hi nityaṃ pareṣv api</li>
    </ul>
    <ul id="R_2.056.005">
        <li id="R_2.056.005ab">bhartā tu khalu nārīṇāṃ guṇavān nirguṇo 'pi vā</li>
        <li id="R_2.056.005cd">dharmaṃ vimṛśamānānāṃ pratyakṣaṃ devi daivatam</li>
    </ul>
    <ul id="R_2.056.006">
        <li id="R_2.056.006ab">sā tvaṃ dharmaparā nityaṃ dṛṣṭalokaparāvara</li>
        <li id="R_2.056.006cd">nārhase vipriyaṃ vaktuṃ duḥkhitāpi suduḥkhitam</li>
    </ul>
    <ul id="R_2.056.007">
        <li id="R_2.056.007ab">tad vākyaṃ karuṇaṃ rājñaḥ śrutvā dīnasya bhāṣitam</li>
        <li id="R_2.056.007cd">kausalyā vyasṛjad bāṣpaṃ praṇālīva navodakam</li>
    </ul>
    <ul id="R_2.056.008">
        <li id="R_2.056.008ab">sa mūdrhṇi baddhvā rudatī rājñaḥ padmam ivāñjalim</li>
        <li id="R_2.056.008cd">saṃbhramād abravīt trastā tvaramāṇākṣaraṃ vacaḥ</li>
    </ul>
    <ul id="R_2.056.009">
        <li id="R_2.056.009ab">prasīda śirasā yāce bhūmau nitatitāsmi te</li>
        <li id="R_2.056.009cd">yācitāsmi hatā deva hantavyāhaṃ na hi tvayā</li>
    </ul>
    <ul id="R_2.056.010">
        <li id="R_2.056.010ab">naiṣā hi sā strī bhavati ślāghanīyena dhīmatā</li>
        <li id="R_2.056.010cd">ubhayor lokayor vīra patyā yā saṃprasādyate</li>
    </ul>
    <ul id="R_2.056.011">
        <li id="R_2.056.011ab">jānāmi dharmaṃ dharmajña tvāṃ jāne satyavādinam</li>
        <li id="R_2.056.011cd">putraśokārtayā tat tu mayā kim api bhāṣitam</li>
    </ul>
    <ul id="R_2.056.012">
        <li id="R_2.056.012ab">śoko nāśayate dhairyaṃ śoko nāśayate śrutam</li>
        <li id="R_2.056.012cd">śoko nāśayate sarvaṃ nāsti śokasamo ripuḥ</li>
    </ul>
    <ul id="R_2.056.013">
        <li id="R_2.056.013ab">śayam āpatitaḥ soḍhuṃ praharo ripuhastataḥ</li>
        <li id="R_2.056.013cd">soḍhum āpatitaḥ śokaḥ susūkṣmo 'pi na śakyate</li>
    </ul>
    <ul id="R_2.056.014">
        <li id="R_2.056.014ab">vanavāsāya rāmasya pañcarātro 'dya gaṇyate</li>
        <li id="R_2.056.014cd">yaḥ śokahataharṣāyāḥ pañcavarṣopamo mama</li>
    </ul>
    <ul id="R_2.056.015">
        <li id="R_2.056.015ab">taṃ hi cintayamānāyāḥ śoko 'yaṃ hṛdi vardhate</li>
        <li id="R_2.056.015cd">adīnām iva vegena samudrasalilaṃ mahat</li>
    </ul>
    <ul id="R_2.056.016">
        <li id="R_2.056.016ab">evaṃ hi kathayantyās tu kausalyāyāḥ śubhaṃ vacaḥ</li>
        <li id="R_2.056.016cd">mandaraśmir abhūt suryo rajanī cābhyavartata</li>
    </ul>
    <ul id="R_2.056.017">
        <li id="R_2.056.017ab">atha prahlādito vākyair devyā kausalyayā nṛpaḥ</li>
        <li id="R_2.056.017cd">śokena ca samākrānto nidrāyā vaśam eyivān</li>
    </ul>
    <ul id="R_2.057.001">
        <li id="R_2.057.001ab">pratibuddho muhur tena śokopahatacetanaḥ</li>
        <li id="R_2.057.001cd">atha rājā daśarathaḥ sa cintām abhyapadyata</li>
    </ul>
    <ul id="R_2.057.002">
        <li id="R_2.057.002ab">rāmalakṣmaṇayoś caiva vivāsād vāsavopamam</li>
        <li id="R_2.057.002cd">āviveśopasargas taṃ tamaḥ sūryam ivāsuram</li>
    </ul>
    <ul id="R_2.057.003">
        <li id="R_2.057.003ab">sa rājā rajanīṃ ṣaṣṭhīṃ rāme pravrajite vanam</li>
        <li id="R_2.057.003cd">ardharātre daśarathaḥ saṃsmaran duṣkṛtaṃ kṛtam</li>
        <li id="R_2.057.003ef">kausalyāṃ putraśokārtām idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.057.004">
        <li id="R_2.057.004ab">yad ācarati kalyāṇi śubhaṃ vā yadi vāśubham</li>
        <li id="R_2.057.004cd">tad eva labhate bhadre kartā karmajam ātmanaḥ</li>
    </ul>
    <ul id="R_2.057.005">
        <li id="R_2.057.005ab">guru lāghavam arthānām ārambhe karmaṇāṃ phalam</li>
        <li id="R_2.057.005cd">doṣaṃ vā yo na jānāti sa bāla iti hocyate</li>
    </ul>
    <ul id="R_2.057.006">
        <li id="R_2.057.006ab">kaś cid āmravaṇaṃ chittvā palāśāṃś ca niṣiñcati</li>
        <li id="R_2.057.006cd">puṣpaṃ dṛṣṭvā phale gṛdhnuḥ sa śocati phalāgame</li>
    </ul>
    <ul id="R_2.057.007">
        <li id="R_2.057.007ab">so 'ham āmravaṇaṃ chittvā palāśāṃś ca nyaṣecayam</li>
        <li id="R_2.057.007cd">rāmaṃ phalāgame tyaktvā paścāc chocāmi durmatiḥ</li>
    </ul>
    <ul id="R_2.057.008">
        <li id="R_2.057.008ab">labdhaśabdena kausalye kumāreṇa dhanuṣmatā</li>
        <li id="R_2.057.008cd">kumāraḥ śabdavedhīti mayā pāpam idaṃ kṛtam</li>
        <li id="R_2.057.008ef">tad idaṃ me 'nusaṃprāptaṃ devi duḥkhaṃ svayaṃ kṛtam</li>
    </ul>
    <ul id="R_2.057.009">
        <li id="R_2.057.009ab">saṃmohād iha bālena yathā syād bhakṣitaṃ viṣam</li>
        <li id="R_2.057.009cd">evaṃ mamāpy avijñātaṃ śabdavedhyamayaṃ phalam</li>
    </ul>
    <ul id="R_2.057.010">
        <li id="R_2.057.010ab">devy anūḍhā tvam abhavo yuvarājo bhavāmy aham</li>
        <li id="R_2.057.010cd">tataḥ prāvṛḍ anuprāptā madakāmavivardhinī</li>
    </ul>
    <ul id="R_2.057.011">
        <li id="R_2.057.011ab">upāsyahi rasān bhaumāṃs taptvā ca jagad aṃśubhiḥ</li>
        <li id="R_2.057.011cd">paretācaritāṃ bhīmāṃ ravir āviśate diśam</li>
    </ul>
    <ul id="R_2.057.012">
        <li id="R_2.057.012ab">uṣṇam antardadhe sadyaḥ snigdhā dadṛśire ghanāḥ</li>
        <li id="R_2.057.012cd">tato jahṛṣire sarve bhekasāraṅgabarhiṇaḥ</li>
    </ul>
    <ul id="R_2.057.013">
        <li id="R_2.057.013ab">patitenāmbhasā channaḥ patamānena cāsakṛt</li>
        <li id="R_2.057.013cd">ābabhau mattasāraṅgas toyarāśir ivācalaḥ</li>
    </ul>
    <ul id="R_2.057.014">
        <li id="R_2.057.014ab">tasminn atisukhe kāle dhanuṣmān iṣumān rathī</li>
        <li id="R_2.057.014cd">vyāyāma kṛtasaṃkalpaḥ sarayūm anvagāṃ nadīm</li>
    </ul>
    <ul id="R_2.057.015">
        <li id="R_2.057.015ab">nipāne mahiṣaṃ rātrau gajaṃ vābhyāgataṃ nadīm</li>
        <li id="R_2.057.015cd">anyaṃ vā śvāpadaṃ kaṃ cij jighāṃsur ajitendriyaḥ</li>
    </ul>
    <ul id="R_2.057.016">
        <li id="R_2.057.016ab">athāndhakāre tv aśrauṣaṃ jale kumbhasya paryataḥ</li>
        <li id="R_2.057.016cd">acakṣur viṣaye ghoṣaṃ vāraṇasyeva nardataḥ</li>
    </ul>
    <ul id="R_2.057.017">
        <li id="R_2.057.017ab">tato 'haṃ śaram uddhṛtya dīptam āśīviṣopamam</li>
        <li id="R_2.057.017cd">amuñcaṃ niśitaṃ bāṇam aham āśīviṣopamam</li>
    </ul>
    <ul id="R_2.057.018">
        <li id="R_2.057.018ab">tatra vāg uṣasi vyaktā prādurāsīd vanaukasaḥ</li>
        <li id="R_2.057.018cd">hā heti patatas toye vāg abhūt tatra mānuṣī</li>
        <li id="R_2.057.018ef">katham asmadvidhe śastraṃ nipatet tu tapasvini</li>
    </ul>
    <ul id="R_2.057.019">
        <li id="R_2.057.019ab">praviviktāṃ nadīṃ rātrāv udāhāro 'ham āgataḥ</li>
        <li id="R_2.057.019cd">iṣuṇābhihataḥ kena kasya vā kiṃ kṛtaṃ mayā</li>
    </ul>
    <ul id="R_2.057.020">
        <li id="R_2.057.020ab">ṛṣer hi nyasta daṇḍasya vane vanyena jīvataḥ</li>
        <li id="R_2.057.020cd">kathaṃ nu śastreṇa vadho madvidhasya vidhīyate</li>
    </ul>
    <ul id="R_2.057.021">
        <li id="R_2.057.021ab">jaṭābhāradharasyaiva valkalājinavāsasaḥ</li>
        <li id="R_2.057.021cd">ko vadhena mamārthī syāt kiṃ vāsyāpakṛtaṃ mayā</li>
    </ul>
    <ul id="R_2.057.022">
        <li id="R_2.057.022ab">evaṃ niṣphalam ārabdhaṃ kevalānarthasaṃhitam</li>
        <li id="R_2.057.022cd">na kaś cit sādhu manyeta yathaiva gurutalpagam</li>
    </ul>
    <ul id="R_2.057.023">
        <li id="R_2.057.023ab">nemaṃ tathānuśocāmi jīvitakṣayam ātmanaḥ</li>
        <li id="R_2.057.023cd">mātaraṃ pitaraṃ cobhāv anuśocāmi madvidhe</li>
    </ul>
    <ul id="R_2.057.024">
        <li id="R_2.057.024ab">tad etān mithunaṃ vṛddhaṃ cirakālabhṛtaṃ mayā</li>
        <li id="R_2.057.024cd">mayi pañcatvam āpanne kāṃ vṛttiṃ vartayiṣyati</li>
    </ul>
    <ul id="R_2.057.025">
        <li id="R_2.057.025ab">vṛddhau ca mātāpitarāv ahaṃ caikeṣuṇā hataḥ</li>
        <li id="R_2.057.025cd">kena sma nihatāḥ sarve subālenākṛtātmanā</li>
    </ul>
    <ul id="R_2.057.026">
        <li id="R_2.057.026ab">taṃ giraṃ karuṇāṃ śrutvā mama dharmānukāṅkṣiṇaḥ</li>
        <li id="R_2.057.026cd">karābhyāṃ saśaraṃ cāpaṃ vyathitasyāpatad bhuvi</li>
    </ul>
    <ul id="R_2.057.027">
        <li id="R_2.057.027ab">taṃ deśam aham āgamya dīnasattvaḥ sudurmanāḥ</li>
        <li id="R_2.057.027cd">apaśyam iṣuṇā tīre sarayvās tāpasaṃ hatam</li>
    </ul>
    <ul id="R_2.057.028">
        <li id="R_2.057.028ab">sa mām udvīkṣya netrābhyāṃ trastam asvasthacetasaṃ</li>
        <li id="R_2.057.028cd">ity uvāca vacaḥ krūraṃ didhakṣann iva tejasā</li>
    </ul>
    <ul id="R_2.057.029">
        <li id="R_2.057.029ab">kiṃ tavāpakṛtaṃ rājan vane nivasatā mayā</li>
        <li id="R_2.057.029cd">jihīrṣur ambho gurvarthaṃ yad ahaṃ tāḍitas tvayā</li>
    </ul>
    <ul id="R_2.057.030">
        <li id="R_2.057.030ab">ekena khalu bāṇena marmaṇy abhihate mayi</li>
        <li id="R_2.057.030cd">dvāv andhau nihatau vṛddhau mātā janayitā ca me</li>
    </ul>
    <ul id="R_2.057.031">
        <li id="R_2.057.031ab">tau nūnaṃ durbalāv andhau matpratīkṣau pipāsitau</li>
        <li id="R_2.057.031cd">ciram āśākṛtāṃ tṛṣṇāṃ kaṣṭāṃ saṃdhārayiṣyataḥ</li>
    </ul>
    <ul id="R_2.057.032">
        <li id="R_2.057.032ab">na nūnaṃ tapaso vāsti phalayogaḥ śrutasya vā</li>
        <li id="R_2.057.032cd">pitā yan māṃ na jānāti śayānaṃ patitaṃ bhuvi</li>
    </ul>
    <ul id="R_2.057.033">
        <li id="R_2.057.033ab">jānann api ca kiṃ kuryād aśaktir aparikramaḥ</li>
        <li id="R_2.057.033cd">bhidyamānam ivāśaktas trātum anyo nago nagam</li>
    </ul>
    <ul id="R_2.057.034">
        <li id="R_2.057.034ab">pitus tvam eva me gatvā śīghram ācakṣva rāghava</li>
        <li id="R_2.057.034cd">na tvām anudahet kruddho vanaṃ vahnir ivaidhitaḥ</li>
    </ul>
    <ul id="R_2.057.035">
        <li id="R_2.057.035ab">iyam ekapadī rājan yato me pitur āśramaḥ</li>
        <li id="R_2.057.035cd">taṃ prasādaya gatvā tvaṃ na tvāṃ sa kupitaḥ śapet</li>
    </ul>
    <ul id="R_2.057.036">
        <li id="R_2.057.036ab">viśalyaṃ kuru māṃ rājan marma me niśitaḥ śaraḥ</li>
        <li id="R_2.057.036cd">ruṇaddhi mṛdu sotsedhaṃ tīram amburayo yathā</li>
    </ul>
    <ul id="R_2.057.037">
        <li id="R_2.057.037ab">na dvijātir ahaṃ rājan mā bhūt te manaso vyathā</li>
        <li id="R_2.057.037cd">śūdrāyām asmi vaiśyena jāto janapadādhipa</li>
    </ul>
    <ul id="R_2.057.038">
        <li id="R_2.057.038ab">itīva vadataḥ kṛcchrād bāṇābhihatamarmaṇaḥ</li>
        <li id="R_2.057.038cd">tasya tv ānamyamānasya taṃ bāṇam aham uddharam</li>
    </ul>
    <ul id="R_2.057.039">
        <li id="R_2.057.039ab">
            <seg type="pāda" n="a">jalārdragātraṃ tu vilapya kṛcchān</seg>
            <seg type="pāda" n="b">marmavraṇaṃ saṃtatam ucchasantam</seg>
        </li>
        <li id="R_2.057.039cd">
            <seg type="pāda" n="c">tataḥ sarayvāṃ tam ahaṃ śayānaṃ</seg>
            <seg type="pāda" n="d">samīkṣya bhadre subhṛśaṃ viṣaṇṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.058.001">
        <li id="R_2.058.001ab">tad ajñānān mahat pāpaṃ kṛtvā saṃkulitendriyaḥ</li>
        <li id="R_2.058.001cd">ekas tv acintayaṃ buddhyā kathaṃ nu sukṛtaṃ bhavet</li>
    </ul>
    <ul id="R_2.058.002">
        <li id="R_2.058.002ab">tatas taṃ ghaṭam ādaya pūrṇaṃ paramavāriṇā</li>
        <li id="R_2.058.002cd">āśramaṃ tam ahaṃ prāpya yathākhyātapathaṃ gataḥ</li>
    </ul>
    <ul id="R_2.058.003">
        <li id="R_2.058.003ab">tatrāhaṃ durbalāv andhau vṛddhāv apariṇāyakau</li>
        <li id="R_2.058.003cd">apaśyaṃ tasya pitarau lūnapakṣāv iva dvijau</li>
    </ul>
    <ul id="R_2.058.004">
        <li id="R_2.058.004ab">tannimittābhir āsīnau kathābhir aparikramau</li>
        <li id="R_2.058.004cd">tām āśāṃ matkṛte hīnāv udāsīnāv anāthavat</li>
    </ul>
    <ul id="R_2.058.005">
        <li id="R_2.058.005ab">padaśabdaṃ tu me śrutvā munir vākyam abhāṣata</li>
        <li id="R_2.058.005cd">kiṃ cirāyasi me putra pānīyaṃ kṣipram ānaya</li>
    </ul>
    <ul id="R_2.058.006">
        <li id="R_2.058.006ab">yannimittam idaṃ tāta salile krīḍitaṃ tvayā</li>
        <li id="R_2.058.006cd">utkaṇṭhitā te māteyaṃ praviśa kṣipram āśramam</li>
    </ul>
    <ul id="R_2.058.007">
        <li id="R_2.058.007ab">yad vyalīkaṃ kṛtaṃ putra mātrā te yadi vā mayā</li>
        <li id="R_2.058.007cd">na tan manasi kartavyaṃ tvayā tāta tapasvinā</li>
    </ul>
    <ul id="R_2.058.008">
        <li id="R_2.058.008ab">tvaṃ gatis tv agatīnāṃ ca cakṣus tvaṃ hīnacakṣuṣām</li>
        <li id="R_2.058.008cd">samāsaktās tvayi prāṇāḥ kiṃ cin nau nābhibhāṣase</li>
    </ul>
    <ul id="R_2.058.009">
        <li id="R_2.058.009ab">munim avyaktayā vācā tam ahaṃ sajjamānayā</li>
        <li id="R_2.058.009cd">hīnavyañjanayā prekṣya bhīto bhīta ivābruvam</li>
    </ul>
    <ul id="R_2.058.010">
        <li id="R_2.058.010ab">manasaḥ karma ceṣṭābhir abhisaṃstabhya vāgbalam</li>
        <li id="R_2.058.010cd">ācacakṣe tv ahaṃ tasmai putravyasanajaṃ bhayam</li>
    </ul>
    <ul id="R_2.058.011">
        <li id="R_2.058.011ab">kṣatriyo 'haṃ daśaratho nāhaṃ putro mahātmanaḥ</li>
        <li id="R_2.058.011cd">sajjanāvamataṃ duḥkham idaṃ prāptaṃ svakarmajam</li>
    </ul>
    <ul id="R_2.058.012">
        <li id="R_2.058.012ab">bhagavaṃś cāpahasto 'haṃ sarayūtīram āgataḥ</li>
        <li id="R_2.058.012cd">jighāṃsuḥ śvāpadaṃ kiṃ cin nipāne vāgataṃ gajam</li>
    </ul>
    <ul id="R_2.058.013">
        <li id="R_2.058.013ab">tatra śruto mayā śabdo jale kumbhasya pūryataḥ</li>
        <li id="R_2.058.013cd">dvipo 'yam iti matvā hi bāṇenābhihato mayā</li>
    </ul>
    <ul id="R_2.058.014">
        <li id="R_2.058.014ab">gatvā nadyās tatas tīram apaśyam iṣuṇā hṛdi</li>
        <li id="R_2.058.014cd">vinirbhinnaṃ gataprāṇaṃ śayānaṃ bhuvi tāpasaṃ</li>
    </ul>
    <ul id="R_2.058.015">
        <li id="R_2.058.015ab">bhagavañ śabdam ālakṣya mayā gajajighāṃsunā</li>
        <li id="R_2.058.015cd">visṛṣṭo 'mbhasi nārācas tena te nihataḥ sutaḥ</li>
    </ul>
    <ul id="R_2.058.016">
        <li id="R_2.058.016ab">sa coddhṛtena bāṇena tatraiva svargam āsthitaḥ</li>
        <li id="R_2.058.016cd">bhagavantāv ubhau śocann andhāv iti vilapya ca</li>
    </ul>
    <ul id="R_2.058.017">
        <li id="R_2.058.017ab">ajñānād bhavataḥ putraḥ sahasābhihato mayā</li>
        <li id="R_2.058.017cd">śeṣam evaṃgate yat syāt tat prasīdatu me muniḥ</li>
    </ul>
    <ul id="R_2.058.018">
        <li id="R_2.058.018ab">sa tac chrutvā vacaḥ krūraṃ niḥśvasañ śokakarśitaḥ</li>
        <li id="R_2.058.018cd">mām uvāca mahātejāḥ kṛtāñjalim upasthitam</li>
    </ul>
    <ul id="R_2.058.019">
        <li id="R_2.058.019ab">yady etad aśubhaṃ karma na sma me kathayeḥ svayam</li>
        <li id="R_2.058.019cd">phalen mūrdhā sma te rājan sadyaḥ śatasahasradhā</li>
    </ul>
    <ul id="R_2.058.020">
        <li id="R_2.058.020ab">kṣatriyeṇa vadho rājan vānaprasthe viśeṣataḥ</li>
        <li id="R_2.058.020cd">jñānapūrvaṃ kṛtaḥ sthānāc cyāvayed api vajriṇam</li>
    </ul>
    <ul id="R_2.058.021">
        <li id="R_2.058.021ab">ajñānād dhi kṛtaṃ yasmād idaṃ tenaiva jīvasi</li>
        <li id="R_2.058.021cd">api hy adya kulaṃ nasyād rāghavāṇāṃ kuto bhavān</li>
    </ul>
    <ul id="R_2.058.022">
        <li id="R_2.058.022ab">naya nau nṛpa taṃ deśam iti māṃ cābhyabhāṣata</li>
        <li id="R_2.058.022cd">adya taṃ draṣṭum icchāvaḥ putraṃ paścimadarśanam</li>
    </ul>
    <ul id="R_2.058.023">
        <li id="R_2.058.023ab">rudhireṇāvasitāṅgaṃ prakīrṇājinavāsasaṃ</li>
        <li id="R_2.058.023cd">śayānaṃ bhuvi niḥsaṃjñaṃ dharmarājavaśaṃ gatam</li>
    </ul>
    <ul id="R_2.058.024">
        <li id="R_2.058.024ab">athāham ekas taṃ deśaṃ nītvā tau bhṛśaduḥkhitau</li>
        <li id="R_2.058.024cd">asparśayam ahaṃ putraṃ taṃ muniṃ saha bhāryayā</li>
    </ul>
    <ul id="R_2.058.025">
        <li id="R_2.058.025ab">tau putram ātmanaḥ spṛṣṭvā tam āsādya tapasvinau</li>
        <li id="R_2.058.025cd">nipetatuḥ śarīre 'sya pitā cāsyedam abravīt</li>
    </ul>
    <ul id="R_2.058.026">
        <li id="R_2.058.026ab">na nv ahaṃ te priyaḥ putra mātaraṃ paśya dhārmika</li>
        <li id="R_2.058.026cd">kiṃ nu nāliṅgase putra sukumāra vaco vada</li>
    </ul>
    <ul id="R_2.058.027">
        <li id="R_2.058.027ab">kasya vāpararātre 'haṃ śroṣyāmi hṛdayaṃgamam</li>
        <li id="R_2.058.027cd">adhīyānasya madhuraṃ śāstraṃ vānyad viśeṣataḥ</li>
    </ul>
    <ul id="R_2.058.028">
        <li id="R_2.058.028ab">ko māṃ saṃdhyām upāsyaiva snātvā hutahutāśanaḥ</li>
        <li id="R_2.058.028cd">ślāghayiṣyaty upāsīnaḥ putraśokabhayārditam</li>
    </ul>
    <ul id="R_2.058.029">
        <li id="R_2.058.029ab">kandamūlaphalaṃ hṛtvā ko māṃ priyam ivātithim</li>
        <li id="R_2.058.029cd">bhojayiṣyaty akarmaṇyam apragraham anāyakam</li>
    </ul>
    <ul id="R_2.058.030">
        <li id="R_2.058.030ab">imām andhāṃ ca vṛddhāṃ ca mātaraṃ te tapasvinīm</li>
        <li id="R_2.058.030cd">kathaṃ putra bhariṣyāmi kṛpaṇāṃ putragardhinīm</li>
    </ul>
    <ul id="R_2.058.031">
        <li id="R_2.058.031ab">tiṣṭha mā mā gamaḥ putra yamasya sadanaṃ prati</li>
        <li id="R_2.058.031cd">śvo mayā saha gantāsi jananyā ca samedhitaḥ</li>
    </ul>
    <ul id="R_2.058.032">
        <li id="R_2.058.032ab">ubhāv api ca śokārtāv anāthau kṛpaṇau vane</li>
        <li id="R_2.058.032cd">kṣipram eva gamiṣyāvas tvayā hīnau yamakṣayam</li>
    </ul>
    <ul id="R_2.058.033">
        <li id="R_2.058.033ab">tato vaivasvataṃ dṛṣṭvā taṃ pravakṣyāmi bhāratīm</li>
        <li id="R_2.058.033cd">kṣamatāṃ dharmarājo me bibhṛyāt pitarāv ayam</li>
    </ul>
    <ul id="R_2.058.034">
        <li id="R_2.058.034ab">apāpo 'si yathā putra nihataḥ pāpakarmaṇā</li>
        <li id="R_2.058.034cd">tena satyena gacchāśu ye lokāḥ śastrayodhinām</li>
    </ul>
    <ul id="R_2.058.035">
        <li id="R_2.058.035ab">yānti śūrā gatiṃ yāṃ ca saṃgrāmeṣv anivartinaḥ</li>
        <li id="R_2.058.035cd">hatās tv abhimukhāḥ putra gatiṃ tāṃ paramāṃ vraja</li>
    </ul>
    <ul id="R_2.058.036">
        <li id="R_2.058.036ab">yāṃ gatiṃ sagaraḥ śaibyo dilīpo janamejayaḥ</li>
        <li id="R_2.058.036cd">nahuṣo dhundhumāraś ca prāptās tāṃ gaccha putraka</li>
    </ul>
    <ul id="R_2.058.037">
        <li id="R_2.058.037ab">yā gatiḥ sarvasādhūnāṃ svādhyāyāt patasaś ca yā</li>
        <li id="R_2.058.037cd">bhūmidasyāhitāgneś ca ekapatnīvratasya ca</li>
    </ul>
    <ul id="R_2.058.038">
        <li id="R_2.058.038ab">gosahasrapradātṝṇāṃ yā yā gurubhṛtām api</li>
        <li id="R_2.058.038cd">dehanyāsakṛtāṃ yā ca tāṃ gatiṃ gaccha putraka</li>
        <li id="R_2.058.038ef">na hi tv asmin kule jāto gacchaty akuśalāṃ gatim</li>
    </ul>
    <ul id="R_2.058.039">
        <li id="R_2.058.039ab">evaṃ sa kṛpaṇaṃ tatra paryadevayatāsakṛt</li>
        <li id="R_2.058.039cd">tato 'smai kartum udakaṃ pravṛttaḥ saha bhāryayā</li>
    </ul>
    <ul id="R_2.058.040">
        <li id="R_2.058.040ab">sa tu divyena rūpeṇa muniputraḥ svakarmabhiḥ</li>
        <li id="R_2.058.040cd">āśvāsya ca muhūrtaṃ tu pitarau vākyam abravīt</li>
    </ul>
    <ul id="R_2.058.041">
        <li id="R_2.058.041ab">sthānam asmi mahat prāpto bhavatoḥ paricāraṇāt</li>
        <li id="R_2.058.041cd">bhavantāv api ca kṣipraṃ mama mūlam upaiṣyataḥ</li>
    </ul>
    <ul id="R_2.058.042">
        <li id="R_2.058.042ab">evam uktvā tu divyena vimānena vapuṣmatā</li>
        <li id="R_2.058.042cd">āruroha divaṃ kṣipraṃ muniputro jitendriyaḥ</li>
    </ul>
    <ul id="R_2.058.043">
        <li id="R_2.058.043ab">sa kṛtvā tūdakaṃ tūrṇaṃ tāpasaḥ saha bhāryayā</li>
        <li id="R_2.058.043cd">mām uvāca mahātejāḥ kṛtāñjalim upasthitam</li>
    </ul>
    <ul id="R_2.058.044">
        <li id="R_2.058.044ab">adyaiva jahi māṃ rājan maraṇe nāsti me vyathā</li>
        <li id="R_2.058.044cd">yac chareṇaikaputraṃ māṃ tvam akārṣīr aputrakam</li>
    </ul>
    <ul id="R_2.058.045">
        <li id="R_2.058.045ab">tvayā tu yad avijñānān nihato me sutaḥ śuciḥ</li>
        <li id="R_2.058.045cd">tena tvām abhiśapsyāmi suduḥkham atidāruṇam</li>
    </ul>
    <ul id="R_2.058.046">
        <li id="R_2.058.046ab">putravyasanajaṃ duḥkhaṃ yad etan mama sāmpratam</li>
        <li id="R_2.058.046cd">evaṃ tvaṃ putraśokena rājan kālaṃ kariṣyasi</li>
    </ul>
    <ul id="R_2.058.047">
        <li id="R_2.058.047ab">tasmān mām āgataṃ bhadre tasyodārasya tad vacaḥ</li>
        <li id="R_2.058.047cd">yad ahaṃ putraśokena saṃtyakṣyāmy adya jīvitam</li>
    </ul>
    <ul id="R_2.058.048">
        <li id="R_2.058.048ab">yadi māṃ saṃspṛśed rāmaḥ sakṛd adyālabheta vā</li>
        <li id="R_2.058.048cd">na tan me sadṛśaṃ devi yan mayā rāghave kṛtam</li>
    </ul>
    <ul id="R_2.058.049">
        <li id="R_2.058.049ab">cakṣuṣā tvāṃ na paśyāmi smṛtir mama vilupyate</li>
        <li id="R_2.058.049cd">dūtā vaivasvatasyaite kausalye tvarayanti mām</li>
    </ul>
    <ul id="R_2.058.050">
        <li id="R_2.058.050ab">atas tu kiṃ duḥkhataraṃ yad ahaṃ jīvitakṣaye</li>
        <li id="R_2.058.050cd">na hi paśyāmi dharmajñaṃ rāmaṃ satyaparākyamam</li>
    </ul>
    <ul id="R_2.058.051">
        <li id="R_2.058.051ab">na te manuṣyā devās te ye cāruśubhakuṇḍalam</li>
        <li id="R_2.058.051cd">mukhaṃ drakṣyanti rāmasya varṣe pañcadaśe punaḥ</li>
    </ul>
    <ul id="R_2.058.052">
        <li id="R_2.058.052ab">padmapatrekṣaṇaṃ subhru sudaṃṣṭraṃ cārunāsikam</li>
        <li id="R_2.058.052cd">dhanyā drakṣyanti rāmasya tārādhipanibhaṃ mukham</li>
    </ul>
    <ul id="R_2.058.053">
        <li id="R_2.058.053ab">sadṛśaṃ śāradasyendoḥ phullasya kamalasya ca</li>
        <li id="R_2.058.053cd">sugandhi mama nāthasya dhanyā drakṣyanti tanmukham</li>
    </ul>
    <ul id="R_2.058.054">
        <li id="R_2.058.054ab">nivṛttavanavāsaṃ tam ayodhyāṃ punar āgatam</li>
        <li id="R_2.058.054cd">drakṣyanti sukhino rāmaṃ śukraṃ mārgagataṃ yathā</li>
    </ul>
    <ul id="R_2.058.055">
        <li id="R_2.058.055ab">ayam ātmabhavaḥ śoko mām anātham acetanam</li>
        <li id="R_2.058.055cd">saṃsādayati vegena yathā kūlaṃ nadīrayaḥ</li>
    </ul>
    <ul id="R_2.058.056">
        <li id="R_2.058.056ab">hā rāghava mahābāho hā mamāyāsa nāśana</li>
        <li id="R_2.058.056cd">rājā daśarathaḥ śocañ jīvitāntam upāgamat</li>
    </ul>
    <ul id="R_2.058.057">
        <li id="R_2.058.057ab">
            <seg type="pāda" n="a">tathā tu dīnaṃ kathayan narādhipaḥ</seg>
            <seg type="pāda" n="b">priyasya putrasya vivāsanāturaḥ</seg>
        </li>
        <li id="R_2.058.057cd">
            <seg type="pāda" n="c">gate 'rdharātre bhṛśaduḥkhapīḍitas</seg>
            <seg type="pāda" n="d">tadā jahau prāṇam udāradarśanaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.059.001">
        <li id="R_2.059.001ab">atha rātryāṃ vyatītāyāṃ prātar evāpare 'hani</li>
        <li id="R_2.059.001cd">bandinaḥ paryupātiṣṭhaṃs tat pārthivaniveśanam</li>
    </ul>
    <ul id="R_2.059.002">
        <li id="R_2.059.002ab">tataḥ śucisamācārāḥ paryupasthāna kovidaḥ</li>
        <li id="R_2.059.002cd">strīvarṣavarabhūyiṣṭhā upatasthur yathāpuram</li>
    </ul>
    <ul id="R_2.059.003">
        <li id="R_2.059.003ab">haricandanasaṃpṛktam udakaṃ kāñcanair ghaṭaiḥ</li>
        <li id="R_2.059.003cd">āninyuḥ snānaśikṣājñā yathākālaṃ yathāvidhi</li>
    </ul>
    <ul id="R_2.059.004">
        <li id="R_2.059.004ab">maṅgalālambhanīyāni prāśanīyān upaskarān</li>
        <li id="R_2.059.004cd">upaninyus tathāpy anyāḥ kumārī bahulāḥ striyaḥ</li>
    </ul>
    <ul id="R_2.059.005">
        <li id="R_2.059.005ab">atha yāḥ kosalendrasya śayanaṃ pratyanantarāḥ</li>
        <li id="R_2.059.005cd">tāḥ striyas tu samāgamya bhartāraṃ pratyabodhayan</li>
    </ul>
    <ul id="R_2.059.006">
        <li id="R_2.059.006ab">tā vepathuparītāś ca rājñaḥ prāṇeṣu śaṅkitāḥ</li>
        <li id="R_2.059.006cd">pratisrotas tṛṇāgrāṇāṃ sadṛśaṃ saṃcakampire</li>
    </ul>
    <ul id="R_2.059.007">
        <li id="R_2.059.007ab">atha saṃvepamanānāṃ strīṇāṃ dṛṣṭvā ca pārthivam</li>
        <li id="R_2.059.007cd">yat tad āśaṅkitaṃ pāpaṃ tasya jajñe viniścayaḥ</li>
    </ul>
    <ul id="R_2.059.008">
        <li id="R_2.059.008ab">tataḥ pracukruśur dīnāḥ sasvaraṃ tā varāṅganāḥ</li>
        <li id="R_2.059.008cd">kareṇava ivāraṇye sthānapracyutayūthapāḥ</li>
    </ul>
    <ul id="R_2.059.009">
        <li id="R_2.059.009ab">tāsām ākranda śabdena sahasodgatacetane</li>
        <li id="R_2.059.009cd">kausalyā ca sumitrāca tyaktanidre babhūvatuḥ</li>
    </ul>
    <ul id="R_2.059.010">
        <li id="R_2.059.010ab">kausalyā ca sumitrā ca dṛṣṭvā spṛṣṭvā ca pārthivam</li>
        <li id="R_2.059.010cd">hā nātheti parikruśya petatur dharaṇītale</li>
    </ul>
    <ul id="R_2.059.011">
        <li id="R_2.059.011ab">sā kosalendraduhitā veṣṭamānā mahītale</li>
        <li id="R_2.059.011cd">na babhrāja rajodhvastā tāreva gaganacyutā</li>
    </ul>
    <ul id="R_2.059.012">
        <li id="R_2.059.012ab">tat samuttrastasaṃbhrāntaṃ paryutsukajanākulam</li>
        <li id="R_2.059.012cd">sarvatas tumulākrandaṃ paritāpārtabāndhavam</li>
    </ul>
    <ul id="R_2.059.013">
        <li id="R_2.059.013ab">sadyo nipatitānandaṃ dīnaviklavadarśanam</li>
        <li id="R_2.059.013cd">babhūva naradevasya sadma diṣṭāntam īyuṣaḥ</li>
    </ul>
    <ul id="R_2.059.014">
        <li id="R_2.059.014ab">
            <seg type="pāda" n="a">atītam ājñāya tu pārthivarṣabhaṃ</seg>
            <seg type="pāda" n="b">yaśasvinaṃ saṃparivārya patnayaḥ</seg>
        </li>
        <li id="R_2.059.014cd">
            <seg type="pāda" n="c">bhṛśaṃ rudantyaḥ karuṇaṃ suduḥkhitāḥ</seg>
            <seg type="pāda" n="d">pragṛhya bāhū vyalapann anāthavat</seg>
        </li>
    </ul>
    <ul id="R_2.060.001">
        <li id="R_2.060.001ab">tam agnim iva saṃśāntam ambuhīnam ivārṇavam</li>
        <li id="R_2.060.001cd">hataprabham ivādityaṃ svargathaṃ prekṣya bhūmipam</li>
    </ul>
    <ul id="R_2.060.002">
        <li id="R_2.060.002ab">kausalyā bāṣpapūrṇākṣī vividhaṃ śokakarśitā</li>
        <li id="R_2.060.002cd">upagṛhya śiro rājñaḥ kaikeyīṃ pratyabhāṣata</li>
    </ul>
    <ul id="R_2.060.003">
        <li id="R_2.060.003ab">sakāmā bhava kaikeyi bhuṅkṣva rājyam akaṇṭakam</li>
        <li id="R_2.060.003cd">tyaktvā rājānam ekāgrā nṛśaṃse duṣṭacāriṇi</li>
    </ul>
    <ul id="R_2.060.004">
        <li id="R_2.060.004ab">vihāya māṃ gato rāmo bhartā ca svargato mama</li>
        <li id="R_2.060.004cd">vipathe sārthahīneva nāhaṃ jīvitum utsahe</li>
    </ul>
    <ul id="R_2.060.005">
        <li id="R_2.060.005ab">bhartāraṃ taṃ parityajya kā strī daivatam ātmanaḥ</li>
        <li id="R_2.060.005cd">icchej jīvitum anyatra kaikeyyās tyaktadharmaṇaḥ</li>
    </ul>
    <ul id="R_2.060.006">
        <li id="R_2.060.006ab">na lubdho budhyate doṣān kiṃ pākam iva bhakṣayan</li>
        <li id="R_2.060.006cd">kubjānimittaṃ kaikeyyā rāghavāṇān kulaṃ hatam</li>
    </ul>
    <ul id="R_2.060.007">
        <li id="R_2.060.007ab">aniyoge niyuktena rājñā rāmaṃ vivāsitam</li>
        <li id="R_2.060.007cd">sabhāryaṃ janakaḥ śrutvā paritapsyaty ahaṃ yathā</li>
    </ul>
    <ul id="R_2.060.008">
        <li id="R_2.060.008ab">rāmaḥ kamalapatrākṣo jīvanāśam ito gataḥ</li>
        <li id="R_2.060.008cd">videharājasya sutā tahā sītā tapasvinī</li>
        <li id="R_2.060.008ef">duḥkhasyānucitā duḥkhaṃ vane paryudvijiṣyati</li>
    </ul>
    <ul id="R_2.060.009">
        <li id="R_2.060.009ab">nadatāṃ bhīmaghoṣāṇāṃ niśāsu mṛgapakṣiṇām</li>
        <li id="R_2.060.009cd">niśamya nūnaṃ saṃstrastā rāghavaṃ saṃśrayiṣyati</li>
    </ul>
    <ul id="R_2.060.010">
        <li id="R_2.060.010ab">vṛddhaś caivālpaputraś ca vaidehīm anicintayan</li>
        <li id="R_2.060.010cd">so 'pi śokasamāviṣṭo nanu tyakṣyati jīvitam</li>
    </ul>
    <ul id="R_2.060.011">
        <li id="R_2.060.011ab">tāṃ tataḥ saṃpariṣvajya vilapantīṃ tapasvinīm</li>
        <li id="R_2.060.011cd">vyapaninyuḥ suduḥkhārtāṃ kausalyāṃ vyāvahārikāḥ</li>
    </ul>
    <ul id="R_2.060.012">
        <li id="R_2.060.012ab">tailadroṇyām athāmātyāḥ saṃveśya jagatīpatim</li>
        <li id="R_2.060.012cd">rājñaḥ sarvāṇy athādiṣṭāś cakruḥ karmāṇy anantaram</li>
    </ul>
    <ul id="R_2.060.013">
        <li id="R_2.060.013ab">na tu saṃkalanaṃ rājño vinā putreṇa mantriṇaḥ</li>
        <li id="R_2.060.013cd">sarvajñāḥ kartum īṣus te tato rakṣanti bhūmipam</li>
    </ul>
    <ul id="R_2.060.014">
        <li id="R_2.060.014ab">tailadroṇyāṃ tu sacivaiḥ śāyitaṃ taṃ narādhipam</li>
        <li id="R_2.060.014cd">hā mṛto 'yam iti jñātvā striyas tāḥ paryadevayan</li>
    </ul>
    <ul id="R_2.060.015">
        <li id="R_2.060.015ab">bāhūn udyamya kṛpaṇā netraprasravaṇair mukhaiḥ</li>
        <li id="R_2.060.015cd">rudantyaḥ śokasaṃtaptāḥ kṛpaṇaṃ paryadevayan</li>
    </ul>
    <ul id="R_2.060.016">
        <li id="R_2.060.016ab">niśānakṣatrahīneva strīva bhartṛvivarjitā</li>
        <li id="R_2.060.016cd">purī nārājatāyodhyā hīnā rājñā mahātmanā</li>
    </ul>
    <ul id="R_2.060.017">
        <li id="R_2.060.017ab">bāṣpaparyākulajanā hāhābhūtakulāṅganā</li>
        <li id="R_2.060.017cd">śūnyacatvaraveśmāntā na babhrāja yathāpuram</li>
    </ul>
    <ul id="R_2.060.018">
        <li id="R_2.060.018ab">
            <seg type="pāda" n="a">gataprabhā dyaur iva bhāskaraṃ vinā</seg>
            <seg type="pāda" n="b">vyapetanakṣatragaṇeva śarvarī</seg>
        </li>
        <li id="R_2.060.018cd">
            <seg type="pāda" n="c">purī babhāse rahitā mahātmanā</seg>
            <seg type="pāda" n="d">na cāsrakaṇṭhākulamārgacatvarā</seg>
        </li>
    </ul>
    <ul id="R_2.060.019">
        <li id="R_2.060.019ab">
            <seg type="pāda" n="a">narāś ca nāryaś ca sametya saṃghaśo</seg>
            <seg type="pāda" n="b">vigarhamāṇā bharatasya mātaram</seg>
        </li>
        <li id="R_2.060.019cd">
            <seg type="pāda" n="c">tadā nagaryāṃ naradevasaṃkṣaye</seg>
            <seg type="pāda" n="d">babhūvur ārtā na ca śarma lebhire</seg>
        </li>
    </ul>
    <ul id="R_2.061.001">
        <li id="R_2.061.001ab">vyatītāyāṃ tu śarvaryām ādityasyodaye tataḥ</li>
        <li id="R_2.061.001cd">sametya rājakartāraḥ sabhām īyur dvijātayaḥ</li>
    </ul>
    <ul id="R_2.061.002">
        <li id="R_2.061.002ab">mārkaṇḍeyo 'tha maudgalyo vāmadevaś ca kāśyapaḥ</li>
        <li id="R_2.061.002cd">kātyayano gautamaś ca jābāliś ca mahāyaśāḥ</li>
    </ul>
    <ul id="R_2.061.003">
        <li id="R_2.061.003ab">ete dvijāḥ sahāmātyaiḥ pṛthag vācam udīrayan</li>
        <li id="R_2.061.003cd">vasiṣṭham evābhimukhāḥ śreṣṭho rājapurohitam</li>
    </ul>
    <ul id="R_2.061.004">
        <li id="R_2.061.004ab">atītā śarvarī duḥkhaṃ yā no varṣaśatopamā</li>
        <li id="R_2.061.004cd">asmin pañcatvam āpanne putraśokena pārthive</li>
    </ul>
    <ul id="R_2.061.005">
        <li id="R_2.061.005ab">svargataś ca mahārājo rāmaś cāraṇyam āśritaḥ</li>
        <li id="R_2.061.005cd">lakṣmaṇaś cāpi tejasvī rāmeṇaiva gataḥ saha</li>
    </ul>
    <ul id="R_2.061.006">
        <li id="R_2.061.006ab">ubhau bharataśatrughnau kkekayeṣu paraṃtapau</li>
        <li id="R_2.061.006cd">pure rājagṛhe ramye mātāmahaniveśane</li>
    </ul>
    <ul id="R_2.061.007">
        <li id="R_2.061.007ab">ikṣvākūṇām ihādyaiva kaś cid rājā vidhīyatām</li>
        <li id="R_2.061.007cd">arājakaṃ hi no rāṣṭraṃ na vināśam avāpnuyāt</li>
    </ul>
    <ul id="R_2.061.008">
        <li id="R_2.061.008ab">nārājale janapade vidyunmālī mahāsvanaḥ</li>
        <li id="R_2.061.008cd">abhivarṣati parjanyo mahīṃ divyena vāriṇā</li>
    </ul>
    <ul id="R_2.061.009">
        <li id="R_2.061.009ab">nārājake janapade bījamuṣṭiḥ prakīryate</li>
        <li id="R_2.061.009cd">nārākake pituḥ putro bhāryā vā vartate vaśe</li>
    </ul>
    <ul id="R_2.061.010">
        <li id="R_2.061.010ab">arājake dhanaṃ nāsti nāsti bhāryāpy arājake</li>
        <li id="R_2.061.010cd">idam atyāhitaṃ cānyat kutaḥ satyam arājake</li>
    </ul>
    <ul id="R_2.061.011">
        <li id="R_2.061.011ab">nārājake janapade kārayanti sabhāṃ narāḥ</li>
        <li id="R_2.061.011cd">udyānāni ca ramyāṇi hṛṣṭāḥ puṇyagṛhāṇi ca</li>
    </ul>
    <ul id="R_2.061.012">
        <li id="R_2.061.012ab">nārājake janapade yajñaśīlā dvijātayaḥ</li>
        <li id="R_2.061.012cd">satrāṇy anvāsate dāntā brāhmaṇāḥ saṃśitavratāḥ</li>
    </ul>
    <ul id="R_2.061.013">
        <li id="R_2.061.013ab">nārājake janapade prabhūtanaṭanartakāḥ</li>
        <li id="R_2.061.013cd">utsavāś ca samājāś ca vardhante rāṣṭravardhanāḥ</li>
    </ul>
    <ul id="R_2.061.014">
        <li id="R_2.061.014ab">nārajake janapade siddhārthā vyavahāriṇaḥ</li>
        <li id="R_2.061.014cd">kathābhir anurajyante kathāśīlāḥ kathāpriyaiḥ</li>
    </ul>
    <ul id="R_2.061.015">
        <li id="R_2.061.015ab">nārājake janapade vāhanaiḥ śīghragāmibhiḥ</li>
        <li id="R_2.061.015cd">narā niryānty araṇyāni nārībhiḥ saha kāminaḥ</li>
    </ul>
    <ul id="R_2.061.016">
        <li id="R_2.061.016ab">nārākaje janapade dhanavantaḥ surakṣitāḥ</li>
        <li id="R_2.061.016cd">śerate vivṛta dvārāḥ kṛṣigorakṣajīvinaḥ</li>
    </ul>
    <ul id="R_2.061.017">
        <li id="R_2.061.017ab">nārājake janapade vaṇijo dūragāminaḥ</li>
        <li id="R_2.061.017cd">gacchanti kṣemam adhvānaṃ bahupuṇyasamācitāḥ</li>
    </ul>
    <ul id="R_2.061.018">
        <li id="R_2.061.018ab">nārājake janapade caraty ekacaro vaśī</li>
        <li id="R_2.061.018cd">bhāvayann ātmanātmānaṃ yatrasāyaṃgṛho muniḥ</li>
    </ul>
    <ul id="R_2.061.019">
        <li id="R_2.061.019ab">nārājake janapade yogakṣemaṃ pravartate</li>
        <li id="R_2.061.019cd">na cāpy arājake senā śatrūn viṣahate yudhi</li>
    </ul>
    <ul id="R_2.061.020">
        <li id="R_2.061.020ab">yathā hy anudakā nadyo yathā vāpy atṛṇaṃ vanam</li>
        <li id="R_2.061.020cd">agopālā yathā gāvas tathā rāṣṭram arājakam</li>
    </ul>
    <ul id="R_2.061.021">
        <li id="R_2.061.021ab">nārājake janapade svakaṃ bhavati kasya cit</li>
        <li id="R_2.061.021cd">matsyā iva narā nityaṃ bhakṣayanti parasparam</li>
    </ul>
    <ul id="R_2.061.022">
        <li id="R_2.061.022ab">yehi saṃbhinnamaryādā nāstikāś chinnasaṃśayāḥ</li>
        <li id="R_2.061.022cd">te 'pi bhāvāya kalpante rājadaṇḍanipīḍitāḥ</li>
    </ul>
    <ul id="R_2.061.023">
        <li id="R_2.061.023ab">aho tama ivedaṃ syān na prajñāyeta kiṃ cana</li>
        <li id="R_2.061.023cd">rājā cen na bhaveṃl loke vibhajan sādhvasādhunī</li>
    </ul>
    <ul id="R_2.061.024">
        <li id="R_2.061.024ab">jīvaty api mahārāje tavaiva vacanaṃ vayam</li>
        <li id="R_2.061.024cd">nātikramāmahe sarve velāṃ prāpyeva sāgaraḥ</li>
    </ul>
    <ul id="R_2.061.025">
        <li id="R_2.061.025ab">
            <seg type="pāda" n="a">sa naḥ samīkṣya dvijavaryavṛttaṃ</seg>
            <seg type="pāda" n="b">nṛpaṃ vinā rājyam araṇyabhūtam</seg>
        </li>
        <li id="R_2.061.025cd">
            <seg type="pāda" n="c">kumāram ikṣvākusutaṃ vadānyaṃ</seg>
            <seg type="pāda" n="d">tvam eva rājānam ihābhiṣiñcaya</seg>
        </li>
    </ul>
    <ul id="R_2.062.001">
        <li id="R_2.062.001ab">teṣāṃ tad vacanaṃ śrutvā vasiṣṭhaḥ pratyuvāca ha</li>
        <li id="R_2.062.001cd">mitrāmātyagaṇān sarvān brāhmaṇāṃs tān idaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.062.002">
        <li id="R_2.062.002ab">yad asau mātulakule pure rājagṛhe sukhī</li>
        <li id="R_2.062.002cd">bharato vasati bhrātrā śatrughnena samanvitaḥ</li>
    </ul>
    <ul id="R_2.062.003">
        <li id="R_2.062.003ab">tac chīghraṃ javanā dūtā gacchantu tvaritair hayaiḥ</li>
        <li id="R_2.062.003cd">ānetuṃ bhrātarau vīrau kiṃ samīkṣāmahe vayam</li>
    </ul>
    <ul id="R_2.062.004">
        <li id="R_2.062.004ab">gacchantv iti tataḥ sarve vasiṣṭhaṃ vākyam abruvan</li>
        <li id="R_2.062.004cd">teṣāṃ tad vacanaṃ śrutvā vasiṣṭho vākyam abravīt</li>
    </ul>
    <ul id="R_2.062.005">
        <li id="R_2.062.005ab">ehi siddhārtha vijaya jayantāśokanandana</li>
        <li id="R_2.062.005cd">śrūyatām itikartavyaṃ sarvān eva bravīmi vaḥ</li>
    </ul>
    <ul id="R_2.062.006">
        <li id="R_2.062.006ab">puraṃ rājagṛhaṃ gatvā śīghraṃ śīghrajavair hayaiḥ</li>
        <li id="R_2.062.006cd">tyaktaśokair idaṃ vācyaḥ śāsanād bharato mama</li>
    </ul>
    <ul id="R_2.062.007">
        <li id="R_2.062.007ab">purohitas tvāṃ kuśalaṃ prāha sarve ca mantriṇaḥ</li>
        <li id="R_2.062.007cd">tvaramāṇaś ca niryāhi kṛtyam ātyayikaṃ tvayā</li>
    </ul>
    <ul id="R_2.062.008">
        <li id="R_2.062.008ab">mā cāsmai proṣitaṃ rāmaṃ mā cāsmai pitaraṃ mṛtam</li>
        <li id="R_2.062.008cd">bhavantaḥ śaṃsiṣur gatvā rāghavāṇām imaṃ kṣayam</li>
    </ul>
    <ul id="R_2.062.009">
        <li id="R_2.062.009ab">kauśeyāni ca vastrāṇi bhūṣaṇāni varāṇi ca</li>
        <li id="R_2.062.009cd">kṣipram ādāya rājñaś ca bharatasya ca gacchata</li>
        <li id="R_2.062.009ef">vasiṣṭhenābhyanujñātā dūtāḥ saṃtvaritā yayuḥ</li>
    </ul>
    <ul id="R_2.062.010">
        <li id="R_2.062.010ab">te hastina pure gaṅgāṃ tīrtvā pratyaṅmukhā yayuḥ</li>
        <li id="R_2.062.010cd">pāñcāladeśam āsādya madhyena kurujāṅgalam</li>
    </ul>
    <ul id="R_2.062.011">
        <li id="R_2.062.011ab">te prasannodakāṃ divyāṃ nānāvihagasevitām</li>
        <li id="R_2.062.011cd">upātijagmur vegena śaradaṇḍāṃ janākulām</li>
    </ul>
    <ul id="R_2.062.012">
        <li id="R_2.062.012ab">nikūlavṛkṣam āsādya divyaṃ satyopayācanam</li>
        <li id="R_2.062.012cd">abhigamyābhivādyaṃ taṃ kuliṅgāṃ prāviśan purīm</li>
    </ul>
    <ul id="R_2.062.013">
        <li id="R_2.062.013ab">abhikālaṃ tataḥ prāpya tejo'bhibhavanāc cyutāḥ</li>
        <li id="R_2.062.013cd">yayur madhyena bāhlīkān sudāmānaṃ ca parvatam</li>
        <li id="R_2.062.013ef">viṣṇoḥ padaṃ prekṣamāṇā vipāśāṃ cāpi śālmalīm</li>
    </ul>
    <ul id="R_2.062.014">
        <li id="R_2.062.014ab">te śrāntavāhanā dūtā vikṛṣṭena satā pathā</li>
        <li id="R_2.062.014cd">giri vrajaṃ pura varaṃ śīghram āsedur añjasā</li>
    </ul>
    <ul id="R_2.062.015">
        <li id="R_2.062.015ab">
            <seg type="pāda" n="a">bhartuḥ priyārthaṃ kularakṣaṇārthaṃ</seg>
            <seg type="pāda" n="b">bhartuś ca vaṃśasya parigrahārtham</seg>
        </li>
        <li id="R_2.062.015cd">
            <seg type="pāda" n="c">aheḍamānās tvarayā sma dūtā</seg>
            <seg type="pāda" n="d">rātryāṃ tu te tat puram eva yātāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.063.001">
        <li id="R_2.063.001ab">yām eva rātriṃ te dūtāḥ praviśanti sma tāṃ purīm</li>
        <li id="R_2.063.001cd">bharatenāpi tāṃ rātriṃ svapno dṛṣṭo 'yam apriyaḥ</li>
    </ul>
    <ul id="R_2.063.002">
        <li id="R_2.063.002ab">vyuṣṭām eva tu tāṃ rātriṃ dṛṣṭvā taṃ svapnam apriyam</li>
        <li id="R_2.063.002cd">putro rājādhirājasya subhṛśaṃ paryatapyata</li>
    </ul>
    <ul id="R_2.063.003">
        <li id="R_2.063.003ab">tapyamānaṃ samājñāya vayasyāḥ priyavādinaḥ</li>
        <li id="R_2.063.003cd">āyāsaṃ hi vineṣyantaḥ sabhāyāṃ cakrire kathāḥ</li>
    </ul>
    <ul id="R_2.063.004">
        <li id="R_2.063.004ab">vādayanti tathā śāntiṃ lāsayanty api cāpare</li>
        <li id="R_2.063.004cd">nāṭakāny apare prāhur hāsyāni vividhāni ca</li>
    </ul>
    <ul id="R_2.063.005">
        <li id="R_2.063.005ab">sa tair mahātmā bharataḥ sakhibhiḥ priya vādibhiḥ</li>
        <li id="R_2.063.005cd">goṣṭhīhāsyāni kurvadbhir na prāhṛṣyata rāghavaḥ</li>
    </ul>
    <ul id="R_2.063.006">
        <li id="R_2.063.006ab">tam abravīt priyasakho bharataṃ sakhibhir vṛtam</li>
        <li id="R_2.063.006cd">suhṛdbhiḥ paryupāsīnaḥ kiṃ sakhe nānumodase</li>
    </ul>
    <ul id="R_2.063.007">
        <li id="R_2.063.007ab">evaṃ bruvāṇaṃ suhṛdaṃ bharataḥ pratyuvāca ha</li>
        <li id="R_2.063.007cd">śṛṇu tvaṃ yan nimittaṃme dainyam etad upāgatam</li>
    </ul>
    <ul id="R_2.063.008">
        <li id="R_2.063.008ab">svapne pitaram adrākṣaṃ malinaṃ muktamūrdhajam</li>
        <li id="R_2.063.008cd">patantam adriśikharāt kaluṣe gomaye hrade</li>
    </ul>
    <ul id="R_2.063.009">
        <li id="R_2.063.009ab">plavamānaś ca me dṛṣṭaḥ sa tasmin gomayahrade</li>
        <li id="R_2.063.009cd">pibann añjalinā tailaṃ hasann iva muhur muhuḥ</li>
    </ul>
    <ul id="R_2.063.010">
        <li id="R_2.063.010ab">tatas tilodanaṃ bhuktvā punaḥ punar adhaḥśirāḥ</li>
        <li id="R_2.063.010cd">tailenābhyaktasarvāṅgas tailam evāvagāhata</li>
    </ul>
    <ul id="R_2.063.011">
        <li id="R_2.063.011ab">svapne 'pi sāgaraṃ śuṣkaṃ candraṃ ca patitaṃ bhuvi</li>
        <li id="R_2.063.011cd">sahasā cāpi saṃśantaṃ jvalitaṃ jātavedasaṃ</li>
    </ul>
    <ul id="R_2.063.012">
        <li id="R_2.063.012ab">avadīrṇāṃ ca pṛthivīṃ śuṣkāṃś ca vividhān drumān</li>
        <li id="R_2.063.012cd">ahaṃ paśyāmi vidhvastān sadhūmāṃś caiva pārvatān</li>
    </ul>
    <ul id="R_2.063.013">
        <li id="R_2.063.013ab">pīṭhe kārṣṇāyase cainaṃ niṣaṇṇaṃ kṛṣṇavāsasaṃ</li>
        <li id="R_2.063.013cd">prahasanti sma rājānaṃ pramadāḥ kṛṣṇapiṅgalāḥ</li>
    </ul>
    <ul id="R_2.063.014">
        <li id="R_2.063.014ab">tvaramāṇaś ca dharmātmā raktamālyānulepanaḥ</li>
        <li id="R_2.063.014cd">rathena kharayuktena prayāto dakṣiṇāmukhaḥ</li>
    </ul>
    <ul id="R_2.063.015">
        <li id="R_2.063.015ab">evam etan mayā dṛṣṭam imāṃ rātriṃ bhayāvahām</li>
        <li id="R_2.063.015cd">ahaṃ rāmo 'tha vā rājā lakṣmaṇo vā mariṣyati</li>
    </ul>
    <ul id="R_2.063.016">
        <li id="R_2.063.016ab">naro yānena yaḥ svapne kharayuktena yāti hi</li>
        <li id="R_2.063.016cd">acirāt tasya dhūmāgraṃ citāyāṃ saṃpradṛśyate</li>
        <li id="R_2.063.016ef">etannimittaṃ dīno 'haṃ tan na vaḥ pratipūjaye</li>
    </ul>
    <ul id="R_2.063.017">
        <li id="R_2.063.017ab">śuṣyatīva ca me kaṇṭho na svastham iva me manaḥ</li>
        <li id="R_2.063.017cd">jugupsann iva cātmānaṃ na ca paśyāmi kāraṇam</li>
    </ul>
    <ul id="R_2.063.018">
        <li id="R_2.063.018ab">
            <seg type="pāda" n="a">imāṃ hi duḥsvapnagatiṃ niśāmya tām</seg>
            <seg type="pāda" n="b">anekarūpām avitarkitāṃ purā</seg>
        </li>
        <li id="R_2.063.018cd">
            <seg type="pāda" n="c">bhayaṃ mahat tad dhṛdayān na yāti me</seg>
            <seg type="pāda" n="d">vicintya rājānam acintyadarśanam</seg>
        </li>
    </ul>
    <ul id="R_2.064.001">
        <li id="R_2.064.001ab">bharate bruvati svapnaṃ dūtās te klāntavāhanāḥ</li>
        <li id="R_2.064.001cd">praviśyāsahyaparikhaṃ ramyaṃ rājagṛhaṃ puram</li>
    </ul>
    <ul id="R_2.064.002">
        <li id="R_2.064.002ab">samāgamya tu rājñā ca rājaputreṇa cārcitāḥ</li>
        <li id="R_2.064.002cd">rājñaḥ pādau gṛhītvā tu tam ūcur bharataṃ vacaḥ</li>
    </ul>
    <ul id="R_2.064.003">
        <li id="R_2.064.003ab">purohitas tvā kuśalaṃ prāha sarve ca mantriṇaḥ</li>
        <li id="R_2.064.003cd">tvaramāṇaś ca niryāhi kṛtyam ātyayikaṃ tvayā</li>
    </ul>
    <ul id="R_2.064.004">
        <li id="R_2.064.004ab">atra viṃśatikoṭyas tu nṛpater mātulasya te</li>
        <li id="R_2.064.004cd">daśakoṭyas tu saṃpūrṇās tathaiva ca nṛpātmaja</li>
    </ul>
    <ul id="R_2.064.005">
        <li id="R_2.064.005ab">pratigṛhya ca tat sarvaṃ svanuraktaḥ suhṛjjane</li>
        <li id="R_2.064.005cd">dūtān uvāca bharataḥ kāmaiḥ saṃpratipūjya tān</li>
    </ul>
    <ul id="R_2.064.006">
        <li id="R_2.064.006ab">kaccit sukuśalī rājā pitā daśaratho mama</li>
        <li id="R_2.064.006cd">kaccic cārāgatā rāme lakṣmaṇe vā mahātmani</li>
    </ul>
    <ul id="R_2.064.007">
        <li id="R_2.064.007ab">āryā ca dharmaniratā dharmajñā dharmadarśinī</li>
        <li id="R_2.064.007cd">arogā cāpi kausalyā mātā rāmasya dhīmataḥ</li>
    </ul>
    <ul id="R_2.064.008">
        <li id="R_2.064.008ab">kaccit sumitrā dharmajñā jananī lakṣmaṇasya yā</li>
        <li id="R_2.064.008cd">śatrughnasya ca vīrasya sārogā cāpi madhyamā</li>
    </ul>
    <ul id="R_2.064.009">
        <li id="R_2.064.009ab">ātmakāmā sadā caṇḍī krodhanā prājñamāninī</li>
        <li id="R_2.064.009cd">arogā cāpi kaikeyī mātā me kim uvāca ha</li>
    </ul>
    <ul id="R_2.064.010">
        <li id="R_2.064.010ab">evam uktās tu te dūtā bharatena mahātmanā</li>
        <li id="R_2.064.010cd">ūcuḥ saṃpraśritaṃ vākyam idaṃ taṃ bharataṃ tadā</li>
        <li id="R_2.064.010ef">kuśalās te naravyāghra yeṣāṃ kuśalam icchasi</li>
    </ul>
    <ul id="R_2.064.011">
        <li id="R_2.064.011ab">bharataś cāpi tān dūtān evam ukto 'bhyabhāṣata</li>
        <li id="R_2.064.011cd">āpṛcche 'haṃ mahārājaṃ dūtāḥ saṃtvarayanti mām</li>
    </ul>
    <ul id="R_2.064.012">
        <li id="R_2.064.012ab">evam uktvā tu tān dūtān bharataḥ pārthivātmajaḥ</li>
        <li id="R_2.064.012cd">dūtaiḥ saṃcodito vākyaṃ mātāmaham uvāca ha</li>
    </ul>
    <ul id="R_2.064.013">
        <li id="R_2.064.013ab">rājan pitur gamiṣyāmi sakāśaṃ dūtacoditaḥ</li>
        <li id="R_2.064.013cd">punar apy aham eṣyāmi yadā me tvaṃ smariṣyasi</li>
    </ul>
    <ul id="R_2.064.014">
        <li id="R_2.064.014ab">bharatenaivam uktas tu nṛpo mātāmahas tadā</li>
        <li id="R_2.064.014cd">tam uvāca śubhaṃ vākyaṃ śirasy āghrāya rāghavam</li>
    </ul>
    <ul id="R_2.064.015">
        <li id="R_2.064.015ab">gaccha tātānujāne tvāṃ kaikeyī suprajās tvayā</li>
        <li id="R_2.064.015cd">mātaraṃ kuśalaṃ brūyāḥ pitaraṃ ca paraṃtapa</li>
    </ul>
    <ul id="R_2.064.016">
        <li id="R_2.064.016ab">purohitaṃ ca kuśalaṃ ye cānye dvijasattamāḥ</li>
        <li id="R_2.064.016cd">tau ca tāta maheṣvāsau bhrātaru rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_2.064.017">
        <li id="R_2.064.017ab">tasmai hastyuttamāṃś citrān kambalān ajināni ca</li>
        <li id="R_2.064.017cd">abhisatkṛtya kaikeyo bharatāya dhanaṃ dadau</li>
    </ul>
    <ul id="R_2.064.018">
        <li id="R_2.064.018ab">rukma niṣkasahasre dve ṣoḍaśāśvaśatāni ca</li>
        <li id="R_2.064.018cd">satkṛtya kaikeyī putraṃ kekayo dhanam ādiśat</li>
    </ul>
    <ul id="R_2.064.019">
        <li id="R_2.064.019ab">tathāmātyān abhipretān viśvāsyāṃś ca guṇānvitān</li>
        <li id="R_2.064.019cd">dadāv aśvapatiḥ śīghraṃ bharatāyānuyāyinaḥ</li>
    </ul>
    <ul id="R_2.064.020">
        <li id="R_2.064.020ab">airāvatān aindraśirān nāgān vai priyadarśanān</li>
        <li id="R_2.064.020cd">kharāñ śīghrān susaṃyuktān mātulo 'smai dhanaṃ dadau</li>
    </ul>
    <ul id="R_2.064.021">
        <li id="R_2.064.021ab">antaḥpure 'tisaṃvṛddhān vyāghravīryabalānvitān</li>
        <li id="R_2.064.021cd">daṃṣṭrāyudhān mahākāyāñ śunaś copāyanaṃ dadau</li>
    </ul>
    <ul id="R_2.064.022">
        <li id="R_2.064.022ab">sa mātāmaham āpṛcchya mātulaṃ ca yudhājitam</li>
        <li id="R_2.064.022cd">ratham āruhya bharataḥ śatrughnasahito yayau</li>
    </ul>
    <ul id="R_2.064.023">
        <li id="R_2.064.023ab">rathān maṇḍalacakrāṃś ca yojayitvā paraḥśatam</li>
        <li id="R_2.064.023cd">uṣṭrago'śvakharair bhṛtyā bharataṃ yāntam anvayuḥ</li>
    </ul>
    <ul id="R_2.064.024">
        <li id="R_2.064.024ab">
            <seg type="pāda" n="a">balena gupto bharato mahātmā</seg>
            <seg type="pāda" n="b">sahāryakasyātmasamair amātyaiḥ</seg>
        </li>
        <li id="R_2.064.024cd">
            <seg type="pāda" n="c">ādāya śatrughnam apetaśatrur</seg>
            <seg type="pāda" n="d">gṛhād yayau siddha ivendralokāt</seg>
        </li>
    </ul>
    <ul id="R_2.065.001">
        <li id="R_2.065.001ab">sa prāṅmukho rājagṛhād abhiniryāya vīryavān</li>
        <li id="R_2.065.001cd">hrādinīṃ dūrapārāṃ ca pratyaksrotas taraṅgiṇīm</li>
        <li id="R_2.065.001ef">śatadrūm atarac chrīmān nadīm ikṣvākunandanaḥ</li>
    </ul>
    <ul id="R_2.065.002">
        <li id="R_2.065.002ab">eladhāne nadīṃ tīrtvā prāpya cāparaparpaṭān</li>
        <li id="R_2.065.002cd">śilām ākurvatīṃ tīrtvā āgneyaṃ śalyakartanam</li>
    </ul>
    <ul id="R_2.065.003">
        <li id="R_2.065.003ab">satyasaṃdhaḥ śuciḥ śrīmān prekṣamāṇaḥ śilāvahām</li>
        <li id="R_2.065.003cd">atyayāt sa mahāśailān vanaṃ caitrarathaṃ prati</li>
    </ul>
    <ul id="R_2.065.004">
        <li id="R_2.065.004ab">veginīṃ ca kuliṅgākhyāṃ hrādinīṃ parvatāvṛtām</li>
        <li id="R_2.065.004cd">yamunāṃ prāpya saṃtīrṇo balam āśvāsayat tadā</li>
    </ul>
    <ul id="R_2.065.005">
        <li id="R_2.065.005ab">śītīkṛtya tu gātrāṇi klāntān āśvāsya vājinaḥ</li>
        <li id="R_2.065.005cd">tatra snātvā ca pītvā ca prāyād ādāya codakam</li>
    </ul>
    <ul id="R_2.065.006">
        <li id="R_2.065.006ab">rājaputro mahāraṇyam anabhīkṣṇopasevitam</li>
        <li id="R_2.065.006cd">bhadro bhadreṇa yānena mārutaḥ kham ivātyayāt</li>
    </ul>
    <ul id="R_2.065.007">
        <li id="R_2.065.007ab">toraṇaṃ dakṣiṇārdhena jambūprastham upāgamat</li>
        <li id="R_2.065.007cd">varūthaṃ ca yayau ramyaṃ grāmaṃ daśarathātmajaḥ</li>
    </ul>
    <ul id="R_2.065.008">
        <li id="R_2.065.008ab">tatra ramye vane vāsaṃ kṛtvāsau prāṅmukho yayau</li>
        <li id="R_2.065.008cd">udyānam ujjihānāyāḥ priyakā yatra pādapāḥ</li>
    </ul>
    <ul id="R_2.065.009">
        <li id="R_2.065.009ab">sālāṃs tu priyakān prāpya śīghrān āsthāya vājinaḥ</li>
        <li id="R_2.065.009cd">anujñāpyātha bharato vāhinīṃ tvarito yayau</li>
    </ul>
    <ul id="R_2.065.010">
        <li id="R_2.065.010ab">vāsaṃ kṛtvā sarvatīrthe tīrtvā cottānakāṃ nadīm</li>
        <li id="R_2.065.010cd">anyā nadīś ca vividhāḥ pārvatīyais turaṃgamaiḥ</li>
    </ul>
    <ul id="R_2.065.011">
        <li id="R_2.065.011ab">hastipṛṣṭhakam āsādya kuṭikām atyavartata</li>
        <li id="R_2.065.011cd">tatāra ca naravyāghro lauhitye sa kapīvatīm</li>
        <li id="R_2.065.011ef">ekasāle sthāṇumatīṃ vinate gomatīṃ nadīm</li>
    </ul>
    <ul id="R_2.065.012">
        <li id="R_2.065.012ab">kaliṅga nagare cāpi prāpya sālavanaṃ tadā</li>
        <li id="R_2.065.012cd">bharataḥ kṣipram āgacchat supariśrāntavāhanaḥ</li>
    </ul>
    <ul id="R_2.065.013">
        <li id="R_2.065.013ab">vanaṃ ca samatītyāśu śarvaryām aruṇodaye</li>
        <li id="R_2.065.013cd">ayodhyāṃ manunā rājñā nirmitāṃ sa dadarśa ha</li>
    </ul>
    <ul id="R_2.065.014">
        <li id="R_2.065.014ab">tāṃ purīṃ puruṣavyāghraḥ saptarātroṣiṭaḥ pathi</li>
        <li id="R_2.065.014cd">ayodhyām agrato dṛṣṭvā rathe sārathim abravīt</li>
    </ul>
    <ul id="R_2.065.015">
        <li id="R_2.065.015ab">eṣā nātipratītā me puṇyodyānā yaśasvinī</li>
        <li id="R_2.065.015cd">ayodhyā dṛśyate dūrāt sārathe pāṇḍumṛttikā</li>
    </ul>
    <ul id="R_2.065.016">
        <li id="R_2.065.016ab">yajvabhir guṇasaṃpannair brāhmaṇair vedapāragaiḥ</li>
        <li id="R_2.065.016cd">bhūyiṣṭham ṛddhair ākīrṇā rājarṣivarapālitā</li>
    </ul>
    <ul id="R_2.065.017">
        <li id="R_2.065.017ab">ayodhyāyāṃ purāśabdaḥ śrūyate tumulo mahān</li>
        <li id="R_2.065.017cd">samantān naranārīṇāṃ tam adya na śṛṇomy aham</li>
    </ul>
    <ul id="R_2.065.018">
        <li id="R_2.065.018ab">udyānāni hi sāyāhne krīḍitvoparatair naraiḥ</li>
        <li id="R_2.065.018cd">samantād vipradhāvadbhiḥ prakāśante mamānyadā</li>
    </ul>
    <ul id="R_2.065.019">
        <li id="R_2.065.019ab">tāny adyānurudantīva parityaktāni kāmibhiḥ</li>
        <li id="R_2.065.019cd">araṇyabhūteva purī sārathe pratibhāti me</li>
    </ul>
    <ul id="R_2.065.020">
        <li id="R_2.065.020ab">na hy atra yānair dṛśyante na gajair na ca vājibhiḥ</li>
        <li id="R_2.065.020cd">niryānto vābhiyānto vā naramukhyā yathāpuram</li>
    </ul>
    <ul id="R_2.065.021">
        <li id="R_2.065.021ab">aniṣṭāni ca pāpāni paśyāmi vividhāni ca</li>
        <li id="R_2.065.021cd">nimittāny amanojñāni tena sīdati te manaḥ</li>
    </ul>
    <ul id="R_2.065.022">
        <li id="R_2.065.022ab">dvāreṇa vaijayantena prāviśac chrāntavāhanaḥ</li>
        <li id="R_2.065.022cd">dvāḥsthair utthāya vijayaṃ pṛṣṭas taiḥ sahito yayau</li>
    </ul>
    <ul id="R_2.065.023">
        <li id="R_2.065.023ab">sa tv anekāgrahṛdayo dvāḥsthaṃ pratyarcya taṃ janam</li>
        <li id="R_2.065.023cd">sūtam aśvapateḥ klāntam abravīt tatra rāghavaḥ</li>
    </ul>
    <ul id="R_2.065.024">
        <li id="R_2.065.024ab">śrutā no yādṛśāḥ pūrvaṃ nṛpatīnāṃ vināśane</li>
        <li id="R_2.065.024cd">ākārās tān ahaṃ sarvān iha paśyāmi sārathe</li>
    </ul>
    <ul id="R_2.065.025">
        <li id="R_2.065.025ab">malinaṃ cāśrupūrṇākṣaṃ dīnaṃ dhyānaparaṃ kṛśam</li>
        <li id="R_2.065.025cd">sastrī puṃsaṃ ca paśyāmi janam utkaṇṭhitaṃ pure</li>
    </ul>
    <ul id="R_2.065.026">
        <li id="R_2.065.026ab">ity evam uktvā bharataḥ sūtaṃ taṃ dīnamānasaḥ</li>
        <li id="R_2.065.026cd">tāny aniṣṭāny ayodhyāyāṃ prekṣya rājagṛhaṃ yayau</li>
    </ul>
    <ul id="R_2.065.027">
        <li id="R_2.065.027ab">
            <seg type="pāda" n="a">tāṃ śūnyaśṛṅgāṭakaveśmarathyāṃ</seg>
            <seg type="pāda" n="b">rajo'ruṇadvārakapāṭayantrām</seg>
        </li>
        <li id="R_2.065.027cd">
            <seg type="pāda" n="c">dṛṣṭvā purīm indrapurī prakāśāṃ</seg>
            <seg type="pāda" n="d">duḥkhena saṃpūrṇataro babhūva</seg>
        </li>
    </ul>
    <ul id="R_2.065.028">
        <li id="R_2.065.028ab">
            <seg type="pāda" n="a">bahūni paśyan manaso 'priyāṇi</seg>
            <seg type="pāda" n="b">yāny annyadā nāsya pure babhūvuḥ</seg>
        </li>
        <li id="R_2.065.028cd">
            <seg type="pāda" n="c">avākśirā dīnamanā nahṛṣṭaḥ</seg>
            <seg type="pāda" n="d">pitur mahātmā praviveśa veśma</seg>
        </li>
    </ul>
    <ul id="R_2.066.001">
        <li id="R_2.066.001ab">apaśyaṃs tu tatas tatra pitaraṃ pitur ālaye</li>
        <li id="R_2.066.001cd">jagāma bharato draṣṭuṃ mātaraṃ mātur ālaye</li>
    </ul>
    <ul id="R_2.066.002">
        <li id="R_2.066.002ab">anuprāptaṃ tu taṃ dṛṣṭvā kaikeyī proṣitaṃ sutam</li>
        <li id="R_2.066.002cd">utpapāta tadā hṛṣṭā tyaktvā sauvarṇamānasaṃ</li>
    </ul>
    <ul id="R_2.066.003">
        <li id="R_2.066.003ab">sa praviśyaiva dharmātmā svagṛhaṃ śrīvivarjitam</li>
        <li id="R_2.066.003cd">bharataḥ prekṣya jagrāha jananyāś caraṇau śubhau</li>
    </ul>
    <ul id="R_2.066.004">
        <li id="R_2.066.004ab">taṃ mūrdhni samupāghrāya pariṣvajya yaśasvinam</li>
        <li id="R_2.066.004cd">aṅke bharatam āropya praṣṭuṃ samupacakrame</li>
    </ul>
    <ul id="R_2.066.005">
        <li id="R_2.066.005ab">adya te kati cid rātryaś cyutasyāryakaveśmanaḥ</li>
        <li id="R_2.066.005cd">api nādhvaśramaḥ śīghraṃ rathenāpatatas tava</li>
    </ul>
    <ul id="R_2.066.006">
        <li id="R_2.066.006ab">āryakas te sukuśalo yudhājin mātulas tava</li>
        <li id="R_2.066.006cd">pravāsāc ca sukhaṃ putra sarvaṃ me vaktum arhasi</li>
    </ul>
    <ul id="R_2.066.007">
        <li id="R_2.066.007ab">evaṃ pṛṣṭhas tu kaikeyyā priyaṃ pārthivanandanaḥ</li>
        <li id="R_2.066.007cd">ācaṣṭa bharataḥ sarvaṃ mātre rājīvalocanaḥ</li>
    </ul>
    <ul id="R_2.066.008">
        <li id="R_2.066.008ab">adya me saptamī rātriś cyutasyāryakaveśmanaḥ</li>
        <li id="R_2.066.008cd">ambāyāḥ kuśalī tāto yudhājin mātulaś ca me</li>
    </ul>
    <ul id="R_2.066.009">
        <li id="R_2.066.009ab">yan me dhanaṃ ca ratnaṃ ca dadau rājā paraṃtapaḥ</li>
        <li id="R_2.066.009cd">pariśrāntaṃ pathy abhavat tato 'haṃ pūrvam āgataḥ</li>
    </ul>
    <ul id="R_2.066.010">
        <li id="R_2.066.010ab">rājavākyaharair dūtais tvaryamāṇo 'ham āgataḥ</li>
        <li id="R_2.066.010cd">yad ahaṃ praṣṭum icchāmi tad ambā vaktum arhasi</li>
    </ul>
    <ul id="R_2.066.011">
        <li id="R_2.066.011ab">śūnyo 'yaṃ śayanīyas te paryaṅko hemabhūṣitaḥ</li>
        <li id="R_2.066.011cd">na cāyam ikṣvākujanaḥ prahṛṣṭaḥ pratibhāti me</li>
    </ul>
    <ul id="R_2.066.012">
        <li id="R_2.066.012ab">rājā bhavati bhūyiṣṭham ihāmbāyā niveśane</li>
        <li id="R_2.066.012cd">tam ahaṃ nādya paśyāmi draṣṭum icchann ihāgataḥ</li>
    </ul>
    <ul id="R_2.066.013">
        <li id="R_2.066.013ab">pitur grahīṣye caraṇau taṃ mamākhyāhi pṛcchataḥ</li>
        <li id="R_2.066.013cd">āhosvid amba jyeṣṭhāyāḥ kausalyāyā niveśane</li>
    </ul>
    <ul id="R_2.066.014">
        <li id="R_2.066.014ab">taṃ pratyuvāca kaikeyī priyavad ghoram apriyam</li>
        <li id="R_2.066.014cd">ajānantaṃ prajānantī rājyalobhena mohitā</li>
        <li id="R_2.066.014ef">yā gatiḥ sarvabhūtānāṃ tāṃ gatiṃ te pitā gataḥ</li>
    </ul>
    <ul id="R_2.066.015">
        <li id="R_2.066.015ab">tac chrutvā bharato vākyaṃ dharmābhijanavāñ śuciḥ</li>
        <li id="R_2.066.015cd">papāta sahasā bhūmau pitṛśokabalārditaḥ</li>
    </ul>
    <ul id="R_2.066.016">
        <li id="R_2.066.016ab">tataḥ śokena saṃvītaḥ pitur maraṇaduḥkhitaḥ</li>
        <li id="R_2.066.016cd">vilalāpa mahātejā bhrāntākulitacetanaḥ</li>
    </ul>
    <ul id="R_2.066.017">
        <li id="R_2.066.017ab">etat suruciraṃ bhāti pitur me śayanaṃ purā</li>
        <li id="R_2.066.017cd">tad idaṃ na vibhāty adya vihīnaṃ tena dhīmatā</li>
    </ul>
    <ul id="R_2.066.018">
        <li id="R_2.066.018ab">tam ārtaṃ devasaṃkāśaṃ samīkṣya patitaṃ bhuvi</li>
        <li id="R_2.066.018cd">utthāpayitvā śokārtaṃ vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.066.019">
        <li id="R_2.066.019ab">uttiṣṭhottiṣṭha kiṃ śeṣe rājaputra mahāyaśaḥ</li>
        <li id="R_2.066.019cd">tvadvidhā na hi śocanti santaḥ sadasi saṃmatāḥ</li>
    </ul>
    <ul id="R_2.066.020">
        <li id="R_2.066.020ab">sa rudatyā ciraṃ kālaṃ bhūmau viparivṛtya ca</li>
        <li id="R_2.066.020cd">jananīṃ pratyuvācedaṃ śokair bahubhir āvṛtaḥ</li>
    </ul>
    <ul id="R_2.066.021">
        <li id="R_2.066.021ab">abhiṣekṣyati rāmaṃ tu rājā yajñaṃ nu yakṣyati</li>
        <li id="R_2.066.021cd">ity ahaṃ kṛtasaṃkalpo hṛṣṭo yātrām ayāsiṣam</li>
    </ul>
    <ul id="R_2.066.022">
        <li id="R_2.066.022ab">tad idaṃ hy anyathā bhūtaṃ vyavadīrṇaṃ mano mama</li>
        <li id="R_2.066.022cd">pitaraṃ yo na paśyāmi nityaṃ priyahite ratam</li>
    </ul>
    <ul id="R_2.066.023">
        <li id="R_2.066.023ab">amba kenātyagād rājā vyādhinā mayy anāgate</li>
        <li id="R_2.066.023cd">dhanyā rāmādayaḥ sarve yaiḥ pitā saṃskṛtaḥ svayam</li>
    </ul>
    <ul id="R_2.066.024">
        <li id="R_2.066.024ab">na nūnaṃ māṃ mahārājaḥ prāptaṃ jānāti kīrtimān</li>
        <li id="R_2.066.024cd">upajighred dhi māṃ mūrdhni tātaḥ saṃnamya satvaram</li>
    </ul>
    <ul id="R_2.066.025">
        <li id="R_2.066.025ab">kva sa pāṇiḥ sukhasparśas tātasyākliṣṭakarmaṇaḥ</li>
        <li id="R_2.066.025cd">yena māṃ rajasā dhvastam abhīkṣṇaṃ parimārjati</li>
    </ul>
    <ul id="R_2.066.026">
        <li id="R_2.066.026ab">yo me bhrātā pitā bandhur yasya dāso 'smi dhīmataḥ</li>
        <li id="R_2.066.026cd">tasya māṃ śīghram ākhyāhi rāmasyākliṣṭa karmaṇaḥ</li>
    </ul>
    <ul id="R_2.066.027">
        <li id="R_2.066.027ab">pitā hi bhavati jyeṣṭho dharmam āryasya jānataḥ</li>
        <li id="R_2.066.027cd">tasya pādau grahīṣyāmi sa hīdānīṃ gatir mama</li>
    </ul>
    <ul id="R_2.066.028">
        <li id="R_2.066.028ab">ārye kim abravīd rājā pitā me satyavikramaḥ</li>
        <li id="R_2.066.028cd">paścimaṃ sādhu saṃdeśam icchāmi śrotum ātmanaḥ</li>
    </ul>
    <ul id="R_2.066.029">
        <li id="R_2.066.029ab">iti pṛṣṭā yathātattvaṃ kaikeyī vākyam abravīt</li>
        <li id="R_2.066.029cd">rāmeti rājā vilapan hā sīte lakṣmaṇeti ca</li>
        <li id="R_2.066.029ef">sa mahātmā paraṃ lokaṃ gato gatimatāṃ varaḥ</li>
    </ul>
    <ul id="R_2.066.030">
        <li id="R_2.066.030ab">imāṃ tu paścimāṃ vācaṃ vyājahāra pitā tava</li>
        <li id="R_2.066.030cd">kāla dharmaparikṣiptaḥ pāśair iva mahāgajaḥ</li>
    </ul>
    <ul id="R_2.066.031">
        <li id="R_2.066.031ab">siddhārthās tu narā rāmam āgataṃ sītayā saha</li>
        <li id="R_2.066.031cd">lakṣmaṇaṃ ca mahābāhuṃ drakṣyanti punar āgatam</li>
    </ul>
    <ul id="R_2.066.032">
        <li id="R_2.066.032ab">tac chrutvā viṣasādaiva dvitīyā priyaśaṃsanāt</li>
        <li id="R_2.066.032cd">viṣaṇṇavadano bhūtvā bhūyaḥ papraccha mātaram</li>
    </ul>
    <ul id="R_2.066.033">
        <li id="R_2.066.033ab">kva cedānīṃ sa dharmātmā kausalyānandavardhanaḥ</li>
        <li id="R_2.066.033cd">lakṣmaṇena saha bhrātrā sītayā ca samaṃ gataḥ</li>
    </ul>
    <ul id="R_2.066.034">
        <li id="R_2.066.034ab">tathā pṛṣṭā yathātattvam ākhyātum upacakrame</li>
        <li id="R_2.066.034cd">mātāsya yugapad vākyaṃ vipriyaṃ priyaśaṅkayā</li>
    </ul>
    <ul id="R_2.066.035">
        <li id="R_2.066.035ab">sa hi rājasutaḥ putra cīravāsā mahāvanam</li>
        <li id="R_2.066.035cd">daṇḍakān saha vaidehyā lakṣmaṇānucaro gataḥ</li>
    </ul>
    <ul id="R_2.066.036">
        <li id="R_2.066.036ab">tac chrutvā bharatas trasto bhrātuś cāritraśaṅkayā</li>
        <li id="R_2.066.036cd">svasya vaṃśasya māhātmyāt praṣṭuṃ samupacakrame</li>
    </ul>
    <ul id="R_2.066.037">
        <li id="R_2.066.037ab">kaccin na brāhmaṇavadhaṃ hṛtaṃ rāmeṇa kasya cit</li>
        <li id="R_2.066.037cd">kaccin nāḍhyo daridro vā tenāpāpo vihiṃsitaḥ</li>
    </ul>
    <ul id="R_2.066.038">
        <li id="R_2.066.038ab">kaccin na paradārān vā rājaputro 'bhimanyate</li>
        <li id="R_2.066.038cd">kasmāt sa daṇḍakāraṇye bhrūṇaheva vivāsitaḥ</li>
    </ul>
    <ul id="R_2.066.039">
        <li id="R_2.066.039ab">athāsya capalā mātā tat svakarma yathātatham</li>
        <li id="R_2.066.039cd">tenaiva strīsvabhāvena vyāhartum upacakrame</li>
    </ul>
    <ul id="R_2.066.040">
        <li id="R_2.066.040ab">na brāhmaṇadhanaṃ kiṃ cid dhṛtaṃ rāmeṇa kasya cit</li>
        <li id="R_2.066.040cd">kaś cin nāḍhyo daridro vā tenāpāpo vihiṃsitaḥ</li>
        <li id="R_2.066.040ef">na rāmaḥ paradārāṃś ca cakṣurbhyām api paśyati</li>
    </ul>
    <ul id="R_2.066.041">
        <li id="R_2.066.041ab">mayā tu putra śrutvaiva rāmasyaivābhiṣecanam</li>
        <li id="R_2.066.041cd">yācitas te pitā rājyaṃ rāmasya ca vivāsanam</li>
    </ul>
    <ul id="R_2.066.042">
        <li id="R_2.066.042ab">sa svavṛttiṃ samāsthāya pitā te tat tathākarot</li>
        <li id="R_2.066.042cd">rāmaś ca sahasaumitriḥ preṣitaḥ saha sītayā</li>
    </ul>
    <ul id="R_2.066.043">
        <li id="R_2.066.043ab">tam apaśyan priyaṃ putraṃ mahīpālo mahāyaśāḥ</li>
        <li id="R_2.066.043cd">putraśokaparidyūnaḥ pañcatvam upapedivān</li>
    </ul>
    <ul id="R_2.066.044">
        <li id="R_2.066.044ab">tvayā tv idānīṃ dharmajña rājatvam avalambyatām</li>
        <li id="R_2.066.044cd">tvatkṛte hi mayā sarvam idam evaṃvidhaṃ kṛtam</li>
    </ul>
    <ul id="R_2.066.045">
        <li id="R_2.066.045ab">
            <seg type="pāda" n="a">tat putra śīghraṃ vidhinā vidhijñair</seg>
            <seg type="pāda" n="b">vasiṣṭhamukhyaiḥ sahito dvijendraiḥ</seg>
        </li>
        <li id="R_2.066.045cd">
            <seg type="pāda" n="c">saṃkālya rājānam adīnasattvam</seg>
            <seg type="pāda" n="d">ātmānam urvyām abhiṣecayasva</seg>
        </li>
    </ul>
    <ul id="R_2.067.001">
        <li id="R_2.067.001ab">śrutvā tu pitaraṃ vṛttaṃ bhrātaru ca vivāsitau</li>
        <li id="R_2.067.001cd">bharato duḥkhasaṃtapta idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.067.002">
        <li id="R_2.067.002ab">kiṃ nuṇkāryaṃ hatasyeha mama rājyena śocataḥ</li>
        <li id="R_2.067.002cd">vihīnasyātha pitrā ca bhrātrā pitṛsamena ca</li>
    </ul>
    <ul id="R_2.067.003">
        <li id="R_2.067.003ab">duḥkhe me duḥkham akaror vraṇe kṣāram ivādadhāḥ</li>
        <li id="R_2.067.003cd">rājānaṃ pretabhāvasthaṃ kṛtvā rāmaṃ ca tāpasaṃ</li>
    </ul>
    <ul id="R_2.067.004">
        <li id="R_2.067.004ab">kulasya tvam abhāvāya kālarātrir ivāgatā</li>
        <li id="R_2.067.004cd">aṅgāram upagūhya sma pitā me nāvabuddhavān</li>
    </ul>
    <ul id="R_2.067.005">
        <li id="R_2.067.005ab">kausalyā ca sumitrā ca putraśokābhipīḍite</li>
        <li id="R_2.067.005cd">duṣkaraṃ yadi jīvetāṃ prāpya tvāṃ jananīṃ mama</li>
    </ul>
    <ul id="R_2.067.006">
        <li id="R_2.067.006ab">nanu tv āryo 'pi dharmātmā tvayi vṛttim anuttamām</li>
        <li id="R_2.067.006cd">vartate guruvṛttijño yathā mātari vartate</li>
    </ul>
    <ul id="R_2.067.007">
        <li id="R_2.067.007ab">tathā jyeṣṭhā hi me mātā kausalyā dīrghadarśinī</li>
        <li id="R_2.067.007cd">tvayi dharmaṃ samāsthāya bhaginyām iva vartate</li>
    </ul>
    <ul id="R_2.067.008">
        <li id="R_2.067.008ab">tasyāḥ putraṃ kṛtātmānaṃ cīravalkalavāsasaṃ</li>
        <li id="R_2.067.008cd">prasthāpya vanavāsāya kathaṃ pāpe na śocasi</li>
    </ul>
    <ul id="R_2.067.009">
        <li id="R_2.067.009ab">apāpadarśinaṃ śūraṃ kṛtātmānaṃ yaśasvinam</li>
        <li id="R_2.067.009cd">pravrājya cīravasanaṃ kiṃ nu paśyasi kāraṇam</li>
    </ul>
    <ul id="R_2.067.010">
        <li id="R_2.067.010ab">lubdhāyā vidito manye na te 'haṃ rāghavaṃ prati</li>
        <li id="R_2.067.010cd">tathā hy anartho rājyārthaṃ tvayā nīto mahān ayam</li>
    </ul>
    <ul id="R_2.067.011">
        <li id="R_2.067.011ab">ahaṃ hi puruṣavyāghrāv apaśyan rāmalakṣmaṇau</li>
        <li id="R_2.067.011cd">kena śaktiprabhāvena rājyaṃ rakṣitum utsahe</li>
    </ul>
    <ul id="R_2.067.012">
        <li id="R_2.067.012ab">taṃ hi nityaṃ mahārājo balavantaṃ mahābalaḥ</li>
        <li id="R_2.067.012cd">apāśrito 'bhūd dharmātmā merur meruvanaṃ yathā</li>
    </ul>
    <ul id="R_2.067.013">
        <li id="R_2.067.013ab">so 'haṃ katham imaṃ bhāraṃ mahādhuryasamudyatam</li>
        <li id="R_2.067.013cd">damyo dhuram ivāsādya saheyaṃ kena caujasā</li>
    </ul>
    <ul id="R_2.067.014">
        <li id="R_2.067.014ab">atha vā me bhavec chaktir yogair buddhibalena vā</li>
        <li id="R_2.067.014cd">sakāmāṃ na kariṣyāmi tvām ahaṃ putragardhinīm</li>
        <li id="R_2.067.014ef">nivartayiṣyāmi vanād bhrātaraṃ svajanapriyam</li>
    </ul>
    <ul id="R_2.067.015">
        <li id="R_2.067.015ab">
            <seg type="pāda" n="a">ity evam uktvā bharato mahātmā</seg>
            <seg type="pāda" n="b">priyetarair vākyagaṇais tudaṃs tām</seg>
        </li>
        <li id="R_2.067.015cd">
            <seg type="pāda" n="c">śokāturaś cāpi nanāda bhūyaḥ</seg>
            <seg type="pāda" n="d">siṃho yathā parvatagahvarasthaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.068.001">
        <li id="R_2.068.001ab">tāṃ tathā garhayitvā tu mātaraṃ bharatas tadā</li>
        <li id="R_2.068.001cd">roṣeṇa mahatāviṣṭaḥ punar evābravīd vacaḥ</li>
    </ul>
    <ul id="R_2.068.002">
        <li id="R_2.068.002ab">rājyād bhraṃśasva kaikeyi nṛśaṃse duṣṭacāriṇi</li>
        <li id="R_2.068.002cd">parityaktā ca dharmeṇa mā mṛtaṃ rudatī bhava</li>
    </ul>
    <ul id="R_2.068.003">
        <li id="R_2.068.003ab">kiṃ nu te 'dūṣayad rājā rāmo vā bhṛśadhārmikaḥ</li>
        <li id="R_2.068.003cd">yayor mṛtyur vivāsaś ca tvatkṛte tulyam āgatau</li>
    </ul>
    <ul id="R_2.068.004">
        <li id="R_2.068.004ab">bhrūṇahatyām asi prāptā kulasyāsya vināśanāt</li>
        <li id="R_2.068.004cd">kaikeyi narakaṃ gaccha mā ca bhartuḥ salokatām</li>
    </ul>
    <ul id="R_2.068.005">
        <li id="R_2.068.005ab">yat tvayā hīdṛśaṃ pāpaṃ kṛtaṃ ghoreṇa karmaṇā</li>
        <li id="R_2.068.005cd">sarvalokapriyaṃ hitvā mamāpy āpāditaṃ bhayam</li>
    </ul>
    <ul id="R_2.068.006">
        <li id="R_2.068.006ab">tvatkṛte me pitā vṛtto rāmaś cāraṇyam āśritaḥ</li>
        <li id="R_2.068.006cd">ayaśo jīvaloke ca tvayāhaṃ pratipāditaḥ</li>
    </ul>
    <ul id="R_2.068.007">
        <li id="R_2.068.007ab">mātṛrūpe mamāmitre nṛśaṃse rājyakāmuke</li>
        <li id="R_2.068.007cd">na te 'ham abhibhāṣyo 'smi durvṛtte patighātini</li>
    </ul>
    <ul id="R_2.068.008">
        <li id="R_2.068.008ab">kausalyā ca sumitrā ca yāś cānyā mama mātaraḥ</li>
        <li id="R_2.068.008cd">duḥkhena mahatāviṣṭās tvāṃ prāpya kuladūṣiṇīm</li>
    </ul>
    <ul id="R_2.068.009">
        <li id="R_2.068.009ab">na tvam aśvapateḥ kanyā dharmarājasya dhīmataḥ</li>
        <li id="R_2.068.009cd">rākṣasī tatra jātāsi kulapradhvaṃsinī pituḥ</li>
    </ul>
    <ul id="R_2.068.010">
        <li id="R_2.068.010ab">yat tvayā dhārmiko rāmo nityaṃ satyaparāyaṇaḥ</li>
        <li id="R_2.068.010cd">vanaṃ prasthāpito duḥkhāt pitā ca tridivaṃ gataḥ</li>
    </ul>
    <ul id="R_2.068.011">
        <li id="R_2.068.011ab">yat pradhānāsi tat pāpaṃ mayi pitrā vinākṛte</li>
        <li id="R_2.068.011cd">bhrātṛbhyāṃ ca parityakte sarvalokasya cāpriye</li>
    </ul>
    <ul id="R_2.068.012">
        <li id="R_2.068.012ab">kausalyāṃ dharmasaṃyuktāṃ viyuktāṃ pāpaniścaye</li>
        <li id="R_2.068.012cd">kṛtvā kaṃ prāpsyase tv adya lokaṃ nirayagāminī</li>
    </ul>
    <ul id="R_2.068.013">
        <li id="R_2.068.013ab">kiṃ nāvabudhyase krūre niyataṃ bandhusaṃśrayam</li>
        <li id="R_2.068.013cd">jyeṣṭhaṃ pitṛsamaṃ rāmaṃ kausalyāyātmasaṃbhavam</li>
    </ul>
    <ul id="R_2.068.014">
        <li id="R_2.068.014ab">aṅgapratyaṅgajaḥ putro hṛdayāc cāpi jāyate</li>
        <li id="R_2.068.014cd">tasmāt priyataro mātuḥ priyatvān na tu bāndhavaḥ</li>
    </ul>
    <ul id="R_2.068.015">
        <li id="R_2.068.015ab">anyadā kila dharmajñā surabhiḥ surasaṃmatā</li>
        <li id="R_2.068.015cd">vahamānau dadarśorvyāṃ putrau vigatacetasau</li>
    </ul>
    <ul id="R_2.068.016">
        <li id="R_2.068.016ab">tāv ardhadivase śrāntau dṛṣṭvā putrau mahītale</li>
        <li id="R_2.068.016cd">ruroda putra śokena bāṣpaparyākulekṣaṇā</li>
    </ul>
    <ul id="R_2.068.017">
        <li id="R_2.068.017ab">adhastād vrajatas tasyāḥ surarājño mahātmanaḥ</li>
        <li id="R_2.068.017cd">bindavaḥ patitā gātre sūkṣmāḥ surabhigandhinaḥ</li>
    </ul>
    <ul id="R_2.068.018">
        <li id="R_2.068.018ab">tāṃ dṛṣṭvā śokasaṃtaptāṃ vajrapāṇir yaśasvinīm</li>
        <li id="R_2.068.018cd">indraḥ prāñjalir udvignaḥ surarājo 'bravīd vacaḥ</li>
    </ul>
    <ul id="R_2.068.019">
        <li id="R_2.068.019ab">bhayaṃ kaccin na cāsmāsu kutaś cid vidyate mahat</li>
        <li id="R_2.068.019cd">kuto nimittaḥ śokas te brūhi sarvahitaiṣiṇi</li>
    </ul>
    <ul id="R_2.068.020">
        <li id="R_2.068.020ab">evam uktā tu surabhiḥ surarājena dhīmatā</li>
        <li id="R_2.068.020cd">patyuvāca tato dhīrā vākyaṃ vākyaviśāradā</li>
    </ul>
    <ul id="R_2.068.021">
        <li id="R_2.068.021ab">śāntaṃ pātaṃ na vaḥ kiṃ cit kutaś cid amarādhipa</li>
        <li id="R_2.068.021cd">ahaṃ tu magnau śocāmi svaputrau viṣame sthitau</li>
    </ul>
    <ul id="R_2.068.022">
        <li id="R_2.068.022ab">etau dṛṣṭvā kṛṣau dīnau sūryaraśmipratāpinau</li>
        <li id="R_2.068.022cd">vadhyamānau balīvardau karṣakeṇa surādhipa</li>
    </ul>
    <ul id="R_2.068.023">
        <li id="R_2.068.023ab">mama kāyāt prasūtau hi duḥkhitau bhāra pīḍitau</li>
        <li id="R_2.068.023cd">yau dṛṣṭvā paritapye 'haṃ nāsti putrasamaḥ priyaḥ</li>
    </ul>
    <ul id="R_2.068.024">
        <li id="R_2.068.024ab">yasyāḥ putra sahasrāṇi sāpi śocati kāmadhuk</li>
        <li id="R_2.068.024cd">kiṃ punar yā vinā rāmaṃ kausalyā vartayiṣyati</li>
    </ul>
    <ul id="R_2.068.025">
        <li id="R_2.068.025ab">ekaputrā ca sādhvī ca vivatseyaṃ tvayā kṛtā</li>
        <li id="R_2.068.025cd">tasmāt tvaṃ satataṃ duḥkhaṃ pretya ceha ca lapsyase</li>
    </ul>
    <ul id="R_2.068.026">
        <li id="R_2.068.026ab">ahaṃ hy apacitiṃ bhrātuḥ pituś ca sakalām imām</li>
        <li id="R_2.068.026cd">vardhanaṃ yaśasaś cāpi kariṣyāmi na saṃśayaḥ</li>
    </ul>
    <ul id="R_2.068.027">
        <li id="R_2.068.027ab">ānāyayitvā tanayaṃ kausalyāyā mahādyutim</li>
        <li id="R_2.068.027cd">svayam eva pravekṣyāmi vanaṃ muniniṣevitam</li>
    </ul>
    <ul id="R_2.068.028">
        <li id="R_2.068.028ab">iti nāga ivāraṇye tomarāṅkuśacoditaḥ</li>
        <li id="R_2.068.028cd">papāta bhuvi saṃkruddho niḥśvasann iva pannagaḥ</li>
    </ul>
    <ul id="R_2.068.029">
        <li id="R_2.068.029ab">
            <seg type="pāda" n="a">saṃraktanetraḥ śithilāmbaras tadā</seg>
            <seg type="pāda" n="b">vidhūtasarvābharaṇaḥ paraṃtapaḥ</seg>
        </li>
        <li id="R_2.068.029cd">
            <seg type="pāda" n="c">babhūva bhūmau patito nṛpātmajaḥ</seg>
            <seg type="pāda" n="d">śacīpateḥ ketur ivotsavakṣaye</seg>
        </li>
    </ul>
    <ul id="R_2.069.001">
        <li id="R_2.069.001ab">tathaiva krośatas tasya bharatasya mahātmanaḥ</li>
        <li id="R_2.069.001cd">kausalyā śabdam ājñāya sumitrām idam abravīt</li>
    </ul>
    <ul id="R_2.069.002">
        <li id="R_2.069.002ab">āgataḥ krūrakāryāyāḥ kaikeyyā bharataḥ sutaḥ</li>
        <li id="R_2.069.002cd">tam ahaṃ draṣṭum icchāmi bharataṃ dīrghadarśinam</li>
    </ul>
    <ul id="R_2.069.003">
        <li id="R_2.069.003ab">evam uktvā sumitrāṃ sā vivarṇā malināmbarā</li>
        <li id="R_2.069.003cd">pratasthe bharato yatra vepamānā vicetanā</li>
    </ul>
    <ul id="R_2.069.004">
        <li id="R_2.069.004ab">sa tu rāmānujaś cāpi śatrughnasahitas tadā</li>
        <li id="R_2.069.004cd">pratasthe bharato yatra kausalyāyā niveśanam</li>
    </ul>
    <ul id="R_2.069.005">
        <li id="R_2.069.005ab">tataḥ śatrughnabharatau kausalyāṃ prekṣya duḥkhitau</li>
        <li id="R_2.069.005cd">paryaṣvajetāṃ duḥkhārtāṃ patitāṃ naṣṭacetanām</li>
    </ul>
    <ul id="R_2.069.006">
        <li id="R_2.069.006ab">bharataṃ pratyuvācedaṃ kausalyā bhṛśaduḥkhitā</li>
        <li id="R_2.069.006cd">idaṃ te rājyakāmasya rājyaṃ prāptam akaṇṭakam</li>
        <li id="R_2.069.006ef">saṃprāptaṃ bata kaikeyyā śīghraṃ krūreṇa karmaṇā</li>
    </ul>
    <ul id="R_2.069.007">
        <li id="R_2.069.007ab">prasthāpya cīravasanaṃ putraṃ me vanavāsinam</li>
        <li id="R_2.069.007cd">kaikeyī kaṃ guṇaṃ tatra paśyati krūradarśinī</li>
    </ul>
    <ul id="R_2.069.008">
        <li id="R_2.069.008ab">kṣipraṃ mām api kaikeyī prasthāpayitum arhati</li>
        <li id="R_2.069.008cd">hiraṇyanābho yatrāste suto me sumahāyaśāḥ</li>
    </ul>
    <ul id="R_2.069.009">
        <li id="R_2.069.009ab">atha vā svayam evāhaṃ sumitrānucarā sukham</li>
        <li id="R_2.069.009cd">agnihotraṃ puraskṛtya prasthāsye yatra rāghavaḥ</li>
    </ul>
    <ul id="R_2.069.010">
        <li id="R_2.069.010ab">kāmaṃ vā svayam evādya tatra māṃ netum arhasi</li>
        <li id="R_2.069.010cd">yatrāsau puruṣavyāghras tapyate me tapaḥ sutaḥ</li>
    </ul>
    <ul id="R_2.069.011">
        <li id="R_2.069.011ab">idaṃ hi tava vistīrṇaṃ dhanadhānyasamācitam</li>
        <li id="R_2.069.011cd">hastyaśvarathasaṃpūrṇaṃ rājyaṃ niryātitaṃ tayā</li>
    </ul>
    <ul id="R_2.069.012">
        <li id="R_2.069.012ab">evaṃ vilapamānāṃ tāṃ bharataḥ prāñjalis tadā</li>
        <li id="R_2.069.012cd">kausalyāṃ pratyuvācedaṃ śokair bahubhir āvṛtām</li>
    </ul>
    <ul id="R_2.069.013">
        <li id="R_2.069.013ab">ārye kasmād ajānantaṃ garhase mām akilbiṣam</li>
        <li id="R_2.069.013cd">vipulāṃ ca mama prītiṃ sthirāṃ jānāsi rāghave</li>
    </ul>
    <ul id="R_2.069.014">
        <li id="R_2.069.014ab">kṛtā śāstrānugā buddhir mā bhūt tasya kadā cana</li>
        <li id="R_2.069.014cd">satyasaṃdhaḥ satāṃ śreṣṭho yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.015">
        <li id="R_2.069.015ab">praiṣyaṃ pāpīyasāṃ yātu sūryaṃ ca prati mehatu</li>
        <li id="R_2.069.015cd">hantu pādena gāṃ suptāṃ yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.016">
        <li id="R_2.069.016ab">kārayitvā mahat karma bhartā bhṛtyam anarthakam</li>
        <li id="R_2.069.016cd">adharmo yo 'sya so 'syās tu yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.017">
        <li id="R_2.069.017ab">paripālayamānasya rājño bhūtāni putravat</li>
        <li id="R_2.069.017cd">tatas tu druhyatāṃ pāpaṃ yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.018">
        <li id="R_2.069.018ab">baliṣaḍbhāgam uddhṛtya nṛpasyārakṣataḥ prajāḥ</li>
        <li id="R_2.069.018cd">adharmo yo 'sya so 'syāstu yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.019">
        <li id="R_2.069.019ab">saṃśrutya ca tapasvibhyaḥ satre vai yajñadakṣiṇām</li>
        <li id="R_2.069.019cd">tāṃ vipralapatāṃ pāpaṃ yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.020">
        <li id="R_2.069.020ab">hastyaśvarathasaṃbādhe yuddhe śastrasamākule</li>
        <li id="R_2.069.020cd">mā sma kārṣīt satāṃ dharmaṃ yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.021">
        <li id="R_2.069.021ab">upadiṣṭaṃ susūkṣmārthaṃ śāstraṃ yatnena dhīmatā</li>
        <li id="R_2.069.021cd">sa nāśayatu duṣṭātmā yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.022">
        <li id="R_2.069.022ab">pāyasaṃ kṛsaraṃ chāgaṃ vṛthā so 'śnātu nirghṛṇaḥ</li>
        <li id="R_2.069.022cd">gurūṃś cāpy avajānātu yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.023">
        <li id="R_2.069.023ab">putrair dāraiś ca bhṛtyaiś ca svagṛhe parivāritaḥ</li>
        <li id="R_2.069.023cd">sa eko mṛṣṭam aśnātu yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.024">
        <li id="R_2.069.024ab">rājastrībālavṛddhānāṃ vadhe yat pāpam ucyate</li>
        <li id="R_2.069.024cd">bhṛtyatyāge ca yat pāpaṃ tat pāpaṃ pratipadyatām</li>
    </ul>
    <ul id="R_2.069.025">
        <li id="R_2.069.025ab">ubhe saṃdhye śayānasya yat pāpaṃ parikalpyate</li>
        <li id="R_2.069.025cd">tac ca pāpaṃ bhavet tasya yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.026">
        <li id="R_2.069.026ab">yad agnidāyake pāpaṃ yat pāpaṃ gurutalpage</li>
        <li id="R_2.069.026cd">mitradrohe ca yat pāpaṃ tat pāpaṃ pratipadyatām</li>
    </ul>
    <ul id="R_2.069.027">
        <li id="R_2.069.027ab">devatānāṃ pitṝṇāṃ ca mātā pitros tathaiva ca</li>
        <li id="R_2.069.027cd">mā sma kārṣīt sa śuśrūṣāṃ yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.028">
        <li id="R_2.069.028ab">satāṃ lokāt satāṃ kīrtyāḥ sajjuṣṭāt karmaṇas tathā</li>
        <li id="R_2.069.028cd">bhraśyatu kṣipram adyaiva yasyāryo 'numate gataḥ</li>
    </ul>
    <ul id="R_2.069.029">
        <li id="R_2.069.029ab">vihīnāṃ patiputrābhyāṃ kausalyāṃ pārthivātmajaḥ</li>
        <li id="R_2.069.029cd">evam āśvasayann eva duḥkhārto nipapāta ha</li>
    </ul>
    <ul id="R_2.069.030">
        <li id="R_2.069.030ab">tathā tu śapathaiḥ kaṣṭaiḥ śapamānam acetanam</li>
        <li id="R_2.069.030cd">bharataṃ śokasaṃtaptaṃ kausalyā vākyam abravīt</li>
    </ul>
    <ul id="R_2.069.031">
        <li id="R_2.069.031ab">mama duḥkham idaṃ putra bhūyaḥ samupajāyate</li>
        <li id="R_2.069.031cd">śapathaiḥ śapamāno hi prāṇān uparuṇatsi me</li>
    </ul>
    <ul id="R_2.069.032">
        <li id="R_2.069.032ab">diṣṭyā na calito dharmād ātmā te sahalakṣmaṇaḥ</li>
        <li id="R_2.069.032cd">vatsa satyapratijño me satāṃ lokān avāpsyasi</li>
    </ul>
    <ul id="R_2.069.033">
        <li id="R_2.069.033ab">evaṃ vilapamānasya duḥkhārtasya mahātmanaḥ</li>
        <li id="R_2.069.033cd">mohāc ca śokasaṃrodhād babhūva lulitaṃ manaḥ</li>
    </ul>
    <ul id="R_2.069.034">
        <li id="R_2.069.034ab">
            <seg type="pāda" n="a">lālapyamānasya vicetanasya</seg>
            <seg type="pāda" n="b">pranaṣṭabuddheḥ patitasya bhūmau</seg>
        </li>
        <li id="R_2.069.034cd">
            <seg type="pāda" n="c">muhur muhur niḥśvasataś ca dīrghaṃ</seg>
            <seg type="pāda" n="d">sā tasya śokena jagāma rātriḥ</seg>
        </li>
    </ul>
    <ul id="R_2.070.001">
        <li id="R_2.070.001ab">tam evaṃ śokasaṃtaptaṃ bharataṃ kekayīsutam</li>
        <li id="R_2.070.001cd">uvāca vadatāṃ śreṣṭho vasiṣṭhaḥ śreṣṭhavāg ṛṣiḥ</li>
    </ul>
    <ul id="R_2.070.002">
        <li id="R_2.070.002ab">alaṃ śokena bhadraṃ te rājaputra mahāyaśaḥ</li>
        <li id="R_2.070.002cd">prāptakālaṃ narapateḥ kuru saṃyānam uttaram</li>
    </ul>
    <ul id="R_2.070.003">
        <li id="R_2.070.003ab">vasiṣṭhasya vacaḥ śrutvā bharato dhāraṇāṃ gataḥ</li>
        <li id="R_2.070.003cd">pretakāryāṇi sarvāṇi kārayām āsa dharmavit</li>
    </ul>
    <ul id="R_2.070.004">
        <li id="R_2.070.004ab">uddhṛtaṃ tailasaṃkledāt sa tu bhūmau niveśitam</li>
        <li id="R_2.070.004cd">āpītavarṇavadanaṃ prasuptam iva bhūmipam</li>
    </ul>
    <ul id="R_2.070.005">
        <li id="R_2.070.005ab">niveśya śayane cāgrye nānāratnapariṣkṛte</li>
        <li id="R_2.070.005cd">tato daśarathaṃ putro vilalāpa suduḥkhitaḥ</li>
    </ul>
    <ul id="R_2.070.006">
        <li id="R_2.070.006ab">kiṃ te vyavasitaṃ rājan proṣite mayy anāgate</li>
        <li id="R_2.070.006cd">vivāsya rāmaṃ dharmajñaṃ lakṣmaṇaṃ ca mahābalam</li>
    </ul>
    <ul id="R_2.070.007">
        <li id="R_2.070.007ab">kva yāsyasi mahārāja hitvemaṃ duḥkhitaṃ janam</li>
        <li id="R_2.070.007cd">hīnaṃ puruṣasiṃhena rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_2.070.008">
        <li id="R_2.070.008ab">yogakṣemaṃ tu te rājan ko 'smin kalpayitā pure</li>
        <li id="R_2.070.008cd">tvayi prayāte svas tāta rāme ca vanam āśrite</li>
    </ul>
    <ul id="R_2.070.009">
        <li id="R_2.070.009ab">vidhavā pṛthivī rājaṃs tvayā hīnā na rājate</li>
        <li id="R_2.070.009cd">hīnacandreva rajanī nagarī pratibhāti mām</li>
    </ul>
    <ul id="R_2.070.010">
        <li id="R_2.070.010ab">evaṃ vilapamānaṃ taṃ bharataṃ dīnamānasaṃ</li>
        <li id="R_2.070.010cd">abravīd vacanaṃ bhūyo vasiṣṭhas tu mahān ṛṣiḥ</li>
    </ul>
    <ul id="R_2.070.011">
        <li id="R_2.070.011ab">pretakāryāṇi yāny asya kartavyāni viśāmpateḥ</li>
        <li id="R_2.070.011cd">tāny avyagraṃ mahābāho kriyatām avicāritam</li>
    </ul>
    <ul id="R_2.070.012">
        <li id="R_2.070.012ab">tatheti bharato vākyaṃ vasiṣṭhasyābhipūjya tat</li>
        <li id="R_2.070.012cd">ṛtvikpurohitācāryāṃs tvarayām āsa sarvaśaḥ</li>
    </ul>
    <ul id="R_2.070.013">
        <li id="R_2.070.013ab">ye tv agrato narendrasya agny agārād bahiṣkṛtāḥ</li>
        <li id="R_2.070.013cd">ṛtvigbhir yājakaiś caiva te hriyante yathāvidhi</li>
    </ul>
    <ul id="R_2.070.014">
        <li id="R_2.070.014ab">śibilāyām athāropya rājānaṃ gatacetanam</li>
        <li id="R_2.070.014cd">bāṣpakaṇṭhā vimanasas tam ūhuḥ paricārakāḥ</li>
    </ul>
    <ul id="R_2.070.015">
        <li id="R_2.070.015ab">hiraṇyaṃ ca suvarṇaṃ ca vāsāṃsi vividhāni ca</li>
        <li id="R_2.070.015cd">prakiranto janā mārgaṃ nṛpater agrato yayuḥ</li>
    </ul>
    <ul id="R_2.070.016">
        <li id="R_2.070.016ab">candanāguruniryāsān saralaṃ padmakaṃ tathā</li>
        <li id="R_2.070.016cd">devadārūṇi cāhṛtya citāṃ cakrus tathāpare</li>
    </ul>
    <ul id="R_2.070.017">
        <li id="R_2.070.017ab">gandhān uccāvacāṃś cānyāṃs tatra dattvātha bhūmipam</li>
        <li id="R_2.070.017cd">tataḥ saṃveśayām āsuś citāmadhye tam ṛtvijaḥ</li>
    </ul>
    <ul id="R_2.070.018">
        <li id="R_2.070.018ab">tathā hutāśanaṃ hutvā jepus tasya tadartvijaḥ</li>
        <li id="R_2.070.018cd">jaguś ca te yathāśāstraṃ tatra sāmāni sāmagāḥ</li>
    </ul>
    <ul id="R_2.070.019">
        <li id="R_2.070.019ab">śibikābhiś ca yānaiś ca yathārhaṃ tasya yoṣitaḥ</li>
        <li id="R_2.070.019cd">nagarān niryayus tatra vṛddhaiḥ parivṛtās tadā</li>
    </ul>
    <ul id="R_2.070.020">
        <li id="R_2.070.020ab">prasavyaṃ cāpi taṃ cakrur ṛtvijo 'gnicitaṃ nṛpam</li>
        <li id="R_2.070.020cd">striyaś ca śokasaṃtaptāḥ kausalyā pramukhās tadā</li>
    </ul>
    <ul id="R_2.070.021">
        <li id="R_2.070.021ab">krauñcīnām iva nārīṇāṃ ninādas tatra śuśruve</li>
        <li id="R_2.070.021cd">ārtānāṃ karuṇaṃ kāle krośantīnāṃ sahasraśaḥ</li>
    </ul>
    <ul id="R_2.070.022">
        <li id="R_2.070.022ab">tato rudantyo vivaśā vilapya ca punaḥ punaḥ</li>
        <li id="R_2.070.022cd">yānebhyaḥ sarayūtīram avaterur varāṅganāḥ</li>
    </ul>
    <ul id="R_2.070.023">
        <li id="R_2.070.023ab">
            <seg type="pāda" n="a">kṛtodakaṃ te bharatena sārdhaṃ</seg>
            <seg type="pāda" n="b">nṛpāṅganā mantripurohitāś ca</seg>
        </li>
        <li id="R_2.070.023cd">
            <seg type="pāda" n="c">puraṃ praviśyāśruparītanetrā</seg>
            <seg type="pāda" n="d">bhūmau daśāhaṃ vyanayanta duḥkham</seg>
        </li>
    </ul>
    <ul id="R_2.071.001">
        <li id="R_2.071.001ab">tato daśāhe 'tigate kṛtaśauco nṛpātmajaḥ</li>
        <li id="R_2.071.001cd">dvādaśe 'hani saṃprāpte śrāddhakarmāṇy akārayat</li>
    </ul>
    <ul id="R_2.071.002">
        <li id="R_2.071.002ab">brāhmaṇebhyo dadau ratnaṃ dhanam annaṃ ca puṣkalam</li>
        <li id="R_2.071.002cd">bāstikaṃ bahuśuklaṃ ca gāś cāpi śataśas tathā</li>
    </ul>
    <ul id="R_2.071.003">
        <li id="R_2.071.003ab">dāsīdāsaṃ ca yānaṃ ca veśmāni sumahānti ca</li>
        <li id="R_2.071.003cd">brāhmaṇebhyo dadau putro rājñas tasyaurdhvadaihikam</li>
    </ul>
    <ul id="R_2.071.004">
        <li id="R_2.071.004ab">tataḥ prabhātasamaye divase 'tha trayodaśe</li>
        <li id="R_2.071.004cd">vilalāpa mahābāhur bharataḥ śokamūrchitaḥ</li>
    </ul>
    <ul id="R_2.071.005">
        <li id="R_2.071.005ab">śabdāpihitakaṇṭhaś ca śodhanārtham upāgataḥ</li>
        <li id="R_2.071.005cd">citāmūle pitur vākyam idam āha suduḥkhitaḥ</li>
    </ul>
    <ul id="R_2.071.006">
        <li id="R_2.071.006ab">tāta yasmin niṣṛṣṭo 'haṃ tvayā bhrātari rāghave</li>
        <li id="R_2.071.006cd">tasmin vanaṃ pravrajite śūnye tyakto 'smy ahaṃ tvayā</li>
    </ul>
    <ul id="R_2.071.007">
        <li id="R_2.071.007ab">yathāgatir anāthāyāḥ putraḥ pravrājito vanam</li>
        <li id="R_2.071.007cd">tām ambāṃ tāta kausalyāṃ tyaktvā tvaṃ kva gato nṛpa</li>
    </ul>
    <ul id="R_2.071.008">
        <li id="R_2.071.008ab">dṛṣṭvā bhasmāruṇaṃ tac ca dagdhāsthisthānamaṇḍalam</li>
        <li id="R_2.071.008cd">pituḥ śarīra nirvāṇaṃ niṣṭanan viṣasāda ha</li>
    </ul>
    <ul id="R_2.071.009">
        <li id="R_2.071.009ab">sa tu dṛṣṭvā rudan dīnaḥ papāta dharaṇītale</li>
        <li id="R_2.071.009cd">utthāpyamānaḥ śakrasya yantra dhvaja iva cyutaḥ</li>
    </ul>
    <ul id="R_2.071.010">
        <li id="R_2.071.010ab">abhipetus tataḥ sarve tasyāmātyāḥ śucivratam</li>
        <li id="R_2.071.010cd">antakāle nipatitaṃ yayātim ṛṣayo yathā</li>
    </ul>
    <ul id="R_2.071.011">
        <li id="R_2.071.011ab">śatrughnaś cāpi bharataṃ dṛṣṭvā śokapariplutam</li>
        <li id="R_2.071.011cd">visaṃjño nyapatad bhūmau bhūmipālam anusmaran</li>
    </ul>
    <ul id="R_2.071.012">
        <li id="R_2.071.012ab">unmatta iva niścetā vilalāpa suduḥkhitaḥ</li>
        <li id="R_2.071.012cd">smṛtvā pitur guṇāṅgāni tāni tāni tadā tadā</li>
    </ul>
    <ul id="R_2.071.013">
        <li id="R_2.071.013ab">mantharā prabhavas tīvraḥ kaikeyīgrāhasaṃkulaḥ</li>
        <li id="R_2.071.013cd">varadānamayo 'kṣobhyo 'majjayac chokasāgaraḥ</li>
    </ul>
    <ul id="R_2.071.014">
        <li id="R_2.071.014ab">sukumāraṃ ca bālaṃ ca satataṃ lālitaṃ tvayā</li>
        <li id="R_2.071.014cd">kva tāta bharataṃ hitvā vilapantaṃ gato bhavān</li>
    </ul>
    <ul id="R_2.071.015">
        <li id="R_2.071.015ab">nanu bhojyeṣu pāneṣu vastreṣv ābharaṇeṣu ca</li>
        <li id="R_2.071.015cd">pravārayasi naḥ sarvāṃs tan naḥ ko 'dya kariṣyati</li>
    </ul>
    <ul id="R_2.071.016">
        <li id="R_2.071.016ab">avadāraṇa kāle tu pṛthivī nāvadīryate</li>
        <li id="R_2.071.016cd">vihīnā yā tvayā rājñā dharmajñena mahātmanā</li>
    </ul>
    <ul id="R_2.071.017">
        <li id="R_2.071.017ab">pitari svargam āpanne rāme cāraṇyam āśrite</li>
        <li id="R_2.071.017cd">kiṃ me jīvita sāmarthyaṃ pravekṣyāmi hutāśanam</li>
    </ul>
    <ul id="R_2.071.018">
        <li id="R_2.071.018ab">hīno bhrātrā ca pitrā ca śūnyām ikṣvākupālitām</li>
        <li id="R_2.071.018cd">ayodhyāṃ na pravekṣyāmi pravekṣyāmi tapovanam</li>
    </ul>
    <ul id="R_2.071.019">
        <li id="R_2.071.019ab">tayor vilapitaṃ śrutvā vyasanaṃ cānvavekṣya tat</li>
        <li id="R_2.071.019cd">bhṛśam ārtatarā bhūyaḥ sarva evānugāminaḥ</li>
    </ul>
    <ul id="R_2.071.020">
        <li id="R_2.071.020ab">tato viṣaṇṇau śrāntau ca śatrughnabharatāv ubhau</li>
        <li id="R_2.071.020cd">dharaṇyāṃ saṃvyaceṣṭetāṃ bhagnaśṛṅgāv ivarṣabhau</li>
    </ul>
    <ul id="R_2.071.021">
        <li id="R_2.071.021ab">tataḥ prakṛtimān vaidyaḥ pitur eṣāṃ purohitaḥ</li>
        <li id="R_2.071.021cd">vasiṣṭho bharataṃ vākyam utthāpya tam uvāca ha</li>
    </ul>
    <ul id="R_2.071.022">
        <li id="R_2.071.022ab">trīṇi dvandvāni bhūteṣu pravṛttāny aviśeṣataḥ</li>
        <li id="R_2.071.022cd">teṣu cāparihāryeṣu naivaṃ bhavitum arhati</li>
    </ul>
    <ul id="R_2.071.023">
        <li id="R_2.071.023ab">sumantraś cāpi śatrughnam utthāpyābhiprasādya ca</li>
        <li id="R_2.071.023cd">śrāvayām āsa tattvajñaḥ sarvabhūtabhavābhavau</li>
    </ul>
    <ul id="R_2.071.024">
        <li id="R_2.071.024ab">utthitau tau naravyāghrau prakāśete yaśasvinau</li>
        <li id="R_2.071.024cd">varṣātapapariklinnau pṛthag indradhvajāv iva</li>
    </ul>
    <ul id="R_2.071.025">
        <li id="R_2.071.025ab">aśrūṇi parimṛdnantau raktākṣau dīnabhāṣiṇau</li>
        <li id="R_2.071.025cd">amātyās tvarayanti sma tanayau cāparāḥ kriyāḥ</li>
    </ul>
    <ul id="R_2.072.001">
        <li id="R_2.072.001ab">atra yātrāṃ samīhantaṃ śatrughno lakṣmaṇānujaḥ</li>
        <li id="R_2.072.001cd">bharataṃ śokasaṃtaptam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.072.002">
        <li id="R_2.072.002ab">gatir yaḥ sarvabhūtānāṃ duḥkhe kiṃ punar ātmanaḥ</li>
        <li id="R_2.072.002cd">sa rāmaḥ sattva saṃpannaḥ striyā pravrājito vanam</li>
    </ul>
    <ul id="R_2.072.003">
        <li id="R_2.072.003ab">balavān vīrya saṃpanno lakṣmaṇo nāma yo 'py asau</li>
        <li id="R_2.072.003cd">kiṃ na mocayate rāmaṃ kṛtvāpi pitṛnigraham</li>
    </ul>
    <ul id="R_2.072.004">
        <li id="R_2.072.004ab">pūrvam eva tu nigrāhyaḥ samavekṣya nayānayau</li>
        <li id="R_2.072.004cd">utpathaṃ yaḥ samārūḍho nāryā rājā vaśaṃ gataḥ</li>
    </ul>
    <ul id="R_2.072.005">
        <li id="R_2.072.005ab">iti saṃbhāṣamāṇe tu śatrughne lakṣmaṇānuje</li>
        <li id="R_2.072.005cd">prāgdvāre 'bhūt tadā kubjā sarvābharaṇabhūṣitā</li>
    </ul>
    <ul id="R_2.072.006">
        <li id="R_2.072.006ab">liptā candanasāreṇa rājavastrāṇi bibhratī</li>
        <li id="R_2.072.006cd">mekhalā dāmabhiś citrai rajjubaddheva vānarī</li>
    </ul>
    <ul id="R_2.072.007">
        <li id="R_2.072.007ab">tāṃ samīkṣya tadā dvāḥstho bhṛśaṃ pāpasya kāriṇīm</li>
        <li id="R_2.072.007cd">gṛhītvākaruṇaṃ kubjāṃ śatrughnāya nyavedayat</li>
    </ul>
    <ul id="R_2.072.008">
        <li id="R_2.072.008ab">yasyāḥ kṛte vane rāmo nyastadehaś ca vaḥ pitā</li>
        <li id="R_2.072.008cd">seyaṃ pāpā nṛśaṃsā ca tasyāḥ kuru yathāmati</li>
    </ul>
    <ul id="R_2.072.009">
        <li id="R_2.072.009ab">śatrughnaś ca tad ājñāya vacanaṃ bhṛśaduḥkhitaḥ</li>
        <li id="R_2.072.009cd">antaḥpuracarān sarvān ity uvāca dhṛtavrataḥ</li>
    </ul>
    <ul id="R_2.072.010">
        <li id="R_2.072.010ab">tīvram utpāditaṃ duḥkhaṃ bhrātṝṇāṃ me tathā pituḥ</li>
        <li id="R_2.072.010cd">yayā seyaṃ nṛśaṃsasya karmaṇaḥ phalam aśnutām</li>
    </ul>
    <ul id="R_2.072.011">
        <li id="R_2.072.011ab">evam uktā ca tenāśu sakhī janasamāvṛtā</li>
        <li id="R_2.072.011cd">gṛhītā balavat kubjā sā tadgṛham anādayat</li>
    </ul>
    <ul id="R_2.072.012">
        <li id="R_2.072.012ab">tataḥ subhṛśa saṃtaptas tasyāḥ sarvaḥ sakhījanaḥ</li>
        <li id="R_2.072.012cd">kruddham ājñāya śatrughnaṃ vyapalāyata sarvaśaḥ</li>
    </ul>
    <ul id="R_2.072.013">
        <li id="R_2.072.013ab">amantrayata kṛtsnaś ca tasyāḥ sarvasakhījanaḥ</li>
        <li id="R_2.072.013cd">yathāyaṃ samupakrānto niḥśeṣaṃ naḥ kariṣyati</li>
    </ul>
    <ul id="R_2.072.014">
        <li id="R_2.072.014ab">sānukrośāṃ vadānyāṃ ca dharmajñāṃ ca yaśasvinīm</li>
        <li id="R_2.072.014cd">kausalyāṃ śaraṇaṃ yāmaḥ sā hi no 'stu dhruvā gatiḥ</li>
    </ul>
    <ul id="R_2.072.015">
        <li id="R_2.072.015ab">sa ca roṣeṇa tāmrākṣaḥ śatrughnaḥ śatrutāpanaḥ</li>
        <li id="R_2.072.015cd">vicakarṣa tadā kubjāṃ krośantīṃ pṛthivītale</li>
    </ul>
    <ul id="R_2.072.016">
        <li id="R_2.072.016ab">tasyā hy ākṛṣyamāṇāyā mantharāyās tatas tataḥ</li>
        <li id="R_2.072.016cd">citraṃ bahuvidhaṃ bhāṇḍaṃ pṛthivyāṃ tad vyaśīryata</li>
    </ul>
    <ul id="R_2.072.017">
        <li id="R_2.072.017ab">tena bhāṇḍena saṃkīrṇaṃ śrīmadrājaniveśanam</li>
        <li id="R_2.072.017cd">aśobhata tadā bhūyaḥ śāradaṃ gaganaṃ yathā</li>
    </ul>
    <ul id="R_2.072.018">
        <li id="R_2.072.018ab">sa balī balavat krodhād gṛhītvā puruṣarṣabhaḥ</li>
        <li id="R_2.072.018cd">kaikeyīm abhinirbhartsya babhāṣe paruṣaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.072.019">
        <li id="R_2.072.019ab">tair vākyaiḥ paruṣair duḥkhaiḥ kaikeyī bhṛśaduḥkhitā</li>
        <li id="R_2.072.019cd">śatrughna bhayasaṃtrastā putraṃ śaraṇam āgatā</li>
    </ul>
    <ul id="R_2.072.020">
        <li id="R_2.072.020ab">tāṃ prekṣya bharataḥ kruddhaṃ śatrughnam idam abravīt</li>
        <li id="R_2.072.020cd">avadhyāḥ sarvabhūtānāṃ pramadāḥ kṣamyatām iti</li>
    </ul>
    <ul id="R_2.072.021">
        <li id="R_2.072.021ab">hanyām aham imāṃ pāpāṃ kaikeyīṃ duṣṭacāriṇīm</li>
        <li id="R_2.072.021cd">yadi māṃ dhārmiko rāmo nāsūyen mātṛghātakam</li>
    </ul>
    <ul id="R_2.072.022">
        <li id="R_2.072.022ab">imām api hatāṃ kubjāṃ yadi jānāti rāghavaḥ</li>
        <li id="R_2.072.022cd">tvāṃ ca māṃ caiva dharmātmā nābhibhāṣiṣyate dhruvam</li>
    </ul>
    <ul id="R_2.072.023">
        <li id="R_2.072.023ab">bharatasya vacaḥ śrutvā śatrughno lakṣmaṇānujaḥ</li>
        <li id="R_2.072.023cd">nyavartata tato roṣāt tāṃ mumoca ca mantharām</li>
    </ul>
    <ul id="R_2.072.024">
        <li id="R_2.072.024ab">sā pādamūle kaikeyyā mantharā nipapāta ha</li>
        <li id="R_2.072.024cd">niḥśvasantī suduḥkhārtā kṛpaṇaṃ vilalāpa ca</li>
    </ul>
    <ul id="R_2.072.025">
        <li id="R_2.072.025ab">
            <seg type="pāda" n="a">śatrughnavikṣepavimūḍhasaṃjñāṃ</seg>
            <seg type="pāda" n="b">samīkṣya kubjāṃ bharatasya mātā</seg>
        </li>
        <li id="R_2.072.025cd">
            <seg type="pāda" n="c">śanaiḥ samāśvāsayad ārtarūpāṃ</seg>
            <seg type="pāda" n="d">krauñcīṃ vilagnām iva vīkṣamāṇām</seg>
        </li>
    </ul>
    <ul id="R_2.073.001">
        <li id="R_2.073.001ab">tataḥ prabhātasamaye divase 'tha caturdaśe</li>
        <li id="R_2.073.001cd">sametya rājakartāro bharataṃ vākyam abruvan</li>
    </ul>
    <ul id="R_2.073.002">
        <li id="R_2.073.002ab">gato daśarathaḥ svargaṃ yo no gurutaro guruḥ</li>
        <li id="R_2.073.002cd">rāmaṃ pravrājya vai jyeṣṭhaṃ lakṣmaṇaṃ ca mahābalam</li>
    </ul>
    <ul id="R_2.073.003">
        <li id="R_2.073.003ab">tvam adya bhava no rājā rājaputra mahāyaśaḥ</li>
        <li id="R_2.073.003cd">saṃgatyā nāparādhnoti rājyam etad anāyakam</li>
    </ul>
    <ul id="R_2.073.004">
        <li id="R_2.073.004ab">ābhiṣecanikaṃ sarvam idam ādāya rāghava</li>
        <li id="R_2.073.004cd">pratīkṣate tvāṃ svajanaḥ śreṇayaś ca nṛpātmaja</li>
    </ul>
    <ul id="R_2.073.005">
        <li id="R_2.073.005ab">rājyaṃ gṛhāṇa bharata pitṛpaitāmahaṃ mahat</li>
        <li id="R_2.073.005cd">abhiṣecaya cātmānaṃ pāhi cāsmān nararṣabha</li>
    </ul>
    <ul id="R_2.073.006">
        <li id="R_2.073.006ab">ābhiṣecanikaṃ bhāṇḍaṃ kṛtvā sarvaṃ pradakṣiṇam</li>
        <li id="R_2.073.006cd">bharatas taṃ janaṃ sarvaṃ pratyuvāca dhṛtavrataḥ</li>
    </ul>
    <ul id="R_2.073.007">
        <li id="R_2.073.007ab">jyeṣṭhasya rājatā nityam ucitā hi kulasya naḥ</li>
        <li id="R_2.073.007cd">naivaṃ bhavanto māṃ vaktum arhanti kuśalā janāḥ</li>
    </ul>
    <ul id="R_2.073.008">
        <li id="R_2.073.008ab">rāmaḥ pūrvo hi no bhrātā bhaviṣyati mahīpatiḥ</li>
        <li id="R_2.073.008cd">ahaṃ tv araṇye vatsyāmi varṣāṇi nava pañca ca</li>
    </ul>
    <ul id="R_2.073.009">
        <li id="R_2.073.009ab">yujyatāṃ mahatī senā caturaṅgamahābalā</li>
        <li id="R_2.073.009cd">ānayiṣyāmy ahaṃ jyeṣṭhaṃ bhrātaraṃ rāghavaṃ vanāt</li>
    </ul>
    <ul id="R_2.073.010">
        <li id="R_2.073.010ab">ābhiṣecanikaṃ caiva sarvam etad upaskṛtam</li>
        <li id="R_2.073.010cd">puraskṛtya gamiṣyāmi rāmahetor vanaṃ prati</li>
    </ul>
    <ul id="R_2.073.011">
        <li id="R_2.073.011ab">tatraiva taṃ naravyāghram abhiṣicya puraskṛtam</li>
        <li id="R_2.073.011cd">āneṣyāmi tu vai rāmaṃ havyavāham ivādhvarāt</li>
    </ul>
    <ul id="R_2.073.012">
        <li id="R_2.073.012ab">na sakāmā kariṣyāmi svam imāṃ mātṛgandhinīm</li>
        <li id="R_2.073.012cd">vane vatsyāmy ahaṃ durge rāmo rājā bhaviṣyati</li>
    </ul>
    <ul id="R_2.073.013">
        <li id="R_2.073.013ab">kriyatāṃ śilpibhiḥ panthāḥ samāni viṣamāṇi ca</li>
        <li id="R_2.073.013cd">rakṣiṇaś cānusaṃyāntu pathi durgavicārakāḥ</li>
    </ul>
    <ul id="R_2.073.014">
        <li id="R_2.073.014ab">evaṃ saṃbhāṣamāṇaṃ taṃ rāmahetor nṛpātmajam</li>
        <li id="R_2.073.014cd">pratyuvāca janaḥ sarvaḥ śrīmadvākyam anuttamam</li>
    </ul>
    <ul id="R_2.073.015">
        <li id="R_2.073.015ab">evaṃ te bhāṣamāṇasya padmā śrīr upatiṣṭhatām</li>
        <li id="R_2.073.015cd">yas tvaṃ jyeṣṭhe nṛpasute pṛthivīṃ dātum icchasi</li>
    </ul>
    <ul id="R_2.073.016">
        <li id="R_2.073.016ab">
            <seg type="pāda" n="a">anuttamaṃ tad vacanaṃ nṛpātmaja</seg>
            <seg type="pāda" n="b">prabhāṣitaṃ saṃśravaṇe niśamya ca</seg>
        </li>
        <li id="R_2.073.016cd">
            <seg type="pāda" n="c">praharṣajās taṃ prati bāṣpabindavo</seg>
            <seg type="pāda" n="d">nipetur āryānananetrasaṃbhavāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.073.017">
        <li id="R_2.073.017ab">
            <seg type="pāda" n="a">ūcus te vacanam idaṃ niśamya hṛṣṭāḥ</seg>
            <seg type="pāda" n="b">sāmātyāḥ sapariṣado viyātaśokāḥ</seg>
        </li>
        <li id="R_2.073.017cd">
            <seg type="pāda" n="c">panthānaṃ naravarabhaktimāñ janaś ca</seg>
            <seg type="pāda" n="d">vyādiṣṭas tava vacanāc ca śilpivargaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.074.001">
        <li id="R_2.074.001ab">atha bhūmipradeśajñāḥ sūtrakarmaviśāradāḥ</li>
        <li id="R_2.074.001cd">svakarmābhiratāḥ śūrāḥ khanakā yantrakās tathā</li>
    </ul>
    <ul id="R_2.074.002">
        <li id="R_2.074.002ab">karmāntikāḥ sthapatayaḥ puruṣā yantrakovidāḥ</li>
        <li id="R_2.074.002cd">tathā vardhakayaś caiva mārgiṇo vṛkṣatakṣakāḥ</li>
    </ul>
    <ul id="R_2.074.003">
        <li id="R_2.074.003ab">kūpakārāḥ sudhākārā vaṃśakarmakṛtas tathā</li>
        <li id="R_2.074.003cd">samarthā ye ca draṣṭāraḥ puratas te pratasthire</li>
    </ul>
    <ul id="R_2.074.004">
        <li id="R_2.074.004ab">sa tu harṣāt tam uddeśaṃ janaugho vipulaḥ prayān</li>
        <li id="R_2.074.004cd">aśobhata mahāvegaḥ sāgarasyeva parvaṇi</li>
    </ul>
    <ul id="R_2.074.005">
        <li id="R_2.074.005ab">te svavāraṃ samāsthāya vartmakarmāṇi kovidāḥ</li>
        <li id="R_2.074.005cd">karaṇair vividhopetaiḥ purastāt saṃpratasthire</li>
    </ul>
    <ul id="R_2.074.006">
        <li id="R_2.074.006ab">latāvallīś ca gulmāṃś ca sthāṇūn aśmana eva ca</li>
        <li id="R_2.074.006cd">janās te cakrire mārgaṃ chindanto vividhān drumān</li>
    </ul>
    <ul id="R_2.074.007">
        <li id="R_2.074.007ab">avṛkṣeṣu ca deśeṣu ke cid vṛkṣān aropayan</li>
        <li id="R_2.074.007cd">ke cit kuṭhāraiṣ ṭaṅkaiś ca dātraiś chindan kva cit kva cit</li>
    </ul>
    <ul id="R_2.074.008">
        <li id="R_2.074.008ab">apare vīraṇastambān balino balavattarāḥ</li>
        <li id="R_2.074.008cd">vidhamanti sma durgāṇi sthalāni ca tatas tataḥ</li>
    </ul>
    <ul id="R_2.074.009">
        <li id="R_2.074.009ab">apare 'pūrayan kūpān pāṃsubhiḥ śvabhram āyatam</li>
        <li id="R_2.074.009cd">nimnabhāgāṃs tathā ke cit samāṃś cakruḥ samantataḥ</li>
    </ul>
    <ul id="R_2.074.010">
        <li id="R_2.074.010ab">babandhur bandhanīyāṃś ca kṣodyān saṃcukṣudus tadā</li>
        <li id="R_2.074.010cd">bibhidur bhedanīyāṃś ca tāṃs tān deśān narās tadā</li>
    </ul>
    <ul id="R_2.074.011">
        <li id="R_2.074.011ab">acireṇaiva kālena parivāhān bahūdakān</li>
        <li id="R_2.074.011cd">cakrur bahuvidhākārān sāgarapratimān bahūn</li>
        <li id="R_2.074.011ef">udapānān bahuvidhān vedikā parimaṇḍitān</li>
    </ul>
    <ul id="R_2.074.012">
        <li id="R_2.074.012ab">sasudhākuṭṭimatalaḥ prapuṣpitamahīruhaḥ</li>
        <li id="R_2.074.012cd">mattodghuṣṭadvijagaṇaḥ patākābhir alaṃkṛtaḥ</li>
    </ul>
    <ul id="R_2.074.013">
        <li id="R_2.074.013ab">candanodakasaṃsikto nānākusumabhūṣitaḥ</li>
        <li id="R_2.074.013cd">bahv aśobhata senāyāḥ panthāḥ svargapathopamaḥ</li>
    </ul>
    <ul id="R_2.074.014">
        <li id="R_2.074.014ab">ājñāpyātha yathājñapti yuktās te 'dhikṛtā narāḥ</li>
        <li id="R_2.074.014cd">ramaṇīyeṣu deśeṣu bahusvāduphaleṣu ca</li>
    </ul>
    <ul id="R_2.074.015">
        <li id="R_2.074.015ab">yo niveśas tv abhipreto bharatasya mahātmanaḥ</li>
        <li id="R_2.074.015cd">bhūyas taṃ śobhayām āsur bhūṣābhir bhūṣaṇopamam</li>
    </ul>
    <ul id="R_2.074.016">
        <li id="R_2.074.016ab">nakṣatreṣu praśasteṣu muhūrteṣu ca tadvidaḥ</li>
        <li id="R_2.074.016cd">niveśaṃ sthāpayām āsur bharatasya mahātmanaḥ</li>
    </ul>
    <ul id="R_2.074.017">
        <li id="R_2.074.017ab">bahupāṃsucayāś cāpi parikhāparivāritāḥ</li>
        <li id="R_2.074.017cd">tatrendrakīlapratimāḥ pratolīvaraśobhitāḥ</li>
    </ul>
    <ul id="R_2.074.018">
        <li id="R_2.074.018ab">prāsādamālāsaṃyuktāḥ saudhaprākārasaṃvṛtāḥ</li>
        <li id="R_2.074.018cd">patākā śobhitāḥ sarve sunirmitamahāpathāḥ</li>
    </ul>
    <ul id="R_2.074.019">
        <li id="R_2.074.019ab">visarpadbhir ivākāśe viṭaṅkāgravimānakaiḥ</li>
        <li id="R_2.074.019cd">samucchritair niveśās te babhuḥ śakrapuropamāḥ</li>
    </ul>
    <ul id="R_2.074.020">
        <li id="R_2.074.020ab">jāhnavīṃ tu samāsādya vividhadruma kānanām</li>
        <li id="R_2.074.020cd">śītalāmalapānīyāṃ mahāmīnasamākulām</li>
    </ul>
    <ul id="R_2.074.021">
        <li id="R_2.074.021ab">
            <seg type="pāda" n="a">sacandratārāgaṇamaṇḍitaṃ yathā</seg>
            <seg type="pāda" n="b">nabhaḥkṣapāyām amalaṃ virājate</seg>
        </li>
        <li id="R_2.074.021cd">
            <seg type="pāda" n="c">narendramārgaḥ sa tathā vyarājata</seg>
            <seg type="pāda" n="d">krameṇa ramyaḥ śubhaśilpinirmitaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.075.001">
        <li id="R_2.075.001ab">tato nāndīmukhīṃ rātriṃ bharataṃ sūtamāgadhāḥ</li>
        <li id="R_2.075.001cd">tuṣṭuvur vāgviśeṣajñāḥ stavair maṅgalasaṃhitaiḥ</li>
    </ul>
    <ul id="R_2.075.002">
        <li id="R_2.075.002ab">suvarṇakoṇābhihataḥ prāṇadad yāmadundubhiḥ</li>
        <li id="R_2.075.002cd">dadhmuḥ śaṅkhāṃś ca śataśo vādyāṃś coccāvacasvarān</li>
    </ul>
    <ul id="R_2.075.003">
        <li id="R_2.075.003ab">sa tūrya ghoṣaḥ sumahān divam āpūrayann iva</li>
        <li id="R_2.075.003cd">bharataṃ śokasaṃtaptaṃ bhūyaḥ śokair arandhrayat</li>
    </ul>
    <ul id="R_2.075.004">
        <li id="R_2.075.004ab">tato prabuddho bharatas taṃ ghoṣaṃ saṃnivartya ca</li>
        <li id="R_2.075.004cd">nāhaṃ rājeti cāpy uktvā śatrughnam idam abravīt</li>
    </ul>
    <ul id="R_2.075.005">
        <li id="R_2.075.005ab">paśya śatrughna kaikeyyā lokasyāpakṛtaṃ mahat</li>
        <li id="R_2.075.005cd">visṛjya mayi duḥkhāni rājā daśaratho gataḥ</li>
    </ul>
    <ul id="R_2.075.006">
        <li id="R_2.075.006ab">tasyaiṣā dharmarājasya dharmamūlā mahātmanaḥ</li>
        <li id="R_2.075.006cd">paribhramati rājaśrīr naur ivākarṇikā jale</li>
    </ul>
    <ul id="R_2.075.007">
        <li id="R_2.075.007ab">ity evaṃ bharataṃ prekṣya vilapantaṃ vicetanam</li>
        <li id="R_2.075.007cd">kṛpaṇaṃ ruruduḥ sarvāḥ sasvaraṃ yoṣitas tadā</li>
    </ul>
    <ul id="R_2.075.008">
        <li id="R_2.075.008ab">tathā tasmin vilapati vasiṣṭho rājadharmavit</li>
        <li id="R_2.075.008cd">sabhām ikṣvākunāthasya praviveśa mahāyaśāḥ</li>
    </ul>
    <ul id="R_2.075.009">
        <li id="R_2.075.009ab">śāta kumbhamayīṃ ramyāṃ maṇiratnasamākulām</li>
        <li id="R_2.075.009cd">sudharmām iva dharmātmā sagaṇaḥ pratyapadyata</li>
    </ul>
    <ul id="R_2.075.010">
        <li id="R_2.075.010ab">sa kāñcanamayaṃ pīṭhaṃ parārdhyāstaraṇāvṛtam</li>
        <li id="R_2.075.010cd">adhyāsta sarvavedajño dūtān anuśaśāsa ca</li>
    </ul>
    <ul id="R_2.075.011">
        <li id="R_2.075.011ab">brāhmaṇān kṣatriyān yodhān amātyān gaṇaballabhān</li>
        <li id="R_2.075.011cd">kṣipram ānayatāvyagrāḥ kṛtyam ātyayikaṃ hi naḥ</li>
    </ul>
    <ul id="R_2.075.012">
        <li id="R_2.075.012ab">tato halahalāśabdo mahān samudapadyata</li>
        <li id="R_2.075.012cd">rathair aśvair gajaiś cāpi janānām upagacchatām</li>
    </ul>
    <ul id="R_2.075.013">
        <li id="R_2.075.013ab">tato bharatam āyāntaṃ śatakratum ivāmarāḥ</li>
        <li id="R_2.075.013cd">pratyanandan prakṛtayo yathā daśarathaṃ tathā</li>
    </ul>
    <ul id="R_2.075.014">
        <li id="R_2.075.014ab">
            <seg type="pāda" n="a">hrada iva timināgasaṃvṛtaḥ</seg>
            <seg type="pāda" n="b">stimitajalo maṇiśaṅkhaśarkaraḥ</seg>
        </li>
        <li id="R_2.075.014cd">
            <seg type="pāda" n="c">daśarathasutaśobhitā sabhā</seg>
            <seg type="pāda" n="d">sadaśaratheva babhau yathā purā</seg>
        </li>
    </ul>
    <ul id="R_2.076.001">
        <li id="R_2.076.001ab">tām āryagaṇasaṃpūrṇāṃ bharataḥ pragrahāṃ sabhām</li>
        <li id="R_2.076.001cd">dadarśa buddhisaṃpannaḥ pūrṇacandrāṃ niśām iva</li>
    </ul>
    <ul id="R_2.076.002">
        <li id="R_2.076.002ab">āsanāni yathānyāyam āryāṇāṃ viśatāṃ tadā</li>
        <li id="R_2.076.002cd">adṛśyata ghanāpāye pūrṇacandreva śarvarī</li>
    </ul>
    <ul id="R_2.076.003">
        <li id="R_2.076.003ab">rājñas tu prakṛtīḥ sarvāḥ samagrāḥ prekṣya dharmavit</li>
        <li id="R_2.076.003cd">idaṃ purohito vākyaṃ bharataṃ mṛdu cābravīt</li>
    </ul>
    <ul id="R_2.076.004">
        <li id="R_2.076.004ab">tāta rājā daśarathaḥ svargato dharmam ācaran</li>
        <li id="R_2.076.004cd">dhana dhānyavatīṃ sphītāṃ pradāya pṛthivīṃ tava</li>
    </ul>
    <ul id="R_2.076.005">
        <li id="R_2.076.005ab">rāmas tathā satyadhṛtiḥ satāṃ dharmam anusmaran</li>
        <li id="R_2.076.005cd">nājahāt pitur ādeśaṃ śaśī jyotsnām ivoditaḥ</li>
    </ul>
    <ul id="R_2.076.006">
        <li id="R_2.076.006ab">pitrā bhrātrā ca te dattaṃ rājyaṃ nihatakaṇṭakam</li>
        <li id="R_2.076.006cd">tad bhuṅkṣva muditāmātyaḥ kṣipram evābhiṣecaya</li>
    </ul>
    <ul id="R_2.076.007">
        <li id="R_2.076.007ab">udīcyāś ca pratīcyāś ca dākṣiṇātyāś ca kevalāḥ</li>
        <li id="R_2.076.007cd">koṭyāparāntāḥ sāmudrā ratnāny abhiharantu te</li>
    </ul>
    <ul id="R_2.076.008">
        <li id="R_2.076.008ab">tac chrutvā bharato vākyaṃ śokenābhipariplutaḥ</li>
        <li id="R_2.076.008cd">jagāma manasā rāmaṃ dharmajño dharmakāṅkṣayā</li>
    </ul>
    <ul id="R_2.076.009">
        <li id="R_2.076.009ab">sa bāṣpakalayā vācā kalahaṃsasvaro yuvā</li>
        <li id="R_2.076.009cd">vilalāpa sabhāmadhye jagarhe ca purohitam</li>
    </ul>
    <ul id="R_2.076.010">
        <li id="R_2.076.010ab">caritabrahmacaryasya vidyā snātasya dhīmataḥ</li>
        <li id="R_2.076.010cd">dharme prayatamānasya ko rājyaṃ madvidho haret</li>
    </ul>
    <ul id="R_2.076.011">
        <li id="R_2.076.011ab">kathaṃ daśarathāj jāto bhaved rājyāpahārakaḥ</li>
        <li id="R_2.076.011cd">rājyaṃ cāhaṃ ca rāmasya dharmaṃ vaktum ihārhasi</li>
    </ul>
    <ul id="R_2.076.012">
        <li id="R_2.076.012ab">jyeṣṭhaḥ śreṣṭhaś ca dharmātmā dilīpanahuṣopamaḥ</li>
        <li id="R_2.076.012cd">labdhum arhati kākutstho rājyaṃ daśaratho yathā</li>
    </ul>
    <ul id="R_2.076.013">
        <li id="R_2.076.013ab">anāryajuṣṭam asvargyaṃ kuryāṃ pāpam ahaṃ yadi</li>
        <li id="R_2.076.013cd">ikṣvākūṇām ahaṃ loke bhaveyaṃ kulapāṃsanaḥ</li>
    </ul>
    <ul id="R_2.076.014">
        <li id="R_2.076.014ab">yad dhi mātrā kṛtaṃ pāpaṃ nāhaṃ tad abhirocaye</li>
        <li id="R_2.076.014cd">ihastho vanadurgasthaṃ namasyāmi kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_2.076.015">
        <li id="R_2.076.015ab">rāmam evānugacchāmi sa rājā dvipadāṃ varaḥ</li>
        <li id="R_2.076.015cd">trayāṇām api lokānāṃ rāghavo rājyam arhati</li>
    </ul>
    <ul id="R_2.076.016">
        <li id="R_2.076.016ab">tad vākyaṃ dharmasaṃyuktaṃ śrutvā sarve sabhāsadaḥ</li>
        <li id="R_2.076.016cd">harṣān mumucur aśrūṇi rāme nihitacetasaḥ</li>
    </ul>
    <ul id="R_2.076.017">
        <li id="R_2.076.017ab">yadi tv āryaṃ na śakṣyāmi vinivartayituṃ vanāt</li>
        <li id="R_2.076.017cd">vane tatraiva vatsyāmi yathāryo lakṣmaṇas tathā</li>
    </ul>
    <ul id="R_2.076.018">
        <li id="R_2.076.018ab">sarvopāyaṃ tu vartiṣye vinivartayituṃ balāt</li>
        <li id="R_2.076.018cd">samakṣam ārya miśrāṇāṃ sādhūnāṃ guṇavartinām</li>
    </ul>
    <ul id="R_2.076.019">
        <li id="R_2.076.019ab">evam uktvā tu dharmātmā bharato bhrātṛvatsalaḥ</li>
        <li id="R_2.076.019cd">samīpastham uvācedaṃ sumantraṃ mantrakovidam</li>
    </ul>
    <ul id="R_2.076.020">
        <li id="R_2.076.020ab">tūrṇam utthāya gaccha tvaṃ sumantra mama śāsanāt</li>
        <li id="R_2.076.020cd">yātrām ājñāpaya kṣipraṃ balaṃ caiva samānaya</li>
    </ul>
    <ul id="R_2.076.021">
        <li id="R_2.076.021ab">evam uktaḥ sumantras tu bharatena mahātmanā</li>
        <li id="R_2.076.021cd">prahṛṣṭaḥ so 'diśat sarvaṃ yathā saṃdiṣṭam iṣṭavat</li>
    </ul>
    <ul id="R_2.076.022">
        <li id="R_2.076.022ab">tāḥ prahṛṣṭāḥ prakṛtayo balādhyakṣā balasya ca</li>
        <li id="R_2.076.022cd">śrutvā yātrāṃ samājñaptāṃ rāghavasya nivartane</li>
    </ul>
    <ul id="R_2.076.023">
        <li id="R_2.076.023ab">tato yodhāṅganāḥ sarvā bhartṝn sarvān gṛhegṛhe</li>
        <li id="R_2.076.023cd">yātrā gamanam ājñāya tvarayanti sma harṣitāḥ</li>
    </ul>
    <ul id="R_2.076.024">
        <li id="R_2.076.024ab">te hayair gorathaiḥ śīghraiḥ syandanaiś ca manojavaiḥ</li>
        <li id="R_2.076.024cd">saha yodhair balādhyakṣā balaṃ sarvam acodayan</li>
    </ul>
    <ul id="R_2.076.025">
        <li id="R_2.076.025ab">sajjaṃ tu tad balaṃ dṛṣṭvā bharato gurusaṃnidhau</li>
        <li id="R_2.076.025cd">rathaṃ me tvarayasveti sumantraṃ pārśvato 'bravīt</li>
    </ul>
    <ul id="R_2.076.026">
        <li id="R_2.076.026ab">bharatasya tu tasyājñāṃ pratigṛhya praharṣitaḥ</li>
        <li id="R_2.076.026cd">rathaṃ gṛhītvā prayayau yuktaṃ paramavājibhiḥ</li>
    </ul>
    <ul id="R_2.076.027">
        <li id="R_2.076.027ab">
            <seg type="pāda" n="a">sa rāghavaḥ satyadhṛtiḥ pratāpavān</seg>
            <seg type="pāda" n="b">bruvan suyuktaṃ dṛḍhasatyavikramaḥ</seg>
        </li>
        <li id="R_2.076.027cd">
            <seg type="pāda" n="c">guruṃ mahāraṇyagataṃ yaśasvinaṃ</seg>
            <seg type="pāda" n="d">prasādayiṣyan bharato 'bravīt tadā</seg>
        </li>
    </ul>
    <ul id="R_2.076.028">
        <li id="R_2.076.028ab">
            <seg type="pāda" n="a">tūrṇaṃ samutthāya sumantra gaccha</seg>
            <seg type="pāda" n="b">balasya yogāya balapradhānān</seg>
        </li>
        <li id="R_2.076.028cd">
            <seg type="pāda" n="c">ānetum icchāmi hi taṃ vanasthaṃ</seg>
            <seg type="pāda" n="d">prasādya rāmaṃ jagato hitāya</seg>
        </li>
    </ul>
    <ul id="R_2.076.029">
        <li id="R_2.076.029ab">
            <seg type="pāda" n="a">sa sūtaputro bharatena samyag</seg>
            <seg type="pāda" n="b">ājñāpitaḥ saṃparipūrṇakāmaḥ</seg>
        </li>
        <li id="R_2.076.029cd">
            <seg type="pāda" n="c">śaśāsa sarvān prakṛtipradhānān</seg>
            <seg type="pāda" n="d">balasya mukhyāṃś ca suhṛjjanaṃ ca</seg>
        </li>
    </ul>
    <ul id="R_2.076.030">
        <li id="R_2.076.030ab">
            <seg type="pāda" n="a">tataḥ samutthāya kule kule te</seg>
            <seg type="pāda" n="b">rājanyavaiśyā vṛṣalāś ca viprāḥ</seg>
        </li>
        <li id="R_2.076.030cd">
            <seg type="pāda" n="c">ayūyujann uṣṭrarathān kharāṃś ca</seg>
            <seg type="pāda" n="d">nāgān hayāṃś caiva kulaprasūtān</seg>
        </li>
    </ul>
    <ul id="R_2.077.001">
        <li id="R_2.077.001ab">tataḥ samutthitaḥ kālyam āsthāya syandanottamam</li>
        <li id="R_2.077.001cd">prayayau bharataḥ śīghraṃ rāmadarśanakāṅkṣayā</li>
    </ul>
    <ul id="R_2.077.002">
        <li id="R_2.077.002ab">agrataḥ prayayus tasya sarve mantripurodhasaḥ</li>
        <li id="R_2.077.002cd">adhiruhya hayair yuktān rathān sūryarathopamān</li>
    </ul>
    <ul id="R_2.077.003">
        <li id="R_2.077.003ab">navanāgasahasrāṇi kalpitāni yathāvidhi</li>
        <li id="R_2.077.003cd">anvayur bharataṃ yāntam ikṣvāku kulanandanam</li>
    </ul>
    <ul id="R_2.077.004">
        <li id="R_2.077.004ab">ṣaṣṭhī rathasahasrāṇi dhanvino vividhāyudhāḥ</li>
        <li id="R_2.077.004cd">anvayur bharataṃ yāntaṃ rājaputraṃ yaśasvinam</li>
    </ul>
    <ul id="R_2.077.005">
        <li id="R_2.077.005ab">śataṃ sahasrāṇy aśvānāṃ samārūḍhāni rāghavam</li>
        <li id="R_2.077.005cd">anvayur bharataṃ yāntaṃ rājaputraṃ yaśasvinam</li>
    </ul>
    <ul id="R_2.077.006">
        <li id="R_2.077.006ab">kaikeyī ca sumitrā ca kausalyā ca yaśasvinī</li>
        <li id="R_2.077.006cd">rāmānayanasaṃhṛṣṭā yayur yānena bhāsvatā</li>
    </ul>
    <ul id="R_2.077.007">
        <li id="R_2.077.007ab">prayātāś cāryasaṃghātā rāmaṃ draṣṭuṃ salakṣmaṇam</li>
        <li id="R_2.077.007cd">tasyaiva ca kathāś citrāḥ kurvāṇā hṛṣṭamānasāḥ</li>
    </ul>
    <ul id="R_2.077.008">
        <li id="R_2.077.008ab">meghaśyāmaṃ mahābāhuṃ sthirasattvaṃ dṛḍhavratam</li>
        <li id="R_2.077.008cd">kadā drakṣyāmahe rāmaṃ jagataḥ śokanāśanam</li>
    </ul>
    <ul id="R_2.077.009">
        <li id="R_2.077.009ab">dṛṣṭa eva hi naḥ śokam apaneṣyati rāghavaḥ</li>
        <li id="R_2.077.009cd">tamaḥ sarvasya lokasya samudyann iva bhāskaraḥ</li>
    </ul>
    <ul id="R_2.077.010">
        <li id="R_2.077.010ab">ity evaṃ kathayantas te saṃprahṛṣṭāḥ kathāḥ śubhāḥ</li>
        <li id="R_2.077.010cd">pariṣvajānāś cānyonyaṃ yayur nāgarikās tadā</li>
    </ul>
    <ul id="R_2.077.011">
        <li id="R_2.077.011ab">ye ca tatrāpare sarve saṃmatā ye ca naigamāḥ</li>
        <li id="R_2.077.011cd">rāmaṃ prati yayur hṛṣṭāḥ sarvāḥ prakṛtayas tadā</li>
    </ul>
    <ul id="R_2.077.012">
        <li id="R_2.077.012ab">maṇi kārāś ca ye ke cit kumbhakārāś ca śobhanāḥ</li>
        <li id="R_2.077.012cd">sūtrakarmakṛtaś caiva ye ca śastropajīvinaḥ</li>
    </ul>
    <ul id="R_2.077.013">
        <li id="R_2.077.013ab">māyūrakāḥ krākacikā rocakā vedhakās tathā</li>
        <li id="R_2.077.013cd">dantakārāḥ sudhākārās tathā gandhopajīvinaḥ</li>
    </ul>
    <ul id="R_2.077.014">
        <li id="R_2.077.014ab">suvarṇakārāḥ prakhyātās tathā kambaladhāvakāḥ</li>
        <li id="R_2.077.014cd">snāpakācchādakā vaidyā dhūpakāḥ śauṇḍikās tathā</li>
    </ul>
    <ul id="R_2.077.015">
        <li id="R_2.077.015ab">rajakās tunnavāyāś ca grāmaghoṣamahattarāḥ</li>
        <li id="R_2.077.015cd">śailūṣāś ca saha strībhir yānti kaivartakās tathā</li>
    </ul>
    <ul id="R_2.077.016">
        <li id="R_2.077.016ab">samāhitā vedavido brāhmaṇā vṛttasaṃmatāḥ</li>
        <li id="R_2.077.016cd">gorathair bharataṃ yāntam anujagmuḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_2.077.017">
        <li id="R_2.077.017ab">suveṣāḥ śuddhavasanās tāmramṛṣṭānulepanāḥ</li>
        <li id="R_2.077.017cd">sarve te vividhair yānaiḥ śanair bharatam anvayuḥ</li>
    </ul>
    <ul id="R_2.077.018">
        <li id="R_2.077.018ab">prahṛṣṭamuditā senā sānvayāt kaikayīsutam</li>
        <li id="R_2.077.018cd">vyavatiṣṭhata sā senā bharatasyānuyāyinī</li>
    </ul>
    <ul id="R_2.077.019">
        <li id="R_2.077.019ab">nirīkṣyānugatāṃ senāṃ tāṃ ca gaṅgāṃ śivodakām</li>
        <li id="R_2.077.019cd">bharataḥ sacivān sarvān abravīd vākyakovidaḥ</li>
    </ul>
    <ul id="R_2.077.020">
        <li id="R_2.077.020ab">niveśayata me sainyam abhiprāyeṇa sarvaśaḥ</li>
        <li id="R_2.077.020cd">viśrāntaḥ pratariṣyāmaḥ śva idānīṃ mahānadīm</li>
    </ul>
    <ul id="R_2.077.021">
        <li id="R_2.077.021ab">dātuṃ ca tāvad icchāmi svar gatasya mahīpateḥ</li>
        <li id="R_2.077.021cd">aurdhvadeha nimittārtham avatīryodakaṃ nadīm</li>
    </ul>
    <ul id="R_2.077.022">
        <li id="R_2.077.022ab">tasyaivaṃ bruvato 'mātyās tathety uktvā samāhitāḥ</li>
        <li id="R_2.077.022cd">nyaveśayaṃs tāṃś chandena svena svena pṛthakpṛthak</li>
    </ul>
    <ul id="R_2.077.023">
        <li id="R_2.077.023ab">
            <seg type="pāda" n="a">niveśya gaṅgām anu tāṃ mahānadīṃ</seg>
            <seg type="pāda" n="b">camūṃ vidhānaiḥ paribarha śobhinīm</seg>
        </li>
        <li id="R_2.077.023cd">
            <seg type="pāda" n="c">uvāsa rāmasya tadā mahātmano</seg>
            <seg type="pāda" n="d">vicintayāno bharato nivartanam</seg>
        </li>
    </ul>
    <ul id="R_2.078.001">
        <li id="R_2.078.001ab">tato niviṣṭāṃ dhvajinīṃ gaṅgām anvāśritāṃ nadīm</li>
        <li id="R_2.078.001cd">niṣādarājo dṛṣṭvaiva jñātīn saṃtvarito 'bravīt</li>
    </ul>
    <ul id="R_2.078.002">
        <li id="R_2.078.002ab">mahatīyam ataḥ senā sāgarābhā pradṛśyate</li>
        <li id="R_2.078.002cd">nāsyāntam avagacchāmi manasāpi vicintayan</li>
    </ul>
    <ul id="R_2.078.003">
        <li id="R_2.078.003ab">sa eṣa hi mahākāyaḥ kovidāradhvajo rathe</li>
        <li id="R_2.078.003cd">bandhayiṣyati vā dāśān atha vāsmān vadhiṣyati</li>
    </ul>
    <ul id="R_2.078.004">
        <li id="R_2.078.004ab">atha dāśarathiṃ rāmaṃ pitrā rājyād vivāsitam</li>
        <li id="R_2.078.004cd">bharataḥ kaikeyīputro hantuṃ samadhigacchati</li>
    </ul>
    <ul id="R_2.078.005">
        <li id="R_2.078.005ab">bhartā caiva sakhā caiva rāmo dāśarathir mama</li>
        <li id="R_2.078.005cd">tasyārthakāmāḥ saṃnaddhā gaṅgānūpe 'tra tiṣṭhata</li>
    </ul>
    <ul id="R_2.078.006">
        <li id="R_2.078.006ab">tiṣṭhantu sarvadāśāś ca gaṅgām anvāśritā nadīm</li>
        <li id="R_2.078.006cd">balayuktā nadīrakṣā māṃsamūlaphalāśanāḥ</li>
    </ul>
    <ul id="R_2.078.007">
        <li id="R_2.078.007ab">nāvāṃ śatānāṃ pañcānāṃ kaivartānāṃ śataṃ śatam</li>
        <li id="R_2.078.007cd">saṃnaddhānāṃ tathā yūnāṃ tiṣṭhantv atyabhyacodayat</li>
    </ul>
    <ul id="R_2.078.008">
        <li id="R_2.078.008ab">yadā tuṣṭas tu bharato rāmasyeha bhaviṣyati</li>
        <li id="R_2.078.008cd">seyaṃ svastimayī senā gaṅgām adya tariṣyati</li>
    </ul>
    <ul id="R_2.078.009">
        <li id="R_2.078.009ab">ity uktvopāyanaṃ gṛhya matsyamāṃsamadhūni ca</li>
        <li id="R_2.078.009cd">abhicakrāma bharataṃ niṣādādhipatir guhaḥ</li>
    </ul>
    <ul id="R_2.078.010">
        <li id="R_2.078.010ab">tam āyāntaṃ tu saṃprekṣya sūtaputraḥ pratāpavān</li>
        <li id="R_2.078.010cd">bharatāyācacakṣe 'tha vinayajño vinītavat</li>
    </ul>
    <ul id="R_2.078.011">
        <li id="R_2.078.011ab">eṣa jñātisahasreṇa sthapatiḥ parivāritaḥ</li>
        <li id="R_2.078.011cd">kuśalo daṇḍakāraṇye vṛddho bhrātuś ca te sakhā</li>
    </ul>
    <ul id="R_2.078.012">
        <li id="R_2.078.012ab">tasmāt paśyatu kākutstha tvāṃ niṣādādhipo guhaḥ</li>
        <li id="R_2.078.012cd">asaṃśayaṃ vijānīte yatra tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_2.078.013">
        <li id="R_2.078.013ab">etat tu vacanaṃ śrutvā sumantrād bharataḥ śubham</li>
        <li id="R_2.078.013cd">uvāca vacanaṃ śīghraṃ guhaḥ paśyatu mām iti</li>
    </ul>
    <ul id="R_2.078.014">
        <li id="R_2.078.014ab">labdhvābhyanujñāṃ saṃhṛṣṭo jñātibhiḥ parivāritaḥ</li>
        <li id="R_2.078.014cd">āgamya bharataṃ prahvo guho vacanam abravīt</li>
    </ul>
    <ul id="R_2.078.015">
        <li id="R_2.078.015ab">niṣkuṭaś caiva deśo 'yaṃ vañcitāś cāpi te vayam</li>
        <li id="R_2.078.015cd">nivedayāmas te sarve svake dāśakule vasa</li>
    </ul>
    <ul id="R_2.078.016">
        <li id="R_2.078.016ab">asti mūlaṃ phalaṃ caiva niṣādaiḥ samupāhṛtam</li>
        <li id="R_2.078.016cd">ārdraṃ ca māṃsaṃ śuṣkaṃ ca vanyaṃ coccāvacaṃ mahat</li>
    </ul>
    <ul id="R_2.078.017">
        <li id="R_2.078.017ab">āśaṃse svāśitā senā vatsyatīmāṃ vibhāvarīm</li>
        <li id="R_2.078.017cd">arcito vividhaiḥ kāmaiḥ śvaḥ sasainyo gamiṣyasi</li>
    </ul>
    <ul id="R_2.079.001">
        <li id="R_2.079.001ab">evam uktas tu bharato niṣādādhipatiṃ guham</li>
        <li id="R_2.079.001cd">pratyuvāca mahāprājño vākyaṃ hetvarthasaṃhitam</li>
    </ul>
    <ul id="R_2.079.002">
        <li id="R_2.079.002ab">ūrjitaḥ khalu te kāmaḥ kṛto mama guroḥ sakhe</li>
        <li id="R_2.079.002cd">yo me tvam īdṛśīṃ senām eko 'bhyarcitum icchasi</li>
    </ul>
    <ul id="R_2.079.003">
        <li id="R_2.079.003ab">ity uktvā tu mahātejā guhaṃ vacanam uttamam</li>
        <li id="R_2.079.003cd">abravīd bharataḥ śrīmān niṣādādhipatiṃ punaḥ</li>
    </ul>
    <ul id="R_2.079.004">
        <li id="R_2.079.004ab">katareṇa gamiṣyāmi bharadvājāśramaṃ guha</li>
        <li id="R_2.079.004cd">gahano 'yaṃ bhṛśaṃ deśo gaṅgānūpo duratyayaḥ</li>
    </ul>
    <ul id="R_2.079.005">
        <li id="R_2.079.005ab">tasya tad vacanaṃ śrutvā rājaputrasya dhīmataḥ</li>
        <li id="R_2.079.005cd">abravīt prāñjalir vākyaṃ guho gahanagocaraḥ</li>
    </ul>
    <ul id="R_2.079.006">
        <li id="R_2.079.006ab">dāśās tv anugamiṣyanti dhanvinaḥ susamāhitāḥ</li>
        <li id="R_2.079.006cd">ahaṃ cānugamiṣyāmi rājaputra mahāyaśaḥ</li>
    </ul>
    <ul id="R_2.079.007">
        <li id="R_2.079.007ab">kaccin na duṣṭo vrajasi rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_2.079.007cd">iyaṃ te mahatī senā śaṅkāṃ janayatīva me</li>
    </ul>
    <ul id="R_2.079.008">
        <li id="R_2.079.008ab">tam evam abhibhāṣantam ākāśa iva nirmalaḥ</li>
        <li id="R_2.079.008cd">bharataḥ ślakṣṇayā vācā guhaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.079.009">
        <li id="R_2.079.009ab">mā bhūt sa kālo yat kaṣṭaṃ na māṃ śaṅkitum arhasi</li>
        <li id="R_2.079.009cd">rāghavaḥ sa hi me bhrātā jyeṣṭhaḥ pitṛsamo mama</li>
    </ul>
    <ul id="R_2.079.010">
        <li id="R_2.079.010ab">taṃ nivartayituṃ yāmi kākutsthaṃ vanavāsinam</li>
        <li id="R_2.079.010cd">buddhir anyā na te kāryā guha satyaṃ bravīmi te</li>
    </ul>
    <ul id="R_2.079.011">
        <li id="R_2.079.011ab">sa tu saṃhṛṣṭavadanaḥ śrutvā bharatabhāṣitam</li>
        <li id="R_2.079.011cd">punar evābravīd vākyaṃ bharataṃ prati harṣitaḥ</li>
    </ul>
    <ul id="R_2.079.012">
        <li id="R_2.079.012ab">dhanyas tvaṃ na tvayā tulyaṃ paśyāmi jagatītale</li>
        <li id="R_2.079.012cd">ayatnād āgataṃ rājyaṃ yas tvaṃ tyaktum ihecchasi</li>
    </ul>
    <ul id="R_2.079.013">
        <li id="R_2.079.013ab">śāśvatī khalu te kīrtir lokān anucariṣyati</li>
        <li id="R_2.079.013cd">yas tvaṃ kṛcchragataṃ rāmaṃ pratyānayitum icchasi</li>
    </ul>
    <ul id="R_2.079.014">
        <li id="R_2.079.014ab">evaṃ saṃbhāṣamāṇasya guhasya bharataṃ tadā</li>
        <li id="R_2.079.014cd">babhau naṣṭaprabhaḥ sūryo rajanī cābhyavartata</li>
    </ul>
    <ul id="R_2.079.015">
        <li id="R_2.079.015ab">saṃniveśya sa tāṃ senāṃ guhena paritoṣitaḥ</li>
        <li id="R_2.079.015cd">śatrughnena saha śrīmāñ śayanaṃ punar āgamat</li>
    </ul>
    <ul id="R_2.079.016">
        <li id="R_2.079.016ab">rāmacintāmayaḥ śoko bharatasya mahātmanaḥ</li>
        <li id="R_2.079.016cd">upasthito hy anarhasya dharmaprekṣasya tādṛśaḥ</li>
    </ul>
    <ul id="R_2.079.017">
        <li id="R_2.079.017ab">antardāhena dahanaḥ saṃtāpayati rāghavam</li>
        <li id="R_2.079.017cd">vanadāhābhisaṃtaptaṃ gūḍho 'gnir iva pādapam</li>
    </ul>
    <ul id="R_2.079.018">
        <li id="R_2.079.018ab">prasrutaḥ sarvagātrebhyaḥ svedaḥ śokāgnisaṃbhavaḥ</li>
        <li id="R_2.079.018cd">yathā sūryāṃśusaṃtapto himavān prasruto himam</li>
    </ul>
    <ul id="R_2.079.019">
        <li id="R_2.079.019ab">dhyānanirdaraśailena viniḥśvasitadhātunā</li>
        <li id="R_2.079.019cd">dainyapādapasaṃghena śokāyāsādhiśṛṅgiṇā</li>
    </ul>
    <ul id="R_2.079.020">
        <li id="R_2.079.020ab">pramohānantasattvena saṃtāpauṣadhiveṇunā</li>
        <li id="R_2.079.020cd">ākrānto duḥkhaśailena mahatā kaikayīsutaḥ</li>
    </ul>
    <ul id="R_2.079.021">
        <li id="R_2.079.021ab">
            <seg type="pāda" n="a">guhena sārdhaṃ bharataḥ samāgato</seg>
            <seg type="pāda" n="b">mahānubhāvaḥ sajanaḥ samāhitaḥ</seg>
        </li>
        <li id="R_2.079.021cd">
            <seg type="pāda" n="c">sudurmanās taṃ bharataṃ tadā punar</seg>
            <seg type="pāda" n="d">guhaḥ samāśvāsayad agrajaṃ prati</seg>
        </li>
    </ul>
    <ul id="R_2.080.001">
        <li id="R_2.080.001ab">ācacakṣe 'tha sadbhāvaṃ lakṣmaṇasya mahātmanaḥ</li>
        <li id="R_2.080.001cd">bharatāyāprameyāya guho gahanagocaraḥ</li>
    </ul>
    <ul id="R_2.080.002">
        <li id="R_2.080.002ab">taṃ jāgrataṃ guṇair yuktaṃ varacāpeṣudhāriṇam</li>
        <li id="R_2.080.002cd">bhrātṛ guptyartham atyantam ahaṃ lakṣmaṇam abravam</li>
    </ul>
    <ul id="R_2.080.003">
        <li id="R_2.080.003ab">iyaṃ tāta sukhā śayyā tvadartham upakalpitā</li>
        <li id="R_2.080.003cd">pratyāśvasihi śeṣvāsyāṃ sukhaṃ rāghavanandana</li>
    </ul>
    <ul id="R_2.080.004">
        <li id="R_2.080.004ab">ucito 'yaṃ janaḥ sarve duḥkhānāṃ tvaṃ sukhocitaḥ</li>
        <li id="R_2.080.004cd">dharmātmaṃs tasya guptyarthaṃ jāgariṣyāmahe vayam</li>
    </ul>
    <ul id="R_2.080.005">
        <li id="R_2.080.005ab">na hi rāmāt priyataro mamāsti bhuvi kaś cana</li>
        <li id="R_2.080.005cd">motsuko bhūr bravīmy etad apy asatyaṃ tavāgrataḥ</li>
    </ul>
    <ul id="R_2.080.006">
        <li id="R_2.080.006ab">asya prasādād āśaṃse loke 'smin sumahad yaśaḥ</li>
        <li id="R_2.080.006cd">dharmāvāptiṃ ca vipulām arthāvāptiṃ ca kevalām</li>
    </ul>
    <ul id="R_2.080.007">
        <li id="R_2.080.007ab">so 'haṃ priyasakhaṃ rāmaṃ śayānaṃ saha sītayā</li>
        <li id="R_2.080.007cd">rakṣiṣyāmi dhanuṣpāṇiḥ sarvaiḥ svair jñātibhiḥ saha</li>
    </ul>
    <ul id="R_2.080.008">
        <li id="R_2.080.008ab">na hi me 'viditaṃ kiṃ cid vane 'smiṃś carataḥ sadā</li>
        <li id="R_2.080.008cd">caturaṅgaṃ hy api balaṃ prasahema vayaṃ yudhi</li>
    </ul>
    <ul id="R_2.080.009">
        <li id="R_2.080.009ab">evam asmābhir uktena lakṣmaṇena mahātmanā</li>
        <li id="R_2.080.009cd">anunītā vayaṃ sarve dharmam evānupaśyatā</li>
    </ul>
    <ul id="R_2.080.010">
        <li id="R_2.080.010ab">kathaṃ dāśarathau bhūmau śayāne saha sītayā</li>
        <li id="R_2.080.010cd">śakyā nidrāmayā labdhuṃ jīvitaṃ vā sukhāni vā</li>
    </ul>
    <ul id="R_2.080.011">
        <li id="R_2.080.011ab">yo na devāsuraiḥ sarvaiḥ śakyaḥ prasahituṃ yudhi</li>
        <li id="R_2.080.011cd">taṃ paśya guha saṃviṣṭaṃ tṛṇeṣu saha sītayā</li>
    </ul>
    <ul id="R_2.080.012">
        <li id="R_2.080.012ab">mahatā tapasā labdho vividhaiś ca pariśramaiḥ</li>
        <li id="R_2.080.012cd">eko daśarathasyaiṣa putraḥ sadṛśalakṣaṇaḥ</li>
    </ul>
    <ul id="R_2.080.013">
        <li id="R_2.080.013ab">asmin pravrājite rājā na ciraṃ vartayiṣyati</li>
        <li id="R_2.080.013cd">vidhavā medinī nūnaṃ kṣipram eva bhaviṣyati</li>
    </ul>
    <ul id="R_2.080.014">
        <li id="R_2.080.014ab">vinadya sumahānādaṃ śrameṇoparatāḥ striyaḥ</li>
        <li id="R_2.080.014cd">nirghoṣoparataṃ nūnam adya rājaniveśanam</li>
    </ul>
    <ul id="R_2.080.015">
        <li id="R_2.080.015ab">kausalyā caiva rājā ca tathaiva jananī mama</li>
        <li id="R_2.080.015cd">nāśaṃse yadi te sarve jīveyuḥ śarvarīm imām</li>
    </ul>
    <ul id="R_2.080.016">
        <li id="R_2.080.016ab">jīved api hi me mātā śatrughnasyānvavekṣayā</li>
        <li id="R_2.080.016cd">duḥkhitā yā tu kausalyā vīrasūr vinaśiṣyati</li>
    </ul>
    <ul id="R_2.080.017">
        <li id="R_2.080.017ab">atikrāntam atikrāntam anavāpya manoratham</li>
        <li id="R_2.080.017cd">rājye rāmam anikṣipya pitā me vinaśiṣyati</li>
    </ul>
    <ul id="R_2.080.018">
        <li id="R_2.080.018ab">siddhārthāḥ pitaraṃ vṛttaṃ tasmin kāle hy upasthite</li>
        <li id="R_2.080.018cd">pretakāryeṣu sarveṣu saṃskariṣyanti bhūmipam</li>
    </ul>
    <ul id="R_2.080.019">
        <li id="R_2.080.019ab">ramyacatvarasaṃsthānāṃ suvibhaktamahāpathām</li>
        <li id="R_2.080.019cd">harmyaprāsādasaṃpannāṃ sarvaratnavibhūṣitām</li>
    </ul>
    <ul id="R_2.080.020">
        <li id="R_2.080.020ab">gajāśvarathasaṃbādhāṃ tūryanādavināditām</li>
        <li id="R_2.080.020cd">sarvakalyāṇasaṃpūrṇāṃ hṛṣṭapuṣṭajanākulām</li>
    </ul>
    <ul id="R_2.080.021">
        <li id="R_2.080.021ab">ārāmodyānasaṃpūrṇāṃ samājotsavaśālinīm</li>
        <li id="R_2.080.021cd">sukhitā vicariṣyanti rājadhānīṃ pitur mama</li>
    </ul>
    <ul id="R_2.080.022">
        <li id="R_2.080.022ab">api satyapratijñena sārdhaṃ kuśalinā vayam</li>
        <li id="R_2.080.022cd">nivṛtte samaye hy asmin sukhitāḥ praviśemahi</li>
    </ul>
    <ul id="R_2.080.023">
        <li id="R_2.080.023ab">paridevayamānasya tasyaivaṃ sumahātmanaḥ</li>
        <li id="R_2.080.023cd">tiṣṭhato rājaputrasya śarvarī sātyavartata</li>
    </ul>
    <ul id="R_2.080.024">
        <li id="R_2.080.024ab">prabhāte vimale sūrye kārayitvā jaṭā ubhau</li>
        <li id="R_2.080.024cd">asmin bhāgīrathītīre sukhaṃ saṃtāritau mayā</li>
    </ul>
    <ul id="R_2.080.025">
        <li id="R_2.080.025ab">
            <seg type="pāda" n="a">jaṭādharau tau drumacīravāsasau</seg>
            <seg type="pāda" n="b">mahābalau kuñjarayūthapopamau</seg>
        </li>
        <li id="R_2.080.025cd">
            <seg type="pāda" n="c">vareṣucāpāsidharau paraṃtapau</seg>
            <seg type="pāda" n="d">vyavekṣamāṇau saha sītayā gatau</seg>
        </li>
    </ul>
    <ul id="R_2.081.001">
        <li id="R_2.081.001ab">guhasya vacanaṃ śrutvā bharato bhṛśam apriyam</li>
        <li id="R_2.081.001cd">dhyānaṃ jagāma tatraiva yatra tac chrutam apriyam</li>
    </ul>
    <ul id="R_2.081.002">
        <li id="R_2.081.002ab">sukumāro mahāsattvaḥ siṃhaskandho mahābhujaḥ</li>
        <li id="R_2.081.002cd">puṇḍarīka viśālākṣas taruṇaḥ priyadarśanaḥ</li>
    </ul>
    <ul id="R_2.081.003">
        <li id="R_2.081.003ab">pratyāśvasya muhūrtaṃ tu kālaṃ paramadurmanāḥ</li>
        <li id="R_2.081.003cd">papāta sahasā totrair hṛdi viddha iva dvipaḥ</li>
    </ul>
    <ul id="R_2.081.004">
        <li id="R_2.081.004ab">tadavasthaṃ tu bharataṃ śatrughno 'nantara sthitaḥ</li>
        <li id="R_2.081.004cd">pariṣvajya rurodoccair visaṃjñaḥ śokakarśitaḥ</li>
    </ul>
    <ul id="R_2.081.005">
        <li id="R_2.081.005ab">tataḥ sarvāḥ samāpetur mātaro bharatasya tāḥ</li>
        <li id="R_2.081.005cd">upavāsa kṛśā dīnā bhartṛvyasanakarśitāḥ</li>
    </ul>
    <ul id="R_2.081.006">
        <li id="R_2.081.006ab">tāś ca taṃ patitaṃ bhūmau rudantyaḥ paryavārayan</li>
        <li id="R_2.081.006cd">kausalyā tv anusṛtyainaṃ durmanāḥ pariṣasvaje</li>
    </ul>
    <ul id="R_2.081.007">
        <li id="R_2.081.007ab">vatsalā svaṃ yathā vatsam upagūhya tapasvinī</li>
        <li id="R_2.081.007cd">paripapraccha bharataṃ rudantī śokalālasā</li>
    </ul>
    <ul id="R_2.081.008">
        <li id="R_2.081.008ab">putravyādhir na te kaccic charīraṃ paribādhate</li>
        <li id="R_2.081.008cd">adya rājakulasyāsya tvadadhīnaṃ hi jīvitam</li>
    </ul>
    <ul id="R_2.081.009">
        <li id="R_2.081.009ab">tvāṃ dṛṣṭvā putra jīvāmi rāme sabhrātṛke gate</li>
        <li id="R_2.081.009cd">vṛtte daśarathe rājñi nātha ekas tvam adya naḥ</li>
    </ul>
    <ul id="R_2.081.010">
        <li id="R_2.081.010ab">kaccin na lakṣmaṇe putra śrutaṃ te kiṃ cid apriyam</li>
        <li id="R_2.081.010cd">putra vā hy ekaputrāyāḥ sahabhārye vanaṃ gate</li>
    </ul>
    <ul id="R_2.081.011">
        <li id="R_2.081.011ab">sa muhūrtaṃ samāśvasya rudann eva mahāyaśāḥ</li>
        <li id="R_2.081.011cd">kausalyāṃ parisāntvyedaṃ guhaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_2.081.012">
        <li id="R_2.081.012ab">bhrātā me kvāvasad rātriṃ kva sītā kva ca lakṣmaṇaḥ</li>
        <li id="R_2.081.012cd">asvapac chayane kasmin kiṃ bhuktvā guha śaṃsa me</li>
    </ul>
    <ul id="R_2.081.013">
        <li id="R_2.081.013ab">so 'bravīd bharataṃ pṛṣṭo niṣādādhipatir guhaḥ</li>
        <li id="R_2.081.013cd">yad vidhaṃ pratipede ca rāme priyahite 'tithau</li>
    </ul>
    <ul id="R_2.081.014">
        <li id="R_2.081.014ab">annam uccāvacaṃ bhakṣyāḥ phalāni vividhāni ca</li>
        <li id="R_2.081.014cd">rāmāyābhyavahārārthaṃ bahucopahṛtaṃ mayā</li>
    </ul>
    <ul id="R_2.081.015">
        <li id="R_2.081.015ab">tat sarvaṃ pratyanujñāsīd rāmaḥ satyaparākramaḥ</li>
        <li id="R_2.081.015cd">na hi tat pratyagṛhṇāt sa kṣatradharmam anusmaran</li>
    </ul>
    <ul id="R_2.081.016">
        <li id="R_2.081.016ab">na hy asmābhiḥ pratigrāhyaṃ sakhe deyaṃ tu sarvadā</li>
        <li id="R_2.081.016cd">iti tena vayaṃ rājann anunītā mahātmanā</li>
    </ul>
    <ul id="R_2.081.017">
        <li id="R_2.081.017ab">lakṣmaṇena samānītaṃ pītvā vāri mahāyaśāḥ</li>
        <li id="R_2.081.017cd">aupavāsyaṃ tadākārṣīd rāghavaḥ saha sītayā</li>
    </ul>
    <ul id="R_2.081.018">
        <li id="R_2.081.018ab">tatas tu jalaśeṣeṇa lakṣmaṇo 'py akarot tadā</li>
        <li id="R_2.081.018cd">vāg yatās te trayaḥ saṃdhyām upāsata samāhitāḥ</li>
    </ul>
    <ul id="R_2.081.019">
        <li id="R_2.081.019ab">saumitris tu tataḥ paścād akarot svāstaraṃ śubham</li>
        <li id="R_2.081.019cd">svayam ānīya barhīṃṣi kṣipraṃ rāghava kāraṇāt</li>
    </ul>
    <ul id="R_2.081.020">
        <li id="R_2.081.020ab">tasmin samāviśad rāmaḥ svāstare saha sītayā</li>
        <li id="R_2.081.020cd">prakṣālya ca tayoḥ pādāv apacakrāma lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_2.081.021">
        <li id="R_2.081.021ab">etat tad iṅgudīmūlam idam eva ca tat tṛṇam</li>
        <li id="R_2.081.021cd">yasmin rāmaś ca sītā ca rātriṃ tāṃ śayitāv ubhau</li>
    </ul>
    <ul id="R_2.081.022">
        <li id="R_2.081.022ab">
            <seg type="pāda" n="a">niyamya pṛṣṭhe tu talāṅgulitravāñ</seg>
            <seg type="pāda" n="b">śaraiḥ supūrṇāv iṣudhī paraṃtapaḥ</seg>
        </li>
        <li id="R_2.081.022cd">
            <seg type="pāda" n="c">mahad dhanuḥ sajyam upohya lakṣmaṇo</seg>
            <seg type="pāda" n="d">niśām atiṣṭhat parito 'sya kevalam</seg>
        </li>
    </ul>
    <ul id="R_2.081.023">
        <li id="R_2.081.023ab">
            <seg type="pāda" n="a">tatas tv ahaṃ cottamabāṇacāpadhṛk</seg>
            <seg type="pāda" n="b">sthito 'bhavaṃ tatra sa yatra lakṣmaṇaḥ</seg>
        </li>
        <li id="R_2.081.023cd">
            <seg type="pāda" n="c">atandribhir jñātibhir āttakārmukair</seg>
            <seg type="pāda" n="d">mahendrakalpaṃ paripālayaṃs tadā</seg>
        </li>
    </ul>
    <ul id="R_2.082.001">
        <li id="R_2.082.001ab">tac chrutvā nipuṇaṃ sarvaṃ bharataḥ saha mantribhiḥ</li>
        <li id="R_2.082.001cd">iṅgudīmūlam āgamya rāmaśayyām avekṣya tām</li>
    </ul>
    <ul id="R_2.082.002">
        <li id="R_2.082.002ab">abravīj jananīḥ sarvā iha tena mahātmanā</li>
        <li id="R_2.082.002cd">śarvarī śayitā bhūmāv idam asya vimarditam</li>
    </ul>
    <ul id="R_2.082.003">
        <li id="R_2.082.003ab">mahābhāgakulīnena mahābhāgena dhīmatā</li>
        <li id="R_2.082.003cd">jāto daśarathenorvyāṃ na rāmaḥ svaptum arhati</li>
    </ul>
    <ul id="R_2.082.004">
        <li id="R_2.082.004ab">ajinottarasaṃstīrṇe varāstaraṇasaṃcaye</li>
        <li id="R_2.082.004cd">śayitvā puruṣavyāghraḥ kathaṃ śete mahītale</li>
    </ul>
    <ul id="R_2.082.005">
        <li id="R_2.082.005ab">prāsādāgra vimāneṣu valabhīṣu ca sarvadā</li>
        <li id="R_2.082.005cd">haimarājatabhaumeṣu varāstaraṇaśāliṣu</li>
    </ul>
    <ul id="R_2.082.006">
        <li id="R_2.082.006ab">puṣpasaṃcayacitreṣu candanāgarugandhiṣu</li>
        <li id="R_2.082.006cd">pāṇḍurābhraprakāśeṣu śukasaṃgharuteṣu ca</li>
    </ul>
    <ul id="R_2.082.007">
        <li id="R_2.082.007ab">gītavāditranirghoṣair varābharaṇaniḥsvanaiḥ</li>
        <li id="R_2.082.007cd">mṛdaṅgavaraśabdaiś ca satataṃ pratibodhitaḥ</li>
    </ul>
    <ul id="R_2.082.008">
        <li id="R_2.082.008ab">bandibhir vanditaḥ kāle bahubhiḥ sūtamāgadhaiḥ</li>
        <li id="R_2.082.008cd">gāthābhir anurūpābhiḥ stutibhiś ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_2.082.009">
        <li id="R_2.082.009ab">aśraddheyam idaṃ loke na satyaṃ pratibhāti mā</li>
        <li id="R_2.082.009cd">muhyate khalu me bhāvaḥ svapno 'yam iti me matiḥ</li>
    </ul>
    <ul id="R_2.082.010">
        <li id="R_2.082.010ab">na nūnaṃ daivataṃ kiṃ cit kālena balavattaram</li>
        <li id="R_2.082.010cd">yatra dāśarathī rāmo bhūmāv evaṃ śayīta saḥ</li>
    </ul>
    <ul id="R_2.082.011">
        <li id="R_2.082.011ab">videharājasya sutā sītā ca priyadarśanā</li>
        <li id="R_2.082.011cd">dayitā śayitā bhūmau snuṣā daśarathasya ca</li>
    </ul>
    <ul id="R_2.082.012">
        <li id="R_2.082.012ab">iyaṃ śayyā mama bhrātur idaṃ hi parivartitam</li>
        <li id="R_2.082.012cd">sthaṇḍile kaṭhine sarvaṃ gātrair vimṛditaṃ tṛṇam</li>
    </ul>
    <ul id="R_2.082.013">
        <li id="R_2.082.013ab">manye sābharaṇā suptā sītāsmiñ śayane tadā</li>
        <li id="R_2.082.013cd">tatra tatra hi dṛśyante saktāḥ kanakabindavaḥ</li>
    </ul>
    <ul id="R_2.082.014">
        <li id="R_2.082.014ab">uttarīyam ihāsaktaṃ suvyaktaṃ sītayā tadā</li>
        <li id="R_2.082.014cd">tathā hy ete prakāśante saktāḥ kauśeyatantavaḥ</li>
    </ul>
    <ul id="R_2.082.015">
        <li id="R_2.082.015ab">manye bhartuḥ sukhā śayyā yena bālā tapasvinī</li>
        <li id="R_2.082.015cd">sukumārī satī duḥkhaṃ na vijānāti maithilī</li>
    </ul>
    <ul id="R_2.082.016">
        <li id="R_2.082.016ab">sārvabhauma kule jātaḥ sarvalokasukhāvahaḥ</li>
        <li id="R_2.082.016cd">sarvalokapriyas tyaktvā rājyaṃ priyam anuttamam</li>
    </ul>
    <ul id="R_2.082.017">
        <li id="R_2.082.017ab">katham indīvaraśyāmo raktākṣaḥ priyadarśanaḥ</li>
        <li id="R_2.082.017cd">sukhabhāgī ca duḥkhārhaḥ śayito bhuvi rāghavaḥ</li>
    </ul>
    <ul id="R_2.082.018">
        <li id="R_2.082.018ab">siddhārthā khalu vaidehī patiṃ yānugatā vanam</li>
        <li id="R_2.082.018cd">vayaṃ saṃśayitāḥ sarve hīnās tena mahātmanā</li>
    </ul>
    <ul id="R_2.082.019">
        <li id="R_2.082.019ab">akarṇadhārā pṛthivī śūnyeva pratibhāti mā</li>
        <li id="R_2.082.019cd">gate daśarathe svarge rāme cāraṇyam āśrite</li>
    </ul>
    <ul id="R_2.082.020">
        <li id="R_2.082.020ab">na ca prārthayate kaś cin manasāpi vasuṃdharām</li>
        <li id="R_2.082.020cd">vane 'pi vasatas tasya bāhuvīryābhirakṣitām</li>
    </ul>
    <ul id="R_2.082.021">
        <li id="R_2.082.021ab">śūnyasaṃvaraṇārakṣām ayantritahayadvipām</li>
        <li id="R_2.082.021cd">apāvṛtapuradvārāṃ rājadhānīm arakṣitām</li>
    </ul>
    <ul id="R_2.082.022">
        <li id="R_2.082.022ab">aprahṛṣṭabalāṃ nyūnāṃ viṣamasthām anāvṛtām</li>
        <li id="R_2.082.022cd">śatravo nābhimanyante bhakṣyān viṣakṛtān iva</li>
    </ul>
    <ul id="R_2.082.023">
        <li id="R_2.082.023ab">adya prabhṛti bhūmau tu śayiṣye 'haṃ tṛṇeṣu vā</li>
        <li id="R_2.082.023cd">phalamūlāśano nityaṃ jaṭācīrāṇi dhārayan</li>
    </ul>
    <ul id="R_2.082.024">
        <li id="R_2.082.024ab">tasyārtham uttaraṃ kālaṃ nivatsyāmi sukhaṃ vane</li>
        <li id="R_2.082.024cd">taṃ pratiśravam āmucya nāsya mithyā bhaviṣyati</li>
    </ul>
    <ul id="R_2.082.025">
        <li id="R_2.082.025ab">vasantaṃ bhrātur arthāya śatrughno mānuvatsyati</li>
        <li id="R_2.082.025cd">lakṣmaṇena saha tv āryo ayodhyāṃ pālayiṣyati</li>
    </ul>
    <ul id="R_2.082.026">
        <li id="R_2.082.026ab">abhiṣekṣyanti kākutstham ayodhyāyāṃ dvijātayaḥ</li>
        <li id="R_2.082.026cd">api me devatāḥ kuryur imaṃ satyaṃ manoratham</li>
    </ul>
    <ul id="R_2.082.027">
        <li id="R_2.082.027ab">
            <seg type="pāda" n="a">prasādyamānaḥ śirasā mayā svayaṃ</seg>
            <seg type="pāda" n="b">bahuprakāraṃ yadi na prapatsyate</seg>
        </li>
        <li id="R_2.082.027cd">
            <seg type="pāda" n="c">tato 'nuvatsyāmi cirāya rāghavaṃ</seg>
            <seg type="pāda" n="d">vane vasan nārhati mām upekṣitum</seg>
        </li>
    </ul>
    <ul id="R_2.083.001">
        <li id="R_2.083.001ab">vyuṣya rātriṃ tu tatraiva gaṅgākūle sa rāghavaḥ</li>
        <li id="R_2.083.001cd">bharataḥ kālyam utthāya śatrughnam idam abravīt</li>
    </ul>
    <ul id="R_2.083.002">
        <li id="R_2.083.002ab">śatrughottiṣṭha kiṃ śeṣe niṣādādhipatiṃ guham</li>
        <li id="R_2.083.002cd">śīghram ānaya bhadraṃ te tārayiṣyati vāhinīm</li>
    </ul>
    <ul id="R_2.083.003">
        <li id="R_2.083.003ab">jāgarmi nāhaṃ svapimi tathaivāryaṃ vicintayan</li>
        <li id="R_2.083.003cd">ity evam abravīd bhrātrā śatrughno 'pi pracoditaḥ</li>
    </ul>
    <ul id="R_2.083.004">
        <li id="R_2.083.004ab">iti saṃvadator evam anyonyaṃ narasiṃhayoḥ</li>
        <li id="R_2.083.004cd">āgamya prāñjaliḥ kāle guho bharatam abravīt</li>
    </ul>
    <ul id="R_2.083.005">
        <li id="R_2.083.005ab">kaccit sukhaṃ nadītīre 'vātsīḥ kākutstha śarvarīm</li>
        <li id="R_2.083.005cd">kaccic ca saha sainyasya tava sarvam anāmayam</li>
    </ul>
    <ul id="R_2.083.006">
        <li id="R_2.083.006ab">guhasya tat tu vacanaṃ śrutvā snehād udīritam</li>
        <li id="R_2.083.006cd">rāmasyānuvaśo vākyaṃ bharato 'pīdam abravīt</li>
    </ul>
    <ul id="R_2.083.007">
        <li id="R_2.083.007ab">sukhā naḥ śarvarī rājan pūjitāś cāpi te vayam</li>
        <li id="R_2.083.007cd">gaṅgāṃ tu naubhir bahvībhir dāśāḥ saṃtārayantu naḥ</li>
    </ul>
    <ul id="R_2.083.008">
        <li id="R_2.083.008ab">tato guhaḥ saṃtvaritaḥ śrutvā bharataśāsanam</li>
        <li id="R_2.083.008cd">pratipraviśya nagaraṃ taṃ jñātijanam abravīt</li>
    </ul>
    <ul id="R_2.083.009">
        <li id="R_2.083.009ab">uttiṣṭhata prabudhyadhvaṃ bhadram astu hi vaḥ sadā</li>
        <li id="R_2.083.009cd">nāvaḥ samanukarṣadhvaṃ tārayiṣyāma vāhinīm</li>
    </ul>
    <ul id="R_2.083.010">
        <li id="R_2.083.010ab">te tathoktāḥ samutthāya tvaritā rājaśāsanāt</li>
        <li id="R_2.083.010cd">pañca nāvāṃ śatāny eva samāninyuḥ samantataḥ</li>
    </ul>
    <ul id="R_2.083.011">
        <li id="R_2.083.011ab">anyāḥ svastikavijñeyā mahāghaṇḍā dharā varāḥ</li>
        <li id="R_2.083.011cd">śobhamānāḥ patākinyo yuktavātāḥ susaṃhatāḥ</li>
    </ul>
    <ul id="R_2.083.012">
        <li id="R_2.083.012ab">tataḥ svastikavijñeyāṃ pāṇḍukambalasaṃvṛtām</li>
        <li id="R_2.083.012cd">sanandighoṣāṃ kalyāṇīṃ guho nāvam upāharat</li>
    </ul>
    <ul id="R_2.083.013">
        <li id="R_2.083.013ab">tām āruroha bharataḥ śatrughnaś ca mahābalaḥ</li>
        <li id="R_2.083.013cd">kausalyā ca sumitrā ca yāś cānyā rājayoṣitaḥ</li>
    </ul>
    <ul id="R_2.083.014">
        <li id="R_2.083.014ab">purohitaś ca tat pūrvaṃ gurave brāhmaṇāś ca ye</li>
        <li id="R_2.083.014cd">anantaraṃ rājadārās tathaiva śakaṭāpaṇāḥ</li>
    </ul>
    <ul id="R_2.083.015">
        <li id="R_2.083.015ab">āvāsam ādīpayatāṃ tīrthaṃ cāpy avagāhatām</li>
        <li id="R_2.083.015cd">bhāṇḍāni cādadānānāṃ ghoṣas tridivam aspṛśat</li>
    </ul>
    <ul id="R_2.083.016">
        <li id="R_2.083.016ab">patākinyas tu tā nāvaḥ svayaṃ dāśair adhiṣṭhitāḥ</li>
        <li id="R_2.083.016cd">vahantyo janam ārūḍhaṃ tadā saṃpetur āśugāḥ</li>
    </ul>
    <ul id="R_2.083.017">
        <li id="R_2.083.017ab">nārīṇām abhipūrṇās tu kāś cit kāś cit tu vājinām</li>
        <li id="R_2.083.017cd">kaś cit tatra vahanti sma yānayugyaṃ mahādhanam</li>
    </ul>
    <ul id="R_2.083.018">
        <li id="R_2.083.018ab">tāḥ sma gatvā paraṃ tīram avaropya ca taṃ janam</li>
        <li id="R_2.083.018cd">nivṛttāḥ kāṇḍacitrāṇi kriyante dāśabandhubhiḥ</li>
    </ul>
    <ul id="R_2.083.019">
        <li id="R_2.083.019ab">savaijayantās tu gajā gajārohaiḥ pracoditāḥ</li>
        <li id="R_2.083.019cd">tarantaḥ sma prakāśante sadhvajā iva parvatāḥ</li>
    </ul>
    <ul id="R_2.083.020">
        <li id="R_2.083.020ab">nāvaś cāruruhus tv anye plavais terus tathāpare</li>
        <li id="R_2.083.020cd">anye kumbhaghaṭais terur anye teruś ca bāhubhiḥ</li>
    </ul>
    <ul id="R_2.083.021">
        <li id="R_2.083.021ab">sā puṇyā dhvajinī gaṅgāṃ dāśaiḥ saṃtāritā svayam</li>
        <li id="R_2.083.021cd">maitre muhūrte prayayau prayāgavanam uttamam</li>
    </ul>
    <ul id="R_2.083.022">
        <li id="R_2.083.022ab">
            <seg type="pāda" n="a">āśvāsayitvā ca camūṃ mahātmā</seg>
            <seg type="pāda" n="b">niveśayitvā ca yathopajoṣam</seg>
        </li>
        <li id="R_2.083.022cd">
            <seg type="pāda" n="c">draṣṭuṃ bharadvājam ṛṣipravaryam</seg>
            <seg type="pāda" n="d">ṛtvig vṛtaḥ san bharataḥ pratasthe</seg>
        </li>
    </ul>
    <ul id="R_2.084.001">
        <li id="R_2.084.001ab">bharadvājāśramaṃ dṛṣṭvā krośād eva nararṣabhaḥ</li>
        <li id="R_2.084.001cd">balaṃ sarvam avasthāpya jagāma saha mantribhiḥ</li>
    </ul>
    <ul id="R_2.084.002">
        <li id="R_2.084.002ab">padbhyām eva hi dharmajño nyastaśastraparicchadaḥ</li>
        <li id="R_2.084.002cd">vasāno vāsasī kṣaume purodhāya purohitam</li>
    </ul>
    <ul id="R_2.084.003">
        <li id="R_2.084.003ab">tataḥ saṃdarśane tasya bharadvājasya rāghavaḥ</li>
        <li id="R_2.084.003cd">mantriṇas tān avasthāpya jagāmānu purohitam</li>
    </ul>
    <ul id="R_2.084.004">
        <li id="R_2.084.004ab">vasiṣṭham atha dṛṣṭvaiva bharadvājo mahātapāḥ</li>
        <li id="R_2.084.004cd">saṃcacālāsanāt tūrṇaṃ śiṣyān arghyam iti bruvan</li>
    </ul>
    <ul id="R_2.084.005">
        <li id="R_2.084.005ab">samāgamya vasiṣṭhena bharatenābhivāditaḥ</li>
        <li id="R_2.084.005cd">abudhyata mahātejāḥ sutaṃ daśarathasya tam</li>
    </ul>
    <ul id="R_2.084.006">
        <li id="R_2.084.006ab">tābhyām arghyaṃ ca pādyaṃ ca dattvā paścāt phalāni ca</li>
        <li id="R_2.084.006cd">ānupūrvyāc ca dharmajñaḥ papraccha kuśalaṃ kule</li>
    </ul>
    <ul id="R_2.084.007">
        <li id="R_2.084.007ab">ayodhyāyāṃ bale kośe mitreṣv api ca mantriṣu</li>
        <li id="R_2.084.007cd">jānan daśarathaṃ vṛttaṃ na rājānam udāharat</li>
    </ul>
    <ul id="R_2.084.008">
        <li id="R_2.084.008ab">vasiṣṭho bharataś cainaṃ papracchatur anāmayam</li>
        <li id="R_2.084.008cd">śarīre 'gniṣu vṛkṣeṣu śiṣyeṣu mṛgapakṣiṣu</li>
    </ul>
    <ul id="R_2.084.009">
        <li id="R_2.084.009ab">tatheti ca pratijñāya bharadvājo mahātapāḥ</li>
        <li id="R_2.084.009cd">bharataṃ pratyuvācedaṃ rāghavasnehabandhanāt</li>
    </ul>
    <ul id="R_2.084.010">
        <li id="R_2.084.010ab">kim ihāgamane kāryaṃ tava rājyaṃ praśāsataḥ</li>
        <li id="R_2.084.010cd">etad ācakṣva me sarvaṃ na hi me śudhyate manaḥ</li>
    </ul>
    <ul id="R_2.084.011">
        <li id="R_2.084.011ab">suṣuve yama mitraghnaṃ kausalyānandavardhanam</li>
        <li id="R_2.084.011cd">bhrātrā saha sabhāryo yaś ciraṃ pravrājito vanam</li>
    </ul>
    <ul id="R_2.084.012">
        <li id="R_2.084.012ab">niyuktaḥ strīniyuktena pitrā yo 'sau mahāyaśāḥ</li>
        <li id="R_2.084.012cd">vanavāsī bhavetīha samāḥ kila caturdaśa</li>
    </ul>
    <ul id="R_2.084.013">
        <li id="R_2.084.013ab">kaccin na tasyāpāpasya pāpaṃ kartum ihecchasi</li>
        <li id="R_2.084.013cd">akaṇṭakaṃ bhoktumanā rājyaṃ tasyānujasya ca</li>
    </ul>
    <ul id="R_2.084.014">
        <li id="R_2.084.014ab">evam ukto bharadvājaṃ bharataḥ pratyuvāca ha</li>
        <li id="R_2.084.014cd">paryaśru nayano duḥkhād vācā saṃsajjamānayā</li>
    </ul>
    <ul id="R_2.084.015">
        <li id="R_2.084.015ab">hato 'smi yadi mām evaṃ bhagavān api manyate</li>
        <li id="R_2.084.015cd">matto na doṣam āśaṅker naivaṃ mām anuśādhi hi</li>
    </ul>
    <ul id="R_2.084.016">
        <li id="R_2.084.016ab">na caitad iṣṭaṃ mātā me yad avocan madantare</li>
        <li id="R_2.084.016cd">nāham etena tuṣṭaś ca na tad vacanam ādade</li>
    </ul>
    <ul id="R_2.084.017">
        <li id="R_2.084.017ab">ahaṃ tu taṃ naravyāghram upayātaḥ prasādakaḥ</li>
        <li id="R_2.084.017cd">pratinetum ayodhyāṃ ca pādau tasyābhivanditum</li>
    </ul>
    <ul id="R_2.084.018">
        <li id="R_2.084.018ab">tvaṃ mām evaṃgataṃ matvā prasādaṃ kartum arhasi</li>
        <li id="R_2.084.018cd">śaṃsa me bhagavan rāmaḥ kva saṃprati mahīpatiḥ</li>
    </ul>
    <ul id="R_2.084.019">
        <li id="R_2.084.019ab">uvāca taṃ bharadvājaḥ prasādād bharataṃ vacaḥ</li>
        <li id="R_2.084.019cd">tvayy etat puruṣavyāghra yuktaṃ rāghavavaṃśaje</li>
        <li id="R_2.084.019ef">guruvṛttir damaś caiva sādhūnāṃ cānuyāyitā</li>
    </ul>
    <ul id="R_2.084.020">
        <li id="R_2.084.020ab">jāne caitan manaḥsthaṃ te dṛḍhīkaraṇam astv iti</li>
        <li id="R_2.084.020cd">apṛcchaṃ tvāṃ tavātyarthaṃ kīrtiṃ samabhivardhayan</li>
    </ul>
    <ul id="R_2.084.021">
        <li id="R_2.084.021ab">asau vasati te bhrātā citrakūṭe mahāgirau</li>
        <li id="R_2.084.021cd">śvas tu gantāsi taṃ deśaṃ vasādya saha mantribhiḥ</li>
        <li id="R_2.084.021ef">etaṃ me kuru suprājña kāmaṃ kāmārthakovida</li>
    </ul>
    <ul id="R_2.084.022">
        <li id="R_2.084.022ab">
            <seg type="pāda" n="a">tatas tathety evam udāradarśanaḥ</seg>
            <seg type="pāda" n="b">pratītarūpo bharato 'bravīd vacaḥ</seg>
        </li>
        <li id="R_2.084.022cd">
            <seg type="pāda" n="c">cakāra buddhiṃ ca tadā mahāśrame</seg>
            <seg type="pāda" n="d">niśānivāsāya narādhipātmajaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.085.001">
        <li id="R_2.085.001ab">kṛtabuddhiṃ nivāsāya tathaiva sa munis tadā</li>
        <li id="R_2.085.001cd">bharataṃ kaikayī putram ātithyena nyamantrayat</li>
    </ul>
    <ul id="R_2.085.002">
        <li id="R_2.085.002ab">abravīd bharatas tv enaṃ nanv idaṃ bhavatā kṛtam</li>
        <li id="R_2.085.002cd">pādyam arghyaṃ tathātithyaṃ vane yad ūpapadyate</li>
    </ul>
    <ul id="R_2.085.003">
        <li id="R_2.085.003ab">athovāca bharadvājo bharataṃ prahasann iva</li>
        <li id="R_2.085.003cd">jāne tvāṃ prīti saṃyuktaṃ tuṣyes tvaṃ yena kena cit</li>
    </ul>
    <ul id="R_2.085.004">
        <li id="R_2.085.004ab">senāyās tu tavaitasyāḥ kartum icchāmi bhojanam</li>
        <li id="R_2.085.004cd">mama pritir yathā rūpā tvam arho manujarṣabha</li>
    </ul>
    <ul id="R_2.085.005">
        <li id="R_2.085.005ab">kimarthaṃ cāpi nikṣipya dūre balam ihāgataḥ</li>
        <li id="R_2.085.005cd">kasmān nehopayāto 'si sabalaḥ puruṣarṣabha</li>
    </ul>
    <ul id="R_2.085.006">
        <li id="R_2.085.006ab">bharataḥ pratyuvācedaṃ prāñjalis taṃ tapodhanam</li>
        <li id="R_2.085.006cd">sasainyo nopayāto 'smi bhagavan bhagavad bhayāt</li>
    </ul>
    <ul id="R_2.085.007">
        <li id="R_2.085.007ab">vāji mukhyā manuṣyāś ca mattāś ca vara vāraṇāḥ</li>
        <li id="R_2.085.007cd">pracchādya mahatīṃ bhūmiṃ bhagavann anuyānti mām</li>
    </ul>
    <ul id="R_2.085.008">
        <li id="R_2.085.008ab">te vṛkṣān udakaṃ bhūmim āśrameṣūṭajāṃs tathā</li>
        <li id="R_2.085.008cd">na hiṃsyur iti tenāham eka evāgatas tataḥ</li>
    </ul>
    <ul id="R_2.085.009">
        <li id="R_2.085.009ab">ānīyatām itaḥ senety ājñaptaḥ paramarṣiṇā</li>
        <li id="R_2.085.009cd">tathā tu cakre bharataḥ senāyāḥ samupāgamam</li>
    </ul>
    <ul id="R_2.085.010">
        <li id="R_2.085.010ab">agniśālāṃ praviśyātha pītvāpaḥ parimṛjya ca</li>
        <li id="R_2.085.010cd">ātithyasya kriyāhetor viśvakarmāṇam āhvayat</li>
    </ul>
    <ul id="R_2.085.011">
        <li id="R_2.085.011ab">āhvaye viśvakarmāṇam ahaṃ tvaṣṭāram eva ca</li>
        <li id="R_2.085.011cd">ātithyaṃ kartum icchāmi tatra me saṃvidhīyatām</li>
    </ul>
    <ul id="R_2.085.012">
        <li id="R_2.085.012ab">prāk srotasaś ca yā nadyaḥ pratyak srotasa eva ca</li>
        <li id="R_2.085.012cd">pṛthivyām antarikṣe ca samāyāntv adya sarvaśaḥ</li>
    </ul>
    <ul id="R_2.085.013">
        <li id="R_2.085.013ab">anyāḥ sravantu maireyaṃ surām anyāḥ suniṣṭhitām</li>
        <li id="R_2.085.013cd">aparāś codakaṃ śītam ikṣukāṇḍarasopamam</li>
    </ul>
    <ul id="R_2.085.014">
        <li id="R_2.085.014ab">āhvaye devagandharvān viśvāvasuhahāhuhūn</li>
        <li id="R_2.085.014cd">tathaivāpsaraso devīr gandharvīś cāpi sarvaśaḥ</li>
    </ul>
    <ul id="R_2.085.015">
        <li id="R_2.085.015ab">ghṛtācīm atha viśvācīṃ miśrakeśīm alambusām</li>
        <li id="R_2.085.015cd">śakraṃ yāś copatiṣṭhanti brahmāṇaṃ yāś ca bhāminīḥ</li>
        <li id="R_2.085.015ef">sarvās tumburuṇā sārdham āhvaye saparicchadāḥ</li>
    </ul>
    <ul id="R_2.085.016">
        <li id="R_2.085.016ab">vanaṃ kuruṣu yad divyaṃ vāso bhūṣaṇapatravat</li>
        <li id="R_2.085.016cd">divyanārīphalaṃ śaśvat tat kauberam ihaiva tu</li>
    </ul>
    <ul id="R_2.085.017">
        <li id="R_2.085.017ab">iha me bhagavān somo vidhattām annam uttamam</li>
        <li id="R_2.085.017cd">bhakṣyaṃ bhojyaṃ ca coṣyaṃ ca lehyaṃ ca vividhaṃ bahu</li>
    </ul>
    <ul id="R_2.085.018">
        <li id="R_2.085.018ab">vicitrāṇi ca mālyāni pādapapracyutāni ca</li>
        <li id="R_2.085.018cd">surādīni ca peyāni māṃsāni vividhāni ca</li>
    </ul>
    <ul id="R_2.085.019">
        <li id="R_2.085.019ab">evaṃ samādhinā yuktas tejasāpratimena ca</li>
        <li id="R_2.085.019cd">śikṣāsvarasamāyuktaṃ tapasā cābravīn muniḥ</li>
    </ul>
    <ul id="R_2.085.020">
        <li id="R_2.085.020ab">manasā dhyāyatas tasya prāṅmukhasya kṛtāñjaleḥ</li>
        <li id="R_2.085.020cd">ājagmus tāni sarvāṇi daivatāni pṛthakpṛthak</li>
    </ul>
    <ul id="R_2.085.021">
        <li id="R_2.085.021ab">malayaṃ durduraṃ caiva tataḥ svedanudo 'nilaḥ</li>
        <li id="R_2.085.021cd">upaspṛśya vavau yuktyā supriyātmā sukhaḥ śivaḥ</li>
    </ul>
    <ul id="R_2.085.022">
        <li id="R_2.085.022ab">tato 'bhyavartanta ghanā divyāḥ kusumavṛṣṭayaḥ</li>
        <li id="R_2.085.022cd">devadundubhighoṣaś ca dikṣu sarvāsu śuśruve</li>
    </ul>
    <ul id="R_2.085.023">
        <li id="R_2.085.023ab">pravavuś cottamā vātā nanṛtuś cāpsarogaṇāḥ</li>
        <li id="R_2.085.023cd">prajagur devagandharvā vīṇā pramumucuḥ svarān</li>
    </ul>
    <ul id="R_2.085.024">
        <li id="R_2.085.024ab">sa śabdo dyāṃ ca bhūmiṃ ca prāṇināṃ śravaṇāni ca</li>
        <li id="R_2.085.024cd">viveśoccāritaḥ ślakṣṇaḥ samo layaguṇānvitaḥ</li>
    </ul>
    <ul id="R_2.085.025">
        <li id="R_2.085.025ab">tasminn uparate śabde divye śrotrasukhe nṛṇām</li>
        <li id="R_2.085.025cd">dadarśa bhārataṃ sainyaṃ vidhānaṃ viśvakarmaṇaḥ</li>
    </ul>
    <ul id="R_2.085.026">
        <li id="R_2.085.026ab">babhūva hi samā bhūmiḥ samantāt pañcayojanam</li>
        <li id="R_2.085.026cd">śādvalair bahubhiś channā nīlavaidūryasaṃnibhaiḥ</li>
    </ul>
    <ul id="R_2.085.027">
        <li id="R_2.085.027ab">tasmin bilvāḥ kapitthāś ca panasā bījapūrakāḥ</li>
        <li id="R_2.085.027cd">āmalakyo babhūvuś ca cūtāś ca phalabhūṣaṇāḥ</li>
    </ul>
    <ul id="R_2.085.028">
        <li id="R_2.085.028ab">uttarebhyaḥ kurubhyaś ca vanaṃ divyopabhogavat</li>
        <li id="R_2.085.028cd">ājagāma nadī divyā tīrajair bahubhir vṛtā</li>
    </ul>
    <ul id="R_2.085.029">
        <li id="R_2.085.029ab">catuḥśālāni śubhrāṇi śālāś ca gajavājinām</li>
        <li id="R_2.085.029cd">harmyaprāsādasaṃghātās toraṇāni śubhāni ca</li>
    </ul>
    <ul id="R_2.085.030">
        <li id="R_2.085.030ab">sitameghanibhaṃ cāpi rājaveśma sutoraṇam</li>
        <li id="R_2.085.030cd">śuklamālyakṛtākāraṃ divyagandhasamukṣitam</li>
    </ul>
    <ul id="R_2.085.031">
        <li id="R_2.085.031ab">caturasram asaṃbādhaṃ śayanāsanayānavat</li>
        <li id="R_2.085.031cd">divyaiḥ sarvarasair yuktaṃ divyabhojanavastravat</li>
    </ul>
    <ul id="R_2.085.032">
        <li id="R_2.085.032ab">upakalpita sarvānnaṃ dhautanirmalabhājanam</li>
        <li id="R_2.085.032cd">kḷptasarvāsanaṃ śrīmat svāstīrṇaśayanottamam</li>
    </ul>
    <ul id="R_2.085.033">
        <li id="R_2.085.033ab">praviveśa mahābāhur anujñāto maharṣiṇā</li>
        <li id="R_2.085.033cd">veśma tad ratnasaṃpūrṇaṃ bharataḥ kaikayīsutaḥ</li>
    </ul>
    <ul id="R_2.085.034">
        <li id="R_2.085.034ab">anujagmuś ca taṃ sarve mantriṇaḥ sapurohitāḥ</li>
        <li id="R_2.085.034cd">babhūvuś ca mudā yuktā taṃ dṛṣṭvā veśma saṃvidhim</li>
    </ul>
    <ul id="R_2.085.035">
        <li id="R_2.085.035ab">tatra rājāsanaṃ divyaṃ vyajanaṃ chatram eva ca</li>
        <li id="R_2.085.035cd">bharato mantribhiḥ sārdham abhyavartata rājavat</li>
    </ul>
    <ul id="R_2.085.036">
        <li id="R_2.085.036ab">āsanaṃ pūjayām āsa rāmāyābhipraṇamya ca</li>
        <li id="R_2.085.036cd">vālavyajanam ādāya nyaṣīdat sacivāsane</li>
    </ul>
    <ul id="R_2.085.037">
        <li id="R_2.085.037ab">ānupūrvyān niṣeduś ca sarve mantrapurohitāḥ</li>
        <li id="R_2.085.037cd">tataḥ senāpatiḥ paścāt praśāstā ca niṣedatuḥ</li>
    </ul>
    <ul id="R_2.085.038">
        <li id="R_2.085.038ab">tatas tatra muhūrtena nadyaḥ pāyasakardamāḥ</li>
        <li id="R_2.085.038cd">upātiṣṭhanta bharataṃ bharadvājasya śāsanat</li>
    </ul>
    <ul id="R_2.085.039">
        <li id="R_2.085.039ab">tāsām ubhayataḥ kūlaṃ pāṇḍumṛttikalepanāḥ</li>
        <li id="R_2.085.039cd">ramyāś cāvasathā divyā brahmaṇas tu prasādajāḥ</li>
    </ul>
    <ul id="R_2.085.040">
        <li id="R_2.085.040ab">tenaiva ca muhūrtena divyābharaṇabhūṣitāḥ</li>
        <li id="R_2.085.040cd">āgur viṃśatisāhasrā brahmaṇā prahitāḥ striyaḥ</li>
    </ul>
    <ul id="R_2.085.041">
        <li id="R_2.085.041ab">suvarṇamaṇimuktena pravālena ca śobhitāḥ</li>
        <li id="R_2.085.041cd">āgur viṃśatisāhasrāḥ kuberaprahitāḥ striyaḥ</li>
    </ul>
    <ul id="R_2.085.042">
        <li id="R_2.085.042ab">yābhir gṛhītaḥ puruṣaḥ sonmāda iva lakṣyate</li>
        <li id="R_2.085.042cd">āgur viṃśatisāhasrā nandanād apsarogaṇāḥ</li>
    </ul>
    <ul id="R_2.085.043">
        <li id="R_2.085.043ab">nāradas tumburur gopaḥ parvataḥ sūryavarcasaḥ</li>
        <li id="R_2.085.043cd">ete gandharvarājāno bharatasyāgrato jaguḥ</li>
    </ul>
    <ul id="R_2.085.044">
        <li id="R_2.085.044ab">alambusā miśrakeśī puṇḍarīkātha vāmanā</li>
        <li id="R_2.085.044cd">upānṛtyaṃs tu bharataṃ bharadvājasya śāsanāt</li>
    </ul>
    <ul id="R_2.085.045">
        <li id="R_2.085.045ab">yāni mālyāni deveṣu yāni caitrarathe vane</li>
        <li id="R_2.085.045cd">prayāge tāny adṛśyanta bharadvājasya śāsanāt</li>
    </ul>
    <ul id="R_2.085.046">
        <li id="R_2.085.046ab">bilvā mārdaṅgikā āsañ śamyā grāhā bibhītakāḥ</li>
        <li id="R_2.085.046cd">aśvatthā nartakāś cāsan bharadvājasya tejasā</li>
    </ul>
    <ul id="R_2.085.047">
        <li id="R_2.085.047ab">tataḥ saralatālāś ca tilakā naktamālakāḥ</li>
        <li id="R_2.085.047cd">prahṛṣṭās tatra saṃpetuḥ kubjābhūtātha vāmanāḥ</li>
    </ul>
    <ul id="R_2.085.048">
        <li id="R_2.085.048ab">śiṃśapāmalakī jambūr yāś cānyāḥ kānane latāḥ</li>
        <li id="R_2.085.048cd">pramadā vigrahaṃ kṛtvā bharadvājāśrame 'vasan</li>
    </ul>
    <ul id="R_2.085.049">
        <li id="R_2.085.049ab">surāṃ surāpāḥ pibata pāyasaṃ ca bubhukśitāḥ</li>
        <li id="R_2.085.049cd">māṃsani ca sumedhyāni bhakṣyantāṃ yāvad icchatha</li>
    </ul>
    <ul id="R_2.085.050">
        <li id="R_2.085.050ab">utsādya snāpayanti sma nadītīreṣu valguṣu</li>
        <li id="R_2.085.050cd">apy ekam ekaṃ puruṣaṃ pramadāḥ satpa cāṣṭa ca</li>
    </ul>
    <ul id="R_2.085.051">
        <li id="R_2.085.051ab">saṃvahantyaḥ samāpetur nāryo ruciralocanāḥ</li>
        <li id="R_2.085.051cd">parimṛjya tathānyonyaṃ pāyayanti varāṅganāḥ</li>
    </ul>
    <ul id="R_2.085.052">
        <li id="R_2.085.052ab">hayān gajān kharān uṣṭrāṃs tathaiva surabheḥ sutān</li>
        <li id="R_2.085.052cd">ikṣūṃś ca madhujālāṃś ca bhojayanti sma vāhanān</li>
        <li id="R_2.085.052ef">ikṣvākuvarayodhānāṃ codayanto mahābalāḥ</li>
    </ul>
    <ul id="R_2.085.053">
        <li id="R_2.085.053ab">nāśvabandho 'śvam ājānān na gajaṃ kuñjaragrahaḥ</li>
        <li id="R_2.085.053cd">mattapramattamuditā camūḥ sā tatra saṃbabhau</li>
    </ul>
    <ul id="R_2.085.054">
        <li id="R_2.085.054ab">tarpitā sarvakāmais te raktacandanarūṣitāḥ</li>
        <li id="R_2.085.054cd">apsarogaṇasaṃyuktāḥ sainyā vācam udairayan</li>
    </ul>
    <ul id="R_2.085.055">
        <li id="R_2.085.055ab">naivāyodhyāṃ gamiṣyāmo na gamiṣyāma daṇḍakān</li>
        <li id="R_2.085.055cd">kuśalaṃ bharatasyāstu rāmasyāstu tathā sukham</li>
    </ul>
    <ul id="R_2.085.056">
        <li id="R_2.085.056ab">iti pādātayodhāś ca hastyaśvārohabandhakāḥ</li>
        <li id="R_2.085.056cd">anāthās taṃ vidhiṃ labdhvā vācam etām udairayan</li>
    </ul>
    <ul id="R_2.085.057">
        <li id="R_2.085.057ab">saṃprahṛṣṭā vinedus te narās tatra sahasraśaḥ</li>
        <li id="R_2.085.057cd">bharatasyānuyātāraḥ svarge 'yam iti cābruvan</li>
    </ul>
    <ul id="R_2.085.058">
        <li id="R_2.085.058ab">tato bhuktavatāṃ teṣāṃ tad annam amṛtopamam</li>
        <li id="R_2.085.058cd">divyān udvīkṣya bhakṣyāṃs tān abhavad bhakṣaṇe matiḥ</li>
    </ul>
    <ul id="R_2.085.059">
        <li id="R_2.085.059ab">preṣyāś ceṭyaś ca vadhvaś ca balasthāś cāpi sarvaśaḥ</li>
        <li id="R_2.085.059cd">babhūvus te bhṛśaṃ tṛptāḥ sarve cāhatavāsasaḥ</li>
    </ul>
    <ul id="R_2.085.060">
        <li id="R_2.085.060ab">kuñjarāś ca kharoṣṭraś ca go'śvāś ca mṛgapakṣiṇaḥ</li>
        <li id="R_2.085.060cd">babhūvuḥ subhṛtās tatra nānyo hy anyam akalpayat</li>
    </ul>
    <ul id="R_2.085.061">
        <li id="R_2.085.061ab">nāśuklavāsās tatrāsīt kṣudhito malino 'pi vā</li>
        <li id="R_2.085.061cd">rajasā dhvastakeśo vā naraḥ kaś cid adṛśyata</li>
    </ul>
    <ul id="R_2.085.062">
        <li id="R_2.085.062ab">ājaiś cāpi ca vārāhair niṣṭhānavarasaṃcayaiḥ</li>
        <li id="R_2.085.062cd">phalaniryūhasaṃsiddhaiḥ sūpair gandharasānvitaiḥ</li>
    </ul>
    <ul id="R_2.085.063">
        <li id="R_2.085.063ab">puṣpadhvajavatīḥ pūrṇāḥ śuklasyānnasya cābhitaḥ</li>
        <li id="R_2.085.063cd">dadṛśur vismitās tatra narā lauhīḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_2.085.064">
        <li id="R_2.085.064ab">babhūvur vanapārśveṣu kūpāḥ pāyasakardamāḥ</li>
        <li id="R_2.085.064cd">tāś ca kāmadughā gāvo drumāś cāsan madhuścyutaḥ</li>
    </ul>
    <ul id="R_2.085.065">
        <li id="R_2.085.065ab">vāpyo maireyapūrṇāś ca mṛṣṭamāṃsacayair vṛtāḥ</li>
        <li id="R_2.085.065cd">prataptapiṭharaiś cāpi mārgamāyūrakaukkuṭaiḥ</li>
    </ul>
    <ul id="R_2.085.066">
        <li id="R_2.085.066ab">pātrīṇāṃ ca sahasrāṇi śātakumbhamayāni ca</li>
        <li id="R_2.085.066cd">sthālyaḥ kumbhyaḥ karambhyaś ca dadhipūrṇāḥ susaṃskṛtāḥ</li>
        <li id="R_2.085.066ef">yauvanasthasya gaurasya kapitthasya sugandhinaḥ</li>
    </ul>
    <ul id="R_2.085.067">
        <li id="R_2.085.067ab">hradāḥ pūrṇā rasālasya dadhnaḥ śvetasya cāpare</li>
        <li id="R_2.085.067cd">babhūvuḥ pāyasasyānte śarkarāyāś ca saṃcayāḥ</li>
    </ul>
    <ul id="R_2.085.068">
        <li id="R_2.085.068ab">kalkāṃś cūrṇakaṣāyāṃś ca snānāni vividhāni ca</li>
        <li id="R_2.085.068cd">dadṛśur bhājanasthāni tīrtheṣu saritāṃ narāḥ</li>
    </ul>
    <ul id="R_2.085.069">
        <li id="R_2.085.069ab">śuklān aṃśumataś cāpi dantadhāvanasaṃcayān</li>
        <li id="R_2.085.069cd">śuklāṃś candanakalkāṃś ca samudgeṣv avatiṣṭhataḥ</li>
    </ul>
    <ul id="R_2.085.070">
        <li id="R_2.085.070ab">darpaṇān parimṛṣṭāṃś ca vāsasāṃ cāpi saṃcayān</li>
        <li id="R_2.085.070cd">pādukopānahāṃ caiva yugmān yatra sahasraśaḥ</li>
    </ul>
    <ul id="R_2.085.071">
        <li id="R_2.085.071ab">āñjanīḥ kaṅkatān kūrcāṃś chatrāṇi ca dhanūṃṣi ca</li>
        <li id="R_2.085.071cd">marmatrāṇāni citrāṇi śayanāny āsanāni ca</li>
    </ul>
    <ul id="R_2.085.072">
        <li id="R_2.085.072ab">pratipānahradān pūrṇān kharoṣṭragajavājinām</li>
        <li id="R_2.085.072cd">avagāhya sutīrthāṃś ca hradān sotpala puṣkarān</li>
    </ul>
    <ul id="R_2.085.073">
        <li id="R_2.085.073ab">nīlavaidūryavarṇāṃś ca mṛdūn yavasasaṃcayān</li>
        <li id="R_2.085.073cd">nirvāpārthaṃ paśūnāṃ te dadṛśus tatra sarvaśaḥ</li>
    </ul>
    <ul id="R_2.085.074">
        <li id="R_2.085.074ab">vyasmayanta manuṣyās te svapnakalpaṃ tad adbhutam</li>
        <li id="R_2.085.074cd">dṛṣṭvātithyaṃ kṛtaṃ tādṛg bharatasya maharṣiṇā</li>
    </ul>
    <ul id="R_2.085.075">
        <li id="R_2.085.075ab">ity evaṃ ramamāṇānāṃ devānām iva nandane</li>
        <li id="R_2.085.075cd">bharadvājāśrame ramye sā rātrir vyatyavartata</li>
    </ul>
    <ul id="R_2.085.076">
        <li id="R_2.085.076ab">pratijagmuś ca tā nadyo gandharvāś ca yathāgatam</li>
        <li id="R_2.085.076cd">bharadvājam anujñāpya tāś ca sarvā varāṅganāḥ</li>
    </ul>
    <ul id="R_2.085.077">
        <li id="R_2.085.077ab">
            <seg type="pāda" n="a">tathaiva mattā madirotkaṭā narās</seg>
            <seg type="pāda" n="b">tathaiva divyāgurucandanokṣitāḥ</seg>
        </li>
        <li id="R_2.085.077cd">
            <seg type="pāda" n="c">tathaiva divyā vividhāḥ sraguttamāḥ</seg>
            <seg type="pāda" n="d">pṛthakprakīrṇā manujaiḥ pramarditāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.086.001">
        <li id="R_2.086.001ab">tatas tāṃ rajanīm uṣya bharataḥ saparicchadaḥ</li>
        <li id="R_2.086.001cd">kṛtātithyo bharadvājaṃ kāmād abhijagāma ha</li>
    </ul>
    <ul id="R_2.086.002">
        <li id="R_2.086.002ab">tam ṛṣiḥ puruṣavyāghraṃ prekṣya prāñjalim āgatam</li>
        <li id="R_2.086.002cd">hutāgnihotro bharataṃ bharadvājo 'bhyabhāṣata</li>
    </ul>
    <ul id="R_2.086.003">
        <li id="R_2.086.003ab">kaccid atra sukhā rātris tavāsmadviṣaye gatā</li>
        <li id="R_2.086.003cd">samagras te janaḥ kaccid ātithye śaṃsa me 'nagha</li>
    </ul>
    <ul id="R_2.086.004">
        <li id="R_2.086.004ab">tam uvācāñjaliṃ kṛtvā bharato 'bhipraṇamya ca</li>
        <li id="R_2.086.004cd">āśramād abhiniṣkrantam ṛṣim uttama tejasaṃ</li>
    </ul>
    <ul id="R_2.086.005">
        <li id="R_2.086.005ab">sukhoṣito 'smi bhagavan samagrabalavāhanaḥ</li>
        <li id="R_2.086.005cd">tarpitaḥ sarvakāmaiś ca sāmātyo balavat tvayā</li>
    </ul>
    <ul id="R_2.086.006">
        <li id="R_2.086.006ab">apetaklamasaṃtāpāḥ subhakṣyāḥ supratiśrayāḥ</li>
        <li id="R_2.086.006cd">api preṣyān upādāya sarve sma susukhoṣitāḥ</li>
    </ul>
    <ul id="R_2.086.007">
        <li id="R_2.086.007ab">āmantraye 'haṃ bhagavan kāmaṃ tvām ṛṣisattama</li>
        <li id="R_2.086.007cd">samīpaṃ prasthitaṃ bhrātur maireṇekṣasva cakṣuṣā</li>
    </ul>
    <ul id="R_2.086.008">
        <li id="R_2.086.008ab">āśramaṃ tasya dharmajña dhārmikasya mahātmanaḥ</li>
        <li id="R_2.086.008cd">ācakṣva katamo mārgaḥ kiyān iti ca śaṃsa me</li>
    </ul>
    <ul id="R_2.086.009">
        <li id="R_2.086.009ab">iti pṛṣṭas tu bharataṃ bhrātṛdarśanalālasaṃ</li>
        <li id="R_2.086.009cd">pratyuvāca mahātejā bharadvājo mahātapāḥ</li>
    </ul>
    <ul id="R_2.086.010">
        <li id="R_2.086.010ab">bharatārdhatṛtīyeṣu yojaneṣv ajane vane</li>
        <li id="R_2.086.010cd">citrakūṭo giris tatra ramyanirdarakānanaḥ</li>
    </ul>
    <ul id="R_2.086.011">
        <li id="R_2.086.011ab">uttaraṃ pārśvam āsādya tasya mandākinī nadī</li>
        <li id="R_2.086.011cd">puṣpitadrumasaṃchannā ramyapuṣpitakānanā</li>
    </ul>
    <ul id="R_2.086.012">
        <li id="R_2.086.012ab">anantaraṃ tat saritaś citrakūṭaś ca parvataḥ</li>
        <li id="R_2.086.012cd">tato parṇakuṭī tāta tatra tau vasato dhruvam</li>
    </ul>
    <ul id="R_2.086.013">
        <li id="R_2.086.013ab">dakṣiṇenaiva mārgeṇa savyadakṣiṇam eva ca</li>
        <li id="R_2.086.013cd">gajavājirathākīrṇāṃ vāhinīṃ vāhinīpate</li>
        <li id="R_2.086.013ef">vāhayasva mahābhāga tato drakṣyasi rāghavam</li>
    </ul>
    <ul id="R_2.086.014">
        <li id="R_2.086.014ab">prayāṇam iti ca śrutvā rājarājasya yoṣitaḥ</li>
        <li id="R_2.086.014cd">hitvā yānāni yānārhā brāhmaṇaṃ paryavārayan</li>
    </ul>
    <ul id="R_2.086.015">
        <li id="R_2.086.015ab">vepamānā kṛśā dīnā saha devyā sumantriyā</li>
        <li id="R_2.086.015cd">kausalyā tatra jagrāha karābhyāṃ caraṇau muneḥ</li>
    </ul>
    <ul id="R_2.086.016">
        <li id="R_2.086.016ab">asamṛddhena kāmena sarvalokasya garhitā</li>
        <li id="R_2.086.016cd">kaikeyī tasya jagrāha caraṇau savyapatrapā</li>
    </ul>
    <ul id="R_2.086.017">
        <li id="R_2.086.017ab">taṃ pradakṣiṇam āgamya bhagavantaṃ mahāmunim</li>
        <li id="R_2.086.017cd">adūrād bharatasyaiva tasthau dīnamanās tadā</li>
    </ul>
    <ul id="R_2.086.018">
        <li id="R_2.086.018ab">tataḥ papraccha bharataṃ bharadvājo dṛḍhavrataḥ</li>
        <li id="R_2.086.018cd">viśeṣaṃ jñātum icchāmi mātṝṇāṃ tava rāghava</li>
    </ul>
    <ul id="R_2.086.019">
        <li id="R_2.086.019ab">evam uktas tu bharato bharadvājena dhārmikaḥ</li>
        <li id="R_2.086.019cd">uvāca prāñjalir bhūtvā vākyaṃ vacanakovidaḥ</li>
    </ul>
    <ul id="R_2.086.020">
        <li id="R_2.086.020ab">yām imāṃ bhagavan dīnāṃ śokān aśanakarśitām</li>
        <li id="R_2.086.020cd">pitur hi mahiṣīṃ devīṃ devatām iva paśyasi</li>
    </ul>
    <ul id="R_2.086.021">
        <li id="R_2.086.021ab">eṣā taṃ puruṣavyāghraṃ siṃhavikrāntagāminam</li>
        <li id="R_2.086.021cd">kausalyā suṣuve rāmaṃ dhātāram aditir yathā</li>
    </ul>
    <ul id="R_2.086.022">
        <li id="R_2.086.022ab">asyā vāmabhujaṃ śliṣṭā yaiṣā tiṣṭhati durmanāḥ</li>
        <li id="R_2.086.022cd">karṇikārasya śākheva śīrṇapuṣpā vanāntare</li>
    </ul>
    <ul id="R_2.086.023">
        <li id="R_2.086.023ab">etasyās tau sutau devyāḥ kumārau devavarṇinau</li>
        <li id="R_2.086.023cd">ubhau lakṣmaṇaśatrughnau vīrau satyaparākramau</li>
    </ul>
    <ul id="R_2.086.024">
        <li id="R_2.086.024ab">yasyāḥ kṛte naravyāghrau jīvanāśam ito gatau</li>
        <li id="R_2.086.024cd">rājā putravihīnaś ca svargaṃ daśaratho gataḥ</li>
    </ul>
    <ul id="R_2.086.025">
        <li id="R_2.086.025ab">aiśvaryakāmāṃ kaikeyīm anāryām āryarūpiṇīm</li>
        <li id="R_2.086.025cd">mamaitāṃ mātaraṃ viddhi nṛśaṃsāṃ pāpaniścayām</li>
        <li id="R_2.086.025ef">yatomūlaṃ hi paśyāmi vyasanaṃ mahad ātmanaḥ</li>
    </ul>
    <ul id="R_2.086.026">
        <li id="R_2.086.026ab">ity uktvā naraśārdūlo bāṣpagadgadayā girā</li>
        <li id="R_2.086.026cd">sa niśaśvāsa tāmrākṣo kruddho nāga ivāsakṛt</li>
    </ul>
    <ul id="R_2.086.027">
        <li id="R_2.086.027ab">bharadvājo maharṣis taṃ bruvantaṃ bharataṃ tadā</li>
        <li id="R_2.086.027cd">pratyuvāca mahābuddhir idaṃ vacanam arthavat</li>
    </ul>
    <ul id="R_2.086.028">
        <li id="R_2.086.028ab">na doṣeṇāvagantavyā kaikeyī bharata tvayā</li>
        <li id="R_2.086.028cd">rāmapravrājanaṃ hy etat sukhodarkaṃ bhaviṣyati</li>
    </ul>
    <ul id="R_2.086.029">
        <li id="R_2.086.029ab">abhivādya tu saṃsiddhaḥ kṛtvā cainaṃ pradakṣiṇam</li>
        <li id="R_2.086.029cd">āmantrya bharataḥ sainyaṃ yujyatām ity acodayat</li>
    </ul>
    <ul id="R_2.086.030">
        <li id="R_2.086.030ab">tato vājirathān yuktvā divyān hemapariṣkritān</li>
        <li id="R_2.086.030cd">adhyārohat prayāṇārthī bahūn bahuvidho janaḥ</li>
    </ul>
    <ul id="R_2.086.031">
        <li id="R_2.086.031ab">gajakanyāgajāś caiva hemakakṣyāḥ patākinaḥ</li>
        <li id="R_2.086.031cd">jīmūtā iva gharmānte saghoṣāḥ saṃpratasthire</li>
    </ul>
    <ul id="R_2.086.032">
        <li id="R_2.086.032ab">vividhāny api yānāni mahāni ca laghūni ca</li>
        <li id="R_2.086.032cd">prayayuḥ sumahārhāṇi pādair eva padātayaḥ</li>
    </ul>
    <ul id="R_2.086.033">
        <li id="R_2.086.033ab">atha yānapravekais tu kausalyāpramukhāḥ striyaḥ</li>
        <li id="R_2.086.033cd">rāmadarśanakāṅkṣiṇyaḥ prayayur muditās tadā</li>
    </ul>
    <ul id="R_2.086.034">
        <li id="R_2.086.034ab">sa cārkataruṇābhāsāṃ niyuktāṃ śibikāṃ śubhām</li>
        <li id="R_2.086.034cd">āsthāya prayayau śrīmān bharataḥ saparicchadaḥ</li>
    </ul>
    <ul id="R_2.086.035">
        <li id="R_2.086.035ab">sā prayātā mahāsenā gajavājirathākulā</li>
        <li id="R_2.086.035cd">dakṣiṇāṃ diśam āvṛtya mahāmegha ivotthitaḥ</li>
        <li id="R_2.086.035ef">vanāni tu vyatikramya juṣṭāni mṛgapakṣibhiḥ</li>
    </ul>
    <ul id="R_2.086.036">
        <li id="R_2.086.036ab">
            <seg type="pāda" n="a">sā saṃprahṛṣṭadvipavājiyodhā</seg>
            <seg type="pāda" n="b">vitrāsayantī mṛgapakṣisaṃghān</seg>
        </li>
        <li id="R_2.086.036cd">
            <seg type="pāda" n="c">mahad vanaṃ tat pravigāhamānā</seg>
            <seg type="pāda" n="d">rarāja senā bharatasya tatra</seg>
        </li>
    </ul>
    <ul id="R_2.087.001">
        <li id="R_2.087.001ab">tayā mahatyā yāyinyā dhvajinyā vanavāsinaḥ</li>
        <li id="R_2.087.001cd">arditā yūthapā mattāḥ sayūthāḥ saṃpradudruvuḥ</li>
    </ul>
    <ul id="R_2.087.002">
        <li id="R_2.087.002ab">ṛkṣāḥ pṛṣatasaṃghāś ca ruravaś ca samantataḥ</li>
        <li id="R_2.087.002cd">dṛśyante vanarājīṣu giriṣv api nadīṣu ca</li>
    </ul>
    <ul id="R_2.087.003">
        <li id="R_2.087.003ab">sa saṃpratasthe dharmātmā prīto daśarathātmajaḥ</li>
        <li id="R_2.087.003cd">vṛto mahatyā nādinyā senayā caturaṅgayā</li>
    </ul>
    <ul id="R_2.087.004">
        <li id="R_2.087.004ab">sāgaraughanibhā senā bharatasya mahātmanaḥ</li>
        <li id="R_2.087.004cd">mahīṃ saṃchādayām āsa prāvṛṣi dyām ivāmbudaḥ</li>
    </ul>
    <ul id="R_2.087.005">
        <li id="R_2.087.005ab">turaṃgaughair avatatā vāraṇaiś ca mahājavaiḥ</li>
        <li id="R_2.087.005cd">anālakṣyā ciraṃ kālaṃ tasmin kāle babhūva bhūḥ</li>
    </ul>
    <ul id="R_2.087.006">
        <li id="R_2.087.006ab">sa yātvā dūram adhvānaṃ supariśrānta vāhanaḥ</li>
        <li id="R_2.087.006cd">uvāca bharataḥ śrīmān vasiṣṭhaṃ mantriṇāṃ varam</li>
    </ul>
    <ul id="R_2.087.007">
        <li id="R_2.087.007ab">yādṛśaṃ lakṣyate rūpaṃ yathā caiva śrutaṃ mayā</li>
        <li id="R_2.087.007cd">vyaktaṃ prāptāḥ sma taṃ deśaṃ bharadvājo yam abravīt</li>
    </ul>
    <ul id="R_2.087.008">
        <li id="R_2.087.008ab">ayaṃ giriś citrakūṭas tathā mandākinī nadī</li>
        <li id="R_2.087.008cd">etat prakāśate dūrān nīlameghanibhaṃ vanam</li>
    </ul>
    <ul id="R_2.087.009">
        <li id="R_2.087.009ab">gireḥ sānūni ramyāṇi citrakūṭasya saṃprati</li>
        <li id="R_2.087.009cd">vāraṇair avamṛdyante māmakaiḥ parvatopamaiḥ</li>
    </ul>
    <ul id="R_2.087.010">
        <li id="R_2.087.010ab">muñcanti kusumāny ete nagāḥ parvatasānuṣu</li>
        <li id="R_2.087.010cd">nīlā ivātapāpāye toyaṃ toyadharā ghanāḥ</li>
    </ul>
    <ul id="R_2.087.011">
        <li id="R_2.087.011ab">kinnarācaritoddeśaṃ paśya śatrughna parvatam</li>
        <li id="R_2.087.011cd">hayaiḥ samantād ākīrṇaṃ makarair iva sāgaram</li>
    </ul>
    <ul id="R_2.087.012">
        <li id="R_2.087.012ab">ete mṛgagaṇā bhānti śīghravegāḥ pracoditāḥ</li>
        <li id="R_2.087.012cd">vāyupraviddhāḥ śaradi megharājya ivāmbare</li>
    </ul>
    <ul id="R_2.087.013">
        <li id="R_2.087.013ab">kurvanti kusumāpīḍāñ śiraḥsu surabhīn amī</li>
        <li id="R_2.087.013cd">meghaprakāśaiḥ phalakair dākṣiṇātyā yathā narāḥ</li>
    </ul>
    <ul id="R_2.087.014">
        <li id="R_2.087.014ab">niṣkūjam iva bhūtvedaṃ vanaṃ ghorapradarśanam</li>
        <li id="R_2.087.014cd">ayodhyeva janākīrṇā saṃprati pratibhāti mā</li>
    </ul>
    <ul id="R_2.087.015">
        <li id="R_2.087.015ab">khurair udīrito reṇur divaṃ pracchādya tiṣṭhati</li>
        <li id="R_2.087.015cd">taṃ vahaty anilaḥ śīghraṃ kurvann iva mama priyam</li>
    </ul>
    <ul id="R_2.087.016">
        <li id="R_2.087.016ab">syandanāṃs turagopetān sūtamukhyair adhiṣṭhitān</li>
        <li id="R_2.087.016cd">etān saṃpatataḥ śīghraṃ paśya śatrughna kānane</li>
    </ul>
    <ul id="R_2.087.017">
        <li id="R_2.087.017ab">etān vitrāsitān paśya barhiṇaḥ priyadarśanān</li>
        <li id="R_2.087.017cd">etam āviśataḥ śailam adhivāsaṃ patatriṇām</li>
    </ul>
    <ul id="R_2.087.018">
        <li id="R_2.087.018ab">atimātram ayaṃ deśo manojñaḥ pratibhāti mā</li>
        <li id="R_2.087.018cd">tāpasānāṃ nivāso 'yaṃ vyaktaṃ svargapatho yathā</li>
    </ul>
    <ul id="R_2.087.019">
        <li id="R_2.087.019ab">mṛgā mṛgībhiḥ sahitā bahavaḥ pṛṣatā vane</li>
        <li id="R_2.087.019cd">manojña rūpā lakṣyante kusumair iva citritaḥ</li>
    </ul>
    <ul id="R_2.087.020">
        <li id="R_2.087.020ab">sādhu sainyāḥ pratiṣṭhantāṃ vicinvantu ca kānanam</li>
        <li id="R_2.087.020cd">yathā tau puruṣavyāghrau dṛśyete rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_2.087.021">
        <li id="R_2.087.021ab">bharatasya vacaḥ śrutvā puruṣāḥ śastrapāṇayaḥ</li>
        <li id="R_2.087.021cd">viviśus tad vanaṃ śūrā dhūmaṃ ca dadṛśus tataḥ</li>
    </ul>
    <ul id="R_2.087.022">
        <li id="R_2.087.022ab">te samālokya dhūmāgram ūcur bharatam āgatāḥ</li>
        <li id="R_2.087.022cd">nāmanuṣye bhavaty agnir vyaktam atraiva rāghavau</li>
    </ul>
    <ul id="R_2.087.023">
        <li id="R_2.087.023ab">atha nātra naravyāghrau rājaputrau paraṃtapau</li>
        <li id="R_2.087.023cd">anye rāmopamāḥ santi vyaktam atra tapasvinaḥ</li>
    </ul>
    <ul id="R_2.087.024">
        <li id="R_2.087.024ab">tac chrutvā bharatas teṣāṃ vacanaṃ sādhu saṃmatam</li>
        <li id="R_2.087.024cd">sainyān uvāca sarvāṃs tān amitrabalamardanaḥ</li>
    </ul>
    <ul id="R_2.087.025">
        <li id="R_2.087.025ab">yat tā bhavantas tiṣṭhantu neto gantavyam agrataḥ</li>
        <li id="R_2.087.025cd">aham eva gamiṣyāmi sumantro gurur eva ca</li>
    </ul>
    <ul id="R_2.087.026">
        <li id="R_2.087.026ab">evam uktās tataḥ sarve tatra tasthuḥ samantataḥ</li>
        <li id="R_2.087.026cd">bharato yatra dhūmāgraṃ tatra dṛṣṭiṃ samādadhat</li>
    </ul>
    <ul id="R_2.087.027">
        <li id="R_2.087.027ab">
            <seg type="pāda" n="a">vyavasthitā yā bharatena sā camūr</seg>
            <seg type="pāda" n="b">nirīkṣamāṇāpi ca dhūmam agrataḥ</seg>
        </li>
        <li id="R_2.087.027cd">
            <seg type="pāda" n="c">babhūva hṛṣṭā nacireṇa jānatī</seg>
            <seg type="pāda" n="d">priyasya rāmasya samāgamaṃ tadā</seg>
        </li>
    </ul>
    <ul id="R_2.088.001">
        <li id="R_2.088.001ab">dīrghakāloṣitas tasmin girau girivanapriyaḥ</li>
        <li id="R_2.088.001cd">videhyāḥ priyamākāṅkṣan svaṃ ca cittaṃ vilobhayan</li>
    </ul>
    <ul id="R_2.088.002">
        <li id="R_2.088.002ab">atha dāśarathiś citraṃ citrakūṭam adarśayat</li>
        <li id="R_2.088.002cd">bhāryām amarasaṃkāśaḥ śacīm iva puraṃdaraḥ</li>
    </ul>
    <ul id="R_2.088.003">
        <li id="R_2.088.003ab">na rājyād bhraṃśanaṃ bhadre na suhṛdbhir vinābhavaḥ</li>
        <li id="R_2.088.003cd">mano me bādhate dṛṣṭvā ramaṇīyam imaṃ girim</li>
    </ul>
    <ul id="R_2.088.004">
        <li id="R_2.088.004ab">paśyemam acalaṃ bhadre nānādvijagaṇāyutam</li>
        <li id="R_2.088.004cd">śikharaiḥ kham ivodviddhair dhātumadbhir vibhūṣitam</li>
    </ul>
    <ul id="R_2.088.005">
        <li id="R_2.088.005ab">ke cid rajatasaṃkāśāḥ ke cit kṣatajasaṃnibhāḥ</li>
        <li id="R_2.088.005cd">pītamāñjiṣṭhavarṇāś ca ke cin maṇivaraprabhāḥ</li>
    </ul>
    <ul id="R_2.088.006">
        <li id="R_2.088.006ab">puṣyārkaketukābhāś ca ke cij jyotī rasaprabhāḥ</li>
        <li id="R_2.088.006cd">virājante 'calendrasya deśā dhātuvibhūṣitāḥ</li>
    </ul>
    <ul id="R_2.088.007">
        <li id="R_2.088.007ab">nānāmṛgagaṇadvīpitarakṣvṛkṣagaṇair vṛtaḥ</li>
        <li id="R_2.088.007cd">aduṣṭair bhāty ayaṃ śailo bahupakṣisamākulaḥ</li>
    </ul>
    <ul id="R_2.088.008">
        <li id="R_2.088.008ab">āmrajambvasanair lodhraiḥ priyālaiḥ panasair dhavaiḥ</li>
        <li id="R_2.088.008cd">aṅkolair bhavyatiniśair bilvatindukaveṇubhiḥ</li>
    </ul>
    <ul id="R_2.088.009">
        <li id="R_2.088.009ab">kāśmaryariṣṭavaraṇair madhūkais tilakais tathā</li>
        <li id="R_2.088.009cd">badaryāmalakair nīpair vetradhanvanabījakaiḥ</li>
    </ul>
    <ul id="R_2.088.010">
        <li id="R_2.088.010ab">puṣpavadbhiḥ phalopetaiś chāyāvadbhir manoramaiḥ</li>
        <li id="R_2.088.010cd">evamādibhir ākīrṇaḥ śriyaṃ puṣyaty ayaṃ giriḥ</li>
    </ul>
    <ul id="R_2.088.011">
        <li id="R_2.088.011ab">śailaprastheṣu ramyeṣu paśyemān kāmaharṣaṇān</li>
        <li id="R_2.088.011cd">kinnarān dvaṃdvaśo bhadre ramamāṇān manasvinaḥ</li>
    </ul>
    <ul id="R_2.088.012">
        <li id="R_2.088.012ab">śākhāvasaktān khaḍgāṃś ca pravarāṇy ambarāṇi ca</li>
        <li id="R_2.088.012cd">paśya vidyādharastrīṇāṃ krīḍed deśān manoramān</li>
    </ul>
    <ul id="R_2.088.013">
        <li id="R_2.088.013ab">jalaprapātair udbhedair niṣyandaiś ca kva cit kva cit</li>
        <li id="R_2.088.013cd">sravadbhir bhāty ayaṃ śailaḥ sravan mada iva dvipaḥ</li>
    </ul>
    <ul id="R_2.088.014">
        <li id="R_2.088.014ab">guhāsamīraṇo gandhān nānāpuṣpabhavān vahan</li>
        <li id="R_2.088.014cd">ghrāṇatarpaṇam abhyetya kaṃ naraṃ na praharṣayet</li>
    </ul>
    <ul id="R_2.088.015">
        <li id="R_2.088.015ab">yadīha śarado 'nekās tvayā sārdham anindite</li>
        <li id="R_2.088.015cd">lakṣmaṇena ca vatsyāmi na māṃ śokaḥ pradhakṣyati</li>
    </ul>
    <ul id="R_2.088.016">
        <li id="R_2.088.016ab">bahupuṣpaphale ramye nānādvijagaṇāyute</li>
        <li id="R_2.088.016cd">vicitraśikhare hy asmin ratavān asmi bhāmini</li>
    </ul>
    <ul id="R_2.088.017">
        <li id="R_2.088.017ab">anena vanavāsena mayā prāptaṃ phaladvayam</li>
        <li id="R_2.088.017cd">pituś cānṛṇatā dharme bharatasya priyaṃ tathā</li>
    </ul>
    <ul id="R_2.088.018">
        <li id="R_2.088.018ab">vaidehi ramase kaccic citrakūṭe mayā saha</li>
        <li id="R_2.088.018cd">paśyantī vividhān bhāvān manovākkāyasaṃyatān</li>
    </ul>
    <ul id="R_2.088.019">
        <li id="R_2.088.019ab">idam evāmṛtaṃ prāhū rājñāṃ rājarṣayaḥ pare</li>
        <li id="R_2.088.019cd">vanavāsaṃ bhavārthāya pretya me prapitāmahāḥ</li>
    </ul>
    <ul id="R_2.088.020">
        <li id="R_2.088.020ab">śilāḥ śailasya śobhante viśālāḥ śataśo 'bhitaḥ</li>
        <li id="R_2.088.020cd">bahulā bahulair varṇair nīlapītasitāruṇaiḥ</li>
    </ul>
    <ul id="R_2.088.021">
        <li id="R_2.088.021ab">niśi bhānty acalendrasya hutāśanaśikhā iva</li>
        <li id="R_2.088.021cd">oṣadhyaḥ svaprabhā lakṣmyā bhrājamānāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_2.088.022">
        <li id="R_2.088.022ab">ke cit kṣayanibhā deśāḥ ke cid udyānasaṃnibhāḥ</li>
        <li id="R_2.088.022cd">ke cid ekaśilā bhānti parvatasyāsya bhāmini</li>
    </ul>
    <ul id="R_2.088.023">
        <li id="R_2.088.023ab">bhittveva vasudhāṃ bhāti citrakūṭaḥ samutthitaḥ</li>
        <li id="R_2.088.023cd">citrakūṭasya kūṭo 'sau dṛśyate sarvataḥ śivaḥ</li>
    </ul>
    <ul id="R_2.088.024">
        <li id="R_2.088.024ab">kuṣṭhapuṃnāgatagarabhūrjapatrottaracchadān</li>
        <li id="R_2.088.024cd">kāmināṃ svāstarān paśya kuśeśayadalāyutān</li>
    </ul>
    <ul id="R_2.088.025">
        <li id="R_2.088.025ab">mṛditāś cāpaviddhāś ca dṛśyante kamalasrajaḥ</li>
        <li id="R_2.088.025cd">kāmibhir vanite paśya phalāni vividhāni ca</li>
    </ul>
    <ul id="R_2.088.026">
        <li id="R_2.088.026ab">vasvaukasārāṃ nalinīm atyetīvottarān kurūn</li>
        <li id="R_2.088.026cd">parvataś citrakūṭo 'sau bahumūlaphalodakaḥ</li>
    </ul>
    <ul id="R_2.088.027">
        <li id="R_2.088.027ab">
            <seg type="pāda" n="a">imaṃ tu kālaṃ vanite vijahrivāṃs</seg>
            <seg type="pāda" n="b">tvayā ca sīte saha lakṣmaṇena ca</seg>
        </li>
        <li id="R_2.088.027cd">
            <seg type="pāda" n="c">ratiṃ prapatsye kuladharmavardhinīṃ</seg>
            <seg type="pāda" n="d">satāṃ pathi svair niyamaiḥ paraiḥ sthitaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.089.001">
        <li id="R_2.089.001ab">atha śailād viniṣkramya maithilīṃ kosaleśvaraḥ</li>
        <li id="R_2.089.001cd">adarśayac chubhajalāṃ ramyāṃ mandākinīṃ nadīm</li>
    </ul>
    <ul id="R_2.089.002">
        <li id="R_2.089.002ab">abravīc ca varārohāṃ cārucandranibhānanām</li>
        <li id="R_2.089.002cd">videharājasya sutāṃ rāmo rājīvalocanaḥ</li>
    </ul>
    <ul id="R_2.089.003">
        <li id="R_2.089.003ab">vicitrapulināṃ ramyāṃ haṃsasārasasevitām</li>
        <li id="R_2.089.003cd">kusumair upasaṃpannāṃ paśya mandākinīṃ nadīm</li>
    </ul>
    <ul id="R_2.089.004">
        <li id="R_2.089.004ab">nānāvidhais tīraruhair vṛtāṃ puṣpaphaladrumaiḥ</li>
        <li id="R_2.089.004cd">rājantīṃ rājarājasya nalinīm iva sarvataḥ</li>
    </ul>
    <ul id="R_2.089.005">
        <li id="R_2.089.005ab">mṛgayūthanipītāni kaluṣāmbhāṃsi sāmpratam</li>
        <li id="R_2.089.005cd">tīrthāni ramaṇīyāni ratiṃ saṃjanayanti me</li>
    </ul>
    <ul id="R_2.089.006">
        <li id="R_2.089.006ab">jaṭājinadharāḥ kāle valkalottaravāsasaḥ</li>
        <li id="R_2.089.006cd">ṛṣayas tv avagāhante nadīṃ mandākinīṃ priye</li>
    </ul>
    <ul id="R_2.089.007">
        <li id="R_2.089.007ab">ādityam upatiṣṭhante niyamād ūrdhvabāhavaḥ</li>
        <li id="R_2.089.007cd">ete 'pare viśālākṣi munayaḥ saṃśitavratāḥ</li>
    </ul>
    <ul id="R_2.089.008">
        <li id="R_2.089.008ab">mārutoddhūta śikharaiḥ pranṛtta iva parvataḥ</li>
        <li id="R_2.089.008cd">pādapaiḥ patrapuṣpāṇi sṛjadbhir abhito nadīm</li>
    </ul>
    <ul id="R_2.089.009">
        <li id="R_2.089.009ab">kaccin maṇinikāśodāṃ kaccit pulinaśālinīm</li>
        <li id="R_2.089.009cd">kaccit siddhajanākīrṇāṃ paśya mandākinīṃ nadīm</li>
    </ul>
    <ul id="R_2.089.010">
        <li id="R_2.089.010ab">nirdhūtān vāyunā paśya vitatān puṣpasaṃcayān</li>
        <li id="R_2.089.010cd">poplūyamānān aparān paśya tvaṃ jalamadhyagān</li>
    </ul>
    <ul id="R_2.089.011">
        <li id="R_2.089.011ab">tāṃś cātivalgu vacaso rathāṅgāhvayanā dvijāḥ</li>
        <li id="R_2.089.011cd">adhirohanti kalyāṇi niṣkūjantaḥ śubhā giraḥ</li>
    </ul>
    <ul id="R_2.089.012">
        <li id="R_2.089.012ab">darśanaṃ citrakūṭasya mandākinyāś ca śobhane</li>
        <li id="R_2.089.012cd">adhikaṃ puravāsāc ca manye ca tava darśanāt</li>
    </ul>
    <ul id="R_2.089.013">
        <li id="R_2.089.013ab">vidhūtakaluṣaiḥ siddhais tapodamaśamānvitaiḥ</li>
        <li id="R_2.089.013cd">nityavikṣobhita jalāṃ vihāhasva mayā saha</li>
    </ul>
    <ul id="R_2.089.014">
        <li id="R_2.089.014ab">sakhīvac ca vigāhasva sīte mandakinīm imām</li>
        <li id="R_2.089.014cd">kamalāny avamajjantī puṣkarāṇi ca bhāmini</li>
    </ul>
    <ul id="R_2.089.015">
        <li id="R_2.089.015ab">tvaṃ paurajanavad vyālān ayodhyām iva parvatam</li>
        <li id="R_2.089.015cd">manyasva vanite nityaṃ sarayūvad imāṃ nadīm</li>
    </ul>
    <ul id="R_2.089.016">
        <li id="R_2.089.016ab">lakṣmaṇaś caiva dharmātmā mannideśe vyavasthitaḥ</li>
        <li id="R_2.089.016cd">tvaṃ cānukūlā vaidehi prītiṃ janayatho mama</li>
    </ul>
    <ul id="R_2.089.017">
        <li id="R_2.089.017ab">upaspṛśaṃs triṣavaṇaṃ madhumūlaphalāśanaḥ</li>
        <li id="R_2.089.017cd">nāyodhyāyai na rājyāya spṛhaye 'dya tvayā saha</li>
    </ul>
    <ul id="R_2.089.018">
        <li id="R_2.089.018ab">
            <seg type="pāda" n="a">imāṃ hi ramyāṃ gajayūthalolitāṃ</seg>
            <seg type="pāda" n="b">nipītatoyāṃ gajasiṃhavānaraiḥ</seg>
        </li>
        <li id="R_2.089.018cd">
            <seg type="pāda" n="c">supuṣpitaiḥ puṣpadharair alaṃkṛtāṃ</seg>
            <seg type="pāda" n="d">na so 'sti yaḥ syān na gatakramaḥ sukhī</seg>
        </li>
    </ul>
    <ul id="R_2.089.019">
        <li id="R_2.089.019ab">
            <seg type="pāda" n="a">itīva rāmo bahusaṃgataṃ vacaḥ</seg>
            <seg type="pāda" n="b">priyā sahāyaḥ saritaṃ prati bruvan</seg>
        </li>
        <li id="R_2.089.019cd">
            <seg type="pāda" n="c">cacāra ramyaṃ nayanāñjanaprabhaṃ</seg>
            <seg type="pāda" n="d">sa citrakūṭaṃ raghuvaṃśavardhanaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.090.001">
        <li id="R_2.090.001ab">tathā tatrāsatas tasya bharatasyopayāyinaḥ</li>
        <li id="R_2.090.001cd">sainya reṇuś ca śabdaś ca prādurāstāṃ nabhaḥ spṛśau</li>
    </ul>
    <ul id="R_2.090.002">
        <li id="R_2.090.002ab">etasminn antare trastāḥ śabdena mahatā tataḥ</li>
        <li id="R_2.090.002cd">arditā yūthapā mattāḥ sayūthā dudruvur diśaḥ</li>
    </ul>
    <ul id="R_2.090.003">
        <li id="R_2.090.003ab">sa taṃ sainyasamudbhūtaṃ śabdaṃ śuśrava rāghavaḥ</li>
        <li id="R_2.090.003cd">tāṃś ca vipradrutān sarvān yūthapān anvavaikṣata</li>
    </ul>
    <ul id="R_2.090.004">
        <li id="R_2.090.004ab">tāṃś ca vidravato dṛṣṭvā taṃ ca śrutvā sa niḥsvanam</li>
        <li id="R_2.090.004cd">uvāca rāmaḥ saumitriṃ lakṣmaṇaṃ dīptatejasaṃ</li>
    </ul>
    <ul id="R_2.090.005">
        <li id="R_2.090.005ab">hanta lakṣmaṇa paśyeha sumitrā suprajās tvayā</li>
        <li id="R_2.090.005cd">bhīmastanitagambhīras tumulaḥ śrūyate svanaḥ</li>
    </ul>
    <ul id="R_2.090.006">
        <li id="R_2.090.006ab">rājā vā rājamātro vā mṛgayām aṭate vane</li>
        <li id="R_2.090.006cd">anyad vā śvāpadaṃ kiṃ cit saumitre jñātum arhasi</li>
        <li id="R_2.090.006ef">sarvam etad yathātattvam acirāj jñātum arhasi</li>
    </ul>
    <ul id="R_2.090.007">
        <li id="R_2.090.007ab">sa lakṣmaṇaḥ saṃtvaritaḥ sālam āruhya puṣpitam</li>
        <li id="R_2.090.007cd">prekṣamāṇo diśaḥ sarvāḥ pūrvāṃ diśam avaikṣata</li>
    </ul>
    <ul id="R_2.090.008">
        <li id="R_2.090.008ab">udaṅmukhaḥ prekṣamāṇo dadarśa mahatīṃ camūm</li>
        <li id="R_2.090.008cd">rathāśvagajasaṃbādhāṃ yattair yuktāṃ padātibhiḥ</li>
    </ul>
    <ul id="R_2.090.009">
        <li id="R_2.090.009ab">tām aśvagajasaṃpūrṇāṃ rathadhvajavibhūṣitām</li>
        <li id="R_2.090.009cd">śaśaṃsa senāṃ rāmāya vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.090.010">
        <li id="R_2.090.010ab">agniṃ saṃśamayatv āryaḥ sītā ca bhajatāṃ guhām</li>
        <li id="R_2.090.010cd">sajyaṃ kuruṣva cāpaṃ ca śarāṃś ca kavacaṃ tathā</li>
    </ul>
    <ul id="R_2.090.011">
        <li id="R_2.090.011ab">taṃ rāmaḥ puruṣavyāghro lakṣmaṇaṃ pratyuvāca ha</li>
        <li id="R_2.090.011cd">aṅgāvekṣasva saumitre kasyaitāṃ manyase camūm</li>
    </ul>
    <ul id="R_2.090.012">
        <li id="R_2.090.012ab">evam ukktas tu rāmeṇa lakṣmāṇo vākyam abravīt</li>
        <li id="R_2.090.012cd">didhakṣann iva tāṃ senāṃ ruṣitaḥ pāvako yathā</li>
    </ul>
    <ul id="R_2.090.013">
        <li id="R_2.090.013ab">saṃpannaṃ rājyam icchaṃs tu vyaktaṃ prāpyābhiṣecanam</li>
        <li id="R_2.090.013cd">āvāṃ hantuṃ samabhyeti kaikeyyā bharataḥ sutaḥ</li>
    </ul>
    <ul id="R_2.090.014">
        <li id="R_2.090.014ab">eṣa vai sumahāñ śrīmān viṭapī saṃprakāśate</li>
        <li id="R_2.090.014cd">virājaty udgataskandhaḥ kovidāra dhvajo rathe</li>
    </ul>
    <ul id="R_2.090.015">
        <li id="R_2.090.015ab">bhajanty ete yathākāmam aśvān āruhya śīghragān</li>
        <li id="R_2.090.015cd">ete bhrājanti saṃhṛṣṭā gajān āruhya sādinaḥ</li>
    </ul>
    <ul id="R_2.090.016">
        <li id="R_2.090.016ab">gṛhītadhanuṣau cāvāṃ giriṃ vīra śrayāvahe</li>
        <li id="R_2.090.016cd">atha vehaiva tiṣṭhāvaḥ saṃnaddhāv udyatāyudhau</li>
        <li id="R_2.090.016ef">api nau vaśam āgacchet kovidāradhvajo raṇe</li>
    </ul>
    <ul id="R_2.090.017">
        <li id="R_2.090.017ab">api drakṣyāmi bharataṃ yatkṛte vyasanaṃ mahat</li>
        <li id="R_2.090.017cd">tvayā rāghava saṃprāptaṃ sītayā ca mayā tathā</li>
    </ul>
    <ul id="R_2.090.018">
        <li id="R_2.090.018ab">yannimittaṃ bhavān rājyāc cyuto rāghava śāśvatīm</li>
        <li id="R_2.090.018cd">saṃprāpto 'yam arir vīra bharato vadhya eva me</li>
    </ul>
    <ul id="R_2.090.019">
        <li id="R_2.090.019ab">bharatasya vadhe doṣaṃ nāhaṃ paśyāmi rāghava</li>
        <li id="R_2.090.019cd">pūrvāpakāriṇāṃ tyāge na hy adharmo vidhīyate</li>
        <li id="R_2.090.019ef">etasmin nihate kṛtsnām anuśādhi vasuṃdharām</li>
    </ul>
    <ul id="R_2.090.020">
        <li id="R_2.090.020ab">adya putraṃ hataṃ saṃkhye kaikeyī rājyakāmukā</li>
        <li id="R_2.090.020cd">mayā paśyet suduḥkhārtā hastibhagnam iva drumam</li>
    </ul>
    <ul id="R_2.090.021">
        <li id="R_2.090.021ab">kaikeyīṃ ca vadhiṣyāmi sānubandhāṃ sabāndhavām</li>
        <li id="R_2.090.021cd">kaluṣeṇādya mahatā medinī parimucyatām</li>
    </ul>
    <ul id="R_2.090.022">
        <li id="R_2.090.022ab">adyemaṃ saṃyataṃ krodham asatkāraṃ ca mānada</li>
        <li id="R_2.090.022cd">mokṣyāmi śatrusainyeṣu kakṣeṣv iva hutāśanam</li>
    </ul>
    <ul id="R_2.090.023">
        <li id="R_2.090.023ab">adyaitac citrakūṭasya kānanaṃ niśitaiḥ śaraiḥ</li>
        <li id="R_2.090.023cd">bhindañ śatruśarīrāṇi kariṣye śoṇitokṣitam</li>
    </ul>
    <ul id="R_2.090.024">
        <li id="R_2.090.024ab">śarair nirbhinnahṛdayān kuñjarāṃs turagāṃs tathā</li>
        <li id="R_2.090.024cd">śvāpadāḥ parikarṣantu narāś ca nihatān mayā</li>
    </ul>
    <ul id="R_2.090.025">
        <li id="R_2.090.025ab">śarāṇāṃ dhanuṣaś cāham anṛṇo 'smi mahāvane</li>
        <li id="R_2.090.025cd">sasainyaṃ bharataṃ hatvā bhaviṣyāmi na saṃśayaḥ</li>
    </ul>
    <ul id="R_2.091.001">
        <li id="R_2.091.001ab">susaṃrabdhaṃ tu saumitriṃ lakṣmaṇaṃ krodhamūrchitam</li>
        <li id="R_2.091.001cd">rāmas tu parisāntvyātha vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.091.002">
        <li id="R_2.091.002ab">kim atra dhanuṣā kāryam asinā vā sacarmaṇā</li>
        <li id="R_2.091.002cd">maheṣvāse mahāprājñe bharate svayam āgate</li>
    </ul>
    <ul id="R_2.091.003">
        <li id="R_2.091.003ab">prāptakālaṃ yad eṣo 'smān bharato draṣṭum icchati</li>
        <li id="R_2.091.003cd">asmāsu manasāpy eṣa nāhitaṃ kiṃ cid ācaret</li>
    </ul>
    <ul id="R_2.091.004">
        <li id="R_2.091.004ab">vipriyaṃ kṛtapūrvaṃ te bharatena kadā na kim</li>
        <li id="R_2.091.004cd">īdṛśaṃ vā bhayaṃ te 'dya bharataṃ yo 'tra śaṅkase</li>
    </ul>
    <ul id="R_2.091.005">
        <li id="R_2.091.005ab">na hi te niṣṭhuraṃ vācyo bharato nāpriyaṃ vacaḥ</li>
        <li id="R_2.091.005cd">ahaṃ hy apriyam uktaḥ syāṃ bharatasyāpriye kṛte</li>
    </ul>
    <ul id="R_2.091.006">
        <li id="R_2.091.006ab">kathaṃ nu putrāḥ pitaraṃ hanyuḥ kasyāṃ cid āpadi</li>
        <li id="R_2.091.006cd">bhrātā vā bhrātaraṃ hanyāt saumitre prāṇam ātmanaḥ</li>
    </ul>
    <ul id="R_2.091.007">
        <li id="R_2.091.007ab">yadi rājyasya hetos tvam imāṃ vācaṃ prabhāṣase</li>
        <li id="R_2.091.007cd">vakṣyāmi bharataṃ dṛṣṭvā rājyam asmai pradīyatām</li>
    </ul>
    <ul id="R_2.091.008">
        <li id="R_2.091.008ab">ucyamāno hi bharato mayā lakṣmaṇa tattvataḥ</li>
        <li id="R_2.091.008cd">rājyam asmai prayaccheti bāḍham ity eva vakṣyati</li>
    </ul>
    <ul id="R_2.091.009">
        <li id="R_2.091.009ab">tathokto dharmaśīlena bhrātrā tasya hite rataḥ</li>
        <li id="R_2.091.009cd">lakṣmaṇaḥ praviveśeva svāni gātrāṇi lajjayā</li>
    </ul>
    <ul id="R_2.091.010">
        <li id="R_2.091.010ab">vrīḍitaṃ lakṣmaṇaṃ dṛṣṭvā rāghavaḥ pratyuvāca ha</li>
        <li id="R_2.091.010cd">eṣa manye mahābāhur ihāsmān draṣṭum āgataḥ</li>
    </ul>
    <ul id="R_2.091.011">
        <li id="R_2.091.011ab">vanavāsam anudhyāya gṛhāya pratineṣyati</li>
        <li id="R_2.091.011cd">imāṃ vāpy eśa vaidehīm atyantasukhasevinīm</li>
    </ul>
    <ul id="R_2.091.012">
        <li id="R_2.091.012ab">etau tau saṃprakāśete gotravantau manoramau</li>
        <li id="R_2.091.012cd">vāyuvegasamau vīra javanau turagottamau</li>
    </ul>
    <ul id="R_2.091.013">
        <li id="R_2.091.013ab">sa eṣa sumahākāyaḥ kampate vāhinīmukhe</li>
        <li id="R_2.091.013cd">nāgaḥ śatruṃjayo nāma vṛddhas tātasya dhīmataḥ</li>
    </ul>
    <ul id="R_2.091.014">
        <li id="R_2.091.014ab">avatīrya tu sālāgrāt tasmāt sa samitiṃjayaḥ</li>
        <li id="R_2.091.014cd">lakṣmaṇaḥ prāñjalir bhūtvā tasthau rāmasya pārśvataḥ</li>
    </ul>
    <ul id="R_2.091.015">
        <li id="R_2.091.015ab">bharatenātha saṃdiṣṭā saṃmardo na bhaved iti</li>
        <li id="R_2.091.015cd">samantāt tasya śailasya senāvāsam akalpayat</li>
    </ul>
    <ul id="R_2.091.016">
        <li id="R_2.091.016ab">adhyardham ikṣvākucamūr yojanaṃ parvatasya sā</li>
        <li id="R_2.091.016cd">pārśve nyaviśad āvṛtya gajavājirathākulā</li>
    </ul>
    <ul id="R_2.091.017">
        <li id="R_2.091.017ab">
            <seg type="pāda" n="a">sā citrakūṭe bharatena senā</seg>
            <seg type="pāda" n="b">dharmaṃ puraskṛtya vidhūya darpam</seg>
        </li>
        <li id="R_2.091.017cd">
            <seg type="pāda" n="c">prasādanārthaṃ raghunandanasya</seg>
            <seg type="pāda" n="d">virocate nītimatā praṇītā</seg>
        </li>
    </ul>
    <ul id="R_2.092.001">
        <li id="R_2.092.001ab">niveśya senāṃ tu vibhuḥ padbhyāṃ pādavatāṃ varaḥ</li>
        <li id="R_2.092.001cd">abhigantuṃ sa kākutstham iyeṣa guruvartakam</li>
    </ul>
    <ul id="R_2.092.002">
        <li id="R_2.092.002ab">niviṣṭa mātre sainye tu yathoddeśaṃ vinītavat</li>
        <li id="R_2.092.002cd">bharato bhrātaraṃ vākyaṃ śatrughnam idam abravīt</li>
    </ul>
    <ul id="R_2.092.003">
        <li id="R_2.092.003ab">kṣipraṃ vanam idaṃ saumya narasaṃghaiḥ samantataḥ</li>
        <li id="R_2.092.003cd">lubdhaiś ca sahitair ebhis tvam anveṣitum arhasi</li>
    </ul>
    <ul id="R_2.092.004">
        <li id="R_2.092.004ab">yāvan na rāmaṃ drakṣyāmi lakṣmaṇaṃ vā mahābalam</li>
        <li id="R_2.092.004cd">vaidehīṃ vā mahābhāgāṃ na me śāntir bhaviṣyati</li>
    </ul>
    <ul id="R_2.092.005">
        <li id="R_2.092.005ab">yāvan na candrasaṃkāśaṃ drakṣyāmi śubham ānanam</li>
        <li id="R_2.092.005cd">bhrātuḥ padmapalāśākṣaṃ na me śāntir bhaviṣyati</li>
    </ul>
    <ul id="R_2.092.006">
        <li id="R_2.092.006ab">yāvan na caraṇau bhrātuḥ pārthiva vyañjanānvitau</li>
        <li id="R_2.092.006cd">śirasā dhārayiṣyāmi na me śāntir bhaviṣyati</li>
    </ul>
    <ul id="R_2.092.007">
        <li id="R_2.092.007ab">yāvan na rājye rājyārhaḥ pitṛpaitāmahe sthitaḥ</li>
        <li id="R_2.092.007cd">abhiṣekajalaklinno na me śāntir bhaviṣyati</li>
    </ul>
    <ul id="R_2.092.008">
        <li id="R_2.092.008ab">kṛtakṛtyā mahābhāgā vaidehī janakātmajā</li>
        <li id="R_2.092.008cd">bhartāraṃ sāgarāntāyāḥ pṛthivyā yānugacchati</li>
    </ul>
    <ul id="R_2.092.009">
        <li id="R_2.092.009ab">subhagaś citrakūṭo 'sau girirājopamo giriḥ</li>
        <li id="R_2.092.009cd">yasmin vasati kākutsthaḥ kubera ivanandane</li>
    </ul>
    <ul id="R_2.092.010">
        <li id="R_2.092.010ab">kṛtakāryam idaṃ durgaṃ vanaṃ vyālaniṣevitam</li>
        <li id="R_2.092.010cd">yad adhyāste mahātejā rāmaḥ śastrabhṛtāṃ varaḥ</li>
    </ul>
    <ul id="R_2.092.011">
        <li id="R_2.092.011ab">evam uktvā mahātejā bharataḥ puruṣarṣabhaḥ</li>
        <li id="R_2.092.011cd">padbhyām eva mahātejāḥ praviveśa mahad vanam</li>
    </ul>
    <ul id="R_2.092.012">
        <li id="R_2.092.012ab">sa tāni drumajālāni jātāni girisānuṣu</li>
        <li id="R_2.092.012cd">puṣpitāgrāṇi madhyena jagāma vadatāṃ varaḥ</li>
    </ul>
    <ul id="R_2.092.013">
        <li id="R_2.092.013ab">sa gireś citrakūṭasya sālam āsādya puṣpitam</li>
        <li id="R_2.092.013cd">rāmāśramagatasyāgner dadarśa dhvajam ucchritam</li>
    </ul>
    <ul id="R_2.092.014">
        <li id="R_2.092.014ab">taṃ dṛṣṭvā bharataḥ śrīmān mumoda sahabāndhavaḥ</li>
        <li id="R_2.092.014cd">atra rāma iti jñātvā gataḥ pāram ivāmbhasaḥ</li>
    </ul>
    <ul id="R_2.092.015">
        <li id="R_2.092.015ab">
            <seg type="pāda" n="a">sa citrakūṭe tu girau niśāmya</seg>
            <seg type="pāda" n="b">rāmāśramaṃ puṇyajanopapannam</seg>
        </li>
        <li id="R_2.092.015cd">
            <seg type="pāda" n="c">guhena sārdhaṃ tvarito jagāma</seg>
            <seg type="pāda" n="d">punar niveśyaiva camūṃ mahātmā</seg>
        </li>
    </ul>
    <ul id="R_2.093.001">
        <li id="R_2.093.001ab">niviṣṭāyāṃ tu senāyām utsuko bharatas tadā</li>
        <li id="R_2.093.001cd">jagāma bhrātaraṃ draṣṭuṃ śatrughnam anudarśayan</li>
    </ul>
    <ul id="R_2.093.002">
        <li id="R_2.093.002ab">ṛṣiṃ vasiṣṭhaṃ saṃdiśya mātṝr me śīghram ānaya</li>
        <li id="R_2.093.002cd">iti taritam agre sa jāgama guruvatsalaḥ</li>
    </ul>
    <ul id="R_2.093.003">
        <li id="R_2.093.003ab">sumantras tv api śatughnam adūrād anvapadyata</li>
        <li id="R_2.093.003cd">rāmadārśanajas tarṣo bharatasyeva tasya ca</li>
    </ul>
    <ul id="R_2.093.004">
        <li id="R_2.093.004ab">gacchann evātha bharatas tāpasālayasaṃsthitām</li>
        <li id="R_2.093.004cd">bhrātuḥ parṇakuṭīṃ śrīmān uṭajaṃ ca dadarśa ha</li>
    </ul>
    <ul id="R_2.093.005">
        <li id="R_2.093.005ab">śālāyās tv agratas tasyā dadarśa bharatas tadā</li>
        <li id="R_2.093.005cd">kāṣṭāni cāvabhagnāni puṣpāṇy avacitāni ca</li>
    </ul>
    <ul id="R_2.093.006">
        <li id="R_2.093.006ab">dadarśa ca vane tasmin mahataḥ saṃcayān kṛtān</li>
        <li id="R_2.093.006cd">mṛgāṇāṃ mahiṣāṇāṃ ca karīṣaiḥ śītakāraṇāt</li>
    </ul>
    <ul id="R_2.093.007">
        <li id="R_2.093.007ab">gacchan eva mahābāhur dyutimān bharatas tadā</li>
        <li id="R_2.093.007cd">śatrughnaṃ cābravīd dhṛṣṭas tān amātyāṃś ca sarvaśaḥ</li>
    </ul>
    <ul id="R_2.093.008">
        <li id="R_2.093.008ab">manye prāptāḥ sma taṃ deśaṃ bharadvājo yam abravīt</li>
        <li id="R_2.093.008cd">nātidūre hi manye 'haṃ nadīṃ mandākinīm itaḥ</li>
    </ul>
    <ul id="R_2.093.009">
        <li id="R_2.093.009ab">uccair baddhāni cīrāṇi lakṣmaṇena bhaved ayam</li>
        <li id="R_2.093.009cd">abhijñānakṛtaḥ panthā vikāle gantum icchatā</li>
    </ul>
    <ul id="R_2.093.010">
        <li id="R_2.093.010ab">idaṃ codāttadantānāṃ kuñjarāṇāṃ tarasvinām</li>
        <li id="R_2.093.010cd">śailapārśve parikrāntam anyonyam abhigarjatām</li>
    </ul>
    <ul id="R_2.093.011">
        <li id="R_2.093.011ab">yam evādhātum icchanti tāpasāḥ satataṃ vane</li>
        <li id="R_2.093.011cd">tasyāsau dṛśyate dhūmaḥ saṃkulaḥ kṛṣṭavartmanaḥ</li>
    </ul>
    <ul id="R_2.093.012">
        <li id="R_2.093.012ab">atrāhaṃ puruṣavyāghraṃ gurusatkārakāriṇam</li>
        <li id="R_2.093.012cd">āryaṃ drakṣyāmi saṃhṛṣṭo maharṣim iva rāghavam</li>
    </ul>
    <ul id="R_2.093.013">
        <li id="R_2.093.013ab">atha gatvā muhūrtaṃ tu citrakūṭaṃ sa rāghavaḥ</li>
        <li id="R_2.093.013cd">mandākinīm anuprāptas taṃ janaṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.093.014">
        <li id="R_2.093.014ab">jagatyāṃ puruṣavyāghra āste vīrāsane rataḥ</li>
        <li id="R_2.093.014cd">janendro nirjanaṃ prāpya dhin me janma sajīvitam</li>
    </ul>
    <ul id="R_2.093.015">
        <li id="R_2.093.015ab">matkṛte vyasanaṃ prāpto lokanātho mahādyutiḥ</li>
        <li id="R_2.093.015cd">sarān kāmān parityajya vane vasati rāghavaḥ</li>
    </ul>
    <ul id="R_2.093.016">
        <li id="R_2.093.016ab">iti lokasamākruṣṭaḥ pādeṣv adya prasādayan</li>
        <li id="R_2.093.016cd">rāmasya nipatiṣyāmi sītāyāś ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_2.093.017">
        <li id="R_2.093.017ab">evaṃ sa vilapaṃs tasmin vane daśarathātmajaḥ</li>
        <li id="R_2.093.017cd">dadarśa mahatīṃ puṇyāṃ parṇaśālāṃ manoramām</li>
    </ul>
    <ul id="R_2.093.018">
        <li id="R_2.093.018ab">sālatālāśvakarṇānāṃ parṇair bahubhir āvṛtām</li>
        <li id="R_2.093.018cd">viśālāṃ mṛdubhis tīrṇāṃ kuśair vedim ivādhvare</li>
    </ul>
    <ul id="R_2.093.019">
        <li id="R_2.093.019ab">śakrāyudha nikāśaiś ca kārmukair bhārasādhanaiḥ</li>
        <li id="R_2.093.019cd">rukmapṛṣṭhair mahāsāraiḥ śobhitāṃ śatrubādhakaiḥ</li>
    </ul>
    <ul id="R_2.093.020">
        <li id="R_2.093.020ab">arkaraśmipratīkāśair ghorais tūṇīgataiḥ śaraiḥ</li>
        <li id="R_2.093.020cd">śobhitāṃ dīptavadanaiḥ sarpair bhogavatīm iva</li>
    </ul>
    <ul id="R_2.093.021">
        <li id="R_2.093.021ab">mahārajatavāsobhyām asibhyāṃ ca virājitām</li>
        <li id="R_2.093.021cd">rukmabinduvicitrābhyāṃ carmabhyāṃ cāpi śobhitām</li>
    </ul>
    <ul id="R_2.093.022">
        <li id="R_2.093.022ab">godhāṅgulitrair āsāktaiś citraiḥ kāñcanabhūṣitaiḥ</li>
        <li id="R_2.093.022cd">arisaṃghair anādhṛṣyāṃ mṛgaiḥ siṃhaguhām iva</li>
    </ul>
    <ul id="R_2.093.023">
        <li id="R_2.093.023ab">prāgudaksravaṇāṃ vediṃ viśālāṃ dīptapāvakām</li>
        <li id="R_2.093.023cd">dadarśa bharatas tatra puṇyāṃ rāmaniveśane</li>
    </ul>
    <ul id="R_2.093.024">
        <li id="R_2.093.024ab">nirīkṣya sa muhūrtaṃ tu dadarśa bharato gurum</li>
        <li id="R_2.093.024cd">uṭaje rāmam āsīnāṃ jaṭāmaṇḍaladhāriṇam</li>
    </ul>
    <ul id="R_2.093.025">
        <li id="R_2.093.025ab">taṃ tu kṛṣṇājinadharaṃ cīravalkalavāsasaṃ</li>
        <li id="R_2.093.025cd">dadarśa rāmam āsīnam abhitaḥ pāvakopamam</li>
    </ul>
    <ul id="R_2.093.026">
        <li id="R_2.093.026ab">siṃhaskandhaṃ mahābāhuṃ puṇḍarīkanibhekṣaṇam</li>
        <li id="R_2.093.026cd">pṛthivyāḥ sagarāntāyā bhartāraṃ dharmacāriṇam</li>
    </ul>
    <ul id="R_2.093.027">
        <li id="R_2.093.027ab">upaviṣṭaṃ mahābāhuṃ brahmāṇam iva śāśvatam</li>
        <li id="R_2.093.027cd">sthaṇḍile darbhasasmtīrṇe sītayā lakṣmaṇena ca</li>
    </ul>
    <ul id="R_2.093.028">
        <li id="R_2.093.028ab">taṃ dṛṣṭvā bharataḥ śrīmān duḥkhamohapariplutaḥ</li>
        <li id="R_2.093.028cd">abhyadhāvata dharmātmā bharataḥ kaikayīsutaḥ</li>
    </ul>
    <ul id="R_2.093.029">
        <li id="R_2.093.029ab">dṛṣṭvā ca vilalāpārto bāṣpasaṃdigdhayā girā</li>
        <li id="R_2.093.029cd">aśaknuvan dhārayituṃ dhairyād vacanam abravīt</li>
    </ul>
    <ul id="R_2.093.030">
        <li id="R_2.093.030ab">yaḥ saṃsadi prakṛtibhir bhaved yukta upāsitum</li>
        <li id="R_2.093.030cd">vanyair mṛgair upāsīnaḥ so 'yam āste mamāgrajaḥ</li>
    </ul>
    <ul id="R_2.093.031">
        <li id="R_2.093.031ab">vāsobhir bahusāhasrair yo mahātmā purocitaḥ</li>
        <li id="R_2.093.031cd">mṛgājine so 'yam iha pravaste dharmam ācaran</li>
    </ul>
    <ul id="R_2.093.032">
        <li id="R_2.093.032ab">adhārayad yo vividhāś citrāḥ sumanasas tadā</li>
        <li id="R_2.093.032cd">so 'yaṃ jaṭābhāram imaṃ sahate rāghavaḥ katham</li>
    </ul>
    <ul id="R_2.093.033">
        <li id="R_2.093.033ab">yasya yajñair yathādiṣṭair yukto dharmasya saṃcayaḥ</li>
        <li id="R_2.093.033cd">śarīra kleśasaṃbhūtaṃ sa dharmaṃ parimārgate</li>
    </ul>
    <ul id="R_2.093.034">
        <li id="R_2.093.034ab">candanena mahārheṇa yasyāṅgam upasevitam</li>
        <li id="R_2.093.034cd">malena tasyāṅgam idaṃ katham āryasya sevyate</li>
    </ul>
    <ul id="R_2.093.035">
        <li id="R_2.093.035ab">mannimittam idaṃ duḥkhaṃ prāpto rāmaḥ sukhocitaḥ</li>
        <li id="R_2.093.035cd">dhig jīvitaṃ nṛśaṃsasya mama lokavigarhitam</li>
    </ul>
    <ul id="R_2.093.036">
        <li id="R_2.093.036ab">ity evaṃ vilapan dīnaḥ prasvinnamukhapaṅkajaḥ</li>
        <li id="R_2.093.036cd">pādāv aprāpya rāmasya papāta bharato rudan</li>
    </ul>
    <ul id="R_2.093.037">
        <li id="R_2.093.037ab">duḥkhābhitapto bharato rājaputro mahābalaḥ</li>
        <li id="R_2.093.037cd">uktvāryeti sakṛd dīnaṃ punar novāca kiṃ cana</li>
    </ul>
    <ul id="R_2.093.038">
        <li id="R_2.093.038ab">bāṣpāpihita kaṇṭhaś ca prekṣya rāmaṃ yaśasvinam</li>
        <li id="R_2.093.038cd">āryety evābhisaṃkruśya vyāhartuṃ nāśakat tataḥ</li>
    </ul>
    <ul id="R_2.093.039">
        <li id="R_2.093.039ab">śatrughnaś cāpi rāmasya vavande caraṇau rudan</li>
        <li id="R_2.093.039cd">tāv ubhau sa samāliṅgya rāmo 'py aśrūṇy avartayat</li>
    </ul>
    <ul id="R_2.093.040">
        <li id="R_2.093.040ab">
            <seg type="pāda" n="a">tataḥ sumantreṇa guhena caiva</seg>
            <seg type="pāda" n="b">samīyatū rājasutāv araṇye</seg>
        </li>
        <li id="R_2.093.040cd">
            <seg type="pāda" n="c">divākaraś caiva niśākaraś ca</seg>
            <seg type="pāda" n="d">yathāmbare śukrabṛhaspatibhyām</seg>
        </li>
    </ul>
    <ul id="R_2.093.041">
        <li id="R_2.093.041ab">
            <seg type="pāda" n="a">tān pārthivān vāraṇayūthapābhān</seg>
            <seg type="pāda" n="b">samāgatāṃs tatra mahaty araṇye</seg>
        </li>
        <li id="R_2.093.041cd">
            <seg type="pāda" n="c">vanaukasas te 'pi samīkṣya sarve 'py</seg>
            <seg type="pāda" n="d">aśrūṇy amuñcan pravihāya harṣam</seg>
        </li>
    </ul>
    <ul id="R_2.094.001">
        <li id="R_2.094.001ab">āghrāya rāmas taṃ mūrdhni pariṣvajya ca rāghavaḥ</li>
        <li id="R_2.094.001cd">aṅke bharatam āropya paryapṛcchat samāhitaḥ</li>
    </ul>
    <ul id="R_2.094.002">
        <li id="R_2.094.002ab">kva nu te 'bhūt pitā tāta yad araṇyaṃ tvam āgataḥ</li>
        <li id="R_2.094.002cd">na hi tvaṃ jīvatas tasya vanam āgantum arhasi</li>
    </ul>
    <ul id="R_2.094.003">
        <li id="R_2.094.003ab">cirasya bata paśyāmi dūrād bharatam āgatam</li>
        <li id="R_2.094.003cd">duṣpratīkam araṇye 'smin kiṃ tāta vanam āgataḥ</li>
    </ul>
    <ul id="R_2.094.004">
        <li id="R_2.094.004ab">kaccid daśaratho rājā kuśalī satyasaṃgaraḥ</li>
        <li id="R_2.094.004cd">rājasūyāśvamedhānām āhartā dharmaniścayaḥ</li>
    </ul>
    <ul id="R_2.094.005">
        <li id="R_2.094.005ab">sa kaccid brāhmaṇo vidvān dharmanityo mahādyutiḥ</li>
        <li id="R_2.094.005cd">ikṣvākūṇām upādhyāyo yathāvat tāta pūjyate</li>
    </ul>
    <ul id="R_2.094.006">
        <li id="R_2.094.006ab">tāta kaccic ca kausalyā sumitrā ca prajāvatī</li>
        <li id="R_2.094.006cd">sukhinī kaccid āryā ca devī nandati kaikayī</li>
    </ul>
    <ul id="R_2.094.007">
        <li id="R_2.094.007ab">kaccid vinaya saṃpannaḥ kulaputro bahuśrutaḥ</li>
        <li id="R_2.094.007cd">anasūyur anudraṣṭā satkṛtas te purohitaḥ</li>
    </ul>
    <ul id="R_2.094.008">
        <li id="R_2.094.008ab">kaccid agniṣu te yukto vidhijño matimān ṛjuḥ</li>
        <li id="R_2.094.008cd">hutaṃ ca hoṣyamāṇaṃ ca kāle vedayate sadā</li>
    </ul>
    <ul id="R_2.094.009">
        <li id="R_2.094.009ab">iṣvastravarasaṃpannam arthaśāstraviśāradam</li>
        <li id="R_2.094.009cd">sudhanvānam upādhyāyaṃ kaccit tvaṃ tāta manyase</li>
    </ul>
    <ul id="R_2.094.010">
        <li id="R_2.094.010ab">kaccid ātma samāḥ śūrāḥ śrutavanto jitendriyāḥ</li>
        <li id="R_2.094.010cd">kulīnāś ceṅgitajñāś ca kṛtās te tāta mantriṇaḥ</li>
    </ul>
    <ul id="R_2.094.011">
        <li id="R_2.094.011ab">mantro vijayamūlaṃ hi rājñāṃ bhavati rāghava</li>
        <li id="R_2.094.011cd">susaṃvṛto mantradharair amātyaiḥ śāstrakovidaiḥ</li>
    </ul>
    <ul id="R_2.094.012">
        <li id="R_2.094.012ab">kaccin nidrāvaśaṃ naiṣi kaccit kāle vibudhyase</li>
        <li id="R_2.094.012cd">kac ciṃś cāpararātriṣu cintayasy arthanaipuṇam</li>
    </ul>
    <ul id="R_2.094.013">
        <li id="R_2.094.013ab">kaccin mantrayase naikaḥ kaccin na bahubhiḥ saha</li>
        <li id="R_2.094.013cd">kaccit te mantrito mantro rāṣṭraṃ na paridhāvati</li>
    </ul>
    <ul id="R_2.094.014">
        <li id="R_2.094.014ab">kaccid arthaṃ viniścitya laghumūlaṃ mahodayam</li>
        <li id="R_2.094.014cd">kṣipram ārabhase kartuṃ na dīrghayasi rāghava</li>
    </ul>
    <ul id="R_2.094.015">
        <li id="R_2.094.015ab">kaccit tu sukṛtāny eva kṛtarūpāṇi vā punaḥ</li>
        <li id="R_2.094.015cd">vidus te sarvakāryāṇi na kartavyāni pārthivāḥ</li>
    </ul>
    <ul id="R_2.094.016">
        <li id="R_2.094.016ab">kaccin na tarkair yuktvā vā ye cāpy aparikīrtitāḥ</li>
        <li id="R_2.094.016cd">tvayā vā tava vāmātyair budhyate tāta mantritam</li>
    </ul>
    <ul id="R_2.094.017">
        <li id="R_2.094.017ab">kaccit sahasrān mūrkhāṇām ekam icchasi paṇḍitam</li>
        <li id="R_2.094.017cd">paṇḍito hy arthakṛcchreṣu kuryān niḥśreyasaṃ mahat</li>
    </ul>
    <ul id="R_2.094.018">
        <li id="R_2.094.018ab">sahasrāṇy api mūrkhāṇāṃ yady upāste mahīpatiḥ</li>
        <li id="R_2.094.018cd">atha vāpy ayutāny eva nāsti teṣu sahāyatā</li>
    </ul>
    <ul id="R_2.094.019">
        <li id="R_2.094.019ab">eko 'py amātyo medhāvī śūro dakṣo vicakṣaṇaḥ</li>
        <li id="R_2.094.019cd">rājānaṃ rājamātraṃ vā prāpayen mahatīṃ śriyam</li>
    </ul>
    <ul id="R_2.094.020">
        <li id="R_2.094.020ab">kaccin mukhyā mahatsv eva madhyameṣu ca madhyamāḥ</li>
        <li id="R_2.094.020cd">jaghanyāś ca jaghanyeṣu bhṛtyāḥ karmasu yojitāḥ</li>
    </ul>
    <ul id="R_2.094.021">
        <li id="R_2.094.021ab">amātyān upadhātītān pitṛpaitāmahāñ śucīn</li>
        <li id="R_2.094.021cd">śreṣṭhāñ śreṣṭheṣu kaccit tvaṃ niyojayasi karmasu</li>
    </ul>
    <ul id="R_2.094.022">
        <li id="R_2.094.022ab">kaccit tvāṃ nāvajānanti yājakāḥ patitaṃ yathā</li>
        <li id="R_2.094.022cd">ugrapratigrahītāraṃ kāmayānam iva striyaḥ</li>
    </ul>
    <ul id="R_2.094.023">
        <li id="R_2.094.023ab">upāyakuśalaṃ vaidyaṃ bhṛtyasaṃdūṣaṇe ratam</li>
        <li id="R_2.094.023cd">śūram aiśvaryakāmaṃ ca yo na hanti sa vadhyate</li>
    </ul>
    <ul id="R_2.094.024">
        <li id="R_2.094.024ab">kaccid dhṛṣṭaś ca śūraś ca dhṛtimān matimāñ śuciḥ</li>
        <li id="R_2.094.024cd">kulīnaś cānuraktaś ca dakṣaḥ senāpatiḥ kṛtaḥ</li>
    </ul>
    <ul id="R_2.094.025">
        <li id="R_2.094.025ab">balavantaś ca kaccit te mukhyā yuddhaviśāradāḥ</li>
        <li id="R_2.094.025cd">dṛṣṭāpadānā vikrāntās tvayā satkṛtya mānitāḥ</li>
    </ul>
    <ul id="R_2.094.026">
        <li id="R_2.094.026ab">ka cid balasya bhaktaṃ ca vetanaṃ ca yathocitam</li>
        <li id="R_2.094.026cd">saṃprāptakālaṃ dātavyaṃ dadāsi na vilambase</li>
    </ul>
    <ul id="R_2.094.027">
        <li id="R_2.094.027ab">kālātikramaṇe hy eva bhakta vetanayor bhṛtāḥ</li>
        <li id="R_2.094.027cd">bhartuḥ kupyanti duṣyanti so 'narthaḥ sumahān smṛtaḥ</li>
    </ul>
    <ul id="R_2.094.028">
        <li id="R_2.094.028ab">kaccit sarve 'nuraktās tvāṃ kulaputrāḥ pradhānataḥ</li>
        <li id="R_2.094.028cd">kaccit prāṇāṃs tavārtheṣu saṃtyajanti samāhitāḥ</li>
    </ul>
    <ul id="R_2.094.029">
        <li id="R_2.094.029ab">kaccij jānapado vidvān dakṣiṇaḥ pratibhānavān</li>
        <li id="R_2.094.029cd">yathoktavādī dūtas te kṛto bharata paṇḍitaḥ</li>
    </ul>
    <ul id="R_2.094.030">
        <li id="R_2.094.030ab">kaccid aṣṭādaśāny eṣu svapakṣe daśa pañca ca</li>
        <li id="R_2.094.030cd">tribhis tribhir avijñātair vetsi tīrthāni cārakaiḥ</li>
    </ul>
    <ul id="R_2.094.031">
        <li id="R_2.094.031ab">kaccid vyapāstān ahitān pratiyātāṃś ca sarvadā</li>
        <li id="R_2.094.031cd">durbalān anavajñāya vartase ripusūdana</li>
    </ul>
    <ul id="R_2.094.032">
        <li id="R_2.094.032ab">kaccin na lokāyatikān brāhmaṇāṃs tāta sevase</li>
        <li id="R_2.094.032cd">anartha kuśalā hy ete bālāḥ paṇḍitamāninaḥ</li>
    </ul>
    <ul id="R_2.094.033">
        <li id="R_2.094.033ab">dharmaśāstreṣu mukhyeṣu vidyamāneṣu durbudhāḥ</li>
        <li id="R_2.094.033cd">buddhimān vīkṣikīṃ prāpya nirarthaṃ pravadanti te</li>
    </ul>
    <ul id="R_2.094.034">
        <li id="R_2.094.034ab">vīrair adhyuṣitāṃ pūrvam asmākaṃ tāta pūrvakaiḥ</li>
        <li id="R_2.094.034cd">satyanāmāṃ dṛḍhadvārāṃ hastyaśvarathasaṃkulām</li>
    </ul>
    <ul id="R_2.094.035">
        <li id="R_2.094.035ab">brāhmaṇaiḥ kṣatriyair vaiśyaiḥ svakarmanirataiḥ sadā</li>
        <li id="R_2.094.035cd">jitendriyair mahotsāhair vṛtāmātyaiḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_2.094.036">
        <li id="R_2.094.036ab">prāsādair vividhākārair vṛtāṃ vaidyajanākulām</li>
        <li id="R_2.094.036cd">kaccit samuditāṃ sphītām ayodhyāṃ parirakṣasi</li>
    </ul>
    <ul id="R_2.094.037">
        <li id="R_2.094.037ab">kaccic caityaśatair juṣṭaḥ suniviṣṭajanākulaḥ</li>
        <li id="R_2.094.037cd">devasthānaiḥ prapābhiś ca taḍāgaiś copaśobhitaḥ</li>
    </ul>
    <ul id="R_2.094.038">
        <li id="R_2.094.038ab">prahṛṣṭanaranārīkaḥ samājotsavaśobhitaḥ</li>
        <li id="R_2.094.038cd">sukṛṣṭasīmā paśumān hiṃsābhir abhivarjitaḥ</li>
    </ul>
    <ul id="R_2.094.039">
        <li id="R_2.094.039ab">adevamātṛko ramyaḥ śvāpadaiḥ parivarjitaḥ</li>
        <li id="R_2.094.039cd">kaccij janapadaḥ sphītaḥ sukhaṃ vasati rāghava</li>
    </ul>
    <ul id="R_2.094.040">
        <li id="R_2.094.040ab">kaccit te dayitāḥ sarve kṛṣigorakṣajīvinaḥ</li>
        <li id="R_2.094.040cd">vārtāyāṃ saṃśritas tāta loko hi sukham edhate</li>
    </ul>
    <ul id="R_2.094.041">
        <li id="R_2.094.041ab">teṣāṃ guptiparīhāraiḥ kaccit te bharaṇaṃ kṛtam</li>
        <li id="R_2.094.041cd">rakṣyā hi rājñā dharmeṇa sarve viṣayavāsinaḥ</li>
    </ul>
    <ul id="R_2.094.042">
        <li id="R_2.094.042ab">kaccit striyaḥ sāntvayasi kaccit tāś ca surakṣitāḥ</li>
        <li id="R_2.094.042cd">kaccin na śraddadhāsyāsāṃ kaccid guhyaṃ na bhāṣase</li>
    </ul>
    <ul id="R_2.094.043">
        <li id="R_2.094.043ab">kaccin nāgavanaṃ guptaṃ kuñjarāṇāṃ ca tṛpyasi</li>
        <li id="R_2.094.043cd">kaccid darśayase nityaṃ manuṣyāṇāṃ vibhūṣitam</li>
        <li id="R_2.094.043ef">utthāyotthāya pūrvāhṇe rājaputro mahāpathe</li>
    </ul>
    <ul id="R_2.094.044">
        <li id="R_2.094.044ab">kaccit sarvāṇi durgāṇi dhanadhānyāyudhodakaiḥ</li>
        <li id="R_2.094.044cd">yantraiś ca paripūrṇāni tathā śilpidhanurdharaiḥ</li>
    </ul>
    <ul id="R_2.094.045">
        <li id="R_2.094.045ab">āyas te vipulaḥ kaccit kaccid alpataro vyayaḥ</li>
        <li id="R_2.094.045cd">apātreṣu na te kaccit kośo gacchati rāghava</li>
    </ul>
    <ul id="R_2.094.046">
        <li id="R_2.094.046ab">devatārthe ca pitrarthe brāhmaṇābhyāgateṣu ca</li>
        <li id="R_2.094.046cd">yodheṣu mitravargeṣu kaccid gacchati te vyayaḥ</li>
    </ul>
    <ul id="R_2.094.047">
        <li id="R_2.094.047ab">kaccid āryo viśuddhātmā kṣāritaś corakarmaṇā</li>
        <li id="R_2.094.047cd">apṛṣṭaḥ śāstrakuśalair na lobhād badhyate śuciḥ</li>
    </ul>
    <ul id="R_2.094.048">
        <li id="R_2.094.048ab">gṛhītaś caiva pṛṣṭaś ca kāle dṛṣṭaḥ sakāraṇaḥ</li>
        <li id="R_2.094.048cd">kaccin na mucyate coro dhanalobhān nararṣabha</li>
    </ul>
    <ul id="R_2.094.049">
        <li id="R_2.094.049ab">vyasane kaccid āḍhyasya dugatasya ca rāghava</li>
        <li id="R_2.094.049cd">arthaṃ virāgāḥ paśyanti tavāmātyā bahuśrutāḥ</li>
    </ul>
    <ul id="R_2.094.050">
        <li id="R_2.094.050ab">yāni mithyābhiśastānāṃ patanty asrāṇi rāghava</li>
        <li id="R_2.094.050cd">tāni putrapaśūn ghnanti prītyartham anuśāsataḥ</li>
    </ul>
    <ul id="R_2.094.051">
        <li id="R_2.094.051ab">kaccid vṛdhāṃś ca bālāṃś ca vaidyamukhyāṃś ca rāghava</li>
        <li id="R_2.094.051cd">dānena manasā vācā tribhir etair bubhūṣase</li>
    </ul>
    <ul id="R_2.094.052">
        <li id="R_2.094.052ab">kaccid gurūṃś ca vṛddhāṃś ca tāpasān devatātithīn</li>
        <li id="R_2.094.052cd">caityāṃś ca sarvān siddhārthān brāhmaṇāṃś ca namasyasi</li>
    </ul>
    <ul id="R_2.094.053">
        <li id="R_2.094.053ab">kaccid arthena vā dharmaṃ dharmaṃ dharmeṇa vā punaḥ</li>
        <li id="R_2.094.053cd">ubhau vā prītilobhena kāmena na vibādhase</li>
    </ul>
    <ul id="R_2.094.054">
        <li id="R_2.094.054ab">kaccid arthaṃ ca dharmaṃ ca kāmaṃ ca jayatāṃ vara</li>
        <li id="R_2.094.054cd">vibhajya kāle kālajña sarvān bharata sevase</li>
    </ul>
    <ul id="R_2.094.055">
        <li id="R_2.094.055ab">kaccit te brāhmaṇāḥ śarma sarvaśāstrārthakovidaḥ</li>
        <li id="R_2.094.055cd">āśaṃsante mahāprājña paurajānapadaiḥ saha</li>
    </ul>
    <ul id="R_2.094.056">
        <li id="R_2.094.056ab">nāstikyam anṛtaṃ krodhaṃ pramādaṃ dīrghasūtratām</li>
        <li id="R_2.094.056cd">adarśanaṃ jñānavatām ālasyaṃ pañcavṛttitām</li>
    </ul>
    <ul id="R_2.094.057">
        <li id="R_2.094.057ab">ekacintanam arthānām anarthajñaiś ca mantraṇam</li>
        <li id="R_2.094.057cd">niścitānām anārambhaṃ mantrasyāparilakṣaṇam</li>
    </ul>
    <ul id="R_2.094.058">
        <li id="R_2.094.058ab">maṅgalasyāprayogaṃ ca pratyutthānaṃ ca sarvaśaḥ</li>
        <li id="R_2.094.058cd">kaccit tvaṃ varjayasy etān rājadoṣāṃś caturdaśa</li>
    </ul>
    <ul id="R_2.094.059">
        <li id="R_2.094.059ab">kaccit svādukṛtaṃ bhojyam eko nāśnāsi rāghava</li>
        <li id="R_2.094.059cd">kaccid āśaṃsamānebhyo mitrebhyaḥ saṃprayacchasi</li>
    </ul>
    <ul id="R_2.095.001">
        <li id="R_2.095.001ab">rāmasya vacanaṃ śrutvā bharataḥ pratyuvāca ha</li>
        <li id="R_2.095.001cd">kiṃ me dharmād vihīnasya rājadharmaḥ kariṣyati</li>
    </ul>
    <ul id="R_2.095.002">
        <li id="R_2.095.002ab">śāśvato 'yaṃ sadā dharmaḥ sthito 'smāsu nararṣabha</li>
        <li id="R_2.095.002cd">jyeṣṭha putre sthite rājan na kanīyān bhaven nṛpaḥ</li>
    </ul>
    <ul id="R_2.095.003">
        <li id="R_2.095.003ab">sa samṛddhāṃ mayā sārdham ayodhyāṃ gaccha rāghava</li>
        <li id="R_2.095.003cd">abhiṣecaya cātmānaṃ kulasyāsya bhavāya naḥ</li>
    </ul>
    <ul id="R_2.095.004">
        <li id="R_2.095.004ab">rājānaṃ mānuṣaṃ prāhur devatve saṃmato mama</li>
        <li id="R_2.095.004cd">yasya dharmārthasahitaṃ vṛttam āhur amānuṣam</li>
    </ul>
    <ul id="R_2.095.005">
        <li id="R_2.095.005ab">kekayasthe ca mayi tu tvayi cāraṇyam āśrite</li>
        <li id="R_2.095.005cd">divam ārya gato rājā yāyajūkaḥ satāṃ mataḥ</li>
    </ul>
    <ul id="R_2.095.006">
        <li id="R_2.095.006ab">uttiṣṭha puruṣavyāghra kriyatām udakaṃ pituḥ</li>
        <li id="R_2.095.006cd">ahaṃ cāyaṃ ca śatrughnaḥ pūrvam eva kṛtodakau</li>
    </ul>
    <ul id="R_2.095.007">
        <li id="R_2.095.007ab">priyeṇa kila dattaṃ hi pitṛlokeṣu rāghava</li>
        <li id="R_2.095.007cd">akṣayyaṃ bhavatīty āhur bhavāṃś caiva pituḥ priyaḥ</li>
    </ul>
    <ul id="R_2.095.008">
        <li id="R_2.095.008ab">tāṃ śrutvā karuṇāṃ vācaṃ pitur maraṇasaṃhitām</li>
        <li id="R_2.095.008cd">rāghavo bharatenoktāṃ babhūva gatacetanaḥ</li>
    </ul>
    <ul id="R_2.095.009">
        <li id="R_2.095.009ab">vāgvajraṃ bharatenoktam amanojñaṃ paraṃtapaḥ</li>
        <li id="R_2.095.009cd">pragṛhya bāhū rāmo vai puṣpitāgro yathā drumaḥ</li>
        <li id="R_2.095.009ef">vane paraśunā kṛttas tathā bhuvi papāta ha</li>
    </ul>
    <ul id="R_2.095.010">
        <li id="R_2.095.010ab">tathā hi patitaṃ rāmaṃ jagatyāṃ jagatīpatim</li>
        <li id="R_2.095.010cd">kūlaghātapariśrāntaṃ prasuptam iva kuñjaram</li>
    </ul>
    <ul id="R_2.095.011">
        <li id="R_2.095.011ab">bhrātaras te maheṣvāsaṃ sarvataḥ śokakarśitam</li>
        <li id="R_2.095.011cd">rudantaḥ saha vaidehyā siṣicuḥ salilena vai</li>
    </ul>
    <ul id="R_2.095.012">
        <li id="R_2.095.012ab">sa tu saṃjñāṃ punar labdhvā netrābhyām āsram utsṛjan</li>
        <li id="R_2.095.012cd">upākrāmata kākutsthaḥ kṛpaṇaṃ bahubhāṣitum</li>
    </ul>
    <ul id="R_2.095.013">
        <li id="R_2.095.013ab">kiṃ nu tasya mayā kāryaṃ durjātena mahātmanā</li>
        <li id="R_2.095.013cd">yo mṛto mama śokena na mayā cāpi saṃskṛtaḥ</li>
    </ul>
    <ul id="R_2.095.014">
        <li id="R_2.095.014ab">aho bharata siddhārtho yena rājā tvayānagha</li>
        <li id="R_2.095.014cd">śatrugheṇa ca sarveṣu pretakṛtyeṣu satkṛtaḥ</li>
    </ul>
    <ul id="R_2.095.015">
        <li id="R_2.095.015ab">niṣpradhānām anekāgraṃ narendreṇa vinākṛtām</li>
        <li id="R_2.095.015cd">nivṛttavanavāso 'pi nāyodhyāṃ gantum utsahe</li>
    </ul>
    <ul id="R_2.095.016">
        <li id="R_2.095.016ab">samāptavanavāsaṃ mām ayodhyāyāṃ paraṃtapa</li>
        <li id="R_2.095.016cd">ko nu śāsiṣyati punas tāte lokāntaraṃ gate</li>
    </ul>
    <ul id="R_2.095.017">
        <li id="R_2.095.017ab">purā prekṣya suvṛttaṃ māṃ pitā yāny āha sāntvayan</li>
        <li id="R_2.095.017cd">vākyāni tāni śroṣyāmi kutaḥ karṇasukhāny aham</li>
    </ul>
    <ul id="R_2.095.018">
        <li id="R_2.095.018ab">evam uktvā sa bharataṃ bhāryām abhyetya rāghavaḥ</li>
        <li id="R_2.095.018cd">uvāca śokasaṃtaptaḥ pūrṇacandranibhānanām</li>
    </ul>
    <ul id="R_2.095.019">
        <li id="R_2.095.019ab">sīte mṛtas te śvaśuraḥ pitrā hīno 'si lakṣmaṇa</li>
        <li id="R_2.095.019cd">bharato duḥkham ācaṣṭe svargataṃ pṛthivīpatim</li>
    </ul>
    <ul id="R_2.095.020">
        <li id="R_2.095.020ab">sāntvayitvā tu tāṃ rāmo rudantīṃ janakātmajām</li>
        <li id="R_2.095.020cd">uvāca lakṣmaṇaṃ tatra duḥkhito duḥkhitaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.095.021">
        <li id="R_2.095.021ab">ānayeṅgudipiṇyākaṃ cīram āhara cottaram</li>
        <li id="R_2.095.021cd">jalakriyārthaṃ tātasya gamiṣyāmi mahātmanaḥ</li>
    </ul>
    <ul id="R_2.095.022">
        <li id="R_2.095.022ab">sītā purastād vrajatu tvam enām abhito vraja</li>
        <li id="R_2.095.022cd">ahaṃ paścād gamiṣyāmi gatir hy eṣā sudāruṇā</li>
    </ul>
    <ul id="R_2.095.023">
        <li id="R_2.095.023ab">tato nityānugas teṣāṃ viditātmā mahāmatiḥ</li>
        <li id="R_2.095.023cd">mṛdur dāntaś ca śāntaś ca rāme ca dṛḍha bhaktimān</li>
    </ul>
    <ul id="R_2.095.024">
        <li id="R_2.095.024ab">sumantras tair nṛpasutaiḥ sārdham āśvāsya rāghavam</li>
        <li id="R_2.095.024cd">avātārayad ālambya nadīṃ mandākinīṃ śivām</li>
    </ul>
    <ul id="R_2.095.025">
        <li id="R_2.095.025ab">te sutīrthāṃ tataḥ kṛcchrād upāgamya yaśasvinaḥ</li>
        <li id="R_2.095.025cd">nadīṃ mandākinīṃ ramyāṃ sadā puṣpitakānanām</li>
    </ul>
    <ul id="R_2.095.026">
        <li id="R_2.095.026ab">śīghrasrotasam āsādya tīrthaṃ śivam akardamam</li>
        <li id="R_2.095.026cd">siṣicus tūdakaṃ rājñe tata etad bhavatv iti</li>
    </ul>
    <ul id="R_2.095.027">
        <li id="R_2.095.027ab">pragṛhya ca mahīpālo jalapūritam añjalim</li>
        <li id="R_2.095.027cd">diśaṃ yāmyām abhimukho rudan vacanam abravīt</li>
    </ul>
    <ul id="R_2.095.028">
        <li id="R_2.095.028ab">etat te rājaśārdūla vimalaṃ toyam akṣayam</li>
        <li id="R_2.095.028cd">pitṛlokagatasyādya maddattam upatiṣṭhatu</li>
    </ul>
    <ul id="R_2.095.029">
        <li id="R_2.095.029ab">tato mandākinī tīrāt pratyuttīrya sa rāghavaḥ</li>
        <li id="R_2.095.029cd">pituś cakāra tejasvī nivāpaṃ bhrātṛbhiḥ saha</li>
    </ul>
    <ul id="R_2.095.030">
        <li id="R_2.095.030ab">aiṅgudaṃ badarīmiśraṃ piṇyākaṃ darbhasaṃstare</li>
        <li id="R_2.095.030cd">nyasya rāmaḥ suduḥkhārto rudan vacanam abravīt</li>
    </ul>
    <ul id="R_2.095.031">
        <li id="R_2.095.031ab">idaṃ bhuṅkṣva mahārājaprīto yad aśanā vayam</li>
        <li id="R_2.095.031cd">yadannaḥ puruṣo bhavati tadannās tasya devatāḥ</li>
    </ul>
    <ul id="R_2.095.032">
        <li id="R_2.095.032ab">tatas tenaiva mārgeṇa pratyuttīrya nadītaṭāt</li>
        <li id="R_2.095.032cd">āruroha naravyāghro ramyasānuṃ mahīdharam</li>
    </ul>
    <ul id="R_2.095.033">
        <li id="R_2.095.033ab">tataḥ parṇakuṭīdvāram āsādya jagatīpatiḥ</li>
        <li id="R_2.095.033cd">parijagrāha pāṇibhyām ubhau bharatalakṣmaṇau</li>
    </ul>
    <ul id="R_2.095.034">
        <li id="R_2.095.034ab">teṣāṃ tu rudatāṃ śabdāt pratiśrutkābhavad girau</li>
        <li id="R_2.095.034cd">bhrātṝṇāṃ saha vaidehyā siṃhānāṃ nardatām iva</li>
    </ul>
    <ul id="R_2.095.035">
        <li id="R_2.095.035ab">vijñāya tumulaṃ śabdaṃ trastā bharatasainikāḥ</li>
        <li id="R_2.095.035cd">abruvaṃś cāpi rāmeṇa bharataḥ saṃgato dhruvam</li>
        <li id="R_2.095.035ef">teṣām eva mahāñ śabdaḥ śocatāṃ pitaraṃ mṛtam</li>
    </ul>
    <ul id="R_2.095.036">
        <li id="R_2.095.036ab">atha vāsān parityajya taṃ sarve 'bhimukhāḥ svanam</li>
        <li id="R_2.095.036cd">apy eka manaso jagmur yathāsthānaṃ pradhāvitāḥ</li>
    </ul>
    <ul id="R_2.095.037">
        <li id="R_2.095.037ab">hayair anye gajair anye rathair anye svalaṃkṛtaiḥ</li>
        <li id="R_2.095.037cd">sukumārās tathaivānye padbhir eva narā yayuḥ</li>
    </ul>
    <ul id="R_2.095.038">
        <li id="R_2.095.038ab">aciraproṣitaṃ rāmaṃ ciraviproṣitaṃ yathā</li>
        <li id="R_2.095.038cd">draṣṭukāmo janaḥ sarvo jagāma sahasāśramam</li>
    </ul>
    <ul id="R_2.095.039">
        <li id="R_2.095.039ab">bhrātṝṇāṃ tvaritās te tu draṣṭukāmāḥ samāgamam</li>
        <li id="R_2.095.039cd">yayur bahuvidhair yānaiḥ khuranemisamākulaiḥ</li>
    </ul>
    <ul id="R_2.095.040">
        <li id="R_2.095.040ab">sā bhūmir bahubhir yānaiḥ khuranemisamāhatā</li>
        <li id="R_2.095.040cd">mumoca tumulaṃ śabdaṃ dyaur ivābhrasamāgame</li>
    </ul>
    <ul id="R_2.095.041">
        <li id="R_2.095.041ab">tena vitrāsitā nāgāḥ kareṇuparivāritāḥ</li>
        <li id="R_2.095.041cd">āvāsayanto gandhena jagmur anyad vanaṃ tataḥ</li>
    </ul>
    <ul id="R_2.095.042">
        <li id="R_2.095.042ab">varāhamṛgasiṃhāś ca mahiṣāḥ sarkṣavānarāḥ</li>
        <li id="R_2.095.042cd">vyāghra gokarṇagavayā vitreṣuḥ pṛṣataiḥ saha</li>
    </ul>
    <ul id="R_2.095.043">
        <li id="R_2.095.043ab">rathāṅgasāhvā natyūhā haṃsāḥ kāraṇḍavāḥ plavāḥ</li>
        <li id="R_2.095.043cd">tathā puṃskokilāḥ krauñcā visaṃjñā bhejire diśaḥ</li>
    </ul>
    <ul id="R_2.095.044">
        <li id="R_2.095.044ab">tena śabdena vitrastair ākāśaṃ pakṣibhir vṛtam</li>
        <li id="R_2.095.044cd">manuṣyair āvṛtā bhūmir ubhayaṃ prababhau tadā</li>
    </ul>
    <ul id="R_2.095.045">
        <li id="R_2.095.045ab">tān narān bāṣpapūrṇākṣān samīkṣyātha suduḥkhitān</li>
        <li id="R_2.095.045cd">paryaṣvajata dharmajñaḥ pitṛvan mātṛvac ca saḥ</li>
    </ul>
    <ul id="R_2.095.046">
        <li id="R_2.095.046ab">
            <seg type="pāda" n="a">sa tatra kāṃś cit pariṣasvaje narān</seg>
            <seg type="pāda" n="b">narāś ca ke cit tu tam abhyavādayan</seg>
        </li>
        <li id="R_2.095.046cd">
            <seg type="pāda" n="c">cakāra sarvān savayasyabāndhavān</seg>
            <seg type="pāda" n="d">yathārham āsādya tadā nṛpātmajaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.095.047">
        <li id="R_2.095.047ab">
            <seg type="pāda" n="a">tataḥ sa teṣāṃ rudatāṃ mahātmanāṃ</seg>
            <seg type="pāda" n="b">bhuvaṃ ca khaṃ cānuvinādayan svanaḥ</seg>
        </li>
        <li id="R_2.095.047cd">
            <seg type="pāda" n="c">guhā girīṇāṃ ca diśaś ca saṃtataṃ</seg>
            <seg type="pāda" n="d">mṛdaṅgaghoṣapratimo viśuśruve</seg>
        </li>
    </ul>
    <ul id="R_2.096.001">
        <li id="R_2.096.001ab">vasiṣṭhaḥ purataḥ kṛtvā dārān daśarathasya ca</li>
        <li id="R_2.096.001cd">abhicakrāma taṃ deśaṃ rāmadarśanatarṣitaḥ</li>
    </ul>
    <ul id="R_2.096.002">
        <li id="R_2.096.002ab">rājapatnyaś ca gacchantyo mandaṃ mandākinīṃ prati</li>
        <li id="R_2.096.002cd">dadṛśus tatra tat tīrthaṃ rāmalakṣmaṇasevitam</li>
    </ul>
    <ul id="R_2.096.003">
        <li id="R_2.096.003ab">kausalyā bāṣpapūrṇena mukhena pariśuṣyatā</li>
        <li id="R_2.096.003cd">sumitrām abravīd dīnā yāś cānyā rājayoṣitaḥ</li>
    </ul>
    <ul id="R_2.096.004">
        <li id="R_2.096.004ab">idaṃ teṣām anāthānāṃ kliṣṭam akliṣṭa karmaṇām</li>
        <li id="R_2.096.004cd">vane prāk kevalaṃ tīrthaṃ ye te nirviṣayī kṛtāḥ</li>
    </ul>
    <ul id="R_2.096.005">
        <li id="R_2.096.005ab">itaḥ sumitre putras te sadā jalam atandritaḥ</li>
        <li id="R_2.096.005cd">svayaṃ harati saumitrir mama putrasya kāraṇāt</li>
    </ul>
    <ul id="R_2.096.006">
        <li id="R_2.096.006ab">dakṣiṇāgreṣu darbheṣu sā dadarśa mahītale</li>
        <li id="R_2.096.006cd">pitur iṅgudipiṇyākaṃ nyastam āyatalocanā</li>
    </ul>
    <ul id="R_2.096.007">
        <li id="R_2.096.007ab">taṃ bhūmau pitur ārtena nyastaṃ rāmeṇa vīkṣya sā</li>
        <li id="R_2.096.007cd">uvāca devī kausalyā sarvā daśarathastriyaḥ</li>
    </ul>
    <ul id="R_2.096.008">
        <li id="R_2.096.008ab">idam ikṣvākunāthasya rāghavasya mahātmanaḥ</li>
        <li id="R_2.096.008cd">rāghaveṇa pitur dattaṃ paśyataitad yathāvidhi</li>
    </ul>
    <ul id="R_2.096.009">
        <li id="R_2.096.009ab">tasya devasamānasya pārthivasya mahātmanaḥ</li>
        <li id="R_2.096.009cd">naitad aupayikaṃ manye bhuktabhogasya bhojanam</li>
    </ul>
    <ul id="R_2.096.010">
        <li id="R_2.096.010ab">caturantāṃ mahīṃ bhuktvā mahendra sadṛśo bhuvi</li>
        <li id="R_2.096.010cd">katham iṅgudipiṇyākaṃ sa bhuṅkte vasudhādhipaḥ</li>
    </ul>
    <ul id="R_2.096.011">
        <li id="R_2.096.011ab">ato duḥkhataraṃ loke na kiṃ cit pratibhāti mā</li>
        <li id="R_2.096.011cd">yatra rāmaḥ pitur dadyād iṅgudīkṣodam ṛddhimān</li>
    </ul>
    <ul id="R_2.096.012">
        <li id="R_2.096.012ab">rāmeṇeṅgudipiṇyākaṃ pitur dattaṃ samīkṣya me</li>
        <li id="R_2.096.012cd">kathaṃ duḥkhena hṛdayaṃ na sphoṭati sahasradhā</li>
    </ul>
    <ul id="R_2.096.013">
        <li id="R_2.096.013ab">evam ārtāṃ sapatnyas tā jagmur āśvāsya tāṃ tadā</li>
        <li id="R_2.096.013cd">dadṛśuś cāśrame rāmaṃ svargāc cyutam ivāmaram</li>
    </ul>
    <ul id="R_2.096.014">
        <li id="R_2.096.014ab">sarvabhogaiḥ parityaktaṃ rāma saṃprekṣya mātaraḥ</li>
        <li id="R_2.096.014cd">ārtā mumucur aśrūṇi sasvaraṃ śokakarśitāḥ</li>
    </ul>
    <ul id="R_2.096.015">
        <li id="R_2.096.015ab">tāsāṃ rāmaḥ samutthāya jagrāha caraṇāñ śubhān</li>
        <li id="R_2.096.015cd">mātṝṇāṃ manujavyāghraḥ sarvāsāṃ satyasaṃgaraḥ</li>
    </ul>
    <ul id="R_2.096.016">
        <li id="R_2.096.016ab">tāḥ pāṇibhiḥ sukhasparśair mṛdvaṅgulitalaiḥ śubhaiḥ</li>
        <li id="R_2.096.016cd">pramamārjū rajaḥ pṛṣṭhād rāmasyāyatalocanāḥ</li>
    </ul>
    <ul id="R_2.096.017">
        <li id="R_2.096.017ab">saumitrir api tāḥ sarvā mātṝḥ saṃprekṣya duḥkhitaḥ</li>
        <li id="R_2.096.017cd">abhyavādayatāsaktaṃ śanai rāmād anantaram</li>
    </ul>
    <ul id="R_2.096.018">
        <li id="R_2.096.018ab">yathā rāme tathā tasmin sarvā vavṛtire striyaḥ</li>
        <li id="R_2.096.018cd">vṛttiṃ daśarathāj jāte lakṣmaṇe śubhalakṣaṇe</li>
    </ul>
    <ul id="R_2.096.019">
        <li id="R_2.096.019ab">sītāpi caraṇāṃs tāsām upasaṃgṛhya duḥkhitā</li>
        <li id="R_2.096.019cd">śvaśrūṇām aśrupūrṇākṣī sā babhūvāgrataḥ sthitā</li>
    </ul>
    <ul id="R_2.096.020">
        <li id="R_2.096.020ab">tāṃ pariṣvajya duḥkhārtāṃ mātā duhitaraṃ yathā</li>
        <li id="R_2.096.020cd">vanavāsakṛśāṃ dīnāṃ kausalyā vākyam abravīt</li>
    </ul>
    <ul id="R_2.096.021">
        <li id="R_2.096.021ab">videharājasya sutā snuṣā daśarathasya ca</li>
        <li id="R_2.096.021cd">rāmapatnī kathaṃ duḥkhaṃ saṃprāptā nirjane vane</li>
    </ul>
    <ul id="R_2.096.022">
        <li id="R_2.096.022ab">padmam ātapasaṃtaptaṃ parikliṣṭam ivotpalam</li>
        <li id="R_2.096.022cd">kāñcanaṃ rajasā dhvastaṃ kliṣṭaṃ candram ivāmbudaiḥ</li>
    </ul>
    <ul id="R_2.096.023">
        <li id="R_2.096.023ab">mukhaṃ te prekṣya māṃ śoko dahaty agnir ivāśrayam</li>
        <li id="R_2.096.023cd">bhṛśaṃ manasi vaidehi vyasanāraṇisaṃbhavaḥ</li>
    </ul>
    <ul id="R_2.096.024">
        <li id="R_2.096.024ab">bruvantyām evam ārtāyāṃ jananyāṃ bharatāgrajaḥ</li>
        <li id="R_2.096.024cd">pādāv āsādya jagrāha vasiṣṭhasya sa rāghavaḥ</li>
    </ul>
    <ul id="R_2.096.025">
        <li id="R_2.096.025ab">
            <seg type="pāda" n="a">purohitasyāgnisamasya tasya vai</seg>
            <seg type="pāda" n="b">bṛhaspater indra ivāmarādhipaḥ</seg>
        </li>
        <li id="R_2.096.025cd">
            <seg type="pāda" n="c">pragṛhya pādau susamṛddhatejasaḥ</seg>
            <seg type="pāda" n="d">sahaiva tenopaviveśa rāghavaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.096.026">
        <li id="R_2.096.026ab">
            <seg type="pāda" n="a">tato jaghanyaṃ sahitaiḥ sa mantribhiḥ</seg>
            <seg type="pāda" n="b">purapradhānaiś ca sahaiva sainikaiḥ</seg>
        </li>
        <li id="R_2.096.026cd">
            <seg type="pāda" n="c">janena dharmajñatamena dharmavān</seg>
            <seg type="pāda" n="d">upopaviṣṭo bharatas tadāgrajam</seg>
        </li>
    </ul>
    <ul id="R_2.096.027">
        <li id="R_2.096.027ab">
            <seg type="pāda" n="a">upopaviṣṭas tu tadā sa vīryavāṃs</seg>
            <seg type="pāda" n="b">tapasviveṣeṇa samīkṣya rāghavam</seg>
        </li>
        <li id="R_2.096.027cd">
            <seg type="pāda" n="c">śriyā jvalantaṃ bharataḥ kṛtāñjalir</seg>
            <seg type="pāda" n="d">yathā mahendraḥ prayataḥ prajāpatim</seg>
        </li>
    </ul>
    <ul id="R_2.096.028">
        <li id="R_2.096.028ab">
            <seg type="pāda" n="a">kim eṣa vākyaṃ bharato 'dya rāghavaṃ</seg>
            <seg type="pāda" n="b">praṇamya satkṛtya ca sādhu vakṣyati</seg>
        </li>
        <li id="R_2.096.028cd">
            <seg type="pāda" n="c">itīva tasyāryajanasya tattvato</seg>
            <seg type="pāda" n="d">babhūva kautūhalam uttamaṃ tadā</seg>
        </li>
    </ul>
    <ul id="R_2.096.029">
        <li id="R_2.096.029ab">
            <seg type="pāda" n="a">sa rāghavaḥ satyadhṛtiś ca lakṣmaṇo</seg>
            <seg type="pāda" n="b">mahānubhāvo bharataś ca dhārmikaḥ</seg>
        </li>
        <li id="R_2.096.029cd">
            <seg type="pāda" n="c">vṛtāḥ suhṛdbhiś ca virejur adhvare</seg>
            <seg type="pāda" n="d">yathā sadasyaiḥ sahitās trayo 'gnayaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.097.001">
        <li id="R_2.097.001ab">taṃ tu rāmaḥ samāśvāsya bhrātaraṃ guruvatsalam</li>
        <li id="R_2.097.001cd">lakṣmaṇena saha bhrātrā praṣṭuṃ samupacakrame</li>
    </ul>
    <ul id="R_2.097.002">
        <li id="R_2.097.002ab">kim etad iccheyam ahaṃ śrotuṃ pravyāhṛtaṃ tvayā</li>
        <li id="R_2.097.002cd">yasmāt tvam āgato deśam imaṃ cīrajaṭājinī</li>
    </ul>
    <ul id="R_2.097.003">
        <li id="R_2.097.003ab">yannimittam imaṃ deśaṃ kṛṣṇājinajaṭādharaḥ</li>
        <li id="R_2.097.003cd">hitvā rājyaṃ praviṣṭas tvaṃ tat sarvaṃ vaktum arhasi</li>
    </ul>
    <ul id="R_2.097.004">
        <li id="R_2.097.004ab">ity uktaḥ kekayīputraḥ kākutsthena mahātmanā</li>
        <li id="R_2.097.004cd">pragṛhya balavad bhūyaḥ prāñjalir vākyam abravīt</li>
    </ul>
    <ul id="R_2.097.005">
        <li id="R_2.097.005ab">āryaṃ tātaḥ parityajya kṛtvā karma suduṣkaram</li>
        <li id="R_2.097.005cd">gataḥ svargaṃ mahābāhuḥ putraśokābhipīḍitaḥ</li>
    </ul>
    <ul id="R_2.097.006">
        <li id="R_2.097.006ab">striyā niyuktaḥ kaikeyyā mama mātrā paraṃtapa</li>
        <li id="R_2.097.006cd">cakāra sumahat pāpam idam ātmayaśoharam</li>
    </ul>
    <ul id="R_2.097.007">
        <li id="R_2.097.007ab">sā rājyaphalam aprāpya vidhavā śokakarśitā</li>
        <li id="R_2.097.007cd">patiṣyati mahāghore niraye jananī mama</li>
    </ul>
    <ul id="R_2.097.008">
        <li id="R_2.097.008ab">tasya me dāsabhūtasya prasādaṃ kartum arhasi</li>
        <li id="R_2.097.008cd">abhiṣiñcasva cādyaiva rājyena maghavān iva</li>
    </ul>
    <ul id="R_2.097.009">
        <li id="R_2.097.009ab">imāḥ prakṛtayaḥ sarvā vidhavā māturaś ca yāḥ</li>
        <li id="R_2.097.009cd">tvat sakāśam anuprāptāḥ prasādaṃ kartum arhasi</li>
    </ul>
    <ul id="R_2.097.010">
        <li id="R_2.097.010ab">tadānupūrvyā yuktaṃ ca yuktaṃ cātmani mānada</li>
        <li id="R_2.097.010cd">rājyaṃ prāpnuhi dharmeṇa sakāmān suhṛdaḥ kuru</li>
    </ul>
    <ul id="R_2.097.011">
        <li id="R_2.097.011ab">bhavatv avidhavā bhūmiḥ samagrā patinā tvayā</li>
        <li id="R_2.097.011cd">śaśinā vimaleneva śāradī rajanī yathā</li>
    </ul>
    <ul id="R_2.097.012">
        <li id="R_2.097.012ab">ebhiś ca sacivaiḥ sārdhaṃ śirasā yācito mayā</li>
        <li id="R_2.097.012cd">bhrātuḥ śiṣyasya dāsasya prasādaṃ kartum arhasi</li>
    </ul>
    <ul id="R_2.097.013">
        <li id="R_2.097.013ab">tad idaṃ śāśvataṃ pitryaṃ sarvaṃ sacivamaṇḍalam</li>
        <li id="R_2.097.013cd">pūjitaṃ puruṣavyāghra nātikramitum utsahe</li>
    </ul>
    <ul id="R_2.097.014">
        <li id="R_2.097.014ab">evam uktvā mahābāhuḥ sabāṣpaḥ kekayīsutaḥ</li>
        <li id="R_2.097.014cd">rāmasya śirasā pādau jagrāha bharataḥ punaḥ</li>
    </ul>
    <ul id="R_2.097.015">
        <li id="R_2.097.015ab">taṃ mattam iva mātaṅgaṃ niḥśvasantaṃ punaḥ punaḥ</li>
        <li id="R_2.097.015cd">bhrātaraṃ bharataṃ rāmaḥ pariṣvajyedam abravīt</li>
    </ul>
    <ul id="R_2.097.016">
        <li id="R_2.097.016ab">kulīnaḥ sattvasaṃpannas tejasvī caritavrataḥ</li>
        <li id="R_2.097.016cd">rājyahetoḥ kathaṃ pāpam ācaret tvadvidho janaḥ</li>
    </ul>
    <ul id="R_2.097.017">
        <li id="R_2.097.017ab">na doṣaṃ tvayi paśyāmi sūkṣmam apy ari sūdana</li>
        <li id="R_2.097.017cd">na cāpi jananīṃ bālyāt tvaṃ vigarhitum arhasi</li>
    </ul>
    <ul id="R_2.097.018">
        <li id="R_2.097.018ab">yāvat pitari dharmajña gauravaṃ lokasatkṛte</li>
        <li id="R_2.097.018cd">tāvad dharmabhṛtāṃ śreṣṭha jananyām api gauravam</li>
    </ul>
    <ul id="R_2.097.019">
        <li id="R_2.097.019ab">etābhyāṃ dharmaśīlābhyāṃ vanaṃ gaccheti rāghava</li>
        <li id="R_2.097.019cd">mātā pitṛbhyām ukto 'haṃ katham anyat samācare</li>
    </ul>
    <ul id="R_2.097.020">
        <li id="R_2.097.020ab">tvayā rājyam ayodhyāyāṃ prāptavyaṃ lokasatkṛtam</li>
        <li id="R_2.097.020cd">vastavyaṃ daṇḍakāraṇye mayā valkalavāsasā</li>
    </ul>
    <ul id="R_2.097.021">
        <li id="R_2.097.021ab">evaṃ kṛtvā mahārājo vibhāgaṃ lokasaṃnidhau</li>
        <li id="R_2.097.021cd">vyādiśya ca mahātejā divaṃ daśaratho gataḥ</li>
    </ul>
    <ul id="R_2.097.022">
        <li id="R_2.097.022ab">sa ca pramāṇaṃ dharmātmā rājā lokagurus tava</li>
        <li id="R_2.097.022cd">pitrā dattaṃ yathābhāgam upabhoktuṃ tvam arhasi</li>
    </ul>
    <ul id="R_2.097.023">
        <li id="R_2.097.023ab">caturdaśa samāḥ saumya daṇḍakāraṇyam āśritaḥ</li>
        <li id="R_2.097.023cd">upabhokṣye tv ahaṃ dattaṃ bhāgaṃ pitrā mahātmanā</li>
    </ul>
    <ul id="R_2.097.024">
        <li id="R_2.097.024ab">
            <seg type="pāda" n="a">yad abravīn māṃ naralokasatkṛtaḥ</seg>
            <seg type="pāda" n="b">pitā mahātmā vibudhādhipopamaḥ</seg>
        </li>
        <li id="R_2.097.024cd">
            <seg type="pāda" n="c">tad eva manye paramātmano hitaṃ</seg>
            <seg type="pāda" n="d">na sarvalokeśvarabhāvam avyayam</seg>
        </li>
    </ul>
    <ul id="R_2.098.001">
        <li id="R_2.098.001ab">tataḥ puruṣasiṃhānāṃ vṛtānāṃ taiḥ suhṛdgaṇaiḥ</li>
        <li id="R_2.098.001cd">śocatām eva rajanī duḥkhena vyatyavartata</li>
    </ul>
    <ul id="R_2.098.002">
        <li id="R_2.098.002ab">rajanyāṃ suprabhātāyāṃ bhrātaras te suhṛdvṛtāḥ</li>
        <li id="R_2.098.002cd">mandākinyāṃ hutaṃ japyaṃ kṛtvā rāmam upāgaman</li>
    </ul>
    <ul id="R_2.098.003">
        <li id="R_2.098.003ab">tūṣṇīṃ te samupāsīnā na kaś cit kiṃ cid abravīt</li>
        <li id="R_2.098.003cd">bharatas tu suhṛnmadhye rāmavacanam abravīt</li>
    </ul>
    <ul id="R_2.098.004">
        <li id="R_2.098.004ab">sāntvitā māmikā mātā dattaṃ rājyam idaṃ mama</li>
        <li id="R_2.098.004cd">tad dadāmi tavaivāhaṃ bhuṅkṣva rājyam akaṇṭakam</li>
    </ul>
    <ul id="R_2.098.005">
        <li id="R_2.098.005ab">mahatevāmbuvegena bhinnaḥ setur jalāgame</li>
        <li id="R_2.098.005cd">durāvāraṃ tvadanyena rājyakhaṇḍam idaṃ mahat</li>
    </ul>
    <ul id="R_2.098.006">
        <li id="R_2.098.006ab">gatiṃ khara ivāśvasya tārkṣyasyeva patatriṇaḥ</li>
        <li id="R_2.098.006cd">anugantuṃ na śaktir me gatiṃ tava mahīpate</li>
    </ul>
    <ul id="R_2.098.007">
        <li id="R_2.098.007ab">sujīvaṃ nityaśas tasya yaḥ parair upajīvyate</li>
        <li id="R_2.098.007cd">rāma tena tu durjīvaṃ yaḥ parān upajīvati</li>
    </ul>
    <ul id="R_2.098.008">
        <li id="R_2.098.008ab">yathā tu ropito vṛkṣaḥ puruṣeṇa vivardhitaḥ</li>
        <li id="R_2.098.008cd">hrasvakena durāroho rūḍhaskandho mahādrumaḥ</li>
    </ul>
    <ul id="R_2.098.009">
        <li id="R_2.098.009ab">sa yadā puṣpito bhūtvā phalāni na vidarśayet</li>
        <li id="R_2.098.009cd">sa tāṃ nānubhavet prītiṃ yasya hetoḥ prabhāvitaḥ</li>
    </ul>
    <ul id="R_2.098.010">
        <li id="R_2.098.010ab">eṣopamā mahābāho tvam arthaṃ vettum arhasi</li>
        <li id="R_2.098.010cd">yadi tvam asmān ṛṣabho bhartā bhṛtyān na śādhi hi</li>
    </ul>
    <ul id="R_2.098.011">
        <li id="R_2.098.011ab">śreṇayas tvāṃ mahārāja paśyantv agryāś ca sarvaśaḥ</li>
        <li id="R_2.098.011cd">pratapantam ivādityaṃ rājye sthitam ariṃdamam</li>
    </ul>
    <ul id="R_2.098.012">
        <li id="R_2.098.012ab">tavānuyāne kākutṣṭha mattā nardantu kuñjarāḥ</li>
        <li id="R_2.098.012cd">antaḥpura gatā nāryo nandantu susamāhitāḥ</li>
    </ul>
    <ul id="R_2.098.013">
        <li id="R_2.098.013ab">tasya sādhv ity amanyanta nāgarā vividhā janāḥ</li>
        <li id="R_2.098.013cd">bharatasya vacaḥ śrutvā rāmaṃ pratyanuyācataḥ</li>
    </ul>
    <ul id="R_2.098.014">
        <li id="R_2.098.014ab">tam evaṃ duḥkhitaṃ prekṣya vilapantaṃ yaśasvinam</li>
        <li id="R_2.098.014cd">rāmaḥ kṛtātmā bharataṃ samāśvāsayad ātmavān</li>
    </ul>
    <ul id="R_2.098.015">
        <li id="R_2.098.015ab">nātmanaḥ kāmakāro 'sti puruṣo 'yam anīśvaraḥ</li>
        <li id="R_2.098.015cd">itaś cetarataś cainaṃ kṛtāntaḥ parikarṣati</li>
    </ul>
    <ul id="R_2.098.016">
        <li id="R_2.098.016ab">sarve kṣayāntā nicayāḥ patanāntāḥ samucchrayāḥ</li>
        <li id="R_2.098.016cd">saṃyogā viprayogāntā maraṇāntaṃ ca jīvitam</li>
    </ul>
    <ul id="R_2.098.017">
        <li id="R_2.098.017ab">yathā phalānaṃ pakvānāṃ nānyatra patanād bhayam</li>
        <li id="R_2.098.017cd">evaṃ narasya jātasya nānyatra maraṇād bhayam</li>
    </ul>
    <ul id="R_2.098.018">
        <li id="R_2.098.018ab">yathāgāraṃ dṛḍhasthūṇaṃ jīrṇaṃ bhūtvāvasīdati</li>
        <li id="R_2.098.018cd">tathāvasīdanti narā jarāmṛtyuvaśaṃ gatāḥ</li>
    </ul>
    <ul id="R_2.098.019">
        <li id="R_2.098.019ab">ahorātrāṇi gacchanti sarveṣāṃ prāṇinām iha</li>
        <li id="R_2.098.019cd">āyūṃṣi kṣapayanty āśu grīṣme jalam ivāṃśavaḥ</li>
    </ul>
    <ul id="R_2.098.020">
        <li id="R_2.098.020ab">ātmānam anuśoca tvaṃ kim anyam anuśocasi</li>
        <li id="R_2.098.020cd">āyus te hīyate yasya sthitasya ca gatasya ca</li>
    </ul>
    <ul id="R_2.098.021">
        <li id="R_2.098.021ab">sahaiva mṛtyur vrajati saha mṛtyur niṣīdati</li>
        <li id="R_2.098.021cd">gatvā sudīrgham adhvānaṃ saha mṛtyur nivartate</li>
    </ul>
    <ul id="R_2.098.022">
        <li id="R_2.098.022ab">gātreṣu valayaḥ prāptāḥ śvetāś caiva śiroruhāḥ</li>
        <li id="R_2.098.022cd">jarayā puruṣo jīrṇaḥ kiṃ hi kṛtvā prabhāvayet</li>
    </ul>
    <ul id="R_2.098.023">
        <li id="R_2.098.023ab">nandanty udita āditye nandanty astam ite ravau</li>
        <li id="R_2.098.023cd">ātmano nāvabudhyante manuṣyā jīvitakṣayam</li>
    </ul>
    <ul id="R_2.098.024">
        <li id="R_2.098.024ab">hṛṣyanty ṛtumukhaṃ dṛṣṭvā navaṃ navam ihāgatam</li>
        <li id="R_2.098.024cd">ṛtūnāṃ parivartena prāṇināṃ prāṇasaṃkṣayaḥ</li>
    </ul>
    <ul id="R_2.098.025">
        <li id="R_2.098.025ab">yathā kāṣṭhaṃ ca kāṣṭhaṃ ca sameyātāṃ mahārṇave</li>
        <li id="R_2.098.025cd">sametya ca vyapeyātāṃ kālam āsādya kaṃ cana</li>
    </ul>
    <ul id="R_2.098.026">
        <li id="R_2.098.026ab">evaṃ bhāryāś ca putrāś ca jñātayaś ca vasūni ca</li>
        <li id="R_2.098.026cd">sametya vyavadhāvanti dhruvo hy eṣāṃ vinābhavaḥ</li>
    </ul>
    <ul id="R_2.098.027">
        <li id="R_2.098.027ab">nātra kaś cid yathā bhāvaṃ prāṇī samabhivartate</li>
        <li id="R_2.098.027cd">tena tasmin na sāmarthyaṃ pretasyāsty anuśocataḥ</li>
    </ul>
    <ul id="R_2.098.028">
        <li id="R_2.098.028ab">yathā hi sārthaṃ gacchantaṃ brūyāt kaś cit pathi sthitaḥ</li>
        <li id="R_2.098.028cd">aham apy āgamiṣyāmi pṛṣṭhato bhavatām iti</li>
    </ul>
    <ul id="R_2.098.029">
        <li id="R_2.098.029ab">evaṃ pūrvair gato mārgaḥ pitṛpaitāmaho dhruvaḥ</li>
        <li id="R_2.098.029cd">tam āpannaḥ kathaṃ śoced yasya nāsti vyatikramaḥ</li>
    </ul>
    <ul id="R_2.098.030">
        <li id="R_2.098.030ab">vayasaḥ patamānasya srotaso vānivartinaḥ</li>
        <li id="R_2.098.030cd">ātmā sukhe niyoktavyaḥ sukhabhājaḥ prajāḥ smṛtāḥ</li>
    </ul>
    <ul id="R_2.098.031">
        <li id="R_2.098.031ab">dharmātmā sa śubhaiḥ kṛtsnaiḥ kratubhiś cāptadakṣiṇaiḥ</li>
        <li id="R_2.098.031cd">dhūtapāpo gataḥ svargaṃ pitā naḥ pṛthivīpatiḥ</li>
    </ul>
    <ul id="R_2.098.032">
        <li id="R_2.098.032ab">bhṛtyānāṃ bharaṇāt samyak prajānāṃ paripālanāt</li>
        <li id="R_2.098.032cd">arthādānāc ca dhārmeṇa pitā nas tridivaṃ gataḥ</li>
    </ul>
    <ul id="R_2.098.033">
        <li id="R_2.098.033ab">iṣṭvā bahuvidhair yajñair bhogāṃś cāvāpya puṣkalān</li>
        <li id="R_2.098.033cd">uttamaṃ cāyur āsādya svar gataḥ pṛthivīpatiḥ</li>
    </ul>
    <ul id="R_2.098.034">
        <li id="R_2.098.034ab">sa jīrṇaṃ mānuṣaṃ dehaṃ parityajya pitā hi naḥ</li>
        <li id="R_2.098.034cd">daivīm ṛddhim anuprāpto brahmalokavihāriṇīm</li>
    </ul>
    <ul id="R_2.098.035">
        <li id="R_2.098.035ab">taṃ tu naivaṃ vidhaḥ kaś cit prājñaḥ śocitum arhati</li>
        <li id="R_2.098.035cd">tvadvidho yadvidhaś cāpi śrutavān buddhimattaraḥ</li>
    </ul>
    <ul id="R_2.098.036">
        <li id="R_2.098.036ab">ete bahuvidhāḥ śokā vilāpa rudite tathā</li>
        <li id="R_2.098.036cd">varjanīyā hi dhīreṇa sarvāvasthāsu dhīmatā</li>
    </ul>
    <ul id="R_2.098.037">
        <li id="R_2.098.037ab">sa svastho bhava mā śoco yātvā cāvasa tāṃ purīm</li>
        <li id="R_2.098.037cd">tathā pitrā niyukto 'si vaśinā vadatāmv vara</li>
    </ul>
    <ul id="R_2.098.038">
        <li id="R_2.098.038ab">yatrāham api tenaiva niyuktaḥ puṇyakarmaṇā</li>
        <li id="R_2.098.038cd">tatraivāhaṃ kariṣyāmi pitur āryasya śāsanam</li>
    </ul>
    <ul id="R_2.098.039">
        <li id="R_2.098.039ab">na mayā śāsanaṃ tasya tyaktuṃ nyāyyam ariṃdama</li>
        <li id="R_2.098.039cd">tat tvayāpi sadā mānyaṃ sa vai bandhuḥ sa naḥ pitā</li>
    </ul>
    <ul id="R_2.098.040">
        <li id="R_2.098.040ab">evam uktvā tu virate rāme vacanam arthavat</li>
        <li id="R_2.098.040cd">uvāca bharataś citraṃ dhārmiko dhārmikaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.098.041">
        <li id="R_2.098.041ab">ko hi syād īdṛśo loke yādṛśas tvam ariṃdama</li>
        <li id="R_2.098.041cd">na tvāṃ pravyathayed duḥkhaṃ prītir vā na praharṣayet</li>
    </ul>
    <ul id="R_2.098.042">
        <li id="R_2.098.042ab">saṃmataś cāsi vṛddhānāṃ tāṃś ca pṛcchasi saṃśayān</li>
        <li id="R_2.098.042cd">yathā mṛtas tathā jīvan yathāsati tathā sati</li>
    </ul>
    <ul id="R_2.098.043">
        <li id="R_2.098.043ab">yasyaiṣa buddhilābhaḥ syāt paritapyeta kena saḥ</li>
        <li id="R_2.098.043cd">sa evaṃ vyasanaṃ prāpya na viṣīditum arhati</li>
    </ul>
    <ul id="R_2.098.044">
        <li id="R_2.098.044ab">amaropamasattvas tvaṃ mahātmā satyasaṃgaraḥ</li>
        <li id="R_2.098.044cd">sarvajñaḥ sarvadarśī ca buddhimāṃś cāsi rāghava</li>
    </ul>
    <ul id="R_2.098.045">
        <li id="R_2.098.045ab">na tvām evaṃ guṇair yuktaṃ prabhavābhavakovidam</li>
        <li id="R_2.098.045cd">aviṣahyatamaṃ duḥkham āsādayitum arhati</li>
    </ul>
    <ul id="R_2.098.046">
        <li id="R_2.098.046ab">proṣite mayi yat pāpaṃ mātrā matkāraṇāt kṛtam</li>
        <li id="R_2.098.046cd">kṣudrayā tad aniṣṭaṃ me prasīdatu bhavān mama</li>
    </ul>
    <ul id="R_2.098.047">
        <li id="R_2.098.047ab">dharmabandhena baddho 'smi tenemāṃ neha mātaram</li>
        <li id="R_2.098.047cd">hanmi tīvreṇa daṇḍena daṇḍārhāṃ pāpakāriṇīm</li>
    </ul>
    <ul id="R_2.098.048">
        <li id="R_2.098.048ab">kathaṃ daśarathāj jātaḥ śuddhābhijanakarmaṇaḥ</li>
        <li id="R_2.098.048cd">jānan dharmam adharmiṣṭhaṃ kuryāṃ karma jugupsitam</li>
    </ul>
    <ul id="R_2.098.049">
        <li id="R_2.098.049ab">guruḥ kriyāvān vṛddhaś ca rājā pretaḥ piteti ca</li>
        <li id="R_2.098.049cd">tātaṃ na parigarheyaṃ daivataṃ ceti saṃsadi</li>
    </ul>
    <ul id="R_2.098.050">
        <li id="R_2.098.050ab">ko hi dharmārthayor hīnam īdṛśaṃ karma kilbiṣam</li>
        <li id="R_2.098.050cd">striyāḥ priyacikīrṣuḥ san kuryād dharmajña dharmavit</li>
    </ul>
    <ul id="R_2.098.051">
        <li id="R_2.098.051ab">antakāle hi bhūtāni muhyantīti purāśrutiḥ</li>
        <li id="R_2.098.051cd">rājñaivaṃ kurvatā loke pratyakṣā sā śrutiḥ kṛtā</li>
    </ul>
    <ul id="R_2.098.052">
        <li id="R_2.098.052ab">sādhv artham abhisaṃdhāya krodhān mohāc ca sāhasāt</li>
        <li id="R_2.098.052cd">tātasya yad atikrāntaṃ pratyāharatu tad bhavān</li>
    </ul>
    <ul id="R_2.098.053">
        <li id="R_2.098.053ab">pitur hi samatikrāntaṃ putro yaḥ sādhu manyate</li>
        <li id="R_2.098.053cd">tad apatyaṃ mataṃ loke viparītam ato 'nyathā</li>
    </ul>
    <ul id="R_2.098.054">
        <li id="R_2.098.054ab">tad apatyaṃ bhavān astu mā bhavān duṣkṛtaṃ pituḥ</li>
        <li id="R_2.098.054cd">abhipat tat kṛtaṃ karma loke dhīravigarhitam</li>
    </ul>
    <ul id="R_2.098.055">
        <li id="R_2.098.055ab">kaikeyīṃ māṃ ca tātaṃ ca suhṛdo bāndhavāṃś ca naḥ</li>
        <li id="R_2.098.055cd">paurajānapadān sarvāṃs trātu sarvam idaṃ bhavān</li>
    </ul>
    <ul id="R_2.098.056">
        <li id="R_2.098.056ab">kva cāraṇyaṃ kva ca kṣātraṃ kva jaṭāḥ kva ca pālanam</li>
        <li id="R_2.098.056cd">īdṛśaṃ vyāhataṃ karma na bhavān kartum arhati</li>
    </ul>
    <ul id="R_2.098.057">
        <li id="R_2.098.057ab">atha kleśajam eva tvaṃ dharmaṃ caritum icchasi</li>
        <li id="R_2.098.057cd">dharmeṇa caturo varṇān pālayan kleśam āpnuhi</li>
    </ul>
    <ul id="R_2.098.058">
        <li id="R_2.098.058ab">caturṇām āśramāṇāṃ hi gārhasthyaṃ śreṣṭham āśramam</li>
        <li id="R_2.098.058cd">āhur dharmajña dharmajñās taṃ kathaṃ tyaktum arhasi</li>
    </ul>
    <ul id="R_2.098.059">
        <li id="R_2.098.059ab">śrutena bālaḥ sthānena janmanā bhavato hy aham</li>
        <li id="R_2.098.059cd">sa kathaṃ pālayiṣyāmi bhūmiṃ bhavati tiṣṭhati</li>
    </ul>
    <ul id="R_2.098.060">
        <li id="R_2.098.060ab">hīnabuddhiguṇo bālo hīnaḥ sthānena cāpy aham</li>
        <li id="R_2.098.060cd">bhavatā ca vinā bhūto na vartayitum utsahe</li>
    </ul>
    <ul id="R_2.098.061">
        <li id="R_2.098.061ab">idaṃ nikhilam avyagraṃ pitryaṃ rājyam akaṇṭakam</li>
        <li id="R_2.098.061cd">anuśādhi svadharmeṇa dharmajña saha bāndhavaiḥ</li>
    </ul>
    <ul id="R_2.098.062">
        <li id="R_2.098.062ab">ihaiva tvābhiṣiñcantu dharmajña saha bāndhavaiḥ</li>
        <li id="R_2.098.062cd">ṛtvijaḥ savasiṣṭhāś ca mantravan mantrakovidāḥ</li>
    </ul>
    <ul id="R_2.098.063">
        <li id="R_2.098.063ab">abhiṣiktas tvam asmābhir ayodhyāṃ pālane vraja</li>
        <li id="R_2.098.063cd">vijitya tarasā lokān marudbhir iva vāsavaḥ</li>
    </ul>
    <ul id="R_2.098.064">
        <li id="R_2.098.064ab">ṛṇāni trīṇy apākurvan durhṛdaḥ sādhu nirdahan</li>
        <li id="R_2.098.064cd">suhṛdas tarpayan kāmais tvam evātrānuśādhi mām</li>
    </ul>
    <ul id="R_2.098.065">
        <li id="R_2.098.065ab">adyārya muditāḥ santu suhṛdas te 'bhiṣecane</li>
        <li id="R_2.098.065cd">adya bhītāḥ pālayantāṃ durhṛdas te diśo daśa</li>
    </ul>
    <ul id="R_2.098.066">
        <li id="R_2.098.066ab">ākrośaṃ mama mātuś ca pramṛjya puruṣarṣabha</li>
        <li id="R_2.098.066cd">adya tatra bhavantaṃ ca pitaraṃ rakṣa kilbiṣāt</li>
    </ul>
    <ul id="R_2.098.067">
        <li id="R_2.098.067ab">śirasā tvābhiyāce 'haṃ kuruṣva karuṇāṃ mayi</li>
        <li id="R_2.098.067cd">bāndhaveṣu ca sarveṣu bhūteṣv iva maheśvaraḥ</li>
    </ul>
    <ul id="R_2.098.068">
        <li id="R_2.098.068ab">atha vā pṛṣṭhataḥ kṛtvā vanam eva bhavān itaḥ</li>
        <li id="R_2.098.068cd">gamiṣyati gamiṣyāmi bhavatā sārdham apy aham</li>
    </ul>
    <ul id="R_2.098.069">
        <li id="R_2.098.069ab">
            <seg type="pāda" n="a">tathāpi rāmo bharatena tāmyata</seg>
            <seg type="pāda" n="b">prasādyamānaḥ śirasā mahīpatiḥ</seg>
        </li>
        <li id="R_2.098.069cd">
            <seg type="pāda" n="c">na caiva cakre gamanāya sattvavān</seg>
            <seg type="pāda" n="d">matiṃ pitus tadvacane pratiṣṭhitaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.098.070">
        <li id="R_2.098.070ab">
            <seg type="pāda" n="a">tad adbhutaṃ sthairyam avekṣya rāghave</seg>
            <seg type="pāda" n="b">samaṃ jano harṣam avāpa duḥkhitaḥ</seg>
        </li>
        <li id="R_2.098.070cd">
            <seg type="pāda" n="c">na yāty ayodhyām iti duḥkhito 'bhavat</seg>
            <seg type="pāda" n="d">sthirapratijñatvam avekṣya harṣitaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.098.071">
        <li id="R_2.098.071ab">
            <seg type="pāda" n="a">tam ṛtvijo naigamayūthavallabhās</seg>
            <seg type="pāda" n="b">tathā visaṃjñāśrukalāś ca mātaraḥ</seg>
        </li>
        <li id="R_2.098.071cd">
            <seg type="pāda" n="c">tathā bruvāṇaṃ bharataṃ pratuṣṭuvuḥ</seg>
            <seg type="pāda" n="d">praṇamya rāmaṃ ca yayācire saha</seg>
        </li>
    </ul>
    <ul id="R_2.099.001">
        <li id="R_2.099.001ab">punar evaṃ bruvāṇaṃ tu bharataṃ lakṣmaṇāgrajaḥ</li>
        <li id="R_2.099.001cd">pratyuvaca tataḥ śrīmāñ jñātimadhye 'tisatkṛtaḥ</li>
    </ul>
    <ul id="R_2.099.002">
        <li id="R_2.099.002ab">upapannam idaṃ vākyaṃ yat tvam evam abhāṣathāḥ</li>
        <li id="R_2.099.002cd">jātaḥ putro daśarathāt kaikeyyāṃ rājasattamāt</li>
    </ul>
    <ul id="R_2.099.003">
        <li id="R_2.099.003ab">purā bhrātaḥ pitā naḥ sa mātaraṃ te samudvahan</li>
        <li id="R_2.099.003cd">mātāmahe samāśrauṣīd rājyaśulkam anuttamam</li>
    </ul>
    <ul id="R_2.099.004">
        <li id="R_2.099.004ab">devāsure ca saṃgrāme jananyai tava pārthivaḥ</li>
        <li id="R_2.099.004cd">saṃprahṛṣṭo dadau rājā varam ārādhitaḥ prabhuḥ</li>
    </ul>
    <ul id="R_2.099.005">
        <li id="R_2.099.005ab">tataḥ sā saṃpratiśrāvya tava mātā yaśasvinī</li>
        <li id="R_2.099.005cd">ayācata naraśreṣṭhaṃ dvau varau varavarṇinī</li>
    </ul>
    <ul id="R_2.099.006">
        <li id="R_2.099.006ab">tava rājyaṃ naravyāghra mama pravrājanaṃ tathā</li>
        <li id="R_2.099.006cd">tac ca rājā tathā tasyai niyuktaḥ pradadau varam</li>
    </ul>
    <ul id="R_2.099.007">
        <li id="R_2.099.007ab">tena pitrāham apy atra niyuktaḥ puruṣarṣabha</li>
        <li id="R_2.099.007cd">caturdaśa vane vāsaṃ varṣāṇi varadānikam</li>
    </ul>
    <ul id="R_2.099.008">
        <li id="R_2.099.008ab">so 'haṃ vanam idaṃ prāpto nirjanaṃ lakṣmaṇānvitaḥ</li>
        <li id="R_2.099.008cd">śītayā cāpratidvandvaḥ satyavāde sthitaḥ pituḥ</li>
    </ul>
    <ul id="R_2.099.009">
        <li id="R_2.099.009ab">bhavān api tathety eva pitaraṃ satyavādinam</li>
        <li id="R_2.099.009cd">kartum arhati rājendraṃ kṣipram evābhiṣecanāt</li>
    </ul>
    <ul id="R_2.099.010">
        <li id="R_2.099.010ab">ṛṇān mocaya rājānaṃ matkṛte bharata prabhum</li>
        <li id="R_2.099.010cd">pitaraṃ trāhi dharmajña mātaraṃ cābhinandaya</li>
    </ul>
    <ul id="R_2.099.011">
        <li id="R_2.099.011ab">śrūyate hi purā tāta śrutir gītā yaśasvinī</li>
        <li id="R_2.099.011cd">gayena yajamānena gayeṣv eva pitṝn prati</li>
    </ul>
    <ul id="R_2.099.012">
        <li id="R_2.099.012ab">puṃ nāmnā narakād yasmāt pitaraṃ trāyate sutaḥ</li>
        <li id="R_2.099.012cd">tasmāt putra iti proktaḥ pitṝn yat pāti vā sutaḥ</li>
    </ul>
    <ul id="R_2.099.013">
        <li id="R_2.099.013ab">eṣṭavyā bahavaḥ putrā guṇavanto bahuśrutāḥ</li>
        <li id="R_2.099.013cd">teṣāṃ vai samavetānām api kaś cid gayāṃ vrajet</li>
    </ul>
    <ul id="R_2.099.014">
        <li id="R_2.099.014ab">evaṃ rājarṣayaḥ sarve pratītā rājanandana</li>
        <li id="R_2.099.014cd">tasmāt trāhi naraśreṣṭha pitaraṃ narakāt prabho</li>
    </ul>
    <ul id="R_2.099.015">
        <li id="R_2.099.015ab">ayodhyāṃ gaccha bharata prakṛtīr anurañjaya</li>
        <li id="R_2.099.015cd">śatrughna sahito vīra saha sarvair dvijātibhiḥ</li>
    </ul>
    <ul id="R_2.099.016">
        <li id="R_2.099.016ab">pravekṣye daṇḍakāraṇyam aham apy avilambayan</li>
        <li id="R_2.099.016cd">ābhyāṃ tu sahito rājan vaidehyā lakṣmaṇena ca</li>
    </ul>
    <ul id="R_2.099.017">
        <li id="R_2.099.017ab">
            <seg type="pāda" n="a">tvaṃ rājā bhava bharata svayaṃ narāṇāṃ</seg>
            <seg type="pāda" n="b">vanyānām aham api rājarāṇ mṛgāṇām</seg>
        </li>
        <li id="R_2.099.017cd">
            <seg type="pāda" n="c">gaccha tvaṃ puravaram adya saṃprahṛṣṭaḥ</seg>
            <seg type="pāda" n="d">saṃhṛṣṭas tv aham api daṇḍakān pravekṣye</seg>
        </li>
    </ul>
    <ul id="R_2.099.018">
        <li id="R_2.099.018ab">
            <seg type="pāda" n="a">chāyāṃ te dinakarabhāḥ prabādhamānaṃ</seg>
            <seg type="pāda" n="b">varṣatraṃ bharata karotu mūrdhni śītām</seg>
        </li>
        <li id="R_2.099.018cd">
            <seg type="pāda" n="c">eteṣām aham api kānanadrumāṇāṃ</seg>
            <seg type="pāda" n="d">chāyāṃ tām atiśayinīṃ sukhaṃ śrayiṣye</seg>
        </li>
    </ul>
    <ul id="R_2.099.019">
        <li id="R_2.099.019ab">
            <seg type="pāda" n="a">śatrughnaḥ kuśalamatis tu te sahāyaḥ</seg>
            <seg type="pāda" n="b">saumitrir mama viditaḥ pradhānamitram</seg>
        </li>
        <li id="R_2.099.019cd">
            <seg type="pāda" n="c">catvāras tanayavarā vayaṃ narendraṃ</seg>
            <seg type="pāda" n="d">satyasthaṃ bharata carāma mā viṣādam</seg>
        </li>
    </ul>
    <ul id="R_2.100.001">
        <li id="R_2.100.001ab">āśvāsayantaṃ bharataṃ jābālir brāhmaṇottamaḥ</li>
        <li id="R_2.100.001cd">uvāca rāmaṃ dharmajñaṃ dharmāpetam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.100.002">
        <li id="R_2.100.002ab">sādhu rāghava mā bhūt te buddhir evaṃ nirarthakā</li>
        <li id="R_2.100.002cd">prākṛtasya narasyeva ārya buddhes tapasvinaḥ</li>
    </ul>
    <ul id="R_2.100.003">
        <li id="R_2.100.003ab">kaḥ kasya puruṣo bandhuḥ kim āpyaṃ kasya kena cit</li>
        <li id="R_2.100.003cd">yad eko jāyate jantur eka eva vinaśyati</li>
    </ul>
    <ul id="R_2.100.004">
        <li id="R_2.100.004ab">tasmān mātā pitā ceti rāma sajjeta yo naraḥ</li>
        <li id="R_2.100.004cd">unmatta iva sa jñeyo nāsti kā cid dhi kasya cit</li>
    </ul>
    <ul id="R_2.100.005">
        <li id="R_2.100.005ab">yathā grāmāntaraṃ gacchan naraḥ kaś cit kva cid vaset</li>
        <li id="R_2.100.005cd">utsṛjya ca tam āvāsaṃ pratiṣṭhetāpare 'hani</li>
    </ul>
    <ul id="R_2.100.006">
        <li id="R_2.100.006ab">evam eva manuṣyāṇāṃ pitā mātā gṛhaṃ vasu</li>
        <li id="R_2.100.006cd">āvāsamātraṃ kākutstha sajjante nātra sajjanāḥ</li>
    </ul>
    <ul id="R_2.100.007">
        <li id="R_2.100.007ab">pitryaṃ rājyaṃ samutsṛjya sa nārhati narottama</li>
        <li id="R_2.100.007cd">āsthātuṃ kāpathaṃ duḥkhaṃ viṣamaṃ bahukaṇṭakam</li>
    </ul>
    <ul id="R_2.100.008">
        <li id="R_2.100.008ab">samṛddhāyām ayodhyāyām ātmānam abhiṣecaya</li>
        <li id="R_2.100.008cd">ekaveṇīdharā hi tvāṃ nagarī saṃpratīkṣate</li>
    </ul>
    <ul id="R_2.100.009">
        <li id="R_2.100.009ab">rājabhogān anubhavan mahārhān pārthivātmaja</li>
        <li id="R_2.100.009cd">vihara tvam ayodhyāyāṃ yathā śakras triviṣṭape</li>
    </ul>
    <ul id="R_2.100.010">
        <li id="R_2.100.010ab">na te kaś cid daśarataḥs tvaṃ ca tasya na kaś cana</li>
        <li id="R_2.100.010cd">anyo rājā tvam anyaś ca tasmāt kuru yad ucyate</li>
    </ul>
    <ul id="R_2.100.011">
        <li id="R_2.100.011ab">gataḥ sa nṛpatis tatra gantavyaṃ yatra tena vai</li>
        <li id="R_2.100.011cd">pravṛttir eṣā martyānāṃ tvaṃ tu mithyā vihanyase</li>
    </ul>
    <ul id="R_2.100.012">
        <li id="R_2.100.012ab">arthadharmaparā ye ye tāṃs tāñ śocāmi netarān</li>
        <li id="R_2.100.012cd">te hi duḥkham iha prāpya vināśaṃ pretya bhejire</li>
    </ul>
    <ul id="R_2.100.013">
        <li id="R_2.100.013ab">aṣṭakā pitṛdaivatyam ity ayaṃ prasṛto janaḥ</li>
        <li id="R_2.100.013cd">annasyopadravaṃ paśya mṛto hi kim aśiṣyati</li>
    </ul>
    <ul id="R_2.100.014">
        <li id="R_2.100.014ab">yadi bhuktam ihānyena deham anyasya gacchati</li>
        <li id="R_2.100.014cd">dadyāt pravasataḥ śrāddhaṃ na tat pathy aśanaṃ bhavet</li>
    </ul>
    <ul id="R_2.100.015">
        <li id="R_2.100.015ab">dānasaṃvananā hy ete granthā medhāvibhiḥ kṛtāḥ</li>
        <li id="R_2.100.015cd">yajasva dehi dīkṣasva tapas tapyasva saṃtyaja</li>
    </ul>
    <ul id="R_2.100.016">
        <li id="R_2.100.016ab">sa nāsti param ity eva kuru buddhiṃ mahāmate</li>
        <li id="R_2.100.016cd">pratyakṣaṃ yat tad ātiṣṭha parokṣaṃ pṛṣṭhataḥ kuru</li>
    </ul>
    <ul id="R_2.100.017">
        <li id="R_2.100.017ab">satāṃ buddhiṃ puraskṛtya sarvalokanidarśinīm</li>
        <li id="R_2.100.017cd">rājyaṃ tvaṃ pratigṛhṇīṣva bharatena prasāditaḥ</li>
    </ul>
    <ul id="R_2.101.001">
        <li id="R_2.101.001ab">jābāles tu vacaḥ śrutvā rāmaḥ satyātmanāṃ varaḥ</li>
        <li id="R_2.101.001cd">uvāca parayā yuktyā svabuddhyā cāvipannayā</li>
    </ul>
    <ul id="R_2.101.002">
        <li id="R_2.101.002ab">bhavān me priyakāmārthaṃ vacanaṃ yad ihoktavān</li>
        <li id="R_2.101.002cd">akāryaṃ kāryasaṃkāśam apathyaṃ pathyasaṃmitam</li>
    </ul>
    <ul id="R_2.101.003">
        <li id="R_2.101.003ab">nirmaryādas tu puruṣaḥ pāpācārasamanvitaḥ</li>
        <li id="R_2.101.003cd">mānaṃ na labhate satsu bhinnacāritradarśanaḥ</li>
    </ul>
    <ul id="R_2.101.004">
        <li id="R_2.101.004ab">kulīnam akulīnaṃ vā vīraṃ puruṣamāninam</li>
        <li id="R_2.101.004cd">cāritram eva vyākhyāti śuciṃ vā yadi vāśucim</li>
    </ul>
    <ul id="R_2.101.005">
        <li id="R_2.101.005ab">anāryas tv āryasaṃkāśaḥ śaucād dhīnas tathā śuciḥ</li>
        <li id="R_2.101.005cd">lakṣaṇyavad alakṣaṇyo duḥśīlaḥ śīlavān iva</li>
    </ul>
    <ul id="R_2.101.006">
        <li id="R_2.101.006ab">adharmaṃ dharmaveṣeṇa yadīmaṃ lokasaṃkaram</li>
        <li id="R_2.101.006cd">abhipatsye śubhaṃ hitvā kriyāvidhivivarjitam</li>
    </ul>
    <ul id="R_2.101.007">
        <li id="R_2.101.007ab">kaś cetayānaḥ puruṣaḥ kāryākāryavicakṣaṇaḥ</li>
        <li id="R_2.101.007cd">bahu maṃsyati māṃ loke durvṛttaṃ lokadūṣaṇam</li>
    </ul>
    <ul id="R_2.101.008">
        <li id="R_2.101.008ab">kasya yāsyāmy ahaṃ vṛttaṃ kena vā svargam āpnuyām</li>
        <li id="R_2.101.008cd">anayā vartamāno 'haṃ vṛttyā hīnapratijñayā</li>
    </ul>
    <ul id="R_2.101.009">
        <li id="R_2.101.009ab">kāmavṛttas tv ayaṃ lokaḥ kṛtsnaḥ samupavartate</li>
        <li id="R_2.101.009cd">yadvṛttāḥ santi rājānas tadvṛttāḥ santi hi prajāḥ</li>
    </ul>
    <ul id="R_2.101.010">
        <li id="R_2.101.010ab">satyam evānṛśaṃsyaṃ ca rājavṛttaṃ sanātanam</li>
        <li id="R_2.101.010cd">tasmāt satyātmakaṃ rājyaṃ satye lokaḥ pratiṣṭhitaḥ</li>
    </ul>
    <ul id="R_2.101.011">
        <li id="R_2.101.011ab">ṛṣayaś caiva devāś ca satyam eva hi menire</li>
        <li id="R_2.101.011cd">satyavādī hi loke 'smin paramaṃ gacchati kṣayam</li>
    </ul>
    <ul id="R_2.101.012">
        <li id="R_2.101.012ab">udvijante yathā sarpān narād anṛtavādinaḥ</li>
        <li id="R_2.101.012cd">dharmaḥ satyaṃ paro loke mūlaṃ svargasya cocyate</li>
    </ul>
    <ul id="R_2.101.013">
        <li id="R_2.101.013ab">satyam eveśvaro loke satyaṃ padmā samāśritā</li>
        <li id="R_2.101.013cd">satyamūlāni sarvāṇi satyān nāsti paraṃ padam</li>
    </ul>
    <ul id="R_2.101.014">
        <li id="R_2.101.014ab">dattam iṣṭaṃ hutaṃ caiva taptāni ca tapāṃsi ca</li>
        <li id="R_2.101.014cd">vedāḥ satyapratiṣṭhānās tasmāt satyaparo bhavet</li>
    </ul>
    <ul id="R_2.101.015">
        <li id="R_2.101.015ab">ekaḥ pālayate lokam ekaḥ pālayate kulam</li>
        <li id="R_2.101.015cd">majjaty eko hi niraya ekaḥ svarge mahīyate</li>
    </ul>
    <ul id="R_2.101.016">
        <li id="R_2.101.016ab">so 'haṃ pitur nideśaṃ tu kimarthaṃ nānupālaye</li>
        <li id="R_2.101.016cd">satyapratiśravaḥ satyaṃ satyena samayīkṛtaḥ</li>
    </ul>
    <ul id="R_2.101.017">
        <li id="R_2.101.017ab">naiva lobhān na mohād vā na cājñānāt tamo'nvitaḥ</li>
        <li id="R_2.101.017cd">setuṃ satyasya bhetsyāmi guroḥ satyapratiśravaḥ</li>
    </ul>
    <ul id="R_2.101.018">
        <li id="R_2.101.018ab">asatyasaṃdhasya sataś calasyāsthiracetasaḥ</li>
        <li id="R_2.101.018cd">naiva devā na pitaraḥ pratīcchantīti naḥ śrutam</li>
    </ul>
    <ul id="R_2.101.019">
        <li id="R_2.101.019ab">pratyagātmam imaṃ dharmaṃ satyaṃ paśyāmy ahaṃ svayam</li>
        <li id="R_2.101.019cd">bhāraḥ satpuruṣācīrṇas tad artham abhinandyate</li>
    </ul>
    <ul id="R_2.101.020">
        <li id="R_2.101.020ab">kṣātraṃ dharmam ahaṃ tyakṣye hy adharmaṃ dharmasaṃhitam</li>
        <li id="R_2.101.020cd">kṣudraur nṛśaṃsair lubdhaiś ca sevitaṃ pāpakarmabhiḥ</li>
    </ul>
    <ul id="R_2.101.021">
        <li id="R_2.101.021ab">kāyena kurute pāpaṃ manasā saṃpradhārya ca</li>
        <li id="R_2.101.021cd">anṛtaṃ jihvayā cāha trividhaṃ karma pātakam</li>
    </ul>
    <ul id="R_2.101.022">
        <li id="R_2.101.022ab">bhūmiḥ kīrtir yaśo lakṣmīḥ puruṣaṃ prārthayanti hi</li>
        <li id="R_2.101.022cd">svargasthaṃ cānubadhnanti satyam eva bhajeta tat</li>
    </ul>
    <ul id="R_2.101.023">
        <li id="R_2.101.023ab">śreṣṭhaṃ hy anāryam eva syād yad bhavān avadhārya mām</li>
        <li id="R_2.101.023cd">āha yuktikarair vākyair idaṃ bhadraṃ kuruṣva ha</li>
    </ul>
    <ul id="R_2.101.024">
        <li id="R_2.101.024ab">kathaṃ hy ahaṃ pratijñāya vanavāsam imaṃ guroḥ</li>
        <li id="R_2.101.024cd">bharatasya kariṣyāmi vaco hitvā guror vacaḥ</li>
    </ul>
    <ul id="R_2.101.025">
        <li id="R_2.101.025ab">sthirā mayā pratijñātā pratijñā gurusaṃnidhau</li>
        <li id="R_2.101.025cd">prahṛṣṭamānasā devī kaikeyī cābhavat tadā</li>
    </ul>
    <ul id="R_2.101.026">
        <li id="R_2.101.026ab">vanavāsaṃ vasann evaṃ śucir niyatabhojanaḥ</li>
        <li id="R_2.101.026cd">mūlaiḥ puṣpaiḥ phalaiḥ puṇyaiḥ pitṝn devāṃś ca tarpayan</li>
    </ul>
    <ul id="R_2.101.027">
        <li id="R_2.101.027ab">saṃtuṣṭapañcavargo 'haṃ lokayātrāṃ pravartaye</li>
        <li id="R_2.101.027cd">akuhaḥ śraddadhānaḥ san kāryākāryavicakṣaṇaḥ</li>
    </ul>
    <ul id="R_2.101.028">
        <li id="R_2.101.028ab">karmabhūmim imāṃ prāpya kartavyaṃ karma yac chubham</li>
        <li id="R_2.101.028cd">agnir vāyuś ca somaś ca karmaṇāṃ phalabhāginaḥ</li>
    </ul>
    <ul id="R_2.101.029">
        <li id="R_2.101.029ab">śataṃ kratūnām āhṛtya devarāṭ tridivaṃ gataḥ</li>
        <li id="R_2.101.029cd">tapāṃsy ugrāṇi cāsthāya divaṃ yātā maharṣayaḥ</li>
    </ul>
    <ul id="R_2.101.030">
        <li id="R_2.101.030ab">
            <seg type="pāda" n="a">satyaṃ ca dharmaṃ ca parākramaṃ ca</seg>
            <seg type="pāda" n="b">bhūtānukampāṃ priyavāditāṃ ca</seg>
        </li>
        <li id="R_2.101.030cd">
            <seg type="pāda" n="c">dvijātidevātithipūjanaṃ ca</seg>
            <seg type="pāda" n="d">panthānam āhus tridivasya santaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.101.031">
        <li id="R_2.101.031ab">
            <seg type="pāda" n="a">dharme ratāḥ satpuruṣaiḥ sametās</seg>
            <seg type="pāda" n="b">tejasvino dānaguṇapradhānāḥ</seg>
        </li>
        <li id="R_2.101.031cd">
            <seg type="pāda" n="c">ahiṃsakā vītamalāś ca loke</seg>
            <seg type="pāda" n="d">bhavanti pūjyā munayaḥ pradhānāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.102.001">
        <li id="R_2.102.001ab">kruddham ājñāya rāmaṃ tu vasiṣṭhaḥ pratyuvāca ha</li>
        <li id="R_2.102.001cd">jābālir api jānīte lokasyāsya gatāgatim</li>
        <li id="R_2.102.001ef">nivartayitukāmas tu tvām etad vākyam abravīt</li>
    </ul>
    <ul id="R_2.102.002">
        <li id="R_2.102.002ab">imāṃ lokasamutpattiṃ lokanātha nibodha me</li>
        <li id="R_2.102.002cd">sarvaṃ salilam evāsīt pṛthivī yatra nirmitā</li>
        <li id="R_2.102.002ef">tataḥ samabhavad brahmā svayambhūr daivataiḥ saha</li>
    </ul>
    <ul id="R_2.102.003">
        <li id="R_2.102.003ab">sa varāhas tato bhūtvā projjahāra vasuṃdharām</li>
        <li id="R_2.102.003cd">asṛjac ca jagat sarvaṃ saha putraiḥ kṛtātmabhiḥ</li>
    </ul>
    <ul id="R_2.102.004">
        <li id="R_2.102.004ab">ākāśaprabhavo brahmā śāśvato nitya avyayaḥ</li>
        <li id="R_2.102.004cd">tasmān marīciḥ saṃjajñe marīceḥ kaśyapaḥ sutaḥ</li>
    </ul>
    <ul id="R_2.102.005">
        <li id="R_2.102.005ab">vivasvān kaśyapāj jajñe manur vaivasvataḥ smṛtaḥ</li>
        <li id="R_2.102.005cd">sa tu prajāpatiḥ pūrvam ikṣvākus tu manoḥ sutaḥ</li>
    </ul>
    <ul id="R_2.102.006">
        <li id="R_2.102.006ab">yasyeyaṃ prathamaṃ dattā samṛddhā manunā mahī</li>
        <li id="R_2.102.006cd">tam ikṣvākum ayodhyāyāṃ rājānaṃ viddhi pūrvakam</li>
    </ul>
    <ul id="R_2.102.007">
        <li id="R_2.102.007ab">ikṣvākos tu sutaḥ śrīmān kukṣir eveti viśrutaḥ</li>
        <li id="R_2.102.007cd">kukṣer athātmajo vīro vikukṣir udapadyata</li>
    </ul>
    <ul id="R_2.102.008">
        <li id="R_2.102.008ab">vikukṣes tu mahātejā bāṇaḥ putraḥ pratāpavān</li>
        <li id="R_2.102.008cd">bāṇasya tu mahābāhur anaraṇyo mahāyaśāḥ</li>
    </ul>
    <ul id="R_2.102.009">
        <li id="R_2.102.009ab">nānāvṛṣṭir babhūvāsmin na durbhikṣaṃ satāṃ vare</li>
        <li id="R_2.102.009cd">anaraṇye mahārāje taskaro vāpi kaś cana</li>
    </ul>
    <ul id="R_2.102.010">
        <li id="R_2.102.010ab">anaraṇyān mahābāhuḥ pṛthū rājā babhūva ha</li>
        <li id="R_2.102.010cd">tasmāt pṛthor mahārājas triśaṅkur udapadyata</li>
        <li id="R_2.102.010ef">sa satyavacanād vīraḥ saśarīro divaṃ gataḥ</li>
    </ul>
    <ul id="R_2.102.011">
        <li id="R_2.102.011ab">triśaṅkor abhavat sūnur dhundhumāro mahāyaśāḥ</li>
        <li id="R_2.102.011cd">dhundhumārān mahātejā yuvanāśvo vyajāyata</li>
    </ul>
    <ul id="R_2.102.012">
        <li id="R_2.102.012ab">yuvanāśva sutaḥ śrīmān māndhātā samapadyata</li>
        <li id="R_2.102.012cd">māndhātus tu mahātejāḥ susaṃdhir udapadyata</li>
    </ul>
    <ul id="R_2.102.013">
        <li id="R_2.102.013ab">susaṃdher api putrau dvau dhruvasaṃdhiḥ prasenajit</li>
        <li id="R_2.102.013cd">yaśasvī dhruvasaṃdhes tu bharato ripusūdanaḥ</li>
    </ul>
    <ul id="R_2.102.014">
        <li id="R_2.102.014ab">bharatāt tu mahābāhor asito nāma jāyata</li>
        <li id="R_2.102.014cd">yasyaite pratirājāna udapadyanta śatravaḥ</li>
        <li id="R_2.102.014ef">haihayās tālajaṅghāś ca śūrāś ca śaśabindavaḥ</li>
    </ul>
    <ul id="R_2.102.015">
        <li id="R_2.102.015ab">tāṃs tu sarvān prativyūhya yuddhe rājā pravāsitaḥ</li>
        <li id="R_2.102.015cd">sa ca śailavare ramye babhūvābhirato muniḥ</li>
        <li id="R_2.102.015ef">dve cāsya bhārye garbhiṇyau babhūvatur iti śrutiḥ</li>
    </ul>
    <ul id="R_2.102.016">
        <li id="R_2.102.016ab">bhārgavaś cyavano nāma himavantam upāśritaḥ</li>
        <li id="R_2.102.016cd">tam ṛṣiṃ samupāgamya kālindī tv abhyavādayat</li>
    </ul>
    <ul id="R_2.102.017">
        <li id="R_2.102.017ab">sa tām abhyavadad vipro varepsuṃ putrajanmani</li>
        <li id="R_2.102.017cd">tataḥ sā gṛham āgamya devī putraṃ vyajāyata</li>
    </ul>
    <ul id="R_2.102.018">
        <li id="R_2.102.018ab">sapatnyā tu garas tasyai datto garbhajighāṃsayā</li>
        <li id="R_2.102.018cd">gareṇa saha tenaiva jātaḥ sa sagaro 'bhavat</li>
    </ul>
    <ul id="R_2.102.019">
        <li id="R_2.102.019ab">sa rājā sagaro nāma yaḥ samudram akhānayat</li>
        <li id="R_2.102.019cd">iṣṭvā parvaṇi vegena trāsayantam imāḥ prajāḥ</li>
    </ul>
    <ul id="R_2.102.020">
        <li id="R_2.102.020ab">asamañjas tu putro 'bhūt sagarasyeti naḥ śrutam</li>
        <li id="R_2.102.020cd">jīvann eva sa pitrā tu nirastaḥ pāpakarmakṛt</li>
    </ul>
    <ul id="R_2.102.021">
        <li id="R_2.102.021ab">aṃśumān iti putro 'bhūd asamañjasya vīryavān</li>
        <li id="R_2.102.021cd">dilīpo 'ṃśumataḥ putro dilīpasya bhagīrathaḥ</li>
    </ul>
    <ul id="R_2.102.022">
        <li id="R_2.102.022ab">bhagīrathāt kakutsthas tu kākutsthā yena tu smṛtāḥ</li>
        <li id="R_2.102.022cd">kakutsthasya tu putro 'bhūd raghur yena tu rāghavaḥ</li>
    </ul>
    <ul id="R_2.102.023">
        <li id="R_2.102.023ab">raghos tu putras tejasvī pravṛddhaḥ puruṣādakaḥ</li>
        <li id="R_2.102.023cd">kalmāṣapādaḥ saudāsa ity evaṃ prathito bhuvi</li>
    </ul>
    <ul id="R_2.102.024">
        <li id="R_2.102.024ab">kalmāṣapādaputro 'bhūc chaṅkhaṇas tv iti viśrutaḥ</li>
        <li id="R_2.102.024cd">yas tu tad vīryam āsādya sahaseno vyanīnaśat</li>
    </ul>
    <ul id="R_2.102.025">
        <li id="R_2.102.025ab">śaṅkhaṇasya tu putro 'bhūc chūraḥ śrīmān sudarśanaḥ</li>
        <li id="R_2.102.025cd">sudarśanasyāgnivarṇa agnivarṣasya śīghragaḥ</li>
    </ul>
    <ul id="R_2.102.026">
        <li id="R_2.102.026ab">śīghragasya maruḥ putro maroḥ putraḥ praśuśrukaḥ</li>
        <li id="R_2.102.026cd">praśuśrukasya putro 'bhūd ambarīṣo mahādyutiḥ</li>
    </ul>
    <ul id="R_2.102.027">
        <li id="R_2.102.027ab">ambarīṣasya putro 'bhūn nahuṣaḥ satyavikramaḥ</li>
        <li id="R_2.102.027cd">nahuṣasya ca nābhāgaḥ putraḥ paramadhārmikaḥ</li>
    </ul>
    <ul id="R_2.102.028">
        <li id="R_2.102.028ab">ajaś ca suvrataś caiva nābhāgasya sutāv ubhau</li>
        <li id="R_2.102.028cd">ajasya caiva dharmātmā rājā daśarathaḥ sutaḥ</li>
    </ul>
    <ul id="R_2.102.029">
        <li id="R_2.102.029ab">tasya jyeṣṭho 'si dāyādo rāma ity abhiviśrutaḥ</li>
        <li id="R_2.102.029cd">tad gṛhāṇa svakaṃ rājyam avekṣasva jagan nṛpa</li>
    </ul>
    <ul id="R_2.102.030">
        <li id="R_2.102.030ab">ikṣvākūṇāṃ hi sarveṣāṃ rājā bhavati pūrvajaḥ</li>
        <li id="R_2.102.030cd">pūrvaje nāvaraḥ putro jyeṣṭho rājye 'bhiṣicyate</li>
    </ul>
    <ul id="R_2.102.031">
        <li id="R_2.102.031ab">
            <seg type="pāda" n="a">sa rāghavāṇāṃ kuladharmam ātmanaḥ</seg>
            <seg type="pāda" n="b">sanātanaṃ nādya vihātum arhasi</seg>
        </li>
        <li id="R_2.102.031cd">
            <seg type="pāda" n="c">prabhūtaratnām anuśādhi medinīṃ</seg>
            <seg type="pāda" n="d">prabhūtarāṣṭrāṃ pitṛvan mahāyaśāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.103.001">
        <li id="R_2.103.001ab">vasiṣṭhas tu tadā rāmam uktvā rājapurohitaḥ</li>
        <li id="R_2.103.001cd">abravīd dharmasaṃyuktaṃ punar evāparaṃ vacaḥ</li>
    </ul>
    <ul id="R_2.103.002">
        <li id="R_2.103.002ab">puruṣasyeha jātasya bhavanti guravas trayaḥ</li>
        <li id="R_2.103.002cd">ācāryaś caiva kākutstha pitā mātā ca rāghava</li>
    </ul>
    <ul id="R_2.103.003">
        <li id="R_2.103.003ab">pitā hy enaṃ janayati puruṣaṃ puruṣarṣabha</li>
        <li id="R_2.103.003cd">prajñāṃ dadāti cācāryas tasmāt sa gurur ucyate</li>
    </ul>
    <ul id="R_2.103.004">
        <li id="R_2.103.004ab">sa te 'haṃ pitur ācāryas tava caiva paraṃtapa</li>
        <li id="R_2.103.004cd">mama tvaṃ vacanaṃ kurvan nātivarteḥ satāṃ gatim</li>
    </ul>
    <ul id="R_2.103.005">
        <li id="R_2.103.005ab">imā hi te pariṣadaḥ śreṇayaś ca samāgatāḥ</li>
        <li id="R_2.103.005cd">eṣu tāta caran dharmaṃ nātivarteḥ satāṃ gatim</li>
    </ul>
    <ul id="R_2.103.006">
        <li id="R_2.103.006ab">vṛddhāyā dharmaśīlāyā mātur nārhasy avartitum</li>
        <li id="R_2.103.006cd">asyās tu vacanaṃ kurvan nātivarteḥ satāṃ gatim</li>
    </ul>
    <ul id="R_2.103.007">
        <li id="R_2.103.007ab">bharatasya vacaḥ kurvan yācamānasya rāghava</li>
        <li id="R_2.103.007cd">ātmānaṃ nātivartes tvaṃ satyadharmaparākrama</li>
    </ul>
    <ul id="R_2.103.008">
        <li id="R_2.103.008ab">evaṃ madhuram uktas tu guruṇā rāghavaḥ svayam</li>
        <li id="R_2.103.008cd">pratyuvāca samāsīnaṃ vasiṣṭhaṃ puruṣarṣabhaḥ</li>
    </ul>
    <ul id="R_2.103.009">
        <li id="R_2.103.009ab">yan mātāpitarau vṛttaṃ tanaye kurutaḥ sadā</li>
        <li id="R_2.103.009cd">na supratikaraṃ tat tu mātrā pitrā ca yat kṛtam</li>
    </ul>
    <ul id="R_2.103.010">
        <li id="R_2.103.010ab">yathāśakti pradānena snāpanāc chādanena ca</li>
        <li id="R_2.103.010cd">nityaṃ ca priyavādena tathā saṃvardhanena ca</li>
    </ul>
    <ul id="R_2.103.011">
        <li id="R_2.103.011ab">sa hi rājā janayitā pitā daśaratho mama</li>
        <li id="R_2.103.011cd">ājñātaṃ yan mayā tasya na tan mithyā bhaviṣyati</li>
    </ul>
    <ul id="R_2.103.012">
        <li id="R_2.103.012ab">evam uktas tu rāmeṇa bharataḥ pratyanantaram</li>
        <li id="R_2.103.012cd">uvāca paramodāraḥ sūtaṃ paramadurmanāḥ</li>
    </ul>
    <ul id="R_2.103.013">
        <li id="R_2.103.013ab">iha me sthaṇḍile śīghraṃ kuśān āstara sārathe</li>
        <li id="R_2.103.013cd">āryaṃ pratyupavekṣyāmi yāvan me na prasīdati</li>
    </ul>
    <ul id="R_2.103.014">
        <li id="R_2.103.014ab">anāhāro nirāloko dhanahīno yathā dvijaḥ</li>
        <li id="R_2.103.014cd">śeṣye purastāc chālāyā yāvan na pratiyāsyati</li>
    </ul>
    <ul id="R_2.103.015">
        <li id="R_2.103.015ab">sa tu rāmam avekṣantaṃ sumantraṃ prekṣya durmanāḥ</li>
        <li id="R_2.103.015cd">kuśottaram upasthāpya bhūmāv evāstarat svayam</li>
    </ul>
    <ul id="R_2.103.016">
        <li id="R_2.103.016ab">tam uvāca mahātejā rāmo rājarṣisattamāḥ</li>
        <li id="R_2.103.016cd">kiṃ māṃ bharata kurvāṇaṃ tāta pratyupavekṣyasi</li>
    </ul>
    <ul id="R_2.103.017">
        <li id="R_2.103.017ab">brāhmaṇo hy ekapārśvena narān roddhum ihārhati</li>
        <li id="R_2.103.017cd">na tu mūrdhāvasiktānāṃ vidhiḥ pratyupaveśane</li>
    </ul>
    <ul id="R_2.103.018">
        <li id="R_2.103.018ab">uttiṣṭha naraśārdūla hitvaitad dāruṇaṃ vratam</li>
        <li id="R_2.103.018cd">puravaryām itaḥ kṣipram ayodhyāṃ yāhi rāghava</li>
    </ul>
    <ul id="R_2.103.019">
        <li id="R_2.103.019ab">āsīnas tv eva bharataḥ paurajānapadaṃ janam</li>
        <li id="R_2.103.019cd">uvāca sarvataḥ prekṣya kim āryaṃ nānuśāsatha</li>
    </ul>
    <ul id="R_2.103.020">
        <li id="R_2.103.020ab">te tam ūcur mahātmānaṃ paurajānapadā janāḥ</li>
        <li id="R_2.103.020cd">kākutstham abhijānīmaḥ samyag vadati rāghavaḥ</li>
    </ul>
    <ul id="R_2.103.021">
        <li id="R_2.103.021ab">eṣo 'pi hi mahābhāgaḥ pitur vacasi tiṣṭhati</li>
        <li id="R_2.103.021cd">ata eva na śaktāḥ smo vyāvartayitum añjasā</li>
    </ul>
    <ul id="R_2.103.022">
        <li id="R_2.103.022ab">teṣām ājñāya vacanaṃ rāmo vacanam abravīt</li>
        <li id="R_2.103.022cd">evaṃ nibodha vacanaṃ suhṛdāṃ dharmacakṣuṣām</li>
    </ul>
    <ul id="R_2.103.023">
        <li id="R_2.103.023ab">etac caivobhayaṃ śrutvā samyak saṃpaśya rāghava</li>
        <li id="R_2.103.023cd">uttiṣṭha tvaṃ mahābāho māṃ ca spṛśa tathodakam</li>
    </ul>
    <ul id="R_2.103.024">
        <li id="R_2.103.024ab">athotthāya jalaṃ spṛṣṭvā bharato vākyam abravīt</li>
        <li id="R_2.103.024cd">śṛṇvantu me pariṣado mantriṇaḥ śreṇayas tathā</li>
    </ul>
    <ul id="R_2.103.025">
        <li id="R_2.103.025ab">na yāce pitaraṃ rājyaṃ nānuśāsāmi mātaram</li>
        <li id="R_2.103.025cd">āryaṃ paramadharmajñam abhijānāmi rāghavam</li>
    </ul>
    <ul id="R_2.103.026">
        <li id="R_2.103.026ab">yadi tv avaśyaṃ vastavyaṃ kartavyaṃ ca pitur vacaḥ</li>
        <li id="R_2.103.026cd">aham eva nivatsyāmi caturdaśa vane samāḥ</li>
    </ul>
    <ul id="R_2.103.027">
        <li id="R_2.103.027ab">dharmātmā tasya tathyena bhrātur vākyena vismitaḥ</li>
        <li id="R_2.103.027cd">uvāca rāmaḥ saṃprekṣya paurajānapadaṃ janam</li>
    </ul>
    <ul id="R_2.103.028">
        <li id="R_2.103.028ab">vikrītam āhitaṃ krītaṃ yat pitrā jīvatā mama</li>
        <li id="R_2.103.028cd">na tal lopayituṃ śakyaṃ mayā vā bharatena vā</li>
    </ul>
    <ul id="R_2.103.029">
        <li id="R_2.103.029ab">upadhir na mayā kāryo vanavāse jugupsitaḥ</li>
        <li id="R_2.103.029cd">yuktam uktaṃ ca kaikeyyā pitrā me sukṛtaṃ kṛtam</li>
    </ul>
    <ul id="R_2.103.030">
        <li id="R_2.103.030ab">jānāmi bharataṃ kṣāntaṃ gurusatkārakāriṇam</li>
        <li id="R_2.103.030cd">sarvam evātra kalyāṇaṃ satyasaṃdhe mahātmani</li>
    </ul>
    <ul id="R_2.103.031">
        <li id="R_2.103.031ab">anena dharmaśīlena vanāt pratyāgataḥ punaḥ</li>
        <li id="R_2.103.031cd">bhrātrā saha bhaviṣyāmi pṛthivyāḥ patir uttamaḥ</li>
    </ul>
    <ul id="R_2.103.032">
        <li id="R_2.103.032ab">vṛto rājā hi kaikeyyā mayā tad vacanaṃ kṛtam</li>
        <li id="R_2.103.032cd">anṛtān mocayānena pitaraṃ taṃ mahīpatim</li>
    </ul>
    <ul id="R_2.104.001">
        <li id="R_2.104.001ab">tam apratimatejobhyāṃ bhrātṛbhyāṃ romaharṣaṇam</li>
        <li id="R_2.104.001cd">vismitāḥ saṃgamaṃ prekṣya samavetā maharṣayaḥ</li>
    </ul>
    <ul id="R_2.104.002">
        <li id="R_2.104.002ab">antarhitās tv ṛṣigaṇāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_2.104.002cd">tau bhrātarau mahātmānau kākutsthau praśaśaṃsire</li>
    </ul>
    <ul id="R_2.104.003">
        <li id="R_2.104.003ab">sa dhanyo yasya putrau dvau dharmajñau dharmavikramau</li>
        <li id="R_2.104.003cd">śrutvā vayaṃ hi saṃbhāṣām ubhayoḥ spṛhayāmahe</li>
    </ul>
    <ul id="R_2.104.004">
        <li id="R_2.104.004ab">tatas tv ṛṣigaṇāḥ kṣipraṃ daśagrīvavadhaiṣiṇaḥ</li>
        <li id="R_2.104.004cd">bharataṃ rājaśārdūlam ity ūcuḥ saṃgatā vacaḥ</li>
    </ul>
    <ul id="R_2.104.005">
        <li id="R_2.104.005ab">kule jāta mahāprājña mahāvṛtta mahāyaśaḥ</li>
        <li id="R_2.104.005cd">grāhyaṃ rāmasya vākyaṃ te pitaraṃ yady avekṣase</li>
    </ul>
    <ul id="R_2.104.006">
        <li id="R_2.104.006ab">sadānṛṇam imaṃ rāmaṃ vayam icchāmahe pituḥ</li>
        <li id="R_2.104.006cd">anṛṇatvāc ca kaikeyyāḥ svargaṃ daśaratho gataḥ</li>
    </ul>
    <ul id="R_2.104.007">
        <li id="R_2.104.007ab">etāvad uktvā vacanaṃ gandharvāḥ samaharṣayaḥ</li>
        <li id="R_2.104.007cd">rājarṣayaś caiva tathā sarve svāṃ svāṃ gatiṃ gatāḥ</li>
    </ul>
    <ul id="R_2.104.008">
        <li id="R_2.104.008ab">hlāditas tena vākyena śubhena śubhadarśanaḥ</li>
        <li id="R_2.104.008cd">rāmaḥ saṃhṛṣṭavadanas tān ṛṣīn abhyapūjayat</li>
    </ul>
    <ul id="R_2.104.009">
        <li id="R_2.104.009ab">srastagātras tu bharataḥ sa vācā sajjamānayā</li>
        <li id="R_2.104.009cd">kṛtāñjalir idaṃ vākyaṃ rāghavaṃ punar abravīt</li>
    </ul>
    <ul id="R_2.104.010">
        <li id="R_2.104.010ab">rājadharmam anuprekṣya kuladharmānusaṃtatim</li>
        <li id="R_2.104.010cd">kartum arhasi kākutstha mama mātuś ca yācanām</li>
    </ul>
    <ul id="R_2.104.011">
        <li id="R_2.104.011ab">rakṣituṃ sumahad rājyam aham ekas tu notsahe</li>
        <li id="R_2.104.011cd">paurajānapadāṃś cāpi raktān rañjayituṃ tathā</li>
    </ul>
    <ul id="R_2.104.012">
        <li id="R_2.104.012ab">jñātayaś ca hi yodhāś ca mitrāṇi suhṛdaś ca naḥ</li>
        <li id="R_2.104.012cd">tvām eva pratikāṅkṣante parjanyam iva karṣakāḥ</li>
    </ul>
    <ul id="R_2.104.013">
        <li id="R_2.104.013ab">idaṃ rājyaṃ mahāprājña sthāpaya pratipadya hi</li>
        <li id="R_2.104.013cd">śaktimān asi kākutstha lokasya paripālane</li>
    </ul>
    <ul id="R_2.104.014">
        <li id="R_2.104.014ab">ity uktvā nyapatad bhrātuḥ pādayor bharatas tadā</li>
        <li id="R_2.104.014cd">bhṛśaṃ saṃprārthayām āsa rāmam evaṃ priyaṃ vadaḥ</li>
    </ul>
    <ul id="R_2.104.015">
        <li id="R_2.104.015ab">tam aṅke bhrātaraṃ kṛtvā rāmo vacanam abravīt</li>
        <li id="R_2.104.015cd">śyāmaṃ nalinapatrākṣaṃ mattahaṃsasvaraḥ svayam</li>
    </ul>
    <ul id="R_2.104.016">
        <li id="R_2.104.016ab">āgatā tvām iyaṃ buddhiḥ svajā vainayikī ca yā</li>
        <li id="R_2.104.016cd">bhṛśam utsahase tāta rakṣituṃ pṛthivīm api</li>
    </ul>
    <ul id="R_2.104.017">
        <li id="R_2.104.017ab">amātyaiś ca suhṛdbhiś ca buddhimadbhiś ca mantribhiḥ</li>
        <li id="R_2.104.017cd">sarvakāryāṇi saṃmantrya sumahānty api kāraya</li>
    </ul>
    <ul id="R_2.104.018">
        <li id="R_2.104.018ab">lakṣmīś candrād apeyād vā himavān vā himaṃ tyajet</li>
        <li id="R_2.104.018cd">atīyāt sāgaro velāṃ na pratijñām ahaṃ pituḥ</li>
    </ul>
    <ul id="R_2.104.019">
        <li id="R_2.104.019ab">kāmād vā tāta lobhād vā mātrā tubhyam idaṃ kṛtam</li>
        <li id="R_2.104.019cd">na tan manasi kartavyaṃ vartitavyaṃ ca mātṛvat</li>
    </ul>
    <ul id="R_2.104.020">
        <li id="R_2.104.020ab">evaṃ bruvāṇaṃ bharataḥ kausalyāsutam abravīt</li>
        <li id="R_2.104.020cd">tejasādityasaṃkāśaṃ pratipaccandradarśanam</li>
    </ul>
    <ul id="R_2.104.021">
        <li id="R_2.104.021ab">adhirohārya pādābhyāṃ pāduke hemabhūṣite</li>
        <li id="R_2.104.021cd">ete hi sarvalokasya yogakṣemaṃ vidhāsyataḥ</li>
    </ul>
    <ul id="R_2.104.022">
        <li id="R_2.104.022ab">so 'dhiruhya naravyāghraḥ pāduke hy avaruhya ca</li>
        <li id="R_2.104.022cd">prāyacchat sumahātejā bharatāya mahātmane</li>
    </ul>
    <ul id="R_2.104.023">
        <li id="R_2.104.023ab">
            <seg type="pāda" n="a">sa pāduke te bharataḥ pratāpavān</seg>
            <seg type="pāda" n="b">svalaṃkṛte saṃparigṛhya dharmavit</seg>
        </li>
        <li id="R_2.104.023cd">
            <seg type="pāda" n="c">pradakṣiṇaṃ caiva cakāra rāghavaṃ</seg>
            <seg type="pāda" n="d">cakāra caivottamanāgamūrdhani</seg>
        </li>
    </ul>
    <ul id="R_2.104.024">
        <li id="R_2.104.024ab">
            <seg type="pāda" n="a">athānupūrvyāt pratipūjya taṃ janaṃ</seg>
            <seg type="pāda" n="b">gurūṃś ca mantriprakṛtīs tathānujau</seg>
        </li>
        <li id="R_2.104.024cd">
            <seg type="pāda" n="c">vyasarjayad rāghavavaṃśavardhanaḥ</seg>
            <seg type="pāda" n="d">sthitaḥ svadharme himavān ivācalaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.104.025">
        <li id="R_2.104.025ab">
            <seg type="pāda" n="a">taṃ mātaro bāṣpagṛhītakaṇṭho</seg>
            <seg type="pāda" n="b">duḥkhena nāmantrayituṃ hi śekuḥ</seg>
        </li>
        <li id="R_2.104.025cd">
            <seg type="pāda" n="c">sa tv eva mātṝr abhivādya sarvā</seg>
            <seg type="pāda" n="d">rudan kuṭīṃ svāṃ praviveśa rāmaḥ</seg>
        </li>
    </ul>
    <ul id="R_2.105.001">
        <li id="R_2.105.001ab">tataḥ śirasi kṛtvā tu pāduke bharatas tadā</li>
        <li id="R_2.105.001cd">āruroha rathaṃ hṛṣṭaḥ śatrughnena samanvitaḥ</li>
    </ul>
    <ul id="R_2.105.002">
        <li id="R_2.105.002ab">vasiṣṭho vāmadevaś ca jābāliś ca dṛḍhavrataḥ</li>
        <li id="R_2.105.002cd">agrataḥ prayayuḥ sarve mantriṇo mantrapūjitāḥ</li>
    </ul>
    <ul id="R_2.105.003">
        <li id="R_2.105.003ab">mandākinīṃ nadīṃ ramyāṃ prāṅmukhās te yayus tadā</li>
        <li id="R_2.105.003cd">pradakṣiṇaṃ ca kurvāṇāś citrakūṭaṃ mahāgirim</li>
    </ul>
    <ul id="R_2.105.004">
        <li id="R_2.105.004ab">paśyan dhātusahasrāṇi ramyāṇi vividhāni ca</li>
        <li id="R_2.105.004cd">prayayau tasya pārśvena sasainyo bharatas tadā</li>
    </ul>
    <ul id="R_2.105.005">
        <li id="R_2.105.005ab">adūrāc citrakūṭasya dadarśa bharatas tadā</li>
        <li id="R_2.105.005cd">āśramaṃ yatra sa munir bharadvājaḥ kṛtālayaḥ</li>
    </ul>
    <ul id="R_2.105.006">
        <li id="R_2.105.006ab">sa tam āśramam āgamya bharadvājasya buddhimān</li>
        <li id="R_2.105.006cd">avatīrya rathāt pādau vavande kulanandanaḥ</li>
    </ul>
    <ul id="R_2.105.007">
        <li id="R_2.105.007ab">tato hṛṣṭo bharadvājo bharataṃ vākyam abravīt</li>
        <li id="R_2.105.007cd">api kṛtyaṃ kṛtaṃ tāta rāmeṇa ca samāgatam</li>
    </ul>
    <ul id="R_2.105.008">
        <li id="R_2.105.008ab">evam uktas tu bharato bharadvājena dhīmatā</li>
        <li id="R_2.105.008cd">pratyuvāca bharadvājaṃ bharato dharmavatsalaḥ</li>
    </ul>
    <ul id="R_2.105.009">
        <li id="R_2.105.009ab">sa yācyamāno guruṇā mayā ca dṛḍhavikramaḥ</li>
        <li id="R_2.105.009cd">rāghavaḥ paramaprīto vasiṣṭhaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_2.105.010">
        <li id="R_2.105.010ab">pituḥ pratijñāṃ tām eva pālayiṣyāmi tattvataḥ</li>
        <li id="R_2.105.010cd">caturdaśa hi varṣāṇi ya pratijñā pitur mama</li>
    </ul>
    <ul id="R_2.105.011">
        <li id="R_2.105.011ab">evam ukto mahāprājño vasiṣṭhaḥ pratyuvāca ha</li>
        <li id="R_2.105.011cd">vākyajño vākyakuśalaṃ rāghavaṃ vacanaṃ mahat</li>
    </ul>
    <ul id="R_2.105.012">
        <li id="R_2.105.012ab">ete prayaccha saṃhṛṣṭaḥ pāduke hemabhūṣite</li>
        <li id="R_2.105.012cd">ayodhyāyāṃ mahāprājña yogakṣemakare tava</li>
    </ul>
    <ul id="R_2.105.013">
        <li id="R_2.105.013ab">evam ukto vasiṣṭhena rāghavaḥ prāṅmukhaḥ sthitaḥ</li>
        <li id="R_2.105.013cd">pāduke hemavikṛte mama rājyāya te dadau</li>
    </ul>
    <ul id="R_2.105.014">
        <li id="R_2.105.014ab">nivṛtto 'ham anujñāto rāmeṇa sumahātmanā</li>
        <li id="R_2.105.014cd">ayodhyām eva gacchāmi gṛhītvā pāduke śubhe</li>
    </ul>
    <ul id="R_2.105.015">
        <li id="R_2.105.015ab">etac chrutvā śubhaṃ vākyaṃ bharatasya mahātmanaḥ</li>
        <li id="R_2.105.015cd">bharadvājaḥ śubhataraṃ munir vākyam udāharat</li>
    </ul>
    <ul id="R_2.105.016">
        <li id="R_2.105.016ab">naitac citraṃ naravyāghra śīlavṛttavatāṃ vara</li>
        <li id="R_2.105.016cd">yad āryaṃ tvayi tiṣṭhet tu nimne vṛṣṭim ivodakam</li>
    </ul>
    <ul id="R_2.105.017">
        <li id="R_2.105.017ab">amṛtaḥ sa mahābāhuḥ pitā daśarathas tava</li>
        <li id="R_2.105.017cd">yasya tvam īdṛśaḥ putro dharmātmā dharmavatsalaḥ</li>
    </ul>
    <ul id="R_2.105.018">
        <li id="R_2.105.018ab">tam ṛṣiṃ tu mahātmānam uktavākyaṃ kṛtāñjaliḥ</li>
        <li id="R_2.105.018cd">āmantrayitum ārebhe caraṇāv upagṛhya ca</li>
    </ul>
    <ul id="R_2.105.019">
        <li id="R_2.105.019ab">tataḥ pradakṣiṇaṃ kṛtvā bharadvājaṃ punaḥ punaḥ</li>
        <li id="R_2.105.019cd">bharatas tu yayau śrīmān ayodhyāṃ saha mantribhiḥ</li>
    </ul>
    <ul id="R_2.105.020">
        <li id="R_2.105.020ab">yānaiś ca śakaṭaiś caiva hayaiś nāgaiś ca sā camūḥ</li>
        <li id="R_2.105.020cd">punar nivṛttā vistīrṇā bharatasyānuyāyinī</li>
    </ul>
    <ul id="R_2.105.021">
        <li id="R_2.105.021ab">tatas te yamunāṃ divyāṃ nadīṃ tīrtvormimālinīm</li>
        <li id="R_2.105.021cd">dadṛśus tāṃ punaḥ sarve gaṅgāṃ śivajalāṃ nadīm</li>
    </ul>
    <ul id="R_2.105.022">
        <li id="R_2.105.022ab">tāṃ ramyajalasaṃpūrṇāṃ saṃtīrya saha bāndhavaḥ</li>
        <li id="R_2.105.022cd">śṛṅgaverapuraṃ ramyaṃ praviveśa sasainikaḥ</li>
    </ul>
    <ul id="R_2.105.023">
        <li id="R_2.105.023ab">śṛṅgaverapurād bhūya ayodhyāṃ saṃdadarśa ha</li>
        <li id="R_2.105.023cd">bharato duḥkhasaṃtaptaḥ sārathiṃ cedam abravīt</li>
    </ul>
    <ul id="R_2.105.024">
        <li id="R_2.105.024ab">sārathe paśya vidhvastā ayodhyā na prakāśate</li>
        <li id="R_2.105.024cd">nirākārā nirānandā dīnā pratihatasvanā</li>
    </ul>
    <ul id="R_2.106.001">
        <li id="R_2.106.001ab">snigdhagambhīraghoṣeṇa syandanenopayān prabhuḥ</li>
        <li id="R_2.106.001cd">ayodhyāṃ bharataḥ kṣipraṃ praviveśa mahāyaśāḥ</li>
    </ul>
    <ul id="R_2.106.002">
        <li id="R_2.106.002ab">biḍālolūkacaritām ālīnanaravāraṇām</li>
        <li id="R_2.106.002cd">timirābhyāhatāṃ kālīm aprakāśāṃ niśām iva</li>
    </ul>
    <ul id="R_2.106.003">
        <li id="R_2.106.003ab">rāhuśatroḥ priyāṃ patnīṃ śriyā prajvalitaprabhām</li>
        <li id="R_2.106.003cd">graheṇābhyutthitenaikāṃ rohiṇīm iva pīḍitām</li>
    </ul>
    <ul id="R_2.106.004">
        <li id="R_2.106.004ab">alpoṣṇakṣubdhasalilāṃ gharmottaptavihaṃgamām</li>
        <li id="R_2.106.004cd">līnamīnajhaṣagrāhāṃ kṛśāṃ girinadīm iva</li>
    </ul>
    <ul id="R_2.106.005">
        <li id="R_2.106.005ab">vidhūmām iva hemābhām adhvarāgnisamutthitām</li>
        <li id="R_2.106.005cd">havirabhyukṣitāṃ paścāc chikhāṃ vipralayaṃ gatām</li>
    </ul>
    <ul id="R_2.106.006">
        <li id="R_2.106.006ab">vidhvastakavacāṃ rugṇagajavājirathadhvajām</li>
        <li id="R_2.106.006cd">hatapravīrām āpannāṃ camūm iva mahāhave</li>
    </ul>
    <ul id="R_2.106.007">
        <li id="R_2.106.007ab">saphenāṃ sasvanāṃ bhūtvā sāgarasya samutthitām</li>
        <li id="R_2.106.007cd">praśāntamārutoddhūtāṃ jalormim iva niḥsvanām</li>
    </ul>
    <ul id="R_2.106.008">
        <li id="R_2.106.008ab">tyaktāṃ yajñāyudhaiḥ sarvair abhirūpaiś ca yājakaiḥ</li>
        <li id="R_2.106.008cd">sutyākāle vinirvṛtte vediṃ gataravām iva</li>
    </ul>
    <ul id="R_2.106.009">
        <li id="R_2.106.009ab">goṣṭhamadhye sthitām ārtām acarantīṃ navaṃ tṛṇam</li>
        <li id="R_2.106.009cd">govṛṣeṇa parityaktāṃ gavāṃ patnīm ivotsukām</li>
    </ul>
    <ul id="R_2.106.010">
        <li id="R_2.106.010ab">prabhākarālaiḥ susnigdhaiḥ prajvaladbhir ivottamaiḥ</li>
        <li id="R_2.106.010cd">viyuktāṃ maṇibhir jātyair navāṃ muktāvalīm iva</li>
    </ul>
    <ul id="R_2.106.011">
        <li id="R_2.106.011ab">sahasā calitāṃ sthānān mahīṃ puṇyakṣayād gatām</li>
        <li id="R_2.106.011cd">saṃhṛtadyutivistārāṃ tārām iva divaś cyutām</li>
    </ul>
    <ul id="R_2.106.012">
        <li id="R_2.106.012ab">puṣpanaddhāṃ vasantānte mattabhramaraśālinīm</li>
        <li id="R_2.106.012cd">drutadāvāgnivipluṣṭāṃ klāntāṃ vanalatām iva</li>
    </ul>
    <ul id="R_2.106.013">
        <li id="R_2.106.013ab">saṃmūḍhanigamāṃ sarvāṃ saṃkṣiptavipaṇāpaṇām</li>
        <li id="R_2.106.013cd">pracchannaśaśinakṣatrāṃ dyām ivāmbudharair vṛtām</li>
    </ul>
    <ul id="R_2.106.014">
        <li id="R_2.106.014ab">kṣīṇapānottamair bhinnaiḥ śarāvair abhisaṃvṛtām</li>
        <li id="R_2.106.014cd">hataśauṇḍām ivākāśe pānabhūmim asaṃskṛtām</li>
    </ul>
    <ul id="R_2.106.015">
        <li id="R_2.106.015ab">vṛkṇabhūmitalāṃ nimnāṃ vṛkṇapātraiḥ samāvṛtām</li>
        <li id="R_2.106.015cd">upayuktodakāṃ bhagnāṃ prapāṃ nipatitām iva</li>
    </ul>
    <ul id="R_2.106.016">
        <li id="R_2.106.016ab">vipulāṃ vitatāṃ caiva yuktapāśāṃ tarasvinām</li>
        <li id="R_2.106.016cd">bhūmau bāṇair viniṣkṛttāṃ patitāṃ jyām ivāyudhāt</li>
    </ul>
    <ul id="R_2.106.017">
        <li id="R_2.106.017ab">sahasā yuddhaśauṇḍena hayāroheṇa vāhitām</li>
        <li id="R_2.106.017cd">nikṣiptabhāṇḍām utsṛṣṭāṃ kiśorīm iva durbalām</li>
    </ul>
    <ul id="R_2.106.018">
        <li id="R_2.106.018ab">prāvṛṣi pravigāḍhāyāṃ praviṣṭasyābhra maṇḍalam</li>
        <li id="R_2.106.018cd">pracchannāṃ nīlajīmūtair bhāskarasya prabhām iva</li>
    </ul>
    <ul id="R_2.106.019">
        <li id="R_2.106.019ab">bharatas tu rathasthaḥ sañ śrīmān daśarathātmajaḥ</li>
        <li id="R_2.106.019cd">vāhayantaṃ rathaśreṣṭhaṃ sārathiṃ vākyam abravīt</li>
    </ul>
    <ul id="R_2.106.020">
        <li id="R_2.106.020ab">kiṃ nu khalv adya gambhīro mūrchito na niśamyate</li>
        <li id="R_2.106.020cd">yathāpuram ayodhyāyāṃ gītavāditraniḥsvanaḥ</li>
    </ul>
    <ul id="R_2.106.021">
        <li id="R_2.106.021ab">vāruṇīmadagandhāś ca mālyagandhaś ca mūrchitaḥ</li>
        <li id="R_2.106.021cd">dhūpitāgarugandhaś ca na pravāti samantataḥ</li>
    </ul>
    <ul id="R_2.106.022">
        <li id="R_2.106.022ab">yānapravaraghoṣaś ca snigdhaś ca hayaniḥsvanaḥ</li>
        <li id="R_2.106.022cd">pramattagajanādaś ca mahāṃś ca rathaniḥsvanaḥ</li>
        <li id="R_2.106.022ef">nedānīṃ śrūyate puryām asyāṃ rāme vivāsite</li>
    </ul>
    <ul id="R_2.106.023">
        <li id="R_2.106.023ab">taruṇaiś cāru veṣaiś ca narair unnatagāmibhiḥ</li>
        <li id="R_2.106.023cd">saṃpatadbhir ayodhyāyāṃ na vibhānti mahāpathāḥ</li>
    </ul>
    <ul id="R_2.106.024">
        <li id="R_2.106.024ab">evaṃ bahuvidhaṃ jalpan viveśa vasatiṃ pituḥ</li>
        <li id="R_2.106.024cd">tena hīnāṃ narendreṇa siṃhahīnāṃ guhām iva</li>
    </ul>
    <ul id="R_2.107.001">
        <li id="R_2.107.001ab">tato nikṣipya mātṝḥ sa ayodhyāyāṃ dṛḍhavrataḥ</li>
        <li id="R_2.107.001cd">bharataḥ śokasaṃtapto gurūn idam athābravīt</li>
    </ul>
    <ul id="R_2.107.002">
        <li id="R_2.107.002ab">nandigrāmaṃ gamiṣyāmi sarvān āmantraye 'dya vaḥ</li>
        <li id="R_2.107.002cd">tatra duḥkham idaṃ sarvaṃ sahiṣye rāghavaṃ vinā</li>
    </ul>
    <ul id="R_2.107.003">
        <li id="R_2.107.003ab">gataś ca hi divaṃ rājā vanasthaś ca gurur mama</li>
        <li id="R_2.107.003cd">rāmaṃ pratīkṣe rājyāya sa hi rājā mahāyaśāḥ</li>
    </ul>
    <ul id="R_2.107.004">
        <li id="R_2.107.004ab">etac chrutvā śubhaṃ vākyaṃ bharatasya mahātmanaḥ</li>
        <li id="R_2.107.004cd">abruvan mantriṇaḥ sarve vasiṣṭhaś ca purohitaḥ</li>
    </ul>
    <ul id="R_2.107.005">
        <li id="R_2.107.005ab">sadṛśaṃ ślāghanīyaṃ ca yad uktaṃ bharata tvayā</li>
        <li id="R_2.107.005cd">vacanaṃ bhrātṛvātsalyād anurūpaṃ tavaiva tat</li>
    </ul>
    <ul id="R_2.107.006">
        <li id="R_2.107.006ab">nityaṃ te bandhulubdhasya tiṣṭhato bhrātṛsauhṛde</li>
        <li id="R_2.107.006cd">āryamārgaṃ prapannasya nānumanyeta kaḥ pumān</li>
    </ul>
    <ul id="R_2.107.007">
        <li id="R_2.107.007ab">mantriṇāṃ vacanaṃ śrutvā yathābhilaṣitaṃ priyam</li>
        <li id="R_2.107.007cd">abravīt sārathiṃ vākyaṃ ratho me yujyatām iti</li>
    </ul>
    <ul id="R_2.107.008">
        <li id="R_2.107.008ab">prahṛṣṭavadanaḥ sarvā mātṝḥ samabhivādya saḥ</li>
        <li id="R_2.107.008cd">āruroha rathaṃ śrīmāñ śatrughnena samanvitaḥ</li>
    </ul>
    <ul id="R_2.107.009">
        <li id="R_2.107.009ab">āruhya tu rathaṃ śīghraṃ śatrughnabharatāv ubhau</li>
        <li id="R_2.107.009cd">yayatuḥ paramaprītau vṛtau mantripurohitaiḥ</li>
    </ul>
    <ul id="R_2.107.010">
        <li id="R_2.107.010ab">agrato puravas tatra vasiṣṭha pramukhā dvijāḥ</li>
        <li id="R_2.107.010cd">prayayuḥ prāṅmukhāḥ sarve nandigrāmo yato 'bhavat</li>
    </ul>
    <ul id="R_2.107.011">
        <li id="R_2.107.011ab">balaṃ ca tad anāhūtaṃ gajāśvarathasaṃkulam</li>
        <li id="R_2.107.011cd">prayayau bharate yāte sarve ca puravāsinaḥ</li>
    </ul>
    <ul id="R_2.107.012">
        <li id="R_2.107.012ab">rathasthaḥ sa tu dharmātmā bharato bhrātṛvatsalaḥ</li>
        <li id="R_2.107.012cd">nandigrāmaṃ yayau tūrṇaṃ śirasy ādhāya pāduke</li>
    </ul>
    <ul id="R_2.107.013">
        <li id="R_2.107.013ab">tatas tu bharataḥ kṣipraṃ nandigrāmaṃ praviśya saḥ</li>
        <li id="R_2.107.013cd">avatīrya rathāt tūrṇaṃ gurūn idam uvāca ha</li>
    </ul>
    <ul id="R_2.107.014">
        <li id="R_2.107.014ab">etad rājyaṃ mama bhrātrā dattaṃ saṃnyāsavat svayam</li>
        <li id="R_2.107.014cd">yogakṣemavahe ceme pāduke hemabhūṣite</li>
        <li id="R_2.107.014ef">tam imaṃ pālayiṣyāmi rāghavāgamanaṃ prati</li>
    </ul>
    <ul id="R_2.107.015">
        <li id="R_2.107.015ab">kṣipraṃ saṃyojayitvā tu rāghavasya punaḥ svayam</li>
        <li id="R_2.107.015cd">caraṇau tau tu rāmasya drakṣyāmi sahapādukau</li>
    </ul>
    <ul id="R_2.107.016">
        <li id="R_2.107.016ab">tato nikṣiptabhāro 'haṃ rāghaveṇa samāgataḥ</li>
        <li id="R_2.107.016cd">nivedya gurave rājyaṃ bhajiṣye guruvṛttitām</li>
    </ul>
    <ul id="R_2.107.017">
        <li id="R_2.107.017ab">rāghavāya ca saṃnyāsaṃ dattveme varapāduke</li>
        <li id="R_2.107.017cd">rājyaṃ cedam ayodhyāṃ ca dhūtapāpo bhavāmi ca</li>
    </ul>
    <ul id="R_2.107.018">
        <li id="R_2.107.018ab">abhiṣikte tu kākutsthe prahṛṣṭamudite jane</li>
        <li id="R_2.107.018cd">prītir mama yaśaś caiva bhaved rājyāc caturguṇam</li>
    </ul>
    <ul id="R_2.107.019">
        <li id="R_2.107.019ab">evaṃ tu vilapan dīno bharataḥ sa mahāyaśāḥ</li>
        <li id="R_2.107.019cd">nandigrāme 'karod rājyaṃ duḥkhito mantribhiḥ saha</li>
    </ul>
    <ul id="R_2.107.020">
        <li id="R_2.107.020ab">sa valkalajaṭādhārī muniveṣadharaḥ prabhuḥ</li>
        <li id="R_2.107.020cd">nandigrāme 'vasad vīraḥ sasainyo bharatas tadā</li>
    </ul>
    <ul id="R_2.107.021">
        <li id="R_2.107.021ab">rāmāgamanam ākāṅkṣan bharato bhrātṛvatsalaḥ</li>
        <li id="R_2.107.021cd">bhrātur vacanakārī ca pratijñāpāragas tadā</li>
    </ul>
    <ul id="R_2.107.022">
        <li id="R_2.107.022ab">pāduke tv abhiṣicyātha nandigrāme 'vasat tadā</li>
        <li id="R_2.107.022cd">bharataḥ śāsanaṃ sarvaṃ pādukābhyāṃ nyavedayat</li>
    </ul>
    <ul id="R_2.108.001">
        <li id="R_2.108.001ab">pratiprayāte bharate vasan rāmas tapovane</li>
        <li id="R_2.108.001cd">lakṣayām āsa sodvegam athautsukyaṃ tapasvinām</li>
    </ul>
    <ul id="R_2.108.002">
        <li id="R_2.108.002ab">ye tatra citrakūṭasya purastāt tāpasāśrame</li>
        <li id="R_2.108.002cd">rāmam āśritya niratās tān alakṣayad utsukān</li>
    </ul>
    <ul id="R_2.108.003">
        <li id="R_2.108.003ab">nayanair bhṛkuṭībhiś ca rāmaṃ nirdiśya śaṅkitāḥ</li>
        <li id="R_2.108.003cd">anyonyam upajalpantaḥ śanaiś cakrur mithaḥ kathāḥ</li>
    </ul>
    <ul id="R_2.108.004">
        <li id="R_2.108.004ab">teṣām autsukyam ālakṣya rāmas tv ātmani śaṅkitaḥ</li>
        <li id="R_2.108.004cd">kṛtāñjalir uvācedam ṛṣiṃ kulapatiṃ tataḥ</li>
    </ul>
    <ul id="R_2.108.005">
        <li id="R_2.108.005ab">na kaccid bhagavan kiṃ cit pūrvavṛttam idaṃ mayi</li>
        <li id="R_2.108.005cd">dṛśyate vikṛtaṃ yena vikriyante tapasvinaḥ</li>
    </ul>
    <ul id="R_2.108.006">
        <li id="R_2.108.006ab">pramādāc caritaṃ kaccit kiṃ cin nāvarajasya me</li>
        <li id="R_2.108.006cd">lakṣmaṇasyarṣibhir dṛṣṭaṃ nānurūpam ivātmanaḥ</li>
    </ul>
    <ul id="R_2.108.007">
        <li id="R_2.108.007ab">kaccic chuśrūṣamāṇā vaḥ śuśrūṣaṇaparā mayi</li>
        <li id="R_2.108.007cd">pramadābhyucitāṃ vṛttiṃ sītā yuktaṃ na vartate</li>
    </ul>
    <ul id="R_2.108.008">
        <li id="R_2.108.008ab">atharṣir jarayā vṛddhas tapasā ca jarāṃ gataḥ</li>
        <li id="R_2.108.008cd">vepamāna ivovāca rāmaṃ bhūtadayāparam</li>
    </ul>
    <ul id="R_2.108.009">
        <li id="R_2.108.009ab">kutaḥ kalyāṇasattvāyāḥ kalyāṇābhirates tathā</li>
        <li id="R_2.108.009cd">calanaṃ tāta vaidehyās tapasviṣu viśeṣataḥ</li>
    </ul>
    <ul id="R_2.108.010">
        <li id="R_2.108.010ab">tvannimittam idaṃ tāvat tāpasān prati vartate</li>
        <li id="R_2.108.010cd">rakṣobhyas tena saṃvignāḥ kathayanti mithaḥ kathāḥ</li>
    </ul>
    <ul id="R_2.108.011">
        <li id="R_2.108.011ab">rāvaṇāvarajaḥ kaś cit kharo nāmeha rākṣasaḥ</li>
        <li id="R_2.108.011cd">utpāṭya tāpasān sarvāñ janasthānaniketanān</li>
    </ul>
    <ul id="R_2.108.012">
        <li id="R_2.108.012ab">dhṛṣṭaś ca jitakāśī ca nṛśaṃsaḥ puruṣādakaḥ</li>
        <li id="R_2.108.012cd">avaliptaś ca pāpaś ca tvāṃ ca tāta na mṛṣyate</li>
    </ul>
    <ul id="R_2.108.013">
        <li id="R_2.108.013ab">tvaṃ yadā prabhṛti hy asminn āśrame tāta vartase</li>
        <li id="R_2.108.013cd">tadā prabhṛti rakṣāṃsi viprakurvanti tāpasān</li>
    </ul>
    <ul id="R_2.108.014">
        <li id="R_2.108.014ab">darśayanti hi bībhatsaiḥ krūrair bhīṣaṇakair api</li>
        <li id="R_2.108.014cd">nānā rūpair virūpaiś ca rūpair asukhadarśanaiḥ</li>
    </ul>
    <ul id="R_2.108.015">
        <li id="R_2.108.015ab">apraśastair aśucibhiḥ saṃprayojya ca tāpasān</li>
        <li id="R_2.108.015cd">pratighnanty aparān kṣipram anāryāḥ purataḥ sthitaḥ</li>
    </ul>
    <ul id="R_2.108.016">
        <li id="R_2.108.016ab">teṣu teṣv āśramasthāneṣv abuddham avalīya ca</li>
        <li id="R_2.108.016cd">ramante tāpasāṃs tatra nāśayanto 'lpacetasaḥ</li>
    </ul>
    <ul id="R_2.108.017">
        <li id="R_2.108.017ab">apakṣipanti srugbhāṇḍān agnīn siñcanti vāriṇā</li>
        <li id="R_2.108.017cd">kalaśāṃś ca pramṛdnanti havane samupasthite</li>
    </ul>
    <ul id="R_2.108.018">
        <li id="R_2.108.018ab">tair durātmabhir āviṣṭān āśramān prajihāsavaḥ</li>
        <li id="R_2.108.018cd">gamanāyānyadeśasya codayanty ṛṣayo 'dya mām</li>
    </ul>
    <ul id="R_2.108.019">
        <li id="R_2.108.019ab">tat purā rāma śārīrām upahiṃsāṃ tapasviṣu</li>
        <li id="R_2.108.019cd">darśayati hi duṣṭās te tyakṣyāma imam āśramam</li>
    </ul>
    <ul id="R_2.108.020">
        <li id="R_2.108.020ab">bahumūlaphalaṃ citram avidūrād ito vanam</li>
        <li id="R_2.108.020cd">purāṇāśramam evāhaṃ śrayiṣye sagaṇaḥ punaḥ</li>
    </ul>
    <ul id="R_2.108.021">
        <li id="R_2.108.021ab">kharas tvayy api cāyuktaṃ purā tāta pravartate</li>
        <li id="R_2.108.021cd">sahāsmābhir ito gaccha yadi buddhiḥ pravartate</li>
    </ul>
    <ul id="R_2.108.022">
        <li id="R_2.108.022ab">sakalatrasya saṃdeho nityaṃ yat tasya rāghava</li>
        <li id="R_2.108.022cd">samarthasyāpi hi sato vāso duḥkha ihādya te</li>
    </ul>
    <ul id="R_2.108.023">
        <li id="R_2.108.023ab">ity uktavantaṃ rāmas taṃ rājaputras tapasvinam</li>
        <li id="R_2.108.023cd">na śaśākottarair vākyair avaroddhuṃ samutsukam</li>
    </ul>
    <ul id="R_2.108.024">
        <li id="R_2.108.024ab">abhinandya samāpṛcchya samādhāya ca rāghavam</li>
        <li id="R_2.108.024cd">sa jagāmāśramaṃ tyaktvā kulaiḥ kulapatiḥ saha</li>
    </ul>
    <ul id="R_2.108.025">
        <li id="R_2.108.025ab">
            <seg type="pāda" n="a">rāmaḥ saṃsādhya tv ṛṣigaṇam anugamanād</seg>
            <seg type="pāda" n="b">deśāt tasmāccit kulapatim abhivādyarṣim</seg>
        </li>
        <li id="R_2.108.025cd">
            <seg type="pāda" n="c">samyakprītais tair anumata upadiṣṭārthaḥ</seg>
            <seg type="pāda" n="d">puṇyaṃ vāsāya svanilayam upasaṃpede</seg>
        </li>
    </ul>
    <ul id="R_2.108.026">
        <li id="R_2.108.026ab">
            <seg type="pāda" n="a">āśramaṃ tv ṛṣivirahitaṃ prabhuḥ</seg>
            <seg type="pāda" n="b">kṣaṇam api na jahau sa rāghavaḥ</seg>
        </li>
        <li id="R_2.108.026cd">
            <seg type="pāda" n="c">rāghavaṃ hi satatam anugatās</seg>
            <seg type="pāda" n="d">tāpasāś carṣicaritadhṛtaguṇāḥ</seg>
        </li>
    </ul>
    <ul id="R_2.109.001">
        <li id="R_2.109.001ab">rāghavas tv apayāteṣu tapasviṣu vicintayan</li>
        <li id="R_2.109.001cd">na tatrārocayad vāsaṃ kāraṇair bahubhis tadā</li>
    </ul>
    <ul id="R_2.109.002">
        <li id="R_2.109.002ab">iha me bharato dṛṣṭo mātaraś ca sanāgarāḥ</li>
        <li id="R_2.109.002cd">sā ca me smṛtir anveti tān nityam anuśocataḥ</li>
    </ul>
    <ul id="R_2.109.003">
        <li id="R_2.109.003ab">skandhāvāraniveśena tena tasya mahātmanaḥ</li>
        <li id="R_2.109.003cd">hayahastikarīṣaiś ca upamardaḥ kṛto bhṛśam</li>
    </ul>
    <ul id="R_2.109.004">
        <li id="R_2.109.004ab">tasmād anyatra gacchāma iti saṃcintya rāghavaḥ</li>
        <li id="R_2.109.004cd">prātiṣṭhata sa vaidehyā lakṣmaṇena ca saṃgataḥ</li>
    </ul>
    <ul id="R_2.109.005">
        <li id="R_2.109.005ab">so 'trer āśramam āsādya taṃ vavande mahāyaśāḥ</li>
        <li id="R_2.109.005cd">taṃ cāpi bhagavān atriḥ putravat pratyapadyata</li>
    </ul>
    <ul id="R_2.109.006">
        <li id="R_2.109.006ab">svayam ātithyam ādiśya sarvam asya susatkṛtam</li>
        <li id="R_2.109.006cd">saumitriṃ ca mahābhāgāṃ sītāṃ ca samasāntvayat</li>
    </ul>
    <ul id="R_2.109.007">
        <li id="R_2.109.007ab">patnīṃ ca tam anuprāptāṃ vṛddhām āmantrya satkṛtām</li>
        <li id="R_2.109.007cd">sāntvayām āsa dharmajñaḥ sarvabhūtahite rataḥ</li>
    </ul>
    <ul id="R_2.109.008">
        <li id="R_2.109.008ab">anasūyāṃ mahābhāgāṃ tāpasīṃ dharmacāriṇīm</li>
        <li id="R_2.109.008cd">pratigṛhṇīṣva vaidehīm abravīd ṛṣisattamaḥ</li>
    </ul>
    <ul id="R_2.109.009">
        <li id="R_2.109.009ab">rāmāya cācacakṣe tāṃ tāpasīṃ dharmacāriṇīm</li>
        <li id="R_2.109.009cd">daśa varṣāṇy anāvṛṣṭyā dagdhe loke nirantaram</li>
    </ul>
    <ul id="R_2.109.010">
        <li id="R_2.109.010ab">yayā mūlaphale sṛṣṭe jāhnavī ca pravartitā</li>
        <li id="R_2.109.010cd">ugreṇa tapasā yuktā niyamaiś cāpy alaṃkṛtā</li>
    </ul>
    <ul id="R_2.109.011">
        <li id="R_2.109.011ab">daśavarṣasahasrāṇi yayā taptaṃ mahat tapaḥ</li>
        <li id="R_2.109.011cd">anasūyāvratais tāta pratyūhāś ca nibarhitāḥ</li>
    </ul>
    <ul id="R_2.109.012">
        <li id="R_2.109.012ab">devakāryanimittaṃ ca yayā saṃtvaramāṇayā</li>
        <li id="R_2.109.012cd">daśarātraṃ kṛtvā rātriḥ seyaṃ māteva te 'nagha</li>
    </ul>
    <ul id="R_2.109.013">
        <li id="R_2.109.013ab">tām imāṃ sarvabhūtānāṃ namaskāryāṃ yaśasvinīm</li>
        <li id="R_2.109.013cd">abhigacchatu vaidehī vṛddhām akrodhanāṃ sadā</li>
    </ul>
    <ul id="R_2.109.014">
        <li id="R_2.109.014ab">evaṃ bruvāṇaṃ tam ṛṣiṃ tathety uktvā sa rāghavaḥ</li>
        <li id="R_2.109.014cd">sītām uvāca dharmajñām idaṃ vacanam uttamam</li>
    </ul>
    <ul id="R_2.109.015">
        <li id="R_2.109.015ab">rājaputri śrutaṃ tv etan muner asya samīritam</li>
        <li id="R_2.109.015cd">śreyo 'rtham ātmanaḥ śīghram abhigaccha tapasvinīm</li>
    </ul>
    <ul id="R_2.109.016">
        <li id="R_2.109.016ab">anasūyeti yā loke karmabhiḥ kyātim āgatā</li>
        <li id="R_2.109.016cd">tāṃ śīghram abhigaccha tvam abhigamyāṃ tapasvinīm</li>
    </ul>
    <ul id="R_2.109.017">
        <li id="R_2.109.017ab">sītā tv etad vacaḥ śrutvā rāghavasya hitaiṣiṇī</li>
        <li id="R_2.109.017cd">tām atripatnīṃ dharmajñām abhicakrāma maithilī</li>
    </ul>
    <ul id="R_2.109.018">
        <li id="R_2.109.018ab">śithilāṃ valitāṃ vṛddhāṃ jarāpāṇḍuramūrdhajām</li>
        <li id="R_2.109.018cd">satataṃ vepamānāṅgīṃ pravāte kadalī yathā</li>
    </ul>
    <ul id="R_2.109.019">
        <li id="R_2.109.019ab">tāṃ tu sītā mahābhāgām anasūyāṃ pativratām</li>
        <li id="R_2.109.019cd">abhyavādayad avyagrā svaṃ nāma samudāharat</li>
    </ul>
    <ul id="R_2.109.020">
        <li id="R_2.109.020ab">abhivādya ca vaidehī tāpasīṃ tām aninditām</li>
        <li id="R_2.109.020cd">baddhāñjalipuṭā hṛṣṭā paryapṛcchad anāmayam</li>
    </ul>
    <ul id="R_2.109.021">
        <li id="R_2.109.021ab">tataḥ sītāṃ mahābhāgāṃ dṛṣṭvā tāṃ dharmacāriṇīm</li>
        <li id="R_2.109.021cd">sāntvayanty abravīd dhṛṣṭā diṣṭyā dharmam avekṣase</li>
    </ul>
    <ul id="R_2.109.022">
        <li id="R_2.109.022ab">tyaktvā jñātijanaṃ sīte mānam ṛddhiṃ ca mānini</li>
        <li id="R_2.109.022cd">avaruddhaṃ vane rāmaṃ diṣṭyā tvam anugacchasi</li>
    </ul>
    <ul id="R_2.109.023">
        <li id="R_2.109.023ab">nagarastho vanastho vā pāpo vā yadi vāśubhaḥ</li>
        <li id="R_2.109.023cd">yāsāṃ strīṇāṃ priyo bhartā tāsāṃ lokā mahodayāḥ</li>
    </ul>
    <ul id="R_2.109.024">
        <li id="R_2.109.024ab">duḥśīlaḥ kāmavṛtto vā dhanair vā parivarjitaḥ</li>
        <li id="R_2.109.024cd">strīṇām ārya svabhāvānāṃ paramaṃ daivataṃ patiḥ</li>
    </ul>
    <ul id="R_2.109.025">
        <li id="R_2.109.025ab">nāto viśiṣṭaṃ paśyāmi bāndhavaṃ vimṛśanty aham</li>
        <li id="R_2.109.025cd">sarvatra yogyaṃ vaidehi tapaḥ kṛtam ivāvyayam</li>
    </ul>
    <ul id="R_2.109.026">
        <li id="R_2.109.026ab">na tv evam avagacchanti guṇa doṣam asat striyaḥ</li>
        <li id="R_2.109.026cd">kāmavaktavyahṛdayā bhartṛnāthāś caranti yāḥ</li>
    </ul>
    <ul id="R_2.109.027">
        <li id="R_2.109.027ab">prāpnuvanty ayaśaś caiva dharmabhraṃśaṃ ca maithili</li>
        <li id="R_2.109.027cd">akārya vaśam āpannāḥ striyo yāḥ khalu tad vidhāḥ</li>
    </ul>
    <ul id="R_2.109.028">
        <li id="R_2.109.028ab">tvadvidhās tu guṇair yuktā dṛṣṭalokaparāvarāḥ</li>
        <li id="R_2.109.028cd">striyaḥ svarge cariṣyanti yathā puṇyakṛtas tathā</li>
    </ul>
    <ul id="R_2.110.001">
        <li id="R_2.110.001ab">sā tv evam uktā vaidehī anasūyān asūyayā</li>
        <li id="R_2.110.001cd">pratipūjya vaco mandaṃ pravaktum upacakrame</li>
    </ul>
    <ul id="R_2.110.002">
        <li id="R_2.110.002ab">naitad āścaryam āryāyā yan māṃ tvam anubhāṣase</li>
        <li id="R_2.110.002cd">viditaṃ tu mamāpy etad yathā nāryāḥ patir guruḥ</li>
    </ul>
    <ul id="R_2.110.003">
        <li id="R_2.110.003ab">yady apy eṣa bhaved bhartā mamārye vṛttavarjitaḥ</li>
        <li id="R_2.110.003cd">advaidham upavartavyas tathāpy eṣa mayā bhavet</li>
    </ul>
    <ul id="R_2.110.004">
        <li id="R_2.110.004ab">kiṃ punar yo guṇaślāghyaḥ sānukrośo jitendriyaḥ</li>
        <li id="R_2.110.004cd">sthirānurāgo dharmātmā mātṛvartī pitṛ priyaḥ</li>
    </ul>
    <ul id="R_2.110.005">
        <li id="R_2.110.005ab">yāṃ vṛttiṃ vartate rāmaḥ kausalyāyāṃ mahābalaḥ</li>
        <li id="R_2.110.005cd">tām eva nṛpanārīṇām anyāsām api vartate</li>
    </ul>
    <ul id="R_2.110.006">
        <li id="R_2.110.006ab">sakṛd dṛṣṭāsv api strīṣu nṛpeṇa nṛpavatsalaḥ</li>
        <li id="R_2.110.006cd">mātṛvad vartate vīro mānam utsṛjya dharmavit</li>
    </ul>
    <ul id="R_2.110.007">
        <li id="R_2.110.007ab">āgacchantyāś ca vijanaṃ vanam evaṃ bhayāvaham</li>
        <li id="R_2.110.007cd">samāhitaṃ hi me śvaśrvā hṛdaye yat sthitaṃ mama</li>
    </ul>
    <ul id="R_2.110.008">
        <li id="R_2.110.008ab">prāṇipradānakāle ca yat purā tv agnisaṃnidhau</li>
        <li id="R_2.110.008cd">anuśiṣṭā jananyāsmi vākyaṃ tad api me dhṛtam</li>
    </ul>
    <ul id="R_2.110.009">
        <li id="R_2.110.009ab">navīkṛtaṃ tu tat sarvaṃ vākyais te dharmacāriṇi</li>
        <li id="R_2.110.009cd">patiśuśrūṣaṇān nāryās tapo nānyad vidhīyate</li>
    </ul>
    <ul id="R_2.110.010">
        <li id="R_2.110.010ab">sāvitrī patiśuśrūṣāṃ kṛtvā svarge mahīyate</li>
        <li id="R_2.110.010cd">tathā vṛttiś ca yātā tvaṃ patiśuśrūṣayā divam</li>
    </ul>
    <ul id="R_2.110.011">
        <li id="R_2.110.011ab">variṣṭhā sarvanārīṇām eṣā ca divi devatā</li>
        <li id="R_2.110.011cd">rohiṇī ca vinā candraṃ muhūrtam api dṛśyate</li>
    </ul>
    <ul id="R_2.110.012">
        <li id="R_2.110.012ab">evaṃvidhāś ca pravarāḥ striyo bhartṛdṛḍhavratāḥ</li>
        <li id="R_2.110.012cd">devaloke mahīyante puṇyena svena karmaṇā</li>
    </ul>
    <ul id="R_2.110.013">
        <li id="R_2.110.013ab">tato 'nasūyā saṃhṛṣṭā śrutvoktaṃ sītayā vacaḥ</li>
        <li id="R_2.110.013cd">śirasy āghrāya covāca maithilīṃ harṣayanty uta</li>
    </ul>
    <ul id="R_2.110.014">
        <li id="R_2.110.014ab">niyamair vividhair āptaṃ tapo hi mahad asti me</li>
        <li id="R_2.110.014cd">tat saṃśritya balaṃ sīte chandaye tvāṃ śucivrate</li>
    </ul>
    <ul id="R_2.110.015">
        <li id="R_2.110.015ab">upapannaṃ ca yuktaṃ ca vacanaṃ tava maithili</li>
        <li id="R_2.110.015cd">prītā cāsmy ucitaṃ kiṃ te karavāṇi bravīhi me</li>
        <li id="R_2.110.015ef">kṛtam ity abravīt sītā tapobalasamanvitām</li>
    </ul>
    <ul id="R_2.110.016">
        <li id="R_2.110.016ab">sā tv evam uktā dharmajñā tayā prītatarābhavat</li>
        <li id="R_2.110.016cd">saphalaṃ ca praharṣaṃ te hanta sīte karomy aham</li>
    </ul>
    <ul id="R_2.110.017">
        <li id="R_2.110.017ab">idaṃ divyaṃ varaṃ mālyaṃ vastram ābharaṇāni ca</li>
        <li id="R_2.110.017cd">aṅgarāgaṃ ca vaidehi mahārham anulepanam</li>
    </ul>
    <ul id="R_2.110.018">
        <li id="R_2.110.018ab">mayā dattam idaṃ sīte tava gātrāṇi śobhayet</li>
        <li id="R_2.110.018cd">anurūpam asaṃkliṣṭaṃ nityam eva bhaviṣyati</li>
    </ul>
    <ul id="R_2.110.019">
        <li id="R_2.110.019ab">aṅgarāgeṇa divyena liptāṅgī janakātmaje</li>
        <li id="R_2.110.019cd">śobhayiṣyāmi bhartāraṃ yathā śrīr viṣṇum avyayam</li>
    </ul>
    <ul id="R_2.110.020">
        <li id="R_2.110.020ab">sā vastram aṅgarāgaṃ ca bhūṣaṇāni srajas tathā</li>
        <li id="R_2.110.020cd">maithilī pratijagrāha prītidānam anuttamam</li>
    </ul>
    <ul id="R_2.110.021">
        <li id="R_2.110.021ab">pratigṛhya ca tat sītā prītidānaṃ yaśasvinī</li>
        <li id="R_2.110.021cd">śliṣṭāñjalipuṭā dhīrā samupāsta tapodhanām</li>
    </ul>
    <ul id="R_2.110.022">
        <li id="R_2.110.022ab">tathā sītām upāsīnām anasūyā dṛḍhavratā</li>
        <li id="R_2.110.022cd">vacanaṃ praṣṭum ārebhe kathāṃ kāṃ cid anupriyām</li>
    </ul>
    <ul id="R_2.110.023">
        <li id="R_2.110.023ab">svayaṃvare kila prāptā tvam anena yaśasvinā</li>
        <li id="R_2.110.023cd">rāghaveṇeti me sīte kathā śrutim upāgatā</li>
    </ul>
    <ul id="R_2.110.024">
        <li id="R_2.110.024ab">tāṃ kathāṃ śrotum icchāmi vistareṇa ca maithili</li>
        <li id="R_2.110.024cd">yathānubhūtaṃ kārtsnyena tan me tvaṃ vaktum arhasi</li>
    </ul>
    <ul id="R_2.110.025">
        <li id="R_2.110.025ab">evam uktā tu sā sītā tāṃ tato dharmacāriṇīm</li>
        <li id="R_2.110.025cd">śrūyatām iti coktvā vai kathayām āsa tāṃ kathām</li>
    </ul>
    <ul id="R_2.110.026">
        <li id="R_2.110.026ab">mithilādhipatir vīro janako nāma dharmavit</li>
        <li id="R_2.110.026cd">kṣatradharmaṇy abhirato nyāyataḥ śāsti medinīm</li>
    </ul>
    <ul id="R_2.110.027">
        <li id="R_2.110.027ab">tasya lāṅgalahastasya karṣataḥ kṣetramaṇḍalam</li>
        <li id="R_2.110.027cd">ahaṃ kilotthitā bhittvā jagatīṃ nṛpateḥ sutā</li>
    </ul>
    <ul id="R_2.110.028">
        <li id="R_2.110.028ab">sa māṃ dṛṣṭvā narapatir muṣṭivikṣepatatparaḥ</li>
        <li id="R_2.110.028cd">pāṃśu guṇṭhita sarvāṅgīṃ vismito janako 'bhavat</li>
    </ul>
    <ul id="R_2.110.029">
        <li id="R_2.110.029ab">anapatyena ca snehād aṅkam āropya ca svayam</li>
        <li id="R_2.110.029cd">mameyaṃ tanayety uktvā sneho mayi nipātitaḥ</li>
    </ul>
    <ul id="R_2.110.030">
        <li id="R_2.110.030ab">antarikṣe ca vāg uktāpratimā mānuṣī kila</li>
        <li id="R_2.110.030cd">evam etan narapate dharmeṇa tanayā tava</li>
    </ul>
    <ul id="R_2.110.031">
        <li id="R_2.110.031ab">tataḥ prahṛṣṭo dharmātmā pitā me mithilādhipaḥ</li>
        <li id="R_2.110.031cd">avāpto vipulām ṛddhiṃ mām avāpya narādhipaḥ</li>
    </ul>
    <ul id="R_2.110.032">
        <li id="R_2.110.032ab">dattvā cāsmīṣṭavad devyai jyeṣṭhāyai puṇyakarmaṇā</li>
        <li id="R_2.110.032cd">tayā saṃbhāvitā cāsmi snigdhayā mātṛsauhṛdāt</li>
    </ul>
    <ul id="R_2.110.033">
        <li id="R_2.110.033ab">patisaṃyogasulabhaṃ vayo dṛṣṭvā tu me pitā</li>
        <li id="R_2.110.033cd">cintām abhyagamad dīno vittanāśād ivādhanaḥ</li>
    </ul>
    <ul id="R_2.110.034">
        <li id="R_2.110.034ab">sadṛśāc cāpakṛṣṭāc ca loke kanyāpitā janāt</li>
        <li id="R_2.110.034cd">pradharṣaṇām avāpnoti śakreṇāpi samo bhuvi</li>
    </ul>
    <ul id="R_2.110.035">
        <li id="R_2.110.035ab">tāṃ dharṣaṇām adūrasthāṃ saṃdṛśyātmani pārthivaḥ</li>
        <li id="R_2.110.035cd">cinntārṇavagataḥ pāraṃ nāsasādāplavo yatha</li>
    </ul>
    <ul id="R_2.110.036">
        <li id="R_2.110.036ab">ayonijāṃ hi māṃ jñātvā nādhyagacchat sa cintayan</li>
        <li id="R_2.110.036cd">sadṛśaṃ cānurūpaṃ ca mahīpālaḥ patiṃ mama</li>
    </ul>
    <ul id="R_2.110.037">
        <li id="R_2.110.037ab">tasya buddhir iyaṃ jātā cintayānasya saṃtatam</li>
        <li id="R_2.110.037cd">svayaṃ varaṃ tanūjāyāḥ kariṣyāmīti dhīmataḥ</li>
    </ul>
    <ul id="R_2.110.038">
        <li id="R_2.110.038ab">mahāyajñe tadā tasya varuṇena mahātmanā</li>
        <li id="R_2.110.038cd">dattaṃ dhanurvaraṃ prītyā tūṇī cākṣayya sāyakau</li>
    </ul>
    <ul id="R_2.110.039">
        <li id="R_2.110.039ab">asaṃcālyaṃ manuṣyaiś ca yatnenāpi ca gauravāt</li>
        <li id="R_2.110.039cd">tan na śaktā namayituṃ svapneṣv api narādhipāḥ</li>
    </ul>
    <ul id="R_2.110.040">
        <li id="R_2.110.040ab">tad dhanuḥ prāpya me pitrā vyāhṛtaṃ satyavādinā</li>
        <li id="R_2.110.040cd">samavāye narendrāṇāṃ pūrvam āmantrya pārthivān</li>
    </ul>
    <ul id="R_2.110.041">
        <li id="R_2.110.041ab">idaṃ ca dhanur udyamya sajyaṃ yaḥ kurute naraḥ</li>
        <li id="R_2.110.041cd">tasya me duhitā bhāryā bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_2.110.042">
        <li id="R_2.110.042ab">tac ca dṛṣṭvā dhanuḥśreṣṭhaṃ gauravād girisaṃnibham</li>
        <li id="R_2.110.042cd">abhivādya nṛpā jagmur aśaktās tasya tolane</li>
    </ul>
    <ul id="R_2.110.043">
        <li id="R_2.110.043ab">sudīrghasya tu kālasya rāghavo 'yaṃ mahādyutiḥ</li>
        <li id="R_2.110.043cd">viśvāmitreṇa sahito yajñaṃ draṣṭuṃ samāgataḥ</li>
    </ul>
    <ul id="R_2.110.044">
        <li id="R_2.110.044ab">lakṣmaṇena saha bhrātrā rāmaḥ satyaparākramaḥ</li>
        <li id="R_2.110.044cd">viśvāmitras tu dharmātmā mama pitrā supūjitaḥ</li>
    </ul>
    <ul id="R_2.110.045">
        <li id="R_2.110.045ab">provāca pitaraṃ tatra rāghavo rāmalakṣmaṇau</li>
        <li id="R_2.110.045cd">sutau daśarathasyemau dhanurdarśanakāṅkṣiṇau</li>
        <li id="R_2.110.045ef">ity uktas tena vipreṇa tad dhanuḥ samupānayat</li>
    </ul>
    <ul id="R_2.110.046">
        <li id="R_2.110.046ab">nimeṣāntaramātreṇa tad ānamya sa vīryavān</li>
        <li id="R_2.110.046cd">jyāṃ samāropya jhaṭiti pūrayām āsa vīryavān</li>
    </ul>
    <ul id="R_2.110.047">
        <li id="R_2.110.047ab">tena pūrayatā vegān madhye bhagnaṃ dvidhā dhanuḥ</li>
        <li id="R_2.110.047cd">tasya śabdo 'bhavad bhīmaḥ patitasyāśaner iva</li>
    </ul>
    <ul id="R_2.110.048">
        <li id="R_2.110.048ab">tato 'haṃ tatra rāmāya pitrā satyābhisaṃdhinā</li>
        <li id="R_2.110.048cd">udyatā dātum udyamya jalabhājanam uttamam</li>
    </ul>
    <ul id="R_2.110.049">
        <li id="R_2.110.049ab">dīyamānāṃ na tu tadā pratijagrāha rāghavaḥ</li>
        <li id="R_2.110.049cd">avijñāya pituś chandam ayodhyādhipateḥ prabhoḥ</li>
    </ul>
    <ul id="R_2.110.050">
        <li id="R_2.110.050ab">tataḥ śvaśuram āmantrya vṛddhaṃ daśarathaṃ nṛpam</li>
        <li id="R_2.110.050cd">mama pitrā ahaṃ dattā rāmāya viditātmane</li>
    </ul>
    <ul id="R_2.110.051">
        <li id="R_2.110.051ab">mama caivānujā sādhvī ūrmilā priyadarśanā</li>
        <li id="R_2.110.051cd">bhāryārthe lakṣmaṇasyāpi dattā pitrā mama svayam</li>
    </ul>
    <ul id="R_2.110.052">
        <li id="R_2.110.052ab">evaṃ dattāsmi rāmāya tadā tasmin svayaṃ vare</li>
        <li id="R_2.110.052cd">anuraktā ca dharmeṇa patiṃ vīryavatāṃ varam</li>
    </ul>
    <ul id="R_2.111.001">
        <li id="R_2.111.001ab">anasūyā tu dharmajñā śrutvā tāṃ mahatīṃ kathām</li>
        <li id="R_2.111.001cd">paryaṣvajata bāhubhyāṃ śirasy āghrāya maithilīm</li>
    </ul>
    <ul id="R_2.111.002">
        <li id="R_2.111.002ab">vyaktākṣarapadaṃ citraṃ bhāṣitaṃ madhuraṃ tvayā</li>
        <li id="R_2.111.002cd">yathā svayaṃvaraṃ vṛttaṃ tat sarvaṃ hi śrutaṃ mayā</li>
    </ul>
    <ul id="R_2.111.003">
        <li id="R_2.111.003ab">rame 'haṃ kathayā te tu dṛḍhaṃ madhurabhāṣiṇi</li>
        <li id="R_2.111.003cd">ravir astaṃ gataḥ śrīmān upohya rajanīṃ śivām</li>
    </ul>
    <ul id="R_2.111.004">
        <li id="R_2.111.004ab">divasaṃ prati kīrṇānām āhārārthaṃ patatriṇām</li>
        <li id="R_2.111.004cd">saṃdhyākāle nilīnānāṃ nidrārthaṃ śrūyate dhvaniḥ</li>
    </ul>
    <ul id="R_2.111.005">
        <li id="R_2.111.005ab">ete cāpy abhiṣekārdrā munayaḥ phalaśodhanāḥ</li>
        <li id="R_2.111.005cd">sahitā upavartante salilāplutavalkalāḥ</li>
    </ul>
    <ul id="R_2.111.006">
        <li id="R_2.111.006ab">ṛṣīṇām agnihotreṣu huteṣu vidhipurvakam</li>
        <li id="R_2.111.006cd">kapotāṅgāruṇo dhūmo dṛśyate pavanoddhataḥ</li>
    </ul>
    <ul id="R_2.111.007">
        <li id="R_2.111.007ab">alpaparṇā hi taravo ghanībhūtāḥ samantataḥ</li>
        <li id="R_2.111.007cd">viprakṛṣṭe 'pi ye deśe na prakāśanti vai diśaḥ</li>
    </ul>
    <ul id="R_2.111.008">
        <li id="R_2.111.008ab">rajanī rasasattvāni pracaranti samantataḥ</li>
        <li id="R_2.111.008cd">tapovanamṛgā hy ete veditīrtheṣu śerate</li>
    </ul>
    <ul id="R_2.111.009">
        <li id="R_2.111.009ab">saṃpravṛttā niśā sīte nakṣatrasamalaṃkṛtā</li>
        <li id="R_2.111.009cd">jyotsnā prāvaraṇaś candro dṛśyate 'bhyudito 'mbare</li>
    </ul>
    <ul id="R_2.111.010">
        <li id="R_2.111.010ab">gamyatām anujānāmi rāmasyānucarī bhava</li>
        <li id="R_2.111.010cd">kathayantyā hi madhuraṃ tvayāhaṃ paritoṣitā</li>
    </ul>
    <ul id="R_2.111.011">
        <li id="R_2.111.011ab">alaṃkuru ca tāvat tvaṃ pratyakṣaṃ mama maithili</li>
        <li id="R_2.111.011cd">prītiṃ janaya me vatsa divyālaṃkāraśobhinī</li>
    </ul>
    <ul id="R_2.111.012">
        <li id="R_2.111.012ab">sā tadā samalaṃkṛtya sītā surasutopamā</li>
        <li id="R_2.111.012cd">praṇamya śirasā tasyai rāmaṃ tv abhimukhī yayau</li>
    </ul>
    <ul id="R_2.111.013">
        <li id="R_2.111.013ab">tathā tu bhūṣitāṃ sītāṃ dadarśa vadatāṃ varaḥ</li>
        <li id="R_2.111.013cd">rāghavaḥ prītidānena tapasvinyā jaharṣa ca</li>
    </ul>
    <ul id="R_2.111.014">
        <li id="R_2.111.014ab">nyavedayat tataḥ sarvaṃ sītā rāmāya maithilī</li>
        <li id="R_2.111.014cd">prītidānaṃ tapasvinyā vasanābharaṇasrajām</li>
    </ul>
    <ul id="R_2.111.015">
        <li id="R_2.111.015ab">prahṛṣṭas tv abhavad rāmo lakṣmaṇaś ca mahārathaḥ</li>
        <li id="R_2.111.015cd">maithilyāḥ satkriyāṃ dṛṣṭvā mānuṣeṣu sudurlabhām</li>
    </ul>
    <ul id="R_2.111.016">
        <li id="R_2.111.016ab">tatas tāṃ sarvarīṃ prītaḥ puṇyāṃ śaśinibhānanaḥ</li>
        <li id="R_2.111.016cd">arcitas tāpasaiḥ siddhair uvāsa raghunandanaḥ</li>
    </ul>
    <ul id="R_2.111.017">
        <li id="R_2.111.017ab">tasyāṃ rātryāṃ vyatītāyām abhiṣicya hutāgnikān</li>
        <li id="R_2.111.017cd">āpṛcchetāṃ naravyāghrau tāpasān vanagocarān</li>
    </ul>
    <ul id="R_2.111.018">
        <li id="R_2.111.018ab">tāv ūcus te vanacarās tāpasā dharmacāriṇaḥ</li>
        <li id="R_2.111.018cd">vanasya tasya saṃcāraṃ rākṣasaiḥ samabhiplutam</li>
    </ul>
    <ul id="R_2.111.019">
        <li id="R_2.111.019ab">eṣa panthā maharṣīṇāṃ phalāny āharatāṃ vane</li>
        <li id="R_2.111.019cd">anena tu vanaṃ durgaṃ gantuṃ rāghava te kṣamam</li>
    </ul>
    <ul id="R_2.111.020">
        <li id="R_2.111.020ab">
            <seg type="pāda" n="a">itīva taiḥ prāñjalibhis tapasvibhir</seg>
            <seg type="pāda" n="b">dvijaiḥ kṛtasvastyayanaḥ paraṃtapaḥ</seg>
        </li>
        <li id="R_2.111.020cd">
            <seg type="pāda" n="c">vanaṃ sabhāryaḥ praviveśa rāghavaḥ</seg>
            <seg type="pāda" n="d">salakṣmaṇaḥ sūrya ivābhramaṇḍalam</seg>
        </li>
    </ul>
	{/if}
</div>
<div class="kanda-3">
	<h4>Kāṇḍa 3</h4>
		{#if kanda3}
    <ul id="R_3.001.001">
        <li id="R_3.001.001ab">praviśya tu mahāraṇyaṃ daṇḍakāraṇyam ātmavān</li>
        <li id="R_3.001.001cd">dadarśa rāmo durdharṣas tāpasāśramamaṇḍalam</li>
    </ul>
    <ul id="R_3.001.002">
        <li id="R_3.001.002ab">kuśacīraparikṣiptaṃ brāhmyā lakṣmyā samāvṛtam</li>
        <li id="R_3.001.002cd">yathā pradīptaṃ durdharśaṃ gagane sūryamaṇḍalam</li>
    </ul>
    <ul id="R_3.001.003">
        <li id="R_3.001.003ab">śaraṇyaṃ sarvabhūtānāṃ susamṛṣṭājiraṃ sadā</li>
        <li id="R_3.001.003cd">pūjitaṃ copanṛttaṃ ca nityam apsarasāṃ gaṇaiḥ</li>
    </ul>
    <ul id="R_3.001.004">
        <li id="R_3.001.004ab">viśālair agniśaraṇaiḥ srugbhāṇḍair ajinaiḥ kuśaiḥ</li>
        <li id="R_3.001.004cd">samidbhis toyakalaśaiḥ phalamūlaiś ca śobhitam</li>
    </ul>
    <ul id="R_3.001.005">
        <li id="R_3.001.005ab">āraṇyaiś ca mahāvṛkṣaiḥ puṇyaiḥ svāduphalair vṛtam</li>
        <li id="R_3.001.005cd">balihomārcitaṃ puṇyaṃ brahmaghoṣanināditam</li>
    </ul>
    <ul id="R_3.001.006">
        <li id="R_3.001.006ab">puṣpair vanyaiḥ parikṣiptaṃ padminyā ca sapadmayā</li>
        <li id="R_3.001.006cd">phalamūlāśanair dāntaiś cīrakṛṣṇājināmbaraiḥ</li>
    </ul>
    <ul id="R_3.001.007">
        <li id="R_3.001.007ab">sūryavaiśvānarābhaiś ca purāṇair munibhir vṛtam</li>
        <li id="R_3.001.007cd">puṇyaiś a niyatāhāraiḥ śobhitaṃ paramarṣibhiḥ</li>
    </ul>
    <ul id="R_3.001.008">
        <li id="R_3.001.008ab">tad brahmabhavanaprakhyaṃ brahmaghoṣanināditam</li>
        <li id="R_3.001.008cd">brahmavidbhir mahābhāgair brāhmaṇair upaśobhitam</li>
    </ul>
    <ul id="R_3.001.009">
        <li id="R_3.001.009ab">tad dṛṣṭvā rāghavaḥ śrīmāṃs tāpasāśramamaṇḍalam</li>
        <li id="R_3.001.009cd">abhyagacchan mahātejā vijyaṃ kṛtvā mahad dhanuḥ</li>
    </ul>
    <ul id="R_3.001.010">
        <li id="R_3.001.010ab">divyajñānopapannās te rāmaṃ dṛṣṭvā maharṣayaḥ</li>
        <li id="R_3.001.010cd">abhyagacchaṃs tadā prītā vaidehīṃ ca yaśasvinīm</li>
    </ul>
    <ul id="R_3.001.011">
        <li id="R_3.001.011ab">te taṃ somam ivodyantaṃ dṛṣṭvā vai dharmacāriṇaḥ</li>
        <li id="R_3.001.011cd">maṅgalāni prayuñjānāḥ pratyagṛhṇan dṛḍhavratāḥ</li>
    </ul>
    <ul id="R_3.001.012">
        <li id="R_3.001.012ab">rūpasaṃhananaṃ lakṣmīṃ saukumāryaṃ suveṣatām</li>
        <li id="R_3.001.012cd">dadṛśur vismitākārā rāmasya vanavāsinaḥ</li>
    </ul>
    <ul id="R_3.001.013">
        <li id="R_3.001.013ab">vaidehīṃ lakṣmaṇaṃ rāmaṃ netrair animiṣair iva</li>
        <li id="R_3.001.013cd">āścaryabhūtān dadṛśuḥ sarve te vanacāriṇaḥ</li>
    </ul>
    <ul id="R_3.001.014">
        <li id="R_3.001.014ab">atrainaṃ hi mahābhāgāḥ sarvabhūtahite ratāḥ</li>
        <li id="R_3.001.014cd">atithiṃ parṇaśālāyāṃ rāghavaṃ saṃnyaveśayan</li>
    </ul>
    <ul id="R_3.001.015">
        <li id="R_3.001.015ab">tato rāmasya satkṛtya vidhinā pāvakopamāḥ</li>
        <li id="R_3.001.015cd">ājahrus te mahābhāgāḥ salilaṃ dharmacāriṇaḥ</li>
    </ul>
    <ul id="R_3.001.016">
        <li id="R_3.001.016ab">mūlaṃ puṣpaṃ phalaṃ vanyam āśramaṃ ca mahātmanaḥ</li>
        <li id="R_3.001.016cd">nivedayītvā dharmajñās tataḥ prāñjalayo 'bruvan</li>
    </ul>
    <ul id="R_3.001.017">
        <li id="R_3.001.017ab">dharmapālo janasyāsya śaraṇyaś ca mahāyaśāḥ</li>
        <li id="R_3.001.017cd">pūjanīyaś ca mānyaś ca rājā daṇḍadharo guruḥ</li>
    </ul>
    <ul id="R_3.001.018">
        <li id="R_3.001.018ab">indrasyaiva caturbhāgaḥ prajā rakṣati rāghava</li>
        <li id="R_3.001.018cd">rājā tasmād vanān bhogān bhuṅkte lokanamaskṛtaḥ</li>
    </ul>
    <ul id="R_3.001.019">
        <li id="R_3.001.019ab">te vayaṃ bhavatā rakṣyā bhavadviṣayavāsinaḥ</li>
        <li id="R_3.001.019cd">nagarastho vanastho vā tvaṃ no rājā janeśvaraḥ</li>
    </ul>
    <ul id="R_3.001.020">
        <li id="R_3.001.020ab">nyastadaṇḍā vayaṃ rājañ jitakrodhā jitendriyāḥ</li>
        <li id="R_3.001.020cd">rakṣitavyās tvayā śaśvad garbhabhūtās tapodhanāḥ</li>
    </ul>
    <ul id="R_3.001.021">
        <li id="R_3.001.021ab">evam uktvā phalair mūlaiḥ puṣpair vanyaiś ca rāghavam</li>
        <li id="R_3.001.021cd">anyaiś ca vividhāhāraiḥ salakṣmaṇam apūjayan</li>
    </ul>
    <ul id="R_3.001.022">
        <li id="R_3.001.022ab">tathānye tāpasāḥ siddhā rāmaṃ vaiśvānaropamāḥ</li>
        <li id="R_3.001.022cd">nyāyavṛttā yathānyāyaṃ tarpayām āsur īśvaram</li>
    </ul>
    <ul id="R_3.002.001">
        <li id="R_3.002.001ab">kṛtātithyo 'tha rāmas tu sūryasyodayanaṃ prati</li>
        <li id="R_3.002.001cd">āmantrya sa munīn sarvān vanam evānvagāhata</li>
    </ul>
    <ul id="R_3.002.002">
        <li id="R_3.002.002ab">nānāmṛgagaṇākīrṇaṃ śārdūlavṛkasevitam</li>
        <li id="R_3.002.002cd">dhvastavṛkṣalatāgulmaṃ durdarśa salilāśayam</li>
    </ul>
    <ul id="R_3.002.003">
        <li id="R_3.002.003ab">niṣkūjanānāśakuni jhillikā gaṇanāditam</li>
        <li id="R_3.002.003cd">lakṣmaṇānugato rāmo vanamadhyaṃ dadarśa ha</li>
    </ul>
    <ul id="R_3.002.004">
        <li id="R_3.002.004ab">vanamadhye tu kākutsthas tasmin ghoramṛgāyute</li>
        <li id="R_3.002.004cd">dadarśa giriśṛṅgābhaṃ puruṣādaṃ mahāsvanam</li>
    </ul>
    <ul id="R_3.002.005">
        <li id="R_3.002.005ab">gabhīrākṣaṃ mahāvaktraṃ vikaṭaṃ viṣamodaram</li>
        <li id="R_3.002.005cd">bībhatsaṃ viṣamaṃ dīrghaṃ vikṛtaṃ ghoradarśanam</li>
    </ul>
    <ul id="R_3.002.006">
        <li id="R_3.002.006ab">vasānaṃ carmavaiyāghraṃ vasārdraṃ rudhirokṣitam</li>
        <li id="R_3.002.006cd">trāsanaṃ sarvabhūtānāṃ vyāditāsyam ivāntakam</li>
    </ul>
    <ul id="R_3.002.007">
        <li id="R_3.002.007ab">trīn siṃhāṃś caturo vyāghrān dvau vṛkau pṛṣatān daśa</li>
        <li id="R_3.002.007cd">saviṣāṇaṃ vasādigdhaṃ gajasya ca śiro mahat</li>
    </ul>
    <ul id="R_3.002.008">
        <li id="R_3.002.008ab">avasajyāyase śūle vinadantaṃ mahāsvanam</li>
        <li id="R_3.002.008cd">sa rāmaṃ lakṣmaṇaṃ caiva sītāṃ dṛṣṭvā ca maithilīm</li>
    </ul>
    <ul id="R_3.002.009">
        <li id="R_3.002.009ab">abhyadhāvat susaṃkruddhaḥ prajāḥ kāla ivāntakaḥ</li>
        <li id="R_3.002.009cd">sa kṛtvā bhairavaṃ nādaṃ cālayann iva medinīm</li>
    </ul>
    <ul id="R_3.002.010">
        <li id="R_3.002.010ab">aṅgenādāya vaidehīm apakramya tato 'bravīt</li>
        <li id="R_3.002.010cd">yuvāṃ jaṭācīradharau sabhāryau kṣīṇajīvitau</li>
    </ul>
    <ul id="R_3.002.011">
        <li id="R_3.002.011ab">praviṣṭau daṇḍakāraṇyaṃ śaracāpāsidhāriṇau</li>
        <li id="R_3.002.011cd">kathaṃ tāpasayor vāṃ ca vāsaḥ pramadayā saha</li>
    </ul>
    <ul id="R_3.002.012">
        <li id="R_3.002.012ab">adharmacāriṇau pāpau kau yuvāṃ munidūṣakau</li>
        <li id="R_3.002.012cd">ahaṃ vanam idaṃ durgaṃ virāgho nāma rākṣasaḥ</li>
    </ul>
    <ul id="R_3.002.013">
        <li id="R_3.002.013ab">carāmi sāyudho nityam ṛṣimāṃsāni bhakṣayan</li>
        <li id="R_3.002.013cd">iyaṃ nārī varārohā mama bharyā bhaviṣyati</li>
        <li id="R_3.002.013ef">yuvayoḥ pāpayoś cāhaṃ pāsyāmi rudhiraṃ mṛdhe</li>
    </ul>
    <ul id="R_3.002.014">
        <li id="R_3.002.014ab">tasyaivaṃ bruvato dhṛṣṭaṃ virādhasya durātmanaḥ</li>
        <li id="R_3.002.014cd">śrutvā sagarvitaṃ vākyaṃ saṃbhrāntā janakātmajā</li>
        <li id="R_3.002.014ef">sītā prāvepatodvegāt pravāte kadalī yathā</li>
    </ul>
    <ul id="R_3.002.015">
        <li id="R_3.002.015ab">tāṃ dṛṣṭvā rāghavaḥ sītāṃ virādhāṅkagatāṃ śubhām</li>
        <li id="R_3.002.015cd">abravīl lakṣmaṇaṃ vākyaṃ mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_3.002.016">
        <li id="R_3.002.016ab">paśya saumya narendrasya janakasyātmasaṃbhavām</li>
        <li id="R_3.002.016cd">mama bhāryāṃ śubhācārāṃ virādhāṅke praveśitām</li>
        <li id="R_3.002.016ef">atyanta sukhasaṃvṛddhāṃ rājaputrīṃ yaśasvinīm</li>
    </ul>
    <ul id="R_3.002.017">
        <li id="R_3.002.017ab">yad abhipretam asmāsu priyaṃ vara vṛtaṃ ca yat</li>
        <li id="R_3.002.017cd">kaikeyyās tu susaṃvṛttaṃ kṣipram adyaiva lakṣmaṇa</li>
    </ul>
    <ul id="R_3.002.018">
        <li id="R_3.002.018ab">yā na tuṣyati rājyena putrārthe dīrghadarśinī</li>
        <li id="R_3.002.018cd">yayāhaṃ sarvabhūtānāṃ hitaḥ prasthāpito vanam</li>
        <li id="R_3.002.018ef">adyedānīṃ sakāmā sā yā mātā mama madhyamā</li>
    </ul>
    <ul id="R_3.002.019">
        <li id="R_3.002.019ab">parasparśāt tu vaidehyā na duḥkhataram asti me</li>
        <li id="R_3.002.019cd">pitur vināśāt saumitre svarājyaharaṇāt tathā</li>
    </ul>
    <ul id="R_3.002.020">
        <li id="R_3.002.020ab">iti bruvati kākutsthe bāṣpaśokapariplute</li>
        <li id="R_3.002.020cd">abravīl lakṣmaṇaḥ kruddho ruddho nāga iva śvasan</li>
    </ul>
    <ul id="R_3.002.021">
        <li id="R_3.002.021ab">anātha iva bhūtānāṃ nāthas tvaṃ vāsavopamaḥ</li>
        <li id="R_3.002.021cd">mayā preṣyeṇa kākutstha kimarthaṃ paritapsyase</li>
    </ul>
    <ul id="R_3.002.022">
        <li id="R_3.002.022ab">śareṇa nihatasyādya mayā kruddhena rakṣasaḥ</li>
        <li id="R_3.002.022cd">virādhasya gatāsor hi mahī pāsyati śoṇitam</li>
    </ul>
    <ul id="R_3.002.023">
        <li id="R_3.002.023ab">rājyakāme mama krodho bharate yo babhūva ha</li>
        <li id="R_3.002.023cd">taṃ virādhe vimokṣyāmi vajrī vajram ivācale</li>
    </ul>
    <ul id="R_3.002.024">
        <li id="R_3.002.024ab">
            <seg type="pāda" n="a">mama bhujabalavegavegitaḥ</seg>
            <seg type="pāda" n="b">patatu śaro 'sya mahān mahorasi</seg>
        </li>
        <li id="R_3.002.024cd">
            <seg type="pāda" n="c">vyapanayatu tanoś ca jīvitaṃ</seg>
            <seg type="pāda" n="d">patatu tataś ca mahīṃ vighūrṇitaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.003.001">
        <li id="R_3.003.001ab">athovāca punar vākyaṃ virādhaḥ pūrayan vanam</li>
        <li id="R_3.003.001cd">ātmānaṃ pṛcchate brūtaṃ kau yuvāṃ kva gamiṣyathaḥ</li>
    </ul>
    <ul id="R_3.003.002">
        <li id="R_3.003.002ab">tam uvāca tato rāmo rākṣasaṃ jvalitānanam</li>
        <li id="R_3.003.002cd">pṛcchantaṃ sumahātejā ikṣvākukulam ātmanaḥ</li>
    </ul>
    <ul id="R_3.003.003">
        <li id="R_3.003.003ab">kṣatriyo vṛttasaṃpannau viddhi nau vanagocarau</li>
        <li id="R_3.003.003cd">tvāṃ tu veditum icchāvaḥ kas tvaṃ carasi daṇḍakān</li>
    </ul>
    <ul id="R_3.003.004">
        <li id="R_3.003.004ab">tam uvāca virādhas tu rāmaṃ satyaparākramam</li>
        <li id="R_3.003.004cd">hanta vakṣyāmi te rājan nibodha mama rāghava</li>
    </ul>
    <ul id="R_3.003.005">
        <li id="R_3.003.005ab">putraḥ kila jayasyāhaṃ mātā mama śatahradā</li>
        <li id="R_3.003.005cd">virādha iti mām āhuḥ pṛthivyāṃ sarvarākṣasāḥ</li>
    </ul>
    <ul id="R_3.003.006">
        <li id="R_3.003.006ab">tapasā cāpi me prāptā brahmaṇo hi prasādajā</li>
        <li id="R_3.003.006cd">śastreṇāvadhyatā loke 'cchedyābhedyatvam eva ca</li>
    </ul>
    <ul id="R_3.003.007">
        <li id="R_3.003.007ab">utsṛjya pramadām enām anapekṣau yathāgatam</li>
        <li id="R_3.003.007cd">tvaramāṇau pālayethāṃ na vāṃ jīvitam ādade</li>
    </ul>
    <ul id="R_3.003.008">
        <li id="R_3.003.008ab">taṃ rāmaḥ pratyuvācedaṃ kopasaṃraktalocanaḥ</li>
        <li id="R_3.003.008cd">rākṣasaṃ vikṛtākāraṃ virādhaṃ pāpacetasaṃ</li>
    </ul>
    <ul id="R_3.003.009">
        <li id="R_3.003.009ab">kṣudra dhik tvāṃ tu hīnārthaṃ mṛtyum anveṣase dhruvam</li>
        <li id="R_3.003.009cd">raṇe saṃprāpsyase tiṣṭha na me jīvan gamiṣyasi</li>
    </ul>
    <ul id="R_3.003.010">
        <li id="R_3.003.010ab">tataḥ sajyaṃ dhanuḥ kṛtvā rāmaḥ suniśitāñ śarān</li>
        <li id="R_3.003.010cd">suśīghram abhisaṃdhāya rākṣasaṃ nijaghāna ha</li>
    </ul>
    <ul id="R_3.003.011">
        <li id="R_3.003.011ab">dhanuṣā jyāguṇavatā saptabāṇān mumoca ha</li>
        <li id="R_3.003.011cd">rukmapuṅkhān mahāvegān suparṇānilatulyagān</li>
    </ul>
    <ul id="R_3.003.012">
        <li id="R_3.003.012ab">te śarīraṃ virādhasya bhittvā barhiṇavāsasaḥ</li>
        <li id="R_3.003.012cd">nipetuḥ śoṇitādigdhā dharaṇyāṃ pāvakopamāḥ</li>
    </ul>
    <ul id="R_3.003.013">
        <li id="R_3.003.013ab">sa vinadya mahānādaṃ śūlaṃ śakradhvajopamam</li>
        <li id="R_3.003.013cd">pragṛhyāśobhata tadā vyāttānana ivāntakaḥ</li>
    </ul>
    <ul id="R_3.003.014">
        <li id="R_3.003.014ab">tac chūlaṃ vajrasaṃkāśaṃ gagane jvalanopamam</li>
        <li id="R_3.003.014cd">dvābhyāṃ śarābhyāṃ ciccheda rāmaḥ śastrabhṛtāṃ varaḥ</li>
    </ul>
    <ul id="R_3.003.015">
        <li id="R_3.003.015ab">tasya raudrasya saumitrir bāhuṃ savyaṃ babhañja ha</li>
        <li id="R_3.003.015cd">rāmas tu dakṣiṇaṃ bāhuṃ tarasā tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_3.003.016">
        <li id="R_3.003.016ab">sa bhagnabāhuḥ saṃvigno nipapātāśu rākṣasaḥ</li>
        <li id="R_3.003.016cd">dharaṇyāṃ meghasaṃkāśo vajrabhinna ivācalaḥ</li>
        <li id="R_3.003.016ef">idaṃ provāca kākutsthaṃ virādhaḥ puruṣarṣabham</li>
    </ul>
    <ul id="R_3.003.017">
        <li id="R_3.003.017ab">kausalyā suprajās tāta rāmas tvaṃ vidito mayā</li>
        <li id="R_3.003.017cd">vaidehī ca mahābhāgā lakṣmaṇaś ca mahāyaśāḥ</li>
    </ul>
    <ul id="R_3.003.018">
        <li id="R_3.003.018ab">abhiśāpād ahaṃ ghorāṃ praviṣṭo rākṣasīṃ tanum</li>
        <li id="R_3.003.018cd">tumburur nāma gandharvaḥ śapto vaiśvaraṇena hi</li>
    </ul>
    <ul id="R_3.003.019">
        <li id="R_3.003.019ab">prasādyamānaś ca mayā so 'bravīn māṃ mahāyaśāḥ</li>
        <li id="R_3.003.019cd">yadā dāśarathī rāmas tvāṃ vadhiṣyati saṃyuge</li>
    </ul>
    <ul id="R_3.003.020">
        <li id="R_3.003.020ab">tadā prakṛtim āpanno bhavān svargaṃ gamiṣyati</li>
        <li id="R_3.003.020cd">iti vaiśravaṇo rājā rambhāsaktam uvāca ha</li>
    </ul>
    <ul id="R_3.003.021">
        <li id="R_3.003.021ab">anupasthīyamāno māṃ saṃkruddho vyajahāra ha</li>
        <li id="R_3.003.021cd">tava prasādān mukto 'ham abhiśāpāt sudāruṇāt</li>
        <li id="R_3.003.021ef">bhavanaṃ svaṃ gamiṣyāmi svasti vo 'stu paraṃtapa</li>
    </ul>
    <ul id="R_3.003.022">
        <li id="R_3.003.022ab">ito vasati dharmātmā śarabhaṅgaḥ pratāpavān</li>
        <li id="R_3.003.022cd">adhyardhayojane tāta maharṣiḥ sūryasaṃnibhaḥ</li>
    </ul>
    <ul id="R_3.003.023">
        <li id="R_3.003.023ab">taṃ kṣipram abhigaccha tvaṃ sa te śreyo vidhāsyati</li>
        <li id="R_3.003.023cd">avaṭe cāpi māṃ rāma nikṣipya kuśalī vraja</li>
    </ul>
    <ul id="R_3.003.024">
        <li id="R_3.003.024ab">rakṣasāṃ gatasattvānām eṣa dharmaḥ sanātanaḥ</li>
        <li id="R_3.003.024cd">avaṭe ye nidhīyante teṣāṃ lokāḥ sanātanāḥ</li>
    </ul>
    <ul id="R_3.003.025">
        <li id="R_3.003.025ab">evam uktvā tu kākutsthaṃ virādhaḥ śarapīḍitaḥ</li>
        <li id="R_3.003.025cd">babhūva svargasaṃprāpto nyastadeho mahābalaḥ</li>
    </ul>
    <ul id="R_3.003.026">
        <li id="R_3.003.026ab">taṃ muktakaṇṭham utkṣipya śaṅkukarṇaṃ mahāsvanam</li>
        <li id="R_3.003.026cd">virādhaṃ prākṣipac chvabhre nadantaṃ bhairavasvanam</li>
    </ul>
    <ul id="R_3.003.027">
        <li id="R_3.003.027ab">
            <seg type="pāda" n="a">tatas tu tau kāñcanacitrakārmukau</seg>
            <seg type="pāda" n="b">nihatya rakṣaḥ parigṛhya maithilīm</seg>
        </li>
        <li id="R_3.003.027cd">
            <seg type="pāda" n="c">vijahratus tau muditau mahāvane</seg>
            <seg type="pāda" n="d">divi sthitau candradivākarāv iva</seg>
        </li>
    </ul>
    <ul id="R_3.004.001">
        <li id="R_3.004.001ab">hatvā tu taṃ bhīmabalaṃ virādhaṃ rākṣasaṃ vane</li>
        <li id="R_3.004.001cd">tataḥ sītāṃ pariṣvajya samāśvāsya ca vīryavān</li>
        <li id="R_3.004.001ef">abravīl lakṣmaṇāṃ rāmo bhrātaraṃ dīptatejasaṃ</li>
    </ul>
    <ul id="R_3.004.002">
        <li id="R_3.004.002ab">kaṣṭaṃ vanam idaṃ durgaṃ na ca smo vanagocarāḥ</li>
        <li id="R_3.004.002cd">abhigacchāmahe śīghraṃ śarabhaṅgaṃ tapodhanam</li>
    </ul>
    <ul id="R_3.004.003">
        <li id="R_3.004.003ab">āśramaṃ śarabhaṅgasya rāghavo 'bhijagāma ha</li>
    </ul>
    <ul id="R_3.004.004">
        <li id="R_3.004.004ab">tasya devaprabhāvasya tapasā bhāvitātmanaḥ</li>
        <li id="R_3.004.004cd">samīpe śarabhaṅgasya dadarśa mahad adbhutam</li>
    </ul>
    <ul id="R_3.004.005">
        <li id="R_3.004.005ab">vibhrājamānaṃ vapuṣā sūryavaiśvānaropamam</li>
        <li id="R_3.004.005cd">asaṃspṛśantaṃ vasudhāṃ dadarśa vibudheśvaram</li>
    </ul>
    <ul id="R_3.004.006">
        <li id="R_3.004.006ab">suprabhābharaṇaṃ devaṃ virajo 'mbaradhāriṇam</li>
        <li id="R_3.004.006cd">tadvidhair eva bahubhiḥ pūjyamānaṃ mahātmabhiḥ</li>
    </ul>
    <ul id="R_3.004.007">
        <li id="R_3.004.007ab">haribhir vājibhir yuktam antarikṣagataṃ ratham</li>
        <li id="R_3.004.007cd">dadarśādūratas tasya taruṇādityasaṃnibham</li>
    </ul>
    <ul id="R_3.004.008">
        <li id="R_3.004.008ab">pāṇḍurābhraghanaprakhyaṃ candramaṇḍalasaṃnibham</li>
        <li id="R_3.004.008cd">apaśyad vimalaṃ chatraṃ citramālyopaśobhitam</li>
    </ul>
    <ul id="R_3.004.009">
        <li id="R_3.004.009ab">cāmaravyajane cāgrye rukmadaṇḍe mahādhane</li>
        <li id="R_3.004.009cd">gṛhīte vananārībhyāṃ dhūyamāne ca mūrdhani</li>
    </ul>
    <ul id="R_3.004.010">
        <li id="R_3.004.010ab">gandharvāmarasiddhāś ca bahavaḥ paramarṣayaḥ</li>
        <li id="R_3.004.010cd">antarikṣagataṃ devaṃ vāgbhir agryābhir īḍire</li>
    </ul>
    <ul id="R_3.004.011">
        <li id="R_3.004.011ab">dṛṣṭvā śatakratuṃ tatra rāmo lakṣmaṇam abravīt</li>
        <li id="R_3.004.011cd">ye hayāḥ puruhūtasya purā śakrasya naḥ śrutāḥ</li>
        <li id="R_3.004.011ef">antarikṣagatā divyās ta ime harayo dhruvam</li>
    </ul>
    <ul id="R_3.004.012">
        <li id="R_3.004.012ab">ime ca puruṣavyāghra ye tiṣṭhanty abhito ratham</li>
        <li id="R_3.004.012cd">śataṃ śataṃ kuṇḍalino yuvānaḥ khaḍgapāṇayaḥ</li>
    </ul>
    <ul id="R_3.004.013">
        <li id="R_3.004.013ab">urodeśeṣu sarveṣāṃ hārā jvalanasaṃnibhāḥ</li>
        <li id="R_3.004.013cd">rūpaṃ bibhrati saumitre pañcaviṃśativārṣikam</li>
    </ul>
    <ul id="R_3.004.014">
        <li id="R_3.004.014ab">etad dhi kila devānāṃ vayo bhavati nityadā</li>
        <li id="R_3.004.014cd">yatheme puruṣavyāghrā dṛśyante priyadarśanāḥ</li>
    </ul>
    <ul id="R_3.004.015">
        <li id="R_3.004.015ab">ihaiva saha vaidehyā muhūrtaṃ tiṣṭha lakṣmaṇa</li>
        <li id="R_3.004.015cd">yāvaj janāmy ahaṃ vyaktaṃ ka eṣa dyutimān rathe</li>
    </ul>
    <ul id="R_3.004.016">
        <li id="R_3.004.016ab">tam evam uktvā saumitrim ihaiva sthīyatām iti</li>
        <li id="R_3.004.016cd">abhicakrāma kākutsthaḥ śarabhaṅgāśramaṃ prati</li>
    </ul>
    <ul id="R_3.004.017">
        <li id="R_3.004.017ab">tataḥ samabhigacchantaṃ prekṣya rāmaṃ śacīpatiḥ</li>
        <li id="R_3.004.017cd">śarabhaṅgam anujñāpya vibudhān idam abravīt</li>
    </ul>
    <ul id="R_3.004.018">
        <li id="R_3.004.018ab">ihopayāty asau rāmo yāvan māṃ nābhibhāṣate</li>
        <li id="R_3.004.018cd">niṣṭhāṃ nayata tāvat tu tato māṃ draṣṭum arhati</li>
    </ul>
    <ul id="R_3.004.019">
        <li id="R_3.004.019ab">jitavantaṃ kṛtārthaṃ ca draṣṭāham acirād imam</li>
        <li id="R_3.004.019cd">karma hy anena kartavyaṃ mahad anyaiḥ suduṣkaram</li>
    </ul>
    <ul id="R_3.004.020">
        <li id="R_3.004.020ab">iti vajrī tam āmantrya mānayitvā ca tāpasaṃ</li>
        <li id="R_3.004.020cd">rathena hariyuktena yayau divam ariṃdamaḥ</li>
    </ul>
    <ul id="R_3.004.021">
        <li id="R_3.004.021ab">prayāte tu sahasrākṣe rāghavaḥ saparicchadaḥ</li>
        <li id="R_3.004.021cd">agnihotram upāsīnaṃ śarabhaṅgam upāgamat</li>
    </ul>
    <ul id="R_3.004.022">
        <li id="R_3.004.022ab">tasya pādau ca saṃgṛhya rāmaḥ sītā ca lakṣmaṇaḥ</li>
        <li id="R_3.004.022cd">niṣedus tadanujñātā labdhavāsā nimantritāḥ</li>
    </ul>
    <ul id="R_3.004.023">
        <li id="R_3.004.023ab">tataḥ śakropayānaṃ tu paryapṛcchat sa rāghavaḥ</li>
        <li id="R_3.004.023cd">śarabhaṅgaś ca tat sarvaṃ rāghavāya nyavedayat</li>
    </ul>
    <ul id="R_3.004.024">
        <li id="R_3.004.024ab">mām eṣa varado rāma brahmalokaṃ ninīṣati</li>
        <li id="R_3.004.024cd">jitam ugreṇa tapasā duṣprāpam akṛtātmabhiḥ</li>
    </ul>
    <ul id="R_3.004.025">
        <li id="R_3.004.025ab">ahaṃ jñātvā naravyāghra vartamānam adūrataḥ</li>
        <li id="R_3.004.025cd">brahmalokaṃ na gacchāmi tvām adṛṣṭvā priyātithim</li>
    </ul>
    <ul id="R_3.004.026">
        <li id="R_3.004.026ab">samāgamya gamiṣyāmi tridivaṃ devasevitam</li>
        <li id="R_3.004.026cd">akṣayā naraśārdūla jitā lokā mayā śubhāḥ</li>
        <li id="R_3.004.026ef">brāhmyāś ca nākapṛṣṭhyāś ca pratigṛhṇīṣva māmakān</li>
    </ul>
    <ul id="R_3.004.027">
        <li id="R_3.004.027ab">evam ukto naravyāghraḥ sarvaśāstraviśāradaḥ</li>
        <li id="R_3.004.027cd">ṛṣiṇā śarabhaṅgena rāghavo vākyam abravīt</li>
    </ul>
    <ul id="R_3.004.028">
        <li id="R_3.004.028ab">aham evāhariṣyāmi sarvāṃl lokān mahāmune</li>
        <li id="R_3.004.028cd">āvāsaṃ tv aham icchāmi pradiṣṭam iha kānane</li>
    </ul>
    <ul id="R_3.004.029">
        <li id="R_3.004.029ab">rāghaveṇaivam uktas tu śakratulyabalena vai</li>
        <li id="R_3.004.029cd">śarabhaṅgo mahāprājñaḥ punar evābravīd vacaḥ</li>
    </ul>
    <ul id="R_3.004.030">
        <li id="R_3.004.030ab">sutīkṣṇam abhigaccha tvaṃ śucau deśe tapasvinam</li>
        <li id="R_3.004.030cd">ramaṇīye vanoddeśe sa te vāsaṃ vidhāsyati</li>
    </ul>
    <ul id="R_3.004.031">
        <li id="R_3.004.031ab">eṣa panthā naravyāghra muhūrtaṃ paśya tāta mām</li>
        <li id="R_3.004.031cd">yāvaj jahāmi gātrāṇi jīrṇaṃ tvacam ivoragaḥ</li>
    </ul>
    <ul id="R_3.004.032">
        <li id="R_3.004.032ab">tato 'gniṃ sa samādhāya hutvā cājyena mantravit</li>
        <li id="R_3.004.032cd">śarabhaṅgo mahātejāḥ praviveśa hutāśanam</li>
    </ul>
    <ul id="R_3.004.033">
        <li id="R_3.004.033ab">tasya romāṇi keśāṃś ca dadāhāgnir mahātmanaḥ</li>
        <li id="R_3.004.033cd">jīrṇaṃ tvacaṃ tathāsthīni yac ca māṃsaṃ ca śoṇitam</li>
    </ul>
    <ul id="R_3.004.034">
        <li id="R_3.004.034ab">sa ca pāvakasaṃkāśaḥ kumāraḥ samapadyata</li>
        <li id="R_3.004.034cd">utthāyāgnicayāt tasmāc charabhaṅgo vyarocata</li>
    </ul>
    <ul id="R_3.004.035">
        <li id="R_3.004.035ab">sa lokān āhitāgnīnām ṛṣīṇāṃ ca mahātmanām</li>
        <li id="R_3.004.035cd">devānāṃ ca vyatikramya brahmalokaṃ vyarohata</li>
    </ul>
    <ul id="R_3.004.036">
        <li id="R_3.004.036ab">
            <seg type="pāda" n="a">sa puṇyakarmā bhuvane dvijarṣabhaḥ</seg>
            <seg type="pāda" n="b">pitāmahaṃ sānucaraṃ dadarśa ha</seg>
        </li>
        <li id="R_3.004.036cd">
            <seg type="pāda" n="c">pitāmahaś cāpi samīkṣya taṃ dvijaṃ</seg>
            <seg type="pāda" n="d">nananda susvāgatam ity uvāca ha</seg>
        </li>
    </ul>
    <ul id="R_3.005.001">
        <li id="R_3.005.001ab">śarabhaṅge divaṃ prāpte munisaṃghāḥ samāgatāḥ</li>
        <li id="R_3.005.001cd">abhyagacchanta kākutsthaṃ rāmaṃ jvalitatejasaṃ</li>
    </ul>
    <ul id="R_3.005.002">
        <li id="R_3.005.002ab">vaikhānasā vālakhilyāḥ saṃprakṣālā marīcipāḥ</li>
        <li id="R_3.005.002cd">aśmakuṭṭāś ca bahavaḥ patrāhārāś ca tāpasāḥ</li>
    </ul>
    <ul id="R_3.005.003">
        <li id="R_3.005.003ab">dantolūkhalinaś caiva tathaivonmajjakāḥ pare</li>
        <li id="R_3.005.003cd">munayaḥ salilāhārā vāyubhakṣās tathāpare</li>
    </ul>
    <ul id="R_3.005.004">
        <li id="R_3.005.004ab">ākāśanilayāś caiva tathā sthaṇḍilaśāyinaḥ</li>
        <li id="R_3.005.004cd">tathordhvavāsino dāntās tathārdrapaṭavāsasaḥ</li>
    </ul>
    <ul id="R_3.005.005">
        <li id="R_3.005.005ab">sajapāś ca taponityās tathā pañcatapo'nvitāḥ</li>
        <li id="R_3.005.005cd">sarve brāhmyā śriyā juṣṭā dṛḍhayogasamāhitāḥ</li>
        <li id="R_3.005.005ef">śarabhaṅgāśrame rāmam abhijagmuś ca tāpasāḥ</li>
    </ul>
    <ul id="R_3.005.006">
        <li id="R_3.005.006ab">abhigamya ca dharmajñā rāmaṃ dharmabhṛtāṃ varam</li>
        <li id="R_3.005.006cd">ūcuḥ paramadharmajñam ṛṣisaṃghāḥ samāhitāḥ</li>
    </ul>
    <ul id="R_3.005.007">
        <li id="R_3.005.007ab">tvam ikṣvākukulasyāsya pṛthivyāś ca mahārathaḥ</li>
        <li id="R_3.005.007cd">pradhānaś cāsi nāthaś ca devānāṃ maghavān iva</li>
    </ul>
    <ul id="R_3.005.008">
        <li id="R_3.005.008ab">viśrutas triṣu lokeṣu yaśasā vikrameṇa ca</li>
        <li id="R_3.005.008cd">pitṛvratatvaṃ satyaṃ ca tvayi dharmaś ca puṣkalaḥ</li>
    </ul>
    <ul id="R_3.005.009">
        <li id="R_3.005.009ab">tvām āsādya mahātmānaṃ dharmajñaṃ dharmavatsalam</li>
        <li id="R_3.005.009cd">arthitvān nātha vakṣyāmas tac ca naḥ kṣantum arhasi</li>
    </ul>
    <ul id="R_3.005.010">
        <li id="R_3.005.010ab">adhārmas tu mahāṃs tāta bhavet tasya mahīpateḥ</li>
        <li id="R_3.005.010cd">yo hared baliṣaḍbhāgaṃ na ca rakṣati putravat</li>
    </ul>
    <ul id="R_3.005.011">
        <li id="R_3.005.011ab">yuñjānaḥ svān iva prāṇān prāṇair iṣṭān sutān iva</li>
        <li id="R_3.005.011cd">nityayuktaḥ sadā rakṣan sarvān viṣayavāsinaḥ</li>
    </ul>
    <ul id="R_3.005.012">
        <li id="R_3.005.012ab">prāpnoti śāśvatīṃ rāma kīrtiṃ sa bahuvārṣikīm</li>
        <li id="R_3.005.012cd">brahmaṇaḥ sthānam āsādya tatra cāpi mahīyate</li>
    </ul>
    <ul id="R_3.005.013">
        <li id="R_3.005.013ab">yat karoti paraṃ dharmaṃ munir mūlaphalāśanaḥ</li>
        <li id="R_3.005.013cd">tatra rājñaś caturbhāgaḥ prajā dharmeṇa rakṣataḥ</li>
    </ul>
    <ul id="R_3.005.014">
        <li id="R_3.005.014ab">so 'yaṃ brāhmaṇabhūyiṣṭho vānaprasthagaṇo mahān</li>
        <li id="R_3.005.014cd">tvan nātho 'nāthavad rāma rākṣasair vadhyate bhṛśam</li>
    </ul>
    <ul id="R_3.005.015">
        <li id="R_3.005.015ab">ehi paśya śarīrāṇi munīnāṃ bhāvitātmanām</li>
        <li id="R_3.005.015cd">hatānāṃ rākṣasair ghorair bahūnāṃ bahudhā vane</li>
    </ul>
    <ul id="R_3.005.016">
        <li id="R_3.005.016ab">pampānadīnivāsānām anumandākinīm api</li>
        <li id="R_3.005.016cd">citrakūṭālayānāṃ ca kriyate kadanaṃ mahat</li>
    </ul>
    <ul id="R_3.005.017">
        <li id="R_3.005.017ab">evaṃ vayaṃ na mṛṣyāmo viprakāraṃ tapasvinam</li>
        <li id="R_3.005.017cd">kriyamāṇaṃ vane ghoraṃ rakṣobhir bhīmakarmabhiḥ</li>
    </ul>
    <ul id="R_3.005.018">
        <li id="R_3.005.018ab">tatas tvāṃ śaraṇārthaṃ ca śaraṇyaṃ samupasthitāḥ</li>
        <li id="R_3.005.018cd">paripālaya no rāma vadhyamānān niśācaraiḥ</li>
    </ul>
    <ul id="R_3.005.019">
        <li id="R_3.005.019ab">etac chrutvā tu kākutsthas tāpasānāṃ tapasvinām</li>
        <li id="R_3.005.019cd">idaṃ provāca dharmātmā sarvān eva tapasvinaḥ</li>
        <li id="R_3.005.019ef">naivam arhatha māṃ vaktum ājñāpyo 'haṃ tapasvinam</li>
    </ul>
    <ul id="R_3.005.020">
        <li id="R_3.005.020ab">bhavatām arthasiddhyartham āgato 'haṃ yadṛcchayā</li>
        <li id="R_3.005.020cd">tasya me 'yaṃ vane vāso bhaviṣyati mahāphalaḥ</li>
        <li id="R_3.005.020ef">tapasvināṃ raṇe śatrūn hantum icchāmi rākṣasān</li>
    </ul>
    <ul id="R_3.005.021">
        <li id="R_3.005.021ab">
            <seg type="pāda" n="a">dattvā varaṃ cāpi tapodhanānāṃ</seg>
            <seg type="pāda" n="b">dharme dhṛtātmā sahalakṣmaṇena</seg>
        </li>
        <li id="R_3.005.021cd">
            <seg type="pāda" n="c">tapodhanaiś cāpi sahārya vṛttaḥ</seg>
            <seg type="pāda" n="d">sutīṣkṇam evābhijagāma vīraḥ</seg>
        </li>
    </ul>
    <ul id="R_3.006.001">
        <li id="R_3.006.001ab">rāmas tu sahito bhrātrā sītayā ca paraṃtapaḥ</li>
        <li id="R_3.006.001cd">sutīkṣṇasyāśramapadaṃ jagāma saha tair dvijaiḥ</li>
    </ul>
    <ul id="R_3.006.002">
        <li id="R_3.006.002ab">sa gatvā dūram adhvānaṃ nadīs tīrtva bahūdakāḥ</li>
        <li id="R_3.006.002cd">dadarśa vipulaṃ śailaṃ mahāmegham ivonnatam</li>
    </ul>
    <ul id="R_3.006.003">
        <li id="R_3.006.003ab">tatas tad ikṣvākuvarau satataṃ vividhair drumaiḥ</li>
        <li id="R_3.006.003cd">kānanaṃ tau viviśatuḥ sītayā saha rāghavau</li>
    </ul>
    <ul id="R_3.006.004">
        <li id="R_3.006.004ab">praviṣṭas tu vanaṃ ghoraṃ bahupuṣpaphaladrumam</li>
        <li id="R_3.006.004cd">dadarśāśramam ekānte cīramālāpariṣkṛtam</li>
    </ul>
    <ul id="R_3.006.005">
        <li id="R_3.006.005ab">tatra tāpasam āsīnaṃ malapaṅkajaṭādharam</li>
        <li id="R_3.006.005cd">rāmaḥ sutīkṣṇaṃ vidhivat tapovṛddham abhāṣata</li>
    </ul>
    <ul id="R_3.006.006">
        <li id="R_3.006.006ab">rāmo 'ham asmi bhagavan bhavantaṃ draṣṭum āgataḥ</li>
        <li id="R_3.006.006cd">tan mābhivada dharmajña maharṣe satyavikrama</li>
    </ul>
    <ul id="R_3.006.007">
        <li id="R_3.006.007ab">sa nirīkṣya tato vīraṃ rāmaṃ dharmabhṛtāṃ varam</li>
        <li id="R_3.006.007cd">samāśliṣya ca bāhubhyām idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.006.008">
        <li id="R_3.006.008ab">svāgataṃ khalu te vīra rāma dharmabhṛtāṃ vara</li>
        <li id="R_3.006.008cd">āśramo 'yaṃ tvayākrāntaḥ sanātha iva sāmpratam</li>
    </ul>
    <ul id="R_3.006.009">
        <li id="R_3.006.009ab">pratīkṣamāṇas tvām eva nārohe 'haṃ mahāyaśaḥ</li>
        <li id="R_3.006.009cd">devalokam ito vīra dehaṃ tyaktvā mahītale</li>
    </ul>
    <ul id="R_3.006.010">
        <li id="R_3.006.010ab">citrakūṭam upādāya rājyabhraṣṭo 'si me śrutaḥ</li>
        <li id="R_3.006.010cd">ihopayātaḥ kākutstho devarājaḥ śatakratuḥ</li>
        <li id="R_3.006.010ef">sarvāṃl lokāñ jitān āha mama puṇyena karmaṇā</li>
    </ul>
    <ul id="R_3.006.011">
        <li id="R_3.006.011ab">teṣu devarṣijuṣṭeṣu jiteṣu tapasā mayā</li>
        <li id="R_3.006.011cd">matprasādāt sabhāryas tvaṃ viharasva salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_3.006.012">
        <li id="R_3.006.012ab">tam ugratapasaṃ dīptaṃ maharṣiṃ satyavādinam</li>
        <li id="R_3.006.012cd">pratyuvācātmavān rāmo brahmāṇam iva vāsavaḥ</li>
    </ul>
    <ul id="R_3.006.013">
        <li id="R_3.006.013ab">aham evāhariṣyāmi svayaṃ lokān mahāmune</li>
        <li id="R_3.006.013cd">āvāsaṃ tv aham icchāmi pradiṣṭam iha kānane</li>
    </ul>
    <ul id="R_3.006.014">
        <li id="R_3.006.014ab">bhavān sarvatra kuśalaḥ sarvabhūtahite rataḥ</li>
        <li id="R_3.006.014cd">ākhyātaḥ śarabhaṅgena gautamena mahātmanā</li>
    </ul>
    <ul id="R_3.006.015">
        <li id="R_3.006.015ab">evam uktas tu rāmeṇa maharṣir lokaviśrutaḥ</li>
        <li id="R_3.006.015cd">abravīn madhuraṃ vākyaṃ harṣeṇa mahatāplutaḥ</li>
    </ul>
    <ul id="R_3.006.016">
        <li id="R_3.006.016ab">ayam evāśramo rāma guṇavān ramyatām iha</li>
        <li id="R_3.006.016cd">ṛṣisaṃghānucaritaḥ sadā mūlaphalair yutaḥ</li>
    </ul>
    <ul id="R_3.006.017">
        <li id="R_3.006.017ab">imam āśramam āgamya mṛgasaṃghā mahāyaśāḥ</li>
        <li id="R_3.006.017cd">aṭitvā pratigacchanti lobhayitvākutobhayāḥ</li>
    </ul>
    <ul id="R_3.006.018">
        <li id="R_3.006.018ab">tac chrutvā vacanaṃ tasya maharṣer lakṣmaṇāgrajaḥ</li>
        <li id="R_3.006.018cd">uvāca vacanaṃ dhīro vikṛṣya saśaraṃ dhanuḥ</li>
    </ul>
    <ul id="R_3.006.019">
        <li id="R_3.006.019ab">tān ahaṃ sumahābhāga mṛgasaṃghān samāgatān</li>
        <li id="R_3.006.019cd">hanyāṃ niśitadhāreṇa śareṇāśanivarcasā</li>
    </ul>
    <ul id="R_3.006.020">
        <li id="R_3.006.020ab">bhavāṃs tatrābhiṣajyeta kiṃ syāt kṛcchrataraṃ tataḥ</li>
        <li id="R_3.006.020cd">etasminn āśrame vāsaṃ ciraṃ tu na samarthaye</li>
    </ul>
    <ul id="R_3.006.021">
        <li id="R_3.006.021ab">tam evam uktvā varadaṃ rāmaḥ saṃdhyām upāgamat</li>
        <li id="R_3.006.021cd">anvāsya paścimāṃ saṃdhyāṃ tatra vāsam akalpayat</li>
    </ul>
    <ul id="R_3.006.022">
        <li id="R_3.006.022ab">
            <seg type="pāda" n="a">tataḥ śubhaṃ tāpasabhojyam annaṃ</seg>
            <seg type="pāda" n="b">svayaṃ sutīkṣṇaḥ puruṣarṣabhābhyām</seg>
        </li>
        <li id="R_3.006.022cd">
            <seg type="pāda" n="c">tābhyāṃ susatkṛtya dadau mahātmā</seg>
            <seg type="pāda" n="d">saṃdhyānivṛttau rajanīṃ samīkṣya</seg>
        </li>
    </ul>
    <ul id="R_3.007.001">
        <li id="R_3.007.001ab">rāmas tu sahasaumitriḥ sutīkṣṇenābhipūjitaḥ</li>
        <li id="R_3.007.001cd">pariṇamya niśāṃ tatra prabhāte pratyabudhyata</li>
    </ul>
    <ul id="R_3.007.002">
        <li id="R_3.007.002ab">utthāya tu yathākālaṃ rāghavaḥ saha sītayā</li>
        <li id="R_3.007.002cd">upāspṛśat suśītena jalenotpalagandhinā</li>
    </ul>
    <ul id="R_3.007.003">
        <li id="R_3.007.003ab">atha te 'gniṃ surāṃś caiva vaidehī rāmalakṣmaṇau</li>
        <li id="R_3.007.003cd">kālyaṃ vidhivad abhyarcya tapasviśaraṇe vane</li>
    </ul>
    <ul id="R_3.007.004">
        <li id="R_3.007.004ab">udayanntaṃ dinakaraṃ dṛṣṭvā vigatakalmaṣāḥ</li>
        <li id="R_3.007.004cd">sutīkṣṇam abhigamyedaṃ ślakṣṇaṃ vacanam abruvan</li>
    </ul>
    <ul id="R_3.007.005">
        <li id="R_3.007.005ab">sukhoṣitāḥ sma bhagavaṃs tvayā pūjyena pūjitāḥ</li>
        <li id="R_3.007.005cd">āpṛcchāmaḥ prayāsyāmo munayas tvarayanti naḥ</li>
    </ul>
    <ul id="R_3.007.006">
        <li id="R_3.007.006ab">tvarāmahe vayaṃ draṣṭuṃ kṛtsnam āśramamaṇḍalam</li>
        <li id="R_3.007.006cd">ṛṣīṇāṃ puṇyaśīlānāṃ daṇḍakāraṇyavāsinām</li>
    </ul>
    <ul id="R_3.007.007">
        <li id="R_3.007.007ab">abhyanujñātum icchāmaḥ sahaibhir munipuṅgavaiḥ</li>
        <li id="R_3.007.007cd">dharmanityais tapodāntair viśikhair iva pāvakaiḥ</li>
    </ul>
    <ul id="R_3.007.008">
        <li id="R_3.007.008ab">aviṣahyātapo yāvat sūryo nātivirājite</li>
        <li id="R_3.007.008cd">amārgeṇāgatāṃ lakṣmīṃ prāpyevānvayavarjitaḥ</li>
    </ul>
    <ul id="R_3.007.009">
        <li id="R_3.007.009ab">tāvad icchāmahe gantum ity uktvā caraṇau muneḥ</li>
        <li id="R_3.007.009cd">vavande sahasaumitriḥ sītayā saha rāghavaḥ</li>
    </ul>
    <ul id="R_3.007.010">
        <li id="R_3.007.010ab">tau saṃspṛśantau caraṇāv utthāpya munipuṃgavaḥ</li>
        <li id="R_3.007.010cd">gāḍham āliṅgya sasneham idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.007.011">
        <li id="R_3.007.011ab">ariṣṭaṃ gaccha panthānaṃ rāma saumitriṇā saha</li>
        <li id="R_3.007.011cd">sītayā cānayā sārdhaṃ chāyayevānuvṛttayā</li>
    </ul>
    <ul id="R_3.007.012">
        <li id="R_3.007.012ab">paśyāśramapadaṃ ramyaṃ daṇḍakāraṇyavāsinām</li>
        <li id="R_3.007.012cd">eṣāṃ tapasvināṃ vīra tapasā bhāvitātmanām</li>
    </ul>
    <ul id="R_3.007.013">
        <li id="R_3.007.013ab">suprājyaphalamūlāni puṣpitāni vanāni ca</li>
        <li id="R_3.007.013cd">praśāntamṛgayūthāni śāntapakṣigaṇāni ca</li>
    </ul>
    <ul id="R_3.007.014">
        <li id="R_3.007.014ab">phullapaṅkajaṣaḍāni prasannasalilāni ca</li>
        <li id="R_3.007.014cd">kāraṇḍavavikīrṇāni taṭākāni sarāṃsi ca</li>
    </ul>
    <ul id="R_3.007.015">
        <li id="R_3.007.015ab">drakṣyase dṛṣṭiramyāṇi giriprasravaṇāni ca</li>
        <li id="R_3.007.015cd">ramaṇīyāny araṇyāni mayūrābhirutāni ca</li>
    </ul>
    <ul id="R_3.007.016">
        <li id="R_3.007.016ab">gamyatāṃ vatsa saumitre bhavān api ca gacchatu</li>
        <li id="R_3.007.016cd">āgantavyaṃ ca te dṛṣṭvā punar evāśramaṃ mama</li>
    </ul>
    <ul id="R_3.007.017">
        <li id="R_3.007.017ab">evam uktas tathety uktvā kākutsthaḥ sahalakṣmaṇaḥ</li>
        <li id="R_3.007.017cd">pradakṣiṇaṃ muniṃ kṛtā prasthātum upacakrame</li>
    </ul>
    <ul id="R_3.007.018">
        <li id="R_3.007.018ab">tataḥ śubhatare tūṇī dhanuṣī cāyatekṣaṇā</li>
        <li id="R_3.007.018cd">dadau sītā tayor bhrātroḥ khaḍgau ca vimalau tataḥ</li>
    </ul>
    <ul id="R_3.007.019">
        <li id="R_3.007.019ab">ābadhya ca śubhe tūṇī cāpe cādāya sasvane</li>
        <li id="R_3.007.019cd">niṣkrāntāv āśramād gantum ubhau tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_3.008.001">
        <li id="R_3.008.001ab">sutīkṣṇenābhyanujñātaṃ prasthitaṃ raghunandanam</li>
        <li id="R_3.008.001cd">vaidehī snigdhayā vācā bhartāram idam abravīt</li>
    </ul>
    <ul id="R_3.008.002">
        <li id="R_3.008.002ab">ayaṃ dharmaḥ susūkṣmeṇa vidhinā prāpyate mahān</li>
        <li id="R_3.008.002cd">nivṛttena ca śakyo 'yaṃ vyasanāt kāmajād iha</li>
    </ul>
    <ul id="R_3.008.003">
        <li id="R_3.008.003ab">trīṇy eva vyasanāny atra kāmajāni bhavanty uta</li>
        <li id="R_3.008.003cd">mithyā vākyaṃ paramakaṃ tasmād gurutarāv ubhau</li>
        <li id="R_3.008.003ef">paradārābhigamanaṃ vinā vairaṃ ca raudratā</li>
    </ul>
    <ul id="R_3.008.004">
        <li id="R_3.008.004ab">mithyāvākyaṃ na te bhūtaṃ na bhaviṣyati rāghava</li>
        <li id="R_3.008.004cd">kuto 'bhilaṣaṇaṃ strīṇāṃ pareṣāṃ dharmanāśanam</li>
    </ul>
    <ul id="R_3.008.005">
        <li id="R_3.008.005ab">tac ca sarvaṃ mahābāho śakyaṃ voḍhuṃ jitendriyaiḥ</li>
        <li id="R_3.008.005cd">tava vaśyendriyatvaṃ ca jānāmi śubhadarśana</li>
    </ul>
    <ul id="R_3.008.006">
        <li id="R_3.008.006ab">tṛtīyaṃ yad idaṃ raudraṃ paraprāṇābhihiṃsanam</li>
        <li id="R_3.008.006cd">nirvairaṃ kriyate mohāt tac ca te samupasthitam</li>
    </ul>
    <ul id="R_3.008.007">
        <li id="R_3.008.007ab">pratijñātas tvayā vīra daṇḍakāraṇyavāsinām</li>
        <li id="R_3.008.007cd">ṛṣīṇāṃ rakṣaṇārthāya vadhaḥ saṃyati rakṣasām</li>
    </ul>
    <ul id="R_3.008.008">
        <li id="R_3.008.008ab">etannimittaṃ ca vanaṃ daṇḍakā iti viśrutam</li>
        <li id="R_3.008.008cd">prasthitas tvaṃ saha bhrātrā dhṛtabāṇaśarāsanaḥ</li>
    </ul>
    <ul id="R_3.008.009">
        <li id="R_3.008.009ab">tatas tvāṃ prasthitaṃ dṛṣṭvā mama cintākulaṃ manaḥ</li>
        <li id="R_3.008.009cd">tvad vṛttaṃ cintayantyā vai bhaven niḥśreyasaṃ hitam</li>
    </ul>
    <ul id="R_3.008.010">
        <li id="R_3.008.010ab">na hi me rocate vīra gamanaṃ daṇḍakān prati</li>
        <li id="R_3.008.010cd">kāraṇaṃ tatra vakṣyāmi vadantyāḥ śrūyatāṃ mama</li>
    </ul>
    <ul id="R_3.008.011">
        <li id="R_3.008.011ab">tvaṃ hi bāṇadhanuṣpāṇir bhrātrā saha vanaṃ gataḥ</li>
        <li id="R_3.008.011cd">dṛṣṭvā vanacarān sarvān kaccit kuryāḥ śaravyayam</li>
    </ul>
    <ul id="R_3.008.012">
        <li id="R_3.008.012ab">kṣatriyāṇām iha dhanur hutāśasyendhanāni ca</li>
        <li id="R_3.008.012cd">samīpataḥ sthitaṃ tejobalam ucchrayate bhṛśam</li>
    </ul>
    <ul id="R_3.008.013">
        <li id="R_3.008.013ab">purā kila mahābāho tapasvī satyavāk śuciḥ</li>
        <li id="R_3.008.013cd">kasmiṃś cid abhavat puṇye vane ratamṛgadvije</li>
    </ul>
    <ul id="R_3.008.014">
        <li id="R_3.008.014ab">tasyaiva tapaso vighnaṃ kartum indraḥ śacīpatiḥ</li>
        <li id="R_3.008.014cd">khaḍgapāṇir athāgacchad āśramaṃ bhaṭa rūpadhṛk</li>
    </ul>
    <ul id="R_3.008.015">
        <li id="R_3.008.015ab">tasmiṃs tad āśramapade nihitaḥ khaḍga uttamaḥ</li>
        <li id="R_3.008.015cd">sa nyāsavidhinā dattaḥ puṇye tapasi tiṣṭhataḥ</li>
    </ul>
    <ul id="R_3.008.016">
        <li id="R_3.008.016ab">sa tac chastram anuprāpya nyāsarakṣaṇatatparaḥ</li>
        <li id="R_3.008.016cd">vane tu vicaraty eva rakṣan pratyayam ātmanaḥ</li>
    </ul>
    <ul id="R_3.008.017">
        <li id="R_3.008.017ab">yatra gacchaty upādātuṃ mūlāni ca phalāni ca</li>
        <li id="R_3.008.017cd">na vinā yāti taṃ khaḍgaṃ nyāsarakṣaṇatatparaḥ</li>
    </ul>
    <ul id="R_3.008.018">
        <li id="R_3.008.018ab">nityaṃ śastraṃ parivahan krameṇa sa tapodhanaḥ</li>
        <li id="R_3.008.018cd">cakāra raudrīṃ svāṃ buddhiṃ tyaktvā tapasi niścayam</li>
    </ul>
    <ul id="R_3.008.019">
        <li id="R_3.008.019ab">tataḥ sa raudrābhirataḥ pramatto 'dharmakarṣitaḥ</li>
        <li id="R_3.008.019cd">tasya śastrasya saṃvāsāj jagāma narakaṃ muniḥ</li>
    </ul>
    <ul id="R_3.008.020">
        <li id="R_3.008.020ab">snehāc ca bahumānāc ca smāraye tvāṃ na śikṣaye</li>
        <li id="R_3.008.020cd">na kathaṃ cana sā kāryā hṛhītadhanuṣā tvayā</li>
    </ul>
    <ul id="R_3.008.021">
        <li id="R_3.008.021ab">buddhir vairaṃ vinā hantuṃ rākṣasān daṇḍakāśritān</li>
        <li id="R_3.008.021cd">aparādhaṃ vinā hantuṃ lokān vīra na kāmaye</li>
    </ul>
    <ul id="R_3.008.022">
        <li id="R_3.008.022ab">kṣatriyāṇāṃ tu vīrāṇāṃ vaneṣu niyatātmanām</li>
        <li id="R_3.008.022cd">dhanuṣā kāryam etāvad ārtānām abhirakṣaṇam</li>
    </ul>
    <ul id="R_3.008.023">
        <li id="R_3.008.023ab">kva ca śastraṃ kva ca vanaṃ kva ca kṣātraṃ tapaḥ kva ca</li>
        <li id="R_3.008.023cd">vyāviddham idam asmābhir deśadharmas tu pūjyatām</li>
    </ul>
    <ul id="R_3.008.024">
        <li id="R_3.008.024ab">tad āryakaluṣā buddhir jāyate śastrasevanāt</li>
        <li id="R_3.008.024cd">punar gatvā tv ayodhyāyāṃ kṣatradharmaṃ cariṣyasi</li>
    </ul>
    <ul id="R_3.008.025">
        <li id="R_3.008.025ab">akṣayā tu bhavet prītiḥ śvaśrū śvaśurayor mama</li>
        <li id="R_3.008.025cd">yadi rājyaṃ hi saṃnyasya bhaves tvaṃ nirato muniḥ</li>
    </ul>
    <ul id="R_3.008.026">
        <li id="R_3.008.026ab">dharmād arthaḥ prabhavati dharmāt prabhavate sukham</li>
        <li id="R_3.008.026cd">dharmeṇa labhate sarvaṃ dharmasāram idaṃ jagat</li>
    </ul>
    <ul id="R_3.008.027">
        <li id="R_3.008.027ab">ātmānaṃ niyamais tais taiḥ karṣayitvā prayatnataḥ</li>
        <li id="R_3.008.027cd">prāpyate nipuṇair dharmo na sukhāl labhyate sukham</li>
    </ul>
    <ul id="R_3.008.028">
        <li id="R_3.008.028ab">nityaṃ śucimatiḥ saumya cara dharmaṃ tapovane</li>
        <li id="R_3.008.028cd">sarvaṃ hi viditaṃ tubhyaṃ trailokyam api tattvataḥ</li>
    </ul>
    <ul id="R_3.008.029">
        <li id="R_3.008.029ab">
            <seg type="pāda" n="a">strīcāpalād etad udāhṛtaṃ me</seg>
            <seg type="pāda" n="b">dharmaṃ ca vaktuṃ tava kaḥ samarthaḥ</seg>
        </li>
        <li id="R_3.008.029cd">
            <seg type="pāda" n="c">vicārya buddhyā tu sahānujena</seg>
            <seg type="pāda" n="d">yad rocate tat kuru mācireṇa</seg>
        </li>
    </ul>
    <ul id="R_3.009.001">
        <li id="R_3.009.001ab">vākyam etat tu vaidehyā vyāhṛtaṃ bhartṛbhaktayā</li>
        <li id="R_3.009.001cd">śrutvā dharme sthito rāmaḥ pratyuvācātha maithilīm</li>
    </ul>
    <ul id="R_3.009.002">
        <li id="R_3.009.002ab">hitam uktaṃ tvayā devi snigdhayā sadṛśaṃ vacaḥ</li>
        <li id="R_3.009.002cd">kulaṃ vyapadiśantyā ca dharmajñe janakātmaje</li>
    </ul>
    <ul id="R_3.009.003">
        <li id="R_3.009.003ab">kiṃ tu vakṣyāmy ahaṃ devi tvayaivoktam idaṃ vacaḥ</li>
        <li id="R_3.009.003cd">kṣatriyair dhāryate cāpo nārtaśabdo bhaved iti</li>
    </ul>
    <ul id="R_3.009.004">
        <li id="R_3.009.004ab">te cārtā daṇḍakāraṇye munayaḥ saṃśitavratāḥ</li>
        <li id="R_3.009.004cd">māṃ sīte svayam āgamya śaraṇyāḥ śaraṇaṃ gatāḥ</li>
    </ul>
    <ul id="R_3.009.005">
        <li id="R_3.009.005ab">vasanto dharmaniratā vane mūlaphalāśanāḥ</li>
        <li id="R_3.009.005cd">na labhante sukhaṃ bhītā rākṣasaiḥ krūrakarmabhiḥ</li>
    </ul>
    <ul id="R_3.009.006">
        <li id="R_3.009.006ab">kāle kāle ca niratā niyamair vividhair vane</li>
        <li id="R_3.009.006cd">bhakṣyante rākṣasair bhīmair naramāṃsopajīvibhiḥ</li>
    </ul>
    <ul id="R_3.009.007">
        <li id="R_3.009.007ab">te bhakṣyamāṇā munayo daṇḍakāraṇyavāsinaḥ</li>
        <li id="R_3.009.007cd">asmān abhyavapadyeti mām ūcur dvijasattamāḥ</li>
    </ul>
    <ul id="R_3.009.008">
        <li id="R_3.009.008ab">mayā tu vacanaṃ śrutvā teṣām evaṃ mukhāc cyutam</li>
        <li id="R_3.009.008cd">kṛtvā caraṇaśuśrūṣāṃ vākyam etad udāhṛtam</li>
    </ul>
    <ul id="R_3.009.009">
        <li id="R_3.009.009ab">prasīdantu bhavanto me hrīr eṣā hi mamātulā</li>
        <li id="R_3.009.009cd">yadīdṛśair ahaṃ viprair upastheyair upasthitaḥ</li>
        <li id="R_3.009.009ef">kiṃ karomīti ca mayā vyāhṛtaṃ dvijasaṃnidhau</li>
    </ul>
    <ul id="R_3.009.010">
        <li id="R_3.009.010ab">sarvair eva samāgamya vāg iyaṃ samudāhṛtā</li>
        <li id="R_3.009.010cd">rākṣasair daṇḍakāraṇye bahubhiḥ kāmarūpibhiḥ</li>
        <li id="R_3.009.010ef">arditāḥ sma bhṛśaṃ rāma bhavān nas trātum arhati</li>
    </ul>
    <ul id="R_3.009.011">
        <li id="R_3.009.011ab">homakāle tu saṃprāpte parvakāleṣu cānagha</li>
        <li id="R_3.009.011cd">dharṣayanti sma durdharṣā rākṣasāḥ piśitāśanāḥ</li>
    </ul>
    <ul id="R_3.009.012">
        <li id="R_3.009.012ab">rākṣasair dharṣitānāṃ ca tāpasānāṃ tapasvinām</li>
        <li id="R_3.009.012cd">gatiṃ mṛgayamāṇānāṃ bhavān naḥ paramā gatiḥ</li>
    </ul>
    <ul id="R_3.009.013">
        <li id="R_3.009.013ab">kāmaṃ tapaḥ prabhāvena śaktā hantuṃ niśācarān</li>
        <li id="R_3.009.013cd">cirārjitaṃ tu necchāmas tapaḥ khaṇḍayituṃ vayam</li>
    </ul>
    <ul id="R_3.009.014">
        <li id="R_3.009.014ab">bahuvighnaṃ taponityaṃ duścaraṃ caiva rāghava</li>
        <li id="R_3.009.014cd">tena śāpaṃ na muñcāmo bhakṣyamāṇāś ca rākṣasaiḥ</li>
    </ul>
    <ul id="R_3.009.015">
        <li id="R_3.009.015ab">tad ardyamānān rakṣobhir daṇḍakāraṇyavāsibhiḥ</li>
        <li id="R_3.009.015cd">rakṣanas tvaṃ saha bhrātrā tvannāthā hi vayaṃ vane</li>
    </ul>
    <ul id="R_3.009.016">
        <li id="R_3.009.016ab">mayā caitad vacaḥ śrutvā kārtsnyena paripālanam</li>
        <li id="R_3.009.016cd">ṛṣīṇāṃ daṇḍakāraṇye saṃśrutaṃ janakātmaje</li>
    </ul>
    <ul id="R_3.009.017">
        <li id="R_3.009.017ab">saṃśrutya ca na śakṣyāmi jīvamānaḥ pratiśravam</li>
        <li id="R_3.009.017cd">munīnām anyathā kartuṃ satyam iṣṭaṃ hi me sadā</li>
    </ul>
    <ul id="R_3.009.018">
        <li id="R_3.009.018ab">apy ahaṃ jīvitaṃ jahyāṃ tvāṃ vā sīte salakṣmaṇām</li>
        <li id="R_3.009.018cd">na tu pratijñāṃ saṃśrutya brāhmaṇebhyo viśeṣataḥ</li>
    </ul>
    <ul id="R_3.009.019">
        <li id="R_3.009.019ab">tad avaśyaṃ mayā kāryam ṛṣīṇāṃ paripālanam</li>
        <li id="R_3.009.019cd">anuktenāpi vaidehi pratijñāya tu kiṃ punaḥ</li>
    </ul>
    <ul id="R_3.009.020">
        <li id="R_3.009.020ab">mama snehāc ca sauhārdād idam uktaṃ tvayā vacaḥ</li>
        <li id="R_3.009.020cd">parituṣṭo 'smy ahaṃ sīte na hy aniṣṭo 'nuśiṣyate</li>
        <li id="R_3.009.020ef">sadṛśaṃ cānurūpaṃ ca kulasya tava śobhane</li>
    </ul>
    <ul id="R_3.009.021">
        <li id="R_3.009.021ab">
            <seg type="pāda" n="a">ity evam uktvā vacanaṃ mahātmā</seg>
            <seg type="pāda" n="b">sītāṃ priyāṃ maithila rājaputrīm</seg>
        </li>
        <li id="R_3.009.021cd">
            <seg type="pāda" n="c">rāmo dhanuṣmān sahalakṣmaṇena</seg>
            <seg type="pāda" n="d">jagāma ramyāṇi tapovanāni</seg>
        </li>
    </ul>
    <ul id="R_3.010.001">
        <li id="R_3.010.001ab">agrataḥ prayayau rāmaḥ sītā madhye sumadhyamā</li>
        <li id="R_3.010.001cd">pṛṣṭhatas tu dhanuṣpāṇir lakṣmaṇo 'nujagāma ha</li>
    </ul>
    <ul id="R_3.010.002">
        <li id="R_3.010.002ab">tau paśyamānau vividhāñ śailaprasthān vanāni ca</li>
        <li id="R_3.010.002cd">nadīś ca vividhā ramyā jagmatuḥ saha sītayā</li>
    </ul>
    <ul id="R_3.010.003">
        <li id="R_3.010.003ab">sārasāṃś cakravākāṃś ca nadīpulinacāriṇaḥ</li>
        <li id="R_3.010.003cd">sarāṃsi ca sapadmāni yutāni jalajaiḥ khagaiḥ</li>
    </ul>
    <ul id="R_3.010.004">
        <li id="R_3.010.004ab">yūthabaddhāṃś ca pṛṣatān madonmattān viṣāṇinaḥ</li>
        <li id="R_3.010.004cd">mahiṣāṃś ca varāhāṃś ca gajāṃś ca drumavairiṇaḥ</li>
    </ul>
    <ul id="R_3.010.005">
        <li id="R_3.010.005ab">te gatvā dūram adhvānaṃ lambamāne divākare</li>
        <li id="R_3.010.005cd">dadṛśuḥ sahitā ramyaṃ taṭākaṃ yojanāyatam</li>
    </ul>
    <ul id="R_3.010.006">
        <li id="R_3.010.006ab">padmapuṣkarasaṃbādhaṃ gajayūthair alaṃkṛtam</li>
        <li id="R_3.010.006cd">sārasair haṃsakādambaiḥ saṃkulaṃ jalacāribhiḥ</li>
    </ul>
    <ul id="R_3.010.007">
        <li id="R_3.010.007ab">prasannasalile ramyatasmin sarasi śuśruve</li>
        <li id="R_3.010.007cd">gītavāditranirghoṣo na tu kaś cana dṛśyate</li>
    </ul>
    <ul id="R_3.010.008">
        <li id="R_3.010.008ab">tataḥ kautūhalād rāmo lakṣmaṇaś ca mahārathaḥ</li>
        <li id="R_3.010.008cd">muniṃ dharmabhṛtaṃ nāma praṣṭuṃ samupacakrame</li>
    </ul>
    <ul id="R_3.010.009">
        <li id="R_3.010.009ab">idam atyadbhutaṃ śrutvā sarveṣāṃ no mahāmune</li>
        <li id="R_3.010.009cd">kautūhalaṃ mahaj jātaṃ kim idaṃ sādhu kathyatām</li>
    </ul>
    <ul id="R_3.010.010">
        <li id="R_3.010.010ab">tenaivam ukto dharmātmā rāghaveṇa munis tadā</li>
        <li id="R_3.010.010cd">prabhāvaṃ sarasaḥ kṛtsnam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_3.010.011">
        <li id="R_3.010.011ab">idaṃ pañcāpsaro nāma taṭākaṃ sārvakālikam</li>
        <li id="R_3.010.011cd">nirmitaṃ tapasā rāma muninā māṇḍakarṇinā</li>
    </ul>
    <ul id="R_3.010.012">
        <li id="R_3.010.012ab">sa hi tepe tapas tīvraṃ māṇḍakarṇir mahāmuniḥ</li>
        <li id="R_3.010.012cd">daśavarṣasahasrāṇi vāyubhakṣo jalāśraya</li>
    </ul>
    <ul id="R_3.010.013">
        <li id="R_3.010.013ab">tataḥ pravyathitāḥ sarve devāḥ sāgnipurogamāḥ</li>
        <li id="R_3.010.013cd">abruvan vacanaṃ sarve paraspara samāgatāḥ</li>
        <li id="R_3.010.013ef">asmakaṃ kasya cit sthānam eṣa prārthayate muniḥ</li>
    </ul>
    <ul id="R_3.010.014">
        <li id="R_3.010.014ab">tataḥ kartuṃ tapovighnaṃ sarvair devair niyojitāḥ</li>
        <li id="R_3.010.014cd">pradhānāpsarasaḥ pañcavidyuccalitavarcasaḥ</li>
    </ul>
    <ul id="R_3.010.015">
        <li id="R_3.010.015ab">apsarobhis tatas tābhir munir dṛṣṭaparāvaraḥ</li>
        <li id="R_3.010.015cd">nīto madanavaśyatvaṃ surāṇāṃ kāryasiddhaye</li>
    </ul>
    <ul id="R_3.010.016">
        <li id="R_3.010.016ab">tāś caivāpsarasaḥ pañcamuneḥ patnītvam āgatāḥ</li>
        <li id="R_3.010.016cd">taṭāke nirmitaṃ tāsām asminn antarhitaṃ gṛham</li>
    </ul>
    <ul id="R_3.010.017">
        <li id="R_3.010.017ab">tatraivāpsarasaḥ pañcanivasantyo yathāsukham</li>
        <li id="R_3.010.017cd">ramayanti tapoyogān muniṃ yauvanam āsthitam</li>
    </ul>
    <ul id="R_3.010.018">
        <li id="R_3.010.018ab">tāsāṃ saṃkrīḍamānānām eṣa vāditraniḥsvanaḥ</li>
        <li id="R_3.010.018cd">śrūyate bhūṣaṇonmiśro gītaśabdo manoharaḥ</li>
    </ul>
    <ul id="R_3.010.019">
        <li id="R_3.010.019ab">āścaryam iti tasyaitad vacanaṃ bhāvitātmanaḥ</li>
        <li id="R_3.010.019cd">rāghavaḥ pratijagrāha saha bhrātrā mahāyaśāḥ</li>
    </ul>
    <ul id="R_3.010.020">
        <li id="R_3.010.020ab">evaṃ kathayamānasya dadarśāśramamaṇḍalam</li>
        <li id="R_3.010.020cd">kuśacīraparikṣiptaṃ nānāvṛkṣasamāvṛtam</li>
    </ul>
    <ul id="R_3.010.021">
        <li id="R_3.010.021ab">praviśya saha vaidehyā lakṣmaṇena ca rāghavaḥ</li>
        <li id="R_3.010.021cd">tadā tasmin sa kākutsthaḥ śrīmaty āśramamaṇḍale</li>
    </ul>
    <ul id="R_3.010.022">
        <li id="R_3.010.022ab">uṣitvā susukhaṃ tatra pūrjyamāno maharṣibhiḥ</li>
        <li id="R_3.010.022cd">jagāma cāśramāṃs teṣāṃ paryāyeṇa tapasvinām</li>
    </ul>
    <ul id="R_3.010.023">
        <li id="R_3.010.023ab">yeṣām uṣitavān pūrvaṃ sakāśe sa mahāstravit</li>
        <li id="R_3.010.023cd">kva cit paridaśān māsān ekaṃ saṃvatsaraṃ kva cit</li>
    </ul>
    <ul id="R_3.010.024">
        <li id="R_3.010.024ab">kva cic ca caturo māsān pañcaṣaṭ cāparān kva cit</li>
        <li id="R_3.010.024cd">aparatrādhikān māsān adhyardham adhikaṃ kva cit</li>
    </ul>
    <ul id="R_3.010.025">
        <li id="R_3.010.025ab">trīn māsān aṣṭamāsāṃś ca rāghavo nyavasat sukham</li>
        <li id="R_3.010.025cd">tathā saṃvasatas tasya munīnām āśrameṣu vai</li>
        <li id="R_3.010.025ef">ramataś cānukulyena yayuḥ saṃvatsarā daśa</li>
    </ul>
    <ul id="R_3.010.026">
        <li id="R_3.010.026ab">parisṛtya ca dharmajño rāghavaḥ saha sītayā</li>
        <li id="R_3.010.026cd">sutīkṣṇasyāśramaṃ śrīmān punar evājagāma ha</li>
    </ul>
    <ul id="R_3.010.027">
        <li id="R_3.010.027ab">sa tam āśramam āgamya munibhiḥ pratipūjitaḥ</li>
        <li id="R_3.010.027cd">tatrāpi nyavasad rāmaḥ kaṃ cit kālam ariṃdamaḥ</li>
    </ul>
    <ul id="R_3.010.028">
        <li id="R_3.010.028ab">athāśramastho vinayāt kadā cit taṃ mahāmunim</li>
        <li id="R_3.010.028cd">upāsīnaḥ sa kākutsthaḥ sutīkṣṇam idam abravīt</li>
    </ul>
    <ul id="R_3.010.029">
        <li id="R_3.010.029ab">asminn araṇye bhagavann agastyo munisattamaḥ</li>
        <li id="R_3.010.029cd">vasatīti mayā nityaṃ kathāḥ kathayatāṃ śrutam</li>
    </ul>
    <ul id="R_3.010.030">
        <li id="R_3.010.030ab">na tu jānāmi taṃ deśaṃ vanasyāsya mahattayā</li>
        <li id="R_3.010.030cd">kutrāśramapadaṃ puṇyaṃ maharṣes tasya dhīmataḥ</li>
    </ul>
    <ul id="R_3.010.031">
        <li id="R_3.010.031ab">prasādāt tatra bhavataḥ sānujaḥ saha sītayā</li>
        <li id="R_3.010.031cd">agastyam abhigaccheyam abhivādayituṃ munim</li>
    </ul>
    <ul id="R_3.010.032">
        <li id="R_3.010.032ab">manoratho mahān eṣa hṛdi saṃparivartate</li>
        <li id="R_3.010.032cd">yad ahaṃ taṃ munivaraṃ śuśrūṣeyam api svayam</li>
    </ul>
    <ul id="R_3.010.033">
        <li id="R_3.010.033ab">iti rāmasya sa muniḥ śrutvā dharmātmano vacaḥ</li>
        <li id="R_3.010.033cd">sutīkṣṇaḥ pratyuvācedaṃ prīto daśarathātmajam</li>
    </ul>
    <ul id="R_3.010.034">
        <li id="R_3.010.034ab">aham apy etad eva tvāṃ vaktukāmaḥ salakṣmaṇam</li>
        <li id="R_3.010.034cd">agastyam abhigaccheti sītayā saha rāghava</li>
    </ul>
    <ul id="R_3.010.035">
        <li id="R_3.010.035ab">diṣṭyā tv idānīm arthe 'smin svayam eva bravīṣi mām</li>
        <li id="R_3.010.035cd">aham ākhyāmi te vatsa yatrāgastyo mahāmuniḥ</li>
    </ul>
    <ul id="R_3.010.036">
        <li id="R_3.010.036ab">yojanāny āśramāt tāta yāhi catvāri vai tataḥ</li>
        <li id="R_3.010.036cd">dakṣiṇena mahāñ śrīmān agastyabhrātur āśramaḥ</li>
    </ul>
    <ul id="R_3.010.037">
        <li id="R_3.010.037ab">sthalaprāye vanoddeśe pippalīvanaśobhite</li>
        <li id="R_3.010.037cd">bahupuṣpaphale ramye nānāśakuninādite</li>
    </ul>
    <ul id="R_3.010.038">
        <li id="R_3.010.038ab">padminyo vividhās tatra prasannasalilāḥ śivāḥ</li>
        <li id="R_3.010.038cd">haṃsakāraṇḍavākīrṇāś cakravākopaśobhitāḥ</li>
    </ul>
    <ul id="R_3.010.039">
        <li id="R_3.010.039ab">tatraikāṃ rajanīm uṣya prabhāte rāma gamyatām</li>
        <li id="R_3.010.039cd">dakṣiṇāṃ diśam āsthāya vanakhaṇḍasya pārśvataḥ</li>
    </ul>
    <ul id="R_3.010.040">
        <li id="R_3.010.040ab">tatrāgastyāśramapadaṃ gatvā yojanam antaram</li>
        <li id="R_3.010.040cd">ramaṇīye vanoddeśe bahupādapa saṃvṛte</li>
        <li id="R_3.010.040ef">raṃsyate tatra vaidehī lakṣmaṇaś ca tvayā saha</li>
    </ul>
    <ul id="R_3.010.041">
        <li id="R_3.010.041ab">sa hi ramyo vanoddeśo bahupādapasaṃkulaḥ</li>
        <li id="R_3.010.041cd">yadi buddhiḥ kṛtā draṣṭum agastyaṃ taṃ mahāmunim</li>
        <li id="R_3.010.041ef">adyaiva gamane buddhiṃ rocayasva mahāyaśaḥ</li>
    </ul>
    <ul id="R_3.010.042">
        <li id="R_3.010.042ab">iti rāmo muneḥ śrutvā saha bhrātrābhivādya ca</li>
        <li id="R_3.010.042cd">pratasthe 'gastyam uddiśya sānujaḥ saha sītayā</li>
    </ul>
    <ul id="R_3.010.043">
        <li id="R_3.010.043ab">paśyan vanāni citrāṇi parvapāṃś cābhrasaṃnibhān</li>
        <li id="R_3.010.043cd">sarāṃsi saritaś caiva pathi mārgavaśānugāḥ</li>
    </ul>
    <ul id="R_3.010.044">
        <li id="R_3.010.044ab">sutīkṣṇenopadiṣṭena gatvā tena pathā sukham</li>
        <li id="R_3.010.044cd">idaṃ paramasaṃhṛṣṭo vākyaṃ lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_3.010.045">
        <li id="R_3.010.045ab">etad evāśramapadaṃ nūnaṃ tasya mahātmanaḥ</li>
        <li id="R_3.010.045cd">agastyasya muner bhrātur dṛśyate puṇyakarmaṇaḥ</li>
    </ul>
    <ul id="R_3.010.046">
        <li id="R_3.010.046ab">yathā hīme vanasyāsya jñātāḥ pathi sahasraśaḥ</li>
        <li id="R_3.010.046cd">saṃnatāḥ phalabhareṇa puṣpabhāreṇa ca drumāḥ</li>
    </ul>
    <ul id="R_3.010.047">
        <li id="R_3.010.047ab">pippalīnāṃ ca pakvānāṃ vanād asmād upāgataḥ</li>
        <li id="R_3.010.047cd">gandho 'yaṃ pavanotkṣiptaḥ sahasā kaṭukodayaḥ</li>
    </ul>
    <ul id="R_3.010.048">
        <li id="R_3.010.048ab">tatra tatra ca dṛśyante saṃkṣiptāḥ kāṣṭhasaṃcayāḥ</li>
        <li id="R_3.010.048cd">lūnāś ca pathi dṛśyante darbhā vaidūryavarcasaḥ</li>
    </ul>
    <ul id="R_3.010.049">
        <li id="R_3.010.049ab">etac ca vanamadhyasthaṃ kṛṣṇābhraśikharopamam</li>
        <li id="R_3.010.049cd">pāvakasyāśramasthasya dhūmāgraṃ saṃpradṛśyate</li>
    </ul>
    <ul id="R_3.010.050">
        <li id="R_3.010.050ab">vivikteṣu ca tīrtheṣu kṛtasnānā dvijātayaḥ</li>
        <li id="R_3.010.050cd">puṣpopahāraṃ kurvanti kusumaiḥ svayam ārjitaiḥ</li>
    </ul>
    <ul id="R_3.010.051">
        <li id="R_3.010.051ab">tat sutīkṣṇasya vacanaṃ yathā saumya mayā śrutam</li>
        <li id="R_3.010.051cd">agastyasyāśramo bhrātur nūnam eṣa bhaviṣyati</li>
    </ul>
    <ul id="R_3.010.052">
        <li id="R_3.010.052ab">nigṛhya tarasā mṛtyuṃ lokānāṃ hitakāmyayā</li>
        <li id="R_3.010.052cd">yasya bhrātrā kṛteyaṃ dik śaraṇyā puṇyakarmaṇā</li>
    </ul>
    <ul id="R_3.010.053">
        <li id="R_3.010.053ab">ihaikadā kila krūro vātāpir api celvalaḥ</li>
        <li id="R_3.010.053cd">bhrātarau sahitāv āstāṃ brāhmaṇaghnau mahāsurau</li>
    </ul>
    <ul id="R_3.010.054">
        <li id="R_3.010.054ab">dhārayan brāhmaṇaṃ rūpam ilvalaḥ saṃskṛtaṃ vadan</li>
        <li id="R_3.010.054cd">āmantrayati viprān sa śrāddham uddiśya nirghṛṇaḥ</li>
    </ul>
    <ul id="R_3.010.055">
        <li id="R_3.010.055ab">bhrātaraṃ saṃskṛtaṃ bhrātā tatas taṃ meṣarūpiṇam</li>
        <li id="R_3.010.055cd">tān dvijān bhojayām āsa śrāddhadṛṣṭena karmaṇā</li>
    </ul>
    <ul id="R_3.010.056">
        <li id="R_3.010.056ab">tato bhuktavatāṃ teṣāṃ viprāṇām ilvalo 'bravīt</li>
        <li id="R_3.010.056cd">vātāpe niṣkramasveti svareṇa mahatā vadan</li>
    </ul>
    <ul id="R_3.010.057">
        <li id="R_3.010.057ab">tato bhrātur vacaḥ śrutvā vātāpir meṣavan nadan</li>
        <li id="R_3.010.057cd">bhittvā bhitvā śarīrāṇi brāhmaṇānāṃ viniṣpatat</li>
    </ul>
    <ul id="R_3.010.058">
        <li id="R_3.010.058ab">brāhmaṇānāṃ sahasrāṇi tair evaṃ kāmarūpibhiḥ</li>
        <li id="R_3.010.058cd">vināśitāni saṃhatya nityaśaḥ piśitāśanaiḥ</li>
    </ul>
    <ul id="R_3.010.059">
        <li id="R_3.010.059ab">agastyena tadā devaiḥ prārthitena maharṣiṇā</li>
        <li id="R_3.010.059cd">anubhūya kila śrāddhe bhakṣitaḥ sa mahāsuraḥ</li>
    </ul>
    <ul id="R_3.010.060">
        <li id="R_3.010.060ab">tataḥ saṃpannam ity uktvā dattvā hastāvasecanam</li>
        <li id="R_3.010.060cd">bhrātaraṃ niṣkramasveti ilvalaḥ so 'bhyabhāṣata</li>
    </ul>
    <ul id="R_3.010.061">
        <li id="R_3.010.061ab">taṃ tathā bhāṣamāṇaṃ tu bhrātaraṃ vipraghātinam</li>
        <li id="R_3.010.061cd">abravīt prahasan dhīmān agastyo munisattamaḥ</li>
    </ul>
    <ul id="R_3.010.062">
        <li id="R_3.010.062ab">kuto niṣkramituṃ śaktir mayā jīrṇasya rakṣasaḥ</li>
        <li id="R_3.010.062cd">bhrātus te meṣa rūpasya gatasya yamasādanam</li>
    </ul>
    <ul id="R_3.010.063">
        <li id="R_3.010.063ab">atha tasya vacaḥ śrutvā bhrātur nidhanasaṃśritam</li>
        <li id="R_3.010.063cd">pradharṣayitum ārebhe muniṃ krodhān niśācaraḥ</li>
    </ul>
    <ul id="R_3.010.064">
        <li id="R_3.010.064ab">so 'bhyadravad dvijendraṃ taṃ muninā dīptatejasā</li>
        <li id="R_3.010.064cd">cakṣuṣānalakalpena nirdagdho nidhanaṃ gataḥ</li>
    </ul>
    <ul id="R_3.010.065">
        <li id="R_3.010.065ab">tasyāyam āśramo bhrātus taṭākavanaśobhitaḥ</li>
        <li id="R_3.010.065cd">viprānukampayā yena karmedaṃ duṣkaraṃ kṛtam</li>
    </ul>
    <ul id="R_3.010.066">
        <li id="R_3.010.066ab">evaṃ kathayamānasya tasya saumitriṇā saha</li>
        <li id="R_3.010.066cd">rāmasyāstaṃ gataḥ sūryaḥ saṃdhyākālo 'bhyavartata</li>
    </ul>
    <ul id="R_3.010.067">
        <li id="R_3.010.067ab">upāsya paścimāṃ saṃdhyāṃ saha bhrātrā yathāvidhi</li>
        <li id="R_3.010.067cd">praviveśāśramapadaṃ tam ṛṣiṃ cābhyavādayan</li>
    </ul>
    <ul id="R_3.010.068">
        <li id="R_3.010.068ab">samyak pratigṛhītas tu muninā tena rāghavaḥ</li>
        <li id="R_3.010.068cd">nyavasat tāṃ niśām ekāṃ prāśya mūlaphalāni ca</li>
    </ul>
    <ul id="R_3.010.069">
        <li id="R_3.010.069ab">tasyāṃ rātryāṃ vyatītāyāṃ vimale sūryamaṇḍale</li>
        <li id="R_3.010.069cd">bhrātaraṃ tam agastyasya āmantrayata rāghavaḥ</li>
    </ul>
    <ul id="R_3.010.070">
        <li id="R_3.010.070ab">abhivādaye tvā bhagavan sukham adhyuṣito niśām</li>
        <li id="R_3.010.070cd">āmantraye tvāṃ gacchāmi guruṃ te draṣṭum agrajam</li>
    </ul>
    <ul id="R_3.010.071">
        <li id="R_3.010.071ab">gamyatām iti tenokto jagāma raghunandanaḥ</li>
        <li id="R_3.010.071cd">yathoddiṣṭena mārgeṇa vanaṃ tac cāvalokayan</li>
    </ul>
    <ul id="R_3.010.072">
        <li id="R_3.010.072ab">nīvārān panasāṃs tālāṃs timiśān vañjulān dhavān</li>
        <li id="R_3.010.072cd">ciribilvān madhūkāṃś ca bilvān api ca tindukān</li>
    </ul>
    <ul id="R_3.010.073">
        <li id="R_3.010.073ab">puṣpitān puṣpitāgrābhir latābhir anuveṣṭitān</li>
        <li id="R_3.010.073cd">dadarśa rāmaḥ śataśas tatra kāntārapādapān</li>
    </ul>
    <ul id="R_3.010.074">
        <li id="R_3.010.074ab">hastihastair vimṛditān vānarair upaśobhitān</li>
        <li id="R_3.010.074cd">mattaiḥ śakunisaṃghaiś ca śataśaḥ pratināditān</li>
    </ul>
    <ul id="R_3.010.075">
        <li id="R_3.010.075ab">tato 'bravīt samīpasthaṃ rāmo rājīvalocanaḥ</li>
        <li id="R_3.010.075cd">pṛṣṭhato 'nugataṃ vīraṃ lakṣmaṇaṃ lakṣmivardhanam</li>
    </ul>
    <ul id="R_3.010.076">
        <li id="R_3.010.076ab">snigdhapatrā yathā vṛkṣā yathā kṣāntā mṛgadvijāḥ</li>
        <li id="R_3.010.076cd">āśramo nātidūrastho maharṣer bhāvitātmanaḥ</li>
    </ul>
    <ul id="R_3.010.077">
        <li id="R_3.010.077ab">agastya iti vikhyāto loke svenaiva karmaṇā</li>
        <li id="R_3.010.077cd">āśramo dṛśyate tasya pariśrānta śramāpahaḥ</li>
    </ul>
    <ul id="R_3.010.078">
        <li id="R_3.010.078ab">prājyadhūmākulavanaś cīramālāpariṣkṛtaḥ</li>
        <li id="R_3.010.078cd">praśāntamṛgayūthaś ca nānāśakunināditaḥ</li>
    </ul>
    <ul id="R_3.010.079">
        <li id="R_3.010.079ab">nigṛhya tarasā mṛtyuṃ lokānāṃ hitakāmyayā</li>
        <li id="R_3.010.079cd">dakṣiṇā dik kṛtā yena śaraṇyā puṇyakarmaṇā</li>
    </ul>
    <ul id="R_3.010.080">
        <li id="R_3.010.080ab">tasyedam āśramapadaṃ prabhāvād yasya rākṣasaiḥ</li>
        <li id="R_3.010.080cd">dig iyaṃ dakṣiṇā trāsād dṛśyate nopabhujyate</li>
    </ul>
    <ul id="R_3.010.081">
        <li id="R_3.010.081ab">yadā prabhṛti cākrāntā dig iyaṃ puṇyakarmaṇā</li>
        <li id="R_3.010.081cd">tadā prabhṛti nirvairāḥ praśāntā rajanīcarāḥ</li>
    </ul>
    <ul id="R_3.010.082">
        <li id="R_3.010.082ab">nāmnā ceyaṃ bhagavato dakṣiṇā dik pradakṣiṇā</li>
        <li id="R_3.010.082cd">prathitā triṣu lokeṣu durdharṣā krūrakarmabhiḥ</li>
    </ul>
    <ul id="R_3.010.083">
        <li id="R_3.010.083ab">mārgaṃ niroddhuṃ satataṃ bhāskarasyācalottamaḥ</li>
        <li id="R_3.010.083cd">saṃdeśaṃ pālayaṃs tasya vindhyaśaulo na vardhate</li>
    </ul>
    <ul id="R_3.010.084">
        <li id="R_3.010.084ab">ayaṃ dīrghāyuṣas tasya loke viśrutakarmaṇaḥ</li>
        <li id="R_3.010.084cd">agastyasyāśramaḥ śrīmān vinītamṛgasevitaḥ</li>
    </ul>
    <ul id="R_3.010.085">
        <li id="R_3.010.085ab">eṣa lokārcitaḥ sādhur hite nityaṃ rataḥ satām</li>
        <li id="R_3.010.085cd">asmān adhigatān eṣa śreyasā yojayiṣyati</li>
    </ul>
    <ul id="R_3.010.086">
        <li id="R_3.010.086ab">ārādhayiṣyāmy atrāham agastyaṃ taṃ mahāmunim</li>
        <li id="R_3.010.086cd">śeṣaṃ ca vanavāsasya saumya vatsyāmy ahaṃ prabho</li>
    </ul>
    <ul id="R_3.010.087">
        <li id="R_3.010.087ab">atra devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_3.010.087cd">agastyaṃ niyatāhāraṃ satataṃ paryupāsate</li>
    </ul>
    <ul id="R_3.010.088">
        <li id="R_3.010.088ab">nātra jīven mṛṣāvādī krūro vā yadi vā śaṭhaḥ</li>
        <li id="R_3.010.088cd">nṛśaṃsaḥ kāmavṛtto vā munir eṣa tathāvidhaḥ</li>
    </ul>
    <ul id="R_3.010.089">
        <li id="R_3.010.089ab">atra devāś ca yakṣāś ca nāgāś ca patagaiḥ saha</li>
        <li id="R_3.010.089cd">vasanti niyatāhārā dharmam ārādhayiṣṇavaḥ</li>
    </ul>
    <ul id="R_3.010.090">
        <li id="R_3.010.090ab">atra siddhā mahātmāno vimānaiḥ sūryasaṃnibhaiḥ</li>
        <li id="R_3.010.090cd">tyaktvā dehān navair dehaiḥ svaryātāḥ paramarṣayaḥ</li>
    </ul>
    <ul id="R_3.010.091">
        <li id="R_3.010.091ab">yakṣatvam amaratvaṃ ca rājyāni vividhāni ca</li>
        <li id="R_3.010.091cd">atra devāḥ prayacchanti bhūtair ārādhitāḥ śubhaiḥ</li>
    </ul>
    <ul id="R_3.010.092">
        <li id="R_3.010.092ab">āgatāḥ smāśramapadaṃ saumitre praviśāgrataḥ</li>
        <li id="R_3.010.092cd">nivedayeha māṃ prāptam ṛṣaye saha sītayā</li>
    </ul>
    <ul id="R_3.011.001">
        <li id="R_3.011.001ab">sa praviśyāśramapadaṃ lakṣmaṇo rāghavānujaḥ</li>
        <li id="R_3.011.001cd">agastyaśiṣyam āsādya vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_3.011.002">
        <li id="R_3.011.002ab">rājā daśaratho nāma jyeṣṭhas tasya suto balī</li>
        <li id="R_3.011.002cd">rāmaḥ prāpto muniṃ draṣṭuṃ bhāryayā saha sītayā</li>
    </ul>
    <ul id="R_3.011.003">
        <li id="R_3.011.003ab">lakṣmaṇo nāma tasyāhaṃ bhrātā tv avarajo hitaḥ</li>
        <li id="R_3.011.003cd">anukūlaś ca bhaktaś ca yadi te śrotram āgataḥ</li>
    </ul>
    <ul id="R_3.011.004">
        <li id="R_3.011.004ab">te vayaṃ vanam atyugraṃ praviṣṭāḥ pitṛśāsanāt</li>
        <li id="R_3.011.004cd">draṣṭum icchāmahe sarve bhagavantaṃ nivedyatām</li>
    </ul>
    <ul id="R_3.011.005">
        <li id="R_3.011.005ab">tasya tad vacanaṃ śrutvā lakṣmaṇasya tapodhanaḥ</li>
        <li id="R_3.011.005cd">tathety uktvāgniśaraṇaṃ praviveśa niveditum</li>
    </ul>
    <ul id="R_3.011.006">
        <li id="R_3.011.006ab">sa praviśya muniśreṣṭhaṃ tapasā duṣpradharṣaṇam</li>
        <li id="R_3.011.006cd">kṛtāñjalir uvācedaṃ rāmāgamanam añjasā</li>
    </ul>
    <ul id="R_3.011.007">
        <li id="R_3.011.007ab">putrau daśarathasyemau rāmo lakṣmaṇa eva ca</li>
        <li id="R_3.011.007cd">praviṣṭāv āśramapadaṃ sītayā saha bhāryayā</li>
    </ul>
    <ul id="R_3.011.008">
        <li id="R_3.011.008ab">draṣṭuṃ bhavantam āyātau śuśrūṣārtham ariṃdamau</li>
        <li id="R_3.011.008cd">yad atrānantaraṃ tattvam ājñāpayitum arhasi</li>
    </ul>
    <ul id="R_3.011.009">
        <li id="R_3.011.009ab">tataḥ śiṣyād upaśrutya prāptaṃ rāmaṃ salakṣmaṇam</li>
        <li id="R_3.011.009cd">vaidehīṃ ca mahābhāgām idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.011.010">
        <li id="R_3.011.010ab">diṣṭyā rāmaś cirasyādya draṣṭuṃ māṃ samupāgataḥ</li>
        <li id="R_3.011.010cd">manasā kāṅkṣitaṃ hy asya mayāpy āgamanaṃ prati</li>
    </ul>
    <ul id="R_3.011.011">
        <li id="R_3.011.011ab">gamyatāṃ satkṛto rāmaḥ sabhāryaḥ sahalakṣmaṇaḥ</li>
        <li id="R_3.011.011cd">praveśyatāṃ samīpaṃ me kiṃ cāsau na praveśitaḥ</li>
    </ul>
    <ul id="R_3.011.012">
        <li id="R_3.011.012ab">evam uktas tu muninā dharmajñena mahātmanā</li>
        <li id="R_3.011.012cd">abhivādyābravīc chiṣyas tatheti niyatāñjaliḥ</li>
    </ul>
    <ul id="R_3.011.013">
        <li id="R_3.011.013ab">tato niṣkramya saṃbhrāntaḥ śiṣyo lakṣmaṇam abravīt</li>
        <li id="R_3.011.013cd">kvāsau rāmo muniṃ draṣṭum etu praviśatu svayam</li>
    </ul>
    <ul id="R_3.011.014">
        <li id="R_3.011.014ab">tato gatvāśramapadaṃ śiṣyeṇa saha lakṣmaṇaḥ</li>
        <li id="R_3.011.014cd">darśayām āsa kākutsthaṃ sītāṃ ca janakātmajām</li>
    </ul>
    <ul id="R_3.011.015">
        <li id="R_3.011.015ab">taṃ śiṣyaḥ praśritaṃ vākyam agastyavacanaṃ bruvan</li>
        <li id="R_3.011.015cd">prāveśayad yathānyāyaṃ satkārārthaṃ susatkṛtam</li>
    </ul>
    <ul id="R_3.011.016">
        <li id="R_3.011.016ab">praviveśa tato rāmaḥ sītayā sahalakṣmaṇaḥ</li>
        <li id="R_3.011.016cd">praśāntahariṇākīrṇam āśramaṃ hy avalokayan</li>
    </ul>
    <ul id="R_3.011.017">
        <li id="R_3.011.017ab">sa tatra brahmaṇaḥ sthānam agneḥ sthānaṃ tathaiva ca</li>
        <li id="R_3.011.017cd">viṣṇoḥ sthānaṃ mahendrasya sthānaṃ caiva vivasvataḥ</li>
    </ul>
    <ul id="R_3.011.018">
        <li id="R_3.011.018ab">somasthānaṃ bhagasthānaṃ sthānaṃ kauberam eva ca</li>
        <li id="R_3.011.018cd">dhātur vidhātuḥ sthānaṃ ca vāyoḥ sthānaṃ tathaiva ca</li>
    </ul>
    <ul id="R_3.011.019">
        <li id="R_3.011.019ab">tataḥ śiṣyaiḥ parivṛto munir apy abhiniṣpatat</li>
        <li id="R_3.011.019cd">taṃ dadarśāgrato rāmo munīnāṃ dīptatejasaṃ</li>
        <li id="R_3.011.019ef">abravīd vacanaṃ vīro lakṣmaṇaṃ lakṣmivardhanam</li>
    </ul>
    <ul id="R_3.011.020">
        <li id="R_3.011.020ab">eṣa lakṣmaṇa niṣkrāmaty agastyo bhagavān ṛṣiḥ</li>
        <li id="R_3.011.020cd">audāryeṇāvagacchāmi nidhānaṃ tapasām imam</li>
    </ul>
    <ul id="R_3.011.021">
        <li id="R_3.011.021ab">evam uktvā mahābāhur agastyaṃ sūryavarcasaṃ</li>
        <li id="R_3.011.021cd">jagrāha paramaprītas tasya pādau paraṃtapaḥ</li>
    </ul>
    <ul id="R_3.011.022">
        <li id="R_3.011.022ab">abhivādya tu dharmātmā tasthau rāmaḥ kṛtāñjaliḥ</li>
        <li id="R_3.011.022cd">sītayā saha vaidehyā tadā rāma salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_3.011.023">
        <li id="R_3.011.023ab">pratigṛhya ca kākutstham arcayitvāsanodakaiḥ</li>
        <li id="R_3.011.023cd">kuśalapraśnam uktvā ca āsyatām iti so 'bravīt</li>
    </ul>
    <ul id="R_3.011.024">
        <li id="R_3.011.024ab">agniṃ hutvā pradāyārghyam atithiṃ pratipūjya ca</li>
        <li id="R_3.011.024cd">vānaprasthena dharmeṇa sa teṣāṃ bhojanaṃ dadau</li>
    </ul>
    <ul id="R_3.011.025">
        <li id="R_3.011.025ab">prathamaṃ copaviśyātha dharmajño munipuṃgavaḥ</li>
        <li id="R_3.011.025cd">uvāca rāmam āsīnaṃ prāñjaliṃ dharmakovidam</li>
    </ul>
    <ul id="R_3.011.026">
        <li id="R_3.011.026ab">anyathā khalu kākutstha tapasvī samudācaran</li>
        <li id="R_3.011.026cd">duḥsākṣīva pare loke svāni māṃsāni bhakṣayet</li>
    </ul>
    <ul id="R_3.011.027">
        <li id="R_3.011.027ab">rājā sarvasya lokasya dharmacārī mahārathaḥ</li>
        <li id="R_3.011.027cd">pūjanīyaś ca mānyaś ca bhavān prāptaḥ priyātithiḥ</li>
    </ul>
    <ul id="R_3.011.028">
        <li id="R_3.011.028ab">evam uktvā phalair mūlaiḥ puṣpaiś cānyaiś ca rāghavam</li>
        <li id="R_3.011.028cd">pūjayitvā yathākāmaṃ punar eva tato 'bravīt</li>
    </ul>
    <ul id="R_3.011.029">
        <li id="R_3.011.029ab">idaṃ divyaṃ mahac cāpaṃ hemavajravibhūṣitam</li>
        <li id="R_3.011.029cd">vaiṣṇavaṃ puruṣavyāghra nirmitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_3.011.030">
        <li id="R_3.011.030ab">amoghaḥ sūryasaṃkāśo brahmadattaḥ śarottamaḥ</li>
        <li id="R_3.011.030cd">datto mama mahendreṇa tūṇī cākṣayasāyakau</li>
    </ul>
    <ul id="R_3.011.031">
        <li id="R_3.011.031ab">saṃpūrṇau niśitair bāṇair jvaladbhir iva pāvakaiḥ</li>
        <li id="R_3.011.031cd">mahārājata kośo 'yam asir hemavibhūṣitaḥ</li>
    </ul>
    <ul id="R_3.011.032">
        <li id="R_3.011.032ab">anena dhanuṣā rāma hatvā saṃkhye mahāsurān</li>
        <li id="R_3.011.032cd">ājahāra śriyaṃ dīptāṃ purā viṣṇur divaukasām</li>
    </ul>
    <ul id="R_3.011.033">
        <li id="R_3.011.033ab">tad dhanus tau ca tūṇīrau śaraṃ khaḍgaṃ ca mānada</li>
        <li id="R_3.011.033cd">jayāya pratigṛhṇīṣva vajraṃ vajradharo yathā</li>
    </ul>
    <ul id="R_3.011.034">
        <li id="R_3.011.034ab">evam uktvā mahātejāḥ samastaṃ tad varāyudham</li>
        <li id="R_3.011.034cd">dattvā rāmāya bhagavān agastyaḥ punar abravīt</li>
    </ul>
    <ul id="R_3.012.001">
        <li id="R_3.012.001ab">rāma prīto 'smi bhadraṃ te parituṣṭo 'smi lakṣmaṇa</li>
        <li id="R_3.012.001cd">abhivādayituṃ yan māṃ prāptau sthaḥ saha sītayā</li>
    </ul>
    <ul id="R_3.012.002">
        <li id="R_3.012.002ab">adhvaśrameṇa vāṃ khedo bādhate pracuraśramaḥ</li>
        <li id="R_3.012.002cd">vyaktam utkaṇṭhate cāpi maithilī janakātmajā</li>
    </ul>
    <ul id="R_3.012.003">
        <li id="R_3.012.003ab">eṣā hi sukumārī ca duḥkhaiś ca na vimānitā</li>
        <li id="R_3.012.003cd">prājyadoṣaṃ vanaṃ praptā bhartṛsnehapracoditā</li>
    </ul>
    <ul id="R_3.012.004">
        <li id="R_3.012.004ab">yathaiṣā ramate rāma iha sītā tathā kuru</li>
        <li id="R_3.012.004cd">duṣkaraṃ kṛtavaty eṣā vane tvām anugacchatī</li>
    </ul>
    <ul id="R_3.012.005">
        <li id="R_3.012.005ab">eṣā hi prakṛtiḥ strīṇām āsṛṣṭe raghunandana</li>
        <li id="R_3.012.005cd">samastham anurajyante viṣamasthaṃ tyajanti ca</li>
    </ul>
    <ul id="R_3.012.006">
        <li id="R_3.012.006ab">śatahradānāṃ lolatvaṃ śastrāṇāṃ tīkṣṇatāṃ tathā</li>
        <li id="R_3.012.006cd">garuḍānilayoḥ śaighryam anugacchanti yoṣitaḥ</li>
    </ul>
    <ul id="R_3.012.007">
        <li id="R_3.012.007ab">iyaṃ tu bhavato bhāryā doṣair etair vivarjitāḥ</li>
        <li id="R_3.012.007cd">ślāghyā ca vyapadeśyā ca yathā devī hy arundhatī</li>
    </ul>
    <ul id="R_3.012.008">
        <li id="R_3.012.008ab">alaṃkṛto 'yaṃ deśaś ca yatra saumitriṇā saha</li>
        <li id="R_3.012.008cd">vaidehyā cānayā rāma vatsyasi tvam ariṃdama</li>
    </ul>
    <ul id="R_3.012.009">
        <li id="R_3.012.009ab">evam uktas tu muninā rāghavaḥ saṃyatāñjaliḥ</li>
        <li id="R_3.012.009cd">uvāca praśritaṃ vākyam ṛṣiṃ dīptam ivānalam</li>
    </ul>
    <ul id="R_3.012.010">
        <li id="R_3.012.010ab">dhanyo 'smy anugṛhīto 'smi yasya me munipuṃgavaḥ</li>
        <li id="R_3.012.010cd">guṇaiḥ sabhrātṛbhāryasya varadaḥ parituṣyati</li>
    </ul>
    <ul id="R_3.012.011">
        <li id="R_3.012.011ab">kiṃ tu vyādiśa me deśaṃ sodakaṃ bahukānanam</li>
        <li id="R_3.012.011cd">yatrāśramapadaṃ kṛtvā vaseyaṃ nirataḥ sukham</li>
    </ul>
    <ul id="R_3.012.012">
        <li id="R_3.012.012ab">tato 'bravīn muni śreṣṭhaḥ śrutvā rāmasya bhāṣitam</li>
        <li id="R_3.012.012cd">dhyātvā muhūrtaṃ dharmātmā dhīro dhīrataraṃ vacaḥ</li>
    </ul>
    <ul id="R_3.012.013">
        <li id="R_3.012.013ab">ito dviyojane tāta bahumūlaphalodakaḥ</li>
        <li id="R_3.012.013cd">deśo bahumṛgaḥ śrīmān pañcavaṭy abhiviśrutaḥ</li>
    </ul>
    <ul id="R_3.012.014">
        <li id="R_3.012.014ab">tatra gatvāśramapadaṃ kṛtvā saumitriṇā saha</li>
        <li id="R_3.012.014cd">ramasva tvaṃ pitur vākyaṃ yathoktam anupālayan</li>
    </ul>
    <ul id="R_3.012.015">
        <li id="R_3.012.015ab">vidito hy eṣa vṛttānto mama sarvas tavānagha</li>
        <li id="R_3.012.015cd">tapasaś ca prabhāvena snehād daśarathasya ca</li>
    </ul>
    <ul id="R_3.012.016">
        <li id="R_3.012.016ab">hṛdayasthaś ca te chando vijñātas tapasā mayā</li>
        <li id="R_3.012.016cd">iha vāsaṃ pratijñāya mayā saha tapovane</li>
    </ul>
    <ul id="R_3.012.017">
        <li id="R_3.012.017ab">ataś ca tvām ahaṃ brūmi gaccha pañcavaṭīm iti</li>
        <li id="R_3.012.017cd">sa hi ramyo vanoddeśo maithilī tatra raṃsyate</li>
    </ul>
    <ul id="R_3.012.018">
        <li id="R_3.012.018ab">sa deśaḥ ślāghanīyaś ca nātidūre ca rāghava</li>
        <li id="R_3.012.018cd">godāvaryāḥ samīpe ca maithilī tatra raṃsyate</li>
    </ul>
    <ul id="R_3.012.019">
        <li id="R_3.012.019ab">prājyamūlaphalaiś caiva nānādvija gaṇair yutaḥ</li>
        <li id="R_3.012.019cd">viviktaś ca mahābāho puṇyo ramyas tathaiva ca</li>
    </ul>
    <ul id="R_3.012.020">
        <li id="R_3.012.020ab">bhavān api sadāraś ca śaktaś ca parirakṣaṇe</li>
        <li id="R_3.012.020cd">api cātra vasan rāmas tāpasān pālayiṣyasi</li>
    </ul>
    <ul id="R_3.012.021">
        <li id="R_3.012.021ab">etad ālakṣyate vīra madhukānāṃ mahad vanam</li>
        <li id="R_3.012.021cd">uttareṇāsya gantavyaṃ nyagrodham abhigacchatā</li>
    </ul>
    <ul id="R_3.012.022">
        <li id="R_3.012.022ab">tataḥ sthalam upāruhya parvatasyāvidūrataḥ</li>
        <li id="R_3.012.022cd">khyātaḥ pañcavaṭīty eva nityapuṣpitakānanaḥ</li>
    </ul>
    <ul id="R_3.012.023">
        <li id="R_3.012.023ab">agastyenaivam uktas tu rāmaḥ saumitriṇā saha</li>
        <li id="R_3.012.023cd">sātkṛtyāmantrayām āsa tam ṛṣiṃ satyavādinam</li>
    </ul>
    <ul id="R_3.012.024">
        <li id="R_3.012.024ab">tau tu tenābhyanujñātau kṛtapādābhivandanau</li>
        <li id="R_3.012.024cd">tadāśramāt pañcavaṭīṃ jagmatuḥ saha sītayā</li>
    </ul>
    <ul id="R_3.012.025">
        <li id="R_3.012.025ab">
            <seg type="pāda" n="a">gṛhītacāpau tu narādhipātmajau</seg>
            <seg type="pāda" n="b">viṣaktatūṇī samareṣv akātarau</seg>
        </li>
        <li id="R_3.012.025cd">
            <seg type="pāda" n="c">yathopadiṣṭena pathā maharṣiṇā</seg>
            <seg type="pāda" n="d">prajagmatuḥ pañcavaṭīṃ samāhitau</seg>
        </li>
    </ul>
    <ul id="R_3.013.001">
        <li id="R_3.013.001ab">atha pañcavaṭīṃ gacchann antarā raghunandanaḥ</li>
        <li id="R_3.013.001cd">āsasāda mahākāyaṃ gṛdhraṃ bhīmaparākramam</li>
    </ul>
    <ul id="R_3.013.002">
        <li id="R_3.013.002ab">taṃ dṛṣṭvā tau mahābhāgau vanasthaṃ rāmalakṣmaṇau</li>
        <li id="R_3.013.002cd">menāte rākṣasaṃ pakṣiṃ bruvāṇau ko bhavān iti</li>
    </ul>
    <ul id="R_3.013.003">
        <li id="R_3.013.003ab">sa tau madhurayā vācā saumyayā prīṇayann iva</li>
        <li id="R_3.013.003cd">uvāca vatsa māṃ viddhi vayasyaṃ pitur ātmanaḥ</li>
    </ul>
    <ul id="R_3.013.004">
        <li id="R_3.013.004ab">sa taṃ pitṛsakhaṃ buddhvā pūjayām āsa rāghavaḥ</li>
        <li id="R_3.013.004cd">sa tasya kulam avyagram atha papraccha nāma ca</li>
    </ul>
    <ul id="R_3.013.005">
        <li id="R_3.013.005ab">rāmasya vacanaṃ śrutvā kulam ātmānam eva ca</li>
        <li id="R_3.013.005cd">ācacakṣe dvijas tasmai sarvabhūtasamudbhavam</li>
    </ul>
    <ul id="R_3.013.006">
        <li id="R_3.013.006ab">pūrvakāle mahābāho ye prajāpatayo 'bhavan</li>
        <li id="R_3.013.006cd">tān me nigadataḥ sarvān āditaḥ śṛṇu rāghava</li>
    </ul>
    <ul id="R_3.013.007">
        <li id="R_3.013.007ab">kardamaḥ prathamas teṣāṃ vikṛtas tadanantaram</li>
        <li id="R_3.013.007cd">śeṣaś ca saṃśrayaś caiva bahuputraś ca vīryavān</li>
    </ul>
    <ul id="R_3.013.008">
        <li id="R_3.013.008ab">sthāṇur marīcir atriś ca kratuś caiva mahābalaḥ</li>
        <li id="R_3.013.008cd">pulastyaś cāṅgirāś caiva pracetāḥ pulahas tathā</li>
    </ul>
    <ul id="R_3.013.009">
        <li id="R_3.013.009ab">dakṣo vivasvān aparo 'riṣṭanemiś ca rāghava</li>
        <li id="R_3.013.009cd">kaśyapaś ca mahātejās teṣām āsīc ca paścimaḥ</li>
    </ul>
    <ul id="R_3.013.010">
        <li id="R_3.013.010ab">prajāpates tu dakṣasya babhūvur iti naḥ śrutam</li>
        <li id="R_3.013.010cd">ṣaṣṭir duhitaro rāma yaśasvinyo mahāyaśaḥ</li>
    </ul>
    <ul id="R_3.013.011">
        <li id="R_3.013.011ab">kaśyapaḥ pratijagrāha tāsām aṣṭau sumadhyamāḥ</li>
        <li id="R_3.013.011cd">aditiṃ ca ditiṃ caiva danūm api ca kālakām</li>
    </ul>
    <ul id="R_3.013.012">
        <li id="R_3.013.012ab">tāmrāṃ krodhavaśāṃ caiva manuṃ cāpy analām api</li>
        <li id="R_3.013.012cd">tās tu kanyās tataḥ prītaḥ kaśyapaḥ punar abravīt</li>
    </ul>
    <ul id="R_3.013.013">
        <li id="R_3.013.013ab">putrāṃs trailokyabhartṝn vai janayiṣyatha mat samān</li>
        <li id="R_3.013.013cd">aditis tan manā rāma ditiś ca danur eva ca</li>
    </ul>
    <ul id="R_3.013.014">
        <li id="R_3.013.014ab">kālakā ca mahābāho śeṣās tv amanaso 'bhavan</li>
        <li id="R_3.013.014cd">adityāṃ jajñire devās trayastriṃśad ariṃdama</li>
    </ul>
    <ul id="R_3.013.015">
        <li id="R_3.013.015ab">ādityā vasavo rudrā aśvinau ca paraṃtapa</li>
        <li id="R_3.013.015cd">ditis tv ajanayat putrān daityāṃs tāta yaśasvinaḥ</li>
    </ul>
    <ul id="R_3.013.016">
        <li id="R_3.013.016ab">teṣām iyaṃ vasumatī purāsīt savanārṇavā</li>
        <li id="R_3.013.016cd">danus tv ajanayat putram aśvagrīvam ariṃdama</li>
    </ul>
    <ul id="R_3.013.017">
        <li id="R_3.013.017ab">narakaṃ kālakaṃ caiva kālakāpi vyajāyata</li>
        <li id="R_3.013.017cd">krauñcīṃ bhāsīṃ tathā śyenīṃ dhṛtarāṣṭrīṃ tathā śukīm</li>
    </ul>
    <ul id="R_3.013.018">
        <li id="R_3.013.018ab">tāmrāpi suṣuve kanyāḥ pañcaitā lokaviśrutāḥ</li>
        <li id="R_3.013.018cd">ulūkāñ janayat krauñcī bhāsī bhāsān vyajāyata</li>
    </ul>
    <ul id="R_3.013.019">
        <li id="R_3.013.019ab">śyenī śyenāṃś ca gṛdhrāṃś ca vyajāyata sutejasaḥ</li>
        <li id="R_3.013.019cd">dhṛtarāṣṭrī tu haṃsāṃś ca kalahaṃsāṃś ca sarvaśaḥ</li>
    </ul>
    <ul id="R_3.013.020">
        <li id="R_3.013.020ab">cakravākāṃś ca bhadraṃ te vijajñe sāpi bhāminī</li>
        <li id="R_3.013.020cd">śukī natāṃ vijajñe tu natāyā vinatā sutā</li>
    </ul>
    <ul id="R_3.013.021">
        <li id="R_3.013.021ab">daśakrodhavaśā rāma vijajñe 'py ātmasaṃbhavāḥ</li>
        <li id="R_3.013.021cd">mṛgīṃ ca mṛgamandāṃ ca harīṃ bhadramadām api</li>
    </ul>
    <ul id="R_3.013.022">
        <li id="R_3.013.022ab">mātaṅgīm atha śārdūlīṃ śvetāṃ ca surabhīṃ tathā</li>
        <li id="R_3.013.022cd">sarvalakṣaṇasaṃpannāṃ surasāṃ kadrukām api</li>
    </ul>
    <ul id="R_3.013.023">
        <li id="R_3.013.023ab">apatyaṃ tu mṛgāḥ sarve mṛgyā naravarottama</li>
        <li id="R_3.013.023cd">ṛṣkāś ca mṛgamandāyāḥ sṛmarāś camarās tathā</li>
    </ul>
    <ul id="R_3.013.024">
        <li id="R_3.013.024ab">tatas tv irāvatīṃ nāma jajñe bhadramadā sutām</li>
        <li id="R_3.013.024cd">tasyās tv airāvataḥ putro lokanātho mahāgajaḥ</li>
    </ul>
    <ul id="R_3.013.025">
        <li id="R_3.013.025ab">haryāś ca harayo 'patyaṃ vānarāś ca tapasvinaḥ</li>
        <li id="R_3.013.025cd">golāṅgūlāṃś ca śārdūlī vyāghrāṃś cājanayat sutān</li>
    </ul>
    <ul id="R_3.013.026">
        <li id="R_3.013.026ab">mātaṅgyās tv atha mātaṅgā apatyaṃ manujarṣabha</li>
        <li id="R_3.013.026cd">diśāgajaṃ tu śvetākṣaṃ śvetā vyajanayat sutam</li>
    </ul>
    <ul id="R_3.013.027">
        <li id="R_3.013.027ab">tato duhitarau rāma surabhir devy ajāyata</li>
        <li id="R_3.013.027cd">rohiṇīṃ nāma bhadraṃ te gandharvīṃ ca yaśasvinīm</li>
    </ul>
    <ul id="R_3.013.028">
        <li id="R_3.013.028ab">rohiṇy ajanayad gā vai gandharvī vājinaḥ sutān</li>
        <li id="R_3.013.028cd">surasājanayan nāgān rāma kadrūś ca pannagān</li>
    </ul>
    <ul id="R_3.013.029">
        <li id="R_3.013.029ab">manur manuṣyāñ janayat kaśyapasya mahātmanaḥ</li>
        <li id="R_3.013.029cd">brāhmaṇān kṣatriyān vaiśyāñ śūdrāṃś ca manujarṣabha</li>
    </ul>
    <ul id="R_3.013.030">
        <li id="R_3.013.030ab">mukhato brāhmaṇā jātā urasaḥ kṣatriyās tathā</li>
        <li id="R_3.013.030cd">ūrubhyāṃ jajñire vaiśyāḥ padbhyāṃ śūdrā iti śrutiḥ</li>
    </ul>
    <ul id="R_3.013.031">
        <li id="R_3.013.031ab">sarvān puṇyaphalān vṛkṣān analāpi vyajāyata</li>
        <li id="R_3.013.031cd">vinatā ca śukī pautrī kadrūś ca surasā svasā</li>
    </ul>
    <ul id="R_3.013.032">
        <li id="R_3.013.032ab">kadrūr nāgasahaskraṃ tu vijajñe dharaṇīdharam</li>
        <li id="R_3.013.032cd">dvau putrau vinatāyās tu garuḍo 'ruṇa eva ca</li>
    </ul>
    <ul id="R_3.013.033">
        <li id="R_3.013.033ab">tasmāj jāto 'ham aruṇāt saṃpātiś ca mamāgrajaḥ</li>
        <li id="R_3.013.033cd">jaṭāyur iti māṃ viddhi śyenīputram ariṃdama</li>
    </ul>
    <ul id="R_3.013.034">
        <li id="R_3.013.034ab">so 'haṃ vāsasahāyas te bhaviṣyāmi yadīcchasi</li>
        <li id="R_3.013.034cd">sītāṃ ca tāta rakṣiṣye tvayi yāte salakṣmaṇe</li>
    </ul>
    <ul id="R_3.013.035">
        <li id="R_3.013.035ab">
            <seg type="pāda" n="a">jaṭāyuṣaṃ tu pratipūjya rāghavo</seg>
            <seg type="pāda" n="b">mudā pariṣvajya ca saṃnato 'bhavat</seg>
        </li>
        <li id="R_3.013.035cd">
            <seg type="pāda" n="c">pitur hi śuśrāva sakhitvam ātmavāñ</seg>
            <seg type="pāda" n="d">jaṭāyuṣā saṃkathitaṃ punaḥ punaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.013.036">
        <li id="R_3.013.036ab">
            <seg type="pāda" n="a">sa tatra sītāṃ paridāya maithilīṃ</seg>
            <seg type="pāda" n="b">sahaiva tenātibalena pakṣiṇā</seg>
        </li>
        <li id="R_3.013.036cd">
            <seg type="pāda" n="c">jagāma tāṃ pañcavaṭīṃ salakṣmaṇo</seg>
            <seg type="pāda" n="d">ripūn didhakṣañ śalabhān ivānalaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.014.001">
        <li id="R_3.014.001ab">tataḥ pañcavaṭīṃ gatvā nānāvyālamṛgāyutām</li>
        <li id="R_3.014.001cd">uvāca bhrātaraṃ rāmo lakṣmaṇaṃ dīptatejasaṃ</li>
    </ul>
    <ul id="R_3.014.002">
        <li id="R_3.014.002ab">āgatāḥ sma yathoddiṣṭam amuṃ deśaṃ maharṣiṇā</li>
        <li id="R_3.014.002cd">ayaṃ pañcavaṭī deśaḥ saumya puṣpitakānanaḥ</li>
    </ul>
    <ul id="R_3.014.003">
        <li id="R_3.014.003ab">sarvataś cāryatāṃ dṛṣṭiḥ kānane nipuṇo hy asi</li>
        <li id="R_3.014.003cd">āśramaḥ katarasmin no deśe bhavati saṃmataḥ</li>
    </ul>
    <ul id="R_3.014.004">
        <li id="R_3.014.004ab">ramate yatra vaidehī tvam ahaṃ caiva lakṣmaṇa</li>
        <li id="R_3.014.004cd">tādṛśo dṛśyatāṃ deśaḥ saṃnikṛṣṭajalāśayaḥ</li>
    </ul>
    <ul id="R_3.014.005">
        <li id="R_3.014.005ab">vanarāmaṇyakaṃ yatra jalarāmaṇyakaṃ tathā</li>
        <li id="R_3.014.005cd">saṃnikṛṣṭaṃ ca yatra syāt samitpuṣpakuśodakam</li>
    </ul>
    <ul id="R_3.014.006">
        <li id="R_3.014.006ab">evam uktas tu rāmeṇa lakmaṇaḥ saṃyatāñjaliḥ</li>
        <li id="R_3.014.006cd">sītā samakṣaṃ kākutstham idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.014.007">
        <li id="R_3.014.007ab">paravān asmi kākutstha tvayi varṣaśataṃ sthite</li>
        <li id="R_3.014.007cd">svayaṃ tu rucire deśe kriyatām iti māṃ vada</li>
    </ul>
    <ul id="R_3.014.008">
        <li id="R_3.014.008ab">suprītas tena vākyena lakṣmaṇasya mahādyutiḥ</li>
        <li id="R_3.014.008cd">vimṛśan rocayām āsa deśaṃ sarvaguṇānvitam</li>
    </ul>
    <ul id="R_3.014.009">
        <li id="R_3.014.009ab">sa taṃ ruciram ākramya deśam āśramakarmaṇi</li>
        <li id="R_3.014.009cd">haste gṛhītvā hastena rāmaḥ saumitrim abravīt</li>
    </ul>
    <ul id="R_3.014.010">
        <li id="R_3.014.010ab">ayaṃ deśaḥ samaḥ śrīmān puṣpitair tarubhir vṛtaḥ</li>
        <li id="R_3.014.010cd">ihāśramapadaṃ saumya yathāvat kartum arhasi</li>
    </ul>
    <ul id="R_3.014.011">
        <li id="R_3.014.011ab">iyam ādityasaṃkāśaiḥ padmaiḥ surabhigandhibhiḥ</li>
        <li id="R_3.014.011cd">adūre dṛśyate ramyā padminī padmaśobhitā</li>
    </ul>
    <ul id="R_3.014.012">
        <li id="R_3.014.012ab">yathākhyātam agastyena muninā bhāvitātmanā</li>
        <li id="R_3.014.012cd">iyaṃ godāvarī ramyā puṣpitais tarubhir vṛtā</li>
    </ul>
    <ul id="R_3.014.013">
        <li id="R_3.014.013ab">haṃsakāraṇḍavākīrṇā cakravākopaśobhitā</li>
        <li id="R_3.014.013cd">nātidūre na cāsanne mṛgayūthanipīḍitā</li>
    </ul>
    <ul id="R_3.014.014">
        <li id="R_3.014.014ab">mayūranāditā ramyāḥ prāṃśavo bahukandarāḥ</li>
        <li id="R_3.014.014cd">dṛśyante girayaḥ saumya phullais tarubhir āvṛtāḥ</li>
    </ul>
    <ul id="R_3.014.015">
        <li id="R_3.014.015ab">sauvarṇe rājatais tāmrair deśe deśe ca dhātubhiḥ</li>
        <li id="R_3.014.015cd">gavākṣitā ivābhānti gajāḥ paramabhaktibhiḥ</li>
    </ul>
    <ul id="R_3.014.016">
        <li id="R_3.014.016ab">sālais tālais tamālaiś ca kharjūraiḥ panasāmrakaiḥ</li>
        <li id="R_3.014.016cd">nīvārais timiśaiś caiva puṃnāgaiś copaśobhitāḥ</li>
    </ul>
    <ul id="R_3.014.017">
        <li id="R_3.014.017ab">cūtair aśokais tilakaiś campakaiḥ ketakair api</li>
        <li id="R_3.014.017cd">puṣpagulmalatopetais tais tais tarubhir āvṛtāḥ</li>
    </ul>
    <ul id="R_3.014.018">
        <li id="R_3.014.018ab">candanaiḥ syandanair nīpaiḥ panasair lakucair api</li>
        <li id="R_3.014.018cd">dhavāśvakarṇakhadiraiḥ śamīkiṃśukapāṭalaiḥ</li>
    </ul>
    <ul id="R_3.014.019">
        <li id="R_3.014.019ab">idaṃ puṇyam idaṃ medhyam idaṃ bahumṛgadvijam</li>
        <li id="R_3.014.019cd">iha vatsyāma saumitre sārdham etena pakṣiṇā</li>
    </ul>
    <ul id="R_3.014.020">
        <li id="R_3.014.020ab">evam uktas tu rāmeṇa lakṣmaṇaḥ paravīrahā</li>
        <li id="R_3.014.020cd">acireṇāśramaṃ bhrātuś cakāra sumahābalaḥ</li>
    </ul>
    <ul id="R_3.014.021">
        <li id="R_3.014.021ab">parṇaśālāṃ suvipulāṃ tatra saṃghātamṛttikām</li>
        <li id="R_3.014.021cd">sustambhāṃ maskarair dīrghaiḥ kṛtavaṃśāṃ suśobhanām</li>
    </ul>
    <ul id="R_3.014.022">
        <li id="R_3.014.022ab">sa gatvā lakṣmaṇaḥ śrīmān nadīṃ godāvarīṃ tadā</li>
        <li id="R_3.014.022cd">snātvā padmāni cādāya saphalaḥ punar āgataḥ</li>
    </ul>
    <ul id="R_3.014.023">
        <li id="R_3.014.023ab">tataḥ puṣpabaliṃ kṛtvā śāntiṃ ca sa yathāvidhi</li>
        <li id="R_3.014.023cd">darśayām āsa rāmāya tad āśramapadaṃ kṛtam</li>
    </ul>
    <ul id="R_3.014.024">
        <li id="R_3.014.024ab">sa taṃ dṛṣṭvā kṛtaṃ saumyam āśramaṃ saha sītayā</li>
        <li id="R_3.014.024cd">rāghavaḥ parṇaśālāyāṃ harṣam āhārayat param</li>
    </ul>
    <ul id="R_3.014.025">
        <li id="R_3.014.025ab">susaṃhṛṣṭaḥ pariṣvajya bāhubhyāṃ lakṣmaṇaṃ tadā</li>
        <li id="R_3.014.025cd">atisnigdhaṃ ca gāḍhaṃ ca vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_3.014.026">
        <li id="R_3.014.026ab">prīto 'smi te mahat karma tvayā kṛtam idaṃ prabho</li>
        <li id="R_3.014.026cd">pradeyo yannimittaṃ te pariṣvaṅgo mayā kṛtaḥ</li>
    </ul>
    <ul id="R_3.014.027">
        <li id="R_3.014.027ab">bhāvajñena kṛtajñena dharmajñena ca lakṣmaṇa</li>
        <li id="R_3.014.027cd">tvayā putreṇa dharmātmā na saṃvṛttaḥ pitā mama</li>
    </ul>
    <ul id="R_3.014.028">
        <li id="R_3.014.028ab">evaṃ lakṣmaṇam uktvā tu rāghavo lakṣmivardhanaḥ</li>
        <li id="R_3.014.028cd">tasmin deśe bahuphale nyavasat sa sukhaṃ vaśī</li>
    </ul>
    <ul id="R_3.014.029">
        <li id="R_3.014.029ab">kaṃ cit kālaṃ sa dharmātmā sītayā lakṣmaṇena ca</li>
        <li id="R_3.014.029cd">anvāsyamāno nyavasat svargaloke yathāmaraḥ</li>
    </ul>
    <ul id="R_3.015.001">
        <li id="R_3.015.001ab">vasatas tasya tu sukhaṃ rāghavasya mahātmanaḥ</li>
        <li id="R_3.015.001cd">śaradvyapāye hemanta ṛtur iṣṭaḥ pravartate</li>
    </ul>
    <ul id="R_3.015.002">
        <li id="R_3.015.002ab">sa kadā cit prabhātāyāṃ śarvaryāṃ raghunandanaḥ</li>
        <li id="R_3.015.002cd">prayayāv abhiṣekārthaṃ ramyāṃ godāvarīṃ nadīm</li>
    </ul>
    <ul id="R_3.015.003">
        <li id="R_3.015.003ab">prahvaḥ kalaśahastas taṃ sītayā saha vīryavān</li>
        <li id="R_3.015.003cd">pṛṣṭhato 'nuvrajan bhrātā saumitrir idam abravīt</li>
    </ul>
    <ul id="R_3.015.004">
        <li id="R_3.015.004ab">ayaṃ sa kālaḥ saṃprāptaḥ priyo yas te priyaṃvada</li>
        <li id="R_3.015.004cd">alaṃkṛta ivābhāti yena saṃvatsaraḥ śubhaḥ</li>
    </ul>
    <ul id="R_3.015.005">
        <li id="R_3.015.005ab">nīhāraparuṣo lokaḥ pṛthivī sasyamālinī</li>
        <li id="R_3.015.005cd">jalāny anupabhogyāni subhago havyavāhanaḥ</li>
    </ul>
    <ul id="R_3.015.006">
        <li id="R_3.015.006ab">navāgrayaṇapūjābhir abhyarcya pitṛdevatāḥ</li>
        <li id="R_3.015.006cd">kṛtāgrayaṇakāḥ kāle santo vigatakalmaṣāḥ</li>
    </ul>
    <ul id="R_3.015.007">
        <li id="R_3.015.007ab">prājyakāmā janapadāḥ saṃpannataragorasāḥ</li>
        <li id="R_3.015.007cd">vicaranti mahīpālā yātrārthaṃ vijigīṣavaḥ</li>
    </ul>
    <ul id="R_3.015.008">
        <li id="R_3.015.008ab">sevamāne dṛḍhaṃ sūrye diśam antakasevitām</li>
        <li id="R_3.015.008cd">vihīnatilakeva strī nottarā dik prakāśate</li>
    </ul>
    <ul id="R_3.015.009">
        <li id="R_3.015.009ab">prakṛtyā himakośāḍhyo dūrasūryaś ca sāmpratam</li>
        <li id="R_3.015.009cd">yathārthanāmā suvyaktaṃ himavān himavān giriḥ</li>
    </ul>
    <ul id="R_3.015.010">
        <li id="R_3.015.010ab">atyantasukhasaṃcārā madhyāhne sparśataḥ sukhāḥ</li>
        <li id="R_3.015.010cd">divasāḥ subhagādityāś chāyāsaliladurbhagāḥ</li>
    </ul>
    <ul id="R_3.015.011">
        <li id="R_3.015.011ab">mṛdusūryāḥ sanīhārāḥ paṭuśītāḥ samārutāḥ</li>
        <li id="R_3.015.011cd">śūnyāraṇyā himadhvastā divasā bhānti sāmpratam</li>
    </ul>
    <ul id="R_3.015.012">
        <li id="R_3.015.012ab">nivṛttākāśaśayanāḥ puṣyanītā himāruṇāḥ</li>
        <li id="R_3.015.012cd">śītā vṛddhatarāyāmās triyāmā yānti sāmpratam</li>
    </ul>
    <ul id="R_3.015.013">
        <li id="R_3.015.013ab">ravisaṃkrāntasaubhāgyas tuṣārāruṇamaṇḍalaḥ</li>
        <li id="R_3.015.013cd">niḥśvāsāndha ivādarśaś candramā na prakāśate</li>
    </ul>
    <ul id="R_3.015.014">
        <li id="R_3.015.014ab">jyotsnā tuṣāramalinā paurṇamāsyāṃ na rājate</li>
        <li id="R_3.015.014cd">sīteva cātapaśyāmā lakṣyate na tu śobhate</li>
    </ul>
    <ul id="R_3.015.015">
        <li id="R_3.015.015ab">prakṛtyā śītalasparśo himaviddhaś ca sāmpratam</li>
        <li id="R_3.015.015cd">pravāti paścimo vāyuḥ kāle dviguṇaśītalaḥ</li>
    </ul>
    <ul id="R_3.015.016">
        <li id="R_3.015.016ab">bāṣpacchannāny araṇyāni yavagodhūmavanti ca</li>
        <li id="R_3.015.016cd">śobhante 'bhyudite sūrye nadadbhiḥ krauñcasārasaiḥ</li>
    </ul>
    <ul id="R_3.015.017">
        <li id="R_3.015.017ab">kharjūrapuṣpākṛtibhiḥ śirobhiḥ pūrṇataṇḍulaiḥ</li>
        <li id="R_3.015.017cd">śobhante kiṃ cidālambāḥ śālayaḥ kanakaprabhāḥ</li>
    </ul>
    <ul id="R_3.015.018">
        <li id="R_3.015.018ab">mayūkhair upasarpadbhir himanīhārasaṃvṛtaiḥ</li>
        <li id="R_3.015.018cd">dūram abhyuditaḥ sūryaḥ śaśāṅka iva lakṣyate</li>
    </ul>
    <ul id="R_3.015.019">
        <li id="R_3.015.019ab">agrāhyavīryaḥ pūrvāhṇe madhyāhne sparśataḥ sukhaḥ</li>
        <li id="R_3.015.019cd">saṃraktaḥ kiṃ cid āpāṇḍur ātapaḥ śobhate kṣitau</li>
    </ul>
    <ul id="R_3.015.020">
        <li id="R_3.015.020ab">avaśyāyanipātena kiṃ cit praklinnaśādvalā</li>
        <li id="R_3.015.020cd">vanānāṃ śobhate bhūmir niviṣṭataruṇātapā</li>
    </ul>
    <ul id="R_3.015.021">
        <li id="R_3.015.021ab">avaśyāyatamonaddhā nīhāratamasāvṛtāḥ</li>
        <li id="R_3.015.021cd">prasuptā iva lakṣyante vipuṣpā vanarājayaḥ</li>
    </ul>
    <ul id="R_3.015.022">
        <li id="R_3.015.022ab">bāṣpasaṃchannasalilā rutavijñeyasārasāḥ</li>
        <li id="R_3.015.022cd">himārdravālukais tīraiḥ sarito bhānti sāmpratam</li>
    </ul>
    <ul id="R_3.015.023">
        <li id="R_3.015.023ab">tuṣārapatanāc caiva mṛdutvād bhāskarasya ca</li>
        <li id="R_3.015.023cd">śaityād agāgrastham api prāyeṇa rasavaj jalam</li>
    </ul>
    <ul id="R_3.015.024">
        <li id="R_3.015.024ab">jarājarjaritaiḥ parṇaiḥ śīrṇakesarakarṇikaiḥ</li>
        <li id="R_3.015.024cd">nālaśeṣā himadhvastā na bhānti kamalākarāḥ</li>
    </ul>
    <ul id="R_3.015.025">
        <li id="R_3.015.025ab">asmiṃs tu puruṣavyāghra kāle duḥkhasamanvitaḥ</li>
        <li id="R_3.015.025cd">tapaś carati dharmātmā tvadbhaktyā bharataḥ pure</li>
    </ul>
    <ul id="R_3.015.026">
        <li id="R_3.015.026ab">tyaktvā rājyaṃ ca mānaṃ ca bhogāṃś ca vividhān bahūn</li>
        <li id="R_3.015.026cd">tapasvī niyatāhāraḥ śete śīte mahītale</li>
    </ul>
    <ul id="R_3.015.027">
        <li id="R_3.015.027ab">so 'pi velām imāṃ nūnam abhiṣekārtham udyataḥ</li>
        <li id="R_3.015.027cd">vṛtaḥ prakṛtibhir nityaṃ prayāti sarayūṃ nadīm</li>
    </ul>
    <ul id="R_3.015.028">
        <li id="R_3.015.028ab">atyantasukhasaṃvṛddhaḥ sukumāro himārditaḥ</li>
        <li id="R_3.015.028cd">kathaṃ tv apararātreṣu sarayūm avagāhate</li>
    </ul>
    <ul id="R_3.015.029">
        <li id="R_3.015.029ab">padmapatrekṣaṇaḥ śyāmaḥ śrīmān nirudaro mahān</li>
        <li id="R_3.015.029cd">dharmajñaḥ satyavādī ca hrīniṣedho jitendriyaḥ</li>
    </ul>
    <ul id="R_3.015.030">
        <li id="R_3.015.030ab">priyābhibhāṣī madhuro dīrghabāhur ariṃdamaḥ</li>
        <li id="R_3.015.030cd">saṃtyajya vividhān saukhyān āryaṃ sarvātmanāśritaḥ</li>
    </ul>
    <ul id="R_3.015.031">
        <li id="R_3.015.031ab">jitaḥ svargas tava bhrātrā bharatena mahātmanā</li>
        <li id="R_3.015.031cd">vanastham api tāpasye yas tvām anuvidhīyate</li>
    </ul>
    <ul id="R_3.015.032">
        <li id="R_3.015.032ab">na pitryam anuvarntante mātṛkaṃ dvipadā iti</li>
        <li id="R_3.015.032cd">khyāto lokapravādo 'yaṃ bharatenānyathākṛtaḥ</li>
    </ul>
    <ul id="R_3.015.033">
        <li id="R_3.015.033ab">bhartā daśaratho yasyāḥ sādhuś ca bharataḥ sutaḥ</li>
        <li id="R_3.015.033cd">kathaṃ nu sāmbā kaikeyī tādṛśī krūradarśinī</li>
    </ul>
    <ul id="R_3.015.034">
        <li id="R_3.015.034ab">ity evaṃ lakṣmaṇe vākyaṃ snehād bruvati dhārmike</li>
        <li id="R_3.015.034cd">parivādaṃ jananyās tam asahan rāghavo 'bravīt</li>
    </ul>
    <ul id="R_3.015.035">
        <li id="R_3.015.035ab">na te 'mbā madhyamā tāta garhitavyā kathaṃ cana</li>
        <li id="R_3.015.035cd">tām evekṣvākunāthasya bharatasya kathāṃ kuru</li>
    </ul>
    <ul id="R_3.015.036">
        <li id="R_3.015.036ab">niścitāpi hi me buddhir vanavāse dṛḍhavratā</li>
        <li id="R_3.015.036cd">bharatasnehasaṃtaptā bāliśīkriyate punaḥ</li>
    </ul>
    <ul id="R_3.015.037">
        <li id="R_3.015.037ab">ity evaṃ vilapaṃs tatra prāpya godāvarīṃ nadīm</li>
        <li id="R_3.015.037cd">cakre 'bhiṣekaṃ kākutsthaḥ sānujaḥ saha sītayā</li>
    </ul>
    <ul id="R_3.015.038">
        <li id="R_3.015.038ab">tarpayitvātha salilais te pitṝn daivatāni ca</li>
        <li id="R_3.015.038cd">stuvanti smoditaṃ sūryaṃ devatāś ca samāhitāḥ</li>
    </ul>
    <ul id="R_3.015.039">
        <li id="R_3.015.039ab">
            <seg type="pāda" n="a">kṛtābhiṣekaḥ sa rarāja rāmaḥ</seg>
            <seg type="pāda" n="b">sītādvitīyaḥ saha lakṣmaṇena</seg>
        </li>
        <li id="R_3.015.039cd">
            <seg type="pāda" n="c">kṛtābhiṣekas tv agarājaputryā</seg>
            <seg type="pāda" n="d">rudraḥ sanandir bhagavān iveśaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.016.001">
        <li id="R_3.016.001ab">kṛtābhiṣeko rāmas tu sītā saumitrir eva ca</li>
        <li id="R_3.016.001cd">tasmād godāvarītīrāt tato jagmuḥ svam āśramam</li>
    </ul>
    <ul id="R_3.016.002">
        <li id="R_3.016.002ab">āśramaṃ tam upāgamya rāghavaḥ sahalakṣmaṇaḥ</li>
        <li id="R_3.016.002cd">kṛtvā paurvāhṇikaṃ karma parṇaśālām upāgamat</li>
    </ul>
    <ul id="R_3.016.003">
        <li id="R_3.016.003ab">sa rāmaḥ parṇaśālāyām āsīnaḥ saha sītayā</li>
        <li id="R_3.016.003cd">virarāja mahābāhuś citrayā candramā iva</li>
        <li id="R_3.016.003ef">lakṣmaṇena saha bhrātrā cakāra vividhāḥ kathāḥ</li>
    </ul>
    <ul id="R_3.016.004">
        <li id="R_3.016.004ab">tadāsīnasya rāmasya kathāsaṃsaktacetasaḥ</li>
        <li id="R_3.016.004cd">taṃ deśaṃ rākṣasī kā cid ājagāma yadṛcchayā</li>
    </ul>
    <ul id="R_3.016.005">
        <li id="R_3.016.005ab">sā tu śūrpaṇakhā nāma daśagrīvasya rakṣasaḥ</li>
        <li id="R_3.016.005cd">bhaginī rāmam āsādya dadarśa tridaśopamam</li>
    </ul>
    <ul id="R_3.016.006">
        <li id="R_3.016.006ab">siṃhoraskaṃ mahābāhuṃ padmapatranibhekṣaṇam</li>
        <li id="R_3.016.006cd">sukumāraṃ mahāsattvaṃ pārthivavyañjanānvitam</li>
    </ul>
    <ul id="R_3.016.007">
        <li id="R_3.016.007ab">rāmam indīvaraśyāmaṃ kandarpasadṛśaprabham</li>
        <li id="R_3.016.007cd">babhūvendropamaṃ dṛṣṭvā rākṣasī kāmamohitā</li>
    </ul>
    <ul id="R_3.016.008">
        <li id="R_3.016.008ab">sumukhaṃ durmukhī rāmaṃ vṛttamadhyaṃ mahodarī</li>
        <li id="R_3.016.008cd">viśālākṣaṃ virūpākṣī sukeśaṃ tāmramūrdhajā</li>
    </ul>
    <ul id="R_3.016.009">
        <li id="R_3.016.009ab">priyarūpaṃ virūpā sā susvaraṃ bhairavasvanā</li>
        <li id="R_3.016.009cd">taruṇaṃ dāruṇā vṛddhā dakṣiṇaṃ vāmabhāṣiṇī</li>
    </ul>
    <ul id="R_3.016.010">
        <li id="R_3.016.010ab">nyāyavṛttaṃ sudurvṛttā priyam apriyadarśanā</li>
        <li id="R_3.016.010cd">śarīrajasamāviṣṭā rākṣasī rāmam abravīt</li>
    </ul>
    <ul id="R_3.016.011">
        <li id="R_3.016.011ab">jaṭī tāpasarūpeṇa sabhāryaḥ śaracāpadhṛk</li>
        <li id="R_3.016.011cd">āgatas tvam imaṃ deśaṃ kathaṃ rākṣasasevitam</li>
    </ul>
    <ul id="R_3.016.012">
        <li id="R_3.016.012ab">evam uktas tu rākṣasyā śūrpaṇakhyā paraṃtapaḥ</li>
        <li id="R_3.016.012cd">ṛjubuddhitayā sarvam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_3.016.013">
        <li id="R_3.016.013ab">āsīd daśaratho nāma rājā tridaśavikramaḥ</li>
        <li id="R_3.016.013cd">tasyāham agrajaḥ putro rāmo nāma janaiḥ śrutaḥ</li>
    </ul>
    <ul id="R_3.016.014">
        <li id="R_3.016.014ab">bhrātāyaṃ lakṣmaṇo nāma yavīyān mām anuvrataḥ</li>
        <li id="R_3.016.014cd">iyaṃ bhāryā ca vaidehī mama sīteti viśrutā</li>
    </ul>
    <ul id="R_3.016.015">
        <li id="R_3.016.015ab">niyogāt tu narendrasya pitur mātuś ca yantritaḥ</li>
        <li id="R_3.016.015cd">dharmārthaṃ dharmakāṅkṣī ca vanaṃ vastum ihāgataḥ</li>
    </ul>
    <ul id="R_3.016.016">
        <li id="R_3.016.016ab">tvāṃ tu veditum icchāmi kathyatāṃ kāsi kasya vā</li>
        <li id="R_3.016.016cd">iha vā kiṃnimittaṃ tvam āgatā brūhi tattvataḥ</li>
    </ul>
    <ul id="R_3.016.017">
        <li id="R_3.016.017ab">sābravīd vacanaṃ śrutvā rākṣasī madanārditā</li>
        <li id="R_3.016.017cd">śrūyatāṃ rāma vakṣyāmi tattvārthaṃ vacanaṃ mama</li>
    </ul>
    <ul id="R_3.016.018">
        <li id="R_3.016.018ab">ahaṃ śūrpaṇakhā nāma rākṣasī kāmarūpiṇī</li>
        <li id="R_3.016.018cd">araṇyaṃ vicarāmīdam ekā sarvabhayaṃkarā</li>
    </ul>
    <ul id="R_3.016.019">
        <li id="R_3.016.019ab">rāvaṇo nāma me bhrātā rākṣaso rākṣaseśvaraḥ</li>
        <li id="R_3.016.019cd">pravṛddhanidraś ca sadā kumbhakarṇo mahābalaḥ</li>
    </ul>
    <ul id="R_3.016.020">
        <li id="R_3.016.020ab">vibhīṣaṇas tu dharmātmā na tu rākṣasaceṣṭitaḥ</li>
        <li id="R_3.016.020cd">prakhyātavīryau ca raṇe bhrātarau kharadūṣaṇau</li>
    </ul>
    <ul id="R_3.016.021">
        <li id="R_3.016.021ab">tān ahaṃ samatikrāntā rāma tvāpūrvadarśanāt</li>
        <li id="R_3.016.021cd">samupetāsmi bhāvena bhartāraṃ puruṣottamam</li>
        <li id="R_3.016.021ef">cirāya bhava bhartā me sītayā kiṃ kariṣyasi</li>
    </ul>
    <ul id="R_3.016.022">
        <li id="R_3.016.022ab">vikṛtā ca virūpā ca na seyaṃ sadṛśī tava</li>
        <li id="R_3.016.022cd">aham evānurūpā te bhāryārūpeṇa paśya mām</li>
    </ul>
    <ul id="R_3.016.023">
        <li id="R_3.016.023ab">imāṃ virūpām asatīṃ karālāṃ nirṇatodarīm</li>
        <li id="R_3.016.023cd">anena saha te bhrātrā bhakṣayiṣyāmi mānuṣīm</li>
    </ul>
    <ul id="R_3.016.024">
        <li id="R_3.016.024ab">tataḥ parvataśṛṅgāṇi vanāni vividhāni ca</li>
        <li id="R_3.016.024cd">paśyan saha mayā kānta daṇḍakān vicariṣyasi</li>
    </ul>
    <ul id="R_3.016.025">
        <li id="R_3.016.025ab">ity evam uktaḥ kākutsthaḥ prahasya madirekṣaṇām</li>
        <li id="R_3.016.025cd">idaṃ vacanam ārebhe vaktuṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_3.017.001">
        <li id="R_3.017.001ab">tāṃ tu śūrpaṇakhāṃ rāmaḥ kāmapāśāvapāśitām</li>
        <li id="R_3.017.001cd">svecchayā ślakṣṇayā vācā smitapūrvam athābravīt</li>
    </ul>
    <ul id="R_3.017.002">
        <li id="R_3.017.002ab">kṛtadāro 'smi bhavati bhāryeyaṃ dayitā mama</li>
        <li id="R_3.017.002cd">tvadvidhānāṃ tu nārīṇāṃ suduḥkhā sasapatnatā</li>
    </ul>
    <ul id="R_3.017.003">
        <li id="R_3.017.003ab">anujas tv eṣa me bhrātā śīlavān priyadarśanaḥ</li>
        <li id="R_3.017.003cd">śrīmān akṛtadāraś ca lakṣmaṇo nāma vīryavān</li>
    </ul>
    <ul id="R_3.017.004">
        <li id="R_3.017.004ab">apūrvī bhāryayā cārthī taruṇaḥ priyadarśanaḥ</li>
        <li id="R_3.017.004cd">anurūpaś ca te bhartā rūpasyāsya bhaviṣyati</li>
    </ul>
    <ul id="R_3.017.005">
        <li id="R_3.017.005ab">enaṃ bhaja viśālākṣi bhartāraṃ bhrātaraṃ mama</li>
        <li id="R_3.017.005cd">asapatnā varārohe merum arkaprabhā yathā</li>
    </ul>
    <ul id="R_3.017.006">
        <li id="R_3.017.006ab">iti rāmeṇa sā proktā rākṣasī kāmamohitā</li>
        <li id="R_3.017.006cd">visṛjya rāmaṃ sahasā tato lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_3.017.007">
        <li id="R_3.017.007ab">asya rūpasya te yuktā bhāryāhaṃ varavarṇinī</li>
        <li id="R_3.017.007cd">mayā saha sukhaṃ sarvān daṇḍakān vicariṣyasi</li>
    </ul>
    <ul id="R_3.017.008">
        <li id="R_3.017.008ab">evam uktas tu saumitrī rākṣasyā vākyakovidaḥ</li>
        <li id="R_3.017.008cd">tataḥ śūrpaṇakhīṃ smitvā lakṣmaṇo yuktam abravīt</li>
    </ul>
    <ul id="R_3.017.009">
        <li id="R_3.017.009ab">kathaṃ dāsasya me dāsī bhāryā bhavitum icchasi</li>
        <li id="R_3.017.009cd">so 'ham āryeṇa paravān bhrātrā kamalavarṇinī</li>
    </ul>
    <ul id="R_3.017.010">
        <li id="R_3.017.010ab">samṛddhārthasya siddhārthā muditāmalavarṇinī</li>
        <li id="R_3.017.010cd">āryasya tvaṃ viśālākṣi bhāryā bhava yavīyasī</li>
    </ul>
    <ul id="R_3.017.011">
        <li id="R_3.017.011ab">etāṃ virūpām asatīṃ karālāṃ nirṇatodarīm</li>
        <li id="R_3.017.011cd">bhāryāṃ vṛddhāṃ parityajya tvām evaiṣa bhajiṣyati</li>
    </ul>
    <ul id="R_3.017.012">
        <li id="R_3.017.012ab">ko hi rūpam idaṃ śreṣṭhaṃ saṃtyajya varavarṇini</li>
        <li id="R_3.017.012cd">mānuṣeṣu varārohe kuryād bhāvaṃ vicakṣaṇaḥ</li>
    </ul>
    <ul id="R_3.017.013">
        <li id="R_3.017.013ab">iti sā lakṣmaṇenoktā karālā nirṇatodarī</li>
        <li id="R_3.017.013cd">manyate tad vacaḥ satyaṃ parihāsāvicakṣaṇā</li>
    </ul>
    <ul id="R_3.017.014">
        <li id="R_3.017.014ab">sā rāmaṃ parṇaśālāyām upaviṣṭaṃ paraṃtapam</li>
        <li id="R_3.017.014cd">sītayā saha durdharṣam abravīt kāmamohitā</li>
    </ul>
    <ul id="R_3.017.015">
        <li id="R_3.017.015ab">imāṃ virūpām asatīṃ karālāṃ nirṇatodarīm</li>
        <li id="R_3.017.015cd">vṛddhāṃ bhāryām avaṣṭabhya na māṃ tvaṃ bahu manyase</li>
    </ul>
    <ul id="R_3.017.016">
        <li id="R_3.017.016ab">adyemāṃ bhakṣayiṣyāmi paśyatas tava mānuṣīm</li>
        <li id="R_3.017.016cd">tvayā saha cariṣyāmi niḥsapatnā yathāsukham</li>
    </ul>
    <ul id="R_3.017.017">
        <li id="R_3.017.017ab">ity uktvā mṛgaśāvākṣīm alātasadṛśekṣaṇā</li>
        <li id="R_3.017.017cd">abhyadhāvat susaṃkruddhā maholkā rohiṇīm iva</li>
    </ul>
    <ul id="R_3.017.018">
        <li id="R_3.017.018ab">tāṃ mṛtyupāśapratimām āpatantīṃ mahābalaḥ</li>
        <li id="R_3.017.018cd">nigṛhya rāmaḥ kupitas tato lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_3.017.019">
        <li id="R_3.017.019ab">krūrair anāryaiḥ saumitre parihāsaḥ kathaṃ cana</li>
        <li id="R_3.017.019cd">na kāryaḥ paśya vaidehīṃ kathaṃ cit saumya jīvatīm</li>
    </ul>
    <ul id="R_3.017.020">
        <li id="R_3.017.020ab">imāṃ virūpām asatīm atimattāṃ mahodarīm</li>
        <li id="R_3.017.020cd">rākṣasīṃ puruṣavyāghra virūpayitum arhasi</li>
    </ul>
    <ul id="R_3.017.021">
        <li id="R_3.017.021ab">ity ukto lakṣmaṇas tasyāḥ kruddho rāmasya paśyataḥ</li>
        <li id="R_3.017.021cd">uddhṛtya khaḍgaṃ ciccheda karṇanāsaṃ mahābalaḥ</li>
    </ul>
    <ul id="R_3.017.022">
        <li id="R_3.017.022ab">nikṛttakarṇanāsā tu visvaraṃ sā vinadya ca</li>
        <li id="R_3.017.022cd">yathāgataṃ pradudrāva ghorā śūrpaṇakhā vanam</li>
    </ul>
    <ul id="R_3.017.023">
        <li id="R_3.017.023ab">sā virūpā mahāghorā rākṣasī śoṇitokṣitā</li>
        <li id="R_3.017.023cd">nanāda vividhān nādān yathā prāvṛṣi toyadaḥ</li>
    </ul>
    <ul id="R_3.017.024">
        <li id="R_3.017.024ab">sā vikṣarantī rudhiraṃ bahudhā ghoradarśanā</li>
        <li id="R_3.017.024cd">pragṛhya bāhū garjantī praviveśa mahāvanam</li>
    </ul>
    <ul id="R_3.017.025">
        <li id="R_3.017.025ab">
            <seg type="pāda" n="a">tatas tu sā rākṣasasaṃghasaṃvṛtaṃ</seg>
            <seg type="pāda" n="b">kharaṃ janasthānagataṃ virūpitā</seg>
        </li>
        <li id="R_3.017.025cd">
            <seg type="pāda" n="c">upetya taṃ bhrātaram ugratejasaṃ</seg>
            <seg type="pāda" n="d">papāta bhūmau gaganād yathāśaniḥ</seg>
        </li>
    </ul>
    <ul id="R_3.017.026">
        <li id="R_3.017.026ab">
            <seg type="pāda" n="a">tataḥ sabhāryaṃ bhayamohamūrchitā</seg>
            <seg type="pāda" n="b">salakṣmaṇaṃ rāghavam āgataṃ vanam</seg>
        </li>
        <li id="R_3.017.026cd">
            <seg type="pāda" n="c">virūpaṇaṃ cātmani śoṇitokṣitā</seg>
            <seg type="pāda" n="d">śaśaṃsa sarvaṃ bhaginī kharasya sā</seg>
        </li>
    </ul>
    <ul id="R_3.018.001">
        <li id="R_3.018.001ab">tāṃ tathā patitāṃ dṛṣṭvā virūpāṃ śoṇitokṣitām</li>
        <li id="R_3.018.001cd">bhaginīṃ krodhasaṃtaptaḥ kharaḥ papraccha rākṣasaḥ</li>
    </ul>
    <ul id="R_3.018.002">
        <li id="R_3.018.002ab">balavikramasaṃpannā kāmagā kāmarūpiṇī</li>
        <li id="R_3.018.002cd">imām avasthāṃ nītā tvaṃ kenāntakasamā gatā</li>
    </ul>
    <ul id="R_3.018.003">
        <li id="R_3.018.003ab">devagandharvabhūtānām ṛṣīṇāṃ ca mahātmanām</li>
        <li id="R_3.018.003cd">ko 'yam evaṃ mahāvīryas tvāṃ virūpāṃ cakāra ha</li>
    </ul>
    <ul id="R_3.018.004">
        <li id="R_3.018.004ab">na hi paśyāmy ahaṃ loke yaḥ kuryān mama vipriyam</li>
        <li id="R_3.018.004cd">antarena sahasrākṣaṃ mahendraṃ pākaśāsanam</li>
    </ul>
    <ul id="R_3.018.005">
        <li id="R_3.018.005ab">adyāhaṃ mārgaṇaiḥ prāṇān ādāsye jīvitāntakaiḥ</li>
        <li id="R_3.018.005cd">salile kṣīram āsaktaṃ niṣpibann iva sārasaḥ</li>
    </ul>
    <ul id="R_3.018.006">
        <li id="R_3.018.006ab">nihatasya mayā saṃkhye śarasaṃkṛttamarmaṇaḥ</li>
        <li id="R_3.018.006cd">saphenaṃ rudhiraṃ raktaṃ medinī kasya pāsyati</li>
    </ul>
    <ul id="R_3.018.007">
        <li id="R_3.018.007ab">kasya patrarathāḥ kāyān māṃsam utkṛtya saṃgatāḥ</li>
        <li id="R_3.018.007cd">prahṛṣṭā bhakṣayiṣyanti nihatasya mayā raṇe</li>
    </ul>
    <ul id="R_3.018.008">
        <li id="R_3.018.008ab">taṃ na devā na gandharvā na piśācā na rākṣasāḥ</li>
        <li id="R_3.018.008cd">mayāpakṛṣṭaṃ kṛpaṇaṃ śaktās trātuṃ mahāhave</li>
    </ul>
    <ul id="R_3.018.009">
        <li id="R_3.018.009ab">upalabhya śanaiḥ saṃjñāṃ taṃ me śaṃsitum arhasi</li>
        <li id="R_3.018.009cd">yena tvaṃ durvinītena vane vikramya nirjitā</li>
    </ul>
    <ul id="R_3.018.010">
        <li id="R_3.018.010ab">iti bhrātur vacaḥ śrutvā kruddhasya ca viśeṣataḥ</li>
        <li id="R_3.018.010cd">tataḥ śūrpaṇakhā vākyaṃ sabāṣpam idam abravīt</li>
    </ul>
    <ul id="R_3.018.011">
        <li id="R_3.018.011ab">taruṇau rūpasaṃpannau sukūmārau mahābalau</li>
        <li id="R_3.018.011cd">puṇḍarīkaviśālākṣau cīrakṛṣṇājināmbarau</li>
    </ul>
    <ul id="R_3.018.012">
        <li id="R_3.018.012ab">gandharvarājapratimau pārthivavyañjanānvitau</li>
        <li id="R_3.018.012cd">devau vā mānuṣau vā tau na tarkayitum utsahe</li>
    </ul>
    <ul id="R_3.018.013">
        <li id="R_3.018.013ab">taruṇī rūpasaṃpannā sarvābharaṇabhūṣitā</li>
        <li id="R_3.018.013cd">dṛṣṭā tatra mayā nārī tayor madhye sumadhyamā</li>
    </ul>
    <ul id="R_3.018.014">
        <li id="R_3.018.014ab">tābhyām ubhābhyāṃ saṃbhūya pramadām adhikṛtya tām</li>
        <li id="R_3.018.014cd">imām avasthāṃ nītāhaṃ yathānāthāsatī tathā</li>
    </ul>
    <ul id="R_3.018.015">
        <li id="R_3.018.015ab">tasyāś cānṛjuvṛttāyās tayoś ca hatayor aham</li>
        <li id="R_3.018.015cd">saphenaṃ pātum icchāmi rudhiraṃ raṇamūrdhani</li>
    </ul>
    <ul id="R_3.018.016">
        <li id="R_3.018.016ab">eṣa me prathamaḥ kāmaḥ kṛtas tāta tvayā bhavet</li>
        <li id="R_3.018.016cd">tasyās tayoś ca rudhiraṃ pibeyam aham āhave</li>
    </ul>
    <ul id="R_3.018.017">
        <li id="R_3.018.017ab">iti tasyāṃ bruvāṇāyāṃ caturdaśa mahābalān</li>
        <li id="R_3.018.017cd">vyādideśa kharaḥ kruddho rākṣasān antakopamān</li>
    </ul>
    <ul id="R_3.018.018">
        <li id="R_3.018.018ab">mānuṣau śastrasaṃpannau cīrakṛṣṇājināmbarau</li>
        <li id="R_3.018.018cd">praviṣṭau daṇḍakāraṇyaṃ ghoraṃ pramadayā saha</li>
    </ul>
    <ul id="R_3.018.019">
        <li id="R_3.018.019ab">tau hatvā tāṃ ca durvṛttām upāvartitum arhatha</li>
        <li id="R_3.018.019cd">iyaṃ ca rudhiraṃ teṣāṃ bhaginī mama pāsyati</li>
    </ul>
    <ul id="R_3.018.020">
        <li id="R_3.018.020ab">manoratho 'yam iṣṭo 'syā bhaginyā mama rākṣasāḥ</li>
        <li id="R_3.018.020cd">śīghraṃ saṃpadyatāṃ gatvā tau pramathya svatejasā</li>
    </ul>
    <ul id="R_3.018.021">
        <li id="R_3.018.021ab">iti pratisamādiṣṭā rākṣasās te caturdaśa</li>
        <li id="R_3.018.021cd">tatra jagmus tayā sārdhaṃ ghanā vāteritā yathā</li>
    </ul>
    <ul id="R_3.019.001">
        <li id="R_3.019.001ab">tataḥ śūrpaṇakhā ghorā rāghavāśramam āgatā</li>
        <li id="R_3.019.001cd">rakṣasām ācacakṣe tau bhrātarau saha sītayā</li>
    </ul>
    <ul id="R_3.019.002">
        <li id="R_3.019.002ab">te rāmaṃ parṇaśālāyām upaviṣṭaṃ mahābalam</li>
        <li id="R_3.019.002cd">dadṛśuḥ sītayā sārdhaṃ vaidehyā lakṣmaṇena ca</li>
    </ul>
    <ul id="R_3.019.003">
        <li id="R_3.019.003ab">tān dṛṣṭvā rāghavaḥ śrīmān āgatāṃ tāṃ ca rākṣasīm</li>
        <li id="R_3.019.003cd">abravīd bhrātaraṃ rāmo lakṣmaṇaṃ dīptatejasaṃ</li>
    </ul>
    <ul id="R_3.019.004">
        <li id="R_3.019.004ab">muhūrtaṃ bhava saumitre sītāyāḥ pratyanantaraḥ</li>
        <li id="R_3.019.004cd">imān asyā vadhiṣyāmi padavīm āgatān iha</li>
    </ul>
    <ul id="R_3.019.005">
        <li id="R_3.019.005ab">vākyam etat tataḥ śrutvā rāmasya viditātmanaḥ</li>
        <li id="R_3.019.005cd">tatheti lakṣmaṇo vākyaṃ rāmasya pratyapūjayat</li>
    </ul>
    <ul id="R_3.019.006">
        <li id="R_3.019.006ab">rāghavo 'pi mahac cāpaṃ cāmīkaravibhūṣitam</li>
        <li id="R_3.019.006cd">cakāra sajyaṃ dharmātmā tāni rakṣāṃsi cābravīt</li>
    </ul>
    <ul id="R_3.019.007">
        <li id="R_3.019.007ab">putrau daśarathasyāvāṃ bhrātarau rāmalakṣmaṇau</li>
        <li id="R_3.019.007cd">praviṣṭau sītayā sārdhaṃ duścaraṃ daṇḍakāvanam</li>
    </ul>
    <ul id="R_3.019.008">
        <li id="R_3.019.008ab">phalamūlāśanau dāntau tāpasau dharmacāriṇau</li>
        <li id="R_3.019.008cd">vasantau daṇḍakāraṇye kimartham upahiṃsatha</li>
    </ul>
    <ul id="R_3.019.009">
        <li id="R_3.019.009ab">yuṣmān pāpātmakān hantuṃ viprakārān mahāvane</li>
        <li id="R_3.019.009cd">ṛṣīṇāṃ tu niyogena prāpto 'haṃ saśarāsanaḥ</li>
    </ul>
    <ul id="R_3.019.010">
        <li id="R_3.019.010ab">tiṣṭhataivātra saṃtuṣṭā nopasarpitum arhatha</li>
        <li id="R_3.019.010cd">yadi prāṇair ihārtho vo nivartadhvaṃ niśācarāḥ</li>
    </ul>
    <ul id="R_3.019.011">
        <li id="R_3.019.011ab">tasya tad vacanaṃ śrutvā rākṣasās te caturdaśa</li>
        <li id="R_3.019.011cd">ūcur vācaṃ susaṃkruddhā brahmaghnaḥ śūlapāṇayaḥ</li>
    </ul>
    <ul id="R_3.019.012">
        <li id="R_3.019.012ab">saṃraktanayanā ghorā rāmaṃ raktāntalocanam</li>
        <li id="R_3.019.012cd">paruṣā madhurābhāṣaṃ hṛṣṭādṛṣṭaparākramam</li>
    </ul>
    <ul id="R_3.019.013">
        <li id="R_3.019.013ab">krodham utpādya no bhartuḥ kharasya sumahātmanaḥ</li>
        <li id="R_3.019.013cd">tvam eva hāsyase prāṇān adyāsmābhir hato yudhi</li>
    </ul>
    <ul id="R_3.019.014">
        <li id="R_3.019.014ab">kā hi te śaktir ekasya bahūnāṃ raṇamūrdhani</li>
        <li id="R_3.019.014cd">asmākam agrataḥ sthātuṃ kiṃ punar yoddhum āhave</li>
    </ul>
    <ul id="R_3.019.015">
        <li id="R_3.019.015ab">ebhir bāhuprayuktair naḥ parighaiḥ śūlapaṭṭiśaiḥ</li>
        <li id="R_3.019.015cd">prāṇāṃs tyakṣyasi vīryaṃ ca dhanuś ca karapīḍitam</li>
    </ul>
    <ul id="R_3.019.016">
        <li id="R_3.019.016ab">ity evam uktvā saṃrabdhā rākṣasās te caturdaśa</li>
        <li id="R_3.019.016cd">udyatāyudhanistriṃśā rāmam evābhidudruvuḥ</li>
        <li id="R_3.019.016ef">cikṣipus tāni śūlāni rāghavaṃ prati durjayam</li>
    </ul>
    <ul id="R_3.019.017">
        <li id="R_3.019.017ab">tāni śūlāni kākutsthaḥ samastāni caturdaśa</li>
        <li id="R_3.019.017cd">tāvadbhir eva ciccheda śaraiḥ kāñcanabhūṣaṇaiḥ</li>
    </ul>
    <ul id="R_3.019.018">
        <li id="R_3.019.018ab">tataḥ paścān mahātejā nārācān sūryasaṃnibhān</li>
        <li id="R_3.019.018cd">jagrāha paramakruddhaś caturdaśa śilāśitān</li>
    </ul>
    <ul id="R_3.019.019">
        <li id="R_3.019.019ab">gṛhītvā dhanur āyamya lakṣyān uddiśya rākṣasān</li>
        <li id="R_3.019.019cd">mumoca rāghavo bāṇān vajrān iva śatakratuḥ</li>
    </ul>
    <ul id="R_3.019.020">
        <li id="R_3.019.020ab">rukmapuṅkhāś ca viśikhāḥ pradīptā hemabhūṣaṇāḥ</li>
        <li id="R_3.019.020cd">antarikṣe maholkānāṃ babhūvus tulyavarcasaḥ</li>
    </ul>
    <ul id="R_3.019.021">
        <li id="R_3.019.021ab">te bhittvā rakṣasāṃ vegād vakṣāṃsi rudhirāplutāḥ</li>
        <li id="R_3.019.021cd">viniṣpetus tadā bhūmau nyamajjantāśanisvanāḥ</li>
    </ul>
    <ul id="R_3.019.022">
        <li id="R_3.019.022ab">te bhinnahṛdayā bhūmau chinnamūlā iva drumāḥ</li>
        <li id="R_3.019.022cd">nipetuḥ śoṇitārdrāṅgā vikṛtā vigatāsavaḥ</li>
    </ul>
    <ul id="R_3.019.023">
        <li id="R_3.019.023ab">tān bhūmau patitān dṛṣṭvā rākṣasī krodhamūrchitā</li>
        <li id="R_3.019.023cd">paritrastā punas tatra vyasṛjad bhairavaṃ ravam</li>
    </ul>
    <ul id="R_3.019.024">
        <li id="R_3.019.024ab">sā nadantī mahānādaṃ javāc chūrpaṇakhā punaḥ</li>
        <li id="R_3.019.024cd">upagamya kharaṃ sā tu kiṃ cit saṃśuṣka śoṇitā</li>
        <li id="R_3.019.024ef">papāta punar evārtā saniryāseva vallarī</li>
    </ul>
    <ul id="R_3.019.025">
        <li id="R_3.019.025ab">
            <seg type="pāda" n="a">nipātitān prekṣya raṇe tu rākṣasān</seg>
            <seg type="pāda" n="b">pradhāvitā śūrpaṇakhā punas tataḥ</seg>
        </li>
        <li id="R_3.019.025cd">
            <seg type="pāda" n="c">vadhaṃ ca teṣāṃ nikhilena rakṣasāṃ</seg>
            <seg type="pāda" n="d">śaśaṃsa sarvaṃ bhaginī kharasya sā</seg>
        </li>
    </ul>
    <ul id="R_3.020.001">
        <li id="R_3.020.001ab">sa punaḥ patitāṃ dṛṣṭvā krodhāc chūrpaṇakhāṃ kharaḥ</li>
        <li id="R_3.020.001cd">uvāca vyaktatā vācā tām anarthārtham āgatām</li>
    </ul>
    <ul id="R_3.020.002">
        <li id="R_3.020.002ab">mayā tv idānīṃ śūrās te rākṣasā rudhirāśanāḥ</li>
        <li id="R_3.020.002cd">tvatpriyārthaṃ vinirdiṣṭāḥ kimarthaṃ rudyate punaḥ</li>
    </ul>
    <ul id="R_3.020.003">
        <li id="R_3.020.003ab">bhaktāś caivānuraktāś ca hitāś ca mama nityaśaḥ</li>
        <li id="R_3.020.003cd">ghnanto 'pi na nihantavyā na na kuryur vaco mama</li>
    </ul>
    <ul id="R_3.020.004">
        <li id="R_3.020.004ab">kim etac chrotum icchāmi kāraṇaṃ yatkṛte punaḥ</li>
        <li id="R_3.020.004cd">hā nātheti vinardantī sarpavad veṣṭase kṣitau</li>
    </ul>
    <ul id="R_3.020.005">
        <li id="R_3.020.005ab">anāthavad vilapasi kiṃ nu nāthe mayi sthite</li>
        <li id="R_3.020.005cd">uttiṣṭhottiṣṭha mā bhaiṣīr vaiklavyaṃ tyajyatām iha</li>
    </ul>
    <ul id="R_3.020.006">
        <li id="R_3.020.006ab">ity evam uktā durdharṣā khareṇa parisāntvitā</li>
        <li id="R_3.020.006cd">vimṛjya nayane sāsre kharaṃ bhrātaram abravīt</li>
    </ul>
    <ul id="R_3.020.007">
        <li id="R_3.020.007ab">preṣitāś ca tvayā śūrā rākṣasās te caturdaśa</li>
        <li id="R_3.020.007cd">nihantuṃ rāghavaṃ ghorā matpriyārthaṃ salakṣmaṇam</li>
    </ul>
    <ul id="R_3.020.008">
        <li id="R_3.020.008ab">te tu rāmeṇa sāmarṣāḥ śūlapaṭṭiśapāṇayaḥ</li>
        <li id="R_3.020.008cd">samare nihatāḥ sarve sāyakair marmabhedibhiḥ</li>
    </ul>
    <ul id="R_3.020.009">
        <li id="R_3.020.009ab">tān bhūmau patitān dṛṣṭvā kṣaṇenaiva mahābalān</li>
        <li id="R_3.020.009cd">rāmasya ca mahat karma mahāṃs trāso 'bhavan mama</li>
    </ul>
    <ul id="R_3.020.010">
        <li id="R_3.020.010ab">sāsmi bhītā samudvignā viṣaṇṇā ca niśācara</li>
        <li id="R_3.020.010cd">śaraṇaṃ tvāṃ punaḥ prāptā sarvato bhayadarśinī</li>
    </ul>
    <ul id="R_3.020.011">
        <li id="R_3.020.011ab">viṣādanakrādhyuṣite paritrāsormimālini</li>
        <li id="R_3.020.011cd">kiṃ māṃ na trāyase magnāṃ vipule śokasāgare</li>
    </ul>
    <ul id="R_3.020.012">
        <li id="R_3.020.012ab">ete ca nihatā bhūmau rāmeṇa niśitaiḥ śaraiḥ</li>
        <li id="R_3.020.012cd">ye ca me padavīṃ prāptā rākṣasāḥ piśitāśanāḥ</li>
    </ul>
    <ul id="R_3.020.013">
        <li id="R_3.020.013ab">mayi te yady anukrośo yadi rakṣaḥsu teṣu ca</li>
        <li id="R_3.020.013cd">rāmeṇa yadi śaktis te tejo vāsti niśācara</li>
        <li id="R_3.020.013ef">daṇḍakāraṇyanilayaṃ jahi rākṣasakaṇṭakam</li>
    </ul>
    <ul id="R_3.020.014">
        <li id="R_3.020.014ab">yadi rāmaṃ mamāmitram adya tvaṃ na vadhiṣyasi</li>
        <li id="R_3.020.014cd">tava caivāgrataḥ prāṇāṃs tyakṣyāmi nirapatrapā</li>
    </ul>
    <ul id="R_3.020.015">
        <li id="R_3.020.015ab">buddhyāham anupaśyāmi na tvaṃ rāmasya saṃyuge</li>
        <li id="R_3.020.015cd">sthātuṃ pratimukhe śaktaḥ sacāpasya mahāraṇe</li>
    </ul>
    <ul id="R_3.020.016">
        <li id="R_3.020.016ab">śūramānī na śūras tvaṃ mithyāropitavikramaḥ</li>
        <li id="R_3.020.016cd">mānuṣau yan na śaknoṣi hantuṃ tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_3.020.017">
        <li id="R_3.020.017ab">apayāhi janasthānāt tvaritaḥ sahabāndhavaḥ</li>
        <li id="R_3.020.017cd">niḥsattvasyālpavīryasya vāsas te kīdṛśas tv iha</li>
    </ul>
    <ul id="R_3.020.018">
        <li id="R_3.020.018ab">rāmatejo'bhibhūto hi tvaṃ kṣipraṃ vinaśiṣyasi</li>
        <li id="R_3.020.018cd">sa hi tejaḥsamāyukto rāmo daśarathātmajaḥ</li>
        <li id="R_3.020.018ef">bhrātā cāsya mahāvīryo yena cāsmi virūpitā</li>
    </ul>
    <ul id="R_3.021.001">
        <li id="R_3.021.001ab">evam ādharṣitaḥ śūraḥ śūrpaṇakhyā kharas tadā</li>
        <li id="R_3.021.001cd">uvāca rakṣasāṃ madhye kharaḥ kharataraṃ vacaḥ</li>
    </ul>
    <ul id="R_3.021.002">
        <li id="R_3.021.002ab">tavāpamānaprabhavaḥ krodho 'yam atulo mama</li>
        <li id="R_3.021.002cd">na śakyate dhārayituṃ lavaṇāmbha ivotthitam</li>
    </ul>
    <ul id="R_3.021.003">
        <li id="R_3.021.003ab">na rāmaṃ gaṇaye vīryān mānuṣaṃ kṣīṇajīvitam</li>
        <li id="R_3.021.003cd">ātmā duścaritaiḥ prāṇān hato yo 'dya vimokṣyati</li>
    </ul>
    <ul id="R_3.021.004">
        <li id="R_3.021.004ab">bāṣpaḥ saṃhriyatām eṣa saṃbhramaś ca vimucyatām</li>
        <li id="R_3.021.004cd">ahaṃ rāmaḥ saha bhrātrā nayāmi yamasādanam</li>
    </ul>
    <ul id="R_3.021.005">
        <li id="R_3.021.005ab">paraśvadhahatasyādya mandaprāṇasya bhūtale</li>
        <li id="R_3.021.005cd">rāmasya rudhiraṃ raktam uṣṇaṃ pāsyasi rākṣasi</li>
    </ul>
    <ul id="R_3.021.006">
        <li id="R_3.021.006ab">sā prahṛṣṭvā vacaḥ śrutvā kharasya vadanāc cyutam</li>
        <li id="R_3.021.006cd">praśaśaṃsa punar maurkhyād bhrātaraṃ rakṣasāṃ varam</li>
    </ul>
    <ul id="R_3.021.007">
        <li id="R_3.021.007ab">tayā paruṣitaḥ pūrvaṃ punar eva praśaṃsitaḥ</li>
        <li id="R_3.021.007cd">abravīd dūṣaṇaṃ nāma kharaḥ senāpatiṃ tadā</li>
    </ul>
    <ul id="R_3.021.008">
        <li id="R_3.021.008ab">caturdaśa sahasrāṇi mama cittānuvartinām</li>
        <li id="R_3.021.008cd">rakṣasīṃ bhīmavegānāṃ samareṣv anivartinām</li>
    </ul>
    <ul id="R_3.021.009">
        <li id="R_3.021.009ab">nīlajīmūtavarṇānāṃ ghorāṇāṃ krūrakarmaṇām</li>
        <li id="R_3.021.009cd">lokasiṃhāvihārāṇāṃ balinām ugratejasām</li>
    </ul>
    <ul id="R_3.021.010">
        <li id="R_3.021.010ab">teṣāṃ śārdūladarpāṇāṃ mahāsyānāṃ mahaujasām</li>
        <li id="R_3.021.010cd">sarvodyogam udīrṇānāṃ rakṣasāṃ saumya kāraya</li>
    </ul>
    <ul id="R_3.021.011">
        <li id="R_3.021.011ab">upasthāpaya me kṣipraṃ rathaṃ saumya dhanūṃṣi ca</li>
        <li id="R_3.021.011cd">śarāṃś ca citrān khaḍgāṃś ca śaktīś ca vividhāḥ śitāḥ</li>
    </ul>
    <ul id="R_3.021.012">
        <li id="R_3.021.012ab">agre niryātum icchāmi paulastyānāṃ mahātmanām</li>
        <li id="R_3.021.012cd">vadhārthaṃ durvinītasya rāmasya raṇakovidaḥ</li>
    </ul>
    <ul id="R_3.021.013">
        <li id="R_3.021.013ab">iti tasya bruvāṇasya sūryavarṇaṃ mahāratham</li>
        <li id="R_3.021.013cd">sadaśvaiḥ śabalair yuktam ācacakṣe 'tha dūṣaṇaḥ</li>
    </ul>
    <ul id="R_3.021.014">
        <li id="R_3.021.014ab">taṃ meruśikharākāraṃ taptakāñcanabhūṣaṇam</li>
        <li id="R_3.021.014cd">hemacakram asaṃbādhaṃ vaidūryamaya kūbaram</li>
    </ul>
    <ul id="R_3.021.015">
        <li id="R_3.021.015ab">matsyaiḥ puṣpair drumaiḥ śailaiś candrasūryaiś ca kāñcanaiḥ</li>
        <li id="R_3.021.015cd">māṅgalyaiḥ pakṣisaṃghaiś ca tārābhiś ca samāvṛtam</li>
    </ul>
    <ul id="R_3.021.016">
        <li id="R_3.021.016ab">dhvajanistriṃśasaṃpannaṃ kiṅkiṇīkavibhūṣitam</li>
        <li id="R_3.021.016cd">sadaśvayuktaṃ so 'marṣād āruroha rathaṃ kharaḥ</li>
    </ul>
    <ul id="R_3.021.017">
        <li id="R_3.021.017ab">niśāmya taṃ rathagataṃ rākṣasā bhīmavikramāḥ</li>
        <li id="R_3.021.017cd">tasthuḥ saṃparivāryainaṃ dūṣaṇaṃ ca mahābalam</li>
    </ul>
    <ul id="R_3.021.018">
        <li id="R_3.021.018ab">kharas tu tān maheṣvāsān ghoracarmāyudhadhvajān</li>
        <li id="R_3.021.018cd">niryātety abravīd dṛṣṭvā rathasthaḥ sarvarākṣasān</li>
    </ul>
    <ul id="R_3.021.019">
        <li id="R_3.021.019ab">tatas tad rākṣasaṃ sainyaṃ ghoracarmāyudhadhvajam</li>
        <li id="R_3.021.019cd">nirjagāma janasthānān mahānādaṃ mahājavam</li>
    </ul>
    <ul id="R_3.021.020">
        <li id="R_3.021.020ab">mudgaraiḥ paṭṭiśaiḥ śūlaiḥ sutīkṣṇaiś ca paraśvadhaiḥ</li>
        <li id="R_3.021.020cd">khaḍgaiś cakraiś ca hastasthair bhrājamānaiś ca tomaraiḥ</li>
    </ul>
    <ul id="R_3.021.021">
        <li id="R_3.021.021ab">śaktibhiḥ patighair ghorair atimātraiś ca kārmukaiḥ</li>
        <li id="R_3.021.021cd">gadāsimusalair vajrair gṛhītair bhīmadarśanaiḥ</li>
    </ul>
    <ul id="R_3.021.022">
        <li id="R_3.021.022ab">rākṣasānāṃ sughorāṇāṃ sahasrāṇi caturdaśa</li>
        <li id="R_3.021.022cd">niryātāni janasthānāt kharacittānuvartinām</li>
    </ul>
    <ul id="R_3.021.023">
        <li id="R_3.021.023ab">tāṃs tv abhidravato dṛṣṭvā rākṣasān bhīmavikramān</li>
        <li id="R_3.021.023cd">kharasyāpi rathaḥ kiṃ cij jagāma tadanantaram</li>
    </ul>
    <ul id="R_3.021.024">
        <li id="R_3.021.024ab">tatas tāñ śabalān aśvāṃs taptakāñcanabhūṣitān</li>
        <li id="R_3.021.024cd">kharasya matam ājñāya sārathiḥ samacodayat</li>
    </ul>
    <ul id="R_3.021.025">
        <li id="R_3.021.025ab">sa codito rathaḥ śīghraṃ kharasya ripughātinaḥ</li>
        <li id="R_3.021.025cd">śabdenāpūrayām āsa diśaś ca pratiśas tathā</li>
    </ul>
    <ul id="R_3.021.026">
        <li id="R_3.021.026ab">
            <seg type="pāda" n="a">pravṛddhamanyus tu kharaḥ kharasvano</seg>
            <seg type="pāda" n="b">ripor vadhārthaṃ tvarito yathāntakaḥ</seg>
        </li>
        <li id="R_3.021.026cd">
            <seg type="pāda" n="c">acūcudat sārathim unnadan punar</seg>
            <seg type="pāda" n="d">mahābalo megha ivāśmavarṣavān</seg>
        </li>
    </ul>
    <ul id="R_3.022.001">
        <li id="R_3.022.001ab">tat prayātaṃ balaṃ ghoram aśivaṃ śoṇitodakam</li>
        <li id="R_3.022.001cd">abhyavarṣan mahāmeghas tumulo gardabhāruṇaḥ</li>
    </ul>
    <ul id="R_3.022.002">
        <li id="R_3.022.002ab">nipetus turagās tasya rathayuktā mahājavāḥ</li>
        <li id="R_3.022.002cd">same puṣpacite deśe rājamārge yadṛcchayā</li>
    </ul>
    <ul id="R_3.022.003">
        <li id="R_3.022.003ab">śyāmaṃ rudhiraparyantaṃ babhūva pariveṣaṇam</li>
        <li id="R_3.022.003cd">alātacakrapratimaṃ pratigṛhya divākaram</li>
    </ul>
    <ul id="R_3.022.004">
        <li id="R_3.022.004ab">tato dhvajam upāgamya hemadaṇḍaṃ samucchritam</li>
        <li id="R_3.022.004cd">samākramya mahākāyas tasthau gṛdhraḥ sudāruṇaḥ</li>
    </ul>
    <ul id="R_3.022.005">
        <li id="R_3.022.005ab">janasthānasamīpe ca samākramya kharasvanāḥ</li>
        <li id="R_3.022.005cd">visvarān vividhāṃś cakrur māṃsādā mṛgapakṣiṇaḥ</li>
    </ul>
    <ul id="R_3.022.006">
        <li id="R_3.022.006ab">vyājahruś ca padīptāyāṃ diśi vai bhairavasvanam</li>
        <li id="R_3.022.006cd">aśivā yātu dāhānāṃ śivā ghorā mahāsvanāḥ</li>
    </ul>
    <ul id="R_3.022.007">
        <li id="R_3.022.007ab">prabhinnagirisaṃkāśās toyaśoṣitadhāriṇaḥ</li>
        <li id="R_3.022.007cd">ākāśaṃ tad anākāśaṃ cakrur bhīmā balāhakāḥ</li>
    </ul>
    <ul id="R_3.022.008">
        <li id="R_3.022.008ab">babhūva timiraṃ ghoram uddhataṃ romaharṣaṇam</li>
        <li id="R_3.022.008cd">diśo vā vidiśo vāpi suvyaktaṃ na cakāśire</li>
    </ul>
    <ul id="R_3.022.009">
        <li id="R_3.022.009ab">kṣatajārdrasavarṇābhā saṃdhyākālaṃ vinā babhau</li>
        <li id="R_3.022.009cd">kharasyābhimukhaṃ nedus tadā ghorā mṛgāḥ khagāḥ</li>
    </ul>
    <ul id="R_3.022.010">
        <li id="R_3.022.010ab">nityāśivakarā yuddhe śivā ghoranidarśanāḥ</li>
        <li id="R_3.022.010cd">nedur balasyābhimukhaṃ jvālodgāribhir ānanaiḥ</li>
    </ul>
    <ul id="R_3.022.011">
        <li id="R_3.022.011ab">kabandhaḥ parighābhāso dṛśyate bhāskarāntike</li>
        <li id="R_3.022.011cd">jagrāha sūryaṃ svarbhānur aparvaṇi mahāgrahaḥ</li>
    </ul>
    <ul id="R_3.022.012">
        <li id="R_3.022.012ab">pravāti mārutaḥ śīghraṃ niṣprabho 'bhūd divākaraḥ</li>
        <li id="R_3.022.012cd">utpetuś ca vinā rātriṃ tārāḥ khadyotasaprabhāḥ</li>
    </ul>
    <ul id="R_3.022.013">
        <li id="R_3.022.013ab">saṃlīnamīnavihagā nalinyaḥ puṣpapaṅkajāḥ</li>
        <li id="R_3.022.013cd">tasmin kṣaṇe babhūvuś ca vinā puṣpaphalair drumāḥ</li>
    </ul>
    <ul id="R_3.022.014">
        <li id="R_3.022.014ab">uddhūtaś ca vinā vātaṃ reṇur jaladharāruṇaḥ</li>
        <li id="R_3.022.014cd">vīcīkūcīti vāśyanto babhūvus tatra sārikāḥ</li>
    </ul>
    <ul id="R_3.022.015">
        <li id="R_3.022.015ab">ulkāś cāpi sanirghoṣā nipetur ghoradarśanāḥ</li>
        <li id="R_3.022.015cd">pracacāla mahī cāpi saśailavanakānanā</li>
    </ul>
    <ul id="R_3.022.016">
        <li id="R_3.022.016ab">kharasya ca rathasthasya nardamānasya dhīmataḥ</li>
        <li id="R_3.022.016cd">prākampata bhujaḥ savyaḥ kharaś cāsyāvasajjata</li>
    </ul>
    <ul id="R_3.022.017">
        <li id="R_3.022.017ab">sāsrā saṃpadyate dṛṣṭiḥ paśyamānasya sarvataḥ</li>
        <li id="R_3.022.017cd">lalāṭe ca rujā jātā na ca mohān nyavartata</li>
    </ul>
    <ul id="R_3.022.018">
        <li id="R_3.022.018ab">tān samīkṣya mahotpātān utthitān romaharṣaṇān</li>
        <li id="R_3.022.018cd">abravīd rākṣasān sarvān prahasan sa kharas tadā</li>
    </ul>
    <ul id="R_3.022.019">
        <li id="R_3.022.019ab">mahotpātān imān sarvān utthitān ghoradarśanān</li>
        <li id="R_3.022.019cd">na cintayāmy ahaṃ vīryād balavān durbalān iva</li>
    </ul>
    <ul id="R_3.022.020">
        <li id="R_3.022.020ab">tārā api śarais tīkṣṇaiḥ pātayeyaṃ nabhastalāt</li>
        <li id="R_3.022.020cd">mṛtyuṃ maraṇadharmeṇa saṃkruddho yojayāmy aham</li>
    </ul>
    <ul id="R_3.022.021">
        <li id="R_3.022.021ab">rāghavaṃ taṃ balotsiktaṃ bhrātaraṃ cāpi lakṣmaṇam</li>
        <li id="R_3.022.021cd">ahatvā sāyakais tīkṣṇair nopāvartitum utsahe</li>
    </ul>
    <ul id="R_3.022.022">
        <li id="R_3.022.022ab">sakāmā bhaginī me 'stu pītvā tu rudhiraṃ tayoḥ</li>
        <li id="R_3.022.022cd">yannimittaṃ tu rāmasya lakṣmaṇasya viparyayaḥ</li>
    </ul>
    <ul id="R_3.022.023">
        <li id="R_3.022.023ab">na kva cit prāptapūrvo me saṃyugeṣu parājayaḥ</li>
        <li id="R_3.022.023cd">yuṣmākam etat pratyakṣaṃ nānṛtaṃ kathayāmy aham</li>
    </ul>
    <ul id="R_3.022.024">
        <li id="R_3.022.024ab">devarājam api kruddho mattairāvatayāyinam</li>
        <li id="R_3.022.024cd">vajrahastaṃ raṇe hanyāṃ kiṃ punas tau ca mānuṣau</li>
    </ul>
    <ul id="R_3.022.025">
        <li id="R_3.022.025ab">sā tasya garjitaṃ śrutvā rākṣasasya mahācamūḥ</li>
        <li id="R_3.022.025cd">praharṣam atulaṃ lebhe mṛtyupāśāvapāśitā</li>
    </ul>
    <ul id="R_3.022.026">
        <li id="R_3.022.026ab">sameyuś ca mahātmāno yuddhadarśanakāṅkṣiṇaḥ</li>
        <li id="R_3.022.026cd">ṛṣayo devagandharvāḥ siddhāś ca saha cāraṇaiḥ</li>
    </ul>
    <ul id="R_3.022.027">
        <li id="R_3.022.027ab">sametya coruḥ sahitās te 'nyāyaṃ puṇyakarmaṇaḥ</li>
        <li id="R_3.022.027cd">svasti gobrāhmaṇebhyo 'stu lokānāṃ ye ca saṃmatāḥ</li>
    </ul>
    <ul id="R_3.022.028">
        <li id="R_3.022.028ab">jayatāṃ rāghavo yuddhe paulastyān rajanīcarān</li>
        <li id="R_3.022.028cd">cakrā hasto yathā yuddhe sarvān asurapuṃgavān</li>
    </ul>
    <ul id="R_3.022.029">
        <li id="R_3.022.029ab">etac cānyac ca bahuśo bruvāṇāḥ paramarṣayaḥ</li>
        <li id="R_3.022.029cd">dadṛśur vāhinīṃ teṣāṃ rākṣasānāṃ gatāyuṣām</li>
    </ul>
    <ul id="R_3.022.030">
        <li id="R_3.022.030ab">rathena tu kharo vegāt sainyasyāgrād viniḥsṛtaḥ</li>
        <li id="R_3.022.030cd">taṃ dṛṣṭvā rākṣasaṃ bhūyo rākṣasāś ca viniḥsṛtāḥ</li>
    </ul>
    <ul id="R_3.022.031">
        <li id="R_3.022.031ab">śyena gāmī pṛthugrīvo yajñaśatrur vihaṃgamaḥ</li>
        <li id="R_3.022.031cd">durjayaḥ karavīrākṣaḥ paruṣaḥ kālakārmukaḥ</li>
    </ul>
    <ul id="R_3.022.032">
        <li id="R_3.022.032ab">meghamālī mahāmālī sarpāsyo rudhirāśanaḥ</li>
        <li id="R_3.022.032cd">dvādaśaite mahāvīryāḥ pratasthur abhitaḥ kharam</li>
    </ul>
    <ul id="R_3.022.033">
        <li id="R_3.022.033ab">mahākapālaḥ sthūlākṣaḥ pramāthī triśirās tathā</li>
        <li id="R_3.022.033cd">catvāra ete senāgryā dūṣaṇaṃ pṛṣṭhato 'nvayuḥ</li>
    </ul>
    <ul id="R_3.022.034">
        <li id="R_3.022.034ab">
            <seg type="pāda" n="a">sā bhīmavegā samarābhikāmā</seg>
            <seg type="pāda" n="b">sudāruṇā rākṣasavīra senā</seg>
        </li>
        <li id="R_3.022.034cd">
            <seg type="pāda" n="c">tau rājaputrau sahasābhyupetā</seg>
            <seg type="pāda" n="d">mālāgrahāṇām iva candrasūryau</seg>
        </li>
    </ul>
    <ul id="R_3.023.001">
        <li id="R_3.023.001ab">āśramaṃ prati yāte tu khare kharaparākrame</li>
        <li id="R_3.023.001cd">tān evautpātikān rāmaḥ saha bhrātrā dadarśa ha</li>
    </ul>
    <ul id="R_3.023.002">
        <li id="R_3.023.002ab">tān utpātān mahāghorān utthitān romaharṣaṇān</li>
        <li id="R_3.023.002cd">prajānām ahitān dṛṣṭvā vākyaṃ lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_3.023.003">
        <li id="R_3.023.003ab">imān paśya mahābāho sarvabhūtāpahāriṇaḥ</li>
        <li id="R_3.023.003cd">samutthitān mahotpātān saṃhartuṃ sarvarākṣasān</li>
    </ul>
    <ul id="R_3.023.004">
        <li id="R_3.023.004ab">amī rudhiradhārās tu visṛjantaḥ kharasvanān</li>
        <li id="R_3.023.004cd">vyomni meghā vivartante paruṣā gardabhāruṇāḥ</li>
    </ul>
    <ul id="R_3.023.005">
        <li id="R_3.023.005ab">sadhūmāś ca śarāḥ sarve mama yuddhābhinandinaḥ</li>
        <li id="R_3.023.005cd">rukmapṛṣṭhāni cāpāni viveṣṭante ca lakṣmaṇa</li>
    </ul>
    <ul id="R_3.023.006">
        <li id="R_3.023.006ab">yādṛśā iha kūjanti pakṣiṇo vanacāriṇaḥ</li>
        <li id="R_3.023.006cd">agrato no bhayaṃ prāptaṃ saṃśayo jīvitasya ca</li>
    </ul>
    <ul id="R_3.023.007">
        <li id="R_3.023.007ab">saṃprahāras tu sumahān bhaviṣyati na saṃśayaḥ</li>
        <li id="R_3.023.007cd">ayam ākhyāti me bāhuḥ sphuramāṇo muhur muhuḥ</li>
    </ul>
    <ul id="R_3.023.008">
        <li id="R_3.023.008ab">saṃnikarṣe tu naḥ śūra jayaṃ śatroḥ parājayam</li>
        <li id="R_3.023.008cd">suprabhaṃ ca prasannaṃ ca tava vaktraṃ hi lakṣyate</li>
    </ul>
    <ul id="R_3.023.009">
        <li id="R_3.023.009ab">udyatānāṃ hi yuddhārthaṃ yeṣāṃ bhavati lakṣmaṇaḥ</li>
        <li id="R_3.023.009cd">niṣprabhaṃ vadanaṃ teṣāṃ bhavaty āyuḥ parikṣayaḥ</li>
    </ul>
    <ul id="R_3.023.010">
        <li id="R_3.023.010ab">anāgatavidhānaṃ tu kartavyaṃ śubham icchatā</li>
        <li id="R_3.023.010cd">āpadaṃ śaṅkamānena puruṣeṇa vipaścitā</li>
    </ul>
    <ul id="R_3.023.011">
        <li id="R_3.023.011ab">tasmād gṛhītvā vaidehīṃ śarapāṇir dhanurdharaḥ</li>
        <li id="R_3.023.011cd">guhām āśrayaśailasya durgāṃ pādapasaṃkulām</li>
    </ul>
    <ul id="R_3.023.012">
        <li id="R_3.023.012ab">pratikūlitum icchāmi na hi vākyam idaṃ tvayā</li>
        <li id="R_3.023.012cd">śāpito mama pādābhyāṃ gamyatāṃ vatsa māciram</li>
    </ul>
    <ul id="R_3.023.013">
        <li id="R_3.023.013ab">evam uktas tu rāmeṇa lakṣmaṇaḥ saha sītayā</li>
        <li id="R_3.023.013cd">śarān ādāya cāpaṃ ca guhāṃ durgāṃ samāśrayat</li>
    </ul>
    <ul id="R_3.023.014">
        <li id="R_3.023.014ab">tasmin praviṣṭe tu guhāṃ lakṣmaṇe saha sītayā</li>
        <li id="R_3.023.014cd">hanta niryuktam ity uktvā rāmaḥ kavacam āviśat</li>
    </ul>
    <ul id="R_3.023.015">
        <li id="R_3.023.015ab">sā tenāgninikāśena kavacena vibhūṣitaḥ</li>
        <li id="R_3.023.015cd">babhūva rāmas timire vidhūmo 'gnir ivotthitaḥ</li>
    </ul>
    <ul id="R_3.023.016">
        <li id="R_3.023.016ab">sa cāpam udyamya mahac charān ādāya vīryavān</li>
        <li id="R_3.023.016cd">babhūvāvasthitas tatra jyāsvanaiḥ pūrayan diśaḥ</li>
    </ul>
    <ul id="R_3.023.017">
        <li id="R_3.023.017ab">tato devāḥ sagandharvāḥ siddhāś ca saha cāraṇaiḥ</li>
        <li id="R_3.023.017cd">ūcuḥ paramasaṃtrastā guhyakāś ca parasparam</li>
    </ul>
    <ul id="R_3.023.018">
        <li id="R_3.023.018ab">caturdaśa sahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_3.023.018cd">ekaś ca rāmo dharmātmā kathaṃ yuddhaṃ bhaviṣyati</li>
    </ul>
    <ul id="R_3.023.019">
        <li id="R_3.023.019ab">tato gambhīranirhrādaṃ ghoravarmāyudhadhvajam</li>
        <li id="R_3.023.019cd">anīkaṃ yātudhānānāṃ samantāt pratyadṛśyata</li>
    </ul>
    <ul id="R_3.023.020">
        <li id="R_3.023.020ab">siṃhanādaṃ visṛjatām anyonyam abhigarjatām</li>
        <li id="R_3.023.020cd">cāpāni vispharayatāṃ jṛmbhatāṃ cāpy abhīkṣṇaśaḥ</li>
    </ul>
    <ul id="R_3.023.021">
        <li id="R_3.023.021ab">vipraghuṣṭasvanānāṃ ca dundubhīṃś cāpi nighnatām</li>
        <li id="R_3.023.021cd">teṣāṃ sutumulaḥ śabdaḥ pūrayām āsa tad vanam</li>
    </ul>
    <ul id="R_3.023.022">
        <li id="R_3.023.022ab">tena śabdena vitrastāḥ śvāpadā vanacāriṇaḥ</li>
        <li id="R_3.023.022cd">dudruvur yatra niḥśabdaṃ pṛṣṭhato nāvalokayan</li>
    </ul>
    <ul id="R_3.023.023">
        <li id="R_3.023.023ab">tat tv anīkaṃ mahāvegaṃ rāmaṃ samupasarpata</li>
        <li id="R_3.023.023cd">ghṛtanānāpraharaṇaṃ gambhīraṃ sāgaropamam</li>
    </ul>
    <ul id="R_3.023.024">
        <li id="R_3.023.024ab">rāmo 'pi cārayaṃś cakṣuḥ sarvato raṇapaṇḍitaḥ</li>
        <li id="R_3.023.024cd">dadarśa kharasainyaṃ tad yuddhābhimukham udyatam</li>
    </ul>
    <ul id="R_3.023.025">
        <li id="R_3.023.025ab">vitatya ca dhanur bhīmaṃ tūṇyāś coddhṛtya sāyakān</li>
        <li id="R_3.023.025cd">krodham āhārayat tīvraṃ vadhārthaṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_3.023.026">
        <li id="R_3.023.026ab">duṣprekṣyaḥ so 'bhavat kruddho yugāntāgnir iva jvalan</li>
        <li id="R_3.023.026cd">taṃ dṛṣṭvā tejasāviṣṭaṃ prāvyathan vanadevatāḥ</li>
    </ul>
    <ul id="R_3.023.027">
        <li id="R_3.023.027ab">tasya kruddhasya rūpaṃ tu rāmasya dadṛśe tadā</li>
        <li id="R_3.023.027cd">dakṣasyeva kratuṃ hantum udyatasya pinākinaḥ</li>
    </ul>
    <ul id="R_3.024.001">
        <li id="R_3.024.001ab">avaṣṭabdhadhanuṃ rāmaṃ kruddhaṃ ca ripughātinam</li>
        <li id="R_3.024.001cd">dadarśāśramam āgamya kharaḥ saha puraḥsaraiḥ</li>
    </ul>
    <ul id="R_3.024.002">
        <li id="R_3.024.002ab">taṃ dṛṣṭvā saguṇaṃ cāpam udyamya kharaniḥsvanam</li>
        <li id="R_3.024.002cd">rāmasyābhimukhaṃ sūtaṃ codyatām ity acodayat</li>
    </ul>
    <ul id="R_3.024.003">
        <li id="R_3.024.003ab">sa kharasyājñayā sūtas turagān samacodayat</li>
        <li id="R_3.024.003cd">yatra rāmo mahābāhur eko dhunvan dhanuḥ sthitaḥ</li>
    </ul>
    <ul id="R_3.024.004">
        <li id="R_3.024.004ab">taṃ tu niṣpatitaṃ dṛṣṭvā sarve te rajanīcarāḥ</li>
        <li id="R_3.024.004cd">nardamānā mahānādaṃ sacivāḥ paryavārayan</li>
    </ul>
    <ul id="R_3.024.005">
        <li id="R_3.024.005ab">sa teṣāṃ yātudhānānāṃ madhye rato gataḥ kharaḥ</li>
        <li id="R_3.024.005cd">babhūva madhye tārāṇāṃ lohitāṅga ivoditaḥ</li>
    </ul>
    <ul id="R_3.024.006">
        <li id="R_3.024.006ab">tatas taṃ bhīmadhanvānaṃ kruddhāḥ sarve niśācarāḥ</li>
        <li id="R_3.024.006cd">rāmaṃ nānāvidhaiḥ śastrair abhyavarṣanta durjayam</li>
    </ul>
    <ul id="R_3.024.007">
        <li id="R_3.024.007ab">mudgarair āyasaiḥ śūlaiḥ prāsaiḥ khaḍgaiḥ paraśvadhaiḥ</li>
        <li id="R_3.024.007cd">rākṣasāḥ samare rāmaṃ nijaghnū roṣatatparāḥ</li>
    </ul>
    <ul id="R_3.024.008">
        <li id="R_3.024.008ab">te balāhakasaṃkāśā mahānādā mahābalāḥ</li>
        <li id="R_3.024.008cd">abhyadhāvanta kākutsthaṃ rāmaṃ yuddhe jighāṃsavaḥ</li>
    </ul>
    <ul id="R_3.024.009">
        <li id="R_3.024.009ab">te rāme śaravarṣāṇi vyasṛjan rakṣasāṃ guṇāḥ</li>
        <li id="R_3.024.009cd">śailendram iva dhārābhir varṣamāṇā mahādhanāḥ</li>
    </ul>
    <ul id="R_3.024.010">
        <li id="R_3.024.010ab">sa taiḥ parivṛto ghorai rāghavo rakṣasāṃ gaṇaiḥ</li>
        <li id="R_3.024.010cd">tithiṣv iva mahādevo vṛtaḥ pāriṣadāṃ gaṇaiḥ</li>
    </ul>
    <ul id="R_3.024.011">
        <li id="R_3.024.011ab">tāni muktāni śastrāṇi yātudhānaiḥ sa rāghavaḥ</li>
        <li id="R_3.024.011cd">pratijagrāha viśikhair nadyoghān iva sāgaraḥ</li>
    </ul>
    <ul id="R_3.024.012">
        <li id="R_3.024.012ab">sa taiḥ praharaṇair ghorair bhinnagātro na vivyathe</li>
        <li id="R_3.024.012cd">rāmaḥ pradīptair bahubhir vajrair iva mahācalaḥ</li>
    </ul>
    <ul id="R_3.024.013">
        <li id="R_3.024.013ab">sa viddhaḥ kṣatajādigdhaḥ sarvagātreṣu rāghavaḥ</li>
        <li id="R_3.024.013cd">babhūva rāmaḥ saṃdhyābhrair divākara ivāvṛtaḥ</li>
    </ul>
    <ul id="R_3.024.014">
        <li id="R_3.024.014ab">viṣedur devagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_3.024.014cd">ekaṃ sahastrair bahubhis tadā dṛṣṭvā samāvṛtam</li>
    </ul>
    <ul id="R_3.024.015">
        <li id="R_3.024.015ab">tato rāmaḥ susaṃkruddho maṇḍalīkṛtakārmukaḥ</li>
        <li id="R_3.024.015cd">sasarja niśitān bāṇāñ śataśo 'tha sahasraśaḥ</li>
    </ul>
    <ul id="R_3.024.016">
        <li id="R_3.024.016ab">durāvārān durviṣahān kālapāśopamān raṇe</li>
        <li id="R_3.024.016cd">mumoca līlayā rāmaḥ kaṅkapatrān ajihmagān</li>
    </ul>
    <ul id="R_3.024.017">
        <li id="R_3.024.017ab">te śarāḥ śatrusainyeṣu muktā rāmeṇa līlayā</li>
        <li id="R_3.024.017cd">ādadū rakṣasāṃ prāṇān pāśāḥ kālakṛtā iva</li>
    </ul>
    <ul id="R_3.024.018">
        <li id="R_3.024.018ab">bhittvā rākṣasadehāṃs tāṃs te śarā rudhirāplutāḥ</li>
        <li id="R_3.024.018cd">antarikṣagatā rejur dīptāgnisamatejasaḥ</li>
    </ul>
    <ul id="R_3.024.019">
        <li id="R_3.024.019ab">asaṃkhyeyās tu rāmasya sāyakāś cāpamaṇḍalāt</li>
        <li id="R_3.024.019cd">viniṣpetur atīvogrā rakṣaḥ prāṇāpahāriṇaḥ</li>
    </ul>
    <ul id="R_3.024.020">
        <li id="R_3.024.020ab">tair dhanūṃṣi dhvajāgrāṇi varmāṇi ca śirāṃsi ca</li>
        <li id="R_3.024.020cd">bahūn sahastābharaṇān ūrūn karikaropamān</li>
    </ul>
    <ul id="R_3.024.021">
        <li id="R_3.024.021ab">tato nālīkanārācais tīkṣṇāgraiś ca vikarṇibhiḥ</li>
        <li id="R_3.024.021cd">bhīmam ārtasvaraṃ cakrur bhidyamānā niśācarāḥ</li>
    </ul>
    <ul id="R_3.024.022">
        <li id="R_3.024.022ab">tat sainyaṃ niśitair bāṇair arditaṃ marmabhedibhiḥ</li>
        <li id="R_3.024.022cd">rāmeṇa na sukhaṃ lebhe śuṣkaṃ vanam ivāgninā</li>
    </ul>
    <ul id="R_3.024.023">
        <li id="R_3.024.023ab">ke cid bhīmabalāḥ śūrāḥ śūlān khaḍgān paraśvadhān</li>
        <li id="R_3.024.023cd">cikṣipuḥ paramakruddhā rāmāya rajanīcarāḥ</li>
    </ul>
    <ul id="R_3.024.024">
        <li id="R_3.024.024ab">tāni bāṇair mahābāhuḥ śastrāṇy āvārya rāghavaḥ</li>
        <li id="R_3.024.024cd">jahāra samare prāṇāṃś ciccheda ca śirodharān</li>
    </ul>
    <ul id="R_3.024.025">
        <li id="R_3.024.025ab">avaśiṣṭāś ca ye tatra viṣaṇṇāś ca niśācarāḥ</li>
        <li id="R_3.024.025cd">kharam evābhyadhāvanta śaraṇārthaṃ śarārditāḥ</li>
    </ul>
    <ul id="R_3.024.026">
        <li id="R_3.024.026ab">tān sarvān punar ādāya samāśvāsya ca dūṣaṇaḥ</li>
        <li id="R_3.024.026cd">abhyadhāvata kākutsthaṃ kruddho rudram ivāntakaḥ</li>
    </ul>
    <ul id="R_3.024.027">
        <li id="R_3.024.027ab">nivṛttās tu punaḥ sarve dūṣaṇāśrayanirbhayāḥ</li>
        <li id="R_3.024.027cd">rāmam evābhyadhāvanta sālatālaśilāyudhāḥ</li>
    </ul>
    <ul id="R_3.024.028">
        <li id="R_3.024.028ab">tad babhūvādbhutaṃ yuddhaṃ tumulaṃ romaharṣaṇam</li>
        <li id="R_3.024.028cd">rāmasyāsya mahāghoraṃ punas teṣāṃ ca rakṣasām</li>
    </ul>
    <ul id="R_3.025.001">
        <li id="R_3.025.001ab">tad drumāṇāṃ śilānāṃ ca varṣaṃ prāṇaharaṃ mahat</li>
        <li id="R_3.025.001cd">pratijagrāha dharmātmā rāghavas tīkṣṇasāyakaiḥ</li>
    </ul>
    <ul id="R_3.025.002">
        <li id="R_3.025.002ab">pratigṛhya ca tad varaṃ nimīlita ivarṣabhaḥ</li>
        <li id="R_3.025.002cd">rāmaḥ krodhaṃ paraṃ bheje vadhārthaṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_3.025.003">
        <li id="R_3.025.003ab">tataḥ krodhasamāviṣṭaḥ pradīpta iva tejasā</li>
        <li id="R_3.025.003cd">śarair abhyakirat sainyaṃ sarvataḥ sahadūṣaṇam</li>
    </ul>
    <ul id="R_3.025.004">
        <li id="R_3.025.004ab">tataḥ senāpatiḥ kruddho dūṣaṇaḥ śatrudūṣaṇaḥ</li>
        <li id="R_3.025.004cd">jagrāha giriśṛṅgābhaṃ parighaṃ romaharṣaṇam</li>
    </ul>
    <ul id="R_3.025.005">
        <li id="R_3.025.005ab">veṣṭitaṃ kāñcanaiḥ paṭṭair devasainyābhimardanam</li>
        <li id="R_3.025.005cd">āyasaiḥ śaṅkubhis tīkṣṇaiḥ kīrṇaṃ paravasokṣitām</li>
    </ul>
    <ul id="R_3.025.006">
        <li id="R_3.025.006ab">vajrāśanisamasparśaṃ paragopuradāraṇam</li>
        <li id="R_3.025.006cd">taṃ mahoragasaṃkāśaṃ pragṛhya parighaṃ raṇe</li>
        <li id="R_3.025.006ef">dūṣaṇo 'bhyapatad rāmaṃ krūrakarmā niśācaraḥ</li>
    </ul>
    <ul id="R_3.025.007">
        <li id="R_3.025.007ab">tasyābhipatamānasya dūṣaṇasya sa rāghavaḥ</li>
        <li id="R_3.025.007cd">dvābhyāṃ śarābhyāṃ ciccheda sahastābharaṇau bhujau</li>
    </ul>
    <ul id="R_3.025.008">
        <li id="R_3.025.008ab">bhraṣṭas tasya mahākāyaḥ papāta raṇamūrdhani</li>
        <li id="R_3.025.008cd">parighaś chinnahastasya śakradhvaja ivāgrataḥ</li>
    </ul>
    <ul id="R_3.025.009">
        <li id="R_3.025.009ab">sa karābhyāṃ vikīrṇābhyāṃ papāta bhuvi dūṣaṇaḥ</li>
        <li id="R_3.025.009cd">viṣāṇābhyāṃ viśīrṇābhyāṃ manasvīva mahāgajaḥ</li>
    </ul>
    <ul id="R_3.025.010">
        <li id="R_3.025.010ab">dṛṣṭvā taṃ patitaṃ bhūmau dūṣaṇaṃ nihataṃ raṇe</li>
        <li id="R_3.025.010cd">sādhu sādhv iti kākutsthaṃ sarvabhūtāny apūjayan</li>
    </ul>
    <ul id="R_3.025.011">
        <li id="R_3.025.011ab">etasminn antare kruddhās trayaḥ senāgrayāyinaḥ</li>
        <li id="R_3.025.011cd">saṃhatyābhyadravan rāmaṃ mṛtyupāśāvapāśitāḥ</li>
        <li id="R_3.025.011ef">mahākapālaḥ sthūlākṣaḥ pramāthī ca mahābalaḥ</li>
    </ul>
    <ul id="R_3.025.012">
        <li id="R_3.025.012ab">mahākapālo vipulaṃ śūlam udyamya rākṣasaḥ</li>
        <li id="R_3.025.012cd">sthūlākṣaḥ paṭṭiśaṃ gṛhya pramāthī ca paraśvadham</li>
    </ul>
    <ul id="R_3.025.013">
        <li id="R_3.025.013ab">dṛṣṭvaivāpatatas tāṃs tu rāghavaḥ sāyakaiḥ śitaiḥ</li>
        <li id="R_3.025.013cd">tīkṣṇāgraiḥ pratijagrāha saṃprāptān atithīn iva</li>
    </ul>
    <ul id="R_3.025.014">
        <li id="R_3.025.014ab">mahākapālasya śiraś ciccheda raghunaṅganaḥ</li>
        <li id="R_3.025.014cd">asaṃkhyeyais tu bāṇaughaiḥ pramamātha pramāthinam</li>
    </ul>
    <ul id="R_3.025.015">
        <li id="R_3.025.015ab">sthūlākṣasyākṣiṇī tīkṣṇaiḥ pūrayām āsa sāyakaiḥ</li>
        <li id="R_3.025.015cd">sa papāta hato bhūmau viṭapīva mahādrumaḥ</li>
    </ul>
    <ul id="R_3.025.016">
        <li id="R_3.025.016ab">tataḥ pāvakasaṃkāśair hemavajravibhūṣitaiḥ</li>
        <li id="R_3.025.016cd">jaghanaśeṣaṃ tejasvī tasya sainyasya sāyakaiḥ</li>
    </ul>
    <ul id="R_3.025.017">
        <li id="R_3.025.017ab">te rukmapuṅkhā viśikhāḥ sadhūmā iva pāvakāḥ</li>
        <li id="R_3.025.017cd">nijaghnus tāni rakṣāṃsi vajrā iva mahādrumān</li>
    </ul>
    <ul id="R_3.025.018">
        <li id="R_3.025.018ab">rakṣasāṃ tu śataṃ rāmaḥ śatenaikena karṇinā</li>
        <li id="R_3.025.018cd">sahasraṃ ca sahasreṇa jaghāna raṇamūrdhani</li>
    </ul>
    <ul id="R_3.025.019">
        <li id="R_3.025.019ab">tair bhinnavarmābharaṇāś chinnabhinnaśarāsanāḥ</li>
        <li id="R_3.025.019cd">nipetuḥ śoṇitādigdhā dharaṇyāṃ rajanīcarāḥ</li>
    </ul>
    <ul id="R_3.025.020">
        <li id="R_3.025.020ab">tair muktakeśaiḥ samare patitaiḥ śoṇitokṣitaiḥ</li>
        <li id="R_3.025.020cd">āstīrṇā vasudhā kṛtsnā mahāvediḥ kuśair iva</li>
    </ul>
    <ul id="R_3.025.021">
        <li id="R_3.025.021ab">kṣaṇena tu mahāghoraṃ vanaṃ nihatarākṣasaṃ</li>
        <li id="R_3.025.021cd">babhūva niraya prakhyaṃ māṃsaśoṇitakardamam</li>
    </ul>
    <ul id="R_3.025.022">
        <li id="R_3.025.022ab">caturdaśa sahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_3.025.022cd">hatāny ekena rāmeṇa mānuṣeṇa padātinā</li>
    </ul>
    <ul id="R_3.025.023">
        <li id="R_3.025.023ab">tasya sainyasya sarvasya kharaḥ śeṣo mahārathaḥ</li>
        <li id="R_3.025.023cd">rākṣasas triśirāś caiva rāmaś ca ripusūdanaḥ</li>
    </ul>
    <ul id="R_3.025.024">
        <li id="R_3.025.024ab">
            <seg type="pāda" n="a">tatas tu tad bhīmabalaṃ mahāhave</seg>
            <seg type="pāda" n="b">samīkṣya rāmeṇa hataṃ balīyasā</seg>
        </li>
        <li id="R_3.025.024cd">
            <seg type="pāda" n="c">rathena rāmaṃ mahatā kharas tataḥ</seg>
            <seg type="pāda" n="d">samāsasādendra ivodyatāśaniḥ</seg>
        </li>
    </ul>
    <ul id="R_3.026.001">
        <li id="R_3.026.001ab">kharaṃ tu rāmābhimukhaṃ prayāntaṃ vāhinīpatiḥ</li>
        <li id="R_3.026.001cd">rākṣasas triśirā nāma saṃnipatyedam abravīt</li>
    </ul>
    <ul id="R_3.026.002">
        <li id="R_3.026.002ab">māṃ niyojaya vikrānta saṃnivartasva sāhasāt</li>
        <li id="R_3.026.002cd">paśya rāmaṃ mahābāhuṃ saṃyuge vinipātitam</li>
    </ul>
    <ul id="R_3.026.003">
        <li id="R_3.026.003ab">pratijānāmi te satyam āyudhaṃ cāham ālabhe</li>
        <li id="R_3.026.003cd">yathā rāmaṃ vadhiṣyāmi vadhārhaṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_3.026.004">
        <li id="R_3.026.004ab">ahaṃ vāsya raṇe mṛtyur eṣa vā samare mama</li>
        <li id="R_3.026.004cd">vinivartya raṇotsāhaṃ muhūrtaṃ prāśniko bhava</li>
    </ul>
    <ul id="R_3.026.005">
        <li id="R_3.026.005ab">prahṛṣṭo vā hate rāme janasthānaṃ prayāsyasi</li>
        <li id="R_3.026.005cd">mayi vā nihate rāmaṃ saṃyugāyopayāsyasi</li>
    </ul>
    <ul id="R_3.026.006">
        <li id="R_3.026.006ab">kharas triśirasā tena mṛtyulobhāt prasāditaḥ</li>
        <li id="R_3.026.006cd">gaccha yudhyety anujñāto rāghavābhimukho yayau</li>
    </ul>
    <ul id="R_3.026.007">
        <li id="R_3.026.007ab">triśirāś ca rathenaiva vājiyuktena bhāsvatā</li>
        <li id="R_3.026.007cd">abhyadravad raṇe rāmaṃ triśṛṅga iva parvataḥ</li>
    </ul>
    <ul id="R_3.026.008">
        <li id="R_3.026.008ab">śaradhārā samūhān sa mahāmegha ivotsṛjan</li>
        <li id="R_3.026.008cd">vyasṛjat sadṛśaṃ nādaṃ jalārdrasyeva dundubheḥ</li>
    </ul>
    <ul id="R_3.026.009">
        <li id="R_3.026.009ab">āgacchantaṃ triśirasaṃ rākṣasaṃ prekṣya rāghavaḥ</li>
        <li id="R_3.026.009cd">dhanuṣā pratijagrāha vidhunvan sāyakāñ śitān</li>
    </ul>
    <ul id="R_3.026.010">
        <li id="R_3.026.010ab">sa saṃprahāras tumulo rāma triśirasor mahān</li>
        <li id="R_3.026.010cd">babhūvātīva balinoḥ siṃhakuñjarayor iva</li>
    </ul>
    <ul id="R_3.026.011">
        <li id="R_3.026.011ab">tatas triśirasā bāṇair lalāṭe tāḍitas tribhiḥ</li>
        <li id="R_3.026.011cd">amarṣī kupito rāmaḥ saṃrabdham idam abravīt</li>
    </ul>
    <ul id="R_3.026.012">
        <li id="R_3.026.012ab">aho vikramaśūrasya rākṣasasyedṛśaṃ balam</li>
        <li id="R_3.026.012cd">puṣpair iva śarair yasya lalāṭe 'smi parikṣataḥ</li>
        <li id="R_3.026.012ef">mamāpi pratigṛhṇīṣva śarāṃś cāpaguṇacyutān</li>
    </ul>
    <ul id="R_3.026.013">
        <li id="R_3.026.013ab">evam uktvā tu saṃrabdhaḥ śarān āśīviṣopamān</li>
        <li id="R_3.026.013cd">triśiro vakṣasi kruddho nijaghāna caturdaśa</li>
    </ul>
    <ul id="R_3.026.014">
        <li id="R_3.026.014ab">caturbhis turagān asya śaraiḥ saṃnataparvabhiḥ</li>
        <li id="R_3.026.014cd">nyapātayata tejasvī caturas tasya vājinaḥ</li>
    </ul>
    <ul id="R_3.026.015">
        <li id="R_3.026.015ab">aṣṭabhiḥ sāyakaiḥ sūtaṃ rathopasthe nyapātayat</li>
        <li id="R_3.026.015cd">rāmaś ciccheda bāṇena dhvajaṃ cāsya samucchritam</li>
    </ul>
    <ul id="R_3.026.016">
        <li id="R_3.026.016ab">tato hatarathāt tasmād utpatantaṃ niśācaram</li>
        <li id="R_3.026.016cd">bibheda rāmas taṃ bāṇair hṛdaye so 'bhavaj jaḍaḥ</li>
    </ul>
    <ul id="R_3.026.017">
        <li id="R_3.026.017ab">sāyakaiś cāprameyātmā sāmarṣas tasya rakṣasaḥ</li>
        <li id="R_3.026.017cd">śirāṃsy apātayat trīṇi vegavadbhis tribhiḥ śataiḥ</li>
    </ul>
    <ul id="R_3.026.018">
        <li id="R_3.026.018ab">sa bhūmau śoṇitodgārī rāmabāṇābhipīḍitaḥ</li>
        <li id="R_3.026.018cd">nyapatat patitaiḥ pūrvaṃ svaśirobhir niśācaraḥ</li>
    </ul>
    <ul id="R_3.026.019">
        <li id="R_3.026.019ab">hataśeṣās tato bhagnā rākṣasāḥ kharasaṃśrayāḥ</li>
        <li id="R_3.026.019cd">dravanti sma na tiṣṭhanti vyāghratrastā mṛgā iva</li>
    </ul>
    <ul id="R_3.026.020">
        <li id="R_3.026.020ab">tān kharo dravato dṛṣṭvā nivartya ruṣitaḥ svayam</li>
        <li id="R_3.026.020cd">rāmam evābhidudrāva rāhuś candramasaṃ yathā</li>
    </ul>
    <ul id="R_3.027.001">
        <li id="R_3.027.001ab">nihataṃ dūṣaṇaṃ dṛṣṭvā raṇe triśirasā saha</li>
        <li id="R_3.027.001cd">kharasyāpy abhavat trāso dṛṣṭvā rāmasya vikramam</li>
    </ul>
    <ul id="R_3.027.002">
        <li id="R_3.027.002ab">sa dṛṣṭvā rākṣasaṃ sainyam aviṣahyaṃ mahābalam</li>
        <li id="R_3.027.002cd">hatam ekena rāmeṇa dūṣaṇas triśirā api</li>
    </ul>
    <ul id="R_3.027.003">
        <li id="R_3.027.003ab">tad balaṃ hatabhūyiṣṭhaṃ vimanāḥ prekṣya rākṣasaḥ</li>
        <li id="R_3.027.003cd">āsasāda kharo rāmaṃ namucir vāsavaṃ yathā</li>
    </ul>
    <ul id="R_3.027.004">
        <li id="R_3.027.004ab">vikṛṣya balavac cāpaṃ nārācān raktabhojanān</li>
        <li id="R_3.027.004cd">kharaś cikṣepa rāmāya kruddhān āśīviṣān iva</li>
    </ul>
    <ul id="R_3.027.005">
        <li id="R_3.027.005ab">jyāṃ vidhunvan subahuśaḥ śikṣayāstrāṇi darśayan</li>
        <li id="R_3.027.005cd">cacāra samare mārgāñ śarai rathagataḥ kharaḥ</li>
    </ul>
    <ul id="R_3.027.006">
        <li id="R_3.027.006ab">sa sarvāś ca diśo bāṇaiḥ pradiśaś ca mahārathaḥ</li>
        <li id="R_3.027.006cd">pūrayām āsa taṃ dṛṣṭvā rāmo 'pi sumahad dhanuḥ</li>
    </ul>
    <ul id="R_3.027.007">
        <li id="R_3.027.007ab">sa sāyakair durviṣahaiḥ sasphuliṅgair ivāgnibhiḥ</li>
        <li id="R_3.027.007cd">nabhaś cakārāvivaraṃ parjanya iva vṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_3.027.008">
        <li id="R_3.027.008ab">tad babhūva śitair bāṇaiḥ khararāmavisarjitaiḥ</li>
        <li id="R_3.027.008cd">paryākāśam anākāśaṃ sarvataḥ śarasaṃkulam</li>
    </ul>
    <ul id="R_3.027.009">
        <li id="R_3.027.009ab">śarajālāvṛtaḥ sūryo na tadā sma prakāśate</li>
        <li id="R_3.027.009cd">anyonyavadhasaṃrambhād ubhayoḥ saṃprayudhyatoḥ</li>
    </ul>
    <ul id="R_3.027.010">
        <li id="R_3.027.010ab">tato nālīkanārācais tīkṣṇāgraiś ca vikarṇibhiḥ</li>
        <li id="R_3.027.010cd">ājaghāna raṇe rāmaṃ totrair iva mahādvipam</li>
    </ul>
    <ul id="R_3.027.011">
        <li id="R_3.027.011ab">taṃ rathasthaṃ dhanuṣpāṇiṃ rākṣasaṃ paryavasthitam</li>
        <li id="R_3.027.011cd">dadṛśuḥ sarvabhūtāni pāśahastam ivāntakam</li>
    </ul>
    <ul id="R_3.027.012">
        <li id="R_3.027.012ab">taṃ siṃham iva vikrāntaṃ siṃhavikrāntagāminam</li>
        <li id="R_3.027.012cd">dṛṣṭvā nodvijate rāmaḥ siṃhaḥ kṣudramṛgaṃ yathā</li>
    </ul>
    <ul id="R_3.027.013">
        <li id="R_3.027.013ab">tataḥ sūryanikāśena rathena mahatā kharaḥ</li>
        <li id="R_3.027.013cd">āsasāda raṇe rāmaṃ pataṅga iva pāvakam</li>
    </ul>
    <ul id="R_3.027.014">
        <li id="R_3.027.014ab">tato 'sya saśaraṃ cāpaṃ muṣṭideśe mahātmanaḥ</li>
        <li id="R_3.027.014cd">kharaś ciccheda rāmasya darśayan pāṇilāghavam</li>
    </ul>
    <ul id="R_3.027.015">
        <li id="R_3.027.015ab">sa punas tv aparān sapta śarān ādāya varmaṇi</li>
        <li id="R_3.027.015cd">nijaghāna raṇe kruddhaḥ śakrāśanisamaprabhān</li>
    </ul>
    <ul id="R_3.027.016">
        <li id="R_3.027.016ab">tatas tat prahataṃ bāṇaiḥ kharamuktaiḥ suparvabhiḥ</li>
        <li id="R_3.027.016cd">papāta kavacaṃ bhūmau rāmasyādityavarcasaḥ</li>
    </ul>
    <ul id="R_3.027.017">
        <li id="R_3.027.017ab">sa śarair arpitaḥ kruddhaḥ sarvagātreṣu rāghavaḥ</li>
        <li id="R_3.027.017cd">rarāja samare rāmo vidhūmo 'gnir iva jvalan</li>
    </ul>
    <ul id="R_3.027.018">
        <li id="R_3.027.018ab">tato gambhīranirhrādaṃ rāmaḥ śatrunibarhaṇaḥ</li>
        <li id="R_3.027.018cd">cakārāntāya sa ripoḥ sajyam anyan mahad dhanuḥ</li>
    </ul>
    <ul id="R_3.027.019">
        <li id="R_3.027.019ab">sumahad vaiṣṇavaṃ yat tad atisṛṣṭaṃ maharṣiṇā</li>
        <li id="R_3.027.019cd">varaṃ tad dhanur udyamya kharaṃ samabhidhāvata</li>
    </ul>
    <ul id="R_3.027.020">
        <li id="R_3.027.020ab">tataḥ kanakapuṅkhais tu śaraiḥ saṃnataparvabhiḥ</li>
        <li id="R_3.027.020cd">ciccheda rāmaḥ saṃkruddhaḥ kharasya samare dhvajam</li>
    </ul>
    <ul id="R_3.027.021">
        <li id="R_3.027.021ab">sa darśanīyo bahudhā vicchinnaḥ kāñcano dhvajaḥ</li>
        <li id="R_3.027.021cd">jagāma dharaṇīṃ sūryo devatānām ivājñayā</li>
    </ul>
    <ul id="R_3.027.022">
        <li id="R_3.027.022ab">taṃ caturbhiḥ kharaḥ kruddho rāmaṃ gātreṣu mārgaṇaiḥ</li>
        <li id="R_3.027.022cd">vivyādha hṛdi marmajño mātaṅgam iva tomaraiḥ</li>
    </ul>
    <ul id="R_3.027.023">
        <li id="R_3.027.023ab">sa rāmo bahubhir bāṇaiḥ kharakārmukaniḥsṛtaiḥ</li>
        <li id="R_3.027.023cd">viddho rudhirasiktāṅgo babhūva ruṣito bhṛśam</li>
    </ul>
    <ul id="R_3.027.024">
        <li id="R_3.027.024ab">sa dhanur dhanvināṃ śreṣṭhaḥ pragṛhya paramāhave</li>
        <li id="R_3.027.024cd">mumoca parameṣvāsaḥ ṣaṭ śarān abhilakṣitān</li>
    </ul>
    <ul id="R_3.027.025">
        <li id="R_3.027.025ab">śirasy ekena bāṇena dvābhyāṃ bāhvor athārpayat</li>
        <li id="R_3.027.025cd">tribhiś candrārdhavaktraiś ca vakṣasy abhijaghāna ha</li>
    </ul>
    <ul id="R_3.027.026">
        <li id="R_3.027.026ab">tataḥ paścān mahātejā nārācān bhāskaropamān</li>
        <li id="R_3.027.026cd">jighāṃsū rākṣasaṃ kruddhas trayodaśa śilāśitān</li>
    </ul>
    <ul id="R_3.027.027">
        <li id="R_3.027.027ab">tato 'sya yugam ekena caturbhiś caturo hayān</li>
        <li id="R_3.027.027cd">ṣaṣṭhena ca śiraḥ saṃkhye ciccheda kharasāratheḥ</li>
    </ul>
    <ul id="R_3.027.028">
        <li id="R_3.027.028ab">tribhis triveṇuṃ balavān dvābhyām akṣaṃ mahābalaḥ</li>
        <li id="R_3.027.028cd">dvādaśena tu bāṇena kharasya saśaraṃ dhanuḥ</li>
        <li id="R_3.027.028ef">chittvā vajranikāśena rāghavaḥ prahasann iva</li>
        <li id="R_3.027.028gh">trayodaśenendrasamo bibheda samare kharam</li>
    </ul>
    <ul id="R_3.027.029">
        <li id="R_3.027.029ab">prabhagnadhanvā viratho hatāśvo hatasārathiḥ</li>
        <li id="R_3.027.029cd">gadāpāṇir avaplutya tasthau bhūmau kharas tadā</li>
    </ul>
    <ul id="R_3.027.030">
        <li id="R_3.027.030ab">
            <seg type="pāda" n="a">tat karma rāmasya mahārathasya</seg>
            <seg type="pāda" n="b">sametya devāś ca maharṣayaś ca</seg>
        </li>
        <li id="R_3.027.030cd">
            <seg type="pāda" n="c">apūjayan prāñjalayaḥ prahṛṣṭās</seg>
            <seg type="pāda" n="d">tadā vimānāgragatāḥ sametāḥ</seg>
        </li>
    </ul>
    <ul id="R_3.028.001">
        <li id="R_3.028.001ab">kharaṃ tu virathaṃ rāmo gadāpāṇim avasthitam</li>
        <li id="R_3.028.001cd">mṛdupūrvaṃ mahātejāḥ paruṣaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_3.028.002">
        <li id="R_3.028.002ab">gajāśvarathasaṃbādhe bale mahati tiṣṭhatā</li>
        <li id="R_3.028.002cd">kṛtaṃ sudāruṇaṃ karma sarvalokajugupsitam</li>
    </ul>
    <ul id="R_3.028.003">
        <li id="R_3.028.003ab">udvejanīyo bhūtānāṃ nṛśaṃsaḥ pāpakarmakṛt</li>
        <li id="R_3.028.003cd">trayāṇām api lokānām īśvaro 'pi na tiṣṭhati</li>
    </ul>
    <ul id="R_3.028.004">
        <li id="R_3.028.004ab">karma lokaviruddhaṃ tu kurvāṇaṃ kṣaṇadācara</li>
        <li id="R_3.028.004cd">tīkṣṇaṃ sarvajano hanti sarpaṃ duṣṭam ivāgatam</li>
    </ul>
    <ul id="R_3.028.005">
        <li id="R_3.028.005ab">lobhāt pāpāni kurvāṇaḥ kāmād vā yo na budhyate</li>
        <li id="R_3.028.005cd">bhraṣṭaḥ paśyati tasyāntaṃ brāhmaṇī karakād iva</li>
    </ul>
    <ul id="R_3.028.006">
        <li id="R_3.028.006ab">vasato daṇḍakāraṇye tāpasān dharmacāriṇaḥ</li>
        <li id="R_3.028.006cd">kiṃ nu hatvā mahābhāgān phalaṃ prāpsyasi rākṣasa</li>
    </ul>
    <ul id="R_3.028.007">
        <li id="R_3.028.007ab">na ciraṃ pāpakarmāṇaḥ krūrā lokajugupsitāḥ</li>
        <li id="R_3.028.007cd">aiśvaryaṃ prāpya tiṣṭhanti śīrṇamūlā iva drumāḥ</li>
    </ul>
    <ul id="R_3.028.008">
        <li id="R_3.028.008ab">avaśyaṃ labhate kartā phalaṃ pāpasya karmaṇaḥ</li>
        <li id="R_3.028.008cd">ghoraṃ paryāgate kāle drumaḥ puṣpam ivārtavam</li>
    </ul>
    <ul id="R_3.028.009">
        <li id="R_3.028.009ab">nacirāt prāpyate loke pāpānāṃ karmaṇāṃ phalam</li>
        <li id="R_3.028.009cd">saviṣāṇām ivānnānāṃ bhuktānāṃ kṣaṇadācara</li>
    </ul>
    <ul id="R_3.028.010">
        <li id="R_3.028.010ab">pāpam āccaratāṃ ghoraṃ lokasyāpriyam icchatām</li>
        <li id="R_3.028.010cd">aham āsādito rājā prāṇān hantuṃ niśācara</li>
    </ul>
    <ul id="R_3.028.011">
        <li id="R_3.028.011ab">adya hi tvāṃ mayā muktāḥ śarāḥ kāñcanabhūṣaṇāḥ</li>
        <li id="R_3.028.011cd">vidārya nipatiṣyanti valmīkam iva pannagāḥ</li>
    </ul>
    <ul id="R_3.028.012">
        <li id="R_3.028.012ab">ye tvayā daṇḍakāraṇye bhakṣitā dharmacāriṇaḥ</li>
        <li id="R_3.028.012cd">tān adya nihataḥ saṃkhye sasainyo 'nugamiṣyasi</li>
    </ul>
    <ul id="R_3.028.013">
        <li id="R_3.028.013ab">adya tvāṃ nihataṃ bāṇaiḥ paśyantu paramarṣayaḥ</li>
        <li id="R_3.028.013cd">nirayasthaṃ vimānasthā ye tvayā hiṃsitāḥ purā</li>
    </ul>
    <ul id="R_3.028.014">
        <li id="R_3.028.014ab">prahara tvaṃ yathākāmaṃ kuru yatnaṃ kulādhama</li>
        <li id="R_3.028.014cd">adya te pātayiṣyāmi śiras tālaphalaṃ yathā</li>
    </ul>
    <ul id="R_3.028.015">
        <li id="R_3.028.015ab">evam uktas tu rāmeṇa kruddhaḥ saṃraktalocanaḥ</li>
        <li id="R_3.028.015cd">pratyuvāca tato rāmaṃ prahasan krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_3.028.016">
        <li id="R_3.028.016ab">prākṛtān rākṣasān hatvā yuddhe daśarathātmaja</li>
        <li id="R_3.028.016cd">ātmanā katham ātmānam apraśasyaṃ praśaṃsasi</li>
    </ul>
    <ul id="R_3.028.017">
        <li id="R_3.028.017ab">vikrāntā balavanto vā ye bhavanti nararṣabhāḥ</li>
        <li id="R_3.028.017cd">kathayanti na te kiṃ cit tejasā svena garvitāḥ</li>
    </ul>
    <ul id="R_3.028.018">
        <li id="R_3.028.018ab">prākṛtās tv akṛtātmāno loke kṣatriyapāṃsanāḥ</li>
        <li id="R_3.028.018cd">nirarthakaṃ vikatthante yathā rāma vikatthase</li>
    </ul>
    <ul id="R_3.028.019">
        <li id="R_3.028.019ab">kulaṃ vyapadiśan vīraḥ samare ko 'bhidhāsyati</li>
        <li id="R_3.028.019cd">mṛtyukāle hi saṃprāpte svayam aprastave stavam</li>
    </ul>
    <ul id="R_3.028.020">
        <li id="R_3.028.020ab">sarvathā tu laghutvaṃ te katthanena vidarśitam</li>
        <li id="R_3.028.020cd">suvarṇapratirūpeṇa tapteneva kuśāgninā</li>
    </ul>
    <ul id="R_3.028.021">
        <li id="R_3.028.021ab">na tu mām iha tiṣṭhantaṃ paśyasi tvaṃ gadādharam</li>
        <li id="R_3.028.021cd">dharādharam ivākampyaṃ parvataṃ dhātubhiś citam</li>
    </ul>
    <ul id="R_3.028.022">
        <li id="R_3.028.022ab">paryāpto 'haṃ gadāpāṇir hantuṃ prāṇān raṇe tava</li>
        <li id="R_3.028.022cd">trayāṇām api lokānāṃ pāśahasta ivāntakaḥ</li>
    </ul>
    <ul id="R_3.028.023">
        <li id="R_3.028.023ab">kāmaṃ bahv api vaktavyaṃ tvayi vakṣyāmi na tv aham</li>
        <li id="R_3.028.023cd">astaṃ gacched dhi savitā yuddhavighras tato bhavet</li>
    </ul>
    <ul id="R_3.028.024">
        <li id="R_3.028.024ab">caturdaśa sahasrāṇi rākṣasānāṃ hatāni te</li>
        <li id="R_3.028.024cd">tvadvināśāt karomy adya teṣām aśrupramārjanam</li>
    </ul>
    <ul id="R_3.028.025">
        <li id="R_3.028.025ab">ity uktvā paramakruddhas tāṃ gadāṃ paramāṅgadām</li>
        <li id="R_3.028.025cd">kharaś cikṣepa rāmāya pradīptām aśaniṃ yathā</li>
    </ul>
    <ul id="R_3.028.026">
        <li id="R_3.028.026ab">kharabāhupramuktā sā pradīptā mahatī gadā</li>
        <li id="R_3.028.026cd">bhasmavṛkṣāṃś ca gulmāṃś ca kṛtvāgāt tatsamīpataḥ</li>
    </ul>
    <ul id="R_3.028.027">
        <li id="R_3.028.027ab">tām āpatantīṃ jvalitāṃ mṛtyupāśopamāṃ gadā</li>
        <li id="R_3.028.027cd">antarikṣagatāṃ rāmaś ciccheda bahudhā śaraiḥ</li>
    </ul>
    <ul id="R_3.028.028">
        <li id="R_3.028.028ab">sā viśīrṇā śarair bhinnā papāta dharaṇītale</li>
        <li id="R_3.028.028cd">gadāmantrauṣadhibalair vyālīva vinipātitā</li>
    </ul>
    <ul id="R_3.029.001">
        <li id="R_3.029.001ab">bhittvā tu tāṃ gadāṃ bāṇai rāghavo dharmavatsalaḥ</li>
        <li id="R_3.029.001cd">smayamānaḥ kharaṃ vākyaṃ saṃrabdham idam abravīt</li>
    </ul>
    <ul id="R_3.029.002">
        <li id="R_3.029.002ab">etat te balasarvasvaṃ darśitaṃ rākṣasādhama</li>
        <li id="R_3.029.002cd">śaktihīnataro matto vṛthā tvam upagarjitam</li>
    </ul>
    <ul id="R_3.029.003">
        <li id="R_3.029.003ab">eṣā bāṇavinirbhinnā gadā bhūmitalaṃ gatā</li>
        <li id="R_3.029.003cd">abhidhānapragalbhasya tava pratyayaghātinī</li>
    </ul>
    <ul id="R_3.029.004">
        <li id="R_3.029.004ab">yat tvayoktaṃ vinaṣṭānām idam aśrupramārjanam</li>
        <li id="R_3.029.004cd">rākṣasānāṃ karomīti mithyā tad api te vacaḥ</li>
    </ul>
    <ul id="R_3.029.005">
        <li id="R_3.029.005ab">nīcasya kṣudraśīlasya mithyāvṛttasya rakṣasaḥ</li>
        <li id="R_3.029.005cd">prāṇān apahariṣyāmi garutmān amṛtaṃ yathā</li>
    </ul>
    <ul id="R_3.029.006">
        <li id="R_3.029.006ab">adya te bhinnakaṇṭhasya phenabudbudabhūṣitam</li>
        <li id="R_3.029.006cd">vidāritasya madbāṇair mahī pāsyati śoṇitam</li>
    </ul>
    <ul id="R_3.029.007">
        <li id="R_3.029.007ab">pāṃsurūṣitasarvāṅgaḥ srastanyastabhujadvayaḥ</li>
        <li id="R_3.029.007cd">svapsyase gāṃ samāśliṣya durlabhāṃ pramadām iva</li>
    </ul>
    <ul id="R_3.029.008">
        <li id="R_3.029.008ab">pravṛddhanidre śayite tvayi rākṣasapāṃsane</li>
        <li id="R_3.029.008cd">bhaviṣyanty aśaraṇyānāṃ śaraṇyā daṇḍakā ime</li>
    </ul>
    <ul id="R_3.029.009">
        <li id="R_3.029.009ab">janasthāne hatasthāne tava rākṣasamaccharaiḥ</li>
        <li id="R_3.029.009cd">nirbhayā vicariṣyanti sarvato munayo vane</li>
    </ul>
    <ul id="R_3.029.010">
        <li id="R_3.029.010ab">adya viprasariṣyanti rākṣasyo hatabāndhavāḥ</li>
        <li id="R_3.029.010cd">bāṣpārdravadanā dīnā bhayād anyabhayāvahāḥ</li>
    </ul>
    <ul id="R_3.029.011">
        <li id="R_3.029.011ab">adya śokarasajñās tā bhaviṣyanti niśācara</li>
        <li id="R_3.029.011cd">anurūpakulāḥ patnyo yāsāṃ tvaṃ patir īdṛśaḥ</li>
    </ul>
    <ul id="R_3.029.012">
        <li id="R_3.029.012ab">nṛśaṃsaśīla kṣudrātman nityaṃ brāhmaṇakaṇṭaka</li>
        <li id="R_3.029.012cd">tvatkṛte śaṅkitair agnau munibhiḥ pātyate haviḥ</li>
    </ul>
    <ul id="R_3.029.013">
        <li id="R_3.029.013ab">tam evam abhisaṃrabdhaṃ bruvāṇaṃ rāghavaṃ raṇe</li>
        <li id="R_3.029.013cd">kharo nirbhartsayām āsa roṣāt kharatara svanaḥ</li>
    </ul>
    <ul id="R_3.029.014">
        <li id="R_3.029.014ab">dṛḍhaṃ khalv avalipto 'si bhayeṣv api ca nirbhayaḥ</li>
        <li id="R_3.029.014cd">vācyāvācyaṃ tato hi tvaṃ mṛtyuvaśyo na budhyase</li>
    </ul>
    <ul id="R_3.029.015">
        <li id="R_3.029.015ab">kālapāśaparikṣiptā bhavanti puruṣā hi ye</li>
        <li id="R_3.029.015cd">kāryākāryaṃ na jānanti te nirastaṣaḍindriyāḥ</li>
    </ul>
    <ul id="R_3.029.016">
        <li id="R_3.029.016ab">evam uktvā tato rāmaṃ saṃrudhya bhṛkuṭiṃ tataḥ</li>
        <li id="R_3.029.016cd">sa dadarśa mahāsālam avidūre niśācaraḥ</li>
    </ul>
    <ul id="R_3.029.017">
        <li id="R_3.029.017ab">raṇe praharaṇasyārthe sarvato hy avalokayan</li>
        <li id="R_3.029.017cd">sa tam utpāṭayām āsa saṃdṛśya daśanacchadam</li>
    </ul>
    <ul id="R_3.029.018">
        <li id="R_3.029.018ab">taṃ samutkṣipya bāhubhyāṃ vinarditvā mahābalaḥ</li>
        <li id="R_3.029.018cd">rāmam uddiśya cikṣepa hatas tvam iti cābravīt</li>
    </ul>
    <ul id="R_3.029.019">
        <li id="R_3.029.019ab">tam āpatantaṃ bāṇaughaiś chittvā rāmaḥ pratāpavān</li>
        <li id="R_3.029.019cd">roṣam āhārayat tīvraṃ nihantuṃ samare kharam</li>
    </ul>
    <ul id="R_3.029.020">
        <li id="R_3.029.020ab">jātasvedas tato rāmo roṣād raktāntalocanaḥ</li>
        <li id="R_3.029.020cd">nirbibheda sahasreṇa bāṇānāṃ samare kharam</li>
    </ul>
    <ul id="R_3.029.021">
        <li id="R_3.029.021ab">tasya bāṇāntarād raktaṃ bahu susrāva phenilam</li>
        <li id="R_3.029.021cd">gireḥ prasravaṇasyeva toyadhārāparisravaḥ</li>
    </ul>
    <ul id="R_3.029.022">
        <li id="R_3.029.022ab">vihvalaḥ sa kṛto bāṇaiḥ kharo rāmeṇa saṃyuge</li>
        <li id="R_3.029.022cd">matto rudhiragandhena tam evābhyadravad drutam</li>
    </ul>
    <ul id="R_3.029.023">
        <li id="R_3.029.023ab">tam āpatantaṃ saṃrabdhaṃ kṛtāstro rudhirāplutam</li>
        <li id="R_3.029.023cd">apasarpat pratipadaṃ kiṃ cit tvaritavikramaḥ</li>
    </ul>
    <ul id="R_3.029.024">
        <li id="R_3.029.024ab">tataḥ pāvakasaṃkāśaṃ badhāya samare śaram</li>
        <li id="R_3.029.024cd">kharasya rāmo jagrāha brahmadaṇḍam ivāparam</li>
    </ul>
    <ul id="R_3.029.025">
        <li id="R_3.029.025ab">sa tad dattaṃ maghavatā surarājena dhīmatā</li>
        <li id="R_3.029.025cd">saṃdadhe ca sa dharmātmā mumoca ca kharaṃ prati</li>
    </ul>
    <ul id="R_3.029.026">
        <li id="R_3.029.026ab">sa vimukto mahābāṇo nirghātasamaniḥsvanaḥ</li>
        <li id="R_3.029.026cd">rāmeṇa dhanur udyamya kharasyorasi cāpatat</li>
    </ul>
    <ul id="R_3.029.027">
        <li id="R_3.029.027ab">sa papāta kharo bhūmau dahyamānaḥ śarāgninā</li>
        <li id="R_3.029.027cd">rudreṇaiva vinirdagdhaḥ śvetāraṇye yathāndhakaḥ</li>
    </ul>
    <ul id="R_3.029.028">
        <li id="R_3.029.028ab">sa vṛtra iva vajreṇa phenena namucir yathā</li>
        <li id="R_3.029.028cd">balo vendrāśanihato nipapāta hataḥ kharaḥ</li>
    </ul>
    <ul id="R_3.029.029">
        <li id="R_3.029.029ab">tato rājarṣayaḥ sarve saṃgatāḥ paramarṣayaḥ</li>
        <li id="R_3.029.029cd">sabhājya muditā rāmam idaṃ vacanam abruvan</li>
    </ul>
    <ul id="R_3.029.030">
        <li id="R_3.029.030ab">etadarthaṃ mahātejā mahendraḥ pākaśāsanaḥ</li>
        <li id="R_3.029.030cd">śarabhaṅgāśramaṃ puṇyam ājagāma puraṃdaraḥ</li>
    </ul>
    <ul id="R_3.029.031">
        <li id="R_3.029.031ab">ānītas tvam imaṃ deśam upāyena maharṣibhiḥ</li>
        <li id="R_3.029.031cd">eṣāṃ vadhārthaṃ krūrāṇāṃ rakṣasāṃ pāpakarmaṇām</li>
    </ul>
    <ul id="R_3.029.032">
        <li id="R_3.029.032ab">tad idaṃ naḥ kṛtaṃ kāryaṃ tvayā daśarathātmaja</li>
        <li id="R_3.029.032cd">sukhaṃ dharmaṃ cariṣyanti daṇḍakeṣu maharṣayaḥ</li>
    </ul>
    <ul id="R_3.029.033">
        <li id="R_3.029.033ab">etasminn antare vīro lakṣmaṇaḥ saha sītayā</li>
        <li id="R_3.029.033cd">giridurgād viniṣkramya saṃviveśāśramaṃ sukhī</li>
    </ul>
    <ul id="R_3.029.034">
        <li id="R_3.029.034ab">tato rāmas tu vijayī pūjyamāno maharṣibhiḥ</li>
        <li id="R_3.029.034cd">praviveśāśramaṃ vīro lakṣmaṇenābhivāditaḥ</li>
    </ul>
    <ul id="R_3.029.035">
        <li id="R_3.029.035ab">taṃ dṛṣṭvā śatruhantāraṃ maharṣīṇāṃ sukhāvaham</li>
        <li id="R_3.029.035cd">babhūva hṛṣṭā vaidehī bhartāraṃ pariṣasvaje</li>
    </ul>
    <ul id="R_3.030.001">
        <li id="R_3.030.001ab">tataḥ śūrpaṇakhā dṛṣṭvā sahasrāṇi caturdaśa</li>
        <li id="R_3.030.001cd">hatāny ekena rāmeṇa rakṣasāṃ bhīmakarmaṇām</li>
    </ul>
    <ul id="R_3.030.002">
        <li id="R_3.030.002ab">dūṣaṇaṃ ca kharaṃ caiva hataṃ triśirasaṃ raṇe</li>
        <li id="R_3.030.002cd">dṛṣṭvā punar mahānādaṃ nanāda jaladopamā</li>
    </ul>
    <ul id="R_3.030.003">
        <li id="R_3.030.003ab">sā dṛṣṭvā karma rāmasya kṛtam anyaiḥ suduṣkaram</li>
        <li id="R_3.030.003cd">jagāma paramaudvignā laṅkāṃ rāvaṇapālitām</li>
    </ul>
    <ul id="R_3.030.004">
        <li id="R_3.030.004ab">sa dadarśa vimānāgre rāvaṇaṃ dīptatejasaṃ</li>
        <li id="R_3.030.004cd">upopaviṣṭaṃ sacivair marudbhir iva vāsavam</li>
    </ul>
    <ul id="R_3.030.005">
        <li id="R_3.030.005ab">āsīnaṃ sūryasaṃkāśe kāñcane paramāsane</li>
        <li id="R_3.030.005cd">rukmavedigataṃ prājyaṃ jvalantam iva pāvakam</li>
    </ul>
    <ul id="R_3.030.006">
        <li id="R_3.030.006ab">devagandharvabhūtānām ṛṣīṇāṃ ca mahātmanām</li>
        <li id="R_3.030.006cd">ajeyaṃ samare śūraṃ vyāttānanam ivāntakam</li>
    </ul>
    <ul id="R_3.030.007">
        <li id="R_3.030.007ab">devāsuravimardeṣu vajrāśanikṛtavraṇam</li>
        <li id="R_3.030.007cd">airāvataviṣāṇāgrair utkṛṣṭakiṇavakṣasaṃ</li>
    </ul>
    <ul id="R_3.030.008">
        <li id="R_3.030.008ab">viṃśadbhujaṃ daśagrīvaṃ darśanīyaparicchadam</li>
        <li id="R_3.030.008cd">viśālavakṣasaṃ vīraṃ rājalakṣmaṇalakṣitam</li>
    </ul>
    <ul id="R_3.030.009">
        <li id="R_3.030.009ab">snigdhavaidūryasaṃkāśaṃ taptakāñcanakuṇḍalam</li>
        <li id="R_3.030.009cd">subhujaṃ śukladaśanaṃ mahāsyaṃ parvatopamam</li>
    </ul>
    <ul id="R_3.030.010">
        <li id="R_3.030.010ab">viṣṇucakranipātaiś ca śataśo devasaṃyuge</li>
        <li id="R_3.030.010cd">āhatāṅgaṃ samastaiś ca devapraharaṇais tathā</li>
    </ul>
    <ul id="R_3.030.011">
        <li id="R_3.030.011ab">akṣobhyāṇāṃ samudrāṇāṃ kṣobhaṇaṃ kṣiprakāriṇam</li>
        <li id="R_3.030.011cd">kṣeptāraṃ parvatāgrāṇāṃ surāṇāṃ ca pramardanam</li>
    </ul>
    <ul id="R_3.030.012">
        <li id="R_3.030.012ab">ucchettāraṃ ca dharmāṇāṃ paradārābhimarśanam</li>
        <li id="R_3.030.012cd">sarvadivyāstrayoktāraṃ yajñavighnakaraṃ sadā</li>
    </ul>
    <ul id="R_3.030.013">
        <li id="R_3.030.013ab">purīṃ bhogavatīṃ gatvā parājitya ca vāsukim</li>
        <li id="R_3.030.013cd">takṣakasya priyāṃ bhāryāṃ parājitya jahāra yaḥ</li>
    </ul>
    <ul id="R_3.030.014">
        <li id="R_3.030.014ab">kailāsaṃ parvataṃ gatvā vijitya naravāhanam</li>
        <li id="R_3.030.014cd">vimānaṃ puṣpakaṃ tasya kāmagaṃ vai jahāra yaḥ</li>
    </ul>
    <ul id="R_3.030.015">
        <li id="R_3.030.015ab">vanaṃ caitrarathaṃ divyaṃ nalinīṃ nandanaṃ vanam</li>
        <li id="R_3.030.015cd">vināśayati yaḥ krodhād devodyānāni vīryavān</li>
    </ul>
    <ul id="R_3.030.016">
        <li id="R_3.030.016ab">candrasūryau mahābhāgāv uttiṣṭhantau paraṃtapau</li>
        <li id="R_3.030.016cd">nivārayati bāhubhyāṃ yaḥ śailaśikharopamaḥ</li>
    </ul>
    <ul id="R_3.030.017">
        <li id="R_3.030.017ab">daśavarṣasahasrāṇi tapas taptvā mahāvane</li>
        <li id="R_3.030.017cd">purā svayambhuve dhīraḥ śirāṃsy upajahāra yaḥ</li>
    </ul>
    <ul id="R_3.030.018">
        <li id="R_3.030.018ab">devadānavagandharvapiśācapatagoragaiḥ</li>
        <li id="R_3.030.018cd">abhayaṃ yasya saṃgrāme mṛtyuto mānuṣād ṛte</li>
    </ul>
    <ul id="R_3.030.019">
        <li id="R_3.030.019ab">mantrar abhituṣṭaṃ puṇyam adhvareṣu dvijātibhiḥ</li>
        <li id="R_3.030.019cd">havirdhāneṣu yaḥ somam upahanti mahābalaḥ</li>
    </ul>
    <ul id="R_3.030.020">
        <li id="R_3.030.020ab">āptayajñaharaṃ krūraṃ brahmaghnaṃ duṣṭacāriṇam</li>
        <li id="R_3.030.020cd">karkaśaṃ niranukrośaṃ prajānām ahite ratam</li>
        <li id="R_3.030.020ef">rāvaṇaṃ sarvabhūtānāṃ sarvalokabhayāvaham</li>
    </ul>
    <ul id="R_3.030.021">
        <li id="R_3.030.021ab">rākṣasī bhrātaraṃ krūraṃ sā dadarśa mahābalam</li>
        <li id="R_3.030.021cd">taṃ divyavastrābharaṇaṃ divyamālyopaśobhitam</li>
        <li id="R_3.030.021ef">rākṣasendraṃ mahābhāgaṃ paulastya kulanandanam</li>
    </ul>
    <ul id="R_3.030.022">
        <li id="R_3.030.022ab">
            <seg type="pāda" n="a">tam abravīd dīptaviśālalocanaṃ</seg>
            <seg type="pāda" n="b">pradarśayitvā bhayamohamūrchitā</seg>
        </li>
        <li id="R_3.030.022cd">
            <seg type="pāda" n="c">sudāruṇaṃ vākyam abhītacāriṇī</seg>
            <seg type="pāda" n="d">mahātmanā śūrpaṇakhā virūpitā</seg>
        </li>
    </ul>
    <ul id="R_3.031.001">
        <li id="R_3.031.001ab">tataḥ śūrpaṇakhā dīnā rāvaṇaṃ lokarāvaṇam</li>
        <li id="R_3.031.001cd">amātyamadhye saṃkruddhā paruṣaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_3.031.002">
        <li id="R_3.031.002ab">pramattaḥ kāmabhogeṣu svairavṛtto niraṅkuśaḥ</li>
        <li id="R_3.031.002cd">samutpannaṃ bhayaṃ ghoraṃ boddhavyaṃ nāvabudhyase</li>
    </ul>
    <ul id="R_3.031.003">
        <li id="R_3.031.003ab">saktaṃ grāmyeṣu bhogeṣu kāmavṛttaṃ mahīpatim</li>
        <li id="R_3.031.003cd">lubdhaṃ na bahu manyante śmaśānāgnim iva prajāḥ</li>
    </ul>
    <ul id="R_3.031.004">
        <li id="R_3.031.004ab">svayaṃ kāryāṇi yaḥ kāle nānutiṣṭhati pārthivaḥ</li>
        <li id="R_3.031.004cd">sa tu vai saha rājyena taiś ca kāryair vinaśyati</li>
    </ul>
    <ul id="R_3.031.005">
        <li id="R_3.031.005ab">ayuktacāraṃ durdarśam asvādhīnaṃ narādhipam</li>
        <li id="R_3.031.005cd">varjayanti narā dūrān nadīpaṅkam iva dvipāḥ</li>
    </ul>
    <ul id="R_3.031.006">
        <li id="R_3.031.006ab">ye na rakṣanti viṣayam asvādhīnā narādhipaḥ</li>
        <li id="R_3.031.006cd">te na vṛddhyā prakāśante girayaḥ sāgare yathā</li>
    </ul>
    <ul id="R_3.031.007">
        <li id="R_3.031.007ab">ātmavadbhir vigṛhya tvaṃ devagandharvadānavaiḥ</li>
        <li id="R_3.031.007cd">ayuktacāraś capalaḥ kathaṃ rājā bhaviṣyasi</li>
    </ul>
    <ul id="R_3.031.008">
        <li id="R_3.031.008ab">yeṣāṃ cāraś ca kośaś ca nayaś ca jayatāṃ vara</li>
        <li id="R_3.031.008cd">asvādhīnā narendrāṇāṃ prākṛtais te janaiḥ samāḥ</li>
    </ul>
    <ul id="R_3.031.009">
        <li id="R_3.031.009ab">yasmāt paśyanti dūrasthān sarvān arthān narādhipāḥ</li>
        <li id="R_3.031.009cd">cāreṇa tasmād ucyante rājāno dīrghacakṣuṣaḥ</li>
    </ul>
    <ul id="R_3.031.010">
        <li id="R_3.031.010ab">ayuktacāraṃ manye tvāṃ prākṛtaiḥ sacivair vṛtam</li>
        <li id="R_3.031.010cd">svajanaṃ ca janasthānaṃ hataṃ yo nāvabudhyase</li>
    </ul>
    <ul id="R_3.031.011">
        <li id="R_3.031.011ab">caturdaśa sahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_3.031.011cd">hatāny ekena rāmeṇa kharaś ca sahadūṣaṇaḥ</li>
    </ul>
    <ul id="R_3.031.012">
        <li id="R_3.031.012ab">ṛṣīṇām abhayaṃ dattaṃ kṛtakṣemāś ca daṇḍakāḥ</li>
        <li id="R_3.031.012cd">dharṣitaṃ ca janasthānaṃ rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_3.031.013">
        <li id="R_3.031.013ab">tvaṃ tu lubdhaḥ pramattaś ca parādhīnaś ca rāvaṇa</li>
        <li id="R_3.031.013cd">viṣaye sve samutpannaṃ bhayaṃ yo nāvabudhyase</li>
    </ul>
    <ul id="R_3.031.014">
        <li id="R_3.031.014ab">tīkṣṇam alpapradātāraṃ pramattaṃ garvitaṃ śaṭham</li>
        <li id="R_3.031.014cd">vyasane sarvabhūtāni nābhidhāvanti pārthivam</li>
    </ul>
    <ul id="R_3.031.015">
        <li id="R_3.031.015ab">atimāninam agrāhyam ātmasaṃbhāvitaṃ naram</li>
        <li id="R_3.031.015cd">krodhanaṃ vyasane hanti svajano 'pi narādhipam</li>
    </ul>
    <ul id="R_3.031.016">
        <li id="R_3.031.016ab">nānutiṣṭhati kāryāṇi bhayeṣu na bibheti ca</li>
        <li id="R_3.031.016cd">kṣipraṃ rājyāc cyuto dīnas tṛṇais tulyo bhaviṣyati</li>
    </ul>
    <ul id="R_3.031.017">
        <li id="R_3.031.017ab">śuṣkakāṣṭhair bhavet kāryaṃ loṣṭair api ca pāṃsubhiḥ</li>
        <li id="R_3.031.017cd">na tu sthānāt paribhraṣṭaiḥ kāryaṃ syād vasudhādhipaiḥ</li>
    </ul>
    <ul id="R_3.031.018">
        <li id="R_3.031.018ab">upabhuktaṃ yathā vāsaḥ srajo vā mṛditā yathā</li>
        <li id="R_3.031.018cd">evaṃ rājyāt paribhraṣṭaḥ samartho 'pi nirarthakaḥ</li>
    </ul>
    <ul id="R_3.031.019">
        <li id="R_3.031.019ab">apramattaś ca yo rājā sarvajño vijitendriyaḥ</li>
        <li id="R_3.031.019cd">kṛtajño dharmaśīlaś ca sa rājā tiṣṭhate ciram</li>
    </ul>
    <ul id="R_3.031.020">
        <li id="R_3.031.020ab">nayanābhyāṃ prasupto 'pi jāgarti nayacakṣuṣā</li>
        <li id="R_3.031.020cd">vyaktakrodhaprasādaś ca sa rājā pūjyate janaiḥ</li>
    </ul>
    <ul id="R_3.031.021">
        <li id="R_3.031.021ab">tvaṃ tu rāvaṇadurbuddhir guṇair etair vivarjitaḥ</li>
        <li id="R_3.031.021cd">yasya te 'viditaś cārai rakṣasāṃ sumahān vadhaḥ</li>
    </ul>
    <ul id="R_3.031.022">
        <li id="R_3.031.022ab">
            <seg type="pāda" n="a">parāvamantā viṣayeṣu saṃgato</seg>
            <seg type="pāda" n="b">nadeśa kālapravibhāga tattvavit</seg>
        </li>
        <li id="R_3.031.022cd">
            <seg type="pāda" n="c">ayuktabuddhir guṇadoṣaniścaye</seg>
            <seg type="pāda" n="d">vipannarājyo na cirād vipatsyate</seg>
        </li>
    </ul>
    <ul id="R_3.031.023">
        <li id="R_3.031.023ab">
            <seg type="pāda" n="a">iti svadoṣān parikīrtitāṃs tayā</seg>
            <seg type="pāda" n="b">samīkṣya buddhyā kṣaṇadācareśvaraḥ</seg>
        </li>
        <li id="R_3.031.023cd">
            <seg type="pāda" n="c">dhanena darpeṇa balena cānvito</seg>
            <seg type="pāda" n="d">vicintayām āsa ciraṃ sa rāvaṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.032.001">
        <li id="R_3.032.001ab">tataḥ śūrpaṇakhāṃ kruddhāṃ bruvatīṃ paruṣaṃ vacaḥ</li>
        <li id="R_3.032.001cd">amātyamadhye saṃkruddhaḥ paripapraccha rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.032.002">
        <li id="R_3.032.002ab">kaś ca rāmaḥ kathaṃ vīryaḥ kiṃ rūpaḥ kiṃ parākramaḥ</li>
        <li id="R_3.032.002cd">kimarthaṃ daṇḍakāraṇyaṃ praviṣṭaś ca suduścaram</li>
    </ul>
    <ul id="R_3.032.003">
        <li id="R_3.032.003ab">āyudhaṃ kiṃ ca rāmasya nihatā yena rākṣasāḥ</li>
        <li id="R_3.032.003cd">kharaś ca nihataṃ saṃkhye dūṣaṇas triśirās tathā</li>
    </ul>
    <ul id="R_3.032.004">
        <li id="R_3.032.004ab">ity uktā rākṣasendreṇa rākṣasī krodhamūrchitā</li>
        <li id="R_3.032.004cd">tato rāmaṃ yathānyāyam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_3.032.005">
        <li id="R_3.032.005ab">dīrghabāhur viśālākṣaś cīrakṛṣṇājināmbaraḥ</li>
        <li id="R_3.032.005cd">kandarpasamarūpaś ca rāmo daśarathātmajaḥ</li>
    </ul>
    <ul id="R_3.032.006">
        <li id="R_3.032.006ab">śakracāpanibhaṃ cāpaṃ vikṛṣya kanakāṅgadam</li>
        <li id="R_3.032.006cd">dīptān kṣipati nārācān sarpān iva mahāviṣān</li>
    </ul>
    <ul id="R_3.032.007">
        <li id="R_3.032.007ab">nādadānaṃ śarān ghorān na muñcantaṃ mahābalam</li>
        <li id="R_3.032.007cd">na kārmukaṃ vikarṣantaṃ rāmaṃ paśyāmi saṃyuge</li>
    </ul>
    <ul id="R_3.032.008">
        <li id="R_3.032.008ab">hanyamānaṃ tu tat sainyaṃ paśyāmi śaravṛṣṭibhiḥ</li>
        <li id="R_3.032.008cd">indreṇaivottamaṃ sasyam āhataṃ tv aśmavṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_3.032.009">
        <li id="R_3.032.009ab">rakṣasāṃ bhīmavīryāṇāṃ sahasrāṇi caturdaśa</li>
        <li id="R_3.032.009cd">nihatāni śarais tīkṣṇais tenaikena padātinā</li>
    </ul>
    <ul id="R_3.032.010">
        <li id="R_3.032.010ab">ardhādhikamuhūrtena kharaś ca sahadūṣaṇaḥ</li>
        <li id="R_3.032.010cd">ṛṣīṇām abhayaṃ dattaṃ kṛtakṣemāś ca daṇḍakāḥ</li>
    </ul>
    <ul id="R_3.032.011">
        <li id="R_3.032.011ab">ekā kathaṃ cin muktāhaṃ paribhūya mahātmanā</li>
        <li id="R_3.032.011cd">strīvadhaṃ śaṅkamānena rāmeṇa viditātmanā</li>
    </ul>
    <ul id="R_3.032.012">
        <li id="R_3.032.012ab">bhrātā cāsya mahātejā guṇatas tulyavikramaḥ</li>
        <li id="R_3.032.012cd">anuraktaś ca bhaktaś ca lakṣmaṇo nāma vīryavān</li>
    </ul>
    <ul id="R_3.032.013">
        <li id="R_3.032.013ab">amarṣī durjayo jetā vikrānto buddhimān balī</li>
        <li id="R_3.032.013cd">rāmasya dakṣiṇe bāhur nityaṃ prāṇo bahiṣcaraḥ</li>
    </ul>
    <ul id="R_3.032.014">
        <li id="R_3.032.014ab">rāmasya tu viśālākṣī dharmapatnī yaśasvinī</li>
        <li id="R_3.032.014cd">sītā nāma varārohā vaidehī tanumadhyamā</li>
    </ul>
    <ul id="R_3.032.015">
        <li id="R_3.032.015ab">naiva devī na gandharvā na yakṣī na ca kiṃnarī</li>
        <li id="R_3.032.015cd">tathārūpā mayā nārī dṛṣṭapūrvā mahītale</li>
    </ul>
    <ul id="R_3.032.016">
        <li id="R_3.032.016ab">yasya sītā bhaved bhāryā yaṃ ca hṛṣṭā pariṣvajet</li>
        <li id="R_3.032.016cd">atijīvet sa sarveṣu lokeṣv api puraṃdarāt</li>
    </ul>
    <ul id="R_3.032.017">
        <li id="R_3.032.017ab">sā suśīlā vapuḥślāghyā rūpeṇāpratimā bhuvi</li>
        <li id="R_3.032.017cd">tavānurūpā bhāryā sā tvaṃ ca tasyās tathā patiḥ</li>
    </ul>
    <ul id="R_3.032.018">
        <li id="R_3.032.018ab">tāṃ tu vistīrṇajaghanāṃ pīnottuṅgapayodharām</li>
        <li id="R_3.032.018cd">bhāryārthe tu tavānetum udyatāhaṃ varānanām</li>
    </ul>
    <ul id="R_3.032.019">
        <li id="R_3.032.019ab">tāṃ tu dṛṣṭvādya vaidehīṃ pūrṇacandranibhānanām</li>
        <li id="R_3.032.019cd">manmathasya śarāṇāṃ ca tvaṃ vidheyo bhaviṣyasi</li>
    </ul>
    <ul id="R_3.032.020">
        <li id="R_3.032.020ab">yadi tasyām abhiprāyo bhāryārthe tava jāyate</li>
        <li id="R_3.032.020cd">śīghram uddhriyatāṃ pādo jayārtham iha dakṣiṇaḥ</li>
    </ul>
    <ul id="R_3.032.021">
        <li id="R_3.032.021ab">kuru priyaṃ tathā teṣāṃ rakṣasāṃ rākṣaseśvara</li>
        <li id="R_3.032.021cd">vadhāt tasya nṛśaṃsasya rāmasyāśramavāsinaḥ</li>
    </ul>
    <ul id="R_3.032.022">
        <li id="R_3.032.022ab">taṃ śarair niśitair hatvā lakṣmaṇaṃ ca mahāratham</li>
        <li id="R_3.032.022cd">hatanāthāṃ sukhaṃ sītāṃ yathāvad upabhokṣyase</li>
    </ul>
    <ul id="R_3.032.023">
        <li id="R_3.032.023ab">rocate yadi te vākyaṃ mamaitad rākṣaseśvara</li>
        <li id="R_3.032.023cd">kriyatāṃ nirviśaṅkena vacanaṃ mama rāghava</li>
    </ul>
    <ul id="R_3.032.024">
        <li id="R_3.032.024ab">
            <seg type="pāda" n="a">niśamya rāmeṇa śarair ajihmagair</seg>
            <seg type="pāda" n="b">hatāñ janasthānagatān niśācarān</seg>
        </li>
        <li id="R_3.032.024cd">
            <seg type="pāda" n="c">kharaṃ ca buddhvā nihataṃ ca dūṣaṇaṃ</seg>
            <seg type="pāda" n="d">tvam adya kṛtyaṃ pratipattum arhasi</seg>
        </li>
    </ul>
    <ul id="R_3.033.001">
        <li id="R_3.033.001ab">tataḥ śūrpaṇakhā vākyaṃ tac chrutvā romaharṣaṇam</li>
        <li id="R_3.033.001cd">sacivān abhyanujñāya kāryaṃ buddhvā jagāma ha</li>
    </ul>
    <ul id="R_3.033.002">
        <li id="R_3.033.002ab">tat kāryam anugamyātha yathāvad upalabhya ca</li>
        <li id="R_3.033.002cd">doṣāṇāṃ ca guṇānāṃ ca saṃpradhārya balābalam</li>
    </ul>
    <ul id="R_3.033.003">
        <li id="R_3.033.003ab">iti kartavyam ity eva kṛtvā niścayam ātmanaḥ</li>
        <li id="R_3.033.003cd">sthirabuddhis tato ramyāṃ yānaśālāṃ jagāma ha</li>
    </ul>
    <ul id="R_3.033.004">
        <li id="R_3.033.004ab">yānaśālāṃ tato gatvā pracchannaṃ rākṣasādhipaḥ</li>
        <li id="R_3.033.004cd">sūtaṃ saṃcodayām āsa rathaḥ saṃyujyatām iti</li>
    </ul>
    <ul id="R_3.033.005">
        <li id="R_3.033.005ab">evam uktaḥ kṣaṇenaiva sārathir laghuvikramaḥ</li>
        <li id="R_3.033.005cd">rathaṃ saṃyojayām āsa tasyābhimatam uttamam</li>
    </ul>
    <ul id="R_3.033.006">
        <li id="R_3.033.006ab">kāñcanaṃ ratham āsthāya kāmagaṃ ratnabhūṣitam</li>
        <li id="R_3.033.006cd">piśācavadanair yuktaṃ kharaiḥ kanakabhūṣaṇaiḥ</li>
    </ul>
    <ul id="R_3.033.007">
        <li id="R_3.033.007ab">meghapratimanādena sa tena dhanadānujaḥ</li>
        <li id="R_3.033.007cd">rākṣasādhipatiḥ śrīmān yayau nadanadīpatim</li>
    </ul>
    <ul id="R_3.033.008">
        <li id="R_3.033.008ab">sa śvetabālavyasanaḥ śvetacchatro daśānanaḥ</li>
        <li id="R_3.033.008cd">snigdhavaidūryasaṃkāśas taptakāñcanabhūṣaṇaḥ</li>
    </ul>
    <ul id="R_3.033.009">
        <li id="R_3.033.009ab">daśāsyo viṃśatibhujo darśanīya paricchadaḥ</li>
        <li id="R_3.033.009cd">tridaśārir munīndraghno daśaśīrṣa ivādrirāṭ</li>
    </ul>
    <ul id="R_3.033.010">
        <li id="R_3.033.010ab">kāmagaṃ ratham āsthāya śuśubhe rākṣasādhipaḥ</li>
        <li id="R_3.033.010cd">vidyunmaṇḍalavān meghaḥ sabalāka ivāmbare</li>
    </ul>
    <ul id="R_3.033.011">
        <li id="R_3.033.011ab">saśailaṃ sāgarānūpaṃ vīryavān avalokayan</li>
        <li id="R_3.033.011cd">nānāpuṣpaphalair vṛkṣair anukīrṇaṃ sahasraśaḥ</li>
    </ul>
    <ul id="R_3.033.012">
        <li id="R_3.033.012ab">śītamaṅgalatoyābhiḥ padminībhiḥ samantataḥ</li>
        <li id="R_3.033.012cd">viśālair āśramapadair vedimadbhiḥ samāvṛtam</li>
    </ul>
    <ul id="R_3.033.013">
        <li id="R_3.033.013ab">kadaly āḍhakisaṃbādhaṃ nālikeropaśobhitam</li>
        <li id="R_3.033.013cd">sālais tālais tamālaiś ca tarubhiś ca supuṣpitaiḥ</li>
    </ul>
    <ul id="R_3.033.014">
        <li id="R_3.033.014ab">atyantaniyatāhāraiḥ śobhitaṃ paramarṣibhiḥ</li>
        <li id="R_3.033.014cd">nāgaiḥ suparṇair gandharvaiḥ kiṃnaraiś ca sahasraśaḥ</li>
    </ul>
    <ul id="R_3.033.015">
        <li id="R_3.033.015ab">jitakāmaiś ca siddhaiś ca cāmaṇaiś copaśobhitam</li>
        <li id="R_3.033.015cd">ājair vaikhānasair māṣair vālakhilyair marīcipaiḥ</li>
    </ul>
    <ul id="R_3.033.016">
        <li id="R_3.033.016ab">divyābharaṇamālyābhir divyarūpābhir āvṛtam</li>
        <li id="R_3.033.016cd">krīḍā ratividhijñābhir apsarobhiḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_3.033.017">
        <li id="R_3.033.017ab">sevitaṃ devapatnībhiḥ śrīmatībhiḥ śriyā vṛtam</li>
        <li id="R_3.033.017cd">devadānavasaṃghaiś ca caritaṃ tv amṛtāśibhiḥ</li>
    </ul>
    <ul id="R_3.033.018">
        <li id="R_3.033.018ab">haṃsakrauñcaplavākīrṇaṃ sārasaiḥ saṃpraṇāditam</li>
        <li id="R_3.033.018cd">vaidūryaprastaraṃ ramyaṃ snigdhaṃ sāgaratejasā</li>
    </ul>
    <ul id="R_3.033.019">
        <li id="R_3.033.019ab">pāṇḍurāṇi viśālāni divyamālyayutāni ca</li>
        <li id="R_3.033.019cd">tūryagītābhijuṣṭāni vimānāni samantataḥ</li>
    </ul>
    <ul id="R_3.033.020">
        <li id="R_3.033.020ab">tapasā jitalokānāṃ kāmagāny abhisaṃpatan</li>
        <li id="R_3.033.020cd">gandharvāpsarasaś caiva dadarśa dhanadānujaḥ</li>
    </ul>
    <ul id="R_3.033.021">
        <li id="R_3.033.021ab">niryāsarasamūlānāṃ candanānāṃ sahasraśaḥ</li>
        <li id="R_3.033.021cd">vanāni paśyan saumyāni ghrāṇatṛptikarāṇi ca</li>
    </ul>
    <ul id="R_3.033.022">
        <li id="R_3.033.022ab">agarūṇāṃ ca mukhyānāṃ vanāny upavanāni ca</li>
        <li id="R_3.033.022cd">takkolānāṃ ca jātyānāṃ phalānāṃ ca sugandhinām</li>
    </ul>
    <ul id="R_3.033.023">
        <li id="R_3.033.023ab">puṣpāṇi ca tamālasya gulmāni maricasya ca</li>
        <li id="R_3.033.023cd">muktānāṃ ca samūhāni śuṣyamāṇāni tīrataḥ</li>
    </ul>
    <ul id="R_3.033.024">
        <li id="R_3.033.024ab">śaṅkhānāṃ prastaraṃ caiva pravālanicayaṃ tathā</li>
        <li id="R_3.033.024cd">kāñcanāni ca śailāni rājatāni ca sarvaśaḥ</li>
    </ul>
    <ul id="R_3.033.025">
        <li id="R_3.033.025ab">prasravāṇi manojñāni prasannāni hradāni ca</li>
        <li id="R_3.033.025cd">dhanadhānyopapannāni strīratnair āvṛtāni ca</li>
    </ul>
    <ul id="R_3.033.026">
        <li id="R_3.033.026ab">hastyaśvarathagāḍhāni nagarāṇy avalokayan</li>
        <li id="R_3.033.026cd">taṃ samaṃ sarvataḥ snigdhaṃ mṛdusaṃsparśamārutam</li>
    </ul>
    <ul id="R_3.033.027">
        <li id="R_3.033.027ab">anūpaṃ sindhurājasya dadarśa tridivopamam</li>
        <li id="R_3.033.027cd">tatrāpaśyat sa meghābhaṃ nyagrodham ṛṣibhir vṛtam</li>
    </ul>
    <ul id="R_3.033.028">
        <li id="R_3.033.028ab">samantād yasya tāḥ śākhāḥ śatayojanam āyatāḥ</li>
        <li id="R_3.033.028cd">yasya hastinam ādāya mahākāyaṃ ca kaccapam</li>
        <li id="R_3.033.028ef">bhakṣārthaṃ garuḍaḥ śākhām ājagāma mahābalaḥ</li>
    </ul>
    <ul id="R_3.033.029">
        <li id="R_3.033.029ab">tasya tāṃ sahasā śākhāṃ bhāreṇa patagottamaḥ</li>
        <li id="R_3.033.029cd">suparṇaḥ parṇabahulāṃ babhañjātha mahābalaḥ</li>
    </ul>
    <ul id="R_3.033.030">
        <li id="R_3.033.030ab">tatra vaikhānasā māṣā vālakhilyā marīcipāḥ</li>
        <li id="R_3.033.030cd">ajā babhūvur dhūmrāś ca saṃgatāḥ paramarṣayaḥ</li>
    </ul>
    <ul id="R_3.033.031">
        <li id="R_3.033.031ab">teṣāṃ dayārthaṃ garuḍas tāṃ śākhāṃ śatayojanām</li>
        <li id="R_3.033.031cd">jagāmādāya vegena tau cobhau gajakacchapau</li>
    </ul>
    <ul id="R_3.033.032">
        <li id="R_3.033.032ab">ekapādena dharmātmā bhakṣayitvā tad āmiṣam</li>
        <li id="R_3.033.032cd">niṣādaviṣayaṃ hatvā śākhayā patagottamaḥ</li>
        <li id="R_3.033.032ef">praharṣam atulaṃ lebhe mokṣayitvā mahāmunīn</li>
    </ul>
    <ul id="R_3.033.033">
        <li id="R_3.033.033ab">sa tenaiva praharṣeṇa dviguṇīkṛtavikramaḥ</li>
        <li id="R_3.033.033cd">amṛtānayanārthaṃ vai cakāra matimān matim</li>
    </ul>
    <ul id="R_3.033.034">
        <li id="R_3.033.034ab">ayojālāni nirmathya bhittvā ratnagṛhaṃ varam</li>
        <li id="R_3.033.034cd">mahendrabhavanād guptam ājahārāmṛtaṃ tataḥ</li>
    </ul>
    <ul id="R_3.033.035">
        <li id="R_3.033.035ab">taṃ maharṣigaṇair juṣṭaṃ suparṇakṛtalakṣaṇam</li>
        <li id="R_3.033.035cd">nāmnā subhadraṃ nyagrodhaṃ dadarśa dhanadānujaḥ</li>
    </ul>
    <ul id="R_3.033.036">
        <li id="R_3.033.036ab">taṃ tu gatvā paraṃ pāraṃ samudrasya nadīpateḥ</li>
        <li id="R_3.033.036cd">dadarśāśramam ekānte puṇye ramye vanāntare</li>
    </ul>
    <ul id="R_3.033.037">
        <li id="R_3.033.037ab">tatra kṛṣṇājinadharaṃ jaṭāvalkaladhāriṇam</li>
        <li id="R_3.033.037cd">dadarśa niyatāhāraṃ mārīcaṃ nāma rākṣasaṃ</li>
    </ul>
    <ul id="R_3.033.038">
        <li id="R_3.033.038ab">sa rāvaṇaḥ samāgamya vidhivat tena rakṣasā</li>
        <li id="R_3.033.038cd">tataḥ paścād idaṃ vākyam abravīd vākyakovidaḥ</li>
    </ul>
    <ul id="R_3.034.001">
        <li id="R_3.034.001ab">mārīca śrūyatāṃ tāta vacanaṃ mama bhāṣataḥ</li>
        <li id="R_3.034.001cd">ārto 'smi mama cārtasya bhavān hi paramā gatiḥ</li>
    </ul>
    <ul id="R_3.034.002">
        <li id="R_3.034.002ab">jānīṣe tvaṃ janasthānaṃ bhrātā yatra kharo mama</li>
        <li id="R_3.034.002cd">dūṣaṇaś ca mahābāhuḥ svasā śūrpaṇakhā ca me</li>
    </ul>
    <ul id="R_3.034.003">
        <li id="R_3.034.003ab">triśirāś ca mahātejā rākṣasaḥ piśitāśanaḥ</li>
        <li id="R_3.034.003cd">anye ca bahavaḥ śūrā labdhalakṣā niśācarāḥ</li>
    </ul>
    <ul id="R_3.034.004">
        <li id="R_3.034.004ab">vasanti manniyogena adhivāsaṃ ca rākṣasaḥ</li>
        <li id="R_3.034.004cd">bādhamānā mahāraṇye munīn ye dharmacāriṇaḥ</li>
    </ul>
    <ul id="R_3.034.005">
        <li id="R_3.034.005ab">caturdaśa sahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_3.034.005cd">śūrāṇāṃ labdhalakṣāṇāṃ kharacittānuvartinām</li>
    </ul>
    <ul id="R_3.034.006">
        <li id="R_3.034.006ab">te tv idānīṃ janasthāne vasamānā mahābalāḥ</li>
        <li id="R_3.034.006cd">saṃgatāḥ param āyattā rāmeṇa saha saṃyuge</li>
    </ul>
    <ul id="R_3.034.007">
        <li id="R_3.034.007ab">tena saṃjātaroṣeṇa rāmeṇa raṇamūrdhani</li>
        <li id="R_3.034.007cd">anuktvā paruṣaṃ kiṃ cic charair vyāpāritaṃ dhanuḥ</li>
    </ul>
    <ul id="R_3.034.008">
        <li id="R_3.034.008ab">caturdaśa sahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_3.034.008cd">nihatāni śarais tīkṣṇair mānuṣeṇa padātinā</li>
    </ul>
    <ul id="R_3.034.009">
        <li id="R_3.034.009ab">kharaś ca nihataḥ saṃkhye dūṣaṇaś ca nipātitaḥ</li>
        <li id="R_3.034.009cd">hatvā triśirasaṃ cāpi nirbhayā daṇḍakāḥ kṛtāḥ</li>
    </ul>
    <ul id="R_3.034.010">
        <li id="R_3.034.010ab">pitrā nirastaḥ kruddhena sabhāryaḥ kṣīṇajīvitaḥ</li>
        <li id="R_3.034.010cd">sa hantā tasya sainyasya rāmaḥ kṣatriyapāṃsanaḥ</li>
    </ul>
    <ul id="R_3.034.011">
        <li id="R_3.034.011ab">aśīlaḥ karkaśas tīkṣṇo mūrkho lubdho 'jitendriyaḥ</li>
        <li id="R_3.034.011cd">tyaktadharmas tv adharmātmā bhūtānām ahite rataḥ</li>
    </ul>
    <ul id="R_3.034.012">
        <li id="R_3.034.012ab">yena vairaṃ vināraṇye sattvam āśritya kevalam</li>
        <li id="R_3.034.012cd">karṇanāsāpahāreṇa bhaginī me virūpitā</li>
    </ul>
    <ul id="R_3.034.013">
        <li id="R_3.034.013ab">tasya bhāryāṃ janasthānāt sītāṃ surasutopamām</li>
        <li id="R_3.034.013cd">ānayiṣyāmi vikramya sahāyas tatra me bhava</li>
    </ul>
    <ul id="R_3.034.014">
        <li id="R_3.034.014ab">tvayā hy ahaṃ sahāyena pārśvasthena mahābala</li>
        <li id="R_3.034.014cd">bhrātṛbhiś ca surān yuddhe samagrān nābhicintaye</li>
    </ul>
    <ul id="R_3.034.015">
        <li id="R_3.034.015ab">tat sahāyo bhava tvaṃ me samartho hy asi rākṣasa</li>
        <li id="R_3.034.015cd">vīrye yuddhe ca darpe ca na hy asti sadṛśas tava</li>
    </ul>
    <ul id="R_3.034.016">
        <li id="R_3.034.016ab">etadartham ahaṃ prāptas tvatsamīpaṃ niśācara</li>
        <li id="R_3.034.016cd">śṛṇu tat karma sāhāyye yat kāryaṃ vacanān mama</li>
    </ul>
    <ul id="R_3.034.017">
        <li id="R_3.034.017ab">sauvarṇas tvaṃ mṛgo bhūtvā citro rajatabindubhiḥ</li>
        <li id="R_3.034.017cd">āśrame tasya rāmasya sītāyāḥ pramukhe cara</li>
    </ul>
    <ul id="R_3.034.018">
        <li id="R_3.034.018ab">tvāṃ tu niḥsaṃśayaṃ sītā dṛṣṭvā tu mṛgarūpiṇam</li>
        <li id="R_3.034.018cd">gṛhyatām iti bhartāraṃ lakṣmaṇaṃ cābhidhāsyati</li>
    </ul>
    <ul id="R_3.034.019">
        <li id="R_3.034.019ab">tatas tayor apāye tu śūnye sītāṃ yathāsukham</li>
        <li id="R_3.034.019cd">nirābādho hariṣyāmi rāhuś candraprabhām iva</li>
    </ul>
    <ul id="R_3.034.020">
        <li id="R_3.034.020ab">tataḥ paścāt sukhaṃ rāme bhāryāharaṇakarśite</li>
        <li id="R_3.034.020cd">visrabdhaṃ prahariṣyāmi kṛtārthenāntarātmanā</li>
    </ul>
    <ul id="R_3.034.021">
        <li id="R_3.034.021ab">tasya rāmakathāṃ śrutvā mārīcasya mahātmanaḥ</li>
        <li id="R_3.034.021cd">śuṣkaṃ samabhavad vaktraṃ paritrasto babhūva ca</li>
    </ul>
    <ul id="R_3.034.022">
        <li id="R_3.034.022ab">
            <seg type="pāda" n="a">sa rāvaṇaṃ trastaviṣaṇṇacetā</seg>
            <seg type="pāda" n="b">mahāvane rāmaparākramajñaḥ</seg>
        </li>
        <li id="R_3.034.022cd">
            <seg type="pāda" n="c">kṛtāñjalis tattvam uvāca vākyaṃ</seg>
            <seg type="pāda" n="d">hitaṃ ca tasmai hitam ātmanaś ca</seg>
        </li>
    </ul>
    <ul id="R_3.035.001">
        <li id="R_3.035.001ab">tac chrutvā rākṣasendrasya vākyaṃ vākyaviśāradaḥ</li>
        <li id="R_3.035.001cd">pratyuvāca mahāprājño mārīco rākṣaseśvaram</li>
    </ul>
    <ul id="R_3.035.002">
        <li id="R_3.035.002ab">sulabhāḥ puruṣā rājan satataṃ priyavādinaḥ</li>
        <li id="R_3.035.002cd">apriyasya ca pathyasya vaktā śrotā ca durlabhaḥ</li>
    </ul>
    <ul id="R_3.035.003">
        <li id="R_3.035.003ab">na nūnaṃ budhyase rāmaṃ mahāvīryaṃ guṇonnatam</li>
        <li id="R_3.035.003cd">ayuktacāraś capalo mahendravaruṇopamam</li>
    </ul>
    <ul id="R_3.035.004">
        <li id="R_3.035.004ab">api svasti bhavet tāta sarveṣāṃ bhuvi rakṣasām</li>
        <li id="R_3.035.004cd">api rāmo na saṃkruddhaḥ kuryāl lokam arākṣasaṃ</li>
    </ul>
    <ul id="R_3.035.005">
        <li id="R_3.035.005ab">api te jīvitāntāya notpannā janakātmajā</li>
        <li id="R_3.035.005cd">api sītā nimittaṃ ca na bhaved vyasanaṃ mahat</li>
    </ul>
    <ul id="R_3.035.006">
        <li id="R_3.035.006ab">api tvām īśvaraṃ prāpya kāmavṛttaṃ niraṅkuśam</li>
        <li id="R_3.035.006cd">na vinaśyet purī laṅkā tvayā saha sarākṣasā</li>
    </ul>
    <ul id="R_3.035.007">
        <li id="R_3.035.007ab">tvadvidhaḥ kāmavṛtto hi duḥśīlaḥ pāpamantritaḥ</li>
        <li id="R_3.035.007cd">ātmānaṃ svajanaṃ rāṣṭraṃ sa rājā hanti durmatiḥ</li>
    </ul>
    <ul id="R_3.035.008">
        <li id="R_3.035.008ab">na ca pitrā parityakto nāmaryādaḥ kathaṃ cana</li>
        <li id="R_3.035.008cd">na lubdho na ca duḥśīlo na ca kṣatriyapāṃsanaḥ</li>
    </ul>
    <ul id="R_3.035.009">
        <li id="R_3.035.009ab">na ca dharmaguṇair hīnaiḥ kausalyānandavardhanaḥ</li>
        <li id="R_3.035.009cd">na ca tīkṣṇo hi bhūtānāṃ sarveṣāṃ ca hite rataḥ</li>
    </ul>
    <ul id="R_3.035.010">
        <li id="R_3.035.010ab">vañcitaṃ pitaraṃ dṛṣṭvā kaikeyyā satyavādinam</li>
        <li id="R_3.035.010cd">kariṣyāmīti dharmātmā tataḥ pravrajito vanam</li>
    </ul>
    <ul id="R_3.035.011">
        <li id="R_3.035.011ab">kaikeyyāḥ priyakāmārthaṃ pitur daśarathasya ca</li>
        <li id="R_3.035.011cd">hitvā rājyaṃ ca bhogāṃś ca praviṣṭo daṇḍakāvanam</li>
    </ul>
    <ul id="R_3.035.012">
        <li id="R_3.035.012ab">na rāmaḥ karkaśas tāta nāvidvān nājitendriyaḥ</li>
        <li id="R_3.035.012cd">anṛtaṃ na śrutaṃ caiva naiva tvaṃ vaktum arhasi</li>
    </ul>
    <ul id="R_3.035.013">
        <li id="R_3.035.013ab">rāmo vigrahavān dharmaḥ sādhuḥ satyaparākramaḥ</li>
        <li id="R_3.035.013cd">rājā sarvasya lokasya devānām iva vāsavaḥ</li>
    </ul>
    <ul id="R_3.035.014">
        <li id="R_3.035.014ab">kathaṃ tvaṃ tasya vaidehīṃ rakṣitāṃ svena tejasā</li>
        <li id="R_3.035.014cd">icchasi prasabhaṃ hartuṃ prabhām iva vivasvataḥ</li>
    </ul>
    <ul id="R_3.035.015">
        <li id="R_3.035.015ab">śarārciṣam anādhṛṣyaṃ cāpakhaḍgendhanaṃ raṇe</li>
        <li id="R_3.035.015cd">rāmāgniṃ sahasā dīptaṃ na praveṣṭuṃ tvam arhasi</li>
    </ul>
    <ul id="R_3.035.016">
        <li id="R_3.035.016ab">dhanurvyāditadīptāsyaṃ śarārciṣam amarṣaṇam</li>
        <li id="R_3.035.016cd">cāpabāṇadharaṃ vīraṃ śatrusenāpahāriṇam</li>
    </ul>
    <ul id="R_3.035.017">
        <li id="R_3.035.017ab">rājyaṃ sukhaṃ ca saṃtyajya jīvitaṃ ceṣṭam ātmanaḥ</li>
        <li id="R_3.035.017cd">nātyāsādayituṃ tāta rāmāntakam ihārhasi</li>
    </ul>
    <ul id="R_3.035.018">
        <li id="R_3.035.018ab">aprameyaṃ hi tat tejo yasya sā janakātmajā</li>
        <li id="R_3.035.018cd">na tvaṃ samarthas tāṃ hartuṃ rāmacāpāśrayāṃ vane</li>
    </ul>
    <ul id="R_3.035.019">
        <li id="R_3.035.019ab">prāṇebhyo 'pi priyatarā bhāryā nityam anuvratā</li>
        <li id="R_3.035.019cd">dīptasyeva hutāśasya śikhā sītā sumadhyamā</li>
    </ul>
    <ul id="R_3.035.020">
        <li id="R_3.035.020ab">kim udyamaṃ vyartham imaṃ kṛtvā te rākṣasādhipa</li>
        <li id="R_3.035.020cd">dṛṣṭaś cet tvaṃ raṇe tena tad antaṃ tava jīvitam</li>
        <li id="R_3.035.020ef">jīvitaṃ ca sukhaṃ caiva rājyaṃ caiva sudurlabham</li>
    </ul>
    <ul id="R_3.035.021">
        <li id="R_3.035.021ab">sa sarvaiḥ sacivaiḥ sārdhaṃ vibhīṣaṇapuraskṛtaiḥ</li>
        <li id="R_3.035.021cd">mantrayitvā tu dharmiṣṭhaiḥ kṛtvā niścayam ātmanaḥ</li>
    </ul>
    <ul id="R_3.035.022">
        <li id="R_3.035.022ab">doṣāṇāṃ ca guṇānāṃ ca saṃpradhārya balābalam</li>
        <li id="R_3.035.022cd">ātmanaś ca balaṃ jñātvā rāghavasya ca tattvataḥ</li>
        <li id="R_3.035.022ef">hitaṃ hi tava niścitya kṣamaṃ tvaṃ kartum arhasi</li>
    </ul>
    <ul id="R_3.035.023">
        <li id="R_3.035.023ab">
            <seg type="pāda" n="a">ahaṃ tu manye tava na kṣamaṃ raṇe</seg>
            <seg type="pāda" n="b">samāgamaṃ kosalarājasūnunā</seg>
        </li>
        <li id="R_3.035.023cd">
            <seg type="pāda" n="c">idaṃ hi bhūyaḥ śṛṇu vākyam uttamaṃ</seg>
            <seg type="pāda" n="d">kṣamaṃ ca yuktaṃ ca niśācarādhipa</seg>
        </li>
    </ul>
    <ul id="R_3.036.001">
        <li id="R_3.036.001ab">kadā cid apy ahaṃ vīryāt paryaṭan pṛthivīm imām</li>
        <li id="R_3.036.001cd">balaṃ nāgasahasrasya dhārayan parvatopamaḥ</li>
    </ul>
    <ul id="R_3.036.002">
        <li id="R_3.036.002ab">nīlajīmūtasaṃkāśas taptakāñcanakuṇḍalaḥ</li>
        <li id="R_3.036.002cd">bhayaṃ lokasya janayan kirīṭī parighāyudhaḥ</li>
        <li id="R_3.036.002ef">vyacaraṃ daṇḍakāraṇyam ṛṣimāṃsāni bhakṣayan</li>
    </ul>
    <ul id="R_3.036.003">
        <li id="R_3.036.003ab">viśvāmitro 'tha dharmātmā madvitrasto mahāmuniḥ</li>
        <li id="R_3.036.003cd">svayaṃ gatvā daśarathaṃ narendram idam abravīt</li>
    </ul>
    <ul id="R_3.036.004">
        <li id="R_3.036.004ab">ayaṃ rakṣatu māṃ rāmaḥ parvakāle samāhitaḥ</li>
        <li id="R_3.036.004cd">mārīcān me bhayaṃ ghoraṃ samutpannaṃ nareśvara</li>
    </ul>
    <ul id="R_3.036.005">
        <li id="R_3.036.005ab">ity evam ukto dharmātmā rājā daśarathas tadā</li>
        <li id="R_3.036.005cd">pratyuvāca mahābhāgaṃ viśvāmitraṃ mahāmunim</li>
    </ul>
    <ul id="R_3.036.006">
        <li id="R_3.036.006ab">ūna ṣoḍaśa varṣo 'yam akṛtāstraś ca rāghavaḥ</li>
        <li id="R_3.036.006cd">kāmaṃ tu mama yat sainyaṃ mayā saha gamiṣyati</li>
        <li id="R_3.036.006ef">badhiṣyāmi muniśreṣṭha śatruṃ tava yathepsitam</li>
    </ul>
    <ul id="R_3.036.007">
        <li id="R_3.036.007ab">ity evam uktaḥ sa munī rājānaṃ punar abravīt</li>
        <li id="R_3.036.007cd">rāmān nānyad balaṃ loke paryāptaṃ tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_3.036.008">
        <li id="R_3.036.008ab">bālo 'py eṣa mahātejāḥ samarthas tasya nigrahe</li>
        <li id="R_3.036.008cd">gamiṣye rāmam ādāya svasti te 'stu paraṃtapaḥ</li>
    </ul>
    <ul id="R_3.036.009">
        <li id="R_3.036.009ab">ity evam uktvā sa munis tam ādāya nṛpātmajam</li>
        <li id="R_3.036.009cd">jagāma paramaprīto viśvāmitraḥ svam āśramam</li>
    </ul>
    <ul id="R_3.036.010">
        <li id="R_3.036.010ab">taṃ tadā daṇḍakāraṇye yajñam uddiśya dīkṣitam</li>
        <li id="R_3.036.010cd">babhūvāvasthito rāmaś citraṃ visphārayan dhanuḥ</li>
    </ul>
    <ul id="R_3.036.011">
        <li id="R_3.036.011ab">ajātavyañjanaḥ śrīmān bālaḥ śyāmaḥ śubhekṣaṇaḥ</li>
        <li id="R_3.036.011cd">ekavastradharo dhanvī śikhī kanakamālayā</li>
    </ul>
    <ul id="R_3.036.012">
        <li id="R_3.036.012ab">śobhayan daṇḍakāraṇyaṃ dīptena svena tejasā</li>
        <li id="R_3.036.012cd">adṛśyata tadā rāmo bālacandra ivoditaḥ</li>
    </ul>
    <ul id="R_3.036.013">
        <li id="R_3.036.013ab">tato 'haṃ meghasaṃkāśas taptakāñcanakuṇḍalaḥ</li>
        <li id="R_3.036.013cd">balī dattavaro darpād ājagāma tadāśramam</li>
    </ul>
    <ul id="R_3.036.014">
        <li id="R_3.036.014ab">tena dṛṣṭaḥ praviṣṭo 'haṃ sahasaivodyatāyudhaḥ</li>
        <li id="R_3.036.014cd">māṃ tu dṛṣṭvā dhanuḥ sajyam asaṃbhrāntaś cakāra ha</li>
    </ul>
    <ul id="R_3.036.015">
        <li id="R_3.036.015ab">avajānann ahaṃ mohād bālo 'yam iti rāghavam</li>
        <li id="R_3.036.015cd">viśvāmitrasya tāṃ vedim adhyadhāvaṃ kṛtatvaraḥ</li>
    </ul>
    <ul id="R_3.036.016">
        <li id="R_3.036.016ab">tena muktas tato bāṇaḥ śitaḥ śatrunibarhaṇaḥ</li>
        <li id="R_3.036.016cd">tenāhaṃ tāḍitaḥ kṣiptaḥ samudre śatayojane</li>
    </ul>
    <ul id="R_3.036.017">
        <li id="R_3.036.017ab">rāmasya śaravegena nirasto bhrāntacetanaḥ</li>
        <li id="R_3.036.017cd">pātito 'haṃ tadā tena gambhīre sāgarāmbhasi</li>
        <li id="R_3.036.017ef">prāpya saṃjñāṃ cirāt tāta laṅkāṃ prati gataḥ purīm</li>
    </ul>
    <ul id="R_3.036.018">
        <li id="R_3.036.018ab">evam asmi tadā muktaḥ sahāyās te nipātitāḥ</li>
        <li id="R_3.036.018cd">akṛtāstreṇa rāmeṇa bālenākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_3.036.019">
        <li id="R_3.036.019ab">tan mayā vāryamāṇas tvaṃ yadi rāmeṇa vigraham</li>
        <li id="R_3.036.019cd">kariṣyasy āpadaṃ ghorāṃ kṣipraṃ prāpya naśiṣyasi</li>
    </ul>
    <ul id="R_3.036.020">
        <li id="R_3.036.020ab">krīḍā ratividhijñānāṃ samājotsavaśālinām</li>
        <li id="R_3.036.020cd">rakṣasāṃ caiva saṃtāpam anarthaṃ cāhariṣyasi</li>
    </ul>
    <ul id="R_3.036.021">
        <li id="R_3.036.021ab">harmyaprāsādasaṃbādhāṃ nānāratnavibhūṣitām</li>
        <li id="R_3.036.021cd">drakṣyasi tvaṃ purīṃ laṅkāṃ vinaṣṭāṃ maithilīkṛte</li>
    </ul>
    <ul id="R_3.036.022">
        <li id="R_3.036.022ab">akurvanto 'pi pāpāni śucayaḥ pāpasaṃśrayāt</li>
        <li id="R_3.036.022cd">parapāpair vinaśyanti matsyā nāgahrade yathā</li>
    </ul>
    <ul id="R_3.036.023">
        <li id="R_3.036.023ab">divyacandanadigdhāṅgān divyābharaṇabhūṣitān</li>
        <li id="R_3.036.023cd">drakṣyasy abhihatān bhūmau tava doṣāt tu rākṣasān</li>
    </ul>
    <ul id="R_3.036.024">
        <li id="R_3.036.024ab">hṛtadārān sadārāṃś ca daśavidravato diśaḥ</li>
        <li id="R_3.036.024cd">hataśeṣān aśaraṇān drakṣyasi tvaṃ niśācarān</li>
    </ul>
    <ul id="R_3.036.025">
        <li id="R_3.036.025ab">śarajālaparikṣiptām agnijvālāsamāvṛtām</li>
        <li id="R_3.036.025cd">pradagdhabhavanāṃ laṅkāṃ drakṣyasi tvam asaṃśayam</li>
    </ul>
    <ul id="R_3.036.026">
        <li id="R_3.036.026ab">pramadānāṃ sahasrāṇi tava rājan parigrahaḥ</li>
        <li id="R_3.036.026cd">bhava svadāranirataḥ svakulaṃ rakṣarākṣasa</li>
    </ul>
    <ul id="R_3.036.027">
        <li id="R_3.036.027ab">mānaṃ vṛddhiṃ ca rājyaṃ ca jīvitaṃ ceṣṭam ātmanaḥ</li>
        <li id="R_3.036.027cd">yadīcchasi ciraṃ bhoktuṃ mā kṛthā rāma vipriyam</li>
    </ul>
    <ul id="R_3.036.028">
        <li id="R_3.036.028ab">
            <seg type="pāda" n="a">nivāryamāṇaḥ suhṛdā mayā bhṛśaṃ</seg>
            <seg type="pāda" n="b">prasahya sītāṃ yadi dharṣayiṣyasi</seg>
        </li>
        <li id="R_3.036.028cd">
            <seg type="pāda" n="c">gamiṣyasi kṣīṇabalaḥ sabāndhavo</seg>
            <seg type="pāda" n="d">yamakṣayaṃ rāmaśarāttajīvitaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.037.001">
        <li id="R_3.037.001ab">evam asmi tadā muktaḥ kathaṃ cit tena saṃyuge</li>
        <li id="R_3.037.001cd">idānīm api yad vṛttaṃ tac chṛṇuṣva yad uttaram</li>
    </ul>
    <ul id="R_3.037.002">
        <li id="R_3.037.002ab">rākṣasābhyām ahaṃ dvābhyām anirviṇṇas tathā kṛtaḥ</li>
        <li id="R_3.037.002cd">sahito mṛgarūpābhyāṃ praviṣṭo daṇḍakāvanam</li>
    </ul>
    <ul id="R_3.037.003">
        <li id="R_3.037.003ab">dīptajihvo mahākāyas tīkṣṇaśṛṇgo mahābalaḥ</li>
        <li id="R_3.037.003cd">vyacaran daṇḍakāraṇyaṃ māṃsabhakṣo mahāmṛgaḥ</li>
    </ul>
    <ul id="R_3.037.004">
        <li id="R_3.037.004ab">agnihotreṣu tīrtheṣu caityavṛkṣeṣu rāvaṇa</li>
        <li id="R_3.037.004cd">atyantaghoro vyacaraṃs tāpasāṃs tān pradharṣayan</li>
    </ul>
    <ul id="R_3.037.005">
        <li id="R_3.037.005ab">nihatya daṇḍakāraṇye tāpasān dharmacāriṇaḥ</li>
        <li id="R_3.037.005cd">rudhirāṇi pibaṃs teṣāṃ tathā māṃsāni bhakṣayan</li>
    </ul>
    <ul id="R_3.037.006">
        <li id="R_3.037.006ab">ṛṣimāṃsāśanaḥ krūras trāsayan vanagocarān</li>
        <li id="R_3.037.006cd">tadā rudhiramatto 'haṃ vyacaraṃ daṇḍakāvanam</li>
    </ul>
    <ul id="R_3.037.007">
        <li id="R_3.037.007ab">tadāhaṃ daṇḍakāraṇye vicaran dharmadūṣakaḥ</li>
        <li id="R_3.037.007cd">āsādayaṃ tadā rāmaṃ tāpasaṃ dharmam āśritam</li>
    </ul>
    <ul id="R_3.037.008">
        <li id="R_3.037.008ab">vaidehīṃ ca mahābhāgāṃ lakṣmaṇaṃ ca mahāratham</li>
        <li id="R_3.037.008cd">tāpasaṃ niyatāhāraṃ sarvabhūtahite ratam</li>
    </ul>
    <ul id="R_3.037.009">
        <li id="R_3.037.009ab">so 'haṃ vanagataṃ rāmaṃ paribhūya mahābalam</li>
        <li id="R_3.037.009cd">tāpaso 'yam iti jñātvā pūrvavairam anusmaran</li>
    </ul>
    <ul id="R_3.037.010">
        <li id="R_3.037.010ab">abhyadhāvaṃ susaṃkruddhas tīkṣṇaśṛṅgo mṛgākṛtiḥ</li>
        <li id="R_3.037.010cd">jighāṃsur akṛtaprajñas taṃ prahāram anusmaran</li>
    </ul>
    <ul id="R_3.037.011">
        <li id="R_3.037.011ab">tena muktās trayo bāṇāḥ śitāḥ śatrunibarhaṇāḥ</li>
        <li id="R_3.037.011cd">vikṛṣya balavac cāpaṃ suparṇānilatulyagāḥ</li>
    </ul>
    <ul id="R_3.037.012">
        <li id="R_3.037.012ab">te bāṇā vajrasaṃkāśāḥ sughorā raktabhojanāḥ</li>
        <li id="R_3.037.012cd">ājagmuḥ sahitāḥ sarve trayaḥ saṃnataparvaṇaḥ</li>
    </ul>
    <ul id="R_3.037.013">
        <li id="R_3.037.013ab">parākramajño rāmasya śaṭho dṛṣṭabhayaḥ purā</li>
        <li id="R_3.037.013cd">samutkrāntas tato muktas tāv ubhau rākṣasau hatau</li>
    </ul>
    <ul id="R_3.037.014">
        <li id="R_3.037.014ab">śareṇa mukto rāmasya kathaṃ cit prāpya jīvitam</li>
        <li id="R_3.037.014cd">iha pravrājito yuktas tāpaso 'haṃ samāhitaḥ</li>
    </ul>
    <ul id="R_3.037.015">
        <li id="R_3.037.015ab">vṛkṣe vṛkṣe hi paśyāmi cīrakṛṣṇājināmbaram</li>
        <li id="R_3.037.015cd">gṛhītadhanuṣaṃ rāmaṃ pāśahastam ivāntakam</li>
    </ul>
    <ul id="R_3.037.016">
        <li id="R_3.037.016ab">api rāmasahasrāṇi bhītaḥ paśyāmi rāvaṇa</li>
        <li id="R_3.037.016cd">rāmabhūtam idaṃ sarvam araṇyaṃ pratibhāti me</li>
    </ul>
    <ul id="R_3.037.017">
        <li id="R_3.037.017ab">rāmam eva hi paśyāmi rahite rākṣaseśvara</li>
        <li id="R_3.037.017cd">dṛṣṭvā svapnagataṃ rāmam udbhramāmi vicetanaḥ</li>
    </ul>
    <ul id="R_3.037.018">
        <li id="R_3.037.018ab">rakārādīni nāmāni rāmatrastasya rāvaṇa</li>
        <li id="R_3.037.018cd">ratnāni ca rathāś caiva trāsaṃ saṃjanayanti me</li>
    </ul>
    <ul id="R_3.037.019">
        <li id="R_3.037.019ab">ahaṃ tasya prabhāvajño na yuddhaṃ tena te kṣamam</li>
        <li id="R_3.037.019cd">raṇe rāmeṇa yudhyasva kṣamāṃ vā kuru rākṣasa</li>
        <li id="R_3.037.019ef">na te rāmakathā kāryā yadi māṃ draṣṭum icchasi</li>
    </ul>
    <ul id="R_3.037.020">
        <li id="R_3.037.020ab">
            <seg type="pāda" n="a">idaṃ vaco bandhuhitārthinā mayā</seg>
            <seg type="pāda" n="b">yathocyamānaṃ yadi nābhipatsyase</seg>
        </li>
        <li id="R_3.037.020cd">
            <seg type="pāda" n="c">sabāndhavas tyakṣyasi jīvitaṃ raṇe</seg>
            <seg type="pāda" n="d">hato 'dya rāmeṇa śarair ajihmagaiḥ</seg>
        </li>
    </ul>
    <ul id="R_3.038.001">
        <li id="R_3.038.001ab">mārīcena tu tad vākyaṃ kṣamaṃ yuktaṃ ca rāvaṇaḥ</li>
        <li id="R_3.038.001cd">ukto na pratijagrāha martukāma ivauṣadham</li>
    </ul>
    <ul id="R_3.038.002">
        <li id="R_3.038.002ab">taṃ pathyahitavaktāraṃ mārīcaṃ rākṣasādhipaḥ</li>
        <li id="R_3.038.002cd">abravīt paruṣaṃ vākyam ayuktaṃ kālacoditaḥ</li>
    </ul>
    <ul id="R_3.038.003">
        <li id="R_3.038.003ab">yat kilaitad ayuktārthaṃ mārīca mayi kathyate</li>
        <li id="R_3.038.003cd">vākyaṃ niṣphalam atyarthaṃ bījam uptam ivoṣare</li>
    </ul>
    <ul id="R_3.038.004">
        <li id="R_3.038.004ab">tvadvākyair na tu māṃ śakyaṃ bhettuṃ rāmasya saṃyuge</li>
        <li id="R_3.038.004cd">pāpaśīlasya mūrkhasya mānuṣasya viśeṣataḥ</li>
    </ul>
    <ul id="R_3.038.005">
        <li id="R_3.038.005ab">yas tyaktvā suhṛdo rājyaṃ mātaraṃ pitaraṃ tathā</li>
        <li id="R_3.038.005cd">strīvākyaṃ prākṛtaṃ śrutvā vanam ekapade gataḥ</li>
    </ul>
    <ul id="R_3.038.006">
        <li id="R_3.038.006ab">avaśyaṃ tu mayā tasya saṃyuge kharaghātinaḥ</li>
        <li id="R_3.038.006cd">prāṇaiḥ priyatarā sītā hartavyā tava saṃnidhau</li>
    </ul>
    <ul id="R_3.038.007">
        <li id="R_3.038.007ab">evaṃ me niścitā buddhir hṛdi mārīca vartate</li>
        <li id="R_3.038.007cd">na vyāvartayituṃ śakyā sendrair api surāsuraiḥ</li>
    </ul>
    <ul id="R_3.038.008">
        <li id="R_3.038.008ab">doṣaṃ guṇaṃ vā saṃpṛṣṭas tvam evaṃ vaktum arhasi</li>
        <li id="R_3.038.008cd">apāyaṃ vāpy upāyaṃ vā kāryasyāsya viniścaye</li>
    </ul>
    <ul id="R_3.038.009">
        <li id="R_3.038.009ab">saṃpṛṣṭena tu vaktavyaṃ sacivena vipaścitā</li>
        <li id="R_3.038.009cd">udyatāñjalinā rājño ya icched bhūtim ātmanaḥ</li>
    </ul>
    <ul id="R_3.038.010">
        <li id="R_3.038.010ab">vākyam apratikūlaṃ tu mṛdupūrvaṃ śubhaṃ hitam</li>
        <li id="R_3.038.010cd">upacāreṇa yuktaṃ ca vaktavyo vasudhādhipaḥ</li>
    </ul>
    <ul id="R_3.038.011">
        <li id="R_3.038.011ab">sāvamardaṃ tu yad vākyaṃ mārīca hitam ucyate</li>
        <li id="R_3.038.011cd">nābhinandati tad rājā mānārho mānavarjitam</li>
    </ul>
    <ul id="R_3.038.012">
        <li id="R_3.038.012ab">pañcarūpāṇi rājāno dhārayanty amitaujasaḥ</li>
        <li id="R_3.038.012cd">agner indrasya somasya yamasya varuṇasya ca</li>
        <li id="R_3.038.012ef">auṣṇyaṃ tathā vikramaṃ ca saumyaṃ daṇḍaṃ prasannatām</li>
    </ul>
    <ul id="R_3.038.013">
        <li id="R_3.038.013ab">tasmāt sarvāsv avasthāsu mānyāḥ pūjyāś ca pārthivāḥ</li>
        <li id="R_3.038.013cd">tvaṃ tu dharmam avijñāya kevalaṃ moham āsthitaḥ</li>
    </ul>
    <ul id="R_3.038.014">
        <li id="R_3.038.014ab">abhyāgataṃ māṃ daurātmyāt paruṣaṃ vadasīdṛśam</li>
        <li id="R_3.038.014cd">guṇadoṣau na pṛcchāmi kṣamaṃ cātmani rākṣasa</li>
        <li id="R_3.038.014ef">asmiṃs tu sa bhavān kṛtye sāhāyyaṃ kartum arhati</li>
    </ul>
    <ul id="R_3.038.015">
        <li id="R_3.038.015ab">sauvarṇas tvaṃ mṛgo bhūtvā citro rajatabindubhiḥ</li>
        <li id="R_3.038.015cd">pralobhayitvā vaidehīṃ yatheṣṭaṃ gantum arhasi</li>
    </ul>
    <ul id="R_3.038.016">
        <li id="R_3.038.016ab">tvāṃ tu māyāmṛgaṃ dṛṣṭvā kāñcanaṃ jātavismayā</li>
        <li id="R_3.038.016cd">ānayainam iti kṣipraṃ rāmaṃ vakṣyati maithilī</li>
    </ul>
    <ul id="R_3.038.017">
        <li id="R_3.038.017ab">apakrānte ca kākutsthe lakṣmaṇe ca yathāsukham</li>
        <li id="R_3.038.017cd">ānayiṣyāmi vaidehīṃ sahasrākṣaḥ śacīm iva</li>
    </ul>
    <ul id="R_3.038.018">
        <li id="R_3.038.018ab">evaṃ kṛtvā tv idaṃ kāryaṃ yatheṣṭaṃ gaccha rākṣasa</li>
        <li id="R_3.038.018cd">rājyasyārdhaṃ pradāsyāmi mārīca tava suvrata</li>
    </ul>
    <ul id="R_3.038.019">
        <li id="R_3.038.019ab">gaccha saumya śivaṃ mārgaṃ kāryasyāsya vivṛddhaye</li>
        <li id="R_3.038.019cd">prāpya sītām ayuddhena vañcayitvā tu rāghavam</li>
        <li id="R_3.038.019ef">laṅkāṃ prati gamiṣyāmi kṛtakāryaḥ saha tvayā</li>
    </ul>
    <ul id="R_3.038.020">
        <li id="R_3.038.020ab">etat kāryam avaśyaṃ me balād api kariṣyasi</li>
        <li id="R_3.038.020cd">rājño hi pratikūlastho na jātu sukham edhate</li>
    </ul>
    <ul id="R_3.038.021">
        <li id="R_3.038.021ab">
            <seg type="pāda" n="a">āsādya taṃ jīvitasaṃśayas te</seg>
            <seg type="pāda" n="b">mṛtyur dhruvo hy adya mayā virudhya</seg>
        </li>
        <li id="R_3.038.021cd">
            <seg type="pāda" n="c">etad yathāvat parigṛhya buddhyā</seg>
            <seg type="pāda" n="d">yad atra pathyaṃ kuru tat tathā tvam</seg>
        </li>
    </ul>
    <ul id="R_3.039.001">
        <li id="R_3.039.001ab">ājñapto rājavad vākyaṃ pratikūlaṃ niśācaraḥ</li>
        <li id="R_3.039.001cd">abravīt paruṣaṃ vākyaṃ mārīco rākṣasādhipam</li>
    </ul>
    <ul id="R_3.039.002">
        <li id="R_3.039.002ab">kenāyam upadiṣṭas te vināśaḥ pāpakarmaṇā</li>
        <li id="R_3.039.002cd">saputrasya sarāṣṭrasya sāmātyasya niśācara</li>
    </ul>
    <ul id="R_3.039.003">
        <li id="R_3.039.003ab">kas tvayā sukhinā rājan nābhinandati pāpakṛt</li>
        <li id="R_3.039.003cd">kenedam upadiṣṭaṃ te mṛtyudvāram upāyataḥ</li>
    </ul>
    <ul id="R_3.039.004">
        <li id="R_3.039.004ab">śatravas tava suvyaktaṃ hīnavīryā niśācara</li>
        <li id="R_3.039.004cd">icchanti tvāṃ vinaśyantam uparuddhaṃ balīyasā</li>
    </ul>
    <ul id="R_3.039.005">
        <li id="R_3.039.005ab">kenedam upadiṣṭaṃ te kṣudreṇāhitavādinā</li>
        <li id="R_3.039.005cd">yas tvām icchati naśyantaṃ svakṛtena niśācara</li>
    </ul>
    <ul id="R_3.039.006">
        <li id="R_3.039.006ab">vadhyāḥ khalu na hanyante sacivās tava rāvaṇa</li>
        <li id="R_3.039.006cd">ye tvām utpatham ārūḍhaṃ na nigṛhṇanti sarvaśaḥ</li>
    </ul>
    <ul id="R_3.039.007">
        <li id="R_3.039.007ab">amātyaiḥ kāmavṛtto hi rājā kāpatham āśritaḥ</li>
        <li id="R_3.039.007cd">nigrāhyaḥ sarvathā sadbhir na nigrāhyo nigṛhyase</li>
    </ul>
    <ul id="R_3.039.008">
        <li id="R_3.039.008ab">dharmam arthaṃ ca kāmaṃ ca yaśaś ca jayatāṃ vara</li>
        <li id="R_3.039.008cd">svāmiprasādāt sacivāḥ prāpnuvanti niśācara</li>
    </ul>
    <ul id="R_3.039.009">
        <li id="R_3.039.009ab">viparyaye tu tat sarvaṃ vyarthaṃ bhavati rāvaṇa</li>
        <li id="R_3.039.009cd">vyasanaṃ svāmivaiguṇyāt prāpnuvantītare janāḥ</li>
    </ul>
    <ul id="R_3.039.010">
        <li id="R_3.039.010ab">rājamūlo hi dharmaś ca jayaś ca jayatāṃ vara</li>
        <li id="R_3.039.010cd">tasmāt sarvāsv avasthāsu rakṣitavyo narādhipaḥ</li>
    </ul>
    <ul id="R_3.039.011">
        <li id="R_3.039.011ab">rājyaṃ pālayituṃ śakyaṃ na tīkṣṇena niśācara</li>
        <li id="R_3.039.011cd">na cāpi pratikūlena nāvinītena rākṣasa</li>
    </ul>
    <ul id="R_3.039.012">
        <li id="R_3.039.012ab">ye tīkṣṇamantrāḥ sacivā bhajyante saha tena vai</li>
        <li id="R_3.039.012cd">viṣameṣu rathāḥ śīghraṃ mandasārathayo yathā</li>
    </ul>
    <ul id="R_3.039.013">
        <li id="R_3.039.013ab">bahavaḥ sādhavo loke yuktadharmam anuṣṭhitāḥ</li>
        <li id="R_3.039.013cd">pareṣām aparādhena vinaṣṭāḥ saparicchadāḥ</li>
    </ul>
    <ul id="R_3.039.014">
        <li id="R_3.039.014ab">svāminā pratikūlena prajās tīkṣṇena rāvaṇa</li>
        <li id="R_3.039.014cd">rakṣyamāṇā na vardhante meṣā gomāyunā yathā</li>
    </ul>
    <ul id="R_3.039.015">
        <li id="R_3.039.015ab">avaśyaṃ vinaśiṣyanti sarve rāvaṇa rākṣasāḥ</li>
        <li id="R_3.039.015cd">yeṣāṃ tvaṃ karkaśo rājā durbuddhir ajitendriyaḥ</li>
    </ul>
    <ul id="R_3.039.016">
        <li id="R_3.039.016ab">tad idaṃ kākatālīyaṃ ghoram āsāditaṃ tvayā</li>
        <li id="R_3.039.016cd">atra kiṃ śobhanaṃ yat tvaṃ sasainyo vinaśiṣyasi</li>
    </ul>
    <ul id="R_3.039.017">
        <li id="R_3.039.017ab">māṃ nihatya tu rāmo 'sau nacirāt tvāṃ vadhiṣyati</li>
        <li id="R_3.039.017cd">anena kṛtakṛtyo 'smi mriye yad ariṇā hataḥ</li>
    </ul>
    <ul id="R_3.039.018">
        <li id="R_3.039.018ab">darśanād eva rāmasya hataṃ mām upadhāraya</li>
        <li id="R_3.039.018cd">ātmānaṃ ca hataṃ viddhi hṛtvā sītāṃ sabāndhavam</li>
    </ul>
    <ul id="R_3.039.019">
        <li id="R_3.039.019ab">ānayiṣyasi cet sītām āśramāt sahito mayā</li>
        <li id="R_3.039.019cd">naiva tvam asi naivāhaṃ naiva laṅkā na rākṣasāḥ</li>
    </ul>
    <ul id="R_3.039.020">
        <li id="R_3.039.020ab">
            <seg type="pāda" n="a">nivāryamāṇas tu mayā hitaiṣiṇā</seg>
            <seg type="pāda" n="b">na mṛṣyase vākyam idaṃ niśācara</seg>
        </li>
        <li id="R_3.039.020cd">
            <seg type="pāda" n="c">paretakalpā hi gatāyuṣo narā</seg>
            <seg type="pāda" n="d">hitaṃ na gṛhṇanti suhṛdbhir īritam</seg>
        </li>
    </ul>
    <ul id="R_3.040.001">
        <li id="R_3.040.001ab">evam uktvā tu paruṣaṃ mārīco rāvaṇaṃ tataḥ</li>
        <li id="R_3.040.001cd">gacchāvety abravīd dīno bhayād rātriṃcaraprabhoḥ</li>
    </ul>
    <ul id="R_3.040.002">
        <li id="R_3.040.002ab">dṛṣṭaś cāhaṃ punas tena śaracāpāsidhāriṇā</li>
        <li id="R_3.040.002cd">madvadhodyataśastreṇa vinaṣṭaṃ jīvitaṃ ca me</li>
    </ul>
    <ul id="R_3.040.003">
        <li id="R_3.040.003ab">kiṃ tu kartuṃ mayā śakyam evaṃ tvayi durātmani</li>
        <li id="R_3.040.003cd">eṣa gacchāmy ahaṃ tāta svasti te 'stu niśācara</li>
    </ul>
    <ul id="R_3.040.004">
        <li id="R_3.040.004ab">prahṛṣṭas tv abhavat tena vacanena sa rākṣasaḥ</li>
        <li id="R_3.040.004cd">pariṣvajya susaṃśliṣṭam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.040.005">
        <li id="R_3.040.005ab">etac chauṇḍīryayuktaṃ te macchandād iva bhāṣitam</li>
        <li id="R_3.040.005cd">idānīm asi mārīcaḥ pūrvam anyo niśācaraḥ</li>
    </ul>
    <ul id="R_3.040.006">
        <li id="R_3.040.006ab">āruhyatām ayaṃ śīghraṃ khago ratnavibhūṣitaḥ</li>
        <li id="R_3.040.006cd">mayā saha ratho yuktaḥ piśācavadanaiḥ kharaiḥ</li>
    </ul>
    <ul id="R_3.040.007">
        <li id="R_3.040.007ab">tato rāvaṇamārīcau vimānam iva taṃ ratham</li>
        <li id="R_3.040.007cd">āruhya yayatuḥ śīghraṃ tasmād āśramamaṇḍalāt</li>
    </ul>
    <ul id="R_3.040.008">
        <li id="R_3.040.008ab">tathaiva tatra paśyantau pattanāni vanāni ca</li>
        <li id="R_3.040.008cd">girīṃś ca saritaḥ sarvā rāṣṭrāṇi nagarāṇi ca</li>
    </ul>
    <ul id="R_3.040.009">
        <li id="R_3.040.009ab">sametya daṇḍakāraṇyaṃ rāghavasyāśramaṃ tataḥ</li>
        <li id="R_3.040.009cd">dadarśa sahamarīco rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.040.010">
        <li id="R_3.040.010ab">avatīrya rathāt tasmāt tataḥ kāñcanabhūṣaṇāt</li>
        <li id="R_3.040.010cd">haste gṛhītvā mārīcaṃ rāvaṇo vākyam abravīt</li>
    </ul>
    <ul id="R_3.040.011">
        <li id="R_3.040.011ab">etad rāmāśramapadaṃ dṛśyate kadalīvṛtam</li>
        <li id="R_3.040.011cd">kriyatāṃ tat sakhe śīghraṃ yadarthaṃ vayam āgatāḥ</li>
    </ul>
    <ul id="R_3.040.012">
        <li id="R_3.040.012ab">sa rāvaṇavacaḥ śrutvā mārīco rākṣasas tadā</li>
        <li id="R_3.040.012cd">mṛgo bhūtvāśramadvāri rāmasya vicacāra ha</li>
    </ul>
    <ul id="R_3.040.013">
        <li id="R_3.040.013ab">maṇipravaraśṛṅgāgraḥ sitāsitamukhākṛtiḥ</li>
        <li id="R_3.040.013cd">raktapadmotpalamukha indranīlotpalaśravāḥ</li>
    </ul>
    <ul id="R_3.040.014">
        <li id="R_3.040.014ab">kiṃ cid abhyunnata grīva indranīlanibhodaraḥ</li>
        <li id="R_3.040.014cd">madhūkanibhapārśvaś ca kañjakiñjalkasaṃnibhaḥ</li>
    </ul>
    <ul id="R_3.040.015">
        <li id="R_3.040.015ab">vaidūryasaṃkāśakhuras tanujaṅghaḥ susaṃhataḥ</li>
        <li id="R_3.040.015cd">indrāyudhasavarṇena pucchenordhvaṃ virājitaḥ</li>
    </ul>
    <ul id="R_3.040.016">
        <li id="R_3.040.016ab">manoharasnigdhavarṇo ratnair nānāvidhair vṛtaḥ</li>
        <li id="R_3.040.016cd">kṣaṇena rākṣaso jāto mṛgaḥ paramaśobhanaḥ</li>
    </ul>
    <ul id="R_3.040.017">
        <li id="R_3.040.017ab">vanaṃ prajvalayan ramyaṃ rāmāśramapadaṃ ca tat</li>
        <li id="R_3.040.017cd">manoharaṃ darśanīyaṃ rūpaṃ kṛtvā sa rākṣasaḥ</li>
    </ul>
    <ul id="R_3.040.018">
        <li id="R_3.040.018ab">pralobhanārthaṃ vaidehyā nānādhātuvicitritam</li>
        <li id="R_3.040.018cd">vicaran gacchate samyak śādvalāni samantataḥ</li>
    </ul>
    <ul id="R_3.040.019">
        <li id="R_3.040.019ab">rūpyabinduśataiś citro bhūtvā ca priyadarśanaḥ</li>
        <li id="R_3.040.019cd">viṭapīnāṃ kisalayān bhaṅktvādan vicacāra ha</li>
    </ul>
    <ul id="R_3.040.020">
        <li id="R_3.040.020ab">kadalīgṛhakaṃ gatvā karṇikārān itas tataḥ</li>
        <li id="R_3.040.020cd">samāśrayan mandagatiḥ sītāsaṃdarśanaṃ tadā</li>
    </ul>
    <ul id="R_3.040.021">
        <li id="R_3.040.021ab">rājīvacitrapṛṣṭhaḥ sa virarāja mahāmṛgaḥ</li>
        <li id="R_3.040.021cd">rāmāśramapadābhyāśe vicacāra yathāsukham</li>
    </ul>
    <ul id="R_3.040.022">
        <li id="R_3.040.022ab">punar gatvā nivṛttaś ca vicacāra mṛgottamaḥ</li>
        <li id="R_3.040.022cd">gatvā muhūrtaṃ tvarayā punaḥ pratinivartate</li>
    </ul>
    <ul id="R_3.040.023">
        <li id="R_3.040.023ab">vikrīḍaṃś ca punar bhūmau punar eva niṣīdati</li>
        <li id="R_3.040.023cd">āśramadvāram āgamya mṛgayūthāni gacchati</li>
    </ul>
    <ul id="R_3.040.024">
        <li id="R_3.040.024ab">mṛgayūthair anugataḥ punar eva nivartate</li>
        <li id="R_3.040.024cd">sītādarśanam ākāṅkṣan rākṣaso mṛgatāṃ gataḥ</li>
    </ul>
    <ul id="R_3.040.025">
        <li id="R_3.040.025ab">paribhramati citrāṇi maṇḍalāni viniṣpatan</li>
        <li id="R_3.040.025cd">samudvīkṣya ca sarve taṃ mṛgā ye 'nye vanecarāḥ</li>
    </ul>
    <ul id="R_3.040.026">
        <li id="R_3.040.026ab">upagamya samāghrāya vidravanti diśo daśa</li>
        <li id="R_3.040.026cd">rākṣasaḥ so 'pi tān vanyān mṛgān mṛgavadhe rataḥ</li>
    </ul>
    <ul id="R_3.040.027">
        <li id="R_3.040.027ab">pracchādanārthaṃ bhāvasya na bhakṣayati saṃspṛśan</li>
        <li id="R_3.040.027cd">tasminn eva tataḥ kāle vaidehī śubhalocanā</li>
    </ul>
    <ul id="R_3.040.028">
        <li id="R_3.040.028ab">kusumāpacaye vyagrā pādapān atyavartata</li>
        <li id="R_3.040.028cd">karṇikārān aśokāṃś ca cūṭāṃś ca madirekṣaṇā</li>
    </ul>
    <ul id="R_3.040.029">
        <li id="R_3.040.029ab">kusumāny apacinvantī cacāra rucirānanā</li>
        <li id="R_3.040.029cd">anarhāraṇyavāsasya sā taṃ ratnamayaṃ mṛgam</li>
        <li id="R_3.040.029ef">muktāmaṇivicitrāṅgaṃ dadarśa paramāṅganā</li>
    </ul>
    <ul id="R_3.040.030">
        <li id="R_3.040.030ab">taṃ vai ruciradaṇtauṣṭhaṃ rūpyadhātutanūruham</li>
        <li id="R_3.040.030cd">vismayotphullanayanā sasnehaṃ samudaikṣata</li>
    </ul>
    <ul id="R_3.040.031">
        <li id="R_3.040.031ab">sa ca tāṃ rāmadayitāṃ paśyan māyāmayo mṛgaḥ</li>
        <li id="R_3.040.031cd">vicacāra tatas tatra dīpayann iva tad vanam</li>
    </ul>
    <ul id="R_3.040.032">
        <li id="R_3.040.032ab">adṛṣṭapūrvaṃ dṛṣṭvā taṃ nānāratnamayaṃ mṛgam</li>
        <li id="R_3.040.032cd">vismayaṃ paramaṃ sītā jagāma janakātmajā</li>
    </ul>
    <ul id="R_3.041.001">
        <li id="R_3.041.001ab">sā taṃ saṃprekṣya suśroṇī kusumāni vicinvatī</li>
        <li id="R_3.041.001cd">hemarājatavarṇābhyāṃ pārśvābhyām upaśobhitam</li>
    </ul>
    <ul id="R_3.041.002">
        <li id="R_3.041.002ab">prahṛṣṭā cānavadyāṅgī mṛṣṭahāṭakavarṇinī</li>
        <li id="R_3.041.002cd">bhartāram api cākrandal lakṣmaṇaṃ caiva sāyudham</li>
    </ul>
    <ul id="R_3.041.003">
        <li id="R_3.041.003ab">tayāhūtau naravyāghrau vaidehyā rāmalakṣmaṇau</li>
        <li id="R_3.041.003cd">vīkṣamāṇau tu taṃ deśaṃ tadā dadṛśatur mṛgam</li>
    </ul>
    <ul id="R_3.041.004">
        <li id="R_3.041.004ab">śaṅkamānas tu taṃ dṛṣṭvā lakṣmaṇo rāmam abravīt</li>
        <li id="R_3.041.004cd">tam evainam ahaṃ manye mārīcaṃ rākṣasaṃ mṛgam</li>
    </ul>
    <ul id="R_3.041.005">
        <li id="R_3.041.005ab">caranto mṛgayāṃ hṛṣṭāḥ pāpenopādhinā vane</li>
        <li id="R_3.041.005cd">anena nihatā rāma rājānaḥ kāmarūpiṇā</li>
    </ul>
    <ul id="R_3.041.006">
        <li id="R_3.041.006ab">asya māyāvido māyāmṛgarūpam idaṃ kṛtam</li>
        <li id="R_3.041.006cd">bhānumatpuruṣavyāghra gandharvapurasaṃnibham</li>
    </ul>
    <ul id="R_3.041.007">
        <li id="R_3.041.007ab">mṛgo hy evaṃvidho ratnavicitro nāsti rāghava</li>
        <li id="R_3.041.007cd">jagatyāṃ jagatīnātha māyaiṣā hi na saṃśayaḥ</li>
    </ul>
    <ul id="R_3.041.008">
        <li id="R_3.041.008ab">evaṃ bruvāṇaṃ kākutsthaṃ prativārya śucismitā</li>
        <li id="R_3.041.008cd">uvāca sītā saṃhṛṣṭā chadmanā hṛtacetanā</li>
    </ul>
    <ul id="R_3.041.009">
        <li id="R_3.041.009ab">āryaputrābhirāmo 'sau mṛgo harati me manaḥ</li>
        <li id="R_3.041.009cd">ānayainaṃ mahābāho krīḍārthaṃ no bhaviṣyati</li>
    </ul>
    <ul id="R_3.041.010">
        <li id="R_3.041.010ab">ihāśramapade 'smākaṃ bahavaḥ puṇyadarśanāḥ</li>
        <li id="R_3.041.010cd">mṛgāś caranti sahitāś camarāḥ sṛmarās tathā</li>
    </ul>
    <ul id="R_3.041.011">
        <li id="R_3.041.011ab">ṛkṣāḥ pṛṣatasaṃghāś ca vānarāḥ kiṃnarās tathā</li>
        <li id="R_3.041.011cd">vicaranti mahābāho rūpaśreṣṭhā mahābalāḥ</li>
    </ul>
    <ul id="R_3.041.012">
        <li id="R_3.041.012ab">na cāsya sadṛśo rājan dṛṣṭapūrvo mṛgaḥ purā</li>
        <li id="R_3.041.012cd">tejasā kṣamayā dīptyā yathāyaṃ mṛgasattamaḥ</li>
    </ul>
    <ul id="R_3.041.013">
        <li id="R_3.041.013ab">nānāvarṇavicitrāṅgo ratnabindusamācitaḥ</li>
        <li id="R_3.041.013cd">dyotayan vanam avyagraṃ śobhate śaśisaṃnibhaḥ</li>
    </ul>
    <ul id="R_3.041.014">
        <li id="R_3.041.014ab">aho rūpam aho lakṣmīḥ svarasaṃpac ca śobhanā</li>
        <li id="R_3.041.014cd">mṛgo 'dbhuto vicitro 'sau hṛdayaṃ haratīva me</li>
    </ul>
    <ul id="R_3.041.015">
        <li id="R_3.041.015ab">yadi grahaṇam abhyeti jīvann eva mṛgas tava</li>
        <li id="R_3.041.015cd">āścaryabhūtaṃ bhavati vismayaṃ janayiṣyati</li>
    </ul>
    <ul id="R_3.041.016">
        <li id="R_3.041.016ab">samāptavanavāsānāṃ rājyasthānāṃ ca naḥ punaḥ</li>
        <li id="R_3.041.016cd">antaḥpuravibhūṣārtho mṛga eṣa bhaviṣyati</li>
    </ul>
    <ul id="R_3.041.017">
        <li id="R_3.041.017ab">bharatasyāryaputrasya śvaśrūṇāṃ mama ca prabho</li>
        <li id="R_3.041.017cd">mṛgarūpam idaṃ divyaṃ vismayaṃ janayiṣyati</li>
    </ul>
    <ul id="R_3.041.018">
        <li id="R_3.041.018ab">jīvan na yadi te 'bhyeti grahaṇaṃ mṛgasattamaḥ</li>
        <li id="R_3.041.018cd">ajinaṃ naraśārdūla ruciraṃ me bhaviṣyati</li>
    </ul>
    <ul id="R_3.041.019">
        <li id="R_3.041.019ab">nihatasyāsya sattvasya jāmbūnadamayatvaci</li>
        <li id="R_3.041.019cd">śaṣpabṛsyāṃ vinītāyām icchāmy aham upāsitum</li>
    </ul>
    <ul id="R_3.041.020">
        <li id="R_3.041.020ab">kāmavṛttam idaṃ raudraṃ strīṇām asadṛśaṃ matam</li>
        <li id="R_3.041.020cd">vapuṣā tv asya sattvasya vismayo janito mama</li>
    </ul>
    <ul id="R_3.041.021">
        <li id="R_3.041.021ab">tena kāñcanaromṇā tu maṇipravaraśṛṅgiṇā</li>
        <li id="R_3.041.021cd">taruṇādityavarṇena nakṣatrapathavarcasā</li>
        <li id="R_3.041.021ef">babhūva rāghavasyāpi mano vismayam āgatam</li>
    </ul>
    <ul id="R_3.041.022">
        <li id="R_3.041.022ab">evaṃ sītāvacaḥ śrutvā dṛṣṭvā ca mṛgam adbhutam</li>
        <li id="R_3.041.022cd">uvāca rāghavo hṛṣṭo bhrātaraṃ lakṣmaṇaṃ vacaḥ</li>
    </ul>
    <ul id="R_3.041.023">
        <li id="R_3.041.023ab">paśya lakṣmaṇa vaidehyāḥ spṛhāṃ mṛgagatām imām</li>
        <li id="R_3.041.023cd">rūpaśreṣṭhatayā hy eṣa mṛgo 'dya na bhaviṣyati</li>
    </ul>
    <ul id="R_3.041.024">
        <li id="R_3.041.024ab">na vane nandanoddeśe na caitrarathasaṃśraye</li>
        <li id="R_3.041.024cd">kutaḥ pṛthivyāṃ saumitre yo 'sya kaś cit samo mṛgaḥ</li>
    </ul>
    <ul id="R_3.041.025">
        <li id="R_3.041.025ab">pratilomānulomāś ca rucirā romarājayaḥ</li>
        <li id="R_3.041.025cd">śobhante mṛgam āśritya citrāḥ kanakabindubhiḥ</li>
    </ul>
    <ul id="R_3.041.026">
        <li id="R_3.041.026ab">paśyāsya jṛmbhamāṇasya dīptām agniśikhopamām</li>
        <li id="R_3.041.026cd">jihvāṃ mukhān niḥsarantīṃ meghād iva śatahradām</li>
    </ul>
    <ul id="R_3.041.027">
        <li id="R_3.041.027ab">masāragalvarkamukhaḥ śaṅkhamuktānibhodaraḥ</li>
        <li id="R_3.041.027cd">kasya nāmānirūpyo 'sau na mano lobhayen mṛgaḥ</li>
    </ul>
    <ul id="R_3.041.028">
        <li id="R_3.041.028ab">kasya rūpam idaṃ dṛṣṭvā jāmbūnadamayaprabham</li>
        <li id="R_3.041.028cd">nānāratnamayaṃ divyaṃ na mano vismayaṃ vrajet</li>
    </ul>
    <ul id="R_3.041.029">
        <li id="R_3.041.029ab">māṃsahetor api mṛgān vihārārthaṃ ca dhanvinaḥ</li>
        <li id="R_3.041.029cd">ghnanti lakṣmaṇa rājāno mṛgayāyāṃ mahāvane</li>
    </ul>
    <ul id="R_3.041.030">
        <li id="R_3.041.030ab">dhanāni vyavasāyena vicīyante mahāvane</li>
        <li id="R_3.041.030cd">dhātavo vividhāś cāpi maṇiratnasuvarṇinaḥ</li>
    </ul>
    <ul id="R_3.041.031">
        <li id="R_3.041.031ab">tat sāram akhilaṃ nṝṇāṃ dhanaṃ nicayavardhanam</li>
        <li id="R_3.041.031cd">manasā cintitaṃ sarvaṃ yathā śukrasya lakṣmaṇa</li>
    </ul>
    <ul id="R_3.041.032">
        <li id="R_3.041.032ab">arthī yenārthakṛtyena saṃvrajaty avicārayan</li>
        <li id="R_3.041.032cd">tam artham arthaśāstrajñaḥ prāhur arthyāś ca lakṣmaṇa</li>
    </ul>
    <ul id="R_3.041.033">
        <li id="R_3.041.033ab">etasya mṛgaratnasya parārdhye kāñcanatvaci</li>
        <li id="R_3.041.033cd">upavekṣyati vaidehī mayā saha sumadhyamā</li>
    </ul>
    <ul id="R_3.041.034">
        <li id="R_3.041.034ab">na kādalī na priyakī na praveṇī na cāvikī</li>
        <li id="R_3.041.034cd">bhaved etasya sadṛśī sparśaneneti me matiḥ</li>
    </ul>
    <ul id="R_3.041.035">
        <li id="R_3.041.035ab">eṣa caiva mṛgaḥ śrīmān yaś ca divyo nabhaścaraḥ</li>
        <li id="R_3.041.035cd">ubhāv etau mṛgau divyau tārāmṛgamahīmṛgau</li>
    </ul>
    <ul id="R_3.041.036">
        <li id="R_3.041.036ab">yadi vāyaṃ tathā yan māṃ bhaved vadasi lakṣmaṇa</li>
        <li id="R_3.041.036cd">māyaiṣā rākṣasasyeti kartavyo 'sya vadho mayā</li>
    </ul>
    <ul id="R_3.041.037">
        <li id="R_3.041.037ab">etena hi nṛśaṃsena mārīcenākṛtātmanā</li>
        <li id="R_3.041.037cd">vane vicaratā pūrvaṃ hiṃsitā munipuṃgavāḥ</li>
    </ul>
    <ul id="R_3.041.038">
        <li id="R_3.041.038ab">utthāya bahavo yena mṛgayāyāṃ janādhipāḥ</li>
        <li id="R_3.041.038cd">nihatāḥ parameṣvāsās tasmād vadhyas tv ayaṃ mṛgaḥ</li>
    </ul>
    <ul id="R_3.041.039">
        <li id="R_3.041.039ab">purastād iha vātāpiḥ paribhūya tapasvinaḥ</li>
        <li id="R_3.041.039cd">udarastho dvijān hanti svagarbho 'śvatarīm iva</li>
    </ul>
    <ul id="R_3.041.040">
        <li id="R_3.041.040ab">sa kadā cic cirāl loke āsasāda mahāmunim</li>
        <li id="R_3.041.040cd">agastyaṃ tejasā yuktaṃ bhakṣyas tasya babhūva ha</li>
    </ul>
    <ul id="R_3.041.041">
        <li id="R_3.041.041ab">samutthāne ca tad rūpaṃ kartukāmaṃ samīkṣya tam</li>
        <li id="R_3.041.041cd">utsmayitvā tu bhagavān vātāpim idam abravīt</li>
    </ul>
    <ul id="R_3.041.042">
        <li id="R_3.041.042ab">tvayāvigaṇya vātāpe paribhūtāś ca tejasā</li>
        <li id="R_3.041.042cd">jīvaloke dvijaśreṣṭhās tasmād asi jarāṃ gataḥ</li>
    </ul>
    <ul id="R_3.041.043">
        <li id="R_3.041.043ab">evaṃ tan na bhaved rakṣo vātāpir iva lakṣmaṇa</li>
        <li id="R_3.041.043cd">madvidhaṃ yo 'timanyeta dharmanityaṃ jitendriyam</li>
    </ul>
    <ul id="R_3.041.044">
        <li id="R_3.041.044ab">bhaved dhato 'yaṃ vātāpir agastyeneva mā gatiḥ</li>
        <li id="R_3.041.044cd">iha tvaṃ bhava saṃnaddho yantrito rakṣa maithilīm</li>
    </ul>
    <ul id="R_3.041.045">
        <li id="R_3.041.045ab">asyām āyattam asmākaṃ yat kṛtyaṃ raghunandana</li>
        <li id="R_3.041.045cd">aham enaṃ vadhiṣyāmi grahīṣyāmy atha vā mṛgam</li>
    </ul>
    <ul id="R_3.041.046">
        <li id="R_3.041.046ab">yāvad gacchāmi saumitre mṛgam ānayituṃ drutam</li>
        <li id="R_3.041.046cd">paśya lakṣmaṇa vaidehīṃ mṛgatvaci gataspṛhām</li>
    </ul>
    <ul id="R_3.041.047">
        <li id="R_3.041.047ab">tvacā pradhānayā hy eṣa mṛgo 'dya na bhaviṣyati</li>
        <li id="R_3.041.047cd">apramattena te bhāvyam āśramasthena sītayā</li>
    </ul>
    <ul id="R_3.041.048">
        <li id="R_3.041.048ab">yāvat pṛṣatam ekena sāyakena nihanmy aham</li>
        <li id="R_3.041.048cd">hatvaitac carma ādāya śīghram eṣyāmi lakṣmaṇa</li>
    </ul>
    <ul id="R_3.041.049">
        <li id="R_3.041.049ab">
            <seg type="pāda" n="a">pradakṣiṇenātibalena pakṣiṇā</seg>
            <seg type="pāda" n="b">jaṭāyuṣā buddhimatā ca lakṣmaṇa</seg>
        </li>
        <li id="R_3.041.049cd">
            <seg type="pāda" n="c">bhavāpramattaḥ pratigṛhya maithilīṃ</seg>
            <seg type="pāda" n="d">pratikṣaṇaṃ sarvata eva śaṅkitaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.042.001">
        <li id="R_3.042.001ab">tathā tu taṃ samādiśya bhrātaraṃ raghunandanaḥ</li>
        <li id="R_3.042.001cd">babandhāsiṃ mahātejā jāmbūnadamayatsarum</li>
    </ul>
    <ul id="R_3.042.002">
        <li id="R_3.042.002ab">tatas triviṇataṃ cāpam ādāyātmavibhūṣaṇam</li>
        <li id="R_3.042.002cd">ābadhya ca kalāpau dvau jagāmodagravikramaḥ</li>
    </ul>
    <ul id="R_3.042.003">
        <li id="R_3.042.003ab">taṃ vañcayāno rājendram āpatantaṃ nirīkṣya vai</li>
        <li id="R_3.042.003cd">babhūvāntarhitas trāsāt punaḥ saṃdarśane 'bhavat</li>
    </ul>
    <ul id="R_3.042.004">
        <li id="R_3.042.004ab">baddhāsir dhanur ādāya pradudrāva yato mṛgaḥ</li>
        <li id="R_3.042.004cd">taṃ sa paśyati rūpeṇa dyotamānam ivāgrataḥ</li>
    </ul>
    <ul id="R_3.042.005">
        <li id="R_3.042.005ab">avekṣyāvekṣya dhāvantaṃ dhanuṣpāṇir mahāvane</li>
        <li id="R_3.042.005cd">ativṛttam iṣoḥ pātāl lobhayānaṃ kadā cana</li>
    </ul>
    <ul id="R_3.042.006">
        <li id="R_3.042.006ab">śaṅkitaṃ tu samudbhrāntam utpatantam ivāmbare</li>
        <li id="R_3.042.006cd">daśyamānam adṛśyaṃ ca navoddeśeṣu keṣu cit</li>
    </ul>
    <ul id="R_3.042.007">
        <li id="R_3.042.007ab">chinnābhrair iva saṃvītaṃ śāradaṃ candramaṇḍalam</li>
        <li id="R_3.042.007cd">muhūrtād eva dadṛśe muhur dūrāt prakāśate</li>
    </ul>
    <ul id="R_3.042.008">
        <li id="R_3.042.008ab">darśanādarśanenaiva so 'pākarṣata rāghavam</li>
        <li id="R_3.042.008cd">āsīt kruddhas tu kākutstho vivaśas tena mohitaḥ</li>
    </ul>
    <ul id="R_3.042.009">
        <li id="R_3.042.009ab">athāvatasthe suśrāntaś chāyām āśritya śādvale</li>
        <li id="R_3.042.009cd">mṛgaiḥ parivṛto vanyair adūrāt pratyadṛśyata</li>
    </ul>
    <ul id="R_3.042.010">
        <li id="R_3.042.010ab">dṛṣṭvā rāmo mahātejās taṃ hantuṃ kṛtaniścayaḥ</li>
        <li id="R_3.042.010cd">saṃdhāya sudṛḍhe cāpe vikṛṣya balavad balī</li>
    </ul>
    <ul id="R_3.042.011">
        <li id="R_3.042.011ab">tam eva mṛgam uddiśya jvalantam iva pannagam</li>
        <li id="R_3.042.011cd">mumoca jvalitaṃ dīptam astrabrahmavinirmitam</li>
    </ul>
    <ul id="R_3.042.012">
        <li id="R_3.042.012ab">sa bhṛśaṃ mṛgarūpasya vinirbhidya śarottamaḥ</li>
        <li id="R_3.042.012cd">mārīcasyaiva hṛdayaṃ vibhedāśanisaṃnibhaḥ</li>
    </ul>
    <ul id="R_3.042.013">
        <li id="R_3.042.013ab">tālamātram athotpatya nyapatat sa śarāturaḥ</li>
        <li id="R_3.042.013cd">vyanadad bhairavaṃ nādaṃ dharaṇyām alpajīvitaḥ</li>
        <li id="R_3.042.013ef">mriyamāṇas tu mārīco jahau tāṃ kṛtrimāṃ tanum</li>
    </ul>
    <ul id="R_3.042.014">
        <li id="R_3.042.014ab">saṃprāptakālam ājñāya cakāra ca tataḥ svaram</li>
        <li id="R_3.042.014cd">sadṛśaṃ rāghavasyaiva hā sīte lakṣmaṇeti ca</li>
    </ul>
    <ul id="R_3.042.015">
        <li id="R_3.042.015ab">tena marmaṇi nirviddhaḥ śareṇānupamena hi</li>
        <li id="R_3.042.015cd">mṛgarūpaṃ tu tat tyaktvā rākṣasaṃ rūpam ātmanaḥ</li>
        <li id="R_3.042.015ef">cakre sa sumahākāyo mārīco jīvitaṃ tyajan</li>
    </ul>
    <ul id="R_3.042.016">
        <li id="R_3.042.016ab">tato vicitrakeyūraḥ sarvābharaṇabhūṣitaḥ</li>
        <li id="R_3.042.016cd">hemamālī mahādaṃṣṭro rākṣaso 'bhūc charāhataḥ</li>
    </ul>
    <ul id="R_3.042.017">
        <li id="R_3.042.017ab">taṃ dṛṣṭvā patitaṃ bhūmau rākṣasaṃ ghoradarśanam</li>
        <li id="R_3.042.017cd">jagāma manasā sītāṃ lakṣmaṇasya vacaḥ smaran</li>
    </ul>
    <ul id="R_3.042.018">
        <li id="R_3.042.018ab">hā sīte lakṣmaṇety evam ākruśya tu mahāsvaram</li>
        <li id="R_3.042.018cd">mamāra rākṣasaḥ so 'yaṃ śrutvā sītā kathaṃ bhavet</li>
    </ul>
    <ul id="R_3.042.019">
        <li id="R_3.042.019ab">lakṣmaṇaś ca mahābāhuḥ kām avasthāṃ gamiṣyati</li>
        <li id="R_3.042.019cd">iti saṃcintya dharmātmā rāmo hṛṣṭatanūruhaḥ</li>
    </ul>
    <ul id="R_3.042.020">
        <li id="R_3.042.020ab">tatra rāmaṃ bhayaṃ tīvram āviveśa viṣādajam</li>
        <li id="R_3.042.020cd">rākṣasaṃ mṛgarūpaṃ taṃ hatvā śrutvā ca tat svaram</li>
    </ul>
    <ul id="R_3.042.021">
        <li id="R_3.042.021ab">nihatya pṛṣataṃ cānyaṃ māṃsam ādāya rāghavaḥ</li>
        <li id="R_3.042.021cd">tvaramāṇo janasthānaṃ sasārābhimukhas tadā</li>
    </ul>
    <ul id="R_3.043.001">
        <li id="R_3.043.001ab">ārtasvaraṃ tu taṃ bhartur vijñāya sadṛśaṃ vane</li>
        <li id="R_3.043.001cd">uvāca lakṣmaṇaṃ sītā gaccha jānīhi rāghavam</li>
    </ul>
    <ul id="R_3.043.002">
        <li id="R_3.043.002ab">na hi me jīvitaṃ sthāne hṛdayaṃ vāvatiṣṭhate</li>
        <li id="R_3.043.002cd">krośataḥ paramārtasya śrutaḥ śabdo mayā bhṛśam</li>
    </ul>
    <ul id="R_3.043.003">
        <li id="R_3.043.003ab">ākrandamānaṃ tu vane bhrātaraṃ trātum arhasi</li>
        <li id="R_3.043.003cd">taṃ kṣipram abhidhāva tvaṃ bhrātaraṃ śaraṇaiṣiṇam</li>
    </ul>
    <ul id="R_3.043.004">
        <li id="R_3.043.004ab">rakṣasāṃ vaśam āpannaṃ siṃhānām iva govṛṣam</li>
        <li id="R_3.043.004cd">na jagāma tathoktas tu bhrātur ājñāya śāsanam</li>
    </ul>
    <ul id="R_3.043.005">
        <li id="R_3.043.005ab">tam uvāca tatas tatra kupitā janakātmajā</li>
        <li id="R_3.043.005cd">saumitre mitrarūpeṇa bhrātus tvam asi śatruvat</li>
    </ul>
    <ul id="R_3.043.006">
        <li id="R_3.043.006ab">yas tvam asyām avasthāyāṃ bhrātaraṃ nābhipadyase</li>
        <li id="R_3.043.006cd">icchasi tvaṃ vinaśyantaṃ rāmaṃ lakṣmaṇa matkṛte</li>
    </ul>
    <ul id="R_3.043.007">
        <li id="R_3.043.007ab">vyasanaṃ te priyaṃ manye sneho bhrātari nāsti te</li>
        <li id="R_3.043.007cd">tena tiṣṭhasi visrabdhas tam apaśyan mahādyutim</li>
    </ul>
    <ul id="R_3.043.008">
        <li id="R_3.043.008ab">kiṃ hi saṃśayam āpanne tasminn iha mayā bhavet</li>
        <li id="R_3.043.008cd">kartavyam iha tiṣṭhantyā yat pradhānas tvam āgataḥ</li>
    </ul>
    <ul id="R_3.043.009">
        <li id="R_3.043.009ab">iti bruvāṇaṃ vaidehīṃ bāṣpaśokapariplutām</li>
        <li id="R_3.043.009cd">abravīl lakṣmaṇas trastāṃ sītāṃ mṛgavadhūm iva</li>
    </ul>
    <ul id="R_3.043.010">
        <li id="R_3.043.010ab">devi devamanuṣyeṣu gandharveṣu patatriṣu</li>
        <li id="R_3.043.010cd">rākṣaseṣu piśāceṣu kiṃnareṣu mṛgeṣu ca</li>
    </ul>
    <ul id="R_3.043.011">
        <li id="R_3.043.011ab">dānaveṣu ca ghoreṣu na sa vidyeta śobhane</li>
        <li id="R_3.043.011cd">yo rāmaṃ pratiyudhyeta samare vāsavopamam</li>
    </ul>
    <ul id="R_3.043.012">
        <li id="R_3.043.012ab">avadhyaḥ samare rāmo naivaṃ tvaṃ vaktum arhasi</li>
        <li id="R_3.043.012cd">na tvām asmin vane hātum utsahe rāghavaṃ vinā</li>
    </ul>
    <ul id="R_3.043.013">
        <li id="R_3.043.013ab">anivāryaṃ balaṃ tasya balair balavatām api</li>
        <li id="R_3.043.013cd">tribhir lokaiḥ samudyuktaiḥ seśvaraiḥ sāmarair api</li>
    </ul>
    <ul id="R_3.043.014">
        <li id="R_3.043.014ab">hṛdayaṃ nirvṛtaṃ te 'stu saṃtāpas tyajyatām ayam</li>
        <li id="R_3.043.014cd">āgamiṣyati te bhartā śīghraṃ hatvā mṛgottamam</li>
    </ul>
    <ul id="R_3.043.015">
        <li id="R_3.043.015ab">na sa tasya svaro vyaktaṃ na kaś cid api daivataḥ</li>
        <li id="R_3.043.015cd">gandharvanagaraprakhyā māyā sā tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_3.043.016">
        <li id="R_3.043.016ab">nyāsabhūtāsi vaidehi nyastā mayi mahātmanā</li>
        <li id="R_3.043.016cd">rāmeṇa tvaṃ varārohe na tvāṃ tyaktum ihotsahe</li>
    </ul>
    <ul id="R_3.043.017">
        <li id="R_3.043.017ab">kṛtavairāś ca kalyāṇi vayam etair niśācaraiḥ</li>
        <li id="R_3.043.017cd">kharasya nidhane devi janasthānavadhaṃ prati</li>
    </ul>
    <ul id="R_3.043.018">
        <li id="R_3.043.018ab">rākṣasā vidhinā vāco visṛjanti mahāvane</li>
        <li id="R_3.043.018cd">hiṃsāvihārā vaidehi na cintayitum arhasi</li>
    </ul>
    <ul id="R_3.043.019">
        <li id="R_3.043.019ab">lakṣmaṇenaivam uktā tu kruddhā saṃraktalocanā</li>
        <li id="R_3.043.019cd">abravīt paruṣaṃ vākyaṃ lakṣmaṇaṃ satyavādinam</li>
    </ul>
    <ul id="R_3.043.020">
        <li id="R_3.043.020ab">anārya karuṇārambha nṛśaṃsa kulapāṃsana</li>
        <li id="R_3.043.020cd">ahaṃ tava priyaṃ manye tenaitāni prabhāṣase</li>
    </ul>
    <ul id="R_3.043.021">
        <li id="R_3.043.021ab">naitac citraṃ sapatneṣu pāpaṃ lakṣmaṇa yad bhavet</li>
        <li id="R_3.043.021cd">tvadvidheṣu nṛśaṃseṣu nityaṃ pracchannacāriṣu</li>
    </ul>
    <ul id="R_3.043.022">
        <li id="R_3.043.022ab">suduṣṭas tvaṃ vane rāmam ekam eko 'nugacchasi</li>
        <li id="R_3.043.022cd">mama hetoḥ praticchannaḥ prayukto bharatena vā</li>
    </ul>
    <ul id="R_3.043.023">
        <li id="R_3.043.023ab">katham indīvaraśyāmaṃ rāmaṃ padmanibhekṣaṇam</li>
        <li id="R_3.043.023cd">upasaṃśritya bhartāraṃ kāmayeyaṃ pṛthag janam</li>
    </ul>
    <ul id="R_3.043.024">
        <li id="R_3.043.024ab">samakṣaṃ tava saumitre prāṇāṃs tyakṣye na saṃśayaḥ</li>
        <li id="R_3.043.024cd">rāmaṃ vinā kṣaṇam api na hi jīvāmi bhūtale</li>
    </ul>
    <ul id="R_3.043.025">
        <li id="R_3.043.025ab">ity uktaḥ paruṣaṃ vākyaṃ sītayā somaharṣaṇam</li>
        <li id="R_3.043.025cd">abravīl lakṣmaṇaḥ sītāṃ prāñjalir vijitendriyaḥ</li>
    </ul>
    <ul id="R_3.043.026">
        <li id="R_3.043.026ab">uttaraṃ notsahe vaktuṃ daivataṃ bhavatī mama</li>
        <li id="R_3.043.026cd">vākyam apratirūpaṃ tu na citraṃ strīṣu maithili</li>
    </ul>
    <ul id="R_3.043.027">
        <li id="R_3.043.027ab">svabhāvas tv eṣa nārīṇām eṣu lokeṣu dṛśyate</li>
        <li id="R_3.043.027cd">vimuktadharmāś capalās tīkṣṇā bhedakarāḥ striyaḥ</li>
    </ul>
    <ul id="R_3.043.028">
        <li id="R_3.043.028ab">upaśṛṇvantu me sarve sākṣibhūtā vanecarāḥ</li>
        <li id="R_3.043.028cd">nyāyavādī yathā vākyam ukto 'haṃ paruṣaṃ tvayā</li>
    </ul>
    <ul id="R_3.043.029">
        <li id="R_3.043.029ab">dhik tvām adya praṇaśya tvaṃ yan mām evaṃ viśaṅkase</li>
        <li id="R_3.043.029cd">strītvād duṣṭasvabhāvena guruvākye vyavasthitam</li>
    </ul>
    <ul id="R_3.043.030">
        <li id="R_3.043.030ab">gamiṣye yatra kākutsthaḥ svasti te 'stu varānane</li>
        <li id="R_3.043.030cd">rakṣantu tvāṃ viśālākṣi samagrā vanadevatāḥ</li>
    </ul>
    <ul id="R_3.043.031">
        <li id="R_3.043.031ab">nimittāni hi ghorāṇi yāni prādurbhavanti me</li>
        <li id="R_3.043.031cd">api tvāṃ saha rāmeṇa paśyeyaṃ punar āgataḥ</li>
    </ul>
    <ul id="R_3.043.032">
        <li id="R_3.043.032ab">lakṣmaṇenaivam uktā tu rudatī janakātmajā</li>
        <li id="R_3.043.032cd">pratyuvāca tato vākyaṃ tīvraṃ bāṣpapariplutā</li>
    </ul>
    <ul id="R_3.043.033">
        <li id="R_3.043.033ab">godāvarīṃ pravekṣyāmi vinā rāmeṇa lakṣmaṇa</li>
        <li id="R_3.043.033cd">ābandhiṣye 'tha vā tyakṣye viṣame deham ātmanaḥ</li>
    </ul>
    <ul id="R_3.043.034">
        <li id="R_3.043.034ab">pibāmi vā viṣaṃ tīkṣṇaṃ pravekṣyāmi hutāśanam</li>
        <li id="R_3.043.034cd">na tv ahaṃ rāghavād anyaṃ padāpi puruṣaṃ spṛśe</li>
    </ul>
    <ul id="R_3.043.035">
        <li id="R_3.043.035ab">iti lakṣmaṇam ākruśya sītā duḥkhasamanvitā</li>
        <li id="R_3.043.035cd">pāṇibhyāṃ rudatī duḥkhād udaraṃ prajaghāna ha</li>
    </ul>
    <ul id="R_3.043.036">
        <li id="R_3.043.036ab">
            <seg type="pāda" n="a">tām ārtarūpāṃ vimanā rudantīṃ</seg>
            <seg type="pāda" n="b">saumitrir ālokya viśālanetrām</seg>
        </li>
        <li id="R_3.043.036cd">
            <seg type="pāda" n="c">āśvāsayām āsa na caiva bhartus</seg>
            <seg type="pāda" n="d">taṃ bhrātaraṃ kiṃ cid uvāca sītā</seg>
        </li>
    </ul>
    <ul id="R_3.043.037">
        <li id="R_3.043.037ab">
            <seg type="pāda" n="a">tatas tu sītām abhivādya lakṣmaṇaḥ</seg>
            <seg type="pāda" n="b">kṛtāñjaliḥ kiṃ cid abhipraṇamya</seg>
        </li>
        <li id="R_3.043.037cd">
            <seg type="pāda" n="c">avekṣamāṇo bahuśaś ca maithilīṃ</seg>
            <seg type="pāda" n="d">jagāma rāmasya samīpam ātmavān</seg>
        </li>
    </ul>
    <ul id="R_3.044.001">
        <li id="R_3.044.001ab">tayā paruṣam uktas tu kupito rāghavānujaḥ</li>
        <li id="R_3.044.001cd">sa vikāṅkṣan bhṛśaṃ rāmaṃ pratasthe nacirād iva</li>
    </ul>
    <ul id="R_3.044.002">
        <li id="R_3.044.002ab">tadāsādya daśagrīvaḥ kṣipram antaram āsthitaḥ</li>
        <li id="R_3.044.002cd">abhicakrāma vaidehīṃ parivrājakarūpadhṛk</li>
    </ul>
    <ul id="R_3.044.003">
        <li id="R_3.044.003ab">ślakṣṇakāṣāyasaṃvītaḥ śikhī chatrī upānahī</li>
        <li id="R_3.044.003cd">vāme cāṃse 'vasajyātha śubhe yaṣṭikamaṇḍalū</li>
        <li id="R_3.044.003ef">parivrājakarūpeṇa vaidehīṃ samupāgamat</li>
    </ul>
    <ul id="R_3.044.004">
        <li id="R_3.044.004ab">tām āsasādātibalo bhrātṛbhyāṃ rahitāṃ vane</li>
        <li id="R_3.044.004cd">rahitāṃ sūryacandrābhyāṃ saṃdhyām iva mahattamaḥ</li>
    </ul>
    <ul id="R_3.044.005">
        <li id="R_3.044.005ab">tām apaśyat tato bālāṃ rājaputrīṃ yaśasvinīm</li>
        <li id="R_3.044.005cd">rohiṇīṃ śaśinā hīnāṃ grahavad bhṛśadāruṇaḥ</li>
    </ul>
    <ul id="R_3.044.006">
        <li id="R_3.044.006ab">tam ugraṃ pāpakarmāṇaṃ janasthānaruhā drumāḥ</li>
        <li id="R_3.044.006cd">samīkṣya na prakampante na pravāti ca mārutaḥ</li>
    </ul>
    <ul id="R_3.044.007">
        <li id="R_3.044.007ab">śīghrasrotāś ca taṃ dṛṣṭvā vīkṣantaṃ raktalocanam</li>
        <li id="R_3.044.007cd">stimitaṃ gantum ārebhe bhayād godāvarī nadī</li>
    </ul>
    <ul id="R_3.044.008">
        <li id="R_3.044.008ab">rāmasya tv antaraṃ prepsur daśagrīvas tadantare</li>
        <li id="R_3.044.008cd">upatasthe ca vaidehīṃ bhikṣurūpeṇa rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.044.009">
        <li id="R_3.044.009ab">abhavyo bhavyarūpeṇa bhartāram anuśocatīm</li>
        <li id="R_3.044.009cd">abhyavartata vaidehīṃ citrām iva śanaiścaraḥ</li>
    </ul>
    <ul id="R_3.044.010">
        <li id="R_3.044.010ab">sa pāpo bhavyarūpeṇa tṛṇaiḥ kūpa ivāvṛtaḥ</li>
        <li id="R_3.044.010cd">atiṣṭhat prekṣya vaidehīṃ rāmapatnīṃ yaśasvinīm</li>
    </ul>
    <ul id="R_3.044.011">
        <li id="R_3.044.011ab">śubhāṃ ruciradantauṣṭhīṃ pūrṇacandranibhānanām</li>
        <li id="R_3.044.011cd">āsīnāṃ parṇaśālāyāṃ bāṣpaśokābhipīḍitām</li>
    </ul>
    <ul id="R_3.044.012">
        <li id="R_3.044.012ab">sa tāṃ padmapalāśākṣīṃ pītakauśeyavāsinīm</li>
        <li id="R_3.044.012cd">abhyagacchata vaidehīṃ duṣṭacetā niśācaraḥ</li>
    </ul>
    <ul id="R_3.044.013">
        <li id="R_3.044.013ab">sa manmathaśarāviṣṭo brahmaghoṣam udīrayan</li>
        <li id="R_3.044.013cd">abravīt praśritaṃ vākyaṃ rahite rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.044.014">
        <li id="R_3.044.014ab">tām uttamāṃ trilokānāṃ padmahīnām iva śriyam</li>
        <li id="R_3.044.014cd">vibhrājamānāṃ vapuṣā rāvaṇaḥ praśaśaṃsa ha</li>
    </ul>
    <ul id="R_3.044.015">
        <li id="R_3.044.015ab">kā tvaṃ kāñcanavarṇābhe pītakauśeyavāsini</li>
        <li id="R_3.044.015cd">kamalānāṃ śubhāṃ mālāṃ padminīva ca bibhratī</li>
    </ul>
    <ul id="R_3.044.016">
        <li id="R_3.044.016ab">hrīḥ śrīḥ kīrtiḥ śubhā lakṣmīr apsarā vā śubhānane</li>
        <li id="R_3.044.016cd">bhūtir vā tvaṃ varārohe ratir vā svairacāriṇī</li>
    </ul>
    <ul id="R_3.044.017">
        <li id="R_3.044.017ab">samāḥ śikhariṇaḥ snigdhāḥ pāṇḍurā daśanās tava</li>
        <li id="R_3.044.017cd">viśāle vimale netre raktānte kṛṣṇatārake</li>
    </ul>
    <ul id="R_3.044.018">
        <li id="R_3.044.018ab">viśālaṃ jaghanaṃ pīnam ūrū karikaropamau</li>
        <li id="R_3.044.018cd">etāv upacitau vṛttau sahitau saṃpragalbhitau</li>
    </ul>
    <ul id="R_3.044.019">
        <li id="R_3.044.019ab">pīnonnatamukhau kāntau snigdhatālaphalopamau</li>
        <li id="R_3.044.019cd">maṇipravekābharaṇau rucirau te payodharau</li>
    </ul>
    <ul id="R_3.044.020">
        <li id="R_3.044.020ab">cārusmite cārudati cārunetre vilāsini</li>
        <li id="R_3.044.020cd">mano harasi me rāme nadīkūlam ivāmbhasā</li>
    </ul>
    <ul id="R_3.044.021">
        <li id="R_3.044.021ab">karāntamitamadhyāsi sukeśī saṃhatastanī</li>
        <li id="R_3.044.021cd">naiva devī na gandharvī na yakṣī na ca kiṃnarī</li>
    </ul>
    <ul id="R_3.044.022">
        <li id="R_3.044.022ab">naivaṃrūpā mayā nārī dṛṣṭapūrvā mahītale</li>
        <li id="R_3.044.022cd">iha vāsaś ca kāntāre cittam unmāthayanti me</li>
    </ul>
    <ul id="R_3.044.023">
        <li id="R_3.044.023ab">sā pratikrāma bhadraṃ te na tvaṃ vastum ihārhasi</li>
        <li id="R_3.044.023cd">rākṣasānām ayaṃ vāso ghorāṇāṃ kāmarūpiṇām</li>
    </ul>
    <ul id="R_3.044.024">
        <li id="R_3.044.024ab">prāsādāgryāṇi ramyāṇi nagaropavanāni ca</li>
        <li id="R_3.044.024cd">saṃpannāni sugandhīni yuktāny ācarituṃ tvayā</li>
    </ul>
    <ul id="R_3.044.025">
        <li id="R_3.044.025ab">varaṃ mālyaṃ varaṃ pānaṃ varaṃ vastraṃ ca śobhane</li>
        <li id="R_3.044.025cd">bhartāraṃ ca varaṃ manye tvadyuktam asitekṣaṇe</li>
    </ul>
    <ul id="R_3.044.026">
        <li id="R_3.044.026ab">kā tvaṃ bhavasi rudrāṇāṃ marutāṃ vā śucismite</li>
        <li id="R_3.044.026cd">vasūnāṃ vā varārohe devatā pratibhāsi me</li>
    </ul>
    <ul id="R_3.044.027">
        <li id="R_3.044.027ab">neha gacchantī gandharvā na devā na ca kiṃnarāḥ</li>
        <li id="R_3.044.027cd">rākṣasānām ayaṃ vāsaḥ kathaṃ nu tvam ihāgatā</li>
    </ul>
    <ul id="R_3.044.028">
        <li id="R_3.044.028ab">iha śākhāmṛgāḥ siṃhā dvīpivyāghramṛgās tathā</li>
        <li id="R_3.044.028cd">ṛkṣās tarakṣavaḥ kaṅkāḥ kathaṃ tebhyo na bibhyase</li>
    </ul>
    <ul id="R_3.044.029">
        <li id="R_3.044.029ab">madānvitānāṃ ghorāṇāṃ kuñjarāṇāṃ tarasvinām</li>
        <li id="R_3.044.029cd">katham ekā mahāraṇye na bibheṣi vanānane</li>
    </ul>
    <ul id="R_3.044.030">
        <li id="R_3.044.030ab">kāsi kasya kutaś ca tvaṃ kiṃnimittaṃ ca daṇḍakān</li>
        <li id="R_3.044.030cd">ekā carasi kalyāṇi ghorān rākṣasasevitān</li>
    </ul>
    <ul id="R_3.044.031">
        <li id="R_3.044.031ab">iti praśastā vaidehī rāvaṇena durātmanā</li>
        <li id="R_3.044.031cd">dvijātiveṣeṇa hi taṃ dṛṣṭvā rāvaṇam āgatam</li>
        <li id="R_3.044.031ef">sarvair atithisatkāraiḥ pūjayām āsa maithilī</li>
    </ul>
    <ul id="R_3.044.032">
        <li id="R_3.044.032ab">upānīyāsanaṃ pūrvaṃ pādyenābhinimantrya ca</li>
        <li id="R_3.044.032cd">abravīt siddham ity eva tadā taṃ saumyadarśanam</li>
    </ul>
    <ul id="R_3.044.033">
        <li id="R_3.044.033ab">
            <seg type="pāda" n="a">dvijātiveṣeṇa samīkṣya maithilī</seg>
            <seg type="pāda" n="b">tam āgataṃ pātrakusumbhadhāriṇam</seg>
        </li>
        <li id="R_3.044.033cd">
            <seg type="pāda" n="c">aśakyam uddveṣṭum upāyadarśanān</seg>
            <seg type="pāda" n="d">nyamantrayad brāhmaṇavad yathāgatam</seg>
        </li>
    </ul>
    <ul id="R_3.044.034">
        <li id="R_3.044.034ab">
            <seg type="pāda" n="a">iyaṃ bṛsī brāhmaṇa kāmam āsyatām</seg>
            <seg type="pāda" n="b">idaṃ ca pādyaṃ pratigṛhyatām iti</seg>
        </li>
        <li id="R_3.044.034cd">
            <seg type="pāda" n="c">idaṃ ca siddhaṃ vanajātam uttamaṃ</seg>
            <seg type="pāda" n="d">tvadartham avyagram ihopabhujyatām</seg>
        </li>
    </ul>
    <ul id="R_3.044.035">
        <li id="R_3.044.035ab">
            <seg type="pāda" n="a">nimantryamāṇaḥ pratipūrṇabhāṣiṇīṃ</seg>
            <seg type="pāda" n="b">narendrapatnīṃ prasamīkṣya maithilīm</seg>
        </li>
        <li id="R_3.044.035cd">
            <seg type="pāda" n="c">prahasya tasyā haraṇe dhṛtaṃ manaḥ</seg>
            <seg type="pāda" n="d">samarpayām āsa vadhāya rāvaṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.044.036">
        <li id="R_3.044.036ab">
            <seg type="pāda" n="a">tataḥ suveṣaṃ mṛgayā gataṃ patiṃ</seg>
            <seg type="pāda" n="b">pratīkṣamāṇā sahalakṣmaṇaṃ tadā</seg>
        </li>
        <li id="R_3.044.036cd">
            <seg type="pāda" n="c">nirīkṣamāṇā haritaṃ dadarśa tan</seg>
            <seg type="pāda" n="d">mahad vanaṃ naiva tu rāmalakṣmaṇau</seg>
        </li>
    </ul>
    <ul id="R_3.045.001">
        <li id="R_3.045.001ab">rāvaṇena tu vaidehī tadā pṛṣṭā jihīrṣuṇā</li>
        <li id="R_3.045.001cd">parivrājakarūpeṇa śaśaṃsātmānam ātmanā</li>
    </ul>
    <ul id="R_3.045.002">
        <li id="R_3.045.002ab">brāhmaṇaś cātithiś caiṣa anukto hi śapeta mām</li>
        <li id="R_3.045.002cd">iti dhyātvā muhūrtaṃ tu sītā vacanam abravīt</li>
    </ul>
    <ul id="R_3.045.003">
        <li id="R_3.045.003ab">duhitā janakasyāhaṃ maithilasya mahātmanaḥ</li>
        <li id="R_3.045.003cd">sītā nāmnāsmi bhadraṃ te rāmabhāryā dvijottama</li>
    </ul>
    <ul id="R_3.045.004">
        <li id="R_3.045.004ab">saṃvatsaraṃ cādhyuṣitā rāghavasya niveśane</li>
        <li id="R_3.045.004cd">bhuñjānā mānuṣān bhogān sarvakāmasamṛddhinī</li>
    </ul>
    <ul id="R_3.045.005">
        <li id="R_3.045.005ab">tataḥ saṃvatsarād ūrdhvaṃ samamanyata me patim</li>
        <li id="R_3.045.005cd">abhiṣecayituṃ rāmaṃ sameto rājamantribhiḥ</li>
    </ul>
    <ul id="R_3.045.006">
        <li id="R_3.045.006ab">tasmin saṃbhriyamāṇe tu rāghavasyābhiṣecane</li>
        <li id="R_3.045.006cd">kaikeyī nāma bhartāraṃ mamāryā yācate varam</li>
    </ul>
    <ul id="R_3.045.007">
        <li id="R_3.045.007ab">pratigṛhya tu kaikeyī śvaśuraṃ sukṛtena me</li>
        <li id="R_3.045.007cd">mama pravrājanaṃ bhartur bharatasyābhiṣecanam</li>
        <li id="R_3.045.007ef">dvāv ayācata bhartāraṃ satyasaṃdhaṃ nṛpottamam</li>
    </ul>
    <ul id="R_3.045.008">
        <li id="R_3.045.008ab">nādya bhokṣye na ca svapsye na pāsye 'haṃ kadā cana</li>
        <li id="R_3.045.008cd">eṣa me jīvitasyānto rāmo yady abhiṣicyate</li>
    </ul>
    <ul id="R_3.045.009">
        <li id="R_3.045.009ab">iti bruvāṇāṃ kaikeyīṃ śvaśuro me sa mānadaḥ</li>
        <li id="R_3.045.009cd">ayācatārthair anvarthair na ca yācñāṃ cakāra sā</li>
    </ul>
    <ul id="R_3.045.010">
        <li id="R_3.045.010ab">mama bhartā mahātejā vayasā pañcaviṃśakaḥ</li>
        <li id="R_3.045.010cd">rāmeti prathito loke guṇavān satyavāk śuciḥ</li>
        <li id="R_3.045.010ef">viśālākṣo mahābāhuḥ sarvabhūtahite rataḥ</li>
    </ul>
    <ul id="R_3.045.011">
        <li id="R_3.045.011ab">abhiṣekāya tu pituḥ samīpaṃ rāmam āgatam</li>
        <li id="R_3.045.011cd">kaikeyī mama bhartāram ity uvāca drutaṃ vacaḥ</li>
    </ul>
    <ul id="R_3.045.012">
        <li id="R_3.045.012ab">tava pitrā samājñaptaṃ mamedaṃ śṛṇu rāghava</li>
        <li id="R_3.045.012cd">bharatāya pradātavyam idaṃ rājyam akaṇṭakam</li>
    </ul>
    <ul id="R_3.045.013">
        <li id="R_3.045.013ab">tvayā tu khalu vastavyaṃ nava varṣāṇi pañca ca</li>
        <li id="R_3.045.013cd">vane pravraja kākutstha pitaraṃ mocayānṛtāt</li>
    </ul>
    <ul id="R_3.045.014">
        <li id="R_3.045.014ab">tathety uvāca tāṃ rāmaḥ kaikeyīm akutobhayaḥ</li>
        <li id="R_3.045.014cd">cakāra tad vacas tasyā mama bhartā dṛḍhavrataḥ</li>
    </ul>
    <ul id="R_3.045.015">
        <li id="R_3.045.015ab">dadyān na pratigṛhṇīyāt satyabrūyān na cānṛtam</li>
        <li id="R_3.045.015cd">etad brāhmaṇa rāmasya vrataṃ dhruvam anuttamam</li>
    </ul>
    <ul id="R_3.045.016">
        <li id="R_3.045.016ab">tasya bhrātā tu vaimātro lakṣmaṇo nāma vīryavān</li>
        <li id="R_3.045.016cd">rāmasya puruṣavyāghraḥ sahāyaḥ samare 'rihā</li>
    </ul>
    <ul id="R_3.045.017">
        <li id="R_3.045.017ab">sa bhrātā lakṣmaṇo nāma dharmacārī dṛḍhavrataḥ</li>
        <li id="R_3.045.017cd">anvagacchad dhanuṣpāṇiḥ pravrajantaṃ mayā saha</li>
    </ul>
    <ul id="R_3.045.018">
        <li id="R_3.045.018ab">te vayaṃ pracyutā rājyāt kaileyyās tu kṛte trayaḥ</li>
        <li id="R_3.045.018cd">vicarāma dvijaśreṣṭha vanaṃ gambhīram ojasā</li>
    </ul>
    <ul id="R_3.045.019">
        <li id="R_3.045.019ab">samāśvasa muhūrtaṃ tu śakyaṃ vastum iha tvayā</li>
        <li id="R_3.045.019cd">āgamiṣyati me bhartā vanyam ādāya puṣkalam</li>
    </ul>
    <ul id="R_3.045.020">
        <li id="R_3.045.020ab">sa tvaṃ nāma ca gotraṃ ca kulam ācakṣva tattvataḥ</li>
        <li id="R_3.045.020cd">ekaś ca daṇḍakāraṇye kimarthaṃ carasi dvija</li>
    </ul>
    <ul id="R_3.045.021">
        <li id="R_3.045.021ab">evaṃ bruvatyāṃ sītāyāṃ rāmapatnyāṃ mahābalaḥ</li>
        <li id="R_3.045.021cd">pratyuvācottaraṃ tīvraṃ rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.045.022">
        <li id="R_3.045.022ab">yena vitrāsitā lokāḥ sadevāsurapannagāḥ</li>
        <li id="R_3.045.022cd">ahaṃ sa rāvaṇo nāma sīte rakṣogaṇeśvaraḥ</li>
    </ul>
    <ul id="R_3.045.023">
        <li id="R_3.045.023ab">tvāṃ tu kāñcanavarṇābhāṃ dṛṣṭvā kauśeyavāsinīm</li>
        <li id="R_3.045.023cd">ratiṃ svakeṣu dāreṣu nādhigacchāmy anindite</li>
    </ul>
    <ul id="R_3.045.024">
        <li id="R_3.045.024ab">bahvīnām uttamastrīṇām āhṛtānām itas tataḥ</li>
        <li id="R_3.045.024cd">sarvāsām eva bhadraṃ te mamāgramahiṣī bhava</li>
    </ul>
    <ul id="R_3.045.025">
        <li id="R_3.045.025ab">laṅkā nāma samudrasya madhye mama mahāpurī</li>
        <li id="R_3.045.025cd">sāgareṇa parikṣiptā niviṣṭā girimūrdhani</li>
    </ul>
    <ul id="R_3.045.026">
        <li id="R_3.045.026ab">tatra sīte mayā sārdhaṃ vaneṣu vicariṣyasi</li>
        <li id="R_3.045.026cd">na cāsyāraṇyavāsasya spṛhayiṣyasi bhāmini</li>
    </ul>
    <ul id="R_3.045.027">
        <li id="R_3.045.027ab">pañcadāsyaḥ sahasrāṇi sarvābharaṇabhūṣitāḥ</li>
        <li id="R_3.045.027cd">sīte paricariṣyanti bhāryā bhavasi me yadi</li>
    </ul>
    <ul id="R_3.045.028">
        <li id="R_3.045.028ab">rāvaṇenaivam uktā tu kupitā janakātmajā</li>
        <li id="R_3.045.028cd">pratyuvācānavadyāṅgī tam anādṛtya rākṣasaṃ</li>
    </ul>
    <ul id="R_3.045.029">
        <li id="R_3.045.029ab">mahāgirim ivākampyaṃ mahendrasadṛśaṃ patim</li>
        <li id="R_3.045.029cd">mahodadhim ivākṣobhyam ahaṃ rāmam anuvratā</li>
    </ul>
    <ul id="R_3.045.030">
        <li id="R_3.045.030ab">mahābāhuṃ mahoraskaṃ siṃhavikrāntagāminam</li>
        <li id="R_3.045.030cd">nṛsiṃhaṃ siṃhasaṃkāśam ahaṃ rāmam anuvratā</li>
    </ul>
    <ul id="R_3.045.031">
        <li id="R_3.045.031ab">pūrṇacandrānanaṃ vīraṃ rājavatsaṃ jitendriyam</li>
        <li id="R_3.045.031cd">pṛthukīrtiṃ mahābāhum ahaṃ rāmam anuvratā</li>
    </ul>
    <ul id="R_3.045.032">
        <li id="R_3.045.032ab">tvaṃ punar jambukaḥ siṃhīṃ mām ihecchasi durlabhām</li>
        <li id="R_3.045.032cd">nāhaṃ śakyā tvayā spraṣṭum ādityasya prabhā yathā</li>
    </ul>
    <ul id="R_3.045.033">
        <li id="R_3.045.033ab">pādapān kāñcanān nūnaṃ bahūn paśyasi mandabhāk</li>
        <li id="R_3.045.033cd">rāghavasya priyāṃ bhāryāṃ yas tvam icchasi rāvaṇa</li>
    </ul>
    <ul id="R_3.045.034">
        <li id="R_3.045.034ab">kṣudhitasya ca siṃhasya mṛgaśatros tarasvinaḥ</li>
        <li id="R_3.045.034cd">āśīviṣasya vadanād daṃṣṭrām ādātum icchasi</li>
    </ul>
    <ul id="R_3.045.035">
        <li id="R_3.045.035ab">mandaraṃ parvataśreṣṭhaṃ pāṇinā hartum icchasi</li>
        <li id="R_3.045.035cd">kālakūṭaṃ viṣaṃ pītvā svastimān gantum icchasi</li>
    </ul>
    <ul id="R_3.045.036">
        <li id="R_3.045.036ab">akṣisūcyā pramṛjasi jihvayā leḍhi ca kṣuram</li>
        <li id="R_3.045.036cd">rāghavasya priyāṃ bhāryām adhigantuṃ tvam icchasi</li>
    </ul>
    <ul id="R_3.045.037">
        <li id="R_3.045.037ab">avasajya śilāṃ kaṇṭhe samudraṃ tartum icchasi</li>
        <li id="R_3.045.037cd">sūryā candramasau cobhau prāṇibhyāṃ hartum icchasi</li>
        <li id="R_3.045.037ef">yo rāmasya priyāṃ bhāryāṃ pradharṣayitum icchasi</li>
    </ul>
    <ul id="R_3.045.038">
        <li id="R_3.045.038ab">agniṃ prajvalitaṃ dṛṣṭvā vastreṇāhartum icchasi</li>
        <li id="R_3.045.038cd">kalyāṇa vṛttāṃ rāmasya yo bhāryāṃ hartum icchasi</li>
    </ul>
    <ul id="R_3.045.039">
        <li id="R_3.045.039ab">ayomukhānāṃ śūlānām agre caritum icchasi</li>
        <li id="R_3.045.039cd">rāmasya sadṛśīṃ bhāryāṃ yo 'dhigantuṃ tvam icchasi</li>
    </ul>
    <ul id="R_3.045.040">
        <li id="R_3.045.040ab">
            <seg type="pāda" n="a">yad antaraṃ siṃhaśṛgālayor vane</seg>
            <seg type="pāda" n="b">yad antaraṃ syandanikāsamudrayoḥ</seg>
        </li>
        <li id="R_3.045.040cd">
            <seg type="pāda" n="c">surāgryasauvīrakayor yad antaraṃ</seg>
            <seg type="pāda" n="d">tad antaraṃ dāśarathes tavaiva ca</seg>
        </li>
    </ul>
    <ul id="R_3.045.041">
        <li id="R_3.045.041ab">
            <seg type="pāda" n="a">yad antaraṃ kāñcanasīsalohayor</seg>
            <seg type="pāda" n="b">yad antaraṃ candanavāripaṅkayoḥ</seg>
        </li>
        <li id="R_3.045.041cd">
            <seg type="pāda" n="c">yad antaraṃ hastibiḍālayor vane</seg>
            <seg type="pāda" n="d">tad antaraṃ daśarathes tavaiva ca</seg>
        </li>
    </ul>
    <ul id="R_3.045.042">
        <li id="R_3.045.042ab">
            <seg type="pāda" n="a">yad antaraṃ vāyasavainateyayor</seg>
            <seg type="pāda" n="b">yad antaraṃ madgumayūrayor api</seg>
        </li>
        <li id="R_3.045.042cd">
            <seg type="pāda" n="c">yad antaraṃ sārasagṛdhrayor vane</seg>
            <seg type="pāda" n="d">tad antaraṃ dāśarathes tavaiva ca</seg>
        </li>
    </ul>
    <ul id="R_3.045.043">
        <li id="R_3.045.043ab">
            <seg type="pāda" n="a">tasmin sahasrākṣasamaprabhāve</seg>
            <seg type="pāda" n="b">rāme sthite kārmukabāṇapāṇau</seg>
        </li>
        <li id="R_3.045.043cd">
            <seg type="pāda" n="c">hṛtāpi te 'haṃ na jarāṃ gamiṣye</seg>
            <seg type="pāda" n="d">vajraṃ yathā makṣikayāvagīrṇam</seg>
        </li>
    </ul>
    <ul id="R_3.045.044">
        <li id="R_3.045.044ab">
            <seg type="pāda" n="a">itīva tad vākyam aduṣṭabhāvā</seg>
            <seg type="pāda" n="b">sudṛṣṭam uktvā rajanīcaraṃ tam</seg>
        </li>
        <li id="R_3.045.044cd">
            <seg type="pāda" n="c">gātraprakampād vyathitā babhūva</seg>
            <seg type="pāda" n="d">vātoddhatā sā kadalīva tanvī</seg>
        </li>
    </ul>
    <ul id="R_3.045.045">
        <li id="R_3.045.045ab">
            <seg type="pāda" n="a">tāṃ vepamānām upalakṣya sītāṃ</seg>
            <seg type="pāda" n="b">sa rāvaṇo mṛtyusamaprabhāvaḥ</seg>
        </li>
        <li id="R_3.045.045cd">
            <seg type="pāda" n="c">kulaṃ balaṃ nāma ca karma cātmanaḥ</seg>
            <seg type="pāda" n="d">samācacakṣe bhayakāraṇārtham</seg>
        </li>
    </ul>
    <ul id="R_3.046.001">
        <li id="R_3.046.001ab">evaṃ bruvatyāṃ sītāyāṃ saṃrabdhaḥ paruṣākṣaram</li>
        <li id="R_3.046.001cd">lalāṭe bhrukuṭīṃ kṛtvā rāvaṇaḥ pratyuvāca ha</li>
    </ul>
    <ul id="R_3.046.002">
        <li id="R_3.046.002ab">bhrātā vaiśravaṇasyāhaṃ sāpatnyo varavarṇini</li>
        <li id="R_3.046.002cd">rāvaṇo nāma bhadraṃ te daśagrīvaḥ pratāpavān</li>
    </ul>
    <ul id="R_3.046.003">
        <li id="R_3.046.003ab">yasya devāḥ sagandharvāḥ piśācapatagoragāḥ</li>
        <li id="R_3.046.003cd">vidravanti bhayād bhītā mṛtyor iva sadā prajāḥ</li>
    </ul>
    <ul id="R_3.046.004">
        <li id="R_3.046.004ab">yena vaiśravaṇo bhrātā vaimātraḥ kāraṇāntare</li>
        <li id="R_3.046.004cd">dvandvam āsāditaḥ krodhād raṇe vikramya nirjitaḥ</li>
    </ul>
    <ul id="R_3.046.005">
        <li id="R_3.046.005ab">madbhayārtaḥ parityajya svam adhiṣṭhānam ṛddhimat</li>
        <li id="R_3.046.005cd">kailāsaṃ parvataśreṣṭham adhyāste naravāhanaḥ</li>
    </ul>
    <ul id="R_3.046.006">
        <li id="R_3.046.006ab">yasya tat puṣpakaṃ nāma vimānaṃ kāmagaṃ śubham</li>
        <li id="R_3.046.006cd">vīryād āvarjitaṃ bhadre yena yāmi vihāyasaṃ</li>
    </ul>
    <ul id="R_3.046.007">
        <li id="R_3.046.007ab">mama saṃjātaroṣasya mukhaṃ dṛṣṭvaiva maithili</li>
        <li id="R_3.046.007cd">vidravanti paritrastāḥ surāḥ śakrapurogamāḥ</li>
    </ul>
    <ul id="R_3.046.008">
        <li id="R_3.046.008ab">yatra tiṣṭhāmy ahaṃ tatra māruto vāti śaṅkitaḥ</li>
        <li id="R_3.046.008cd">tīvrāṃśuḥ śiśirāṃśuś ca bhayāt saṃpadyate raviḥ</li>
    </ul>
    <ul id="R_3.046.009">
        <li id="R_3.046.009ab">niṣkampapatrās taravo nadyaś ca stimitodakāḥ</li>
        <li id="R_3.046.009cd">bhavanti yatra yatrāhaṃ tiṣṭhāmi ca carāmi ca</li>
    </ul>
    <ul id="R_3.046.010">
        <li id="R_3.046.010ab">mama pāre samudrasya laṅkā nāma purī śubhā</li>
        <li id="R_3.046.010cd">saṃpūrṇā rākṣasair ghorair yathendrasyāmarāvatī</li>
    </ul>
    <ul id="R_3.046.011">
        <li id="R_3.046.011ab">prākāreṇa parikṣiptā pāṇḍureṇa virājitā</li>
        <li id="R_3.046.011cd">hemakakṣyā purī ramyā vaidūryamaya toraṇā</li>
    </ul>
    <ul id="R_3.046.012">
        <li id="R_3.046.012ab">hastyaśvarathasaṃbhādhā tūryanādavināditā</li>
        <li id="R_3.046.012cd">sarvakāmaphalair vṛkṣaiḥ saṃkulodyānaśobhitā</li>
    </ul>
    <ul id="R_3.046.013">
        <li id="R_3.046.013ab">tatra tvaṃ vasatī sīte rājaputri mayā saha</li>
        <li id="R_3.046.013cd">na sramiṣyasi nārīṇāṃ mānuṣīṇāṃ manasvini</li>
    </ul>
    <ul id="R_3.046.014">
        <li id="R_3.046.014ab">bhuñjānā mānuṣān bhogān divyāṃś ca varavarṇini</li>
        <li id="R_3.046.014cd">na smariṣyasi rāmasya mānuṣasya gatāyuṣaḥ</li>
    </ul>
    <ul id="R_3.046.015">
        <li id="R_3.046.015ab">sthāpayitvā priyaṃ putraṃ rājñā daśarathena yaḥ</li>
        <li id="R_3.046.015cd">mandavīryaḥ suto jyeṣṭhas tataḥ prasthāpito vanam</li>
    </ul>
    <ul id="R_3.046.016">
        <li id="R_3.046.016ab">tena kiṃ bhraṣṭarājyena rāmeṇa gatacetasā</li>
        <li id="R_3.046.016cd">kariṣyasi viśālākṣi tāpasena tapasvinā</li>
    </ul>
    <ul id="R_3.046.017">
        <li id="R_3.046.017ab">sarvarākṣasabhartāraṃ kāmāt svayam ihāgatam</li>
        <li id="R_3.046.017cd">na manmathaśarāviṣṭaṃ pratyākhyātuṃ tvam arhasi</li>
    </ul>
    <ul id="R_3.046.018">
        <li id="R_3.046.018ab">pratyākhyāya hi māṃ bhīru paritāpaṃ gamiṣyasi</li>
        <li id="R_3.046.018cd">caraṇenābhihatyeva purūravasam urvaśī</li>
    </ul>
    <ul id="R_3.046.019">
        <li id="R_3.046.019ab">evam uktā tu vaidehī kruddhā saṃraktalocanā</li>
        <li id="R_3.046.019cd">abravīt paruṣaṃ vākyaṃ rahite rākṣasādhipam</li>
    </ul>
    <ul id="R_3.046.020">
        <li id="R_3.046.020ab">kathaṃ vaiśravaṇaṃ devaṃ sarvabhūtanamaskṛtam</li>
        <li id="R_3.046.020cd">bhrātaraṃ vyapadiśya tvam aśubhaṃ kartum icchasi</li>
    </ul>
    <ul id="R_3.046.021">
        <li id="R_3.046.021ab">avaśyaṃ vinaśiṣyanti sarve rāvaṇa rākṣasāḥ</li>
        <li id="R_3.046.021cd">yeṣāṃ tvaṃ karkaśo rājā durbuddhir ajitendriyaḥ</li>
    </ul>
    <ul id="R_3.046.022">
        <li id="R_3.046.022ab">apahṛtya śacīṃ bhāryāṃ śakyam indrasya jīvitum</li>
        <li id="R_3.046.022cd">na tu rāmasya bhāryāṃ mām apanīyāsti jīvitam</li>
    </ul>
    <ul id="R_3.046.023">
        <li id="R_3.046.023ab">
            <seg type="pāda" n="a">jīvec ciraṃ vajradharasya hastāc</seg>
            <seg type="pāda" n="b">chacīṃ pradhṛṣyāpratirūparūpām</seg>
        </li>
        <li id="R_3.046.023cd">
            <seg type="pāda" n="c">na mādṛśīṃ rākṣasadharṣayitvā</seg>
            <seg type="pāda" n="d">pītāmṛtasyāpi tavāsti mokṣaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.047.001">
        <li id="R_3.047.001ab">sītāyā vacanaṃ śrutvā daśagrīvaḥ pratāpavān</li>
        <li id="R_3.047.001cd">haste hastaṃ samāhatya cakāra sumahad vapuḥ</li>
    </ul>
    <ul id="R_3.047.002">
        <li id="R_3.047.002ab">sa maithilīṃ punar vākyaṃ babhāṣe ca tato bhṛśam</li>
        <li id="R_3.047.002cd">nonmattayā śrutau manye mama vīryaparākramau</li>
    </ul>
    <ul id="R_3.047.003">
        <li id="R_3.047.003ab">udvaheyaṃ bhujābhyāṃ tu medinīm ambare sthitaḥ</li>
        <li id="R_3.047.003cd">āpibeyaṃ samudraṃ ca mṛtyuṃ hanyāṃ raṇe sthitaḥ</li>
    </ul>
    <ul id="R_3.047.004">
        <li id="R_3.047.004ab">arkaṃ rundhyāṃ śarais tīkṣṇair vibhindyāṃ hi mahītalam</li>
        <li id="R_3.047.004cd">kāmarūpiṇam unmatte paśya māṃ kāmadaṃ patim</li>
    </ul>
    <ul id="R_3.047.005">
        <li id="R_3.047.005ab">evam uktavatas tasya rāvaṇasya śikhiprabhe</li>
        <li id="R_3.047.005cd">kruddhasya hariparyante rakte netre babhūvatuḥ</li>
    </ul>
    <ul id="R_3.047.006">
        <li id="R_3.047.006ab">sadyaḥ saumyaṃ parityajya bhikṣurūpaṃ sa rāvaṇaḥ</li>
        <li id="R_3.047.006cd">svaṃ rūpaṃ kālarūpābhaṃ bheje vaiśravaṇānujaḥ</li>
    </ul>
    <ul id="R_3.047.007">
        <li id="R_3.047.007ab">saṃraktanayanaḥ śrīmāṃs taptakāñcanakuṇḍalaḥ</li>
        <li id="R_3.047.007cd">daśāsyaḥ kārmukī bāṇī babhūva kṣaṇadācaraḥ</li>
    </ul>
    <ul id="R_3.047.008">
        <li id="R_3.047.008ab">sa parivrājakacchadma mahākāyo vihāya tat</li>
        <li id="R_3.047.008cd">pratipede svakaṃ rūpaṃ rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.047.009">
        <li id="R_3.047.009ab">saṃraktanayanaḥ krodhāj jīmūtanicayaprabhaḥ</li>
        <li id="R_3.047.009cd">raktāmbaradharas tasthau strīratnaṃ prekṣya maithilīm</li>
    </ul>
    <ul id="R_3.047.010">
        <li id="R_3.047.010ab">sa tām asitakeśāntāṃ bhāskarasya prabhām iva</li>
        <li id="R_3.047.010cd">vasanābharaṇopetāṃ maithilīṃ rāvaṇo 'bravīt</li>
    </ul>
    <ul id="R_3.047.011">
        <li id="R_3.047.011ab">triṣu lokeṣu vikhyātaṃ yadi bhartāram icchasi</li>
        <li id="R_3.047.011cd">mām āśraya varārohe tavāhaṃ sadṛśaḥ patiḥ</li>
    </ul>
    <ul id="R_3.047.012">
        <li id="R_3.047.012ab">māṃ bhajasva cirāya tvam ahaṃ ślāghyas tava priyaḥ</li>
        <li id="R_3.047.012cd">naiva cāhaṃ kva cid bhadre kariṣye tava vipriyam</li>
        <li id="R_3.047.012ef">tyajyatāṃ mānuṣo bhāvo mayi bhāvaḥ praṇīyatām</li>
    </ul>
    <ul id="R_3.047.013">
        <li id="R_3.047.013ab">rājyāc cyutam asiddhārthaṃ rāmaṃ parimitāyuṣam</li>
        <li id="R_3.047.013cd">kair guṇair anuraktāsi mūḍhe paṇḍitamānini</li>
    </ul>
    <ul id="R_3.047.014">
        <li id="R_3.047.014ab">yaḥ striyā vacanād rājyaṃ vihāya sasuhṛjjanam</li>
        <li id="R_3.047.014cd">asmin vyālānucarite vane vasati durmatiḥ</li>
    </ul>
    <ul id="R_3.047.015">
        <li id="R_3.047.015ab">ity uktvā maithilīṃ vākyaṃ priyārhāṃ priyavādinīm</li>
        <li id="R_3.047.015cd">jagrāha rāvaṇaḥ sītāṃ budhaḥ khe rohiṇīm iva</li>
    </ul>
    <ul id="R_3.047.016">
        <li id="R_3.047.016ab">vāmena sītāṃ padmākṣīṃ mūrdhajeṣu kareṇa saḥ</li>
        <li id="R_3.047.016cd">ūrvos tu dakṣiṇenaiva parijagrāha pāṇinā</li>
    </ul>
    <ul id="R_3.047.017">
        <li id="R_3.047.017ab">taṃ dṛṣṭvā giriśṛṅgābhaṃ tīkṣṇadaṃṣṭraṃ mahābhujam</li>
        <li id="R_3.047.017cd">prādravan mṛtyusaṃkāśaṃ bhayārtā vanadevatāḥ</li>
    </ul>
    <ul id="R_3.047.018">
        <li id="R_3.047.018ab">sa ca māyāmayo divyaḥ kharayuktaḥ kharasvanaḥ</li>
        <li id="R_3.047.018cd">pratyadṛśyata hemāṅgo rāvaṇasya mahārathaḥ</li>
    </ul>
    <ul id="R_3.047.019">
        <li id="R_3.047.019ab">tatas tāṃ paruṣair vākyair abhitarjya mahāsvanaḥ</li>
        <li id="R_3.047.019cd">aṅkenādāya vaidehīṃ ratham āropayat tadā</li>
    </ul>
    <ul id="R_3.047.020">
        <li id="R_3.047.020ab">sā gṛhītāticukrośa rāvaṇena yaśasvinī</li>
        <li id="R_3.047.020cd">rāmeti sītā duḥkhārtā rāmaṃ dūragataṃ vane</li>
    </ul>
    <ul id="R_3.047.021">
        <li id="R_3.047.021ab">tām akāmāṃ sa kāmārtaḥ pannagendravadhūm iva</li>
        <li id="R_3.047.021cd">viveṣṭamānām ādāya utpapāthātha rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.047.022">
        <li id="R_3.047.022ab">tataḥ sā rākṣasendreṇa hriyamāṇā vihāyasā</li>
        <li id="R_3.047.022cd">bhṛśaṃ cukrośa matteva bhrāntacittā yathāturā</li>
    </ul>
    <ul id="R_3.047.023">
        <li id="R_3.047.023ab">hā lakṣmaṇa mahābāho gurucittaprasādaka</li>
        <li id="R_3.047.023cd">hriyamāṇāṃ na jānīṣe rakṣasā kāmarūpiṇā</li>
    </ul>
    <ul id="R_3.047.024">
        <li id="R_3.047.024ab">jīvitaṃ sukham arthāṃś ca dharmahetoḥ parityajan</li>
        <li id="R_3.047.024cd">hriyamāṇām adharmeṇa māṃ rāghava na paśyasi</li>
    </ul>
    <ul id="R_3.047.025">
        <li id="R_3.047.025ab">nanu nāmāvinītānāṃ vinetāsi paraṃtapa</li>
        <li id="R_3.047.025cd">katham evaṃvidhaṃ pāpaṃ na tvaṃ śādhi hi rāvaṇam</li>
    </ul>
    <ul id="R_3.047.026">
        <li id="R_3.047.026ab">nanu sadyo 'vinītasya dṛśyate karmaṇaḥ phalam</li>
        <li id="R_3.047.026cd">kālo 'py aṅgī bhavaty atra sasyānām iva paktaye</li>
    </ul>
    <ul id="R_3.047.027">
        <li id="R_3.047.027ab">sa karma kṛtavān etat kālopahatacetanaḥ</li>
        <li id="R_3.047.027cd">jīvitāntakaraṃ ghoraṃ rāmād vyasanam āpnuhi</li>
    </ul>
    <ul id="R_3.047.028">
        <li id="R_3.047.028ab">hantedānīṃ sakāmā tu kaikeyī bāndhavaiḥ saha</li>
        <li id="R_3.047.028cd">hriyeyaṃ dharmakāmasya dharmapatnī yaśasvinaḥ</li>
    </ul>
    <ul id="R_3.047.029">
        <li id="R_3.047.029ab">āmantraye janasthānaṃ karṇikārāṃś ca puṣpitān</li>
        <li id="R_3.047.029cd">kṣipraṃ rāmāya śaṃsadhvaṃ sītāṃ harati rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.047.030">
        <li id="R_3.047.030ab">mālyavantaṃ śikhariṇaṃ vande prasravaṇaṃ girim</li>
        <li id="R_3.047.030cd">kṣipraṃ rāmāya śaṃsadhvaṃ sītāṃ harati rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.047.031">
        <li id="R_3.047.031ab">haṃsasārasasaṃghuṣṭāṃ vande godāvarīṃ nadīm</li>
        <li id="R_3.047.031cd">kṣipraṃ rāmāya śaṃsadhvaṃ sītāṃ harati rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.047.032">
        <li id="R_3.047.032ab">daivatāni ca yānty asmin vane vividhapādape</li>
        <li id="R_3.047.032cd">namaskaromy ahaṃ tebhyo bhartuḥ śaṃsata māṃ hṛtām</li>
    </ul>
    <ul id="R_3.047.033">
        <li id="R_3.047.033ab">yāni kāni cid apy atra sattvāni nivasanty uta</li>
        <li id="R_3.047.033cd">sarvāṇi śaraṇaṃ yāmi mṛgapakṣigaṇān api</li>
    </ul>
    <ul id="R_3.047.034">
        <li id="R_3.047.034ab">hriyamāṇāṃ priyāṃ bhartuḥ prāṇebhyo 'pi garīyasīm</li>
        <li id="R_3.047.034cd">vivaśāpahṛtā sītā rāvaṇeneti śaṃsata</li>
    </ul>
    <ul id="R_3.047.035">
        <li id="R_3.047.035ab">viditvā māṃ mahābāhur amutrāpi mahābalaḥ</li>
        <li id="R_3.047.035cd">āneṣyati parākramya vaivasvatahṛtām api</li>
    </ul>
    <ul id="R_3.047.036">
        <li id="R_3.047.036ab">rāmāya tu yathātattvaṃ jaṭāyo haraṇaṃ mama</li>
        <li id="R_3.047.036cd">lakṣmaṇāya ca tat sarvam ākhyātavyam aśeṣataḥ</li>
    </ul>
    <ul id="R_3.048.001">
        <li id="R_3.048.001ab">taṃ śabdam avasuptasya jaṭāyur atha śuśruve</li>
        <li id="R_3.048.001cd">niraikṣad rāvaṇaṃ kṣipraṃ vaidehīṃ ca dadarśa saḥ</li>
    </ul>
    <ul id="R_3.048.002">
        <li id="R_3.048.002ab">tataḥ parvatakūṭābhas tīkṣṇatuṇḍaḥ khagottamaḥ</li>
        <li id="R_3.048.002cd">vanaspatigataḥ śrīmān vyājahāra śubhāṃ giram</li>
    </ul>
    <ul id="R_3.048.003">
        <li id="R_3.048.003ab">daśagrīvasthito dharme purāṇe satyasaṃśrayaḥ</li>
        <li id="R_3.048.003cd">jaṭāyur nāma nāmnāhaṃ gṛdhrarājo mahābalaḥ</li>
    </ul>
    <ul id="R_3.048.004">
        <li id="R_3.048.004ab">rājā sarvasya lokasya mahendravaruṇopamaḥ</li>
        <li id="R_3.048.004cd">lokānāṃ ca hite yukto rāmo daśarathātmajaḥ</li>
    </ul>
    <ul id="R_3.048.005">
        <li id="R_3.048.005ab">tasyaiṣā lokanāthasya dharmapatnī yaśasvinī</li>
        <li id="R_3.048.005cd">sītā nāma varārohā yāṃ tvaṃ hartum ihecchasi</li>
    </ul>
    <ul id="R_3.048.006">
        <li id="R_3.048.006ab">kathaṃ rājā sthito dharme paradārān parāmṛśet</li>
        <li id="R_3.048.006cd">rakṣaṇīyā viśeṣeṇa rājadārā mahābalaḥ</li>
        <li id="R_3.048.006ef">nivartaya matiṃ nīcāṃ paradārābhimarśanam</li>
    </ul>
    <ul id="R_3.048.007">
        <li id="R_3.048.007ab">na tat samācared dhīro yat paro 'sya vigarhayet</li>
        <li id="R_3.048.007cd">yathātmanas tathānyeṣāṃ dārā rakṣyā vimarśanāt</li>
    </ul>
    <ul id="R_3.048.008">
        <li id="R_3.048.008ab">arthaṃ vā yadi vā kāmaṃ śiṣṭāḥ śāstreṣv anāgatam</li>
        <li id="R_3.048.008cd">vyavasyanty anu rājānaṃ dharmaṃ paurastyanandana</li>
    </ul>
    <ul id="R_3.048.009">
        <li id="R_3.048.009ab">rājā dharmaś ca kāmaś ca dravyāṇāṃ cottamo nidhiḥ</li>
        <li id="R_3.048.009cd">dharmaḥ śubhaṃ vā pāpaṃ vā rājamūlaṃ pravartate</li>
    </ul>
    <ul id="R_3.048.010">
        <li id="R_3.048.010ab">pāpasvabhāvaś capalaḥ kathaṃ tvaṃ rakṣasāṃ vara</li>
        <li id="R_3.048.010cd">aiśvaryam abhisaṃprāpto vimānam iva duṣkṛtī</li>
    </ul>
    <ul id="R_3.048.011">
        <li id="R_3.048.011ab">kāmasvabhāvo yo yasya na sa śakyaḥ pramārjitum</li>
        <li id="R_3.048.011cd">na hi duṣṭātmanām ārya mā vasaty ālaye ciram</li>
    </ul>
    <ul id="R_3.048.012">
        <li id="R_3.048.012ab">viṣaye vā pure vā te yadā rāmo mahābalaḥ</li>
        <li id="R_3.048.012cd">nāparādhyati dharmātmā kathaṃ tasyāparādhyasi</li>
    </ul>
    <ul id="R_3.048.013">
        <li id="R_3.048.013ab">yadi śūrpaṇakhāhetor janasthānagataḥ kharaḥ</li>
        <li id="R_3.048.013cd">ativṛtto hataḥ pūrvaṃ rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_3.048.014">
        <li id="R_3.048.014ab">atra brūhi yathāsatyaṃ ko rāmasya vyatikramaḥ</li>
        <li id="R_3.048.014cd">yasya tvaṃ lokanāthasya hṛtvā bhāryāṃ gamiṣyasi</li>
    </ul>
    <ul id="R_3.048.015">
        <li id="R_3.048.015ab">kṣipraṃ visṛja vaidehīṃ mā tvā ghoreṇa cakṣuṣā</li>
        <li id="R_3.048.015cd">dahed dahana bhūtena vṛtram indrāśanir yathā</li>
    </ul>
    <ul id="R_3.048.016">
        <li id="R_3.048.016ab">sarpam āśīviṣaṃ baddhvā vastrānte nāvabudhyase</li>
        <li id="R_3.048.016cd">grīvāyāṃ pratimuktaṃ ca kālapāśaṃ na paśyasi</li>
    </ul>
    <ul id="R_3.048.017">
        <li id="R_3.048.017ab">sa bhāraḥ saumya bhartavyo yo naraṃ nāvasādayet</li>
        <li id="R_3.048.017cd">tad annam upabhoktavyaṃ jīryate yad anāmayam</li>
    </ul>
    <ul id="R_3.048.018">
        <li id="R_3.048.018ab">yat kṛtvā na bhaved dharmo na kīrtir na yaśo bhuvi</li>
        <li id="R_3.048.018cd">śarīrasya bhavet khedaḥ kas tat karma samācaret</li>
    </ul>
    <ul id="R_3.048.019">
        <li id="R_3.048.019ab">ṣaṣṭivarṣasahasrāṇi mama jātasya rāvaṇa</li>
        <li id="R_3.048.019cd">pitṛpaitāmahaṃ rājyaṃ yathāvad anutiṣṭhataḥ</li>
    </ul>
    <ul id="R_3.048.020">
        <li id="R_3.048.020ab">vṛddho 'haṃ tvaṃ yuvā dhanvī sarathaḥ kavacī śarī</li>
        <li id="R_3.048.020cd">tathāpy ādāya vaidehīṃ kuśalī na gamiṣyasi</li>
    </ul>
    <ul id="R_3.048.021">
        <li id="R_3.048.021ab">na śaktas tvaṃ balād dhartuṃ vaidehīṃ mama paśyataḥ</li>
        <li id="R_3.048.021cd">hetubhir nyāyasaṃyuktair dhruvāṃ vedaśrutīm iva</li>
    </ul>
    <ul id="R_3.048.022">
        <li id="R_3.048.022ab">yudhyasva yadi śūro 'si muhūrtaṃ tiṣṭha rāvaṇa</li>
        <li id="R_3.048.022cd">śayiṣyase hato bhūmau yathāpūrvaṃ kharas tathā</li>
    </ul>
    <ul id="R_3.048.023">
        <li id="R_3.048.023ab">asakṛt saṃyuge yena nihatā daityadānavāḥ</li>
        <li id="R_3.048.023cd">nacirāc cīravāsās tvāṃ rāmo yudhi vadhiṣyati</li>
    </ul>
    <ul id="R_3.048.024">
        <li id="R_3.048.024ab">kiṃ nu śakyaṃ mayā kartuṃ gatau dūraṃ nṛpātmajau</li>
        <li id="R_3.048.024cd">kṣipraṃ tvaṃ naśyase nīca tayor bhīto na saṃśayaḥ</li>
    </ul>
    <ul id="R_3.048.025">
        <li id="R_3.048.025ab">na hi me jīvamānasya nayiṣyasi śubhām imām</li>
        <li id="R_3.048.025cd">sītāṃ kamalapatrākṣīṃ rāmasya mahaṣīṃ priyām</li>
    </ul>
    <ul id="R_3.048.026">
        <li id="R_3.048.026ab">avaśyaṃ tu mayā kāryaṃ priyaṃ tasya mahātmanaḥ</li>
        <li id="R_3.048.026cd">jīvitenāpi rāmasya tathā daśarathasya ca</li>
    </ul>
    <ul id="R_3.048.027">
        <li id="R_3.048.027ab">tiṣṭha tiṣṭha daśagrīva muhūrtaṃ paśya rāvaṇa</li>
        <li id="R_3.048.027cd">yuddhātithyaṃ pradāsyāmi yathāprāṇaṃ niśācara</li>
        <li id="R_3.048.027ef">vṛntād iva phalaṃ tvāṃ tu pātayeyaṃ rathottamāt</li>
    </ul>
    <ul id="R_3.049.001">
        <li id="R_3.049.001ab">ity uktasya yathānyāyaṃ rāvaṇasya jaṭāyuṣā</li>
        <li id="R_3.049.001cd">kruddhasyāgninibhāḥ sarvā rejur viṃśatidṛṣṭayaḥ</li>
    </ul>
    <ul id="R_3.049.002">
        <li id="R_3.049.002ab">saṃraktanayanaḥ kopāt taptakāñcanakuṇḍalaḥ</li>
        <li id="R_3.049.002cd">rākṣasendro 'bhidudrāva patagendram amarṣaṇaḥ</li>
    </ul>
    <ul id="R_3.049.003">
        <li id="R_3.049.003ab">sa saṃprahāras tumulas tayos tasmin mahāvane</li>
        <li id="R_3.049.003cd">babhūva vātoddhatayor meghayor gagane yathā</li>
    </ul>
    <ul id="R_3.049.004">
        <li id="R_3.049.004ab">tad babhūvādbhutaṃ yuddhaṃ gṛdhrarākṣasayos tadā</li>
        <li id="R_3.049.004cd">sapakṣayor mālyavator mahāparvatayor iva</li>
    </ul>
    <ul id="R_3.049.005">
        <li id="R_3.049.005ab">tato nālīkanārācais tīkṣṇāgraiś ca vikarṇibhiḥ</li>
        <li id="R_3.049.005cd">abhyavarṣan mahāghorair gṛdhrarājaṃ mahābalaḥ</li>
    </ul>
    <ul id="R_3.049.006">
        <li id="R_3.049.006ab">sa tāni śarajālāni gṛdhraḥ patraratheśvaraḥ</li>
        <li id="R_3.049.006cd">jaṭāyuḥ pratijagrāha rāvaṇāstrāṇi saṃyuge</li>
    </ul>
    <ul id="R_3.049.007">
        <li id="R_3.049.007ab">tasya tīkṣṇanakhābhyāṃ tu caraṇābhyāṃ mahābalaḥ</li>
        <li id="R_3.049.007cd">cakāra bahudhā gātre vraṇān patagasattamaḥ</li>
    </ul>
    <ul id="R_3.049.008">
        <li id="R_3.049.008ab">atha krodhād daśagrīvo jagrāha daśamārgaṇān</li>
        <li id="R_3.049.008cd">mṛtyudaṇḍanibhān ghorāñ śatrumardanakāṅkṣayā</li>
    </ul>
    <ul id="R_3.049.009">
        <li id="R_3.049.009ab">sa tair bāṇair mahāvīryaḥ pūrṇamuktair ajihmagaiḥ</li>
        <li id="R_3.049.009cd">bibheda niśitais tīkṣṇair gṛdhraṃ ghoraiḥ śilīmukhaiḥ</li>
    </ul>
    <ul id="R_3.049.010">
        <li id="R_3.049.010ab">sa rākṣasarathe paśyañ jānakīṃ bāṣpalocanām</li>
        <li id="R_3.049.010cd">acintayitvā bāṇāṃs tān rākṣasaṃ samabhidravat</li>
    </ul>
    <ul id="R_3.049.011">
        <li id="R_3.049.011ab">tato 'sya saśaraṃ cāpaṃ muktāmaṇivibhūṣitam</li>
        <li id="R_3.049.011cd">caraṇābhyāṃ mahātejā babhañja patageśvaraḥ</li>
    </ul>
    <ul id="R_3.049.012">
        <li id="R_3.049.012ab">tac cāgnisadṛśaṃ dīptaṃ rāvaṇasya śarāvaram</li>
        <li id="R_3.049.012cd">pakṣābhyāṃ ca mahātejā vyadhunot patageśvaraḥ</li>
    </ul>
    <ul id="R_3.049.013">
        <li id="R_3.049.013ab">kāñcanoraśchadān divyān piśācavadanān kharān</li>
        <li id="R_3.049.013cd">tāṃś cāsya javasaṃpannāñ jaghāna samare balī</li>
    </ul>
    <ul id="R_3.049.014">
        <li id="R_3.049.014ab">varaṃ triveṇusaṃpannaṃ kāmagaṃ pāvakārciṣam</li>
        <li id="R_3.049.014cd">maṇihemavicitrāṅgaṃ babhañja ca mahāratham</li>
        <li id="R_3.049.014ef">pūrṇacandrapratīkāśaṃ chatraṃ ca vyajanaiḥ saha</li>
    </ul>
    <ul id="R_3.049.015">
        <li id="R_3.049.015ab">sa bhagnadhanvā viratho hatāśvo hatasārathiḥ</li>
        <li id="R_3.049.015cd">aṅkenādāya vaidehīṃ papāta bhuvi rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.049.016">
        <li id="R_3.049.016ab">dṛṣṭvā nipatitaṃ bhūmau rāvaṇaṃ bhagnavāhanam</li>
        <li id="R_3.049.016cd">sādhu sādhv iti bhūtāni gṛdhrarājam apūjayan</li>
    </ul>
    <ul id="R_3.049.017">
        <li id="R_3.049.017ab">pariśrāntaṃ tu taṃ dṛṣṭvā jarayā pakṣiyūthapam</li>
        <li id="R_3.049.017cd">utpapāta punar hṛṣṭo maithilīṃ gṛhya rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.049.018">
        <li id="R_3.049.018ab">taṃ prahṛṣṭaṃ nidhāyāṅke gacchantaṃ janakātmajām</li>
        <li id="R_3.049.018cd">gṛdhrarājaḥ samutpatya jaṭāyur idam abravīt</li>
    </ul>
    <ul id="R_3.049.019">
        <li id="R_3.049.019ab">vajrasaṃsparśabāṇasya bhāryāṃ rāmasya rāvaṇa</li>
        <li id="R_3.049.019cd">alpabuddhe harasy enāṃ vadhāya khalu rakṣasām</li>
    </ul>
    <ul id="R_3.049.020">
        <li id="R_3.049.020ab">samitrabandhuḥ sāmātyaḥ sabalaḥ saparicchadaḥ</li>
        <li id="R_3.049.020cd">viṣapānaṃ pibasy etat pipāsita ivodakam</li>
    </ul>
    <ul id="R_3.049.021">
        <li id="R_3.049.021ab">anubandham ajānantaḥ karmaṇām avicakṣaṇāḥ</li>
        <li id="R_3.049.021cd">śīghram eva vinaśyanti yathā tvaṃ vinaśiṣyasi</li>
    </ul>
    <ul id="R_3.049.022">
        <li id="R_3.049.022ab">baddhas tvaṃ kālapāśena kva gatas tasya mokṣyase</li>
        <li id="R_3.049.022cd">vadhāya baḍiśaṃ gṛhya sāmiṣaṃ jalajo yathā</li>
    </ul>
    <ul id="R_3.049.023">
        <li id="R_3.049.023ab">na hi jātu durādharṣau kākutsthau tava rāvaṇa</li>
        <li id="R_3.049.023cd">dharṣaṇaṃ cāśramasyāsya kṣamiṣyete tu rāghavau</li>
    </ul>
    <ul id="R_3.049.024">
        <li id="R_3.049.024ab">yathā tvayā kṛtaṃ karma bhīruṇā lokagarhitam</li>
        <li id="R_3.049.024cd">taskarācarito mārgo naiṣa vīraniṣevitaḥ</li>
    </ul>
    <ul id="R_3.049.025">
        <li id="R_3.049.025ab">yudhyasva yadi śūro 'si muhūrtaṃ tiṣṭha rāvaṇa</li>
        <li id="R_3.049.025cd">śayiṣyase hato bhūmau yathā bhrātā kharas tathā</li>
    </ul>
    <ul id="R_3.049.026">
        <li id="R_3.049.026ab">paretakāle puruṣo yat karma pratipadyate</li>
        <li id="R_3.049.026cd">vināśāyātmano 'dharmyaṃ pratipanno 'si karma tat</li>
    </ul>
    <ul id="R_3.049.027">
        <li id="R_3.049.027ab">pāpānubandho vai yasya karmaṇaḥ ko nu tat pumān</li>
        <li id="R_3.049.027cd">kurvīta lokādhipatiḥ svayambhūr bhagavān api</li>
    </ul>
    <ul id="R_3.049.028">
        <li id="R_3.049.028ab">evam uktvā śubhaṃ vākyaṃ jaṭāyus tasya rakṣasaḥ</li>
        <li id="R_3.049.028cd">nipapāta bhṛśaṃ pṛṣṭhe daśagrīvasya vīryavān</li>
    </ul>
    <ul id="R_3.049.029">
        <li id="R_3.049.029ab">taṃ gṛhītvā nakhais tīkṣṇair virarāda samantataḥ</li>
        <li id="R_3.049.029cd">adhirūḍho gajārohi yathā syād duṣṭavāraṇam</li>
    </ul>
    <ul id="R_3.049.030">
        <li id="R_3.049.030ab">virarāda nakhair asya tuṇḍaṃ pṛṣṭhe samarpayan</li>
        <li id="R_3.049.030cd">keśāṃś cotpāṭayām āsa nakhapakṣamukhāyudhaḥ</li>
    </ul>
    <ul id="R_3.049.031">
        <li id="R_3.049.031ab">sa tathā gṛdhrarājena kliśyamāno muhur muhuḥ</li>
        <li id="R_3.049.031cd">amarṣasphuritauṣṭhaḥ san prākampata sa rākṣasaḥ</li>
    </ul>
    <ul id="R_3.049.032">
        <li id="R_3.049.032ab">saṃpariṣvajya vaidehīṃ vāmenāṅkena rāvaṇaḥ</li>
        <li id="R_3.049.032cd">talenābhijaghānārto jaṭāyuṃ krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_3.049.033">
        <li id="R_3.049.033ab">jaṭāyus tam atikramya tuṇḍenāsya kharādhipaḥ</li>
        <li id="R_3.049.033cd">vāmabāhūn daśa tadā vyapāharad ariṃdamaḥ</li>
    </ul>
    <ul id="R_3.049.034">
        <li id="R_3.049.034ab">tataḥ kruddho daśakrīvaḥ sītām utsṛjya vīryavān</li>
        <li id="R_3.049.034cd">muṣṭibhyāṃ caraṇābhyāṃ ca gṛdhrarājam apothayat</li>
    </ul>
    <ul id="R_3.049.035">
        <li id="R_3.049.035ab">tato muhūrtaṃ saṃgrāmo babhūvātulavīryayoḥ</li>
        <li id="R_3.049.035cd">rākṣasānāṃ ca mukhyasya pakṣiṇāṃ pravarasya ca</li>
    </ul>
    <ul id="R_3.049.036">
        <li id="R_3.049.036ab">tasya vyāyacchamānasya rāmasyārthe 'tha rāvaṇaḥ</li>
        <li id="R_3.049.036cd">pakṣau pādau ca pārśvau ca khaḍgam uddhṛtya so 'cchinat</li>
    </ul>
    <ul id="R_3.049.037">
        <li id="R_3.049.037ab">sa chinnapakṣaḥ sahasā rakṣasā raudrakarmaṇā</li>
        <li id="R_3.049.037cd">nipapāta hato gṛdhro dharaṇyām alpajīvitaḥ</li>
    </ul>
    <ul id="R_3.049.038">
        <li id="R_3.049.038ab">taṃ dṛṣṭvā patitaṃ bhūmau kṣatajārdraṃ jaṭāyuṣam</li>
        <li id="R_3.049.038cd">abhyadhāvata vaidehī svabandhum iva duḥkhitā</li>
    </ul>
    <ul id="R_3.049.039">
        <li id="R_3.049.039ab">
            <seg type="pāda" n="a">taṃ nīlajīmūtanikāśakalpaṃ</seg>
            <seg type="pāda" n="b">supāṇḍuroraskam udāravīryam</seg>
        </li>
        <li id="R_3.049.039cd">
            <seg type="pāda" n="c">dadarśa laṅkādhipatiḥ pṛthivyāṃ</seg>
            <seg type="pāda" n="d">jaṭāyuṣaṃ śāntam ivāgnidāvam</seg>
        </li>
    </ul>
    <ul id="R_3.049.040">
        <li id="R_3.049.040ab">
            <seg type="pāda" n="a">tatas tu taṃ patrarathaṃ mahītale</seg>
            <seg type="pāda" n="b">nipātitaṃ rāvaṇavegamarditam</seg>
        </li>
        <li id="R_3.049.040cd">
            <seg type="pāda" n="c">punaḥ pariṣvajya śaśiprabhānanā</seg>
            <seg type="pāda" n="d">ruroda sītā janakātmajā tadā</seg>
        </li>
    </ul>
    <ul id="R_3.050.001">
        <li id="R_3.050.001ab">tam alpajīvitaṃ bhūmau sphurantaṃ rākṣasādhipaḥ</li>
        <li id="R_3.050.001cd">dadarśa gṛdhraṃ patitaṃ samīpe rāghavāśramāt</li>
    </ul>
    <ul id="R_3.050.002">
        <li id="R_3.050.002ab">sā tu tārādhipamukhī rāvaṇena samīkṣya tam</li>
        <li id="R_3.050.002cd">gṛdhrarājaṃ vinihataṃ vilalāpa suduḥkhitā</li>
    </ul>
    <ul id="R_3.050.003">
        <li id="R_3.050.003ab">nimittaṃ lakṣaṇajñānaṃ śakunisvaradarśanam</li>
        <li id="R_3.050.003cd">avaśyaṃ sukhaduḥkheṣu narāṇāṃ pratidṛśyate</li>
    </ul>
    <ul id="R_3.050.004">
        <li id="R_3.050.004ab">na nūnaṃ rāma jānāsi mahad vyasanam ātmajaḥ</li>
        <li id="R_3.050.004cd">dhāvanti nūnaṃ kākutstha madarthaṃ mṛgapakṣiṇaḥ</li>
    </ul>
    <ul id="R_3.050.005">
        <li id="R_3.050.005ab">trāhi mām adya kākutstha lakṣmaṇeti varāṅganā</li>
        <li id="R_3.050.005cd">susaṃtrastā samākrandac chṛṇvatāṃ tu yathāntike</li>
    </ul>
    <ul id="R_3.050.006">
        <li id="R_3.050.006ab">tāṃ kliṣṭamālyābharaṇāṃ vilapantīm anāthavat</li>
        <li id="R_3.050.006cd">abhyadhāvata vaidehīṃ rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.050.007">
        <li id="R_3.050.007ab">tāṃ latām iva veṣṭantīm āliṅgantīṃ mahādrumān</li>
        <li id="R_3.050.007cd">muñca muñceti bahuśaḥ pravadan rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.050.008">
        <li id="R_3.050.008ab">krośantīṃ rāma rāmeti rāmeṇa rahitāṃ vane</li>
        <li id="R_3.050.008cd">jīvitāntāya keśeṣu jagrāhāntakasaṃnibhaḥ</li>
    </ul>
    <ul id="R_3.050.009">
        <li id="R_3.050.009ab">pradharṣitāyāṃ vaidehyāṃ babhūva sacarācaram</li>
        <li id="R_3.050.009cd">jagat sarvam amaryādaṃ tamasāndhena saṃvṛtam</li>
    </ul>
    <ul id="R_3.050.010">
        <li id="R_3.050.010ab">dṛṣṭvā sītāṃ parāmṛṣṭāṃ dīnāṃ divyena cakṣuṣā</li>
        <li id="R_3.050.010cd">kṛtaṃ kāryam iti śrīmān vyājahāra pitāmahaḥ</li>
    </ul>
    <ul id="R_3.050.011">
        <li id="R_3.050.011ab">prahṛṣṭā vyathitāś cāsan sarve te paramarṣayaḥ</li>
        <li id="R_3.050.011cd">dṛṣṭvā sītāṃ parāmṛṣṭāṃ daṇḍakāraṇyavāsinaḥ</li>
    </ul>
    <ul id="R_3.050.012">
        <li id="R_3.050.012ab">sa tu tāṃ rāma rāmeti rudantīṃ lakṣmaṇeti ca</li>
        <li id="R_3.050.012cd">jagāmākāśam ādāya rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_3.050.013">
        <li id="R_3.050.013ab">taptābharaṇasarvāṅgī pītakauśeyavāsanī</li>
        <li id="R_3.050.013cd">rarāja rājaputrī tu vidyut saudāmanī yathā</li>
    </ul>
    <ul id="R_3.050.014">
        <li id="R_3.050.014ab">uddhūtena ca vastreṇa tasyāḥ pītena rāvaṇaḥ</li>
        <li id="R_3.050.014cd">adhikaṃ paribabhrāja girir dīpa ivāgninā</li>
    </ul>
    <ul id="R_3.050.015">
        <li id="R_3.050.015ab">tasyāḥ paramakalyāṇyās tāmrāṇi surabhīṇi ca</li>
        <li id="R_3.050.015cd">padmapatrāṇi vaidehyā abhyakīryanta rāvaṇam</li>
    </ul>
    <ul id="R_3.050.016">
        <li id="R_3.050.016ab">tasyāḥ kauśeyam uddhūtam ākāśe kanakaprabham</li>
        <li id="R_3.050.016cd">babhau cādityarāgeṇa tāmram abhram ivātape</li>
    </ul>
    <ul id="R_3.050.017">
        <li id="R_3.050.017ab">tasyās tad vimalaṃ vaktram ākāśe rāvaṇāṅkagam</li>
        <li id="R_3.050.017cd">na rarāja vinā rāmaṃ vinālam iva paṅkajam</li>
    </ul>
    <ul id="R_3.050.018">
        <li id="R_3.050.018ab">babhūva jaladaṃ nīlaṃ bhittvā candra ivoditaḥ</li>
        <li id="R_3.050.018cd">sulalāṭaṃ sukeśāntaṃ padmagarbhābham avraṇam</li>
        <li id="R_3.050.018ef">śuklaiḥ suvimalair dantaiḥ prabhāvadbhir alaṃkṛtam</li>
    </ul>
    <ul id="R_3.050.019">
        <li id="R_3.050.019ab">ruditaṃ vyapamṛṣṭāstraṃ candravat priyadarśanam</li>
        <li id="R_3.050.019cd">sunāsaṃ cārutāmrauṣṭham ākāṣe hāṭakaprabham</li>
    </ul>
    <ul id="R_3.050.020">
        <li id="R_3.050.020ab">rākṣasendrasamādhūtaṃ tasyās tad vacanaṃ śubham</li>
        <li id="R_3.050.020cd">śuśubhe na vinā rāmaṃ divā candra ivoditaḥ</li>
    </ul>
    <ul id="R_3.050.021">
        <li id="R_3.050.021ab">sā hemavarṇā nīlāṅgaṃ maithilī rākṣasādhipam</li>
        <li id="R_3.050.021cd">śuśubhe kāñcanī kāñcī nīlaṃ maṇim ivāśritā</li>
    </ul>
    <ul id="R_3.050.022">
        <li id="R_3.050.022ab">sā padmagaurī hemābhā rāvaṇaṃ janakātmajā</li>
        <li id="R_3.050.022cd">vidyudghanam ivāviśya śuśubhe taptabhūṣaṇā</li>
    </ul>
    <ul id="R_3.050.023">
        <li id="R_3.050.023ab">tasyā bhūṣaṇaghoṣeṇa vaidehyā rākṣasādhipaḥ</li>
        <li id="R_3.050.023cd">babhūva vimalo nīlaḥ saghoṣa iva toyadaḥ</li>
    </ul>
    <ul id="R_3.050.024">
        <li id="R_3.050.024ab">uttamāṅgacyutā tasyāḥ puṣpavṛṣṭiḥ samantataḥ</li>
        <li id="R_3.050.024cd">sītāyā hriyamāṇāyāḥ papāta dharaṇītale</li>
    </ul>
    <ul id="R_3.050.025">
        <li id="R_3.050.025ab">sā tu rāvaṇavegena puṣpavṛṣṭiḥ samantataḥ</li>
        <li id="R_3.050.025cd">samādhūtā daśagrīvaṃ punar evābhyavartata</li>
    </ul>
    <ul id="R_3.050.026">
        <li id="R_3.050.026ab">abhyavartata puṣpāṇāṃ dhārā vaiśravaṇānujam</li>
        <li id="R_3.050.026cd">nakṣatramālāvimalā meruṃ nagam ivottamam</li>
    </ul>
    <ul id="R_3.050.027">
        <li id="R_3.050.027ab">caraṇān nūpuraṃ bhraṣṭaṃ vaidehyā ratnabhūṣitam</li>
        <li id="R_3.050.027cd">vidyunmaṇḍalasaṃkāśaṃ papāta madhurasvanam</li>
    </ul>
    <ul id="R_3.050.028">
        <li id="R_3.050.028ab">tarupravālaraktā sā nīlāṅgaṃ rākṣaseśvaram</li>
        <li id="R_3.050.028cd">prāśobhayata vaidehī gajaṃ kaṣyeva kāñcanī</li>
    </ul>
    <ul id="R_3.050.029">
        <li id="R_3.050.029ab">tāṃ maholkām ivākāśe dīpyamānāṃ svatejasā</li>
        <li id="R_3.050.029cd">jahārākāśam āviśya sītāṃ vaiśravaṇānujaḥ</li>
    </ul>
    <ul id="R_3.050.030">
        <li id="R_3.050.030ab">tasyās tāny agnivarṇāni bhūṣaṇāni mahītale</li>
        <li id="R_3.050.030cd">saghoṣāṇy avakīryanta kṣīṇās tārā ivāmbarāt</li>
    </ul>
    <ul id="R_3.050.031">
        <li id="R_3.050.031ab">tasyāḥ stanāntarād bhraṣṭo hāras tārādhipadyutiḥ</li>
        <li id="R_3.050.031cd">vaidehyā nipatan bhāti gaṅgeva gaganāc cyutā</li>
    </ul>
    <ul id="R_3.050.032">
        <li id="R_3.050.032ab">utpāta vātābhihatā nānādvija gaṇāyutāḥ</li>
        <li id="R_3.050.032cd">mā bhair iti vidhūtāgrā vyājahrur iva pādapāḥ</li>
    </ul>
    <ul id="R_3.050.033">
        <li id="R_3.050.033ab">nalinyo dhvastakamalās trastamīnajale carāḥ</li>
        <li id="R_3.050.033cd">sakhīm iva gatotsāhāṃ śocantīva sma maithilīm</li>
    </ul>
    <ul id="R_3.050.034">
        <li id="R_3.050.034ab">samantād abhisaṃpatya siṃhavyāghramṛgadvijāḥ</li>
        <li id="R_3.050.034cd">anvadhāvaṃs tadā roṣāt sītācchāyānugāminaḥ</li>
    </ul>
    <ul id="R_3.050.035">
        <li id="R_3.050.035ab">jalaprapātāsramukhāḥ śṛṅgair ucchritabāhavaḥ</li>
        <li id="R_3.050.035cd">sītāyāṃ hriyamāṇāyāṃ vikrośantīva parvatāḥ</li>
    </ul>
    <ul id="R_3.050.036">
        <li id="R_3.050.036ab">hriyamāṇāṃ tu vaidehīṃ dṛṣṭvā dīno divākaraḥ</li>
        <li id="R_3.050.036cd">pravidhvastaprabhaḥ śrīmān āsīt pāṇḍuramaṇḍalaḥ</li>
    </ul>
    <ul id="R_3.050.037">
        <li id="R_3.050.037ab">nāsti dharmaḥ kutaḥ satyaṃ nārjavaṃ nānṛśaṃsatā</li>
        <li id="R_3.050.037cd">yatra rāmasya vaidehīṃ bhāryāṃ harati rāvaṇaḥ</li>
    </ul>
    <ul id="R_3.050.038">
        <li id="R_3.050.038ab">iti sarvāṇi bhūtāni gaṇaśaḥ paryadevayan</li>
        <li id="R_3.050.038cd">vitrastakā dīnamukhā rurudur mṛgapotakāḥ</li>
    </ul>
    <ul id="R_3.050.039">
        <li id="R_3.050.039ab">udvīkṣyodvīkṣya nayanair āsrapātāvilekṣaṇāḥ</li>
        <li id="R_3.050.039cd">supravepitagātrāś ca babhūvur vanadevatāḥ</li>
    </ul>
    <ul id="R_3.050.040">
        <li id="R_3.050.040ab">vikrośantīṃ dṛḍhaṃ sītāṃ dṛṣṭvā duḥkhaṃ tathā gatām</li>
        <li id="R_3.050.040cd">tāṃ tu lakṣmaṇa rāmeti krośantīṃ madhurasvarām</li>
    </ul>
    <ul id="R_3.050.041">
        <li id="R_3.050.041ab">avekṣamāṇāṃ bahuṣo vaidehīṃ dharaṇītalam</li>
        <li id="R_3.050.041cd">sa tām ākulakeśāntāṃ vipramṛṣṭaviśeṣakām</li>
        <li id="R_3.050.041ef">jahārātmavināśāya daśagrīvo manasvinām</li>
    </ul>
    <ul id="R_3.050.042">
        <li id="R_3.050.042ab">
            <seg type="pāda" n="a">tatas tu sā cārudatī śucismitā</seg>
            <seg type="pāda" n="b">vinākṛtā bandhujanena maithilī</seg>
        </li>
        <li id="R_3.050.042cd">
            <seg type="pāda" n="c">apaśyatī rāghavalakṣmaṇāv ubhau</seg>
            <seg type="pāda" n="d">vivarṇavaktrā bhayabhārapīḍitā</seg>
        </li>
    </ul>
    <ul id="R_3.051.001">
        <li id="R_3.051.001ab">kham utpatantaṃ taṃ dṛṣṭvā maithilī janakātmajā</li>
        <li id="R_3.051.001cd">duḥkhitā paramodvignā bhaye mahati vartinī</li>
    </ul>
    <ul id="R_3.051.002">
        <li id="R_3.051.002ab">roṣarodanatāmrākṣī bhīmākṣaṃ rākṣasādhipam</li>
        <li id="R_3.051.002cd">rudatī karuṇaṃ sītā hriyamāṇedam abravīt</li>
    </ul>
    <ul id="R_3.051.003">
        <li id="R_3.051.003ab">na vyapatrapase nīca karmaṇānena rāvaṇa</li>
        <li id="R_3.051.003cd">jñātvā virahitāṃ yo māṃ corayitvā palāyase</li>
    </ul>
    <ul id="R_3.051.004">
        <li id="R_3.051.004ab">tvayaiva nūnaṃ duṣṭātman bhīruṇā hartum icchatā</li>
        <li id="R_3.051.004cd">mamāpavāhito bhartā mṛgarūpeṇa māyayā</li>
        <li id="R_3.051.004ef">yo hi mām udyatas trātuṃ so 'py ayaṃ vinipātitaḥ</li>
    </ul>
    <ul id="R_3.051.005">
        <li id="R_3.051.005ab">paramaṃ khalu te vīryaṃ dṛśyate rākṣasādhama</li>
        <li id="R_3.051.005cd">viśrāvya nāmadheyaṃ hi yuddhe nāsti jitā tvayā</li>
    </ul>
    <ul id="R_3.051.006">
        <li id="R_3.051.006ab">īdṛśaṃ garhitaṃ karma kathaṃ kṛtvā na lajjase</li>
        <li id="R_3.051.006cd">striyāś ca haraṇaṃ nīca rahite ca parasya ca</li>
    </ul>
    <ul id="R_3.051.007">
        <li id="R_3.051.007ab">kathayiṣyanti lokeṣu puruṣāḥ karma kutsitam</li>
        <li id="R_3.051.007cd">sunṛśaṃsam adharmiṣṭhaṃ tava śauṇḍīryamāninaḥ</li>
    </ul>
    <ul id="R_3.051.008">
        <li id="R_3.051.008ab">dhik te śauryaṃ ca sattvaṃ ca yat tvayā kathitaṃ tadā</li>
        <li id="R_3.051.008cd">kulākrośakaraṃ loke dhik te cāritram īdṛśam</li>
    </ul>
    <ul id="R_3.051.009">
        <li id="R_3.051.009ab">kiṃ śakyaṃ kartum evaṃ hi yaj javenaiva dhāvasi</li>
        <li id="R_3.051.009cd">muhūrtam api tiṣṭhasva na jīvan pratiyāsyasi</li>
    </ul>
    <ul id="R_3.051.010">
        <li id="R_3.051.010ab">na hi cakṣuḥpathaṃ prāpya tayoḥ pārthivaputrayoḥ</li>
        <li id="R_3.051.010cd">sasainyo 'pi samartaḥs tvaṃ muhūrtam api jīvitum</li>
    </ul>
    <ul id="R_3.051.011">
        <li id="R_3.051.011ab">na tvaṃ tayoḥ śarasparśaṃ soḍhuṃ śaktaḥ kathaṃ cana</li>
        <li id="R_3.051.011cd">vane prajvalitasyeva sparśam agner vihaṃgamaḥ</li>
    </ul>
    <ul id="R_3.051.012">
        <li id="R_3.051.012ab">sādhu kṛtvātmanaḥ pathyaṃ sādhu māṃ muñca rāvaṇa</li>
        <li id="R_3.051.012cd">matpradharṣaṇaruṣṭo hi bhrātrā saha patir mama</li>
        <li id="R_3.051.012ef">vidhāsyati vināśāya tvaṃ māṃ yadi na muñcasi</li>
    </ul>
    <ul id="R_3.051.013">
        <li id="R_3.051.013ab">yena tvaṃ vyavasāyena balān māṃ hartum icchasi</li>
        <li id="R_3.051.013cd">vyavasāyaḥ sa te nīca bhaviṣyati nirarthakaḥ</li>
    </ul>
    <ul id="R_3.051.014">
        <li id="R_3.051.014ab">na hy ahaṃ tam apaśyantī bhartāraṃ vibudhopamam</li>
        <li id="R_3.051.014cd">utsahe śatruvaśagā prāṇān dhārayituṃ ciram</li>
    </ul>
    <ul id="R_3.051.015">
        <li id="R_3.051.015ab">na nūnaṃ cātmanaḥ śreyaḥ pathyaṃ vā samavekṣase</li>
        <li id="R_3.051.015cd">mṛtyukāle yathā martyo viparītāni sevate</li>
    </ul>
    <ul id="R_3.051.016">
        <li id="R_3.051.016ab">mumūrṣūṇāṃ hi sarveṣāṃ yat pathyaṃ tan na rocate</li>
        <li id="R_3.051.016cd">paśyāmīva hi kaṇṭhe tvāṃ kālapāśāvapāśitam</li>
    </ul>
    <ul id="R_3.051.017">
        <li id="R_3.051.017ab">yathā cāsmin bhayasthāne na bibheṣe daśānana</li>
        <li id="R_3.051.017cd">vyaktaṃ hiraṇmayān hi tvaṃ saṃpaśyasi mahīruhān</li>
    </ul>
    <ul id="R_3.051.018">
        <li id="R_3.051.018ab">nadīṃ vairataṇīṃ ghorāṃ rudhiraughanivāhinīm</li>
        <li id="R_3.051.018cd">khaḍgapatravanaṃ caiva bhīmaṃ paśyasi rāvaṇa</li>
    </ul>
    <ul id="R_3.051.019">
        <li id="R_3.051.019ab">taptakāñcanapuṣpāṃ ca vaidūryapravaracchadām</li>
        <li id="R_3.051.019cd">drakṣyase śālmalīṃ tīkṣṇām āyasaiḥ kaṇṭakaiś citām</li>
    </ul>
    <ul id="R_3.051.020">
        <li id="R_3.051.020ab">na hi tvam īdṛśaṃ kṛtvā tasyālīkaṃ mahātmanaḥ</li>
        <li id="R_3.051.020cd">dhārituṃ śakṣyasi ciraṃ viṣaṃ pītveva nirghṛṇaḥ</li>
    </ul>
    <ul id="R_3.051.021">
        <li id="R_3.051.021ab">baddhas tvaṃ kālapāśena durnivāreṇa rāvaṇa</li>
        <li id="R_3.051.021cd">kva gato lapsyase śarma bhartur mama mahātmanaḥ</li>
    </ul>
    <ul id="R_3.051.022">
        <li id="R_3.051.022ab">nimeṣāntaramātreṇa vinā bhrātaram āhave</li>
        <li id="R_3.051.022cd">rākṣasā nihatā yena sahasrāṇi caturdaśa</li>
    </ul>
    <ul id="R_3.051.023">
        <li id="R_3.051.023ab">sa kathaṃ rāghavo vīraḥ sarvāstrakuśalo balī</li>
        <li id="R_3.051.023cd">na tvāṃ hanyāc charais tīkṣṇair iṣṭabhāryāpahāriṇam</li>
    </ul>
    <ul id="R_3.051.024">
        <li id="R_3.051.024ab">etac cānyac ca paruṣaṃ vaidehī rāvaṇāṅkagā</li>
        <li id="R_3.051.024cd">bhayaśokasamāviṣṭā karuṇaṃ vilalāpa ha</li>
    </ul>
    <ul id="R_3.051.025">
        <li id="R_3.051.025ab">
            <seg type="pāda" n="a">tathā bhṛśārtāṃ bahu caiva bhāṣiṇīṃ</seg>
            <seg type="pāda" n="b">vilalāpa pūrvaṃ karuṇaṃ ca bhāminīm</seg>
        </li>
        <li id="R_3.051.025cd">
            <seg type="pāda" n="c">jahāra pāpas taruṇīṃ viveṣṭatīṃ</seg>
            <seg type="pāda" n="d">nṛpātmajām āgatagātravepathum</seg>
        </li>
    </ul>
    <ul id="R_3.052.001">
        <li id="R_3.052.001ab">hriyamāṇā tu vaidehī kaṃ cin nātham apaśyatī</li>
        <li id="R_3.052.001cd">dadarśa giriśṛṅgasthān pañcavānarapuṃgavān</li>
    </ul>
    <ul id="R_3.052.002">
        <li id="R_3.052.002ab">teṣāṃ madhye viśālākṣī kauśeyaṃ kanakaprabham</li>
        <li id="R_3.052.002cd">uttarīyaṃ varārohā śubhāny ābharaṇāni ca</li>
        <li id="R_3.052.002ef">mumoca yadi rāmāya śaṃseyur iti maithilī</li>
    </ul>
    <ul id="R_3.052.003">
        <li id="R_3.052.003ab">vastram utsṛjya tan madhye vinikṣiptaṃ sabhūṣaṇam</li>
        <li id="R_3.052.003cd">saṃbhramāt tu daśagrīvas tat karma na ca buddhivān</li>
    </ul>
    <ul id="R_3.052.004">
        <li id="R_3.052.004ab">piṅgākṣās tāṃ viśālākṣīṃ netrair animiṣair iva</li>
        <li id="R_3.052.004cd">vikrośantīṃ tadā sītāṃ dadṛśur vānararṣabhāḥ</li>
    </ul>
    <ul id="R_3.052.005">
        <li id="R_3.052.005ab">sa ca pampām atikramya laṅkām abhimukhaḥ purīm</li>
        <li id="R_3.052.005cd">jagāma rudatīṃ gṛhya maithilīṃ rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_3.052.006">
        <li id="R_3.052.006ab">tāṃ jahāra susaṃhṛṣṭo rāvaṇo mṛtyum ātmanaḥ</li>
        <li id="R_3.052.006cd">utsaṅgenaiva bhujagīṃ tīkṣṇadaṃṣṭrāṃ mahāviṣām</li>
    </ul>
    <ul id="R_3.052.007">
        <li id="R_3.052.007ab">vanāni saritaḥ śailān sarāṃsi ca vihāyasā</li>
        <li id="R_3.052.007cd">sa kṣipraṃ samatīyāya śaraś cāpād iva cyutaḥ</li>
    </ul>
    <ul id="R_3.052.008">
        <li id="R_3.052.008ab">timinakraniketaṃ tu varuṇālayam akṣayam</li>
        <li id="R_3.052.008cd">saritāṃ śaraṇaṃ gatvā samatīyāya sāgaram</li>
    </ul>
    <ul id="R_3.052.009">
        <li id="R_3.052.009ab">saṃbhramāt parivṛttormī ruddhamīnamahoragaḥ</li>
        <li id="R_3.052.009cd">vaidehyāṃ hriyamāṇāyāṃ babhūva varuṇālayaḥ</li>
    </ul>
    <ul id="R_3.052.010">
        <li id="R_3.052.010ab">antarikṣagatā vācaḥ sasṛjuś cāraṇās tadā</li>
        <li id="R_3.052.010cd">etad anto daśagrīva iti siddhās tadābruvan</li>
    </ul>
    <ul id="R_3.052.011">
        <li id="R_3.052.011ab">sa tu sītāṃ viveṣṭantīm aṅkenādāya rāvaṇaḥ</li>
        <li id="R_3.052.011cd">praviveśa purīṃ laṅkāṃ rūpiṇīṃ mṛtyum ātmanaḥ</li>
    </ul>
    <ul id="R_3.052.012">
        <li id="R_3.052.012ab">so 'bhigamya purīṃ laṅkāṃ suvibhaktamahāpathām</li>
        <li id="R_3.052.012cd">saṃrūḍhakakṣyā bahulaṃ svam antaḥpuram āviśat</li>
    </ul>
    <ul id="R_3.052.013">
        <li id="R_3.052.013ab">tatra tām asitāpāṅgīṃ śokamohaparāyaṇām</li>
        <li id="R_3.052.013cd">nidadhe rāvaṇaḥ sītāṃ mayo māyām ivāsurīm</li>
    </ul>
    <ul id="R_3.052.014">
        <li id="R_3.052.014ab">abravīc ca daśagrīvaḥ piśācīr ghoradarśanāḥ</li>
        <li id="R_3.052.014cd">yathā naināṃ pumān strī vā sītāṃ paśyaty asaṃmataḥ</li>
    </ul>
    <ul id="R_3.052.015">
        <li id="R_3.052.015ab">muktāmaṇisuvarṇāni vastrāṇy ābharaṇāni ca</li>
        <li id="R_3.052.015cd">yad yad icchet tad evāsyā deyaṃ macchandato yathā</li>
    </ul>
    <ul id="R_3.052.016">
        <li id="R_3.052.016ab">yā ca vakṣyati vaidehīṃ vacanaṃ kiṃ cid apriyam</li>
        <li id="R_3.052.016cd">ajñānād yadi vā jñānān na tasyā jīvitaṃ priyam</li>
    </ul>
    <ul id="R_3.052.017">
        <li id="R_3.052.017ab">tathoktvā rākṣasīs tās tu rākṣasendraḥ pratāpavān</li>
        <li id="R_3.052.017cd">niṣkramyāntaḥpurāt tasmāt kiṃ kṛtyam iti cintayan</li>
        <li id="R_3.052.017ef">dadarśāṣṭau mahāvīryān rākṣasān piśitāśanān</li>
    </ul>
    <ul id="R_3.052.018">
        <li id="R_3.052.018ab">sa tān dṛṣṭvā mahāvīryo varadānena mohitaḥ</li>
        <li id="R_3.052.018cd">uvācaitān idaṃ vākyaṃ praśasya balavīryataḥ</li>
    </ul>
    <ul id="R_3.052.019">
        <li id="R_3.052.019ab">nānāpraharaṇāḥ kṣipram ito gacchata satvarāḥ</li>
        <li id="R_3.052.019cd">janasthānaṃ hatasthānaṃ bhūtapūrvaṃ kharālayam</li>
    </ul>
    <ul id="R_3.052.020">
        <li id="R_3.052.020ab">tatroṣyatāṃ janasthāne śūnye nihatarākṣase</li>
        <li id="R_3.052.020cd">pauruṣaṃ balam āśritya trāsam utsṛjya dūrataḥ</li>
    </ul>
    <ul id="R_3.052.021">
        <li id="R_3.052.021ab">balaṃ hi sumahad yan me janasthāne niveśitam</li>
        <li id="R_3.052.021cd">sadūṣaṇakharaṃ yuddhe hataṃ tad rāmasāyakaiḥ</li>
    </ul>
    <ul id="R_3.052.022">
        <li id="R_3.052.022ab">tataḥ krodho mamāpūrvo dhairyasyopari vardhate</li>
        <li id="R_3.052.022cd">vairaṃ ca sumahaj jātaṃ rāmaṃ prati sudāruṇam</li>
    </ul>
    <ul id="R_3.052.023">
        <li id="R_3.052.023ab">niryātayitum icchāmi tac ca vairam ahaṃ ripoḥ</li>
        <li id="R_3.052.023cd">na hi lapsyāmy ahaṃ nidrām ahatvā saṃyuge ripum</li>
    </ul>
    <ul id="R_3.052.024">
        <li id="R_3.052.024ab">taṃ tv idānīm ahaṃ hatvā kharadūṣaṇaghātinam</li>
        <li id="R_3.052.024cd">rāmaṃ śarmopalapsyāmi dhanaṃ labdhveva nirdhanaḥ</li>
    </ul>
    <ul id="R_3.052.025">
        <li id="R_3.052.025ab">janasthāne vasadbhis tu bhavadbhī rāmam āśritā</li>
        <li id="R_3.052.025cd">pravṛttir upanetavyā kiṃ karotīti tattvataḥ</li>
    </ul>
    <ul id="R_3.052.026">
        <li id="R_3.052.026ab">apramādāc ca gantavyaṃ sarvair eva niśācaraiḥ</li>
        <li id="R_3.052.026cd">kartavyaś ca sadā yatno rāghavasya vadhaṃ prati</li>
    </ul>
    <ul id="R_3.052.027">
        <li id="R_3.052.027ab">yuṣmākaṃ hi balajño 'haṃ bahuśo raṇamūrdhani</li>
        <li id="R_3.052.027cd">ataś cāsmiñ janasthāne mayā yūyaṃ niyojitāḥ</li>
    </ul>
    <ul id="R_3.052.028">
        <li id="R_3.052.028ab">
            <seg type="pāda" n="a">tataḥ priyaṃ vākyam upetya rākṣasā</seg>
            <seg type="pāda" n="b">mahārtham aṣṭāv abhivādya rāvaṇam</seg>
        </li>
        <li id="R_3.052.028cd">
            <seg type="pāda" n="c">vihāya laṅkāṃ sahitāḥ pratasthire</seg>
            <seg type="pāda" n="d">yato janasthānam alakṣyadarśanāḥ</seg>
        </li>
    </ul>
    <ul id="R_3.052.029">
        <li id="R_3.052.029ab">
            <seg type="pāda" n="a">tatas tu sītām upalabhya rāvaṇaḥ</seg>
            <seg type="pāda" n="b">susaṃprahṛṣṭaḥ parigṛhya maithilīm</seg>
        </li>
        <li id="R_3.052.029cd">
            <seg type="pāda" n="c">prasajya rāmeṇa ca vairam uttamaṃ</seg>
            <seg type="pāda" n="d">babhūva mohān muditaḥ sa rākṣasaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.053.001">
        <li id="R_3.053.001ab">saṃdiśya rākṣasān ghorān rāvaṇo 'ṣṭau mahābalān</li>
        <li id="R_3.053.001cd">ātmānaṃ buddhivaiklavyāt kṛtakṛtyam amanyata</li>
    </ul>
    <ul id="R_3.053.002">
        <li id="R_3.053.002ab">sa cintayāno vaidehīṃ kāmabāṇasamarpitaḥ</li>
        <li id="R_3.053.002cd">praviveśa gṛhaṃ ramyaṃ sītāṃ draṣṭum abhitvaran</li>
    </ul>
    <ul id="R_3.053.003">
        <li id="R_3.053.003ab">sa praviśya tu tadveśma rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_3.053.003cd">apaśyad rākṣasīmadhye sītāṃ śokaparāyaṇam</li>
    </ul>
    <ul id="R_3.053.004">
        <li id="R_3.053.004ab">aśrupūrṇamukhīṃ dīnāṃ śokabhārāvapīḍitām</li>
        <li id="R_3.053.004cd">vāyuvegair ivākrāntāṃ majjantīṃ nāvam arṇave</li>
    </ul>
    <ul id="R_3.053.005">
        <li id="R_3.053.005ab">mṛgayūthaparibhraṣṭāṃ mṛgīṃ śvabhir ivāvṛtām</li>
        <li id="R_3.053.005cd">adhomukhamukhīṃ dīnām abhyetya ca niśācaraḥ</li>
    </ul>
    <ul id="R_3.053.006">
        <li id="R_3.053.006ab">tāṃ tu śokavaśāṃ dīnām avaśāṃ rākṣasādhipaḥ</li>
        <li id="R_3.053.006cd">sa balād darśayām āsa gṛhaṃ devagṛhopamam</li>
    </ul>
    <ul id="R_3.053.007">
        <li id="R_3.053.007ab">harmyaprāsādasaṃbadhaṃ strīsahasraniṣevitam</li>
        <li id="R_3.053.007cd">nānāpakṣigaṇair juṣṭaṃ nānāratnasamanvitam</li>
    </ul>
    <ul id="R_3.053.008">
        <li id="R_3.053.008ab">kāñcanais tāpanīyaiś ca sphāṭikai rājatais tathā</li>
        <li id="R_3.053.008cd">vajravaidūryacitraiś ca stambhair dṛṣṭimanoharaiḥ</li>
    </ul>
    <ul id="R_3.053.009">
        <li id="R_3.053.009ab">divyadundubhinirhrādaṃ taptakāñcanatoraṇam</li>
        <li id="R_3.053.009cd">sopānaṃ kāñcanaṃ citram āruroha tayā saha</li>
    </ul>
    <ul id="R_3.053.010">
        <li id="R_3.053.010ab">dāntakā rājatāś caiva gavākṣāḥ priyadarśanāḥ</li>
        <li id="R_3.053.010cd">hemajālāvṛtāś cāsaṃs tatra prāsādapaṅktayaḥ</li>
    </ul>
    <ul id="R_3.053.011">
        <li id="R_3.053.011ab">sudhāmaṇivicitrāṇi bhūmibhāgāni sarvaśaḥ</li>
        <li id="R_3.053.011cd">daśagrīvaḥ svabhavane prādarśayata maithilīm</li>
    </ul>
    <ul id="R_3.053.012">
        <li id="R_3.053.012ab">dīrghikāḥ puṣkariṇyaś ca nānāpuṣpasamāvṛtāḥ</li>
        <li id="R_3.053.012cd">rāvaṇo darśayām āsa sītāṃ śokaparāyaṇām</li>
    </ul>
    <ul id="R_3.053.013">
        <li id="R_3.053.013ab">darśayitvā tu vaidehīṃ kṛtsnaṃ tad bhavanottamam</li>
        <li id="R_3.053.013cd">uvāca vākyaṃ pāpātmā rāvaṇo janakātmajām</li>
    </ul>
    <ul id="R_3.053.014">
        <li id="R_3.053.014ab">daśarākṣasakoṭyaś ca dvāviṃśatir athāparāḥ</li>
        <li id="R_3.053.014cd">varjayitvā jarā vṛddhān bālāṃś ca rajanīcarān</li>
    </ul>
    <ul id="R_3.053.015">
        <li id="R_3.053.015ab">teṣāṃ prabhur ahaṃ sīte sarveṣāṃ bhīmakarmaṇām</li>
        <li id="R_3.053.015cd">sahasram ekam ekasya mama kāryapuraḥsaram</li>
    </ul>
    <ul id="R_3.053.016">
        <li id="R_3.053.016ab">yad idaṃ rājyatantraṃ me tvayi sarvaṃ pratiṣṭhitam</li>
        <li id="R_3.053.016cd">jīvitaṃ ca viśālākṣi tvaṃ me prāṇair garīyasī</li>
    </ul>
    <ul id="R_3.053.017">
        <li id="R_3.053.017ab">bahūnāṃ strīsahasrāṇāṃ mama yo 'sau parigrahaḥ</li>
        <li id="R_3.053.017cd">tāsāṃ tvam īśvarī sīte mama bhāryā bhava priye</li>
    </ul>
    <ul id="R_3.053.018">
        <li id="R_3.053.018ab">sādhu kiṃ te 'nyayā buddhyā rocayasva vaco mama</li>
        <li id="R_3.053.018cd">bhajasva mābhitaptasya prasādaṃ kartum arhasi</li>
    </ul>
    <ul id="R_3.053.019">
        <li id="R_3.053.019ab">parikṣiptā samudreṇa laṅkeyaṃ śatayojanā</li>
        <li id="R_3.053.019cd">neyaṃ dharṣayituṃ śakyā sendrair api surāsuraiḥ</li>
    </ul>
    <ul id="R_3.053.020">
        <li id="R_3.053.020ab">na deveṣu na yakṣeṣu na gandharveṣu narṣiṣu</li>
        <li id="R_3.053.020cd">ahaṃ paśyāmi lokeṣu yo me vīryasamo bhavet</li>
    </ul>
    <ul id="R_3.053.021">
        <li id="R_3.053.021ab">rājyabhraṣṭena dīnena tāpasena gatāyuṣā</li>
        <li id="R_3.053.021cd">kiṃ kariṣyasi rāmeṇa mānuṣeṇālpatejasā</li>
    </ul>
    <ul id="R_3.053.022">
        <li id="R_3.053.022ab">bhajasva sīte mām eva bhartāhaṃ sadṛśas tava</li>
        <li id="R_3.053.022cd">yauvanaṃ hy adhruvaṃ bhīru ramasveha mayā saha</li>
    </ul>
    <ul id="R_3.053.023">
        <li id="R_3.053.023ab">darśane mā kṛthā buddhiṃ rāghavasya varānane</li>
        <li id="R_3.053.023cd">kāsya śaktir ihāgantum api sīte manorathaiḥ</li>
    </ul>
    <ul id="R_3.053.024">
        <li id="R_3.053.024ab">na śakyo vāyur ākāśe pāśair baddhaṃ mahājavaḥ</li>
        <li id="R_3.053.024cd">dīpyamānasya vāpy agner grahītuṃ vimalāṃ śikhām</li>
    </ul>
    <ul id="R_3.053.025">
        <li id="R_3.053.025ab">trayāṇām api lokānāṃ na taṃ paśyāmi śobhane</li>
        <li id="R_3.053.025cd">vikrameṇa nayed yas tvāṃ madbāhuparipālitām</li>
    </ul>
    <ul id="R_3.053.026">
        <li id="R_3.053.026ab">laṅkāyāṃ sumahad rājyam idaṃ tvam anupālaya</li>
        <li id="R_3.053.026cd">abhiṣekodakaklinnā tuṣṭā ca ramayasva mām</li>
    </ul>
    <ul id="R_3.053.027">
        <li id="R_3.053.027ab">duṣkṛtaṃ yat purā karma vanavāsena tad gatam</li>
        <li id="R_3.053.027cd">yaś ca te sukṛto dharmas tasyeha phalam āpnuhi</li>
    </ul>
    <ul id="R_3.053.028">
        <li id="R_3.053.028ab">iha sarvāṇi mālyāni divyagandhāni maithili</li>
        <li id="R_3.053.028cd">bhūṣaṇāni ca mukhyāni tāni seva mayā saha</li>
    </ul>
    <ul id="R_3.053.029">
        <li id="R_3.053.029ab">puṣpakaṃ nāma suśroṇi bhrātur vaiśravaṇasya me</li>
        <li id="R_3.053.029cd">vimānaṃ ramaṇīyaṃ ca tad vimānaṃ manojavam</li>
    </ul>
    <ul id="R_3.053.030">
        <li id="R_3.053.030ab">tatra sīte mayā sārdhaṃ viharasva yathāsukham</li>
        <li id="R_3.053.030cd">vadanaṃ padmasaṃkāśaṃ vimalaṃ cārudarśanam</li>
    </ul>
    <ul id="R_3.053.031">
        <li id="R_3.053.031ab">śokārtaṃ tu varārohe na bhrājati varānane</li>
        <li id="R_3.053.031cd">alaṃ vrīḍena vaidehi dharmalopa kṛtena te</li>
    </ul>
    <ul id="R_3.053.032">
        <li id="R_3.053.032ab">ārṣo 'yaṃ daivaniṣyando yas tvām abhigamiṣyati</li>
        <li id="R_3.053.032cd">etau pādau mayā snigdhau śirobhiḥ paripīḍitau</li>
    </ul>
    <ul id="R_3.053.033">
        <li id="R_3.053.033ab">prasādaṃ kuru me kṣipraṃ vaśyo dāso 'ham asmi te</li>
        <li id="R_3.053.033cd">nemāḥ śūnyā mayā vācaḥ śuṣyamāṇena bhāṣitāḥ</li>
    </ul>
    <ul id="R_3.053.034">
        <li id="R_3.053.034ab">na cāpi rāvaṇaḥ kāṃ cin mūrdhnā strīṃ praṇameta ha</li>
        <li id="R_3.053.034cd">evam uktvā daśagrīvo maithilīṃ janakātmajām</li>
    </ul>
    <ul id="R_3.053.035">
        <li id="R_3.053.035ab">kṛtāntavaśam āpanno mameyam iti manyate</li>
    </ul>
    <ul id="R_3.054.001">
        <li id="R_3.054.001ab">sā tathoktā tu vaidehī nirbhayā śokakarṣitā</li>
        <li id="R_3.054.001cd">tṛṇam antarataḥ kṛtvā rāvaṇaṃ pratyabhāṣata</li>
    </ul>
    <ul id="R_3.054.002">
        <li id="R_3.054.002ab">rājā daśaratho nāma dharmasetur ivācalaḥ</li>
        <li id="R_3.054.002cd">satyasandhaḥ parijñāto yasya putraḥ sa rāghavaḥ</li>
    </ul>
    <ul id="R_3.054.003">
        <li id="R_3.054.003ab">rāmo nāma sa dharmātmā triṣu lokeṣu viśrutaḥ</li>
        <li id="R_3.054.003cd">dīrghabāhur viśālākṣo daivataṃ sa patir mama</li>
    </ul>
    <ul id="R_3.054.004">
        <li id="R_3.054.004ab">ikṣvākūṇāṃ kule jātaḥ siṃhaskandho mahādyutiḥ</li>
        <li id="R_3.054.004cd">lakṣmaṇena saha bhrātrā yas te prāṇāṃ hariṣyati</li>
    </ul>
    <ul id="R_3.054.005">
        <li id="R_3.054.005ab">pratyakṣaṃ yady ahaṃ tasya tvayā syāṃ dharṣitā balāt</li>
        <li id="R_3.054.005cd">śayitā tvaṃ hataḥ saṃkhye janasthāne yathā kharaḥ</li>
    </ul>
    <ul id="R_3.054.006">
        <li id="R_3.054.006ab">ya ete rākṣasāḥ proktā ghorarūpā mahābalāḥ</li>
        <li id="R_3.054.006cd">rāghave nirviṣāḥ sarve suparṇe pannagā yathā</li>
    </ul>
    <ul id="R_3.054.007">
        <li id="R_3.054.007ab">tasya jyāvipramuktās te śarāḥ kāñcanabhūṣaṇāḥ</li>
        <li id="R_3.054.007cd">śarīraṃ vidhamiṣyanti gaṅgākūlam ivormayaḥ</li>
    </ul>
    <ul id="R_3.054.008">
        <li id="R_3.054.008ab">asurair vā surair vā tvaṃ yady avadho 'si rāvaṇa</li>
        <li id="R_3.054.008cd">utpādya sumahad vairaṃ jīvaṃs tasya na mokṣyase</li>
    </ul>
    <ul id="R_3.054.009">
        <li id="R_3.054.009ab">sa te jīvitaśeṣasya rāghavo 'ntakaro balī</li>
        <li id="R_3.054.009cd">paśor yūpagatasyeva jīvitaṃ tava durlabham</li>
    </ul>
    <ul id="R_3.054.010">
        <li id="R_3.054.010ab">yadi paśyet sa rāmas tvāṃ roṣadīptena cakṣuṣā</li>
        <li id="R_3.054.010cd">rakṣas tvam adya nirdagdho gaccheḥ sadyaḥ parābhavam</li>
    </ul>
    <ul id="R_3.054.011">
        <li id="R_3.054.011ab">yaś candraṃ nabhaso bhūmau pātayen nāśayeta vā</li>
        <li id="R_3.054.011cd">sāgaraṃ śoṣayed vāpi sa sītāṃ mocayed iha</li>
    </ul>
    <ul id="R_3.054.012">
        <li id="R_3.054.012ab">gatāyus tvaṃ gataśrīko gatasattvo gatendriyaḥ</li>
        <li id="R_3.054.012cd">laṅkā vaidhavyasaṃyuktā tvatkṛtena bhaviṣyati</li>
    </ul>
    <ul id="R_3.054.013">
        <li id="R_3.054.013ab">na te pāpam idaṃ karma sukhodarkaṃ bhaviṣyati</li>
        <li id="R_3.054.013cd">yāhaṃ nītā vinā bhāvaṃ patipārśvāt tvayā vanāt</li>
    </ul>
    <ul id="R_3.054.014">
        <li id="R_3.054.014ab">sa hi daivatasaṃyukto mama bhartā mahādyutiḥ</li>
        <li id="R_3.054.014cd">nirbhayo vīryam āśritya śūnye vasati daṇḍake</li>
    </ul>
    <ul id="R_3.054.015">
        <li id="R_3.054.015ab">sa te darpaṃ balaṃ vīryam utsekaṃ ca tathāvidham</li>
        <li id="R_3.054.015cd">apaneṣyati gātrebhyaḥ śaravarṣeṇa saṃyuge</li>
    </ul>
    <ul id="R_3.054.016">
        <li id="R_3.054.016ab">yadā vināśo bhūtānāṃ dṛśyate kālacoditaḥ</li>
        <li id="R_3.054.016cd">tadā kārye pramādyanti narāḥ kālavaśaṃ gatāḥ</li>
    </ul>
    <ul id="R_3.054.017">
        <li id="R_3.054.017ab">māṃ pradhṛṣya sa te kālaḥ prāpto 'yaṃ rakṣasādhama</li>
        <li id="R_3.054.017cd">ātmano rākṣasānāṃ ca vadhāyāntaḥpurasya ca</li>
    </ul>
    <ul id="R_3.054.018">
        <li id="R_3.054.018ab">na śakyā yajñamadhyasthā vediḥ srugbhāṇḍa maṇḍitā</li>
        <li id="R_3.054.018cd">dvijātimantrasaṃpūtā caṇḍālenāvamarditum</li>
    </ul>
    <ul id="R_3.054.019">
        <li id="R_3.054.019ab">idaṃ śarīraṃ niḥsaṃjñaṃ bandha vā ghātayasva vā</li>
        <li id="R_3.054.019cd">nedaṃ śarīraṃ rakṣyaṃ me jīvitaṃ vāpi rākṣasa</li>
        <li id="R_3.054.019ef">na hi śakṣyāmy upakrośaṃ pṛthivyāṃ dātum ātmanaḥ</li>
    </ul>
    <ul id="R_3.054.020">
        <li id="R_3.054.020ab">evam uktvā tu vaidehī kroddhāt suparuṣaṃ vacaḥ</li>
        <li id="R_3.054.020cd">rāvaṇaṃ maithilī tatra punar novāca kiṃ cana</li>
    </ul>
    <ul id="R_3.054.021">
        <li id="R_3.054.021ab">sītāyā vacanaṃ śrutvā paruṣaṃ romaharṣaṇam</li>
        <li id="R_3.054.021cd">pratyuvāca tataḥ sītāṃ bhayasaṃdarśanaṃ vacaḥ</li>
    </ul>
    <ul id="R_3.054.022">
        <li id="R_3.054.022ab">śṛṇu maithili madvākyaṃ māsān dvādaśa bhāmini</li>
        <li id="R_3.054.022cd">kālenānena nābhyeṣi yadi māṃ cāruhāsini</li>
        <li id="R_3.054.022ef">tatas tvāṃ prātarāśārthaṃ sūdāś chetsyanti leśaśaḥ</li>
    </ul>
    <ul id="R_3.054.023">
        <li id="R_3.054.023ab">ity uktvā paruṣaṃ vākyaṃ rāvaṇaḥ śatrurāvaṇaḥ</li>
        <li id="R_3.054.023cd">rākṣasīś ca tataḥ kruddha idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.054.024">
        <li id="R_3.054.024ab">śīghram evaṃ hi rākṣasyo vikṛtā ghoradarśanāḥ</li>
        <li id="R_3.054.024cd">darpam asyā vineṣyantu māṃsaśoṇitabhojanāḥ</li>
    </ul>
    <ul id="R_3.054.025">
        <li id="R_3.054.025ab">vacanād eva tās tasya vikṛtā ghoradarśanāḥ</li>
        <li id="R_3.054.025cd">kṛtaprāñjalayo bhūtvā maithilīṃ paryavārayan</li>
    </ul>
    <ul id="R_3.054.026">
        <li id="R_3.054.026ab">sa tāḥ provāca rājā tu rāvaṇo ghoradarśanaḥ</li>
        <li id="R_3.054.026cd">pracālya caraṇotkarṣair dārayann iva medinīm</li>
    </ul>
    <ul id="R_3.054.027">
        <li id="R_3.054.027ab">aśokavanikāmadhye maithilī nīyatām iti</li>
        <li id="R_3.054.027cd">tatreyaṃ rakṣyatāṃ gūḍham uṣmābhiḥ parivāritā</li>
    </ul>
    <ul id="R_3.054.028">
        <li id="R_3.054.028ab">tatraināṃ tarjanair ghoraiḥ punaḥ sāntvaiś ca maithilīm</li>
        <li id="R_3.054.028cd">ānayadhvaṃ vaśaṃ sarvā vanyāṃ gajavadhūm iva</li>
    </ul>
    <ul id="R_3.054.029">
        <li id="R_3.054.029ab">iti pratisamādiṣṭā rākṣasyo rāvaṇena tāḥ</li>
        <li id="R_3.054.029cd">aśokavanikāṃ jagmur maithilīṃ parigṛhya tām</li>
    </ul>
    <ul id="R_3.054.030">
        <li id="R_3.054.030ab">sarvakāmaphalair vṛkṣair nānāpuṣpaphalair vṛtām</li>
        <li id="R_3.054.030cd">sarvakālamadaiś cāpi dvijaiḥ samupasevitām</li>
    </ul>
    <ul id="R_3.054.031">
        <li id="R_3.054.031ab">sā tu śokaparītāṅgī maithilī janakātmajā</li>
        <li id="R_3.054.031cd">rākṣasī vaśam āpannā vyāghrīṇāṃ hariṇī yathā</li>
    </ul>
    <ul id="R_3.054.032">
        <li id="R_3.054.032ab">
            <seg type="pāda" n="a">na vindate tatra tu śarma maithilī</seg>
            <seg type="pāda" n="b">virūpanetrābhir atīva tarjitā</seg>
        </li>
        <li id="R_3.054.032cd">
            <seg type="pāda" n="c">patiṃ smarantī dayitaṃ ca devaraṃ</seg>
            <seg type="pāda" n="d">vicetanābhūd bhayaśokapīḍitā</seg>
        </li>
    </ul>
    <ul id="R_3.055.001">
        <li id="R_3.055.001ab">rākṣasaṃ mṛgarūpeṇa carantaṃ kāmarūpiṇam</li>
        <li id="R_3.055.001cd">nihatya rāmo mārīcaṃ tūrṇaṃ pathi nyavartata</li>
    </ul>
    <ul id="R_3.055.002">
        <li id="R_3.055.002ab">tasya saṃtvaramāṇasya draṣṭukāmasya maithilīm</li>
        <li id="R_3.055.002cd">krūrasvaro 'tha gomāyur vinanādāsya pṛṣṭhataḥ</li>
    </ul>
    <ul id="R_3.055.003">
        <li id="R_3.055.003ab">sa tasya svaram ājñāya dāruṇaṃ romaharṣaṇam</li>
        <li id="R_3.055.003cd">cintayām āsa gomāyoḥ svareṇa pariśaṅkitaḥ</li>
    </ul>
    <ul id="R_3.055.004">
        <li id="R_3.055.004ab">aśubhaṃ bata manye 'haṃ gomāyur vāśyate yathā</li>
        <li id="R_3.055.004cd">svasti syād api vaidehyā rākṣasair bhakṣaṇaṃ vinā</li>
    </ul>
    <ul id="R_3.055.005">
        <li id="R_3.055.005ab">mārīcena tu vijñāya svaram ālakṣya māmakam</li>
        <li id="R_3.055.005cd">vikruṣṭaṃ mṛgarūpeṇa lakṣmaṇaḥ śṛṇuyād yadi</li>
    </ul>
    <ul id="R_3.055.006">
        <li id="R_3.055.006ab">sa saumitriḥ svaraṃ śrutvā tāṃ ca hitvātha maithilīm</li>
        <li id="R_3.055.006cd">tayaiva prahitaḥ kṣipraṃ matsakāśam ihaiṣyati</li>
    </ul>
    <ul id="R_3.055.007">
        <li id="R_3.055.007ab">rākṣasaiḥ sahitair nūnaṃ sītāyā īpsito vadhaḥ</li>
        <li id="R_3.055.007cd">kāñcanaś ca mṛgo bhūtvā vyapanīyāśramāt tu mām</li>
    </ul>
    <ul id="R_3.055.008">
        <li id="R_3.055.008ab">dūraṃ nītvā tu mārīco rākṣaso 'bhūc charāhataḥ</li>
        <li id="R_3.055.008cd">hā lakṣmaṇa hato 'smīti yad vākyaṃ vyajahāra ha</li>
    </ul>
    <ul id="R_3.055.009">
        <li id="R_3.055.009ab">api svasti bhaved dvābhyāṃ rahitābhyāṃ mayā vane</li>
        <li id="R_3.055.009cd">janasthānanimittaṃ hi kṛtavairo 'smi rākṣasaiḥ</li>
        <li id="R_3.055.009ef">nimittāni ca ghorāṇi dṛśyante 'dya bahūni ca</li>
    </ul>
    <ul id="R_3.055.010">
        <li id="R_3.055.010ab">ity evaṃ cintayan rāmaḥ śrutvā gomāyuniḥsvanam</li>
        <li id="R_3.055.010cd">ātmanaś cāpanayanaṃ mṛgarūpeṇa rakṣasā</li>
        <li id="R_3.055.010ef">ājagāma janasthānaṃ rāghavaḥ pariśaṅkitaḥ</li>
    </ul>
    <ul id="R_3.055.011">
        <li id="R_3.055.011ab">taṃ dīnamānasaṃ dīnam āsedur mṛgapakṣiṇaḥ</li>
        <li id="R_3.055.011cd">savyaṃ kṛtvā mahātmānaṃ ghorāṃś ca sasṛjuḥ svarān</li>
    </ul>
    <ul id="R_3.055.012">
        <li id="R_3.055.012ab">tāni dṛṣṭvā nimittāni mahāghorāṇi rāghavaḥ</li>
        <li id="R_3.055.012cd">tato lakṣaṇam āyāntaṃ dadarśa vigataprabham</li>
    </ul>
    <ul id="R_3.055.013">
        <li id="R_3.055.013ab">tato 'vidūre rāmeṇa samīyāya sa lakṣmaṇaḥ</li>
        <li id="R_3.055.013cd">viṣaṇṇaḥ sa viṣaṇṇena duḥkhito duḥkhabhāginā</li>
    </ul>
    <ul id="R_3.055.014">
        <li id="R_3.055.014ab">saṃjagarhe 'tha taṃ bhrātā jeṣṭho lakṣmaṇam āgatam</li>
        <li id="R_3.055.014cd">vihāya sītāṃ vijane vane rākṣasasevite</li>
    </ul>
    <ul id="R_3.055.015">
        <li id="R_3.055.015ab">gṛhītvā ca karaṃ savyaṃ lakṣmaṇaṃ raghunandanaḥ</li>
        <li id="R_3.055.015cd">uvāca madhurodarkam idaṃ paruṣam ārtavat</li>
    </ul>
    <ul id="R_3.055.016">
        <li id="R_3.055.016ab">aho lakṣmaṇa garhyaṃ te kṛtaṃ yat tvaṃ vihāya tām</li>
        <li id="R_3.055.016cd">sītām ihāgataḥ saumya kaccit svasti bhaved iti</li>
    </ul>
    <ul id="R_3.055.017">
        <li id="R_3.055.017ab">na me 'sti saṃśayo vīra sarvathā janakātmajā</li>
        <li id="R_3.055.017cd">vinaṣṭā bhakṣitā vāpa rākṣasair vanacāribhiḥ</li>
    </ul>
    <ul id="R_3.055.018">
        <li id="R_3.055.018ab">aśubhāny eva bhūyiṣṭhaṃ yathā prādurbhavanti me</li>
        <li id="R_3.055.018cd">api lakṣmaṇa sītāyāḥ sāmagryaṃ prāpnuyāvahe</li>
    </ul>
    <ul id="R_3.055.019">
        <li id="R_3.055.019ab">
            <seg type="pāda" n="a">idaṃ hi rakṣomṛgasaṃnikāśaṃ</seg>
            <seg type="pāda" n="b">pralobhya māṃ dūram anuprayātam</seg>
        </li>
        <li id="R_3.055.019cd">
            <seg type="pāda" n="c">hataṃ kathaṃ cin mahatā śrameṇa</seg>
            <seg type="pāda" n="d">sa rākṣaso 'bhūn mriyamāṇa eva</seg>
        </li>
    </ul>
    <ul id="R_3.055.020">
        <li id="R_3.055.020ab">
            <seg type="pāda" n="a">manaś ca me dīnam ihāprahṛṣṭaṃ</seg>
            <seg type="pāda" n="b">cakṣuś ca savyaṃ kurute vikāram</seg>
        </li>
        <li id="R_3.055.020cd">
            <seg type="pāda" n="c">asaṃśayaṃ lakṣmaṇa nāsti sītā</seg>
            <seg type="pāda" n="d">hṛtā mṛtā vā pathi vartate vā</seg>
        </li>
    </ul>
    <ul id="R_3.056.001">
        <li id="R_3.056.001ab">sa dṛṣṭvā lakṣmaṇaṃ dīnaṃ śūnye daśarathātmajaḥ</li>
        <li id="R_3.056.001cd">paryapṛcchata dharmātmā vaidehīm āgataṃ vinā</li>
    </ul>
    <ul id="R_3.056.002">
        <li id="R_3.056.002ab">prasthitaṃ daṇḍakāraṇyaṃ yā mām anujagāma ha</li>
        <li id="R_3.056.002cd">kva sā lakṣmaṇa vaidehī yāṃ hitvā tvam ihāgataḥ</li>
    </ul>
    <ul id="R_3.056.003">
        <li id="R_3.056.003ab">rājyabhraṣṭasya dīnasya daṇḍakān paridhāvataḥ</li>
        <li id="R_3.056.003cd">kva sā duḥkhasahāyā me vaidehī tanumadhyamā</li>
    </ul>
    <ul id="R_3.056.004">
        <li id="R_3.056.004ab">yāṃ vinā notsahe vīra muhūrtam api jīvitum</li>
        <li id="R_3.056.004cd">kva sā prāṇasahāyā me sītā surasutopamā</li>
    </ul>
    <ul id="R_3.056.005">
        <li id="R_3.056.005ab">patitvam amarāṇāṃ vā pṛthivyāś cāpi lakṣmaṇa</li>
        <li id="R_3.056.005cd">vinā tāṃ tapanīyābhāṃ neccheyaṃ janakātmajām</li>
    </ul>
    <ul id="R_3.056.006">
        <li id="R_3.056.006ab">kaccij jīvati vaidehī prāṇaiḥ priyatarā mama</li>
        <li id="R_3.056.006cd">kaccit pravrājanaṃ saumya na me mithyā bhaviṣyati</li>
    </ul>
    <ul id="R_3.056.007">
        <li id="R_3.056.007ab">sītānimittaṃ saumitre mṛte mayi gate tvayi</li>
        <li id="R_3.056.007cd">kaccit sakāmā sukhitā kaikeyī sā bhaviṣyati</li>
    </ul>
    <ul id="R_3.056.008">
        <li id="R_3.056.008ab">saputrarājyāṃ siddhārthāṃ mṛtaputrā tapasvinī</li>
        <li id="R_3.056.008cd">upasthāsyati kausalyā kaccin saumya na kaikayīm</li>
    </ul>
    <ul id="R_3.056.009">
        <li id="R_3.056.009ab">yadi jīvati vaidehī gamiṣyāmy āśramaṃ punaḥ</li>
        <li id="R_3.056.009cd">suvṛttā yadi vṛttā sā prāṇāṃs tyakṣyāmi lakṣmaṇa</li>
    </ul>
    <ul id="R_3.056.010">
        <li id="R_3.056.010ab">yadi mām āśramagataṃ vaidehī nābhibhāṣate</li>
        <li id="R_3.056.010cd">punaḥ prahasitā sītā vinaśiṣyāmi lakṣmaṇa</li>
    </ul>
    <ul id="R_3.056.011">
        <li id="R_3.056.011ab">brūhi lakṣmaṇa vaidehī yadi jīvati vā na vā</li>
        <li id="R_3.056.011cd">tvayi pramatte rakṣobhir bhakṣitā vā tapasvinī</li>
    </ul>
    <ul id="R_3.056.012">
        <li id="R_3.056.012ab">sukumārī ca bālā ca nityaṃ cāduḥkhadarśinī</li>
        <li id="R_3.056.012cd">madviyogena vaidehī vyaktaṃ śocati durmanāḥ</li>
    </ul>
    <ul id="R_3.056.013">
        <li id="R_3.056.013ab">sarvathā rakṣasā tena jihmena sudurātmanā</li>
        <li id="R_3.056.013cd">vadatā lakṣmaṇety uccais tavāpi janitaṃ bhayam</li>
    </ul>
    <ul id="R_3.056.014">
        <li id="R_3.056.014ab">śrutaś ca śaṅke vaidehyā sa svaraḥ sadṛśo mama</li>
        <li id="R_3.056.014cd">trastayā preṣitas tvaṃ ca draṣṭuṃ māṃ śīghram āgataḥ</li>
    </ul>
    <ul id="R_3.056.015">
        <li id="R_3.056.015ab">sarvathā tu kṛtaṃ kaṣṭaṃ sītām utsṛjatā vane</li>
        <li id="R_3.056.015cd">pratikartuṃ nṛśaṃsānāṃ rakṣasāṃ dattam antaram</li>
    </ul>
    <ul id="R_3.056.016">
        <li id="R_3.056.016ab">duḥkhitāḥ kharaghātena rākṣasāḥ piśitāśanāḥ</li>
        <li id="R_3.056.016cd">taiḥ sītā nihatā ghorair bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_3.056.017">
        <li id="R_3.056.017ab">aho 'smi vyasane magnaḥ sarvathā ripunāśana</li>
        <li id="R_3.056.017cd">kiṃ tv idānīṃ kariṣyāmi śaṅke prāptavyam īdṛśam</li>
    </ul>
    <ul id="R_3.056.018">
        <li id="R_3.056.018ab">iti sītāṃ varārohāṃ cintayann eva rāghavaḥ</li>
        <li id="R_3.056.018cd">ājagāma janasthānaṃ tvarayā sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_3.056.019">
        <li id="R_3.056.019ab">
            <seg type="pāda" n="a">vigarhamāṇo 'nujam ārtarūpaṃ</seg>
            <seg type="pāda" n="b">kṣudhā śramāc caiva pipāsayā ca</seg>
        </li>
        <li id="R_3.056.019cd">
            <seg type="pāda" n="c">viniḥśvasañ śuṣkamukho viṣaṇṇaḥ</seg>
            <seg type="pāda" n="d">pratiśrayaṃ prāpya samīkṣya śūnyam</seg>
        </li>
    </ul>
    <ul id="R_3.056.020">
        <li id="R_3.056.020ab">
            <seg type="pāda" n="a">svam āśramaṃ saṃpravigāhya vīro</seg>
            <seg type="pāda" n="b">vihāradeśān anusṛtya kāṃś cit</seg>
        </li>
        <li id="R_3.056.020cd">
            <seg type="pāda" n="c">etat tad ity eva nivāsabhūmau</seg>
            <seg type="pāda" n="d">prahṛṣṭaromā vyathito babhūva</seg>
        </li>
    </ul>
    <ul id="R_3.057.001">
        <li id="R_3.057.001ab">athāśramād upāvṛttam antarā raghunandanaḥ</li>
        <li id="R_3.057.001cd">paripapraccha saumitriṃ rāmo duḥkhārditaḥ punaḥ</li>
    </ul>
    <ul id="R_3.057.002">
        <li id="R_3.057.002ab">tam uvāca kimarthaṃ tvam āgato 'pāsya maithilīm</li>
        <li id="R_3.057.002cd">yadā sā tava viśvāsād vane viharitā mayā</li>
    </ul>
    <ul id="R_3.057.003">
        <li id="R_3.057.003ab">dṛṣṭvaivābhyāgataṃ tvāṃ me maithilīṃ tyajya lakṣmaṇa</li>
        <li id="R_3.057.003cd">śaṅkamānaṃ mahat pāpaṃ yat satyaṃ vyathitaṃ manaḥ</li>
    </ul>
    <ul id="R_3.057.004">
        <li id="R_3.057.004ab">sphurate nayanaṃ savyaṃ bāhuś ca hṛdayaṃ ca me</li>
        <li id="R_3.057.004cd">dṛṣṭvā lakṣmaṇa dūre tvāṃ sītāvirahitaṃ pathi</li>
    </ul>
    <ul id="R_3.057.005">
        <li id="R_3.057.005ab">evam uktas tu saumitrir lakṣmaṇaḥ śubhalakṣaṇaḥ</li>
        <li id="R_3.057.005cd">bhūyo duḥkhasamāviṣṭo duḥkhitaṃ rāmam abravīt</li>
    </ul>
    <ul id="R_3.057.006">
        <li id="R_3.057.006ab">na svayaṃ kāmakāreṇa tāṃ tyaktvāham ihāgataḥ</li>
        <li id="R_3.057.006cd">pracoditas tayaivograis tvatsakāśam ihāgataḥ</li>
    </ul>
    <ul id="R_3.057.007">
        <li id="R_3.057.007ab">āryeṇeva parikruṣṭaṃ hā sīte lakṣmaṇeti ca</li>
        <li id="R_3.057.007cd">paritrāhīti yad vākyaṃ maithilyās tac chrutiṃ gatam</li>
    </ul>
    <ul id="R_3.057.008">
        <li id="R_3.057.008ab">sā tam ārtasvaraṃ śrutvā tava snehena maithilī</li>
        <li id="R_3.057.008cd">gaccha gaccheti mām āha rudantī bhayavihvalā</li>
    </ul>
    <ul id="R_3.057.009">
        <li id="R_3.057.009ab">pracodyamānena mayā gaccheti bahuśas tayā</li>
        <li id="R_3.057.009cd">pratyuktā maithilī vākyam idaṃ tvatpratyayānvitam</li>
    </ul>
    <ul id="R_3.057.010">
        <li id="R_3.057.010ab">na tat paśyāmy ahaṃ rakṣo yad asya bhayam āvahet</li>
        <li id="R_3.057.010cd">nirvṛtā bhava nāsty etat kenāpy evam udāhṛtam</li>
    </ul>
    <ul id="R_3.057.011">
        <li id="R_3.057.011ab">vigarhitaṃ ca nīcaṃ ca katham āryo 'bhidhāsyati</li>
        <li id="R_3.057.011cd">trāhīti vacanaṃ sīte yas trāyet tridaśān api</li>
    </ul>
    <ul id="R_3.057.012">
        <li id="R_3.057.012ab">kiṃnimittaṃ tu kenāpi bhrātur ālambya me svaram</li>
        <li id="R_3.057.012cd">visvaraṃ vyāhṛtaṃ vākyaṃ lakṣmaṇa trāhi mām iti</li>
        <li id="R_3.057.012ef">na bhavatyā vyathā kāryā kunārījanasevitā</li>
    </ul>
    <ul id="R_3.057.013">
        <li id="R_3.057.013ab">alaṃ vaiklavyam ālambya svasthā bhava nirutsukā</li>
        <li id="R_3.057.013cd">na cāsti triṣu lokeṣu pumān yo rāghavaṃ raṇe</li>
        <li id="R_3.057.013ef">jāto vā jāyamāno vā saṃyuge yaḥ parājayet</li>
    </ul>
    <ul id="R_3.057.014">
        <li id="R_3.057.014ab">evam uktā tu vaidehī parimohitacetanā</li>
        <li id="R_3.057.014cd">uvācāśrūṇi muñcantī dāruṇaṃ mām idaṃ vacaḥ</li>
    </ul>
    <ul id="R_3.057.015">
        <li id="R_3.057.015ab">bhāvo mayi tavātyarthaṃ pāpa eva niveśitaḥ</li>
        <li id="R_3.057.015cd">vinaṣṭe bhrātari prāpte na ca tvaṃ mām avāpsyasi</li>
    </ul>
    <ul id="R_3.057.016">
        <li id="R_3.057.016ab">saṃketād bharatena tvaṃ rāmaṃ samanugacchasi</li>
        <li id="R_3.057.016cd">krośantaṃ hi yathātyarthaṃ nainam abhyavapadyase</li>
    </ul>
    <ul id="R_3.057.017">
        <li id="R_3.057.017ab">ripuḥ pracchannacārī tvaṃ madartham anugacchasi</li>
        <li id="R_3.057.017cd">rāghavasyāntaraprepsus tathainaṃ nābhipadyase</li>
    </ul>
    <ul id="R_3.057.018">
        <li id="R_3.057.018ab">evam ukto hi vaidehyā saṃrabdho raktalocanaḥ</li>
        <li id="R_3.057.018cd">krodhāt prasphuramāṇauṣṭha āśramād abhinirgataḥ</li>
    </ul>
    <ul id="R_3.057.019">
        <li id="R_3.057.019ab">evaṃ bruvāṇaṃ saumitriṃ rāmaḥ saṃtāpamohitaḥ</li>
        <li id="R_3.057.019cd">abravīd duṣkṛtaṃ saumya tāṃ vinā yat tvam āgataḥ</li>
    </ul>
    <ul id="R_3.057.020">
        <li id="R_3.057.020ab">jānann api samarthaṃ māṃ rakṣasāṃ vinivāraṇe</li>
        <li id="R_3.057.020cd">anena krodhavākyena maithilyā niḥsṛto bhavān</li>
    </ul>
    <ul id="R_3.057.021">
        <li id="R_3.057.021ab">na hi te parituṣyāmi tyaktvā yad yāsi maithilīm</li>
        <li id="R_3.057.021cd">kruddhāyāḥ paruṣaṃ śrutvā striyā yat tvam ihāgataḥ</li>
    </ul>
    <ul id="R_3.057.022">
        <li id="R_3.057.022ab">sarvathā tv apanītaṃ te sītayā yat pracoditaḥ</li>
        <li id="R_3.057.022cd">krodhasya vaśam āgamya nākaroḥ śāsanaṃ mama</li>
    </ul>
    <ul id="R_3.057.023">
        <li id="R_3.057.023ab">asau hi rākṣasaḥ śete śareṇābhihato mayā</li>
        <li id="R_3.057.023cd">mṛgarūpeṇa yenāham āśramād apavāditaḥ</li>
    </ul>
    <ul id="R_3.057.024">
        <li id="R_3.057.024ab">
            <seg type="pāda" n="a">vikṛṣya cāpaṃ paridhāya sāyakaṃ</seg>
            <seg type="pāda" n="b">salīla bāṇena ca tāḍito mayā</seg>
        </li>
        <li id="R_3.057.024cd">
            <seg type="pāda" n="c">mārgīṃ tanuṃ tyajya ca viklavasvaro</seg>
            <seg type="pāda" n="d">babhūva keyūradharaḥ sa rākṣasaḥ</seg>
        </li>
    </ul>
    <ul id="R_3.057.025">
        <li id="R_3.057.025ab">
            <seg type="pāda" n="a">śarāhatenaiva tadārtayā girā</seg>
            <seg type="pāda" n="b">svaraṃ mamālambya sudūrasaṃśravam</seg>
        </li>
        <li id="R_3.057.025cd">
            <seg type="pāda" n="c">udāhṛtaṃ tad vacanaṃ sudāruṇaṃ</seg>
            <seg type="pāda" n="d">tvam āgato yena vihāya maithilīm</seg>
        </li>
    </ul>
    <ul id="R_3.058.001">
        <li id="R_3.058.001ab">bhṛśam āvrajamānasya tasyādhovāmalocanam</li>
        <li id="R_3.058.001cd">prāsphurac cāskhalad rāmo vepathuś cāsya jāyate</li>
    </ul>
    <ul id="R_3.058.002">
        <li id="R_3.058.002ab">upālakṣya nimittāni so 'śubhāni muhur muhuḥ</li>
        <li id="R_3.058.002cd">api kṣemaṃ tu sītāyā iti vai vyājahāra ha</li>
    </ul>
    <ul id="R_3.058.003">
        <li id="R_3.058.003ab">tvaramāṇo jagāmātha sītādarśanalālasaḥ</li>
        <li id="R_3.058.003cd">śūnyam āvasathaṃ dṛṣṭvā babhūvodvignamānasaḥ</li>
    </ul>
    <ul id="R_3.058.004">
        <li id="R_3.058.004ab">udbhramann iva vegena vikṣipan raghunandanaḥ</li>
        <li id="R_3.058.004cd">tatra tatroṭajasthānam abhivīkṣya samantataḥ</li>
    </ul>
    <ul id="R_3.058.005">
        <li id="R_3.058.005ab">dadarśa parṇaśālāṃ ca rahitāṃ sītayā tadā</li>
        <li id="R_3.058.005cd">śriyā virahitāṃ dhvastāṃ hemante padminīm iva</li>
    </ul>
    <ul id="R_3.058.006">
        <li id="R_3.058.006ab">rudantam iva vṛkṣaiś ca mlānapuṣpamṛgadvijam</li>
        <li id="R_3.058.006cd">śriyā vihīnaṃ vidhvastaṃ saṃtyaktavanadaivatam</li>
    </ul>
    <ul id="R_3.058.007">
        <li id="R_3.058.007ab">viprakīrṇājinakuśaṃ vipraviddhabṛsīkaṭam</li>
        <li id="R_3.058.007cd">dṛṣṭvā śūnyoṭajasthānaṃ vilalāpa punaḥ punaḥ</li>
    </ul>
    <ul id="R_3.058.008">
        <li id="R_3.058.008ab">hṛtā mṛtā vā naṣṭā vā bhakṣitā vā bhaviṣyati</li>
        <li id="R_3.058.008cd">nilīnāpy atha vā bhīrur atha vā vanam āśritā</li>
    </ul>
    <ul id="R_3.058.009">
        <li id="R_3.058.009ab">gatā vicetuṃ puṣpāṇi phalāny api ca vā punaḥ</li>
        <li id="R_3.058.009cd">atha vā padminīṃ yātā jalārthaṃ vā nadīṃ gatā</li>
    </ul>
    <ul id="R_3.058.010">
        <li id="R_3.058.010ab">yatnān mṛgayamāṇas tu nāsasāda vane priyām</li>
        <li id="R_3.058.010cd">śokaraktekṣaṇaḥ śokād unmatta iva lakṣyate</li>
    </ul>
    <ul id="R_3.058.011">
        <li id="R_3.058.011ab">vṛkṣād vṛkṣaṃ pradhāvan sa girīṃś cāpi nadīn nadīm</li>
        <li id="R_3.058.011cd">babhūva vilapan rāmaḥ śokapaṅkārṇavaplutaḥ</li>
    </ul>
    <ul id="R_3.058.012">
        <li id="R_3.058.012ab">asti kaccit tvayā dṛṣṭā sā kadambapriyā priyā</li>
        <li id="R_3.058.012cd">kadamba yadi jānīṣe śaṃsa sītāṃ śubhānanām</li>
    </ul>
    <ul id="R_3.058.013">
        <li id="R_3.058.013ab">snigdhapallavasaṃkāśāṃ pītakauśeyavāsinīm</li>
        <li id="R_3.058.013cd">śaṃsasva yadi vā dṛṣṭā bilva bilvopamastanī</li>
    </ul>
    <ul id="R_3.058.014">
        <li id="R_3.058.014ab">atha vārjuna śaṃsa tvaṃ priyāṃ tām arjunapriyām</li>
        <li id="R_3.058.014cd">janakasya sutā bhīrur yadi jīvati vā na vā</li>
    </ul>
    <ul id="R_3.058.015">
        <li id="R_3.058.015ab">kakubhaḥ kakubhoruṃ tāṃ vyaktaṃ jānāti maithilīm</li>
        <li id="R_3.058.015cd">latāpallavapuṣpāḍhyo bhāti hy eṣa vanaspatiḥ</li>
    </ul>
    <ul id="R_3.058.016">
        <li id="R_3.058.016ab">bhramarair upagītaś ca yathā drumavaro hy ayam</li>
        <li id="R_3.058.016cd">eṣa vyaktaṃ vijānāti tilakas tilakapriyām</li>
    </ul>
    <ul id="R_3.058.017">
        <li id="R_3.058.017ab">aśokaśokāpanuda śokopahatacetasaṃ</li>
        <li id="R_3.058.017cd">tvannāmānaṃ kuru kṣipraṃ priyāsaṃdarśanena mām</li>
    </ul>
    <ul id="R_3.058.018">
        <li id="R_3.058.018ab">yadi tāla tvayā dṛṣṭā pakvatālaphalastanī</li>
        <li id="R_3.058.018cd">kathayasva varārohāṃ kāruṣyaṃ yadi te mayi</li>
    </ul>
    <ul id="R_3.058.019">
        <li id="R_3.058.019ab">yadi dṛṣṭā tvayā sītā jambujāmbūnadaprabhā</li>
        <li id="R_3.058.019cd">priyāṃ yadi vijānīṣe niḥśaṅkaṃ kathayasva me</li>
    </ul>
    <ul id="R_3.058.020">
        <li id="R_3.058.020ab">atha vā mṛgaśāvākṣīṃ mṛga jānāsi maithilīm</li>
        <li id="R_3.058.020cd">mṛgaviprekṣaṇī kāntā mṛgībhiḥ sahitā bhavet</li>
    </ul>
    <ul id="R_3.058.021">
        <li id="R_3.058.021ab">gaja sā gajanāsorur yadi dṛṣṭā tvayā bhavet</li>
        <li id="R_3.058.021cd">tāṃ manye viditāṃ tubhyam ākhyāhi varavāraṇa</li>
    </ul>
    <ul id="R_3.058.022">
        <li id="R_3.058.022ab">śārdūla yadi sā dṛṣṭā priyā candranibhānanā</li>
        <li id="R_3.058.022cd">maithilī mama visrabdhaḥ kathayasva na te bhayam</li>
    </ul>
    <ul id="R_3.058.023">
        <li id="R_3.058.023ab">kiṃ dhāvasi priye nūnaṃ dṛṣṭāsi kamalekṣaṇe</li>
        <li id="R_3.058.023cd">vṛkṣeṇācchādya cātmānaṃ kiṃ māṃ na pratibhāṣase</li>
    </ul>
    <ul id="R_3.058.024">
        <li id="R_3.058.024ab">tiṣṭha tiṣṭha varārohe na te 'sti karuṇā mayi</li>
        <li id="R_3.058.024cd">nātyarthaṃ hāsyaśīlāsi kimarthaṃ mām upekṣase</li>
    </ul>
    <ul id="R_3.058.025">
        <li id="R_3.058.025ab">pītakauśeyakenāsi sūcitā varavarṇini</li>
        <li id="R_3.058.025cd">dhāvanty api mayā dṛṣṭā tiṣṭha yady asti sauhṛdam</li>
    </ul>
    <ul id="R_3.058.026">
        <li id="R_3.058.026ab">naiva sā nūnam atha vā hiṃsitā cāruhāsinī</li>
        <li id="R_3.058.026cd">kṛcchraṃ prāptaṃ hi māṃ nūnaṃ yathopekṣitum arhati</li>
    </ul>
    <ul id="R_3.058.027">
        <li id="R_3.058.027ab">vyaktaṃ sā bhakṣitā bālā rākṣasaiḥ piśitāśanaiḥ</li>
        <li id="R_3.058.027cd">vibhajyāṅgāni sarvāṇi mayā virahitā priyā</li>
    </ul>
    <ul id="R_3.058.028">
        <li id="R_3.058.028ab">nūnaṃ tac chubhadantauṣṭhaṃ mukhaṃ niṣprabhatāṃ gatam</li>
        <li id="R_3.058.028cd">sā hi campakavarṇābhā grīvā graiveya śobhitā</li>
    </ul>
    <ul id="R_3.058.029">
        <li id="R_3.058.029ab">komalā vilapantyās tu kāntāyā bhakṣitā śubhā</li>
        <li id="R_3.058.029cd">nūnaṃ vikṣipyamāṇau tau bāhū pallavakomalau</li>
    </ul>
    <ul id="R_3.058.030">
        <li id="R_3.058.030ab">bhakṣitau vepamānāgrau sahastābharaṇāṅgadau</li>
        <li id="R_3.058.030cd">mayā virahitā bālā rakṣasāṃ bhakṣaṇāya vai</li>
    </ul>
    <ul id="R_3.058.031">
        <li id="R_3.058.031ab">sārtheneva parityaktā bhakṣitā bahubāndhavā</li>
        <li id="R_3.058.031cd">hā lakṣmaṇa mahābāho paśyasi tvaṃ priyāṃ kva cit</li>
    </ul>
    <ul id="R_3.058.032">
        <li id="R_3.058.032ab">hā priye kva gatā bhadre hā sīteti punaḥ punaḥ</li>
        <li id="R_3.058.032cd">ity evaṃ vilapan rāmaḥ paridhāvan vanād vanam</li>
    </ul>
    <ul id="R_3.058.033">
        <li id="R_3.058.033ab">kva cid udbhramate vegāt kva cid vibhramate balāt</li>
        <li id="R_3.058.033cd">kva cin matta ivābhāti kāntān veṣaṇatatparaḥ</li>
    </ul>
    <ul id="R_3.058.034">
        <li id="R_3.058.034ab">sa vanāni nadīḥ śailān giriprasravaṇāni ca</li>
        <li id="R_3.058.034cd">kānanāni ca vegena bhramaty aparisaṃsthitaḥ</li>
    </ul>
    <ul id="R_3.058.035">
        <li id="R_3.058.035ab">
            <seg type="pāda" n="a">tathā sa gatvā vipulaṃ mahad vanaṃ</seg>
            <seg type="pāda" n="b">parītya sarvaṃ tv atha maithilīṃ prati</seg>
        </li>
        <li id="R_3.058.035cd">
            <seg type="pāda" n="c">aniṣṭhitāśaḥ sa cakāra mārgaṇe</seg>
            <seg type="pāda" n="d">punaḥ priyāyāḥ paramaṃ pariśramam</seg>
        </li>
    </ul>
    <ul id="R_3.059.001">
        <li id="R_3.059.001ab">dṛṣṭāśramapadaṃ śūnyaṃ rāmo daśarathātmajaḥ</li>
        <li id="R_3.059.001cd">rahitāṃ parṇaśālāṃ ca vidhvastāny āsanāni ca</li>
    </ul>
    <ul id="R_3.059.002">
        <li id="R_3.059.002ab">adṛṣṭvā tatra vaidehīṃ saṃnirīkṣya ca sarvaśaḥ</li>
        <li id="R_3.059.002cd">uvāca rāmaḥ prākruśya pragṛhya rucirau bhujau</li>
    </ul>
    <ul id="R_3.059.003">
        <li id="R_3.059.003ab">kva nu lakṣmaṇa vaidehī kaṃ vā deśam ito gatā</li>
        <li id="R_3.059.003cd">kenāhṛtā vā saumitre bhakṣitā kena vā priyā</li>
    </ul>
    <ul id="R_3.059.004">
        <li id="R_3.059.004ab">vṛṣkeṇāvārya yadi māṃ sīte hasitum icchasi</li>
        <li id="R_3.059.004cd">alaṃ te hasitenādya māṃ bhajasva suduḥkhitam</li>
    </ul>
    <ul id="R_3.059.005">
        <li id="R_3.059.005ab">yaiḥ saha krīḍase sīte viśvastair mṛgapotakaiḥ</li>
        <li id="R_3.059.005cd">ete hīnās tvayā saumye dhyāyanty asrāvilekṣaṇāḥ</li>
    </ul>
    <ul id="R_3.059.006">
        <li id="R_3.059.006ab">mṛtaṃ śokena mahatā sītāharaṇajena mām</li>
        <li id="R_3.059.006cd">paraloke mahārājo nūnaṃ drakṣyati me pitā</li>
    </ul>
    <ul id="R_3.059.007">
        <li id="R_3.059.007ab">kathaṃ pratijñāṃ saṃśrutya mayā tvam abhiyojitaḥ</li>
        <li id="R_3.059.007cd">apūrayitvā taṃ kālaṃ matsakāśam ihāgataḥ</li>
    </ul>
    <ul id="R_3.059.008">
        <li id="R_3.059.008ab">kāmavṛttam anāryaṃ māṃ mṛṣāvādinam eva ca</li>
        <li id="R_3.059.008cd">dhik tvām iti pare loke vyaktaṃ vakṣyati me pitā</li>
    </ul>
    <ul id="R_3.059.009">
        <li id="R_3.059.009ab">vivaśaṃ śokasaṃtaptaṃ dīnaṃ bhagnamanoratham</li>
        <li id="R_3.059.009cd">mām ihotsṛjya karuṇaṃ kīrtir naram ivānṛjum</li>
    </ul>
    <ul id="R_3.059.010">
        <li id="R_3.059.010ab">kva gacchasi varārohe mām utsṛjya sumadhyame</li>
        <li id="R_3.059.010cd">tvayā virahitaś cāhaṃ mokṣye jīvitam ātmanaḥ</li>
    </ul>
    <ul id="R_3.059.011">
        <li id="R_3.059.011ab">itīva vilapan rāmaḥ sītādarśanalālasaḥ</li>
        <li id="R_3.059.011cd">na dadarśa suduḥkhārto rāghavo janakātmajām</li>
    </ul>
    <ul id="R_3.059.012">
        <li id="R_3.059.012ab">anāsādayamānaṃ taṃ sītāṃ daśarathātmajam</li>
        <li id="R_3.059.012cd">paṅkam āsādya vipulaṃ sīdantam iva kuñjaram</li>
        <li id="R_3.059.012ef">lakṣmaṇo rāmam atyartham uvāca hitakāmyayā</li>
    </ul>
    <ul id="R_3.059.013">
        <li id="R_3.059.013ab">mā viṣādaṃ mahābāho kuru yatnaṃ mayā saha</li>
        <li id="R_3.059.013cd">idaṃ ca hi vanaṃ śūra bahukandaraśobhitam</li>
    </ul>
    <ul id="R_3.059.014">
        <li id="R_3.059.014ab">priyakānanasaṃcārā vanonmattā ca maithilī</li>
        <li id="R_3.059.014cd">sā vanaṃ vā praviṣṭā syān nalinīṃ vā supuṣpitām</li>
    </ul>
    <ul id="R_3.059.015">
        <li id="R_3.059.015ab">saritaṃ vāpi saṃprāptā mīnavañjurasevitām</li>
        <li id="R_3.059.015cd">vitrāsayitukāmā vā līnā syāt kānane kva cit</li>
        <li id="R_3.059.015ef">jijñāsamānā vaidehī tvāṃ māṃ ca puruṣarṣabha</li>
    </ul>
    <ul id="R_3.059.016">
        <li id="R_3.059.016ab">tasyā hy anveṣaṇe śrīman kṣipram eva yatāvahe</li>
        <li id="R_3.059.016cd">vanaṃ sarvaṃ vicinuvo yatra sā janakātmajā</li>
        <li id="R_3.059.016ef">manyase yadi kākutstha mā sma śoke manaḥ kṛthāḥ</li>
    </ul>
    <ul id="R_3.059.017">
        <li id="R_3.059.017ab">evam uktas tu sauhārdāl lakṣmaṇena samāhitaḥ</li>
        <li id="R_3.059.017cd">saha saumitriṇā rāmo vicetum upacakrame</li>
        <li id="R_3.059.017ef">tau vanāni girīṃś caiva saritaś ca sarāṃsi ca</li>
    </ul>
    <ul id="R_3.059.018">
        <li id="R_3.059.018ab">nikhilena vicinvantau sītāṃ daśarathātmajau</li>
        <li id="R_3.059.018cd">tasya śailasya sānūni guhāś ca śikharāṇi ca</li>
    </ul>
    <ul id="R_3.059.019">
        <li id="R_3.059.019ab">nikhilena vicinvantau naiva tām abhijagmatuḥ</li>
        <li id="R_3.059.019cd">vicitya sarvataḥ śailaṃ rāmo lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_3.059.020">
        <li id="R_3.059.020ab">neha paśyāmi saumitre vaidehīṃ parvate śubhe</li>
        <li id="R_3.059.020cd">tato duḥkhābhisaṃtapto lakṣmaṇo vākyam abravīt</li>
    </ul>
    <ul id="R_3.059.021">
        <li id="R_3.059.021ab">vicaran daṇḍakāraṇyaṃ bhrātaraṃ dīptatejasaṃ</li>
        <li id="R_3.059.021cd">prāpsyasi tvaṃ mahāprājña maithilīṃ janakātmajām</li>
    </ul>
    <ul id="R_3.059.022">
        <li id="R_3.059.022ab">yathā viṣṇur mahābāhur baliṃ baddhvā mahīm imām</li>
        <li id="R_3.059.022cd">evam uktas tu vīreṇa lakṣmaṇena sa rāghavaḥ</li>
    </ul>
    <ul id="R_3.059.023">
        <li id="R_3.059.023ab">uvāca dīnayā vācā duḥkhābhihatacetanaḥ</li>
        <li id="R_3.059.023cd">vanaṃ sarvaṃ suvicitaṃ padminyaḥ phullapaṅkajāḥ</li>
    </ul>
    <ul id="R_3.059.024">
        <li id="R_3.059.024ab">giriś cāyaṃ mahāprājña bahukandaranirjharaḥ</li>
        <li id="R_3.059.024cd">na hi paśyāmi vaidehīṃ prāṇebhyo 'pi garīyasīm</li>
    </ul>
    <ul id="R_3.059.025">
        <li id="R_3.059.025ab">evaṃ sa vilapan rāmaḥ sītāharaṇakarśitaḥ</li>
        <li id="R_3.059.025cd">dīnaḥ śokasamāviṣṭo muhūrtaṃ vihvalo 'bhavat</li>
    </ul>
    <ul id="R_3.059.026">
        <li id="R_3.059.026ab">sa vihvalitasarvāṅgo gatabuddhir vicetanaḥ</li>
        <li id="R_3.059.026cd">viṣasādāturo dīno niḥśvasyāśītam āyatam</li>
    </ul>
    <ul id="R_3.059.027">
        <li id="R_3.059.027ab">bahuśaḥ sa tu niḥśvasya rāmo rājīvalocanaḥ</li>
        <li id="R_3.059.027cd">hā priyeti vicukrośa bahuśo bāṣpagadgadaḥ</li>
    </ul>
    <ul id="R_3.059.028">
        <li id="R_3.059.028ab">taṃ sāntvayām āsa tato lakṣmaṇaḥ priyabāndhavaḥ</li>
        <li id="R_3.059.028cd">bahuprakāraṃ dharmajñaḥ praśritaḥ praśritāñjaliḥ</li>
    </ul>
    <ul id="R_3.059.029">
        <li id="R_3.059.029ab">anādṛtya tu tad vākyaṃ lakṣmaṇauṣṭhapuṭacyutam</li>
        <li id="R_3.059.029cd">apaśyaṃs tāṃ priyāṃ sītāṃ prākrośat sa punaḥ punaḥ</li>
    </ul>
    <ul id="R_3.060.001">
        <li id="R_3.060.001ab">sa dīno dīnayā vācā lakṣmaṇaṃ vākyam abravīt</li>
        <li id="R_3.060.001cd">śīghraṃ lakṣmaṇa jānīhi gatvā godāvarīṃ nadīm</li>
        <li id="R_3.060.001ef">api godāvarīṃ sītā padmāny ānayituṃ gatā</li>
    </ul>
    <ul id="R_3.060.002">
        <li id="R_3.060.002ab">evam uktas tu rāmeṇa lakṣmaṇaḥ punar eva hi</li>
        <li id="R_3.060.002cd">nadīṃ godāvarīṃ ramyāṃ jagāma laghuvikramaḥ</li>
    </ul>
    <ul id="R_3.060.003">
        <li id="R_3.060.003ab">tāṃ lakṣmaṇas tīrthavatīṃ vicitvā rāmam abravīt</li>
        <li id="R_3.060.003cd">naināṃ paśyāmi tīrtheṣu krośato na śṛṇoti me</li>
    </ul>
    <ul id="R_3.060.004">
        <li id="R_3.060.004ab">kaṃ nu sā deśam āpannā vaidehī kleśanāśinī</li>
        <li id="R_3.060.004cd">na hi taṃ vedmi vai rāma yatra sā tanumadhyamā</li>
    </ul>
    <ul id="R_3.060.005">
        <li id="R_3.060.005ab">lakṣmaṇasya vacaḥ śrutvā dīnaḥ saṃtāpa mohitaḥ</li>
        <li id="R_3.060.005cd">rāmaḥ samabhicakrāma svayaṃ godāvarīṃ nadīm</li>
    </ul>
    <ul id="R_3.060.006">
        <li id="R_3.060.006ab">sa tām upasthito rāmaḥ kva sītety evam abravīt</li>
    </ul>
    <ul id="R_3.060.007">
        <li id="R_3.060.007ab">bhūtāni rākṣasendreṇa vadhārheṇa hṛtām api</li>
        <li id="R_3.060.007cd">na tāṃ śaśaṃsū rāmāya tathā godāvarī nadī</li>
    </ul>
    <ul id="R_3.060.008">
        <li id="R_3.060.008ab">tataḥ pracoditā bhūtaiḥ śaṃsāsmai tāṃ priyām iti</li>
        <li id="R_3.060.008cd">na ca sābhyavadat sītāṃ pṛṣṭā rāmeṇa śocitā</li>
    </ul>
    <ul id="R_3.060.009">
        <li id="R_3.060.009ab">rāvaṇasya ca tad rūpaṃ karmāṇi ca durātmanaḥ</li>
        <li id="R_3.060.009cd">dhyātvā bhayāt tu vaidehīṃ sā nadī na śaśaṃsa tām</li>
    </ul>
    <ul id="R_3.060.010">
        <li id="R_3.060.010ab">nirāśas tu tayā nadyā sītāyā darśane kṛtaḥ</li>
        <li id="R_3.060.010cd">uvāca rāmaḥ saumitriṃ sītādarśanakarśitaḥ</li>
    </ul>
    <ul id="R_3.060.011">
        <li id="R_3.060.011ab">kiṃ nu lakṣmaṇa vakṣyāmi sametya janakaṃ vacaḥ</li>
        <li id="R_3.060.011cd">mātaraṃ caiva vaidehyā vinā tām aham apriyam</li>
    </ul>
    <ul id="R_3.060.012">
        <li id="R_3.060.012ab">yā me rājyavihīnasya vane vanyena jīvataḥ</li>
        <li id="R_3.060.012cd">sarvaṃ vyapanayac chokaṃ vaidehī kva nu sā gatā</li>
    </ul>
    <ul id="R_3.060.013">
        <li id="R_3.060.013ab">jñātipakṣavihīnasya rājaputrīm apaśyataḥ</li>
        <li id="R_3.060.013cd">manye dīrghā bhaviṣyanti rātrayo mama jāgrataḥ</li>
    </ul>
    <ul id="R_3.060.014">
        <li id="R_3.060.014ab">godāvarīṃ janasthānam imaṃ prasravaṇaṃ girim</li>
        <li id="R_3.060.014cd">sarvāṇy anucariṣyāmi yadi sītā hi dṛśyate</li>
    </ul>
    <ul id="R_3.060.015">
        <li id="R_3.060.015ab">evaṃ saṃbhāṣamāṇau tāv anyonyaṃ bhrātarāv ubhau</li>
        <li id="R_3.060.015cd">vasuṃdharāyāṃ patitaṃ puṣpamārgam apaśyatām</li>
    </ul>
    <ul id="R_3.060.016">
        <li id="R_3.060.016ab">tāṃ puṣpavṛṣṭiṃ patitāṃ dṛṣṭvā rāmo mahītale</li>
        <li id="R_3.060.016cd">uvāca lakṣmaṇaṃ vīro duḥkhito duḥkhitaṃ vacaḥ</li>
    </ul>
    <ul id="R_3.060.017">
        <li id="R_3.060.017ab">abhijānāmi puṣpāṇi tānīmāmīha lakṣmaṇa</li>
        <li id="R_3.060.017cd">apinaddhāni vaidehyā mayā dattāni kānane</li>
    </ul>
    <ul id="R_3.060.018">
        <li id="R_3.060.018ab">evam uktvā mahābāhur lakṣmaṇaṃ puruṣarṣabham</li>
        <li id="R_3.060.018cd">kruddho 'bravīd giriṃ tatra siṃhaḥ kṣudramṛgaṃ yathā</li>
    </ul>
    <ul id="R_3.060.019">
        <li id="R_3.060.019ab">tāṃ hemavarṇāṃ hemābhāṃ sītāṃ darśaya parvata</li>
        <li id="R_3.060.019cd">yāvat sānūni sarvāṇi na te vidhvaṃsayāmy aham</li>
    </ul>
    <ul id="R_3.060.020">
        <li id="R_3.060.020ab">mama bāṇāgninirdagdho bhasmībhūto bhaviṣyasi</li>
        <li id="R_3.060.020cd">asevyaḥ satataṃ caiva nistṛṇadrumapallavaḥ</li>
    </ul>
    <ul id="R_3.060.021">
        <li id="R_3.060.021ab">imāṃ vā saritaṃ cādya śoṣayiṣyāmi lakṣmaṇa</li>
        <li id="R_3.060.021cd">yadi nākhyāti me sītām adya candranibhānanām</li>
    </ul>
    <ul id="R_3.060.022">
        <li id="R_3.060.022ab">evaṃ sa ruṣito rāmo didhakṣann iva cakṣuṣā</li>
        <li id="R_3.060.022cd">dadarśa bhūmau niṣkrāntaṃ rākṣasasya padaṃ mahat</li>
    </ul>
    <ul id="R_3.060.023">
        <li id="R_3.060.023ab">sa samīkṣya parikrāntaṃ sītāyā rākṣasasya ca</li>
        <li id="R_3.060.023cd">saṃbhrāntahṛdayo rāmaḥ śaśaṃsa bhrātaraṃ priyam</li>
    </ul>
    <ul id="R_3.060.024">
        <li id="R_3.060.024ab">paśya lakṣmaṇa vaidehyāḥ śīrṇāḥ kanakabindavaḥ</li>
        <li id="R_3.060.024cd">bhūṣaṇānāṃ hi saumitre mālyāni vividhāni ca</li>
    </ul>
    <ul id="R_3.060.025">
        <li id="R_3.060.025ab">taptabindunikāśaiś ca citraiḥ kṣatajabindubhiḥ</li>
        <li id="R_3.060.025cd">āvṛtaṃ paśya saumitre sarvato dharaṇītalam</li>
    </ul>
    <ul id="R_3.060.026">
        <li id="R_3.060.026ab">manye lakṣmaṇa vaidehī rākṣasaiḥ kāmarūpibhiḥ</li>
        <li id="R_3.060.026cd">bhittvā bhittvā vibhaktā vā bhakṣitā vā bhaviṣyati</li>
    </ul>
    <ul id="R_3.060.027">
        <li id="R_3.060.027ab">tasya nimittaṃ vaidehyā dvayor vivadamānayoḥ</li>
        <li id="R_3.060.027cd">babhūva yuddhaṃ saumitre ghoraṃ rākṣasayor iha</li>
    </ul>
    <ul id="R_3.060.028">
        <li id="R_3.060.028ab">muktāmaṇicitaṃ cedaṃ tapanīyavibhūṣitam</li>
        <li id="R_3.060.028cd">dharaṇyāṃ patitaṃ saumya kasya bhagnaṃ mahad dhanuḥ</li>
    </ul>
    <ul id="R_3.060.029">
        <li id="R_3.060.029ab">taruṇādityasaṃkāśaṃ vaidūryagulikācitam</li>
        <li id="R_3.060.029cd">viśīrṇaṃ patitaṃ bhūmau kavacaṃ kasya kāñcanam</li>
    </ul>
    <ul id="R_3.060.030">
        <li id="R_3.060.030ab">chatraṃ śataśalākaṃ ca divyamālyopaśobhitam</li>
        <li id="R_3.060.030cd">bhagnadaṇḍam idaṃ kasya bhūmau saumya nipātitam</li>
    </ul>
    <ul id="R_3.060.031">
        <li id="R_3.060.031ab">kāñcanoraśchadāś ceme piśācavadanāḥ kharāḥ</li>
        <li id="R_3.060.031cd">bhīmarūpā mahākāyāḥ kasya vā nihatā raṇe</li>
    </ul>
    <ul id="R_3.060.032">
        <li id="R_3.060.032ab">dīptapāvakasaṃkāśo dyutimān samaradhvajaḥ</li>
        <li id="R_3.060.032cd">apaviddhaś ca bhagnaś ca kasya sāṃgrāmiko rathaḥ</li>
    </ul>
    <ul id="R_3.060.033">
        <li id="R_3.060.033ab">rathākṣamātrā viśikhās tapanīyavibhūṣaṇāḥ</li>
        <li id="R_3.060.033cd">kasyeme 'bhihatā bāṇāḥ prakīrṇā ghorakarmaṇaḥ</li>
    </ul>
    <ul id="R_3.060.034">
        <li id="R_3.060.034ab">vairaṃ śataguṇaṃ paśya mamedaṃ jīvitāntakam</li>
        <li id="R_3.060.034cd">sughorahṛdayaiḥ saumya rākṣasaiḥ kāmarūpibhiḥ</li>
    </ul>
    <ul id="R_3.060.035">
        <li id="R_3.060.035ab">hṛtā mṛtā vā sītā hi bhakṣitā vā tapasvinī</li>
        <li id="R_3.060.035cd">na dharmas trāyate sītāṃ hriyamāṇāṃ mahāvane</li>
    </ul>
    <ul id="R_3.060.036">
        <li id="R_3.060.036ab">bhakṣitāyāṃ hi vaidehyāṃ hṛtāyām api lakṣmaṇa</li>
        <li id="R_3.060.036cd">ke hi loke priyaṃ kartuṃ śaktāḥ saumya mameśvarāḥ</li>
    </ul>
    <ul id="R_3.060.037">
        <li id="R_3.060.037ab">kartāram api lokānāṃ śūraṃ karuṇavedinam</li>
        <li id="R_3.060.037cd">ajñānād avamanyeran sarvabhūtāni lakṣmaṇa</li>
    </ul>
    <ul id="R_3.060.038">
        <li id="R_3.060.038ab">mṛduṃ lokahite yuktaṃ dāntaṃ karuṇavedinam</li>
        <li id="R_3.060.038cd">nirvīrya iti manyante nūnaṃ māṃ tridaśeśvarāḥ</li>
    </ul>
    <ul id="R_3.060.039">
        <li id="R_3.060.039ab">māṃ prāpya hi guṇo doṣaḥ saṃvṛttaḥ paśya lakṣmaṇa</li>
        <li id="R_3.060.039cd">adyaiva sarvabhūtānāṃ rakṣasām abhavāya ca</li>
        <li id="R_3.060.039ef">saṃhṛtyaiva śaśijyotsnāṃ mahān sūrya ivoditaḥ</li>
    </ul>
    <ul id="R_3.060.040">
        <li id="R_3.060.040ab">naiva yakṣā na gandharvā na piśācā na rākṣasāḥ</li>
        <li id="R_3.060.040cd">kiṃnarā vā manuṣyā vā sukhaṃ prāpsyanti lakṣmaṇa</li>
    </ul>
    <ul id="R_3.060.041">
        <li id="R_3.060.041ab">mamāstrabāṇasaṃpūrṇam ākāśaṃ paśya lakṣmaṇa</li>
        <li id="R_3.060.041cd">niḥsaṃpātaṃ kariṣyāmi hy adya trailokyacāriṇām</li>
    </ul>
    <ul id="R_3.060.042">
        <li id="R_3.060.042ab">saṃniruddhagrahagaṇam āvāritaniśākaram</li>
        <li id="R_3.060.042cd">vipranaṣṭānalamarudbhāskaradyutisaṃvṛtam</li>
    </ul>
    <ul id="R_3.060.043">
        <li id="R_3.060.043ab">vinirmathitaśailāgraṃ śuṣyamāṇajalāśayam</li>
        <li id="R_3.060.043cd">dhvastadrumalatāgulmaṃ vipraṇāśitasāgaram</li>
    </ul>
    <ul id="R_3.060.044">
        <li id="R_3.060.044ab">na tāṃ kuśalinīṃ sītāṃ pradāsyanti mameśvarāḥ</li>
        <li id="R_3.060.044cd">asmin muhūrte saumitre mama drakṣyanti vikramam</li>
    </ul>
    <ul id="R_3.060.045">
        <li id="R_3.060.045ab">nākāśam utpatiṣyanti sarvabhūtāni lakṣmaṇa</li>
        <li id="R_3.060.045cd">mama cāpaguṇān muktair bāṇajālair nirantaram</li>
    </ul>
    <ul id="R_3.060.046">
        <li id="R_3.060.046ab">arditaṃ mama nārācair dhvastabhrāntamṛgadvijam</li>
        <li id="R_3.060.046cd">samākulam amaryādaṃ jagat paśyādya lakṣmaṇa</li>
    </ul>
    <ul id="R_3.060.047">
        <li id="R_3.060.047ab">ākarṇapūrṇair iṣubhir jīvalokaṃ durāvaraiḥ</li>
        <li id="R_3.060.047cd">kariṣye maithilīhetor apiśācam arākṣasaṃ</li>
    </ul>
    <ul id="R_3.060.048">
        <li id="R_3.060.048ab">mama roṣaprayuktānāṃ sāyakānāṃ balaṃ surāḥ</li>
        <li id="R_3.060.048cd">drakṣyanty adya vimuktānām amarṣād dūragāminām</li>
    </ul>
    <ul id="R_3.060.049">
        <li id="R_3.060.049ab">naiva devā na daiteyā na piśācā na rākṣasāḥ</li>
        <li id="R_3.060.049cd">bhaviṣyanti mama krodhāt trailokye vipraṇāśite</li>
    </ul>
    <ul id="R_3.060.050">
        <li id="R_3.060.050ab">devadānavayakṣāṇāṃ lokā ye rakṣasām api</li>
        <li id="R_3.060.050cd">bahudhā nipatiṣyanti bāṇaughaiḥ śakulīkṛtāḥ</li>
        <li id="R_3.060.050ef">nirmaryādān imāṃl lokān kariṣyāmy adya sāyakaiḥ</li>
    </ul>
    <ul id="R_3.060.051">
        <li id="R_3.060.051ab">yathā jarā yathā mṛtyur yathākālo yathāvidhiḥ</li>
        <li id="R_3.060.051cd">nityaṃ na pratihanyante sarvabhūteṣu lakṣmaṇa</li>
        <li id="R_3.060.051ef">tathāhaṃ krodhasaṃyukto na nivāryo 'smy asaṃśayam</li>
    </ul>
    <ul id="R_3.060.052">
        <li id="R_3.060.052ab">
            <seg type="pāda" n="a">pureva me cārudatīm aninditāṃ</seg>
            <seg type="pāda" n="b">diśanti sītāṃ yadi nādya maithilīm</seg>
        </li>
        <li id="R_3.060.052cd">
            <seg type="pāda" n="c">sadevagandharvamanuṣya pannagaṃ</seg>
            <seg type="pāda" n="d">jagat saśailaṃ parivartayāmy aham</seg>
        </li>
    </ul>
    <ul id="R_3.061.001">
        <li id="R_3.061.001ab">tapyamānaṃ tathā rāmaṃ sītāharaṇakarśitam</li>
        <li id="R_3.061.001cd">lokānām abhave yuktaṃ sāmvartakam ivānalam</li>
    </ul>
    <ul id="R_3.061.002">
        <li id="R_3.061.002ab">vīkṣamāṇaṃ dhanuḥ sajyaṃ niḥśvasantaṃ muhur muhuḥ</li>
        <li id="R_3.061.002cd">hantukāmaṃ paśuṃ rudraṃ kruddhaṃ dakṣakratau yathā</li>
    </ul>
    <ul id="R_3.061.003">
        <li id="R_3.061.003ab">adṛṣṭapūrvaṃ saṃkruddhaṃ dṛṣṭvā rāmaṃ sa lakṣmaṇaḥ</li>
        <li id="R_3.061.003cd">abravīt prāñjalir vākyaṃ mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_3.061.004">
        <li id="R_3.061.004ab">purā bhūtvā mṛdur dāntaḥ sarvabhūtahite rataḥ</li>
        <li id="R_3.061.004cd">na krodhavaśam āpannaḥ prakṛtiṃ hātum arhasi</li>
    </ul>
    <ul id="R_3.061.005">
        <li id="R_3.061.005ab">candre lakṣṇīḥ prabhā sūrye gatir vāyau bhuvi kṣamā</li>
        <li id="R_3.061.005cd">etac ca niyataṃ sarvaṃ tvayi cānuttamaṃ yaśaḥ</li>
    </ul>
    <ul id="R_3.061.006">
        <li id="R_3.061.006ab">na tu jānāmi kasyāyaṃ bhagnaḥ sāṃgrāmiko rathaḥ</li>
        <li id="R_3.061.006cd">kena vā kasya vā hetoḥ sāyudhaḥ saparicchadaḥ</li>
    </ul>
    <ul id="R_3.061.007">
        <li id="R_3.061.007ab">khuranemikṣataś cāyaṃ sikto rudhirabindubhiḥ</li>
        <li id="R_3.061.007cd">deśo nivṛttasaṃgrāmaḥ sughoraḥ pārthivātmaja</li>
    </ul>
    <ul id="R_3.061.008">
        <li id="R_3.061.008ab">ekasya tu vimardo 'yaṃ na dvayor vadatāṃ vara</li>
        <li id="R_3.061.008cd">na hi vṛttaṃ hi paśyāmi balasya mahataḥ padam</li>
    </ul>
    <ul id="R_3.061.009">
        <li id="R_3.061.009ab">naikasya tu kṛte lokān vināśayitum arhasi</li>
        <li id="R_3.061.009cd">yuktadaṇḍā hi mṛdavaḥ praśāntā vasudhādhipāḥ</li>
    </ul>
    <ul id="R_3.061.010">
        <li id="R_3.061.010ab">sadā tvaṃ sarvabhūtānāṃ śaraṇyaḥ paramā gatiḥ</li>
        <li id="R_3.061.010cd">ko nu dārapraṇāśaṃ te sādhu manyeta rāghava</li>
    </ul>
    <ul id="R_3.061.011">
        <li id="R_3.061.011ab">saritaḥ sāgarāḥ śailā devagandharvadānavāḥ</li>
        <li id="R_3.061.011cd">nālaṃ te vipriyaṃ kartuṃ dīkṣitasyeva sādhavaḥ</li>
    </ul>
    <ul id="R_3.061.012">
        <li id="R_3.061.012ab">yena rājan hṛtā sītā tam anveṣitum arhasi</li>
        <li id="R_3.061.012cd">maddvitīyo dhanuṣpāṇiḥ sahāyaiḥ paramarṣibhiḥ</li>
    </ul>
    <ul id="R_3.061.013">
        <li id="R_3.061.013ab">samudraṃ ca viceṣyāmaḥ parvatāṃś ca vanāni ca</li>
        <li id="R_3.061.013cd">guhāś ca vividhā ghorā nalinīḥ pārvatīś ca ha</li>
    </ul>
    <ul id="R_3.061.014">
        <li id="R_3.061.014ab">devagandharvalokāṃś ca viceṣyāmaḥ samāhitāḥ</li>
        <li id="R_3.061.014cd">yāvan nādhigamiṣyāmas tava bhāryāpahāriṇam</li>
    </ul>
    <ul id="R_3.061.015">
        <li id="R_3.061.015ab">na cet sāmnā pradāsyanti patnīṃ te tridaśeśvarāḥ</li>
        <li id="R_3.061.015cd">kosalendra tataḥ paścāt prāptakālaṃ kariṣyasi</li>
    </ul>
    <ul id="R_3.061.016">
        <li id="R_3.061.016ab">
            <seg type="pāda" n="a">śīlena sāmnā vinayena sītāṃ</seg>
            <seg type="pāda" n="b">nayena na prāpsyasi cen narendra</seg>
        </li>
        <li id="R_3.061.016cd">
            <seg type="pāda" n="c">tataḥ samutsādaya hemapuṅkhair</seg>
            <seg type="pāda" n="d">mahendravajrapratimaiḥ śaraughaiḥ</seg>
        </li>
    </ul>
    <ul id="R_3.062.001">
        <li id="R_3.062.001ab">taṃ tathā śokasaṃtaptaṃ vilapantam anāthavat</li>
        <li id="R_3.062.001cd">mohena mahatāviṣṭaṃ paridyūnam acetanam</li>
    </ul>
    <ul id="R_3.062.002">
        <li id="R_3.062.002ab">tataḥ saumitrir āśvāsya muhūrtād iva lakṣmaṇaḥ</li>
        <li id="R_3.062.002cd">rāmaṃ saṃbodhayām āsa caraṇau cābhipīḍayan</li>
    </ul>
    <ul id="R_3.062.003">
        <li id="R_3.062.003ab">mahatā tapasā rāma mahatā cāpi karmaṇā</li>
        <li id="R_3.062.003cd">rājñā daśarathenāsīl labdho 'mṛtam ivāmaraiḥ</li>
    </ul>
    <ul id="R_3.062.004">
        <li id="R_3.062.004ab">tava caiva guṇair baddhas tvadviyogān mahīpatiḥ</li>
        <li id="R_3.062.004cd">rājā devatvam āpanno bharatasya yathā śrutam</li>
    </ul>
    <ul id="R_3.062.005">
        <li id="R_3.062.005ab">yadi duḥkham idaṃ prāptaṃ kākutstha na sahiṣyase</li>
        <li id="R_3.062.005cd">prākṛtaś cālpasattvaś ca itaraḥ kaḥ sahiṣyati</li>
    </ul>
    <ul id="R_3.062.006">
        <li id="R_3.062.006ab">duḥkhito hi bhavāṃl lokāṃs tejasā yadi dhakṣyate</li>
        <li id="R_3.062.006cd">ārtāḥ prajā naravyāghra kva nu yāsyanti nirvṛtim</li>
    </ul>
    <ul id="R_3.062.007">
        <li id="R_3.062.007ab">lokasvabhāva evaiṣa yayātir nahuṣātmajaḥ</li>
        <li id="R_3.062.007cd">gataḥ śakreṇa sālokyam anayas taṃ samaspṛśat</li>
    </ul>
    <ul id="R_3.062.008">
        <li id="R_3.062.008ab">maharṣayo vasiṣṭhas tu yaḥ pitur naḥ purohitaḥ</li>
        <li id="R_3.062.008cd">ahnā putraśataṃ jajñe tathaivāsya punar hatam</li>
    </ul>
    <ul id="R_3.062.009">
        <li id="R_3.062.009ab">yā ceyaṃ jagato mātā devī lokanamaskṛtā</li>
        <li id="R_3.062.009cd">asyāś ca calanaṃ bhūmer dṛśyate satyasaṃśrava</li>
    </ul>
    <ul id="R_3.062.010">
        <li id="R_3.062.010ab">yau cemau jagatāṃ netre yatra sarvaṃ pratiṣṭhitam</li>
        <li id="R_3.062.010cd">ādityacandrau grahaṇam abhyupetau mahābalau</li>
    </ul>
    <ul id="R_3.062.011">
        <li id="R_3.062.011ab">sumahānty api bhūtāni devāś ca puruṣarṣabha</li>
        <li id="R_3.062.011cd">na daivasya pramuñcanti sarvabhūtāni dehinaḥ</li>
    </ul>
    <ul id="R_3.062.012">
        <li id="R_3.062.012ab">śakrādiṣv api deveṣu vartamānau nayānayau</li>
        <li id="R_3.062.012cd">śrūyete naraśārdūla na tvaṃ vyathitum arhasi</li>
    </ul>
    <ul id="R_3.062.013">
        <li id="R_3.062.013ab">naṣṭāyām api vaidehyāṃ hṛtāyām api cānagha</li>
        <li id="R_3.062.013cd">śocituṃ nārhase vīra yathānyaḥ prākṛtas tathā</li>
    </ul>
    <ul id="R_3.062.014">
        <li id="R_3.062.014ab">tvadvidhā hi na śocanti satataṃ satyadarśinaḥ</li>
        <li id="R_3.062.014cd">sumahatsv api kṛcchreṣu rāmānirviṇṇadarśaṇāḥ</li>
    </ul>
    <ul id="R_3.062.015">
        <li id="R_3.062.015ab">tattvato hi naraśreṣṭha buddhyā samanucintaya</li>
        <li id="R_3.062.015cd">buddhyā yuktā mahāprājñā vijānanti śubhāśubhe</li>
    </ul>
    <ul id="R_3.062.016">
        <li id="R_3.062.016ab">adṛṣṭaguṇadoṣāṇām adhṛtānāṃ ca karmaṇām</li>
        <li id="R_3.062.016cd">nāntareṇa kriyāṃ teṣāṃ phalam iṣṭaṃ pravartate</li>
    </ul>
    <ul id="R_3.062.017">
        <li id="R_3.062.017ab">mām eva hi purā vīra tvam eva bahuṣo 'nvaśāḥ</li>
        <li id="R_3.062.017cd">anuśiṣyād dhi ko nu tvām api sākṣād bṛhaspatiḥ</li>
    </ul>
    <ul id="R_3.062.018">
        <li id="R_3.062.018ab">buddhiś ca te mahāprājña devair api duranvayā</li>
        <li id="R_3.062.018cd">śokenābhiprasuptaṃ te jñānaṃ saṃbodhayāmy aham</li>
    </ul>
    <ul id="R_3.062.019">
        <li id="R_3.062.019ab">divyaṃ ca mānuṣaṃ caivam ātmanaś ca parākramam</li>
        <li id="R_3.062.019cd">ikṣvākuvṛṣabhāvekṣya yatasva dviṣatāṃ badhe</li>
    </ul>
    <ul id="R_3.062.020">
        <li id="R_3.062.020ab">kiṃ te sarvavināśena kṛtena puruṣarṣabha</li>
        <li id="R_3.062.020cd">tam eva tu ripuṃ pāpaṃ vijñāyoddhartum arhasi</li>
    </ul>
    <ul id="R_3.063.001">
        <li id="R_3.063.001ab">pūrvajo 'py uktamātras tu lakṣmaṇena subhāṣitam</li>
        <li id="R_3.063.001cd">sāragrāhī mahāsāraṃ pratijagrāha rāghavaḥ</li>
    </ul>
    <ul id="R_3.063.002">
        <li id="R_3.063.002ab">saṃnigṛhya mahābāhuḥ pravṛddhaṃ kopam ātmanaḥ</li>
        <li id="R_3.063.002cd">avaṣṭabhya dhanuś citraṃ rāmo lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_3.063.003">
        <li id="R_3.063.003ab">kiṃ kariṣyāvahe vatsa kva vā gacchāva lakṣmaṇa</li>
        <li id="R_3.063.003cd">kenopāyena paśyeyaṃ sītām iti vicintaya</li>
    </ul>
    <ul id="R_3.063.004">
        <li id="R_3.063.004ab">taṃ tathā paritāpārtaṃ lakṣmaṇo rāmam abravīt</li>
        <li id="R_3.063.004cd">idam eva janasthānaṃ tvam anveṣitum arhasi</li>
    </ul>
    <ul id="R_3.063.005">
        <li id="R_3.063.005ab">rākṣasair bahubhiḥ kīrṇaṃ nānādrumalatāyutam</li>
        <li id="R_3.063.005cd">santīha giridurgāṇi nirdarāḥ kandarāṇi ca</li>
    </ul>
    <ul id="R_3.063.006">
        <li id="R_3.063.006ab">guhāś ca vividhā ghorā nānāmṛgagaṇākulāḥ</li>
        <li id="R_3.063.006cd">āvāsāḥ kiṃnarāṇāṃ ca gandharvabhavanāni ca</li>
    </ul>
    <ul id="R_3.063.007">
        <li id="R_3.063.007ab">tāni yukto mayā sārdhaṃ tvam anveṣitum arhasi</li>
        <li id="R_3.063.007cd">tvadvidho buddhisaṃpannā māhātmāno nararṣabha</li>
    </ul>
    <ul id="R_3.063.008">
        <li id="R_3.063.008ab">āpatsu na prakampante vāyuvegair ivācalāḥ</li>
        <li id="R_3.063.008cd">ity uktas tad vanaṃ sarvaṃ vicacāra salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_3.063.009">
        <li id="R_3.063.009ab">kruddho rāmaḥ śaraṃ ghoraṃ saṃdhāya dhanuṣi kṣuram</li>
        <li id="R_3.063.009cd">tataḥ parvatakūṭābhaṃ mahābhāgaṃ dvijottamam</li>
    </ul>
    <ul id="R_3.063.010">
        <li id="R_3.063.010ab">dadarśa patitaṃ bhūmau kṣatajārdraṃ jaṭāyuṣam</li>
        <li id="R_3.063.010cd">taṃ dṛṣṭvā giriśṛṅgābhaṃ rāmo lakṣmaṇam abravīt</li>
        <li id="R_3.063.010ef">anena sītā vaidehī bhakṣitā nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_3.063.011">
        <li id="R_3.063.011ab">gṛdhrarūpam idaṃ vyaktaṃ rakṣo bhramati kānanam</li>
        <li id="R_3.063.011cd">bhakṣayitvā viśālākṣīm āste sītāṃ yathāsukham</li>
        <li id="R_3.063.011ef">enaṃ vadhiṣye dīptāgrair ghorair bāṇair ajihmagaiḥ</li>
    </ul>
    <ul id="R_3.063.012">
        <li id="R_3.063.012ab">ity uktvābhyapatad gṛdhraṃ saṃdhāya dhanuṣi kṣuram</li>
        <li id="R_3.063.012cd">kruddho rāmaḥ samudrāntāṃ cālayann iva medinīm</li>
    </ul>
    <ul id="R_3.063.013">
        <li id="R_3.063.013ab">taṃ dīnadīnayā vācā saphenaṃ rudhiraṃ vaman</li>
        <li id="R_3.063.013cd">abhyabhāṣata pakṣī tu rāmaṃ daśarathātmajam</li>
    </ul>
    <ul id="R_3.063.014">
        <li id="R_3.063.014ab">yām oṣadhim ivāyuṣmann anveṣasi mahāvane</li>
        <li id="R_3.063.014cd">sā devī mama ca prāṇā rāvaṇenobhayaṃ hṛtam</li>
    </ul>
    <ul id="R_3.063.015">
        <li id="R_3.063.015ab">tvayā virahitā devī lakṣmaṇena ca rāghava</li>
        <li id="R_3.063.015cd">hriyamāṇā mayā dṛṣṭā rāvaṇena balīyasā</li>
    </ul>
    <ul id="R_3.063.016">
        <li id="R_3.063.016ab">sītām abhyavapan no 'haṃ rāvaṇaś ca raṇe mayā</li>
        <li id="R_3.063.016cd">vidhvaṃsitarathacchatraḥ pātito dharaṇītale</li>
    </ul>
    <ul id="R_3.063.017">
        <li id="R_3.063.017ab">etad asya dhanur bhagnam etad asya śarāvaram</li>
        <li id="R_3.063.017cd">ayam asya raṇe rāma bhagnaḥ sāṃgrāmiko rathaḥ</li>
    </ul>
    <ul id="R_3.063.018">
        <li id="R_3.063.018ab">pariśrāntasya me pakṣau chittvā khaḍgena rāvaṇaḥ</li>
        <li id="R_3.063.018cd">sītām ādāya vaidehīm utpapāta vihāyasaṃ</li>
        <li id="R_3.063.018ef">rakṣasā nihataṃ pūrvma na māṃ hantuṃ tvam arhasi</li>
    </ul>
    <ul id="R_3.063.019">
        <li id="R_3.063.019ab">rāmas tasya tu vijñāya sītāsaktāṃ priyāṃ kathām</li>
        <li id="R_3.063.019cd">gṛdhrarājaṃ pariṣvajya ruroda sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_3.063.020">
        <li id="R_3.063.020ab">ekam ekāyane durge niḥśvasantaṃ kathaṃ cana</li>
        <li id="R_3.063.020cd">samīkṣya duḥkhito rāmaḥ saumitrim idam abravīt</li>
    </ul>
    <ul id="R_3.063.021">
        <li id="R_3.063.021ab">rājyād bhraṃśo vane vāsaḥ sītā naṣṭā hato dvijaḥ</li>
        <li id="R_3.063.021cd">īdṛśīyaṃ mamālakṣmīr nirdahed api pāvakam</li>
    </ul>
    <ul id="R_3.063.022">
        <li id="R_3.063.022ab">saṃpūrṇam api ced adya pratareyaṃ mahodadhim</li>
        <li id="R_3.063.022cd">so 'pi nūnaṃ mamālakṣmyā viśuṣyet saritāṃ patiḥ</li>
    </ul>
    <ul id="R_3.063.023">
        <li id="R_3.063.023ab">nāsty abhāgyataro loke matto 'smin sacarācare</li>
        <li id="R_3.063.023cd">yeneyaṃ mahatī prāptā mayā vyasanavāgurā</li>
    </ul>
    <ul id="R_3.063.024">
        <li id="R_3.063.024ab">ayaṃ pitṛvayasyo me gṛdhrarājo jarānvitaḥ</li>
        <li id="R_3.063.024cd">śete vinihato bhūmau mama bhāgyaviparyayāt</li>
    </ul>
    <ul id="R_3.063.025">
        <li id="R_3.063.025ab">ity evam uktvā bahuśo rāghavaḥ sahalakṣmaṇaḥ</li>
        <li id="R_3.063.025cd">jaṭāyuṣaṃ ca pasparśa pitṛsnehaṃ nidarśayan</li>
    </ul>
    <ul id="R_3.063.026">
        <li id="R_3.063.026ab">
            <seg type="pāda" n="a">nikṛttapakṣaṃ rudhirāvasiktaṃ</seg>
            <seg type="pāda" n="b">taṃ gṛdhrarājaṃ parirabhya rāmaḥ</seg>
        </li>
        <li id="R_3.063.026cd">
            <seg type="pāda" n="c">kva maithili prāṇasamā mameti</seg>
            <seg type="pāda" n="d">vimucya vācaṃ nipapāta bhūmau</seg>
        </li>
    </ul>
    <ul id="R_3.064.001">
        <li id="R_3.064.001ab">rāmaḥ prekṣya tu taṃ gṛdhraṃ bhuvi raudreṇa pātitam</li>
        <li id="R_3.064.001cd">saumitriṃ mitrasaṃpannam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_3.064.002">
        <li id="R_3.064.002ab">mamāyaṃ nūnam artheṣu yatamāno vihaṃgamaḥ</li>
        <li id="R_3.064.002cd">rākṣasena hataḥ saṃkhye prāṇāṃs tyajati dustyajān</li>
    </ul>
    <ul id="R_3.064.003">
        <li id="R_3.064.003ab">ayam asya śarīre 'smin prāṇo lakṣmaṇa vidyate</li>
        <li id="R_3.064.003cd">tathā svaravihīno 'yaṃ viklavaṃ samudīkṣate</li>
    </ul>
    <ul id="R_3.064.004">
        <li id="R_3.064.004ab">jaṭāyo yadi śaknoṣi vākyaṃ vyāharituṃ punaḥ</li>
        <li id="R_3.064.004cd">sītām ākhyāhi bhadraṃ te vadham ākhyāhi cātmanaḥ</li>
    </ul>
    <ul id="R_3.064.005">
        <li id="R_3.064.005ab">kiṃnimitto 'harat sītāṃ rāvaṇas tasya kiṃ mayā</li>
        <li id="R_3.064.005cd">aparāddhaṃ tu yaṃ dṛṣṭvā rāvaṇena hṛtā priyā</li>
    </ul>
    <ul id="R_3.064.006">
        <li id="R_3.064.006ab">kathaṃ tac candrasaṃkāśaṃ mukham āsīn manoharam</li>
        <li id="R_3.064.006cd">sītayā kāni coktāni tasmin kāle dvijottama</li>
    </ul>
    <ul id="R_3.064.007">
        <li id="R_3.064.007ab">kathaṃvīryaḥ kathaṃrūpaḥ kiṃkarmā sa ca rākṣasaḥ</li>
        <li id="R_3.064.007cd">kva cāsya bhavanaṃ tāta brūhi me paripṛcchataḥ</li>
    </ul>
    <ul id="R_3.064.008">
        <li id="R_3.064.008ab">tam udvīkṣyātha dīnātmā vilapantam anantaram</li>
        <li id="R_3.064.008cd">vācātisannayā rāmaṃ jaṭāyur idam abravīt</li>
    </ul>
    <ul id="R_3.064.009">
        <li id="R_3.064.009ab">sā hṛtā rākṣasendreṇa rāvaṇena vihāyasā</li>
        <li id="R_3.064.009cd">māyām āsthāya vipulāṃ vātadurdinasaṃkulām</li>
    </ul>
    <ul id="R_3.064.010">
        <li id="R_3.064.010ab">pariśrāntasya me tāta pakṣau chittvā niśācaraḥ</li>
        <li id="R_3.064.010cd">sītām ādāya vaidehīṃ prayāto dakṣiṇā mukhaḥ</li>
    </ul>
    <ul id="R_3.064.011">
        <li id="R_3.064.011ab">uparudhyanti me prāṇā dṛṣṭir bhramati rāghava</li>
        <li id="R_3.064.011cd">paśyāmi vṛkṣān sauvarṇān uśīrakṛtamūrdhajān</li>
    </ul>
    <ul id="R_3.064.012">
        <li id="R_3.064.012ab">yena yāti muhūrtena sītām ādāya rāvaṇaḥ</li>
        <li id="R_3.064.012cd">vipranaṣṭaṃ dhanaṃ kṣipraṃ tat svāmipratipadyate</li>
    </ul>
    <ul id="R_3.064.013">
        <li id="R_3.064.013ab">vindo nāma muhūrto 'sau sa ca kākutstha nābudhat</li>
        <li id="R_3.064.013cd">jhaṣavad baḍiśaṃ gṛhya kṣipram eva vinaśyati</li>
    </ul>
    <ul id="R_3.064.014">
        <li id="R_3.064.014ab">na ca tvayā vyathā kāryā janakasya sutāṃ prati</li>
        <li id="R_3.064.014cd">vaidehyā raṃsyase kṣipraṃ hatvā taṃ rākṣasaṃ raṇe</li>
    </ul>
    <ul id="R_3.064.015">
        <li id="R_3.064.015ab">asaṃmūḍhasya gṛdhrasya rāmaṃ pratyanubhāṣataḥ</li>
        <li id="R_3.064.015cd">āsyāt susrāva rudhiraṃ mriyamāṇasya sāmiṣam</li>
    </ul>
    <ul id="R_3.064.016">
        <li id="R_3.064.016ab">putro viśravasaḥ sākṣād bhrātā vaiśravaṇasya ca</li>
        <li id="R_3.064.016cd">ity uktvā durlabhān prāṇān mumoca patageśvaraḥ</li>
    </ul>
    <ul id="R_3.064.017">
        <li id="R_3.064.017ab">brūhi brūhīti rāmasya bruvāṇasya kṛtāñjaleḥ</li>
        <li id="R_3.064.017cd">tyaktvā śarīraṃ gṛdhrasya jagmuḥ prāṇā vihāyasaṃ</li>
    </ul>
    <ul id="R_3.064.018">
        <li id="R_3.064.018ab">sa nikṣipya śiro bhūmau prasārya caraṇau tadā</li>
        <li id="R_3.064.018cd">vikṣipya ca śarīraṃ svaṃ papāta dharaṇītale</li>
    </ul>
    <ul id="R_3.064.019">
        <li id="R_3.064.019ab">taṃ gṛdhraṃ prekṣya tāmrākṣaṃ gatāsum acalopamam</li>
        <li id="R_3.064.019cd">rāmaḥ subahubhir duḥkhair dīnaḥ saumitrim abravīt</li>
    </ul>
    <ul id="R_3.064.020">
        <li id="R_3.064.020ab">bahūni rakṣasāṃ vāse varṣāṇi vasatā sukham</li>
        <li id="R_3.064.020cd">anena daṇḍakāraṇye vicīrṇam iha pakṣiṇā</li>
    </ul>
    <ul id="R_3.064.021">
        <li id="R_3.064.021ab">anekavārṣiko yas tu cirakālaṃ samutthitaḥ</li>
        <li id="R_3.064.021cd">so 'yam adya hataḥ śete kālo hi duratikramaḥ</li>
    </ul>
    <ul id="R_3.064.022">
        <li id="R_3.064.022ab">paśya lakṣmaṇa gṛdhro 'yam upakārī hataś ca me</li>
        <li id="R_3.064.022cd">sītām abhyavapan no vai rāvaṇena balīyasā</li>
    </ul>
    <ul id="R_3.064.023">
        <li id="R_3.064.023ab">gṛdhrarājyaṃ parityajya pitṛpaitāmahaṃ mahat</li>
        <li id="R_3.064.023cd">mama hetor ayaṃ prāṇān mumoca patageśvaraḥ</li>
    </ul>
    <ul id="R_3.064.024">
        <li id="R_3.064.024ab">sarvatra khalu dṛśyante sādhavo dharmacāriṇaḥ</li>
        <li id="R_3.064.024cd">śūrāḥ śaraṇyāḥ saumitre tiryagyonigateṣv api</li>
    </ul>
    <ul id="R_3.064.025">
        <li id="R_3.064.025ab">sītāharaṇajaṃ duḥkhaṃ na me saumya tathāgatam</li>
        <li id="R_3.064.025cd">yathā vināśo gṛdhrasya matkṛte ca paraṃtapa</li>
    </ul>
    <ul id="R_3.064.026">
        <li id="R_3.064.026ab">rājā daśarathaḥ śrīmān yathā mama mayā yaśāḥ</li>
        <li id="R_3.064.026cd">pūjanīyaś ca mānyaś ca tathāyaṃ patageśvaraḥ</li>
    </ul>
    <ul id="R_3.064.027">
        <li id="R_3.064.027ab">saumitre hara kāṣṭhāni nirmathiṣyāmi pāvakam</li>
        <li id="R_3.064.027cd">gṛdhrarājaṃ didhakṣāmi matkṛte nidhanaṃ gatam</li>
    </ul>
    <ul id="R_3.064.028">
        <li id="R_3.064.028ab">nāthaṃ patagalokasya citām āropayāmy aham</li>
        <li id="R_3.064.028cd">imaṃ dhakṣyāmi saumitre hataṃ raudreṇa rakṣasā</li>
    </ul>
    <ul id="R_3.064.029">
        <li id="R_3.064.029ab">yā gatir yajñaśīlānām āhitāgneś ca yā gatiḥ</li>
        <li id="R_3.064.029cd">aparāvartināṃ yā ca yā ca bhūmipradāyinām</li>
    </ul>
    <ul id="R_3.064.030">
        <li id="R_3.064.030ab">mayā tvaṃ samanujñāto gaccha lokān anuttamān</li>
        <li id="R_3.064.030cd">gṛdhrarāja mahāsattva saṃskṛtaś ca mayā vraja</li>
    </ul>
    <ul id="R_3.064.031">
        <li id="R_3.064.031ab">evam uktvā citāṃ dīptām āropya patageśvaram</li>
        <li id="R_3.064.031cd">dadāha rāmo dharmātmā svabandhum iva duḥkhitaḥ</li>
    </ul>
    <ul id="R_3.064.032">
        <li id="R_3.064.032ab">rāmo 'tha sahasaumitrir vanaṃ yātvā sa vīryavān</li>
        <li id="R_3.064.032cd">sthūlān hatvā mahārohīn anu tastāra taṃ dvijam</li>
    </ul>
    <ul id="R_3.064.033">
        <li id="R_3.064.033ab">rohimāṃsāni coddhṛtya peśīkṛtvā mahāyaśāḥ</li>
        <li id="R_3.064.033cd">śakunāya dadau rāmo ramye haritaśādvale</li>
    </ul>
    <ul id="R_3.064.034">
        <li id="R_3.064.034ab">yat tat pretasya martyasya kathayanti dvijātayaḥ</li>
        <li id="R_3.064.034cd">tat svargagamanaṃ tasya kṣipraṃ rāmo jajāpa ha</li>
    </ul>
    <ul id="R_3.064.035">
        <li id="R_3.064.035ab">tato godāvarīṃ gatvā nadīṃ naravarātmajau</li>
        <li id="R_3.064.035cd">udakaṃ cakratus tasmai gṛdhrarājāya tāv ubhau</li>
    </ul>
    <ul id="R_3.064.036">
        <li id="R_3.064.036ab">
            <seg type="pāda" n="a">sa gṛdhrarājaḥ kṛtavān yaśaskaraṃ</seg>
            <seg type="pāda" n="b">suduṣkaraṃ karma raṇe nipātitaḥ</seg>
        </li>
        <li id="R_3.064.036cd">
            <seg type="pāda" n="c">maharṣikalpena ca saṃskṛtas tadā</seg>
            <seg type="pāda" n="d">jagāma puṇyāṃ gatim ātmanaḥ śubhām</seg>
        </li>
    </ul>
    <ul id="R_3.065.001">
        <li id="R_3.065.001ab">kṛtvaivam udakaṃ tasmai prasthitau rāghavau tadā</li>
        <li id="R_3.065.001cd">avekṣantau vane sītāṃ paścimāṃ jagmatur diśam</li>
    </ul>
    <ul id="R_3.065.002">
        <li id="R_3.065.002ab">tāṃ diśaṃ dakṣiṇāṃ gatvā śaracāpāsidhāriṇau</li>
        <li id="R_3.065.002cd">aviprahatam aikṣvākau panthānaṃ pratipedatuḥ</li>
    </ul>
    <ul id="R_3.065.003">
        <li id="R_3.065.003ab">gulmair vṛkṣaiś ca bahubhir latābhiś ca praveṣṭitam</li>
        <li id="R_3.065.003cd">āvṛtaṃ sarvato durgaṃ gahanaṃ ghoradarśanam</li>
    </ul>
    <ul id="R_3.065.004">
        <li id="R_3.065.004ab">vyatikramya tu vegena gṛhītvā dakṣiṇāṃ diśam</li>
        <li id="R_3.065.004cd">subhīmaṃ tan mahāraṇyaṃ vyatiyātau mahābalau</li>
    </ul>
    <ul id="R_3.065.005">
        <li id="R_3.065.005ab">tataḥ paraṃ janasthānāt trikrośaṃ gamya rāghavau</li>
        <li id="R_3.065.005cd">krauñcāraṇyaṃ viviśatur gahanaṃ tau mahaujasau</li>
    </ul>
    <ul id="R_3.065.006">
        <li id="R_3.065.006ab">nānāmeghaghanaprakhyaṃ prahṛṣṭam iva sarvataḥ</li>
        <li id="R_3.065.006cd">nānāvarṇaiḥ śubhaiḥ puṣpair mṛgapakṣigaṇair yutam</li>
    </ul>
    <ul id="R_3.065.007">
        <li id="R_3.065.007ab">didṛkṣamāṇau vaidehīṃ tad vanaṃ tau vicikyatuḥ</li>
        <li id="R_3.065.007cd">tatra tatrāvatiṣṭhantau sītāharaṇakarśitau</li>
    </ul>
    <ul id="R_3.065.008">
        <li id="R_3.065.008ab">lakṣmaṇas tu mahātejāḥ sattvavāñ śīlavāñ śuciḥ</li>
        <li id="R_3.065.008cd">abravīt prāñjalir vākyaṃ bhrātaraṃ dīptatejasaṃ</li>
    </ul>
    <ul id="R_3.065.009">
        <li id="R_3.065.009ab">spandate me dṛḍhaṃ bāhur udvignam iva me manaḥ</li>
        <li id="R_3.065.009cd">prāyaśaś cāpy aniṣṭāni nimittāny upalakṣaye</li>
    </ul>
    <ul id="R_3.065.010">
        <li id="R_3.065.010ab">tasmāt sajjībhavārya tvaṃ kuruṣva vacanaṃ hitam</li>
        <li id="R_3.065.010cd">mamaiva hi nimittāni sadyaḥ śaṃsanti saṃbhramam</li>
    </ul>
    <ul id="R_3.065.011">
        <li id="R_3.065.011ab">eṣa vañculako nāma pakṣī paramadāruṇaḥ</li>
        <li id="R_3.065.011cd">āvayor vijayaṃ yuddhe śaṃsann iva vinardati</li>
    </ul>
    <ul id="R_3.065.012">
        <li id="R_3.065.012ab">tayor anveṣator evaṃ sarvaṃ tad vanam ojasā</li>
        <li id="R_3.065.012cd">saṃjajñe vipulaḥ śabdaḥ prabhañjann iva tad vanam</li>
    </ul>
    <ul id="R_3.065.013">
        <li id="R_3.065.013ab">saṃveṣṭitam ivātyarthaṃ gahanaṃ mātariśvanā</li>
        <li id="R_3.065.013cd">vanasya tasya śabdo 'bhūd divam āpūrayann iva</li>
    </ul>
    <ul id="R_3.065.014">
        <li id="R_3.065.014ab">taṃ śabdaṃ kāṅkṣamāṇas tu rāmaḥ kakṣe sahānujaḥ</li>
        <li id="R_3.065.014cd">dadarśa sumahākāyaṃ rākṣasaṃ vipulorasaṃ</li>
    </ul>
    <ul id="R_3.065.015">
        <li id="R_3.065.015ab">āsedatus tatas tatra tāv ubhau pramukhe sthitam</li>
        <li id="R_3.065.015cd">vivṛddham aśirogrīvaṃ kabandham udare mukham</li>
    </ul>
    <ul id="R_3.065.016">
        <li id="R_3.065.016ab">romabhir nicitais tīkṣṇair mahāgirim ivocchritam</li>
        <li id="R_3.065.016cd">nīlameghanibhaṃ raudraṃ meghastanitaniḥsvanam</li>
    </ul>
    <ul id="R_3.065.017">
        <li id="R_3.065.017ab">mahāpakṣmeṇa piṅgena vipulenāyatena ca</li>
        <li id="R_3.065.017cd">ekenorasi ghoreṇa nayanenāśudarśinā</li>
    </ul>
    <ul id="R_3.065.018">
        <li id="R_3.065.018ab">mahādaṃṣṭropapannaṃ taṃ lelihānaṃ mahāmukham</li>
        <li id="R_3.065.018cd">bhakṣayantaṃ mahāghorān ṛkṣasiṃhamṛgadvipān</li>
    </ul>
    <ul id="R_3.065.019">
        <li id="R_3.065.019ab">ghorau bhujau vikurvāṇam ubhau yojanam āyatau</li>
        <li id="R_3.065.019cd">karābhyāṃ vividhān gṛhya ṛṣkān pakṣigaṇān mṛgān</li>
    </ul>
    <ul id="R_3.065.020">
        <li id="R_3.065.020ab">ākarṣantaṃ vikarṣantam anekān mṛgayūthapān</li>
        <li id="R_3.065.020cd">sthitam āvṛtya panthānaṃ tayor bhrātroḥ prapannayoḥ</li>
    </ul>
    <ul id="R_3.065.021">
        <li id="R_3.065.021ab">atha tau samatikramya krośamātre dadarśatuḥ</li>
        <li id="R_3.065.021cd">mahāntaṃ dāruṇaṃ bhīmaṃ kabandhaṃ bhujasaṃvṛtam</li>
    </ul>
    <ul id="R_3.065.022">
        <li id="R_3.065.022ab">sa mahābāhur atyarthaṃ prasārya vipulau bhujau</li>
        <li id="R_3.065.022cd">jagrāha sahitāv eva rāghavau pīḍayan balāt</li>
    </ul>
    <ul id="R_3.065.023">
        <li id="R_3.065.023ab">khaḍginau dṛḍhadhanvānau tigmatejau mahābhujau</li>
        <li id="R_3.065.023cd">bhrātarau vivaśaṃ prāptau kṛṣyamāṇau mahābalau</li>
    </ul>
    <ul id="R_3.065.024">
        <li id="R_3.065.024ab">tāv uvāca mahābāhuḥ kabandho dānavottamaḥ</li>
        <li id="R_3.065.024cd">kau yuvāṃ vṛṣabhaskandhau mahākhaḍgadhanurdharau</li>
    </ul>
    <ul id="R_3.065.025">
        <li id="R_3.065.025ab">ghoraṃ deśam imaṃ prāptau mama bhakṣāv upasthitau</li>
        <li id="R_3.065.025cd">vadataṃ kāryam iha vāṃ kimarthaṃ cāgatau yuvām</li>
    </ul>
    <ul id="R_3.065.026">
        <li id="R_3.065.026ab">imaṃ deśam anuprāptau kṣudhārtasyeha tiṣṭhataḥ</li>
        <li id="R_3.065.026cd">sabāṇacāpakhaḍgau ca tīkṣṇaśṛṅgāv ivarṣabhau</li>
        <li id="R_3.065.026ef">mamāsyam anusaṃprāptau durlabhaṃ jīvitaṃ punaḥ</li>
    </ul>
    <ul id="R_3.065.027">
        <li id="R_3.065.027ab">tasya tad vacanaṃ śrutvā kabandhasya durātmanaḥ</li>
        <li id="R_3.065.027cd">uvāca lakṣmaṇaṃ rāmo mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_3.065.028">
        <li id="R_3.065.028ab">kṛcchrāt kṛcchrataraṃ prāpya dāruṇaṃ satyavikrama</li>
        <li id="R_3.065.028cd">vyasanaṃ jīvitāntāya prāptam aprāpya tāṃ priyām</li>
    </ul>
    <ul id="R_3.065.029">
        <li id="R_3.065.029ab">kālasya sumahad vīryaṃ sarvabhūteṣu lakṣmaṇa</li>
        <li id="R_3.065.029cd">tvāṃ ca māṃ ca naravyāghra vyasanaiḥ paśya mohitau</li>
        <li id="R_3.065.029ef">nātibhāro 'sti daivasya sarvabhuteṣu lakṣmaṇa</li>
    </ul>
    <ul id="R_3.065.030">
        <li id="R_3.065.030ab">śūrāś ca balavantaś ca kṛtāstrāś ca raṇājire</li>
        <li id="R_3.065.030cd">kālābhipannāḥ sīdanti yathā vālukasetavaḥ</li>
    </ul>
    <ul id="R_3.065.031">
        <li id="R_3.065.031ab">
            <seg type="pāda" n="a">iti bruvāṇo dṛḍhasatyavikramo</seg>
            <seg type="pāda" n="b">mahāyaśā dāśarathiḥ pratāpavān</seg>
        </li>
        <li id="R_3.065.031cd">
            <seg type="pāda" n="c">avekṣya saumitrim udagravikramaṃ</seg>
            <seg type="pāda" n="d">sthirāṃ tadā svāṃ matim ātmanākarot</seg>
        </li>
    </ul>
    <ul id="R_3.066.001">
        <li id="R_3.066.001ab">tau tu tatra sthitau dṛṣṭvā bhrātarau rāmalakṣmaṇau</li>
        <li id="R_3.066.001cd">bāhupāśaparikṣiptau kabandho vākyam abravīt</li>
    </ul>
    <ul id="R_3.066.002">
        <li id="R_3.066.002ab">tiṣṭhataḥ kiṃ nu māṃ dṛṣṭvā kṣudhārtaṃ kṣatriyarṣabhau</li>
        <li id="R_3.066.002cd">āhārārthaṃ tu saṃdiṣṭau daivena gatacetasau</li>
    </ul>
    <ul id="R_3.066.003">
        <li id="R_3.066.003ab">tac chrutvā lakṣmaṇo vākyaṃ prāptakālaṃ hitaṃ tadā</li>
        <li id="R_3.066.003cd">uvācārtisamāpanno vikrame kṛtaniścayaḥ</li>
    </ul>
    <ul id="R_3.066.004">
        <li id="R_3.066.004ab">tvāṃ ca māṃ ca purā tūrṇam ādatte rākṣasādhamaḥ</li>
        <li id="R_3.066.004cd">tasmād asibhyām asyāśu bāhū chindāvahe gurū</li>
    </ul>
    <ul id="R_3.066.005">
        <li id="R_3.066.005ab">tatas tau deśakālajñau khaḍgābhyām eva rāghavau</li>
        <li id="R_3.066.005cd">acchindatāṃ susaṃhṛṣṭau bāhū tasyāṃsadeśayoḥ</li>
    </ul>
    <ul id="R_3.066.006">
        <li id="R_3.066.006ab">dakṣiṇo dakṣiṇaṃ bāhum asaktam asinā tataḥ</li>
        <li id="R_3.066.006cd">ciccheda rāmo vegena savyaṃ vīras tu lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_3.066.007">
        <li id="R_3.066.007ab">sa papāta mahābāhuś chinnabāhur mahāsvanaḥ</li>
        <li id="R_3.066.007cd">khaṃ ca gāṃ ca diśaś caiva nādayañ jalado yathā</li>
    </ul>
    <ul id="R_3.066.008">
        <li id="R_3.066.008ab">sa nikṛttau bhujau dṛṣṭvā śoṇitaughapariplutaḥ</li>
        <li id="R_3.066.008cd">dīnaḥ papraccha tau vīrau kau yuvām iti dānavaḥ</li>
    </ul>
    <ul id="R_3.066.009">
        <li id="R_3.066.009ab">iti tasya bruvāṇasya lakṣmaṇaḥ śubhalakṣaṇaḥ</li>
        <li id="R_3.066.009cd">śaśaṃsa tasya kākutsthaṃ kabandhasya mahābalaḥ</li>
    </ul>
    <ul id="R_3.066.010">
        <li id="R_3.066.010ab">ayam ikṣvākudāyādo rāmo nāma janaiḥ śrutaḥ</li>
        <li id="R_3.066.010cd">asyaivāvarajaṃ viddhi bhrātaraṃ māṃ ca lakṣmaṇam</li>
    </ul>
    <ul id="R_3.066.011">
        <li id="R_3.066.011ab">asya devaprabhāvasya vasato vijane vane</li>
        <li id="R_3.066.011cd">rakṣasāpahṛtā bhāryā yām icchantāv ihāgatau</li>
    </ul>
    <ul id="R_3.066.012">
        <li id="R_3.066.012ab">tvaṃ tu ko vā kimarthaṃ vā kabandha sadṛśo vane</li>
        <li id="R_3.066.012cd">āsyenorasi dīptena bhagnajaṅgho viceṣṭase</li>
    </ul>
    <ul id="R_3.066.013">
        <li id="R_3.066.013ab">evam uktaḥ kabandhas tu lakṣmaṇenottaraṃ vacaḥ</li>
        <li id="R_3.066.013cd">uvāca paramaprītas tad indravacanaṃ smaran</li>
    </ul>
    <ul id="R_3.066.014">
        <li id="R_3.066.014ab">svāgataṃ vāṃ naravyāghrau diṣṭyā paśyāmi cāpy aham</li>
        <li id="R_3.066.014cd">diṣṭyā cemau nikṛttau me yuvābhyāṃ bāhubandhanau</li>
    </ul>
    <ul id="R_3.066.015">
        <li id="R_3.066.015ab">virūpaṃ yac ca me rūpaṃ prāptaṃ hy avinayād yathā</li>
        <li id="R_3.066.015cd">tan me śṛṇu naravyāghra tattvataḥ śaṃsatas tava</li>
    </ul>
    <ul id="R_3.067.001">
        <li id="R_3.067.001ab">purā rāma mahābāho mahābalaparākrama</li>
        <li id="R_3.067.001cd">rūpam āsīn mamācintyaṃ triṣu lokeṣu viśrutam</li>
        <li id="R_3.067.001ef">yathā somasya śakrasya sūryasya ca yathā vapuḥ</li>
    </ul>
    <ul id="R_3.067.002">
        <li id="R_3.067.002ab">so 'haṃ rūpam idaṃ kṛtvā lokavitrāsanaṃ mahat</li>
        <li id="R_3.067.002cd">ṛṣīn vanagatān rāma trāsayāmi tatas tataḥ</li>
    </ul>
    <ul id="R_3.067.003">
        <li id="R_3.067.003ab">tataḥ sthūlaśirā nāma maharṣiḥ kopito mayā</li>
        <li id="R_3.067.003cd">saṃcinvan vividhaṃ vanyaṃ rūpeṇānena dharṣitaḥ</li>
    </ul>
    <ul id="R_3.067.004">
        <li id="R_3.067.004ab">tenāham uktaḥ prekṣyaivaṃ ghoraśāpābhidhāyinā</li>
        <li id="R_3.067.004cd">etad eva nṛśaṃsaṃ te rūpam astu vigarhitam</li>
    </ul>
    <ul id="R_3.067.005">
        <li id="R_3.067.005ab">sa mayā yācitaḥ kruddhaḥ śāpasyānto bhaved iti</li>
        <li id="R_3.067.005cd">abhiśāpakṛtasyeti tenedaṃ bhāṣitaṃ vacaḥ</li>
    </ul>
    <ul id="R_3.067.006">
        <li id="R_3.067.006ab">yadā chittvā bhujau rāmas tvāṃ dahed vijane vane</li>
        <li id="R_3.067.006cd">tadā tvaṃ prāpsyase rūpaṃ svam eva vipulaṃ śubham</li>
    </ul>
    <ul id="R_3.067.007">
        <li id="R_3.067.007ab">śriyā virājitaṃ putraṃ danos tvaṃ viddhi lakṣmaṇa</li>
        <li id="R_3.067.007cd">indrakopād idaṃ rūpaṃ prāptam evaṃ raṇājire</li>
    </ul>
    <ul id="R_3.067.008">
        <li id="R_3.067.008ab">ahaṃ hi tapasogreṇa pitāmaham atoṣayam</li>
        <li id="R_3.067.008cd">dīrgham āyuḥ sa me prādāt tato māṃ vibhramo 'spṛśat</li>
    </ul>
    <ul id="R_3.067.009">
        <li id="R_3.067.009ab">dīrgham āyur mayā prāptaṃ kiṃ me śakraḥ kariṣyati</li>
        <li id="R_3.067.009cd">ity evaṃ buddhim āsthāya raṇe śakram adharṣayam</li>
    </ul>
    <ul id="R_3.067.010">
        <li id="R_3.067.010ab">tasya bāhupramuktena vajreṇa śataparvaṇā</li>
        <li id="R_3.067.010cd">sakthinī ca śiraś caiva śarīre saṃpraveśitam</li>
    </ul>
    <ul id="R_3.067.011">
        <li id="R_3.067.011ab">sa mayā yācyamānaḥ sann ānayad yamasādanam</li>
        <li id="R_3.067.011cd">pitāmahavacaḥ satyaṃ tad astv iti mamābravīt</li>
    </ul>
    <ul id="R_3.067.012">
        <li id="R_3.067.012ab">anāhāraḥ kathaṃ śakto bhagnasakthiśiromukhaḥ</li>
        <li id="R_3.067.012cd">vajreṇābhihataḥ kālaṃ sudīrgham api jīvitum</li>
    </ul>
    <ul id="R_3.067.013">
        <li id="R_3.067.013ab">evam uktas tu me śakro bāhū yojanam āyatau</li>
        <li id="R_3.067.013cd">prādād āsyaṃ ca me kukṣau tīkṣṇadaṃṣṭram akalpayat</li>
    </ul>
    <ul id="R_3.067.014">
        <li id="R_3.067.014ab">so 'haṃ bhujābhyāṃ dīrghābhyāṃ samākṛṣya vanecarān</li>
        <li id="R_3.067.014cd">siṃhadvipamṛgavyāghrān bhakṣayāmi samantataḥ</li>
    </ul>
    <ul id="R_3.067.015">
        <li id="R_3.067.015ab">sa tu mām abravīd indro yadā rāmaḥ salakṣmaṇaḥ</li>
        <li id="R_3.067.015cd">chetsyate samare bāhū tadā svargaṃ gamiṣyasi</li>
    </ul>
    <ul id="R_3.067.016">
        <li id="R_3.067.016ab">sa tvaṃ rāmo 'si bhadraṃ te nāham anyena rāghava</li>
        <li id="R_3.067.016cd">śakyo hantuṃ yathātattvam evam uktaṃ maharṣiṇā</li>
    </ul>
    <ul id="R_3.067.017">
        <li id="R_3.067.017ab">ahaṃ hi matisācivyaṃ kariṣyāmi nararṣabha</li>
        <li id="R_3.067.017cd">mitraṃ caivopadekṣyāmi yuvābhyāṃ saṃskṛto 'gninā</li>
    </ul>
    <ul id="R_3.067.018">
        <li id="R_3.067.018ab">evam uktas tu dharmātmā danunā tena rāghavaḥ</li>
        <li id="R_3.067.018cd">idaṃ jagāda vacanaṃ lakṣmaṇasyopaśṛṇvataḥ</li>
    </ul>
    <ul id="R_3.067.019">
        <li id="R_3.067.019ab">rāvaṇena hṛtā sītā mama bhāryā yaśasvinī</li>
        <li id="R_3.067.019cd">niṣkrāntasya janasthānāt saha bhrātrā yathāsukham</li>
    </ul>
    <ul id="R_3.067.020">
        <li id="R_3.067.020ab">nāmamātraṃ tu jānāmi na rūpaṃ tasya rakṣasaḥ</li>
        <li id="R_3.067.020cd">nivāsaṃ vā prabhāvaṃ vā vayaṃ tasya na vidmahe</li>
    </ul>
    <ul id="R_3.067.021">
        <li id="R_3.067.021ab">śokārtānām anāthānām evaṃ viparidhāvatām</li>
        <li id="R_3.067.021cd">kāruṇyaṃ sadṛśaṃ kartum upakāre ca vartatām</li>
    </ul>
    <ul id="R_3.067.022">
        <li id="R_3.067.022ab">kāṣṭhāny ānīya śuṣkāṇi kāle bhagnāni kuñjaraiḥ</li>
        <li id="R_3.067.022cd">bhakṣyāmas tvāṃ vayaṃ vīra śvabhre mahati kalpite</li>
    </ul>
    <ul id="R_3.067.023">
        <li id="R_3.067.023ab">sa tvaṃ sītāṃ samācakṣva yena vā yatra vā hṛtā</li>
        <li id="R_3.067.023cd">kuru kalyāṇam atyarthaṃ yadi jānāsi tattvataḥ</li>
    </ul>
    <ul id="R_3.067.024">
        <li id="R_3.067.024ab">evam uktas tu rāmeṇa vākyaṃ danur anuttamam</li>
        <li id="R_3.067.024cd">provāca kuśalo vaktuṃ vaktāram api rāghavam</li>
    </ul>
    <ul id="R_3.067.025">
        <li id="R_3.067.025ab">divyam asti na me jñānaṃ nābhijānāmi maithilīm</li>
        <li id="R_3.067.025cd">yas tāṃ jñāsyati taṃ vakṣye dagdhaḥ svaṃ rūpam āsthitaḥ</li>
    </ul>
    <ul id="R_3.067.026">
        <li id="R_3.067.026ab">adagdhasya hi vijñātuṃ śaktir asti na me prabho</li>
        <li id="R_3.067.026cd">rākṣasaṃ taṃ mahāvīryaṃ sītā yena hṛtā tava</li>
    </ul>
    <ul id="R_3.067.027">
        <li id="R_3.067.027ab">vijñānaṃ hi mahad bhraṣṭaṃ śāpadoṣeṇa rāghava</li>
        <li id="R_3.067.027cd">svakṛtena mayā prāptaṃ rūpaṃ lokavigarhitam</li>
    </ul>
    <ul id="R_3.067.028">
        <li id="R_3.067.028ab">kiṃ tu yāvan na yāty astaṃ savitā śrāntavāhanaḥ</li>
        <li id="R_3.067.028cd">tāvan mām avaṭe kṣiptvā daha rāma yathāvidhi</li>
    </ul>
    <ul id="R_3.067.029">
        <li id="R_3.067.029ab">dagdhas tvayāham avaṭe nyāyena raghunandana</li>
        <li id="R_3.067.029cd">vakṣyāmi tam ahaṃ vīra yas taṃ jñāsyati rākṣasaṃ</li>
    </ul>
    <ul id="R_3.067.030">
        <li id="R_3.067.030ab">tena sakhyaṃ ca kartavyaṃ nyāyyavṛttena rāghava</li>
        <li id="R_3.067.030cd">kalpayiṣyati te prītaḥ sāhāyyaṃ laghuvikramaḥ</li>
    </ul>
    <ul id="R_3.067.031">
        <li id="R_3.067.031ab">na hi tasyāsty avijñātaṃ triṣu lokeṣu rāghava</li>
        <li id="R_3.067.031cd">sarvān parisṛto lokān purā vai kāraṇāntare</li>
    </ul>
    <ul id="R_3.068.001">
        <li id="R_3.068.001ab">evam uktau tu tau vīrau kabandhena nareśvarau</li>
        <li id="R_3.068.001cd">giripradaram āsādya pāvakaṃ visasarjatuḥ</li>
    </ul>
    <ul id="R_3.068.002">
        <li id="R_3.068.002ab">lakṣmaṇas tu maholkābhir jvalitābhiḥ samantataḥ</li>
        <li id="R_3.068.002cd">citām ādīpayām āsa sā prajajvāla sarvataḥ</li>
    </ul>
    <ul id="R_3.068.003">
        <li id="R_3.068.003ab">tac charīraṃ kabandhasya ghṛtapiṇḍopamaṃ mahat</li>
        <li id="R_3.068.003cd">medasā pacyamānasya mandaṃ dahati pāvaka</li>
    </ul>
    <ul id="R_3.068.004">
        <li id="R_3.068.004ab">sa vidhūya citām āśu vidhūmo 'gnir ivotthitaḥ</li>
        <li id="R_3.068.004cd">araje vāsasī vibhran mālāṃ divyāṃ mahābalaḥ</li>
    </ul>
    <ul id="R_3.068.005">
        <li id="R_3.068.005ab">tataś citāyā vegena bhāsvaro virajāmbaraḥ</li>
        <li id="R_3.068.005cd">utpapātāśu saṃhṛṣṭaḥ sarvapratyaṅgabhūṣaṇaḥ</li>
    </ul>
    <ul id="R_3.068.006">
        <li id="R_3.068.006ab">vimāne bhāsvare tiṣṭhan haṃsayukte yaśaskare</li>
        <li id="R_3.068.006cd">prabhayā ca mahātejā diśo daśa virājayan</li>
    </ul>
    <ul id="R_3.068.007">
        <li id="R_3.068.007ab">so 'ntarikṣagato rāmaṃ kabandho vākyam abravīt</li>
        <li id="R_3.068.007cd">śṛṇu rāghava tattvena yathā sīmām avāpsyasi</li>
    </ul>
    <ul id="R_3.068.008">
        <li id="R_3.068.008ab">rāma ṣaḍ yuktayo loke yābhiḥ sarvaṃ vimṛśyate</li>
        <li id="R_3.068.008cd">parimṛṣṭo daśāntena daśābhāgena sevyate</li>
    </ul>
    <ul id="R_3.068.009">
        <li id="R_3.068.009ab">daśābhāgagato hīnas tvaṃ rāma sahalakṣmaṇaḥ</li>
        <li id="R_3.068.009cd">yat kṛte vyasanaṃ prāptaṃ tvayā dārapradharṣaṇam</li>
    </ul>
    <ul id="R_3.068.010">
        <li id="R_3.068.010ab">tad avaśyaṃ tvayā kāryaḥ sa suhṛt suhṛdāṃ vara</li>
        <li id="R_3.068.010cd">akṛtvā na hi te siddhim ahaṃ paśyāmi cintayan</li>
    </ul>
    <ul id="R_3.068.011">
        <li id="R_3.068.011ab">śrūyatāṃ rāma vakṣyāmi sugrīvo nāma vānaraḥ</li>
        <li id="R_3.068.011cd">bhrātrā nirastaḥ kruddhena vālinā śakrasūnunā</li>
    </ul>
    <ul id="R_3.068.012">
        <li id="R_3.068.012ab">ṛṣyamūke girivare pampāparyantaśobhite</li>
        <li id="R_3.068.012cd">nivasaty ātmavān vīraś caturbhiḥ saha vānaraiḥ</li>
    </ul>
    <ul id="R_3.068.013">
        <li id="R_3.068.013ab">vayasyaṃ taṃ kuru kṣipram ito gatvādya rāghava</li>
        <li id="R_3.068.013cd">adrohāya samāgamya dīpyamāne vibhāvasau</li>
    </ul>
    <ul id="R_3.068.014">
        <li id="R_3.068.014ab">na ca te so 'vamantavyaḥ sugrīvo vānarādhipaḥ</li>
        <li id="R_3.068.014cd">kṛtajñaḥ kāmarūpī ca sahāyārthī ca vīryavān</li>
    </ul>
    <ul id="R_3.068.015">
        <li id="R_3.068.015ab">śaktau hy adya yuvāṃ kartuṃ kāryaṃ tasya cikīrṣitam</li>
        <li id="R_3.068.015cd">kṛtārtho vākṛtārtho vā kṛtyaṃ tava kariṣyati</li>
    </ul>
    <ul id="R_3.068.016">
        <li id="R_3.068.016ab">sa ṛkṣarajasaḥ putraḥ pampām aṭati śaṅkitaḥ</li>
        <li id="R_3.068.016cd">bhāskarasyaurasaḥ putro vālinā kṛtakilbiṣaḥ</li>
    </ul>
    <ul id="R_3.068.017">
        <li id="R_3.068.017ab">saṃnidhāyāyudhaṃ kṣipram ṛṣyamūkālayaṃ kapim</li>
        <li id="R_3.068.017cd">kuru rāghava satyena vayasyaṃ vanacāriṇam</li>
    </ul>
    <ul id="R_3.068.018">
        <li id="R_3.068.018ab">sa hi sthānāni sarvāṇi kārtsnyena kapikuñjaraḥ</li>
        <li id="R_3.068.018cd">naramāṃsāśināṃ loke naipuṇyād adhigacchati</li>
    </ul>
    <ul id="R_3.068.019">
        <li id="R_3.068.019ab">na tasyāviditaṃ loke kiṃ cid asti hi rāghava</li>
        <li id="R_3.068.019cd">yāvat sūryaḥ pratapati sahasrāṃśur ariṃdama</li>
    </ul>
    <ul id="R_3.068.020">
        <li id="R_3.068.020ab">sa nadīr vipulāñ śailān giridurgāṇi kandarān</li>
        <li id="R_3.068.020cd">anviṣya vānaraiḥ sārdhaṃ patnīṃ te 'dhigamiṣyati</li>
    </ul>
    <ul id="R_3.068.021">
        <li id="R_3.068.021ab">vānarāṃś ca mahākāyān preṣayiṣyati rāghava</li>
        <li id="R_3.068.021cd">diśo vicetuṃ tāṃ sītāṃ tvadviyogena śocatīm</li>
    </ul>
    <ul id="R_3.068.022">
        <li id="R_3.068.022ab">
            <seg type="pāda" n="a">sa meruśṛṅgāgragatām aninditāṃ</seg>
            <seg type="pāda" n="b">praviśya pātālatale 'pi vāśritām</seg>
        </li>
        <li id="R_3.068.022cd">
            <seg type="pāda" n="c">plavaṃgamānāṃ pravaras tava priyāṃ</seg>
            <seg type="pāda" n="d">nihatya rakṣāṃsi punaḥ pradāsyati</seg>
        </li>
    </ul>
    <ul id="R_3.069.001">
        <li id="R_3.069.001ab">nidarśayitvā rāmāya sītāyāḥ pratipādane</li>
        <li id="R_3.069.001cd">vākyam anvartham arthajñaḥ kabandhaḥ punar abravīt</li>
    </ul>
    <ul id="R_3.069.002">
        <li id="R_3.069.002ab">eṣa rāma śivaḥ panthā yatraite puṣpitā drumāḥ</li>
        <li id="R_3.069.002cd">pratīcīṃ diśam āśritya prakāśante manoramāḥ</li>
    </ul>
    <ul id="R_3.069.003">
        <li id="R_3.069.003ab">jambūpriyālapanasāḥ plakṣanyagrodhatindukāḥ</li>
        <li id="R_3.069.003cd">aśvatthāḥ karṇikārāś ca cūtāś cānye ca pādapāḥ</li>
    </ul>
    <ul id="R_3.069.004">
        <li id="R_3.069.004ab">tān āruhyātha vā bhūmau pātayitvā ca tān balāt</li>
        <li id="R_3.069.004cd">phalāny amṛtakalpāni bhakṣayantau gamiṣyathaḥ</li>
    </ul>
    <ul id="R_3.069.005">
        <li id="R_3.069.005ab">caṅkramantau varān deśāñ śailāc chailaṃ vanād vanam</li>
        <li id="R_3.069.005cd">tataḥ puṣkariṇīṃ vīrau pampāṃ nāma gamiṣyathaḥ</li>
    </ul>
    <ul id="R_3.069.006">
        <li id="R_3.069.006ab">aśarkarām avibhraṃśāṃ samatīrtham aśaivalām</li>
        <li id="R_3.069.006cd">rāma saṃjātavālūkāṃ kamalotpalaśobhitām</li>
    </ul>
    <ul id="R_3.069.007">
        <li id="R_3.069.007ab">tatra haṃsāḥ plavāḥ krauñcāḥ kurarāś caiva rāghava</li>
        <li id="R_3.069.007cd">valgusvarā nikūjanti pampāsalilagocarāḥ</li>
    </ul>
    <ul id="R_3.069.008">
        <li id="R_3.069.008ab">nodvijante narān dṛṣṭvā vadhasyākovidāḥ śubhāḥ</li>
        <li id="R_3.069.008cd">ghṛtapiṇḍopamān sthūlāṃs tān dvijān bhakṣayiṣyathaḥ</li>
    </ul>
    <ul id="R_3.069.009">
        <li id="R_3.069.009ab">rohitān vakratuṇḍāṃś ca nalamīnāṃś ca rāghava</li>
        <li id="R_3.069.009cd">pampāyām iṣubhir matsyāṃs tatra rāma varān hatān</li>
    </ul>
    <ul id="R_3.069.010">
        <li id="R_3.069.010ab">nistvakpakṣān ayastaptān akṛśān ekakaṇṭakān</li>
        <li id="R_3.069.010cd">tava bhaktyā samāyukto lakṣmaṇaḥ saṃpradāsyati</li>
    </ul>
    <ul id="R_3.069.011">
        <li id="R_3.069.011ab">bhṛśaṃ te khādato matsyān pampāyāḥ puṣpasaṃcaye</li>
        <li id="R_3.069.011cd">padmagandhi śivaṃ vāri sukhaśītam anāmayam</li>
    </ul>
    <ul id="R_3.069.012">
        <li id="R_3.069.012ab">uddhṛtya sa tadākliṣṭaṃ rūpyasphaṭikasaṃnibham</li>
        <li id="R_3.069.012cd">atha puṣkaraparṇena lakṣmaṇaḥ pāyayiṣyati</li>
    </ul>
    <ul id="R_3.069.013">
        <li id="R_3.069.013ab">sthūlān giriguhāśayyān varāhān vanacāriṇaḥ</li>
        <li id="R_3.069.013cd">apāṃ lobhād upāvṛttān vṛṣabhān iva nardataḥ</li>
        <li id="R_3.069.013ef">rūpānvitāṃś ca pampāyāṃ drakṣyasi tvaṃ narottama</li>
    </ul>
    <ul id="R_3.069.014">
        <li id="R_3.069.014ab">sāyāhne vicaran rāma viṭapī mālyadhāriṇaḥ</li>
        <li id="R_3.069.014cd">śītodakaṃ ca pampāyāṃ dṛṣṭvā śokaṃ vihāsyasi</li>
    </ul>
    <ul id="R_3.069.015">
        <li id="R_3.069.015ab">sumanobhiś citāṃs tatra tilakān naktamālakān</li>
        <li id="R_3.069.015cd">utpalāni ca phullāni paṅkajāni ca rāghava</li>
    </ul>
    <ul id="R_3.069.016">
        <li id="R_3.069.016ab">na tāni kaś cin mālyāni tatrāropayitā naraḥ</li>
        <li id="R_3.069.016cd">mataṅgaśiṣyās tatrāsann ṛṣayaḥ susamāhitaḥ</li>
    </ul>
    <ul id="R_3.069.017">
        <li id="R_3.069.017ab">teṣāṃ bhārābhitaptānāṃ vanyam āharatāṃ guroḥ</li>
        <li id="R_3.069.017cd">ye prapetur mahīṃ tūrṇaṃ śarīrāt svedabindavaḥ</li>
    </ul>
    <ul id="R_3.069.018">
        <li id="R_3.069.018ab">tāni mālyāni jātāni munīnāṃ tapasā tadā</li>
        <li id="R_3.069.018cd">svedabindusamutthāni na vinaśyanti rāghava</li>
    </ul>
    <ul id="R_3.069.019">
        <li id="R_3.069.019ab">teṣām adyāpi tatraiva dṛśyate paricāriṇī</li>
        <li id="R_3.069.019cd">śramaṇī śabarī nāma kākutstha cirajīvinī</li>
    </ul>
    <ul id="R_3.069.020">
        <li id="R_3.069.020ab">tvāṃ tu dharme sthitā nityaṃ sarvabhūtanamaskṛtam</li>
        <li id="R_3.069.020cd">dṛṣṭvā devopamaṃ rāma svargalokaṃ gamiṣyati</li>
    </ul>
    <ul id="R_3.069.021">
        <li id="R_3.069.021ab">tatas tad rāma pampāyās tīram āśritya paścimam</li>
        <li id="R_3.069.021cd">āśramasthānam atulaṃ guhyaṃ kākutstha paśyasi</li>
    </ul>
    <ul id="R_3.069.022">
        <li id="R_3.069.022ab">na tatrākramituṃ nāgāḥ śaknuvanti tam āśramam</li>
        <li id="R_3.069.022cd">ṛṣes tasya mataṅgasya vidhānāt tac ca kānanam</li>
    </ul>
    <ul id="R_3.069.023">
        <li id="R_3.069.023ab">tasmin nandanasaṃkāśe devāraṇyopame vane</li>
        <li id="R_3.069.023cd">nānāvihagasaṃkīrṇe raṃsyase rāma nirvṛtaḥ</li>
    </ul>
    <ul id="R_3.069.024">
        <li id="R_3.069.024ab">ṛṣyamūkas tu pampāyāḥ purastāt puṣpitadrumaḥ</li>
        <li id="R_3.069.024cd">suduḥkhārohaṇo nāma śiśunāgābhirakṣitaḥ</li>
        <li id="R_3.069.024ef">udāro brahmaṇā caiva pūrvakāle vinirmitaḥ</li>
    </ul>
    <ul id="R_3.069.025">
        <li id="R_3.069.025ab">śayānaḥ puruṣo rāma tasya śailasya mūrdhani</li>
        <li id="R_3.069.025cd">yat svapne labhate vittaṃ tat prabuddho 'dhigacchati</li>
    </ul>
    <ul id="R_3.069.026">
        <li id="R_3.069.026ab">na tv enaṃ viṣamācāraḥ pāpakarmādhirohati</li>
        <li id="R_3.069.026cd">tatraiva praharanty enaṃ suptam ādāya rākṣasāḥ</li>
    </ul>
    <ul id="R_3.069.027">
        <li id="R_3.069.027ab">tato 'pi śiśunāgānām ākrandaḥ śrūyate mahān</li>
        <li id="R_3.069.027cd">krīḍatāṃ rāma pampāyāṃ mataṅgāraṇyavāsinām</li>
    </ul>
    <ul id="R_3.069.028">
        <li id="R_3.069.028ab">siktā rudhiradhārābhiḥ saṃhatya paramadvipāḥ</li>
        <li id="R_3.069.028cd">pracaranti pṛthak kīrṇā meghavarṇās tarasvinaḥ</li>
    </ul>
    <ul id="R_3.069.029">
        <li id="R_3.069.029ab">te tatra pītvā pānīyaṃ vimalaṃ śītam avyayam</li>
        <li id="R_3.069.029cd">nivṛttāḥ saṃvigāhante vanāni vanagocarāḥ</li>
    </ul>
    <ul id="R_3.069.030">
        <li id="R_3.069.030ab">rāma tasya tu śailasya mahatī śobhate guhā</li>
        <li id="R_3.069.030cd">śilāpidhānā kākutstha duḥkhaṃ cāsyāḥ praveśanam</li>
    </ul>
    <ul id="R_3.069.031">
        <li id="R_3.069.031ab">tasyā guhāyāḥ prāgdvāre mahāñ śītodako hradaḥ</li>
        <li id="R_3.069.031cd">bahumūlaphalo ramyo nānānagasamāvṛtaḥ</li>
    </ul>
    <ul id="R_3.069.032">
        <li id="R_3.069.032ab">tasyāṃ vasati sugrīvaś caturbhiḥ saha vānaraiḥ</li>
        <li id="R_3.069.032cd">kadā cic chikhare tasya parvatasyāvatiṣṭhate</li>
    </ul>
    <ul id="R_3.069.033">
        <li id="R_3.069.033ab">kabandhas tv anuśāsyaivaṃ tāv ubhau rāmalakṣmaṇau</li>
        <li id="R_3.069.033cd">sragvī bhāskaravarṇābhaḥ khe vyarocata vīryavān</li>
    </ul>
    <ul id="R_3.069.034">
        <li id="R_3.069.034ab">taṃ tu khasthaṃ mahābhāgaṃ kabandhaṃ rāmalakṣmaṇau</li>
        <li id="R_3.069.034cd">prasthitau tvaṃ vrajasveti vākyam ūcatur antikāt</li>
    </ul>
    <ul id="R_3.069.035">
        <li id="R_3.069.035ab">gamyatāṃ kāryasiddhyartham iti tāv abravīc ca saḥ</li>
        <li id="R_3.069.035cd">suprītau tāv anujñāpya kabandhaḥ prasthitas tadā</li>
    </ul>
    <ul id="R_3.069.036">
        <li id="R_3.069.036ab">
            <seg type="pāda" n="a">sa tat kabandhaḥ pratipadya rūpaṃ</seg>
            <seg type="pāda" n="b">vṛtaḥ śriyā bhāskaratulyadehaḥ</seg>
        </li>
        <li id="R_3.069.036cd">
            <seg type="pāda" n="c">nidarśayan rāmam avekṣya khasthaḥ</seg>
            <seg type="pāda" n="d">sakhyaṃ kuruṣveti tadābhyuvāca</seg>
        </li>
    </ul>
    <ul id="R_3.070.001">
        <li id="R_3.070.001ab">tau kabandhena taṃ mārgaṃ pampāyā darśitaṃ vane</li>
        <li id="R_3.070.001cd">ātasthatur diśaṃ gṛhya pratīcīṃ nṛvarātmajau</li>
    </ul>
    <ul id="R_3.070.002">
        <li id="R_3.070.002ab">tau śaileṣv ācitānekān kṣaudrakalpaphaladrumān</li>
        <li id="R_3.070.002cd">vīkṣantau jagmatur draṣṭuṃ sugrīvaṃ rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_3.070.003">
        <li id="R_3.070.003ab">kṛtvā ca śailapṛṣṭhe tu tau vāsaṃ raghunandanau</li>
        <li id="R_3.070.003cd">pampāyāḥ paścimaṃ tīraṃ rāghavāv upatasthatuḥ</li>
    </ul>
    <ul id="R_3.070.004">
        <li id="R_3.070.004ab">tau puṣkariṇyāḥ pampāyās tīram āsādya paścimam</li>
        <li id="R_3.070.004cd">apaśyatāṃ tatas tatra śabaryā ramyam āśramam</li>
    </ul>
    <ul id="R_3.070.005">
        <li id="R_3.070.005ab">tau tam āśramam āsādya drumair bahubhir āvṛtam</li>
        <li id="R_3.070.005cd">suramyam abhivīkṣantau śabarīm abhyupeyatuḥ</li>
    </ul>
    <ul id="R_3.070.006">
        <li id="R_3.070.006ab">tau tu dṛṣṭvā tadā siddhā samutthāya kṛtāñjaliḥ</li>
        <li id="R_3.070.006cd">pādau jagrāha rāmasya lakṣmaṇasya ca dhīmataḥ</li>
    </ul>
    <ul id="R_3.070.007">
        <li id="R_3.070.007ab">tām uvāca tato rāmaḥ śramaṇīṃ saṃśitavratām</li>
        <li id="R_3.070.007cd">kaccit te nirjitā vighnāḥ kaccit te vardhate tapaḥ</li>
    </ul>
    <ul id="R_3.070.008">
        <li id="R_3.070.008ab">kaccit te niyataḥ kopa āhāraś ca tapodhane</li>
        <li id="R_3.070.008cd">kaccit te niyamāḥ prāptāḥ kaccit te manasaḥ sukham</li>
        <li id="R_3.070.008ef">kaccit te guruśuśrūṣā saphalā cārubhāṣiṇi</li>
    </ul>
    <ul id="R_3.070.009">
        <li id="R_3.070.009ab">rāmeṇa tāpasī pṛṣṭhā sā siddhā siddhasaṃmatā</li>
        <li id="R_3.070.009cd">śaśaṃsa śabarī vṛddhā rāmāya pratyupasthitā</li>
    </ul>
    <ul id="R_3.070.010">
        <li id="R_3.070.010ab">citrakūṭaṃ tvayi prāpte vimānair atulaprabhaiḥ</li>
        <li id="R_3.070.010cd">itas te divam ārūḍhā yān ahaṃ paryacāriṣam</li>
    </ul>
    <ul id="R_3.070.011">
        <li id="R_3.070.011ab">taiś cāham uktā dharmajñair mahābhāgair maharṣibhiḥ</li>
        <li id="R_3.070.011cd">āgamiṣyati te rāmaḥ supuṇyam imam āśramam</li>
    </ul>
    <ul id="R_3.070.012">
        <li id="R_3.070.012ab">sa te pratigrahītavyaḥ saumitrisahito 'tithiḥ</li>
        <li id="R_3.070.012cd">taṃ ca dṛṣṭvā varāṃl lokān akṣayāṃs tvaṃ gamiṣyasi</li>
    </ul>
    <ul id="R_3.070.013">
        <li id="R_3.070.013ab">mayā tu vividhaṃ vanyaṃ saṃcitaṃ puruṣarṣabha</li>
        <li id="R_3.070.013cd">tavārthe puruṣavyāghra pampāyās tīrasaṃbhavam</li>
    </ul>
    <ul id="R_3.070.014">
        <li id="R_3.070.014ab">evam uktaḥ sa dharmātmā śabaryā śabarīm idam</li>
        <li id="R_3.070.014cd">rāghavaḥ prāha vijñāne tāṃ nityam abahiṣkṛtām</li>
    </ul>
    <ul id="R_3.070.015">
        <li id="R_3.070.015ab">danoḥ sakāśāt tattvena prabhāvaṃ te mahātmanaḥ</li>
        <li id="R_3.070.015cd">śrutaṃ pratyakṣam icchāmi saṃdraṣṭuṃ yadi manyase</li>
    </ul>
    <ul id="R_3.070.016">
        <li id="R_3.070.016ab">etat tu vacanaṃ śrutvā rāmavaktrād viniḥsṛtam</li>
        <li id="R_3.070.016cd">śabarī darśayām āsa tāv ubhau tad vanaṃ mahat</li>
    </ul>
    <ul id="R_3.070.017">
        <li id="R_3.070.017ab">paśya meghaghanaprakhyaṃ mṛgapakṣisamākulam</li>
        <li id="R_3.070.017cd">mataṅgavanam ity eva viśrutaṃ raghunandana</li>
    </ul>
    <ul id="R_3.070.018">
        <li id="R_3.070.018ab">iha te bhāvitātmāno guravo me mahādyute</li>
        <li id="R_3.070.018cd">juhavāṃś cakrire tīrthaṃ mantravan mantrapūjitam</li>
    </ul>
    <ul id="R_3.070.019">
        <li id="R_3.070.019ab">iyaṃ pratyak sthalī vedī yatra te me susatkṛtāḥ</li>
        <li id="R_3.070.019cd">puṣpopahāraṃ kurvanti śramād udvepibhiḥ karaiḥ</li>
    </ul>
    <ul id="R_3.070.020">
        <li id="R_3.070.020ab">teṣāṃ tapaḥ prabhāvena paśyādyāpi raghūttama</li>
        <li id="R_3.070.020cd">dyotayanti diśaḥ sarvāḥ śriyā vedyo 'tulaprabhāḥ</li>
    </ul>
    <ul id="R_3.070.021">
        <li id="R_3.070.021ab">aśaknuvadbhis tair gantum upavāsaśramālasaiḥ</li>
        <li id="R_3.070.021cd">cintite 'bhyāgatān paśya sametān sapta sāgarān</li>
    </ul>
    <ul id="R_3.070.022">
        <li id="R_3.070.022ab">kṛtābhiṣekais tair nyastā valkalāḥ pādapeṣv iha</li>
        <li id="R_3.070.022cd">adyāpi na viśuṣyanti pradeśe raghunandana</li>
    </ul>
    <ul id="R_3.070.023">
        <li id="R_3.070.023ab">kṛtsnaṃ vanam idaṃ dṛṣṭaṃ śrotavyaṃ ca śrutaṃ tvayā</li>
        <li id="R_3.070.023cd">tad icchāmy abhyanujñātā tyaktum etat kalevaram</li>
    </ul>
    <ul id="R_3.070.024">
        <li id="R_3.070.024ab">teṣām icchāmy ahaṃ gantuṃ samīpaṃ bhāvitātmanām</li>
        <li id="R_3.070.024cd">munīnām āśraṃmo yeṣām ahaṃ ca paricāriṇī</li>
    </ul>
    <ul id="R_3.070.025">
        <li id="R_3.070.025ab">dharmiṣṭhaṃ tu vacaḥ śrutvā rāghavaḥ sahalakṣmaṇaḥ</li>
        <li id="R_3.070.025cd">anujānāmi gaccheti prahṛṣṭavadano 'bravīt</li>
    </ul>
    <ul id="R_3.070.026">
        <li id="R_3.070.026ab">anujñātā tu rāmeṇa hutvātmānaṃ hutāśane</li>
        <li id="R_3.070.026cd">jvalatpāvakasaṃkāśā svargam eva jagāma sā</li>
    </ul>
    <ul id="R_3.070.027">
        <li id="R_3.070.027ab">yatra te sukṛtātmāno viharanti maharṣayaḥ</li>
        <li id="R_3.070.027cd">tat puṇyaṃ śabarīsthānaṃ jagāmātmasamādhinā</li>
    </ul>
    <ul id="R_3.071.001">
        <li id="R_3.071.001ab">divaṃ tu tasyāṃ yātāyāṃ śabaryāṃ svena karmaṇā</li>
        <li id="R_3.071.001cd">lakṣmaṇena saha bhrātrā cintayām āsa rāghavaḥ</li>
    </ul>
    <ul id="R_3.071.002">
        <li id="R_3.071.002ab">cintayitvā tu dharmātmā prabhāvaṃ taṃ mahātmanām</li>
        <li id="R_3.071.002cd">hitakāriṇam ekāgraṃ lakṣmaṇaṃ rāghavo 'bravīt</li>
    </ul>
    <ul id="R_3.071.003">
        <li id="R_3.071.003ab">dṛṣṭo 'yam āśramaḥ saumya bahvāścaryaḥ kṛtātmanām</li>
        <li id="R_3.071.003cd">viśvastamṛgaśārdūlo nānāvihagasevitaḥ</li>
    </ul>
    <ul id="R_3.071.004">
        <li id="R_3.071.004ab">saptānāṃ ca samudrāṇām eṣu tīrtheṣu lakṣmaṇa</li>
        <li id="R_3.071.004cd">upaspṛṣṭaṃ ca vidhivat pitaraś cāpi tarpitāḥ</li>
    </ul>
    <ul id="R_3.071.005">
        <li id="R_3.071.005ab">pranaṣṭam aśubhaṃ yat tat kalyāṇaṃ samupasthitam</li>
        <li id="R_3.071.005cd">tena tv etat prahṛṣṭaṃ me mano lakṣmaṇa saṃprati</li>
    </ul>
    <ul id="R_3.071.006">
        <li id="R_3.071.006ab">hṛdaye hi naravyāghra śubham āvirbhaviṣyati</li>
        <li id="R_3.071.006cd">tad āgaccha gamiṣyāvaḥ pampāṃ tāṃ priyadarśanām</li>
    </ul>
    <ul id="R_3.071.007">
        <li id="R_3.071.007ab">ṛśyamūko girir yatra nātidūre prakāśate</li>
        <li id="R_3.071.007cd">yasmin vasati dharmātmā sugrīvo 'ṃśumataḥ sutaḥ</li>
        <li id="R_3.071.007ef">nityaṃ vālibhayāt trastaś caturbhiḥ saha vānaraiḥ</li>
    </ul>
    <ul id="R_3.071.008">
        <li id="R_3.071.008ab">abhitvare ca taṃ draṣṭuṃ sugrīvaṃ vānararṣabham</li>
        <li id="R_3.071.008cd">tadadhīnaṃ hi me saumya sītāyāḥ parimārgaṇam</li>
    </ul>
    <ul id="R_3.071.009">
        <li id="R_3.071.009ab">iti bruvāṇaṃ taṃ rāmaṃ saumitrir idam abravīt</li>
        <li id="R_3.071.009cd">gacchāvas tvaritaṃ tatra mamāpi tvarate manaḥ</li>
    </ul>
    <ul id="R_3.071.010">
        <li id="R_3.071.010ab">āśramāt tu tatas tasmān niṣkramya sa viśāṃ patiḥ</li>
        <li id="R_3.071.010cd">ājagāma tataḥ pampāṃ lakṣmaṇena sahābhibhūḥ</li>
    </ul>
    <ul id="R_3.071.011">
        <li id="R_3.071.011ab">samīkṣamāṇaḥ puṣpāḍhyaṃ sarvato vipuladrumam</li>
        <li id="R_3.071.011cd">koyaṣṭibhiś cārjunakaiḥ śatapatraiś ca kīcakaiḥ</li>
        <li id="R_3.071.011ef">etaiś cānyaiś ca vividhair nāditaṃ tad vanaṃ mahat</li>
    </ul>
    <ul id="R_3.071.012">
        <li id="R_3.071.012ab">sa rāmo vidhivān vṛkṣān sarāṃsi vividhāni ca</li>
        <li id="R_3.071.012cd">paśyan kāmābhisaṃtapto jagāma paramaṃ hradam</li>
    </ul>
    <ul id="R_3.071.013">
        <li id="R_3.071.013ab">sa tām āsādya vai rāmo dūrād udakavāhinīm</li>
        <li id="R_3.071.013cd">mataṅgasarasaṃ nāma hradaṃ samavagāhata</li>
    </ul>
    <ul id="R_3.071.014">
        <li id="R_3.071.014ab">sa tu śokasamāviṣṭo rāmo daśarathātmajaḥ</li>
        <li id="R_3.071.014cd">viveśa nalinīṃ pampāṃ paṅkajaiś ca samāvṛtām</li>
    </ul>
    <ul id="R_3.071.015">
        <li id="R_3.071.015ab">tilakāśokapuṃnāgabakuloddāla kāśinīm</li>
        <li id="R_3.071.015cd">ramyopavanasaṃbādhāṃ padmasaṃpīḍitodakām</li>
    </ul>
    <ul id="R_3.071.016">
        <li id="R_3.071.016ab">sphaṭikopamatoyāḍhyāṃ ślakṣṇavālukasaṃtatām</li>
        <li id="R_3.071.016cd">matsyakacchapasaṃbādhāṃ tīrasthadrumaśobhitām</li>
    </ul>
    <ul id="R_3.071.017">
        <li id="R_3.071.017ab">sakhībhir iva yuktābhir latābhir anuveṣṭitām</li>
        <li id="R_3.071.017cd">kiṃnaroragagandharvayakṣarākṣasasevitām</li>
        <li id="R_3.071.017ef">nānādrumalatākīrṇāṃ śītavārinidhiṃ śubhām</li>
    </ul>
    <ul id="R_3.071.018">
        <li id="R_3.071.018ab">padmaiḥ saugandhikais tāmrāṃ śuklāṃ kumudamaṇḍalaiḥ</li>
        <li id="R_3.071.018cd">nīlāṃ kuvalayoddhātair bahuvarṇāṃ kuthām iva</li>
    </ul>
    <ul id="R_3.071.019">
        <li id="R_3.071.019ab">aravindotpalavatīṃ padmasaugandhikāyutām</li>
        <li id="R_3.071.019cd">puṣpitāmravaṇopetāṃ barhiṇodghuṣṭanāditām</li>
    </ul>
    <ul id="R_3.071.020">
        <li id="R_3.071.020ab">sa tāṃ dṛṣṭvā tataḥ pampāṃ rāmaḥ saumitriṇā saha</li>
        <li id="R_3.071.020cd">vilalāpa ca tejasvī kāmād daśarathātmajaḥ</li>
    </ul>
    <ul id="R_3.071.021">
        <li id="R_3.071.021ab">tilakair bījapūraiś ca vaṭaiḥ śukladrumais tathā</li>
        <li id="R_3.071.021cd">puṣpitaiḥ karavīraiś ca puṃnāgaiś ca supuṣpitaiḥ</li>
    </ul>
    <ul id="R_3.071.022">
        <li id="R_3.071.022ab">mālatīkundagulmaiś ca bhaṇḍīrair niculais tathā</li>
        <li id="R_3.071.022cd">aśokaiḥ saptaparṇaiś ca ketakair atimuktakaiḥ</li>
        <li id="R_3.071.022ef">anyaiś ca vividhair vṛkṣaiḥ pramadevopaśobhitām</li>
    </ul>
    <ul id="R_3.071.023">
        <li id="R_3.071.023ab">asyās tīre tu pūrvoktaḥ parvato dhātumaṇḍitaḥ</li>
        <li id="R_3.071.023cd">ṛśyamūka iti khyātaś citrapuṣpitakānanaḥ</li>
    </ul>
    <ul id="R_3.071.024">
        <li id="R_3.071.024ab">harir ṛkṣarajo nāmnaḥ putras tasya mahātmanaḥ</li>
        <li id="R_3.071.024cd">adhyāste taṃ mahāvīryaḥ sugrīva iti viśrutaḥ</li>
    </ul>
    <ul id="R_3.071.025">
        <li id="R_3.071.025ab">sugrīvam abhigaccha tvaṃ vānarendraṃ nararṣabha</li>
        <li id="R_3.071.025cd">ity uvāca punar vākyaṃ lakṣmaṇaṃ satyavikramam</li>
    </ul>
    <ul id="R_3.071.026">
        <li id="R_3.071.026ab">
            <seg type="pāda" n="a">tato mahad vartma ca dūrasaṃkramaṃ</seg>
            <seg type="pāda" n="b">krameṇa gatvā pravilokayan vanam</seg>
        </li>
        <li id="R_3.071.026cd">
            <seg type="pāda" n="c">dadarśa pampāṃ śubhadarśa kānanām</seg>
            <seg type="pāda" n="d">anekanānāvidhapakṣisaṃkulām</seg>
        </li>
    </ul>
	{/if}
</div>
<div class="kanda-4">
<h4>Kāṇḍa 4</h4>
		{#if kanda4}
    <ul id="R_4.001.001">
        <li id="R_4.001.001ab">sa tāṃ puṣkariṇīṃ gatvā padmotpalajhaṣākulām</li>
        <li id="R_4.001.001cd">rāmaḥ saumitrisahito vilalāpākulendriyaḥ</li>
    </ul>
    <ul id="R_4.001.002">
        <li id="R_4.001.002ab">tasya dṛṣṭvaiva tāṃ harṣād indriyāṇi cakampire</li>
        <li id="R_4.001.002cd">sa kāmavaśam āpannaḥ saumitrim idam abravīt</li>
    </ul>
    <ul id="R_4.001.003">
        <li id="R_4.001.003ab">saumitre paśya pampāyāḥ kānanaṃ śubhadarśanam</li>
        <li id="R_4.001.003cd">yatra rājanti śailābhā drumāḥ saśikharā iva</li>
    </ul>
    <ul id="R_4.001.004">
        <li id="R_4.001.004ab">māṃ tu śokābhisaṃtaptam ādhayaḥ pīḍayanti vai</li>
        <li id="R_4.001.004cd">bharatasya ca duḥkhena vaidehyā haraṇena ca</li>
    </ul>
    <ul id="R_4.001.005">
        <li id="R_4.001.005ab">adhikaṃ pravibhāty etan nīlapītaṃ tu śādvalam</li>
        <li id="R_4.001.005cd">drumāṇāṃ vividhaiḥ puṣpaiḥ paristomair ivārpitam</li>
    </ul>
    <ul id="R_4.001.006">
        <li id="R_4.001.006ab">sukhānilo 'yaṃ saumitre kālaḥ pracuramanmathaḥ</li>
        <li id="R_4.001.006cd">gandhavān surabhir māso jātapuṣpaphaladrumaḥ</li>
    </ul>
    <ul id="R_4.001.007">
        <li id="R_4.001.007ab">paśya rūpāṇi saumitre vanānāṃ puṣpaśālinām</li>
        <li id="R_4.001.007cd">sṛjatāṃ puṣpavarṣāṇi varṣaṃ toyamucām iva</li>
    </ul>
    <ul id="R_4.001.008">
        <li id="R_4.001.008ab">prastareṣu ca ramyeṣu vividhāḥ kānanadrumāḥ</li>
        <li id="R_4.001.008cd">vāyuvegapracalitāḥ puṣpair avakiranti gām</li>
    </ul>
    <ul id="R_4.001.009">
        <li id="R_4.001.009ab">mārutaḥ sukhaṃ saṃsparśe vāti candanaśītalaḥ</li>
        <li id="R_4.001.009cd">ṣaṭpadair anukūjadbhir vaneṣu madhugandhiṣu</li>
    </ul>
    <ul id="R_4.001.010">
        <li id="R_4.001.010ab">giriprastheṣu ramyeṣu puṣpavadbhir manoramaiḥ</li>
        <li id="R_4.001.010cd">saṃsaktaśikharā śailā virājanti mahādrumaiḥ</li>
    </ul>
    <ul id="R_4.001.011">
        <li id="R_4.001.011ab">puṣpitāgrāṃś ca paśyemān karṇikārān samantataḥ</li>
        <li id="R_4.001.011cd">hāṭakapratisaṃchannān narān pītāmbarān iva</li>
    </ul>
    <ul id="R_4.001.012">
        <li id="R_4.001.012ab">ayaṃ vasantaḥ saumitre nānāvihaganāditaḥ</li>
        <li id="R_4.001.012cd">sītayā viprahīṇasya śokasaṃdīpano mama</li>
    </ul>
    <ul id="R_4.001.013">
        <li id="R_4.001.013ab">māṃ hi śokasamākrāntaṃ saṃtāpayati manmathaḥ</li>
        <li id="R_4.001.013cd">hṛṣṭaḥ pravadamānaś ca samāhvayati kokilaḥ</li>
    </ul>
    <ul id="R_4.001.014">
        <li id="R_4.001.014ab">eṣa dātyūhako hṛṣṭo ramye māṃ vananirjhare</li>
        <li id="R_4.001.014cd">praṇadan manmathāviṣṭaṃ śocayiṣyati lakṣmaṇa</li>
    </ul>
    <ul id="R_4.001.015">
        <li id="R_4.001.015ab">vimiśrā vihagāḥ pumbhir ātmavyūhābhinanditāḥ</li>
        <li id="R_4.001.015cd">bhṛṅgarājapramuditāḥ saumitre madhurasvarāḥ</li>
    </ul>
    <ul id="R_4.001.016">
        <li id="R_4.001.016ab">māṃ hi sā mṛgaśāvākṣī cintāśokabalātkṛtam</li>
        <li id="R_4.001.016cd">saṃtāpayati saumitre krūraś caitravanānilaḥ</li>
    </ul>
    <ul id="R_4.001.017">
        <li id="R_4.001.017ab">śikhinībhiḥ parivṛtā mayūrā girisānuṣu</li>
        <li id="R_4.001.017cd">manmathābhiparītasya mama manmathavardhanāḥ</li>
    </ul>
    <ul id="R_4.001.018">
        <li id="R_4.001.018ab">paśya lakṣṇama nṛtyantaṃ mayūram upanṛtyati</li>
        <li id="R_4.001.018cd">śikhinī manmathārtaiṣā bhartāraṃ girisānuṣu</li>
    </ul>
    <ul id="R_4.001.019">
        <li id="R_4.001.019ab">mayūrasya vane nūnaṃ rakṣasā na hṛtā priyā</li>
        <li id="R_4.001.019cd">mama tv ayaṃ vinā vāsaḥ puṣpamāse suduḥsahaḥ</li>
    </ul>
    <ul id="R_4.001.020">
        <li id="R_4.001.020ab">paśya lakṣmaṇa puṣpāṇi niṣphalāni bhavanti me</li>
        <li id="R_4.001.020cd">puṣpabhārasamṛddhānāṃ vanānāṃ śiśirātyaye</li>
    </ul>
    <ul id="R_4.001.021">
        <li id="R_4.001.021ab">vadanti rāvaṃ muditāḥ śakunāḥ saṃghaśaḥ kalam</li>
        <li id="R_4.001.021cd">āhvayanta ivānyonyaṃ kāmonmādakarā mama</li>
    </ul>
    <ul id="R_4.001.022">
        <li id="R_4.001.022ab">nūnaṃ paravaśā sītā sāpi śocaty ahaṃ yathā</li>
        <li id="R_4.001.022cd">śyāmā padmapalāśākṣī mṛdubhāṣā ca me priyā</li>
    </ul>
    <ul id="R_4.001.023">
        <li id="R_4.001.023ab">eṣa puṣpavaho vāyuḥ sukhasparśo himāvahaḥ</li>
        <li id="R_4.001.023cd">tāṃ vicintayataḥ kāntāṃ pāvakapratimo mama</li>
    </ul>
    <ul id="R_4.001.024">
        <li id="R_4.001.024ab">tāṃ vinātha vihaṃgo 'sau pakṣī praṇaditas tadā</li>
        <li id="R_4.001.024cd">vāyasaḥ pādapagataḥ prahṛṣṭam abhinardati</li>
    </ul>
    <ul id="R_4.001.025">
        <li id="R_4.001.025ab">eṣa vai tatra vaidehyā vihagaḥ pratihārakaḥ</li>
        <li id="R_4.001.025cd">pakṣī māṃ tu viśālākṣyāḥ samīpam upaneṣyati</li>
    </ul>
    <ul id="R_4.001.026">
        <li id="R_4.001.026ab">paśya lakṣmaṇa saṃnādaṃ vane madavivardhanam</li>
        <li id="R_4.001.026cd">puṣpitāgreṣu vṛkṣeṣu dvijānām upakūjatām</li>
    </ul>
    <ul id="R_4.001.027">
        <li id="R_4.001.027ab">saumitre paśya pampāyāś citrāsu vanarājiṣu</li>
        <li id="R_4.001.027cd">nalināni prakāśante jale taruṇasūryavat</li>
    </ul>
    <ul id="R_4.001.028">
        <li id="R_4.001.028ab">eṣā prasannasalilā padmanīlotpalāyatā</li>
        <li id="R_4.001.028cd">haṃsakāraṇḍavākīrṇā pampā saugandhikāyutā</li>
    </ul>
    <ul id="R_4.001.029">
        <li id="R_4.001.029ab">cakravākayutā nityaṃ citraprasthavanāntarā</li>
        <li id="R_4.001.029cd">mātaṅgamṛgayūthaiś ca śobhate salilārthibhiḥ</li>
    </ul>
    <ul id="R_4.001.030">
        <li id="R_4.001.030ab">padmakośapalāśāni draṣṭuṃ dṛṣṭir hi manyate</li>
        <li id="R_4.001.030cd">sītāyā netrakośābhyāṃ sadṛśānīti lakṣmaṇa</li>
    </ul>
    <ul id="R_4.001.031">
        <li id="R_4.001.031ab">padmakesarasaṃsṛṣṭo vṛkṣāntaraviniḥsṛtaḥ</li>
        <li id="R_4.001.031cd">niḥśvāsa iva sītāyā vāti vāyur manoharaḥ</li>
    </ul>
    <ul id="R_4.001.032">
        <li id="R_4.001.032ab">saumitre paśya pampāyā dakṣiṇe girisānuni</li>
        <li id="R_4.001.032cd">puṣpitāṃ karṇikārasya yaṣṭiṃ paramaśobhanām</li>
    </ul>
    <ul id="R_4.001.033">
        <li id="R_4.001.033ab">adhikaṃ śailarājo 'yaṃ dhātubhis tu vibhūṣitaḥ</li>
        <li id="R_4.001.033cd">vicitraṃ sṛjate reṇuṃ vāyuvegavighaṭṭitam</li>
    </ul>
    <ul id="R_4.001.034">
        <li id="R_4.001.034ab">giriprasthās tu saumitre sarvataḥ saṃprapuṣpitaiḥ</li>
        <li id="R_4.001.034cd">niṣpatraiḥ sarvato ramyaiḥ pradīpā iva kuṃśukaiḥ</li>
    </ul>
    <ul id="R_4.001.035">
        <li id="R_4.001.035ab">pampātīraruhāś ceme saṃsaktā madhugandhinaḥ</li>
        <li id="R_4.001.035cd">mālatīmallikāṣaṇḍāḥ karavīrāś ca puṣpitāḥ</li>
    </ul>
    <ul id="R_4.001.036">
        <li id="R_4.001.036ab">ketakyaḥ sinduvārāś ca vāsantyaś ca supuṣpitāḥ</li>
        <li id="R_4.001.036cd">mādhavyo gandhapūrṇāś ca kundagulmāś ca sarvaśaḥ</li>
    </ul>
    <ul id="R_4.001.037">
        <li id="R_4.001.037ab">ciribilvā madhūkāś ca vañjulā bakulās tathā</li>
        <li id="R_4.001.037cd">campakās tilakāś caiva nāgavṛkṣāś ca puṣpitāḥ</li>
    </ul>
    <ul id="R_4.001.038">
        <li id="R_4.001.038ab">nīpāś ca varaṇāś caiva kharjūrāś ca supuṣpitāḥ</li>
        <li id="R_4.001.038cd">aṅkolāś ca kuraṇṭāś ca cūrṇakāḥ pāribhadrakāḥ</li>
    </ul>
    <ul id="R_4.001.039">
        <li id="R_4.001.039ab">cūtāḥ pāṭalayaś caiva kovidārāś ca puṣpitāḥ</li>
        <li id="R_4.001.039cd">mucukundārjunāś caiva dṛśyante girisānuṣu</li>
    </ul>
    <ul id="R_4.001.040">
        <li id="R_4.001.040ab">ketakoddālakāś caiva śirīṣāḥ śiṃśapā dhavāḥ</li>
        <li id="R_4.001.040cd">śālmalyaḥ kiṃśukāś caiva raktāḥ kurabakās tathā</li>
        <li id="R_4.001.040ef">tiniśā nakta mālāś ca candanāḥ syandanās tathā</li>
    </ul>
    <ul id="R_4.001.041">
        <li id="R_4.001.041ab">vividhā vividhaiḥ puṣpais tair eva nagasānuṣu</li>
        <li id="R_4.001.041cd">vikīrṇaiḥ pītaraktābhāḥ saumitre prastarāḥ kṛtāḥ</li>
    </ul>
    <ul id="R_4.001.042">
        <li id="R_4.001.042ab">himānte paśya saumitre vṛkṣāṇāṃ puṣpasaṃbhavam</li>
        <li id="R_4.001.042cd">puṣpamāse hi taravaḥ saṃgharṣād iva puṣpitāḥ</li>
    </ul>
    <ul id="R_4.001.043">
        <li id="R_4.001.043ab">paśya śītajalāṃ cemāṃ saumitre puṣkarāyutām</li>
        <li id="R_4.001.043cd">cakravākānucaritāṃ kāraṇḍavaniṣevitām</li>
        <li id="R_4.001.043ef">plavaiḥ krauñcaiś ca saṃpūrṇāṃ varāhamṛgasevitām</li>
    </ul>
    <ul id="R_4.001.044">
        <li id="R_4.001.044ab">adhikaṃ śobhate pampāvikūjadbhir vihaṃgamaiḥ</li>
    </ul>
    <ul id="R_4.001.045">
        <li id="R_4.001.045ab">dīpayantīva me kāmaṃ vividhā muditā dvijāḥ</li>
        <li id="R_4.001.045cd">śyāmāṃ candramukhīṃ smṛtvā priyāṃ padmanibhekṣaṇām</li>
    </ul>
    <ul id="R_4.001.046">
        <li id="R_4.001.046ab">paya sānuṣu citreṣu mṛgībhiḥ sahitān mṛgān</li>
        <li id="R_4.001.046cd">māṃ punar mṛgaśāvākṣyā vaidehyā virahīkṛtam</li>
    </ul>
    <ul id="R_4.001.047">
        <li id="R_4.001.047ab">evaṃ sa vilapaṃs tatra śokopahatacetanaḥ</li>
        <li id="R_4.001.047cd">avekṣata śivāṃ pampāṃ ramyavārivahāṃ śubhām</li>
    </ul>
    <ul id="R_4.001.048">
        <li id="R_4.001.048ab">
            <seg type="pāda" n="a">nirīkṣamāṇaḥ sahasā mahātmā</seg>
            <seg type="pāda" n="b">sarvaṃ vanaṃ nirjharakandaraṃ ca</seg>
        </li>
        <li id="R_4.001.048cd">
            <seg type="pāda" n="c">udvignacetāḥ saha lakṣmaṇena</seg>
            <seg type="pāda" n="d">vicārya duḥkhopahataḥ pratasthe</seg>
        </li>
    </ul>
    <ul id="R_4.001.049">
        <li id="R_4.001.049ab">
            <seg type="pāda" n="a">tāv ṛṣyamūkaṃ sahitau prayātau</seg>
            <seg type="pāda" n="b">sugrīvaśākhāmṛgasevitaṃ tam</seg>
        </li>
        <li id="R_4.001.049cd">
            <seg type="pāda" n="c">trastās tu dṛṣṭvā harayo babhūvur</seg>
            <seg type="pāda" n="d">mahaujasau rāghavalakṣmaṇau tau</seg>
        </li>
    </ul>
    <ul id="R_4.002.001">
        <li id="R_4.002.001ab">tau tu dṛṣṭvā mahātmānau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_4.002.001cd">varāyudhadharau vīrau sugrīvaḥ śaṅkito 'bhavat</li>
    </ul>
    <ul id="R_4.002.002">
        <li id="R_4.002.002ab">udvignahṛdayaḥ sarvā diśaḥ samavalokayan</li>
        <li id="R_4.002.002cd">na vyatiṣṭhata kasmiṃś cid deśe vānarapuṃgavaḥ</li>
    </ul>
    <ul id="R_4.002.003">
        <li id="R_4.002.003ab">naiva cakre manaḥ sthāne vīkṣamāṇo mahābalau</li>
        <li id="R_4.002.003cd">kapeḥ paramabhītasya cittaṃ vyavasasāda ha</li>
    </ul>
    <ul id="R_4.002.004">
        <li id="R_4.002.004ab">cintayitvā sa dharmātmā vimṛśya gurulāghavam</li>
        <li id="R_4.002.004cd">sugrīvaḥ paramodvignaḥ sarvair anucaraiḥ saha</li>
    </ul>
    <ul id="R_4.002.005">
        <li id="R_4.002.005ab">tataḥ sa sacivebhyas tu sugrīvaḥ plavagādhipaḥ</li>
        <li id="R_4.002.005cd">śaśaṃsa paramodvignaḥ paśyaṃs tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_4.002.006">
        <li id="R_4.002.006ab">etau vanam idaṃ durgaṃ vālipraṇihitau dhruvam</li>
        <li id="R_4.002.006cd">chadmanā cīravasanau pracarantāv ihāgatau</li>
    </ul>
    <ul id="R_4.002.007">
        <li id="R_4.002.007ab">tataḥ sugrīvasacivā dṛṣṭvā paramadhanvinau</li>
        <li id="R_4.002.007cd">jagmur giritaṭāt tasmād anyac chikharam uttamam</li>
    </ul>
    <ul id="R_4.002.008">
        <li id="R_4.002.008ab">te kṣipram abhigamyātha yūthapā yūthaparṣabham</li>
        <li id="R_4.002.008cd">harayo vānaraśreṣṭhaṃ parivāryopatasthire</li>
    </ul>
    <ul id="R_4.002.009">
        <li id="R_4.002.009ab">ekam ekāyanagatāḥ plavamānā girer girim</li>
        <li id="R_4.002.009cd">prakampayanto vegena girīṇāṃ śikharāṇi ca</li>
    </ul>
    <ul id="R_4.002.010">
        <li id="R_4.002.010ab">tataḥ śākhāmṛgāḥ sarve plavamānā mahābalāḥ</li>
        <li id="R_4.002.010cd">babhañjuś ca nagāṃs tatra puṣpitān durgasaṃśritān</li>
    </ul>
    <ul id="R_4.002.011">
        <li id="R_4.002.011ab">āplavanto harivarāḥ sarvatas taṃ mahāgirim</li>
        <li id="R_4.002.011cd">mṛgamārjāraśārdūlāṃs trāsayanto yayus tadā</li>
    </ul>
    <ul id="R_4.002.012">
        <li id="R_4.002.012ab">tataḥ sugrīvasacivāḥ parvatendraṃ samāśritāḥ</li>
        <li id="R_4.002.012cd">saṃgamya kapimukhyena sarve prāñjalayaḥ sthitāḥ</li>
    </ul>
    <ul id="R_4.002.013">
        <li id="R_4.002.013ab">tatas taṃ bhayasaṃtrastaṃ vālikilbiṣaśaṅkitam</li>
        <li id="R_4.002.013cd">uvāca hanumān vākyaṃ sugrīvaṃ vākyakovidaḥ</li>
    </ul>
    <ul id="R_4.002.014">
        <li id="R_4.002.014ab">yasmād udvignacetās tvaṃ pradruto haripuṃgava</li>
        <li id="R_4.002.014cd">taṃ krūradarśanaṃ krūraṃ neha paśyāmi vālinam</li>
    </ul>
    <ul id="R_4.002.015">
        <li id="R_4.002.015ab">yasmāt tava bhayaṃ saumya pūrvajāt pāpakarmaṇaḥ</li>
        <li id="R_4.002.015cd">sa neha vālī duṣṭātmā na te paśyāmy ahaṃ bhayam</li>
    </ul>
    <ul id="R_4.002.016">
        <li id="R_4.002.016ab">aho śākhāmṛgatvaṃ te vyaktam eva plavaṃgama</li>
        <li id="R_4.002.016cd">laghucittatayātmānaṃ na sthāpayasi yo matau</li>
    </ul>
    <ul id="R_4.002.017">
        <li id="R_4.002.017ab">buddhivijñānasaṃpanna iṅgitaiḥ sarvam ācara</li>
        <li id="R_4.002.017cd">na hy abuddhiṃ gato rājā sarvabhūtāni śāsti hi</li>
    </ul>
    <ul id="R_4.002.018">
        <li id="R_4.002.018ab">sugrīvas tu śubhaṃ vākyaṃ śrutvā sarvaṃ hanūmataḥ</li>
        <li id="R_4.002.018cd">tataḥ śubhataraṃ vākyaṃ hanūmantam uvāca ha</li>
    </ul>
    <ul id="R_4.002.019">
        <li id="R_4.002.019ab">dīrghabāhū viśālākṣau śaracāpāsidhāriṇau</li>
        <li id="R_4.002.019cd">kasya na syād bhayaṃ dṛṣṭvā etau surasutopamau</li>
    </ul>
    <ul id="R_4.002.020">
        <li id="R_4.002.020ab">vālipraṇihitāv etau śaṅke 'haṃ puruṣottamau</li>
        <li id="R_4.002.020cd">rājāno bahumitrāś ca viśvāso nātra hi kṣamaḥ</li>
    </ul>
    <ul id="R_4.002.021">
        <li id="R_4.002.021ab">arayaś ca manuṣyeṇa vijñeyāś channacāriṇaḥ</li>
        <li id="R_4.002.021cd">viśvastānām aviśvastāś chidreṣu praharanti hi</li>
    </ul>
    <ul id="R_4.002.022">
        <li id="R_4.002.022ab">kṛtyeṣu vālī medhāvī rājāno bahudarśanāḥ</li>
        <li id="R_4.002.022cd">bhavanti parahantāras te jñeyāḥ prākṛtair naraiḥ</li>
    </ul>
    <ul id="R_4.002.023">
        <li id="R_4.002.023ab">tau tvayā prākṛtenaiva gatvā jñeyau plavaṃgama</li>
        <li id="R_4.002.023cd">śaṅkitānāṃ prakāraiś ca rūpavyābhāṣaṇena ca</li>
    </ul>
    <ul id="R_4.002.024">
        <li id="R_4.002.024ab">lakṣayasva tayor bhāvaṃ prahṛṣṭamanasau yadi</li>
        <li id="R_4.002.024cd">viśvāsayan praśaṃsābhir iṅgitaiś ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_4.002.025">
        <li id="R_4.002.025ab">mamaivābhimukhaṃ sthitvā pṛccha tvaṃ haripuṃgava</li>
        <li id="R_4.002.025cd">prayojanaṃ praveśasya vanasyāsya dhanurdharau</li>
    </ul>
    <ul id="R_4.002.026">
        <li id="R_4.002.026ab">śuddhātmānau yadi tv etau jānīhi tvaṃ plavaṃgama</li>
        <li id="R_4.002.026cd">vyābhāṣitair vā rūpair vā vijñeyā duṣṭatānayoḥ</li>
    </ul>
    <ul id="R_4.002.027">
        <li id="R_4.002.027ab">ity evaṃ kapirājena saṃdiṣṭo mārutātmajaḥ</li>
        <li id="R_4.002.027cd">cakāra gamane buddhiṃ yatra tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_4.002.028">
        <li id="R_4.002.028ab">
            <seg type="pāda" n="a">tatheti saṃpūjya vacas tu tasya</seg>
            <seg type="pāda" n="b">kapeḥ subhītasya durāsadasya</seg>
        </li>
        <li id="R_4.002.028cd">
            <seg type="pāda" n="c">mahānubhāvo hanumān yayau tadā</seg>
            <seg type="pāda" n="d">sa yatra rāmo 'tibalaś ca lakṣmaṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.003.001">
        <li id="R_4.003.001ab">vaco vijñāya hanumān sugrīvasya mahātmanaḥ</li>
        <li id="R_4.003.001cd">parvatād ṛśyamūkāt tu pupluve yatra rāghavau</li>
    </ul>
    <ul id="R_4.003.002">
        <li id="R_4.003.002ab">sa tatra gatvā hanumān balavān vānarottamaḥ</li>
        <li id="R_4.003.002cd">upacakrāma tau vāgbhir mṛdvībhiḥ satyavikramaḥ</li>
    </ul>
    <ul id="R_4.003.003">
        <li id="R_4.003.003ab">svakaṃ rūpaṃ parityajya bhikṣurūpeṇa vānaraḥ</li>
        <li id="R_4.003.003cd">ābabhāṣe ca tau vīrau yathāvat praśaśaṃsa ca</li>
    </ul>
    <ul id="R_4.003.004">
        <li id="R_4.003.004ab">rājarṣidevapratimau tāpasau saṃśitavratau</li>
        <li id="R_4.003.004cd">deśaṃ katham imaṃ prāptau bhavantau varavarṇinau</li>
    </ul>
    <ul id="R_4.003.005">
        <li id="R_4.003.005ab">trāsayantau mṛgagaṇān anyāṃś ca vanacāriṇaḥ</li>
        <li id="R_4.003.005cd">pampātīraruhān vṛkṣān vīkṣamāṇau samantataḥ</li>
    </ul>
    <ul id="R_4.003.006">
        <li id="R_4.003.006ab">imāṃ nadīṃ śubhajalāṃ śobhayantau tarasvinau</li>
        <li id="R_4.003.006cd">dhairyavantau suvarṇābhau kau yuvāṃ cīravāsasau</li>
    </ul>
    <ul id="R_4.003.007">
        <li id="R_4.003.007ab">siṃhaviprekṣitau vīrau siṃhātibalavikramau</li>
        <li id="R_4.003.007cd">śakracāpanibhe cāpe pragṛhya vipulair bhujaiḥ</li>
    </ul>
    <ul id="R_4.003.008">
        <li id="R_4.003.008ab">śrīmantau rūpasaṃpannau vṛṣabhaśreṣṭhavikramau</li>
        <li id="R_4.003.008cd">hastihastopamabhujau dyutimantau nararṣabhau</li>
    </ul>
    <ul id="R_4.003.009">
        <li id="R_4.003.009ab">prabhayā parvatendro 'yaṃ yuvayor avabhāsitaḥ</li>
        <li id="R_4.003.009cd">rājyārhāv amaraprakhyau kathaṃ deśam ihāgatau</li>
    </ul>
    <ul id="R_4.003.010">
        <li id="R_4.003.010ab">padmapatrekṣaṇau vīrau jaṭāmaṇḍaladhāriṇau</li>
        <li id="R_4.003.010cd">anyonyasadṛśau vīrau devalokād ivāgatau</li>
    </ul>
    <ul id="R_4.003.011">
        <li id="R_4.003.011ab">yadṛcchayeva saṃprāptau candrasūryau vasuṃdharām</li>
        <li id="R_4.003.011cd">viśālavakṣasau vīrau mānuṣau devarūpiṇau</li>
    </ul>
    <ul id="R_4.003.012">
        <li id="R_4.003.012ab">siṃhaskandhau mahāsattvau samadāv iva govṛṣau</li>
        <li id="R_4.003.012cd">āyatāś ca suvṛttāś ca bāhavaḥ parighottamāḥ</li>
        <li id="R_4.003.012ef">sarvabhūṣaṇabhūṣārhāḥ kim arthaṃ na vibhūṣitaḥ</li>
    </ul>
    <ul id="R_4.003.013">
        <li id="R_4.003.013ab">ubhau yogyāv ahaṃ manye rakṣituṃ pṛthivīm imām</li>
        <li id="R_4.003.013cd">sasāgaravanāṃ kṛtsnāṃ vindhyameruvibhūṣitām</li>
    </ul>
    <ul id="R_4.003.014">
        <li id="R_4.003.014ab">ime ca dhanuṣī citre ślakṣṇe citrānulepane</li>
        <li id="R_4.003.014cd">prakāśete yathendrasya vajre hemavibhūṣite</li>
    </ul>
    <ul id="R_4.003.015">
        <li id="R_4.003.015ab">saṃpūrṇā niśitair bāṇair tūṇāś ca śubhadarśanāḥ</li>
        <li id="R_4.003.015cd">jīvitāntakarair ghorair jvaladbhir iva pannagaiḥ</li>
    </ul>
    <ul id="R_4.003.016">
        <li id="R_4.003.016ab">mahāpramāṇau vipulau taptahāṭakabhūṣitau</li>
        <li id="R_4.003.016cd">khaḍgāv etau virājete nirmuktabhujagāv iva</li>
    </ul>
    <ul id="R_4.003.017">
        <li id="R_4.003.017ab">evaṃ māṃ paribhāṣantaṃ kasmād vai nābhibhāṣathaḥ</li>
    </ul>
    <ul id="R_4.003.018">
        <li id="R_4.003.018ab">sugrīvo nāma dharmātmā kaś cid vānarayūthapaḥ</li>
        <li id="R_4.003.018cd">vīro vinikṛto bhrātrā jagad bhramati duḥkhitaḥ</li>
    </ul>
    <ul id="R_4.003.019">
        <li id="R_4.003.019ab">prāpto 'haṃ preṣitas tena sugrīveṇa mahātmanā</li>
        <li id="R_4.003.019cd">rājñā vānaramukhyānāṃ hanumān nāma vānaraḥ</li>
    </ul>
    <ul id="R_4.003.020">
        <li id="R_4.003.020ab">yuvābhyāṃ saha dharmātmā sugrīvaḥ sakhyam icchati</li>
        <li id="R_4.003.020cd">tasya māṃ sacivaṃ vittaṃ vānaraṃ pavanātmajam</li>
    </ul>
    <ul id="R_4.003.021">
        <li id="R_4.003.021ab">bhikṣurūpapraticchannaṃ sugrīvapriyakāmyayā</li>
        <li id="R_4.003.021cd">ṛśyamūkād iha prāptaṃ kāmagaṃ kāmarūpiṇam</li>
    </ul>
    <ul id="R_4.003.022">
        <li id="R_4.003.022ab">evam uktvā tu hanumāṃs tau vīrau rāmalakṣmaṇau</li>
        <li id="R_4.003.022cd">vākyajñau vākyakuśalaḥ punar novāca kiṃ cana</li>
    </ul>
    <ul id="R_4.003.023">
        <li id="R_4.003.023ab">etac chrutvā vacas tasya rāmo lakṣmaṇam abravīt</li>
        <li id="R_4.003.023cd">prahṛṣṭavadanaḥ śrīmān bhrātaraṃ pārśvataḥ sthitam</li>
    </ul>
    <ul id="R_4.003.024">
        <li id="R_4.003.024ab">sacivo 'yaṃ kapīndrasya sugrīvasya mahātmanaḥ</li>
        <li id="R_4.003.024cd">tam eva kāṅkṣamāṇasya mamāntikam upāgataḥ</li>
    </ul>
    <ul id="R_4.003.025">
        <li id="R_4.003.025ab">tam abhyabhāṣa saumitre sugrīvasacivaṃ kapim</li>
        <li id="R_4.003.025cd">vākyajñaṃ madhurair vākyaiḥ snehayuktam ariṃdamam</li>
    </ul>
    <ul id="R_4.004.001">
        <li id="R_4.004.001ab">tataḥ prahṛṣṭo hanumān kṛtyavān iti tad vacaḥ</li>
        <li id="R_4.004.001cd">śrutvā madhurasaṃbhāṣaṃ sugrīvaṃ manasā gataḥ</li>
    </ul>
    <ul id="R_4.004.002">
        <li id="R_4.004.002ab">bhavyo rājyāgamas tasya sugrīvasya mahātmanaḥ</li>
        <li id="R_4.004.002cd">yad ayaṃ kṛtyavān prāptaḥ kṛtyaṃ caitad upāgatam</li>
    </ul>
    <ul id="R_4.004.003">
        <li id="R_4.004.003ab">tataḥ paramasaṃhṛṣṭo hanūmān plavagarṣabhaḥ</li>
        <li id="R_4.004.003cd">pratyuvāca tato vākyaṃ rāmaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_4.004.004">
        <li id="R_4.004.004ab">kimarthaṃ tvaṃ vanaṃ ghoraṃ pampākānanamaṇḍitam</li>
        <li id="R_4.004.004cd">āgataḥ sānujo durgaṃ nānāvyālamṛgāyutam</li>
    </ul>
    <ul id="R_4.004.005">
        <li id="R_4.004.005ab">tasya tad vacanaṃ śrutvā lakṣmaṇo rāmacoditaḥ</li>
        <li id="R_4.004.005cd">ācacakṣe mahātmānaṃ rāmaṃ daśarathātmajam</li>
    </ul>
    <ul id="R_4.004.006">
        <li id="R_4.004.006ab">rājā daśaratho nāma dyutimān dharmavatsalaḥ</li>
        <li id="R_4.004.006cd">tasyāyaṃ pūrvajaḥ putro rāmo nāma janaiḥ śrutaḥ</li>
    </ul>
    <ul id="R_4.004.007">
        <li id="R_4.004.007ab">śaraṇyaḥ sarvabhūtānāṃ pitur nirdeśapāragaḥ</li>
        <li id="R_4.004.007cd">vīro daśarathasyāyaṃ putrāṇāṃ guṇavattaraḥ</li>
    </ul>
    <ul id="R_4.004.008">
        <li id="R_4.004.008ab">rājyād bhraṣṭo vane vastuṃ mayā sārdham ihāgataḥ</li>
        <li id="R_4.004.008cd">bhāryayā ca mahātejāḥ sītayānugato vaśī</li>
        <li id="R_4.004.008ef">dinakṣaye mahātejāḥ prabhayeva divākaraḥ</li>
    </ul>
    <ul id="R_4.004.009">
        <li id="R_4.004.009ab">aham asyāvaro bhrātā guṇair dāsyam upāgataḥ</li>
        <li id="R_4.004.009cd">kṛtajñasya bahujñasya lakṣmaṇo nāma nāmataḥ</li>
    </ul>
    <ul id="R_4.004.010">
        <li id="R_4.004.010ab">sukhārhasya mahārhasya sarvabhūtahitātmanaḥ</li>
        <li id="R_4.004.010cd">aiśvaryeṇa vihīnasya vanavāsāśritasya ca</li>
    </ul>
    <ul id="R_4.004.011">
        <li id="R_4.004.011ab">rakṣasāpahṛtā bhāryā rahite kāmarūpiṇā</li>
        <li id="R_4.004.011cd">tac ca na jñāyate rakṣaḥ patnī yenāsya sā hṛtā</li>
    </ul>
    <ul id="R_4.004.012">
        <li id="R_4.004.012ab">danur nāma śriyaḥ putraḥ śāpād rākṣasatāṃ gataḥ</li>
        <li id="R_4.004.012cd">ākhyātas tena sugrīvaḥ samartho vānarādhipaḥ</li>
    </ul>
    <ul id="R_4.004.013">
        <li id="R_4.004.013ab">sa jñāsyati mahāvīryas tava bhāryāpahāriṇam</li>
        <li id="R_4.004.013cd">evam uktvā danuḥ svargaṃ bhrājamāno gataḥ sukham</li>
    </ul>
    <ul id="R_4.004.014">
        <li id="R_4.004.014ab">etat te sarvam ākhyātaṃ yāthātathyena pṛcchataḥ</li>
        <li id="R_4.004.014cd">ahaṃ caiva hi rāmaś ca sugrīvaṃ śaraṇaṃ gatau</li>
    </ul>
    <ul id="R_4.004.015">
        <li id="R_4.004.015ab">eṣa dattvā ca vittāni prāpya cānuttamaṃ yaśaḥ</li>
        <li id="R_4.004.015cd">lokanāthaḥ purā bhūtvā sugrīvaṃ nātham icchati</li>
    </ul>
    <ul id="R_4.004.016">
        <li id="R_4.004.016ab">śokābhibhūte rāme tu śokārte śaraṇaṃ gate</li>
        <li id="R_4.004.016cd">kartum arhati sugrīvaḥ prasādaṃ saha yūthapaiḥ</li>
    </ul>
    <ul id="R_4.004.017">
        <li id="R_4.004.017ab">evaṃ bruvāṇaṃ saumitriṃ karuṇaṃ sāśrupātanam</li>
        <li id="R_4.004.017cd">hanūmān pratyuvācedaṃ vākyaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_4.004.018">
        <li id="R_4.004.018ab">īdṛśā buddhisaṃpannā jitakrodhā jitendriyāḥ</li>
        <li id="R_4.004.018cd">draṣṭavyā vānarendreṇa diṣṭyā darśanam āgatāḥ</li>
    </ul>
    <ul id="R_4.004.019">
        <li id="R_4.004.019ab">sa hi rājyāc ca vibhraṣṭaḥ kṛtavairaś ca vālinā</li>
        <li id="R_4.004.019cd">hṛtadāro vane trasto bhrātrā vinikṛto bhṛśam</li>
    </ul>
    <ul id="R_4.004.020">
        <li id="R_4.004.020ab">kariṣyati sa sāhāyyaṃ yuvayor bhāskarātmajaḥ</li>
        <li id="R_4.004.020cd">sugrīvaḥ saha cāsmābhiḥ sītāyāḥ parimārgaṇe</li>
    </ul>
    <ul id="R_4.004.021">
        <li id="R_4.004.021ab">ity evam uktvā hanumāñ ślakṣṇaṃ madhurayā girā</li>
        <li id="R_4.004.021cd">babhāṣe so 'bhigacchāmaḥ sugrīvam iti rāghavam</li>
    </ul>
    <ul id="R_4.004.022">
        <li id="R_4.004.022ab">evaṃ bruvāṇaṃ dharmātmā hanūmantaṃ sa lakṣmaṇaḥ</li>
        <li id="R_4.004.022cd">pratipūjya yathānyāyam idaṃ provāca rāghavam</li>
    </ul>
    <ul id="R_4.004.023">
        <li id="R_4.004.023ab">kapiḥ kathayate hṛṣṭo yathāyaṃ mārutātmajaḥ</li>
        <li id="R_4.004.023cd">kṛtyavān so 'pi saṃprāptaḥ kṛtakṛtyo 'si rāghava</li>
    </ul>
    <ul id="R_4.004.024">
        <li id="R_4.004.024ab">prasannamukhavarṇaś ca vyaktaṃ hṛṣṭaś ca bhāṣate</li>
        <li id="R_4.004.024cd">nānṛtaṃ vakṣyate vīro hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_4.004.025">
        <li id="R_4.004.025ab">tataḥ sa tu mahāprājño hanūmān mārutātmajaḥ</li>
        <li id="R_4.004.025cd">jagāmādāya tau vīrau harirājāya rāghavau</li>
    </ul>
    <ul id="R_4.004.026">
        <li id="R_4.004.026ab">
            <seg type="pāda" n="a">sa tu vipula yaśāḥ kapipravīraḥ</seg>
            <seg type="pāda" n="b">pavanasutaḥ kṛtakṛtyavat prahṛṣṭaḥ</seg>
        </li>
        <li id="R_4.004.026cd">
            <seg type="pāda" n="c">girivaram uruvikramaḥ prayātaḥ</seg>
            <seg type="pāda" n="d">sa śubhamatiḥ saha rāmalakṣmaṇābhyām</seg>
        </li>
    </ul>
    <ul id="R_4.005.001">
        <li id="R_4.005.001ab">ṛśyamūkāt tu hanumān gatvā taṃ malayaṃ giram</li>
        <li id="R_4.005.001cd">ācacakṣe tadā vīrau kapirājāya rāghavau</li>
    </ul>
    <ul id="R_4.005.002">
        <li id="R_4.005.002ab">ayaṃ rāmo mahāprājñaḥ saṃprāpto dṛḍhavikramaḥ</li>
        <li id="R_4.005.002cd">lakṣmaṇena saha bhrātrā rāmo 'yaṃ satyavikramaḥ</li>
    </ul>
    <ul id="R_4.005.003">
        <li id="R_4.005.003ab">ikṣvākūṇāṃ kule jāto rāmo daśarathātmajaḥ</li>
        <li id="R_4.005.003cd">dharme nigaditaś caiva pitur nirdeśapālakaḥ</li>
    </ul>
    <ul id="R_4.005.004">
        <li id="R_4.005.004ab">tasyāsya vasato 'raṇye niyatasya mahātmanaḥ</li>
        <li id="R_4.005.004cd">rakṣasāpahṛtā bhāryā sa tvāṃ śaraṇam āgataḥ</li>
    </ul>
    <ul id="R_4.005.005">
        <li id="R_4.005.005ab">rājasūyāśvamedhaiś ca vahnir yenābhitarpitaḥ</li>
        <li id="R_4.005.005cd">dakṣiṇāś ca tathotsṛṣṭā gāvaḥ śatasahasraśaḥ</li>
    </ul>
    <ul id="R_4.005.006">
        <li id="R_4.005.006ab">tapasā satyavākyena vasudhā yena pālitā</li>
        <li id="R_4.005.006cd">strīhetos tasya putro 'yaṃ rāmas tvāṃ śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_4.005.007">
        <li id="R_4.005.007ab">bhavatā sakhyakāmau tau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_4.005.007cd">pratigṛhyārcayasvemau pūjanīyatamāv ubhau</li>
    </ul>
    <ul id="R_4.005.008">
        <li id="R_4.005.008ab">śrutvā hanumato vākyaṃ sugrīvo hṛṣṭamānasaḥ</li>
        <li id="R_4.005.008cd">bhayaṃ sa rāghavād ghoraṃ prajahau vigatajvaraḥ</li>
    </ul>
    <ul id="R_4.005.009">
        <li id="R_4.005.009ab">sa kṛtvā mānuṣaṃ rūpaṃ sugrīvaḥ plavagādhipaḥ</li>
        <li id="R_4.005.009cd">darśanīyatamo bhūtvā prītyā provāca rāghavam</li>
    </ul>
    <ul id="R_4.005.010">
        <li id="R_4.005.010ab">bhavān dharmavinītaś ca vikrāntaḥ sarvavatsalaḥ</li>
        <li id="R_4.005.010cd">ākhyātā vāyuputreṇa tattvato me bhavadguṇāḥ</li>
    </ul>
    <ul id="R_4.005.011">
        <li id="R_4.005.011ab">tan mamaivaiṣa satkāro lābhaś caivottamaḥ prabho</li>
        <li id="R_4.005.011cd">yat tvam icchasi sauhārdaṃ vānareṇa mayā saha</li>
    </ul>
    <ul id="R_4.005.012">
        <li id="R_4.005.012ab">rocate yadi vā sakhyaṃ bāhur eṣa prasāritaḥ</li>
        <li id="R_4.005.012cd">gṛhyatāṃ pāṇinā pāṇir maryādā vadhyatāṃ dhruvā</li>
    </ul>
    <ul id="R_4.005.013">
        <li id="R_4.005.013ab">etat tu vacanaṃ śrutvā sugrīvasya subhāṣitam</li>
        <li id="R_4.005.013cd">saṃprahṛṣṭamanā hastaṃ pīḍayām āsa pāṇinā</li>
        <li id="R_4.005.013ef">hṛdyaṃ sauhṛdam ālambya paryaṣvajata pīḍitam</li>
    </ul>
    <ul id="R_4.005.014">
        <li id="R_4.005.014ab">tato hanūmān saṃtyajya bhikṣurūpam ariṃdamaḥ</li>
        <li id="R_4.005.014cd">kāṣṭhayoḥ svena rūpeṇa janayām āsa pāvakam</li>
    </ul>
    <ul id="R_4.005.015">
        <li id="R_4.005.015ab">dīpyamānaṃ tato vahniṃ puṣpair abhyarcya satkṛtam</li>
        <li id="R_4.005.015cd">tayor madhye tu suprīto nidadhe susamāhitaḥ</li>
    </ul>
    <ul id="R_4.005.016">
        <li id="R_4.005.016ab">tato 'gniṃ dīpyamānaṃ tau cakratuś ca pradakṣiṇam</li>
        <li id="R_4.005.016cd">sugrīvo rāghavaś caiva vayasyatvam upāgatau</li>
    </ul>
    <ul id="R_4.005.017">
        <li id="R_4.005.017ab">tataḥ suprīta manasau tāv ubhau harirāghavau</li>
        <li id="R_4.005.017cd">anyonyam abhivīkṣantau na tṛptim upajagmatuḥ</li>
    </ul>
    <ul id="R_4.005.018">
        <li id="R_4.005.018ab">tataḥ sarvārthavidvāṃsaṃ rāmaṃ daśarathātmajam</li>
        <li id="R_4.005.018cd">sugrīvaḥ prāha tejasvī vākyam ekamanās tadā</li>
    </ul>
    <ul id="R_4.006.001">
        <li id="R_4.006.001ab">ayam ākhyāti me rāma sacivo mantrisattamaḥ</li>
        <li id="R_4.006.001cd">hanumān yannimittaṃ tvaṃ nirjanaṃ vanam āgataḥ</li>
    </ul>
    <ul id="R_4.006.002">
        <li id="R_4.006.002ab">lakṣmaṇena saha bhrātrā vasataś ca vane tava</li>
        <li id="R_4.006.002cd">rakṣasāpahṛtā bhāryā maithilī janakātmajā</li>
    </ul>
    <ul id="R_4.006.003">
        <li id="R_4.006.003ab">tvayā viyuktā rudatī lakṣmaṇena ca dhīmatā</li>
        <li id="R_4.006.003cd">antaraṃ prepsunā tena hatvā gṛdhraṃ jaṭāyuṣam</li>
    </ul>
    <ul id="R_4.006.004">
        <li id="R_4.006.004ab">bhāryāviyogajaṃ duḥkhaṃ nacirāt tvaṃ vimokṣyase</li>
        <li id="R_4.006.004cd">ahaṃ tām ānayiṣyāmi naṣṭāṃ vedaśrutiṃ yathā</li>
    </ul>
    <ul id="R_4.006.005">
        <li id="R_4.006.005ab">rasātale vā vartantīṃ vartantīṃ vā nabhastale</li>
        <li id="R_4.006.005cd">aham ānīya dāsyāmi tava bhāryām ariṃdama</li>
    </ul>
    <ul id="R_4.006.006">
        <li id="R_4.006.006ab">idaṃ tathyaṃ mama vacas tvam avehi ca rāghava</li>
        <li id="R_4.006.006cd">tyaja śokaṃ mahābāho tāṃ kāntām ānayāmi te</li>
    </ul>
    <ul id="R_4.006.007">
        <li id="R_4.006.007ab">anumānāt tu jānāmi maithilī sā na saṃśayaḥ</li>
        <li id="R_4.006.007cd">hriyamāṇā mayā dṛṣṭā rakṣasā krūrakarmaṇā</li>
    </ul>
    <ul id="R_4.006.008">
        <li id="R_4.006.008ab">krośantī rāma rāmeti lakṣmaṇeti ca visvaram</li>
        <li id="R_4.006.008cd">sphurantī rāvaṇasyāṅke pannagendravadhūr yathā</li>
    </ul>
    <ul id="R_4.006.009">
        <li id="R_4.006.009ab">ātmanā pañcamaṃ māṃ hi dṛṣṭvā śailataṭe sthitam</li>
        <li id="R_4.006.009cd">uttarīyaṃ tayā tyaktaṃ śubhāny ābharaṇāni ca</li>
    </ul>
    <ul id="R_4.006.010">
        <li id="R_4.006.010ab">tāny asmābhir gṛhītāni nihitāni ca rāghava</li>
        <li id="R_4.006.010cd">ānayiṣyāmy ahaṃ tāni pratyabhijñātum arhasi</li>
    </ul>
    <ul id="R_4.006.011">
        <li id="R_4.006.011ab">tam abravīt tato rāmaḥ sugrīvaṃ priyavādinam</li>
        <li id="R_4.006.011cd">ānayasva sakhe śīghraṃ kimarthaṃ pravilambase</li>
    </ul>
    <ul id="R_4.006.012">
        <li id="R_4.006.012ab">evam uktas tu sugrīvaḥ śailasya gahanāṃ guhām</li>
        <li id="R_4.006.012cd">praviveśa tataḥ śīghraṃ rāghavapriyakāmyayā</li>
    </ul>
    <ul id="R_4.006.013">
        <li id="R_4.006.013ab">uttarīyaṃ gṛhītvā tu śubhāny ābharaṇāni ca</li>
        <li id="R_4.006.013cd">idaṃ paśyeti rāmāya darśayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_4.006.014">
        <li id="R_4.006.014ab">tato gṛhītvā tadvāsaḥ śubhāny ābharaṇāni ca</li>
        <li id="R_4.006.014cd">abhavad bāṣpasaṃruddho nīhāreṇeva candramāḥ</li>
    </ul>
    <ul id="R_4.006.015">
        <li id="R_4.006.015ab">sītāsnehapravṛttena sa tu bāṣpeṇa dūṣitaḥ</li>
        <li id="R_4.006.015cd">hā priyeti rudan dhairyam utsṛjya nyapatat kṣitau</li>
    </ul>
    <ul id="R_4.006.016">
        <li id="R_4.006.016ab">hṛdi kṛtvā sa bahuśas tam alaṃkāram uttamam</li>
        <li id="R_4.006.016cd">niśaśvāsa bhṛśaṃ sarpo bilastha iva roṣitaḥ</li>
    </ul>
    <ul id="R_4.006.017">
        <li id="R_4.006.017ab">avicchinnāśruvegas tu saumitriṃ vīkṣya pārśvataḥ</li>
        <li id="R_4.006.017cd">paridevayituṃ dīnaṃ rāmaḥ samupacakrame</li>
    </ul>
    <ul id="R_4.006.018">
        <li id="R_4.006.018ab">paśya lakṣmaṇa vaidehyā saṃtyaktaṃ hriyamāṇayā</li>
        <li id="R_4.006.018cd">uttarīyam idaṃ bhūmau śarīrād bhūṣaṇāni ca</li>
    </ul>
    <ul id="R_4.006.019">
        <li id="R_4.006.019ab">śādvalinyāṃ dhruvaṃ bhūmyāṃ sītayā hriyamāṇayā</li>
        <li id="R_4.006.019cd">utsṛṣṭaṃ bhūṣaṇam idaṃ tathārūpaṃ hi dṛśyate</li>
    </ul>
    <ul id="R_4.006.020">
        <li id="R_4.006.020ab">brūhi sugrīva kaṃ deśaṃ hriyantī lakṣitā tvayā</li>
        <li id="R_4.006.020cd">rakṣasā raudrarūpeṇa mama prāṇasamā priyā</li>
    </ul>
    <ul id="R_4.006.021">
        <li id="R_4.006.021ab">kva vā vasati tad rakṣo mahad vyasanadaṃ mama</li>
        <li id="R_4.006.021cd">yannimittam ahaṃ sarvān nāśayiṣyāmi rākṣasān</li>
    </ul>
    <ul id="R_4.006.022">
        <li id="R_4.006.022ab">haratā maithilīṃ yena māṃ ca roṣayatā bhṛśam</li>
        <li id="R_4.006.022cd">ātmano jīvitāntāya mṛtyudvāram apāvṛtam</li>
    </ul>
    <ul id="R_4.006.023">
        <li id="R_4.006.023ab">
            <seg type="pāda" n="a">mama dayitatamā hṛtā vanād</seg>
            <seg type="pāda" n="b">rajanicareṇa vimathya yena sā</seg>
        </li>
        <li id="R_4.006.023cd">
            <seg type="pāda" n="c">kathaya mama ripuṃ tam adya vai</seg>
            <seg type="pāda" n="d">pravagapate yamasaṃnidhiṃ nayāmi</seg>
        </li>
    </ul>
    <ul id="R_4.007.001">
        <li id="R_4.007.001ab">evam uktas tu sugrīvo rāmeṇārtena vānaraḥ</li>
        <li id="R_4.007.001cd">abravīt prāñjalir vākyaṃ sabāṣpaṃ bāṣpagadgadaḥ</li>
    </ul>
    <ul id="R_4.007.002">
        <li id="R_4.007.002ab">na jāne nilayaṃ tasya sarvathā pāparakṣasaḥ</li>
        <li id="R_4.007.002cd">sāmarthyaṃ vikramaṃ vāpi dauṣkuleyasya vā kulam</li>
    </ul>
    <ul id="R_4.007.003">
        <li id="R_4.007.003ab">satyaṃ tu pratijānāmi tyaja śokam ariṃdama</li>
        <li id="R_4.007.003cd">kariṣyāmi tathā yatnaṃ yathā prāpsyasi maithilīm</li>
    </ul>
    <ul id="R_4.007.004">
        <li id="R_4.007.004ab">rāvaṇaṃ sagaṇaṃ hatvā paritoṣyātmapauruṣam</li>
        <li id="R_4.007.004cd">tathāsmi kartā nacirād yathā prīto bhaviṣyasi</li>
    </ul>
    <ul id="R_4.007.005">
        <li id="R_4.007.005ab">alaṃ vaiklavyam ālambya dhairyam ātmagataṃ smara</li>
        <li id="R_4.007.005cd">tvadvidhānāṃ na sadṛśam īdṛśaṃ buddhilāghavam</li>
    </ul>
    <ul id="R_4.007.006">
        <li id="R_4.007.006ab">mayāpi vyasanaṃ prāptaṃ bhāryā haraṇajaṃ mahat</li>
        <li id="R_4.007.006cd">na cāham evaṃ śocāmi na ca dhairyaṃ parityaje</li>
    </ul>
    <ul id="R_4.007.007">
        <li id="R_4.007.007ab">nāhaṃ tām anuśocāmi prākṛto vānaro 'pi san</li>
        <li id="R_4.007.007cd">mahātmā ca vinītaś cā kiṃ punar dhṛtimān bhavān</li>
    </ul>
    <ul id="R_4.007.008">
        <li id="R_4.007.008ab">bāṣpam āpatitaṃ dhairyān nigrahītuṃ tvam arhasi</li>
        <li id="R_4.007.008cd">maryādāṃ sattvayuktānāṃ dhṛtiṃ notsraṣṭum arhasi</li>
    </ul>
    <ul id="R_4.007.009">
        <li id="R_4.007.009ab">vyasane vārtha kṛcchre vā bhaye vā jīvitāntage</li>
        <li id="R_4.007.009cd">vimṛśan vai svayā buddhyā dhṛtimān nāvasīdati</li>
    </ul>
    <ul id="R_4.007.010">
        <li id="R_4.007.010ab">bāliśas tu naro nityaṃ vaiklavyaṃ yo 'nuvartate</li>
        <li id="R_4.007.010cd">sa majjaty avaśaḥ śoke bhārākrānteva naur jale</li>
    </ul>
    <ul id="R_4.007.011">
        <li id="R_4.007.011ab">eṣo 'ñjalir mayā baddhaḥ praṇayāt tvāṃ prasādaye</li>
        <li id="R_4.007.011cd">pauruṣaṃ śraya śokasya nāntaraṃ dātum arhasi</li>
    </ul>
    <ul id="R_4.007.012">
        <li id="R_4.007.012ab">ye śokam anuvartante na teṣāṃ vidyate sukham</li>
        <li id="R_4.007.012cd">tejaś ca kṣīyate teṣāṃ na tvaṃ śocitum arhasi</li>
    </ul>
    <ul id="R_4.007.013">
        <li id="R_4.007.013ab">hitaṃ vayasya bhāvena brūhi nopadiśāmi te</li>
        <li id="R_4.007.013cd">vayasyatāṃ pūjayan me na tvaṃ śocitum arhasi</li>
    </ul>
    <ul id="R_4.007.014">
        <li id="R_4.007.014ab">madhuraṃ sāntvitas tena sugrīveṇa sa rāghavaḥ</li>
        <li id="R_4.007.014cd">mukham aśrupariklinnaṃ vastrāntena pramārjayat</li>
    </ul>
    <ul id="R_4.007.015">
        <li id="R_4.007.015ab">prakṛtiṣṭhas tu kākutsthaḥ sugrīvavacanāt prabhuḥ</li>
        <li id="R_4.007.015cd">saṃpariṣvajya sugrīvam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.007.016">
        <li id="R_4.007.016ab">kartavyaṃ yad vayasyena snigdhena ca hitena ca</li>
        <li id="R_4.007.016cd">anurūpaṃ ca yuktaṃ ca kṛtaṃ sugrīva tat tvayā</li>
    </ul>
    <ul id="R_4.007.017">
        <li id="R_4.007.017ab">eṣa ca prakṛtiṣṭho 'ham anunītas tvayā sakhe</li>
        <li id="R_4.007.017cd">durlabho hīdṛśo bandhur asmin kāle viśeṣataḥ</li>
    </ul>
    <ul id="R_4.007.018">
        <li id="R_4.007.018ab">kiṃ tu yatnas tvayā kāryo maithilyāḥ parimārgaṇe</li>
        <li id="R_4.007.018cd">rākṣasasya ca raudrasya rāvaṇasya durātmanaḥ</li>
    </ul>
    <ul id="R_4.007.019">
        <li id="R_4.007.019ab">mayā ca yad anuṣṭheyaṃ visrabdhena tad ucyatām</li>
        <li id="R_4.007.019cd">varṣāsv iva ca sukṣetre sarvaṃ saṃpadyate tava</li>
    </ul>
    <ul id="R_4.007.020">
        <li id="R_4.007.020ab">mayā ca yad idaṃ vākyam abhimānāt samīritam</li>
        <li id="R_4.007.020cd">tat tvayā hariśārdūla tattvam ity upadhāryatām</li>
    </ul>
    <ul id="R_4.007.021">
        <li id="R_4.007.021ab">anṛtaṃ noktapūrvaṃ me na ca vakṣye kadā cana</li>
        <li id="R_4.007.021cd">etat te pratijānāmi satyenaiva śapāmi te</li>
    </ul>
    <ul id="R_4.007.022">
        <li id="R_4.007.022ab">tataḥ prahṛṣṭaḥ sugrīvo vānaraiḥ sacivaiḥ saha</li>
        <li id="R_4.007.022cd">rāghavasya vacaḥ śrutvā pratijñātaṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_4.007.023">
        <li id="R_4.007.023ab">
            <seg type="pāda" n="a">mahānubhāvasya vaco niśamya</seg>
            <seg type="pāda" n="b">harir narāṇām ṛṣabhasya tasya</seg>
        </li>
        <li id="R_4.007.023cd">
            <seg type="pāda" n="c">kṛtaṃ sa mene harivīra mukhyas</seg>
            <seg type="pāda" n="d">tadā svakāryaṃ hṛdayena vidvān</seg>
        </li>
    </ul>
    <ul id="R_4.008.001">
        <li id="R_4.008.001ab">parituṣṭas tu sugrīvas tena vākyena vānaraḥ</li>
        <li id="R_4.008.001cd">lakṣmaṇasyāgrajaṃ rāmam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.008.002">
        <li id="R_4.008.002ab">sarvathāham anugrāhyo devatānām asaṃśayaḥ</li>
        <li id="R_4.008.002cd">upapannaguṇopetaḥ sakhā yasya bhavān mama</li>
    </ul>
    <ul id="R_4.008.003">
        <li id="R_4.008.003ab">śakyaṃ khalu bhaved rāma sahāyena tvayānagha</li>
        <li id="R_4.008.003cd">surarājyam api prāptuṃ svarājyaṃ kiṃ punaḥ prabho</li>
    </ul>
    <ul id="R_4.008.004">
        <li id="R_4.008.004ab">so 'haṃ sabhājyo bandhūnāṃ suhṛdāṃ caiva rāghava</li>
        <li id="R_4.008.004cd">yasyāgnisākṣikaṃ mitraṃ labdhaṃ rāghavavaṃśajam</li>
    </ul>
    <ul id="R_4.008.005">
        <li id="R_4.008.005ab">aham apy anurūpas te vayasyo jñāsyase śanaiḥ</li>
        <li id="R_4.008.005cd">na tu vaktuṃ samartho 'haṃ svayam ātmagatān guṇān</li>
    </ul>
    <ul id="R_4.008.006">
        <li id="R_4.008.006ab">mahātmanāṃ tu bhūyiṣṭhaṃ tvadvidhānāṃ kṛtātmanām</li>
        <li id="R_4.008.006cd">niścalā bhavati prītir dhairyam ātmavatām iva</li>
    </ul>
    <ul id="R_4.008.007">
        <li id="R_4.008.007ab">rajataṃ vā suvarṇaṃ vā vastrāṇy ābharaṇāni vā</li>
        <li id="R_4.008.007cd">avibhaktāni sādhūnām avagacchanti sādhavaḥ</li>
    </ul>
    <ul id="R_4.008.008">
        <li id="R_4.008.008ab">āḍhyo vāpi daridro vā duḥkhitaḥ sukhito 'pi vā</li>
        <li id="R_4.008.008cd">nirdoṣo vā sadoṣo vā vayasyaḥ paramā gatiḥ</li>
    </ul>
    <ul id="R_4.008.009">
        <li id="R_4.008.009ab">dhanatyāgaḥ sukhatyāgo dehatyāgo 'pi vā punaḥ</li>
        <li id="R_4.008.009cd">vayasyārthe pravartante snehaṃ dṛṣṭvā tathāvidham</li>
    </ul>
    <ul id="R_4.008.010">
        <li id="R_4.008.010ab">tat tathety abravīd rāmaḥ sugrīvaṃ priyavādinam</li>
        <li id="R_4.008.010cd">lakṣmaṇasyāgrato lakṣmyā vāsavasyeva dhīmataḥ</li>
    </ul>
    <ul id="R_4.008.011">
        <li id="R_4.008.011ab">tato rāmaṃ sthitaṃ dṛṣṭvā lakṣmaṇaṃ ca mahābalam</li>
        <li id="R_4.008.011cd">sugrīvaḥ sarvataś cakṣur vane lolam apātayat</li>
    </ul>
    <ul id="R_4.008.012">
        <li id="R_4.008.012ab">sa dadarśa tataḥ sālam avidūre harīśvaraḥ</li>
        <li id="R_4.008.012cd">supuṣpam īṣatpatrāḍhyaṃ bhramarair upaśobhitam</li>
    </ul>
    <ul id="R_4.008.013">
        <li id="R_4.008.013ab">tasyaikāṃ parṇabahulāṃ bhaṅktvā śākhāṃ supuṣpitām</li>
        <li id="R_4.008.013cd">sālasyāstīrya sugrīvo niṣasāda sarāghavaḥ</li>
    </ul>
    <ul id="R_4.008.014">
        <li id="R_4.008.014ab">tāv āsīnau tato dṛṣṭvā hanūmān api lakṣmaṇam</li>
        <li id="R_4.008.014cd">sālaśākhāṃ samutpāṭya vinītam upaveśayat</li>
    </ul>
    <ul id="R_4.008.015">
        <li id="R_4.008.015ab">tataḥ prahṛṣṭaḥ sugrīvaḥ ślakṣṇaṃ madhurayā girā</li>
        <li id="R_4.008.015cd">uvāca praṇayād rāmaṃ harṣavyākulitākṣaram</li>
    </ul>
    <ul id="R_4.008.016">
        <li id="R_4.008.016ab">ahaṃ vinikṛto bhrātrā carāmy eṣa bhayārditaḥ</li>
        <li id="R_4.008.016cd">ṛśyamūkaṃ girivaraṃ hṛtabhāryaḥ suduḥkhitaḥ</li>
    </ul>
    <ul id="R_4.008.017">
        <li id="R_4.008.017ab">so 'haṃ trasto bhaye magno vasāmy udbhrāntacetanaḥ</li>
        <li id="R_4.008.017cd">vālinā nikṛto bhrātrā kṛtavairaś ca rāghava</li>
    </ul>
    <ul id="R_4.008.018">
        <li id="R_4.008.018ab">vālino me bhayārtasya sarvalokābhayaṃkara</li>
        <li id="R_4.008.018cd">mamāpi tvam anāthasya prasādaṃ kartum arhasi</li>
    </ul>
    <ul id="R_4.008.019">
        <li id="R_4.008.019ab">evam uktas tu tejasvī dharmajño dharmavatsalaḥ</li>
        <li id="R_4.008.019cd">pratyuvāca sa kākutsthaḥ sugrīvaṃ prahasann iva</li>
    </ul>
    <ul id="R_4.008.020">
        <li id="R_4.008.020ab">upakāraphalaṃ mitram apakāro 'rilakṣaṇam</li>
        <li id="R_4.008.020cd">adyaiva taṃ haniṣyāmi tava bhāryāpahāriṇam</li>
    </ul>
    <ul id="R_4.008.021">
        <li id="R_4.008.021ab">ime hi me mahāvegāḥ patriṇas tigmatejasaḥ</li>
        <li id="R_4.008.021cd">kārtikeyavanodbhūtāḥ śarā hemavibhūṣitāḥ</li>
    </ul>
    <ul id="R_4.008.022">
        <li id="R_4.008.022ab">kaṅkapatrapraticchannā mahendrāśanisaṃnibhāḥ</li>
        <li id="R_4.008.022cd">suparvāṇaḥ sutīkṣṇāgrā saroṣā bhujagā iva</li>
    </ul>
    <ul id="R_4.008.023">
        <li id="R_4.008.023ab">bhrātṛsaṃjñam amitraṃ te vālinaṃ kṛtakilbiṣam</li>
        <li id="R_4.008.023cd">śarair vinihataṃ paśya vikīrṇam iva parvatam</li>
    </ul>
    <ul id="R_4.008.024">
        <li id="R_4.008.024ab">rāghavasya vacaḥ śrutvā sugrīvo vāhinīpatiḥ</li>
        <li id="R_4.008.024cd">praharṣam atulaṃ lebhe sādhu sādhv iti cābravīt</li>
    </ul>
    <ul id="R_4.008.025">
        <li id="R_4.008.025ab">rāmaśokābhibhūto 'haṃ śokārtānāṃ bhavān gatiḥ</li>
        <li id="R_4.008.025cd">vayasya iti kṛtvā hi tvayy ahaṃ paridevaye</li>
    </ul>
    <ul id="R_4.008.026">
        <li id="R_4.008.026ab">tvaṃ hi pāṇipradānena vayasyo so 'gnisākṣikaḥ</li>
        <li id="R_4.008.026cd">kṛtaḥ prāṇair bahumataḥ satyenāpi śapāmy aham</li>
    </ul>
    <ul id="R_4.008.027">
        <li id="R_4.008.027ab">vayasya iti kṛtvā ca visrabdhaṃ pravadāmy aham</li>
        <li id="R_4.008.027cd">duḥkham antargataṃ yan me mano dahati nityaśaḥ</li>
    </ul>
    <ul id="R_4.008.028">
        <li id="R_4.008.028ab">etāvad uktvā vacanaṃ bāṣpadūṣitalocanaḥ</li>
        <li id="R_4.008.028cd">bāṣpopahatayā vācā noccaiḥ śaknoti bhāṣitum</li>
    </ul>
    <ul id="R_4.008.029">
        <li id="R_4.008.029ab">bāṣpavegaṃ tu sahasā nadīvegam ivāgatam</li>
        <li id="R_4.008.029cd">dhārayām āsa dhairyeṇa sugrīvo rāmasaṃnidhau</li>
    </ul>
    <ul id="R_4.008.030">
        <li id="R_4.008.030ab">saṃnigṛhya tu taṃ bāṣpaṃ pramṛjya nayane śubhe</li>
        <li id="R_4.008.030cd">viniḥśvasya ca tejasvī rāghavaṃ punar abravīt</li>
    </ul>
    <ul id="R_4.008.031">
        <li id="R_4.008.031ab">purāhaṃ valinā rāma rājyāt svād avaropitaḥ</li>
        <li id="R_4.008.031cd">paruṣāṇi ca saṃśrāvya nirdhūto 'smi balīyasā</li>
    </ul>
    <ul id="R_4.008.032">
        <li id="R_4.008.032ab">hṛtā bhāryā ca me tena prāṇebhyo 'pi garīyasī</li>
        <li id="R_4.008.032cd">suhṛdaś ca madīyā ye saṃyatā bandhaneṣu te</li>
    </ul>
    <ul id="R_4.008.033">
        <li id="R_4.008.033ab">yatnavāṃś ca suduṣṭātmā mad vināśāya rāghava</li>
        <li id="R_4.008.033cd">bahuśas tat prayuktāś ca vānarā nihatā mayā</li>
    </ul>
    <ul id="R_4.008.034">
        <li id="R_4.008.034ab">śaṅkayā tv etayā cāhaṃ dṛṣṭvā tvām api rāghava</li>
        <li id="R_4.008.034cd">nopasarpāmy ahaṃ bhīto bhaye sarve hi bibhyati</li>
    </ul>
    <ul id="R_4.008.035">
        <li id="R_4.008.035ab">kevalaṃ hi sahāyā me hanumat pramukhās tv ime</li>
        <li id="R_4.008.035cd">ato 'haṃ dhārayāmy adya prāṇān kṛcchra gato 'pi san</li>
    </ul>
    <ul id="R_4.008.036">
        <li id="R_4.008.036ab">ete hi kapayaḥ snigdhā māṃ rakṣanti samantataḥ</li>
        <li id="R_4.008.036cd">saha gacchanti gantavye nityaṃ tiṣṭhanti ca sthite</li>
    </ul>
    <ul id="R_4.008.037">
        <li id="R_4.008.037ab">saṃkṣepas tv eṣa me rāma kim uktvā vistaraṃ hi te</li>
        <li id="R_4.008.037cd">sa me jyeṣṭho ripur bhrātā vālī viśrutapauruṣaḥ</li>
    </ul>
    <ul id="R_4.008.038">
        <li id="R_4.008.038ab">tadvināśād dhi me duḥkhaṃ pranaṣṭaṃ syād anantaram</li>
        <li id="R_4.008.038cd">sukhaṃ me jīvitaṃ caiva tadvināśanibandhanam</li>
    </ul>
    <ul id="R_4.008.039">
        <li id="R_4.008.039ab">eṣa me rāma śokāntaḥ śokārtena niveditaḥ</li>
        <li id="R_4.008.039cd">duḥkhito 'duḥkhito vāpi sakhyur nityaṃ sakhā gatiḥ</li>
    </ul>
    <ul id="R_4.008.040">
        <li id="R_4.008.040ab">śrutvaitac ca vaco rāmaḥ sugrīvam idam abravīt</li>
        <li id="R_4.008.040cd">kiṃnimittam abhūd vairaṃ śrotum icchāmi tattvataḥ</li>
    </ul>
    <ul id="R_4.008.041">
        <li id="R_4.008.041ab">sukhaṃ hi kāraṇaṃ śrutvā vairasya tava vānara</li>
        <li id="R_4.008.041cd">ānantaryaṃ vidhāsyāmi saṃpradhārya balābalam</li>
    </ul>
    <ul id="R_4.008.042">
        <li id="R_4.008.042ab">balavān hi mamāmarṣaḥ śrutvā tvām avamānitam</li>
        <li id="R_4.008.042cd">vardhate hṛdayotkampī prāvṛḍvega ivāmbhasaḥ</li>
    </ul>
    <ul id="R_4.008.043">
        <li id="R_4.008.043ab">hṛṣṭaḥ kathaya visrabdho yāvad āropyate dhanuḥ</li>
        <li id="R_4.008.043cd">sṛṣṭaś ca hi mayā bāṇo nirastaś ca ripus tava</li>
    </ul>
    <ul id="R_4.008.044">
        <li id="R_4.008.044ab">evam uktas tu sugrīvaḥ kākutsthena mahātmanā</li>
        <li id="R_4.008.044cd">praharṣam atulaṃ lebhe caturbhiḥ saha vānaraiḥ</li>
    </ul>
    <ul id="R_4.008.045">
        <li id="R_4.008.045ab">tataḥ prahṛṣṭavadanaḥ sugrīvo lakṣmaṇāgraje</li>
        <li id="R_4.008.045cd">vairasya kāraṇaṃ tattvam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_4.009.001">
        <li id="R_4.009.001ab">vālī nāma mama bhrātā jyeṣṭhaḥ śatruniṣūdanaḥ</li>
        <li id="R_4.009.001cd">pitur bahumato nityaṃ mama cāpi tathā purā</li>
    </ul>
    <ul id="R_4.009.002">
        <li id="R_4.009.002ab">pitary uparate 'smākaṃ jyeṣṭho 'yam iti mantribhiḥ</li>
        <li id="R_4.009.002cd">kapīnām īśvaro rājye kṛtaḥ paramasaṃmataḥ</li>
    </ul>
    <ul id="R_4.009.003">
        <li id="R_4.009.003ab">rājyaṃ praśāsatas tasya pitṛpaitāmahaṃ mahat</li>
        <li id="R_4.009.003cd">ahaṃ sarveṣu kāleṣu praṇataḥ preṣyavat sthitaḥ</li>
    </ul>
    <ul id="R_4.009.004">
        <li id="R_4.009.004ab">māyāvī nāma tejasvī pūrvajo dundubheḥ sutaḥ</li>
        <li id="R_4.009.004cd">tena tasya mahad vairaṃ strīkṛtaṃ viśrutaṃ purā</li>
    </ul>
    <ul id="R_4.009.005">
        <li id="R_4.009.005ab">sa tu supte jane rātrau kiṣkindhād vāram āgataḥ</li>
        <li id="R_4.009.005cd">nardati sma susaṃrabdho vālinaṃ cāhvayad raṇe</li>
    </ul>
    <ul id="R_4.009.006">
        <li id="R_4.009.006ab">prasuptas tu mama bhrātā narditaṃ bhairavasvanam</li>
        <li id="R_4.009.006cd">śrutvā na mamṛṣe vālī niṣpapāta javāt tadā</li>
    </ul>
    <ul id="R_4.009.007">
        <li id="R_4.009.007ab">sa tu vai niḥsṛtaḥ krodhāt taṃ hantum asurottamam</li>
        <li id="R_4.009.007cd">vāryamāṇas tataḥ strībhir mayā ca praṇatātmanā</li>
    </ul>
    <ul id="R_4.009.008">
        <li id="R_4.009.008ab">sa tu nirdhūya sarvānno nirjagāma mahābalaḥ</li>
        <li id="R_4.009.008cd">tato 'ham api sauhārdān niḥsṛto vālinā saha</li>
    </ul>
    <ul id="R_4.009.009">
        <li id="R_4.009.009ab">sa tu me bhrātaraṃ dṛṣṭvā māṃ ca dūrād avasthitam</li>
        <li id="R_4.009.009cd">asuro jātasaṃtrāsaḥ pradudrāva tadā bhṛśam</li>
    </ul>
    <ul id="R_4.009.010">
        <li id="R_4.009.010ab">tasmin dravati saṃtraste hy āvāṃ drutataraṃ gatau</li>
        <li id="R_4.009.010cd">prakāśo 'pi kṛto mārgaś candreṇodgacchatā tadā</li>
    </ul>
    <ul id="R_4.009.011">
        <li id="R_4.009.011ab">sa tṛṇair āvṛtaṃ durgaṃ dharaṇyā vivaraṃ mahat</li>
        <li id="R_4.009.011cd">praviveśāsuro vegād āvām āsādya viṣṭhitau</li>
    </ul>
    <ul id="R_4.009.012">
        <li id="R_4.009.012ab">taṃ praviṣṭaṃ ripuṃ dṛṣṭvā bilaṃ roṣavaśaṃ gataḥ</li>
        <li id="R_4.009.012cd">mām uvāca tadā vālī vacanaṃ kṣubhitendriyaḥ</li>
    </ul>
    <ul id="R_4.009.013">
        <li id="R_4.009.013ab">iha tvaṃ tiṣṭha sugrīva biladvāri samāhitaḥ</li>
        <li id="R_4.009.013cd">yāvad atra praviśyāhaṃ nihanmi samare ripum</li>
    </ul>
    <ul id="R_4.009.014">
        <li id="R_4.009.014ab">mayā tv etad vacaḥ śrutvā yācitaḥ sa paraṃtapa</li>
        <li id="R_4.009.014cd">śāpayitvā ca māṃ padbhyāṃ praviveśa bilaṃ tadā</li>
    </ul>
    <ul id="R_4.009.015">
        <li id="R_4.009.015ab">tasya praviṣṭasya bilaṃ sāgraḥ saṃvatsaro gataḥ</li>
        <li id="R_4.009.015cd">sthitasya ca mama dvāri sa kālo vyatyavartata</li>
    </ul>
    <ul id="R_4.009.016">
        <li id="R_4.009.016ab">ahaṃ tu naṣṭaṃ taṃ jñātvā snehād āgatasaṃbhramaḥ</li>
        <li id="R_4.009.016cd">bhrātaraṃ na hi paśyāmi pāpaśaṅki ca me manaḥ</li>
    </ul>
    <ul id="R_4.009.017">
        <li id="R_4.009.017ab">atha dīrghasya kālasya bilāt tasmād viniḥsṛtam</li>
        <li id="R_4.009.017cd">saphenaṃ rudhiraṃ raktam ahaṃ dṛṣṭvā suduḥkhitaḥ</li>
    </ul>
    <ul id="R_4.009.018">
        <li id="R_4.009.018ab">nardatām asurāṇāṃ ca dhvanir me śrotram āgataḥ</li>
        <li id="R_4.009.018cd">nirastasya ca saṃgrāme krośato niḥsvano guroḥ</li>
    </ul>
    <ul id="R_4.009.019">
        <li id="R_4.009.019ab">ahaṃ tv avagato buddhyā cihnais tair bhrātaraṃ hatam</li>
        <li id="R_4.009.019cd">pidhāya ca biladvāraṃ śilayā girimātrayā</li>
        <li id="R_4.009.019ef">śokārtaś codakaṃ kṛtvā kiṣkindhām āgataḥ sakhe</li>
    </ul>
    <ul id="R_4.009.020">
        <li id="R_4.009.020ab">gūhamānasya me tattvaṃ yatnato mantribhiḥ śrutam</li>
        <li id="R_4.009.020cd">tato 'haṃ taiḥ samāgamya sametair abhiṣecitaḥ</li>
    </ul>
    <ul id="R_4.009.021">
        <li id="R_4.009.021ab">rājyaṃ praśāsatas tasya nyāyato mama rāghava</li>
        <li id="R_4.009.021cd">ājagāma ripuṃ hatvā vālī tam asurottamam</li>
    </ul>
    <ul id="R_4.009.022">
        <li id="R_4.009.022ab">abhiṣiktaṃ tu māṃ dṛṣṭvā krodhāt saṃraktalocanaḥ</li>
        <li id="R_4.009.022cd">madīyān mantriṇo baddhvā paruṣaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_4.009.023">
        <li id="R_4.009.023ab">nigrahe 'pi samarthasya taṃ pāpaṃ prati rāghava</li>
        <li id="R_4.009.023cd">na prāvartata me buddhir bhrātṛgauravayantritā</li>
    </ul>
    <ul id="R_4.009.024">
        <li id="R_4.009.024ab">mānayaṃs taṃ mahātmānaṃ yathāvac cābhyavādayam</li>
        <li id="R_4.009.024cd">uktāś ca nāśiṣas tena saṃtuṣṭenāntarātmanā</li>
    </ul>
    <ul id="R_4.010.001">
        <li id="R_4.010.001ab">tataḥ krodhasamāviṣṭaṃ saṃrabdhaṃ tam upāgatam</li>
        <li id="R_4.010.001cd">ahaṃ prasādayāṃ cakre bhrātaraṃ priyakāmyayā</li>
    </ul>
    <ul id="R_4.010.002">
        <li id="R_4.010.002ab">diṣṭyāsi kuśalī prāpto nihataś ca tvayā ripuḥ</li>
        <li id="R_4.010.002cd">anāthasya hi me nāthas tvam eko 'nāthanandanaḥ</li>
    </ul>
    <ul id="R_4.010.003">
        <li id="R_4.010.003ab">idaṃ bahuśalākaṃ te pūrṇacandram ivoditam</li>
        <li id="R_4.010.003cd">chatraṃ savālavyajanaṃ pratīcchasva mayodyatam</li>
    </ul>
    <ul id="R_4.010.004">
        <li id="R_4.010.004ab">tvam eva rājā mānārhaḥ sadā cāhaṃ yathāpurā</li>
        <li id="R_4.010.004cd">nyāsabhūtam idaṃ rājyaṃ tava niryātayāmy aham</li>
    </ul>
    <ul id="R_4.010.005">
        <li id="R_4.010.005ab">mā ca roṣaṃ kṛthāḥ saumya mayi śatrunibarhaṇa</li>
        <li id="R_4.010.005cd">yāce tvāṃ śirasā rājan mayā baddho 'yam añjaliḥ</li>
    </ul>
    <ul id="R_4.010.006">
        <li id="R_4.010.006ab">balād asmi samāgamya mantribhiḥ puravāsibhiḥ</li>
        <li id="R_4.010.006cd">rājabhāve niyukto 'haṃ śūnyadeśajigīṣayā</li>
    </ul>
    <ul id="R_4.010.007">
        <li id="R_4.010.007ab">snigdham evaṃ bruvāṇaṃ māṃ sa tu nirbhartsya vānaraḥ</li>
        <li id="R_4.010.007cd">dhik tvām iti ca mām uktvā bahu tat tad uvāca ha</li>
    </ul>
    <ul id="R_4.010.008">
        <li id="R_4.010.008ab">prakṛtīś ca samānīya mantriṇaś caiva saṃmatān</li>
        <li id="R_4.010.008cd">mām āha suhṛdāṃ madhye vākyaṃ paramagarhitam</li>
    </ul>
    <ul id="R_4.010.009">
        <li id="R_4.010.009ab">viditaṃ vo yathā rātrau māyāvī sa mahāsuraḥ</li>
        <li id="R_4.010.009cd">māṃ samāhvayata krūro yuddhākāṅkṣī sudurmatiḥ</li>
    </ul>
    <ul id="R_4.010.010">
        <li id="R_4.010.010ab">tasya tad garjitaṃ śrutvā niḥsṛto 'haṃ nṛpālayāt</li>
        <li id="R_4.010.010cd">anuyātaś ca māṃ tūrṇam ayaṃ bhrātā sudāruṇaḥ</li>
    </ul>
    <ul id="R_4.010.011">
        <li id="R_4.010.011ab">sa tu dṛṣṭvaiva māṃ rātrau sadvitīyaṃ mahābalaḥ</li>
        <li id="R_4.010.011cd">prādravad bhayasaṃtrasto vīkṣyāvāṃ tam anudrutau</li>
        <li id="R_4.010.011ef">anudrutas tu vegena praviveśa mahābilam</li>
    </ul>
    <ul id="R_4.010.012">
        <li id="R_4.010.012ab">taṃ praviṣṭaṃ viditvā tu sughoraṃ sumahad bilam</li>
        <li id="R_4.010.012cd">ayam ukto 'tha me bhrātā mayā tu krūradarśanaḥ</li>
    </ul>
    <ul id="R_4.010.013">
        <li id="R_4.010.013ab">ahatvā nāsti me śaktiḥ pratigantum itaḥ purīm</li>
        <li id="R_4.010.013cd">biladvāri pratīkṣa tvaṃ yāvad enaṃ nihanmy aham</li>
    </ul>
    <ul id="R_4.010.014">
        <li id="R_4.010.014ab">sthito 'yam iti matvā tu praviṣṭo 'haṃ durāsadam</li>
        <li id="R_4.010.014cd">taṃ ca me mārgamāṇasya gataḥ saṃvatsaras tadā</li>
    </ul>
    <ul id="R_4.010.015">
        <li id="R_4.010.015ab">sa tu dṛṣṭo mayā śatrur anirvedād bhayāvahaḥ</li>
        <li id="R_4.010.015cd">nihataś ca mayā tatra so 'suro bandhubhiḥ saha</li>
    </ul>
    <ul id="R_4.010.016">
        <li id="R_4.010.016ab">tasyāsyāt tu pravṛttena rudhiraugheṇa tad bilam</li>
        <li id="R_4.010.016cd">pūrṇam āsīd durākrāmaṃ stanatas tasya bhūtale</li>
    </ul>
    <ul id="R_4.010.017">
        <li id="R_4.010.017ab">sūdayitvā tu taṃ śatruṃ vikrāntaṃ dundubheḥ sutam</li>
        <li id="R_4.010.017cd">niṣkrāmann eva paśyāmi bilasya pihitaṃ mukham</li>
    </ul>
    <ul id="R_4.010.018">
        <li id="R_4.010.018ab">vikrośamānasya tu me sugrīveti punaḥ punaḥ</li>
        <li id="R_4.010.018cd">yadā prativaco nāsti tato 'haṃ bhṛśaduḥkhitaḥ</li>
    </ul>
    <ul id="R_4.010.019">
        <li id="R_4.010.019ab">pādaprahārais tu mayā bahuśas tad vidāritam</li>
        <li id="R_4.010.019cd">tato 'haṃ tena niṣkramya yathā punar upāgataḥ</li>
    </ul>
    <ul id="R_4.010.020">
        <li id="R_4.010.020ab">tatrānenāsmi saṃruddho rājyaṃ mārgayatātmanaḥ</li>
        <li id="R_4.010.020cd">sugrīveṇa nṛśaṃsena vismṛtya bhrātṛsauhṛdam</li>
    </ul>
    <ul id="R_4.010.021">
        <li id="R_4.010.021ab">evam uktvā tu māṃ tatra vastreṇaikena vānaraḥ</li>
        <li id="R_4.010.021cd">tadā nirvāsayām āsa vālī vigatasādhvasaḥ</li>
    </ul>
    <ul id="R_4.010.022">
        <li id="R_4.010.022ab">tenāham apaviddhaś ca hṛtadāraś ca rāghava</li>
        <li id="R_4.010.022cd">tadbhayāc ca mahīkṛtsnā krānteyaṃ savanārṇavā</li>
    </ul>
    <ul id="R_4.010.023">
        <li id="R_4.010.023ab">ṛśyamūkaṃ girivaraṃ bhāryāharaṇaduḥkhitaḥ</li>
        <li id="R_4.010.023cd">praviṣṭo 'smi durādharṣaṃ vālinaḥ kāraṇāntare</li>
    </ul>
    <ul id="R_4.010.024">
        <li id="R_4.010.024ab">etat te sarvam ākhyātaṃ vairānukathanaṃ mahat</li>
        <li id="R_4.010.024cd">anāgasā mayā prāptaṃ vyasanaṃ paśya rāghava</li>
    </ul>
    <ul id="R_4.010.025">
        <li id="R_4.010.025ab">vālinas tu bhayārtasya sarvalokābhayaṃkara</li>
        <li id="R_4.010.025cd">kartum arhasi me vīra prasādaṃ tasya nigrahāt</li>
    </ul>
    <ul id="R_4.010.026">
        <li id="R_4.010.026ab">evam uktaḥ sa tejasvī dharmajño dharmasaṃhitam</li>
        <li id="R_4.010.026cd">vacanaṃ vaktum ārebhe sugrīvaṃ prahasann iva</li>
    </ul>
    <ul id="R_4.010.027">
        <li id="R_4.010.027ab">amoghāḥ sūryasaṃkāśā mameme niśitāḥ śarāḥ</li>
        <li id="R_4.010.027cd">tasmin vālini durvṛtte patiṣyanti ruṣānvitāḥ</li>
    </ul>
    <ul id="R_4.010.028">
        <li id="R_4.010.028ab">yāvat taṃ na hi paśyeyaṃ tava bhāryāpahāriṇam</li>
        <li id="R_4.010.028cd">tāvat sa jīvet pāpātmā vālī cāritradūṣakaḥ</li>
    </ul>
    <ul id="R_4.010.029">
        <li id="R_4.010.029ab">ātmānumānāt paśyāmi magnaṃ tvāṃ śokasāgare</li>
        <li id="R_4.010.029cd">tvām ahaṃ tārayiṣyāmi kāmaṃ prāpsyasi puṣkalam</li>
    </ul>
    <ul id="R_4.011.001">
        <li id="R_4.011.001ab">rāmasya vacanaṃ śrutvā harṣapauruṣavardhanam</li>
        <li id="R_4.011.001cd">sugrīvaḥ pūjayāṃ cakre rāghavaṃ praśaśaṃsa ca</li>
    </ul>
    <ul id="R_4.011.002">
        <li id="R_4.011.002ab">asaṃśayaṃ prajvalitais tīkṣṇair marmātigaiḥ śaraiḥ</li>
        <li id="R_4.011.002cd">tvaṃ daheḥ kupito lokān yugānta iva bhāskaraḥ</li>
    </ul>
    <ul id="R_4.011.003">
        <li id="R_4.011.003ab">vālinaḥ pauruṣaṃ yat tad yac ca vīryaṃ dhṛtiś ca yā</li>
        <li id="R_4.011.003cd">tan mamaikamanāḥ śrutvā vidhatsva yadanantaram</li>
    </ul>
    <ul id="R_4.011.004">
        <li id="R_4.011.004ab">samudrāt paścimāt pūrvaṃ dakṣiṇād api cottaram</li>
        <li id="R_4.011.004cd">krāmaty anudite sūrye vālī vyapagataklamaḥ</li>
    </ul>
    <ul id="R_4.011.005">
        <li id="R_4.011.005ab">agrāṇy āruhya śailānāṃ śikharāṇi mahānty api</li>
        <li id="R_4.011.005cd">ūrdhvam utkṣipya tarasā pratigṛhṇāti vīryavān</li>
    </ul>
    <ul id="R_4.011.006">
        <li id="R_4.011.006ab">bahavaḥ sāravantaś ca vaneṣu vividhā drumāḥ</li>
        <li id="R_4.011.006cd">vālinā tarasā bhagnā balaṃ prathayatātmanaḥ</li>
    </ul>
    <ul id="R_4.011.007">
        <li id="R_4.011.007ab">mahiṣo dundubhir nāma kailāsaśikharaprabhaḥ</li>
        <li id="R_4.011.007cd">balaṃ nāgasahasrasya dhārayām āsa vīryavān</li>
    </ul>
    <ul id="R_4.011.008">
        <li id="R_4.011.008ab">vīryotsekena duṣṭātmā varadānāc ca mohitaḥ</li>
        <li id="R_4.011.008cd">jagāma sa mahākāyaḥ samudraṃ saritāṃ patim</li>
    </ul>
    <ul id="R_4.011.009">
        <li id="R_4.011.009ab">ūrmimantam atikramya sāgaraṃ ratnasaṃcayam</li>
        <li id="R_4.011.009cd">mama yuddhaṃ prayaccheti tam uvāca mahārṇavam</li>
    </ul>
    <ul id="R_4.011.010">
        <li id="R_4.011.010ab">tataḥ samudro dharmātmā samutthāya mahābalaḥ</li>
        <li id="R_4.011.010cd">abravīd vacanaṃ rājann asuraṃ kālacoditam</li>
    </ul>
    <ul id="R_4.011.011">
        <li id="R_4.011.011ab">samartho nāsmi te dātuṃ yuddhaṃ yuddhaviśārada</li>
        <li id="R_4.011.011cd">śrūyatām abhidhāsyāmi yas te yuddhaṃ pradāsyati</li>
    </ul>
    <ul id="R_4.011.012">
        <li id="R_4.011.012ab">śailarājo mahāraṇye tapasviśaraṇaṃ param</li>
        <li id="R_4.011.012cd">śaṃkaraśvaśuro nāmnā himavān iti viśrutaḥ</li>
    </ul>
    <ul id="R_4.011.013">
        <li id="R_4.011.013ab">guhā prasravaṇopeto bahukandaranirjharaḥ</li>
        <li id="R_4.011.013cd">sa samarthas tava prītim atulāṃ kartum āhave</li>
    </ul>
    <ul id="R_4.011.014">
        <li id="R_4.011.014ab">taṃ bhītam iti vijñāya samudram asurottamaḥ</li>
        <li id="R_4.011.014cd">himavadvanam āgacchac charaś cāpād iva cyutaḥ</li>
    </ul>
    <ul id="R_4.011.015">
        <li id="R_4.011.015ab">tatas tasya gireḥ śvetā gajendravipulāḥ śilāḥ</li>
        <li id="R_4.011.015cd">cikṣepa bahudhā bhūmau dundubhir vinanāda ca</li>
    </ul>
    <ul id="R_4.011.016">
        <li id="R_4.011.016ab">tataḥ śvetāmbudākāraḥ saumyaḥ prītikarākṛtiḥ</li>
        <li id="R_4.011.016cd">himavān abravīd vākyaṃ sva eva śikhare sthitaḥ</li>
    </ul>
    <ul id="R_4.011.017">
        <li id="R_4.011.017ab">kleṣṭum arhasi māṃ na tvaṃ dundubhe dharmavatsala</li>
        <li id="R_4.011.017cd">raṇakarmasv akuśalas tapasviśaraṇaṃ hy aham</li>
    </ul>
    <ul id="R_4.011.018">
        <li id="R_4.011.018ab">tasya tad vacanaṃ śrutvā girirājasya dhīmataḥ</li>
        <li id="R_4.011.018cd">uvāca dundubhir vākyaṃ krodhāt saṃraktalocanaḥ</li>
    </ul>
    <ul id="R_4.011.019">
        <li id="R_4.011.019ab">yadi yuddhe 'samarthas tvaṃ madbhayād vā nirudyamaḥ</li>
        <li id="R_4.011.019cd">tam ācakṣva pradadyān me yo 'dya yuddhaṃ yuyutsataḥ</li>
    </ul>
    <ul id="R_4.011.020">
        <li id="R_4.011.020ab">himavān abravīd vākyaṃ śrutvā vākyaviśāradaḥ</li>
        <li id="R_4.011.020cd">anuktapūrvaṃ dharmātmā krodhāt tam asurottamam</li>
    </ul>
    <ul id="R_4.011.021">
        <li id="R_4.011.021ab">vālī nāma mahāprājñaḥ śakratulyaparākramaḥ</li>
        <li id="R_4.011.021cd">adhyāste vānaraḥ śrīmān kiṣkindhām atulaprabhām</li>
    </ul>
    <ul id="R_4.011.022">
        <li id="R_4.011.022ab">sa samartho mahāprājñas tava yuddhaviśāradaḥ</li>
        <li id="R_4.011.022cd">dvandvayuddhaṃ mahad dātuṃ namucer iva vāsavaḥ</li>
    </ul>
    <ul id="R_4.011.023">
        <li id="R_4.011.023ab">taṃ śīghram abhigaccha tvaṃ yadi yuddham ihecchasi</li>
        <li id="R_4.011.023cd">sa hi durdharṣaṇo nityaṃ śūraḥ samarakarmaṇi</li>
    </ul>
    <ul id="R_4.011.024">
        <li id="R_4.011.024ab">śrutvā himavato vākyaṃ krodhāviṣṭaḥ sa dundubhiḥ</li>
        <li id="R_4.011.024cd">jagāma tāṃ purīṃ tasya kiṣkindhāṃ vālinas tadā</li>
    </ul>
    <ul id="R_4.011.025">
        <li id="R_4.011.025ab">dhārayan māhiṣaṃ rūpaṃ tīkṣṇaśṛṅgo bhayāvahaḥ</li>
        <li id="R_4.011.025cd">prāvṛṣīva mahāmeghas toyapūrṇo nabhastale</li>
    </ul>
    <ul id="R_4.011.026">
        <li id="R_4.011.026ab">tatas tu dvāram āgamya kiṣkindhāyā mahābalaḥ</li>
        <li id="R_4.011.026cd">nanarda kampayan bhūmiṃ dundubhir dundubhir yathā</li>
    </ul>
    <ul id="R_4.011.027">
        <li id="R_4.011.027ab">samīpajān drumān bhañjan vasudhāṃ dārayan khuraiḥ</li>
        <li id="R_4.011.027cd">viṣāṇenollekhan darpāt taddvāraṃ dvirado yathā</li>
    </ul>
    <ul id="R_4.011.028">
        <li id="R_4.011.028ab">antaḥpuragato vālī śrutvā śabdam amarṣaṇaḥ</li>
        <li id="R_4.011.028cd">niṣpapāta saha strībhis tārābhir iva candramāḥ</li>
    </ul>
    <ul id="R_4.011.029">
        <li id="R_4.011.029ab">mitaṃ vyaktākṣarapadaṃ tam uvāca sa dundubhim</li>
        <li id="R_4.011.029cd">harīṇām īśvaro vālī sarveṣāṃ vanacāriṇām</li>
    </ul>
    <ul id="R_4.011.030">
        <li id="R_4.011.030ab">kimarthaṃ nagaradvāram idaṃ ruddhvā vinardasi</li>
        <li id="R_4.011.030cd">dundubhe vidito me 'si rakṣa prāṇān mahābala</li>
    </ul>
    <ul id="R_4.011.031">
        <li id="R_4.011.031ab">tasya tad vacanaṃ śrutvā vānarendrasya dhīmataḥ</li>
        <li id="R_4.011.031cd">uvāca dundubhir vākyaṃ krodhāt saṃraktalocanaḥ</li>
    </ul>
    <ul id="R_4.011.032">
        <li id="R_4.011.032ab">na tvaṃ strīsaṃnidhau vīra vacanaṃ vaktum arhasi</li>
        <li id="R_4.011.032cd">mama yuddhaṃ prayaccha tvaṃ tato jñāsyāmi te balam</li>
    </ul>
    <ul id="R_4.011.033">
        <li id="R_4.011.033ab">atha vā dhārayiṣyāmi krodham adya niśām imām</li>
        <li id="R_4.011.033cd">gṛhyatām udayaḥ svairaṃ kāmabhogeṣu vānara</li>
    </ul>
    <ul id="R_4.011.034">
        <li id="R_4.011.034ab">yo hi mattaṃ pramattaṃ vā suptaṃ vā rahitaṃ bhṛśam</li>
        <li id="R_4.011.034cd">hanyāt sa bhrūṇahā loke tvadvidhaṃ madamohitam</li>
    </ul>
    <ul id="R_4.011.035">
        <li id="R_4.011.035ab">sa prahasyābravīn mandaṃ krodhāt tam asurottamam</li>
        <li id="R_4.011.035cd">visṛjya tāḥ striyaḥ sarvās tārāprabhṛtikās tadā</li>
    </ul>
    <ul id="R_4.011.036">
        <li id="R_4.011.036ab">matto 'yam iti mā maṃsthā yady abhīto 'si saṃyuge</li>
        <li id="R_4.011.036cd">mado 'yaṃ saṃprahāre 'smin vīrapānaṃ samarthyatām</li>
    </ul>
    <ul id="R_4.011.037">
        <li id="R_4.011.037ab">tam evam uktvā saṃkruddho mālām utkṣipya kāñcanīm</li>
        <li id="R_4.011.037cd">pitrā dattāṃ mahendreṇa yuddhāya vyavatiṣṭhata</li>
    </ul>
    <ul id="R_4.011.038">
        <li id="R_4.011.038ab">viṣāṇayor gṛhītvā taṃ dundubhiṃ girisaṃnibham</li>
        <li id="R_4.011.038cd">vālī vyāpātayāṃ cakre nanarda ca mahāsvanam</li>
    </ul>
    <ul id="R_4.011.039">
        <li id="R_4.011.039ab">yuddhe prāṇahare tasmin niṣpiṣṭo dundubhis tadā</li>
        <li id="R_4.011.039cd">śrotrābhyām atha raktaṃ tu tasya susrāva pātyataḥ</li>
        <li id="R_4.011.039ef">papāta ca mahākāyaḥ kṣitau pañcatvam āgataḥ</li>
    </ul>
    <ul id="R_4.011.040">
        <li id="R_4.011.040ab">taṃ tolayitvā bāhubhyāṃ gatasattvam acetanam</li>
        <li id="R_4.011.040cd">cikṣepa vegavān vālī vegenaikena yojanam</li>
    </ul>
    <ul id="R_4.011.041">
        <li id="R_4.011.041ab">tasya vegapraviddhasya vaktrāt kṣatajabindavaḥ</li>
        <li id="R_4.011.041cd">prapetur mārutotkṣiptā mataṅgasyāśramaṃ prati</li>
    </ul>
    <ul id="R_4.011.042">
        <li id="R_4.011.042ab">tān dṛṣṭvā patitāṃs tatra muniḥ śoṇitavipruṣaḥ</li>
        <li id="R_4.011.042cd">utsasarja mahāśāpaṃ kṣeptāraṃ vālinaṃ prati</li>
        <li id="R_4.011.042ef">iha tenāpraveṣṭavyaṃ praviṣṭasya badho bhavet</li>
    </ul>
    <ul id="R_4.011.043">
        <li id="R_4.011.043ab">sa maharṣiṃ samāsādya yācate sma kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_4.011.044">
        <li id="R_4.011.044ab">tataḥ śāpabhayād bhīta ṛśyamūkaṃ mahāgirim</li>
        <li id="R_4.011.044cd">praveṣṭuṃ necchati harir draṣṭuṃ vāpi nareśvara</li>
    </ul>
    <ul id="R_4.011.045">
        <li id="R_4.011.045ab">tasyāpraveśaṃ jñātvāham idaṃ rāma mahāvanam</li>
        <li id="R_4.011.045cd">vicarāmi sahāmātyo viṣādena vivarjitaḥ</li>
    </ul>
    <ul id="R_4.011.046">
        <li id="R_4.011.046ab">eṣo 'sthinicayas tasya dundubheḥ saṃprakāśate</li>
        <li id="R_4.011.046cd">vīryotsekān nirastasya girikūṭanibho mahān</li>
    </ul>
    <ul id="R_4.011.047">
        <li id="R_4.011.047ab">ime ca vipulāḥ sālāḥ sapta śākhāvalambinaḥ</li>
        <li id="R_4.011.047cd">yatraikaṃ ghaṭate vālī niṣpatrayitum ojasā</li>
    </ul>
    <ul id="R_4.011.048">
        <li id="R_4.011.048ab">etad asyāsamaṃ vīryaṃ mayā rāma prakāśitam</li>
        <li id="R_4.011.048cd">kathaṃ taṃ vālinaṃ hantuṃ samare śakṣyase nṛpa</li>
    </ul>
    <ul id="R_4.011.049">
        <li id="R_4.011.049ab">yadi bhindyād bhavān sālān imāṃs tv ekeṣuṇā tataḥ</li>
        <li id="R_4.011.049cd">jānīyāṃ tvāṃ mahābāho samarthaṃ vālino vadhe</li>
    </ul>
    <ul id="R_4.011.050">
        <li id="R_4.011.050ab">tasya tad vacanaṃ śrutvā sugrīvasya mahātmanaḥ</li>
        <li id="R_4.011.050cd">rāghavo dundubheḥ kāyaṃ pādāṅguṣṭhena līlayā</li>
        <li id="R_4.011.050ef">tolayitvā mahābāhuś cikṣepa daśayojanam</li>
    </ul>
    <ul id="R_4.011.051">
        <li id="R_4.011.051ab">kṣiptaṃ dṛṣṭvā tataḥ kāyaṃ sugrīvaḥ punar abravīt</li>
        <li id="R_4.011.051cd">lakṣmaṇasyāgrato rāmam idaṃ vacanam arthavat</li>
    </ul>
    <ul id="R_4.011.052">
        <li id="R_4.011.052ab">ārdraḥ samāṃsapratyagraḥ kṣiptaḥ kāyaḥ purā sakhe</li>
        <li id="R_4.011.052cd">laghuḥ saṃprati nirmāṃsas tṛṇabhūtaś ca rāghava</li>
        <li id="R_4.011.052ef">nātra śakyaṃ balaṃ jñātuṃ tava vā tasya vādhikam</li>
    </ul>
    <ul id="R_4.012.001">
        <li id="R_4.012.001ab">etac ca vacanaṃ śrutvā sugrīveṇa subhāṣitam</li>
        <li id="R_4.012.001cd">pratyayārthaṃ mahātejā rāmo jagrāha kārmukam</li>
    </ul>
    <ul id="R_4.012.002">
        <li id="R_4.012.002ab">sa gṛhītvā dhanur ghoraṃ śaram ekaṃ ca mānadaḥ</li>
        <li id="R_4.012.002cd">sālān uddiśya cikṣepa jyāsvanaiḥ pūrayan diśaḥ</li>
    </ul>
    <ul id="R_4.012.003">
        <li id="R_4.012.003ab">sa visṛṣṭo balavatā bāṇaḥ svarṇapariṣkṛtaḥ</li>
        <li id="R_4.012.003cd">bhittvā sālān giriprasthe sapta bhūmiṃ viveśa ha</li>
    </ul>
    <ul id="R_4.012.004">
        <li id="R_4.012.004ab">praviṣṭas tu muhūrtena rasāṃ bhittvā mahājavaḥ</li>
        <li id="R_4.012.004cd">niṣpatya ca punas tūrṇaṃ svatūṇīṃ praviveśa ha</li>
    </ul>
    <ul id="R_4.012.005">
        <li id="R_4.012.005ab">tān dṛṣṭvā sapta nirbhinnān sālān vānarapuṃgavaḥ</li>
        <li id="R_4.012.005cd">rāmasya śaravegena vismayaṃ paramaṃ gataḥ</li>
    </ul>
    <ul id="R_4.012.006">
        <li id="R_4.012.006ab">sa mūrdhnā nyapatad bhūmau pralambīkṛtabhūṣaṇaḥ</li>
        <li id="R_4.012.006cd">sugrīvaḥ paramaprīto rāghavāya kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_4.012.007">
        <li id="R_4.012.007ab">idaṃ covāca dharmajñaṃ karmaṇā tena harṣitaḥ</li>
        <li id="R_4.012.007cd">rāmaṃ sarvāstraviduṣāṃ śreṣṭhaṃ śūram avasthitam</li>
    </ul>
    <ul id="R_4.012.008">
        <li id="R_4.012.008ab">sendrān api surān sarvāṃs tvaṃ bāṇaiḥ puruṣarṣabha</li>
        <li id="R_4.012.008cd">samarthaḥ samare hantuṃ kiṃ punar vālinaṃ prabho</li>
    </ul>
    <ul id="R_4.012.009">
        <li id="R_4.012.009ab">yena sapta mahāsālā girir bhūmiś ca dāritāḥ</li>
        <li id="R_4.012.009cd">bāṇenaikena kākutstha sthātā te ko raṇāgrataḥ</li>
    </ul>
    <ul id="R_4.012.010">
        <li id="R_4.012.010ab">adya me vigataḥ śokaḥ prītir adya parā mama</li>
        <li id="R_4.012.010cd">suhṛdaṃ tvāṃ samāsādya mahendravaruṇopamam</li>
    </ul>
    <ul id="R_4.012.011">
        <li id="R_4.012.011ab">tam adyaiva priyārthaṃ me vairiṇaṃ bhrātṛrūpiṇam</li>
        <li id="R_4.012.011cd">vālinaṃ jahi kākutstha mayā baddho 'yam añjaliḥ</li>
    </ul>
    <ul id="R_4.012.012">
        <li id="R_4.012.012ab">tato rāmaḥ pariṣvajya sugrīvaṃ priyadarśanam</li>
        <li id="R_4.012.012cd">pratyuvāca mahāprājño lakṣmaṇānumataṃ vacaḥ</li>
    </ul>
    <ul id="R_4.012.013">
        <li id="R_4.012.013ab">asmād gacchāma kiṣkindhāṃ kṣipraṃ gaccha tvam agrataḥ</li>
        <li id="R_4.012.013cd">gatvā cāhvaya sugrīva vālinaṃ bhrātṛgandhinam</li>
    </ul>
    <ul id="R_4.012.014">
        <li id="R_4.012.014ab">sarve te tvaritaṃ gatvā kiṣkindhāṃ vālinaḥ purīm</li>
        <li id="R_4.012.014cd">vṛkṣair ātmānam āvṛtya vyatiṣṭhan gahane vane</li>
    </ul>
    <ul id="R_4.012.015">
        <li id="R_4.012.015ab">sugrīvo vyanadad ghoraṃ vālino hvānakāraṇāt</li>
        <li id="R_4.012.015cd">gāḍhaṃ parihito vegān nādair bhindann ivāmbaram</li>
    </ul>
    <ul id="R_4.012.016">
        <li id="R_4.012.016ab">taṃ śrutvā ninadaṃ bhrātuḥ kruddho vālī mahābalaḥ</li>
        <li id="R_4.012.016cd">niṣpapāta susaṃrabdho bhāskaro 'stataṭād iva</li>
    </ul>
    <ul id="R_4.012.017">
        <li id="R_4.012.017ab">tataḥ sutumulaṃ yuddhaṃ vālisugrīvayor abhūt</li>
        <li id="R_4.012.017cd">gagane grahayor ghoraṃ budhāṅgārakayor iva</li>
    </ul>
    <ul id="R_4.012.018">
        <li id="R_4.012.018ab">talair aśanikalpaiś ca vajrakalpaiś ca muṣṭibhiḥ</li>
        <li id="R_4.012.018cd">jaghnatuḥ samare 'nyonyaṃ bhrātarau krodhamūrchitau</li>
    </ul>
    <ul id="R_4.012.019">
        <li id="R_4.012.019ab">tato rāmo dhanuṣpāṇis tāv ubhau samudīkṣya tu</li>
        <li id="R_4.012.019cd">anyonyasadṛśau vīrāv ubhau devāv ivāśvinau</li>
    </ul>
    <ul id="R_4.012.020">
        <li id="R_4.012.020ab">yan nāvagacchat sugrīvaṃ vālinaṃ vāpi rāghavaḥ</li>
        <li id="R_4.012.020cd">tato na kṛtavān buddhiṃ moktum antakaraṃ śaram</li>
    </ul>
    <ul id="R_4.012.021">
        <li id="R_4.012.021ab">etasminn antare bhagnaḥ sugrīvas tena vālinā</li>
        <li id="R_4.012.021cd">apaśyan rāghavaṃ nātham ṛśyamūkaṃ pradudruve</li>
    </ul>
    <ul id="R_4.012.022">
        <li id="R_4.012.022ab">klānto rudhirasiktāṅgaḥ prahārair jarjarīkṛtaḥ</li>
        <li id="R_4.012.022cd">vālinābhidrutaḥ krodhāt praviveśa mahāvanam</li>
    </ul>
    <ul id="R_4.012.023">
        <li id="R_4.012.023ab">taṃ praviṣṭaṃ vanaṃ dṛṣṭvā vālī śāpabhayāt tataḥ</li>
        <li id="R_4.012.023cd">mukto hy asi tvam ity uktvā sa nivṛtto mahābalaḥ</li>
    </ul>
    <ul id="R_4.012.024">
        <li id="R_4.012.024ab">rāghavo 'pi saha bhrātrā saha caiva hanūmatā</li>
        <li id="R_4.012.024cd">tad eva vanam āgacchat sugrīvo yatra vānaraḥ</li>
    </ul>
    <ul id="R_4.012.025">
        <li id="R_4.012.025ab">taṃ samīkṣyāgataṃ rāmaṃ sugrīvaḥ sahalakṣmaṇam</li>
        <li id="R_4.012.025cd">hrīmān dīnam uvācedaṃ vasudhām avalokayan</li>
    </ul>
    <ul id="R_4.012.026">
        <li id="R_4.012.026ab">āhvayasveti mām uktvā darśayitvā ca vikramam</li>
        <li id="R_4.012.026cd">vairiṇā ghātayitvā ca kim idānīṃ tvayā kṛtam</li>
    </ul>
    <ul id="R_4.012.027">
        <li id="R_4.012.027ab">tām eva velāṃ vaktavyaṃ tvayā rāghava tattvataḥ</li>
        <li id="R_4.012.027cd">vālinaṃ na nihanmīti tato nāham ito vraje</li>
    </ul>
    <ul id="R_4.012.028">
        <li id="R_4.012.028ab">tasya caivaṃ bruvāṇasya sugrīvasya mahātmanaḥ</li>
        <li id="R_4.012.028cd">karuṇaṃ dīnayā vācā rāghavaḥ punar abravīt</li>
    </ul>
    <ul id="R_4.012.029">
        <li id="R_4.012.029ab">sugrīva śrūyatāṃ tātaḥ krodhaś ca vyapanīyatām</li>
        <li id="R_4.012.029cd">kāraṇaṃ yena bāṇo 'yaṃ na mayā sa visarjitaḥ</li>
    </ul>
    <ul id="R_4.012.030">
        <li id="R_4.012.030ab">alaṃkāreṇa veṣeṇa pramāṇena gatena ca</li>
        <li id="R_4.012.030cd">tvaṃ ca sugrīva vālī ca sadṛśau sthaḥ parasparam</li>
    </ul>
    <ul id="R_4.012.031">
        <li id="R_4.012.031ab">svareṇa varcasā caiva prekṣitena ca vānara</li>
        <li id="R_4.012.031cd">vikrameṇa ca vākyaiś ca vyaktiṃ vāṃ nopalakṣaye</li>
    </ul>
    <ul id="R_4.012.032">
        <li id="R_4.012.032ab">tato 'haṃ rūpasādṛśyān mohito vānarottama</li>
        <li id="R_4.012.032cd">notsṛjāmi mahāvegaṃ śaraṃ śatrunibarhaṇam</li>
    </ul>
    <ul id="R_4.012.033">
        <li id="R_4.012.033ab">etanmuhūrte tu mayā paśya vālinam āhave</li>
        <li id="R_4.012.033cd">nirastam iṣuṇaikena veṣṭamānaṃ mahītale</li>
    </ul>
    <ul id="R_4.012.034">
        <li id="R_4.012.034ab">abhijñānaṃ kuruṣva tvam ātmano vānareśvara</li>
        <li id="R_4.012.034cd">yena tvām abhijānīyāṃ dvandvayuddham upāgatam</li>
    </ul>
    <ul id="R_4.012.035">
        <li id="R_4.012.035ab">gajapuṣpīm imāṃ phullām utpāṭya śubhalakṣaṇām</li>
        <li id="R_4.012.035cd">kuru lakṣmaṇa kaṇṭhe 'sya sugrīvasya mahātmanaḥ</li>
    </ul>
    <ul id="R_4.012.036">
        <li id="R_4.012.036ab">tato giritaṭe jātām utpāṭya kusumāyutām</li>
        <li id="R_4.012.036cd">lakṣmaṇo gajapuṣpīṃ tāṃ tasya kaṇṭhe vyasarjayat</li>
    </ul>
    <ul id="R_4.012.037">
        <li id="R_4.012.037ab">sa tathā śuśubhe śrīmāṃl latayā kaṇṭhasaktayā</li>
        <li id="R_4.012.037cd">mālayeva balākānāṃ sasaṃdhya iva toyadaḥ</li>
    </ul>
    <ul id="R_4.012.038">
        <li id="R_4.012.038ab">vibhrājamāno vapuṣā rāmavākyasamāhitaḥ</li>
        <li id="R_4.012.038cd">jagāma saha rāmeṇa kiṣkindhāṃ vālipālitām</li>
    </ul>
    <ul id="R_4.013.001">
        <li id="R_4.013.001ab">ṛśyamūkāt sa dharmātmā kiṣkindhāṃ lakṣmaṇāgrajaḥ</li>
        <li id="R_4.013.001cd">jagāma sahasugrīvo vālivikramapālitām</li>
    </ul>
    <ul id="R_4.013.002">
        <li id="R_4.013.002ab">samudyamya mahac cāpaṃ rāmaḥ kāñcanabhūṣitam</li>
        <li id="R_4.013.002cd">śarāṃś cāditya saṃkāśān gṛhītvā raṇasādhakān</li>
    </ul>
    <ul id="R_4.013.003">
        <li id="R_4.013.003ab">agratas tu yayau tasya rāghavasya mahātmanaḥ</li>
        <li id="R_4.013.003cd">sugrīvaḥ saṃhatagrīvo lakṣmaṇaś ca mahābalaḥ</li>
    </ul>
    <ul id="R_4.013.004">
        <li id="R_4.013.004ab">pṛṣṭhato hanumān vīro nalo nīlaś ca vānaraḥ</li>
        <li id="R_4.013.004cd">tāraś caiva mahātejā hariyūthapa yūthapāḥ</li>
    </ul>
    <ul id="R_4.013.005">
        <li id="R_4.013.005ab">te vīkṣamāṇā vṛkṣāṃś ca puṣpabhārāvalambinaḥ</li>
        <li id="R_4.013.005cd">prasannāmbuvahāś caiva saritaḥ sāgaraṃ gamāḥ</li>
    </ul>
    <ul id="R_4.013.006">
        <li id="R_4.013.006ab">kandarāṇi ca śailāṃś ca nirjharāṇi guhās tathā</li>
        <li id="R_4.013.006cd">śikharāṇi ca mukhyāni darīś ca priyadarśanāḥ</li>
    </ul>
    <ul id="R_4.013.007">
        <li id="R_4.013.007ab">vaidūryavimalaiḥ parṇaiḥ padmaiś cākāśakuḍmalaiḥ</li>
        <li id="R_4.013.007cd">śobhitān sajalān mārge taṭākāṃś ca vyalokayan</li>
    </ul>
    <ul id="R_4.013.008">
        <li id="R_4.013.008ab">kāraṇḍaiḥ sārasair haṃsair vañjūlair jalakukkuṭaiḥ</li>
        <li id="R_4.013.008cd">cakravākais tathā cānyaiḥ śakunaiḥ pratināditān</li>
    </ul>
    <ul id="R_4.013.009">
        <li id="R_4.013.009ab">mṛduśaṣpāṅkurāhārān nirbhayān vanagocarān</li>
        <li id="R_4.013.009cd">carataḥ sarvato 'paśyan sthalīṣu hariṇān sthitān</li>
    </ul>
    <ul id="R_4.013.010">
        <li id="R_4.013.010ab">taṭākavairiṇaś cāpi śukladantavibhūṣitān</li>
        <li id="R_4.013.010cd">ghorān ekacarān vanyān dviradān kūlaghātinaḥ</li>
    </ul>
    <ul id="R_4.013.011">
        <li id="R_4.013.011ab">vane vanacarāṃś cānyān khecarāṃś ca vihaṃgamān</li>
        <li id="R_4.013.011cd">paśyantas tvaritā jagmuḥ sugrīvavaśavartinaḥ</li>
    </ul>
    <ul id="R_4.013.012">
        <li id="R_4.013.012ab">teṣāṃ tu gacchatāṃ tatra tvaritaṃ raghunandanaḥ</li>
        <li id="R_4.013.012cd">drumaṣaṇḍaṃ vanaṃ dṛṣṭvā rāmaḥ sugrīvam abravīt</li>
    </ul>
    <ul id="R_4.013.013">
        <li id="R_4.013.013ab">eṣa megha ivākāśe vṛkṣaṣaṇḍaḥ prakāśate</li>
        <li id="R_4.013.013cd">meghasaṃghātavipulaḥ paryantakadalīvṛtaḥ</li>
    </ul>
    <ul id="R_4.013.014">
        <li id="R_4.013.014ab">kim etaj jñātum icchāmi sakhe kautūhalaṃ mama</li>
        <li id="R_4.013.014cd">kautūhalāpanayanaṃ kartum icchāmy ahaṃ tvayā</li>
    </ul>
    <ul id="R_4.013.015">
        <li id="R_4.013.015ab">tasya tad vacanaṃ śrutvā rāghavasya mahātmanaḥ</li>
        <li id="R_4.013.015cd">gacchann evācacakṣe 'tha sugrīvas tan mahad vanam</li>
    </ul>
    <ul id="R_4.013.016">
        <li id="R_4.013.016ab">etad rāghava vistīrṇam āśramaṃ śramanāśanam</li>
        <li id="R_4.013.016cd">udyānavanasaṃpannaṃ svādumūlaphalodakam</li>
    </ul>
    <ul id="R_4.013.017">
        <li id="R_4.013.017ab">atra saptajanā nāma munayaḥ saṃśitavratāḥ</li>
        <li id="R_4.013.017cd">saptaivāsann adhaḥśīrṣā niyataṃ jalaśāyinaḥ</li>
    </ul>
    <ul id="R_4.013.018">
        <li id="R_4.013.018ab">saptarātrakṛtāhārā vāyunā vanavāsinaḥ</li>
        <li id="R_4.013.018cd">divaṃ varṣaśatair yātāḥ saptabhiḥ sakalevarāḥ</li>
    </ul>
    <ul id="R_4.013.019">
        <li id="R_4.013.019ab">teṣām evaṃ prabhāvena drumaprākārasaṃvṛtam</li>
        <li id="R_4.013.019cd">āśramaṃ sudurādharṣam api sendraiḥ surāsuraiḥ</li>
    </ul>
    <ul id="R_4.013.020">
        <li id="R_4.013.020ab">pakṣiṇo varjayanty etat tathānye vanacāriṇaḥ</li>
        <li id="R_4.013.020cd">viśanti mohād ye 'py atra nivartante na te punaḥ</li>
    </ul>
    <ul id="R_4.013.021">
        <li id="R_4.013.021ab">vibhūṣaṇaravāś cātra śrūyante sakalākṣarāḥ</li>
        <li id="R_4.013.021cd">tūryagītasvanāś cāpi gandho divyaś ca rāghava</li>
    </ul>
    <ul id="R_4.013.022">
        <li id="R_4.013.022ab">tretāgnayo 'pi dīpyante dhūmo hy eṣa pradṛśyate</li>
        <li id="R_4.013.022cd">veṣṭayann iva vṛkṣāgrān kapotāṅgāruṇo ghanaḥ</li>
    </ul>
    <ul id="R_4.013.023">
        <li id="R_4.013.023ab">kuru praṇāmaṃ dharmātmaṃs tān samuddiśya rāghavaḥ</li>
        <li id="R_4.013.023cd">lakṣmaṇena saha bhrātrā prayataḥ saṃyatāñjaliḥ</li>
    </ul>
    <ul id="R_4.013.024">
        <li id="R_4.013.024ab">praṇamanti hi ye teṣām ṛṣīṇāṃ bhāvitātmanām</li>
        <li id="R_4.013.024cd">na teṣām aśubhaṃ kiṃ cic charīre rāma dṛśyate</li>
    </ul>
    <ul id="R_4.013.025">
        <li id="R_4.013.025ab">tato rāmaḥ saha bhrātrā lakṣmaṇena kṛtāñjaliḥ</li>
        <li id="R_4.013.025cd">samuddiśya mahātmānas tān ṛṣīn abhyavādayat</li>
    </ul>
    <ul id="R_4.013.026">
        <li id="R_4.013.026ab">abhivādya ca dharmātmā rāmo bhrātā ca lakṣmaṇaḥ</li>
        <li id="R_4.013.026cd">sugrīvo vānarāś caiva jagmuḥ saṃhṛṣṭamānasāḥ</li>
    </ul>
    <ul id="R_4.013.027">
        <li id="R_4.013.027ab">te gatvā dūram adhvānaṃ tasmāt saptajanāśramāt</li>
        <li id="R_4.013.027cd">dadṛśus tāṃ durādharṣāṃ kiṣkindhāṃ vālipālitām</li>
    </ul>
    <ul id="R_4.014.001">
        <li id="R_4.014.001ab">sarve te tvaritaṃ gatvā kiṣkindhāṃ vālipālitām</li>
        <li id="R_4.014.001cd">vṛkṣair ātmānam āvṛtya vyatiṣṭhan gahane vane</li>
    </ul>
    <ul id="R_4.014.002">
        <li id="R_4.014.002ab">vicārya sarvato dṛṣṭiṃ kānane kānanapriyaḥ</li>
        <li id="R_4.014.002cd">sugrīvo vipulagrīvaḥ krodham āhārayad bhṛśam</li>
    </ul>
    <ul id="R_4.014.003">
        <li id="R_4.014.003ab">tataḥ sa ninadaṃ ghoraṃ kṛtvā yuddhāya cāhvayat</li>
        <li id="R_4.014.003cd">parivāraiḥ parivṛto nādair bhindann ivāmbaram</li>
    </ul>
    <ul id="R_4.014.004">
        <li id="R_4.014.004ab">atha bālārkasadṛśo dṛptasiṃhagatis tadā</li>
        <li id="R_4.014.004cd">dṛṣṭvā rāmaṃ kriyādakṣaṃ sugrīvo vākyam abravīt</li>
    </ul>
    <ul id="R_4.014.005">
        <li id="R_4.014.005ab">harivāgurayā vyāptaṃ taptakāñcanatoraṇām</li>
        <li id="R_4.014.005cd">prāptāḥ sma dhvajayantrāḍhyāṃ kiṣkindhāṃ vālinaḥ purīm</li>
    </ul>
    <ul id="R_4.014.006">
        <li id="R_4.014.006ab">pratijñā yā tvayā vīra kṛtā vālivadhe purā</li>
        <li id="R_4.014.006cd">saphalāṃ tāṃ kuru kṣipraṃ latāṃ kāla ivāgataḥ</li>
    </ul>
    <ul id="R_4.014.007">
        <li id="R_4.014.007ab">evam uktas tu dharmātmā sugrīveṇa sa rāghavaḥ</li>
        <li id="R_4.014.007cd">tam athovāca sugrīvaṃ vacanaṃ śatrusūdanaḥ</li>
    </ul>
    <ul id="R_4.014.008">
        <li id="R_4.014.008ab">kṛtābhijñāna cihnas tvam anayā gajasāhvayā</li>
        <li id="R_4.014.008cd">viparīta ivākāśe sūryo nakṣatra mālayā</li>
    </ul>
    <ul id="R_4.014.009">
        <li id="R_4.014.009ab">adya vālisamutthaṃ te bhayaṃ vairaṃ ca vānara</li>
        <li id="R_4.014.009cd">ekenāhaṃ pramokṣyāmi bāṇamokṣeṇa saṃyuge</li>
    </ul>
    <ul id="R_4.014.010">
        <li id="R_4.014.010ab">mama darśaya sugrīvavairiṇaṃ bhrātṛrūpiṇam</li>
        <li id="R_4.014.010cd">vālī vinihato yāvad vane pāṃsuṣu veṣṭate</li>
    </ul>
    <ul id="R_4.014.011">
        <li id="R_4.014.011ab">yadi dṛṣṭipathaṃ prāpto jīvan sa vinivartate</li>
        <li id="R_4.014.011cd">tato doṣeṇa mā gacchet sadyo garhec ca mā bhavān</li>
    </ul>
    <ul id="R_4.014.012">
        <li id="R_4.014.012ab">pratyakṣaṃ sapta te sālā mayā bāṇena dāritāḥ</li>
        <li id="R_4.014.012cd">tato vetsi balenādya bālinaṃ nihataṃ mayā</li>
    </ul>
    <ul id="R_4.014.013">
        <li id="R_4.014.013ab">anṛtaṃ noktapūrvaṃ me vīra kṛcchre 'pi tiṣṭhatā</li>
        <li id="R_4.014.013cd">dharmalobhaparītena na ca vakṣye kathaṃ cana</li>
    </ul>
    <ul id="R_4.014.014">
        <li id="R_4.014.014ab">saphalāṃ ca kariṣyāmi pratijñāṃ jahi saṃbhramam</li>
        <li id="R_4.014.014cd">prasūtaṃ kalamaṃ kṣetre varṣeṇeva śatakratuḥ</li>
    </ul>
    <ul id="R_4.014.015">
        <li id="R_4.014.015ab">tadāhvānanimittaṃ tvaṃ vālino hemamālinaḥ</li>
        <li id="R_4.014.015cd">sugrīva kuru taṃ śabdaṃ niṣpated yena vānaraḥ</li>
    </ul>
    <ul id="R_4.014.016">
        <li id="R_4.014.016ab">jitakāśī jayaślāghī tvayā cādharṣitaḥ purāt</li>
        <li id="R_4.014.016cd">niṣpatiṣyaty asaṃgena vālī sa priyasaṃyugaḥ</li>
    </ul>
    <ul id="R_4.014.017">
        <li id="R_4.014.017ab">ripūṇāṃ dharṣaṇaṃ śūrā marṣayanti na saṃyuge</li>
        <li id="R_4.014.017cd">jānantas tu svakaṃ vīryaṃ strīsamakṣaṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_4.014.018">
        <li id="R_4.014.018ab">sa tu rāmavacaḥ śrutvā sugrīvo hemapiṅgalaḥ</li>
        <li id="R_4.014.018cd">nanarda krūranādena vinirbhindann ivāmbaram</li>
    </ul>
    <ul id="R_4.014.019">
        <li id="R_4.014.019ab">tasya śabdena vitrastā gāvo yānti hataprabhāḥ</li>
        <li id="R_4.014.019cd">rājadoṣaparāmṛṣṭāḥ kulastriya ivākulāḥ</li>
    </ul>
    <ul id="R_4.014.020">
        <li id="R_4.014.020ab">dravanti ca mṛgāḥ śīghraṃ bhagnā iva raṇe hayāḥ</li>
        <li id="R_4.014.020cd">patanti ca khagā bhūmau kṣīṇapuṇyā iva grahāḥ</li>
    </ul>
    <ul id="R_4.014.021">
        <li id="R_4.014.021ab">
            <seg type="pāda" n="a">tataḥ sa jīmūtagaṇapraṇādo</seg>
            <seg type="pāda" n="b">nādaṃ vyamuñcat tvarayā pratītaḥ</seg>
        </li>
        <li id="R_4.014.021cd">
            <seg type="pāda" n="c">sūryātmajaḥ śauryavivṛddhatejāḥ</seg>
            <seg type="pāda" n="d">saritpatir vānilacañcalormiḥ</seg>
        </li>
    </ul>
    <ul id="R_4.015.001">
        <li id="R_4.015.001ab">atha tasya ninādaṃ taṃ sugrīvasya mahātmanaḥ</li>
        <li id="R_4.015.001cd">śuśrāvāntaḥpuragato vālī bhrātur amarṣaṇaḥ</li>
    </ul>
    <ul id="R_4.015.002">
        <li id="R_4.015.002ab">śrutvā tu tasya ninadaṃ sarvabhūtaprakampanam</li>
        <li id="R_4.015.002cd">madaś caikapade naṣṭaḥ krodhaś cāpatito mahān</li>
    </ul>
    <ul id="R_4.015.003">
        <li id="R_4.015.003ab">sa tu roṣaparītāṅgo vālī saṃdhyātapaprabhaḥ</li>
        <li id="R_4.015.003cd">uparakta ivādityaḥ sadyo niṣprabhatāṃ gataḥ</li>
    </ul>
    <ul id="R_4.015.004">
        <li id="R_4.015.004ab">vālī daṃṣṭrā karālas tu krodhād dīptāgnisaṃnibhaḥ</li>
        <li id="R_4.015.004cd">bhāty utpatitapadmābhaḥ samṛṇāla iva hradaḥ</li>
    </ul>
    <ul id="R_4.015.005">
        <li id="R_4.015.005ab">śabdaṃ durmarṣaṇaṃ śrutvā niṣpapāta tato hariḥ</li>
        <li id="R_4.015.005cd">vegena caraṇanyāsair dārayann iva medinīm</li>
    </ul>
    <ul id="R_4.015.006">
        <li id="R_4.015.006ab">taṃ tu tārā pariṣvajya snehād darśitasauhṛdā</li>
        <li id="R_4.015.006cd">uvāca trastasaṃbhrāntā hitodarkam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_4.015.007">
        <li id="R_4.015.007ab">sādhu krodham imaṃ vīra nadī vegam ivāgatam</li>
        <li id="R_4.015.007cd">śayanād utthitaḥ kālyaṃ tyaja bhuktām iva srajam</li>
    </ul>
    <ul id="R_4.015.008">
        <li id="R_4.015.008ab">sahasā tava niṣkrāmo mama tāvan na rocate</li>
        <li id="R_4.015.008cd">śrūyatām abhidhāsyāmi yannimittaṃ nivāryase</li>
    </ul>
    <ul id="R_4.015.009">
        <li id="R_4.015.009ab">pūrvam āpatitaḥ krodhāt sa tvām āhvayate yudhi</li>
        <li id="R_4.015.009cd">niṣpatya ca nirastas te hanyamāno diśo gataḥ</li>
    </ul>
    <ul id="R_4.015.010">
        <li id="R_4.015.010ab">tvayā tasya nirastasya pīḍitasya viśeṣataḥ</li>
        <li id="R_4.015.010cd">ihaitya punar āhvānaṃ śaṅkāṃ janayatīva me</li>
    </ul>
    <ul id="R_4.015.011">
        <li id="R_4.015.011ab">darpaś ca vyavasāyaś ca yādṛśas tasya nardataḥ</li>
        <li id="R_4.015.011cd">ninādasya ca saṃrambho naitad alpaṃ hi kāraṇam</li>
    </ul>
    <ul id="R_4.015.012">
        <li id="R_4.015.012ab">nāsahāyam ahaṃ manye sugrīvaṃ tam ihāgatam</li>
        <li id="R_4.015.012cd">avaṣṭabdhasahāyaś ca yam āśrityaiṣa garjati</li>
    </ul>
    <ul id="R_4.015.013">
        <li id="R_4.015.013ab">prakṛtyā nipuṇaś caiva buddhimāṃś caiva vānaraḥ</li>
        <li id="R_4.015.013cd">aparīkṣitavīryeṇa sugrīvaḥ saha naiṣyati</li>
    </ul>
    <ul id="R_4.015.014">
        <li id="R_4.015.014ab">pūrvam eva mayā vīra śrutaṃ kathayato vacaḥ</li>
        <li id="R_4.015.014cd">aṅgadasya kumārasya vakṣyāmi tvā hitaṃ vacaḥ</li>
    </ul>
    <ul id="R_4.015.015">
        <li id="R_4.015.015ab">tava bhrātur hi vikhyātaḥ sahāyo raṇakarkaśaḥ</li>
        <li id="R_4.015.015cd">rāmaḥ parabalāmardī yugāntāgnir ivotthitaḥ</li>
    </ul>
    <ul id="R_4.015.016">
        <li id="R_4.015.016ab">nivāsavṛkṣaḥ sādhūnām āpannānāṃ parā gatiḥ</li>
        <li id="R_4.015.016cd">ārtānāṃ saṃśrayaś caiva yaśasaś caikabhājanam</li>
    </ul>
    <ul id="R_4.015.017">
        <li id="R_4.015.017ab">jñānavijñānasaṃpanno nideśo nirataḥ pituḥ</li>
        <li id="R_4.015.017cd">dhātūnām iva śailendro guṇānām ākaro mahān</li>
    </ul>
    <ul id="R_4.015.018">
        <li id="R_4.015.018ab">tatkṣamaṃ na virodhas te saha tena mahātmanā</li>
        <li id="R_4.015.018cd">durjayenāprameyena rāmeṇa raṇakarmasu</li>
    </ul>
    <ul id="R_4.015.019">
        <li id="R_4.015.019ab">śūra vakṣyāmi te kiṃ cin na cecchāmy abhyasūyitum</li>
        <li id="R_4.015.019cd">śrūyatāṃ kriyatāṃ caiva tava vakṣyāmi yad dhitam</li>
    </ul>
    <ul id="R_4.015.020">
        <li id="R_4.015.020ab">yauvarājyena sugrīvaṃ tūrṇaṃ sādhv abhiṣecaya</li>
        <li id="R_4.015.020cd">vigrahaṃ mā kṛthā vīra bhrātrā rājan balīyasā</li>
    </ul>
    <ul id="R_4.015.021">
        <li id="R_4.015.021ab">ahaṃ hi te kṣamaṃ manye tava rāmeṇa sauhṛdam</li>
        <li id="R_4.015.021cd">sugrīveṇa ca saṃprītiṃ vairam utsṛjya dūrataḥ</li>
    </ul>
    <ul id="R_4.015.022">
        <li id="R_4.015.022ab">lālanīyo hi te bhrātā yavīyān eṣa vānaraḥ</li>
        <li id="R_4.015.022cd">tatra vā sann ihastho vā sarvathā bandhur eva te</li>
    </ul>
    <ul id="R_4.015.023">
        <li id="R_4.015.023ab">yadi te matpriyaṃ kāryaṃ yadi cāvaiṣi māṃ hitām</li>
        <li id="R_4.015.023cd">yācyamānaḥ prayatnena sādhu vākyaṃ kuruṣva me</li>
    </ul>
    <ul id="R_4.016.001">
        <li id="R_4.016.001ab">tām evaṃ bruvatīṃ tārāṃ tārādhipanibhānanām</li>
        <li id="R_4.016.001cd">vālī nirbhartsayām āsa vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_4.016.002">
        <li id="R_4.016.002ab">garjato 'sya ca saṃrambhaṃ bhrātuḥ śatror viśeṣataḥ</li>
        <li id="R_4.016.002cd">marṣayiṣyāmy ahaṃ kena kāraṇena varānane</li>
    </ul>
    <ul id="R_4.016.003">
        <li id="R_4.016.003ab">adharṣitānāṃ śūrāṇāṃ samareṣv anivartinām</li>
        <li id="R_4.016.003cd">dharṣaṇāmarṣaṇaṃ bhīru maraṇād atiricyate</li>
    </ul>
    <ul id="R_4.016.004">
        <li id="R_4.016.004ab">soḍhuṃ na ca samartho 'haṃ yuddhakāmasya saṃyuge</li>
        <li id="R_4.016.004cd">sugrīvasya ca saṃrambhaṃ hīnagrīvasya garjataḥ</li>
    </ul>
    <ul id="R_4.016.005">
        <li id="R_4.016.005ab">na ca kāryo viṣādas te rāghavaṃ prati matkṛte</li>
        <li id="R_4.016.005cd">dharmajñaś ca kṛtajñaś ca kathaṃ pāpaṃ kariṣyati</li>
    </ul>
    <ul id="R_4.016.006">
        <li id="R_4.016.006ab">nivartasva saha strībhiḥ kathaṃ bhūyo 'nugacchasi</li>
        <li id="R_4.016.006cd">sauhṛdaṃ darśitaṃ tāre mayi bhaktiḥ kṛtā tvayā</li>
    </ul>
    <ul id="R_4.016.007">
        <li id="R_4.016.007ab">pratiyotsyāmy ahaṃ gatvā sugrīvaṃ jahi saṃbhramam</li>
        <li id="R_4.016.007cd">darpaṃ cāsya vineṣyāmi na ca prāṇair vimokṣyate</li>
    </ul>
    <ul id="R_4.016.008">
        <li id="R_4.016.008ab">śāpitāsi mama prāṇair nivartasva jayena ca</li>
        <li id="R_4.016.008cd">ahaṃ jitvā nivartiṣye tam alaṃ bhrātaraṃ raṇe</li>
    </ul>
    <ul id="R_4.016.009">
        <li id="R_4.016.009ab">taṃ tu tārā pariṣvajya vālinaṃ priyavādinī</li>
        <li id="R_4.016.009cd">cakāra rudatī mandaṃ dakṣiṇā sā pradakṣiṇam</li>
    </ul>
    <ul id="R_4.016.010">
        <li id="R_4.016.010ab">tataḥ svastyayanaṃ kṛtvā mantravad vijayaiṣiṇī</li>
        <li id="R_4.016.010cd">antaḥpuraṃ saha strībhiḥ praviṣṭā śokamohitā</li>
    </ul>
    <ul id="R_4.016.011">
        <li id="R_4.016.011ab">praviṣṭāyāṃ tu tārāyāṃ saha strībhiḥ svam ālayam</li>
        <li id="R_4.016.011cd">nagarān niryayau kruddho mahāsarpa iva śvasan</li>
    </ul>
    <ul id="R_4.016.012">
        <li id="R_4.016.012ab">sa niḥśvasya mahāvego vālī paramaroṣaṇaḥ</li>
        <li id="R_4.016.012cd">sarvataś cārayan dṛṣṭiṃ śatrudarśanakāṅkṣayā</li>
    </ul>
    <ul id="R_4.016.013">
        <li id="R_4.016.013ab">sa dadarśa tataḥ śrīmān sugrīvaṃ hemapiṅgalam</li>
        <li id="R_4.016.013cd">susaṃvītam avaṣṭabdhaṃ dīpyamānam ivānalam</li>
    </ul>
    <ul id="R_4.016.014">
        <li id="R_4.016.014ab">sa taṃ dṛṣṭvā mahāvīryaṃ sugrīvaṃ paryavasthitam</li>
        <li id="R_4.016.014cd">gāḍhaṃ paridadhe vāso vālī paramaroṣaṇaḥ</li>
    </ul>
    <ul id="R_4.016.015">
        <li id="R_4.016.015ab">sa vālī gāḍhasaṃvīto muṣṭim udyamya vīryavān</li>
        <li id="R_4.016.015cd">sugrīvam evābhimukho yayau yoddhuṃ kṛtakṣaṇaḥ</li>
    </ul>
    <ul id="R_4.016.016">
        <li id="R_4.016.016ab">śliṣṭamuṣṭiṃ samudyamya saṃrabdhataram āgataḥ</li>
        <li id="R_4.016.016cd">sugrīvo 'pi samuddiśya vālinaṃ hemamālinam</li>
    </ul>
    <ul id="R_4.016.017">
        <li id="R_4.016.017ab">taṃ vālī krodhatāmrākṣaḥ sugrīvaṃ raṇapaṇḍitam</li>
        <li id="R_4.016.017cd">āpatantaṃ mahāvegam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.016.018">
        <li id="R_4.016.018ab">eṣa muṣṭir mayā baddho gāḍhaḥ sunihitāṅguliḥ</li>
        <li id="R_4.016.018cd">mayā vegavimuktas te prāṇān ādāya yāsyati</li>
    </ul>
    <ul id="R_4.016.019">
        <li id="R_4.016.019ab">evam uktas tu sugrīvaḥ kruddho vālinam abravīt</li>
        <li id="R_4.016.019cd">tavaiva ca haran prāṇān muṣṭiḥ patatu mūrdhani</li>
    </ul>
    <ul id="R_4.016.020">
        <li id="R_4.016.020ab">tāḍitas tena saṃkruddhaḥ samabhikramya vegataḥ</li>
        <li id="R_4.016.020cd">abhavac choṇitodgārī sotpīḍa iva parvataḥ</li>
    </ul>
    <ul id="R_4.016.021">
        <li id="R_4.016.021ab">sugrīveṇa tu niḥsaṃgaṃ sālam utpāṭya tejasā</li>
        <li id="R_4.016.021cd">gātreṣv abhihato vālī vajreṇeva mahāgiriḥ</li>
    </ul>
    <ul id="R_4.016.022">
        <li id="R_4.016.022ab">sa tu vālī pracaritaḥ sālatāḍanavihvalaḥ</li>
        <li id="R_4.016.022cd">gurubhārasamākrāntā sāgare naur ivābhavat</li>
    </ul>
    <ul id="R_4.016.023">
        <li id="R_4.016.023ab">tau bhīmabalavikrāntau suparṇasamaveginau</li>
        <li id="R_4.016.023cd">pravṛddhau ghoravapuṣau candrasūryāv ivāmbare</li>
    </ul>
    <ul id="R_4.016.024">
        <li id="R_4.016.024ab">vālinā bhagnadarpas tu sugrīvo mandavikramaḥ</li>
        <li id="R_4.016.024cd">vālinaṃ prati sāmarṣo darśayām āsa lāghavam</li>
    </ul>
    <ul id="R_4.016.025">
        <li id="R_4.016.025ab">tato dhanuṣi saṃdhāya śaram āśīviṣopamam</li>
        <li id="R_4.016.025cd">rāghaveṇa mahābāṇo vālivakṣasi pātitaḥ</li>
    </ul>
    <ul id="R_4.016.026">
        <li id="R_4.016.026ab">vegenābhihato vālī nipapāta mahītale</li>
    </ul>
    <ul id="R_4.016.027">
        <li id="R_4.016.027ab">
            <seg type="pāda" n="a">athokṣitaḥ śoṇitatoyavisravaiḥ</seg>
            <seg type="pāda" n="b">supuṣpitāśoka ivāniloddhataḥ</seg>
        </li>
        <li id="R_4.016.027cd">
            <seg type="pāda" n="c">vicetano vāsavasūnur āhave</seg>
            <seg type="pāda" n="d">prabhraṃśitendradhvajavat kṣitiṃ gataḥ</seg>
        </li>
    </ul>
    <ul id="R_4.017.001">
        <li id="R_4.017.001ab">tataḥ śareṇābhihato rāmeṇa raṇakarkaśaḥ</li>
        <li id="R_4.017.001cd">papāta sahasā vālī nikṛtta iva pādapaḥ</li>
    </ul>
    <ul id="R_4.017.002">
        <li id="R_4.017.002ab">sa bhūmau nyastasarvāṅgas taptakāñcanabhūṣaṇaḥ</li>
        <li id="R_4.017.002cd">apatad devarājasya muktaraśmir iva dhvajaḥ</li>
    </ul>
    <ul id="R_4.017.003">
        <li id="R_4.017.003ab">tasmin nipatite bhūmau haryṛṣāṇāṃ gaṇeśvare</li>
        <li id="R_4.017.003cd">naṣṭacandram iva vyoma na vyarājata bhūtalam</li>
    </ul>
    <ul id="R_4.017.004">
        <li id="R_4.017.004ab">bhūmau nipatitasyāpi tasya dehaṃ mahātmanaḥ</li>
        <li id="R_4.017.004cd">na śrīr jahāti na prāṇā na tejo na parākramaḥ</li>
    </ul>
    <ul id="R_4.017.005">
        <li id="R_4.017.005ab">śakradattā varā mālā kāñcanī ratnabhūṣitā</li>
        <li id="R_4.017.005cd">dadhāra harimukhyasya prāṇāṃs tejaḥ śriyaṃ ca sā</li>
    </ul>
    <ul id="R_4.017.006">
        <li id="R_4.017.006ab">sa tayā mālayā vīro haimayā hariyūthapaḥ</li>
        <li id="R_4.017.006cd">saṃdhyānugataparyantaḥ payodhara ivābhavat</li>
    </ul>
    <ul id="R_4.017.007">
        <li id="R_4.017.007ab">tasya mālā ca dehaś ca marmaghātī ca yaḥ śaraḥ</li>
        <li id="R_4.017.007cd">tridheva racitā lakṣmīḥ patitasyāpi śobhate</li>
    </ul>
    <ul id="R_4.017.008">
        <li id="R_4.017.008ab">tad astraṃ tasya vīrasya svargamārgaprabhāvanam</li>
        <li id="R_4.017.008cd">rāmabāṇāsanakṣiptam āvahat paramāṃ gatim</li>
    </ul>
    <ul id="R_4.017.009">
        <li id="R_4.017.009ab">taṃ tathā patitaṃ saṃkhye gatārciṣam ivānalam</li>
        <li id="R_4.017.009cd">yayātim iva puṇyānte devalokāt paricyutam</li>
    </ul>
    <ul id="R_4.017.010">
        <li id="R_4.017.010ab">ādityam iva kālena yugānte bhuvi pātitam</li>
        <li id="R_4.017.010cd">mahendram iva durdharṣaṃ mahendram iva duḥsaham</li>
    </ul>
    <ul id="R_4.017.011">
        <li id="R_4.017.011ab">mahendraputraṃ patitaṃ vālinaṃ hemamālinam</li>
        <li id="R_4.017.011cd">siṃhoraskaṃ mahābāhuṃ dīptāsyaṃ harilocanam</li>
        <li id="R_4.017.011ef">lakṣmaṇānugato rāmo dadarśopasasarpa ca</li>
    </ul>
    <ul id="R_4.017.012">
        <li id="R_4.017.012ab">sa dṛṣṭvā rāghavaṃ vālī lakṣmaṇaṃ ca mahābalam</li>
        <li id="R_4.017.012cd">abravīt praśritaṃ vākyaṃ paruṣaṃ dharmasaṃhitam</li>
    </ul>
    <ul id="R_4.017.013">
        <li id="R_4.017.013ab">parāṅmukhavadhaṃ kṛtvā ko nu prāptas tvayā guṇaḥ</li>
        <li id="R_4.017.013cd">yad ahaṃ yuddhasaṃrabdhas tvatkṛte nidhanaṃ gataḥ</li>
    </ul>
    <ul id="R_4.017.014">
        <li id="R_4.017.014ab">kulīnaḥ sattvasaṃpannas tejasvī caritavrataḥ</li>
        <li id="R_4.017.014cd">rāmaḥ karuṇavedī ca prajānāṃ ca hite rataḥ</li>
    </ul>
    <ul id="R_4.017.015">
        <li id="R_4.017.015ab">sānukrośo mahotsāhaḥ samayajño dṛḍhavrataḥ</li>
        <li id="R_4.017.015cd">iti te sarvabhūtāni kathayanti yaśo bhuvi</li>
    </ul>
    <ul id="R_4.017.016">
        <li id="R_4.017.016ab">tān guṇān saṃpradhāryāham agryaṃ cābhijanaṃ tava</li>
        <li id="R_4.017.016cd">tārayā pratiṣiddhaḥ san sugrīveṇa samāgataḥ</li>
    </ul>
    <ul id="R_4.017.017">
        <li id="R_4.017.017ab">na mām anyena saṃrabdhaṃ pramattaṃ veddhum arhasi</li>
        <li id="R_4.017.017cd">iti me buddhir utpannā babhūvādarśane tava</li>
    </ul>
    <ul id="R_4.017.018">
        <li id="R_4.017.018ab">na tvāṃ vinihatātmānaṃ dharmadhvajam adhārmikam</li>
        <li id="R_4.017.018cd">jāne pāpasamācāraṃ tṛṇaiḥ kūpam ivāvṛtam</li>
    </ul>
    <ul id="R_4.017.019">
        <li id="R_4.017.019ab">satāṃ veṣadharaṃ pāpaṃ pracchannam iva pāvakam</li>
        <li id="R_4.017.019cd">nāhaṃ tvām abhijānāni dharmacchadmābhisaṃvṛtam</li>
    </ul>
    <ul id="R_4.017.020">
        <li id="R_4.017.020ab">viṣaye vā pure vā te yadā nāpakaromy aham</li>
        <li id="R_4.017.020cd">na ca tvāṃ pratijāne 'haṃ kasmāt tvaṃ haṃsy akilbiṣam</li>
    </ul>
    <ul id="R_4.017.021">
        <li id="R_4.017.021ab">phalamūlāśanaṃ nityaṃ vānaraṃ vanagocaram</li>
        <li id="R_4.017.021cd">mām ihāpratiyudhyantam anyena ca samāgatam</li>
    </ul>
    <ul id="R_4.017.022">
        <li id="R_4.017.022ab">tvaṃ narādhipateḥ putraḥ pratītaḥ priyadarśanaḥ</li>
        <li id="R_4.017.022cd">liṅgam apy asti te rājan dṛśyate dharmasaṃhitam</li>
    </ul>
    <ul id="R_4.017.023">
        <li id="R_4.017.023ab">kaḥ kṣatriyakule jātaḥ śrutavān naṣṭasaṃśayaḥ</li>
        <li id="R_4.017.023cd">dharmaliṅga praticchannaḥ krūraṃ karma samācaret</li>
    </ul>
    <ul id="R_4.017.024">
        <li id="R_4.017.024ab">rāma rājakule jāto dharmavān iti viśrutaḥ</li>
        <li id="R_4.017.024cd">abhavyo bhavyarūpeṇa kimarthaṃ paridhāvasi</li>
    </ul>
    <ul id="R_4.017.025">
        <li id="R_4.017.025ab">sāma dānaṃ kṣamā dharmaḥ satyaṃ dhṛtiparākramau</li>
        <li id="R_4.017.025cd">pārthivānāṃ guṇā rājan daṇḍaś cāpy apakāriṣu</li>
    </ul>
    <ul id="R_4.017.026">
        <li id="R_4.017.026ab">vayaṃ vanacarā rāma mṛgā mūlaphalāśanāḥ</li>
        <li id="R_4.017.026cd">eṣā prakṛtir asmākaṃ puruṣas tvaṃ nareśvaraḥ</li>
    </ul>
    <ul id="R_4.017.027">
        <li id="R_4.017.027ab">bhūmir hiraṇyaṃ rūpyaṃ ca nigrahe kāraṇāni ca</li>
        <li id="R_4.017.027cd">tatra kas te vane lobho madīyeṣu phaleṣu vā</li>
    </ul>
    <ul id="R_4.017.028">
        <li id="R_4.017.028ab">nayaś ca vinayaś cobhau nigrahānugrahāv api</li>
        <li id="R_4.017.028cd">rājavṛttir asaṃkīrṇā na nṛpāḥ kāmavṛttayaḥ</li>
    </ul>
    <ul id="R_4.017.029">
        <li id="R_4.017.029ab">tvaṃ tu kāmapradhānaś ca kopanaś cānavasthitaḥ</li>
        <li id="R_4.017.029cd">rājavṛttaiś ca saṃkīrṇaḥ śarāsanaparāyaṇaḥ</li>
    </ul>
    <ul id="R_4.017.030">
        <li id="R_4.017.030ab">na te 'sty apacitir dharme nārthe buddhir avasthitā</li>
        <li id="R_4.017.030cd">indriyaiḥ kāmavṛttaḥ san kṛṣyase manujeśvara</li>
    </ul>
    <ul id="R_4.017.031">
        <li id="R_4.017.031ab">hatvā bāṇena kākutstha mām ihānaparādhinam</li>
        <li id="R_4.017.031cd">kiṃ vakṣyasi satāṃ madhye karma kṛtvā jugupsitam</li>
    </ul>
    <ul id="R_4.017.032">
        <li id="R_4.017.032ab">rājahā brahmahā goghnaś coraḥ prāṇivadhe rataḥ</li>
        <li id="R_4.017.032cd">nāstikaḥ parivettā ca sarve nirayagāminaḥ</li>
    </ul>
    <ul id="R_4.017.033">
        <li id="R_4.017.033ab">adhāryaṃ carma me sadbhī romāṇy asthi ca varjitam</li>
        <li id="R_4.017.033cd">abhakṣyāṇi ca māṃsāni tvadvidhair dharmacāribhiḥ</li>
    </ul>
    <ul id="R_4.017.034">
        <li id="R_4.017.034ab">pañca pañcanakhā bhakṣyā brahmakṣatreṇa rāghava</li>
        <li id="R_4.017.034cd">śalyakaḥ śvāvidho godhā śaśaḥ kūrmaś ca pañcamaḥ</li>
    </ul>
    <ul id="R_4.017.035">
        <li id="R_4.017.035ab">carma cāsthi ca me rājan na spṛśanti manīṣiṇaḥ</li>
        <li id="R_4.017.035cd">abhakṣyāṇi ca māṃsāni so 'haṃ pañcanakho hataḥ</li>
    </ul>
    <ul id="R_4.017.036">
        <li id="R_4.017.036ab">tvayā nāthena kākutstha na sanāthā vasuṃdharā</li>
        <li id="R_4.017.036cd">pramadā śīlasaṃpannā dhūrtena patitā yathā</li>
    </ul>
    <ul id="R_4.017.037">
        <li id="R_4.017.037ab">śaṭho naikṛtikaḥ kṣudro mithyā praśritamānasaḥ</li>
        <li id="R_4.017.037cd">kathaṃ daśarathena tvaṃ jātaḥ pāpo mahātmanā</li>
    </ul>
    <ul id="R_4.017.038">
        <li id="R_4.017.038ab">chinnacāritryakakṣyeṇa satāṃ dharmātivartinā</li>
        <li id="R_4.017.038cd">tyaktadharmāṅkuśenāhaṃ nihato rāmahastinā</li>
    </ul>
    <ul id="R_4.017.039">
        <li id="R_4.017.039ab">dṛśyamānas tu yudhyethā mayā yudhi nṛpātmaja</li>
        <li id="R_4.017.039cd">adya vaivasvataṃ devaṃ paśyes tvaṃ nihato mayā</li>
    </ul>
    <ul id="R_4.017.040">
        <li id="R_4.017.040ab">tvayādṛśyena tu raṇe nihato 'haṃ durāsadaḥ</li>
        <li id="R_4.017.040cd">prasuptaḥ pannageneva naraḥ pānavaśaṃ gataḥ</li>
    </ul>
    <ul id="R_4.017.041">
        <li id="R_4.017.041ab">sugrīvapriyakāmena yad ahaṃ nihatas tvayā</li>
        <li id="R_4.017.041cd">kaṇṭhe baddhvā pradadyāṃ te 'nihataṃ rāvaṇaṃ raṇe</li>
    </ul>
    <ul id="R_4.017.042">
        <li id="R_4.017.042ab">nyastāṃ sāgaratoye vā pātāle vāpi maithilīm</li>
        <li id="R_4.017.042cd">jānayeyaṃ tavādeśāc chvetām aśvatarīm iva</li>
    </ul>
    <ul id="R_4.017.043">
        <li id="R_4.017.043ab">yuktaṃ yat prapnuyād rājyaṃ sugrīvaḥ svargate mayi</li>
        <li id="R_4.017.043cd">ayuktaṃ yad adharmeṇa tvayāhaṃ nihato raṇe</li>
    </ul>
    <ul id="R_4.017.044">
        <li id="R_4.017.044ab">kāmam evaṃvidhaṃ lokaḥ kālena viniyujyate</li>
        <li id="R_4.017.044cd">kṣamaṃ ced bhavatā prāptam uttaraṃ sādhu cintyatām</li>
    </ul>
    <ul id="R_4.017.045">
        <li id="R_4.017.045ab">
            <seg type="pāda" n="a">ity evam uktvā pariśuṣkavaktraḥ</seg>
            <seg type="pāda" n="b">śarābhighātād vyathito mahātmā</seg>
        </li>
        <li id="R_4.017.045cd">
            <seg type="pāda" n="c">samīkṣya rāmaṃ ravisaṃnikāśaṃ</seg>
            <seg type="pāda" n="d">tūṣṇīṃ babhūvāmararājasūnuḥ</seg>
        </li>
    </ul>
    <ul id="R_4.018.001">
        <li id="R_4.018.001ab">ity uktaḥ praśritaṃ vākyaṃ dharmārthasahitaṃ hitam</li>
        <li id="R_4.018.001cd">paruṣaṃ vālinā rāmo nihatena vicetasā</li>
    </ul>
    <ul id="R_4.018.002">
        <li id="R_4.018.002ab">taṃ niṣprabham ivādityaṃ muktatoyam ivāmbudam</li>
        <li id="R_4.018.002cd">uktavākyaṃ hariśreṣṭham upaśāntam ivānalam</li>
    </ul>
    <ul id="R_4.018.003">
        <li id="R_4.018.003ab">dharmārthaguṇasaṃpannaṃ harīśvaram anuttamam</li>
        <li id="R_4.018.003cd">adhikṣiptas tadā rāmaḥ paścād vālinam abravīt</li>
    </ul>
    <ul id="R_4.018.004">
        <li id="R_4.018.004ab">dharmam arthaṃ ca kāmaṃ ca samayaṃ cāpi laukikam</li>
        <li id="R_4.018.004cd">avijñāya kathaṃ bālyān mām ihādya vigarhase</li>
    </ul>
    <ul id="R_4.018.005">
        <li id="R_4.018.005ab">apṛṣṭvā buddhisaṃpannān vṛddhān ācāryasaṃmatān</li>
        <li id="R_4.018.005cd">saumya vānaracāpalyāt tvaṃ māṃ vaktum ihecchasi</li>
    </ul>
    <ul id="R_4.018.006">
        <li id="R_4.018.006ab">ikṣvākūṇām iyaṃ bhūmiḥ saśailavanakānanā</li>
        <li id="R_4.018.006cd">mṛgapakṣimanuṣyāṇāṃ nigrahānugrahāv api</li>
    </ul>
    <ul id="R_4.018.007">
        <li id="R_4.018.007ab">tāṃ pālayati dharmātmā bharataḥ satyavāg ṛjuḥ</li>
        <li id="R_4.018.007cd">dharmakāmārthatattvajño nigrahānugrahe rataḥ</li>
    </ul>
    <ul id="R_4.018.008">
        <li id="R_4.018.008ab">nayaś ca vinayaś cobhau yasmin satyaṃ ca susthitam</li>
        <li id="R_4.018.008cd">vikramaś ca yathā dṛṣṭaḥ sa rājā deśakālavit</li>
    </ul>
    <ul id="R_4.018.009">
        <li id="R_4.018.009ab">tasya dharmakṛtādeśā vayam anye ca pārthivaḥ</li>
        <li id="R_4.018.009cd">carāmo vasudhāṃ kṛtsnāṃ dharmasaṃtānam icchavaḥ</li>
    </ul>
    <ul id="R_4.018.010">
        <li id="R_4.018.010ab">tasmin nṛpatiśārdūla bharate dharmavatsale</li>
        <li id="R_4.018.010cd">pālayaty akhilāṃ bhūmiṃ kaś cared dharmanigraham</li>
    </ul>
    <ul id="R_4.018.011">
        <li id="R_4.018.011ab">te vayaṃ mārgavibhraṣṭaṃ svadharme parame sthitāḥ</li>
        <li id="R_4.018.011cd">bharatājñāṃ puraskṛtya nigṛhṇīmo yathāvidhi</li>
    </ul>
    <ul id="R_4.018.012">
        <li id="R_4.018.012ab">tvaṃ tu saṃkliṣṭadharmā ca karmaṇā ca vigarhitaḥ</li>
        <li id="R_4.018.012cd">kāmatantrapradhānaś ca na sthito rājavartmani</li>
    </ul>
    <ul id="R_4.018.013">
        <li id="R_4.018.013ab">jyeṣṭho bhrātā pitā caiva yaś ca vidyāṃ prayacchati</li>
        <li id="R_4.018.013cd">trayas te pitaro jñeyā dharme ca pathi vartinaḥ</li>
    </ul>
    <ul id="R_4.018.014">
        <li id="R_4.018.014ab">yavīyān ātmanaḥ putraḥ śiṣyaś cāpi guṇoditaḥ</li>
        <li id="R_4.018.014cd">putravat te trayaś cintyā dharmaś ced atra kāraṇam</li>
    </ul>
    <ul id="R_4.018.015">
        <li id="R_4.018.015ab">sūkṣmaḥ paramadurjñeyaḥ satāṃ dharmaḥ plavaṃgama</li>
        <li id="R_4.018.015cd">hṛdisthaḥ sarvabhūtānām ātmā veda śubhāśubham</li>
    </ul>
    <ul id="R_4.018.016">
        <li id="R_4.018.016ab">capalaś capalaiḥ sārdhaṃ vānarair akṛtātmabhiḥ</li>
        <li id="R_4.018.016cd">jātyandha iva jātyandhair mantrayan drakṣyase nu kim</li>
    </ul>
    <ul id="R_4.018.017">
        <li id="R_4.018.017ab">ahaṃ tu vyaktatām asya vacanasya bravīmi te</li>
        <li id="R_4.018.017cd">na hi māṃ kevalaṃ roṣāt tvaṃ vigarhitum arhasi</li>
    </ul>
    <ul id="R_4.018.018">
        <li id="R_4.018.018ab">tad etat kāraṇaṃ paśya yadarthaṃ tvaṃ mayā hataḥ</li>
        <li id="R_4.018.018cd">bhrātur vartasi bhāryāyāṃ tyaktvā dharmaṃ sanātanam</li>
    </ul>
    <ul id="R_4.018.019">
        <li id="R_4.018.019ab">asya tvaṃ dharamāṇasya sugrīvasya mahātmanaḥ</li>
        <li id="R_4.018.019cd">rumāyāṃ vartase kāmāt snuṣāyāṃ pāpakarmakṛt</li>
    </ul>
    <ul id="R_4.018.020">
        <li id="R_4.018.020ab">tad vyatītasya te dharmāt kāmavṛttasya vānara</li>
        <li id="R_4.018.020cd">bhrātṛbhāryābhimarśe 'smin daṇḍo 'yaṃ pratipāditaḥ</li>
    </ul>
    <ul id="R_4.018.021">
        <li id="R_4.018.021ab">na hi dharmaviruddhasya lokavṛttād apeyuṣaḥ</li>
        <li id="R_4.018.021cd">daṇḍād anyatra paśyāmi nigrahaṃ hariyūthapa</li>
    </ul>
    <ul id="R_4.018.022">
        <li id="R_4.018.022ab">aurasīṃ bhaginīṃ vāpi bhāryāṃ vāpy anujasya yaḥ</li>
        <li id="R_4.018.022cd">pracareta naraḥ kāmāt tasya daṇḍo vadhaḥ smṛtaḥ</li>
    </ul>
    <ul id="R_4.018.023">
        <li id="R_4.018.023ab">bharatas tu mahīpālo vayaṃ tv ādeśavartinaḥ</li>
        <li id="R_4.018.023cd">tvaṃ ca dharmād atikrāntaḥ kathaṃ śakyam upekṣitum</li>
    </ul>
    <ul id="R_4.018.024">
        <li id="R_4.018.024ab">gurudharmavyatikrāntaṃ prājño dharmeṇa pālayan</li>
        <li id="R_4.018.024cd">bharataḥ kāmavṛttānāṃ nigrahe paryavasthitaḥ</li>
    </ul>
    <ul id="R_4.018.025">
        <li id="R_4.018.025ab">vayaṃ tu bharatādeśaṃ vidhiṃ kṛtvā harīśvara</li>
        <li id="R_4.018.025cd">tvadvidhān bhinnamaryādān niyantuṃ paryavasthitāḥ</li>
    </ul>
    <ul id="R_4.018.026">
        <li id="R_4.018.026ab">sugrīveṇa ca me sakhyaṃ lakṣmaṇena yathā tathā</li>
        <li id="R_4.018.026cd">dārarājyanimittaṃ ca niḥśreyasi rataḥ sa me</li>
    </ul>
    <ul id="R_4.018.027">
        <li id="R_4.018.027ab">pratijñā ca mayā dattā tadā vānarasaṃnidhau</li>
        <li id="R_4.018.027cd">pratijñā ca kathaṃ śakyā madvidhenānavekṣitum</li>
    </ul>
    <ul id="R_4.018.028">
        <li id="R_4.018.028ab">tad ebhiḥ kāraṇaiḥ sarvair mahadbhir dharmasaṃhitaiḥ</li>
        <li id="R_4.018.028cd">śāsanaṃ tava yad yuktaṃ tad bhavān anumanyatām</li>
    </ul>
    <ul id="R_4.018.029">
        <li id="R_4.018.029ab">sarvathā dharma ity eva draṣṭavyas tava nigrahaḥ</li>
        <li id="R_4.018.029cd">vayasyasyopakartavyaṃ dharmam evānupaśyatā</li>
    </ul>
    <ul id="R_4.018.030">
        <li id="R_4.018.030ab">rājabhir dhṛtadaṇḍās tu kṛtvā pāpāni mānavāḥ</li>
        <li id="R_4.018.030cd">nirmalāḥ svargam āyānti santaḥ sukṛtino yathā</li>
    </ul>
    <ul id="R_4.018.031">
        <li id="R_4.018.031ab">āryeṇa mama māndhātrā vyasanaṃ ghoram īpsitam</li>
        <li id="R_4.018.031cd">śramaṇena kṛte pāpe yathā pāpaṃ kṛtaṃ tvayā</li>
    </ul>
    <ul id="R_4.018.032">
        <li id="R_4.018.032ab">anyair api kṛtaṃ pāpaṃ pramattair vasudhādhipaiḥ</li>
        <li id="R_4.018.032cd">prāyaścittaṃ ca kurvanti tena tac chāmyate rajaḥ</li>
    </ul>
    <ul id="R_4.018.033">
        <li id="R_4.018.033ab">tad alaṃ paritāpena dharmataḥ parikalpitaḥ</li>
        <li id="R_4.018.033cd">vadho vānaraśārdūla na vayaṃ svavaśe sthitāḥ</li>
    </ul>
    <ul id="R_4.018.034">
        <li id="R_4.018.034ab">vāgurābhiś ca pāśaiś ca kūṭaiś ca vividhair narāḥ</li>
        <li id="R_4.018.034cd">praticchannāś ca dṛśyāś ca gṛhṇanti subahūn mṛgān</li>
        <li id="R_4.018.034ef">pradhāvitān vā vitrastān visrabdhān ativiṣṭhitān</li>
    </ul>
    <ul id="R_4.018.035">
        <li id="R_4.018.035ab">pramattān apramattān vā narā māṃsārthino bhṛśam</li>
        <li id="R_4.018.035cd">vidhyanti vimukhāṃś cāpi na ca doṣo 'tra vidyate</li>
    </ul>
    <ul id="R_4.018.036">
        <li id="R_4.018.036ab">yānti rājarṣayaś cātra mṛgayāṃ dharmakovidāḥ</li>
        <li id="R_4.018.036cd">tasmāt tvaṃ nihato yuddhe mayā bāṇena vānara</li>
        <li id="R_4.018.036ef">ayudhyan pratiyudhyan vā yasmāc chākhāmṛgo hy asi</li>
    </ul>
    <ul id="R_4.018.037">
        <li id="R_4.018.037ab">durlabhasya ca dharmasya jīvitasya śubhasya ca</li>
        <li id="R_4.018.037cd">rājāno vānaraśreṣṭha pradātāro na saṃśayaḥ</li>
    </ul>
    <ul id="R_4.018.038">
        <li id="R_4.018.038ab">tān na hiṃsyān na cākrośen nākṣipen nāpriyaṃ vadet</li>
        <li id="R_4.018.038cd">devā mānuṣarūpeṇa caranty ete mahītale</li>
    </ul>
    <ul id="R_4.018.039">
        <li id="R_4.018.039ab">tvaṃ tu dharmam avijñāya kevalaṃ roṣam āsthitaḥ</li>
        <li id="R_4.018.039cd">pradūṣayasi māṃ dharme pitṛpaitāmahe sthitam</li>
    </ul>
    <ul id="R_4.018.040">
        <li id="R_4.018.040ab">evam uktas tu rāmeṇa vālī pravyathito bhṛśam</li>
        <li id="R_4.018.040cd">pratyuvāca tato rāmaṃ prāñjalir vānareśvaraḥ</li>
    </ul>
    <ul id="R_4.018.041">
        <li id="R_4.018.041ab">yat tvam āttha naraśreṣṭha tad evaṃ nātra saṃśayaḥ</li>
        <li id="R_4.018.041cd">prativaktuṃ prakṛṣṭe hi nāpakṛṣṭas tu śaknuyāt</li>
    </ul>
    <ul id="R_4.018.042">
        <li id="R_4.018.042ab">yad ayuktaṃ mayā pūrvaṃ pramādād vākyam apriyam</li>
        <li id="R_4.018.042cd">tatrāpi khalu me doṣaṃ kartuṃ nārhasi rāghava</li>
    </ul>
    <ul id="R_4.018.043">
        <li id="R_4.018.043ab">tvaṃ hi dṛṣṭārthatattvajñaḥ prajānāṃ ca hite rataḥ</li>
        <li id="R_4.018.043cd">kāryakāraṇasiddhau te prasannā buddhir avyayā</li>
    </ul>
    <ul id="R_4.018.044">
        <li id="R_4.018.044ab">mām apy avagataṃ dharmād vyatikrāntapuraskṛtam</li>
        <li id="R_4.018.044cd">dharmasaṃhitayā vācā dharmajña paripālaya</li>
    </ul>
    <ul id="R_4.018.045">
        <li id="R_4.018.045ab">bāṣpasaṃruddhakaṇṭhas tu vālī sārtaravaḥ śanaiḥ</li>
        <li id="R_4.018.045cd">uvāca rāmaṃ saṃprekṣya paṅkalagna iva dvipaḥ</li>
    </ul>
    <ul id="R_4.018.046">
        <li id="R_4.018.046ab">na tv ātmānam ahaṃ śoce na tārāṃ nāpi bāndhavān</li>
        <li id="R_4.018.046cd">yathā putraṃ guṇaśreṣṭham aṅgadaṃ kanakāṅgadam</li>
    </ul>
    <ul id="R_4.018.047">
        <li id="R_4.018.047ab">sa mamādarśanād dīno bālyāt prabhṛti lālitaḥ</li>
        <li id="R_4.018.047cd">taṭāka iva pītāmbur upaśoṣaṃ gamiṣyati</li>
    </ul>
    <ul id="R_4.018.048">
        <li id="R_4.018.048ab">sugrīve cāṅgade caiva vidhatsva matim uttamām</li>
        <li id="R_4.018.048cd">tvaṃ hi śāstā ca goptā ca kāryākāryavidhau sthitaḥ</li>
    </ul>
    <ul id="R_4.018.049">
        <li id="R_4.018.049ab">yā te narapate vṛttir bharate lakṣmaṇe ca yā</li>
        <li id="R_4.018.049cd">sugrīve cāṅgade rājaṃs tāṃ cintayitum arhasi</li>
    </ul>
    <ul id="R_4.018.050">
        <li id="R_4.018.050ab">maddoṣakṛtadoṣāṃ tāṃ yathā tārāṃ tapasvinīm</li>
        <li id="R_4.018.050cd">sugrīvo nāvamanyeta tathāvasthātum arhasi</li>
    </ul>
    <ul id="R_4.018.051">
        <li id="R_4.018.051ab">tvayā hy anugṛhītena śakyaṃ rājyam upāsitum</li>
        <li id="R_4.018.051cd">tvadvaśe vartamānena tava cittānuvartinā</li>
    </ul>
    <ul id="R_4.018.052">
        <li id="R_4.018.052ab">sa tam āśvāsayad rāmo vālinaṃ vyaktadarśanam</li>
    </ul>
    <ul id="R_4.018.053">
        <li id="R_4.018.053ab">na vayaṃ bhavatā cintyā nāpy ātmā harisattama</li>
        <li id="R_4.018.053cd">vayaṃ bhavadviśeṣeṇa dharmataḥ kṛtaniścayāḥ</li>
    </ul>
    <ul id="R_4.018.054">
        <li id="R_4.018.054ab">daṇḍye yaḥ pātayed daṇḍaṃ daṇḍyo yaś cāpi daṇḍyate</li>
        <li id="R_4.018.054cd">kāryakāraṇasiddhārthāv ubhau tau nāvasīdataḥ</li>
    </ul>
    <ul id="R_4.018.055">
        <li id="R_4.018.055ab">tad bhavān daṇḍasaṃyogād asmād vigatakalmaṣaḥ</li>
        <li id="R_4.018.055cd">gataḥ svāṃ prakṛtiṃ dharmyāṃ dharmadṛṣṭtena vartmanā</li>
    </ul>
    <ul id="R_4.018.056">
        <li id="R_4.018.056ab">
            <seg type="pāda" n="a">sa tasya vākyaṃ madhuraṃ mahātmanaḥ</seg>
            <seg type="pāda" n="b">samāhitaṃ dharmapathānuvartinaḥ</seg>
        </li>
        <li id="R_4.018.056cd">
            <seg type="pāda" n="c">niśamya rāmasya raṇāvamardino</seg>
            <seg type="pāda" n="d">vacaḥ suyuktaṃ nijagāda vānaraḥ</seg>
        </li>
    </ul>
    <ul id="R_4.018.057">
        <li id="R_4.018.057ab">
            <seg type="pāda" n="a">śarābhitaptena vicetasā mayā</seg>
            <seg type="pāda" n="b">pradūṣitas tvaṃ yad ajānatā prabho</seg>
        </li>
        <li id="R_4.018.057cd">
            <seg type="pāda" n="c">idaṃ mahendropamabhīmavikrama</seg>
            <seg type="pāda" n="d">prasāditas tvaṃ kṣama me mahīśvara</seg>
        </li>
    </ul>
    <ul id="R_4.019.001">
        <li id="R_4.019.001ab">sa vānaramahārājaḥ śayānaḥ śaravikṣataḥ</li>
        <li id="R_4.019.001cd">pratyukto hetumadvākyair nottaraṃ pratyapadyata</li>
    </ul>
    <ul id="R_4.019.002">
        <li id="R_4.019.002ab">aśmabhiḥ paribhinnāṅgaḥ pādapair āhato bhṛśam</li>
        <li id="R_4.019.002cd">rāmabāṇena cākrānto jīvitānte mumoha saḥ</li>
    </ul>
    <ul id="R_4.019.003">
        <li id="R_4.019.003ab">taṃ bhāryābāṇamokṣeṇa rāmadattena saṃyuge</li>
        <li id="R_4.019.003cd">hataṃ plavagaśārdūlaṃ tārā śuśrāva vālinam</li>
    </ul>
    <ul id="R_4.019.004">
        <li id="R_4.019.004ab">sā saputrāpriyaṃ śrutvā vadhaṃ bhartuḥ sudāruṇam</li>
        <li id="R_4.019.004cd">niṣpapāta bhṛśaṃ trastā vividhād girigahvarāt</li>
    </ul>
    <ul id="R_4.019.005">
        <li id="R_4.019.005ab">ye tv aṅgadaparīvārā vānarā hi mahābalāḥ</li>
        <li id="R_4.019.005cd">te sakārmukam ālokya rāmaṃ trastāḥ pradudruvuḥ</li>
    </ul>
    <ul id="R_4.019.006">
        <li id="R_4.019.006ab">sā dadarśa tatas trastān harīn āpatato drutam</li>
        <li id="R_4.019.006cd">yūthād iva paribhraṣṭān mṛgān nihatayūthapān</li>
    </ul>
    <ul id="R_4.019.007">
        <li id="R_4.019.007ab">tān uvāca samāsādya duḥkhitān duḥkhitā satī</li>
        <li id="R_4.019.007cd">rāma vitrāsitān sarvān anubaddhān iveṣubhiḥ</li>
    </ul>
    <ul id="R_4.019.008">
        <li id="R_4.019.008ab">vānarā rājasiṃhasya yasya yūyaṃ puraḥsarāḥ</li>
        <li id="R_4.019.008cd">taṃ vihāya suvitrastāḥ kasmād dravata durgatāḥ</li>
    </ul>
    <ul id="R_4.019.009">
        <li id="R_4.019.009ab">rājyahetoḥ sa ced bhrātā bhrātā raudreṇa pātitaḥ</li>
        <li id="R_4.019.009cd">rāmeṇa prasṛtair dūrān mārgaṇair dūra pātibhiḥ</li>
    </ul>
    <ul id="R_4.019.010">
        <li id="R_4.019.010ab">kapipatnyā vacaḥ śrutvā kapayaḥ kāmarūpiṇaḥ</li>
        <li id="R_4.019.010cd">prāptakālam aviśliṣṭam ūcur vacanam aṅganām</li>
    </ul>
    <ul id="R_4.019.011">
        <li id="R_4.019.011ab">jīva putre nivartasya putraṃ rakṣasva cāndagam</li>
        <li id="R_4.019.011cd">antako rāma rūpeṇa hatvā nayati vālinam</li>
    </ul>
    <ul id="R_4.019.012">
        <li id="R_4.019.012ab">kṣiptān vṛkṣān samāvidhya vipulāś ca śilās tathā</li>
        <li id="R_4.019.012cd">vālī vajrasamair bāṇair vajreṇeva nipātitaḥ</li>
    </ul>
    <ul id="R_4.019.013">
        <li id="R_4.019.013ab">abhidrutam idaṃ sarvaṃ vidrutaṃ prasṛtaṃ balam</li>
        <li id="R_4.019.013cd">asmin plavagaśārdūle hate śakrasamaprabhe</li>
    </ul>
    <ul id="R_4.019.014">
        <li id="R_4.019.014ab">rakṣyatāṃ nagaraṃ śūrair aṅgadaś cābhiṣicyatām</li>
        <li id="R_4.019.014cd">padasthaṃ vālinaḥ putraṃ bhajiṣyanti plavaṃgamāḥ</li>
    </ul>
    <ul id="R_4.019.015">
        <li id="R_4.019.015ab">atha vā ruciraṃ sthānam iha te rucirānane</li>
        <li id="R_4.019.015cd">āviśanti hi durgāṇi kṣipram adyaiva vānarāḥ</li>
    </ul>
    <ul id="R_4.019.016">
        <li id="R_4.019.016ab">abhāryāḥ saha bhāryāś ca santy atra vanacāriṇaḥ</li>
        <li id="R_4.019.016cd">lubdhebhyo viprayuktebhyaḥ svebhyo nas tumulaṃ bhayam</li>
    </ul>
    <ul id="R_4.019.017">
        <li id="R_4.019.017ab">alpāntaragatānāṃ tu śrutvā vacanam aṅganā</li>
        <li id="R_4.019.017cd">ātmanaḥ pratirūpaṃ sā babhāṣe cāruhāsinī</li>
    </ul>
    <ul id="R_4.019.018">
        <li id="R_4.019.018ab">putreṇa mama kiṃ kāryaṃ kiṃ rājyena kim ātmanā</li>
        <li id="R_4.019.018cd">kapisiṃhe mahābhāge tasmin bhartari naśyati</li>
    </ul>
    <ul id="R_4.019.019">
        <li id="R_4.019.019ab">pādamūlaṃ gamiṣyāmi tasyaivāhaṃ mahātmanaḥ</li>
        <li id="R_4.019.019cd">yo 'sau rāmaprayuktena śareṇa vinipātitaḥ</li>
    </ul>
    <ul id="R_4.019.020">
        <li id="R_4.019.020ab">evam uktvā pradudrāva rudatī śokakarśitā</li>
        <li id="R_4.019.020cd">śiraś coraś ca bāhubhyāṃ duḥkhena samabhighnatī</li>
    </ul>
    <ul id="R_4.019.021">
        <li id="R_4.019.021ab">āvrajantī dadarśātha patiṃ nipatitaṃ bhuvi</li>
        <li id="R_4.019.021cd">hantāraṃ dānavendrāṇāṃ samareṣv anivartinām</li>
    </ul>
    <ul id="R_4.019.022">
        <li id="R_4.019.022ab">kṣeptāraṃ parvatendrāṇāṃ vajrāṇām iva vāsavam</li>
        <li id="R_4.019.022cd">mahāvātasamāviṣṭaṃ mahāmeghaughaniḥsvanam</li>
    </ul>
    <ul id="R_4.019.023">
        <li id="R_4.019.023ab">śakratulyaparākrāntaṃ vṛṣṭvevoparataṃ ghanam</li>
        <li id="R_4.019.023cd">nardantaṃ nardatāṃ bhīmaṃ śūraṃ śūreṇa pātitam</li>
    </ul>
    <ul id="R_4.019.024">
        <li id="R_4.019.024ab">śārdūlenāmiṣasyārthe mṛgarājaṃ yathā hatam</li>
        <li id="R_4.019.024cd">arcitaṃ sarvalokasya sapatākaṃ savedikam</li>
    </ul>
    <ul id="R_4.019.025">
        <li id="R_4.019.025ab">nāgahetoḥ suparṇena caityam unmathitaṃ yathā</li>
        <li id="R_4.019.025cd">avaṣṭabhyāvatiṣṭhantaṃ dadarśa dhanur ūrjitam</li>
    </ul>
    <ul id="R_4.019.026">
        <li id="R_4.019.026ab">rāmaṃ rāmānujaṃ caiva bhartuś caivānujaṃ śubhā</li>
        <li id="R_4.019.026cd">tān atītya samāsādya bhartāraṃ nihataṃ raṇe</li>
    </ul>
    <ul id="R_4.019.027">
        <li id="R_4.019.027ab">samīkṣya vyathitā bhūmau saṃbhrāntā nipapāta ha</li>
        <li id="R_4.019.027cd">supteva punar utthāya āryaputreti krośatī</li>
    </ul>
    <ul id="R_4.019.028">
        <li id="R_4.019.028ab">ruroda sā patiṃ dṛṣṭvā saṃditaṃ mṛtyudāmabhiḥ</li>
        <li id="R_4.019.028cd">tām avekṣya tu sugrīvaḥ krośantīṃ kurarīm iva</li>
    </ul>
    <ul id="R_4.019.029">
        <li id="R_4.019.029ab">viṣādam agamat kaṣṭaṃ dṛṣṭvā cāṅgadam āgatam</li>
    </ul>
    <ul id="R_4.020.001">
        <li id="R_4.020.001ab">rāmacāpavisṛṣṭena śareṇāntakareṇa tam</li>
        <li id="R_4.020.001cd">dṛṣṭvā vinihataṃ bhūmau tārā tārādhipānanā</li>
    </ul>
    <ul id="R_4.020.002">
        <li id="R_4.020.002ab">sā samāsādya bhartāraṃ paryaṣvajata bhāminī</li>
        <li id="R_4.020.002cd">iṣuṇābhihataṃ dṛṣṭvā vālinaṃ kuñjaropamam</li>
    </ul>
    <ul id="R_4.020.003">
        <li id="R_4.020.003ab">vānarendraṃ mahendrābhaṃ śokasaṃtaptamānasā</li>
        <li id="R_4.020.003cd">tārā tarum ivonmūlaṃ paryadevayad āturā</li>
    </ul>
    <ul id="R_4.020.004">
        <li id="R_4.020.004ab">raṇe dāruṇavikrānta pravīra plavatāṃ vara</li>
        <li id="R_4.020.004cd">kiṃ dīnām apurobhāgām adya tvaṃ nābhibhāṣase</li>
    </ul>
    <ul id="R_4.020.005">
        <li id="R_4.020.005ab">uttiṣṭha hariśārdūla bhajasva śayanottamam</li>
        <li id="R_4.020.005cd">naivaṃvidhāḥ śerate hi bhūmau nṛpatisattamāḥ</li>
    </ul>
    <ul id="R_4.020.006">
        <li id="R_4.020.006ab">atīva khalu te kāntā vasudhā vasudhādhipa</li>
        <li id="R_4.020.006cd">gatāsur api yāṃ gātrair māṃ vihāya niṣevase</li>
    </ul>
    <ul id="R_4.020.007">
        <li id="R_4.020.007ab">vyaktam anyā tvayā vīra dharmataḥ saṃpravartatā</li>
        <li id="R_4.020.007cd">kiṣkindheva purī ramyā svargamārge vinirmitā</li>
    </ul>
    <ul id="R_4.020.008">
        <li id="R_4.020.008ab">yāny asmābhis tvayā sārdhaṃ vaneṣu madhugandhiṣu</li>
        <li id="R_4.020.008cd">vihṛtāni tvayā kāle teṣām uparamaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_4.020.009">
        <li id="R_4.020.009ab">nirānandā nirāśāhaṃ nimagnā śokasāgare</li>
        <li id="R_4.020.009cd">tvayi pañcatvam āpanne mahāyūthapayūthape</li>
    </ul>
    <ul id="R_4.020.010">
        <li id="R_4.020.010ab">hṛdayaṃ susthiraṃ mahyaṃ dṛṣṭvā vinihataṃ bhuvi</li>
        <li id="R_4.020.010cd">yan na śokābhisaṃtaptaṃ sphuṭate 'dya sahasradhā</li>
    </ul>
    <ul id="R_4.020.011">
        <li id="R_4.020.011ab">sugrīvasya tvayā bhāryā hṛtā sa ca vivāsitaḥ</li>
        <li id="R_4.020.011cd">yat tat tasya tvayā vyuṣṭiḥ prāpteyaṃ plavagādhipa</li>
    </ul>
    <ul id="R_4.020.012">
        <li id="R_4.020.012ab">niḥśreyasaparā mohāt tvayā cāhaṃ vigarhitā</li>
        <li id="R_4.020.012cd">yaiṣābruvaṃ hitaṃ vākyaṃ vānarendrahitaiṣiṇī</li>
    </ul>
    <ul id="R_4.020.013">
        <li id="R_4.020.013ab">kālo niḥsaṃśayo nūnaṃ jīvitāntakaras tava</li>
        <li id="R_4.020.013cd">balād yenāvapanno 'si sugrīvasyāvaśo vaśam</li>
    </ul>
    <ul id="R_4.020.014">
        <li id="R_4.020.014ab">vaidhavyaṃ śokasaṃtāpaṃ kṛpaṇaṃ kṛpaṇā satī</li>
        <li id="R_4.020.014cd">aduḥkhopacitā pūrvaṃ vartayiṣyāmy anāthavat</li>
    </ul>
    <ul id="R_4.020.015">
        <li id="R_4.020.015ab">lālitaś cāṅgado vīraḥ sukumāraḥ sukhocitaḥ</li>
        <li id="R_4.020.015cd">vatsyate kām avasthāṃ me pitṛvye krodhamūrchite</li>
    </ul>
    <ul id="R_4.020.016">
        <li id="R_4.020.016ab">kuruṣva pitaraṃ putra sudṛṣṭaṃ dharmavatsalam</li>
        <li id="R_4.020.016cd">durlabhaṃ darśanaṃ tv asya tava vatsa bhaviṣyati</li>
    </ul>
    <ul id="R_4.020.017">
        <li id="R_4.020.017ab">samāśvāsaya putraṃ tvaṃ saṃdeśaṃ saṃdiśasva ca</li>
        <li id="R_4.020.017cd">mūrdhni cainaṃ samāghrāya pravāsaṃ prasthito hy asi</li>
    </ul>
    <ul id="R_4.020.018">
        <li id="R_4.020.018ab">rāmeṇa hi mahat karma kṛtaṃ tvām abhinighnatā</li>
        <li id="R_4.020.018cd">ānṛṇyaṃ tu gataṃ tasya sugrīvasya pratiśrave</li>
    </ul>
    <ul id="R_4.020.019">
        <li id="R_4.020.019ab">sakāmo bhava sugrīva rumāṃ tvaṃ pratipatsyase</li>
        <li id="R_4.020.019cd">bhuṅkṣva rājyam anudvignaḥ śasto bhrātā ripus tava</li>
    </ul>
    <ul id="R_4.020.020">
        <li id="R_4.020.020ab">kiṃ mām evaṃ vilapatīṃ preṃṇā tvaṃ nābhibhāṣase</li>
        <li id="R_4.020.020cd">imāḥ paśya varā bahvīr bhāryās te vānareśvara</li>
    </ul>
    <ul id="R_4.020.021">
        <li id="R_4.020.021ab">tasyā vilapitaṃ śrutvā vānaryaḥ sarvataś ca tāḥ</li>
        <li id="R_4.020.021cd">parigṛhyāṅgadaṃ dīnaṃ duḥkhārtāḥ paricukruśuḥ</li>
    </ul>
    <ul id="R_4.020.022">
        <li id="R_4.020.022ab">
            <seg type="pāda" n="a">kim aṅgadaṃ sāṅgada vīra bāho</seg>
            <seg type="pāda" n="b">vihāya yāsy adya cirapravāsaṃ</seg>
        </li>
        <li id="R_4.020.022cd">
            <seg type="pāda" n="c">na yuktam evaṃ guṇasaṃnikṛṣṭaṃ</seg>
            <seg type="pāda" n="d">vihāya putraṃ priyaputra gantum</seg>
        </li>
    </ul>
    <ul id="R_4.020.023">
        <li id="R_4.020.023ab">
            <seg type="pāda" n="a">kim apriyaṃ te priyacāruveṣa</seg>
            <seg type="pāda" n="b">kṛtaṃ mayā nātha sutena vā te</seg>
        </li>
        <li id="R_4.020.023cd">
            <seg type="pāda" n="c">sahāyinīm adya vihāya vīra</seg>
            <seg type="pāda" n="d">yamakṣayaṃ gacchasi durvinītam</seg>
        </li>
    </ul>
    <ul id="R_4.020.024">
        <li id="R_4.020.024ab">
            <seg type="pāda" n="a">yady apriyaṃ kiṃ cid asaṃpradhārya</seg>
            <seg type="pāda" n="b">kṛtaṃ mayā syāt tava dīrghabāho</seg>
        </li>
        <li id="R_4.020.024cd">
            <seg type="pāda" n="c">kṣamasva me tad dharivaṃśa nātha</seg>
            <seg type="pāda" n="d">vrajāmi mūrdhnā tava vīra pādau</seg>
        </li>
    </ul>
    <ul id="R_4.020.025">
        <li id="R_4.020.025ab">
            <seg type="pāda" n="a">tathā tu tārā karuṇaṃ rudantī</seg>
            <seg type="pāda" n="b">bhartuḥ samīpe saha vānarībhiḥ</seg>
        </li>
        <li id="R_4.020.025cd">
            <seg type="pāda" n="c">vyavasyata prāyam anindyavarṇā</seg>
            <seg type="pāda" n="d">upopaveṣṭuṃ bhuvi yatra vālī</seg>
        </li>
    </ul>
    <ul id="R_4.021.001">
        <li id="R_4.021.001ab">tato nipatitāṃ tārāṃ cyutāṃ tārām ivāmbarāt</li>
        <li id="R_4.021.001cd">śanair āśvāsayām āsa hanūmān hariyūthapaḥ</li>
    </ul>
    <ul id="R_4.021.002">
        <li id="R_4.021.002ab">guṇadoṣakṛtaṃ jantuḥ svakarmaphalahetukam</li>
        <li id="R_4.021.002cd">avyagras tad avāpnoti sarvaṃ pretya śubhāśubham</li>
    </ul>
    <ul id="R_4.021.003">
        <li id="R_4.021.003ab">śocyā śocasi kaṃ śocyaṃ dīnaṃ dīnānukampase</li>
        <li id="R_4.021.003cd">kaś ca kasyānuśocyo 'sti dehe 'smin budbudopame</li>
    </ul>
    <ul id="R_4.021.004">
        <li id="R_4.021.004ab">aṅgadas tu kumāro 'yaṃ draṣṭavyo jīvaputrayā</li>
        <li id="R_4.021.004cd">āyatyā ca vidheyāni samarthāny asya cintaya</li>
    </ul>
    <ul id="R_4.021.005">
        <li id="R_4.021.005ab">jānāsy aniyatām evaṃ bhūtānām āgatiṃ gatim</li>
        <li id="R_4.021.005cd">tasmāc chubhaṃ hi kartavyaṃ paṇḍite naihalaukikam</li>
    </ul>
    <ul id="R_4.021.006">
        <li id="R_4.021.006ab">yasmin harisahasrāṇi prayutāny arbudāni ca</li>
        <li id="R_4.021.006cd">vartayanti kṛtāṃśāni so 'yaṃ diṣṭāntam āgataḥ</li>
    </ul>
    <ul id="R_4.021.007">
        <li id="R_4.021.007ab">yad ayaṃ nyāyadṛṣṭārthaḥ sāmadānakṣamāparaḥ</li>
        <li id="R_4.021.007cd">gato dharmajitāṃ bhūmiṃ nainaṃ śocitum arhasi</li>
    </ul>
    <ul id="R_4.021.008">
        <li id="R_4.021.008ab">sarve ca hariśārdūla putraś cāyaṃ tavāṅgadaḥ</li>
        <li id="R_4.021.008cd">haryṛṣkapatirājyaṃ ca tvatsanātham anindite</li>
    </ul>
    <ul id="R_4.021.009">
        <li id="R_4.021.009ab">tāv imau śokasaṃtaptau śanaiḥ preraya bhāmini</li>
        <li id="R_4.021.009cd">tvayā parigṛhīto 'yam aṅgadaḥ śāstu medinīm</li>
    </ul>
    <ul id="R_4.021.010">
        <li id="R_4.021.010ab">saṃtatiś ca yathādṛṣṭā kṛtyaṃ yac cāpi sāmpratam</li>
        <li id="R_4.021.010cd">rājñas tat kriyatāṃ sarvam eṣa kālasya niścayaḥ</li>
    </ul>
    <ul id="R_4.021.011">
        <li id="R_4.021.011ab">saṃskāryo harirājas tu aṅgadaś cābhiṣicyatām</li>
        <li id="R_4.021.011cd">siṃhāsanagataṃ putraṃ paśyantī śāntim eṣyasi</li>
    </ul>
    <ul id="R_4.021.012">
        <li id="R_4.021.012ab">sā tasya vacanaṃ śrutvā bhartṛvyasanapīḍitā</li>
        <li id="R_4.021.012cd">abravīd uttaraṃ tārā hanūmantam avasthitam</li>
    </ul>
    <ul id="R_4.021.013">
        <li id="R_4.021.013ab">aṅgada pratirūpāṇāṃ putrāṇām ekataḥ śatam</li>
        <li id="R_4.021.013cd">hatasyāpy asya vīrasya gātrasaṃśleṣaṇaṃ varam</li>
    </ul>
    <ul id="R_4.021.014">
        <li id="R_4.021.014ab">na cāhaṃ harirājasya prabhavāmy aṅgadasya vā</li>
        <li id="R_4.021.014cd">pitṛvyastasya sugrīvaḥ sarvakāryeṣv anantaraḥ</li>
    </ul>
    <ul id="R_4.021.015">
        <li id="R_4.021.015ab">na hy eṣā buddhir āstheyā hanūmann aṅgadaṃ prati</li>
        <li id="R_4.021.015cd">pitā hi bandhuḥ putrasya na mātā harisattama</li>
    </ul>
    <ul id="R_4.021.016">
        <li id="R_4.021.016ab">
            <seg type="pāda" n="a">na hi mama harirājasaṃśrayāt</seg>
            <seg type="pāda" n="b">kṣamataram asti paratra ceha vā</seg>
        </li>
        <li id="R_4.021.016cd">
            <seg type="pāda" n="c">abhimukhahatavīrasevitaṃ</seg>
            <seg type="pāda" n="d">śayanam idaṃ mama sevituṃ kṣamam</seg>
        </li>
    </ul>
    <ul id="R_4.022.001">
        <li id="R_4.022.001ab">vīkṣamāṇas tu mandāsuḥ sarvato mandam ucchvasan</li>
        <li id="R_4.022.001cd">ādāv eva tu sugrīvaṃ dadarśa tv ātmajāgrataḥ</li>
    </ul>
    <ul id="R_4.022.002">
        <li id="R_4.022.002ab">taṃ prāptavijayaṃ vālī sugrīvaṃ plavageśvaram</li>
        <li id="R_4.022.002cd">ābhāṣya vyaktayā vācā sasneham idam abravīt</li>
    </ul>
    <ul id="R_4.022.003">
        <li id="R_4.022.003ab">sugrīvadoṣeṇa na māṃ gantum arhasi kilbiṣāt</li>
        <li id="R_4.022.003cd">kṛṣyamāṇaṃ bhaviṣyeṇa buddhimohena māṃ balāt</li>
    </ul>
    <ul id="R_4.022.004">
        <li id="R_4.022.004ab">yugapadvihitaṃ tāta na manye sukham āvayoḥ</li>
        <li id="R_4.022.004cd">sauhārdaṃ bhrātṛyuktaṃ hi tad idaṃ jātam anyathā</li>
    </ul>
    <ul id="R_4.022.005">
        <li id="R_4.022.005ab">pratipadya tvam adyaiva rājyam eṣāṃ vanaukasām</li>
        <li id="R_4.022.005cd">mām apy adyaiva gacchantaṃ viddhi vaivasvatakṣayam</li>
    </ul>
    <ul id="R_4.022.006">
        <li id="R_4.022.006ab">jīvitaṃ ca hi rājyaṃ ca śriyaṃ ca vipulām imām</li>
        <li id="R_4.022.006cd">prajahāmy eṣa vai tūrṇaṃ mahac cāgarhitaṃ yaśaḥ</li>
    </ul>
    <ul id="R_4.022.007">
        <li id="R_4.022.007ab">asyāṃ tv aham avasthāyāṃ vīra vakṣyāmi yad vacaḥ</li>
        <li id="R_4.022.007cd">yady apy asukaraṃ rājan kartum eva tad arhasi</li>
    </ul>
    <ul id="R_4.022.008">
        <li id="R_4.022.008ab">sukhārhaṃ sukhasaṃvṛddhaṃ bālam enam abāliśam</li>
        <li id="R_4.022.008cd">bāṣpapūrṇamukhaṃ paśya bhūmau patitam aṅgadam</li>
    </ul>
    <ul id="R_4.022.009">
        <li id="R_4.022.009ab">mama prāṇaiḥ priyataraṃ putraṃ putram ivaurasaṃ</li>
        <li id="R_4.022.009cd">mayā hīnam ahīnārthaṃ sarvataḥ paripālaya</li>
    </ul>
    <ul id="R_4.022.010">
        <li id="R_4.022.010ab">tvam apy asya hi dātā ca paritrātā ca sarvataḥ</li>
        <li id="R_4.022.010cd">bhayeṣv abhayadaś caiva yathāhaṃ plavageśvara</li>
    </ul>
    <ul id="R_4.022.011">
        <li id="R_4.022.011ab">eṣa tārātmajaḥ śrīmāṃs tvayā tulyaparākramaḥ</li>
        <li id="R_4.022.011cd">rakṣasāṃ tu vadhe teṣām agratas te bhaviṣyati</li>
    </ul>
    <ul id="R_4.022.012">
        <li id="R_4.022.012ab">anurūpāṇi karmāṇi vikramya balavān raṇe</li>
        <li id="R_4.022.012cd">kariṣyaty eṣa tāreyas tarasvī taruṇo 'ṅgadaḥ</li>
    </ul>
    <ul id="R_4.022.013">
        <li id="R_4.022.013ab">suṣeṇaduhitā ceyam arthasūkṣmaviniścaye</li>
        <li id="R_4.022.013cd">autpātike ca vividhe sarvataḥ pariniṣṭhitā</li>
    </ul>
    <ul id="R_4.022.014">
        <li id="R_4.022.014ab">yad eṣā sādhv iti brūyāt kāryaṃ tan muktasaṃśayam</li>
        <li id="R_4.022.014cd">na hi tārāmataṃ kiṃ cid anyathā parivartate</li>
    </ul>
    <ul id="R_4.022.015">
        <li id="R_4.022.015ab">rāghavasya ca te kāryaṃ kartavyam aviśaṅkayā</li>
        <li id="R_4.022.015cd">syād adharmo hy akaraṇe tvāṃ ca hiṃsyād vimānitaḥ</li>
    </ul>
    <ul id="R_4.022.016">
        <li id="R_4.022.016ab">imāṃ ca mālām ādhatsva divyāṃ sugrīvakāñcanīm</li>
        <li id="R_4.022.016cd">udārā śrīḥ sthitā hy asyāṃ saṃprajahyān mṛte mayi</li>
    </ul>
    <ul id="R_4.022.017">
        <li id="R_4.022.017ab">ity evam uktaḥ sugrīvo vālinā bhrātṛsauhṛdāt</li>
        <li id="R_4.022.017cd">harṣaṃ tyaktvā punar dīno grahagrasta ivoḍurāṭ</li>
    </ul>
    <ul id="R_4.022.018">
        <li id="R_4.022.018ab">tad vālivacanāc chāntaḥ kurvan yuktam atandritaḥ</li>
        <li id="R_4.022.018cd">jagrāha so 'bhyanujñāto mālāṃ tāṃ caiva kāñcanīm</li>
    </ul>
    <ul id="R_4.022.019">
        <li id="R_4.022.019ab">tāṃ mālāṃ kāñcanīṃ dattvā vālī dṛṣṭvātmajaṃ sthitam</li>
        <li id="R_4.022.019cd">saṃsiddhaḥ pretya bhāvāya snehād aṅgadam abravīt</li>
    </ul>
    <ul id="R_4.022.020">
        <li id="R_4.022.020ab">deśakālau bhajasvādya kṣamamāṇaḥ priyāpriye</li>
        <li id="R_4.022.020cd">sukhaduḥkhasahaḥ kāle sugrīvavaśago bhava</li>
    </ul>
    <ul id="R_4.022.021">
        <li id="R_4.022.021ab">yathā hi tvaṃ mahābāho lālitaḥ satataṃ mayā</li>
        <li id="R_4.022.021cd">na tathā vartamānaṃ tvāṃ sugrīvo bahu maṃsyate</li>
    </ul>
    <ul id="R_4.022.022">
        <li id="R_4.022.022ab">māsyāmitrair gataṃ gaccher mā śatrubhir ariṃdama</li>
        <li id="R_4.022.022cd">bhartur arthaparo dāntaḥ sugrīvavaśago bhava</li>
    </ul>
    <ul id="R_4.022.023">
        <li id="R_4.022.023ab">na cātipraṇayaḥ kāryaḥ kartavyo 'praṇayaś ca te</li>
        <li id="R_4.022.023cd">ubhayaṃ hi mahādoṣaṃ tasmād antaradṛg bhava</li>
    </ul>
    <ul id="R_4.022.024">
        <li id="R_4.022.024ab">ity uktvātha vivṛttākṣaḥ śarasaṃpīḍito bhṛśam</li>
        <li id="R_4.022.024cd">vivṛtair daśanair bhīmair babhūvotkrāntajīvitaḥ</li>
    </ul>
    <ul id="R_4.022.025">
        <li id="R_4.022.025ab">
            <seg type="pāda" n="a">hate tu vīre plavagādhipe tadā</seg>
            <seg type="pāda" n="b">plavaṃgamās tatra na śarma lebhire</seg>
        </li>
        <li id="R_4.022.025cd">
            <seg type="pāda" n="c">vanecarāḥ siṃhayute mahāvane</seg>
            <seg type="pāda" n="d">yathā hi gāvo nihate gavāṃ patau</seg>
        </li>
    </ul>
    <ul id="R_4.022.026">
        <li id="R_4.022.026ab">
            <seg type="pāda" n="a">tatas tu tārā vyasanārṇava plutā</seg>
            <seg type="pāda" n="b">mṛtasyā bhartur vadanaṃ samīkṣya sā</seg>
        </li>
        <li id="R_4.022.026cd">
            <seg type="pāda" n="c">jagāma bhūmiṃ parirabhya vālinaṃ</seg>
            <seg type="pāda" n="d">mahādrumaṃ chinnam ivāśritā latā</seg>
        </li>
    </ul>
    <ul id="R_4.023.001">
        <li id="R_4.023.001ab">tataḥ samupajighrantī kapirājasya tanmukham</li>
        <li id="R_4.023.001cd">patiṃ lokāc cyutaṃ tārā mṛtaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.023.002">
        <li id="R_4.023.002ab">śeṣe tvaṃ viṣame duḥkham akṛtvā vacanaṃ mama</li>
        <li id="R_4.023.002cd">upalopacite vīra suduḥkhe vasudhātale</li>
    </ul>
    <ul id="R_4.023.003">
        <li id="R_4.023.003ab">mattaḥ priyatarā nūnaṃ vānarendra mahī tava</li>
        <li id="R_4.023.003cd">śeṣe hi tāṃ pariṣvajya māṃ ca na pratibhāṣase</li>
    </ul>
    <ul id="R_4.023.004">
        <li id="R_4.023.004ab">sugrīva eva vikrānto vīra sāhasika priya</li>
        <li id="R_4.023.004cd">ṛkṣavānaramukhyās tvāṃ balinaṃ paryupāsate</li>
    </ul>
    <ul id="R_4.023.005">
        <li id="R_4.023.005ab">eṣāṃ vilapitaṃ kṛcchram aṅgadasya ca śocataḥ</li>
        <li id="R_4.023.005cd">mama cemāṃ giraṃ śrutvā kiṃ tvaṃ na pratibudhyase</li>
    </ul>
    <ul id="R_4.023.006">
        <li id="R_4.023.006ab">idaṃ tac chūraśayanaṃ yatra śeṣe hato yudhi</li>
        <li id="R_4.023.006cd">śāyitā nihatā yatra tvayaiva ripavaḥ purā</li>
    </ul>
    <ul id="R_4.023.007">
        <li id="R_4.023.007ab">viśuddhasattvābhijana priyayuddha mama priya</li>
        <li id="R_4.023.007cd">mām anāthāṃ vihāyaikāṃ gatas tvam asi mānada</li>
    </ul>
    <ul id="R_4.023.008">
        <li id="R_4.023.008ab">śūrāya na pradātavyā kanyā khalu vipaścitā</li>
        <li id="R_4.023.008cd">śūrabhāryāṃ hatāṃ paśya sadyo māṃ vidhavāṃ kṛtām</li>
    </ul>
    <ul id="R_4.023.009">
        <li id="R_4.023.009ab">avabhagnaś ca me māno bhagnā me śāśvatī gatiḥ</li>
        <li id="R_4.023.009cd">agādhe ca nimagnāsmi vipule śokasāgare</li>
    </ul>
    <ul id="R_4.023.010">
        <li id="R_4.023.010ab">aśmasāramayaṃ nūnam idaṃ me hṛdayaṃ dṛḍham</li>
        <li id="R_4.023.010cd">bhartāraṃ nihataṃ dṛṣṭvā yan nādya śatadhā gatam</li>
    </ul>
    <ul id="R_4.023.011">
        <li id="R_4.023.011ab">suhṛc caiva hi bhartā ca prakṛtyā ca mama priyaḥ</li>
        <li id="R_4.023.011cd">āhave ca parākrāntaḥ śūraḥ pañcatvam āgataḥ</li>
    </ul>
    <ul id="R_4.023.012">
        <li id="R_4.023.012ab">patihīnā tu yā nārī kāmaṃ bhavatu putriṇī</li>
        <li id="R_4.023.012cd">dhanadhānyaiḥ supūrṇāpi vidhavety ucyate budhaiḥ</li>
    </ul>
    <ul id="R_4.023.013">
        <li id="R_4.023.013ab">svagātraprabhave vīra śeṣe rudhiramaṇḍale</li>
        <li id="R_4.023.013cd">kṛmirāgaparistome tvam evaṃ śayane yathā</li>
    </ul>
    <ul id="R_4.023.014">
        <li id="R_4.023.014ab">reṇuśoṇitasaṃvītaṃ gātraṃ tava samantataḥ</li>
        <li id="R_4.023.014cd">parirabdhuṃ na śaknomi bhujābhyāṃ plavagarṣabha</li>
    </ul>
    <ul id="R_4.023.015">
        <li id="R_4.023.015ab">kṛtakṛtyo 'dya sugrīvo vaire 'sminn atidāruṇe</li>
        <li id="R_4.023.015cd">yasya rāmavimuktena hṛtam ekeṣuṇā bhayam</li>
    </ul>
    <ul id="R_4.023.016">
        <li id="R_4.023.016ab">śareṇa hṛdi lagnena gātrasaṃsparśane tava</li>
        <li id="R_4.023.016cd">vāryāmi tvāṃ nirīkṣantī tvayi pañcatvam āgate</li>
    </ul>
    <ul id="R_4.023.017">
        <li id="R_4.023.017ab">udbabarha śaraṃ nīlas tasya gātragataṃ tadā</li>
        <li id="R_4.023.017cd">girigahvarasaṃlīnaṃ dīptam āśīviṣaṃ yathā</li>
    </ul>
    <ul id="R_4.023.018">
        <li id="R_4.023.018ab">tasya niṣkṛṣyamāṇasya bāṇasya ca babhau dyutiḥ</li>
        <li id="R_4.023.018cd">astamastakasaṃruddho raśmir dinakarād iva</li>
    </ul>
    <ul id="R_4.023.019">
        <li id="R_4.023.019ab">petuḥ kṣatajadhārās tu vraṇebhyas tasya sarvaśaḥ</li>
        <li id="R_4.023.019cd">tāmragairikasaṃpṛktā dhārā iva dharādharāt</li>
    </ul>
    <ul id="R_4.023.020">
        <li id="R_4.023.020ab">avakīrṇaṃ vimārjantī bhartāraṃ raṇareṇunā</li>
        <li id="R_4.023.020cd">asrair nayanajaiḥ śūraṃ siṣecāstrasamāhatam</li>
    </ul>
    <ul id="R_4.023.021">
        <li id="R_4.023.021ab">rudhirokṣitasarvāṅgaṃ dṛṣṭvā vinihataṃ patim</li>
        <li id="R_4.023.021cd">uvāca tārā piṅgākṣaṃ putram aṅgadam aṅganā</li>
    </ul>
    <ul id="R_4.023.022">
        <li id="R_4.023.022ab">avasthāṃ paścimāṃ paśya pituḥ putra sudāruṇām</li>
        <li id="R_4.023.022cd">saṃprasaktasya vairasya gato 'ntaḥ pāpakarmaṇā</li>
    </ul>
    <ul id="R_4.023.023">
        <li id="R_4.023.023ab">bālasūryodayatanuṃ prayāntaṃ yamasādanam</li>
        <li id="R_4.023.023cd">abhivādaya rājānaṃ pitaraṃ putra mānadam</li>
    </ul>
    <ul id="R_4.023.024">
        <li id="R_4.023.024ab">evam uktaḥ samutthāya jagrāha caraṇau pituḥ</li>
        <li id="R_4.023.024cd">bhujābhyāṃ pīnavṛtābhyām aṅgado 'ham iti bruvan</li>
    </ul>
    <ul id="R_4.023.025">
        <li id="R_4.023.025ab">abhivādayamānaṃ tvām aṅgadaṃ tvaṃ yathāpurā</li>
        <li id="R_4.023.025cd">dīrghāyur bhava putreti kimarthaṃ nābhibhāṣase</li>
    </ul>
    <ul id="R_4.023.026">
        <li id="R_4.023.026ab">ahaṃ putrasahāyā tvām upāse gatacetanam</li>
        <li id="R_4.023.026cd">siṃhena nihataṃ sadyo gauḥ savatseva govṛṣam</li>
    </ul>
    <ul id="R_4.023.027">
        <li id="R_4.023.027ab">iṣṭvā saṃgrāmayajñena nānāpraharaṇāmbhasā</li>
        <li id="R_4.023.027cd">asminn avabhṛthe snātaḥ kathaṃ patnyā mayā vinā</li>
    </ul>
    <ul id="R_4.023.028">
        <li id="R_4.023.028ab">yā dattā devarājena tava tuṣṭena saṃyuge</li>
        <li id="R_4.023.028cd">śātakumbhamayīṃ mālāṃ tāṃ te paśyāmi neha kim</li>
    </ul>
    <ul id="R_4.023.029">
        <li id="R_4.023.029ab">rājaśrīr na jahāti tvāṃ gatāsum api mānada</li>
        <li id="R_4.023.029cd">sūryasyāvartamānasya śailarājam iva prabhā</li>
    </ul>
    <ul id="R_4.023.030">
        <li id="R_4.023.030ab">
            <seg type="pāda" n="a">na me vacaḥ pathyam idaṃ tvayā kṛtaṃ</seg>
            <seg type="pāda" n="b">na cāsmi śaktā hi nivāraṇe tava</seg>
        </li>
        <li id="R_4.023.030cd">
            <seg type="pāda" n="c">hatā saputrāsmi hatena saṃyuge</seg>
            <seg type="pāda" n="d">saha tvayā śrīr vijahāti mām iha</seg>
        </li>
    </ul>
    <ul id="R_4.024.001">
        <li id="R_4.024.001ab">gatāsuṃ vālinaṃ dṛṣṭvā rāghavas tadanantaram</li>
        <li id="R_4.024.001cd">abravīt praśritaṃ vākyaṃ sugrīvaṃ śatrutāpanaḥ</li>
    </ul>
    <ul id="R_4.024.002">
        <li id="R_4.024.002ab">na śokaparitāpena śreyasā yujyate mṛtaḥ</li>
        <li id="R_4.024.002cd">yad atrānantaraṃ kāryaṃ tat samādhātum arhatha</li>
    </ul>
    <ul id="R_4.024.003">
        <li id="R_4.024.003ab">lokavṛttam anuṣṭheyaṃ kṛtaṃ vo bāṣpamokṣaṇam</li>
        <li id="R_4.024.003cd">na kālād uttaraṃ kiṃ cit karma śakyam upāsitum</li>
    </ul>
    <ul id="R_4.024.004">
        <li id="R_4.024.004ab">niyataḥ kāraṇaṃ loke niyatiḥ karmasādhanam</li>
        <li id="R_4.024.004cd">niyatiḥ sarvabhūtānāṃ niyogeṣv iha kāraṇam</li>
    </ul>
    <ul id="R_4.024.005">
        <li id="R_4.024.005ab">na kartā kasya cit kaś cin niyoge cāpi neśvaraḥ</li>
        <li id="R_4.024.005cd">svabhāve vartate lokas tasya kālaḥ parāyaṇam</li>
    </ul>
    <ul id="R_4.024.006">
        <li id="R_4.024.006ab">na kālaḥ kālam atyeti na kālaḥ parihīyate</li>
        <li id="R_4.024.006cd">svabhāvaṃ vā samāsādya na kaś cid ativartate</li>
    </ul>
    <ul id="R_4.024.007">
        <li id="R_4.024.007ab">na kālasyāsti bandhutvaṃ na hetur na parākramaḥ</li>
        <li id="R_4.024.007cd">na mitrajñātisaṃbandhaḥ kāraṇaṃ nātmano vaśaḥ</li>
    </ul>
    <ul id="R_4.024.008">
        <li id="R_4.024.008ab">kiṃ tu kāla parīṇāmo draṣṭavyaḥ sādhu paśyatā</li>
        <li id="R_4.024.008cd">dharmaś cārthaś ca kāmaś ca kālakramasamāhitāḥ</li>
    </ul>
    <ul id="R_4.024.009">
        <li id="R_4.024.009ab">itaḥ svāṃ prakṛtiṃ vālī gataḥ prāptaḥ kriyāphalam</li>
        <li id="R_4.024.009cd">dharmārthakāmasaṃyogaiḥ pavitraṃ plavageśvara</li>
    </ul>
    <ul id="R_4.024.010">
        <li id="R_4.024.010ab">svadharmasya ca saṃyogāj jitas tena mahātmanā</li>
        <li id="R_4.024.010cd">svargaḥ parigṛhītaś ca prāṇān aparirakṣatā</li>
    </ul>
    <ul id="R_4.024.011">
        <li id="R_4.024.011ab">eṣā vai niyatiḥ śreṣṭhā yāṃ gato hariyūthapaḥ</li>
        <li id="R_4.024.011cd">tad alaṃ paritāpena prāptakālam upāsyatām</li>
    </ul>
    <ul id="R_4.024.012">
        <li id="R_4.024.012ab">vacanānte tu rāmasya lakṣmaṇaḥ paravīrahā</li>
        <li id="R_4.024.012cd">avadat praśritaṃ vākyaṃ sugrīvaṃ gatacetasaṃ</li>
    </ul>
    <ul id="R_4.024.013">
        <li id="R_4.024.013ab">kuru tvam asya sugrīva pretakāryam anantaram</li>
        <li id="R_4.024.013cd">tārāṅgadābhyāṃ sahito vālino dahanaṃ prati</li>
    </ul>
    <ul id="R_4.024.014">
        <li id="R_4.024.014ab">samājñāpaya kāṣṭhāni śuṣkāṇi ca bahūni ca</li>
        <li id="R_4.024.014cd">candanāni ca divyāni vālisaṃskārakāraṇāt</li>
    </ul>
    <ul id="R_4.024.015">
        <li id="R_4.024.015ab">samāśvāsaya cainaṃ tvam aṅgadaṃ dīnacetasaṃ</li>
        <li id="R_4.024.015cd">mā bhūr bāliśabuddhis tvaṃ tvadadhīnam idaṃ puram</li>
    </ul>
    <ul id="R_4.024.016">
        <li id="R_4.024.016ab">aṅgadas tv ānayen mālyaṃ vastrāṇi vividhāni ca</li>
        <li id="R_4.024.016cd">ghṛtaṃ tailam atho gandhān yac cātra samanantaram</li>
    </ul>
    <ul id="R_4.024.017">
        <li id="R_4.024.017ab">tvaṃ tāra śibikāṃ śīghram ādāyāgaccha saṃbhramāt</li>
        <li id="R_4.024.017cd">tvarā guṇavatī yuktā hy asmin kāle viśeṣataḥ</li>
    </ul>
    <ul id="R_4.024.018">
        <li id="R_4.024.018ab">sajjībhavantu plavagāḥ śibikāvāhanocitāḥ</li>
        <li id="R_4.024.018cd">samarthā balinaś caiva nirhariṣyanti vālinam</li>
    </ul>
    <ul id="R_4.024.019">
        <li id="R_4.024.019ab">evam uktvā tu sugrīvaṃ sumitrānandavardhanaḥ</li>
        <li id="R_4.024.019cd">tasthau bhrātṛsamīpastho lakṣmaṇaḥ paravīrahā</li>
    </ul>
    <ul id="R_4.024.020">
        <li id="R_4.024.020ab">lakṣmaṇasya vacaḥ śrutvā tāraḥ saṃbhrāntamānasaḥ</li>
        <li id="R_4.024.020cd">praviveśa guhāṃ śīghraṃ śibikāsaktamānasaḥ</li>
    </ul>
    <ul id="R_4.024.021">
        <li id="R_4.024.021ab">ādāya śibikāṃ tāraḥ sa tu paryāpayat punaḥ</li>
        <li id="R_4.024.021cd">vānarair uhyamānāṃ tāṃ śūrair udvahanocitaiḥ</li>
    </ul>
    <ul id="R_4.024.022">
        <li id="R_4.024.022ab">tato vālinam udyamya sugrīvaḥ śibikāṃ tadā</li>
        <li id="R_4.024.022cd">āropayata vikrośann aṅgadena sahaiva tu</li>
    </ul>
    <ul id="R_4.024.023">
        <li id="R_4.024.023ab">āropya śibikāṃ caiva vālinaṃ gatajīvitam</li>
        <li id="R_4.024.023cd">alaṃkāraiś ca vividhair mālyair vastraiś ca bhūṣitam</li>
    </ul>
    <ul id="R_4.024.024">
        <li id="R_4.024.024ab">ājñāpayat tadā rājā sugrīvaḥ plavageśvaraḥ</li>
        <li id="R_4.024.024cd">aurdhvadehikam āryasya kriyatām anurūpataḥ</li>
    </ul>
    <ul id="R_4.024.025">
        <li id="R_4.024.025ab">viśrāṇayanto ratnāni vividhāni bahūni ca</li>
        <li id="R_4.024.025cd">agrataḥ plavagā yāntu śibikā tadanantaram</li>
    </ul>
    <ul id="R_4.024.026">
        <li id="R_4.024.026ab">rājñām ṛddhiviśeṣā hi dṛśyante bhuvi yādṛśāḥ</li>
        <li id="R_4.024.026cd">tādṛśaṃ vālinaḥ kṣipraṃ prākurvann aurdhvadehikam</li>
    </ul>
    <ul id="R_4.024.027">
        <li id="R_4.024.027ab">aṅgadam aprigṛhyāśu tāraprabhṛtayas tathā</li>
        <li id="R_4.024.027cd">krośantaḥ prayayuḥ sarve vānarā hatabāndhavāḥ</li>
    </ul>
    <ul id="R_4.024.028">
        <li id="R_4.024.028ab">tārāprabhṛtayaḥ sarvā vānaryo hatayūthapāḥ</li>
        <li id="R_4.024.028cd">anujagmur hi bhartāraṃ krośantyaḥ karuṇasvanāḥ</li>
    </ul>
    <ul id="R_4.024.029">
        <li id="R_4.024.029ab">tāsāṃ ruditaśabdena vānarīṇāṃ vanāntare</li>
        <li id="R_4.024.029cd">vanāni girayaḥ sarve vikrośantīva sarvataḥ</li>
    </ul>
    <ul id="R_4.024.030">
        <li id="R_4.024.030ab">puline girinadyās tu vivikte jalasaṃvṛte</li>
        <li id="R_4.024.030cd">citāṃ cakruḥ subahavo vānarā vanacāriṇaḥ</li>
    </ul>
    <ul id="R_4.024.031">
        <li id="R_4.024.031ab">avaropya tataḥ skandhāc chibikāṃ vahanocitāḥ</li>
        <li id="R_4.024.031cd">tasthur ekāntam āśritya sarve śokasamanvitāḥ</li>
    </ul>
    <ul id="R_4.024.032">
        <li id="R_4.024.032ab">tatas tārā patiṃ dṛṣṭvā śibikātalaśāyinam</li>
        <li id="R_4.024.032cd">āropyāṅke śiras tasya vilalāpa suduḥkhitā</li>
    </ul>
    <ul id="R_4.024.033">
        <li id="R_4.024.033ab">janaṃ ca paśyasīmaṃ tvaṃ kasmāc chokābhipīḍitam</li>
        <li id="R_4.024.033cd">prahṛṣṭam iva te vaktraṃ gatāsor api mānada</li>
        <li id="R_4.024.033ef">astārkasamavarṇaṃ ca lakṣyate jīvato yathā</li>
    </ul>
    <ul id="R_4.024.034">
        <li id="R_4.024.034ab">eṣa tvāṃ rāmarūpeṇa kālaḥ karṣati vānara</li>
        <li id="R_4.024.034cd">yena sma vidhavāḥ sarvāḥ kṛtā ekeṣuṇā raṇe</li>
    </ul>
    <ul id="R_4.024.035">
        <li id="R_4.024.035ab">imās tās tava rājendravānaryo vallabhāḥ sadā</li>
        <li id="R_4.024.035cd">pādair vikṛṣṭam adhvānam āgatāḥ kiṃ na budhyase</li>
    </ul>
    <ul id="R_4.024.036">
        <li id="R_4.024.036ab">taveṣṭā nanu nāmaitā bhāryāś candranibhānanāḥ</li>
        <li id="R_4.024.036cd">idānīṃ nekṣase kasmāt sugrīvaṃ plavageśvaram</li>
    </ul>
    <ul id="R_4.024.037">
        <li id="R_4.024.037ab">ete hi sacivā rājaṃs tāraprabhṛtayas tava</li>
        <li id="R_4.024.037cd">puravāsijanaś cāyaṃ parivāryāsate 'nagha</li>
    </ul>
    <ul id="R_4.024.038">
        <li id="R_4.024.038ab">visarjayainān pravalān yathocitam ariṃdama</li>
        <li id="R_4.024.038cd">tataḥ krīḍāmahe sarvā vaneṣu madirotkaṭāḥ</li>
    </ul>
    <ul id="R_4.024.039">
        <li id="R_4.024.039ab">evaṃ vilapatīṃ tārāṃ patiśokapariplutām</li>
        <li id="R_4.024.039cd">utthāpayanti sma tadā vānaryaḥ śokakarśitāḥ</li>
    </ul>
    <ul id="R_4.024.040">
        <li id="R_4.024.040ab">sugrīveṇa tataḥ sārdham aṅgadaḥ pitaraṃ rudan</li>
        <li id="R_4.024.040cd">citām āropayām āsa śokenābhihatendriyaḥ</li>
    </ul>
    <ul id="R_4.024.041">
        <li id="R_4.024.041ab">tato 'gniṃ vidhivad dattvā so 'pasavyaṃ cakāra ha</li>
        <li id="R_4.024.041cd">pitaraṃ dīrgham adhvānaṃ prasthitaṃ vyākulendriyaḥ</li>
    </ul>
    <ul id="R_4.024.042">
        <li id="R_4.024.042ab">saṃskṛtya vālinaṃ te tu vidhipūrvaṃ plavaṃgamāḥ</li>
        <li id="R_4.024.042cd">ājagmur udakaṃ kartuṃ nadīṃ śītajalāṃ śubhām</li>
    </ul>
    <ul id="R_4.024.043">
        <li id="R_4.024.043ab">tatas te sahitās tatra aṅgadaṃ sthāpya cāgrataḥ</li>
        <li id="R_4.024.043cd">sugrīvatārāsahitāḥ siṣicur vāline jalam</li>
    </ul>
    <ul id="R_4.024.044">
        <li id="R_4.024.044ab">sugrīveṇaiva dīnena dīno bhūtvā mahābalaḥ</li>
        <li id="R_4.024.044cd">samānaśokaḥ kākutsthaḥ pretakāryāṇy akārayat</li>
    </ul>
    <ul id="R_4.025.001">
        <li id="R_4.025.001ab">tataḥ śokābhisaṃtaptaṃ sugrīvaṃ klinnavāsanam</li>
        <li id="R_4.025.001cd">śākhāmṛgamahāmātrāḥ parivāryopatasthire</li>
    </ul>
    <ul id="R_4.025.002">
        <li id="R_4.025.002ab">abhigamya mahābāhuṃ rāmam akliṣṭakāriṇam</li>
        <li id="R_4.025.002cd">sthitāḥ prāñjalayaḥ sarve pitāmaham ivarṣayaḥ</li>
    </ul>
    <ul id="R_4.025.003">
        <li id="R_4.025.003ab">tataḥ kāñcanaśailābhas taruṇārkanibhānanaḥ</li>
        <li id="R_4.025.003cd">abravīt prāñjalir vākyaṃ hanumān mārutātmajaḥ</li>
    </ul>
    <ul id="R_4.025.004">
        <li id="R_4.025.004ab">bhavatprasādāt sugrīvaḥ pitṛpaitāmahaṃ mahat</li>
        <li id="R_4.025.004cd">vānarāṇāṃ suduṣprāpaṃ prāpto rājyam idaṃ prabho</li>
    </ul>
    <ul id="R_4.025.005">
        <li id="R_4.025.005ab">bhavatā samanujñātaḥ praviśya nagaraṃ śubham</li>
        <li id="R_4.025.005cd">saṃvidhāsyati kāryāṇi sarvāṇi sasuhṛjjanaḥ</li>
    </ul>
    <ul id="R_4.025.006">
        <li id="R_4.025.006ab">snāto 'yaṃ vividhair gandhair auṣadhaiś ca yathāvidhi</li>
        <li id="R_4.025.006cd">arcayiṣyati ratnaiś ca mālyaiś ca tvāṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_4.025.007">
        <li id="R_4.025.007ab">imāṃ giriguhāṃ ramyām abhigantum ito 'rhasi</li>
        <li id="R_4.025.007cd">kuruṣva svāmi saṃbandhaṃ vānarān saṃpraharṣayan</li>
    </ul>
    <ul id="R_4.025.008">
        <li id="R_4.025.008ab">evam ukto hanumatā rāghavaḥ paravīrahā</li>
        <li id="R_4.025.008cd">pratyuvāca hanūmantaṃ buddhimān vākyakovidaḥ</li>
    </ul>
    <ul id="R_4.025.009">
        <li id="R_4.025.009ab">caturdaśasamāḥ saumya grāmaṃ vā yadi vā puram</li>
        <li id="R_4.025.009cd">na pravekṣyāmi hanuman pitur nirdeśapālakaḥ</li>
    </ul>
    <ul id="R_4.025.010">
        <li id="R_4.025.010ab">susamṛddhāṃ guhāṃ divyāṃ sugrīvo vānararṣabhaḥ</li>
        <li id="R_4.025.010cd">praviṣṭo vidhivad vīraḥ kṣipraṃ rājye 'bhiṣicyatām</li>
    </ul>
    <ul id="R_4.025.011">
        <li id="R_4.025.011ab">evam uktvā hanūmantaṃ rāmaḥ sugrīvam abravīt</li>
        <li id="R_4.025.011cd">imam apy aṅgadaṃ vīra yauvarājye 'bhiṣecaya</li>
    </ul>
    <ul id="R_4.025.012">
        <li id="R_4.025.012ab">pūrvo 'yaṃ vārṣiko māsaḥ śrāvaṇaḥ salilāgamaḥ</li>
        <li id="R_4.025.012cd">pravṛttāḥ saumya catvāro māsā vārṣikasaṃjñitāḥ</li>
    </ul>
    <ul id="R_4.025.013">
        <li id="R_4.025.013ab">nāyam udyogasamayaḥ praviśa tvaṃ purīṃ śubhām</li>
        <li id="R_4.025.013cd">asmin vatsyāmy ahaṃ saumya parvate sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_4.025.014">
        <li id="R_4.025.014ab">iyaṃ giriguhā ramyā viśālā yuktamārutā</li>
        <li id="R_4.025.014cd">prabhūtasalilā saumya prabhūtakamalotpalā</li>
    </ul>
    <ul id="R_4.025.015">
        <li id="R_4.025.015ab">kārtike samanuprāpte tvaṃ rāvaṇavadhe yata</li>
        <li id="R_4.025.015cd">eṣa naḥ samayaḥ saumya praviśa tvaṃ svam ālayam</li>
        <li id="R_4.025.015ef">abhiṣiñcasva rājye ca suhṛdaḥ saṃpraharṣaya</li>
    </ul>
    <ul id="R_4.025.016">
        <li id="R_4.025.016ab">iti rāmābhyanujñātaḥ sugrīvo vānararṣabhaḥ</li>
        <li id="R_4.025.016cd">praviveśa purīṃ ramyāṃ kiṣkindhāṃ vālipālitām</li>
    </ul>
    <ul id="R_4.025.017">
        <li id="R_4.025.017ab">taṃ vānarasahasrāṇi praviṣṭaṃ vānareśvaram</li>
        <li id="R_4.025.017cd">abhivādya prahṛṣṭāni sarvataḥ paryavārayan</li>
    </ul>
    <ul id="R_4.025.018">
        <li id="R_4.025.018ab">tataḥ prakṛtayaḥ sarvā dṛṣṭvā harigaṇeśvaram</li>
        <li id="R_4.025.018cd">praṇamya mūrdhnā patitā vasudhāyāṃ samāhitāḥ</li>
    </ul>
    <ul id="R_4.025.019">
        <li id="R_4.025.019ab">sugrīvaḥ prakṛtīḥ sarvāḥ saṃbhāṣyotthāpya vīryavān</li>
        <li id="R_4.025.019cd">bhrātur antaḥpuraṃ saumyaṃ praviveśa mahābalaḥ</li>
    </ul>
    <ul id="R_4.025.020">
        <li id="R_4.025.020ab">praviśya tv abhiniṣkrāntaṃ sugrīvaṃ vānararṣabham</li>
        <li id="R_4.025.020cd">abhyaṣiñcanta suhṛdaḥ sahasrākṣam ivāmarāḥ</li>
    </ul>
    <ul id="R_4.025.021">
        <li id="R_4.025.021ab">tasya pāṇḍuram ājahruś chatraṃ hemapariṣkṛtam</li>
        <li id="R_4.025.021cd">śukle ca bālavyajane hemadaṇḍe yaśaskare</li>
    </ul>
    <ul id="R_4.025.022">
        <li id="R_4.025.022ab">tathā sarvāṇi ratnāni sarvabījauṣadhāni ca</li>
        <li id="R_4.025.022cd">sakṣīrāṇāṃ ca vṛkṣāṇāṃ prarohān kusumāni ca</li>
    </ul>
    <ul id="R_4.025.023">
        <li id="R_4.025.023ab">śuklāni caiva vastrāṇi śvetaṃ caivānulepanam</li>
        <li id="R_4.025.023cd">sugandhīni ca mālyāni sthalajāny ambujāni ca</li>
    </ul>
    <ul id="R_4.025.024">
        <li id="R_4.025.024ab">candanāni ca divyāni gandhāṃś ca vividhān bahūn</li>
        <li id="R_4.025.024cd">akṣataṃ jātarūpaṃ ca priyaṅgumadhusarpiṣī</li>
    </ul>
    <ul id="R_4.025.025">
        <li id="R_4.025.025ab">dadhicarma ca vaiyāghraṃ vārāhī cāpy upānahau</li>
        <li id="R_4.025.025cd">samālambhanam ādāya rocanāṃ samanaḥśilām</li>
        <li id="R_4.025.025ef">ājagmus tatra muditā varāḥ kanyās tu ṣoḍaśa</li>
    </ul>
    <ul id="R_4.025.026">
        <li id="R_4.025.026ab">tatas te vānaraśreṣṭhaṃ yathākālaṃ yathāvidhi</li>
        <li id="R_4.025.026cd">ratnair vastraiś ca bhakṣyaiś ca toṣayitvā dvijarṣabhān</li>
    </ul>
    <ul id="R_4.025.027">
        <li id="R_4.025.027ab">tataḥ kuśaparistīrṇaṃ samiddhaṃ jātavedasaṃ</li>
        <li id="R_4.025.027cd">mantrapūtena haviṣā hutvā mantravido janāḥ</li>
    </ul>
    <ul id="R_4.025.028">
        <li id="R_4.025.028ab">tato hemapratiṣṭhāne varāstaraṇasaṃvṛte</li>
        <li id="R_4.025.028cd">prāsādaśikhare ramye citramālyopaśobhite</li>
    </ul>
    <ul id="R_4.025.029">
        <li id="R_4.025.029ab">prāṅmukhaṃ vividhair mantraiḥ sthāpayitvā varāsane</li>
        <li id="R_4.025.029cd">nadīnadebhyaḥ saṃhṛtya tīrthebhyaś ca samantataḥ</li>
    </ul>
    <ul id="R_4.025.030">
        <li id="R_4.025.030ab">āhṛtya ca samudrebhyaḥ sarvebhyo vānararṣabhāḥ</li>
        <li id="R_4.025.030cd">apaḥ kanakakumbheṣu nidhāya vimalāḥ śubhāḥ</li>
    </ul>
    <ul id="R_4.025.031">
        <li id="R_4.025.031ab">śubhair vṛṣabhaśṛṅgaiś ca kalaśaiś cāpi kāñcanaiḥ</li>
        <li id="R_4.025.031cd">śāstradṛṣṭena vidhinā maharṣivihitena ca</li>
    </ul>
    <ul id="R_4.025.032">
        <li id="R_4.025.032ab">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
        <li id="R_4.025.032cd">maindaś ca dvividaś caiva hanūmāñ jāmbavān nalaḥ</li>
    </ul>
    <ul id="R_4.025.033">
        <li id="R_4.025.033ab">abhyaṣiñcanta sugrīvaṃ prasannena sugandhinā</li>
        <li id="R_4.025.033cd">salilena sahasrākṣaṃ vasavo vāsavaṃ yathā</li>
    </ul>
    <ul id="R_4.025.034">
        <li id="R_4.025.034ab">abhiṣikte tu sugrīve sarve vānarapuṃgavāḥ</li>
        <li id="R_4.025.034cd">pracukruśur mahātmāno hṛṣṭās tatra sahasraśaḥ</li>
    </ul>
    <ul id="R_4.025.035">
        <li id="R_4.025.035ab">rāmasya tu vacaḥ kurvan sugrīvo haripuṃgavaḥ</li>
        <li id="R_4.025.035cd">aṅgadaṃ saṃpariṣvajya yauvarājye 'bhiṣecayat</li>
    </ul>
    <ul id="R_4.025.036">
        <li id="R_4.025.036ab">aṅgade cābhiṣikte tu sānukrośāḥ plavaṃgamāḥ</li>
        <li id="R_4.025.036cd">sādhu sādhv iti sugrīvaṃ mahātmāno 'bhyapūjayan</li>
    </ul>
    <ul id="R_4.025.037">
        <li id="R_4.025.037ab">hṛṣṭapuṣṭajanākīrṇā patākādhvajaśobhitā</li>
        <li id="R_4.025.037cd">babhūva nagarī ramyā kṣikindhā girigahvare</li>
    </ul>
    <ul id="R_4.025.038">
        <li id="R_4.025.038ab">
            <seg type="pāda" n="a">nivedya rāmāya tadā mahātmane</seg>
            <seg type="pāda" n="b">mahābhiṣekaṃ kapivāhinīpatiḥ</seg>
        </li>
        <li id="R_4.025.038cd">
            <seg type="pāda" n="c">rumāṃ ca bhāryāṃ pratilabhya vīryavān</seg>
            <seg type="pāda" n="d">avāpa rājyaṃ tridaśādhipo yathā</seg>
        </li>
    </ul>
    <ul id="R_4.026.001">
        <li id="R_4.026.001ab">abhiṣikte tu sugrīve praviṣṭe vānare guhām</li>
        <li id="R_4.026.001cd">ājagāma saha bhrātrā rāmaḥ prasravaṇaṃ girim</li>
    </ul>
    <ul id="R_4.026.002">
        <li id="R_4.026.002ab">śārdūlamṛgasaṃghuṣṭaṃ siṃhair bhīmaravair vṛtam</li>
        <li id="R_4.026.002cd">nānāgulmalatāgūḍhaṃ bahupādapasaṃkulam</li>
    </ul>
    <ul id="R_4.026.003">
        <li id="R_4.026.003ab">ṛkṣavānaragopucchair mārjāraiś ca niṣevitam</li>
        <li id="R_4.026.003cd">megharāśinibhaṃ śailaṃ nityaṃ śucijalāśrayam</li>
    </ul>
    <ul id="R_4.026.004">
        <li id="R_4.026.004ab">tasya śailasya śikhare mahatīm āyatāṃ guhām</li>
        <li id="R_4.026.004cd">pratyagṛhṇata vāsārthaṃ rāmaḥ saumitriṇā saha</li>
    </ul>
    <ul id="R_4.026.005">
        <li id="R_4.026.005ab">avasat tatra dharmātmā rāghavaḥ sahalakṣmaṇaḥ</li>
        <li id="R_4.026.005cd">bahudṛśyadarīkuñje tasmin prasravaṇe girau</li>
    </ul>
    <ul id="R_4.026.006">
        <li id="R_4.026.006ab">susukhe 'pi bahudravye tasmin hi dharaṇīdhare</li>
        <li id="R_4.026.006cd">vasatas tasya rāmasya ratir alpāpi nābhavat</li>
        <li id="R_4.026.006ef">hṛtāṃ hi bhāryāṃ smarataḥ prāṇebhyo 'pi garīyasīm</li>
    </ul>
    <ul id="R_4.026.007">
        <li id="R_4.026.007ab">udayābhyuditaṃ dṛṣṭvā śaśāṅkaṃ ca viśeṣataḥ</li>
        <li id="R_4.026.007cd">āviveśa na taṃ nidrā niśāsu śayanaṃ gatam</li>
    </ul>
    <ul id="R_4.026.008">
        <li id="R_4.026.008ab">tat samutthena śokena bāṣpopahatacetasaṃ</li>
        <li id="R_4.026.008cd">taṃ śocamānaṃ kākutsthaṃ nityaṃ śokaparāyaṇam</li>
        <li id="R_4.026.008ef">tulyaduḥkho 'bravīd bhrātā lakṣmaṇo 'nunayan vacaḥ</li>
    </ul>
    <ul id="R_4.026.009">
        <li id="R_4.026.009ab">alaṃ vīra vyathāṃ gatvā na tvaṃ śocitum arhasi</li>
        <li id="R_4.026.009cd">śocato hy avasīdanti sarvārthā viditaṃ hi te</li>
    </ul>
    <ul id="R_4.026.010">
        <li id="R_4.026.010ab">bhavān kriyāparo loke bhavān devaparāyaṇaḥ</li>
        <li id="R_4.026.010cd">āstiko dharmaśīlaś ca vyavasāyī ca rāghava</li>
    </ul>
    <ul id="R_4.026.011">
        <li id="R_4.026.011ab">na hy avyavasitaḥ śatruṃ rākṣasaṃ taṃ viśeṣataḥ</li>
        <li id="R_4.026.011cd">samarthas tvaṃ raṇe hantuṃ vikramair jihmakāriṇam</li>
    </ul>
    <ul id="R_4.026.012">
        <li id="R_4.026.012ab">samunmūlaya śokaṃ tvaṃ vyavasāyaṃ sthiraṃ kuru</li>
        <li id="R_4.026.012cd">tataḥ saparivāraṃ taṃ nirmūlaṃ kuru rākṣasaṃ</li>
    </ul>
    <ul id="R_4.026.013">
        <li id="R_4.026.013ab">pṛthivīm api kākutstha sasāgaravanācalām</li>
        <li id="R_4.026.013cd">parivartayituṃ śaktaḥ kim aṅga puna rāvaṇam</li>
    </ul>
    <ul id="R_4.026.014">
        <li id="R_4.026.014ab">ahaṃ tu khalu te vīryaṃ prasuptaṃ pratibodhaye</li>
        <li id="R_4.026.014cd">dīptair āhutibhiḥ kāle bhasmac channam ivānalam</li>
    </ul>
    <ul id="R_4.026.015">
        <li id="R_4.026.015ab">lakṣmaṇasya tu tad vākyaṃ pratipūjya hitaṃ śubham</li>
        <li id="R_4.026.015cd">rāghavaḥ suhṛdaṃ snigdham idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.026.016">
        <li id="R_4.026.016ab">vācyaṃ yad anuraktena snigdhena ca hitena ca</li>
        <li id="R_4.026.016cd">satyavikrama yuktena tad uktaṃ lakṣmaṇa tvayā</li>
    </ul>
    <ul id="R_4.026.017">
        <li id="R_4.026.017ab">eṣa śokaḥ parityaktaḥ sarvakāryāvasādakaḥ</li>
        <li id="R_4.026.017cd">vikrameṣv apratihataṃ tejaḥ protsāhayāmy aham</li>
    </ul>
    <ul id="R_4.026.018">
        <li id="R_4.026.018ab">śaratkālaṃ pratīkṣe 'ham iyaṃ prāvṛḍ upasthitā</li>
        <li id="R_4.026.018cd">tataḥ sarāṣṭraṃ sagaṇaṃ rākṣasaṃ taṃ nihanmy aham</li>
    </ul>
    <ul id="R_4.026.019">
        <li id="R_4.026.019ab">tasya tad vacanaṃ śrutvā hṛṣṭo rāmasya lakṣmaṇaḥ</li>
        <li id="R_4.026.019cd">punar evābravīd vākyaṃ saumitrir mitranandanaḥ</li>
    </ul>
    <ul id="R_4.026.020">
        <li id="R_4.026.020ab">etat te sadṛśaṃ vākyam uktaṃ śatrunibarhaṇa</li>
        <li id="R_4.026.020cd">idānīm asi kākutstha prakṛtiṃ svām upāgataḥ</li>
    </ul>
    <ul id="R_4.026.021">
        <li id="R_4.026.021ab">vijñāya hy ātmano vīryaṃ tathyaṃ bhavitum arhasi</li>
        <li id="R_4.026.021cd">etat sadṛśam uktaṃ te śrutasyābhijanasya ca</li>
    </ul>
    <ul id="R_4.026.022">
        <li id="R_4.026.022ab">tasmāt puruṣaśārdūla cintayañ śatrunigraham</li>
        <li id="R_4.026.022cd">varṣārātram anuprāptam atikrāmaya rāghava</li>
    </ul>
    <ul id="R_4.026.023">
        <li id="R_4.026.023ab">
            <seg type="pāda" n="a">niyamya kopaṃ pratipālyatāṃ śarat</seg>
            <seg type="pāda" n="b">kṣamasva māsāṃś caturo mayā saha</seg>
        </li>
        <li id="R_4.026.023cd">
            <seg type="pāda" n="c">vasācale 'smin mṛgarājasevite</seg>
            <seg type="pāda" n="d">saṃvardhayañ śatruvadhe samudyataḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.001">
        <li id="R_4.027.001ab">sa tadā vālinaṃ hatvā sugrīvam abhiṣicya ca</li>
        <li id="R_4.027.001cd">vasan mālyavataḥ pṛṣṭe rāmo lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_4.027.002">
        <li id="R_4.027.002ab">ayaṃ sa kālaḥ saṃprāptaḥ samayo 'dya jalāgamaḥ</li>
        <li id="R_4.027.002cd">saṃpaśya tvaṃ nabho meghaiḥ saṃvṛtaṃ girisaṃnibhaiḥ</li>
    </ul>
    <ul id="R_4.027.003">
        <li id="R_4.027.003ab">nava māsa dhṛtaṃ garbhaṃ bhāskārasya gabhastibhiḥ</li>
        <li id="R_4.027.003cd">pītvā rasaṃ samudrāṇāṃ dyauḥ prasūte rasāyanam</li>
    </ul>
    <ul id="R_4.027.004">
        <li id="R_4.027.004ab">śakyam ambaram āruhya meghasopānapaṅktibhiḥ</li>
        <li id="R_4.027.004cd">kuṭajārjunamālābhir alaṃkartuṃ divākaram</li>
    </ul>
    <ul id="R_4.027.005">
        <li id="R_4.027.005ab">saṃdhyārāgotthitais tāmrair anteṣv adhikapāṇḍuraiḥ</li>
        <li id="R_4.027.005cd">snigdhair abhrapaṭacchadair baddhavraṇam ivāmbaram</li>
    </ul>
    <ul id="R_4.027.006">
        <li id="R_4.027.006ab">mandamārutaniḥśvāsaṃ saṃdhyācandanarañjitam</li>
        <li id="R_4.027.006cd">āpāṇḍujaladaṃ bhāti kāmāturam ivāmbaram</li>
    </ul>
    <ul id="R_4.027.007">
        <li id="R_4.027.007ab">eṣā dharmaparikliṣṭā navavāripariplutā</li>
        <li id="R_4.027.007cd">sīteva śokasaṃtaptā mahī bāṣpaṃ vimuñcati</li>
    </ul>
    <ul id="R_4.027.008">
        <li id="R_4.027.008ab">meghodaravinirmuktāḥ kahlārasukhaśītalāḥ</li>
        <li id="R_4.027.008cd">śakyam añjalibhiḥ pātuṃ vātāḥ ketakigandhinaḥ</li>
    </ul>
    <ul id="R_4.027.009">
        <li id="R_4.027.009ab">eṣa phullārjunaḥ śailaḥ ketakair adhivāsitaḥ</li>
        <li id="R_4.027.009cd">sugrīva iva śāntārir dhārābhir abhiṣicyate</li>
    </ul>
    <ul id="R_4.027.010">
        <li id="R_4.027.010ab">meghakṛṣṇājinadharā dhārāyajñopavītinaḥ</li>
        <li id="R_4.027.010cd">mārutāpūritaguhāḥ prādhītā iva parvatāḥ</li>
    </ul>
    <ul id="R_4.027.011">
        <li id="R_4.027.011ab">kaśābhir iva haimībhir vidyudbhir iva tāḍitam</li>
        <li id="R_4.027.011cd">antaḥstanitanirghoṣaṃ savedanam ivāmbaram</li>
    </ul>
    <ul id="R_4.027.012">
        <li id="R_4.027.012ab">nīlameghāśritā vidyut sphurantī pratibhāti me</li>
        <li id="R_4.027.012cd">sphurantī rāvaṇasyāṅke vaidehīva tapasvinī</li>
    </ul>
    <ul id="R_4.027.013">
        <li id="R_4.027.013ab">imās tā manmathavatāṃ hitāḥ pratihatā diśaḥ</li>
        <li id="R_4.027.013cd">anuliptā iva ghanair naṣṭagrahaniśākarāḥ</li>
    </ul>
    <ul id="R_4.027.014">
        <li id="R_4.027.014ab">kva cid bāṣpābhisaṃruddhān varṣāgamasamutsukān</li>
        <li id="R_4.027.014cd">kuṭajān paśya saumitre puṣṭitān girisānuṣu</li>
        <li id="R_4.027.014ef">mama śokābhibhūtasya kāmasaṃdīpanān sthitān</li>
    </ul>
    <ul id="R_4.027.015">
        <li id="R_4.027.015ab">
            <seg type="pāda" n="a">rajaḥ praśāntaṃ sahimo 'dya vāyur</seg>
            <seg type="pāda" n="b">nidāghadoṣaprasarāḥ praśāntāḥ</seg>
        </li>
        <li id="R_4.027.015cd">
            <seg type="pāda" n="c">sthitā hi yātrā vasudhādhipānāṃ</seg>
            <seg type="pāda" n="d">pravāsino yānti narāḥ svadeśān</seg>
        </li>
    </ul>
    <ul id="R_4.027.016">
        <li id="R_4.027.016ab">
            <seg type="pāda" n="a">saṃprasthitā mānasavāsalubdhāḥ</seg>
            <seg type="pāda" n="b">priyānvitāḥ saṃprati cakravākaḥ</seg>
        </li>
        <li id="R_4.027.016cd">
            <seg type="pāda" n="c">abhīkṣṇavarṣodakavikṣateṣu</seg>
            <seg type="pāda" n="d">yānāni mārgeṣu na saṃpatanti</seg>
        </li>
    </ul>
    <ul id="R_4.027.017">
        <li id="R_4.027.017ab">
            <seg type="pāda" n="a">kva cit prakāśaṃ kva cid aprakāśaṃ</seg>
            <seg type="pāda" n="b">nabhaḥ prakīrṇāmbudharaṃ vibhāti</seg>
        </li>
        <li id="R_4.027.017cd">
            <seg type="pāda" n="c">kva cit kva cit parvatasaṃniruddhaṃ</seg>
            <seg type="pāda" n="d">rūpaṃ yathā śāntamahārṇavasya</seg>
        </li>
    </ul>
    <ul id="R_4.027.018">
        <li id="R_4.027.018ab">
            <seg type="pāda" n="a">vyāmiśritaṃ sarjakadambapuṣpair</seg>
            <seg type="pāda" n="b">navaṃ jalaṃ parvatadhātutāmram</seg>
        </li>
        <li id="R_4.027.018cd">
            <seg type="pāda" n="c">mayūrakekābhir anuprayātaṃ</seg>
            <seg type="pāda" n="d">śailāpagāḥ śīghrataraṃ vahanti</seg>
        </li>
    </ul>
    <ul id="R_4.027.019">
        <li id="R_4.027.019ab">
            <seg type="pāda" n="a">rasākulaṃ ṣaṭpadasaṃnikāśaṃ</seg>
            <seg type="pāda" n="b">prabhujyate jambuphalaṃ prakāmam</seg>
        </li>
        <li id="R_4.027.019cd">
            <seg type="pāda" n="c">anekavarṇaṃ pavanāvadhūtaṃ</seg>
            <seg type="pāda" n="d">bhūmau pataty āmraphalaṃ vipakvam</seg>
        </li>
    </ul>
    <ul id="R_4.027.020">
        <li id="R_4.027.020ab">
            <seg type="pāda" n="a">vidyutpatākāḥ sabalāka mālāḥ</seg>
            <seg type="pāda" n="b">śailendrakūṭākṛtisaṃnikāśāḥ</seg>
        </li>
        <li id="R_4.027.020cd">
            <seg type="pāda" n="c">garjanti meghāḥ samudīrṇanādā</seg>
            <seg type="pāda" n="d">mattagajendrā iva saṃyugasthaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.021">
        <li id="R_4.027.021ab">
            <seg type="pāda" n="a">meghābhikāmī parisaṃpatantī</seg>
            <seg type="pāda" n="b">saṃmoditā bhāti balākapaṅktiḥ</seg>
        </li>
        <li id="R_4.027.021cd">
            <seg type="pāda" n="c">vātāvadhūtā varapauṇḍarīkī</seg>
            <seg type="pāda" n="d">lambeva mālā racitāmbarasya</seg>
        </li>
    </ul>
    <ul id="R_4.027.022">
        <li id="R_4.027.022ab">
            <seg type="pāda" n="a">nidrā śanaiḥ keśavam abhyupaiti</seg>
            <seg type="pāda" n="b">drutaṃ nadī sāgaram abhyupaiti</seg>
        </li>
        <li id="R_4.027.022cd">
            <seg type="pāda" n="c">hṛṣṭā balākā ghanam abhyupaiti</seg>
            <seg type="pāda" n="d">kāntā sakāmā priyam abhyupaiti</seg>
        </li>
    </ul>
    <ul id="R_4.027.023">
        <li id="R_4.027.023ab">
            <seg type="pāda" n="a">jātā vanāntāḥ śikhisupranṛttā</seg>
            <seg type="pāda" n="b">jātāḥ kadambāḥ sakadambaśākhāḥ</seg>
        </li>
        <li id="R_4.027.023cd">
            <seg type="pāda" n="c">jātā vṛṣā goṣu samānakāmā</seg>
            <seg type="pāda" n="d">jātā mahī sasyavanābhirāmā</seg>
        </li>
    </ul>
    <ul id="R_4.027.024">
        <li id="R_4.027.024ab">
            <seg type="pāda" n="a">vahanti varṣanti nadanti bhānti</seg>
            <seg type="pāda" n="b">dhyāyanti nṛtyanti samāśvasanti</seg>
        </li>
        <li id="R_4.027.024cd">
            <seg type="pāda" n="c">nadyo ghanā mattagajā vanāntāḥ</seg>
            <seg type="pāda" n="d">priyāvinīhāḥ śikhinaḥ plavaṃgāḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.025">
        <li id="R_4.027.025ab">
            <seg type="pāda" n="a">praharṣitāḥ ketakapuṣpagandham</seg>
            <seg type="pāda" n="b">āghrāya hṛṣṭā vananirjhareṣu</seg>
        </li>
        <li id="R_4.027.025cd">
            <seg type="pāda" n="c">prapāta śabdākulitā gajendrāḥ</seg>
            <seg type="pāda" n="d">sārdhaṃ mayūraiḥ samadā nadanti</seg>
        </li>
    </ul>
    <ul id="R_4.027.026">
        <li id="R_4.027.026ab">
            <seg type="pāda" n="a">dhārānipātair abhihanyamānāḥ</seg>
            <seg type="pāda" n="b">kadambaśākhāsu vilambamānāḥ</seg>
        </li>
        <li id="R_4.027.026cd">
            <seg type="pāda" n="c">kṣaṇārjitaṃ puṣparasāvagāḍhaṃ</seg>
            <seg type="pāda" n="d">śanair madaṃ ṣaṭcaraṇās tyajanti</seg>
        </li>
    </ul>
    <ul id="R_4.027.027">
        <li id="R_4.027.027ab">
            <seg type="pāda" n="a">aṅgāracūrṇotkarasaṃnikāśaiḥ</seg>
            <seg type="pāda" n="b">phalaiḥ suparyāpta rasaiḥ samṛddhaiḥ</seg>
        </li>
        <li id="R_4.027.027cd">
            <seg type="pāda" n="c">jambūdrumāṇāṃ pravibhānti śākhā</seg>
            <seg type="pāda" n="d">nilīyamānā iva ṣaṭpadaughaiḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.028">
        <li id="R_4.027.028ab">
            <seg type="pāda" n="a">taḍitpatākābhir alaṃkṛtānām</seg>
            <seg type="pāda" n="b">udīrṇagambhīramahāravāṇām</seg>
        </li>
        <li id="R_4.027.028cd">
            <seg type="pāda" n="c">vibhānti rūpāṇi balāhakānāṃ</seg>
            <seg type="pāda" n="d">raṇodyatānām iva vāraṇānām</seg>
        </li>
    </ul>
    <ul id="R_4.027.029">
        <li id="R_4.027.029ab">
            <seg type="pāda" n="a">mārgānugaḥ śailavanānusārī</seg>
            <seg type="pāda" n="b">saṃprasthito megharavaṃ niśamya</seg>
        </li>
        <li id="R_4.027.029cd">
            <seg type="pāda" n="c">yuddhābhikāmaḥ pratināgaśaṅkī</seg>
            <seg type="pāda" n="d">matto gajendraḥ pratisaṃnivṛttaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.030">
        <li id="R_4.027.030ab">
            <seg type="pāda" n="a">muktāsakāśaṃ salilaṃ patad vai</seg>
            <seg type="pāda" n="b">sunirmalaṃ patrapuṭeṣu lagnam</seg>
        </li>
        <li id="R_4.027.030cd">
            <seg type="pāda" n="c">hṛṣṭā vivarṇacchadanā vihaṃgāḥ</seg>
            <seg type="pāda" n="d">surendradattaṃ tṛṣitāḥ pibanti</seg>
        </li>
    </ul>
    <ul id="R_4.027.031">
        <li id="R_4.027.031ab">
            <seg type="pāda" n="a">nīleṣu nīlā navavāripūrṇā</seg>
            <seg type="pāda" n="b">megheṣu meghāḥ pravibhānti saktāḥ</seg>
        </li>
        <li id="R_4.027.031cd">
            <seg type="pāda" n="c">davāgnidagdheṣu davāgnidagdhāḥ</seg>
            <seg type="pāda" n="d">śaileṣu śailā iva baddhamūlāḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.032">
        <li id="R_4.027.032ab">
            <seg type="pāda" n="a">mattā gajendrā muditā gavendrā</seg>
            <seg type="pāda" n="b">vaneṣu viśrāntatarā mṛgendrāḥ</seg>
        </li>
        <li id="R_4.027.032cd">
            <seg type="pāda" n="c">ramyā nagendrā nibhṛtā nagendrāḥ</seg>
            <seg type="pāda" n="d">prakrīḍito vāridharaiḥ surendraḥ</seg>
        </li>
    </ul>
    <ul id="R_4.027.033">
        <li id="R_4.027.033ab">vṛttā yātrā narendrāṇāṃ senā pratinivartate</li>
        <li id="R_4.027.033cd">vairāṇi caiva mārgāś ca salilena samīkṛtāḥ</li>
    </ul>
    <ul id="R_4.027.034">
        <li id="R_4.027.034ab">māsi prauṣṭhapade brahma brāhmaṇānāṃ vivakṣatām</li>
        <li id="R_4.027.034cd">ayam adhyāyasamayaḥ sāmagānām upasthitaḥ</li>
    </ul>
    <ul id="R_4.027.035">
        <li id="R_4.027.035ab">nivṛttakarmāyatano nūnaṃ saṃcitasaṃcayaḥ</li>
        <li id="R_4.027.035cd">āṣāḍhīm abhyupagato bharataḥ koṣakādhipaḥ</li>
    </ul>
    <ul id="R_4.027.036">
        <li id="R_4.027.036ab">nūnam āpūryamāṇāyāḥ sarayvā vadhate rayaḥ</li>
        <li id="R_4.027.036cd">māṃ samīkṣya samāyāntam ayodhyāyā iva svanaḥ</li>
    </ul>
    <ul id="R_4.027.037">
        <li id="R_4.027.037ab">imāḥ sphītaguṇā varṣāḥ sugrīvaḥ sukham aśnute</li>
        <li id="R_4.027.037cd">vijitāriḥ sadāraś ca rājye mahati ca sthitaḥ</li>
    </ul>
    <ul id="R_4.027.038">
        <li id="R_4.027.038ab">ahaṃ tu hṛtadāraś ca rājyāc ca mahataś cyutaḥ</li>
        <li id="R_4.027.038cd">nadīkūlam iva klinnam avasīdāmi lakṣmaṇa</li>
    </ul>
    <ul id="R_4.027.039">
        <li id="R_4.027.039ab">śokaś ca mama vistīrṇo varṣāś ca bhṛśadurgamāḥ</li>
        <li id="R_4.027.039cd">rāvaṇaś ca mahāñ śatrur apāraṃ pratibhāti me</li>
    </ul>
    <ul id="R_4.027.040">
        <li id="R_4.027.040ab">ayātrāṃ caiva dṛṣṭvemāṃ mārgāṃś ca bhṛśadurgamān</li>
        <li id="R_4.027.040cd">praṇate caiva sugrīve na mayā kiṃ cid īritam</li>
    </ul>
    <ul id="R_4.027.041">
        <li id="R_4.027.041ab">api cātiparikliṣṭaṃ cirād dāraiḥ samāgatam</li>
        <li id="R_4.027.041cd">ātmakāryagarīyastvād vaktuṃ necchāmi vānaram</li>
    </ul>
    <ul id="R_4.027.042">
        <li id="R_4.027.042ab">svayam eva hi viśramya jñātvā kālam upāgatam</li>
        <li id="R_4.027.042cd">upakāraṃ ca sugrīvo vetsyate nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_4.027.043">
        <li id="R_4.027.043ab">tasmāt kālapratīkṣo 'haṃ sthito 'smi śubhalakṣaṇa</li>
        <li id="R_4.027.043cd">sugrīvasya nadīnāṃ ca prasādam anupālayan</li>
    </ul>
    <ul id="R_4.027.044">
        <li id="R_4.027.044ab">upakāreṇa vīro hi pratikāreṇa yujyate</li>
        <li id="R_4.027.044cd">akṛtajño 'pratikṛto hanti sattvavatāṃ manaḥ</li>
    </ul>
    <ul id="R_4.027.045">
        <li id="R_4.027.045ab">
            <seg type="pāda" n="a">athaivam uktaḥ praṇidhāya lakṣmaṇaḥ</seg>
            <seg type="pāda" n="b">kṛtāñjalis tat pratipūjya bhāṣitam</seg>
        </li>
        <li id="R_4.027.045cd">
            <seg type="pāda" n="c">uvāca rāmaṃ svabhirāma darśanaṃ</seg>
            <seg type="pāda" n="d">pradarśayan darśanam ātmanaḥ śubham</seg>
        </li>
    </ul>
    <ul id="R_4.027.046">
        <li id="R_4.027.046ab">
            <seg type="pāda" n="a">yathoktam etat tava sarvam īpsitaṃ</seg>
            <seg type="pāda" n="b">narendra kartā nacirād dharīśvaraḥ</seg>
        </li>
        <li id="R_4.027.046cd">
            <seg type="pāda" n="c">śaratpratīkṣaḥ kṣamatām imaṃ bhavāñ</seg>
            <seg type="pāda" n="d">jalaprapātaṃ ripunigrahe dhṛtaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.028.001">
        <li id="R_4.028.001ab">samīkṣya vimalaṃ vyoma gatavidyudbalāhakam</li>
        <li id="R_4.028.001cd">sārasāravasaṃghuṣṭaṃ ramyajyotsnānulepanam</li>
    </ul>
    <ul id="R_4.028.002">
        <li id="R_4.028.002ab">samṛddhārthaṃ ca sugrīvaṃ mandadharmārthasaṃgraham</li>
        <li id="R_4.028.002cd">atyartham asatāṃ mārgam ekāntagatamānasaṃ</li>
    </ul>
    <ul id="R_4.028.003">
        <li id="R_4.028.003ab">nivṛttakāryaṃ siddhārthaṃ pramadābhirataṃ sadā</li>
        <li id="R_4.028.003cd">prāptavantam abhipretān sarvān eva manorathān</li>
    </ul>
    <ul id="R_4.028.004">
        <li id="R_4.028.004ab">svāṃ ca pātnīm abhipretāṃ tārāṃ cāpi samīpsitām</li>
        <li id="R_4.028.004cd">viharantam ahorātraṃ kṛtārthaṃ vigatajvalam</li>
    </ul>
    <ul id="R_4.028.005">
        <li id="R_4.028.005ab">krīḍantam iva deveśaṃ nandane 'psarasāṃ gaṇaiḥ</li>
        <li id="R_4.028.005cd">mantriṣu nyastakāryaṃ ca mantriṇām anavekṣakam</li>
    </ul>
    <ul id="R_4.028.006">
        <li id="R_4.028.006ab">utsannarājyasaṃdeśaṃ kāmavṛttam avasthitam</li>
        <li id="R_4.028.006cd">niścitārtho 'rthatattvajñaḥ kāladharmaviśeṣavit</li>
    </ul>
    <ul id="R_4.028.007">
        <li id="R_4.028.007ab">prasādya vākyair madhurair hetumadbhir manoramaiḥ</li>
        <li id="R_4.028.007cd">vākyavid vākyatattvajñaṃ harīśaṃ mārutātmajaḥ</li>
    </ul>
    <ul id="R_4.028.008">
        <li id="R_4.028.008ab">hitaṃ tathyaṃ ca pathyaṃ ca sāmadharmārthanītimat</li>
        <li id="R_4.028.008cd">praṇayaprītisaṃyuktaṃ viśvāsakṛtaniścayam</li>
        <li id="R_4.028.008ef">harīśvaram upāgamya hanumān vākyam abravīt</li>
    </ul>
    <ul id="R_4.028.009">
        <li id="R_4.028.009ab">rājyaṃ prāptaṃ yaśaś caiva kaulī śrīr abhivarthitā</li>
        <li id="R_4.028.009cd">mitrāṇāṃ saṃgrahaḥ śeṣas tad bhavān kartum arhati</li>
    </ul>
    <ul id="R_4.028.010">
        <li id="R_4.028.010ab">yo hi mitreṣu kālajñaḥ satataṃ sādhu vartate</li>
        <li id="R_4.028.010cd">tasya rājyaṃ ca kīrtiś ca pratāpaś cābhivardhate</li>
    </ul>
    <ul id="R_4.028.011">
        <li id="R_4.028.011ab">yasya kośaś ca daṇḍaś ca mitrāṇy ātmā ca bhūmipa</li>
        <li id="R_4.028.011cd">samavetāni sarvāṇi sa rājyaṃ mahad aśnute</li>
    </ul>
    <ul id="R_4.028.012">
        <li id="R_4.028.012ab">tad bhavān vṛttasaṃpannaḥ sthitaḥ pathi niratyaye</li>
        <li id="R_4.028.012cd">mitrārtham abhinītārthaṃ yathāvat kartum arhati</li>
    </ul>
    <ul id="R_4.028.013">
        <li id="R_4.028.013ab">yas tu kālavyatīteṣu mitrakāryeṣu vartate</li>
        <li id="R_4.028.013cd">sa kṛtvā mahato 'py arthān na mitrārthena yujyate</li>
    </ul>
    <ul id="R_4.028.014">
        <li id="R_4.028.014ab">kriyatāṃ rāghavasyaitad vaidehyāḥ parimārgaṇam</li>
        <li id="R_4.028.014cd">tad idaṃ vīra kāryaṃ te kālātītam ariṃdama</li>
    </ul>
    <ul id="R_4.028.015">
        <li id="R_4.028.015ab">na ca kālam atītaṃ te nivedayati kālavit</li>
        <li id="R_4.028.015cd">tvaramāṇo 'pi san prājñas tava rājan vaśānugaḥ</li>
    </ul>
    <ul id="R_4.028.016">
        <li id="R_4.028.016ab">kulasya ketuḥ sphītasya dīrghabandhuś ca rāghavaḥ</li>
        <li id="R_4.028.016cd">aprameyaprabhāvaś ca svayaṃ cāpratimo guṇaiḥ</li>
    </ul>
    <ul id="R_4.028.017">
        <li id="R_4.028.017ab">tasya tvaṃ kuru vai kāryaṃ pūrvaṃ tena kṛtaṃ tava</li>
        <li id="R_4.028.017cd">harīśvara hariśreṣṭhān ājñāpayitum arhasi</li>
    </ul>
    <ul id="R_4.028.018">
        <li id="R_4.028.018ab">na hi tāvad bhavet kālo vyatītaś codanād ṛte</li>
        <li id="R_4.028.018cd">coditasya hi kāryasya bhavet kālavyatikramaḥ</li>
    </ul>
    <ul id="R_4.028.019">
        <li id="R_4.028.019ab">akartur api kāryasya bhavān kartā harīśvara</li>
        <li id="R_4.028.019cd">kiṃ punaḥ pratikartus te rājyena ca dhanena ca</li>
    </ul>
    <ul id="R_4.028.020">
        <li id="R_4.028.020ab">śaktimān asi vikrānto vānararṣka gaṇeśvara</li>
        <li id="R_4.028.020cd">kartuṃ dāśaratheḥ prītim ājñāyāṃ kiṃ nu sajjase</li>
    </ul>
    <ul id="R_4.028.021">
        <li id="R_4.028.021ab">kāmaṃ khalu śarair śaktaḥ surāsuramahoragān</li>
        <li id="R_4.028.021cd">vaśe dāśarathiḥ kartuṃ tvatpratijñāṃ tu kāṅkṣate</li>
    </ul>
    <ul id="R_4.028.022">
        <li id="R_4.028.022ab">prāṇatyāgāviśaṅkena kṛtaṃ tena tava priyam</li>
        <li id="R_4.028.022cd">tasya mārgāma vaidehīṃ pṛthivyām api cāmbare</li>
    </ul>
    <ul id="R_4.028.023">
        <li id="R_4.028.023ab">na devā na ca gandharvā nāsurā na marudgaṇāḥ</li>
        <li id="R_4.028.023cd">na ca yakṣā bhayaṃ tasya kuryuḥ kim uta rākṣasāḥ</li>
    </ul>
    <ul id="R_4.028.024">
        <li id="R_4.028.024ab">tad evaṃ śaktiyuktasya pūrvaṃ priyakṛtas tathā</li>
        <li id="R_4.028.024cd">rāmasyārhasi piṅgeśa kartuṃ sarvātmanā priyam</li>
    </ul>
    <ul id="R_4.028.025">
        <li id="R_4.028.025ab">nādhastād avanau nāpsu gatir nopari cāmbare</li>
        <li id="R_4.028.025cd">kasya cit sajjate 'smākaṃ kapīśvara tavājñayā</li>
    </ul>
    <ul id="R_4.028.026">
        <li id="R_4.028.026ab">tad ājñāpaya kaḥ kiṃ te kṛte vasatu kutra cit</li>
        <li id="R_4.028.026cd">harayo hy apradhṛṣyās te santi koṭyagrato 'nagha</li>
    </ul>
    <ul id="R_4.028.027">
        <li id="R_4.028.027ab">tasya tad vacanaṃ śrutvā kāle sādhuniveditam</li>
        <li id="R_4.028.027cd">sugrīvaḥ sattvasaṃpannaś cakāra matim uttamām</li>
    </ul>
    <ul id="R_4.028.028">
        <li id="R_4.028.028ab">sa saṃdideśābhimataṃ nīlaṃ nityakṛtodyamam</li>
        <li id="R_4.028.028cd">dikṣu sarvāsu sarveṣāṃ sainyānām upasaṃgrahe</li>
    </ul>
    <ul id="R_4.028.029">
        <li id="R_4.028.029ab">yathā senā samagrā me yūthapālāś ca sarvaśaḥ</li>
        <li id="R_4.028.029cd">samāgacchanty asaṃgena senāgrāṇi tathā kuru</li>
    </ul>
    <ul id="R_4.028.030">
        <li id="R_4.028.030ab">ye tv antapālāḥ plavagāḥ śīghragā vyavasāyinaḥ</li>
        <li id="R_4.028.030cd">samānayantu te sainyaṃ tvaritāḥ śāsanān mama</li>
        <li id="R_4.028.030ef">svayaṃ cānantaraṃ sainyaṃ bhavān evānupaśyatu</li>
    </ul>
    <ul id="R_4.028.031">
        <li id="R_4.028.031ab">tripañcarātrād ūrdhvaṃ yaḥ prāpnuyān neha vānaraḥ</li>
        <li id="R_4.028.031cd">tasya prāṇāntiko daṇḍo nātra kāryā vicāraṇā</li>
    </ul>
    <ul id="R_4.028.032">
        <li id="R_4.028.032ab">
            <seg type="pāda" n="a">harīṃś ca vṛddhān upayātu sāṅgado</seg>
            <seg type="pāda" n="b">bhavān mamājñām adhikṛtya niścitām</seg>
        </li>
        <li id="R_4.028.032cd">
            <seg type="pāda" n="c">iti vyavasthāṃ haripuṃgaveśvaro</seg>
            <seg type="pāda" n="d">vidhāya veśma praviveśa vīryavān</seg>
        </li>
    </ul>
    <ul id="R_4.029.001">
        <li id="R_4.029.001ab">guhāṃ praviṣṭe sugrīve vimukte gagane ghanaiḥ</li>
        <li id="R_4.029.001cd">varṣarātroṣito rāmaḥ kāmaśokābhipīḍitaḥ</li>
    </ul>
    <ul id="R_4.029.002">
        <li id="R_4.029.002ab">pāṇḍuraṃ gaganaṃ dṛṣṭvā vimalaṃ candramaṇḍalam</li>
        <li id="R_4.029.002cd">śāradīṃ rajanīṃ caiva dṛṣṭvā jyotsnānulepanām</li>
    </ul>
    <ul id="R_4.029.003">
        <li id="R_4.029.003ab">kāmavṛttaṃ ca sugrīvaṃ naṣṭāṃ ca janakātmajām</li>
        <li id="R_4.029.003cd">buddhvā kālam atītaṃ ca mumoha paramāturaḥ</li>
    </ul>
    <ul id="R_4.029.004">
        <li id="R_4.029.004ab">sa tu saṃjñām upāgamya muhūrtān matimān punaḥ</li>
        <li id="R_4.029.004cd">manaḥsthām api vaidehīṃ cintayām āsa rāghavaḥ</li>
    </ul>
    <ul id="R_4.029.005">
        <li id="R_4.029.005ab">āsīnaḥ parvatasyāgre hemadhātuvibhūṣite</li>
        <li id="R_4.029.005cd">śāradaṃ gaganaṃ dṛṣṭva jagāma manasā priyām</li>
    </ul>
    <ul id="R_4.029.006">
        <li id="R_4.029.006ab">dṛṣṭvā ca vimalaṃ vyoma gatavidyudbalāhakam</li>
        <li id="R_4.029.006cd">sārasāravasaṃghuṣṭaṃ vilalāpārtayā girā</li>
    </ul>
    <ul id="R_4.029.007">
        <li id="R_4.029.007ab">sārasāravasaṃnādaiḥ sārasāravanādinī</li>
        <li id="R_4.029.007cd">yāśrame ramate bālā sādya me ramate katham</li>
    </ul>
    <ul id="R_4.029.008">
        <li id="R_4.029.008ab">puṣpitāṃś cāsanān dṛṣṭvā kāñcanān iva nirmalān</li>
        <li id="R_4.029.008cd">kathaṃ sa ramate bālā paśyantī mām apaśyatī</li>
    </ul>
    <ul id="R_4.029.009">
        <li id="R_4.029.009ab">yā purā kalahaṃsānāṃ svareṇa kalabhāṣiṇī</li>
        <li id="R_4.029.009cd">budhyate cārusarvāṅgī sādya me budhyate katham</li>
    </ul>
    <ul id="R_4.029.010">
        <li id="R_4.029.010ab">niḥsvanaṃ cakravākānāṃ niśamya sahacāriṇām</li>
        <li id="R_4.029.010cd">puṇḍarīkaviśālākṣī katham eṣā bhaviṣyati</li>
    </ul>
    <ul id="R_4.029.011">
        <li id="R_4.029.011ab">sarāṃsi sarito vāpīḥ kānanāni vanāni ca</li>
        <li id="R_4.029.011cd">tāṃ vinā mṛgaśāvākṣīṃ caran nādya sukhaṃ labhe</li>
    </ul>
    <ul id="R_4.029.012">
        <li id="R_4.029.012ab">api tāṃ madviyogāc ca saukumāryāc ca bhāminīm</li>
        <li id="R_4.029.012cd">na dūraṃ pīḍayet kāmaḥ śaradguṇanirantaraḥ</li>
    </ul>
    <ul id="R_4.029.013">
        <li id="R_4.029.013ab">evamādi naraśreṣṭho vilalāpa nṛpātmajaḥ</li>
        <li id="R_4.029.013cd">vihaṃga iva sāraṅgaḥ salilaṃ tridaśeśvarāt</li>
    </ul>
    <ul id="R_4.029.014">
        <li id="R_4.029.014ab">tataś cañcūrya ramyeṣu phalārthī girisānuṣu</li>
        <li id="R_4.029.014cd">dadarśa paryupāvṛtto lakṣmīvāṃl lakṣmaṇo 'grajam</li>
    </ul>
    <ul id="R_4.029.015">
        <li id="R_4.029.015ab">
            <seg type="pāda" n="a">taṃ cintayā duḥsahayā parītaṃ</seg>
            <seg type="pāda" n="b">visaṃjñam ekaṃ vijane manasvī</seg>
        </li>
        <li id="R_4.029.015cd">
            <seg type="pāda" n="c">bhrātur viṣādāt paritāpadīnaḥ</seg>
            <seg type="pāda" n="d">samīkṣya saumitrir uvāca rāmam</seg>
        </li>
    </ul>
    <ul id="R_4.029.016">
        <li id="R_4.029.016ab">
            <seg type="pāda" n="a">kim ārya kāmasya vaśaṃgatena</seg>
            <seg type="pāda" n="b">kim ātmapauruṣyaparābhavena</seg>
        </li>
        <li id="R_4.029.016cd">
            <seg type="pāda" n="c">ayaṃ sadā saṃhṛiyate samādhiḥ</seg>
            <seg type="pāda" n="d">kim atra yogena nivartitena</seg>
        </li>
    </ul>
    <ul id="R_4.029.017">
        <li id="R_4.029.017ab">
            <seg type="pāda" n="a">kriyābhiyogaṃ manasaḥ prasādaṃ</seg>
            <seg type="pāda" n="b">samādhiyogānugataṃ ca kālam</seg>
        </li>
        <li id="R_4.029.017cd">
            <seg type="pāda" n="c">sahāyasāmarthyam adīnasattva</seg>
            <seg type="pāda" n="d">svakarmahetuṃ ca kuruṣva hetum</seg>
        </li>
    </ul>
    <ul id="R_4.029.018">
        <li id="R_4.029.018ab">
            <seg type="pāda" n="a">na jānakī mānavavaṃśanātha</seg>
            <seg type="pāda" n="b">tvayā sanāthā sulabhā pareṇa</seg>
        </li>
        <li id="R_4.029.018cd">
            <seg type="pāda" n="c">na cāgnicūḍāṃ jvalitām upetya</seg>
            <seg type="pāda" n="d">na dahyate vīravarārha kaś cit</seg>
        </li>
    </ul>
    <ul id="R_4.029.019">
        <li id="R_4.029.019ab">
            <seg type="pāda" n="a">salakṣmaṇaṃ lakṣmaṇam apradhṛṣyaṃ</seg>
            <seg type="pāda" n="b">svabhāvajaṃ vākyam uvāca rāmaḥ</seg>
        </li>
        <li id="R_4.029.019cd">
            <seg type="pāda" n="c">hitaṃ ca pathyaṃ ca nayaprasaktaṃ</seg>
            <seg type="pāda" n="d">sasāmadharmārthasamāhitaṃ ca</seg>
        </li>
    </ul>
    <ul id="R_4.029.020">
        <li id="R_4.029.020ab">
            <seg type="pāda" n="a">niḥsaṃśayaṃ kāryam avekṣitavyaṃ</seg>
            <seg type="pāda" n="b">kriyāviśeṣo hy anuvartitavyaḥ</seg>
        </li>
        <li id="R_4.029.020cd">
            <seg type="pāda" n="c">nanu pravṛttasya durāsadasya</seg>
            <seg type="pāda" n="d">kumārakāryasya phalaṃ na cintyam</seg>
        </li>
    </ul>
    <ul id="R_4.029.021">
        <li id="R_4.029.021ab">atha padmapalāśākṣīṃ maithilīm anucintayan</li>
        <li id="R_4.029.021cd">uvāca lakṣmaṇaṃ rāmo mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_4.029.022">
        <li id="R_4.029.022ab">tarpayitvā sahasrākṣaḥ salilena vasuṃdharām</li>
        <li id="R_4.029.022cd">nirvartayitvā sasyāni kṛtakarmā vyavasthitaḥ</li>
    </ul>
    <ul id="R_4.029.023">
        <li id="R_4.029.023ab">snigdhagambhīranirghoṣāḥ śailadrumapurogamāḥ</li>
        <li id="R_4.029.023cd">visṛjya salilaṃ meghāḥ pariśrāntā nṛpātmaja</li>
    </ul>
    <ul id="R_4.029.024">
        <li id="R_4.029.024ab">nīlotpaladalaśyāmaḥ śyāmīkṛtvā diśo daśa</li>
        <li id="R_4.029.024cd">vimadā iva mātaṅgāḥ śāntavegāḥ payodharāḥ</li>
    </ul>
    <ul id="R_4.029.025">
        <li id="R_4.029.025ab">jalagarbhā mahāvegāḥ kuṭajārjunagandhinaḥ</li>
        <li id="R_4.029.025cd">caritvā viratāḥ saumya vṛṣṭivātāḥ samudyatāḥ</li>
    </ul>
    <ul id="R_4.029.026">
        <li id="R_4.029.026ab">ghanānāṃ vāraṇānāṃ ca mayūrāṇāṃ ca lakṣmaṇa</li>
        <li id="R_4.029.026cd">nādaḥ prasravaṇānāṃ ca praśāntaḥ sahasānagha</li>
    </ul>
    <ul id="R_4.029.027">
        <li id="R_4.029.027ab">abhivṛṣṭā mahāmeghair nirmalāś citrasānavaḥ</li>
        <li id="R_4.029.027cd">anuliptā ivābhānti girayaś candraraśmibhiḥ</li>
    </ul>
    <ul id="R_4.029.028">
        <li id="R_4.029.028ab">darśayanti śarannadyaḥ pulināni śanaiḥ śanaiḥ</li>
        <li id="R_4.029.028cd">navasaṃgamasavrīḍā jaghanānīva yoṣitaḥ</li>
    </ul>
    <ul id="R_4.029.029">
        <li id="R_4.029.029ab">prasannasalilāḥ saumya kurarībhir vināditāḥ</li>
        <li id="R_4.029.029cd">cakravākagaṇākīrṇā vibhānti salilāśayāḥ</li>
    </ul>
    <ul id="R_4.029.030">
        <li id="R_4.029.030ab">anyonyabaddhavairāṇāṃ jigīṣūṇāṃ nṛpātmaja</li>
        <li id="R_4.029.030cd">udyogasamayaḥ saumya pārthivānām upasthitaḥ</li>
    </ul>
    <ul id="R_4.029.031">
        <li id="R_4.029.031ab">iyaṃ sā prathamā yātrā pārthivānāṃ nṛpātmaja</li>
        <li id="R_4.029.031cd">na ca paśyāmi sugrīvam udyogaṃ vā tathāvidham</li>
    </ul>
    <ul id="R_4.029.032">
        <li id="R_4.029.032ab">catvāro vārṣikā māsā gatā varṣaśatopamāḥ</li>
        <li id="R_4.029.032cd">mama śokābhitaptasya saumya sītām apaśyataḥ</li>
    </ul>
    <ul id="R_4.029.033">
        <li id="R_4.029.033ab">priyāvihīne duḥkhārte hṛtarājye vivāsite</li>
        <li id="R_4.029.033cd">kṛpāṃ na kurute rājā sugrīvo mayi lakṣmaṇa</li>
    </ul>
    <ul id="R_4.029.034">
        <li id="R_4.029.034ab">anātho hṛtarājyo 'yaṃ rāvaṇena ca dharṣitaḥ</li>
        <li id="R_4.029.034cd">dīno dūragṛhaḥ kāmī māṃ caiva śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_4.029.035">
        <li id="R_4.029.035ab">ity etaiḥ kāraṇaiḥ saumya sugrīvasya durātmanaḥ</li>
        <li id="R_4.029.035cd">ahaṃ vānararājasya paribhūtaḥ paraṃtapa</li>
    </ul>
    <ul id="R_4.029.036">
        <li id="R_4.029.036ab">sa kālaṃ parisaṃkhyāya sītāyāḥ parimārgaṇe</li>
        <li id="R_4.029.036cd">kṛtārthaḥ samayaṃ kṛtvā durmatir nāvabudhyate</li>
    </ul>
    <ul id="R_4.029.037">
        <li id="R_4.029.037ab">tvaṃ praviśya ca kiṣkindhāṃ brūhi vānarapuṃgavam</li>
        <li id="R_4.029.037cd">mūrkhaṃ grāmya sukhe saktaṃ sugrīvaṃ vacanān mama</li>
    </ul>
    <ul id="R_4.029.038">
        <li id="R_4.029.038ab">arthinām upapannānāṃ pūrvaṃ cāpy upakāriṇām</li>
        <li id="R_4.029.038cd">āśāṃ saṃśrutya yo hanti sa loke puruṣādhamaḥ</li>
    </ul>
    <ul id="R_4.029.039">
        <li id="R_4.029.039ab">śubhaṃ vā yadi vā pāpaṃ yo hi vākyam udīritam</li>
        <li id="R_4.029.039cd">satyena parigṛhṇāti sa vīraḥ puruṣottamaḥ</li>
    </ul>
    <ul id="R_4.029.040">
        <li id="R_4.029.040ab">kṛtārthā hy akṛtārthānāṃ mitrāṇāṃ na bhavanti ye</li>
        <li id="R_4.029.040cd">tān mṛtān api kravyādaḥ kṛtaghnān nopabhuñjate</li>
    </ul>
    <ul id="R_4.029.041">
        <li id="R_4.029.041ab">nūnaṃ kāñcanapṛṣṭhasya vikṛṣṭasya mayā raṇe</li>
        <li id="R_4.029.041cd">draṣṭum icchanti cāpasya rūpaṃ vidyudgaṇopamam</li>
    </ul>
    <ul id="R_4.029.042">
        <li id="R_4.029.042ab">ghoraṃ jyātalanirghoṣaṃ kruddhasya mama saṃyuge</li>
        <li id="R_4.029.042cd">nirghoṣam iva vajrasya punaḥ saṃśrotum icchati</li>
    </ul>
    <ul id="R_4.029.043">
        <li id="R_4.029.043ab">kāmam evaṃgate 'py asya parijñāte parākrame</li>
        <li id="R_4.029.043cd">tvatsahāyasya me vīra na cintā syān nṛpātmaja</li>
    </ul>
    <ul id="R_4.029.044">
        <li id="R_4.029.044ab">yadartham ayam ārambhaḥ kṛtaḥ parapuraṃjaya</li>
        <li id="R_4.029.044cd">samayaṃ nābhijānāti kṛtārthaḥ plavageśvaraḥ</li>
    </ul>
    <ul id="R_4.029.045">
        <li id="R_4.029.045ab">varṣāsamayakālaṃ tu pratijñāya harīśvaraḥ</li>
        <li id="R_4.029.045cd">vyatītāṃś caturo māsān viharan nāvabudhyate</li>
    </ul>
    <ul id="R_4.029.046">
        <li id="R_4.029.046ab">sāmātyapariṣat krīḍan pānam evopasevate</li>
        <li id="R_4.029.046cd">śokadīneṣu nāsmāsu sugrīvaḥ kurute dayām</li>
    </ul>
    <ul id="R_4.029.047">
        <li id="R_4.029.047ab">ucyatāṃ gaccha sugrīvas tvayā vatsa mahābala</li>
        <li id="R_4.029.047cd">mama roṣasya yadrūpaṃ brūyāś cainam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_4.029.048">
        <li id="R_4.029.048ab">na ca saṃkucitaḥ panthā yena vālī hato gataḥ</li>
        <li id="R_4.029.048cd">samaye tiṣṭha sugrīvamā vālipatham anvagāḥ</li>
    </ul>
    <ul id="R_4.029.049">
        <li id="R_4.029.049ab">eka eva raṇe vālī śareṇa nihato mayā</li>
        <li id="R_4.029.049cd">tvāṃ tu satyād atikrāntaṃ haniṣyāmi sabāndhavam</li>
    </ul>
    <ul id="R_4.029.050">
        <li id="R_4.029.050ab">tad evaṃ vihite kārye yad dhitaṃ puruṣarṣabha</li>
        <li id="R_4.029.050cd">tat tad brūhi naraśreṣṭha tvara kālavyatikramaḥ</li>
    </ul>
    <ul id="R_4.029.051">
        <li id="R_4.029.051ab">
            <seg type="pāda" n="a">kuruṣva satyaṃ mayi vānareśvara</seg>
            <seg type="pāda" n="b">pratiśrutaṃ dharmam avekṣya śāśvatam</seg>
        </li>
        <li id="R_4.029.051cd">
            <seg type="pāda" n="c">mā vālinaṃ pretya gato yamakṣayaṃ</seg>
            <seg type="pāda" n="d">tvam adya paśyer mama coditaiḥ śaraiḥ</seg>
        </li>
    </ul>
    <ul id="R_4.029.052">
        <li id="R_4.029.052ab">
            <seg type="pāda" n="a">sa pūrvajaṃ tīvravivṛddhakopaṃ</seg>
            <seg type="pāda" n="b">lālapyamānaṃ prasamīkṣya dīnam</seg>
        </li>
        <li id="R_4.029.052cd">
            <seg type="pāda" n="c">cakāra tīvrāṃ matim ugratejā</seg>
            <seg type="pāda" n="d">harīśvaramānavavaṃśanāthaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.030.001">
        <li id="R_4.030.001ab">
            <seg type="pāda" n="a">sa kāminaṃ dīnam adīnasattvaḥ</seg>
            <seg type="pāda" n="b">śokābhipannaṃ samudīrṇakopam</seg>
        </li>
        <li id="R_4.030.001cd">
            <seg type="pāda" n="c">narendrasūnur naradevaputraṃ</seg>
            <seg type="pāda" n="d">rāmānujaḥ pūrvajam ity uvāca</seg>
        </li>
    </ul>
    <ul id="R_4.030.002">
        <li id="R_4.030.002ab">
            <seg type="pāda" n="a">na vānaraḥ sthāsyati sādhuvṛtte</seg>
            <seg type="pāda" n="b">na maṃsyate kāryaphalānuṣaṅgān</seg>
        </li>
        <li id="R_4.030.002cd">
            <seg type="pāda" n="c">na bhakṣyate vānararājyalakṣmīṃ</seg>
            <seg type="pāda" n="d">tathā hi nābhikramate 'sya buddhiḥ</seg>
        </li>
    </ul>
    <ul id="R_4.030.003">
        <li id="R_4.030.003ab">
            <seg type="pāda" n="a">matikṣayād grāmyasukheṣu saktas</seg>
            <seg type="pāda" n="b">tava prasādāpratikārabuddhiḥ</seg>
        </li>
        <li id="R_4.030.003cd">
            <seg type="pāda" n="c">hato 'grajaṃ paśyatu vālinaṃ sa</seg>
            <seg type="pāda" n="d">na rājyam evaṃ viguṇasya deyam</seg>
        </li>
    </ul>
    <ul id="R_4.030.004">
        <li id="R_4.030.004ab">
            <seg type="pāda" n="a">na dhāraye kopam udīrṇavegaṃ</seg>
            <seg type="pāda" n="b">nihanmi sugrīvam asatyam adya</seg>
        </li>
        <li id="R_4.030.004cd">
            <seg type="pāda" n="c">haripravīraiḥ saha vāliputro</seg>
            <seg type="pāda" n="d">narendrapatnyā vicayaṃ karotu</seg>
        </li>
    </ul>
    <ul id="R_4.030.005">
        <li id="R_4.030.005ab">
            <seg type="pāda" n="a">tam āttabāṇāsanam utpatantaṃ</seg>
            <seg type="pāda" n="b">niveditārthaṃ raṇacaṇḍakopam</seg>
        </li>
        <li id="R_4.030.005cd">
            <seg type="pāda" n="c">uvaca rāmaḥ paravīrahantā</seg>
            <seg type="pāda" n="d">svavekṣitaṃ sānunayaṃ ca vākyam</seg>
        </li>
    </ul>
    <ul id="R_4.030.006">
        <li id="R_4.030.006ab">na hi vai tvadvidho loke pāpam evaṃ samācaret</li>
        <li id="R_4.030.006cd">pāpam āryeṇa yo hanti sa vīraḥ puruṣottamaḥ</li>
    </ul>
    <ul id="R_4.030.007">
        <li id="R_4.030.007ab">nedam adya tvayā grāhyaṃ sādhuvṛttena lakṣmaṇa</li>
        <li id="R_4.030.007cd">tāṃ prītim anuvartasva pūrvavṛttaṃ ca saṃgatam</li>
    </ul>
    <ul id="R_4.030.008">
        <li id="R_4.030.008ab">sāmopahitayā vācā rūkṣāṇi parivarjayan</li>
        <li id="R_4.030.008cd">vaktum arhasi sugrīvaṃ vyatītaṃ kālaparyaye</li>
    </ul>
    <ul id="R_4.030.009">
        <li id="R_4.030.009ab">so' grajenānuśiṣṭārtho yathāvat puruṣarṣabhaḥ</li>
        <li id="R_4.030.009cd">praviveśa purīṃ vīro lakṣmaṇaḥ paravīrahā</li>
    </ul>
    <ul id="R_4.030.010">
        <li id="R_4.030.010ab">tataḥ śubhamatiḥ prājño bhrātuḥ priyahite rataḥ</li>
        <li id="R_4.030.010cd">lakṣmaṇaḥ pratisaṃrabdho jagāma bhavanaṃ kapeḥ</li>
    </ul>
    <ul id="R_4.030.011">
        <li id="R_4.030.011ab">śakrabāṇāsanaprakhyaṃ dhanuḥ kālāntakopamaḥ</li>
        <li id="R_4.030.011cd">pragṛhya giriśṛṅgābhaṃ mandaraḥ sānumān iva</li>
    </ul>
    <ul id="R_4.030.012">
        <li id="R_4.030.012ab">yathoktakārī vacanam uttaraṃ caiva sottaram</li>
        <li id="R_4.030.012cd">bṛhaspatisamo buddhyā mattvā rāmānujas tadā</li>
    </ul>
    <ul id="R_4.030.013">
        <li id="R_4.030.013ab">kāmakrodhasamutthena bhrātuḥ kopāgninā vṛtaḥ</li>
        <li id="R_4.030.013cd">prabhañjana ivāprītaḥ prayayau lakṣmaṇas tadā</li>
    </ul>
    <ul id="R_4.030.014">
        <li id="R_4.030.014ab">sālatālāśvakarṇāṃś ca tarasā pātayan bahūn</li>
        <li id="R_4.030.014cd">paryasyan girikūṭāni drumān anyāṃś ca vegataḥ</li>
    </ul>
    <ul id="R_4.030.015">
        <li id="R_4.030.015ab">śilāś ca śakalīkurvan padbhyāṃ gaja ivāśugaḥ</li>
        <li id="R_4.030.015cd">dūram ekapadaṃ tyaktvā yayau kāryavaśād drutam</li>
    </ul>
    <ul id="R_4.030.016">
        <li id="R_4.030.016ab">tām apaśyad balākīrṇāṃ harirājamahāpurīm</li>
        <li id="R_4.030.016cd">durgām ikṣvākuśārdūlaḥ kiṣkindhāṃ girisaṃkaṭe</li>
    </ul>
    <ul id="R_4.030.017">
        <li id="R_4.030.017ab">roṣāt prasphuramāṇauṣṭhaḥ sugrīvaṃ prati kalṣmaṇaḥ</li>
        <li id="R_4.030.017cd">dadarśa vānarān bhīmān kiṣkindhāyā bahiścarān</li>
    </ul>
    <ul id="R_4.030.018">
        <li id="R_4.030.018ab">śailaśṛṅgāṇi śataśaḥ pravṛddhāṃś ca mahīruhān</li>
        <li id="R_4.030.018cd">jagṛhuḥ kuñjaraprakhyā vānarāḥ parvatāntare</li>
    </ul>
    <ul id="R_4.030.019">
        <li id="R_4.030.019ab">tān gṛhītapraharaṇān harīn dṛṣṭvā tu lakṣmaṇaḥ</li>
        <li id="R_4.030.019cd">babhūva dviguṇaṃ kruddho bahvindhana ivānalaḥ</li>
    </ul>
    <ul id="R_4.030.020">
        <li id="R_4.030.020ab">taṃ te bhayaparītāṅgāḥ kruddhaṃ dṛṣṭvā plavaṃgamāḥ</li>
        <li id="R_4.030.020cd">kālamṛtyuyugāntābhaṃ śataśo vidrutā diśaḥ</li>
    </ul>
    <ul id="R_4.030.021">
        <li id="R_4.030.021ab">tataḥ sugrīvabhavanaṃ praviśya haripuṃgavāḥ</li>
        <li id="R_4.030.021cd">krodham āgamanaṃ caiva lakṣmaṇasya nyavedayan</li>
    </ul>
    <ul id="R_4.030.022">
        <li id="R_4.030.022ab">tārayā sahitaḥ kāmī saktaḥ kapivṛṣo rahaḥ</li>
        <li id="R_4.030.022cd">na teṣāṃ kapivīrāṇāṃ śuśrāva vacanaṃ tadā</li>
    </ul>
    <ul id="R_4.030.023">
        <li id="R_4.030.023ab">tataḥ sacivasaṃdiṣṭā harayo romaharṣaṇāḥ</li>
        <li id="R_4.030.023cd">girikuñjarameghābhā nagaryā niryayus tadā</li>
    </ul>
    <ul id="R_4.030.024">
        <li id="R_4.030.024ab">nakhadaṃṣṭrāyudhā ghorāḥ sarve vikṛtadarśanāḥ</li>
        <li id="R_4.030.024cd">sarve śārdūladarpāś ca sarve ca vikṛtānanāḥ</li>
    </ul>
    <ul id="R_4.030.025">
        <li id="R_4.030.025ab">daśanāgabalāḥ ke cit ke cid daśaguṇottarāḥ</li>
        <li id="R_4.030.025cd">ke cin nāgasahasrasya babhūvus tulyavikramāḥ</li>
    </ul>
    <ul id="R_4.030.026">
        <li id="R_4.030.026ab">kṛtsnāṃ hi kapibhir vyāptāṃ drumahastair mahābalaiḥ</li>
        <li id="R_4.030.026cd">apaśyal lakṣmaṇaḥ kruddhaḥ kiṣkindhāṃ tāṃ durāsadam</li>
    </ul>
    <ul id="R_4.030.027">
        <li id="R_4.030.027ab">tatas te harayaḥ sarve prākāraparikhāntarāt</li>
        <li id="R_4.030.027cd">niṣkramyodagrasattvās tu tasthur āviṣkṛtaṃ tadā</li>
    </ul>
    <ul id="R_4.030.028">
        <li id="R_4.030.028ab">sugrīvasya pramādaṃ ca pūrvajaṃ cārtam ātmavān</li>
        <li id="R_4.030.028cd">buddhvā kopavaśaṃ vīraḥ punar eva jagāma saḥ</li>
    </ul>
    <ul id="R_4.030.029">
        <li id="R_4.030.029ab">sa dīrghoṣṇamahocchvāsaḥ kopasaṃraktalocanaḥ</li>
        <li id="R_4.030.029cd">babhūva naraśārdūlasadhūma iva pāvakaḥ</li>
    </ul>
    <ul id="R_4.030.030">
        <li id="R_4.030.030ab">bāṇaśalyasphurajjihvaḥ sāyakāsanabhogavān</li>
        <li id="R_4.030.030cd">svatejoviṣasaṃghātaḥ pañcāsya iva pannagaḥ</li>
    </ul>
    <ul id="R_4.030.031">
        <li id="R_4.030.031ab">taṃ dīptam iva kālāgniṃ nāgendram iva kopitam</li>
        <li id="R_4.030.031cd">samāsādyāṅgadas trāsād viṣādam agamad bhṛśam</li>
    </ul>
    <ul id="R_4.030.032">
        <li id="R_4.030.032ab">so 'ṅgadaṃ roṣatāmrākṣaḥ saṃdideśa mahāyaśāḥ</li>
        <li id="R_4.030.032cd">sugrīvaḥ kathyatāṃ vatsa mamāgamanam ity uta</li>
    </ul>
    <ul id="R_4.030.033">
        <li id="R_4.030.033ab">eṣa rāmānujaḥ prāptas tvatsakāśam ariṃdamaḥ</li>
        <li id="R_4.030.033cd">bhrātur vyasanasaṃtapto dvāri tiṣṭhati lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_4.030.034">
        <li id="R_4.030.034ab">lakṣmaṇasya vacaḥ śrutvā śokāviṣṭo 'ṅgado 'bravīt</li>
        <li id="R_4.030.034cd">pituḥ samīpam āgamya saumitrir ayam āgataḥ</li>
    </ul>
    <ul id="R_4.030.035">
        <li id="R_4.030.035ab">te mahaughanibhaṃ dṛṣṭvā vajrāśanisamasvanam</li>
        <li id="R_4.030.035cd">siṃhanādaṃ samaṃ cakrur lakṣmaṇasya samīpataḥ</li>
    </ul>
    <ul id="R_4.030.036">
        <li id="R_4.030.036ab">tena śabdena mahatā pratyabudhyata vānaraḥ</li>
        <li id="R_4.030.036cd">madavihvalatāmrākṣo vyākulasragvibhūṣaṇaḥ</li>
    </ul>
    <ul id="R_4.030.037">
        <li id="R_4.030.037ab">athāṅgadavacaḥ śrutvā tenaiva ca samāgatau</li>
        <li id="R_4.030.037cd">mantriṇo vānarendrasya saṃmatodāradarśinau</li>
    </ul>
    <ul id="R_4.030.038">
        <li id="R_4.030.038ab">plakṣaś caiva prabhāvaś ca mantriṇāv arthadharmayoḥ</li>
        <li id="R_4.030.038cd">vaktum uccāvacaṃ prāptaṃ lakṣmaṇaṃ tau śaśaṃsatuḥ</li>
    </ul>
    <ul id="R_4.030.039">
        <li id="R_4.030.039ab">prasādayitvā sugrīvaṃ vacanaiḥ sāmaniścitaiḥ</li>
        <li id="R_4.030.039cd">āsīnaṃ paryupāsīnau yathā śakraṃ marutpatim</li>
    </ul>
    <ul id="R_4.030.040">
        <li id="R_4.030.040ab">satyasaṃdhau mahābhāgau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_4.030.040cd">vayasya bhāvaṃ saṃprāptau rājyārhau rājyadāyinau</li>
    </ul>
    <ul id="R_4.030.041">
        <li id="R_4.030.041ab">tayor eko dhanuṣpāṇir dvāri tiṣṭhati lakṣmaṇaḥ</li>
        <li id="R_4.030.041cd">yasya bhītāḥ pravepante nādān muñcanti vānarāḥ</li>
    </ul>
    <ul id="R_4.030.042">
        <li id="R_4.030.042ab">sa eṣa rāghavabhrātā lakṣmaṇo vākyasārathiḥ</li>
        <li id="R_4.030.042cd">vyavasāya rathaḥ prāptas tasya rāmasya śāsanāt</li>
    </ul>
    <ul id="R_4.030.043">
        <li id="R_4.030.043ab">tasya mūrdhnā praṇamya tvaṃ saputraḥ saha bandhubhiḥ</li>
        <li id="R_4.030.043cd">rājaṃs tiṣṭha svasamaye bhava satyapratiśravaḥ</li>
    </ul>
    <ul id="R_4.031.001">
        <li id="R_4.031.001ab">aṅgadasya vacaḥ śrutvā sugrīvaḥ sacivaiḥ saha</li>
        <li id="R_4.031.001cd">lakṣmaṇaṃ kupitaṃ śrutvā mumocāsanam ātmavān</li>
    </ul>
    <ul id="R_4.031.002">
        <li id="R_4.031.002ab">sacivān abravīd vākyaṃ niścitya gurulāghavam</li>
        <li id="R_4.031.002cd">mantrajñān mantrakuśalo mantreṣu pariniṣṭhitaḥ</li>
    </ul>
    <ul id="R_4.031.003">
        <li id="R_4.031.003ab">na me durvyāhṛtaṃ kiṃ cin nāpi me duranuṣṭhitam</li>
        <li id="R_4.031.003cd">lakṣmaṇo rāghavabhrātā kruddhaḥ kim iti cintaye</li>
    </ul>
    <ul id="R_4.031.004">
        <li id="R_4.031.004ab">asuhṛdbhir mamāmitrair nityam antaradarśibhiḥ</li>
        <li id="R_4.031.004cd">mama doṣān asaṃbhūtāñ śrāvito rāghavānujaḥ</li>
    </ul>
    <ul id="R_4.031.005">
        <li id="R_4.031.005ab">atra tāvad yathābuddhi sarvair eva yathāvidhi</li>
        <li id="R_4.031.005cd">bhavadbhir niścayas tasya vijñeyo nipuṇaṃ śanaiḥ</li>
    </ul>
    <ul id="R_4.031.006">
        <li id="R_4.031.006ab">na khalv asti mama trāso lakṣmaṇān nāpi rāghavāt</li>
        <li id="R_4.031.006cd">mitraṃ tv asthāna kupitaṃ janayaty eva saṃbhramam</li>
    </ul>
    <ul id="R_4.031.007">
        <li id="R_4.031.007ab">sarvathā sukaraṃ mitraṃ duṣkaraṃ paripālanam</li>
        <li id="R_4.031.007cd">anityatvāt tu cittānāṃ prītir alpe 'pi bhidyate</li>
    </ul>
    <ul id="R_4.031.008">
        <li id="R_4.031.008ab">atonimittaṃ trasto 'haṃ rāmeṇa tu mahātmanā</li>
        <li id="R_4.031.008cd">yan mamopakṛtaṃ śakyaṃ pratikartuṃ na tan mayā</li>
    </ul>
    <ul id="R_4.031.009">
        <li id="R_4.031.009ab">sugrīveṇaivam uktas tu hanumān haripuṃgavaḥ</li>
        <li id="R_4.031.009cd">uvāca svena tarkeṇa madhye vānaramantriṇām</li>
    </ul>
    <ul id="R_4.031.010">
        <li id="R_4.031.010ab">sarvathā naitad āścaryaṃ yat tvaṃ harigaṇeśvara</li>
        <li id="R_4.031.010cd">na vismarasi susnigdham upakārakṛtaṃ śubham</li>
    </ul>
    <ul id="R_4.031.011">
        <li id="R_4.031.011ab">rāghaveṇa tu śūreṇa bhayam utsṛjya dūrataḥ</li>
        <li id="R_4.031.011cd">tvatpriyārthaṃ hato vālī śakratulyaparākramaḥ</li>
    </ul>
    <ul id="R_4.031.012">
        <li id="R_4.031.012ab">sarvathā praṇayāt kruddho rāghavo nātra saṃśayaḥ</li>
        <li id="R_4.031.012cd">bhrātaraṃ sa prahitavāṃl lakṣmaṇaṃ lakṣmivardhanam</li>
    </ul>
    <ul id="R_4.031.013">
        <li id="R_4.031.013ab">tvaṃ pramatto na jānīṣe kālaṃ kalavidāṃ vara</li>
        <li id="R_4.031.013cd">phullasaptacchadaśyāmā pravṛttā tu śarac chivā</li>
    </ul>
    <ul id="R_4.031.014">
        <li id="R_4.031.014ab">nirmala grahanakṣatrā dyauḥ pranaṣṭabalāhakā</li>
        <li id="R_4.031.014cd">prasannāś ca diśaḥ sarvāḥ saritaś ca sarāṃsi ca</li>
    </ul>
    <ul id="R_4.031.015">
        <li id="R_4.031.015ab">prāptam udyogakālaṃ tu nāvaiṣi haripuṃgava</li>
        <li id="R_4.031.015cd">tvaṃ pramatta iti vyaktaṃ lakṣmaṇo 'yam ihāgataḥ</li>
    </ul>
    <ul id="R_4.031.016">
        <li id="R_4.031.016ab">ārtasya hṛtadārasya paruṣaṃ puruṣāntarāt</li>
        <li id="R_4.031.016cd">vacanaṃ marṣaṇīyaṃ te rāghavasya mahātmanaḥ</li>
    </ul>
    <ul id="R_4.031.017">
        <li id="R_4.031.017ab">kṛtāparādhasya hi te nānyat paśyāmy ahaṃ kṣamam</li>
        <li id="R_4.031.017cd">antareṇāñjaliṃ baddhvā lakṣmaṇasya prasādanāt</li>
    </ul>
    <ul id="R_4.031.018">
        <li id="R_4.031.018ab">niyuktair mantribhir vācyo avaśyaṃ pārthivo hitam</li>
        <li id="R_4.031.018cd">ata eva bhayaṃ tyaktvā bravīmy avadhṛtaṃ vacaḥ</li>
    </ul>
    <ul id="R_4.031.019">
        <li id="R_4.031.019ab">abhikruddhaḥ samartho hi cāpam udyamya rāghavaḥ</li>
        <li id="R_4.031.019cd">sadevāsuragandharvaṃ vaśe sthāpayituṃ jagat</li>
    </ul>
    <ul id="R_4.031.020">
        <li id="R_4.031.020ab">na sa kṣamaḥ kopayituṃ yaḥ prasādya punar bhavet</li>
        <li id="R_4.031.020cd">pūrvopakāraṃ smaratā kṛtajñena viśeṣataḥ</li>
    </ul>
    <ul id="R_4.031.021">
        <li id="R_4.031.021ab">tasya mūrdhnā praṇamya tvaṃ saputraḥ sasuhṛjjanaḥ</li>
        <li id="R_4.031.021cd">rājaṃs tiṣṭha svasamaye bhartur bhāryeva tadvaśe</li>
    </ul>
    <ul id="R_4.031.022">
        <li id="R_4.031.022ab">
            <seg type="pāda" n="a">na rāmarāmānujaśāsanaṃ tvayā</seg>
            <seg type="pāda" n="b">kapīndrayuktaṃ manasāpy apohitum</seg>
        </li>
        <li id="R_4.031.022cd">
            <seg type="pāda" n="c">mano hi te jñāsyati mānuṣaṃ balaṃ</seg>
            <seg type="pāda" n="d">sarāghavasyāsya surendravarcasaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.032.001">
        <li id="R_4.032.001ab">atha pratisamādiṣṭo lakṣmaṇaḥ paravīrahā</li>
        <li id="R_4.032.001cd">praviveśa guhāṃ ghorāṃ kiṣkindhāṃ rāmaśāsanāt</li>
    </ul>
    <ul id="R_4.032.002">
        <li id="R_4.032.002ab">dvārasthā harayas tatra mahākāyā mahābalāḥ</li>
        <li id="R_4.032.002cd">babhūvur lakṣmaṇaṃ dṛṣṭvā sarve prāñjalayaḥ sthitāḥ</li>
    </ul>
    <ul id="R_4.032.003">
        <li id="R_4.032.003ab">niḥśvasantaṃ tu taṃ dṛṣṭvā kruddhaṃ daśarathātmajam</li>
        <li id="R_4.032.003cd">babhūvur harayas trastā na cainaṃ paryavārayan</li>
    </ul>
    <ul id="R_4.032.004">
        <li id="R_4.032.004ab">sa taṃ ratnamayīṃ śrīmān divyāṃ puṣpitakānanām</li>
        <li id="R_4.032.004cd">ramyāṃ ratnasamākīrṇāṃ dadarśa mahatīṃ guhām</li>
    </ul>
    <ul id="R_4.032.005">
        <li id="R_4.032.005ab">harmyaprāsādasaṃbādhāṃ nānāpaṇyopaśobhitām</li>
        <li id="R_4.032.005cd">sarvakāmaphalair vṛkṣaiḥ puṣpitair upaśobhitām</li>
    </ul>
    <ul id="R_4.032.006">
        <li id="R_4.032.006ab">devagandharvaputraiś ca vānaraiḥ kāmarūpibhiḥ</li>
        <li id="R_4.032.006cd">divya mālyāmbaradhāraiḥ śobhitāṃ priyadarśanaiḥ</li>
    </ul>
    <ul id="R_4.032.007">
        <li id="R_4.032.007ab">candanāgarupadmānāṃ gandhaiḥ surabhigandhinām</li>
        <li id="R_4.032.007cd">maireyāṇāṃ madhūnāṃ ca saṃmoditamahāpathām</li>
    </ul>
    <ul id="R_4.032.008">
        <li id="R_4.032.008ab">vindhyamerugiriprasthaiḥ prāsādair naikabhūmibhiḥ</li>
        <li id="R_4.032.008cd">dadarśa girinadyaś ca vimalās tatra rāghavaḥ</li>
    </ul>
    <ul id="R_4.032.009">
        <li id="R_4.032.009ab">aṅgadasya gṛhaṃ ramyaṃ maindasya dvividasya ca</li>
        <li id="R_4.032.009cd">gavayasya gavākṣasya gajasya śarabhasya ca</li>
    </ul>
    <ul id="R_4.032.010">
        <li id="R_4.032.010ab">vidyunmāleś ca saṃpāteḥ sūryākṣasya hanūmataḥ</li>
        <li id="R_4.032.010cd">vīrabāhoḥ subāhoś ca nalasya ca mahātmanaḥ</li>
    </ul>
    <ul id="R_4.032.011">
        <li id="R_4.032.011ab">kumudasya suṣeṇasya tārajāmbavatos tathā</li>
        <li id="R_4.032.011cd">dadhivaktrasya nīlasya supāṭalasunetrayoḥ</li>
    </ul>
    <ul id="R_4.032.012">
        <li id="R_4.032.012ab">eteṣāṃ kapimukhyānāṃ rājamārge mahātmanām</li>
        <li id="R_4.032.012cd">dadarśa gṛhamukhyāni mahāsārāṇi lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_4.032.013">
        <li id="R_4.032.013ab">pāṇḍurābhraprakāśāni divyamālyayutāni ca</li>
        <li id="R_4.032.013cd">prabhūtadhanadhānyāni strīratnaiḥ śobhitāni ca</li>
    </ul>
    <ul id="R_4.032.014">
        <li id="R_4.032.014ab">pāṇḍureṇa tu śailena parikṣiptaṃ durāsadam</li>
        <li id="R_4.032.014cd">vānarendragṛhaṃ ramyaṃ mahendrasadanopamam</li>
    </ul>
    <ul id="R_4.032.015">
        <li id="R_4.032.015ab">śulkaiḥ prāsādaśikharaiḥ kailāsaśikharopamaiḥ</li>
        <li id="R_4.032.015cd">sarvakāmaphalair vṛkṣaiḥ puṣṭitair upaśobhitam</li>
    </ul>
    <ul id="R_4.032.016">
        <li id="R_4.032.016ab">mahendradattaiḥ śrīmadbhir nīlajīmūtasaṃnibhaiḥ</li>
        <li id="R_4.032.016cd">divyapuṣpaphalair vṛkṣaiḥ śītacchāyair manoramaiḥ</li>
    </ul>
    <ul id="R_4.032.017">
        <li id="R_4.032.017ab">haribhiḥ saṃvṛtadvāraṃ balibhiḥ śastrapāṇibhiḥ</li>
        <li id="R_4.032.017cd">divyamālyāvṛtaṃ śubhraṃ taptakāñcanatoraṇam</li>
    </ul>
    <ul id="R_4.032.018">
        <li id="R_4.032.018ab">sugrīvasya gṛhaṃ ramyaṃ praviveśa mahābalaḥ</li>
        <li id="R_4.032.018cd">avāryamāṇaḥ saumitrir mahābhram iva bhāskaraḥ</li>
    </ul>
    <ul id="R_4.032.019">
        <li id="R_4.032.019ab">sa sapta kakṣyā dharmātmā yānāsanasamāvṛtāḥ</li>
        <li id="R_4.032.019cd">praviśya sumahad guptaṃ dadarśāntaḥpuraṃ mahat</li>
    </ul>
    <ul id="R_4.032.020">
        <li id="R_4.032.020ab">haimarājataparyaṅkair bahubhiś ca varāsanaiḥ</li>
        <li id="R_4.032.020cd">mahārhāstaraṇopetais tatra tatropaśobhitam</li>
    </ul>
    <ul id="R_4.032.021">
        <li id="R_4.032.021ab">praviśann eva satataṃ śuśrāva madhurasvaram</li>
        <li id="R_4.032.021cd">tantrīgītasamākīrṇaṃ samagītapadākṣaram</li>
    </ul>
    <ul id="R_4.032.022">
        <li id="R_4.032.022ab">bahvīś ca vividhākārā rūpayauvanagarvitāḥ</li>
        <li id="R_4.032.022cd">striyaḥ sugrīvabhavane dadarśa sa mahābalaḥ</li>
    </ul>
    <ul id="R_4.032.023">
        <li id="R_4.032.023ab">dṛṣṭvābhijanasaṃpannāś citramālyakṛtasrajaḥ</li>
        <li id="R_4.032.023cd">varamālyakṛtavyagrā bhūṣaṇottamabhūṣitāḥ</li>
    </ul>
    <ul id="R_4.032.024">
        <li id="R_4.032.024ab">nātṛptān nāti ca vyagrān nānudāttaparicchadān</li>
        <li id="R_4.032.024cd">sugrīvānucarāṃś cāpi lakṣayām āsa lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_4.032.025">
        <li id="R_4.032.025ab">tataḥ sugrīvam āsīnaṃ kāñcane paramāsane</li>
        <li id="R_4.032.025cd">mahārhāstaraṇopete dadarśādityasaṃnibham</li>
    </ul>
    <ul id="R_4.032.026">
        <li id="R_4.032.026ab">divyābharaṇacitrāṅgaṃ divyarūpaṃ yaśasvinam</li>
        <li id="R_4.032.026cd">divyamālyāmbaradharaṃ mahendram iva durjayam</li>
        <li id="R_4.032.026ef">divyābharaṇamālyābhiḥ pramadābhiḥ samāvṛtam</li>
    </ul>
    <ul id="R_4.032.027">
        <li id="R_4.032.027ab">
            <seg type="pāda" n="a">rumāṃ tu vīraḥ parirabhya gāḍhaṃ</seg>
            <seg type="pāda" n="b">varāsanastho varahemavarṇaḥ</seg>
        </li>
        <li id="R_4.032.027cd">
            <seg type="pāda" n="c">dadarśa saumitrim adīnasattvaṃ</seg>
            <seg type="pāda" n="d">viśālanetraḥ suviśālanetram</seg>
        </li>
    </ul>
    <ul id="R_4.033.001">
        <li id="R_4.033.001ab">tam apratihataṃ kruddhaṃ praviṣṭaṃ puruṣarṣabham</li>
        <li id="R_4.033.001cd">sugrīvo lakṣmaṇaṃ dṛṣṭvā babhūva vyathitendriyaḥ</li>
    </ul>
    <ul id="R_4.033.002">
        <li id="R_4.033.002ab">kruddhaṃ niḥśvasamānaṃ taṃ pradīptam iva tejasā</li>
        <li id="R_4.033.002cd">bhrātur vyasanasaṃtaptaṃ dṛṣṭvā daśarathātmajam</li>
    </ul>
    <ul id="R_4.033.003">
        <li id="R_4.033.003ab">utpapāta hariśreṣṭho hitvā sauvarṇam āsanam</li>
        <li id="R_4.033.003cd">mahān mahendrasya yathā svalaṃkṛta iva dhvajaḥ</li>
    </ul>
    <ul id="R_4.033.004">
        <li id="R_4.033.004ab">utpatantam anūtpetū rumāprabhṛtayaḥ striyaḥ</li>
        <li id="R_4.033.004cd">sugrīvaṃ gagane pūrṇaṃ candraṃ tārāgaṇā iva</li>
    </ul>
    <ul id="R_4.033.005">
        <li id="R_4.033.005ab">saṃraktanayanaḥ śrīmān vicacāla kṛtāñjaliḥ</li>
        <li id="R_4.033.005cd">babhūvāvasthitas tatra kalpavṛkṣo mahān iva</li>
    </ul>
    <ul id="R_4.033.006">
        <li id="R_4.033.006ab">rumā dvitīyaṃ sugrīvaṃ nārīmadhyagataṃ sthitam</li>
        <li id="R_4.033.006cd">abravīl lakṣmaṇaḥ kruddhaḥ satāraṃ śaśinaṃ yathā</li>
    </ul>
    <ul id="R_4.033.007">
        <li id="R_4.033.007ab">sattvābhijanasaṃpannaḥ sānukrośo jitendriyaḥ</li>
        <li id="R_4.033.007cd">kṛtajñaḥ satyavādī ca rājā loke mahīyate</li>
    </ul>
    <ul id="R_4.033.008">
        <li id="R_4.033.008ab">yas tu rājā sthito 'dharme mitrāṇām upakāriṇām</li>
        <li id="R_4.033.008cd">mithyāpratijñāṃ kurute ko nṛśaṃsataras tataḥ</li>
    </ul>
    <ul id="R_4.033.009">
        <li id="R_4.033.009ab">śatam aśvānṛte hanti sahasraṃ tu gavānṛte</li>
        <li id="R_4.033.009cd">ātmānaṃ svajanaṃ hanti puruṣaḥ puruṣānṛte</li>
    </ul>
    <ul id="R_4.033.010">
        <li id="R_4.033.010ab">pūrvaṃ kṛtārtho mitrāṇāṃ na tat pratikaroti yaḥ</li>
        <li id="R_4.033.010cd">kṛtaghnaḥ sarvabhūtānāṃ sa vadhyaḥ plavageśvara</li>
    </ul>
    <ul id="R_4.033.011">
        <li id="R_4.033.011ab">gīto 'yaṃ brahmaṇā ślokaḥ sarvalokanamaskṛtaḥ</li>
        <li id="R_4.033.011cd">dṛṣṭvā kṛtaghnaṃ kruddhena taṃ nibodha plavaṃgama</li>
    </ul>
    <ul id="R_4.033.012">
        <li id="R_4.033.012ab">brahmaghne ca surāpe ca core bhagnavrate tathā</li>
        <li id="R_4.033.012cd">niṣkṛtir vihitā sadbhiḥ kṛtaghne nāsti niṣkṛtiḥ</li>
    </ul>
    <ul id="R_4.033.013">
        <li id="R_4.033.013ab">anāryas tvaṃ kṛtaghnaś ca mithyāvādī ca vānara</li>
        <li id="R_4.033.013cd">pūrvaṃ kṛtārtho rāmasya na tat pratikaroṣi yat</li>
    </ul>
    <ul id="R_4.033.014">
        <li id="R_4.033.014ab">nanu nāma kṛtārthena tvayā rāmasya vānara</li>
        <li id="R_4.033.014cd">sītāyā mārgaṇe yatnaḥ kartavyaḥ kṛtam icchatā</li>
    </ul>
    <ul id="R_4.033.015">
        <li id="R_4.033.015ab">sa tvaṃ grāmyeṣu bhogeṣu sakto mithyā pratiśravaḥ</li>
        <li id="R_4.033.015cd">na tvāṃ rāmo vijānīte sarpaṃ maṇḍūkarāviṇam</li>
    </ul>
    <ul id="R_4.033.016">
        <li id="R_4.033.016ab">mahābhāgena rāmeṇa pāpaḥ karuṇavedinā</li>
        <li id="R_4.033.016cd">harīṇāṃ prāpito rājyaṃ tvaṃ durātmā mahātmanā</li>
    </ul>
    <ul id="R_4.033.017">
        <li id="R_4.033.017ab">kṛtaṃ cen nābhijānīṣe rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_4.033.017cd">sadyas tvaṃ niśitair bāṇair hato drakṣyasi vālinam</li>
    </ul>
    <ul id="R_4.033.018">
        <li id="R_4.033.018ab">na ca saṃkucitaḥ panthā yena vālī hato gataḥ</li>
        <li id="R_4.033.018cd">samaye tiṣṭha sugrīva mā vālipatham anvagāḥ</li>
    </ul>
    <ul id="R_4.033.019">
        <li id="R_4.033.019ab">
            <seg type="pāda" n="a">na nūnam ikṣvākuvarasya kārmukāc</seg>
            <seg type="pāda" n="b">cyutāñ śarān paśyasi vajrasaṃnibhān</seg>
        </li>
        <li id="R_4.033.019cd">
            <seg type="pāda" n="c">tataḥ sukhaṃ nāma niṣevase sukhī</seg>
            <seg type="pāda" n="d">na rāmakāryaṃ manasāpy avekṣase</seg>
        </li>
    </ul>
    <ul id="R_4.034.001">
        <li id="R_4.034.001ab">tathā bruvāṇaṃ saumitriṃ pradīptam iva tejasā</li>
        <li id="R_4.034.001cd">abravīl lakṣmaṇaṃ tārā tārādhipanibhānanā</li>
    </ul>
    <ul id="R_4.034.002">
        <li id="R_4.034.002ab">naivaṃ lakṣmaṇa vaktavyo nāyaṃ paruṣam arhati</li>
        <li id="R_4.034.002cd">harīṇām īśvaraḥ śrotuṃ tava vaktrād viśeṣataḥ</li>
    </ul>
    <ul id="R_4.034.003">
        <li id="R_4.034.003ab">naivākṛtajñaḥ sugrīvo na śaṭho nāpi dāruṇaḥ</li>
        <li id="R_4.034.003cd">naivānṛtakatho vīra na jihmaś ca kapīśvaraḥ</li>
    </ul>
    <ul id="R_4.034.004">
        <li id="R_4.034.004ab">upakāraṃ kṛtaṃ vīro nāpy ayaṃ vismṛtaḥ kapiḥ</li>
        <li id="R_4.034.004cd">rāmeṇa vīra sugrīvo yad anyair duṣkaraṃ raṇe</li>
    </ul>
    <ul id="R_4.034.005">
        <li id="R_4.034.005ab">rāmaprasādāt kīrtiṃ ca kapirājyaṃ ca śāśvatam</li>
        <li id="R_4.034.005cd">prāptavān iha sugrīvo rumāṃ māṃ ca paraṃtapa</li>
    </ul>
    <ul id="R_4.034.006">
        <li id="R_4.034.006ab">suduḥkhaṃ śāyitaḥ pūrvaṃ prāpyedaṃ sukham uttamam</li>
        <li id="R_4.034.006cd">prāptakālaṃ na jānīte viśvāmitro yathā muniḥ</li>
    </ul>
    <ul id="R_4.034.007">
        <li id="R_4.034.007ab">ghṛtācyāṃ kila saṃsakto daśavarṣāṇi lakṣmaṇa</li>
        <li id="R_4.034.007cd">aho 'manyata dharmātmā viśvāmitro mahāmuniḥ</li>
    </ul>
    <ul id="R_4.034.008">
        <li id="R_4.034.008ab">sa hi prāptaṃ na jānīte kālaṃ kālavidāṃ varaḥ</li>
        <li id="R_4.034.008cd">viśvāmitro mahātejāḥ kiṃ punar yaḥ pṛthagjanaḥ</li>
    </ul>
    <ul id="R_4.034.009">
        <li id="R_4.034.009ab">dehadharmaṃ gatasyāsya pariśrāntasya lakṣmaṇa</li>
        <li id="R_4.034.009cd">avitṛptasya kāmeṣu rāmaḥ kṣantum ihārhati</li>
    </ul>
    <ul id="R_4.034.010">
        <li id="R_4.034.010ab">na ca roṣavaśaṃ tāta gantum arhasi lakṣmaṇa</li>
        <li id="R_4.034.010cd">niścayārtham avijñāya sahasā prākṛto yathā</li>
    </ul>
    <ul id="R_4.034.011">
        <li id="R_4.034.011ab">sattvayuktā hi puruṣās tvadvidhāḥ puruṣarṣabha</li>
        <li id="R_4.034.011cd">avimṛśya na roṣasya sahasā yānti vaśyatām</li>
    </ul>
    <ul id="R_4.034.012">
        <li id="R_4.034.012ab">prasādaye tvāṃ dharmajña sugrīvārthe samāhitā</li>
        <li id="R_4.034.012cd">mahān roṣasamutpannaḥ saṃrambhas tyajyatām ayam</li>
    </ul>
    <ul id="R_4.034.013">
        <li id="R_4.034.013ab">rumāṃ māṃ kapirājyaṃ ca dhanadhānyavasūni ca</li>
        <li id="R_4.034.013cd">rāmapriyārthaṃ sugrīvas tyajed iti matir mama</li>
    </ul>
    <ul id="R_4.034.014">
        <li id="R_4.034.014ab">samāneṣvyati sugrīvaḥ sītayā saha rāghavam</li>
        <li id="R_4.034.014cd">śaśāṅkam iva rohiṣyā nihatvā rāvaṇaṃ raṇe</li>
    </ul>
    <ul id="R_4.034.015">
        <li id="R_4.034.015ab">śatakoṭisahasrāṇi laṅkāyāṃ kila rakṣasām</li>
        <li id="R_4.034.015cd">ayutāni ca ṣaṭtriṃśat sahasrāṇi śatāni ca</li>
    </ul>
    <ul id="R_4.034.016">
        <li id="R_4.034.016ab">ahatvā tāṃś ca durdharṣān rākṣasān kāmarūpiṇaḥ</li>
        <li id="R_4.034.016cd">na śakyo rāvaṇo hantuṃ yena sā maithilī hṛtā</li>
    </ul>
    <ul id="R_4.034.017">
        <li id="R_4.034.017ab">te na śakyā raṇe hantum asahāyena lakṣmaṇa</li>
        <li id="R_4.034.017cd">rāvaṇaḥ krūrakarmā ca sugrīveṇa viśeṣataḥ</li>
    </ul>
    <ul id="R_4.034.018">
        <li id="R_4.034.018ab">evam ākhyātavān vālī sa hy abhijño harīśvaraḥ</li>
        <li id="R_4.034.018cd">āgamas tu na me vyaktaḥ śravāt tasya bravīmy aham</li>
    </ul>
    <ul id="R_4.034.019">
        <li id="R_4.034.019ab">tvatsahāyanimittaṃ vai preṣitā haripuṃgavāḥ</li>
        <li id="R_4.034.019cd">ānetuṃ vānarān yuddhe subahūn hariyūthapān</li>
    </ul>
    <ul id="R_4.034.020">
        <li id="R_4.034.020ab">tāṃś ca pratīkṣamāṇo 'yaṃ vikrāntān sumahābalān</li>
        <li id="R_4.034.020cd">rāghavasyārthasiddhyarthaṃ na niryāti harīśvaraḥ</li>
    </ul>
    <ul id="R_4.034.021">
        <li id="R_4.034.021ab">kṛtā tu saṃsthā saumitre sugrīveṇa yathāpurā</li>
        <li id="R_4.034.021cd">adya tair vānarair sarvair āgantavyaṃ mahābalaiḥ</li>
    </ul>
    <ul id="R_4.034.022">
        <li id="R_4.034.022ab">ṛkṣakoṭisahasrāṇi golāṅgūlaśatāni ca</li>
        <li id="R_4.034.022cd">adya tvām upayāsyanti jahi kopam ariṃdama</li>
        <li id="R_4.034.022ef">koṭyo 'nekās tu kākutstha kapīnāṃ dīptatejasām</li>
    </ul>
    <ul id="R_4.034.023">
        <li id="R_4.034.023ab">
            <seg type="pāda" n="a">tava hi mukham idaṃ nirīkṣya kopāt</seg>
            <seg type="pāda" n="b">kṣatajanibhe nayane nirīkṣamāṇāḥ</seg>
        </li>
        <li id="R_4.034.023cd">
            <seg type="pāda" n="c">harivaravanitā na yānti śāntiṃ</seg>
            <seg type="pāda" n="d">prathamabhayasya hi śaṅkitāḥ sma sarvāḥ</seg>
        </li>
    </ul>
    <ul id="R_4.035.001">
        <li id="R_4.035.001ab">ity uktas tārayā vākyaṃ praśritaṃ dharmasaṃhitam</li>
        <li id="R_4.035.001cd">mṛdusvabhāvaḥ saumitriḥ pratijagrāha tad vacaḥ</li>
    </ul>
    <ul id="R_4.035.002">
        <li id="R_4.035.002ab">tasmin pratigṛhīte tu vākye harigaṇeśvaraḥ</li>
        <li id="R_4.035.002cd">lakṣmaṇāt sumahat trāsaṃ vastraṃ klinnam ivātyajat</li>
    </ul>
    <ul id="R_4.035.003">
        <li id="R_4.035.003ab">tataḥ kaṇṭhagataṃ mālyaṃ citraṃ bahuguṇaṃ mahat</li>
        <li id="R_4.035.003cd">ciccheda vimadaś cāsīt sugrīvo vānareśvaraḥ</li>
    </ul>
    <ul id="R_4.035.004">
        <li id="R_4.035.004ab">sa lakṣmaṇaṃ bhīmabalaṃ sarvavānarasattamaḥ</li>
        <li id="R_4.035.004cd">abravīt praśritaṃ vākyaṃ sugrīvaḥ saṃpraharṣayan</li>
    </ul>
    <ul id="R_4.035.005">
        <li id="R_4.035.005ab">pranaṣṭā śrīś ca kīrtiś ca kapirājyaṃ ca śāśvatam</li>
        <li id="R_4.035.005cd">rāmaprasādāt saumitre punaḥ prāptam idaṃ mayā</li>
    </ul>
    <ul id="R_4.035.006">
        <li id="R_4.035.006ab">kaḥ śaktas tasya devasya khyātasya svena karmaṇā</li>
        <li id="R_4.035.006cd">tādṛśaṃ vikramaṃ vīra pratikartum ariṃdama</li>
    </ul>
    <ul id="R_4.035.007">
        <li id="R_4.035.007ab">sītāṃ prāpsyati dharmātmā vadhiṣyati ca rāvaṇam</li>
        <li id="R_4.035.007cd">sahāyamātreṇa mayā rāghavaḥ svena tejasā</li>
    </ul>
    <ul id="R_4.035.008">
        <li id="R_4.035.008ab">sahāyakṛtyaṃ hi tasya yena sapta mahādrumāḥ</li>
        <li id="R_4.035.008cd">śailaś ca vasudhā caiva bāṇenaikena dāritāḥ</li>
    </ul>
    <ul id="R_4.035.009">
        <li id="R_4.035.009ab">dhanur visphāramāṇasya yasya śabdena lakṣmaṇa</li>
        <li id="R_4.035.009cd">saśailā kampitā bhūmiḥ sahāyais tasya kiṃ nu vai</li>
    </ul>
    <ul id="R_4.035.010">
        <li id="R_4.035.010ab">anuyātrāṃ narendrasya kariṣye 'haṃ nararṣabha</li>
        <li id="R_4.035.010cd">gacchato rāvaṇaṃ hantuṃ vairiṇaṃ sapuraḥsaram</li>
    </ul>
    <ul id="R_4.035.011">
        <li id="R_4.035.011ab">yadi kiṃ cid atikrāntaṃ viśvāsāt praṇayena vā</li>
        <li id="R_4.035.011cd">preṣyasya kṣamitavyaṃ me na kaś cin nāparādhyati</li>
    </ul>
    <ul id="R_4.035.012">
        <li id="R_4.035.012ab">iti tasya bruvāṇasya sugrīvasya mahātmanaḥ</li>
        <li id="R_4.035.012cd">abhaval lakṣmaṇaḥ prītaḥ preṃṇā cedam uvāca ha</li>
    </ul>
    <ul id="R_4.035.013">
        <li id="R_4.035.013ab">sarvathā hi mama bhrātā sanātho vānareśvara</li>
        <li id="R_4.035.013cd">tvayā nāthena sugrīva praśritena viśeṣataḥ</li>
    </ul>
    <ul id="R_4.035.014">
        <li id="R_4.035.014ab">yas te prabhāvaḥ sugrīva yac ca te śaucam uttamam</li>
        <li id="R_4.035.014cd">arhas taṃ kapirājyasya śriyaṃ bhoktum anuttamām</li>
    </ul>
    <ul id="R_4.035.015">
        <li id="R_4.035.015ab">sahāyena ca sugrīva tvayā rāmaḥ pratāpavān</li>
        <li id="R_4.035.015cd">vadhiṣyati raṇe śatrūn acirān nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_4.035.016">
        <li id="R_4.035.016ab">dharmajñasya kṛtajñasya saṃgrāmeṣv anivartinaḥ</li>
        <li id="R_4.035.016cd">upapannaṃ ca yuktaṃ ca sugrīva tava bhāṣitam</li>
    </ul>
    <ul id="R_4.035.017">
        <li id="R_4.035.017ab">doṣajñaḥ sati sāmarthye ko 'nyo bhāṣitum arhati</li>
        <li id="R_4.035.017cd">varjayitvā mama jyeṣṭhaṃ tvāṃ ca vānarasattama</li>
    </ul>
    <ul id="R_4.035.018">
        <li id="R_4.035.018ab">sadṛśaś cāsi rāmasya vikrameṇa balena ca</li>
        <li id="R_4.035.018cd">sahāyo daivatair dattaś cirāya haripuṃgava</li>
    </ul>
    <ul id="R_4.035.019">
        <li id="R_4.035.019ab">kiṃ tu śīghram ito vīra niṣkrāma tvaṃ mayā saha</li>
        <li id="R_4.035.019cd">sāntvayasva vayasyaṃ ca bhāryāharaṇaduḥkhitam</li>
    </ul>
    <ul id="R_4.035.020">
        <li id="R_4.035.020ab">yac ca śokābhibhūtasya śrutvā rāmasya bhāṣitam</li>
        <li id="R_4.035.020cd">mayā tvaṃ paruṣāṇy uktas tac ca tvaṃ kṣantum arhasi</li>
    </ul>
    <ul id="R_4.036.001">
        <li id="R_4.036.001ab">evam uktas tu sugrīvo lakṣmaṇena mahātmanā</li>
        <li id="R_4.036.001cd">hanumantaṃ sthitaṃ pārśve sacivaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_4.036.002">
        <li id="R_4.036.002ab">mahendrahimavadvindhyakailāsaśikhareṣu ca</li>
        <li id="R_4.036.002cd">mandare pāṇḍuśikhare pañcaśaileṣu ye sthitāḥ</li>
    </ul>
    <ul id="R_4.036.003">
        <li id="R_4.036.003ab">taruṇādityavarṇeṣu bhrājamāneṣu sarvaśaḥ</li>
        <li id="R_4.036.003cd">parvateṣu samudrānte paścimasyāṃ tu ye diśi</li>
    </ul>
    <ul id="R_4.036.004">
        <li id="R_4.036.004ab">ādityabhavane caiva girau saṃdhyābhrasaṃnibhe</li>
        <li id="R_4.036.004cd">padmatālavanaṃ bhīmaṃ saṃśritā haripuṃgavāḥ</li>
    </ul>
    <ul id="R_4.036.005">
        <li id="R_4.036.005ab">añjanāmbudasaṃkāśāḥ kuñjarapratimaujasaḥ</li>
        <li id="R_4.036.005cd">añjane parate caiva ye vasanti plavaṃgamāḥ</li>
    </ul>
    <ul id="R_4.036.006">
        <li id="R_4.036.006ab">manaḥśilā guhāvāsā vānarāḥ kanakaprabhāḥ</li>
        <li id="R_4.036.006cd">merupārśvagatāś caiva ye ca dhūmragiriṃ śritāḥ</li>
    </ul>
    <ul id="R_4.036.007">
        <li id="R_4.036.007ab">taruṇādityavarṇāś ca parvate ye mahāruṇe</li>
        <li id="R_4.036.007cd">pibanto madhumaireyaṃ bhīmavegāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_4.036.008">
        <li id="R_4.036.008ab">vaneṣu ca suramyeṣu sugandhiṣu mahatsu ca</li>
        <li id="R_4.036.008cd">tāpasānāṃ ca ramyeṣu vanānteṣu samantataḥ</li>
    </ul>
    <ul id="R_4.036.009">
        <li id="R_4.036.009ab">tāṃs tāṃs tvam ānaya kṣipraṃ pṛthivyāṃ sarvavānarān</li>
        <li id="R_4.036.009cd">sāmadānādibhiḥ kalpair āśu preṣaya vānarān</li>
    </ul>
    <ul id="R_4.036.010">
        <li id="R_4.036.010ab">preṣitāḥ prathamaṃ ye ca mayā dūtā mahājavāḥ</li>
        <li id="R_4.036.010cd">tvaraṇārthaṃ tu bhūyas tvaṃ harīn saṃpreṣayāparān</li>
    </ul>
    <ul id="R_4.036.011">
        <li id="R_4.036.011ab">ye prasaktāś ca kāmeṣu dīrghasūtrāś ca vānarāḥ</li>
        <li id="R_4.036.011cd">ihānayasva tān sarvāñ śīghraṃ tu mama śāsanāt</li>
    </ul>
    <ul id="R_4.036.012">
        <li id="R_4.036.012ab">ahobhir daśabhir ye ca nāgacchanti mamājñayā</li>
        <li id="R_4.036.012cd">hantavyās te durātmāno rājaśāsanadūṣakāḥ</li>
    </ul>
    <ul id="R_4.036.013">
        <li id="R_4.036.013ab">śatāny atha sahasrāṇi koṭyaś ca mama śāsanāt</li>
        <li id="R_4.036.013cd">prayāntu kapisiṃhānāṃ diśo mama mate sthitāḥ</li>
    </ul>
    <ul id="R_4.036.014">
        <li id="R_4.036.014ab">meghaparvatasaṃkāśāś chādayanta ivāmbaram</li>
        <li id="R_4.036.014cd">ghorarūpāḥ kapiśreṣṭhā yāntu macchāsanād itaḥ</li>
    </ul>
    <ul id="R_4.036.015">
        <li id="R_4.036.015ab">te gatijñā gatiṃ gatvā pṛthivyāṃ sarvavānarāḥ</li>
        <li id="R_4.036.015cd">ānayantu harīn sarvāṃs tvaritāḥ śāsanān mama</li>
    </ul>
    <ul id="R_4.036.016">
        <li id="R_4.036.016ab">tasya vānararājasya śrutvā vāyusuto vacaḥ</li>
        <li id="R_4.036.016cd">dikṣu sarvāsu vikrāntān preṣayām āsa vānarān</li>
    </ul>
    <ul id="R_4.036.017">
        <li id="R_4.036.017ab">te padaṃ viṣṇuvikrāntaṃ patatrijyotiradhvagāḥ</li>
        <li id="R_4.036.017cd">prayātāḥ prahitā rājñā harayas tatkṣaṇena vai</li>
    </ul>
    <ul id="R_4.036.018">
        <li id="R_4.036.018ab">te samudreṣu giriṣu vaneṣu ca saritsu ca</li>
        <li id="R_4.036.018cd">vānarā vānarān sarvān rāmahetor acodayan</li>
    </ul>
    <ul id="R_4.036.019">
        <li id="R_4.036.019ab">mṛtyukālopamasyājñāṃ rājarājasya vānarāḥ</li>
        <li id="R_4.036.019cd">sugrīvasyāyayuḥ śrutvā sugrīvabhayadarśinaḥ</li>
    </ul>
    <ul id="R_4.036.020">
        <li id="R_4.036.020ab">tatas te 'ñjanasaṃkāśā gires tasmān mahājavāḥ</li>
        <li id="R_4.036.020cd">tisraḥ koṭyaḥ plavaṃgānāṃ niryayur yatra rāghavaḥ</li>
    </ul>
    <ul id="R_4.036.021">
        <li id="R_4.036.021ab">astaṃ gacchati yatrārkas tasmin girivare ratāḥ</li>
        <li id="R_4.036.021cd">taptahemasamābhāsās tasmāt koṭyo daśacyutāḥ</li>
    </ul>
    <ul id="R_4.036.022">
        <li id="R_4.036.022ab">kailāsa śikharebhyaś ca siṃhakesaravarcasām</li>
        <li id="R_4.036.022cd">tataḥ koṭisahasrāṇi vānarāṇām upāgaman</li>
    </ul>
    <ul id="R_4.036.023">
        <li id="R_4.036.023ab">phalamūlena jīvanto himavantam upāśritāḥ</li>
        <li id="R_4.036.023cd">teṣāṃ koṭisahasrāṇāṃ sahasraṃ samavartata</li>
    </ul>
    <ul id="R_4.036.024">
        <li id="R_4.036.024ab">aṅgāraka samānānāṃ bhīmānāṃ bhīmakarmaṇām</li>
        <li id="R_4.036.024cd">vindhyād vānarakoṭīnāṃ sahasrāṇy apatan drutam</li>
    </ul>
    <ul id="R_4.036.025">
        <li id="R_4.036.025ab">kṣīrodavelānilayās tamālavanavāsinaḥ</li>
        <li id="R_4.036.025cd">nārikelāśanāś caiva teṣāṃ saṃkhyā na vidyate</li>
    </ul>
    <ul id="R_4.036.026">
        <li id="R_4.036.026ab">vanebhyo gahvarebhyaś ca saridbhyaś ca mahājavāḥ</li>
        <li id="R_4.036.026cd">āgacchad vānarī senā pibantīva divākaram</li>
    </ul>
    <ul id="R_4.036.027">
        <li id="R_4.036.027ab">ye tu tvarayituṃ yātā vānarāḥ sarvavānarān</li>
        <li id="R_4.036.027cd">te vīrā himavac chailaṃ dadṛśus taṃ mahādrumam</li>
    </ul>
    <ul id="R_4.036.028">
        <li id="R_4.036.028ab">tasmin girivare ramye yajño maheśvaraḥ purā</li>
        <li id="R_4.036.028cd">sarvadevamanastoṣo babhau divyo manoharaḥ</li>
    </ul>
    <ul id="R_4.036.029">
        <li id="R_4.036.029ab">annaviṣyandajātāni mūlāni ca phalāni ca</li>
        <li id="R_4.036.029cd">amṛtasvādukalpāni dadṛśus tatra vānarāḥ</li>
    </ul>
    <ul id="R_4.036.030">
        <li id="R_4.036.030ab">tad anna saṃbhavaṃ divyaṃ phalaṃ mūlaṃ manoharam</li>
        <li id="R_4.036.030cd">yaḥ kaś cit sakṛd aśnāti māsaṃ bhavati tarpitaḥ</li>
    </ul>
    <ul id="R_4.036.031">
        <li id="R_4.036.031ab">tāni mūlāni divyāni phalāni ca phalāśanāḥ</li>
        <li id="R_4.036.031cd">auṣadhāni ca divyāni jagṛhur hariyūthapāḥ</li>
    </ul>
    <ul id="R_4.036.032">
        <li id="R_4.036.032ab">tasmāc ca yajñāyatanāt puṣpāṇi surabhīṇi ca</li>
        <li id="R_4.036.032cd">āninyur vānarā gatvā sugrīvapriyakāraṇāt</li>
    </ul>
    <ul id="R_4.036.033">
        <li id="R_4.036.033ab">te tu sarve harivarāḥ pṛthivyāṃ sarvavānarān</li>
        <li id="R_4.036.033cd">saṃcodayitvā tvaritaṃ yūthānāṃ jagmur agrataḥ</li>
    </ul>
    <ul id="R_4.036.034">
        <li id="R_4.036.034ab">te tu tena muhūrtena yūthapāḥ śīghrakāriṇaḥ</li>
        <li id="R_4.036.034cd">kiṣkindhāṃ tvarayā prāptāḥ sugrīvo yatra vānaraḥ</li>
    </ul>
    <ul id="R_4.036.035">
        <li id="R_4.036.035ab">te gṛhītvauṣadhīḥ sarvāḥ phalaṃ mūlaṃ ca vānarāḥ</li>
        <li id="R_4.036.035cd">taṃ pratigrāhayām āsur vacanaṃ cedam abruvan</li>
    </ul>
    <ul id="R_4.036.036">
        <li id="R_4.036.036ab">sarve parigatāḥ śailāḥ samudrāś ca vanāni ca</li>
        <li id="R_4.036.036cd">pṛthivyāṃ vānarāḥ sarve śāsanād upayānti te</li>
    </ul>
    <ul id="R_4.036.037">
        <li id="R_4.036.037ab">evaṃ śrutvā tato hṛṣṭaḥ sugrīvaḥ plavagādhipaḥ</li>
        <li id="R_4.036.037cd">pratijagrāha ca prītas teṣāṃ sarvam upāyanam</li>
    </ul>
    <ul id="R_4.037.001">
        <li id="R_4.037.001ab">pratigṛhya ca tat sarvam upānayam upāhṛtam</li>
        <li id="R_4.037.001cd">vānarān sāntvayitvā ca sarvān eva vyasarjayat</li>
    </ul>
    <ul id="R_4.037.002">
        <li id="R_4.037.002ab">visarjayitvā sa harīñ śūrāṃs tān kṛtakarmaṇaḥ</li>
        <li id="R_4.037.002cd">mene kṛtārtham ātmānaṃ rāghavaṃ ca mahābalam</li>
    </ul>
    <ul id="R_4.037.003">
        <li id="R_4.037.003ab">sa lakṣmaṇo bhīmabalaṃ sarvavānarasattamam</li>
        <li id="R_4.037.003cd">abravīt praśritaṃ vākyaṃ sugrīvaṃ saṃpraharṣayan</li>
        <li id="R_4.037.003ef">kiṣkindhāyā viniṣkrāma yadi te saumya rocate</li>
    </ul>
    <ul id="R_4.037.004">
        <li id="R_4.037.004ab">tasya tad vacanaṃ śrutvā lakṣmaṇasya subhāṣitam</li>
        <li id="R_4.037.004cd">sugrīvaḥ paramaprīto vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_4.037.005">
        <li id="R_4.037.005ab">evaṃ bhavatu gacchāmaḥ stheyaṃ tvacchāsane mayā</li>
        <li id="R_4.037.005cd">tam evam uktvā sugrīvo lakṣmaṇaṃ śubhalakṣmaṇam</li>
    </ul>
    <ul id="R_4.037.006">
        <li id="R_4.037.006ab">visarjayām āsa tadā tārām anyāś ca yoṣitaḥ</li>
        <li id="R_4.037.006cd">etety uccair harivarān sugrīvaḥ samudāharat</li>
    </ul>
    <ul id="R_4.037.007">
        <li id="R_4.037.007ab">tasya tad vacanaṃ śrutvā harayaḥ śīghram āyayuḥ</li>
        <li id="R_4.037.007cd">baddhāñjalipuṭāḥ sarve ye syuḥ strīdarśanakṣamāḥ</li>
    </ul>
    <ul id="R_4.037.008">
        <li id="R_4.037.008ab">tān uvāca tataḥ prāptān rājārkasadṛśaprabhaḥ</li>
        <li id="R_4.037.008cd">upasthāpayata kṣipraṃ śibikāṃ mama vānarāḥ</li>
    </ul>
    <ul id="R_4.037.009">
        <li id="R_4.037.009ab">śrutvā tu vacanaṃ tasya harayaḥ śīghravikramāḥ</li>
        <li id="R_4.037.009cd">samupasthāpayām āsuḥ śibikāṃ priyadarśanām</li>
    </ul>
    <ul id="R_4.037.010">
        <li id="R_4.037.010ab">tām upasthāpitāṃ dṛṣṭvā śibikāṃ vānarādhipaḥ</li>
        <li id="R_4.037.010cd">lakṣmaṇāruhyatāṃ śīghram iti saumitrim abravīt</li>
    </ul>
    <ul id="R_4.037.011">
        <li id="R_4.037.011ab">ity uktvā kāñcanaṃ yānaṃ sugrīvaḥ sūryasaṃnibham</li>
        <li id="R_4.037.011cd">bṛhadbhir haribhir yuktam āruroha salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_4.037.012">
        <li id="R_4.037.012ab">pāṇḍureṇātapatreṇa dhriyamāṇena mūrdhani</li>
        <li id="R_4.037.012cd">śuklaiś ca bālavyajanair dhūyamānaiḥ samantataḥ</li>
    </ul>
    <ul id="R_4.037.013">
        <li id="R_4.037.013ab">śaṅkhabherīninādaiś ca bandibhiś cābhivanditaḥ</li>
        <li id="R_4.037.013cd">niryayau prāpya sugrīvo rājyaśriyam anuttamām</li>
    </ul>
    <ul id="R_4.037.014">
        <li id="R_4.037.014ab">sa vānaraśatais tīṣkṇair bahubhiḥ śastrapāṇibhiḥ</li>
        <li id="R_4.037.014cd">parikīrṇo yayau tatra yatra rāmo vyavasthitaḥ</li>
    </ul>
    <ul id="R_4.037.015">
        <li id="R_4.037.015ab">sa taṃ deśam anuprāpya śreṣṭhaṃ rāmaniṣevitam</li>
        <li id="R_4.037.015cd">avātaran mahātejāḥ śibikāyāḥ salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_4.037.016">
        <li id="R_4.037.016ab">āsādya ca tato rāmaṃ kṛtāñjalipuṭo 'bhavat</li>
        <li id="R_4.037.016cd">kṛtāñjalau sthite tasmin vānarāś cabhavaṃs tathā</li>
    </ul>
    <ul id="R_4.037.017">
        <li id="R_4.037.017ab">taṭākam iva tad dṛṣṭvā rāmaḥ kuḍmalapaṅkajam</li>
        <li id="R_4.037.017cd">vānarāṇāṃ mahat sainyaṃ sugrīve prītimān abhūt</li>
    </ul>
    <ul id="R_4.037.018">
        <li id="R_4.037.018ab">pādayoḥ patitaṃ mūrdhnā tam utthāpya harīśvaram</li>
        <li id="R_4.037.018cd">preṃṇā ca bahumānāc ca rāghavaḥ pariṣasvaje</li>
    </ul>
    <ul id="R_4.037.019">
        <li id="R_4.037.019ab">pariṣvajya ca dharmātmā niṣīdeti tato 'bravīt</li>
        <li id="R_4.037.019cd">taṃ niṣaṇṇaṃ tato dṛṣṭvā kṣitau rāmo 'bravīd vacaḥ</li>
    </ul>
    <ul id="R_4.037.020">
        <li id="R_4.037.020ab">dharmam arthaṃ ca kāmaṃ ca kāle yas tu niṣevate</li>
        <li id="R_4.037.020cd">vibhajya satataṃ vīra sa rājā harisattama</li>
    </ul>
    <ul id="R_4.037.021">
        <li id="R_4.037.021ab">hitvā dharmaṃ tathārthaṃ ca kāmaṃ yas tu niṣevate</li>
        <li id="R_4.037.021cd">sa vṛkṣāgre yathā suptaḥ patitaḥ pratibudhyate</li>
    </ul>
    <ul id="R_4.037.022">
        <li id="R_4.037.022ab">amitrāṇāṃ vadhe yukto mitrāṇāṃ saṃgrahe rataḥ</li>
        <li id="R_4.037.022cd">trivargaphalabhoktā tu rājā dharmeṇa yujyate</li>
    </ul>
    <ul id="R_4.037.023">
        <li id="R_4.037.023ab">udyogasamayas tv eṣa prāptaḥ śatruvināśana</li>
        <li id="R_4.037.023cd">saṃcintyatāṃ hi piṅgeśa haribhiḥ saha mantribhiḥ</li>
    </ul>
    <ul id="R_4.037.024">
        <li id="R_4.037.024ab">evam uktas tu sugrīvo rāmaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.037.025">
        <li id="R_4.037.025ab">pranaṣṭā śrīś ca kīrtiś ca kapirājyaṃ ca śāśvatam</li>
        <li id="R_4.037.025cd">tvatprasādān mahābāho punaḥ prāptam idaṃ mayā</li>
    </ul>
    <ul id="R_4.037.026">
        <li id="R_4.037.026ab">tava devaprasadāc ca bhrātuś ca jayatāṃ vara</li>
        <li id="R_4.037.026cd">kṛtaṃ na pratikuryād yaḥ puruṣāṇāṃ sa dūṣakaḥ</li>
    </ul>
    <ul id="R_4.037.027">
        <li id="R_4.037.027ab">ete vānaramukhyāś ca śataśaḥ śatrusūdana</li>
        <li id="R_4.037.027cd">prāptāś cādāya balinaḥ pṛthivyāṃ sarvavānarān</li>
    </ul>
    <ul id="R_4.037.028">
        <li id="R_4.037.028ab">ṛkṣāś cāvahitāḥ śūrā golāṅgūlāś ca rāghava</li>
        <li id="R_4.037.028cd">kāntāra vanadurgāṇām abhijñā ghoradarśanāḥ</li>
    </ul>
    <ul id="R_4.037.029">
        <li id="R_4.037.029ab">devagandharvaputrāś ca vānarāḥ kāmarūpiṇaḥ</li>
        <li id="R_4.037.029cd">svaiḥ svaiḥ parivṛtāḥ sainyair vartante pathi rāghava</li>
    </ul>
    <ul id="R_4.037.030">
        <li id="R_4.037.030ab">śataiḥ śatasahasraiś ca koṭibhiś ca plavaṃgamāḥ</li>
        <li id="R_4.037.030cd">ayutaiś cāvṛtā vīrā śaṅkubhiś ca paraṃtapa</li>
    </ul>
    <ul id="R_4.037.031">
        <li id="R_4.037.031ab">arbudair arbudaśatair madhyaiś cāntaiś ca vānarāḥ</li>
        <li id="R_4.037.031cd">samudraiś ca parārdhaiś ca harayo hariyūthapāḥ</li>
    </ul>
    <ul id="R_4.037.032">
        <li id="R_4.037.032ab">āgamiṣyanti te rājan mahendrasamavikramāḥ</li>
        <li id="R_4.037.032cd">merumandarasaṃkāśā vindhyamerukṛtālayāḥ</li>
    </ul>
    <ul id="R_4.037.033">
        <li id="R_4.037.033ab">te tvām abhigamiṣyanti rākṣasaṃ ye sabāndhavam</li>
        <li id="R_4.037.033cd">nihatya rāvaṇaṃ saṃkhye hy ānayiṣyanti maithilīm</li>
    </ul>
    <ul id="R_4.037.034">
        <li id="R_4.037.034ab">
            <seg type="pāda" n="a">tatas tam udyogam avekṣya buddhimān</seg>
            <seg type="pāda" n="b">haripravīrasya nideśavartinaḥ</seg>
        </li>
        <li id="R_4.037.034cd">
            <seg type="pāda" n="c">babhūva harṣād vasudhādhipātmajaḥ</seg>
            <seg type="pāda" n="d">prabuddhanīlotpalatulyadarśanaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.038.001">
        <li id="R_4.038.001ab">iti bruvāṇaṃ sugrīvaṃ rāmo dharmabhṛtāṃ varaḥ</li>
        <li id="R_4.038.001cd">bāhubhyāṃ saṃpariṣvajya pratyuvāca kṛtāñjalim</li>
    </ul>
    <ul id="R_4.038.002">
        <li id="R_4.038.002ab">yad indro varṣate varṣaṃ na tac citraṃ bhaved bhuvi</li>
        <li id="R_4.038.002cd">ādityo vā sahasrāṃśuḥ kuryād vitimiraṃ nabhaḥ</li>
    </ul>
    <ul id="R_4.038.003">
        <li id="R_4.038.003ab">candramā raśmibhiḥ kuryāt pṛthivīṃ saumya nirmalām</li>
        <li id="R_4.038.003cd">tvadvidho vāpi mitrāṇāṃ pratikuryāt paraṃtapa</li>
    </ul>
    <ul id="R_4.038.004">
        <li id="R_4.038.004ab">evaṃ tvayi na tac citraṃ bhaved yat saumya śobhanam</li>
        <li id="R_4.038.004cd">jānāmy ahaṃ tvāṃ sugrīva satataṃ priyavādinam</li>
    </ul>
    <ul id="R_4.038.005">
        <li id="R_4.038.005ab">tvatsanāthaḥ sakhe saṃkhye jetāsmi sakalān arīn</li>
        <li id="R_4.038.005cd">tvam eva me suhṛn mitraṃ sāhāyyaṃ kartum arhasi</li>
    </ul>
    <ul id="R_4.038.006">
        <li id="R_4.038.006ab">jahārātmavināśāya vaidehīṃ rākṣasādhamaḥ</li>
        <li id="R_4.038.006cd">vañcayitvā tu paulomīm anuhlādo yathā śacīm</li>
    </ul>
    <ul id="R_4.038.007">
        <li id="R_4.038.007ab">nacirāt taṃ haniṣyāmi rāvaṇaṃ niśitaiḥ śaraiḥ</li>
        <li id="R_4.038.007cd">paulomyāḥ pitaraṃ dṛptaṃ śatakratur ivārihā</li>
    </ul>
    <ul id="R_4.038.008">
        <li id="R_4.038.008ab">etasminn antare caiva rajaḥ samabhivartata</li>
        <li id="R_4.038.008cd">uṣṇāṃ tīvrāṃ sahasrāṃśoś chādayad gagane prabhām</li>
    </ul>
    <ul id="R_4.038.009">
        <li id="R_4.038.009ab">diśaḥ paryākulāś cāsan rajasā tena mūrchitāḥ</li>
        <li id="R_4.038.009cd">cacāla ca mahī sarvā saśailavanakānanā</li>
    </ul>
    <ul id="R_4.038.010">
        <li id="R_4.038.010ab">tato nagendrasaṃkāśais tīkṣṇa daṃṣṭrair mahābalaiḥ</li>
        <li id="R_4.038.010cd">kṛtsnā saṃchāditā bhūmir asaṃkhyeyaiḥ plavaṃgamaiḥ</li>
    </ul>
    <ul id="R_4.038.011">
        <li id="R_4.038.011ab">nimeṣāntaramātreṇa tatas tair hariyūthapaiḥ</li>
        <li id="R_4.038.011cd">koṭīśataparīvāraiḥ kāmarūpibhir āvṛtā</li>
    </ul>
    <ul id="R_4.038.012">
        <li id="R_4.038.012ab">nādeyaiḥ pārvatīyaiś ca sāmudraiś ca mahābalaiḥ</li>
        <li id="R_4.038.012cd">haribhir meghanirhrādair anyaiś ca vanacāribhiḥ</li>
    </ul>
    <ul id="R_4.038.013">
        <li id="R_4.038.013ab">taruṇādityavarṇaiś ca śaśigauraiś ca vānaraiḥ</li>
        <li id="R_4.038.013cd">padmakesaravarṇaiś ca śvetair merukṛtālayaiḥ</li>
    </ul>
    <ul id="R_4.038.014">
        <li id="R_4.038.014ab">koṭīsahasrair daśabhiḥ śrīmān parivṛtas tadā</li>
        <li id="R_4.038.014cd">vīraḥ śatabalir nāma vānaraḥ pratyadṛśyata</li>
    </ul>
    <ul id="R_4.038.015">
        <li id="R_4.038.015ab">tataḥ kāñcanaśailābhas tārāyā vīryavān pitā</li>
        <li id="R_4.038.015cd">anekair daśasāhasraiḥ koṭibhiḥ pratyadṛśyata</li>
    </ul>
    <ul id="R_4.038.016">
        <li id="R_4.038.016ab">padmakesarasaṃkāśas taruṇārkanibhānanaḥ</li>
        <li id="R_4.038.016cd">buddhimān vānaraśreṣṭhaḥ sarvavānarasattamaḥ</li>
    </ul>
    <ul id="R_4.038.017">
        <li id="R_4.038.017ab">anīkair bahusāhasrair vānarāṇāṃ samanvitaḥ</li>
        <li id="R_4.038.017cd">pitā hanumataḥ śrīmān kesarī pratyadṛśyata</li>
    </ul>
    <ul id="R_4.038.018">
        <li id="R_4.038.018ab">golāṅgūlamahārājo gavākṣo bhīmavikramaḥ</li>
        <li id="R_4.038.018cd">vṛtaḥ koṭisahasreṇa vānarāṇām adṛśyata</li>
    </ul>
    <ul id="R_4.038.019">
        <li id="R_4.038.019ab">ṛkṣāṇāṃ bhīmavegānāṃ dhūmraḥ śatrunibarhaṇaḥ</li>
        <li id="R_4.038.019cd">vṛtaḥ koṭisahasrābhyāṃ dvābhyāṃ samabhivartata</li>
    </ul>
    <ul id="R_4.038.020">
        <li id="R_4.038.020ab">mahācalanibhair ghoraiḥ panaso nāma yūthapaḥ</li>
        <li id="R_4.038.020cd">ājagāma mahāvīryas tisṛbhiḥ koṭibhir vṛtaḥ</li>
    </ul>
    <ul id="R_4.038.021">
        <li id="R_4.038.021ab">nīlāñjanacayākāro nīlo nāmātha yūthapaḥ</li>
        <li id="R_4.038.021cd">adṛśyata mahākāyaḥ koṭibhir daśabhir vṛtaḥ</li>
    </ul>
    <ul id="R_4.038.022">
        <li id="R_4.038.022ab">darīmukhaś ca balavān yūthapo 'bhyāyayau tadā</li>
        <li id="R_4.038.022cd">vṛtaḥ koṭisahasreṇa sugrīvaṃ samupasthitaḥ</li>
    </ul>
    <ul id="R_4.038.023">
        <li id="R_4.038.023ab">maindaś ca dvividaś cobhāv aśviputrau mahāvalau</li>
        <li id="R_4.038.023cd">koṭikoṭisahasreṇa vānarāṇām adṛśyatām</li>
    </ul>
    <ul id="R_4.038.024">
        <li id="R_4.038.024ab">tataḥ koṭisahasrāṇāṃ sahasreṇa śatena ca</li>
        <li id="R_4.038.024cd">pṛṣṭhato 'nugataḥ prāpto haribhir gandhamādanaḥ</li>
    </ul>
    <ul id="R_4.038.025">
        <li id="R_4.038.025ab">tataḥ padmasahasreṇa vṛtaḥ śaṅkuśatena ca</li>
        <li id="R_4.038.025cd">yuvarājo 'ṅgadaḥ prāptaḥ pitṛtulyaparākramaḥ</li>
    </ul>
    <ul id="R_4.038.026">
        <li id="R_4.038.026ab">tatas tārādyutis tāro harir bhīmaparākramaḥ</li>
        <li id="R_4.038.026cd">pañcabhir harikoṭībhir dūrataḥ pratyadṛśyata</li>
    </ul>
    <ul id="R_4.038.027">
        <li id="R_4.038.027ab">indrajānuḥ kapir vīro yūthapaḥ pratyadṛśyata</li>
        <li id="R_4.038.027cd">ekādaśānāṃ koṭīnām īśvaras taiś ca saṃvṛtaḥ</li>
    </ul>
    <ul id="R_4.038.028">
        <li id="R_4.038.028ab">tato rambhas tv anuprāptas taruṇādityasaṃnibhaḥ</li>
        <li id="R_4.038.028cd">ayutena vṛtaś caiva sahasreṇa śatena ca</li>
    </ul>
    <ul id="R_4.038.029">
        <li id="R_4.038.029ab">tato yūthapatir vīro durmukho nāma vānaraḥ</li>
        <li id="R_4.038.029cd">pratyadṛśyata koṭibhyāṃ dvābhyāṃ parivṛto balī</li>
    </ul>
    <ul id="R_4.038.030">
        <li id="R_4.038.030ab">kailāsaśikharākārair vānarair bhīmavikramaiḥ</li>
        <li id="R_4.038.030cd">vṛtaḥ koṭisahasreṇa hanumān pratyadṛśyata</li>
    </ul>
    <ul id="R_4.038.031">
        <li id="R_4.038.031ab">nalaś cāpi mahāvīryaḥ saṃvṛto drumavāsibhiḥ</li>
        <li id="R_4.038.031cd">koṭīśatena saṃprāptaḥ sahasreṇa śatena ca</li>
    </ul>
    <ul id="R_4.038.032">
        <li id="R_4.038.032ab">śarabhaḥ kumudo vahnir vānaro rambha eva ca</li>
        <li id="R_4.038.032cd">ete cānye ca bahavo vānarāḥ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_4.038.033">
        <li id="R_4.038.033ab">āvṛtya pṛthivīṃ sarvāṃ parvatāṃś ca vanāni ca</li>
        <li id="R_4.038.033cd">āplavantaḥ plavantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_4.038.033ef">abhyavartanta sugrīvaṃ sūryam abhragaṇā iva</li>
    </ul>
    <ul id="R_4.038.034">
        <li id="R_4.038.034ab">kurvāṇā bahuśabdāṃś ca prahṛṣṭā balaśālinaḥ</li>
        <li id="R_4.038.034cd">śirobhir vānarendrāya sugrīvāya nyavedayan</li>
    </ul>
    <ul id="R_4.038.035">
        <li id="R_4.038.035ab">apare vānaraśreṣṭhāḥ saṃgamya ca yathocitam</li>
        <li id="R_4.038.035cd">sugrīveṇa samāgamya sthitāḥ prāñjalayas tadā</li>
    </ul>
    <ul id="R_4.038.036">
        <li id="R_4.038.036ab">sugrīvas tvarito rāme sarvāṃs tān vānararṣabhān</li>
        <li id="R_4.038.036cd">nivedayitvā dharmajñaḥ sthitaḥ prāñjalir abravīt</li>
    </ul>
    <ul id="R_4.038.037">
        <li id="R_4.038.037ab">
            <seg type="pāda" n="a">yathā sukhaṃ parvatanirjhareṣu</seg>
            <seg type="pāda" n="b">vaneṣu sarveṣu ca vānarendrāḥ</seg>
        </li>
        <li id="R_4.038.037cd">
            <seg type="pāda" n="c">niveśayitvā vidhivad balāni</seg>
            <seg type="pāda" n="d">balaṃ balajñaḥ pratipattum īṣṭe</seg>
        </li>
    </ul>
    <ul id="R_4.039.001">
        <li id="R_4.039.001ab">atha rājā samṛddhārthaḥ sugrīvaḥ plavageśvaraḥ</li>
        <li id="R_4.039.001cd">uvāca naraśārdūlaṃ rāmaṃ parabalārdanam</li>
    </ul>
    <ul id="R_4.039.002">
        <li id="R_4.039.002ab">āgatā viniviṣṭāś ca balinaḥ kāmarūpiṇaḥ</li>
        <li id="R_4.039.002cd">vānarendrā mahendrābhā ye madviṣayavāsinaḥ</li>
    </ul>
    <ul id="R_4.039.003">
        <li id="R_4.039.003ab">ta ime bahusāhasrair haribhir bhīmavikramaiḥ</li>
        <li id="R_4.039.003cd">āgatā vānarā ghorā daityadānavasaṃnibhāḥ</li>
    </ul>
    <ul id="R_4.039.004">
        <li id="R_4.039.004ab">khyātakarmāpadānāś ca balavanto jitaklamāḥ</li>
        <li id="R_4.039.004cd">parākrameṣu vikhyātā vyavasāyeṣu cottamāḥ</li>
    </ul>
    <ul id="R_4.039.005">
        <li id="R_4.039.005ab">pṛthivyambucarā rāma nānānaganivāsinaḥ</li>
        <li id="R_4.039.005cd">koṭyagraśa ime prāptā vānarās tava kiṃkarāḥ</li>
    </ul>
    <ul id="R_4.039.006">
        <li id="R_4.039.006ab">nideśavartinaḥ sarve sarve guruhite ratāḥ</li>
        <li id="R_4.039.006cd">abhipretam anuṣṭhātuṃ tava śakṣyanty ariṃdama</li>
    </ul>
    <ul id="R_4.039.007">
        <li id="R_4.039.007ab">yan manyase naravyāghra prāptakālaṃ tad ucyatām</li>
        <li id="R_4.039.007cd">tat sainyaṃ tvadvaśe yuktam ājñāpayitum arhasi</li>
    </ul>
    <ul id="R_4.039.008">
        <li id="R_4.039.008ab">kāmam eṣām idaṃ kāryaṃ viditaṃ mama tattvataḥ</li>
        <li id="R_4.039.008cd">tathāpi tu yathā tattvam ājñāpayitum arhasi</li>
    </ul>
    <ul id="R_4.039.009">
        <li id="R_4.039.009ab">tathā bruvāṇaṃ sugrīvaṃ rāmo daśarathātmajaḥ</li>
        <li id="R_4.039.009cd">bāhubhyāṃ saṃpariṣvajya idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.039.010">
        <li id="R_4.039.010ab">jñāyatāṃ saumya vaidehī yadi jīvati vā na vā</li>
        <li id="R_4.039.010cd">sa ca deśo mahāprājña yasmin vasati rāvaṇaḥ</li>
    </ul>
    <ul id="R_4.039.011">
        <li id="R_4.039.011ab">adhigamya ca vaidehīṃ nilayaṃ rāvaṇasya ca</li>
        <li id="R_4.039.011cd">prāptakālaṃ vidhāsyāmi tasmin kāle saha tvayā</li>
    </ul>
    <ul id="R_4.039.012">
        <li id="R_4.039.012ab">nāham asmin prabhuḥ kārye vānareśa na lakṣmaṇaḥ</li>
        <li id="R_4.039.012cd">tvam asya hetuḥ kāryasya prabhuś ca plavageśvara</li>
    </ul>
    <ul id="R_4.039.013">
        <li id="R_4.039.013ab">tvam evājñāpaya vibho mama kāryaviniścayam</li>
        <li id="R_4.039.013cd">tvaṃ hi jānāsi yat kāryaṃ mama vīra na saṃśayaḥ</li>
    </ul>
    <ul id="R_4.039.014">
        <li id="R_4.039.014ab">suhṛddvitīyo vikrāntaḥ prājñaḥ kālaviśeṣavit</li>
        <li id="R_4.039.014cd">bhavān asmaddhite yuktaḥ sukṛtārtho 'rthavittamaḥ</li>
    </ul>
    <ul id="R_4.039.015">
        <li id="R_4.039.015ab">evam uktas tu sugrīvo vinataṃ nāma yūthapam</li>
        <li id="R_4.039.015cd">abravīd rāma sāmnidhye lakṣmaṇasya ca dhīmataḥ</li>
        <li id="R_4.039.015ef">śailābhaṃ meghanirghoṣam ūrjitaṃ plavageśvaram</li>
    </ul>
    <ul id="R_4.039.016">
        <li id="R_4.039.016ab">somasūryātmajaiḥ sārdhaṃ vānarair vānarottama</li>
        <li id="R_4.039.016cd">deśakālanayair yuktaḥ kāryākāryaviniścaye</li>
    </ul>
    <ul id="R_4.039.017">
        <li id="R_4.039.017ab">vṛtaḥ śatasahasreṇa vānarāṇāṃ tarasvinām</li>
        <li id="R_4.039.017cd">adhigaccha diśaṃ pūrvāṃ saśailavanakānanām</li>
    </ul>
    <ul id="R_4.039.018">
        <li id="R_4.039.018ab">tatra sītāṃ ca vaidehīṃ nilayaṃ rāvaṇasya ca</li>
        <li id="R_4.039.018cd">mārgadhvaṃ giridurgeṣu vaneṣu ca nadīṣu ca</li>
    </ul>
    <ul id="R_4.039.019">
        <li id="R_4.039.019ab">nadīṃ bhāgīrathīṃ ramyāṃ sarayūṃ kauśikīṃ tathā</li>
        <li id="R_4.039.019cd">kālindīṃ yamunāṃ ramyāṃ yāmunaṃ ca mahāgirim</li>
    </ul>
    <ul id="R_4.039.020">
        <li id="R_4.039.020ab">sarasvatīṃ ca sindhuṃ ca śoṇaṃ maṇinibhodakam</li>
        <li id="R_4.039.020cd">mahīṃ kālamahīṃ caiva śailakānanaśobhitām</li>
    </ul>
    <ul id="R_4.039.021">
        <li id="R_4.039.021ab">brahmamālān videhāṃś ca mālavān kāśikosalān</li>
        <li id="R_4.039.021cd">māgadhāṃś ca mahāgrāmān puṇḍrān vaṅgāṃs tathaiva ca</li>
    </ul>
    <ul id="R_4.039.022">
        <li id="R_4.039.022ab">pattanaṃ kośakārāṇāṃ bhūmiṃ ca rajatākarām</li>
        <li id="R_4.039.022cd">sarvam etad vicetavyaṃ mṛgayadbhir tatas tataḥ</li>
    </ul>
    <ul id="R_4.039.023">
        <li id="R_4.039.023ab">rāmasya dayitāṃ bhāryāṃ sītāṃ daśarataḥ snuṣām</li>
        <li id="R_4.039.023cd">samudram avagāḍhāṃś ca parvatān pattanāni ca</li>
    </ul>
    <ul id="R_4.039.024">
        <li id="R_4.039.024ab">mandarasya ca ye koṭiṃ saṃśritāḥ ke cid āyatām</li>
        <li id="R_4.039.024cd">karṇaprāvaraṇāś caiva tathā cāpy oṣṭhakarṇakāḥ</li>
    </ul>
    <ul id="R_4.039.025">
        <li id="R_4.039.025ab">ghorā lohamukhāś caiva javanāś caikapādakāḥ</li>
        <li id="R_4.039.025cd">akṣayā balavantaś ca puruṣāḥ puruṣādakāḥ</li>
    </ul>
    <ul id="R_4.039.026">
        <li id="R_4.039.026ab">kirātāḥ karṇacūḍāś ca hemāṅgāḥ priyadarśanāḥ</li>
        <li id="R_4.039.026cd">āmamīnāśanās tatra kirātā dvīpavāsinaḥ</li>
    </ul>
    <ul id="R_4.039.027">
        <li id="R_4.039.027ab">antarjalacarā ghorā naravyāghrā iti śrutāḥ</li>
        <li id="R_4.039.027cd">eteṣām ālayāḥ sarve viceyāḥ kānanaukasaḥ</li>
    </ul>
    <ul id="R_4.039.028">
        <li id="R_4.039.028ab">giribhir ye ca gamyante plavanena plavena ca</li>
        <li id="R_4.039.028cd">ratnavantaṃ yavadvīpaṃ saptarājyopaśobhitam</li>
    </ul>
    <ul id="R_4.039.029">
        <li id="R_4.039.029ab">suvarṇarūpyakaṃ caiva suvarṇākaramaṇḍitam</li>
        <li id="R_4.039.029cd">yavadvīpam atikramya śiśiro nāma parvataḥ</li>
    </ul>
    <ul id="R_4.039.030">
        <li id="R_4.039.030ab">divaṃ spṛśati śṛṅgeṇa devadānavasevitaḥ</li>
        <li id="R_4.039.030cd">eteṣāṃ giridurgeṣu pratāpeṣu vaneṣu ca</li>
    </ul>
    <ul id="R_4.039.031">
        <li id="R_4.039.031ab">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
        <li id="R_4.039.031cd">tataḥ samudradvīpāṃś ca subhīmān draṣṭum arhatha</li>
    </ul>
    <ul id="R_4.039.032">
        <li id="R_4.039.032ab">tatrāsurā mahākāyāś chāyāṃ gṛhṇanti nityaśaḥ</li>
        <li id="R_4.039.032cd">brahmaṇā samanujñātā dīrghakālaṃ bubhukṣitāḥ</li>
    </ul>
    <ul id="R_4.039.033">
        <li id="R_4.039.033ab">taṃ kālameghapratimaṃ mahoraganiṣevitam</li>
        <li id="R_4.039.033cd">abhigamya mahānādaṃ tīrthenaiva mahodadhim</li>
    </ul>
    <ul id="R_4.039.034">
        <li id="R_4.039.034ab">tato raktajalaṃ bhīmaṃ lohitaṃ nāma sāgaram</li>
        <li id="R_4.039.034cd">gatā drakṣyatha tāṃ caiva bṛhatīṃ kūṭaśālmalīm</li>
    </ul>
    <ul id="R_4.039.035">
        <li id="R_4.039.035ab">gṛhaṃ ca vainateyasya nānāratnavibhūṣitam</li>
        <li id="R_4.039.035cd">tatra kailāsasaṃkāśaṃ vihitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_4.039.036">
        <li id="R_4.039.036ab">tatra śailanibhā bhīmā mandehā nāma rākṣasāḥ</li>
        <li id="R_4.039.036cd">śailaśṛṅgeṣu lambante nānārūpā bhayāvahāḥ</li>
    </ul>
    <ul id="R_4.039.037">
        <li id="R_4.039.037ab">te patanti jale nityaṃ sūryasyodayanaṃ prati</li>
        <li id="R_4.039.037cd">abhitaptāś ca sūryeṇa lambante sma punaḥ punaḥ</li>
    </ul>
    <ul id="R_4.039.038">
        <li id="R_4.039.038ab">tataḥ pāṇḍurameghābhaṃ kṣīraudaṃ nāma sāgaram</li>
        <li id="R_4.039.038cd">gatā drakṣyatha durdharṣā mukhā hāram ivormibhiḥ</li>
    </ul>
    <ul id="R_4.039.039">
        <li id="R_4.039.039ab">tasya madhye mahāśveta ṛṣabho nāma parvataḥ</li>
        <li id="R_4.039.039cd">divyagandhaiḥ kusumitai rajataiś ca nagair vṛtaḥ</li>
    </ul>
    <ul id="R_4.039.040">
        <li id="R_4.039.040ab">saraś ca rājataiḥ padmair jvalitair hemakesaraiḥ</li>
        <li id="R_4.039.040cd">nāmnā sudarśanaṃ nāma rājahaṃsaiḥ samākulam</li>
    </ul>
    <ul id="R_4.039.041">
        <li id="R_4.039.041ab">vibudhāś cāraṇā yakṣāḥ kiṃnarāḥ sāpsarogaṇāḥ</li>
        <li id="R_4.039.041cd">hṛṣṭāḥ samabhigacchanti nalinīṃ tāṃ riraṃsavaḥ</li>
    </ul>
    <ul id="R_4.039.042">
        <li id="R_4.039.042ab">kṣīrodaṃ samatikramya tato drakṣyatha vānarāḥ</li>
        <li id="R_4.039.042cd">jalodaṃ sāgaraśreṣṭhaṃ sarvabhūtabhayāvaham</li>
    </ul>
    <ul id="R_4.039.043">
        <li id="R_4.039.043ab">tatra tat kopajaṃ tejaḥ kṛtaṃ hayamukhaṃ mahat</li>
        <li id="R_4.039.043cd">asyāhus tan mahāvegam odanaṃ sacarācaram</li>
    </ul>
    <ul id="R_4.039.044">
        <li id="R_4.039.044ab">tatra vikrośatāṃ nādo bhūtānāṃ sāgaraukasām</li>
        <li id="R_4.039.044cd">śrūyate cāsamarthānāṃ dṛṣṭvā tad vaḍavāmukham</li>
    </ul>
    <ul id="R_4.039.045">
        <li id="R_4.039.045ab">svādūdasyottare deśe yojanāni trayodaśa</li>
        <li id="R_4.039.045cd">jātarūpaśilo nāma mahān kanakaparvataḥ</li>
    </ul>
    <ul id="R_4.039.046">
        <li id="R_4.039.046ab">āsīnaṃ parvatasyāgre sarvabhūtanamaskṛtam</li>
        <li id="R_4.039.046cd">sahasraśirasaṃ devam anantaṃ nīlavāsasaṃ</li>
    </ul>
    <ul id="R_4.039.047">
        <li id="R_4.039.047ab">triśirāḥ kāñcanaḥ ketus tālas tasya mahātmanaḥ</li>
        <li id="R_4.039.047cd">sthāpitaḥ parvatasyāgre virājati savedikaḥ</li>
    </ul>
    <ul id="R_4.039.048">
        <li id="R_4.039.048ab">pūrvasyāṃ diśi nirmāṇaṃ kṛtaṃ tat tridaśeśvaraiḥ</li>
        <li id="R_4.039.048cd">tataḥ paraṃ hemamayaḥ śrīmān udayaparvataḥ</li>
    </ul>
    <ul id="R_4.039.049">
        <li id="R_4.039.049ab">tasya koṭir divaṃ spṛṣṭvā śatayojanam āyatā</li>
        <li id="R_4.039.049cd">jātarūpamayī divyā virājati savedikā</li>
    </ul>
    <ul id="R_4.039.050">
        <li id="R_4.039.050ab">sālais tālais tamālaiś ca karṇikāraiś ca puṣpitaiḥ</li>
        <li id="R_4.039.050cd">jātarūpamayair divyaiḥ śobhate sūryasaṃnibhaiḥ</li>
    </ul>
    <ul id="R_4.039.051">
        <li id="R_4.039.051ab">tatra yojanavistāram ucchritaṃ daśayojanam</li>
        <li id="R_4.039.051cd">śṛṅgaṃ saumanasaṃ nāma jātarūpamayaṃ dhruvam</li>
    </ul>
    <ul id="R_4.039.052">
        <li id="R_4.039.052ab">tatra pūrvaṃ padaṃ kṛtvā purā viṣṇus trivikrame</li>
        <li id="R_4.039.052cd">dvitīyaṃ śikharaṃ meroś cakāra puruṣottamaḥ</li>
    </ul>
    <ul id="R_4.039.053">
        <li id="R_4.039.053ab">uttareṇa parikramya jambūdvīpaṃ divākaraḥ</li>
        <li id="R_4.039.053cd">dṛśyo bhavati bhūyiṣṭhaṃ śikharaṃ tan mahocchrayam</li>
    </ul>
    <ul id="R_4.039.054">
        <li id="R_4.039.054ab">tatra vaikhānasā nāma vālakhilyā maharṣayaḥ</li>
        <li id="R_4.039.054cd">prakāśamānā dṛśyante sūryavarṇās tapasvinaḥ</li>
    </ul>
    <ul id="R_4.039.055">
        <li id="R_4.039.055ab">ayaṃ sudarśano dvīpaḥ puro yasya prakāśate</li>
        <li id="R_4.039.055cd">yasmiṃs tejaś ca cakṣuś ca sarvaprānabhṛtām api</li>
    </ul>
    <ul id="R_4.039.056">
        <li id="R_4.039.056ab">śailasya tasya kuñjeṣu kandareṣu vaneṣu ca</li>
        <li id="R_4.039.056cd">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.039.057">
        <li id="R_4.039.057ab">kāñcanasya ca śailasya sūryasya ca mahātmanaḥ</li>
        <li id="R_4.039.057cd">āviṣṭā tejasā saṃdhyā pūrvā raktā prakāśate</li>
    </ul>
    <ul id="R_4.039.058">
        <li id="R_4.039.058ab">tataḥ paramagamyā syād dik pūrvā tridaśāvṛtā</li>
        <li id="R_4.039.058cd">rahitā candrasūryābhyām adṛśyā timirāvṛtā</li>
    </ul>
    <ul id="R_4.039.059">
        <li id="R_4.039.059ab">śaileṣu teṣu sarveṣu kandareṣu vaneṣu ca</li>
        <li id="R_4.039.059cd">ya ca noktā mayā deśā viceyā teṣu jānakī</li>
    </ul>
    <ul id="R_4.039.060">
        <li id="R_4.039.060ab">etāvad vānaraiḥ śakyaṃ gantuṃ vānarapuṃgavāḥ</li>
        <li id="R_4.039.060cd">abhāskaram amaryādaṃ na jānīmas tataḥ param</li>
    </ul>
    <ul id="R_4.039.061">
        <li id="R_4.039.061ab">adhigamya tu vaidehīṃ nilayaṃ rāvaṇasya ca</li>
        <li id="R_4.039.061cd">māse pūrṇe nivartadhvam udayaṃ prāpya parvatam</li>
    </ul>
    <ul id="R_4.039.062">
        <li id="R_4.039.062ab">ūrdhvaṃ māsān na vastavyaṃ vasan vadhyo bhaven mama</li>
        <li id="R_4.039.062cd">siddhārthāḥ saṃnivartadhvam adhigamya ca maithilīm</li>
    </ul>
    <ul id="R_4.039.063">
        <li id="R_4.039.063ab">
            <seg type="pāda" n="a">mahendrakāntāṃ vanaṣaṇḍa maṇḍitāṃ</seg>
            <seg type="pāda" n="b">diśaṃ caritvā nipuṇena vānarāḥ</seg>
        </li>
        <li id="R_4.039.063cd">
            <seg type="pāda" n="c">avāpya sītāṃ raghuvaṃśajapriyāṃ</seg>
            <seg type="pāda" n="d">tato nivṛttāḥ sukhito bhaviṣyatha</seg>
        </li>
    </ul>
    <ul id="R_4.040.001">
        <li id="R_4.040.001ab">tataḥ prasthāpya sugrīvas tan mahad vānaraṃ balam</li>
        <li id="R_4.040.001cd">dakṣiṇāṃ preṣayām āsa vānarān abhilakṣitān</li>
    </ul>
    <ul id="R_4.040.002">
        <li id="R_4.040.002ab">nīlam agnisutaṃ caiva hanumantaṃ ca vānaram</li>
        <li id="R_4.040.002cd">pitāmahasutaṃ caiva jāmbavantaṃ mahākapim</li>
    </ul>
    <ul id="R_4.040.003">
        <li id="R_4.040.003ab">suhotraṃ ca śarīraṃ ca śaragulmaṃ tathaiva ca</li>
        <li id="R_4.040.003cd">gajaṃ gavākṣaṃ gavayaṃ suṣeṇam ṛṣabhaṃ tathā</li>
    </ul>
    <ul id="R_4.040.004">
        <li id="R_4.040.004ab">maindaṃ ca dvividaṃ caiva vijayaṃ gandhamādanam</li>
        <li id="R_4.040.004cd">ulkāmukham asaṅgaṃ ca hutāśana sutāv ubhau</li>
    </ul>
    <ul id="R_4.040.005">
        <li id="R_4.040.005ab">aṅgadapramukhān vīrān vīraḥ kapigaṇeśvaraḥ</li>
        <li id="R_4.040.005cd">vegavikramasaṃpannān saṃdideśa viśeṣavit</li>
    </ul>
    <ul id="R_4.040.006">
        <li id="R_4.040.006ab">teṣām agreṣaraṃ caiva mahad balam asaṃgagam</li>
        <li id="R_4.040.006cd">vidhāya harivīrāṇām ādiśad dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_4.040.007">
        <li id="R_4.040.007ab">ye ke cana samuddeśās tasyāṃ diśi sudurgamāḥ</li>
        <li id="R_4.040.007cd">kapīśaḥ kapimukhyānāṃ sa teṣāṃ tān udāharat</li>
    </ul>
    <ul id="R_4.040.008">
        <li id="R_4.040.008ab">sahasraśirasaṃ vindhyaṃ nānādrumalatāvṛtam</li>
        <li id="R_4.040.008cd">narmadāṃ ca nadīṃ durgāṃ mahoraganiṣevitām</li>
    </ul>
    <ul id="R_4.040.009">
        <li id="R_4.040.009ab">tato godāvarīṃ ramyāṃ kṛṣṇāveṇīṃ mahānadīm</li>
        <li id="R_4.040.009cd">varadāṃ ca mahābhāgāṃ mahoraganiṣevitām</li>
    </ul>
    <ul id="R_4.040.010">
        <li id="R_4.040.010ab">mekhalān utkalāṃś caiva daśārṇanagarāṇy api</li>
        <li id="R_4.040.010cd">avantīm abhravantīṃ ca sarvam evānupaśyata</li>
    </ul>
    <ul id="R_4.040.011">
        <li id="R_4.040.011ab">vidarbhān ṛṣikāṃś caiva ramyān māhiṣakān api</li>
        <li id="R_4.040.011cd">tathā baṅgān kaliṅgāṃś ca kauśikāṃś ca samantataḥ</li>
    </ul>
    <ul id="R_4.040.012">
        <li id="R_4.040.012ab">anvīkṣya daṇḍakāraṇyaṃ saparvatanadīguham</li>
        <li id="R_4.040.012cd">nadīṃ godāvarīṃ caiva sarvam evānupaśyata</li>
    </ul>
    <ul id="R_4.040.013">
        <li id="R_4.040.013ab">tathaivāndhrāṃś ca puṇḍrāṃś ca colān pāṇḍyān sakeralān</li>
        <li id="R_4.040.013cd">ayomukhaś ca gantavyaḥ parvato dhātumaṇḍitaḥ</li>
    </ul>
    <ul id="R_4.040.014">
        <li id="R_4.040.014ab">vicitraśikharaḥ śrīmāṃś citrapuṣpitakānanaḥ</li>
        <li id="R_4.040.014cd">sacandanavanoddeśo mārgitavyo mahāgiriḥ</li>
    </ul>
    <ul id="R_4.040.015">
        <li id="R_4.040.015ab">tatas tām āpagāṃ divyāṃ prasannasalilāṃ śivām</li>
        <li id="R_4.040.015cd">tatra drakṣyatha kāverīṃ vihṛtām apsarogaṇaiḥ</li>
    </ul>
    <ul id="R_4.040.016">
        <li id="R_4.040.016ab">tasyāsīnaṃ nagasyāgre malayasya mahaujasaṃ</li>
        <li id="R_4.040.016cd">drakṣyathādityasaṃkāśam agastyam ṛṣisattamam</li>
    </ul>
    <ul id="R_4.040.017">
        <li id="R_4.040.017ab">tatas tenābhyanujñātāḥ prasannena mahātmanā</li>
        <li id="R_4.040.017cd">tāmraparṇīṃ grāhajuṣṭāṃ tariṣyatha mahānadīm</li>
    </ul>
    <ul id="R_4.040.018">
        <li id="R_4.040.018ab">sā candanavanair divyaiḥ pracchannā dvīpa śālinī</li>
        <li id="R_4.040.018cd">kānteva yuvatiḥ kāntaṃ samudram avagāhate</li>
    </ul>
    <ul id="R_4.040.019">
        <li id="R_4.040.019ab">tato hemamayaṃ divyaṃ muktāmaṇivibhūṣitam</li>
        <li id="R_4.040.019cd">yuktaṃ kavāṭaṃ pāṇḍyānāṃ gatā drakṣyatha vānarāḥ</li>
    </ul>
    <ul id="R_4.040.020">
        <li id="R_4.040.020ab">tataḥ samudram āsādya saṃpradhāryārthaniścayam</li>
        <li id="R_4.040.020cd">agastyenāntare tatra sāgare viniveśitaḥ</li>
    </ul>
    <ul id="R_4.040.021">
        <li id="R_4.040.021ab">citranānānagaḥ śrīmān mahendraḥ parvatottamaḥ</li>
        <li id="R_4.040.021cd">jātarūpamayaḥ śrīmān avagāḍho mahārṇavam</li>
    </ul>
    <ul id="R_4.040.022">
        <li id="R_4.040.022ab">nānāvidhair nagaiḥ phullair latābhiś copaśobhitam</li>
        <li id="R_4.040.022cd">devarṣiyakṣapravarair apsarobhiś ca sevitam</li>
    </ul>
    <ul id="R_4.040.023">
        <li id="R_4.040.023ab">siddhacāraṇasaṃghaiś ca prakīrṇaṃ sumanoharam</li>
        <li id="R_4.040.023cd">tam upaiti sahasrākṣaḥ sadā parvasu parvasu</li>
    </ul>
    <ul id="R_4.040.024">
        <li id="R_4.040.024ab">dvīpas tasyāpare pāre śatayojanam āyataḥ</li>
        <li id="R_4.040.024cd">agamyo mānuṣair dīptas taṃ mārgadhvaṃ samantataḥ</li>
        <li id="R_4.040.024ef">tatra sarvātmanā sītā mārgitavyā viśeṣataḥ</li>
    </ul>
    <ul id="R_4.040.025">
        <li id="R_4.040.025ab">sa hi deśas tu vadhyasya rāvaṇasya durātmanaḥ</li>
        <li id="R_4.040.025cd">rākṣasādhipater vāsaḥ sahasrākṣasamadyuteḥ</li>
    </ul>
    <ul id="R_4.040.026">
        <li id="R_4.040.026ab">dakṣiṇasya samudrasya madhye tasya tu rākṣasī</li>
        <li id="R_4.040.026cd">aṅgāraketi vikhyātā chāyām ākṣipya bhojinī</li>
    </ul>
    <ul id="R_4.040.027">
        <li id="R_4.040.027ab">tam atikramya lakṣmīvān samudre śatayojane</li>
        <li id="R_4.040.027cd">giriḥ puṣpitako nāma siddhacāraṇasevitaḥ</li>
    </ul>
    <ul id="R_4.040.028">
        <li id="R_4.040.028ab">candrasūryāṃśusaṃkāśaḥ sāgarāmbusamāvṛtaḥ</li>
        <li id="R_4.040.028cd">bhrājate vipulaiḥ śṛṅgair ambaraṃ vilikhann iva</li>
    </ul>
    <ul id="R_4.040.029">
        <li id="R_4.040.029ab">tasyaikaṃ kāñcanaṃ śṛṅgaṃ sevate yaṃ divākaraḥ</li>
        <li id="R_4.040.029cd">śvetaṃ rājatam ekaṃ ca sevate yaṃ niśākaraḥ</li>
    </ul>
    <ul id="R_4.040.030">
        <li id="R_4.040.030ab">na taṃ kṛtaghnāḥ paśyanti na nṛśaṃsā na nāstikāḥ</li>
        <li id="R_4.040.030cd">praṇamya śirasā śailaṃ taṃ vimārgata vānarāḥ</li>
    </ul>
    <ul id="R_4.040.031">
        <li id="R_4.040.031ab">tam atikramya durdharṣāḥ sūryavān nāma parvataḥ</li>
        <li id="R_4.040.031cd">adhvanā durvigāhena yojanāni caturdaśa</li>
    </ul>
    <ul id="R_4.040.032">
        <li id="R_4.040.032ab">tatas tam apy atikramya vaidyuto nāma parvataḥ</li>
        <li id="R_4.040.032cd">sarvakāmaphalair vṛkṣaiḥ sarvakālamanoharaiḥ</li>
    </ul>
    <ul id="R_4.040.033">
        <li id="R_4.040.033ab">tatra bhuktvā varārhāṇi mūlāni ca phalāni ca</li>
        <li id="R_4.040.033cd">madhūni pītvā mukhyāni paraṃ gacchata vānarāḥ</li>
    </ul>
    <ul id="R_4.040.034">
        <li id="R_4.040.034ab">tatra netramanaḥkāntaḥ kuñjaro nāma parvataḥ</li>
        <li id="R_4.040.034cd">agastyabhavanaṃ yatra nirmitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_4.040.035">
        <li id="R_4.040.035ab">tatra yojanavistāram ucchritaṃ daśayojanam</li>
        <li id="R_4.040.035cd">śaraṇaṃ kāñcanaṃ divyaṃ nānāratnavibhūṣitam</li>
    </ul>
    <ul id="R_4.040.036">
        <li id="R_4.040.036ab">tatra bhogavatī nāma sarpāṇām ālayaḥ purī</li>
        <li id="R_4.040.036cd">viśālarathyā durdharṣā sarvataḥ parirakṣitā</li>
        <li id="R_4.040.036ef">rakṣitā pannagair ghorais tīkṣṇadaṃṣṭrair mahāviṣaiḥ</li>
    </ul>
    <ul id="R_4.040.037">
        <li id="R_4.040.037ab">sarparājo mahāghoro yasyāṃ vasati vāsukiḥ</li>
        <li id="R_4.040.037cd">niryāya mārgitavyā ca sā ca bhogavatī purī</li>
    </ul>
    <ul id="R_4.040.038">
        <li id="R_4.040.038ab">taṃ ca deśam atikramya mahān ṛṣabhasaṃsthitaḥ</li>
        <li id="R_4.040.038cd">sarvaratnamayaḥ śrīmān ṛṣabho nāma parvataḥ</li>
    </ul>
    <ul id="R_4.040.039">
        <li id="R_4.040.039ab">gośīrṣakaṃ padmakaṃ ca hariśyāmaṃ ca candanam</li>
        <li id="R_4.040.039cd">divyam utpadyate yatra tac caivāgnisamaprabham</li>
    </ul>
    <ul id="R_4.040.040">
        <li id="R_4.040.040ab">na tu tac candanaṃ dṛṣṭvā spraṣṭavyaṃ ca kadā cana</li>
        <li id="R_4.040.040cd">rohitā nāma gandharvā ghorā rakṣanti tad vanam</li>
    </ul>
    <ul id="R_4.040.041">
        <li id="R_4.040.041ab">tatra gandharvapatayaḥ pañcasūryasamaprabhāḥ</li>
        <li id="R_4.040.041cd">śailūṣo grāmaṇīr bhikṣuḥ śubhro babhrus tathaiva ca</li>
    </ul>
    <ul id="R_4.040.042">
        <li id="R_4.040.042ab">ante pṛthivyā durdharṣās tatra svargajitaḥ sthitāḥ</li>
        <li id="R_4.040.042cd">tataḥ paraṃ na vaḥ sevyaḥ pitṛlokaḥ sudāruṇaḥ</li>
        <li id="R_4.040.042ef">rājadhānī yamasyaiṣā kaṣṭena tamasāvṛtā</li>
    </ul>
    <ul id="R_4.040.043">
        <li id="R_4.040.043ab">etāvad eva yuṣmābhir vīrā vānarapuṃgavāḥ</li>
        <li id="R_4.040.043cd">śakyaṃ vicetuṃ gantuṃ vā nāto gatimatāṃ gatiḥ</li>
    </ul>
    <ul id="R_4.040.044">
        <li id="R_4.040.044ab">sarvam etat samālokya yac cānyad api dṛśyate</li>
        <li id="R_4.040.044cd">gatiṃ viditvā vaidehyāḥ saṃnivartitam arhatha</li>
    </ul>
    <ul id="R_4.040.045">
        <li id="R_4.040.045ab">yas tu māsān nivṛtto 'gre dṛṣṭā sīteti vakṣyati</li>
        <li id="R_4.040.045cd">mattulyavibhavo bhogaiḥ sukhaṃ sa vihariṣyati</li>
    </ul>
    <ul id="R_4.040.046">
        <li id="R_4.040.046ab">tataḥ priyataro nāsti mama prāṇād viśeṣataḥ</li>
        <li id="R_4.040.046cd">kṛtāparādho bahuśo mama bandhur bhaviṣyati</li>
    </ul>
    <ul id="R_4.040.047">
        <li id="R_4.040.047ab">
            <seg type="pāda" n="a">amitabalaparākramā bhavanto</seg>
            <seg type="pāda" n="b">vipulaguṇeṣu kuleṣu ca prasūtāḥ</seg>
        </li>
        <li id="R_4.040.047cd">
            <seg type="pāda" n="c">manujapatisutāṃ yathā labhadhvaṃ</seg>
            <seg type="pāda" n="d">tad adhiguṇaṃ puruṣārtham ārabhadhvam</seg>
        </li>
    </ul>
    <ul id="R_4.041.001">
        <li id="R_4.041.001ab">tataḥ prasthāpya sugrīvas tān harīn dakṣiṇāṃ diśam</li>
        <li id="R_4.041.001cd">buddhivikramasaṃpannān vāyuvegasamāñjave</li>
    </ul>
    <ul id="R_4.041.002">
        <li id="R_4.041.002ab">athāhūya mahātejāḥ suṣeṇaṃ nāma yūthapam</li>
        <li id="R_4.041.002cd">tārāyāḥ pitaraṃ rājā śvaśurabhīmavikramam</li>
    </ul>
    <ul id="R_4.041.003">
        <li id="R_4.041.003ab">abravīt prāñjalir vākyam abhigamya praṇamya ca</li>
        <li id="R_4.041.003cd">sāhāyyaṃ kuru rāmasya kṛtye 'smin samupasthite</li>
    </ul>
    <ul id="R_4.041.004">
        <li id="R_4.041.004ab">vṛtaḥ śatasahasreṇa vānarāṇāṃ tarasvinām</li>
        <li id="R_4.041.004cd">abhigaccha diśaṃ saumya paścimāṃ vāruṇīṃ prabho</li>
    </ul>
    <ul id="R_4.041.005">
        <li id="R_4.041.005ab">surāṣṭrān saha bāhlīkāñ śūrābhīrāṃs tathaiva ca</li>
        <li id="R_4.041.005cd">sphītāñjanapadān ramyān vipulāni purāṇi ca</li>
    </ul>
    <ul id="R_4.041.006">
        <li id="R_4.041.006ab">puṃnāgagahanaṃ kukṣiṃ bahuloddālakākulam</li>
        <li id="R_4.041.006cd">tathā ketakaṣaṇḍāṃś ca mārgadhvaṃ hariyūthapāḥ</li>
    </ul>
    <ul id="R_4.041.007">
        <li id="R_4.041.007ab">pratyak srotogamāś caiva nadyaḥ śītajalāḥ śivāḥ</li>
        <li id="R_4.041.007cd">tāpasānām araṇyāni kāntārā girayaś ca ye</li>
    </ul>
    <ul id="R_4.041.008">
        <li id="R_4.041.008ab">girijālāvṛtāṃ durgāṃ mārgitvā paścimāṃ diśam</li>
        <li id="R_4.041.008cd">tataḥ paścimam āsādya samudraṃ draṣṭum arhatha</li>
        <li id="R_4.041.008ef">timi nakrāyuta jalam akṣobhyam atha vānaraḥ</li>
    </ul>
    <ul id="R_4.041.009">
        <li id="R_4.041.009ab">tataḥ ketakaṣaṇḍeṣu tamālagahaneṣu ca</li>
        <li id="R_4.041.009cd">kapayo vihariṣyanti nārikelavaneṣu ca</li>
    </ul>
    <ul id="R_4.041.010">
        <li id="R_4.041.010ab">tatra sītāṃ ca mārgadhvaṃ nilayaṃ rāvaṇasya ca</li>
        <li id="R_4.041.010cd">marīcipattanaṃ caiva ramyaṃ caiva jaṭīpuram</li>
    </ul>
    <ul id="R_4.041.011">
        <li id="R_4.041.011ab">avantīm aṅgalopāṃ ca tathā cālakṣitaṃ vanam</li>
        <li id="R_4.041.011cd">rāṣṭrāṇi ca viśālāni pattanāni tatas tataḥ</li>
    </ul>
    <ul id="R_4.041.012">
        <li id="R_4.041.012ab">sindhusāgarayoś caiva saṃgame tatra parvataḥ</li>
        <li id="R_4.041.012cd">mahān hemagirir nāma śataśṛṅgo mahādrumaḥ</li>
    </ul>
    <ul id="R_4.041.013">
        <li id="R_4.041.013ab">tasya prastheṣu ramyeṣu siṃhāḥ pakṣagamāḥ sthitāḥ</li>
        <li id="R_4.041.013cd">timimatsyagajāṃś caiva nīḍāny āropayanti te</li>
    </ul>
    <ul id="R_4.041.014">
        <li id="R_4.041.014ab">tāni nīḍāni siṃhānāṃ giriśṛṅgagatāś ca ye</li>
        <li id="R_4.041.014cd">dṛptās tṛptāś ca mātaṅgās toyadasvananiḥsvanāḥ</li>
        <li id="R_4.041.014ef">vicaranti viśāle 'smiṃs toyapūrṇe samantataḥ</li>
    </ul>
    <ul id="R_4.041.015">
        <li id="R_4.041.015ab">tasya śṛṅgaṃ divasparśaṃ kāñcanaṃ citrapādapam</li>
        <li id="R_4.041.015cd">sarvam āśu vicetavyaṃ kapibhiḥ kāmarūpibhiḥ</li>
    </ul>
    <ul id="R_4.041.016">
        <li id="R_4.041.016ab">koṭiṃ tatra samudre tu kāñcanīṃ śatayojanam</li>
        <li id="R_4.041.016cd">durdarśāṃ pariyātrasya gatā drakṣyatha vānarāḥ</li>
    </ul>
    <ul id="R_4.041.017">
        <li id="R_4.041.017ab">koṭyas tatra caturviṃśad gandharvāṇāṃ tarasvinām</li>
        <li id="R_4.041.017cd">vasanty agninikāśānāṃ ghorāṇāṃ kāmarūpiṇām</li>
    </ul>
    <ul id="R_4.041.018">
        <li id="R_4.041.018ab">nātyāsādayitavyās te vānarair bhīmavikramaiḥ</li>
        <li id="R_4.041.018cd">nādeyaṃ ca phalaṃ tasmād deśāt kiṃ cit plavaṃgamaiḥ</li>
    </ul>
    <ul id="R_4.041.019">
        <li id="R_4.041.019ab">durāsadā hi te vīrāḥ sattvavanto mahābalāḥ</li>
        <li id="R_4.041.019cd">phalamūlāni te tatra rakṣante bhīmavikramāḥ</li>
    </ul>
    <ul id="R_4.041.020">
        <li id="R_4.041.020ab">tatra yatnaś ca kartavyo mārgitavyā ca jānakī</li>
        <li id="R_4.041.020cd">na hi tebhyo bhayaṃ kiṃ cit kapitvam anuvartatām</li>
    </ul>
    <ul id="R_4.041.021">
        <li id="R_4.041.021ab">caturbhāge samudrasya cakravān nāma parvataḥ</li>
        <li id="R_4.041.021cd">tatra cakraṃ sahasrāraṃ nirmitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_4.041.022">
        <li id="R_4.041.022ab">tatra pañcajanaṃ hatvā hayagrīvaṃ ca dānavam</li>
        <li id="R_4.041.022cd">ājahāra tataś cakraṃ śaṅkhaṃ ca puruṣottamaḥ</li>
    </ul>
    <ul id="R_4.041.023">
        <li id="R_4.041.023ab">tasya sānuṣu citreṣu viśālāsu guhāsu ca</li>
        <li id="R_4.041.023cd">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.041.024">
        <li id="R_4.041.024ab">yojanāni catuḥṣaṣṭir varāho nāma parvataḥ</li>
        <li id="R_4.041.024cd">suvarṇaśṛṅgaḥ suśrīmān agādhe varuṇālaye</li>
    </ul>
    <ul id="R_4.041.025">
        <li id="R_4.041.025ab">tatra prāgjyotiṣaṃ nāma jātarūpamayaṃ puram</li>
        <li id="R_4.041.025cd">yasmin vasti duṣṭātmā narako nāma guhāsu ca</li>
    </ul>
    <ul id="R_4.041.026">
        <li id="R_4.041.026ab">tasya sānuṣu citreṣu viśālāsu guhāsu ca</li>
        <li id="R_4.041.026cd">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.041.027">
        <li id="R_4.041.027ab">tam atikramya śailendraṃ kāñcanāntaranirdaraḥ</li>
        <li id="R_4.041.027cd">parvataḥ sarvasauvarṇo dhārā prasravaṇāyutaḥ</li>
    </ul>
    <ul id="R_4.041.028">
        <li id="R_4.041.028ab">taṃ gajāś ca varāhāś ca siṃhā vyāghrāś ca sarvataḥ</li>
        <li id="R_4.041.028cd">abhigarjanti satataṃ tena śabdena darpitāḥ</li>
    </ul>
    <ul id="R_4.041.029">
        <li id="R_4.041.029ab">tasmin harihayaḥ śrīmān mahendraḥ pākaśāsanaḥ</li>
        <li id="R_4.041.029cd">abhiṣiktaḥ surai rājā meghavān nāma parvataḥ</li>
    </ul>
    <ul id="R_4.041.030">
        <li id="R_4.041.030ab">tam atikramya śailendraṃ mahendraparipālitam</li>
        <li id="R_4.041.030cd">ṣaṣṭiṃ girisahasrāṇi kāñcanāni gamiṣyatha</li>
    </ul>
    <ul id="R_4.041.031">
        <li id="R_4.041.031ab">taruṇādityavarṇāni bhrājamānāni sarvataḥ</li>
        <li id="R_4.041.031cd">jātarūpamayair vṛkṣaiḥ śobhitāni supuṣpitaiḥ</li>
    </ul>
    <ul id="R_4.041.032">
        <li id="R_4.041.032ab">teṣāṃ madhye sthito rājā merur uttamaparvataḥ</li>
        <li id="R_4.041.032cd">ādityena prasannena śailo dattavaraḥ purā</li>
    </ul>
    <ul id="R_4.041.033">
        <li id="R_4.041.033ab">tenaivam uktaḥ śailendraḥ sarva eva tvadāśrayāḥ</li>
        <li id="R_4.041.033cd">matprasādād bhaviṣyanti divārātrau ca kāñcanāḥ</li>
    </ul>
    <ul id="R_4.041.034">
        <li id="R_4.041.034ab">tvayi ye cāpi vatsyanti devagandharvadānavāḥ</li>
        <li id="R_4.041.034cd">te bhaviṣyanti raktāś ca prabhayā kāñcanaprabhāḥ</li>
    </ul>
    <ul id="R_4.041.035">
        <li id="R_4.041.035ab">ādityā vasavo rudrā marutaś ca divaukasaḥ</li>
        <li id="R_4.041.035cd">āgamya paścimāṃ saṃdhyāṃ merum uttamaparvatam</li>
    </ul>
    <ul id="R_4.041.036">
        <li id="R_4.041.036ab">ādityam upatiṣṭhanti taiś ca sūryo 'bhipūjitaḥ</li>
        <li id="R_4.041.036cd">adṛśyaḥ sarvabhūtānām astaṃ gacchati parvatam</li>
    </ul>
    <ul id="R_4.041.037">
        <li id="R_4.041.037ab">yojanānāṃ sahasrāṇi daśatāni divākaraḥ</li>
        <li id="R_4.041.037cd">muhūrtārdhena taṃ śīghram abhiyāti śiloccayam</li>
    </ul>
    <ul id="R_4.041.038">
        <li id="R_4.041.038ab">śṛṅge tasya mahad divyaṃ bhavanaṃ sūryasaṃnibham</li>
        <li id="R_4.041.038cd">prāsādaguṇasaṃbādhaṃ vihitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_4.041.039">
        <li id="R_4.041.039ab">śobhitaṃ tarubhiś citrair nānāpakṣisamākulaiḥ</li>
        <li id="R_4.041.039cd">niketaṃ pāśahastasya varuṇasya mahātmanaḥ</li>
    </ul>
    <ul id="R_4.041.040">
        <li id="R_4.041.040ab">antarā merum astaṃ ca tālo daśaśirā mahān</li>
        <li id="R_4.041.040cd">jātarūpamayaḥ śrīmān bhrājate citravedikaḥ</li>
    </ul>
    <ul id="R_4.041.041">
        <li id="R_4.041.041ab">teṣu sarveṣu durgeṣu saraḥsu ca saritsu ca</li>
        <li id="R_4.041.041cd">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.041.042">
        <li id="R_4.041.042ab">yatra tiṣṭhati dharmātmā tapasā svena bhāvitaḥ</li>
        <li id="R_4.041.042cd">merusāvarṇir ity eva khyāto vai brahmaṇā samaḥ</li>
    </ul>
    <ul id="R_4.041.043">
        <li id="R_4.041.043ab">praṣṭavyo merusāvarṇir maharṣiḥ sūryasaṃnibhaḥ</li>
        <li id="R_4.041.043cd">praṇamya śirasā bhūmau pravṛttiṃ maithilīṃ prati</li>
    </ul>
    <ul id="R_4.041.044">
        <li id="R_4.041.044ab">etāvaj jīvalokasya bhāskaro rajanīkṣaye</li>
        <li id="R_4.041.044cd">kṛtvā vitimiraṃ sarvam astaṃ gacchati parvatam</li>
    </ul>
    <ul id="R_4.041.045">
        <li id="R_4.041.045ab">etāvad vānaraiḥ śakyaṃ gantuṃ vānarapuṃgavāḥ</li>
        <li id="R_4.041.045cd">abhāskaram amaryādaṃ na jānīmas tataḥ param</li>
    </ul>
    <ul id="R_4.041.046">
        <li id="R_4.041.046ab">adhigamya tu vaidehīṃ nilayaṃ rāvaṇasya ca</li>
        <li id="R_4.041.046cd">astaṃ parvatam āsādya pūrṇe māse nivartata</li>
    </ul>
    <ul id="R_4.041.047">
        <li id="R_4.041.047ab">ūrdhvaṃ māsān na vastavyaṃ vasan vadhyo bhaven mama</li>
        <li id="R_4.041.047cd">sahaiva śūro yuṣmābhiḥ śvaśuro me gamiṣyati</li>
    </ul>
    <ul id="R_4.041.048">
        <li id="R_4.041.048ab">śrotavyaṃ sarvam etasya bhavadbhir diṣṭa kāribhiḥ</li>
        <li id="R_4.041.048cd">gurur eṣa mahābāhuḥ śvaśuro me mahābalaḥ</li>
    </ul>
    <ul id="R_4.041.049">
        <li id="R_4.041.049ab">bhavantaś cāpi vikrāntāḥ pramāṇaṃ sarvakarmasu</li>
        <li id="R_4.041.049cd">pramāṇam enaṃ saṃsthāpya paśyadhvaṃ paścimāṃ diśam</li>
    </ul>
    <ul id="R_4.041.050">
        <li id="R_4.041.050ab">dṛṣṭāyāṃ tu narendrasyā patnyām amitatejasaḥ</li>
        <li id="R_4.041.050cd">kṛtakṛtyā bhaviṣyāmaḥ kṛtasya pratikarmaṇā</li>
    </ul>
    <ul id="R_4.041.051">
        <li id="R_4.041.051ab">ato 'nyad api yat kiṃ cit kāryasyāsya hitaṃ bhavet</li>
        <li id="R_4.041.051cd">saṃpradhārya bhavadbhiś ca deśakālārthasaṃhitam</li>
    </ul>
    <ul id="R_4.041.052">
        <li id="R_4.041.052ab">
            <seg type="pāda" n="a">tataḥ suṣeṇa pramukhāḥ plavaṃgamāḥ</seg>
            <seg type="pāda" n="b">sugrīvavākyaṃ nipuṇaṃ niśamya</seg>
        </li>
        <li id="R_4.041.052cd">
            <seg type="pāda" n="c">āmantrya sarve plavagādhipaṃ te</seg>
            <seg type="pāda" n="d">jagmur diśaṃ tāṃ varuṇābhiguptām</seg>
        </li>
    </ul>
    <ul id="R_4.042.001">
        <li id="R_4.042.001ab">tataḥ saṃdiśya sugrīvaḥ śvaśuraṃ paścimāṃ diśam</li>
        <li id="R_4.042.001cd">vīraṃ śatabaliṃ nāma vānaraṃ vānararṣabhaḥ</li>
    </ul>
    <ul id="R_4.042.002">
        <li id="R_4.042.002ab">uvāca rājā mantrajñaḥ sarvavānarasaṃmatam</li>
        <li id="R_4.042.002cd">vākyam ātmahitaṃ caiva rāmasya ca hitaṃ tathā</li>
    </ul>
    <ul id="R_4.042.003">
        <li id="R_4.042.003ab">vṛtaḥ śatasahasreṇa tvadvidhānāṃ vanaukasām</li>
        <li id="R_4.042.003cd">vaivasvata sutaiḥ sārdhaṃ pratiṣṭhasva svamantribhiḥ</li>
    </ul>
    <ul id="R_4.042.004">
        <li id="R_4.042.004ab">diśaṃ hy udīcīṃ vikrāntāṃ himaśailāvataṃsakām</li>
        <li id="R_4.042.004cd">sarvataḥ parimārgadhvaṃ rāmapatnīm aninditām</li>
    </ul>
    <ul id="R_4.042.005">
        <li id="R_4.042.005ab">asmin kārye vinivṛtte kṛte dāśaratheḥ priye</li>
        <li id="R_4.042.005cd">ṛṇān muktā bhaviṣyāmaḥ kṛtārthārthavidāṃ varāḥ</li>
    </ul>
    <ul id="R_4.042.006">
        <li id="R_4.042.006ab">kṛtaṃ hi priyam asmākaṃ rāghaveṇa mahātmanā</li>
        <li id="R_4.042.006cd">tasya cet pratikāro 'sti saphalaṃ jīvitaṃ bhavet</li>
    </ul>
    <ul id="R_4.042.007">
        <li id="R_4.042.007ab">etāṃ buddhiṃ samāsthāya dṛśyate jānakī yathā</li>
        <li id="R_4.042.007cd">tathā bhavadbhiḥ kartavyam asmatpriyahitaiṣibhiḥ</li>
    </ul>
    <ul id="R_4.042.008">
        <li id="R_4.042.008ab">ayaṃ hi sarvabhūtānāṃ mānyas tu narasattamaḥ</li>
        <li id="R_4.042.008cd">asmāsu cāgataprītī rāmaḥ parapuraṃjayaḥ</li>
    </ul>
    <ul id="R_4.042.009">
        <li id="R_4.042.009ab">imāni vanadurgāṇi nadyaḥ śailāntarāṇi ca</li>
        <li id="R_4.042.009cd">bhavantaḥ parimārgaṃs tu buddhivikramasaṃpadā</li>
    </ul>
    <ul id="R_4.042.010">
        <li id="R_4.042.010ab">tatra mlecchān pulindāṃś ca śūrasenāṃs tathaiva ca</li>
        <li id="R_4.042.010cd">prasthālān bharatāṃś caiva kurūṃś ca saha madrakaiḥ</li>
    </ul>
    <ul id="R_4.042.011">
        <li id="R_4.042.011ab">kāmbojān yavanāṃś caiva śakān āraṭṭakān api</li>
        <li id="R_4.042.011cd">bāhlīkān ṛṣikāṃś caiva pauravān atha ṭaṅkaṇān</li>
    </ul>
    <ul id="R_4.042.012">
        <li id="R_4.042.012ab">cīnān paramacīnāṃś ca nīhārāṃś ca punaḥ punaḥ</li>
        <li id="R_4.042.012cd">anviṣya daradāṃś caiva himavantaṃ vicinvatha</li>
    </ul>
    <ul id="R_4.042.013">
        <li id="R_4.042.013ab">lodhrapadmakaṣaṇḍeṣu devadāruvaneṣu ca</li>
        <li id="R_4.042.013cd">rāvaṇaḥ saha vaidehya mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.042.014">
        <li id="R_4.042.014ab">tataḥ somāśramaṃ gatvā devagandharvasevitam</li>
        <li id="R_4.042.014cd">kālaṃ nāma mahāsānuṃ parvataṃ taṃ gamiṣyatha</li>
    </ul>
    <ul id="R_4.042.015">
        <li id="R_4.042.015ab">mahatsu tasya śṛṅgeṣu nirdareṣu guhāsu ca</li>
        <li id="R_4.042.015cd">vicinudhvaṃ mahābhāgāṃ rāmapatnīṃ yaśasvinīm</li>
    </ul>
    <ul id="R_4.042.016">
        <li id="R_4.042.016ab">tam atikramya śailendraṃ hemavargaṃ mahāgirim</li>
        <li id="R_4.042.016cd">tataḥ sudarśanaṃ nāma parvataṃ gantum arhatha</li>
    </ul>
    <ul id="R_4.042.017">
        <li id="R_4.042.017ab">tasya kānanaṣaṇḍeṣu nirdareṣu guhāsu ca</li>
        <li id="R_4.042.017cd">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.042.018">
        <li id="R_4.042.018ab">tam atikramya cākāśaṃ sarvataḥ śatayojanam</li>
        <li id="R_4.042.018cd">aparvatanadī vṛkṣaṃ sarvasattvavivarjitam</li>
    </ul>
    <ul id="R_4.042.019">
        <li id="R_4.042.019ab">taṃ tu śīghram atikramya kāntāraṃ romaharṣaṇam</li>
        <li id="R_4.042.019cd">kailāsaṃ pāṇḍuraṃ śailaṃ prāpya hṛṣṭā bhaviṣyatha</li>
    </ul>
    <ul id="R_4.042.020">
        <li id="R_4.042.020ab">tatra pāṇḍurameghābhaṃ jāmbūnadapariṣkṛtam</li>
        <li id="R_4.042.020cd">kuberabhavanaṃ divyaṃ nirmitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_4.042.021">
        <li id="R_4.042.021ab">viśālā nalinī yatra prabhūtakamalotpalā</li>
        <li id="R_4.042.021cd">haṃsakāraṇḍavākīrṇā apsarogaṇasevitā</li>
    </ul>
    <ul id="R_4.042.022">
        <li id="R_4.042.022ab">tatra vaiśravaṇo rājā sarvabhūtanamaskṛtaḥ</li>
        <li id="R_4.042.022cd">dhanado ramate śrīmān guhyakaiḥ saha yakṣarāṭ</li>
    </ul>
    <ul id="R_4.042.023">
        <li id="R_4.042.023ab">tasya candranikaśeṣu parvateṣu guhāsu ca</li>
        <li id="R_4.042.023cd">rāvaṇaḥ saha vaidehyā mārgitavyas tatas tataḥ</li>
    </ul>
    <ul id="R_4.042.024">
        <li id="R_4.042.024ab">krauñcaṃ tu girim āsādya bilaṃ tasya sudurgamam</li>
        <li id="R_4.042.024cd">apramattaiḥ praveṣṭavyaṃ duṣpraveśaṃ hi tat smṛtam</li>
    </ul>
    <ul id="R_4.042.025">
        <li id="R_4.042.025ab">vasanti hi mahātmānas tatra sūryasamaprabhāḥ</li>
        <li id="R_4.042.025cd">devair apy arcitāḥ samyag devarūpā maharṣayaḥ</li>
    </ul>
    <ul id="R_4.042.026">
        <li id="R_4.042.026ab">krauñcasya tu guhāś cānyāḥ sānūni śikharāṇi ca</li>
        <li id="R_4.042.026cd">nirdarāś ca nitambāś ca vicetavyās tatas tataḥ</li>
    </ul>
    <ul id="R_4.042.027">
        <li id="R_4.042.027ab">krauñcasya śikharaṃ cāpi nirīkṣya ca tatas tataḥ</li>
        <li id="R_4.042.027cd">avṛkṣaṃ kāmaśailaṃ ca mānasaṃ vihagālayam</li>
    </ul>
    <ul id="R_4.042.028">
        <li id="R_4.042.028ab">na gatis tatra bhūtānāṃ devadānavarakṣasām</li>
        <li id="R_4.042.028cd">sa ca sarvair vicetavyaḥ sasānuprasthabhūdharaḥ</li>
    </ul>
    <ul id="R_4.042.029">
        <li id="R_4.042.029ab">krauñcaṃ girim atikramya maināko nāma parvataḥ</li>
        <li id="R_4.042.029cd">mayasya bhavanaṃ tatra dānavasya svayaṃ kṛtam</li>
    </ul>
    <ul id="R_4.042.030">
        <li id="R_4.042.030ab">mainākas tu vicetavyaḥ sasānuprasthakandaraḥ</li>
        <li id="R_4.042.030cd">strīṇām aśvamukhīnāṃ ca niketās tatra tatra tu</li>
    </ul>
    <ul id="R_4.042.031">
        <li id="R_4.042.031ab">taṃ deśaṃ samatikramya āśramaṃ siddhasevitam</li>
        <li id="R_4.042.031cd">siddhā vaikhānasās tatra vālakhilyāś ca tāpasāḥ</li>
    </ul>
    <ul id="R_4.042.032">
        <li id="R_4.042.032ab">vandyās te tu tapaḥsiddhās tāpasā vītakalmaṣāḥ</li>
        <li id="R_4.042.032cd">praṣṭavyāś cāpi sītāyāḥ pravṛttaṃ vinayānvitaiḥ</li>
    </ul>
    <ul id="R_4.042.033">
        <li id="R_4.042.033ab">hemapuṣkarasaṃchannaṃ tatra vaikhānasaṃ saraḥ</li>
        <li id="R_4.042.033cd">taruṇādityasaṃkāśair haṃsair vicaritaṃ śubhaiḥ</li>
    </ul>
    <ul id="R_4.042.034">
        <li id="R_4.042.034ab">aupavāhyaḥ kuberasya sarvabhauma iti smṛtaḥ</li>
        <li id="R_4.042.034cd">gajaḥ paryeti taṃ deśaṃ sadā saha kareṇubhiḥ</li>
    </ul>
    <ul id="R_4.042.035">
        <li id="R_4.042.035ab">tat sāraḥ samatikramya naṣṭacandradivākaram</li>
        <li id="R_4.042.035cd">anakṣatragaṇaṃ vyoma niṣpayodam anādimat</li>
    </ul>
    <ul id="R_4.042.036">
        <li id="R_4.042.036ab">gabhastibhir ivārkasya sa tu deśaḥ prakāśate</li>
        <li id="R_4.042.036cd">viśrāmyadbhis tapaḥ siddhair devakalpaiḥ svayamprabhaiḥ</li>
    </ul>
    <ul id="R_4.042.037">
        <li id="R_4.042.037ab">taṃ tu deśam atikramya śailodā nāma nimnagā</li>
        <li id="R_4.042.037cd">ubhayos tīrayor yasyāḥ kīcakā nāma veṇavaḥ</li>
    </ul>
    <ul id="R_4.042.038">
        <li id="R_4.042.038ab">te nayanti paraṃ tīraṃ siddhān pratyānayanti ca</li>
        <li id="R_4.042.038cd">uttarāḥ kuravas tatra kṛtapuṇyapratiśriyāḥ</li>
    </ul>
    <ul id="R_4.042.039">
        <li id="R_4.042.039ab">tataḥ kāñcanapadmābhiḥ padminībhiḥ kṛtodakāḥ</li>
        <li id="R_4.042.039cd">nīlavaidūryapatrāḍhyā nadyas tatra sahasraśaḥ</li>
    </ul>
    <ul id="R_4.042.040">
        <li id="R_4.042.040ab">raktotpalavanaiś cātra maṇḍitāś ca hiraṇmayaiḥ</li>
        <li id="R_4.042.040cd">taruṇādityasadṛśair bhānti tatra jalāśayāḥ</li>
    </ul>
    <ul id="R_4.042.041">
        <li id="R_4.042.041ab">mahārhamaṇipatraiś ca kāñcanaprabha kesaraiḥ</li>
        <li id="R_4.042.041cd">nīlotpalavanaiś citraiḥ sa deśaḥ sarvatovṛtaḥ</li>
    </ul>
    <ul id="R_4.042.042">
        <li id="R_4.042.042ab">nistulābhiś ca muktābhir maṇibhiś ca mahādhanaiḥ</li>
        <li id="R_4.042.042cd">udbhūtapulinās tatra jātarūpaiś ca nimnagāḥ</li>
    </ul>
    <ul id="R_4.042.043">
        <li id="R_4.042.043ab">sarvaratnamayaiś citrair avagāḍhā nagottamaiḥ</li>
        <li id="R_4.042.043cd">jātarūpamayaiś cāpi hutāśanasamaprabhaiḥ</li>
    </ul>
    <ul id="R_4.042.044">
        <li id="R_4.042.044ab">nityapuṣpaphalāś cātra nagāḥ patrarathākulāḥ</li>
        <li id="R_4.042.044cd">divyagandharasasparśāḥ sarvakāmān sravanti ca</li>
    </ul>
    <ul id="R_4.042.045">
        <li id="R_4.042.045ab">nānākārāṇi vāsāṃsi phalanty anye nagottamāḥ</li>
        <li id="R_4.042.045cd">muktāvaidūryacitrāṇi bhūṣaṇāni tathaiva ca</li>
    </ul>
    <ul id="R_4.042.046">
        <li id="R_4.042.046ab">strīṇāṃ yāny anurūpāṇi puruṣāṇāṃ tathaiva ca</li>
        <li id="R_4.042.046cd">sarvartusukhasevyāni phalanty anye nagottamāḥ</li>
    </ul>
    <ul id="R_4.042.047">
        <li id="R_4.042.047ab">mahārhāṇi vicitrāṇi haimāny anye nagottamāḥ</li>
        <li id="R_4.042.047cd">śayanāni prasūyante citrāstāraṇavanti ca</li>
    </ul>
    <ul id="R_4.042.048">
        <li id="R_4.042.048ab">manaḥkāntāni mālyāni phalanty atrāpare drumāḥ</li>
        <li id="R_4.042.048cd">pānāni ca mahārhāṇi bhakṣyāṇi vividhāni ca</li>
    </ul>
    <ul id="R_4.042.049">
        <li id="R_4.042.049ab">striyaś ca guṇasaṃpannā rūpayauvanalakṣitāḥ</li>
        <li id="R_4.042.049cd">gandharvāḥ kiṃnarā siddhā nāgā vidyādharās tathā</li>
        <li id="R_4.042.049ef">ramante sahitās tatra nārībhir bhāskaraprabhāḥ</li>
    </ul>
    <ul id="R_4.042.050">
        <li id="R_4.042.050ab">sarve sukṛtakarmāṇaḥ sarve ratiparāyaṇāḥ</li>
        <li id="R_4.042.050cd">sarve kāmārthasahitā vasanti saha yoṣitaḥ</li>
    </ul>
    <ul id="R_4.042.051">
        <li id="R_4.042.051ab">gītavāditranirghoṣaḥ sotkṛṣṭahasitasvanaḥ</li>
        <li id="R_4.042.051cd">śrūyate satataṃ tatra sarvabhūtamanoharaḥ</li>
    </ul>
    <ul id="R_4.042.052">
        <li id="R_4.042.052ab">tatra nāmuditaḥ kaś cin nāsti kaś cid asatpriyaḥ</li>
        <li id="R_4.042.052cd">ahany ahani vardhante guṇās tatra manoramāḥ</li>
    </ul>
    <ul id="R_4.042.053">
        <li id="R_4.042.053ab">samatikramya taṃ deśam uttaras toyasāṃ nidhiḥ</li>
        <li id="R_4.042.053cd">tatra somagirir nāma madhye hemamayo mahān</li>
    </ul>
    <ul id="R_4.042.054">
        <li id="R_4.042.054ab">indralokagatā ye ca brahmalokagatāś ca ye</li>
        <li id="R_4.042.054cd">devās taṃ samavekṣante girirājaṃ divaṃ gatam</li>
    </ul>
    <ul id="R_4.042.055">
        <li id="R_4.042.055ab">sa tu deśo visūryo 'pi tasya bhāsā prakāśate</li>
        <li id="R_4.042.055cd">sūryalakṣmyābhivijñeyas tapaseva vivasvatā</li>
    </ul>
    <ul id="R_4.042.056">
        <li id="R_4.042.056ab">bhagavān api viśvātmā śambhur ekādaśātmakaḥ</li>
        <li id="R_4.042.056cd">brahmā vasati deveśo brahmarṣiparivāritaḥ</li>
    </ul>
    <ul id="R_4.042.057">
        <li id="R_4.042.057ab">na kathaṃ cana gantavyaṃ kurūṇām uttareṇa vaḥ</li>
        <li id="R_4.042.057cd">anyeṣām api bhūtānāṃ nātikrāmati vai gatiḥ</li>
    </ul>
    <ul id="R_4.042.058">
        <li id="R_4.042.058ab">sā hi somagirir nāma devānām api durgamaḥ</li>
        <li id="R_4.042.058cd">tam ālokya tataḥ kṣipram upāvartitum arhatha</li>
    </ul>
    <ul id="R_4.042.059">
        <li id="R_4.042.059ab">etāvad vānaraiḥ śakyaṃ gantuṃ vānarapuṃgavāḥ</li>
        <li id="R_4.042.059cd">abhāskaram amaryādaṃ na jānīmas tataḥ param</li>
    </ul>
    <ul id="R_4.042.060">
        <li id="R_4.042.060ab">sarvam etad vicetavyaṃ yan mayā parikīrtitam</li>
        <li id="R_4.042.060cd">yad anyad api noktaṃ ca tatrāpi kriyatāṃ matiḥ</li>
    </ul>
    <ul id="R_4.042.061">
        <li id="R_4.042.061ab">
            <seg type="pāda" n="a">tataḥ kṛtaṃ dāśarather mahat priyaṃ</seg>
            <seg type="pāda" n="b">mahattaraṃ cāpi tato mama priyam</seg>
        </li>
        <li id="R_4.042.061cd">
            <seg type="pāda" n="c">kṛtaṃ bhaviṣyaty anilānalopamā</seg>
            <seg type="pāda" n="d">videhajā darśanajena karmaṇā</seg>
        </li>
    </ul>
    <ul id="R_4.042.062">
        <li id="R_4.042.062ab">
            <seg type="pāda" n="a">tataḥ kṛtārthāḥ sahitāḥ sabāndhavā</seg>
            <seg type="pāda" n="b">mayārcitāḥ sarvaguṇair manoramaiḥ</seg>
        </li>
        <li id="R_4.042.062cd">
            <seg type="pāda" n="c">cariṣyathorvīṃ pratiśāntaśatravaḥ</seg>
            <seg type="pāda" n="d">sahapriyā bhūtadharāḥ plavaṃgamāḥ</seg>
        </li>
    </ul>
    <ul id="R_4.043.001">
        <li id="R_4.043.001ab">viśeṣeṇa tu sugrīvo hanumatyartham uktavān</li>
        <li id="R_4.043.001cd">sa hi tasmin hariśreṣṭhe niścitārtho 'rthasādhane</li>
    </ul>
    <ul id="R_4.043.002">
        <li id="R_4.043.002ab">na bhūmau nāntarikṣe vā nāmbare nāmarālaye</li>
        <li id="R_4.043.002cd">nāpsu vā gatisaṃgaṃ te paśyāmi haripuṃgava</li>
    </ul>
    <ul id="R_4.043.003">
        <li id="R_4.043.003ab">sāsurāḥ sahagandharvāḥ sanāganaradevatāḥ</li>
        <li id="R_4.043.003cd">viditāḥ sarvalokās te sasāgaradharādharāḥ</li>
    </ul>
    <ul id="R_4.043.004">
        <li id="R_4.043.004ab">gatir vegaś ca tejaś ca lāghavaṃ ca mahākape</li>
        <li id="R_4.043.004cd">pitus te sadṛśaṃ vīra mārutasya mahaujasaḥ</li>
    </ul>
    <ul id="R_4.043.005">
        <li id="R_4.043.005ab">tejasā vāpi te bhūtaṃ samaṃ bhuvi na vidyate</li>
        <li id="R_4.043.005cd">tad yathā labhyate sītā tattvam evopapādaya</li>
    </ul>
    <ul id="R_4.043.006">
        <li id="R_4.043.006ab">tvayy eva hanumann asti balaṃ buddhiḥ parākramaḥ</li>
        <li id="R_4.043.006cd">deśakālānuvṛttaś ca nayaś ca nayapaṇḍita</li>
    </ul>
    <ul id="R_4.043.007">
        <li id="R_4.043.007ab">tataḥ kāryasamāsaṃgam avagamya hanūmati</li>
        <li id="R_4.043.007cd">viditvā hanumantaṃ ca cintayām āsa rāghavaḥ</li>
    </ul>
    <ul id="R_4.043.008">
        <li id="R_4.043.008ab">sarvathā niścitārtho 'yaṃ hanūmati harīśvaraḥ</li>
        <li id="R_4.043.008cd">niścitārthataraś cāpi hanūmān kāryasādhane</li>
    </ul>
    <ul id="R_4.043.009">
        <li id="R_4.043.009ab">tad evaṃ prasthitasyāsya parijñātasya karmabhiḥ</li>
        <li id="R_4.043.009cd">bhartrā parigṛhītasya dhruvaḥ kāryaphalodayaḥ</li>
    </ul>
    <ul id="R_4.043.010">
        <li id="R_4.043.010ab">taṃ samīkṣya mahātejā vyavasāyottaraṃ harim</li>
        <li id="R_4.043.010cd">kṛtārtha iva saṃvṛttaḥ prahṛṣṭendriyamānasaḥ</li>
    </ul>
    <ul id="R_4.043.011">
        <li id="R_4.043.011ab">dadau tasya tataḥ prītaḥ svanāmāṅkopaśobhitam</li>
        <li id="R_4.043.011cd">aṅgulīyam abhijñānaṃ rājaputryāḥ paraṃtapaḥ</li>
    </ul>
    <ul id="R_4.043.012">
        <li id="R_4.043.012ab">anena tvāṃ hariśreṣṭha cihnena janakātmajā</li>
        <li id="R_4.043.012cd">matsakāśād anuprāptam anudvignānupaśyati</li>
    </ul>
    <ul id="R_4.043.013">
        <li id="R_4.043.013ab">vyavasāyaś ca te vīra sattvayuktaś ca vikramaḥ</li>
        <li id="R_4.043.013cd">sugrīvasya ca saṃdeśaḥ siddhiṃ kathayatīva me</li>
    </ul>
    <ul id="R_4.043.014">
        <li id="R_4.043.014ab">sa tad gṛhya hariśreṣṭhaḥ sthāpya mūrdhni kṛtāñjaliḥ</li>
        <li id="R_4.043.014cd">vanditvā caraṇau caiva prasthitaḥ plavagottamaḥ</li>
    </ul>
    <ul id="R_4.043.015">
        <li id="R_4.043.015ab">
            <seg type="pāda" n="a">sa tat prakarṣan hariṇāṃ balaṃ mahad</seg>
            <seg type="pāda" n="b">babhūva vīraḥ pavanātmajaḥ kapi</seg>
        </li>
        <li id="R_4.043.015cd">
            <seg type="pāda" n="c">gatāmbude vyomni viśuddhamaṇḍalaḥ</seg>
            <seg type="pāda" n="d">śaśīva nakṣatragaṇopaśobhitaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.043.016">
        <li id="R_4.043.016ab">
            <seg type="pāda" n="a">atibalabalam āśritas tavāhaṃ</seg>
            <seg type="pāda" n="b">harivaravikramavikramair analpaiḥ</seg>
        </li>
        <li id="R_4.043.016cd">
            <seg type="pāda" n="c">pavanasuta yathābhigamyate sā</seg>
            <seg type="pāda" n="d">janakasutā hanumaṃs tathā kuruṣva</seg>
        </li>
    </ul>
    <ul id="R_4.044.001">
        <li id="R_4.044.001ab">tad ugraśāsanaṃ bhartur vijñāya haripuṃgavāḥ</li>
        <li id="R_4.044.001cd">śalabhā iva saṃchādya medinīṃ saṃpratasthire</li>
    </ul>
    <ul id="R_4.044.002">
        <li id="R_4.044.002ab">rāmaḥ prasravaṇe tasmin nyavasat sahalakṣmaṇaḥ</li>
        <li id="R_4.044.002cd">pratīkṣamāṇas taṃ māsaṃ yaḥ sītādhigame kṛtaḥ</li>
    </ul>
    <ul id="R_4.044.003">
        <li id="R_4.044.003ab">uttarāṃ tu diśaṃ ramyāṃ girirājasamāvṛtām</li>
        <li id="R_4.044.003cd">pratasthe sahasā vīro hariḥ śatabalis tadā</li>
    </ul>
    <ul id="R_4.044.004">
        <li id="R_4.044.004ab">pūrvāṃ diśaṃ prati yayau vinato hariyūthapaḥ</li>
    </ul>
    <ul id="R_4.044.005">
        <li id="R_4.044.005ab">tārāṅgadādi sahitaḥ plavagaḥ pavanātmajaḥ</li>
        <li id="R_4.044.005cd">agastyacaritām āśāṃ dakṣiṇāṃ hariyūthapaḥ</li>
    </ul>
    <ul id="R_4.044.006">
        <li id="R_4.044.006ab">paścimāṃ tu diśaṃ ghorāṃ suṣeṇaḥ plavageśvaraḥ</li>
        <li id="R_4.044.006cd">pratasthe hariśārdūlo bhṛśaṃ varuṇapālitām</li>
    </ul>
    <ul id="R_4.044.007">
        <li id="R_4.044.007ab">tataḥ sarvā diśo rājā codayitvā yathā tatham</li>
        <li id="R_4.044.007cd">kapisenā patīn mukhyān mumoda sukhitaḥ sukham</li>
    </ul>
    <ul id="R_4.044.008">
        <li id="R_4.044.008ab">evaṃ saṃcoditāḥ sarve rājñā vānarayūthapāḥ</li>
        <li id="R_4.044.008cd">svāṃ svāṃ diśam abhipretya tvaritāḥ saṃpratasthire</li>
    </ul>
    <ul id="R_4.044.009">
        <li id="R_4.044.009ab">nadantaś connadantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_4.044.009cd">kṣvelanto dhāvamānāś ca yayuḥ plavagasattamāḥ</li>
        <li id="R_4.044.009ef">ānayiṣyāmahe sītāṃ haniṣyāmaś ca rāvaṇam</li>
    </ul>
    <ul id="R_4.044.010">
        <li id="R_4.044.010ab">aham eko haniṣyāmi prāptaṃ rāvaṇam āhave</li>
        <li id="R_4.044.010cd">tataś conmathya sahasā hariṣye janakātmajām</li>
    </ul>
    <ul id="R_4.044.011">
        <li id="R_4.044.011ab">vepamānaṃ śrameṇādya bhavadbhiḥ sthīyatām iti</li>
        <li id="R_4.044.011cd">eka evāhariṣyāmi pātālād api jānakīm</li>
    </ul>
    <ul id="R_4.044.012">
        <li id="R_4.044.012ab">vidhamiṣyāmy ahaṃ vṛkṣān dārayiṣyāmy ahaṃ girīn</li>
        <li id="R_4.044.012cd">dharaṇīṃ dārayiṣyāmi kṣobhayiṣyāmi sāgarān</li>
    </ul>
    <ul id="R_4.044.013">
        <li id="R_4.044.013ab">ahaṃ yojanasaṃkhyāyāḥ plavitā nātra saṃśayaḥ</li>
        <li id="R_4.044.013cd">śataṃ yojanasaṃkhyāyāḥ śataṃ samadhikaṃ hy aham</li>
    </ul>
    <ul id="R_4.044.014">
        <li id="R_4.044.014ab">bhūtale sāgare vāpi śaileṣu ca vaneṣu ca</li>
        <li id="R_4.044.014cd">pātālasyāpi vā madhye na mamācchidyate gatiḥ</li>
    </ul>
    <ul id="R_4.044.015">
        <li id="R_4.044.015ab">ity ekaikaṃ tadā tatra vānarā baladarpitāḥ</li>
        <li id="R_4.044.015cd">ūcuś ca vacanaṃ tasmin harirājasya saṃnidhau</li>
    </ul>
    <ul id="R_4.045.001">
        <li id="R_4.045.001ab">gateṣu vānarendreṣu rāmaḥ sugrīvam abravīt</li>
        <li id="R_4.045.001cd">kathaṃ bhavān vinājīte sarvaṃ vai maṇḍalaṃ bhuvaḥ</li>
    </ul>
    <ul id="R_4.045.002">
        <li id="R_4.045.002ab">sugrīvas tu tato rāmam uvāca praṇatātmavān</li>
        <li id="R_4.045.002cd">śrūyatāṃ sarvam ākhyāsye vistareṇa nararṣabha</li>
    </ul>
    <ul id="R_4.045.003">
        <li id="R_4.045.003ab">yadā tu dundubhiṃ nāma dānavaṃ mahiṣākṛtim</li>
        <li id="R_4.045.003cd">parikālayate vālī malayaṃ prati parvatam</li>
    </ul>
    <ul id="R_4.045.004">
        <li id="R_4.045.004ab">tadā viveśa mahiṣo malayasya guhāṃ prati</li>
        <li id="R_4.045.004cd">viveśa vālī tatrāpi malayaṃ tajjighāṃsayā</li>
    </ul>
    <ul id="R_4.045.005">
        <li id="R_4.045.005ab">tato 'haṃ tatra nikṣipto guhād vārivinītavat</li>
        <li id="R_4.045.005cd">na ca niṣkramate vālī tadā saṃvatsare gate</li>
    </ul>
    <ul id="R_4.045.006">
        <li id="R_4.045.006ab">tataḥ kṣatajavegena āpupūre tadā bilam</li>
        <li id="R_4.045.006cd">tad ahaṃ vismito dṛṣṭvā bhrātṛśokaviṣārditaḥ</li>
    </ul>
    <ul id="R_4.045.007">
        <li id="R_4.045.007ab">athāhaṃ kṛtabuddhis tu suvyaktaṃ nihato guruḥ</li>
        <li id="R_4.045.007cd">śilāparvatasaṃkāśā biladvāri mayā kṛtā</li>
        <li id="R_4.045.007ef">aśaknuvan niṣkramituṃ mahiṣo vinaśed iti</li>
    </ul>
    <ul id="R_4.045.008">
        <li id="R_4.045.008ab">tato 'ham āgāṃ kiṣkindhāṃ nirāśas tasya jīvite</li>
        <li id="R_4.045.008cd">rājyaṃ ca sumahat prāptaṃ tārā ca rumayā saha</li>
        <li id="R_4.045.008ef">mitraiś ca sahitas tatra vasāmi vigatajvaraḥ</li>
    </ul>
    <ul id="R_4.045.009">
        <li id="R_4.045.009ab">ājagāma tato vālī hatvā taṃ dānavarṣabham</li>
        <li id="R_4.045.009cd">tato 'ham adadāṃ rājyaṃ gauravād bhayayantritaḥ</li>
    </ul>
    <ul id="R_4.045.010">
        <li id="R_4.045.010ab">sa māṃ jighāṃsur duṣṭātmā vālī pravyathitendriyaḥ</li>
        <li id="R_4.045.010cd">parilākayate krodhād dhāvantaṃ sacivaiḥ saha</li>
    </ul>
    <ul id="R_4.045.011">
        <li id="R_4.045.011ab">tato 'haṃ vālinā tena sānubandhaḥ pradhāvitaḥ</li>
        <li id="R_4.045.011cd">nadīś ca vividhāḥ paśyan vanāni nagarāṇi ca</li>
    </ul>
    <ul id="R_4.045.012">
        <li id="R_4.045.012ab">ādarśatalasaṃkāśā tato vai pṛthivī mayā</li>
        <li id="R_4.045.012cd">alātacakrapratimā dṛṣṭā goṣpadavat tadā</li>
    </ul>
    <ul id="R_4.045.013">
        <li id="R_4.045.013ab">tataḥ pūrvam ahaṃ gatvā dakṣiṇām aham āśritaḥ</li>
        <li id="R_4.045.013cd">diśaṃ ca paścimāṃ bhūyo gato 'smi bhayaśaṅkitaḥ</li>
        <li id="R_4.045.013ef">uttarāṃ tu diśaṃ yāntaṃ hanumān mām athābravīt</li>
    </ul>
    <ul id="R_4.045.014">
        <li id="R_4.045.014ab">idānīṃ me smṛtaṃ rājan yathā vālī harīśvaraḥ</li>
        <li id="R_4.045.014cd">mataṅgena tadā śapto hy asminn āśramamaṇḍale</li>
    </ul>
    <ul id="R_4.045.015">
        <li id="R_4.045.015ab">praviśed yadi vā vālī mūrdhāsya śatadhā bhavet</li>
        <li id="R_4.045.015cd">tatra vāsaḥ sukho 'smākaṃ nirudvigno bhaviṣyati</li>
    </ul>
    <ul id="R_4.045.016">
        <li id="R_4.045.016ab">tataḥ parvatam āsādya ṛśyamūkaṃ nṛpātmaja</li>
        <li id="R_4.045.016cd">na viveśa tadā vālī mataṅgasya bhayāt tadā</li>
    </ul>
    <ul id="R_4.045.017">
        <li id="R_4.045.017ab">evaṃ mayā tadā rājan pratyakṣam upalakṣitam</li>
        <li id="R_4.045.017cd">pṛthivīmaṇḍalaṃ kṛtsnaṃ guhām asmy āgatas tataḥ</li>
    </ul>
    <ul id="R_4.046.001">
        <li id="R_4.046.001ab">darśanārthaṃ tu vaidehyāḥ sarvataḥ kapiyūthapāḥ</li>
        <li id="R_4.046.001cd">vyādiṣṭāḥ kapirājena yathoktaṃ jagmur añjasā</li>
    </ul>
    <ul id="R_4.046.002">
        <li id="R_4.046.002ab">sarāṃsi saritaḥ kakṣān ākāśaṃ nagarāṇi ca</li>
        <li id="R_4.046.002cd">nadīdurgāṃs tathā śailān vicinvanti samantataḥ</li>
    </ul>
    <ul id="R_4.046.003">
        <li id="R_4.046.003ab">sugrīveṇa samākhyātān sarve vānarayūthapāḥ</li>
        <li id="R_4.046.003cd">pradeśān pravicinvanti saśailavanakānanān</li>
    </ul>
    <ul id="R_4.046.004">
        <li id="R_4.046.004ab">vicintya divasaṃ sarve sītādhigamane dhṛtāḥ</li>
        <li id="R_4.046.004cd">samāyānti sma medinyāṃ niśākāleśu vānarāḥ</li>
    </ul>
    <ul id="R_4.046.005">
        <li id="R_4.046.005ab">sarvartukāṃś ca deśeṣu vānarāḥ saphalān drumān</li>
        <li id="R_4.046.005cd">āsādya rajanīṃ śayyāṃ cakruḥ sarveṣv ahaḥsu te</li>
    </ul>
    <ul id="R_4.046.006">
        <li id="R_4.046.006ab">tad ahaḥ prathamaṃ kṛtvā māse prasravaṇaṃ gatāḥ</li>
        <li id="R_4.046.006cd">kapirājena saṃgamya nirāśāḥ kapiyūthapāḥ</li>
    </ul>
    <ul id="R_4.046.007">
        <li id="R_4.046.007ab">vicitya tu diśaṃ pūrvāṃ yathoktāṃ sacivaiḥ saha</li>
        <li id="R_4.046.007cd">adṛṣṭvā vinataḥ sītām ājagāma mahābalaḥ</li>
    </ul>
    <ul id="R_4.046.008">
        <li id="R_4.046.008ab">uttarāṃ tu diśaṃ sarvāṃ vicitya sa mahākapiḥ</li>
        <li id="R_4.046.008cd">āgataḥ saha sainyena vīraḥ śatabalis tadā</li>
    </ul>
    <ul id="R_4.046.009">
        <li id="R_4.046.009ab">suṣeṇaḥ paścimām āśāṃ vicitya saha vānaraiḥ</li>
        <li id="R_4.046.009cd">sametya māse saṃpūrṇe sugrīvam upacakrame</li>
    </ul>
    <ul id="R_4.046.010">
        <li id="R_4.046.010ab">taṃ prasravaṇapṛṣṭhasthaṃ samāsādyābhivādya ca</li>
        <li id="R_4.046.010cd">āsīnaṃ saha rāmeṇa sugrīvam idam abruvan</li>
    </ul>
    <ul id="R_4.046.011">
        <li id="R_4.046.011ab">vicitāḥ parvatāḥ sarve vanāni nagarāṇi ca</li>
        <li id="R_4.046.011cd">nimnagāḥ sāgarāntāś ca sarve janapadās tathā</li>
    </ul>
    <ul id="R_4.046.012">
        <li id="R_4.046.012ab">guhāś ca vicitāḥ sarvā yās tvayā parikīrtitāḥ</li>
        <li id="R_4.046.012cd">vicitāś ca mahāgulmā latāvitatasaṃtatāḥ</li>
    </ul>
    <ul id="R_4.046.013">
        <li id="R_4.046.013ab">gahaneṣu ca deśeṣu durgeṣu viṣameṣu ca</li>
        <li id="R_4.046.013cd">sattvāny atipramāṇāni vicitāni hatāni ca</li>
        <li id="R_4.046.013ef">ye caiva gahanā deśā vicitās te punaḥ punaḥ</li>
    </ul>
    <ul id="R_4.046.014">
        <li id="R_4.046.014ab">
            <seg type="pāda" n="a">udārasattvābhijano mahātmā</seg>
            <seg type="pāda" n="b">sa maithilīṃ drakṣyati vānarendraḥ</seg>
        </li>
        <li id="R_4.046.014cd">
            <seg type="pāda" n="c">diśaṃ tu yām eva gatā tu sītā</seg>
            <seg type="pāda" n="d">tām āsthito vāyusuto hanūmān</seg>
        </li>
    </ul>
    <ul id="R_4.047.001">
        <li id="R_4.047.001ab">sahatārāṅgadābhyāṃ tu gatvā sa hanumān kapiḥ</li>
        <li id="R_4.047.001cd">sugrīveṇa yathoddiṣṭaṃ taṃ deśam upacakrame</li>
    </ul>
    <ul id="R_4.047.002">
        <li id="R_4.047.002ab">sa tu dūram upāgamya sarvais taiḥ kapisattamaiḥ</li>
        <li id="R_4.047.002cd">vicinoti sma vindhyasya guhāś ca gahanāni ca</li>
    </ul>
    <ul id="R_4.047.003">
        <li id="R_4.047.003ab">parvatāgrān nadīdurgān sarāṃsi vipulān drumān</li>
        <li id="R_4.047.003cd">vṛkṣaṣaṇḍāṃś ca vividhān parvatān ghanapādapān</li>
    </ul>
    <ul id="R_4.047.004">
        <li id="R_4.047.004ab">anveṣamāṇās te sarve vānarāḥ sarvato diśam</li>
        <li id="R_4.047.004cd">na sītāṃ dadṛśur vīrā maithilīṃ janakātmajām</li>
    </ul>
    <ul id="R_4.047.005">
        <li id="R_4.047.005ab">te bhakṣayanto mūlāni phalāni vividhāni ca</li>
        <li id="R_4.047.005cd">anveṣamāṇā durdharṣā nyavasaṃs tatra tatra ha</li>
        <li id="R_4.047.005ef">sa tu deśo duranveṣo guhāgahanavān mahān</li>
    </ul>
    <ul id="R_4.047.006">
        <li id="R_4.047.006ab">tyaktvā tu taṃ tadā deśaṃ sarve vai hariyūthapāḥ</li>
        <li id="R_4.047.006cd">deśam anyaṃ durādharṣaṃ viviśuś cākutobhayāḥ</li>
    </ul>
    <ul id="R_4.047.007">
        <li id="R_4.047.007ab">yatra vandhyaphalā vṛkṣā vipuṣpāḥ parṇavarjitāḥ</li>
        <li id="R_4.047.007cd">nistoyāḥ sarito yatra mūlaṃ yatra sudurlabham</li>
    </ul>
    <ul id="R_4.047.008">
        <li id="R_4.047.008ab">na santi mahiṣā yatra na mṛgā na ca hastinaḥ</li>
        <li id="R_4.047.008cd">śārdūlāḥ pakṣiṇo vāpi ye cānye vanagocarāḥ</li>
    </ul>
    <ul id="R_4.047.009">
        <li id="R_4.047.009ab">snigdhapatrāḥ sthale yatra padminyaḥ phullapaṅkajāḥ</li>
        <li id="R_4.047.009cd">prekṣaṇīyāḥ sugandhāś ca bhramaraiś cāpi varjitāḥ</li>
    </ul>
    <ul id="R_4.047.010">
        <li id="R_4.047.010ab">kaṇḍur nāma mahābhāgaḥ satyavādī tapodhanaḥ</li>
        <li id="R_4.047.010cd">maharṣiḥ paramāmarṣī niyamair duṣpradharṣaṇaḥ</li>
    </ul>
    <ul id="R_4.047.011">
        <li id="R_4.047.011ab">tasya tasmin vane putro bālako daśavārṣikaḥ</li>
        <li id="R_4.047.011cd">pranaṣṭo jīvitāntāya kruddhas tatra mahāmuniḥ</li>
    </ul>
    <ul id="R_4.047.012">
        <li id="R_4.047.012ab">tena dharmātmanā śaptaṃ kṛtsnaṃ tatra mahad vanam</li>
        <li id="R_4.047.012cd">aśaraṇyaṃ durādharṣaṃ mṛgapakṣivivarjitam</li>
    </ul>
    <ul id="R_4.047.013">
        <li id="R_4.047.013ab">tasya te kānanāntāṃs tu girīṇāṃ kandarāṇi ca</li>
        <li id="R_4.047.013cd">prabhavāni nadīnāṃca vicinvanti samāhitāḥ</li>
    </ul>
    <ul id="R_4.047.014">
        <li id="R_4.047.014ab">tatra cāpi mahātmāno nāpaśyañ janakātmajām</li>
        <li id="R_4.047.014cd">hartāraṃ rāvaṇaṃ vāpi sugrīvapriyakāriṇaḥ</li>
    </ul>
    <ul id="R_4.047.015">
        <li id="R_4.047.015ab">te praviśya tu taṃ bhīmaṃ latāgulmasamāvṛtam</li>
        <li id="R_4.047.015cd">dadṛśuḥ krūrakarmāṇam asuraṃ suranirbhayam</li>
    </ul>
    <ul id="R_4.047.016">
        <li id="R_4.047.016ab">taṃ dṛṣṭvā vanarā ghoraṃ sthitaṃ śailam ivāparam</li>
        <li id="R_4.047.016cd">gāḍhaṃ parihitāḥ sarve dṛṣṭvā taṃ parvatopamam</li>
    </ul>
    <ul id="R_4.047.017">
        <li id="R_4.047.017ab">so 'pi tān vānarān sarvān naṣṭāḥ sthety abravīd balī</li>
        <li id="R_4.047.017cd">abhyadhāvata saṃkruddho muṣṭim udyamya saṃhitam</li>
    </ul>
    <ul id="R_4.047.018">
        <li id="R_4.047.018ab">tam āpatantaṃ sahasā vāliputro 'ṅgadas tadā</li>
        <li id="R_4.047.018cd">rāvaṇo 'yam iti jñātvā talenābhijaghāna ha</li>
    </ul>
    <ul id="R_4.047.019">
        <li id="R_4.047.019ab">sa vāliputrābhihato vaktrāc choṇitam udvaman</li>
        <li id="R_4.047.019cd">asuro nyapatad bhūmau paryasta iva parvataḥ</li>
    </ul>
    <ul id="R_4.047.020">
        <li id="R_4.047.020ab">te tu tasmin nirucchvāse vānarā jitakāśinaḥ</li>
        <li id="R_4.047.020cd">vyacinvan prāyaśas tatra sarvaṃ tad girigahvaram</li>
    </ul>
    <ul id="R_4.047.021">
        <li id="R_4.047.021ab">vicitaṃ tu tataḥ kṛtvā sarve te kānanaṃ punaḥ</li>
        <li id="R_4.047.021cd">anyadevāparaṃ ghoraṃ viviśur girigahvaram</li>
    </ul>
    <ul id="R_4.047.022">
        <li id="R_4.047.022ab">te vicintya punaḥ khinnā viniṣpatya samāgatāḥ</li>
        <li id="R_4.047.022cd">ekānte vṛkṣamūle tu niṣedur dīnamānasāḥ</li>
    </ul>
    <ul id="R_4.048.001">
        <li id="R_4.048.001ab">athāṅgadas tadā sarvān vānarān idam abravīt</li>
        <li id="R_4.048.001cd">pariśrānto mahāprājñaḥ samāśvāsya śanair vacaḥ</li>
    </ul>
    <ul id="R_4.048.002">
        <li id="R_4.048.002ab">vanāni girayo nadyo durgāṇi gahanāni ca</li>
        <li id="R_4.048.002cd">daryo giriguhāś caiva vicitā naḥ samantataḥ</li>
    </ul>
    <ul id="R_4.048.003">
        <li id="R_4.048.003ab">tatra tatra sahāsmābhir jānakī na ca dṛśyate</li>
        <li id="R_4.048.003cd">tad vā rakṣo hṛtā yena sītā surasutopamā</li>
    </ul>
    <ul id="R_4.048.004">
        <li id="R_4.048.004ab">kālaś ca no mahān yātaḥ sugrīvaś cograśāsanaḥ</li>
        <li id="R_4.048.004cd">tasmād bhavantaḥ sahitā vicinvantu samantataḥ</li>
    </ul>
    <ul id="R_4.048.005">
        <li id="R_4.048.005ab">vihāya tandrīṃ śokaṃ ca nidrāṃ caiva samutthitām</li>
        <li id="R_4.048.005cd">vicinudhvaṃ yathā sītāṃ paśyāmo janakātmajām</li>
    </ul>
    <ul id="R_4.048.006">
        <li id="R_4.048.006ab">anirvedaṃ ca dākṣyaṃ ca manasaś cāparājayam</li>
        <li id="R_4.048.006cd">kāryasiddhikarāṇy āhus tasmād etad bravīmy aham</li>
    </ul>
    <ul id="R_4.048.007">
        <li id="R_4.048.007ab">adyāpīdaṃ vanaṃ durgaṃ vicinvantu vanaukasaḥ</li>
        <li id="R_4.048.007cd">khedaṃ tyaktvā punaḥ sarvaṃ vanam etad vicīyatām</li>
    </ul>
    <ul id="R_4.048.008">
        <li id="R_4.048.008ab">avaśyaṃ kriyamāṇasya dṛśyate karmaṇaḥ phalam</li>
        <li id="R_4.048.008cd">alaṃ nirvedam āgamya na hi no malinaṃ kṣamam</li>
    </ul>
    <ul id="R_4.048.009">
        <li id="R_4.048.009ab">sugrīvaḥ krodhano rājā tīkṣṇadaṇḍaś ca vānarāḥ</li>
        <li id="R_4.048.009cd">bhetavyaṃ tasya satataṃ rāmasya ca mahātmanaḥ</li>
    </ul>
    <ul id="R_4.048.010">
        <li id="R_4.048.010ab">hitārtham etad uktaṃ vaḥ kriyatāṃ yadi rocate</li>
        <li id="R_4.048.010cd">ucyatāṃ vā kṣamaṃ yan naḥ sarveṣām eva vānarāḥ</li>
    </ul>
    <ul id="R_4.048.011">
        <li id="R_4.048.011ab">aṅgadasya vacaḥ śrutvā vacanaṃ gandhamādanaḥ</li>
        <li id="R_4.048.011cd">uvācāvyaktayā vācā pipāsā śramakhinnayā</li>
    </ul>
    <ul id="R_4.048.012">
        <li id="R_4.048.012ab">sadṛśaṃ khalu vo vākyam aṅgado yad uvāca ha</li>
        <li id="R_4.048.012cd">hitaṃ caivānukūlaṃ ca kriyatām asya bhāṣitam</li>
    </ul>
    <ul id="R_4.048.013">
        <li id="R_4.048.013ab">punar mārgāmahe śailān kandarāṃś ca darīs tathā</li>
        <li id="R_4.048.013cd">kānanāni ca śūnyāni giriprasravaṇāni ca</li>
    </ul>
    <ul id="R_4.048.014">
        <li id="R_4.048.014ab">yathoddiṣṭhāni sarvāṇi sugrīveṇa mahātmanā</li>
        <li id="R_4.048.014cd">vicinvantu vanaṃ sarve giridurgāṇi sarvaśaḥ</li>
    </ul>
    <ul id="R_4.048.015">
        <li id="R_4.048.015ab">tataḥ samutthāya punar vānarās te mahābalāḥ</li>
        <li id="R_4.048.015cd">vindhyakānanasaṃkīrṇāṃ vicerur dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_4.048.016">
        <li id="R_4.048.016ab">te śāradābhrapratimaṃ śrīmadrajataparvatam</li>
        <li id="R_4.048.016cd">śṛṅgavantaṃ darīvantam adhiruhya ca vānarāḥ</li>
    </ul>
    <ul id="R_4.048.017">
        <li id="R_4.048.017ab">tatra lodhravanaṃ ramyaṃ saptaparṇavanāni ca</li>
        <li id="R_4.048.017cd">vicinvanto harivarāḥ sītādarśanakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_4.048.018">
        <li id="R_4.048.018ab">tasyāgram adhirūḍhās te śrāntā vipulavikramāḥ</li>
        <li id="R_4.048.018cd">na paśyanti sma vaidehīṃ rāmasya mahiṣīṃ priyām</li>
    </ul>
    <ul id="R_4.048.019">
        <li id="R_4.048.019ab">te tu dṛṣṭigataṃ kṛtvā taṃ śailaṃ bahukandaram</li>
        <li id="R_4.048.019cd">avārohanta harayo vīkṣamāṇāḥ samantataḥ</li>
    </ul>
    <ul id="R_4.048.020">
        <li id="R_4.048.020ab">avaruhya tato bhūmiṃ śrāntā vigatacetasaḥ</li>
        <li id="R_4.048.020cd">sthitvā muhūrtaṃ tatrātha vṛkṣamūlam upāśritāḥ</li>
    </ul>
    <ul id="R_4.048.021">
        <li id="R_4.048.021ab">te muhūrtaṃ samāśvastāḥ kiṃ cid bhagnapariśramāḥ</li>
        <li id="R_4.048.021cd">punar evodyatāḥ kṛtsnāṃ mārgituṃ dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_4.048.022">
        <li id="R_4.048.022ab">hanumatpramukhās te tu prasthitāḥ plavagarṣabhāḥ</li>
        <li id="R_4.048.022cd">vindhyam evāditas tāvad vicerus te samantataḥ</li>
    </ul>
    <ul id="R_4.049.001">
        <li id="R_4.049.001ab">saha tārāṅgadābhyāṃ tu saṃgamya hanumān kapiḥ</li>
        <li id="R_4.049.001cd">vicinoti sma vindhyasya guhāś ca gahanāni ca</li>
    </ul>
    <ul id="R_4.049.002">
        <li id="R_4.049.002ab">siṃhaśārdūlajuṣṭāś ca guhāś ca paritas tathā</li>
        <li id="R_4.049.002cd">viṣameṣu nagendrasya mahāprasravaṇeṣu ca</li>
    </ul>
    <ul id="R_4.049.003">
        <li id="R_4.049.003ab">teṣāṃ tatraiva vasatāṃ sa kālo vyatyavartata</li>
    </ul>
    <ul id="R_4.049.004">
        <li id="R_4.049.004ab">sa hi deśo duranveṣo guhā gahanavān mahān</li>
        <li id="R_4.049.004cd">tatra vāyusutaḥ sarvaṃ vicinoti sma parvatam</li>
    </ul>
    <ul id="R_4.049.005">
        <li id="R_4.049.005ab">paraspareṇa rahitā anyonyasyāvidūrataḥ</li>
        <li id="R_4.049.005cd">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
    </ul>
    <ul id="R_4.049.006">
        <li id="R_4.049.006ab">maindaś ca dvividaś caiva hanumāñ jāmbavān api</li>
        <li id="R_4.049.006cd">aṅgado yuvarājaś ca tāraś ca vanagocaraḥ</li>
    </ul>
    <ul id="R_4.049.007">
        <li id="R_4.049.007ab">girijālāvṛtān deśān mārgitvā dakṣiṇāṃ diśam</li>
        <li id="R_4.049.007cd">kṣutpipāsā parītāś ca śrāntāś ca salilārthinaḥ</li>
        <li id="R_4.049.007ef">avakīrṇaṃ latāvṛkṣair dadṛśus te mahābilam</li>
    </ul>
    <ul id="R_4.049.008">
        <li id="R_4.049.008ab">tataḥ krauñcāś ca haṃsāś ca sārasāś cāpi niṣkraman</li>
        <li id="R_4.049.008cd">jalārdrāś cakravākāś ca raktāṅgāḥ padmareṇubhiḥ</li>
    </ul>
    <ul id="R_4.049.009">
        <li id="R_4.049.009ab">tatas tad bilam āsādya sugandhi duratikramam</li>
        <li id="R_4.049.009cd">vismayavyagramanaso babhūvur vānararṣabhāḥ</li>
    </ul>
    <ul id="R_4.049.010">
        <li id="R_4.049.010ab">saṃjātapariśaṅkās te tad bilaṃ plavagottamāḥ</li>
        <li id="R_4.049.010cd">abhyapadyanta saṃhṛṣṭās tejovanto mahābalāḥ</li>
    </ul>
    <ul id="R_4.049.011">
        <li id="R_4.049.011ab">tataḥ parvatakūṭābho hanumān mārutātmajaḥ</li>
        <li id="R_4.049.011cd">abravīd vānarān sarvān kāntāra vanakovidaḥ</li>
    </ul>
    <ul id="R_4.049.012">
        <li id="R_4.049.012ab">girijālāvṛtān deśān mārgitvā dakṣiṇāṃ diśam</li>
        <li id="R_4.049.012cd">vayaṃ sarve pariśrāntā na ca paśyāmi maithilīm</li>
    </ul>
    <ul id="R_4.049.013">
        <li id="R_4.049.013ab">asmāc cāpi bilād dhaṃsāḥ krauñcāś ca saha sārasaiḥ</li>
        <li id="R_4.049.013cd">jalārdrāś cakravākāś ca niṣpatanti sma sarvaśaḥ</li>
    </ul>
    <ul id="R_4.049.014">
        <li id="R_4.049.014ab">nūnaṃ salilavān atra kūpo vā yadi vā hradaḥ</li>
        <li id="R_4.049.014cd">tathā ceme biladvāre snigdhās tiṣṭhanti pādapāḥ</li>
    </ul>
    <ul id="R_4.049.015">
        <li id="R_4.049.015ab">ity uktās tad bilaṃ sarve viviśus timirāvṛtam</li>
        <li id="R_4.049.015cd">acandrasūryaṃ harayo dadṛśū romaharṣaṇam</li>
    </ul>
    <ul id="R_4.049.016">
        <li id="R_4.049.016ab">tatas tasmin bile durge nānāpādapasaṃkule</li>
        <li id="R_4.049.016cd">anyonyaṃ saṃpariṣvajya jagmur yojanam antaram</li>
    </ul>
    <ul id="R_4.049.017">
        <li id="R_4.049.017ab">te naṣṭasaṃjñās tṛṣitāḥ saṃbhrāntāḥ salilārthinaḥ</li>
        <li id="R_4.049.017cd">paripetur bile tasmin kaṃ cit kālam atandritāḥ</li>
    </ul>
    <ul id="R_4.049.018">
        <li id="R_4.049.018ab">te kṛśā dīnavadanāḥ pariśrāntāḥ plavaṃgamāḥ</li>
        <li id="R_4.049.018cd">ālokaṃ dadṛśur vīrā nirāśā jīvite tadā</li>
    </ul>
    <ul id="R_4.049.019">
        <li id="R_4.049.019ab">tatas taṃ deśam āgamya saumyaṃ vitimiraṃ vanam</li>
        <li id="R_4.049.019cd">dadṛśuḥ kāñcanān vṛkṣān dīptavaiśvānaraprabhān</li>
    </ul>
    <ul id="R_4.049.020">
        <li id="R_4.049.020ab">sālāṃs tālāṃś ca puṃnāgān kakubhān vañjulān dhavān</li>
        <li id="R_4.049.020cd">campakān nāgavṛkṣāṃś ca karṇikārāṃś ca puṣpitān</li>
    </ul>
    <ul id="R_4.049.021">
        <li id="R_4.049.021ab">taruṇādityasaṃkāśān vaidūryamayavedikān</li>
        <li id="R_4.049.021cd">nīlavaidūryavarṇāś ca padminīḥ patagāvṛtāḥ</li>
    </ul>
    <ul id="R_4.049.022">
        <li id="R_4.049.022ab">mahadbhiḥ kāñcanair vṛkṣair vṛtaṃ bālārka saṃnibhaiḥ</li>
        <li id="R_4.049.022cd">jātarūpamayair matsyair mahadbhiś ca sakacchapaiḥ</li>
    </ul>
    <ul id="R_4.049.023">
        <li id="R_4.049.023ab">nalinīs tatra dadṛśuḥ prasannasalilāyutāḥ</li>
        <li id="R_4.049.023cd">kāñcanāni vimānāni rājatāni tathaiva ca</li>
    </ul>
    <ul id="R_4.049.024">
        <li id="R_4.049.024ab">tapanīyagavākṣāṇi muktājālāvṛtāni ca</li>
        <li id="R_4.049.024cd">haimarājatabhaumāni vaidūryamaṇimanti ca</li>
    </ul>
    <ul id="R_4.049.025">
        <li id="R_4.049.025ab">dadṛśus tatra harayo gṛhamukhyāni sarvaśaḥ</li>
        <li id="R_4.049.025cd">puṣpitān phalino vṛkṣān pravālamaṇisaṃnibhān</li>
    </ul>
    <ul id="R_4.049.026">
        <li id="R_4.049.026ab">kāñcanabhramarāṃś caiva madhūni ca samantataḥ</li>
        <li id="R_4.049.026cd">maṇikāñcanacitrāṇi śayanāny āsanāni ca</li>
    </ul>
    <ul id="R_4.049.027">
        <li id="R_4.049.027ab">mahārhāṇi ca yānāni dadṛśus te samantataḥ</li>
        <li id="R_4.049.027cd">haimarājatakāṃsyānāṃ bhājanānāṃ ca saṃcayān</li>
    </ul>
    <ul id="R_4.049.028">
        <li id="R_4.049.028ab">agarūṇāṃ ca divyānāṃ candanānāṃ ca saṃcayān</li>
        <li id="R_4.049.028cd">śucīny abhyavahāryāṇi mūlāni ca phalāni ca</li>
    </ul>
    <ul id="R_4.049.029">
        <li id="R_4.049.029ab">mahārhāṇi ca pānāni madhūni rasavanti ca</li>
        <li id="R_4.049.029cd">divyānām ambarāṇāṃ ca mahārhāṇāṃ ca saṃcayān</li>
        <li id="R_4.049.029ef">kambalānāṃ ca citrāṇām ajinānāṃ ca saṃcayān</li>
    </ul>
    <ul id="R_4.049.030">
        <li id="R_4.049.030ab">tatra tatra vicinvanto bile tatra mahāprabhāḥ</li>
        <li id="R_4.049.030cd">dadṛśur vānarāḥ śūrāḥ striyaṃ kāṃ cid adūrataḥ</li>
    </ul>
    <ul id="R_4.049.031">
        <li id="R_4.049.031ab">tāṃ dṛṣṭvā bhṛśasaṃtrastāś cīrakṛṣṇājināmbarām</li>
        <li id="R_4.049.031cd">tāpasīṃ niyatāhārāṃ jvalantīm iva tejasā</li>
    </ul>
    <ul id="R_4.049.032">
        <li id="R_4.049.032ab">
            <seg type="pāda" n="a">tato hanūmān girisaṃnikāśaḥ</seg>
            <seg type="pāda" n="b">kṛtāñjalis tām abhivādya vṛddhām</seg>
        </li>
        <li id="R_4.049.032cd">
            <seg type="pāda" n="c">papraccha kā tvaṃ bhavanaṃ bilaṃ ca</seg>
            <seg type="pāda" n="d">ratnāni cemāni vadasva kasya</seg>
        </li>
    </ul>
    <ul id="R_4.050.001">
        <li id="R_4.050.001ab">ity uktvā hanumāṃs tatra punaḥ kṛṣṇājināmbarām</li>
        <li id="R_4.050.001cd">abravīt tāṃ mahābhāgāṃ tāpasīṃ dharmacāriṇīm</li>
    </ul>
    <ul id="R_4.050.002">
        <li id="R_4.050.002ab">idaṃ praviṣṭāḥ sahasā bilaṃ timirasaṃvṛtam</li>
        <li id="R_4.050.002cd">kṣutpipāsā pariśrāntāḥ parikhinnāś ca sarvaśaḥ</li>
    </ul>
    <ul id="R_4.050.003">
        <li id="R_4.050.003ab">mahad dhiraṇyā vivaraṃ praviṣṭāḥ sma pipāsitāḥ</li>
        <li id="R_4.050.003cd">imāṃs tv evaṃ vidhān bhāvān vividhān adbhutopamān</li>
        <li id="R_4.050.003ef">dṛṣṭvā vayaṃ pravyathitāḥ saṃbhrāntā naṣṭacetasaḥ</li>
    </ul>
    <ul id="R_4.050.004">
        <li id="R_4.050.004ab">kasyeme kāñcanā vṛkṣās taruṇādityasaṃnibhāḥ</li>
        <li id="R_4.050.004cd">śucīny abhyavahāryāṇi mūlāni ca phalāni ca</li>
    </ul>
    <ul id="R_4.050.005">
        <li id="R_4.050.005ab">kāñcanāni vimānāni rājatāni gṛhāṇi ca</li>
        <li id="R_4.050.005cd">tapanīya gavākṣāṇi maṇijālāvṛtāni ca</li>
    </ul>
    <ul id="R_4.050.006">
        <li id="R_4.050.006ab">puṣpitāḥ phālavantaś ca puṇyāḥ surabhigandhinaḥ</li>
        <li id="R_4.050.006cd">ime jāmbūnadamayāḥ pādapāḥ kasya tejasā</li>
    </ul>
    <ul id="R_4.050.007">
        <li id="R_4.050.007ab">kāñcanāni ca padmāni jātāni vimale jale</li>
        <li id="R_4.050.007cd">kathaṃ matsyāś ca sauvarṇā caranti saha kacchapaiḥ</li>
    </ul>
    <ul id="R_4.050.008">
        <li id="R_4.050.008ab">ātmānam anubhāvaṃ ca kasya caitat tapobalam</li>
        <li id="R_4.050.008cd">ajānatāṃ naḥ sarveṣāṃ sarvam ākhyātum arhasi</li>
    </ul>
    <ul id="R_4.050.009">
        <li id="R_4.050.009ab">evam uktā hanumatā tāpasī dharmacāriṇī</li>
        <li id="R_4.050.009cd">pratyuvāca hanūmantaṃ sarvabhūtahite ratā</li>
    </ul>
    <ul id="R_4.050.010">
        <li id="R_4.050.010ab">mayo nāma mahātejā māyāvī dānavarṣabhaḥ</li>
        <li id="R_4.050.010cd">tenedaṃ nirmitaṃ sarvaṃ māyayā kāñcanaṃ vanam</li>
    </ul>
    <ul id="R_4.050.011">
        <li id="R_4.050.011ab">purā dānavamukhyānāṃ viśvakarmā babhūva ha</li>
        <li id="R_4.050.011cd">yenedaṃ kāñcanaṃ divyaṃ nirmitaṃ bhavanottamam</li>
    </ul>
    <ul id="R_4.050.012">
        <li id="R_4.050.012ab">sa tu varṣasahasrāṇi tapas taptvā mahāvane</li>
        <li id="R_4.050.012cd">pitāmahād varaṃ lebhe sarvam auśasanaṃ dhanam</li>
    </ul>
    <ul id="R_4.050.013">
        <li id="R_4.050.013ab">vidhāya sarvaṃ balavān sarvakāmeśvaras tadā</li>
        <li id="R_4.050.013cd">uvāsa sukhitaḥ kālaṃ kaṃ cid asmin mahāvane</li>
    </ul>
    <ul id="R_4.050.014">
        <li id="R_4.050.014ab">tam apsarasi hemāyāṃ saktaṃ dānavapuṃgavam</li>
        <li id="R_4.050.014cd">vikramyaivāśaniṃ gṛhya jaghāneśaḥ puraṃdaraḥ</li>
    </ul>
    <ul id="R_4.050.015">
        <li id="R_4.050.015ab">idaṃ ca brahmaṇā dattaṃ hemāyai vanam uttamam</li>
        <li id="R_4.050.015cd">śāśvataḥ kāmabhogaś ca gṛhaṃ cedaṃ hiraṇmayam</li>
    </ul>
    <ul id="R_4.050.016">
        <li id="R_4.050.016ab">duhitā merusāvarṇer ahaṃ tasyāḥ svayaṃ prabhā</li>
        <li id="R_4.050.016cd">idaṃ rakṣāmi bhavanaṃ hemāyā vānarottama</li>
    </ul>
    <ul id="R_4.050.017">
        <li id="R_4.050.017ab">mama priyasakhī hemā nṛttagītaviśāradā</li>
        <li id="R_4.050.017cd">tayā dattavarā cāsmi rakṣāmi bhavanottamam</li>
    </ul>
    <ul id="R_4.050.018">
        <li id="R_4.050.018ab">kiṃ kāryaṃ kasya vā hetoḥ kāntārāṇi prapadyatha</li>
        <li id="R_4.050.018cd">kathaṃ cedaṃ vanaṃ durgaṃ yuṣmābhir upalakṣitam</li>
    </ul>
    <ul id="R_4.050.019">
        <li id="R_4.050.019ab">imāny abhyavahāryāṇi mūlāni ca phalāni ca</li>
        <li id="R_4.050.019cd">bhuktvā pītvā ca pānīyaṃ sarvaṃ me vaktum arhatha</li>
    </ul>
    <ul id="R_4.051.001">
        <li id="R_4.051.001ab">atha tān abravīt sarvān viśrāntān hariyūthapān</li>
        <li id="R_4.051.001cd">idaṃ vacanam ekāgrā tāpasī dharmacāriṇī</li>
    </ul>
    <ul id="R_4.051.002">
        <li id="R_4.051.002ab">vānarā yadi vaḥ khedaḥ pranaṣṭaḥ phalabhakṣaṇāt</li>
        <li id="R_4.051.002cd">yadi caitan mayā śrāvyaṃ śrotum icchāmi kathyatām</li>
    </ul>
    <ul id="R_4.051.003">
        <li id="R_4.051.003ab">tasyās tad vacanaṃ śrutvā hanumān mārutātmajaḥ</li>
        <li id="R_4.051.003cd">ārjavena yathātattvam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_4.051.004">
        <li id="R_4.051.004ab">rājā sarvasya lokasya mahendravaruṇopamaḥ</li>
        <li id="R_4.051.004cd">rāmo dāśarathiḥ śrīmān praviṣṭo daṇḍakāvanam</li>
    </ul>
    <ul id="R_4.051.005">
        <li id="R_4.051.005ab">lakṣmaṇena saha bhrātrā vaidehyā cāpi bhāryayā</li>
        <li id="R_4.051.005cd">tasya bhāryā janasthānād rāvaṇena hṛtā balāt</li>
    </ul>
    <ul id="R_4.051.006">
        <li id="R_4.051.006ab">vīras tasya sakhā rājñaḥ sugrīvo nāma vānaraḥ</li>
        <li id="R_4.051.006cd">rājā vānaramukhyānāṃ yena prasthāpitā vayam</li>
    </ul>
    <ul id="R_4.051.007">
        <li id="R_4.051.007ab">agastyacaritām āśāṃ dakṣiṇāṃ yamarakṣitām</li>
        <li id="R_4.051.007cd">sahaibhir vānarair mukhyair aṅgadapramukhair vayam</li>
    </ul>
    <ul id="R_4.051.008">
        <li id="R_4.051.008ab">rāvaṇaṃ sahitāḥ sarve rākṣasaṃ kāmarūpiṇam</li>
        <li id="R_4.051.008cd">sītayā saha vaidehyā mārgadhvam iti coditāḥ</li>
    </ul>
    <ul id="R_4.051.009">
        <li id="R_4.051.009ab">vicitya tu vayaṃ sarve samagrāṃ dakṣiṇāṃ diśam</li>
        <li id="R_4.051.009cd">bubhukṣitāḥ pariśrāntā vṛkṣamūlam upāśritāḥ</li>
    </ul>
    <ul id="R_4.051.010">
        <li id="R_4.051.010ab">vivarṇavadanāḥ sarve sarve dhyānaparāyaṇāḥ</li>
        <li id="R_4.051.010cd">nādhigacchāmahe pāraṃ magnāś cintāmahārṇave</li>
    </ul>
    <ul id="R_4.051.011">
        <li id="R_4.051.011ab">cārayantas tataś cakṣur dṛṣṭavanto mahad bilam</li>
        <li id="R_4.051.011cd">latāpādapasaṃchannaṃ timireṇa samāvṛtam</li>
    </ul>
    <ul id="R_4.051.012">
        <li id="R_4.051.012ab">asmād dhaṃsā jalaklinnāḥ pakṣaiḥ salilareṇubhiḥ</li>
        <li id="R_4.051.012cd">kurarāḥ sārasāś caiva niṣpatanti patatriṇaḥ</li>
        <li id="R_4.051.012ef">sādhv atra praviśāmeti mayā tūktāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_4.051.013">
        <li id="R_4.051.013ab">teṣām api hi sarveṣām anumānam upāgatam</li>
        <li id="R_4.051.013cd">gacchāmaḥ praviśāmeti bhartṛkāryatvarānvitāḥ</li>
    </ul>
    <ul id="R_4.051.014">
        <li id="R_4.051.014ab">tato gāḍhaṃ nipatitā gṛhya hastau parasparam</li>
        <li id="R_4.051.014cd">idaṃ praviṣṭāḥ sahasā bilaṃ timirasaṃvṛtam</li>
    </ul>
    <ul id="R_4.051.015">
        <li id="R_4.051.015ab">etan naḥ kāyam etena kṛtyena vayam āgatāḥ</li>
        <li id="R_4.051.015cd">tvāṃ caivopagatāḥ sarve paridyūnā bubhukṣitāḥ</li>
    </ul>
    <ul id="R_4.051.016">
        <li id="R_4.051.016ab">ātithyadharmadattāni mūlāni ca phalāni ca</li>
        <li id="R_4.051.016cd">asmābhir upabhuktāni bubhukṣāparipīḍitaiḥ</li>
    </ul>
    <ul id="R_4.051.017">
        <li id="R_4.051.017ab">yat tvayā rakṣitāḥ sarve mriyamāṇā bubhukṣayā</li>
        <li id="R_4.051.017cd">brūhi pratyupakārārthaṃ kiṃ te kurvantu vānarāḥ</li>
    </ul>
    <ul id="R_4.051.018">
        <li id="R_4.051.018ab">evam uktā tu sarvajñā vānarais taiḥ svayaṃprabhā</li>
        <li id="R_4.051.018cd">pratyuvāca tataḥ sarvān idaṃ vānarayūthapam</li>
    </ul>
    <ul id="R_4.051.019">
        <li id="R_4.051.019ab">sarveṣāṃ parituṣṭāsmi vānarāṇāṃ tarasvinām</li>
        <li id="R_4.051.019cd">carantyā mama dharmeṇa na kāryam iha kena cit</li>
    </ul>
    <ul id="R_4.052.001">
        <li id="R_4.052.001ab">evam uktaḥ śubhaṃ vākyaṃ tāpasyā dharmasaṃhitam</li>
        <li id="R_4.052.001cd">uvāca hanumān vākyaṃ tām aninditaceṣṭitām</li>
    </ul>
    <ul id="R_4.052.002">
        <li id="R_4.052.002ab">śaraṇaṃ tvāṃ prapannāḥ smaḥ sarve vai dharmacāriṇi</li>
        <li id="R_4.052.002cd">yaḥ kṛtaḥ samayo 'smākaṃ sugrīveṇa mahātmanā</li>
        <li id="R_4.052.002ef">sa tu kālo vyatikrānto bile ca parivartatām</li>
    </ul>
    <ul id="R_4.052.003">
        <li id="R_4.052.003ab">sā tvam asmād bilād ghorād uttārayitum arhasi</li>
    </ul>
    <ul id="R_4.052.004">
        <li id="R_4.052.004ab">tasmāt sugrīvavacanād atikrāntān gatāyuṣaḥ</li>
        <li id="R_4.052.004cd">trātum arhasi naḥ sarvān sugrīvabhayaśaṅkitān</li>
    </ul>
    <ul id="R_4.052.005">
        <li id="R_4.052.005ab">mahac ca kāryam asmābhiḥ kartavyaṃ dharmacāriṇi</li>
        <li id="R_4.052.005cd">tac cāpi na kṛtaṃ kāryam asmābhir iha vāsibhiḥ</li>
    </ul>
    <ul id="R_4.052.006">
        <li id="R_4.052.006ab">evam uktā hanumatā tāpasī vākyam abravīt</li>
        <li id="R_4.052.006cd">jīvatā duṣkaraṃ manye praviṣṭena nivartitum</li>
    </ul>
    <ul id="R_4.052.007">
        <li id="R_4.052.007ab">tapasas tu prabhāvena niyamopārjitena ca</li>
        <li id="R_4.052.007cd">sarvān eva bilād asmād uddhariṣyāmi vānarān</li>
    </ul>
    <ul id="R_4.052.008">
        <li id="R_4.052.008ab">nimīlayata cakṣūṃṣi sarve vānarapuṃgavāḥ</li>
        <li id="R_4.052.008cd">na hi niṣkramituṃ śakyam animīlitalocanaiḥ</li>
    </ul>
    <ul id="R_4.052.009">
        <li id="R_4.052.009ab">tataḥ saṃmīlitāḥ sarve sukumārāṅgulaiḥ karaiḥ</li>
        <li id="R_4.052.009cd">sahasā pidadhur dṛṣṭiṃ hṛṣṭā gamanakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_4.052.010">
        <li id="R_4.052.010ab">vānarās tu mahātmāno hastaruddhamukhās tadā</li>
        <li id="R_4.052.010cd">nimeṣāntaramātreṇa bilād uttāritās tayā</li>
    </ul>
    <ul id="R_4.052.011">
        <li id="R_4.052.011ab">tatas tān vānarān sarvāṃs tāpasī dharmacāriṇī</li>
        <li id="R_4.052.011cd">niḥsṛtān viṣamāt tasmāt samāśvāsyedam abravīt</li>
    </ul>
    <ul id="R_4.052.012">
        <li id="R_4.052.012ab">eṣa vindhyo giriḥ śrīmān nānādrumalatāyutaḥ</li>
        <li id="R_4.052.012cd">eṣa prasavaṇaḥ śailaḥ sāgaro 'yaṃ mahodadhiḥ</li>
    </ul>
    <ul id="R_4.052.013">
        <li id="R_4.052.013ab">svasti vo 'stu gamiṣyāmi bhavanaṃ vānararṣabhāḥ</li>
        <li id="R_4.052.013cd">ity uktvā tad bilaṃ śrīmat praviveśa svayaṃprabhā</li>
    </ul>
    <ul id="R_4.052.014">
        <li id="R_4.052.014ab">tatas te dadṛśur ghoraṃ sāgaraṃ varuṇālayam</li>
        <li id="R_4.052.014cd">apāram abhigarjantaṃ ghorair ūrmibhir ākulam</li>
    </ul>
    <ul id="R_4.052.015">
        <li id="R_4.052.015ab">mayasya māyā vihitaṃ giridurgaṃ vicinvatām</li>
        <li id="R_4.052.015cd">teṣāṃ māso vyatikrānto yo rājñā samayaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_4.052.016">
        <li id="R_4.052.016ab">vindhyasya tu gireḥ pāde saṃprapuṣpitapādape</li>
        <li id="R_4.052.016cd">upaviśya mahābhāgāś cintām āpedire tadā</li>
    </ul>
    <ul id="R_4.052.017">
        <li id="R_4.052.017ab">tataḥ puṣpātibhārāgrāṃl latāśatasamāvṛtān</li>
        <li id="R_4.052.017cd">drumān vāsantikān dṛṣṭvā babhūvur bhayaśaṅkitāḥ</li>
    </ul>
    <ul id="R_4.052.018">
        <li id="R_4.052.018ab">te vasantam anuprāptaṃ prativedya parasparam</li>
        <li id="R_4.052.018cd">naṣṭasaṃdeśakālārthā nipetur dharaṇītale</li>
    </ul>
    <ul id="R_4.052.019">
        <li id="R_4.052.019ab">sa tu siṃharṣabha skandhaḥ pīnāyatabhujaḥ kapiḥ</li>
        <li id="R_4.052.019cd">yuvarājo mahāprājña aṅgado vākyam abravīt</li>
    </ul>
    <ul id="R_4.052.020">
        <li id="R_4.052.020ab">śāsanāt kapirājasya vayaṃ sarve vinirgatāḥ</li>
        <li id="R_4.052.020cd">māsaḥ pūrṇo bilasthānāṃ harayaḥ kiṃ na budhyate</li>
    </ul>
    <ul id="R_4.052.021">
        <li id="R_4.052.021ab">tasminn atīte kāle tu sugrīveṇa kṛte svayam</li>
        <li id="R_4.052.021cd">prāyopaveśanaṃ yuktaṃ sarveṣāṃ ca vanaukasām</li>
    </ul>
    <ul id="R_4.052.022">
        <li id="R_4.052.022ab">tīkṣṇaḥ prakṛtyā sugrīvaḥ svāmibhāve vyavasthitaḥ</li>
        <li id="R_4.052.022cd">na kṣamiṣyati naḥ sarvān aparādhakṛto gatān</li>
    </ul>
    <ul id="R_4.052.023">
        <li id="R_4.052.023ab">apravṛttau ca sītāyāḥ pāpam eva kariṣyati</li>
        <li id="R_4.052.023cd">tasmāt kṣamam ihādyaiva prāyopaviśanaṃ hi naḥ</li>
    </ul>
    <ul id="R_4.052.024">
        <li id="R_4.052.024ab">tyaktvā putrāṃś ca dārāṃś ca dhanāni ca gṛhāṇi ca</li>
        <li id="R_4.052.024cd">yāvan na ghātayed rājā sarvān pratigatān itaḥ</li>
        <li id="R_4.052.024ef">vadhenāpratirūpeṇa śreyān mṛtyur ihaiva naḥ</li>
    </ul>
    <ul id="R_4.052.025">
        <li id="R_4.052.025ab">na cāhaṃ yauvarājyena sugrīveṇābhiṣecitaḥ</li>
        <li id="R_4.052.025cd">narendreṇābhiṣikto 'smi rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_4.052.026">
        <li id="R_4.052.026ab">sa pūrvaṃ baddhavairo māṃ rājā dṛṣṭvā vyatikramam</li>
        <li id="R_4.052.026cd">ghātayiṣyati daṇḍena tīkṣṇena kṛtaniścayaḥ</li>
    </ul>
    <ul id="R_4.052.027">
        <li id="R_4.052.027ab">kiṃ me suhṛdbhir vyasanaṃ paśyadbhir jīvitāntare</li>
        <li id="R_4.052.027cd">ihaiva prāyam āsiṣye puṇye sāgararodhasi</li>
    </ul>
    <ul id="R_4.052.028">
        <li id="R_4.052.028ab">etac chrutvā kumāreṇa yuvarājena bhāṣitam</li>
        <li id="R_4.052.028cd">sarve te vānaraśreṣṭhāḥ karuṇaṃ vākyam abruvan</li>
    </ul>
    <ul id="R_4.052.029">
        <li id="R_4.052.029ab">tīkṣṇaḥ prakṛtyā sugrīvaḥ priyāsaktaś ca rāghavaḥ</li>
        <li id="R_4.052.029cd">adṛṣṭāyāṃ ca vaidehyāṃ dṛṣṭvāsmāṃś ca samāgatān</li>
    </ul>
    <ul id="R_4.052.030">
        <li id="R_4.052.030ab">rāghavapriyakāmārthaṃ ghātayiṣyaty asaṃśayam</li>
        <li id="R_4.052.030cd">na kṣamaṃ cāparāddhānāṃ gamanaṃ svāmipārśvataḥ</li>
    </ul>
    <ul id="R_4.052.031">
        <li id="R_4.052.031ab">
            <seg type="pāda" n="a">plavaṃgamānāṃ tu bhayārditānāṃ</seg>
            <seg type="pāda" n="b">śrutvā vacas tāra idaṃ babhāṣe</seg>
        </li>
        <li id="R_4.052.031cd">
            <seg type="pāda" n="c">alaṃ viṣādena bilaṃ praviśya</seg>
            <seg type="pāda" n="d">vasāma sarve yadi rocate vaḥ</seg>
        </li>
    </ul>
    <ul id="R_4.052.032">
        <li id="R_4.052.032ab">
            <seg type="pāda" n="a">idaṃ hi māyā vihitaṃ sudurgamaṃ</seg>
            <seg type="pāda" n="b">prabhūtavṛkṣodakabhojyapeyam</seg>
        </li>
        <li id="R_4.052.032cd">
            <seg type="pāda" n="c">ihāsti no naiva bhayaṃ puraṃdarān</seg>
            <seg type="pāda" n="d">na rāghavād vānararājato 'pi vā</seg>
        </li>
    </ul>
    <ul id="R_4.052.033">
        <li id="R_4.052.033ab">
            <seg type="pāda" n="a">śrutvāṅgadasyāpi vaco 'nukūlam</seg>
            <seg type="pāda" n="b">ūcuś ca sarve harayaḥ pratītāḥ</seg>
        </li>
        <li id="R_4.052.033cd">
            <seg type="pāda" n="c">yathā na hanyema tathāvidhānam</seg>
            <seg type="pāda" n="d">asaktam adyaiva vidhīyatāṃ naḥ</seg>
        </li>
    </ul>
    <ul id="R_4.053.001">
        <li id="R_4.053.001ab">tathā bruvati tāre tu tārādhipativarcasi</li>
        <li id="R_4.053.001cd">atha mene hṛtaṃ rājyaṃ hanumān aṅgadena tat</li>
    </ul>
    <ul id="R_4.053.002">
        <li id="R_4.053.002ab">buddhyā hy aṣṭāṅgayā yuktaṃ caturbalasamanvitam</li>
        <li id="R_4.053.002cd">caturdaśaguṇaṃ mene hanumān vālinaḥ sutam</li>
    </ul>
    <ul id="R_4.053.003">
        <li id="R_4.053.003ab">āpūryamāṇaṃ śaśvac ca tejobalaparākramaiḥ</li>
        <li id="R_4.053.003cd">śaśinaṃ śuklapakṣādau vardhamānam iva śriyā</li>
    </ul>
    <ul id="R_4.053.004">
        <li id="R_4.053.004ab">bṛhaspatisamaṃ buddhyā vikrame sadṛśaṃ pituḥ</li>
        <li id="R_4.053.004cd">śuśrūṣamāṇaṃ tārasya śukrasyeva puraṃdaram</li>
    </ul>
    <ul id="R_4.053.005">
        <li id="R_4.053.005ab">bhartur arthe pariśrāntaṃ sarvaśāstraviśāradam</li>
        <li id="R_4.053.005cd">abhisaṃdhātum ārebhe hanumān aṅgadaṃ tataḥ</li>
    </ul>
    <ul id="R_4.053.006">
        <li id="R_4.053.006ab">sa caturṇām upāyānāṃ tṛtīyam upavarṇayan</li>
        <li id="R_4.053.006cd">bhedayām āsa tān sarvān vānarān vākyasaṃpadā</li>
    </ul>
    <ul id="R_4.053.007">
        <li id="R_4.053.007ab">teṣu sarveṣu bhinneṣu tato 'bhīṣayad aṅgadam</li>
        <li id="R_4.053.007cd">bhīṣaṇair bahubhir vākyaiḥ kopopāyasamanvitaiḥ</li>
    </ul>
    <ul id="R_4.053.008">
        <li id="R_4.053.008ab">tvaṃ samarthataraḥ pitrā yuddhe tāreya vai dhuram</li>
        <li id="R_4.053.008cd">dṛḍhaṃ dhārayituṃ śaktaḥ kapirājyaṃ yathā pitā</li>
    </ul>
    <ul id="R_4.053.009">
        <li id="R_4.053.009ab">nityam asthiracittā hi kapayo haripuṃgava</li>
        <li id="R_4.053.009cd">nājñāpyaṃ viṣahiṣyanti putradārān vinā tvayā</li>
    </ul>
    <ul id="R_4.053.010">
        <li id="R_4.053.010ab">tvāṃ naite hy anuyuñjeyuḥ pratyakṣaṃ pravadāmi te</li>
        <li id="R_4.053.010cd">yathāyaṃ jāmbavān nīlaḥ suhotraś ca mahākapiḥ</li>
    </ul>
    <ul id="R_4.053.011">
        <li id="R_4.053.011ab">na hy ahaṃ ta ime sarve sāmadānādibhir guṇaiḥ</li>
        <li id="R_4.053.011cd">daṇḍena na tvayā śakyāḥ sugrīvād apakarṣitum</li>
    </ul>
    <ul id="R_4.053.012">
        <li id="R_4.053.012ab">vigṛhyāsanam apy āhur durbalena balīyasaḥ</li>
        <li id="R_4.053.012cd">ātmarakṣākaras tasmān na vigṛhṇīta durbalaḥ</li>
    </ul>
    <ul id="R_4.053.013">
        <li id="R_4.053.013ab">yāṃ cemāṃ manyase dhātrīm etad bilam iti śrutam</li>
        <li id="R_4.053.013cd">etal lakṣmaṇabāṇānām īṣatkāryaṃ vidāraṇe</li>
    </ul>
    <ul id="R_4.053.014">
        <li id="R_4.053.014ab">svalpaṃ hi kṛtam indreṇa kṣipatā hy aśaniṃ purā</li>
        <li id="R_4.053.014cd">lakṣmaṇo niśitair bāṇair bhindyāt patrapuṭaṃ yathā</li>
        <li id="R_4.053.014ef">lakṣmaṇasya ca nārācā bahavaḥ santi tadvidhāḥ</li>
    </ul>
    <ul id="R_4.053.015">
        <li id="R_4.053.015ab">avasthāne yadaiva tvam āsiṣyasi paraṃtapa</li>
        <li id="R_4.053.015cd">tadaiva harayaḥ sarve tyakṣyanti kṛtaniścayāḥ</li>
    </ul>
    <ul id="R_4.053.016">
        <li id="R_4.053.016ab">smarantaḥ putradārāṇāṃ nityodvignā bubhukṣitāḥ</li>
        <li id="R_4.053.016cd">kheditā duḥkhaśayyābhis tvāṃ kariṣyanti pṛṣṭhataḥ</li>
    </ul>
    <ul id="R_4.053.017">
        <li id="R_4.053.017ab">sa tvaṃ hīnaḥ suhṛdbhiś ca hitakāmaiś ca bandhubhiḥ</li>
        <li id="R_4.053.017cd">tṛṇād api bhṛśodvignaḥ spandamānād bhaviṣyasi</li>
    </ul>
    <ul id="R_4.053.018">
        <li id="R_4.053.018ab">na ca jātu na hiṃsyus tvāṃ ghorā lakṣmaṇasāyakāḥ</li>
        <li id="R_4.053.018cd">apavṛttaṃ jighāṃsanto mahāvegā durāsadāḥ</li>
    </ul>
    <ul id="R_4.053.019">
        <li id="R_4.053.019ab">asmābhis tu gataṃ sārdhaṃ vinītavad upasthitam</li>
        <li id="R_4.053.019cd">ānupūrvyāt tu sugrīvo rājye tvāṃ sthāpayiṣyati</li>
    </ul>
    <ul id="R_4.053.020">
        <li id="R_4.053.020ab">dharmakāmaḥ pitṛvyas te prītikāmo dṛḍhavrataḥ</li>
        <li id="R_4.053.020cd">śuciḥ satyapratijñaś ca nā tvāṃ jātu jighāṃsati</li>
    </ul>
    <ul id="R_4.053.021">
        <li id="R_4.053.021ab">priyakāmaś ca te mātus tadarthaṃ cāsya jīvitam</li>
        <li id="R_4.053.021cd">tasyāpatyaṃ ca nāsty anyat tasmād aṅgada gamyatām</li>
    </ul>
    <ul id="R_4.054.001">
        <li id="R_4.054.001ab">śrutvā hanumato vākyaṃ praśritaṃ dharmasaṃhitam</li>
        <li id="R_4.054.001cd">svāmisatkārasaṃyuktam aṅgado vākyam abravīt</li>
    </ul>
    <ul id="R_4.054.002">
        <li id="R_4.054.002ab">sthairyaṃ sarvātmanā śaucam ānṛśaṃsyam athārjavam</li>
        <li id="R_4.054.002cd">vikramaiś caiva dhairyaṃ ca sugrīve nopapadyate</li>
    </ul>
    <ul id="R_4.054.003">
        <li id="R_4.054.003ab">bhrātur jyeṣṭhasya yo bhāryāṃ jīvito mahiṣīṃ priyām</li>
        <li id="R_4.054.003cd">dharmeṇa mātaraṃ yas tu svīkaroti jugupsitaḥ</li>
    </ul>
    <ul id="R_4.054.004">
        <li id="R_4.054.004ab">kathaṃ sa dharmaṃ jānīte yena bhrātrā durātmanā</li>
        <li id="R_4.054.004cd">yuddhāyābhiniyuktena bilasya pihitaṃ mukham</li>
    </ul>
    <ul id="R_4.054.005">
        <li id="R_4.054.005ab">satyāt pāṇigṛhītaś ca kṛtakarmā mahāyaśāḥ</li>
        <li id="R_4.054.005cd">vismṛto rāghavo yena sa kasya sukṛtaṃ smaret</li>
    </ul>
    <ul id="R_4.054.006">
        <li id="R_4.054.006ab">lakṣmaṇasya bhayād yena nādharmabhayabhīruṇā</li>
        <li id="R_4.054.006cd">ādiṣṭā mārgituṃ sītāṃ dharmam asmin kathaṃ bhavet</li>
    </ul>
    <ul id="R_4.054.007">
        <li id="R_4.054.007ab">tasmin pāpe kṛtaghne tu smṛtihīne calātmani</li>
        <li id="R_4.054.007cd">āryaḥ ko viśvasej jātu tat kulīno jijīviṣuḥ</li>
    </ul>
    <ul id="R_4.054.008">
        <li id="R_4.054.008ab">rājye putraṃ pratiṣṭhāpya saguṇo nirguṇo 'pi vā</li>
        <li id="R_4.054.008cd">kathaṃ śatrukulīnaṃ māṃ sugrīvo jīvayiṣyati</li>
    </ul>
    <ul id="R_4.054.009">
        <li id="R_4.054.009ab">bhinnamantro 'parāddhaś ca hīnaśaktiḥ kathaṃ hy aham</li>
        <li id="R_4.054.009cd">kiṣkindhāṃ prāpya jīveyam anātha iva durbalaḥ</li>
    </ul>
    <ul id="R_4.054.010">
        <li id="R_4.054.010ab">upāṃśudaṇḍena hi māṃ bandhanenopapādayet</li>
        <li id="R_4.054.010cd">śaṭhaḥ krūro nṛśaṃsaś ca sugrīvo rājyakāraṇāt</li>
    </ul>
    <ul id="R_4.054.011">
        <li id="R_4.054.011ab">bandhanāc cāvasādān me śreyaḥ prāyopaveśanam</li>
        <li id="R_4.054.011cd">anujānīta māṃ sarve gṛhān gacchantu vānarāḥ</li>
    </ul>
    <ul id="R_4.054.012">
        <li id="R_4.054.012ab">ahaṃ vaḥ pratijānāmi na gamiṣyāmy ahaṃ purīm</li>
        <li id="R_4.054.012cd">ihaiva prāyam āsiṣye śreyo maraṇam eva me</li>
    </ul>
    <ul id="R_4.054.013">
        <li id="R_4.054.013ab">abhivādanapūrvaṃ tu rājā kuśalam eva ca</li>
        <li id="R_4.054.013cd">vācyas tato yavīyān me sugrīvo vānareśvaraḥ</li>
    </ul>
    <ul id="R_4.054.014">
        <li id="R_4.054.014ab">ārogyapūrvaṃ kuśalaṃ vācyā mātā rumā ca me</li>
        <li id="R_4.054.014cd">mātaraṃ caiva me tārām āśvāsayitum arhatha</li>
    </ul>
    <ul id="R_4.054.015">
        <li id="R_4.054.015ab">prakṛtyā priyaputrā sā sānukrośā tapasvinī</li>
        <li id="R_4.054.015cd">vinaṣṭaṃ mām iha śrutvā vyaktaṃ hāsyati jīvitam</li>
    </ul>
    <ul id="R_4.054.016">
        <li id="R_4.054.016ab">etāvad uktvā vacanaṃ vṛddhān apy abhivādya ca</li>
        <li id="R_4.054.016cd">saṃviveśāṅgado bhūmau rudan darbheṣu durmanāḥ</li>
    </ul>
    <ul id="R_4.054.017">
        <li id="R_4.054.017ab">tasya saṃviśatas tatra rudanto vānararṣabhāḥ</li>
        <li id="R_4.054.017cd">nayanebhyaḥ pramumucur uṣṇaṃ vai vāriduḥkhitāḥ</li>
    </ul>
    <ul id="R_4.054.018">
        <li id="R_4.054.018ab">sugrīvaṃ caiva nindantaḥ praśaṃsantaś ca vālinam</li>
        <li id="R_4.054.018cd">parivāryāṅgado sarve vyavasyan prāyam āsitum</li>
    </ul>
    <ul id="R_4.054.019">
        <li id="R_4.054.019ab">mataṃ tad vāliputrasya vijñāya plavagarṣabhāḥ</li>
        <li id="R_4.054.019cd">upaspṛśyodakaṃ sarve prāṅmukhāḥ samupāviśan</li>
        <li id="R_4.054.019ef">dakṣiṇāgreṣu darbheṣu udaktīraṃ samāśritāḥ</li>
    </ul>
    <ul id="R_4.054.020">
        <li id="R_4.054.020ab">
            <seg type="pāda" n="a">sa saṃviśadbhir bahubhir mahīdharo</seg>
            <seg type="pāda" n="b">mahādrikūṭapramitaiḥ plavaṃgamaiḥ</seg>
        </li>
    </ul>
    <ul id="R_4.054.021">
        <li id="R_4.054.021cd">
            <seg type="pāda" n="c">babhūva saṃnāditanirjharāntaro</seg>
            <seg type="pāda" n="d">bhṛśaṃ nadadbhir jaladair ivolbaṇaiḥ</seg>
        </li>
    </ul>
    <ul id="R_4.055.001">
        <li id="R_4.055.001ab">upaviṣṭās tu te sarve yasmin prāyaṃ giristhale</li>
        <li id="R_4.055.001cd">harayo gṛdhrarājaś ca taṃ deśam upacakrame</li>
    </ul>
    <ul id="R_4.055.002">
        <li id="R_4.055.002ab">sāmpātir nāma nāmnā tu cirajīvī vihaṃgamaḥ</li>
        <li id="R_4.055.002cd">bhrātā jaṭāyuṣaḥ śrīmān prakhyātabalapauruṣaḥ</li>
    </ul>
    <ul id="R_4.055.003">
        <li id="R_4.055.003ab">kandarād abhiniṣkramya sa vindhyasya mahāgireḥ</li>
        <li id="R_4.055.003cd">upaviṣṭān harīn dṛṣṭvā hṛṣṭātmā giram abravīt</li>
    </ul>
    <ul id="R_4.055.004">
        <li id="R_4.055.004ab">vidhiḥ kila naraṃ loke vidhānenānuvartate</li>
        <li id="R_4.055.004cd">yathāyaṃ vihito bhakṣyaś cirān mahyam upāgataḥ</li>
    </ul>
    <ul id="R_4.055.005">
        <li id="R_4.055.005ab">paramparāṇāṃ bhakṣiṣye vānarāṇāṃ mṛtaṃ mṛtam</li>
        <li id="R_4.055.005cd">uvācaivaṃ vacaḥ pakṣī tān nirīkṣya plavaṃgamān</li>
    </ul>
    <ul id="R_4.055.006">
        <li id="R_4.055.006ab">tasya tad vacanaṃ śrutvā bhakṣalubdhasya pakṣiṇaḥ</li>
        <li id="R_4.055.006cd">aṅgadaḥ param āyasto hanūmantam athābravīt</li>
    </ul>
    <ul id="R_4.055.007">
        <li id="R_4.055.007ab">paśya sītāpadeśena sākṣād vaivasvato yamaḥ</li>
        <li id="R_4.055.007cd">imaṃ deśam anuprāpto vānarāṇāṃ vipattaye</li>
    </ul>
    <ul id="R_4.055.008">
        <li id="R_4.055.008ab">rāmasya na kṛtaṃ kāryaṃ rājño na ca vacaḥ kṛtam</li>
        <li id="R_4.055.008cd">harīṇām iyam ajñātā vipattiḥ sahasāgatā</li>
    </ul>
    <ul id="R_4.055.009">
        <li id="R_4.055.009ab">vaidehyāḥ priyakāmena kṛtaṃ karma jaṭāyuṣā</li>
        <li id="R_4.055.009cd">gṛdhrarājena yat tatra śrutaṃ vas tad aśeṣataḥ</li>
    </ul>
    <ul id="R_4.055.010">
        <li id="R_4.055.010ab">tathā sarvāṇi bhūtāni tiryagyonigatāny api</li>
        <li id="R_4.055.010cd">priyaṃ kurvanti rāmasya tyaktvā prāṇān yathā vayam</li>
    </ul>
    <ul id="R_4.055.011">
        <li id="R_4.055.011ab">rāghavārthe pariśrāntā vayaṃ saṃtyaktajīvitāḥ</li>
        <li id="R_4.055.011cd">kāntārāṇi prapannāḥ sma na ca paśyāma maithilīm</li>
    </ul>
    <ul id="R_4.055.012">
        <li id="R_4.055.012ab">sa sukhī gṛdhrarājas tu rāvaṇena hato raṇe</li>
        <li id="R_4.055.012cd">muktaś ca sugrīvabhayād gataś ca paramāṃ gatim</li>
    </ul>
    <ul id="R_4.055.013">
        <li id="R_4.055.013ab">jaṭāyuṣo vināśena rājño daśarathasya ca</li>
        <li id="R_4.055.013cd">haraṇena ca vaidehyāḥ saṃśayaṃ harayo gatāḥ</li>
    </ul>
    <ul id="R_4.055.014">
        <li id="R_4.055.014ab">rāmalakṣmaṇayor vāsām araṇye saha sītayā</li>
        <li id="R_4.055.014cd">rāghavasya ca bāṇena vālinaś ca tathā vadhaḥ</li>
    </ul>
    <ul id="R_4.055.015">
        <li id="R_4.055.015ab">rāmakopād aśeṣāṇāṃ rākṣasānāṃ tathā vadhaḥ</li>
        <li id="R_4.055.015cd">kaikeyyā varadānena idaṃ hi vikṛtaṃ kṛtam</li>
    </ul>
    <ul id="R_4.055.016">
        <li id="R_4.055.016ab">tat tu śrutvā tadā vākyam aṅgadasya mukhodgatam</li>
        <li id="R_4.055.016cd">abravīd vacanaṃ gṛdhras tīkṣṇatuṇḍo mahāsvanaḥ</li>
    </ul>
    <ul id="R_4.055.017">
        <li id="R_4.055.017ab">ko 'yaṃ girā ghoṣayati prāṇaiḥ priyatarasya me</li>
        <li id="R_4.055.017cd">jaṭāyuṣo vadhaṃ bhrātuḥ kampayann iva me manaḥ</li>
    </ul>
    <ul id="R_4.055.018">
        <li id="R_4.055.018ab">katham āsīj janasthāne yuddhaṃ rākṣasagṛdhrayoḥ</li>
        <li id="R_4.055.018cd">nāmadheyam idaṃ bhrātuś cirasyādya mayā śrutam</li>
    </ul>
    <ul id="R_4.055.019">
        <li id="R_4.055.019ab">yavīyaso guṇajñasya ślāghanīyasya vikramaiḥ</li>
        <li id="R_4.055.019cd">tad iccheyam ahaṃ śrotuṃ vināśaṃ vānararṣabhāḥ</li>
    </ul>
    <ul id="R_4.055.020">
        <li id="R_4.055.020ab">bhrātur jaṭāyuṣas tasya janasthānanivāsinaḥ</li>
        <li id="R_4.055.020cd">tasyaiva ca mama bhrātuḥ sakhā daśarathaḥ katham</li>
        <li id="R_4.055.020ef">yasya rāmaḥ priyaḥ putro jyeṣṭho gurujanapriyaḥ</li>
    </ul>
    <ul id="R_4.055.021">
        <li id="R_4.055.021ab">sūryāṃśudagdhapakṣatvān na śaknomi visarpitum</li>
        <li id="R_4.055.021cd">iccheyaṃ parvatād asmād avatartum ariṃdamāḥ</li>
    </ul>
    <ul id="R_4.056.001">
        <li id="R_4.056.001ab">śokād bhraṣṭasvaram api śrutvā te hariyūthapāḥ</li>
        <li id="R_4.056.001cd">śraddadhur naiva tad vākyaṃ karmaṇā tasya śaṅkitāḥ</li>
    </ul>
    <ul id="R_4.056.002">
        <li id="R_4.056.002ab">te prāyam upaviṣṭās tu dṛṣṭvā gṛdhraṃ plavaṃgamāḥ</li>
        <li id="R_4.056.002cd">cakrur buddhiṃ tadā raudrāṃ sarvān no bhakṣayiṣyati</li>
    </ul>
    <ul id="R_4.056.003">
        <li id="R_4.056.003ab">sarvathā prāyam āsīnān yadi no bhakṣayiṣyati</li>
        <li id="R_4.056.003cd">kṛtakṛtyā bhaviṣyāmaḥ kṣipraṃ siddhim ito gatāḥ</li>
    </ul>
    <ul id="R_4.056.004">
        <li id="R_4.056.004ab">etāṃ buddhiṃ tataś cakruḥ sarve te vānararṣabhāḥ</li>
        <li id="R_4.056.004cd">avatārya gireḥ śṛṅgād gṛdhram āhāṅgadas tadā</li>
    </ul>
    <ul id="R_4.056.005">
        <li id="R_4.056.005ab">babhūvur kṣarajo nāma vānarendraḥ pratāpavān</li>
        <li id="R_4.056.005cd">mamāryaḥ pārthivaḥ pakṣin dhārmikau tasya cātmajau</li>
    </ul>
    <ul id="R_4.056.006">
        <li id="R_4.056.006ab">sugrīvaś caiva valī ca putrāv oghabalāv ubhau</li>
        <li id="R_4.056.006cd">loke viśrutakarmābhūd rājā vālī pitā mama</li>
    </ul>
    <ul id="R_4.056.007">
        <li id="R_4.056.007ab">rājā kṛtsnasya jagata ikṣvākūṇāṃ mahārathaḥ</li>
        <li id="R_4.056.007cd">rāmo dāśarathiḥ śrīmān praviṣṭo daṇḍakāvanam</li>
    </ul>
    <ul id="R_4.056.008">
        <li id="R_4.056.008ab">lakṣmaṇena saha bhrātrā vaidehyā cāpi bhāryayā</li>
        <li id="R_4.056.008cd">pitur nideśanirato dharmyaṃ panthānam āśritaḥ</li>
        <li id="R_4.056.008ef">tasya bhāryā janasthānād rāvaṇena hṛtā balāt</li>
    </ul>
    <ul id="R_4.056.009">
        <li id="R_4.056.009ab">rāmasya ca pitur mitraṃ jaṭāyur nāma gṛdhrarāṭ</li>
        <li id="R_4.056.009cd">dadarśa sītāṃ vaidehīṃ hriyamāṇāṃ vihāyasā</li>
    </ul>
    <ul id="R_4.056.010">
        <li id="R_4.056.010ab">rāvaṇaṃ virathaṃ kṛtvā sthāpayitvā ca maithilīm</li>
        <li id="R_4.056.010cd">pariśrāntaś ca vṛddhaś ca rāvaṇena hato raṇe</li>
    </ul>
    <ul id="R_4.056.011">
        <li id="R_4.056.011ab">evaṃ gṛdhro hatas tena rāvaṇena bahīyasā</li>
        <li id="R_4.056.011cd">saṃskṛtaś cāpi rāmeṇa gataś ca gatim uttamām</li>
    </ul>
    <ul id="R_4.056.012">
        <li id="R_4.056.012ab">tato mama pitṛvyeṇa sugrīveṇa mahātmanā</li>
        <li id="R_4.056.012cd">cakāra rāghavaḥ sakhyaṃ so 'vadhīt pitaraṃ mama</li>
    </ul>
    <ul id="R_4.056.013">
        <li id="R_4.056.013ab">māma pitrā viruddho hi sugrīvaḥ sacivaiḥ saha</li>
        <li id="R_4.056.013cd">nihatya vālinaṃ rāmas tatas tam abhiṣecayat</li>
    </ul>
    <ul id="R_4.056.014">
        <li id="R_4.056.014ab">sa rājye sthāpitas tena sugrīvo vānareśvaraḥ</li>
        <li id="R_4.056.014cd">rājā vānaramukhyānāṃ yena prasthāpitā vayam</li>
    </ul>
    <ul id="R_4.056.015">
        <li id="R_4.056.015ab">evaṃ rāmaprayuktās tu mārgamāṇās tatas tataḥ</li>
        <li id="R_4.056.015cd">vaidehīṃ nādhigacchāmo rātrau sūryaprabhām iva</li>
    </ul>
    <ul id="R_4.056.016">
        <li id="R_4.056.016ab">te vayaṃ daṇdakāraṇyaṃ vicitya susamāhitāḥ</li>
        <li id="R_4.056.016cd">ajñānāt tu praviṣṭāḥ sma dharaṇyā vivṛtaṃ bilam</li>
    </ul>
    <ul id="R_4.056.017">
        <li id="R_4.056.017ab">mayasya māyā vihitaṃ tad bilaṃ ca vicinvatām</li>
        <li id="R_4.056.017cd">vyatītas tatra no māso yo rājñā sāmayaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_4.056.018">
        <li id="R_4.056.018ab">te vayaṃ kapirājasya sarve vacanakāriṇaḥ</li>
        <li id="R_4.056.018cd">kṛtāṃ saṃsthām atikrāntā bhayāt prāyam upāsmahe</li>
    </ul>
    <ul id="R_4.056.019">
        <li id="R_4.056.019ab">kruddhe tasmiṃs tu kākutsthe sugrīve ca salakṣmaṇe</li>
        <li id="R_4.056.019cd">gatānām api sarveṣāṃ tatra no nāsti jīvitam</li>
    </ul>
    <ul id="R_4.057.001">
        <li id="R_4.057.001ab">ity uktaḥ karuṇaṃ vākyaṃ vānarais tyaktajīvitaiḥ</li>
        <li id="R_4.057.001cd">sabāṣpo vānarān gṛdhraḥ pratyuvāca mahāsvanaḥ</li>
    </ul>
    <ul id="R_4.057.002">
        <li id="R_4.057.002ab">yavīyān mama sa bhrātā jaṭāyur nāma vānarāḥ</li>
        <li id="R_4.057.002cd">yamākhyāta hataṃ yuddhe rāvaṇena balīyasā</li>
    </ul>
    <ul id="R_4.057.003">
        <li id="R_4.057.003ab">vṛddhabhāvād apakṣatvāc chṛṇvaṃs tad api marṣaye</li>
        <li id="R_4.057.003cd">na hi me śaktir adyāsti bhrātur vairavimokṣaṇe</li>
    </ul>
    <ul id="R_4.057.004">
        <li id="R_4.057.004ab">purā vṛtravadhe vṛtte sa cāhaṃ ca jayaiṣiṇau</li>
        <li id="R_4.057.004cd">ādityam upayātau svo jvalantaṃ raśmimālinam</li>
    </ul>
    <ul id="R_4.057.005">
        <li id="R_4.057.005ab">āvṛtyākāśamārgeṇa javena sma gatau bhṛśam</li>
        <li id="R_4.057.005cd">madhyaṃ prāpte ca sūrye ca jaṭāyur avasīdati</li>
    </ul>
    <ul id="R_4.057.006">
        <li id="R_4.057.006ab">tam ahaṃ bhrātaraṃ dṛṣṭvā sūryaraśmibhir arditam</li>
        <li id="R_4.057.006cd">pakṣābhyaṃ chādayām āsa snehāt paramavihvalam</li>
    </ul>
    <ul id="R_4.057.007">
        <li id="R_4.057.007ab">nirdagdhapakṣaḥ patito vindhye 'haṃ vānarottamāḥ</li>
        <li id="R_4.057.007cd">aham asmin vasan bhrātuḥ pravṛttiṃ nopalakṣaye</li>
    </ul>
    <ul id="R_4.057.008">
        <li id="R_4.057.008ab">jaṭāyuṣas tv evam ukto bhrātrā saṃpātinā tadā</li>
        <li id="R_4.057.008cd">yuvarājo mahāprājñaḥ pratyuvācāṅgadas tadā</li>
    </ul>
    <ul id="R_4.057.009">
        <li id="R_4.057.009ab">jaṭāyuṣo yadi bhrātā śrutaṃ te gaditaṃ mayā</li>
        <li id="R_4.057.009cd">ākhyāhi yadi jānāsi nilayaṃ tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_4.057.010">
        <li id="R_4.057.010ab">adīrghadarśinaṃ taṃ vā rāvaṇaṃ rākṣasādhipam</li>
        <li id="R_4.057.010cd">antike yadi vā dūre yadi jānāsi śaṃsa naḥ</li>
    </ul>
    <ul id="R_4.057.011">
        <li id="R_4.057.011ab">tato 'bravīn mahātejā jyeṣṭho bhrātā jaṭāyuṣaḥ</li>
        <li id="R_4.057.011cd">ātmānurūpaṃ vacanaṃ vānarān saṃpraharṣayan</li>
    </ul>
    <ul id="R_4.057.012">
        <li id="R_4.057.012ab">nirdagdhapakṣo gṛdhro 'haṃ gatavīryaḥ plavaṃgamāḥ</li>
        <li id="R_4.057.012cd">vāṅmātreṇa tu rāmasya kariṣye sāhyam uttamam</li>
    </ul>
    <ul id="R_4.057.013">
        <li id="R_4.057.013ab">jānāmi vāruṇāl lokān viṣṇos traivikramān api</li>
        <li id="R_4.057.013cd">devāsuravimardāṃś ca amṛtasya ca manthanam</li>
    </ul>
    <ul id="R_4.057.014">
        <li id="R_4.057.014ab">rāmasya yad idaṃ kāryaṃ kartavyaṃ prathamaṃ mayā</li>
        <li id="R_4.057.014cd">jarayā ca hṛtaṃ tejaḥ prāṇāś ca śithilā mama</li>
    </ul>
    <ul id="R_4.057.015">
        <li id="R_4.057.015ab">taruṇī rūpasaṃpannā sarvābharaṇabhūṣitā</li>
        <li id="R_4.057.015cd">hriyamāṇā mayā dṛṣṭā rāvaṇena durātmanā</li>
    </ul>
    <ul id="R_4.057.016">
        <li id="R_4.057.016ab">krośantī rāma rāmeti lakṣmaṇeti ca bhāminī</li>
        <li id="R_4.057.016cd">bhūṣaṇāny apavidhyantī gātrāṇi ca vidhunvatī</li>
    </ul>
    <ul id="R_4.057.017">
        <li id="R_4.057.017ab">sūryaprabheva śailāgre tasyāḥ kauśeyam uttamam</li>
        <li id="R_4.057.017cd">asite rākṣase bhāti yathā vā taḍidambude</li>
    </ul>
    <ul id="R_4.057.018">
        <li id="R_4.057.018ab">tāṃ tu sītām ahaṃ manye rāmasya parikīrtanāt</li>
        <li id="R_4.057.018cd">śrūyatāṃ me kathayato nilayaṃ tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_4.057.019">
        <li id="R_4.057.019ab">putro viśravasaḥ sākṣād bhrātā vaiśravaṇasya ca</li>
        <li id="R_4.057.019cd">adhyāste nagarīṃ laṅkāṃ rāvaṇo nāma rākasaḥ</li>
    </ul>
    <ul id="R_4.057.020">
        <li id="R_4.057.020ab">ito dvīpe samudrasya saṃpūrṇe śatayojane</li>
        <li id="R_4.057.020cd">tasmiṃl laṅkā purī ramyā nirmitā viśvakarmaṇā</li>
    </ul>
    <ul id="R_4.057.021">
        <li id="R_4.057.021ab">tasyāṃ vasati vaidehī dīnā kauśeyavāsinī</li>
        <li id="R_4.057.021cd">rāvaṇāntaḥpure ruddhā rākṣasībhiḥ surakṣitā</li>
    </ul>
    <ul id="R_4.057.022">
        <li id="R_4.057.022ab">janakasyātmajāṃ rājñas tasyāṃ drakṣyatha maithilīm</li>
        <li id="R_4.057.022cd">laṅkāyām atha guptāyāṃ sāgareṇa samantataḥ</li>
    </ul>
    <ul id="R_4.057.023">
        <li id="R_4.057.023ab">saṃprāpya sāgarasyāntaṃ saṃpūrṇaṃ śatayojanam</li>
        <li id="R_4.057.023cd">āsādya dakṣiṇaṃ kūlaṃ tato drakṣyatha rāvaṇam</li>
    </ul>
    <ul id="R_4.057.024">
        <li id="R_4.057.024ab">tatraiva tvaritāḥ kṣipraṃ vikramadhvaṃ plavaṃgamāḥ</li>
        <li id="R_4.057.024cd">jñānena khalu paśyāmi dṛṣṭvā pratyāgamiṣyatha</li>
    </ul>
    <ul id="R_4.057.025">
        <li id="R_4.057.025ab">ādyaḥ panthāḥ kuliṅgānāṃ ye cānye dhānyajīvinaḥ</li>
        <li id="R_4.057.025cd">dvitīyo balibhojānāṃ ye ca vṛkṣaphalāśinaḥ</li>
    </ul>
    <ul id="R_4.057.026">
        <li id="R_4.057.026ab">bhāsās tṛtīyaṃ gacchanti krauñcāś ca kuraraiḥ saha</li>
        <li id="R_4.057.026cd">śyenāś caturthaṃ gacchanti gṛdhrā gacchanti pañcamam</li>
    </ul>
    <ul id="R_4.057.027">
        <li id="R_4.057.027ab">balavīryopapannānāṃ rūpayauvanaśālinām</li>
        <li id="R_4.057.027cd">ṣaṣṭhas tu panthā haṃsānāṃ vainateyagatiḥ parā</li>
        <li id="R_4.057.027ef">vainateyāc ca no janma sarveṣāṃ vānararṣabhāḥ</li>
    </ul>
    <ul id="R_4.057.028">
        <li id="R_4.057.028ab">garhitaṃ tu kṛtaṃ karma yena sma piśitāśanāḥ</li>
        <li id="R_4.057.028cd">ihastho 'haṃ prapaśyāmi rāvaṇaṃ jānakīṃ tathā</li>
    </ul>
    <ul id="R_4.057.029">
        <li id="R_4.057.029ab">asmākam api sauvarṇaṃ divyaṃ cakṣurbalaṃ tathā</li>
        <li id="R_4.057.029cd">tasmād āhāravīryeṇa nisargeṇa ca vānarāḥ</li>
        <li id="R_4.057.029ef">āyojanaśatāt sāgrād vayaṃ paśyāma nityaśaḥ</li>
    </ul>
    <ul id="R_4.057.030">
        <li id="R_4.057.030ab">asmākaṃ vihitā vṛttir nisārgeṇa ca dūrataḥ</li>
        <li id="R_4.057.030cd">vihitā pādamūle tu vṛttiś caraṇayodhinām</li>
    </ul>
    <ul id="R_4.057.031">
        <li id="R_4.057.031ab">upāyo dṛśyatāṃ kaś cil laṅghane lavaṇāmbhasaḥ</li>
        <li id="R_4.057.031cd">abhigamya tu vaidehīṃ samṛddhārthā gamiṣyatha</li>
    </ul>
    <ul id="R_4.057.032">
        <li id="R_4.057.032ab">samudraṃ netum icchāmi bhavadbhir varuṇālayam</li>
        <li id="R_4.057.032cd">pradāsyāmy udakaṃ bhrātuḥ svargatasya mahātmanaḥ</li>
    </ul>
    <ul id="R_4.057.033">
        <li id="R_4.057.033ab">tato nītvā tu taṃ deśaṃ tīre nadanadīpateḥ</li>
        <li id="R_4.057.033cd">nirdagdhapakṣaṃ saṃpātiṃ vānarāḥ sumahaujasaḥ</li>
    </ul>
    <ul id="R_4.057.034">
        <li id="R_4.057.034ab">punaḥ pratyānayitvā vai taṃ deśaṃ patageśvaram</li>
        <li id="R_4.057.034cd">babhūvur vānarā hṛṣṭāḥ pravṛttim upalabhya te</li>
    </ul>
    <ul id="R_4.058.001">
        <li id="R_4.058.001ab">tatas tad amṛtāsvādaṃ gṛdhrarājena bhāṣitam</li>
        <li id="R_4.058.001cd">niśamya vadato hṛṣṭās te vacaḥ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_4.058.002">
        <li id="R_4.058.002ab">jāmbavān vai hariśreṣṭhaḥ saha sarvaiḥ plavaṃgamaiḥ</li>
        <li id="R_4.058.002cd">bhūtalāt sahasotthāya gṛdhrarājānam abravīt</li>
    </ul>
    <ul id="R_4.058.003">
        <li id="R_4.058.003ab">kva sītā kena vā dṛṣṭā ko vā harati maithilīm</li>
        <li id="R_4.058.003cd">tad ākhyātu bhavān sarvaṃ gatir bhava vanaukasām</li>
    </ul>
    <ul id="R_4.058.004">
        <li id="R_4.058.004ab">ko dāśarathibāṇānāṃ vajraveganipātinām</li>
        <li id="R_4.058.004cd">svayaṃ lakṣmaṇam uktānāṃ na cintayati vikramam</li>
    </ul>
    <ul id="R_4.058.005">
        <li id="R_4.058.005ab">sa harīn prītisaṃyuktān sītā śrutisamāhitān</li>
        <li id="R_4.058.005cd">punar āśvāsayan prīta idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.058.006">
        <li id="R_4.058.006ab">śrūyatām iha vaidehyā yathā me haraṇaṃ śrutam</li>
        <li id="R_4.058.006cd">yena cāpi mamākhyātaṃ yatra cāyatalocanā</li>
    </ul>
    <ul id="R_4.058.007">
        <li id="R_4.058.007ab">aham asmin girau durge bahuyojanam āyate</li>
        <li id="R_4.058.007cd">cirān nipatito vṛddhaḥ kṣīṇaprāṇaparākramaḥ</li>
    </ul>
    <ul id="R_4.058.008">
        <li id="R_4.058.008ab">taṃ mām evaṃgataṃ putraḥ supārśvo nāma nāmataḥ</li>
        <li id="R_4.058.008cd">āhāreṇa yathākālaṃ bibharti patatāṃ varaḥ</li>
    </ul>
    <ul id="R_4.058.009">
        <li id="R_4.058.009ab">tīkṣṇakāmās tu gandharvās tīkṣṇakopā bhujaṃgamāḥ</li>
        <li id="R_4.058.009cd">mṛgāṇāṃ tu bhayaṃ tīkṣṇaṃ tatas tīkṣṇakṣudhā vayam</li>
    </ul>
    <ul id="R_4.058.010">
        <li id="R_4.058.010ab">sa kadā cit kṣudhārtasya mama cāhārakāṅkṣiṇaḥ</li>
        <li id="R_4.058.010cd">gatasūryo 'hani prāpto mama putro hy anāmiṣaḥ</li>
    </ul>
    <ul id="R_4.058.011">
        <li id="R_4.058.011ab">sa mayā vṛddhabhāvāc ca kopāc ca paribhartsitaḥ</li>
        <li id="R_4.058.011cd">kṣutpipāsā parītena kumāraḥ patatāṃ varaḥ</li>
    </ul>
    <ul id="R_4.058.012">
        <li id="R_4.058.012ab">sa mamāhārasaṃrodhāt pīḍitaḥ prītivardhanaḥ</li>
        <li id="R_4.058.012cd">anumānya yathātattvam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_4.058.013">
        <li id="R_4.058.013ab">ahaṃ tāta yathākālam āmiṣārthī kham āplutaḥ</li>
        <li id="R_4.058.013cd">mahendrasya girer dvāram āvṛtya ca samāsthitaḥ</li>
    </ul>
    <ul id="R_4.058.014">
        <li id="R_4.058.014ab">tatra sattvasahasrāṇāṃ sāgarāntaracāriṇām</li>
        <li id="R_4.058.014cd">panthānam eko 'dhyavasaṃ saṃniroddhum avāṅmukhaḥ</li>
    </ul>
    <ul id="R_4.058.015">
        <li id="R_4.058.015ab">tatra kaś cin mayā dṛṣṭaḥ sūryodayasamaprabhām</li>
        <li id="R_4.058.015cd">striyam ādāya gacchan vai bhinnāñjanacayopamaḥ</li>
    </ul>
    <ul id="R_4.058.016">
        <li id="R_4.058.016ab">so 'ham abhyavahārārthī tau dṛṣṭvā kṛtaniścayaḥ</li>
        <li id="R_4.058.016cd">tena sāmnā vinītena panthānam abhiyācitaḥ</li>
    </ul>
    <ul id="R_4.058.017">
        <li id="R_4.058.017ab">na hi sāmopapannānāṃ prahartā vidyate kva cit</li>
        <li id="R_4.058.017cd">nīceṣv api janaḥ kaś cit kim aṅga bata madvidhaḥ</li>
    </ul>
    <ul id="R_4.058.018">
        <li id="R_4.058.018ab">sa yātas tejasā vyoma saṃkṣipann iva vegataḥ</li>
        <li id="R_4.058.018cd">athāhaṃ khe carair bhūtair abhigamya sabhājitaḥ</li>
    </ul>
    <ul id="R_4.058.019">
        <li id="R_4.058.019ab">diṣṭyā jīvasi tāteti abruvan māṃ maharṣayaḥ</li>
        <li id="R_4.058.019cd">kathaṃ cit sakalatro 'sau gatas te svasty asaṃśayam</li>
    </ul>
    <ul id="R_4.058.020">
        <li id="R_4.058.020ab">evam uktas tato 'haṃ taiḥ siddhaiḥ paramaśobhanaiḥ</li>
        <li id="R_4.058.020cd">sa ca me rāvaṇo rājā rakṣasāṃ prativeditaḥ</li>
    </ul>
    <ul id="R_4.058.021">
        <li id="R_4.058.021ab">haran dāśarather bhāryāṃ rāmasya janakātmajām</li>
        <li id="R_4.058.021cd">bhraṣṭābharaṇakauśeyāṃ śokavegaparājitām</li>
    </ul>
    <ul id="R_4.058.022">
        <li id="R_4.058.022ab">rāmalakṣmaṇayor nāma krośantīṃ muktamūrdhajām</li>
        <li id="R_4.058.022cd">eṣa kālātyayas tāvad iti vākyavidāṃ varaḥ</li>
    </ul>
    <ul id="R_4.058.023">
        <li id="R_4.058.023ab">etam arthaṃ samagraṃ me supārśvaḥ pratyavedayat</li>
        <li id="R_4.058.023cd">tac chrutvāpi hi me buddhir nāsīt kā cit parākrame</li>
    </ul>
    <ul id="R_4.058.024">
        <li id="R_4.058.024ab">apakṣo hi kathaṃ pakṣī karma kiṃ cid upakramet</li>
        <li id="R_4.058.024cd">yat tu śakyaṃ mayā kartuṃ vāgbuddhiguṇavartinā</li>
    </ul>
    <ul id="R_4.058.025">
        <li id="R_4.058.025ab">śrūyatāṃ tat pravakṣyāmi bhavatāṃ pauruṣāśrayam</li>
        <li id="R_4.058.025cd">vāṅmatibhyāṃ hi sārveṣāṃ kariṣyāmi priyaṃ hi vaḥ</li>
        <li id="R_4.058.025ef">yad dhi dāśaratheḥ kāryaṃ mama tan nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_4.058.026">
        <li id="R_4.058.026ab">te bhavanto matiśreṣṭhā balavanto manasvinaḥ</li>
        <li id="R_4.058.026cd">sahitāḥ kapirājena devair api durāsadāḥ</li>
    </ul>
    <ul id="R_4.058.027">
        <li id="R_4.058.027ab">rāmalakṣmaṇabāṇāś ca niśitāḥ kaṅkapatriṇaḥ</li>
        <li id="R_4.058.027cd">trayāṇām api lokānāṃ paryāptās trāṇanigrahe</li>
    </ul>
    <ul id="R_4.058.028">
        <li id="R_4.058.028ab">kāmaṃ khalu daśagrīvas tejobalasamanvitaḥ</li>
        <li id="R_4.058.028cd">bhavatāṃ tu samarthānāṃ na kiṃ cid api duṣkaram</li>
    </ul>
    <ul id="R_4.058.029">
        <li id="R_4.058.029ab">tad alaṃ kālasaṃgena kriyatāṃ buddhiniścayaḥ</li>
        <li id="R_4.058.029cd">na hi karmasu sajjante buddhimanto bhavadvidhāḥ</li>
    </ul>
    <ul id="R_4.059.001">
        <li id="R_4.059.001ab">tataḥ kṛtodakaṃ snātaṃ taṃ gṛdhraṃ hariyūthapāḥ</li>
        <li id="R_4.059.001cd">upaviṣṭā girau durge parivārya samantataḥ</li>
    </ul>
    <ul id="R_4.059.002">
        <li id="R_4.059.002ab">tam aṅgadam upāsīnaṃ taiḥ sarvair haribhir vṛtam</li>
        <li id="R_4.059.002cd">janitapratyayo harṣāt saṃpātiḥ punar abravīt</li>
    </ul>
    <ul id="R_4.059.003">
        <li id="R_4.059.003ab">kṛtvā niḥśabdam ekāgrāḥ śṛṇvantu harayo mama</li>
        <li id="R_4.059.003cd">tattvaṃ saṃkīrtayiṣyāmi yathā jānāmi maithilīm</li>
    </ul>
    <ul id="R_4.059.004">
        <li id="R_4.059.004ab">asya vindhyasya śikhare patito 'smi purā vane</li>
        <li id="R_4.059.004cd">sūryātapaparītāṅgo nirdagdhaḥ sūryaraśmibhiḥ</li>
    </ul>
    <ul id="R_4.059.005">
        <li id="R_4.059.005ab">labdhasaṃjñas tu ṣaḍrātrād vivaśo vihvalann iva</li>
        <li id="R_4.059.005cd">vīkṣamāṇo diśaḥ sarvā nābhijānāmi kiṃ cana</li>
    </ul>
    <ul id="R_4.059.006">
        <li id="R_4.059.006ab">tatas tu sāgarāñ śailān nadīḥ sarvāḥ sarāṃsi ca</li>
        <li id="R_4.059.006cd">vanāny aṭavideśāṃś ca samīkṣya matir āgamat</li>
    </ul>
    <ul id="R_4.059.007">
        <li id="R_4.059.007ab">hṛṣṭapakṣigaṇākīrṇaḥ kandarāntarakūṭavān</li>
        <li id="R_4.059.007cd">dakṣiṇasyodadhes tīre vindhyo 'yam iti niścitaḥ</li>
    </ul>
    <ul id="R_4.059.008">
        <li id="R_4.059.008ab">āsīc cātrāśramaṃ puṇyaṃ surair api supūjitam</li>
        <li id="R_4.059.008cd">ṛṣir niśākaro nāma yasminn ugratapābhavat</li>
    </ul>
    <ul id="R_4.059.009">
        <li id="R_4.059.009ab">aṣṭau varṣasahasrāṇi tenāsminn ṛṣiṇā vinā</li>
        <li id="R_4.059.009cd">vasato mama dharmajñāḥ svargate tu niśākare</li>
    </ul>
    <ul id="R_4.059.010">
        <li id="R_4.059.010ab">avatīrya ca vindhyāgrāt kṛcchreṇa viṣamāc chanaiḥ</li>
        <li id="R_4.059.010cd">tīkṣṇadarbhāṃ vasumatīṃ duḥkhena punar āgataḥ</li>
    </ul>
    <ul id="R_4.059.011">
        <li id="R_4.059.011ab">tam ṛṣiṃ draṣṭu kāmo 'smi duḥkhenābhyāgato bhṛśam</li>
        <li id="R_4.059.011cd">jaṭāyuṣā mayā caiva bahuśo 'bhigato hi saḥ</li>
    </ul>
    <ul id="R_4.059.012">
        <li id="R_4.059.012ab">tasyāśramapadābhyāśe vavur vātāḥ sugandhinaḥ</li>
        <li id="R_4.059.012cd">vṛkṣo nāpuṣpitaḥ kaś cid aphalo vā na dṛśyate</li>
    </ul>
    <ul id="R_4.059.013">
        <li id="R_4.059.013ab">upetya cāśramaṃ puṇyaṃ vṛkṣamūlam upāśritaḥ</li>
        <li id="R_4.059.013cd">draṣṭukāmaḥ pratīkṣe ca bhagavantaṃ niśākaram</li>
    </ul>
    <ul id="R_4.059.014">
        <li id="R_4.059.014ab">athāpaśyam adūrastham ṛṣiṃ jvalitatejasaṃ</li>
        <li id="R_4.059.014cd">kṛtābhiṣekaṃ durdharṣam upāvṛttam udaṅmukham</li>
    </ul>
    <ul id="R_4.059.015">
        <li id="R_4.059.015ab">tam ṛkṣāḥ sṛmarā vyāghrāḥ siṃhā nāgāḥ sarīsṛpāḥ</li>
        <li id="R_4.059.015cd">parivāryopagacchanti dātāraṃ prāṇino yathā</li>
    </ul>
    <ul id="R_4.059.016">
        <li id="R_4.059.016ab">tataḥ prāptam ṛṣiṃ jñātvā tāni sattvāni vai yayuḥ</li>
        <li id="R_4.059.016cd">praviṣṭe rājani yathā sarvaṃ sāmātyakaṃ balam</li>
    </ul>
    <ul id="R_4.059.017">
        <li id="R_4.059.017ab">ṛṣis tu dṛṣṭvā māṃ tuṣṭaḥ praviṣṭaś cāśramaṃ punaḥ</li>
        <li id="R_4.059.017cd">muhūrtamātrān niṣkramya tataḥ kāryam apṛcchata</li>
    </ul>
    <ul id="R_4.059.018">
        <li id="R_4.059.018ab">saumya vaikalyatāṃ dṛṣṭvā roṃṇāṃ te nāvagamyate</li>
        <li id="R_4.059.018cd">agnidagdhāv imau pakṣau tvak caiva vraṇitā tava</li>
    </ul>
    <ul id="R_4.059.019">
        <li id="R_4.059.019ab">dvau gṛdhrau dṛṣṭapūrvau me mātariśvasamau jave</li>
        <li id="R_4.059.019cd">gṛdhrāṇāṃ caiva rājānau bhrātarau kāmarūpiṇau</li>
    </ul>
    <ul id="R_4.059.020">
        <li id="R_4.059.020ab">jyeṣṭhas tvaṃ tu ca saṃpātir jaṭāyur anujas tava</li>
        <li id="R_4.059.020cd">mānuṣaṃ rūpam āsthāya gṛhṇītāṃ caraṇau mama</li>
    </ul>
    <ul id="R_4.059.021">
        <li id="R_4.059.021ab">kiṃ te vyādhisamutthānaṃ pakṣayoḥ patanaṃ katham</li>
        <li id="R_4.059.021cd">daṇḍo vāyaṃ dhṛtaḥ kena sarvam ākhyāhi pṛcchataḥ</li>
    </ul>
    <ul id="R_4.060.001">
        <li id="R_4.060.001ab">tatas tad dāruṇaṃ karma duṣkaraṃ sāhasāt kṛtam</li>
        <li id="R_4.060.001cd">ācacakṣe muneḥ sarvaṃ sūryānugamanaṃ tathā</li>
    </ul>
    <ul id="R_4.060.002">
        <li id="R_4.060.002ab">bhagavan vraṇayuktatvāl lajjayā cākulendriyaḥ</li>
        <li id="R_4.060.002cd">pariśrānto na śaknomi vacanaṃ paribhāṣitum</li>
    </ul>
    <ul id="R_4.060.003">
        <li id="R_4.060.003ab">ahaṃ caiva jaṭāyuś ca saṃgharṣād darpamohitau</li>
        <li id="R_4.060.003cd">ākāśaṃ patitau vīrau jighāsantau parākramam</li>
    </ul>
    <ul id="R_4.060.004">
        <li id="R_4.060.004ab">kailāsaśikhare baddhvā munīnām agrataḥ paṇam</li>
        <li id="R_4.060.004cd">raviḥ syād anuyātavyo yāvad astaṃ mahāgirim</li>
    </ul>
    <ul id="R_4.060.005">
        <li id="R_4.060.005ab">athāvāṃ yugapat prāptāv apaśyāva mahītale</li>
        <li id="R_4.060.005cd">rathacakrapramāṇāni nagarāṇi pṛthak pṛthak</li>
    </ul>
    <ul id="R_4.060.006">
        <li id="R_4.060.006ab">kva cid vāditraghoṣāṃś ca brahmaghoṣāṃś ca śuśruva</li>
        <li id="R_4.060.006cd">gāyantīś cāṅganā bahvīḥ paśyāvo raktavāsasaḥ</li>
    </ul>
    <ul id="R_4.060.007">
        <li id="R_4.060.007ab">tūrṇam utpatya cākāśam ādityapatham āsthitau</li>
        <li id="R_4.060.007cd">āvām ālokayāvas tad vanaṃ śādvalasaṃsthitam</li>
    </ul>
    <ul id="R_4.060.008">
        <li id="R_4.060.008ab">upalair iva saṃchannā dṛśyate bhūḥ śiloccayaiḥ</li>
        <li id="R_4.060.008cd">āpagābhiś ca saṃvītā sūtrair iva vasuṃdharā</li>
    </ul>
    <ul id="R_4.060.009">
        <li id="R_4.060.009ab">himavāṃś caiva vindhyaś ca meruś ca sumahān nagaḥ</li>
        <li id="R_4.060.009cd">bhūtale saṃprakāśante nāgā iva jalāśaye</li>
    </ul>
    <ul id="R_4.060.010">
        <li id="R_4.060.010ab">tīvrasvedaś ca khedaś ca bhayaṃ cāsīt tadāvayoḥ</li>
        <li id="R_4.060.010cd">samāviśata mohaś ca mohān mūrchā ca dāruṇā</li>
    </ul>
    <ul id="R_4.060.011">
        <li id="R_4.060.011ab">na dig vijñāyate yāmyā nāgenyā na ca vāruṇī</li>
        <li id="R_4.060.011cd">yugānte niyato loko hato dagdha ivāgninā</li>
    </ul>
    <ul id="R_4.060.012">
        <li id="R_4.060.012ab">yatnena mahatā bhūyo raviḥ samavalokitaḥ</li>
        <li id="R_4.060.012cd">tulyaḥ pṛthvīpramāṇena bhāskaraḥ pratibhāti nau</li>
    </ul>
    <ul id="R_4.060.013">
        <li id="R_4.060.013ab">jaṭāyur mām anāpṛcchya nipapāta mahīṃ tataḥ</li>
        <li id="R_4.060.013cd">taṃ dṛṣṭvā tūrṇam ākāśād ātmānaṃ muktavān aham</li>
    </ul>
    <ul id="R_4.060.014">
        <li id="R_4.060.014ab">pakṣibhyāṃ ca mayā gupto jaṭāyur na pradahyata</li>
        <li id="R_4.060.014cd">pramādāt tatra nirdagdhaḥ patan vāyupathād aham</li>
    </ul>
    <ul id="R_4.060.015">
        <li id="R_4.060.015ab">āśaṅke taṃ nipatitaṃ janasthāne jaṭāyuṣam</li>
        <li id="R_4.060.015cd">ahaṃ tu patito vindhye dagdhapakṣo jaḍīkṛtaḥ</li>
    </ul>
    <ul id="R_4.060.016">
        <li id="R_4.060.016ab">rājyena hīno bhrātrā ca pakṣābhyāṃ vikrameṇa ca</li>
        <li id="R_4.060.016cd">sarvathā martum evecchan patiṣye śikharād gireḥ</li>
    </ul>
    <ul id="R_4.061.001">
        <li id="R_4.061.001ab">evam uktvā muniśreṣṭham arudaṃ duḥkhito bhṛśam</li>
        <li id="R_4.061.001cd">atha dhyātvā muhūrtaṃ tu bhagavān idam abravīt</li>
    </ul>
    <ul id="R_4.061.002">
        <li id="R_4.061.002ab">pakṣau ca te prapakṣau ca punar anyau bhaviṣyataḥ</li>
        <li id="R_4.061.002cd">cakṣuṣī caiva prāṇāś ca vikramaś ca balaṃ ca te</li>
    </ul>
    <ul id="R_4.061.003">
        <li id="R_4.061.003ab">purāṇe sumahat kāryaṃ bhaviṣyaṃ hi mayā śrutam</li>
        <li id="R_4.061.003cd">dṛṣṭaṃ me tapasā caiva śrutvā ca viditaṃ mama</li>
    </ul>
    <ul id="R_4.061.004">
        <li id="R_4.061.004ab">rājā daśaratho nāma kaś cid ikṣvākunandanaḥ</li>
        <li id="R_4.061.004cd">tasya putro mahātejā rāmo nāma bhaviṣyati</li>
    </ul>
    <ul id="R_4.061.005">
        <li id="R_4.061.005ab">araṇyaṃ ca saha bhrātrā lakṣmaṇena gamiṣyati</li>
        <li id="R_4.061.005cd">tasminn arthe niyuktaḥ san pitrā satyaparākramaḥ</li>
    </ul>
    <ul id="R_4.061.006">
        <li id="R_4.061.006ab">nairṛto rāvaṇo nāma tasyā bhāryāṃ hariṣyati</li>
        <li id="R_4.061.006cd">rākṣasendro janasthānād avadhyaḥ suradānavaiḥ</li>
    </ul>
    <ul id="R_4.061.007">
        <li id="R_4.061.007ab">sā ca kāmaiḥ pralobhyantī bhakṣyair bhojyaiś ca maithilī</li>
        <li id="R_4.061.007cd">na bhokṣyati mahābhāgā duḥkhamagnā yaśasvinī</li>
    </ul>
    <ul id="R_4.061.008">
        <li id="R_4.061.008ab">paramānnaṃ tu vaidehyā jñātvā dāsyati vāsavaḥ</li>
        <li id="R_4.061.008cd">yad annam amṛtaprakhyaṃ surāṇām api durlabham</li>
    </ul>
    <ul id="R_4.061.009">
        <li id="R_4.061.009ab">tad annaṃ maithilī prāpya vijñāyendrād idaṃ tv iti</li>
        <li id="R_4.061.009cd">agram uddhṛtya rāmāya bhūtale nirvapiṣyati</li>
    </ul>
    <ul id="R_4.061.010">
        <li id="R_4.061.010ab">yadi jīvati me bhartā lakṣmaṇena saha prabhuḥ</li>
        <li id="R_4.061.010cd">devatvaṃ gatayor vāpi tayor annam idaṃ tv iti</li>
    </ul>
    <ul id="R_4.061.011">
        <li id="R_4.061.011ab">eṣyanty anveṣakās tasyā rāmadūtāḥ plavaṃgamāḥ</li>
        <li id="R_4.061.011cd">ākhyeyā rāmamahiṣī tvayā tebhyo vihaṃgama</li>
    </ul>
    <ul id="R_4.061.012">
        <li id="R_4.061.012ab">sarvathā tu na gantavyam īdṛśaḥ kva gamiṣyasi</li>
        <li id="R_4.061.012cd">deśakālau pratīkṣasva pakṣau tvaṃ pratipatsyase</li>
    </ul>
    <ul id="R_4.061.013">
        <li id="R_4.061.013ab">utsaheyam ahaṃ kartum adyaiva tvāṃ sapakṣakam</li>
        <li id="R_4.061.013cd">ihasthas tvaṃ tu lokānāṃ hitaṃ kāryaṃ kariṣyasi</li>
    </ul>
    <ul id="R_4.061.014">
        <li id="R_4.061.014ab">tvayāpi khalu tat kāryaṃ tayoś ca nṛpaputrayoḥ</li>
        <li id="R_4.061.014cd">brāhmaṇānāṃ surāṇāṃ ca munīnāṃ vāsavasya ca</li>
    </ul>
    <ul id="R_4.061.015">
        <li id="R_4.061.015ab">icchāmy aham api draṣṭuṃ bhrātaru rāmalakṣmaṇau</li>
        <li id="R_4.061.015cd">necche ciraṃ dhārayituṃ prāṇāṃs tyakṣye kalevaram</li>
    </ul>
    <ul id="R_4.062.001">
        <li id="R_4.062.001ab">etair anyaiś ca bahubhir vākyair vākyaviśāradaḥ</li>
        <li id="R_4.062.001cd">māṃ praśasyābhyanujñāpya praviṣṭaḥ sa svam āśramam</li>
    </ul>
    <ul id="R_4.062.002">
        <li id="R_4.062.002ab">kandarāt tu visarpitvā parvatasya śanaiḥ śanaiḥ</li>
        <li id="R_4.062.002cd">ahaṃ vindhyaṃ samāruhya bhavataḥ pratipālaye</li>
    </ul>
    <ul id="R_4.062.003">
        <li id="R_4.062.003ab">adya tv etasya kālasya sāgraṃ varṣaśataṃ gatam</li>
        <li id="R_4.062.003cd">deśakālapratīkṣo 'smi hṛdi kṛtvā muner vacaḥ</li>
    </ul>
    <ul id="R_4.062.004">
        <li id="R_4.062.004ab">mahāprasthānam āsādya svargate tu niśākare</li>
        <li id="R_4.062.004cd">māṃ nirdahati saṃtāpo vitarkair bahubhir vṛtam</li>
    </ul>
    <ul id="R_4.062.005">
        <li id="R_4.062.005ab">utthitāṃ maraṇe buddhiṃ muni vākyair nivartaye</li>
        <li id="R_4.062.005cd">buddhir yā tena me dattā prāṇasaṃrakṣaṇāya tu</li>
        <li id="R_4.062.005ef">sā me 'panayate duḥkhaṃ dīptevāgniśikhā tamaḥ</li>
    </ul>
    <ul id="R_4.062.006">
        <li id="R_4.062.006ab">budhyatā ca mayā vīryaṃ rāvaṇasya durātmanaḥ</li>
        <li id="R_4.062.006cd">putraḥ saṃtarjito vāgbhir na trātā maithilī katham</li>
    </ul>
    <ul id="R_4.062.007">
        <li id="R_4.062.007ab">tasyā vilapitaṃ śrutvā tau ca sītā vinākṛtau</li>
        <li id="R_4.062.007cd">na me daśarathasnehāt putreṇotpāditaṃ priyam</li>
    </ul>
    <ul id="R_4.062.008">
        <li id="R_4.062.008ab">tasya tv evaṃ bruvāṇasya saṃpāter vānaraiḥ saha</li>
        <li id="R_4.062.008cd">utpetatus tadā pakṣau samakṣaṃ vanacāriṇām</li>
    </ul>
    <ul id="R_4.062.009">
        <li id="R_4.062.009ab">sa dṛṣṭvā svāṃ tanuṃ pakṣair udgatair aruṇacchadaiḥ</li>
        <li id="R_4.062.009cd">praharṣam atulaṃ lebhe vānarāṃś cedam abravīt</li>
    </ul>
    <ul id="R_4.062.010">
        <li id="R_4.062.010ab">niśākarasya maharṣeḥ prabhāvād amitātmanaḥ</li>
        <li id="R_4.062.010cd">ādityaraśminirdagdhau pakṣau me punar utthitau</li>
    </ul>
    <ul id="R_4.062.011">
        <li id="R_4.062.011ab">yauvane vartamānasya mamāsīd yaḥ parākramaḥ</li>
        <li id="R_4.062.011cd">tam evādyāvagacchāmi balaṃ pauruṣam eva ca</li>
    </ul>
    <ul id="R_4.062.012">
        <li id="R_4.062.012ab">sarvathā kriyatāṃ yatnaḥ sītām adhigamiṣyatha</li>
        <li id="R_4.062.012cd">pakṣalābho mamāyaṃ vaḥ siddhipratyaya kārakaḥ</li>
    </ul>
    <ul id="R_4.062.013">
        <li id="R_4.062.013ab">ity uktvā tān harīn sarvān saṃpātiḥ patatāṃ varaḥ</li>
        <li id="R_4.062.013cd">utpapāta gireḥ śṛṅgāj jijñāsuḥ khagamo gatim</li>
    </ul>
    <ul id="R_4.062.014">
        <li id="R_4.062.014ab">tasya tad vacanaṃ śrutvā prītisaṃhṛṣṭamānasāḥ</li>
        <li id="R_4.062.014cd">babhūvur hariśārdūlā vikramābhyudayonmukhāḥ</li>
    </ul>
    <ul id="R_4.062.015">
        <li id="R_4.062.015ab">
            <seg type="pāda" n="a">atha pavanasamānavikramāḥ</seg>
            <seg type="pāda" n="b">plavagavarāḥ pratilabdha pauruṣāḥ</seg>
        </li>
        <li id="R_4.062.015cd">
            <seg type="pāda" n="c">abhijidabhimukhāṃ diśaṃ yayur</seg>
            <seg type="pāda" n="d">janakasutā parimārgaṇonmukhāḥ</seg>
        </li>
    </ul>
    <ul id="R_4.063.001">
        <li id="R_4.063.001ab">ākhyātā gṛdhrarājena samutpatya plavaṃgamāḥ</li>
        <li id="R_4.063.001cd">saṃgatāḥ prītisaṃyuktā vineduḥ siṃhavikramāḥ</li>
    </ul>
    <ul id="R_4.063.002">
        <li id="R_4.063.002ab">saṃpāter vacanaṃ śrutvā harayo rāvaṇakṣayam</li>
        <li id="R_4.063.002cd">hṛṣṭāḥ sāgaram ājagmuḥ sītādarśanakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_4.063.003">
        <li id="R_4.063.003ab">abhikramya tu taṃ deśaṃ dadṛśur bhīmavikramāḥ</li>
        <li id="R_4.063.003cd">kṛtsnaṃ lokasya mahataḥ pratibimbam iva sthitam</li>
    </ul>
    <ul id="R_4.063.004">
        <li id="R_4.063.004ab">dakṣiṇasya samudrasya samāsādyottarāṃ diśam</li>
        <li id="R_4.063.004cd">saṃniveśaṃ tataś cakruḥ sahitā vānarottamāḥ</li>
    </ul>
    <ul id="R_4.063.005">
        <li id="R_4.063.005ab">sattvair mahadbhir vikṛtaiḥ krīḍadbhir vividhair jale</li>
        <li id="R_4.063.005cd">vyāttāsyaiḥ sumahākāyair ūrmibhiś ca samākulam</li>
    </ul>
    <ul id="R_4.063.006">
        <li id="R_4.063.006ab">prasuptam iva cānyatra krīḍantam iva cānyataḥ</li>
        <li id="R_4.063.006cd">kva cit parvatamātraiś ca jalarāśibhir āvṛtam</li>
    </ul>
    <ul id="R_4.063.007">
        <li id="R_4.063.007ab">saṃkulaṃ dānavendraiś ca pātālatalavāsibhiḥ</li>
        <li id="R_4.063.007cd">romaharṣakaraṃ dṛṣṭvā viṣeduḥ kapikuñjarāḥ</li>
    </ul>
    <ul id="R_4.063.008">
        <li id="R_4.063.008ab">ākāśam iva duṣpāraṃ sāgaraṃ prekṣya vānarāḥ</li>
        <li id="R_4.063.008cd">viṣeduḥ sahasā sarve kathaṃ kāryam iti bruvan</li>
    </ul>
    <ul id="R_4.063.009">
        <li id="R_4.063.009ab">viṣaṇṇāṃ vāhinīṃ dṛṣṭvā sāgarasya nirīkṣaṇāt</li>
        <li id="R_4.063.009cd">āśvāsayām āsa harīn bhayārtān harisattamaḥ</li>
    </ul>
    <ul id="R_4.063.010">
        <li id="R_4.063.010ab">na niṣādena naḥ kāryaṃ viṣādo doṣavattaraḥ</li>
        <li id="R_4.063.010cd">viṣādo hanti puruṣaṃ bālaṃ kruddha ivoragaḥ</li>
    </ul>
    <ul id="R_4.063.011">
        <li id="R_4.063.011ab">viṣādo 'yaṃ prasahate vikrame paryupasthite</li>
        <li id="R_4.063.011cd">tejasā tasya hīnasya puruṣārtho na sidhyati</li>
    </ul>
    <ul id="R_4.063.012">
        <li id="R_4.063.012ab">tasyāṃ rātryāṃ vyatītāyām aṅgado vānaraiḥ saha</li>
        <li id="R_4.063.012cd">harivṛddhaiḥ samāgamya punar mantram amantrayat</li>
    </ul>
    <ul id="R_4.063.013">
        <li id="R_4.063.013ab">sā vānarāṇāṃ dhvajinī parivāryāṅgadaṃ babhau</li>
        <li id="R_4.063.013cd">vāsavaṃ parivāryeva marutāṃ vāhinī sthitā</li>
    </ul>
    <ul id="R_4.063.014">
        <li id="R_4.063.014ab">ko 'nyas tāṃ vānarīṃ senāṃ śaktaḥ stambhayituṃ bhavet</li>
        <li id="R_4.063.014cd">anyatra vālitanayād anyatra ca hanūmataḥ</li>
    </ul>
    <ul id="R_4.063.015">
        <li id="R_4.063.015ab">tatas tān harivṛddhāṃś ca tac ca sainyam ariṃdamaḥ</li>
        <li id="R_4.063.015cd">anumānyāṅgadaḥ śrīmān vākyam arthavad abravīt</li>
    </ul>
    <ul id="R_4.063.016">
        <li id="R_4.063.016ab">ka idānīṃ mahātejā laṅghayiṣyati sāgaram</li>
        <li id="R_4.063.016cd">kaḥ kariṣyati sugrīvaṃ satyasaṃdham ariṃdamam</li>
    </ul>
    <ul id="R_4.063.017">
        <li id="R_4.063.017ab">ko vīro yojanaśataṃ laṅghayeta plavaṃgamāḥ</li>
        <li id="R_4.063.017cd">imāṃś ca yūthapān sarvān mocayet ko mahābhayāt</li>
    </ul>
    <ul id="R_4.063.018">
        <li id="R_4.063.018ab">kasya prasādād dārāṃś ca putrāṃś caiva gṛhāṇi ca</li>
        <li id="R_4.063.018cd">ito nivṛttāḥ paśyema siddhārthāḥ sukhino vayam</li>
    </ul>
    <ul id="R_4.063.019">
        <li id="R_4.063.019ab">kasya prasādād rāmaṃ ca lakṣmaṇaṃ ca mahābalam</li>
        <li id="R_4.063.019cd">abhigacchema saṃhṛṣṭāḥ sugrīvaṃ ca mahābalam</li>
    </ul>
    <ul id="R_4.063.020">
        <li id="R_4.063.020ab">yadi kaś cit samartho vaḥ sāgaraplavane hariḥ</li>
        <li id="R_4.063.020cd">sa dadātv iha naḥ śīghraṃ puṇyām abhayadakṣiṇām</li>
    </ul>
    <ul id="R_4.063.021">
        <li id="R_4.063.021ab">aṅgadasya vacaḥ śrutvā na kaś cit kiṃ cid abravīt</li>
        <li id="R_4.063.021cd">stimitevābhavat sarvā sā tatra harivāhinī</li>
    </ul>
    <ul id="R_4.063.022">
        <li id="R_4.063.022ab">punar evāṅgadaḥ prāha tān harīn harisattamaḥ</li>
        <li id="R_4.063.022cd">sarve balavatāṃ śreṣṭhā bhavanto dṛḍhavikramāḥ</li>
    </ul>
    <ul id="R_4.063.023">
        <li id="R_4.063.023ab">vyapadeśya kule jātāḥ pūjitāś cāpy abhīkṣṇaśaḥ</li>
        <li id="R_4.063.023cd">na hi vo gamane saṃgaḥ kadā cid api kasya cit</li>
    </ul>
    <ul id="R_4.063.024">
        <li id="R_4.063.024ab">bruvadhvaṃ yasya yā śaktir gamane plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_4.064.001">
        <li id="R_4.064.001ab">tato 'ṅgadavacaḥ śrutvā sarve te vānarottamāḥ</li>
        <li id="R_4.064.001cd">svaṃ svaṃ gatau samutsāham āhus tatra yathākramam</li>
    </ul>
    <ul id="R_4.064.002">
        <li id="R_4.064.002ab">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
        <li id="R_4.064.002cd">maindaś ca dvividaś caiva suṣeṇo jāmbavāṃs tathā</li>
    </ul>
    <ul id="R_4.064.003">
        <li id="R_4.064.003ab">ābabhāṣe gajas tatra plaveyaṃ daśayojanam</li>
        <li id="R_4.064.003cd">gavākṣo yojanāny āha gamiṣyāmīti viṃśatim</li>
    </ul>
    <ul id="R_4.064.004">
        <li id="R_4.064.004ab">gavayo vānaras tatra vānarāṃs tān uvāca ha</li>
        <li id="R_4.064.004cd">triṃśataṃ tu gamiṣyāmi yojanānāṃ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_4.064.005">
        <li id="R_4.064.005ab">śarabho vānaras tatra vānarāṃs tān uvāca ha</li>
        <li id="R_4.064.005cd">catvāriṃśad gamiṣyāmi yojanānāṃ na saṃśayaḥ</li>
    </ul>
    <ul id="R_4.064.006">
        <li id="R_4.064.006ab">vānarāṃs tu mahātejā abravīd gandhamādanaḥ</li>
        <li id="R_4.064.006cd">yojanānāṃ gamiṣyāmi pañcāśat tu na saṃśayaḥ</li>
    </ul>
    <ul id="R_4.064.007">
        <li id="R_4.064.007ab">maindas tu vānaras tatra vānarāṃs tān uvāca ha</li>
        <li id="R_4.064.007cd">yojanānāṃ paraṃ ṣaṣṭim ahaṃ plavitum utsahe</li>
    </ul>
    <ul id="R_4.064.008">
        <li id="R_4.064.008ab">tatas tatra mahātejā dvividaḥ pratyabhāṣata</li>
        <li id="R_4.064.008cd">gamiṣyāmi na saṃdehaḥ saptatiṃ yojanāny aham</li>
    </ul>
    <ul id="R_4.064.009">
        <li id="R_4.064.009ab">suṣeṇas tu hariśreṣṭhaḥ proktavān kapisattamān</li>
        <li id="R_4.064.009cd">aśītiṃ yojanānāṃ tu plaveyaṃ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_4.064.010">
        <li id="R_4.064.010ab">teṣāṃ kathayatāṃ tatra sarvāṃs tān anumānya ca</li>
        <li id="R_4.064.010cd">tato vṛddhatamas teṣāṃ jāmbavān pratyabhāṣata</li>
    </ul>
    <ul id="R_4.064.011">
        <li id="R_4.064.011ab">pūrvam asmākam apy āsīt kaś cid gatiparākramaḥ</li>
        <li id="R_4.064.011cd">te vayaṃ vayasaḥ pāram anuprāptāḥ sma sāmpratam</li>
    </ul>
    <ul id="R_4.064.012">
        <li id="R_4.064.012ab">kiṃ tu naivaṃ gate śakyam idaṃ kāryam upekṣitum</li>
        <li id="R_4.064.012cd">yad arthaṃ kapirājaś ca rāmaś ca kṛtaniścayau</li>
    </ul>
    <ul id="R_4.064.013">
        <li id="R_4.064.013ab">sāmprataṃ kālabhedena yā gatis tāṃ nibodhata</li>
        <li id="R_4.064.013cd">navatiṃ yojanānāṃ tu gamiṣyāmi na saṃśayaḥ</li>
    </ul>
    <ul id="R_4.064.014">
        <li id="R_4.064.014ab">tāṃś ca sarvān hariśreṣṭhāñ jāmbavān punar abravīt</li>
        <li id="R_4.064.014cd">na khalv etāvad evāsīd gamane me parākramaḥ</li>
    </ul>
    <ul id="R_4.064.015">
        <li id="R_4.064.015ab">mayā mahābalaiś caiva yajñe viṣṇuḥ sanātanaḥ</li>
        <li id="R_4.064.015cd">pradakṣiṇīkṛtaḥ pūrvaṃ kramamāṇas trivikramaḥ</li>
    </ul>
    <ul id="R_4.064.016">
        <li id="R_4.064.016ab">sa idānīm ahaṃ vṛddhaḥ plavane mandavikramaḥ</li>
        <li id="R_4.064.016cd">yauvane ca tadāsīn me balam apratimaṃ paraiḥ</li>
    </ul>
    <ul id="R_4.064.017">
        <li id="R_4.064.017ab">saṃpraty etāvatīṃ śaktiṃ gamane tarkayāmy aham</li>
        <li id="R_4.064.017cd">naitāvatā ca saṃsiddhiḥ kāryasyāsya bhaviṣyati</li>
    </ul>
    <ul id="R_4.064.018">
        <li id="R_4.064.018ab">athottaram udārārtham abravīd aṅgadas tadā</li>
        <li id="R_4.064.018cd">anumānya mahāprājño jāmbavantaṃ mahākapim</li>
    </ul>
    <ul id="R_4.064.019">
        <li id="R_4.064.019ab">aham etad gamiṣyāmi yojanānāṃ śataṃ mahat</li>
        <li id="R_4.064.019cd">nivartane tu me śaktiḥ syān na veti na niścitam</li>
    </ul>
    <ul id="R_4.064.020">
        <li id="R_4.064.020ab">tam uvāca hariśreṣṭho jāmbavān vākyakovidaḥ</li>
        <li id="R_4.064.020cd">jñāyate gamane śaktis tava haryṛkṣasattama</li>
    </ul>
    <ul id="R_4.064.021">
        <li id="R_4.064.021ab">kāmaṃ śatasahasraṃ vā na hy eṣa vidhir ucyate</li>
        <li id="R_4.064.021cd">yojanānāṃ bhavāñ śakto gantuṃ pratinivartitum</li>
    </ul>
    <ul id="R_4.064.022">
        <li id="R_4.064.022ab">na hi preṣayitā tata svāmī preṣyaḥ kathaṃ cana</li>
        <li id="R_4.064.022cd">bhavatāyaṃ janaḥ sarvaḥ preṣyaḥ plavagasattama</li>
    </ul>
    <ul id="R_4.064.023">
        <li id="R_4.064.023ab">bhavān kalatram asmākaṃ svāmibhāve vyavasthitaḥ</li>
        <li id="R_4.064.023cd">svāmī kalatraṃ sainyasya gatir eṣā paraṃtapa</li>
    </ul>
    <ul id="R_4.064.024">
        <li id="R_4.064.024ab">tasmāt kalatravat tāta pratipālyaḥ sadā bhavān</li>
        <li id="R_4.064.024cd">api caitasya kāryasya bhavān mūlam ariṃdama</li>
    </ul>
    <ul id="R_4.064.025">
        <li id="R_4.064.025ab">mūlam arthasya saṃrakṣyam eṣa kāryavidāṃ nayaḥ</li>
        <li id="R_4.064.025cd">mūle hi sati sidhyanti guṇāḥ puṣpaphalādayaḥ</li>
    </ul>
    <ul id="R_4.064.026">
        <li id="R_4.064.026ab">tad bhavān asyā kāryasya sādhane satyavikramaḥ</li>
        <li id="R_4.064.026cd">buddhivikramasaṃpanno hetur atra paraṃtapaḥ</li>
    </ul>
    <ul id="R_4.064.027">
        <li id="R_4.064.027ab">guruś ca guruputraś ca tvaṃ hi naḥ kapisattama</li>
        <li id="R_4.064.027cd">bhavantam āśritya vayaṃ samarthā hy arthasādhane</li>
    </ul>
    <ul id="R_4.064.028">
        <li id="R_4.064.028ab">uktavākyaṃ mahāprājñaṃ jāmbavantaṃ mahākapiḥ</li>
        <li id="R_4.064.028cd">pratyuvācottaraṃ vākyaṃ vālisūnur athāṅgadaḥ</li>
    </ul>
    <ul id="R_4.064.029">
        <li id="R_4.064.029ab">yadi nāhaṃ gamiṣyāmi nānyo vānarapuṃgavaḥ</li>
        <li id="R_4.064.029cd">punaḥ khalv idam asmābhiḥ kāryaṃ prāyopaveśanam</li>
    </ul>
    <ul id="R_4.064.030">
        <li id="R_4.064.030ab">na hy akṛtvā haripateḥ saṃdeśaṃ tasya dhīmataḥ</li>
        <li id="R_4.064.030cd">tatrāpi gatvā prāṇānāṃ paśyāmi parirakṣaṇam</li>
    </ul>
    <ul id="R_4.064.031">
        <li id="R_4.064.031ab">sa hi prasāde cātyarthaṃ kope ca harir īśvaraḥ</li>
        <li id="R_4.064.031cd">atītya tasya saṃdeśaṃ vināśo gamane bhavet</li>
    </ul>
    <ul id="R_4.064.032">
        <li id="R_4.064.032ab">tad yathā hy asya kāryasya na bhavaty anyathā gatiḥ</li>
        <li id="R_4.064.032cd">tad bhavān eva dṛṣṭārthaḥ saṃcintayitum arhati</li>
    </ul>
    <ul id="R_4.064.033">
        <li id="R_4.064.033ab">so 'ṅgadena tadā vīraḥ pratyuktaḥ plavagarṣabhaḥ</li>
        <li id="R_4.064.033cd">jāmbavān uttaraṃ vākyaṃ provācedaṃ tato 'ṅgadam</li>
    </ul>
    <ul id="R_4.064.034">
        <li id="R_4.064.034ab">asya te vīra kāryasya na kiṃ cit parihīyate</li>
        <li id="R_4.064.034cd">eṣa saṃcodayāmy enaṃ yaḥ kāryaṃ sādhayiṣyati</li>
    </ul>
    <ul id="R_4.064.035">
        <li id="R_4.064.035ab">
            <seg type="pāda" n="a">tataḥ pratītaṃ plavatāṃ variṣṭham</seg>
            <seg type="pāda" n="b">ekāntam āśritya sukhopaviṣṭam</seg>
        </li>
        <li id="R_4.064.035cd">
            <seg type="pāda" n="c">saṃcodayām āsa haripravīro</seg>
            <seg type="pāda" n="d">haripravīraṃ hanumantam eva</seg>
        </li>
    </ul>
    <ul id="R_4.065.001">
        <li id="R_4.065.001ab">anekaśatasāhasrīṃ viṣaṇṇāṃ harivāhinīm</li>
        <li id="R_4.065.001cd">jāmbavān samudīkṣyaivaṃ hanumantam athābravīt</li>
    </ul>
    <ul id="R_4.065.002">
        <li id="R_4.065.002ab">vīra vānaralokasya sarvaśāstravidāṃ vara</li>
        <li id="R_4.065.002cd">tūṣṇīm ekāntam āśritya hanuman kiṃ na jalpasi</li>
    </ul>
    <ul id="R_4.065.003">
        <li id="R_4.065.003ab">hanuman harirājasya sugrīvasya samo hy asi</li>
        <li id="R_4.065.003cd">rāmalakṣmaṇayoś cāpi tejasā ca balena ca</li>
    </ul>
    <ul id="R_4.065.004">
        <li id="R_4.065.004ab">ariṣṭaneminaḥ putrau vainateyo mahābalaḥ</li>
        <li id="R_4.065.004cd">garutmān iva vikhyāta uttamaḥ sarvapakṣiṇām</li>
    </ul>
    <ul id="R_4.065.005">
        <li id="R_4.065.005ab">bahuśo hi mayā dṛṣṭaḥ sāgare sa mahābalaḥ</li>
        <li id="R_4.065.005cd">bhujagān uddharan pakṣī mahāvego mahāyaśāḥ</li>
    </ul>
    <ul id="R_4.065.006">
        <li id="R_4.065.006ab">pakṣayor yad balaṃ tasya tāvad bhujabalaṃ tava</li>
        <li id="R_4.065.006cd">vikramaś cāpi vegaś ca na te tenāpahīyate</li>
    </ul>
    <ul id="R_4.065.007">
        <li id="R_4.065.007ab">balaṃ buddhiś ca tejaś ca sattvaṃ ca harisattama</li>
        <li id="R_4.065.007cd">viśiṣṭaṃ sarvabhūteṣu kim ātmānaṃ na budhyase</li>
    </ul>
    <ul id="R_4.065.008">
        <li id="R_4.065.008ab">apsarāpsarasāṃ śreṣṭhā vikhyātā puñjikasthalā</li>
        <li id="R_4.065.008cd">ajñaneti parikhyātā patnī kesariṇo hareḥ</li>
    </ul>
    <ul id="R_4.065.009">
        <li id="R_4.065.009ab">abhiśāpād abhūt tāta vānarī kāmarūpiṇī</li>
        <li id="R_4.065.009cd">duhitā vānarendrasya kuñjarasya mahātmanaḥ</li>
    </ul>
    <ul id="R_4.065.010">
        <li id="R_4.065.010ab">kapitve cārusarvāṅgī kadā cit kāmarūpiṇī</li>
        <li id="R_4.065.010cd">mānuṣaṃ vigrahaṃ kṛtvā yauvanottamaśālinī</li>
    </ul>
    <ul id="R_4.065.011">
        <li id="R_4.065.011ab">acarat parvatasyāgre prāvṛḍambudasaṃnibhe</li>
        <li id="R_4.065.011cd">vicitramālyābharaṇā mahārhakṣaumavāsinī</li>
    </ul>
    <ul id="R_4.065.012">
        <li id="R_4.065.012ab">tasyā vastraṃ viśālākṣyāḥ pītaṃ raktadaśaṃ śubham</li>
        <li id="R_4.065.012cd">sthitāyāḥ parvatasyāgre māruto 'paharac chanaiḥ</li>
    </ul>
    <ul id="R_4.065.013">
        <li id="R_4.065.013ab">sa dadarśa tatas tasyā vṛttāv ūrū susaṃhatau</li>
        <li id="R_4.065.013cd">stanau ca pīnau sahitau sujātaṃ cāru cānanam</li>
    </ul>
    <ul id="R_4.065.014">
        <li id="R_4.065.014ab">tāṃ viśālāyataśroṇīṃ tanumadhyāṃ yaśasvinīm</li>
        <li id="R_4.065.014cd">dṛṣṭvaiva śubhasarvāgnīṃ pavanaḥ kāmamohitaḥ</li>
    </ul>
    <ul id="R_4.065.015">
        <li id="R_4.065.015ab">sa tāṃ bhujābhyāṃ pīnābhyāṃ paryaṣvajata mārutaḥ</li>
        <li id="R_4.065.015cd">manmathāviṣṭasarvāṅgo gatātmā tām aninditām</li>
    </ul>
    <ul id="R_4.065.016">
        <li id="R_4.065.016ab">sā tu tatraiva saṃbhrāntā suvṛttā vākyam abravīt</li>
        <li id="R_4.065.016cd">ekapatnīvratam idaṃ ko nāśayitum icchati</li>
    </ul>
    <ul id="R_4.065.017">
        <li id="R_4.065.017ab">añjanāyā vacaḥ śrutvā mārutaḥ pratyabhāṣata</li>
        <li id="R_4.065.017cd">na tvāṃ hiṃsāmi suśroṇi mā bhūt te subhage bhayam</li>
    </ul>
    <ul id="R_4.065.018">
        <li id="R_4.065.018ab">manasāsmi gato yat tvāṃ pariṣvajya yaśasvini</li>
        <li id="R_4.065.018cd">vīryavān buddhisaṃpannaḥ putras tava bhaviṣyati</li>
    </ul>
    <ul id="R_4.065.019">
        <li id="R_4.065.019ab">abhyutthitaṃ tataḥ sūryaṃ bālo dṛṣṭvā mahāvane</li>
        <li id="R_4.065.019cd">phalaṃ ceti jighṛkṣus tvam utplutyābhyapato divam</li>
    </ul>
    <ul id="R_4.065.020">
        <li id="R_4.065.020ab">śatāni trīṇi gatvātha yojanānāṃ mahākape</li>
        <li id="R_4.065.020cd">tejasā tasya nirdhūto na viṣādaṃ tato gataḥ</li>
    </ul>
    <ul id="R_4.065.021">
        <li id="R_4.065.021ab">tāvad āpatatas tūrṇam antarikṣaṃ mahākape</li>
        <li id="R_4.065.021cd">kṣiptam indreṇa te vajraṃ krodhāviṣṭena dhīmatā</li>
    </ul>
    <ul id="R_4.065.022">
        <li id="R_4.065.022ab">tataḥ śailāgraśikhare vāmo hanur abhajyata</li>
        <li id="R_4.065.022cd">tato hi nāmadheyaṃ te hanumān iti kīrtyate</li>
    </ul>
    <ul id="R_4.065.023">
        <li id="R_4.065.023ab">tatas tvāṃ nihataṃ dṛṣṭvā vāyur gandhavahaḥ svayam</li>
        <li id="R_4.065.023cd">trailokye bhṛśasaṃkruddho na vavau vai prabhañjanaḥ</li>
    </ul>
    <ul id="R_4.065.024">
        <li id="R_4.065.024ab">saṃbhrāntāś ca surāḥ sarve trailokye kṣubhite sati</li>
        <li id="R_4.065.024cd">prasādayanti saṃkruddhaṃ mārutaṃ bhuvaneśvarāḥ</li>
    </ul>
    <ul id="R_4.065.025">
        <li id="R_4.065.025ab">prasādite ca pavane brahmā tubhyaṃ varaṃ dadau</li>
        <li id="R_4.065.025cd">aśastravadhyatāṃ tāta samare satyavikrama</li>
    </ul>
    <ul id="R_4.065.026">
        <li id="R_4.065.026ab">vajrasya ca nipātena virujaṃ tvāṃ samīkṣya ca</li>
        <li id="R_4.065.026cd">sahasranetraḥ prītātmā dadau te varam uttamam</li>
    </ul>
    <ul id="R_4.065.027">
        <li id="R_4.065.027ab">svacchandataś ca maraṇaṃ te bhūyād iti vai prabho</li>
        <li id="R_4.065.027cd">sa tvaṃ kesariṇaḥ putraḥ kṣetrajo bhīmavikramaḥ</li>
    </ul>
    <ul id="R_4.065.028">
        <li id="R_4.065.028ab">mārutasyaurasaḥ putras tejasā cāpi tatsamaḥ</li>
        <li id="R_4.065.028cd">tvaṃ hi vāyusuto vatsa plavane cāpi tatsamaḥ</li>
    </ul>
    <ul id="R_4.065.029">
        <li id="R_4.065.029ab">vayam adya gataprāṇā bhavān asmāsu sāmpratam</li>
        <li id="R_4.065.029cd">dākṣyavikramasaṃpannaḥ pakṣirāja ivāparaḥ</li>
    </ul>
    <ul id="R_4.065.030">
        <li id="R_4.065.030ab">trivikrame mayā tāta saśailavanakānanā</li>
        <li id="R_4.065.030cd">triḥ saptakṛtvaḥ pṛthivī parikrāntā pradakṣiṇam</li>
    </ul>
    <ul id="R_4.065.031">
        <li id="R_4.065.031ab">tadā cauṣadhayo 'smābhiḥ saṃcitā devaśāsanāt</li>
        <li id="R_4.065.031cd">niṣpannam amṛtaṃ yābhis tadāsīn no mahad balam</li>
    </ul>
    <ul id="R_4.065.032">
        <li id="R_4.065.032ab">sa idānīm ahaṃ vṛddhaḥ parihīnaparākramaḥ</li>
        <li id="R_4.065.032cd">sāmprataṃ kālam asmākaṃ bhavān sarvaguṇānvitaḥ</li>
    </ul>
    <ul id="R_4.065.033">
        <li id="R_4.065.033ab">tad vijṛmbhasva vikrāntaḥ plavatām uttamo hy asi</li>
        <li id="R_4.065.033cd">tvadvīryaṃ draṣṭukāmeyaṃ sarvā vānaravāhinī</li>
    </ul>
    <ul id="R_4.065.034">
        <li id="R_4.065.034ab">uttiṣṭha hariśārdūla laṅghayasva mahārṇavam</li>
        <li id="R_4.065.034cd">parā hi sarvabhūtānāṃ hanuman yā gatis tava</li>
    </ul>
    <ul id="R_4.065.035">
        <li id="R_4.065.035ab">viṣāṇṇā harayaḥ sarve hanuman kim upekṣase</li>
        <li id="R_4.065.035cd">vikramasva mahāvego viṣṇus trīn vikramān iva</li>
    </ul>
    <ul id="R_4.065.036">
        <li id="R_4.065.036ab">
            <seg type="pāda" n="a">tatas tu vai jāmbavatābhicoditaḥ</seg>
            <seg type="pāda" n="b">pratītavegaḥ pavanātmajaḥ kapiḥ</seg>
        </li>
        <li id="R_4.065.036cd">
            <seg type="pāda" n="c">praharṣayaṃs tāṃ harivīra vāhinīṃ</seg>
            <seg type="pāda" n="d">cakāra rūpaṃ mahad ātmanas tadā</seg>
        </li>
    </ul>
    <ul id="R_4.066.001">
        <li id="R_4.066.001ab">saṃstūyamāno hanumān vyavardhata mahābalaḥ</li>
        <li id="R_4.066.001cd">samāvidhya ca lāṅgūlaṃ harṣāc ca balam eyivān</li>
    </ul>
    <ul id="R_4.066.002">
        <li id="R_4.066.002ab">tasya saṃstūyamānasya sarvair vānarapuṃgavaiḥ</li>
        <li id="R_4.066.002cd">tejasāpūryamāṇasya rūpam āsīd anuttamam</li>
    </ul>
    <ul id="R_4.066.003">
        <li id="R_4.066.003ab">yathā vijṛmbhate siṃho vivṛddho girigahvare</li>
        <li id="R_4.066.003cd">mārutasyaurasaḥ putras tathā saṃprati jṛmbhate</li>
    </ul>
    <ul id="R_4.066.004">
        <li id="R_4.066.004ab">aśobhata mukhaṃ tasya jṛmbhamāṇasya dhīmataḥ</li>
        <li id="R_4.066.004cd">ambarīṣopamaṃ dīptaṃ vidhūma iva pāvakaḥ</li>
    </ul>
    <ul id="R_4.066.005">
        <li id="R_4.066.005ab">harīṇām utthito madhyāt saṃprahṛṣṭatanūruhaḥ</li>
        <li id="R_4.066.005cd">abhivādya harīn vṛddhān hanumān idam abravīt</li>
    </ul>
    <ul id="R_4.066.006">
        <li id="R_4.066.006ab">arujan parvatāgrāṇi hutāśanasakho 'nilaḥ</li>
        <li id="R_4.066.006cd">balavān aprameyaś ca vāyur ākāśagocaraḥ</li>
    </ul>
    <ul id="R_4.066.007">
        <li id="R_4.066.007ab">tasyāhaṃ śīghravegasya śīghragasya mahātmanaḥ</li>
        <li id="R_4.066.007cd">mārutasyaurasaḥ putraḥ plavane nāsti me samaḥ</li>
    </ul>
    <ul id="R_4.066.008">
        <li id="R_4.066.008ab">utsaheyaṃ hi vistīrṇam ālikhantam ivāmbaram</li>
        <li id="R_4.066.008cd">meruṃ girim asaṃgena parigantuṃ sahasraśaḥ</li>
    </ul>
    <ul id="R_4.066.009">
        <li id="R_4.066.009ab">bāhuvegapraṇunnena sāgareṇāham utsahe</li>
        <li id="R_4.066.009cd">samāplāvayituṃ lokaṃ saparvatanadīhradam</li>
    </ul>
    <ul id="R_4.066.010">
        <li id="R_4.066.010ab">mamorujaṅghāvegena bhaviṣyati samutthitaḥ</li>
        <li id="R_4.066.010cd">saṃmūrchitamahāgrāhaḥ samudro varuṇālayaḥ</li>
    </ul>
    <ul id="R_4.066.011">
        <li id="R_4.066.011ab">pannagāśanam ākāśe patantaṃ pakṣisevitam</li>
        <li id="R_4.066.011cd">vainateyam ahaṃ śaktaḥ parigantuṃ sahasraśaḥ</li>
    </ul>
    <ul id="R_4.066.012">
        <li id="R_4.066.012ab">udayāt prasthitaṃ vāpi jvalantaṃ raśmimālinam</li>
        <li id="R_4.066.012cd">anastamitam ādityam abhigantuṃ samutsahe</li>
    </ul>
    <ul id="R_4.066.013">
        <li id="R_4.066.013ab">tato bhūmim asaṃspṛśya punar āgantum utsahe</li>
        <li id="R_4.066.013cd">pravegenaiva mahatā bhīmena plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_4.066.014">
        <li id="R_4.066.014ab">utsaheyam atikrāntuṃ sarvān ākāśagocarān</li>
        <li id="R_4.066.014cd">sāgaraṃ kṣobhayiṣyāmi dārayiṣyāmi medinīm</li>
    </ul>
    <ul id="R_4.066.015">
        <li id="R_4.066.015ab">parvatān kampayiṣyāmi plavamānaḥ plavaṃgamāḥ</li>
        <li id="R_4.066.015cd">hariṣye coruvegena plavamāno mahārṇavam</li>
    </ul>
    <ul id="R_4.066.016">
        <li id="R_4.066.016ab">latānāṃ vīrudhāṃ puṣpaṃ pādapānāṃ ca sarvaśaḥ</li>
        <li id="R_4.066.016cd">anuyāsyati mām adya plavamānaṃ vihāyasā</li>
        <li id="R_4.066.016ef">bhaviṣyati hi me panthāḥ svāteḥ panthā ivāmbare</li>
    </ul>
    <ul id="R_4.066.017">
        <li id="R_4.066.017ab">carantaṃ ghoram ākāśam utpatiṣyantam eva ca</li>
        <li id="R_4.066.017cd">drakṣyanti nipatantaṃ ca sarvabhūtāni vānarāḥ</li>
    </ul>
    <ul id="R_4.066.018">
        <li id="R_4.066.018ab">mahāmerupratīkāśaṃ māṃ drakṣyadhvaṃ plavaṃgamāḥ</li>
        <li id="R_4.066.018cd">divam āvṛtya gacchantaṃ grasamānam ivāmbaram</li>
    </ul>
    <ul id="R_4.066.019">
        <li id="R_4.066.019ab">vidhamiṣyāmi jīmūtān kampayiṣyāmi parvatān</li>
        <li id="R_4.066.019cd">sāgaraṃ kṣobhayiṣyāmi plavamānaḥ samāhitaḥ</li>
    </ul>
    <ul id="R_4.066.020">
        <li id="R_4.066.020ab">vainateyasya vā śaktir mama vā mārutasya vā</li>
        <li id="R_4.066.020cd">ṛte suparṇarājānaṃ mārutaṃ vā mahābalam</li>
        <li id="R_4.066.020ef">na hi bhūtaṃ prapaśyāmi yo māṃ plutam anuvrajet</li>
    </ul>
    <ul id="R_4.066.021">
        <li id="R_4.066.021ab">nimeṣāntaramātreṇa nirālambhanam ambaram</li>
        <li id="R_4.066.021cd">sahasā nipatiṣyāmi ghanād vidyud ivotthitā</li>
    </ul>
    <ul id="R_4.066.022">
        <li id="R_4.066.022ab">bhaviṣyati hi me rūpaṃ plavamānasya sāgaram</li>
        <li id="R_4.066.022cd">viṣṇoḥ prakramamāṇasya tadā trīn vikramān iva</li>
    </ul>
    <ul id="R_4.066.023">
        <li id="R_4.066.023ab">buddhyā cāhaṃ prapaśyāmi manaś ceṣṭā ca me tathā</li>
        <li id="R_4.066.023cd">ahaṃ drakṣyāmi vaidehīṃ pramodadhvaṃ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_4.066.024">
        <li id="R_4.066.024ab">mārutasya samo vege garuḍasya samo jave</li>
        <li id="R_4.066.024cd">ayutaṃ yojanānāṃ tu gamiṣyāmīti me matiḥ</li>
    </ul>
    <ul id="R_4.066.025">
        <li id="R_4.066.025ab">vāsavasya savajrasya brahmaṇo vā svayambhuvaḥ</li>
        <li id="R_4.066.025cd">vikramya sahasā hastād amṛtaṃ tad ihānaye</li>
        <li id="R_4.066.025ef">laṅkāṃ vāpi samutkṣipya gaccheyam iti me matiḥ</li>
    </ul>
    <ul id="R_4.066.026">
        <li id="R_4.066.026ab">tam evaṃ vānaraśreṣṭhaṃ garjantam amitaujasaṃ</li>
        <li id="R_4.066.026cd">uvāca parisaṃhṛṣṭo jāmbavān harisattamaḥ</li>
    </ul>
    <ul id="R_4.066.027">
        <li id="R_4.066.027ab">vīra kesariṇaḥ putra vegavan mārutātmaja</li>
        <li id="R_4.066.027cd">jñātīnāṃ vipulaṃ śokas tvayā tāta praṇāśitaḥ</li>
    </ul>
    <ul id="R_4.066.028">
        <li id="R_4.066.028ab">tava kalyāṇarucayaḥ kapimukhyāḥ samāgatāḥ</li>
        <li id="R_4.066.028cd">maṅgalaṃ kāryasiddhyarthaṃ kariṣyanti samāhitāḥ</li>
    </ul>
    <ul id="R_4.066.029">
        <li id="R_4.066.029ab">ṛṣīṇāṃ ca prasādena kapivṛddhamatena ca</li>
        <li id="R_4.066.029cd">gurūṇāṃ ca prasādena plavasva tvaṃ mahārṇavam</li>
    </ul>
    <ul id="R_4.066.030">
        <li id="R_4.066.030ab">sthāsyāmaś caikapādena yāvadāgamanaṃ tava</li>
        <li id="R_4.066.030cd">tvadgatāni ca sarveṣāṃ jīvitāni vanaukasām</li>
    </ul>
    <ul id="R_4.066.031">
        <li id="R_4.066.031ab">tatas tu hariśārdūlas tān uvāca vanaukasaḥ</li>
        <li id="R_4.066.031cd">neyaṃ mama mahī vegaṃ plavane dhārayiṣyati</li>
    </ul>
    <ul id="R_4.066.032">
        <li id="R_4.066.032ab">etāni hi nagasyāsya śilāsaṃkaṭaśālinaḥ</li>
        <li id="R_4.066.032cd">śikharāṇi mahendrasya sthirāṇi ca mahānti ca</li>
    </ul>
    <ul id="R_4.066.033">
        <li id="R_4.066.033ab">etāni mama niṣpeṣaṃ pādayoḥ patatāṃ varāḥ</li>
        <li id="R_4.066.033cd">plavato dhārayiṣyanti yojanānām itaḥ śatam</li>
    </ul>
    <ul id="R_4.066.034">
        <li id="R_4.066.034ab">tatas tu mārutaprakhyaḥ sa harir mārutātmajaḥ</li>
        <li id="R_4.066.034cd">āruroha nagaśreṣṭhaṃ mahendram arimardanaḥ</li>
    </ul>
    <ul id="R_4.066.035">
        <li id="R_4.066.035ab">vṛtaṃ nānāvidhair vṛkṣair mṛgasevitaśādvalam</li>
        <li id="R_4.066.035cd">latākusumasaṃbādhaṃ nityapuṣpaphaladrumam</li>
    </ul>
    <ul id="R_4.066.036">
        <li id="R_4.066.036ab">siṃhaśārdūlacaritaṃ mattamātaṅgasevitam</li>
        <li id="R_4.066.036cd">mattadvijagaṇodghuṣṭaṃ salilotpīḍasaṃkulam</li>
    </ul>
    <ul id="R_4.066.037">
        <li id="R_4.066.037ab">mahadbhir ucchritaṃ śṛṅgair mahendraṃ sa mahābalaḥ</li>
        <li id="R_4.066.037cd">vicacāra hariśreṣṭho mahendrasamavikramaḥ</li>
    </ul>
    <ul id="R_4.066.038">
        <li id="R_4.066.038ab">pādābhyāṃ pīḍitas tena mahāśailo mahātmanā</li>
        <li id="R_4.066.038cd">rarāsa siṃhābhihato mahān matta iva dvipaḥ</li>
    </ul>
    <ul id="R_4.066.039">
        <li id="R_4.066.039ab">mumoca salilotpīḍān viprakīrṇaśiloccayaḥ</li>
        <li id="R_4.066.039cd">vitrastamṛgamātaṅgaḥ prakampitamahādrumaḥ</li>
    </ul>
    <ul id="R_4.066.040">
        <li id="R_4.066.040ab">nānāgandharvamithunaiḥ pānasaṃsargakarkaśaiḥ</li>
        <li id="R_4.066.040cd">utpatadbhir vihaṃgaiś ca vidyādharagaṇair api</li>
    </ul>
    <ul id="R_4.066.041">
        <li id="R_4.066.041ab">tyajyamānamahāsānuḥ saṃnilīnamahoragaḥ</li>
        <li id="R_4.066.041cd">śailaśṛṅgaśilodghātas tadābhūt sa mahāgiriḥ</li>
    </ul>
    <ul id="R_4.066.042">
        <li id="R_4.066.042ab">niḥśvasadbhis tadā tais tu bhujagair ardhaniḥsṛtaiḥ</li>
        <li id="R_4.066.042cd">sapatāka ivābhāti sa tadā dharaṇīdharaḥ</li>
    </ul>
    <ul id="R_4.066.043">
        <li id="R_4.066.043ab">ṛṣibhis trāsa saṃbhrāntais tyajyamānaḥ śiloccayaḥ</li>
        <li id="R_4.066.043cd">sīdan mahati kāntāre sārthahīna ivādhvagaḥ</li>
    </ul>
    <ul id="R_4.066.044">
        <li id="R_4.066.044ab">
            <seg type="pāda" n="a">sa vegavān vegasamāhitātmā</seg>
            <seg type="pāda" n="b">haripravīraḥ paravīrahantā</seg>
        </li>
        <li id="R_4.066.044cd">
            <seg type="pāda" n="c">manaḥ samādhāya mahānubhāvo</seg>
            <seg type="pāda" n="d">jagāma laṅkāṃ manasā manasvī</seg>
        </li>
    </ul>
	{/if}
</div>
<div class="kanda-5">
<h4>Kāṇḍa 5</h4>
		{#if kanda5}
    <ul id="R_5.001.001">
        <li id="R_5.001.001ab">tato rāvaṇanītāyāḥ sītāyāḥ śatrukarśanaḥ</li>
        <li id="R_5.001.001cd">iyeṣa padam anveṣṭuṃ cāraṇācarite pathi</li>
    </ul>
    <ul id="R_5.001.002">
        <li id="R_5.001.002ab">atha vaidūryavarṇeṣu śādvaleṣu mahābalaḥ</li>
        <li id="R_5.001.002cd">dhīraḥ salilakalpeṣu vicacāra yathāsukham</li>
    </ul>
    <ul id="R_5.001.003">
        <li id="R_5.001.003ab">dvijān vitrāsayan dhīmān urasā pādapān haran</li>
        <li id="R_5.001.003cd">mṛgāṃś ca subahūn nighnan pravṛddha iva kesarī</li>
    </ul>
    <ul id="R_5.001.004">
        <li id="R_5.001.004ab">nīlalohitamāñjiṣṭhapadmavarṇaiḥ sitāsitaiḥ</li>
        <li id="R_5.001.004cd">svabhāvavihitaiś citrair dhātubhiḥ samalaṃkṛtam</li>
    </ul>
    <ul id="R_5.001.005">
        <li id="R_5.001.005ab">kāmarūpibhir āviṣṭam abhīkṣṇaṃ saparicchadaiḥ</li>
        <li id="R_5.001.005cd">yakṣakiṃnaragandharvair devakalpaiś ca pannagaiḥ</li>
    </ul>
    <ul id="R_5.001.006">
        <li id="R_5.001.006ab">sa tasya girivaryasya tale nāgavarāyute</li>
        <li id="R_5.001.006cd">tiṣṭhan kapivaras tatra hrade nāga ivābabhau</li>
    </ul>
    <ul id="R_5.001.007">
        <li id="R_5.001.007ab">sa sūryāya mahendrāya pavanāya svayambhuve</li>
        <li id="R_5.001.007cd">bhūtebhyaś cāñjaliṃ kṛtvā cakāra gamane matim</li>
    </ul>
    <ul id="R_5.001.008">
        <li id="R_5.001.008ab">añjaliṃ prāṅmukhaḥ kurvan pavanāyātmayonaye</li>
        <li id="R_5.001.008cd">tato hi vavṛdhe gantuṃ dakṣiṇo dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_5.001.009">
        <li id="R_5.001.009ab">plavaṃgapravarair dṛṣṭaḥ plavane kṛtaniścayaḥ</li>
        <li id="R_5.001.009cd">vavṛdhe rāmavṛddhyarthaṃ samudra iva parvasu</li>
    </ul>
    <ul id="R_5.001.010">
        <li id="R_5.001.010ab">niṣpramāṇa śarīraḥ saṃl lilaṅghayiṣur arṇavam</li>
        <li id="R_5.001.010cd">bāhubhyāṃ pīḍayām āsa caraṇābhyāṃ ca parvatam</li>
    </ul>
    <ul id="R_5.001.011">
        <li id="R_5.001.011ab">sa cacālācalāś cāru muhūrtaṃ kapipīḍitaḥ</li>
        <li id="R_5.001.011cd">tarūṇāṃ puṣpitāgrāṇāṃ sarvaṃ puṣpam aśātayat</li>
    </ul>
    <ul id="R_5.001.012">
        <li id="R_5.001.012ab">tena pādapamuktena puṣpaugheṇa sugandhinā</li>
        <li id="R_5.001.012cd">sarvataḥ saṃvṛtaḥ śailo babhau puṣpamayo yathā</li>
    </ul>
    <ul id="R_5.001.013">
        <li id="R_5.001.013ab">tena cottamavīryeṇa pīḍyamānaḥ sa parvataḥ</li>
        <li id="R_5.001.013cd">salilaṃ saṃprasusrāva madaṃ matta iva dvipaḥ</li>
    </ul>
    <ul id="R_5.001.014">
        <li id="R_5.001.014ab">pīḍyamānas tu balinā mahendras tena parvataḥ</li>
        <li id="R_5.001.014cd">rītir nirvartayām āsa kāñcanāñjanarājatīḥ</li>
        <li id="R_5.001.014ef">mumoca ca śilāḥ śailo viśālāḥ samanaḥśilāḥ</li>
    </ul>
    <ul id="R_5.001.015">
        <li id="R_5.001.015ab">giriṇā pīḍyamānena pīḍyamānāni sarvaśaḥ</li>
        <li id="R_5.001.015cd">guhāviṣṭāni bhūtāni vinedur vikṛtaiḥ svaraiḥ</li>
    </ul>
    <ul id="R_5.001.016">
        <li id="R_5.001.016ab">sa mahāsattvasaṃnādaḥ śailapīḍānimittajaḥ</li>
        <li id="R_5.001.016cd">pṛthivīṃ pūrayām āsa diśaś copavanāni ca</li>
    </ul>
    <ul id="R_5.001.017">
        <li id="R_5.001.017ab">śirobhiḥ pṛthubhiḥ sarpā vyaktasvastikalakṣaṇaiḥ</li>
        <li id="R_5.001.017cd">vamantaḥ pāvakaṃ ghoraṃ dadaṃśur daśanaiḥ śilāḥ</li>
    </ul>
    <ul id="R_5.001.018">
        <li id="R_5.001.018ab">tās tadā saviṣair daṣṭāḥ kupitais tair mahāśilāḥ</li>
        <li id="R_5.001.018cd">jajvaluḥ pāvakoddīptā vibhiduś ca sahasradhā</li>
    </ul>
    <ul id="R_5.001.019">
        <li id="R_5.001.019ab">yāni cauṣadhajālāni tasmiñ jātāni parvate</li>
        <li id="R_5.001.019cd">viṣaghnāny api nāgānāṃ na śekuḥ śamituṃ viṣam</li>
    </ul>
    <ul id="R_5.001.020">
        <li id="R_5.001.020ab">bhidyate 'yaṃ girir bhūtair iti matvā tapasvinaḥ</li>
        <li id="R_5.001.020cd">trastā vidyādharās tasmād utpetuḥ strīgaṇaiḥ saha</li>
    </ul>
    <ul id="R_5.001.021">
        <li id="R_5.001.021ab">pānabhūmigataṃ hitvā haimam āsanabhājanam</li>
        <li id="R_5.001.021cd">pātrāṇi ca mahārhāṇi karakāṃś ca hiraṇmayān</li>
    </ul>
    <ul id="R_5.001.022">
        <li id="R_5.001.022ab">lehyān uccāvacān bhakṣyān māṃsāni vividhāni ca</li>
        <li id="R_5.001.022cd">ārṣabhāṇi ca carmāṇi khaḍgāṃś ca kanakatsarūn</li>
    </ul>
    <ul id="R_5.001.023">
        <li id="R_5.001.023ab">kṛtakaṇṭhaguṇāḥ kṣībā raktamālyānulepanāḥ</li>
        <li id="R_5.001.023cd">raktākṣāḥ puṣkarākṣāś ca gaganaṃ pratipedire</li>
    </ul>
    <ul id="R_5.001.024">
        <li id="R_5.001.024ab">hāranūpurakeyūra pārihārya dharāḥ striyaḥ</li>
        <li id="R_5.001.024cd">vismitāḥ sasmitās tasthur ākāśe ramaṇaiḥ saha</li>
    </ul>
    <ul id="R_5.001.025">
        <li id="R_5.001.025ab">darśayanto mahāvidyāṃ vidyādharamaharṣayaḥ</li>
        <li id="R_5.001.025cd">sahitās tasthur ākāśe vīkṣāṃ cakruś ca parvatam</li>
    </ul>
    <ul id="R_5.001.026">
        <li id="R_5.001.026ab">śuśruvuś ca tadā śabdam ṛṣīṇāṃ bhāvitātmanām</li>
        <li id="R_5.001.026cd">cāraṇānāṃ ca siddhānāṃ sthitānāṃ vimale 'mbare</li>
    </ul>
    <ul id="R_5.001.027">
        <li id="R_5.001.027ab">eṣa parvatasaṃkāśo hanūmān mārutātmajaḥ</li>
        <li id="R_5.001.027cd">titīrṣati mahāvegaṃ samudraṃ makarālayam</li>
    </ul>
    <ul id="R_5.001.028">
        <li id="R_5.001.028ab">rāmārthaṃ vānarārthaṃ ca cikīrṣan karma duṣkaram</li>
        <li id="R_5.001.028cd">samudrasya paraṃ pāraṃ duṣprāpaṃ prāptum icchati</li>
    </ul>
    <ul id="R_5.001.029">
        <li id="R_5.001.029ab">dudhuve ca sa romāṇi cakampe cācalopamaḥ</li>
        <li id="R_5.001.029cd">nanāda ca mahānādaṃ sumahān iva toyadaḥ</li>
    </ul>
    <ul id="R_5.001.030">
        <li id="R_5.001.030ab">ānupūrvyāc ca vṛttaṃ ca lāṅgūlaṃ romabhiś citam</li>
        <li id="R_5.001.030cd">utpatiṣyan vicikṣepa pakṣirāja ivoragam</li>
    </ul>
    <ul id="R_5.001.031">
        <li id="R_5.001.031ab">tasya lāṅgūlam āviddham ativegasya pṛṣṭhataḥ</li>
        <li id="R_5.001.031cd">dadṛśe garuḍeneva hriyamāṇo mahoragaḥ</li>
    </ul>
    <ul id="R_5.001.032">
        <li id="R_5.001.032ab">bāhū saṃstambhayām āsa mahāparighasaṃnibhau</li>
        <li id="R_5.001.032cd">sasāda ca kapiḥ kaṭyāṃ caraṇau saṃcukopa ca</li>
    </ul>
    <ul id="R_5.001.033">
        <li id="R_5.001.033ab">saṃhṛtya ca bhujau śrīmāṃs tathaiva ca śirodharām</li>
        <li id="R_5.001.033cd">tejaḥ sattvaṃ tathā vīryam āviveśa sa vīryavān</li>
    </ul>
    <ul id="R_5.001.034">
        <li id="R_5.001.034ab">mārgam ālokayan dūrād ūrdhvapraṇihitekṣaṇaḥ</li>
        <li id="R_5.001.034cd">rurodha hṛdaye prāṇān ākāśam avalokayan</li>
    </ul>
    <ul id="R_5.001.035">
        <li id="R_5.001.035ab">padbhyāṃ dṛḍham avasthānaṃ kṛtvā sa kapikuñjaraḥ</li>
        <li id="R_5.001.035cd">nikuñcya karṇau hanumān utpatiṣyan mahābalaḥ</li>
        <li id="R_5.001.035ef">vānarān vānaraśreṣṭha idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.001.036">
        <li id="R_5.001.036ab">yathā rāghavanirmuktaḥ śaraḥ śvasanavikramaḥ</li>
        <li id="R_5.001.036cd">gacchet tadvad gamiṣyāmi laṅkāṃ rāvaṇapālitām</li>
    </ul>
    <ul id="R_5.001.037">
        <li id="R_5.001.037ab">na hi drakṣyāmi yadi tāṃ laṅkāyāṃ janakātmajām</li>
        <li id="R_5.001.037cd">anenaiva hi vegena gamiṣyāmi surālayam</li>
    </ul>
    <ul id="R_5.001.038">
        <li id="R_5.001.038ab">yadi vā tridive sītāṃ na drakṣyāmi kṛtaśramaḥ</li>
        <li id="R_5.001.038cd">baddhvā rākṣasarājānam ānayiṣyāmi rāvaṇam</li>
    </ul>
    <ul id="R_5.001.039">
        <li id="R_5.001.039ab">sarvathā kṛtakāryo 'ham eṣyāmi saha sītayā</li>
        <li id="R_5.001.039cd">ānayiṣyāmi vā laṅkāṃ samutpāṭya sarāvaṇām</li>
    </ul>
    <ul id="R_5.001.040">
        <li id="R_5.001.040ab">evam uktvā tu hanumān vānarān vānarottamaḥ</li>
        <li id="R_5.001.040cd">utpapātātha vegena vegavān avicārayan</li>
    </ul>
    <ul id="R_5.001.041">
        <li id="R_5.001.041ab">samutpatati tasmiṃs tu vegāt te nagarohiṇaḥ</li>
        <li id="R_5.001.041cd">saṃhṛtya viṭapān sarvān samutpetuḥ samantataḥ</li>
    </ul>
    <ul id="R_5.001.042">
        <li id="R_5.001.042ab">sa mattakoyaṣṭibhakān pādapān puṣpaśālinaḥ</li>
        <li id="R_5.001.042cd">udvahann ūruvegena jagāma vimale 'mbare</li>
    </ul>
    <ul id="R_5.001.043">
        <li id="R_5.001.043ab">ūruvegoddhatā vṛkṣā muhūrtaṃ kapim anvayuḥ</li>
        <li id="R_5.001.043cd">prasthitaṃ dīrgham adhvānaṃ svabandhum iva bāndhavāḥ</li>
    </ul>
    <ul id="R_5.001.044">
        <li id="R_5.001.044ab">tam ūruvegonmathitāḥ sālāś cānye nagottamāḥ</li>
        <li id="R_5.001.044cd">anujagmur hanūmantaṃ sainyā iva mahīpatim</li>
    </ul>
    <ul id="R_5.001.045">
        <li id="R_5.001.045ab">supuṣpitāgrair bahubhiḥ pādapair anvitaḥ kapiḥ</li>
        <li id="R_5.001.045cd">hanumān parvatākāro babhūvādbhutadarśanaḥ</li>
    </ul>
    <ul id="R_5.001.046">
        <li id="R_5.001.046ab">sāravanto 'tha ye vṛkṣā nyamajjaṃl lavaṇāmbhasi</li>
        <li id="R_5.001.046cd">bhayād iva mahendrasya parvatā varuṇālaye</li>
    </ul>
    <ul id="R_5.001.047">
        <li id="R_5.001.047ab">sa nānākusumaiḥ kīrṇaḥ kapiḥ sāṅkurakorakaiḥ</li>
        <li id="R_5.001.047cd">śuśubhe meghasaṃkāśaḥ khadyotair iva parvataḥ</li>
    </ul>
    <ul id="R_5.001.048">
        <li id="R_5.001.048ab">vimuktās tasya vegena muktvā puṣpāṇi te drumāḥ</li>
        <li id="R_5.001.048cd">avaśīryanta salile nivṛttāḥ suhṛdo yathā</li>
    </ul>
    <ul id="R_5.001.049">
        <li id="R_5.001.049ab">laghutvenopapannaṃ tad vicitraṃ sāgare 'patat</li>
        <li id="R_5.001.049cd">drumāṇāṃ vividhaṃ puṣpaṃ kapivāyusamīritam</li>
    </ul>
    <ul id="R_5.001.050">
        <li id="R_5.001.050ab">puṣpaugheṇānubaddhena nānāvarṇena vānaraḥ</li>
        <li id="R_5.001.050cd">babhau megha ivodyan vai vidyudgaṇavibhūṣitaḥ</li>
    </ul>
    <ul id="R_5.001.051">
        <li id="R_5.001.051ab">tasya vegasamudbhūtaiḥ puṣpais toyam adṛśyata</li>
        <li id="R_5.001.051cd">tārābhir abhirāmābhir uditābhir ivāmbaram</li>
    </ul>
    <ul id="R_5.001.052">
        <li id="R_5.001.052ab">tasyāmbaragatau bāhū dadṛśāte prasāritau</li>
        <li id="R_5.001.052cd">parvatāgrād viniṣkrāntau pañcāsyāv iva pannagau</li>
    </ul>
    <ul id="R_5.001.053">
        <li id="R_5.001.053ab">pibann iva babhau cāpi sormijālaṃ mahārṇavam</li>
        <li id="R_5.001.053cd">pipāsur iva cākāśaṃ dadṛśe sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.001.054">
        <li id="R_5.001.054ab">tasya vidyutprabhākāre vāyumārgānusāriṇaḥ</li>
        <li id="R_5.001.054cd">nayane viprakāśete parvatasthāv ivānalau</li>
    </ul>
    <ul id="R_5.001.055">
        <li id="R_5.001.055ab">piṅge piṅgākṣamukhyasya bṛhatī parimaṇḍale</li>
        <li id="R_5.001.055cd">cakṣuṣī saṃprakaśete candrasūryāv iva sthitau</li>
    </ul>
    <ul id="R_5.001.056">
        <li id="R_5.001.056ab">mukhaṃ nāsikayā tasya tāmrayā tāmram ābabhau</li>
        <li id="R_5.001.056cd">saṃdhyayā samabhispṛṣṭaṃ yathā sūryasya maṇḍalam</li>
    </ul>
    <ul id="R_5.001.057">
        <li id="R_5.001.057ab">lāṅgalaṃ ca samāviddhaṃ plavamānasya śobhate</li>
        <li id="R_5.001.057cd">ambare vāyuputrasya śakradhvaja ivocchritaḥ</li>
    </ul>
    <ul id="R_5.001.058">
        <li id="R_5.001.058ab">lāṅgūlacakreṇa mahāñ śukladaṃṣṭro 'nilātmajaḥ</li>
        <li id="R_5.001.058cd">vyarocata mahāprājñaḥ pariveṣīva bhāskaraḥ</li>
    </ul>
    <ul id="R_5.001.059">
        <li id="R_5.001.059ab">sphigdeśenābhitāmreṇa rarāja sa mahākapiḥ</li>
        <li id="R_5.001.059cd">mahatā dāriteneva girir gairikadhātunā</li>
    </ul>
    <ul id="R_5.001.060">
        <li id="R_5.001.060ab">tasya vānarasiṃhasya plavamānasya sāgaram</li>
        <li id="R_5.001.060cd">kakṣāntaragato vāyur jīmūta iva garjati</li>
    </ul>
    <ul id="R_5.001.061">
        <li id="R_5.001.061ab">khe yathā nipataty ulkā uttarāntād viniḥsṛtā</li>
        <li id="R_5.001.061cd">dṛśyate sānubandhā ca tathā sa kapikuñjaraḥ</li>
    </ul>
    <ul id="R_5.001.062">
        <li id="R_5.001.062ab">patatpataṃgasaṃkāśo vyāyataḥ śuśubhe kapiḥ</li>
        <li id="R_5.001.062cd">pravṛddha iva mātaṃgaḥ kakṣyayā badhyamānayā</li>
    </ul>
    <ul id="R_5.001.063">
        <li id="R_5.001.063ab">upariṣṭāc charīreṇa chāyayā cāvagāḍhayā</li>
        <li id="R_5.001.063cd">sāgare mārutāviṣṭā naur ivāsīt tadā kapiḥ</li>
    </ul>
    <ul id="R_5.001.064">
        <li id="R_5.001.064ab">yaṃ yaṃ deśaṃ samudrasya jagāma sa mahākapiḥ</li>
        <li id="R_5.001.064cd">sa sa tasyāṅgavegena sonmāda iva lakṣyate</li>
    </ul>
    <ul id="R_5.001.065">
        <li id="R_5.001.065ab">sāgarasyormijālānām urasā śailavarṣmaṇām</li>
        <li id="R_5.001.065cd">abhighnaṃs tu mahāvegaḥ pupluve sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.001.066">
        <li id="R_5.001.066ab">kapivātaś ca balavān meghavātaś ca niḥsṛtaḥ</li>
        <li id="R_5.001.066cd">sāgaraṃ bhīmanirghoṣaṃ kampayām āsatur bhṛśam</li>
    </ul>
    <ul id="R_5.001.067">
        <li id="R_5.001.067ab">vikarṣann ūrmijālāni bṛhanti lavaṇāmbhasi</li>
        <li id="R_5.001.067cd">atyakrāman mahāvegas taraṅgān gaṇayann iva</li>
    </ul>
    <ul id="R_5.001.068">
        <li id="R_5.001.068ab">plavamānaṃ samīkṣyātha bhujaṅgāḥ sāgarālayāḥ</li>
        <li id="R_5.001.068cd">vyomni taṃ kapiśārdūlaṃ suparṇam iti menire</li>
    </ul>
    <ul id="R_5.001.069">
        <li id="R_5.001.069ab">daśayojanavistīrṇā triṃśadyojanam āyatā</li>
        <li id="R_5.001.069cd">chāyā vānarasiṃhasya jale cārutarābhavat</li>
    </ul>
    <ul id="R_5.001.070">
        <li id="R_5.001.070ab">śvetābhraghanarājīva vāyuputrānugāminī</li>
        <li id="R_5.001.070cd">tasya sā śuśubhe chāyā vitatā lavaṇāmbhasi</li>
    </ul>
    <ul id="R_5.001.071">
        <li id="R_5.001.071ab">plavamānaṃ tu taṃ dṛṣṭvā plavagaṃ tvaritaṃ tadā</li>
        <li id="R_5.001.071cd">vavṛṣuḥ puṣpavarṣāṇi devagandharvadānavāḥ</li>
    </ul>
    <ul id="R_5.001.072">
        <li id="R_5.001.072ab">tatāpa na hi taṃ sūryaḥ plavantaṃ vānareśvaram</li>
        <li id="R_5.001.072cd">siṣeve ca tadā vāyū rāmakāryārthasiddhaye</li>
    </ul>
    <ul id="R_5.001.073">
        <li id="R_5.001.073ab">ṛṣayas tuṣṭuvuś cainaṃ plavamānaṃ vihāyasā</li>
        <li id="R_5.001.073cd">jaguś ca devagandharvāḥ praśaṃsanto mahaujasaṃ</li>
    </ul>
    <ul id="R_5.001.074">
        <li id="R_5.001.074ab">nāgāś ca tuṣṭuvur yakṣā rakṣāṃsi vibudhāḥ khagāḥ</li>
        <li id="R_5.001.074cd">prekṣyākāśe kapivaraṃ sahasā vigataklamam</li>
    </ul>
    <ul id="R_5.001.075">
        <li id="R_5.001.075ab">tasmin plavagaśārdūle plavamāne hanūmati</li>
        <li id="R_5.001.075cd">ikṣvākukulamānārthī cintayām āsa sāgaraḥ</li>
    </ul>
    <ul id="R_5.001.076">
        <li id="R_5.001.076ab">sāhāyyaṃ vānarendrasya yadi nāhaṃ hanūmataḥ</li>
        <li id="R_5.001.076cd">kariṣyāmi bhaviṣyāmi sarvavācyo vivakṣatām</li>
    </ul>
    <ul id="R_5.001.077">
        <li id="R_5.001.077ab">aham ikṣvākunāthena sagareṇa vivardhitaḥ</li>
        <li id="R_5.001.077cd">ikṣvākusacivaś cāyaṃ nāvasīditum arhati</li>
    </ul>
    <ul id="R_5.001.078">
        <li id="R_5.001.078ab">tathā mayā vidhātavyaṃ viśrameta yathā kapiḥ</li>
        <li id="R_5.001.078cd">śeṣaṃ ca mayi viśrāntaḥ sukhenātipatiṣyati</li>
    </ul>
    <ul id="R_5.001.079">
        <li id="R_5.001.079ab">iti kṛtvā matiṃ sādhvīṃ samudraś channam ambhasi</li>
        <li id="R_5.001.079cd">hiraṇyanābhaṃ mainākam uvāca girisattamam</li>
    </ul>
    <ul id="R_5.001.080">
        <li id="R_5.001.080ab">tvam ihāsurasaṃghānāṃ pātālatalavāsinām</li>
        <li id="R_5.001.080cd">devarājñā giriśreṣṭha parighaḥ saṃniveśitaḥ</li>
    </ul>
    <ul id="R_5.001.081">
        <li id="R_5.001.081ab">tvam eṣāṃ jñātavīryāṇāṃ punar evotpatiṣyatām</li>
        <li id="R_5.001.081cd">pātālasyāprameyasya dvāram āvṛtya tiṣṭhasi</li>
    </ul>
    <ul id="R_5.001.082">
        <li id="R_5.001.082ab">tiryag ūrdhvam adhaś caiva śaktis te śailavardhitum</li>
        <li id="R_5.001.082cd">tasmāt saṃcodayāmi tvām uttiṣṭha nagasattama</li>
    </ul>
    <ul id="R_5.001.083">
        <li id="R_5.001.083ab">sa eṣa kapiśārdūlas tvām uparyeti vīryavān</li>
        <li id="R_5.001.083cd">hanūmān rāmakāryārthaṃ bhīmakarmā kham āplutaḥ</li>
    </ul>
    <ul id="R_5.001.084">
        <li id="R_5.001.084ab">tasya sāhyaṃ mayā kāryam ikṣvākukulavartinaḥ</li>
        <li id="R_5.001.084cd">mama ikṣvākavaḥ pūjyāḥ paraṃ pūjyatamās tava</li>
    </ul>
    <ul id="R_5.001.085">
        <li id="R_5.001.085ab">kuru sācivyam asmākaṃ na naḥ kāryam atikramet</li>
        <li id="R_5.001.085cd">kartavyam akṛtaṃ kāryaṃ satāṃ manyum udīrayet</li>
    </ul>
    <ul id="R_5.001.086">
        <li id="R_5.001.086ab">salilād ūrdhvam uttiṣṭha tiṣṭhatv eṣa kapis tvayi</li>
        <li id="R_5.001.086cd">asmākam atithiś caiva pūjyaś ca plavatāṃ varaḥ</li>
    </ul>
    <ul id="R_5.001.087">
        <li id="R_5.001.087ab">cāmīkaramahānābha devagandharvasevita</li>
        <li id="R_5.001.087cd">hanūmāṃs tvayi viśrāntas tataḥ śeṣaṃ gamiṣyati</li>
    </ul>
    <ul id="R_5.001.088">
        <li id="R_5.001.088ab">kākutsthasyānṛśaṃsyaṃ ca maithilyāś ca vivāsanam</li>
        <li id="R_5.001.088cd">śramaṃ ca plavagendrasya samīkṣyotthātum arhasi</li>
    </ul>
    <ul id="R_5.001.089">
        <li id="R_5.001.089ab">hiraṇyanābho maināko niśamya lavaṇāmbhasaḥ</li>
        <li id="R_5.001.089cd">utpapāta jalāt tūrṇaṃ mahādrumalatāyutaḥ</li>
    </ul>
    <ul id="R_5.001.090">
        <li id="R_5.001.090ab">sa sāgarajalaṃ bhittvā babhūvātyutthitas tadā</li>
        <li id="R_5.001.090cd">yathā jaladharaṃ bhittvā dīptaraśmir divākaraḥ</li>
    </ul>
    <ul id="R_5.001.091">
        <li id="R_5.001.091ab">śātakumbhamayaiḥ śṛṅgaiḥ sakiṃnaramahoragaiḥ</li>
        <li id="R_5.001.091cd">ādityodayasaṃkāśair ālikhadbhir ivāmbaram</li>
    </ul>
    <ul id="R_5.001.092">
        <li id="R_5.001.092ab">tasya jāmbūnadaiḥ śṛṅgaiḥ parvatasya samutthitaiḥ</li>
        <li id="R_5.001.092cd">ākāśaṃ śastrasaṃkāśam abhavat kāñcanaprabham</li>
    </ul>
    <ul id="R_5.001.093">
        <li id="R_5.001.093ab">jātarūpamayaiḥ śṛṅgair bhrājamānaiḥ svayaṃ prabhaiḥ</li>
        <li id="R_5.001.093cd">ādityaśatasaṃkāśaḥ so 'bhavad girisattamaḥ</li>
    </ul>
    <ul id="R_5.001.094">
        <li id="R_5.001.094ab">tam utthitam asaṃgena hanūmān agrataḥ sthitam</li>
        <li id="R_5.001.094cd">madhye lavaṇatoyasya vighno 'yam iti niścitaḥ</li>
    </ul>
    <ul id="R_5.001.095">
        <li id="R_5.001.095ab">sa tam ucchritam atyarthaṃ mahāvego mahākapiḥ</li>
        <li id="R_5.001.095cd">urasā pātayām āsa jīmūtam iva mārutaḥ</li>
    </ul>
    <ul id="R_5.001.096">
        <li id="R_5.001.096ab">sa tadā pātitas tena kapinā parvatottamaḥ</li>
        <li id="R_5.001.096cd">buddhvā tasya kaper vegaṃ jaharṣa ca nananda ca</li>
    </ul>
    <ul id="R_5.001.097">
        <li id="R_5.001.097ab">tam ākāśagataṃ vīram ākāśe samavasthitam</li>
        <li id="R_5.001.097cd">prīto hṛṣṭamanā vākyam abravīt parvataḥ kapim</li>
        <li id="R_5.001.097ef">mānuṣaṃ dharayan rūpam ātmanaḥ śikhare sthitaḥ</li>
    </ul>
    <ul id="R_5.001.098">
        <li id="R_5.001.098ab">duṣkaraṃ kṛtavān karma tvam idaṃ vānarottama</li>
        <li id="R_5.001.098cd">nipatya mama śṛṅgeṣu viśramasva yathāsukham</li>
    </ul>
    <ul id="R_5.001.099">
        <li id="R_5.001.099ab">rāghāvasya kule jātair udadhiḥ parivardhitaḥ</li>
        <li id="R_5.001.099cd">sa tvāṃ rāmahite yuktaṃ pratyarcayati sāgaraḥ</li>
    </ul>
    <ul id="R_5.001.100">
        <li id="R_5.001.100ab">kṛte ca pratikartavyam eṣa dharmaḥ sanātanaḥ</li>
        <li id="R_5.001.100cd">so 'yaṃ tat pratikārārthī tvattaḥ saṃmānam arhati</li>
    </ul>
    <ul id="R_5.001.101">
        <li id="R_5.001.101ab">tvannimittam anenāhaṃ bahumānāt pracoditaḥ</li>
        <li id="R_5.001.101cd">yojanānāṃ śataṃ cāpi kapir eṣa samāplutaḥ</li>
        <li id="R_5.001.101ef">tava sānuṣu viśrāntaḥ śeṣaṃ prakramatām iti</li>
    </ul>
    <ul id="R_5.001.102">
        <li id="R_5.001.102ab">tiṣṭha tvaṃ hariśārdūla mayi viśramya gamyatām</li>
        <li id="R_5.001.102cd">tad idaṃ gandhavat svādu kandamūlaphalaṃ bahu</li>
        <li id="R_5.001.102ef">tad āsvādya hariśreṣṭha viśrānto 'nugamiṣyasi</li>
    </ul>
    <ul id="R_5.001.103">
        <li id="R_5.001.103ab">asmākam api saṃbandhaḥ kapimukhyas tvayāsti vai</li>
        <li id="R_5.001.103cd">prakhyātas triṣu lokeṣu mahāguṇaparigrahaḥ</li>
    </ul>
    <ul id="R_5.001.104">
        <li id="R_5.001.104ab">vegavantaḥ plavanto ye plavagā mārutātmaja</li>
        <li id="R_5.001.104cd">teṣāṃ mukhyatamaṃ manye tvām ahaṃ kapikuñjara</li>
    </ul>
    <ul id="R_5.001.105">
        <li id="R_5.001.105ab">atithiḥ kila pūjārhaḥ prākṛto 'pi vijānatā</li>
        <li id="R_5.001.105cd">dharmaṃ jijñāsamānena kiṃ punar yādṛśo bhavān</li>
    </ul>
    <ul id="R_5.001.106">
        <li id="R_5.001.106ab">tvaṃ hi devavariṣṭhasya mārutasya mahātmanaḥ</li>
        <li id="R_5.001.106cd">putras tasyaiva vegena sadṛśaḥ kapikuñjara</li>
    </ul>
    <ul id="R_5.001.107">
        <li id="R_5.001.107ab">pūjite tvayi dharmajña pūjāṃ prāpnoti mārutaḥ</li>
        <li id="R_5.001.107cd">tasmāt tvaṃ pūjanīyo me śṛṇu cāpy atra kāraṇam</li>
    </ul>
    <ul id="R_5.001.108">
        <li id="R_5.001.108ab">pūrvaṃ kṛtayuge tāta parvatāḥ pakṣiṇo 'bhavan</li>
        <li id="R_5.001.108cd">te 'pi jagmur diśaḥ sarvā garuḍānilaveginaḥ</li>
    </ul>
    <ul id="R_5.001.109">
        <li id="R_5.001.109ab">tatas teṣu prayāteṣu devasaṃghāḥ saharṣibhiḥ</li>
        <li id="R_5.001.109cd">bhūtāni ca bhayaṃ jagmus teṣāṃ patanaśaṅkayā</li>
    </ul>
    <ul id="R_5.001.110">
        <li id="R_5.001.110ab">tataḥ kruddhaḥ sahasrākṣaḥ parvatānāṃ śatakratuḥ</li>
        <li id="R_5.001.110cd">pakṣāṃś ciccheda vajreṇa tatra tatra sahasraśaḥ</li>
    </ul>
    <ul id="R_5.001.111">
        <li id="R_5.001.111ab">sa mām upagataḥ kruddho vajram udyamya devarāṭ</li>
        <li id="R_5.001.111cd">tato 'haṃ sahasā kṣiptaḥ śvasanena mahātmanā</li>
    </ul>
    <ul id="R_5.001.112">
        <li id="R_5.001.112ab">asmiṃl lavaṇatoye ca prakṣiptaḥ plavagottama</li>
        <li id="R_5.001.112cd">guptapakṣaḥ samagraś ca tava pitrābhirakṣitaḥ</li>
    </ul>
    <ul id="R_5.001.113">
        <li id="R_5.001.113ab">tato 'haṃ mānayāmi tvāṃ mānyo hi mama mārutaḥ</li>
        <li id="R_5.001.113cd">tvayā me hy eṣa saṃbandhaḥ kapimukhya mahāguṇaḥ</li>
    </ul>
    <ul id="R_5.001.114">
        <li id="R_5.001.114ab">asminn evaṃgate kārye sāgarasya mamaiva ca</li>
        <li id="R_5.001.114cd">prītiṃ prītamanā kartuṃ tvam arhasi mahākape</li>
    </ul>
    <ul id="R_5.001.115">
        <li id="R_5.001.115ab">śramaṃ mokṣaya pūjāṃ ca gṛhāṇa kapisattama</li>
        <li id="R_5.001.115cd">prītiṃ ca bahumanyasva prīto 'smi tava darśanāt</li>
    </ul>
    <ul id="R_5.001.116">
        <li id="R_5.001.116ab">evam uktaḥ kapiśreṣṭhas taṃ nagottamam abravīt</li>
        <li id="R_5.001.116cd">prīto 'smi kṛtam ātithyaṃ manyur eṣo 'panīyatām</li>
    </ul>
    <ul id="R_5.001.117">
        <li id="R_5.001.117ab">tvarate kāryakālo me ahaś cāpy ativartate</li>
        <li id="R_5.001.117cd">pratijñā ca mayā dattā na sthātavyam ihāntarā</li>
    </ul>
    <ul id="R_5.001.118">
        <li id="R_5.001.118ab">ity uktvā pāṇinā śailam ālabhya haripuṃgavaḥ</li>
        <li id="R_5.001.118cd">jagāmākāśam āviśya vīryavān prahasann iva</li>
    </ul>
    <ul id="R_5.001.119">
        <li id="R_5.001.119ab">sa parvatasamudrābhyāṃ bahumānād avekṣitaḥ</li>
        <li id="R_5.001.119cd">pūjitaś copapannābhir āśīrbhir anilātmajaḥ</li>
    </ul>
    <ul id="R_5.001.120">
        <li id="R_5.001.120ab">athordhvaṃ dūram utpatya hitvā śailamahārṇavau</li>
        <li id="R_5.001.120cd">pituḥ panthānam āsthāya jagāma vimale 'mbare</li>
    </ul>
    <ul id="R_5.001.121">
        <li id="R_5.001.121ab">bhūyaś cordhvagatiṃ prāpya giriṃ tam avalokayan</li>
        <li id="R_5.001.121cd">vāyusūnur nirālambe jagāma vimale 'mbare</li>
    </ul>
    <ul id="R_5.001.122">
        <li id="R_5.001.122ab">tad dvitīyaṃ hanumato dṛṣṭvā karma suduṣkaram</li>
        <li id="R_5.001.122cd">praśaśaṃsuḥ surāḥ sarve siddhāś ca paramarṣayaḥ</li>
    </ul>
    <ul id="R_5.001.123">
        <li id="R_5.001.123ab">devatāś cābhavan hṛṣṭās tatrasthās tasya karmaṇā</li>
        <li id="R_5.001.123cd">kāñcanasya sunābhasya sahasrākṣaś ca vāsavaḥ</li>
    </ul>
    <ul id="R_5.001.124">
        <li id="R_5.001.124ab">uvāca vacanaṃ dhīmān paritoṣāt sagadgadam</li>
        <li id="R_5.001.124cd">sunābhaṃ parvataśreṣṭhaṃ svayam eva śacīpatiḥ</li>
    </ul>
    <ul id="R_5.001.125">
        <li id="R_5.001.125ab">hiraṇyanābhaśailendraparituṣṭo 'smi te bhṛśam</li>
        <li id="R_5.001.125cd">abhayaṃ te prayacchāmi tiṣṭha saumya yathāsukham</li>
    </ul>
    <ul id="R_5.001.126">
        <li id="R_5.001.126ab">sāhyaṃ kṛtaṃ te sumahad vikrāntasya hanūmataḥ</li>
        <li id="R_5.001.126cd">kramato yojanaśataṃ nirbhayasya bhaye sati</li>
    </ul>
    <ul id="R_5.001.127">
        <li id="R_5.001.127ab">rāmasyaiṣa hi dautyena yāti dāśarather hariḥ</li>
        <li id="R_5.001.127cd">satkriyāṃ kurvatā śakyā toṣito 'smi dṛḍhaṃ tvayā</li>
    </ul>
    <ul id="R_5.001.128">
        <li id="R_5.001.128ab">tataḥ praharṣam alabhad vipulaṃ parvatottamaḥ</li>
        <li id="R_5.001.128cd">devatānāṃ patiṃ dṛṣṭvā parituṣṭaṃ śatakratum</li>
    </ul>
    <ul id="R_5.001.129">
        <li id="R_5.001.129ab">sa vai dattavaraḥ śailo babhūvāvasthitas tadā</li>
        <li id="R_5.001.129cd">hanūmāṃś ca muhūrtena vyaticakrāma sāgaram</li>
    </ul>
    <ul id="R_5.001.130">
        <li id="R_5.001.130ab">tato devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_5.001.130cd">abruvan sūryasaṃkāśāṃ surasāṃ nāgamātaram</li>
    </ul>
    <ul id="R_5.001.131">
        <li id="R_5.001.131ab">ayaṃ vātātmajaḥ śrīmān plavate sāgaropari</li>
        <li id="R_5.001.131cd">hanūmān nāma tasya tvaṃ muhūrtaṃ vighnam ācara</li>
    </ul>
    <ul id="R_5.001.132">
        <li id="R_5.001.132ab">rākṣasaṃ rūpam āsthāya sughoraṃ parvatopamam</li>
        <li id="R_5.001.132cd">daṃṣṭrākarālaṃ piṅgākṣaṃ vaktraṃ kṛtvā nabhaḥspṛśam</li>
    </ul>
    <ul id="R_5.001.133">
        <li id="R_5.001.133ab">balam icchāmahe jñātuṃ bhūyaś cāsya parākramam</li>
        <li id="R_5.001.133cd">tvāṃ vijeṣyaty upāyena viṣadaṃ vā gamiṣyati</li>
    </ul>
    <ul id="R_5.001.134">
        <li id="R_5.001.134ab">evam uktā tu sā devī daivatair abhisatkṛtā</li>
        <li id="R_5.001.134cd">samudramadhye surasā bibhratī rākṣasaṃ vapuḥ</li>
    </ul>
    <ul id="R_5.001.135">
        <li id="R_5.001.135ab">vikṛtaṃ ca virūpaṃ ca sarvasya ca bhayāvaham</li>
        <li id="R_5.001.135cd">plavamānaṃ hanūmantam āvṛtyedam uvāca ha</li>
    </ul>
    <ul id="R_5.001.136">
        <li id="R_5.001.136ab">mama bhakṣaḥ pradiṣṭas tvam īśvarair vānararṣabha</li>
        <li id="R_5.001.136cd">ahaṃ tvāṃ bhakṣayiṣyāmi praviśedaṃ mamānanam</li>
    </ul>
    <ul id="R_5.001.137">
        <li id="R_5.001.137ab">evam uktaḥ surasayā prāñjalir vānararṣabhaḥ</li>
        <li id="R_5.001.137cd">prahṛṣṭavadanaḥ śrīmān idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.001.138">
        <li id="R_5.001.138ab">rāmo dāśarathir nāma praviṣṭo daṇḍakāvanam</li>
        <li id="R_5.001.138cd">lakṣmaṇena saha bhrātrā vaidehyā cāpi bhāryayā</li>
    </ul>
    <ul id="R_5.001.139">
        <li id="R_5.001.139ab">asya kāryaviṣaktasya baddhavairasya rākṣasaiḥ</li>
        <li id="R_5.001.139cd">tasya sītā hṛtā bhāryā rāvaṇena yaśasvinī</li>
    </ul>
    <ul id="R_5.001.140">
        <li id="R_5.001.140ab">tasyāḥ sakāśaṃ dūto 'haṃ gamiṣye rāmaśāsanāt</li>
        <li id="R_5.001.140cd">kartum arhasi rāmasya sāhyaṃ viṣayavāsini</li>
    </ul>
    <ul id="R_5.001.141">
        <li id="R_5.001.141ab">atha vā maithilīṃ dṛṣṭvā rāmaṃ cākliṣṭakāriṇam</li>
        <li id="R_5.001.141cd">āgamiṣyāmi te vaktraṃ satyaṃ pratiśṛṇomi te</li>
    </ul>
    <ul id="R_5.001.142">
        <li id="R_5.001.142ab">evam uktā hanumatā surasā kāmarūpiṇī</li>
        <li id="R_5.001.142cd">abravīn nātivarten māṃ kaś cid eṣa varo mama</li>
    </ul>
    <ul id="R_5.001.143">
        <li id="R_5.001.143ab">evam uktaḥ surasayā kruddho vānarapuṃgavaḥ</li>
        <li id="R_5.001.143cd">abravīt kuru vai vaktraṃ yena māṃ viṣahiṣyase</li>
    </ul>
    <ul id="R_5.001.144">
        <li id="R_5.001.144ab">ity uktvā surasāṃ kruddho daśayojanam āyataḥ</li>
        <li id="R_5.001.144cd">daśayojanavistāro babhūva hanumāṃs tadā</li>
    </ul>
    <ul id="R_5.001.145">
        <li id="R_5.001.145ab">taṃ dṛṣṭvā meghasaṃkāśaṃ daśayojanam āyatam</li>
        <li id="R_5.001.145cd">cakāra surasāpy āsyaṃ viṃśadyojanam āyatam</li>
    </ul>
    <ul id="R_5.001.146">
        <li id="R_5.001.146ab">hanumāṃs tu tataḥ kruddhas triṃśadyojanam āyataḥ</li>
        <li id="R_5.001.146cd">cakāra surasā vaktraṃ catvāriṃśat tathocchritam</li>
    </ul>
    <ul id="R_5.001.147">
        <li id="R_5.001.147ab">babhūva hanumān vīraḥ pañcāśadyojanocchritaḥ</li>
        <li id="R_5.001.147cd">cakāra surasā vaktraṃ ṣaṣṭiyojanam āyatam</li>
    </ul>
    <ul id="R_5.001.148">
        <li id="R_5.001.148ab">tathaiva hanumān vīraḥ saptatiṃ yojanocchritaḥ</li>
        <li id="R_5.001.148cd">cakāra surasā vaktram aśītiṃ yojanāyatam</li>
    </ul>
    <ul id="R_5.001.149">
        <li id="R_5.001.149ab">hanūmān acala prakhyo navatiṃ yojanocchritaḥ</li>
        <li id="R_5.001.149cd">cakāra surasā vaktraṃ śatayojanam āyatam</li>
    </ul>
    <ul id="R_5.001.150">
        <li id="R_5.001.150ab">tad dṛṣṭvā vyāditaṃ tv āsyaṃ vāyuputraḥ sa buddhimān</li>
        <li id="R_5.001.150cd">dīrghajihvaṃ surasayā sughoraṃ narakopamam</li>
    </ul>
    <ul id="R_5.001.151">
        <li id="R_5.001.151ab">sa saṃkṣipyātmanaḥ kāyaṃ jīmūta iva mārutiḥ</li>
        <li id="R_5.001.151cd">tasmin muhūrte hanumān babhūvāṅguṣṭhamātrakaḥ</li>
    </ul>
    <ul id="R_5.001.152">
        <li id="R_5.001.152ab">so 'bhipatyāśu tad vaktraṃ niṣpatya ca mahājavaḥ</li>
        <li id="R_5.001.152cd">antarikṣe sthitaḥ śrīmān idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.001.153">
        <li id="R_5.001.153ab">praviṣṭo 'smi hi te vaktraṃ dākṣāyaṇi namo 'stu te</li>
        <li id="R_5.001.153cd">gamiṣye yatra vaidehī satyaṃ cāstu vacas tava</li>
    </ul>
    <ul id="R_5.001.154">
        <li id="R_5.001.154ab">taṃ dṛṣṭvā vadanān muktaṃ candraṃ rāhumukhād iva</li>
        <li id="R_5.001.154cd">abravīt surasā devī svena rūpeṇa vānaram</li>
    </ul>
    <ul id="R_5.001.155">
        <li id="R_5.001.155ab">arthasiddhyai hariśreṣṭha gaccha saumya yathāsukham</li>
        <li id="R_5.001.155cd">samānaya ca vaidehīṃ rāghaveṇa mahātmanā</li>
    </ul>
    <ul id="R_5.001.156">
        <li id="R_5.001.156ab">tat tṛtīyaṃ hanumato dṛṣṭvā karma suduṣkaram</li>
        <li id="R_5.001.156cd">sādhu sādhv iti bhūtāni praśaśaṃsus tadā harim</li>
    </ul>
    <ul id="R_5.001.157">
        <li id="R_5.001.157ab">sa sāgaram anādhṛṣyam abhyetya varuṇālayam</li>
        <li id="R_5.001.157cd">jagāmākāśam āviśya vegena garuṇopamaḥ</li>
    </ul>
    <ul id="R_5.001.158">
        <li id="R_5.001.158ab">sevite vāridhāribhiḥ patagaiś ca niṣevite</li>
        <li id="R_5.001.158cd">carite kaiśikācāryair airāvataniṣevite</li>
    </ul>
    <ul id="R_5.001.159">
        <li id="R_5.001.159ab">siṃhakuñjaraśārdūlapatagoragavāhanaiḥ</li>
        <li id="R_5.001.159cd">vimānaiḥ saṃpatadbhiś ca vimalaiḥ samalaṃkṛte</li>
    </ul>
    <ul id="R_5.001.160">
        <li id="R_5.001.160ab">vajrāśanisamāghātaiḥ pāvakair upaśobhite</li>
        <li id="R_5.001.160cd">kṛtapuṇyair mahābhāgaiḥ svargajidbhir alaṃkṛte</li>
    </ul>
    <ul id="R_5.001.161">
        <li id="R_5.001.161ab">bahatā havyam atyantaṃ sevite citrabhānunā</li>
        <li id="R_5.001.161cd">grahanakṣatracandrārkatārāgaṇavibhūṣite</li>
    </ul>
    <ul id="R_5.001.162">
        <li id="R_5.001.162ab">maharṣigaṇagandharvanāgayakṣasamākule</li>
        <li id="R_5.001.162cd">vivikte vimale viśve viśvāvasuniṣevite</li>
    </ul>
    <ul id="R_5.001.163">
        <li id="R_5.001.163ab">devarājagajākrānte candrasūryapathe śive</li>
        <li id="R_5.001.163cd">vitāne jīvalokasya vitato brahmanirmite</li>
    </ul>
    <ul id="R_5.001.164">
        <li id="R_5.001.164ab">bahuśaḥ sevite vīrair vidyādharagaṇair varaiḥ</li>
        <li id="R_5.001.164cd">kapinā kṛṣyamāṇāni mahābhrāṇi cakāśire</li>
    </ul>
    <ul id="R_5.001.165">
        <li id="R_5.001.165ab">praviśann abhrajālāni niṣpataṃś ca punaḥ punaḥ</li>
        <li id="R_5.001.165cd">prāvṛṣīndur ivābhāti niṣpatan praviśaṃs tadā</li>
    </ul>
    <ul id="R_5.001.166">
        <li id="R_5.001.166ab">plavamānaṃ tu taṃ dṛṣṭvā siṃhikā nāma rākṣasī</li>
        <li id="R_5.001.166cd">manasā cintayām āsa pravṛddhā kāmarūpiṇī</li>
    </ul>
    <ul id="R_5.001.167">
        <li id="R_5.001.167ab">adya dīrghasya kālasya bhaviṣyāmy aham āśitā</li>
        <li id="R_5.001.167cd">idaṃ hi me mahat sattvaṃ cirasya vaśam āgatam</li>
    </ul>
    <ul id="R_5.001.168">
        <li id="R_5.001.168ab">iti saṃcintya manasā chāyām asya samakṣipat</li>
        <li id="R_5.001.168cd">chāyāyāṃ saṃgṛhītāyāṃ cintayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.001.169">
        <li id="R_5.001.169ab">samākṣipto 'smi sahasā paṅgūkṛtaparākramaḥ</li>
        <li id="R_5.001.169cd">pratilomena vātena mahānaur iva sāgare</li>
    </ul>
    <ul id="R_5.001.170">
        <li id="R_5.001.170ab">tiryag ūrdhvam adhaś caiva vīkṣamāṇas tataḥ kapiḥ</li>
        <li id="R_5.001.170cd">dadarśa sa mahāsattvam utthitaṃ lavaṇāmbhasi</li>
    </ul>
    <ul id="R_5.001.171">
        <li id="R_5.001.171ab">kapirājñā yad ākhyātaṃ sattvam adbhutadarśanam</li>
        <li id="R_5.001.171cd">chāyāgrāhi mahāvīryaṃ tad idaṃ nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_5.001.172">
        <li id="R_5.001.172ab">sa tāṃ buddhvārthatattvena siṃhikāṃ matimān kapiḥ</li>
        <li id="R_5.001.172cd">vyavardhata mahākāyaḥ prāvṛṣīva balāhakaḥ</li>
    </ul>
    <ul id="R_5.001.173">
        <li id="R_5.001.173ab">tasya sā kāyam udvīkṣya vardhamānaṃ mahākapeḥ</li>
        <li id="R_5.001.173cd">vaktraṃ prasārayām āsa pātālāmbarasaṃnibham</li>
    </ul>
    <ul id="R_5.001.174">
        <li id="R_5.001.174ab">sa dadarśa tatas tasyā vikṛtaṃ sumahan mukham</li>
        <li id="R_5.001.174cd">kāyamātraṃ ca medhāvī marmāṇi ca mahākapiḥ</li>
    </ul>
    <ul id="R_5.001.175">
        <li id="R_5.001.175ab">sa tasyā vivṛte vaktre vajrasaṃhananaḥ kapiḥ</li>
        <li id="R_5.001.175cd">saṃkṣipya muhur ātmānaṃ niṣpapāta mahābalaḥ</li>
    </ul>
    <ul id="R_5.001.176">
        <li id="R_5.001.176ab">āsye tasyā nimajjantaṃ dadṛśuḥ siddhacāraṇāḥ</li>
        <li id="R_5.001.176cd">grasyamānaṃ yathā candraṃ pūrṇaṃ parvaṇi rāhuṇā</li>
    </ul>
    <ul id="R_5.001.177">
        <li id="R_5.001.177ab">tatas tasya nakhais tīkṣṇair marmāṇy utkṛtya vānaraḥ</li>
        <li id="R_5.001.177cd">utpapātātha vegena manaḥsaṃpātavikramaḥ</li>
    </ul>
    <ul id="R_5.001.178">
        <li id="R_5.001.178ab">tāṃ hatāṃ vānareṇāśu patitāṃ vīkṣya siṃhikām</li>
        <li id="R_5.001.178cd">bhūtāny ākāśacārīṇi tam ūcuḥ plavagarṣabham</li>
    </ul>
    <ul id="R_5.001.179">
        <li id="R_5.001.179ab">bhīmam adya kṛtaṃ karma mahat sattvaṃ tvayā hatam</li>
        <li id="R_5.001.179cd">sādhayārtham abhipretam ariṣṭaṃ plavatāṃ vara</li>
    </ul>
    <ul id="R_5.001.180">
        <li id="R_5.001.180ab">yasya tv etāni catvāri vānarendra yathā tava</li>
        <li id="R_5.001.180cd">dhṛtir dṛṣṭir matir dākṣyaṃ sa karmasu na sīdati</li>
    </ul>
    <ul id="R_5.001.181">
        <li id="R_5.001.181ab">sa taiḥ saṃbhāvitaḥ pūjyaḥ pratipannaprayojanaḥ</li>
        <li id="R_5.001.181cd">jagāmākāśam āviśya pannagāśanavat kapiḥ</li>
    </ul>
    <ul id="R_5.001.182">
        <li id="R_5.001.182ab">prāptabhūyiṣṭha pāras tu sarvataḥ pratilokayan</li>
        <li id="R_5.001.182cd">yojanānāṃ śatasyānte vanarājiṃ dadarśa saḥ</li>
    </ul>
    <ul id="R_5.001.183">
        <li id="R_5.001.183ab">dadarśa ca patann eva vividhadrumabhūṣitam</li>
        <li id="R_5.001.183cd">dvīpaṃ śākhāmṛgaśreṣṭho malayopavanāni ca</li>
    </ul>
    <ul id="R_5.001.184">
        <li id="R_5.001.184ab">sāgaraṃ sāgarānūpān sāgarānūpajān drumān</li>
        <li id="R_5.001.184cd">sāgarasya ca patnīnāṃ mukhāny api vilokayan</li>
    </ul>
    <ul id="R_5.001.185">
        <li id="R_5.001.185ab">sa mahāmeghasaṃkāśaṃ samīkṣyātmānam ātmanā</li>
        <li id="R_5.001.185cd">nirundhantam ivākāśaṃ cakāra matimān matim</li>
    </ul>
    <ul id="R_5.001.186">
        <li id="R_5.001.186ab">kāyavṛddhiṃ pravegaṃ ca mama dṛṣṭvaiva rākṣasāḥ</li>
        <li id="R_5.001.186cd">mayi kautūhalaṃ kuryur iti mene mahākapiḥ</li>
    </ul>
    <ul id="R_5.001.187">
        <li id="R_5.001.187ab">tataḥ śarīraṃ saṃkṣipya tan mahīdharasaṃnibham</li>
        <li id="R_5.001.187cd">punaḥ prakṛtim āpede vītamoha ivātmavān</li>
    </ul>
    <ul id="R_5.001.188">
        <li id="R_5.001.188ab">
            <seg type="pāda" n="a">sa cārunānāvidharūpadhārī</seg>
            <seg type="pāda" n="b">paraṃ samāsādya samudratīram</seg>
        </li>
        <li id="R_5.001.188cd">
            <seg type="pāda" n="c">parair aśakyapratipannarūpaḥ</seg>
            <seg type="pāda" n="d">samīkṣitātmā samavekṣitārthaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.001.189">
        <li id="R_5.001.189ab">
            <seg type="pāda" n="a">tataḥ sa lambasya gireḥ samṛddhe</seg>
            <seg type="pāda" n="b">vicitrakūṭe nipapāta kūṭe</seg>
        </li>
        <li id="R_5.001.189cd">
            <seg type="pāda" n="c">saketakoddālakanālikere</seg>
            <seg type="pāda" n="d">mahādrikūṭapratimo mahātmā</seg>
        </li>
    </ul>
    <ul id="R_5.001.190">
        <li id="R_5.001.190ab">
            <seg type="pāda" n="a">sa sāgaraṃ dānavapannagāyutaṃ</seg>
            <seg type="pāda" n="b">balena vikramya mahormimālinam</seg>
        </li>
        <li id="R_5.001.190cd">
            <seg type="pāda" n="c">nipatya tīre ca mahodadhes tadā</seg>
            <seg type="pāda" n="d">dadarśa laṅkām amarāvatīm iva</seg>
        </li>
    </ul>
    <ul id="R_5.002.001">
        <li id="R_5.002.001ab">sa sāgaram anādhṛṣyam atikramya mahābalaḥ</li>
        <li id="R_5.002.001cd">trikūṭaśikhare laṅkāṃ sthitāṃ svastho dadarśa ha</li>
    </ul>
    <ul id="R_5.002.002">
        <li id="R_5.002.002ab">tataḥ pādapamuktena puṣpavarṣeṇa vīryavān</li>
        <li id="R_5.002.002cd">abhivṛṣṭaḥ sthitas tatra babhau puṣpamayo yathā</li>
    </ul>
    <ul id="R_5.002.003">
        <li id="R_5.002.003ab">yojanānāṃ śataṃ śrīmāṃs tīrtvāpy uttamavikramaḥ</li>
        <li id="R_5.002.003cd">aniśvasan kapis tatra na glānim adhigacchati</li>
    </ul>
    <ul id="R_5.002.004">
        <li id="R_5.002.004ab">śatāny ahaṃ yojanānāṃ krameyaṃ subahūny api</li>
        <li id="R_5.002.004cd">kiṃ punaḥ sāgarasyāntaṃ saṃkhyātaṃ śatayojanam</li>
    </ul>
    <ul id="R_5.002.005">
        <li id="R_5.002.005ab">sa tu vīryavatāṃ śreṣṭhaḥ plavatām api cottamaḥ</li>
        <li id="R_5.002.005cd">jagāma vegavāṃl laṅkāṃ laṅghayitvā mahodadhim</li>
    </ul>
    <ul id="R_5.002.006">
        <li id="R_5.002.006ab">śādvalāni ca nīlāni gandhavanti vanāni ca</li>
        <li id="R_5.002.006cd">gaṇḍavanti ca madhyena jagāma nagavanti ca</li>
    </ul>
    <ul id="R_5.002.007">
        <li id="R_5.002.007ab">śailāṃś ca tarusaṃchannān vanarājīś ca puṣpitāḥ</li>
        <li id="R_5.002.007cd">abhicakrāma tejasvī hanumān plavagarṣabhaḥ</li>
    </ul>
    <ul id="R_5.002.008">
        <li id="R_5.002.008ab">sa tasminn acale tiṣṭhan vanāny upavanāni ca</li>
        <li id="R_5.002.008cd">sa nagāgre ca tāṃ laṅkāṃ dadarśa pavanātmajaḥ</li>
    </ul>
    <ul id="R_5.002.009">
        <li id="R_5.002.009ab">saralān karṇikārāṃś ca kharjūrāṃś ca supuṣpitān</li>
        <li id="R_5.002.009cd">priyālān muculindāṃś ca kuṭajān ketakān api</li>
    </ul>
    <ul id="R_5.002.010">
        <li id="R_5.002.010ab">priyaṅgūn gandhapūrṇāṃś ca nīpān saptacchadāṃs tathā</li>
        <li id="R_5.002.010cd">asanān kovidārāṃś ca karavīrāṃś ca puṣpitān</li>
    </ul>
    <ul id="R_5.002.011">
        <li id="R_5.002.011ab">puṣpabhāranibaddhāṃś ca tathā mukulitān api</li>
        <li id="R_5.002.011cd">pādapān vihagākīrṇān pavanādhūtamastakān</li>
    </ul>
    <ul id="R_5.002.012">
        <li id="R_5.002.012ab">haṃsakāraṇḍavākīrṇā vāpīḥ padmotpalāyutāḥ</li>
        <li id="R_5.002.012cd">ākrīḍān vividhān ramyān vividhāṃś ca jalāśayān</li>
    </ul>
    <ul id="R_5.002.013">
        <li id="R_5.002.013ab">saṃtatān vividhair vṛkṣaiḥ sarvartuphalapuṣpitaiḥ</li>
        <li id="R_5.002.013cd">udyānāni ca ramyāṇi dadarśa kapikuñjaraḥ</li>
    </ul>
    <ul id="R_5.002.014">
        <li id="R_5.002.014ab">samāsādya ca lakṣmīvāṃl laṅkāṃ rāvaṇapālitām</li>
        <li id="R_5.002.014cd">parikhābhiḥ sapadmābhiḥ sotpalābhir alaṃkṛtām</li>
    </ul>
    <ul id="R_5.002.015">
        <li id="R_5.002.015ab">sītāpaharaṇārthena rāvaṇena surakṣitām</li>
        <li id="R_5.002.015cd">samantād vicaradbhiś ca rākṣasair ugradhanvibhiḥ</li>
    </ul>
    <ul id="R_5.002.016">
        <li id="R_5.002.016ab">kāñcanenāvṛtāṃ ramyāṃ prākāreṇa mahāpurīm</li>
        <li id="R_5.002.016cd">aṭṭālakaśatākīrṇāṃ patākādhvajamālinīm</li>
    </ul>
    <ul id="R_5.002.017">
        <li id="R_5.002.017ab">toraṇaiḥ kāñcanair divyair latāpaṅktivicitritaiḥ</li>
        <li id="R_5.002.017cd">dadarśa hanumāṃl laṅkāṃ divi devapurīm iva</li>
    </ul>
    <ul id="R_5.002.018">
        <li id="R_5.002.018ab">girimūrdhni sthitāṃ laṅkāṃ pāṇḍurair bhavanaiḥ śubhaiḥ</li>
        <li id="R_5.002.018cd">dadarśa sa kapiḥ śrīmān puram ākāśagaṃ yathā</li>
    </ul>
    <ul id="R_5.002.019">
        <li id="R_5.002.019ab">pālitāṃ rākṣasendreṇa nirmitāṃ viśvakarmaṇā</li>
        <li id="R_5.002.019cd">plavamānām ivākāśe dadarśa hanumān purīm</li>
    </ul>
    <ul id="R_5.002.020">
        <li id="R_5.002.020ab">saṃpūrṇāṃ rākṣasair ghorair nāgair bhogavatīm iva</li>
        <li id="R_5.002.020cd">acintyāṃ sukṛtāṃ spaṣṭāṃ kuberādhyuṣitāṃ purā</li>
    </ul>
    <ul id="R_5.002.021">
        <li id="R_5.002.021ab">daṃṣṭribhir bahubhiḥ śūraiḥ śūlapaṭṭiśapāṇibhiḥ</li>
        <li id="R_5.002.021cd">rakṣitāṃ rākṣasair ghorair guhām āśīviṣair api</li>
    </ul>
    <ul id="R_5.002.022">
        <li id="R_5.002.022ab">vapraprākārajaghanāṃ vipulāmbunavāmbarām</li>
        <li id="R_5.002.022cd">śataghnīśūlakeśāntām aṭṭālakavataṃsakām</li>
    </ul>
    <ul id="R_5.002.023">
        <li id="R_5.002.023ab">dvāram uttaram āsādya cintayām āsa vānaraḥ</li>
        <li id="R_5.002.023cd">kailāsaśikharaprakhyam ālikhantam ivāmbaram</li>
        <li id="R_5.002.023ef">dhriyamāṇam ivākāśam ucchritair bhavanottamaiḥ</li>
    </ul>
    <ul id="R_5.002.024">
        <li id="R_5.002.024ab">tasyāś ca mahatīṃ guptiṃ sāgaraṃ ca nirīkṣya saḥ</li>
        <li id="R_5.002.024cd">rāvaṇaṃ ca ripuṃ ghoraṃ cintayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.002.025">
        <li id="R_5.002.025ab">āgatyāpīha harayo bhaviṣyanti nirarthakāḥ</li>
        <li id="R_5.002.025cd">na hi yuddhena vai laṅkā śakyā jetuṃ surair api</li>
    </ul>
    <ul id="R_5.002.026">
        <li id="R_5.002.026ab">imāṃ tu viṣamāṃ durgāṃ laṅkāṃ rāvaṇapālitām</li>
        <li id="R_5.002.026cd">prāpyāpi sa mahābāhuḥ kiṃ kariṣyati rāghavaḥ</li>
    </ul>
    <ul id="R_5.002.027">
        <li id="R_5.002.027ab">avakāśo na sāntvasya rākṣaseṣv abhigamyate</li>
        <li id="R_5.002.027cd">na dānasya na bhedasya naiva yuddhasya dṛśyate</li>
    </ul>
    <ul id="R_5.002.028">
        <li id="R_5.002.028ab">caturṇām eva hi gatir vānarāṇāṃ mahātmanām</li>
        <li id="R_5.002.028cd">vāliputrasya nīlasya mama rājñaś ca dhīmataḥ</li>
    </ul>
    <ul id="R_5.002.029">
        <li id="R_5.002.029ab">yāvaj jānāmi vaidehīṃ yadi jīvati vā na vā</li>
        <li id="R_5.002.029cd">tatraiva cintayiṣyāmi dṛṣṭvā tāṃ janakātmajām</li>
    </ul>
    <ul id="R_5.002.030">
        <li id="R_5.002.030ab">tataḥ sa cintayām āsa muhūrtaṃ kapikuñjaraḥ</li>
        <li id="R_5.002.030cd">giriśṛṅge sthitas tasmin rāmasyābhyudaye rataḥ</li>
    </ul>
    <ul id="R_5.002.031">
        <li id="R_5.002.031ab">anena rūpeṇa mayā na śakyā rakṣasāṃ purī</li>
        <li id="R_5.002.031cd">praveṣṭuṃ rākṣasair guptā krūrair balasamanvitaiḥ</li>
    </ul>
    <ul id="R_5.002.032">
        <li id="R_5.002.032ab">ugraujaso mahāvīryo balavantaś ca rākṣasāḥ</li>
        <li id="R_5.002.032cd">vañcanīyā mayā sarve jānakīṃ parimārgitā</li>
    </ul>
    <ul id="R_5.002.033">
        <li id="R_5.002.033ab">lakṣyālakṣyeṇa rūpeṇa rātrau laṅkā purī mayā</li>
        <li id="R_5.002.033cd">praveṣṭuṃ prāptakālaṃ me kṛtyaṃ sādhayituṃ mahat</li>
    </ul>
    <ul id="R_5.002.034">
        <li id="R_5.002.034ab">tāṃ purīṃ tādṛśīṃ dṛṣṭvā durādharṣāṃ surāsuraiḥ</li>
        <li id="R_5.002.034cd">hanūmāṃś cintayām āsa viniḥśvasya muhur muhuḥ</li>
    </ul>
    <ul id="R_5.002.035">
        <li id="R_5.002.035ab">kenopāyena paśyeyaṃ maithilīṃ janakātmajām</li>
        <li id="R_5.002.035cd">adṛṣṭo rākṣasendreṇa rāvaṇena durātmanā</li>
    </ul>
    <ul id="R_5.002.036">
        <li id="R_5.002.036ab">na vinaśyet kathaṃ kāryaṃ rāmasya viditātmanaḥ</li>
        <li id="R_5.002.036cd">ekām ekaś ca paśyeyaṃ rahite janakātmajām</li>
    </ul>
    <ul id="R_5.002.037">
        <li id="R_5.002.037ab">bhūtāś cārtho vipadyante deśakālavirodhitāḥ</li>
        <li id="R_5.002.037cd">viklavaṃ dūtam āsādya tamaḥ sūryodaye yathā</li>
    </ul>
    <ul id="R_5.002.038">
        <li id="R_5.002.038ab">arthānarthāntare buddhir niścitāpi na śobhate</li>
        <li id="R_5.002.038cd">ghātayanti hi kāryāṇi dūtāḥ paṇḍitamāninaḥ</li>
    </ul>
    <ul id="R_5.002.039">
        <li id="R_5.002.039ab">na vinaśyet kathaṃ kāryaṃ vaiklavyaṃ na kathaṃ bhavet</li>
        <li id="R_5.002.039cd">laṅghanaṃ ca samudrasya kathaṃ nu na vṛthā bhavet</li>
    </ul>
    <ul id="R_5.002.040">
        <li id="R_5.002.040ab">mayi dṛṣṭe tu rakṣobhī rāmasya viditātmanaḥ</li>
        <li id="R_5.002.040cd">bhaved vyartham idaṃ kāryaṃ rāvaṇānartham icchataḥ</li>
    </ul>
    <ul id="R_5.002.041">
        <li id="R_5.002.041ab">na hi śakyaṃ kva cit sthātum avijñātena rākṣasaiḥ</li>
        <li id="R_5.002.041cd">api rākṣasarūpeṇa kim utānyena kena cit</li>
    </ul>
    <ul id="R_5.002.042">
        <li id="R_5.002.042ab">vāyur apy atra nājñātaś cared iti matir mama</li>
        <li id="R_5.002.042cd">na hy asty aviditaṃ kiṃ cid rākṣasānāṃ balīyasām</li>
    </ul>
    <ul id="R_5.002.043">
        <li id="R_5.002.043ab">ihāhaṃ yadi tiṣṭhāmi svena rūpeṇa saṃvṛtaḥ</li>
        <li id="R_5.002.043cd">vināśam upayāsyāmi bhartur arthaś ca hīyate</li>
    </ul>
    <ul id="R_5.002.044">
        <li id="R_5.002.044ab">tad ahaṃ svena rūpeṇa rajanyāṃ hrasvatāṃ gataḥ</li>
        <li id="R_5.002.044cd">laṅkām abhipatiṣyāmi rāghavasyārthasiddhaye</li>
    </ul>
    <ul id="R_5.002.045">
        <li id="R_5.002.045ab">rāvaṇasya purīṃ rātrau praviśya sudurāsadām</li>
        <li id="R_5.002.045cd">vicinvan bhavanaṃ sarvaṃ drakṣyāmi janakātmajām</li>
    </ul>
    <ul id="R_5.002.046">
        <li id="R_5.002.046ab">iti saṃcintya hanumān sūryasyāstamayaṃ kapiḥ</li>
        <li id="R_5.002.046cd">ācakāṅkṣe tadā vīrā vaidehyā darśanotsukaḥ</li>
        <li id="R_5.002.046ef">pṛṣadaṃśakamātraḥ san babhūvādbhutadarśanaḥ</li>
    </ul>
    <ul id="R_5.002.047">
        <li id="R_5.002.047ab">pradoṣakāle hanumāṃs tūrṇam utpatya vīryavān</li>
        <li id="R_5.002.047cd">praviveśa purīṃ ramyāṃ suvibhaktamahāpatham</li>
    </ul>
    <ul id="R_5.002.048">
        <li id="R_5.002.048ab">prāsādamālāvitatāṃ stambhaiḥ kāñcanarājataiḥ</li>
        <li id="R_5.002.048cd">śātakumbhamayair jālair gandharvanagaropamām</li>
    </ul>
    <ul id="R_5.002.049">
        <li id="R_5.002.049ab">saptabhaumāṣṭabhaumaiś ca sa dadarśa mahāpurīm</li>
        <li id="R_5.002.049cd">talaiḥ sphāṭikasaṃpūrṇaiḥ kārtasvaravibhūṣitaiḥ</li>
    </ul>
    <ul id="R_5.002.050">
        <li id="R_5.002.050ab">vaidūryamaṇicitraiś ca muktājālavibhūṣitaiḥ</li>
        <li id="R_5.002.050cd">talaiḥ śuśubhire tāni bhavanāny atra rakṣasām</li>
    </ul>
    <ul id="R_5.002.051">
        <li id="R_5.002.051ab">kāñcanāni vicitrāṇi toraṇāni ca rakṣasām</li>
        <li id="R_5.002.051cd">laṅkām uddyotayām āsuḥ sarvataḥ samalaṃkṛtām</li>
    </ul>
    <ul id="R_5.002.052">
        <li id="R_5.002.052ab">acintyām adbhutākārāṃ dṛṣṭvā laṅkāṃ mahākapiḥ</li>
        <li id="R_5.002.052cd">āsīd viṣaṇṇo hṛṣṭaś ca vaidehyā darśanotsukaḥ</li>
    </ul>
    <ul id="R_5.002.053">
        <li id="R_5.002.053ab">
            <seg type="pāda" n="a">sa pāṇḍurodviddhavimānamālinīṃ</seg>
            <seg type="pāda" n="b">mahārhajāmbūnadajālatoraṇām</seg>
        </li>
        <li id="R_5.002.053cd">
            <seg type="pāda" n="c">yaśasvināṃ rāvaṇabāhupālitāṃ</seg>
            <seg type="pāda" n="d">kṣapācarair bhīmabalaiḥ samāvṛtām</seg>
        </li>
    </ul>
    <ul id="R_5.002.054">
        <li id="R_5.002.054ab">
            <seg type="pāda" n="a">candro 'pi sācivyam ivāsya kurvaṃs</seg>
            <seg type="pāda" n="b">tārāgaṇair madhyagato virājan</seg>
        </li>
        <li id="R_5.002.054cd">
            <seg type="pāda" n="c">jyotsnāvitānena vitatya lokam</seg>
            <seg type="pāda" n="d">uttiṣṭhate naikasahasraraśmiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.002.055">
        <li id="R_5.002.055ab">
            <seg type="pāda" n="a">śaṅkhaprabhaṃ kṣīramṛṇālavarṇam</seg>
            <seg type="pāda" n="b">udgacchamānaṃ vyavabhāsamānam</seg>
        </li>
        <li id="R_5.002.055cd">
            <seg type="pāda" n="c">dadarśa candraṃ sa kapipravīraḥ</seg>
            <seg type="pāda" n="d">poplūyamānaṃ sarasīva haṃsaṃ</seg>
        </li>
    </ul>
    <ul id="R_5.003.001">
        <li id="R_5.003.001ab">sa lambaśikhare lambe lambatoyadasaṃnibhe</li>
        <li id="R_5.003.001cd">sattvam āsthāya medhāvī hanumān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.003.002">
        <li id="R_5.003.002ab">niśi laṅkāṃ mahāsattvo viveśa kapikuñjaraḥ</li>
        <li id="R_5.003.002cd">ramyakānanatoyāḍhyāṃ purīṃ rāvaṇapālitām</li>
    </ul>
    <ul id="R_5.003.003">
        <li id="R_5.003.003ab">śāradāmbudharaprakhyair bhavanair upaśobhitām</li>
        <li id="R_5.003.003cd">sāgaropamanirghoṣāṃ sāgarānilasevitām</li>
    </ul>
    <ul id="R_5.003.004">
        <li id="R_5.003.004ab">supuṣṭabalasaṃguptāṃ yathaiva viṭapāvatīm</li>
        <li id="R_5.003.004cd">cārutoraṇaniryūhāṃ pāṇḍuradvāratoraṇām</li>
    </ul>
    <ul id="R_5.003.005">
        <li id="R_5.003.005ab">bhujagācaritāṃ guptāṃ śubhāṃ bhogavatīm iva</li>
        <li id="R_5.003.005cd">tāṃ savidyudghanākīrṇāṃ jyotirmārganiṣevitām</li>
    </ul>
    <ul id="R_5.003.006">
        <li id="R_5.003.006ab">caṇḍamārutanirhrādāṃ yathendrasyāmarāvatīm</li>
        <li id="R_5.003.006cd">śātakumbhena mahatā prākāreṇābhisaṃvṛtām</li>
    </ul>
    <ul id="R_5.003.007">
        <li id="R_5.003.007ab">kiṅkiṇījālaghoṣābhiḥ patākābhir alaṃkṛtām</li>
        <li id="R_5.003.007cd">āsādya sahasā hṛṣṭaḥ prākāram abhipedivān</li>
    </ul>
    <ul id="R_5.003.008">
        <li id="R_5.003.008ab">vismayāviṣṭahṛdayaḥ purīm ālokya sarvataḥ</li>
        <li id="R_5.003.008cd">jāmbūnadamayair dvārair vaidūryakṛtavedikaiḥ</li>
    </ul>
    <ul id="R_5.003.009">
        <li id="R_5.003.009ab">maṇisphaṭika muktābhir maṇikuṭṭimabhūṣitaiḥ</li>
        <li id="R_5.003.009cd">taptahāṭakaniryūhai rājatāmalapāṇḍuraiḥ</li>
    </ul>
    <ul id="R_5.003.010">
        <li id="R_5.003.010ab">vaidūryatalasopānaiḥ sphāṭikāntarapāṃsubhiḥ</li>
        <li id="R_5.003.010cd">cārusaṃjavanopetaiḥ kham ivotpatitaiḥ śubhaiḥ</li>
    </ul>
    <ul id="R_5.003.011">
        <li id="R_5.003.011ab">krauñcabarhiṇasaṃghuṣṭe rājahaṃsaniṣevitaiḥ</li>
        <li id="R_5.003.011cd">tūryābharaṇanirghoṣaiḥ sarvataḥ pratināditām</li>
    </ul>
    <ul id="R_5.003.012">
        <li id="R_5.003.012ab">vasvokasārāpratimāṃ samīkṣya nagarīṃ tataḥ</li>
        <li id="R_5.003.012cd">kham ivotpatitāṃ laṅkāṃ jaharṣa hanumān kapiḥ</li>
    </ul>
    <ul id="R_5.003.013">
        <li id="R_5.003.013ab">tāṃ samīkṣya purīṃ laṅkāṃ rākṣasādhipateḥ śubhām</li>
        <li id="R_5.003.013cd">anuttamām ṛddhiyutāṃ cintayām āsa vīryavān</li>
    </ul>
    <ul id="R_5.003.014">
        <li id="R_5.003.014ab">neyam anyena nagarī śakyā dharṣayituṃ balāt</li>
        <li id="R_5.003.014cd">rakṣitā rāvaṇabalair udyatāyudhadhāribhiḥ</li>
    </ul>
    <ul id="R_5.003.015">
        <li id="R_5.003.015ab">kumudāṅgadayor vāpi suṣeṇasya mahākapeḥ</li>
        <li id="R_5.003.015cd">prasiddheyaṃ bhaved bhūmir maindadvividayor api</li>
    </ul>
    <ul id="R_5.003.016">
        <li id="R_5.003.016ab">vivasvatas tanūjasya hareś ca kuśaparvaṇaḥ</li>
        <li id="R_5.003.016cd">ṛkṣasya ketumālasya mama caiva gatir bhavet</li>
    </ul>
    <ul id="R_5.003.017">
        <li id="R_5.003.017ab">samīkṣya tu mahābāho rāghavasya parākramam</li>
        <li id="R_5.003.017cd">lakṣmaṇasya ca vikrāntam abhavat prītimān kapiḥ</li>
    </ul>
    <ul id="R_5.003.018">
        <li id="R_5.003.018ab">tāṃ ratnavasanopetāṃ koṣṭhāgārāvataṃsakām</li>
        <li id="R_5.003.018cd">yantrāgārastanīm ṛddhāṃ pramadām iva bhūṣitām</li>
    </ul>
    <ul id="R_5.003.019">
        <li id="R_5.003.019ab">tāṃ naṣṭatimirāṃ dīpair bhāsvaraiś ca mahāgṛhaiḥ</li>
        <li id="R_5.003.019cd">nagarīṃ rākṣasendrasya dadarśa sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.003.020">
        <li id="R_5.003.020ab">praviṣṭaḥ sattvasaṃpanno niśāyāṃ mārutātmajaḥ</li>
        <li id="R_5.003.020cd">sa mahāpatham āsthāya muktāpuṣpavirājitam</li>
    </ul>
    <ul id="R_5.003.021">
        <li id="R_5.003.021ab">hasitodghuṣṭaninadais tūryaghoṣa puraḥ saraiḥ</li>
        <li id="R_5.003.021cd">vajrāṅkuśanikāśaiś ca vajrajālavibhūṣitaiḥ</li>
        <li id="R_5.003.021ef">gṛhamedhaiḥ purī ramyā babhāse dyaur ivāmbudaiḥ</li>
    </ul>
    <ul id="R_5.003.022">
        <li id="R_5.003.022ab">prajajvāla tadā laṅkā rakṣogaṇagṛhaiḥ śubhaiḥ</li>
        <li id="R_5.003.022cd">sitābhrasadṛśaiś citraiḥ padmasvastikasaṃsthitaiḥ</li>
        <li id="R_5.003.022ef">vardhamānagṛhaiś cāpi sarvataḥ suvibhāṣitaiḥ</li>
    </ul>
    <ul id="R_5.003.023">
        <li id="R_5.003.023ab">tāṃ citramālyābharaṇāṃ kapirājahitaṃkaraḥ</li>
        <li id="R_5.003.023cd">rāghavārthaṃ carañ śrīmān dadarśa ca nananda ca</li>
    </ul>
    <ul id="R_5.003.024">
        <li id="R_5.003.024ab">śuśrāva madhuraṃ gītaṃ tristhānasvarabhūṣitam</li>
        <li id="R_5.003.024cd">strīṇāṃ madasamṛddhānāṃ divi cāpsarasām iva</li>
    </ul>
    <ul id="R_5.003.025">
        <li id="R_5.003.025ab">śuśrāva kāñcīninādaṃ nūpurāṇāṃ ca niḥsvanam</li>
        <li id="R_5.003.025cd">sopānaninadāṃś caiva bhavaneṣu mahātmanam</li>
        <li id="R_5.003.025ef">āsphoṭitaninādāṃś ca kṣveḍitāṃś ca tatas tataḥ</li>
    </ul>
    <ul id="R_5.003.026">
        <li id="R_5.003.026ab">svādhyāya niratāṃś caiva yātudhānān dadarśa saḥ</li>
        <li id="R_5.003.026cd">rāvaṇastavasaṃyuktān garjato rākṣasān api</li>
    </ul>
    <ul id="R_5.003.027">
        <li id="R_5.003.027ab">rājamārgaṃ samāvṛtya sthitaṃ rakṣobalaṃ mahat</li>
        <li id="R_5.003.027cd">dadarśa madhyame gulme rākṣasasya carān bahūn</li>
    </ul>
    <ul id="R_5.003.028">
        <li id="R_5.003.028ab">dīkṣitāñ jaṭilān muṇḍān go'jināmbaravāsasaḥ</li>
        <li id="R_5.003.028cd">darbhamuṣṭipraharaṇān agnikuṇḍāyudhāṃs tathā</li>
    </ul>
    <ul id="R_5.003.029">
        <li id="R_5.003.029ab">kūṭamudgarapāṇīṃś ca daṇḍāyudhadharān api</li>
        <li id="R_5.003.029cd">ekākṣānekakarṇāṃś ca calallambapayodharān</li>
    </ul>
    <ul id="R_5.003.030">
        <li id="R_5.003.030ab">karālān bhugnavaktrāṃś ca vikaṭān vāmanāṃs tathā</li>
        <li id="R_5.003.030cd">dhanvinaḥ khaḍginaś caiva śataghnī musalāyudhān</li>
        <li id="R_5.003.030ef">parighottamahastāṃś ca vicitrakavacojjvalān</li>
    </ul>
    <ul id="R_5.003.031">
        <li id="R_5.003.031ab">nātiṣṭhūlān nātikṛśān nātidīrghātihrasvakān</li>
        <li id="R_5.003.031cd">virūpān bahurūpāṃś ca surūpāṃś ca suvarcasaḥ</li>
    </ul>
    <ul id="R_5.003.032">
        <li id="R_5.003.032ab">śaktivṛkṣāyudhāṃś caiva paṭṭiśāśanidhāriṇaḥ</li>
        <li id="R_5.003.032cd">kṣepaṇīpāśahastāṃś ca dadarśa sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.003.033">
        <li id="R_5.003.033ab">sragviṇas tv anuliptāṃś ca varābharaṇabhūṣitān</li>
        <li id="R_5.003.033cd">tīkṣṇaśūladharāṃś caiva vajriṇaś ca mahābalān</li>
    </ul>
    <ul id="R_5.003.034">
        <li id="R_5.003.034ab">śatasāhasram avyagram ārakṣaṃ madhyamaṃ kapiḥ</li>
        <li id="R_5.003.034cd">prākārāvṛtam atyantaṃ dadarśa sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.003.035">
        <li id="R_5.003.035ab">triviṣṭapanibhaṃ divyaṃ divyanādavināditam</li>
        <li id="R_5.003.035cd">vājiheṣitasaṃghuṣṭaṃ nāditaṃ bhūṣaṇais tathā</li>
    </ul>
    <ul id="R_5.003.036">
        <li id="R_5.003.036ab">rathair yānair vimānaiś ca tathā gajahayaiḥ śubhaiḥ</li>
        <li id="R_5.003.036cd">vāraṇaiś ca caturdantaiḥ śvetābhranicayopamaiḥ</li>
    </ul>
    <ul id="R_5.003.037">
        <li id="R_5.003.037ab">bhūṣitaṃ ruciradvāraṃ mattaiś ca mṛgapakṣibhiḥ</li>
        <li id="R_5.003.037cd">rākṣasādhipater guptam āviveśa gṛhaṃ kapiḥ</li>
    </ul>
    <ul id="R_5.004.001">
        <li id="R_5.004.001ab">
            <seg type="pāda" n="a">tataḥ sa madhyaṃ gatam aṃśumantaṃ</seg>
            <seg type="pāda" n="b">jyotsnāvitānaṃ mahad udvamantam</seg>
        </li>
        <li id="R_5.004.001cd">
            <seg type="pāda" n="c">dadarśa dhīmān divi bhānumantaṃ</seg>
            <seg type="pāda" n="d">goṣṭhe vṛṣaṃ mattam iva bhramantam</seg>
        </li>
    </ul>
    <ul id="R_5.004.002">
        <li id="R_5.004.002ab">
            <seg type="pāda" n="a">lokasya pāpāni vināśayantaṃ</seg>
            <seg type="pāda" n="b">mahodadhiṃ cāpi samedhayantam</seg>
        </li>
        <li id="R_5.004.002cd">
            <seg type="pāda" n="c">bhūtāni sarvāṇi virājayantaṃ</seg>
            <seg type="pāda" n="d">dadarśa śītāṃśum athābhiyāntam</seg>
        </li>
    </ul>
    <ul id="R_5.004.003">
        <li id="R_5.004.003ab">
            <seg type="pāda" n="a">yā bhāti lakṣmīr bhuvi mandarasthā</seg>
            <seg type="pāda" n="b">tathā pradoṣeṣu ca sāgarasthā</seg>
        </li>
        <li id="R_5.004.003cd">
            <seg type="pāda" n="c">tathaiva toyeṣu ca puṣkarasthā</seg>
            <seg type="pāda" n="d">rarāja sā cāruniśākarasthā</seg>
        </li>
    </ul>
    <ul id="R_5.004.004">
        <li id="R_5.004.004ab">
            <seg type="pāda" n="a">haṃso yathā rājatapañjurasthaḥ</seg>
            <seg type="pāda" n="b">siṃho yathā mandarakandarasthaḥ</seg>
        </li>
        <li id="R_5.004.004cd">
            <seg type="pāda" n="c">vīro yathā garvitakuñjarasthaś</seg>
            <seg type="pāda" n="d">candro 'pi babhrāja tathāmbarasthaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.005">
        <li id="R_5.004.005ab">
            <seg type="pāda" n="a">sthitaḥ kakudmān iva tīkṣṇaśṛṅgo</seg>
            <seg type="pāda" n="b">mahācalaḥ śveta ivoccaśṛṅgaḥ</seg>
        </li>
        <li id="R_5.004.005cd">
            <seg type="pāda" n="c">hastīva jāmbūnadabaddhaśṛṅgo</seg>
            <seg type="pāda" n="d">vibhāti candraḥ paripūrṇaśṛṅgaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.006">
        <li id="R_5.004.006ab">
            <seg type="pāda" n="a">prakāśacandrodayanaṣṭadoṣaḥ</seg>
            <seg type="pāda" n="b">pravṛddharakṣaḥ piśitāśadoṣaḥ</seg>
        </li>
        <li id="R_5.004.006cd">
            <seg type="pāda" n="c">rāmābhirāmeritacittadoṣaḥ</seg>
            <seg type="pāda" n="d">svargaprakāśo bhagavān pradoṣaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.007">
        <li id="R_5.004.007ab">
            <seg type="pāda" n="a">tantrī svanāḥ karṇasukhāḥ pravṛttāḥ</seg>
            <seg type="pāda" n="b">svapanti nāryaḥ patibhiḥ suvṛttāḥ</seg>
        </li>
        <li id="R_5.004.007cd">
            <seg type="pāda" n="c">naktaṃcarāś cāpi tathā pravṛttā</seg>
            <seg type="pāda" n="d">vihartum atyadbhutaraudravṛttāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.008">
        <li id="R_5.004.008ab">
            <seg type="pāda" n="a">mattapramattāni samākulāni</seg>
            <seg type="pāda" n="b">rathāśvabhadrāsanasaṃkulāni</seg>
        </li>
        <li id="R_5.004.008cd">
            <seg type="pāda" n="c">vīraśriyā cāpi samākulāni</seg>
            <seg type="pāda" n="d">dadarśa dhīmān sa kapiḥ kulāni</seg>
        </li>
    </ul>
    <ul id="R_5.004.009">
        <li id="R_5.004.009ab">
            <seg type="pāda" n="a">parasparaṃ cādhikam ākṣipanti</seg>
            <seg type="pāda" n="b">bhujāṃś ca pīnān adhivikṣipanti</seg>
        </li>
        <li id="R_5.004.009cd">
            <seg type="pāda" n="c">mattapralāpān adhivikṣipanti</seg>
            <seg type="pāda" n="d">mattāni cānyonyam adhikṣipanti</seg>
        </li>
    </ul>
    <ul id="R_5.004.010">
        <li id="R_5.004.010ab">
            <seg type="pāda" n="a">rakṣāṃsi vakṣāṃsi ca vikṣipanti</seg>
            <seg type="pāda" n="b">gātrāṇi kāntāsu ca vikṣipanti</seg>
        </li>
        <li id="R_5.004.010cd">
            <seg type="pāda" n="c">dadarśa kāntāś ca samālapanti</seg>
            <seg type="pāda" n="d">tathāparās tatra punaḥ svapanti</seg>
        </li>
    </ul>
    <ul id="R_5.004.011">
        <li id="R_5.004.011ab">
            <seg type="pāda" n="a">mahāgajaiś cāpi tathā nadadbhiḥ</seg>
            <seg type="pāda" n="b">sūpūjitaiś cāpi tathā susadbhiḥ</seg>
        </li>
        <li id="R_5.004.011cd">
            <seg type="pāda" n="c">rarāja vīraiś ca viniḥśvasadbhir</seg>
            <seg type="pāda" n="d">hrado bhujaṅgair iva niḥśvasadbhiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.012">
        <li id="R_5.004.012ab">
            <seg type="pāda" n="a">buddhipradhānān rucirābhidhānān</seg>
            <seg type="pāda" n="b">saṃśraddadhānāñ jagataḥ pradhānān</seg>
        </li>
        <li id="R_5.004.012cd">
            <seg type="pāda" n="c">nānāvidhānān rucirābhidhānān</seg>
            <seg type="pāda" n="d">dadarśa tasyāṃ puri yātudhānān</seg>
        </li>
    </ul>
    <ul id="R_5.004.013">
        <li id="R_5.004.013ab">
            <seg type="pāda" n="a">nananda dṛṣṭvā sa ca tān surūpān</seg>
            <seg type="pāda" n="b">nānāguṇān ātmaguṇānurūpān</seg>
        </li>
        <li id="R_5.004.013cd">
            <seg type="pāda" n="c">vidyotamānān sa ca tān surūpān</seg>
            <seg type="pāda" n="d">dadarśa kāṃś cic ca punar virūpān</seg>
        </li>
    </ul>
    <ul id="R_5.004.014">
        <li id="R_5.004.014ab">
            <seg type="pāda" n="a">tato varārhāḥ suviśuddhabhāvās</seg>
            <seg type="pāda" n="b">teṣāṃ striyas tatra mahānubhāvāḥ</seg>
        </li>
        <li id="R_5.004.014cd">
            <seg type="pāda" n="c">priyeṣu pāneṣu ca saktabhāvā</seg>
            <seg type="pāda" n="d">dadarśa tārā iva suprabhāvāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.015">
        <li id="R_5.004.015ab">
            <seg type="pāda" n="a">śriyā jvalantīs trapayopagūḍhā</seg>
            <seg type="pāda" n="b">niśīthakāle ramaṇopagūḍhāḥ</seg>
        </li>
        <li id="R_5.004.015cd">
            <seg type="pāda" n="c">dadarśa kāś cit pramadopagūḍhā</seg>
            <seg type="pāda" n="d">yathā vihaṃgāḥ kusumopagūḍāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.016">
        <li id="R_5.004.016ab">
            <seg type="pāda" n="a">anyāḥ punar harmyatalopaviṣṭās</seg>
            <seg type="pāda" n="b">tatra priyāṅkeṣu sukhopaviṣṭāḥ</seg>
        </li>
        <li id="R_5.004.016cd">
            <seg type="pāda" n="c">bhartuḥ priyā dharmaparā niviṣṭā</seg>
            <seg type="pāda" n="d">dadarśa dhīmān manadābhiviṣṭāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.017">
        <li id="R_5.004.017ab">
            <seg type="pāda" n="a">aprāvṛtāḥ kāñcanarājivarṇāḥ</seg>
            <seg type="pāda" n="b">kāś cit parārdhyās tapanīyavarṇāḥ</seg>
        </li>
        <li id="R_5.004.017cd">
            <seg type="pāda" n="c">punaś ca kāś cic chaśalakṣmavarṇāḥ</seg>
            <seg type="pāda" n="d">kāntaprahīṇā rucirāṅgavarṇāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.018">
        <li id="R_5.004.018ab">
            <seg type="pāda" n="a">tataḥ priyān prāpya mano'bhirāmān</seg>
            <seg type="pāda" n="b">suprītiyuktāḥ prasamīkṣya rāmāḥ</seg>
        </li>
        <li id="R_5.004.018cd">
            <seg type="pāda" n="c">gṛheṣu hṛṣṭāḥ paramābhirāmā</seg>
            <seg type="pāda" n="d">haripravīraḥ sa dadarśa rāmāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.019">
        <li id="R_5.004.019ab">
            <seg type="pāda" n="a">candraprakāśāś ca hi vaktramālā</seg>
            <seg type="pāda" n="b">vakrākṣipakṣmāś ca sunetramālāḥ</seg>
        </li>
        <li id="R_5.004.019cd">
            <seg type="pāda" n="c">vibhūṣaṇānāṃ ca dadarśa mālāḥ</seg>
            <seg type="pāda" n="d">śatahradānām iva cārumālāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.004.020">
        <li id="R_5.004.020ab">
            <seg type="pāda" n="a">na tv eva sītāṃ paramābhijātāṃ</seg>
            <seg type="pāda" n="b">pathi sthite rājakule prajātām</seg>
        </li>
        <li id="R_5.004.020cd">
            <seg type="pāda" n="c">latāṃ praphullām iva sādhujātāṃ</seg>
            <seg type="pāda" n="d">dadarśa tanvīṃ manasābhijātām</seg>
        </li>
    </ul>
    <ul id="R_5.004.021">
        <li id="R_5.004.021ab">
            <seg type="pāda" n="a">sanātane vartmani saṃniviṣṭāṃ</seg>
            <seg type="pāda" n="b">rāmekṣaṇīṃ tāṃ madanābhiviṣṭām</seg>
        </li>
        <li id="R_5.004.021cd">
            <seg type="pāda" n="c">bhartur manaḥ śrīmad anupraviṣṭāṃ</seg>
            <seg type="pāda" n="d">strībhyo varābhyaś ca sadā viśiṣṭām</seg>
        </li>
    </ul>
    <ul id="R_5.004.022">
        <li id="R_5.004.022ab">
            <seg type="pāda" n="a">uṣṇārditāṃ sānusṛtāsrakaṇṭhīṃ</seg>
            <seg type="pāda" n="b">purā varārhottamaniṣkakaṇṭhīm</seg>
        </li>
        <li id="R_5.004.022cd">
            <seg type="pāda" n="c">sujātapakṣmām abhiraktakaṇṭhīṃ</seg>
            <seg type="pāda" n="d">vane pravṛttām iva nīlakaṇṭhīm</seg>
        </li>
    </ul>
    <ul id="R_5.004.023">
        <li id="R_5.004.023ab">
            <seg type="pāda" n="a">avyaktalekhām iva candralekhāṃ</seg>
            <seg type="pāda" n="b">pāṃsupradigdhām iva hemalekhām</seg>
        </li>
        <li id="R_5.004.023cd">
            <seg type="pāda" n="c">kṣataprarūḍhām iva bāṇalekhāṃ</seg>
            <seg type="pāda" n="d">vāyuprabhinnām iva meghalekhām</seg>
        </li>
    </ul>
    <ul id="R_5.004.024">
        <li id="R_5.004.024ab">
            <seg type="pāda" n="a">sītām apaśyan manujeśvarasya</seg>
            <seg type="pāda" n="b">rāmasya patnīṃ vadatāṃ varasya</seg>
        </li>
        <li id="R_5.004.024cd">
            <seg type="pāda" n="c">babhūva duḥkhābhihataś cirasya</seg>
            <seg type="pāda" n="d">plavaṃgamo manda ivācirasya</seg>
        </li>
    </ul>
    <ul id="R_5.005.001">
        <li id="R_5.005.001ab">sa nikāmaṃ vināmeṣu vicaran kāmarūpadhṛk</li>
        <li id="R_5.005.001cd">vicacāra kapir laṅkāṃ lāghavena samanvitaḥ</li>
    </ul>
    <ul id="R_5.005.002">
        <li id="R_5.005.002ab">āsasādātha lakṣmīvān rākṣasendraniveśanam</li>
        <li id="R_5.005.002cd">prākāreṇārkavarṇena bhāsvareṇābhisaṃvṛtam</li>
    </ul>
    <ul id="R_5.005.003">
        <li id="R_5.005.003ab">rakṣitaṃ rākṣasair bhīmaiḥ siṃhair iva mahad vanam</li>
        <li id="R_5.005.003cd">samīkṣamāṇo bhavanaṃ cakāśe kapikuñjaraḥ</li>
    </ul>
    <ul id="R_5.005.004">
        <li id="R_5.005.004ab">rūpyakopahitaiś citrais toraṇair hemabhūṣitaiḥ</li>
        <li id="R_5.005.004cd">vicitrābhiś ca kakṣyābhir dvāraiś ca rucirair vṛtam</li>
    </ul>
    <ul id="R_5.005.005">
        <li id="R_5.005.005ab">gajāsthitair mahāmātraiḥ śūraiś ca vigataśramaiḥ</li>
        <li id="R_5.005.005cd">upasthitam asaṃhāryair hayaiḥ syandanayāyibhiḥ</li>
    </ul>
    <ul id="R_5.005.006">
        <li id="R_5.005.006ab">siṃhavyāghratanutrāṇair dāntakāñcanarājataiḥ</li>
        <li id="R_5.005.006cd">ghoṣavadbhir vicitraiś ca sadā vicaritaṃ rathaiḥ</li>
    </ul>
    <ul id="R_5.005.007">
        <li id="R_5.005.007ab">bahuratnasamākīrṇaṃ parārdhyāsanabhājanam</li>
        <li id="R_5.005.007cd">mahārathasamāvāsaṃ mahārathamahāsanam</li>
    </ul>
    <ul id="R_5.005.008">
        <li id="R_5.005.008ab">dṛśyaiś ca paramodārais tais taiś ca mṛgapakṣibhiḥ</li>
        <li id="R_5.005.008cd">vividhair bahusāhasraiḥ paripūrṇaṃ samantataḥ</li>
    </ul>
    <ul id="R_5.005.009">
        <li id="R_5.005.009ab">vinītair antapālaiś ca rakṣobhiś ca surakṣitam</li>
        <li id="R_5.005.009cd">mukhyābhiś ca varastrībhiḥ paripūrṇaṃ samantataḥ</li>
    </ul>
    <ul id="R_5.005.010">
        <li id="R_5.005.010ab">muditapramadā ratnaṃ rākṣasendraniveśanam</li>
        <li id="R_5.005.010cd">varābharaṇanirhrādaiḥ samudrasvananiḥsvanam</li>
    </ul>
    <ul id="R_5.005.011">
        <li id="R_5.005.011ab">tad rājaguṇasaṃpannaṃ mukhyaiś ca varacandanaiḥ</li>
        <li id="R_5.005.011cd">bherīmṛdaṅgābhirutaṃ śaṅkhaghoṣavināditam</li>
    </ul>
    <ul id="R_5.005.012">
        <li id="R_5.005.012ab">nityārcitaṃ parvahutaṃ pūjitaṃ rākṣasaiḥ sadā</li>
        <li id="R_5.005.012cd">samudram iva gambhīraṃ samudram iva niḥsvanam</li>
    </ul>
    <ul id="R_5.005.013">
        <li id="R_5.005.013ab">mahātmāno mahad veśma mahāratnaparicchadam</li>
        <li id="R_5.005.013cd">mahājanasamākīrṇaṃ dadarśa sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.005.014">
        <li id="R_5.005.014ab">virājamānaṃ vapuṣā gajāśvarathasaṃkulam</li>
        <li id="R_5.005.014cd">laṅkābharaṇam ity eva so 'manyata mahākapiḥ</li>
    </ul>
    <ul id="R_5.005.015">
        <li id="R_5.005.015ab">gṛhād gṛhaṃ rākṣasānām udyānāni ca vānaraḥ</li>
        <li id="R_5.005.015cd">vīkṣamāṇo hy asaṃtrastaḥ prāsādāṃś ca cacāra saḥ</li>
    </ul>
    <ul id="R_5.005.016">
        <li id="R_5.005.016ab">avaplutya mahāvegaḥ prahastasya niveśanam</li>
        <li id="R_5.005.016cd">tato 'nyat pupluve veśma mahāpārśvasya vīryavān</li>
    </ul>
    <ul id="R_5.005.017">
        <li id="R_5.005.017ab">atha meghapratīkāśaṃ kumbhakarṇaniveśanam</li>
        <li id="R_5.005.017cd">vibhīṣaṇasya ca tathā pupluve sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.005.018">
        <li id="R_5.005.018ab">mahodarasya ca tathā virūpākṣasya caiva hi</li>
        <li id="R_5.005.018cd">vidyujjihvasya bhavanaṃ vidyunmāles tathaiva ca</li>
        <li id="R_5.005.018ef">vajradaṃṣṭrasya ca tathā pupluve sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.005.019">
        <li id="R_5.005.019ab">śukasya ca mahāvegaḥ sāraṇasya ca dhīmataḥ</li>
        <li id="R_5.005.019cd">tathā cendrajito veśma jagāma hariyūthapaḥ</li>
    </ul>
    <ul id="R_5.005.020">
        <li id="R_5.005.020ab">jambumāleḥ sumāleś ca jagāma hariyūthapaḥ</li>
        <li id="R_5.005.020cd">raśmiketoś ca bhavanaṃ sūryaśatros tathaiva ca</li>
    </ul>
    <ul id="R_5.005.021">
        <li id="R_5.005.021ab">dhūmrākṣasya ca saṃpāter bhavanaṃ mārutātmajaḥ</li>
        <li id="R_5.005.021cd">vidyudrūpasya bhīmasya ghanasya vighanasya ca</li>
    </ul>
    <ul id="R_5.005.022">
        <li id="R_5.005.022ab">śukanābhasya vakrasya śaṭhasya vikaṭasya ca</li>
        <li id="R_5.005.022cd">hrasvakarṇasya daṃṣṭrasya romaśasya ca rakṣasaḥ</li>
    </ul>
    <ul id="R_5.005.023">
        <li id="R_5.005.023ab">yuddhonmattasya mattasya dhvajagrīvasya nādinaḥ</li>
        <li id="R_5.005.023cd">vidyujjihvendrajihvānāṃ tathā hastimukhasya ca</li>
    </ul>
    <ul id="R_5.005.024">
        <li id="R_5.005.024ab">karālasya piśācasya śoṇitākṣasya caiva hi</li>
        <li id="R_5.005.024cd">kramamāṇaḥ krameṇaiva hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.005.025">
        <li id="R_5.005.025ab">teṣu teṣu mahārheṣu bhavaneṣu mahāyaśāḥ</li>
        <li id="R_5.005.025cd">teṣām ṛddhimatām ṛddhiṃ dadarśa sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.005.026">
        <li id="R_5.005.026ab">sarveṣāṃ samatikramya bhavanāni samantataḥ</li>
        <li id="R_5.005.026cd">āsasādātha lakṣmīvān rākṣasendraniveśanam</li>
    </ul>
    <ul id="R_5.005.027">
        <li id="R_5.005.027ab">rāvaṇasyopaśāyinyo dadarśa harisattamaḥ</li>
        <li id="R_5.005.027cd">vicaran hariśārdūlo rākṣasīr vikṛtekṣaṇāḥ</li>
        <li id="R_5.005.027ef">śūlamudgarahastāś ca śakto tomaradhāriṇīḥ</li>
    </ul>
    <ul id="R_5.005.028">
        <li id="R_5.005.028ab">dadarśa vividhān gulmāṃs tasya rakṣaḥpater gṛhe</li>
    </ul>
    <ul id="R_5.005.029">
        <li id="R_5.005.029ab">raktāñ śvetān sitāṃś caiva harīṃś caiva mahājavān</li>
        <li id="R_5.005.029cd">kulīnān rūpasaṃpannān gajān paragajārujān</li>
    </ul>
    <ul id="R_5.005.030">
        <li id="R_5.005.030ab">niṣṭhitān gajaśikhāyām airāvatasamān yudhi</li>
        <li id="R_5.005.030cd">nihantṝn parasainyānāṃ gṛhe tasmin dadarśa saḥ</li>
    </ul>
    <ul id="R_5.005.031">
        <li id="R_5.005.031ab">kṣarataś ca yathā meghān sravataś ca yathā girīn</li>
        <li id="R_5.005.031cd">meghastanitanirghoṣān durdharṣān samare paraiḥ</li>
    </ul>
    <ul id="R_5.005.032">
        <li id="R_5.005.032ab">sahasraṃ vāhinīs tatra jāmbūnadapariṣkṛtāḥ</li>
        <li id="R_5.005.032cd">hemajālair avicchinnās taruṇādityasaṃnibhāḥ</li>
    </ul>
    <ul id="R_5.005.033">
        <li id="R_5.005.033ab">dadarśa rākṣasendrasya rāvaṇasya niveśane</li>
        <li id="R_5.005.033cd">śibikā vividhākārāḥ sa kapir mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.005.034">
        <li id="R_5.005.034ab">latāgṛhāṇi citrāṇi citraśālāgṛhāṇi ca</li>
        <li id="R_5.005.034cd">krīḍāgṛhāṇi cānyāni dāruparvatakān api</li>
    </ul>
    <ul id="R_5.005.035">
        <li id="R_5.005.035ab">kāmasya gṛhakaṃ ramyaṃ divāgṛhakam eva ca</li>
        <li id="R_5.005.035cd">dadarśa rākṣasendrasya rāvaṇasya niveśane</li>
    </ul>
    <ul id="R_5.005.036">
        <li id="R_5.005.036ab">sa mandaratalaprakhyaṃ mayūrasthānasaṃkulam</li>
        <li id="R_5.005.036cd">dhvajayaṣṭibhir ākīrṇaṃ dadarśa bhavanottamam</li>
    </ul>
    <ul id="R_5.005.037">
        <li id="R_5.005.037ab">anantaratnanicayaṃ nidhijālaṃ samantataḥ</li>
        <li id="R_5.005.037cd">dhīraniṣṭhitakarmāntaṃ gṛhaṃ bhūtapater iva</li>
    </ul>
    <ul id="R_5.005.038">
        <li id="R_5.005.038ab">arcirbhiś cāpi ratnānāṃ tejasā rāvaṇasya ca</li>
        <li id="R_5.005.038cd">virarājātha tad veśma raśmimān iva raśmibhiḥ</li>
    </ul>
    <ul id="R_5.005.039">
        <li id="R_5.005.039ab">jāmbūnadamayāny eva śayanāny āsanāni ca</li>
        <li id="R_5.005.039cd">bhājanāni ca śubhrāṇi dadarśa hariyūthapaḥ</li>
    </ul>
    <ul id="R_5.005.040">
        <li id="R_5.005.040ab">madhvāsavakṛtakledaṃ maṇibhājanasaṃkulam</li>
        <li id="R_5.005.040cd">manoramam asaṃbādhaṃ kuberabhavanaṃ yathā</li>
    </ul>
    <ul id="R_5.005.041">
        <li id="R_5.005.041ab">nūpurāṇāṃ ca ghoṣeṇa kāñcīnāṃ ninadena ca</li>
        <li id="R_5.005.041cd">mṛdaṅgatalaghoṣaiś ca ghoṣavadbhir vināditam</li>
    </ul>
    <ul id="R_5.005.042">
        <li id="R_5.005.042ab">prāsādasaṃghātayutaṃ strīratnaśatasaṃkulam</li>
        <li id="R_5.005.042cd">suvyūḍhakakṣyaṃ hanumān praviveśa mahāgṛham</li>
    </ul>
    <ul id="R_5.006.001">
        <li id="R_5.006.001ab">
            <seg type="pāda" n="a">sa veśmajālaṃ balavān dadarśa</seg>
            <seg type="pāda" n="b">vyāsaktavaidūryasuvarṇajālam</seg>
        </li>
        <li id="R_5.006.001cd">
            <seg type="pāda" n="c">yathā mahat prāvṛṣi meghajālaṃ</seg>
            <seg type="pāda" n="d">vidyutpinaddhaṃ savihaṃgajālam</seg>
        </li>
    </ul>
    <ul id="R_5.006.002">
        <li id="R_5.006.002ab">
            <seg type="pāda" n="a">niveśanānāṃ vividhāś ca śālāḥ</seg>
            <seg type="pāda" n="b">pradhānaśaṅkhāyudhacāpaśālāḥ</seg>
        </li>
        <li id="R_5.006.002cd">
            <seg type="pāda" n="c">manoharāś cāpi punar viśālā</seg>
            <seg type="pāda" n="d">dadarśa veśmādriṣu candraśālāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.006.003">
        <li id="R_5.006.003ab">
            <seg type="pāda" n="a">gṛhāṇi nānāvasurājitāni</seg>
            <seg type="pāda" n="b">devāsuraiś cāpi supūjitāni</seg>
        </li>
        <li id="R_5.006.003cd">
            <seg type="pāda" n="c">sarvaiś ca doṣaiḥ parivarjitāni</seg>
            <seg type="pāda" n="d">kapir dadarśa svabalārjitāni</seg>
        </li>
    </ul>
    <ul id="R_5.006.004">
        <li id="R_5.006.004ab">
            <seg type="pāda" n="a">tāni prayatnābhisamāhitāni</seg>
            <seg type="pāda" n="b">mayena sākṣād iva nirmitāni</seg>
        </li>
        <li id="R_5.006.004cd">
            <seg type="pāda" n="c">mahītale sarvaguṇottarāṇi</seg>
            <seg type="pāda" n="d">dadarśa laṅkādhipater gṛhāṇi</seg>
        </li>
    </ul>
    <ul id="R_5.006.005">
        <li id="R_5.006.005ab">
            <seg type="pāda" n="a">tato dadarśocchritamegharūpaṃ</seg>
            <seg type="pāda" n="b">manoharaṃ kāñcanacārurūpam</seg>
        </li>
        <li id="R_5.006.005cd">
            <seg type="pāda" n="c">rakṣo'dhipasyātmabalānurūpaṃ</seg>
            <seg type="pāda" n="d">gṛhottamaṃ hy apratirūparūpam</seg>
        </li>
    </ul>
    <ul id="R_5.006.006">
        <li id="R_5.006.006ab">
            <seg type="pāda" n="a">mahītale svargam iva prakīrṇaṃ</seg>
            <seg type="pāda" n="b">śriyā jvalantaṃ bahuratnakīrṇam</seg>
        </li>
        <li id="R_5.006.006cd">
            <seg type="pāda" n="c">nānātarūṇāṃ kusumāvakīrṇaṃ</seg>
            <seg type="pāda" n="d">girer ivāgraṃ rajasāvakīrṇam</seg>
        </li>
    </ul>
    <ul id="R_5.006.007">
        <li id="R_5.006.007ab">
            <seg type="pāda" n="a">nārīpravekair iva dīpyamānaṃ</seg>
            <seg type="pāda" n="b">taḍidbhir ambhodavad arcyamānam</seg>
        </li>
        <li id="R_5.006.007cd">
            <seg type="pāda" n="c">haṃsapravekair iva vāhyamānaṃ</seg>
            <seg type="pāda" n="d">śriyā yutaṃ khe sukṛtāṃ vimānam</seg>
        </li>
    </ul>
    <ul id="R_5.006.008">
        <li id="R_5.006.008ab">
            <seg type="pāda" n="a">yathā nagāgraṃ bahudhātucitraṃ</seg>
            <seg type="pāda" n="b">yathā nabhaś ca grahacandracitram</seg>
        </li>
        <li id="R_5.006.008cd">
            <seg type="pāda" n="c">dadarśa yuktīkṛtameghacitraṃ</seg>
            <seg type="pāda" n="d">vimānaratnaṃ bahuratnacitram</seg>
        </li>
    </ul>
    <ul id="R_5.006.009">
        <li id="R_5.006.009ab">
            <seg type="pāda" n="a">mahī kṛtā parvatarājipūrṇā</seg>
            <seg type="pāda" n="b">śailāḥ kṛtā vṛkṣavitānapūrṇāḥ</seg>
        </li>
        <li id="R_5.006.009cd">
            <seg type="pāda" n="c">vṛkṣāḥ kṛtāḥ puṣpavitānapūrṇāḥ</seg>
            <seg type="pāda" n="d">puṣpaṃ kṛtaṃ kesarapatrapūrṇam</seg>
        </li>
    </ul>
    <ul id="R_5.006.010">
        <li id="R_5.006.010ab">
            <seg type="pāda" n="a">kṛtāni veśmāni ca pāṇḍurāṇi</seg>
            <seg type="pāda" n="b">tathā supuṣpā api puṣkariṇyaḥ</seg>
        </li>
        <li id="R_5.006.010cd">
            <seg type="pāda" n="c">punaś ca padmāni sakesarāṇi</seg>
            <seg type="pāda" n="d">dhanyāni citrāṇi tathā vanāni</seg>
        </li>
    </ul>
    <ul id="R_5.006.011">
        <li id="R_5.006.011ab">
            <seg type="pāda" n="a">puṣpāhvayaṃ nāma virājamānaṃ</seg>
            <seg type="pāda" n="b">ratnaprabhābhiś ca vivardhamānam</seg>
        </li>
        <li id="R_5.006.011cd">
            <seg type="pāda" n="c">veśmottamānām api coccamānaṃ</seg>
            <seg type="pāda" n="d">mahākapis tatra mahāvimānam</seg>
        </li>
    </ul>
    <ul id="R_5.006.012">
        <li id="R_5.006.012ab">
            <seg type="pāda" n="a">kṛtāś ca vaidūryamayā vihaṃgā</seg>
            <seg type="pāda" n="b">rūpyapravālaiś ca tathā vihaṃgāḥ</seg>
        </li>
        <li id="R_5.006.012cd">
            <seg type="pāda" n="c">citrāś ca nānāvasubhir bhujaṃgā</seg>
            <seg type="pāda" n="d">jātyānurūpās turagāḥ śubhāṅgāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.006.013">
        <li id="R_5.006.013ab">
            <seg type="pāda" n="a">pravālajāmbūnadapuṣpapakṣāḥ</seg>
            <seg type="pāda" n="b">salīlam āvarjitajihmapakṣāḥ</seg>
        </li>
        <li id="R_5.006.013cd">
            <seg type="pāda" n="c">kāmasya sākṣād iva bhānti pakṣāḥ</seg>
            <seg type="pāda" n="d">kṛtā vihaṃgāḥ sumukhāḥ supakṣāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.006.014">
        <li id="R_5.006.014ab">
            <seg type="pāda" n="a">niyujyamānāś ca gajāḥ suhastāḥ</seg>
            <seg type="pāda" n="b">sakesarāś cotpalapatrahastāḥ</seg>
        </li>
        <li id="R_5.006.014cd">
            <seg type="pāda" n="c">babhūva devī ca kṛtā suhastā</seg>
            <seg type="pāda" n="d">lakṣmīs tathā padmini padmahastā</seg>
        </li>
    </ul>
    <ul id="R_5.006.015">
        <li id="R_5.006.015ab">
            <seg type="pāda" n="a">itīva tad gṛham abhigamya śobhanaṃ</seg>
            <seg type="pāda" n="b">savismayo nagam iva cāruśobhanam</seg>
        </li>
        <li id="R_5.006.015cd">
            <seg type="pāda" n="c">punaś ca tat paramasugandhi sundaraṃ</seg>
            <seg type="pāda" n="d">himātyaye nagam iva cārukandaram</seg>
        </li>
    </ul>
    <ul id="R_5.006.016">
        <li id="R_5.006.016ab">
            <seg type="pāda" n="a">tataḥ sa tāṃ kapir abhipatya pūjitāṃ</seg>
            <seg type="pāda" n="b">caran purīṃ daśamukhabāhupālitām</seg>
        </li>
        <li id="R_5.006.016cd">
            <seg type="pāda" n="c">adṛśya tāṃ janakasutāṃ supūjitāṃ</seg>
            <seg type="pāda" n="d">suduḥkhitāṃ patiguṇaveganirjitām</seg>
        </li>
    </ul>
    <ul id="R_5.006.017">
        <li id="R_5.006.017ab">
            <seg type="pāda" n="a">tatas tadā bahuvidhabhāvitātmanaḥ</seg>
            <seg type="pāda" n="b">kṛtātmano janakasutāṃ suvartmanaḥ</seg>
        </li>
        <li id="R_5.006.017cd">
            <seg type="pāda" n="c">apaśyato 'bhavad atiduḥkhitaṃ manaḥ</seg>
            <seg type="pāda" n="d">sucakṣuṣaḥ pravicarato mahātmanaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.007.001">
        <li id="R_5.007.001ab">tasyālayavariṣṭhasya madhye vipulam āyatam</li>
        <li id="R_5.007.001cd">dadarśa bhavanaśreṣṭhaṃ hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.007.002">
        <li id="R_5.007.002ab">ardhayojanavistīrṇam āyataṃ yojanaṃ hi tat</li>
        <li id="R_5.007.002cd">bhavanaṃ rākṣasendrasya bahuprāsādasaṃkulam</li>
    </ul>
    <ul id="R_5.007.003">
        <li id="R_5.007.003ab">mārgamāṇas tu vaidehīṃ sītām āyatalocanām</li>
        <li id="R_5.007.003cd">sarvataḥ paricakrāma hanūmān arisūdanaḥ</li>
    </ul>
    <ul id="R_5.007.004">
        <li id="R_5.007.004ab">caturviṣāṇair dviradais triviṣāṇais tathaiva ca</li>
        <li id="R_5.007.004cd">parikṣiptam asaṃbādhaṃ rakṣyamāṇam udāyudhaiḥ</li>
    </ul>
    <ul id="R_5.007.005">
        <li id="R_5.007.005ab">rākṣasībhiś ca patnībhī rāvaṇasya niveśanam</li>
        <li id="R_5.007.005cd">āhṛtābhiś ca vikramya rājakanyābhir āvṛtam</li>
    </ul>
    <ul id="R_5.007.006">
        <li id="R_5.007.006ab">tan nakramakarākīrṇaṃ timiṃgilajhaṣākulam</li>
        <li id="R_5.007.006cd">vāyuvegasamādhūtaṃ pannagair iva sāgaram</li>
    </ul>
    <ul id="R_5.007.007">
        <li id="R_5.007.007ab">yā hi vaiśvaraṇe lakṣmīr yā cendre harivāhane</li>
        <li id="R_5.007.007cd">sā rāvaṇagṛhe sarvā nityam evānapāyinī</li>
    </ul>
    <ul id="R_5.007.008">
        <li id="R_5.007.008ab">yā ca rājñaḥ kuberasya yamasya varuṇasya ca</li>
        <li id="R_5.007.008cd">tādṛśī tad viśiṣṭā vā ṛddhī rakṣo gṛheṣv iha</li>
    </ul>
    <ul id="R_5.007.009">
        <li id="R_5.007.009ab">tasya harmyasya madhyasthaṃ veśma cānyat sunirmitam</li>
        <li id="R_5.007.009cd">bahuniryūha saṃkīrṇaṃ dadarśa pavanātmajaḥ</li>
    </ul>
    <ul id="R_5.007.010">
        <li id="R_5.007.010ab">brahmaṇo 'rthe kṛtaṃ divyaṃ divi yad viśvakarmaṇā</li>
        <li id="R_5.007.010cd">vimānaṃ puṣpakaṃ nāma sarvaratnavibhūṣitam</li>
    </ul>
    <ul id="R_5.007.011">
        <li id="R_5.007.011ab">pareṇa tapasā lebhe yat kuberaḥ pitāmahāt</li>
        <li id="R_5.007.011cd">kuberam ojasā jitvā lebhe tad rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_5.007.012">
        <li id="R_5.007.012ab">īhā mṛgasamāyuktaiḥ kāryasvarahiraṇmayaiḥ</li>
        <li id="R_5.007.012cd">sukṛtair ācitaṃ stambhaiḥ pradīptam iva ca śriyā</li>
    </ul>
    <ul id="R_5.007.013">
        <li id="R_5.007.013ab">merumandarasaṃkāśair ullikhadbhir ivāmbaram</li>
        <li id="R_5.007.013cd">kūṭāgāraiḥ śubhākāraiḥ sarvataḥ samalaṃkṛtam</li>
    </ul>
    <ul id="R_5.007.014">
        <li id="R_5.007.014ab">jvalanārkapratīkāśaṃ sukṛtaṃ viśvakarmaṇā</li>
        <li id="R_5.007.014cd">hemasopānasaṃyuktaṃ cārupravaravedikam</li>
    </ul>
    <ul id="R_5.007.015">
        <li id="R_5.007.015ab">jālavātāyanair yuktaṃ kāñcanaiḥ sthāṭikair api</li>
        <li id="R_5.007.015cd">indranīlamahānīlamaṇipravaravedikam</li>
        <li id="R_5.007.015ef">vimānaṃ puṣpakaṃ divyam āruroha mahākapiḥ</li>
    </ul>
    <ul id="R_5.007.016">
        <li id="R_5.007.016ab">tatrasthaḥ sa tadā gandhaṃ pānabhakṣyānnasaṃbhavam</li>
        <li id="R_5.007.016cd">divyaṃ saṃmūrchitaṃ jighran rūpavantam ivānilam</li>
    </ul>
    <ul id="R_5.007.017">
        <li id="R_5.007.017ab">sa gandhas taṃ mahāsattvaṃ bandhur bandhum ivottamam</li>
        <li id="R_5.007.017cd">ita ehīty uvāceva tatra yatra sa rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.007.018">
        <li id="R_5.007.018ab">tatas tāṃ prasthitaḥ śālāṃ dadarśa mahatīṃ śubhām</li>
        <li id="R_5.007.018cd">rāvaṇasya manaḥkāntāṃ kāntām iva varastriyam</li>
    </ul>
    <ul id="R_5.007.019">
        <li id="R_5.007.019ab">maṇisopānavikṛtāṃ hemajālavirājitām</li>
        <li id="R_5.007.019cd">sphāṭikair āvṛtatalāṃ dantāntaritarūpikām</li>
    </ul>
    <ul id="R_5.007.020">
        <li id="R_5.007.020ab">muktābhiś ca pravālaiś ca rūpyacāmīkarair api</li>
        <li id="R_5.007.020cd">vibhūṣitāṃ maṇistambhaiḥ subahustambhabhūṣitām</li>
    </ul>
    <ul id="R_5.007.021">
        <li id="R_5.007.021ab">samair ṛjubhir atyuccaiḥ samantāt suvibhūṣitaiḥ</li>
        <li id="R_5.007.021cd">stambhaiḥ pakṣair ivātyuccair divaṃ saṃprasthitām iva</li>
    </ul>
    <ul id="R_5.007.022">
        <li id="R_5.007.022ab">mahatyā kuthayāstrīṇaṃ pṛthivīlakṣaṇāṅkayā</li>
        <li id="R_5.007.022cd">pṛthivīm iva vistīrṇāṃ sarāṣṭragṛhamālinīm</li>
    </ul>
    <ul id="R_5.007.023">
        <li id="R_5.007.023ab">nāditāṃ mattavihagair divyagandhādhivāsitām</li>
        <li id="R_5.007.023cd">parārdhyāstaraṇopetāṃ rakṣo'dhipaniṣevitām</li>
    </ul>
    <ul id="R_5.007.024">
        <li id="R_5.007.024ab">dhūmrām agarudhūpena vimalāṃ haṃsapāṇḍurām</li>
        <li id="R_5.007.024cd">citrāṃ puṣpopahāreṇa kalmāṣīm iva suprabhām</li>
    </ul>
    <ul id="R_5.007.025">
        <li id="R_5.007.025ab">manaḥsaṃhlādajananīṃ varṇasyāpi prasādinīm</li>
        <li id="R_5.007.025cd">tāṃ śokanāśinīṃ divyāṃ śriyaḥ saṃjananīm iva</li>
    </ul>
    <ul id="R_5.007.026">
        <li id="R_5.007.026ab">indriyāṇīndriyārthais tu pañca pañcabhir uttamaiḥ</li>
        <li id="R_5.007.026cd">tarpayām āsa māteva tadā rāvaṇapālitā</li>
    </ul>
    <ul id="R_5.007.027">
        <li id="R_5.007.027ab">svargo 'yaṃ devaloko 'yam indrasyeyaṃ purī bhavet</li>
        <li id="R_5.007.027cd">siddhir veyaṃ parā hi syād ity amanyata mārutiḥ</li>
    </ul>
    <ul id="R_5.007.028">
        <li id="R_5.007.028ab">pradhyāyata ivāpaśyat pradīpāṃs tatra kāñcanān</li>
        <li id="R_5.007.028cd">dhūrtān iva mahādhūrtair devanena parājitān</li>
    </ul>
    <ul id="R_5.007.029">
        <li id="R_5.007.029ab">dīpānāṃ ca prakāśena tejasā rāvaṇasya ca</li>
        <li id="R_5.007.029cd">arcirbhir bhūṣaṇānāṃ ca pradīptety abhyamanyata</li>
    </ul>
    <ul id="R_5.007.030">
        <li id="R_5.007.030ab">tato 'paśyat kuthāsīnaṃ nānāvarṇāmbarasrajam</li>
        <li id="R_5.007.030cd">sahasraṃ varanārīṇāṃ nānāveṣavibhūṣitam</li>
    </ul>
    <ul id="R_5.007.031">
        <li id="R_5.007.031ab">parivṛtte 'rdharātre tu pānanidrāvaśaṃ gatam</li>
        <li id="R_5.007.031cd">krīḍitvoparataṃ rātrau suṣvāpa balavat tadā</li>
    </ul>
    <ul id="R_5.007.032">
        <li id="R_5.007.032ab">tat prasuptaṃ viruruce niḥśabdāntarabhūṣaṇam</li>
        <li id="R_5.007.032cd">niḥśabdahaṃsabhramaraṃ yathā padmavanaṃ mahat</li>
    </ul>
    <ul id="R_5.007.033">
        <li id="R_5.007.033ab">tāsāṃ saṃvṛtadantāni mīlitākṣāṇi mārutiḥ</li>
        <li id="R_5.007.033cd">apaśyat padmagandhīni vadanāni suyoṣitām</li>
    </ul>
    <ul id="R_5.007.034">
        <li id="R_5.007.034ab">prabuddhānīva padmāni tāsāṃ bhūtvā kṣapākṣaye</li>
        <li id="R_5.007.034cd">punaḥsaṃvṛtapatrāṇi rātrāv iva babhus tadā</li>
    </ul>
    <ul id="R_5.007.035">
        <li id="R_5.007.035ab">imāni mukhapadmāni niyataṃ mattaṣaṭpadāḥ</li>
        <li id="R_5.007.035cd">ambujānīva phullāni prārthayanti punaḥ punaḥ</li>
    </ul>
    <ul id="R_5.007.036">
        <li id="R_5.007.036ab">iti vāmanyata śrīmān upapattyā mahākapiḥ</li>
        <li id="R_5.007.036cd">mene hi guṇatas tāni samāni salilodbhavaiḥ</li>
    </ul>
    <ul id="R_5.007.037">
        <li id="R_5.007.037ab">sā tasya śuśubhe śālā tābhiḥ strībhir virājitā</li>
        <li id="R_5.007.037cd">śāradīva prasannā dyaus tārābhir abhiśobhitā</li>
    </ul>
    <ul id="R_5.007.038">
        <li id="R_5.007.038ab">sa ca tābhiḥ parivṛtaḥ śuśubhe rākṣasādhipaḥ</li>
        <li id="R_5.007.038cd">yathā hy uḍupatiḥ śrīmāṃs tārābhir abhisaṃvṛtaḥ</li>
    </ul>
    <ul id="R_5.007.039">
        <li id="R_5.007.039ab">yāś cyavante 'mbarāt tārāḥ puṇyaśeṣasamāvṛtāḥ</li>
        <li id="R_5.007.039cd">imās tāḥ saṃgatāḥ kṛtsnā iti mene haris tadā</li>
    </ul>
    <ul id="R_5.007.040">
        <li id="R_5.007.040ab">tārāṇām iva suvyaktaṃ mahatīnāṃ śubhārciṣām</li>
        <li id="R_5.007.040cd">prabhāvarṇaprasādāś ca virejus tatra yoṣitām</li>
    </ul>
    <ul id="R_5.007.041">
        <li id="R_5.007.041ab">vyāvṛttagurupīnasrakprakīrṇavarabhūṣaṇāḥ</li>
        <li id="R_5.007.041cd">pānavyāyāmakāleṣu nidrāpahṛtacetasaḥ</li>
    </ul>
    <ul id="R_5.007.042">
        <li id="R_5.007.042ab">vyāvṛttatilakāḥ kāś cit kāś cid udbhrāntanūpurāḥ</li>
        <li id="R_5.007.042cd">pārśve galitahārāś ca kāś cit paramayoṣitaḥ</li>
    </ul>
    <ul id="R_5.007.043">
        <li id="R_5.007.043ab">mukhā hāravṛtāś cānyāḥ kāś cit prasrastavāsasaḥ</li>
        <li id="R_5.007.043cd">vyāviddharaśanā dāmāḥ kiśorya iva vāhitāḥ</li>
    </ul>
    <ul id="R_5.007.044">
        <li id="R_5.007.044ab">sukuṇḍaladharāś cānyā vicchinnamṛditasrajaḥ</li>
        <li id="R_5.007.044cd">gajendramṛditāḥ phullā latā iva mahāvane</li>
    </ul>
    <ul id="R_5.007.045">
        <li id="R_5.007.045ab">candrāṃśukiraṇābhāś ca hārāḥ kāsāṃ cid utkaṭāḥ</li>
        <li id="R_5.007.045cd">haṃsā iva babhuḥ suptāḥ stanamadhyeṣu yoṣitām</li>
    </ul>
    <ul id="R_5.007.046">
        <li id="R_5.007.046ab">aparāsāṃ ca vaidūryāḥ kādambā iva pakṣiṇaḥ</li>
        <li id="R_5.007.046cd">hemasūtrāṇi cānyāsāṃ cakravākā ivābhavan</li>
    </ul>
    <ul id="R_5.007.047">
        <li id="R_5.007.047ab">haṃsakāraṇḍavākīrṇāś cakravākopaśobhitāḥ</li>
        <li id="R_5.007.047cd">āpagā iva tā rejur jaghanaiḥ pulinair iva</li>
    </ul>
    <ul id="R_5.007.048">
        <li id="R_5.007.048ab">kiṅkiṇījālasaṃkāśās tā hemavipulāmbujāḥ</li>
        <li id="R_5.007.048cd">bhāvagrāhā yaśastīrāḥ suptā nadya ivābabhuḥ</li>
    </ul>
    <ul id="R_5.007.049">
        <li id="R_5.007.049ab">mṛduṣv aṅgeṣu kāsāṃ cit kucāgreṣu ca saṃsthitāḥ</li>
        <li id="R_5.007.049cd">babhūvur bhūṣaṇānīva śubhā bhūṣaṇarājayaḥ</li>
    </ul>
    <ul id="R_5.007.050">
        <li id="R_5.007.050ab">aṃśukāntāś ca kāsāṃ cin mukhamārutakampitāḥ</li>
        <li id="R_5.007.050cd">upary upari vaktrāṇāṃ vyādhūyante punaḥ punaḥ</li>
    </ul>
    <ul id="R_5.007.051">
        <li id="R_5.007.051ab">tāḥ pātākā ivoddhūtāḥ patnīnāṃ ruciraprabhāḥ</li>
        <li id="R_5.007.051cd">nānāvarṇasuvarṇānāṃ vaktramūleṣu rejire</li>
    </ul>
    <ul id="R_5.007.052">
        <li id="R_5.007.052ab">vavalguś cātra kāsāṃ cit kuṇḍalāni śubhārciṣām</li>
        <li id="R_5.007.052cd">mukhamārutasaṃsargān mandaṃ mandaṃ suyoṣitām</li>
    </ul>
    <ul id="R_5.007.053">
        <li id="R_5.007.053ab">śarkarāsavagandhaḥ sa prakṛtyā surabhiḥ sukhaḥ</li>
        <li id="R_5.007.053cd">tāsāṃ vadananiḥśvāsaḥ siṣeve rāvaṇaṃ tadā</li>
    </ul>
    <ul id="R_5.007.054">
        <li id="R_5.007.054ab">rāvaṇānanaśaṅkāś ca kāś cid rāvaṇayoṣitaḥ</li>
        <li id="R_5.007.054cd">mukhāni sma sapatnīnām upājighran punaḥ punaḥ</li>
    </ul>
    <ul id="R_5.007.055">
        <li id="R_5.007.055ab">atyarthaṃ saktamanaso rāvaṇe tā varastriyaḥ</li>
        <li id="R_5.007.055cd">asvatantrāḥ sapatnīnāṃ priyam evācaraṃs tadā</li>
    </ul>
    <ul id="R_5.007.056">
        <li id="R_5.007.056ab">bāhūn upanidhāyānyāḥ pārihārya vibhūṣitāḥ</li>
        <li id="R_5.007.056cd">aṃśukāni ca ramyāṇi pramadās tatra śiśyire</li>
    </ul>
    <ul id="R_5.007.057">
        <li id="R_5.007.057ab">anyā vakṣasi cānyasyās tasyāḥ kā cit punar bhujam</li>
        <li id="R_5.007.057cd">aparā tv aṅkam anyasyās tasyāś cāpy aparā bhujau</li>
    </ul>
    <ul id="R_5.007.058">
        <li id="R_5.007.058ab">ūrupārśvakaṭīpṛṣṭham anyonyasya samāśritāḥ</li>
        <li id="R_5.007.058cd">parasparaniviṣṭāṅgyo madasnehavaśānugāḥ</li>
    </ul>
    <ul id="R_5.007.059">
        <li id="R_5.007.059ab">anyonyasyāṅgasaṃsparśāt prīyamāṇāḥ sumadhyamāḥ</li>
        <li id="R_5.007.059cd">ekīkṛtabhujāḥ sarvāḥ suṣupus tatra yoṣitaḥ</li>
    </ul>
    <ul id="R_5.007.060">
        <li id="R_5.007.060ab">anyonyabhujasūtreṇa strīmālāgrathitā hi sā</li>
        <li id="R_5.007.060cd">māleva grathitā sūtre śuśubhe mattaṣaṭpadā</li>
    </ul>
    <ul id="R_5.007.061">
        <li id="R_5.007.061ab">latānāṃ mādhave māsi phullānāṃ vāyusevanāt</li>
        <li id="R_5.007.061cd">anyonyamālāgrathitaṃ saṃsaktakusumoccayam</li>
    </ul>
    <ul id="R_5.007.062">
        <li id="R_5.007.062ab">vyativeṣṭitasuskantham anyonyabhramarākulam</li>
        <li id="R_5.007.062cd">āsīd vanam ivoddhūtaṃ strīvanaṃ rāvaṇasya tat</li>
    </ul>
    <ul id="R_5.007.063">
        <li id="R_5.007.063ab">uciteṣv api suvyaktaṃ na tāsāṃ yoṣitāṃ tadā</li>
        <li id="R_5.007.063cd">vivekaḥ śakya ādhātuṃ bhūṣaṇāṅgāmbarasrajām</li>
    </ul>
    <ul id="R_5.007.064">
        <li id="R_5.007.064ab">rāvaṇe sukhasaṃviṣṭe tāḥ striyo vividhaprabhāḥ</li>
        <li id="R_5.007.064cd">jvalantaḥ kāñcanā dīpāḥ prekṣantānimiṣā iva</li>
    </ul>
    <ul id="R_5.007.065">
        <li id="R_5.007.065ab">rājarṣipitṛdaityānāṃ gandharvāṇāṃ ca yoṣitaḥ</li>
        <li id="R_5.007.065cd">rakṣasāṃ cābhavan kanyās tasya kāmavaśaṃ gatāḥ</li>
    </ul>
    <ul id="R_5.007.066">
        <li id="R_5.007.066ab">
            <seg type="pāda" n="a">na tatra kā cit pramadā prasahya</seg>
            <seg type="pāda" n="b">vīryopapannena guṇena labdhā</seg>
        </li>
        <li id="R_5.007.066cd">
            <seg type="pāda" n="c">na cānyakāmāpi na cānyapūrvā</seg>
            <seg type="pāda" n="d">vinā varārhāṃ janakātmajāṃ tu</seg>
        </li>
    </ul>
    <ul id="R_5.007.067">
        <li id="R_5.007.067ab">
            <seg type="pāda" n="a">na cākulīnā na ca hīnarūpā</seg>
            <seg type="pāda" n="b">nādakṣiṇā nānupacāra yuktā</seg>
        </li>
        <li id="R_5.007.067cd">
            <seg type="pāda" n="c">bhāryābhavat tasya na hīnasattvā</seg>
            <seg type="pāda" n="d">na cāpi kāntasya na kāmanīyā</seg>
        </li>
    </ul>
    <ul id="R_5.007.068">
        <li id="R_5.007.068ab">
            <seg type="pāda" n="a">babhūva buddhis tu harīśvarasya</seg>
            <seg type="pāda" n="b">yadīdṛśī rāghavadharmapatnī</seg>
        </li>
        <li id="R_5.007.068cd">
            <seg type="pāda" n="c">imā yathā rākṣasarājabhāryāḥ</seg>
            <seg type="pāda" n="d">sujātam asyeti hi sādhubuddheḥ</seg>
        </li>
    </ul>
    <ul id="R_5.007.069">
        <li id="R_5.007.069ab">
            <seg type="pāda" n="a">punaś ca so 'cintayad ārtarūpo</seg>
            <seg type="pāda" n="b">dhruvaṃ viśiṣṭā guṇato hi sītā</seg>
        </li>
        <li id="R_5.007.069cd">
            <seg type="pāda" n="c">athāyam asyāṃ kṛtavān mahātmā</seg>
            <seg type="pāda" n="d">laṅkeśvaraḥ kaṣṭam anāryakarma</seg>
        </li>
    </ul>
    <ul id="R_5.008.001">
        <li id="R_5.008.001ab">tatra divyopamaṃ mukhyaṃ sphāṭikaṃ ratnabhūṣitam</li>
        <li id="R_5.008.001cd">avekṣamāṇo hanumān dadarśa śayanāsanam</li>
    </ul>
    <ul id="R_5.008.002">
        <li id="R_5.008.002ab">tasya caikatame deśe so 'gryamālyavibhūṣitam</li>
        <li id="R_5.008.002cd">dadarśa pāṇḍuraṃ chatraṃ tārādhipatisaṃnibham</li>
    </ul>
    <ul id="R_5.008.003">
        <li id="R_5.008.003ab">bālavyajanahastābhir vījyamānaṃ samantataḥ</li>
        <li id="R_5.008.003cd">gandhaiś ca vividhair juṣṭaṃ varadhūpena dhūpitam</li>
    </ul>
    <ul id="R_5.008.004">
        <li id="R_5.008.004ab">paramāstaraṇāstīrṇam āvikājinasaṃvṛtam</li>
        <li id="R_5.008.004cd">dāmabhir varamālyānāṃ samantād upaśobhitam</li>
    </ul>
    <ul id="R_5.008.005">
        <li id="R_5.008.005ab">tasmiñ jīmūtasaṃkāśaṃ pradīptottamakuṇḍalam</li>
        <li id="R_5.008.005cd">lohitākṣaṃ mahābāhuṃ mahārajatavāsasaṃ</li>
    </ul>
    <ul id="R_5.008.006">
        <li id="R_5.008.006ab">lohitenānuliptāṅgaṃ candanena sugandhinā</li>
        <li id="R_5.008.006cd">saṃdhyāraktam ivākāśe toyadaṃ sataḍidguṇam</li>
    </ul>
    <ul id="R_5.008.007">
        <li id="R_5.008.007ab">vṛtam ābharaṇair divyaiḥ surūpaṃ kāmarūpiṇam</li>
        <li id="R_5.008.007cd">savṛkṣavanagulmāḍhyaṃ prasuptam iva mandaram</li>
    </ul>
    <ul id="R_5.008.008">
        <li id="R_5.008.008ab">krīḍitvoparataṃ rātrau varābharaṇabhūṣitam</li>
        <li id="R_5.008.008cd">priyaṃ rākṣasakanyānāṃ rākṣasānāṃ sukhāvaham</li>
    </ul>
    <ul id="R_5.008.009">
        <li id="R_5.008.009ab">pītvāpy uparataṃ cāpi dadarśa sa mahākapiḥ</li>
        <li id="R_5.008.009cd">bhāskare śayane vīraṃ prasuptaṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_5.008.010">
        <li id="R_5.008.010ab">niḥśvasantaṃ yathā nāgaṃ rāvaṇaṃ vānarottamaḥ</li>
        <li id="R_5.008.010cd">āsādya paramodvignaḥ so 'pāsarpat subhītavat</li>
    </ul>
    <ul id="R_5.008.011">
        <li id="R_5.008.011ab">athārohaṇam āsādya vedikāntaram āśritaḥ</li>
        <li id="R_5.008.011cd">suptaṃ rākṣasaśārdūlaṃ prekṣate sma mahākapiḥ</li>
    </ul>
    <ul id="R_5.008.012">
        <li id="R_5.008.012ab">śuśubhe rākṣasendrasya svapataḥ śayanottamam</li>
        <li id="R_5.008.012cd">gandhahastini saṃviṣṭe yathāprasravaṇaṃ mahat</li>
    </ul>
    <ul id="R_5.008.013">
        <li id="R_5.008.013ab">kāñcanāṅgadanaddhau ca dadarśa sa mahātmanaḥ</li>
        <li id="R_5.008.013cd">vikṣiptau rākṣasendrasya bhujāv indradhvajopamau</li>
    </ul>
    <ul id="R_5.008.014">
        <li id="R_5.008.014ab">airāvataviṣāṇāgrair āpīḍitakṛtavraṇau</li>
        <li id="R_5.008.014cd">vajrollikhitapīnāṃsau viṣṇucakraparikṣitau</li>
    </ul>
    <ul id="R_5.008.015">
        <li id="R_5.008.015ab">pīnau samasujātāṃsau saṃgatau balasaṃyutau</li>
        <li id="R_5.008.015cd">sulakṣaṇa nakhāṅguṣṭhau svaṅgulītalalakṣitau</li>
    </ul>
    <ul id="R_5.008.016">
        <li id="R_5.008.016ab">saṃhatau parighākārau vṛttau karikaropamau</li>
        <li id="R_5.008.016cd">vikṣiptau śayane śubhre pañcaśīrṣāv ivoragau</li>
    </ul>
    <ul id="R_5.008.017">
        <li id="R_5.008.017ab">śaśakṣatajakalpena suśītena sugandhinā</li>
        <li id="R_5.008.017cd">candanena parārdhyena svanuliptau svalaṃkṛtau</li>
    </ul>
    <ul id="R_5.008.018">
        <li id="R_5.008.018ab">uttamastrīvimṛditau gandhottamaniṣevitau</li>
        <li id="R_5.008.018cd">yakṣapannagagandharvadevadānavarāviṇau</li>
    </ul>
    <ul id="R_5.008.019">
        <li id="R_5.008.019ab">dadarśa sa kapis tasya bāhū śayanasaṃsthitau</li>
        <li id="R_5.008.019cd">mandarasyāntare suptau mahārhī ruṣitāv iva</li>
    </ul>
    <ul id="R_5.008.020">
        <li id="R_5.008.020ab">tābhyāṃ sa paripūrṇābhyāṃ bhujābhyāṃ rākṣasādhipaḥ</li>
        <li id="R_5.008.020cd">śuśubhe 'calasaṃkāśaḥ śṛṅgābhyām iva mandaraḥ</li>
    </ul>
    <ul id="R_5.008.021">
        <li id="R_5.008.021ab">cūtapuṃnāgasurabhir bakulottamasaṃyutaḥ</li>
        <li id="R_5.008.021cd">mṛṣṭānnarasasaṃyuktaḥ pānagandhapuraḥsaraḥ</li>
    </ul>
    <ul id="R_5.008.022">
        <li id="R_5.008.022ab">tasya rākṣasasiṃhasya niścakrāma mukhān mahān</li>
        <li id="R_5.008.022cd">śayānasya viniḥśvāsaḥ pūrayann iva tad gṛham</li>
    </ul>
    <ul id="R_5.008.023">
        <li id="R_5.008.023ab">muktāmaṇivicitreṇa kāñcanena virājatā</li>
        <li id="R_5.008.023cd">mukuṭenāpavṛttena kuṇḍalojjvalitānanam</li>
    </ul>
    <ul id="R_5.008.024">
        <li id="R_5.008.024ab">raktacandanadigdhena tathā hāreṇa śobhitā</li>
        <li id="R_5.008.024cd">pīnāyataviśālena vakṣasābhivirājitam</li>
    </ul>
    <ul id="R_5.008.025">
        <li id="R_5.008.025ab">pāṇḍureṇāpaviddhena kṣaumeṇa kṣatajekṣaṇam</li>
        <li id="R_5.008.025cd">mahārheṇa susaṃvītaṃ pītenottamavāsasā</li>
    </ul>
    <ul id="R_5.008.026">
        <li id="R_5.008.026ab">māṣarāśipratīkāśaṃ niḥśvasantaṃ bhujaṅgavat</li>
        <li id="R_5.008.026cd">gāṅge mahati toyānte prasutamiva kuñjaram</li>
    </ul>
    <ul id="R_5.008.027">
        <li id="R_5.008.027ab">caturbhiḥ kāñcanair dīpair dīpyamānaiś caturdiśam</li>
        <li id="R_5.008.027cd">prakāśīkṛtasarvāṅgaṃ meghaṃ vidyudgaṇair iva</li>
    </ul>
    <ul id="R_5.008.028">
        <li id="R_5.008.028ab">pādamūlagatāś cāpi dadarśa sumahātmanaḥ</li>
        <li id="R_5.008.028cd">patnīḥ sa priyabhāryasya tasya rakṣaḥpater gṛhe</li>
    </ul>
    <ul id="R_5.008.029">
        <li id="R_5.008.029ab">śaśiprakāśavadanā varakuṇḍalabhūṣitāḥ</li>
        <li id="R_5.008.029cd">amlānamālyābharaṇā dadarśa hariyūthapaḥ</li>
    </ul>
    <ul id="R_5.008.030">
        <li id="R_5.008.030ab">nṛttavāditrakuśalā rākṣasendrabhujāṅkagāḥ</li>
        <li id="R_5.008.030cd">varābharaṇadhāriṇyo niṣannā dadṛśe kapiḥ</li>
    </ul>
    <ul id="R_5.008.031">
        <li id="R_5.008.031ab">vajravaidūryagarbhāṇi śravaṇānteṣu yoṣitām</li>
        <li id="R_5.008.031cd">dadarśa tāpanīyāni kuṇḍalāny aṅgadāni ca</li>
    </ul>
    <ul id="R_5.008.032">
        <li id="R_5.008.032ab">tāsāṃ candropamair vaktraiḥ śubhair lalitakuṇḍalaiḥ</li>
        <li id="R_5.008.032cd">virarāja vimānaṃ tan nabhas tārāgaṇair iva</li>
    </ul>
    <ul id="R_5.008.033">
        <li id="R_5.008.033ab">madavyāyāmakhinnās tā rākṣasendrasya yoṣitaḥ</li>
        <li id="R_5.008.033cd">teṣu teṣv avakāśeṣu prasuptās tanumadhyamāḥ</li>
    </ul>
    <ul id="R_5.008.034">
        <li id="R_5.008.034ab">kā cid vīṇāṃ pariṣvajya prasuptā saṃprakāśate</li>
        <li id="R_5.008.034cd">mahānadīprakīrṇeva nalinī potam āśritā</li>
    </ul>
    <ul id="R_5.008.035">
        <li id="R_5.008.035ab">anyā kakṣagatenaiva maḍḍukenāsitekṣaṇā</li>
        <li id="R_5.008.035cd">prasuptā bhāminī bhāti bālaputreva vatsalā</li>
    </ul>
    <ul id="R_5.008.036">
        <li id="R_5.008.036ab">paṭahaṃ cārusarvāṅgī pīḍya śete śubhastanī</li>
        <li id="R_5.008.036cd">cirasya ramaṇaṃ labdhvā pariṣvajyeva kāminī</li>
    </ul>
    <ul id="R_5.008.037">
        <li id="R_5.008.037ab">kā cid aṃśaṃ pariṣvajya suptā kamalalocanā</li>
        <li id="R_5.008.037cd">nidrāvaśam anuprāptā sahakānteva bhāminī</li>
    </ul>
    <ul id="R_5.008.038">
        <li id="R_5.008.038ab">anyā kanakasaṃkāśair mṛdupīnair manoramaiḥ</li>
        <li id="R_5.008.038cd">mṛdaṅgaṃ paripīḍyāṅgaiḥ prasuptā mattalocanā</li>
    </ul>
    <ul id="R_5.008.039">
        <li id="R_5.008.039ab">bhujapārśvāntarasthena kakṣageṇa kṛśodarī</li>
        <li id="R_5.008.039cd">paṇavena sahānindyā suptā madakṛtaśramā</li>
    </ul>
    <ul id="R_5.008.040">
        <li id="R_5.008.040ab">ḍiṇḍimaṃ parigṛhyānyā tathaivāsaktaḍiṇḍimā</li>
        <li id="R_5.008.040cd">prasuptā taruṇaṃ vatsam upagūhyeva bhāminī</li>
    </ul>
    <ul id="R_5.008.041">
        <li id="R_5.008.041ab">kā cid āḍambaraṃ nārī bhujasaṃbhogapīḍitam</li>
        <li id="R_5.008.041cd">kṛtvā kamalapatrākṣī prasuptā madamohitā</li>
    </ul>
    <ul id="R_5.008.042">
        <li id="R_5.008.042ab">kalaśīm apaviddhyānyā prasuptā bhāti bhāminī</li>
        <li id="R_5.008.042cd">vasante puṣpaśabalā māleva parimārjitā</li>
    </ul>
    <ul id="R_5.008.043">
        <li id="R_5.008.043ab">pāṇibhyāṃ ca kucau kā cit suvarṇakalaśopamau</li>
        <li id="R_5.008.043cd">upagūhyābalā suptā nidrābalaparājitā</li>
    </ul>
    <ul id="R_5.008.044">
        <li id="R_5.008.044ab">anyā kamalapatrākṣī pūrṇendusadṛśānanā</li>
        <li id="R_5.008.044cd">anyām āliṅgya suśroṇī prasuptā madavihvalā</li>
    </ul>
    <ul id="R_5.008.045">
        <li id="R_5.008.045ab">ātodyāni vicitrāṇi pariṣvajya varastriyaḥ</li>
        <li id="R_5.008.045cd">nipīḍya ca kucaiḥ suptāḥ kāminyaḥ kāmukān iva</li>
    </ul>
    <ul id="R_5.008.046">
        <li id="R_5.008.046ab">tāsām ekāntavinyaste śayānāṃ śayane śubhe</li>
        <li id="R_5.008.046cd">dadarśa rūpasaṃpannām aparāṃ sa kapiḥ striyam</li>
    </ul>
    <ul id="R_5.008.047">
        <li id="R_5.008.047ab">muktāmaṇisamāyuktair bhūṣaṇaiḥ suvibhūṣitām</li>
        <li id="R_5.008.047cd">vibhūṣayantīm iva ca svaśriyā bhavanottamam</li>
    </ul>
    <ul id="R_5.008.048">
        <li id="R_5.008.048ab">gaurīṃ kanakavarṇābhām iṣṭām antaḥpureśvarīm</li>
        <li id="R_5.008.048cd">kapir mandodarīṃ tatra śayānāṃ cārurūpiṇīm</li>
    </ul>
    <ul id="R_5.008.049">
        <li id="R_5.008.049ab">sa tāṃ dṛṣṭvā mahābāhur bhūṣitāṃ mārutātmajaḥ</li>
        <li id="R_5.008.049cd">tarkayām āsa sīteti rūpayauvanasaṃpadā</li>
        <li id="R_5.008.049ef">harṣeṇa mahatā yukto nananda hariyūthapaḥ</li>
    </ul>
    <ul id="R_5.008.050">
        <li id="R_5.008.050ab">
            <seg type="pāda" n="a">āsphoṭayām āsa cucumba pucchaṃ</seg>
            <seg type="pāda" n="b">nananda cikrīḍa jagau jagāma</seg>
        </li>
        <li id="R_5.008.050cd">
            <seg type="pāda" n="c">stambhān arohan nipapāta bhūmau</seg>
            <seg type="pāda" n="d">nidarśayan svāṃ prakṛtiṃ kapīnām</seg>
        </li>
    </ul>
    <ul id="R_5.009.001">
        <li id="R_5.009.001ab">avadhūya ca tāṃ buddhiṃ babhūvāvasthitas tadā</li>
        <li id="R_5.009.001cd">jagāma cāparāṃ cintāṃ sītāṃ prati mahākapiḥ</li>
    </ul>
    <ul id="R_5.009.002">
        <li id="R_5.009.002ab">na rāmeṇa viyuktā sā svaptum arhati bhāminī</li>
        <li id="R_5.009.002cd">na bhoktuṃ nāpy alaṃkartuṃ na pānam upasevitum</li>
    </ul>
    <ul id="R_5.009.003">
        <li id="R_5.009.003ab">nānyaṃ naram upasthātuṃ surāṇām api ceśvaram</li>
        <li id="R_5.009.003cd">na hi rāmasamaḥ kaś cid vidyate tridaśeṣv api</li>
        <li id="R_5.009.003ef">anyeyam iti niścitya pānabhūmau cacāra saḥ</li>
    </ul>
    <ul id="R_5.009.004">
        <li id="R_5.009.004ab">krīḍitenāparāḥ klāntā gītena ca tathā parāḥ</li>
        <li id="R_5.009.004cd">nṛttena cāparāḥ klāntāḥ pānaviprahatās tathā</li>
    </ul>
    <ul id="R_5.009.005">
        <li id="R_5.009.005ab">murajeṣu mṛdaṅgeṣu pīṭhikāsu ca saṃsthitāḥ</li>
        <li id="R_5.009.005cd">tathāstaraṇamukhyyeṣu saṃviṣṭāś cāparāḥ striyaḥ</li>
    </ul>
    <ul id="R_5.009.006">
        <li id="R_5.009.006ab">aṅganānāṃ sahasreṇa bhūṣitena vibhūṣaṇaiḥ</li>
        <li id="R_5.009.006cd">rūpasaṃlāpaśīlena yuktagītārthabhāṣiṇā</li>
    </ul>
    <ul id="R_5.009.007">
        <li id="R_5.009.007ab">deśakālābhiyuktena yuktavākyābhidhāyinā</li>
        <li id="R_5.009.007cd">ratābhiratasaṃsuptaṃ dadarśa hariyūthapaḥ</li>
    </ul>
    <ul id="R_5.009.008">
        <li id="R_5.009.008ab">tāsāṃ madhye mahābāhuḥ śuśubhe rākṣaseśvaraḥ</li>
        <li id="R_5.009.008cd">goṣṭhe mahati mukhyānāṃ gavāṃ madhye yathā vṛṣaḥ</li>
    </ul>
    <ul id="R_5.009.009">
        <li id="R_5.009.009ab">sa rākṣasendraḥ śuśubhe tābhiḥ parivṛtaḥ svayam</li>
        <li id="R_5.009.009cd">kareṇubhir yathāraṇyaṃ parikīrṇo mahādvipaḥ</li>
    </ul>
    <ul id="R_5.009.010">
        <li id="R_5.009.010ab">sarvakāmair upetāṃ ca pānabhūmiṃ mahātmanaḥ</li>
        <li id="R_5.009.010cd">dadarśa kapiśārdūlas tasya rakṣaḥpater gṛhe</li>
    </ul>
    <ul id="R_5.009.011">
        <li id="R_5.009.011ab">mṛgāṇāṃ mahiṣāṇāṃ ca varāhāṇāṃ ca bhāgaśaḥ</li>
        <li id="R_5.009.011cd">tatra nyastāni māṃsāni pānabhūmau dadarśa saḥ</li>
    </ul>
    <ul id="R_5.009.012">
        <li id="R_5.009.012ab">raukmeṣu ca viśaleṣu bhājaneṣv ardhabhakṣitān</li>
        <li id="R_5.009.012cd">dadarśa kapiśārdūla mayūrān kukkuṭāṃs tathā</li>
    </ul>
    <ul id="R_5.009.013">
        <li id="R_5.009.013ab">varāhavārdhrāṇasakān dadhisauvarcalāyutān</li>
        <li id="R_5.009.013cd">śalyān mṛgamayūrāṃś ca hanūmān anvavaikṣata</li>
    </ul>
    <ul id="R_5.009.014">
        <li id="R_5.009.014ab">kṛkarān vividhān siddhāṃś cakorān ardhabhakṣitān</li>
        <li id="R_5.009.014cd">mahiṣān ekaśalyāṃś ca chāgāṃś ca kṛtaniṣṭhitān</li>
        <li id="R_5.009.014ef">lekhyam uccāvacaṃ peyaṃ bhojyāni vividhāni ca</li>
    </ul>
    <ul id="R_5.009.015">
        <li id="R_5.009.015ab">tathāmlalavaṇottaṃsair vividhai rāgaṣāḍavaiḥ</li>
        <li id="R_5.009.015cd">hāra nūpurakeyūrair apaviddhair mahādhanaiḥ</li>
    </ul>
    <ul id="R_5.009.016">
        <li id="R_5.009.016ab">pānabhājanavikṣiptaiḥ phalaiś ca vividhair api</li>
        <li id="R_5.009.016cd">kṛtapuṣpopahārā bhūr adhikaṃ puṣyati śriyam</li>
    </ul>
    <ul id="R_5.009.017">
        <li id="R_5.009.017ab">tatra tatra ca vinyastaiḥ suśliṣṭaiḥ śayanāsanaiḥ</li>
        <li id="R_5.009.017cd">pānabhūmir vinā vahniṃ pradīptevopalakṣyate</li>
    </ul>
    <ul id="R_5.009.018">
        <li id="R_5.009.018ab">bahuprakārair vividhair varasaṃskārasaṃskṛtaiḥ</li>
        <li id="R_5.009.018cd">māṃsaiḥ kuśalasaṃyuktaiḥ pānabhūmigataiḥ pṛthak</li>
    </ul>
    <ul id="R_5.009.019">
        <li id="R_5.009.019ab">divyāḥ prasannā vividhāḥ surāḥ kṛtasurā api</li>
        <li id="R_5.009.019cd">śarkarāsavamādhvīkāḥ puṣpāsavaphalāsavāḥ</li>
        <li id="R_5.009.019ef">vāsacūrṇaiś ca vividhair mṛṣṭās tais taiḥ pṛthakpṛthak</li>
    </ul>
    <ul id="R_5.009.020">
        <li id="R_5.009.020ab">saṃtatā śuśubhe bhūmir mālyaiś ca bahusaṃsthitaiḥ</li>
        <li id="R_5.009.020cd">hiraṇmayaiś ca karakair bhājanaiḥ sphāṭikair api</li>
        <li id="R_5.009.020ef">jāmbūnadamayaiś cānyaiḥ karakair abhisaṃvṛtā</li>
    </ul>
    <ul id="R_5.009.021">
        <li id="R_5.009.021ab">rājateṣu ca kumbheṣu jāmbūnadamayeṣu ca</li>
        <li id="R_5.009.021cd">pānaśreṣṭhaṃ tadā bhūri kapis tatra dadarśa ha</li>
    </ul>
    <ul id="R_5.009.022">
        <li id="R_5.009.022ab">so 'paśyac chātakumbhāni śīdhor maṇimayāni ca</li>
        <li id="R_5.009.022cd">rājatāni ca pūrṇāni bhājanāni mahākapiḥ</li>
    </ul>
    <ul id="R_5.009.023">
        <li id="R_5.009.023ab">kva cid ardhāvaśeṣāṇi kva cit pītāni sarvaśaḥ</li>
        <li id="R_5.009.023cd">kva cin naiva prapītāni pānāni sa dadarśa ha</li>
    </ul>
    <ul id="R_5.009.024">
        <li id="R_5.009.024ab">kva cid bhakṣyāṃś ca vividhān kva cit pānāni bhāgaśaḥ</li>
        <li id="R_5.009.024cd">kva cid annāvaśeṣāṇi paśyan vai vicacāra ha</li>
    </ul>
    <ul id="R_5.009.025">
        <li id="R_5.009.025ab">kva cit prabhinnaiḥ karakaiḥ kva cid āloḍitair ghaṭaiḥ</li>
        <li id="R_5.009.025cd">kva cit saṃpṛktamālyāni jalāni ca phalāni ca</li>
    </ul>
    <ul id="R_5.009.026">
        <li id="R_5.009.026ab">śayanāny atra nārīṇāṃ śūnyāni bahudhā punaḥ</li>
        <li id="R_5.009.026cd">parasparaṃ samāśliṣya kāś cit suptā varāṅganāḥ</li>
    </ul>
    <ul id="R_5.009.027">
        <li id="R_5.009.027ab">kā cic ca vastram anyasyā apahṛtyopaguhya ca</li>
        <li id="R_5.009.027cd">upagamyābalā suptā nidrābalaparājitā</li>
    </ul>
    <ul id="R_5.009.028">
        <li id="R_5.009.028ab">tāsām ucchvāsavātena vastraṃ mālyaṃ ca gātrajam</li>
        <li id="R_5.009.028cd">nātyarthaṃ spandate citraṃ prāpya mandam ivānilam</li>
    </ul>
    <ul id="R_5.009.029">
        <li id="R_5.009.029ab">candanasya ca śītasya śīdhor madhurasasya ca</li>
        <li id="R_5.009.029cd">vividhasya ca mālyasya puṣpasya vividhasya ca</li>
    </ul>
    <ul id="R_5.009.030">
        <li id="R_5.009.030ab">bahudhā mārutas tatra gandhaṃ vividham udvahan</li>
        <li id="R_5.009.030cd">snānānāṃ candanānāṃ ca dhūpānāṃ caiva mūrchitaḥ</li>
        <li id="R_5.009.030ef">pravavau surabhir gandho vimāne puṣpake tadā</li>
    </ul>
    <ul id="R_5.009.031">
        <li id="R_5.009.031ab">śyāmāvadātās tatrānyāḥ kāś cit kṛṣṇā varāṅganāḥ</li>
        <li id="R_5.009.031cd">kāś cit kāñcanavarṇāṅgyaḥ pramadā rākṣasālaye</li>
    </ul>
    <ul id="R_5.009.032">
        <li id="R_5.009.032ab">tāsāṃ nidrāvaśatvāc ca madanena vimūrchitam</li>
        <li id="R_5.009.032cd">padminīnāṃ prasuptānāṃ rūpam āsīd yathaiva hi</li>
    </ul>
    <ul id="R_5.009.033">
        <li id="R_5.009.033ab">evaṃ sarvam aśeṣeṇa rāvaṇāntaḥpuraṃ kapiḥ</li>
        <li id="R_5.009.033cd">dadarśa sumahātejā na dadarśa ca jānakīm</li>
    </ul>
    <ul id="R_5.009.034">
        <li id="R_5.009.034ab">nirīkṣamāṇaś ca tatas tāḥ striyaḥ sa mahākapiḥ</li>
        <li id="R_5.009.034cd">jagāma mahatīṃ cintāṃ dharmasādhvasaśaṅkitaḥ</li>
    </ul>
    <ul id="R_5.009.035">
        <li id="R_5.009.035ab">paradārāvarodhasya prasuptasya nirīkṣaṇam</li>
        <li id="R_5.009.035cd">idaṃ khalu mamātyarthaṃ dharmalopaṃ kariṣyati</li>
    </ul>
    <ul id="R_5.009.036">
        <li id="R_5.009.036ab">na hi me paradārāṇāṃ dṛṣṭir viṣayavartinī</li>
        <li id="R_5.009.036cd">ayaṃ cātra mayā dṛṣṭaḥ paradāraparigrahaḥ</li>
    </ul>
    <ul id="R_5.009.037">
        <li id="R_5.009.037ab">tasya prādurabhūc cintāpunar anyā manasvinaḥ</li>
        <li id="R_5.009.037cd">niścitaikāntacittasya kāryaniścayadarśinī</li>
    </ul>
    <ul id="R_5.009.038">
        <li id="R_5.009.038ab">kāmaṃ dṛṣṭvā mayā sarvā viśvastā rāvaṇastriyaḥ</li>
        <li id="R_5.009.038cd">na tu me manasaḥ kiṃ cid vaikṛtyam upapadyate</li>
    </ul>
    <ul id="R_5.009.039">
        <li id="R_5.009.039ab">mano hi hetuḥ sarveṣām indriyāṇāṃ pravartate</li>
        <li id="R_5.009.039cd">śubhāśubhāsv avasthāsu tac ca me suvyavasthitam</li>
    </ul>
    <ul id="R_5.009.040">
        <li id="R_5.009.040ab">nānyatra hi mayā śakyā vaidehī parimārgitum</li>
        <li id="R_5.009.040cd">striyo hi strīṣu dṛśyante sadā saṃparimārgaṇe</li>
    </ul>
    <ul id="R_5.009.041">
        <li id="R_5.009.041ab">yasya sattvasya yā yonis tasyāṃ tat parimārgyate</li>
        <li id="R_5.009.041cd">na śakyaṃ pramadā naṣṭā mṛgīṣu parimārgitum</li>
    </ul>
    <ul id="R_5.009.042">
        <li id="R_5.009.042ab">tad idaṃ mārgitaṃ tāvac chuddhena manasā mayā</li>
        <li id="R_5.009.042cd">rāvaṇāntaḥpuraṃ saraṃ dṛśyate na ca jānakī</li>
    </ul>
    <ul id="R_5.009.043">
        <li id="R_5.009.043ab">devagandharvakanyāś ca nāgakanyāś ca vīryavān</li>
        <li id="R_5.009.043cd">avekṣamāṇo hanumān naivāpaśyata jānakīm</li>
    </ul>
    <ul id="R_5.009.044">
        <li id="R_5.009.044ab">tām apaśyan kapis tatra paśyaṃś cānyā varastriyaḥ</li>
        <li id="R_5.009.044cd">apakramya tadā vīraḥ pradhyātum upacakrame</li>
    </ul>
    <ul id="R_5.010.001">
        <li id="R_5.010.001ab">
            <seg type="pāda" n="a">sa tasya madhye bhavanasya vānaro</seg>
            <seg type="pāda" n="b">latāgṛhāṃś citragṛhān niśāgṛhān</seg>
        </li>
        <li id="R_5.010.001cd">
            <seg type="pāda" n="c">jagāma sītāṃ prati darśanotsuko</seg>
            <seg type="pāda" n="d">na caiva tāṃ paśyati cārudarśanām</seg>
        </li>
    </ul>
    <ul id="R_5.010.002">
        <li id="R_5.010.002ab">
            <seg type="pāda" n="a">sa cintayām āsa tato mahākapiḥ</seg>
            <seg type="pāda" n="b">priyām apaśyan raghunandanasya tām</seg>
        </li>
        <li id="R_5.010.002cd">
            <seg type="pāda" n="c">dhruvaṃ nu sītā mriyate yathā na me</seg>
            <seg type="pāda" n="d">vicinvato darśanam eti maithilī</seg>
        </li>
    </ul>
    <ul id="R_5.010.003">
        <li id="R_5.010.003ab">
            <seg type="pāda" n="a">sā rākṣasānāṃ pravareṇa bālā</seg>
            <seg type="pāda" n="b">svaśīlasaṃrakṣaṇa tat parā satī</seg>
        </li>
        <li id="R_5.010.003cd">
            <seg type="pāda" n="c">anena nūnaṃ pratiduṣṭakarmaṇā</seg>
            <seg type="pāda" n="d">hatā bhaved āryapathe pare sthitā</seg>
        </li>
    </ul>
    <ul id="R_5.010.004">
        <li id="R_5.010.004ab">
            <seg type="pāda" n="a">virūparūpā vikṛtā vivarcaso</seg>
            <seg type="pāda" n="b">mahānanā dīrghavirūpadarśanāḥ</seg>
        </li>
        <li id="R_5.010.004cd">
            <seg type="pāda" n="c">samīkṣya sā rākṣasarājayoṣito</seg>
            <seg type="pāda" n="d">bhayād vinaṣṭā janakeśvarātmajā</seg>
        </li>
    </ul>
    <ul id="R_5.010.005">
        <li id="R_5.010.005ab">
            <seg type="pāda" n="a">sītām adṛṣṭvā hy anavāpya pauruṣaṃ</seg>
            <seg type="pāda" n="b">vihṛtya kālaṃ saha vānaraiś ciram</seg>
        </li>
        <li id="R_5.010.005cd">
            <seg type="pāda" n="c">na me 'sti sugrīvasamīpagā gatiḥ</seg>
            <seg type="pāda" n="d">sutīkṣṇadaṇḍo balavāṃś ca vānaraḥ</seg>
        </li>
    </ul>
    <ul id="R_5.010.006">
        <li id="R_5.010.006ab">dṛṣṭam antaḥpuraṃ sarvaṃ dṛṣṭvā rāvaṇayoṣitaḥ</li>
        <li id="R_5.010.006cd">na sītā dṛśyate sādhvī vṛthā jāto mama śramaḥ</li>
    </ul>
    <ul id="R_5.010.007">
        <li id="R_5.010.007ab">kiṃ nu māṃ vānarāḥ sarve gataṃ vakṣyanti saṃgatāḥ</li>
        <li id="R_5.010.007cd">gatvā tatra tvayā vīra kiṃ kṛtaṃ tad vadasva naḥ</li>
    </ul>
    <ul id="R_5.010.008">
        <li id="R_5.010.008ab">adṛṣṭvā kiṃ pravakṣyāmi tām ahaṃ janakātmajām</li>
        <li id="R_5.010.008cd">dhruvaṃ prāyam upeṣyanti kālasya vyativartane</li>
    </ul>
    <ul id="R_5.010.009">
        <li id="R_5.010.009ab">kiṃ vā vakṣyati vṛddhaś ca jāmbavān aṅgadaś ca saḥ</li>
        <li id="R_5.010.009cd">gataṃ pāraṃ samudrasya vānarāś ca samāgatāḥ</li>
    </ul>
    <ul id="R_5.010.010">
        <li id="R_5.010.010ab">anirvedaḥ śriyo mūlam anirvedaḥ paraṃ sukham</li>
        <li id="R_5.010.010cd">bhūyas tāvad viceṣyāmi na yatra vicayaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_5.010.011">
        <li id="R_5.010.011ab">anirvedo hi satataṃ sarvārtheṣu pravartakaḥ</li>
        <li id="R_5.010.011cd">karoti saphalaṃ jantoḥ karma yac ca karoti saḥ</li>
    </ul>
    <ul id="R_5.010.012">
        <li id="R_5.010.012ab">tasmād anirveda kṛtaṃ yatnaṃ ceṣṭe 'ham uttamam</li>
        <li id="R_5.010.012cd">adṛṣṭāṃś ca viceṣyāmi deśān rāvaṇapālitān</li>
    </ul>
    <ul id="R_5.010.013">
        <li id="R_5.010.013ab">āpānaśālāvicitās tathā puṣpagṛhāṇi ca</li>
        <li id="R_5.010.013cd">citraśālāś ca vicitā bhūyaḥ krīḍāgṛhāṇi ca</li>
    </ul>
    <ul id="R_5.010.014">
        <li id="R_5.010.014ab">niṣkuṭāntararathyāś ca vimānāni ca sarvaśaḥ</li>
        <li id="R_5.010.014cd">iti saṃcintya bhūyo 'pi vicetum upacakrame</li>
    </ul>
    <ul id="R_5.010.015">
        <li id="R_5.010.015ab">bhūmīgṛhāṃś caityagṛhān gṛhātigṛhakān api</li>
        <li id="R_5.010.015cd">utpatan nipataṃś cāpi tiṣṭhan gacchan punaḥ kva cit</li>
    </ul>
    <ul id="R_5.010.016">
        <li id="R_5.010.016ab">apāvṛṇvaṃś ca dvārāṇi kapāṭāny avaghaṭṭayan</li>
        <li id="R_5.010.016cd">praviśan niṣpataṃś cāpi prapatann utpatann api</li>
        <li id="R_5.010.016ef">sarvam apy avakāśaṃ sa vicacāra mahākapiḥ</li>
    </ul>
    <ul id="R_5.010.017">
        <li id="R_5.010.017ab">caturaṅgulamātro 'pi nāvakāśaḥ sa vidyate</li>
        <li id="R_5.010.017cd">rāvaṇāntaḥpure tasmin yaṃ kapir na jagāma saḥ</li>
    </ul>
    <ul id="R_5.010.018">
        <li id="R_5.010.018ab">prākarāntararathyāś ca vedikaś caityasaṃśrayāḥ</li>
        <li id="R_5.010.018cd">śvabhrāś ca puṣkariṇyaś ca sarvaṃ tenāvalokitam</li>
    </ul>
    <ul id="R_5.010.019">
        <li id="R_5.010.019ab">rākṣasyo vividhākārā virūpā vikṛtās tathā</li>
        <li id="R_5.010.019cd">dṛṣṭā hanūmatā tatra na tu sā janakātmajā</li>
    </ul>
    <ul id="R_5.010.020">
        <li id="R_5.010.020ab">rūpeṇāpratimā loke varā vidyādhara striyaḥ</li>
        <li id="R_5.010.020cd">dṛṭā hanūmatā tatra na tu rāghavanandinī</li>
    </ul>
    <ul id="R_5.010.021">
        <li id="R_5.010.021ab">nāgakanyā varārohāḥ pūrṇacandranibhānanāḥ</li>
        <li id="R_5.010.021cd">dṛṣṭā hanūmatā tatra na tu sītā sumadhyamā</li>
    </ul>
    <ul id="R_5.010.022">
        <li id="R_5.010.022ab">pramathya rākṣasendreṇa nāgakanyā balād dhṛtāḥ</li>
        <li id="R_5.010.022cd">dṛṣṭā hanūmatā tatra na sā janakanandinī</li>
    </ul>
    <ul id="R_5.010.023">
        <li id="R_5.010.023ab">so 'paśyaṃs tāṃ mahābāhuḥ paśyaṃś cānyā varastriyaḥ</li>
        <li id="R_5.010.023cd">viṣasāda mahābāhur hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.010.024">
        <li id="R_5.010.024ab">udyogaṃ vānarendrāṇaṃ plavanaṃ sāgarasya ca</li>
        <li id="R_5.010.024cd">vyarthaṃ vīkṣyānilasutaś cintāṃ punar upāgamat</li>
    </ul>
    <ul id="R_5.010.025">
        <li id="R_5.010.025ab">avatīrya vimānāc ca hanūmān mārutātmajaḥ</li>
        <li id="R_5.010.025cd">cintām upajagāmātha śokopahatacetanaḥ</li>
    </ul>
    <ul id="R_5.011.001">
        <li id="R_5.011.001ab">vimānāt tu susaṃkramya prākāraṃ hariyūthapaḥ</li>
        <li id="R_5.011.001cd">hanūmān vegavān āsīd yathā vidyudghanāntare</li>
    </ul>
    <ul id="R_5.011.002">
        <li id="R_5.011.002ab">saṃparikramya hanumān rāvaṇasya niveśanān</li>
        <li id="R_5.011.002cd">adṛṣṭvā jānakīṃ sītām abravīd vacanaṃ kapiḥ</li>
    </ul>
    <ul id="R_5.011.003">
        <li id="R_5.011.003ab">bhūyiṣṭhaṃ loḍitā laṅkā rāmasya caratā priyam</li>
        <li id="R_5.011.003cd">na hi paśyāmi vaidehīṃ sītāṃ sarvāṅgaśobhanām</li>
    </ul>
    <ul id="R_5.011.004">
        <li id="R_5.011.004ab">palvalāni taṭākāni sarāṃsi saritas tathā</li>
        <li id="R_5.011.004cd">nadyo 'nūpavanāntāś ca durgāś ca dharaṇīdharāḥ</li>
        <li id="R_5.011.004ef">loḍitā vasudhā sarvā na ca paśyāmi jānakīm</li>
    </ul>
    <ul id="R_5.011.005">
        <li id="R_5.011.005ab">iha saṃpātinā sītā rāvaṇasya niveśane</li>
        <li id="R_5.011.005cd">ākhyātā gṛdhrarājena na ca paśyāmi tām aham</li>
    </ul>
    <ul id="R_5.011.006">
        <li id="R_5.011.006ab">kiṃ nu sītātha vaidehī maithilī janakātmajā</li>
        <li id="R_5.011.006cd">upatiṣṭheta vivaśā rāvaṇaṃ duṣṭacāriṇam</li>
    </ul>
    <ul id="R_5.011.007">
        <li id="R_5.011.007ab">kṣipram utpatato manye sītām ādāya rakṣasaḥ</li>
        <li id="R_5.011.007cd">bibhyato rāmabāṇānām antarā patitā bhavet</li>
    </ul>
    <ul id="R_5.011.008">
        <li id="R_5.011.008ab">atha vā hriyamāṇāyāḥ pathi siddhaniṣevite</li>
        <li id="R_5.011.008cd">manye patitam āryāyā hṛdayaṃ prekṣya sāgaram</li>
    </ul>
    <ul id="R_5.011.009">
        <li id="R_5.011.009ab">rāvaṇasyoruvegena bhujābhyāṃ pīḍitena ca</li>
        <li id="R_5.011.009cd">tayā manye viśālākṣyā tyaktaṃ jīvitam āryayā</li>
    </ul>
    <ul id="R_5.011.010">
        <li id="R_5.011.010ab">upary upari vā nūnaṃ sāgaraṃ kramatas tadā</li>
        <li id="R_5.011.010cd">viveṣṭamānā patitā samudre janakātmajā</li>
    </ul>
    <ul id="R_5.011.011">
        <li id="R_5.011.011ab">āho kṣudreṇa cānena rakṣantī śīlam ātmanaḥ</li>
        <li id="R_5.011.011cd">abandhur bhakṣitā sītā rāvaṇena tapasvinī</li>
    </ul>
    <ul id="R_5.011.012">
        <li id="R_5.011.012ab">atha vā rākṣasendrasya patnībhir asitekṣaṇā</li>
        <li id="R_5.011.012cd">aduṣṭā duṣṭabhāvābhir bhakṣitā sā bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.013">
        <li id="R_5.011.013ab">saṃpūrṇacandrapratimaṃ padmapatranibhekṣaṇam</li>
        <li id="R_5.011.013cd">rāmasya dhyāyatī vaktraṃ pañcatvaṃ kṛpaṇā gatā</li>
    </ul>
    <ul id="R_5.011.014">
        <li id="R_5.011.014ab">hā rāma lakṣmaṇety eva hāyodhyeti ca maithilī</li>
        <li id="R_5.011.014cd">vilapya bahu vaidehī nyastadehā bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.015">
        <li id="R_5.011.015ab">atha vā nihitā manye rāvaṇasya niveśane</li>
        <li id="R_5.011.015cd">nūnaṃ lālapyate mandaṃ pañjarastheva śārikā</li>
    </ul>
    <ul id="R_5.011.016">
        <li id="R_5.011.016ab">janakasya kule jātā rāmapatnī sumadhyamā</li>
        <li id="R_5.011.016cd">katham utpalapatrākṣī rāvaṇasya vaśaṃ vrajet</li>
    </ul>
    <ul id="R_5.011.017">
        <li id="R_5.011.017ab">vinaṣṭā vā pranaṣṭā vā mṛtā vā janakātmajā</li>
        <li id="R_5.011.017cd">rāmasya priyabhāryasya na nivedayituṃ kṣamam</li>
    </ul>
    <ul id="R_5.011.018">
        <li id="R_5.011.018ab">nivedyamāne doṣaḥ syād doṣaḥ syād anivedane</li>
        <li id="R_5.011.018cd">kathaṃ nu khalu kartavyaṃ viṣamaṃ pratibhāti me</li>
    </ul>
    <ul id="R_5.011.019">
        <li id="R_5.011.019ab">asminn evaṃgate karye prāptakālaṃ kṣamaṃ ca kim</li>
        <li id="R_5.011.019cd">bhaved iti matiṃ bhūyo hanumān pravicārayan</li>
    </ul>
    <ul id="R_5.011.020">
        <li id="R_5.011.020ab">yadi sītām adṛṣṭvāhaṃ vānarendrapurīm itaḥ</li>
        <li id="R_5.011.020cd">gamiṣyāmi tataḥ ko me puruṣārtho bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.021">
        <li id="R_5.011.021ab">mamedaṃ laṅghanaṃ vyarthaṃ sāgarasya bhaviṣyati</li>
        <li id="R_5.011.021cd">praveśaś civa laṅkāyā rākṣasānāṃ ca darśanam</li>
    </ul>
    <ul id="R_5.011.022">
        <li id="R_5.011.022ab">kiṃ vā vakṣyati sugrīvo harayo va samāgatāḥ</li>
        <li id="R_5.011.022cd">kiṣkindhāṃ samanuprāptau tau vā daśarathātmajau</li>
    </ul>
    <ul id="R_5.011.023">
        <li id="R_5.011.023ab">gatvā tu yadi kākutsthaṃ vakṣyāmi param apriyam</li>
        <li id="R_5.011.023cd">na dṛṣṭeti mayā sītā tatas tyakṣyanti jīvitam</li>
    </ul>
    <ul id="R_5.011.024">
        <li id="R_5.011.024ab">paruṣaṃ dāruṇaṃ krūraṃ tīkṣṇam indriyatāpanam</li>
        <li id="R_5.011.024cd">sītānimittaṃ durvākyaṃ śrutvā sa na bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.025">
        <li id="R_5.011.025ab">taṃ tu kṛcchragataṃ dṛṣṭvā pañcatvagatamānasaṃ</li>
        <li id="R_5.011.025cd">bhṛśānurakto medhāvī na bhaviṣyati lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.011.026">
        <li id="R_5.011.026ab">vinaṣṭau bhrātarau śrutvā bharato 'pi mariṣyati</li>
        <li id="R_5.011.026cd">bharataṃ ca mṛtaṃ dṛṣṭvā śatrughno na bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.027">
        <li id="R_5.011.027ab">putrān mṛtān samīkṣyātha na bhaviṣyanti mātaraḥ</li>
        <li id="R_5.011.027cd">kausalyā ca sumitrā ca kaikeyī ca na saṃśayaḥ</li>
    </ul>
    <ul id="R_5.011.028">
        <li id="R_5.011.028ab">kṛtajñaḥ satyasaṃdhaś ca sugrīvaḥ plavagādhipaḥ</li>
        <li id="R_5.011.028cd">rāmaṃ tathā gataṃ dṛṣṭvā tatas tyakṣyanti jīvitam</li>
    </ul>
    <ul id="R_5.011.029">
        <li id="R_5.011.029ab">durmanā vyathitā dīnā nirānandā tapasvinī</li>
        <li id="R_5.011.029cd">pīḍitā bhartṛśokena rumā tyakṣyati jīvitam</li>
    </ul>
    <ul id="R_5.011.030">
        <li id="R_5.011.030ab">vālijena tu duḥkhena pīḍitā śokakarśitā</li>
        <li id="R_5.011.030cd">pañcatvagamane rājñas tārāpi na bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.031">
        <li id="R_5.011.031ab">mātāpitror vināśena sugrīva vyasanena ca</li>
        <li id="R_5.011.031cd">kumāro 'py aṅgadaḥ kasmād dhārayiṣyati jīvitam</li>
    </ul>
    <ul id="R_5.011.032">
        <li id="R_5.011.032ab">bhartṛjena tu śokena abhibhūtā vanaukasaḥ</li>
        <li id="R_5.011.032cd">śirāṃsy abhihaniṣyanti talair muṣṭibhir eva ca</li>
    </ul>
    <ul id="R_5.011.033">
        <li id="R_5.011.033ab">sāntvenānupradānena mānena ca yaśasvinā</li>
        <li id="R_5.011.033cd">lālitāḥ kapirājena prāṇāṃs tyakṣyanti vānarāḥ</li>
    </ul>
    <ul id="R_5.011.034">
        <li id="R_5.011.034ab">na vaneṣu na śaileṣu na nirodheṣu vā punaḥ</li>
        <li id="R_5.011.034cd">krīḍām anubhaviṣyanti sametya kapikuñjarāḥ</li>
    </ul>
    <ul id="R_5.011.035">
        <li id="R_5.011.035ab">saputradārāḥ sāmātyā bhartṛvyasanapīḍitāḥ</li>
        <li id="R_5.011.035cd">śailāgrebhyaḥ patiṣyanti sametya viṣameṣu ca</li>
    </ul>
    <ul id="R_5.011.036">
        <li id="R_5.011.036ab">viṣam udbandhanaṃ vāpi praveśaṃ jvalanasya vā</li>
        <li id="R_5.011.036cd">upavāsam atho śastraṃ pracariṣyanti vānarāḥ</li>
    </ul>
    <ul id="R_5.011.037">
        <li id="R_5.011.037ab">ghoram ārodanaṃ manye gate mayi bhaviṣyati</li>
        <li id="R_5.011.037cd">ikṣvākukulanāśaś ca nāśaś caiva vanaukasām</li>
    </ul>
    <ul id="R_5.011.038">
        <li id="R_5.011.038ab">so 'haṃ naiva gamiṣyāmi kiṣkindhāṃ nagarīm itaḥ</li>
        <li id="R_5.011.038cd">na hi śakṣyāmy ahaṃ draṣṭuṃ sugrīvaṃ maithilīṃ vinā</li>
    </ul>
    <ul id="R_5.011.039">
        <li id="R_5.011.039ab">mayy agacchati cehasthe dharmātmānau mahārathau</li>
        <li id="R_5.011.039cd">āśayā tau dhariṣyete vanarāś ca manasvinaḥ</li>
    </ul>
    <ul id="R_5.011.040">
        <li id="R_5.011.040ab">hastādāno mukhādāno niyato vṛkṣamūlikaḥ</li>
        <li id="R_5.011.040cd">vānaprastho bhaviṣyāmi adṛṣṭvā janakātmajām</li>
    </ul>
    <ul id="R_5.011.041">
        <li id="R_5.011.041ab">sāgarānūpaje deśe bahumūlaphalodake</li>
        <li id="R_5.011.041cd">citāṃ kṛtvā pravekṣyāmi samiddham araṇīsutam</li>
    </ul>
    <ul id="R_5.011.042">
        <li id="R_5.011.042ab">upaviṣṭasya vā samyag liṅginaṃ sādhayiṣyataḥ</li>
        <li id="R_5.011.042cd">śarīraṃ bhakṣayiṣyanti vāyasāḥ śvāpadāni ca</li>
    </ul>
    <ul id="R_5.011.043">
        <li id="R_5.011.043ab">idam apy ṛṣibhir dṛṣṭaṃ niryāṇam iti me matiḥ</li>
        <li id="R_5.011.043cd">samyag āpaḥ pravekṣyāmi na cet paśyāmi jānakīm</li>
    </ul>
    <ul id="R_5.011.044">
        <li id="R_5.011.044ab">sujātamūlā subhagā kīrtimālāyaśasvinī</li>
        <li id="R_5.011.044cd">prabhagnā cirarātrīyaṃ mama sītām apaśyataḥ</li>
    </ul>
    <ul id="R_5.011.045">
        <li id="R_5.011.045ab">tāpaso vā bhaviṣyāmi niyato vṛkṣamūlikaḥ</li>
        <li id="R_5.011.045cd">netaḥ pratigamiṣyāmi tām adṛṣṭvāsitekṣaṇām</li>
    </ul>
    <ul id="R_5.011.046">
        <li id="R_5.011.046ab">yadītaḥ pratigacchāmi sītām anadhigamya tām</li>
        <li id="R_5.011.046cd">aṅgadaḥ sahitaiḥ sarvair vānarair na bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.047">
        <li id="R_5.011.047ab">vināśe bahavo doṣā jīvan prāpnoti bhadrakam</li>
        <li id="R_5.011.047cd">tasmāt prāṇān dhariṣyāmi dhruvo jīvati saṃgamaḥ</li>
    </ul>
    <ul id="R_5.011.048">
        <li id="R_5.011.048ab">evaṃ bahuvidhaṃ duḥkhaṃ manasā dhārayan muhuḥ</li>
        <li id="R_5.011.048cd">nādhyagacchat tadā pāraṃ śokasya kapikuñjaraḥ</li>
    </ul>
    <ul id="R_5.011.049">
        <li id="R_5.011.049ab">rāvaṇaṃ vā vadhiṣyāmi daśagrīvaṃ mahābalam</li>
        <li id="R_5.011.049cd">kāmam astu hṛtā sītā pratyācīrṇaṃ bhaviṣyati</li>
    </ul>
    <ul id="R_5.011.050">
        <li id="R_5.011.050ab">atha vainaṃ samutkṣipya upary upari sāgaram</li>
        <li id="R_5.011.050cd">rāmāyopahariṣyāmi paśuṃ paśupater iva</li>
    </ul>
    <ul id="R_5.011.051">
        <li id="R_5.011.051ab">iti cintā samāpannaḥ sītām anadhigamya tām</li>
        <li id="R_5.011.051cd">dhyānaśokā parītātmā cintayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.011.052">
        <li id="R_5.011.052ab">yāvat sītāṃ na paśyāmi rāmapatnīṃ yaśasvinīm</li>
        <li id="R_5.011.052cd">tāvad etāṃ purīṃ laṅkāṃ vicinomi punaḥ punaḥ</li>
    </ul>
    <ul id="R_5.011.053">
        <li id="R_5.011.053ab">saṃpāti vacanāc cāpi rāmaṃ yady ānayāmy aham</li>
        <li id="R_5.011.053cd">apaśyan rāghavo bhāryāṃ nirdahet sarvavānarān</li>
    </ul>
    <ul id="R_5.011.054">
        <li id="R_5.011.054ab">ihaiva niyatāhāro vatsyāmi niyatendriyaḥ</li>
        <li id="R_5.011.054cd">na matkṛte vinaśyeyuḥ sarve te naravānarāḥ</li>
    </ul>
    <ul id="R_5.011.055">
        <li id="R_5.011.055ab">aśokavanikā cāpi mahatīyaṃ mahādrumā</li>
        <li id="R_5.011.055cd">imām abhigamiṣyāmi na hīyaṃ vicitā mayā</li>
    </ul>
    <ul id="R_5.011.056">
        <li id="R_5.011.056ab">vasūn rudrāṃs tathādityān aśvinau maruto 'pi ca</li>
        <li id="R_5.011.056cd">namaskṛtvā gamiṣyāmi rakṣasāṃ śokavardhanaḥ</li>
    </ul>
    <ul id="R_5.011.057">
        <li id="R_5.011.057ab">jitvā tu rākṣasān devīm ikṣvākukulanandinīm</li>
        <li id="R_5.011.057cd">saṃpradāsyāmi rāmāyā yathāsiddhiṃ tapasvine</li>
    </ul>
    <ul id="R_5.011.058">
        <li id="R_5.011.058ab">sa muhūrtam iva dhyātvā cintāvigrathitendriyaḥ</li>
        <li id="R_5.011.058cd">udatiṣṭhan mahābāhur hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.011.059">
        <li id="R_5.011.059ab">
            <seg type="pāda" n="a">namo 'stu rāmāya salakṣmaṇāya</seg>
            <seg type="pāda" n="b">devyai ca tasyai janakātmajāyai</seg>
        </li>
        <li id="R_5.011.059cd">
            <seg type="pāda" n="c">namo 'stu rudrendrayamānilebhyo</seg>
            <seg type="pāda" n="d">namo 'stu candrārkamarudgaṇebhyaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.011.060">
        <li id="R_5.011.060ab">sa tebhyas tu namaskṛtvā sugrīvāya ca mārutiḥ</li>
        <li id="R_5.011.060cd">diśaḥ sarvāḥ samālokya aśokavanikāṃ prati</li>
    </ul>
    <ul id="R_5.011.061">
        <li id="R_5.011.061ab">sa gatvā manasā pūrvam aśokavanikāṃ śubhām</li>
        <li id="R_5.011.061cd">uttaraṃ cintayām āsa vānaro mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.011.062">
        <li id="R_5.011.062ab">dhruvaṃ tu rakṣobahulā bhaviṣyati vanākulā</li>
        <li id="R_5.011.062cd">aśokavanikā cintyā sarvasaṃskārasaṃskṛtā</li>
    </ul>
    <ul id="R_5.011.063">
        <li id="R_5.011.063ab">rakṣiṇaś cātra vihitā nūnaṃ rakṣanti pādapān</li>
        <li id="R_5.011.063cd">bhagavān api sarvātmā nātikṣobhaṃ pravāyati</li>
    </ul>
    <ul id="R_5.011.064">
        <li id="R_5.011.064ab">saṃkṣipto 'yaṃ mayātmā ca rāmārthe rāvaṇasya ca</li>
        <li id="R_5.011.064cd">siddhiṃ me saṃvidhāsyanti devāḥ sarṣigaṇās tv iha</li>
    </ul>
    <ul id="R_5.011.065">
        <li id="R_5.011.065ab">brahmā svayambhūr bhagavān devāś caiva diśantu me</li>
        <li id="R_5.011.065cd">siddhim agniś ca vāyuś ca puruhūtaś ca vajradhṛt</li>
    </ul>
    <ul id="R_5.011.066">
        <li id="R_5.011.066ab">varuṇaḥ pāśahastaś ca somādityai tathaiva ca</li>
        <li id="R_5.011.066cd">aśvinau ca mahātmānau marutaḥ sarva eva ca</li>
    </ul>
    <ul id="R_5.011.067">
        <li id="R_5.011.067ab">siddhiṃ sarvāṇi bhūtāni bhūtānāṃ caiva yaḥ prabhuḥ</li>
        <li id="R_5.011.067cd">dāsyanti mama ye cānye adṛṣṭāḥ pathi gocarāḥ</li>
    </ul>
    <ul id="R_5.011.068">
        <li id="R_5.011.068ab">
            <seg type="pāda" n="a">tad unnasaṃ pāṇḍuradantam avraṇaṃ</seg>
            <seg type="pāda" n="b">śucismitaṃ padmapalāśalocanam</seg>
        </li>
        <li id="R_5.011.068cd">
            <seg type="pāda" n="c">drakṣye tad āryāvadanaṃ kadā nv ahaṃ</seg>
            <seg type="pāda" n="d">prasannatārādhipatulyadarśanam</seg>
        </li>
    </ul>
    <ul id="R_5.011.069">
        <li id="R_5.011.069ab">
            <seg type="pāda" n="a">kṣudreṇa pāpena nṛśaṃsakarmaṇā</seg>
            <seg type="pāda" n="b">sudāruṇālāṃkṛtaveṣadhāriṇā</seg>
        </li>
        <li id="R_5.011.069cd">
            <seg type="pāda" n="c">balābhibhūtā abalā tapasvinī</seg>
            <seg type="pāda" n="d">kathaṃ nu me dṛṣṭapathe 'dya sā bhavet</seg>
        </li>
    </ul>
    <ul id="R_5.012.001">
        <li id="R_5.012.001ab">sa muhūrtam iva dhyatvā manasā cādhigamya tām</li>
        <li id="R_5.012.001cd">avapluto mahātejāḥ prākāraṃ tasya veśmanaḥ</li>
    </ul>
    <ul id="R_5.012.002">
        <li id="R_5.012.002ab">sa tu saṃhṛṣṭasarvāṅgaḥ prākārastho mahākapiḥ</li>
        <li id="R_5.012.002cd">puṣpitāgrān vasantādau dadarśa vividhān drumān</li>
    </ul>
    <ul id="R_5.012.003">
        <li id="R_5.012.003ab">sālān aśokān bhavyāṃś ca campakāṃś ca supuṣpitān</li>
        <li id="R_5.012.003cd">uddālakān nāgavṛkṣāṃś cūtān kapimukhān api</li>
    </ul>
    <ul id="R_5.012.004">
        <li id="R_5.012.004ab">athāmravaṇasaṃchannāṃ latāśatasamāvṛtām</li>
        <li id="R_5.012.004cd">jyāmukta iva nārācaḥ pupluve vṛkṣavāṭikām</li>
    </ul>
    <ul id="R_5.012.005">
        <li id="R_5.012.005ab">sa praviṣya vicitrāṃ tāṃ vihagair abhināditām</li>
        <li id="R_5.012.005cd">rājataiḥ kāñcanaiś caiva pādapaiḥ sarvatovṛtām</li>
    </ul>
    <ul id="R_5.012.006">
        <li id="R_5.012.006ab">vihagair mṛgasaṃghaiś ca vicitrāṃ citrakānanām</li>
        <li id="R_5.012.006cd">uditādityasaṃkāśāṃ dadarśa hanumān kapiḥ</li>
    </ul>
    <ul id="R_5.012.007">
        <li id="R_5.012.007ab">vṛtāṃ nānāvidhair vṛkṣaiḥ puṣpopagaphalopagaiḥ</li>
        <li id="R_5.012.007cd">kokilair bhṛṅgarājaiś ca mattair nityaniṣevitām</li>
    </ul>
    <ul id="R_5.012.008">
        <li id="R_5.012.008ab">prahṛṣṭamanuje kale mṛgapakṣisamākule</li>
        <li id="R_5.012.008cd">mattabarhiṇasaṃghuṣṭāṃ nānādvijagaṇāyutām</li>
    </ul>
    <ul id="R_5.012.009">
        <li id="R_5.012.009ab">mārgamāṇo varārohāṃ rājaputrīm aninditām</li>
        <li id="R_5.012.009cd">sukhaprasuptān vihagān bodhayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.012.010">
        <li id="R_5.012.010ab">utpatadbhir dvijagaṇaiḥ pakṣaiḥ sālāḥ samāhatāḥ</li>
        <li id="R_5.012.010cd">anekavarṇā vividhā mumucuḥ puṣpavṛṣṭayaḥ</li>
    </ul>
    <ul id="R_5.012.011">
        <li id="R_5.012.011ab">puṣpāvakīrṇaḥ śuśubhe hanumān mārutātmajaḥ</li>
        <li id="R_5.012.011cd">aśokavanikāmadhye yathā puṣpamayo giriḥ</li>
    </ul>
    <ul id="R_5.012.012">
        <li id="R_5.012.012ab">diśaḥ sarvābhidāvantaṃ vṛkṣaṣaṇḍagataṃ kapim</li>
        <li id="R_5.012.012cd">dṛṣṭvā sarvāṇi bhūtāni vasanta iti menire</li>
    </ul>
    <ul id="R_5.012.013">
        <li id="R_5.012.013ab">vṛkṣebhyaḥ patitaiḥ puṣpair avakīrṇā pṛthagvidhaiḥ</li>
        <li id="R_5.012.013cd">rarāja vasudhā tatra pramadeva vibhūṣitā</li>
    </ul>
    <ul id="R_5.012.014">
        <li id="R_5.012.014ab">tarasvinā te taravas tarasābhiprakampitāḥ</li>
        <li id="R_5.012.014cd">kusumāni vicitrāṇi sasṛjuḥ kapinā tadā</li>
    </ul>
    <ul id="R_5.012.015">
        <li id="R_5.012.015ab">nirdhūtapatraśikharāḥ śīrṇapuṣpaphaladrumāḥ</li>
        <li id="R_5.012.015cd">nikṣiptavastrābharaṇā dhūrtā iva parājitāḥ</li>
    </ul>
    <ul id="R_5.012.016">
        <li id="R_5.012.016ab">hanūmatā vegavatā kampitās te nagottamāḥ</li>
        <li id="R_5.012.016cd">puṣpaparṇaphalāny āśu mumucuḥ puṣpaśālinaḥ</li>
    </ul>
    <ul id="R_5.012.017">
        <li id="R_5.012.017ab">vihaṃgasaṃghair hīnās te skandhamātrāśrayā drumāḥ</li>
        <li id="R_5.012.017cd">babhūvur agamāḥ sarve māruteneva nirdhutāḥ</li>
    </ul>
    <ul id="R_5.012.018">
        <li id="R_5.012.018ab">vidhūtakeśī yuvatir yathā mṛditavarṇikā</li>
        <li id="R_5.012.018cd">niṣpītaśubhadantauṣṭhī nakhair dantaiś ca vikṣatā</li>
    </ul>
    <ul id="R_5.012.019">
        <li id="R_5.012.019ab">tathā lāṅgūlahastaiś ca caraṇābhyāṃ ca marditā</li>
        <li id="R_5.012.019cd">babhūvāśokavanikā prabhagnavarapādapā</li>
    </ul>
    <ul id="R_5.012.020">
        <li id="R_5.012.020ab">mahālatānāṃ dāmāni vyadhamat tarasā kapiḥ</li>
        <li id="R_5.012.020cd">yathā prāvṛṣi vindhyasya meghajālāni mārutaḥ</li>
    </ul>
    <ul id="R_5.012.021">
        <li id="R_5.012.021ab">sa tatra maṇibhūmīś ca rājatīś ca manoramāḥ</li>
        <li id="R_5.012.021cd">tathā kāñcanabhūmīś ca vicaran dadṛśe kapiḥ</li>
    </ul>
    <ul id="R_5.012.022">
        <li id="R_5.012.022ab">vāpīś ca vividhākārāḥ pūrṇāḥ paramavāriṇā</li>
        <li id="R_5.012.022cd">mahārhair maṇisopānair upapannās tatas tataḥ</li>
    </ul>
    <ul id="R_5.012.023">
        <li id="R_5.012.023ab">muktāpravālasikatā sphaṭikāntarakuṭṭimāḥ</li>
        <li id="R_5.012.023cd">kāñcanais tarubhiś citrais tīrajair upaśobhitāḥ</li>
    </ul>
    <ul id="R_5.012.024">
        <li id="R_5.012.024ab">phullapadmotpalavanāś cakravākopakūjitāḥ</li>
        <li id="R_5.012.024cd">natyūharutasaṃghuṣṭā haṃsasārasanāditāḥ</li>
    </ul>
    <ul id="R_5.012.025">
        <li id="R_5.012.025ab">dīrghābhir drumayuktābhiḥ saridbhiś ca samantataḥ</li>
        <li id="R_5.012.025cd">amṛtopamatoyābhiḥ śivābhir upasaṃskṛtāḥ</li>
    </ul>
    <ul id="R_5.012.026">
        <li id="R_5.012.026ab">latāśatair avatatāḥ santānakasamāvṛtāḥ</li>
        <li id="R_5.012.026cd">nānāgulmāvṛtavanāḥ karavīrakṛtāntarāḥ</li>
    </ul>
    <ul id="R_5.012.027">
        <li id="R_5.012.027ab">tato 'mbudharasaṃkāśaṃ pravṛddhaśikharaṃ girim</li>
        <li id="R_5.012.027cd">vicitrakūṭaṃ kūṭaiś ca sarvataḥ parivāritam</li>
    </ul>
    <ul id="R_5.012.028">
        <li id="R_5.012.028ab">śilāgṛhair avatataṃ nānāvṛkṣaiḥ samāvṛtam</li>
        <li id="R_5.012.028cd">dadarśa kapiśārdūlo ramyaṃ jagati parvatam</li>
    </ul>
    <ul id="R_5.012.029">
        <li id="R_5.012.029ab">dadarśa ca nagāt tasmān nadīṃ nipatitāṃ kapiḥ</li>
        <li id="R_5.012.029cd">aṅkād iva samutpatya priyasya patitāṃ priyām</li>
    </ul>
    <ul id="R_5.012.030">
        <li id="R_5.012.030ab">jale nipatitāgraiś ca pādapair upaśobhitām</li>
        <li id="R_5.012.030cd">vāryamāṇām iva kruddhāṃ pramadāṃ priyabandhubhiḥ</li>
    </ul>
    <ul id="R_5.012.031">
        <li id="R_5.012.031ab">punar āvṛttatoyāṃ ca dadarśa sa mahākapiḥ</li>
        <li id="R_5.012.031cd">prasannām iva kāntasya kāntāṃ punar upasthitām</li>
    </ul>
    <ul id="R_5.012.032">
        <li id="R_5.012.032ab">tasyādūrāt sa padminyo nānādvijagaṇāyutāḥ</li>
        <li id="R_5.012.032cd">dadarśa kapiśārdūlo hanumān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.012.033">
        <li id="R_5.012.033ab">kṛtrimāṃ dīrghikāṃ cāpi pūrṇāṃ śītena vāriṇā</li>
        <li id="R_5.012.033cd">maṇipravarasopānāṃ muktāsikataśobhitām</li>
    </ul>
    <ul id="R_5.012.034">
        <li id="R_5.012.034ab">vividhair mṛgasaṃghaiś ca vicitrāṃ citrakānanām</li>
        <li id="R_5.012.034cd">prāsādaiḥ sumahadbhiś ca nirmitair viśvakarmaṇā</li>
        <li id="R_5.012.034ef">kānanaiḥ kṛtrimaiś cāpi sarvataḥ samalaṃkṛtām</li>
    </ul>
    <ul id="R_5.012.035">
        <li id="R_5.012.035ab">ye ke cit pādapās tatra puṣpopagaphalopagāḥ</li>
        <li id="R_5.012.035cd">sacchatrāḥ savitardīkāḥ sarve sauvarṇavedikāḥ</li>
    </ul>
    <ul id="R_5.012.036">
        <li id="R_5.012.036ab">latāpratānair bahubhiḥ parṇaiś ca bahubhir vṛtām</li>
        <li id="R_5.012.036cd">kāñcanīṃ śiṃśupām ekāṃ dadarśa sa mahākapiḥ</li>
    </ul>
    <ul id="R_5.012.037">
        <li id="R_5.012.037ab">so 'paśyad bhūmibhāgāṃś ca gartaprasravaṇāni ca</li>
        <li id="R_5.012.037cd">suvarṇavṛkṣān aparān dadarśa śikhisaṃnibhān</li>
    </ul>
    <ul id="R_5.012.038">
        <li id="R_5.012.038ab">teṣāṃ drumāṇāṃ prabhayā meror iva mahākapiḥ</li>
        <li id="R_5.012.038cd">amanyata tadā vīraḥ kāñcano 'smīti vānaraḥ</li>
    </ul>
    <ul id="R_5.012.039">
        <li id="R_5.012.039ab">tāṃ kāñcanais tarugaṇair mārutena ca vījitām</li>
        <li id="R_5.012.039cd">kiṅkiṇīśatanirghoṣāṃ dṛṣṭvā vismayam āgamat</li>
    </ul>
    <ul id="R_5.012.040">
        <li id="R_5.012.040ab">supuṣpitāgrāṃ rucirāṃ taruṇāṅkurapallavām</li>
        <li id="R_5.012.040cd">tām āruhya mahāvegaḥ śiṃśapāṃ parṇasaṃvṛtām</li>
    </ul>
    <ul id="R_5.012.041">
        <li id="R_5.012.041ab">ito drakṣyāmi vaidehīṃ rāma darśanalālasām</li>
        <li id="R_5.012.041cd">itaś cetaś ca duḥkhārtāṃ saṃpatantīṃ yadṛcchayā</li>
    </ul>
    <ul id="R_5.012.042">
        <li id="R_5.012.042ab">aśokavanikā ceyaṃ dṛḍhaṃ ramyā durātmanaḥ</li>
        <li id="R_5.012.042cd">campakaiś candanaiś cāpi bakulaiś ca vibhūṣitā</li>
    </ul>
    <ul id="R_5.012.043">
        <li id="R_5.012.043ab">iyaṃ ca nalinī ramyā dvijasaṃghaniṣevitā</li>
        <li id="R_5.012.043cd">imāṃ sā rāmamahiṣī nūnam eṣyati jānakī</li>
    </ul>
    <ul id="R_5.012.044">
        <li id="R_5.012.044ab">sā rāma rāmamahiṣī rāghavasya priyā sadā</li>
        <li id="R_5.012.044cd">vanasaṃcārakuśalā nūnam eṣyati jānakī</li>
    </ul>
    <ul id="R_5.012.045">
        <li id="R_5.012.045ab">atha vā mṛgaśāvākṣī vanasyāsya vicakṣaṇā</li>
        <li id="R_5.012.045cd">vanam eṣyati sā ceha rāmacintānukarśitā</li>
    </ul>
    <ul id="R_5.012.046">
        <li id="R_5.012.046ab">rāmaśokābhisaṃtaptā sā devī vāmalocanā</li>
        <li id="R_5.012.046cd">vanavāsaratā nityam eṣyate vanacāriṇī</li>
    </ul>
    <ul id="R_5.012.047">
        <li id="R_5.012.047ab">vanecarāṇāṃ satataṃ nūnaṃ spṛhayate purā</li>
        <li id="R_5.012.047cd">rāmasya dayitā bhāryā janakasya sutā satī</li>
    </ul>
    <ul id="R_5.012.048">
        <li id="R_5.012.048ab">saṃdhyākālamanāḥ śyāmā dhruvam eṣyati jānakī</li>
        <li id="R_5.012.048cd">nadīṃ cemāṃ śivajalāṃ saṃdhyārthe varavarṇinī</li>
    </ul>
    <ul id="R_5.012.049">
        <li id="R_5.012.049ab">tasyāś cāpy anurūpeyam aśokavanikā śubhā</li>
        <li id="R_5.012.049cd">śubhā yā pārthivendrasya patnī rāmasya saṃmitā</li>
    </ul>
    <ul id="R_5.012.050">
        <li id="R_5.012.050ab">yadi jivati sā devī tārādhipanibhānanā</li>
        <li id="R_5.012.050cd">āgamiṣyati sāvaśyam imāṃ śivajalāṃ nadīm</li>
    </ul>
    <ul id="R_5.012.051">
        <li id="R_5.012.051ab">
            <seg type="pāda" n="a">evaṃ tu matvā hanumān mahātmā</seg>
            <seg type="pāda" n="b">pratīkṣamāṇo manujendrapatnīm</seg>
        </li>
        <li id="R_5.012.051cd">
            <seg type="pāda" n="c">avekṣamāṇaś ca dadarśa sarvaṃ</seg>
            <seg type="pāda" n="d">supuṣpite parṇaghane nilīnaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.013.001">
        <li id="R_5.013.001ab">sa vīkṣamāṇas tatrastho mārgamāṇaś ca maithilīm</li>
        <li id="R_5.013.001cd">avekṣamāṇaś ca mahīṃ sarvāṃ tām anvavaikṣata</li>
    </ul>
    <ul id="R_5.013.002">
        <li id="R_5.013.002ab">santāna kalatābhiś ca pādapair upaśobhitām</li>
        <li id="R_5.013.002cd">divyagandharasopetāṃ sarvataḥ samalaṃkṛtām</li>
    </ul>
    <ul id="R_5.013.003">
        <li id="R_5.013.003ab">tāṃ sa nandanasaṃkāśāṃ mṛgapakṣibhir āvṛtām</li>
        <li id="R_5.013.003cd">harmyaprāsādasaṃbādhāṃ kokilākulaniḥsvanām</li>
    </ul>
    <ul id="R_5.013.004">
        <li id="R_5.013.004ab">kāñcanotpalapadmābhir vāpībhir upaśobhitām</li>
        <li id="R_5.013.004cd">bahvāsanakuthopetāṃ bahubhūmigṛhāyutām</li>
    </ul>
    <ul id="R_5.013.005">
        <li id="R_5.013.005ab">sarvartukusumai ramyaiḥ phalavadbhiś ca pādapaiḥ</li>
        <li id="R_5.013.005cd">puṣpitānām aśokānāṃ śriyā sūryodayaprabhām</li>
    </ul>
    <ul id="R_5.013.006">
        <li id="R_5.013.006ab">pradīptām iva tatrastho mārutiḥ samudaikṣata</li>
        <li id="R_5.013.006cd">niṣpatraśākhāṃ vihagaiḥ kriyamāṇām ivāsakṛt</li>
        <li id="R_5.013.006ef">viniṣpatadbhiḥ śataśaś citraiḥ puṣpāvataṃsakaiḥ</li>
    </ul>
    <ul id="R_5.013.007">
        <li id="R_5.013.007ab">āmūlapuṣpanicitair aśokaiḥ śokanāśanaiḥ</li>
        <li id="R_5.013.007cd">puṣpabhārātibhāraiś ca spṛśadbhir iva medinīm</li>
    </ul>
    <ul id="R_5.013.008">
        <li id="R_5.013.008ab">karṇikāraiḥ kusumitaiḥ kiṃśukaiś ca supuṣpitaiḥ</li>
        <li id="R_5.013.008cd">sa deśaḥ prabhayā teṣāṃ pradīpta iva sarvataḥ</li>
    </ul>
    <ul id="R_5.013.009">
        <li id="R_5.013.009ab">puṃnāgāḥ saptaparṇāś ca campakoddālakās tathā</li>
        <li id="R_5.013.009cd">vivṛddhamūlā bahavaḥ śobhante sma supuṣpitāḥ</li>
    </ul>
    <ul id="R_5.013.010">
        <li id="R_5.013.010ab">śātakumbhanibhāḥ ke cit ke cid agniśikhopamāḥ</li>
        <li id="R_5.013.010cd">nīlāñjananibhāḥ ke cit tatrāśokāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_5.013.011">
        <li id="R_5.013.011ab">nandanaṃ vividhodyānaṃ citraṃ caitrarathaṃ yathā</li>
        <li id="R_5.013.011cd">ativṛttam ivācintyaṃ divyaṃ ramyaṃ śriyā vṛtam</li>
    </ul>
    <ul id="R_5.013.012">
        <li id="R_5.013.012ab">dvitīyam iva cākāśaṃ puṣpajyotirgaṇāyutam</li>
        <li id="R_5.013.012cd">puṣparatnaśataiś citraṃ pañcamaṃ sāgaraṃ yathā</li>
    </ul>
    <ul id="R_5.013.013">
        <li id="R_5.013.013ab">sarvartupuṣpair nicitaṃ pādapair madhugandhibhiḥ</li>
        <li id="R_5.013.013cd">nānāninādair udyānaṃ ramyaṃ mṛgagaṇair dvijaiḥ</li>
    </ul>
    <ul id="R_5.013.014">
        <li id="R_5.013.014ab">anekagandhapravahaṃ puṇyagandhaṃ manoramam</li>
        <li id="R_5.013.014cd">śailendram iva gandhāḍhyaṃ dvitīyaṃ gandhamādanam</li>
    </ul>
    <ul id="R_5.013.015">
        <li id="R_5.013.015ab">aśokavanikāyāṃ tu tasyāṃ vānarapuṃgavaḥ</li>
        <li id="R_5.013.015cd">sa dadarśāvidūrasthaṃ caityaprāsādam ūrjitam</li>
    </ul>
    <ul id="R_5.013.016">
        <li id="R_5.013.016ab">madhye stambhasahasreṇa sthitaṃ kailāsapāṇḍuram</li>
        <li id="R_5.013.016cd">pravālakṛtasopānaṃ taptakāñcanavedikam</li>
    </ul>
    <ul id="R_5.013.017">
        <li id="R_5.013.017ab">muṣṇantam iva cakṣūṃṣi dyotamānam iva śriyā</li>
        <li id="R_5.013.017cd">vimalaṃ prāṃśubhāvatvād ullikhantam ivāmbaram</li>
    </ul>
    <ul id="R_5.013.018">
        <li id="R_5.013.018ab">tato malinasaṃvītāṃ rākṣasībhiḥ samāvṛtām</li>
        <li id="R_5.013.018cd">upavāsakṛśāṃ dīnāṃ niḥśvasāntīṃ punaḥ punaḥ</li>
        <li id="R_5.013.018ef">dadarśa śuklapakṣādau candrarekhām ivāmalām</li>
    </ul>
    <ul id="R_5.013.019">
        <li id="R_5.013.019ab">mandaprakhyāyamānena rūpeṇa ruciraprabhām</li>
        <li id="R_5.013.019cd">pinaddhāṃ dhūmajālena śikhām iva vibhāvasoḥ</li>
    </ul>
    <ul id="R_5.013.020">
        <li id="R_5.013.020ab">pītenaikena saṃvītāṃ kliṣṭenottamavāsasā</li>
        <li id="R_5.013.020cd">sapaṅkām analaṃkārāṃ vipadmām iva padminīm</li>
    </ul>
    <ul id="R_5.013.021">
        <li id="R_5.013.021ab">vrīḍitāṃ duḥkhasaṃtaptāṃ parimlānāṃ tapasvinīm</li>
        <li id="R_5.013.021cd">graheṇāṅgārakeṇaiva pīḍitām iva rohiṇīm</li>
    </ul>
    <ul id="R_5.013.022">
        <li id="R_5.013.022ab">aśrupūrṇamukhīṃ dīnāṃ kṛśām ananaśena ca</li>
        <li id="R_5.013.022cd">śokadhyānaparāṃ dīnāṃ nityaṃ duḥkhaparāyaṇām</li>
    </ul>
    <ul id="R_5.013.023">
        <li id="R_5.013.023ab">priyaṃ janam apaśyantīṃ paśyantīṃ rākṣasīgaṇam</li>
        <li id="R_5.013.023cd">svagaṇena mṛgīṃ hīnāṃ śvagaṇābhivṛtām iva</li>
    </ul>
    <ul id="R_5.013.024">
        <li id="R_5.013.024ab">nīlanāgābhayā veṇyā jaghanaṃ gatayaikayā</li>
        <li id="R_5.013.024cd">sukhārhāṃ duḥkhasaṃtaptāṃ vyasanānām akodivām</li>
    </ul>
    <ul id="R_5.013.025">
        <li id="R_5.013.025ab">tāṃ samīkṣya viśālākṣīm adhikaṃ malināṃ kṛśām</li>
        <li id="R_5.013.025cd">tarkayām āsa sīteti kāraṇair upapādibhiḥ</li>
    </ul>
    <ul id="R_5.013.026">
        <li id="R_5.013.026ab">hriyamāṇā tadā tena rakṣasā kāmarūpiṇā</li>
        <li id="R_5.013.026cd">yathārūpā hi dṛṣṭā vai tathārūpeyam aṅganā</li>
    </ul>
    <ul id="R_5.013.027">
        <li id="R_5.013.027ab">pūrṇacandrānanāṃ subhrūṃ cāruvṛttapayodharām</li>
        <li id="R_5.013.027cd">kurvantīṃ prabhayā devīṃ sarvā vitimirā diśaḥ</li>
    </ul>
    <ul id="R_5.013.028">
        <li id="R_5.013.028ab">tāṃ nīlakeśīṃ bimbauṣṭhīṃ sumadhyāṃ supratiṣṭhitām</li>
        <li id="R_5.013.028cd">sītāṃ padmapalāśākṣīṃ manmathasya ratiṃ yathā</li>
    </ul>
    <ul id="R_5.013.029">
        <li id="R_5.013.029ab">iṣṭāṃ sarvasya jagataḥ pūrṇacandraprabhām iva</li>
        <li id="R_5.013.029cd">bhūmau sutanum āsīnāṃ niyatām iva tāpasīm</li>
    </ul>
    <ul id="R_5.013.030">
        <li id="R_5.013.030ab">niḥśvāsabahulāṃ bhīruṃ bhujagendravadhūm iva</li>
        <li id="R_5.013.030cd">śokajālena mahatā vitatena na rājatīm</li>
    </ul>
    <ul id="R_5.013.031">
        <li id="R_5.013.031ab">saṃsaktāṃ dhūmajālena śikhām iva vibhāvasoḥ</li>
        <li id="R_5.013.031cd">tāṃ smṛtīm iva saṃdigdhām ṛddhiṃ nipatitām iva</li>
    </ul>
    <ul id="R_5.013.032">
        <li id="R_5.013.032ab">vihatām iva ca śraddhām āśāṃ pratihatām iva</li>
        <li id="R_5.013.032cd">sopasargāṃ yathā siddhiṃ buddhiṃ sakaluṣām iva</li>
    </ul>
    <ul id="R_5.013.033">
        <li id="R_5.013.033ab">abhūtenāpavādena kīrtiṃ nipatitām iva</li>
        <li id="R_5.013.033cd">rāmoparodhavyathitāṃ rakṣoharaṇakarśitām</li>
    </ul>
    <ul id="R_5.013.034">
        <li id="R_5.013.034ab">abalāṃ mṛgaśāvākṣīṃ vīkṣamāṇāṃ tatas tataḥ</li>
        <li id="R_5.013.034cd">bāṣpāmbupratipūrṇena kṛṣṇavaktrākṣipakṣmaṇā</li>
        <li id="R_5.013.034ef">vadanenāprasannena niḥśvasantīṃ punaḥ punaḥ</li>
    </ul>
    <ul id="R_5.013.035">
        <li id="R_5.013.035ab">malapaṅkadharāṃ dīnāṃ maṇḍanārhām amaṇḍitām</li>
        <li id="R_5.013.035cd">prabhāṃ nakṣatrarājasya kālameghair ivāvṛtām</li>
    </ul>
    <ul id="R_5.013.036">
        <li id="R_5.013.036ab">tasya saṃdidihe buddhir muhuḥ sītāṃ nirīkṣya tu</li>
        <li id="R_5.013.036cd">āmnāyānām ayogena vidyāṃ praśithilām iva</li>
    </ul>
    <ul id="R_5.013.037">
        <li id="R_5.013.037ab">duḥkhena bubudhe sītāṃ hanumān analaṃkṛtām</li>
        <li id="R_5.013.037cd">saṃskāreṇa yathāhīnāṃ vācam arthāntaraṃ gatām</li>
    </ul>
    <ul id="R_5.013.038">
        <li id="R_5.013.038ab">tāṃ samīkṣya viśālākṣīṃ rājaputrīm aninditām</li>
        <li id="R_5.013.038cd">tarkayām āsa sīteti kāraṇair upapādayan</li>
    </ul>
    <ul id="R_5.013.039">
        <li id="R_5.013.039ab">vaidehyā yāni cāṅgeṣu tadā rāmo 'nvakīrtayat</li>
        <li id="R_5.013.039cd">tāny ābharaṇajālāni gātraśobhīny alakṣayat</li>
    </ul>
    <ul id="R_5.013.040">
        <li id="R_5.013.040ab">sukṛtau karṇaveṣṭau ca śvadaṃṣṭrau ca susaṃsthitau</li>
        <li id="R_5.013.040cd">maṇividrumacitrāṇi hasteṣv ābharaṇāni ca</li>
    </ul>
    <ul id="R_5.013.041">
        <li id="R_5.013.041ab">śyāmāni cirayuktatvāt tathā saṃsthānavanti ca</li>
        <li id="R_5.013.041cd">tāny evaitāni manye 'haṃ yāni rāmo 'vnakīrtayat</li>
    </ul>
    <ul id="R_5.013.042">
        <li id="R_5.013.042ab">tatra yāny avahīnāni tāny ahaṃ nopalakṣaye</li>
        <li id="R_5.013.042cd">yāny asyā nāvahīnāni tānīmāni na saṃśayaḥ</li>
    </ul>
    <ul id="R_5.013.043">
        <li id="R_5.013.043ab">pītaṃ kanakapaṭṭābhaṃ srastaṃ tad vasanaṃ śubham</li>
        <li id="R_5.013.043cd">uttarīyaṃ nagāsaktaṃ tadā dṛṣṭaṃ plavaṃgamaiḥ</li>
    </ul>
    <ul id="R_5.013.044">
        <li id="R_5.013.044ab">bhūṣaṇāni ca mukhyāni dṛṣṭāni dharaṇītale</li>
        <li id="R_5.013.044cd">anayaivāpaviddhāni svanavanti mahānti ca</li>
    </ul>
    <ul id="R_5.013.045">
        <li id="R_5.013.045ab">idaṃ ciragṛhītatvād vasanaṃ kliṣṭavattaram</li>
        <li id="R_5.013.045cd">tathā hi nūnaṃ tad varṇaṃ tathā śrīmad yathetarat</li>
    </ul>
    <ul id="R_5.013.046">
        <li id="R_5.013.046ab">iyaṃ kanakavarṇāṅgī rāmasya mahiṣī priyā</li>
        <li id="R_5.013.046cd">pranaṣṭāpi satī yasya manaso na praṇaśyati</li>
    </ul>
    <ul id="R_5.013.047">
        <li id="R_5.013.047ab">iyaṃ sā yat kṛte rāmaś caturbhiḥ paritapyate</li>
        <li id="R_5.013.047cd">kāruṇyenānṛśaṃsyena śokena madanena ca</li>
    </ul>
    <ul id="R_5.013.048">
        <li id="R_5.013.048ab">strī pranaṣṭeti kāruṇyād āśritety ānṛśaṃsyataḥ</li>
        <li id="R_5.013.048cd">patnī naṣṭeti śokena priyeti madanena ca</li>
    </ul>
    <ul id="R_5.013.049">
        <li id="R_5.013.049ab">asyā devyā yathā rūpam aṅgapratyaṅgasauṣṭhavam</li>
        <li id="R_5.013.049cd">rāmasya ca yathārūpaṃ tasyeyam asitekṣaṇā</li>
    </ul>
    <ul id="R_5.013.050">
        <li id="R_5.013.050ab">asyā devyā manas tasmiṃs tasya cāsyāṃ pratiṣṭhitam</li>
        <li id="R_5.013.050cd">teneyaṃ sa ca dharmātmā muhūrtam api jīvati</li>
    </ul>
    <ul id="R_5.013.051">
        <li id="R_5.013.051ab">duṣkaraṃ kurute rāmo ya imāṃ mattakāśinīm</li>
        <li id="R_5.013.051cd">sītāṃ vinā mahābāhur muhūrtam api jīvati</li>
    </ul>
    <ul id="R_5.013.052">
        <li id="R_5.013.052ab">evaṃ sītāṃ tadā dṛṣṭvā hṛṣṭaḥ pavanasaṃbhavaḥ</li>
        <li id="R_5.013.052cd">jagāma manasā rāmaṃ praśaśaṃsa ca taṃ prabhum</li>
    </ul>
    <ul id="R_5.014.001">
        <li id="R_5.014.001ab">praśasya tu praśastavyāṃ sītāṃ tāṃ haripuṃgavaḥ</li>
        <li id="R_5.014.001cd">guṇābhirāmaṃ rāmaṃ ca punaś cintāparo 'bhavat</li>
    </ul>
    <ul id="R_5.014.002">
        <li id="R_5.014.002ab">sa muhūrtam iva dhyātvā bāṣpaparyākulekṣaṇaḥ</li>
        <li id="R_5.014.002cd">sītām āśritya tejasvī hanumān vilalāpa ha</li>
    </ul>
    <ul id="R_5.014.003">
        <li id="R_5.014.003ab">mānyā guruvinītasya lakṣmaṇasya gurupriyā</li>
        <li id="R_5.014.003cd">yadi sītāpi duḥkhārtā kālo hi duratikramaḥ</li>
    </ul>
    <ul id="R_5.014.004">
        <li id="R_5.014.004ab">rāmasya vyavasāyajñā lakṣmaṇasya ca dhīmataḥ</li>
        <li id="R_5.014.004cd">nātyarthaṃ kṣubhyate devī gaṅgeva jaladāgame</li>
    </ul>
    <ul id="R_5.014.005">
        <li id="R_5.014.005ab">tulyaśīlavayovṛttāṃ tulyābhijanalakṣaṇām</li>
        <li id="R_5.014.005cd">rāghavo 'rhati vaidehīṃ taṃ ceyam asitekṣaṇā</li>
    </ul>
    <ul id="R_5.014.006">
        <li id="R_5.014.006ab">tāṃ dṛṣṭvā navahemābhāṃ lokakāntām iva śriyam</li>
        <li id="R_5.014.006cd">jagāma manasā rāmaṃ vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_5.014.007">
        <li id="R_5.014.007ab">asyā hetor viśālākṣyā hato vālī mahābalaḥ</li>
        <li id="R_5.014.007cd">rāvaṇapratimo vīrye kabandhaś ca nipātitaḥ</li>
    </ul>
    <ul id="R_5.014.008">
        <li id="R_5.014.008ab">virādhaś ca hataḥ saṃkhye rākṣaso bhīmavikramaḥ</li>
        <li id="R_5.014.008cd">vane rāmeṇa vikramya mahendreṇeva śambaraḥ</li>
    </ul>
    <ul id="R_5.014.009">
        <li id="R_5.014.009ab">caturdaśasahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_5.014.009cd">nihatāni janasthāne śarair agniśikhopamaiḥ</li>
    </ul>
    <ul id="R_5.014.010">
        <li id="R_5.014.010ab">kharaś ca nihataḥ saṃkhye triśirāś ca nipātitaḥ</li>
        <li id="R_5.014.010cd">dūṣaṇaś ca mahātejā rāmeṇa viditātmanā</li>
    </ul>
    <ul id="R_5.014.011">
        <li id="R_5.014.011ab">aiśvaryaṃ vānarāṇāṃ ca durlabhaṃ vālipālitam</li>
        <li id="R_5.014.011cd">asyā nimitte sugrīvaḥ prāptavāṃl lokasatkṛtam</li>
    </ul>
    <ul id="R_5.014.012">
        <li id="R_5.014.012ab">sāgaraś ca mayā krāntaḥ śrīmān nadanadīpatiḥ</li>
        <li id="R_5.014.012cd">asyā hetor viśālākṣyāḥ purī ceyaṃ nirīkṣitā</li>
    </ul>
    <ul id="R_5.014.013">
        <li id="R_5.014.013ab">yadi rāmaḥ samudrāntāṃ medinīṃ parivartayet</li>
        <li id="R_5.014.013cd">asyāḥ kṛte jagac cāpi yuktam ity eva me matiḥ</li>
    </ul>
    <ul id="R_5.014.014">
        <li id="R_5.014.014ab">rājyaṃ vā triṣu lokeṣu sītā vā janakātmajā</li>
        <li id="R_5.014.014cd">trailokyarājyaṃ sakalaṃ sītāyā nāpnuyāt kalām</li>
    </ul>
    <ul id="R_5.014.015">
        <li id="R_5.014.015ab">iyaṃ sā dharmaśīlasya maithilasya mahātmanaḥ</li>
        <li id="R_5.014.015cd">sutā janakarājasya sītā bhartṛdṛḍhavratā</li>
    </ul>
    <ul id="R_5.014.016">
        <li id="R_5.014.016ab">utthitā medinīṃ bhittvā kṣetre halamukhakṣate</li>
        <li id="R_5.014.016cd">padmareṇunibhaiḥ kīrṇā śubhaiḥ kedārapāṃsubhiḥ</li>
    </ul>
    <ul id="R_5.014.017">
        <li id="R_5.014.017ab">vikrāntasyāryaśīlasya saṃyugeṣv anivartinaḥ</li>
        <li id="R_5.014.017cd">snuṣā daśarathasyaiṣā jyeṣṭhā rājño yaśasvinī</li>
    </ul>
    <ul id="R_5.014.018">
        <li id="R_5.014.018ab">dharmajñasya kṛtajñasya rāmasya viditātmanaḥ</li>
        <li id="R_5.014.018cd">iyaṃ sā dayitā bhāryā rākṣasī vaśam āgatā</li>
    </ul>
    <ul id="R_5.014.019">
        <li id="R_5.014.019ab">sarvān bhogān parityajya bhartṛsnehabalāt kṛtā</li>
        <li id="R_5.014.019cd">acintayitvā duḥkhāni praviṣṭā nirjanaṃ vanam</li>
    </ul>
    <ul id="R_5.014.020">
        <li id="R_5.014.020ab">saṃtuṣṭā phalamūlena bhartṛśuśrūṣaṇe ratā</li>
        <li id="R_5.014.020cd">yā parāṃ bhajate prītiṃ vane 'pi bhavane yathā</li>
    </ul>
    <ul id="R_5.014.021">
        <li id="R_5.014.021ab">seyaṃ kanakavarṇāṅgī nityaṃ susmitabhāṣiṇī</li>
        <li id="R_5.014.021cd">sahate yātanām etām anarthānām abhāginī</li>
    </ul>
    <ul id="R_5.014.022">
        <li id="R_5.014.022ab">imāṃ tu śīlasaṃpannāṃ draṣṭum icchati rāghavaḥ</li>
        <li id="R_5.014.022cd">rāvaṇena pramathitāṃ prapām iva pipāsitaḥ</li>
    </ul>
    <ul id="R_5.014.023">
        <li id="R_5.014.023ab">asyā nūnaṃ punar lābhād rāghavaḥ prītim eṣyati</li>
        <li id="R_5.014.023cd">rājā rājyaparibhraṣṭaḥ punaḥ prāpyeva medinīm</li>
    </ul>
    <ul id="R_5.014.024">
        <li id="R_5.014.024ab">kāmabhogaiḥ parityaktā hīnā bandhujanena ca</li>
        <li id="R_5.014.024cd">dhārayaty ātmano dehaṃ tatsamāgamakāṅkṣiṇī</li>
    </ul>
    <ul id="R_5.014.025">
        <li id="R_5.014.025ab">naiṣā paśyati rākṣasyo nemān puṣpaphaladrumān</li>
        <li id="R_5.014.025cd">ekasthahṛdayā nūnaṃ rāmam evānupaśyati</li>
    </ul>
    <ul id="R_5.014.026">
        <li id="R_5.014.026ab">bhartā nāma paraṃ nāryā bhūṣaṇaṃ bhūṣaṇād api</li>
        <li id="R_5.014.026cd">eṣā hi rahitā tena śobhanārhā na śobhate</li>
    </ul>
    <ul id="R_5.014.027">
        <li id="R_5.014.027ab">duṣkaraṃ kurute rāmo hīno yad anayā prabhuḥ</li>
        <li id="R_5.014.027cd">dhārayaty ātmano dehaṃ na duḥkhenāvasīdati</li>
    </ul>
    <ul id="R_5.014.028">
        <li id="R_5.014.028ab">imām asitakeśāntāṃ śatapatranibhekṣaṇām</li>
        <li id="R_5.014.028cd">sukhārhāṃ duḥkhitāṃ dṛṣṭvā mamāpi vyathitaṃ manaḥ</li>
    </ul>
    <ul id="R_5.014.029">
        <li id="R_5.014.029ab">
            <seg type="pāda" n="a">kṣitikṣamā puṣkarasaṃnibhākṣī</seg>
            <seg type="pāda" n="b">yā rakṣitā rāghavalakṣmaṇābhyām</seg>
        </li>
        <li id="R_5.014.029cd">
            <seg type="pāda" n="c">sā rākṣasībhir vikṛtekṣaṇābhiḥ</seg>
            <seg type="pāda" n="d">saṃrakṣyate saṃprati vṛkṣamūle</seg>
        </li>
    </ul>
    <ul id="R_5.014.030">
        <li id="R_5.014.030ab">
            <seg type="pāda" n="a">himahatanalinīva naṣṭaśobhā</seg>
            <seg type="pāda" n="b">vyasanaparamparayā nipīḍyamānā</seg>
        </li>
        <li id="R_5.014.030cd">
            <seg type="pāda" n="c">sahacararahiteva cakravākī</seg>
            <seg type="pāda" n="d">janakasutā kṛpaṇāṃ daśāṃ prapannā</seg>
        </li>
    </ul>
    <ul id="R_5.014.031">
        <li id="R_5.014.031ab">
            <seg type="pāda" n="a">asyā hi puṣpāvanatāgraśākhāḥ</seg>
            <seg type="pāda" n="b">śokaṃ dṛḍhaṃ vai janayaty aśokāḥ</seg>
        </li>
        <li id="R_5.014.031cd">
            <seg type="pāda" n="c">himavyapāyena ca mandaraśmir</seg>
            <seg type="pāda" n="d">abhyutthito naikasahasraraśmiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.014.032">
        <li id="R_5.014.032ab">
            <seg type="pāda" n="a">ity evam arthaṃ kapir anvavekṣya</seg>
            <seg type="pāda" n="b">sīteyam ity eva niviṣṭabuddhiḥ</seg>
        </li>
        <li id="R_5.014.032cd">
            <seg type="pāda" n="c">saṃśritya tasmin niṣasāda vṛkṣe</seg>
            <seg type="pāda" n="d">balī harīṇām ṛṣabhas tarasvī</seg>
        </li>
    </ul>
    <ul id="R_5.015.001">
        <li id="R_5.015.001ab">tataḥ kumudaṣaṇḍābho nirmalaṃ nirmalaḥ svayam</li>
        <li id="R_5.015.001cd">prajagāma nabhaś candro haṃso nīlam ivodakam</li>
    </ul>
    <ul id="R_5.015.002">
        <li id="R_5.015.002ab">sācivyam iva kurvan sa prabhayā nirmalaprabhaḥ</li>
        <li id="R_5.015.002cd">candramā raśmibhiḥ śītaiḥ siṣeve pavanātmajam</li>
    </ul>
    <ul id="R_5.015.003">
        <li id="R_5.015.003ab">sa dadarśa tataḥ sītāṃ pūrṇacandranibhānanām</li>
        <li id="R_5.015.003cd">śokabhārair iva nyastāṃ bhārair nāvam ivāmbhasi</li>
    </ul>
    <ul id="R_5.015.004">
        <li id="R_5.015.004ab">didṛkṣamāṇo vaidehīṃ hanūmān mārutātmajaḥ</li>
        <li id="R_5.015.004cd">sa dadarśāvidūrasthā rākṣasīr ghoradarśanāḥ</li>
    </ul>
    <ul id="R_5.015.005">
        <li id="R_5.015.005ab">ekākṣīm ekakarṇāṃ ca karṇaprāvaraṇāṃ tathā</li>
        <li id="R_5.015.005cd">akarṇāṃ śaṅkukarṇāṃ ca mastakocchvāsanāsikām</li>
    </ul>
    <ul id="R_5.015.006">
        <li id="R_5.015.006ab">atikāyottamāṅgīṃ ca tanudīrghaśirodharām</li>
        <li id="R_5.015.006cd">dhvastakeśīṃ tathākeśīṃ keśakambaladhāriṇīm</li>
    </ul>
    <ul id="R_5.015.007">
        <li id="R_5.015.007ab">lambakarṇalalāṭāṃ ca lambodarapayodharām</li>
        <li id="R_5.015.007cd">lambauṣṭhīṃ cibukauṣṭhīṃ ca lambāsyāṃ lambajānukām</li>
    </ul>
    <ul id="R_5.015.008">
        <li id="R_5.015.008ab">hrasvāṃ dīrghāṃ ca kubjāṃ ca vikaṭāṃ vāmanāṃ tathā</li>
        <li id="R_5.015.008cd">karālāṃ bhugnavastrāṃ ca piṅgākṣīṃ vikṛtānanām</li>
    </ul>
    <ul id="R_5.015.009">
        <li id="R_5.015.009ab">vikṛtāḥ piṅgalāḥ kālīḥ krodhanāḥ kalahapriyāḥ</li>
        <li id="R_5.015.009cd">kālāyasamahāśūlakūṭamudgaradhāriṇīḥ</li>
    </ul>
    <ul id="R_5.015.010">
        <li id="R_5.015.010ab">varāhamṛgaśārdūlamahiṣājaśivā mukhāḥ</li>
        <li id="R_5.015.010cd">gajoṣṭrahayapādāś ca nikhātaśiraso 'parāḥ</li>
    </ul>
    <ul id="R_5.015.011">
        <li id="R_5.015.011ab">ekahastaikapādāś ca kharakarṇyaśvakarṇikāḥ</li>
        <li id="R_5.015.011cd">gokarṇīr hastikarṇīś ca harikarṇīs tathāparāḥ</li>
    </ul>
    <ul id="R_5.015.012">
        <li id="R_5.015.012ab">anāsā atināsāś ca tiryan nāsā vināsikāḥ</li>
        <li id="R_5.015.012cd">gajasaṃnibhanāsāś ca lalāṭocchvāsanāsikāḥ</li>
    </ul>
    <ul id="R_5.015.013">
        <li id="R_5.015.013ab">hastipādā mahāpādā gopādāḥ pādacūlikāḥ</li>
        <li id="R_5.015.013cd">atimātraśirogrīvā atimātrakucodarīḥ</li>
    </ul>
    <ul id="R_5.015.014">
        <li id="R_5.015.014ab">atimātrāsya netrāś ca dīrghajihvānakhās tathā</li>
        <li id="R_5.015.014cd">ajāmukhīr hastimukhīr gomukhīḥ sūkarīmukhīḥ</li>
    </ul>
    <ul id="R_5.015.015">
        <li id="R_5.015.015ab">hayoṣṭrakharavaktrāś ca rākṣasīr ghoradarśanāḥ</li>
        <li id="R_5.015.015cd">śūlamudgarahastāś ca krodhanāḥ kalahapriyāḥ</li>
    </ul>
    <ul id="R_5.015.016">
        <li id="R_5.015.016ab">karālā dhūmrakeśīś ca rakṣasīr vikṛtānanāḥ</li>
        <li id="R_5.015.016cd">pibantīḥ satataṃ pānaṃ sadā māṃsasurāpriyāḥ</li>
    </ul>
    <ul id="R_5.015.017">
        <li id="R_5.015.017ab">māṃsaśoṇitadigdhāṅgīr māṃsaśoṇitabhojanāḥ</li>
        <li id="R_5.015.017cd">tā dadarśa kapiśreṣṭho romaharṣaṇadarśanāḥ</li>
    </ul>
    <ul id="R_5.015.018">
        <li id="R_5.015.018ab">skandhavantam upāsīnāḥ parivārya vanaspatim</li>
        <li id="R_5.015.018cd">tasyādhastāc ca tāṃ devīṃ rājaputrīm aninditām</li>
    </ul>
    <ul id="R_5.015.019">
        <li id="R_5.015.019ab">lakṣayām āsa lakṣmīvān hanūmāñ janakātmajām</li>
        <li id="R_5.015.019cd">niṣprabhāṃ śokasaṃtaptāṃ malasaṃkulamūrdhajām</li>
    </ul>
    <ul id="R_5.015.020">
        <li id="R_5.015.020ab">kṣīṇapuṇyāṃ cyutāṃ bhūmau tārāṃ nipatitām iva</li>
        <li id="R_5.015.020cd">cāritrya vyapadeśāḍhyāṃ bhartṛdarśanadurgatām</li>
    </ul>
    <ul id="R_5.015.021">
        <li id="R_5.015.021ab">bhūṣaṇair uttamair hīnāṃ bhartṛvātsalyabhūṣitām</li>
        <li id="R_5.015.021cd">rākṣasādhipasaṃruddhāṃ bandhubhiś ca vinākṛtām</li>
    </ul>
    <ul id="R_5.015.022">
        <li id="R_5.015.022ab">viyūthāṃ siṃhasaṃruddhāṃ baddhāṃ gajavadhūm iva</li>
        <li id="R_5.015.022cd">candralekhāṃ payodānte śāradābhrair ivāvṛtām</li>
    </ul>
    <ul id="R_5.015.023">
        <li id="R_5.015.023ab">kliṣṭarūpām asaṃsparśād ayuktām iva vallakīm</li>
        <li id="R_5.015.023cd">sītāṃ bhartṛhite yuktām ayuktāṃ rakṣasāṃ vaśe</li>
    </ul>
    <ul id="R_5.015.024">
        <li id="R_5.015.024ab">aśokavanikāmadhye śokasāgaram āplutām</li>
        <li id="R_5.015.024cd">tābhiḥ parivṛtāṃ tatra sagrahām iva rohiṇīm</li>
        <li id="R_5.015.024cd">dadarśa hanumān devīṃ latām akusumām iva</li>
    </ul>
    <ul id="R_5.015.025">
        <li id="R_5.015.025ab">sā malena ca digdhāṅgī vapuṣā cāpy alaṃkṛtā</li>
        <li id="R_5.015.025cd">mṛṇālī paṅkadigdheva vibhāti ca na bhāti ca</li>
    </ul>
    <ul id="R_5.015.026">
        <li id="R_5.015.026ab">malinena tu vastreṇa parikliṣṭena bhāminīm</li>
        <li id="R_5.015.026cd">saṃvṛtāṃ mṛgaśāvākṣīṃ dadarśa hanumān kapiḥ</li>
    </ul>
    <ul id="R_5.015.027">
        <li id="R_5.015.027ab">tāṃ devīṃ dīnavadanām adīnāṃ bhartṛtejasā</li>
        <li id="R_5.015.027cd">rakṣitāṃ svena śīlena sītām asitalocanām</li>
    </ul>
    <ul id="R_5.015.028">
        <li id="R_5.015.028ab">tāṃ dṛṣṭvā hanumān sītāṃ mṛgaśāvanibhekṣaṇām</li>
        <li id="R_5.015.028cd">mṛgakanyām iva trastāṃ vīkṣamāṇāṃ samantataḥ</li>
    </ul>
    <ul id="R_5.015.029">
        <li id="R_5.015.029ab">dahantīm iva niḥśvāsair vṛkṣān pallavadhāriṇaḥ</li>
        <li id="R_5.015.029cd">saṃghātam iva śokānāṃ duḥkhasyormim ivotthitām</li>
    </ul>
    <ul id="R_5.015.030">
        <li id="R_5.015.030ab">tāṃ kṣāmāṃ suvibhaktāṅgīṃ vinābharaṇaśobhinīm</li>
    </ul>
    <ul id="R_5.015.031">
        <li id="R_5.015.031cd">praharṣam atulaṃ lebhe mārutiḥ prekṣya maithilīm</li>
        <li id="R_5.015.031ab">harṣajāni ca so 'śrūṇi tāṃ dṛṣṭvā madirekṣaṇām</li>
        <li id="R_5.015.031cd">mumoca hanumāṃs tatra namaś cakre ca rāghavam</li>
    </ul>
    <ul id="R_5.015.032">
        <li id="R_5.015.032ab">namaskṛtvā ca rāmāya lakṣmaṇāya ca vīryavān</li>
        <li id="R_5.015.032cd">sītādarśanasaṃhṛṣṭo hanūmān saṃvṛto 'bhavat</li>
    </ul>
    <ul id="R_5.016.001">
        <li id="R_5.016.001ab">tathā viprekṣamāṇasya vanaṃ puṣpitapādapam</li>
        <li id="R_5.016.001cd">vicinvataś ca vaidehīṃ kiṃ cic cheṣā niśābhavat</li>
    </ul>
    <ul id="R_5.016.002">
        <li id="R_5.016.002ab">ṣaḍaṅgavedaviduṣāṃ kratupravarayājinām</li>
        <li id="R_5.016.002cd">śuśrāva brahmaghoṣāṃś ca virātre brahmarakṣasām</li>
    </ul>
    <ul id="R_5.016.003">
        <li id="R_5.016.003ab">atha maṅgalavāditraiḥ śabdaiḥ śrotramanoharaiḥ</li>
        <li id="R_5.016.003cd">prābodhyata mahābāhur daśagrīvo mahābalaḥ</li>
    </ul>
    <ul id="R_5.016.004">
        <li id="R_5.016.004ab">vibudhya tu yathākālaṃ rākṣasendraḥ pratāvapān</li>
        <li id="R_5.016.004cd">srastamālyāmbaradharo vaidehīm anvacintayat</li>
    </ul>
    <ul id="R_5.016.005">
        <li id="R_5.016.005ab">bhṛśaṃ niyuktas tasyāṃ ca madanena madotkaṭaḥ</li>
        <li id="R_5.016.005cd">na sa taṃ rākṣasaḥ kāmaṃ śaśākātmani gūhitum</li>
    </ul>
    <ul id="R_5.016.006">
        <li id="R_5.016.006ab">sa sarvābharaṇair yukto bibhrac chriyam anuttamām</li>
        <li id="R_5.016.006cd">tāṃ nagair vividhair juṣṭāṃ sarvapuṣpaphalopagaiḥ</li>
    </ul>
    <ul id="R_5.016.007">
        <li id="R_5.016.007ab">vṛtāṃ puṣkariṇībhiś ca nānāpuṣpopaśobhitām</li>
        <li id="R_5.016.007cd">sadāmadaiś ca vihagair vicitrāṃ paramādbhutām</li>
    </ul>
    <ul id="R_5.016.008">
        <li id="R_5.016.008ab">īhāmṛgaiś ca vividhaiś vṛtāṃ dṛṣṭimanoharaiḥ</li>
        <li id="R_5.016.008cd">vīthīḥ saṃprekṣamāṇaś ca maṇikāñcanatoraṇāḥ</li>
    </ul>
    <ul id="R_5.016.009">
        <li id="R_5.016.009ab">nānāmṛgagaṇākīrṇāṃ phalaiḥ prapatitair vṛtām</li>
        <li id="R_5.016.009cd">aśokavanikām eva prāviśat saṃtatadrumām</li>
    </ul>
    <ul id="R_5.016.010">
        <li id="R_5.016.010ab">aṅganāśatamātraṃ tu taṃ vrajantam anuvrajat</li>
        <li id="R_5.016.010cd">mahendram iva paulastyaṃ devagandharvayoṣitaḥ</li>
    </ul>
    <ul id="R_5.016.011">
        <li id="R_5.016.011ab">dīpikāḥ kāñcanīḥ kāś cij jagṛhus tatra yoṣitaḥ</li>
        <li id="R_5.016.011cd">bālavyajanahastāś ca tālavṛntāni cāparāḥ</li>
    </ul>
    <ul id="R_5.016.012">
        <li id="R_5.016.012ab">kāñcanair api bhṛṅgārair jahruḥ salilam agrataḥ</li>
        <li id="R_5.016.012cd">maṇḍalāgrān asīṃś caiva gṛhyānyāḥ pṛṣṭhato yayuḥ</li>
    </ul>
    <ul id="R_5.016.013">
        <li id="R_5.016.013ab">kā cid ratnamayīṃ pātrīṃ pūrṇāṃ pānasya bhāminī</li>
        <li id="R_5.016.013cd">dakṣiṇā dakṣiṇenaiva tadā jagrāha pāṇinā</li>
    </ul>
    <ul id="R_5.016.014">
        <li id="R_5.016.014ab">rājahaṃsapratīkāśaṃ chatraṃ pūrṇaśaśiprabham</li>
        <li id="R_5.016.014cd">sauvarṇadaṇḍam aparā gṛhītvā pṛṣṭhato yayau</li>
    </ul>
    <ul id="R_5.016.015">
        <li id="R_5.016.015ab">nidrāmadaparītākṣyo rāvaṇasyottamastriyaḥ</li>
        <li id="R_5.016.015cd">anujagmuḥ patiṃ vīraṃ ghanaṃ vidyullatā iva</li>
    </ul>
    <ul id="R_5.016.016">
        <li id="R_5.016.016ab">tataḥ kāñcīninādaṃ ca nūpurāṇāṃ ca niḥsvanam</li>
        <li id="R_5.016.016cd">śuśrāva paramastrīṇāṃ sa kapir mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.016.017">
        <li id="R_5.016.017ab">taṃ cāpratimakarmāṇam acintyabalapauruṣam</li>
        <li id="R_5.016.017cd">dvāradeśam anuprāptaṃ dadarśa hanumān kapiḥ</li>
    </ul>
    <ul id="R_5.016.018">
        <li id="R_5.016.018ab">dīpikābhir anekābhiḥ samantād avabhāsitam</li>
        <li id="R_5.016.018cd">gandhatailāvasiktābhir dhriyamāṇābhir agrataḥ</li>
    </ul>
    <ul id="R_5.016.019">
        <li id="R_5.016.019ab">kāmadarpamadair yuktaṃ jihmatāmrāyatekṣaṇam</li>
        <li id="R_5.016.019cd">samakṣam iva kandarpam apaviddha śarāsanam</li>
    </ul>
    <ul id="R_5.016.020">
        <li id="R_5.016.020ab">mathitāmṛtaphenābham arajo vastram uttamam</li>
        <li id="R_5.016.020cd">salīlam anukarṣantaṃ vimuktaṃ saktam aṅgade</li>
    </ul>
    <ul id="R_5.016.021">
        <li id="R_5.016.021ab">taṃ patraviṭape līnaḥ patrapuṣpaghanāvṛtaḥ</li>
        <li id="R_5.016.021cd">samīpam upasaṃkrāntaṃ nidhyātum upacakrame</li>
    </ul>
    <ul id="R_5.016.022">
        <li id="R_5.016.022ab">avekṣamāṇaś ca tato dadarśa kapikuñjaraḥ</li>
        <li id="R_5.016.022cd">rūpayauvanasaṃpannā rāvaṇasya varastriyaḥ</li>
    </ul>
    <ul id="R_5.016.023">
        <li id="R_5.016.023ab">tābhiḥ parivṛto rājā surūpābhir mahāyaśāḥ</li>
        <li id="R_5.016.023cd">tanmṛgadvijasaṃghuṣṭaṃ praviṣṭaḥ pramadāvanam</li>
    </ul>
    <ul id="R_5.016.024">
        <li id="R_5.016.024ab">kṣībo vicitrābharaṇaḥ śaṅkukarṇo mahābalaḥ</li>
        <li id="R_5.016.024cd">tena viśravasaḥ putraḥ sa dṛṣṭo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_5.016.025">
        <li id="R_5.016.025ab">vṛtaḥ paramanārībhis tārābhir iva candramāḥ</li>
        <li id="R_5.016.025cd">taṃ dadarśa mahātejās tejovantaṃ mahākapiḥ</li>
    </ul>
    <ul id="R_5.016.026">
        <li id="R_5.016.026ab">rāvaṇo 'yaṃ mahābāhur iti saṃcintya vānaraḥ</li>
        <li id="R_5.016.026cd">avapluto mahātejā hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.016.027">
        <li id="R_5.016.027ab">sa tathāpy ugratejāḥ san nirdhūtas tasya tejasā</li>
        <li id="R_5.016.027cd">patraguhyāntare sakto hanūmān saṃvṛto 'bhavat</li>
    </ul>
    <ul id="R_5.016.028">
        <li id="R_5.016.028ab">sa tām asitakeśāntāṃ suśroṇīṃ saṃhatastanīm</li>
        <li id="R_5.016.028cd">didṛkṣur asitāpāṅgīm upāvartata rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.017.001">
        <li id="R_5.017.001ab">tasminn eva tataḥ kāle rājaputrī tv aninditā</li>
        <li id="R_5.017.001cd">rūpayauvanasaṃpannaṃ bhūṣaṇottamabhūṣitam</li>
    </ul>
    <ul id="R_5.017.002">
        <li id="R_5.017.002ab">tato dṛṣṭvaiva vaidehī rāvaṇaṃ rākṣasādhipam</li>
        <li id="R_5.017.002cd">prāvepata varārohā pravāte kadalī yathā</li>
    </ul>
    <ul id="R_5.017.003">
        <li id="R_5.017.003ab">ūrubhyām udaraṃ chādya bāhubhyāṃ ca payodharau</li>
        <li id="R_5.017.003cd">upaviṣṭā viśālākṣī rudantī varavarṇinī</li>
    </ul>
    <ul id="R_5.017.004">
        <li id="R_5.017.004ab">daśagrīvas tu vaidehīṃ rakṣitāṃ rākṣasīgaṇaiḥ</li>
        <li id="R_5.017.004cd">dadarśa dīnāṃ duḥkhārtaṃ nāvaṃ sannām ivārṇave</li>
    </ul>
    <ul id="R_5.017.005">
        <li id="R_5.017.005ab">asaṃvṛtāyām āsīnāṃ dharaṇyāṃ saṃśitavratām</li>
        <li id="R_5.017.005cd">chinnāṃ prapatitāṃ bhūmau śākhām iva vanaspateḥ</li>
        <li id="R_5.017.005ef">malamaṇḍanadigdhāṅgīṃ maṇḍanārhām amaṇḍitām</li>
    </ul>
    <ul id="R_5.017.006">
        <li id="R_5.017.006ab">samīpaṃ rājasiṃhasya rāmasya viditātmanaḥ</li>
        <li id="R_5.017.006cd">saṃkalpahayasaṃyuktair yāntīm iva manorathaiḥ</li>
    </ul>
    <ul id="R_5.017.007">
        <li id="R_5.017.007ab">śuṣyantīṃ rudatīm ekāṃ dhyānaśokaparāyaṇām</li>
        <li id="R_5.017.007cd">duḥkhasyāntam apaśyantīṃ rāmāṃ rāmam anuvratām</li>
    </ul>
    <ul id="R_5.017.008">
        <li id="R_5.017.008ab">veṣṭamānām athāviṣṭāṃ pannagendravadhūm iva</li>
        <li id="R_5.017.008cd">dhūpyamānāṃ graheṇeva rohiṇīṃ dhūmaketunā</li>
    </ul>
    <ul id="R_5.017.009">
        <li id="R_5.017.009ab">vṛttaśīle kule jātām ācāravati dhārmike</li>
        <li id="R_5.017.009cd">punaḥ saṃskāram āpannāṃ jātam iva ca duṣkule</li>
    </ul>
    <ul id="R_5.017.010">
        <li id="R_5.017.010ab">sannām iva mahākīrtiṃ śraddhām iva vimānitām</li>
        <li id="R_5.017.010cd">prajñām iva parikṣīṇām āśāṃ pratihatām iva</li>
    </ul>
    <ul id="R_5.017.011">
        <li id="R_5.017.011ab">āyatīm iva vidhvastām ājñāṃ pratihatām iva</li>
        <li id="R_5.017.011cd">dīptām iva diśaṃ kāle pūjām apahṛtām iva</li>
    </ul>
    <ul id="R_5.017.012">
        <li id="R_5.017.012ab">padminīm iva vidhvastāṃ hataśūrāṃ camūm iva</li>
        <li id="R_5.017.012cd">prabhām iva tapodhvastām upakṣīṇām ivāpagām</li>
    </ul>
    <ul id="R_5.017.013">
        <li id="R_5.017.013ab">vedīm iva parāmṛṣṭāṃ śāntām agniśikhām iva</li>
        <li id="R_5.017.013cd">paurṇamāsīm iva niśāṃ rāhugrastendumaṇḍalām</li>
    </ul>
    <ul id="R_5.017.014">
        <li id="R_5.017.014ab">utkṛṣṭaparṇakamalāṃ vitrāsitavihaṃgamām</li>
        <li id="R_5.017.014cd">hastihastaparāmṛṣṭām ākulāṃ padminīm iva</li>
    </ul>
    <ul id="R_5.017.015">
        <li id="R_5.017.015ab">patiśokāturāṃ śuṣkāṃ nadīṃ visrāvitām iva</li>
        <li id="R_5.017.015cd">parayā mṛjayā hīnāṃ kṛṣṇapakṣe niśām iva</li>
    </ul>
    <ul id="R_5.017.016">
        <li id="R_5.017.016ab">sukumārīṃ sujātāṅgīṃ ratnagarbhagṛhocitām</li>
        <li id="R_5.017.016cd">tapyamānām ivoṣṇena mṛṇālīm aciroddhṛtām</li>
    </ul>
    <ul id="R_5.017.017">
        <li id="R_5.017.017ab">gṛhītāmālitāṃ stambhe yūthapena vinākṛtām</li>
        <li id="R_5.017.017cd">niḥśvasantīṃ suduḥkhārtāṃ gajarājavadhūm iva</li>
    </ul>
    <ul id="R_5.017.018">
        <li id="R_5.017.018ab">ekayā dīrghayā veṇyā śobhamānām ayatnataḥ</li>
        <li id="R_5.017.018cd">nīlayā nīradāpāye vanarājyā mahīm iva</li>
    </ul>
    <ul id="R_5.017.019">
        <li id="R_5.017.019ab">upavāsena śokena dhyānena ca bhayena ca</li>
        <li id="R_5.017.019cd">parikṣīṇāṃ kṛśāṃ dīnām alpāhārāṃ tapodhanām</li>
    </ul>
    <ul id="R_5.017.020">
        <li id="R_5.017.020ab">āyācamānāṃ duḥkhārtāṃ prāñjaliṃ devatām iva</li>
        <li id="R_5.017.020cd">bhāvena raghumukhyasya daśagrīvaparābhavam</li>
    </ul>
    <ul id="R_5.017.021">
        <li id="R_5.017.021ab">
            <seg type="pāda" n="a">samīkṣamāṇāṃ rudatīm aninditāṃ</seg>
            <seg type="pāda" n="b">supakṣmatāmrāyataśuklalocanām</seg>
        </li>
        <li id="R_5.017.021cd">
            <seg type="pāda" n="c">anuvratāṃ rāmam atīva maithilīṃ</seg>
            <seg type="pāda" n="d">pralobhayām āsa vadhāya rāvaṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.018.001">
        <li id="R_5.018.001ab">sa tāṃ parivṛtāṃ dīnāṃ nirānandāṃ tapasvinīm</li>
        <li id="R_5.018.001cd">sākārair madhurair vākyair nyadarśayata rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.018.002">
        <li id="R_5.018.002ab">māṃ dṛṣṭvā nāganāsorugūhamānā stanodaram</li>
        <li id="R_5.018.002cd">adarśanam ivātmānaṃ bhayān netuṃ tvam icchasi</li>
    </ul>
    <ul id="R_5.018.003">
        <li id="R_5.018.003ab">kāmaye tvāṃ viśālākṣi bahumanyasva māṃ priye</li>
        <li id="R_5.018.003cd">sarvāṅgaguṇasaṃpanne sarvalokamanohare</li>
    </ul>
    <ul id="R_5.018.004">
        <li id="R_5.018.004ab">neha ke cin manuṣyā vā rākṣasāḥ kāmarūpiṇaḥ</li>
        <li id="R_5.018.004cd">vyapasarpatu te sīte bhayaṃ mattaḥ samutthitam</li>
    </ul>
    <ul id="R_5.018.005">
        <li id="R_5.018.005ab">svadharme rakṣasāṃ bhīru sarvathaiṣa na saṃśayaḥ</li>
        <li id="R_5.018.005cd">gamanaṃ vā parastrīṇāṃ haraṇaṃ saṃpramathya vā</li>
    </ul>
    <ul id="R_5.018.006">
        <li id="R_5.018.006ab">evaṃ caitad akāmāṃ ca na tvāṃ sprakṣyāmi maithili</li>
        <li id="R_5.018.006cd">kāmaṃ kāmaḥ śarīre me yathākāmaṃ pravartatām</li>
    </ul>
    <ul id="R_5.018.007">
        <li id="R_5.018.007ab">devi neha bhayaṃ kāryaṃ mayi viśvasihi priye</li>
        <li id="R_5.018.007cd">praṇayasva ca tattvena maivaṃ bhūḥ śokalālasā</li>
    </ul>
    <ul id="R_5.018.008">
        <li id="R_5.018.008ab">ekaveṇī dharāśayyā dhyānaṃ malinam ambaram</li>
        <li id="R_5.018.008cd">asthāne 'py upavāsaś ca naitāny aupayikāni te</li>
    </ul>
    <ul id="R_5.018.009">
        <li id="R_5.018.009ab">vicitrāṇi ca mālyāni candanāny agarūṇi ca</li>
        <li id="R_5.018.009cd">vividhāni ca vāsāṃsi divyāny ābharaṇāni ca</li>
    </ul>
    <ul id="R_5.018.010">
        <li id="R_5.018.010ab">mahārhāṇi ca pānāni yānāni śayanāni ca</li>
        <li id="R_5.018.010cd">gītaṃ nṛttaṃ ca vādyaṃ ca labha māṃ prāpya maithili</li>
    </ul>
    <ul id="R_5.018.011">
        <li id="R_5.018.011ab">strīratnam asi maivaṃ bhūḥ kuru gātreṣu bhūṣaṇam</li>
        <li id="R_5.018.011cd">māṃ prāpya tu kathaṃ hi syās tvam anarhā suvigrahe</li>
    </ul>
    <ul id="R_5.018.012">
        <li id="R_5.018.012ab">idaṃ te cārusaṃjātaṃ yauvanaṃ vyativartate</li>
        <li id="R_5.018.012cd">yad atītaṃ punar naiti srotaḥ śīghram apām iva</li>
    </ul>
    <ul id="R_5.018.013">
        <li id="R_5.018.013ab">tvāṃ kṛtvoparato manye rūpakartā sa viśvakṛt</li>
        <li id="R_5.018.013cd">na hi rūpopamā tv anyā tavāsti śubhadarśane</li>
    </ul>
    <ul id="R_5.018.014">
        <li id="R_5.018.014ab">tvāṃ samāsādya vaidehi rūpayauvanaśālinīm</li>
        <li id="R_5.018.014cd">kaḥ pumān ativarteta sākṣād api pitāmahaḥ</li>
    </ul>
    <ul id="R_5.018.015">
        <li id="R_5.018.015ab">yad yat paśyāmi te gātraṃ śītāṃśusadṛśānane</li>
        <li id="R_5.018.015cd">tasmiṃs tasmin pṛthuśroṇi cakṣur mama nibadhyate</li>
    </ul>
    <ul id="R_5.018.016">
        <li id="R_5.018.016ab">bhava maithili bhāryā me moham enaṃ visarjaya</li>
        <li id="R_5.018.016cd">bahvīnām uttamastrīṇāṃ mamāgramahiṣī bhava</li>
    </ul>
    <ul id="R_5.018.017">
        <li id="R_5.018.017ab">lokebhyo yāni ratnāni saṃpramathyāhṛtāni me</li>
        <li id="R_5.018.017cd">tāni te bhīru sarvāṇi rājyaṃ caitad ahaṃ ca te</li>
    </ul>
    <ul id="R_5.018.018">
        <li id="R_5.018.018ab">vijitya pṛthivīṃ sarvāṃ nānānagaramālinīm</li>
        <li id="R_5.018.018cd">janakāya pradāsyāmi tava hetor vilāsini</li>
    </ul>
    <ul id="R_5.018.019">
        <li id="R_5.018.019ab">neha paśyāmi loke 'nyaṃ yo me pratibalo bhavet</li>
        <li id="R_5.018.019cd">paśya me sumahad vīryam apratidvandvam āhave</li>
    </ul>
    <ul id="R_5.018.020">
        <li id="R_5.018.020ab">asakṛt saṃyuge bhagnā mayā vimṛditadhvajāḥ</li>
        <li id="R_5.018.020cd">aśaktāḥ pratyanīkeṣu sthātuṃ mama surāsurāḥ</li>
    </ul>
    <ul id="R_5.018.021">
        <li id="R_5.018.021ab">iccha māṃ kriyatām adya pratikarma tavottamam</li>
        <li id="R_5.018.021cd">saprabhāṇy avasajjantāṃ tavāṅge bhūṣaṇāni ca</li>
        <li id="R_5.018.021ef">sādhu paśyāmi te rūpaṃ saṃyuktaṃ pratikarmaṇā</li>
    </ul>
    <ul id="R_5.018.022">
        <li id="R_5.018.022ab">pratikarmābhisaṃyuktā dākṣiṇyena varānane</li>
        <li id="R_5.018.022cd">bhuṅkṣva bhogān yathākāmaṃ piba bhīru ramasva ca</li>
        <li id="R_5.018.022ef">yatheṣṭaṃ ca prayaccha tvaṃ pṛthivīṃ vā dhanāni ca</li>
    </ul>
    <ul id="R_5.018.023">
        <li id="R_5.018.023ab">lalasva mayi visrabdhā dhṛṣṭam ājñāpayasva ca</li>
        <li id="R_5.018.023cd">matprabhāvāl lalantyāś ca lalantāṃ bāndhavās tava</li>
    </ul>
    <ul id="R_5.018.024">
        <li id="R_5.018.024ab">ṛddhiṃ mamānupaśya tvaṃ śriyaṃ bhadre yaśaś ca me</li>
        <li id="R_5.018.024cd">kiṃ kariṣyasi rāmeṇa subhage cīravāsasā</li>
    </ul>
    <ul id="R_5.018.025">
        <li id="R_5.018.025ab">nikṣiptavijayo rāmo gataśrīr vanagocaraḥ</li>
        <li id="R_5.018.025cd">vratī sthaṇḍilaśāyī ca śaṅke jīvati vā na vā</li>
    </ul>
    <ul id="R_5.018.026">
        <li id="R_5.018.026ab">na hi vaidehi rāmas tvāṃ draṣṭuṃ vāpy upalapsyate</li>
        <li id="R_5.018.026cd">puro balākair asitair meghair jyotsnām ivāvṛtām</li>
    </ul>
    <ul id="R_5.018.027">
        <li id="R_5.018.027ab">na cāpi mama hastāt tvāṃ prāptum arhati rāghavaḥ</li>
        <li id="R_5.018.027cd">hiraṇyakaśipuḥ kīrtim indrahastagatām iva</li>
    </ul>
    <ul id="R_5.018.028">
        <li id="R_5.018.028ab">cārusmite cārudati cārunetre vilāsini</li>
        <li id="R_5.018.028cd">mano harasi me bhīru suparṇaḥ pannagaṃ yathā</li>
    </ul>
    <ul id="R_5.018.029">
        <li id="R_5.018.029ab">kliṣṭakauśeyavasanāṃ tanvīm apy analaṃkṛtām</li>
        <li id="R_5.018.029cd">tāṃ dṛṣṭvā sveṣu dāreṣu ratiṃ nopalabhāmy aham</li>
    </ul>
    <ul id="R_5.018.030">
        <li id="R_5.018.030ab">antaḥpuranivāsinyaḥ striyaḥ sarvaguṇānvitāḥ</li>
        <li id="R_5.018.030cd">yāvantyo mama sarvāsām aiśvaryaṃ kuru jānaki</li>
    </ul>
    <ul id="R_5.018.031">
        <li id="R_5.018.031ab">mama hy asitakeśānte trailokyapravarāḥ striyaḥ</li>
        <li id="R_5.018.031cd">tās tvāṃ paricariṣyanti śriyam apsaraso yathā</li>
    </ul>
    <ul id="R_5.018.032">
        <li id="R_5.018.032ab">yāni vaiśravaṇe subhru ratnāni ca dhanāni ca</li>
        <li id="R_5.018.032cd">tāni lokāṃś ca suśroṇi māṃ ca bhuṅkṣva yathāsukham</li>
    </ul>
    <ul id="R_5.018.033">
        <li id="R_5.018.033ab">na rāmas tapasā devi na balena na vikramaiḥ</li>
        <li id="R_5.018.033cd">na dhanena mayā tulyas tejasā yaśasāpi vā</li>
    </ul>
    <ul id="R_5.018.034">
        <li id="R_5.018.034ab">
            <seg type="pāda" n="a">piba vihara ramasva bhuṅkṣva bhogān</seg>
            <seg type="pāda" n="b">dhananicayaṃ pradiśāmi medinīṃ ca</seg>
        </li>
        <li id="R_5.018.034cd">
            <seg type="pāda" n="c">mayi lala lalane yathāsukhaṃ tvaṃ</seg>
            <seg type="pāda" n="d">tvayi ca sametya lalantu bāndhavās te</seg>
        </li>
    </ul>
    <ul id="R_5.018.035">
        <li id="R_5.018.035ab">
            <seg type="pāda" n="a">kusumitatarujālasaṃtatāni</seg>
            <seg type="pāda" n="b">bhramarayutāni samudratīrajāni</seg>
        </li>
        <li id="R_5.018.035cd">
            <seg type="pāda" n="c">kanakavimalahārabhūṣitāṅgī</seg>
            <seg type="pāda" n="d">vihara mayā saha bhīru kānanāni</seg>
        </li>
    </ul>
    <ul id="R_5.019.001">
        <li id="R_5.019.001ab">tasya tad vacanaṃ śrutvā sītā raudrasya rakṣasaḥ</li>
        <li id="R_5.019.001cd">ārtā dīnasvarā dīnaṃ pratyuvāca śanair vacaḥ</li>
    </ul>
    <ul id="R_5.019.002">
        <li id="R_5.019.002ab">duḥkhārtā rudatī sītā vepamānā tapasvinī</li>
        <li id="R_5.019.002cd">cintayantī varārohā patim eva pativratā</li>
    </ul>
    <ul id="R_5.019.003">
        <li id="R_5.019.003ab">tṛṇam antarataḥ kṛtvā pratyuvāca śucismitā</li>
        <li id="R_5.019.003cd">nivartaya mano mattaḥ svajane kriyatāṃ manaḥ</li>
    </ul>
    <ul id="R_5.019.004">
        <li id="R_5.019.004ab">na māṃ prārthayituṃ yuktas tvaṃ siddhim iva pāpakṛt</li>
        <li id="R_5.019.004cd">akāryaṃ na mayā kāryam ekapatnyā vigarhitam</li>
        <li id="R_5.019.004ef">kulaṃ saṃprāptayā puṇyaṃ kule mahati jātayā</li>
    </ul>
    <ul id="R_5.019.005">
        <li id="R_5.019.005ab">evam uktvā tu vaidehī rāvaṇaṃ taṃ yaśasvinī</li>
        <li id="R_5.019.005cd">rākṣasaṃ pṛṣṭhataḥ kṛtvā bhūyo vacanam abravīt</li>
    </ul>
    <ul id="R_5.019.006">
        <li id="R_5.019.006ab">nāham aupayikī bhāryā parabhāryā satī tava</li>
        <li id="R_5.019.006cd">sādhu dharmam avekṣasva sādhu sādhuvrataṃ cara</li>
    </ul>
    <ul id="R_5.019.007">
        <li id="R_5.019.007ab">yathā tava tathānyeṣāṃ rakṣyā dārā niśācara</li>
        <li id="R_5.019.007cd">ātmānam upamāṃ kṛtvā sveṣu dāreṣu ramyatām</li>
    </ul>
    <ul id="R_5.019.008">
        <li id="R_5.019.008ab">atuṣṭaṃ sveṣu dāreṣu capalaṃ calitendriyam</li>
        <li id="R_5.019.008cd">nayanti nikṛtiprajñāṃ paradārāḥ parābhavam</li>
    </ul>
    <ul id="R_5.019.009">
        <li id="R_5.019.009ab">iha santo na vā santi sato vā nānuvartase</li>
        <li id="R_5.019.009cd">vaco mithyā praṇītātmā pathyam uktaṃ vicakṣaṇaiḥ</li>
    </ul>
    <ul id="R_5.019.010">
        <li id="R_5.019.010ab">akṛtātmānam āsādya rājānam anaye ratam</li>
        <li id="R_5.019.010cd">samṛddhāni vinaśyanti rāṣṭrāṇi nagarāṇi ca</li>
    </ul>
    <ul id="R_5.019.011">
        <li id="R_5.019.011ab">tatheyaṃ tvāṃ samāsādya laṅkā ratnaugha saṃkulā</li>
        <li id="R_5.019.011cd">aparādhāt tavaikasya nacirād vinaśiṣyati</li>
    </ul>
    <ul id="R_5.019.012">
        <li id="R_5.019.012ab">svakṛtair hanyamānasya rāvaṇādīrghadarśinaḥ</li>
        <li id="R_5.019.012cd">abhinandanti bhūtāni vināśe pāpakarmaṇaḥ</li>
    </ul>
    <ul id="R_5.019.013">
        <li id="R_5.019.013ab">evaṃ tvāṃ pāpakarmāṇaṃ vakṣyanti nikṛtā janāḥ</li>
        <li id="R_5.019.013cd">diṣṭyaitad vyasanaṃ prāpto raudra ity eva harṣitāḥ</li>
    </ul>
    <ul id="R_5.019.014">
        <li id="R_5.019.014ab">śakyā lobhayituṃ nāham aiśvaryeṇa dhanena vā</li>
        <li id="R_5.019.014cd">ananyā rāghaveṇāhaṃ bhāskareṇa prabhā yathā</li>
    </ul>
    <ul id="R_5.019.015">
        <li id="R_5.019.015ab">upadhāya bhujaṃ tasya lokanāthasya satkṛtam</li>
        <li id="R_5.019.015cd">kathaṃ nāmopadhāsyāmi bhujam anyasya kasya cit</li>
    </ul>
    <ul id="R_5.019.016">
        <li id="R_5.019.016ab">aham aupayikī bhāryā tasyaiva vasudhāpateḥ</li>
        <li id="R_5.019.016cd">vratasnātasya viprasya vidyeva viditātmanaḥ</li>
    </ul>
    <ul id="R_5.019.017">
        <li id="R_5.019.017ab">sādhu rāvaṇa rāmeṇa māṃ samānaya duḥkhitām</li>
        <li id="R_5.019.017cd">vane vāśitayā sārdhaṃ kareṇveva gajādhipam</li>
    </ul>
    <ul id="R_5.019.018">
        <li id="R_5.019.018ab">mitram aupayikaṃ kartuṃ rāmaḥ sthānaṃ parīpsatā</li>
        <li id="R_5.019.018cd">vadhaṃ cānicchatā ghoraṃ tvayāsau puruṣarṣabhaḥ</li>
    </ul>
    <ul id="R_5.019.019">
        <li id="R_5.019.019ab">varjayed vajram utsṛṣṭaṃ varjayed antakaś ciram</li>
        <li id="R_5.019.019cd">tvadvidhaṃ na tu saṃkruddho lokanāthaḥ sa rāghavaḥ</li>
    </ul>
    <ul id="R_5.019.020">
        <li id="R_5.019.020ab">rāmasya dhanuṣaḥ śabdaṃ śroṣyasi tvaṃ mahāsvanam</li>
        <li id="R_5.019.020cd">śatakratuvisṛṣṭasya nirghoṣam aśaner iva</li>
    </ul>
    <ul id="R_5.019.021">
        <li id="R_5.019.021ab">iha śīghraṃ suparvāṇo jvalitāsyā ivoragāḥ</li>
        <li id="R_5.019.021cd">iṣavo nipatiṣyanti rāmalakṣmaṇalakṣaṇāḥ</li>
    </ul>
    <ul id="R_5.019.022">
        <li id="R_5.019.022ab">rakṣāṃsi parinighnantaḥ puryām asyāṃ samantataḥ</li>
        <li id="R_5.019.022cd">asaṃpātaṃ kariṣyanti patantaḥ kaṅkavāsasaḥ</li>
    </ul>
    <ul id="R_5.019.023">
        <li id="R_5.019.023ab">rākṣasendramahāsarpān sa rāmagaruḍo mahān</li>
        <li id="R_5.019.023cd">uddhariṣyati vegena vainateya ivoragān</li>
    </ul>
    <ul id="R_5.019.024">
        <li id="R_5.019.024ab">apaneṣyati māṃ bhartā tvattaḥ śīghram ariṃdamaḥ</li>
        <li id="R_5.019.024cd">asurebhyaḥ śriyaṃ dīptāṃ viṣṇus tribhir iva kramaiḥ</li>
    </ul>
    <ul id="R_5.019.025">
        <li id="R_5.019.025ab">janasthāne hatasthāne nihate rakṣasāṃ bale</li>
        <li id="R_5.019.025cd">aśaktena tvayā rakṣaḥ kṛtam etad asādhu vai</li>
    </ul>
    <ul id="R_5.019.026">
        <li id="R_5.019.026ab">āśramaṃ tu tayoḥ śūnyaṃ praviśya narasiṃhayoḥ</li>
        <li id="R_5.019.026cd">gocaraṃ gatayor bhrātror apanītā tvayādhama</li>
    </ul>
    <ul id="R_5.019.027">
        <li id="R_5.019.027ab">na hi gandham upāghrāya rāmalakṣmaṇayos tvayā</li>
        <li id="R_5.019.027cd">śakyaṃ saṃdarśane sthātuṃ śunā śārdūlayor iva</li>
    </ul>
    <ul id="R_5.019.028">
        <li id="R_5.019.028ab">tasya te vigrahe tābhyāṃ yugagrahaṇam asthiram</li>
        <li id="R_5.019.028cd">vṛtrasyevendrabāhubhyāṃ bāhor ekasya nigrahaḥ</li>
    </ul>
    <ul id="R_5.019.029">
        <li id="R_5.019.029ab">kṣipraṃ tava sa nātho me rāmaḥ saumitriṇā saha</li>
        <li id="R_5.019.029cd">toyam alpam ivādityaḥ prāṇān ādāsyate śaraiḥ</li>
    </ul>
    <ul id="R_5.019.030">
        <li id="R_5.019.030ab">
            <seg type="pāda" n="a">giriṃ kuberasya gato 'tha vālayaṃ</seg>
            <seg type="pāda" n="b">sabhāṃ gato vā varuṇasya rājñaḥ</seg>
        </li>
        <li id="R_5.019.030cd">
            <seg type="pāda" n="c">asaṃśayaṃ dāśarather na mokṣyase</seg>
            <seg type="pāda" n="d">mahādrumaḥ kālahato 'śaner iva</seg>
        </li>
    </ul>
    <ul id="R_5.020.001">
        <li id="R_5.020.001ab">sītāyā vacanaṃ śrutvā paruṣaṃ rākṣasādhipaḥ</li>
        <li id="R_5.020.001cd">pratyuvāca tataḥ sītāṃ vipriyaṃ priyadarśanām</li>
    </ul>
    <ul id="R_5.020.002">
        <li id="R_5.020.002ab">yathā yathā sāntvayitā vaśyaḥ strīṇāṃ tathā tathā</li>
        <li id="R_5.020.002cd">yathā yathā priyaṃ vaktā paribhūtas tathā tathā</li>
    </ul>
    <ul id="R_5.020.003">
        <li id="R_5.020.003ab">saṃniyacchati me krodhaṃ tvayi kāmaḥ samutthitaḥ</li>
        <li id="R_5.020.003cd">dravato mārgam āsādya hayān iva susārathiḥ</li>
    </ul>
    <ul id="R_5.020.004">
        <li id="R_5.020.004ab">vāmaḥ kāmo manuṣyāṇāṃ yasmin kila nibadhyate</li>
        <li id="R_5.020.004cd">jane tasmiṃs tv anukrośaḥ snehaś ca kila jāyate</li>
    </ul>
    <ul id="R_5.020.005">
        <li id="R_5.020.005ab">etasmāt kāraṇān na tāṃ ghatayāmi varānane</li>
        <li id="R_5.020.005cd">vadhārhām avamānārhāṃ mithyāpravrajite ratām</li>
    </ul>
    <ul id="R_5.020.006">
        <li id="R_5.020.006ab">paruṣāṇi hi vākyāni yāni yāni bravīṣi mām</li>
        <li id="R_5.020.006cd">teṣu teṣu vadho yuktas tava maithili dāruṇaḥ</li>
    </ul>
    <ul id="R_5.020.007">
        <li id="R_5.020.007ab">evam uktvā tu vaidehīṃ rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_5.020.007cd">krodhasaṃrambhasaṃyuktaḥ sītām uttaram abravīt</li>
    </ul>
    <ul id="R_5.020.008">
        <li id="R_5.020.008ab">dvau māsau rakṣitavyau me yo 'vadhis te mayā kṛtaḥ</li>
        <li id="R_5.020.008cd">tataḥ śayanam āroha mama tvaṃ varavarṇini</li>
    </ul>
    <ul id="R_5.020.009">
        <li id="R_5.020.009ab">dvābhyām ūrdhvaṃ tu māsābhyāṃ bhartāraṃ mām anicchatīm</li>
        <li id="R_5.020.009cd">mama tvāṃ prātarāśārtham ārabhante mahānase</li>
    </ul>
    <ul id="R_5.020.010">
        <li id="R_5.020.010ab">tāṃ tarjyamānāṃ saṃprekṣya rākṣasendreṇa jānakīm</li>
        <li id="R_5.020.010cd">devagandharvakanyās tā viṣedur vipulekṣaṇāḥ</li>
    </ul>
    <ul id="R_5.020.011">
        <li id="R_5.020.011ab">oṣṭhaprakārair aparā netravaktrais tathāparāḥ</li>
        <li id="R_5.020.011cd">sītām āśvāsayām āsus tarjitāṃ tena rakṣasā</li>
    </ul>
    <ul id="R_5.020.012">
        <li id="R_5.020.012ab">tābhir āśvāsitā sītā rāvaṇaṃ rākṣasādhipam</li>
        <li id="R_5.020.012cd">uvācātmahitaṃ vākyaṃ vṛttaśauṇḍīryagarvitam</li>
    </ul>
    <ul id="R_5.020.013">
        <li id="R_5.020.013ab">nūnaṃ na te janaḥ kaś cid asin niḥśreyase sthitaḥ</li>
        <li id="R_5.020.013cd">nivārayati yo na tvāṃ karmaṇo 'smād vigarhitāt</li>
    </ul>
    <ul id="R_5.020.014">
        <li id="R_5.020.014ab">māṃ hi dharmātmanaḥ patnīṃ śacīm iva śacīpateḥ</li>
        <li id="R_5.020.014cd">tvadanyas triṣu lokeṣu prārthayen manasāpi kaḥ</li>
    </ul>
    <ul id="R_5.020.015">
        <li id="R_5.020.015ab">rākṣasādhama rāmasya bhāryām amitatejasaḥ</li>
        <li id="R_5.020.015cd">uktavān asi yat pāpaṃ kva gatas tasya mokṣyase</li>
    </ul>
    <ul id="R_5.020.016">
        <li id="R_5.020.016ab">yathā dṛptaś ca mātaṅgaḥ śaśaś ca sahitau vane</li>
        <li id="R_5.020.016cd">tathā dviradavad rāmas tvaṃ nīca śaśavat smṛtaḥ</li>
    </ul>
    <ul id="R_5.020.017">
        <li id="R_5.020.017ab">sa tvam ikṣvākunāthaṃ vai kṣipann iha na lajjase</li>
        <li id="R_5.020.017cd">cakṣuṣo viṣayaṃ tasya na tāvad upagacchasi</li>
    </ul>
    <ul id="R_5.020.018">
        <li id="R_5.020.018ab">ime te nayane krūre virūpe kṛṣṇapiṅgale</li>
        <li id="R_5.020.018cd">kṣitau na patite kasmān mām anāryanirīkṣitaḥ</li>
    </ul>
    <ul id="R_5.020.019">
        <li id="R_5.020.019ab">tasya dharmātmanaḥ patnīṃ snuṣāṃ daśarathasya ca</li>
        <li id="R_5.020.019cd">kathaṃ vyāharato māṃ te na jihvā pāpa śīryate</li>
    </ul>
    <ul id="R_5.020.020">
        <li id="R_5.020.020ab">asaṃdeśāt tu rāmasya tapasaś cānupālanāt</li>
        <li id="R_5.020.020cd">na tvāṃ kurmi daśagrīva bhasma bhasmārhatejasā</li>
    </ul>
    <ul id="R_5.020.021">
        <li id="R_5.020.021ab">nāpahartum ahaṃ śakyā tasya rāmasya dhīmataḥ</li>
        <li id="R_5.020.021cd">vidhis tava vadhārthāya vihito nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_5.020.022">
        <li id="R_5.020.022ab">śūreṇa dhanadabhrātā balaiḥ samuditena ca</li>
        <li id="R_5.020.022cd">apohya rāmaṃ kasmād dhi dāracauryaṃ tvayā kṛtam</li>
    </ul>
    <ul id="R_5.020.023">
        <li id="R_5.020.023ab">sītāyā vacanaṃ śrutvā rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_5.020.023cd">vivṛtya nayane krūre jānakīm anvavaikṣata</li>
    </ul>
    <ul id="R_5.020.024">
        <li id="R_5.020.024ab">nīlajīmūtasaṃkāśo mahābhujaśirodharaḥ</li>
        <li id="R_5.020.024cd">siṃhasattvagatiḥ śrīmān dīptajihvogralocanaḥ</li>
    </ul>
    <ul id="R_5.020.025">
        <li id="R_5.020.025ab">calāgramakuṭaḥ prāṃśuś citramālyānulepanaḥ</li>
        <li id="R_5.020.025cd">raktamālyāmbaradharas taptāṅgadavibhūṣaṇaḥ</li>
    </ul>
    <ul id="R_5.020.026">
        <li id="R_5.020.026ab">śroṇīsūtreṇa mahatā mekakena susaṃvṛtaḥ</li>
        <li id="R_5.020.026cd">amṛtotpādanaddhena bhujaṃgeneva mandaraḥ</li>
    </ul>
    <ul id="R_5.020.027">
        <li id="R_5.020.027ab">taruṇādityavarṇābhyāṃ kuṇḍalābhyāṃ vibhūṣitaḥ</li>
        <li id="R_5.020.027cd">raktapallavapuṣpābhyām aśokābhyām ivācalaḥ</li>
    </ul>
    <ul id="R_5.020.028">
        <li id="R_5.020.028ab">avekṣamāṇo vaidehīṃ kopasaṃraktalocanaḥ</li>
        <li id="R_5.020.028cd">uvāca rāvaṇaḥ sītāṃ bhujaṃga iva niḥśvasan</li>
    </ul>
    <ul id="R_5.020.029">
        <li id="R_5.020.029ab">anayenābhisaṃpannam arthahīnam anuvrate</li>
        <li id="R_5.020.029cd">nāśayāmy aham adya tvāṃ sūryaḥ saṃdhyām ivaujasā</li>
    </ul>
    <ul id="R_5.020.030">
        <li id="R_5.020.030ab">ity uktvā maithilīṃ rājā rāvaṇaḥ śatrurāvaṇaḥ</li>
        <li id="R_5.020.030cd">saṃdideśa tataḥ sarvā rākṣasīr ghoradarśanāḥ</li>
    </ul>
    <ul id="R_5.020.031">
        <li id="R_5.020.031ab">ekākṣīm ekakarṇāṃ ca karṇaprāvaraṇāṃ tathā</li>
        <li id="R_5.020.031cd">gokarṇīṃ hastikarṇīṃ ca lambakarṇīm akarṇikām</li>
    </ul>
    <ul id="R_5.020.032">
        <li id="R_5.020.032ab">hastipadya śvapadyau ca gopadīṃ pādacūlikām</li>
        <li id="R_5.020.032cd">ekākṣīm ekapādīṃ ca pṛthupādīm apādikām</li>
    </ul>
    <ul id="R_5.020.033">
        <li id="R_5.020.033ab">atimātraśirogrīvām atimātrakucodarīm</li>
        <li id="R_5.020.033cd">atimātrāsyanetrāṃ ca dīrghajihvām ajihvikām</li>
        <li id="R_5.020.033ef">anāsikāṃ siṃhamukhīṃ gomukhīṃ sūkarīmukhīm</li>
    </ul>
    <ul id="R_5.020.034">
        <li id="R_5.020.034ab">yathā madvaśagā sītā kṣipraṃ bhavati jānakī</li>
        <li id="R_5.020.034cd">tathā kuruta rākṣasyaḥ sarvāḥ kṣipraṃ sametya ca</li>
    </ul>
    <ul id="R_5.020.035">
        <li id="R_5.020.035ab">pratilomānulomaiś ca sāmadānādibhedanaiḥ</li>
        <li id="R_5.020.035cd">āvartayata vaidehīṃ daṇḍasyodyamanena ca</li>
    </ul>
    <ul id="R_5.020.036">
        <li id="R_5.020.036ab">iti pratisamādiśya rākṣasendraḥ punaḥ punaḥ</li>
        <li id="R_5.020.036cd">kāmamanyuparītātmā jānakīṃ paryatarjayat</li>
    </ul>
    <ul id="R_5.020.037">
        <li id="R_5.020.037ab">upagamya tataḥ kṣipraṃ rākṣasī dhānyamālinī</li>
        <li id="R_5.020.037cd">pariṣvajya daśagrīvam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.020.038">
        <li id="R_5.020.038ab">mayā krīḍa mahārājasītayā kiṃ tavānayā</li>
        <li id="R_5.020.038cd">akāmāṃ kāmayānasya śarīram upatapyate</li>
        <li id="R_5.020.038ef">icchantīṃ kāmayānasya prītir bhavati śobhanā</li>
    </ul>
    <ul id="R_5.020.039">
        <li id="R_5.020.039ab">evam uktas tu rākṣasyā samutkṣiptas tato balī</li>
        <li id="R_5.020.039cd">jvaladbhāskaravarṇābhaṃ praviveśa niveśanam</li>
    </ul>
    <ul id="R_5.020.040">
        <li id="R_5.020.040ab">devagandharvakanyāś ca nāgakanyāś ca tās tataḥ</li>
        <li id="R_5.020.040cd">parivārya daśagrīvaṃ viviśus tad gṛhottamam</li>
    </ul>
    <ul id="R_5.020.041">
        <li id="R_5.020.041ab">
            <seg type="pāda" n="a">sa maithilīṃ dharmaparām avasthitāṃ</seg>
            <seg type="pāda" n="b">pravepamānāṃ paribhartsya rāvaṇaḥ</seg>
        </li>
        <li id="R_5.020.041cd">
            <seg type="pāda" n="c">vihāya sītāṃ madanena mohitaḥ</seg>
            <seg type="pāda" n="d">svam eva veśma praviveśa bhāsvaram</seg>
        </li>
    </ul>
    <ul id="R_5.021.001">
        <li id="R_5.021.001ab">ity uktvā maithilīṃ rājā rāvaṇaḥ śatrurāvaṇaḥ</li>
        <li id="R_5.021.001cd">saṃdiśya ca tataḥ sarvā rākṣasīr nirjagāma ha</li>
    </ul>
    <ul id="R_5.021.002">
        <li id="R_5.021.002ab">niṣkrānte rākṣasendre tu punar antaḥpuraṃ gate</li>
        <li id="R_5.021.002cd">rākṣasyo bhīmarūpās tāḥ sītāṃ samabhidudruvuḥ</li>
    </ul>
    <ul id="R_5.021.003">
        <li id="R_5.021.003ab">tataḥ sītām upāgamya rākṣasyaḥ krodhamūrchitāḥ</li>
        <li id="R_5.021.003cd">paraṃ paruṣayā vācā vaidehīm idam abruvan</li>
    </ul>
    <ul id="R_5.021.004">
        <li id="R_5.021.004ab">paulastyasya variṣṭhasya rāvaṇasya mahātmanaḥ</li>
        <li id="R_5.021.004cd">daśagrīvasya bhāryātvaṃ sīte na bahu manyase</li>
    </ul>
    <ul id="R_5.021.005">
        <li id="R_5.021.005ab">tatas tv ekajaṭā nāma rākṣasī vākyam abravīt</li>
        <li id="R_5.021.005cd">āmantrya krodhatāmrākṣī sītāṃ karatalodarīm</li>
    </ul>
    <ul id="R_5.021.006">
        <li id="R_5.021.006ab">prajāpatīnāṃ ṣaṇṇāṃ tu caturtho yaḥ prajāpatiḥ</li>
        <li id="R_5.021.006cd">mānaso brahmaṇaḥ putraḥ pulastya iti viśrutaḥ</li>
    </ul>
    <ul id="R_5.021.007">
        <li id="R_5.021.007ab">pulastyasya tu tejasvī maharṣir mānasaḥ sutaḥ</li>
        <li id="R_5.021.007cd">nāmnā sa viśravā nāma prajāpatisamaprabhaḥ</li>
    </ul>
    <ul id="R_5.021.008">
        <li id="R_5.021.008ab">tasya putro viśālākṣi rāvaṇaḥ śatrurāvaṇaḥ</li>
        <li id="R_5.021.008cd">tasya tvaṃ rākṣasendrasya bhāryā bhavitum arhasi</li>
        <li id="R_5.021.008ef">mayoktaṃ cārusarvāṅgi vākyaṃ kiṃ nānumanyase</li>
    </ul>
    <ul id="R_5.021.009">
        <li id="R_5.021.009ab">tato harijaṭā nāma rākṣasī vākyam abravīt</li>
        <li id="R_5.021.009cd">vivṛtya nayane kopān mārjārasadṛśekṣaṇā</li>
    </ul>
    <ul id="R_5.021.010">
        <li id="R_5.021.010ab">yena devās trayastriṃśad devarājaś ca nirjitaḥ</li>
        <li id="R_5.021.010cd">tasya tvaṃ rākṣasendrasya bhāryā bhavitum arhasi</li>
    </ul>
    <ul id="R_5.021.011">
        <li id="R_5.021.011ab">vīryotsiktasya śūrasya saṃgrāmeṣv anivartinaḥ</li>
        <li id="R_5.021.011cd">balino vīryayuktasyā bhāryātvaṃ kiṃ na lapsyase</li>
    </ul>
    <ul id="R_5.021.012">
        <li id="R_5.021.012ab">priyāṃ bahumatāṃ bhāryāṃ tyaktvā rājā mahābalaḥ</li>
        <li id="R_5.021.012cd">sarvāsāṃ ca mahābhāgāṃ tvām upaiṣyati rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.021.013">
        <li id="R_5.021.013ab">samṛddhaṃ strīsahasreṇa nānāratnopaśobhitam</li>
        <li id="R_5.021.013cd">antaḥpuraṃ samutsṛjya tvām upaiṣyati rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.021.014">
        <li id="R_5.021.014ab">asakṛd devatā yuddhe nāgagandharvadānavāḥ</li>
        <li id="R_5.021.014cd">nirjitāḥ samare yena sa te pārśvam upāgataḥ</li>
    </ul>
    <ul id="R_5.021.015">
        <li id="R_5.021.015ab">tasya sarvasamṛddhasyā rāvaṇasya mahātmanaḥ</li>
        <li id="R_5.021.015cd">kimarthaṃ rākṣasendrasya bhāryātvaṃ necchase 'dhame</li>
    </ul>
    <ul id="R_5.021.016">
        <li id="R_5.021.016ab">yasya sūryo na tapati bhīto yasya ca mārutaḥ</li>
        <li id="R_5.021.016cd">na vāti smāyatāpāṅge kiṃ tvaṃ tasya na tiṣṭhasi</li>
    </ul>
    <ul id="R_5.021.017">
        <li id="R_5.021.017ab">puṣpavṛṣṭiṃ ca taravo mumucur yasya vai bhayāt</li>
        <li id="R_5.021.017cd">śailāś ca subhru pānīyaṃ jaladāś ca yadecchati</li>
    </ul>
    <ul id="R_5.021.018">
        <li id="R_5.021.018ab">tasya nairṛtarājasya rājarājasya bhāmini</li>
        <li id="R_5.021.018cd">kiṃ tvaṃ na kuruṣe buddhiṃ bhāryārthe rāvaṇasya hi</li>
    </ul>
    <ul id="R_5.021.019">
        <li id="R_5.021.019ab">sādhu te tattvato devi kathitaṃ sādhu bhāmini</li>
        <li id="R_5.021.019cd">gṛhāṇa susmite vākyam anyathā na bhaviṣyasi</li>
    </ul>
    <ul id="R_5.022.001">
        <li id="R_5.022.001ab">tataḥ sītām upāgamya rākṣasyo vikṛtānanāḥ</li>
        <li id="R_5.022.001cd">paruṣaṃ paruṣā nārya ūcus tā vākyam apriyam</li>
    </ul>
    <ul id="R_5.022.002">
        <li id="R_5.022.002ab">kiṃ tvam antaḥpure sīte sarvabhūtamanohare</li>
        <li id="R_5.022.002cd">mahārhaśayanopete na vāsam anumanyase</li>
    </ul>
    <ul id="R_5.022.003">
        <li id="R_5.022.003ab">mānuṣī mānuṣasyaiva bhāryātvaṃ bahu manyase</li>
        <li id="R_5.022.003cd">pratyāhara mano rāmān na tvaṃ jātu bhaviṣyasi</li>
    </ul>
    <ul id="R_5.022.004">
        <li id="R_5.022.004ab">mānuṣī mānuṣaṃ taṃ tu rāmam icchasi śobhane</li>
        <li id="R_5.022.004cd">rājyād bhraṣṭam asiddhārthaṃ viklavaṃ tam anindite</li>
    </ul>
    <ul id="R_5.022.005">
        <li id="R_5.022.005ab">rākṣasīnāṃ vacaḥ śrutvā sītā padmanibhekṣaṇā</li>
        <li id="R_5.022.005cd">netrābhyām aśrupūrṇābhyām idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.022.006">
        <li id="R_5.022.006ab">yad idaṃ lokavidviṣṭam udāharatha saṃgatāḥ</li>
        <li id="R_5.022.006cd">naitan manasi vākyaṃ me kilbiṣaṃ pratitiṣṭhati</li>
    </ul>
    <ul id="R_5.022.007">
        <li id="R_5.022.007ab">na mānuṣī rākṣasasya bhāryā bhavitum arhati</li>
        <li id="R_5.022.007cd">kāmaṃ khādata māṃ sarvā na kariṣyāmi vo vacaḥ</li>
        <li id="R_5.022.007ef">dīno vā rājyahīno vā yo me bhartā sa me guruḥ</li>
    </ul>
    <ul id="R_5.022.008">
        <li id="R_5.022.008ab">sītāyā vacanaṃ śrutvā rākṣasyaḥ krodhamūrchitāḥ</li>
        <li id="R_5.022.008cd">bhartsayanti sma paruṣair vākyai rāvaṇacoditāḥ</li>
    </ul>
    <ul id="R_5.022.009">
        <li id="R_5.022.009ab">avalīnaḥ sa nirvākyo hanumāñ śiṃśapādrume</li>
        <li id="R_5.022.009cd">sītāṃ saṃtarjayantīs tā rākṣasīr aśṛṇot kapiḥ</li>
    </ul>
    <ul id="R_5.022.010">
        <li id="R_5.022.010ab">tām abhikramya saṃrabdhā vepamānāṃ samantataḥ</li>
        <li id="R_5.022.010cd">bhṛśaṃ saṃlilihur dīptān pralambadaśanacchadān</li>
    </ul>
    <ul id="R_5.022.011">
        <li id="R_5.022.011ab">ūcuś ca paramakruddhāḥ pragṛhyāśu paraśvadhān</li>
        <li id="R_5.022.011cd">neyam arhati bhartāraṃ rāvaṇaṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_5.022.012">
        <li id="R_5.022.012ab">sā bhartsyamānā bhīmābhī rākṣasībhir varānanā</li>
        <li id="R_5.022.012cd">sā bāṣpam apamārjantī śiṃśapāṃ tām upāgamat</li>
    </ul>
    <ul id="R_5.022.013">
        <li id="R_5.022.013ab">tatas tāṃ śiṃśapāṃ sītā rākṣasībhiḥ samāvṛtā</li>
        <li id="R_5.022.013cd">abhigamya viśālākṣī tasthau śokapariplutā</li>
    </ul>
    <ul id="R_5.022.014">
        <li id="R_5.022.014ab">tāṃ kṛśāṃ dīnavadanāṃ malināmbaradhāriṇīm</li>
        <li id="R_5.022.014cd">bhartsayāṃ cakrire bhīmā rākṣasyas tāḥ samantataḥ</li>
    </ul>
    <ul id="R_5.022.015">
        <li id="R_5.022.015ab">tatas tāṃ vinatā nāma rākṣasī bhīmadarśanā</li>
        <li id="R_5.022.015cd">abravīt kupitākārā karālā nirṇatodarī</li>
    </ul>
    <ul id="R_5.022.016">
        <li id="R_5.022.016ab">sīte paryāptam etāvad bhartṛsneho nidarśitaḥ</li>
        <li id="R_5.022.016cd">sarvatrātikṛtaṃ bhadre vyasanāyopakalpate</li>
    </ul>
    <ul id="R_5.022.017">
        <li id="R_5.022.017ab">parituṣṭāsmi bhadraṃ te mānuṣas te kṛto vidhiḥ</li>
        <li id="R_5.022.017cd">mamāpi tu vacaḥ pathyaṃ bruvantyāḥ kuru maithili</li>
    </ul>
    <ul id="R_5.022.018">
        <li id="R_5.022.018ab">rāvaṇaṃ bhaja bhartāraṃ bhartāraṃ sarvarakṣasām</li>
        <li id="R_5.022.018cd">vikrāntaṃ rūpavantaṃ ca sureśam iva vāsavam</li>
    </ul>
    <ul id="R_5.022.019">
        <li id="R_5.022.019ab">dakṣiṇaṃ tyāgaśīlaṃ ca sarvasya priyavādinam</li>
        <li id="R_5.022.019cd">mānuṣaṃ kṛpaṇaṃ rāmaṃ tyaktvā rāvaṇam āśraya</li>
    </ul>
    <ul id="R_5.022.020">
        <li id="R_5.022.020ab">divyāṅgarāgā vaidehi divyābharaṇabhūṣitā</li>
        <li id="R_5.022.020cd">adya prabhṛti sarveṣāṃ lokānām īśvarī bhava</li>
        <li id="R_5.022.020ef">agneḥ svāhā yathā devī śacīvendrasya śobhane</li>
    </ul>
    <ul id="R_5.022.021">
        <li id="R_5.022.021ab">kiṃ te rāmeṇa vaidehi kṛpaṇena gatāyuṣā</li>
    </ul>
    <ul id="R_5.022.022">
        <li id="R_5.022.022ab">etad uktaṃ ca me vākyaṃ yadi tvaṃ na kariṣyasi</li>
        <li id="R_5.022.022cd">asmin muhūrte sarvās tvāṃ bhakṣayiṣyāmahe vayam</li>
    </ul>
    <ul id="R_5.022.023">
        <li id="R_5.022.023ab">anyā tu vikaṭā nāma lambamānapayodharā</li>
        <li id="R_5.022.023cd">abravīt kupitā sītāṃ muṣṭim udyamya garjatī</li>
    </ul>
    <ul id="R_5.022.024">
        <li id="R_5.022.024ab">bahūny apratirūpāṇi vacanāni sudurmate</li>
        <li id="R_5.022.024cd">anukrośān mṛdutvāc ca soḍhāni tava maithili</li>
        <li id="R_5.022.024ef">na ca naḥ kuruṣe vākyaṃ hitaṃ kālapuraskṛtam</li>
    </ul>
    <ul id="R_5.022.025">
        <li id="R_5.022.025ab">ānītāsi samudrasya pāram anyair durāsadam</li>
        <li id="R_5.022.025cd">rāvaṇāntaḥpuraṃ ghoraṃ praviṣṭā cāsi maithili</li>
    </ul>
    <ul id="R_5.022.026">
        <li id="R_5.022.026ab">rāvaṇasya gṛhe rudhā asmābhis tu surakṣitā</li>
        <li id="R_5.022.026cd">na tvāṃ śaktaḥ paritrātum api sākṣāt puraṃdaraḥ</li>
    </ul>
    <ul id="R_5.022.027">
        <li id="R_5.022.027ab">kuruṣva hitavādinyā vacanaṃ mama maithili</li>
        <li id="R_5.022.027cd">alam aśruprapātena tyaja śokam anarthakam</li>
    </ul>
    <ul id="R_5.022.028">
        <li id="R_5.022.028ab">bhaja prītiṃ praharṣaṃ ca tyajaitāṃ nityadainyatām</li>
        <li id="R_5.022.028cd">sīte rākṣasarājena saha krīḍa yathāsukham</li>
    </ul>
    <ul id="R_5.022.029">
        <li id="R_5.022.029ab">jānāsi hi yathā bhīru strīṇāṃ yauvanam adhruvam</li>
        <li id="R_5.022.029cd">yāvan na te vyatikrāmet tāvat sukham avāpnuhi</li>
    </ul>
    <ul id="R_5.022.030">
        <li id="R_5.022.030ab">udyānāni ca ramyāṇi parvatopavanāni ca</li>
        <li id="R_5.022.030cd">saha rākṣasarājena cara tvaṃ madirekṣaṇe</li>
    </ul>
    <ul id="R_5.022.031">
        <li id="R_5.022.031ab">strīsahasrāṇi te sapta vaśe sthāsyanti sundari</li>
        <li id="R_5.022.031cd">rāvaṇaṃ bhaja bhartāraṃ bhartāraṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_5.022.032">
        <li id="R_5.022.032ab">utpāṭya vā te hṛdayaṃ bhakṣayiṣyāmi maithili</li>
        <li id="R_5.022.032cd">yadi me vyāhṛtaṃ vākyaṃ na yathāvat kariṣyasi</li>
    </ul>
    <ul id="R_5.022.033">
        <li id="R_5.022.033ab">tataś caṇḍodarī nāma rākṣasī krūradarśanā</li>
        <li id="R_5.022.033cd">bhrāmayantī mahac chūlam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.022.034">
        <li id="R_5.022.034ab">imāṃ hariṇalokākṣīṃ trāsotkampapayodharām</li>
        <li id="R_5.022.034cd">rāvaṇena hṛtāṃ dṛṣṭvā daurhṛdo me mahān abhūt</li>
    </ul>
    <ul id="R_5.022.035">
        <li id="R_5.022.035ab">yakṛtplīham athotpīḍaṃ hṛdayaṃ ca sabandhanam</li>
        <li id="R_5.022.035cd">antrāṇy api tathā śīrṣaṃ khādeyam iti me matiḥ</li>
    </ul>
    <ul id="R_5.022.036">
        <li id="R_5.022.036ab">tatas tu praghasā nāma rākṣasī vākyam abravīt</li>
        <li id="R_5.022.036cd">kaṇṭham asyā nṛśaṃsāyāḥ pīḍayāmaḥ kim āsyate</li>
    </ul>
    <ul id="R_5.022.037">
        <li id="R_5.022.037ab">nivedyatāṃ tato rājñe mānuṣī sā mṛteti ha</li>
        <li id="R_5.022.037cd">nātra kaś cana saṃdehaḥ khādateti sa vakṣyati</li>
    </ul>
    <ul id="R_5.022.038">
        <li id="R_5.022.038ab">tatas tv ajāmukhī nāma rākṣasī vākyam abravīt</li>
        <li id="R_5.022.038cd">viśasyemāṃ tataḥ sarvān samān kuruta pīlukān</li>
    </ul>
    <ul id="R_5.022.039">
        <li id="R_5.022.039ab">vibhajāma tataḥ sarvā vivādo me na rocate</li>
        <li id="R_5.022.039cd">peyam ānīyatāṃ kṣipraṃ mālyaṃ ca vividhaṃ bahu</li>
    </ul>
    <ul id="R_5.022.040">
        <li id="R_5.022.040ab">tataḥ śūrpaṇakhā nāma rākṣasī vākyam abravīt</li>
        <li id="R_5.022.040cd">ajāmukhā yad uktaṃ hi tad eva mama rocate</li>
    </ul>
    <ul id="R_5.022.041">
        <li id="R_5.022.041ab">surā cānīyatāṃ kṣipraṃ sarvaśokavināśinī</li>
        <li id="R_5.022.041cd">mānuṣaṃ māṃsam āsādya nṛtyāmo 'tha nikumbhilām</li>
    </ul>
    <ul id="R_5.022.042">
        <li id="R_5.022.042ab">evaṃ saṃbhartsyamānā sā sītā surasutopamā</li>
        <li id="R_5.022.042cd">rākṣasībhiḥ sughorābhir dhairyam utsṛjya roditi</li>
    </ul>
    <ul id="R_5.023.001">
        <li id="R_5.023.001ab">tathā tāsāṃ vadantīnāṃ paruṣaṃ dāruṇaṃ bahu</li>
        <li id="R_5.023.001cd">rākṣasīnām asaumyānāṃ ruroda janakātmajā</li>
    </ul>
    <ul id="R_5.023.002">
        <li id="R_5.023.002ab">evam uktā tu vaidehī rākṣasībhir manasvinī</li>
        <li id="R_5.023.002cd">uvāca paramatrastā bāṣpagadgadayā girā</li>
    </ul>
    <ul id="R_5.023.003">
        <li id="R_5.023.003ab">na mānuṣī rākṣasasya bhāryā bhavitum arhati</li>
        <li id="R_5.023.003cd">kāmaṃ khādata māṃ sarvā na kariṣyāmi vo vacaḥ</li>
    </ul>
    <ul id="R_5.023.004">
        <li id="R_5.023.004ab">sā rākṣasī madhyagatā sītā surasutopamā</li>
        <li id="R_5.023.004cd">na śarma lebhe duḥkhārtā rāvaṇena ca tarjitā</li>
    </ul>
    <ul id="R_5.023.005">
        <li id="R_5.023.005ab">vepate smādhikaṃ sītā viśantīvāṅgam ātmanaḥ</li>
        <li id="R_5.023.005cd">vane yūthaparibhraṣṭā mṛgī kokair ivārditā</li>
    </ul>
    <ul id="R_5.023.006">
        <li id="R_5.023.006ab">sā tv aśokasya vipulāṃ śākhām ālambya puṣpitām</li>
        <li id="R_5.023.006cd">cintayām āsa śokena bhartāraṃ bhagnamānasā</li>
    </ul>
    <ul id="R_5.023.007">
        <li id="R_5.023.007ab">sā snāpayantī vipulau stanau netrajalasravaiḥ</li>
        <li id="R_5.023.007cd">cintayantī na śokasya tadāntam adhigacchati</li>
    </ul>
    <ul id="R_5.023.008">
        <li id="R_5.023.008ab">sā vepamānā patitā pravāte kadalī yathā</li>
        <li id="R_5.023.008cd">rākṣasīnāṃ bhayatrastā vivarṇavadanābhavat</li>
    </ul>
    <ul id="R_5.023.009">
        <li id="R_5.023.009ab">tasyā sā dīrghavipulā vepantyāḥ sītayā tadā</li>
        <li id="R_5.023.009cd">dadṛśe kampinī veṇī vyālīva parisarpatī</li>
    </ul>
    <ul id="R_5.023.010">
        <li id="R_5.023.010ab">sā niḥśvasantī duḥkhārtā śokopahatacetanā</li>
        <li id="R_5.023.010cd">ārtā vyasṛjad aśrūṇi maithilī vilalāpa ha</li>
    </ul>
    <ul id="R_5.023.011">
        <li id="R_5.023.011ab">hā rāmeti ca duḥkhārtā punar hā lakṣmaṇeti ca</li>
        <li id="R_5.023.011cd">hā śvaśru mama kausalye hā sumitreti bhāvini</li>
    </ul>
    <ul id="R_5.023.012">
        <li id="R_5.023.012ab">lokapravādaḥ satyo 'yaṃ paṇḍitaiḥ samudāhṛtaḥ</li>
        <li id="R_5.023.012cd">akāle durlabho mṛtyuḥ striyā vā puruṣasya vā</li>
    </ul>
    <ul id="R_5.023.013">
        <li id="R_5.023.013ab">yatrāham ābhiḥ krūrābhī rākṣasībhir ihārditā</li>
        <li id="R_5.023.013cd">jīvāmi hīnā rāmeṇa muhūrtam api duḥkhitā</li>
    </ul>
    <ul id="R_5.023.014">
        <li id="R_5.023.014ab">eṣālpapuṇyā kṛpaṇā vinaśiṣyāmy anāthavat</li>
        <li id="R_5.023.014cd">samudramadhye nau pūrṇā vāyuvegair ivāhatā</li>
    </ul>
    <ul id="R_5.023.015">
        <li id="R_5.023.015ab">bhartāraṃ tam apaśyantī rākṣasīvaśam āgatā</li>
        <li id="R_5.023.015cd">sīdāmi khalu śokena kūlaṃ toyahataṃ yathā</li>
    </ul>
    <ul id="R_5.023.016">
        <li id="R_5.023.016ab">taṃ padmadalapatrākṣaṃ siṃhavikrāntagāminam</li>
        <li id="R_5.023.016cd">dhanyāḥ paśyanti me nāthaṃ kṛtajñaṃ priyavādinam</li>
    </ul>
    <ul id="R_5.023.017">
        <li id="R_5.023.017ab">sarvathā tena hīnāyā rāmeṇa viditātmanā</li>
        <li id="R_5.023.017cd">tīṣkṇaṃ viṣam ivāsvādya durlabhaṃ mama jīvitam</li>
    </ul>
    <ul id="R_5.023.018">
        <li id="R_5.023.018ab">kīdṛśaṃ tu mayā pāpaṃ purā dehāntare kṛtam</li>
        <li id="R_5.023.018cd">yenedaṃ prāpyate duḥkhaṃ mayā ghoraṃ sudāruṇam</li>
    </ul>
    <ul id="R_5.023.019">
        <li id="R_5.023.019ab">jīvitaṃ tyaktum icchāmi śokena mahatā vṛtā</li>
        <li id="R_5.023.019cd">rākṣasībhiś ca rakṣantyā rāmo nāsādyate mayā</li>
    </ul>
    <ul id="R_5.023.020">
        <li id="R_5.023.020ab">dhig astu khalu mānuṣyaṃ dhig astu paravaśyatām</li>
        <li id="R_5.023.020cd">na śakyaṃ yat parityaktum ātmacchandena jīvitam</li>
    </ul>
    <ul id="R_5.024.001">
        <li id="R_5.024.001ab">prasaktāśrumukhīty evaṃ bruvantī janakātmajā</li>
        <li id="R_5.024.001cd">adhomukhamukhī bālā vilaptum upacakrame</li>
    </ul>
    <ul id="R_5.024.002">
        <li id="R_5.024.002ab">unmatteva pramatteva bhrāntacitteva śocatī</li>
        <li id="R_5.024.002cd">upāvṛttā kiśorīva viveṣṭantī mahītale</li>
    </ul>
    <ul id="R_5.024.003">
        <li id="R_5.024.003ab">rāghavasyāpramattasya rakṣasā kāmarūpiṇā</li>
        <li id="R_5.024.003cd">rāvaṇena pramathyāham ānītā krośatī balāt</li>
    </ul>
    <ul id="R_5.024.004">
        <li id="R_5.024.004ab">rākṣasī vaśam āpannā bhartyamānā sudāruṇam</li>
        <li id="R_5.024.004cd">cintayantī suduḥkhārtā nāhaṃ jīvitum utsahe</li>
    </ul>
    <ul id="R_5.024.005">
        <li id="R_5.024.005ab">na hi me jīvitenārtho naivārthair na ca bhūṣaṇaiḥ</li>
        <li id="R_5.024.005cd">vasantyā rākṣasī madhye vinā rāmaṃ mahāratham</li>
    </ul>
    <ul id="R_5.024.006">
        <li id="R_5.024.006ab">dhiṅ mām anāryām asatīṃ yāhaṃ tena vinā kṛtā</li>
        <li id="R_5.024.006cd">muhūrtam api rakṣāmi jīvitaṃ pāpajīvitā</li>
    </ul>
    <ul id="R_5.024.007">
        <li id="R_5.024.007ab">kā ca me jīvite śraddhā sukhe vā taṃ priyaṃ vinā</li>
        <li id="R_5.024.007cd">bhartāraṃ sāgarāntāyā vasudhāyāḥ priyaṃ vadam</li>
    </ul>
    <ul id="R_5.024.008">
        <li id="R_5.024.008ab">bhidyatāṃ bhakṣyatāṃ vāpi śarīraṃ visṛjāmy aham</li>
        <li id="R_5.024.008cd">na cāpy ahaṃ ciraṃ duḥkhaṃ saheyaṃ priyavarjitā</li>
    </ul>
    <ul id="R_5.024.009">
        <li id="R_5.024.009ab">caraṇenāpi savyena na spṛśeyaṃ niśācaram</li>
        <li id="R_5.024.009cd">rāvaṇaṃ kiṃ punar ahaṃ kāmayeyaṃ vigarhitam</li>
    </ul>
    <ul id="R_5.024.010">
        <li id="R_5.024.010ab">pratyākhyātaṃ na jānāti nātmānaṃ nātmanaḥ kulam</li>
        <li id="R_5.024.010cd">yo nṛśaṃsa svabhāvena māṃ prārthayitum icchati</li>
    </ul>
    <ul id="R_5.024.011">
        <li id="R_5.024.011ab">chinnā bhinnā vibhaktā vā dīpte vāgnau pradīpitā</li>
        <li id="R_5.024.011cd">rāvaṇaṃ nopatiṣṭheyaṃ kiṃ pralāpena vaś ciram</li>
    </ul>
    <ul id="R_5.024.012">
        <li id="R_5.024.012ab">khyātaḥ prājñaḥ kṛtajñaś ca sānukrośaś ca rāghavaḥ</li>
        <li id="R_5.024.012cd">sadvṛtto niranukrośaḥ śaṅke madbhāgyasaṃkṣayāt</li>
    </ul>
    <ul id="R_5.024.013">
        <li id="R_5.024.013ab">rākṣasānāṃ janasthāne sahasrāṇi caturdaśa</li>
        <li id="R_5.024.013cd">yenaikena nirastāni sa māṃ kiṃ nābhipadyate</li>
    </ul>
    <ul id="R_5.024.014">
        <li id="R_5.024.014ab">niruddhā rāvaṇenāham alpavīryeṇa rakṣasā</li>
        <li id="R_5.024.014cd">samarthaḥ khalu me bhartā rāvaṇaṃ hantum āhave</li>
    </ul>
    <ul id="R_5.024.015">
        <li id="R_5.024.015ab">virādho daṇḍakāraṇye yena rākṣasapuṃgavaḥ</li>
        <li id="R_5.024.015cd">raṇe rāmeṇa nihataḥ sa māṃ kiṃ nābhipadyate</li>
    </ul>
    <ul id="R_5.024.016">
        <li id="R_5.024.016ab">kāmaṃ madhye samudrasya laṅkeyaṃ duṣpradharṣaṇā</li>
        <li id="R_5.024.016cd">na tu rāghavabāṇānāṃ gatirodhī ha vidyate</li>
    </ul>
    <ul id="R_5.024.017">
        <li id="R_5.024.017ab">kiṃ nu tat kāraṇaṃ yena rāmo dṛḍhaparākramaḥ</li>
        <li id="R_5.024.017cd">rakṣasāpahṛtāṃ bhāryām iṣṭāṃ nābhyavapadyate</li>
    </ul>
    <ul id="R_5.024.018">
        <li id="R_5.024.018ab">ihasthāṃ māṃ na jānīte śaṅke lakṣmaṇapūrvajaḥ</li>
        <li id="R_5.024.018cd">jānann api hi tejasvī dharṣaṇāṃ marṣayiṣyati</li>
    </ul>
    <ul id="R_5.024.019">
        <li id="R_5.024.019ab">hṛteti yo 'dhigatvā māṃ rāghavāya nivedayet</li>
        <li id="R_5.024.019cd">gṛdhrarājo 'pi sa raṇe rāvaṇena nipātitaḥ</li>
    </ul>
    <ul id="R_5.024.020">
        <li id="R_5.024.020ab">kṛtaṃ karma mahat tena māṃ tadābhyavapadyatā</li>
        <li id="R_5.024.020cd">tiṣṭhatā rāvaṇadvandve vṛddhenāpi jaṭāyuṣā</li>
    </ul>
    <ul id="R_5.024.021">
        <li id="R_5.024.021ab">yadi mām iha jānīyād vartamānāṃ sa rāghavaḥ</li>
        <li id="R_5.024.021cd">adya bāṇair abhikruddhaḥ kuryāl lokam arākṣasaṃ</li>
    </ul>
    <ul id="R_5.024.022">
        <li id="R_5.024.022ab">vidhamec ca purīṃ laṅkāṃ śoṣayec ca mahodadhim</li>
        <li id="R_5.024.022cd">rāvaṇasya ca nīcasya kīrtiṃ nāma ca nāśayet</li>
    </ul>
    <ul id="R_5.024.023">
        <li id="R_5.024.023ab">tato nihatanathānāṃ rākṣasīnāṃ gṛhe gṛhe</li>
        <li id="R_5.024.023cd">yathāham evaṃ rudatī tathā bhūyo na saṃśayaḥ</li>
        <li id="R_5.024.023ef">anviṣya rakṣasāṃ laṅkāṃ kuryād rāmaḥ salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.024.024">
        <li id="R_5.024.024ab">na hi tābhyāṃ ripur dṛṣṭo muhūtam api jīvati</li>
        <li id="R_5.024.024cd">citā dhūmākulapathā gṛdhramaṇḍalasaṃkulā</li>
        <li id="R_5.024.024ef">acireṇa tu laṅkeyaṃ śmaśānasadṛśī bhavet</li>
    </ul>
    <ul id="R_5.024.025">
        <li id="R_5.024.025ab">acireṇaiva kālena prāpsyāmy eva manoratham</li>
        <li id="R_5.024.025cd">duṣprasthāno 'yam ākhyāti sarveṣāṃ vo viparyayaḥ</li>
    </ul>
    <ul id="R_5.024.026">
        <li id="R_5.024.026ab">yādṛśāni tu dṛśyante laṅkāyām aśubhāni tu</li>
        <li id="R_5.024.026cd">acireṇaiva kālena bhaviṣyati hataprabhā</li>
    </ul>
    <ul id="R_5.024.027">
        <li id="R_5.024.027ab">nūnaṃ laṅkā hate pāpe rāvaṇe rākṣasādhipe</li>
        <li id="R_5.024.027cd">śoṣaṃ yāsyati durdharṣā pramadā vidhavā yathā</li>
    </ul>
    <ul id="R_5.024.028">
        <li id="R_5.024.028ab">puṣyotsavasamṛddhā ca naṣṭabhartrī sarākṣasā</li>
        <li id="R_5.024.028cd">bhaviṣyati purī laṅkā naṣṭabhartrī yathāṅganā</li>
    </ul>
    <ul id="R_5.024.029">
        <li id="R_5.024.029ab">nūnaṃ rākṣasakanyānāṃ rudantīnāṃ gṛhe gṛhe</li>
        <li id="R_5.024.029cd">śroṣyāmi nacirād eva duḥkhārtānām iha dhvanim</li>
    </ul>
    <ul id="R_5.024.030">
        <li id="R_5.024.030ab">sāndhakārā hatadyotā hatarākṣasapuṃgavā</li>
        <li id="R_5.024.030cd">bhaviṣyati purī laṅkā nirdagdhā rāmasāyakaiḥ</li>
    </ul>
    <ul id="R_5.024.031">
        <li id="R_5.024.031ab">yadi nāma sa śūro māṃ rāmo raktāntalocanaḥ</li>
        <li id="R_5.024.031cd">jānīyād vartamānāṃ hi rāvaṇasya niveśane</li>
    </ul>
    <ul id="R_5.024.032">
        <li id="R_5.024.032ab">anena tu nṛśaṃsena rāvaṇenādhamena me</li>
        <li id="R_5.024.032cd">samayo yas tu nirdiṣṭas tasya kālo 'yam āgataḥ</li>
    </ul>
    <ul id="R_5.024.033">
        <li id="R_5.024.033ab">akāryaṃ ye na jānanti nairṛtāḥ pāpakāriṇaḥ</li>
        <li id="R_5.024.033cd">adharmāt tu mahotpāto bhaviṣyati hi sāmpratam</li>
    </ul>
    <ul id="R_5.024.034">
        <li id="R_5.024.034ab">naite dharmaṃ vijānanti rākṣasāḥ piśitāśanāḥ</li>
        <li id="R_5.024.034cd">dhruvaṃ māṃ prātarāśārthe rākṣasaḥ kalpayiṣyati</li>
    </ul>
    <ul id="R_5.024.035">
        <li id="R_5.024.035ab">sāhaṃ kathaṃ kariṣyāmi taṃ vinā priyadarśanam</li>
        <li id="R_5.024.035cd">rāmaṃ raktāntanayanam apaśyantī suduḥkhitā</li>
    </ul>
    <ul id="R_5.024.036">
        <li id="R_5.024.036ab">yadi kaś cit pradātā me viṣasyādya bhaved iha</li>
        <li id="R_5.024.036cd">kṣipraṃ vaivasvataṃ devaṃ paśyeyaṃ patinā vinā</li>
    </ul>
    <ul id="R_5.024.037">
        <li id="R_5.024.037ab">nājānāj jīvatīṃ rāmaḥ sa māṃ lakṣmaṇapūrvajaḥ</li>
        <li id="R_5.024.037cd">jānantau tau na kuryātāṃ norvyāṃ hi mama mārgaṇam</li>
    </ul>
    <ul id="R_5.024.038">
        <li id="R_5.024.038ab">nūnaṃ mamaiva śokena sa vīro lakṣmaṇāgrajaḥ</li>
        <li id="R_5.024.038cd">devalokam ito yātas tyaktvā dehaṃ mahītale</li>
    </ul>
    <ul id="R_5.024.039">
        <li id="R_5.024.039ab">dhanyā devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_5.024.039cd">mama paśyanti ye nāthaṃ rāmaṃ rājīvalocanam</li>
    </ul>
    <ul id="R_5.024.040">
        <li id="R_5.024.040ab">atha vā na hi tasyārthe dharmakāmasya dhīmataḥ</li>
        <li id="R_5.024.040cd">mayā rāmasya rājarṣer bhāryayā paramātmanaḥ</li>
    </ul>
    <ul id="R_5.024.041">
        <li id="R_5.024.041ab">dṛśyamāne bhavet prītaḥ sauhṛdaṃ nāsty apaśyataḥ</li>
        <li id="R_5.024.041cd">nāśayanti kṛtaghrās tu na rāmo nāśayiṣyati</li>
    </ul>
    <ul id="R_5.024.042">
        <li id="R_5.024.042ab">kiṃ nu me na guṇāḥ ke cit kiṃ vā bhāgya kṣayo hi me</li>
        <li id="R_5.024.042cd">yāhaṃ sītā varārheṇa hīnā rāmeṇa bhāminī</li>
    </ul>
    <ul id="R_5.024.043">
        <li id="R_5.024.043ab">śreyo me jīvitān martuṃ vihīnā yā mahātmanā</li>
        <li id="R_5.024.043cd">rāmād akliṣṭacāritrāc chūrāc chatrunibarhaṇāt</li>
    </ul>
    <ul id="R_5.024.044">
        <li id="R_5.024.044ab">atha vā nyastaśastrau tau vane mūlaphalāśanau</li>
        <li id="R_5.024.044cd">bhrātarau hi nara śreṣṭhau carantau vanagocarau</li>
    </ul>
    <ul id="R_5.024.045">
        <li id="R_5.024.045ab">atha vā rākṣasendreṇa rāvaṇena durātmanā</li>
        <li id="R_5.024.045cd">chadmanā ghātitau śūrau bhrātarau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_5.024.046">
        <li id="R_5.024.046ab">sāham evaṃgate kāle martum icchāmi sarvathā</li>
        <li id="R_5.024.046cd">na ca me vihito mṛtyur asmin duḥkhe 'pi vartati</li>
    </ul>
    <ul id="R_5.024.047">
        <li id="R_5.024.047ab">dhanyāḥ khalu mahātmāno munayaḥ satyasaṃmatāḥ</li>
        <li id="R_5.024.047cd">jitātmāno mahābhāgā yeṣāṃ na staḥ priyāpriye</li>
    </ul>
    <ul id="R_5.024.048">
        <li id="R_5.024.048ab">priyān na saṃbhaved duḥkham apriyād adhikaṃ bhayam</li>
        <li id="R_5.024.048cd">tābhyāṃ hi ye viyujyante namas teṣāṃ mahātmanām</li>
    </ul>
    <ul id="R_5.024.049">
        <li id="R_5.024.049ab">sāhaṃ tyaktā priyeṇeha rāmeṇa viditātmanā</li>
        <li id="R_5.024.049cd">prāṇāṃs tyakṣyāmi pāpasya rāvaṇasya gatā vaśam</li>
    </ul>
    <ul id="R_5.025.001">
        <li id="R_5.025.001ab">ity uktāḥ sītayā ghoraṃ rākṣasyaḥ krodhamūrchitāḥ</li>
        <li id="R_5.025.001cd">kāś cij jagmus tad ākhyātuṃ rāvaṇasya tarasvinaḥ</li>
    </ul>
    <ul id="R_5.025.002">
        <li id="R_5.025.002ab">tataḥ sītām upāgamya rākṣasyo ghoradarśanāḥ</li>
        <li id="R_5.025.002cd">punaḥ paruṣam ekārtham anarthārtham athābruvan</li>
    </ul>
    <ul id="R_5.025.003">
        <li id="R_5.025.003ab">hantedānīṃ tavānārye sīte pāpaviniścaye</li>
        <li id="R_5.025.003cd">rākṣasyo bhakṣayiṣyanti māṃsam etad yathāsukham</li>
    </ul>
    <ul id="R_5.025.004">
        <li id="R_5.025.004ab">sītāṃ tābhir anāryābhir dṛṣṭvā saṃtarjitāṃ tadā</li>
        <li id="R_5.025.004cd">rākṣasī trijaṭāvṛddhā śayānā vākyam abravīt</li>
    </ul>
    <ul id="R_5.025.005">
        <li id="R_5.025.005ab">ātmānaṃ khādatānāryā na sītāṃ bhakṣayiṣyatha</li>
        <li id="R_5.025.005cd">janakasya sutām iṣṭāṃ snuṣāṃ daśarathasya ca</li>
    </ul>
    <ul id="R_5.025.006">
        <li id="R_5.025.006ab">svapno hy adya mayā dṛṣṭo dāruṇo romaharṣaṇaḥ</li>
        <li id="R_5.025.006cd">rākṣasānām abhāvāya bhartur asyā bhavāya ca</li>
    </ul>
    <ul id="R_5.025.007">
        <li id="R_5.025.007ab">evam uktās trijaṭayā rākṣasyaḥ krodhamūrchitāḥ</li>
        <li id="R_5.025.007cd">sarvā evābruvan bhītās trijaṭāṃ tām idaṃ vacaḥ</li>
    </ul>
    <ul id="R_5.025.008">
        <li id="R_5.025.008ab">kathayasva tvayā dṛṣṭaḥ svapne 'yaṃ kīdṛśo niśi</li>
    </ul>
    <ul id="R_5.025.009">
        <li id="R_5.025.009ab">tāsāṃ śrutvā tu vacanaṃ rākṣasīnāṃ mukhodgatam</li>
        <li id="R_5.025.009cd">uvāca vacanaṃ kāle trijaṭāsvapnasaṃśritam</li>
    </ul>
    <ul id="R_5.025.010">
        <li id="R_5.025.010ab">gajadantamayīṃ divyāṃ śibikām antarikṣagām</li>
        <li id="R_5.025.010cd">yuktāṃ vājisahasreṇa svayam āsthāya rāghavaḥ</li>
    </ul>
    <ul id="R_5.025.011">
        <li id="R_5.025.011ab">svapne cādya mayā dṛṣṭā sītā śuklāmbarāvṛtā</li>
        <li id="R_5.025.011cd">sāgareṇa parikṣiptaṃ śvetaparvatam āsthitā</li>
        <li id="R_5.025.011ef">rāmeṇa saṃgatā sītā bhāskareṇa prabhā yathā</li>
    </ul>
    <ul id="R_5.025.012">
        <li id="R_5.025.012ab">rāghavaś ca mayā dṛṣṭaś caturdantaṃ mahāgajam</li>
        <li id="R_5.025.012cd">ārūḍhaḥ śailasaṃkāśaṃ cacāra sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.025.013">
        <li id="R_5.025.013ab">tatas tau naraśārdūlau dīpyamānau svatejasā</li>
        <li id="R_5.025.013cd">śuklamālyāmbaradharau jānakīṃ paryupasthitau</li>
    </ul>
    <ul id="R_5.025.014">
        <li id="R_5.025.014ab">tatas tasya nagasyāgre ākāśasthasya dantinaḥ</li>
        <li id="R_5.025.014cd">bhartrā parigṛhītasya jānakī skandham āśritā</li>
    </ul>
    <ul id="R_5.025.015">
        <li id="R_5.025.015ab">bhartur aṅkāt samutpatya tataḥ kamalalocanā</li>
        <li id="R_5.025.015cd">candrasūryau mayā dṛṣṭā pāṇibhyāṃ parimārjatī</li>
    </ul>
    <ul id="R_5.025.016">
        <li id="R_5.025.016ab">tatas tābhyāṃ kumārābhyām āsthitaḥ sa gajottamaḥ</li>
        <li id="R_5.025.016cd">sītayā ca viśālākṣyā laṅkāyā upari sthitaḥ</li>
    </ul>
    <ul id="R_5.025.017">
        <li id="R_5.025.017ab">pāṇḍurarṣabhayuktena rathenāṣṭayujā svayam</li>
        <li id="R_5.025.017cd">śuklamālyāmbaradharo lakṣmaṇena samāgataḥ</li>
        <li id="R_5.025.017ef">lakṣmaṇena saha bhrātrā sītayā saha bhāryayā</li>
    </ul>
    <ul id="R_5.025.018">
        <li id="R_5.025.018ab">vimānāt puṣpakād adya rāvaṇaḥ patito bhuvi</li>
        <li id="R_5.025.018cd">kṛṣyapāṇaḥ striyā dṛṣṭo muṇḍaḥ kṛṣṇāmbaraḥ punaḥ</li>
    </ul>
    <ul id="R_5.025.019">
        <li id="R_5.025.019ab">rathena kharayuktena raktamālyānulepanaḥ</li>
        <li id="R_5.025.019cd">prayāto dakṣiṇām āśāṃ praviṣṭaḥ kardamaṃ hradam</li>
    </ul>
    <ul id="R_5.025.020">
        <li id="R_5.025.020ab">kaṇṭhe baddhvā daśagrīvaṃ pramadā raktavāsinī</li>
        <li id="R_5.025.020cd">kālī kardamaliptāṅgī diśaṃ yāmyāṃ prakarṣati</li>
    </ul>
    <ul id="R_5.025.021">
        <li id="R_5.025.021ab">varāheṇa daśagrīvaḥ śiṃśumāreṇa cendrajit</li>
        <li id="R_5.025.021cd">uṣṭreṇa kumbhakarṇaś ca prayāto dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_5.025.022">
        <li id="R_5.025.022ab">samājaś ca mahān vṛtto gītavāditraniḥsvanaḥ</li>
        <li id="R_5.025.022cd">pibatāṃ raktamālyānāṃ rakṣasāṃ raktavāsasām</li>
    </ul>
    <ul id="R_5.025.023">
        <li id="R_5.025.023ab">laṅkā ceyaṃ purī ramyā savājirathasaṃkulā</li>
        <li id="R_5.025.023cd">sāgare patitā dṛṣṭā bhagnagopuratoraṇā</li>
    </ul>
    <ul id="R_5.025.024">
        <li id="R_5.025.024ab">pītva tailaṃ pranṛttāś ca prahasantyo mahāsvanāḥ</li>
        <li id="R_5.025.024cd">laṅkāyāṃ bhasmarūkṣāyāṃ sarvā rākṣasayoṣitaḥ</li>
    </ul>
    <ul id="R_5.025.025">
        <li id="R_5.025.025ab">kumbhakarṇādayaś ceme sarve rākṣasapuṃgavāḥ</li>
        <li id="R_5.025.025cd">raktaṃ nivasanaṃ gṛhya praviṣṭā gomayahrade</li>
    </ul>
    <ul id="R_5.025.026">
        <li id="R_5.025.026ab">apagacchata naśyadhvaṃ sītām āpnoti rāghavaḥ</li>
        <li id="R_5.025.026cd">ghātayet paramāmarṣī sarvaiḥ sārdhaṃ hi rākṣasaiḥ</li>
    </ul>
    <ul id="R_5.025.027">
        <li id="R_5.025.027ab">priyāṃ bahumatāṃ bhāryāṃ vanavāsam anuvratām</li>
        <li id="R_5.025.027cd">bhartsitāṃ tarjitāṃ vāpi nānumaṃsyati rāghavaḥ</li>
    </ul>
    <ul id="R_5.025.028">
        <li id="R_5.025.028ab">tad alaṃ krūravākyair vaḥ sāntvam evābhidhīyatām</li>
        <li id="R_5.025.028cd">abhiyācāma vaidehīm etad dhi mama rocate</li>
    </ul>
    <ul id="R_5.025.029">
        <li id="R_5.025.029ab">yasyā hy evaṃ vidhaḥ svapno duḥkhitāyāḥ pradṛśyate</li>
        <li id="R_5.025.029cd">sā duḥkhair bahubhir muktā priyaṃ prāpnoty anuttamam</li>
    </ul>
    <ul id="R_5.025.030">
        <li id="R_5.025.030ab">bhartsitām api yācadhvaṃ rākṣasyaḥ kiṃ vivakṣayā</li>
        <li id="R_5.025.030cd">rāghavād dhi bhayaṃ ghoraṃ rākṣasānām upasthitam</li>
    </ul>
    <ul id="R_5.025.031">
        <li id="R_5.025.031ab">praṇipāta prasannā hi maithilī janakātmajā</li>
        <li id="R_5.025.031cd">alam eṣā paritrātuṃ rākṣasyo mahato bhayāt</li>
    </ul>
    <ul id="R_5.025.032">
        <li id="R_5.025.032ab">api cāsyā viśālākṣyā na kiṃ cid upalakṣaye</li>
        <li id="R_5.025.032cd">viruddham api cāṅgeṣu susūkṣmam api lakṣmaṇam</li>
    </ul>
    <ul id="R_5.025.033">
        <li id="R_5.025.033ab">chāyā vaiguṇya mātraṃ tu śaṅke duḥkham upasthitam</li>
        <li id="R_5.025.033cd">aduḥkhārhām imāṃ devīṃ vaihāyasam upasthitām</li>
    </ul>
    <ul id="R_5.025.034">
        <li id="R_5.025.034ab">arthasiddhiṃ tu vaidehyāḥ paśyāmy aham upasthitām</li>
        <li id="R_5.025.034cd">rākṣasendravināśaṃ ca vijayaṃ rāghavasya ca</li>
    </ul>
    <ul id="R_5.025.035">
        <li id="R_5.025.035ab">nimittabhūtam etat tu śrotum asyā mahat priyam</li>
        <li id="R_5.025.035cd">dṛśyate ca sphurac cakṣuḥ padmapatram ivāyatam</li>
    </ul>
    <ul id="R_5.025.036">
        <li id="R_5.025.036ab">īṣac ca hṛṣito vāsyā dakṣiṇāyā hy adakṣiṇaḥ</li>
        <li id="R_5.025.036cd">akasmād eva vaidehyā bāhur ekaḥ prakampate</li>
    </ul>
    <ul id="R_5.025.037">
        <li id="R_5.025.037ab">kareṇuhastapratimaḥ savyaś corur anuttamaḥ</li>
        <li id="R_5.025.037cd">vepan sūcayatīvāsyā rāghavaṃ purataḥ sthitam</li>
    </ul>
    <ul id="R_5.025.038">
        <li id="R_5.025.038ab">
            <seg type="pāda" n="a">pakṣī ca śākhā nilayaṃ praviṣṭaḥ</seg>
            <seg type="pāda" n="b">punaḥ punaś cottamasāntvavādī</seg>
        </li>
        <li id="R_5.025.038cd">
            <seg type="pāda" n="c">sukhāgatāṃ vācam udīrayāṇaḥ</seg>
            <seg type="pāda" n="d">punaḥ punaś codayatīva hṛṣṭaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.026.001">
        <li id="R_5.026.001ab">
            <seg type="pāda" n="a">sā rākṣasendrasya vaco niśamya</seg>
            <seg type="pāda" n="b">tad rāvaṇasyāpriyam apriyārtā</seg>
        </li>
        <li id="R_5.026.001cd">
            <seg type="pāda" n="c">sītā vitatrāsa yathā vanānte</seg>
            <seg type="pāda" n="d">siṃhābhipannā gajarājakanyā</seg>
        </li>
    </ul>
    <ul id="R_5.026.002">
        <li id="R_5.026.002ab">
            <seg type="pāda" n="a">sā rākṣasī madhyagatā ca bhīrur</seg>
            <seg type="pāda" n="b">vāgbhir bhṛśaṃ rāvaṇatarjitā ca</seg>
        </li>
        <li id="R_5.026.002cd">
            <seg type="pāda" n="c">kāntāramadhye vijane visṛṣṭā</seg>
            <seg type="pāda" n="d">bāleva kanyā vilalāpa sītā</seg>
        </li>
    </ul>
    <ul id="R_5.026.003">
        <li id="R_5.026.003ab">
            <seg type="pāda" n="a">satyaṃ batedaṃ pravadanti loke</seg>
            <seg type="pāda" n="b">nākālamṛtyur bhavatīti santaḥ</seg>
        </li>
        <li id="R_5.026.003cd">
            <seg type="pāda" n="c">yatrāham evaṃ paribhartsyamānā</seg>
            <seg type="pāda" n="d">jīvāmi kiṃ cit kṣaṇam apy apuṇyā</seg>
        </li>
    </ul>
    <ul id="R_5.026.004">
        <li id="R_5.026.004ab">
            <seg type="pāda" n="a">sukhād vihīnaṃ bahuduḥkhapūrṇam</seg>
            <seg type="pāda" n="b">idaṃ tu nūnaṃ hṛdayaṃ sthiraṃ me</seg>
        </li>
        <li id="R_5.026.004cd">
            <seg type="pāda" n="c">vidīryate yan na sahasradhādya</seg>
            <seg type="pāda" n="d">vajrāhataṃ śṛṅgam ivācalasya</seg>
        </li>
    </ul>
    <ul id="R_5.026.005">
        <li id="R_5.026.005ab">
            <seg type="pāda" n="a">naivāsti nūnaṃ mama doṣam atra</seg>
            <seg type="pāda" n="b">vadhyāham asyāpriyadarśanasya</seg>
        </li>
        <li id="R_5.026.005cd">
            <seg type="pāda" n="c">bhāvaṃ na cāsyāham anupradātum</seg>
            <seg type="pāda" n="d">alaṃ dvijo mantram ivādvijāya</seg>
        </li>
    </ul>
    <ul id="R_5.026.006">
        <li id="R_5.026.006ab">
            <seg type="pāda" n="a">nūnaṃ mamāṅgāny acirād anāryaḥ</seg>
            <seg type="pāda" n="b">śastraiḥ śitaiś chetsyati rākṣasendraḥ</seg>
        </li>
        <li id="R_5.026.006cd">
            <seg type="pāda" n="c">tasminn anāgacchati lokanāthe</seg>
            <seg type="pāda" n="d">garbhasthajantor iva śalyakṛntaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.026.007">
        <li id="R_5.026.007ab">
            <seg type="pāda" n="a">duḥkhaṃ batedaṃ mama duḥkhitāyā</seg>
            <seg type="pāda" n="b">māsau cirāyābhigamiṣyato dvau</seg>
        </li>
        <li id="R_5.026.007cd">
            <seg type="pāda" n="c">baddhasya vadhyasya yathā niśānte</seg>
            <seg type="pāda" n="d">rājāparādhād iva taskarasya</seg>
        </li>
    </ul>
    <ul id="R_5.026.008">
        <li id="R_5.026.008ab">
            <seg type="pāda" n="a">hā rāma hā lakṣmaṇa hā sumitre</seg>
            <seg type="pāda" n="b">hā rāma mātaḥ saha me jananyā</seg>
        </li>
        <li id="R_5.026.008cd">
            <seg type="pāda" n="c">eṣā vipadyāmy aham alpabhāgyā</seg>
            <seg type="pāda" n="d">mahārṇave naur iva mūḍha vātā</seg>
        </li>
    </ul>
    <ul id="R_5.026.009">
        <li id="R_5.026.009ab">
            <seg type="pāda" n="a">tarasvinau dhārayatā mṛgasya</seg>
            <seg type="pāda" n="b">sattvena rūpaṃ manujendraputrau</seg>
        </li>
        <li id="R_5.026.009cd">
            <seg type="pāda" n="c">nūnaṃ viśastau mama kāraṇāt tau</seg>
            <seg type="pāda" n="d">siṃharṣabhau dvāv iva vaidyutena</seg>
        </li>
    </ul>
    <ul id="R_5.026.010">
        <li id="R_5.026.010ab">
            <seg type="pāda" n="a">nūnaṃ sa kālo mṛgarūpadhārī</seg>
            <seg type="pāda" n="b">mām alpabhāgyāṃ lulubhe tadānīm</seg>
        </li>
        <li id="R_5.026.010cd">
            <seg type="pāda" n="c">yatrāryaputraṃ visasarja mūḍhā</seg>
            <seg type="pāda" n="d">rāmānujaṃ lakṣmaṇapūrvakaṃ ca</seg>
        </li>
    </ul>
    <ul id="R_5.026.011">
        <li id="R_5.026.011ab">
            <seg type="pāda" n="a">hā rāma satyavrata dīrghavāho</seg>
            <seg type="pāda" n="b">hā pūrṇacandrapratimānavaktra</seg>
        </li>
        <li id="R_5.026.011cd">
            <seg type="pāda" n="c">hā jīvalokasya hitaḥ priyaś ca</seg>
            <seg type="pāda" n="d">vadhyāṃ na māṃ vetsi hi rākṣasānām</seg>
        </li>
    </ul>
    <ul id="R_5.026.012">
        <li id="R_5.026.012ab">
            <seg type="pāda" n="a">ananyadevatvam iyaṃ kṣamā ca</seg>
            <seg type="pāda" n="b">bhūmau ca śayyā niyamaś ca dharme</seg>
        </li>
        <li id="R_5.026.012cd">
            <seg type="pāda" n="c">pativratātvaṃ viphalaṃ mamedaṃ</seg>
            <seg type="pāda" n="d">kṛtaṃ kṛtaghneṣv iva mānuṣāṇām</seg>
        </li>
    </ul>
    <ul id="R_5.026.013">
        <li id="R_5.026.013ab">
            <seg type="pāda" n="a">mogho hi dharmaś carito mamāyaṃ</seg>
            <seg type="pāda" n="b">tathaikapatnītvam idaṃ nirartham</seg>
        </li>
        <li id="R_5.026.013cd">
            <seg type="pāda" n="c">yā tvāṃ na paśyāmi kṛśā vivarṇā</seg>
            <seg type="pāda" n="d">hīnā tvayā saṃgamane nirāśā</seg>
        </li>
    </ul>
    <ul id="R_5.026.014">
        <li id="R_5.026.014ab">
            <seg type="pāda" n="a">pitur nirdeśaṃ niyamena kṛtvā</seg>
            <seg type="pāda" n="b">vanān nivṛttaś caritavrataś ca</seg>
        </li>
        <li id="R_5.026.014cd">
            <seg type="pāda" n="c">strībhis tu manye vipulekṣaṇābhiḥ</seg>
            <seg type="pāda" n="d">saṃraṃsyase vītabhayaḥ kṛtārthaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.026.015">
        <li id="R_5.026.015ab">
            <seg type="pāda" n="a">ahaṃ tu rāma tvayi jātakāmā</seg>
            <seg type="pāda" n="b">ciraṃ vināśāya nibaddhabhāvā</seg>
        </li>
        <li id="R_5.026.015cd">
            <seg type="pāda" n="c">moghaṃ caritvātha tapovrataṃ ca</seg>
            <seg type="pāda" n="d">tyakṣyāmi dhig jīvitam alpabhāgyā</seg>
        </li>
    </ul>
    <ul id="R_5.026.016">
        <li id="R_5.026.016ab">
            <seg type="pāda" n="a">sā jīvitaṃ kṣipram ahaṃ tyajeyaṃ</seg>
            <seg type="pāda" n="b">viṣeṇa śastreṇa śitena vāpi</seg>
        </li>
        <li id="R_5.026.016cd">
            <seg type="pāda" n="c">viṣasya dātā na tu me 'sti kaś cic</seg>
            <seg type="pāda" n="d">chastrasya vā veśmani rākṣasasya</seg>
        </li>
    </ul>
    <ul id="R_5.026.017">
        <li id="R_5.026.017ab">
            <seg type="pāda" n="a">śokābhitaptā bahudhā vicintya</seg>
            <seg type="pāda" n="b">sītātha veṇyudgrathanaṃ gṛhītvā</seg>
        </li>
        <li id="R_5.026.017cd">
            <seg type="pāda" n="c">udbadhya veṇyudgrathanena śīghram</seg>
            <seg type="pāda" n="d">ahaṃ gamiṣyāmi yamasya mūlam</seg>
        </li>
    </ul>
    <ul id="R_5.026.018">
        <li id="R_5.026.018ab">
            <seg type="pāda" n="a">itīva sītā bahudhā vilapya</seg>
            <seg type="pāda" n="b">sarvātmanā rāmam anusmarantī</seg>
        </li>
        <li id="R_5.026.018cd">
            <seg type="pāda" n="c">pravepamānā pariśuṣkavaktrā</seg>
            <seg type="pāda" n="d">nagottamaṃ puṣpitam āsasāda</seg>
        </li>
    </ul>
    <ul id="R_5.026.019">
        <li id="R_5.026.019ab">
            <seg type="pāda" n="a">upasthitā sā mṛdur sarvagātrī</seg>
            <seg type="pāda" n="b">śākhāṃ gṛhītvātha nagasya tasya</seg>
        </li>
        <li id="R_5.026.019cd">
            <seg type="pāda" n="c">tasyās tu rāmaṃ pravicintayantyā</seg>
            <seg type="pāda" n="d">rāmānujaṃ svaṃ ca kulaṃ śubhāṅgyāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.026.020">
        <li id="R_5.026.020ab">
            <seg type="pāda" n="a">śokānimittāni tadā bahūni</seg>
            <seg type="pāda" n="b">dhairyārjitāni pravarāṇi loke</seg>
        </li>
        <li id="R_5.026.020cd">
            <seg type="pāda" n="c">prādurnimittāni tadā babhūvuḥ</seg>
            <seg type="pāda" n="d">purāpi siddhāny upalakṣitāni</seg>
        </li>
    </ul>
    <ul id="R_5.027.001">
        <li id="R_5.027.001ab">
            <seg type="pāda" n="a">tathāgatāṃ tāṃ vyathitām aninditāṃ</seg>
            <seg type="pāda" n="b">vyapetaharṣāṃ paridīnamānasām</seg>
        </li>
        <li id="R_5.027.001cd">
            <seg type="pāda" n="c">śubhāṃ nimittāni śubhāni bhejire</seg>
            <seg type="pāda" n="d">naraṃ śriyā juṣṭam ivopajīvinaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.027.002">
        <li id="R_5.027.002ab">
            <seg type="pāda" n="a">tasyāḥ śubhaṃ vāmam arālapakṣma</seg>
            <seg type="pāda" n="b">rājīvṛtaṃ kṛṣṇaviśālaśuklam</seg>
        </li>
        <li id="R_5.027.002cd">
            <seg type="pāda" n="c">prāspandataikaṃ nayanaṃ sukeśyā</seg>
            <seg type="pāda" n="d">mīnāhataṃ padmam ivābhitāmram</seg>
        </li>
    </ul>
    <ul id="R_5.027.003">
        <li id="R_5.027.003ab">
            <seg type="pāda" n="a">bhujaś ca cārvañcitapīnavṛttaḥ</seg>
            <seg type="pāda" n="b">parārdhya kālāgurucandanārhaḥ</seg>
        </li>
        <li id="R_5.027.003cd">
            <seg type="pāda" n="c">anuttamenādhyuṣitaḥ priyeṇa</seg>
            <seg type="pāda" n="d">cireṇa vāmaḥ samavepatāśu</seg>
        </li>
    </ul>
    <ul id="R_5.027.004">
        <li id="R_5.027.004ab">
            <seg type="pāda" n="a">gajendrahastapratimaś ca pīnas</seg>
            <seg type="pāda" n="b">tayor dvayoḥ saṃhatayoḥ sujātaḥ</seg>
        </li>
        <li id="R_5.027.004cd">
            <seg type="pāda" n="c">praspandamānaḥ punar ūrur asyā</seg>
            <seg type="pāda" n="d">rāmaṃ purastāt sthitam ācacakṣe</seg>
        </li>
    </ul>
    <ul id="R_5.027.005">
        <li id="R_5.027.005ab">
            <seg type="pāda" n="a">śubhaṃ punar hemasamānavarṇam</seg>
            <seg type="pāda" n="b">īṣadrajodhvastam ivāmalākṣyāḥ</seg>
        </li>
        <li id="R_5.027.005cd">
            <seg type="pāda" n="c">vāsaḥ sthitāyāḥ śikharāgradantyāḥ</seg>
            <seg type="pāda" n="d">kiṃ cit parisraṃsata cārugātryāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.027.006">
        <li id="R_5.027.006ab">
            <seg type="pāda" n="a">etair nimittair aparaiś ca subhrūḥ</seg>
            <seg type="pāda" n="b">saṃbodhitā prāg api sādhusiddhaiḥ</seg>
        </li>
        <li id="R_5.027.006cd">
            <seg type="pāda" n="c">vātātapaklāntam iva pranaṣṭaṃ</seg>
            <seg type="pāda" n="d">varṣeṇa bījaṃ pratisaṃjaharṣa</seg>
        </li>
    </ul>
    <ul id="R_5.027.007">
        <li id="R_5.027.007ab">
            <seg type="pāda" n="a">tasyāḥ punar bimbaphalopamauṣṭhaṃ</seg>
            <seg type="pāda" n="b">svakṣibhrukeśāntam arālapakṣma</seg>
        </li>
        <li id="R_5.027.007cd">
            <seg type="pāda" n="c">vaktraṃ babhāse sitaśukladaṃṣṭraṃ</seg>
            <seg type="pāda" n="d">rāhor mukhāc candra iva pramuktaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.027.008">
        <li id="R_5.027.008ab">
            <seg type="pāda" n="a">sā vītaśokā vyapanītatandrī</seg>
            <seg type="pāda" n="b">śāntajvarā harṣavibuddhasattvā</seg>
        </li>
        <li id="R_5.027.008cd">
            <seg type="pāda" n="c">aśobhatāryā vadanena śukle</seg>
            <seg type="pāda" n="d">śītānśunā rātrir ivoditena</seg>
        </li>
    </ul>
    <ul id="R_5.028.001">
        <li id="R_5.028.001ab">hanumān api vikrāntaḥ sarvaṃ śuśrāva tattvataḥ</li>
        <li id="R_5.028.001cd">sītāyās trijaṭāyāś ca rākṣasīnāṃ ca tarjanam</li>
    </ul>
    <ul id="R_5.028.002">
        <li id="R_5.028.002ab">avekṣamāṇas tāṃ devīṃ devatām iva nandane</li>
        <li id="R_5.028.002cd">tato bahuvidhāṃ cintāṃ cintayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.028.003">
        <li id="R_5.028.003ab">yāṃ kapīnāṃ sahasrāṇi subahūny ayutāni ca</li>
        <li id="R_5.028.003cd">dikṣu sarvāsu mārgante seyam āsāditā mayā</li>
    </ul>
    <ul id="R_5.028.004">
        <li id="R_5.028.004ab">cāreṇa tu suyuktena śatroḥ śaktim avekṣitā</li>
        <li id="R_5.028.004cd">gūḍhena caratā tāvad avekṣitam idaṃ mayā</li>
    </ul>
    <ul id="R_5.028.005">
        <li id="R_5.028.005ab">rākṣasānāṃ viśeṣaś ca purī ceyam avekṣitā</li>
        <li id="R_5.028.005cd">rākṣasādhipater asya prabhāvo rāvaṇasya ca</li>
    </ul>
    <ul id="R_5.028.006">
        <li id="R_5.028.006ab">yuktaṃ tasyāprameyasya sarvasattvadayāvataḥ</li>
        <li id="R_5.028.006cd">samāśvāsayituṃ bhāryāṃ patidarśanakāṅkṣiṇīm</li>
    </ul>
    <ul id="R_5.028.007">
        <li id="R_5.028.007ab">aham āśvāsayāmy enāṃ pūrṇacandranibhānanām</li>
        <li id="R_5.028.007cd">adṛṣṭaduḥkhāṃ duḥkhasya na hy antam adhigacchatīm</li>
    </ul>
    <ul id="R_5.028.008">
        <li id="R_5.028.008ab">yadi hy aham imāṃ devīṃ śokopahatacetanām</li>
        <li id="R_5.028.008cd">anāśvāsya gamiṣyāmi doṣavad gamanaṃ bhavet</li>
    </ul>
    <ul id="R_5.028.009">
        <li id="R_5.028.009ab">gate hi mayi tatreyaṃ rājaputrī yaśasvinī</li>
        <li id="R_5.028.009cd">paritrāṇam avindantī jānakī jīvitaṃ tyajet</li>
    </ul>
    <ul id="R_5.028.010">
        <li id="R_5.028.010ab">mayā ca sa mahābāhuḥ pūrṇacandranibhānanaḥ</li>
        <li id="R_5.028.010cd">samāśvāsayituṃ nyāyyaḥ sītādarśanalālasaḥ</li>
    </ul>
    <ul id="R_5.028.011">
        <li id="R_5.028.011ab">niśācarīṇāṃ pratyakṣam akṣamaṃ cābhibhāṣaṇam</li>
        <li id="R_5.028.011cd">kathaṃ nu khalu kartavyam idaṃ kṛcchra gato hy aham</li>
    </ul>
    <ul id="R_5.028.012">
        <li id="R_5.028.012ab">anena rātriśeṣeṇa yadi nāśvāsyate mayā</li>
        <li id="R_5.028.012cd">sarvathā nāsti saṃdehaḥ parityakṣyati jīvitam</li>
    </ul>
    <ul id="R_5.028.013">
        <li id="R_5.028.013ab">rāmaś ca yadi pṛcchen māṃ kiṃ māṃ sītābravīd vacaḥ</li>
        <li id="R_5.028.013cd">kim ahaṃ taṃ pratibrūyām asaṃbhāṣya sumadhyamām</li>
    </ul>
    <ul id="R_5.028.014">
        <li id="R_5.028.014ab">sītāsaṃdeśarahitaṃ mām itas tvarayā gatam</li>
        <li id="R_5.028.014cd">nirdahed api kākutsthaḥ kruddhas tīvreṇa cakṣuṣā</li>
    </ul>
    <ul id="R_5.028.015">
        <li id="R_5.028.015ab">yadi ced yojayiṣyāmi bhartāraṃ rāmakāraṇāt</li>
        <li id="R_5.028.015cd">vyartham āgamanaṃ tasya sasainyasya bhaviṣyati</li>
    </ul>
    <ul id="R_5.028.016">
        <li id="R_5.028.016ab">antaraṃ tv aham āsādya rākṣasīnām iha sthitaḥ</li>
        <li id="R_5.028.016cd">śanair āśvāsayiṣyāmi saṃtāpabahulām imām</li>
    </ul>
    <ul id="R_5.028.017">
        <li id="R_5.028.017ab">ahaṃ hy atitanuś caiva vanaraś ca viśeṣataḥ</li>
        <li id="R_5.028.017cd">vācaṃ codāhariṣyāmi mānuṣīm iha saṃskṛtām</li>
    </ul>
    <ul id="R_5.028.018">
        <li id="R_5.028.018ab">yadi vācaṃ pradāsyāmi dvijātir iva saṃskṛtām</li>
        <li id="R_5.028.018cd">rāvaṇaṃ manyamānā māṃ sītā bhītā bhaviṣyati</li>
    </ul>
    <ul id="R_5.028.019">
        <li id="R_5.028.019ab">avaśyam eva vaktavyaṃ mānuṣaṃ vākyam arthavat</li>
        <li id="R_5.028.019cd">mayā sāntvayituṃ śakyā nānyatheyam aninditā</li>
    </ul>
    <ul id="R_5.028.020">
        <li id="R_5.028.020ab">seyam ālokya me rūpaṃ jānakī bhāṣitaṃ tathā</li>
        <li id="R_5.028.020cd">rakṣobhis trāsitā pūrvaṃ bhūyas trāsaṃ gamiṣyati</li>
    </ul>
    <ul id="R_5.028.021">
        <li id="R_5.028.021ab">tato jātaparitrāsā śabdaṃ kuryān manasvinī</li>
        <li id="R_5.028.021cd">jānamānā viśālākṣī rāvaṇaṃ kāmarūpiṇam</li>
    </ul>
    <ul id="R_5.028.022">
        <li id="R_5.028.022ab">sītayā ca kṛte śabde sahasā rākṣasīgaṇaḥ</li>
        <li id="R_5.028.022cd">nānāpraharaṇo ghoraḥ sameyād antakopamaḥ</li>
    </ul>
    <ul id="R_5.028.023">
        <li id="R_5.028.023ab">tato māṃ saṃparikṣipya sarvato vikṛtānanāḥ</li>
        <li id="R_5.028.023cd">vadhe ca grahaṇe caiva kuryur yatnaṃ yathābalam</li>
    </ul>
    <ul id="R_5.028.024">
        <li id="R_5.028.024ab">taṃ māṃ śākhāḥ praśākhāś ca skandhāṃś cottamaśākhinām</li>
        <li id="R_5.028.024cd">dṛṣṭvā viparidhāvantaṃ bhaveyur bhayaśaṅkitāḥ</li>
    </ul>
    <ul id="R_5.028.025">
        <li id="R_5.028.025ab">mama rūpaṃ ca saṃprekṣya vanaṃ vicarato mahat</li>
        <li id="R_5.028.025cd">rākṣasyo bhayavitrastā bhaveyur vikṛtānanāḥ</li>
    </ul>
    <ul id="R_5.028.026">
        <li id="R_5.028.026ab">tataḥ kuryuḥ samāhvānaṃ rākṣasyo rakṣasām api</li>
        <li id="R_5.028.026cd">rākṣasendraniyuktānāṃ rākṣasendraniveśane</li>
    </ul>
    <ul id="R_5.028.027">
        <li id="R_5.028.027ab">te śūlaśaranistriṃśa vividhāyudhapāṇayaḥ</li>
        <li id="R_5.028.027cd">āpateyur vimarde 'smin vegenodvignakāriṇaḥ</li>
    </ul>
    <ul id="R_5.028.028">
        <li id="R_5.028.028ab">saṃkruddhas tais tu parito vidhaman rakṣasāṃ balam</li>
        <li id="R_5.028.028cd">śaknuyaṃ na tu saṃprāptuṃ paraṃ pāraṃ mahodadheḥ</li>
    </ul>
    <ul id="R_5.028.029">
        <li id="R_5.028.029ab">māṃ vā gṛhṇīyur āplutya bahavaḥ śīghrakāriṇaḥ</li>
        <li id="R_5.028.029cd">syād iyaṃ cāgṛhītārthā mama ca grahaṇaṃ bhavet</li>
    </ul>
    <ul id="R_5.028.030">
        <li id="R_5.028.030ab">hiṃsābhirucayo hiṃsyur imāṃ vā janakātmajām</li>
        <li id="R_5.028.030cd">vipannaṃ syāt tataḥ kāryaṃ rāmasugrīvayor idam</li>
    </ul>
    <ul id="R_5.028.031">
        <li id="R_5.028.031ab">uddeśe naṣṭamārge 'smin rākṣasaiḥ parivārite</li>
        <li id="R_5.028.031cd">sāgareṇa parikṣipte gupte vasati jānakī</li>
    </ul>
    <ul id="R_5.028.032">
        <li id="R_5.028.032ab">viśaste vā gṛhīte vā rakṣobhir mayi saṃyuge</li>
        <li id="R_5.028.032cd">nānyaṃ paśyāmi rāmasya sahāyaṃ kāryasādhane</li>
    </ul>
    <ul id="R_5.028.033">
        <li id="R_5.028.033ab">vimṛśaṃś ca na paśyāmi yo hate mayi vānaraḥ</li>
        <li id="R_5.028.033cd">śatayojanavistīrṇaṃ laṅghayeta mahodadhim</li>
    </ul>
    <ul id="R_5.028.034">
        <li id="R_5.028.034ab">kāmaṃ hantuṃ samartho 'smi sahasrāṇy api rakṣasām</li>
        <li id="R_5.028.034cd">na tu śakṣyāmi saṃprāptuṃ paraṃ pāraṃ mahodadheḥ</li>
    </ul>
    <ul id="R_5.028.035">
        <li id="R_5.028.035ab">asatyāni ca yuddhāni saṃśayo me na rocate</li>
        <li id="R_5.028.035cd">kaś ca niḥsaṃśayaṃ kāryaṃ kuryāt prājñaḥ sasaṃśayam</li>
    </ul>
    <ul id="R_5.028.036">
        <li id="R_5.028.036ab">eṣa doṣo mahān hi syān mama sītābhibhāṣaṇe</li>
        <li id="R_5.028.036cd">prāṇatyāgaś ca vaidehyā bhaved anabhibhāṣaṇe</li>
    </ul>
    <ul id="R_5.028.037">
        <li id="R_5.028.037ab">bhūtāś cārthā vinaśyanti deśakālavirodhitāḥ</li>
        <li id="R_5.028.037cd">viklavaṃ dūtam āsādya tamaḥ sūryodaye yathā</li>
    </ul>
    <ul id="R_5.028.038">
        <li id="R_5.028.038ab">arthānarthāntare buddhir niścitāpi na śobhate</li>
        <li id="R_5.028.038cd">ghātayanti hi kāryāṇi dūtāḥ paṇḍitamāninaḥ</li>
    </ul>
    <ul id="R_5.028.039">
        <li id="R_5.028.039ab">na vinaśyet kathaṃ kāryaṃ vaiklavyaṃ na kathaṃ bhavet</li>
        <li id="R_5.028.039cd">laṅghanaṃ ca samudrasya kathaṃ nu na vṛthā bhavet</li>
    </ul>
    <ul id="R_5.028.040">
        <li id="R_5.028.040ab">kathaṃ nu khalu vākyaṃ me śṛṇuyān nodvijeta ca</li>
        <li id="R_5.028.040cd">iti saṃcintya hanumāṃś cakāra matimān matim</li>
    </ul>
    <ul id="R_5.028.041">
        <li id="R_5.028.041ab">rāmam akliṣṭakarmāṇaṃ svabandhum anukīrtayan</li>
        <li id="R_5.028.041cd">nainām udvejayiṣyāmi tad bandhugatamānasām</li>
    </ul>
    <ul id="R_5.028.042">
        <li id="R_5.028.042ab">ikṣvākūṇāṃ variṣṭhasya rāmasya viditātmanaḥ</li>
        <li id="R_5.028.042cd">śubhāni dharmayuktāni vacanāni samarpayan</li>
    </ul>
    <ul id="R_5.028.043">
        <li id="R_5.028.043ab">śrāvayiṣyāmi sarvāṇi madhurāṃ prabruvan giram</li>
        <li id="R_5.028.043cd">śraddhāsyati yathā hīyaṃ tathā sarvaṃ samādadhe</li>
    </ul>
    <ul id="R_5.028.044">
        <li id="R_5.028.044ab">
            <seg type="pāda" n="a">iti sa bahuvidhaṃ mahānubhāvo</seg>
            <seg type="pāda" n="b">jagatipateḥ pramadām avekṣamāṇaḥ</seg>
        </li>
        <li id="R_5.028.044cd">
            <seg type="pāda" n="c">madhuram avitathaṃ jagāda vākyaṃ</seg>
            <seg type="pāda" n="d">drumaviṭapāntaram āsthito hanūmān</seg>
        </li>
    </ul>
    <ul id="R_5.029.001">
        <li id="R_5.029.001ab">evaṃ bahuvidhāṃ cintāṃ cintayitva mahākapiḥ</li>
        <li id="R_5.029.001cd">saṃśrave madhuraṃ vākyaṃ vaidehyā vyājahāra ha</li>
    </ul>
    <ul id="R_5.029.002">
        <li id="R_5.029.002ab">rājā daśaratho nāma rathakuñjaravājinām</li>
        <li id="R_5.029.002cd">puṇyaśīlo mahākīrtir ṛjur āsīn mahāyaśāḥ</li>
        <li id="R_5.029.002ef">cakravartikule jātaḥ puraṃdarasamo bale</li>
    </ul>
    <ul id="R_5.029.003">
        <li id="R_5.029.003ab">ahiṃsāratir akṣudro ghṛṇī satyaparākramaḥ</li>
        <li id="R_5.029.003cd">mukhyaś cekṣvākuvaṃśasya lakṣmīvāṃl lakṣmivardhanaḥ</li>
    </ul>
    <ul id="R_5.029.004">
        <li id="R_5.029.004ab">pārthivavyañjanair yuktaḥ pṛthuśrīḥ pārthivarṣabhaḥ</li>
        <li id="R_5.029.004cd">pṛthivyāṃ caturantayāṃ viśrutaḥ sukhadaḥ sukhī</li>
    </ul>
    <ul id="R_5.029.005">
        <li id="R_5.029.005ab">tasya putraḥ priyo jyeṣṭhas tārādhipanibhānanaḥ</li>
        <li id="R_5.029.005cd">rāmo nāma viśeṣajñaḥ śreṣṭhaḥ sarvadhanuṣmatām</li>
    </ul>
    <ul id="R_5.029.006">
        <li id="R_5.029.006ab">rakṣitā svasya vṛttasya svajanasyāpi rakṣitā</li>
        <li id="R_5.029.006cd">rakṣitā jīvalokasya dharmasya ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_5.029.007">
        <li id="R_5.029.007ab">tasya satyābhisaṃdhasya vṛddhasya vacanāt pituḥ</li>
        <li id="R_5.029.007cd">sabhāryaḥ saha ca bhrātrā vīraḥ pravrajito vanam</li>
    </ul>
    <ul id="R_5.029.008">
        <li id="R_5.029.008ab">tena tatra mahāraṇye mṛgayāṃ paridhāvatā</li>
        <li id="R_5.029.008cd">janasthānavadhaṃ śrutvā hatau ca kharadūṣaṇau</li>
        <li id="R_5.029.008ef">tatas tv amarṣāpahṛtā jānakī rāvaṇena tu</li>
    </ul>
    <ul id="R_5.029.009">
        <li id="R_5.029.009ab">yathārūpāṃ yathāvarṇāṃ yathālakṣmīṃ viniścitām</li>
        <li id="R_5.029.009cd">aśrauṣaṃ rāghavasyāhaṃ seyam āsāditā mayā</li>
    </ul>
    <ul id="R_5.029.010">
        <li id="R_5.029.010ab">virarāmaivam uktvāsau vācaṃ vānarapuṃgavaḥ</li>
        <li id="R_5.029.010cd">jānakī cāpi tac chrutvā vismayaṃ paramaṃ gatā</li>
    </ul>
    <ul id="R_5.029.011">
        <li id="R_5.029.011ab">tataḥ sā vakrakeśāntā sukeśī keśasaṃvṛtam</li>
        <li id="R_5.029.011cd">unnamya vadanaṃ bhīruḥ śiṃśapāvṛkṣam aikṣata</li>
    </ul>
    <ul id="R_5.029.012">
        <li id="R_5.029.012ab">
            <seg type="pāda" n="a">sā tiryag ūrdhvaṃ ca tathāpy adhastān</seg>
            <seg type="pāda" n="b">nirīkṣamāṇā tam acintya buddhim</seg>
        </li>
        <li id="R_5.029.012cd">
            <seg type="pāda" n="c">dadarśa piṅgādhipater amātyaṃ</seg>
            <seg type="pāda" n="d">vātātmajaṃ sūryam ivodayastham</seg>
        </li>
    </ul>
    <ul id="R_5.030.001">
        <li id="R_5.030.001ab">tataḥ śākhāntare līnaṃ dṛṣṭvā calitamānasā</li>
        <li id="R_5.030.001cd">sā dadarśa kapiṃ tatra praśritaṃ priyavādinam</li>
    </ul>
    <ul id="R_5.030.002">
        <li id="R_5.030.002ab">sā tu dṛṣṭvā hariśreṣṭhaṃ vinītavad upasthitam</li>
        <li id="R_5.030.002cd">maithilī cintayām āsa svapno 'yam iti bhāminī</li>
    </ul>
    <ul id="R_5.030.003">
        <li id="R_5.030.003ab">
            <seg type="pāda" n="a">sā taṃ samīkṣyaiva bhṛśaṃ visaṃjñā</seg>
            <seg type="pāda" n="b">gatāsukalpeva babhūva sītā</seg>
        </li>
        <li id="R_5.030.003cd">
            <seg type="pāda" n="c">cireṇa saṃjñāṃ pratilabhya caiva</seg>
            <seg type="pāda" n="d">vicintayām āsa viśālanetrā</seg>
        </li>
    </ul>
    <ul id="R_5.030.004">
        <li id="R_5.030.004ab">
            <seg type="pāda" n="a">svapno mayāyaṃ vikṛto 'dya dṛṣṭaḥ</seg>
            <seg type="pāda" n="b">śākhāmṛgaḥ śāstragaṇair niṣiddhaḥ</seg>
        </li>
        <li id="R_5.030.004cd">
            <seg type="pāda" n="c">svasty astu rāmāya salakṣmaṇāya</seg>
            <seg type="pāda" n="d">tathā pitur me janakasya rājñaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.030.005">
        <li id="R_5.030.005ab">
            <seg type="pāda" n="a">svapno 'pi nāyaṃ na hi me 'sti nidrā</seg>
            <seg type="pāda" n="b">śokena duḥkhena ca pīḍitāyāḥ</seg>
        </li>
        <li id="R_5.030.005cd">
            <seg type="pāda" n="c">sukhaṃ hi me nāsti yato 'smi hīnā</seg>
            <seg type="pāda" n="d">tenendupūrṇapratimānanena</seg>
        </li>
    </ul>
    <ul id="R_5.030.006">
        <li id="R_5.030.006ab">
            <seg type="pāda" n="a">ahaṃ hi tasyādya mano bhavena</seg>
            <seg type="pāda" n="b">saṃpīḍitā tad gatasarvabhāvā</seg>
        </li>
        <li id="R_5.030.006cd">
            <seg type="pāda" n="c">vicintayantī satataṃ tam eva</seg>
            <seg type="pāda" n="d">tathaiva paśyāmi tathā śṛṇomi</seg>
        </li>
    </ul>
    <ul id="R_5.030.007">
        <li id="R_5.030.007ab">
            <seg type="pāda" n="a">manorathaḥ syād iti cintayāmi</seg>
            <seg type="pāda" n="b">tathāpi buddhyā ca vitarkayāmi</seg>
        </li>
        <li id="R_5.030.007cd">
            <seg type="pāda" n="c">kiṃ kāraṇaṃ tasya hi nāsti rūpaṃ</seg>
            <seg type="pāda" n="d">suvyaktarūpaś ca vadaty ayaṃ mām</seg>
        </li>
    </ul>
    <ul id="R_5.030.008">
        <li id="R_5.030.008ab">
            <seg type="pāda" n="a">namo 'stu vācaspataye savajriṇe</seg>
            <seg type="pāda" n="b">svayambhuve caiva hutāśanāya</seg>
        </li>
        <li id="R_5.030.008cd">
            <seg type="pāda" n="c">anena coktaṃ yad idaṃ mamāgrato</seg>
            <seg type="pāda" n="d">vanaukasā tac ca tathāstu nānyathā</seg>
        </li>
    </ul>
    <ul id="R_5.031.001">
        <li id="R_5.031.001ab">tām abravīn mahātejā hanūmān mārutātmajaḥ</li>
        <li id="R_5.031.001cd">śirasy añjalim ādhāya sītāṃ madhurayā girā</li>
    </ul>
    <ul id="R_5.031.002">
        <li id="R_5.031.002ab">kā nu padmapalāśākṣī kliṣṭakauśeyavāsinī</li>
        <li id="R_5.031.002cd">drumasya śākhām ālambya tiṣṭhasi tvam aninditā</li>
    </ul>
    <ul id="R_5.031.003">
        <li id="R_5.031.003ab">kimarthaṃ tava netrābhyāṃ vāri sravati śokajam</li>
        <li id="R_5.031.003cd">puṇḍarīkapalāśābhyāṃ viprakīrṇam ivodakam</li>
    </ul>
    <ul id="R_5.031.004">
        <li id="R_5.031.004ab">surāṇām asurāṇāṃ ca nāgagandharvarakṣasām</li>
        <li id="R_5.031.004cd">yakṣāṇāṃ kiṃnarāṇāṃ ca kā tvaṃ bhavasi śobhane</li>
    </ul>
    <ul id="R_5.031.005">
        <li id="R_5.031.005ab">kā tvaṃ bhavasi rudrāṇāṃ marutāṃ vā varānane</li>
        <li id="R_5.031.005cd">vasūnāṃ vā varārohe devatā pratibhāsi me</li>
    </ul>
    <ul id="R_5.031.006">
        <li id="R_5.031.006ab">kiṃ nu candramasā hīnā patitā vibudhālayāt</li>
        <li id="R_5.031.006cd">rohiṇī jyotiṣāṃ śreṣṭhā śreṣṭhā sarvaguṇānvitā</li>
    </ul>
    <ul id="R_5.031.007">
        <li id="R_5.031.007ab">kopād vā yadi vā mohād bhartāram asitekṣaṇā</li>
        <li id="R_5.031.007cd">vasiṣṭhaṃ kopayitvā tvaṃ nāsi kalyāṇy arundhatī</li>
    </ul>
    <ul id="R_5.031.008">
        <li id="R_5.031.008ab">ko nau putraḥ pitā bhrāta bhartā vā te sumadhyame</li>
        <li id="R_5.031.008cd">asmāl lokād amuṃ lokaṃ gataṃ tvam anuśocasi</li>
    </ul>
    <ul id="R_5.031.009">
        <li id="R_5.031.009ab">vyañjanāni hi te yāni lakṣaṇāni ca lakṣaye</li>
        <li id="R_5.031.009cd">mahiṣī bhūmipālasya rājakanyāsi me matā</li>
    </ul>
    <ul id="R_5.031.010">
        <li id="R_5.031.010ab">rāvaṇena janasthānād balād apahṛtā yadi</li>
        <li id="R_5.031.010cd">sītā tvam asi bhadraṃ te tan mamācakṣva pṛcchataḥ</li>
    </ul>
    <ul id="R_5.031.011">
        <li id="R_5.031.011ab">sā tasya vacanaṃ śrutvā rāmakīrtanaharṣitā</li>
        <li id="R_5.031.011cd">uvāca vākyaṃ vaidehī hanūmantaṃ drumāśritam</li>
    </ul>
    <ul id="R_5.031.012">
        <li id="R_5.031.012ab">duhitā janakasyāhaṃ vaidehasya mahātmanaḥ</li>
        <li id="R_5.031.012cd">sītā ca nāma nāmnāhaṃ bhāryā rāmasya dhīmataḥ</li>
    </ul>
    <ul id="R_5.031.013">
        <li id="R_5.031.013ab">samā dvādaśa tatrāhaṃ rāghavasya niveśane</li>
        <li id="R_5.031.013cd">bhuñjānā mānuṣān bhogān sarvakāmasamṛddhinī</li>
    </ul>
    <ul id="R_5.031.014">
        <li id="R_5.031.014ab">tatas trayodaśe varṣe rājyenekṣvākunandanam</li>
        <li id="R_5.031.014cd">abhiṣecayituṃ rājā sopādhyāyaḥ pracakrame</li>
    </ul>
    <ul id="R_5.031.015">
        <li id="R_5.031.015ab">tasmin saṃbhriyamāṇe tu rāghavasyābhiṣecane</li>
        <li id="R_5.031.015cd">kaikeyī nāma bhartāraṃ devī vacanam abravīt</li>
    </ul>
    <ul id="R_5.031.016">
        <li id="R_5.031.016ab">na pibeyaṃ na khādeyaṃ pratyahaṃ mama bhojanam</li>
        <li id="R_5.031.016cd">eṣa me jīvitasyānto rāmo yady abhiṣicyate</li>
    </ul>
    <ul id="R_5.031.017">
        <li id="R_5.031.017ab">yat tad uktaṃ tvayā vākyaṃ prītyā nṛpatisattama</li>
        <li id="R_5.031.017cd">tac cen na vitathaṃ kāryaṃ vanaṃ gacchatu rāghavaḥ</li>
    </ul>
    <ul id="R_5.031.018">
        <li id="R_5.031.018ab">sa rājā satyavāg devyā varadānam anusmaran</li>
        <li id="R_5.031.018cd">mumoha vacanaṃ śrutvā kaikeyyāḥ krūram apriyam</li>
    </ul>
    <ul id="R_5.031.019">
        <li id="R_5.031.019ab">tatas tu sthaviro rājā satyadharme vyavasthitaḥ</li>
        <li id="R_5.031.019cd">jyeṣṭhaṃ yaśasvinaṃ putraṃ rudan rājyam ayācata</li>
    </ul>
    <ul id="R_5.031.020">
        <li id="R_5.031.020ab">sa pitur vacanaṃ śrīmān abhiṣekāt paraṃ priyam</li>
        <li id="R_5.031.020cd">manasā pūrvam āsādya vācā pratigṛhītavān</li>
    </ul>
    <ul id="R_5.031.021">
        <li id="R_5.031.021ab">dadyān na pratigṛhṇīyān na brūyat kiṃ cid apriyam</li>
        <li id="R_5.031.021cd">api jīvitahetor hi rāmaḥ satyaparākramaḥ</li>
    </ul>
    <ul id="R_5.031.022">
        <li id="R_5.031.022ab">sa vihāyottarīyāṇi mahārhāṇi mahāyaśāḥ</li>
        <li id="R_5.031.022cd">visṛjya manasā rājyaṃ jananyai māṃ samādiśat</li>
    </ul>
    <ul id="R_5.031.023">
        <li id="R_5.031.023ab">sāhaṃ tasyāgratas tūrṇaṃ prasthitā vanacāriṇī</li>
        <li id="R_5.031.023cd">na hi me tena hīnāyā vāsaḥ svarge 'pi rocate</li>
    </ul>
    <ul id="R_5.031.024">
        <li id="R_5.031.024ab">prāg eva tu mahābhāgaḥ saumitrir mitranandanaḥ</li>
        <li id="R_5.031.024cd">pūrvajasyānuyātrārthe drumacīrair alaṃkṛtaḥ</li>
    </ul>
    <ul id="R_5.031.025">
        <li id="R_5.031.025ab">te vayaṃ bhartur ādeśaṃ bahu mānyadṛḍhavratāḥ</li>
        <li id="R_5.031.025cd">praviṣṭāḥ sma purād dṛṣṭaṃ vanaṃ gambhīradarśanam</li>
    </ul>
    <ul id="R_5.031.026">
        <li id="R_5.031.026ab">vasato daṇḍakāraṇye tasyāham amitaujasaḥ</li>
        <li id="R_5.031.026cd">rakṣasāpahṛtā bhāryā rāvaṇena durātmanā</li>
    </ul>
    <ul id="R_5.031.027">
        <li id="R_5.031.027ab">dvau māsau tena me kālo jīvitānugrahaḥ kṛtaḥ</li>
        <li id="R_5.031.027cd">ūrdhvaṃ dvābhyāṃ tu māsābhyāṃ tatas tyakṣyāmi jīvitam</li>
    </ul>
    <ul id="R_5.032.001">
        <li id="R_5.032.001ab">tasyās tad vacanaṃ śrutvā hanūmān hariyūthapaḥ</li>
        <li id="R_5.032.001cd">duḥkhād duḥkhābhibhūtāyāḥ sāntam uttaram abravīt</li>
    </ul>
    <ul id="R_5.032.002">
        <li id="R_5.032.002ab">ahaṃ rāmasya saṃdeśād devi dūtas tavāgataḥ</li>
        <li id="R_5.032.002cd">vaidehi kuśalī rāmas tvāṃ ca kauśalam abravīt</li>
    </ul>
    <ul id="R_5.032.003">
        <li id="R_5.032.003ab">yo brāhmam astraṃ vedāṃś ca veda vedavidāṃ varaḥ</li>
        <li id="R_5.032.003cd">sa tvāṃ dāśarathī rāmo devi kauśalam abravīt</li>
    </ul>
    <ul id="R_5.032.004">
        <li id="R_5.032.004ab">lakṣmaṇaś ca mahātejā bhartus te 'nucaraḥ priyaḥ</li>
        <li id="R_5.032.004cd">kṛtavāñ śokasaṃtaptaḥ śirasā te 'bhivādanam</li>
    </ul>
    <ul id="R_5.032.005">
        <li id="R_5.032.005ab">sā tayoḥ kuśalaṃ devī niśamya narasiṃhayoḥ</li>
        <li id="R_5.032.005cd">prītisaṃhṛṣṭasarvāṅgī hanūmāntam athābravīt</li>
    </ul>
    <ul id="R_5.032.006">
        <li id="R_5.032.006ab">kalyāṇī bata gatheyaṃ laukikī pratibhāti me</li>
        <li id="R_5.032.006cd">ehi jīvantam ānado naraṃ varṣaśatād api</li>
    </ul>
    <ul id="R_5.032.007">
        <li id="R_5.032.007ab">tayoḥ samāgame tasmin prītir utpāditādbhutā</li>
        <li id="R_5.032.007cd">paraspareṇa cālāpaṃ viśvastau tau pracakratuḥ</li>
    </ul>
    <ul id="R_5.032.008">
        <li id="R_5.032.008ab">tasyās tad vacanaṃ śrutvā hanūmān hariyūthapaḥ</li>
        <li id="R_5.032.008cd">sītāyāḥ śokadīnāyāḥ samīpam upacakrame</li>
    </ul>
    <ul id="R_5.032.009">
        <li id="R_5.032.009ab">yathā yathā samīpaṃ sa hanūmān upasarpati</li>
        <li id="R_5.032.009cd">tathā tathā rāvaṇaṃ sā taṃ sītā pariśaṅkate</li>
    </ul>
    <ul id="R_5.032.010">
        <li id="R_5.032.010ab">aho dhig dhik kṛtam idaṃ kathitaṃ hi yad asya me</li>
        <li id="R_5.032.010cd">rūpāntaram upāgamya sa evāyaṃ hi rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.032.011">
        <li id="R_5.032.011ab">tām aśokasya śākhāṃ sā vimuktvā śokakarśitā</li>
        <li id="R_5.032.011cd">tasyām evānavadyāṅgī dharaṇyāṃ samupāviśat</li>
    </ul>
    <ul id="R_5.032.012">
        <li id="R_5.032.012ab">avandata mahābāhus tatas tāṃ janakātmajām</li>
        <li id="R_5.032.012cd">sā cainaṃ bhayavitrastā bhūyo naivābhyudaikṣata</li>
    </ul>
    <ul id="R_5.032.013">
        <li id="R_5.032.013ab">taṃ dṛṣṭvā vandamānaṃ tu sītā śaśinibhānanā</li>
        <li id="R_5.032.013cd">abravīd dīrgham ucchvasya vānaraṃ madhurasvarā</li>
    </ul>
    <ul id="R_5.032.014">
        <li id="R_5.032.014ab">māyāṃ praviṣṭo māyāvī yadi tvaṃ rāvaṇaḥ svayam</li>
        <li id="R_5.032.014cd">utpādayasi me bhūyaḥ saṃtāpaṃ tan na śobhanam</li>
    </ul>
    <ul id="R_5.032.015">
        <li id="R_5.032.015ab">svaṃ parityajya rūpaṃ yaḥ parivrājakarūpadhṛt</li>
        <li id="R_5.032.015cd">janasthāne mayā dṛṣṭas tvaṃ sa evāsi rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.032.016">
        <li id="R_5.032.016ab">upavāsakṛśāṃ dīnāṃ kāmarūpa niśācara</li>
        <li id="R_5.032.016cd">saṃtāpayasi māṃ bhūyaḥ saṃtāpaṃ tan na śobhanam</li>
    </ul>
    <ul id="R_5.032.017">
        <li id="R_5.032.017ab">yadi rāmasya dūtas tvam āgato bhadram astu te</li>
        <li id="R_5.032.017cd">pṛcchāmi tvāṃ hariśreṣṭha priyā rāma kathā hi me</li>
    </ul>
    <ul id="R_5.032.018">
        <li id="R_5.032.018ab">guṇān rāmasya kathaya priyasya mama vānara</li>
        <li id="R_5.032.018cd">cittaṃ harasi me saumya nadīkūlaṃ yathā rayaḥ</li>
    </ul>
    <ul id="R_5.032.019">
        <li id="R_5.032.019ab">aho svapnasya sukhatā yāham evaṃ cirāhṛtā</li>
        <li id="R_5.032.019cd">preṣitaṃ nāma paśyāmi rāghaveṇa vanaukasaṃ</li>
    </ul>
    <ul id="R_5.032.020">
        <li id="R_5.032.020ab">svapne 'pi yady ahaṃ vīraṃ rāghavaṃ sahalakṣmaṇam</li>
        <li id="R_5.032.020cd">paśyeyaṃ nāvasīdeyaṃ svapno 'pi mama matsarī</li>
    </ul>
    <ul id="R_5.032.021">
        <li id="R_5.032.021ab">nāhaṃ svapnam imaṃ manye svapne dṛṣṭvā hi vānaram</li>
        <li id="R_5.032.021cd">na śakyo 'bhyudayaḥ prāptuṃ prāptaś cābhyudayo mama</li>
    </ul>
    <ul id="R_5.032.022">
        <li id="R_5.032.022ab">kiṃ nu syāc cittamoho 'yaṃ bhaved vātagatis tv iyam</li>
        <li id="R_5.032.022cd">unmādajo vikāro vā syād iyaṃ mṛgatṛṣṇikā</li>
    </ul>
    <ul id="R_5.032.023">
        <li id="R_5.032.023ab">atha vā nāyam unmādo moho 'py unmādalakṣmaṇaḥ</li>
        <li id="R_5.032.023cd">saṃbudhye cāham ātmānam imaṃ cāpi vanaukasaṃ</li>
    </ul>
    <ul id="R_5.032.024">
        <li id="R_5.032.024ab">ity evaṃ bahudhā sītā saṃpradhārya balābalam</li>
        <li id="R_5.032.024cd">rakṣasāṃ kāmarūpatvān mene taṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_5.032.025">
        <li id="R_5.032.025ab">etāṃ buddhiṃ tadā kṛtvā sītā sā tanumadhyamā</li>
        <li id="R_5.032.025cd">na prativyājahārātha vānaraṃ janakātmajā</li>
    </ul>
    <ul id="R_5.032.026">
        <li id="R_5.032.026ab">sītāyāś cintitaṃ buddhvā hanūmān mārutātmajaḥ</li>
        <li id="R_5.032.026cd">śrotrānukūlair vacanais tadā tāṃ saṃpraharṣayat</li>
    </ul>
    <ul id="R_5.032.027">
        <li id="R_5.032.027ab">āditya iva tejasvī lokakāntaḥ śaśī yathā</li>
        <li id="R_5.032.027cd">rājā sarvasya lokasya devo vaiśravaṇo yathā</li>
    </ul>
    <ul id="R_5.032.028">
        <li id="R_5.032.028ab">vikrameṇopapannaś ca yathā viṣṇur mahāyaśāḥ</li>
        <li id="R_5.032.028cd">satyavādī madhuravāg devo vācaspatir yathā</li>
    </ul>
    <ul id="R_5.032.029">
        <li id="R_5.032.029ab">rūpavān subhagaḥ śrīmān kandarpa iva mūrtimān</li>
        <li id="R_5.032.029cd">sthānakrodhaprahartā ca śreṣṭho loke mahārathaḥ</li>
        <li id="R_5.032.029ef">bāhucchāyām avaṣṭabdho yasya loko mahātmanaḥ</li>
    </ul>
    <ul id="R_5.032.030">
        <li id="R_5.032.030ab">apakṛṣyāśramapadān mṛgarūpeṇa rāghavam</li>
        <li id="R_5.032.030cd">śūnye yenāpanītāsi tasya drakṣyasi yat phalam</li>
    </ul>
    <ul id="R_5.032.031">
        <li id="R_5.032.031ab">nacirād rāvaṇaṃ saṃkhye yo vadhiṣyati vīryavān</li>
        <li id="R_5.032.031cd">roṣapramuktair iṣubhir jvaladbhir iva pāvakaiḥ</li>
    </ul>
    <ul id="R_5.032.032">
        <li id="R_5.032.032ab">tenāhaṃ preṣito dūtas tvatsakāśam ihāgataḥ</li>
        <li id="R_5.032.032cd">tvadviyogena duḥkhārtaḥ sa tvāṃ kauśalam abravīt</li>
    </ul>
    <ul id="R_5.032.033">
        <li id="R_5.032.033ab">lakṣmaṇaś ca mahātejāḥ sumitrānandavardhanaḥ</li>
        <li id="R_5.032.033cd">abhivādya mahābāhuḥ so 'pi kauśalam abravīt</li>
    </ul>
    <ul id="R_5.032.034">
        <li id="R_5.032.034ab">rāmasya ca sakhā devi sugrīvo nāma vānaraḥ</li>
        <li id="R_5.032.034cd">rājā vānaramukhyānāṃ sa tvāṃ kauśalam abravīt</li>
    </ul>
    <ul id="R_5.032.035">
        <li id="R_5.032.035ab">nityaṃ smarati rāmas tvāṃ sasugrīvaḥ salakṣmaṇaḥ</li>
        <li id="R_5.032.035cd">diṣṭyā jīvasi vaidehi rākṣasī vaśam āgatā</li>
    </ul>
    <ul id="R_5.032.036">
        <li id="R_5.032.036ab">nacirād drakṣyase rāmaṃ lakṣmaṇaṃ ca mahāratham</li>
        <li id="R_5.032.036cd">madhye vānarakoṭīnāṃ sugrīvaṃ cāmitaujasaṃ</li>
    </ul>
    <ul id="R_5.032.037">
        <li id="R_5.032.037ab">ahaṃ sugrīvasacivo hanūmān nāma vānaraḥ</li>
        <li id="R_5.032.037cd">praviṣṭo nagarīṃ laṅkāṃ laṅghayitvā mahodadhim</li>
    </ul>
    <ul id="R_5.032.038">
        <li id="R_5.032.038ab">kṛtvā mūrdhni padanyāsaṃ rāvaṇasya durātmanaḥ</li>
        <li id="R_5.032.038cd">tvāṃ draṣṭum upayāto 'haṃ samāśritya parākramam</li>
    </ul>
    <ul id="R_5.032.039">
        <li id="R_5.032.039ab">nāham asmi tathā devi yathā mām avagacchasi</li>
        <li id="R_5.032.039cd">viśaṅkā tyajyatām eṣā śraddhatsva vadato mama</li>
    </ul>
    <ul id="R_5.033.001">
        <li id="R_5.033.001ab">tāṃ tu rāma kathāṃ śrutvā vaidehī vānararṣabhāt</li>
        <li id="R_5.033.001cd">uvāca vacanaṃ sāntvam idaṃ madhurayā girā</li>
    </ul>
    <ul id="R_5.033.002">
        <li id="R_5.033.002ab">kva te rāmeṇa saṃsargaḥ kathaṃ jānāsi lakṣmaṇam</li>
        <li id="R_5.033.002cd">vānarāṇāṃ narāṇāṃ ca katham āsīt samāgamaḥ</li>
    </ul>
    <ul id="R_5.033.003">
        <li id="R_5.033.003ab">yāni rāmasya liṅgāni lakṣmaṇasya ca vānara</li>
        <li id="R_5.033.003cd">tāni bhūyaḥ samācakṣva na māṃ śokaḥ samāviśet</li>
    </ul>
    <ul id="R_5.033.004">
        <li id="R_5.033.004ab">kīdṛśaṃ tasya saṃsthānaṃ rūpaṃ rāmasya kīdṛśam</li>
        <li id="R_5.033.004cd">katham ūrū kathaṃ bāhū lakṣmaṇasya ca śaṃsa me</li>
    </ul>
    <ul id="R_5.033.005">
        <li id="R_5.033.005ab">evam uktas tu vaidehyā hanūmān mārutātmajaḥ</li>
        <li id="R_5.033.005cd">tato rāmaṃ yathātattvam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_5.033.006">
        <li id="R_5.033.006ab">jānantī bata diṣṭyā māṃ vaidehi paripṛcchasi</li>
        <li id="R_5.033.006cd">bhartuḥ kamalapatrākṣi saṃkhyānaṃ lakṣmaṇasya ca</li>
    </ul>
    <ul id="R_5.033.007">
        <li id="R_5.033.007ab">yāni rāmasya cihnāni lakṣmaṇasya ca yāni vai</li>
        <li id="R_5.033.007cd">lakṣitāni viśālākṣi vadataḥ śṛṇu tāni me</li>
    </ul>
    <ul id="R_5.033.008">
        <li id="R_5.033.008ab">rāmaḥ kamalapatrākṣaḥ sarvabhūtamanoharaḥ</li>
        <li id="R_5.033.008cd">rūpadākṣiṇyasaṃpannaḥ prasūto janakātmaje</li>
    </ul>
    <ul id="R_5.033.009">
        <li id="R_5.033.009ab">tejasādityasaṃkāśaḥ kṣamayā pṛthivīsamaḥ</li>
        <li id="R_5.033.009cd">bṛhaspatisamo buddhyā yaśasā vāsavopamaḥ</li>
    </ul>
    <ul id="R_5.033.010">
        <li id="R_5.033.010ab">rakṣitā jīvalokasya svajanasya ca rakṣitā</li>
        <li id="R_5.033.010cd">rakṣitā svasya vṛttasya dharmasya ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_5.033.011">
        <li id="R_5.033.011ab">rāmo bhāmini lokasya cāturvarṇyasya rakṣitā</li>
        <li id="R_5.033.011cd">maryādānāṃ ca lokasya kartā kārayitā ca saḥ</li>
    </ul>
    <ul id="R_5.033.012">
        <li id="R_5.033.012ab">arciṣmān arcito 'tyarthaṃ brahmacaryavrate sthitaḥ</li>
        <li id="R_5.033.012cd">sādhūnām upakārajñaḥ pracārajñaś ca karmaṇām</li>
    </ul>
    <ul id="R_5.033.013">
        <li id="R_5.033.013ab">rājavidyāvinītaś ca brāhmaṇānām upāsitā</li>
        <li id="R_5.033.013cd">śrutavāñ śīlasaṃpanno vinītaś ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_5.033.014">
        <li id="R_5.033.014ab">yajurvedavinītaś ca vedavidbhiḥ supūjitaḥ</li>
        <li id="R_5.033.014cd">dhanurvede ca vede ca vedāṅgeṣu ca niṣṭhitaḥ</li>
    </ul>
    <ul id="R_5.033.015">
        <li id="R_5.033.015ab">vipulāṃso mahābāhuḥ kambugrīvaḥ śubhānanaḥ</li>
        <li id="R_5.033.015cd">gūḍhajatruḥ sutāmrākṣo rāmo devi janaiḥ śrutaḥ</li>
    </ul>
    <ul id="R_5.033.016">
        <li id="R_5.033.016ab">dundubhisvananirghoṣaḥ snigdhavarṇaḥ pratāpavān</li>
        <li id="R_5.033.016cd">samaḥ samavibhaktāṅgo varṇaṃ śyāmaṃ samāśritaḥ</li>
    </ul>
    <ul id="R_5.033.017">
        <li id="R_5.033.017ab">tristhiras tripralambaś ca trisamas triṣu connataḥ</li>
        <li id="R_5.033.017cd">trivalīvāṃs tryavaṇataś caturvyaṅgas triśīrṣavān</li>
    </ul>
    <ul id="R_5.033.018">
        <li id="R_5.033.018ab">catuṣkalaś caturlekhaś catuṣkiṣkuś catuḥsamaḥ</li>
        <li id="R_5.033.018cd">caturdaśasamadvandvaś caturdaṣṭaś caturgatiḥ</li>
    </ul>
    <ul id="R_5.033.019">
        <li id="R_5.033.019ab">mahauṣṭhahanunāsaś ca pañcasnigdho 'ṣṭavaṃśavān</li>
        <li id="R_5.033.019cd">daśapadmo daśabṛhat tribhir vyāpto dviśuklavān</li>
        <li id="R_5.033.019ef">ṣaḍunnato navatanus tribhir vyāpnoti rāghavaḥ</li>
    </ul>
    <ul id="R_5.033.020">
        <li id="R_5.033.020ab">satyadharmaparaḥ śrīmān saṃgrahānugrahe rataḥ</li>
        <li id="R_5.033.020cd">deśakālavibhāgajñaḥ sarvalokapriyaṃvadaḥ</li>
    </ul>
    <ul id="R_5.033.021">
        <li id="R_5.033.021ab">bhrātā ca tasya dvaimātraḥ saumitrir aparājitaḥ</li>
        <li id="R_5.033.021cd">anurāgeṇa rūpeṇa guṇaiś caiva tathāvidhaḥ</li>
    </ul>
    <ul id="R_5.033.022">
        <li id="R_5.033.022ab">tvām eva mārgamāṇo tau vicarantau vasuṃdharām</li>
        <li id="R_5.033.022cd">dadarśatur mṛgapatiṃ pūrvajenāvaropitam</li>
    </ul>
    <ul id="R_5.033.023">
        <li id="R_5.033.023ab">ṛśyamūkasya pṛṣṭhe tu bahupādapasaṃkule</li>
        <li id="R_5.033.023cd">bhrātur bhāryārtam āsīnaṃ sugrīvaṃ priyadarśanam</li>
    </ul>
    <ul id="R_5.033.024">
        <li id="R_5.033.024ab">vayaṃ tu harirājaṃ taṃ sugrīvaṃ satyasaṃgaram</li>
        <li id="R_5.033.024cd">paricaryāmahe rājyāt pūrvajenāvaropitam</li>
    </ul>
    <ul id="R_5.033.025">
        <li id="R_5.033.025ab">tatas tau cīravasanau dhanuḥpravarapāṇinau</li>
        <li id="R_5.033.025cd">ṛśyamūkasya śailasya ramyaṃ deśam upāgatau</li>
    </ul>
    <ul id="R_5.033.026">
        <li id="R_5.033.026ab">sa tau dṛṣṭvā naravyāghrau dhanvinau vānararṣabhaḥ</li>
        <li id="R_5.033.026cd">abhipluto gires tasya śikharaṃ bhayamohitaḥ</li>
    </ul>
    <ul id="R_5.033.027">
        <li id="R_5.033.027ab">tataḥ sa śikhare tasmin vānarendro vyavasthitaḥ</li>
        <li id="R_5.033.027cd">tayoḥ samīpaṃ mām eva preṣayām āsa satvaraḥ</li>
    </ul>
    <ul id="R_5.033.028">
        <li id="R_5.033.028ab">tāv ahaṃ puruṣavyāghrau sugrīvavacanāt prabhū</li>
        <li id="R_5.033.028cd">rūpalakṣaṇasaṃpannau kṛtāñjalir upasthitaḥ</li>
    </ul>
    <ul id="R_5.033.029">
        <li id="R_5.033.029ab">tau parijñātatattvārthau mayā prītisamanvitau</li>
        <li id="R_5.033.029cd">pṛṣṭham āropya taṃ deśaṃ prāpitau puruṣarṣabhau</li>
    </ul>
    <ul id="R_5.033.030">
        <li id="R_5.033.030ab">niveditau ca tattvena sugrīvāya mahātmane</li>
        <li id="R_5.033.030cd">tayor anyonyasaṃbhāṣād bhṛśaṃ prītir ajāyata</li>
    </ul>
    <ul id="R_5.033.031">
        <li id="R_5.033.031ab">tatra tau kīrtisaṃpannau harīśvaranareśvarau</li>
        <li id="R_5.033.031cd">parasparakṛtāśvāsau kathayā pūrvavṛttayā</li>
    </ul>
    <ul id="R_5.033.032">
        <li id="R_5.033.032ab">taṃ tataḥ sāntvayām āsa sugrīvaṃ lakṣmaṇāgrajaḥ</li>
        <li id="R_5.033.032cd">strīhetor vālinā bhrātrā nirastam uru tejasā</li>
    </ul>
    <ul id="R_5.033.033">
        <li id="R_5.033.033ab">tatas tvan nāśajaṃ śokaṃ rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_5.033.033cd">lakṣmaṇo vānarendrāya sugrīvāya nyavedayat</li>
    </ul>
    <ul id="R_5.033.034">
        <li id="R_5.033.034ab">sa śrutvā vānarendras tu lakṣmaṇeneritaṃ vacaḥ</li>
        <li id="R_5.033.034cd">tadāsīn niṣprabho 'tyarthaṃ grahagrasta ivāṃśumān</li>
    </ul>
    <ul id="R_5.033.035">
        <li id="R_5.033.035ab">tatas tvadgātraśobhīni rakṣasā hriyamāṇayā</li>
        <li id="R_5.033.035cd">yāny ābharaṇajālāni pātitāni mahītale</li>
    </ul>
    <ul id="R_5.033.036">
        <li id="R_5.033.036ab">tāni sarvāṇi rāmāya ānīya hariyūthapāḥ</li>
        <li id="R_5.033.036cd">saṃhṛṣṭā darśayām āsur gatiṃ tu na vidus tava</li>
    </ul>
    <ul id="R_5.033.037">
        <li id="R_5.033.037ab">tāni rāmāya dattāni mayaivopahṛtāni ca</li>
        <li id="R_5.033.037cd">svanavanty avakīrṇanti tasmin vihatacetasi</li>
    </ul>
    <ul id="R_5.033.038">
        <li id="R_5.033.038ab">tāny aṅke darśanīyāni kṛtvā bahuvidhaṃ tataḥ</li>
        <li id="R_5.033.038cd">tena devaprakāśena devena paridevitam</li>
    </ul>
    <ul id="R_5.033.039">
        <li id="R_5.033.039ab">paśyatas tasyā rudatas tāmyataś ca punaḥ punaḥ</li>
        <li id="R_5.033.039cd">prādīpayan dāśarathes tāni śokahutāśanam</li>
    </ul>
    <ul id="R_5.033.040">
        <li id="R_5.033.040ab">śayitaṃ ca ciraṃ tena duḥkhārtena mahātmanā</li>
        <li id="R_5.033.040cd">mayāpi vividhair vākyaiḥ kṛcchrād utthāpitaḥ punaḥ</li>
    </ul>
    <ul id="R_5.033.041">
        <li id="R_5.033.041ab">tāni dṛṣṭvā mahārhāṇi darśayitvā muhur muhuḥ</li>
        <li id="R_5.033.041cd">rāghavaḥ sahasaumitriḥ sugrīve sa nyavedayat</li>
    </ul>
    <ul id="R_5.033.042">
        <li id="R_5.033.042ab">sa tavādarśanād ārye rāghavaḥ paritapyate</li>
        <li id="R_5.033.042cd">mahatā jvalatā nityam agninevāgniparvataḥ</li>
    </ul>
    <ul id="R_5.033.043">
        <li id="R_5.033.043ab">tvatkṛte tam anidrā ca śokaś cintā ca rāghavam</li>
        <li id="R_5.033.043cd">tāpayanti mahātmānam agnyagāram ivāgnayaḥ</li>
    </ul>
    <ul id="R_5.033.044">
        <li id="R_5.033.044ab">tavādarśanaśokena rāghavaḥ pravicālyate</li>
        <li id="R_5.033.044cd">mahatā bhūmikampena mahān iva śiloccayaḥ</li>
    </ul>
    <ul id="R_5.033.045">
        <li id="R_5.033.045ab">kānānāni suramyāṇi nadīprasravaṇāni ca</li>
        <li id="R_5.033.045cd">caran na ratim āpnoti tvam apaśyan nṛpātmaje</li>
    </ul>
    <ul id="R_5.033.046">
        <li id="R_5.033.046ab">sa tvāṃ manujaśārdūlaḥ kṣipraṃ prāpsyati rāghavaḥ</li>
        <li id="R_5.033.046cd">samitrabāndhavaṃ hatvā rāvaṇaṃ janakātmaje</li>
    </ul>
    <ul id="R_5.033.047">
        <li id="R_5.033.047ab">sahitau rāmasugrīvāv ubhāv akurutāṃ tadā</li>
        <li id="R_5.033.047cd">samayaṃ vālinaṃ hantuṃ tava cānveṣaṇaṃ tathā</li>
    </ul>
    <ul id="R_5.033.048">
        <li id="R_5.033.048ab">tato nihatya tarasā rāmo vālinam āhave</li>
        <li id="R_5.033.048cd">sarvarkṣaharisaṃghānāṃ sugrīvam akarot patim</li>
    </ul>
    <ul id="R_5.033.049">
        <li id="R_5.033.049ab">rāmasugrīvayor aikyaṃ devy evaṃ samajāyata</li>
        <li id="R_5.033.049cd">hanūmantaṃ ca māṃ viddhi tayor dūtam ihāgatam</li>
    </ul>
    <ul id="R_5.033.050">
        <li id="R_5.033.050ab">svarājyaṃ prāpya sugrīvaḥ samanīya mahāharīn</li>
        <li id="R_5.033.050cd">tvadarthaṃ preṣayām āsa diśo daśa mahābalān</li>
    </ul>
    <ul id="R_5.033.051">
        <li id="R_5.033.051ab">ādiṣṭā vānarendreṇa sugrīveṇa mahaujasaḥ</li>
        <li id="R_5.033.051cd">adrirājapratīkāśāḥ sarvataḥ prasthitā mahīm</li>
    </ul>
    <ul id="R_5.033.052">
        <li id="R_5.033.052ab">aṅgado nāma lakṣmīvān vālisūnur mahābalaḥ</li>
        <li id="R_5.033.052cd">prasthitaḥ kapiśārdūlas tribhāgabalasaṃvṛtaḥ</li>
    </ul>
    <ul id="R_5.033.053">
        <li id="R_5.033.053ab">teṣāṃ no vipranaṣṭānāṃ vindhye parvatasattame</li>
        <li id="R_5.033.053cd">bhṛśaṃ śokaparītanām ahorātragaṇā gatāḥ</li>
    </ul>
    <ul id="R_5.033.054">
        <li id="R_5.033.054ab">te vayaṃ kāryanairāśyāt kālasyātikrameṇa ca</li>
        <li id="R_5.033.054cd">bhayāc ca kapirājasya prāṇāṃs tyaktuṃ vyavasthitāḥ</li>
    </ul>
    <ul id="R_5.033.055">
        <li id="R_5.033.055ab">vicitya vanadurgāṇi giriprasravaṇāni ca</li>
        <li id="R_5.033.055cd">anāsādya padaṃ devyāḥ prāṇāṃs tyaktuṃ vyavasthitāḥ</li>
    </ul>
    <ul id="R_5.033.056">
        <li id="R_5.033.056ab">bhṛśaṃ śokārṇave magnaḥ paryadevayad aṅgadaḥ</li>
        <li id="R_5.033.056cd">tava nāśaṃ ca vaidehi vālinaś ca tathā vadham</li>
        <li id="R_5.033.056ef">prāyopaveśam asmākaṃ maraṇaṃ ca jaṭāyuṣaḥ</li>
    </ul>
    <ul id="R_5.033.057">
        <li id="R_5.033.057ab">teṣāṃ naḥ svāmisaṃdeśān nirāśānāṃ mumūrṣatām</li>
        <li id="R_5.033.057cd">kāryahetor ivāyātaḥ śakunir vīryavān mahān</li>
    </ul>
    <ul id="R_5.033.058">
        <li id="R_5.033.058ab">gṛdhrarājasya sodaryaḥ saṃpātir nāma gṛdhrarāṭ</li>
        <li id="R_5.033.058cd">śrutvā bhrātṛvadhaṃ kopād idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.033.059">
        <li id="R_5.033.059ab">yavīyān kena me bhrātā hataḥ kva ca vināśitaḥ</li>
        <li id="R_5.033.059cd">etad ākhyātum icchāmi bhavadbhir vānarottamāḥ</li>
    </ul>
    <ul id="R_5.033.060">
        <li id="R_5.033.060ab">aṅgado 'kathayat tasya janasthāne mahad vadham</li>
        <li id="R_5.033.060cd">rakṣasā bhīmarūpeṇa tvām uddiśya yathātatham</li>
    </ul>
    <ul id="R_5.033.061">
        <li id="R_5.033.061ab">jaṭāyos tu vadhaṃ śrutvā duḥkhitaḥ so 'ruṇātmajaḥ</li>
        <li id="R_5.033.061cd">tvām āha sa varārohe vasantīṃ rāvaṇālaye</li>
    </ul>
    <ul id="R_5.033.062">
        <li id="R_5.033.062ab">tasya tad vacanaṃ śrutvā saṃpāteḥ prītivardhanam</li>
        <li id="R_5.033.062cd">aṅgadapramukhāḥ sarve tataḥ saṃprasthitā vayam</li>
        <li id="R_5.033.062ef">tvaddarśanakṛtotsāhā hṛṣṭās tuṣṭāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_5.033.063">
        <li id="R_5.033.063ab">athāhaṃ harisainyasya sāgaraṃ dṛśya sīdataḥ</li>
        <li id="R_5.033.063cd">vyavadhūya bhayaṃ tīvraṃ yojanānāṃ śataṃ plutaḥ</li>
    </ul>
    <ul id="R_5.033.064">
        <li id="R_5.033.064ab">laṅkā cāpi mayā rātrau praviṣṭā rākṣasākulā</li>
        <li id="R_5.033.064cd">rāvaṇaś ca mayā dṛṣṭas tvaṃ ca śokanipīḍitā</li>
    </ul>
    <ul id="R_5.033.065">
        <li id="R_5.033.065ab">etat te sarvam ākhyātaṃ yathāvṛttam anindite</li>
        <li id="R_5.033.065cd">abhibhāṣasva māṃ devi dūto dāśarather aham</li>
    </ul>
    <ul id="R_5.033.066">
        <li id="R_5.033.066ab">tvaṃ māṃ rāmakṛtodyogaṃ tvannimittam ihāgatam</li>
        <li id="R_5.033.066cd">sugrīva sacivaṃ devi budhyasva pavanātmajam</li>
    </ul>
    <ul id="R_5.033.067">
        <li id="R_5.033.067ab">kuśalī tava kākutsthaḥ sarvaśastrabhṛtāṃ varaḥ</li>
        <li id="R_5.033.067cd">guror ārādhane yukto lakṣmaṇaś ca sulakṣaṇaḥ</li>
    </ul>
    <ul id="R_5.033.068">
        <li id="R_5.033.068ab">tasya vīryavato devi bhartus tava hite rataḥ</li>
        <li id="R_5.033.068cd">aham ekas tu saṃprāptaḥ sugrīvavacanād iha</li>
    </ul>
    <ul id="R_5.033.069">
        <li id="R_5.033.069ab">mayeyam asahāyena caratā kāmarūpiṇā</li>
        <li id="R_5.033.069cd">dakṣiṇā dig anukrāntā tvanmārgavicayaiṣiṇā</li>
    </ul>
    <ul id="R_5.033.070">
        <li id="R_5.033.070ab">diṣṭyāhaṃ harisainyānāṃ tvannāśam anuśocatām</li>
        <li id="R_5.033.070cd">apaneṣyāmi saṃtāpaṃ tavābhigamaśaṃsanāt</li>
    </ul>
    <ul id="R_5.033.071">
        <li id="R_5.033.071ab">diṣṭyā hi na mama vyarthaṃ devi sāgaralaṅghanam</li>
        <li id="R_5.033.071cd">prāpsyāmy aham idaṃ diṣṭyā tvaddarśanakṛtaṃ yaśaḥ</li>
    </ul>
    <ul id="R_5.033.072">
        <li id="R_5.033.072ab">rāghavaś ca mahāvīryaḥ kṣipraṃ tvām abhipatsyate</li>
        <li id="R_5.033.072cd">samitrabāndhavaṃ hatvā rāvaṇaṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_5.033.073">
        <li id="R_5.033.073ab">kaurajo nāma vaidehi girīṇām uttamo giriḥ</li>
        <li id="R_5.033.073cd">tato gacchati gokarṇaṃ parvataṃ kesarī hariḥ</li>
    </ul>
    <ul id="R_5.033.074">
        <li id="R_5.033.074ab">sa ca devarṣibhir dṛṣṭaḥ pitā mama mahākapiḥ</li>
        <li id="R_5.033.074cd">tīrthe nadīpateḥ puṇye śambasādanam uddharat</li>
    </ul>
    <ul id="R_5.033.075">
        <li id="R_5.033.075ab">tasyāhaṃ hariṇaḥ kṣetre jāto vātena maithili</li>
        <li id="R_5.033.075cd">hanūmān iti vikhyāto loke svenaiva karmaṇā</li>
        <li id="R_5.033.075ef">viśvāsārthaṃ tu vaidehi bhartur uktā mayā guṇāḥ</li>
    </ul>
    <ul id="R_5.033.076">
        <li id="R_5.033.076ab">evaṃ viśvāsitā sītā hetubhiḥ śokakarśitā</li>
        <li id="R_5.033.076cd">upapannair abhijñānair dūtaṃ tam avagacchati</li>
    </ul>
    <ul id="R_5.033.077">
        <li id="R_5.033.077ab">atulaṃ ca gatā harṣaṃ praharṣeṇa tu jānakī</li>
        <li id="R_5.033.077cd">netrābhyāṃ vakrapakṣmābhyāṃ mumocānandajaṃ jalam</li>
    </ul>
    <ul id="R_5.033.078">
        <li id="R_5.033.078ab">cāru tac cānanaṃ tasyās tāmraśuklāyatekṣaṇam</li>
        <li id="R_5.033.078cd">aśobhata viśālākṣyā rāhumukta ivoḍurāṭ</li>
        <li id="R_5.033.078ef">hanūmantaṃ kapiṃ vyaktaṃ manyate nānyatheti sā</li>
    </ul>
    <ul id="R_5.033.079">
        <li id="R_5.033.079ab">athovāca hanūmāṃs tām uttaraṃ priyadarśanām</li>
    </ul>
    <ul id="R_5.033.080">
        <li id="R_5.033.080ab">
            <seg type="pāda" n="a">hate 'sure saṃyati śambasādane</seg>
            <seg type="pāda" n="b">kapipravīreṇa maharṣicodanāt</seg>
        </li>
        <li id="R_5.033.080cd">
            <seg type="pāda" n="c">tato 'smi vāyuprabhavo hi maithili</seg>
            <seg type="pāda" n="d">prabhāvatas tatpratimaś ca vānaraḥ</seg>
        </li>
    </ul>
    <ul id="R_5.034.001">
        <li id="R_5.034.001ab">bhūya eva mahātejā hanūmān mārutātmajaḥ</li>
        <li id="R_5.034.001cd">abravīt praśritaṃ vākyaṃ sītāpratyayakāraṇāt</li>
    </ul>
    <ul id="R_5.034.002">
        <li id="R_5.034.002ab">vānaro 'haṃ mahābhāge dūto rāmasya dhīmataḥ</li>
        <li id="R_5.034.002cd">rāmanāmāṅkitaṃ cedaṃ paśya devy aṅgulīyakam</li>
        <li id="R_5.034.002ef">samāśvasihi bhadraṃ te kṣīṇaduḥkhaphalā hy asi</li>
    </ul>
    <ul id="R_5.034.003">
        <li id="R_5.034.003ab">gṛhītvā prekṣamāṇā sā bhartuḥ karavibhūṣaṇam</li>
        <li id="R_5.034.003cd">bhartāram iva saṃprāptā jānakī muditābhavat</li>
    </ul>
    <ul id="R_5.034.004">
        <li id="R_5.034.004ab">cāru tad vadanaṃ tasyās tāmraśuklāyatekṣaṇam</li>
        <li id="R_5.034.004cd">babhūva praharṣodagraṃ rāhumukta ivoḍurāṭ</li>
    </ul>
    <ul id="R_5.034.005">
        <li id="R_5.034.005ab">tataḥ sā hrīmatī bālā bhartuḥ saṃdeśaharṣitā</li>
        <li id="R_5.034.005cd">parituṭṣā priyaṃ śrutvā prāśaṃsata mahākapim</li>
    </ul>
    <ul id="R_5.034.006">
        <li id="R_5.034.006ab">vikrāntas tvaṃ samarthas tvaṃ prājñas tvaṃ vānarottama</li>
        <li id="R_5.034.006cd">yenedaṃ rākṣasapadaṃ tvayaikena pradharṣitam</li>
    </ul>
    <ul id="R_5.034.007">
        <li id="R_5.034.007ab">śatayojanavistīrṇaḥ sāgaro makarālayaḥ</li>
        <li id="R_5.034.007cd">vikramaślāghanīyena kramatā goṣpadīkṛtaḥ</li>
    </ul>
    <ul id="R_5.034.008">
        <li id="R_5.034.008ab">na hi tvāṃ prākṛtaṃ manye vanaraṃ vanararṣabha</li>
        <li id="R_5.034.008cd">yasya te nāsti saṃtrāso rāvaṇān nāpi saṃbhramaḥ</li>
    </ul>
    <ul id="R_5.034.009">
        <li id="R_5.034.009ab">arhase ca kapiśreṣṭha mayā samabhibhāṣitum</li>
        <li id="R_5.034.009cd">yady asi preṣitas tena rāmeṇa viditātmanā</li>
    </ul>
    <ul id="R_5.034.010">
        <li id="R_5.034.010ab">preṣayiṣyati durdharṣo rāmo na hy aparīkṣitam</li>
        <li id="R_5.034.010cd">parākramam avijñāya matsakāśaṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_5.034.011">
        <li id="R_5.034.011ab">diṣṭyā ca kuśalī rāmo dharmātmā dharmavatsalaḥ</li>
        <li id="R_5.034.011cd">lakṣmaṇaś ca mahātejāḥ sumitrānandavardhanaḥ</li>
    </ul>
    <ul id="R_5.034.012">
        <li id="R_5.034.012ab">kuśalī yadi kākutsthaḥ kiṃ nu sāgaramekhalām</li>
        <li id="R_5.034.012cd">mahīṃ dahati kopena yugāntāgnir ivotthitaḥ</li>
    </ul>
    <ul id="R_5.034.013">
        <li id="R_5.034.013ab">atha vā śaktimantau tau surāṇām api nigrahe</li>
        <li id="R_5.034.013cd">mamaiva tu na duḥkhānām asti manye viparyayaḥ</li>
    </ul>
    <ul id="R_5.034.014">
        <li id="R_5.034.014ab">kaccic ca vyathate rāmaḥ kaccin na paripatyate</li>
        <li id="R_5.034.014cd">uttarāṇi ca kāryāṇi kurute puruṣottamaḥ</li>
    </ul>
    <ul id="R_5.034.015">
        <li id="R_5.034.015ab">kaccin na dīnaḥ saṃbhrāntaḥ kāryeṣu ca na muhyati</li>
        <li id="R_5.034.015cd">kaccin puruṣakāryāṇi kurute nṛpateḥ sutaḥ</li>
    </ul>
    <ul id="R_5.034.016">
        <li id="R_5.034.016ab">dvividhaṃ trividhopāyam upāyam api sevate</li>
        <li id="R_5.034.016cd">vijigīṣuḥ suhṛt kaccin mitreṣu ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_5.034.017">
        <li id="R_5.034.017ab">kaccin mitrāṇi labhate mitraiś cāpy abhigamyate</li>
        <li id="R_5.034.017cd">kaccit kalyāṇamitraś ca mitraiś cāpi puraskṛtaḥ</li>
    </ul>
    <ul id="R_5.034.018">
        <li id="R_5.034.018ab">kaccid āśāsti devānāṃ prasādaṃ pārthivātmajaḥ</li>
        <li id="R_5.034.018cd">kaccit puruṣakāraṃ ca daivaṃ ca pratipadyate</li>
    </ul>
    <ul id="R_5.034.019">
        <li id="R_5.034.019ab">kaccin na vigatasneho vivāsān mayi rāghavaḥ</li>
        <li id="R_5.034.019cd">kaccin māṃ vyasanād asmān mokṣayiṣyati vānaraḥ</li>
    </ul>
    <ul id="R_5.034.020">
        <li id="R_5.034.020ab">sukhānām ucito nityam asukhānām anūcitaḥ</li>
        <li id="R_5.034.020cd">duḥkham uttaram āsādya kaccid rāmo na sīdati</li>
    </ul>
    <ul id="R_5.034.021">
        <li id="R_5.034.021ab">kausalyāyās tathā kaccit sumitrāyās tathaiva ca</li>
        <li id="R_5.034.021cd">abhīkṣṇaṃ śrūyate kaccit kuśalaṃ bharatasya ca</li>
    </ul>
    <ul id="R_5.034.022">
        <li id="R_5.034.022ab">mannimittena mānārhaḥ kaccic chokena rāghavaḥ</li>
        <li id="R_5.034.022cd">kaccin nānyamanā rāmaḥ kaccin māṃ tārayiṣyati</li>
    </ul>
    <ul id="R_5.034.023">
        <li id="R_5.034.023ab">kaccid akṣauhiṇīṃ bhīmāṃ bharato bhrātṛvatsalaḥ</li>
        <li id="R_5.034.023cd">dhvajinīṃ mantribhir guptāṃ preṣayiṣyati matkṛte</li>
    </ul>
    <ul id="R_5.034.024">
        <li id="R_5.034.024ab">vānarādhipatiḥ śrīmān sugrīvaḥ kaccid eṣyati</li>
        <li id="R_5.034.024cd">matkṛte haribhir vīrair vṛto dantanakhāyudhaiḥ</li>
    </ul>
    <ul id="R_5.034.025">
        <li id="R_5.034.025ab">kaccic ca lakṣmaṇaḥ śūraḥ sumitrānandavardhanaḥ</li>
        <li id="R_5.034.025cd">astravic charajālena rākṣasān vidhamiṣyati</li>
    </ul>
    <ul id="R_5.034.026">
        <li id="R_5.034.026ab">raudreṇa kaccid astreṇa rāmeṇa nihataṃ raṇe</li>
        <li id="R_5.034.026cd">drakṣyāmy alpena kālena rāvaṇaṃ sasuhṛjjanam</li>
    </ul>
    <ul id="R_5.034.027">
        <li id="R_5.034.027ab">
            <seg type="pāda" n="a">kaccin na tad dhemasamānavarṇaṃ</seg>
            <seg type="pāda" n="b">tasyānanaṃ padmasamānagandhi</seg>
        </li>
        <li id="R_5.034.027cd">
            <seg type="pāda" n="c">mayā vinā śuṣyati śokadīnaṃ</seg>
            <seg type="pāda" n="d">jalakṣaye padmam ivātapena</seg>
        </li>
    </ul>
    <ul id="R_5.034.028">
        <li id="R_5.034.028ab">
            <seg type="pāda" n="a">dharmāpadeśāt tyajataś ca rājyāṃ</seg>
            <seg type="pāda" n="b">māṃ cāpy araṇyaṃ nayataḥ padātim</seg>
        </li>
        <li id="R_5.034.028cd">
            <seg type="pāda" n="c">nāsīd vyathā yasya na bhīr na śokaḥ</seg>
            <seg type="pāda" n="d">kaccit sa dhairyaṃ hṛdaye karoti</seg>
        </li>
    </ul>
    <ul id="R_5.034.029">
        <li id="R_5.034.029ab">
            <seg type="pāda" n="a">na cāsya mātā na pitā na cānyaḥ</seg>
            <seg type="pāda" n="b">snehād viśiṣṭo 'sti mayā samo vā</seg>
        </li>
        <li id="R_5.034.029cd">
            <seg type="pāda" n="c">tāvad dhy ahaṃ dūtajijīviṣeyaṃ</seg>
            <seg type="pāda" n="d">yāvat pravṛttiṃ śṛṇuyāṃ priyasya</seg>
        </li>
    </ul>
    <ul id="R_5.034.030">
        <li id="R_5.034.030ab">
            <seg type="pāda" n="a">itīva devī vacanaṃ mahārthaṃ</seg>
            <seg type="pāda" n="b">taṃ vānarendraṃ madhurārtham uktvā</seg>
        </li>
        <li id="R_5.034.030cd">
            <seg type="pāda" n="c">śrotuṃ punas tasya vaco 'bhirāmaṃ</seg>
            <seg type="pāda" n="d">rāmārthayuktaṃ virarāma rāmā</seg>
        </li>
    </ul>
    <ul id="R_5.034.031">
        <li id="R_5.034.031ab">sītāyā vacanaṃ śrutvā mārutir bhīmavikramaḥ</li>
        <li id="R_5.034.031cd">śirasy añjalim ādhāya vākyam uttaram abravīt</li>
    </ul>
    <ul id="R_5.034.032">
        <li id="R_5.034.032ab">na tvām ihasthāṃ jānīte rāmaḥ kamalalocanaḥ</li>
        <li id="R_5.034.032cd">śrutvaiva tu vaco mahyaṃ kṣipram eṣyati rāghavaḥ</li>
    </ul>
    <ul id="R_5.034.033">
        <li id="R_5.034.033ab">camūṃ prakarṣan mahatīṃ haryṛṣkagaṇasaṃkulām</li>
        <li id="R_5.034.033cd">viṣṭambhayitvā bāṇaughair akṣobhyaṃ varuṇālayam</li>
        <li id="R_5.034.033ef">kariṣyati purīṃ laṅkāṃ kākutsthaḥ śāntarākṣasām</li>
    </ul>
    <ul id="R_5.034.034">
        <li id="R_5.034.034ab">tatra yady antarā mṛtyur yadi devāḥ sahāsurāḥ</li>
        <li id="R_5.034.034cd">sthāsyanti pathi rāmasya sa tān api vadhiṣyati</li>
    </ul>
    <ul id="R_5.034.035">
        <li id="R_5.034.035ab">tavādarśanajenārye śokena sa pariplutaḥ</li>
        <li id="R_5.034.035cd">na śarma labhate rāmaḥ siṃhārdita iva dvipaḥ</li>
    </ul>
    <ul id="R_5.034.036">
        <li id="R_5.034.036ab">dardareṇa ca te devi śape mūlaphalena ca</li>
        <li id="R_5.034.036cd">malayena ca vindhyena meruṇā mandareṇa ca</li>
    </ul>
    <ul id="R_5.034.037">
        <li id="R_5.034.037ab">yathā sunayanaṃ valgu bimbauṣṭhaṃ cārukuṇḍalam</li>
        <li id="R_5.034.037cd">mukhaṃ drakṣyasi rāmasya pūrṇacandram ivoditam</li>
    </ul>
    <ul id="R_5.034.038">
        <li id="R_5.034.038ab">kṣipraṃ drakṣyasi vaidehi rāmaṃ prasravaṇe girau</li>
        <li id="R_5.034.038cd">śatakratum ivāsīnaṃ nākapṛṣṭhasya mūrdhani</li>
    </ul>
    <ul id="R_5.034.039">
        <li id="R_5.034.039ab">na māṃsaṃ rāghavo bhuṅkte na cāpi madhusevate</li>
        <li id="R_5.034.039cd">vanyaṃ suvihitaṃ nityaṃ bhaktam aśnāti pañcamam</li>
    </ul>
    <ul id="R_5.034.040">
        <li id="R_5.034.040ab">naiva daṃśān na maśakān na kīṭān na sarīsṛpān</li>
        <li id="R_5.034.040cd">rāghavo 'panayed gatrāt tvadgatenāntarātmanā</li>
    </ul>
    <ul id="R_5.034.041">
        <li id="R_5.034.041ab">nityaṃ dhyānaparo rāmo nityaṃ śokaparāyaṇaḥ</li>
        <li id="R_5.034.041cd">nānyac cintayate kiṃ cit sa tu kāmavaśaṃ gataḥ</li>
    </ul>
    <ul id="R_5.034.042">
        <li id="R_5.034.042ab">anidraḥ satataṃ rāmaḥ supto 'pi ca narottamaḥ</li>
        <li id="R_5.034.042cd">sīteti madhurāṃ vāṇīṃ vyāharan pratibudhyate</li>
    </ul>
    <ul id="R_5.034.043">
        <li id="R_5.034.043ab">dṛṣṭvā phalaṃ vā puṣpaṃ vā yac cānyat strīmanoharam</li>
        <li id="R_5.034.043cd">bahuśo hā priyety evaṃ śvasaṃs tvām abhibhāṣate</li>
    </ul>
    <ul id="R_5.034.044">
        <li id="R_5.034.044ab">
            <seg type="pāda" n="a">sa devi nityaṃ paritapyamānas</seg>
            <seg type="pāda" n="b">tvām eva sītety abhibhāṣamāṇaḥ</seg>
        </li>
        <li id="R_5.034.044cd">
            <seg type="pāda" n="c">dhṛtavrato rājasuto mahātmā</seg>
            <seg type="pāda" n="d">tavaiva lābhāya kṛtaprayatnaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.034.045">
        <li id="R_5.034.045ab">
            <seg type="pāda" n="a">sā rāmasaṃkīrtanavītaśokā</seg>
            <seg type="pāda" n="b">rāmasya śokena samānaśokā</seg>
        </li>
        <li id="R_5.034.045cd">
            <seg type="pāda" n="c">śaranmukhenāmbudaśeṣacandrā</seg>
            <seg type="pāda" n="d">niśeva vaidehasutā babhūva</seg>
        </li>
    </ul>
    <ul id="R_5.035.001">
        <li id="R_5.035.001ab">sītā tad vacanaṃ śrutvā pūrṇacandranibhānanā</li>
        <li id="R_5.035.001cd">hanūmantam uvācedaṃ dharmārthasahitaṃ vacaḥ</li>
    </ul>
    <ul id="R_5.035.002">
        <li id="R_5.035.002ab">amṛtaṃ viṣasaṃsṛṣṭaṃ tvayā vānarabhāṣitam</li>
        <li id="R_5.035.002cd">yac ca nānyamanā rāmo yac ca śokaparāyaṇaḥ</li>
    </ul>
    <ul id="R_5.035.003">
        <li id="R_5.035.003ab">aiśvarye vā suvistīrṇe vyasane vā sudāruṇe</li>
        <li id="R_5.035.003cd">rajjveva puruṣaṃ baddhvā kṛtāntaḥ parikarṣati</li>
    </ul>
    <ul id="R_5.035.004">
        <li id="R_5.035.004ab">vidhir nūnam asaṃhāryaḥ prāṇināṃ plavagottama</li>
        <li id="R_5.035.004cd">saumitriṃ māṃ ca rāmaṃ ca vyasanaiḥ paśya mohitān</li>
    </ul>
    <ul id="R_5.035.005">
        <li id="R_5.035.005ab">śokasyāsya kadā pāraṃ rāghavo 'dhigamiṣyati</li>
        <li id="R_5.035.005cd">plavamānaḥ pariśrānto hatanauḥ sāgare yathā</li>
    </ul>
    <ul id="R_5.035.006">
        <li id="R_5.035.006ab">rākṣasānāṃ kṣayaṃ kṛtvā sūdayitvā ca rāvaṇam</li>
        <li id="R_5.035.006cd">laṅkām unmūlitāṃ kṛtvā kadā drakṣyati māṃ patiḥ</li>
    </ul>
    <ul id="R_5.035.007">
        <li id="R_5.035.007ab">sa vācyaḥ saṃtvarasveti yāvad eva na pūryate</li>
        <li id="R_5.035.007cd">ayaṃ saṃvatsaraḥ kālas tāvad dhi mama jīvitam</li>
    </ul>
    <ul id="R_5.035.008">
        <li id="R_5.035.008ab">vartate daśamo māso dvau tu śeṣau plavaṃgama</li>
        <li id="R_5.035.008cd">rāvaṇena nṛśaṃsena samayo yaḥ kṛto mama</li>
    </ul>
    <ul id="R_5.035.009">
        <li id="R_5.035.009ab">vibhīṣaṇena ca bhrātrā mama niryātanaṃ prati</li>
        <li id="R_5.035.009cd">anunītaḥ prayatnena na ca tat kurute matim</li>
    </ul>
    <ul id="R_5.035.010">
        <li id="R_5.035.010ab">mama pratipradānaṃ hi rāvaṇasya na rocate</li>
        <li id="R_5.035.010cd">rāvaṇaṃ mārgate saṃkhye mṛtyuḥ kālavaśaṃ gatam</li>
    </ul>
    <ul id="R_5.035.011">
        <li id="R_5.035.011ab">jyeṣṭhā kanyānalā nama vibhīṣaṇasutā kape</li>
        <li id="R_5.035.011cd">tayā mamaitad ākhyātaṃ mātrā prahitayā svayam</li>
    </ul>
    <ul id="R_5.035.012">
        <li id="R_5.035.012ab">avindhyo nāma medhāvī vidvān rākṣasapuṃgavaḥ</li>
        <li id="R_5.035.012cd">dhṛtimāñ śīlavān vṛddho rāvaṇasya susaṃmataḥ</li>
    </ul>
    <ul id="R_5.035.013">
        <li id="R_5.035.013ab">rāmāt kṣayam anuprāptaṃ rakṣasāṃ pratyacodayat</li>
        <li id="R_5.035.013cd">na ca tasyāpi duṣṭātmā śṛṇoti vacanaṃ hitam</li>
    </ul>
    <ul id="R_5.035.014">
        <li id="R_5.035.014ab">āśaṃseti hariśreṣṭha kṣipraṃ māṃ prāpsyate patiḥ</li>
        <li id="R_5.035.014cd">antarātmā hi me śuddhas tasmiṃś ca bahavo guṇāḥ</li>
    </ul>
    <ul id="R_5.035.015">
        <li id="R_5.035.015ab">utsāhaḥ pauruṣaṃ sattvam ānṛśaṃsyaṃ kṛtajñatā</li>
        <li id="R_5.035.015cd">vikramaś ca prabhāvaś ca santi vānararāghave</li>
    </ul>
    <ul id="R_5.035.016">
        <li id="R_5.035.016ab">caturdaśasahasrāṇi rākṣasānāṃ jaghāna yaḥ</li>
        <li id="R_5.035.016cd">janasthāne vinā bhrātrā śatruḥ kas tasya nodvijet</li>
    </ul>
    <ul id="R_5.035.017">
        <li id="R_5.035.017ab">na sa śakyas tulayituṃ vyasanaiḥ puruṣarṣabhaḥ</li>
        <li id="R_5.035.017cd">ahaṃ tasyānubhāvajñā śakrasyeva pulomajā</li>
    </ul>
    <ul id="R_5.035.018">
        <li id="R_5.035.018ab">śarajālāṃśumāñ śūraḥ kape rāmadivākaraḥ</li>
        <li id="R_5.035.018cd">śatrurakṣomayaṃ toyam upaśoṣaṃ nayiṣyati</li>
    </ul>
    <ul id="R_5.035.019">
        <li id="R_5.035.019ab">iti saṃjalpamānāṃ tāṃ rāmārthe śokakarśitām</li>
        <li id="R_5.035.019cd">aśrusaṃpūrṇavadanām uvāca hanumān kapiḥ</li>
    </ul>
    <ul id="R_5.035.020">
        <li id="R_5.035.020ab">śrutvaiva tu vaco mahyaṃ kṣipram eṣyati rāghavaḥ</li>
        <li id="R_5.035.020cd">camūṃ prakarṣan mahatīṃ haryṛkṣagaṇasaṃkulām</li>
    </ul>
    <ul id="R_5.035.021">
        <li id="R_5.035.021ab">atha vā mocayiṣyāmi tām adyaiva hi rākṣasāt</li>
        <li id="R_5.035.021cd">asmād duḥkhād upāroha mama pṛṣṭham anindite</li>
    </ul>
    <ul id="R_5.035.022">
        <li id="R_5.035.022ab">tvaṃ hi pṛṣṭhagatāṃ kṛtvā saṃtariṣyāmi sāgaram</li>
        <li id="R_5.035.022cd">śaktir asti hi me voḍhuṃ laṅkām api sarāvaṇām</li>
    </ul>
    <ul id="R_5.035.023">
        <li id="R_5.035.023ab">ahaṃ prasravaṇasthāya rāghavāyādya maithili</li>
        <li id="R_5.035.023cd">prāpayiṣyāmi śakrāya havyaṃ hutam ivānalaḥ</li>
    </ul>
    <ul id="R_5.035.024">
        <li id="R_5.035.024ab">drakṣyasy adyaiva vaidehi rāghavaṃ sahalakṣmaṇam</li>
        <li id="R_5.035.024cd">vyavasāya samāyuktaṃ viṣṇuṃ daityavadhe yathā</li>
    </ul>
    <ul id="R_5.035.025">
        <li id="R_5.035.025ab">tvaddarśanakṛtotsāham āśramasthaṃ mahābalam</li>
        <li id="R_5.035.025cd">puraṃdaram ivāsīnaṃ nāgarājasya mūrdhani</li>
    </ul>
    <ul id="R_5.035.026">
        <li id="R_5.035.026ab">pṛṣṭham āroha me devi mā vikāṅkṣasva śobhane</li>
        <li id="R_5.035.026cd">yogam anviccha rāmeṇa śaśāṅkeneva rohiṇī</li>
    </ul>
    <ul id="R_5.035.027">
        <li id="R_5.035.027ab">kathayantīva candreṇa sūryeṇeva suvarcalā</li>
        <li id="R_5.035.027cd">matpṛṣṭham adhiruhya tvaṃ tarākāśamahārṇavam</li>
    </ul>
    <ul id="R_5.035.028">
        <li id="R_5.035.028ab">na hi me saṃprayātasya tvām ito nayato 'ṅgane</li>
        <li id="R_5.035.028cd">anugantuṃ gatiṃ śaktāḥ sarve laṅkānivāsinaḥ</li>
    </ul>
    <ul id="R_5.035.029">
        <li id="R_5.035.029ab">yathaivāham iha prāptas tathaivāham asaṃśayam</li>
        <li id="R_5.035.029cd">yāsyāmi paśya vaidehi tvām udyamya vihāyasaṃ</li>
    </ul>
    <ul id="R_5.035.030">
        <li id="R_5.035.030ab">maithilī tu hariśreṣṭhāc chrutvā vacanam adbhutam</li>
        <li id="R_5.035.030cd">harṣavismitasarvāṅgī hanūmantam athābravīt</li>
    </ul>
    <ul id="R_5.035.031">
        <li id="R_5.035.031ab">hanūman dūram adhvanaṃ kathaṃ māṃ voḍhum icchasi</li>
        <li id="R_5.035.031cd">tad eva khalu te manye kapitvaṃ hariyūthapa</li>
    </ul>
    <ul id="R_5.035.032">
        <li id="R_5.035.032ab">kathaṃ vālpaśarīras tvaṃ mām ito netum icchasi</li>
        <li id="R_5.035.032cd">sakāśaṃ mānavendrasya bhartur me plavagarṣabha</li>
    </ul>
    <ul id="R_5.035.033">
        <li id="R_5.035.033ab">sītāyā vacanaṃ śrutvā hanūmān mārutātmajaḥ</li>
        <li id="R_5.035.033cd">cintayām āsa lakṣmīvān navaṃ paribhavaṃ kṛtam</li>
    </ul>
    <ul id="R_5.035.034">
        <li id="R_5.035.034ab">na me jānāti sattvaṃ vā prabhāvaṃ vāsitekṣaṇā</li>
        <li id="R_5.035.034cd">tasmāt paśyatu vaidehī yad rūpaṃ mama kāmataḥ</li>
    </ul>
    <ul id="R_5.035.035">
        <li id="R_5.035.035ab">iti saṃcintya hanumāṃs tadā plavagasattamaḥ</li>
        <li id="R_5.035.035cd">darśayām āsa vaidehyāḥ svarūpam arimardanaḥ</li>
    </ul>
    <ul id="R_5.035.036">
        <li id="R_5.035.036ab">sa tasmāt pādapād dhīmān āplutya plavagarṣabhaḥ</li>
        <li id="R_5.035.036cd">tato vardhitum ārebhe sītāpratyayakāraṇāt</li>
    </ul>
    <ul id="R_5.035.037">
        <li id="R_5.035.037ab">merumandārasaṃkāśo babhau dīptānalaprabhaḥ</li>
        <li id="R_5.035.037cd">agrato vyavatasthe ca sītāyā vānararṣabhaḥ</li>
    </ul>
    <ul id="R_5.035.038">
        <li id="R_5.035.038ab">hariḥ parvatasaṃkāśas tāmravaktro mahābalaḥ</li>
        <li id="R_5.035.038cd">vajradaṃṣṭranakho bhīmo vaidehīm idam abravīt</li>
    </ul>
    <ul id="R_5.035.039">
        <li id="R_5.035.039ab">saparvatavanoddeśāṃ sāṭṭaprākāratoraṇām</li>
        <li id="R_5.035.039cd">laṅkām imāṃ sanathāṃ vā nayituṃ śaktir asti me</li>
    </ul>
    <ul id="R_5.035.040">
        <li id="R_5.035.040ab">tad avasthāpya tāṃ buddhir alaṃ devi vikāṅkṣayā</li>
        <li id="R_5.035.040cd">viśokaṃ kuru vaidehi rāghavaṃ sahalakṣmaṇam</li>
    </ul>
    <ul id="R_5.035.041">
        <li id="R_5.035.041ab">taṃ dṛṣṭvācalasaṃkāśam uvāca janakātmajā</li>
        <li id="R_5.035.041cd">padmapatraviśālākṣī mārutasyaurasaṃ sutam</li>
    </ul>
    <ul id="R_5.035.042">
        <li id="R_5.035.042ab">tava sattvaṃ balaṃ caiva vijānāmi mahākape</li>
        <li id="R_5.035.042cd">vāyor iva gatiṃ cāpi tejaś cāgnir ivādbhutam</li>
    </ul>
    <ul id="R_5.035.043">
        <li id="R_5.035.043ab">prākṛto 'nyaḥ kathaṃ cemāṃ bhūmim āgantum arhati</li>
        <li id="R_5.035.043cd">udadher aprameyasya pāraṃ vānarapuṃgava</li>
    </ul>
    <ul id="R_5.035.044">
        <li id="R_5.035.044ab">jānāmi gamane śaktiṃ nayane cāpi te mama</li>
        <li id="R_5.035.044cd">avaśyaṃ sāmpradhāryāśu kāryasiddhir ihātmanaḥ</li>
    </ul>
    <ul id="R_5.035.045">
        <li id="R_5.035.045ab">ayuktaṃ tu kapiśreṣṭha mayā gantuṃ tvayā saha</li>
        <li id="R_5.035.045cd">vāyuvegasavegasya vego māṃ mohayet tava</li>
    </ul>
    <ul id="R_5.035.046">
        <li id="R_5.035.046ab">aham ākāśam āsaktā upary upari sāgaram</li>
        <li id="R_5.035.046cd">prapateyaṃ hi te pṛṣṭhād bhayād vegena gacchataḥ</li>
    </ul>
    <ul id="R_5.035.047">
        <li id="R_5.035.047ab">patitā sāgare cāhaṃ timinakrajhaṣākule</li>
        <li id="R_5.035.047cd">bhayeyam āśu vivaśā yādasām annam uttamam</li>
    </ul>
    <ul id="R_5.035.048">
        <li id="R_5.035.048ab">na ca śakṣye tvayā sārdhaṃ gantuṃ śatruvināśana</li>
        <li id="R_5.035.048cd">kalatravati saṃdehas tvayy api syād asaṃśayam</li>
    </ul>
    <ul id="R_5.035.049">
        <li id="R_5.035.049ab">hriyamāṇāṃ tu māṃ dṛṣṭvā rākṣasā bhīmavikramāḥ</li>
        <li id="R_5.035.049cd">anugaccheyur ādiṣṭā rāvaṇena durātmanā</li>
    </ul>
    <ul id="R_5.035.050">
        <li id="R_5.035.050ab">tais tvaṃ parivṛtaḥ śūraiḥ śūlam udgara pāṇibhiḥ</li>
        <li id="R_5.035.050cd">bhaves tvaṃ saṃśayaṃ prāpto mayā vīra kalatravān</li>
    </ul>
    <ul id="R_5.035.051">
        <li id="R_5.035.051ab">sāyudhā bahavo vyomni rākṣasās tvaṃ nirāyudhaḥ</li>
        <li id="R_5.035.051cd">kathaṃ śakṣyasi saṃyātuṃ māṃ caiva parirakṣitum</li>
    </ul>
    <ul id="R_5.035.052">
        <li id="R_5.035.052ab">yudhyamānasya rakṣobhis tatas taiḥ krūrakarmabhiḥ</li>
        <li id="R_5.035.052cd">prapateyaṃ hi te pṛṣṭhad bhayārtā kapisattama</li>
    </ul>
    <ul id="R_5.035.053">
        <li id="R_5.035.053ab">atha rakṣāṃsi bhīmāni mahānti balavanti ca</li>
        <li id="R_5.035.053cd">kathaṃ cit sāmparāye tvāṃ jayeyuḥ kapisattama</li>
    </ul>
    <ul id="R_5.035.054">
        <li id="R_5.035.054ab">atha vā yudhyamānasya pateyaṃ vimukhasya te</li>
        <li id="R_5.035.054cd">patitāṃ ca gṛhītvā māṃ nayeyuḥ pāparākṣasāḥ</li>
    </ul>
    <ul id="R_5.035.055">
        <li id="R_5.035.055ab">māṃ vā hareyus tvaddhastād viśaseyur athāpi vā</li>
        <li id="R_5.035.055cd">avyavasthau hi dṛśyete yuddhe jayaparājayau</li>
    </ul>
    <ul id="R_5.035.056">
        <li id="R_5.035.056ab">ahaṃ vāpi vipadyeyaṃ rakṣobhir abhitarjitā</li>
        <li id="R_5.035.056cd">tvatprayatno hariśreṣṭha bhaven niṣphala eva tu</li>
    </ul>
    <ul id="R_5.035.057">
        <li id="R_5.035.057ab">kāmaṃ tvam api paryāpto nihantuṃ sarvarākṣasān</li>
        <li id="R_5.035.057cd">rāghavasya yaśo hīyet tvayā śastais tu rākṣasaiḥ</li>
    </ul>
    <ul id="R_5.035.058">
        <li id="R_5.035.058ab">atha vādāya rakṣāṃsi nyasyeyuḥ saṃvṛte hi mām</li>
        <li id="R_5.035.058cd">yatra te nābhijānīyur harayo nāpi rāghavaḥ</li>
    </ul>
    <ul id="R_5.035.059">
        <li id="R_5.035.059ab">ārambhas tu madartho 'yaṃ tatas tava nirarthakaḥ</li>
        <li id="R_5.035.059cd">tvayā hi saha rāmasya mahān āgamane guṇaḥ</li>
    </ul>
    <ul id="R_5.035.060">
        <li id="R_5.035.060ab">mayi jīvitam āyattaṃ rāghavasya mahātmanaḥ</li>
        <li id="R_5.035.060cd">bhrātṝṇāṃ ca mahābāho tava rājakulasya ca</li>
    </ul>
    <ul id="R_5.035.061">
        <li id="R_5.035.061ab">tau nirāśau madarthe tu śokasaṃtāpakarśitau</li>
        <li id="R_5.035.061cd">saha sarvarkṣaharibhis tyakṣyataḥ prāṇasaṃgraham</li>
    </ul>
    <ul id="R_5.035.062">
        <li id="R_5.035.062ab">bhartur bhaktiṃ puraskṛtya rāmād anyasya vānara</li>
        <li id="R_5.035.062cd">nāhaṃ spraṣṭuṃ padā gātram iccheyaṃ vānarottama</li>
    </ul>
    <ul id="R_5.035.063">
        <li id="R_5.035.063ab">yad ahaṃ gātrasaṃsparśaṃ rāvaṇasya gatā balāt</li>
        <li id="R_5.035.063cd">anīśā kiṃ kariṣyāmi vināthā vivaśā satī</li>
    </ul>
    <ul id="R_5.035.064">
        <li id="R_5.035.064ab">yadi rāmo daśagrīvam iha hatvā sarākṣasaṃ</li>
        <li id="R_5.035.064cd">mām ito gṛhya gaccheta tat tasya sadṛśaṃ bhavet</li>
    </ul>
    <ul id="R_5.035.065">
        <li id="R_5.035.065ab">
            <seg type="pāda" n="a">śrutā hi dṛṣṭāś ca mayā parākramā</seg>
            <seg type="pāda" n="b">mahātmanas tasya raṇāvamardinaḥ</seg>
        </li>
        <li id="R_5.035.065cd">
            <seg type="pāda" n="c">na devagandharvabhujaṃgarākṣasā</seg>
            <seg type="pāda" n="d">bhavanti rāmeṇa samā hi saṃyuge</seg>
        </li>
    </ul>
    <ul id="R_5.035.066">
        <li id="R_5.035.066ab">
            <seg type="pāda" n="a">samīkṣya taṃ saṃyati citrakārmukaṃ</seg>
            <seg type="pāda" n="b">mahābalaṃ vāsavatulyavikramam</seg>
        </li>
        <li id="R_5.035.066cd">
            <seg type="pāda" n="c">salakṣmaṇaṃ ko viṣaheta rāghavaṃ</seg>
            <seg type="pāda" n="d">hutāśanaṃ dīptam ivānileritam</seg>
        </li>
    </ul>
    <ul id="R_5.035.067">
        <li id="R_5.035.067ab">
            <seg type="pāda" n="a">salakṣmaṇaṃ rāghavam ājimardanaṃ</seg>
            <seg type="pāda" n="b">diśāgajaṃ mattam iva vyavasthitam</seg>
        </li>
        <li id="R_5.035.067cd">
            <seg type="pāda" n="c">saheta ko vānaramukhya saṃyuge</seg>
            <seg type="pāda" n="d">yugāntasūryapratimaṃ śarārciṣam</seg>
        </li>
    </ul>
    <ul id="R_5.035.068">
        <li id="R_5.035.068ab">
            <seg type="pāda" n="a">sa me hariśreṣṭha salakṣmaṇaṃ patiṃ</seg>
            <seg type="pāda" n="b">sayūthapaṃ kṣipram ihopapādaya</seg>
        </li>
        <li id="R_5.035.068cd">
            <seg type="pāda" n="c">cirāya rāmaṃ prati śokakarśitāṃ</seg>
            <seg type="pāda" n="d">kuruṣva māṃ vānaramukhya harṣitām</seg>
        </li>
    </ul>
    <ul id="R_5.036.001">
        <li id="R_5.036.001ab">tataḥ sa kapiśārdūlas tena vākyena harṣitaḥ</li>
        <li id="R_5.036.001cd">sītām uvāca tac chrutvā vākyaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_5.036.002">
        <li id="R_5.036.002ab">yuktarūpaṃ tvayā devi bhāṣitaṃ śubhadarśane</li>
        <li id="R_5.036.002cd">sadṛśaṃ strīsvabhāvasya sādhvīnāṃ vinayasya ca</li>
    </ul>
    <ul id="R_5.036.003">
        <li id="R_5.036.003ab">strītvaṃ na tu samarthaṃ hi sāgaraṃ vyativartitum</li>
        <li id="R_5.036.003cd">mām adhiṣṭhāya vistīrṇaṃ śatayojanam āyatam</li>
    </ul>
    <ul id="R_5.036.004">
        <li id="R_5.036.004ab">dvitīyaṃ kāraṇaṃ yac ca bravīṣi vinayānvite</li>
        <li id="R_5.036.004cd">rāmād anyasya nārhāmi saṃsparśam iti jānaki</li>
    </ul>
    <ul id="R_5.036.005">
        <li id="R_5.036.005ab">etat te devi sadṛśaṃ patnyās tasya mahātmanaḥ</li>
        <li id="R_5.036.005cd">kā hy anyā tvām ṛte devi brūyād vacanam īdṛśam</li>
    </ul>
    <ul id="R_5.036.006">
        <li id="R_5.036.006ab">śroṣyate caiva kākutsthaḥ sarvaṃ niravaśeṣataḥ</li>
        <li id="R_5.036.006cd">ceṣṭitaṃ yat tvayā devi bhāṣitaṃ mama cāgrataḥ</li>
    </ul>
    <ul id="R_5.036.007">
        <li id="R_5.036.007ab">kāraṇair bahubhir devi rāma priyacikīrṣayā</li>
        <li id="R_5.036.007cd">snehapraskannamanasā mayaitat samudīritam</li>
    </ul>
    <ul id="R_5.036.008">
        <li id="R_5.036.008ab">laṅkāyā duṣpraveśatvād dustaratvān mahodadheḥ</li>
        <li id="R_5.036.008cd">sāmarthyād ātmanaś caiva mayaitat samudāhṛtam</li>
    </ul>
    <ul id="R_5.036.009">
        <li id="R_5.036.009ab">icchāmi tvāṃ samānetum adyaiva raghubandhunā</li>
        <li id="R_5.036.009cd">gurusnehena bhaktyā ca nānyathā tad udāhṛtam</li>
    </ul>
    <ul id="R_5.036.010">
        <li id="R_5.036.010ab">yadi notsahase yātuṃ mayā sārdham anindite</li>
        <li id="R_5.036.010cd">abhijñānaṃ prayaccha tvaṃ jānīyād rāghavo hi yat</li>
    </ul>
    <ul id="R_5.036.011">
        <li id="R_5.036.011ab">evam uktā hanumatā sītā surasutopamā</li>
        <li id="R_5.036.011cd">uvāca vacanaṃ mandaṃ bāṣpapragrathitākṣaram</li>
    </ul>
    <ul id="R_5.036.012">
        <li id="R_5.036.012ab">idaṃ śreṣṭham abhijñānaṃ brūyās tvaṃ tu mama priyam</li>
        <li id="R_5.036.012cd">śailasya citrakūṭasya pāde pūrvottare tadā</li>
    </ul>
    <ul id="R_5.036.013">
        <li id="R_5.036.013ab">tāpasāśramavāsinyāḥ prājyamūlaphalodake</li>
        <li id="R_5.036.013cd">tasmin siddhāśrame deśe mandākinyā adūrataḥ</li>
    </ul>
    <ul id="R_5.036.014">
        <li id="R_5.036.014ab">tasyopavanaṣaṇḍeṣu nānāpuṣpasugandhiṣu</li>
        <li id="R_5.036.014cd">vihṛtya salilaklinnā tavāṅke samupāviśam</li>
    </ul>
    <ul id="R_5.036.015">
        <li id="R_5.036.015ab">paryāyeṇa prasuptaś ca mamāṅke bharatāgrajaḥ</li>
    </ul>
    <ul id="R_5.036.016">
        <li id="R_5.036.016ab">tato māṃsasamāyukto vāyasaḥ paryatuṇḍayat</li>
        <li id="R_5.036.016cd">tam ahaṃ loṣṭam udyamya vārayāmi sma vāyasaṃ</li>
    </ul>
    <ul id="R_5.036.017">
        <li id="R_5.036.017ab">dārayan sa ca māṃ kākas tatraiva parilīyate</li>
        <li id="R_5.036.017cd">na cāpy uparaman māṃsād bhakṣārthī balibhojanaḥ</li>
    </ul>
    <ul id="R_5.036.018">
        <li id="R_5.036.018ab">utkarṣantyāṃ ca raśanāṃ kruddhāyāṃ mayi pakṣiṇe</li>
        <li id="R_5.036.018cd">sraṃsamāne ca vasane tato dṛṣṭā tvayā hy aham</li>
    </ul>
    <ul id="R_5.036.019">
        <li id="R_5.036.019ab">tvayā vihasitā cāhaṃ kruddhā saṃlajjitā tadā</li>
        <li id="R_5.036.019cd">bhakṣya gṛddhena kālena dāritā tvām upāgatā</li>
    </ul>
    <ul id="R_5.036.020">
        <li id="R_5.036.020ab">āsīnasya ca te śrāntā punar utsaṅgam āviśam</li>
        <li id="R_5.036.020cd">krudhyantī ca prahṛṣṭena tvayāhaṃ parisāntvitā</li>
    </ul>
    <ul id="R_5.036.021">
        <li id="R_5.036.021ab">bāṣpapūrṇamukhī mandaṃ cakṣuṣī parimārjatī</li>
        <li id="R_5.036.021cd">lakṣitāhaṃ tvayā nātha vāyasena prakopitā</li>
    </ul>
    <ul id="R_5.036.022">
        <li id="R_5.036.022ab">āśīviṣa iva kruddhaḥ śvasān vākyam abhāṣathāḥ</li>
        <li id="R_5.036.022cd">kena te nāganāsoru vikṣataṃ vai stanāntaram</li>
        <li id="R_5.036.022ef">kaḥ krīḍati saroṣeṇa pañcavaktreṇa bhoginā</li>
    </ul>
    <ul id="R_5.036.023">
        <li id="R_5.036.023ab">vīkṣamāṇas tatas taṃ vai vāyasaṃ samavaikṣathāḥ</li>
        <li id="R_5.036.023cd">nakhaiḥ sarudhirais tīkṣṇair mām evābhimukhaṃ sthitam</li>
    </ul>
    <ul id="R_5.036.024">
        <li id="R_5.036.024ab">putraḥ kila sa śakrasya vāyasaḥ patatāṃ varaḥ</li>
        <li id="R_5.036.024cd">dharāntaracaraḥ śīghraṃ pavanasya gatau samaḥ</li>
    </ul>
    <ul id="R_5.036.025">
        <li id="R_5.036.025ab">tatas tasmin mahābāhuḥ kopasaṃvartitekṣaṇaḥ</li>
        <li id="R_5.036.025cd">vāyase kṛtavān krūrāṃ matiṃ matimatāṃ vara</li>
    </ul>
    <ul id="R_5.036.026">
        <li id="R_5.036.026ab">sa darbhasaṃstarād gṛhya brahmaṇo 'streṇa yojayaḥ</li>
        <li id="R_5.036.026cd">sa dīpta iva kālāgnir jajvālābhimukho dvijam</li>
    </ul>
    <ul id="R_5.036.027">
        <li id="R_5.036.027ab">cikṣepitha pradīptāṃ tām iṣīkāṃ vāyasaṃ prati</li>
        <li id="R_5.036.027cd">anusṛṣṭas tadā kālo jagāma vividhāṃ gatim</li>
        <li id="R_5.036.027ef">trāṇakāma imaṃ lokaṃ sarvaṃ vai vicacāra ha</li>
    </ul>
    <ul id="R_5.036.028">
        <li id="R_5.036.028ab">sa pitrā ca parityaktaḥ suraiḥ sarvair maharṣibhiḥ</li>
        <li id="R_5.036.028cd">trīṃl lokān saṃparikramya tvām eva śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_5.036.029">
        <li id="R_5.036.029ab">taṃ tvaṃ nipatitaṃ bhūmau śaraṇyaḥ śaraṇāgatam</li>
        <li id="R_5.036.029cd">vadhārham api kākutstha kṛpayā paryapālayaḥ</li>
        <li id="R_5.036.029ef">na śarma labdhvā lokeṣu tvām eva śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_5.036.030">
        <li id="R_5.036.030ab">paridyūnaṃ viṣaṇṇaṃ ca sa tvam āyāntam uktavān</li>
        <li id="R_5.036.030cd">moghaṃ kartuṃ na śakyaṃ tu brāhmam astraṃ tad ucyatām</li>
    </ul>
    <ul id="R_5.036.031">
        <li id="R_5.036.031ab">tatas tasyākṣi kākasya hinasti sma sa dakṣiṇam</li>
    </ul>
    <ul id="R_5.036.032">
        <li id="R_5.036.032ab">sa te tadā namaskṛtvā rājñe daśarathāya ca</li>
        <li id="R_5.036.032cd">tvayā vīra visṛṣṭas tu pratipede svam ālayam</li>
    </ul>
    <ul id="R_5.036.033">
        <li id="R_5.036.033ab">matkṛte kākamātre 'pi brahmāstraṃ samudīritam</li>
        <li id="R_5.036.033cd">kasmād yo māṃ harat tvattaḥ kṣamase taṃ mahīpate</li>
    </ul>
    <ul id="R_5.036.034">
        <li id="R_5.036.034ab">sa kuruṣva mahotsāhaṃ kṛpāṃ mayi nararṣabha</li>
        <li id="R_5.036.034cd">ānṛśaṃsyaṃ paro dharmas tvatta eva mayā śrutaḥ</li>
    </ul>
    <ul id="R_5.036.035">
        <li id="R_5.036.035ab">jānāmi tvāṃ mahāvīryaṃ mahotsāhaṃ mahābalam</li>
        <li id="R_5.036.035cd">apārapāram akṣobhyaṃ gāmbhīryāt sāgaropamam</li>
        <li id="R_5.036.035ef">bhartāraṃ sasamudrāyā dharaṇyā vāsavopamam</li>
    </ul>
    <ul id="R_5.036.036">
        <li id="R_5.036.036ab">evam astravidāṃ śreṣṭhaḥ sattvavān balavān api</li>
        <li id="R_5.036.036cd">kimartham astraṃ rakṣaḥsu na yojayasi rāghava</li>
    </ul>
    <ul id="R_5.036.037">
        <li id="R_5.036.037ab">na nāgā nāpi gandharvā nāsurā na marudgaṇāḥ</li>
        <li id="R_5.036.037cd">rāmasya samare vegaṃ śaktāḥ prati samādhitum</li>
    </ul>
    <ul id="R_5.036.038">
        <li id="R_5.036.038ab">tasyā vīryavataḥ kaś cid yady asti mayi saṃbhramaḥ</li>
        <li id="R_5.036.038cd">kimarthaṃ na śarais tīkṣṇaiḥ kṣayaṃ nayati rākṣasān</li>
    </ul>
    <ul id="R_5.036.039">
        <li id="R_5.036.039ab">bhrātur ādeśam ādāya lakṣmaṇo vā paraṃtapaḥ</li>
        <li id="R_5.036.039cd">kasya hetor na māṃ vīraḥ paritrāti mahābalaḥ</li>
    </ul>
    <ul id="R_5.036.040">
        <li id="R_5.036.040ab">yadi tau puruṣavyāghrau vāyvindrasamatejasau</li>
        <li id="R_5.036.040cd">surāṇām api durdharṣo kimarthaṃ mām upekṣataḥ</li>
    </ul>
    <ul id="R_5.036.041">
        <li id="R_5.036.041ab">mamaiva duṣkṛtaṃ kiṃ cin mahad asti na saṃśayaḥ</li>
        <li id="R_5.036.041cd">samarthāv api tau yan māṃ nāvekṣete paraṃtapau</li>
    </ul>
    <ul id="R_5.036.042">
        <li id="R_5.036.042ab">kausalyā lokabhartāraṃ suṣuve yaṃ manasvinī</li>
        <li id="R_5.036.042cd">taṃ mamārthe sukhaṃ pṛccha śirasā cābhivādaya</li>
    </ul>
    <ul id="R_5.036.043">
        <li id="R_5.036.043ab">srajaś ca sarvaratnāni priyā yāś ca varāṅganāḥ</li>
        <li id="R_5.036.043cd">aiśvaryaṃ ca viśālāyāṃ pṛthivyām api durlabham</li>
    </ul>
    <ul id="R_5.036.044">
        <li id="R_5.036.044ab">pitaraṃ mātaraṃ caiva saṃmānyābhiprasādya ca</li>
        <li id="R_5.036.044cd">anupravrajito rāmaṃ sumitrā yena suprajāḥ</li>
        <li id="R_5.036.044ef">ānukūlyena dharmātmā tyaktvā sukham anuttamam</li>
    </ul>
    <ul id="R_5.036.045">
        <li id="R_5.036.045ab">anugacchati kākutsthaṃ bhrātaraṃ pālayan vane</li>
        <li id="R_5.036.045cd">siṃhaskandho mahābāhur manasvī priyadarśanaḥ</li>
    </ul>
    <ul id="R_5.036.046">
        <li id="R_5.036.046ab">pitṛvad vartate rāme mātṛvan māṃ samācaran</li>
        <li id="R_5.036.046cd">hriyamāṇāṃ tadā vīro na tu māṃ veda lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.036.047">
        <li id="R_5.036.047ab">vṛddhopasevī lakṣmīvāñ śakto na bahubhāṣitā</li>
        <li id="R_5.036.047cd">rājaputraḥ priyaśreṣṭhaḥ sadṛśaḥ śvaśurasya me</li>
    </ul>
    <ul id="R_5.036.048">
        <li id="R_5.036.048ab">mattaḥ priyataro nityaṃ bhrātā rāmasya lakṣmaṇaḥ</li>
        <li id="R_5.036.048cd">niyukto dhuri yasyāṃ tu tām udvahati vīryavān</li>
    </ul>
    <ul id="R_5.036.049">
        <li id="R_5.036.049ab">yaṃ dṛṣṭvā rāghavo naiva vṛddham āryam anusmarat</li>
        <li id="R_5.036.049cd">sa mamārthāya kuśalaṃ vaktavyo vacanān mama</li>
        <li id="R_5.036.049ef">mṛdur nityaṃ śucir dakṣaḥ priyo rāmasya lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.036.050">
        <li id="R_5.036.050ab">idaṃ brūyāś ca me nāthaṃ śūraṃ rāmaṃ punaḥ punaḥ</li>
        <li id="R_5.036.050cd">jīvitaṃ dhārayiṣyāmi māsaṃ daśarathātmaja</li>
        <li id="R_5.036.050ef">ūrdhvaṃ māsān na jīveyaṃ satyenāhaṃ bravīmi te</li>
    </ul>
    <ul id="R_5.036.051">
        <li id="R_5.036.051ab">rāvaṇenoparuddhāṃ māṃ nikṛtyā pāpakarmaṇā</li>
        <li id="R_5.036.051cd">trātum arhasi vīra tvaṃ pātālād iva kauśikīm</li>
    </ul>
    <ul id="R_5.036.052">
        <li id="R_5.036.052ab">tato vastragataṃ muktvā divyaṃ cūḍāmaṇiṃ śubham</li>
        <li id="R_5.036.052cd">pradeyo rāghavāyeti sītā hanumate dadau</li>
    </ul>
    <ul id="R_5.036.053">
        <li id="R_5.036.053ab">pratigṛhya tato vīro maṇiratnam anuttamam</li>
        <li id="R_5.036.053cd">aṅgulyā yojayām āsa na hy asyā prābhavad bhujaḥ</li>
    </ul>
    <ul id="R_5.036.054">
        <li id="R_5.036.054ab">maṇiratnaṃ kapivaraḥ pratigṛhyābhivādya ca</li>
        <li id="R_5.036.054cd">sītāṃ pradakṣiṇaṃ kṛtvā praṇataḥ pārśvataḥ sthitaḥ</li>
    </ul>
    <ul id="R_5.036.055">
        <li id="R_5.036.055ab">harṣeṇa mahatā yuktaḥ sītādarśanajena saḥ</li>
        <li id="R_5.036.055cd">hṛdayena gato rāmaṃ śarīreṇa tu viṣṭhitaḥ</li>
    </ul>
    <ul id="R_5.036.056">
        <li id="R_5.036.056ab">
            <seg type="pāda" n="a">maṇivaram upagṛhya taṃ mahārhaṃ</seg>
            <seg type="pāda" n="b">janakanṛpātmajayā dhṛtaṃ prabhāvāt</seg>
        </li>
        <li id="R_5.036.056cd">
            <seg type="pāda" n="c">girivarapavanāvadhūtamuktaḥ</seg>
            <seg type="pāda" n="d">sukhitamanāḥ pratisaṃkramaṃ prapede</seg>
        </li>
    </ul>
    <ul id="R_5.037.001">
        <li id="R_5.037.001ab">maṇiṃ dattvā tataḥ sītā hanūmantam athābravīt</li>
        <li id="R_5.037.001cd">abhijñānam abhijñātam etad rāmasya tattvataḥ</li>
    </ul>
    <ul id="R_5.037.002">
        <li id="R_5.037.002ab">maṇiṃ tu dṛṣṭvā rāmo vai trayāṇāṃ saṃsmariṣyati</li>
        <li id="R_5.037.002cd">vīro jananyā mama ca rājño daśarathasya ca</li>
    </ul>
    <ul id="R_5.037.003">
        <li id="R_5.037.003ab">sa bhūyas tvaṃ samutsāhe codito harisattama</li>
        <li id="R_5.037.003cd">asmin kāryasamārambhe pracintaya yaduttaram</li>
    </ul>
    <ul id="R_5.037.004">
        <li id="R_5.037.004ab">tvam asmin kāryaniryoge pramāṇaṃ harisattama</li>
        <li id="R_5.037.004cd">tasya cintaya yo yatno duḥkhakṣayakaro bhavet</li>
    </ul>
    <ul id="R_5.037.005">
        <li id="R_5.037.005ab">sa tatheti pratijñāya mārutir bhīmavikramaḥ</li>
        <li id="R_5.037.005cd">śirasāvandya vaidehīṃ gamanāyopacakrame</li>
    </ul>
    <ul id="R_5.037.006">
        <li id="R_5.037.006ab">jñātvā saṃprasthitaṃ devī vānaraṃ mārutātmajam</li>
        <li id="R_5.037.006cd">bāṣpagadgadayā vācā maithilī vākyam abravīt</li>
    </ul>
    <ul id="R_5.037.007">
        <li id="R_5.037.007ab">kuśalaṃ hanuman brūyāḥ sahitau rāmalakṣmaṇau</li>
        <li id="R_5.037.007cd">sugrīvaṃ ca sahāmātyaṃ vṛddhān sarvāṃś ca vānarān</li>
    </ul>
    <ul id="R_5.037.008">
        <li id="R_5.037.008ab">yathā ca sa mahābāhur māṃ tārayati rāghavaḥ</li>
        <li id="R_5.037.008cd">asmād duḥkhāmbusaṃrodhāt tvaṃ samādhātum arhasi</li>
    </ul>
    <ul id="R_5.037.009">
        <li id="R_5.037.009ab">jīvantīṃ māṃ yathā rāmaḥ saṃbhāvayati kīrtimān</li>
        <li id="R_5.037.009cd">tat tvayā hanuman vācyaṃ vācā dharmam avāpnuhi</li>
    </ul>
    <ul id="R_5.037.010">
        <li id="R_5.037.010ab">nityam utsāhayuktāś ca vācaḥ śrutvā mayeritāḥ</li>
        <li id="R_5.037.010cd">vardhiṣyate dāśaratheḥ pauruṣaṃ madavāptaye</li>
    </ul>
    <ul id="R_5.037.011">
        <li id="R_5.037.011ab">matsaṃdeśayutā vācas tvattaḥ śrutvaiva rāghavaḥ</li>
        <li id="R_5.037.011cd">parākramavidhiṃ vīro vidhivat saṃvidhāsyati</li>
    </ul>
    <ul id="R_5.037.012">
        <li id="R_5.037.012ab">sītāyās tad vacaḥ śrutvā hanumān mārutātmajaḥ</li>
        <li id="R_5.037.012cd">śirasy añjalim ādhāya vākyam uttaram abravīt</li>
    </ul>
    <ul id="R_5.037.013">
        <li id="R_5.037.013ab">kṣipram eṣyati kākutstho haryṛkṣapravarair vṛtaḥ</li>
        <li id="R_5.037.013cd">yas te yudhi vijityārīñ śokaṃ vyapanayiṣyati</li>
    </ul>
    <ul id="R_5.037.014">
        <li id="R_5.037.014ab">na hi paśyāmi martyeṣu nāmareṣv asureṣu vā</li>
        <li id="R_5.037.014cd">yas tasya vamato bāṇān sthātum utsahate 'grataḥ</li>
    </ul>
    <ul id="R_5.037.015">
        <li id="R_5.037.015ab">apy arkam api parjanyam api vaivasvataṃ yamam</li>
        <li id="R_5.037.015cd">sa hi soḍhuṃ raṇe śaktas tavahetor viśeṣataḥ</li>
    </ul>
    <ul id="R_5.037.016">
        <li id="R_5.037.016ab">sa hi sāgaraparyantāṃ mahīṃ śāsitum īhate</li>
        <li id="R_5.037.016cd">tvan nimitto hi rāmasya jayo janakanandini</li>
    </ul>
    <ul id="R_5.037.017">
        <li id="R_5.037.017ab">tasya tad vacanaṃ śrutvā samyak satyaṃ subhāṣitam</li>
        <li id="R_5.037.017cd">jānakī bahu mene 'tha vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_5.037.018">
        <li id="R_5.037.018ab">tatas taṃ prasthitaṃ sītā vīkṣamāṇā punaḥ punaḥ</li>
        <li id="R_5.037.018cd">bhartuḥ snehānvitaṃ vākyaṃ sauhārdād anumānayat</li>
    </ul>
    <ul id="R_5.037.019">
        <li id="R_5.037.019ab">yadi vā manyase vīra vasaikāham ariṃdama</li>
        <li id="R_5.037.019cd">kasmiṃś cit saṃvṛte deśe viśrāntaḥ śvo gamiṣyasi</li>
    </ul>
    <ul id="R_5.037.020">
        <li id="R_5.037.020ab">mama ced alpabhāgyāyāḥ sāmnidhyāt tava vīryavān</li>
        <li id="R_5.037.020cd">asya śokasya mahato muhūrtaṃ mokṣaṇaṃ bhavet</li>
    </ul>
    <ul id="R_5.037.021">
        <li id="R_5.037.021ab">gate hi hariśārdūla punarāgamanāya tu</li>
        <li id="R_5.037.021cd">prāṇānām api saṃdeho mama syān nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_5.037.022">
        <li id="R_5.037.022ab">tavādarśanajaḥ śoko bhūyo māṃ paritāpayet</li>
        <li id="R_5.037.022cd">duḥkhād duḥkhaparāmṛṣṭāṃ dīpayann iva vānara</li>
    </ul>
    <ul id="R_5.037.023">
        <li id="R_5.037.023ab">ayaṃ ca vīra saṃdehas tiṣṭhatīva mamāgrataḥ</li>
        <li id="R_5.037.023cd">sumahāṃs tvatsahāyeṣu haryṛkṣeṣu harīśvara</li>
    </ul>
    <ul id="R_5.037.024">
        <li id="R_5.037.024ab">kathaṃ nu khalu duṣpāraṃ tariṣyanti mahodadhim</li>
        <li id="R_5.037.024cd">tāni haryṛkṣasainyāni tau vā naravarātmajau</li>
    </ul>
    <ul id="R_5.037.025">
        <li id="R_5.037.025ab">trayāṇām eva bhūtānāṃ sāgarasyeha laṅghane</li>
        <li id="R_5.037.025cd">śaktiḥ syād vainateyasya tava vā mārutasya vā</li>
    </ul>
    <ul id="R_5.037.026">
        <li id="R_5.037.026ab">tad asmin kāryaniryoge vīraivaṃ duratikrame</li>
        <li id="R_5.037.026cd">kiṃ paśyasi samādhānaṃ tvaṃ hi kāryavidāṃ varaḥ</li>
    </ul>
    <ul id="R_5.037.027">
        <li id="R_5.037.027ab">kāmam asya tvam evaikaḥ kāryasya parisādhane</li>
        <li id="R_5.037.027cd">paryāptaḥ paravīraghna yaśasyas te balodayaḥ</li>
    </ul>
    <ul id="R_5.037.028">
        <li id="R_5.037.028ab">balaiḥ samagrair yadi māṃ rāvaṇaṃ jitya saṃyuge</li>
        <li id="R_5.037.028cd">vijayī svapuraṃ yāyāt tat tu me syād yaśaskaram</li>
    </ul>
    <ul id="R_5.037.029">
        <li id="R_5.037.029ab">balais tu saṃkulāṃ kṛtvā laṅkāṃ parabalārdanaḥ</li>
        <li id="R_5.037.029cd">māṃ nayed yadi kākutsthas tat tasya sadṛśaṃ bhavet</li>
    </ul>
    <ul id="R_5.037.030">
        <li id="R_5.037.030ab">tad yathā tasya vikrāntam anurūpaṃ mahātmanaḥ</li>
        <li id="R_5.037.030cd">bhaved āhava śūrasya tathā tvam upapādaya</li>
    </ul>
    <ul id="R_5.037.031">
        <li id="R_5.037.031ab">tad arthopahitaṃ vākyaṃ sahitaṃ hetusaṃhitam</li>
        <li id="R_5.037.031cd">niśamya hanumāñ śeṣaṃ vākyam uttaram abravīt</li>
    </ul>
    <ul id="R_5.037.032">
        <li id="R_5.037.032ab">devi haryṛkṣasainyānām īśvaraḥ plavatāṃ varaḥ</li>
        <li id="R_5.037.032cd">sugrīvaḥ sattvasaṃpannas tavārthe kṛtaniścayaḥ</li>
    </ul>
    <ul id="R_5.037.033">
        <li id="R_5.037.033ab">sa vānarasahasrāṇāṃ koṭībhir abhisaṃvṛtaḥ</li>
        <li id="R_5.037.033cd">kṣipram eṣyati vaidehi rākṣasānāṃ nibarhaṇaḥ</li>
    </ul>
    <ul id="R_5.037.034">
        <li id="R_5.037.034ab">tasya vikramasaṃpannāḥ sattvavanto mahābalāḥ</li>
        <li id="R_5.037.034cd">manaḥsaṃkalpasaṃpātā nideśe harayaḥ sthitāḥ</li>
    </ul>
    <ul id="R_5.037.035">
        <li id="R_5.037.035ab">yeṣāṃ nopari nādhastān na tiryak sajjate gatiḥ</li>
        <li id="R_5.037.035cd">na ca karmasu sīdanti mahatsv amitatejasaḥ</li>
    </ul>
    <ul id="R_5.037.036">
        <li id="R_5.037.036ab">asakṛt tair mahotsahaiḥ sasāgaradharādharā</li>
        <li id="R_5.037.036cd">pradakṣiṇīkṛtā bhūmir vāyumārgānusāribhiḥ</li>
    </ul>
    <ul id="R_5.037.037">
        <li id="R_5.037.037ab">madviśiṣṭāś ca tulyāś ca santi tatra vanaukasaḥ</li>
        <li id="R_5.037.037cd">mattaḥ pratyavaraḥ kaś cin nāsti sugrīvasaṃnidhau</li>
    </ul>
    <ul id="R_5.037.038">
        <li id="R_5.037.038ab">ahaṃ tāvad iha prāptaḥ kiṃ punas te mahābalāḥ</li>
        <li id="R_5.037.038cd">na hi prakṛṣṭāḥ preṣyante preṣyante hītare janāḥ</li>
    </ul>
    <ul id="R_5.037.039">
        <li id="R_5.037.039ab">tad alaṃ paritāpena devi śoko vyapaitu te</li>
        <li id="R_5.037.039cd">ekotpātena te laṅkām eṣyanti hariyūthapāḥ</li>
    </ul>
    <ul id="R_5.037.040">
        <li id="R_5.037.040ab">mama pṛṣṭhagatau tau ca candrasūryāv ivoditau</li>
        <li id="R_5.037.040cd">tvatsakāśaṃ mahāsattvau nṛsiṃhāv āgamiṣyataḥ</li>
    </ul>
    <ul id="R_5.037.041">
        <li id="R_5.037.041ab">tau hi vīrau naravarau sahitau rāmalakṣmaṇau</li>
        <li id="R_5.037.041cd">āgamya nagarīṃ laṅkāṃ sāyakair vidhamiṣyataḥ</li>
    </ul>
    <ul id="R_5.037.042">
        <li id="R_5.037.042ab">sagaṇaṃ rāvaṇaṃ hatvā rāghavo raghunandanaḥ</li>
        <li id="R_5.037.042cd">tvām ādāya varārohe svapuraṃ pratiyāsyati</li>
    </ul>
    <ul id="R_5.037.043">
        <li id="R_5.037.043ab">tad āśvasihi bhadraṃ te bhava tvaṃ kālakāṅkṣiṇī</li>
        <li id="R_5.037.043cd">nacirād drakṣyase rāmaṃ prajvajantam ivānilam</li>
    </ul>
    <ul id="R_5.037.044">
        <li id="R_5.037.044ab">nihate rākṣasendre ca saputrāmātyabāndhave</li>
        <li id="R_5.037.044cd">tvaṃ sameṣyasi rāmeṇa śaśāṅkeneva rohiṇī</li>
    </ul>
    <ul id="R_5.037.045">
        <li id="R_5.037.045ab">kṣipraṃ tvaṃ devi śokasya pāraṃ yāsyasi maithili</li>
        <li id="R_5.037.045cd">rāvaṇaṃ caiva rāmeṇa nihataṃ drakṣyase 'cirāt</li>
    </ul>
    <ul id="R_5.037.046">
        <li id="R_5.037.046ab">evam āśvasya vaidehīṃ hanūmān mārutātmajaḥ</li>
        <li id="R_5.037.046cd">gamanāya matiṃ kṛtvā vaidehīṃ punar abravīt</li>
    </ul>
    <ul id="R_5.037.047">
        <li id="R_5.037.047ab">tam arighnaṃ kṛtātmānaṃ kṣipraṃ drakṣyasi rāghavam</li>
        <li id="R_5.037.047cd">lakṣmaṇaṃ ca dhanuṣpāṇiṃ laṅkādvāram upasthitam</li>
    </ul>
    <ul id="R_5.037.048">
        <li id="R_5.037.048ab">nakhadaṃṣṭrāyudhān vīrān siṃhaśārdūlavikramān</li>
        <li id="R_5.037.048cd">vānarān vāraṇendrābhān kṣipraṃ drakṣyasi saṃgatān</li>
    </ul>
    <ul id="R_5.037.049">
        <li id="R_5.037.049ab">śailāmbudanikāśānāṃ laṅkāmalayasānuṣu</li>
        <li id="R_5.037.049cd">nardatāṃ kapimukhyānām ārye yūthāny anekaśaḥ</li>
    </ul>
    <ul id="R_5.037.050">
        <li id="R_5.037.050ab">sa tu marmaṇi ghoreṇa tāḍito manmatheṣuṇā</li>
        <li id="R_5.037.050cd">na śarma labhate rāmaḥ siṃhārdita iva dvipaḥ</li>
    </ul>
    <ul id="R_5.037.051">
        <li id="R_5.037.051ab">mā rudo devi śokena mā bhūt te manaso 'priyam</li>
        <li id="R_5.037.051cd">śacīva pathyā śakreṇa bhartrā nāthavatī hy asi</li>
    </ul>
    <ul id="R_5.037.052">
        <li id="R_5.037.052ab">rāmād viśiṣṭaḥ ko 'nyo 'sti kaś cit saumitriṇā samaḥ</li>
        <li id="R_5.037.052cd">agnimārutakalpau tau bhrātarau tava saṃśrayau</li>
    </ul>
    <ul id="R_5.037.053">
        <li id="R_5.037.053ab">
            <seg type="pāda" n="a">nāsmiṃś ciraṃ vatsyasi devi deśe</seg>
            <seg type="pāda" n="b">rakṣogaṇair adhyuṣito 'tiraudre</seg>
        </li>
        <li id="R_5.037.053cd">
            <seg type="pāda" n="c">na te cirād āgamanaṃ priyasya</seg>
            <seg type="pāda" n="d">kṣamasva matsaṃgamakālamātram</seg>
        </li>
    </ul>
    <ul id="R_5.038.001">
        <li id="R_5.038.001ab">śrutvā tu vacanaṃ tasya vāyusūnor mahātmanaḥ</li>
        <li id="R_5.038.001cd">uvācātmahitaṃ vākyaṃ sītā surasutopamā</li>
    </ul>
    <ul id="R_5.038.002">
        <li id="R_5.038.002ab">tvāṃ dṛṣṭvā priyavaktāraṃ saṃprahṛṣyāmi vānara</li>
        <li id="R_5.038.002cd">ardhasaṃjātasasyeva vṛṣṭiṃ prāpya vasuṃdharā</li>
    </ul>
    <ul id="R_5.038.003">
        <li id="R_5.038.003ab">yathā taṃ puruṣavyāghraṃ gātraiḥ śokābhikarśitaiḥ</li>
        <li id="R_5.038.003cd">saṃspṛśeyaṃ sakāmāhaṃ tathā kuru dayāṃ mayi</li>
    </ul>
    <ul id="R_5.038.004">
        <li id="R_5.038.004ab">abhijñānaṃ ca rāmasya dattaṃ harigaṇottama</li>
        <li id="R_5.038.004cd">kṣiptām īṣikāṃ kākasya kopād ekākṣiśātanīm</li>
    </ul>
    <ul id="R_5.038.005">
        <li id="R_5.038.005ab">manaḥśilāyās tikalo gaṇḍapārśve niveśitaḥ</li>
        <li id="R_5.038.005cd">tvayā pranaṣṭe tilake taṃ kila smartum arhasi</li>
    </ul>
    <ul id="R_5.038.006">
        <li id="R_5.038.006ab">sa vīryavān kathaṃ sītāṃ hṛtāṃ samanumanyase</li>
        <li id="R_5.038.006cd">vasantīṃ rakṣasāṃ madhye mahendravaruṇopama</li>
    </ul>
    <ul id="R_5.038.007">
        <li id="R_5.038.007ab">eṣa cūḍāmaṇir divyo mayā suparirakṣitaḥ</li>
        <li id="R_5.038.007cd">etaṃ dṛṣṭvā prahṛṣyāmi vyasane tvām ivānagha</li>
    </ul>
    <ul id="R_5.038.008">
        <li id="R_5.038.008ab">eṣa niryātitaḥ śrīmān mayā te vārisaṃbhavaḥ</li>
        <li id="R_5.038.008cd">ataḥ paraṃ na śakṣyāmi jīvituṃ śokalālasā</li>
    </ul>
    <ul id="R_5.038.009">
        <li id="R_5.038.009ab">asahyāni ca duḥkhāni vācaś ca hṛdayacchidaḥ</li>
        <li id="R_5.038.009cd">rākṣasīnāṃ sughorāṇāṃ tvatkṛte marṣayāmy aham</li>
    </ul>
    <ul id="R_5.038.010">
        <li id="R_5.038.010ab">dhārayiṣyāmi māsaṃ tu jīvitaṃ śatrusūdana</li>
        <li id="R_5.038.010cd">māsād ūrdhvaṃ na jīviṣye tvayā hīnā nṛpātmaja</li>
    </ul>
    <ul id="R_5.038.011">
        <li id="R_5.038.011ab">ghoro rākṣasarājo 'yaṃ dṛṣṭiś ca na sukhā mayi</li>
        <li id="R_5.038.011cd">tvāṃ ca śrutvā vipadyantaṃ na jīveyam ahaṃ kṣaṇam</li>
    </ul>
    <ul id="R_5.038.012">
        <li id="R_5.038.012ab">vaidehyā vacanaṃ śrutvā karuṇaṃ sāśrubhāṣitam</li>
        <li id="R_5.038.012cd">athābravīn mahātejā hanumān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.038.013">
        <li id="R_5.038.013ab">tvacchokavimukho rāmo devi satyena te śape</li>
        <li id="R_5.038.013cd">rāme śokābhibhūte tu lakṣmaṇaḥ paritapyate</li>
    </ul>
    <ul id="R_5.038.014">
        <li id="R_5.038.014ab">dṛṣṭā kathaṃ cid bhavatī na kālaḥ pariśocitum</li>
        <li id="R_5.038.014cd">imaṃ muhūrtaṃ duḥkhānām antaṃ drakṣyasi bhāmini</li>
    </ul>
    <ul id="R_5.038.015">
        <li id="R_5.038.015ab">tāv ubhau puruṣavyāghrau rājaputrāv aninditau</li>
        <li id="R_5.038.015cd">tvaddarśanakṛtotsāhau laṅkāṃ bhasmīkariṣyataḥ</li>
    </ul>
    <ul id="R_5.038.016">
        <li id="R_5.038.016ab">hatvā tu samare krūraṃ rāvaṇaṃ saha bāndhavam</li>
        <li id="R_5.038.016cd">rāghavau tvāṃ viśālākṣi svāṃ purīṃ prāpayiṣyataḥ</li>
    </ul>
    <ul id="R_5.038.017">
        <li id="R_5.038.017ab">yat tu rāmo vijānīyād abhijñānam anindite</li>
        <li id="R_5.038.017cd">prītisaṃjananaṃ tasya bhūyas tvaṃ dātum arhasi</li>
    </ul>
    <ul id="R_5.038.018">
        <li id="R_5.038.018ab">sābravīd dattam eveha mayābhijñānam uttamam</li>
        <li id="R_5.038.018cd">etad eva hi rāmasya dṛṣṭvā matkeśabhūṣaṇam</li>
        <li id="R_5.038.018ef">śraddheyaṃ hanuman vākyaṃ tava vīra bhaviṣyati</li>
    </ul>
    <ul id="R_5.038.019">
        <li id="R_5.038.019ab">sa taṃ maṇivaraṃ gṛhya śrīmān plavagasattamaḥ</li>
        <li id="R_5.038.019cd">praṇamya śirasā devīṃ gamanāyopacakrame</li>
    </ul>
    <ul id="R_5.038.020">
        <li id="R_5.038.020ab">tam utpātakṛtotsāham avekṣya haripuṃgavam</li>
        <li id="R_5.038.020cd">vardhamānaṃ mahāvegam uvāca janakātmajā</li>
        <li id="R_5.038.020ef">aśrupūrṇamukhī dīnā bāṣpagadgadayā girā</li>
    </ul>
    <ul id="R_5.038.021">
        <li id="R_5.038.021ab">hanūman siṃhasaṃkāśau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_5.038.021cd">sugrīvaṃ ca sahāmātyaṃ sarvān brūyā anāmayam</li>
    </ul>
    <ul id="R_5.038.022">
        <li id="R_5.038.022ab">yathā ca sa mahābāhur māṃ tārayati rāghavaḥ</li>
        <li id="R_5.038.022cd">asmād duḥkhāmbusaṃrodhāt tat samādhātum arhasi</li>
    </ul>
    <ul id="R_5.038.023">
        <li id="R_5.038.023ab">
            <seg type="pāda" n="a">imaṃ ca tīvraṃ mama śokavegaṃ</seg>
            <seg type="pāda" n="b">rakṣobhir ebhiḥ paribhartsanaṃ ca</seg>
        </li>
        <li id="R_5.038.023cd">
            <seg type="pāda" n="c">brūyās tu rāmasya gataḥ samīpaṃ</seg>
            <seg type="pāda" n="d">śivaś ca te 'dhvāstu haripravīra</seg>
        </li>
    </ul>
    <ul id="R_5.038.024">
        <li id="R_5.038.024ab">
            <seg type="pāda" n="a">sa rājaputryā prativeditārthaḥ</seg>
            <seg type="pāda" n="b">kapiḥ kṛtārthaḥ parihṛṣṭacetāḥ</seg>
        </li>
        <li id="R_5.038.024cd">
            <seg type="pāda" n="c">tad alpaśeṣaṃ prasamīkṣya kāryaṃ</seg>
            <seg type="pāda" n="d">diśaṃ hy udīcīṃ manasā jagāma</seg>
        </li>
    </ul>
    <ul id="R_5.039.001">
        <li id="R_5.039.001ab">sa ca vāgbhiḥ praśastābhir gamiṣyan pūjitas tayā</li>
        <li id="R_5.039.001cd">tasmād deśād apakramya cintayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.039.002">
        <li id="R_5.039.002ab">alpaśeṣam idaṃ kāryaṃ dṛṣṭeyam asitekṣaṇā</li>
        <li id="R_5.039.002cd">trīn upāyān atikramya caturtha iha dṛśyate</li>
    </ul>
    <ul id="R_5.039.003">
        <li id="R_5.039.003ab">
            <seg type="pāda" n="a">na sāma rakṣaḥsu guṇāya kalpate</seg>
            <seg type="pāda" n="b">na danam arthopaciteṣu vartate</seg>
        </li>
        <li id="R_5.039.003cd">
            <seg type="pāda" n="c">na bhedasādhyā baladarpitā janāḥ</seg>
            <seg type="pāda" n="d">parākramas tv eṣa mameha rocate</seg>
        </li>
    </ul>
    <ul id="R_5.039.004">
        <li id="R_5.039.004ab">
            <seg type="pāda" n="a">na cāsya kāryasya parākramād ṛte</seg>
            <seg type="pāda" n="b">viniścayaḥ kaś cid ihopapadyate</seg>
        </li>
        <li id="R_5.039.004cd">
            <seg type="pāda" n="c">hṛtapravīrās tu raṇe hi rākṣasāḥ</seg>
            <seg type="pāda" n="d">kathaṃ cid īyur yad ihādya mārdavam</seg>
        </li>
    </ul>
    <ul id="R_5.039.005">
        <li id="R_5.039.005ab">kārye karmaṇi nirdiṣṭo yo bahūny api sādhayet</li>
        <li id="R_5.039.005cd">pūrvakāryavirodhena sa kāryaṃ kartum arhati</li>
    </ul>
    <ul id="R_5.039.006">
        <li id="R_5.039.006ab">na hy ekaḥ sādhako hetuḥ svalpasyāpīha karmaṇaḥ</li>
        <li id="R_5.039.006cd">yo hy arthaṃ bahudhā veda sa samartho 'rthasādhane</li>
    </ul>
    <ul id="R_5.039.007">
        <li id="R_5.039.007ab">
            <seg type="pāda" n="a">ihaiva tāvat kṛtaniścayo hy ahaṃ</seg>
            <seg type="pāda" n="b">yadi vrajeyaṃ plavageśvarālayam</seg>
        </li>
        <li id="R_5.039.007cd">
            <seg type="pāda" n="c">parātmasaṃmarda viśeṣatattvavit</seg>
            <seg type="pāda" n="d">tataḥ kṛtaṃ syān mama bhartṛśāsanam</seg>
        </li>
    </ul>
    <ul id="R_5.039.008">
        <li id="R_5.039.008ab">
            <seg type="pāda" n="a">kathaṃ nu khalv adya bhavet sukhāgataṃ</seg>
            <seg type="pāda" n="b">prasahya yuddhaṃ mama rākṣasaiḥ saha</seg>
        </li>
        <li id="R_5.039.008cd">
            <seg type="pāda" n="c">tathaiva khalv ātmabalaṃ ca sāravat</seg>
            <seg type="pāda" n="d">samānayen māṃ ca raṇe daśānanaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.039.009">
        <li id="R_5.039.009ab">idam asya nṛśaṃsasya nandanopamam uttamam</li>
        <li id="R_5.039.009cd">vanaṃ netramanaḥkāntaṃ nānādrumalatāyutam</li>
    </ul>
    <ul id="R_5.039.010">
        <li id="R_5.039.010ab">idaṃ vidhvaṃsayiṣyāmi śuṣkaṃ vanam ivānalaḥ</li>
        <li id="R_5.039.010cd">asmin bhagne tataḥ kopaṃ kariṣyati sa rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.039.011">
        <li id="R_5.039.011ab">
            <seg type="pāda" n="a">tato mahat sāśvamahārathadvipaṃ</seg>
            <seg type="pāda" n="b">balaṃ samāneṣv api rākṣasādhipaḥ</seg>
        </li>
        <li id="R_5.039.011cd">
            <seg type="pāda" n="c">triśūlakālāyasapaṭṭiśāyudhaṃ</seg>
            <seg type="pāda" n="d">tato mahad yuddham idaṃ bhaviṣyati</seg>
        </li>
    </ul>
    <ul id="R_5.039.012">
        <li id="R_5.039.012ab">
            <seg type="pāda" n="a">ahaṃ tu taiḥ saṃyati caṇḍavikramaiḥ</seg>
            <seg type="pāda" n="b">sametya rakṣobhir asaṃgavikramaḥ</seg>
        </li>
        <li id="R_5.039.012cd">
            <seg type="pāda" n="c">nihatya tad rāvaṇacoditaṃ balaṃ</seg>
            <seg type="pāda" n="d">sukhaṃ gamiṣyāmi kapīśvarālayam</seg>
        </li>
    </ul>
    <ul id="R_5.039.013">
        <li id="R_5.039.013ab">tato mārutavat kruddho mārutir bhīmavikramaḥ</li>
        <li id="R_5.039.013cd">ūruvegena mahatā drumān kṣeptum athārabhat</li>
    </ul>
    <ul id="R_5.039.014">
        <li id="R_5.039.014ab">tatas tad dhanumān vīro babhañja pramadāvanam</li>
        <li id="R_5.039.014cd">mattadvijasamāghuṣṭaṃ nānādrumalatāyutam</li>
    </ul>
    <ul id="R_5.039.015">
        <li id="R_5.039.015ab">tad vanaṃ mathitair vṛkṣair bhinnaiś ca salilāśayaiḥ</li>
        <li id="R_5.039.015cd">cūrṇitaiḥ parvatāgraiś ca babhūvāpriyadarśanam</li>
    </ul>
    <ul id="R_5.039.016">
        <li id="R_5.039.016ab">
            <seg type="pāda" n="a">latāgṛhaiś citragṛhaiś ca nāśitair</seg>
            <seg type="pāda" n="b">mahoragair vyālamṛgaiś ca nirdhutaiḥ</seg>
        </li>
        <li id="R_5.039.016cd">
            <seg type="pāda" n="c">śilāgṛhair unmathitais tathā gṛhaiḥ</seg>
            <seg type="pāda" n="d">pranaṣṭarūpaṃ tad abhūn mahad vanam</seg>
        </li>
    </ul>
    <ul id="R_5.039.017">
        <li id="R_5.039.017ab">
            <seg type="pāda" n="a">sa tasya kṛtvārthapater mahākapir</seg>
            <seg type="pāda" n="b">mahad vyalīkaṃ manaso mahātmanaḥ</seg>
        </li>
        <li id="R_5.039.017cd">
            <seg type="pāda" n="c">yuyutsur eko bahubhir mahābalaiḥ</seg>
            <seg type="pāda" n="d">śriyā jvalaṃs toraṇam āśritaḥ kapiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.040.001">
        <li id="R_5.040.001ab">tataḥ pakṣininādena vṛkṣabhaṅgasvanena ca</li>
        <li id="R_5.040.001cd">babhūvus trāsasaṃbhrāntāḥ sarve laṅkānivāsinaḥ</li>
    </ul>
    <ul id="R_5.040.002">
        <li id="R_5.040.002ab">vidrutāś ca bhayatrastā vinedur mṛgapakṣuṇaḥ</li>
        <li id="R_5.040.002cd">rakṣasāṃ ca nimittāni krūrāṇi pratipedire</li>
    </ul>
    <ul id="R_5.040.003">
        <li id="R_5.040.003ab">tato gatāyāṃ nidrāyāṃ rākṣasyo vikṛtānanāḥ</li>
        <li id="R_5.040.003cd">tad vanaṃ dadṛśur bhagnaṃ taṃ ca vīraṃ mahākapim</li>
    </ul>
    <ul id="R_5.040.004">
        <li id="R_5.040.004ab">sa tā dṛṣṭva mahābāhur mahāsattvo mahābalaḥ</li>
        <li id="R_5.040.004cd">cakāra sumahad rūpaṃ rākṣasīnāṃ bhayāvaham</li>
    </ul>
    <ul id="R_5.040.005">
        <li id="R_5.040.005ab">tatas taṃ girisaṃkāśam atikāyaṃ mahābalam</li>
        <li id="R_5.040.005cd">rākṣasyo vānaraṃ dṛṣṭvā papracchur janakātmajām</li>
    </ul>
    <ul id="R_5.040.006">
        <li id="R_5.040.006ab">ko 'yaṃ kasya kuto vāyaṃ kiṃnimittam ihāgataḥ</li>
        <li id="R_5.040.006cd">kathaṃ tvayā sahānena saṃvādaḥ kṛta ity uta</li>
    </ul>
    <ul id="R_5.040.007">
        <li id="R_5.040.007ab">ācakṣva no viśālākṣi mā bhūt te subhage bhayam</li>
        <li id="R_5.040.007cd">saṃvādam asitāpāṅge tvayā kiṃ kṛtavān ayam</li>
    </ul>
    <ul id="R_5.040.008">
        <li id="R_5.040.008ab">athābravīt tadā sādhvī sītā sarvāṅgaśobhanā</li>
        <li id="R_5.040.008cd">rakṣasāṃ kāmarūpāṇāṃ vijñāne mama kā gatiḥ</li>
    </ul>
    <ul id="R_5.040.009">
        <li id="R_5.040.009ab">yūyam evāsya jānīta yo 'yaṃ yad vā kariṣyati</li>
        <li id="R_5.040.009cd">ahir eva aheḥ pādān vijānāti na saṃśayaḥ</li>
    </ul>
    <ul id="R_5.040.010">
        <li id="R_5.040.010ab">aham apy asya bhītāsmi nainaṃ jānāmi ko 'nvayam</li>
        <li id="R_5.040.010cd">vedmi rākṣasam evainaṃ kāmarūpiṇam āgatam</li>
    </ul>
    <ul id="R_5.040.011">
        <li id="R_5.040.011ab">vaidehyā vacanaṃ śrutvā rākṣasyo vidrutā drutam</li>
        <li id="R_5.040.011cd">sthitāḥ kāś cid gatāḥ kāś cid rāvaṇāya niveditum</li>
    </ul>
    <ul id="R_5.040.012">
        <li id="R_5.040.012ab">rāvaṇasya samīpe tu rākṣasyo vikṛtānanāḥ</li>
        <li id="R_5.040.012cd">virūpaṃ vānaraṃ bhīmam ākhyātum upacakramuḥ</li>
    </ul>
    <ul id="R_5.040.013">
        <li id="R_5.040.013ab">aśokavanikā madhye rājan bhīmavapuḥ kapiḥ</li>
        <li id="R_5.040.013cd">sītayā kṛtasaṃvādas tiṣṭhaty amitavikramaḥ</li>
    </ul>
    <ul id="R_5.040.014">
        <li id="R_5.040.014ab">na ca taṃ jānakī sītā hariṃ hariṇalocaṇā</li>
        <li id="R_5.040.014cd">asmābhir bahudhā pṛṣṭā nivedayitum icchati</li>
    </ul>
    <ul id="R_5.040.015">
        <li id="R_5.040.015ab">vāsavasya bhaved dūto dūto vaiśravaṇasya vā</li>
        <li id="R_5.040.015cd">preṣito vāpi rāmeṇa sītānveṣaṇakāṅkṣayā</li>
    </ul>
    <ul id="R_5.040.016">
        <li id="R_5.040.016ab">tena tvadbhūtarūpeṇa yat tat tava manoharam</li>
        <li id="R_5.040.016cd">nānāmṛgagaṇākīrṇaṃ pramṛṣṭaṃ pramadāvanam</li>
    </ul>
    <ul id="R_5.040.017">
        <li id="R_5.040.017ab">na tatra kaś cid uddeśo yas tena na vināśitaḥ</li>
        <li id="R_5.040.017cd">yatra sā jānakī sītā sa tena na vināśitaḥ</li>
    </ul>
    <ul id="R_5.040.018">
        <li id="R_5.040.018ab">jānakīrakṣaṇārthaṃ vā śramād vā nopalabhyate</li>
        <li id="R_5.040.018cd">atha vā kaḥ śramas tasya saiva tenābhirakṣitā</li>
    </ul>
    <ul id="R_5.040.019">
        <li id="R_5.040.019ab">cārupallavapatrāḍhyaṃ yaṃ sītā svayam āsthitā</li>
        <li id="R_5.040.019cd">pravṛddhaḥ śiṃśapāvṛkṣaḥ sa ca tenābhirakṣitaḥ</li>
    </ul>
    <ul id="R_5.040.020">
        <li id="R_5.040.020ab">tasyograrūpasyograṃ tvaṃ daṇḍam ājñātum arhasi</li>
        <li id="R_5.040.020cd">sītā saṃbhāṣitā yena tad vanaṃ ca vināśitam</li>
    </ul>
    <ul id="R_5.040.021">
        <li id="R_5.040.021ab">manaḥparigṛhītāṃ tāṃ tava rakṣogaṇeśvara</li>
        <li id="R_5.040.021cd">kaḥ sītām abhibhāṣeta yo na syāt tyaktajīvitaḥ</li>
    </ul>
    <ul id="R_5.040.022">
        <li id="R_5.040.022ab">rākṣasīnāṃ vacaḥ śrutvā rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_5.040.022cd">hutāgir iva jajvāla kopasaṃvartitekṣaṇaḥ</li>
    </ul>
    <ul id="R_5.040.023">
        <li id="R_5.040.023ab">ātmanaḥ sadṛśāñ śūrān kiṃkarān nāma rākṣasān</li>
        <li id="R_5.040.023cd">vyādideśa mahātejā nigrahārthaṃ hanūmataḥ</li>
    </ul>
    <ul id="R_5.040.024">
        <li id="R_5.040.024ab">teṣām aśītisāhasraṃ kiṃkarāṇāṃ tarasvinām</li>
        <li id="R_5.040.024cd">niryayur bhavanāt tasmāt kūṭamudgarapāṇayaḥ</li>
    </ul>
    <ul id="R_5.040.025">
        <li id="R_5.040.025ab">mahodarā mahādaṃṣṭrā ghorarūpā mahābalāḥ</li>
        <li id="R_5.040.025cd">yuddhābhimanasaḥ sarve hanūmadgrahaṇonmukhāḥ</li>
    </ul>
    <ul id="R_5.040.026">
        <li id="R_5.040.026ab">te kapiṃ taṃ samāsādya toraṇastham avasthitam</li>
        <li id="R_5.040.026cd">abhipetur mahāvegāḥ pataṅgā iva pāvakam</li>
    </ul>
    <ul id="R_5.040.027">
        <li id="R_5.040.027ab">te gadābhir vicitrābhiḥ parighaiḥ kāñcanāṅgadaiḥ</li>
        <li id="R_5.040.027cd">ājaghnur vānaraśreṣṭhaṃ śarair ādityasaṃnibhaiḥ</li>
    </ul>
    <ul id="R_5.040.028">
        <li id="R_5.040.028ab">hanūmān api tejasvī śrīmān parvatasaṃnibhaḥ</li>
        <li id="R_5.040.028cd">kṣitāv āvidhya lāṅgūlaṃ nanāda ca mahāsvanam</li>
    </ul>
    <ul id="R_5.040.029">
        <li id="R_5.040.029ab">tasya saṃnādaśabdena te 'bhavan bhayaśaṅkitāḥ</li>
        <li id="R_5.040.029cd">dadṛśuś ca hanūmantaṃ saṃdhyāmegham ivonnatam</li>
    </ul>
    <ul id="R_5.040.030">
        <li id="R_5.040.030ab">svāmisaṃdeśaniḥśaṅkās tatas te rākṣasāḥ kapim</li>
        <li id="R_5.040.030cd">citraiḥ praharaṇair bhīmair abhipetus tatas tataḥ</li>
    </ul>
    <ul id="R_5.040.031">
        <li id="R_5.040.031ab">sa taiḥ parivṛtaḥ śūraiḥ sarvataḥ sa mahābalaḥ</li>
        <li id="R_5.040.031cd">āsasādāyasaṃ bhīmaṃ parighaṃ toraṇāśritam</li>
    </ul>
    <ul id="R_5.040.032">
        <li id="R_5.040.032ab">sa taṃ parigham ādāya jaghāna rajanīcarān</li>
    </ul>
    <ul id="R_5.040.033">
        <li id="R_5.040.033ab">sa pannagam ivādāya sphurantaṃ vinatāsutaḥ</li>
        <li id="R_5.040.033cd">vicacārāmbare vīraḥ parigṛhya ca mārutiḥ</li>
    </ul>
    <ul id="R_5.040.034">
        <li id="R_5.040.034ab">sa hatvā rākṣasān vīraḥ kiṃkarān mārutātmajaḥ</li>
        <li id="R_5.040.034cd">yuddhākāṅkṣī punar vīras toraṇaṃ samupasthitaḥ</li>
    </ul>
    <ul id="R_5.040.035">
        <li id="R_5.040.035ab">tatas tasmād bhayān muktāḥ kati cit tatra rākṣasāḥ</li>
        <li id="R_5.040.035cd">nihatān kiṃkarān sarvān rāvaṇāya nyavedayan</li>
    </ul>
    <ul id="R_5.040.036">
        <li id="R_5.040.036ab">
            <seg type="pāda" n="a">sa rākṣasānāṃ nihataṃ mahābalaṃ</seg>
            <seg type="pāda" n="b">niśamya rājā parivṛttalocanaḥ</seg>
        </li>
        <li id="R_5.040.036cd">
            <seg type="pāda" n="c">samādideśāpratimaṃ parākrame</seg>
            <seg type="pāda" n="d">prahastaputraṃ samare sudurjayam</seg>
        </li>
    </ul>
    <ul id="R_5.041.001">
        <li id="R_5.041.001ab">tataḥ sa kiṃkarān hatvā hanūmān dhyānam āsthitaḥ</li>
        <li id="R_5.041.001cd">vanaṃ bhagnaṃ mayā caityaprāsādo na vināśitaḥ</li>
        <li id="R_5.041.001ef">tasmāt prāsādam apy evam imaṃ vidhvaṃsayāmy aham</li>
    </ul>
    <ul id="R_5.041.002">
        <li id="R_5.041.002ab">iti saṃcintya hanumān manasā darśayan balam</li>
        <li id="R_5.041.002cd">caityaprāsādam āplutya meruśṛṅgam ivonnatam</li>
        <li id="R_5.041.002ef">āruroha hariśreṣṭho hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.041.003">
        <li id="R_5.041.003ab">saṃpradhṛṣya ca durdharṣaś caityaprāsādam unnatam</li>
        <li id="R_5.041.003cd">hanūmān prajvalaṃl lakṣmyā pāriyātropamo 'bhavat</li>
    </ul>
    <ul id="R_5.041.004">
        <li id="R_5.041.004ab">sa bhūtvā tu mahākāyo hanūmān mārutātmajaḥ</li>
        <li id="R_5.041.004cd">dhṛṣṭam āsphoṭayām āsa laṅkāṃ śabdena pūrayan</li>
    </ul>
    <ul id="R_5.041.005">
        <li id="R_5.041.005ab">tasyāsphoṭitaśabdena mahatā śrotraghātinā</li>
        <li id="R_5.041.005cd">petur vihaṃgā gaganād uccaiś cedam aghoṣayat</li>
    </ul>
    <ul id="R_5.041.006">
        <li id="R_5.041.006ab">jayaty atibalo rāmo lakṣmaṇaś ca mahābalaḥ</li>
        <li id="R_5.041.006cd">rājā jayati sugrīvo rāghaveṇābhipālitaḥ</li>
    </ul>
    <ul id="R_5.041.007">
        <li id="R_5.041.007ab">dāso 'haṃ kosalendrasya rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_5.041.007cd">hanumāñ śatrusainyānāṃ nihantā mārutātmajaḥ</li>
    </ul>
    <ul id="R_5.041.008">
        <li id="R_5.041.008ab">na rāvaṇasahasraṃ me yuddhe pratibalaṃ bhavet</li>
        <li id="R_5.041.008cd">śilābhis tu praharataḥ pādapaiś ca sahasraśaḥ</li>
    </ul>
    <ul id="R_5.041.009">
        <li id="R_5.041.009ab">ardayitvā purīṃ laṅkām abhivādya ca maithilīm</li>
        <li id="R_5.041.009cd">samṛddhārtho gamiṣyāmi miṣatāṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_5.041.010">
        <li id="R_5.041.010ab">evam uktvā vimānasthaś caityasthān haripuṃgavaḥ</li>
        <li id="R_5.041.010cd">nanāda bhīmanirhrādo rakṣasāṃ janayan bhayam</li>
    </ul>
    <ul id="R_5.041.011">
        <li id="R_5.041.011ab">tena śabdena mahatā caityapālāḥ śataṃ yayuḥ</li>
        <li id="R_5.041.011cd">gṛhītvā vividhān astrān prāsān khaḍgān paraśvadhān</li>
        <li id="R_5.041.011ef">visṛjanto mahākṣayā mārutiṃ paryavārayan</li>
    </ul>
    <ul id="R_5.041.012">
        <li id="R_5.041.012ab">āvarta iva gaṅgāyās toyasya vipulo mahān</li>
        <li id="R_5.041.012cd">parikṣipya hariśreṣṭhaṃ sa babhau rakṣasāṃ gaṇaḥ</li>
    </ul>
    <ul id="R_5.041.013">
        <li id="R_5.041.013ab">tato vātātmajaḥ kruddho bhīmarūpaṃ samāsthitaḥ</li>
    </ul>
    <ul id="R_5.041.014">
        <li id="R_5.041.014ab">prāsādasya mahāṃs tasya stambhaṃ hemapariṣkṛtam</li>
        <li id="R_5.041.014cd">utpāṭayitvā vegena hanūmān mārutātmajaḥ</li>
        <li id="R_5.041.014ef">tatas taṃ bhrāmayām āsa śatadhāraṃ mahābalaḥ</li>
    </ul>
    <ul id="R_5.041.015">
        <li id="R_5.041.015ab">sa rākṣasaśataṃ hatvā vajreṇendra ivāsurān</li>
        <li id="R_5.041.015cd">antarikṣasthitaḥ śrīmān idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.041.016">
        <li id="R_5.041.016ab">mādṛśānāṃ sahasrāṇi visṛṣṭāni mahātmanām</li>
        <li id="R_5.041.016cd">balināṃ vānarendrāṇāṃ sugrīvavaśavartinām</li>
    </ul>
    <ul id="R_5.041.017">
        <li id="R_5.041.017ab">śataiḥ śatasahasraiś ca koṭībhir ayutair api</li>
        <li id="R_5.041.017cd">āgamiṣyati sugrīvaḥ sarveṣāṃ vo niṣūdanaḥ</li>
    </ul>
    <ul id="R_5.041.018">
        <li id="R_5.041.018ab">neyam asti purī laṅkā na yūyaṃ na ca rāvaṇaḥ</li>
        <li id="R_5.041.018cd">yasmād ikṣvākunāthena baddhaṃ vairaṃ mahātmanā</li>
    </ul>
    <ul id="R_5.042.001">
        <li id="R_5.042.001ab">saṃdiṣṭo rākṣasendreṇa prahastasya suto balī</li>
        <li id="R_5.042.001cd">jambumālī mahādaṃṣṭro nirjagāma dhanurdharaḥ</li>
    </ul>
    <ul id="R_5.042.002">
        <li id="R_5.042.002ab">raktamālyāmbaradharaḥ sragvī rucirakuṇḍalaḥ</li>
        <li id="R_5.042.002cd">mahān vivṛttanayanaś caṇḍaḥ samaradurjayaḥ</li>
    </ul>
    <ul id="R_5.042.003">
        <li id="R_5.042.003ab">dhanuḥ śakradhanuḥ prakhyaṃ mahad rucirasāyakam</li>
        <li id="R_5.042.003cd">visphārayāṇo vegena vajrāśanisamasvanam</li>
    </ul>
    <ul id="R_5.042.004">
        <li id="R_5.042.004ab">tasya visphāraghoṣeṇa dhanuṣo mahatā diśaḥ</li>
        <li id="R_5.042.004cd">pradiśaś ca nabhaś caiva sahasā samapūryata</li>
    </ul>
    <ul id="R_5.042.005">
        <li id="R_5.042.005ab">rathena kharayuktena tam āgatam udīkṣya saḥ</li>
        <li id="R_5.042.005cd">hanūmān vegasaṃpanno jaharṣa ca nanāda ca</li>
    </ul>
    <ul id="R_5.042.006">
        <li id="R_5.042.006ab">taṃ toraṇaviṭaṅkasthaṃ hanūmantaṃ mahākapim</li>
        <li id="R_5.042.006cd">jambumālī mahābāhur vivyādha niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_5.042.007">
        <li id="R_5.042.007ab">ardhacandreṇa vadane śirasy ekena karṇinā</li>
        <li id="R_5.042.007cd">bāhvor vivyādha nārācair daśabhis taṃ kapīśvaram</li>
    </ul>
    <ul id="R_5.042.008">
        <li id="R_5.042.008ab">tasya tac chuśubhe tāmraṃ śareṇābhihataṃ mukham</li>
        <li id="R_5.042.008cd">śaradīvāmbujaṃ phullaṃ viddhaṃ bhāskararaśminā</li>
    </ul>
    <ul id="R_5.042.009">
        <li id="R_5.042.009ab">cukopa bāṇābhihato rākṣasasya mahākapiḥ</li>
        <li id="R_5.042.009cd">tataḥ pārśve 'tivipulāṃ dadarśa mahatīṃ śilām</li>
    </ul>
    <ul id="R_5.042.010">
        <li id="R_5.042.010ab">tarasā tāṃ samutpāṭya cikṣepa balavad balī</li>
        <li id="R_5.042.010cd">tāṃ śarair daśabhiḥ kruddhas tāḍayām āsa rākṣasaḥ</li>
    </ul>
    <ul id="R_5.042.011">
        <li id="R_5.042.011ab">vipannaṃ karma tad dṛṣṭvā hanūmāṃś caṇḍavikramaḥ</li>
        <li id="R_5.042.011cd">sālaṃ vipulam utpāṭya bhrāmayām āsa vīryavān</li>
    </ul>
    <ul id="R_5.042.012">
        <li id="R_5.042.012ab">bhrāmayantaṃ kapiṃ dṛṣṭvā sālavṛkṣaṃ mahābalam</li>
        <li id="R_5.042.012cd">cikṣepa subahūn bāṇāñ jambumālī mahābalaḥ</li>
    </ul>
    <ul id="R_5.042.013">
        <li id="R_5.042.013ab">sālaṃ caturbhir ciccheda vānaraṃ pañcabhir bhuje</li>
        <li id="R_5.042.013cd">urasy ekena bāṇena daśabhis tu stanāntare</li>
    </ul>
    <ul id="R_5.042.014">
        <li id="R_5.042.014ab">sa śaraiḥ pūritatanuḥ krodhena mahatā vṛtaḥ</li>
        <li id="R_5.042.014cd">tam eva parighaṃ gṛhya bhrāmayām āsa vegitaḥ</li>
    </ul>
    <ul id="R_5.042.015">
        <li id="R_5.042.015ab">ativego 'tivegena bhrāmayitvā balotkaṭaḥ</li>
        <li id="R_5.042.015cd">parighaṃ pātayām āsa jambumāler mahorasi</li>
    </ul>
    <ul id="R_5.042.016">
        <li id="R_5.042.016ab">tasya caiva śiro nāsti na bāhū na ca jānunī</li>
        <li id="R_5.042.016cd">na dhanur na ratho nāśvās tatrādṛśyanta neṣavaḥ</li>
    </ul>
    <ul id="R_5.042.017">
        <li id="R_5.042.017ab">sa hatas tarasā tena jambumālī mahārathaḥ</li>
        <li id="R_5.042.017cd">papāta nihato bhūmau cūrṇitāṅgavibhūṣaṇaḥ</li>
    </ul>
    <ul id="R_5.042.018">
        <li id="R_5.042.018ab">jambumāliṃ ca nihataṃ kiṃkarāṃś ca mahābalān</li>
        <li id="R_5.042.018cd">cukrodha rāvaṇaḥ śrutvā kopasaṃraktalocanaḥ</li>
    </ul>
    <ul id="R_5.042.019">
        <li id="R_5.042.019ab">
            <seg type="pāda" n="a">sa roṣasaṃvartitatāmralocanaḥ</seg>
            <seg type="pāda" n="b">prahastaputre nihate mahābale</seg>
        </li>
        <li id="R_5.042.019cd">
            <seg type="pāda" n="c">amātyaputrān ativīryavikramān</seg>
            <seg type="pāda" n="d">samādideśāśu niśācareśvaraḥ</seg>
        </li>
    </ul>
    <ul id="R_5.043.001">
        <li id="R_5.043.001ab">tatas te rākṣasendreṇa coditā mantriṇaḥ sutāḥ</li>
        <li id="R_5.043.001cd">niryayur bhavanāt tasmāt sapta saptārcivarcasaḥ</li>
    </ul>
    <ul id="R_5.043.002">
        <li id="R_5.043.002ab">mahābalaparīvārā dhanuṣmanto mahābalāḥ</li>
        <li id="R_5.043.002cd">kṛtāstrāstravidāṃ śreṣṭhāḥ parasparajayaiṣiṇaḥ</li>
    </ul>
    <ul id="R_5.043.003">
        <li id="R_5.043.003ab">hemajālaparikṣiptair dhvajavadbhiḥ patākibhiḥ</li>
        <li id="R_5.043.003cd">toyadasvananirghoṣair vājiyuktair mahārathaiḥ</li>
    </ul>
    <ul id="R_5.043.004">
        <li id="R_5.043.004ab">taptakāñcanacitrāṇi cāpāny amitavikramāḥ</li>
        <li id="R_5.043.004cd">visphārayantaḥ saṃhṛṣṭās taḍidvanta ivāmbudāḥ</li>
    </ul>
    <ul id="R_5.043.005">
        <li id="R_5.043.005ab">jananyas tās tatas teṣāṃ viditvā kiṃkarān hatān</li>
        <li id="R_5.043.005cd">babhūvuḥ śokasaṃbhrāntāḥ sabāndhavasuhṛjjanāḥ</li>
    </ul>
    <ul id="R_5.043.006">
        <li id="R_5.043.006ab">te parasparasaṃgharṣās taptakāñcanabhūṣaṇāḥ</li>
        <li id="R_5.043.006cd">abhipetur hanūmantaṃ toraṇastham avasthitam</li>
    </ul>
    <ul id="R_5.043.007">
        <li id="R_5.043.007ab">sṛjanto bāṇavṛṣṭiṃ te rathagarjitaniḥsvanāḥ</li>
        <li id="R_5.043.007cd">vṛṣṭimanta ivāmbhodā vicerur nairṛtarṣabhāḥ</li>
    </ul>
    <ul id="R_5.043.008">
        <li id="R_5.043.008ab">avakīrṇas tatas tābhir hanūmāñ śaravṛṣṭibhiḥ</li>
        <li id="R_5.043.008cd">abhavat saṃvṛtākāraḥ śailarāḍ iva vṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_5.043.009">
        <li id="R_5.043.009ab">sa śarān vañcayām āsa teṣām āśucaraḥ kapiḥ</li>
        <li id="R_5.043.009cd">rathavegāṃś ca vīrāṇāṃ vicaran vimale 'mbare</li>
    </ul>
    <ul id="R_5.043.010">
        <li id="R_5.043.010ab">sa taiḥ krīḍan dhanuṣmadbhir vyomni vīraḥ prakāśate</li>
        <li id="R_5.043.010cd">dhanuṣmadbhir yathā meghair mārutaḥ prabhur ambare</li>
    </ul>
    <ul id="R_5.043.011">
        <li id="R_5.043.011ab">sa kṛtvā ninadaṃ ghoraṃ trāsayaṃs tāṃ mahācamūm</li>
        <li id="R_5.043.011cd">cakāra hanumān vegaṃ teṣu rakṣaḥsu vīryavān</li>
    </ul>
    <ul id="R_5.043.012">
        <li id="R_5.043.012ab">talenābhihanat kāṃś cit pādaiḥ kāṃś cit paraṃtapaḥ</li>
        <li id="R_5.043.012cd">muṣṭinābhyahanat kāṃś cin nakhaiḥ kāṃś cid vyadārayat</li>
    </ul>
    <ul id="R_5.043.013">
        <li id="R_5.043.013ab">pramamāthorasā kāṃś cid ūrubhyām aparān kapiḥ</li>
        <li id="R_5.043.013cd">ke cit tasyaiva nādena tatraiva patitā bhuvi</li>
    </ul>
    <ul id="R_5.043.014">
        <li id="R_5.043.014ab">tatas teṣv avapanneṣu bhūmau nipatiteṣu ca</li>
        <li id="R_5.043.014cd">tat sainyam agamat sarvaṃ diśo daśabhayārditam</li>
    </ul>
    <ul id="R_5.043.015">
        <li id="R_5.043.015ab">vinedur visvaraṃ nāgā nipetur bhuvi vājinaḥ</li>
        <li id="R_5.043.015cd">bhagnanīḍadhvajacchatrair bhūś ca kīrṇābhavad rathaiḥ</li>
    </ul>
    <ul id="R_5.043.016">
        <li id="R_5.043.016ab">
            <seg type="pāda" n="a">sa tān pravṛddhān vinihatya rākṣasān</seg>
            <seg type="pāda" n="b">mahābalaś caṇḍaparākramaḥ kapiḥ</seg>
        </li>
        <li id="R_5.043.016cd">
            <seg type="pāda" n="c">yuyutsur anyaiḥ punar eva rākṣasais</seg>
            <seg type="pāda" n="d">tad eva vīro 'bhijagāma toraṇam</seg>
        </li>
    </ul>
    <ul id="R_5.044.001">
        <li id="R_5.044.001ab">hatān mantrisutān buddhvā vānareṇa mahātmanā</li>
        <li id="R_5.044.001cd">rāvaṇaḥ saṃvṛtākāraś cakāra matim uttamām</li>
    </ul>
    <ul id="R_5.044.002">
        <li id="R_5.044.002ab">sa virūpākṣayūpākṣau durdharaṃ caiva rākṣasaṃ</li>
        <li id="R_5.044.002cd">praghasaṃ bhāsakarṇaṃ ca pañcasenāgranāyakān</li>
    </ul>
    <ul id="R_5.044.003">
        <li id="R_5.044.003ab">saṃdideśa daśagrīvo vīrān nayaviśāradān</li>
        <li id="R_5.044.003cd">hanūmadgrahaṇe vyagrān vāyuvegasamān yudhi</li>
    </ul>
    <ul id="R_5.044.004">
        <li id="R_5.044.004ab">yāta senāgragāḥ sarve mahābalaparigrahāḥ</li>
        <li id="R_5.044.004cd">savājirathamātaṅgāḥ sa kapiḥ śāsyatām iti</li>
    </ul>
    <ul id="R_5.044.005">
        <li id="R_5.044.005ab">yat taiś ca khalu bhāvyaṃ syāt tam āsādya vanālayam</li>
        <li id="R_5.044.005cd">karma cāpi samādheyaṃ deśakālavirodhitam</li>
    </ul>
    <ul id="R_5.044.006">
        <li id="R_5.044.006ab">na hy ahaṃ taṃ kapiṃ manye karmaṇā pratitarkayan</li>
        <li id="R_5.044.006cd">sarvathā tan mahad bhūtaṃ mahābalaparigraham</li>
        <li id="R_5.044.006ef">bhaved indreṇa vā sṛṣṭam asmadarthaṃ tapobalāt</li>
    </ul>
    <ul id="R_5.044.007">
        <li id="R_5.044.007ab">sanāgayakṣagandharvā devāsuramaharṣayaḥ</li>
        <li id="R_5.044.007cd">yuṣmābhiḥ sahitaiḥ sarvair mayā saha vinirjitāḥ</li>
    </ul>
    <ul id="R_5.044.008">
        <li id="R_5.044.008ab">tair avaśyaṃ vidhātavyaṃ vyalīkaṃ kiṃ cid eva naḥ</li>
        <li id="R_5.044.008cd">tad eva nātra saṃdehaḥ prasahya parigṛhyatām</li>
    </ul>
    <ul id="R_5.044.009">
        <li id="R_5.044.009ab">nāvamanyo bhavadbhiś ca hariḥ krūraparākramaḥ</li>
        <li id="R_5.044.009cd">dṛṣṭā hi harayaḥ śīghrā mayā vipulavikramāḥ</li>
    </ul>
    <ul id="R_5.044.010">
        <li id="R_5.044.010ab">vālī ca saha sugrīvo jāmbavāṃś ca mahābalaḥ</li>
        <li id="R_5.044.010cd">nīlaḥ senāpatiś caiva ye cānye dvividādayaḥ</li>
    </ul>
    <ul id="R_5.044.011">
        <li id="R_5.044.011ab">naiva teṣāṃ gatir bhīmā na tejo na parākramaḥ</li>
        <li id="R_5.044.011cd">na matir na balotsāho na rūpaparikalpanam</li>
    </ul>
    <ul id="R_5.044.012">
        <li id="R_5.044.012ab">mahat sattvam idaṃ jñeyaṃ kapirūpaṃ vyavasthitam</li>
        <li id="R_5.044.012cd">prayatnaṃ mahad āsthāya kriyatām asya nigrahaḥ</li>
    </ul>
    <ul id="R_5.044.013">
        <li id="R_5.044.013ab">kāmaṃ lokās trayaḥ sendrāḥ sasurāsuramānavāḥ</li>
        <li id="R_5.044.013cd">bhavatām agrataḥ sthātuṃ na paryāptā raṇājire</li>
    </ul>
    <ul id="R_5.044.014">
        <li id="R_5.044.014ab">tathāpi tu nayajñena jayam ākāṅkṣatā raṇe</li>
        <li id="R_5.044.014cd">ātmā rakṣyaḥ prayatnena yuddhasiddhir hi cañcalā</li>
    </ul>
    <ul id="R_5.044.015">
        <li id="R_5.044.015ab">te svāmivacanaṃ sarve pratigṛhya mahaujasaḥ</li>
        <li id="R_5.044.015cd">samutpetur mahāvegā hutāśasamatejasaḥ</li>
    </ul>
    <ul id="R_5.044.016">
        <li id="R_5.044.016ab">rathaiś ca mattair nāgaiś ca vājibhiś ca mahājavaiḥ</li>
        <li id="R_5.044.016cd">śastraiś ca vividhais tīkṣṇaiḥ sarvaiś copacitā balaiḥ</li>
    </ul>
    <ul id="R_5.044.017">
        <li id="R_5.044.017ab">tatas taṃ dadṛśur vīrā dīpyamānaṃ mahākapim</li>
        <li id="R_5.044.017cd">raśmimantam ivodyantaṃ svatejoraśmimālinam</li>
    </ul>
    <ul id="R_5.044.018">
        <li id="R_5.044.018ab">toraṇasthaṃ mahāvegaṃ mahāsattvaṃ mahābalam</li>
        <li id="R_5.044.018cd">mahāmatiṃ mahotsāhaṃ mahākāyaṃ mahābalam</li>
    </ul>
    <ul id="R_5.044.019">
        <li id="R_5.044.019ab">taṃ samīkṣyaiva te sarve dikṣu sarvāsv avasthitāḥ</li>
        <li id="R_5.044.019cd">tais taiḥ praharaṇair bhīmair abhipetus tatas tataḥ</li>
    </ul>
    <ul id="R_5.044.020">
        <li id="R_5.044.020ab">tasya pañcāyasās tīkṣṇāḥ sitāḥ pītamukhāḥ śarāḥ</li>
        <li id="R_5.044.020cd">śirasty utpalapatrābhā durdhareṇa nipātitāḥ</li>
    </ul>
    <ul id="R_5.044.021">
        <li id="R_5.044.021ab">sa taiḥ pañcabhir āviddhaḥ śaraiḥ śirasi vānaraḥ</li>
        <li id="R_5.044.021cd">utpapāta nadan vyomni diśo daśa vinādayan</li>
    </ul>
    <ul id="R_5.044.022">
        <li id="R_5.044.022ab">tatas tu durdharo vīraḥ sarathaḥ sajjakārmukaḥ</li>
        <li id="R_5.044.022cd">kirañ śaraśatair naikair abhipede mahābalaḥ</li>
    </ul>
    <ul id="R_5.044.023">
        <li id="R_5.044.023ab">sa kapir vārayām āsa taṃ vyomni śaravarṣiṇam</li>
        <li id="R_5.044.023cd">vṛṣṭimantaṃ payodānte payodam iva mārutaḥ</li>
    </ul>
    <ul id="R_5.044.024">
        <li id="R_5.044.024ab">ardyamānas tatas tena durdhareṇānilātmajaḥ</li>
        <li id="R_5.044.024cd">cakāra ninadaṃ bhūyo vyavardhata ca vegavān</li>
    </ul>
    <ul id="R_5.044.025">
        <li id="R_5.044.025ab">sa dūraṃ sahasotpatya durdharasya rathe hariḥ</li>
        <li id="R_5.044.025cd">nipapāta mahāvego vidyudrāśir girāv iva</li>
    </ul>
    <ul id="R_5.044.026">
        <li id="R_5.044.026ab">tatas taṃ mathitāṣṭāśvaṃ rathaṃ bhagnākṣakūvaram</li>
        <li id="R_5.044.026cd">vihāya nyapatad bhūmau durdharas tyaktajīvitaḥ</li>
    </ul>
    <ul id="R_5.044.027">
        <li id="R_5.044.027ab">taṃ virūpākṣayūpākṣau dṛṣṭvā nipatitaṃ bhuvi</li>
        <li id="R_5.044.027cd">saṃjātaroṣau durdharṣāv utpetatur ariṃdamau</li>
    </ul>
    <ul id="R_5.044.028">
        <li id="R_5.044.028ab">sa tābhyāṃ sahasotpatya viṣṭhito vimale 'mbare</li>
        <li id="R_5.044.028cd">mudgarābhyāṃ mahābāhur vakṣasy abhihataḥ kapiḥ</li>
    </ul>
    <ul id="R_5.044.029">
        <li id="R_5.044.029ab">tayor vegavator vegaṃ vinihatya mahābalaḥ</li>
        <li id="R_5.044.029cd">nipapāta punar bhūmau suparṇasamavikramaḥ</li>
    </ul>
    <ul id="R_5.044.030">
        <li id="R_5.044.030ab">sa sālavṛkṣam āsādya samutpāṭya ca vānaraḥ</li>
        <li id="R_5.044.030cd">tāv ubhau rākṣasau vīrau jaghāna pavanātmajaḥ</li>
    </ul>
    <ul id="R_5.044.031">
        <li id="R_5.044.031ab">tatas tāṃs trīn hatāñ jñātvā vānareṇa tarasvinā</li>
        <li id="R_5.044.031cd">abhipede mahāvegaḥ prasahya praghaso harim</li>
    </ul>
    <ul id="R_5.044.032">
        <li id="R_5.044.032ab">bhāsakarṇaś ca saṃkruddhaḥ śūlam ādāya vīryavān</li>
        <li id="R_5.044.032cd">ekataḥ kapiśārdūlaṃ yaśasvinam avasthitau</li>
    </ul>
    <ul id="R_5.044.033">
        <li id="R_5.044.033ab">paṭṭiśena śitāgreṇa praghasaḥ pratyapothayat</li>
        <li id="R_5.044.033cd">bhāsakarṇaś ca śūlena rākṣasaḥ kapisattamam</li>
    </ul>
    <ul id="R_5.044.034">
        <li id="R_5.044.034ab">sa tābhyāṃ vikṣatair gātrair asṛgdigdhatanūruhaḥ</li>
        <li id="R_5.044.034cd">abhavad vānaraḥ kruddho bālasūryasamaprabhaḥ</li>
    </ul>
    <ul id="R_5.044.035">
        <li id="R_5.044.035ab">samutpāṭya gireḥ śṛṅgaṃ samṛgavyālapādapam</li>
        <li id="R_5.044.035cd">jaghāna hanumān vīro rākṣasau kapikuñjaraḥ</li>
    </ul>
    <ul id="R_5.044.036">
        <li id="R_5.044.036ab">tatas teṣv avasanneṣu senāpatiṣu pañcasu</li>
        <li id="R_5.044.036cd">balaṃ tad avaśeṣaṃ tu nāśayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.044.037">
        <li id="R_5.044.037ab">aśvair aśvān gajair nāgān yodhair yodhān rathai rathān</li>
        <li id="R_5.044.037cd">sa kapir nāśayām āsa sahasrākṣa ivāsurān</li>
    </ul>
    <ul id="R_5.044.038">
        <li id="R_5.044.038ab">hatair nāgaiś ca turagair bhagnākṣaiś ca mahārathaiḥ</li>
        <li id="R_5.044.038cd">hataiś ca rākṣasair bhūmī ruddhamārgā samantataḥ</li>
    </ul>
    <ul id="R_5.044.039">
        <li id="R_5.044.039ab">
            <seg type="pāda" n="a">tataḥ kapis tān dhvajinīpatīn raṇe</seg>
            <seg type="pāda" n="b">nihatya vīrān sabalān savāhanān</seg>
        </li>
        <li id="R_5.044.039cd">
            <seg type="pāda" n="c">tad eva vīraḥ parigṛhya toraṇaṃ</seg>
            <seg type="pāda" n="d">kṛtakṣaṇaḥ kāla iva prajākṣaye</seg>
        </li>
    </ul>
    <ul id="R_5.045.001">
        <li id="R_5.045.001ab">
            <seg type="pāda" n="a">senāpatīn pañca sa tu pramāpitān</seg>
            <seg type="pāda" n="b">hanūmatā sānucarān savāhanān</seg>
        </li>
        <li id="R_5.045.001cd">
            <seg type="pāda" n="c">samīkṣya rājā samaroddhatonmukhaṃ</seg>
            <seg type="pāda" n="d">kumāram akṣaṃ prasamaikṣatākṣatam</seg>
        </li>
    </ul>
    <ul id="R_5.045.002">
        <li id="R_5.045.002ab">
            <seg type="pāda" n="a">sa tasya dṛṣṭyarpaṇasaṃpracoditaḥ</seg>
            <seg type="pāda" n="b">pratāpavān kāñcanacitrakārmukaḥ</seg>
        </li>
        <li id="R_5.045.002cd">
            <seg type="pāda" n="c">samutpapātātha sadasy udīrito</seg>
            <seg type="pāda" n="d">dvijātimukhyair haviṣeva pāvakaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.003">
        <li id="R_5.045.003ab">
            <seg type="pāda" n="a">tato mahad bāladivākaraprabhaṃ</seg>
            <seg type="pāda" n="b">prataptajāmbūnadajālasaṃtatam</seg>
        </li>
        <li id="R_5.045.003cd">
            <seg type="pāda" n="c">rathāṃ samāsthāya yayau sa vīryavān</seg>
            <seg type="pāda" n="d">mahāhariṃ taṃ prati nairṛtarṣabhaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.004">
        <li id="R_5.045.004ab">
            <seg type="pāda" n="a">tatas tapaḥsaṃgrahasaṃcayārjitaṃ</seg>
            <seg type="pāda" n="b">prataptajāmbūnadajālaśobhitam</seg>
        </li>
        <li id="R_5.045.004cd">
            <seg type="pāda" n="c">patākinaṃ ratnavibhūṣitadhvajaṃ</seg>
            <seg type="pāda" n="d">manojavāṣṭāśvavaraiḥ suyojitam</seg>
        </li>
    </ul>
    <ul id="R_5.045.005">
        <li id="R_5.045.005ab">
            <seg type="pāda" n="a">surāsurādhṛṣyam asaṃgacāriṇaṃ</seg>
            <seg type="pāda" n="b">raviprabhaṃ vyomacaraṃ samāhitam</seg>
        </li>
        <li id="R_5.045.005cd">
            <seg type="pāda" n="c">satūṇam aṣṭāsinibaddhabandhuraṃ</seg>
            <seg type="pāda" n="d">yathākramāveśitaśaktitomaram</seg>
        </li>
    </ul>
    <ul id="R_5.045.006">
        <li id="R_5.045.006ab">
            <seg type="pāda" n="a">virājamānaṃ pratipūrṇavastunā</seg>
            <seg type="pāda" n="b">sahemadāmnā śaśisūryavarvasā</seg>
        </li>
        <li id="R_5.045.006cd">
            <seg type="pāda" n="c">divākarābhaṃ ratham āsthitas tataḥ</seg>
            <seg type="pāda" n="d">sa nirjagāmāmaratulyavikramaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.007">
        <li id="R_5.045.007ab">
            <seg type="pāda" n="a">sa pūrayan khaṃ ca mahīṃ ca sācalāṃ</seg>
            <seg type="pāda" n="b">turaṃgamataṅgamahārathasvanaiḥ</seg>
        </li>
        <li id="R_5.045.007cd">
            <seg type="pāda" n="c">balaiḥ sametaiḥ sa hi toraṇasthitaṃ</seg>
            <seg type="pāda" n="d">samartham āsīnam upāgamat kapim</seg>
        </li>
    </ul>
    <ul id="R_5.045.008">
        <li id="R_5.045.008ab">
            <seg type="pāda" n="a">sa taṃ samāsādya hariṃ harīkṣaṇo</seg>
            <seg type="pāda" n="b">yugāntakālāgnim iva prajākṣaye</seg>
        </li>
        <li id="R_5.045.008cd">
            <seg type="pāda" n="c">avasthitaṃ vismitajātasaṃbhramaḥ</seg>
            <seg type="pāda" n="d">samaikṣatākṣo bahumānacakṣuṣā</seg>
        </li>
    </ul>
    <ul id="R_5.045.009">
        <li id="R_5.045.009ab">
            <seg type="pāda" n="a">sa tasya vegaṃ ca kaper mahātmanaḥ</seg>
            <seg type="pāda" n="b">parākramaṃ cāriṣu pārthivātmajaḥ</seg>
        </li>
        <li id="R_5.045.009cd">
            <seg type="pāda" n="c">vicārayan khaṃ ca balaṃ mahābalo</seg>
            <seg type="pāda" n="d">himakṣaye sūrya ivābhivardhate</seg>
        </li>
    </ul>
    <ul id="R_5.045.010">
        <li id="R_5.045.010ab">
            <seg type="pāda" n="a">sa jātamanyuḥ prasamīkṣya vikramaṃ</seg>
            <seg type="pāda" n="b">sthiraḥ sthitaḥ saṃyati durnivāraṇam</seg>
        </li>
        <li id="R_5.045.010cd">
            <seg type="pāda" n="c">samāhitātmā hanumantam āhave</seg>
            <seg type="pāda" n="d">pracodayām āsa śarais tribhiḥ śitaiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.011">
        <li id="R_5.045.011ab">
            <seg type="pāda" n="a">tataḥ kapiṃ taṃ prasamīkṣya garvitaṃ</seg>
            <seg type="pāda" n="b">jitaśramaṃ śatruparājayor jitam</seg>
        </li>
        <li id="R_5.045.011cd">
            <seg type="pāda" n="c">avaikṣatākṣaḥ samudīrṇamānasaḥ</seg>
            <seg type="pāda" n="d">sabāṇapāṇiḥ pragṛhītakārmukaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.012">
        <li id="R_5.045.012ab">
            <seg type="pāda" n="a">sa hemaniṣkāṅgadacārukuṇḍalaḥ</seg>
            <seg type="pāda" n="b">samāsasādāśu parākramaḥ kapim</seg>
        </li>
        <li id="R_5.045.012cd">
            <seg type="pāda" n="c">tayor babhūvāpratimaḥ samāgamaḥ</seg>
            <seg type="pāda" n="d">surāsurāṇām api saṃbhramapradaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.013">
        <li id="R_5.045.013ab">
            <seg type="pāda" n="a">rarāsa bhūmir na tatāpa bhānumān</seg>
            <seg type="pāda" n="b">vavau na vāyuḥ pracacāla cācalaḥ</seg>
        </li>
        <li id="R_5.045.013cd">
            <seg type="pāda" n="c">kapeḥ kumārasya ca vīkṣya saṃyugaṃ</seg>
            <seg type="pāda" n="d">nanāda ca dyaur udadhiś ca cukṣubhe</seg>
        </li>
    </ul>
    <ul id="R_5.045.014">
        <li id="R_5.045.014ab">
            <seg type="pāda" n="a">tataḥ sa vīraḥ sumukhān patatriṇaḥ</seg>
            <seg type="pāda" n="b">suvarṇapuṅkhān saviṣān ivoragān</seg>
        </li>
        <li id="R_5.045.014cd">
            <seg type="pāda" n="c">samādhisaṃyogavimokṣatattvavic</seg>
            <seg type="pāda" n="d">charān atha trīn kapimūrdhny apātayat</seg>
        </li>
    </ul>
    <ul id="R_5.045.015">
        <li id="R_5.045.015ab">
            <seg type="pāda" n="a">sa taiḥ śarair mūrdhni samaṃ nipātitaiḥ</seg>
            <seg type="pāda" n="b">kṣarann asṛgdigdhavivṛttalocanaḥ</seg>
        </li>
        <li id="R_5.045.015cd">
            <seg type="pāda" n="c">navoditādityanibhaḥ śarāṃśumān</seg>
            <seg type="pāda" n="d">vyarājatāditya ivāṃśumālikaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.016">
        <li id="R_5.045.016ab">
            <seg type="pāda" n="a">tataḥ sa piṅgādhipamantrisattamaḥ</seg>
            <seg type="pāda" n="b">samīkṣya taṃ rājavarātmajaṃ raṇe</seg>
        </li>
        <li id="R_5.045.016cd">
            <seg type="pāda" n="c">udagracitrāyudhacitrakārmukaṃ</seg>
            <seg type="pāda" n="d">jaharṣa cāpūryata cāhavonmukhaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.017">
        <li id="R_5.045.017ab">
            <seg type="pāda" n="a">sa mandarāgrastha ivāṃśumālī</seg>
            <seg type="pāda" n="b">vivṛddhakopo balavīryasaṃyutaḥ</seg>
        </li>
        <li id="R_5.045.017cd">
            <seg type="pāda" n="c">kumāram akṣaṃ sabalaṃ savāhanaṃ</seg>
            <seg type="pāda" n="d">dadāha netrāgnimarīcibhis tadā</seg>
        </li>
    </ul>
    <ul id="R_5.045.018">
        <li id="R_5.045.018ab">
            <seg type="pāda" n="a">tataḥ sa bāṇāsanaśakrakārmukaḥ</seg>
            <seg type="pāda" n="b">śarapravarṣo yudhi rākṣasāmbudaḥ</seg>
        </li>
        <li id="R_5.045.018cd">
            <seg type="pāda" n="c">śarān mumocāśu harīśvarācale</seg>
            <seg type="pāda" n="d">balāhako vṛṣṭim ivācalottame</seg>
        </li>
    </ul>
    <ul id="R_5.045.019">
        <li id="R_5.045.019ab">
            <seg type="pāda" n="a">tataḥ kapis taṃ raṇacaṇḍavikramaṃ</seg>
            <seg type="pāda" n="b">vivṛddhatejobalavīryasāyakam</seg>
        </li>
        <li id="R_5.045.019cd">
            <seg type="pāda" n="c">kumāram akṣaṃ prasamīkṣya saṃyuge</seg>
            <seg type="pāda" n="d">nanāda harṣād ghanatulyavikramaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.020">
        <li id="R_5.045.020ab">
            <seg type="pāda" n="a">sa bālabhāvād yudhi vīryadarpitaḥ</seg>
            <seg type="pāda" n="b">pravṛddhamanyuḥ kṣatajopamekṣaṇaḥ</seg>
        </li>
        <li id="R_5.045.020cd">
            <seg type="pāda" n="c">samāsasādāpratimaṃ raṇe kapiṃ</seg>
            <seg type="pāda" n="d">gajo mahākūpam ivāvṛtaṃ tṛṇaiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.021">
        <li id="R_5.045.021ab">
            <seg type="pāda" n="a">sa tena bāṇaiḥ prasabhaṃ nipātitaiś</seg>
            <seg type="pāda" n="b">cakāra nādaṃ ghananādaniḥsvanaḥ</seg>
        </li>
        <li id="R_5.045.021cd">
            <seg type="pāda" n="c">samutpapātāśu nabhaḥ sa mārutir</seg>
            <seg type="pāda" n="d">bhujoruvikṣepaṇa ghoradarśanaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.022">
        <li id="R_5.045.022ab">
            <seg type="pāda" n="a">samutpatantaṃ samabhidravad balī</seg>
            <seg type="pāda" n="b">sa rākṣasānāṃ pravaraḥ pratāpavān</seg>
        </li>
        <li id="R_5.045.022cd">
            <seg type="pāda" n="c">rathī rathaśreṣṭhatamaḥ kirañ śaraiḥ</seg>
            <seg type="pāda" n="d">payodharaḥ śailam ivāśmavṛṣṭibhiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.023">
        <li id="R_5.045.023ab">
            <seg type="pāda" n="a">sa tāñ śarāṃs tasya vimokṣayan kapiś</seg>
            <seg type="pāda" n="b">cacāra vīraḥ pathi vāyusevite</seg>
        </li>
        <li id="R_5.045.023cd">
            <seg type="pāda" n="c">śarāntare mārutavad viniṣpatan</seg>
            <seg type="pāda" n="d">manojavaḥ saṃyati caṇḍavikramaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.024">
        <li id="R_5.045.024ab">
            <seg type="pāda" n="a">tam āttabāṇāsanam āhavonmukhaṃ</seg>
            <seg type="pāda" n="b">kham āstṛṇantaṃ vividhaiḥ śarottamaiḥ</seg>
        </li>
        <li id="R_5.045.024cd">
            <seg type="pāda" n="c">avaikṣatākṣaṃ bahumānacakṣuṣā</seg>
            <seg type="pāda" n="d">jagāma cintāṃ ca sa mārutātmajaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.025">
        <li id="R_5.045.025ab">
            <seg type="pāda" n="a">tataḥ śarair bhinnabhujāntaraḥ kapiḥ</seg>
            <seg type="pāda" n="b">kumāravaryeṇa mahātmanā nadan</seg>
        </li>
        <li id="R_5.045.025cd">
            <seg type="pāda" n="c">mahābhujaḥ karmaviśeṣatattvavid</seg>
            <seg type="pāda" n="d">vicintayām āsa raṇe parākramam</seg>
        </li>
    </ul>
    <ul id="R_5.045.026">
        <li id="R_5.045.026ab">
            <seg type="pāda" n="a">abālavad bāladivākaraprabhaḥ</seg>
            <seg type="pāda" n="b">karoty ayaṃ karma mahan mahābalaḥ</seg>
        </li>
        <li id="R_5.045.026cd">
            <seg type="pāda" n="c">na cāsya sarvāhavakarmaśobhinaḥ</seg>
            <seg type="pāda" n="d">pramāpaṇe me matir atra jāyate</seg>
        </li>
    </ul>
    <ul id="R_5.045.027">
        <li id="R_5.045.027ab">
            <seg type="pāda" n="a">ayaṃ mahātmā ca mahāṃś ca vīryataḥ</seg>
            <seg type="pāda" n="b">samāhitaś cātisahaś ca saṃyuge</seg>
        </li>
        <li id="R_5.045.027cd">
            <seg type="pāda" n="c">asaṃśayaṃ karmaguṇodayād ayaṃ</seg>
            <seg type="pāda" n="d">sanāgayakṣair munibhiś ca pūjitaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.028">
        <li id="R_5.045.028ab">
            <seg type="pāda" n="a">parākramotsāhavivṛddhamānasaḥ</seg>
            <seg type="pāda" n="b">samīkṣate māṃ pramukhāgataḥ sthitaḥ</seg>
        </li>
        <li id="R_5.045.028cd">
            <seg type="pāda" n="c">parākramo hy asya manāṃsi kampayet</seg>
            <seg type="pāda" n="d">surāsurāṇām api śīghrakāriṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.029">
        <li id="R_5.045.029ab">
            <seg type="pāda" n="a">na khalv ayaṃ nābhibhaved upekṣitaḥ</seg>
            <seg type="pāda" n="b">parākramo hy asya raṇe vivardhate</seg>
        </li>
        <li id="R_5.045.029cd">
            <seg type="pāda" n="c">pramāpaṇaṃ tv eva mamāsya rocate</seg>
            <seg type="pāda" n="d">na vardhamāno 'gnir upekṣituṃ kṣamaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.030">
        <li id="R_5.045.030ab">
            <seg type="pāda" n="a">iti pravegaṃ tu parasya tarkayan</seg>
            <seg type="pāda" n="b">svakarmayogaṃ ca vidhāya vīryavān</seg>
        </li>
        <li id="R_5.045.030cd">
            <seg type="pāda" n="c">cakāra vegaṃ tu mahābalas tadā</seg>
            <seg type="pāda" n="d">matiṃ ca cakre 'sya vadhe mahākapiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.031">
        <li id="R_5.045.031ab">
            <seg type="pāda" n="a">sa tasya tān aṣṭahayān mahājavān</seg>
            <seg type="pāda" n="b">samāhitān bhārasahān vivartane</seg>
        </li>
        <li id="R_5.045.031cd">
            <seg type="pāda" n="c">jaghāna vīraḥ pathi vāyusevite</seg>
            <seg type="pāda" n="d">talaprahālaiḥ pavanātmajaḥ kapiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.032">
        <li id="R_5.045.032ab">
            <seg type="pāda" n="a">tatas talenābhihato mahārathaḥ</seg>
            <seg type="pāda" n="b">sa tasya piṅgādhipamantrinirjitaḥ</seg>
        </li>
        <li id="R_5.045.032cd">
            <seg type="pāda" n="c">sa bhagnanīḍaḥ parimuktakūbaraḥ</seg>
            <seg type="pāda" n="d">papāta bhūmau hatavājir ambarāt</seg>
        </li>
    </ul>
    <ul id="R_5.045.033">
        <li id="R_5.045.033ab">
            <seg type="pāda" n="a">sa taṃ parityajya mahāratho rathaṃ</seg>
            <seg type="pāda" n="b">sakārmukaḥ khaḍgadharaḥ kham utpatat</seg>
        </li>
        <li id="R_5.045.033cd">
            <seg type="pāda" n="c">tapo'bhiyogād ṛṣir ugravīryavān</seg>
            <seg type="pāda" n="d">vihāya dehaṃ marutām ivālayam</seg>
        </li>
    </ul>
    <ul id="R_5.045.034">
        <li id="R_5.045.034ab">
            <seg type="pāda" n="a">tataḥ kapis taṃ vicarantam ambare</seg>
            <seg type="pāda" n="b">patatrirājānilasiddhasevite</seg>
        </li>
        <li id="R_5.045.034cd">
            <seg type="pāda" n="c">sametya taṃ mārutavegavikramaḥ</seg>
            <seg type="pāda" n="d">krameṇa jagrāha ca pādayor dṛḍham</seg>
        </li>
    </ul>
    <ul id="R_5.045.035">
        <li id="R_5.045.035ab">
            <seg type="pāda" n="a">sa taṃ samāvidhya sahasraśaḥ kapir</seg>
            <seg type="pāda" n="b">mahoragaṃ gṛhya ivāṇḍajeśvaraḥ</seg>
        </li>
        <li id="R_5.045.035cd">
            <seg type="pāda" n="c">mumoca vegāt pitṛtulyavikramo</seg>
            <seg type="pāda" n="d">mahītale saṃyati vānarottamaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.036">
        <li id="R_5.045.036ab">
            <seg type="pāda" n="a">sa bhagnabāhūrukaṭīśiro dharaḥ</seg>
            <seg type="pāda" n="b">kṣarann asṛn nirmathitāsthilocanaḥ</seg>
        </li>
        <li id="R_5.045.036cd">
            <seg type="pāda" n="c">sa bhinnasaṃdhiḥ pravikīrṇabandhano</seg>
            <seg type="pāda" n="d">hataḥ kṣitau vāyusutena rākṣasaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.037">
        <li id="R_5.045.037ab">
            <seg type="pāda" n="a">mahākapir bhūmitale nipīḍya taṃ</seg>
            <seg type="pāda" n="b">cakāra rakṣo'dhipater mahad bhayam</seg>
        </li>
    </ul>
    <ul id="R_5.045.038">
        <li id="R_5.045.038ab">
            <seg type="pāda" n="a">maharṣibhiś cakracarair mahāvrataiḥ</seg>
            <seg type="pāda" n="b">sametya bhūtaiś ca sayakṣapannagaiḥ</seg>
        </li>
        <li id="R_5.045.038cd">
            <seg type="pāda" n="c">suraiś ca sendrair bhṛśajātavismayair</seg>
            <seg type="pāda" n="d">hate kumāre sa kapir nirīkṣitaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.045.039">
        <li id="R_5.045.039ab">
            <seg type="pāda" n="a">nihatya taṃ vajrasutopamaprabhaṃ</seg>
            <seg type="pāda" n="b">kumāram akṣaṃ kṣatajopamekṣaṇam</seg>
        </li>
        <li id="R_5.045.039cd">
            <seg type="pāda" n="c">tad eva vīro 'bhijagāma toraṇaṃ</seg>
            <seg type="pāda" n="d">kṛtakṣaṇaḥ kāla iva prajākṣaye</seg>
        </li>
    </ul>
    <ul id="R_5.046.001">
        <li id="R_5.046.001ab">
            <seg type="pāda" n="a">tatas tu rakṣo'dhipatir mahātmā</seg>
            <seg type="pāda" n="b">hanūmatākṣe nihate kumāre</seg>
        </li>
        <li id="R_5.046.001cd">
            <seg type="pāda" n="c">manaḥ samādhāya tadendrakalpaṃ</seg>
            <seg type="pāda" n="d">samādideśendrajitaṃ sa roṣāt</seg>
        </li>
    </ul>
    <ul id="R_5.046.002">
        <li id="R_5.046.002ab">
            <seg type="pāda" n="a">tvam astravic chastrabhṛtāṃ variṣṭhaḥ</seg>
            <seg type="pāda" n="b">surāsurāṇām api śokadātā</seg>
        </li>
        <li id="R_5.046.002cd">
            <seg type="pāda" n="c">sureṣu sendreṣu ca dṛṣṭakarmā</seg>
            <seg type="pāda" n="d">pitāmahārādhanasaṃcitāstraḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.003">
        <li id="R_5.046.003ab">tavāstrabalam āsādya nāsurā na marudgaṇāḥ</li>
        <li id="R_5.046.003cd">na kaś cit triṣu lokeṣu saṃyuge na gataśramaḥ</li>
    </ul>
    <ul id="R_5.046.004">
        <li id="R_5.046.004ab">bhujavīryābhiguptaś ca tapasā cābhirakṣitaḥ</li>
        <li id="R_5.046.004cd">deśakālavibhāgajñas tvam eva matisattamaḥ</li>
    </ul>
    <ul id="R_5.046.005">
        <li id="R_5.046.005ab">
            <seg type="pāda" n="a">na te 'sty aśakyaṃ samareṣu karmaṇā</seg>
            <seg type="pāda" n="b">na te 'sty akāryaṃ matipūrvamantraṇe</seg>
        </li>
        <li id="R_5.046.005cd">
            <seg type="pāda" n="c">na so 'sti kaś cit triṣu saṃgraheṣu vai</seg>
            <seg type="pāda" n="d">na veda yas te 'strabalaṃ balaṃ ca te</seg>
        </li>
    </ul>
    <ul id="R_5.046.006">
        <li id="R_5.046.006ab">
            <seg type="pāda" n="a">mamānurūpaṃ tapaso balaṃ ca te</seg>
            <seg type="pāda" n="b">parākramaś cāstrabalaṃ ca saṃyuge</seg>
        </li>
        <li id="R_5.046.006cd">
            <seg type="pāda" n="c">na tvāṃ samāsādya raṇāvamarde</seg>
            <seg type="pāda" n="d">manaḥ śramaṃ gacchati niścitārtham</seg>
        </li>
    </ul>
    <ul id="R_5.046.007">
        <li id="R_5.046.007ab">nihatā iṃkarāḥ sarve jambumālī ca rākṣasaḥ</li>
        <li id="R_5.046.007cd">amātyaputrā vīrāś ca pañca senāgrayāyinaḥ</li>
    </ul>
    <ul id="R_5.046.008">
        <li id="R_5.046.008ab">sahodaras te dayitaḥ kumāro 'kṣaś ca sūditaḥ</li>
        <li id="R_5.046.008cd">na tu teṣv eva me sāro yas tvayy ariniṣūdana</li>
    </ul>
    <ul id="R_5.046.009">
        <li id="R_5.046.009ab">
            <seg type="pāda" n="a">idaṃ hi dṛṣṭvā matiman mahad balaṃ</seg>
            <seg type="pāda" n="b">kapeḥ prabhāvaṃ ca parākramaṃ ca</seg>
        </li>
        <li id="R_5.046.009cd">
            <seg type="pāda" n="c">tvam ātmanaś cāpi samīkṣya sāraṃ</seg>
            <seg type="pāda" n="d">kuruṣva vegaṃ svabalānurūpam</seg>
        </li>
    </ul>
    <ul id="R_5.046.010">
        <li id="R_5.046.010ab">
            <seg type="pāda" n="a">balāvamardas tvayi saṃnikṛṣṭe</seg>
            <seg type="pāda" n="b">yathā gate śāmyati śāntaśatrau</seg>
        </li>
        <li id="R_5.046.010cd">
            <seg type="pāda" n="c">tathā samīkṣyātmabalaṃ paraṃ ca</seg>
            <seg type="pāda" n="d">samārabhasvāstravidāṃ variṣṭha</seg>
        </li>
    </ul>
    <ul id="R_5.046.011">
        <li id="R_5.046.011ab">na khalv iyaṃ matiḥ śreṣṭhā yat tvāṃ saṃpreṣayāmy aham</li>
        <li id="R_5.046.011cd">iyaṃ ca rājadharmāṇāṃ kṣatrasya ca matir matā</li>
    </ul>
    <ul id="R_5.046.012">
        <li id="R_5.046.012ab">nānāśastraiś ca saṃgrāme vaiśāradyam ariṃdama</li>
        <li id="R_5.046.012cd">avaśyam eva boddhavyaṃ kāmyaś ca vijayo raṇe</li>
    </ul>
    <ul id="R_5.046.013">
        <li id="R_5.046.013ab">
            <seg type="pāda" n="a">tataḥ pitus tad vacanaṃ niśamya</seg>
            <seg type="pāda" n="b">pradakṣiṇaṃ dakṣasutaprabhāvaḥ</seg>
        </li>
        <li id="R_5.046.013cd">
            <seg type="pāda" n="c">cakāra bhartāram adīnasattvo</seg>
            <seg type="pāda" n="d">raṇāya vīraḥ pratipannabuddhiḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.014">
        <li id="R_5.046.014ab">tatas taiḥ svagaṇair iṣṭair indrajit pratipūjitaḥ</li>
        <li id="R_5.046.014cd">yuddhoddhatakṛtotsāhaḥ saṃgrāmaṃ pratipadyata</li>
    </ul>
    <ul id="R_5.046.015">
        <li id="R_5.046.015ab">śrīmān padmapalāśākṣo rākṣasādhipateḥ sutaḥ</li>
        <li id="R_5.046.015cd">nirjagāma mahātejāḥ samudra iva parvasu</li>
    </ul>
    <ul id="R_5.046.016">
        <li id="R_5.046.016ab">
            <seg type="pāda" n="a">sa pakṣi rājopamatulyavegair</seg>
            <seg type="pāda" n="b">vyālaiś caturbhiḥ sitatīkṣṇadaṃṣṭraiḥ</seg>
        </li>
        <li id="R_5.046.016cd">
            <seg type="pāda" n="c">rathaṃ samāyuktam asaṃgavegaṃ</seg>
            <seg type="pāda" n="d">samārurohendrajid indrakalpaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.017">
        <li id="R_5.046.017ab">sa rathī dhanvināṃ śreṣṭhaḥ śastrajño 'stravidāṃ varaḥ</li>
        <li id="R_5.046.017cd">rathenābhiyayau kṣipraṃ hanūmān yatra so 'bhavat</li>
    </ul>
    <ul id="R_5.046.018">
        <li id="R_5.046.018ab">sa tasya rathanirghoṣaṃ jyāsvanaṃ kārmukasya ca</li>
        <li id="R_5.046.018cd">niśamya harivīro 'sau saṃprahṛṣṭataro 'bhavat</li>
    </ul>
    <ul id="R_5.046.019">
        <li id="R_5.046.019ab">sumahac cāpam ādāya śitaśalyāṃś ca sāyakān</li>
        <li id="R_5.046.019cd">hanūmantam abhipretya jagāma raṇapaṇḍitaḥ</li>
    </ul>
    <ul id="R_5.046.020">
        <li id="R_5.046.020ab">
            <seg type="pāda" n="a">tasmiṃs tataḥ saṃyati jātaharṣe</seg>
            <seg type="pāda" n="b">raṇāya nirgacchati bāṇapāṇau</seg>
        </li>
        <li id="R_5.046.020cd">
            <seg type="pāda" n="c">diśaś ca sarvāḥ kaluṣā babhūvur</seg>
            <seg type="pāda" n="d">mṛgāś ca raudrā bahudhā vineduḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.021">
        <li id="R_5.046.021ab">
            <seg type="pāda" n="a">samāgatās tatra tu nāgayakṣā</seg>
            <seg type="pāda" n="b">maharṣayaś cakracarāś ca siddhāḥ</seg>
        </li>
        <li id="R_5.046.021cd">
            <seg type="pāda" n="c">nabhaḥ samāvṛtya ca pakṣisaṃghā</seg>
            <seg type="pāda" n="d">vinedur uccaiḥ paramaprahṛṣṭāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.022">
        <li id="R_5.046.022ab">āyantaṃ sarathaṃ dṛṣṭvā tūrṇam indrajitaṃ kapiḥ</li>
        <li id="R_5.046.022cd">vinanāda mahānādaṃ vyavardhata ca vegavān</li>
    </ul>
    <ul id="R_5.046.023">
        <li id="R_5.046.023ab">indrajit tu rathaṃ divyam āsthitaś citrakārmukaḥ</li>
        <li id="R_5.046.023cd">dhanur visphārayām āsa taḍidūrjitaniḥsvanam</li>
    </ul>
    <ul id="R_5.046.024">
        <li id="R_5.046.024ab">
            <seg type="pāda" n="a">tataḥ sametāv atitīkṣṇavegau</seg>
            <seg type="pāda" n="b">mahābalau tau raṇanirviśaṅkau</seg>
        </li>
        <li id="R_5.046.024cd">
            <seg type="pāda" n="c">kapiś ca rakṣo'dhipateś ca putraḥ</seg>
            <seg type="pāda" n="d">surāsurendrāv iva baddhavairau</seg>
        </li>
    </ul>
    <ul id="R_5.046.025">
        <li id="R_5.046.025ab">
            <seg type="pāda" n="a">sa tasya vīrasya mahārathasyā</seg>
            <seg type="pāda" n="b">dhanuṣmataḥ saṃyati saṃmatasya</seg>
        </li>
        <li id="R_5.046.025cd">
            <seg type="pāda" n="c">śarapravegaṃ vyahanat pravṛddhaś</seg>
            <seg type="pāda" n="d">cacāra mārge pitur aprameyaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.026">
        <li id="R_5.046.026ab">
            <seg type="pāda" n="a">tataḥ śarān āyatatīkṣṇaśalyān</seg>
            <seg type="pāda" n="b">supatriṇaḥ kāñcanacitrapuṅkhān</seg>
        </li>
        <li id="R_5.046.026cd">
            <seg type="pāda" n="c">mumoca vīraḥ paravīrahantā</seg>
            <seg type="pāda" n="d">susaṃtatān vajranipātavegān</seg>
        </li>
    </ul>
    <ul id="R_5.046.027">
        <li id="R_5.046.027ab">
            <seg type="pāda" n="a">sa tasya tat syandananiḥsvanaṃ ca</seg>
            <seg type="pāda" n="b">mṛdaṅgabherīpaṭahasvanaṃ ca</seg>
        </li>
        <li id="R_5.046.027cd">
            <seg type="pāda" n="c">vikṛṣyamāṇasya ca kārmukasya</seg>
            <seg type="pāda" n="d">niśamya ghoṣaṃ punar utpapāta</seg>
        </li>
    </ul>
    <ul id="R_5.046.028">
        <li id="R_5.046.028ab">śarāṇām antareṣv āśu vyavartata mahākapiḥ</li>
        <li id="R_5.046.028cd">haris tasyābhilakṣasya mokṣayaṃl lakṣyasaṃgraham</li>
    </ul>
    <ul id="R_5.046.029">
        <li id="R_5.046.029ab">śarāṇām agratas tasya punaḥ samabhivartata</li>
        <li id="R_5.046.029cd">prasārya hastau hanumān utpapātānilātmajaḥ</li>
    </ul>
    <ul id="R_5.046.030">
        <li id="R_5.046.030ab">tāv ubhau vegasaṃpannau raṇakarmaviśāradau</li>
        <li id="R_5.046.030cd">sarvabhūtamanogrāhi cakratur yuddham uttamam</li>
    </ul>
    <ul id="R_5.046.031">
        <li id="R_5.046.031ab">
            <seg type="pāda" n="a">hanūmato veda na rākṣaso 'ntaraṃ</seg>
            <seg type="pāda" n="b">na mārutis tasya mahātmano 'ntaram</seg>
        </li>
        <li id="R_5.046.031cd">
            <seg type="pāda" n="c">parasparaṃ nirviṣahau babhūvatuḥ</seg>
            <seg type="pāda" n="d">sametya tau devasamānavikramau</seg>
        </li>
    </ul>
    <ul id="R_5.046.032">
        <li id="R_5.046.032ab">
            <seg type="pāda" n="a">tatas tu lakṣye sa vihanyamāne</seg>
            <seg type="pāda" n="b">śareṣu mogheṣu ca saṃpatatsu</seg>
        </li>
        <li id="R_5.046.032cd">
            <seg type="pāda" n="c">jagāma cintāṃ mahatīṃ mahātmā</seg>
            <seg type="pāda" n="d">samādhisaṃyogasamāhitātmā</seg>
        </li>
    </ul>
    <ul id="R_5.046.033">
        <li id="R_5.046.033ab">
            <seg type="pāda" n="a">tato matiṃ rākṣasarājasūnuś</seg>
            <seg type="pāda" n="b">cakāra tasmin harivīramukhye</seg>
        </li>
        <li id="R_5.046.033cd">
            <seg type="pāda" n="c">avadhyatāṃ tasya kapeḥ samīkṣya</seg>
            <seg type="pāda" n="d">kathaṃ nigacched iti nigrahārtham</seg>
        </li>
    </ul>
    <ul id="R_5.046.034">
        <li id="R_5.046.034ab">tataḥ paitāmahāṃ vīraḥ so 'stram astravidāṃ varaḥ</li>
        <li id="R_5.046.034cd">saṃdadhe sumahātejās taṃ haripravaraṃ prati</li>
    </ul>
    <ul id="R_5.046.035">
        <li id="R_5.046.035ab">avadhyo 'yam iti jñātvā tam astreṇāstratattvavit</li>
        <li id="R_5.046.035cd">nijagrāha mahābāhur mārutātmajam indrajit</li>
    </ul>
    <ul id="R_5.046.036">
        <li id="R_5.046.036ab">tena baddhas tato 'streṇa rākṣasena sa vānaraḥ</li>
        <li id="R_5.046.036cd">abhavan nirviceṣṭaś ca papāta ca mahītale</li>
    </ul>
    <ul id="R_5.046.037">
        <li id="R_5.046.037ab">
            <seg type="pāda" n="a">tato 'tha buddhvā sa tadāstrabandhaṃ</seg>
            <seg type="pāda" n="b">prabhoḥ prabhāvād vigatālpavegaḥ</seg>
        </li>
        <li id="R_5.046.037cd">
            <seg type="pāda" n="c">pitāmahānugraham ātmanaś ca</seg>
            <seg type="pāda" n="d">vicintayām āsa haripravīraḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.038">
        <li id="R_5.046.038ab">tataḥ svāyambhuvair mantrair brahmāstram abhimantritam</li>
        <li id="R_5.046.038cd">hanūmāṃś cintayām āsa varadānaṃ pitāmahāt</li>
    </ul>
    <ul id="R_5.046.039">
        <li id="R_5.046.039ab">
            <seg type="pāda" n="a">na me 'strabandhasya ca śaktir asti</seg>
            <seg type="pāda" n="b">vimokṣaṇe lokaguroḥ prabhāvāt</seg>
        </li>
        <li id="R_5.046.039cd">
            <seg type="pāda" n="c">ity evam evaṃvihito 'strabandho</seg>
            <seg type="pāda" n="d">mayātmayoner anuvartitavyaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.040">
        <li id="R_5.046.040ab">
            <seg type="pāda" n="a">sa vīryam astrasya kapir vicārya</seg>
            <seg type="pāda" n="b">pitāmahānugraham ātmanaś ca</seg>
        </li>
        <li id="R_5.046.040cd">
            <seg type="pāda" n="c">vimokṣaśaktiṃ paricintayitvā</seg>
            <seg type="pāda" n="d">pitāmahājñām anuvartate sma</seg>
        </li>
    </ul>
    <ul id="R_5.046.041">
        <li id="R_5.046.041ab">astreṇāpi hi baddhasya bhayaṃ mama na jāyate</li>
        <li id="R_5.046.041cd">pitāmahamahendrābhyāṃ rakṣitasyānilena ca</li>
    </ul>
    <ul id="R_5.046.042">
        <li id="R_5.046.042ab">grahaṇe cāpi rakṣobhir mahan me guṇadarśanam</li>
        <li id="R_5.046.042cd">rākṣasendreṇa saṃvādas tasmād gṛhṇantu māṃ pare</li>
    </ul>
    <ul id="R_5.046.043">
        <li id="R_5.046.043ab">
            <seg type="pāda" n="a">sa niścitārthaḥ paravīrahantā</seg>
            <seg type="pāda" n="b">samīkṣya karī vinivṛttaceṣṭaḥ</seg>
        </li>
        <li id="R_5.046.043cd">
            <seg type="pāda" n="c">paraiḥ prasahyābhigatair nigṛhya</seg>
            <seg type="pāda" n="d">nanāda tais taiḥ paribhartsyamānaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.044">
        <li id="R_5.046.044ab">tatas taṃ rākṣasā dṛṣṭvā nirviceṣṭam ariṃdamam</li>
        <li id="R_5.046.044cd">babandhuḥ śaṇavalkaiś ca drumacīraiś ca saṃhataiḥ</li>
    </ul>
    <ul id="R_5.046.045">
        <li id="R_5.046.045ab">
            <seg type="pāda" n="a">sa rocayām āsa paraiś ca bandhanaṃ</seg>
            <seg type="pāda" n="b">prasahya vīrair abhinigrahaṃ ca</seg>
        </li>
        <li id="R_5.046.045cd">
            <seg type="pāda" n="c">kautūhalān māṃ yadi rākṣasendro</seg>
            <seg type="pāda" n="d">draṣṭuṃ vyavasyed iti niścitārthaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.046.046">
        <li id="R_5.046.046ab">sa baddhas tena valkena vimukto 'streṇa vīryavān</li>
        <li id="R_5.046.046cd">astrabandhaḥ sa cānyaṃ hi na bandham anuvartate</li>
    </ul>
    <ul id="R_5.046.047">
        <li id="R_5.046.047ab">
            <seg type="pāda" n="a">athendrajit taṃ drumacīrabandhaṃ</seg>
            <seg type="pāda" n="b">vicārya vīraḥ kapisattamaṃ tam</seg>
        </li>
        <li id="R_5.046.047cd">
            <seg type="pāda" n="c">vimuktam astreṇa jagāma cintām</seg>
            <seg type="pāda" n="d">anyena baddho hy anuvartate 'stram</seg>
        </li>
    </ul>
    <ul id="R_5.046.048">
        <li id="R_5.046.048ab">
            <seg type="pāda" n="a">aho mahat karma kṛtaṃ nirarthakaṃ</seg>
            <seg type="pāda" n="b">na rākṣasair mantragatir vimṛṣṭā</seg>
        </li>
        <li id="R_5.046.048cd">
            <seg type="pāda" n="c">punaś ca nāstre vihate 'stram anyat</seg>
            <seg type="pāda" n="d">pravartate saṃśayitāḥ sma sarve</seg>
        </li>
    </ul>
    <ul id="R_5.046.049">
        <li id="R_5.046.049ab">astreṇa hanumān mukto nātmānam avabudhyate</li>
        <li id="R_5.046.049cd">kṛṣyamāṇas tu rakṣobhis taiś ca bandhair nipīḍitaḥ</li>
    </ul>
    <ul id="R_5.046.050">
        <li id="R_5.046.050ab">hanyamānas tataḥ krūrai rākṣasaiḥ kāṣṭhamuṣṭibhiḥ</li>
        <li id="R_5.046.050cd">samīpaṃ rākṣasendrasya prākṛṣyata sa vānaraḥ</li>
    </ul>
    <ul id="R_5.046.051">
        <li id="R_5.046.051ab">
            <seg type="pāda" n="a">athendrajit taṃ prasamīkṣya muktam</seg>
            <seg type="pāda" n="b">astreṇa baddhaṃ drumacīrasūtraiḥ</seg>
        </li>
        <li id="R_5.046.051cd">
            <seg type="pāda" n="c">vyadarśayat tatra mahābalaṃ taṃ</seg>
            <seg type="pāda" n="d">haripravīraṃ sagaṇāya rājñe</seg>
        </li>
    </ul>
    <ul id="R_5.046.052">
        <li id="R_5.046.052ab">taṃ mattam iva mātaṅgaṃ baddhaṃ kapivarottamam</li>
        <li id="R_5.046.052cd">rākṣasā rākṣasendrāya rāvaṇāya nyavedayan</li>
    </ul>
    <ul id="R_5.046.053">
        <li id="R_5.046.053ab">ko 'yaṃ kasya kuto vāpi kiṃ kāryaṃ ko vyapāśrayaḥ</li>
        <li id="R_5.046.053cd">iti rākṣasavīrāṇāṃ tatra saṃjajñire kathāḥ</li>
    </ul>
    <ul id="R_5.046.054">
        <li id="R_5.046.054ab">hanyatāṃ dahyatāṃ vāpi bhakṣyatām iti cāpare</li>
        <li id="R_5.046.054cd">rākṣasās tatra saṃkruddhāḥ parasparam athābruvan</li>
    </ul>
    <ul id="R_5.046.055">
        <li id="R_5.046.055ab">
            <seg type="pāda" n="a">atītya mārgaṃ sahasā mahātmā</seg>
            <seg type="pāda" n="b">sa tatra rakṣo'dhipapādamūle</seg>
        </li>
        <li id="R_5.046.055cd">
            <seg type="pāda" n="c">dadarśa rājñaḥ paricāravṛddhān</seg>
            <seg type="pāda" n="d">gṛhaṃ mahāratnavibhūṣitaṃ ca</seg>
        </li>
    </ul>
    <ul id="R_5.046.056">
        <li id="R_5.046.056ab">sa dadarśa mahātejā rāvaṇaḥ kapisattamam</li>
        <li id="R_5.046.056cd">rakṣobhir vikṛtākāraiḥ kṛṣyamāṇam itas tataḥ</li>
    </ul>
    <ul id="R_5.046.057">
        <li id="R_5.046.057ab">rākṣasādhipatiṃ cāpi dadarśa kapisattamaḥ</li>
        <li id="R_5.046.057cd">tejobalasamāyuktaṃ tapantam iva bhāskaram</li>
    </ul>
    <ul id="R_5.046.058">
        <li id="R_5.046.058ab">
            <seg type="pāda" n="a">sa roṣasaṃvartitatāmradṛṣṭir</seg>
            <seg type="pāda" n="b">daśānanas taṃ kapim anvavekṣya</seg>
        </li>
        <li id="R_5.046.058cd">
            <seg type="pāda" n="c">athopaviṣṭān kulaśīlavṛddhān</seg>
            <seg type="pāda" n="d">samādiśat taṃ prati mantramukhyān</seg>
        </li>
    </ul>
    <ul id="R_5.046.059">
        <li id="R_5.046.059ab">
            <seg type="pāda" n="a">yathākramaṃ taiḥ sa kapiś ca pṛṣṭaḥ</seg>
            <seg type="pāda" n="b">kāryārtham arthasya ca mūlam ādau</seg>
        </li>
        <li id="R_5.046.059cd">
            <seg type="pāda" n="c">nivedayām āsa harīśvarasya</seg>
            <seg type="pāda" n="d">dūtaḥ sakāśād aham āgato 'smi</seg>
        </li>
    </ul>
    <ul id="R_5.047.001">
        <li id="R_5.047.001ab">tataḥ sa karmaṇā tasya vismito bhīmavikramaḥ</li>
        <li id="R_5.047.001cd">hanumān roṣatāmrākṣo rakṣo'dhipam avaikṣata</li>
    </ul>
    <ul id="R_5.047.002">
        <li id="R_5.047.002ab">bhājamānaṃ mahārheṇa kāñcanena virājatā</li>
        <li id="R_5.047.002cd">muktājālāvṛtenātha mukuṭena mahādyutim</li>
    </ul>
    <ul id="R_5.047.003">
        <li id="R_5.047.003ab">vajrasaṃyogasaṃyuktair mahārhamaṇivigrahaiḥ</li>
        <li id="R_5.047.003cd">haimair ābharaṇaiś citrair manaseva prakalpitaiḥ</li>
    </ul>
    <ul id="R_5.047.004">
        <li id="R_5.047.004ab">mahārhakṣaumasaṃvītaṃ raktacandanarūṣitam</li>
        <li id="R_5.047.004cd">svanuliptaṃ vicitrābhir vividhabhiś ca bhaktibhiḥ</li>
    </ul>
    <ul id="R_5.047.005">
        <li id="R_5.047.005ab">vipulair darśanīyaiś ca rakṣākṣair bhīmadarśanaiḥ</li>
        <li id="R_5.047.005cd">dīptatīkṣṇamahādaṃṣṭraiḥ pralambadaśanacchadaiḥ</li>
    </ul>
    <ul id="R_5.047.006">
        <li id="R_5.047.006ab">śirobhir daśabhir vīraṃ bhrājamānaṃ mahaujasaṃ</li>
        <li id="R_5.047.006cd">nānāvyālasamākīrṇaiḥ śikharair iva mandaram</li>
    </ul>
    <ul id="R_5.047.007">
        <li id="R_5.047.007ab">nīlāñjanacaya prakhyaṃ hāreṇorasi rājatā</li>
        <li id="R_5.047.007cd">pūrṇacandrābhavaktreṇa sabalākam ivāmbudam</li>
    </ul>
    <ul id="R_5.047.008">
        <li id="R_5.047.008ab">bāhubhir baddhakeyūraiś candanottamarūṣitaiḥ</li>
        <li id="R_5.047.008cd">bhrājamānāṅgadaiḥ pīnaiḥ pañcaśīrṣair ivoragaiḥ</li>
    </ul>
    <ul id="R_5.047.009">
        <li id="R_5.047.009ab">mahati sphāṭike citre ratnasaṃyogasaṃskṛte</li>
        <li id="R_5.047.009cd">uttamāstaraṇāstīrṇe upaviṣṭaṃ varāsane</li>
    </ul>
    <ul id="R_5.047.010">
        <li id="R_5.047.010ab">alaṃkṛtābhir atyarthaṃ pramadābhiḥ samantataḥ</li>
        <li id="R_5.047.010cd">vālavyajanahastābhir ārāt samupasevitam</li>
    </ul>
    <ul id="R_5.047.011">
        <li id="R_5.047.011ab">durdhareṇa prahastena mahāpārśvena rakṣasā</li>
        <li id="R_5.047.011cd">mantribhir mantratattvajñair nikumbhena ca mantriṇā</li>
    </ul>
    <ul id="R_5.047.012">
        <li id="R_5.047.012ab">upopaviṣṭaṃ rakṣobhiś caturbhir baladarpitaiḥ</li>
        <li id="R_5.047.012cd">kṛtsnaiḥ parivṛtaṃ lokaṃ caturbhir iva sāgaraiḥ</li>
    </ul>
    <ul id="R_5.047.013">
        <li id="R_5.047.013ab">mantribhir mantratattvajñair anyaiś ca śubhabuddhibhiḥ</li>
        <li id="R_5.047.013cd">anvāsyamānaṃ sacivaiḥ surair iva sureśvaram</li>
    </ul>
    <ul id="R_5.047.014">
        <li id="R_5.047.014ab">apaśyad rākṣasapatiṃ hanūmān atitejasaṃ</li>
        <li id="R_5.047.014cd">viṣṭhitaṃ meruśikhare satoyam iva toyadam</li>
    </ul>
    <ul id="R_5.047.015">
        <li id="R_5.047.015ab">sa taiḥ saṃpīḍyamāno 'pi rakṣobhir bhīmavikramaiḥ</li>
        <li id="R_5.047.015cd">vismayaṃ paramaṃ gatvā rakṣo'dhipam avaikṣata</li>
    </ul>
    <ul id="R_5.047.016">
        <li id="R_5.047.016ab">bhrājamānaṃ tato dṛṣṭvā hanumān rākṣaseśvaram</li>
        <li id="R_5.047.016cd">manasā cintayām āsa tejasā tasya mohitaḥ</li>
    </ul>
    <ul id="R_5.047.017">
        <li id="R_5.047.017ab">aho rūpam aho dhairyam aho sattvam aho dyutiḥ</li>
        <li id="R_5.047.017cd">aho rākṣasarājasya sarvalakṣaṇayuktatā</li>
    </ul>
    <ul id="R_5.047.018">
        <li id="R_5.047.018ab">yady adharmo na balavān syād ayaṃ rākṣaseśvaraḥ</li>
        <li id="R_5.047.018cd">syād ayaṃ suralokasya saśakrasyāpi rakṣitā</li>
    </ul>
    <ul id="R_5.047.019">
        <li id="R_5.047.019ab">tena bibhyati khalv asmāl lokāḥ sāmaradānavāḥ</li>
        <li id="R_5.047.019cd">ayaṃ hy utsahate kruddhaḥ kartum ekārṇavaṃ jagat</li>
    </ul>
    <ul id="R_5.047.020">
        <li id="R_5.047.020ab">iti cintāṃ bahuvidhām akaron matimān kapiḥ</li>
        <li id="R_5.047.020cd">dṛṣṭvā rākṣasarājasya prabhāvam amitaujasaḥ</li>
    </ul>
    <ul id="R_5.048.001">
        <li id="R_5.048.001ab">tam udvīkṣya mahābāhuḥ piṅgākṣaṃ purataḥ sthitam</li>
        <li id="R_5.048.001cd">roṣeṇa mahatāviṣṭo rāvaṇo lokarāvaṇaḥ</li>
    </ul>
    <ul id="R_5.048.002">
        <li id="R_5.048.002ab">sa rājā roṣatāmrākṣaḥ prahastaṃ mantrisattamam</li>
        <li id="R_5.048.002cd">kālayuktam uvācedaṃ vaco vipulam arthavat</li>
    </ul>
    <ul id="R_5.048.003">
        <li id="R_5.048.003ab">durātmā pṛcchyatām eṣa kutaḥ kiṃ vāsya kāraṇam</li>
        <li id="R_5.048.003cd">vanabhaṅge ca ko 'syārtho rākṣasīnāṃ ca tarjane</li>
    </ul>
    <ul id="R_5.048.004">
        <li id="R_5.048.004ab">rāvaṇasya vacaḥ śrutvā prahasto vākyam abravīt</li>
        <li id="R_5.048.004cd">samāśvasihi bhadraṃ te na bhīḥ kāryā tvayā kape</li>
    </ul>
    <ul id="R_5.048.005">
        <li id="R_5.048.005ab">yadi tāvat tvam indreṇa preṣito rāvaṇālayam</li>
        <li id="R_5.048.005cd">tattvam ākhyāhi mā te bhūd bhayaṃ vānara mokṣyase</li>
    </ul>
    <ul id="R_5.048.006">
        <li id="R_5.048.006ab">yadi vaiśravaṇasya tvaṃ yamasya varuṇasya ca</li>
        <li id="R_5.048.006cd">cārurūpam idaṃ kṛtvā yamasya varuṇasya ca</li>
    </ul>
    <ul id="R_5.048.007">
        <li id="R_5.048.007ab">viṣṇunā preṣito vāpi dūto vijayakāṅkṣiṇā</li>
        <li id="R_5.048.007cd">na hi te vānaraṃ tejo rūpamātraṃ tu vānaram</li>
    </ul>
    <ul id="R_5.048.008">
        <li id="R_5.048.008ab">tattvataḥ kathayasvādya tato vānara mokṣyase</li>
        <li id="R_5.048.008cd">anṛtaṃ vadataś cāpi durlabhaṃ tava jīvitam</li>
    </ul>
    <ul id="R_5.048.009">
        <li id="R_5.048.009ab">atha vā yannimittas te praveśo rāvaṇālaye</li>
    </ul>
    <ul id="R_5.048.010">
        <li id="R_5.048.010ab">evam ukto harivaras tadā rakṣogaṇeśvaram</li>
        <li id="R_5.048.010cd">abravīn nāsmi śakrasya yamasya varuṇasya vā</li>
    </ul>
    <ul id="R_5.048.011">
        <li id="R_5.048.011ab">dhanadena na me sakhyaṃ viṣṇunā nāsmi coditaḥ</li>
        <li id="R_5.048.011cd">jātir eva mama tv eṣā vānaro 'ham ihāgataḥ</li>
    </ul>
    <ul id="R_5.048.012">
        <li id="R_5.048.012ab">darśane rākṣasendrasya durlabhe tad idaṃ mayā</li>
        <li id="R_5.048.012cd">vanaṃ rākṣasarājasya darśanārthe vināśitam</li>
    </ul>
    <ul id="R_5.048.013">
        <li id="R_5.048.013ab">tatas te rākṣasāḥ prāptā balino yuddhakāṅkṣiṇaḥ</li>
        <li id="R_5.048.013cd">rakṣaṇārthaṃ ca dehasya pratiyuddhā mayā raṇe</li>
    </ul>
    <ul id="R_5.048.014">
        <li id="R_5.048.014ab">astrapāśair na śakyo 'haṃ baddhuṃ devāsurair api</li>
        <li id="R_5.048.014cd">pitāmahād eva varo mamāpy eṣo 'bhyupāgataḥ</li>
    </ul>
    <ul id="R_5.048.015">
        <li id="R_5.048.015ab">rājānaṃ draṣṭukāmena mayāstram anuvartitam</li>
        <li id="R_5.048.015cd">vimukto aham astreṇa rākṣasais tv atipīḍitaḥ</li>
    </ul>
    <ul id="R_5.048.016">
        <li id="R_5.048.016ab">dūto 'ham iti vijñeyo rāghavasyāmitaujasaḥ</li>
        <li id="R_5.048.016cd">śrūyatāṃ cāpi vacanaṃ mama pathyam idaṃ prabho</li>
    </ul>
    <ul id="R_5.049.001">
        <li id="R_5.049.001ab">taṃ samīkṣya mahāsattvaṃ sattvavān harisattamaḥ</li>
        <li id="R_5.049.001cd">vākyam arthavad avyagras tam uvāca daśānanam</li>
    </ul>
    <ul id="R_5.049.002">
        <li id="R_5.049.002ab">ahaṃ sugrīvasaṃdeśād iha prāptas tavālayam</li>
        <li id="R_5.049.002cd">rākṣasendra harīśas tvāṃ bhrātā kuśalam abravīt</li>
    </ul>
    <ul id="R_5.049.003">
        <li id="R_5.049.003ab">bhrātuḥ śṛṇu samādeśaṃ sugrīvasya mahātmanaḥ</li>
        <li id="R_5.049.003cd">dharmārthopahitaṃ vākyam iha cāmutra ca kṣamam</li>
    </ul>
    <ul id="R_5.049.004">
        <li id="R_5.049.004ab">rājā daśaratho nāma rathakuñjaravājimān</li>
        <li id="R_5.049.004cd">piteva bandhur lokasya sureśvarasamadyutiḥ</li>
    </ul>
    <ul id="R_5.049.005">
        <li id="R_5.049.005ab">jyeṣṭhas tasya mahābāhuḥ putraḥ priyakaraḥ prabhuḥ</li>
        <li id="R_5.049.005cd">pitur nideśān niṣkrāntaḥ praviṣṭo daṇḍakāvanam</li>
    </ul>
    <ul id="R_5.049.006">
        <li id="R_5.049.006ab">lakṣmaṇena saha bhrātrā sītayā cāpi bhāryayā</li>
        <li id="R_5.049.006cd">rāmo nāma mahātejā dharmyaṃ panthānam āśritaḥ</li>
    </ul>
    <ul id="R_5.049.007">
        <li id="R_5.049.007ab">tasya bhāryā vane naṣṭā sītā patim anuvratā</li>
        <li id="R_5.049.007cd">vaidehasya sutā rājño janakasya mahātmanaḥ</li>
    </ul>
    <ul id="R_5.049.008">
        <li id="R_5.049.008ab">sa mārgamāṇas tāṃ devīṃ rājaputraḥ sahānujaḥ</li>
        <li id="R_5.049.008cd">ṛśyamūkam anuprāptaḥ sugrīveṇa ca saṃgataḥ</li>
    </ul>
    <ul id="R_5.049.009">
        <li id="R_5.049.009ab">tasya tena pratijñātaṃ sītāyāḥ parimārgaṇam</li>
        <li id="R_5.049.009cd">sugrīvasyāpi rāmeṇa harirājyaṃ niveditam</li>
    </ul>
    <ul id="R_5.049.010">
        <li id="R_5.049.010ab">tatas tena mṛdhe hatvā rājaputreṇa vālinam</li>
        <li id="R_5.049.010cd">sugrīvaḥ sthāpito rājye haryṛkṣāṇāṃ gaṇeśvaraḥ</li>
    </ul>
    <ul id="R_5.049.011">
        <li id="R_5.049.011ab">sa sītāmārgaṇe vyagraḥ sugrīvaḥ satyasaṃgaraḥ</li>
        <li id="R_5.049.011cd">harīn saṃpreṣayām āsa diśaḥ sarvā harīśvaraḥ</li>
    </ul>
    <ul id="R_5.049.012">
        <li id="R_5.049.012ab">tāṃ harīṇāṃ sahasrāṇi śatāni niyutāni ca</li>
        <li id="R_5.049.012cd">dikṣu sarvāsu mārgante adhaś copari cāmbare</li>
    </ul>
    <ul id="R_5.049.013">
        <li id="R_5.049.013ab">vainateya samāḥ ke cit ke cit tatrānilopamāḥ</li>
        <li id="R_5.049.013cd">asaṃgagatayaḥ śīghrā harivīrā mahābalāḥ</li>
    </ul>
    <ul id="R_5.049.014">
        <li id="R_5.049.014ab">ahaṃ tu hanumān nāma mārutasyaurasaḥ sutaḥ</li>
        <li id="R_5.049.014cd">sītāyās tu kṛte tūrṇaṃ śatayojanam āyatam</li>
        <li id="R_5.049.014ef">samudraṃ laṅghayitvaiva tāṃ didṛkṣur ihāgataḥ</li>
    </ul>
    <ul id="R_5.049.015">
        <li id="R_5.049.015ab">tad bhavān dṛṣṭadharmārthas tapaḥ kṛtaparigrahaḥ</li>
        <li id="R_5.049.015cd">paradārān mahāprājña noparoddhuṃ tvam arhasi</li>
    </ul>
    <ul id="R_5.049.016">
        <li id="R_5.049.016ab">na hi dharmaviruddheṣu bahv apāyeṣu karmasu</li>
        <li id="R_5.049.016cd">mūlaghātiṣu sajjante buddhimanto bhavadvidhāḥ</li>
    </ul>
    <ul id="R_5.049.017">
        <li id="R_5.049.017ab">kaś ca lakṣmaṇamuktānāṃ rāmakopānuvartinām</li>
        <li id="R_5.049.017cd">śarāṇām agrataḥ sthātuṃ śakto devāsureṣv api</li>
    </ul>
    <ul id="R_5.049.018">
        <li id="R_5.049.018ab">na cāpi triṣu lokeṣu rājan vidyeta kaś cana</li>
        <li id="R_5.049.018cd">rāghavasya vyalīkaṃ yaḥ kṛtvā sukham avāpnuyāt</li>
    </ul>
    <ul id="R_5.049.019">
        <li id="R_5.049.019ab">tat trikālahitaṃ vākyaṃ dharmyam arthānubandhi ca</li>
        <li id="R_5.049.019cd">manyasva naradevāya jānakī pratidīyatām</li>
    </ul>
    <ul id="R_5.049.020">
        <li id="R_5.049.020ab">dṛṣṭā hīyaṃ mayā devī labdhaṃ yad iha durlabham</li>
        <li id="R_5.049.020cd">uttaraṃ karma yac cheṣaṃ nimittaṃ tatra rāghavaḥ</li>
    </ul>
    <ul id="R_5.049.021">
        <li id="R_5.049.021ab">lakṣiteyaṃ mayā sītā tathā śokaparāyaṇā</li>
        <li id="R_5.049.021cd">gṛhya yāṃ nābhijānāsi pañcāsyām iva pannagīm</li>
    </ul>
    <ul id="R_5.049.022">
        <li id="R_5.049.022ab">neyaṃ jarayituṃ śakyā sāsurair amarair api</li>
        <li id="R_5.049.022cd">viṣasaṃsṛṣṭam atyarthaṃ bhuktam annam ivaujasā</li>
    </ul>
    <ul id="R_5.049.023">
        <li id="R_5.049.023ab">tapaḥsaṃtāpalabdhas te yo 'yaṃ dharmaparigrahaḥ</li>
        <li id="R_5.049.023cd">na sa nāśayituṃ nyāyya ātmaprāṇaparigrahaḥ</li>
    </ul>
    <ul id="R_5.049.024">
        <li id="R_5.049.024ab">avadhyatāṃ tapobhir yāṃ bhavān samanupaśyati</li>
        <li id="R_5.049.024cd">ātmanaḥ sāsurair devair hetus tatrāpy ayaṃ mahān</li>
    </ul>
    <ul id="R_5.049.025">
        <li id="R_5.049.025ab">sugrīvo na hi devo 'yaṃ nāsuro na ca mānuṣaḥ</li>
        <li id="R_5.049.025cd">na rākṣaso na gandharvo na yakṣo na ca pannagaḥ</li>
    </ul>
    <ul id="R_5.049.026">
        <li id="R_5.049.026ab">mānuṣo rāghavo rājan sugrīvaś ca harīśvaraḥ</li>
        <li id="R_5.049.026cd">tasmāt prāṇaparitrāṇaṃ kathaṃ rājan kariṣyasi</li>
    </ul>
    <ul id="R_5.049.027">
        <li id="R_5.049.027ab">na tu dharmopasaṃhāram adharmaphalasaṃhitam</li>
        <li id="R_5.049.027cd">tad eva phalam anveti dharmaś cādharmanāśanaḥ</li>
    </ul>
    <ul id="R_5.049.028">
        <li id="R_5.049.028ab">prāptaṃ dharmaphalaṃ tāvad bhavatā nātra saṃśayaḥ</li>
        <li id="R_5.049.028cd">phalam asyāpy adharmasya kṣipram eva prapatsyase</li>
    </ul>
    <ul id="R_5.049.029">
        <li id="R_5.049.029ab">janasthānavadhaṃ buddhvā buddhvā vālivadhaṃ tathā</li>
        <li id="R_5.049.029cd">rāmasugrīvasakhyaṃ ca budhyasva hitam ātmanaḥ</li>
    </ul>
    <ul id="R_5.049.030">
        <li id="R_5.049.030ab">kāmaṃ khalv aham apy ekaḥ savājirathakuñjarām</li>
        <li id="R_5.049.030cd">laṅkāṃ nāśayituṃ śaktas tasyaiṣa tu viniścayaḥ</li>
    </ul>
    <ul id="R_5.049.031">
        <li id="R_5.049.031ab">rāmeṇa hi pratijñātaṃ haryṛkṣagaṇasaṃnidhau</li>
        <li id="R_5.049.031cd">utsādanam amitrāṇāṃ sītā yais tu pradharṣitā</li>
    </ul>
    <ul id="R_5.049.032">
        <li id="R_5.049.032ab">apakurvan hi rāmasya sākṣād api puraṃdaraḥ</li>
        <li id="R_5.049.032cd">na sukhaṃ prāpnuyād anyaḥ kiṃ punas tvadvidho janaḥ</li>
    </ul>
    <ul id="R_5.049.033">
        <li id="R_5.049.033ab">yāṃ sītety abhijānāsi yeyaṃ tiṣṭhati te vaśe</li>
        <li id="R_5.049.033cd">kālarātrīti tāṃ viddhi sarvalaṅkāvināśinīm</li>
    </ul>
    <ul id="R_5.049.034">
        <li id="R_5.049.034ab">tad alaṃ kālapāśena sītā vigraharūpiṇā</li>
        <li id="R_5.049.034cd">svayaṃ skandhāvasaktena kṣamam ātmani cintyatām</li>
    </ul>
    <ul id="R_5.049.035">
        <li id="R_5.049.035ab">sītāyās tejasā dagdhāṃ rāmakopaprapīḍitām</li>
        <li id="R_5.049.035cd">dahyamanām imāṃ paśya purīṃ sāṭṭapratolikām</li>
    </ul>
    <ul id="R_5.049.036">
        <li id="R_5.049.036ab">
            <seg type="pāda" n="a">sa sauṣṭhavopetam adīnavādinaḥ</seg>
            <seg type="pāda" n="b">kaper niśamyāpratimo 'priyaṃ vacaḥ</seg>
        </li>
        <li id="R_5.049.036cd">
            <seg type="pāda" n="c">daśānanaḥ kopavivṛttalocanaḥ</seg>
            <seg type="pāda" n="d">samādiśat tasya vadhaṃ mahākapeḥ</seg>
        </li>
    </ul>
    <ul id="R_5.050.001">
        <li id="R_5.050.001ab">tasya tad vacanaṃ śrutvā vānarasya mahātmanaḥ</li>
        <li id="R_5.050.001cd">ājñāpayad vadhaṃ tasya rāvaṇaḥ krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_5.050.002">
        <li id="R_5.050.002ab">vadhe tasya samājñapte rāvaṇena durātmanā</li>
        <li id="R_5.050.002cd">niveditavato dautyaṃ nānumene vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_5.050.003">
        <li id="R_5.050.003ab">taṃ rakṣo'dhipatiṃ kruddhaṃ tac ca kāryam upasthitam</li>
        <li id="R_5.050.003cd">viditvā cintayām āsa kāryaṃ kāryavidhau sthitaḥ</li>
    </ul>
    <ul id="R_5.050.004">
        <li id="R_5.050.004ab">niścitārthas tataḥ sāmnāpūjya śatrujidagrajam</li>
        <li id="R_5.050.004cd">uvāca hitam atyarthaṃ vākyaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_5.050.005">
        <li id="R_5.050.005ab">rājan dharmaviruddhaṃ ca lokavṛtteś ca garhitam</li>
        <li id="R_5.050.005cd">tava cāsadṛśaṃ vīra kaper asya pramāpaṇam</li>
    </ul>
    <ul id="R_5.050.006">
        <li id="R_5.050.006ab">
            <seg type="pāda" n="a">asaṃśayaṃ śatrur ayaṃ pravṛddhaḥ</seg>
            <seg type="pāda" n="b">kṛtaṃ hy anenāpriyam aprameyam</seg>
        </li>
        <li id="R_5.050.006cd">
            <seg type="pāda" n="c">na dūtavadhyāṃ pravadanti santo</seg>
            <seg type="pāda" n="d">dūtasya dṛṣṭā bahavo hi daṇḍāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.050.007">
        <li id="R_5.050.007ab">
            <seg type="pāda" n="a">vairūpyām aṅgeṣu kaśābhighāto</seg>
            <seg type="pāda" n="b">mauṇḍyaṃ tathā lakṣmaṇasaṃnipātaḥ</seg>
        </li>
        <li id="R_5.050.007cd">
            <seg type="pāda" n="c">etān hi dūte pravadanti daṇḍān</seg>
            <seg type="pāda" n="d">vadhas tu dūtasya na naḥ śruto 'pi</seg>
        </li>
    </ul>
    <ul id="R_5.050.008">
        <li id="R_5.050.008ab">
            <seg type="pāda" n="a">kathaṃ ca dharmārthavinītabuddhiḥ</seg>
            <seg type="pāda" n="b">parāvarapratyayaniścitārthaḥ</seg>
        </li>
        <li id="R_5.050.008cd">
            <seg type="pāda" n="c">bhavadvidhaḥ kopavaśe hi tiṣṭhet</seg>
            <seg type="pāda" n="d">kopaṃ niyacchanti hi sattvavantaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.050.009">
        <li id="R_5.050.009ab">
            <seg type="pāda" n="a">na dharmavāde na ca lokavṛtte</seg>
            <seg type="pāda" n="b">na śāstrabuddhigrahaṇeṣu vāpi</seg>
        </li>
        <li id="R_5.050.009cd">
            <seg type="pāda" n="c">vidyeta kaś cit tava vīratulyas</seg>
            <seg type="pāda" n="d">tvaṃ hy uttamaḥ sarvasurāsurāṇām</seg>
        </li>
    </ul>
    <ul id="R_5.050.010">
        <li id="R_5.050.010ab">na cāpy asya kaper ghāte kaṃ cit paśyāmy ahaṃ guṇam</li>
        <li id="R_5.050.010cd">teṣv ayaṃ pātyatāṃ daṇḍo yair ayaṃ preṣitaḥ kapiḥ</li>
    </ul>
    <ul id="R_5.050.011">
        <li id="R_5.050.011ab">sādhur vā yadi vāsādhur parair eṣa samarpitaḥ</li>
        <li id="R_5.050.011cd">bruvan parārthaṃ paravān na dūto vadham arhati</li>
    </ul>
    <ul id="R_5.050.012">
        <li id="R_5.050.012ab">api cāsmin hate rājan nānyaṃ paśyāmi khecaram</li>
        <li id="R_5.050.012cd">iha yaḥ punar āgacchet paraṃ pāraṃ mahodadhiḥ</li>
    </ul>
    <ul id="R_5.050.013">
        <li id="R_5.050.013ab">tasmān nāsya vadhe yatnaḥ kāryaḥ parapuraṃjaya</li>
        <li id="R_5.050.013cd">bhavān sendreṣu deveṣu yatnam āsthātum arhati</li>
    </ul>
    <ul id="R_5.050.014">
        <li id="R_5.050.014ab">
            <seg type="pāda" n="a">asmin vinaṣṭe na hi dūtam anyaṃ</seg>
            <seg type="pāda" n="b">paśyāmi yas tau nararājaputrau</seg>
        </li>
        <li id="R_5.050.014cd">
            <seg type="pāda" n="c">yuddhāya yuddhapriyadurvinītāv</seg>
            <seg type="pāda" n="d">udyojayed dīrghapathāvaruddhau</seg>
        </li>
    </ul>
    <ul id="R_5.050.015">
        <li id="R_5.050.015ab">
            <seg type="pāda" n="a">parākramotsāhamanasvināṃ ca</seg>
            <seg type="pāda" n="b">surāsurāṇām api durjayena</seg>
        </li>
        <li id="R_5.050.015cd">
            <seg type="pāda" n="c">tvayā manonandana nairṛtānāṃ</seg>
            <seg type="pāda" n="d">yuddhāyatir nāśayituṃ na yuktā</seg>
        </li>
    </ul>
    <ul id="R_5.050.016">
        <li id="R_5.050.016ab">
            <seg type="pāda" n="a">hitāś ca śūrāś ca samāhitāś ca</seg>
            <seg type="pāda" n="b">kuleṣu jātāś ca mahāguṇeṣu</seg>
        </li>
        <li id="R_5.050.016cd">
            <seg type="pāda" n="c">manasvinaḥ śastrabhṛtāṃ variṣṭhāḥ</seg>
            <seg type="pāda" n="d">koṭyagraśaste subhṛtāś ca yodhāḥ</seg>
        </li>
    </ul>
    <ul id="R_5.050.017">
        <li id="R_5.050.017ab">
            <seg type="pāda" n="a">tad ekadeśena balasya tāvat</seg>
            <seg type="pāda" n="b">ke cit tavādeśakṛto 'payāntu</seg>
        </li>
        <li id="R_5.050.017cd">
            <seg type="pāda" n="c">tau rājaputrau vinigṛhya mūḍhau</seg>
            <seg type="pāda" n="d">pareṣu te bhāvayituṃ prabhāvam</seg>
        </li>
    </ul>
    <ul id="R_5.051.001">
        <li id="R_5.051.001ab">tasya tad vacanaṃ śrutvā daśagrīvo mahābalaḥ</li>
        <li id="R_5.051.001cd">deśakālahitaṃ vākyaṃ bhrātur uttamam abravīt</li>
    </ul>
    <ul id="R_5.051.002">
        <li id="R_5.051.002ab">samyag uktaṃ hi bhavatā dūtavadhyā vigarhitā</li>
        <li id="R_5.051.002cd">avaśyaṃ tu vadhād anyaḥ kriyatām asya nigrahaḥ</li>
    </ul>
    <ul id="R_5.051.003">
        <li id="R_5.051.003ab">kapīnāṃ kila lāṅgūlam iṣṭaṃ bhavati bhūṣaṇam</li>
        <li id="R_5.051.003cd">tad asya dīpyatāṃ śīghraṃ tena dagdhena gacchatu</li>
    </ul>
    <ul id="R_5.051.004">
        <li id="R_5.051.004ab">tataḥ paśyantv imaṃ dīnam aṅgavairūpyakarśitam</li>
        <li id="R_5.051.004cd">samitrā jñātayaḥ sarve bāndhavāḥ sasuhṛjjanāḥ</li>
    </ul>
    <ul id="R_5.051.005">
        <li id="R_5.051.005ab">ājñāpayad rākṣasendraḥ puraṃ sarvaṃ sacatvaram</li>
        <li id="R_5.051.005cd">lāṅgūlena pradīptena rakṣobhiḥ pariṇīyatām</li>
    </ul>
    <ul id="R_5.051.006">
        <li id="R_5.051.006ab">tasya tad vacanaṃ śrutvā rākṣasāḥ kopakarkaśāḥ</li>
        <li id="R_5.051.006cd">veṣṭante tasya lāṅgūlaṃ jīrṇaiḥ kārpāsikaiḥ paṭaiḥ</li>
    </ul>
    <ul id="R_5.051.007">
        <li id="R_5.051.007ab">saṃveṣṭyamāne lāṅgūle vyavardhata mahākapiḥ</li>
        <li id="R_5.051.007cd">śuṣkam indhanam āsādya vaneṣv iva hutāśanaḥ</li>
    </ul>
    <ul id="R_5.051.008">
        <li id="R_5.051.008ab">tailena pariṣicyātha te 'gniṃ tatrāvapātayan</li>
    </ul>
    <ul id="R_5.051.009">
        <li id="R_5.051.009ab">lāṅgūlena pradīptena rākṣasāṃs tān apātayat</li>
        <li id="R_5.051.009cd">roṣāmarṣaparītātmā bālasūryasamānanaḥ</li>
    </ul>
    <ul id="R_5.051.010">
        <li id="R_5.051.010ab">sa bhūyaḥ saṃgataiḥ krūrai rākasair harisattamaḥ</li>
        <li id="R_5.051.010cd">nibaddhaḥ kṛtavān vīras tatkālasadṛśīṃ matim</li>
    </ul>
    <ul id="R_5.051.011">
        <li id="R_5.051.011ab">kāmaṃ khalu na me śaktā nibadhasyāpi rākṣasāḥ</li>
        <li id="R_5.051.011cd">chittvā pāśān samutpatya hanyām aham imān punaḥ</li>
    </ul>
    <ul id="R_5.051.012">
        <li id="R_5.051.012ab">sarveṣām eva paryāpto rākṣasānām ahaṃ yudhi</li>
        <li id="R_5.051.012cd">kiṃ tu rāmasya prītyarthaṃ viṣahiṣye 'ham īdṛśam</li>
    </ul>
    <ul id="R_5.051.013">
        <li id="R_5.051.013ab">laṅkā carayitavyā me punar eva bhaved iti</li>
        <li id="R_5.051.013cd">rātrau na hi sudṛṣṭā me durgakarmavidhānataḥ</li>
        <li id="R_5.051.013ef">avaśyam eva draṣṭavyā mayā laṅkā niśākṣaye</li>
    </ul>
    <ul id="R_5.051.014">
        <li id="R_5.051.014ab">kāmaṃ bandhaiś ca me bhūyaḥ pucchasyoddīpanena ca</li>
        <li id="R_5.051.014cd">pīḍāṃ kurvantu rakṣāṃsi na me 'sti manasaḥ śramaḥ</li>
    </ul>
    <ul id="R_5.051.015">
        <li id="R_5.051.015ab">tatas te saṃvṛtākāraṃ sattvavantaṃ mahākapim</li>
        <li id="R_5.051.015cd">parigṛhya yayur hṛṣṭā rākṣasāḥ kapikuñjaram</li>
    </ul>
    <ul id="R_5.051.016">
        <li id="R_5.051.016ab">śaṅkhabherīninādais tair ghoṣayantaḥ svakarmabhiḥ</li>
        <li id="R_5.051.016cd">rākṣasāḥ krūrakarmāṇaś cārayanti sma tāṃ purīm</li>
    </ul>
    <ul id="R_5.051.017">
        <li id="R_5.051.017ab">hanumāṃś cārayām āsa rākṣasānāṃ mahāpurīm</li>
        <li id="R_5.051.017cd">athāpaśyad vimānāni vicitrāṇi mahākapiḥ</li>
    </ul>
    <ul id="R_5.051.018">
        <li id="R_5.051.018ab">saṃvṛtān bhūmibhāgāṃś ca suvibhaktāṃś ca catvarān</li>
        <li id="R_5.051.018cd">rathyāś ca gṛhasaṃbādhāḥ kapiḥ śṛṅgāṭakāni ca</li>
    </ul>
    <ul id="R_5.051.019">
        <li id="R_5.051.019ab">catvareṣu catuṣkeṣu rājamārge tathaiva ca</li>
        <li id="R_5.051.019cd">ghoṣayanti kapiṃ sarve cārīka iti rākṣasāḥ</li>
    </ul>
    <ul id="R_5.051.020">
        <li id="R_5.051.020ab">dīpyamāne tatas tasya lāṅgūlāgre hanūmataḥ</li>
        <li id="R_5.051.020cd">rākṣasyas tā virūpākṣyaḥ śaṃsur devyās tad apriyam</li>
    </ul>
    <ul id="R_5.051.021">
        <li id="R_5.051.021ab">yas tvayā kṛtasaṃvādaḥ sīte tāmramukhaḥ kapiḥ</li>
        <li id="R_5.051.021cd">lāṅgūlena pradīptena sa eṣa pariṇīyate</li>
    </ul>
    <ul id="R_5.051.022">
        <li id="R_5.051.022ab">śrutvā tad vacanaṃ krūram ātmāpaharaṇopamam</li>
        <li id="R_5.051.022cd">vaidehī śokasaṃtaptā hutāśanam upāgamat</li>
    </ul>
    <ul id="R_5.051.023">
        <li id="R_5.051.023ab">maṅgalābhimukhī tasya sā tadāsīn mahākapeḥ</li>
        <li id="R_5.051.023cd">upatasthe viśālākṣī prayatā havyavāhanam</li>
    </ul>
    <ul id="R_5.051.024">
        <li id="R_5.051.024ab">yady asti patiśuśrūṣā yady asti caritaṃ tapaḥ</li>
        <li id="R_5.051.024cd">yadi cāsty ekapatnītvaṃ śīto bhava hanūmataḥ</li>
    </ul>
    <ul id="R_5.051.025">
        <li id="R_5.051.025ab">yadi kaś cid anukrośas tasya mayy asti dhīmataḥ</li>
        <li id="R_5.051.025cd">yadi vā bhāgyaśeṣaṃ me śīto bhava hanūmataḥ</li>
    </ul>
    <ul id="R_5.051.026">
        <li id="R_5.051.026ab">yadi māṃ vṛttasaṃpannāṃ tatsamāgamalālasām</li>
        <li id="R_5.051.026cd">sa vijānāti dharmātmā śīto bhava hanūmataḥ</li>
    </ul>
    <ul id="R_5.051.027">
        <li id="R_5.051.027ab">yadi māṃ tārayaty āryaḥ sugrīvaḥ satyasaṃgaraḥ</li>
        <li id="R_5.051.027cd">asmād duḥkhān mahābāhuḥ śīto bhava hanūmataḥ</li>
    </ul>
    <ul id="R_5.051.028">
        <li id="R_5.051.028ab">tatas tīkṣṇārcir avyagraḥ pradakṣiṇaśikho 'nalaḥ</li>
        <li id="R_5.051.028cd">jajvāla mṛgaśāvākṣyāḥ śaṃsann iva śivaṃ kapeḥ</li>
    </ul>
    <ul id="R_5.051.029">
        <li id="R_5.051.029ab">dahyamāne ca lāṅgūle cintayām āsa vānaraḥ</li>
        <li id="R_5.051.029cd">pradīpto 'gnir ayaṃ kasmān na māṃ dahati sarvataḥ</li>
    </ul>
    <ul id="R_5.051.030">
        <li id="R_5.051.030ab">dṛśyate ca mahājvālaḥ karoti ca na me rujam</li>
        <li id="R_5.051.030cd">śiśirasyeva saṃpāto lāṅgūlāgre pratiṣṭhitaḥ</li>
    </ul>
    <ul id="R_5.051.031">
        <li id="R_5.051.031ab">atha vā tad idaṃ vyaktaṃ yad dṛṣṭaṃ plavatā mayā</li>
        <li id="R_5.051.031cd">rāmaprabhāvād āścaryaṃ parvataḥ saritāṃ patau</li>
    </ul>
    <ul id="R_5.051.032">
        <li id="R_5.051.032ab">yadi tāvat samudrasya mainākasya ca dhīmatha</li>
        <li id="R_5.051.032cd">rāmārthaṃ saṃbhramas tādṛk kim agnir na kariṣyati</li>
    </ul>
    <ul id="R_5.051.033">
        <li id="R_5.051.033ab">sītāyāś cānṛśaṃsyena tejasā rāghavasya ca</li>
        <li id="R_5.051.033cd">pituś ca mama sakhyena na māṃ dahati pāvakaḥ</li>
    </ul>
    <ul id="R_5.051.034">
        <li id="R_5.051.034ab">bhūyaḥ sa cintayām āsa muhūrtaṃ kapikuñjaraḥ</li>
        <li id="R_5.051.034cd">utpapātātha vegena nanāda ca mahākapiḥ</li>
    </ul>
    <ul id="R_5.051.035">
        <li id="R_5.051.035ab">puradvāraṃ tataḥ śrīmāñ śailaśṛṅgam ivonnatam</li>
        <li id="R_5.051.035cd">vibhaktarakṣaḥsaṃbādham āsasādānilātmajaḥ</li>
    </ul>
    <ul id="R_5.051.036">
        <li id="R_5.051.036ab">sa bhūtvā śailasaṃkāśaḥ kṣaṇena punar ātmavān</li>
        <li id="R_5.051.036cd">hrasvatāṃ paramāṃ prāpto bandhanāny avaśātayat</li>
    </ul>
    <ul id="R_5.051.037">
        <li id="R_5.051.037ab">vimuktaś cābhavac chrīmān punaḥ parvatasaṃnibhaḥ</li>
        <li id="R_5.051.037cd">vīkṣamāṇaś ca dadṛśe parighaṃ toraṇāśritam</li>
    </ul>
    <ul id="R_5.051.038">
        <li id="R_5.051.038ab">sa taṃ gṛhya mahābāhuḥ kālāyasapariṣkṛtam</li>
        <li id="R_5.051.038cd">rakṣiṇas tān punaḥ sarvān sūdayām āsa mārutiḥ</li>
    </ul>
    <ul id="R_5.051.039">
        <li id="R_5.051.039ab">
            <seg type="pāda" n="a">sa tān nihatvā raṇacaṇḍavikramaḥ</seg>
            <seg type="pāda" n="b">samīkṣamāṇaḥ punar eva laṅkām</seg>
        </li>
        <li id="R_5.051.039cd">
            <seg type="pāda" n="c">pradīptalāṅgūlakṛtārcimālī</seg>
            <seg type="pāda" n="d">prakāśatāditya ivāṃśumālī</seg>
        </li>
    </ul>
    <ul id="R_5.052.001">
        <li id="R_5.052.001ab">vīkṣamāṇas tato laṅkāṃ kapiḥ kṛtamanorathaḥ</li>
        <li id="R_5.052.001cd">vardhamānasamutsāhaḥ kāryaśeṣam acintayat</li>
    </ul>
    <ul id="R_5.052.002">
        <li id="R_5.052.002ab">kiṃ nu khalv aviśiṣṭaṃ me kartavyam iha sāmpratam</li>
        <li id="R_5.052.002cd">yad eṣāṃ rakṣasāṃ bhūyaḥ saṃtāpajananaṃ bhavet</li>
    </ul>
    <ul id="R_5.052.003">
        <li id="R_5.052.003ab">vanaṃ tāvat pramathitaṃ prakṛṣṭā rākṣasā hatāḥ</li>
        <li id="R_5.052.003cd">balaikadeśaḥ kṣapitaḥ śeṣaṃ durgavināśanam</li>
    </ul>
    <ul id="R_5.052.004">
        <li id="R_5.052.004ab">durge vināśite karma bhavet sukhapariśramam</li>
        <li id="R_5.052.004cd">alpayatnena kārye 'smin mama syāt saphalaḥ śramaḥ</li>
    </ul>
    <ul id="R_5.052.005">
        <li id="R_5.052.005ab">yo hy ayaṃ mama lāṅgūle dīpyate havyavāhanaḥ</li>
        <li id="R_5.052.005cd">asya saṃtarpaṇaṃ nyāyyaṃ kartum ebhir gṛhottamaiḥ</li>
    </ul>
    <ul id="R_5.052.006">
        <li id="R_5.052.006ab">tataḥ pradīptalāṅgūlaḥ savidyud iva toyadaḥ</li>
        <li id="R_5.052.006cd">bhavanāgreṣu laṅkāyā vicacāra mahākapiḥ</li>
    </ul>
    <ul id="R_5.052.007">
        <li id="R_5.052.007ab">mumoca hanumān agniṃ kālānalaśikhopamam</li>
    </ul>
    <ul id="R_5.052.008">
        <li id="R_5.052.008ab">śvasanena ca saṃyogād ativego mahābalaḥ</li>
        <li id="R_5.052.008cd">kālāgnir iva jajvāla prāvardhata hutāśanaḥ</li>
    </ul>
    <ul id="R_5.052.009">
        <li id="R_5.052.009ab">pradīptam agniṃ pavanas teṣu veśmasu cārayat</li>
    </ul>
    <ul id="R_5.052.010">
        <li id="R_5.052.010ab">tāni kāñcanajālāni muktāmaṇimayāni ca</li>
        <li id="R_5.052.010cd">bhavanāny avaśīryanta ratnavanti mahānti ca</li>
    </ul>
    <ul id="R_5.052.011">
        <li id="R_5.052.011ab">tāni bhagnavimānāni nipetur vasudhātale</li>
        <li id="R_5.052.011cd">bhavanānīva siddhānām ambarāt puṇyasaṃkṣaye</li>
    </ul>
    <ul id="R_5.052.012">
        <li id="R_5.052.012ab">vajravidrumavaidūryamuktārajatasaṃhitān</li>
        <li id="R_5.052.012cd">vicitrān bhavanād dhātūn syandamānān dadarśa saḥ</li>
    </ul>
    <ul id="R_5.052.013">
        <li id="R_5.052.013ab">nāgnis tṛpyati kāṣṭhānāṃ tṛṇānāṃ ca yathā tathā</li>
        <li id="R_5.052.013cd">hanūmān rākṣasendrāṇāṃ vadhe kiṃ cin na tṛpyati</li>
    </ul>
    <ul id="R_5.052.014">
        <li id="R_5.052.014ab">
            <seg type="pāda" n="a">hutāśanajvālasamāvṛtā sā</seg>
            <seg type="pāda" n="b">hatapravīrā parivṛttayodhā</seg>
        </li>
        <li id="R_5.052.014cd">
            <seg type="pāda" n="c">hanūmātaḥ krodhabalābhibhūtā</seg>
            <seg type="pāda" n="d">babhūva śāpopahateva laṅkā</seg>
        </li>
    </ul>
    <ul id="R_5.052.015">
        <li id="R_5.052.015ab">
            <seg type="pāda" n="a">sasaṃbhramaṃ trastaviṣaṇṇarākṣasāṃ</seg>
            <seg type="pāda" n="b">samujjvalaj jvālahutāśanāṅkitām</seg>
        </li>
        <li id="R_5.052.015cd">
            <seg type="pāda" n="c">dadarśa laṅkāṃ hanumān mahāmanāḥ</seg>
            <seg type="pāda" n="d">svayambhukopopahatām ivāvanim</seg>
        </li>
    </ul>
    <ul id="R_5.052.016">
        <li id="R_5.052.016ab">
            <seg type="pāda" n="a">sa rākṣasāṃs tān subahūṃś ca hatvā</seg>
            <seg type="pāda" n="b">vanaṃ ca bhaṅktvā bahupādapaṃ tat</seg>
        </li>
        <li id="R_5.052.016cd">
            <seg type="pāda" n="c">visṛjya rakṣo bhavaneṣu cāgniṃ</seg>
            <seg type="pāda" n="d">jagāma rāmaṃ manasā mahātmā</seg>
        </li>
    </ul>
    <ul id="R_5.052.017">
        <li id="R_5.052.017ab">laṅkāṃ samastāṃ saṃdīpya lāṅgūlāgniṃ mahākapiḥ</li>
        <li id="R_5.052.017cd">nirvāpayām āsa tadā samudre harisattamaḥ</li>
    </ul>
    <ul id="R_5.053.001">
        <li id="R_5.053.001ab">saṃdīpyamānāṃ vidhvastāṃ trastarakṣo gaṇāṃ purīm</li>
        <li id="R_5.053.001cd">avekṣya hānumāṃl laṅkāṃ cintayām āsa vānaraḥ</li>
    </ul>
    <ul id="R_5.053.002">
        <li id="R_5.053.002ab">tasyābhūt sumahāṃs trāsaḥ kutsā cātmany ajāyata</li>
        <li id="R_5.053.002cd">laṅkāṃ pradahatā karma kiṃsvit kṛtam idaṃ mayā</li>
    </ul>
    <ul id="R_5.053.003">
        <li id="R_5.053.003ab">dhanyās te puruṣaśreṣṭha ye buddhyā kopam utthitam</li>
        <li id="R_5.053.003cd">nirundhanti mahātmāno dīptam agnim ivāmbhasā</li>
    </ul>
    <ul id="R_5.053.004">
        <li id="R_5.053.004ab">yadi dagdhā tv iyaṃ laṅkā nūnam āryāpi jānakī</li>
        <li id="R_5.053.004cd">dagdhā tena mayā bhartur hataṃ kāryam ajānatā</li>
    </ul>
    <ul id="R_5.053.005">
        <li id="R_5.053.005ab">yad artham ayam ārambhas tat kāryam avasāditam</li>
        <li id="R_5.053.005cd">mayā hi dahatā laṅkāṃ na sītā parirakṣitā</li>
    </ul>
    <ul id="R_5.053.006">
        <li id="R_5.053.006ab">īṣatkāryam idaṃ kāryaṃ kṛtam āsīn na saṃśayaḥ</li>
        <li id="R_5.053.006cd">tasya krodhābhibhūtena mayā mūlakṣayaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_5.053.007">
        <li id="R_5.053.007ab">vinaṣṭā jānakī vyaktaṃ na hy adagdhaḥ pradṛśyate</li>
        <li id="R_5.053.007cd">laṅkāyāḥ kaś cid uddeśaḥ sarvā bhasmīkṛtā purī</li>
    </ul>
    <ul id="R_5.053.008">
        <li id="R_5.053.008ab">yadi tad vihataṃ kāryaṃ mayā prajñāviparyayāt</li>
        <li id="R_5.053.008cd">ihaiva prāṇasaṃnyāso mamāpi hy atirocate</li>
    </ul>
    <ul id="R_5.053.009">
        <li id="R_5.053.009ab">kim agnau nipatāmy adya āhosvid vaḍavāmukhe</li>
        <li id="R_5.053.009cd">śarīram āho sattvānāṃ dadmi sāgaravāsinām</li>
    </ul>
    <ul id="R_5.053.010">
        <li id="R_5.053.010ab">kathaṃ hi jīvatā śakyo mayā draṣṭuṃ harīśvaraḥ</li>
        <li id="R_5.053.010cd">tau vā puruṣaśārdūlau kāryasarvasvaghātinā</li>
    </ul>
    <ul id="R_5.053.011">
        <li id="R_5.053.011ab">mayā khalu tad evedaṃ roṣadoṣāt pradarśitam</li>
        <li id="R_5.053.011cd">prathitaṃ triṣu lokeṣu kapitam anavasthitam</li>
    </ul>
    <ul id="R_5.053.012">
        <li id="R_5.053.012ab">dhig astu rājasaṃ bhāvam anīśam anavasthitam</li>
        <li id="R_5.053.012cd">īśvareṇāpi yad rāgān mayā sītā na rakṣitā</li>
    </ul>
    <ul id="R_5.053.013">
        <li id="R_5.053.013ab">vinaṣṭāyāṃ tu sītāyāṃ tāv ubhau vinaśiṣyataḥ</li>
        <li id="R_5.053.013cd">tayor vināśe sugrīvaḥ sabandhur vinaśiṣyati</li>
    </ul>
    <ul id="R_5.053.014">
        <li id="R_5.053.014ab">etad eva vacaḥ śrutvā bharato bhrātṛvatsalaḥ</li>
        <li id="R_5.053.014cd">dharmātmā sahaśatrughnaḥ kathaṃ śakṣyati jīvitum</li>
    </ul>
    <ul id="R_5.053.015">
        <li id="R_5.053.015ab">ikṣvākuvaṃśe dharmiṣṭhe gate nāśam asaṃśayam</li>
        <li id="R_5.053.015cd">bhaviṣyanti prajāḥ sarvāḥ śokasaṃtāpapīḍitāḥ</li>
    </ul>
    <ul id="R_5.053.016">
        <li id="R_5.053.016ab">tad ahaṃ bhāgyarahito luptadharmārthasaṃgrahaḥ</li>
        <li id="R_5.053.016cd">roṣadoṣaparītātmā vyaktaṃ lokavināśanaḥ</li>
    </ul>
    <ul id="R_5.053.017">
        <li id="R_5.053.017ab">iti cintayatas tasya nimittāny upapedire</li>
        <li id="R_5.053.017cd">pūram apy upalabdhāni sākṣāt punar acintayat</li>
    </ul>
    <ul id="R_5.053.018">
        <li id="R_5.053.018ab">atha vā cārusarvāṅgī rakṣitā svena tejasā</li>
        <li id="R_5.053.018cd">na naśiṣyati kalyāṇī nāgnir agnau pravartate</li>
    </ul>
    <ul id="R_5.053.019">
        <li id="R_5.053.019ab">na hi dharmān manas tasya bhāryām amitatejasaḥ</li>
        <li id="R_5.053.019cd">svacāritrābhiguptāṃ tāṃ spraṣṭum arhati pāvakaḥ</li>
    </ul>
    <ul id="R_5.053.020">
        <li id="R_5.053.020ab">nūnaṃ rāmaprabhāvena vaidehyāḥ sukṛtena ca</li>
        <li id="R_5.053.020cd">yan māṃ dahanakarmāyaṃ nādahad dhavyavāhanaḥ</li>
    </ul>
    <ul id="R_5.053.021">
        <li id="R_5.053.021ab">trayāṇāṃ bharatādīnāṃ bhrātṝṇāṃ devatā ca yā</li>
        <li id="R_5.053.021cd">rāmasya ca manaḥkāntā sā kathaṃ vinaśiṣyati</li>
    </ul>
    <ul id="R_5.053.022">
        <li id="R_5.053.022ab">yad vā dahanakarmāyaṃ sarvatra prabhur avyayaḥ</li>
        <li id="R_5.053.022cd">na me dahati lāṅgūlaṃ katham āryāṃ pradhakṣyati</li>
    </ul>
    <ul id="R_5.053.023">
        <li id="R_5.053.023ab">tapasā satyavākyena ananyatvāc ca bhartari</li>
        <li id="R_5.053.023cd">api sā nirdahed agniṃ na tām agniḥ pradhakṣyati</li>
    </ul>
    <ul id="R_5.053.024">
        <li id="R_5.053.024ab">sa tathā cintayaṃs tatra devyā dharmaparigraham</li>
        <li id="R_5.053.024cd">śuśrāva hanumān vākyaṃ cāraṇānāṃ mahātmanām</li>
    </ul>
    <ul id="R_5.053.025">
        <li id="R_5.053.025ab">aho khalu kṛtaṃ karma durviṣahyaṃ hanūmatā</li>
        <li id="R_5.053.025cd">agniṃ visṛjatābhīkṣṇaṃ bhīmaṃ rākṣasasadmani</li>
    </ul>
    <ul id="R_5.053.026">
        <li id="R_5.053.026ab">dagdheyaṃ nagarī laṅkā sāṭṭaprākāratoraṇā</li>
        <li id="R_5.053.026cd">jānakī na ca dagdheti vismayo 'dbhuta eva naḥ</li>
    </ul>
    <ul id="R_5.053.027">
        <li id="R_5.053.027ab">sa nimittaiś ca dṛṣṭārthaiḥ kāraṇaiś ca mahāguṇaiḥ</li>
        <li id="R_5.053.027cd">ṛṣivākyaiś ca hanumān abhavat prītamānasaḥ</li>
    </ul>
    <ul id="R_5.053.028">
        <li id="R_5.053.028ab">
            <seg type="pāda" n="a">tataḥ kapiḥ prāptamanorathārthas</seg>
            <seg type="pāda" n="b">tām akṣatāṃ rājasutāṃ viditvā</seg>
        </li>
        <li id="R_5.053.028cd">
            <seg type="pāda" n="c">pratyakṣatas tāṃ punar eva dṛṣṭvā</seg>
            <seg type="pāda" n="d">pratiprayāṇāya matiṃ cakāra</seg>
        </li>
    </ul>
    <ul id="R_5.054.001">
        <li id="R_5.054.001ab">tatas tu śiṃśapāmūle jānakīṃ paryavasthitām</li>
        <li id="R_5.054.001cd">abhivādyābravīd diṣṭyā paśyāmi tvām ihākṣatām</li>
    </ul>
    <ul id="R_5.054.002">
        <li id="R_5.054.002ab">tatas taṃ prasthitaṃ sītā vīkṣamāṇā punaḥ punaḥ</li>
        <li id="R_5.054.002cd">bhartṛsnehānvitaṃ vākyaṃ hanūmantam abhāṣata</li>
    </ul>
    <ul id="R_5.054.003">
        <li id="R_5.054.003ab">kāmam asya tvam evaikaḥ kāryasya parisādhane</li>
        <li id="R_5.054.003cd">paryāptaḥ paravīraghna yaśasyas te balodayaḥ</li>
    </ul>
    <ul id="R_5.054.004">
        <li id="R_5.054.004ab">balais tu saṃkulāṃ kṛtvā laṅkāṃ parabalārdanaḥ</li>
        <li id="R_5.054.004cd">māṃ nayed yadi kākutsthas tasya tat sādṛśaṃ bhavet</li>
    </ul>
    <ul id="R_5.054.005">
        <li id="R_5.054.005ab">tad yathā tasya vikrāntam anurūpaṃ mahātmanaḥ</li>
        <li id="R_5.054.005cd">bhavaty āhavaśūrasya tattvam evopapādaya</li>
    </ul>
    <ul id="R_5.054.006">
        <li id="R_5.054.006ab">tad arthopahitaṃ vākyaṃ praśritaṃ hetusaṃhitam</li>
        <li id="R_5.054.006cd">niśamya hanumāṃs tasyā vākyam uttaram abravīt</li>
    </ul>
    <ul id="R_5.054.007">
        <li id="R_5.054.007ab">kṣipram eṣyati kākutstho haryṛkṣapravarair vṛtaḥ</li>
        <li id="R_5.054.007cd">yas te yudhi vijityārīñ śokaṃ vyapanayiṣyati</li>
    </ul>
    <ul id="R_5.054.008">
        <li id="R_5.054.008ab">evam āśvāsya vaidehīṃ hanūmān mārutātmajaḥ</li>
        <li id="R_5.054.008cd">gamanāya matiṃ kṛtvā vaidehīm abhyavādayat</li>
    </ul>
    <ul id="R_5.054.009">
        <li id="R_5.054.009ab">tataḥ sa kapiśārdūlaḥ svāmisaṃdarśanotsukaḥ</li>
        <li id="R_5.054.009cd">āruroha giriśreṣṭham ariṣṭam arimardanaḥ</li>
    </ul>
    <ul id="R_5.054.010">
        <li id="R_5.054.010ab">tuṅgapadmakajuṣṭābhir nīlābhir vanarājibhiḥ</li>
        <li id="R_5.054.010cd">sālatālāśvakarṇaiś ca vaṃśaiś ca bahubhir vṛtam</li>
    </ul>
    <ul id="R_5.054.011">
        <li id="R_5.054.011ab">latāvitānair vitataiḥ puṣpavadbhir alaṃkṛtam</li>
        <li id="R_5.054.011cd">nānāmṛgagaṇākīrṇaṃ dhātuniṣyandabhūṣitam</li>
    </ul>
    <ul id="R_5.054.012">
        <li id="R_5.054.012ab">bahuprasravaṇopetaṃ śilāsaṃcayasaṃkaṭam</li>
        <li id="R_5.054.012cd">maharṣiyakṣagandharvakiṃnaroragasevitam</li>
    </ul>
    <ul id="R_5.054.013">
        <li id="R_5.054.013ab">latāpādapasaṃbādhaṃ siṃhākulitakandaram</li>
        <li id="R_5.054.013cd">vyāghrasaṃghasamākīrṇaṃ svādumūlaphaladrumam</li>
    </ul>
    <ul id="R_5.054.014">
        <li id="R_5.054.014ab">tam ārurohātibalaḥ parvataṃ plavagottamaḥ</li>
        <li id="R_5.054.014cd">rāmadarśanaśīghreṇa praharṣeṇābhicoditaḥ</li>
    </ul>
    <ul id="R_5.054.015">
        <li id="R_5.054.015ab">tena pādatalākrāntā ramyeṣu girisānuṣu</li>
        <li id="R_5.054.015cd">saghoṣāḥ samaśīryanta śilāś cūrṇīkṛtās tataḥ</li>
    </ul>
    <ul id="R_5.054.016">
        <li id="R_5.054.016ab">sa tam āruhya śailendraṃ vyavardhata mahākapiḥ</li>
        <li id="R_5.054.016cd">dakṣiṇād uttaraṃ pāraṃ prārthayaṃl lavaṇāmbhasaḥ</li>
    </ul>
    <ul id="R_5.054.017">
        <li id="R_5.054.017ab">adhiruhya tato vīraḥ parvataṃ pavanātmajaḥ</li>
        <li id="R_5.054.017cd">dadarśa sāgaraṃ bhīmaṃ mīnoraganiṣevitam</li>
    </ul>
    <ul id="R_5.054.018">
        <li id="R_5.054.018ab">sa māruta ivākāśaṃ mārutasyātmasaṃbhavaḥ</li>
        <li id="R_5.054.018cd">prapede hariśārdūlo dakṣiṇād uttarāṃ diśam</li>
    </ul>
    <ul id="R_5.054.019">
        <li id="R_5.054.019ab">sa tadā pīḍitas tena kapinā parvatottamaḥ</li>
        <li id="R_5.054.019cd">rarāsa saha tair bhūtaiḥ prāviśad vasudhātalam</li>
        <li id="R_5.054.019ef">kampamānaiś ca śikharaiḥ patadbhir api ca drumaiḥ</li>
    </ul>
    <ul id="R_5.054.020">
        <li id="R_5.054.020ab">tasyoruvegān mathitāḥ pādapāḥ puṣpaśālinaḥ</li>
        <li id="R_5.054.020cd">nipetur bhūtale rugṇāḥ śakrāyudhahatā iva</li>
    </ul>
    <ul id="R_5.054.021">
        <li id="R_5.054.021ab">kandarodarasaṃsthānāṃ pīḍitānāṃ mahaujasām</li>
        <li id="R_5.054.021cd">siṃhānāṃ ninado bhīmo nabho bhindan sa śuśruve</li>
    </ul>
    <ul id="R_5.054.022">
        <li id="R_5.054.022ab">srastavyāviddhavasanā vyākulīkṛtabhūṣaṇā</li>
        <li id="R_5.054.022cd">vidyādharyaḥ samutpetuḥ sahasā dharaṇīdharāt</li>
    </ul>
    <ul id="R_5.054.023">
        <li id="R_5.054.023ab">atipramāṇā balino dīptajihvā mahāviṣāḥ</li>
        <li id="R_5.054.023cd">nipīḍitaśirogrīvā vyaveṣṭanta mahāhayaḥ</li>
    </ul>
    <ul id="R_5.054.024">
        <li id="R_5.054.024ab">kiṃnaroragagandharvayakṣavidyādharās tathā</li>
        <li id="R_5.054.024cd">pīḍitaṃ taṃ nagavaraṃ tyaktvā gaganam āsthitāḥ</li>
    </ul>
    <ul id="R_5.054.025">
        <li id="R_5.054.025ab">sa ca bhūmidharaḥ śrīmān balinā tena pīḍitaḥ</li>
        <li id="R_5.054.025cd">savṛkṣaśikharodagrāḥ praviveśa rasātalam</li>
    </ul>
    <ul id="R_5.054.026">
        <li id="R_5.054.026ab">daśayojanavistāras triṃśadyojanam ucchritaḥ</li>
        <li id="R_5.054.026cd">dharaṇyāṃ samatāṃ yātaḥ sa babhūva dharādharaḥ</li>
    </ul>
    <ul id="R_5.055.001">
        <li id="R_5.055.001ab">sacandrakumudaṃ ramyaṃ sārkakāraṇḍavaṃ śubham</li>
        <li id="R_5.055.001cd">tiṣyaśravaṇakadambam abhraśaivalaśādvalam</li>
    </ul>
    <ul id="R_5.055.002">
        <li id="R_5.055.002ab">punarvasu mahāmīnaṃ lohitāṅgamahāgraham</li>
        <li id="R_5.055.002cd">airāvatamahādvīpaṃ svātīhaṃsaviloḍitam</li>
    </ul>
    <ul id="R_5.055.003">
        <li id="R_5.055.003ab">vātasaṃghātajātormiṃ candrāṃśuśiśirāmbumat</li>
        <li id="R_5.055.003cd">bhujaṃgayakṣagandharvaprabuddhakamalotpalam</li>
    </ul>
    <ul id="R_5.055.004">
        <li id="R_5.055.004ab">grasamāna ivākāśaṃ tārādhipam ivālikhan</li>
        <li id="R_5.055.004cd">harann iva sanakṣatraṃ gaganaṃ sārkamaṇḍalam</li>
    </ul>
    <ul id="R_5.055.005">
        <li id="R_5.055.005ab">mārutasyālayaṃ śrīmān kapir vyomacaro mahān</li>
        <li id="R_5.055.005cd">hanūmān meghajālāni vikarṣann iva gacchati</li>
    </ul>
    <ul id="R_5.055.006">
        <li id="R_5.055.006ab">pāṇḍurāruṇavarṇāni nīlamāñjiṣṭhakāni ca</li>
        <li id="R_5.055.006cd">haritāruṇavarṇāni mahābhrāṇi cakāśire</li>
    </ul>
    <ul id="R_5.055.007">
        <li id="R_5.055.007ab">praviśann abhrajālāni niṣkramaṃś ca punaḥ punaḥ</li>
        <li id="R_5.055.007cd">pracchannaś ca prakāśaś ca candramā iva lakṣyate</li>
    </ul>
    <ul id="R_5.055.008">
        <li id="R_5.055.008ab">nadan nādena mahatā meghasvanamahāsvanaḥ</li>
        <li id="R_5.055.008cd">ājagāma mahātejāḥ punar madhyena sāgaram</li>
    </ul>
    <ul id="R_5.055.009">
        <li id="R_5.055.009ab">parvatendraṃ sunābhaṃ ca samupaspṛśya vīryavān</li>
        <li id="R_5.055.009cd">jyāmukta iva nārāco mahāvego 'bhyupāgataḥ</li>
    </ul>
    <ul id="R_5.055.010">
        <li id="R_5.055.010ab">sa kiṃ cid anusaṃprāptaḥ samālokya mahāgirim</li>
        <li id="R_5.055.010cd">mahendrameghasaṃkāśaṃ nanāda haripuṃgavaḥ</li>
    </ul>
    <ul id="R_5.055.011">
        <li id="R_5.055.011ab">niśamya nadato nādaṃ vānarās te samantataḥ</li>
        <li id="R_5.055.011cd">babhūvur utsukāḥ sarve suhṛddarśanakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_5.055.012">
        <li id="R_5.055.012ab">jāmbavān sa hariśreṣṭhaḥ prītisaṃhṛṣṭamānasaḥ</li>
        <li id="R_5.055.012cd">upāmantrya harīn sarvān idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_5.055.013">
        <li id="R_5.055.013ab">sarvathā kṛtakāryo 'sau hanūmān nātra saṃśayaḥ</li>
        <li id="R_5.055.013cd">na hy asyākṛtakāryasya nāda evaṃvidho bhavet</li>
    </ul>
    <ul id="R_5.055.014">
        <li id="R_5.055.014ab">tasyā bāhūruvegaṃ ca ninādaṃ ca mahātmanaḥ</li>
        <li id="R_5.055.014cd">niśamya harayo hṛṣṭāḥ samutpetus tatas tataḥ</li>
    </ul>
    <ul id="R_5.055.015">
        <li id="R_5.055.015ab">te nagāgrān nagāgrāṇi śikharāc chikharāṇi ca</li>
        <li id="R_5.055.015cd">prahṛṣṭāḥ samapadyanta hanūmantaṃ didṛkṣavaḥ</li>
    </ul>
    <ul id="R_5.055.016">
        <li id="R_5.055.016ab">te prītāḥ pādapāgreṣu gṛhya śākhāḥ supuṣpitāḥ</li>
        <li id="R_5.055.016cd">vāsāṃsīva prakāśāni samāvidhyanta vānarāḥ</li>
    </ul>
    <ul id="R_5.055.017">
        <li id="R_5.055.017ab">tam abhraghanasaṃkāśam āpatantaṃ mahākapim</li>
        <li id="R_5.055.017cd">dṛṣṭvā te vānarāḥ sarve tasthuḥ prāñjalayas tadā</li>
    </ul>
    <ul id="R_5.055.018">
        <li id="R_5.055.018ab">tatas tu vegavāṃs tasya girer girinibhaḥ kapiḥ</li>
        <li id="R_5.055.018cd">nipapāta mahendrasya śikhare pādapākule</li>
    </ul>
    <ul id="R_5.055.019">
        <li id="R_5.055.019ab">tatas te prītamanasaḥ sarve vānarapuṃgavāḥ</li>
        <li id="R_5.055.019cd">hanūmantaṃ mahātmānaṃ parivāryopatasthire</li>
    </ul>
    <ul id="R_5.055.020">
        <li id="R_5.055.020ab">parivārya ca te sarve parāṃ prītim upāgatāḥ</li>
        <li id="R_5.055.020cd">prahṛṣṭavadanāḥ sarve tam arogam upāgatam</li>
    </ul>
    <ul id="R_5.055.021">
        <li id="R_5.055.021ab">upāyanāni cādāya mūlāni ca phalāni ca</li>
        <li id="R_5.055.021cd">pratyarcayan hariśreṣṭhaṃ harayo mārutātmajam</li>
    </ul>
    <ul id="R_5.055.022">
        <li id="R_5.055.022ab">vinedur muditāḥ ke cic cakruḥ kila kilāṃ tathā</li>
        <li id="R_5.055.022cd">hṛṣṭāḥ pādapaśākhāś ca āninyur vānararṣabhāḥ</li>
    </ul>
    <ul id="R_5.055.023">
        <li id="R_5.055.023ab">hanūmāṃs tu gurūn vṛddhāñ jāmbavat pramukhāṃs tadā</li>
        <li id="R_5.055.023cd">kumāram aṅgadaṃ caiva so 'vandata mahākapiḥ</li>
    </ul>
    <ul id="R_5.055.024">
        <li id="R_5.055.024ab">sa tābhyāṃ pūjitaḥ pūjyaḥ kapibhiś ca prasāditaḥ</li>
        <li id="R_5.055.024cd">dṛṣṭā devīti vikrāntaḥ saṃkṣepeṇa nyavedayat</li>
    </ul>
    <ul id="R_5.055.025">
        <li id="R_5.055.025ab">niṣasāda ca hastena gṛhītvā vālinaḥ sutam</li>
        <li id="R_5.055.025cd">ramaṇīye vanoddeśe mahendrasya gires tadā</li>
    </ul>
    <ul id="R_5.055.026">
        <li id="R_5.055.026ab">hanūmān abravīd dhṛṣṭas tadā tān vānararṣabhān</li>
        <li id="R_5.055.026cd">aśokavanikāsaṃsthā dṛṣṭā sā janakātmajā</li>
    </ul>
    <ul id="R_5.055.027">
        <li id="R_5.055.027ab">rakṣyamāṇā sughorābhī rākṣasībhir aninditā</li>
        <li id="R_5.055.027cd">ekaveṇīdharā bālā rāmadarśanalālasā</li>
        <li id="R_5.055.027ef">upavāsapariśrāntā malinā jaṭilā kṛśā</li>
    </ul>
    <ul id="R_5.055.028">
        <li id="R_5.055.028ab">tato dṛṣṭeti vacanaṃ mahārtham amṛtopamam</li>
        <li id="R_5.055.028cd">niśamya māruteḥ sarve muditā vānarā bhavan</li>
    </ul>
    <ul id="R_5.055.029">
        <li id="R_5.055.029ab">kṣveḍanty anye nadanty anye garjanty anye mahābalāḥ</li>
        <li id="R_5.055.029cd">cakruḥ kila kilām anye pratigarjanti cāpare</li>
    </ul>
    <ul id="R_5.055.030">
        <li id="R_5.055.030ab">ke cid ucchritalāṅgūlāḥ prahṛṣṭāḥ kapikuñjarāḥ</li>
        <li id="R_5.055.030cd">añcitāyatadīrghāṇi lāṅgūlāni pravivyadhuḥ</li>
    </ul>
    <ul id="R_5.055.031">
        <li id="R_5.055.031ab">apare tu hanūmantaṃ vānarā vāraṇopamam</li>
        <li id="R_5.055.031cd">āplutya giriśṛṅgebhyaḥ saṃspṛśanti sma harṣitāḥ</li>
    </ul>
    <ul id="R_5.055.032">
        <li id="R_5.055.032ab">uktavākyaṃ hanūmantam aṅgadas tu tadābravīt</li>
        <li id="R_5.055.032cd">sarveṣāṃ harivīrāṇāṃ madhye vācam anuttamām</li>
    </ul>
    <ul id="R_5.055.033">
        <li id="R_5.055.033ab">sattve vīrye na te kaś cit samo vānaravidyate</li>
        <li id="R_5.055.033cd">yad avaplutya vistīrṇaṃ sāgaraṃ punar āgataḥ</li>
    </ul>
    <ul id="R_5.055.034">
        <li id="R_5.055.034ab">diṣṭyā dṛṣṭā tvayā devī rāmapatnī yaśasvinī</li>
        <li id="R_5.055.034cd">diṣṭyā tyakṣyati kākutsthaḥ śokaṃ sītā viyogajam</li>
    </ul>
    <ul id="R_5.055.035">
        <li id="R_5.055.035ab">tato 'ṅgadaṃ hanūmantaṃ jāmbavantaṃ ca vānarāḥ</li>
        <li id="R_5.055.035cd">parivārya pramuditā bhejire vipulāḥ śilāḥ</li>
    </ul>
    <ul id="R_5.055.036">
        <li id="R_5.055.036ab">śrotukāmāḥ samudrasya laṅghanaṃ vānarottamāḥ</li>
        <li id="R_5.055.036cd">darśanaṃ cāpi laṅkāyāḥ sītāyā rāvaṇasya ca</li>
        <li id="R_5.055.036ef">tasthuḥ prāñjalayaḥ sarve hanūmad vadanonmukhāḥ</li>
    </ul>
    <ul id="R_5.055.037">
        <li id="R_5.055.037ab">tasthau tatrāṅgadaḥ śrīmān vānarair bahubhir vṛtaḥ</li>
        <li id="R_5.055.037cd">upāsyamāno vibudhair divi devapatir yathā</li>
    </ul>
    <ul id="R_5.055.038">
        <li id="R_5.055.038ab">
            <seg type="pāda" n="a">hanūmatā kīrtimatā yaśasvinā</seg>
            <seg type="pāda" n="b">tathāṅgadenāṅgadabaddhabāhunā</seg>
        </li>
        <li id="R_5.055.038cd">
            <seg type="pāda" n="c">mudā tadādhyāsitam unnataṃ mahan</seg>
            <seg type="pāda" n="d">mahīdharāgraṃ jvalitaṃ śriyābhavat</seg>
        </li>
    </ul>
    <ul id="R_5.056.001">
        <li id="R_5.056.001ab">tatas tasya gireḥ śṛṅge mahendrasya mahābalāḥ</li>
        <li id="R_5.056.001cd">hanumatpramukhāḥ prītiṃ harayo jagmur uttamām</li>
    </ul>
    <ul id="R_5.056.002">
        <li id="R_5.056.002ab">taṃ tataḥ pratisaṃhṛṣṭaḥ prītimantaṃ mahākapim</li>
        <li id="R_5.056.002cd">jāmbavān kāryavṛttāntam apṛcchad anilātmajam</li>
    </ul>
    <ul id="R_5.056.003">
        <li id="R_5.056.003ab">kathaṃ dṛṣṭā tvayā devī kathaṃ vā tatra vartate</li>
        <li id="R_5.056.003cd">tasyāṃ vā sa kathaṃ vṛttaḥ krūrakarmā daśānanaḥ</li>
    </ul>
    <ul id="R_5.056.004">
        <li id="R_5.056.004ab">tattvataḥ sarvam etan naḥ prabrūhi tvaṃ mahākape</li>
        <li id="R_5.056.004cd">śrutārthāś cintayiṣyāmo bhūyaḥ kāryaviniścayam</li>
    </ul>
    <ul id="R_5.056.005">
        <li id="R_5.056.005ab">yaś cārthas tatra vaktavyo gatair asmābhir ātmavān</li>
        <li id="R_5.056.005cd">rakṣitavyaṃ ca yat tatra tad bhavān vyākarotu naḥ</li>
    </ul>
    <ul id="R_5.056.006">
        <li id="R_5.056.006ab">sa niyuktas tatas tena saṃprahṛṣṭatanūruhaḥ</li>
        <li id="R_5.056.006cd">namasyañ śirasā devyai sītāyai pratyabhāṣata</li>
    </ul>
    <ul id="R_5.056.007">
        <li id="R_5.056.007ab">pratyakṣam eva bhavatāṃ mahendrāgrāt kham āplutaḥ</li>
        <li id="R_5.056.007cd">udadher dakṣiṇaṃ pāraṃ kāṅkṣamāṇaḥ samāhitaḥ</li>
    </ul>
    <ul id="R_5.056.008">
        <li id="R_5.056.008ab">gacchataś ca hi me ghoraṃ vighnarūpam ivābhavat</li>
        <li id="R_5.056.008cd">kāñcanaṃ śikharaṃ divyaṃ paśyāmi sumanoharam</li>
    </ul>
    <ul id="R_5.056.009">
        <li id="R_5.056.009ab">sthitaṃ panthānam āvṛtya mene vighnaṃ ca taṃ nagam</li>
    </ul>
    <ul id="R_5.056.010">
        <li id="R_5.056.010ab">upasaṃgamya taṃ divyaṃ kāñcanaṃ nagasattamam</li>
        <li id="R_5.056.010cd">kṛtā me manasā buddhir bhettavyo 'yaṃ mayeti ca</li>
    </ul>
    <ul id="R_5.056.011">
        <li id="R_5.056.011ab">prahataṃ ca mayā tasya lāṅgūlena mahāgireḥ</li>
        <li id="R_5.056.011cd">śikharaṃ sūryasaṃkāśaṃ vyaśīryata sahasradhā</li>
    </ul>
    <ul id="R_5.056.012">
        <li id="R_5.056.012ab">vyavasāyaṃ ca me buddhvā sa hovāca mahāgiriḥ</li>
        <li id="R_5.056.012cd">putreti madhurāṃ bāṇīṃ manaḥprahlādayann iva</li>
    </ul>
    <ul id="R_5.056.013">
        <li id="R_5.056.013ab">pitṛvyaṃ cāpi māṃ viddhi sakhāyaṃ mātariśvanaḥ</li>
        <li id="R_5.056.013cd">mainākam iti vikhyātaṃ nivasantaṃ mahodadhau</li>
    </ul>
    <ul id="R_5.056.014">
        <li id="R_5.056.014ab">pakṣvavantaḥ purā putra babhūvuḥ parvatottamāḥ</li>
        <li id="R_5.056.014cd">chandataḥ pṛthivīṃ cerur bādhamānāḥ samantataḥ</li>
    </ul>
    <ul id="R_5.056.015">
        <li id="R_5.056.015ab">śrutvā nagānāṃ caritaṃ mahendraḥ pākaśāsanaḥ</li>
        <li id="R_5.056.015cd">ciccheda bhagavān pakṣān vajreṇaiṣāṃ sahasraśaḥ</li>
    </ul>
    <ul id="R_5.056.016">
        <li id="R_5.056.016ab">ahaṃ tu mokṣitas tasmāt tava pitrā mahātmanā</li>
        <li id="R_5.056.016cd">mārutena tadā vatsa prakṣipto 'smi mahārṇave</li>
    </ul>
    <ul id="R_5.056.017">
        <li id="R_5.056.017ab">rāmasya ca mayā sāhye vartitavyam ariṃdama</li>
        <li id="R_5.056.017cd">rāmo dharmabhṛtāṃ śreṣṭho mahendrasamavikramaḥ</li>
    </ul>
    <ul id="R_5.056.018">
        <li id="R_5.056.018ab">etac chrutvā mayā tasya mainākasya mahātmanaḥ</li>
        <li id="R_5.056.018cd">kāryam āvedya tu girer uddhataṃ ca mano mama</li>
    </ul>
    <ul id="R_5.056.019">
        <li id="R_5.056.019ab">tena cāham anujñāto mainākena mahātmanā</li>
        <li id="R_5.056.019cd">uttamaṃ javam āsthāya śeṣam adhvānam āsthitaḥ</li>
    </ul>
    <ul id="R_5.056.020">
        <li id="R_5.056.020ab">tato 'haṃ suciraṃ kālaṃ vegenābhyagamaṃ pathi</li>
        <li id="R_5.056.020cd">tataḥ paśyāmy ahaṃ devīṃ surasāṃ nāgamātaram</li>
    </ul>
    <ul id="R_5.056.021">
        <li id="R_5.056.021ab">samudramadhye sā devī vacanaṃ mām abhāṣata</li>
        <li id="R_5.056.021cd">mama bhakṣyaḥ pradiṣṭas tvam amārair harisattamam</li>
        <li id="R_5.056.021ef">tatas tvāṃ bhakṣayiṣyāmi vihitas tvaṃ cirasya me</li>
    </ul>
    <ul id="R_5.056.022">
        <li id="R_5.056.022ab">evam uktaḥ surasayā prāñjaliḥ praṇataḥ sthitaḥ</li>
        <li id="R_5.056.022cd">vivarṇavadano bhūtvā vākyaṃ cedam udīrayam</li>
    </ul>
    <ul id="R_5.056.023">
        <li id="R_5.056.023ab">rāmo dāśarathiḥ śrīmān praviṣṭo daṇḍakāvanam</li>
        <li id="R_5.056.023cd">lakṣmaṇena saha bhrātrā sītayā ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_5.056.024">
        <li id="R_5.056.024ab">tasya sītā hṛtā bhāryā rāvaṇena durātmanā</li>
        <li id="R_5.056.024cd">tasyāḥ sakāśaṃ dūto 'haṃ gamiṣye rāmaśāsanāt</li>
    </ul>
    <ul id="R_5.056.025">
        <li id="R_5.056.025ab">kartum arhasi rāmasya sāhyaṃ viṣayavāsini</li>
    </ul>
    <ul id="R_5.056.026">
        <li id="R_5.056.026ab">atha vā maithilīṃ dṛṣṭvā rāmaṃ cākliṣṭakāriṇam</li>
        <li id="R_5.056.026cd">āgamiṣyāmi te vaktraṃ satyaṃ pratiśṛṇoti me</li>
    </ul>
    <ul id="R_5.056.027">
        <li id="R_5.056.027ab">evam uktā mayā sā tu surasā kāmarūpiṇī</li>
        <li id="R_5.056.027cd">abravīn nātivarteta kaś cid eṣa varo mama</li>
    </ul>
    <ul id="R_5.056.028">
        <li id="R_5.056.028ab">evam uktaḥ surasayā daśayojanam āyataḥ</li>
        <li id="R_5.056.028cd">tato 'rdhaguṇavistāro babhūvāhaṃ kṣaṇena tu</li>
    </ul>
    <ul id="R_5.056.029">
        <li id="R_5.056.029ab">matpramāṇānurūpaṃ ca vyāditaṃ tanmukhaṃ tayā</li>
        <li id="R_5.056.029cd">tad dṛṣṭvā vyāditaṃ tv āsyaṃ hrasvaṃ hy akaravaṃ vapuḥ</li>
    </ul>
    <ul id="R_5.056.030">
        <li id="R_5.056.030ab">tasmin muhūrte ca punar babhūvāṅguṣṭhasaṃmitaḥ</li>
        <li id="R_5.056.030cd">abhipatyāśu tad vaktraṃ nirgato 'haṃ tataḥ kṣaṇāt</li>
    </ul>
    <ul id="R_5.056.031">
        <li id="R_5.056.031ab">abravīt surasā devī svena rūpeṇa māṃ punaḥ</li>
        <li id="R_5.056.031cd">arthasiddhyai hariśreṣṭha gaccha saumya yathāsukham</li>
    </ul>
    <ul id="R_5.056.032">
        <li id="R_5.056.032ab">samānaya ca vaidehīṃ rāghaveṇa mahātmanā</li>
        <li id="R_5.056.032cd">sukhī bhava mahābāho prītāsmi tava vānara</li>
    </ul>
    <ul id="R_5.056.033">
        <li id="R_5.056.033ab">tato 'haṃ sādhu sādhvīti sarvabhūtaiḥ praśaṃsitaḥ</li>
        <li id="R_5.056.033cd">tato 'ntarikṣaṃ vipulaṃ pluto 'haṃ garuḍo yathā</li>
    </ul>
    <ul id="R_5.056.034">
        <li id="R_5.056.034ab">chāyā me nigṛhītā ca na ca paśyāmi kiṃ cana</li>
        <li id="R_5.056.034cd">so 'haṃ vigatavegas tu diśo daśa vilokayan</li>
        <li id="R_5.056.034ef">na kiṃ cit tatra paśyāmi yena me 'pahṛtā gatiḥ</li>
    </ul>
    <ul id="R_5.056.035">
        <li id="R_5.056.035ab">tato me buddhir utpannā kiṃ nāma gamane mama</li>
        <li id="R_5.056.035cd">īdṛśo vighna utpanno rūpaṃ yatra na dṛśyate</li>
    </ul>
    <ul id="R_5.056.036">
        <li id="R_5.056.036ab">adho bhāgena me dṛṣṭiḥ śocatā pātitā mayā</li>
        <li id="R_5.056.036cd">tato 'drākṣam ahaṃ bhīmāṃ rākṣasīṃ salile śayām</li>
    </ul>
    <ul id="R_5.056.037">
        <li id="R_5.056.037ab">prahasya ca mahānādam ukto 'haṃ bhīmayā tayā</li>
        <li id="R_5.056.037cd">avasthitam asaṃbhrāntam idaṃ vākyam aśobhanam</li>
    </ul>
    <ul id="R_5.056.038">
        <li id="R_5.056.038ab">kvāsi gantā mahākāya kṣudhitāyā mamepsitaḥ</li>
        <li id="R_5.056.038cd">bhakṣaḥ prīṇaya me dehaṃ ciram āhāravarjitam</li>
    </ul>
    <ul id="R_5.056.039">
        <li id="R_5.056.039ab">bāḍham ity eva tāṃ vāṇīṃ pratyagṛhṇām ahaṃ tataḥ</li>
        <li id="R_5.056.039cd">āsya pramāṇād adhikaṃ tasyāḥ kāyam apūrayam</li>
    </ul>
    <ul id="R_5.056.040">
        <li id="R_5.056.040ab">tasyāś cāsyaṃ mahad bhīmaṃ vardhate mama bhakṣaṇe</li>
        <li id="R_5.056.040cd">na ca māṃ sā tu bubudhe mama vā vikṛtaṃ kṛtam</li>
    </ul>
    <ul id="R_5.056.041">
        <li id="R_5.056.041ab">tato 'haṃ vipulaṃ rūpaṃ saṃkṣipya nimiṣāntarāt</li>
        <li id="R_5.056.041cd">tasyā hṛdayam ādāya prapatāmi nabhastalam</li>
    </ul>
    <ul id="R_5.056.042">
        <li id="R_5.056.042ab">sā visṛṣṭabhujā bhīmā papāta lavaṇāmbhasi</li>
        <li id="R_5.056.042cd">mayā parvatasaṃkāśā nikṛttahṛdayā satī</li>
    </ul>
    <ul id="R_5.056.043">
        <li id="R_5.056.043ab">śṛṇomi khagatānāṃ ca siddhānāṃ cāraṇaiḥ saha</li>
        <li id="R_5.056.043cd">rākṣasī siṃhikā bhīmā kṣipraṃ hanumatā hṛtā</li>
    </ul>
    <ul id="R_5.056.044">
        <li id="R_5.056.044ab">tāṃ hatvā punar evāhaṃ kṛtyam ātyayikaṃ smaran</li>
        <li id="R_5.056.044cd">gatvā ca mahad adhvānaṃ paśyāmi nagamaṇḍitam</li>
        <li id="R_5.056.044ef">dakṣiṇaṃ tīram udadher laṅkā yatra ca sā purī</li>
    </ul>
    <ul id="R_5.056.045">
        <li id="R_5.056.045ab">astaṃ dinakare yāte rakṣasāṃ nilayaṃ purīm</li>
        <li id="R_5.056.045cd">praviṣṭo 'ham avijñāto rakṣobhir bhīmavikramaiḥ</li>
    </ul>
    <ul id="R_5.056.046">
        <li id="R_5.056.046ab">tatrāhaṃ sarvarātraṃ tu vicinvañ janakātmajām</li>
        <li id="R_5.056.046cd">rāvaṇāntaḥpuragato na cāpaśyaṃ sumadhyamām</li>
    </ul>
    <ul id="R_5.056.047">
        <li id="R_5.056.047ab">tataḥ sītām apaśyaṃs tu rāvaṇasya niveśane</li>
        <li id="R_5.056.047cd">śokasāgaram āsādya na pāram upalakṣaye</li>
    </ul>
    <ul id="R_5.056.048">
        <li id="R_5.056.048ab">śocatā ca mayā dṛṣṭaṃ prākāreṇa samāvṛtam</li>
        <li id="R_5.056.048cd">kāñcanena vikṛṣṭena gṛhopavanam uttamam</li>
    </ul>
    <ul id="R_5.056.049">
        <li id="R_5.056.049ab">sa prākāram avaplutya paśyāmi bahupādapam</li>
    </ul>
    <ul id="R_5.056.050">
        <li id="R_5.056.050ab">aśokavanikāmadhye śiṃśapāpādapo mahān</li>
        <li id="R_5.056.050cd">tam āruhya ca paśyāmi kāñcanaṃ kadalī vanam</li>
    </ul>
    <ul id="R_5.056.051">
        <li id="R_5.056.051ab">adūrāc chiṃśapāvṛkṣāt paśyāmi vanavarṇinīm</li>
        <li id="R_5.056.051cd">śyāmāṃ kamalapatrākṣīm upavāsakṛśānanām</li>
    </ul>
    <ul id="R_5.056.052">
        <li id="R_5.056.052ab">rākṣasībhir virūpābhiḥ krūrābhir abhisaṃvṛtām</li>
        <li id="R_5.056.052cd">māṃsaśoṇitabhakṣyābhir vyāghrībhir hariṇīṃ yathā</li>
    </ul>
    <ul id="R_5.056.053">
        <li id="R_5.056.053ab">tāṃ dṛṣṭvā tādṛśīṃ nārīṃ rāmapatnīm aninditām</li>
        <li id="R_5.056.053cd">tatraiva śiṃśapāvṛkṣe paśyann aham avasthitaḥ</li>
    </ul>
    <ul id="R_5.056.054">
        <li id="R_5.056.054ab">tato halahalāśabdaṃ kāñcīnūpuramiśritam</li>
        <li id="R_5.056.054cd">śṛṇomy adhikagambhīraṃ rāvaṇasya niveśane</li>
    </ul>
    <ul id="R_5.056.055">
        <li id="R_5.056.055ab">tato 'haṃ paramodvignaḥ svarūpaṃ pratyasaṃharam</li>
        <li id="R_5.056.055cd">ahaṃ ca śiṃśapāvṛkṣe pakṣīva gahane sthitaḥ</li>
    </ul>
    <ul id="R_5.056.056">
        <li id="R_5.056.056ab">tato rāvaṇadārāś ca rāvaṇaś ca mahābalaḥ</li>
        <li id="R_5.056.056cd">taṃ deśaṃ samanuprāptā yatra sītābhavat sthitā</li>
    </ul>
    <ul id="R_5.056.057">
        <li id="R_5.056.057ab">taṃ dṛṣṭvātha varārohā sītā rakṣogaṇeśvaram</li>
        <li id="R_5.056.057cd">saṃkucyorū stanau pīnau bāhubhyāṃ parirabhya ca</li>
    </ul>
    <ul id="R_5.056.058">
        <li id="R_5.056.058ab">tām uvāca daśagrīvaḥ sītāṃ paramaduḥkhitām</li>
        <li id="R_5.056.058cd">avākśirāḥ prapatito bahu manyasva mām iti</li>
    </ul>
    <ul id="R_5.056.059">
        <li id="R_5.056.059ab">yadi cet tvaṃ tu māṃ darpān nābhinandasi garvite</li>
        <li id="R_5.056.059cd">dvimāsānantaraṃ sīte pāsyāmi rudhiraṃ tava</li>
    </ul>
    <ul id="R_5.056.060">
        <li id="R_5.056.060ab">etac chrutvā vacas tasya rāvaṇasya durātmanaḥ</li>
        <li id="R_5.056.060cd">uvāca paramakruddhā sītā vacanam uttamam</li>
    </ul>
    <ul id="R_5.056.061">
        <li id="R_5.056.061ab">rākṣasādhama rāmasya bhāryām amitatejasaḥ</li>
        <li id="R_5.056.061cd">ikṣvākukulanāthasya snuṣāṃ daśarathasya ca</li>
        <li id="R_5.056.061ef">avācyaṃ vadato jihvā kathaṃ na patitā tava</li>
    </ul>
    <ul id="R_5.056.062">
        <li id="R_5.056.062ab">kiṃsvid vīryaṃ tavānārya yo māṃ bhartur asaṃnidhau</li>
        <li id="R_5.056.062cd">apahṛtyāgataḥ pāpa tenādṛṣṭo mahātmanā</li>
    </ul>
    <ul id="R_5.056.063">
        <li id="R_5.056.063ab">na tvaṃ rāmasya sadṛśo dāsye 'py asyā na yujyase</li>
        <li id="R_5.056.063cd">yajñīyaḥ satyavāk caiva raṇaślāghī ca rāghavaḥ</li>
    </ul>
    <ul id="R_5.056.064">
        <li id="R_5.056.064ab">jānakyā paruṣaṃ vākyam evam ukto daśānanaḥ</li>
        <li id="R_5.056.064cd">jajvāla sahasā kopāc citāstha iva pāvakaḥ</li>
    </ul>
    <ul id="R_5.056.065">
        <li id="R_5.056.065ab">vivṛtya nayane krūre muṣṭim udyamya dakṣiṇam</li>
        <li id="R_5.056.065cd">maithilīṃ hantum ārabdhaḥ strībhir hāhākṛtaṃ tadā</li>
    </ul>
    <ul id="R_5.056.066">
        <li id="R_5.056.066ab">strīṇāṃ madhyāt samutpatya tasya bhāryā durātmanaḥ</li>
        <li id="R_5.056.066cd">varā mandodarī nāma tayā sa pratiṣedhitaḥ</li>
    </ul>
    <ul id="R_5.056.067">
        <li id="R_5.056.067ab">uktaś ca madhurāṃ vāṇīṃ tayā sa madanārditaḥ</li>
        <li id="R_5.056.067cd">sītayā tava kiṃ kāryaṃ mahendrasamavikrama</li>
        <li id="R_5.056.067ef">mayā saha ramasvādya madviśiṣṭā na jānakī</li>
    </ul>
    <ul id="R_5.056.068">
        <li id="R_5.056.068ab">devagandharvakanyābhir yakṣakanyābhir eva ca</li>
        <li id="R_5.056.068cd">sārdhaṃ prabho ramasveha sītayā kiṃ kariṣyasi</li>
    </ul>
    <ul id="R_5.056.069">
        <li id="R_5.056.069ab">tatas tābhiḥ sametābhir nārībhiḥ sa mahābalaḥ</li>
        <li id="R_5.056.069cd">utthāpya sahasā nīto bhavanaṃ svaṃ niśācaraḥ</li>
    </ul>
    <ul id="R_5.056.070">
        <li id="R_5.056.070ab">yāte tasmin daśagrīve rākṣasyo vikṛtānanāḥ</li>
        <li id="R_5.056.070cd">sītāṃ nirbhartsayām āsur vākyaiḥ krūraiḥ sudāruṇaiḥ</li>
    </ul>
    <ul id="R_5.056.071">
        <li id="R_5.056.071ab">tṛṇavad bhāṣitaṃ tāsāṃ gaṇayām āsa jānakī</li>
        <li id="R_5.056.071cd">tarjitaṃ ca tadā tāsāṃ sītāṃ prāpya nirarthakam</li>
    </ul>
    <ul id="R_5.056.072">
        <li id="R_5.056.072ab">vṛthāgarjitaniśceṣṭā rākṣasyaḥ piśitāśanāḥ</li>
        <li id="R_5.056.072cd">rāvaṇāya śaśaṃsus tāḥ sītāvyavasitaṃ mahat</li>
    </ul>
    <ul id="R_5.056.073">
        <li id="R_5.056.073ab">tatas tāḥ sahitāḥ sarvā vihatāśā nirudyamāḥ</li>
        <li id="R_5.056.073cd">parikṣipya samantāt tāṃ nidrāvaśam upāgatāḥ</li>
    </ul>
    <ul id="R_5.056.074">
        <li id="R_5.056.074ab">tāsu caiva prasuptāsu sītā bhartṛhite ratā</li>
        <li id="R_5.056.074cd">vilapya karuṇaṃ dīnā praśuśoca suduḥkhitā</li>
    </ul>
    <ul id="R_5.056.075">
        <li id="R_5.056.075ab">tāṃ cāhaṃ tādṛśīṃ dṛṣṭvā sītāyā dāruṇāṃ daśām</li>
        <li id="R_5.056.075cd">cintayām āsa viśrānto na ca me nirvṛtaṃ manaḥ</li>
    </ul>
    <ul id="R_5.056.076">
        <li id="R_5.056.076ab">saṃbhāṣaṇārthe ca mayā jānakyāś cintito vidhiḥ</li>
        <li id="R_5.056.076cd">ikṣvākukulavaṃśas tu tato mama puraskṛtaḥ</li>
    </ul>
    <ul id="R_5.056.077">
        <li id="R_5.056.077ab">śrutvā tu gaditāṃ vācaṃ rājarṣigaṇapūjitām</li>
        <li id="R_5.056.077cd">pratyabhāṣata māṃ devī bāṣpaiḥ pihitalocanā</li>
    </ul>
    <ul id="R_5.056.078">
        <li id="R_5.056.078ab">kas tvaṃ kena kathaṃ ceha prāpto vānarapuṃgava</li>
        <li id="R_5.056.078cd">kā ca rāmeṇa te prītis tan me śaṃsitum arhasi</li>
    </ul>
    <ul id="R_5.056.079">
        <li id="R_5.056.079ab">tasyās tad vacanaṃ śrutvā aham apy abruvaṃ vacaḥ</li>
        <li id="R_5.056.079cd">devi rāmasya bhartus te sahāyo bhīmavikramaḥ</li>
        <li id="R_5.056.079ef">sugrīvo nāma vikrānto vānarendo mahābalaḥ</li>
    </ul>
    <ul id="R_5.056.080">
        <li id="R_5.056.080ab">tasya māṃ viddhi bhṛtyaṃ tvaṃ hanūmantam ihāgatam</li>
        <li id="R_5.056.080cd">bhartrāhaṃ prahitas tubhyaṃ rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_5.056.081">
        <li id="R_5.056.081ab">idaṃ ca puruṣavyāghraḥ śrīmān dāśarathiḥ svayam</li>
        <li id="R_5.056.081cd">aṅgulīyam abhijñānam adāt tubhyaṃ yaśasvini</li>
    </ul>
    <ul id="R_5.056.082">
        <li id="R_5.056.082ab">tad icchāmi tvayājñaptaṃ devi kiṃ karavāṇy aham</li>
        <li id="R_5.056.082cd">rāmalakṣmaṇayoḥ pārśvaṃ nayāmi tvāṃ kim uttaram</li>
    </ul>
    <ul id="R_5.056.083">
        <li id="R_5.056.083ab">etac chrutvā viditvā ca sītā janakanandinī</li>
        <li id="R_5.056.083cd">āha rāvaṇam utsādya rāghavo māṃ nayatv iti</li>
    </ul>
    <ul id="R_5.056.084">
        <li id="R_5.056.084ab">praṇamya śirasā devīm aham āryām aninditām</li>
        <li id="R_5.056.084cd">rāghavasya manohlādam abhijñānam ayāciṣam</li>
    </ul>
    <ul id="R_5.056.085">
        <li id="R_5.056.085ab">evam uktā varārohā maṇipravaram uttamam</li>
        <li id="R_5.056.085cd">prāyacchat paramodvignā vācā māṃ saṃdideśa ha</li>
    </ul>
    <ul id="R_5.056.086">
        <li id="R_5.056.086ab">tatas tasyai praṇamyāhaṃ rājaputryai samāhitaḥ</li>
        <li id="R_5.056.086cd">pradakṣiṇaṃ parikrāmam ihābhyudgatamānasaḥ</li>
    </ul>
    <ul id="R_5.056.087">
        <li id="R_5.056.087ab">uttaraṃ punar evāha niścitya manasā tadā</li>
        <li id="R_5.056.087cd">hanūman mama vṛttāntaṃ vaktum arhasi rāghave</li>
    </ul>
    <ul id="R_5.056.088">
        <li id="R_5.056.088ab">yathā śrutvaiva nacirāt tāv ubhau rāmalakṣmaṇau</li>
        <li id="R_5.056.088cd">sugrīvasahitau vīrāv upeyātāṃ tathā kuru</li>
    </ul>
    <ul id="R_5.056.089">
        <li id="R_5.056.089ab">yady anyathā bhaved etad dvau māsau jīvitaṃ mama</li>
        <li id="R_5.056.089cd">na māṃ drakṣyati kākutstho mriye sāham anāthavat</li>
    </ul>
    <ul id="R_5.056.090">
        <li id="R_5.056.090ab">tac chrutvā karuṇaṃ vākyaṃ krodho mām abhyavartata</li>
        <li id="R_5.056.090cd">uttaraṃ ca mayā dṛṣṭaṃ kāryaśeṣam anantaram</li>
    </ul>
    <ul id="R_5.056.091">
        <li id="R_5.056.091ab">tato 'vardhata me kāyas tadā parvatasaṃnibhaḥ</li>
        <li id="R_5.056.091cd">yuddhakāṅkṣī vanaṃ tac ca vināśayitum ārabhe</li>
    </ul>
    <ul id="R_5.056.092">
        <li id="R_5.056.092ab">tad bhagnaṃ vanaṣaṇḍaṃ tu bhrāntatrastamṛgadvijam</li>
        <li id="R_5.056.092cd">pratibuddhā nirīkṣante rākṣasyo vikṛtānanāḥ</li>
    </ul>
    <ul id="R_5.056.093">
        <li id="R_5.056.093ab">māṃ ca dṛṣṭvā vane tasmin samāgamya tatas tataḥ</li>
        <li id="R_5.056.093cd">tāḥ samabhyāgatāḥ kṣipraṃ rāvaṇāyācacakṣire</li>
    </ul>
    <ul id="R_5.056.094">
        <li id="R_5.056.094ab">rājan vanam idaṃ durgaṃ tava bhagnaṃ durātmanā</li>
        <li id="R_5.056.094cd">vānareṇa hy avijñāya tava vīryaṃ mahābala</li>
    </ul>
    <ul id="R_5.056.095">
        <li id="R_5.056.095ab">durbuddhes tasya rājendra tava vipriyakāriṇaḥ</li>
        <li id="R_5.056.095cd">vadham ājñāpaya kṣipraṃ yathāsau vilayaṃ vrajet</li>
    </ul>
    <ul id="R_5.056.096">
        <li id="R_5.056.096ab">tac chrutvā rākṣasendreṇa visṛṣṭā bhṛśadurjayāḥ</li>
        <li id="R_5.056.096cd">rākṣasāḥ kiṃkarā nāma rāvaṇasya mano'nugāḥ</li>
    </ul>
    <ul id="R_5.056.097">
        <li id="R_5.056.097ab">teṣām aśītisāhasraṃ śūlamudgarapāṇinām</li>
        <li id="R_5.056.097cd">mayā tasmin vanoddeśe parigheṇa niṣūditam</li>
    </ul>
    <ul id="R_5.056.098">
        <li id="R_5.056.098ab">teṣāṃ tu hataśeṣā ye te gatā laghuvikramāḥ</li>
        <li id="R_5.056.098cd">nihataṃ ca mayā sainyaṃ rāvaṇāyācacakṣire</li>
    </ul>
    <ul id="R_5.056.099">
        <li id="R_5.056.099ab">tato me buddhir utpannā caityaprāsādam ākramam</li>
    </ul>
    <ul id="R_5.056.100">
        <li id="R_5.056.100ab">tatrasthān rākṣasān hatvā śataṃ stambhena vai punaḥ</li>
        <li id="R_5.056.100cd">lalāma bhūto laṅkāyā mayā vidhvaṃsito ruṣā</li>
    </ul>
    <ul id="R_5.056.101">
        <li id="R_5.056.101ab">tataḥ prahastasya sutaṃ jambumālinam ādiśat</li>
    </ul>
    <ul id="R_5.056.102">
        <li id="R_5.056.102ab">tam ahaṃ balasaṃpannaṃ rākṣasaṃ raṇakovidam</li>
        <li id="R_5.056.102cd">parigheṇātighoreṇa sūdayāmi sahānugam</li>
    </ul>
    <ul id="R_5.056.103">
        <li id="R_5.056.103ab">tac chrutvā rākṣasendras tu mantriputrān mahābalān</li>
        <li id="R_5.056.103cd">padātibalasaṃpannān preṣayām āsa rāvaṇaḥ</li>
        <li id="R_5.056.103ef">parigheṇaiva tān sarvān nayāmi yamasādanam</li>
    </ul>
    <ul id="R_5.056.104">
        <li id="R_5.056.104ab">mantriputrān hatāñ śrutvā samare laghuvikramān</li>
        <li id="R_5.056.104cd">pañcasenāgragāñ śūrān preṣayām āsa rāvaṇaḥ</li>
        <li id="R_5.056.104ef">tān ahaṃ saha sainyān vai sarvān evābhyasūdayam</li>
    </ul>
    <ul id="R_5.056.105">
        <li id="R_5.056.105ab">tataḥ punar daśagrīvaḥ putram akṣaṃ mahābalam</li>
        <li id="R_5.056.105cd">bahubhī rākasaiḥ sārdhaṃ preṣayām āsa saṃyuge</li>
    </ul>
    <ul id="R_5.056.106">
        <li id="R_5.056.106ab">taṃ tu mandodarī putraṃ kumāraṃ raṇapaṇḍitam</li>
        <li id="R_5.056.106cd">sahasā khaṃ samutkrāntaṃ pādayoś ca gṛhītavān</li>
        <li id="R_5.056.106ef">carmāsinaṃ śataguṇaṃ bhrāmayitvā vyapeṣayam</li>
    </ul>
    <ul id="R_5.056.107">
        <li id="R_5.056.107ab">tam akṣam āgataṃ bhagnaṃ niśamya sa daśānanaḥ</li>
        <li id="R_5.056.107cd">tata indrajitaṃ nāma dvitīyaṃ rāvaṇaḥ sutam</li>
        <li id="R_5.056.107ef">vyādideśa susaṃkruddho balinaṃ yuddhadurmadam</li>
    </ul>
    <ul id="R_5.056.108">
        <li id="R_5.056.108ab">tasyāpy ahaṃ balaṃ sarvaṃ taṃ ca rākṣasapuṃgavam</li>
        <li id="R_5.056.108cd">naṣṭaujasaṃ raṇe kṛtvā paraṃ harṣam upāgamam</li>
    </ul>
    <ul id="R_5.056.109">
        <li id="R_5.056.109ab">mahatā hi mahābāhuḥ pratyayena mahābalaḥ</li>
        <li id="R_5.056.109cd">preṣito rāvaṇenaiṣa saha vīrair madotkaṭaiḥ</li>
    </ul>
    <ul id="R_5.056.110">
        <li id="R_5.056.110ab">brāhmeṇāstreṇa sa tu māṃ prabadhnāc cātivegataḥ</li>
        <li id="R_5.056.110cd">rajjūbhir abhibadhnanti tato māṃ tatra rākṣasāḥ</li>
    </ul>
    <ul id="R_5.056.111">
        <li id="R_5.056.111ab">rāvaṇasya samīpaṃ ca gṛhītvā mām upānayan</li>
        <li id="R_5.056.111cd">dṛṣṭvā saṃbhāṣitaś cāhaṃ rāvaṇena durātmanā</li>
    </ul>
    <ul id="R_5.056.112">
        <li id="R_5.056.112ab">pṛṣṭaś ca laṅkāgamanaṃ rākṣasānāṃ ca tad vadham</li>
        <li id="R_5.056.112cd">tat sarvaṃ ca mayā tatra sītārtham iti jalpitam</li>
    </ul>
    <ul id="R_5.056.113">
        <li id="R_5.056.113ab">asyāhaṃ darśanākāṅkṣī prāptas tvadbhavanaṃ vibho</li>
        <li id="R_5.056.113cd">mārutasyaurasaḥ putro vānaro hanumān aham</li>
    </ul>
    <ul id="R_5.056.114">
        <li id="R_5.056.114ab">rāmadūtaṃ ca māṃ viddhi sugrīvasacivaṃ kapim</li>
        <li id="R_5.056.114cd">so 'haṃ dautyena rāmasya tvatsamīpam ihāgataḥ</li>
    </ul>
    <ul id="R_5.056.115">
        <li id="R_5.056.115ab">śṛṇu cāpi samādeśaṃ yad ahaṃ prabravīmi te</li>
        <li id="R_5.056.115cd">rākṣaseśa harīśas tvāṃ vākyam āha samāhitam</li>
        <li id="R_5.056.115ef">dharmārthakāmasahitaṃ hitaṃ pathyam ivāśanam</li>
    </ul>
    <ul id="R_5.056.116">
        <li id="R_5.056.116ab">vasato ṛṣyamūke me parvate vipuladrume</li>
        <li id="R_5.056.116cd">rāghavo raṇavikrānto mitratvaṃ samupāgataḥ</li>
    </ul>
    <ul id="R_5.056.117">
        <li id="R_5.056.117ab">tena me kathitaṃ rājan bhāryā me rakṣasā hṛtā</li>
        <li id="R_5.056.117cd">tatra sāhāyyahetor me samayaṃ kartum arhasi</li>
    </ul>
    <ul id="R_5.056.118">
        <li id="R_5.056.118ab">vālinā hṛtarājyena sugrīveṇa saha prabhuḥ</li>
        <li id="R_5.056.118cd">cakre 'gnisākṣikaṃ sakyaṃ rāghavaḥ sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.056.119">
        <li id="R_5.056.119ab">tena vālinam utsādya śareṇaikena saṃyuge</li>
        <li id="R_5.056.119cd">vānarāṇāṃ mahārājaḥ kṛtaḥ saṃplavatāṃ prabhuḥ</li>
    </ul>
    <ul id="R_5.056.120">
        <li id="R_5.056.120ab">tasya sāhāyyam asmābhiḥ kāryaṃ sarvātmanā tv iha</li>
        <li id="R_5.056.120cd">tena prasthāpitas tubhyaṃ samīpam iha dharmataḥ</li>
    </ul>
    <ul id="R_5.056.121">
        <li id="R_5.056.121ab">kṣipram ānīyatāṃ sītā dīyatāṃ rāghavasya ca</li>
        <li id="R_5.056.121cd">yāvan na harayo vīrā vidhamanti balaṃ tava</li>
    </ul>
    <ul id="R_5.056.122">
        <li id="R_5.056.122ab">vānarāṇāṃ prabhavo hi na kena viditaḥ purā</li>
        <li id="R_5.056.122cd">devatānāṃ sakāśaṃ ca ye gacchanti nimantritāḥ</li>
    </ul>
    <ul id="R_5.056.123">
        <li id="R_5.056.123ab">iti vānararājas tvām āhety abhihito mayā</li>
        <li id="R_5.056.123cd">mām aikṣata tato ruṣṭaś cakṣuṣā pradahann iva</li>
    </ul>
    <ul id="R_5.056.124">
        <li id="R_5.056.124ab">tena vadhyo 'ham ājñapto rakṣasā raudrakarmaṇā</li>
    </ul>
    <ul id="R_5.056.125">
        <li id="R_5.056.125ab">tato vibhīṣaṇo nāma tasya bhrātā mahāmatiḥ</li>
        <li id="R_5.056.125cd">tena rākṣasarājo 'sau yācito mama kāraṇāt</li>
    </ul>
    <ul id="R_5.056.126">
        <li id="R_5.056.126ab">dūtavadhyā na dṛṣṭā hi rājaśāstreṣu rākṣasa</li>
        <li id="R_5.056.126cd">dūtena veditavyaṃ ca yathārthaṃ hitavādinā</li>
    </ul>
    <ul id="R_5.056.127">
        <li id="R_5.056.127ab">sumahaty aparādhe 'pi dūtasyātulavikramaḥ</li>
        <li id="R_5.056.127cd">virūpakaraṇaṃ dṛṣṭaṃ na vadho 'stīha śāstrataḥ</li>
    </ul>
    <ul id="R_5.056.128">
        <li id="R_5.056.128ab">vibhīṣaṇenaivam ukto rāvaṇaḥ saṃdideśa tān</li>
        <li id="R_5.056.128cd">rākṣasān etad evādya lāṅgūlaṃ dahyatām iti</li>
    </ul>
    <ul id="R_5.056.129">
        <li id="R_5.056.129ab">tatas tasya vacaḥ śrutvā mama pucchaṃ samantataḥ</li>
        <li id="R_5.056.129cd">veṣṭitaṃ śaṇavalkaiś ca paṭaiḥ kārpāsakais tathā</li>
    </ul>
    <ul id="R_5.056.130">
        <li id="R_5.056.130ab">rākṣasāḥ siddhasaṃnāhās tatas te caṇḍavikramāḥ</li>
        <li id="R_5.056.130cd">tad ādīpyanta me pucchaṃ hanantaḥ kāṣṭhamuṣṭibhiḥ</li>
    </ul>
    <ul id="R_5.056.131">
        <li id="R_5.056.131ab">baddhasya bahubhiḥ pāśair yantritasya ca rākṣasaiḥ</li>
        <li id="R_5.056.131cd">na me pīḍā bhavet kā cid didṛkṣor nagarīṃ divā</li>
    </ul>
    <ul id="R_5.056.132">
        <li id="R_5.056.132ab">tatas te rākṣasāḥ śūrā baddhaṃ mām agnisaṃvṛtam</li>
        <li id="R_5.056.132cd">aghoṣayan rājamārge nagaradvāram āgatāḥ</li>
    </ul>
    <ul id="R_5.056.133">
        <li id="R_5.056.133ab">tato 'haṃ sumahad rūpaṃ saṃkṣipya punar ātmanaḥ</li>
        <li id="R_5.056.133cd">vimocayitvā taṃ bandhaṃ prakṛtiṣṭhaḥ sthitaḥ punaḥ</li>
    </ul>
    <ul id="R_5.056.134">
        <li id="R_5.056.134ab">āyasaṃ parighaṃ gṛhya tāni rakṣāṃsy asūdayam</li>
        <li id="R_5.056.134cd">tatas tan nagaradvāraṃ vegenāplutavān aham</li>
    </ul>
    <ul id="R_5.056.135">
        <li id="R_5.056.135ab">pucchena ca pradīptena tāṃ purīṃ sāṭṭagopurām</li>
        <li id="R_5.056.135cd">dahāmy aham asaṃbhrānto yugāntāgnir iva prajāḥ</li>
    </ul>
    <ul id="R_5.056.136">
        <li id="R_5.056.136ab">dagdhvā laṅkāṃ punaś caiva śaṅkā mām abhyavartata</li>
        <li id="R_5.056.136cd">dahatā ca mayā laṅkāṃ dagdhā sītā na saṃśayaḥ</li>
    </ul>
    <ul id="R_5.056.137">
        <li id="R_5.056.137ab">athāhaṃ vācam aśrauṣaṃ cāraṇānāṃ śubhākṣarām</li>
        <li id="R_5.056.137cd">jānakī na ca dagdheti vismayodantabhāṣiṇām</li>
    </ul>
    <ul id="R_5.056.138">
        <li id="R_5.056.138ab">tato me buddhir utpannā śrutvā tām adbhutāṃ giram</li>
        <li id="R_5.056.138cd">punar dṛṣṭā ca vaidehī visṛṣṭaś ca tayā punaḥ</li>
    </ul>
    <ul id="R_5.056.139">
        <li id="R_5.056.139ab">rāghavasya prabhāvena bhavatāṃ caiva tejasā</li>
        <li id="R_5.056.139cd">sugrīvasya ca kāryārthaṃ mayā sarvam anuṣṭhitam</li>
    </ul>
    <ul id="R_5.056.140">
        <li id="R_5.056.140ab">etat sarvaṃ mayā tatra yathāvad upapāditam</li>
        <li id="R_5.056.140cd">atra yan na kṛtaṃ śeṣaṃ tat sarvaṃ kriyatām iti</li>
    </ul>
    <ul id="R_5.057.001">
        <li id="R_5.057.001ab">etad ākhyānaṃ tat sarvaṃ hanūmān mārutātmajaḥ</li>
        <li id="R_5.057.001cd">bhūyaḥ samupacakrāma vacanaṃ vaktum uttaram</li>
    </ul>
    <ul id="R_5.057.002">
        <li id="R_5.057.002ab">saphalo rāghavodyogaḥ sugrīvasya ca saṃbhramaḥ</li>
        <li id="R_5.057.002cd">śīlam āsādya sītāyā mama ca plavanaṃ mahat</li>
    </ul>
    <ul id="R_5.057.003">
        <li id="R_5.057.003ab">āryāyāḥ sadṛśaṃ śīlaṃ sītāyāḥ plavagarṣabhāḥ</li>
        <li id="R_5.057.003cd">tapasā dhārayel lokān kruddhā vā nirdahed api</li>
    </ul>
    <ul id="R_5.057.004">
        <li id="R_5.057.004ab">sarvathātipravṛddho 'sau rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_5.057.004cd">yasya tāṃ spṛśato gātraṃ tapasā na vināśitam</li>
    </ul>
    <ul id="R_5.057.005">
        <li id="R_5.057.005ab">na tad agniśikhā kuryāt saṃspṛṣṭā pāṇinā satī</li>
        <li id="R_5.057.005cd">janakasyātmajā kuryād utkrodhakaluṣīkṛtā</li>
    </ul>
    <ul id="R_5.057.006">
        <li id="R_5.057.006ab">aśokavanikāmadhye rāvaṇasya durātmanaḥ</li>
        <li id="R_5.057.006cd">adhastāc chiṃśapāvṛkṣe sādhvī karuṇam āsthitā</li>
    </ul>
    <ul id="R_5.057.007">
        <li id="R_5.057.007ab">rākṣasībhiḥ parivṛtā śokasaṃtāpakarśitā</li>
        <li id="R_5.057.007cd">meghalekhāparivṛtā candralekheva niṣprabhā</li>
    </ul>
    <ul id="R_5.057.008">
        <li id="R_5.057.008ab">acintayantī vaidehī rāvaṇaṃ baladarpitam</li>
        <li id="R_5.057.008cd">pativratā ca suśroṇī avaṣṭabdhā ca jānakī</li>
    </ul>
    <ul id="R_5.057.009">
        <li id="R_5.057.009ab">anuraktā hi vaidehī rāmaṃ sarvātmanā śubhā</li>
        <li id="R_5.057.009cd">ananyacittā rāme ca paulomīva puraṃdare</li>
    </ul>
    <ul id="R_5.057.010">
        <li id="R_5.057.010ab">tad ekavāsaḥsaṃvītā rajodhvastā tathaiva ca</li>
        <li id="R_5.057.010cd">śokasaṃtāpadīnāṅgī sītā bhartṛhite ratā</li>
    </ul>
    <ul id="R_5.057.011">
        <li id="R_5.057.011ab">sā mayā rākṣasī madhye tarjyamānā muhur muhuḥ</li>
        <li id="R_5.057.011cd">rākṣasībhir virūpābhir dṛṣṭā hi pramadā vane</li>
    </ul>
    <ul id="R_5.057.012">
        <li id="R_5.057.012ab">ekaveṇīdharā dīnā bhartṛcintāparāyaṇā</li>
        <li id="R_5.057.012cd">adhaḥśayyā vivarṇāṅgī padminīva himāgame</li>
    </ul>
    <ul id="R_5.057.013">
        <li id="R_5.057.013ab">rāvaṇād vinivṛttārthā martavyakṛtaniścayā</li>
        <li id="R_5.057.013cd">kathaṃ cin mṛgaśāvākṣī viśvāsam upapāditā</li>
    </ul>
    <ul id="R_5.057.014">
        <li id="R_5.057.014ab">tataḥ saṃbhāṣitā caiva sarvam arthaṃ ca darśitā</li>
        <li id="R_5.057.014cd">rāmasugrīvasakhyaṃ ca śrutvā prītim upāgatā</li>
    </ul>
    <ul id="R_5.057.015">
        <li id="R_5.057.015ab">niyataḥ samudācāro bhaktir bhartari cottamā</li>
    </ul>
    <ul id="R_5.057.016">
        <li id="R_5.057.016ab">yan na hanti daśagrīvaṃ sa mahātmā daśānanaḥ</li>
        <li id="R_5.057.016cd">nimittamātraṃ rāmas tu vadhe tasya bhaviṣyati</li>
    </ul>
    <ul id="R_5.057.017">
        <li id="R_5.057.017ab">evam āste mahābhāgā sītā śokaparāyaṇā</li>
        <li id="R_5.057.017cd">yad atra pratikartavyaṃ tat sarvam upapādyatām</li>
    </ul>
    <ul id="R_5.058.001">
        <li id="R_5.058.001ab">tasya tad vacanaṃ śrutvā vālisūnur abhāṣata</li>
        <li id="R_5.058.001cd">jāmbavatpramukhān sarvān anujñāpya mahākapīn</li>
    </ul>
    <ul id="R_5.058.002">
        <li id="R_5.058.002ab">asminn evaṃgate kārye bhavatāṃ ca nivedite</li>
        <li id="R_5.058.002cd">nyāyyaṃ sma saha vaidehyā draṣṭuṃ tau pārthivātmajau</li>
    </ul>
    <ul id="R_5.058.003">
        <li id="R_5.058.003ab">aham eko 'pi paryāptaḥ sarākṣasagaṇāṃ purīm</li>
        <li id="R_5.058.003cd">tāṃ laṅkāṃ tarasā hantuṃ rāvaṇaṃ ca mahābalam</li>
    </ul>
    <ul id="R_5.058.004">
        <li id="R_5.058.004ab">kiṃ punaḥ sahito vīrair balavadbhiḥ kṛtātmabhiḥ</li>
        <li id="R_5.058.004cd">kṛtāstraiḥ plavagaiḥ śaktair bhavadbhir vijayaiṣibhiḥ</li>
    </ul>
    <ul id="R_5.058.005">
        <li id="R_5.058.005ab">ahaṃ tu rāvaṇaṃ yuddhe sasainyaṃ sapuraḥsaram</li>
        <li id="R_5.058.005cd">saputraṃ vidhamiṣyāmi sahodarayutaṃ yudhi</li>
    </ul>
    <ul id="R_5.058.006">
        <li id="R_5.058.006ab">brāhmam aindraṃ ca raudraṃ ca vāyavyaṃ vāruṇaṃ tathā</li>
        <li id="R_5.058.006cd">yadi śakrajito 'strāṇi durnirīkṣyāṇi saṃyuge</li>
        <li id="R_5.058.006ef">tāny ahaṃ vidhamiṣyāmi nihaniṣyāmi rākṣasān</li>
    </ul>
    <ul id="R_5.058.007">
        <li id="R_5.058.007ab">bhavatām abhyanujñāto vikramo me ruṇaddhi tam</li>
    </ul>
    <ul id="R_5.058.008">
        <li id="R_5.058.008ab">mayātulā visṛṣṭā hi śailavṛṣṭir nirantarā</li>
        <li id="R_5.058.008cd">devān api raṇe hanyāt kiṃ punas tān niśācarān</li>
    </ul>
    <ul id="R_5.058.009">
        <li id="R_5.058.009ab">sāgaro 'py atiyād velāṃ mandaraḥ pracaled api</li>
        <li id="R_5.058.009cd">na jāmbavantaṃ samare kampayed arivāhinī</li>
    </ul>
    <ul id="R_5.058.010">
        <li id="R_5.058.010ab">sarvarākṣasasaṃghānāṃ rākṣasā ye ca pūrvakāḥ</li>
        <li id="R_5.058.010cd">alam eko vināśāya vīro vāyusutaḥ kapiḥ</li>
    </ul>
    <ul id="R_5.058.011">
        <li id="R_5.058.011ab">panasasyoruvegena nīlasya ca mahātmanaḥ</li>
        <li id="R_5.058.011cd">mandaro 'py avaśīryeta kiṃ punar yudhi rākṣasāḥ</li>
    </ul>
    <ul id="R_5.058.012">
        <li id="R_5.058.012ab">sadevāsurayuddheṣu gandharvoragapakṣiṣu</li>
        <li id="R_5.058.012cd">maindasya pratiyoddhāraṃ śaṃsata dvividasya vā</li>
    </ul>
    <ul id="R_5.058.013">
        <li id="R_5.058.013ab">aśviputrau mahāvegāv etau plavagasattamau</li>
        <li id="R_5.058.013cd">pitāmahavarotsekāt paramaṃ darpam āsthitau</li>
    </ul>
    <ul id="R_5.058.014">
        <li id="R_5.058.014ab">aśvinor mānanārthaṃ hi sarvalokapitāmahaḥ</li>
        <li id="R_5.058.014cd">sarvāvadhyatvam atulam anayor dattavān purā</li>
    </ul>
    <ul id="R_5.058.015">
        <li id="R_5.058.015ab">varotsekena mattau ca pramathya mahatīṃ camūm</li>
        <li id="R_5.058.015cd">surāṇām amṛtaṃ vīrau pītavantau plavaṃgamau</li>
    </ul>
    <ul id="R_5.058.016">
        <li id="R_5.058.016ab">etāv eva hi saṃkruddhau savājirathakuñjarām</li>
        <li id="R_5.058.016cd">laṅkāṃ nāśayituṃ śaktau sarve tiṣṭhantu vānarāḥ</li>
    </ul>
    <ul id="R_5.058.017">
        <li id="R_5.058.017ab">ayuktaṃ tu vinā devīṃ dṛṣṭabadbhiḥ plavaṃgamāḥ</li>
        <li id="R_5.058.017cd">samīpaṃ gantum asmābhī rāghavasya mahātmanaḥ</li>
    </ul>
    <ul id="R_5.058.018">
        <li id="R_5.058.018ab">dṛṣṭā devī na cānītā iti tatra nivedanam</li>
        <li id="R_5.058.018cd">ayuktam iva paśyāmi bhavadbhiḥ khyātavikramaiḥ</li>
    </ul>
    <ul id="R_5.058.019">
        <li id="R_5.058.019ab">na hi vaḥ plavate kaś cin nāpi kaś cit parākrame</li>
        <li id="R_5.058.019cd">tulyaḥ sāmaradaityeṣu lokeṣu harisattamāḥ</li>
    </ul>
    <ul id="R_5.058.020">
        <li id="R_5.058.020ab">teṣv evaṃ hatavīreṣu rākṣaseṣu hanūmatā</li>
        <li id="R_5.058.020cd">kim anyad atra kartavyaṃ gṛhītvā yāma jānakīm</li>
    </ul>
    <ul id="R_5.058.021">
        <li id="R_5.058.021ab">tam evaṃ kṛtasaṃkalpaṃ jāmbavān harisattamaḥ</li>
        <li id="R_5.058.021cd">uvāca paramaprīto vākyam arthavad arthavit</li>
    </ul>
    <ul id="R_5.058.022">
        <li id="R_5.058.022ab">
            <seg type="pāda" n="a">na tāvad eṣā matir akṣamā no</seg>
            <seg type="pāda" n="b">yathā bhavān paśyati rājaputra</seg>
        </li>
        <li id="R_5.058.022cd">
            <seg type="pāda" n="c">yathā tu rāmasya matir niviṣṭā</seg>
            <seg type="pāda" n="d">tathā bhavān paśyatu kāryasiddhim</seg>
        </li>
    </ul>
    <ul id="R_5.059.001">
        <li id="R_5.059.001ab">tato jāmbavato vākyam agṛhṇanta vanaukasaḥ</li>
        <li id="R_5.059.001cd">aṅgadapramukhā vīrā hanūmāṃś ca mahākapiḥ</li>
    </ul>
    <ul id="R_5.059.002">
        <li id="R_5.059.002ab">prītimantas tataḥ sarve vāyuputrapuraḥsarāḥ</li>
        <li id="R_5.059.002cd">mahendrāgraṃ parityajya pupluvuḥ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_5.059.003">
        <li id="R_5.059.003ab">merumandarasaṃkāśā mattā iva mahāgajāḥ</li>
        <li id="R_5.059.003cd">chādayanta ivākāśaṃ mahākāyā mahābalāḥ</li>
    </ul>
    <ul id="R_5.059.004">
        <li id="R_5.059.004ab">sabhājyamānaṃ bhūtais tam ātmavantaṃ mahābalam</li>
        <li id="R_5.059.004cd">hanūmantaṃ mahāvegaṃ vahanta iva dṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_5.059.005">
        <li id="R_5.059.005ab">rāghave cārthanirvṛttiṃ bhartuś ca paramaṃ yaśaḥ</li>
        <li id="R_5.059.005cd">samādhāya samṛddhārthāḥ karmasiddhibhir unnatāḥ</li>
    </ul>
    <ul id="R_5.059.006">
        <li id="R_5.059.006ab">priyākhyānonmukhāḥ sarve sarve yuddhābhinandinaḥ</li>
        <li id="R_5.059.006cd">sarve rāmapratīkāre niścitārthā manasvinaḥ</li>
    </ul>
    <ul id="R_5.059.007">
        <li id="R_5.059.007ab">plavamānāḥ kham āplutya tatas te kānanaukṣakaḥ</li>
        <li id="R_5.059.007cd">nandanopamam āsedur vanaṃ drumalatāyutam</li>
    </ul>
    <ul id="R_5.059.008">
        <li id="R_5.059.008ab">yat tan madhuvanaṃ nāma sugrīvasyābhirakṣitam</li>
        <li id="R_5.059.008cd">adhṛṣyaṃ sarvabhūtānāṃ sarvabhūtamanoharam</li>
    </ul>
    <ul id="R_5.059.009">
        <li id="R_5.059.009ab">yad rakṣati mahāvīryaḥ sadā dadhimukhaḥ kapiḥ</li>
        <li id="R_5.059.009cd">mātulaḥ kapimukhyasya sugrīvasya mahātmanaḥ</li>
    </ul>
    <ul id="R_5.059.010">
        <li id="R_5.059.010ab">te tad vanam upāgamya babhūvuḥ paramotkaṭāḥ</li>
        <li id="R_5.059.010cd">vānarā vānarendrasya manaḥkāntatamaṃ mahat</li>
    </ul>
    <ul id="R_5.059.011">
        <li id="R_5.059.011ab">tatas te vānarā hṛṣṭā dṛṣṭvā madhuvanaṃ mahat</li>
        <li id="R_5.059.011cd">kumāram abhyayācanta madhūni madhupiṅgalāḥ</li>
    </ul>
    <ul id="R_5.059.012">
        <li id="R_5.059.012ab">tataḥ kumāras tān vṛddhāñ jāmbavatpramukhān kapīn</li>
        <li id="R_5.059.012cd">anumānya dadau teṣāṃ nisargaṃ madhubhakṣaṇe</li>
    </ul>
    <ul id="R_5.059.013">
        <li id="R_5.059.013ab">tataś cānumatāḥ sarve saṃprahṛṣṭā vanaukasaḥ</li>
        <li id="R_5.059.013cd">muditāś ca tatas te ca pranṛtyanti tatas tataḥ</li>
    </ul>
    <ul id="R_5.059.014">
        <li id="R_5.059.014ab">
            <seg type="pāda" n="a">gāyanti ke cit praṇamanti ke cin</seg>
            <seg type="pāda" n="b">nṛtyanti ke cit prahasanti ke cit</seg>
        </li>
        <li id="R_5.059.014cd">
            <seg type="pāda" n="c">patanti ke cid vicaranti ke cit</seg>
            <seg type="pāda" n="d">plavanti ke cit pralapanti ke cit</seg>
        </li>
    </ul>
    <ul id="R_5.059.015">
        <li id="R_5.059.015ab">
            <seg type="pāda" n="a">parasparaṃ ke cid upāśrayante</seg>
            <seg type="pāda" n="b">parasparaṃ ke cid atibruvante</seg>
        </li>
        <li id="R_5.059.015cd">
            <seg type="pāda" n="c">drumād drumaṃ ke cid abhiplavante</seg>
            <seg type="pāda" n="d">kṣitau nagāgrān nipatanti ke cit</seg>
        </li>
    </ul>
    <ul id="R_5.059.016">
        <li id="R_5.059.016ab">
            <seg type="pāda" n="a">mahītalāt ke cid udīrṇavegā</seg>
            <seg type="pāda" n="b">mahādrumāgrāṇy abhisaṃpatante</seg>
        </li>
        <li id="R_5.059.016cd">
            <seg type="pāda" n="c">gāyantam anyaḥ prahasann upaiti</seg>
            <seg type="pāda" n="d">hasantam anyaḥ prahasann upaiti</seg>
        </li>
    </ul>
    <ul id="R_5.059.017">
        <li id="R_5.059.017ab">
            <seg type="pāda" n="a">rudantam anyaḥ prarudann upaiti</seg>
            <seg type="pāda" n="b">nudantam anyaḥ praṇudann upaiti</seg>
        </li>
        <li id="R_5.059.017cd">
            <seg type="pāda" n="c">samākulaṃ tat kapisainyam āsīn</seg>
            <seg type="pāda" n="d">madhuprapānotkaṭa sattvaceṣṭam</seg>
        </li>
        <li id="R_5.059.017ef">
            <seg type="pāda" n="e">na cātra kaś cin na babhūva matto</seg>
            <seg type="pāda" n="f">na cātra kaś cin na babhūva tṛpto</seg>
        </li>
    </ul>
    <ul id="R_5.059.018">
        <li id="R_5.059.018ab">
            <seg type="pāda" n="a">tato vanaṃ tat paribhakṣyamāṇaṃ</seg>
            <seg type="pāda" n="b">drumāṃś ca vidhvaṃsitapatrapuṣpān</seg>
        </li>
        <li id="R_5.059.018cd">
            <seg type="pāda" n="c">samīkṣya kopād dadhivaktranāmā</seg>
            <seg type="pāda" n="d">nivārayām āsa kapiḥ kapīṃs tān</seg>
        </li>
    </ul>
    <ul id="R_5.059.019">
        <li id="R_5.059.019ab">
            <seg type="pāda" n="a">sa taiḥ pravṛddhaiḥ paribhartsyamāno</seg>
            <seg type="pāda" n="b">vanasya goptā harivīravṛddhaḥ</seg>
        </li>
        <li id="R_5.059.019cd">
            <seg type="pāda" n="c">cakāra bhūyo matim ugratejā</seg>
            <seg type="pāda" n="d">vanasya rakṣāṃ prati vānarebhyaḥ</seg>
        </li>
    </ul>
    <ul id="R_5.059.020">
        <li id="R_5.059.020ab">
            <seg type="pāda" n="a">uvāca kāṃś cit paruṣāṇi dhṛṣṭam</seg>
            <seg type="pāda" n="b">asaktam anyāṃś ca talair jaghāna</seg>
        </li>
        <li id="R_5.059.020cd">
            <seg type="pāda" n="c">sametya kaiś cit kalahaṃ cakāra</seg>
            <seg type="pāda" n="d">tathaiva sāmnopajagāma kāṃś cit</seg>
        </li>
    </ul>
    <ul id="R_5.059.021">
        <li id="R_5.059.021ab">
            <seg type="pāda" n="a">sa tair madāc cāprativārya vegair</seg>
            <seg type="pāda" n="b">balāc ca tenāprativāryamāṇaiḥ</seg>
        </li>
        <li id="R_5.059.021cd">
            <seg type="pāda" n="c">pradharṣitas tyaktabhayaiḥ sametya</seg>
            <seg type="pāda" n="d">prakṛṣyate cāpy anavekṣya doṣam</seg>
        </li>
    </ul>
    <ul id="R_5.059.022">
        <li id="R_5.059.022ab">
            <seg type="pāda" n="a">nakhais tudanto daśanair daśantas</seg>
            <seg type="pāda" n="b">talaiś ca pādaiś ca samāpnuvantaḥ</seg>
        </li>
        <li id="R_5.059.022cd">
            <seg type="pāda" n="c">madāt kapiṃ taṃ kapayaḥ samagrā</seg>
            <seg type="pāda" n="d">mahāvanaṃ nirviṣayaṃ ca cakruḥ</seg>
        </li>
    </ul>
    <ul id="R_5.060.001">
        <li id="R_5.060.001ab">tān uvāca hariśreṣṭho hanūmān vānararṣabhaḥ</li>
        <li id="R_5.060.001cd">avyagramanaso yūyaṃ madhu sevata vānarāḥ</li>
    </ul>
    <ul id="R_5.060.002">
        <li id="R_5.060.002ab">śrutvā hanumato vākyaṃ harīṇāṃ pravaro 'ṅgadaḥ</li>
        <li id="R_5.060.002cd">pratyuvāca prasannātmā pibantu harayo madhu</li>
    </ul>
    <ul id="R_5.060.003">
        <li id="R_5.060.003ab">avaśyaṃ kṛtakāryasya vākyaṃ hanumato mayā</li>
        <li id="R_5.060.003cd">akāryam api kartavyaṃ kim aṅga punar īdṛśam</li>
    </ul>
    <ul id="R_5.060.004">
        <li id="R_5.060.004ab">andagasya mukhāc chrutvā vacanaṃ vānararṣabhāḥ</li>
        <li id="R_5.060.004cd">sādhu sādhv iti saṃhṛṣṭā vānarāḥ pratyapūjayan</li>
    </ul>
    <ul id="R_5.060.005">
        <li id="R_5.060.005ab">pūjayitvāṅgadaṃ sarve vānarā vānararṣabham</li>
        <li id="R_5.060.005cd">jagmur madhuvanaṃ yatra nadīvega iva drutam</li>
    </ul>
    <ul id="R_5.060.006">
        <li id="R_5.060.006ab">te prahṛṣṭā madhuvanaṃ pālān ākramya vīryataḥ</li>
        <li id="R_5.060.006cd">atisargāc ca paṭavo dṛṣṭvā śrutvā ca maithilīm</li>
    </ul>
    <ul id="R_5.060.007">
        <li id="R_5.060.007ab">utpatya ca tataḥ sarve vanapālān samāgatāḥ</li>
        <li id="R_5.060.007cd">tāḍayanti sma śataśaḥ saktān madhuvane tadā</li>
    </ul>
    <ul id="R_5.060.008">
        <li id="R_5.060.008ab">madhūni droṇamātrāṇi bahubhiḥ parigṛhya te</li>
        <li id="R_5.060.008cd">ghnanti sma sahitāḥ sarve bhakṣayanti tathāpare</li>
    </ul>
    <ul id="R_5.060.009">
        <li id="R_5.060.009ab">ke cit pītvāpavidhyanti madhūni madhupiṅgalāḥ</li>
        <li id="R_5.060.009cd">madhūcciṣṭena ke cic ca jaghnur anyonyam utkaṭāḥ</li>
    </ul>
    <ul id="R_5.060.010">
        <li id="R_5.060.010ab">apare vṛkṣamūleṣu śākhāṃ gṛhya vyavasthitaḥ</li>
        <li id="R_5.060.010cd">atyarthaṃ ca madaglānāḥ parṇāny āstīrya śerate</li>
    </ul>
    <ul id="R_5.060.011">
        <li id="R_5.060.011ab">unmattabhūtāḥ plavagā madhumattāś ca hṛṣṭavat</li>
        <li id="R_5.060.011cd">kṣipanty api tathānyonyaṃ skhalanty api tathāpare</li>
    </ul>
    <ul id="R_5.060.012">
        <li id="R_5.060.012ab">ke cit kṣveḍān prakurvanti ke cit kūjanti hṛṣṭavat</li>
        <li id="R_5.060.012cd">harayo madhunā mattāḥ ke cit suptā mahītale</li>
    </ul>
    <ul id="R_5.060.013">
        <li id="R_5.060.013ab">ye 'py atra madhupālāḥ syuḥ preṣyā dadhimukhasya tu</li>
        <li id="R_5.060.013cd">te 'pi tair vānarair bhīmaiḥ pratiṣiddhā diśo gatāḥ</li>
    </ul>
    <ul id="R_5.060.014">
        <li id="R_5.060.014ab">jānubhiś ca prakṛṣṭāś ca devamārgaṃ ca darśitāḥ</li>
        <li id="R_5.060.014cd">abruvan paramodvignā gatvā dadhimukhaṃ vacaḥ</li>
    </ul>
    <ul id="R_5.060.015">
        <li id="R_5.060.015ab">hanūmatā dattavarair hataṃ madhuvanaṃ balāt</li>
        <li id="R_5.060.015cd">vayaṃ ca jānubhiḥ kṛṣṭā devamārgaṃ ca darśitāḥ</li>
    </ul>
    <ul id="R_5.060.016">
        <li id="R_5.060.016ab">tato dadhimukhaḥ kruddho vanapas tatra vānaraḥ</li>
        <li id="R_5.060.016cd">hataṃ madhuvanaṃ śrutvā sāntvayām āsa tān harīn</li>
    </ul>
    <ul id="R_5.060.017">
        <li id="R_5.060.017ab">etāgacchata gacchāmo vānarān atidarpitān</li>
        <li id="R_5.060.017cd">balenāvārayiṣyāmo madhu bhakṣayato vayam</li>
    </ul>
    <ul id="R_5.060.018">
        <li id="R_5.060.018ab">śrutvā dadhimukhasyedaṃ vacanaṃ vānararṣabhāḥ</li>
        <li id="R_5.060.018cd">punar vīrā madhuvanaṃ tenaiva sahitā yayuḥ</li>
    </ul>
    <ul id="R_5.060.019">
        <li id="R_5.060.019ab">madhye caiṣāṃ dadhimukhaḥ pragṛhya sumahātarum</li>
        <li id="R_5.060.019cd">samabhyadhāvad vegenā te ca sarve plavaṃgamāḥ</li>
    </ul>
    <ul id="R_5.060.020">
        <li id="R_5.060.020ab">te śilāḥ pādapāṃś cāpi pāṣāṇāṃś cāpi vānarāḥ</li>
        <li id="R_5.060.020cd">gṛhītvābhyāgaman kruddhā yatra te kapikuñjarāḥ</li>
    </ul>
    <ul id="R_5.060.021">
        <li id="R_5.060.021ab">te svāmivacanaṃ vīrā hṛdayeṣv avasajya tat</li>
        <li id="R_5.060.021cd">tvarayā hy abhyadhāvanta sālatālaśilāyudhāḥ</li>
    </ul>
    <ul id="R_5.060.022">
        <li id="R_5.060.022ab">vṛkṣasthāṃś ca talasthāṃś ca vānarān baladarpitān</li>
        <li id="R_5.060.022cd">abhyakrāmanta te vīrāḥ pālās tatra sahasraśaḥ</li>
    </ul>
    <ul id="R_5.060.023">
        <li id="R_5.060.023ab">atha dṛṣṭvā dadhimukhaṃ kruddhaṃ vānarapuṃgavāḥ</li>
        <li id="R_5.060.023cd">abhyadhāvanta vegena hanūmatpramukhās tadā</li>
    </ul>
    <ul id="R_5.060.024">
        <li id="R_5.060.024ab">taṃ savṛkṣaṃ mahābāhum āpatantaṃ mahābalam</li>
        <li id="R_5.060.024cd">āryakaṃ prāharat tatra bāhubhyāṃ kupito 'ṅgadaḥ</li>
    </ul>
    <ul id="R_5.060.025">
        <li id="R_5.060.025ab">madāndhaś a na vedainam āryako 'yaṃ mameti saḥ</li>
        <li id="R_5.060.025cd">athainaṃ niṣpipeṣāśu vegavad vasudhātale</li>
    </ul>
    <ul id="R_5.060.026">
        <li id="R_5.060.026ab">sa bhagnabāhur vimukho vihvalaḥ śoṇitokṣitaḥ</li>
        <li id="R_5.060.026cd">mumoha sahasā vīro muhūrtaṃ kapikuñjaraḥ</li>
    </ul>
    <ul id="R_5.060.027">
        <li id="R_5.060.027ab">sa kathaṃ cid vimuktas tair vānarair vānararṣabhaḥ</li>
        <li id="R_5.060.027cd">uvācaikāntam āgamya bhṛtyāṃs tān samupāgatān</li>
    </ul>
    <ul id="R_5.060.028">
        <li id="R_5.060.028ab">ete tiṣṭhantu gacchāmo bhartā no yatra vānaraḥ</li>
        <li id="R_5.060.028cd">sugrīvo vipulagrīvaḥ saha rāmeṇa tiṣṭhati</li>
    </ul>
    <ul id="R_5.060.029">
        <li id="R_5.060.029ab">sarvaṃ caivāṅgade doṣaṃ śrāvayiṣyāmi pārthiva</li>
        <li id="R_5.060.029cd">amarṣī vacanaṃ śrutvā ghātayiṣyati vānarān</li>
    </ul>
    <ul id="R_5.060.030">
        <li id="R_5.060.030ab">iṣṭaṃ madhuvanaṃ hy etat sugrīvasya mahātmanaḥ</li>
        <li id="R_5.060.030cd">pitṛpaitāmahaṃ divyaṃ devair api durāsadam</li>
    </ul>
    <ul id="R_5.060.031">
        <li id="R_5.060.031ab">sa vānarān imān sarvān madhulubdhān gatāyuṣaḥ</li>
        <li id="R_5.060.031cd">ghātayiṣyati daṇḍena sugrīvaḥ sasuhṛjjanān</li>
    </ul>
    <ul id="R_5.060.032">
        <li id="R_5.060.032ab">vadhyā hy ete durātmāno nṛpājñā paribhāvinaḥ</li>
        <li id="R_5.060.032cd">amarṣaprabhavo roṣaḥ saphalo no bhaviṣyati</li>
    </ul>
    <ul id="R_5.060.033">
        <li id="R_5.060.033ab">evam uktvā dadhimukho vanapālān mahābalaḥ</li>
        <li id="R_5.060.033cd">jagāma sahasotpatya vanapālaiḥ samanvitaḥ</li>
    </ul>
    <ul id="R_5.060.034">
        <li id="R_5.060.034ab">nimeṣāntaramātreṇa sa hi prāpto vanālayaḥ</li>
        <li id="R_5.060.034cd">sahasrāṃśusuto dhīmān sugrīvo yatra vānaraḥ</li>
    </ul>
    <ul id="R_5.060.035">
        <li id="R_5.060.035ab">rāmaṃ ca lakṣmaṇaṃ caiva dṛṣṭvā sugrīvam eva ca</li>
        <li id="R_5.060.035cd">samapratiṣṭhāṃ jagatīm ākāśān nipapāta ha</li>
    </ul>
    <ul id="R_5.060.036">
        <li id="R_5.060.036ab">sa nipatya mahāvīryaḥ sarvais taiḥ parivāritaḥ</li>
        <li id="R_5.060.036cd">harir dadhimukhaḥ pālaiḥ pālānāṃ parameśvaraḥ</li>
    </ul>
    <ul id="R_5.060.037">
        <li id="R_5.060.037ab">sa dīnavadano bhūtvā kṛtvā śirasi cāñjalim</li>
        <li id="R_5.060.037cd">sugrīvasya śubhau mūrdhnā caraṇau pratyapīḍayat</li>
    </ul>
    <ul id="R_5.061.001">
        <li id="R_5.061.001ab">tato mūrdhnā nipatitaṃ vānaraṃ vānararṣabhaḥ</li>
        <li id="R_5.061.001cd">dṛṣṭvaivodvignahṛdayo vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_5.061.002">
        <li id="R_5.061.002ab">uttiṣṭhottiṣṭha kasmāt tvaṃ pādayoḥ patito mama</li>
        <li id="R_5.061.002cd">abhayaṃ te bhaved vīra satyam evābhidhīyatām</li>
    </ul>
    <ul id="R_5.061.003">
        <li id="R_5.061.003ab">sa tu viśvāsitas tena sugrīveṇa mahātmanā</li>
        <li id="R_5.061.003cd">utthāya ca mahāprājño vākyaṃ dadhimukho 'bravīt</li>
    </ul>
    <ul id="R_5.061.004">
        <li id="R_5.061.004ab">naivarkṣarajasā rājan na tvayā nāpi vālinā</li>
        <li id="R_5.061.004cd">vanaṃ nisṛṣṭapūrvaṃ hi bhakṣitaṃ tat tu vānaraiḥ</li>
    </ul>
    <ul id="R_5.061.005">
        <li id="R_5.061.005ab">ebhiḥ pradharṣitāś caiva vāritā vanarakṣibhiḥ</li>
        <li id="R_5.061.005cd">madhūny acintayitvemān bhakṣayanti pibanti ca</li>
    </ul>
    <ul id="R_5.061.006">
        <li id="R_5.061.006ab">śiṣṭam atrāpavidhyanti bhakṣayanti tathāpare</li>
        <li id="R_5.061.006cd">nivāryamāṇās te sarve bhruvau vai darśayanti hi</li>
    </ul>
    <ul id="R_5.061.007">
        <li id="R_5.061.007ab">ime hi saṃrabdhatarās tathā taiḥ saṃpradharṣitāḥ</li>
        <li id="R_5.061.007cd">vārayanto vanāt tasmāt kruddhair vānarapuṃgavaiḥ</li>
    </ul>
    <ul id="R_5.061.008">
        <li id="R_5.061.008ab">tatas tair bahubhir vīrair vānarair vānararṣabhāḥ</li>
        <li id="R_5.061.008cd">saṃraktanayanaiḥ krodhād dharayaḥ saṃpracālitāḥ</li>
    </ul>
    <ul id="R_5.061.009">
        <li id="R_5.061.009ab">pāṇibhir nihatāḥ ke cit ke cij jānubhir āhatāḥ</li>
        <li id="R_5.061.009cd">prakṛṣṭāś ca yathākāmaṃ devamārgaṃ ca darśitāḥ</li>
    </ul>
    <ul id="R_5.061.010">
        <li id="R_5.061.010ab">evam ete hatāḥ śūrās tvayi tiṣṭhati bhartari</li>
        <li id="R_5.061.010cd">kṛtsnaṃ madhuvanaṃ caiva prakāmaṃ taiḥ prabhakṣyate</li>
    </ul>
    <ul id="R_5.061.011">
        <li id="R_5.061.011ab">evaṃ vijñāpyamānaṃ tu sugrīvaṃ vānararṣabham</li>
        <li id="R_5.061.011cd">apṛcchat taṃ mahāprājño lakṣmaṇaḥ paravīrahā</li>
    </ul>
    <ul id="R_5.061.012">
        <li id="R_5.061.012ab">kim ayaṃ vānaro rājan vanapaḥ pratyupasthitaḥ</li>
        <li id="R_5.061.012cd">kaṃ cārtham abhinirdiśya duḥkhito vākyam abravīt</li>
    </ul>
    <ul id="R_5.061.013">
        <li id="R_5.061.013ab">evam uktas tu sugrīvo lakṣmaṇena mahātmanā</li>
        <li id="R_5.061.013cd">lakṣmaṇaṃ pratyuvācedaṃ vākyaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_5.061.014">
        <li id="R_5.061.014ab">ārya lakṣmaṇa saṃprāha vīro dadhimukhaḥ kapiḥ</li>
        <li id="R_5.061.014cd">aṅgadapramukhair vīrair bhakṣitaṃ madhuvānaraiḥ</li>
    </ul>
    <ul id="R_5.061.015">
        <li id="R_5.061.015ab">naiṣām akṛtakṛtyānām īdṛśaḥ syād upakramaḥ</li>
        <li id="R_5.061.015cd">vanaṃ yathābhipannaṃ taiḥ sādhitaṃ karma vānaraiḥ</li>
    </ul>
    <ul id="R_5.061.016">
        <li id="R_5.061.016ab">dṛṣṭā devī na saṃdeho na cānyena hanūmatā</li>
        <li id="R_5.061.016cd">na hy anyaḥ sādhane hetuḥ karmaṇo 'sya hanūmataḥ</li>
    </ul>
    <ul id="R_5.061.017">
        <li id="R_5.061.017ab">kāryasiddhir hanumati matiś ca haripuṃgava</li>
        <li id="R_5.061.017cd">vyavasāyaś ca vīryaṃ ca śrutaṃ cāpi pratiṣṭhitam</li>
    </ul>
    <ul id="R_5.061.018">
        <li id="R_5.061.018ab">jāmbavān yatra netā syād aṅgadasya baleśvaraḥ</li>
        <li id="R_5.061.018cd">hanūmāṃś cāpy adhiṣṭhātā na tasya gatir anyathā</li>
    </ul>
    <ul id="R_5.061.019">
        <li id="R_5.061.019ab">aṅgadapramukhair vīrair hataṃ madhuvanaṃ kila</li>
        <li id="R_5.061.019cd">vicintya dakṣiṇām āśām āgatair haripuṃgavaiḥ</li>
    </ul>
    <ul id="R_5.061.020">
        <li id="R_5.061.020ab">āgataiś ca praviṣṭaṃ tad yathā madhuvanaṃ hi taiḥ</li>
        <li id="R_5.061.020cd">dharṣitaṃ ca vanaṃ kṛtsnam upayuktaṃ ca vānaraiḥ</li>
        <li id="R_5.061.020ef">vāritāḥ sahitāḥ pālās tathā jānubhir āhatāḥ</li>
    </ul>
    <ul id="R_5.061.021">
        <li id="R_5.061.021ab">etadartham ayaṃ prāpto vaktuṃ madhuravāg iha</li>
        <li id="R_5.061.021cd">nāmnā dadhimukho nāma hariḥ prakhyātavikramaḥ</li>
    </ul>
    <ul id="R_5.061.022">
        <li id="R_5.061.022ab">dṛṣṭā sītā mahābāho saumitre paśya tattvataḥ</li>
        <li id="R_5.061.022cd">abhigamya yathā sarve pibanti madhu vānarāḥ</li>
    </ul>
    <ul id="R_5.061.023">
        <li id="R_5.061.023ab">na cāpy adṛṣṭvā vaidehīṃ viśrutāḥ puruṣarṣabha</li>
        <li id="R_5.061.023cd">vanaṃ dātta varaṃ divyaṃ dharṣayeyur vanaukasaḥ</li>
    </ul>
    <ul id="R_5.061.024">
        <li id="R_5.061.024ab">tataḥ prahṛṣṭo dharmātmā lakṣmaṇaḥ saharāghavaḥ</li>
        <li id="R_5.061.024cd">śrutvā karṇasukhāṃ vāṇīṃ sugrīvavadanāc cyutām</li>
    </ul>
    <ul id="R_5.061.025">
        <li id="R_5.061.025ab">prāhṛṣyata bhṛśaṃ rāmo lakṣmaṇaś ca mahāyaśāḥ</li>
        <li id="R_5.061.025cd">śrutvā dadhimukhasyedaṃ sugrīvas tu prahṛṣya ca</li>
        <li id="R_5.061.025ef">vanapālaṃ punar vākyaṃ sugrīvaḥ pratyabhāṣata</li>
    </ul>
    <ul id="R_5.061.026">
        <li id="R_5.061.026ab">prīto 'smi saumya yad bhuktaṃ vanaṃ taiḥ kṛtakarmabhiḥ</li>
        <li id="R_5.061.026cd">marṣitaṃ marṣaṇīyaṃ ca ceṣṭitaṃ kṛtakarmaṇām</li>
    </ul>
    <ul id="R_5.061.027">
        <li id="R_5.061.027ab">
            <seg type="pāda" n="a">icchāmi śīghraṃ hanumatpradhānān</seg>
            <seg type="pāda" n="b">śākhāmṛgāṃs tān mṛgarājadarpān</seg>
        </li>
        <li id="R_5.061.027cd">
            <seg type="pāda" n="c">draṣṭuṃ kṛtārthān saha rāghavābhyāṃ</seg>
            <seg type="pāda" n="d">śrotuṃ ca sītādhigame prayatnam</seg>
        </li>
    </ul>
    <ul id="R_5.062.001">
        <li id="R_5.062.001ab">sugrīveṇaivam uktas tu hṛṣṭo dadhimukhaḥ kapiḥ</li>
        <li id="R_5.062.001cd">rāghavaṃ lakṣmaṇaṃ caiva sugrīvaṃ cābhyavādayat</li>
    </ul>
    <ul id="R_5.062.002">
        <li id="R_5.062.002ab">sa praṇamya ca sugrīvaṃ rāghavau ca mahābalau</li>
        <li id="R_5.062.002cd">vānaraiḥ sahitaiḥ śūrair divam evotpapāta ha</li>
    </ul>
    <ul id="R_5.062.003">
        <li id="R_5.062.003ab">sa yathaivāgataḥ pūrvaṃ tathaiva tvarito gataḥ</li>
        <li id="R_5.062.003cd">nipatya gaganād bhūmau tad vanaṃ praviveśa ha</li>
    </ul>
    <ul id="R_5.062.004">
        <li id="R_5.062.004ab">sa praviṣṭo madhuvanaṃ dadarśa hariyūthapān</li>
        <li id="R_5.062.004cd">vimadān uddhatān sarvān mehamānān madhūdakam</li>
    </ul>
    <ul id="R_5.062.005">
        <li id="R_5.062.005ab">sa tān upāgamad vīro baddhvā karapuṭāñjalim</li>
        <li id="R_5.062.005cd">uvāca vacanaṃ ślakṣṇam idaṃ hṛṣṭavad aṅgadam</li>
    </ul>
    <ul id="R_5.062.006">
        <li id="R_5.062.006ab">saumya roṣo na kartavyo yad ebhir abhivāritaḥ</li>
        <li id="R_5.062.006cd">ajñānād rakṣibhiḥ krodhād bhavantaḥ pratiṣedhitāḥ</li>
    </ul>
    <ul id="R_5.062.007">
        <li id="R_5.062.007ab">yuvarājas tvam īśaś ca vanasyāsya mahābala</li>
        <li id="R_5.062.007cd">maurkhyāt pūrvaṃ kṛto doṣas tad bhavān kṣantum arhati</li>
    </ul>
    <ul id="R_5.062.008">
        <li id="R_5.062.008ab">yathaiva hi pitā te 'bhūt pūrvaṃ harigaṇeśvaraḥ</li>
        <li id="R_5.062.008cd">tathā tvam api sugrīvo nānyas tu harisattama</li>
    </ul>
    <ul id="R_5.062.009">
        <li id="R_5.062.009ab">ākhyātaṃ hi mayā gatvā pitṛvyasya tavānagha</li>
        <li id="R_5.062.009cd">ihopayānaṃ sarveṣām eteṣāṃ vanacāriṇām</li>
    </ul>
    <ul id="R_5.062.010">
        <li id="R_5.062.010ab">sa tvadāgamanaṃ śrutvā sahaibhir hariyūthapaiḥ</li>
        <li id="R_5.062.010cd">prahṛṣṭo na tu ruṣṭo 'sau vanaṃ śrutvā pradharṣitam</li>
    </ul>
    <ul id="R_5.062.011">
        <li id="R_5.062.011ab">prahṛṣṭo māṃ pitṛvyas te sugrīvo vānareśvaraḥ</li>
        <li id="R_5.062.011cd">śīghraṃ preṣaya sarvāṃs tān iti hovāca pārthivaḥ</li>
    </ul>
    <ul id="R_5.062.012">
        <li id="R_5.062.012ab">śrutvā dadhimukhasyaitad vacanaṃ ślakṣṇam aṅgadaḥ</li>
        <li id="R_5.062.012cd">abravīt tān hariśreṣṭho vākyaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_5.062.013">
        <li id="R_5.062.013ab">śaṅke śruto 'yaṃ vṛttānto rāmeṇa hariyūthapāḥ</li>
        <li id="R_5.062.013cd">tat kṣamaṃ neha naḥ sthātuṃ kṛte kārye paraṃtapāḥ</li>
    </ul>
    <ul id="R_5.062.014">
        <li id="R_5.062.014ab">pītvā madhu yathākāmaṃ viśrāntā vanacāriṇaḥ</li>
        <li id="R_5.062.014cd">kiṃ śeṣaṃ gamanaṃ tatra sugrīvo yatra me guruḥ</li>
    </ul>
    <ul id="R_5.062.015">
        <li id="R_5.062.015ab">sarve yathā māṃ vakṣyanti sametya hariyūthapāḥ</li>
        <li id="R_5.062.015cd">tathāsmi kartā kartavye bhavadbhiḥ paravān aham</li>
    </ul>
    <ul id="R_5.062.016">
        <li id="R_5.062.016ab">nājñāpayitum īśo 'haṃ yuvarājo 'smi yady api</li>
        <li id="R_5.062.016cd">ayuktaṃ kṛtakarmāṇo yūyaṃ dharṣayituṃ mayā</li>
    </ul>
    <ul id="R_5.062.017">
        <li id="R_5.062.017ab">bruvataś cāṅgadaś caivaṃ śrutvā vacanam avyayam</li>
        <li id="R_5.062.017cd">prahṛṣṭamanaso vākyam idam ūcur vanaukasaḥ</li>
    </ul>
    <ul id="R_5.062.018">
        <li id="R_5.062.018ab">evaṃ vakṣyati ko rājan prabhuḥ san vānararṣabha</li>
        <li id="R_5.062.018cd">aiśvaryamadamatto hi sarvo 'ham iti manyate</li>
    </ul>
    <ul id="R_5.062.019">
        <li id="R_5.062.019ab">tava cedaṃ susadṛśaṃ vākyaṃ nānyasya kasya cit</li>
        <li id="R_5.062.019cd">saṃnatir hi tavākhyāti bhaviṣyac chubhabhāgyatām</li>
    </ul>
    <ul id="R_5.062.020">
        <li id="R_5.062.020ab">sarve vayam api prāptās tatra gantuṃ kṛtakṣaṇāḥ</li>
        <li id="R_5.062.020cd">sa yatra harivīrāṇāṃ sugrīvaḥ patir avyayaḥ</li>
    </ul>
    <ul id="R_5.062.021">
        <li id="R_5.062.021ab">tvayā hy anuktair haribhir naiva śakyaṃ padāt padam</li>
        <li id="R_5.062.021cd">kva cid gantuṃ hariśreṣṭha brūmaḥ satyam idaṃ tu te</li>
    </ul>
    <ul id="R_5.062.022">
        <li id="R_5.062.022ab">evaṃ tu vadatāṃ teṣām aṅgadaḥ pratyabhāṣata</li>
        <li id="R_5.062.022cd">bāḍhaṃ gacchāma ity uktvā utpapāta mahītalāt</li>
    </ul>
    <ul id="R_5.062.023">
        <li id="R_5.062.023ab">utpatantam anūtpetuḥ sarve te hariyūthapāḥ</li>
        <li id="R_5.062.023cd">kṛtvākāśaṃ nirākāśaṃ yajñotkṣiptā ivānalāḥ</li>
    </ul>
    <ul id="R_5.062.024">
        <li id="R_5.062.024ab">te 'mbaraṃ sahasotpatya vegavantaḥ plavaṃgamāḥ</li>
        <li id="R_5.062.024cd">vinadanto mahānādaṃ ghanā vāteritā yathā</li>
    </ul>
    <ul id="R_5.062.025">
        <li id="R_5.062.025ab">aṅgade hy ananuprāpte sugrīvo vānarādhipaḥ</li>
        <li id="R_5.062.025cd">uvāca śokopahataṃ rāmaṃ kamalalocanam</li>
    </ul>
    <ul id="R_5.062.026">
        <li id="R_5.062.026ab">samāśvasihi bhadraṃ te dṛṣṭā devī na saṃśayaḥ</li>
        <li id="R_5.062.026cd">nāgantum iha śakyaṃ tair atīte samaye hi naḥ</li>
    </ul>
    <ul id="R_5.062.027">
        <li id="R_5.062.027ab">na matsakāśam āgacchet kṛtye hi vinipātite</li>
        <li id="R_5.062.027cd">yuvarājo mahābāhuḥ plavatāṃ pravaro 'ṅgadaḥ</li>
    </ul>
    <ul id="R_5.062.028">
        <li id="R_5.062.028ab">yady apy akṛtakṛtyānām īdṛśaḥ syād upakramaḥ</li>
        <li id="R_5.062.028cd">bhavet tu dīnavadano bhrāntaviplutamānasaḥ</li>
    </ul>
    <ul id="R_5.062.029">
        <li id="R_5.062.029ab">pitṛpaitāmahaṃ caitat pūrvakair abhirakṣitam</li>
        <li id="R_5.062.029cd">na me madhuvanaṃ hanyād ahṛṣṭaḥ plavageśvaraḥ</li>
    </ul>
    <ul id="R_5.062.030">
        <li id="R_5.062.030ab">kausalyā suprajā rāma samāśvasihi suvrata</li>
        <li id="R_5.062.030cd">dṛṣṭā devī na saṃdeho na cānyena hanūmatā</li>
        <li id="R_5.062.030ef">na hy anyaḥ karmaṇo hetuḥ sādhane tadvidho bhavet</li>
    </ul>
    <ul id="R_5.062.031">
        <li id="R_5.062.031ab">hanūmati hi siddhiś ca matiś ca matisattama</li>
        <li id="R_5.062.031cd">vyavasāyaś ca vīryaṃ ca sūrye teja iva dhruvam</li>
    </ul>
    <ul id="R_5.062.032">
        <li id="R_5.062.032ab">jāmbavān yatra netā syād aṅgadaś ca baleśvaraḥ</li>
        <li id="R_5.062.032cd">hanūmāṃś cāpy adhiṣṭhātā na tasya gatir anyathā</li>
    </ul>
    <ul id="R_5.062.033">
        <li id="R_5.062.033ab">mā bhūś cintā samāyuktaḥ saṃpraty amitavikrama</li>
    </ul>
    <ul id="R_5.062.034">
        <li id="R_5.062.034ab">tataḥ kila kilā śabdaṃ śuśrāvāsannam ambare</li>
        <li id="R_5.062.034cd">hanūmat karmadṛptānāṃ nardatāṃ kānanaukasām</li>
        <li id="R_5.062.034ef">kiṣkindhām upayātānāṃ siddhiṃ kathayatām iva</li>
    </ul>
    <ul id="R_5.062.035">
        <li id="R_5.062.035ab">tataḥ śrutvā ninādaṃ taṃ kapīnāṃ kapisattamaḥ</li>
        <li id="R_5.062.035cd">āyatāñcitalāṅgūlaḥ so 'bhavad dhṛṣṭamānasaḥ</li>
    </ul>
    <ul id="R_5.062.036">
        <li id="R_5.062.036ab">ājagmus te 'pi harayo rāmadarśanakāṅkṣiṇaḥ</li>
        <li id="R_5.062.036cd">aṅgadaṃ purataḥ kṛtvā hanūmantaṃ ca vānaram</li>
    </ul>
    <ul id="R_5.062.037">
        <li id="R_5.062.037ab">te 'ṅgadapramukhā vīrāḥ prahṛṣṭāś ca mudānvitāḥ</li>
        <li id="R_5.062.037cd">nipetur harirājasya samīpe rāghavasya ca</li>
    </ul>
    <ul id="R_5.062.038">
        <li id="R_5.062.038ab">hanūmāṃś ca mahābahuḥ praṇamya śirasā tataḥ</li>
        <li id="R_5.062.038cd">niyatām akṣatāṃ devīṃ rāghavāya nyavedayat</li>
    </ul>
    <ul id="R_5.062.039">
        <li id="R_5.062.039ab">niścitārthaṃ tatas tasmin sugrīvaṃ pavanātmaje</li>
        <li id="R_5.062.039cd">lakṣmaṇaḥ prītimān prītaṃ bahumānād avaikṣata</li>
    </ul>
    <ul id="R_5.062.040">
        <li id="R_5.062.040ab">prītyā ca ramamāṇo 'tha rāghavaḥ paravīrahā</li>
        <li id="R_5.062.040cd">bahu mānena mahatā hanūmantam avaikṣata</li>
    </ul>
    <ul id="R_5.063.001">
        <li id="R_5.063.001ab">tataḥ prasravaṇaṃ śailaṃ te gatvā citrakānanam</li>
        <li id="R_5.063.001cd">praṇamya śirasā rāmaṃ lakṣmaṇaṃ ca mahābalam</li>
    </ul>
    <ul id="R_5.063.002">
        <li id="R_5.063.002ab">yuvarājaṃ puraskṛtya sugrīvam abhivādya ca</li>
        <li id="R_5.063.002cd">pravṛttam atha sītāyāḥ pravaktum upacakramuḥ</li>
    </ul>
    <ul id="R_5.063.003">
        <li id="R_5.063.003ab">rāvaṇāntaḥpure rodhaṃ rākṣasībhiś ca tarjanam</li>
        <li id="R_5.063.003cd">rāme samanurāgaṃ ca yaś cāpi samayaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_5.063.004">
        <li id="R_5.063.004ab">etad ākhyānti te sarve harayo rāma saṃnidhau</li>
        <li id="R_5.063.004cd">vaidehīm akṣatāṃ śrutvā rāmas tūttaram abravīt</li>
    </ul>
    <ul id="R_5.063.005">
        <li id="R_5.063.005ab">kva sītā vartate devī kathaṃ ca mayi vartate</li>
        <li id="R_5.063.005cd">etan me sarvam ākhyāta vaidehīṃ prati vānarāḥ</li>
    </ul>
    <ul id="R_5.063.006">
        <li id="R_5.063.006ab">rāmasya gaditaṃ śrutva harayo rāmasaṃnidhau</li>
        <li id="R_5.063.006cd">codayanti hanūmantaṃ sītāvṛttāntakovidam</li>
    </ul>
    <ul id="R_5.063.007">
        <li id="R_5.063.007ab">śrutvā tu vacanaṃ teṣāṃ hanūmān mārutātmajaḥ</li>
        <li id="R_5.063.007cd">uvāca vākyaṃ vākyajñaḥ sītāyā darśanaṃ yathā</li>
    </ul>
    <ul id="R_5.063.008">
        <li id="R_5.063.008ab">samudraṃ laṅghayitvāhaṃ śatayojanam āyatam</li>
        <li id="R_5.063.008cd">agacchaṃ jānakīṃ sītāṃ mārgamāṇo didṛkṣayā</li>
    </ul>
    <ul id="R_5.063.009">
        <li id="R_5.063.009ab">tatra laṅketi nagarī rāvaṇasya durātmanaḥ</li>
        <li id="R_5.063.009cd">dakṣiṇasya samudrasya tīre vasati dakṣiṇe</li>
    </ul>
    <ul id="R_5.063.010">
        <li id="R_5.063.010ab">tatra dṛṣṭā mayā sītā rāvaṇāntaḥpure satī</li>
        <li id="R_5.063.010cd">saṃnyasya tvayi jīvantī rāmā rāma manoratham</li>
    </ul>
    <ul id="R_5.063.011">
        <li id="R_5.063.011ab">dṛṣṭā me rākṣasī madhye tarjyamānā muhur muhuḥ</li>
        <li id="R_5.063.011cd">rākṣasībhir virūpābhī rakṣitā pramadāvane</li>
    </ul>
    <ul id="R_5.063.012">
        <li id="R_5.063.012ab">duḥkham āpadyate devī tavāduḥkhocitā satī</li>
        <li id="R_5.063.012cd">rāvaṇāntaḥpure ruddhvā rākṣasībhiḥ surakṣitā</li>
    </ul>
    <ul id="R_5.063.013">
        <li id="R_5.063.013ab">ekaveṇīdharā dīnā tvayi cintāparāyaṇā</li>
        <li id="R_5.063.013cd">adhaḥśayyā vivarṇāṅgī padminīva himāgame</li>
    </ul>
    <ul id="R_5.063.014">
        <li id="R_5.063.014ab">rāvaṇād vinivṛttārthā martavyakṛtaniścayā</li>
        <li id="R_5.063.014cd">devī kathaṃ cit kākutstha tvanmanā mārgitā mayā</li>
    </ul>
    <ul id="R_5.063.015">
        <li id="R_5.063.015ab">ikṣvākuvaṃśavikhyātiṃ śanaiḥ kīrtayatānagha</li>
        <li id="R_5.063.015cd">sa mayā naraśārdūla viśvāsam upapāditā</li>
    </ul>
    <ul id="R_5.063.016">
        <li id="R_5.063.016ab">tataḥ saṃbhāṣitā devī sarvam arthaṃ ca darśitā</li>
        <li id="R_5.063.016cd">rāmasugrīvasakhyaṃ ca śrutvā prītim upāgatā</li>
    </ul>
    <ul id="R_5.063.017">
        <li id="R_5.063.017ab">niyataḥ samudācāro bhaktiś cāsyās tathā tvayi</li>
        <li id="R_5.063.017cd">evaṃ mayā mahābhāgā dṛṣṭā janakanandinī</li>
        <li id="R_5.063.017ef">ugreṇa tapasā yuktā tvadbhaktyā puruṣarṣabha</li>
    </ul>
    <ul id="R_5.063.018">
        <li id="R_5.063.018ab">abhijñānaṃ ca me dattaṃ yathāvṛttaṃ tavāntike</li>
        <li id="R_5.063.018cd">citrakūṭe mahāprājña vāyasaṃ prati rāghava</li>
    </ul>
    <ul id="R_5.063.019">
        <li id="R_5.063.019ab">vijñāpyaś ca nara vyāghro rāmo vāyusuta tvayā</li>
        <li id="R_5.063.019cd">akhileneha yad dṛṣṭam iti mām āha jānakī</li>
    </ul>
    <ul id="R_5.063.020">
        <li id="R_5.063.020ab">idaṃ cāsmai pradātavyaṃ yatnāt suparirakṣitam</li>
        <li id="R_5.063.020cd">bruvatā vacanāny evaṃ sugrīvasyopaśṛṇvataḥ</li>
    </ul>
    <ul id="R_5.063.021">
        <li id="R_5.063.021ab">eṣa cūḍāmaṇiḥ śrīmān mayā te yatnarakṣitaḥ</li>
        <li id="R_5.063.021cd">manaḥśilāyās tikalas taṃ smarasveti cābravīt</li>
    </ul>
    <ul id="R_5.063.022">
        <li id="R_5.063.022ab">eṣa niryātitaḥ śrīmān mayā te vārisaṃbhavaḥ</li>
        <li id="R_5.063.022cd">etaṃ dṛṣṭvā pramodiṣye vyasane tvām ivānagha</li>
    </ul>
    <ul id="R_5.063.023">
        <li id="R_5.063.023ab">jīvitaṃ dhārayiṣyāmi māsaṃ daśarathātmaja</li>
        <li id="R_5.063.023cd">ūrdhvaṃ māsān na jīveyaṃ rakṣasāṃ vaśam āgatā</li>
    </ul>
    <ul id="R_5.063.024">
        <li id="R_5.063.024ab">iti mām abravīt sītā kṛśāṅgī dharma cāriṇī</li>
        <li id="R_5.063.024cd">rāvaṇāntaḥpure ruddhā mṛgīvotphullalocanā</li>
    </ul>
    <ul id="R_5.063.025">
        <li id="R_5.063.025ab">etad eva mayākhyātaṃ sarvaṃ rāghava yad yathā</li>
        <li id="R_5.063.025cd">sarvathā sāgarajale saṃtāraḥ pravidhīyatām</li>
    </ul>
    <ul id="R_5.063.026">
        <li id="R_5.063.026ab">
            <seg type="pāda" n="a">tau jātāśvāsau rājaputrau viditvā</seg>
            <seg type="pāda" n="b">tac cābhijñānaṃ rāghavāya pradāya</seg>
        </li>
        <li id="R_5.063.026cd">
            <seg type="pāda" n="c">devyā cākhyātaṃ sarvam evānupūrvyād</seg>
            <seg type="pāda" n="d">vācā saṃpūrṇaṃ vāyuputraḥ śaśaṃsa</seg>
        </li>
    </ul>
    <ul id="R_5.064.001">
        <li id="R_5.064.001ab">evam ukto hanumatā rāmo daśarathātmajaḥ</li>
        <li id="R_5.064.001cd">taṃ maṇiṃ hṛdaye kṛtvā praruroda salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_5.064.002">
        <li id="R_5.064.002ab">taṃ tu dṛṣṭvā maṇiśreṣṭhaṃ rāghavaḥ śokakarśitaḥ</li>
        <li id="R_5.064.002cd">netrābhyām aśrupūrṇābhyāṃ sugrīvam idam abravīt</li>
    </ul>
    <ul id="R_5.064.003">
        <li id="R_5.064.003ab">yathaiva dhenuḥ sravati snehād vatsasya vatsalā</li>
        <li id="R_5.064.003cd">tathā mamāpi hṛdayaṃ maṇiratnasya darśanāt</li>
    </ul>
    <ul id="R_5.064.004">
        <li id="R_5.064.004ab">maṇiratnam idaṃ dattaṃ vaidehyāḥ śvaśureṇa me</li>
        <li id="R_5.064.004cd">vadhūkāle yathā baddham adhikaṃ mūrdhni śobhate</li>
    </ul>
    <ul id="R_5.064.005">
        <li id="R_5.064.005ab">ayaṃ hi jalasaṃbhūto maṇiḥ pravarapūjitaḥ</li>
        <li id="R_5.064.005cd">yajñe paramatuṣṭena dattaḥ śakreṇa dhīmatā</li>
    </ul>
    <ul id="R_5.064.006">
        <li id="R_5.064.006ab">imaṃ dṛṣṭvā maṇiśreṣṭhaṃ tathā tātasya darśanam</li>
        <li id="R_5.064.006cd">adyāsmy avagataḥ saumya vaidehasya tathā vibhoḥ</li>
    </ul>
    <ul id="R_5.064.007">
        <li id="R_5.064.007ab">ayaṃ hi śobhate tasyāḥ priyāyā mūrdhni me maṇiḥ</li>
        <li id="R_5.064.007cd">adyāsya darśanenāhaṃ prāptāṃ tām iva cintaye</li>
    </ul>
    <ul id="R_5.064.008">
        <li id="R_5.064.008ab">kim āha sītā vaidehī brūhi saumya punaḥ punaḥ</li>
        <li id="R_5.064.008cd">parāsum iva toyena siñcantī vākyavāriṇā</li>
    </ul>
    <ul id="R_5.064.009">
        <li id="R_5.064.009ab">itas tu kiṃ duḥkhataraṃ yad imaṃ vārisaṃbhavam</li>
        <li id="R_5.064.009cd">maṇiṃ paśyāmi saumitre vaidehīm āgataṃ vinā</li>
    </ul>
    <ul id="R_5.064.010">
        <li id="R_5.064.010ab">ciraṃ jīvati vaidehī yadi māsaṃ dhariṣyati</li>
        <li id="R_5.064.010cd">kṣaṇaṃ saumya na jīveyaṃ vinā tām asitekṣaṇām</li>
    </ul>
    <ul id="R_5.064.011">
        <li id="R_5.064.011ab">naya mām api taṃ deśaṃ yatra dṛṣṭā mama priyā</li>
        <li id="R_5.064.011cd">na tiṣṭheyaṃ kṣaṇam api pravṛttim upalabhya ca</li>
    </ul>
    <ul id="R_5.064.012">
        <li id="R_5.064.012ab">kathaṃ sā mama suśroṇi bhīru bhīruḥ satī tadā</li>
        <li id="R_5.064.012cd">bhayāvahānāṃ ghorāṇāṃ madhye tiṣṭhati rakṣasām</li>
    </ul>
    <ul id="R_5.064.013">
        <li id="R_5.064.013ab">śāradas timironmukho nūnaṃ candra ivāmbudaiḥ</li>
        <li id="R_5.064.013cd">āvṛtaṃ vadanaṃ tasyā na virājati rākṣasaiḥ</li>
    </ul>
    <ul id="R_5.064.014">
        <li id="R_5.064.014ab">kim āha sītā hanumaṃs tattvataḥ kathayasva me</li>
        <li id="R_5.064.014cd">etena khalu jīviṣye bheṣajenāturo yathā</li>
    </ul>
    <ul id="R_5.064.015">
        <li id="R_5.064.015ab">madhurā madhurālāpā kim āha mama bhāminī</li>
        <li id="R_5.064.015cd">madvihīnā varārohā hanuman kathayasva me</li>
        <li id="R_5.064.015ef">duḥkhād duḥkhataraṃ prāpya kathaṃ jīvati jānakī</li>
    </ul>
    <ul id="R_5.065.001">
        <li id="R_5.065.001ab">evam uktas tu hanumān rāghaveṇa mahātmanā</li>
        <li id="R_5.065.001cd">sītāyā bhāṣitaṃ sarvaṃ nyavedayata rāghave</li>
    </ul>
    <ul id="R_5.065.002">
        <li id="R_5.065.002ab">idam uktavatī devī jānakī puruṣarṣabha</li>
        <li id="R_5.065.002cd">pūrvavṛttam abhijñānaṃ citrakūṭe yathā tatham</li>
    </ul>
    <ul id="R_5.065.003">
        <li id="R_5.065.003ab">sukhasuptā tvayā sārdhaṃ jānakī pūrvam utthitā</li>
        <li id="R_5.065.003cd">vāyasaḥ sahasotpatya virarāda stanāntare</li>
    </ul>
    <ul id="R_5.065.004">
        <li id="R_5.065.004ab">paryāyeṇa ca suptas tvaṃ devyaṅke bharatāgraja</li>
        <li id="R_5.065.004cd">punaś ca kila pakṣī sa devyā janayati vyathām</li>
    </ul>
    <ul id="R_5.065.005">
        <li id="R_5.065.005ab">tataḥ punar upāgamya virarāda bhṛśaṃ kila</li>
        <li id="R_5.065.005cd">tatas tvaṃ bodhitas tasyāḥ śoṇitena samukṣitaḥ</li>
    </ul>
    <ul id="R_5.065.006">
        <li id="R_5.065.006ab">vāyasena ca tenaiva satataṃ bādhyamānayā</li>
        <li id="R_5.065.006cd">bodhitaḥ kila devyās tvaṃ sukhasuptaḥ paraṃtapa</li>
    </ul>
    <ul id="R_5.065.007">
        <li id="R_5.065.007ab">tāṃ tu dṛṣṭvā mahābāho rāditāṃ ca stanāntare</li>
        <li id="R_5.065.007cd">āśīviṣa iva kruddho niḥśvasann abhyabhāṣathāḥ</li>
    </ul>
    <ul id="R_5.065.008">
        <li id="R_5.065.008ab">nakhāgraiḥ kena te bhīru dāritaṃ tu stanāntaram</li>
        <li id="R_5.065.008cd">kaḥ krīḍati saroṣeṇa pañcavaktreṇa bhoginā</li>
    </ul>
    <ul id="R_5.065.009">
        <li id="R_5.065.009ab">nirīkṣamāṇaḥ sahasā vāyasaṃ samavaikṣatāḥ</li>
        <li id="R_5.065.009cd">nakhaiḥ sarudhirais tīkṣṇair mām evābhimukhaṃ sthitam</li>
    </ul>
    <ul id="R_5.065.010">
        <li id="R_5.065.010ab">sutaḥ kila sa śakrasya vāyasaḥ patatāṃ varaḥ</li>
        <li id="R_5.065.010cd">dharāntaracaraḥ śīghraṃ pavanasya gatau samaḥ</li>
    </ul>
    <ul id="R_5.065.011">
        <li id="R_5.065.011ab">tatas tasmin mahābāho kopasaṃvartitekṣaṇaḥ</li>
        <li id="R_5.065.011cd">vāyase tvaṃ kṛtvāḥ krūrāṃ matiṃ matimatāṃ vara</li>
    </ul>
    <ul id="R_5.065.012">
        <li id="R_5.065.012ab">sa darbhaṃ saṃstarād gṛhya brahmāstreṇa nyayojayaḥ</li>
        <li id="R_5.065.012cd">sa dīpta iva kālāgnir jajvālābhimukhaḥ khagam</li>
    </ul>
    <ul id="R_5.065.013">
        <li id="R_5.065.013ab">sa tvaṃ pradīptaṃ cikṣepa darbhaṃ taṃ vāyasaṃ prati</li>
        <li id="R_5.065.013cd">tatas tu vāyasaṃ dīptaḥ sa darbho 'nujagāma ha</li>
    </ul>
    <ul id="R_5.065.014">
        <li id="R_5.065.014ab">sa pitrā ca parityaktaḥ suraiḥ sarvair maharṣibhiḥ</li>
        <li id="R_5.065.014cd">trīṃl lokān saṃparikramya trātāraṃ nādhigacchati</li>
    </ul>
    <ul id="R_5.065.015">
        <li id="R_5.065.015ab">taṃ tvaṃ nipatitaṃ bhūmau śaraṇyaḥ śaraṇāgatam</li>
        <li id="R_5.065.015cd">vadhārham api kākutstha kṛpayā paripālayaḥ</li>
    </ul>
    <ul id="R_5.065.016">
        <li id="R_5.065.016ab">mogham astraṃ na śakyaṃ tu kartum ity eva rāghava</li>
        <li id="R_5.065.016cd">tatas tasyākṣikākasya hinasti sma sa dakṣiṇam</li>
    </ul>
    <ul id="R_5.065.017">
        <li id="R_5.065.017ab">rāma tvāṃ sa namaskṛtvā rājño daśarathasya ca</li>
        <li id="R_5.065.017cd">visṛṣṭas tu tadā kākaḥ pratipede kham ālayam</li>
    </ul>
    <ul id="R_5.065.018">
        <li id="R_5.065.018ab">evam astravidāṃ śreṣṭhaḥ sattvavāñ śīlavān api</li>
        <li id="R_5.065.018cd">kimartham astraṃ rakṣaḥsu na yojayasi rāghava</li>
    </ul>
    <ul id="R_5.065.019">
        <li id="R_5.065.019ab">na nāgā nāpi gandharvā nāsurā na marudgaṇāḥ</li>
        <li id="R_5.065.019cd">tava rāma mukhe sthātuṃ śaktāḥ pratisamādhitum</li>
    </ul>
    <ul id="R_5.065.020">
        <li id="R_5.065.020ab">tava vīryavataḥ kaccin mayi yady asti saṃbhramaḥ</li>
        <li id="R_5.065.020cd">kṣipraṃ suniśitair bāṇair hanyatāṃ yudhi rāvaṇaḥ</li>
    </ul>
    <ul id="R_5.065.021">
        <li id="R_5.065.021ab">bhrātur ādeśam ādāya lakṣmaṇo vā paraṃtapaḥ</li>
        <li id="R_5.065.021cd">sa kimarthaṃ naravaro na māṃ rakṣati rāghavaḥ</li>
    </ul>
    <ul id="R_5.065.022">
        <li id="R_5.065.022ab">śaktau tau puruṣavyāghrau vāyvagnisamatejasau</li>
        <li id="R_5.065.022cd">surāṇām api durdharṣau kimarthaṃ mām upekṣataḥ</li>
    </ul>
    <ul id="R_5.065.023">
        <li id="R_5.065.023ab">mamaiva duṣkṛtaṃ kiṃ cin mahad asti na saṃśayaḥ</li>
        <li id="R_5.065.023cd">samarthau sahitau yan māṃ nāpekṣete paraṃtapau</li>
    </ul>
    <ul id="R_5.065.024">
        <li id="R_5.065.024ab">vaidehyā vacanaṃ śrutvā karuṇaṃ sāśrubhāṣitam</li>
        <li id="R_5.065.024cd">punar apy aham āryāṃ tām idaṃ vacanam abruvam</li>
    </ul>
    <ul id="R_5.065.025">
        <li id="R_5.065.025ab">tvacchokavimukho rāmo devi satyena te śape</li>
        <li id="R_5.065.025cd">rāme duḥkhābhibhūte ca lakṣmaṇaḥ paritapyate</li>
    </ul>
    <ul id="R_5.065.026">
        <li id="R_5.065.026ab">kathaṃ cid bhavatī dṛṣṭā na kālaḥ pariśocitum</li>
        <li id="R_5.065.026cd">imaṃ muhūrtaṃ duḥkhānām antaṃ drakṣyasi bhāmini</li>
    </ul>
    <ul id="R_5.065.027">
        <li id="R_5.065.027ab">tāv ubhau naraśārdūlau rājaputrāv ariṃdamau</li>
        <li id="R_5.065.027cd">tvaddarśanakṛtotsāhau laṅkāṃ bhasmīkariṣyataḥ</li>
    </ul>
    <ul id="R_5.065.028">
        <li id="R_5.065.028ab">hatvā ca samare raudraṃ rāvaṇaṃ saha bāndhavam</li>
        <li id="R_5.065.028cd">rāghavas tvāṃ mahābāhuḥ svāṃ purīṃ nayate dhruvam</li>
    </ul>
    <ul id="R_5.065.029">
        <li id="R_5.065.029ab">yat tu rāmo vijānīyād abhijñānam anindite</li>
        <li id="R_5.065.029cd">prītisaṃjananaṃ tasya pradātuṃ tattvam arhasi</li>
    </ul>
    <ul id="R_5.065.030">
        <li id="R_5.065.030ab">sābhivīkṣya diśaḥ sarvā veṇyudgrathanam uttamam</li>
        <li id="R_5.065.030cd">muktvā vastrād dadau mahyaṃ maṇim etaṃ mahābala</li>
    </ul>
    <ul id="R_5.065.031">
        <li id="R_5.065.031ab">pratigṛhya maṇiṃ divyaṃ tava heto raghūttama</li>
        <li id="R_5.065.031cd">śirasā saṃpraṇamyainām aham āgamane tvare</li>
    </ul>
    <ul id="R_5.065.032">
        <li id="R_5.065.032ab">gamane ca kṛtotsāham avekṣya varavarṇinī</li>
        <li id="R_5.065.032cd">vivardhamānaṃ ca hi mām uvāca janakātmajā</li>
        <li id="R_5.065.032ef">aśrupūrṇamukhī dīnā bāṣpasaṃdigdhabhāṣiṇī</li>
    </ul>
    <ul id="R_5.065.033">
        <li id="R_5.065.033ab">hanuman siṃhasaṃkāśau tāv ubhau rāmalakṣmaṇau</li>
        <li id="R_5.065.033cd">sugrīvaṃ ca sahāmātyaṃ sarvān brūyā anāmayam</li>
    </ul>
    <ul id="R_5.065.034">
        <li id="R_5.065.034ab">yathā ca sa mahābāhur māṃ tārayati rāghavaḥ</li>
        <li id="R_5.065.034cd">asmād duḥkhāmbusaṃrodhāt tat samādhātum arhasi</li>
    </ul>
    <ul id="R_5.065.035">
        <li id="R_5.065.035ab">
            <seg type="pāda" n="a">imaṃ ca tīvraṃ mama śokavegaṃ</seg>
            <seg type="pāda" n="b">rakṣobhir ebhiḥ paribhartsanaṃ ca</seg>
        </li>
        <li id="R_5.065.035cd">
            <seg type="pāda" n="c">brūyās tu rāmasya gataḥ samīpaṃ</seg>
            <seg type="pāda" n="d">śivaś ca te 'dhvāstu haripravīra</seg>
        </li>
    </ul>
    <ul id="R_5.065.036">
        <li id="R_5.065.036ab">
            <seg type="pāda" n="a">etat tavāryā nṛparājasiṃha</seg>
            <seg type="pāda" n="b">sītā vacaḥ prāha viṣādapūrvam</seg>
        </li>
        <li id="R_5.065.036cd">
            <seg type="pāda" n="c">etac ca buddhvā gaditaṃ mayā tvaṃ</seg>
            <seg type="pāda" n="d">śraddhatsva sītāṃ kuśalāṃ samagrām</seg>
        </li>
    </ul>
    <ul id="R_5.066.001">
        <li id="R_5.066.001ab">athāham uttaraṃ devyā punar uktaḥ sasaṃbhramam</li>
        <li id="R_5.066.001cd">tava snehān naravyāghra sauhāryād anumānya ca</li>
    </ul>
    <ul id="R_5.066.002">
        <li id="R_5.066.002ab">evaṃ bahuvidhaṃ vācyo rāmo dāśarathis tvayā</li>
        <li id="R_5.066.002cd">yathā mām āpnuyāc chīghraṃ hatvā rāvaṇam āhave</li>
    </ul>
    <ul id="R_5.066.003">
        <li id="R_5.066.003ab">yadi vā manyase vīra vasaikāham ariṃdama</li>
        <li id="R_5.066.003cd">kasmiṃś cit saṃvṛte deśe viśrāntaḥ śvo gamiṣyasi</li>
    </ul>
    <ul id="R_5.066.004">
        <li id="R_5.066.004ab">mama cāpy alpabhāgyāyāḥ sāmnidhyāt tava vānara</li>
        <li id="R_5.066.004cd">asya śokavipākasya muhūrtaṃ syād vimokṣaṇam</li>
    </ul>
    <ul id="R_5.066.005">
        <li id="R_5.066.005ab">gate hi tvayi vikrānte punarāgamanāya vai</li>
        <li id="R_5.066.005cd">prāṇānām api saṃdeho mama syān nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_5.066.006">
        <li id="R_5.066.006ab">tavādarśanajaḥ śoko bhūyo māṃ paritāpayet</li>
        <li id="R_5.066.006cd">duḥkhād duḥkhaparābhūtāṃ durgatāṃ duḥkhabhāginīm</li>
    </ul>
    <ul id="R_5.066.007">
        <li id="R_5.066.007ab">ayaṃ tu vīrasaṃdehas tiṣṭhatīva mamāgrataḥ</li>
        <li id="R_5.066.007cd">sumahāṃs tvatsahāyeṣu haryṛkṣeṣu asaṃśayaḥ</li>
    </ul>
    <ul id="R_5.066.008">
        <li id="R_5.066.008ab">kathaṃ nu khalu duṣpāraṃ tariṣyanti mahodadhim</li>
        <li id="R_5.066.008cd">tāni haryṛkṣasainyāni tau vā naravarātmajau</li>
    </ul>
    <ul id="R_5.066.009">
        <li id="R_5.066.009ab">trayāṇām eva bhūtānāṃ sāgarasyāsya laṅghane</li>
        <li id="R_5.066.009cd">śaktiḥ syād vainateyasya vāyor vā tava vānagha</li>
    </ul>
    <ul id="R_5.066.010">
        <li id="R_5.066.010ab">tad asmin kāryaniyoge vīraivaṃ duratikrame</li>
        <li id="R_5.066.010cd">kiṃ paśyasi samādhānaṃ brūhi kāryavidāṃ vara</li>
    </ul>
    <ul id="R_5.066.011">
        <li id="R_5.066.011ab">kāmam asya tvam evaikaḥ kāryasya parisādhane</li>
        <li id="R_5.066.011cd">paryāptaḥ paravīraghna yaśasyas te balodayaḥ</li>
    </ul>
    <ul id="R_5.066.012">
        <li id="R_5.066.012ab">balaiḥ samagrair yadi māṃ hatvā rāvaṇam āhave</li>
        <li id="R_5.066.012cd">vijayī svāṃ purīṃ rāmo nayet tat syād yaśaskaram</li>
    </ul>
    <ul id="R_5.066.013">
        <li id="R_5.066.013ab">yathāhaṃ tasya vīrasya vanād upadhinā hṛtā</li>
        <li id="R_5.066.013cd">rakṣasā tad bhayād eva tathā nārhati rāghavaḥ</li>
    </ul>
    <ul id="R_5.066.014">
        <li id="R_5.066.014ab">balais tu saṃkulāṃ kṛtvā laṅkāṃ parabalārdanaḥ</li>
        <li id="R_5.066.014cd">māṃ nayed yadi kākutsthas tat tasya sadṛśaṃ bhavet</li>
    </ul>
    <ul id="R_5.066.015">
        <li id="R_5.066.015ab">tad yathā tasya vikrāntam anurūpaṃ mahātmanaḥ</li>
        <li id="R_5.066.015cd">bhavaty āhavaśūrasya tathā tvam upapādaya</li>
    </ul>
    <ul id="R_5.066.016">
        <li id="R_5.066.016ab">tad arthopahitaṃ vākyaṃ praśritaṃ hetusaṃhitam</li>
        <li id="R_5.066.016cd">niśamyāhaṃ tataḥ śeṣaṃ vākyam uttaram abruvam</li>
    </ul>
    <ul id="R_5.066.017">
        <li id="R_5.066.017ab">devi haryṛkṣasainyānām īśvaraḥ plavatāṃ varaḥ</li>
        <li id="R_5.066.017cd">sugrīvaḥ sattvasaṃpannas tavārthe kṛtaniścayaḥ</li>
    </ul>
    <ul id="R_5.066.018">
        <li id="R_5.066.018ab">tasya vikramasaṃpannāḥ sattvavanto mahābalāḥ</li>
        <li id="R_5.066.018cd">manaḥsaṃkalpasaṃpātā nideśe harayaḥ sthitāḥ</li>
    </ul>
    <ul id="R_5.066.019">
        <li id="R_5.066.019ab">yeṣāṃ nopari nādhastān na tiryak sajjate gatiḥ</li>
        <li id="R_5.066.019cd">na ca karmasu sīdanti mahatsv amitatejasaḥ</li>
    </ul>
    <ul id="R_5.066.020">
        <li id="R_5.066.020ab">asakṛt tair mahābhāgair vānarair balasaṃyutaiḥ</li>
        <li id="R_5.066.020cd">pradakṣiṇīkṛtā bhūmir vāyumārgānusāribhiḥ</li>
    </ul>
    <ul id="R_5.066.021">
        <li id="R_5.066.021ab">madviśiṣṭāś ca tulyāś ca santi tatra vanaukasaḥ</li>
        <li id="R_5.066.021cd">mattaḥ pratyavaraḥ kaś cin nāsti sugrīvasaṃnidhau</li>
    </ul>
    <ul id="R_5.066.022">
        <li id="R_5.066.022ab">ahaṃ tāvad iha prāptaḥ kiṃ punas te mahābalāḥ</li>
        <li id="R_5.066.022cd">na hi prakṛṣṭāḥ preṣyante preṣyante hītare janāḥ</li>
    </ul>
    <ul id="R_5.066.023">
        <li id="R_5.066.023ab">tad alaṃ paritāpena devi manyur vyapaitu te</li>
        <li id="R_5.066.023cd">ekotpātena te laṅkām eṣyanti hariyūthapāḥ</li>
    </ul>
    <ul id="R_5.066.024">
        <li id="R_5.066.024ab">mama pṛṣṭhagatau tau ca candrasūryāv ivoditau</li>
        <li id="R_5.066.024cd">tvatsakāśaṃ mahābhāge nṛsiṃhāv āgamiṣyataḥ</li>
    </ul>
    <ul id="R_5.066.025">
        <li id="R_5.066.025ab">arighnaṃ siṃhasaṃkāśaṃ kṣipraṃ drakṣyasi rāghavam</li>
        <li id="R_5.066.025cd">lakṣmaṇaṃ ca dhanuṣpāṇiṃ laṅkā dvāram upasthitam</li>
    </ul>
    <ul id="R_5.066.026">
        <li id="R_5.066.026ab">nakhadaṃṣṭrāyudhān vīrān siṃhaśārdūlavikramān</li>
        <li id="R_5.066.026cd">vānarān vānarendrābhān kṣipraṃ drakṣyasi saṃgatān</li>
    </ul>
    <ul id="R_5.066.027">
        <li id="R_5.066.027ab">śailāmbudan nikāśānāṃ laṅkāmalayasānuṣu</li>
        <li id="R_5.066.027cd">nardatāṃ kapimukhyānām acirāc choṣyase svanam</li>
    </ul>
    <ul id="R_5.066.028">
        <li id="R_5.066.028ab">nivṛttavanavāsaṃ ca tvayā sārdham ariṃdamam</li>
        <li id="R_5.066.028cd">abhiṣiktam ayodhyāyāṃ kṣipraṃ drakṣyasi rāghavam</li>
    </ul>
    <ul id="R_5.066.029">
        <li id="R_5.066.029ab">
            <seg type="pāda" n="a">tato mayā vāgbhir adīnabhāṣiṇī</seg>
            <seg type="pāda" n="b">śivābhir iṣṭābhir abhiprasāditā</seg>
        </li>
        <li id="R_5.066.029cd">
            <seg type="pāda" n="c">jagāma śāntiṃ mama maithilātmajā</seg>
            <seg type="pāda" n="d">tavāpi śokena tathābhipīḍitā</seg>
        </li>
    </ul>
	{/if}
</div>
<div class="kanda-6">
<h4>Kāṇḍa 6</h4>
		{#if kanda6}
    <ul id="R_6.001.001">
        <li id="R_6.001.001ab">śrutvā hanumato vākyaṃ yathāvad abhibhāṣitam</li>
        <li id="R_6.001.001cd">rāmaḥ prītisamāyukto vākyam uttaram abravīt</li>
    </ul>
    <ul id="R_6.001.002">
        <li id="R_6.001.002ab">kṛtaṃ hanumatā kāryaṃ sumahad bhuvi duṣkaram</li>
        <li id="R_6.001.002cd">manasāpi yad anyena na śakyaṃ dharaṇītale</li>
    </ul>
    <ul id="R_6.001.003">
        <li id="R_6.001.003ab">na hi taṃ paripaśyāmi yas tareta mahārṇavam</li>
        <li id="R_6.001.003cd">anyatra garuṇād vāyor anyatra ca hanūmataḥ</li>
    </ul>
    <ul id="R_6.001.004">
        <li id="R_6.001.004ab">devadānavayakṣāṇāṃ gandharvoragarakṣasām</li>
        <li id="R_6.001.004cd">apradhṛṣyāṃ purīṃ laṅkāṃ rāvaṇena surakṣitām</li>
    </ul>
    <ul id="R_6.001.005">
        <li id="R_6.001.005ab">praviṣṭaḥ sattvam āśritya jīvan ko nāma niṣkramet</li>
        <li id="R_6.001.005cd">ko viśet sudurādharṣāṃ rākṣasaiś ca surakṣitām</li>
        <li id="R_6.001.005ef">yo vīryabalasaṃpanno na samaḥ syād dhanūmataḥ</li>
    </ul>
    <ul id="R_6.001.006">
        <li id="R_6.001.006ab">bhṛtyakāryaṃ hanumatā sugrīvasya kṛtaṃ mahat</li>
        <li id="R_6.001.006cd">evaṃ vidhāya svabalaṃ sadṛśaṃ vikramasya ca</li>
    </ul>
    <ul id="R_6.001.007">
        <li id="R_6.001.007ab">yo hi bhṛtyo niyuktaḥ san bhartrā karmaṇi duṣkare</li>
        <li id="R_6.001.007cd">kuryāt tadanurāgeṇa tam āhuḥ puruṣottamam</li>
    </ul>
    <ul id="R_6.001.008">
        <li id="R_6.001.008ab">niyukto nṛpateḥ kāryaṃ na kuryād yaḥ samāhitaḥ</li>
        <li id="R_6.001.008cd">bhṛtyo yuktaḥ samarthaś ca tam āhuḥ puruṣādhamam</li>
    </ul>
    <ul id="R_6.001.009">
        <li id="R_6.001.009ab">tanniyoge niyuktena kṛtaṃ kṛtyaṃ hanūmatā</li>
        <li id="R_6.001.009cd">na cātmā laghutāṃ nītaḥ sugrīvaś cāpi toṣitaḥ</li>
    </ul>
    <ul id="R_6.001.010">
        <li id="R_6.001.010ab">ahaṃ ca raghuvaṃśaś ca lakṣmaṇaś ca mahābalaḥ</li>
        <li id="R_6.001.010cd">vaidehyā darśanenādya dharmataḥ parirakṣitāḥ</li>
    </ul>
    <ul id="R_6.001.011">
        <li id="R_6.001.011ab">idaṃ tu mama dīnasyā mano bhūyaḥ prakarṣati</li>
        <li id="R_6.001.011cd">yad ihāsya priyākhyātur na kurmi sadṛśaṃ priyam</li>
    </ul>
    <ul id="R_6.001.012">
        <li id="R_6.001.012ab">eṣa sarvasvabhūtas tu pariṣvaṅgo hanūmataḥ</li>
        <li id="R_6.001.012cd">mayā kālam imaṃ prāpya dattas tasya mahātmanaḥ</li>
    </ul>
    <ul id="R_6.001.013">
        <li id="R_6.001.013ab">sarvathā sukṛtaṃ tāvat sītāyāḥ parimārgaṇam</li>
        <li id="R_6.001.013cd">sāgaraṃ tu samāsādya punar naṣṭaṃ mano mama</li>
    </ul>
    <ul id="R_6.001.014">
        <li id="R_6.001.014ab">kathaṃ nāma samudrasya duṣpārasya mahāmbhasaḥ</li>
        <li id="R_6.001.014cd">harayo dakṣiṇaṃ pāraṃ gamiṣyanti samāhitāḥ</li>
    </ul>
    <ul id="R_6.001.015">
        <li id="R_6.001.015ab">yady apy eṣa tu vṛttānto vaidehyā gadito mama</li>
        <li id="R_6.001.015cd">samudrapāragamane harīṇāṃ kim ivottaram</li>
    </ul>
    <ul id="R_6.001.016">
        <li id="R_6.001.016ab">ity uktvā śokasaṃbhrānto rāmaḥ śatrunibarhaṇaḥ</li>
        <li id="R_6.001.016cd">hanūmantaṃ mahābāhus tato dhyānam upāgamat</li>
    </ul>
    <ul id="R_6.002.001">
        <li id="R_6.002.001ab">taṃ tu śokaparidyūnaṃ rāmaṃ daśarathātmajam</li>
        <li id="R_6.002.001cd">uvāca vacanaṃ śrīmān sugrīvaḥ śokanāśanam</li>
    </ul>
    <ul id="R_6.002.002">
        <li id="R_6.002.002ab">kiṃ tvaṃ saṃtapyase vīra yathānyaḥ prākṛtas tathā</li>
        <li id="R_6.002.002cd">maivaṃ bhūs tyaja saṃtāpaṃ kṛtaghna iva sauhṛdam</li>
    </ul>
    <ul id="R_6.002.003">
        <li id="R_6.002.003ab">saṃtāpasya ca te sthānaṃ na hi paśyāmi rāghava</li>
        <li id="R_6.002.003cd">pravṛttāv upalabdhāyāṃ jñāte ca nilaye ripoḥ</li>
    </ul>
    <ul id="R_6.002.004">
        <li id="R_6.002.004ab">dhṛtimāñ śāstravit prājñaḥ paṇḍitaś cāsi rāghava</li>
        <li id="R_6.002.004cd">tyajemāṃ pāpikāṃ buddhiṃ kṛtvātmevārthadūṣaṇīm</li>
    </ul>
    <ul id="R_6.002.005">
        <li id="R_6.002.005ab">samudraṃ laṅghayitvā tu mahānakrasamākulam</li>
        <li id="R_6.002.005cd">laṅkām ārohayiṣyāmo haniṣyāmaś ca te ripum</li>
    </ul>
    <ul id="R_6.002.006">
        <li id="R_6.002.006ab">nirutsāhasya dīnasya śokaparyākulātmanaḥ</li>
        <li id="R_6.002.006cd">sarvārthā vyavasīdanti vyasanaṃ cādhigacchati</li>
    </ul>
    <ul id="R_6.002.007">
        <li id="R_6.002.007ab">ime śūrāḥ samarthāś ca sarve no hariyūthapāḥ</li>
        <li id="R_6.002.007cd">tvatpriyārthaṃ kṛtotsāhāḥ praveṣṭum api pāvakam</li>
    </ul>
    <ul id="R_6.002.008">
        <li id="R_6.002.008ab">eṣāṃ harṣeṇa jānāmi tarkaś cāsmin dṛḍho mama</li>
        <li id="R_6.002.008cd">vikrameṇa samāneṣye sītāṃ hatvā yathā ripum</li>
    </ul>
    <ul id="R_6.002.009">
        <li id="R_6.002.009ab">setur atra yathā vadhyed yathā paśyema tāṃ purīm</li>
        <li id="R_6.002.009cd">tasya rākṣasarājasya tathā tvaṃ kuru rāghava</li>
    </ul>
    <ul id="R_6.002.010">
        <li id="R_6.002.010ab">dṛṣṭvā tāṃ hi purīṃ laṅkāṃ trikūṭaśikhare sthitām</li>
        <li id="R_6.002.010cd">hataṃ ca rāvaṇaṃ yuddhe darśanād upadhāraya</li>
    </ul>
    <ul id="R_6.002.011">
        <li id="R_6.002.011ab">setubaddhaḥ samudre ca yāval laṅkā samīpataḥ</li>
        <li id="R_6.002.011cd">sarvaṃ tīrṇaṃ ca vai sainyaṃ jitam ity upadhāryatām</li>
    </ul>
    <ul id="R_6.002.012">
        <li id="R_6.002.012ab">ime hi samare śūrā harayaḥ kāmarūpiṇaḥ</li>
        <li id="R_6.002.012cd">tad alaṃ viklavā buddhī rājan sarvārthanāśanī</li>
    </ul>
    <ul id="R_6.002.013">
        <li id="R_6.002.013ab">puruṣasya hi loke 'smiñ śokaḥ śauryāpakarṣaṇaḥ</li>
        <li id="R_6.002.013cd">yat tu kāryaṃ manuṣyeṇa śauṇḍīryam avalambatā</li>
        <li id="R_6.002.013ef">śūrāṇāṃ hi manuṣyāṇāṃ tvadvidhānāṃ mahātmanām</li>
    </ul>
    <ul id="R_6.002.014">
        <li id="R_6.002.014ab">vinaṣṭe vā pranaṣṭe vā śokaḥ sarvārthanāśanaḥ</li>
        <li id="R_6.002.014cd">tvaṃ tu buddhimatāṃ śreṣṭhaḥ sarvaśāstrārthakovidaḥ</li>
    </ul>
    <ul id="R_6.002.015">
        <li id="R_6.002.015ab">madvidhaiḥ sacivaiḥ sārtham ariṃ jetum ihārhasi</li>
        <li id="R_6.002.015cd">na hi paśyāmy ahaṃ kaṃ cit triṣu lokeṣu rāghava</li>
    </ul>
    <ul id="R_6.002.016">
        <li id="R_6.002.016ab">gṛhītadhanuṣo yas te tiṣṭhed abhimukho raṇe</li>
        <li id="R_6.002.016cd">vānareṣu samāsaktaṃ na te kāryaṃ vipatsyate</li>
    </ul>
    <ul id="R_6.002.017">
        <li id="R_6.002.017ab">acirād drakṣyase sītāṃ tīrtvā sāgaram akṣayam</li>
        <li id="R_6.002.017cd">tad alaṃ śokam ālambya krodham ālamba bhūpate</li>
    </ul>
    <ul id="R_6.002.018">
        <li id="R_6.002.018ab">niśceṣṭāḥ kṣatriyā mandāḥ sarve caṇḍasya bibhyati</li>
        <li id="R_6.002.018cd">laṅganārthaṃ ca ghorasya samudrasya nadīpateḥ</li>
    </ul>
    <ul id="R_6.002.019">
        <li id="R_6.002.019ab">sahāsmābhir ihopetaḥ sūkṣmabuddhir vicāraya</li>
        <li id="R_6.002.019cd">ime hi samare śūrā harayaḥ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_6.002.020">
        <li id="R_6.002.020ab">tān arīn vidhamiṣyanti śilāpādapavṛṣṭibhiḥ</li>
        <li id="R_6.002.020cd">kathaṃ cit paripaśyāmas te vayaṃ varuṇālayam</li>
    </ul>
    <ul id="R_6.002.021">
        <li id="R_6.002.021ab">kim uktvā bahudhā cāpi sarvathā vijayī bhavān</li>
    </ul>
    <ul id="R_6.003.001">
        <li id="R_6.003.001ab">sugrīvasya vacaḥ śrutvā hetumat paramārthavit</li>
        <li id="R_6.003.001cd">pratijagrāha kākutstho hanūmantam athābravīt</li>
    </ul>
    <ul id="R_6.003.002">
        <li id="R_6.003.002ab">tarasā setubandhena sāgarocchoṣaṇena vā</li>
        <li id="R_6.003.002cd">sarvathā susamartho 'smi sāgarasyāsya laṅghane</li>
    </ul>
    <ul id="R_6.003.003">
        <li id="R_6.003.003ab">kati durgāṇi durgāyā laṅkāyās tad bravīhi me</li>
        <li id="R_6.003.003cd">jñātum icchāmi tat sarvaṃ darśanād iva vānara</li>
    </ul>
    <ul id="R_6.003.004">
        <li id="R_6.003.004ab">balasya parimāṇaṃ ca dvāradurgakriyām api</li>
        <li id="R_6.003.004cd">gupti karma ca laṅkāyā rakṣasāṃ sadanāni ca</li>
    </ul>
    <ul id="R_6.003.005">
        <li id="R_6.003.005ab">yathāsukhaṃ yathāvac ca laṅkāyām asi dṛṣṭavān</li>
        <li id="R_6.003.005cd">saram ācakṣva tattvena sarvathā kuśalo hy asi</li>
    </ul>
    <ul id="R_6.003.006">
        <li id="R_6.003.006ab">śrutvā rāmasya vacanaṃ hanūmān mārutātmajaḥ</li>
        <li id="R_6.003.006cd">vākyaṃ vākyavidāṃ śreṣṭho rāmaṃ punar athābravīt</li>
    </ul>
    <ul id="R_6.003.007">
        <li id="R_6.003.007ab">śrūyatāṃ sarvam ākhyāsye durgakarmavidhānataḥ</li>
        <li id="R_6.003.007cd">guptā purī yathā laṅkā rakṣitā ca yathā balaiḥ</li>
    </ul>
    <ul id="R_6.003.008">
        <li id="R_6.003.008ab">parāṃ samṛddhiṃ laṅkāyāḥ sāgarasya ca bhīmatām</li>
        <li id="R_6.003.008cd">vibhāgaṃ ca balaughasya nirdeśaṃ vāhanasya ca</li>
    </ul>
    <ul id="R_6.003.009">
        <li id="R_6.003.009ab">prahṛṣṭā muditā laṅkā mattadvipasamākulā</li>
        <li id="R_6.003.009cd">mahatī rathasaṃpūrṇā rakṣogaṇasamākulā</li>
    </ul>
    <ul id="R_6.003.010">
        <li id="R_6.003.010ab">dṛḍhabaddhakavāṭāni mahāparighavanti ca</li>
        <li id="R_6.003.010cd">dvārāṇi vipulāny asyāś catvāri sumahānti ca</li>
    </ul>
    <ul id="R_6.003.011">
        <li id="R_6.003.011ab">vapreṣūpalayantrāṇi balavanti mahānti ca</li>
        <li id="R_6.003.011cd">āgataṃ parasainyaṃ tais tatra pratinivāryate</li>
    </ul>
    <ul id="R_6.003.012">
        <li id="R_6.003.012ab">dvāreṣu saṃskṛtā bhīmāḥ kālāyasamayāḥ śitāḥ</li>
        <li id="R_6.003.012cd">śataśo rocitā vīraiḥ śataghnyo rakṣasāṃ gaṇaiḥ</li>
    </ul>
    <ul id="R_6.003.013">
        <li id="R_6.003.013ab">sauvarṇaś ca mahāṃs tasyāḥ prākāro duṣpradharṣaṇaḥ</li>
        <li id="R_6.003.013cd">maṇividrumavaidūryamuktāvicaritāntaraḥ</li>
    </ul>
    <ul id="R_6.003.014">
        <li id="R_6.003.014ab">sarvataś ca mahābhīmāḥ śītatoyā mahāśubhāḥ</li>
        <li id="R_6.003.014cd">agādhā grāhavatyaś ca parikhā mīnasevitāḥ</li>
    </ul>
    <ul id="R_6.003.015">
        <li id="R_6.003.015ab">dvāreṣu tāsāṃ catvāraḥ saṃkramāḥ paramāyatāḥ</li>
        <li id="R_6.003.015cd">yantrair upetā bahubhir mahadbhir dṛḍhasaṃdhibhiḥ</li>
    </ul>
    <ul id="R_6.003.016">
        <li id="R_6.003.016ab">trāyante saṃkramās tatra parasainyāgame sati</li>
        <li id="R_6.003.016cd">yantrais tair avakīryante parikhāsu samantataḥ</li>
    </ul>
    <ul id="R_6.003.017">
        <li id="R_6.003.017ab">ekas tv akampyo balavān saṃkramaḥ sumahādṛḍhaḥ</li>
        <li id="R_6.003.017cd">kāñcanair bahubhiḥ stambhair vedikābhiś ca śobhitaḥ</li>
    </ul>
    <ul id="R_6.003.018">
        <li id="R_6.003.018ab">svayaṃ prakṛtisaṃpanno yuyutsū rāma rāvaṇaḥ</li>
        <li id="R_6.003.018cd">utthitaś cāpramattaś ca balānām anudarśane</li>
    </ul>
    <ul id="R_6.003.019">
        <li id="R_6.003.019ab">laṅkā purī nirālambā devadurgā bhayāvahā</li>
        <li id="R_6.003.019cd">nādeyaṃ pārvataṃ vanyaṃ kṛtrimaṃ ca caturvidham</li>
    </ul>
    <ul id="R_6.003.020">
        <li id="R_6.003.020ab">sthitā pāre samudrasya dūrapārasya rāghava</li>
        <li id="R_6.003.020cd">naupathaś cāpi nāsty atra nirādeśaś ca sarvataḥ</li>
    </ul>
    <ul id="R_6.003.021">
        <li id="R_6.003.021ab">śailāgre racitā durgā sā pūr devapuropamā</li>
        <li id="R_6.003.021cd">vājivāraṇasaṃpūrṇā laṅkā paramadurjayā</li>
    </ul>
    <ul id="R_6.003.022">
        <li id="R_6.003.022ab">parighāś ca śataghnyaś ca yantrāṇi vividhāni ca</li>
        <li id="R_6.003.022cd">śobhayanti purīṃ laṅkāṃ rāvaṇasya durātmanaḥ</li>
    </ul>
    <ul id="R_6.003.023">
        <li id="R_6.003.023ab">ayutaṃ rakṣasām atra paścimadvāram āśritam</li>
        <li id="R_6.003.023cd">śūlahastā durādharṣāḥ sarve khaḍgāgrayodhinaḥ</li>
    </ul>
    <ul id="R_6.003.024">
        <li id="R_6.003.024ab">niyutaṃ rakṣasām atra dakṣiṇadvāram āśritam</li>
        <li id="R_6.003.024cd">caturaṅgeṇa sainyena yodhās tatrāpy anuttamāḥ</li>
    </ul>
    <ul id="R_6.003.025">
        <li id="R_6.003.025ab">prayutaṃ rakṣasām atra pūrvadvāraṃ samāśritam</li>
        <li id="R_6.003.025cd">carmakhaḍgadharāḥ sarve tathā sarvāstrakovidāḥ</li>
    </ul>
    <ul id="R_6.003.026">
        <li id="R_6.003.026ab">arbudaṃ rakṣasām atra uttaradvāram āśritam</li>
        <li id="R_6.003.026cd">rathinaś cāśvavāhāś ca kulaputrāḥ supūjitāḥ</li>
    </ul>
    <ul id="R_6.003.027">
        <li id="R_6.003.027ab">śataṃ śatasahasrāṇāṃ madhyamaṃ gulmam āśritam</li>
        <li id="R_6.003.027cd">yātudhānā durādharṣāḥ sāgrakoṭiś ca rakṣasām</li>
    </ul>
    <ul id="R_6.003.028">
        <li id="R_6.003.028ab">te mayā saṃkramā bhagnāḥ parikhāś cāvapūritāḥ</li>
        <li id="R_6.003.028cd">dagdhā ca nagarī laṅkā prākārāś cāvasāditāḥ</li>
    </ul>
    <ul id="R_6.003.029">
        <li id="R_6.003.029ab">yena kena tu mārgeṇa tarāma varuṇālayam</li>
        <li id="R_6.003.029cd">hateti nagarī laṅkāṃ vānarair avadhāryatām</li>
    </ul>
    <ul id="R_6.003.030">
        <li id="R_6.003.030ab">aṅgado dvivido maindo jāmbavān panaso nalaḥ</li>
        <li id="R_6.003.030cd">nīlaḥ senāpatiś caiva balaśeṣeṇa kiṃ tava</li>
    </ul>
    <ul id="R_6.003.031">
        <li id="R_6.003.031ab">plavamānā hi gatvā tāṃ rāvaṇasya mahāpurīm</li>
        <li id="R_6.003.031cd">saprakārāṃ sabhavanām ānayiṣyanti maithilīm</li>
    </ul>
    <ul id="R_6.003.032">
        <li id="R_6.003.032ab">evam ājñāpaya kṣipraṃ balānāṃ sarvasaṃgraham</li>
        <li id="R_6.003.032cd">muhūrtena tu yuktena prasthānam abhirocaya</li>
    </ul>
    <ul id="R_6.004.001">
        <li id="R_6.004.001ab">śrutvā hanūmato vākyaṃ yathāvad anupūrvaśaḥ</li>
        <li id="R_6.004.001cd">tato 'bravīn mahātejā rāmaḥ satyaparākramaḥ</li>
    </ul>
    <ul id="R_6.004.002">
        <li id="R_6.004.002ab">yāṃ nivedayase laṅkāṃ purīṃ bhīmasya rakṣasaḥ</li>
        <li id="R_6.004.002cd">kṣipram enāṃ vadhiṣyāmi satyam etad bravīmi te</li>
    </ul>
    <ul id="R_6.004.003">
        <li id="R_6.004.003ab">asmin muhūrte sugrīva prayāṇam abhirocaye</li>
        <li id="R_6.004.003cd">yukto muhūrto vijayaḥ prāpto madhyaṃ divākaraḥ</li>
    </ul>
    <ul id="R_6.004.004">
        <li id="R_6.004.004ab">uttarā phalgunī hy adya śvas tu hastena yokṣyate</li>
        <li id="R_6.004.004cd">abhiprayāma sugrīva sarvānīkasamāvṛtāḥ</li>
    </ul>
    <ul id="R_6.004.005">
        <li id="R_6.004.005ab">nimittāni ca dhanyāni yāni prādurbhavanti me</li>
        <li id="R_6.004.005cd">nihatya rāvaṇaṃ sītām ānayiṣyāmi jānakīm</li>
    </ul>
    <ul id="R_6.004.006">
        <li id="R_6.004.006ab">upariṣṭād dhi nayanaṃ sphuramāṇam idaṃ mama</li>
        <li id="R_6.004.006cd">vijayaṃ samanuprāptaṃ śaṃsatīva manoratham</li>
    </ul>
    <ul id="R_6.004.007">
        <li id="R_6.004.007ab">agre yātu balasyāsya nīlo mārgam avekṣitum</li>
        <li id="R_6.004.007cd">vṛtaḥ śatasahasreṇa vānarāṇāṃ tarasvinām</li>
    </ul>
    <ul id="R_6.004.008">
        <li id="R_6.004.008ab">phalamūlavatā nīla śītakānanavāriṇā</li>
        <li id="R_6.004.008cd">pathā madhumatā cāśu senāṃ senāpate naya</li>
    </ul>
    <ul id="R_6.004.009">
        <li id="R_6.004.009ab">dūṣayeyur durātmānaḥ pathi mūlaphalodakam</li>
        <li id="R_6.004.009cd">rākṣasāḥ parirakṣethās tebhyas tvaṃ nityam udyataḥ</li>
    </ul>
    <ul id="R_6.004.010">
        <li id="R_6.004.010ab">nimneṣu vanadurgeṣu vaneṣu ca vanaukasaḥ</li>
        <li id="R_6.004.010cd">abhiplutyābhipaśyeyuḥ pareṣāṃ nihataṃ balam</li>
    </ul>
    <ul id="R_6.004.011">
        <li id="R_6.004.011ab">sāgaraughanibhaṃ bhīmam agrānīkaṃ mahābalāḥ</li>
        <li id="R_6.004.011cd">kapisiṃhā prakarṣantu śataśo 'tha sahasraśaḥ</li>
    </ul>
    <ul id="R_6.004.012">
        <li id="R_6.004.012ab">gajaś ca girisaṃkāśo gavayaś ca mahābalaḥ</li>
        <li id="R_6.004.012cd">gavākṣaś cāgrato yāntu gavāṃ dṛptā ivarṣabhāḥ</li>
    </ul>
    <ul id="R_6.004.013">
        <li id="R_6.004.013ab">yātu vānaravāhinyā vānaraḥ plavatāṃ patiḥ</li>
        <li id="R_6.004.013cd">pālayan dakṣiṇaṃ pārśvam ṛṣabho vānararṣabhaḥ</li>
    </ul>
    <ul id="R_6.004.014">
        <li id="R_6.004.014ab">gandhahastīva durdharṣas tarasvī gandhamādanaḥ</li>
        <li id="R_6.004.014cd">yātu vānaravāhinyāḥ savyaṃ pārśvam adhiṣṭhitaḥ</li>
    </ul>
    <ul id="R_6.004.015">
        <li id="R_6.004.015ab">yāsyāmi balamadhye 'haṃ balaugham abhiharṣayan</li>
        <li id="R_6.004.015cd">adhiruhya hanūmantam airāvatam iveśvaraḥ</li>
    </ul>
    <ul id="R_6.004.016">
        <li id="R_6.004.016ab">aṅgadenaiṣa saṃyātu lakṣmaṇaś cāntakopamaḥ</li>
        <li id="R_6.004.016cd">sārvabhaumeṇa bhūteśo draviṇādhipatir yathā</li>
    </ul>
    <ul id="R_6.004.017">
        <li id="R_6.004.017ab">jāmbavāṃś ca suṣeṇaś ca vegadarśī ca vānaraḥ</li>
        <li id="R_6.004.017cd">ṛkṣarājo mahāsattvaḥ kukṣiṃ rakṣantu te trayaḥ</li>
    </ul>
    <ul id="R_6.004.018">
        <li id="R_6.004.018ab">rāghavasya vacaḥ śrutvā sugrīvo vāhinīpatiḥ</li>
        <li id="R_6.004.018cd">vyādideśa mahāvīryān vānarān vānararṣabhaḥ</li>
    </ul>
    <ul id="R_6.004.019">
        <li id="R_6.004.019ab">te vānaragaṇāḥ sarve samutpatya yuyutsavaḥ</li>
        <li id="R_6.004.019cd">guhābhyaḥ śikharebhyaś ca āśu pupluvire tadā</li>
    </ul>
    <ul id="R_6.004.020">
        <li id="R_6.004.020ab">tato vānararājena lakṣmaṇena ca pūjitaḥ</li>
        <li id="R_6.004.020cd">jagāma rāmo dharmātmā sasainyo dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_6.004.021">
        <li id="R_6.004.021ab">śataiḥ śatasahasraiś ca koṭībhir ayutair api</li>
        <li id="R_6.004.021cd">vāraṇābhiś ca haribhir yayau parivṛtas tadā</li>
    </ul>
    <ul id="R_6.004.022">
        <li id="R_6.004.022ab">taṃ yāntam anuyāti sma mahatī harivāhinī</li>
    </ul>
    <ul id="R_6.004.023">
        <li id="R_6.004.023ab">hṛṣṭāḥ pramuditāḥ sarve sugrīveṇābhipālitāḥ</li>
        <li id="R_6.004.023cd">āplavantaḥ plavantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_6.004.023ef">kṣvelanto ninadantaś ca jagmur vai dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_6.004.024">
        <li id="R_6.004.024ab">bhakṣayantaḥ sugandhīni madhūni ca phalāni ca</li>
        <li id="R_6.004.024cd">udvahanto mahāvṛkṣān mañjarīpuñjadhāriṇaḥ</li>
    </ul>
    <ul id="R_6.004.025">
        <li id="R_6.004.025ab">anyonyaṃ sahasā dṛṣṭā nirvahanti kṣipanti ca</li>
        <li id="R_6.004.025cd">patantaś cotpatanty anye pātayanty apare parān</li>
    </ul>
    <ul id="R_6.004.026">
        <li id="R_6.004.026ab">rāvaṇo no nihantavyaḥ sarve ca rajanīcarāḥ</li>
        <li id="R_6.004.026cd">iti garjanti harayo rāghavasya samīpataḥ</li>
    </ul>
    <ul id="R_6.004.027">
        <li id="R_6.004.027ab">purastād ṛṣabho vīro nīlaḥ kumuda eva ca</li>
        <li id="R_6.004.027cd">pathānaṃ śodhayanti sma vānarair bahubhiḥ saha</li>
    </ul>
    <ul id="R_6.004.028">
        <li id="R_6.004.028ab">madhye tu rājā sugrīvo rāmo lakṣmaṇa eva ca</li>
        <li id="R_6.004.028cd">bahubhir balibhir bhīmair vṛtāḥ śatrunibarhaṇaḥ</li>
    </ul>
    <ul id="R_6.004.029">
        <li id="R_6.004.029ab">hariḥ śatabalir vīraḥ koṭībhir daśabhir vṛtaḥ</li>
        <li id="R_6.004.029cd">sarvām eko hy avaṣṭabhya rarakṣa harivāhinīm</li>
    </ul>
    <ul id="R_6.004.030">
        <li id="R_6.004.030ab">koṭīśataparīvāraḥ kesarī panaso gajaḥ</li>
        <li id="R_6.004.030cd">arkaś cātibalaḥ pārśvam ekaṃ tasyābhirakṣati</li>
    </ul>
    <ul id="R_6.004.031">
        <li id="R_6.004.031ab">suṣeṇo jāmbavāṃś caiva ṛkṣair bahubhir āvṛtaḥ</li>
        <li id="R_6.004.031cd">sugrīvaṃ purataḥ kṛtvā jaghanaṃ saṃrarakṣatuḥ</li>
    </ul>
    <ul id="R_6.004.032">
        <li id="R_6.004.032ab">teṣāṃ senāpatir vīro nīlo vānarapuṃgavaḥ</li>
        <li id="R_6.004.032cd">saṃpatan patatāṃ śreṣṭhas tad balaṃ paryapālayat</li>
    </ul>
    <ul id="R_6.004.033">
        <li id="R_6.004.033ab">darīmikhaḥ prajaṅghaś ca jambho 'tha rabhasaḥ kapiḥ</li>
        <li id="R_6.004.033cd">sarvataś ca yayur vīrās tvarayantaḥ plavaṃgamān</li>
    </ul>
    <ul id="R_6.004.034">
        <li id="R_6.004.034ab">evaṃ te hariśārdūlā gacchanto baladarpitāḥ</li>
        <li id="R_6.004.034cd">apaśyaṃs te giriśreṣṭhaṃ sahyaṃ drumalatāyutam</li>
    </ul>
    <ul id="R_6.004.035">
        <li id="R_6.004.035ab">sāgaraughanibhaṃ bhīmaṃ tad vānarabalaṃ mahat</li>
        <li id="R_6.004.035cd">niḥsasarpa mahāghoṣaṃ bhīmavega ivārṇavaḥ</li>
    </ul>
    <ul id="R_6.004.036">
        <li id="R_6.004.036ab">tasya dāśaratheḥ pārśve śūrās te kapikuñjarāḥ</li>
        <li id="R_6.004.036cd">tūrṇam āpupluvuḥ sarve sadaśvā iva coditāḥ</li>
    </ul>
    <ul id="R_6.004.037">
        <li id="R_6.004.037ab">kapibhyām uhyamānau tau śuśubhate nararṣabhau</li>
        <li id="R_6.004.037cd">mahadbhyām iva saṃspṛṣṭau grāhābhyāṃ candrabhāskarau</li>
    </ul>
    <ul id="R_6.004.038">
        <li id="R_6.004.038ab">tam aṅgadagato rāmaṃ lakṣmaṇaḥ śubhayā girā</li>
        <li id="R_6.004.038cd">uvāca pratipūrṇārthaḥ smṛtimān pratibhānavān</li>
    </ul>
    <ul id="R_6.004.039">
        <li id="R_6.004.039ab">hṛtām avāpya vaidehīṃ kṣipraṃ hatvā ca rāvaṇam</li>
        <li id="R_6.004.039cd">samṛddhārthaḥ samṛddhārthām ayodhyāṃ pratiyāsyasi</li>
    </ul>
    <ul id="R_6.004.040">
        <li id="R_6.004.040ab">mahānti ca nimittāni divi bhūmau ca rāghava</li>
        <li id="R_6.004.040cd">śubhānti tava paśyāmi sarvāṇy evārthasiddhaye</li>
    </ul>
    <ul id="R_6.004.041">
        <li id="R_6.004.041ab">anu vāti śubho vāyuḥ senāṃ mṛduhitaḥ sukhaḥ</li>
        <li id="R_6.004.041cd">pūrṇavalgusvarāś ceme pravadanti mṛgadvijāḥ</li>
    </ul>
    <ul id="R_6.004.042">
        <li id="R_6.004.042ab">prasannāś ca diśaḥ sarvā vimalaś ca divākaraḥ</li>
        <li id="R_6.004.042cd">uśanā ca prasannārcir anu tvāṃ bhārgavo gataḥ</li>
    </ul>
    <ul id="R_6.004.043">
        <li id="R_6.004.043ab">brahmarāśir viśuddhaś ca śuddhāś ca paramarṣayaḥ</li>
        <li id="R_6.004.043cd">arciṣmantaḥ prakāśante dhruvaṃ sarve pradakṣiṇam</li>
    </ul>
    <ul id="R_6.004.044">
        <li id="R_6.004.044ab">triśaṅkur vimalo bhāti rājarṣiḥ sapurohitaḥ</li>
        <li id="R_6.004.044cd">pitāmahavaro 'smākam iṣkvākūṇāṃ mahātmanām</li>
    </ul>
    <ul id="R_6.004.045">
        <li id="R_6.004.045ab">vimale ca prakāśete viśākhe nirupadrave</li>
        <li id="R_6.004.045cd">nakṣatraṃ param asmākam ikṣvākūṇāṃ mahātmanām</li>
    </ul>
    <ul id="R_6.004.046">
        <li id="R_6.004.046ab">nairṛtaṃ nairṛtānāṃ ca nakṣatram abhipīḍyate</li>
        <li id="R_6.004.046cd">mūlaṃ mūlavatā spṛṣṭaṃ dhūpyate dhūmaketunā</li>
    </ul>
    <ul id="R_6.004.047">
        <li id="R_6.004.047ab">saraṃ caitad vināśāya rākṣasānām upasthitam</li>
        <li id="R_6.004.047cd">kāle kālagṛhītānāṃ nakatraṃ grahapīḍitam</li>
    </ul>
    <ul id="R_6.004.048">
        <li id="R_6.004.048ab">prasannāḥ surasāś cāpo vanāni phalavanti ca</li>
        <li id="R_6.004.048cd">pravānty abhyadhikaṃ gandhā yathartukusumā drumāḥ</li>
    </ul>
    <ul id="R_6.004.049">
        <li id="R_6.004.049ab">vyūḍhāni kapisainyāni prakāśante 'dhikaṃ prabho</li>
        <li id="R_6.004.049cd">devānām iva sainyāni saṃgrāme tārakāmaye</li>
    </ul>
    <ul id="R_6.004.050">
        <li id="R_6.004.050ab">evam ārya samīkṣyaitān prīto bhavitum arhasi</li>
        <li id="R_6.004.050cd">iti bhrātaram āśvāsya hṛṣṭaḥ saumitrir abravīt</li>
    </ul>
    <ul id="R_6.004.051">
        <li id="R_6.004.051ab">athāvṛtya mahīṃ kṛtsnāṃ jagāma mahatī camūḥ</li>
        <li id="R_6.004.051cd">ṛkṣavānaraśārdūlair nakhadaṃṣṭrāyudhair vṛtā</li>
    </ul>
    <ul id="R_6.004.052">
        <li id="R_6.004.052ab">karāgraiś caraṇāgraiś ca vānarair uddhataṃ rajaḥ</li>
        <li id="R_6.004.052cd">bhaumam antardadhe lokaṃ nivārya savituḥ prabhām</li>
    </ul>
    <ul id="R_6.004.053">
        <li id="R_6.004.053ab">sā sma yāti divārātraṃ mahatī harivāhinī</li>
        <li id="R_6.004.053cd">hṛṣṭapramuditā senā sugrīveṇābhirakṣitā</li>
    </ul>
    <ul id="R_6.004.054">
        <li id="R_6.004.054ab">vanarās tvaritaṃ yānti sarve yuddhābhinandanaḥ</li>
        <li id="R_6.004.054cd">mumokṣayiṣavaḥ sītāṃ muhūrtaṃ kvāpi nāsata</li>
    </ul>
    <ul id="R_6.004.055">
        <li id="R_6.004.055ab">tataḥ pādapasaṃbādhaṃ nānāmṛgasamākulam</li>
        <li id="R_6.004.055cd">sahyaparvatam āsedur malayaṃ ca mahī dharam</li>
    </ul>
    <ul id="R_6.004.056">
        <li id="R_6.004.056ab">kānanāni vicitrāṇi nadīprasravaṇāni ca</li>
        <li id="R_6.004.056cd">paśyann api yayau rāmaḥ sahyasya malayasya ca</li>
    </ul>
    <ul id="R_6.004.057">
        <li id="R_6.004.057ab">campakāṃs tilakāṃś cūtān aśokān sinduvārakān</li>
        <li id="R_6.004.057cd">karavīrāṃś ca timiśān bhañjanti sma plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.004.058">
        <li id="R_6.004.058ab">phalāny amṛtagandhīni mūlāni kusumāni ca</li>
        <li id="R_6.004.058cd">bubhujur vānarās tatra pādapānāṃ balotkaṭāḥ</li>
    </ul>
    <ul id="R_6.004.059">
        <li id="R_6.004.059ab">droṇamātrapramāṇāni lambamānāni vānarāḥ</li>
        <li id="R_6.004.059cd">yayuḥ pibanto hṛṣṭās te madhūni madhupiṅgalāḥ</li>
    </ul>
    <ul id="R_6.004.060">
        <li id="R_6.004.060ab">pādapān avabhañjanto vikarṣantas tathā latāḥ</li>
        <li id="R_6.004.060cd">vidhamanto girivarān prayayuḥ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_6.004.061">
        <li id="R_6.004.061ab">vṛkṣebhyo 'nye tu kapayo nardanto madhudarpitāḥ</li>
        <li id="R_6.004.061cd">anye vṛkṣān prapadyante prapatanty api cāpare</li>
    </ul>
    <ul id="R_6.004.062">
        <li id="R_6.004.062ab">babhūva vasudhā tais tu saṃpūrṇā haripuṃgavaiḥ</li>
        <li id="R_6.004.062cd">yathā kamalakedāraiḥ pakvair iva vasuṃdharā</li>
    </ul>
    <ul id="R_6.004.063">
        <li id="R_6.004.063ab">mahendram atha saṃprāpya rāmo rājīvalocanaḥ</li>
        <li id="R_6.004.063cd">adhyārohan mahābāhuḥ śikharaṃ drumabhūṣitam</li>
    </ul>
    <ul id="R_6.004.064">
        <li id="R_6.004.064ab">tataḥ śikharam āruhya rāmo daśarathātmajaḥ</li>
        <li id="R_6.004.064cd">kūrmamīnasamākīrṇam apaśyat salilāśayam</li>
    </ul>
    <ul id="R_6.004.065">
        <li id="R_6.004.065ab">te sahyaṃ samatikramya malayaṃ ca mahāgirim</li>
        <li id="R_6.004.065cd">āsedur ānupūrvyeṇa samudraṃ bhīmaniḥsvanam</li>
    </ul>
    <ul id="R_6.004.066">
        <li id="R_6.004.066ab">avaruhya jagāmāśu velāvanam anuttamam</li>
        <li id="R_6.004.066cd">rāmo ramayatāṃ śreṣṭhaḥ sasugrīvaḥ salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.004.067">
        <li id="R_6.004.067ab">atha dhautopalatalāṃ toyaughaiḥ sahasotthitaiḥ</li>
        <li id="R_6.004.067cd">velām āsādya vipulāṃ rāmo vacanam abravīt</li>
    </ul>
    <ul id="R_6.004.068">
        <li id="R_6.004.068ab">ete vayam anuprāptāḥ sugrīva varuṇālayam</li>
        <li id="R_6.004.068cd">ihedānīṃ vicintā sā yā na pūrvaṃ samutthitā</li>
    </ul>
    <ul id="R_6.004.069">
        <li id="R_6.004.069ab">ataḥ paramatīro 'yaṃ sāgaraḥ saritāṃ pati</li>
        <li id="R_6.004.069cd">na cāyam anupāyena śakyas taritum arṇavaḥ</li>
    </ul>
    <ul id="R_6.004.070">
        <li id="R_6.004.070ab">tad ihaiva niveśo 'stu mantraḥ prastūyatām iha</li>
        <li id="R_6.004.070cd">yathedaṃ vānarabalaṃ paraṃ pāram avāpnuyāt</li>
    </ul>
    <ul id="R_6.004.071">
        <li id="R_6.004.071ab">itīva sa mahābāhuḥ sītāharaṇakarśitaḥ</li>
        <li id="R_6.004.071cd">rāmaḥ sāgaram āsādya vāsam ājñāpayat tadā</li>
    </ul>
    <ul id="R_6.004.072">
        <li id="R_6.004.072ab">saṃprāpto mantrakālo naḥ sāgarasyeha laṅghane</li>
        <li id="R_6.004.072cd">svāṃ svāṃ senāṃ samutsṛjya mā ca kaś cit kuto vrajet</li>
        <li id="R_6.004.072ef">gacchantu vānarāḥ śūrā jñeyaṃ channaṃ bhayaṃ ca naḥ</li>
    </ul>
    <ul id="R_6.004.073">
        <li id="R_6.004.073ab">rāmasya vacanaṃ śrutvā sugrīvaḥ sahalakṣmaṇaḥ</li>
        <li id="R_6.004.073cd">senāṃ nyaveśayat tīre sāgarasya drumāyute</li>
    </ul>
    <ul id="R_6.004.074">
        <li id="R_6.004.074ab">virarāja samīpasthaṃ sāgarasya tu tad balam</li>
        <li id="R_6.004.074cd">madhupāṇḍujalaḥ śrīmān dvitīya iva sāgaraḥ</li>
    </ul>
    <ul id="R_6.004.075">
        <li id="R_6.004.075ab">velāvanam upāgamya tatas te haripuṃgavāḥ</li>
        <li id="R_6.004.075cd">viniviṣṭāḥ paraṃ pāraṃ kāṅkṣamāṇā mahodadheḥ</li>
    </ul>
    <ul id="R_6.004.076">
        <li id="R_6.004.076ab">sā mahārṇavam āsādya hṛṣṭā vānaravāhinī</li>
        <li id="R_6.004.076cd">vāyuvegasamādhūtaṃ paśyamānā mahārṇavam</li>
    </ul>
    <ul id="R_6.004.077">
        <li id="R_6.004.077ab">dūrapāram asaṃbādhaṃ rakṣogaṇaniṣevitam</li>
        <li id="R_6.004.077cd">paśyanto varuṇāvāsaṃ niṣedur hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.004.078">
        <li id="R_6.004.078ab">caṇḍanakragrahaṃ ghoraṃ kṣapādau divasakṣaye</li>
        <li id="R_6.004.078cd">candrodaye samādhūtaṃ praticandrasamākulam</li>
    </ul>
    <ul id="R_6.004.079">
        <li id="R_6.004.079ab">caṇḍānilamahāgrāhaiḥ kīrṇaṃ timitimiṃgilaiḥ</li>
        <li id="R_6.004.079cd">dīptabhogair ivākrīrṇaṃ bhujaṃgair varuṇālayam</li>
    </ul>
    <ul id="R_6.004.080">
        <li id="R_6.004.080ab">avagāḍhaṃ mahāsattair nānāśailasamākulam</li>
        <li id="R_6.004.080cd">durgaṃ drugam amārgaṃ tam agādham asurālayam</li>
    </ul>
    <ul id="R_6.004.081">
        <li id="R_6.004.081ab">makarair nāgabhogaiś ca vigāḍhā vātalohitāḥ</li>
        <li id="R_6.004.081cd">utpetuś ca nipetuś ca pravṛddhā jalarāśayaḥ</li>
    </ul>
    <ul id="R_6.004.082">
        <li id="R_6.004.082ab">agnicūrṇam ivāviddhaṃ bhāskarāmbumanoragam</li>
        <li id="R_6.004.082cd">surāriviṣayaṃ ghoraṃ pātālaviṣamaṃ sadā</li>
    </ul>
    <ul id="R_6.004.083">
        <li id="R_6.004.083ab">sāgaraṃ cāmbaraprakhyam ambaraṃ sāgaropamam</li>
        <li id="R_6.004.083cd">sāgaraṃ cāmbaraṃ ceti nirviśeṣam adṛśyata</li>
    </ul>
    <ul id="R_6.004.084">
        <li id="R_6.004.084ab">saṃpṛktaṃ nabhasā hy ambhaḥ saṃpṛktaṃ ca nabho 'mbhasā</li>
        <li id="R_6.004.084cd">tādṛgrūpe sma dṛśyete tārā ratnasamākule</li>
    </ul>
    <ul id="R_6.004.085">
        <li id="R_6.004.085ab">samutpatitameghasya vīcci mālākulasya ca</li>
        <li id="R_6.004.085cd">viśeṣo na dvayor āsīt sāgarasyāmbarasya ca</li>
    </ul>
    <ul id="R_6.004.086">
        <li id="R_6.004.086ab">anyonyair āhatāḥ saktāḥ sasvanur bhīmaniḥsvanāḥ</li>
        <li id="R_6.004.086cd">ūrmayaḥ sindhurājasya mahābherya ivāhave</li>
    </ul>
    <ul id="R_6.004.087">
        <li id="R_6.004.087ab">ratnaughajalasaṃnādaṃ viṣaktam iva vāyunā</li>
        <li id="R_6.004.087cd">utpatantam iva kruddhaṃ yādogaṇasamākulam</li>
    </ul>
    <ul id="R_6.004.088">
        <li id="R_6.004.088ab">dadṛśus te mahātmāno vātāhatajalāśayam</li>
        <li id="R_6.004.088cd">aniloddhūtam ākāśe pravalgatam ivormibhiḥ</li>
        <li id="R_6.004.088ef">bhrāntormijalasaṃnādaṃ pralolam iva sāgaram</li>
    </ul>
    <ul id="R_6.005.001">
        <li id="R_6.005.001ab">sā tu nīlena vidhivat svārakṣā susamāhitā</li>
        <li id="R_6.005.001cd">sāgarasyottare tīre sādhu senā niveśitā</li>
    </ul>
    <ul id="R_6.005.002">
        <li id="R_6.005.002ab">maindaś ca dvividhaś cobhau tatra vānarapuṃgavau</li>
        <li id="R_6.005.002cd">viceratuś ca tāṃ senāṃ rakṣārthaṃ sarvato diśam</li>
    </ul>
    <ul id="R_6.005.003">
        <li id="R_6.005.003ab">niviṣṭāyāṃ tu senāyāṃ tīre nadanadīpateḥ</li>
        <li id="R_6.005.003cd">pārśvasthaṃ lakṣmaṇaṃ dṛṣṭvā rāmo vacanam abravīt</li>
    </ul>
    <ul id="R_6.005.004">
        <li id="R_6.005.004ab">śokaś ca kila kālena gacchatā hy apagacchati</li>
        <li id="R_6.005.004cd">mama cāpaśyataḥ kāntām ahany ahani vardhate</li>
    </ul>
    <ul id="R_6.005.005">
        <li id="R_6.005.005ab">na me duḥkhaṃ priyā dūre na me duḥkhaṃ hṛteti ca</li>
        <li id="R_6.005.005cd">etad evānuśocāmi vayo 'syā hy ativartate</li>
    </ul>
    <ul id="R_6.005.006">
        <li id="R_6.005.006ab">vāhi vāta yataḥ kanyā tāṃ spṛṣṭvā mām api spṛśa</li>
        <li id="R_6.005.006cd">tvayi me gātrasaṃsparśaś candre dṛṣṭisamāgamaḥ</li>
    </ul>
    <ul id="R_6.005.007">
        <li id="R_6.005.007ab">tan me dahati gātrāṇi viṣaṃ pītam ivāśaye</li>
        <li id="R_6.005.007cd">hā nātheti priyā sā māṃ hriyamāṇā yad abravīt</li>
    </ul>
    <ul id="R_6.005.008">
        <li id="R_6.005.008ab">tadviyogendhanavatā taccintāvipulārciṣā</li>
        <li id="R_6.005.008cd">rātriṃ divaṃ śarīraṃ me dahyate madanāgninā</li>
    </ul>
    <ul id="R_6.005.009">
        <li id="R_6.005.009ab">avagāhyārṇavaṃ svapsye saumitre bhavatā vinā</li>
        <li id="R_6.005.009cd">kathaṃ cit prajvalan kāmaḥ samāsuptaṃ jale dahet</li>
    </ul>
    <ul id="R_6.005.010">
        <li id="R_6.005.010ab">bahv etat kāmayānasya śakyam etena jīvitum</li>
        <li id="R_6.005.010cd">yad ahaṃ sā ca vāmorur ekāṃ dharaṇim āśritau</li>
    </ul>
    <ul id="R_6.005.011">
        <li id="R_6.005.011ab">kedārasyeva kedāraḥ sodakasya nirūdakaḥ</li>
        <li id="R_6.005.011cd">upasnehena jīvāmi jīvantīṃ yac chṛṇomi tām</li>
    </ul>
    <ul id="R_6.005.012">
        <li id="R_6.005.012ab">kadā tu khalu susśoṇīṃ śatapatrāyatekṣaṇām</li>
        <li id="R_6.005.012cd">vijitya śatrūn drakṣyāmi sītāṃ sphītām iva śriyam</li>
    </ul>
    <ul id="R_6.005.013">
        <li id="R_6.005.013ab">kadā nu cārubimbauṣṭhaṃ tasyāḥ padmam ivānanam</li>
        <li id="R_6.005.013cd">īṣadunnamya pāsyāmi rasāyanam ivāturaḥ</li>
    </ul>
    <ul id="R_6.005.014">
        <li id="R_6.005.014ab">tau tasyāḥ saṃhatau pīnau stanau tālaphalopamau</li>
        <li id="R_6.005.014cd">kadā nu khalu sotkampau hasantyā māṃ bhajiṣyataḥ</li>
    </ul>
    <ul id="R_6.005.015">
        <li id="R_6.005.015ab">sā nūnam asitāpāṅgī rakṣomadhyagatā satī</li>
        <li id="R_6.005.015cd">mannāthā nāthahīneva trātāraṃ nādhigacchati</li>
    </ul>
    <ul id="R_6.005.016">
        <li id="R_6.005.016ab">kadā vikṣobhya rakṣāṃsi sā vidhūyotpatiṣyati</li>
        <li id="R_6.005.016cd">vidhūya jaladān nīlāñ śaśilekhā śaratsv iva</li>
    </ul>
    <ul id="R_6.005.017">
        <li id="R_6.005.017ab">svabhāvatanukā nūnaṃ śokenānaśanena ca</li>
        <li id="R_6.005.017cd">bhūyas tanutarā sītā deśakālaviparyayāt</li>
    </ul>
    <ul id="R_6.005.018">
        <li id="R_6.005.018ab">kadā nu rākṣasendrasya nidhāyorasi sāyakān</li>
        <li id="R_6.005.018cd">sītāṃ pratyāhariṣyāmi śokam utsṛjya mānasaṃ</li>
    </ul>
    <ul id="R_6.005.019">
        <li id="R_6.005.019ab">kadā nu khalu māṃ sādhvī sītāmarasutopamā</li>
        <li id="R_6.005.019cd">sotkaṇṭhā kaṇṭham ālambya mokṣyaty ānandajaṃ jalam</li>
    </ul>
    <ul id="R_6.005.020">
        <li id="R_6.005.020ab">kadā śokam imaṃ ghoraṃ maithilī viprayogajam</li>
        <li id="R_6.005.020cd">sahasā vipramokṣyāmi vāsaḥ śukletaraṃ yathā</li>
    </ul>
    <ul id="R_6.005.021">
        <li id="R_6.005.021ab">evaṃ vilapatas tasya tatra rāmasya dhīmataḥ</li>
        <li id="R_6.005.021cd">dinakṣayān mandavapur bhāskaro 'stam upāgamat</li>
    </ul>
    <ul id="R_6.005.022">
        <li id="R_6.005.022ab">āśvāsito lakṣmaṇena rāmaḥ saṃdhyām upāsata</li>
        <li id="R_6.005.022cd">smaran kamalapatrākṣīṃ sītāṃ śokākulīkṛtaḥ</li>
    </ul>
    <ul id="R_6.006.001">
        <li id="R_6.006.001ab">laṅkāyāṃ tu kṛtaṃ karma ghoraṃ dṛṣṭvā bhavāvaham</li>
        <li id="R_6.006.001cd">rākṣasendro hanumatā śakreṇeva mahātmanā</li>
        <li id="R_6.006.001ef">abravīd rākṣasān sarvān hriyā kiṃ cid avāṅmukhaḥ</li>
    </ul>
    <ul id="R_6.006.002">
        <li id="R_6.006.002ab">dharṣitā ca praviṣṭā ca laṅkā duṣprasahā purī</li>
        <li id="R_6.006.002cd">tena vānaramātreṇa dṛṣṭā sītā ca jānakī</li>
    </ul>
    <ul id="R_6.006.003">
        <li id="R_6.006.003ab">prasādo dharṣitaś caityaḥ pravarā rākṣasā hatāḥ</li>
        <li id="R_6.006.003cd">āvilā ca purī laṅkā sarvā hanumatā kṛtā</li>
    </ul>
    <ul id="R_6.006.004">
        <li id="R_6.006.004ab">kiṃ kariṣyāmi bhadraṃ vaḥ kiṃ vā yuktam anantaram</li>
        <li id="R_6.006.004cd">ucyatāṃ naḥ samarthaṃ yat kṛtaṃ ca sukṛtaṃ bhavet</li>
    </ul>
    <ul id="R_6.006.005">
        <li id="R_6.006.005ab">mantramūlaṃ hi vijayaṃ prāhur āryā manasvinaḥ</li>
        <li id="R_6.006.005cd">tasmād vai rocaye mantraṃ rāmaṃ prati mahābalāḥ</li>
    </ul>
    <ul id="R_6.006.006">
        <li id="R_6.006.006ab">trividhāḥ puruṣā loke uttamādhamamadhyamāḥ</li>
        <li id="R_6.006.006cd">teṣāṃ tu samavetānāṃ guṇadoṣaṃ vadāmy aham</li>
    </ul>
    <ul id="R_6.006.007">
        <li id="R_6.006.007ab">mantribhir hitasaṃyuktaiḥ samarthair mantranirṇaye</li>
        <li id="R_6.006.007cd">mitrair vāpi samānārthair bāndhavair api vā hitaiḥ</li>
    </ul>
    <ul id="R_6.006.008">
        <li id="R_6.006.008ab">sahito mantrayitvā yaḥ karmārambhān pravartayet</li>
        <li id="R_6.006.008cd">daive ca kurute yatnaṃ tam āhuḥ puruṣottamam</li>
    </ul>
    <ul id="R_6.006.009">
        <li id="R_6.006.009ab">eko 'rthaṃ vimṛśed eko dharme prakurute manaḥ</li>
        <li id="R_6.006.009cd">ekaḥ kāryāṇi kurute tam āhur madhyamaṃ naram</li>
    </ul>
    <ul id="R_6.006.010">
        <li id="R_6.006.010ab">guṇadoṣāv aniścitya tyaktvā daivavyapāśrayam</li>
        <li id="R_6.006.010cd">kariṣyāmīti yaḥ kāryam upekṣet sa narādhamaḥ</li>
    </ul>
    <ul id="R_6.006.011">
        <li id="R_6.006.011ab">yatheme puruṣā nityam uttamādhamamadhyamāḥ</li>
        <li id="R_6.006.011cd">evaṃ mantro 'pi vijñeya uttamādhamamadhyamaḥ</li>
    </ul>
    <ul id="R_6.006.012">
        <li id="R_6.006.012ab">aikamatyam upāgamya śāstradṛṣṭena cakṣuṣā</li>
        <li id="R_6.006.012cd">mantriṇo yatra nirastās tam āhur mantram uttamam</li>
    </ul>
    <ul id="R_6.006.013">
        <li id="R_6.006.013ab">bahvyo 'pi matayo gatvā mantriṇo hy arthanirṇaye</li>
        <li id="R_6.006.013cd">punar yatraikatāṃ prāptaḥ sa mantro madhyamaḥ smṛtaḥ</li>
    </ul>
    <ul id="R_6.006.014">
        <li id="R_6.006.014ab">anyonyamatim āsthāya yatra saṃpratibhāṣyate</li>
        <li id="R_6.006.014cd">na caikamatye śreyo 'sti mantraḥ so 'dhama ucyate</li>
    </ul>
    <ul id="R_6.006.015">
        <li id="R_6.006.015ab">tasmāt sumantritaṃ sādhu bhavanto mantrisattamāḥ</li>
        <li id="R_6.006.015cd">kāryaṃ saṃpratipadyantām etat kṛtyatamaṃ mama</li>
    </ul>
    <ul id="R_6.006.016">
        <li id="R_6.006.016ab">vānarāṇāṃ hi vīrāṇāṃ sahasraiḥ parivāritaḥ</li>
        <li id="R_6.006.016cd">rāmo 'bhyeti purīṃ laṅkām asmākam uparodhakaḥ</li>
    </ul>
    <ul id="R_6.006.017">
        <li id="R_6.006.017ab">tariṣyati ca suvyaktaṃ rāghavaḥ sāgaraṃ sukham</li>
        <li id="R_6.006.017cd">tarasā yuktarūpeṇa sānujaḥ sabalānugaḥ</li>
    </ul>
    <ul id="R_6.006.018">
        <li id="R_6.006.018ab">asminn evaṃgate kārye viruddhe vānaraiḥ saha</li>
        <li id="R_6.006.018cd">hitaṃ pure ca sainye ca sarvaṃ saṃmantryatāṃ mama</li>
    </ul>
    <ul id="R_6.007.001">
        <li id="R_6.007.001ab">ity uktā rākṣasendreṇa rākṣasās te mahābalāḥ</li>
        <li id="R_6.007.001cd">ūcuḥ prāñjalayaḥ sarve rāvaṇaṃ rākṣaseśvaram</li>
    </ul>
    <ul id="R_6.007.002">
        <li id="R_6.007.002ab">rājan parighaśaktyṛṣṭiśūlapaṭṭasasaṃkulam</li>
        <li id="R_6.007.002cd">sumahan no balaṃ kasmād viṣādaṃ bhajate bhavān</li>
    </ul>
    <ul id="R_6.007.003">
        <li id="R_6.007.003ab">kailāsaśikharāvāsī yakṣair bahubhir āvṛtaḥ</li>
        <li id="R_6.007.003cd">sumahat kadanaṃ kṛtvā vaśyas te dhanadaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_6.007.004">
        <li id="R_6.007.004ab">sa maheśvarasakhyena ślāghamānas tvayā vibho</li>
        <li id="R_6.007.004cd">nirjitaḥ samare roṣāl lokapālo mahābalaḥ</li>
    </ul>
    <ul id="R_6.007.005">
        <li id="R_6.007.005ab">vinihatya ca yakṣaughān vikṣobhya ca vigṛhya ca</li>
        <li id="R_6.007.005cd">tvayā kailāsaśikharād vimānam idam āhṛtam</li>
    </ul>
    <ul id="R_6.007.006">
        <li id="R_6.007.006ab">mayena dānavendreṇa tvadbhayāt sakhyam icchatā</li>
        <li id="R_6.007.006cd">duhitā tava bhāryārthe dattā rākṣasapuṃgava</li>
    </ul>
    <ul id="R_6.007.007">
        <li id="R_6.007.007ab">dānavendro madhur nāma vīryotsikto durāsadaḥ</li>
        <li id="R_6.007.007cd">vigṛhya vaśam ānītaḥ kumbhīnasyāḥ sukhāvahaḥ</li>
    </ul>
    <ul id="R_6.007.008">
        <li id="R_6.007.008ab">nirjitās te mahābāho nāgā gatvā rasātalam</li>
        <li id="R_6.007.008cd">vāsukis takṣakaḥ śaṅkho jaṭī ca vaśam āhṛtāḥ</li>
    </ul>
    <ul id="R_6.007.009">
        <li id="R_6.007.009ab">akṣayā balavantaś ca śūrā labdhavarāḥ punaḥ</li>
        <li id="R_6.007.009cd">tvayā saṃvatsaraṃ yuddhvā samare dānavā vibho</li>
    </ul>
    <ul id="R_6.007.010">
        <li id="R_6.007.010ab">svabalaṃ samupāśritya nītā vaśam ariṃdama</li>
        <li id="R_6.007.010cd">māyāś cādhigatās tatra bahavo rākṣasādhipa</li>
    </ul>
    <ul id="R_6.007.011">
        <li id="R_6.007.011ab">śūrāś ca balavantaś ca varuṇasya sutā raṇe</li>
        <li id="R_6.007.011cd">nirjitās te mahābāho caturvidhabalānugāḥ</li>
    </ul>
    <ul id="R_6.007.012">
        <li id="R_6.007.012ab">mṛtyudaṇḍamahāgrāhaṃ śālmalidvīpamaṇḍitam</li>
        <li id="R_6.007.012cd">avagāhya tvayā rājan yamasya balasāgaram</li>
    </ul>
    <ul id="R_6.007.013">
        <li id="R_6.007.013ab">jayaś ca viplulaḥ prāpto mṛtyuś ca pratiṣedhitaḥ</li>
        <li id="R_6.007.013cd">suyuddhena ca te sarve lokās tatra sutoṣitāḥ</li>
    </ul>
    <ul id="R_6.007.014">
        <li id="R_6.007.014ab">kṣatriyair bahubhir vīraiḥ śakratulyaparākramaiḥ</li>
        <li id="R_6.007.014cd">āsīd vasumatī pūrṇā mahadbhir iva pādapaiḥ</li>
    </ul>
    <ul id="R_6.007.015">
        <li id="R_6.007.015ab">teṣāṃ vīryaguṇotsāhair na samo rāghavo raṇe</li>
        <li id="R_6.007.015cd">prasahya te tvayā rājan hatāḥ paramadurjayāḥ</li>
    </ul>
    <ul id="R_6.007.016">
        <li id="R_6.007.016ab">rājan nāpad ayukteyam āgatā prākṛtāj janāt</li>
        <li id="R_6.007.016cd">hṛdi naiva tvayā kāryā tvaṃ vadhiṣyasi rāghavam</li>
    </ul>
    <ul id="R_6.008.001">
        <li id="R_6.008.001ab">tato nīlāmbudanibhaḥ prahasto nāma rākṣasaḥ</li>
        <li id="R_6.008.001cd">abravīt prāñjalir vākyaṃ śūraḥ senāpatis tadā</li>
    </ul>
    <ul id="R_6.008.002">
        <li id="R_6.008.002ab">devadānavagandharvāḥ piśācapatagoragāḥ</li>
        <li id="R_6.008.002cd">na tvāṃ dharṣayituṃ śaktāḥ kiṃ punar vānarā raṇe</li>
    </ul>
    <ul id="R_6.008.003">
        <li id="R_6.008.003ab">sarve pramattā viśvastā vañcitāḥ sma hanūmatā</li>
        <li id="R_6.008.003cd">na hi me jīvato gacchej jīvan sa vanagocaraḥ</li>
    </ul>
    <ul id="R_6.008.004">
        <li id="R_6.008.004ab">sarvāṃ sāgaraparyantāṃ saśailavanakānanām</li>
        <li id="R_6.008.004cd">karomy avānarāṃ bhūmim ājñāpayatu māṃ bhavān</li>
    </ul>
    <ul id="R_6.008.005">
        <li id="R_6.008.005ab">rakṣāṃ caiva vidhāsyāmi vānarād rajanīcara</li>
        <li id="R_6.008.005cd">nāgamiṣyati te duḥkhaṃ kiṃ cid ātmāparādhajam</li>
    </ul>
    <ul id="R_6.008.006">
        <li id="R_6.008.006ab">abravīc ca susaṃkruddho durmukho nāma rākṣasaḥ</li>
        <li id="R_6.008.006cd">idaṃ na kṣamaṇīyaṃ hi sarveṣāṃ naḥ pradharṣaṇam</li>
    </ul>
    <ul id="R_6.008.007">
        <li id="R_6.008.007ab">ayaṃ paribhavo bhūyaḥ purasyāntaḥpurasya ca</li>
        <li id="R_6.008.007cd">śrīmato rākṣasendrasya vānarendrapradharṣaṇam</li>
    </ul>
    <ul id="R_6.008.008">
        <li id="R_6.008.008ab">asmin muhūrte hatvaiko nivartiṣyāmi vānarān</li>
        <li id="R_6.008.008cd">praviṣṭān sāgaraṃ bhīmam ambaraṃ vā rasātalam</li>
    </ul>
    <ul id="R_6.008.009">
        <li id="R_6.008.009ab">tato 'bravīt susaṃkruddho vajradaṃṣṭro mahābalaḥ</li>
        <li id="R_6.008.009cd">pragṛhya parighaṃ ghoraṃ māṃsaśoṇitarūpitam</li>
    </ul>
    <ul id="R_6.008.010">
        <li id="R_6.008.010ab">kiṃ vo hanumatā kāryaṃ kṛpaṇena tapasvinā</li>
        <li id="R_6.008.010cd">rāme tiṣṭhati durdharṣe sugrīve sahalakṣmaṇe</li>
    </ul>
    <ul id="R_6.008.011">
        <li id="R_6.008.011ab">adya rāmaṃ sasugrīvaṃ parigheṇa salakṣmaṇam</li>
        <li id="R_6.008.011cd">āgamiṣyāmi hatvaiko vikṣobhya harivāhinīm</li>
    </ul>
    <ul id="R_6.008.012">
        <li id="R_6.008.012ab">kaumbhakarṇis tato vīro nikumbho nāma vīryavān</li>
        <li id="R_6.008.012cd">abravīt paramakurddho rāvaṇaṃ lokarāvaṇam</li>
    </ul>
    <ul id="R_6.008.013">
        <li id="R_6.008.013ab">sarve bhavantas tiṣṭhantu mahārājena saṃgatāḥ</li>
        <li id="R_6.008.013cd">aham eko haniṣyāmi rāghavaṃ sahalakṣmaṇam</li>
    </ul>
    <ul id="R_6.008.014">
        <li id="R_6.008.014ab">tato vajrahanur nāma rākṣasaḥ parvatopamaḥ</li>
        <li id="R_6.008.014cd">kruddhaḥ parilihan vaktraṃ jihvayā vākyam abravīt</li>
    </ul>
    <ul id="R_6.008.015">
        <li id="R_6.008.015ab">svairaṃ kurvantu kāryāṇi bhavanto vigatajvarāḥ</li>
        <li id="R_6.008.015cd">eko 'haṃ bhakṣayiṣyāmi tān sarvān hariyūthapān</li>
    </ul>
    <ul id="R_6.008.016">
        <li id="R_6.008.016ab">svasthāḥ krīḍantu niścintāḥ pibantu madhuvāruṇīm</li>
        <li id="R_6.008.016cd">aham eko haniṣyāmi sugrīvaṃ sahalakṣmaṇam</li>
        <li id="R_6.008.016ef">sāṅgadaṃ ca hanūmantaṃ rāmaṃ ca raṇakuñjaram</li>
    </ul>
    <ul id="R_6.009.001">
        <li id="R_6.009.001ab">tato nikumbho rabhasaḥ sūryaśatrur mahābalaḥ</li>
        <li id="R_6.009.001cd">suptaghno yajñakopaś ca mahāpārśvo mahodaraḥ</li>
    </ul>
    <ul id="R_6.009.002">
        <li id="R_6.009.002ab">agniketuś ca durdharṣo raśmiketuś ca rākṣasaḥ</li>
        <li id="R_6.009.002cd">indrajic ca mahātejā balavān rāvaṇātmajaḥ</li>
    </ul>
    <ul id="R_6.009.003">
        <li id="R_6.009.003ab">prahasto 'tha virūpākṣo vajradaṃṣṭro mahābalaḥ</li>
        <li id="R_6.009.003cd">dhūmrākṣaś cātikāyaś ca durmukhaś caiva rākṣasaḥ</li>
    </ul>
    <ul id="R_6.009.004">
        <li id="R_6.009.004ab">parighān paṭṭasān prāsāñ śaktiśūlaparaśvadhān</li>
        <li id="R_6.009.004cd">cāpāni ca sabāṇāni khaḍgāṃś ca vipulāñ śitān</li>
    </ul>
    <ul id="R_6.009.005">
        <li id="R_6.009.005ab">pragṛhya paramakruddhāḥ samutpatya ca rākṣasāḥ</li>
        <li id="R_6.009.005cd">abruvan rāvaṇaṃ sarve pradīptā iva tejasā</li>
    </ul>
    <ul id="R_6.009.006">
        <li id="R_6.009.006ab">adya rāmaṃ vadhiṣyāmaḥ sugrīvaṃ ca salakṣmaṇam</li>
        <li id="R_6.009.006cd">kṛpaṇaṃ ca hanūmantaṃ laṅkā yena pradharṣitā</li>
    </ul>
    <ul id="R_6.009.007">
        <li id="R_6.009.007ab">tān gṛhītāyudhān sarvān vārayitvā vibhīṣaṇaḥ</li>
        <li id="R_6.009.007cd">abravīt prāñjalir vākyaṃ punaḥ pratyupaveśya tān</li>
    </ul>
    <ul id="R_6.009.008">
        <li id="R_6.009.008ab">apy upāyais tribhis tāta yo 'rthaḥ prāptuṃ na śakyate</li>
        <li id="R_6.009.008cd">tasya vikramakālāṃs tān yuktān āhur manīṣiṇaḥ</li>
    </ul>
    <ul id="R_6.009.009">
        <li id="R_6.009.009ab">pramatteṣv abhiyukteṣu daivena prahateṣu ca</li>
        <li id="R_6.009.009cd">vikramās tāta sidhyanti parīkṣya vidhinā kṛtāḥ</li>
    </ul>
    <ul id="R_6.009.010">
        <li id="R_6.009.010ab">apramattaṃ kathaṃ taṃ tu vijigīṣuṃ bale sthitam</li>
        <li id="R_6.009.010cd">jitaroṣaṃ durādharṣaṃ pradharṣayitum icchatha</li>
    </ul>
    <ul id="R_6.009.011">
        <li id="R_6.009.011ab">samudraṃ laṅghayitvā tu ghoraṃ nadanadīpatim</li>
        <li id="R_6.009.011cd">kṛtaṃ hanumatā karma duṣkaraṃ tarkayeta kaḥ</li>
    </ul>
    <ul id="R_6.009.012">
        <li id="R_6.009.012ab">balāny aparimeyāni vīryāṇi ca niśācarāḥ</li>
        <li id="R_6.009.012cd">pareṣāṃ sahasāvajñā na kartavyā kathaṃ cana</li>
    </ul>
    <ul id="R_6.009.013">
        <li id="R_6.009.013ab">kiṃ ca rākṣasarājasya rāmeṇāpakṛtaṃ purā</li>
        <li id="R_6.009.013cd">ājahāra janasthānād yasya bhāryāṃ yaśasvinaḥ</li>
    </ul>
    <ul id="R_6.009.014">
        <li id="R_6.009.014ab">kharo yady ativṛttas tu rāmeṇa nihato raṇe</li>
        <li id="R_6.009.014cd">avaśyaṃ prāṇināṃ prāṇā rakṣitavyā yathā balam</li>
    </ul>
    <ul id="R_6.009.015">
        <li id="R_6.009.015ab">etannimittaṃ vaidehī bhayaṃ naḥ sumahad bhavet</li>
        <li id="R_6.009.015cd">āhṛtā sā parityājyā kalahārthe kṛte na kim</li>
    </ul>
    <ul id="R_6.009.016">
        <li id="R_6.009.016ab">na naḥ kṣamaṃ vīryavatā tena dharmānuvartinā</li>
        <li id="R_6.009.016cd">vairaṃ nirarthakaṃ kartuṃ dīyatām asya maithilī</li>
    </ul>
    <ul id="R_6.009.017">
        <li id="R_6.009.017ab">yāvan na sagajāṃ sāśvāṃ bahuratnasamākulām</li>
        <li id="R_6.009.017cd">purīṃ dārayate bāṇair dīyatām asya maithilī</li>
    </ul>
    <ul id="R_6.009.018">
        <li id="R_6.009.018ab">yāvat sughorā mahatī durdharṣā harivāhinī</li>
        <li id="R_6.009.018cd">nāvaskandati no laṅkāṃ tāvat sītā pradīyatām</li>
    </ul>
    <ul id="R_6.009.019">
        <li id="R_6.009.019ab">vinaśyed dhi purī laṅkā śūrāḥ sarve ca rākṣasāḥ</li>
        <li id="R_6.009.019cd">rāmasya dayitā patnī na svayaṃ yadi dīyate</li>
    </ul>
    <ul id="R_6.009.020">
        <li id="R_6.009.020ab">prasādaye tvāṃ bandhutvāt kuruṣva vacanaṃ mama</li>
        <li id="R_6.009.020cd">hitaṃ pathyaṃ tv ahaṃ brūmi dīyatām asya maithilī</li>
    </ul>
    <ul id="R_6.009.021">
        <li id="R_6.009.021ab">
            <seg type="pāda" n="a">purā śaratsūryamarīcisaṃnibhān</seg>
            <seg type="pāda" n="b">navāgrapuṅkhān sudṛḍhān nṛpātmajaḥ</seg>
        </li>
        <li id="R_6.009.021cd">
            <seg type="pāda" n="c">sṛjaty amoghān viśikhān vadhāya te</seg>
            <seg type="pāda" n="d">pradīyatāṃ dāśarathāya maithilī</seg>
        </li>
    </ul>
    <ul id="R_6.009.022">
        <li id="R_6.009.022ab">
            <seg type="pāda" n="a">tyajasva kopaṃ sukhadharmanāśanaṃ</seg>
            <seg type="pāda" n="b">bhajasva dharmaṃ ratikīrtivardhanam</seg>
        </li>
        <li id="R_6.009.022cd">
            <seg type="pāda" n="c">prasīda jīvema saputrabāndhavāḥ</seg>
            <seg type="pāda" n="d">pradīyatāṃ dāśarathāya maithilī</seg>
        </li>
    </ul>
    <ul id="R_6.010.001">
        <li id="R_6.010.001ab">suniviṣṭaṃ hitaṃ vākyam uktavantaṃ vibhīṣaṇam</li>
        <li id="R_6.010.001cd">abravīt paruṣaṃ vākyaṃ rāvaṇaḥ kālacoditaḥ</li>
    </ul>
    <ul id="R_6.010.002">
        <li id="R_6.010.002ab">vaset saha sapatnena kruddhenāśīviṣeṇa vā</li>
        <li id="R_6.010.002cd">na tu mitrapravādena saṃvasec chatrusevinā</li>
    </ul>
    <ul id="R_6.010.003">
        <li id="R_6.010.003ab">jānāmi śīlaṃ jñātīnāṃ sarvalokeṣu rākṣasa</li>
        <li id="R_6.010.003cd">hṛṣyanti vyasaneṣv ete jñātīnāṃ jñātayaḥ sadā</li>
    </ul>
    <ul id="R_6.010.004">
        <li id="R_6.010.004ab">pradhānaṃ sādhakaṃ vaidyaṃ dharmaśīlaṃ ca rākṣasa</li>
        <li id="R_6.010.004cd">jñātayo hy avamanyante śūraṃ paribhavanti ca</li>
    </ul>
    <ul id="R_6.010.005">
        <li id="R_6.010.005ab">nityam anyonyasaṃhṛṣṭā vyasaneṣv ātatāyinaḥ</li>
        <li id="R_6.010.005cd">pracchannahṛdayā ghorā jñātayas tu bhayāvahāḥ</li>
    </ul>
    <ul id="R_6.010.006">
        <li id="R_6.010.006ab">śrūyante hastibhir gītāḥ ślokāḥ padmavane kva cit</li>
        <li id="R_6.010.006cd">pāśahastān narān dṛṣṭvā śṛṇu tān gadato mama</li>
    </ul>
    <ul id="R_6.010.007">
        <li id="R_6.010.007ab">nāgnir nānyāni śastrāṇi na naḥ pāśā bhayāvahāḥ</li>
        <li id="R_6.010.007cd">ghorāḥ svārthaprayuktās tu jñātayo no bhayāvahāḥ</li>
    </ul>
    <ul id="R_6.010.008">
        <li id="R_6.010.008ab">upāyam ete vakṣyanti grahaṇe nātra saṃśayaḥ</li>
        <li id="R_6.010.008cd">kṛtsnād bhayāj jñātibhayaṃ sukaṣṭaṃ viditaṃ ca naḥ</li>
    </ul>
    <ul id="R_6.010.009">
        <li id="R_6.010.009ab">vidyate goṣu saṃpannaṃ vidyate brāhmaṇe damaḥ</li>
        <li id="R_6.010.009cd">vidyate strīṣu cāpalyaṃ vidyate jñātito bhayam</li>
    </ul>
    <ul id="R_6.010.010">
        <li id="R_6.010.010ab">tato neṣṭam idaṃ saumya yad ahaṃ lokasatkṛtaḥ</li>
        <li id="R_6.010.010cd">aiśvaryam abhijātaś ca ripūṇāṃ mūrdhni ca sthitaḥ</li>
    </ul>
    <ul id="R_6.010.011">
        <li id="R_6.010.011ab">anyas tv evaṃvidhaṃ brūyād vākyam etan niśācara</li>
        <li id="R_6.010.011cd">asmin muhūrte na bhavet tvāṃ tu dhik kulapāṃsanam</li>
    </ul>
    <ul id="R_6.010.012">
        <li id="R_6.010.012ab">ity uktaḥ paruṣaṃ vākyaṃ nyāyavādī vibhīṣaṇaḥ</li>
        <li id="R_6.010.012cd">utpapāta gadāpāṇiś caturbhiḥ saha rākṣasaiḥ</li>
    </ul>
    <ul id="R_6.010.013">
        <li id="R_6.010.013ab">abravīc ca tadā vākyaṃ jātakrodho vibhīṣaṇaḥ</li>
        <li id="R_6.010.013cd">antarikṣagataḥ śrīmān bhrātaraṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_6.010.014">
        <li id="R_6.010.014ab">sa tvaṃ bhrātāsi me rājan brūhi māṃ yad yad icchasi</li>
        <li id="R_6.010.014cd">idaṃ tu paruṣaṃ vākyaṃ na kṣamāmy anṛtaṃ tava</li>
    </ul>
    <ul id="R_6.010.015">
        <li id="R_6.010.015ab">sunītaṃ hitakāmena vākyam uktaṃ daśānana</li>
        <li id="R_6.010.015cd">na gṛhṇanty akṛtātmānaḥ kālasya vaśam āgatāḥ</li>
    </ul>
    <ul id="R_6.010.016">
        <li id="R_6.010.016ab">sulabhāḥ puruṣā rājan satataṃ priyavādinaḥ</li>
        <li id="R_6.010.016cd">apriyasya tu pathyasya vaktā śrotā ca durlabhaḥ</li>
    </ul>
    <ul id="R_6.010.017">
        <li id="R_6.010.017ab">baddhaṃ kālasya pāśena sarvabhūtāpahāriṇā</li>
        <li id="R_6.010.017cd">na naśyantam upekṣeyaṃ pradīptaṃ śaraṇaṃ yathā</li>
    </ul>
    <ul id="R_6.010.018">
        <li id="R_6.010.018ab">dīptapāvakasaṃkāśaiḥ śitaiḥ kāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.010.018cd">na tvām icchāmy ahaṃ draṣṭuṃ rāmeṇa nihataṃ śaraiḥ</li>
    </ul>
    <ul id="R_6.010.019">
        <li id="R_6.010.019ab">śūrāś ca balavantaś ca kṛtāstrāś ca raṇājire</li>
        <li id="R_6.010.019cd">kālābhipannā sīdanti yathā vālukasetavaḥ</li>
    </ul>
    <ul id="R_6.010.020">
        <li id="R_6.010.020ab">ātmānaṃ sarvathā rakṣa purīṃ cemāṃ sarākṣasām</li>
        <li id="R_6.010.020cd">svasti te 'stu gamiṣyāmi sukhī bhava mayā vinā</li>
    </ul>
    <ul id="R_6.010.021">
        <li id="R_6.010.021ab">
            <seg type="pāda" n="a">nivāryamāṇasya mayā hitaiṣiṇā</seg>
            <seg type="pāda" n="b">na rocate te vacanaṃ niśācara</seg>
        </li>
        <li id="R_6.010.021cd">
            <seg type="pāda" n="c">parītakālā hi gatāyuṣo narā</seg>
            <seg type="pāda" n="d">hitaṃ na gṛhṇanti suhṛdbhir īritam</seg>
        </li>
    </ul>
    <ul id="R_6.011.001">
        <li id="R_6.011.001ab">ity uktvā paruṣaṃ vākyaṃ rāvaṇaṃ rāvaṇānujaḥ</li>
        <li id="R_6.011.001cd">ājagāma muhūrtena yatra rāmaḥ salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.011.002">
        <li id="R_6.011.002ab">taṃ meruśikharākāraṃ dīptām iva śatahradām</li>
        <li id="R_6.011.002cd">gaganasthaṃ mahīsthās te dadṛśur vānarādhipāḥ</li>
    </ul>
    <ul id="R_6.011.003">
        <li id="R_6.011.003ab">tam ātmapañcamaṃ dṛṣṭvā sugrīvo vānarādhipaḥ</li>
        <li id="R_6.011.003cd">vānaraiḥ saha durdharṣaś cintayām āsa buddhimān</li>
    </ul>
    <ul id="R_6.011.004">
        <li id="R_6.011.004ab">cintayitvā muhūrtaṃ tu vānarāṃs tān uvāca ha</li>
        <li id="R_6.011.004cd">hanūmatpramukhān sarvān idaṃ vacanam uttamam</li>
    </ul>
    <ul id="R_6.011.005">
        <li id="R_6.011.005ab">eṣa sarvāyudhopetaś caturbhiḥ saha rākṣasaiḥ</li>
        <li id="R_6.011.005cd">rākṣaso 'bhyeti paśyadhvam asmān hantuṃ na saṃśayaḥ</li>
    </ul>
    <ul id="R_6.011.006">
        <li id="R_6.011.006ab">sugrīvasya vacaḥ śrutvā sarve te vānarottamāḥ</li>
        <li id="R_6.011.006cd">sālān udyamya śailāṃś ca idaṃ vacanam abruvan</li>
    </ul>
    <ul id="R_6.011.007">
        <li id="R_6.011.007ab">śīghraṃ vyādiśa no rājan vadhāyaiṣāṃ durātmanām</li>
        <li id="R_6.011.007cd">nipatantu hatāś caite dharaṇyām alpajīvitāḥ</li>
    </ul>
    <ul id="R_6.011.008">
        <li id="R_6.011.008ab">teṣāṃ saṃbhāṣamāṇānām anyonyaṃ sa vibhīṣaṇaḥ</li>
        <li id="R_6.011.008cd">uttaraṃ tīram āsādya khastha eva vyatiṣṭhata</li>
    </ul>
    <ul id="R_6.011.009">
        <li id="R_6.011.009ab">uvāca ca mahāprājñaḥ svareṇa mahatā mahān</li>
        <li id="R_6.011.009cd">sugrīvaṃ tāṃś ca saṃprekṣya khastha eva vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.011.010">
        <li id="R_6.011.010ab">rāvaṇo nāma durvṛtto rākṣaso rākṣaseśvaraḥ</li>
        <li id="R_6.011.010cd">tasyāham anujo bhrātā vibhīṣaṇa iti śrutaḥ</li>
    </ul>
    <ul id="R_6.011.011">
        <li id="R_6.011.011ab">tena sītā janasthānād dhṛtā hatvā jaṭāyuṣam</li>
        <li id="R_6.011.011cd">ruddhvā ca vivaśā dīnā rākṣasībhiḥ surakṣitā</li>
    </ul>
    <ul id="R_6.011.012">
        <li id="R_6.011.012ab">tam ahaṃ hetubhir vākyair vividhaiś ca nyadarśayam</li>
        <li id="R_6.011.012cd">sādhu niryātyatāṃ sītā rāmāyeti punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.011.013">
        <li id="R_6.011.013ab">sa ca na pratijagrāha rāvaṇaḥ kālacoditaḥ</li>
        <li id="R_6.011.013cd">ucyamāno hitaṃ vākyaṃ viparīta ivauṣadham</li>
    </ul>
    <ul id="R_6.011.014">
        <li id="R_6.011.014ab">so 'haṃ paruṣitas tena dāsavac cāvamānitaḥ</li>
        <li id="R_6.011.014cd">tyaktvā putrāṃś ca dārāṃś ca rāghavaṃ śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_6.011.015">
        <li id="R_6.011.015ab">sarvalokaśaraṇyāya rāghavāya mahātmane</li>
        <li id="R_6.011.015cd">nivedayata māṃ kṣipraṃ vibhīṣaṇam upasthitam</li>
    </ul>
    <ul id="R_6.011.016">
        <li id="R_6.011.016ab">etat tu vacanaṃ śrutvā sugrīvo laghuvikramaḥ</li>
        <li id="R_6.011.016cd">lakṣmaṇasyāgrato rāmaṃ saṃrabdham idam abravīt</li>
    </ul>
    <ul id="R_6.011.017">
        <li id="R_6.011.017ab">rāvaṇasyānujo bhrātā vibhīṣaṇa iti śrutaḥ</li>
        <li id="R_6.011.017cd">caturbhiḥ saha rakṣobhir bhavantaṃ śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_6.011.018">
        <li id="R_6.011.018ab">rāvaṇena praṇihitaṃ tam avehi vibhīṣaṇam</li>
        <li id="R_6.011.018cd">tasyāhaṃ nigrahaṃ manye kṣamaṃ kṣamavatāṃ vara</li>
    </ul>
    <ul id="R_6.011.019">
        <li id="R_6.011.019ab">rākṣaso jihmayā buddhyā saṃdiṣṭo 'yam upasthitaḥ</li>
        <li id="R_6.011.019cd">prahartuṃ māyayā channo viśvaste tvayi rāghava</li>
    </ul>
    <ul id="R_6.011.020">
        <li id="R_6.011.020ab">badhyatām eṣa tīvreṇa daṇḍena sacivaiḥ saha</li>
        <li id="R_6.011.020cd">rāvaṇasya nṛśaṃsasya bhrātā hy eṣa vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.011.021">
        <li id="R_6.011.021ab">evam uktvā tu taṃ rāmaṃ saṃrabdho vāhinīpatiḥ</li>
        <li id="R_6.011.021cd">vākyajño vākyakuśalaṃ tato maunam upāgamat</li>
    </ul>
    <ul id="R_6.011.022">
        <li id="R_6.011.022ab">sugrīvasya tu tad vākyaṃ śrutvā rāmo mahābalaḥ</li>
        <li id="R_6.011.022cd">samīpasthān uvācedaṃ hanūmatpramukhān harīn</li>
    </ul>
    <ul id="R_6.011.023">
        <li id="R_6.011.023ab">yad uktaṃ kapirājena rāvaṇāvarajaṃ prati</li>
        <li id="R_6.011.023cd">vākyaṃ hetumad atyarthaṃ bhavadbhir api tac chrutam</li>
    </ul>
    <ul id="R_6.011.024">
        <li id="R_6.011.024ab">suhṛdā hy arthakṛccheṣu yuktaṃ buddhimatā satā</li>
        <li id="R_6.011.024cd">samarthenāpi saṃdeṣṭuṃ śāśvatīṃ bhūtim icchatā</li>
    </ul>
    <ul id="R_6.011.025">
        <li id="R_6.011.025ab">ity evaṃ paripṛṣṭās te svaṃ svaṃ matam atandritāḥ</li>
        <li id="R_6.011.025cd">sopacāraṃ tadā rāmam ūcur hitacikīrṣavaḥ</li>
    </ul>
    <ul id="R_6.011.026">
        <li id="R_6.011.026ab">ajñātaṃ nāsti te kiṃ cit triṣu lokeṣu rāghava</li>
        <li id="R_6.011.026cd">ātmānaṃ pūjayan rāma pṛcchasy asmān suhṛttayā</li>
    </ul>
    <ul id="R_6.011.027">
        <li id="R_6.011.027ab">tvaṃ hi satyavrataḥ śūro dhārmiko dṛḍhavikramaḥ</li>
        <li id="R_6.011.027cd">parīkṣya kārā smṛtimān nisṛṣṭātmā suhṛtsu ca</li>
    </ul>
    <ul id="R_6.011.028">
        <li id="R_6.011.028ab">tasmād ekaikaśas tāvad bruvantu sacivās tava</li>
        <li id="R_6.011.028cd">hetuto matisaṃpannāḥ samarthāś ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.011.029">
        <li id="R_6.011.029ab">ity ukte rāghavāyātha matimān aṅgado 'grataḥ</li>
        <li id="R_6.011.029cd">vibhīṣaṇaparīkṣārtham uvāca vacanaṃ hariḥ</li>
    </ul>
    <ul id="R_6.011.030">
        <li id="R_6.011.030ab">śatroḥ sakāśāt saṃprāptaḥ sarvathā śaṅkya eva hi</li>
        <li id="R_6.011.030cd">viśvāsayogyaḥ sahasā na kartavyo vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.011.031">
        <li id="R_6.011.031ab">chādayitvātmabhāvaṃ hi caranti śaṭhabuddhayaḥ</li>
        <li id="R_6.011.031cd">praharanti ca randhreṣu so 'narthaḥ sumahān bhavet</li>
    </ul>
    <ul id="R_6.011.032">
        <li id="R_6.011.032ab">arthānarthau viniścitya vyavasāyaṃ bhajeta ha</li>
        <li id="R_6.011.032cd">guṇataḥ saṃgrahaṃ kuryād doṣatas tu visarjayet</li>
    </ul>
    <ul id="R_6.011.033">
        <li id="R_6.011.033ab">yadi doṣo mahāṃs tasmiṃs tyajyatām aviśaṅkitam</li>
        <li id="R_6.011.033cd">guṇān vāpi bahūñ jñātvā saṃgrahaḥ kriyatāṃ nṛpa</li>
    </ul>
    <ul id="R_6.011.034">
        <li id="R_6.011.034ab">śarabhas tv atha niścitya sārthaṃ vacanam abravīt</li>
        <li id="R_6.011.034cd">kṣipram asmin naravyāghra cāraḥ pratividhīyatām</li>
    </ul>
    <ul id="R_6.011.035">
        <li id="R_6.011.035ab">praṇidhāya hi cāreṇa yathāvat sūkṣmabuddhinā</li>
        <li id="R_6.011.035cd">parīkṣya ca tataḥ kāryo yathānyāyaṃ parigrahaḥ</li>
    </ul>
    <ul id="R_6.011.036">
        <li id="R_6.011.036ab">jāmbavāṃs tv atha saṃprekṣya śāstrabuddhyā vicakṣaṇaḥ</li>
        <li id="R_6.011.036cd">vākyaṃ vijñāpayām āsa guṇavad doṣavarjitam</li>
    </ul>
    <ul id="R_6.011.037">
        <li id="R_6.011.037ab">baddhavairāc ca pāpāc ca rākṣasendrād vibhīṣaṇaḥ</li>
        <li id="R_6.011.037cd">adeśa kāle saṃprāptaḥ sarvathā śaṅkyatām ayam</li>
    </ul>
    <ul id="R_6.011.038">
        <li id="R_6.011.038ab">tato maindas tu saṃprekṣya nayāpanayakovidaḥ</li>
        <li id="R_6.011.038cd">vākyaṃ vacanasaṃpanno babhāṣe hetumattaram</li>
    </ul>
    <ul id="R_6.011.039">
        <li id="R_6.011.039ab">vacanaṃ nāma tasyaiṣa rāvaṇasya vibhīṣaṇaḥ</li>
        <li id="R_6.011.039cd">pṛcchyatāṃ madhureṇāyaṃ śanair naravareśvara</li>
    </ul>
    <ul id="R_6.011.040">
        <li id="R_6.011.040ab">bhāvam asya tu vijñāya tatas tattvaṃ kariṣyasi</li>
        <li id="R_6.011.040cd">yadi dṛṣṭo na duṣṭo vā buddhipūrvaṃ nararṣabha</li>
    </ul>
    <ul id="R_6.011.041">
        <li id="R_6.011.041ab">atha saṃskārasaṃpanno hanūmān sacivottamaḥ</li>
        <li id="R_6.011.041cd">uvāca vacanaṃ ślakṣṇam arthavan madhuraṃ laghu</li>
    </ul>
    <ul id="R_6.011.042">
        <li id="R_6.011.042ab">na bhavantaṃ matiśreṣṭhaṃ samarthaṃ vadatāṃ varam</li>
        <li id="R_6.011.042cd">atiśāyayituṃ śakto bṛhaspatir api bruvan</li>
    </ul>
    <ul id="R_6.011.043">
        <li id="R_6.011.043ab">na vādān nāpi saṃgharṣān nādhikyān na ca kāmataḥ</li>
        <li id="R_6.011.043cd">vakṣyāmi vacanaṃ rājan yathārthaṃ rāmagauravāt</li>
    </ul>
    <ul id="R_6.011.044">
        <li id="R_6.011.044ab">arthānarthanimittaṃ hi yad uktaṃ sacivais tava</li>
        <li id="R_6.011.044cd">tatra doṣaṃ prapaśyāmi kriyā na hy upapadyate</li>
    </ul>
    <ul id="R_6.011.045">
        <li id="R_6.011.045ab">ṛte niyogāt sāmarthyam avaboddhuṃ na śakyate</li>
        <li id="R_6.011.045cd">sahasā viniyogo hi doṣavān pratibhāti me</li>
    </ul>
    <ul id="R_6.011.046">
        <li id="R_6.011.046ab">cārapraṇihitaṃ yuktaṃ yad uktaṃ sacivais tava</li>
        <li id="R_6.011.046cd">arthasyāsaṃbhavāt tatra kāraṇaṃ nopapadyate</li>
    </ul>
    <ul id="R_6.011.047">
        <li id="R_6.011.047ab">adeśa kāle saṃprāpta ity ayaṃ yad vibhīṣaṇaḥ</li>
        <li id="R_6.011.047cd">vivakṣā cātra me 'stīyaṃ tāṃ nibodha yathā mati</li>
    </ul>
    <ul id="R_6.011.048">
        <li id="R_6.011.048ab">sa eṣa deśaḥ kālaś ca bhavatīha yathā tathā</li>
        <li id="R_6.011.048cd">puruṣāt puruṣaṃ prāpya tathā doṣaguṇāv api</li>
    </ul>
    <ul id="R_6.011.049">
        <li id="R_6.011.049ab">daurātmyaṃ rāvaṇe dṛṣṭvā vikramaṃ ca tathā tvayi</li>
        <li id="R_6.011.049cd">yuktam āgamanaṃ tasya sadṛśaṃ tasya buddhitaḥ</li>
    </ul>
    <ul id="R_6.011.050">
        <li id="R_6.011.050ab">ajñātarūpaiḥ puruṣaiḥ sa rājan pṛcchyatām iti</li>
        <li id="R_6.011.050cd">yad uktam atra me prekṣā kā cid asti samīkṣitā</li>
    </ul>
    <ul id="R_6.011.051">
        <li id="R_6.011.051ab">pṛcchyamāno viśaṅketa sahasā buddhimān vacaḥ</li>
        <li id="R_6.011.051cd">tatra mitraṃ praduṣyeta mithyapṛṣṭaṃ sukhāgatam</li>
    </ul>
    <ul id="R_6.011.052">
        <li id="R_6.011.052ab">aśakyaḥ sahasā rājan bhāvo vettuṃ parasya vai</li>
        <li id="R_6.011.052cd">antaḥ svabhāvair gītais tair naipuṇyaṃ paśyatā bhṛśam</li>
    </ul>
    <ul id="R_6.011.053">
        <li id="R_6.011.053ab">na tv asya bruvato jātu lakṣyate duṣṭabhāvatā</li>
        <li id="R_6.011.053cd">prasannaṃ vadanaṃ cāpi tasmān me nāsti saṃśayaḥ</li>
    </ul>
    <ul id="R_6.011.054">
        <li id="R_6.011.054ab">aśaṅkitamatiḥ svastho na śaṭhaḥ parisarpati</li>
        <li id="R_6.011.054cd">na cāsya duṣṭā vāk cāpi tasmān nāstīha saṃśayaḥ</li>
    </ul>
    <ul id="R_6.011.055">
        <li id="R_6.011.055ab">ākāraś chādyamāno 'pi na śakyo vinigūhitum</li>
        <li id="R_6.011.055cd">balād dhi vivṛṇoty eva bhāvam antargataṃ nṛṇām</li>
    </ul>
    <ul id="R_6.011.056">
        <li id="R_6.011.056ab">deśakālopapannaṃ ca kāryaṃ kāryavidāṃ vara</li>
        <li id="R_6.011.056cd">saphalaṃ kurute kṣipraṃ prayogeṇābhisaṃhitam</li>
    </ul>
    <ul id="R_6.011.057">
        <li id="R_6.011.057ab">udyogaṃ tava saṃprekṣya mithyāvṛttaṃ ca rāvaṇam</li>
        <li id="R_6.011.057cd">vālinaś ca vadhaṃ śrutvā sugrīvaṃ cābhiṣecitam</li>
    </ul>
    <ul id="R_6.011.058">
        <li id="R_6.011.058ab">rājyaṃ prārthayamānaś ca buddhipūrvam ihāgataḥ</li>
        <li id="R_6.011.058cd">etāvat tu puraskṛtya yujyate tv asya saṃgrahaḥ</li>
    </ul>
    <ul id="R_6.011.059">
        <li id="R_6.011.059ab">yathāśakti mayoktaṃ tu rākṣasasyārjavaṃ prati</li>
        <li id="R_6.011.059cd">tvaṃ pramāṇaṃ tu śeṣasya śrutvā buddhimatāṃ vara</li>
    </ul>
    <ul id="R_6.012.001">
        <li id="R_6.012.001ab">atha rāmaḥ prasannātmā śrutvā vāyusutasya ha</li>
        <li id="R_6.012.001cd">pratyabhāṣata durdharṣaḥ śrutavān ātmani sthitam</li>
    </ul>
    <ul id="R_6.012.002">
        <li id="R_6.012.002ab">mamāpi tu vivakṣāsti kā cit prati vibhīṣaṇam</li>
        <li id="R_6.012.002cd">śrutam icchāmi tat sarvaṃ bhavadbhiḥ śreyasi sthitaiḥ</li>
    </ul>
    <ul id="R_6.012.003">
        <li id="R_6.012.003ab">mitrabhāvena saṃprāptaṃ na tyajeyaṃ kathaṃ cana</li>
        <li id="R_6.012.003cd">doṣo yady api tasya syāt satām etad agarhitam</li>
    </ul>
    <ul id="R_6.012.004">
        <li id="R_6.012.004ab">rāmasya vacanaṃ śrutvā sugrīvaḥ plavageśvaraḥ</li>
        <li id="R_6.012.004cd">pratyabhāṣata kākutsthaṃ sauhārdenābhicoditaḥ</li>
    </ul>
    <ul id="R_6.012.005">
        <li id="R_6.012.005ab">kim atra citraṃ dharmajña lokanāthaśikhāmaṇe</li>
        <li id="R_6.012.005cd">yat tvam āryaṃ prabhāṣethāḥ sattvavān sapathe sthitaḥ</li>
    </ul>
    <ul id="R_6.012.006">
        <li id="R_6.012.006ab">mama cāpy antarātmāyaṃ śuddhiṃ vetti vibhīṣaṇam</li>
        <li id="R_6.012.006cd">anumanāc ca bhāvāc ca sarvataḥ suparīkṣitaḥ</li>
    </ul>
    <ul id="R_6.012.007">
        <li id="R_6.012.007ab">tasmāt kṣipraṃ sahāsmābhis tulyo bhavatu rāghava</li>
        <li id="R_6.012.007cd">vibhīṣaṇo mahāprājñaḥ sakhitvaṃ cābhyupaitu naḥ</li>
    </ul>
    <ul id="R_6.012.008">
        <li id="R_6.012.008ab">sa sugrīvasya tad vākyaṃ rāmaḥ śrutvā vimṛśya ca</li>
        <li id="R_6.012.008cd">tataḥ śubhataraṃ vākyam uvāca haripuṃgavam</li>
    </ul>
    <ul id="R_6.012.009">
        <li id="R_6.012.009ab">suduṣṭo vāpy aduṣṭo vā kim eṣa rajanīcaraḥ</li>
        <li id="R_6.012.009cd">sūkṣmam apy ahitaṃ kartuṃ mamāśaktaḥ kathaṃ cana</li>
    </ul>
    <ul id="R_6.012.010">
        <li id="R_6.012.010ab">piśācān dānavān yakṣān pṛthivyāṃ caiva rākṣasān</li>
        <li id="R_6.012.010cd">aṅgulyagreṇa tān hanyām icchan harigaṇeśvara</li>
    </ul>
    <ul id="R_6.012.011">
        <li id="R_6.012.011ab">śrūyate hi kapotena śatruḥ śaraṇam āgataḥ</li>
        <li id="R_6.012.011cd">arcitaś ca yathānyāyaṃ svaiś ca māṃsair nimantritaḥ</li>
    </ul>
    <ul id="R_6.012.012">
        <li id="R_6.012.012ab">sa hi taṃ pratijagrāha bhāryā hartāram āgatam</li>
        <li id="R_6.012.012cd">kapoto vānaraśreṣṭha kiṃ punar madvidho janaḥ</li>
    </ul>
    <ul id="R_6.012.013">
        <li id="R_6.012.013ab">ṛṣeḥ kaṇvasya putreṇa kaṇḍunā paramarṣiṇā</li>
        <li id="R_6.012.013cd">śṛṇu gāthāṃ purā gītāṃ dharmiṣṭhāṃ satyavādinā</li>
    </ul>
    <ul id="R_6.012.014">
        <li id="R_6.012.014ab">baddhāñjalipuṭaṃ dīnaṃ yācantaṃ śaraṇāgatam</li>
        <li id="R_6.012.014cd">na hanyād ānṛśaṃsyārtham api śatruṃ paraṃ pata</li>
    </ul>
    <ul id="R_6.012.015">
        <li id="R_6.012.015ab">ārto vā yadi vā dṛptaḥ pareṣāṃ śaraṇaṃ gataḥ</li>
        <li id="R_6.012.015cd">ariḥ prāṇān parityajya rakṣitavyaḥ kṛtātmanā</li>
    </ul>
    <ul id="R_6.012.016">
        <li id="R_6.012.016ab">sa ced bhayād vā mohād vā kāmād vāpi na rakṣati</li>
        <li id="R_6.012.016cd">svayā śaktyā yathātattvaṃ tat pāpaṃ lokagarhitam</li>
    </ul>
    <ul id="R_6.012.017">
        <li id="R_6.012.017ab">vinaṣṭaḥ paśyatas tasya rakṣiṇaḥ śaraṇāgataḥ</li>
        <li id="R_6.012.017cd">ādāya sukṛtaṃ tasya sarvaṃ gacched arakṣitaḥ</li>
    </ul>
    <ul id="R_6.012.018">
        <li id="R_6.012.018ab">evaṃ doṣo mahān atra prapannānām arakṣaṇe</li>
        <li id="R_6.012.018cd">asvargyaṃ cāyaśasyaṃ ca balavīryavināśanam</li>
    </ul>
    <ul id="R_6.012.019">
        <li id="R_6.012.019ab">kariṣyāmi yathārthaṃ tu kaṇḍor vacanam uttamam</li>
        <li id="R_6.012.019cd">dharmiṣṭhaṃ ca yaśasyaṃ ca svargyaṃ syāt tu phalodaye</li>
    </ul>
    <ul id="R_6.012.020">
        <li id="R_6.012.020ab">sakṛd eva prapannāya tavāsmīti ca yācate</li>
        <li id="R_6.012.020cd">abhayaṃ sarvabhūtebhyo dadāmy etad vrataṃ mama</li>
    </ul>
    <ul id="R_6.012.021">
        <li id="R_6.012.021ab">ānayainaṃ hariśreṣṭha dattam asyābhayaṃ mayā</li>
        <li id="R_6.012.021cd">vibhīṣaṇo vā sugrīva yadi vā rāvaṇaḥ svayam</li>
    </ul>
    <ul id="R_6.012.022">
        <li id="R_6.012.022ab">
            <seg type="pāda" n="a">tatas tu sugrīvavaco niśamya tad</seg>
            <seg type="pāda" n="b">dharīśvareṇābhihitaṃ nareśvaraḥ</seg>
        </li>
        <li id="R_6.012.022cd">
            <seg type="pāda" n="c">vibhīṣaṇenāśu jagāma saṃgamaṃ</seg>
            <seg type="pāda" n="d">patatrirājena yathā puraṃdaraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.013.001">
        <li id="R_6.013.001ab">rāghaveṇābhaye datte saṃnato rāvaṇānujaḥ</li>
        <li id="R_6.013.001cd">khāt papātāvaniṃ hṛṣṭo bhaktair anucaraiḥ saha</li>
    </ul>
    <ul id="R_6.013.002">
        <li id="R_6.013.002ab">sa tu rāmasya dharmātmā nipapāta vibhīṣaṇaḥ</li>
        <li id="R_6.013.002cd">pādayoḥ śaraṇānveṣī caturbhiḥ saha rākṣasaiḥ</li>
    </ul>
    <ul id="R_6.013.003">
        <li id="R_6.013.003ab">abravīc ca tadā rāmaṃ vākyaṃ tatra vibhīṣaṇaḥ</li>
        <li id="R_6.013.003cd">dharmayuktaṃ ca yuktaṃ ca sāmprataṃ saṃpraharṣaṇam</li>
    </ul>
    <ul id="R_6.013.004">
        <li id="R_6.013.004ab">anujo rāvaṇasyāhaṃ tena cāsmy avamānitaḥ</li>
        <li id="R_6.013.004cd">bhavantaṃ sarvabhūtānāṃ śaraṇyaṃ śaraṇaṃ gataḥ</li>
    </ul>
    <ul id="R_6.013.005">
        <li id="R_6.013.005ab">parityaktā mayā laṅkā mitrāṇi ca dhanāni ca</li>
        <li id="R_6.013.005cd">bhavadgataṃ me rājyaṃ ca jīvitaṃ ca sukhāni ca</li>
    </ul>
    <ul id="R_6.013.006">
        <li id="R_6.013.006ab">rākṣasānāṃ vadhe sāhyaṃ laṅkāyāś ca pradharṣaṇe</li>
        <li id="R_6.013.006cd">kariṣyāmi yathāprāṇaṃ pravekṣyāmi ca vāhinīm</li>
    </ul>
    <ul id="R_6.013.007">
        <li id="R_6.013.007ab">iti bruvāṇaṃ rāmas tu pariṣvajya vibhīṣaṇam</li>
        <li id="R_6.013.007cd">abravīl lakṣmaṇaṃ prītaḥ samudrāj jalam ānaya</li>
    </ul>
    <ul id="R_6.013.008">
        <li id="R_6.013.008ab">tena cemaṃ mahāprājñam abhiṣiñca vibhīṣaṇam</li>
        <li id="R_6.013.008cd">rājānaṃ rakṣasāṃ kṣipraṃ prasanne mayi mānada</li>
    </ul>
    <ul id="R_6.013.009">
        <li id="R_6.013.009ab">evam uktas tu saumitrir abhyaṣiñcad vibhīṣaṇam</li>
        <li id="R_6.013.009cd">madhye vānaramukhyānāṃ rājānaṃ rāmaśāsanāt</li>
    </ul>
    <ul id="R_6.013.010">
        <li id="R_6.013.010ab">taṃ prasādaṃ tu rāmasya dṛṣṭvā sadyaḥ plavaṃgamāḥ</li>
        <li id="R_6.013.010cd">pracukruśur mahānādān sādhu sādhv iti cābruvan</li>
    </ul>
    <ul id="R_6.013.011">
        <li id="R_6.013.011ab">abravīc ca hanūmāṃś ca sugrīvaś ca vibhīṣaṇam</li>
        <li id="R_6.013.011cd">kathaṃ sāgaram akṣobhyaṃ tarāma varuṇālayam</li>
    </ul>
    <ul id="R_6.013.012">
        <li id="R_6.013.012ab">upāyair abhigacchāmo yathā nadanadīpatim</li>
        <li id="R_6.013.012cd">tarāma tarasā sarve sasainyā varuṇālayam</li>
    </ul>
    <ul id="R_6.013.013">
        <li id="R_6.013.013ab">evam uktas tu dharmajñaḥ pratyuvāca vibhīṣaṇaḥ</li>
        <li id="R_6.013.013cd">samudraṃ rāghavo rājā śaraṇaṃ gantum arhati</li>
    </ul>
    <ul id="R_6.013.014">
        <li id="R_6.013.014ab">khānitaḥ sagareṇāyam aprameyo mahodadhiḥ</li>
        <li id="R_6.013.014cd">kartum arhati rāmasya jñāteḥ kāryaṃ mahodadhiḥ</li>
    </ul>
    <ul id="R_6.013.015">
        <li id="R_6.013.015ab">evaṃ vibhīṣaṇenokte rākṣasena vipaścitā</li>
        <li id="R_6.013.015cd">prakṛtyā dharmaśīlasya rāghavasyāpy arocata</li>
    </ul>
    <ul id="R_6.013.016">
        <li id="R_6.013.016ab">sa lakṣmaṇaṃ mahātejāḥ sugrīvaṃ ca harīśvaram</li>
        <li id="R_6.013.016cd">satkriyārthaṃ kriyādakṣaḥ smitapūrvam uvāca ha</li>
    </ul>
    <ul id="R_6.013.017">
        <li id="R_6.013.017ab">vibhīṣaṇasya mantro 'yaṃ mama lakṣmaṇa rocate</li>
        <li id="R_6.013.017cd">brūhi tvaṃ sahasugrīvas tavāpi yadi rocate</li>
    </ul>
    <ul id="R_6.013.018">
        <li id="R_6.013.018ab">sugrīvaḥ paṇḍito nityaṃ bhavān mantravicakṣaṇaḥ</li>
        <li id="R_6.013.018cd">ubhābhyāṃ saṃpradhāryāryaṃ rocate yat tad ucyatām</li>
    </ul>
    <ul id="R_6.013.019">
        <li id="R_6.013.019ab">evam uktau tu tau vīrāv ubhau sugrīvalakṣmaṇau</li>
        <li id="R_6.013.019cd">samudācāra saṃyuktam idaṃ vacanam ūcatuḥ</li>
    </ul>
    <ul id="R_6.013.020">
        <li id="R_6.013.020ab">kimarthaṃ no naravyāghra na rociṣyati rāghava</li>
        <li id="R_6.013.020cd">vibhīṣaṇena yat tūktam asmin kāle sukhāvaham</li>
    </ul>
    <ul id="R_6.013.021">
        <li id="R_6.013.021ab">abaddhvā sāgare setuṃ ghore 'smin varuṇālaye</li>
        <li id="R_6.013.021cd">laṅkā nāsādituṃ śakyā sendrair api surāsuraiḥ</li>
    </ul>
    <ul id="R_6.013.022">
        <li id="R_6.013.022ab">vibhīṣaṇasya śūrasya yathārthaṃ kriyatāṃ vacaḥ</li>
        <li id="R_6.013.022cd">alaṃ kālātyayaṃ kṛtvā samudro 'yaṃ niyujyatām</li>
    </ul>
    <ul id="R_6.013.023">
        <li id="R_6.013.023ab">evam uktaḥ kuśāstīrṇe tīre nadanadīpateḥ</li>
        <li id="R_6.013.023cd">saṃviveśa tadā rāmo vedyām iva hutāśanaḥ</li>
    </ul>
    <ul id="R_6.014.001">
        <li id="R_6.014.001ab">tasya rāmasya suptasya kuśāstīrṇe mahītale</li>
        <li id="R_6.014.001cd">niyamād apramattasya niśās tisro 'ticakramuḥ</li>
    </ul>
    <ul id="R_6.014.002">
        <li id="R_6.014.002ab">na ca darśayate mandas tadā rāmasya sāgaraḥ</li>
        <li id="R_6.014.002cd">prayatenāpi rāmeṇa yathārham abhipūjitaḥ</li>
    </ul>
    <ul id="R_6.014.003">
        <li id="R_6.014.003ab">samudrasya tataḥ kruddho rāmo raktāntalocanaḥ</li>
        <li id="R_6.014.003cd">samīpastham uvācedaṃ lakṣmaṇaṃ śubhalakṣmaṇam</li>
    </ul>
    <ul id="R_6.014.004">
        <li id="R_6.014.004ab">paśya tāvad anāryasya pūjyamānasya lakṣmaṇa</li>
        <li id="R_6.014.004cd">avalepaṃ samudrasya na darśayati yat svayam</li>
    </ul>
    <ul id="R_6.014.005">
        <li id="R_6.014.005ab">praśamaś ca kṣamā caiva ārjavaṃ priyavāditā</li>
        <li id="R_6.014.005cd">asāmarthyaṃ phalanty ete nirguṇeṣu satāṃ guṇāḥ</li>
    </ul>
    <ul id="R_6.014.006">
        <li id="R_6.014.006ab">ātmapraśaṃsinaṃ duṣṭaṃ dhṛṣṭaṃ viparidhāvakam</li>
        <li id="R_6.014.006cd">sarvatrotsṛṣṭadaṇḍaṃ ca lokaḥ satkurute naram</li>
    </ul>
    <ul id="R_6.014.007">
        <li id="R_6.014.007ab">na sāmnā śakyate kīrtir na sāmnā śakyate yaśaḥ</li>
        <li id="R_6.014.007cd">prāptuṃ lakṣmaṇa loke 'smiñ jayo vā raṇamūdhani</li>
    </ul>
    <ul id="R_6.014.008">
        <li id="R_6.014.008ab">adya madbāṇanirbhinnair makarair makarālayam</li>
        <li id="R_6.014.008cd">niruddhatoyaṃ saumitre plavadbhiḥ paśya sarvataḥ</li>
    </ul>
    <ul id="R_6.014.009">
        <li id="R_6.014.009ab">mahābhogāni matsyānāṃ kariṇāṃ ca karān iha</li>
        <li id="R_6.014.009cd">bhogāṃś ca paśya nāgānāṃ mayā bhinnāni lakṣmaṇa</li>
    </ul>
    <ul id="R_6.014.010">
        <li id="R_6.014.010ab">saśaṅkhaśuktikā jālaṃ samīnamakaraṃ śaraiḥ</li>
        <li id="R_6.014.010cd">adya yuddhena mahatā samudraṃ pariśoṣaye</li>
    </ul>
    <ul id="R_6.014.011">
        <li id="R_6.014.011ab">kṣamayā hi samāyuktaṃ mām ayaṃ makarālayaḥ</li>
        <li id="R_6.014.011cd">asamarthaṃ vijānāti dhik kṣamām īdṛśe jane</li>
    </ul>
    <ul id="R_6.014.012">
        <li id="R_6.014.012ab">cāpam ānaya saumitre śarāṃś cāśīviṣopamān</li>
        <li id="R_6.014.012cd">adyākṣobhyam api kruddhaḥ kṣobhayiṣyāmi sāgaram</li>
    </ul>
    <ul id="R_6.014.013">
        <li id="R_6.014.013ab">velāsu kṛtamaryādaṃ sahasormisamākulam</li>
        <li id="R_6.014.013cd">nirmaryādaṃ kariṣyāmi sāyakair varuṇālayam</li>
    </ul>
    <ul id="R_6.014.014">
        <li id="R_6.014.014ab">evam uktvā dhanuṣpāṇiḥ krodhavisphāritekṣaṇaḥ</li>
        <li id="R_6.014.014cd">babhūva rāmo durdharṣo yugāntāgnir iva jvalan</li>
    </ul>
    <ul id="R_6.014.015">
        <li id="R_6.014.015ab">saṃpīḍya ca dhanur ghoraṃ kampayitvā śarair jagat</li>
        <li id="R_6.014.015cd">mumoca viśikhān ugrān vajrāṇīva śatakratuḥ</li>
    </ul>
    <ul id="R_6.014.016">
        <li id="R_6.014.016ab">te jvalanto mahāvegās tejasā sāyakottamāḥ</li>
        <li id="R_6.014.016cd">praviśanti samudrasya salilaṃ trastapannagam</li>
    </ul>
    <ul id="R_6.014.017">
        <li id="R_6.014.017ab">tato vegaḥ samudrasya sanakramakaro mahān</li>
        <li id="R_6.014.017cd">saṃbabhūva mahāghoraḥ samārutaravas tadā</li>
    </ul>
    <ul id="R_6.014.018">
        <li id="R_6.014.018ab">mahormimālāvitataḥ śaṅkhaśuktisamākulaḥ</li>
        <li id="R_6.014.018cd">sadhūmaparivṛttormiḥ sahasābhūn mahodadhiḥ</li>
    </ul>
    <ul id="R_6.014.019">
        <li id="R_6.014.019ab">vyathitāḥ pannagāś cāsan dīptāsyā dīptalocanāḥ</li>
        <li id="R_6.014.019cd">dānavāś ca mahāvīryāḥ pātālatalavāsinaḥ</li>
    </ul>
    <ul id="R_6.014.020">
        <li id="R_6.014.020ab">ūrmayaḥ sindhurājasya sanakramakarās tadā</li>
        <li id="R_6.014.020cd">vindhyamandarasaṃkāśāḥ samutpetuḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_6.014.021">
        <li id="R_6.014.021ab">āghūrṇitataraṅgaughaḥ saṃbhrāntoragarākṣasaḥ</li>
        <li id="R_6.014.021cd">udvartita mahāgrāhaḥ saṃvṛttaḥ salilāśayaḥ</li>
    </ul>
    <ul id="R_6.015.001">
        <li id="R_6.015.001ab">tato madhyāt samudrasya sāgaraḥ svayam utthitaḥ</li>
        <li id="R_6.015.001cd">udayan hi mahāśailān meror iva divākaraḥ</li>
        <li id="R_6.015.001ef">pannagaiḥ saha dīptāsyaiḥ samudraḥ pratyadṛśyata</li>
    </ul>
    <ul id="R_6.015.002">
        <li id="R_6.015.002ab">snigdhavaidūryasaṃkāśo jāmbūnadavibhūṣitaḥ</li>
        <li id="R_6.015.002cd">raktamālyāmbaradharaḥ padmapatranibhekṣaṇaḥ</li>
    </ul>
    <ul id="R_6.015.003">
        <li id="R_6.015.003ab">sāgaraḥ samatikramya pūrvam āmantrya vīryavān</li>
        <li id="R_6.015.003cd">abravīt prāñjalir vākyaṃ rāghavaṃ śarapāṇinam</li>
    </ul>
    <ul id="R_6.015.004">
        <li id="R_6.015.004ab">pṛthivī vāyur ākāśam āpo jyotiś ca rāghavaḥ</li>
        <li id="R_6.015.004cd">svabhāve saumya tiṣṭhanti śāśvataṃ mārgam āśritāḥ</li>
    </ul>
    <ul id="R_6.015.005">
        <li id="R_6.015.005ab">tat svabhāvo mamāpy eṣa yad agādho 'ham aplavaḥ</li>
        <li id="R_6.015.005cd">vikāras tu bhaved rādha etat te pravadāmy aham</li>
    </ul>
    <ul id="R_6.015.006">
        <li id="R_6.015.006ab">na kāmān na ca lobhād vā na bhayāt pārthivātmaja</li>
        <li id="R_6.015.006cd">grāhanakrākulajalaṃ stambhayeyaṃ kathaṃ cana</li>
    </ul>
    <ul id="R_6.015.007">
        <li id="R_6.015.007ab">vidhāsye rāma yenāpi viṣahiṣye hy ahaṃ tathā</li>
        <li id="R_6.015.007cd">grāhā na prahariṣyanti yāvat senā tariṣyati</li>
    </ul>
    <ul id="R_6.015.008">
        <li id="R_6.015.008ab">ayaṃ saumya nalo nāma tanujo viśvakarmaṇaḥ</li>
        <li id="R_6.015.008cd">pitrā dattavaraḥ śrīmān pratimo viśvakarmaṇaḥ</li>
    </ul>
    <ul id="R_6.015.009">
        <li id="R_6.015.009ab">eṣa setuṃ mahotsāhaḥ karotu mayi vānaraḥ</li>
        <li id="R_6.015.009cd">tam ahaṃ dhārayiṣyāmi tathā hy eṣa yathā pitā</li>
    </ul>
    <ul id="R_6.015.010">
        <li id="R_6.015.010ab">evam uktvodadhir naṣṭaḥ samutthāya nalas tataḥ</li>
        <li id="R_6.015.010cd">abravīd vānaraśreṣṭho vākyaṃ rāmaṃ mahābalaḥ</li>
    </ul>
    <ul id="R_6.015.011">
        <li id="R_6.015.011ab">ahaṃ setuṃ kariṣyāmi vistīrṇe varuṇālaye</li>
        <li id="R_6.015.011cd">pituḥ sāmarthyam āsthāya tattvam āha mahodadhiḥ</li>
    </ul>
    <ul id="R_6.015.012">
        <li id="R_6.015.012ab">mama mātur varo datto mandare viśvakarmaṇā</li>
        <li id="R_6.015.012cd">aurasas tasya putro 'haṃ sadṛśo viśvakarmaṇā</li>
    </ul>
    <ul id="R_6.015.013">
        <li id="R_6.015.013ab">na cāpy aham anukto vai prabrūyām ātmano guṇān</li>
        <li id="R_6.015.013cd">kāmam adyaiva badhnantu setuṃ vānarapuṃgavāḥ</li>
    </ul>
    <ul id="R_6.015.014">
        <li id="R_6.015.014ab">tato nisṛṣṭarāmeṇa sarvato hariyūthapāḥ</li>
        <li id="R_6.015.014cd">abhipetur mahāraṇyaṃ hṛṣṭāḥ śatasahasraśaḥ</li>
    </ul>
    <ul id="R_6.015.015">
        <li id="R_6.015.015ab">te nagān nagasaṃkāśāḥ śākhāmṛgagaṇarṣabhāḥ</li>
        <li id="R_6.015.015cd">babhañjur vānarās tatra pracakarṣuś ca sāgaram</li>
    </ul>
    <ul id="R_6.015.016">
        <li id="R_6.015.016ab">te sālaiś cāśvakarṇaiś ca dhavair vaṃśaiś ca vānarāḥ</li>
        <li id="R_6.015.016cd">kuṭajair arjunais tālais tikalais timiśair api</li>
    </ul>
    <ul id="R_6.015.017">
        <li id="R_6.015.017ab">bilvakaiḥ saptaparṇaiś ca karṇikāraiś ca puṣpitaiḥ</li>
        <li id="R_6.015.017cd">cūtaiś cāśokavṛkṣaiś ca sāgaraṃ samapūrayan</li>
    </ul>
    <ul id="R_6.015.018">
        <li id="R_6.015.018ab">samūlāṃś ca vimūlāṃś ca pādapān harisattamāḥ</li>
        <li id="R_6.015.018cd">indraketūn ivodyamya prajahrur harayas tarūn</li>
    </ul>
    <ul id="R_6.015.019">
        <li id="R_6.015.019ab">prakṣipyamāṇair acalaiḥ sahasā jalam uddhatam</li>
        <li id="R_6.015.019cd">samutpatitam ākāśam apāsarpat tatas tataḥ</li>
    </ul>
    <ul id="R_6.015.020">
        <li id="R_6.015.020ab">daśayojanavistīrṇaṃ śatayojanam āyatam</li>
        <li id="R_6.015.020cd">nalaś cakre mahāsetuṃ madhye nadanadīpateḥ</li>
    </ul>
    <ul id="R_6.015.021">
        <li id="R_6.015.021ab">śilānāṃ kṣipyamāṇānāṃ śailānāṃ tatra pātyatām</li>
        <li id="R_6.015.021cd">babhūva tumulaḥ śabdas tadā tasmin mahodadhau</li>
    </ul>
    <ul id="R_6.015.022">
        <li id="R_6.015.022ab">sa nalena kṛtaḥ setuḥ sāgare makarālaye</li>
        <li id="R_6.015.022cd">śuśubhe subhagaḥ śrīmān svātīpatha ivāmbare</li>
    </ul>
    <ul id="R_6.015.023">
        <li id="R_6.015.023ab">tato devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
    </ul>
    <ul id="R_6.015.024">
        <li id="R_6.015.024ab">āplavantaḥ plavantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_6.015.024cd">tam acintyam asahyaṃ ca adbhutaṃ lomaharṣaṇam</li>
        <li id="R_6.015.024ef">dadṛśuḥ sarvabhūtāni sāgare setubandhanam</li>
    </ul>
    <ul id="R_6.015.025">
        <li id="R_6.015.025ab">tāni koṭisahasrāṇi vānarāṇāṃ mahaujasām</li>
        <li id="R_6.015.025cd">badhnantaḥ sāgare setuṃ jagmuḥ pāraṃ mahodadheḥ</li>
    </ul>
    <ul id="R_6.015.026">
        <li id="R_6.015.026ab">viśālaḥ sukṛtaḥ śrīmān subhūmiḥ susamāhitaḥ</li>
        <li id="R_6.015.026cd">aśobhata mahāsetuḥ sīmanta iva sāgare</li>
    </ul>
    <ul id="R_6.015.027">
        <li id="R_6.015.027ab">tataḥ pare samudrasya gadāpāṇir vibhīṣaṇaḥ</li>
        <li id="R_6.015.027cd">pareṣām abhighatārtham atiṣṭhat sacivaiḥ saha</li>
    </ul>
    <ul id="R_6.015.028">
        <li id="R_6.015.028ab">agratas tasya sainyasya śrīmān rāmaḥ salakṣmaṇaḥ</li>
        <li id="R_6.015.028cd">jagāma dhanvī dharmātmā sugrīveṇa samanvitaḥ</li>
    </ul>
    <ul id="R_6.015.029">
        <li id="R_6.015.029ab">anye madhyena gacchanti pārśvato 'nye plavaṃgamāḥ</li>
        <li id="R_6.015.029cd">salile prapatanty anye mārgam anye na lebhire</li>
        <li id="R_6.015.029ef">ke cid vaihāyasa gatāḥ suparṇā iva pupluvuḥ</li>
    </ul>
    <ul id="R_6.015.030">
        <li id="R_6.015.030ab">ghoṣeṇa mahatā ghoṣaṃ sāgarasya samucchritam</li>
        <li id="R_6.015.030cd">bhīmam antardadhe bhīmā tarantī harivāhinī</li>
    </ul>
    <ul id="R_6.015.031">
        <li id="R_6.015.031ab">vānarāṇāṃ hi sā tīrṇā vāhinī nala setunā</li>
        <li id="R_6.015.031cd">tīre niviviśe rājñā bahumūlaphalodake</li>
    </ul>
    <ul id="R_6.015.032">
        <li id="R_6.015.032ab">
            <seg type="pāda" n="a">tad adbhutaṃ rāghava karma duṣkaraṃ</seg>
            <seg type="pāda" n="b">samīkṣya devāḥ saha siddhacāraṇaiḥ</seg>
        </li>
        <li id="R_6.015.032cd">
            <seg type="pāda" n="c">upetya rāmaṃ sahitā maharṣibhiḥ</seg>
            <seg type="pāda" n="d">samabhyaṣiñcan suśubhair jalaiḥ pṛthak</seg>
        </li>
    </ul>
    <ul id="R_6.015.033">
        <li id="R_6.015.033ab">
            <seg type="pāda" n="a">jayasva śatrūn naradeva medinīṃ</seg>
            <seg type="pāda" n="b">sasāgarāṃ pālaya śāśvatīḥ samāḥ</seg>
        </li>
        <li id="R_6.015.033cd">
            <seg type="pāda" n="c">itīva rāmaṃ naradevasatkṛtaṃ</seg>
            <seg type="pāda" n="d">śubhair vacobhir vividhair apūjayan</seg>
        </li>
    </ul>
    <ul id="R_6.016.001">
        <li id="R_6.016.001ab">sabale sāgaraṃ tīrṇe rāme daśarathātmaje</li>
        <li id="R_6.016.001cd">amātyau rāvaṇaḥ śrīmān abravīc chukasāraṇau</li>
    </ul>
    <ul id="R_6.016.002">
        <li id="R_6.016.002ab">samagraṃ sāgaraṃ tīrṇaṃ dustaraṃ vānaraṃ balam</li>
        <li id="R_6.016.002cd">abhūtapūrvaṃ rāmeṇa sāgare setubandhanam</li>
    </ul>
    <ul id="R_6.016.003">
        <li id="R_6.016.003ab">sāgare setubandhaṃ tu na śraddadhyāṃ kathaṃ cana</li>
        <li id="R_6.016.003cd">avaśyaṃ cāpi saṃkhyeyaṃ tan mayā vānaraṃ balam</li>
    </ul>
    <ul id="R_6.016.004">
        <li id="R_6.016.004ab">bhavantau vānaraṃ sainyaṃ praviśyānupalakṣitau</li>
        <li id="R_6.016.004cd">parimāṇaṃ ca vīryaṃ ca ye ca mukhyāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.016.005">
        <li id="R_6.016.005ab">mantriṇo ye ca rāmasya sugrīvasya ca saṃmatāḥ</li>
        <li id="R_6.016.005cd">ye pūrvam abhivartante ye ca śūrāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.016.006">
        <li id="R_6.016.006ab">sa ca setur yathā baddhaḥ sāgare salilārṇave</li>
        <li id="R_6.016.006cd">niveśaś ca yathā teṣāṃ vānarāṇāṃ mahātmanām</li>
    </ul>
    <ul id="R_6.016.007">
        <li id="R_6.016.007ab">rāmasya vyavasāyaṃ ca vīryaṃ praharaṇāni ca</li>
        <li id="R_6.016.007cd">lakṣmaṇasya ca vīrasya tattvato jñātum arhatha</li>
    </ul>
    <ul id="R_6.016.008">
        <li id="R_6.016.008ab">kaś ca senāpatis teṣāṃ vānarāṇāṃ mahaujasām</li>
        <li id="R_6.016.008cd">etaj jñātvā yathātattvaṃ śīghram agantum arhathaḥ</li>
    </ul>
    <ul id="R_6.016.009">
        <li id="R_6.016.009ab">iti pratisamādiṣṭau rākṣasau śukasāraṇau</li>
        <li id="R_6.016.009cd">harirūpadharau vīrau praviṣṭau vānaraṃ balam</li>
    </ul>
    <ul id="R_6.016.010">
        <li id="R_6.016.010ab">tatas tad vānaraṃ sainyam acintyaṃ lomaharṣaṇam</li>
        <li id="R_6.016.010cd">saṃkhyātuṃ nādhyagacchetāṃ tadā tau śukasāraṇau</li>
    </ul>
    <ul id="R_6.016.011">
        <li id="R_6.016.011ab">tat sthitaṃ parvatāgreṣu nirdareṣu guhāsu ca</li>
        <li id="R_6.016.011cd">samudrasya ca tīreṣu vaneṣūpavaneṣu ca</li>
    </ul>
    <ul id="R_6.016.012">
        <li id="R_6.016.012ab">taramāṇaṃ ca tīrṇaṃ ca tartukāmaṃ ca sarvaśaḥ</li>
        <li id="R_6.016.012cd">niviṣṭaṃ niviśac caiva bhīmanādaṃ mahābalam</li>
    </ul>
    <ul id="R_6.016.013">
        <li id="R_6.016.013ab">tau dadarśa mahātejāḥ pracchannau ca vibhīṣaṇaḥ</li>
        <li id="R_6.016.013cd">ācacakṣe 'tha rāmāya gṛhītvā śukasāraṇau</li>
        <li id="R_6.016.013ef">laṅkāyāḥ samanuprāptau cārau parapuraṃjayau</li>
    </ul>
    <ul id="R_6.016.014">
        <li id="R_6.016.014ab">tau dṛṣṭvā vyathitau rāmaṃ nirāśau jīvite tadā</li>
        <li id="R_6.016.014cd">kṛtāñjalipuṭau bhītau vacanaṃ cedam ūcatuḥ</li>
    </ul>
    <ul id="R_6.016.015">
        <li id="R_6.016.015ab">āvām ihāgatau saumya rāvaṇaprahitāv ubhau</li>
        <li id="R_6.016.015cd">parijñātuṃ balaṃ kṛtsnaṃ tavedaṃ raghunandana</li>
    </ul>
    <ul id="R_6.016.016">
        <li id="R_6.016.016ab">tayos tad vacanaṃ śrutvā rāmo daśarathātmajaḥ</li>
        <li id="R_6.016.016cd">abravīt prahasan vākyaṃ sarvabhūtahite rataḥ</li>
    </ul>
    <ul id="R_6.016.017">
        <li id="R_6.016.017ab">yadi dṛṣṭaṃ balaṃ kṛtsnaṃ vayaṃ vā susamīkṣitāḥ</li>
        <li id="R_6.016.017cd">yathoktaṃ vā kṛtaṃ kāryaṃ chandataḥ pratigamyatām</li>
    </ul>
    <ul id="R_6.016.018">
        <li id="R_6.016.018ab">praviśya nagarīṃ laṅkāṃ bhavadbhyāṃ dhanadānujaḥ</li>
        <li id="R_6.016.018cd">vaktavyo rakṣasāṃ rājā yathoktaṃ vacanaṃ mama</li>
    </ul>
    <ul id="R_6.016.019">
        <li id="R_6.016.019ab">yad balaṃ ca samāśritya sītāṃ me hṛtavān asi</li>
        <li id="R_6.016.019cd">tad darśaya yathākāmaṃ sasainyaḥ sahabāndhavaḥ</li>
    </ul>
    <ul id="R_6.016.020">
        <li id="R_6.016.020ab">śvaḥkāle nagarīṃ laṅkāṃ saprākārāṃ satoraṇām</li>
        <li id="R_6.016.020cd">rākṣasaṃ ca balaṃ paśya śarair vidhvaṃsitaṃ mayā</li>
    </ul>
    <ul id="R_6.016.021">
        <li id="R_6.016.021ab">ghoraṃ roṣam ahaṃ mokṣye balaṃ dhāraya rāvaṇa</li>
        <li id="R_6.016.021cd">śvaḥkāle vajravān vajraṃ dānaveṣv iva vāsavaḥ</li>
    </ul>
    <ul id="R_6.016.022">
        <li id="R_6.016.022ab">iti pratisamādiṣṭau rākṣasau śukasāraṇau</li>
        <li id="R_6.016.022cd">āgamya nagarīṃ laṅkām abrūtāṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_6.016.023">
        <li id="R_6.016.023ab">vibhīṣaṇagṛhītau tu vadhārhau rākṣaseśvara</li>
        <li id="R_6.016.023cd">dṛṣṭvā dharmātmanā muktau rāmeṇāmitatejasā</li>
    </ul>
    <ul id="R_6.016.024">
        <li id="R_6.016.024ab">ekasthānagatā yatra catvāraḥ puruṣarṣabhāḥ</li>
        <li id="R_6.016.024cd">lokapālopamāḥ śūrāḥ kṛtāstrā dṛḍhavikramāḥ</li>
    </ul>
    <ul id="R_6.016.025">
        <li id="R_6.016.025ab">rāmo dāśarathiḥ śrīmāṃl lakṣmaṇaś ca vibhīṣaṇaḥ</li>
        <li id="R_6.016.025cd">sugrīvaś ca mahātejā mahendrasamavikramaḥ</li>
    </ul>
    <ul id="R_6.016.026">
        <li id="R_6.016.026ab">ete śaktāḥ purīṃ laṅkāṃ saprākārāṃ satoraṇām</li>
        <li id="R_6.016.026cd">utpāṭya saṃkrāmayituṃ sarve tiṣṭhantu vānarāḥ</li>
    </ul>
    <ul id="R_6.016.027">
        <li id="R_6.016.027ab">yādṛśaṃ tasya rāmasya rūpaṃ praharaṇāni ca</li>
        <li id="R_6.016.027cd">vadhiṣyati purīṃ laṅkām ekas tiṣṭhantu te trayaḥ</li>
    </ul>
    <ul id="R_6.016.028">
        <li id="R_6.016.028ab">rāmalakṣmaṇaguptā sā sugrīveṇa ca vāhinī</li>
        <li id="R_6.016.028cd">babhūva durdharṣatarā sarvair api surāsuraiḥ</li>
    </ul>
    <ul id="R_6.016.029">
        <li id="R_6.016.029ab">
            <seg type="pāda" n="a">prahṛṣṭarūpā dhvajinī vanaukasāṃ</seg>
            <seg type="pāda" n="b">mahātmanāṃ saṃprati yoddhum icchatām</seg>
        </li>
        <li id="R_6.016.029cd">
            <seg type="pāda" n="c">alaṃ virodhena śamo vidhīyatāṃ</seg>
            <seg type="pāda" n="d">pradīyatāṃ dāśarathāya maithilī</seg>
        </li>
    </ul>
    <ul id="R_6.017.001">
        <li id="R_6.017.001ab">tad vacaḥ pathyam aklībaṃ sāraṇenābhibhāṣitam</li>
        <li id="R_6.017.001cd">niśamya rāvaṇo rājā pratyabhāṣata sāraṇam</li>
    </ul>
    <ul id="R_6.017.002">
        <li id="R_6.017.002ab">yadi mām abhiyuñjīran devagandharvadānavāḥ</li>
        <li id="R_6.017.002cd">naiva sītāṃ pradāsyāmi sarvalokabhayād api</li>
    </ul>
    <ul id="R_6.017.003">
        <li id="R_6.017.003ab">tvaṃ tu saumya paritrasto haribhir nirjito bhṛśam</li>
        <li id="R_6.017.003cd">pratipradānam adyaiva sītāyāḥ sādhu manyase</li>
        <li id="R_6.017.003ef">ko hi nāma sapatno māṃ samare jetum arhati</li>
    </ul>
    <ul id="R_6.017.004">
        <li id="R_6.017.004ab">ity uktvā paruṣaṃ vākyaṃ rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.017.004cd">āruroha tataḥ śrīmān prāsādaṃ himapāṇḍuram</li>
        <li id="R_6.017.004ef">bahutālasamutsedhaṃ rāvaṇo 'tha didṛkṣayā</li>
    </ul>
    <ul id="R_6.017.005">
        <li id="R_6.017.005ab">tābhyāṃ carābhyāṃ sahito rāvaṇaḥ krodhamūrchitaḥ</li>
        <li id="R_6.017.005cd">paśyamānaḥ samudraṃ ca parvatāṃś ca vanāni ca</li>
        <li id="R_6.017.005ef">dadarśa pṛthivīdeśaṃ susaṃpūrṇaṃ plavaṃgamaiḥ</li>
    </ul>
    <ul id="R_6.017.006">
        <li id="R_6.017.006ab">tad apāram asaṃkhyeyaṃ vānarāṇāṃ mahad balam</li>
        <li id="R_6.017.006cd">ālokya rāvaṇo rājā paripapraccha sāraṇam</li>
    </ul>
    <ul id="R_6.017.007">
        <li id="R_6.017.007ab">eṣāṃ vānaramukhyānāṃ ke śūrāḥ ke mahābalāḥ</li>
        <li id="R_6.017.007cd">ke pūrvam abhivartante mahotsāhāḥ samantataḥ</li>
    </ul>
    <ul id="R_6.017.008">
        <li id="R_6.017.008ab">keṣāṃ śṛṇoti sugrīvaḥ ke vā yūthapayūthapāḥ</li>
        <li id="R_6.017.008cd">sāraṇācakṣva me sarvaṃ ke pradhānāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.017.009">
        <li id="R_6.017.009ab">sāraṇo rākṣasendrasya vacanaṃ paripṛcchataḥ</li>
        <li id="R_6.017.009cd">ācacakṣe 'tha mukhyajño mukhyāṃs tāṃs tu vanaukasaḥ</li>
    </ul>
    <ul id="R_6.017.010">
        <li id="R_6.017.010ab">eṣa yo 'bhimukho laṅkāṃ nardaṃs tiṣṭhati vānaraḥ</li>
        <li id="R_6.017.010cd">yūthapānāṃ sahasrāṇāṃ śatena parivāritaḥ</li>
    </ul>
    <ul id="R_6.017.011">
        <li id="R_6.017.011ab">yasya ghoṣeṇa mahatā saprākārā satoraṇā</li>
        <li id="R_6.017.011cd">laṅkā pravepate sarvā saśailavanakānanā</li>
    </ul>
    <ul id="R_6.017.012">
        <li id="R_6.017.012ab">sarvaśākhāmṛgendrasya sugrīvasya mahātmanaḥ</li>
        <li id="R_6.017.012cd">balāgre tiṣṭhate vīro nīlo nāmaiṣa yūthapaḥ</li>
    </ul>
    <ul id="R_6.017.013">
        <li id="R_6.017.013ab">bāhū pragṛhya yaḥ padbhyāṃ mahīṃ gacchati vīryavān</li>
        <li id="R_6.017.013cd">laṅkām abhimukhaḥ kopād abhīkṣṇaṃ ca vijṛmbhate</li>
    </ul>
    <ul id="R_6.017.014">
        <li id="R_6.017.014ab">giriśṛṅgapratīkāśaḥ padmakiñjalkasaṃnibhaḥ</li>
        <li id="R_6.017.014cd">sphoṭayaty abhisaṃrabdho lāṅgūlaṃ ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.017.015">
        <li id="R_6.017.015ab">yasya lāṅgūlaśabdena svanantīva diśo daśa</li>
        <li id="R_6.017.015cd">eṣa vānararājena surgrīveṇābhiṣecitaḥ</li>
        <li id="R_6.017.015ef">yauvarājye 'ṅgado nāma tvām āhvayati saṃyuge</li>
    </ul>
    <ul id="R_6.017.016">
        <li id="R_6.017.016ab">ye tu viṣṭabhya gātrāṇi kṣveḍayanti nadanti ca</li>
        <li id="R_6.017.016cd">utthāya ca vijṛmbhante krodhena haripuṃgavāḥ</li>
    </ul>
    <ul id="R_6.017.017">
        <li id="R_6.017.017ab">ete duṣprasahā ghorāś caṇḍāś caṇḍaparākramāḥ</li>
        <li id="R_6.017.017cd">aṣṭau śatasahasrāṇi daśakoṭiśatāni ca</li>
    </ul>
    <ul id="R_6.017.018">
        <li id="R_6.017.018ab">ya enam anugacchanti vīrāś candanavāsinaḥ</li>
        <li id="R_6.017.018cd">eṣa āśaṃsate laṅkāṃ svenānīkena marditum</li>
    </ul>
    <ul id="R_6.017.019">
        <li id="R_6.017.019ab">śveto rajatasaṃkāśaḥ sabalo bhīmavikramaḥ</li>
        <li id="R_6.017.019cd">buddhimān vānaraḥ śūras triṣu lokeṣu viśrutaḥ</li>
    </ul>
    <ul id="R_6.017.020">
        <li id="R_6.017.020ab">tūrṇaṃ sugrīvam āgamya punar gacchati vānaraḥ</li>
        <li id="R_6.017.020cd">vibhajan vānarīṃ senām anīkāni praharṣayan</li>
    </ul>
    <ul id="R_6.017.021">
        <li id="R_6.017.021ab">yaḥ purā gomatītīre ramyaṃ paryeti parvatam</li>
        <li id="R_6.017.021cd">nāmnā saṃkocano nāma nānānagayuto giriḥ</li>
    </ul>
    <ul id="R_6.017.022">
        <li id="R_6.017.022ab">tatra rājyaṃ praśāsty eṣa kumudo nāma yūthapaḥ</li>
        <li id="R_6.017.022cd">yo 'sau śatasahasrāṇāṃ sahasraṃ parikarṣati</li>
    </ul>
    <ul id="R_6.017.023">
        <li id="R_6.017.023ab">yasya vālā bahuvyāmā dīrghalāṅgūlam āśritāḥ</li>
        <li id="R_6.017.023cd">tāmrāḥ pītāḥ sitāḥ śvetāḥ prakīrṇā ghorakarmaṇaḥ</li>
    </ul>
    <ul id="R_6.017.024">
        <li id="R_6.017.024ab">adīno roṣaṇaś caṇḍaḥ saṃgrāmam abhikāṅkṣati</li>
        <li id="R_6.017.024cd">eṣaivāśaṃsate laṅkāṃ svenānīkena marditum</li>
    </ul>
    <ul id="R_6.017.025">
        <li id="R_6.017.025ab">yas tv eṣa siṃhasaṃkāśaḥ kapilo dīrghakesaraḥ</li>
        <li id="R_6.017.025cd">nibhṛtaḥ prekṣate laṅkāṃ didhakṣann iva cakṣuṣā</li>
    </ul>
    <ul id="R_6.017.026">
        <li id="R_6.017.026ab">vindhyaṃ kṛṣṇagiriṃ sahyaṃ parvataṃ ca sudarśanam</li>
        <li id="R_6.017.026cd">rājan satatam adhyāste rambho nāmaiṣa yūthapaḥ</li>
    </ul>
    <ul id="R_6.017.027">
        <li id="R_6.017.027ab">śataṃ śatasahasrāṇāṃ triṃśac ca hariyūthapāḥ</li>
        <li id="R_6.017.027cd">parivāryānugacchanti laṅkāṃ marditum ojasā</li>
    </ul>
    <ul id="R_6.017.028">
        <li id="R_6.017.028ab">yas tu karṇau vivṛṇute jṛmbhate ca punaḥ punaḥ</li>
        <li id="R_6.017.028cd">na ca saṃvijate mṛtyor na ca yūthād vidhāvati</li>
    </ul>
    <ul id="R_6.017.029">
        <li id="R_6.017.029ab">mahābalo vītabhayo ramyaṃ sālveya parvatam</li>
        <li id="R_6.017.029cd">rājan satatam adhyāste śarabho nāma yūthapaḥ</li>
    </ul>
    <ul id="R_6.017.030">
        <li id="R_6.017.030ab">etasya balinaḥ sarve vihārā nāma yūthapāḥ</li>
        <li id="R_6.017.030cd">rājañ śatasahasrāṇi catvāriṃśat tathaiva ca</li>
    </ul>
    <ul id="R_6.017.031">
        <li id="R_6.017.031ab">yas tu megha ivākāśaṃ mahān āvṛtya tiṣṭhati</li>
        <li id="R_6.017.031cd">madhye vānaravīrāṇāṃ surāṇām iva vāsavaḥ</li>
    </ul>
    <ul id="R_6.017.032">
        <li id="R_6.017.032ab">bherīṇām iva saṃnādo yasyaiṣa śrūyate mahān</li>
        <li id="R_6.017.032cd">ghoraḥ śākhāmṛgendrāṇāṃ saṃgrāmam abhikāṅkṣatām</li>
    </ul>
    <ul id="R_6.017.033">
        <li id="R_6.017.033ab">eṣa parvatam adhyāste pāriyātram anuttamam</li>
        <li id="R_6.017.033cd">yuddhe duṣprasaho nityaṃ panaso nāma yūthapaḥ</li>
    </ul>
    <ul id="R_6.017.034">
        <li id="R_6.017.034ab">enaṃ śatasahasrāṇāṃ śatārdhaṃ paryupāsate</li>
        <li id="R_6.017.034cd">yūthapā yūthapaśreṣṭhaṃ yeṣāṃ yūthāni bhāgaśaḥ</li>
    </ul>
    <ul id="R_6.017.035">
        <li id="R_6.017.035ab">yas tu bhīmāṃ pravalgantīṃ camūṃ tiṣṭhati śobhayan</li>
        <li id="R_6.017.035cd">sthitāṃ tīre samudrasya dvitīya iva sāgaraḥ</li>
    </ul>
    <ul id="R_6.017.036">
        <li id="R_6.017.036ab">eṣa dardarasaṃkāśo vinato nāma yūthapaḥ</li>
        <li id="R_6.017.036cd">pibaṃś carati parṇāśāṃ nadīnām uttamāṃ nadīm</li>
    </ul>
    <ul id="R_6.017.037">
        <li id="R_6.017.037ab">ṣaṣṭiḥ śatasahasrāṇi balam asya plavaṃgamāḥ</li>
        <li id="R_6.017.037cd">tvām āhvayati yuddhāya krathano nāma yūthapaḥ</li>
    </ul>
    <ul id="R_6.017.038">
        <li id="R_6.017.038ab">yas tu gairikavarṇābhaṃ vapuḥ puṣyati vānaraḥ</li>
        <li id="R_6.017.038cd">gavayo nāma tejasvī tvāṃ krodhād abhivartate</li>
    </ul>
    <ul id="R_6.017.039">
        <li id="R_6.017.039ab">enaṃ śatasahasrāṇi saptatiḥ paryupāsate</li>
        <li id="R_6.017.039cd">eṣa āśaṃsate laṅkāṃ svenānīkena marditum</li>
    </ul>
    <ul id="R_6.017.040">
        <li id="R_6.017.040ab">ete duṣprasahā ghorā balinaḥ kāmarūpiṇaḥ</li>
        <li id="R_6.017.040cd">yūthapā yūthapaśreṣṭhā yeṣāṃ saṃkhyā na vidyate</li>
    </ul>
    <ul id="R_6.018.001">
        <li id="R_6.018.001ab">tāṃs tu te 'haṃ pravakṣyāmi prekṣamāṇasya yūthapān</li>
        <li id="R_6.018.001cd">rāghavārthe parākrāntā ye na rakṣanti jīvitam</li>
    </ul>
    <ul id="R_6.018.002">
        <li id="R_6.018.002ab">snigdhā yasya bahuśyāmā bālā lāṅgūlam āśritāḥ</li>
        <li id="R_6.018.002cd">tāmrāḥ pītāḥ sitāḥ śvetāḥ prakīrṇā ghorakarmaṇaḥ</li>
    </ul>
    <ul id="R_6.018.003">
        <li id="R_6.018.003ab">pragṛhītāḥ prakāśante sūryasyeva marīcayaḥ</li>
        <li id="R_6.018.003cd">pṛthivyāṃ cānukṛṣyante haro nāmaiṣa yūthapaḥ</li>
    </ul>
    <ul id="R_6.018.004">
        <li id="R_6.018.004ab">yaṃ pṛṣṭhato 'nugacchanti śataśo 'tha sahasraśaḥ</li>
        <li id="R_6.018.004cd">drumān udyamya sahitā laṅkārohaṇatatparāḥ</li>
    </ul>
    <ul id="R_6.018.005">
        <li id="R_6.018.005ab">eṣa koṭīsahasreṇa vānarāṇāṃ mahaujasām</li>
        <li id="R_6.018.005cd">ākāṅkṣate tvāṃ saṃgrāme jetuṃ parapuraṃjaya</li>
    </ul>
    <ul id="R_6.018.006">
        <li id="R_6.018.006ab">nīlān iva mahāmeghāṃs tiṣṭhato yāṃs tu paśyasi</li>
        <li id="R_6.018.006cd">asitāñ janasaṃkāśān yuddhe satyaparākramān</li>
    </ul>
    <ul id="R_6.018.007">
        <li id="R_6.018.007ab">nakhadaṃṣṭrāyudhān vīrāṃs tīkṣṇakopān bhayāvahān</li>
        <li id="R_6.018.007cd">asaṃkhyeyān anirdeśyān paraṃ pāram ivodadheḥ</li>
    </ul>
    <ul id="R_6.018.008">
        <li id="R_6.018.008ab">parvateṣu ca ye ke cid viṣameṣu nadīṣu ca</li>
        <li id="R_6.018.008cd">ete tvām abhivartante rājann ṛṣkāḥ sudāruṇāḥ</li>
    </ul>
    <ul id="R_6.018.009">
        <li id="R_6.018.009ab">eṣāṃ madhye sthito rājan bhīmākṣo bhīmadarśanaḥ</li>
        <li id="R_6.018.009cd">parjanya iva jīmūtaiḥ samantāt parivāritaḥ</li>
    </ul>
    <ul id="R_6.018.010">
        <li id="R_6.018.010ab">ṛkṣavantaṃ giriśreṣṭham adhyāste narmadāṃ piban</li>
        <li id="R_6.018.010cd">sarvarkṣāṇām adhipatir dhūmro nāmaiṣa yūthapaḥ</li>
    </ul>
    <ul id="R_6.018.011">
        <li id="R_6.018.011ab">yavīyān asya tu bhrātā paśyainaṃ parvatopamam</li>
        <li id="R_6.018.011cd">bhrātrā samāno rūpeṇa viśiṣṭas tu parākrame</li>
    </ul>
    <ul id="R_6.018.012">
        <li id="R_6.018.012ab">sa eṣa jāmbavān nāma mahāyūthapayūthapaḥ</li>
        <li id="R_6.018.012cd">praśānto guruvartī ca saṃprahāreṣv amarṣaṇaḥ</li>
    </ul>
    <ul id="R_6.018.013">
        <li id="R_6.018.013ab">etena sāhyaṃ sumahat kṛtaṃ śakrasya dhīmatā</li>
        <li id="R_6.018.013cd">devāsure jāmbavatā labdhāś ca bahavo varāḥ</li>
    </ul>
    <ul id="R_6.018.014">
        <li id="R_6.018.014ab">āruhya parvatāgrebhyo mahābhravipulāḥ śilāḥ</li>
        <li id="R_6.018.014cd">muñcanti vipulākārā na mṛtyor udvijanti ca</li>
    </ul>
    <ul id="R_6.018.015">
        <li id="R_6.018.015ab">rākṣasānāṃ ca sadṛśāḥ piśācānāṃ ca romaśāḥ</li>
        <li id="R_6.018.015cd">etasya sainye bahavo vicaranty agnitejasaḥ</li>
    </ul>
    <ul id="R_6.018.016">
        <li id="R_6.018.016ab">yaṃ tv enam abhisaṃrabdhaṃ plavamānam iva sthitam</li>
        <li id="R_6.018.016cd">prekṣante vānarāḥ sarve sthitaṃ yūthapayūthapam</li>
    </ul>
    <ul id="R_6.018.017">
        <li id="R_6.018.017ab">eṣa rājan sahasrākṣaṃ paryupāste harīśvaraḥ</li>
        <li id="R_6.018.017cd">balena balasaṃpanno rambho nāmaiṣa yūthapaḥ</li>
    </ul>
    <ul id="R_6.018.018">
        <li id="R_6.018.018ab">yaḥ sthitaṃ yojane śailaṃ gacchan pārśvena sevate</li>
        <li id="R_6.018.018cd">ūrdhvaṃ tathaiva kāyena gataḥ prāpnoti yojanam</li>
    </ul>
    <ul id="R_6.018.019">
        <li id="R_6.018.019ab">yasmān na paramaṃ rūpaṃ catuṣpādeṣu vidyate</li>
        <li id="R_6.018.019cd">śrutaḥ saṃnādano nāma vānarāṇāṃ pitāmahaḥ</li>
    </ul>
    <ul id="R_6.018.020">
        <li id="R_6.018.020ab">yena yuddhaṃ tadā dattaṃ raṇe śakrasya dhīmatā</li>
        <li id="R_6.018.020cd">parājayaś ca na prāptaḥ so 'yaṃ yūthapayūthapaḥ</li>
        <li id="R_6.018.020ef">yasya vikramamāṇasya śakrasyeva parākramaḥ</li>
    </ul>
    <ul id="R_6.018.021">
        <li id="R_6.018.021ab">eṣa gandharvakanyāyām utpannaḥ kṛṣṇavartmanā</li>
        <li id="R_6.018.021cd">purā devāsure yuddhe sāhyārthaṃ tridivaukasām</li>
    </ul>
    <ul id="R_6.018.022">
        <li id="R_6.018.022ab">yasya vaiśravaṇo rājā jambūm upaniṣevate</li>
        <li id="R_6.018.022cd">yo rājā parvatendrāṇāṃ bahukiṃnarasevinām</li>
    </ul>
    <ul id="R_6.018.023">
        <li id="R_6.018.023ab">vihārasukhado nityaṃ bhrātus te rākṣasādhipa</li>
        <li id="R_6.018.023cd">tatraiṣa vasati śrīmān balavān vānararṣabhaḥ</li>
        <li id="R_6.018.023ef">yuddheṣv akatthano nityaṃ krathano nāma yūthapaḥ</li>
    </ul>
    <ul id="R_6.018.024">
        <li id="R_6.018.024ab">vṛtaḥ koṭisahasreṇa harīṇāṃ samupasthitaḥ</li>
        <li id="R_6.018.024cd">eṣaivāśaṃsate laṅkāṃ svenānīkena marditum</li>
    </ul>
    <ul id="R_6.018.025">
        <li id="R_6.018.025ab">yo gaṅgām anu paryeti trāsayan hastiyūthapān</li>
        <li id="R_6.018.025cd">hastināṃ vānarāṇāṃ ca pūrvavairam anusmaran</li>
    </ul>
    <ul id="R_6.018.026">
        <li id="R_6.018.026ab">eṣa yūthapatir netā gacchan giriguhāśayaḥ</li>
        <li id="R_6.018.026cd">harīṇāṃ vāhinī mukhyo nadīṃ haimavatīm anu</li>
    </ul>
    <ul id="R_6.018.027">
        <li id="R_6.018.027ab">uśīra bījam āśritya parvataṃ mandaropamam</li>
        <li id="R_6.018.027cd">ramate vānaraśreṣṭho divi śakra iva svayam</li>
    </ul>
    <ul id="R_6.018.028">
        <li id="R_6.018.028ab">enaṃ śatasahasrāṇāṃ sahasram abhivartate</li>
        <li id="R_6.018.028cd">eṣa durmarṣaṇo rājan pramāthī nāma yūthapaḥ</li>
    </ul>
    <ul id="R_6.018.029">
        <li id="R_6.018.029ab">vātenevoddhataṃ meghaṃ yam enam anupaśyasi</li>
        <li id="R_6.018.029cd">vivartamānaṃ bahuśo yatraitad bahulaṃ rajaḥ</li>
    </ul>
    <ul id="R_6.018.030">
        <li id="R_6.018.030ab">ete 'sitamukhā ghorā golāṅgūlā mahābalāḥ</li>
        <li id="R_6.018.030cd">śataṃ śatasahasrāṇi dṛṣṭvā vai setubandhanam</li>
    </ul>
    <ul id="R_6.018.031">
        <li id="R_6.018.031ab">golāṅgūlaṃ mahāvegaṃ gavākṣaṃ nāma yūthapam</li>
        <li id="R_6.018.031cd">parivāryābhivartante laṅkāṃ marditum ojasā</li>
    </ul>
    <ul id="R_6.018.032">
        <li id="R_6.018.032ab">bhramarācaritā yatra sarvakāmaphaladrumāḥ</li>
        <li id="R_6.018.032cd">yaṃ sūryatulyavarṇābham anuparyeti parvatam</li>
    </ul>
    <ul id="R_6.018.033">
        <li id="R_6.018.033ab">yasya bhāsā sadā bhānti tadvarṇā mṛgapakṣiṇaḥ</li>
        <li id="R_6.018.033cd">yasya prasthaṃ mahātmāno na tyajanti maharṣayaḥ</li>
    </ul>
    <ul id="R_6.018.034">
        <li id="R_6.018.034ab">tatraiṣa ramate rājan ramye kāñcanaparvate</li>
        <li id="R_6.018.034cd">mukhyo vānaramukhyānāṃ kesarī nāma yūthapaḥ</li>
    </ul>
    <ul id="R_6.018.035">
        <li id="R_6.018.035ab">ṣaṣṭir girisahasrāṇāṃ ramyāḥ kāñcanaparvatāḥ</li>
        <li id="R_6.018.035cd">teṣāṃ madhye girivaras tvam ivānagha rakṣasām</li>
    </ul>
    <ul id="R_6.018.036">
        <li id="R_6.018.036ab">tatraite kapilāḥ śvetās tāmrāsyā madhupiṅgalāḥ</li>
        <li id="R_6.018.036cd">nivasanty uttamagirau tīkṣṇadaṃṣṭrānakhāyudhāḥ</li>
    </ul>
    <ul id="R_6.018.037">
        <li id="R_6.018.037ab">siṃha iva caturdaṃṣṭrā vyāghrā iva durāsadāḥ</li>
        <li id="R_6.018.037cd">sarve vaiśvanarasamā jvalitāśīviṣopamāḥ</li>
    </ul>
    <ul id="R_6.018.038">
        <li id="R_6.018.038ab">sudīrghāñcitalāṅgūlā mattamātaṃgasaṃnibhāḥ</li>
        <li id="R_6.018.038cd">mahāparvatasaṃkāśā mahājīmūtanisvanāḥ</li>
    </ul>
    <ul id="R_6.018.039">
        <li id="R_6.018.039ab">eṣa caiṣām adhipatir madhye tiṣṭhati vīryavān</li>
        <li id="R_6.018.039cd">nāmnā pṛthivyāṃ vikhyāto rājañ śatabalīti yaḥ</li>
        <li id="R_6.018.039ef">eṣaivāśaṃsate laṅkāṃ svenānīkena marditum</li>
    </ul>
    <ul id="R_6.018.040">
        <li id="R_6.018.040ab">gajo gavākṣo gavayo nalo nīlaś ca vānaraḥ</li>
        <li id="R_6.018.040cd">ekaika eva yūthānāṃ koṭibhir daśabhir vṛtaḥ</li>
    </ul>
    <ul id="R_6.018.041">
        <li id="R_6.018.041ab">tathānye vānaraśreṣṭhā vindhyaparvatavāsinaḥ</li>
        <li id="R_6.018.041cd">na śakyante bahutvāt tu saṃkhyātuṃ laghuvikramāḥ</li>
    </ul>
    <ul id="R_6.018.042">
        <li id="R_6.018.042ab">
            <seg type="pāda" n="a">sarve mahārāja mahāprabhāvāḥ</seg>
            <seg type="pāda" n="b">sarve mahāśailanikāśakāyāḥ</seg>
        </li>
        <li id="R_6.018.042cd">
            <seg type="pāda" n="c">sarve samarthāḥ pṛthivīṃ kṣaṇena</seg>
            <seg type="pāda" n="d">kartuṃ pravidhvastavikīrṇaśailām</seg>
        </li>
    </ul>
    <ul id="R_6.019.001">
        <li id="R_6.019.001ab">sāraṇasya vacaḥ śrutvā rāvaṇaṃ rākṣasādhipam</li>
        <li id="R_6.019.001cd">balam ālokayan sarvaṃ śuko vākyam athābravīt</li>
    </ul>
    <ul id="R_6.019.002">
        <li id="R_6.019.002ab">sthitān paśyasi yān etān mattān iva mahādvipān</li>
        <li id="R_6.019.002cd">nyagrodhān iva gāṅgeyān sālān haimavatīn iva</li>
    </ul>
    <ul id="R_6.019.003">
        <li id="R_6.019.003ab">ete duṣprasahā rājan balinaḥ kāmarūpiṇaḥ</li>
        <li id="R_6.019.003cd">daityadānavasaṃkāśā yuddhe devaparākramāḥ</li>
    </ul>
    <ul id="R_6.019.004">
        <li id="R_6.019.004ab">eṣāṃ koṭisahasrāṇi nava pañcaca sapta ca</li>
        <li id="R_6.019.004cd">tathā śaṅkhasahasrāṇi tathā vṛndaśatāni ca</li>
    </ul>
    <ul id="R_6.019.005">
        <li id="R_6.019.005ab">ete sugrīvasacivāḥ kiṣkindhānilayāḥ sadā</li>
        <li id="R_6.019.005cd">harayo devagandharvair utpannāḥ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_6.019.006">
        <li id="R_6.019.006ab">yau tau paśyasi tiṣṭhantau kumārau devarūpiṇau</li>
        <li id="R_6.019.006cd">maindaś ca dvividaś cobhau tābhyāṃ nāsti samo yudhi</li>
    </ul>
    <ul id="R_6.019.007">
        <li id="R_6.019.007ab">brahmaṇā samanujñātāv amṛtaprāśināv ubhau</li>
        <li id="R_6.019.007cd">āśaṃsete yudhā laṅkām etau marditum ojasā</li>
    </ul>
    <ul id="R_6.019.008">
        <li id="R_6.019.008ab">yāv etāv etayoḥ pārśve sthitau parvatasaṃnibhau</li>
        <li id="R_6.019.008cd">sumukho vimukhaś caiva mṛtyuputrau pituḥ samau</li>
    </ul>
    <ul id="R_6.019.009">
        <li id="R_6.019.009ab">yaṃ tu paśyasi tiṣṭhantaṃ prabhinnam iva kuñjaram</li>
        <li id="R_6.019.009cd">yo balāt kṣobhayet kruddhaḥ samudram api vānaraḥ</li>
    </ul>
    <ul id="R_6.019.010">
        <li id="R_6.019.010ab">eṣo 'bhigantā laṅkāyā vaidehyās tava ca prabho</li>
        <li id="R_6.019.010cd">enaṃ paśya purā dṛṣṭaṃ vānaraṃ punar āgatam</li>
    </ul>
    <ul id="R_6.019.011">
        <li id="R_6.019.011ab">jyeṣṭhaḥ kesariṇaḥ putro vātātmaja iti śrutaḥ</li>
        <li id="R_6.019.011cd">hanūmān iti vikhyāto laṅghito yena sāgaraḥ</li>
    </ul>
    <ul id="R_6.019.012">
        <li id="R_6.019.012ab">kāmarūpī hariśreṣṭho balarūpasamanvitaḥ</li>
        <li id="R_6.019.012cd">anivāryagatiś caiva yathā satatagaḥ prabhuḥ</li>
    </ul>
    <ul id="R_6.019.013">
        <li id="R_6.019.013ab">udyantaṃ bhāskaraṃ dṛṣṭvā bālaḥ kila pipāsitaḥ</li>
        <li id="R_6.019.013cd">triyojanasahasraṃ tu adhvānam avatīrya hi</li>
    </ul>
    <ul id="R_6.019.014">
        <li id="R_6.019.014ab">ādityam āhariṣyāmi na me kṣut pratiyāsyati</li>
        <li id="R_6.019.014cd">iti saṃcintya manasā puraiṣa baladarpitaḥ</li>
    </ul>
    <ul id="R_6.019.015">
        <li id="R_6.019.015ab">anādhṛṣyatamaṃ devam api devarṣidānavaiḥ</li>
        <li id="R_6.019.015cd">anāsādyaiva patito bhāskarodayane girau</li>
    </ul>
    <ul id="R_6.019.016">
        <li id="R_6.019.016ab">patitasya kaper asya hanur ekā śilātale</li>
        <li id="R_6.019.016cd">kiṃ cid bhinnā dṛḍhahanor hanūmān eṣa tena vai</li>
    </ul>
    <ul id="R_6.019.017">
        <li id="R_6.019.017ab">satyam āgamayogena mamaiṣa vidito hariḥ</li>
        <li id="R_6.019.017cd">nāsya śakyaṃ balaṃ rūpaṃ prabhāvo vānubhāṣitum</li>
    </ul>
    <ul id="R_6.019.018">
        <li id="R_6.019.018ab">eṣa āśaṃsate laṅkām eko marditum ojasā</li>
        <li id="R_6.019.018cd">yaś caiṣo 'nantaraḥ śūraḥ śyāmaḥ padmanibhekṣaṇaḥ</li>
    </ul>
    <ul id="R_6.019.019">
        <li id="R_6.019.019ab">ikṣvākūṇām atiratho loke vikhyāta pauruṣaḥ</li>
        <li id="R_6.019.019cd">yasmin na calate dharmo yo dharmaṃ nātivartate</li>
    </ul>
    <ul id="R_6.019.020">
        <li id="R_6.019.020ab">yo brāhmam astraṃ vedāṃś ca veda vedavidāṃ varaḥ</li>
        <li id="R_6.019.020cd">yo bhindyād gaganaṃ bāṇaiḥ parvatāṃś cāpi dārayet</li>
    </ul>
    <ul id="R_6.019.021">
        <li id="R_6.019.021ab">yasya mṛtyor iva krodhaḥ śakrasyeva parākramaḥ</li>
        <li id="R_6.019.021cd">sa eṣa rāmas tvāṃ yoddhuṃ rājan samabhivartate</li>
    </ul>
    <ul id="R_6.019.022">
        <li id="R_6.019.022ab">yaś caiṣa dakṣiṇe pārśve śuddhajāmbūnadaprabhaḥ</li>
        <li id="R_6.019.022cd">viśālavakṣās tāmrākṣo nīlakuñcitamūrdhajaḥ</li>
    </ul>
    <ul id="R_6.019.023">
        <li id="R_6.019.023ab">eṣo 'sya lakṣmaṇo nāma bhrātā prāṇasamaḥ priyaḥ</li>
        <li id="R_6.019.023cd">naye yuddhe ca kuśalaḥ sarvaśāstraviśāradaḥ</li>
    </ul>
    <ul id="R_6.019.024">
        <li id="R_6.019.024ab">amarṣī durjayo jetā vikrānto buddhimān balī</li>
        <li id="R_6.019.024cd">rāmasya dakṣiṇo bāhur nityaṃ prāṇo bahiścaraḥ</li>
    </ul>
    <ul id="R_6.019.025">
        <li id="R_6.019.025ab">na hy eṣa rāghavasyārthe jīvitaṃ parirakṣati</li>
        <li id="R_6.019.025cd">eṣaivāśaṃsate yuddhe nihantuṃ sarvarākṣasān</li>
    </ul>
    <ul id="R_6.019.026">
        <li id="R_6.019.026ab">yas tu savyam asau pakṣaṃ rāmasyāśritya tiṣṭhati</li>
        <li id="R_6.019.026cd">rakṣogaṇaparikṣipto rājā hy eṣa vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.019.027">
        <li id="R_6.019.027ab">śrīmatā rājarājena laṅkāyām abhiṣecitaḥ</li>
        <li id="R_6.019.027cd">tvām eva pratisaṃrabdho yuddhāyaiṣo 'bhivartate</li>
    </ul>
    <ul id="R_6.019.028">
        <li id="R_6.019.028ab">yaṃ tu paśyasi tiṣṭhantaṃ madhye girim ivācalam</li>
        <li id="R_6.019.028cd">sarvaśākhāmṛgendrāṇāṃ bhartāram aparājitam</li>
    </ul>
    <ul id="R_6.019.029">
        <li id="R_6.019.029ab">tejasā yaśasā buddhyā jñānenābhijanena ca</li>
        <li id="R_6.019.029cd">yaḥ kapīn ati babhrāja himavān iva parvatān</li>
    </ul>
    <ul id="R_6.019.030">
        <li id="R_6.019.030ab">kiṣkindhāṃ yaḥ samadhyāste guhāṃ sagahanadrumām</li>
        <li id="R_6.019.030cd">durgāṃ parvatadurgasthāṃ pradhānaiḥ saha yūthapaiḥ</li>
    </ul>
    <ul id="R_6.019.031">
        <li id="R_6.019.031ab">yasyaiṣā kāñcanī mālā śobhate śatapuṣkarā</li>
        <li id="R_6.019.031cd">kāntā devamanuṣyāṇāṃ yasyāṃ lakṣmīḥ pratiṣṭhitā</li>
    </ul>
    <ul id="R_6.019.032">
        <li id="R_6.019.032ab">etāṃ ca mālāṃ tārāṃ ca kapirājyaṃ ca śāśvatam</li>
        <li id="R_6.019.032cd">sugrīvo vālinaṃ hatvā rāmeṇa pratipāditaḥ</li>
    </ul>
    <ul id="R_6.019.033">
        <li id="R_6.019.033ab">evaṃ koṭisahasreṇa śaṅkūnāṃ ca śatena ca</li>
        <li id="R_6.019.033cd">sugrīvo vānarendras tvāṃ yuddhārtham abhivartate</li>
    </ul>
    <ul id="R_6.019.034">
        <li id="R_6.019.034ab">
            <seg type="pāda" n="a">imāṃ mahārājasamīkṣya vāhinīm</seg>
            <seg type="pāda" n="b">upasthitāṃ prajvalitagrahopamām</seg>
        </li>
        <li id="R_6.019.034cd">
            <seg type="pāda" n="c">tataḥ prayatnaḥ paramo vidhīyatāṃ</seg>
            <seg type="pāda" n="d">yathā jayaḥ syān na paraiḥ parājayaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.020.001">
        <li id="R_6.020.001ab">śukena tu samākhyātāṃs tān dṛṣṭvā hariyūthapān</li>
        <li id="R_6.020.001cd">samīpasthaṃ ca rāmasya bhrātaraṃ svaṃ vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.020.002">
        <li id="R_6.020.002ab">lakṣmaṇaṃ ca mahāvīryaṃ bhujaṃ rāmasya dakṣiṇam</li>
        <li id="R_6.020.002cd">sarvavānararājaṃ ca sugrīvaṃ bhīmavikramam</li>
    </ul>
    <ul id="R_6.020.003">
        <li id="R_6.020.003ab">kiṃ cid āvignahṛdayo jātakrodhaś ca rāvaṇaḥ</li>
        <li id="R_6.020.003cd">bhartsayām āsa tau vīrau kathānte śukasāraṇau</li>
    </ul>
    <ul id="R_6.020.004">
        <li id="R_6.020.004ab">adhomukhau tau praṇatāv abravīc chukasāraṇau</li>
        <li id="R_6.020.004cd">roṣagadgadayā vācā saṃrabdhaḥ paruṣaṃ vacaḥ</li>
    </ul>
    <ul id="R_6.020.005">
        <li id="R_6.020.005ab">na tāvat sadṛśaṃ nāma sacivair upajīvibhiḥ</li>
        <li id="R_6.020.005cd">vipriyaṃ nṛpater vaktuṃ nigrahapragrahe vibhoḥ</li>
    </ul>
    <ul id="R_6.020.006">
        <li id="R_6.020.006ab">ripūṇāṃ pratikūlānāṃ yuddhārtham abhivartatām</li>
        <li id="R_6.020.006cd">ubhābhyāṃ sadṛśaṃ nāma vaktum aprastave stavam</li>
    </ul>
    <ul id="R_6.020.007">
        <li id="R_6.020.007ab">ācāryā guravo vṛddhā vṛthā vāṃ paryupāsitāḥ</li>
        <li id="R_6.020.007cd">sāraṃ yad rājaśāstrāṇām anujīvyaṃ na gṛhyate</li>
    </ul>
    <ul id="R_6.020.008">
        <li id="R_6.020.008ab">gṛhīto vā na vijñāto bhāro jñānasya vochyate</li>
        <li id="R_6.020.008cd">īdṛśaiḥ sacivair yukto mūrkhair diṣṭyā dharāmy aham</li>
    </ul>
    <ul id="R_6.020.009">
        <li id="R_6.020.009ab">kiṃ nu mṛtyor bhayaṃ nāsti māṃ vaktuṃ paruṣaṃ vacaḥ</li>
        <li id="R_6.020.009cd">yasya me śāsato jihvā prayacchati śubhāśubham</li>
    </ul>
    <ul id="R_6.020.010">
        <li id="R_6.020.010ab">apy eva dahanaṃ spṛṣṭvā vane tiṣṭhanti pādapāḥ</li>
        <li id="R_6.020.010cd">rājadoṣaparāmṛṣṭās tiṣṭhante nāparādhinaḥ</li>
    </ul>
    <ul id="R_6.020.011">
        <li id="R_6.020.011ab">hanyām aham imau pāpau śatrupakṣapraśaṃsakau</li>
        <li id="R_6.020.011cd">yadi pūrvopakārair me na krodho mṛdutāṃ vrajet</li>
    </ul>
    <ul id="R_6.020.012">
        <li id="R_6.020.012ab">apadhvaṃsata gacchadhvaṃ saṃnikarṣād ito mama</li>
        <li id="R_6.020.012cd">na hi vāṃ hantum icchāmi smarann upakṛtāni vām</li>
        <li id="R_6.020.012ef">hatāv eva kṛtaghnau tau mayi snehaparāṅmukhau</li>
    </ul>
    <ul id="R_6.020.013">
        <li id="R_6.020.013ab">evam uktau tu savrīḍau tāv ubhau śukasāraṇau</li>
        <li id="R_6.020.013cd">rāvaṇaṃ jayaśabdena pratinandyābhiniḥsṛtau</li>
    </ul>
    <ul id="R_6.020.014">
        <li id="R_6.020.014ab">abravīt sa daśagrīvaḥ samīpasthaṃ mahodaram</li>
        <li id="R_6.020.014cd">upasthāpaya śīghraṃ me cārān nītiviśāradān</li>
    </ul>
    <ul id="R_6.020.015">
        <li id="R_6.020.015ab">tataś carāḥ saṃtvaritāḥ prāptāḥ pārthivaśāsanāt</li>
        <li id="R_6.020.015cd">upasthitāḥ prāñjalayo vardhayitvā jayāśiṣā</li>
    </ul>
    <ul id="R_6.020.016">
        <li id="R_6.020.016ab">tān abravīt tato vākyaṃ rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.020.016cd">cārān pratyayikāñ śūrān bhaktān vigatasādhvasān</li>
    </ul>
    <ul id="R_6.020.017">
        <li id="R_6.020.017ab">ito gacchata rāmasya vyavasāyaṃ parīkṣatha</li>
        <li id="R_6.020.017cd">mantreṣv abhyantarā ye 'sya prītyā tena samāgatāḥ</li>
    </ul>
    <ul id="R_6.020.018">
        <li id="R_6.020.018ab">kathaṃ svapiti jāgarti kim anyac ca kariṣyati</li>
        <li id="R_6.020.018cd">vijñāya nipuṇaṃ sarvam āgantavyam aśeṣataḥ</li>
    </ul>
    <ul id="R_6.020.019">
        <li id="R_6.020.019ab">cāreṇa viditaḥ śatruḥ paṇḍitair vasudhādhipaiḥ</li>
        <li id="R_6.020.019cd">yuddhe svalpena yatnena samāsādya nirasyate</li>
    </ul>
    <ul id="R_6.020.020">
        <li id="R_6.020.020ab">cārās tu te tathety uktvā prahṛṣṭā rākṣaseśvaram</li>
        <li id="R_6.020.020cd">kṛtvā pradakṣiṇaṃ jagmur yatra rāmaḥ salakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.020.021">
        <li id="R_6.020.021ab">te suvelasya śailasya samīpe rāmalakṣmaṇau</li>
        <li id="R_6.020.021cd">pracchannā dadṛśur gatvā sasugrīvavibhīṣaṇau</li>
    </ul>
    <ul id="R_6.020.022">
        <li id="R_6.020.022ab">te tu dharmātmanā dṛṣṭā rākṣasendreṇa rākṣasāḥ</li>
        <li id="R_6.020.022cd">vibhīṣaṇena tatrasthā nigṛhītā yadṛcchayā</li>
    </ul>
    <ul id="R_6.020.023">
        <li id="R_6.020.023ab">vānarair arditās te tu vikrāntair laghuvikramaiḥ</li>
        <li id="R_6.020.023cd">punar laṅkām anuprāptāḥ śvasanto naṣṭacetasaḥ</li>
    </ul>
    <ul id="R_6.020.024">
        <li id="R_6.020.024ab">
            <seg type="pāda" n="a">tato daśagrīvam upasthitās te</seg>
            <seg type="pāda" n="b">cārā bahirnityacarā niśācarāḥ</seg>
        </li>
        <li id="R_6.020.024cd">
            <seg type="pāda" n="c">gireḥ suvelasya samīpavāsinaṃ</seg>
            <seg type="pāda" n="d">nyavedayan bhīmabalaṃ mahābalāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.021.001">
        <li id="R_6.021.001ab">tatas tam akṣobhya balaṃ laṅkādhipataye carāḥ</li>
        <li id="R_6.021.001cd">suvele rāghavaṃ śaile niviṣṭaṃ pratyavedayan</li>
    </ul>
    <ul id="R_6.021.002">
        <li id="R_6.021.002ab">cārāṇāṃ rāvaṇaḥ śrutvā prāptaṃ rāmaṃ mahābalam</li>
        <li id="R_6.021.002cd">jātodvego 'bhavat kiṃ cic chārdūlaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.021.003">
        <li id="R_6.021.003ab">ayathāvac ca te varṇo dīnaś cāsi niśācara</li>
        <li id="R_6.021.003cd">nāsi kaccid amitrāṇāṃ kruddhānāṃ vaśam āgataḥ</li>
    </ul>
    <ul id="R_6.021.004">
        <li id="R_6.021.004ab">iti tenānuśiṣṭas tu vācaṃ mandam udīrayat</li>
        <li id="R_6.021.004cd">tadā rākṣasaśārdūlaṃ śārdūlo bhayavihvalaḥ</li>
    </ul>
    <ul id="R_6.021.005">
        <li id="R_6.021.005ab">na te cārayituṃ śakyā rājan vānarapuṃgavāḥ</li>
        <li id="R_6.021.005cd">vikrāntā balavantaś ca rāghaveṇa ca rakṣitāḥ</li>
    </ul>
    <ul id="R_6.021.006">
        <li id="R_6.021.006ab">nāpi saṃbhāṣituṃ śakyāḥ saṃpraśno 'tra na labhyate</li>
        <li id="R_6.021.006cd">sarvato rakṣyate panthā vānaraiḥ parvatopamaiḥ</li>
    </ul>
    <ul id="R_6.021.007">
        <li id="R_6.021.007ab">praviṣṭamātre jñāto 'haṃ bale tasminn acārite</li>
        <li id="R_6.021.007cd">balād gṛhīto bahubhir bahudhāsmi vidāritaḥ</li>
    </ul>
    <ul id="R_6.021.008">
        <li id="R_6.021.008ab">jānubhir muṣṭibhir dantais talaiś cābhihato bhṛśam</li>
        <li id="R_6.021.008cd">pariṇīto 'smi haribhir balavadbhir amarṣaṇaiḥ</li>
    </ul>
    <ul id="R_6.021.009">
        <li id="R_6.021.009ab">pariṇīya ca sarvatra nīto 'haṃ rāmasaṃsadam</li>
        <li id="R_6.021.009cd">rudhirādigdhasarvāṅgo vihvalaś calitendriyaḥ</li>
    </ul>
    <ul id="R_6.021.010">
        <li id="R_6.021.010ab">haribhir vadhyamānaś ca yācamānaḥ kṛtāñjaliḥ</li>
        <li id="R_6.021.010cd">rāghaveṇa paritrāto jīvāmi ha yadṛcchayā</li>
    </ul>
    <ul id="R_6.021.011">
        <li id="R_6.021.011ab">eṣa śailaiḥ śilābhiś ca pūrayitvā mahārṇavam</li>
        <li id="R_6.021.011cd">dvāram āśritya laṅkāyā rāmas tiṣṭhati sāyudhaḥ</li>
    </ul>
    <ul id="R_6.021.012">
        <li id="R_6.021.012ab">garuḍavyūham āsthāya sarvato haribhir vṛtaḥ</li>
        <li id="R_6.021.012cd">māṃ visṛjya mahātejā laṅkām evābhivartate</li>
    </ul>
    <ul id="R_6.021.013">
        <li id="R_6.021.013ab">purā prākāram āyāti kṣipram ekataraṃ kuru</li>
        <li id="R_6.021.013cd">sītāṃ cāsmai prayacchāśu suyuddhaṃ vā pradīyatām</li>
    </ul>
    <ul id="R_6.021.014">
        <li id="R_6.021.014ab">manasā saṃtatāpātha tac chrutvā rākṣasādhipaḥ</li>
        <li id="R_6.021.014cd">śārdūlasya mahad vākyam athovāca sa rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.021.015">
        <li id="R_6.021.015ab">yadi māṃ pratiyudhyeran devagandharvadānavāḥ</li>
        <li id="R_6.021.015cd">naiva sītāṃ pradāsyāmi sarvalokabhayād api</li>
    </ul>
    <ul id="R_6.021.016">
        <li id="R_6.021.016ab">evam uktvā mahātejā rāvaṇaḥ punar abravīt</li>
        <li id="R_6.021.016cd">cāritā bhavatā senā ke 'tra śūrāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.021.017">
        <li id="R_6.021.017ab">kīdṛśāḥ kiṃprabhāvāś ca vānarā ye durāsadāḥ</li>
        <li id="R_6.021.017cd">kasya putrāś ca pautrāś ca tattvam ākhyāhi rākṣasa</li>
    </ul>
    <ul id="R_6.021.018">
        <li id="R_6.021.018ab">tatr atra pratipatsyāmi jñātvā teṣāṃ balābalam</li>
        <li id="R_6.021.018cd">avaśyaṃ balasaṃkhyānaṃ kartavyaṃ yuddham icchatā</li>
    </ul>
    <ul id="R_6.021.019">
        <li id="R_6.021.019ab">athaivam uktaḥ śārdūlo rāvaṇenottamaś caraḥ</li>
        <li id="R_6.021.019cd">idaṃ vacanam ārebhe vaktuṃ rāvaṇasaṃnidhau</li>
    </ul>
    <ul id="R_6.021.020">
        <li id="R_6.021.020ab">atharkṣarajasaḥ putro yudhi rājan sudurjayaḥ</li>
        <li id="R_6.021.020cd">gadgadasyātha putro 'tra jāmbavān iti viśrutaḥ</li>
    </ul>
    <ul id="R_6.021.021">
        <li id="R_6.021.021ab">gadgadasyaiva putro 'nyo guruputraḥ śatakratoḥ</li>
        <li id="R_6.021.021cd">kadanaṃ yasya putreṇa kṛtam ekena rakṣasām</li>
    </ul>
    <ul id="R_6.021.022">
        <li id="R_6.021.022ab">suṣeṇaś cāpi dharmātmā putro dharmasya vīryavān</li>
        <li id="R_6.021.022cd">saumyaḥ somātmajaś cātra rājan dadhimukhaḥ kapiḥ</li>
    </ul>
    <ul id="R_6.021.023">
        <li id="R_6.021.023ab">sumukho durmukhaś cātra vegadarśī ca vānaraḥ</li>
        <li id="R_6.021.023cd">mṛtyur vānararūpeṇa nūnaṃ sṛṣṭaḥ svayambhuvā</li>
    </ul>
    <ul id="R_6.021.024">
        <li id="R_6.021.024ab">putro hutavahasyātha nīlaḥ senāpatiḥ svayam</li>
        <li id="R_6.021.024cd">anilasya ca putro 'tra hanūmān iti viśrutaḥ</li>
    </ul>
    <ul id="R_6.021.025">
        <li id="R_6.021.025ab">naptā śakrasya durdharṣo balavān aṅgado yuvā</li>
        <li id="R_6.021.025cd">maindaś ca dvividaś cobhau balināv aśvisaṃbhavau</li>
    </ul>
    <ul id="R_6.021.026">
        <li id="R_6.021.026ab">putrā vaivasvatasyātra pañcakālāntakopamāḥ</li>
        <li id="R_6.021.026cd">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
    </ul>
    <ul id="R_6.021.027">
        <li id="R_6.021.027ab">śveto jyotirmukhaś cātra bhāskarasyātmasaṃbhavau</li>
        <li id="R_6.021.027cd">varuṇasya ca putro 'tha hemakūṭaḥ plavaṃgamaḥ</li>
    </ul>
    <ul id="R_6.021.028">
        <li id="R_6.021.028ab">viśvakarmasuto vīro nalaḥ plavagasattamaḥ</li>
        <li id="R_6.021.028cd">vikrānto vegavān atra vasuputraḥ sudurdharaḥ</li>
    </ul>
    <ul id="R_6.021.029">
        <li id="R_6.021.029ab">daśavānarakoṭyaś ca śūrāṇāṃ yuddhakāṅkṣiṇām</li>
        <li id="R_6.021.029cd">śrīmatāṃ devaputrāṇāṃ śeṣān nākhyātum utsahe</li>
    </ul>
    <ul id="R_6.021.030">
        <li id="R_6.021.030ab">putro daśarathasyaiṣa siṃhasaṃhanano yuvā</li>
        <li id="R_6.021.030cd">dūṣaṇo nihato yena kharaś ca triśirās tathā</li>
    </ul>
    <ul id="R_6.021.031">
        <li id="R_6.021.031ab">nāsti rāmasya sadṛśo vikrame bhuvi kaś cana</li>
        <li id="R_6.021.031cd">virādho nihato yena kabandhaś cāntakopamaḥ</li>
    </ul>
    <ul id="R_6.021.032">
        <li id="R_6.021.032ab">vaktuṃ na śakto rāmasya naraḥ kaś cid guṇān kṣitau</li>
        <li id="R_6.021.032cd">janasthānagatā yena tāvanto rākṣasā hatāḥ</li>
    </ul>
    <ul id="R_6.021.033">
        <li id="R_6.021.033ab">lakṣmaṇaś cātra dharmātmā mātaṃgānām ivarṣabhaḥ</li>
        <li id="R_6.021.033cd">yasya bāṇapathaṃ prāpya na jīved api vāsavaḥ</li>
    </ul>
    <ul id="R_6.021.034">
        <li id="R_6.021.034ab">rākṣasānāṃ variṣṭhaś ca tava bhrātā vibhīṣaṇaḥ</li>
        <li id="R_6.021.034cd">parigṛhya purīṃ laṅkāṃ rāghavasya hite rataḥ</li>
    </ul>
    <ul id="R_6.021.035">
        <li id="R_6.021.035ab">iti sarvaṃ samākhyātaṃ tavedaṃ vānaraṃ balam</li>
        <li id="R_6.021.035cd">suvele 'dhiṣṭhitaṃ śaile śeṣakārye bhavān gatiḥ</li>
    </ul>
    <ul id="R_6.022.001">
        <li id="R_6.022.001ab">tatas tam akṣobhyabalaṃ laṅkāyāṃ nṛpateś caraḥ</li>
        <li id="R_6.022.001cd">suvele rāghavaṃ śaile niviṣṭaṃ pratyavedayan</li>
    </ul>
    <ul id="R_6.022.002">
        <li id="R_6.022.002ab">cārāṇāṃ rāvaṇaḥ śrutvā prāptaṃ rāmaṃ mahābalam</li>
        <li id="R_6.022.002cd">jātodvego 'bhavat kiṃ cit sacivāṃś cedam abravīt</li>
    </ul>
    <ul id="R_6.022.003">
        <li id="R_6.022.003ab">mantriṇaḥ śīghram āyāntu sarve vai susamāhitāḥ</li>
        <li id="R_6.022.003cd">ayaṃ no mantrakālo hi saṃprāpta iva rākṣasāḥ</li>
    </ul>
    <ul id="R_6.022.004">
        <li id="R_6.022.004ab">tasya tac chāsanaṃ śrutvā mantriṇo 'bhyāgaman drutam</li>
        <li id="R_6.022.004cd">tataḥ saṃmantrayām āsa sacivai rākṣasaiḥ saha</li>
    </ul>
    <ul id="R_6.022.005">
        <li id="R_6.022.005ab">mantrayitvā sa durdharṣaḥ kṣamaṃ yat samanantaram</li>
        <li id="R_6.022.005cd">visarjayitvā sacivān praviveśa svam ālayam</li>
    </ul>
    <ul id="R_6.022.006">
        <li id="R_6.022.006ab">tato rākṣasam āhūya vidyujjihvaṃ mahābalam</li>
        <li id="R_6.022.006cd">māyāvidaṃ mahāmāyaḥ prāviśad yatra maithilī</li>
    </ul>
    <ul id="R_6.022.007">
        <li id="R_6.022.007ab">vidyujjihvaṃ ca māyājñam abravīd rākṣasādhipaḥ</li>
        <li id="R_6.022.007cd">mohayiṣyāmahe sītāṃ māyayā janakātmajām</li>
    </ul>
    <ul id="R_6.022.008">
        <li id="R_6.022.008ab">śiro māyāmayaṃ gṛhya rāghavasya niśācara</li>
        <li id="R_6.022.008cd">māṃ tvaṃ samupatiṣṭhasva mahac ca saśaraṃ dhanuḥ</li>
    </ul>
    <ul id="R_6.022.009">
        <li id="R_6.022.009ab">evam uktas tathety āha vidyujjihvo niśācaraḥ</li>
        <li id="R_6.022.009cd">tasya tuṣṭo 'bhavad rājā pradadau ca vibhūṣaṇam</li>
    </ul>
    <ul id="R_6.022.010">
        <li id="R_6.022.010ab">aśokavanikāyāṃ tu praviveśa mahābalaḥ</li>
        <li id="R_6.022.010cd">tato dīnām adainyārhāṃ dadarśa dhanadānujaḥ</li>
        <li id="R_6.022.010ef">adhomukhīṃ śokaparām upaviṣṭāṃ mahītale</li>
    </ul>
    <ul id="R_6.022.011">
        <li id="R_6.022.011ab">bhartāram eva dhyāyantīm aśokavanikāṃ gatām</li>
        <li id="R_6.022.011cd">upāsyamānāṃ ghorābhī rākṣasībhir adūrataḥ</li>
    </ul>
    <ul id="R_6.022.012">
        <li id="R_6.022.012ab">upasṛtya tataḥ sītāṃ praharṣan nāma kīrtayan</li>
        <li id="R_6.022.012cd">idaṃ ca vacanaṃ dhṛṣṭam uvāca janakātmajām</li>
    </ul>
    <ul id="R_6.022.013">
        <li id="R_6.022.013ab">sāntvyamānā mayā bhadre yam upāśritya valgase</li>
        <li id="R_6.022.013cd">khara hantā sa te bhartā rāghavaḥ samare hataḥ</li>
    </ul>
    <ul id="R_6.022.014">
        <li id="R_6.022.014ab">chinnaṃ te sarvato mūlaṃ darpas te nihato mayā</li>
        <li id="R_6.022.014cd">vyasanenātmanaḥ sīte mama bhāryā bhaviṣyasi</li>
    </ul>
    <ul id="R_6.022.015">
        <li id="R_6.022.015ab">alpapuṇye nivṛttārthe mūḍhe paṇḍitamānini</li>
        <li id="R_6.022.015cd">śṛṇu bhartṛbadhaṃ sīte ghoraṃ vṛtravadhaṃ yathā</li>
    </ul>
    <ul id="R_6.022.016">
        <li id="R_6.022.016ab">samāyātaḥ samudrāntaṃ māṃ hantuṃ kila rāghavaḥ</li>
        <li id="R_6.022.016cd">vānarendrapraṇītena balena mahatā vṛtaḥ</li>
    </ul>
    <ul id="R_6.022.017">
        <li id="R_6.022.017ab">saṃniviṣṭaḥ samudrasya tīram āsādya dakṣiṇam</li>
        <li id="R_6.022.017cd">balena mahatā rāmo vrajaty astaṃ divākare</li>
    </ul>
    <ul id="R_6.022.018">
        <li id="R_6.022.018ab">athādhvani pariśrāntam ardharātre sthitaṃ balam</li>
        <li id="R_6.022.018cd">sukhasuptaṃ samāsādya cāritaṃ prathamaṃ caraiḥ</li>
    </ul>
    <ul id="R_6.022.019">
        <li id="R_6.022.019ab">tat prahastapraṇītena balena mahatā mama</li>
        <li id="R_6.022.019cd">balam asya hataṃ rātrau yatra rāmaḥ sulakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.022.020">
        <li id="R_6.022.020ab">paṭṭasān parighān khaḍgāṃś cakrān daṇḍān mahāyasān</li>
        <li id="R_6.022.020cd">bāṇajālāni śūlāni bhāsvarān kūṭamudgarān</li>
    </ul>
    <ul id="R_6.022.021">
        <li id="R_6.022.021ab">yaṣṭīś ca tomarān prāsaṃś cakrāṇi musalāni ca</li>
        <li id="R_6.022.021cd">udyamyodyamya rakṣobhir vānareṣu nipātitāḥ</li>
    </ul>
    <ul id="R_6.022.022">
        <li id="R_6.022.022ab">atha suptasya rāmasya prahastena pramāthinā</li>
        <li id="R_6.022.022cd">asaktaṃ kṛtahastena śiraś chinnaṃ mahāsinā</li>
    </ul>
    <ul id="R_6.022.023">
        <li id="R_6.022.023ab">vibhīṣaṇaḥ samutpatya nigṛhīto yadṛcchayā</li>
        <li id="R_6.022.023cd">diśaḥ pravrājitaḥ sarvair lakṣmaṇaḥ plavagaiḥ saha</li>
    </ul>
    <ul id="R_6.022.024">
        <li id="R_6.022.024ab">sugrīvo grīvayā śete bhagnayā plavagādhipaḥ</li>
        <li id="R_6.022.024cd">nirastahanukaḥ śete hanūmān rākṣasair hataḥ</li>
    </ul>
    <ul id="R_6.022.025">
        <li id="R_6.022.025ab">jāmbavān atha jānubhyām utpatan nihato yudhi</li>
        <li id="R_6.022.025cd">paṭṭasair bahubhiś chinno nikṛttaḥ pādapo yathā</li>
    </ul>
    <ul id="R_6.022.026">
        <li id="R_6.022.026ab">maindaś ca dvividaś cobhau nihatau vānararṣabhau</li>
        <li id="R_6.022.026cd">niḥśvasantau rudantau ca rudhireṇa samukṣitau</li>
    </ul>
    <ul id="R_6.022.027">
        <li id="R_6.022.027ab">asinābhyāhataś chinno madhye ripuniṣūdanaḥ</li>
        <li id="R_6.022.027cd">abhiṣṭanati medinyāṃ panasaḥ panaso yathā</li>
    </ul>
    <ul id="R_6.022.028">
        <li id="R_6.022.028ab">nārācair bahubhiś chinnaḥ śete daryāṃ darīmukhaḥ</li>
        <li id="R_6.022.028cd">kumudas tu mahātejā niṣkūjan sāyakair hataḥ</li>
    </ul>
    <ul id="R_6.022.029">
        <li id="R_6.022.029ab">aṅgado bahubhiś chinnaḥ śarair āsādya rākṣasaiḥ</li>
        <li id="R_6.022.029cd">pātito rudhirodgārī kṣitau nipatito 'ṅgadaḥ</li>
    </ul>
    <ul id="R_6.022.030">
        <li id="R_6.022.030ab">harayo mathitā nāgai rathajālais tathāpare</li>
        <li id="R_6.022.030cd">śāyitā mṛditās tatra vāyuvegair ivāmbudāḥ</li>
    </ul>
    <ul id="R_6.022.031">
        <li id="R_6.022.031ab">pradrutāś ca pare trastā hanyamānā jaghanyataḥ</li>
        <li id="R_6.022.031cd">abhidrutās tu rakṣobhiḥ siṃhair iva mahādvipāḥ</li>
    </ul>
    <ul id="R_6.022.032">
        <li id="R_6.022.032ab">sāgare patitāḥ ke cit ke cid gaganam āśritāḥ</li>
        <li id="R_6.022.032cd">ṛkṣā vṛkṣān upārūḍhā vānarais tu vimiśritāḥ</li>
    </ul>
    <ul id="R_6.022.033">
        <li id="R_6.022.033ab">sāgarasya ca tīreṣu śaileṣu ca vaneṣu ca</li>
        <li id="R_6.022.033cd">piṅgākṣās te virūpākṣair bahubhir bahavo hatāḥ</li>
    </ul>
    <ul id="R_6.022.034">
        <li id="R_6.022.034ab">evaṃ tava hato bhartā sasainyo mama senayā</li>
        <li id="R_6.022.034cd">kṣatajārdraṃ rajodhvastam idaṃ cāsyāhṛtaṃ śiraḥ</li>
    </ul>
    <ul id="R_6.022.035">
        <li id="R_6.022.035ab">tataḥ paramadurdharṣo rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.022.035cd">sītāyām upaśṛṇvantyāṃ rākṣasīm idam abravīt</li>
    </ul>
    <ul id="R_6.022.036">
        <li id="R_6.022.036ab">rākṣasaṃ krūrakarmāṇaṃ vidyujjihvaṃ tvam ānaya</li>
        <li id="R_6.022.036cd">yena tad rāghavaśiraḥ saṃgrāmāt svayam āhṛtam</li>
    </ul>
    <ul id="R_6.022.037">
        <li id="R_6.022.037ab">vidyujjihvas tato gṛhya śiras tat saśarāsanam</li>
        <li id="R_6.022.037cd">praṇāmaṃ śirasā kṛtvā rāvaṇasyāgrataḥ sthitaḥ</li>
    </ul>
    <ul id="R_6.022.038">
        <li id="R_6.022.038ab">tam abravīt tato rājā rāvaṇo rākṣasaṃ sthitam</li>
        <li id="R_6.022.038cd">vidyujjihvaṃ mahājihvaṃ samīpaparivartinam</li>
    </ul>
    <ul id="R_6.022.039">
        <li id="R_6.022.039ab">agrataḥ kuru sītāyāḥ śīghraṃ dāśaratheḥ śiraḥ</li>
        <li id="R_6.022.039cd">avasthāṃ paścimāṃ bhartuḥ kṛpaṇā sādhu paśyatu</li>
    </ul>
    <ul id="R_6.022.040">
        <li id="R_6.022.040ab">evam uktaṃ tu tad rakṣaḥ śiras tat priyadarśanam</li>
        <li id="R_6.022.040cd">upanikṣipya sītāyāḥ kṣipram antaradhīyata</li>
    </ul>
    <ul id="R_6.022.041">
        <li id="R_6.022.041ab">rāvaṇaś cāpi cikṣepa bhāsvaraṃ kārmukaṃ mahat</li>
        <li id="R_6.022.041cd">triṣu lokeṣu vikhyātaṃ sītām idam uvāca ha</li>
    </ul>
    <ul id="R_6.022.042">
        <li id="R_6.022.042ab">idaṃ tat tava rāmasya kārmukaṃ jyāsamanvitam</li>
        <li id="R_6.022.042cd">iha prahastenānītaṃ hatvā taṃ niśi mānuṣam</li>
    </ul>
    <ul id="R_6.022.043">
        <li id="R_6.022.043ab">
            <seg type="pāda" n="a">sa vidyujjihvena sahaiva tac chiro</seg>
            <seg type="pāda" n="b">dhanuś ca bhūmau vinikīrya rāvaṇaḥ</seg>
        </li>
        <li id="R_6.022.043cd">
            <seg type="pāda" n="c">videharājasya sutāṃ yaśasvinīṃ</seg>
            <seg type="pāda" n="d">tato 'bravīt tāṃ bhava me vaśānugā</seg>
        </li>
    </ul>
    <ul id="R_6.023.001">
        <li id="R_6.023.001ab">sā sītā tac chiro dṛṣṭvā tac ca kārmukam uttamam</li>
        <li id="R_6.023.001cd">sugrīvapratisaṃsargam ākhyātaṃ ca hanūmatā</li>
    </ul>
    <ul id="R_6.023.002">
        <li id="R_6.023.002ab">nayane mukhavarṇaṃ ca bhartus tat sadṛśaṃ mukham</li>
        <li id="R_6.023.002cd">keśān keśāntadeśaṃ ca taṃ ca cūḍāmaṇiṃ śubham</li>
    </ul>
    <ul id="R_6.023.003">
        <li id="R_6.023.003ab">etaiḥ sarvair abhijñānair abhijñāya suduḥkhitā</li>
        <li id="R_6.023.003cd">vijagarhe 'tha kaikeyīṃ krośantī kurarī yathā</li>
    </ul>
    <ul id="R_6.023.004">
        <li id="R_6.023.004ab">sakāmā bhava kaikeyi hato 'yaṃ kulanandanaḥ</li>
        <li id="R_6.023.004cd">kulam utsāditaṃ sarvaṃ tvayā kalahaśīlayā</li>
    </ul>
    <ul id="R_6.023.005">
        <li id="R_6.023.005ab">āryeṇa kiṃ nu kaikeyyāḥ kṛtaṃ rāmeṇa vipriyam</li>
        <li id="R_6.023.005cd">yad gṛhāc cīravasanas tayā prasthāpito vanam</li>
    </ul>
    <ul id="R_6.023.006">
        <li id="R_6.023.006ab">evam uktvā tu vaidehī vepamānā tapasvinī</li>
        <li id="R_6.023.006cd">jagāma jagatīṃ bālā chinnā tu kadalī yathā</li>
    </ul>
    <ul id="R_6.023.007">
        <li id="R_6.023.007ab">sā muhūrtāt samāśvasya pratilabhya ca cetanām</li>
        <li id="R_6.023.007cd">tac chiraḥ samupāghrāya vilalāpāyatekṣaṇā</li>
    </ul>
    <ul id="R_6.023.008">
        <li id="R_6.023.008ab">hā hatāsmi mahābāho vīravratam anuvratā</li>
        <li id="R_6.023.008cd">imāṃ te paścimāvasthāṃ gatāsmi vidhavā kṛtā</li>
    </ul>
    <ul id="R_6.023.009">
        <li id="R_6.023.009ab">prathamaṃ maraṇaṃ nāryā bhartur vaiguṇyam ucyate</li>
        <li id="R_6.023.009cd">suvṛttaḥ sādhuvṛttāyāḥ saṃvṛttas tvaṃ mamāgrataḥ</li>
    </ul>
    <ul id="R_6.023.010">
        <li id="R_6.023.010ab">duḥkhād duḥkhaṃ prapannāyā magnāyāḥ śokasāgare</li>
        <li id="R_6.023.010cd">yo hi mām udyatas trātuṃ so 'pi tvaṃ vinipātitaḥ</li>
    </ul>
    <ul id="R_6.023.011">
        <li id="R_6.023.011ab">sā śvaśrūr mama kausalyā tvayā putreṇa rāghava</li>
        <li id="R_6.023.011cd">vatseneva yathā dhenur vivatsā vatsalā kṛtā</li>
    </ul>
    <ul id="R_6.023.012">
        <li id="R_6.023.012ab">ādiṣṭaṃ dīrgham āyus te yair acintyaparākrama</li>
        <li id="R_6.023.012cd">anṛtaṃ vacanaṃ teṣām alpāyur asi rāghava</li>
    </ul>
    <ul id="R_6.023.013">
        <li id="R_6.023.013ab">atha vā naśyati prajñā prājñasyāpi satas tava</li>
        <li id="R_6.023.013cd">pacaty enaṃ tathā kālo bhūtānāṃ prabhavo hy ayam</li>
    </ul>
    <ul id="R_6.023.014">
        <li id="R_6.023.014ab">adṛṣṭaṃ mṛtyum āpannaḥ kasmāt tvaṃ nayaśāstravit</li>
        <li id="R_6.023.014cd">vyasanānām upāyajñaḥ kuśalo hy asi varjane</li>
    </ul>
    <ul id="R_6.023.015">
        <li id="R_6.023.015ab">tathā tvaṃ saṃpariṣvajya raudrayātinṛśaṃsayā</li>
        <li id="R_6.023.015cd">kālarātryā mayācchidya hṛtaḥ kamalalocanaḥ</li>
    </ul>
    <ul id="R_6.023.016">
        <li id="R_6.023.016ab">upaśeṣe mahābāho māṃ vihāya tapasvinīm</li>
        <li id="R_6.023.016cd">priyām iva śubhāṃ nārīṃ pṛthivīṃ puruṣarṣabha</li>
    </ul>
    <ul id="R_6.023.017">
        <li id="R_6.023.017ab">arcitaṃ satataṃ yatnād gandhamālyair mayā tava</li>
        <li id="R_6.023.017cd">idaṃ te matpriyaṃ vīra dhanuḥ kāñcanabhūṣitam</li>
    </ul>
    <ul id="R_6.023.018">
        <li id="R_6.023.018ab">pitrā daśarathena tvaṃ śvaśureṇa mamānagha</li>
        <li id="R_6.023.018cd">pūrvaiś ca pitṛbhiḥ sārdhaṃ nūnaṃ svarge samāgataḥ</li>
    </ul>
    <ul id="R_6.023.019">
        <li id="R_6.023.019ab">divi nakṣatrabhūtas tvaṃ mahat karma kṛtaṃ priyam</li>
        <li id="R_6.023.019cd">puṇyaṃ rājarṣivaṃśaṃ tvam ātmanaḥ samupekṣase</li>
    </ul>
    <ul id="R_6.023.020">
        <li id="R_6.023.020ab">kiṃ mān na prekṣase rājan kiṃ māṃ na pratibhāṣase</li>
        <li id="R_6.023.020cd">bālāṃ bālena saṃprāptāṃ bhāryāṃ māṃ sahacāriṇīm</li>
    </ul>
    <ul id="R_6.023.021">
        <li id="R_6.023.021ab">saṃśrutaṃ gṛhṇatā pāṇiṃ cariṣyāmīti yat tvayā</li>
        <li id="R_6.023.021cd">smara tan mama kākutstha naya mām api duḥkhitām</li>
    </ul>
    <ul id="R_6.023.022">
        <li id="R_6.023.022ab">kasmān mām apahāya tvaṃ gato gatimatāṃ vara</li>
        <li id="R_6.023.022cd">asmāl lokād amuṃ lokaṃ tyaktvā mām iha duḥkhitām</li>
    </ul>
    <ul id="R_6.023.023">
        <li id="R_6.023.023ab">kalyāṇair ucitaṃ yat tat pariṣvaktaṃ mayaiva tu</li>
        <li id="R_6.023.023cd">kravyādais tac charīraṃ te nūnaṃ viparikṛṣyate</li>
    </ul>
    <ul id="R_6.023.024">
        <li id="R_6.023.024ab">agniṣṭomādibhir yajñair iṣṭavān āptadakṣiṇaiḥ</li>
        <li id="R_6.023.024cd">agnihotreṇa saṃskāraṃ kena tvaṃ tu na lapsyase</li>
    </ul>
    <ul id="R_6.023.025">
        <li id="R_6.023.025ab">pravrajyām upapannānāṃ trayāṇām ekam āgatam</li>
        <li id="R_6.023.025cd">pariprakṣyati kausalyā lakṣmaṇaṃ śokalālasā</li>
    </ul>
    <ul id="R_6.023.026">
        <li id="R_6.023.026ab">sa tasyāḥ paripṛcchantyā vadhaṃ mitrabalasya te</li>
        <li id="R_6.023.026cd">tava cākhyāsyate nūnaṃ niśāyāṃ rākṣasair vadham</li>
    </ul>
    <ul id="R_6.023.027">
        <li id="R_6.023.027ab">sā tvāṃ suptaṃ hataṃ śrutvā māṃ ca rakṣogṛhaṃ gatām</li>
        <li id="R_6.023.027cd">hṛdayena vidīrṇena na bhaviṣyati rāghava</li>
    </ul>
    <ul id="R_6.023.028">
        <li id="R_6.023.028ab">sādhu pātaya māṃ kṣipraṃ rāmasyopari rāvaṇaḥ</li>
        <li id="R_6.023.028cd">samānaya patiṃ patnyā kuru kalyāṇam uttamam</li>
    </ul>
    <ul id="R_6.023.029">
        <li id="R_6.023.029ab">śirasā me śiraś cāsya kāyaṃ kāyena yojaya</li>
        <li id="R_6.023.029cd">rāvaṇānugamiṣyāmi gatiṃ bhartur mahātmanaḥ</li>
        <li id="R_6.023.029ef">muhūrtam api necchāmi jīvituṃ pāpajīvinā</li>
    </ul>
    <ul id="R_6.023.030">
        <li id="R_6.023.030ab">śrutaṃ mayā vedavidāṃ brāhmaṇānāṃ pitur gṛhe</li>
        <li id="R_6.023.030cd">yāsāṃ strīṇāṃ priyo bhartā tāsāṃ lokā mahodayāḥ</li>
    </ul>
    <ul id="R_6.023.031">
        <li id="R_6.023.031ab">kṣamā yasmin damas tyāgaḥ satyaṃ dharmaḥ kṛtajñatā</li>
        <li id="R_6.023.031cd">ahiṃsā caiva bhūtānāṃ tam ṛte kā gatir mama</li>
    </ul>
    <ul id="R_6.023.032">
        <li id="R_6.023.032ab">iti sā duḥkhasaṃtaptā vilalāpāyatekṣaṇā</li>
        <li id="R_6.023.032cd">bhartuḥ śiro dhanus tatra samīkṣya janakātmajā</li>
    </ul>
    <ul id="R_6.023.033">
        <li id="R_6.023.033ab">evaṃ lālapyamānāyāṃ sītāyāṃ tatra rākṣasaḥ</li>
        <li id="R_6.023.033cd">abhicakrāma bhartāram anīkasthaḥ kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_6.023.034">
        <li id="R_6.023.034ab">vijayasvāryaputreti so 'bhivādya prasādya ca</li>
        <li id="R_6.023.034cd">nyavedayad anuprāptaṃ prahastaṃ vāhinīpatim</li>
    </ul>
    <ul id="R_6.023.035">
        <li id="R_6.023.035ab">amātyaiḥ sahitaḥ sarvaiḥ prahastaḥ samupasthitaḥ</li>
        <li id="R_6.023.035cd">kiṃ cid ātyayikaṃ kāryaṃ teṣāṃ tvaṃ darśanaṃ kuru</li>
    </ul>
    <ul id="R_6.023.036">
        <li id="R_6.023.036ab">etac chrutvā daśagrīvo rākṣasaprativeditam</li>
        <li id="R_6.023.036cd">aśokavanikāṃ tyaktvā mantriṇāṃ darśanaṃ yayau</li>
    </ul>
    <ul id="R_6.023.037">
        <li id="R_6.023.037ab">sa tu sarvaṃ samarthyaiva mantribhiḥ kṛtyam ātmanaḥ</li>
        <li id="R_6.023.037cd">sabhāṃ praviśya vidadhe viditvā rāmavikramam</li>
    </ul>
    <ul id="R_6.023.038">
        <li id="R_6.023.038ab">antardhānaṃ tu tac chīrṣaṃ tac ca kārmukam uttamam</li>
        <li id="R_6.023.038cd">jagāma rāvaṇasyaiva niryāṇasamanantaram</li>
    </ul>
    <ul id="R_6.023.039">
        <li id="R_6.023.039ab">rākṣasendras tu taiḥ sārdhaṃ mantribhir bhīmavikramaiḥ</li>
        <li id="R_6.023.039cd">samarthayām āsa tadā rāmakāryaviniścayam</li>
    </ul>
    <ul id="R_6.023.040">
        <li id="R_6.023.040ab">avidūrasthitān sarvān balādhyakṣān hitaiṣiṇaḥ</li>
        <li id="R_6.023.040cd">abravīt kālasadṛśo rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_6.023.041">
        <li id="R_6.023.041ab">śīghraṃ bherīninādena sphuṭakoṇāhatena me</li>
        <li id="R_6.023.041cd">samānayadhvaṃ sainyāni vaktavyaṃ ca na kāraṇam</li>
    </ul>
    <ul id="R_6.023.042">
        <li id="R_6.023.042ab">
            <seg type="pāda" n="a">tatas tatheti pratigṛhya tad vaco</seg>
            <seg type="pāda" n="b">balādhipās te mahad ātmano balam</seg>
        </li>
        <li id="R_6.023.042cd">
            <seg type="pāda" n="c">samānayaṃś caiva samāgataṃ ca te</seg>
            <seg type="pāda" n="d">nyavedayan bhartari yuddhakāṅkṣiṇi</seg>
        </li>
    </ul>
    <ul id="R_6.024.001">
        <li id="R_6.024.001ab">sītāṃ tu mohitāṃ dṛṣṭvā saramā nāma rākṣasī</li>
        <li id="R_6.024.001cd">āsasādāśu vaidehīṃ priyāṃ praṇayinī sakhī</li>
    </ul>
    <ul id="R_6.024.002">
        <li id="R_6.024.002ab">sā hi tatra kṛtā mitraṃ sītayā rakṣyamāṇayā</li>
        <li id="R_6.024.002cd">rakṣantī rāvaṇād iṣṭā sānukrośā dṛḍhavratā</li>
    </ul>
    <ul id="R_6.024.003">
        <li id="R_6.024.003ab">sā dadarśa sakhīṃ sītāṃ saramā naṣṭacetanām</li>
        <li id="R_6.024.003cd">upāvṛtyotthitāṃ dhvastāṃ vaḍavām iva pāṃsuṣu</li>
    </ul>
    <ul id="R_6.024.004">
        <li id="R_6.024.004ab">tāṃ samāśvāsayām āsa sakhī snehena suvratā</li>
        <li id="R_6.024.004cd">uktā yad rāvaṇena tvaṃ pratyuktaṃ ca svayaṃ tvayā</li>
    </ul>
    <ul id="R_6.024.005">
        <li id="R_6.024.005ab">sakhīsnehena tad bhīru mayā sarvaṃ pratiśrutam</li>
        <li id="R_6.024.005cd">līnayā ganahe śūhye bhayam utsṛjya rāvaṇāt</li>
        <li id="R_6.024.005ef">tava hetor viśālākṣi na hi me jīvitaṃ priyam</li>
    </ul>
    <ul id="R_6.024.006">
        <li id="R_6.024.006ab">sa saṃbhrāntaś ca niṣkrānto yat kṛte rākṣasādhipaḥ</li>
        <li id="R_6.024.006cd">tac ca me viditaṃ sarvam abhiniṣkramya maithili</li>
    </ul>
    <ul id="R_6.024.007">
        <li id="R_6.024.007ab">na śakyaṃ sauptikaṃ kartuṃ rāmasya viditātmanaḥ</li>
        <li id="R_6.024.007cd">vadhaś ca puruṣavyāghre tasminn evopapadyate</li>
    </ul>
    <ul id="R_6.024.008">
        <li id="R_6.024.008ab">na caiva vānarā hantuṃ śakyāḥ pādapayodhinaḥ</li>
        <li id="R_6.024.008cd">surā devarṣabheṇeva rāmeṇa hi surakṣitāḥ</li>
    </ul>
    <ul id="R_6.024.009">
        <li id="R_6.024.009ab">dīrghavṛttabhujaḥ śrīmān mahoraskaḥ pratāpavān</li>
        <li id="R_6.024.009cd">dhanvī saṃhananopeto dharmātmā bhuvi viśrutaḥ</li>
    </ul>
    <ul id="R_6.024.010">
        <li id="R_6.024.010ab">vikrānto rakṣitā nityam ātmanaś ca parasya ca</li>
        <li id="R_6.024.010cd">lakṣmaṇena saha bhrātrā kuśalī nayaśāstravit</li>
    </ul>
    <ul id="R_6.024.011">
        <li id="R_6.024.011ab">hantā parabalaughānām acintyabalapauruṣaḥ</li>
        <li id="R_6.024.011cd">na hato rāghavaḥ śrīmān sīte śatrunibarhaṇaḥ</li>
    </ul>
    <ul id="R_6.024.012">
        <li id="R_6.024.012ab">ayuktabuddhikṛtyena sarvabhūtavirodhinā</li>
        <li id="R_6.024.012cd">iyaṃ prayuktā raudreṇa māyā māyāvidā tvayi</li>
    </ul>
    <ul id="R_6.024.013">
        <li id="R_6.024.013ab">śokas te vigataḥ sarvaḥ kalyāṇaṃ tvām upasthitam</li>
        <li id="R_6.024.013cd">dhruvaṃ tvāṃ bhajate lakṣmīḥ priyaṃ prītikaraṃ śṛṇu</li>
    </ul>
    <ul id="R_6.024.014">
        <li id="R_6.024.014ab">uttīrya sāgaraṃ rāmaḥ saha vānarasenayā</li>
        <li id="R_6.024.014cd">saṃniviṣṭaḥ samudrasya tīram āsādya dakṣiṇam</li>
    </ul>
    <ul id="R_6.024.015">
        <li id="R_6.024.015ab">dṛṣṭo me paripūrṇārthaḥ kākutsthaḥ sahalakṣmaṇaḥ</li>
        <li id="R_6.024.015cd">sahitaiḥ sāgarāntasthair balais tiṣṭhati rakṣitaḥ</li>
    </ul>
    <ul id="R_6.024.016">
        <li id="R_6.024.016ab">anena preṣitā ye ca rākṣasā laghuvikramaḥ</li>
        <li id="R_6.024.016cd">rāghavas tīrṇa ity evaṃ pravṛttis tair ihāhṛtā</li>
    </ul>
    <ul id="R_6.024.017">
        <li id="R_6.024.017ab">sa tāṃ śrutvā viśālākṣi pravṛttiṃ rākṣasādhipaḥ</li>
        <li id="R_6.024.017cd">eṣa mantrayate sarvaiḥ sacivaiḥ saha rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.024.018">
        <li id="R_6.024.018ab">iti bruvāṇā saramā rākṣasī sītayā saha</li>
        <li id="R_6.024.018cd">sarvodyogena sainyānāṃ śabdaṃ śuśrāva bhairavam</li>
    </ul>
    <ul id="R_6.024.019">
        <li id="R_6.024.019ab">daṇḍanirghātavādinyāḥ śrutvā bheryā mahāsvanam</li>
        <li id="R_6.024.019cd">uvāca saramā sītām idaṃ madhurabhāṣiṇī</li>
    </ul>
    <ul id="R_6.024.020">
        <li id="R_6.024.020ab">saṃnāhajananī hy eṣā bhairavā bhīru bherikā</li>
        <li id="R_6.024.020cd">bherīnādaṃ ca gambhīraṃ śṛṇu toyadanisvanam</li>
    </ul>
    <ul id="R_6.024.021">
        <li id="R_6.024.021ab">kalpyante mattamātaṃgā yujyante rathavājinaḥ</li>
        <li id="R_6.024.021cd">tatra tatra ca saṃnaddhāḥ saṃpatanti padātayaḥ</li>
    </ul>
    <ul id="R_6.024.022">
        <li id="R_6.024.022ab">āpūryante rājamārgāḥ sainyair adbhutadarśanaiḥ</li>
        <li id="R_6.024.022cd">vegavadbhir nadadbhiś ca toyaughair iva sāgaraḥ</li>
    </ul>
    <ul id="R_6.024.023">
        <li id="R_6.024.023ab">śāstrāṇāṃ ca prasannānāṃ carmaṇāṃ varmaṇāṃ tathā</li>
        <li id="R_6.024.023cd">rathavājigajānāṃ ca bhūṣitānāṃ ca rakṣasām</li>
    </ul>
    <ul id="R_6.024.024">
        <li id="R_6.024.024ab">prabhāṃ visṛjatāṃ paśya nānāvarṇāṃ samutthitām</li>
        <li id="R_6.024.024cd">vanaṃ nirdahato dharme yathārūpaṃ vibhāvasoḥ</li>
    </ul>
    <ul id="R_6.024.025">
        <li id="R_6.024.025ab">ghaṇṭānāṃ śṛṇu nirghoṣaṃ rathānāṃ śṛṇu nisvanam</li>
        <li id="R_6.024.025cd">hayānāṃ heṣamāṇānāṃ śṛṇu tūryadhvaniṃ yathā</li>
    </ul>
    <ul id="R_6.024.026">
        <li id="R_6.024.026ab">udyatāyudhahastānāṃ rākṣasendrānuyāyinām</li>
        <li id="R_6.024.026cd">saṃbhramo rakṣasām eṣa tumulo lomaharṣaṇaḥ</li>
    </ul>
    <ul id="R_6.024.027">
        <li id="R_6.024.027ab">śrīs tvāṃ bhajati śokaghnī rakṣasāṃ bhayam āgatam</li>
        <li id="R_6.024.027cd">rāmāt kamalapatrākṣi daityānām iva vāsavāt</li>
    </ul>
    <ul id="R_6.024.028">
        <li id="R_6.024.028ab">avajitya jitakrodhas tam acintyaparākramaḥ</li>
        <li id="R_6.024.028cd">rāvaṇaṃ samare hatvā bhartā tvādhigamiṣyati</li>
    </ul>
    <ul id="R_6.024.029">
        <li id="R_6.024.029ab">vikramiṣyati rakṣaḥsu bhartā te sahalakṣmaṇaḥ</li>
        <li id="R_6.024.029cd">yathā śatruṣu śatrughno viṣṇunā saha vāsavaḥ</li>
    </ul>
    <ul id="R_6.024.030">
        <li id="R_6.024.030ab">āgatasya hi rāmasya kṣipram aṅkagatāṃ satīm</li>
        <li id="R_6.024.030cd">ahaṃ drakṣyāmi siddhārthāṃ tvāṃ śatrau vinipātite</li>
    </ul>
    <ul id="R_6.024.031">
        <li id="R_6.024.031ab">aśrūṇy ānandajāni tvaṃ vartayiṣyasi śobhane</li>
        <li id="R_6.024.031cd">samāgamya pariṣvaktā tasyorasi mahorasaḥ</li>
    </ul>
    <ul id="R_6.024.032">
        <li id="R_6.024.032ab">acirān mokṣyate sīte devi te jaghanaṃ gatām</li>
        <li id="R_6.024.032cd">dhṛtām etāṃ bahūn māsān veṇīṃ rāmo mahābalaḥ</li>
    </ul>
    <ul id="R_6.024.033">
        <li id="R_6.024.033ab">tasya dṛṣṭvā mukhaṃ devi pūrṇacandram ivoditam</li>
        <li id="R_6.024.033cd">mokṣyase śokajaṃ vāri nirmokam iva pannagī</li>
    </ul>
    <ul id="R_6.024.034">
        <li id="R_6.024.034ab">rāvaṇaṃ samare hatvā nacirād eva maithili</li>
        <li id="R_6.024.034cd">tvayā samagraṃ priyayā sukhārho lapsyate sukham</li>
    </ul>
    <ul id="R_6.024.035">
        <li id="R_6.024.035ab">samāgatā tvaṃ rāmeṇa modiṣyasi mahātmanā</li>
        <li id="R_6.024.035cd">suvarṣeṇa samāyuktā yathā sasyena medinī</li>
    </ul>
    <ul id="R_6.024.036">
        <li id="R_6.024.036ab">
            <seg type="pāda" n="a">girivaram abhito 'nuvartamāno</seg>
            <seg type="pāda" n="b">haya iva maṇḍalam āśu yaḥ karoti</seg>
        </li>
        <li id="R_6.024.036cd">
            <seg type="pāda" n="c">tam iha śaraṇam abhyupehi devi</seg>
            <seg type="pāda" n="d">divasakaraṃ prabhavo hy ayaṃ prajānām</seg>
        </li>
    </ul>
    <ul id="R_6.025.001">
        <li id="R_6.025.001ab">atha tāṃ jātasaṃtāpāṃ tena vākyena mohitām</li>
        <li id="R_6.025.001cd">saramā hlādayām āsa pṛtivīṃ dyaur ivāmbhasā</li>
    </ul>
    <ul id="R_6.025.002">
        <li id="R_6.025.002ab">tatas tasyā hitaṃ sakhyāś cikīrṣantī sakhī vacaḥ</li>
        <li id="R_6.025.002cd">uvāca kāle kālajñā smitapūrvābhibhāṣiṇī</li>
    </ul>
    <ul id="R_6.025.003">
        <li id="R_6.025.003ab">utsaheyam ahaṃ gatvā tvadvākyam asitekṣaṇe</li>
        <li id="R_6.025.003cd">nivedya kuśalaṃ rāme praticchannā nivartitum</li>
    </ul>
    <ul id="R_6.025.004">
        <li id="R_6.025.004ab">na hi me kramamāṇāyā nirālambe vihāyasi</li>
        <li id="R_6.025.004cd">samartho gatim anvetuṃ pavano garuḍo 'pi vā</li>
    </ul>
    <ul id="R_6.025.005">
        <li id="R_6.025.005ab">evaṃ bruvāṇāṃ tāṃ sītā saramāṃ punar abravīt</li>
        <li id="R_6.025.005cd">madhuraṃ ślakṣṇayā vācā pūrvaśokābhipannayā</li>
    </ul>
    <ul id="R_6.025.006">
        <li id="R_6.025.006ab">samarthā gaganaṃ gantum api vā tvaṃ rasātalam</li>
        <li id="R_6.025.006cd">avagacchāmy akartavyaṃ kartavyaṃ te madantare</li>
    </ul>
    <ul id="R_6.025.007">
        <li id="R_6.025.007ab">matpriyaṃ yadi kartavyaṃ yadi buddhiḥ sthirā tava</li>
        <li id="R_6.025.007cd">jñātum icchāmi taṃ gatvā kiṃ karotīti rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.025.008">
        <li id="R_6.025.008ab">sa hi māyābalaḥ krūro rāvaṇaḥ śatrurāvaṇaḥ</li>
        <li id="R_6.025.008cd">māṃ mohayati duṣṭātmā pītamātreva vāruṇī</li>
    </ul>
    <ul id="R_6.025.009">
        <li id="R_6.025.009ab">tarjāpayati māṃ nityaṃ bhartsāpayati cāsakṛt</li>
        <li id="R_6.025.009cd">rākṣasībhiḥ sughorābhir yā māṃ rakṣanti nityaśaḥ</li>
    </ul>
    <ul id="R_6.025.010">
        <li id="R_6.025.010ab">udvignā śaṅkitā cāsmi na ca svasthaṃ mano mama</li>
        <li id="R_6.025.010cd">tad bhayāc cāham udvignā aśokavanikāṃ gatāḥ</li>
    </ul>
    <ul id="R_6.025.011">
        <li id="R_6.025.011ab">yadi nāma kathā tasya niścitaṃ vāpi yad bhavet</li>
        <li id="R_6.025.011cd">nivedayethāḥ sarvaṃ tat paro me syād anugrahaḥ</li>
    </ul>
    <ul id="R_6.025.012">
        <li id="R_6.025.012ab">sā tv evaṃ bruvatīṃ sītāṃ saramā valgubhāṣiṇī</li>
        <li id="R_6.025.012cd">uvāca vacanaṃ tasyāḥ spṛśantī bāṣpaviklavam</li>
    </ul>
    <ul id="R_6.025.013">
        <li id="R_6.025.013ab">eṣa te yady abhiprāyas tasmād gacchāmi jānaki</li>
        <li id="R_6.025.013cd">gṛhya śatror abhiprāyam upāvṛttāṃ ca paśya mām</li>
    </ul>
    <ul id="R_6.025.014">
        <li id="R_6.025.014ab">evam uktvā tato gatvā samīpaṃ tasya rakṣasaḥ</li>
        <li id="R_6.025.014cd">śuśrāva kathitaṃ tasya rāvaṇasya samantriṇaḥ</li>
    </ul>
    <ul id="R_6.025.015">
        <li id="R_6.025.015ab">sā śrutvā niścayaṃ tasya niścayajñā durātmanaḥ</li>
        <li id="R_6.025.015cd">punar evāgamat kṣipram aśokavanikāṃ tadā</li>
    </ul>
    <ul id="R_6.025.016">
        <li id="R_6.025.016ab">sā praviṣṭā punas tatra dadarśa janakātmajām</li>
        <li id="R_6.025.016cd">pratīkṣamāṇāṃ svām eva bhraṣṭapadmām iva śriyam</li>
    </ul>
    <ul id="R_6.025.017">
        <li id="R_6.025.017ab">tāṃ tu sītā punaḥ prāptāṃ saramāṃ valgubhāṣiṇīm</li>
        <li id="R_6.025.017cd">pariṣvajya ca susnigdhaṃ dadau ca svayam āsanam</li>
    </ul>
    <ul id="R_6.025.018">
        <li id="R_6.025.018ab">ihāsīnā sukhaṃ sarvam ākhyāhi mama tattvataḥ</li>
        <li id="R_6.025.018cd">krūrasya niścayaṃ tasya rāvaṇasya durātmanaḥ</li>
    </ul>
    <ul id="R_6.025.019">
        <li id="R_6.025.019ab">evam uktā tu saramā sītayā vepamānayā</li>
        <li id="R_6.025.019cd">kathitaṃ sarvam ācaṣṭa rāvaṇasya samantriṇaḥ</li>
    </ul>
    <ul id="R_6.025.020">
        <li id="R_6.025.020ab">jananyā rākṣasendro vai tvanmokṣārthaṃ bṛhadvacaḥ</li>
        <li id="R_6.025.020cd">aviddhena ca vaidehi mantrivṛddhena bodhitaḥ</li>
    </ul>
    <ul id="R_6.025.021">
        <li id="R_6.025.021ab">dīyatām abhisatkṛtya manujendrāya maithilī</li>
        <li id="R_6.025.021cd">nidarśanaṃ te paryāptaṃ janasthāne yad adbhutam</li>
    </ul>
    <ul id="R_6.025.022">
        <li id="R_6.025.022ab">laṅghanaṃ ca samudrasya darśanaṃ ca hanūmataḥ</li>
        <li id="R_6.025.022cd">vadhaṃ ca rakṣasāṃ yuddhe kaḥ kuryān mānuṣo bhuvi</li>
    </ul>
    <ul id="R_6.025.023">
        <li id="R_6.025.023ab">evaṃ sa mantrivṛddhaiś ca mātrā ca bahu bhāṣitaḥ</li>
        <li id="R_6.025.023cd">na tvām utsahate moktum artahm arthaparo yathā</li>
    </ul>
    <ul id="R_6.025.024">
        <li id="R_6.025.024ab">notsahaty amṛto moktuṃ yuddhe tvām iti maithili</li>
        <li id="R_6.025.024cd">sāmātyasya nṛśaṃsasya niścayo hy eṣa vartate</li>
    </ul>
    <ul id="R_6.025.025">
        <li id="R_6.025.025ab">tad eṣā susthirā buddhir mṛtyulobhād upasthitā</li>
        <li id="R_6.025.025cd">bhayān na śaktas tvāṃ moktum anirastas tu saṃyuge</li>
        <li id="R_6.025.025ef">rākṣasānāṃ ca sarveṣām ātmanaś ca vadhena hi</li>
    </ul>
    <ul id="R_6.025.026">
        <li id="R_6.025.026ab">nihatya rāvaṇaṃ saṃkhye sarvathā niśitaiḥ śaraiḥ</li>
        <li id="R_6.025.026cd">pratineṣyati rāmas tvām ayodhyām asitekṣaṇe</li>
    </ul>
    <ul id="R_6.025.027">
        <li id="R_6.025.027ab">etasminn antare śabdo bherīśaṅkhasamākulaḥ</li>
        <li id="R_6.025.027cd">śruto vai sarvasainyānāṃ kampayan dharaṇītalam</li>
    </ul>
    <ul id="R_6.025.028">
        <li id="R_6.025.028ab">
            <seg type="pāda" n="a">śrutvā tu taṃ vānarasainyaśabdaṃ</seg>
            <seg type="pāda" n="b">laṅkāgatā rākṣasarājabhṛtyāḥ</seg>
        </li>
        <li id="R_6.025.028cd">
            <seg type="pāda" n="c">naṣṭaujaso dainyaparītaceṣṭāḥ</seg>
            <seg type="pāda" n="d">śreyo na paśyanti nṛpasya doṣaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.026.001">
        <li id="R_6.026.001ab">tena śaṅkhavimiśreṇa bherīśabdena rāghavaḥ</li>
        <li id="R_6.026.001cd">upayato mahābāhū rāmaḥ parapuraṃjayaḥ</li>
    </ul>
    <ul id="R_6.026.002">
        <li id="R_6.026.002ab">taṃ ninādaṃ niśamyātha rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.026.002cd">muhūrtaṃ dhyānam āsthāya sacivān abhyudaikṣata</li>
    </ul>
    <ul id="R_6.026.003">
        <li id="R_6.026.003ab">atha tān sacivāṃs tatra sarvān ābhāṣya rāvaṇaḥ</li>
        <li id="R_6.026.003cd">sabhāṃ saṃnādayan sarvām ity uvāca mahābalaḥ</li>
    </ul>
    <ul id="R_6.026.004">
        <li id="R_6.026.004ab">taraṇaṃ sāgarasyāpi vikramaṃ balasaṃcayam</li>
        <li id="R_6.026.004cd">yad uktavanto rāmasya bhavantas tan mayā śrutam</li>
        <li id="R_6.026.004ef">bhavataś cāpy ahaṃ vedmi yuddhe satyaparākramān</li>
    </ul>
    <ul id="R_6.026.005">
        <li id="R_6.026.005ab">tatas tu sumahāprājño mālyavān nāma rākṣasaḥ</li>
        <li id="R_6.026.005cd">rāvaṇasya vacaḥ śrutvā mātuḥ paitāmaho 'bravīt</li>
    </ul>
    <ul id="R_6.026.006">
        <li id="R_6.026.006ab">vidyāsv abhivinīto yo rājā rājan nayānugaḥ</li>
        <li id="R_6.026.006cd">sa śāsti ciram aiśvaryam arīṃś ca kurute vaśe</li>
    </ul>
    <ul id="R_6.026.007">
        <li id="R_6.026.007ab">saṃdadhāno hi kālena vigṛhṇaṃś cāribhiḥ saha</li>
        <li id="R_6.026.007cd">svapakṣavardhanaṃ kurvan mahad aiśvaryam aśnute</li>
    </ul>
    <ul id="R_6.026.008">
        <li id="R_6.026.008ab">hīyamānena kartavyo rājñā saṃdhiḥ samena ca</li>
        <li id="R_6.026.008cd">na śatrum avamanyeta jyāyān kurvīta vigraham</li>
    </ul>
    <ul id="R_6.026.009">
        <li id="R_6.026.009ab">tan mahyaṃ rocate saṃdhiḥ saha rāmeṇa rāvaṇa</li>
        <li id="R_6.026.009cd">yadartham abhiyuktāḥ sma sītā tasmai pradīyatām</li>
    </ul>
    <ul id="R_6.026.010">
        <li id="R_6.026.010ab">tasya devarṣayaḥ sarve gandharvāś ca jayaiṣiṇaḥ</li>
        <li id="R_6.026.010cd">virodhaṃ mā gamas tena saṃdhis te tena rocatām</li>
    </ul>
    <ul id="R_6.026.011">
        <li id="R_6.026.011ab">asṛjad bhagavān pakṣau dvāv eva hi pitāmahaḥ</li>
        <li id="R_6.026.011cd">surāṇām asurāṇāṃ ca dharmādharmau tadāśrayau</li>
    </ul>
    <ul id="R_6.026.012">
        <li id="R_6.026.012ab">dharmo hi śrūyate pakṣaḥ surāṇāṃ ca mahātmanām</li>
        <li id="R_6.026.012cd">adharmo rakṣasaṃ pakṣo hy asurāṇāṃ ca rāvaṇa</li>
    </ul>
    <ul id="R_6.026.013">
        <li id="R_6.026.013ab">dharmo vai grasate 'dharmaṃ tataḥ kṛtam abhūd yugam</li>
        <li id="R_6.026.013cd">adharmo grasate dharmaṃ tatas tiṣyaḥ pravartate</li>
    </ul>
    <ul id="R_6.026.014">
        <li id="R_6.026.014ab">tat tvayā caratā lokān dharmo vinihato mahān</li>
        <li id="R_6.026.014cd">adharmaḥ pragṛhītaś ca tenāsmadbalinaḥ pare</li>
    </ul>
    <ul id="R_6.026.015">
        <li id="R_6.026.015ab">sa pramādād vivṛddhas te 'dharmo 'hir grasate hi naḥ</li>
        <li id="R_6.026.015cd">vivardhayati pakṣaṃ ca surāṇāṃ surabhāvanaḥ</li>
    </ul>
    <ul id="R_6.026.016">
        <li id="R_6.026.016ab">viṣayeṣu prasaktena yatkiṃcitkāriṇā tvayā</li>
        <li id="R_6.026.016cd">ṛṣīṇām agnikalpānām udvego janito mahān</li>
        <li id="R_6.026.016ef">teṣāṃ prabhāvo durdharṣaḥ pradīpta iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.026.017">
        <li id="R_6.026.017ab">tapasā bhāvitātmāno dharmasyānugrahe ratāḥ</li>
        <li id="R_6.026.017cd">mukhyair yajñair yajanty ete nityaṃ tais tair dvijātayaḥ</li>
    </ul>
    <ul id="R_6.026.018">
        <li id="R_6.026.018ab">juhvaty agnīṃś ca vidhivad vedāṃś coccair adhīyate</li>
        <li id="R_6.026.018cd">abhibhūya ca rakṣāṃsi brahmaghoṣān udairayan</li>
        <li id="R_6.026.018ef">diśo vipradrutāḥ sarve stanayitnur ivoṣṇage</li>
    </ul>
    <ul id="R_6.026.019">
        <li id="R_6.026.019ab">ṛṣīṇām agnikalpānām agnihotrasamutthitaḥ</li>
        <li id="R_6.026.019cd">ādatte rakṣasāṃ tejo dhūmo vyāpya diśo daśa</li>
    </ul>
    <ul id="R_6.026.020">
        <li id="R_6.026.020ab">teṣu teṣu ca deśeṣu puṇyeṣu ca dṛḍhavrataiḥ</li>
        <li id="R_6.026.020cd">caryamāṇaṃ tapas tīvraṃ saṃtāpayati rākṣasān</li>
    </ul>
    <ul id="R_6.026.021">
        <li id="R_6.026.021ab">utpātān vividhān dṛṣṭvā ghorān bahuvidhāṃs tathā</li>
        <li id="R_6.026.021cd">vināśam anupaśyāmi sarveṣāṃ rakṣasām aham</li>
    </ul>
    <ul id="R_6.026.022">
        <li id="R_6.026.022ab">kharābhis tanitā ghorā meghāḥ pratibhayaṃkaraḥ</li>
        <li id="R_6.026.022cd">śoṇitenābhivarṣanti laṅkām uṣṇena sarvataḥ</li>
    </ul>
    <ul id="R_6.026.023">
        <li id="R_6.026.023ab">rudatāṃ vāhanānāṃ ca prapatanty asrabindavaḥ</li>
        <li id="R_6.026.023cd">dhvajā dhvastā vivarṇāś ca na prabhānti yathāpuram</li>
    </ul>
    <ul id="R_6.026.024">
        <li id="R_6.026.024ab">vyālā gomāyavo gṛdhrā vāśanti ca subhairavam</li>
        <li id="R_6.026.024cd">praviśya laṅkām aniśaṃ samavāyāṃś ca kurvate</li>
    </ul>
    <ul id="R_6.026.025">
        <li id="R_6.026.025ab">kālikāḥ pāṇḍurair dantaiḥ prahasanty agrataḥ sthitāḥ</li>
        <li id="R_6.026.025cd">striyaḥ svapneṣu muṣṇantyo gṛhāṇi pratibhāṣya ca</li>
    </ul>
    <ul id="R_6.026.026">
        <li id="R_6.026.026ab">gṛhāṇāṃ balikarmāṇi śvānaḥ paryupabhuñjate</li>
        <li id="R_6.026.026cd">kharā goṣu prajāyante mūṣikā nakulaiḥ saha</li>
    </ul>
    <ul id="R_6.026.027">
        <li id="R_6.026.027ab">mārjārā dvīpibhiḥ sārdhaṃ sūkarāḥ śunakaiḥ saha</li>
        <li id="R_6.026.027cd">kiṃnarā rākṣasaiś cāpi sameyur mānuṣaiḥ saha</li>
    </ul>
    <ul id="R_6.026.028">
        <li id="R_6.026.028ab">pāṇḍurā raktapādāś ca vihagāḥ kālacoditāḥ</li>
        <li id="R_6.026.028cd">rākṣasānāṃ vināśāya kapotā vicaranti ca</li>
    </ul>
    <ul id="R_6.026.029">
        <li id="R_6.026.029ab">cīkī kūcīti vāśantyaḥ śārikā veśmasu sthitāḥ</li>
        <li id="R_6.026.029cd">patanti grathitāś cāpi nirjitāḥ kalahaiṣiṇaḥ</li>
    </ul>
    <ul id="R_6.026.030">
        <li id="R_6.026.030ab">karālo vikaṭo muṇḍaḥ puruṣaḥ kṛṣṇapiṅgalaḥ</li>
        <li id="R_6.026.030cd">kālo gṛhāṇi sarveṣāṃ kāle kāle 'nvavekṣate</li>
        <li id="R_6.026.030ef">etāny anyāni duṣṭāni nimittāny utpatanti ca</li>
    </ul>
    <ul id="R_6.026.031">
        <li id="R_6.026.031ab">viṣṇuṃ manyāmahe rāmaṃ mānuṣaṃ deham āsthitam</li>
        <li id="R_6.026.031cd">na hi mānuṣamātro 'sau rāghavo dṛḍhavikramaḥ</li>
    </ul>
    <ul id="R_6.026.032">
        <li id="R_6.026.032ab">yena baddhaḥ samudrasya sa setuḥ paramādbhutaḥ</li>
        <li id="R_6.026.032cd">kuruṣva nararājena saṃdhiṃ rāmeṇa rāvaṇa</li>
    </ul>
    <ul id="R_6.026.033">
        <li id="R_6.026.033ab">
            <seg type="pāda" n="a">idaṃ vacas tatra nigadya mālyavan</seg>
            <seg type="pāda" n="b">parīkṣya rakṣo'dhipater manaḥ punaḥ</seg>
        </li>
        <li id="R_6.026.033cd">
            <seg type="pāda" n="c">anuttameṣūttamapauruṣo balī</seg>
            <seg type="pāda" n="d">babhūva tūṣṇīṃ samavekṣya rāvaṇam</seg>
        </li>
    </ul>
    <ul id="R_6.027.001">
        <li id="R_6.027.001ab">tat tu mālyavato vākyaṃ hitam uktaṃ daśānanaḥ</li>
        <li id="R_6.027.001cd">na marṣayati duṣṭātmā kālasya vaśam āgataḥ</li>
    </ul>
    <ul id="R_6.027.002">
        <li id="R_6.027.002ab">sa baddhvā bhrukuṭiṃ vaktre krodhasya vaśam āgataḥ</li>
        <li id="R_6.027.002cd">amarṣāt parivṛttākṣo mālyavantam athābravīt</li>
    </ul>
    <ul id="R_6.027.003">
        <li id="R_6.027.003ab">hitabuddhyā yad ahitaṃ vacaḥ paruṣam ucyate</li>
        <li id="R_6.027.003cd">parapakṣaṃ praviśyaiva naitac chrotragataṃ mama</li>
    </ul>
    <ul id="R_6.027.004">
        <li id="R_6.027.004ab">mānuṣaṃ kṛpaṇaṃ rāmam ekaṃ śākhāmṛgāśrayam</li>
        <li id="R_6.027.004cd">samarthaṃ manyase kena tyaktaṃ pitrā vanālayam</li>
    </ul>
    <ul id="R_6.027.005">
        <li id="R_6.027.005ab">rakṣasām īśvaraṃ māṃ ca devatānāṃ bhayaṃkaram</li>
        <li id="R_6.027.005cd">hīnaṃ māṃ manyase kena ahīnaṃ sarvavikramaiḥ</li>
    </ul>
    <ul id="R_6.027.006">
        <li id="R_6.027.006ab">vīradveṣeṇa vā śaṅke pakṣapātena vā ripoḥ</li>
        <li id="R_6.027.006cd">tvayāhaṃ paruṣāṇy uktaḥ paraprotsāhanena vā</li>
    </ul>
    <ul id="R_6.027.007">
        <li id="R_6.027.007ab">prabhavantaṃ padasthaṃ hi paruṣaṃ ko 'bhidhāsyati</li>
        <li id="R_6.027.007cd">paṇḍitaḥ śāstratattvajño vinā protsāhanād ripoḥ</li>
    </ul>
    <ul id="R_6.027.008">
        <li id="R_6.027.008ab">ānīya ca vanāt sītāṃ padmahīnām iva śriyam</li>
        <li id="R_6.027.008cd">kimarthaṃ pratidāsyāmi rāghavasya bhayād aham</li>
    </ul>
    <ul id="R_6.027.009">
        <li id="R_6.027.009ab">vṛtaṃ vānarakoṭībhiḥ sasugrīvaṃ salakṣmaṇam</li>
        <li id="R_6.027.009cd">paśya kaiś cid ahobhis tvaṃ rāghavaṃ nihataṃ mayā</li>
    </ul>
    <ul id="R_6.027.010">
        <li id="R_6.027.010ab">dvandve yasya na tiṣṭhanti daivatāny api saṃyuge</li>
        <li id="R_6.027.010cd">sa kasmād rāvaṇo yuddhe bhayam āhārayiṣyati</li>
    </ul>
    <ul id="R_6.027.011">
        <li id="R_6.027.011ab">dvidhā bhajyeyam apy evaṃ na nameyaṃ tu kasya cit</li>
        <li id="R_6.027.011cd">eṣa me sahajo doṣaḥ svabhāvo duratikramaḥ</li>
    </ul>
    <ul id="R_6.027.012">
        <li id="R_6.027.012ab">yadi tāvat samudre tu setur baddho yadṛcchayā</li>
        <li id="R_6.027.012cd">rāmeṇa vismayaḥ ko 'tra yena te bhayam āgatam</li>
    </ul>
    <ul id="R_6.027.013">
        <li id="R_6.027.013ab">sa tu tīrtvārṇavaṃ rāmaḥ saha vānarasenayā</li>
        <li id="R_6.027.013cd">pratijānāmi te satyaṃ na jīvan pratiyāsyati</li>
    </ul>
    <ul id="R_6.027.014">
        <li id="R_6.027.014ab">evaṃ bruvāṇaṃ saṃrabdhaṃ ruṣṭaṃ vijñāya rāvaṇam</li>
        <li id="R_6.027.014cd">vrīḍito mālyavān vākyaṃ nottaraṃ pratyapadyata</li>
    </ul>
    <ul id="R_6.027.015">
        <li id="R_6.027.015ab">jayāśiṣā ca rājānaṃ vardhayitvā yathocitam</li>
        <li id="R_6.027.015cd">mālyavān abhyanujñāto jagāma svaṃ niveśanam</li>
    </ul>
    <ul id="R_6.027.016">
        <li id="R_6.027.016ab">rāvaṇas tu sahāmātyo mantrayitvā vimṛśya ca</li>
        <li id="R_6.027.016cd">laṅkāyām atulāṃ guptiṃ kārayām āsa rākṣasaḥ</li>
    </ul>
    <ul id="R_6.027.017">
        <li id="R_6.027.017ab">vyādideśa ca pūrvasyāṃ prahastaṃ dvāri rākṣasaṃ</li>
        <li id="R_6.027.017cd">dakṣiṇasyāṃ mahāvīryau mahāpārśva mahodarau</li>
    </ul>
    <ul id="R_6.027.018">
        <li id="R_6.027.018ab">paścimāyām atho dvāri putram indrajitaṃ tathā</li>
        <li id="R_6.027.018cd">vyādideśa mahāmāyaṃ rākṣasair bahubhir vṛtam</li>
    </ul>
    <ul id="R_6.027.019">
        <li id="R_6.027.019ab">uttarasyāṃ puradvāri vyādiśya śukasāraṇau</li>
        <li id="R_6.027.019cd">svayaṃ cātra bhaviṣyāmi mantriṇas tān uvāca ha</li>
    </ul>
    <ul id="R_6.027.020">
        <li id="R_6.027.020ab">rākṣasaṃ tu virūpākṣaṃ mahāvīryaparākramam</li>
        <li id="R_6.027.020cd">madhyame 'sthāpayad gulme bahubhiḥ saha rākṣasaiḥ</li>
    </ul>
    <ul id="R_6.027.021">
        <li id="R_6.027.021ab">evaṃvidhānaṃ laṅkāyāṃ kṛtvā rākṣasapuṃgavaḥ</li>
        <li id="R_6.027.021cd">mene kṛtārtham ātmānaṃ kṛtāntavaśam āgataḥ</li>
    </ul>
    <ul id="R_6.027.022">
        <li id="R_6.027.022ab">
            <seg type="pāda" n="a">visarjayām āsa tataḥ sa mantriṇo</seg>
            <seg type="pāda" n="b">vidhānam ājñāpya purasya puṣkalam</seg>
        </li>
        <li id="R_6.027.022cd">
            <seg type="pāda" n="c">jayāśiṣā mantragaṇena pūjito</seg>
            <seg type="pāda" n="d">viveśa so 'ntaḥpuram ṛddhiman mahat</seg>
        </li>
    </ul>
    <ul id="R_6.028.001">
        <li id="R_6.028.001ab">naravānararājau tau sa ca vāyusutaḥ kapiḥ</li>
        <li id="R_6.028.001cd">jāmbavān ṛkṣarājaś ca rākṣasaś ca vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.028.002">
        <li id="R_6.028.002ab">aṅgado vāliputraś ca saumitriḥ śarabhaḥ kapiḥ</li>
        <li id="R_6.028.002cd">suṣeṇaḥ sahadāyādo maindo dvivida eva ca</li>
    </ul>
    <ul id="R_6.028.003">
        <li id="R_6.028.003ab">gajo gavākṣo kumudo nalo 'tha panasas tathā</li>
        <li id="R_6.028.003cd">amitraviṣayaṃ prāptāḥ samavetāḥ samarthayan</li>
    </ul>
    <ul id="R_6.028.004">
        <li id="R_6.028.004ab">iyaṃ sā lakṣyate laṅkā purī rāvaṇapālitā</li>
        <li id="R_6.028.004cd">sāsuroragagandharvair amarair api durjayā</li>
    </ul>
    <ul id="R_6.028.005">
        <li id="R_6.028.005ab">kāryasiddhiṃ puraskṛtya mantrayadhvaṃ vinirṇaye</li>
        <li id="R_6.028.005cd">nityaṃ saṃnihito hy atra rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_6.028.006">
        <li id="R_6.028.006ab">tathā teṣu bruvāṇeṣu rāvaṇāvarajo 'bravīt</li>
        <li id="R_6.028.006cd">vākyam agrāmyapadavat puṣkalārthaṃ vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.028.007">
        <li id="R_6.028.007ab">analaḥ śarabhaś caiva saṃpātiḥ praghasas tathā</li>
        <li id="R_6.028.007cd">gatvā laṅkāṃ mamāmātyāḥ purīṃ punar ihāgatāḥ</li>
    </ul>
    <ul id="R_6.028.008">
        <li id="R_6.028.008ab">bhūtvā śakunayaḥ sarve praviṣṭāś ca ripor balam</li>
        <li id="R_6.028.008cd">vidhānaṃ vihitaṃ yac ca tad dṛṣṭvā samupasthitāḥ</li>
    </ul>
    <ul id="R_6.028.009">
        <li id="R_6.028.009ab">saṃvidhānaṃ yathāhus te rāvaṇasya durātmanaḥ</li>
        <li id="R_6.028.009cd">rāma tad bruvataḥ sarvaṃ yathātathyena me śṛṇu</li>
    </ul>
    <ul id="R_6.028.010">
        <li id="R_6.028.010ab">pūrvaṃ prahastaḥ sabalo dvāram āsādya tiṣṭhati</li>
        <li id="R_6.028.010cd">dakṣiṇaṃ ca mahāvīryau mahāpārśvamahodarau</li>
    </ul>
    <ul id="R_6.028.011">
        <li id="R_6.028.011ab">indrajit paścimadvāraṃ rākṣasair bahubhir vṛtaḥ</li>
        <li id="R_6.028.011cd">paṭṭasāsidhanuṣmadbhiḥ śūlamudgarapāṇibhiḥ</li>
    </ul>
    <ul id="R_6.028.012">
        <li id="R_6.028.012ab">nānāpraharaṇaiḥ śūrair āvṛto rāvaṇātmajaḥ</li>
        <li id="R_6.028.012cd">rākṣasānāṃ sahasrais tu bahubhiḥ śastrapāṇibhiḥ</li>
    </ul>
    <ul id="R_6.028.013">
        <li id="R_6.028.013ab">yuktaḥ paramasaṃvigno rākṣasair bahubhir vṛtaḥ</li>
        <li id="R_6.028.013cd">uttaraṃ nagaradvāraṃ rāvaṇaḥ svayam āsthitaḥ</li>
    </ul>
    <ul id="R_6.028.014">
        <li id="R_6.028.014ab">virūpākṣas tu mahatā śūlakhaḍgadhanuṣmatā</li>
        <li id="R_6.028.014cd">balena rākṣasaiḥ sārdhaṃ madhyamaṃ gulmam āsthitaḥ</li>
    </ul>
    <ul id="R_6.028.015">
        <li id="R_6.028.015ab">etān evaṃvidhān gulmāṃl laṅkāyāṃ samudīkṣya te</li>
        <li id="R_6.028.015cd">māmakāḥ sacivāḥ sarve śīghraṃ punar ihāgatāḥ</li>
    </ul>
    <ul id="R_6.028.016">
        <li id="R_6.028.016ab">gajānāṃ ca sahasraṃ ca rathānām ayutaṃ pure</li>
        <li id="R_6.028.016cd">hayānām ayute dve ca sāgrakoṭī ca rakṣasām</li>
    </ul>
    <ul id="R_6.028.017">
        <li id="R_6.028.017ab">vikrāntā balavantaś ca saṃyugeṣv ātatāyinaḥ</li>
        <li id="R_6.028.017cd">iṣṭā rākṣasarājasya nityam ete niśācarāḥ</li>
    </ul>
    <ul id="R_6.028.018">
        <li id="R_6.028.018ab">ekaikasyātra yuddhārthe rākṣasasya viśāṃ pate</li>
        <li id="R_6.028.018cd">parivāraḥ sahasrāṇāṃ sahasram upatiṣṭhate</li>
    </ul>
    <ul id="R_6.028.019">
        <li id="R_6.028.019ab">etāṃ pravṛttiṃ laṅkāyāṃ mantriproktaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.028.019cd">rāmaṃ kamalapatrākṣam idam uttaram abravīt</li>
    </ul>
    <ul id="R_6.028.020">
        <li id="R_6.028.020ab">kuberaṃ tu yadā rāma rāvaṇaḥ pratyayudhyata</li>
        <li id="R_6.028.020cd">ṣaṣṭiḥ śatasahasrāṇi tadā niryānti rākṣasāḥ</li>
    </ul>
    <ul id="R_6.028.021">
        <li id="R_6.028.021ab">parākrameṇa vīryeṇa tejasā sattvagauravāt</li>
        <li id="R_6.028.021cd">sadṛśā yo 'tra darpeṇa rāvaṇasya durātmanaḥ</li>
    </ul>
    <ul id="R_6.028.022">
        <li id="R_6.028.022ab">atra manyur na kartavyo roṣaye tvāṃ na bhīṣaye</li>
        <li id="R_6.028.022cd">samartho hy asi vīryeṇa surāṇām api nigrahe</li>
    </ul>
    <ul id="R_6.028.023">
        <li id="R_6.028.023ab">tad bhavāṃś caturaṅgeṇa balena mahatā vṛtaḥ</li>
        <li id="R_6.028.023cd">vyūhyedaṃ vānarānīkaṃ nirmathiṣyasi rāvaṇam</li>
    </ul>
    <ul id="R_6.028.024">
        <li id="R_6.028.024ab">rāvaṇāvaraje vākyam evaṃ bruvati rāghavaḥ</li>
        <li id="R_6.028.024cd">śatrūṇāṃ pratighātārtham idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.028.025">
        <li id="R_6.028.025ab">pūrvadvāre tu laṅkāyā nīlo vānarapuṃgavaḥ</li>
        <li id="R_6.028.025cd">prahastaṃ pratiyoddhā syād vānarair bahubhir vṛtaḥ</li>
    </ul>
    <ul id="R_6.028.026">
        <li id="R_6.028.026ab">aṅgado vāliputras tu balena mahatā vṛtaḥ</li>
        <li id="R_6.028.026cd">dakṣiṇe bādhatāṃ dvāre mahāpārśvamahodarau</li>
    </ul>
    <ul id="R_6.028.027">
        <li id="R_6.028.027ab">hanūmān paścimadvāraṃ nipīḍya pavanātmajaḥ</li>
        <li id="R_6.028.027cd">praviśatv aprameyātmā bahubhiḥ kapibhir vṛtaḥ</li>
    </ul>
    <ul id="R_6.028.028">
        <li id="R_6.028.028ab">daityadānavasaṃghānām ṛṣīṇāṃ ca mahātmanām</li>
        <li id="R_6.028.028cd">viprakārapriyaḥ kṣudro varadānabalānvitaḥ</li>
    </ul>
    <ul id="R_6.028.029">
        <li id="R_6.028.029ab">parikrāmati yaḥ sarvāṃl lokān saṃtāpayan prajāḥ</li>
        <li id="R_6.028.029cd">tasyāhaṃ rākṣasendrasya svayam eva vadhe dhṛtaḥ</li>
    </ul>
    <ul id="R_6.028.030">
        <li id="R_6.028.030ab">uttaraṃ nagaradvāram ahaṃ saumitriṇā saha</li>
        <li id="R_6.028.030cd">nipīḍyābhipravekṣyāmi sabalo yatra rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.028.031">
        <li id="R_6.028.031ab">vānarendraś ca balavān ṛkṣarājaś ca jāmbavān</li>
        <li id="R_6.028.031cd">rākṣasendrānujaś caiva gulme bhavatu madhyame</li>
    </ul>
    <ul id="R_6.028.032">
        <li id="R_6.028.032ab">na caiva mānuṣaṃ rūpaṃ kāryaṃ haribhir āhave</li>
        <li id="R_6.028.032cd">eṣā bhavatu naḥ saṃjñā yuddhe 'smin vānare bale</li>
    </ul>
    <ul id="R_6.028.033">
        <li id="R_6.028.033ab">vānarā eva niścihnaṃ svajane 'smin bhaviṣyati</li>
        <li id="R_6.028.033cd">vayaṃ tu mānuṣeṇaiva sapta yotsyāmahe parān</li>
    </ul>
    <ul id="R_6.028.034">
        <li id="R_6.028.034ab">aham eva saha bhrātrā lakṣmaṇena mahaujasā</li>
        <li id="R_6.028.034cd">ātmanā pañcamaś cāyaṃ sakhā mama vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.028.035">
        <li id="R_6.028.035ab">sa rāmaḥ kāryasiddhyartham evam uktvā vibhīṣaṇam</li>
        <li id="R_6.028.035cd">suvelārohaṇe buddhiṃ cakāra matimān matim</li>
    </ul>
    <ul id="R_6.028.036">
        <li id="R_6.028.036ab">
            <seg type="pāda" n="a">tatas tu rāmo mahatā balena</seg>
            <seg type="pāda" n="b">pracchādya sarvāṃ pṛthivīṃ mahātmā</seg>
        </li>
        <li id="R_6.028.036cd">
            <seg type="pāda" n="c">prahṛṣṭarūpo 'bhijagāma laṅkāṃ</seg>
            <seg type="pāda" n="d">kṛtvā matiṃ so 'rivadhe mahātmā</seg>
        </li>
    </ul>
    <ul id="R_6.029.001">
        <li id="R_6.029.001ab">sa tu kṛtvā suvelasya matim ārohaṇaṃ prati</li>
        <li id="R_6.029.001cd">lakṣmaṇānugato rāmaḥ sugrīvam idam abravīt</li>
    </ul>
    <ul id="R_6.029.002">
        <li id="R_6.029.002ab">vibhīṣaṇaṃ ca dharmajñam anuraktaṃ niśācaram</li>
        <li id="R_6.029.002cd">mantrajñaṃ ca vidhijñaṃ ca ślakṣṇayā parayā girā</li>
    </ul>
    <ul id="R_6.029.003">
        <li id="R_6.029.003ab">suvelaṃ sādhu śailendram imaṃ dhātuśataiś citam</li>
        <li id="R_6.029.003cd">adhyārohāmahe sarve vatsyāmo 'tra niśām imām</li>
    </ul>
    <ul id="R_6.029.004">
        <li id="R_6.029.004ab">laṅkāṃ cālokayiṣyāmo nilayaṃ tasya rakṣasaḥ</li>
        <li id="R_6.029.004cd">yena me maraṇāntāya hṛtā bhāryā durātmanā</li>
    </ul>
    <ul id="R_6.029.005">
        <li id="R_6.029.005ab">yena dharmo na vijñāto na vṛttaṃ na kulaṃ tathā</li>
        <li id="R_6.029.005cd">rākṣasyā nīcayā buddhyā yena tad garhitaṃ kṛtam</li>
    </ul>
    <ul id="R_6.029.006">
        <li id="R_6.029.006ab">yasmin me vardhate roṣaḥ kīrtite rākṣasādhame</li>
        <li id="R_6.029.006cd">yasyāparādhān nīcasya vadhaṃ drakṣyāmi rakṣasām</li>
    </ul>
    <ul id="R_6.029.007">
        <li id="R_6.029.007ab">eko hi kurute pāpaṃ kālapāśavaśaṃ gataḥ</li>
        <li id="R_6.029.007cd">nīcenātmāpacāreṇa kulaṃ tena vinaśyati</li>
    </ul>
    <ul id="R_6.029.008">
        <li id="R_6.029.008ab">evaṃ saṃmantrayann eva sakrodho rāvaṇaṃ prati</li>
        <li id="R_6.029.008cd">rāmaḥ suvelaṃ vāsāya citrasānum upāruhat</li>
    </ul>
    <ul id="R_6.029.009">
        <li id="R_6.029.009ab">pṛṣṭhato lakṣmaṇa cainam anvagacchat samāhitaḥ</li>
        <li id="R_6.029.009cd">saśaraṃ cāpam udyamya sumahad vikrame rataḥ</li>
    </ul>
    <ul id="R_6.029.010">
        <li id="R_6.029.010ab">tam anvarohat sugrīvaḥ sāmātyaḥ savibhīṣaṇaḥ</li>
        <li id="R_6.029.010cd">hanūmān aṅgado nīlo maindo dvivida eva ca</li>
    </ul>
    <ul id="R_6.029.011">
        <li id="R_6.029.011ab">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
        <li id="R_6.029.011cd">panasaḥ kumudaś caiva haro rambhaś ca yūthapaḥ</li>
    </ul>
    <ul id="R_6.029.012">
        <li id="R_6.029.012ab">ete cānye ca bahavo vānarāḥ śīghragāminaḥ</li>
        <li id="R_6.029.012cd">te vāyuvegapravaṇās taṃ giriṃ giricāriṇaḥ</li>
        <li id="R_6.029.012ef">adhyārohanta śataśaḥ suvelaṃ yatra rāghavaḥ</li>
    </ul>
    <ul id="R_6.029.013">
        <li id="R_6.029.013ab">te tv adīrgheṇa kālena girim āruhya sarvataḥ</li>
        <li id="R_6.029.013cd">dadṛśuḥ śikhare tasya viṣaktām iva khe purīm</li>
    </ul>
    <ul id="R_6.029.014">
        <li id="R_6.029.014ab">tāṃ śubhāṃ pravaradvārāṃ prākāravaraśobhitām</li>
        <li id="R_6.029.014cd">laṅkāṃ rākṣasasaṃpūrṇāṃ dadṛśur hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.029.015">
        <li id="R_6.029.015ab">prākāracayasaṃsthaiś ca tathā nīlair niśācaraiḥ</li>
        <li id="R_6.029.015cd">dadṛśus te hariśreṣṭhāḥ prākāram aparaṃ kṛtam</li>
    </ul>
    <ul id="R_6.029.016">
        <li id="R_6.029.016ab">te dṛṣṭvā vānarāḥ sarve rākṣasān yuddhakāṅkṣiṇaḥ</li>
        <li id="R_6.029.016cd">mumucur vipulān nādāṃs tatra rāmasya paśyataḥ</li>
    </ul>
    <ul id="R_6.029.017">
        <li id="R_6.029.017ab">tato 'stam agamat sūryaḥ saṃdhyayā pratirañjitaḥ</li>
        <li id="R_6.029.017cd">pūrṇacandrapradīpā ca kṣapā samabhivartate</li>
    </ul>
    <ul id="R_6.029.018">
        <li id="R_6.029.018ab">
            <seg type="pāda" n="a">tataḥ sa rāmo harivāhinīpatir</seg>
            <seg type="pāda" n="b">vibhīṣaṇena pratinandya satkṛtaḥ</seg>
        </li>
        <li id="R_6.029.018cd">
            <seg type="pāda" n="c">salakṣmaṇo yūthapayūthasaṃvṛtaḥ</seg>
            <seg type="pāda" n="d">suvela pṛṣṭhe nyavasad yathāsukham</seg>
        </li>
    </ul>
    <ul id="R_6.030.001">
        <li id="R_6.030.001ab">tāṃ rātrim uṣitās tatra suvele haripuṃgavāḥ</li>
        <li id="R_6.030.001cd">laṅkāyāṃ dadṛśur vīrā vanāny upavanāni ca</li>
    </ul>
    <ul id="R_6.030.002">
        <li id="R_6.030.002ab">samasaumyāni ramyāṇi viśālāny āyatāni ca</li>
        <li id="R_6.030.002cd">dṛṣṭiramyāṇi te dṛṣṭvā babhūvur jātavismayāḥ</li>
    </ul>
    <ul id="R_6.030.003">
        <li id="R_6.030.003ab">campakāśokapuṃnāgasālatālasamākulā</li>
        <li id="R_6.030.003cd">tamālavanasaṃchannā nāgamālāsamāvṛtā</li>
    </ul>
    <ul id="R_6.030.004">
        <li id="R_6.030.004ab">hintālair arjunair nīpaiḥ saptaparṇaiś ca puṣpitaiḥ</li>
        <li id="R_6.030.004cd">tilakaiḥ karṇikāraiś ca paṭālaiś ca samantataḥ</li>
    </ul>
    <ul id="R_6.030.005">
        <li id="R_6.030.005ab">śuśubhe puṣpitāgraiś ca latāparigatair drumaiḥ</li>
        <li id="R_6.030.005cd">laṅkā bahuvidhair divyair yathendrasyāmarāvatī</li>
    </ul>
    <ul id="R_6.030.006">
        <li id="R_6.030.006ab">vicitrakusumopetai raktakomalapallavaiḥ</li>
        <li id="R_6.030.006cd">śādvalaiś ca tathā nīlaiś citrābhir vanarājibhiḥ</li>
    </ul>
    <ul id="R_6.030.007">
        <li id="R_6.030.007ab">gandhāḍhyāny abhiramyāṇi puṣpāṇi ca phalāni ca</li>
        <li id="R_6.030.007cd">dhārayanty agamās tatra bhūṣaṇānīva mānavāḥ</li>
    </ul>
    <ul id="R_6.030.008">
        <li id="R_6.030.008ab">tac caitrarathasaṃkāśaṃ manojñaṃ nandanopamam</li>
        <li id="R_6.030.008cd">vanaṃ sarvartukaṃ ramyaṃ śuśubhe ṣaṭpadāyutam</li>
    </ul>
    <ul id="R_6.030.009">
        <li id="R_6.030.009ab">natyūhakoyaṣṭibhakair nṛtyamānaiś ca barhibhiḥ</li>
        <li id="R_6.030.009cd">rutaṃ parabhṛtānāṃ ca śuśruve vananirjhare</li>
    </ul>
    <ul id="R_6.030.010">
        <li id="R_6.030.010ab">nityamattavihaṃgāni bhramarācaritāni ca</li>
        <li id="R_6.030.010cd">kokilākulaṣaṇḍāni vihagābhirutāni ca</li>
    </ul>
    <ul id="R_6.030.011">
        <li id="R_6.030.011ab">bhṛṅgarājābhigītāni bhramaraiḥ sevitāni ca</li>
        <li id="R_6.030.011cd">koṇālakavighuṣṭāni sārasābhirutāni ca</li>
    </ul>
    <ul id="R_6.030.012">
        <li id="R_6.030.012ab">viviśus te tatas tāni vanāny upavanāni ca</li>
        <li id="R_6.030.012cd">hṛṣṭāḥ pramuditā vīrā harayaḥ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_6.030.013">
        <li id="R_6.030.013ab">teṣāṃ praviśatāṃ tatra vānarāṇāṃ mahaujasām</li>
        <li id="R_6.030.013cd">puṣpasaṃsargasurabhir vavau ghrāṇasukho 'nilaḥ</li>
    </ul>
    <ul id="R_6.030.014">
        <li id="R_6.030.014ab">anye tu harivīrāṇāṃ yūthān niṣkramya yūthapāḥ</li>
        <li id="R_6.030.014cd">sugrīveṇābhyanujñātā laṅkāṃ jagmuḥ patākinīm</li>
    </ul>
    <ul id="R_6.030.015">
        <li id="R_6.030.015ab">vitrāsayanto vihagāṃs trāsayanto mṛgadvipān</li>
        <li id="R_6.030.015cd">kampayantaś ca tāṃ laṅkāṃ nādaiḥ svair nadatāṃ varāḥ</li>
    </ul>
    <ul id="R_6.030.016">
        <li id="R_6.030.016ab">kurvantas te mahāvegā mahīṃ cāraṇapīḍitām</li>
        <li id="R_6.030.016cd">rajaś ca sahasaivordhvaṃ jagāma caraṇoddhatam</li>
    </ul>
    <ul id="R_6.030.017">
        <li id="R_6.030.017ab">ṛkṣāḥ siṃhā varāhāś ca mahiṣā vāraṇā mṛgāḥ</li>
        <li id="R_6.030.017cd">tena śabdena vitrastā jagmur bhītā diśo daśa</li>
    </ul>
    <ul id="R_6.030.018">
        <li id="R_6.030.018ab">śikharaṃ tu trikūṭasya prāṃśu caikaṃ divispṛśam</li>
        <li id="R_6.030.018cd">samantāt puṣpasaṃchannaṃ mahārajatasaṃnibham</li>
    </ul>
    <ul id="R_6.030.019">
        <li id="R_6.030.019ab">śatayojanavistīrṇaṃ vimalaṃ cārudarśanam</li>
        <li id="R_6.030.019cd">ślakṣṇaṃ śrīman mahac caiva duṣprāpaṃ śakunair api</li>
    </ul>
    <ul id="R_6.030.020">
        <li id="R_6.030.020ab">manasāpi durārohaṃ kiṃ punaḥ karmaṇā janaiḥ</li>
        <li id="R_6.030.020cd">niviṣṭā tatra śikhare laṅkā rāvaṇapālitā</li>
    </ul>
    <ul id="R_6.030.021">
        <li id="R_6.030.021ab">sā purī gopurair uccaiḥ pāṇḍurāmbudasaṃnibhaiḥ</li>
        <li id="R_6.030.021cd">kāñcanena ca sālena rājatena ca śobhitā</li>
    </ul>
    <ul id="R_6.030.022">
        <li id="R_6.030.022ab">prāsādaiś ca vimānaiś ca laṅkā paramabhūṣitā</li>
        <li id="R_6.030.022cd">ghanair ivātapāpāye madhyamaṃ vaiṣṇavaṃ padam</li>
    </ul>
    <ul id="R_6.030.023">
        <li id="R_6.030.023ab">yasyāṃ stambhasahasreṇa prāsādaḥ samalaṃkṛtaḥ</li>
        <li id="R_6.030.023cd">kailāsaśikharākāro dṛśyate kham ivollikhan</li>
    </ul>
    <ul id="R_6.030.024">
        <li id="R_6.030.024ab">caityaḥ sa rākṣasendrasya babhūva purabhūṣaṇam</li>
        <li id="R_6.030.024cd">śatena rakṣasāṃ nityaṃ yaḥ samagreṇa rakṣyate</li>
    </ul>
    <ul id="R_6.030.025">
        <li id="R_6.030.025ab">tāṃ samṛddhāṃ samṛddhārtho lakṣmīvāṃl lakṣmaṇāgrajaḥ</li>
        <li id="R_6.030.025cd">rāvaṇasya purīṃ rāmo dadarśa saha vānaraiḥ</li>
    </ul>
    <ul id="R_6.030.026">
        <li id="R_6.030.026ab">
            <seg type="pāda" n="a">tāṃ ratnapūrṇāṃ bahusaṃvidhānāṃ</seg>
            <seg type="pāda" n="b">prāsādamālābhir alaṃkṛtāṃ ca</seg>
        </li>
        <li id="R_6.030.026cd">
            <seg type="pāda" n="c">purīṃ mahāyantrakavāṭamukhyāṃ</seg>
            <seg type="pāda" n="d">dadarśa rāmo mahatā balena</seg>
        </li>
    </ul>
    <ul id="R_6.031.001">
        <li id="R_6.031.001ab">atha tasmin nimittāni dṛṣṭvā lakṣmaṇapūrvajaḥ</li>
        <li id="R_6.031.001cd">lakṣmaṇaṃ lakṣmisaṃpannam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.031.002">
        <li id="R_6.031.002ab">parigṛhyodakaṃ śītaṃ vanāni phalavanti ca</li>
        <li id="R_6.031.002cd">balaughaṃ saṃvibhajyemaṃ vyūhya tiṣṭhema lakṣmaṇa</li>
    </ul>
    <ul id="R_6.031.003">
        <li id="R_6.031.003ab">lokakṣayakaraṃ bhīmaṃ bhayaṃ paśyāmy upasthitam</li>
        <li id="R_6.031.003cd">nibarhaṇaṃ pravīrāṇām ṛkṣavānararakṣasām</li>
    </ul>
    <ul id="R_6.031.004">
        <li id="R_6.031.004ab">vātāś ca paruṣaṃ vānti kampate ca vasuṃdharā</li>
        <li id="R_6.031.004cd">parvatāgrāṇi vepante patanti dharaṇīdharāḥ</li>
    </ul>
    <ul id="R_6.031.005">
        <li id="R_6.031.005ab">meghāḥ kravyādasaṃkāśāḥ paruṣāḥ paruṣasvanāḥ</li>
        <li id="R_6.031.005cd">krūrāḥ krūraṃ pravarṣanti miśraṃ śoṇitabindubhiḥ</li>
    </ul>
    <ul id="R_6.031.006">
        <li id="R_6.031.006ab">raktacandanasaṃkāśā saṃdhyāparamadāruṇā</li>
        <li id="R_6.031.006cd">jvalac ca nipataty etad ādityād agnimaṇḍalam</li>
    </ul>
    <ul id="R_6.031.007">
        <li id="R_6.031.007ab">ādityam abhivāśyante janayanto mahad bhayam</li>
        <li id="R_6.031.007cd">dīnā dīnasvarā ghorā apraśastā mṛgadvijāḥ</li>
    </ul>
    <ul id="R_6.031.008">
        <li id="R_6.031.008ab">rajanyām aprakāśaś ca saṃtāpayati candramāḥ</li>
        <li id="R_6.031.008cd">kṛṣṇaraktāṃśuparyanto yathā lokasya saṃkṣaye</li>
    </ul>
    <ul id="R_6.031.009">
        <li id="R_6.031.009ab">hrasvo rūkṣo 'praśastaś ca pariveṣaḥ sulohitaḥ</li>
        <li id="R_6.031.009cd">ādityamaṇḍale nīlaṃ lakṣma lakṣmaṇa dṛśyate</li>
    </ul>
    <ul id="R_6.031.010">
        <li id="R_6.031.010ab">dṛśyante na yathāvac ca nakṣatrāṇy abhivartate</li>
        <li id="R_6.031.010cd">yugāntam iva lokasya paśya lakṣmaṇa śaṃsati</li>
    </ul>
    <ul id="R_6.031.011">
        <li id="R_6.031.011ab">kākāḥ śyenās tathā gṛdhrā nīcaiḥ paripatanti ca</li>
        <li id="R_6.031.011cd">śivāś cāpy aśivā vācaḥ pravadanti mahāsvanāḥ</li>
    </ul>
    <ul id="R_6.031.012">
        <li id="R_6.031.012ab">kṣipram adya durādharṣāṃ purīṃ rāvaṇapālitām</li>
        <li id="R_6.031.012cd">abhiyāma javenaiva sarvato haribhir vṛtāḥ</li>
    </ul>
    <ul id="R_6.031.013">
        <li id="R_6.031.013ab">ity evaṃ tu vadan vīro lakṣmaṇaṃ lakṣmaṇāgrajaḥ</li>
        <li id="R_6.031.013cd">tasmād avātarac chīghraṃ parvatāgrān mahābalaḥ</li>
    </ul>
    <ul id="R_6.031.014">
        <li id="R_6.031.014ab">avatīrya tu dharmātmā tasmāc chailāt sa rāghavaḥ</li>
        <li id="R_6.031.014cd">paraiḥ paramadurdharṣaṃ dadarśa balam ātmanaḥ</li>
    </ul>
    <ul id="R_6.031.015">
        <li id="R_6.031.015ab">saṃnahya tu sasugrīvaḥ kapirājabalaṃ mahat</li>
        <li id="R_6.031.015cd">kālajño rāghavaḥ kāle saṃyugāyābhyacodayat</li>
    </ul>
    <ul id="R_6.031.016">
        <li id="R_6.031.016ab">tataḥ kāle mahābāhur balena mahatā vṛtaḥ</li>
        <li id="R_6.031.016cd">prasthitaḥ purato dhanvī laṅkām abhimukhaḥ purīm</li>
    </ul>
    <ul id="R_6.031.017">
        <li id="R_6.031.017ab">taṃ vibhīṣaṇa sugrīvau hanūmāñ jāmbavān nalaḥ</li>
        <li id="R_6.031.017cd">ṛkṣarājas tathā nīlo lakṣmaṇaś cānyayus tadā</li>
    </ul>
    <ul id="R_6.031.018">
        <li id="R_6.031.018ab">tataḥ paścāt sumahatī pṛtanarkṣavanaukasām</li>
        <li id="R_6.031.018cd">pracchādya mahatīṃ bhūmim anuyāti sma rāghavam</li>
    </ul>
    <ul id="R_6.031.019">
        <li id="R_6.031.019ab">śailaśṛṅgāṇi śataśaḥ pravṛddhāṃś ca mahīruhām</li>
        <li id="R_6.031.019cd">jagṛhuḥ kuñjaraprakhyā vānarāḥ paravāraṇāḥ</li>
    </ul>
    <ul id="R_6.031.020">
        <li id="R_6.031.020ab">tau tv adīrgheṇa kālena bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.031.020cd">rāvaṇasya purīṃ laṅkām āsedatur ariṃdamau</li>
    </ul>
    <ul id="R_6.031.021">
        <li id="R_6.031.021ab">patākāmālinīṃ ramyām udyānavanaśobhitām</li>
        <li id="R_6.031.021cd">citravaprāṃ suduṣprāpām uccaprākāratoraṇām</li>
    </ul>
    <ul id="R_6.031.022">
        <li id="R_6.031.022ab">tāṃ surair api durdharṣāṃ rāmavākyapracoditāḥ</li>
        <li id="R_6.031.022cd">yathānideśaṃ saṃpīḍya nyaviśanta vanaukasaḥ</li>
    </ul>
    <ul id="R_6.031.023">
        <li id="R_6.031.023ab">laṅkāyās tūttaradvāraṃ śailaśṛṅgam ivonnatam</li>
        <li id="R_6.031.023cd">rāmaḥ sahānujo dhanvī jugopa ca rurodha ca</li>
    </ul>
    <ul id="R_6.031.024">
        <li id="R_6.031.024ab">laṅkām upaniviṣṭaś ca rāmo daśarathātmajaḥ</li>
        <li id="R_6.031.024cd">lakṣmaṇānucaro vīraḥ purīṃ rāvaṇapālitām</li>
    </ul>
    <ul id="R_6.031.025">
        <li id="R_6.031.025ab">uttaradvāram āsādya yatra tiṣṭhati rāvaṇaḥ</li>
        <li id="R_6.031.025cd">nānyo rāmād dhi tad dvāraṃ samarthaḥ parirakṣitum</li>
    </ul>
    <ul id="R_6.031.026">
        <li id="R_6.031.026ab">rāvaṇādhiṣṭhitaṃ bhīmaṃ varuṇeneva sāgaram</li>
        <li id="R_6.031.026cd">sāyudhau rākṣasair bhīmair abhiguptaṃ samantataḥ</li>
        <li id="R_6.031.026ef">laghūnāṃ trāsajananaṃ pātālam iva dānavaiḥ</li>
    </ul>
    <ul id="R_6.031.027">
        <li id="R_6.031.027ab">vinyastāni ca yodhānāṃ bahūni vividhāni ca</li>
        <li id="R_6.031.027cd">dadarśāyudhajālāni tathaiva kavacāni ca</li>
    </ul>
    <ul id="R_6.031.028">
        <li id="R_6.031.028ab">pūrvaṃ tu dvāram āsādya nīlo haricamūpatiḥ</li>
        <li id="R_6.031.028cd">atiṣṭhat saha maindena dvividena ca vīryavān</li>
    </ul>
    <ul id="R_6.031.029">
        <li id="R_6.031.029ab">aṅgado dakṣiṇadvāraṃ jagrāha sumahābalaḥ</li>
        <li id="R_6.031.029cd">ṛṣabheṇa gavākṣeṇa gajena gavayena ca</li>
    </ul>
    <ul id="R_6.031.030">
        <li id="R_6.031.030ab">hanūmān paścimadvāraṃ rarakṣa balavān kapiḥ</li>
        <li id="R_6.031.030cd">pramāthi praghasābhyāṃ ca vīrair anyaiś ca saṃgataḥ</li>
    </ul>
    <ul id="R_6.031.031">
        <li id="R_6.031.031ab">madhyame ca svayaṃ gulme sugrīvaḥ samatiṣṭhata</li>
        <li id="R_6.031.031cd">saha sarvair hariśreṣṭhaiḥ suparṇaśvasanopamaiḥ</li>
    </ul>
    <ul id="R_6.031.032">
        <li id="R_6.031.032ab">vānarāṇāṃ tu ṣaṭtriṃśat koṭyaḥ prakhyātayūthapāḥ</li>
        <li id="R_6.031.032cd">nipīḍyopaniviṣṭāś ca sugrīvo yatra vānaraḥ</li>
    </ul>
    <ul id="R_6.031.033">
        <li id="R_6.031.033ab">śāsanena tu rāmasya lakṣmaṇaḥ savibhīṣaṇaḥ</li>
        <li id="R_6.031.033cd">dvāre dvāre harīṇāṃ tu koṭiṃ koṭiṃ nyaveśayat</li>
    </ul>
    <ul id="R_6.031.034">
        <li id="R_6.031.034ab">paścimena tu rāmasya sugrīvaḥ saha jāmbavān</li>
        <li id="R_6.031.034cd">adūrān madhyame gulme tasthau bahubalānugaḥ</li>
    </ul>
    <ul id="R_6.031.035">
        <li id="R_6.031.035ab">te tu vānaraśārdūlāḥ śārdūlā iva daṃṣṭriṇaḥ</li>
        <li id="R_6.031.035cd">gṛhītvā drumaśailāgrān hṛṣṭā yuddhāya tasthire</li>
    </ul>
    <ul id="R_6.031.036">
        <li id="R_6.031.036ab">sarve vikṛtalāṅgūlāḥ sarve daṃṣṭrānakhāyudhāḥ</li>
        <li id="R_6.031.036cd">sarve vikṛtacitrāṅgāḥ sarve ca vikṛtānanāḥ</li>
    </ul>
    <ul id="R_6.031.037">
        <li id="R_6.031.037ab">daśanāgabalāḥ ke cit ke cid daśaguṇottarāḥ</li>
        <li id="R_6.031.037cd">ke cin nāgasahasrasya babhūvus tulyavikramāḥ</li>
    </ul>
    <ul id="R_6.031.038">
        <li id="R_6.031.038ab">santi caughā balāḥ ke cit ke cic chataguṇottarāḥ</li>
        <li id="R_6.031.038cd">aprameyabalāś cānye tatrāsan hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.031.039">
        <li id="R_6.031.039ab">adbhutaś ca vicitraś ca teṣām āsīt samāgamaḥ</li>
        <li id="R_6.031.039cd">tatra vānarasainyānāṃ śalabhānām ivodgamaḥ</li>
    </ul>
    <ul id="R_6.031.040">
        <li id="R_6.031.040ab">paripūrṇam ivākāśaṃ saṃchanneva ca medinī</li>
        <li id="R_6.031.040cd">laṅkām upaniviṣṭaiś ca saṃpatadbhiś ca vānaraiḥ</li>
    </ul>
    <ul id="R_6.031.041">
        <li id="R_6.031.041ab">śataṃ śatasahasrāṇāṃ pṛthag ṛkṣavanaukasām</li>
        <li id="R_6.031.041cd">laṅkā dvārāṇy upājagmur anye yoddhuṃ samantataḥ</li>
    </ul>
    <ul id="R_6.031.042">
        <li id="R_6.031.042ab">āvṛtaḥ sa giriḥ sarvais taiḥ samantāt plavaṃgamaiḥ</li>
        <li id="R_6.031.042cd">ayutānāṃ sahasraṃ ca purīṃ tām abhyavartata</li>
    </ul>
    <ul id="R_6.031.043">
        <li id="R_6.031.043ab">vānarair balavadbhiś ca babhūva drumapāṇibhiḥ</li>
        <li id="R_6.031.043cd">sarvataḥ saṃvṛtā laṅkā duṣpraveśāpi vāyunā</li>
    </ul>
    <ul id="R_6.031.044">
        <li id="R_6.031.044ab">rākṣasā vismayaṃ jagmuḥ sahasābhinipīḍitāḥ</li>
        <li id="R_6.031.044cd">vānarair meghasaṃkāśaiḥ śakratulyaparākramaiḥ</li>
    </ul>
    <ul id="R_6.031.045">
        <li id="R_6.031.045ab">mahāñ śabdo 'bhavat tatra balaughasyābhivartataḥ</li>
        <li id="R_6.031.045cd">sāgarasyeva bhinnasya yathā syāt salilasvanaḥ</li>
    </ul>
    <ul id="R_6.031.046">
        <li id="R_6.031.046ab">tena śabdena mahatā saprākārā satoraṇā</li>
        <li id="R_6.031.046cd">laṅkā pracalitā sarvā saśailavanakānanā</li>
    </ul>
    <ul id="R_6.031.047">
        <li id="R_6.031.047ab">rāmalakṣmaṇaguptā sā sugrīveṇa ca vāhinī</li>
        <li id="R_6.031.047cd">babhūva durdharṣatarā sarvair api surāsuraiḥ</li>
    </ul>
    <ul id="R_6.031.048">
        <li id="R_6.031.048ab">rāghavaḥ saṃniveśyaivaṃ sainyaṃ svaṃ rakṣasāṃ vadhe</li>
        <li id="R_6.031.048cd">saṃmantrya mantribhiḥ sārdhaṃ niścitya ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.031.049">
        <li id="R_6.031.049ab">ānantaryam abhiprepsuḥ kramayogārthatattvavit</li>
        <li id="R_6.031.049cd">vibhīṣaṇasyānumate rājadharmam anusmaran</li>
        <li id="R_6.031.049ef">aṅgadaṃ vālitanayaṃ samāhūyedam abravīt</li>
    </ul>
    <ul id="R_6.031.050">
        <li id="R_6.031.050ab">gatvā saumya daśagrīvaṃ brūhi madvacanāt kape</li>
        <li id="R_6.031.050cd">laṅghayitvā purīṃ laṅkāṃ bhayaṃ tyaktvā gatavyathaḥ</li>
    </ul>
    <ul id="R_6.031.051">
        <li id="R_6.031.051ab">bhraṣṭaśrīkagataiśvaryamumūrṣo naṣṭacetanaḥ</li>
        <li id="R_6.031.051cd">ṛṣīṇāṃ devatānāṃ ca gandharvāpsarasāṃ tathā</li>
    </ul>
    <ul id="R_6.031.052">
        <li id="R_6.031.052ab">nāgānām atha yakṣāṇāṃ rājñāṃ ca rajanīcara</li>
        <li id="R_6.031.052cd">yac ca pāpaṃ kṛtaṃ mohād avaliptena rākṣasa</li>
    </ul>
    <ul id="R_6.031.053">
        <li id="R_6.031.053ab">nūnam adya gato darpaḥ svayambhū varadānajaḥ</li>
        <li id="R_6.031.053cd">yasya daṇḍadharas te 'haṃ dārāharaṇakarśitaḥ</li>
        <li id="R_6.031.053ef">daṇḍaṃ dhārayamāṇas tu laṅkādvare vyavasthitaḥ</li>
    </ul>
    <ul id="R_6.031.054">
        <li id="R_6.031.054ab">padavīṃ devatānāṃ ca maharṣīṇāṃ ca rākṣasa</li>
        <li id="R_6.031.054cd">rājarṣīṇāṃ ca sarveṣāṃ gamiṣyasi mayā hataḥ</li>
    </ul>
    <ul id="R_6.031.055">
        <li id="R_6.031.055ab">balena yena vai sītāṃ māyayā rākṣasādhama</li>
        <li id="R_6.031.055cd">mām atikrāmayitvā tvaṃ hṛtavāṃs tad vidarśaya</li>
    </ul>
    <ul id="R_6.031.056">
        <li id="R_6.031.056ab">arākṣasam imaṃ lokaṃ kartāsmi niśitaiḥ śaraiḥ</li>
        <li id="R_6.031.056cd">na cec charaṇam abhyeṣi mām upādāya maithilīm</li>
    </ul>
    <ul id="R_6.031.057">
        <li id="R_6.031.057ab">dharmātmā rakṣasāṃ śreṣṭhaḥ saṃprāpto 'yaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.031.057cd">laṅkaiśvaryaṃ dhruvaṃ śrīmān ayaṃ prāpnoty akaṇṭakam</li>
    </ul>
    <ul id="R_6.031.058">
        <li id="R_6.031.058ab">na hi rājyam adharmeṇa bhoktuṃ kṣaṇam api tvayā</li>
        <li id="R_6.031.058cd">śakyaṃ mūrkhasahāyena pāpenāvijitātmanā</li>
    </ul>
    <ul id="R_6.031.059">
        <li id="R_6.031.059ab">yudhyasva vā dhṛtiṃ kṛtvā śauryam ālambya rākṣasa</li>
        <li id="R_6.031.059cd">maccharais tvaṃ raṇe śāntas tataḥ pūto bhaviṣyasi</li>
    </ul>
    <ul id="R_6.031.060">
        <li id="R_6.031.060ab">yady āviśasi lokāṃs trīn pakṣibhūto manojavaḥ</li>
        <li id="R_6.031.060cd">mama cakṣuṣpathaṃ prāpya na jīvan pratiyāsyasi</li>
    </ul>
    <ul id="R_6.031.061">
        <li id="R_6.031.061ab">bravīmi tvāṃ hitaṃ vākyaṃ kriyatām aurdhvadekikam</li>
        <li id="R_6.031.061cd">sudṛṣṭā kriyatāṃ laṅkā jīvitaṃ te mayi sthitam</li>
    </ul>
    <ul id="R_6.031.062">
        <li id="R_6.031.062ab">ity uktaḥ sa tu tāreyo rāmeṇākliṣṭakarmaṇā</li>
        <li id="R_6.031.062cd">jagāmākāśam āviśya mūrtimān iva havyavāṭ</li>
    </ul>
    <ul id="R_6.031.063">
        <li id="R_6.031.063ab">so 'tipatya muhūrtena śrīmān rāvaṇamandiram</li>
        <li id="R_6.031.063cd">dadarśāsīnam avyagraṃ rāvaṇaṃ sacivaiḥ saha</li>
    </ul>
    <ul id="R_6.031.064">
        <li id="R_6.031.064ab">tatas tasyāvidūreṇa nipatya haripuṃgavaḥ</li>
        <li id="R_6.031.064cd">dīptāgnisadṛśas tasthāv aṅgadaḥ kanakāṅgadaḥ</li>
    </ul>
    <ul id="R_6.031.065">
        <li id="R_6.031.065ab">tad rāmavacanaṃ sarvam anyūnādhikam uttamam</li>
        <li id="R_6.031.065cd">sāmātyaṃ śrāvayām āsa nivedyātmānam ātmanā</li>
    </ul>
    <ul id="R_6.031.066">
        <li id="R_6.031.066ab">dūto 'haṃ kosalendrasya rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_6.031.066cd">vāliputro 'ṅgado nāma yadi te śrotram āgataḥ</li>
    </ul>
    <ul id="R_6.031.067">
        <li id="R_6.031.067ab">āha tvāṃ rāghavo rāmaḥ kausalyānandavardhanaḥ</li>
        <li id="R_6.031.067cd">niṣpatya pratiyudhyasva nṛśaṃsaṃ puruṣādhama</li>
    </ul>
    <ul id="R_6.031.068">
        <li id="R_6.031.068ab">hantāsmi tvāṃ sahāmātyaṃ saputrajñātibāndhavam</li>
        <li id="R_6.031.068cd">nirudvignās trayo lokā bhaviṣyanti hate tvayi</li>
    </ul>
    <ul id="R_6.031.069">
        <li id="R_6.031.069ab">devadānavayakṣāṇāṃ gandharvoragarakṣasām</li>
        <li id="R_6.031.069cd">śatrum adyoddhariṣyāmi tvām ṛṣīṇāṃ ca kaṇṭakam</li>
    </ul>
    <ul id="R_6.031.070">
        <li id="R_6.031.070ab">vibhīṣaṇasya caiśvaryaṃ bhaviṣyati hate tvayi</li>
        <li id="R_6.031.070cd">na cet satkṛtya vaidehīṃ praṇipatya pradāsyasi</li>
    </ul>
    <ul id="R_6.031.071">
        <li id="R_6.031.071ab">ity evaṃ paruṣaṃ vākyaṃ bruvāṇe haripuṃgave</li>
        <li id="R_6.031.071cd">amarṣavaśam āpanno niśācaragaṇeśvaraḥ</li>
    </ul>
    <ul id="R_6.031.072">
        <li id="R_6.031.072ab">tataḥ sa roṣatāmrākṣaḥ śaśāsa sacivāṃs tadā</li>
        <li id="R_6.031.072cd">gṛhyatām eṣa durmedhā vadhyatām iti cāsakṛt</li>
    </ul>
    <ul id="R_6.031.073">
        <li id="R_6.031.073ab">rāvaṇasya vacaḥ śrutvā dīptāgnisamatejasaḥ</li>
        <li id="R_6.031.073cd">jagṛhus taṃ tato ghorāś catvāro rajanīcarāḥ</li>
    </ul>
    <ul id="R_6.031.074">
        <li id="R_6.031.074ab">grāhayām āsa tāreyaḥ svayam ātmānam ātmanā</li>
        <li id="R_6.031.074cd">balaṃ darśayituṃ vīro yātudhānagaṇe tadā</li>
    </ul>
    <ul id="R_6.031.075">
        <li id="R_6.031.075ab">sa tān bāhudvaye saktān ādāya patagān iva</li>
        <li id="R_6.031.075cd">prāsādaṃ śailasaṃkāśam utpāpātāṅgadas tadā</li>
    </ul>
    <ul id="R_6.031.076">
        <li id="R_6.031.076ab">te 'ntarikṣād vinirdhūtās tasya vegena rākṣasāḥ</li>
        <li id="R_6.031.076cd">bhumau nipatitāḥ sarve rākṣasendrasya paśyataḥ</li>
    </ul>
    <ul id="R_6.031.077">
        <li id="R_6.031.077ab">tataḥ prāsādaśikharaṃ śailaśṛṅgam ivonnatam</li>
        <li id="R_6.031.077cd">tat paphāla tadākrāntaṃ daśagrīvasya paśyataḥ</li>
    </ul>
    <ul id="R_6.031.078">
        <li id="R_6.031.078ab">bhaṅktvā prāsādaśikharaṃ nāma viśrāvya cātmanaḥ</li>
        <li id="R_6.031.078cd">vinadya sumahānādam utpapāta vihāyasā</li>
    </ul>
    <ul id="R_6.031.079">
        <li id="R_6.031.079ab">rāvaṇas tu paraṃ cakre krodhaṃ prāsādadharṣaṇāt</li>
        <li id="R_6.031.079cd">vināśaṃ cātmanaḥ paśyan niḥśvāsaparamo 'bhavat</li>
    </ul>
    <ul id="R_6.031.080">
        <li id="R_6.031.080ab">rāmas tu bahubhir hṛṣṭair ninadadbhiḥ plavaṃgamaiḥ</li>
        <li id="R_6.031.080cd">vṛto ripuvadhākāṅkṣī yuddhāyaivābhyavartata</li>
    </ul>
    <ul id="R_6.031.081">
        <li id="R_6.031.081ab">suṣeṇas tu mahāvīryo girikūṭopamo hariḥ</li>
        <li id="R_6.031.081cd">bahubhiḥ saṃvṛtas tatra vānaraiḥ kāmarūpibhiḥ</li>
    </ul>
    <ul id="R_6.031.082">
        <li id="R_6.031.082ab">caturdvārāṇi sarvāṇi sugrīvavacanāt kapiḥ</li>
        <li id="R_6.031.082cd">paryākramata durdharṣo nakṣatrāṇīva candramāḥ</li>
    </ul>
    <ul id="R_6.031.083">
        <li id="R_6.031.083ab">teṣām akṣauhiṇiśataṃ samavekṣya vanaukasām</li>
        <li id="R_6.031.083cd">laṅkām upaniviṣṭānāṃ sāgaraṃ cātivartatām</li>
    </ul>
    <ul id="R_6.031.084">
        <li id="R_6.031.084ab">rākṣasā vismayaṃ jagmus trāsaṃ jagmus tathāpare</li>
        <li id="R_6.031.084cd">apare samaroddharṣād dharṣam evopapedire</li>
    </ul>
    <ul id="R_6.031.085">
        <li id="R_6.031.085ab">kṛtsnaṃ hi kapibhir vyāptaṃ prākāraparikhāntaram</li>
        <li id="R_6.031.085cd">dadṛśū rākṣasā dīnāḥ prākāraṃ vānarīkṛtam</li>
    </ul>
    <ul id="R_6.031.086">
        <li id="R_6.031.086ab">
            <seg type="pāda" n="a">tasmin mahābhīṣaṇake pravṛtte</seg>
            <seg type="pāda" n="b">kolāhale rākṣasarājadhānyām</seg>
        </li>
        <li id="R_6.031.086cd">
            <seg type="pāda" n="c">pragṛhya rakṣāṃsi mahāyudhāni</seg>
            <seg type="pāda" n="d">yugāntavātā iva saṃviceruḥ</seg>
        </li>
    </ul>
    <ul id="R_6.032.001">
        <li id="R_6.032.001ab">tatas te rākṣasās tatra gatvā rāvaṇamandiram</li>
        <li id="R_6.032.001cd">nyavedayan purīṃ ruddhāṃ rāmeṇa saha vānaraiḥ</li>
    </ul>
    <ul id="R_6.032.002">
        <li id="R_6.032.002ab">ruddhāṃ tu nagarīṃ śrutvā jātakrodho niśācaraḥ</li>
        <li id="R_6.032.002cd">vidhānaṃ dviguṇaṃ śrutvā prāsādaṃ so 'dhyarohata</li>
    </ul>
    <ul id="R_6.032.003">
        <li id="R_6.032.003ab">sa dadarśāvṛtāṃ laṅkāṃ saśailavanakānanām</li>
        <li id="R_6.032.003cd">asaṃkhyeyair harigaṇaiḥ sarvato yuddhakāṅkṣibhiḥ</li>
    </ul>
    <ul id="R_6.032.004">
        <li id="R_6.032.004ab">sa dṛṣṭvā vānaraiḥ sarvāṃ vasudhāṃ kavalīkṛtām</li>
        <li id="R_6.032.004cd">kathaṃ kṣapayitavyāḥ syur iti cintāparo 'bhavat</li>
    </ul>
    <ul id="R_6.032.005">
        <li id="R_6.032.005ab">sa cintayitvā suciraṃ dhairyam ālambya rāvaṇaḥ</li>
        <li id="R_6.032.005cd">rāghavaṃ hariyūthāṃś ca dadarśāyatalocanaḥ</li>
    </ul>
    <ul id="R_6.032.006">
        <li id="R_6.032.006ab">prekṣato rākṣasendrasya tāny anīkāni bhāgaśaḥ</li>
        <li id="R_6.032.006cd">rāghavapriyakāmārthaṃ laṅkām āruruhus tadā</li>
    </ul>
    <ul id="R_6.032.007">
        <li id="R_6.032.007ab">te tāmravaktrā hemābhā rāmārthe tyaktajīvitāḥ</li>
        <li id="R_6.032.007cd">laṅkām evāhyavartanta sālatālaśilāyudhāḥ</li>
    </ul>
    <ul id="R_6.032.008">
        <li id="R_6.032.008ab">te drumaiḥ parvatāgraiś ca muṣṭibhiś ca plavaṃgamāḥ</li>
        <li id="R_6.032.008cd">prāsādāgrāṇi coccāni mamantus toraṇāni ca</li>
    </ul>
    <ul id="R_6.032.009">
        <li id="R_6.032.009ab">pārikhāḥ pūrayanti sma prasannasalilāyutāḥ</li>
        <li id="R_6.032.009cd">pāṃsubhiḥ parvatāgraiś ca tṛṇaiḥ kāṣṭhaiś ca vānarāḥ</li>
    </ul>
    <ul id="R_6.032.010">
        <li id="R_6.032.010ab">tataḥ sahasrayūthāś ca koṭiyūthāś ca yūthapāḥ</li>
        <li id="R_6.032.010cd">koṭīśatayutāś cānye laṅkām āruruhus tadā</li>
    </ul>
    <ul id="R_6.032.011">
        <li id="R_6.032.011ab">kāñcanāni pramṛdnantas toraṇāni plavaṃgamāḥ</li>
        <li id="R_6.032.011cd">kailāsaśikharābhāni gopurāṇi pramathya ca</li>
    </ul>
    <ul id="R_6.032.012">
        <li id="R_6.032.012ab">āplavantaḥ plavantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_6.032.012cd">laṅkāṃ tām abhyavartanta mahāvāraṇasaṃnibhāḥ</li>
    </ul>
    <ul id="R_6.032.013">
        <li id="R_6.032.013ab">jayaty atibalo rāmo lakṣmaṇaś ca mahābalaḥ</li>
        <li id="R_6.032.013cd">rājā jayati sugrīvo rāghaveṇābhipālitaḥ</li>
    </ul>
    <ul id="R_6.032.014">
        <li id="R_6.032.014ab">ity evaṃ ghoṣayantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_6.032.014cd">abhyadhāvanta laṅkāyāḥ prākāraṃ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_6.032.015">
        <li id="R_6.032.015ab">vīrabāhuḥ subāhuś ca nalaś ca vanagocaraḥ</li>
        <li id="R_6.032.015cd">nipīḍyopaniviṣṭās te prākāraṃ hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.032.016">
        <li id="R_6.032.016ab">etasminn antare cakruḥ skandhāvāraniveśanam</li>
    </ul>
    <ul id="R_6.032.017">
        <li id="R_6.032.017ab">pūrvadvāraṃ tu kumudaḥ koṭibhir daśabhir vṛtaḥ</li>
        <li id="R_6.032.017cd">āvṛtya balavāṃs tasthau haribhir jitakāśibhiḥ</li>
    </ul>
    <ul id="R_6.032.018">
        <li id="R_6.032.018ab">dakṣiṇadvāram āgamya vīraḥ śatabaliḥ kapiḥ</li>
        <li id="R_6.032.018cd">āvṛtya balavāṃs tasthau viṃśatyā koṭibhir vṛtaḥ</li>
    </ul>
    <ul id="R_6.032.019">
        <li id="R_6.032.019ab">suṣeṇaḥ paścimadvāraṃ gatas tārā pitā hariḥ</li>
        <li id="R_6.032.019cd">āvṛtya balavāṃs tasthau ṣaṣṭi koṭibhir āvṛtaḥ</li>
    </ul>
    <ul id="R_6.032.020">
        <li id="R_6.032.020ab">uttaradvāram āsādya rāmaḥ saumitriṇā saha</li>
        <li id="R_6.032.020cd">āvṛtya balavāṃs tasthau sugrīvaś ca harīśvaraḥ</li>
    </ul>
    <ul id="R_6.032.021">
        <li id="R_6.032.021ab">golāṅgūlo mahākāyo gavākṣo bhīmadarśanaḥ</li>
        <li id="R_6.032.021cd">vṛtaḥ koṭyā mahāvīryas tasthau rāmasya pārvataḥ</li>
    </ul>
    <ul id="R_6.032.022">
        <li id="R_6.032.022ab">ṛṣkāṇāṃ bhīmavegānāṃ dhūmraḥ śatrunibarhaṇaḥ</li>
        <li id="R_6.032.022cd">vṛtaḥ koṭyā mahāvīryas tasthau rāmasya pārśvataḥ</li>
    </ul>
    <ul id="R_6.032.023">
        <li id="R_6.032.023ab">saṃnaddhas tu mahāvīryo gadāpāṇir vibhīṣaṇaḥ</li>
        <li id="R_6.032.023cd">vṛto yas tais tu sacivais tasthau tatra mahābalaḥ</li>
    </ul>
    <ul id="R_6.032.024">
        <li id="R_6.032.024ab">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
        <li id="R_6.032.024cd">samantāt parighāvanto rarakṣur harivāhinīm</li>
    </ul>
    <ul id="R_6.032.025">
        <li id="R_6.032.025ab">tataḥ kopaparītātmā rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.032.025cd">niryāṇaṃ sarvasainyānāṃ drutam ājñāpayat tadā</li>
    </ul>
    <ul id="R_6.032.026">
        <li id="R_6.032.026ab">niṣpatanti tataḥ sainyā hṛṣṭā rāvaṇacoditāḥ</li>
        <li id="R_6.032.026cd">samaye pūryamāṇasya vegā iva mahodadheḥ</li>
    </ul>
    <ul id="R_6.032.027">
        <li id="R_6.032.027ab">etasminn antare ghoraḥ saṃgrāmaḥ samapadyata</li>
        <li id="R_6.032.027cd">rakṣasāṃ vānarāṇāṃ ca yathā devāsure purā</li>
    </ul>
    <ul id="R_6.032.028">
        <li id="R_6.032.028ab">te gadābhiḥ pradīptābhiḥ śaktiśūlaparaśvadhaiḥ</li>
        <li id="R_6.032.028cd">nijaghnur vānarān ghorāḥ kathayantaḥ svavikramān</li>
    </ul>
    <ul id="R_6.032.029">
        <li id="R_6.032.029ab">tathā vṛkṣair mahākāyāḥ parvatāgraiś ca vānarāḥ</li>
        <li id="R_6.032.029cd">rākṣasās tāni rakṣāṃsi nakhair dantaiś ca vegitāḥ</li>
    </ul>
    <ul id="R_6.032.030">
        <li id="R_6.032.030ab">rākṣasās tv apare bhīmāḥ prākārasthā mahīgatān</li>
        <li id="R_6.032.030cd">bhiṇḍipālaiś ca khaḍgaiś ca śūlaiś caiva vyadārayan</li>
    </ul>
    <ul id="R_6.032.031">
        <li id="R_6.032.031ab">vānarāś cāpi saṃkruddhāḥ prākārasthān mahīgatāḥ</li>
        <li id="R_6.032.031cd">rākṣasān pātayām āsuḥ samāplutya plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.032.032">
        <li id="R_6.032.032ab">sa saṃprahāras tumulo māṃsaśoṇitakardamaḥ</li>
        <li id="R_6.032.032cd">rakṣasāṃ vānarāṇāṃ ca saṃbabhūvādbhutopamāḥ</li>
    </ul>
    <ul id="R_6.033.001">
        <li id="R_6.033.001ab">yudhyatāṃ tu tatas teṣāṃ vānarāṇāṃ mahātmanām</li>
        <li id="R_6.033.001cd">rakṣasāṃ saṃbabhūvātha balakopaḥ sudāruṇaḥ</li>
    </ul>
    <ul id="R_6.033.002">
        <li id="R_6.033.002ab">te hayaiḥ kāñcanāpīḍair dhvajaiś cāgniśikhopamaiḥ</li>
        <li id="R_6.033.002cd">rathaiś cādityasaṃkāśaiḥ kavacaiś ca manoramaiḥ</li>
    </ul>
    <ul id="R_6.033.003">
        <li id="R_6.033.003ab">niryayū rākṣasavyāghrā nādayanto diśo daśa</li>
        <li id="R_6.033.003cd">rākṣasā bhīmakarmāṇo rāvaṇasya jayaiṣiṇaḥ</li>
    </ul>
    <ul id="R_6.033.004">
        <li id="R_6.033.004ab">vānarāṇām api camūr mahatī jayam iccatām</li>
        <li id="R_6.033.004cd">abhyadhāvata tāṃ senāṃ rakṣasāṃ kāmarūpiṇām</li>
    </ul>
    <ul id="R_6.033.005">
        <li id="R_6.033.005ab">etasminn antare teṣām anyonyam abhidhāvatām</li>
        <li id="R_6.033.005cd">rakṣasāṃ vānarāṇāṃ ca dvandvayuddham avartata</li>
    </ul>
    <ul id="R_6.033.006">
        <li id="R_6.033.006ab">aṅgadenendrajit sārdhaṃ vāliputreṇa rākṣasaḥ</li>
        <li id="R_6.033.006cd">ayudhyata mahātejās tryambakeṇa yathāndhakaḥ</li>
    </ul>
    <ul id="R_6.033.007">
        <li id="R_6.033.007ab">prajaṅghena ca saṃpātir nityaṃ durmarṣaṇo raṇe</li>
        <li id="R_6.033.007cd">jambūmālinam ārabdho hanūmān api vānaraḥ</li>
    </ul>
    <ul id="R_6.033.008">
        <li id="R_6.033.008ab">saṃgataḥ sumahākrodho rākṣaso rāvaṇānujaḥ</li>
        <li id="R_6.033.008cd">samare tīkṣṇavegena mitraghnena vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.033.009">
        <li id="R_6.033.009ab">tapanena gajaḥ sārdhaṃ rākṣasena mahābalaḥ</li>
        <li id="R_6.033.009cd">nikumbhena mahātejā nīlo 'pi samayudhyata</li>
    </ul>
    <ul id="R_6.033.010">
        <li id="R_6.033.010ab">vānarendras tu sugrīvaḥ praghasena samāgataḥ</li>
        <li id="R_6.033.010cd">saṃgataḥ samare śrīmān virūpākṣeṇa lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.033.011">
        <li id="R_6.033.011ab">agniketuś ca durdharṣo raśmiketuś ca rākṣasaḥ</li>
        <li id="R_6.033.011cd">suptaghno yajñakopaś ca rāmeṇa saha saṃgatāḥ</li>
    </ul>
    <ul id="R_6.033.012">
        <li id="R_6.033.012ab">vajramuṣṭis tu maindena dvividenāśaniprabhaḥ</li>
        <li id="R_6.033.012cd">rākṣasābhyāṃ sughorābhyāṃ kapimukhyau samāgatau</li>
    </ul>
    <ul id="R_6.033.013">
        <li id="R_6.033.013ab">vīraḥ pratapano ghoro rākṣaso raṇadurdharaḥ</li>
        <li id="R_6.033.013cd">samare tīkṣṇavegena nalena samayudhyata</li>
    </ul>
    <ul id="R_6.033.014">
        <li id="R_6.033.014ab">dharmasya putro balavān suṣeṇa iti viśrutaḥ</li>
        <li id="R_6.033.014cd">sa vidyunmālinā sārdham ayudhyata mahākapiḥ</li>
    </ul>
    <ul id="R_6.033.015">
        <li id="R_6.033.015ab">vānarāś cāpare bhīmā rākṣasair aparaiḥ saha</li>
        <li id="R_6.033.015cd">dvandvaṃ samīyur bahudhā yuddhāya bahubhiḥ saha</li>
    </ul>
    <ul id="R_6.033.016">
        <li id="R_6.033.016ab">tatrāsīt sumahad yuddhaṃ tumulaṃ lomaharṣaṇam</li>
        <li id="R_6.033.016cd">rakṣasāṃ vānarāṇāṃ ca vīrāṇāṃ jayam icchatām</li>
    </ul>
    <ul id="R_6.033.017">
        <li id="R_6.033.017ab">harirākṣasadehebhyaḥ prasṛtāḥ keśaśāḍvalāḥ</li>
        <li id="R_6.033.017cd">śarīrasaṃghāṭavahāḥ prasusruḥ śoṇitāpagāḥ</li>
    </ul>
    <ul id="R_6.033.018">
        <li id="R_6.033.018ab">ājaghānendrajit kruddho vajreṇeva śatakratuḥ</li>
        <li id="R_6.033.018cd">aṅgadaṃ gadayā vīraṃ śatrusainyavidāraṇam</li>
    </ul>
    <ul id="R_6.033.019">
        <li id="R_6.033.019ab">tasya kāñcanacitrāṅgaṃ rathaṃ sāśvaṃ sasārathim</li>
        <li id="R_6.033.019cd">jaghāna samare śrīmān aṅgado vegavān kapiḥ</li>
    </ul>
    <ul id="R_6.033.020">
        <li id="R_6.033.020ab">saṃpātis tu tribhir bāṇaiḥ prajaṅghena samāhataḥ</li>
        <li id="R_6.033.020cd">nijaghānāśvakarṇena prajaṅghaṃ raṇamūrdhani</li>
    </ul>
    <ul id="R_6.033.021">
        <li id="R_6.033.021ab">jambūmālī rathasthas tu rathaśaktyā mahābalaḥ</li>
        <li id="R_6.033.021cd">bibheda samare kruddho hanūmantaṃ stanāntare</li>
    </ul>
    <ul id="R_6.033.022">
        <li id="R_6.033.022ab">tasya taṃ ratham āsthāya hanūmān mārutātmajaḥ</li>
        <li id="R_6.033.022cd">pramamātha talenāśu saha tenaiva rakṣasā</li>
    </ul>
    <ul id="R_6.033.023">
        <li id="R_6.033.023ab">bhinnagātraḥ śarais tīkṣṇaiḥ kṣiprahastena rakṣasā</li>
        <li id="R_6.033.023cd">prajaghānādriśṛṅgeṇa tapanaṃ muṣṭinā gajaḥ</li>
    </ul>
    <ul id="R_6.033.024">
        <li id="R_6.033.024ab">grasantam iva sainyāni praghasaṃ vānarādhipaḥ</li>
        <li id="R_6.033.024cd">sugrīvaḥ saptaparṇena nirbibheda jaghāna ca</li>
    </ul>
    <ul id="R_6.033.025">
        <li id="R_6.033.025ab">prapīḍya śaravarṣeṇa rākṣasaṃ bhīmadarśanam</li>
        <li id="R_6.033.025cd">nijaghāna virūpākṣaṃ śareṇaikena lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.033.026">
        <li id="R_6.033.026ab">agniketuś ca durdharṣo raśmiketuś ca rākṣasaḥ</li>
        <li id="R_6.033.026cd">suptighno yajñakopaś ca rāmaṃ nirbibhiduḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.033.027">
        <li id="R_6.033.027ab">teṣāṃ caturṇāṃ rāmas tu śirāṃsi samare śaraiḥ</li>
        <li id="R_6.033.027cd">kruddhaś caturbhiś ciccheda ghorair agniśikhopamaiḥ</li>
    </ul>
    <ul id="R_6.033.028">
        <li id="R_6.033.028ab">vajramuṣṭis tu maindena muṣṭinā nihato raṇe</li>
        <li id="R_6.033.028cd">papāta sarathaḥ sāśvaḥ purāṭṭa iva bhūtale</li>
    </ul>
    <ul id="R_6.033.029">
        <li id="R_6.033.029ab">vajrāśanisamasparśo dvivido 'py aśaniprabham</li>
        <li id="R_6.033.029cd">jaghāna giriśṛṅgeṇa miṣatāṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_6.033.030">
        <li id="R_6.033.030ab">dvividaṃ vānarendraṃ tu drumayodhinam āhave</li>
        <li id="R_6.033.030cd">śarair aśanisaṃkāśaiḥ sa vivyādhāśaniprabhaḥ</li>
    </ul>
    <ul id="R_6.033.031">
        <li id="R_6.033.031ab">sa śarair atividdhāṅgo dvividaḥ krodhamūrchitaḥ</li>
        <li id="R_6.033.031cd">sālena sarathaṃ sāśvaṃ nijaghānāśaniprabham</li>
    </ul>
    <ul id="R_6.033.032">
        <li id="R_6.033.032ab">nikumbhas tu raṇe nīlaṃ nīlāñjanacayaprabham</li>
        <li id="R_6.033.032cd">nirbibheda śarais tīkṣṇaiḥ karair megham ivāṃśumān</li>
    </ul>
    <ul id="R_6.033.033">
        <li id="R_6.033.033ab">punaḥ śaraśatenātha kṣiprahasto niśācaraḥ</li>
        <li id="R_6.033.033cd">bibheda samare nīlaṃ nikumbhaḥ prajahāsa ca</li>
    </ul>
    <ul id="R_6.033.034">
        <li id="R_6.033.034ab">tasyaiva rathacakreṇa nīlo viṣṇur ivāhave</li>
        <li id="R_6.033.034cd">śiraś ciccheda samare nikumbhasya ca sāratheḥ</li>
    </ul>
    <ul id="R_6.033.035">
        <li id="R_6.033.035ab">vidyunmālī rathasthas tu śaraiḥ kāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.033.035cd">suṣeṇaṃ tāḍayām āsa nanāda ca muhur muhuḥ</li>
    </ul>
    <ul id="R_6.033.036">
        <li id="R_6.033.036ab">taṃ rathastham atho dṛṣṭvā suṣeṇo vānarottamaḥ</li>
        <li id="R_6.033.036cd">giriśṛṅgeṇa mahatā ratham āśu nyapātayat</li>
    </ul>
    <ul id="R_6.033.037">
        <li id="R_6.033.037ab">lāghavena tu saṃyukto vidyunmālī niśācaraḥ</li>
        <li id="R_6.033.037cd">apakramya rathāt tūrṇaṃ gadāpāṇiḥ kṣitau sthitaḥ</li>
    </ul>
    <ul id="R_6.033.038">
        <li id="R_6.033.038ab">tataḥ krodhasamāviṣṭaḥ suṣeṇo haripuṃgavaḥ</li>
        <li id="R_6.033.038cd">śilāṃ sumahatīṃ gṛhya niśācaram abhidravat</li>
    </ul>
    <ul id="R_6.033.039">
        <li id="R_6.033.039ab">tam āpatantaṃ gadayā vidyunmālī niśācaraḥ</li>
        <li id="R_6.033.039cd">vakṣasy abhijagnānāśu suṣeṇaṃ harisattamam</li>
    </ul>
    <ul id="R_6.033.040">
        <li id="R_6.033.040ab">gadāprahāraṃ taṃ ghoram acintyaplavagottamaḥ</li>
        <li id="R_6.033.040cd">tāṃ śilāṃ pātayām āsa tasyorasi mahāmṛdhe</li>
    </ul>
    <ul id="R_6.033.041">
        <li id="R_6.033.041ab">śilāprahārābhihato vidyunmālī niśācaraḥ</li>
        <li id="R_6.033.041cd">niṣpiṣṭahṛdayo bhūmau gatāsur nipapāta ha</li>
    </ul>
    <ul id="R_6.033.042">
        <li id="R_6.033.042ab">evaṃ tair vānaraiḥ śūraiḥ śūrās te rajanīcarāḥ</li>
        <li id="R_6.033.042cd">dvandve vimṛditās tatra daityā iva divaukasaiḥ</li>
    </ul>
    <ul id="R_6.033.043">
        <li id="R_6.033.043ab">bhallaiḥ khaḍgair gadābhiś ca śaktitomara paṭṭasaiḥ</li>
        <li id="R_6.033.043cd">apaviddhaś ca bhinnaś ca rathaiḥ sāṃgrāmikair hayaiḥ</li>
    </ul>
    <ul id="R_6.033.044">
        <li id="R_6.033.044ab">nihataiḥ kuñjarair mattais tathā vānararākṣasaiḥ</li>
        <li id="R_6.033.044cd">cakrākṣayugadaṇḍaiś ca bhagnair dharaṇisaṃśritaiḥ</li>
        <li id="R_6.033.044ef">babhūvāyodhanaṃ ghoraṃ gomāyugaṇasevitam</li>
    </ul>
    <ul id="R_6.033.045">
        <li id="R_6.033.045ab">kabandhāni samutpetur dikṣu vānararakṣasām</li>
        <li id="R_6.033.045cd">vimarde tumule tasmin devāsuraraṇopame</li>
    </ul>
    <ul id="R_6.033.046">
        <li id="R_6.033.046ab">
            <seg type="pāda" n="a">vidāryamāṇā haripuṃgavais tadā</seg>
            <seg type="pāda" n="b">niśācarāḥ śoṇitadigdhagātrāḥ</seg>
        </li>
        <li id="R_6.033.046cd">
            <seg type="pāda" n="c">punaḥ suyuddhaṃ tarasā samāśritā</seg>
            <seg type="pāda" n="d">divākarasyāstamayābhikāṅkṣiṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.034.001">
        <li id="R_6.034.001ab">yudhyatām eva teṣāṃ tu tadā vānararakṣasām</li>
        <li id="R_6.034.001cd">ravir astaṃ gato rātriḥ pravṛttā prāṇahāriṇī</li>
    </ul>
    <ul id="R_6.034.002">
        <li id="R_6.034.002ab">anyonyaṃ baddhavairāṇāṃ ghorāṇāṃ jayam icchatām</li>
        <li id="R_6.034.002cd">saṃpravṛttaṃ niśāyuddhaṃ tadā vāraṇarakṣasām</li>
    </ul>
    <ul id="R_6.034.003">
        <li id="R_6.034.003ab">rākṣaso 'sīti harayo hariś cāsīti rākṣasāḥ</li>
        <li id="R_6.034.003cd">anyonyaṃ samare jaghnus tasmiṃs tamasi dāruṇe</li>
    </ul>
    <ul id="R_6.034.004">
        <li id="R_6.034.004ab">jahi dāraya caitīti kathaṃ vidravasīti ca</li>
        <li id="R_6.034.004cd">evaṃ sutumulaḥ śabdas tasmiṃs tamasi śuśruve</li>
    </ul>
    <ul id="R_6.034.005">
        <li id="R_6.034.005ab">kālāḥ kāñcanasaṃnāhās tasmiṃs tamasi rākṣasāḥ</li>
        <li id="R_6.034.005cd">saṃprādṛśyanta śailendrā dīptauṣadhivanā iva</li>
    </ul>
    <ul id="R_6.034.006">
        <li id="R_6.034.006ab">tasmiṃs tamasi duṣpāre rākṣasāḥ krodhamūrchitāḥ</li>
        <li id="R_6.034.006cd">paripetur mahāvegā bhakṣayantaḥ plavaṃgamān</li>
    </ul>
    <ul id="R_6.034.007">
        <li id="R_6.034.007ab">te hayān kāñcanāpīḍan dhvajāṃś cāgniśikhopamān</li>
        <li id="R_6.034.007cd">āplutya daśanais tīkṣṇair bhīmakopā vyadārayan</li>
    </ul>
    <ul id="R_6.034.008">
        <li id="R_6.034.008ab">kuñjarān kuñjarārohān patākādhvajino rathān</li>
        <li id="R_6.034.008cd">cakarṣuś ca dadaṃśuś ca daśanaiḥ krodhamūrchitāḥ</li>
    </ul>
    <ul id="R_6.034.009">
        <li id="R_6.034.009ab">lakṣmaṇaś cāpi rāmaś ca śarair āśīviṣomapaiḥ</li>
        <li id="R_6.034.009cd">dṛśyādṛśyāni rakṣāṃsi pravarāṇi nijaghnatuḥ</li>
    </ul>
    <ul id="R_6.034.010">
        <li id="R_6.034.010ab">turaṃgakhuravidhvastaṃ rathanemisamuddhatam</li>
        <li id="R_6.034.010cd">rurodha karṇanetrāṇiṇyudhyatāṃ dharaṇīrajaḥ</li>
    </ul>
    <ul id="R_6.034.011">
        <li id="R_6.034.011ab">vartamāne tathā ghore saṃgrāme lomaharṣaṇe</li>
        <li id="R_6.034.011cd">rudhirodā mahāvegā nadyas tatra prasusruvuḥ</li>
    </ul>
    <ul id="R_6.034.012">
        <li id="R_6.034.012ab">tato bherīmṛdaṅgānāṃ paṇavānāṃ ca nisvanaḥ</li>
        <li id="R_6.034.012cd">śaṅkhaveṇusvanonmiśraḥ saṃbabhūvādbhutopamaḥ</li>
    </ul>
    <ul id="R_6.034.013">
        <li id="R_6.034.013ab">hatānāṃ stanamānānāṃ rākṣasānāṃ ca nisvanaḥ</li>
        <li id="R_6.034.013cd">śastrāṇāṃ vānarāṇāṃ ca saṃbabhūvātidāruṇaḥ</li>
    </ul>
    <ul id="R_6.034.014">
        <li id="R_6.034.014ab">śastrapuṣpopahārā ca tatrāsīd yuddhamedinī</li>
        <li id="R_6.034.014cd">durjñeyā durniveśā ca śoṇitāsravakardamā</li>
    </ul>
    <ul id="R_6.034.015">
        <li id="R_6.034.015ab">sā babhūva niśā ghorā harirākṣasahāriṇī</li>
        <li id="R_6.034.015cd">kālarātrīva bhūtānāṃ sarveṣāṃ duratikramā</li>
    </ul>
    <ul id="R_6.034.016">
        <li id="R_6.034.016ab">tatas te rākṣasās tatra tasmiṃs tamasi dāruṇe</li>
        <li id="R_6.034.016cd">rāmam evābhyadhāvanta saṃhṛṣṭā śaravṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_6.034.017">
        <li id="R_6.034.017ab">teṣām āpatatāṃ śabdaḥ kruddhānām abhigarjatām</li>
        <li id="R_6.034.017cd">udvarta iva saptānāṃ samudrāṇām abhūt svanaḥ</li>
    </ul>
    <ul id="R_6.034.018">
        <li id="R_6.034.018ab">teṣāṃ rāmaḥ śaraiḥ ṣaḍbhiḥ ṣaḍ jaghāna niśācarān</li>
        <li id="R_6.034.018cd">nimeṣāntaramātreṇa śitair agniśikhopamaiḥ</li>
    </ul>
    <ul id="R_6.034.019">
        <li id="R_6.034.019ab">yajñaśatruś ca durdharṣo mahāpārśvamahodarau</li>
        <li id="R_6.034.019cd">vajradaṃṣṭro mahākāyas tau cobhau śukasāraṇau</li>
    </ul>
    <ul id="R_6.034.020">
        <li id="R_6.034.020ab">te tu rāmeṇa bāṇaughaḥ sarvamarmasu tāḍitāḥ</li>
        <li id="R_6.034.020cd">yuddhād apasṛtās tatra sāvaśeṣāyuṣo 'bhavan</li>
    </ul>
    <ul id="R_6.034.021">
        <li id="R_6.034.021ab">tataḥ kāñcanacitrāṅgaiḥ śarair agniśikhopamaiḥ</li>
        <li id="R_6.034.021cd">diśaś cakāra vimalāḥ pradiśaś ca mahābalaḥ</li>
    </ul>
    <ul id="R_6.034.022">
        <li id="R_6.034.022ab">ye tv anye rākṣasā vīrā rāmasyābhimukhe sthitāḥ</li>
        <li id="R_6.034.022cd">te 'pi naṣṭāḥ samāsādya pataṃgā iva pāvakam</li>
    </ul>
    <ul id="R_6.034.023">
        <li id="R_6.034.023ab">suvarṇapuṅkhair viśikhaiḥ saṃpatadbhiḥ sahasraśaḥ</li>
        <li id="R_6.034.023cd">babhūva rajanī citrā khadyotair iva śāradī</li>
    </ul>
    <ul id="R_6.034.024">
        <li id="R_6.034.024ab">rākṣasānāṃ ca ninadair harīṇāṃ cāpi garjitaiḥ</li>
        <li id="R_6.034.024cd">sā babhūva niśā ghorā bhūyo ghoratarā tadā</li>
    </ul>
    <ul id="R_6.034.025">
        <li id="R_6.034.025ab">tena śabdena mahatā pravṛddhena samantataḥ</li>
        <li id="R_6.034.025cd">trikūṭaḥ kandarākīrṇaḥ pravyāharad ivācalaḥ</li>
    </ul>
    <ul id="R_6.034.026">
        <li id="R_6.034.026ab">golāṅgūlā mahākāyās tamasā tulyavarcasaḥ</li>
        <li id="R_6.034.026cd">saṃpariṣvajya bāhubhyāṃ bhakṣayan rajanīcarān</li>
    </ul>
    <ul id="R_6.034.027">
        <li id="R_6.034.027ab">aṅgadas tu raṇe śatruṃ nihantuṃ samupasthitaḥ</li>
        <li id="R_6.034.027cd">rāvaṇer nijaghānāśu sārathiṃ ca hayān api</li>
    </ul>
    <ul id="R_6.034.028">
        <li id="R_6.034.028ab">indrajit tu rathaṃ tyaktvā hatāśvo hatasārathiḥ</li>
        <li id="R_6.034.028cd">aṅgadena mahāmāyas tatraivāntaradhīyata</li>
    </ul>
    <ul id="R_6.034.029">
        <li id="R_6.034.029ab">so 'ntardhāna gataḥ pāpo rāvaṇī raṇakarkaśaḥ</li>
        <li id="R_6.034.029cd">brahmadattavaro vīro rāvaṇiḥ krodhamūrchitaḥ</li>
        <li id="R_6.034.029ef">adṛśyo niśitān bāṇān mumocāśanivarcasaḥ</li>
    </ul>
    <ul id="R_6.034.030">
        <li id="R_6.034.030ab">sa rāmaṃ lakṣmaṇaṃ caiva ghorair nāgamayaiḥ śaraiḥ</li>
        <li id="R_6.034.030cd">bibheda samare kruddhaḥ sarvagātreṣu rākṣasaḥ</li>
    </ul>
    <ul id="R_6.035.001">
        <li id="R_6.035.001ab">sa tasya gatim anvicchan rājaputraḥ pratāpavān</li>
        <li id="R_6.035.001cd">dideśātibalo rāmo daśavānarayūthapān</li>
    </ul>
    <ul id="R_6.035.002">
        <li id="R_6.035.002ab">dvau suṣeṇasya dāyādau nīlaṃ ca plavagarṣabham</li>
        <li id="R_6.035.002cd">aṅgadaṃ vāliputraṃ ca śarabhaṃ ca tarasvinam</li>
    </ul>
    <ul id="R_6.035.003">
        <li id="R_6.035.003ab">vinataṃ jāmbavantaṃ ca sānuprasthaṃ mahābalam</li>
        <li id="R_6.035.003cd">ṛṣabhaṃ carṣabhaskandham ādideśa paraṃtapaḥ</li>
    </ul>
    <ul id="R_6.035.004">
        <li id="R_6.035.004ab">te saṃprahṛṣṭā harayo bhīmān udyamya pādapān</li>
        <li id="R_6.035.004cd">ākāśaṃ viviśuḥ sarve mārgāmāṇā diśo daśa</li>
    </ul>
    <ul id="R_6.035.005">
        <li id="R_6.035.005ab">teṣāṃ vegavatāṃ vegam iṣubhir vegavattaraiḥ</li>
        <li id="R_6.035.005cd">astravit paramāstreṇa vārayām āsa rāvaṇiḥ</li>
    </ul>
    <ul id="R_6.035.006">
        <li id="R_6.035.006ab">taṃ bhīmavegā harayo nārācaiḥ kṣatavikṣatāḥ</li>
        <li id="R_6.035.006cd">andhakāre na dadṛśur meghaiḥ sūryam ivāvṛtam</li>
    </ul>
    <ul id="R_6.035.007">
        <li id="R_6.035.007ab">rāmalakṣmaṇayor eva sarvamarmabhidaḥ śarān</li>
        <li id="R_6.035.007cd">bhṛśam āveśayām āsa rāvaṇiḥ samitiṃjayaḥ</li>
    </ul>
    <ul id="R_6.035.008">
        <li id="R_6.035.008ab">nirantaraśarīrau tu bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.035.008cd">kruddhenendrajitā vīrau pannagaiḥ śaratāṃ gataiḥ</li>
    </ul>
    <ul id="R_6.035.009">
        <li id="R_6.035.009ab">tayoḥ kṣatajamārgeṇa susrāva rudhiraṃ bahu</li>
        <li id="R_6.035.009cd">tāv ubhau ca prakāśete puṣpitāv iva kiṃśukau</li>
    </ul>
    <ul id="R_6.035.010">
        <li id="R_6.035.010ab">tataḥ paryantaraktākṣo bhinnāñjanacayopamaḥ</li>
        <li id="R_6.035.010cd">rāvaṇir bhrātarau vākyam antardhānagato 'bravīt</li>
    </ul>
    <ul id="R_6.035.011">
        <li id="R_6.035.011ab">yudhyamānam anālakṣyaṃ śakro 'pi tridaśeśvaraḥ</li>
        <li id="R_6.035.011cd">draṣṭum āsādituṃ vāpi na śaktaḥ kiṃ punar yuvām</li>
    </ul>
    <ul id="R_6.035.012">
        <li id="R_6.035.012ab">prāvṛtāv iṣujālena rāghavau kaṅkapatriṇā</li>
        <li id="R_6.035.012cd">eṣa roṣaparītātmā nayāmi yamasādanam</li>
    </ul>
    <ul id="R_6.035.013">
        <li id="R_6.035.013ab">evam uktvā tu dharmajñau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.035.013cd">nirbibheda śitair bāṇaiḥ prajaharṣa nanāda ca</li>
    </ul>
    <ul id="R_6.035.014">
        <li id="R_6.035.014ab">bhinnāñjanacayaśyāmo visphārya vipulaṃ dhanuḥ</li>
        <li id="R_6.035.014cd">bhūyo bhūyaḥ śarān ghorān visasarja mahāmṛdhe</li>
    </ul>
    <ul id="R_6.035.015">
        <li id="R_6.035.015ab">tato marmasu marmajño majjayan niśitāñ śarān</li>
        <li id="R_6.035.015cd">rāmalakṣmaṇayor vīro nanāda ca muhur muhuḥ</li>
    </ul>
    <ul id="R_6.035.016">
        <li id="R_6.035.016ab">baddhau tu śarabandhena tāv ubhau raṇamūrdhani</li>
        <li id="R_6.035.016cd">nimeṣāntaramātreṇa na śekatur udīkṣitum</li>
    </ul>
    <ul id="R_6.035.017">
        <li id="R_6.035.017ab">tato vibhinnasarvāṅgau śaraśalyācitāv ubhau</li>
        <li id="R_6.035.017cd">dhvajāv iva mahendrasya rajjumuktau prakampitau</li>
    </ul>
    <ul id="R_6.035.018">
        <li id="R_6.035.018ab">tau saṃpracalitau vīrau marmabhedena karśitau</li>
        <li id="R_6.035.018cd">nipetatur maheṣvāsau jagatyāṃ jagatīpatī</li>
    </ul>
    <ul id="R_6.035.019">
        <li id="R_6.035.019ab">tau vīraśayane vīrau śayānau rudhirokṣitau</li>
        <li id="R_6.035.019cd">śaraveṣṭitasarvāṅgāv ārtau paramapīḍitau</li>
    </ul>
    <ul id="R_6.035.020">
        <li id="R_6.035.020ab">na hy aviddhaṃ tayor gātraṃ babhūvāṅgulam antaram</li>
        <li id="R_6.035.020cd">nānirbhinnaṃ na cāstabdham ā karāgrād ajihmagaiḥ</li>
    </ul>
    <ul id="R_6.035.021">
        <li id="R_6.035.021ab">tau tu krūreṇa nihatau rakṣasā kāmarūpiṇā</li>
        <li id="R_6.035.021cd">asṛksusruvatus tīvraṃ jalaṃ prasravaṇāv iva</li>
    </ul>
    <ul id="R_6.035.022">
        <li id="R_6.035.022ab">papāta prathamaṃ rāmo viddho marmasu mārgaṇaiḥ</li>
        <li id="R_6.035.022cd">krodhād indrajitā yena purā śakro vinirjitaḥ</li>
    </ul>
    <ul id="R_6.035.023">
        <li id="R_6.035.023ab">nāracair ardhanārācair bhallair añjalikair api</li>
        <li id="R_6.035.023cd">vivyādha vatsadantaiś ca siṃhadaṃṣṭraiḥ kṣurais tathā</li>
    </ul>
    <ul id="R_6.035.024">
        <li id="R_6.035.024ab">sa vīraśayane śiśye vijyam ādāya kārmukam</li>
        <li id="R_6.035.024cd">bhinnamuṣṭiparīṇāhaṃ triṇataṃ rukmabhūṣitam</li>
    </ul>
    <ul id="R_6.035.025">
        <li id="R_6.035.025ab">bāṇapātāntare rāmaṃ patitaṃ puruṣarṣabham</li>
        <li id="R_6.035.025cd">sa tatra lakṣmaṇo dṛṣṭvā nirāśo jīvite 'bhavat</li>
    </ul>
    <ul id="R_6.035.026">
        <li id="R_6.035.026ab">
            <seg type="pāda" n="a">baddhau tu vīrau patitau śayānau</seg>
            <seg type="pāda" n="b">tau vānarāḥ saṃparivārya tasthuḥ</seg>
        </li>
        <li id="R_6.035.026cd">
            <seg type="pāda" n="c">samāgatā vāyusutapramukhyā</seg>
            <seg type="pāda" n="d">viṣadam ārtāḥ paramaṃ ca jagmuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.036.001">
        <li id="R_6.036.001ab">tato dyāṃ pṛthivīṃ caiva vīkṣamāṇā vanaukasaḥ</li>
        <li id="R_6.036.001cd">dadṛśuḥ saṃtatau bāṇair bhrātarau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.036.002">
        <li id="R_6.036.002ab">vṛṣṭvevoparate deve kṛtakarmaṇi rākṣase</li>
        <li id="R_6.036.002cd">ājagāmātha taṃ deśaṃ sasugrīvo vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.036.003">
        <li id="R_6.036.003ab">nīladvividamaindāś ca suṣeṇasumukhāṅgadāḥ</li>
        <li id="R_6.036.003cd">tūrṇaṃ hanumatā sārdham anvaśocanta rāghavau</li>
    </ul>
    <ul id="R_6.036.004">
        <li id="R_6.036.004ab">niśceṣṭau mandaniḥśvāsau śoṇitaughapariplutau</li>
        <li id="R_6.036.004cd">śarajālācitau stabdhau śayānau śaratalpayoḥ</li>
    </ul>
    <ul id="R_6.036.005">
        <li id="R_6.036.005ab">niḥśvasantau yathā sarpau niśceṣṭau mandavikramau</li>
        <li id="R_6.036.005cd">rudhirasrāvadigdhāṅgau tāpanīyāv iva dhvajau</li>
    </ul>
    <ul id="R_6.036.006">
        <li id="R_6.036.006ab">tau vīraśayane vīrau śayānau mandaceṣṭitau</li>
        <li id="R_6.036.006cd">yūthapais taiḥ parivṛtau bāṣpavyākulalocanaiḥ</li>
    </ul>
    <ul id="R_6.036.007">
        <li id="R_6.036.007ab">rāghavau patitau dṛṣṭvā śarajālasamāvṛtau</li>
        <li id="R_6.036.007cd">babhūvur vyathitāḥ sarve vānarāḥ savibhīṣaṇāḥ</li>
    </ul>
    <ul id="R_6.036.008">
        <li id="R_6.036.008ab">antarikṣaṃ nirīkṣanto diśaḥ sarvāś ca vānarāḥ</li>
        <li id="R_6.036.008cd">na cainaṃ māyayā channaṃ dadṛśū rāvaṇiṃ raṇe</li>
    </ul>
    <ul id="R_6.036.009">
        <li id="R_6.036.009ab">taṃ tu māyāpraticchinnaṃ māyayaiva vibhīṣaṇaḥ</li>
        <li id="R_6.036.009cd">vīkṣamāṇo dadarśātha bhrātuḥ putram avasthitam</li>
    </ul>
    <ul id="R_6.036.010">
        <li id="R_6.036.010ab">tam apratima karmāṇam apratidvandvam āhave</li>
        <li id="R_6.036.010cd">dadarśāntarhitaṃ vīraṃ varadānād vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.036.011">
        <li id="R_6.036.011ab">indrajit tv ātmanaḥ karma tau śayānau samīkṣya ca</li>
        <li id="R_6.036.011cd">uvāca paramaprīto harṣayan sarvanairṛtān</li>
    </ul>
    <ul id="R_6.036.012">
        <li id="R_6.036.012ab">dūṣaṇasya ca hantārau kharasya ca mahābalau</li>
        <li id="R_6.036.012cd">sāditau māmakair bāṇair bhrātarau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.036.013">
        <li id="R_6.036.013ab">nemau mokṣayituṃ śakyāv etasmād iṣubandhanāt</li>
        <li id="R_6.036.013cd">sarvair api samāgamya sarṣisaṅghaiḥ surāsuraiḥ</li>
    </ul>
    <ul id="R_6.036.014">
        <li id="R_6.036.014ab">yatkṛte cintayānasya śokārtasya pitur mama</li>
        <li id="R_6.036.014cd">aspṛṣṭvā śayanaṃ gātrais triyāmā yāti śarvatī</li>
    </ul>
    <ul id="R_6.036.015">
        <li id="R_6.036.015ab">kṛtsneyaṃ yatkṛte laṅkā nadī varṣāsv ivākulā</li>
        <li id="R_6.036.015cd">so 'yaṃ mūlaharo 'narthaḥ sarveṣāṃ nihato mayā</li>
    </ul>
    <ul id="R_6.036.016">
        <li id="R_6.036.016ab">rāmasya lakṣmaṇasyaiva sarveṣāṃ ca vanaukasām</li>
        <li id="R_6.036.016cd">vikramā niṣphalāḥ sarve yathā śaradi toyadāḥ</li>
    </ul>
    <ul id="R_6.036.017">
        <li id="R_6.036.017ab">evam uktvā tu tān sarvān rākṣasān paripārśvagān</li>
        <li id="R_6.036.017cd">yūthapān api tān sarvāṃs tāḍayām āsa rāvaṇiḥ</li>
    </ul>
    <ul id="R_6.036.018">
        <li id="R_6.036.018ab">tān ardayitvā bāṇaughais trāsayitvā ca vānarān</li>
        <li id="R_6.036.018cd">prajahāsa mahābāhur vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_6.036.019">
        <li id="R_6.036.019ab">śarabandhena ghoreṇa mayā baddhau camūmukhe</li>
        <li id="R_6.036.019cd">sahitau bhrātarāv etau niśāmayata rākṣasāḥ</li>
    </ul>
    <ul id="R_6.036.020">
        <li id="R_6.036.020ab">evam uktās tu te sarve rākṣasāḥ kūṭayodhinaḥ</li>
        <li id="R_6.036.020cd">paraṃ vismayam ājagmuḥ karmaṇā tena toṣitāḥ</li>
    </ul>
    <ul id="R_6.036.021">
        <li id="R_6.036.021ab">vineduś ca mahānādān sarve te jaladopamāḥ</li>
        <li id="R_6.036.021cd">hato rāma iti jñātvā rāvaṇiṃ samapūjayan</li>
    </ul>
    <ul id="R_6.036.022">
        <li id="R_6.036.022ab">niṣpandau tu tadā dṛṣṭvā tāv ubhau rāmalakṣmaṇau</li>
        <li id="R_6.036.022cd">vasudhāyāṃ nirucchvāsau hatāv ity anvamanyata</li>
    </ul>
    <ul id="R_6.036.023">
        <li id="R_6.036.023ab">harṣeṇa tu samāviṣṭa indrajit samitiṃjayaḥ</li>
        <li id="R_6.036.023cd">praviveśa purīṃ laṅkāṃ harṣayan sarvanairṛtān</li>
    </ul>
    <ul id="R_6.036.024">
        <li id="R_6.036.024ab">rāmalakṣmaṇayor dṛṣṭvā śarīre sāyakaiś cite</li>
        <li id="R_6.036.024cd">sarvāṇi cāṅgopāṅgāni sugrīvaṃ bhayam āviśat</li>
    </ul>
    <ul id="R_6.036.025">
        <li id="R_6.036.025ab">tam uvāca paritrastaṃ vānarendraṃ vibhīṣaṇaḥ</li>
        <li id="R_6.036.025cd">sabāṣpavadanaṃ dīnaṃ śokavyākulalocanam</li>
    </ul>
    <ul id="R_6.036.026">
        <li id="R_6.036.026ab">alaṃ trāsena sugrīva bāṣpavego nigṛhyatām</li>
        <li id="R_6.036.026cd">evaṃ prāyāṇi yuddhāni vijayo nāsti naiṣṭhikaḥ</li>
    </ul>
    <ul id="R_6.036.027">
        <li id="R_6.036.027ab">saśeṣabhāgyatāsmākaṃ yadi vīra bhaviṣyati</li>
        <li id="R_6.036.027cd">moham etau prahāsyete bhrātarau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.036.028">
        <li id="R_6.036.028ab">paryavasthāpayātmānam anāthaṃ māṃ ca vānara</li>
        <li id="R_6.036.028cd">satyadharmānuraktānāṃ nāsti mṛtyukṛtaṃ bhayam</li>
    </ul>
    <ul id="R_6.036.029">
        <li id="R_6.036.029ab">evam uktvā tatas tasya jalaklinnena pāṇinā</li>
        <li id="R_6.036.029cd">sugrīvasya śubhe netre pramamārja vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.036.030">
        <li id="R_6.036.030ab">pramṛjya vadanaṃ tasya kapirājasya dhīmataḥ</li>
        <li id="R_6.036.030cd">abravīt kālasaṃprātam asaṃbhrāntam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_6.036.031">
        <li id="R_6.036.031ab">na kālaḥ kapirājendra vaiklavyam anuvartitum</li>
        <li id="R_6.036.031cd">atisneho 'py akāle 'smin maraṇāyopapadyate</li>
    </ul>
    <ul id="R_6.036.032">
        <li id="R_6.036.032ab">tasmād utsṛjya vaiklavyaṃ sarvakāryavināśanam</li>
        <li id="R_6.036.032cd">hitaṃ rāmapurogāṇāṃ sainyānām anucintyatām</li>
    </ul>
    <ul id="R_6.036.033">
        <li id="R_6.036.033ab">atha vā rakṣyatāṃ rāmo yāvat saṃjñā viparyayaḥ</li>
        <li id="R_6.036.033cd">labdhasaṃjñau tu kākutsthau bhayaṃ no vyapaneṣyataḥ</li>
    </ul>
    <ul id="R_6.036.034">
        <li id="R_6.036.034ab">naitat kiṃ cana rāmasya na ca rāmo mumūrṣati</li>
        <li id="R_6.036.034cd">na hy enaṃ hāsyate lakṣmīr durlabhā yā gatāyuṣām</li>
    </ul>
    <ul id="R_6.036.035">
        <li id="R_6.036.035ab">tasmād āśvāsayātmānaṃ balaṃ cāśvāsaya svakam</li>
        <li id="R_6.036.035cd">yāvat sarvāṇi sainyāni punaḥ saṃsthāpayāmy aham</li>
    </ul>
    <ul id="R_6.036.036">
        <li id="R_6.036.036ab">ete hy utphullanayanās trāsād āgatasādhvasāḥ</li>
        <li id="R_6.036.036cd">karṇe karṇe prakathitā harayo haripuṃgava</li>
    </ul>
    <ul id="R_6.036.037">
        <li id="R_6.036.037ab">māṃ tu dṛṣṭvā pradhāvantam anīkaṃ saṃpraharṣitum</li>
        <li id="R_6.036.037cd">tyajantu harayas trāsaṃ bhuktapūrvām iva srajam</li>
    </ul>
    <ul id="R_6.036.038">
        <li id="R_6.036.038ab">samāśvāsya tu sugrīvaṃ rākṣasendro vibhīṣaṇaḥ</li>
        <li id="R_6.036.038cd">vidrutaṃ vānarānīkaṃ tat samāśvāsayat punaḥ</li>
    </ul>
    <ul id="R_6.036.039">
        <li id="R_6.036.039ab">indrajit tu mahāmāyaḥ sarvasainyasamāvṛtaḥ</li>
        <li id="R_6.036.039cd">viveśa nagarīṃ laṅkāṃ pitaraṃ cābhyupāgamat</li>
    </ul>
    <ul id="R_6.036.040">
        <li id="R_6.036.040ab">tatra rāvaṇam āsīnam abhivādya kṛtāñjaliḥ</li>
        <li id="R_6.036.040cd">ācacakṣe priyaṃ pitre nihatau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.036.041">
        <li id="R_6.036.041ab">utpapāta tato hṛṣṭaḥ putraṃ ca pariṣasvaje</li>
        <li id="R_6.036.041cd">rāvaṇo rakṣasāṃ madhye śrutvā śatrū nipātitau</li>
    </ul>
    <ul id="R_6.036.042">
        <li id="R_6.036.042ab">upāghrāya sa mūrdhny enaṃ papraccha prītamānasaḥ</li>
        <li id="R_6.036.042cd">pṛcchate ca yathāvṛttaṃ pitre sarvaṃ nyavedayat</li>
    </ul>
    <ul id="R_6.036.043">
        <li id="R_6.036.043ab">
            <seg type="pāda" n="a">sa harṣavegānugatāntarātmā</seg>
            <seg type="pāda" n="b">śrutvā vacas tasya mahārathasya</seg>
        </li>
        <li id="R_6.036.043cd">
            <seg type="pāda" n="c">jahau jvaraṃ dāśaratheḥ samutthitaṃ</seg>
            <seg type="pāda" n="d">prahṛṣya vācābhinananda putram</seg>
        </li>
    </ul>
    <ul id="R_6.037.001">
        <li id="R_6.037.001ab">pratipraviṣṭe laṅkāṃ tu kṛtārthe rāvaṇātmaje</li>
        <li id="R_6.037.001cd">rāghavaṃ parivāryārtā rarakṣur vānararṣabhāḥ</li>
    </ul>
    <ul id="R_6.037.002">
        <li id="R_6.037.002ab">hanūmān aṅgado nīlaḥ suṣeṇaḥ kumudo nalaḥ</li>
        <li id="R_6.037.002cd">gajo gavākṣo gavayaḥ śarabho gandhamādanaḥ</li>
    </ul>
    <ul id="R_6.037.003">
        <li id="R_6.037.003ab">jāmbavān ṛṣabhaḥ sundo rambhaḥ śatabaliḥ pṛthuḥ</li>
        <li id="R_6.037.003cd">vyūḍhānīkāś ca yattāś ca drumān ādāya sarvataḥ</li>
    </ul>
    <ul id="R_6.037.004">
        <li id="R_6.037.004ab">vīkṣamāṇā diśaḥ sarvās tiryag ūrdhvaṃ ca vānarāḥ</li>
        <li id="R_6.037.004cd">tṛṇeṣv api ca ceṣṭatsu rākṣasā iti menire</li>
    </ul>
    <ul id="R_6.037.005">
        <li id="R_6.037.005ab">rāvaṇaś cāpi saṃhṛṣṭo visṛjyendrajitaṃ sutam</li>
        <li id="R_6.037.005cd">ājuhāva tataḥ sītā rakṣaṇī rākṣasīs tadā</li>
    </ul>
    <ul id="R_6.037.006">
        <li id="R_6.037.006ab">rākṣasyas trijaṭā cāpi śāsanāt tam upasthitāḥ</li>
        <li id="R_6.037.006cd">tā uvāca tato hṛṣṭo rākṣasī rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_6.037.007">
        <li id="R_6.037.007ab">hatāv indrajitākhyāta vaidehyā rāmalakṣmaṇau</li>
        <li id="R_6.037.007cd">puṣpakaṃ ca samāropya darśayadhvaṃ hatau raṇe</li>
    </ul>
    <ul id="R_6.037.008">
        <li id="R_6.037.008ab">yad āśrayād avaṣṭabdho neyaṃ mām upatiṣṭhati</li>
        <li id="R_6.037.008cd">so 'syā bhartā saha bhrātrā nirasto raṇamūrdhani</li>
    </ul>
    <ul id="R_6.037.009">
        <li id="R_6.037.009ab">nirviśaṅkā nirudvignā nirapekṣā ca maithilī</li>
        <li id="R_6.037.009cd">mām upasthāsyate sītā sarvābharaṇabhūṣitā</li>
    </ul>
    <ul id="R_6.037.010">
        <li id="R_6.037.010ab">adya kālavaśaṃ prāptaṃ raṇe rāmaṃ salakṣmaṇam</li>
        <li id="R_6.037.010cd">avekṣya vinivṛttāśā nānyāṃ gatim apaśyatī</li>
    </ul>
    <ul id="R_6.037.011">
        <li id="R_6.037.011ab">tasya tad vacanaṃ śrutvā rāvaṇasya durātmanaḥ</li>
        <li id="R_6.037.011cd">rākṣasyas tās tathety uktvā prajagmur yatra puṣpakam</li>
    </ul>
    <ul id="R_6.037.012">
        <li id="R_6.037.012ab">tataḥ puṣpakam ādaya rākṣasyo rāvaṇājñayā</li>
        <li id="R_6.037.012cd">aśokavanikāsthāṃ tāṃ maithilīṃ samupānayan</li>
    </ul>
    <ul id="R_6.037.013">
        <li id="R_6.037.013ab">tām ādāya tu rākṣasyo bhartṛśokaparāyaṇām</li>
        <li id="R_6.037.013cd">sītām āropayām āsur vimānaṃ puṣpakaṃ tadā</li>
    </ul>
    <ul id="R_6.037.014">
        <li id="R_6.037.014ab">tataḥ puṣpakam āropya sītāṃ trijaṭayā saha</li>
        <li id="R_6.037.014cd">rāvaṇo 'kārayal laṅkāṃ patākādhvajamālinīm</li>
    </ul>
    <ul id="R_6.037.015">
        <li id="R_6.037.015ab">prāghoṣayata hṛṣṭaś ca laṅkāyāṃ rākṣaseśvaraḥ</li>
        <li id="R_6.037.015cd">rāghavo lakṣmaṇaś caiva hatāv indrajitā raṇe</li>
    </ul>
    <ul id="R_6.037.016">
        <li id="R_6.037.016ab">vimānenāpi sītā tu gatvā trijaṭayā saha</li>
        <li id="R_6.037.016cd">dadarśa vānarāṇāṃ tu sarvaṃ sinyaṃ nipātitam</li>
    </ul>
    <ul id="R_6.037.017">
        <li id="R_6.037.017ab">prahṛṣṭamanasaś cāpi dadarśa piśitāśanān</li>
        <li id="R_6.037.017cd">vānarāṃś cāpi duḥkhārtān rāmalakṣmaṇapārśvataḥ</li>
    </ul>
    <ul id="R_6.037.018">
        <li id="R_6.037.018ab">tataḥ sītā dadarśobhau śayānau śatatalpayoḥ</li>
        <li id="R_6.037.018cd">lakṣmaṇaṃ caiva rāmaṃ ca visaṃjñau śarapīḍitau</li>
    </ul>
    <ul id="R_6.037.019">
        <li id="R_6.037.019ab">vidhvastakavacau vīrau vipraviddhaśarāsanau</li>
        <li id="R_6.037.019cd">sāyakaiś chinnasarvāṅgau śarastambhamayau kṣitau</li>
    </ul>
    <ul id="R_6.037.020">
        <li id="R_6.037.020ab">tau dṛṣṭvā bhrātarau tatra vīrau sā puruṣarṣabhau</li>
        <li id="R_6.037.020cd">duḥkhārtā subhṛśaṃ sītā karuṇaṃ vilalāpa ha</li>
    </ul>
    <ul id="R_6.037.021">
        <li id="R_6.037.021ab">
            <seg type="pāda" n="a">sā bāṣpaśokābhihatā samīkṣya</seg>
            <seg type="pāda" n="b">tau bhrātarau devasamaprabhāvau</seg>
        </li>
        <li id="R_6.037.021cd">
            <seg type="pāda" n="c">vitarkayantī nidhanaṃ tayoḥ sā</seg>
            <seg type="pāda" n="d">duḥkhānvitā vākyam idaṃ jagāda</seg>
        </li>
    </ul>
    <ul id="R_6.038.001">
        <li id="R_6.038.001ab">bhartāraṃ nihataṃ dṛṣṭvā lakṣmaṇaṃ ca mahābalam</li>
        <li id="R_6.038.001cd">vilalāpa bhṛśaṃ sītā karuṇaṃ śokakarśitā</li>
    </ul>
    <ul id="R_6.038.002">
        <li id="R_6.038.002ab">ūcur lakṣaṇikā ye māṃ putriṇy avidhaveti ca</li>
        <li id="R_6.038.002cd">te 'sya sarve hate rāme 'jñānino 'nṛtavādinaḥ</li>
    </ul>
    <ul id="R_6.038.003">
        <li id="R_6.038.003ab">yajvano mahiṣīṃ ye mām ūcuḥ patnīṃ ca satriṇaḥ</li>
        <li id="R_6.038.003cd">te 'dya sarve hate rāme 'jñānino 'nṛtavādinaḥ</li>
    </ul>
    <ul id="R_6.038.004">
        <li id="R_6.038.004ab">vīrapārthivapatnī tvaṃ ye dhanyeti ca māṃ viduḥ</li>
        <li id="R_6.038.004cd">te 'dya sarve hate rāme 'jñānino 'nṛtavādinaḥ</li>
    </ul>
    <ul id="R_6.038.005">
        <li id="R_6.038.005ab">ūcuḥ saṃśravaṇe ye māṃ dvijāḥ kārtāntikāḥ śubhām</li>
        <li id="R_6.038.005cd">te 'dya sarve hate rāme 'jñānino 'nṛtavādinaḥ</li>
    </ul>
    <ul id="R_6.038.006">
        <li id="R_6.038.006ab">imāni khalu padmāni pādayor yaiḥ kila striyaḥ</li>
        <li id="R_6.038.006cd">adhirājye 'bhiṣicyante narendraiḥ patibhiḥ saha</li>
    </ul>
    <ul id="R_6.038.007">
        <li id="R_6.038.007ab">vaidhavyaṃ yānti yair nāryo 'lakṣaṇair bhāgyadurlabhāḥ</li>
        <li id="R_6.038.007cd">nātmanas tāni paśyāmi paśyantī hatalakṣaṇā</li>
    </ul>
    <ul id="R_6.038.008">
        <li id="R_6.038.008ab">satyānīmāni padmāni strīṇām uktvāni lakṣaṇe</li>
        <li id="R_6.038.008cd">tāny adya nihate rāme vitathāni bhavanti me</li>
    </ul>
    <ul id="R_6.038.009">
        <li id="R_6.038.009ab">keśāḥ sūkṣmāḥ samā nīlā bhruvau cāsaṃgate mama</li>
        <li id="R_6.038.009cd">vṛtte cālomaśe jaṅghe dantāś cāviralā mama</li>
    </ul>
    <ul id="R_6.038.010">
        <li id="R_6.038.010ab">śaṅkhe netre karau pādau gulphāv ūrū ca me citau</li>
        <li id="R_6.038.010cd">anuvṛttā nakhāḥ snigdhāḥ samāś cāṅgulayo mama</li>
    </ul>
    <ul id="R_6.038.011">
        <li id="R_6.038.011ab">stanau cāviralau pīnau mamemau magnacūcukau</li>
        <li id="R_6.038.011cd">magnā cotsaṅginī nābhiḥ pārśvoraskaṃ ca me citam</li>
    </ul>
    <ul id="R_6.038.012">
        <li id="R_6.038.012ab">mama varṇo maṇinibho mṛdūny aṅgaruhāṇi ca</li>
        <li id="R_6.038.012cd">pratiṣṭhitāṃ dvadaśabhir mām ūcuḥ śubhalakṣaṇām</li>
    </ul>
    <ul id="R_6.038.013">
        <li id="R_6.038.013ab">samagrayavam acchidraṃ pāṇipādaṃ ca varṇavat</li>
        <li id="R_6.038.013cd">mandasmitety eva ca māṃ kanyālakṣaṇikā viduḥ</li>
    </ul>
    <ul id="R_6.038.014">
        <li id="R_6.038.014ab">adhirājye 'bhiṣeko me brāhmaṇaiḥ patinā saha</li>
        <li id="R_6.038.014cd">kṛtāntakuśalair uktaṃ tat sarvaṃ vitathīkṛtam</li>
    </ul>
    <ul id="R_6.038.015">
        <li id="R_6.038.015ab">śodhayitvā janasthānaṃ pravṛttim upalabhya ca</li>
        <li id="R_6.038.015cd">tīrtvā sāgaram akṣobhyaṃ bhrātarau goṣpade hatau</li>
    </ul>
    <ul id="R_6.038.016">
        <li id="R_6.038.016ab">nanu vāruṇam āgneyam aindraṃ vāyavyam eva ca</li>
        <li id="R_6.038.016cd">astraṃ brahmaśiraś caiva rāghavau pratyapadyatām</li>
    </ul>
    <ul id="R_6.038.017">
        <li id="R_6.038.017ab">adṛśyamānena raṇe māyayā vāsavopamau</li>
        <li id="R_6.038.017cd">mama nāthāv anāthāyā nihatau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.038.018">
        <li id="R_6.038.018ab">na hi dṛṣṭipathaṃ prāpya rāghavasya raṇe ripuḥ</li>
        <li id="R_6.038.018cd">jīvan pratinivarteta yady api syān manojavaḥ</li>
    </ul>
    <ul id="R_6.038.019">
        <li id="R_6.038.019ab">na kālasyātibhāro 'sti kṛtāntaś ca sudurjayaḥ</li>
        <li id="R_6.038.019cd">yatra rāmaḥ saha bhrātrā śete yudhi nipāthitaḥ</li>
    </ul>
    <ul id="R_6.038.020">
        <li id="R_6.038.020ab">nāhaṃ śocāmi bhartāraṃ nihataṃ na ca lakṣmaṇam</li>
        <li id="R_6.038.020cd">nātmānaṃ jananī cāpi yathā śvaśrūṃ tapasvinīm</li>
    </ul>
    <ul id="R_6.038.021">
        <li id="R_6.038.021ab">sā hi cintayate nityaṃ samāptavratam āgatam</li>
        <li id="R_6.038.021cd">kadā drakṣyāmi sītāṃ ca rāmaṃ ca sahalakṣmaṇam</li>
    </ul>
    <ul id="R_6.038.022">
        <li id="R_6.038.022ab">paridevayamānāṃ tāṃ rākṣasī trijaṭābravīt</li>
        <li id="R_6.038.022cd">mā viṣādaṃ kṛthā devi bhartāyaṃ tava jīvati</li>
    </ul>
    <ul id="R_6.038.023">
        <li id="R_6.038.023ab">kāraṇāni ca vakṣyāmi mahānti sadṛśāni ca</li>
        <li id="R_6.038.023cd">yathemau jīvato devi bhrātarau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.038.024">
        <li id="R_6.038.024ab">na hi kopaparītāni harṣaparyutsukāni ca</li>
        <li id="R_6.038.024cd">bhavanti yudhi yodhānāṃ mukhāni nihate patau</li>
    </ul>
    <ul id="R_6.038.025">
        <li id="R_6.038.025ab">idaṃ vimānaṃ vaidehi puṣpakaṃ nāma nāmataḥ</li>
        <li id="R_6.038.025cd">divyaṃ tvāṃ dhārayen nedaṃ yady etau gajajīvitau</li>
    </ul>
    <ul id="R_6.038.026">
        <li id="R_6.038.026ab">hatavīrapradhānā hi hatotsāhā nirudyamā</li>
        <li id="R_6.038.026cd">senā bhramati saṃkhyeṣu hatakarṇeva naur jale</li>
    </ul>
    <ul id="R_6.038.027">
        <li id="R_6.038.027ab">iyaṃ punar asaṃbhrāntā nirudvignā tarasvinī</li>
        <li id="R_6.038.027cd">senā rakṣati kākutsthau māyayā nirjitau raṇe</li>
    </ul>
    <ul id="R_6.038.028">
        <li id="R_6.038.028ab">sā tvaṃ bhava suvisrabdhā anumānaiḥ sukhodayaiḥ</li>
        <li id="R_6.038.028cd">ahatau paśya kākutsthau snehād etad bravīmi te</li>
    </ul>
    <ul id="R_6.038.029">
        <li id="R_6.038.029ab">anṛtaṃ noktapūrvaṃ me na ca vakṣye kadā cana</li>
        <li id="R_6.038.029cd">cāritrasukhaśīlatvāt praviṣṭāsi mano mama</li>
    </ul>
    <ul id="R_6.038.030">
        <li id="R_6.038.030ab">nemau śakyau raṇe jetuṃ sendrair api surāsuraiḥ</li>
        <li id="R_6.038.030cd">etayor ānanaṃ dṛṣṭvā mayā cāveditaṃ tava</li>
    </ul>
    <ul id="R_6.038.031">
        <li id="R_6.038.031ab">idaṃ ca sumahac cihnaṃ śanaiḥ paśyasva maithili</li>
        <li id="R_6.038.031cd">niḥsaṃjñāv apy ubhāv etau naiva lakṣmīr viyujyate</li>
    </ul>
    <ul id="R_6.038.032">
        <li id="R_6.038.032ab">prāyeṇa gatasattvānāṃ puruṣāṇāṃ gatāyuṣām</li>
        <li id="R_6.038.032cd">dṛśyamāneṣu vaktreṣu paraṃ bhavati vaikṛtam</li>
    </ul>
    <ul id="R_6.038.033">
        <li id="R_6.038.033ab">tyaja śokaṃ ca duḥkhaṃ ca mohaṃ ca janakātmaje</li>
        <li id="R_6.038.033cd">rāmalakṣmaṇayor arthe nādya śakyam ajīvitum</li>
    </ul>
    <ul id="R_6.038.034">
        <li id="R_6.038.034ab">śrutvā tu vacanaṃ tasyāḥ sītā surasutopamā</li>
        <li id="R_6.038.034cd">kṛtāñjalir uvācedam evam astv iti maithilī</li>
    </ul>
    <ul id="R_6.038.035">
        <li id="R_6.038.035ab">vimānaṃ puṣpakaṃ tat tu samivartya manojavam</li>
        <li id="R_6.038.035cd">dīnā trijaṭayā sītā laṅkām eva praveśitā</li>
    </ul>
    <ul id="R_6.038.036">
        <li id="R_6.038.036ab">tatas trijaṭayā sārdhaṃ puṣpakād avaruhya sā</li>
        <li id="R_6.038.036cd">aśokavanikām eva rakṣasībhiḥ praveśitā</li>
    </ul>
    <ul id="R_6.038.037">
        <li id="R_6.038.037ab">
            <seg type="pāda" n="a">praviśya sītā bahuvṛkṣaṣaṇḍāṃ</seg>
            <seg type="pāda" n="b">tāṃ rākṣasendrasya vihārabhūmim</seg>
        </li>
        <li id="R_6.038.037cd">
            <seg type="pāda" n="c">saṃprekṣya saṃcintya ca rājaputrau</seg>
            <seg type="pāda" n="d">paraṃ viṣādaṃ samupājagāma</seg>
        </li>
    </ul>
    <ul id="R_6.039.001">
        <li id="R_6.039.001ab">ghoreṇa śarabandhena baddhau daśarathātmajau</li>
        <li id="R_6.039.001cd">niśvasantau yathā nāgau śayānau rudhirokṣitau</li>
    </ul>
    <ul id="R_6.039.002">
        <li id="R_6.039.002ab">sarve te vānaraśreṣṭhāḥ sasugrīvā mahābalāḥ</li>
        <li id="R_6.039.002cd">parivārya mahātmānau tasthuḥ śokapariplutāḥ</li>
    </ul>
    <ul id="R_6.039.003">
        <li id="R_6.039.003ab">etasminn antere rāmaḥ pratyabudhyata vīryavān</li>
        <li id="R_6.039.003cd">sthiratvāt sattvayogāc ca śaraiḥ saṃdānito 'pi san</li>
    </ul>
    <ul id="R_6.039.004">
        <li id="R_6.039.004ab">tato dṛṣṭvā sarudhiraṃ viṣaṇṇaṃ gāḍham arpitam</li>
        <li id="R_6.039.004cd">bhrātaraṃ dīnavadanaṃ paryadevayad āturaḥ</li>
    </ul>
    <ul id="R_6.039.005">
        <li id="R_6.039.005ab">kiṃ nu me sītayā kāryaṃ kiṃ kāryaṃ jīvitena vā</li>
        <li id="R_6.039.005cd">śayānaṃ yo 'dya paśyāmi bhrātaraṃ yudhi nirjitam</li>
    </ul>
    <ul id="R_6.039.006">
        <li id="R_6.039.006ab">śakyā sītā samā nārī prāptuṃ loke vicinvatā</li>
        <li id="R_6.039.006cd">na lakṣmaṇasamo bhrātā sacivaḥ sāmparāyikaḥ</li>
    </ul>
    <ul id="R_6.039.007">
        <li id="R_6.039.007ab">parityakṣyāmy ahaṃ prāṇān vānarāṇāṃ tu paśyatām</li>
        <li id="R_6.039.007cd">yadi pañcatvam āpannaḥ sumitrānandavardhanaḥ</li>
    </ul>
    <ul id="R_6.039.008">
        <li id="R_6.039.008ab">kiṃ nu vakṣyāmi kausalyāṃ mātaraṃ kiṃ nu kaikayīm</li>
        <li id="R_6.039.008cd">katham ambāṃ sumitrāṃca putradarśanalālasām</li>
    </ul>
    <ul id="R_6.039.009">
        <li id="R_6.039.009ab">vivatsāṃ vepamānāṃ ca krośantīṃ kurarīm iva</li>
        <li id="R_6.039.009cd">katham āśvāsayiṣyāmi yadi yāsyāmi taṃ vinā</li>
    </ul>
    <ul id="R_6.039.010">
        <li id="R_6.039.010ab">kathaṃ vakṣyāmi śatrughnaṃ bharataṃ ca yaśasvinam</li>
        <li id="R_6.039.010cd">mayā saha vanaṃ yāto vinā tenāgataḥ punaḥ</li>
    </ul>
    <ul id="R_6.039.011">
        <li id="R_6.039.011ab">upālambhaṃ na śakṣyāmi soḍhuṃ bata sumitrayā</li>
        <li id="R_6.039.011cd">ihaiva dehaṃ tyakṣyāmi na hi jīvitum utsahe</li>
    </ul>
    <ul id="R_6.039.012">
        <li id="R_6.039.012ab">dhiṅ māṃ duṣkṛtakarmāṇam anāryaṃ yatkṛte hy asau</li>
        <li id="R_6.039.012cd">lakṣmaṇaḥ patitaḥ śete śaratalpe gatāsuvat</li>
    </ul>
    <ul id="R_6.039.013">
        <li id="R_6.039.013ab">tvaṃ nityaṃ suviṣaṇṇaṃ mām āśvāsayasi lakṣmaṇa</li>
        <li id="R_6.039.013cd">gatāsur nādya śaknoṣi mām ārtam abhibhāṣitum</li>
    </ul>
    <ul id="R_6.039.014">
        <li id="R_6.039.014ab">yenādya bahavo yuddhe rākṣasā nihatāḥ kṣitau</li>
        <li id="R_6.039.014cd">tasyām eva kṣitau vīraḥ sa śete nihataḥ paraiḥ</li>
    </ul>
    <ul id="R_6.039.015">
        <li id="R_6.039.015ab">śayānaḥ śaratalpe 'smin svaśoṇitapariplutaḥ</li>
        <li id="R_6.039.015cd">śarajālaiś cito bhāti bhāskaro 'stam iva vrajan</li>
    </ul>
    <ul id="R_6.039.016">
        <li id="R_6.039.016ab">bāṇābhihatamarmatvān na śaknoty abhivīkṣitum</li>
        <li id="R_6.039.016cd">rujā cābruvato hy asya dṛṣṭirāgeṇa sūcyate</li>
    </ul>
    <ul id="R_6.039.017">
        <li id="R_6.039.017ab">yathaiva māṃ vanaṃ yāntam anuyāto mahādyutiḥ</li>
        <li id="R_6.039.017cd">aham apy anuyāsyāmi tathaivainaṃ yamakṣayam</li>
    </ul>
    <ul id="R_6.039.018">
        <li id="R_6.039.018ab">iṣṭabandhujano nityaṃ māṃ ca nityam anuvrataḥ</li>
        <li id="R_6.039.018cd">imām adya gato 'vasthāṃ mamānāryasya durnayaiḥ</li>
    </ul>
    <ul id="R_6.039.019">
        <li id="R_6.039.019ab">suruṣṭenāpi vīreṇa lakṣmaṇenā na saṃsmare</li>
        <li id="R_6.039.019cd">paruṣaṃ vipriyaṃ vāpi śrāvitaṃ na kadā cana</li>
    </ul>
    <ul id="R_6.039.020">
        <li id="R_6.039.020ab">visasarjaikavegena pañcabāṇaśatāni yaḥ</li>
        <li id="R_6.039.020cd">iṣvastreṣv adhikas tasmāt kārtavīryāc ca lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.039.021">
        <li id="R_6.039.021ab">astrair astrāṇi yo hanyāc chakrasyāpi mahātmanaḥ</li>
        <li id="R_6.039.021cd">so 'yam urvyāṃhataḥ śete mahārhaśayanocitaḥ</li>
    </ul>
    <ul id="R_6.039.022">
        <li id="R_6.039.022ab">tac ca mithyā pralaptaṃ māṃ pradhakṣyati na saṃśayaḥ</li>
        <li id="R_6.039.022cd">yan mayā na kṛto rājā rākṣasānāṃ vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.039.023">
        <li id="R_6.039.023ab">asmin muhūrte sugrīva pratiyātum ito 'rhasi</li>
        <li id="R_6.039.023cd">matvā hīnaṃ mayā rājan rāvaṇo 'bhidraved balī</li>
    </ul>
    <ul id="R_6.039.024">
        <li id="R_6.039.024ab">aṅgadaṃ tu puraskṛtya sasainyaḥ sasuhṛjjanaḥ</li>
        <li id="R_6.039.024cd">sāgaraṃ tara sugrīva punas tenaiva setunā</li>
    </ul>
    <ul id="R_6.039.025">
        <li id="R_6.039.025ab">kṛtaṃ hanumatā kāryaṃ yad anyair duṣkaraṃ raṇe</li>
        <li id="R_6.039.025cd">ṛkṣarājena tuṣyāmi golāṅgūlādhipena ca</li>
    </ul>
    <ul id="R_6.039.026">
        <li id="R_6.039.026ab">aṅgadena kṛtaṃ karma maindena dvividena ca</li>
        <li id="R_6.039.026cd">yuddhaṃ kesariṇā saṃkhye ghoraṃ saṃpātinā kṛtam</li>
    </ul>
    <ul id="R_6.039.027">
        <li id="R_6.039.027ab">gavayena gavākṣeṇa śarabheṇa gajena ca</li>
        <li id="R_6.039.027cd">anyaiś ca haribhir yuddhaṃ madārthe tyaktajīvitaiḥ</li>
    </ul>
    <ul id="R_6.039.028">
        <li id="R_6.039.028ab">na cātikramituṃ śakyaṃ daivaṃ sugrīva mānuṣaiḥ</li>
        <li id="R_6.039.028cd">yat tu śakyaṃ vayasyena suhṛdā vā paraṃtapa</li>
        <li id="R_6.039.028ef">kṛtaṃ sugrīva tat sarvaṃ bhavatādharmabhīruṇā</li>
    </ul>
    <ul id="R_6.039.029">
        <li id="R_6.039.029ab">mitrakāryaṃ kṛtam idaṃ bhavadbhir vānararṣabhāḥ</li>
        <li id="R_6.039.029cd">anujñātā mayā sarve yatheṣṭaṃ gantum arhatha</li>
    </ul>
    <ul id="R_6.039.030">
        <li id="R_6.039.030ab">śuśruvus tasya te sarve vānarāḥ paridevitam</li>
        <li id="R_6.039.030cd">vartayāṃ cakrur aśrūṇi netraiḥ kṛṣṇetarekṣaṇāḥ</li>
    </ul>
    <ul id="R_6.039.031">
        <li id="R_6.039.031ab">tataḥ sarvāṇy anīkāni sthāpayitvā vibhīṣaṇaḥ</li>
        <li id="R_6.039.031cd">ājagāma gadāpāṇis tvarito yatra rāghavaḥ</li>
    </ul>
    <ul id="R_6.039.032">
        <li id="R_6.039.032ab">taṃ dṛṣṭvā tvaritaṃ yāntaṃ nīlāñjanacayopamam</li>
        <li id="R_6.039.032cd">vānarā dudruvuḥ sarve manyamānās tu rāvaṇim</li>
    </ul>
    <ul id="R_6.040.001">
        <li id="R_6.040.001ab">athovāca mahātejā harirājo mahābalaḥ</li>
        <li id="R_6.040.001cd">kim iyaṃ vyathitā senā mūḍhavāteva naur jale</li>
    </ul>
    <ul id="R_6.040.002">
        <li id="R_6.040.002ab">sugrīvasya vacaḥ śrutvā vāliputro 'ṅgado 'bravīt</li>
        <li id="R_6.040.002cd">na tvaṃ paśyasi rāmaṃ ca lakṣmaṇaṃ ca mahābalam</li>
    </ul>
    <ul id="R_6.040.003">
        <li id="R_6.040.003ab">śarajālācitau vīrāv ubhau daśarathātmajau</li>
        <li id="R_6.040.003cd">śaratalpe mahātmānau śayānau rudhirokṣitau</li>
    </ul>
    <ul id="R_6.040.004">
        <li id="R_6.040.004ab">athābravīd vānarendraḥ sugrīvaḥ putram aṅgadam</li>
        <li id="R_6.040.004cd">nānimittam idaṃ manye bhavitavyaṃ bhayena tu</li>
    </ul>
    <ul id="R_6.040.005">
        <li id="R_6.040.005ab">viṣaṇṇavadanā hy ete tyaktapraharaṇā diśaḥ</li>
        <li id="R_6.040.005cd">prapalāyanti harayas trāsād utphullalocanāḥ</li>
    </ul>
    <ul id="R_6.040.006">
        <li id="R_6.040.006ab">anyonyasya na lajjante na nirīkṣanti pṛṣṭhataḥ</li>
        <li id="R_6.040.006cd">viprakarṣanti cānyonyaṃ patitaṃ laṅghayanti ca</li>
    </ul>
    <ul id="R_6.040.007">
        <li id="R_6.040.007ab">etasminn antare vīro gadāpāṇir vibhīṣaṇaḥ</li>
        <li id="R_6.040.007cd">sugrīvaṃ vardhayām āsa rāghavaṃ ca niraikṣata</li>
    </ul>
    <ul id="R_6.040.008">
        <li id="R_6.040.008ab">vibhīṣaṇaṃ taṃ sugrīvo dṛṣṭvā vānarabhīṣaṇam</li>
        <li id="R_6.040.008cd">ṛkṣarājaṃ samīpasthaṃ jāmbavantam uvāca ha</li>
    </ul>
    <ul id="R_6.040.009">
        <li id="R_6.040.009ab">vibhīṣaṇo 'yaṃ saṃprāpto yaṃ dṛṣṭvā vānararṣabhāḥ</li>
        <li id="R_6.040.009cd">vidravanti paritrastā rāvaṇātmajaśaṅkayā</li>
    </ul>
    <ul id="R_6.040.010">
        <li id="R_6.040.010ab">śīghram etān suvitrastān bahudhā vipradhāvitān</li>
        <li id="R_6.040.010cd">paryavasthāpayākhyāhi vibhīṣaṇam upasthitam</li>
    </ul>
    <ul id="R_6.040.011">
        <li id="R_6.040.011ab">sugrīveṇaivam uktas tu jāmbavān ṛkṣapārthivaḥ</li>
        <li id="R_6.040.011cd">vānarān sāntvayām āsa saṃnivartya prahāvataḥ</li>
    </ul>
    <ul id="R_6.040.012">
        <li id="R_6.040.012ab">te nivṛttāḥ punaḥ sarve vānarās tyaktasaṃbhramāḥ</li>
        <li id="R_6.040.012cd">ṛkṣarājavacaḥ śrutvā taṃ ca dṛṣṭvā vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.040.013">
        <li id="R_6.040.013ab">vibhīṣaṇas tu rāmasya dṛṣṭvā gātraṃ śaraiś citam</li>
        <li id="R_6.040.013cd">lakṣmaṇasya ca dharmātmā babhūva vyathitendriyaḥ</li>
    </ul>
    <ul id="R_6.040.014">
        <li id="R_6.040.014ab">jalaklinnena hastena tayor netre pramṛjya ca</li>
        <li id="R_6.040.014cd">śokasaṃpīḍitamanā ruroda vilalāpa ca</li>
    </ul>
    <ul id="R_6.040.015">
        <li id="R_6.040.015ab">imau tau sattvasaṃpannau vikrāntau priyasaṃyugau</li>
        <li id="R_6.040.015cd">imām avasthāṃ gamitau rākasaiḥ kūṭayodhibhiḥ</li>
    </ul>
    <ul id="R_6.040.016">
        <li id="R_6.040.016ab">bhrātuḥ putreṇa me tena duṣputreṇa durātmanā</li>
        <li id="R_6.040.016cd">rākṣasyā jihmayā buddhyā chalitāv ṛjuvikramau</li>
    </ul>
    <ul id="R_6.040.017">
        <li id="R_6.040.017ab">śarair imāv alaṃ viddhau rudhireṇa samukṣitau</li>
        <li id="R_6.040.017cd">vasudhāyām ima suptau dṛśyete śalyakāv iva</li>
    </ul>
    <ul id="R_6.040.018">
        <li id="R_6.040.018ab">yayor vīryam upāśritya pratiṣṭhā kāṅkṣitā mayā</li>
        <li id="R_6.040.018cd">tāv ubhau dehanāśāya prasuptau puruṣarṣabhau</li>
    </ul>
    <ul id="R_6.040.019">
        <li id="R_6.040.019ab">jīvann adya vipanno 'smi naṣṭarājyamanorathaḥ</li>
        <li id="R_6.040.019cd">prāptapratijñaś ca ripuḥ sakāmo rāvaṇaḥ kṛtaḥ</li>
    </ul>
    <ul id="R_6.040.020">
        <li id="R_6.040.020ab">evaṃ vilapamānaṃ taṃ pariṣvajya vibhīṣaṇam</li>
        <li id="R_6.040.020cd">sugrīvaḥ sattvasaṃpanno harirājo 'bravīd idam</li>
    </ul>
    <ul id="R_6.040.021">
        <li id="R_6.040.021ab">rājyaṃ prāpsyasi dharmajña laṅkāyāṃ nātra saṃśayaḥ</li>
        <li id="R_6.040.021cd">rāvaṇaḥ saha putreṇa sa rājyaṃ neha lapsyate</li>
    </ul>
    <ul id="R_6.040.022">
        <li id="R_6.040.022ab">śarasaṃpīḍitāv etāv ubhau rāghavalakṣmaṇau</li>
        <li id="R_6.040.022cd">tyaktvā mohaṃ vadhiṣyete sagaṇaṃ rāvaṇaṃ raṇe</li>
    </ul>
    <ul id="R_6.040.023">
        <li id="R_6.040.023ab">tam evaṃ sāntvayitvā tu samāśvāsya ca rākṣasaṃ</li>
        <li id="R_6.040.023cd">suṣeṇaṃ śvaśuraṃ pārśve sugrīvas tam uvāca ha</li>
    </ul>
    <ul id="R_6.040.024">
        <li id="R_6.040.024ab">saha śūrair harigaṇair labdhasaṃjñāv ariṃdamau</li>
        <li id="R_6.040.024cd">gaccha tvaṃ bhrātarau gṛhya kiṣkindhāṃ rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.040.025">
        <li id="R_6.040.025ab">ahaṃ tu rāvaṇaṃ hatvā saputraṃ sahabāndhavam</li>
        <li id="R_6.040.025cd">maithilīm ānayiṣyāmi śakro naṣṭām iva śriyam</li>
    </ul>
    <ul id="R_6.040.026">
        <li id="R_6.040.026ab">śrutvaitad vānarendrasya suṣeṇo vākyam abravīt</li>
        <li id="R_6.040.026cd">devāsuraṃ mahāyuddham anubhūtaṃ sudāruṇam</li>
    </ul>
    <ul id="R_6.040.027">
        <li id="R_6.040.027ab">tadā sma dānavā devāñ śarasaṃsparśakovidāḥ</li>
        <li id="R_6.040.027cd">nijaghnuḥ śastraviduṣaś chādayanto muhur muhuḥ</li>
    </ul>
    <ul id="R_6.040.028">
        <li id="R_6.040.028ab">tān ārtān naṣṭasaṃjñāṃś ca parāsūṃś ca bṛhaspatiḥ</li>
        <li id="R_6.040.028cd">vidhyābhir mantrayuktābhir oṣadhībhiś cikitsati</li>
    </ul>
    <ul id="R_6.040.029">
        <li id="R_6.040.029ab">tāny auṣadhāny ānayituṃ kṣīrodaṃ yāntu sāgaram</li>
        <li id="R_6.040.029cd">javena vānarāḥ śīghraṃ saṃpāti panasādayaḥ</li>
    </ul>
    <ul id="R_6.040.030">
        <li id="R_6.040.030ab">harayas tu vijānanti pārvatī te mahauṣadhī</li>
        <li id="R_6.040.030cd">saṃjīvakaraṇīṃ divyāṃ viśalyāṃ devanirmitām</li>
    </ul>
    <ul id="R_6.040.031">
        <li id="R_6.040.031ab">candraś ca nāma droṇaś ca parvatau sāgarottame</li>
        <li id="R_6.040.031cd">amṛtaṃ yatra mathitaṃ tatra te paramauṣadhī</li>
    </ul>
    <ul id="R_6.040.032">
        <li id="R_6.040.032ab">te tatra nihite devaiḥ parvate paramauṣadhī</li>
        <li id="R_6.040.032cd">ayaṃ vāyusuto rājan hanūmāṃs tatra gacchatu</li>
    </ul>
    <ul id="R_6.040.033">
        <li id="R_6.040.033ab">etasminn antare vāyur meghāṃś cāpi savidyutaḥ</li>
        <li id="R_6.040.033cd">paryasyan sāgare toyaṃ kampayann iva parvatān</li>
    </ul>
    <ul id="R_6.040.034">
        <li id="R_6.040.034ab">mahatā pakṣavātena sarve dvīpamahādrumāḥ</li>
        <li id="R_6.040.034cd">nipetur bhagnaviṭapāḥ samūlā lavaṇāmbhasi</li>
    </ul>
    <ul id="R_6.040.035">
        <li id="R_6.040.035ab">abhavan pannagās trastā bhoginas tatravāsinaḥ</li>
        <li id="R_6.040.035cd">śīghraṃ sarvāṇi yādāṃsi jagmuś ca lavaṇārṇavam</li>
    </ul>
    <ul id="R_6.040.036">
        <li id="R_6.040.036ab">tato muhūrtad garuḍaṃ vainateyaṃ mahābalam</li>
        <li id="R_6.040.036cd">vānarā dadṛśuḥ sarve jvalantam iva pāvakam</li>
    </ul>
    <ul id="R_6.040.037">
        <li id="R_6.040.037ab">tam āgatam abhiprekṣya nāgās te vipradudruvuḥ</li>
        <li id="R_6.040.037cd">yais tau satpuruṣau baddhau śarabhūtair mahābalau</li>
    </ul>
    <ul id="R_6.040.038">
        <li id="R_6.040.038ab">tataḥ suparṇaḥ kākutsthau dṛṣṭvā pratyabhinandya ca</li>
        <li id="R_6.040.038cd">vimamarśa ca pāṇibhyāṃ mukhe candrasamaprabhe</li>
    </ul>
    <ul id="R_6.040.039">
        <li id="R_6.040.039ab">vainateyena saṃspṛṣṭās tayoḥ saṃruruhur vraṇāḥ</li>
        <li id="R_6.040.039cd">suvarṇe ca tanū snigdhe tayor āśu babhūvatuḥ</li>
    </ul>
    <ul id="R_6.040.040">
        <li id="R_6.040.040ab">tejo vīryaṃ balaṃ cauja utsāhaś ca mahāguṇāḥ</li>
        <li id="R_6.040.040cd">pradarśanaṃ ca buddhiś ca smṛtiś ca dviguṇaṃ tayoḥ</li>
    </ul>
    <ul id="R_6.040.041">
        <li id="R_6.040.041ab">tāv utthāpya mahāvīryau garuḍo vāsavopamau</li>
        <li id="R_6.040.041cd">ubhau tau sasvaje hṛṣṭau rāmaś cainam uvāca ha</li>
    </ul>
    <ul id="R_6.040.042">
        <li id="R_6.040.042ab">bhavatprasādād vyasanaṃ rāvaṇiprabhavaṃ mahat</li>
        <li id="R_6.040.042cd">āvām iha vyatikrāntau śīghraṃ ca balinau kṛtau</li>
    </ul>
    <ul id="R_6.040.043">
        <li id="R_6.040.043ab">yathā tātaṃ daśarathaṃ yathājaṃ ca pitāmaham</li>
        <li id="R_6.040.043cd">tathā bhavantam āsādya hṛṣayaṃ me prasīdati</li>
    </ul>
    <ul id="R_6.040.044">
        <li id="R_6.040.044ab">ko bhavān rūpasaṃpanno divyasraganulepanaḥ</li>
        <li id="R_6.040.044cd">vasāno viraje vastre divyābharaṇabhūṣitaḥ</li>
    </ul>
    <ul id="R_6.040.045">
        <li id="R_6.040.045ab">tam uvāca mahātejā vainateyo mahābalaḥ</li>
        <li id="R_6.040.045cd">patatrirājaḥ prītātmā harṣaparyākulekṣaṇaḥ</li>
    </ul>
    <ul id="R_6.040.046">
        <li id="R_6.040.046ab">ahaṃ sakhā te kākutstha priyaḥ prāṇo bahiścaraḥ</li>
        <li id="R_6.040.046cd">garutmān iha saṃprāpto yuvayoḥ sāhyakāraṇāt</li>
    </ul>
    <ul id="R_6.040.047">
        <li id="R_6.040.047ab">asurā vā mahāvīryā dānavā vā mahābalāḥ</li>
        <li id="R_6.040.047cd">surāś cāpi sagandharvāḥ puraskṛtya śatakratum</li>
    </ul>
    <ul id="R_6.040.048">
        <li id="R_6.040.048ab">nemaṃ mokṣayituṃ śaktāḥ śarabandhaṃ sudāruṇam</li>
        <li id="R_6.040.048cd">māyā balād indrajitā nirmitaṃ krūrakarmaṇā</li>
    </ul>
    <ul id="R_6.040.049">
        <li id="R_6.040.049ab">ete nāgāḥ kādraveyās tīkṣṇadaṃṣṭrāviṣolbaṇāḥ</li>
        <li id="R_6.040.049cd">rakṣomāyā prabhāvena śarā bhūtvā tvadāśritāḥ</li>
    </ul>
    <ul id="R_6.040.050">
        <li id="R_6.040.050ab">sabhāgyaś cāsi dharmajña rāma satyaparākrama</li>
        <li id="R_6.040.050cd">lakṣmaṇena saha bhrātrā samare ripughātinā</li>
    </ul>
    <ul id="R_6.040.051">
        <li id="R_6.040.051ab">imaṃ śrutvā tu vṛttāntaṃ tvaramāṇo 'ham āgataḥ</li>
        <li id="R_6.040.051cd">sahasā yuvayoḥ snehāt sakhitvam anupālayan</li>
    </ul>
    <ul id="R_6.040.052">
        <li id="R_6.040.052ab">mokṣitau ca mahāghorād asmāt sāyakabandhanāt</li>
        <li id="R_6.040.052cd">apramādaś ca kartavyo yuvābhyāṃ nityam eva hi</li>
    </ul>
    <ul id="R_6.040.053">
        <li id="R_6.040.053ab">prakṛtyā rākṣasāḥ sarve saṃgrāme kūṭayodhinaḥ</li>
        <li id="R_6.040.053cd">śūrāṇāṃ śuddhabhāvānāṃ bhavatām ārjavaṃ balam</li>
    </ul>
    <ul id="R_6.040.054">
        <li id="R_6.040.054ab">tan na viśvasitavyaṃ vo rākṣasānāṃ raṇājire</li>
        <li id="R_6.040.054cd">etenaivopamānena nityajihmā hi rākṣasāḥ</li>
    </ul>
    <ul id="R_6.040.055">
        <li id="R_6.040.055ab">evam uktvā tato rāmaṃ suparṇaḥ sumahābalaḥ</li>
        <li id="R_6.040.055cd">pariṣvajya suhṛtsnigdham āpraṣṭum upacakrame</li>
    </ul>
    <ul id="R_6.040.056">
        <li id="R_6.040.056ab">sakhe rāghava dharmajña ripūṇām api vatsala</li>
        <li id="R_6.040.056cd">abhyanujñātum icchāmi gamiṣyāmi yathāgatam</li>
    </ul>
    <ul id="R_6.040.057">
        <li id="R_6.040.057ab">bālavṛddhāvaśeṣāṃ tu laṅkāṃ kṛtvā śarormibhiḥ</li>
        <li id="R_6.040.057cd">rāvaṇaṃ ca ripuṃ hatvā sītāṃ samupalapsyase</li>
    </ul>
    <ul id="R_6.040.058">
        <li id="R_6.040.058ab">ity evam uktvā vacanaṃ suparṇaḥ śīghravikramaḥ</li>
        <li id="R_6.040.058cd">rāmaṃ ca virujaṃ kṛtvā madhye teṣāṃ vanaukasām</li>
    </ul>
    <ul id="R_6.040.059">
        <li id="R_6.040.059ab">pradakṣiṇaṃ tataḥ kṛtvā pariṣvajya ca vīryavān</li>
        <li id="R_6.040.059cd">jagāmākāśam āviśya suparṇaḥ pavano yathā</li>
    </ul>
    <ul id="R_6.040.060">
        <li id="R_6.040.060ab">virujau rāghavau dṛṣṭvā tato vānarayūthapāḥ</li>
        <li id="R_6.040.060cd">siṃhanādāṃs tadā nedur lāṅgūlaṃ dudhuvuś ca te</li>
    </ul>
    <ul id="R_6.040.061">
        <li id="R_6.040.061ab">tato bherīḥ samājaghnur mṛdaṅgāṃś ca vyanādayan</li>
        <li id="R_6.040.061cd">dadhmuḥ śaṅkhān saṃprahṛṣṭāḥ kṣvelanty api yathāpuram</li>
    </ul>
    <ul id="R_6.040.062">
        <li id="R_6.040.062ab">āsphoṭyāsphoṭya vikrāntā vānarā nagayodhinaḥ</li>
        <li id="R_6.040.062cd">drumān utpāṭya vividhāṃs tasthuḥ śatasahasraśaḥ</li>
    </ul>
    <ul id="R_6.040.063">
        <li id="R_6.040.063ab">visṛjanto mahānādāṃs trāsayanto niśācarān</li>
        <li id="R_6.040.063cd">laṅkādvārāṇy upājagmur yoddhukāmāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.040.064">
        <li id="R_6.040.064ab">
            <seg type="pāda" n="a">tatas tu bhīmas tumulo ninādo</seg>
            <seg type="pāda" n="b">babhūva śākhāmṛgayūthapānām</seg>
        </li>
        <li id="R_6.040.064cd">
            <seg type="pāda" n="c">kṣaye nidāghasya yathā ghanānāṃ</seg>
            <seg type="pāda" n="d">nādaḥ subhīmo nadatāṃ niśīthe</seg>
        </li>
    </ul>
    <ul id="R_6.041.001">
        <li id="R_6.041.001ab">teṣāṃ sutumulaṃ śabdaṃ vānarāṇāṃ tarasvinām</li>
        <li id="R_6.041.001cd">nardatāṃ rākṣasaiḥ sārdhaṃ tadā śuśrāva rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.041.002">
        <li id="R_6.041.002ab">snigdhagambhīranirghoṣaṃ śrutvā sa ninadaṃ bhṛśam</li>
        <li id="R_6.041.002cd">sacivānāṃ tatas teṣāṃ madhye vacanam abravīt</li>
    </ul>
    <ul id="R_6.041.003">
        <li id="R_6.041.003ab">yathāsau saṃprahṛṣṭānāṃ vānarāṇāṃ samutthitaḥ</li>
        <li id="R_6.041.003cd">bahūnāṃ sumahān nādo meghānām iva garjatām</li>
    </ul>
    <ul id="R_6.041.004">
        <li id="R_6.041.004ab">vyaktaṃ sumahatī prītir eteṣāṃ nātra saṃśayaḥ</li>
        <li id="R_6.041.004cd">tathā hi vipulair nādaiś cukṣubhe varuṇālayaḥ</li>
    </ul>
    <ul id="R_6.041.005">
        <li id="R_6.041.005ab">tau tu baddhau śarais tīṣkṇair bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.041.005cd">ayaṃ ca sumahān nādaḥ śaṅkāṃ janayatīva me</li>
    </ul>
    <ul id="R_6.041.006">
        <li id="R_6.041.006ab">etat tu vacanaṃ coktvā mantriṇo rākṣaseśvaraḥ</li>
        <li id="R_6.041.006cd">uvāca nairṛtāṃs tatra samīpaparivartinaḥ</li>
    </ul>
    <ul id="R_6.041.007">
        <li id="R_6.041.007ab">jñāyatāṃ tūrṇam etaṣāṃ sarveṣāṃ vanacāriṇām</li>
        <li id="R_6.041.007cd">śokakāle samutpanne harṣakāraṇam utthitam</li>
    </ul>
    <ul id="R_6.041.008">
        <li id="R_6.041.008ab">tathoktās tena saṃbhrāntāḥ prākāram adhiruhya te</li>
        <li id="R_6.041.008cd">dadṛśuḥ pālitāṃ senāṃ sugrīveṇa mahātmanā</li>
    </ul>
    <ul id="R_6.041.009">
        <li id="R_6.041.009ab">tau ca muktau sughoreṇa śarabandhena rāghavau</li>
        <li id="R_6.041.009cd">samutthitau mahābhāgau viṣeduḥ prekṣya rākṣasāḥ</li>
    </ul>
    <ul id="R_6.041.010">
        <li id="R_6.041.010ab">saṃtrastahṛdayā sarve prākārād avaruhya te</li>
        <li id="R_6.041.010cd">viṣaṇṇavadanāḥ sarve rākṣasendram upasthitāḥ</li>
    </ul>
    <ul id="R_6.041.011">
        <li id="R_6.041.011ab">tad apriyaṃ dīnamukhā rāvaṇasya niśācarāḥ</li>
        <li id="R_6.041.011cd">kṛtsnaṃ nivedayām āsur yathāvad vākyakovidāḥ</li>
    </ul>
    <ul id="R_6.041.012">
        <li id="R_6.041.012ab">yau tāv indrajitā yuddhe bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.041.012cd">nibaddhau śarabandhena niṣprakampabhujau kṛtau</li>
    </ul>
    <ul id="R_6.041.013">
        <li id="R_6.041.013ab">vimuktau śarabandhena tau dṛśyete raṇājire</li>
        <li id="R_6.041.013cd">pāśān iva gajau chittvā gajendrasamavikramau</li>
    </ul>
    <ul id="R_6.041.014">
        <li id="R_6.041.014ab">tac chrutvā vacanaṃ teṣāṃ rākṣasendro mahābalaḥ</li>
        <li id="R_6.041.014cd">cintāśokasamākrānto viṣaṇṇavadano 'bravīt</li>
    </ul>
    <ul id="R_6.041.015">
        <li id="R_6.041.015ab">ghorair dattavarair baddhau śarair āśīviṣomapaiḥ</li>
        <li id="R_6.041.015cd">amoghaiḥ sūryasaṃkāśaiḥ pramathyendrajitā yudhi</li>
    </ul>
    <ul id="R_6.041.016">
        <li id="R_6.041.016ab">tam astrabandham āsādya yadi muktau ripū mama</li>
        <li id="R_6.041.016cd">saṃśayastham idaṃ sarvam anupaśyāmy ahaṃ balam</li>
    </ul>
    <ul id="R_6.041.017">
        <li id="R_6.041.017ab">niṣphalāḥ khalu saṃvṛttāḥ śarā vāsukitejasaḥ</li>
        <li id="R_6.041.017cd">ādattaṃ yais tu saṃgrāme ripūṇāṃ mama jīvitam</li>
    </ul>
    <ul id="R_6.041.018">
        <li id="R_6.041.018ab">evam uktvā tu saṃkruddho niśvasann urago yathā</li>
        <li id="R_6.041.018cd">abravīd rakṣasāṃ madhye dhūmrākṣaṃ nāma rākasaṃ</li>
    </ul>
    <ul id="R_6.041.019">
        <li id="R_6.041.019ab">balena mahatā yukto rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_6.041.019cd">tvaṃ vadhāyābhiniryāhi rāmasya saha vānaraiḥ</li>
    </ul>
    <ul id="R_6.041.020">
        <li id="R_6.041.020ab">evam uktas tu dhūmrākṣo rākṣasendreṇa dhīmatā</li>
        <li id="R_6.041.020cd">kṛtvā praṇāmaṃ saṃhṛṣṭo nirjagāma nṛpālayāt</li>
    </ul>
    <ul id="R_6.041.021">
        <li id="R_6.041.021ab">abhiniṣkramya taddvāraṃ balādhyakṣam uvāca ha</li>
        <li id="R_6.041.021cd">tvarayasva balaṃ tūrṇaṃ kiṃ cireṇa yuyutsataḥ</li>
    </ul>
    <ul id="R_6.041.022">
        <li id="R_6.041.022ab">dhūmrākṣasya vacaḥ śrutvā balādhyakṣo balānugaḥ</li>
        <li id="R_6.041.022cd">balam udyojayām āsa rāvaṇasyājñayā drutam</li>
    </ul>
    <ul id="R_6.041.023">
        <li id="R_6.041.023ab">te baddhaghaṇṭā balino ghorarūpā niśācarāḥ</li>
        <li id="R_6.041.023cd">vinardamānāḥ saṃhṛṣṭā dhūmrākṣaṃ paryavārayan</li>
    </ul>
    <ul id="R_6.041.024">
        <li id="R_6.041.024ab">vividhāyudhahastāś ca śūlamudgarapāṇayaḥ</li>
        <li id="R_6.041.024cd">gadābhiḥ paṭṭasair daṇḍair āyasair musalair bhṛśam</li>
    </ul>
    <ul id="R_6.041.025">
        <li id="R_6.041.025ab">parighair bhiṇḍipālaiś ca bhallaiḥ prāsaiḥ paraśvadhaiḥ</li>
        <li id="R_6.041.025cd">niryayū rākṣasā ghorā nardanto jaladā yathā</li>
    </ul>
    <ul id="R_6.041.026">
        <li id="R_6.041.026ab">rathaiḥ kavacinas tv anye dhvajaiś ca samalaṃkṛtaiḥ</li>
        <li id="R_6.041.026cd">suvarṇajālavihitaiḥ kharaiś ca vividhānanaiḥ</li>
    </ul>
    <ul id="R_6.041.027">
        <li id="R_6.041.027ab">hayaiḥ paramaśīghraiś ca gajendraiś ca madotkaṭaiḥ</li>
        <li id="R_6.041.027cd">niryayū rākṣasavyāghrā vyāghrā iva durāsadāḥ</li>
    </ul>
    <ul id="R_6.041.028">
        <li id="R_6.041.028ab">vṛkasiṃhamukhair yuktaṃ kharaiḥ kanakabhūṣaṇaiḥ</li>
        <li id="R_6.041.028cd">āruroha rathaṃ divyaṃ dhūmrākṣaḥ kharanisvanaḥ</li>
    </ul>
    <ul id="R_6.041.029">
        <li id="R_6.041.029ab">sa niryāto mahāvīryo dhūmrākṣo rākṣasair vṛtaḥ</li>
        <li id="R_6.041.029cd">prahasan paścimadvāraṃ hanūmān yatra yūthapaḥ</li>
    </ul>
    <ul id="R_6.041.030">
        <li id="R_6.041.030ab">prayāntaṃ tu mahāghoraṃ rākṣasaṃ bhīmadarśanam</li>
        <li id="R_6.041.030cd">antarikṣagatāḥ krūrāḥ śakunāḥ pratyavārayan</li>
    </ul>
    <ul id="R_6.041.031">
        <li id="R_6.041.031ab">rathaśīrṣe mahābhīmo gṛdhraś ca nipapāta ha</li>
        <li id="R_6.041.031cd">dhvajāgre grathitāś caiva nipetuḥ kuṇapāśanāḥ</li>
    </ul>
    <ul id="R_6.041.032">
        <li id="R_6.041.032ab">rudhirārdro mahāñ śvetaḥ kabandhaḥ patito bhuvi</li>
        <li id="R_6.041.032cd">visvaraṃ cotsṛjan nādaṃ dhūmrākṣasya samīpataḥ</li>
    </ul>
    <ul id="R_6.041.033">
        <li id="R_6.041.033ab">vavarṣa rudhiraṃ devaḥ saṃcacāla ca medinī</li>
        <li id="R_6.041.033cd">pratilomaṃ vavau vāyur nirghātasamanisvanaḥ</li>
        <li id="R_6.041.033ef">timiraughāvṛtās tatra diśaś ca na cakāśire</li>
    </ul>
    <ul id="R_6.041.034">
        <li id="R_6.041.034ab">sa tūtpātāṃs tato dṛṣṭvā rākṣasānāṃ bhayāvahān</li>
        <li id="R_6.041.034cd">prādurbhūtān sughorāṃś ca dhūmrākṣo vyathito 'bhavat</li>
    </ul>
    <ul id="R_6.041.035">
        <li id="R_6.041.035ab">
            <seg type="pāda" n="a">tataḥ subhīmo bahubhir niśācarair</seg>
            <seg type="pāda" n="b">vṛto 'bhiniṣkramya raṇotsuko balī</seg>
        </li>
        <li id="R_6.041.035cd">
            <seg type="pāda" n="c">dadarśa tāṃ rāghavabāhupālitāṃ</seg>
            <seg type="pāda" n="d">samudrakalpāṃ bahuvānarīṃ camūm</seg>
        </li>
    </ul>
    <ul id="R_6.042.001">
        <li id="R_6.042.001ab">dhūmrākṣaṃ prekṣya niryāntaṃ rākṣasaṃ bhīmanisvanam</li>
        <li id="R_6.042.001cd">vinedur vānarāḥ sarve prahṛṣṭā yuddhakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.042.002">
        <li id="R_6.042.002ab">teṣāṃ tu tumulaṃ yuddhaṃ saṃjajñe harirakṣasām</li>
        <li id="R_6.042.002cd">anyonyaṃ pādapair ghorair nighnataṃ śūlamudgaraiḥ</li>
    </ul>
    <ul id="R_6.042.003">
        <li id="R_6.042.003ab">rākṣasair vānarā ghorā vinikṛttāḥ samantataḥ</li>
        <li id="R_6.042.003cd">vānarai rākṣasāś cāpi drumair bhūmau samīkṛtāḥ</li>
    </ul>
    <ul id="R_6.042.004">
        <li id="R_6.042.004ab">rākṣasāś cāpi saṃkruddhā vānarān niśitaiḥ śaraiḥ</li>
        <li id="R_6.042.004cd">vivyadhur ghorasaṃkāśaiḥ kaṅkapatrair ajihmagaiḥ</li>
    </ul>
    <ul id="R_6.042.005">
        <li id="R_6.042.005ab">te gadābhiś ca bhīmābhiḥ paṭṭasaiḥ kūṭamudgaraiḥ</li>
        <li id="R_6.042.005cd">ghoraiś ca parighaiś citrais triśūlaiś cāpi saṃśitaiḥ</li>
    </ul>
    <ul id="R_6.042.006">
        <li id="R_6.042.006ab">vidāryamāṇā rakṣobhir vānarās te mahābalāḥ</li>
        <li id="R_6.042.006cd">amarṣāj janitoddharṣāś cakruḥ karmāṇy abhītavat</li>
    </ul>
    <ul id="R_6.042.007">
        <li id="R_6.042.007ab">śaranirbhinnagātrās te śūlanirbhinnadehinaḥ</li>
        <li id="R_6.042.007cd">jagṛhus te drumāṃs tatra śilāś ca hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.042.008">
        <li id="R_6.042.008ab">te bhīmavegā harayo nardamānās tatas tataḥ</li>
        <li id="R_6.042.008cd">mamanthū rākṣasān bhīmān nāmāni ca babhāṣire</li>
    </ul>
    <ul id="R_6.042.009">
        <li id="R_6.042.009ab">tad babhūvādbhutaṃ ghoraṃ yuddhaṃ vānararakṣasām</li>
        <li id="R_6.042.009cd">śilābhir vividhābhiś ca bahuśākhaiś ca pādapaiḥ</li>
    </ul>
    <ul id="R_6.042.010">
        <li id="R_6.042.010ab">rākṣasā mathitāḥ ke cid vānarair jitakāśibhiḥ</li>
        <li id="R_6.042.010cd">vavarṣū rudhiraṃ ke cin mukhai rudhirabhojanāḥ</li>
    </ul>
    <ul id="R_6.042.011">
        <li id="R_6.042.011ab">pārśveṣu dāritāḥ ke cit ke cid rāśīkṛtā drumaiḥ</li>
        <li id="R_6.042.011cd">śilābhiś cūrṇitāḥ ke cit ke cid dantair vidāritāḥ</li>
    </ul>
    <ul id="R_6.042.012">
        <li id="R_6.042.012ab">dhvajair vimathitair bhagnaiḥ kharaiś ca vinipātitaiḥ</li>
        <li id="R_6.042.012cd">rathair vidhvaṃsitaiś cāpi patitai rajanīcaraiḥ</li>
    </ul>
    <ul id="R_6.042.013">
        <li id="R_6.042.013ab">vānarair bhīmavikrāntair āplutyāplutya vegitaiḥ</li>
        <li id="R_6.042.013cd">rākṣasāḥ karajais tīkṣṇair mukheṣu vinikartitāḥ</li>
    </ul>
    <ul id="R_6.042.014">
        <li id="R_6.042.014ab">vivarṇavadanā bhūyo viprakīrṇaśiroruhāḥ</li>
        <li id="R_6.042.014cd">mūḍhāḥ śoṇitagandhena nipetur dharaṇītale</li>
    </ul>
    <ul id="R_6.042.015">
        <li id="R_6.042.015ab">naye tu paramakruddhā rākṣasā bhīmavikramāḥ</li>
        <li id="R_6.042.015cd">talair evābhidhāvanti vajrasparśasamair harīn</li>
    </ul>
    <ul id="R_6.042.016">
        <li id="R_6.042.016ab">vanarair āpatantas te vegitā vegavattaraiḥ</li>
        <li id="R_6.042.016cd">muṣṭibhiś caraṇair dantaiḥ pādapaiś cāpapothitāḥ</li>
    </ul>
    <ul id="R_6.042.017">
        <li id="R_6.042.017ab">sanyaṃ tu vidrutaṃ dṛṣṭvā dhūmrākṣo rākṣasarṣabhaḥ</li>
        <li id="R_6.042.017cd">krodhena kadanaṃ cakre vānarāṇāṃ yuyutsatām</li>
    </ul>
    <ul id="R_6.042.018">
        <li id="R_6.042.018ab">prāsaiḥ pramathitāḥ ke cid vānarāḥ śoṇitasravāḥ</li>
        <li id="R_6.042.018cd">mudgarair āhatāḥ ke cit patitā dharaṇītale</li>
    </ul>
    <ul id="R_6.042.019">
        <li id="R_6.042.019ab">parighair mathitaḥ ke cid bhiṇḍipālair vidāritāḥ</li>
        <li id="R_6.042.019cd">paṭṭasair āhatāḥ ke cid vihvalanto gatāsavaḥ</li>
    </ul>
    <ul id="R_6.042.020">
        <li id="R_6.042.020ab">ke cid vinihatā bhūmau rudhirārdrā vanaukasaḥ</li>
        <li id="R_6.042.020cd">ke cid vidrāvitā naṣṭāḥ saṃkruddhai rākṣasair yudhi</li>
    </ul>
    <ul id="R_6.042.021">
        <li id="R_6.042.021ab">vibhinnahṛdayāḥ ke cid ekapārśvena śāyitāḥ</li>
        <li id="R_6.042.021cd">vidāritāstraśūlai ca ke cid āntrair vinisrutāḥ</li>
    </ul>
    <ul id="R_6.042.022">
        <li id="R_6.042.022ab">tat subhīmaṃ mahad yuddhaṃ harirākasa saṃkulam</li>
        <li id="R_6.042.022cd">prababhau śastrabahulaṃ śilāpādapasaṃkulam</li>
    </ul>
    <ul id="R_6.042.023">
        <li id="R_6.042.023ab">dhanurjyātantrimadhuraṃ hikkātālasamanvitam</li>
        <li id="R_6.042.023cd">mandrastanitasaṃgītaṃ yuddhagāndharvam ābabhau</li>
    </ul>
    <ul id="R_6.042.024">
        <li id="R_6.042.024ab">dhūmrākṣas tu dhanuṣpāṇir vānarān raṇamūrdhani</li>
        <li id="R_6.042.024cd">hasan vidrāvayām āsa diśas tāñ śaravṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_6.042.025">
        <li id="R_6.042.025ab">dhūmrākṣeṇārditaṃ sainyaṃ vyathitaṃ dṛśya mārutiḥ</li>
        <li id="R_6.042.025cd">abhyavartata saṃkruddhaḥ pragṛhya vipulāṃ śilām</li>
    </ul>
    <ul id="R_6.042.026">
        <li id="R_6.042.026ab">krodhād dviguṇatāmrākṣaḥ pitṛtulyaparākramaḥ</li>
        <li id="R_6.042.026cd">śilāṃ tāṃ pātayām āsa dhūmrākṣasya rathaṃ prati</li>
    </ul>
    <ul id="R_6.042.027">
        <li id="R_6.042.027ab">āpatantīṃ śilāṃ dṛṣṭvā gadām udyamya saṃbhramāt</li>
        <li id="R_6.042.027cd">rathād āplutya vegena vasudhāyāṃ vyatiṣṭhata</li>
    </ul>
    <ul id="R_6.042.028">
        <li id="R_6.042.028ab">sā pramathya rathaṃ tasya nipapāta śilābhuvi</li>
        <li id="R_6.042.028cd">sacakrakūbaraṃ sāśvaṃ sadhvajaṃ saśarāsanam</li>
    </ul>
    <ul id="R_6.042.029">
        <li id="R_6.042.029ab">sa bhaṅktvā tu rathaṃ tasya hanūmān mārutātmajaḥ</li>
        <li id="R_6.042.029cd">rakṣasāṃ kadanaṃ cakre saskandhaviṭapair drumaiḥ</li>
    </ul>
    <ul id="R_6.042.030">
        <li id="R_6.042.030ab">vibhinnaśiraso bhūtvā rākṣasāḥ śoṇitokṣitāḥ</li>
        <li id="R_6.042.030cd">drumaiḥ pramathitāś cānye nipetur dharaṇītale</li>
    </ul>
    <ul id="R_6.042.031">
        <li id="R_6.042.031ab">vidrāvya rākṣasaṃ sainyaṃ hanūmān mārutātmajaḥ</li>
        <li id="R_6.042.031cd">gireḥ śikharam ādāya dhūmrākṣam abhidudruve</li>
    </ul>
    <ul id="R_6.042.032">
        <li id="R_6.042.032ab">tam āpatantaṃ dhūmrākṣo gadām udyamya vīryavān</li>
        <li id="R_6.042.032cd">vinardamānaḥ sahasā hanūmantam abhidravat</li>
    </ul>
    <ul id="R_6.042.033">
        <li id="R_6.042.033ab">tataḥ kruddhas tu vegena gadāṃ tāṃ bahukaṇṭakām</li>
        <li id="R_6.042.033cd">pātayām āsa dhūmrākṣo mastake tu hanūmataḥ</li>
    </ul>
    <ul id="R_6.042.034">
        <li id="R_6.042.034ab">tāḍitaḥ sa tayā tatra gadayā bhīmarūpayā</li>
        <li id="R_6.042.034cd">sa kapir mārutabalas taṃ prahāram acintayan</li>
        <li id="R_6.042.034ef">dhūmrākṣasya śiro madhye giriśṛṅgam apātayat</li>
    </ul>
    <ul id="R_6.042.035">
        <li id="R_6.042.035ab">sa vihvalitasarvāṅgo giriśṛṅgeṇa tāḍitaḥ</li>
        <li id="R_6.042.035cd">papāta sahasā bhūmau vikīrṇa iva parvataḥ</li>
    </ul>
    <ul id="R_6.042.036">
        <li id="R_6.042.036ab">dhūmrākṣaṃ nihataṃ dṛṣṭvā hataśeṣā niśācarāḥ</li>
        <li id="R_6.042.036cd">trastāḥ praviviśur laṅkāṃ vadhyamānāḥ plavaṃgamaiḥ</li>
    </ul>
    <ul id="R_6.042.037">
        <li id="R_6.042.037ab">
            <seg type="pāda" n="a">sa tu pavanasuto nihatya śatruṃ</seg>
            <seg type="pāda" n="b">kṣatajavahāḥ saritaś ca saṃvikīrya</seg>
        </li>
        <li id="R_6.042.037cd">
            <seg type="pāda" n="c">ripuvadhajanitaśramo mahātmā</seg>
            <seg type="pāda" n="d">mudam agamat kapibhiś ca pūjyamānaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.043.001">
        <li id="R_6.043.001ab">dhūmrākṣaṃ nihataṃ śrutvā rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.043.001cd">balādhyakṣam uvācedaṃ kṛtāñjalim upasthitam</li>
    </ul>
    <ul id="R_6.043.002">
        <li id="R_6.043.002ab">śīghraṃ niryāntu durdharṣā rākṣasā bhīmavikramāḥ</li>
        <li id="R_6.043.002cd">akampanaṃ puraskṛtya sarvaśastraprakovidam</li>
    </ul>
    <ul id="R_6.043.003">
        <li id="R_6.043.003ab">tato nānāpraharaṇā bhīmākṣā bhīmadarśanāḥ</li>
        <li id="R_6.043.003cd">niṣpetū rākṣasā mukhyā balādhyakṣapracoditāḥ</li>
    </ul>
    <ul id="R_6.043.004">
        <li id="R_6.043.004ab">ratham āsthāya vipulaṃ taptakāñcanakuṇḍalaḥ</li>
        <li id="R_6.043.004cd">rākasaiḥ saṃvṛto ghorais tadā niryāty akampanaḥ</li>
    </ul>
    <ul id="R_6.043.005">
        <li id="R_6.043.005ab">na hi kampayituṃ śakyaḥ surair api mahāmṛdhe</li>
        <li id="R_6.043.005cd">akampanas tatas teṣām āditya iva tejasā</li>
    </ul>
    <ul id="R_6.043.006">
        <li id="R_6.043.006ab">tasya nidhāvamānasya saṃrabdhasya yuyutsayā</li>
        <li id="R_6.043.006cd">akasmād dainyam āgacchad dhayānāṃ rathavāhinām</li>
    </ul>
    <ul id="R_6.043.007">
        <li id="R_6.043.007ab">vyasphuran nayanaṃ cāsya savyaṃ yuddhābhinandinaḥ</li>
        <li id="R_6.043.007cd">vivarṇo mukhavarṇaś ca gadgadaś cābhavat svaraḥ</li>
    </ul>
    <ul id="R_6.043.008">
        <li id="R_6.043.008ab">abhavat sudine cāpi durdine rūkṣamārutam</li>
        <li id="R_6.043.008cd">ūcuḥ khagā mṛgāḥ sarve vācaḥ krūrā bhayāvahāḥ</li>
    </ul>
    <ul id="R_6.043.009">
        <li id="R_6.043.009ab">sa siṃhopacitaskandhaḥ śārdūlasamavikramaḥ</li>
        <li id="R_6.043.009cd">tān utpātān acintyaiva nirjagāma raṇājiram</li>
    </ul>
    <ul id="R_6.043.010">
        <li id="R_6.043.010ab">tadā nirgacchatas tasya rakṣasaḥ saha rākṣasaiḥ</li>
        <li id="R_6.043.010cd">babhūva sumahān nādaḥ kṣobhayann iva sāgaram</li>
    </ul>
    <ul id="R_6.043.011">
        <li id="R_6.043.011ab">tena śabdena vitrastā vānarāṇāṃ mahācamūḥ</li>
        <li id="R_6.043.011cd">drumaśailapraharaṇā yoddhuṃ samavatiṣṭhata</li>
    </ul>
    <ul id="R_6.043.012">
        <li id="R_6.043.012ab">teṣāṃ yuddhaṃ mahāraudraṃ saṃjajñe kapirakṣasām</li>
        <li id="R_6.043.012cd">rāmarāvaṇayor arthe samabhityaktajīvinām</li>
    </ul>
    <ul id="R_6.043.013">
        <li id="R_6.043.013ab">sarve hy atibalāḥ śūrāḥ sarve parvatasaṃnibhāḥ</li>
        <li id="R_6.043.013cd">harayo rākṣasāś caiva parasparajighaṃsavaḥ</li>
    </ul>
    <ul id="R_6.043.014">
        <li id="R_6.043.014ab">teṣāṃ vinardātāṃ śabdaḥ saṃyuge 'titarasvinām</li>
        <li id="R_6.043.014cd">śuśruve sumahān krodhād anyonyam abhigarjatām</li>
    </ul>
    <ul id="R_6.043.015">
        <li id="R_6.043.015ab">rajaś cāruṇavarṇābhaṃ subhīmam abhavad bhṛśam</li>
        <li id="R_6.043.015cd">uddhūtaṃ harirakṣobhiḥ saṃrurodha diśo daśa</li>
    </ul>
    <ul id="R_6.043.016">
        <li id="R_6.043.016ab">anyonyaṃ rajasā tena kauśeyoddhūtapāṇḍunā</li>
        <li id="R_6.043.016cd">saṃvṛtāni ca bhūtāni dadṛśur na raṇājire</li>
    </ul>
    <ul id="R_6.043.017">
        <li id="R_6.043.017ab">na dhvajo na patākāvā varma vā turago 'pi vā</li>
        <li id="R_6.043.017cd">āyudhaṃ syandanaṃ vāpi dadṛśe tena reṇunā</li>
    </ul>
    <ul id="R_6.043.018">
        <li id="R_6.043.018ab">śabdaś ca sumahāṃs teṣāṃ nardatām abhidhāvatām</li>
        <li id="R_6.043.018cd">śrūyate tumule yuddhe na rūpāṇi cakāśire</li>
    </ul>
    <ul id="R_6.043.019">
        <li id="R_6.043.019ab">harīn eva susaṃkruddhā harayo jaghnur āhave</li>
        <li id="R_6.043.019cd">rākṣasāś cāpi rakṣāṃsi nijaghnus timire tadā</li>
    </ul>
    <ul id="R_6.043.020">
        <li id="R_6.043.020ab">parāṃś caiva vinighnantaḥ svāṃś ca vānararākṣasāḥ</li>
        <li id="R_6.043.020cd">rudhirārdraṃ tadā cakrur mahīṃ paṅkānulepanām</li>
    </ul>
    <ul id="R_6.043.021">
        <li id="R_6.043.021ab">tatas tu rudhiraugheṇa siktaṃ vyapagataṃ rajaḥ</li>
        <li id="R_6.043.021cd">śarīraśavasaṃkīrṇā babhūva ca vasuṃdharā</li>
    </ul>
    <ul id="R_6.043.022">
        <li id="R_6.043.022ab">drumaśaktiśilāprāsair gadāparighatomaraiḥ</li>
        <li id="R_6.043.022cd">harayo rākṣasās tūrṇaṃ jaghnur anyonyam ojasā</li>
    </ul>
    <ul id="R_6.043.023">
        <li id="R_6.043.023ab">bāhubhiḥ parighākārair yudhyantaḥ parvatopamāḥ</li>
        <li id="R_6.043.023cd">harayo bhīmakarmāṇo rākṣasāñ jaghnur āhave</li>
    </ul>
    <ul id="R_6.043.024">
        <li id="R_6.043.024ab">rākṣasāś cāpi saṃkruddhāḥ prāsatomarapāṇayaḥ</li>
        <li id="R_6.043.024cd">kapīn nijaghnire tatra śastraiḥ paramadāruṇaiḥ</li>
    </ul>
    <ul id="R_6.043.025">
        <li id="R_6.043.025ab">harayas tv api rakṣāṃsi mahādrumamahāśmabhiḥ</li>
        <li id="R_6.043.025cd">vidārayanty abhikramya śastrāṇy ācchidya vīryataḥ</li>
    </ul>
    <ul id="R_6.043.026">
        <li id="R_6.043.026ab">etasminn antare vīrā harayaḥ kumudo nalaḥ</li>
        <li id="R_6.043.026cd">maindaś ca paramakruddhaś cakrur vegam anuttamam</li>
    </ul>
    <ul id="R_6.043.027">
        <li id="R_6.043.027ab">te tu vṛkṣair mahāvegā rākṣasānāṃ camūmukhe</li>
        <li id="R_6.043.027cd">kadanaṃ sumaha cakrur līlayā hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.044.001">
        <li id="R_6.044.001ab">tad dṛṣṭvā sumahat karma kṛtaṃ vānarasattamaiḥ</li>
        <li id="R_6.044.001cd">krodham āhārayām āsa yudhi tīvram akampanaḥ</li>
    </ul>
    <ul id="R_6.044.002">
        <li id="R_6.044.002ab">krodhamūrchitarūpas tu dhnuvan paramakārmukam</li>
        <li id="R_6.044.002cd">dṛṣṭvā tu karma śatrūṇāṃ sārathiṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.044.003">
        <li id="R_6.044.003ab">tatraiva tāvat tvaritaṃ rathaṃ prāpaya sārathe</li>
        <li id="R_6.044.003cd">ete 'tra bahavo ghnanti subahūn rākṣasān raṇe</li>
    </ul>
    <ul id="R_6.044.004">
        <li id="R_6.044.004ab">ete 'tra balavanto hi bhīmakāyāś ca vānarāḥ</li>
        <li id="R_6.044.004cd">drumaśailapraharaṇās tiṣṭhanti pramukhe mama</li>
    </ul>
    <ul id="R_6.044.005">
        <li id="R_6.044.005ab">etān nihantum icchāmi samaraślāghino hy aham</li>
        <li id="R_6.044.005cd">etaiḥ pramathitaṃ sarvaṃ dṛśyate rākṣasaṃ balam</li>
    </ul>
    <ul id="R_6.044.006">
        <li id="R_6.044.006ab">tataḥ prajavitāśvena rathena rathināṃ varaḥ</li>
        <li id="R_6.044.006cd">harīn abhyahanat krodhāc charajālair akampanaḥ</li>
    </ul>
    <ul id="R_6.044.007">
        <li id="R_6.044.007ab">na sthātuṃ vānarāḥ śekuḥ kiṃ punar yoddhum āhave</li>
        <li id="R_6.044.007cd">akampanaśarair bhagnāḥ sarva eva pradudruvuḥ</li>
    </ul>
    <ul id="R_6.044.008">
        <li id="R_6.044.008ab">tān mṛtyuvaśam āpannān akampanavaśaṃ gatān</li>
        <li id="R_6.044.008cd">samīkṣya hanumāñ jñātīn upatasthe mahābalaḥ</li>
    </ul>
    <ul id="R_6.044.009">
        <li id="R_6.044.009ab">taṃ mahāplavagaṃ dṛṣṭvā sarve plavagayūthapāḥ</li>
        <li id="R_6.044.009cd">sametya samare vīrāḥ sahitāḥ paryavārayan</li>
    </ul>
    <ul id="R_6.044.010">
        <li id="R_6.044.010ab">vyavasthitaṃ hanūmantaṃ te dṛṣṭvā hariyūthapāḥ</li>
        <li id="R_6.044.010cd">babhūvur balavanto hi balavantam upāśritāḥ</li>
    </ul>
    <ul id="R_6.044.011">
        <li id="R_6.044.011ab">akampanas tu śailābhaṃ hanūmantam avasthitam</li>
        <li id="R_6.044.011cd">mahendra iva dhārābhiḥ śarair abhivavarṣa ha</li>
    </ul>
    <ul id="R_6.044.012">
        <li id="R_6.044.012ab">acintayitvā bāṇaughāñ śarīre patitāñ śitān</li>
        <li id="R_6.044.012cd">akampanavadhārthāya mano dadhre mahābalaḥ</li>
    </ul>
    <ul id="R_6.044.013">
        <li id="R_6.044.013ab">sa prahasya mahātejā hanūmān mārutātmajaḥ</li>
        <li id="R_6.044.013cd">abhidudrāva tad rakṣaḥ kampayann iva medinīm</li>
    </ul>
    <ul id="R_6.044.014">
        <li id="R_6.044.014ab">tasyābhinardamānasya dīpyamānasya tejasā</li>
        <li id="R_6.044.014cd">babhūva rūpaṃ durdharṣaṃ dīptasyeva vibhāvasoḥ</li>
    </ul>
    <ul id="R_6.044.015">
        <li id="R_6.044.015ab">ātmānaṃ tv apraharaṇaṃ jñātvā krodhasamanvitaḥ</li>
        <li id="R_6.044.015cd">śailam utpāṭayām āsa vegena haripuṃgavaḥ</li>
    </ul>
    <ul id="R_6.044.016">
        <li id="R_6.044.016ab">taṃ gṛhītvā mahāśailaṃ pāṇinaikena mārutiḥ</li>
        <li id="R_6.044.016cd">vinadya sumahānādaṃ bhrāmayām āsa vīryavān</li>
    </ul>
    <ul id="R_6.044.017">
        <li id="R_6.044.017ab">tatas tam abhidudrāva rākṣasendram akampanam</li>
        <li id="R_6.044.017cd">yathā hi namuciṃ saṃkhye vajreṇeva puraṃdaraḥ</li>
    </ul>
    <ul id="R_6.044.018">
        <li id="R_6.044.018ab">akampanas tu tad dṛṣṭvā giriśṛṅgaṃ samudyatam</li>
        <li id="R_6.044.018cd">dūrād eva mahābāṇair ardhacandrair vyadārayat</li>
    </ul>
    <ul id="R_6.044.019">
        <li id="R_6.044.019ab">tat parvatāgram ākāśe rakṣobāṇavidāritam</li>
        <li id="R_6.044.019cd">vikīrṇaṃ patitaṃ dṛṣṭvā hanūmān krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_6.044.020">
        <li id="R_6.044.020ab">so 'śvakarṇaṃ samāsādya roṣadarpānvito hariḥ</li>
        <li id="R_6.044.020cd">tūrṇam utpāṭayām āsa mahāgirim ivocchritam</li>
    </ul>
    <ul id="R_6.044.021">
        <li id="R_6.044.021ab">taṃ gṛhītvā mahāskandhaṃ so 'śvakarṇaṃ mahādyutiḥ</li>
        <li id="R_6.044.021cd">prahasya parayā prītyā bhrāmayām āsa saṃyuge</li>
    </ul>
    <ul id="R_6.044.022">
        <li id="R_6.044.022ab">pradhāvann uruvegena prabhañjaṃs tarasā drumān</li>
        <li id="R_6.044.022cd">hanūmān paramakruddhaś caraṇair dārayat kṣitim</li>
    </ul>
    <ul id="R_6.044.023">
        <li id="R_6.044.023ab">gajāṃś ca sagajārohān sarathān rathinas tathā</li>
        <li id="R_6.044.023cd">jaghāna hanumān dhīmān rākṣasāṃś ca padātikān</li>
    </ul>
    <ul id="R_6.044.024">
        <li id="R_6.044.024ab">tam antakam iva kruddhaṃ samare prāṇahāriṇam</li>
        <li id="R_6.044.024cd">hanūmantam abhiprekṣya rākṣasā vipradudruvuḥ</li>
    </ul>
    <ul id="R_6.044.025">
        <li id="R_6.044.025ab">tam āpatantaṃ saṃkruddhaṃ rākṣasānāṃ bhayāvaham</li>
        <li id="R_6.044.025cd">dadarśākampano vīraś cukrodha ca nanāda ca</li>
    </ul>
    <ul id="R_6.044.026">
        <li id="R_6.044.026ab">sa caturdaśabhir bāṇaiḥ śitair dehavidāraṇaiḥ</li>
        <li id="R_6.044.026cd">nirbibheda hanūmantaṃ mahāvīryam akampanaḥ</li>
    </ul>
    <ul id="R_6.044.027">
        <li id="R_6.044.027ab">sa tathā pratividdhas tu bahvībhiḥ śaravṛṣṭibhiḥ</li>
        <li id="R_6.044.027cd">hanūmān dadṛśe vīraḥ prarūḍha iva sānumān</li>
    </ul>
    <ul id="R_6.044.028">
        <li id="R_6.044.028ab">tato 'nyaṃ vṛkṣam utpāṭya kṛtvā vegam anuttamam</li>
        <li id="R_6.044.028cd">śirasy abhijaghānāśu rākṣasendram akampanam</li>
    </ul>
    <ul id="R_6.044.029">
        <li id="R_6.044.029ab">sa vṛkṣeṇa hatas tena sakrodhena mahātmanā</li>
        <li id="R_6.044.029cd">rākṣaso vānarendreṇa papāta sa mamāra ca</li>
    </ul>
    <ul id="R_6.044.030">
        <li id="R_6.044.030ab">taṃ dṛṣṭvā nihataṃ bhūmau rākṣasendram akampanam</li>
        <li id="R_6.044.030cd">vyathitā rākṣasāḥ sarve kṣitikampa iva drumāḥ</li>
    </ul>
    <ul id="R_6.044.031">
        <li id="R_6.044.031ab">tyaktapraharaṇāḥ sarve rākṣasās te parājitāḥ</li>
        <li id="R_6.044.031cd">laṅkām abhiyayus trastā vānarais tair abhidrutāḥ</li>
    </ul>
    <ul id="R_6.044.032">
        <li id="R_6.044.032ab">te muktakeśāḥ saṃbhrāntā bhagnamānāḥ parājitāḥ</li>
        <li id="R_6.044.032cd">sravacchramajalair aṅgaiḥ śvasanto vipradudruvuḥ</li>
    </ul>
    <ul id="R_6.044.033">
        <li id="R_6.044.033ab">anyonyaṃ pramamantus te viviśur nagaraṃ bhayāt</li>
        <li id="R_6.044.033cd">pṛṣṭhatas te susaṃmūḍhāḥ prekṣamāṇā muhur muhuḥ</li>
    </ul>
    <ul id="R_6.044.034">
        <li id="R_6.044.034ab">teṣu laṅkāṃ praviṣṭeṣu rākṣaseṣu mahābalāḥ</li>
        <li id="R_6.044.034cd">sametya harayaḥ sarve hanūmantam apūjayan</li>
    </ul>
    <ul id="R_6.044.035">
        <li id="R_6.044.035ab">so 'pi prahṛṣṭas tān sarvān harīn saṃpratyapūjayat</li>
        <li id="R_6.044.035cd">hanūmān sattvasaṃpanno yathārham anukūlataḥ</li>
    </ul>
    <ul id="R_6.044.036">
        <li id="R_6.044.036ab">vineduś ca yathā prāṇaṃ harayo jitakāśinaḥ</li>
        <li id="R_6.044.036cd">cakarṣuś ca punas tatra saprāṇān eva rākṣasān</li>
    </ul>
    <ul id="R_6.044.037">
        <li id="R_6.044.037ab">
            <seg type="pāda" n="a">sa vīraśobhām abhajan mahākapiḥ</seg>
            <seg type="pāda" n="b">sametya rakṣāṃsi nihatya mārutiḥ</seg>
        </li>
        <li id="R_6.044.037cd">
            <seg type="pāda" n="c">mahāsuraṃ bhīmam amitranāśanaṃ</seg>
            <seg type="pāda" n="d">yathaiva viṣṇur balinaṃ camūmukhe</seg>
        </li>
    </ul>
    <ul id="R_6.044.038">
        <li id="R_6.044.038ab">
            <seg type="pāda" n="a">apūjayan devagaṇās tadā kapiṃ</seg>
            <seg type="pāda" n="b">svayaṃ ca rāmo 'tibalaś ca lakṣmaṇaḥ</seg>
        </li>
        <li id="R_6.044.038cd">
            <seg type="pāda" n="c">tathaiva sugrīvamukhāḥ plavaṃgamā</seg>
            <seg type="pāda" n="d">vibhīṣaṇaś caiva mahābalas tadā</seg>
        </li>
    </ul>
    <ul id="R_6.045.001">
        <li id="R_6.045.001ab">akampanavadhaṃ śrutvā kruddho vai rākṣaseśvaraḥ</li>
        <li id="R_6.045.001cd">kiṃ cid dīnamukhaś cāpi sacivāṃs tān udaikṣata</li>
    </ul>
    <ul id="R_6.045.002">
        <li id="R_6.045.002ab">sa tu dhyātvā muhūrtaṃ tu mantribhiḥ saṃvicārya ca</li>
        <li id="R_6.045.002cd">purīṃ pariyayau laṅkāṃ sarvān gulmān avekṣitum</li>
    </ul>
    <ul id="R_6.045.003">
        <li id="R_6.045.003ab">tāṃ rākṣasagaṇair guptāṃ gulmair bahubhir āvṛtām</li>
        <li id="R_6.045.003cd">dadarśa nagarīṃ laṅkāṃ patākādhvajamālinīm</li>
    </ul>
    <ul id="R_6.045.004">
        <li id="R_6.045.004ab">ruddhāṃ tu nagarīṃ dṛṣṭvā rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.045.004cd">uvācāmarṣitaḥ kāle prahastaṃ yuddhakovidam</li>
    </ul>
    <ul id="R_6.045.005">
        <li id="R_6.045.005ab">purasyopaniviṣṭasya sahasā pīḍitasya ca</li>
        <li id="R_6.045.005cd">nānyaṃ yuddhāt prapaśyāmi mokṣaṃ yuddhaviśārada</li>
    </ul>
    <ul id="R_6.045.006">
        <li id="R_6.045.006ab">ahaṃ vā kumbhakarṇo vā tvaṃ vā senāpatir mama</li>
        <li id="R_6.045.006cd">indrajid vā nikumbho vā vaheyur bhāram īdṛśam</li>
    </ul>
    <ul id="R_6.045.007">
        <li id="R_6.045.007ab">sa tvaṃ balam itaḥ śīghram ādāya parigṛhya ca</li>
        <li id="R_6.045.007cd">vijayāyābhiniryāhi yatra sarve vanaukasaḥ</li>
    </ul>
    <ul id="R_6.045.008">
        <li id="R_6.045.008ab">niryāṇād eva te nūnaṃ capalā harivāhinī</li>
        <li id="R_6.045.008cd">nardatāṃ rākṣasendrāṇāṃ śrutvā nādaṃ draviṣyati</li>
    </ul>
    <ul id="R_6.045.009">
        <li id="R_6.045.009ab">capalā hy avinītāś ca calacittāś ca vānarāḥ</li>
        <li id="R_6.045.009cd">na sahiṣyanti te nādaṃ siṃhanādam iva dvipāḥ</li>
    </ul>
    <ul id="R_6.045.010">
        <li id="R_6.045.010ab">vidrute ca bale tasmin rāmaḥ saumitriṇā saha</li>
        <li id="R_6.045.010cd">avaśaste nirālambaḥ prahastavaśam eṣyati</li>
    </ul>
    <ul id="R_6.045.011">
        <li id="R_6.045.011ab">āpatsaṃśayitā śreyo nātra niḥsaṃśayīkṛtā</li>
        <li id="R_6.045.011cd">pratilomānulomaṃ vā yad vā no manyase hitam</li>
    </ul>
    <ul id="R_6.045.012">
        <li id="R_6.045.012ab">rāvaṇenaivam uktas tu prahasto vāhinīpatiḥ</li>
        <li id="R_6.045.012cd">rākṣasendram uvācedam asurendram ivośanā</li>
    </ul>
    <ul id="R_6.045.013">
        <li id="R_6.045.013ab">rājan mantritapūrvaṃ naḥ kuśalaiḥ saha mantribhiḥ</li>
        <li id="R_6.045.013cd">vivādaś cāpi no vṛttaḥ samavekṣya parasparam</li>
    </ul>
    <ul id="R_6.045.014">
        <li id="R_6.045.014ab">pradānena tu sītāyāḥ śreyo vyavasitaṃ mayā</li>
        <li id="R_6.045.014cd">apradāne punar yuddhaṃ dṛṣṭam etat tathaiva naḥ</li>
    </ul>
    <ul id="R_6.045.015">
        <li id="R_6.045.015ab">so 'haṃ dānaiś ca mānaiś ca satataṃ pūjitas tvayā</li>
        <li id="R_6.045.015cd">sāntvaiś ca vividhaiḥ kāle kiṃ na kuryāṃ priyaṃ tava</li>
    </ul>
    <ul id="R_6.045.016">
        <li id="R_6.045.016ab">na hi me jīvitaṃ rakṣyaṃ putradāradhanāni vā</li>
        <li id="R_6.045.016cd">tvaṃ paśya māṃ juhūṣantaṃ tvadarthe jīvitaṃ yudhi</li>
    </ul>
    <ul id="R_6.045.017">
        <li id="R_6.045.017ab">evam uktvā tu bhartāraṃ rāvaṇaṃ vāhinīpatiḥ</li>
        <li id="R_6.045.017cd">samānayata me śīghraṃ rākṣasānāṃ mahad balam</li>
    </ul>
    <ul id="R_6.045.018">
        <li id="R_6.045.018ab">madbāṇāśanivegena hatānāṃ tu raṇājire</li>
        <li id="R_6.045.018cd">adya tṛpyantu māṃsena pakṣiṇaḥ kānanaukasām</li>
    </ul>
    <ul id="R_6.045.019">
        <li id="R_6.045.019ab">ity uktās te prahastena balādhyakṣāḥ kṛtatvarāḥ</li>
        <li id="R_6.045.019cd">balam udyojayām āsus tasmin rākṣasamandire</li>
    </ul>
    <ul id="R_6.045.020">
        <li id="R_6.045.020ab">sā babhūva muhūrtena tigmanānāvidhāyudhaiḥ</li>
        <li id="R_6.045.020cd">laṅkā rākṣasavīrais tair gajair iva samākulā</li>
    </ul>
    <ul id="R_6.045.021">
        <li id="R_6.045.021ab">hutāśanaṃ tarpayatāṃ brāhmaṇāṃś ca namasyatām</li>
        <li id="R_6.045.021cd">ājyagandhaprativahaḥ surabhir māruto vavau</li>
    </ul>
    <ul id="R_6.045.022">
        <li id="R_6.045.022ab">srajaś ca vividhākārā jagṛhus tv abhimantritāḥ</li>
        <li id="R_6.045.022cd">saṃgrāmasajjāḥ saṃhṛṣṭā dhārayan rākṣasās tadā</li>
    </ul>
    <ul id="R_6.045.023">
        <li id="R_6.045.023ab">sadhanuṣkāḥ kavacino vegād āplutya rākṣasāḥ</li>
        <li id="R_6.045.023cd">rāvaṇaṃ prekṣya rājānaṃ prahastaṃ paryavārayan</li>
    </ul>
    <ul id="R_6.045.024">
        <li id="R_6.045.024ab">athāmantrya ca rājānaṃ bherīm āhatya bhairavām</li>
        <li id="R_6.045.024cd">āruroha rathaṃ divyaṃ prahastaḥ sajjakalpitam</li>
    </ul>
    <ul id="R_6.045.025">
        <li id="R_6.045.025ab">hayair mahājavair yuktaṃ samyak sūtasusaṃyutam</li>
        <li id="R_6.045.025cd">mahājaladanirghoṣaṃ sākṣāc candrārkabhāsvaram</li>
    </ul>
    <ul id="R_6.045.026">
        <li id="R_6.045.026ab">uragadhvajadurdharṣaṃ suvarūthaṃ svapaskaram</li>
        <li id="R_6.045.026cd">suvarṇajālasaṃyuktaṃ prahasantam iva śriyā</li>
    </ul>
    <ul id="R_6.045.027">
        <li id="R_6.045.027ab">tatas taṃ ratham āsthāya rāvaṇārpitaśāsanaḥ</li>
        <li id="R_6.045.027cd">laṅkāyā niryayau tūrṇaṃ balena mahatā vṛtaḥ</li>
    </ul>
    <ul id="R_6.045.028">
        <li id="R_6.045.028ab">tato duṃdubhinirghoṣaḥ parjanyaninadopamaḥ</li>
        <li id="R_6.045.028cd">śuśruve śaṅkhaśabdaś ca prayāte vāhinīpatau</li>
    </ul>
    <ul id="R_6.045.029">
        <li id="R_6.045.029ab">ninadantaḥ svarān ghorān rākṣasā jagmur agrataḥ</li>
        <li id="R_6.045.029cd">bhīmarūpā mahākāyāḥ prahastasya puraḥsarāḥ</li>
    </ul>
    <ul id="R_6.045.030">
        <li id="R_6.045.030ab">vyūḍhenaiva sughoreṇa pūrvadvārāt sa niryayau</li>
        <li id="R_6.045.030cd">gajayūthanikāśena balena mahatā vṛtaḥ</li>
    </ul>
    <ul id="R_6.045.031">
        <li id="R_6.045.031ab">sāgarapratimaughena vṛtas tena balena saḥ</li>
        <li id="R_6.045.031cd">prahasto niryayau tūrṇaṃ kruddhaḥ kālāntakopamaḥ</li>
    </ul>
    <ul id="R_6.045.032">
        <li id="R_6.045.032ab">tasya niryāṇa ghoṣeṇa rākṣasānāṃ ca nardatām</li>
        <li id="R_6.045.032cd">laṅkāyāṃ sarvabhūtāni vinedur vikṛtaiḥ svaraiḥ</li>
    </ul>
    <ul id="R_6.045.033">
        <li id="R_6.045.033ab">vyabhram ākāśam āviśya māṃsaśoṇitabhojanāḥ</li>
        <li id="R_6.045.033cd">maṇḍalāny apasavyāni khagāś cakrū rathaṃ prati</li>
    </ul>
    <ul id="R_6.045.034">
        <li id="R_6.045.034ab">vamantyaḥ pāvakajvālāḥ śivā ghorā vavāśire</li>
    </ul>
    <ul id="R_6.045.035">
        <li id="R_6.045.035ab">antarikṣāt papātolkā vāyuś ca paruṣo vavau</li>
        <li id="R_6.045.035cd">anyonyam abhisaṃrabdhā grahāś ca na cakāśire</li>
    </ul>
    <ul id="R_6.045.036">
        <li id="R_6.045.036ab">vavarṣū rudhiraṃ cāsya siṣicuś ca puraḥsarān</li>
        <li id="R_6.045.036cd">ketumūrdhani gṛdhro 'sya vilīno dakṣiṇāmukhaḥ</li>
    </ul>
    <ul id="R_6.045.037">
        <li id="R_6.045.037ab">sārather bahuśaś cāsya saṃgrāmam avagāhataḥ</li>
        <li id="R_6.045.037cd">pratodo nyapatad dhastāt sūtasya hayasādinaḥ</li>
    </ul>
    <ul id="R_6.045.038">
        <li id="R_6.045.038ab">niryāṇa śrīś ca yāsyāsīd bhāsvarā ca sudurlabhā</li>
        <li id="R_6.045.038cd">sā nanāśa muhūrtena same ca skhalitā hayāḥ</li>
    </ul>
    <ul id="R_6.045.039">
        <li id="R_6.045.039ab">prahastaṃ tv abhiniryāntaṃ prakhyāta balapauruṣam</li>
        <li id="R_6.045.039cd">yudhi nānāpraharaṇā kapisenābhyavartata</li>
    </ul>
    <ul id="R_6.045.040">
        <li id="R_6.045.040ab">atha ghoṣaḥ sutumulo harīṇāṃ samajāyata</li>
        <li id="R_6.045.040cd">vṛkṣān ārujatāṃ caiva gurvīś cāgṛhṇatāṃ śilāḥ</li>
    </ul>
    <ul id="R_6.045.041">
        <li id="R_6.045.041ab">ubhe pramudite sainye rakṣogaṇavanaukasām</li>
        <li id="R_6.045.041cd">vegitānāṃ samarthānām anyonyavadhakāṅkṣiṇām</li>
        <li id="R_6.045.041ef">parasparaṃ cāhvayatāṃ ninādaḥ śrūyate mahān</li>
    </ul>
    <ul id="R_6.045.042">
        <li id="R_6.045.042ab">
            <seg type="pāda" n="a">tataḥ prahastaḥ kapirājavāhinīm</seg>
            <seg type="pāda" n="b">abhipratasthe vijayāya durmatiḥ</seg>
        </li>
        <li id="R_6.045.042cd">
            <seg type="pāda" n="c">vivṛddhavegāṃ ca viveśa tāṃ camūṃ</seg>
            <seg type="pāda" n="d">yathā mumūrṣuḥ śalabho vibhāvasum</seg>
        </li>
    </ul>
    <ul id="R_6.046.001">
        <li id="R_6.046.001ab">tataḥ prahastaṃ niryāntaṃ bhīmaṃ bhīmaparākramam</li>
        <li id="R_6.046.001cd">garjantaṃ sumahākāyaṃ rākṣasair abhisaṃvṛtam</li>
    </ul>
    <ul id="R_6.046.002">
        <li id="R_6.046.002ab">dadarśa mahatī senā vānarāṇāṃ balīyasām</li>
        <li id="R_6.046.002cd">atisaṃjātaroṣāṇāṃ prahastam abhigarjatām</li>
    </ul>
    <ul id="R_6.046.003">
        <li id="R_6.046.003ab">khaḍgaśaktyaṣṭibāṇāś ca śūlāni musalāni ca</li>
        <li id="R_6.046.003cd">gadāś ca parighāḥ prāsā vividhāś ca paraśvadhāḥ</li>
    </ul>
    <ul id="R_6.046.004">
        <li id="R_6.046.004ab">dhanūṃṣi ca vicitrāṇi rākṣasānāṃ jayaiṣiṇām</li>
        <li id="R_6.046.004cd">pragṛhītāny aśobhanta vānarān abhidhāvatām</li>
    </ul>
    <ul id="R_6.046.005">
        <li id="R_6.046.005ab">jagṛhuḥ pādapāṃś cāpi puṣpitān vānararṣabhāḥ</li>
        <li id="R_6.046.005cd">śilāś ca vipulā dīrghā yoddhukāmāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.046.006">
        <li id="R_6.046.006ab">teṣām anyonyam āsādya saṃgrāmaḥ sumahān abhūt</li>
        <li id="R_6.046.006cd">bahūnām aśmavṛṣṭiṃ ca śaravṛṣṭiṃ ca varṣatām</li>
    </ul>
    <ul id="R_6.046.007">
        <li id="R_6.046.007ab">bahavo rākṣasā yuddhe bahūn vānarayūthapān</li>
        <li id="R_6.046.007cd">vānarā rākṣasāṃś cāpi nijaghnur bahavo bahūn</li>
    </ul>
    <ul id="R_6.046.008">
        <li id="R_6.046.008ab">śūlaiḥ pramathitāḥ ke cit ke cit tu paramāyudhaiḥ</li>
        <li id="R_6.046.008cd">parighair āhatāḥ ke cit ke cic chinnāḥ paraśvadhaiḥ</li>
    </ul>
    <ul id="R_6.046.009">
        <li id="R_6.046.009ab">nirucchvāsāḥ punaḥ ke cit patitā dharaṇītale</li>
        <li id="R_6.046.009cd">vibhinnahṛdayāḥ ke cid iṣusaṃtānasaṃditāḥ</li>
    </ul>
    <ul id="R_6.046.010">
        <li id="R_6.046.010ab">ke cid dvidhākṛtāḥ khaḍgaiḥ sphurantaḥ patitā bhuvi</li>
        <li id="R_6.046.010cd">vānarā rākṣasaiḥ śūlaiḥ pārśvataś ca vidāritāḥ</li>
    </ul>
    <ul id="R_6.046.011">
        <li id="R_6.046.011ab">vānaraiś cāpi saṃkruddhai rākṣasaughāḥ samantataḥ</li>
        <li id="R_6.046.011cd">pādapair giriśṛṅgaiś ca saṃpiṣṭā vasudhātale</li>
    </ul>
    <ul id="R_6.046.012">
        <li id="R_6.046.012ab">vajrasparśatalair hastair muṣṭibhiś ca hatā bhṛśam</li>
        <li id="R_6.046.012cd">vemuḥ śoṇitam āsyebhyo viśīrṇadaśanekṣaṇaḥ</li>
    </ul>
    <ul id="R_6.046.013">
        <li id="R_6.046.013ab">ārtasvaraṃ ca svanatāṃ siṃhanādaṃ ca nardatām</li>
        <li id="R_6.046.013cd">babhūva tumulaḥ śabdo harīṇāṃ rakṣasāṃ yudhi</li>
    </ul>
    <ul id="R_6.046.014">
        <li id="R_6.046.014ab">vānarā rākṣasāḥ kruddhā vīramārgam anuvratāḥ</li>
        <li id="R_6.046.014cd">vivṛttanayanāḥ krūrāś cakruḥ karmāṇy abhītavat</li>
    </ul>
    <ul id="R_6.046.015">
        <li id="R_6.046.015ab">narāntakaḥ kumbhahanur mahānādaḥ samunnataḥ</li>
        <li id="R_6.046.015cd">ete prahastasacivāḥ sarve jaghnur vanaukasaḥ</li>
    </ul>
    <ul id="R_6.046.016">
        <li id="R_6.046.016ab">teṣām āpatatāṃ śīghraṃ nighnatāṃ cāpi vānarān</li>
        <li id="R_6.046.016cd">dvivido giriśṛṅgeṇa jaghānaikaṃ narāntakam</li>
    </ul>
    <ul id="R_6.046.017">
        <li id="R_6.046.017ab">durmukhaḥ punar utpāṭya kapiḥ sa vipuladrumam</li>
        <li id="R_6.046.017cd">rākṣasaṃ kṣiprahastas tu samunnatam apothayat</li>
    </ul>
    <ul id="R_6.046.018">
        <li id="R_6.046.018ab">jāmbavāṃs tu susaṃkruddhaḥ pragṛhya mahatīṃ śilām</li>
        <li id="R_6.046.018cd">pātayām āsa tejasvī mahānādasya vakṣasi</li>
    </ul>
    <ul id="R_6.046.019">
        <li id="R_6.046.019ab">atha kumbhahanus tatra tāreṇāsādya vīryavān</li>
        <li id="R_6.046.019cd">vṛkṣeṇābhihato mūrdhni prāṇāṃs tatyāja rākṣasaḥ</li>
    </ul>
    <ul id="R_6.046.020">
        <li id="R_6.046.020ab">amṛṣyamāṇas tat karma prahasto ratham āsthitaḥ</li>
        <li id="R_6.046.020cd">cakāra kadanaṃ ghoraṃ dhanuṣpāṇir vanaukasām</li>
    </ul>
    <ul id="R_6.046.021">
        <li id="R_6.046.021ab">āvarta iva saṃjajñe ubhayoḥ senayos tadā</li>
        <li id="R_6.046.021cd">kṣubhitasyāprameyasya sāgarasyeva nisvanaḥ</li>
    </ul>
    <ul id="R_6.046.022">
        <li id="R_6.046.022ab">mahatā hi śaraugheṇa prahasto yuddhakovidaḥ</li>
        <li id="R_6.046.022cd">ardayām āsa saṃkruddho vānarān paramāhave</li>
    </ul>
    <ul id="R_6.046.023">
        <li id="R_6.046.023ab">vānarāṇāṃ śarīrais tu rākṣasānāṃ ca medinī</li>
        <li id="R_6.046.023cd">babhūva nicitā ghorā patitair iva parvataiḥ</li>
    </ul>
    <ul id="R_6.046.024">
        <li id="R_6.046.024ab">sā mahīrudhiraugheṇa pracchannā saṃprakāśate</li>
        <li id="R_6.046.024cd">saṃchannā mādhave māsi palāśair iva puṣpitaiḥ</li>
    </ul>
    <ul id="R_6.046.025">
        <li id="R_6.046.025ab">hatavīraughavaprāṃ tu bhagnāyudhamahādrumām</li>
        <li id="R_6.046.025cd">śoṇitaughamahātoyāṃ yamasāgaragāminīm</li>
    </ul>
    <ul id="R_6.046.026">
        <li id="R_6.046.026ab">yakṛtplīhamahāpaṅkāṃ vinikīrṇāntraśaivalām</li>
        <li id="R_6.046.026cd">bhinnakāyaśiromīnām aṅgāvayavaśāḍvalām</li>
    </ul>
    <ul id="R_6.046.027">
        <li id="R_6.046.027ab">gṛdhrahaṃsagaṇākīrṇāṃ kaṅkasārasasevitām</li>
        <li id="R_6.046.027cd">medhaḥphenasamākīrṇām ārtastanitanisvanām</li>
    </ul>
    <ul id="R_6.046.028">
        <li id="R_6.046.028ab">tāṃ kāpuruṣadustārāṃ yuddhabhūmimayīṃ nadīm</li>
        <li id="R_6.046.028cd">nadīm iva ghanāpāye haṃsasārasasevitām</li>
    </ul>
    <ul id="R_6.046.029">
        <li id="R_6.046.029ab">rākṣasāḥ kapimukhyāś ca terus tāṃ dustarāṃ nadīm</li>
        <li id="R_6.046.029cd">yathā padmarajodhvastāṃ nalinīṃ gajayūthapāḥ</li>
    </ul>
    <ul id="R_6.046.030">
        <li id="R_6.046.030ab">tataḥ sṛjantaṃ bāṇaughān prahastaṃ syandane sthitam</li>
        <li id="R_6.046.030cd">dadarśa tarasā nīlo vinighnantaṃ plavaṃgamān</li>
    </ul>
    <ul id="R_6.046.031">
        <li id="R_6.046.031ab">sa taṃ paramadurdharṣam āpatantaṃ mahākapiḥ</li>
        <li id="R_6.046.031cd">prahastaṃ tāḍayām āsa vṛkṣam utpāṭya vīryavān</li>
    </ul>
    <ul id="R_6.046.032">
        <li id="R_6.046.032ab">sa tenābhihataḥ kruddho nadan rākṣasapuṃgavaḥ</li>
        <li id="R_6.046.032cd">vavarṣa śaravarṣāṇi plavagānāṃ camūpatau</li>
    </ul>
    <ul id="R_6.046.033">
        <li id="R_6.046.033ab">apārayan vārayituṃ pratyagṛhṇān nimīlitaḥ</li>
        <li id="R_6.046.033cd">yathaiva govṛṣo varṣaṃ śāradaṃ śīghram āgatam</li>
    </ul>
    <ul id="R_6.046.034">
        <li id="R_6.046.034ab">evam eva prahastasya śaravarṣaṃ durāsadam</li>
        <li id="R_6.046.034cd">nimīlitākṣaḥ sahasā nīlaḥ sehe sudāruṇam</li>
    </ul>
    <ul id="R_6.046.035">
        <li id="R_6.046.035ab">roṣitaḥ śaravarṣeṇa sālena mahatā mahān</li>
        <li id="R_6.046.035cd">prajaghāna hayān nīlaḥ prahastasya manojavān</li>
    </ul>
    <ul id="R_6.046.036">
        <li id="R_6.046.036ab">vidhanus tu kṛtas tena prahasto vāhinīpatiḥ</li>
        <li id="R_6.046.036cd">pragṛhya musalaṃ ghoraṃ syandanād avapupluve</li>
    </ul>
    <ul id="R_6.046.037">
        <li id="R_6.046.037ab">tāv ubhau vāhinīmukhyau jātaroṣau tarasvinau</li>
        <li id="R_6.046.037cd">sthitau kṣatajadigdhāṅgau prabhinnāv iva kuñjarau</li>
    </ul>
    <ul id="R_6.046.038">
        <li id="R_6.046.038ab">ullikhantau sutīkṣṇābhir daṃṣṭrābhir itaretaram</li>
        <li id="R_6.046.038cd">siṃhaśārdūlasadṛśau siṃhaśārdūlaceṣṭitau</li>
    </ul>
    <ul id="R_6.046.039">
        <li id="R_6.046.039ab">vikrāntavijayau vīrau samareṣv anivartinau</li>
        <li id="R_6.046.039cd">kāṅkṣamāṇau yaśaḥ prāptuṃ vṛtravāsavayoḥ samau</li>
    </ul>
    <ul id="R_6.046.040">
        <li id="R_6.046.040ab">ājaghāna tadā nīlaṃ lalāṭe musalena saḥ</li>
        <li id="R_6.046.040cd">prahastaḥ paramāyastas tasya susrāva śoṇitam</li>
    </ul>
    <ul id="R_6.046.041">
        <li id="R_6.046.041ab">tataḥ śoṇitadigdhāṅgaḥ pragṛhya sumahātarum</li>
        <li id="R_6.046.041cd">prahastasyorasi kruddho visasarja mahākapiḥ</li>
    </ul>
    <ul id="R_6.046.042">
        <li id="R_6.046.042ab">tam acintyaprahāraṃ sa pragṛhya musalaṃ mahat</li>
        <li id="R_6.046.042cd">abhidudrāva balinaṃ balī nīlaṃ plavaṃgamam</li>
    </ul>
    <ul id="R_6.046.043">
        <li id="R_6.046.043ab">tam ugravegaṃ saṃrabdham āpatantaṃ mahākapiḥ</li>
        <li id="R_6.046.043cd">tataḥ saṃprekṣya jagrāha mahāvego mahāśilām</li>
    </ul>
    <ul id="R_6.046.044">
        <li id="R_6.046.044ab">tasya yuddhābhikāmasya mṛdhe musalayodhinaḥ</li>
        <li id="R_6.046.044cd">prahastasya śilāṃ nīlo mūrdhni tūrṇam apātayat</li>
    </ul>
    <ul id="R_6.046.045">
        <li id="R_6.046.045ab">sā tena kapimukhyena vimuktā mahatī śilā</li>
        <li id="R_6.046.045cd">bibheda bahudhā ghorā prahastasya śiras tadā</li>
    </ul>
    <ul id="R_6.046.046">
        <li id="R_6.046.046ab">sa gatāsur gataśrīko gatasattvo gatendriyaḥ</li>
        <li id="R_6.046.046cd">papāta sahasā bhūmau chinnamūla iva drumaḥ</li>
    </ul>
    <ul id="R_6.046.047">
        <li id="R_6.046.047ab">vibhinnaśirasas tasya bahu susrāvaśoṇitam</li>
        <li id="R_6.046.047cd">śarīrād api susrāva gireḥ prasravaṇaṃ yathā</li>
    </ul>
    <ul id="R_6.046.048">
        <li id="R_6.046.048ab">hate prahaste nīlena tad akampyaṃ mahad balam</li>
        <li id="R_6.046.048cd">rakṣasām aprahṛṣṭānāṃ laṅkām abhijagāma ha</li>
    </ul>
    <ul id="R_6.046.049">
        <li id="R_6.046.049ab">na śekuḥ samavasthātuṃ nihate vāhinīpatau</li>
        <li id="R_6.046.049cd">setubandhaṃ samāsādya viśīrṇaṃ salilaṃ yathā</li>
    </ul>
    <ul id="R_6.046.050">
        <li id="R_6.046.050ab">hate tasmiṃś camūmukhye rākṣasas te nirudyamāḥ</li>
        <li id="R_6.046.050cd">rakṣaḥpatigṛhaṃ gatvā dhyānamūkatvam āgatāḥ</li>
    </ul>
    <ul id="R_6.046.051">
        <li id="R_6.046.051ab">
            <seg type="pāda" n="a">tatas tu nīlo vijayī mahābalaḥ</seg>
            <seg type="pāda" n="b">praśasyamānaḥ svakṛtena karmaṇā</seg>
        </li>
        <li id="R_6.046.051cd">
            <seg type="pāda" n="c">sametya rāmeṇa salakṣmaṇena</seg>
            <seg type="pāda" n="d">prahṛṣṭarūpas tu babhūva yūthapaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.001">
        <li id="R_6.047.001ab">
            <seg type="pāda" n="a">tasmin hate rākṣasasainyapāle</seg>
            <seg type="pāda" n="b">plavaṃgamānām ṛṣabheṇa yuddhe</seg>
        </li>
        <li id="R_6.047.001cd">
            <seg type="pāda" n="c">bhīmāyudhaṃ sāgaratulyavegaṃ</seg>
            <seg type="pāda" n="d">pradudruve rākṣasarājasainyam</seg>
        </li>
    </ul>
    <ul id="R_6.047.002">
        <li id="R_6.047.002ab">
            <seg type="pāda" n="a">gatvā tu rakṣo'dhipateḥ śaśaṃsuḥ</seg>
            <seg type="pāda" n="b">senāpatiṃ pāvakasūnuśastam</seg>
        </li>
        <li id="R_6.047.002cd">
            <seg type="pāda" n="c">tac cāpi teṣāṃ vacanaṃ niśamya</seg>
            <seg type="pāda" n="d">rakṣo'dhipaḥ krodhavaśaṃ jagāma</seg>
        </li>
    </ul>
    <ul id="R_6.047.003">
        <li id="R_6.047.003ab">
            <seg type="pāda" n="a">saṃkhye prahastaṃ nihataṃ niśamya</seg>
            <seg type="pāda" n="b">śokārditaḥ krodhaparītacetāḥ</seg>
        </li>
        <li id="R_6.047.003cd">
            <seg type="pāda" n="c">uvāca tān nairṛtayodhamukhyān</seg>
            <seg type="pāda" n="d">indro yathā cāmarayodhamukhyān</seg>
        </li>
    </ul>
    <ul id="R_6.047.004">
        <li id="R_6.047.004ab">nāvajñā ripave kāryā yair indrabalasūdanaḥ</li>
        <li id="R_6.047.004cd">sūditaḥ sainyapālo me sānuyātraḥ sakuñjaraḥ</li>
    </ul>
    <ul id="R_6.047.005">
        <li id="R_6.047.005ab">so 'haṃ ripuvināśāya vijayāyāvicārayan</li>
        <li id="R_6.047.005cd">svayam eva gamiṣyāmi raṇaśīrṣaṃ tad adbhutam</li>
    </ul>
    <ul id="R_6.047.006">
        <li id="R_6.047.006ab">adya tad vānarānīkaṃ rāmaṃ ca sahalakṣmaṇam</li>
        <li id="R_6.047.006cd">nirdahiṣyāmi bāṇaughair vanaṃ dīptair ivāgnibhiḥ</li>
    </ul>
    <ul id="R_6.047.007">
        <li id="R_6.047.007ab">
            <seg type="pāda" n="a">sa evam uktvā jvalanaprakāśaṃ</seg>
            <seg type="pāda" n="b">rathaṃ turaṃgottamarājiyuktam</seg>
        </li>
        <li id="R_6.047.007cd">
            <seg type="pāda" n="c">prakāśamānaṃ vapuṣā jvalantaṃ</seg>
            <seg type="pāda" n="d">samārurohāmararājaśatruḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.008">
        <li id="R_6.047.008ab">
            <seg type="pāda" n="a">sa śaṅkhabherīpaṭaha praṇādair</seg>
            <seg type="pāda" n="b">āsphoṭitakṣveḍitasiṃhanādaiḥ</seg>
        </li>
        <li id="R_6.047.008cd">
            <seg type="pāda" n="c">puṇyaiḥ stavaiś cāpy abhipūjyamānas</seg>
            <seg type="pāda" n="d">tadā yayau rākṣasarājamukhyaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.009">
        <li id="R_6.047.009ab">
            <seg type="pāda" n="a">sa śailajīmūtanikāśa rūpair</seg>
            <seg type="pāda" n="b">māṃsāśanaiḥ pāvakadīptanetraiḥ</seg>
        </li>
        <li id="R_6.047.009cd">
            <seg type="pāda" n="c">babhau vṛto rākṣasarājamukhyair</seg>
            <seg type="pāda" n="d">bhūtair vṛto rudra ivāmareśaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.010">
        <li id="R_6.047.010ab">
            <seg type="pāda" n="a">tato nagaryāḥ sahasā mahaujā</seg>
            <seg type="pāda" n="b">niṣkramya tad vānarasainyam ugram</seg>
        </li>
        <li id="R_6.047.010cd">
            <seg type="pāda" n="c">mahārṇavābhrastanitaṃ dadarśa</seg>
            <seg type="pāda" n="d">samudyataṃ pādapaśailahastam</seg>
        </li>
    </ul>
    <ul id="R_6.047.011">
        <li id="R_6.047.011ab">
            <seg type="pāda" n="a">tad rākṣasānīkam atipracaṇḍam</seg>
            <seg type="pāda" n="b">ālokya rāmo bhujagendrabāhuḥ</seg>
        </li>
        <li id="R_6.047.011cd">
            <seg type="pāda" n="c">vibhīṣaṇaṃ śastrabhṛtāṃ variṣṭham</seg>
            <seg type="pāda" n="d">uvāca senānugataḥ pṛthuśrīḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.012">
        <li id="R_6.047.012ab">
            <seg type="pāda" n="a">nānāpatākādhvajaśastrajuṣṭaṃ</seg>
            <seg type="pāda" n="b">prāsāsiśūlāyudhacakrajuṣṭam</seg>
        </li>
        <li id="R_6.047.012cd">
            <seg type="pāda" n="c">sainyaṃ nagendropamanāgajuṣṭaṃ</seg>
            <seg type="pāda" n="d">kasyedam akṣobhyam abhīrujuṣṭam</seg>
        </li>
    </ul>
    <ul id="R_6.047.013">
        <li id="R_6.047.013ab">
            <seg type="pāda" n="a">tatas tu rāmasya niśamya vākyaṃ</seg>
            <seg type="pāda" n="b">vibhīṣaṇaḥ śakrasamānavīryaḥ</seg>
        </li>
        <li id="R_6.047.013cd">
            <seg type="pāda" n="c">śaśaṃsa rāmasya balapravekaṃ</seg>
            <seg type="pāda" n="d">mahātmanāṃ rākṣasapuṃgavānām</seg>
        </li>
    </ul>
    <ul id="R_6.047.014">
        <li id="R_6.047.014ab">
            <seg type="pāda" n="a">yo 'sau gajaskandhagato mahātmā</seg>
            <seg type="pāda" n="b">navoditārkopamatāmravaktraḥ</seg>
        </li>
        <li id="R_6.047.014cd">
            <seg type="pāda" n="c">prakampayan nāgaśiro 'bhyupaiti</seg>
            <seg type="pāda" n="d">hy akampanaṃ tv enam avehi rājan</seg>
        </li>
    </ul>
    <ul id="R_6.047.015">
        <li id="R_6.047.015ab">
            <seg type="pāda" n="a">yo 'sau rathastho mṛgarājaketur</seg>
            <seg type="pāda" n="b">dhūnvan dhanuḥ śakradhanuḥprakāśam</seg>
        </li>
        <li id="R_6.047.015cd">
            <seg type="pāda" n="c">karīva bhāty ugravivṛttadaṃṣṭraḥ</seg>
            <seg type="pāda" n="d">sa indrajin nāma varapradhānaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.016">
        <li id="R_6.047.016ab">
            <seg type="pāda" n="a">yaś caiṣa vindhyāstamahendrakalpo</seg>
            <seg type="pāda" n="b">dhanvī rathastho 'tiratho 'tivīryaḥ</seg>
        </li>
        <li id="R_6.047.016cd">
            <seg type="pāda" n="c">visphārayaṃś cāpam atulyamānaṃ</seg>
            <seg type="pāda" n="d">nāmnātikāyo 'tivivṛddhakāyaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.017">
        <li id="R_6.047.017ab">
            <seg type="pāda" n="a">yo 'sau navārkoditatāmracakṣur</seg>
            <seg type="pāda" n="b">āruhya ghaṇṭāninadapraṇādam</seg>
        </li>
        <li id="R_6.047.017cd">
            <seg type="pāda" n="c">gajaṃ kharaṃ garjati vai mahātmā</seg>
            <seg type="pāda" n="d">mahodaro nāma sa eṣa vīraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.018">
        <li id="R_6.047.018ab">
            <seg type="pāda" n="a">yo 'sau hayaṃ kāñcanacitrabhāṇḍam</seg>
            <seg type="pāda" n="b">āruhya saṃdhyābhragiriprakāśam</seg>
        </li>
        <li id="R_6.047.018cd">
            <seg type="pāda" n="c">prāsaṃ samudyamya marīcinaddhaṃ</seg>
            <seg type="pāda" n="d">piśāca eṣāśanitulyavegaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.019">
        <li id="R_6.047.019ab">
            <seg type="pāda" n="a">yaś caiṣa śūlaṃ niśitaṃ pragṛhya</seg>
            <seg type="pāda" n="b">vidyutprabhaṃ kiṃkaravajravegam</seg>
        </li>
        <li id="R_6.047.019cd">
            <seg type="pāda" n="c">vṛṣendram āsthāya giriprakāśam</seg>
            <seg type="pāda" n="d">āyāti so 'sau triśirā yaśasvī</seg>
        </li>
    </ul>
    <ul id="R_6.047.020">
        <li id="R_6.047.020ab">
            <seg type="pāda" n="a">asau ca jīmūtanikāśa rūpaḥ</seg>
            <seg type="pāda" n="b">kumbhaḥ pṛthuvyūḍhasujātavakṣāḥ</seg>
        </li>
        <li id="R_6.047.020cd">
            <seg type="pāda" n="c">samāhitaḥ pannagarājaketur</seg>
            <seg type="pāda" n="d">visphārayan bhāti dhanur vidhūnvan</seg>
        </li>
    </ul>
    <ul id="R_6.047.021">
        <li id="R_6.047.021ab">
            <seg type="pāda" n="a">yaś caiṣa jāmbūnadavajrajuṣṭaṃ</seg>
            <seg type="pāda" n="b">dīptaṃ sadhūmaṃ parighaṃ pragṛhya</seg>
        </li>
        <li id="R_6.047.021cd">
            <seg type="pāda" n="c">āyāti rakṣobalaketubhūtaḥ</seg>
            <seg type="pāda" n="d">so 'sau nikumbho 'dbhutaghorakarmā</seg>
        </li>
    </ul>
    <ul id="R_6.047.022">
        <li id="R_6.047.022ab">
            <seg type="pāda" n="a">yaś caiṣa cāpāsiśaraughajuṣṭaṃ</seg>
            <seg type="pāda" n="b">patākinaṃ pāvakadīptarūpam</seg>
        </li>
        <li id="R_6.047.022cd">
            <seg type="pāda" n="c">rathaṃ samāsthāya vibhāty udagro</seg>
            <seg type="pāda" n="d">narāntako 'sau nagaśṛṅgayodhī</seg>
        </li>
    </ul>
    <ul id="R_6.047.023">
        <li id="R_6.047.023ab">
            <seg type="pāda" n="a">yaś caiṣa nānāvidhaghorarūpair</seg>
            <seg type="pāda" n="b">vyāghroṣṭranāgendramṛgendravaktraiḥ</seg>
        </li>
        <li id="R_6.047.023cd">
            <seg type="pāda" n="c">bhūtair vṛto bhāti vivṛttanetraiḥ</seg>
            <seg type="pāda" n="d">so 'sau surāṇām api darpahantā</seg>
        </li>
    </ul>
    <ul id="R_6.047.024">
        <li id="R_6.047.024ab">
            <seg type="pāda" n="a">yatraitad indupratimaṃ vibhātic</seg>
            <seg type="pāda" n="b">chattraṃ sitaṃ sūkṣmaśalākam agryam</seg>
        </li>
        <li id="R_6.047.024cd">
            <seg type="pāda" n="c">atraiṣa rakṣo'dhipatir mahātmā</seg>
            <seg type="pāda" n="d">bhūtair vṛto rudra ivāvabhāti</seg>
        </li>
    </ul>
    <ul id="R_6.047.025">
        <li id="R_6.047.025ab">
            <seg type="pāda" n="a">asau kirīṭī calakuṇḍalāsyo</seg>
            <seg type="pāda" n="b">nāgendravindhyopamabhīmakāyaḥ</seg>
        </li>
        <li id="R_6.047.025cd">
            <seg type="pāda" n="c">mahendravaivasvatadarpahantā</seg>
            <seg type="pāda" n="d">rakṣo'dhipaḥ sūrya ivāvabhāti</seg>
        </li>
    </ul>
    <ul id="R_6.047.026">
        <li id="R_6.047.026ab">pratyuvāca tato rāmo vibhīṣaṇam ariṃdamam</li>
        <li id="R_6.047.026cd">aho dīpto mahātejā rāvaṇo rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_6.047.027">
        <li id="R_6.047.027ab">āditya iva duṣprekṣyo raśmibhir bhāti rāvaṇaḥ</li>
        <li id="R_6.047.027cd">suvyaktaṃ lakṣaye hy asya rūpaṃ tejaḥsamāvṛtam</li>
    </ul>
    <ul id="R_6.047.028">
        <li id="R_6.047.028ab">devadānavavīrāṇāṃ vapur naivaṃvidhaṃ bhavet</li>
        <li id="R_6.047.028cd">yādṛśaṃ rākṣasendrasya vapur etat prakāśate</li>
    </ul>
    <ul id="R_6.047.029">
        <li id="R_6.047.029ab">sarve parvatasaṃkāśāḥ sarve parvatayodhinaḥ</li>
        <li id="R_6.047.029cd">sarve dīptāyudhadharā yodhaś cāsya mahaujasaḥ</li>
    </ul>
    <ul id="R_6.047.030">
        <li id="R_6.047.030ab">bhāti rākṣasarājo 'sau pradīptair bhīmavikramaiḥ</li>
        <li id="R_6.047.030cd">bhūtaiḥ parivṛtas tīkṣṇair dehavadbhir ivāntakaḥ</li>
    </ul>
    <ul id="R_6.047.031">
        <li id="R_6.047.031ab">evam uktvā tato rāmo dhanur ādāya vīryavān</li>
        <li id="R_6.047.031cd">lakṣmaṇānucaras tasthau samuddhṛtya śarottamam</li>
    </ul>
    <ul id="R_6.047.032">
        <li id="R_6.047.032ab">
            <seg type="pāda" n="a">tataḥ sa rakṣo'dhipatir mahātmā</seg>
            <seg type="pāda" n="b">rakṣāṃsi tāny āha mahābalāni</seg>
        </li>
        <li id="R_6.047.032cd">
            <seg type="pāda" n="c">dvāreṣu caryāgṛhagopureṣu</seg>
            <seg type="pāda" n="d">sunirvṛtās tiṣṭhata nirviśaṅkāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.033">
        <li id="R_6.047.033ab">
            <seg type="pāda" n="a">visarjayitvā sahasā tatas tān</seg>
            <seg type="pāda" n="b">gateṣu rakṣaḥsu yathāniyogam</seg>
        </li>
        <li id="R_6.047.033cd">
            <seg type="pāda" n="c">vyadārayad vānarasāgaraughaṃ</seg>
            <seg type="pāda" n="d">mahājhaṣaḥ pūrmam ivārṇavaugham</seg>
        </li>
    </ul>
    <ul id="R_6.047.034">
        <li id="R_6.047.034ab">
            <seg type="pāda" n="a">tam āpatantaṃ sahasā samīkṣya</seg>
            <seg type="pāda" n="b">dīpteṣucāpaṃ yudhi rākṣasendram</seg>
        </li>
        <li id="R_6.047.034cd">
            <seg type="pāda" n="c">mahat samutpāṭya mahīdharāgraṃ</seg>
            <seg type="pāda" n="d">dudrāva rakṣo'dhipatiṃ harīśaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.035">
        <li id="R_6.047.035ab">
            <seg type="pāda" n="a">tac chailaśṛṅgaṃ bahuvṛkṣasānuṃ</seg>
            <seg type="pāda" n="b">pragṛhya cikṣepa niśācarāya</seg>
        </li>
        <li id="R_6.047.035cd">
            <seg type="pāda" n="c">tam āpatantaṃ sahasā samīkṣya</seg>
            <seg type="pāda" n="d">bibheda bāṇais tapanīyapuṅkhaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.036">
        <li id="R_6.047.036ab">
            <seg type="pāda" n="a">tasmin pravṛddhottamasānuvṛkṣe</seg>
            <seg type="pāda" n="b">śṛṅge vikīrṇe patite pṛthivyām</seg>
        </li>
        <li id="R_6.047.036cd">
            <seg type="pāda" n="c">mahāhikalpaṃ śaram antakābhaṃ</seg>
            <seg type="pāda" n="d">samādade rākṣasalokanāthaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.037">
        <li id="R_6.047.037ab">
            <seg type="pāda" n="a">sa taṃ gṛhītvānilatulyavegaṃ</seg>
            <seg type="pāda" n="b">savisphuliṅgajvalanaprakāśam</seg>
        </li>
        <li id="R_6.047.037cd">
            <seg type="pāda" n="c">bāṇaṃ mahendrāśanitulyavegaṃ</seg>
            <seg type="pāda" n="d">cikṣepa sugrīvavadhāya ruṣṭaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.038">
        <li id="R_6.047.038ab">
            <seg type="pāda" n="a">sa sāyako rāvaṇabāhumuktaḥ</seg>
            <seg type="pāda" n="b">śakrāśaniprakhyavapuḥ śitāgraḥ</seg>
        </li>
        <li id="R_6.047.038cd">
            <seg type="pāda" n="c">sugrīvam āsādya bibheda vegād</seg>
            <seg type="pāda" n="d">guheritā kraucam ivograśaktiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.039">
        <li id="R_6.047.039ab">
            <seg type="pāda" n="a">sa sāyakārto viparītacetāḥ</seg>
            <seg type="pāda" n="b">kūjan pṛthivyāṃ nipapāta vīraḥ</seg>
        </li>
        <li id="R_6.047.039cd">
            <seg type="pāda" n="c">taṃ prekṣya bhūmau patitaṃ visaṃjmaṃ</seg>
            <seg type="pāda" n="d">neduḥ prahṛṣṭā yudhi yātudhānāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.040">
        <li id="R_6.047.040ab">
            <seg type="pāda" n="a">tato gavākṣo gavayaḥ sudaṃṣṭras</seg>
            <seg type="pāda" n="b">tatharṣabho jyotimukho nalaś ca</seg>
        </li>
        <li id="R_6.047.040cd">
            <seg type="pāda" n="c">śailān samudyamya vivṛddhakāyāḥ</seg>
            <seg type="pāda" n="d">pradudruvus taṃ prati rākṣasendram</seg>
        </li>
    </ul>
    <ul id="R_6.047.041">
        <li id="R_6.047.041ab">
            <seg type="pāda" n="a">teṣāṃ prahārān sa cakāra meghān</seg>
            <seg type="pāda" n="b">rakṣo'dhipo bāṇagaṇaiḥ śitāgraiḥ</seg>
        </li>
        <li id="R_6.047.041cd">
            <seg type="pāda" n="c">tān vānarendrān api bāṇajālair</seg>
            <seg type="pāda" n="d">bibheda jāmbūnadacitrapuṅkhaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.042">
        <li id="R_6.047.042ab">
            <seg type="pāda" n="a">te vānarendrās tridaśāribāṇair</seg>
            <seg type="pāda" n="b">bhinnā nipetur bhuvi bhīmarūpāḥ</seg>
        </li>
        <li id="R_6.047.042cd">
            <seg type="pāda" n="c">tatas tu tad vānarasainyam ugraṃ</seg>
            <seg type="pāda" n="d">pracchādayām āsa sa bāṇajālaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.043">
        <li id="R_6.047.043ab">
            <seg type="pāda" n="a">te vadhyamānāḥ patitāgryavīrā</seg>
            <seg type="pāda" n="b">nānadyamānā bhayaśalyaviddhāḥ</seg>
        </li>
        <li id="R_6.047.043cd">
            <seg type="pāda" n="c">śākhāmṛgā rāvaṇasāyakārtā</seg>
            <seg type="pāda" n="d">jagmuḥ śaraṇyaṃ śaraṇaṃ sma rāmam</seg>
        </li>
    </ul>
    <ul id="R_6.047.044">
        <li id="R_6.047.044ab">
            <seg type="pāda" n="a">tato mahātmā sa dhanur dhanuṣmān</seg>
            <seg type="pāda" n="b">ādāya rāmaḥ saharā jagāma</seg>
        </li>
        <li id="R_6.047.044cd">
            <seg type="pāda" n="c">taṃ lakṣmaṇaḥ prāñjalir abhyupetya</seg>
            <seg type="pāda" n="d">uvāca vākyaṃ paramārthayuktam</seg>
        </li>
    </ul>
    <ul id="R_6.047.045">
        <li id="R_6.047.045ab">kāmam āryaḥ suparyāpto vadhāyāsya durātmanaḥ</li>
        <li id="R_6.047.045cd">vidhamiṣyāmy ahaṃ nīcam anujānīhi māṃ vibho</li>
    </ul>
    <ul id="R_6.047.046">
        <li id="R_6.047.046ab">tam abravīn mahātejā rāmaḥ satyaparākramaḥ</li>
        <li id="R_6.047.046cd">gaccha yatnaparaś cāpi bhava lakṣmaṇa saṃyuge</li>
    </ul>
    <ul id="R_6.047.047">
        <li id="R_6.047.047ab">rāvaṇo hi mahāvīryo raṇe 'dbhutaparākramaḥ</li>
        <li id="R_6.047.047cd">trailokyenāpi saṃkruddho duṣprasahyo na saṃśayaḥ</li>
    </ul>
    <ul id="R_6.047.048">
        <li id="R_6.047.048ab">tasya cchidrāṇi mārgasva svacchidrāṇi ca gopaya</li>
        <li id="R_6.047.048cd">cakṣuṣā dhanuṣā yatnād rakṣātmānaṃ samāhitaḥ</li>
    </ul>
    <ul id="R_6.047.049">
        <li id="R_6.047.049ab">rāghavasya vacaḥ śrutvā saṃpariṣvajya pūjya ca</li>
        <li id="R_6.047.049cd">abhivādya tato rāmaṃ yayau saumitrir āhavam</li>
    </ul>
    <ul id="R_6.047.050">
        <li id="R_6.047.050ab">
            <seg type="pāda" n="a">sa rāvaṇaṃ vāraṇahastabāhur</seg>
            <seg type="pāda" n="b">dadarśa dīptodyatabhīmacāpam</seg>
        </li>
        <li id="R_6.047.050cd">
            <seg type="pāda" n="c">pracchādayantaṃ śaravṛṣṭijālais</seg>
            <seg type="pāda" n="d">tān vānarān bhinnavikīrṇadehān</seg>
        </li>
    </ul>
    <ul id="R_6.047.051">
        <li id="R_6.047.051ab">tam ālokya mahātejā hanūmān mārutātmajā</li>
        <li id="R_6.047.051cd">nivārya śarajālāni pradudrāva sa rāvaṇam</li>
    </ul>
    <ul id="R_6.047.052">
        <li id="R_6.047.052ab">rathaṃ tasya samāsādya bhujam udyamya dakṣiṇam</li>
        <li id="R_6.047.052cd">trāsayan rāvaṇaṃ dhīmān hanūmān vākyam abravīt</li>
    </ul>
    <ul id="R_6.047.053">
        <li id="R_6.047.053ab">devadānavagandharvā yakṣāś ca saha rākṣasaiḥ</li>
        <li id="R_6.047.053cd">avadhyatvāt tvayā bhagnā vānarebhyas tu te bhayam</li>
    </ul>
    <ul id="R_6.047.054">
        <li id="R_6.047.054ab">eṣa me dakṣiṇo bāhuḥ pañcaśākhaḥ samudyataḥ</li>
        <li id="R_6.047.054cd">vidhamiṣyati te dehād bhūtātmānaṃ ciroṣitam</li>
    </ul>
    <ul id="R_6.047.055">
        <li id="R_6.047.055ab">śrutvā hanūmato vākyaṃ rāvaṇo bhīmavikramaḥ</li>
        <li id="R_6.047.055cd">saṃraktanayanaḥ krodhād idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.047.056">
        <li id="R_6.047.056ab">kṣipraṃ prahara niḥśaṅkaṃ sthirāṃ kīrtim avāpnuhi</li>
        <li id="R_6.047.056cd">tatas tvāṃ jñātivikrāntaṃ nāśayiṣyāmi vānara</li>
    </ul>
    <ul id="R_6.047.057">
        <li id="R_6.047.057ab">rāvaṇasya vacaḥ śrutvā vāyusūnur vaco 'bravīt</li>
        <li id="R_6.047.057cd">prahṛtaṃ hi mayā pūrvam akṣaṃ smara sutaṃ tava</li>
    </ul>
    <ul id="R_6.047.058">
        <li id="R_6.047.058ab">evam ukto mahātejā rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.047.058cd">ājaghānānilasutaṃ talenorasi vīryavān</li>
    </ul>
    <ul id="R_6.047.059">
        <li id="R_6.047.059ab">sa talābhihatas tena cacāla ca muhur muhuḥ</li>
        <li id="R_6.047.059cd">ājaghānābhisaṃkruddhas talenaivāmaradviṣam</li>
    </ul>
    <ul id="R_6.047.060">
        <li id="R_6.047.060ab">tatas talenābhihato vānareṇa mahātmanā</li>
        <li id="R_6.047.060cd">daśagrīvaḥ samādhūto yathā bhūmicale 'calaḥ</li>
    </ul>
    <ul id="R_6.047.061">
        <li id="R_6.047.061ab">saṃgrāme taṃ tathā dṛṣṭva rāvaṇaṃ talatāḍitam</li>
        <li id="R_6.047.061cd">ṛṣayo vānarāḥ siddhā nedur devāḥ sahāsurāḥ</li>
    </ul>
    <ul id="R_6.047.062">
        <li id="R_6.047.062ab">athāśvasya mahātejā rāvaṇo vākyam abravīt</li>
        <li id="R_6.047.062cd">sādhu vānaravīryeṇa ślāghanīyo 'si me ripuḥ</li>
    </ul>
    <ul id="R_6.047.063">
        <li id="R_6.047.063ab">rāvaṇenaivam uktas tu mārutir vākyam abravīt</li>
        <li id="R_6.047.063cd">dhig astu mama vīryaṃ tu yat tvaṃ jīvasi rāvaṇa</li>
    </ul>
    <ul id="R_6.047.064">
        <li id="R_6.047.064ab">sakṛt tu praharedānīṃ durbuddhe kiṃ vikatthase</li>
        <li id="R_6.047.064cd">tatas tvāṃ māmako muṣṭir nayiṣyāmi yathākṣayam</li>
        <li id="R_6.047.064ef">tato mārutivākyena krodhas tasya tadājvalat</li>
    </ul>
    <ul id="R_6.047.065">
        <li id="R_6.047.065ab">saṃraktanayano yatnān muṣṭim udyamya dakṣiṇam</li>
        <li id="R_6.047.065cd">pātayām āsa vegena vānarorasi vīryavān</li>
        <li id="R_6.047.065ef">hanūmān vakṣasi vyūdhe saṃcacāla hataḥ punaḥ</li>
    </ul>
    <ul id="R_6.047.066">
        <li id="R_6.047.066ab">vihvalaṃ taṃ tadā dṛṣṭvā hanūmantaṃ mahābalam</li>
        <li id="R_6.047.066cd">rathenātirathaḥ śīghraṃ nīlaṃ prati samabhyagāt</li>
    </ul>
    <ul id="R_6.047.067">
        <li id="R_6.047.067ab">pannagapratimair bhīmaiḥ paramarmātibhedibhiḥ</li>
        <li id="R_6.047.067cd">śarair ādīpayām āsa nīlaṃ haricamūpatim</li>
    </ul>
    <ul id="R_6.047.068">
        <li id="R_6.047.068ab">sa śaraughasamāyasto nīlaḥ kapicamūpatiḥ</li>
        <li id="R_6.047.068cd">kareṇaikena śailāgraṃ rakṣo'dhipataye 'sṛjat</li>
    </ul>
    <ul id="R_6.047.069">
        <li id="R_6.047.069ab">hanūmān api tejasvī samāśvasto mahāmanāḥ</li>
        <li id="R_6.047.069cd">viprekṣamāṇo yuddhepsuḥ saroṣam idam abravīt</li>
    </ul>
    <ul id="R_6.047.070">
        <li id="R_6.047.070ab">nīlena saha saṃyuktaṃ rāvaṇaṃ rākṣaseśvaram</li>
        <li id="R_6.047.070cd">anyena yudhyamānasya na yuktam abhidhāvanam</li>
    </ul>
    <ul id="R_6.047.071">
        <li id="R_6.047.071ab">rāvaṇo 'pi mahātejās tac chṛṅgaṃ saptabhiḥ śaraiḥ</li>
        <li id="R_6.047.071cd">ājaghāna sutīkṣṇāgrais tad vikīrṇaṃ papāta ha</li>
    </ul>
    <ul id="R_6.047.072">
        <li id="R_6.047.072ab">tad vikīrṇaṃ gireḥ śṛṅgaṃ dṛṣṭvā haricamūpatiḥ</li>
        <li id="R_6.047.072cd">kālāgnir iva jajvāla krodhena paravīrahā</li>
    </ul>
    <ul id="R_6.047.073">
        <li id="R_6.047.073ab">so 'śvakarṇān dhavān sālāṃś cūtāṃś cāpi supuṣpitān</li>
        <li id="R_6.047.073cd">anyāṃś ca vividhān vṛkṣān nīlaś cikṣepa saṃyuge</li>
    </ul>
    <ul id="R_6.047.074">
        <li id="R_6.047.074ab">sa tān vṛkṣān samāsādya praticiccheda rāvaṇaḥ</li>
        <li id="R_6.047.074cd">abhyavarṣat sughoreṇa śaravarṣeṇa pāvakim</li>
    </ul>
    <ul id="R_6.047.075">
        <li id="R_6.047.075ab">abhivṛṣṭaḥ śaraugheṇa megheneva mahācalaḥ</li>
        <li id="R_6.047.075cd">hrasvaṃ kṛtvā tadā rūpaṃ dhvajāgre nipapāta ha</li>
    </ul>
    <ul id="R_6.047.076">
        <li id="R_6.047.076ab">pāvakātmajam ālokya dhvajāgre samavasthitam</li>
        <li id="R_6.047.076cd">jajvāla rāvaṇaḥ krodhāt tato nīlo nanāda ha</li>
    </ul>
    <ul id="R_6.047.077">
        <li id="R_6.047.077ab">dhvajāgre dhanuṣaś cāgre kirīṭāgre ca taṃ harim</li>
        <li id="R_6.047.077cd">lakṣmaṇo 'tha hanūmāṃś ca dṛṣṭvā rāmaś ca vismitāḥ</li>
    </ul>
    <ul id="R_6.047.078">
        <li id="R_6.047.078ab">rāvaṇo 'pi mahātejāḥ kapilāghavavismitaḥ</li>
        <li id="R_6.047.078cd">astram āhārayām āsa dīptam āgneyam adbhutam</li>
    </ul>
    <ul id="R_6.047.079">
        <li id="R_6.047.079ab">tatas te cukruśur hṛṣṭā labdhalakṣyāḥ plavaṃgamāḥ</li>
        <li id="R_6.047.079cd">nīlalāghavasaṃbhrāntaṃ dṛṣṭvā rāvaṇam āhave</li>
    </ul>
    <ul id="R_6.047.080">
        <li id="R_6.047.080ab">vānarāṇāṃ ca nādena saṃrabdho rāvaṇas tadā</li>
        <li id="R_6.047.080cd">saṃbhramāviṣṭahṛdayo na kiṃ cit pratyapadyata</li>
    </ul>
    <ul id="R_6.047.081">
        <li id="R_6.047.081ab">āgneyenātha saṃyuktaṃ gṛhītvā rāvaṇaḥ śaram</li>
        <li id="R_6.047.081cd">dhvajaśīrṣasthitaṃ nīlam udaikṣata niśācaraḥ</li>
    </ul>
    <ul id="R_6.047.082">
        <li id="R_6.047.082ab">tato 'bravīn mahātejā rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.047.082cd">kape lāghavayukto 'si māyayā parayānayā</li>
    </ul>
    <ul id="R_6.047.083">
        <li id="R_6.047.083ab">jīvitaṃ khalu rakṣasva yadi śaknoṣi vānara</li>
        <li id="R_6.047.083cd">tāni tāny ātmarūpāṇi sṛjase tvam anekaśaḥ</li>
    </ul>
    <ul id="R_6.047.084">
        <li id="R_6.047.084ab">tathāpi tvāṃ mayā muktaḥ sāyako 'straprayojitaḥ</li>
        <li id="R_6.047.084cd">jīvitaṃ parirakṣantaṃ jīvitād bhraṃśayiṣyati</li>
    </ul>
    <ul id="R_6.047.085">
        <li id="R_6.047.085ab">evam uktvā mahābāhū rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.047.085cd">saṃdhāya bāṇam astreṇa camūpatim atāḍayat</li>
    </ul>
    <ul id="R_6.047.086">
        <li id="R_6.047.086ab">so 'strayuktena bāṇena nīlo vakṣasi tāḍitaḥ</li>
        <li id="R_6.047.086cd">nirdahyamānaḥ sahasā nipapāta mahītale</li>
    </ul>
    <ul id="R_6.047.087">
        <li id="R_6.047.087ab">pitṛmāhātmya saṃyogād ātmanaś cāpi tejasā</li>
        <li id="R_6.047.087cd">jānubhyām apatad bhūmau na ca prāṇair vyayujyata</li>
    </ul>
    <ul id="R_6.047.088">
        <li id="R_6.047.088ab">visaṃjñaṃ vānaraṃ dṛṣṭvā daśagrīvo raṇotsukaḥ</li>
        <li id="R_6.047.088cd">rathenāmbudanādena saumitrim abhidudruve</li>
    </ul>
    <ul id="R_6.047.089">
        <li id="R_6.047.089ab">
            <seg type="pāda" n="a">tam āha saumitrir adīnasattvo</seg>
            <seg type="pāda" n="b">visphārayantaṃ dhanur aprameyam</seg>
        </li>
        <li id="R_6.047.089cd">
            <seg type="pāda" n="c">anvehi mām eva niśācarendra</seg>
            <seg type="pāda" n="d">na vānarāṃs tvaṃ prati yoddhum arhasi</seg>
        </li>
    </ul>
    <ul id="R_6.047.090">
        <li id="R_6.047.090ab">
            <seg type="pāda" n="a">sa tasya vākyaṃ paripūrṇaghoṣaṃ</seg>
            <seg type="pāda" n="b">jyāśabdam ugraṃ ca niśamya rājā</seg>
        </li>
        <li id="R_6.047.090cd">
            <seg type="pāda" n="c">āsādya saumitrim avasthitaṃ taṃ</seg>
            <seg type="pāda" n="d">kopānvitaṃ vākyam uvāca rakṣaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.091">
        <li id="R_6.047.091ab">
            <seg type="pāda" n="a">diṣṭyāsi me rāghava dṛṣṭimārgaṃ</seg>
            <seg type="pāda" n="b">prāpto 'ntagāmī viparītabuddhiḥ</seg>
        </li>
        <li id="R_6.047.091cd">
            <seg type="pāda" n="c">asmin kṣaṇe yāsyasi mṛtyudeśaṃ</seg>
            <seg type="pāda" n="d">saṃsādyamāno mama bāṇajālaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.092">
        <li id="R_6.047.092ab">
            <seg type="pāda" n="a">tam āha saumitrir avismayāno</seg>
            <seg type="pāda" n="b">garjantam udvṛttasitāgradaṃṣṭram</seg>
        </li>
        <li id="R_6.047.092cd">
            <seg type="pāda" n="c">rājan na garjanti mahāprabhāvā</seg>
            <seg type="pāda" n="d">vikatthase pāpakṛtāṃ variṣṭha</seg>
        </li>
    </ul>
    <ul id="R_6.047.093">
        <li id="R_6.047.093ab">
            <seg type="pāda" n="a">jānāmi vīryaṃ tava rākṣasendra</seg>
            <seg type="pāda" n="b">balaṃ pratāpaṃ ca parākramaṃ ca</seg>
        </li>
        <li id="R_6.047.093cd">
            <seg type="pāda" n="c">avasthito 'haṃ śaracāpapāṇir</seg>
            <seg type="pāda" n="d">āgaccha kiṃ moghavikatthanena</seg>
        </li>
    </ul>
    <ul id="R_6.047.094">
        <li id="R_6.047.094ab">
            <seg type="pāda" n="a">sa evam uktaḥ kupitaḥ sasarja</seg>
            <seg type="pāda" n="b">rakṣo'dhipaḥ saptaśarān supuṅkhān</seg>
        </li>
        <li id="R_6.047.094cd">
            <seg type="pāda" n="c">tāṃl lakṣmaṇaḥ kāñcanacitrapuṅkhaiś</seg>
            <seg type="pāda" n="d">ciccheda bāṇair niśitāgradhāraiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.095">
        <li id="R_6.047.095ab">
            <seg type="pāda" n="a">tān prekṣamāṇaḥ sahasā nikṛttān</seg>
            <seg type="pāda" n="b">nikṛttabhogān iva pannagendrān</seg>
        </li>
        <li id="R_6.047.095cd">
            <seg type="pāda" n="c">laṅkeśvaraḥ krodhavaśaṃ jagāma</seg>
            <seg type="pāda" n="d">sasarja cānyān niśitān pṛṣatkān</seg>
        </li>
    </ul>
    <ul id="R_6.047.096">
        <li id="R_6.047.096ab">
            <seg type="pāda" n="a">sa bāṇavarṣaṃ tu vavarṣa tīvraṃ</seg>
            <seg type="pāda" n="b">rāmānujaḥ kārmukasaṃprayuktam</seg>
        </li>
        <li id="R_6.047.096cd">
            <seg type="pāda" n="c">kṣurārdhacandrottamakarṇibhallaiḥ</seg>
            <seg type="pāda" n="d">śarāṃś ca ciccheda na cukṣubhe ca</seg>
        </li>
    </ul>
    <ul id="R_6.047.097">
        <li id="R_6.047.097ab">
            <seg type="pāda" n="a">sa lakṣmaṇaś cāśu śarāñ śitāgrān</seg>
            <seg type="pāda" n="b">mahendravajrāśanitulyavegān</seg>
        </li>
        <li id="R_6.047.097cd">
            <seg type="pāda" n="c">saṃdhāya cāpe jvalanaprakāśān</seg>
            <seg type="pāda" n="d">sasarja rakṣo'dhipater vadhāya</seg>
        </li>
    </ul>
    <ul id="R_6.047.098">
        <li id="R_6.047.098ab">
            <seg type="pāda" n="a">sa tān praciccheda hi rākṣasendraś</seg>
            <seg type="pāda" n="b">chittvā ca tāṃl lakṣmaṇam ājaghāna</seg>
        </li>
        <li id="R_6.047.098cd">
            <seg type="pāda" n="c">śareṇa kālāgnisamaprabheṇa</seg>
            <seg type="pāda" n="d">svayambhudattena lalāṭadeśe</seg>
        </li>
    </ul>
    <ul id="R_6.047.099">
        <li id="R_6.047.099ab">
            <seg type="pāda" n="a">sa lakṣmaṇo rāvaṇasāyakārtaś</seg>
            <seg type="pāda" n="b">cacāla cāpaṃ śithilaṃ pragṛhya</seg>
        </li>
        <li id="R_6.047.099cd">
            <seg type="pāda" n="c">punaś ca saṃjñāṃ pratilabhya kṛcchrāc</seg>
            <seg type="pāda" n="d">ciccheda cāpaṃ tridaśendraśatroḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.100">
        <li id="R_6.047.100ab">
            <seg type="pāda" n="a">nikṛttacāpaṃ tribhir ājaghāna</seg>
            <seg type="pāda" n="b">bāṇais tadā dāśarathiḥ śitāgraiḥ</seg>
        </li>
        <li id="R_6.047.100cd">
            <seg type="pāda" n="c">sa sāyakārto vicacāla rājā</seg>
            <seg type="pāda" n="d">kṛcchrāc ca saṃjñāṃ punar āsasāda</seg>
        </li>
    </ul>
    <ul id="R_6.047.101">
        <li id="R_6.047.101ab">
            <seg type="pāda" n="a">sa kṛttacāpaḥ śaratāḍitaś ca</seg>
            <seg type="pāda" n="b">svedārdragātro rudhirāvasiktaḥ</seg>
        </li>
        <li id="R_6.047.101cd">
            <seg type="pāda" n="c">jagrāha śaktiṃ samudagraśaktiḥ</seg>
            <seg type="pāda" n="d">svayambhudattāṃ yudhi devaśatruḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.102">
        <li id="R_6.047.102ab">
            <seg type="pāda" n="a">sa tāṃ vidhūmānalasaṃnikāśāṃ</seg>
            <seg type="pāda" n="b">vitrāsanīṃ vānaravāhinīnām</seg>
        </li>
        <li id="R_6.047.102cd">
            <seg type="pāda" n="c">cikṣepa śaktiṃ tarasā jvalantīṃ</seg>
            <seg type="pāda" n="d">saumitraye rākṣasarāṣṭranāthaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.103">
        <li id="R_6.047.103ab">
            <seg type="pāda" n="a">tām āpatantīṃ bharatānujo 'strair</seg>
            <seg type="pāda" n="b">jaghāna bāṇaiś ca hutāgnikalpaiḥ</seg>
        </li>
        <li id="R_6.047.103cd">
            <seg type="pāda" n="c">tathāpi sā tasya viveśa śaktir</seg>
            <seg type="pāda" n="d">bhujāntaraṃ dāśarather viśālam</seg>
        </li>
    </ul>
    <ul id="R_6.047.104">
        <li id="R_6.047.104ab">śaktyā brāmyā tu saumitris tāḍitas tu stanāntare</li>
        <li id="R_6.047.104cd">viṣṇor acintyaṃ svaṃ bhāgam ātmānaṃ pratyanusmarat</li>
    </ul>
    <ul id="R_6.047.105">
        <li id="R_6.047.105ab">tato dānavadarpaghnaṃ saumitriṃ devakaṇṭakaḥ</li>
        <li id="R_6.047.105cd">taṃ pīḍayitvā bāhubhyām aprabhur laṅghane 'bhavat</li>
    </ul>
    <ul id="R_6.047.106">
        <li id="R_6.047.106ab">himavān mandaro merus trailokyaṃ vā sahāmaraiḥ</li>
        <li id="R_6.047.106cd">śakyaṃ bhujābhyām uddhartuṃ na saṃkhye bharatānujaḥ</li>
    </ul>
    <ul id="R_6.047.107">
        <li id="R_6.047.107ab">athainaṃ vaiṣṇavaṃ bhāgaṃ mānuṣaṃ deham āsthitam</li>
        <li id="R_6.047.107cd">visaṃjñaṃ lakṣmaṇaṃ dṛṣṭvā rāvaṇo vismito 'bhavat</li>
    </ul>
    <ul id="R_6.047.108">
        <li id="R_6.047.108ab">atha vāyusutaḥ kruddho rāvaṇaṃ samabhidravat</li>
        <li id="R_6.047.108cd">ājaghānorasi kruddho vajrakalpena muṣṭinā</li>
    </ul>
    <ul id="R_6.047.109">
        <li id="R_6.047.109ab">tena muṣṭiprahāreṇa rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.047.109cd">jānubhyām apatad bhūmau cacāla ca papāta ca</li>
    </ul>
    <ul id="R_6.047.110">
        <li id="R_6.047.110ab">visaṃjñaṃ rāvaṇaṃ dṛṣṭvā samare bhīmavikramam</li>
        <li id="R_6.047.110cd">ṛṣayo vānarāś caiva nedur devāḥ savāsavāḥ</li>
    </ul>
    <ul id="R_6.047.111">
        <li id="R_6.047.111ab">hanūmān api tejasvī lakṣmaṇaṃ rāvaṇārditam</li>
        <li id="R_6.047.111cd">anayad rāghavābhyāśaṃ bāhubhyāṃ parigṛhya tam</li>
    </ul>
    <ul id="R_6.047.112">
        <li id="R_6.047.112ab">vāyusūnoḥ suhṛttvena bhaktyā paramayā ca saḥ</li>
        <li id="R_6.047.112cd">śatrūṇām aprakampyo 'pi laghutvam agamat kapeḥ</li>
    </ul>
    <ul id="R_6.047.113">
        <li id="R_6.047.113ab">taṃ samutsṛjya sā śaktiḥ saumitriṃ yudhi durjayam</li>
        <li id="R_6.047.113cd">rāvaṇasya rathe tasmin sthānaṃ punar upāgamat</li>
    </ul>
    <ul id="R_6.047.114">
        <li id="R_6.047.114ab">rāvaṇo 'pi mahātejāḥ prāpya saṃjñāṃ mahāhave</li>
        <li id="R_6.047.114cd">ādade niśitān bāṇāñ jagrāha ca mahad dhanuḥ</li>
    </ul>
    <ul id="R_6.047.115">
        <li id="R_6.047.115ab">āśvastaś ca viśalyaś ca lakṣmaṇaḥ śatrusūdanaḥ</li>
        <li id="R_6.047.115cd">viṣṇor bhāgam amīmāṃsyam ātmānaṃ pratyanusmaran</li>
    </ul>
    <ul id="R_6.047.116">
        <li id="R_6.047.116ab">nipātitamahāvīrāṃ vānarāṇāṃ mahācamūm</li>
        <li id="R_6.047.116cd">rāghavas tu raṇe dṛṣṭvā rāvaṇaṃ samabhidravat</li>
    </ul>
    <ul id="R_6.047.117">
        <li id="R_6.047.117ab">athainam upasaṃgamya hanūmān vākyam abravīt</li>
        <li id="R_6.047.117cd">mama pṛṣṭhaṃ samāruhya rakṣasaṃ śāstum arhasi</li>
    </ul>
    <ul id="R_6.047.118">
        <li id="R_6.047.118ab">tac chrutvā rāghavo vākyaṃ vāyuputreṇa bhāṣitam</li>
        <li id="R_6.047.118cd">ārohat sahasā śūro hanūmantaṃ mahākapim</li>
        <li id="R_6.047.118ef">rathasthaṃ rāvaṇaṃ saṃkhye dadarśa manujādhipaḥ</li>
    </ul>
    <ul id="R_6.047.119">
        <li id="R_6.047.119ab">tam ālokya mahātejāḥ pradudrāva sa rāghavaḥ</li>
        <li id="R_6.047.119cd">vairocanam iva kruddho viṣṇur abhyudyatāyudhaḥ</li>
    </ul>
    <ul id="R_6.047.120">
        <li id="R_6.047.120ab">jyāśabdam akarot tīvraṃ vajraniṣpeṣanisvanam</li>
        <li id="R_6.047.120cd">girā gambhīrayā rāmo rākṣasendram uvāca ha</li>
    </ul>
    <ul id="R_6.047.121">
        <li id="R_6.047.121ab">tiṣṭha tiṣṭha mama tvaṃ hi kṛtvā vipriyam īdṛśam</li>
        <li id="R_6.047.121cd">kva nu rākṣasaśārdūla gato mokṣam avāpsyasi</li>
    </ul>
    <ul id="R_6.047.122">
        <li id="R_6.047.122ab">
            <seg type="pāda" n="a">yadīndravaivasvata bhāskarān vā</seg>
            <seg type="pāda" n="b">svayambhuvaiśvānaraśaṃkarān vā</seg>
        </li>
        <li id="R_6.047.122cd">
            <seg type="pāda" n="c">gamiṣyasi tvaṃ daśa vā diśo vā</seg>
            <seg type="pāda" n="d">tathāpi me nādya gato vimokṣyase</seg>
        </li>
    </ul>
    <ul id="R_6.047.123">
        <li id="R_6.047.123ab">
            <seg type="pāda" n="a">yaś caiṣa śaktyābhihatas tvayādya</seg>
            <seg type="pāda" n="b">icchan viṣādaṃ sahasābhyupetaḥ</seg>
        </li>
        <li id="R_6.047.123cd">
            <seg type="pāda" n="c">sa eṣa rakṣogaṇarāja mṛtyuḥ</seg>
            <seg type="pāda" n="d">saputradārasya tavādya yuddhe</seg>
        </li>
    </ul>
    <ul id="R_6.047.124">
        <li id="R_6.047.124ab">rāghavasya vacaḥ śrutvā rākṣasendro mahākapim</li>
        <li id="R_6.047.124cd">ājaghāna śarais tīkṣṇaiḥ kālānalaśikhopamaiḥ</li>
    </ul>
    <ul id="R_6.047.125">
        <li id="R_6.047.125ab">rākṣasenāhave tasya tāḍitasyāpi sāyakaiḥ</li>
        <li id="R_6.047.125cd">svabhāvatejoyuktasya bhūyas tejo vyavardhata</li>
    </ul>
    <ul id="R_6.047.126">
        <li id="R_6.047.126ab">tato rāmo mahātejā rāvaṇena kṛtavraṇam</li>
        <li id="R_6.047.126cd">dṛṣṭvā plavagaśārdūlaṃ krodhasya vaśam eyivān</li>
    </ul>
    <ul id="R_6.047.127">
        <li id="R_6.047.127ab">
            <seg type="pāda" n="a">tasyābhisaṃkramya rathaṃ sacakraṃ</seg>
            <seg type="pāda" n="b">sāśvadhvajacchatramahāpatākam</seg>
        </li>
        <li id="R_6.047.127cd">
            <seg type="pāda" n="c">sasārathiṃ sāśaniśūlakhaḍgaṃ</seg>
            <seg type="pāda" n="d">rāmaḥ praciccheda śaraiḥ supuṅkhaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.128">
        <li id="R_6.047.128ab">
            <seg type="pāda" n="a">athendraśatruṃ tarasā jaghāna</seg>
            <seg type="pāda" n="b">bāṇena vajrāśanisaṃnibhena</seg>
        </li>
        <li id="R_6.047.128cd">
            <seg type="pāda" n="c">bhujāntare vyūḍhasujātarūpe</seg>
            <seg type="pāda" n="d">vajreṇa meruṃ bhagavān ivendraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.129">
        <li id="R_6.047.129ab">
            <seg type="pāda" n="a">yo vajrapātāśanisaṃnipātān</seg>
            <seg type="pāda" n="b">na cukṣubhe nāpi cacāla rājā</seg>
        </li>
        <li id="R_6.047.129cd">
            <seg type="pāda" n="c">sa rāmabāṇābhihato bhṛśārtaś</seg>
            <seg type="pāda" n="d">cacāla cāpaṃ ca mumoca vīraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.130">
        <li id="R_6.047.130ab">
            <seg type="pāda" n="a">taṃ vihvalantaṃ prasamīkṣya rāmaḥ</seg>
            <seg type="pāda" n="b">samādade dīptam athārdhacandram</seg>
        </li>
        <li id="R_6.047.130cd">
            <seg type="pāda" n="c">tenārkavarṇaṃ sahasā kirīṭaṃ</seg>
            <seg type="pāda" n="d">ciccheda rakṣo'dhipater mahātmāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.047.131">
        <li id="R_6.047.131ab">
            <seg type="pāda" n="a">taṃ nirviṣāśīviṣasaṃnikāśaṃ</seg>
            <seg type="pāda" n="b">śāntārciṣaṃ sūryam ivāprakāśam</seg>
        </li>
        <li id="R_6.047.131cd">
            <seg type="pāda" n="c">gataśriyaṃ kṛttakirīṭakūṭam</seg>
            <seg type="pāda" n="d">uvāca rāmo yudhi rākṣasendram</seg>
        </li>
    </ul>
    <ul id="R_6.047.132">
        <li id="R_6.047.132ab">
            <seg type="pāda" n="a">kṛtaṃ tvayā karma mahat subhīmaṃ</seg>
            <seg type="pāda" n="b">hatapravīraś ca kṛtas tvayāham</seg>
        </li>
        <li id="R_6.047.132cd">
            <seg type="pāda" n="c">tasmāt pariśrānta iti vyavasya</seg>
            <seg type="pāda" n="d">na tvaṃ śarair mṛtyuvaśaṃ nayāmi</seg>
        </li>
    </ul>
    <ul id="R_6.047.133">
        <li id="R_6.047.133ab">
            <seg type="pāda" n="a">sa evam ukto hatadarpaharṣo</seg>
            <seg type="pāda" n="b">nikṛttacāpaḥ sa hatāśvasūtaḥ</seg>
        </li>
        <li id="R_6.047.133cd">
            <seg type="pāda" n="c">śarārditaḥ kṛttamahākirīṭo</seg>
            <seg type="pāda" n="d">viveśa laṅkāṃ sahasā sma rājā</seg>
        </li>
    </ul>
    <ul id="R_6.047.134">
        <li id="R_6.047.134ab">
            <seg type="pāda" n="a">tasmin praviṣṭe rajanīcarendre</seg>
            <seg type="pāda" n="b">mahābale dānavadevaśatrau</seg>
        </li>
        <li id="R_6.047.134cd">
            <seg type="pāda" n="c">harīn viśalyān sahalakṣmaṇena</seg>
            <seg type="pāda" n="d">cakāra rāmaḥ paramāhavāgre</seg>
        </li>
    </ul>
    <ul id="R_6.047.135">
        <li id="R_6.047.135ab">
            <seg type="pāda" n="a">tasmin prabhagne tridaśendraśatrau</seg>
            <seg type="pāda" n="b">surāsurā bhūtagaṇā diśaś ca</seg>
        </li>
        <li id="R_6.047.135cd">
            <seg type="pāda" n="c">sasāgarāḥ sarṣimahoragāś ca</seg>
            <seg type="pāda" n="d">tathaiva bhūmyambucarāś ca hṛṣṭāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.048.001">
        <li id="R_6.048.001ab">sa praviśya purīṃ laṅkāṃ rāmabāṇabhayārditaḥ</li>
        <li id="R_6.048.001cd">bhagnadarpas tadā rājā babhūva vyathitendriyaḥ</li>
    </ul>
    <ul id="R_6.048.002">
        <li id="R_6.048.002ab">mātaṃga iva siṃhena garuḍeneva pannagaḥ</li>
        <li id="R_6.048.002cd">abhibhūto 'bhavad rājā rāghaveṇa mahātmanā</li>
    </ul>
    <ul id="R_6.048.003">
        <li id="R_6.048.003ab">brahmadaṇḍaprakāśānāṃ vidyutsadṛśavarcasām</li>
        <li id="R_6.048.003cd">smaran rāghavabāṇānāṃ vivyathe rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_6.048.004">
        <li id="R_6.048.004ab">sa kāñcanamayaṃ divyam āśritya paramāsanam</li>
        <li id="R_6.048.004cd">vikprekṣamāṇo rakṣāṃsi rāvaṇo vākyam abravīt</li>
    </ul>
    <ul id="R_6.048.005">
        <li id="R_6.048.005ab">sarvaṃ tat khalu me moghaṃ yat taptaṃ paramaṃ tapaḥ</li>
        <li id="R_6.048.005cd">yat samāno mahendreṇa mānuṣeṇāsmi nirjitaḥ</li>
    </ul>
    <ul id="R_6.048.006">
        <li id="R_6.048.006ab">idaṃ tad brahmaṇo ghoraṃ vākyaṃ mām abhyupasthitam</li>
        <li id="R_6.048.006cd">mānuṣebhyo vijānīhi bhayaṃ tvam iti tat tathā</li>
    </ul>
    <ul id="R_6.048.007">
        <li id="R_6.048.007ab">devadānavagandharvair yakṣarākṣasapannagaiḥ</li>
        <li id="R_6.048.007cd">avadhyatvaṃ mayā prāptaṃ mānuṣebhyo na yācitam</li>
    </ul>
    <ul id="R_6.048.008">
        <li id="R_6.048.008ab">etad evābhyupāgamya yatnaṃ kartum ihārhatha</li>
        <li id="R_6.048.008cd">rākṣasāś cāpi tiṣṭhantu caryāgopuramūrdhasu</li>
    </ul>
    <ul id="R_6.048.009">
        <li id="R_6.048.009ab">sa cāpratimagambhīro devadānavadarpahā</li>
        <li id="R_6.048.009cd">brahmaśāpābhibhūtas tu kumbhakarṇo vibodhyatām</li>
    </ul>
    <ul id="R_6.048.010">
        <li id="R_6.048.010ab">sa parājitam ātmānaṃ prahastaṃ ca niṣūditam</li>
        <li id="R_6.048.010cd">jñātvā rakṣobalaṃ bhīmam ādideśa mahābalaḥ</li>
    </ul>
    <ul id="R_6.048.011">
        <li id="R_6.048.011ab">dvāreṣu yatnaḥ kriyatāṃ prākārāś cādhiruhyatām</li>
        <li id="R_6.048.011cd">nidrāvaśasamāviṣṭaḥ kumbhakarṇo vibodhyatām</li>
    </ul>
    <ul id="R_6.048.012">
        <li id="R_6.048.012ab">nava ṣaṭ sapta cāṣṭau ca māsān svapiti rākṣasaḥ</li>
        <li id="R_6.048.012cd">taṃ tu bodhayata kṣipraṃ kumbhakarṇaṃ mahābalam</li>
    </ul>
    <ul id="R_6.048.013">
        <li id="R_6.048.013ab">sa hi saṃkhye mahābāhuḥ kakudaṃ sarvarakṣasām</li>
        <li id="R_6.048.013cd">vānarān rājaputrau ca kṣipram eva vadhiṣyati</li>
    </ul>
    <ul id="R_6.048.014">
        <li id="R_6.048.014ab">kumbhakarṇaḥ sadā śete mūḍho grāmyasukhe rataḥ</li>
        <li id="R_6.048.014cd">rāmeṇābhinirastasya saṃgrāmo 'smin sudāruṇe</li>
    </ul>
    <ul id="R_6.048.015">
        <li id="R_6.048.015ab">bhaviṣyati na me śokaḥ kumbhakarṇe vibodhite</li>
        <li id="R_6.048.015cd">kiṃ kariṣyāmy ahaṃ tena śakratulyabalena hi</li>
    </ul>
    <ul id="R_6.048.016">
        <li id="R_6.048.016ab">īdṛśe vyasane prāpte yo na sāhyāya kalpate</li>
        <li id="R_6.048.016cd">te tu tad vacanaṃ śrutvā rākṣasendrasya rākṣasāḥ</li>
    </ul>
    <ul id="R_6.048.017">
        <li id="R_6.048.017ab">jagmuḥ paramasaṃbhrāntāḥ kumbhakarṇaniveśanam</li>
        <li id="R_6.048.017cd">te rāvaṇasamādiṣṭā māṃsaśoṇitabhojanāḥ</li>
    </ul>
    <ul id="R_6.048.018">
        <li id="R_6.048.018ab">gandhamālyāṃs tathā bhakṣyān ādāya sahasā yayuḥ</li>
        <li id="R_6.048.018cd">tāṃ praviśya mahādvārāṃ sarvato yojanāyatām</li>
    </ul>
    <ul id="R_6.048.019">
        <li id="R_6.048.019ab">kumbhakarṇaguhāṃ ramyāṃ sarvagandhapravāhinīm</li>
        <li id="R_6.048.019cd">pratiṣṭhamānāḥ kṛcchreṇa yatnāt praviviśur guhām</li>
    </ul>
    <ul id="R_6.048.020">
        <li id="R_6.048.020ab">tāṃ praviśya guhāṃ ramyāṃ śubhāṃ kāñcanakuṭṭimām</li>
        <li id="R_6.048.020cd">dadṛśur nairṛtavyāghraṃ śayānaṃ bhīmadarśanam</li>
    </ul>
    <ul id="R_6.048.021">
        <li id="R_6.048.021ab">te tu taṃ vikṛtaṃ suptaṃ vikīrṇam iva parvatam</li>
        <li id="R_6.048.021cd">kumbhakarṇaṃ mahānidraṃ sahitāḥ pratyabodhayan</li>
    </ul>
    <ul id="R_6.048.022">
        <li id="R_6.048.022ab">ūrdhvaromāñcitatanuṃ śvasantam iva pannagam</li>
        <li id="R_6.048.022cd">trāsayantaṃ mahāśvāsaiḥ śayānaṃ bhīmadarśanam</li>
    </ul>
    <ul id="R_6.048.023">
        <li id="R_6.048.023ab">bhīmanāsāpuṭaṃ taṃ tu pātālavipulānanam</li>
        <li id="R_6.048.023cd">dadṛśur nairṛtavyāghraṃ kumbhakarṇaṃ mahābalam</li>
    </ul>
    <ul id="R_6.048.024">
        <li id="R_6.048.024ab">tataś cakrur mahātmānaḥ kumbhakarṇāgratas tadā</li>
        <li id="R_6.048.024cd">māṃsānāṃ merusaṃkāśaṃ rāśiṃ paramatarpaṇam</li>
    </ul>
    <ul id="R_6.048.025">
        <li id="R_6.048.025ab">mṛgāṇāṃ mahiṣāṇāṃ ca varāhāṇāṃ ca saṃcayān</li>
        <li id="R_6.048.025cd">cakrur nairṛtaśārdūlā rāśimann asya cādbhutam</li>
    </ul>
    <ul id="R_6.048.026">
        <li id="R_6.048.026ab">tataḥ śoṇitakumbhāṃś ca madyāni vividhāni ca</li>
        <li id="R_6.048.026cd">purastāt kumbhakarṇasya cakrus tridaśaśatravaḥ</li>
    </ul>
    <ul id="R_6.048.027">
        <li id="R_6.048.027ab">lilipuś ca parārdhyena candanena paraṃtapam</li>
        <li id="R_6.048.027cd">divyair ācchādayām āsur mālyair gandhaiḥ sugandhibhiḥ</li>
    </ul>
    <ul id="R_6.048.028">
        <li id="R_6.048.028ab">dhūpaṃ sugandhaṃ sasṛjus tuṣṭuvuś ca paraṃtapam</li>
        <li id="R_6.048.028cd">jaladā iva conedur yātudhānāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_6.048.029">
        <li id="R_6.048.029ab">śaṅkhān āpūrayām āsuḥ śaśāṅkasadṛśaprabhān</li>
        <li id="R_6.048.029cd">tumulaṃ yugapac cāpi vineduś cāpy amarṣitāḥ</li>
    </ul>
    <ul id="R_6.048.030">
        <li id="R_6.048.030ab">nedur āsphoṭayām āsuś cikṣipus te niśācarāḥ</li>
        <li id="R_6.048.030cd">kumbhakarṇavibodhārthaṃ cakrus te vipulaṃ svanam</li>
    </ul>
    <ul id="R_6.048.031">
        <li id="R_6.048.031ab">
            <seg type="pāda" n="a">saśaṅkhabherīpaṭahapraṇādam</seg>
            <seg type="pāda" n="b">āsphoṭitakṣveḍitasiṃhanādam</seg>
        </li>
        <li id="R_6.048.031cd">
            <seg type="pāda" n="c">diśo dravantas tridivaṃ kirantaḥ</seg>
            <seg type="pāda" n="d">śrutvā vihaṃgāḥ sahasā nipetuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.048.032">
        <li id="R_6.048.032ab">
            <seg type="pāda" n="a">yadā bhṛśaṃ tair ninadair mahātmā</seg>
            <seg type="pāda" n="b">na kumbhakarṇo bubudhe prasuptaḥ</seg>
        </li>
        <li id="R_6.048.032cd">
            <seg type="pāda" n="c">tato musuṇḍīmusalāni sarve</seg>
            <seg type="pāda" n="d">rakṣogaṇās te jagṛhur gadāś ca</seg>
        </li>
    </ul>
    <ul id="R_6.048.033">
        <li id="R_6.048.033ab">
            <seg type="pāda" n="a">taṃ śailaśṛṅgair musalair gadābhir</seg>
            <seg type="pāda" n="b">vṛkṣais talair mudgaramuṣṭibhiś ca</seg>
        </li>
        <li id="R_6.048.033cd">
            <seg type="pāda" n="c">sukhaprasuptaṃ bhuvi kumbhakarṇaṃ</seg>
            <seg type="pāda" n="d">rakṣāṃsy udagrāṇi tadā nijaghnuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.048.034">
        <li id="R_6.048.034ab">tasya niśvāsavātena kumbhakarṇasya rakṣasaḥ</li>
        <li id="R_6.048.034cd">rākṣasā balavanto 'pi sthātuṃ nāśaknuvan puraḥ</li>
    </ul>
    <ul id="R_6.048.035">
        <li id="R_6.048.035ab">tato 'sya purato gāḍhaṃ rākṣasā bhīmavikramāḥ</li>
        <li id="R_6.048.035cd">mṛdaṅgapaṇavān bherīḥ śaṅkhakumbhagaṇāṃs tathā</li>
        <li id="R_6.048.035ef">daśarākṣasasāhasraṃ yugapat paryavādayan</li>
    </ul>
    <ul id="R_6.048.036">
        <li id="R_6.048.036ab">nīlāñjanacayākāraṃ te tu taṃ pratyabodhayan</li>
        <li id="R_6.048.036cd">abhighnanto nadantaś ca naiva saṃvivide tu saḥ</li>
    </ul>
    <ul id="R_6.048.037">
        <li id="R_6.048.037ab">yadā cainaṃ na śekus te pratibodhayituṃ tadā</li>
        <li id="R_6.048.037cd">tato gurutaraṃ yatnaṃ dāruṇaṃ samupākraman</li>
    </ul>
    <ul id="R_6.048.038">
        <li id="R_6.048.038ab">aśvān uṣṭrān kharān nāgāñ jaghnur daṇḍakaśāṅkuśaiḥ</li>
        <li id="R_6.048.038cd">bherīśaṅkhamṛdaṅgāṃś ca sarvaprāṇair avādayan</li>
    </ul>
    <ul id="R_6.048.039">
        <li id="R_6.048.039ab">nijaghnuś cāsya gātrāṇi mahākāṣṭhakaṭaṃ karaiḥ</li>
        <li id="R_6.048.039cd">mudgarair musalaiś caiva sarvaprāṇasamudyataiḥ</li>
    </ul>
    <ul id="R_6.048.040">
        <li id="R_6.048.040ab">tena śabdena mahatā laṅkā samabhipūritā</li>
        <li id="R_6.048.040cd">saparvatavanā sarvā so 'pi naiva prabudhyate</li>
    </ul>
    <ul id="R_6.048.041">
        <li id="R_6.048.041ab">tataḥ sahasraṃ bherīṇāṃ yugapat samahanyata</li>
        <li id="R_6.048.041cd">mṛṣṭakāñcanakoṇānām asaktānāṃ samantataḥ</li>
    </ul>
    <ul id="R_6.048.042">
        <li id="R_6.048.042ab">evam apy atinidras tu yadā naiva prabudhyata</li>
        <li id="R_6.048.042cd">śāpasya vaśam āpannas tataḥ kruddhā niśācarāḥ</li>
    </ul>
    <ul id="R_6.048.043">
        <li id="R_6.048.043ab">mahākrodhasamāviṣṭāḥ sarve bhīmaparākramāḥ</li>
        <li id="R_6.048.043cd">tad rakṣobodhayiṣyantaś cakrur anye parākramam</li>
    </ul>
    <ul id="R_6.048.044">
        <li id="R_6.048.044ab">anye bherīḥ samājaghnur anye cakrur mahāsvanam</li>
        <li id="R_6.048.044cd">keśān anye pralulupuḥ karṇāv anye daśanti ca</li>
        <li id="R_6.048.044ef">na kumbhakarṇaḥ paspande mahānidrāvaśaṃ gataḥ</li>
    </ul>
    <ul id="R_6.048.045">
        <li id="R_6.048.045ab">anye ca balinas tasya kūṭamudgarapāṇayaḥ</li>
        <li id="R_6.048.045cd">mūrdhni vakṣasi gātreṣu pātayan kūṭamudgarān</li>
    </ul>
    <ul id="R_6.048.046">
        <li id="R_6.048.046ab">rajjubandhanabaddhābhiḥ śataghnībhiś ca sarvataḥ</li>
        <li id="R_6.048.046cd">vadhyamāno mahākāyo na prābudhyata rākṣasaḥ</li>
    </ul>
    <ul id="R_6.048.047">
        <li id="R_6.048.047ab">vāraṇānāṃ sahasraṃ tu śarīre 'sya pradhāvitam</li>
        <li id="R_6.048.047cd">kumbhakarṇas tato buddhaḥ sparśaṃ param abudhyata</li>
    </ul>
    <ul id="R_6.048.048">
        <li id="R_6.048.048ab">
            <seg type="pāda" n="a">sa pātyamānair giriśṛṅgavṛkṣair</seg>
            <seg type="pāda" n="b">acintayaṃs tān vipulān prahārān</seg>
        </li>
        <li id="R_6.048.048cd">
            <seg type="pāda" n="c">nidrākṣayāt kṣudbhayapīḍitaś ca</seg>
            <seg type="pāda" n="d">vijṛmbhamāṇaḥ sahasotpapāta</seg>
        </li>
    </ul>
    <ul id="R_6.048.049">
        <li id="R_6.048.049ab">
            <seg type="pāda" n="a">sa nāgabhogācalaśṛṅgakalpau</seg>
            <seg type="pāda" n="b">vikṣipya bāhū giriśṛṅgasārau</seg>
        </li>
        <li id="R_6.048.049cd">
            <seg type="pāda" n="c">vivṛtya vaktraṃ vaḍavāmukhābhaṃ</seg>
            <seg type="pāda" n="d">niśācaro 'sau vikṛtaṃ jajṛmbhe</seg>
        </li>
    </ul>
    <ul id="R_6.048.050">
        <li id="R_6.048.050ab">tasya jājṛmbhamāṇasya vaktraṃ pātālasaṃnibham</li>
        <li id="R_6.048.050cd">dadṛśe meruśṛṅgāgre divākara ivoditaḥ</li>
    </ul>
    <ul id="R_6.048.051">
        <li id="R_6.048.051ab">vijṛmbhamāṇo 'tibalaḥ pratibuddho niśācaraḥ</li>
        <li id="R_6.048.051cd">niśvāsaś cāsya saṃjajñe parvatād iva mārutaḥ</li>
    </ul>
    <ul id="R_6.048.052">
        <li id="R_6.048.052ab">rūpam uttiṣṭhatas tasya kumbhakarṇasya tad babhau</li>
        <li id="R_6.048.052cd">tapānte sabalākasya meghasyeva vivarṣataḥ</li>
    </ul>
    <ul id="R_6.048.053">
        <li id="R_6.048.053ab">tasya dīptāgnisadṛśe vidyutsadṛśavarcasī</li>
        <li id="R_6.048.053cd">dadṛśāte mahānetre dīptāv iva mahāgrahau</li>
    </ul>
    <ul id="R_6.048.054">
        <li id="R_6.048.054ab">ādad bubhukṣito māṃsaṃ śoṇitaṃ tṛṣito 'pibat</li>
        <li id="R_6.048.054cd">medaḥ kumbhaṃ ca madyaṃ ca papau śakraripus tadā</li>
    </ul>
    <ul id="R_6.048.055">
        <li id="R_6.048.055ab">tatas tṛpta iti jñātvā samutpetur niśācarāḥ</li>
        <li id="R_6.048.055cd">śirobhiś ca praṇamyainaṃ sarvataḥ paryavārayan</li>
    </ul>
    <ul id="R_6.048.056">
        <li id="R_6.048.056ab">sa sarvān sāntvayām āsa nairṛtān nairṛtarṣabhaḥ</li>
        <li id="R_6.048.056cd">bodhanād vismitaś cāpi rākṣasān idam abravīt</li>
    </ul>
    <ul id="R_6.048.057">
        <li id="R_6.048.057ab">kimartham aham āhatya bhavadbhiḥ pratibodhitaḥ</li>
        <li id="R_6.048.057cd">kaccit sukuśalaṃ rājño bhayaṃ vā neha kiṃ cana</li>
    </ul>
    <ul id="R_6.048.058">
        <li id="R_6.048.058ab">atha vā dhruvam anyebhyo bhayaṃ param upasthitam</li>
        <li id="R_6.048.058cd">yadartham eva tvaritair bhavadbhiḥ pratibodhitaḥ</li>
    </ul>
    <ul id="R_6.048.059">
        <li id="R_6.048.059ab">adya rākṣasarājasya bhayam utpāṭayāmy aham</li>
        <li id="R_6.048.059cd">pātayiṣye mahendraṃ vā śātayiṣye tathānalam</li>
    </ul>
    <ul id="R_6.048.060">
        <li id="R_6.048.060ab">na hy alpakāraṇe suptaṃ bodhayiṣyati māṃ bhṛśam</li>
        <li id="R_6.048.060cd">tad ākhyātārthatattvena matprabodhanakāraṇam</li>
    </ul>
    <ul id="R_6.048.061">
        <li id="R_6.048.061ab">evaṃ bruvāṇaṃ saṃrabdhaṃ kumbhakarṇam ariṃdamam</li>
        <li id="R_6.048.061cd">yūpākṣaḥ sacivo rājñaḥ kṛtāñjalir uvāca ha</li>
    </ul>
    <ul id="R_6.048.062">
        <li id="R_6.048.062ab">na no devakṛtaṃ kiṃ cid bhayam asti kadā cana</li>
        <li id="R_6.048.062cd">na daityadānavebhyo vā bhayam asti hi tādṛśam</li>
        <li id="R_6.048.062ef">yādṛśaṃ mānuṣaṃ rājan bhayam asmān upasthitam</li>
    </ul>
    <ul id="R_6.048.063">
        <li id="R_6.048.063ab">vānaraiḥ parvatākārair laṅkeyaṃ parivāritā</li>
        <li id="R_6.048.063cd">sītāharaṇasaṃtaptād rāmān nas tumulaṃ bhayam</li>
    </ul>
    <ul id="R_6.048.064">
        <li id="R_6.048.064ab">ekena vānareṇeyaṃ pūrvaṃ dagdhā mahāpurī</li>
        <li id="R_6.048.064cd">kumāro nihataś cākṣaḥ sānuyātraḥ sakuñjaraḥ</li>
    </ul>
    <ul id="R_6.048.065">
        <li id="R_6.048.065ab">svayaṃ rakṣo'dhipaś cāpi paulastyo devakaṇṭakaḥ</li>
        <li id="R_6.048.065cd">mṛteti saṃyuge muktārāmeṇādityatejasā</li>
    </ul>
    <ul id="R_6.048.066">
        <li id="R_6.048.066ab">yan na devaiḥ kṛto rājā nāpi daityair na dānavaiḥ</li>
        <li id="R_6.048.066cd">kṛtaḥ sa iha rāmeṇa vimuktaḥ prāṇasaṃśayāt</li>
    </ul>
    <ul id="R_6.048.067">
        <li id="R_6.048.067ab">sa yūpākṣavacaḥ śrutvā bhrātur yudhi parājayam</li>
        <li id="R_6.048.067cd">kumbhakarṇo vivṛttākṣo yūpākṣam idam abravīt</li>
    </ul>
    <ul id="R_6.048.068">
        <li id="R_6.048.068ab">sarvam adyaiva yūpākṣa harisainyaṃ salakṣmaṇam</li>
        <li id="R_6.048.068cd">rāghavaṃ ca raṇe hatvā paścād drakṣyāmi rāvaṇam</li>
    </ul>
    <ul id="R_6.048.069">
        <li id="R_6.048.069ab">rākṣasāṃs tarpayiṣyāmi harīṇāṃ māṃsaśoṇitaiḥ</li>
        <li id="R_6.048.069cd">rāmalakṣmaṇayoś cāpi svayaṃ pāsyāmi śoṇitam</li>
    </ul>
    <ul id="R_6.048.070">
        <li id="R_6.048.070ab">
            <seg type="pāda" n="a">tat tasya vākyaṃ bruvato niśamya</seg>
            <seg type="pāda" n="b">sagarvitaṃ roṣavivṛddhadoṣam</seg>
        </li>
        <li id="R_6.048.070cd">
            <seg type="pāda" n="c">mahodaro nairṛtayodhamukhyaḥ</seg>
            <seg type="pāda" n="d">kṛtāñjalir vākyam idaṃ babhāṣe</seg>
        </li>
    </ul>
    <ul id="R_6.048.071">
        <li id="R_6.048.071ab">rāvaṇasya vacaḥ śrutvā guṇadoṣu vimṛśya ca</li>
        <li id="R_6.048.071cd">paścād api mahābāho śatrūn yudhi vijeṣyasi</li>
    </ul>
    <ul id="R_6.048.072">
        <li id="R_6.048.072ab">mahodaravacaḥ śrutvā rākṣasaiḥ parivāritaḥ</li>
        <li id="R_6.048.072cd">kumbhakarṇo mahātejāḥ saṃpratasthe mahābalaḥ</li>
    </ul>
    <ul id="R_6.048.073">
        <li id="R_6.048.073ab">taṃ samutthāpya bhīmākṣaṃ bhīmarūpaparākramam</li>
        <li id="R_6.048.073cd">rākṣasās tvaritā jagmur daśagrīvaniveśanam</li>
    </ul>
    <ul id="R_6.048.074">
        <li id="R_6.048.074ab">tato gatvā daśagrīvam āsīnaṃ paramāsane</li>
        <li id="R_6.048.074cd">ūcur baddhāñjalipuṭāḥ sarva eva niśācarāḥ</li>
    </ul>
    <ul id="R_6.048.075">
        <li id="R_6.048.075ab">prabuddhaḥ kumbhakarṇo 'sau bhrātā te rākṣasarṣabha</li>
        <li id="R_6.048.075cd">kathaṃ tatraiva niryātu drakṣyase tam ihāgatam</li>
    </ul>
    <ul id="R_6.048.076">
        <li id="R_6.048.076ab">rāvaṇas tv abravīd dhṛṣṭo yathānyāyaṃ ca pūjitam</li>
        <li id="R_6.048.076cd">draṣṭum enam ihecchāmi yathānyāyaṃ ca pūjitam</li>
    </ul>
    <ul id="R_6.048.077">
        <li id="R_6.048.077ab">tathety uktvā tu te sarve punar āgamya rākṣasāḥ</li>
        <li id="R_6.048.077cd">kumbhakarṇam idaṃ vākyam ūcū rāvaṇacoditāḥ</li>
    </ul>
    <ul id="R_6.048.078">
        <li id="R_6.048.078ab">draṣṭuṃ tvāṃ kāṅkṣate rājā sarvarākṣasapuṃgavaḥ</li>
        <li id="R_6.048.078cd">gamane kriyatāṃ buddhir bhrātaraṃ saṃpraharṣaya</li>
    </ul>
    <ul id="R_6.048.079">
        <li id="R_6.048.079ab">kumbhakarṇas tu durdharṣo bhrātur ājñāya śāsanam</li>
        <li id="R_6.048.079cd">tathety uktvā mahāvīryaḥ śayanād utpapāta ha</li>
    </ul>
    <ul id="R_6.048.080">
        <li id="R_6.048.080ab">prakṣālya vadanaṃ hṛṣṭaḥ snātaḥ paramabhūṣitaḥ</li>
        <li id="R_6.048.080cd">pipāsus tvarayām āsa pānaṃ balasamīraṇam</li>
    </ul>
    <ul id="R_6.048.081">
        <li id="R_6.048.081ab">tatas te tvaritās tasya rājṣasā rāvaṇājñayā</li>
        <li id="R_6.048.081cd">madyaṃ bhakṣyāṃś ca vividhān kṣipram evopahārayan</li>
    </ul>
    <ul id="R_6.048.082">
        <li id="R_6.048.082ab">pītvā ghaṭasahasraṃ sa gamanāyopacakrame</li>
    </ul>
    <ul id="R_6.048.083">
        <li id="R_6.048.083ab">īṣatsamutkaṭo mattas tejobalasamanvitaḥ</li>
        <li id="R_6.048.083cd">kumbhakarṇo babhau hṛṣṭaḥ kālāntakayamopamaḥ</li>
    </ul>
    <ul id="R_6.048.084">
        <li id="R_6.048.084ab">bhrātuḥ sa bhavanaṃ gacchan rakṣobalasamanvitaḥ</li>
        <li id="R_6.048.084cd">kumbhakarṇaḥ padanyāsair akampayata medinīm</li>
    </ul>
    <ul id="R_6.048.085">
        <li id="R_6.048.085ab">
            <seg type="pāda" n="a">sa rājamārgaṃ vapuṣā prakāśayan</seg>
            <seg type="pāda" n="b">sahasraraśmir dharaṇīm ivāṃśubhiḥ</seg>
        </li>
        <li id="R_6.048.085cd">
            <seg type="pāda" n="c">jagāma tatrāñjalimālayā vṛtaḥ</seg>
            <seg type="pāda" n="d">śatakratur geham iva svayambhuvaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.048.086">
        <li id="R_6.048.086ab">
            <seg type="pāda" n="a">ke cic charaṇyaṃ śaraṇaṃ sma rāmaṃ</seg>
            <seg type="pāda" n="b">vrajanti ke cid vyathitāḥ patanti</seg>
        </li>
        <li id="R_6.048.086cd">
            <seg type="pāda" n="c">ke cid diśaḥ sma vyathitāḥ prayānti</seg>
            <seg type="pāda" n="d">ke cid bhayārtā bhuvi śerate sma</seg>
        </li>
    </ul>
    <ul id="R_6.048.087">
        <li id="R_6.048.087ab">
            <seg type="pāda" n="a">tam adriśṛṅgapratimaṃ kirīṭinaṃ</seg>
            <seg type="pāda" n="b">spṛśantam ādityam ivātmatejasā</seg>
        </li>
        <li id="R_6.048.087cd">
            <seg type="pāda" n="c">vanaukasaḥ prekṣya vivṛddham adbhutaṃ</seg>
            <seg type="pāda" n="d">bhayārditā dudruvire tatas tataḥ</seg>
        </li>
    </ul>
    <ul id="R_6.049.001">
        <li id="R_6.049.001ab">tato rāmo mahātejā dhanur ādāya vīryavān</li>
        <li id="R_6.049.001cd">kirīṭinaṃ mahākāyaṃ kumbhakarṇaṃ dadarśa ha</li>
    </ul>
    <ul id="R_6.049.002">
        <li id="R_6.049.002ab">taṃ dṛṣṭvā rākṣasaśreṣṭhaṃ parvatākāradarśanam</li>
        <li id="R_6.049.002cd">kramamāṇam ivākāśaṃ purā nārāyaṇaṃ prabhum</li>
    </ul>
    <ul id="R_6.049.003">
        <li id="R_6.049.003ab">satoyāmbudasaṃkāśaṃ kāñcanāṅgadabhūṣaṇam</li>
        <li id="R_6.049.003cd">dṛṣṭvā punaḥ pradudrāva vānarāṇāṃ mahācamūḥ</li>
    </ul>
    <ul id="R_6.049.004">
        <li id="R_6.049.004ab">vidrutāṃ vāhinīṃ dṛṣṭvā vardhamānaṃ ca rākṣasaṃ</li>
        <li id="R_6.049.004cd">savismayam idaṃ rāmo vibhīṣaṇam uvāca ha</li>
    </ul>
    <ul id="R_6.049.005">
        <li id="R_6.049.005ab">ko 'sau parvatasaṃkaśaḥ kirīṭī harilocanaḥ</li>
        <li id="R_6.049.005cd">laṅkāyāṃ dṛśyate vīraḥ savidyud iva toyadaḥ</li>
    </ul>
    <ul id="R_6.049.006">
        <li id="R_6.049.006ab">pṛthivyāḥ ketubhūto 'sau mahān eko 'tra dṛśyate</li>
        <li id="R_6.049.006cd">yaṃ dṛṣṭvā vānarāḥ sarve vidravanti tatas tataḥ</li>
    </ul>
    <ul id="R_6.049.007">
        <li id="R_6.049.007ab">ācakṣva me mahān ko 'sau rakṣo vā yadi vāsuraḥ</li>
        <li id="R_6.049.007cd">na mayaivaṃvidhaṃ bhūtaṃ dṛṣṭapūrvaṃ kadā cana</li>
    </ul>
    <ul id="R_6.049.008">
        <li id="R_6.049.008ab">sa pṛṣṭo rājaputreṇa rāmeṇākliṣṭakāriṇā</li>
        <li id="R_6.049.008cd">vibhīṣaṇo mahāprājñaḥ kākutstham idam abravīt</li>
    </ul>
    <ul id="R_6.049.009">
        <li id="R_6.049.009ab">yena vaivasvato yuddhe vāsavaś ca parājitaḥ</li>
        <li id="R_6.049.009cd">saiṣa viśravasaḥ putraḥ kumbhakarṇaḥ pratāpavān</li>
    </ul>
    <ul id="R_6.049.010">
        <li id="R_6.049.010ab">
            <seg type="pāda" n="a">etena devā yudhi dānavāś ca</seg>
            <seg type="pāda" n="b">yakṣā bhujaṃgāḥ piśitāśanāś ca</seg>
        </li>
        <li id="R_6.049.010cd">
            <seg type="pāda" n="c">gandharvavidyādharakiṃnarāś ca</seg>
            <seg type="pāda" n="d">sahasraśo rāghava saṃprabhagnāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.049.011">
        <li id="R_6.049.011ab">śūlapāṇiṃ virūpākṣaṃ kumbhakarṇaṃ mahābalam</li>
        <li id="R_6.049.011cd">hantuṃ na śekus tridaśāḥ kālo 'yam iti mohitāḥ</li>
    </ul>
    <ul id="R_6.049.012">
        <li id="R_6.049.012ab">prakṛtyā hy eṣa tejasvī kumbhakarṇo mahābalaḥ</li>
        <li id="R_6.049.012cd">anyeṣāṃ rākṣasendrāṇāṃ varadānakṛtaṃ balam</li>
    </ul>
    <ul id="R_6.049.013">
        <li id="R_6.049.013ab">etena jātamātreṇa kṣudhārtena mahātmanā</li>
        <li id="R_6.049.013cd">bhakṣitāni sahasrāṇi sattvānāṃ subahūny api</li>
    </ul>
    <ul id="R_6.049.014">
        <li id="R_6.049.014ab">teṣu saṃbhakṣyamāṇeṣu prajā bhayanipīḍitāḥ</li>
        <li id="R_6.049.014cd">yānti sma śaraṇaṃ śakraṃ tam apy arthaṃ nyavedayan</li>
    </ul>
    <ul id="R_6.049.015">
        <li id="R_6.049.015ab">
            <seg type="pāda" n="a">sa kumbhakarṇaṃ kupito mahendro</seg>
            <seg type="pāda" n="b">jaghāna vajreṇa śitena vajrī</seg>
        </li>
        <li id="R_6.049.015cd">
            <seg type="pāda" n="c">sa śakravajrābhihato mahātmā</seg>
            <seg type="pāda" n="d">cacāla kopāc ca bhṛśaṃ nanāda</seg>
        </li>
    </ul>
    <ul id="R_6.049.016">
        <li id="R_6.049.016ab">tasya nānadyamānasya kumbhakarṇasya dhīmataḥ</li>
        <li id="R_6.049.016cd">śrutvā ninādaṃ vitrastā bhūyo bhūmir vitatrase</li>
    </ul>
    <ul id="R_6.049.017">
        <li id="R_6.049.017ab">tataḥ kopān mahendrasya kumbhakarṇo mahābalaḥ</li>
        <li id="R_6.049.017cd">vikṛṣyairāvatād dantaṃ jaghānorasi vāsavam</li>
    </ul>
    <ul id="R_6.049.018">
        <li id="R_6.049.018ab">kumbhakarṇaprahārārto vicacāla sa vāsavaḥ</li>
        <li id="R_6.049.018cd">tato viṣeduḥ sahasā devabrahmarṣidānavāḥ</li>
    </ul>
    <ul id="R_6.049.019">
        <li id="R_6.049.019ab">prajābhiḥ saha śakraś ca yayau sthānaṃ svayambhuvaḥ</li>
        <li id="R_6.049.019cd">kumbhakarṇasya daurātmyaṃ śaśaṃsus te prajāpateḥ</li>
        <li id="R_6.049.019ef">prajānāṃ bhakṣaṇaṃ cāpi devānāṃ cāpi dharṣaṇam</li>
    </ul>
    <ul id="R_6.049.020">
        <li id="R_6.049.020ab">evaṃ prajā yadi tv eṣa bhakṣayiṣyati nityaśaḥ</li>
        <li id="R_6.049.020cd">acireṇaiva kālena śūnyo loko bhaviṣyati</li>
    </ul>
    <ul id="R_6.049.021">
        <li id="R_6.049.021ab">vāsavasya vacaḥ śrutvā sarvalokapitāmahaḥ</li>
        <li id="R_6.049.021cd">rakṣāṃsy āvāhayām āsa kumbhakarṇaṃ dadarśa ha</li>
    </ul>
    <ul id="R_6.049.022">
        <li id="R_6.049.022ab">kumbhakarṇaṃ samīkṣyaiva vitatrāsa prajāpatiḥ</li>
        <li id="R_6.049.022cd">dṛṣṭvā niśvasya caivedaṃ svayambhūr idam abravīt</li>
    </ul>
    <ul id="R_6.049.023">
        <li id="R_6.049.023ab">dhruvaṃ lokavināśāya paurastyenāsi nirmitaḥ</li>
        <li id="R_6.049.023cd">tasmāt tvam adya prabhṛti mṛtakalpaḥ śayiṣyasi</li>
        <li id="R_6.049.023ef">brahmaśāpābhibhūto 'tha nipapātāgrataḥ prabhoḥ</li>
    </ul>
    <ul id="R_6.049.024">
        <li id="R_6.049.024ab">tataḥ paramasaṃbhrānto rāvaṇo vākyam abravīt</li>
        <li id="R_6.049.024cd">vivṛddhaḥ kāñcano vṛkṣaḥ phalakāle nikṛtyate</li>
    </ul>
    <ul id="R_6.049.025">
        <li id="R_6.049.025ab">na naptāraṃ svakaṃ nyāyyaṃ śaptum evaṃ prajāpate</li>
        <li id="R_6.049.025cd">na mithyāvacanaś ca tvaṃ svapsyaty eṣa na saṃśayaḥ</li>
        <li id="R_6.049.025ef">kālas tu kriyatām asya śayane jāgare tathā</li>
    </ul>
    <ul id="R_6.049.026">
        <li id="R_6.049.026ab">rāvaṇasya vacaḥ śrutvā svayambhūr idam abravīt</li>
        <li id="R_6.049.026cd">śayitā hy eṣa ṣaṇ māsān ekāhaṃ jāgariṣyati</li>
    </ul>
    <ul id="R_6.049.027">
        <li id="R_6.049.027ab">ekenāhnā tv asau vīraś caran bhūmiṃ bubhukṣitaḥ</li>
        <li id="R_6.049.027cd">vyāttāsyo bhakṣayel lokān saṃkruddha iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.049.028">
        <li id="R_6.049.028ab">so 'sau vyasanam āpannaḥ kumbhakarṇam abodhayat</li>
        <li id="R_6.049.028cd">tvatparākramabhītaś ca rājā saṃprati rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.049.029">
        <li id="R_6.049.029ab">sa eṣa nirgato vīraḥ śibirād bhīmavikramaḥ</li>
        <li id="R_6.049.029cd">vānarān bhṛśasaṃkruddho bhakṣayan paridhāvati</li>
    </ul>
    <ul id="R_6.049.030">
        <li id="R_6.049.030ab">kumbhakarṇaṃ samīkṣyaiva harayo vipradudruvuḥ</li>
        <li id="R_6.049.030cd">katham enaṃ raṇe kruddhaṃ vārayiṣyanti vānarāḥ</li>
    </ul>
    <ul id="R_6.049.031">
        <li id="R_6.049.031ab">ucyantāṃ vānarāḥ sarve yantram etat samucchritam</li>
        <li id="R_6.049.031cd">iti vijñāya harayo bhaviṣyantīha nirbhayāḥ</li>
    </ul>
    <ul id="R_6.049.032">
        <li id="R_6.049.032ab">vibhīṣaṇavacaḥ śrutvā hetumat sumukhodgatam</li>
        <li id="R_6.049.032cd">uvāca rāghavo vākyaṃ nīlaṃ senāpatiṃ tadā</li>
    </ul>
    <ul id="R_6.049.033">
        <li id="R_6.049.033ab">gaccha sainyāni sarvāṇi vyūhya tiṣṭhasva pāvake</li>
        <li id="R_6.049.033cd">dvārāṇy ādāya laṅkāyāś caryāś cāpy atha saṃkramān</li>
    </ul>
    <ul id="R_6.049.034">
        <li id="R_6.049.034ab">śailaśṛṅgāṇi vṛkṣāṃś ca śilāś cāpy upasaṃharan</li>
        <li id="R_6.049.034cd">tiṣṭhantu vānarāḥ sarve sāyudhāḥ śailapāṇayaḥ</li>
    </ul>
    <ul id="R_6.049.035">
        <li id="R_6.049.035ab">rāghaveṇa samādiṣṭo nīlo haricamūpatiḥ</li>
        <li id="R_6.049.035cd">śaśāsa vānarānīkaṃ yathāvat kapikuñjaraḥ</li>
    </ul>
    <ul id="R_6.049.036">
        <li id="R_6.049.036ab">tato gavākṣaḥ śarabho hanumān aṅgado nalaḥ</li>
        <li id="R_6.049.036cd">śailaśṛṅgāṇi śailābhā gṛhītvā dvāram abhyayuḥ</li>
    </ul>
    <ul id="R_6.049.037">
        <li id="R_6.049.037ab">
            <seg type="pāda" n="a">tato harīṇāṃ tad anīkam ugraṃ</seg>
            <seg type="pāda" n="b">rarāja śailodyatavṛkṣahastam</seg>
        </li>
        <li id="R_6.049.037cd">
            <seg type="pāda" n="c">gireḥ samīpānugataṃ yathaiva</seg>
            <seg type="pāda" n="d">mahan mahāmbhodharajālam ugram</seg>
        </li>
    </ul>
    <ul id="R_6.050.001">
        <li id="R_6.050.001ab">sa tu rākṣasaśārdūlo nidrāmadasamākulaḥ</li>
        <li id="R_6.050.001cd">rājamārgaṃ śriyā juṣṭaṃ yayau vipulavikramaḥ</li>
    </ul>
    <ul id="R_6.050.002">
        <li id="R_6.050.002ab">rākṣasānāṃ sahasraiś ca vṛtaḥ paramadurjayaḥ</li>
        <li id="R_6.050.002cd">gṛhebhyaḥ puṣpavarṣeṇa kāryamāṇas tadā yayau</li>
    </ul>
    <ul id="R_6.050.003">
        <li id="R_6.050.003ab">sa hemajālavitataṃ bhānubhāsvaradarśanam</li>
        <li id="R_6.050.003cd">dadarśa vipulaṃ ramyaṃ rākṣasendraniveśanam</li>
    </ul>
    <ul id="R_6.050.004">
        <li id="R_6.050.004ab">
            <seg type="pāda" n="a">sa tat tadā sūrya ivābhrajālaṃ</seg>
            <seg type="pāda" n="b">praviśya rakṣo'dhipater niveśanam</seg>
        </li>
        <li id="R_6.050.004cd">
            <seg type="pāda" n="c">dadarśa dūre 'grajam āsanasthaṃ</seg>
            <seg type="pāda" n="d">svayambhuvaṃ śakra ivāsanastham</seg>
        </li>
    </ul>
    <ul id="R_6.050.005">
        <li id="R_6.050.005ab">so 'bhigamya gṛhaṃ bhrātuḥ kakṣyām abhivigāhya ca</li>
        <li id="R_6.050.005cd">dadarśodvignam āsīnaṃ vimāne puṣpake gurum</li>
    </ul>
    <ul id="R_6.050.006">
        <li id="R_6.050.006ab">atha dṛṣṭvā daśagrīvaḥ kumbhakarṇam upasthitam</li>
        <li id="R_6.050.006cd">tūrṇam utthāya saṃhṛṣṭaḥ saṃnikarṣam upānayat</li>
    </ul>
    <ul id="R_6.050.007">
        <li id="R_6.050.007ab">athāsīnasya paryaṅke kumbhakarṇo mahābalaḥ</li>
        <li id="R_6.050.007cd">bhrātur vavande caraṇāṃ kiṃ kṛtyam iti cābravīt</li>
        <li id="R_6.050.007ef">utpatya cainaṃ mudito rāvaṇaḥ pariṣasvaje</li>
    </ul>
    <ul id="R_6.050.008">
        <li id="R_6.050.008ab">sa bhrātrā saṃpariṣvakto yathāvac cābhinanditaḥ</li>
        <li id="R_6.050.008cd">kumbhakarṇaḥ śubhaṃ divyaṃ pratipede varāsanam</li>
    </ul>
    <ul id="R_6.050.009">
        <li id="R_6.050.009ab">sa tadāsanam āśritya kumbhakarṇo mahābalaḥ</li>
        <li id="R_6.050.009cd">saṃraktanayanaḥ kopād rāvaṇaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.050.010">
        <li id="R_6.050.010ab">kimartham aham ādṛtya tvayā rājan prabodhitaḥ</li>
        <li id="R_6.050.010cd">śaṃsa kasmād bhayaṃ te 'sti ko 'dya preto bhaviṣyati</li>
    </ul>
    <ul id="R_6.050.011">
        <li id="R_6.050.011ab">bhrātaraṃ rāvaṇaḥ kruddhaṃ kumbhakarṇam avasthitam</li>
        <li id="R_6.050.011cd">īṣat tu parivṛttābhyāṃ netrābhyāṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.050.012">
        <li id="R_6.050.012ab">adya te sumahān kālaḥ śayānasya mahābala</li>
        <li id="R_6.050.012cd">sukhitas tvaṃ na jānīṣe mama rāmakṛtaṃ bhayam</li>
    </ul>
    <ul id="R_6.050.013">
        <li id="R_6.050.013ab">eṣa dāśarathī rāmaḥ sugrīvasahito balī</li>
        <li id="R_6.050.013cd">samudraṃ sabalas tīrtvā mūlaṃ naḥ parikṛntati</li>
    </ul>
    <ul id="R_6.050.014">
        <li id="R_6.050.014ab">hanta paśyasva laṅkāyā vanāny upavanāni ca</li>
        <li id="R_6.050.014cd">setunā sukham āgamya vānaraikārṇavaṃ kṛtam</li>
    </ul>
    <ul id="R_6.050.015">
        <li id="R_6.050.015ab">ye rākṣasā mukhyatamā hatās te vānarair yudhi</li>
        <li id="R_6.050.015cd">vānarāṇāṃ kṣayaṃ yuddhe na paśyāmi kadā cana</li>
    </ul>
    <ul id="R_6.050.016">
        <li id="R_6.050.016ab">sarvakṣapitakośaṃ ca sa tvam abhyavapadya mām</li>
        <li id="R_6.050.016cd">trāyasvemāṃ purīṃ laṅkāṃ bālavṛddhāvaśeṣitām</li>
    </ul>
    <ul id="R_6.050.017">
        <li id="R_6.050.017ab">bhrātur arthe mahābāho kuru karma suduṣkaram</li>
        <li id="R_6.050.017cd">mayaivaṃ noktapūrvo hi kaś cid bhrātaḥ paraṃtapa</li>
        <li id="R_6.050.017ef">tvayy asti mama ca snehaḥ parā saṃbhāvanā ca me</li>
    </ul>
    <ul id="R_6.050.018">
        <li id="R_6.050.018ab">devāsuravimardeṣu bahuśo rākṣasarṣabha</li>
        <li id="R_6.050.018cd">tvayā devāḥ prativyūhya nirjitāś cāsurā yudhi</li>
        <li id="R_6.050.018ef">na hi te sarvabhūteṣu dṛśyate sadṛśo balī</li>
    </ul>
    <ul id="R_6.050.019">
        <li id="R_6.050.019ab">
            <seg type="pāda" n="a">kuruṣva me priyahitam etad uttamaṃ</seg>
            <seg type="pāda" n="b">yathāpriyaṃ priyaraṇabāndhavapriya</seg>
        </li>
        <li id="R_6.050.019cd">
            <seg type="pāda" n="c">svatejasā vidhama sapatnavāhinīṃ</seg>
            <seg type="pāda" n="d">śaradghanaṃ pavana ivodyato mahān</seg>
        </li>
    </ul>
    <ul id="R_6.051.001">
        <li id="R_6.051.001ab">tasya rākṣasarājasya niśamya paridevitam</li>
        <li id="R_6.051.001cd">kumbhakarṇo babhāṣe 'tha vacanaṃ prajahāsa ca</li>
    </ul>
    <ul id="R_6.051.002">
        <li id="R_6.051.002ab">dṛṣṭo doṣo hi yo 'smābhiḥ purā mantravinirṇaye</li>
        <li id="R_6.051.002cd">hiteṣv anabhiyuktena so 'yam āsāditas tvayā</li>
    </ul>
    <ul id="R_6.051.003">
        <li id="R_6.051.003ab">śīghraṃ khalv abhyupetaṃ tvāṃ phalaṃ pāpasya karmaṇaḥ</li>
        <li id="R_6.051.003cd">nirayeṣv eva patanaṃ yathā duṣkṛtakarmaṇaḥ</li>
    </ul>
    <ul id="R_6.051.004">
        <li id="R_6.051.004ab">prathamaṃ vai mahārāja kṛtyam etad acintitam</li>
        <li id="R_6.051.004cd">kevalaṃ vīryadarpeṇa nānubandho vicāritaḥ</li>
    </ul>
    <ul id="R_6.051.005">
        <li id="R_6.051.005ab">yaḥ paścāt pūrvakāryāṇi kuryād aiśvaryam āsthitaḥ</li>
        <li id="R_6.051.005cd">pūrvaṃ cottarakāryāṇi na sa veda nayānayau</li>
    </ul>
    <ul id="R_6.051.006">
        <li id="R_6.051.006ab">deśakālavihīnāni karmāṇi viparītavat</li>
        <li id="R_6.051.006cd">kriyamāṇāni duṣyanti havīṃṣy aprayateṣv iva</li>
    </ul>
    <ul id="R_6.051.007">
        <li id="R_6.051.007ab">trayāṇāṃ pañcadhā yogaṃ karmaṇāṃ yaḥ prapaśyati</li>
        <li id="R_6.051.007cd">sacivaiḥ samayaṃ kṛtvā sa sabhye vartate pathi</li>
    </ul>
    <ul id="R_6.051.008">
        <li id="R_6.051.008ab">yathāgamaṃ ca yo rājā samayaṃ vicikīrṣati</li>
        <li id="R_6.051.008cd">budhyate sacivān buddhyā suhṛdaś cānupaśyati</li>
    </ul>
    <ul id="R_6.051.009">
        <li id="R_6.051.009ab">dharmam arthaṃ ca kāmaṃ ca sarvān vā rakṣasāṃ pate</li>
        <li id="R_6.051.009cd">bhajate puruṣaḥ kāle trīṇi dvandvāni vā punaḥ</li>
    </ul>
    <ul id="R_6.051.010">
        <li id="R_6.051.010ab">triṣu caiteṣu yac chreṣṭhaṃ śrutvā tan nāvabudhyate</li>
        <li id="R_6.051.010cd">rājā vā rājamātro vā vyarthaṃ tasya bahuśrutam</li>
    </ul>
    <ul id="R_6.051.011">
        <li id="R_6.051.011ab">upapradānaṃ sāntvaṃ vā bhedaṃ kāle ca vikramam</li>
        <li id="R_6.051.011cd">yogaṃ ca rakṣasāṃ śreṣṭha tāv ubhau ca nayānayau</li>
    </ul>
    <ul id="R_6.051.012">
        <li id="R_6.051.012ab">kāle dharmārthakāmān yaḥ saṃmantrya sacivaiḥ saha</li>
        <li id="R_6.051.012cd">niṣevetātmavāṃl loke na sa vyasanam āpnuyāt</li>
    </ul>
    <ul id="R_6.051.013">
        <li id="R_6.051.013ab">hitānubandham ālokya kāryākāryam ihātmanaḥ</li>
        <li id="R_6.051.013cd">rājā sahārthatattvajñaiḥ sacivaiḥ saha jīvati</li>
    </ul>
    <ul id="R_6.051.014">
        <li id="R_6.051.014ab">anabhijñāya śāstrārthān puruṣāḥ paśubuddhayaḥ</li>
        <li id="R_6.051.014cd">prāgalbhyād vaktum icchanti mantreṣv abhyantarīkṛtāḥ</li>
    </ul>
    <ul id="R_6.051.015">
        <li id="R_6.051.015ab">aśāstraviduṣāṃ teṣāṃ na kāryam ahitaṃ vacaḥ</li>
        <li id="R_6.051.015cd">arthaśāstrānabhijñānāṃ vipulāṃ śriyam icchatām</li>
    </ul>
    <ul id="R_6.051.016">
        <li id="R_6.051.016ab">ahitaṃ ca hitākāraṃ dhārṣṭyāj jalpanti ye narāḥ</li>
        <li id="R_6.051.016cd">avekṣya mantrabāhyās te kartavyāḥ kṛtyadūṣaṇāḥ</li>
    </ul>
    <ul id="R_6.051.017">
        <li id="R_6.051.017ab">vināśayanto bhartāraṃ sahitāḥ śatrubhir budhaiḥ</li>
        <li id="R_6.051.017cd">viparītāni kṛtyāni kārayantīha mantriṇaḥ</li>
    </ul>
    <ul id="R_6.051.018">
        <li id="R_6.051.018ab">tān bhartā mitrasaṃkāśān amitrān mantranirṇaye</li>
        <li id="R_6.051.018cd">vyavahāreṇa jānīyāt sacivān upasaṃhitān</li>
    </ul>
    <ul id="R_6.051.019">
        <li id="R_6.051.019ab">capalasyeha kṛtyāni sahasānupradhāvataḥ</li>
        <li id="R_6.051.019cd">chidram anye prapadyante krauñcasya kham iva dvijāḥ</li>
    </ul>
    <ul id="R_6.051.020">
        <li id="R_6.051.020ab">yo hi śatrum avajñāya nātmānam abhirakṣati</li>
        <li id="R_6.051.020cd">avāpnoti hi so 'narthān sthānāc ca vyavaropyate</li>
    </ul>
    <ul id="R_6.051.021">
        <li id="R_6.051.021ab">tat tu śrutvā daśagrīvaḥ kumbhakarṇasya bhāṣitam</li>
        <li id="R_6.051.021cd">bhrukuṭiṃ caiva saṃcakre kruddhaś cainam uvāca ha</li>
    </ul>
    <ul id="R_6.051.022">
        <li id="R_6.051.022ab">mānyo gurur ivācāryaḥ kiṃ māṃ tvam anuśāsati</li>
        <li id="R_6.051.022cd">kim evaṃ vākśramaṃ kṛtvā kāle yuktaṃ vidhīyatām</li>
    </ul>
    <ul id="R_6.051.023">
        <li id="R_6.051.023ab">vibhramāc cittamohād vā balavīryāśrayeṇa vā</li>
        <li id="R_6.051.023cd">nābhipannam idānīṃ yad vyarthās tasya punaḥ kṛthāḥ</li>
    </ul>
    <ul id="R_6.051.024">
        <li id="R_6.051.024ab">asmin kāle tu yad yuktaṃ tad idānīṃ vidhīyatām</li>
        <li id="R_6.051.024cd">mamāpanayajaṃ doṣaṃ vikrameṇa samīkuru</li>
    </ul>
    <ul id="R_6.051.025">
        <li id="R_6.051.025ab">yadi khalv asti me sneho bhrātṛtvaṃ vāvagacchasi</li>
        <li id="R_6.051.025cd">yadi vā kāryam etat te hṛdi kāryatamaṃ matam</li>
    </ul>
    <ul id="R_6.051.026">
        <li id="R_6.051.026ab">sa suhṛdyo vipannārthaṃ dīnam abhyavapadyate</li>
        <li id="R_6.051.026cd">sa bandhur yo 'panīteṣu sāhāyyāyopakalpate</li>
    </ul>
    <ul id="R_6.051.027">
        <li id="R_6.051.027ab">tam athaivaṃ bruvāṇaṃ tu vacanaṃ dhīradāruṇam</li>
        <li id="R_6.051.027cd">ruṣṭo 'yam iti vijñāya śanaiḥ ślakṣṇam uvāca ha</li>
    </ul>
    <ul id="R_6.051.028">
        <li id="R_6.051.028ab">atīva hi samālakṣya bhrātaraṃ kṣubhitendriyam</li>
        <li id="R_6.051.028cd">kumbhakarṇaḥ śanair vākyaṃ babhāṣe parisāntvayan</li>
    </ul>
    <ul id="R_6.051.029">
        <li id="R_6.051.029ab">alaṃ rākṣasarājendra saṃtāpam upapadya te</li>
        <li id="R_6.051.029cd">roṣaṃ ca saṃparityajya svastho bhavitum arhasi</li>
    </ul>
    <ul id="R_6.051.030">
        <li id="R_6.051.030ab">naitan manasi kartavvyaṃ mayi jīvati pārthiva</li>
        <li id="R_6.051.030cd">tam ahaṃ nāśayiṣyāmi yatkṛte paritapyase</li>
    </ul>
    <ul id="R_6.051.031">
        <li id="R_6.051.031ab">avaśyaṃ tu hitaṃ vācyaṃ sarvāvasthaṃ mayā tava</li>
        <li id="R_6.051.031cd">bandhubhāvād abhihitaṃ bhrātṛsnehāc ca pārthiva</li>
    </ul>
    <ul id="R_6.051.032">
        <li id="R_6.051.032ab">sadṛśaṃ yat tu kāle 'smin kartuṃ snigdhena bandhunā</li>
        <li id="R_6.051.032cd">śatrūṇāṃ kadanaṃ paśya kriyamāṇaṃ mayā raṇe</li>
    </ul>
    <ul id="R_6.051.033">
        <li id="R_6.051.033ab">adya paśya mahābāho mayā samaramūrdhani</li>
        <li id="R_6.051.033cd">hate rāme saha bhrātrā dravantīṃ harivāhinīm</li>
    </ul>
    <ul id="R_6.051.034">
        <li id="R_6.051.034ab">adya rāmasya tad dṛṣṭvā mayānītaṃ raṇāc chiraḥ</li>
        <li id="R_6.051.034cd">sukhībhava mahābāho sītā bhavatu duḥkhitā</li>
    </ul>
    <ul id="R_6.051.035">
        <li id="R_6.051.035ab">adya rāmasya paśyantu nidhanaṃ sumahat priyam</li>
        <li id="R_6.051.035cd">laṅkāyāṃ rākṣasāḥ sarve ye te nihatabāndhavāḥ</li>
    </ul>
    <ul id="R_6.051.036">
        <li id="R_6.051.036ab">adya śokaparītānāṃ svabandhuvadhakāraṇāt</li>
        <li id="R_6.051.036cd">śatror yudhi vināśena karomy asrapramārjanam</li>
    </ul>
    <ul id="R_6.051.037">
        <li id="R_6.051.037ab">adya parvatasaṃkāśaṃ sasūryam iva toyadam</li>
        <li id="R_6.051.037cd">vikīrṇaṃ paśya samare sugrīvaṃ plavageśvaram</li>
    </ul>
    <ul id="R_6.051.038">
        <li id="R_6.051.038ab">na paraḥ preṣaṇīyas te yuddhāyātula vikrama</li>
        <li id="R_6.051.038cd">aham utsādayiṣyāmi śatrūṃs tava mahābala</li>
    </ul>
    <ul id="R_6.051.039">
        <li id="R_6.051.039ab">yadi śakro yadi yamo yadi pāvakamārutau</li>
        <li id="R_6.051.039cd">tān ahaṃ yodhayiṣyāmi kubera varuṇāv api</li>
    </ul>
    <ul id="R_6.051.040">
        <li id="R_6.051.040ab">girimātraśarīrasya śitaśūladharasya me</li>
        <li id="R_6.051.040cd">nardatas tīkṣṇadaṃṣṭrasya bibhīyāc ca puraṃdaraḥ</li>
    </ul>
    <ul id="R_6.051.041">
        <li id="R_6.051.041ab">atha vā tyaktaśastrasya mṛdgatas tarasā ripūn</li>
        <li id="R_6.051.041cd">na me pratimukhe kaś cic chaktaḥ sthātuṃ jijīviṣuḥ</li>
    </ul>
    <ul id="R_6.051.042">
        <li id="R_6.051.042ab">naiva śaktyā na gadayā nāsinā na śitaiḥ śaraiḥ</li>
        <li id="R_6.051.042cd">hastābhyām eva saṃrabdho haniṣyāmy api vajriṇam</li>
    </ul>
    <ul id="R_6.051.043">
        <li id="R_6.051.043ab">yadi me muṣṭivegaṃ sa rāghavo 'dya sahiṣyati</li>
        <li id="R_6.051.043cd">tataḥ pāsyanti bāṇaughā rudhiraṃ rāghavasya te</li>
    </ul>
    <ul id="R_6.051.044">
        <li id="R_6.051.044ab">cintayā bādhyase rājan kimarthaṃ mayi tiṣṭhati</li>
        <li id="R_6.051.044cd">so 'haṃ śatruvināśāya tava niryātum udyataḥ</li>
    </ul>
    <ul id="R_6.051.045">
        <li id="R_6.051.045ab">muñca rāmād bhayaṃ rājan haniṣyāmīha saṃyuge</li>
        <li id="R_6.051.045cd">rāghavaṃ lakṣmaṇaṃ caiva sugrīvaṃ ca mahābalam</li>
        <li id="R_6.051.045ef">asādhāraṇam icchāmi tava dātuṃ mahad yaśaḥ</li>
    </ul>
    <ul id="R_6.051.046">
        <li id="R_6.051.046ab">
            <seg type="pāda" n="a">vadhena te dāśaratheḥ sukhāvahaṃ</seg>
            <seg type="pāda" n="b">sukhaṃ samāhartum ahaṃ vrajāmi</seg>
        </li>
        <li id="R_6.051.046cd">
            <seg type="pāda" n="c">nihatya rāmaṃ sahalakṣmaṇena</seg>
            <seg type="pāda" n="d">khādāmi sarvān hariyūthamukhyān</seg>
        </li>
    </ul>
    <ul id="R_6.051.047">
        <li id="R_6.051.047ab">
            <seg type="pāda" n="a">ramasva kāmaṃ piba cāgryavāruṇīṃ</seg>
            <seg type="pāda" n="b">kuruṣva kṛtyāni vinīyatāṃ jvaraḥ</seg>
        </li>
        <li id="R_6.051.047cd">
            <seg type="pāda" n="c">mayādya rāme gamite yamakṣayaṃ</seg>
            <seg type="pāda" n="d">cirāya sītā vaśagā bhaviṣyati</seg>
        </li>
    </ul>
    <ul id="R_6.052.001">
        <li id="R_6.052.001ab">tad uktam atikāyasya balino bāhuśālinaḥ</li>
        <li id="R_6.052.001cd">kumbhakarṇasya vacanaṃ śrutvovāca mahodaraḥ</li>
    </ul>
    <ul id="R_6.052.002">
        <li id="R_6.052.002ab">kumbhakarṇakule jāto dhṛṣṭaḥ prākṛtadarśanaḥ</li>
        <li id="R_6.052.002cd">avalipto na śaknoṣi kṛtyaṃ sarvatra veditum</li>
    </ul>
    <ul id="R_6.052.003">
        <li id="R_6.052.003ab">na hi rājā na jānīte kumbhakarṇa nayānayau</li>
        <li id="R_6.052.003cd">tvaṃ tu kaiśorakād dhṛṣṭaḥ kevalaṃ vaktum icchasi</li>
    </ul>
    <ul id="R_6.052.004">
        <li id="R_6.052.004ab">sthānaṃ vṛddhiṃ ca hāniṃ ca deśakālavibhāgavit</li>
        <li id="R_6.052.004cd">ātmanaś ca pareṣāṃ ca budhyate rākṣasarṣabha</li>
    </ul>
    <ul id="R_6.052.005">
        <li id="R_6.052.005ab">yat tu śakyaṃ balavatā kartuṃ prākṛtabuddhinā</li>
        <li id="R_6.052.005cd">anupāsitavṛddhena kaḥ kuryāt tādṛśaṃ budhaḥ</li>
    </ul>
    <ul id="R_6.052.006">
        <li id="R_6.052.006ab">yāṃs tu dharmārthakāmāṃs tvaṃ bravīṣi pṛthag āśrayān</li>
        <li id="R_6.052.006cd">anuboddhuṃ svabhāvena na hi lakṣaṇam asti te</li>
    </ul>
    <ul id="R_6.052.007">
        <li id="R_6.052.007ab">karma caiva hi sarveṣāṃ kāraṇānāṃ prayojanam</li>
        <li id="R_6.052.007cd">śreyaḥ pāpīyasāṃ cātra phalaṃ bhavati karmaṇām</li>
    </ul>
    <ul id="R_6.052.008">
        <li id="R_6.052.008ab">niḥśreyasa phalāv eva dharmārthāv itarāv api</li>
        <li id="R_6.052.008cd">adharmānarthayoḥ prāptiḥ phalaṃ ca pratyavāyikam</li>
    </ul>
    <ul id="R_6.052.009">
        <li id="R_6.052.009ab">aihalaukikapāratryaṃ karma pumbhir niṣevyate</li>
        <li id="R_6.052.009cd">karmāṇy api tu kalpyāni labhate kāmam āsthitaḥ</li>
    </ul>
    <ul id="R_6.052.010">
        <li id="R_6.052.010ab">tatra kḷptam idaṃ rājñā hṛdi kāryaṃ mataṃ ca naḥ</li>
        <li id="R_6.052.010cd">śatrau hi sāhasaṃ yat syāt kim ivātrāpanīyate</li>
    </ul>
    <ul id="R_6.052.011">
        <li id="R_6.052.011ab">ekasyaivābhiyāne tu hetur yaḥ prakṛtas tvayā</li>
        <li id="R_6.052.011cd">tatrāpy anupapannaṃ te vakṣyāmi yad asādhu ca</li>
    </ul>
    <ul id="R_6.052.012">
        <li id="R_6.052.012ab">yena pūrvaṃ janasthāne bahavo 'tibalā hatāḥ</li>
        <li id="R_6.052.012cd">rākṣasā rāghavaṃ taṃ tvaṃ katham eko jayiṣyasi</li>
    </ul>
    <ul id="R_6.052.013">
        <li id="R_6.052.013ab">ye purā nirjitās tena janasthāne mahaujasaḥ</li>
        <li id="R_6.052.013cd">rākṣasāṃs tān pure sarvān bhītān adyāpi paśyasi</li>
    </ul>
    <ul id="R_6.052.014">
        <li id="R_6.052.014ab">taṃ siṃham iva saṃkruddhaṃ rāmaṃ daśarathātmajam</li>
        <li id="R_6.052.014cd">sarpaṃ suptam ivābuddhyā prabodhayitum icchasi</li>
    </ul>
    <ul id="R_6.052.015">
        <li id="R_6.052.015ab">jvalantaṃ tejasā nityaṃ krodhena ca durāsadam</li>
        <li id="R_6.052.015cd">kas taṃ mṛtyum ivāsahyam āsādayitum arhati</li>
    </ul>
    <ul id="R_6.052.016">
        <li id="R_6.052.016ab">saṃśayastham idaṃ sarvaṃ śatroḥ pratisamāsane</li>
        <li id="R_6.052.016cd">ekasya gamanaṃ tatra na hi me rocate tava</li>
    </ul>
    <ul id="R_6.052.017">
        <li id="R_6.052.017ab">hīnārthas tu samṛddhārthaṃ ko ripuṃ prākṛto yathā</li>
        <li id="R_6.052.017cd">niścitaṃ jīvitatyāge vaśam ānetum icchati</li>
    </ul>
    <ul id="R_6.052.018">
        <li id="R_6.052.018ab">yasya nāsti manuṣyeṣu sadṛśo rākṣasottama</li>
        <li id="R_6.052.018cd">katham āśaṃsase yoddhuṃ tulyenendravivasvatoḥ</li>
    </ul>
    <ul id="R_6.052.019">
        <li id="R_6.052.019ab">evam uktvā tu saṃrabdhaṃ kumbhakarṇaṃ mahodaraḥ</li>
        <li id="R_6.052.019cd">uvāca rakṣasāṃ madhye rāvaṇo lokarāvaṇam</li>
    </ul>
    <ul id="R_6.052.020">
        <li id="R_6.052.020ab">labdhvā punas tāṃ vaidehīṃ kimarthaṃ tvaṃ prajalpasi</li>
        <li id="R_6.052.020cd">yadecchasi tadā sītā vaśagā te bhaviṣyati</li>
    </ul>
    <ul id="R_6.052.021">
        <li id="R_6.052.021ab">dṛṣṭaḥ kaś cid upāyo me sītopasthānakārakaḥ</li>
        <li id="R_6.052.021cd">rucitaś cet svayā buddhyā rākṣaseśvara taṃ śṛṇu</li>
    </ul>
    <ul id="R_6.052.022">
        <li id="R_6.052.022ab">ahaṃ dvijihvaḥ saṃhrādī kumbhakarṇo vitardanaḥ</li>
        <li id="R_6.052.022cd">pañcarāmavadhāyaite niryāntīty avaghoṣaya</li>
    </ul>
    <ul id="R_6.052.023">
        <li id="R_6.052.023ab">tato gatvā vayaṃ yuddhaṃ dāsyāmas tasya yatnataḥ</li>
        <li id="R_6.052.023cd">jeṣyāmo yadi te śatrūn nopāyaiḥ kṛtyam asti naḥ</li>
    </ul>
    <ul id="R_6.052.024">
        <li id="R_6.052.024ab">atha jīvati naḥ śatrur vayaṃ ca kṛtasaṃyugāḥ</li>
        <li id="R_6.052.024cd">tataḥ samabhipatsyāmo manasā yat samīkṣitum</li>
    </ul>
    <ul id="R_6.052.025">
        <li id="R_6.052.025ab">vayaṃ yuddhād ihaiṣyāmo rudhireṇa samukṣitāḥ</li>
        <li id="R_6.052.025cd">vidārya svatanuṃ bāṇai rāmanāmāṅkitaiḥ śitaiḥ</li>
    </ul>
    <ul id="R_6.052.026">
        <li id="R_6.052.026ab">bhakṣito rāghavo 'smābhir lakṣmaṇaś ceti vādinaḥ</li>
        <li id="R_6.052.026cd">tava pādau grahīṣyāmas tvaṃ naḥ kāma prapūraya</li>
    </ul>
    <ul id="R_6.052.027">
        <li id="R_6.052.027ab">tato 'vaghoṣaya pure gajaskandhena pārthiva</li>
        <li id="R_6.052.027cd">hato rāmaḥ saha bhrātrā sasainya iti sarvataḥ</li>
    </ul>
    <ul id="R_6.052.028">
        <li id="R_6.052.028ab">prīto nāma tato bhūtvā bhṛtyānāṃ tvam ariṃdama</li>
        <li id="R_6.052.028cd">bhogāṃś ca parivārāṃś ca kāmāṃś ca vasudāpaya</li>
    </ul>
    <ul id="R_6.052.029">
        <li id="R_6.052.029ab">tato mālyāni vāsāṃsi vīrāṇām anulepanam</li>
        <li id="R_6.052.029cd">peyaṃ ca bahu yodhebhyaḥ svayaṃ ca muditaḥ piba</li>
    </ul>
    <ul id="R_6.052.030">
        <li id="R_6.052.030ab">tato 'smin bahulībhūte kaulīne sarvato gate</li>
        <li id="R_6.052.030cd">praviśyāśvāsya cāpi tvaṃ sītāṃ rahasi sāntvaya</li>
        <li id="R_6.052.030ef">dhanadhānyaiś ca kāmaiś ca ratnaiś caināṃ pralobhaya</li>
    </ul>
    <ul id="R_6.052.031">
        <li id="R_6.052.031ab">anayopadhayā rājan bhayaśokānubandhayā</li>
        <li id="R_6.052.031cd">akāmā tvadvaśaṃ sītā naṣṭanāthā gamiṣyati</li>
    </ul>
    <ul id="R_6.052.032">
        <li id="R_6.052.032ab">rañjanīyaṃ hi bhartāraṃ vinaṣṭam avagamya sā</li>
        <li id="R_6.052.032cd">nairāśyāt strīlaghutvāc ca tvadvaśaṃ pratipatsyate</li>
    </ul>
    <ul id="R_6.052.033">
        <li id="R_6.052.033ab">sā purā sukhasaṃvṛddhā sukhārhā duḥkhakarṣitā</li>
        <li id="R_6.052.033cd">tvayy adhīnaḥ sukhaṃ jñātvā sarvathopagamiṣyati</li>
    </ul>
    <ul id="R_6.052.034">
        <li id="R_6.052.034ab">
            <seg type="pāda" n="a">etat sunītaṃ mama darśanena</seg>
            <seg type="pāda" n="b">rāmaṃ hi dṛṣṭvaiva bhaved anarthaḥ</seg>
        </li>
        <li id="R_6.052.034cd">
            <seg type="pāda" n="c">ihaiva te setsyati motsuko bhūr</seg>
            <seg type="pāda" n="d">mahān ayuddhena sukhasya lābhaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.052.035">
        <li id="R_6.052.035ab">
            <seg type="pāda" n="a">anaṣṭasainyo hy anavāptasaṃśayo</seg>
            <seg type="pāda" n="b">ripūn ayuddhena jayañ janādhipa</seg>
        </li>
        <li id="R_6.052.035cd">
            <seg type="pāda" n="c">yaśaś ca puṇyaṃ ca mahan mahīpate</seg>
            <seg type="pāda" n="d">śriyaṃ ca kīrtiṃ ca ciraṃ samaśnute</seg>
        </li>
    </ul>
    <ul id="R_6.053.001">
        <li id="R_6.053.001ab">sa tathoktas tu nirbhartsya kumbhakarṇo mahodaram</li>
        <li id="R_6.053.001cd">abravīd rākṣasaśreṣṭhaṃ bhrātaraṃ rāvaṇaṃ tataḥ</li>
    </ul>
    <ul id="R_6.053.002">
        <li id="R_6.053.002ab">so 'haṃ tava bhayaṃ ghoraṃ vadhāt tasya durātmanaḥ</li>
        <li id="R_6.053.002cd">rāmasyādya pramārjāmi nirvairas tvaṃ sukhībhava</li>
    </ul>
    <ul id="R_6.053.003">
        <li id="R_6.053.003ab">garjanti na vṛthā śūra nirjalā iva toyadāḥ</li>
        <li id="R_6.053.003cd">paśya saṃpādyamānaṃ tu garjitaṃ yudhi karmaṇā</li>
    </ul>
    <ul id="R_6.053.004">
        <li id="R_6.053.004ab">na marṣayati cātmānaṃ saṃbhāvayati nātmanā</li>
        <li id="R_6.053.004cd">adarśayitvā śūrās tu karma kurvanti duṣkaram</li>
    </ul>
    <ul id="R_6.053.005">
        <li id="R_6.053.005ab">viklavānām abuddhīnāṃ rājñāṃ paṇḍitamāninām</li>
        <li id="R_6.053.005cd">śṛṇvatām ādita idaṃ tvadvidhānāṃ mahodara</li>
    </ul>
    <ul id="R_6.053.006">
        <li id="R_6.053.006ab">yuddhe kāpuruṣair nityaṃ bhavadbhiḥ priyavādibhiḥ</li>
        <li id="R_6.053.006cd">rājānam anugacchadbhiḥ kṛtyam etad vināśitam</li>
    </ul>
    <ul id="R_6.053.007">
        <li id="R_6.053.007ab">rājaśeṣā kṛtā laṅkā kṣīṇaḥ kośo balaṃ hatam</li>
        <li id="R_6.053.007cd">rājānam imam āsādya suhṛccihnam amitrakam</li>
    </ul>
    <ul id="R_6.053.008">
        <li id="R_6.053.008ab">eṣa niryāmy ahaṃ yuddham udyataḥ śatrunirjaye</li>
        <li id="R_6.053.008cd">durnayaṃ bhavatām adya samīkartuṃ mahāhave</li>
    </ul>
    <ul id="R_6.053.009">
        <li id="R_6.053.009ab">evam uktavato vākyaṃ kumbhakarṇasya dhīmataḥ</li>
        <li id="R_6.053.009cd">pratyuvāca tato vākyaṃ prahasan rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_6.053.010">
        <li id="R_6.053.010ab">mahodaro 'yaṃ rāmāt tu paritrasto na saṃśayaḥ</li>
        <li id="R_6.053.010cd">na hi rocayate tāta yuddhaṃ yuddhaviśārada</li>
    </ul>
    <ul id="R_6.053.011">
        <li id="R_6.053.011ab">kaś cin me tvatsamo nāsti sauhṛdena balena ca</li>
        <li id="R_6.053.011cd">gaccha śatruvadhāya tvaṃ kumbhakarṇajayāya ca</li>
    </ul>
    <ul id="R_6.053.012">
        <li id="R_6.053.012ab">ādade niśitaṃ śūlaṃ vegāc chatrunibarhaṇaḥ</li>
        <li id="R_6.053.012cd">sarvakālāyasaṃ dīptaṃ taptakāñcanabhūṣaṇam</li>
    </ul>
    <ul id="R_6.053.013">
        <li id="R_6.053.013ab">indrāśanisamaṃ bhīmaṃ vajrapratimagauravam</li>
        <li id="R_6.053.013cd">devadānavagandharvayakṣakiṃnarasūdanam</li>
    </ul>
    <ul id="R_6.053.014">
        <li id="R_6.053.014ab">raktamālya mahādāma svataś codgatapāvakam</li>
        <li id="R_6.053.014cd">ādāya niśitaṃ śūlaṃ śatruśoṇitarañjitam</li>
        <li id="R_6.053.014ef">kumbhakarṇo mahātejā rāvaṇaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.053.015">
        <li id="R_6.053.015ab">gamiṣyāmy aham ekākī tiṣṭhatv iha balaṃ mahat</li>
        <li id="R_6.053.015cd">adya tān kṣudhitaḥ kruddho bhakṣayiṣyāmi vānarān</li>
    </ul>
    <ul id="R_6.053.016">
        <li id="R_6.053.016ab">kumbhakarṇavacaḥ śrutvā rāvaṇo vākyam abravīt</li>
        <li id="R_6.053.016cd">sainyaiḥ parivṛto gaccha śūlamudgalapāṇibhiḥ</li>
    </ul>
    <ul id="R_6.053.017">
        <li id="R_6.053.017ab">vānarā hi mahātmānaḥ śīghrāś ca vyavasāyinaḥ</li>
        <li id="R_6.053.017cd">ekākinaṃ pramattaṃ vā nayeyur daśanaiḥ kṣayam</li>
    </ul>
    <ul id="R_6.053.018">
        <li id="R_6.053.018ab">tasmāt paramadurdharṣaiḥ sainyaiḥ parivṛto vraja</li>
        <li id="R_6.053.018cd">rakṣasām ahitaṃ sarvaṃ śatrupakṣaṃ nisūdaya</li>
    </ul>
    <ul id="R_6.053.019">
        <li id="R_6.053.019ab">athāsanāt samutpatya srajaṃ maṇikṛtāntarām</li>
        <li id="R_6.053.019cd">ābabandha mahātejāḥ kumbhakarṇasya rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.053.020">
        <li id="R_6.053.020ab">aṅgadān aṅgulīveṣṭān varāṇy ābharaṇāni ca</li>
        <li id="R_6.053.020cd">hāraṃ ca śaśisaṃkāśam ābabandha mahātmanaḥ</li>
    </ul>
    <ul id="R_6.053.021">
        <li id="R_6.053.021ab">divyāni ca sugandhīni mālyadāmāni rāvaṇaḥ</li>
        <li id="R_6.053.021cd">śrotre cāsajjayām āsa śrīmatī cāsya kuṇḍale</li>
    </ul>
    <ul id="R_6.053.022">
        <li id="R_6.053.022ab">kāñcanāṅgadakeyūro niṣkābharaṇabhūṣitaḥ</li>
        <li id="R_6.053.022cd">kumbhakarṇo bṛhatkarṇaḥ suhuto 'gnir ivābabhau</li>
    </ul>
    <ul id="R_6.053.023">
        <li id="R_6.053.023ab">śroṇīsūtreṇa mahatā mecakena virājitaḥ</li>
        <li id="R_6.053.023cd">amṛtotpādane naddho bhujaṃgeneva mandaraḥ</li>
    </ul>
    <ul id="R_6.053.024">
        <li id="R_6.053.024ab">
            <seg type="pāda" n="a">sa kāñcanaṃ bhārasahaṃ nivātaṃ</seg>
            <seg type="pāda" n="b">vidyutprabhaṃ dīptam ivātmabhāsā</seg>
        </li>
        <li id="R_6.053.024cd">
            <seg type="pāda" n="c">ābadhyamānaḥ kavacaṃ rarāja</seg>
            <seg type="pāda" n="d">saṃdhyābhrasaṃvīta ivādrirājaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.053.025">
        <li id="R_6.053.025ab">sarvābharaṇanaddhāṅgaḥ śūlapāṇiḥ sa rākṣasaḥ</li>
        <li id="R_6.053.025cd">trivikramakṛtotsāho nārāyaṇa ivābabhau</li>
    </ul>
    <ul id="R_6.053.026">
        <li id="R_6.053.026ab">bhrātaraṃ saṃpariṣvajya kṛtvā cāpi pradakṣiṇam</li>
        <li id="R_6.053.026cd">praṇamya śirasā tasmai saṃpratasthe mahābaliḥ</li>
        <li id="R_6.053.026ef">tam āśīrbhiḥ praśastābhiḥ preṣayām āsa rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.053.027">
        <li id="R_6.053.027ab">śaṅkhadundubhinirghoṣaiḥ sainyaiś cāpi varāyudhaiḥ</li>
        <li id="R_6.053.027cd">taṃ gajaiś ca turaṃgaiś ca syandanaiś cāmbudasvanaiḥ</li>
        <li id="R_6.053.027ef">anujagmur mahātmānaṃ rathino rathināṃ varam</li>
    </ul>
    <ul id="R_6.053.028">
        <li id="R_6.053.028ab">sarpair uṣṭraiḥ kharair aśvaiḥ siṃhadvipamṛgadvijaiḥ</li>
        <li id="R_6.053.028cd">anujagmuś ca taṃ ghoraṃ kumbhakarṇaṃ mahābalam</li>
    </ul>
    <ul id="R_6.053.029">
        <li id="R_6.053.029ab">
            <seg type="pāda" n="a">sa puṣpavarṇair avakīryamāṇo</seg>
            <seg type="pāda" n="b">dhṛtātapatraḥ śitaśūlapāṇiḥ</seg>
        </li>
        <li id="R_6.053.029cd">
            <seg type="pāda" n="c">madotkaṭaḥ śoṇitagandhamatto</seg>
            <seg type="pāda" n="d">viniryayau dānavadevaśatruḥ</seg>
        </li>
    </ul>
    <ul id="R_6.053.030">
        <li id="R_6.053.030ab">padātayaś a bahavo mahānādā mahābalāḥ</li>
        <li id="R_6.053.030cd">anvayū rākṣasā bhīmā bhīmākṣāḥ śastrapāṇayaḥ</li>
    </ul>
    <ul id="R_6.053.031">
        <li id="R_6.053.031ab">raktākṣāḥ sumahākāyā nīlāñjanacayopamāḥ</li>
        <li id="R_6.053.031cd">śūrān udyamya khaḍgāṃś ca niśitāṃś ca paraśvadhān</li>
    </ul>
    <ul id="R_6.053.032">
        <li id="R_6.053.032ab">bahuvyāmāṃś ca vipulān kṣepaṇīyān durāsadān</li>
        <li id="R_6.053.032cd">tālaskandhāṃś ca vipulān kṣepaṇīyān durāsadān</li>
    </ul>
    <ul id="R_6.053.033">
        <li id="R_6.053.033ab">athānyad vapur ādāya dāruṇaṃ lomaharṣaṇam</li>
        <li id="R_6.053.033cd">niṣpapāta mahātejāḥ kumbhakarṇo mahābalaḥ</li>
    </ul>
    <ul id="R_6.053.034">
        <li id="R_6.053.034ab">dhanuḥśataparīṇāhaḥ sa ṣaṭśatasamucchitaḥ</li>
        <li id="R_6.053.034cd">raudraḥ śakaṭacakrākṣo mahāparvatasaṃnibhaḥ</li>
    </ul>
    <ul id="R_6.053.035">
        <li id="R_6.053.035ab">saṃnipatya ca rakṣāṃsi dagdhaśailopamo mahān</li>
        <li id="R_6.053.035cd">kumbhakarṇo mahāvaktraḥ prahasann idam abravīt</li>
    </ul>
    <ul id="R_6.053.036">
        <li id="R_6.053.036ab">adya vānaramukhyānāṃ tāni yūthāni bhāgaśaḥ</li>
        <li id="R_6.053.036cd">nirdahiṣyāmi saṃkruddhaḥ śalabhān iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.053.037">
        <li id="R_6.053.037ab">nāparādhyanti me kāmaṃ vānarā vanacāriṇaḥ</li>
        <li id="R_6.053.037cd">jātir asmadvidhānāṃ sā purodyānavibhūṣaṇam</li>
    </ul>
    <ul id="R_6.053.038">
        <li id="R_6.053.038ab">purarodhasya mūlaṃ tu rāghavaḥ sahalakṣmaṇaḥ</li>
        <li id="R_6.053.038cd">hate tasmin hataṃ sarvaṃ taṃ vadhiṣyāmi saṃyuge</li>
    </ul>
    <ul id="R_6.053.039">
        <li id="R_6.053.039ab">evaṃ tasya bruvāṇasya kumbhakarṇasya rākṣasāḥ</li>
        <li id="R_6.053.039cd">nādaṃ cakrur mahāghoraṃ kampayanta ivārṇavam</li>
    </ul>
    <ul id="R_6.053.040">
        <li id="R_6.053.040ab">tasya niṣpatatas tūrṇaṃ kumbhakarṇasya dhīmataḥ</li>
        <li id="R_6.053.040cd">babhūvur ghorarūpāṇi nimittāni samantataḥ</li>
    </ul>
    <ul id="R_6.053.041">
        <li id="R_6.053.041ab">ulkāśaniyutā meghā vineduś ca sudāruṇāḥ</li>
        <li id="R_6.053.041cd">sasāgaravanā caiva vasudhā samakampata</li>
    </ul>
    <ul id="R_6.053.042">
        <li id="R_6.053.042ab">ghorarūpāḥ śivā neduḥ sajvālakavalair mukhaiḥ</li>
        <li id="R_6.053.042cd">maṇḍalāny apasavyāni babandhuś ca vihaṃgamāḥ</li>
    </ul>
    <ul id="R_6.053.043">
        <li id="R_6.053.043ab">niṣpapāta ca gṛdhre 'sya śūle vai pathi gacchataḥ</li>
        <li id="R_6.053.043cd">prāsphuran nayanaṃ cāsya savyo bāhur akampata</li>
    </ul>
    <ul id="R_6.053.044">
        <li id="R_6.053.044ab">niṣpapāta tadā coklā jvalantī bhīmanisvanā</li>
        <li id="R_6.053.044cd">ādityo niṣprabhaś cāsīn na pravāti sukho 'nilaḥ</li>
    </ul>
    <ul id="R_6.053.045">
        <li id="R_6.053.045ab">acintayan mahotpātān utthitāṃl lomaharṣaṇān</li>
        <li id="R_6.053.045cd">niryayau kumbhakarṇas tu kṛtāntabalacoditaḥ</li>
    </ul>
    <ul id="R_6.053.046">
        <li id="R_6.053.046ab">sa laṅghayitvā prākāraṃ padbhyāṃ parvatasaṃnibhaḥ</li>
        <li id="R_6.053.046cd">dadarśābhraghanaprakhyaṃ vānarānīkam adbhutam</li>
    </ul>
    <ul id="R_6.053.047">
        <li id="R_6.053.047ab">te dṛṣṭvā rākṣasaśreṣṭhaṃ vānarāḥ parvatopamam</li>
        <li id="R_6.053.047cd">vāyununnā iva ghanā yayuḥ sarvā diśas tadā</li>
    </ul>
    <ul id="R_6.053.048">
        <li id="R_6.053.048ab">
            <seg type="pāda" n="a">tad vānarānīkam atipracaṇḍaṃ</seg>
            <seg type="pāda" n="b">diśo dravad bhinnam ivābhrajālam</seg>
        </li>
        <li id="R_6.053.048cd">
            <seg type="pāda" n="c">sa kumbhakarṇaḥ samavekṣya harṣān</seg>
            <seg type="pāda" n="d">nanāda bhūyo ghanavad ghanābhaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.053.049">
        <li id="R_6.053.049ab">
            <seg type="pāda" n="a">te tasya ghoraṃ ninadaṃ niśamya</seg>
            <seg type="pāda" n="b">yathā ninādaṃ divi vāridasya</seg>
        </li>
        <li id="R_6.053.049cd">
            <seg type="pāda" n="c">petur dharaṇyāṃ bahavaḥ plavaṃgā</seg>
            <seg type="pāda" n="d">nikṛttamūlā iva sālavṛkṣāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.053.050">
        <li id="R_6.053.050ab">
            <seg type="pāda" n="a">vipulaparighavān sa kumbhakarṇo</seg>
            <seg type="pāda" n="b">ripunidhanāya viniḥsṛto mahātmā</seg>
        </li>
        <li id="R_6.053.050cd">
            <seg type="pāda" n="c">kapi gaṇabhayam ādadat subhīmaṃ</seg>
            <seg type="pāda" n="d">prabhur iva kiṃkaradaṇḍavān yugānte</seg>
        </li>
    </ul>
    <ul id="R_6.054.001">
        <li id="R_6.054.001ab">sa nanāda mahānādaṃ samudram abhinādayan</li>
        <li id="R_6.054.001cd">janayann iva nirghātān vidhamann iva parvatān</li>
    </ul>
    <ul id="R_6.054.002">
        <li id="R_6.054.002ab">tam avadhyaṃ maghavatā yamena varuṇena ca</li>
        <li id="R_6.054.002cd">prekṣya bhīmākṣam āyāntaṃ vānarā vipradudruvuḥ</li>
    </ul>
    <ul id="R_6.054.003">
        <li id="R_6.054.003ab">tāṃs tu vidravato dṛṣṭvā vāliputro 'ṅgado 'bravīt</li>
        <li id="R_6.054.003cd">nalaṃ nīlaṃ gavākṣaṃ ca kumudaṃ ca mahābalam</li>
    </ul>
    <ul id="R_6.054.004">
        <li id="R_6.054.004ab">ātmānam atra vismṛtya vīryāṇy abhijanāni ca</li>
        <li id="R_6.054.004cd">kva gacchata bhayatrastāḥ prākṛtā harayo yathā</li>
    </ul>
    <ul id="R_6.054.005">
        <li id="R_6.054.005ab">sādhu saumyā nivartadhvaṃ kiṃ prāṇān parirakṣatha</li>
        <li id="R_6.054.005cd">nālaṃ yuddhāya vai rakṣo mahatīyaṃ vibhīṣikāḥ</li>
    </ul>
    <ul id="R_6.054.006">
        <li id="R_6.054.006ab">mahatīm utthitām enāṃ rākṣasānāṃ vibhīṣikām</li>
        <li id="R_6.054.006cd">vikramād vidhamiṣyāmo nivartadhvaṃ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.054.007">
        <li id="R_6.054.007ab">kṛcchreṇa tu samāśvāsya saṃgamya ca tatas tataḥ</li>
        <li id="R_6.054.007cd">vṛkṣādrihastā harayaḥ saṃpratasthū raṇājiram</li>
    </ul>
    <ul id="R_6.054.008">
        <li id="R_6.054.008ab">te nivṛtya tu saṃkruddhāḥ kumbhakarṇaṃ vanaukasaḥ</li>
        <li id="R_6.054.008cd">nijaghnuḥ paramakruddhāḥ samadā iva kuñjarāḥ</li>
        <li id="R_6.054.008ef">prāṃśubhir giriśṛṅgaiś ca śilābhiś ca mahābalāḥ</li>
    </ul>
    <ul id="R_6.054.009">
        <li id="R_6.054.009ab">pādapaiḥ puṣpitāgraiś ca hanyamāno na kampate</li>
        <li id="R_6.054.009cd">tasya gātreṣu patitā bhidyante śataśaḥ śilāḥ</li>
        <li id="R_6.054.009ef">pādapāḥ puṣpitāgrāś ca bhagnāḥ petur mahītale</li>
    </ul>
    <ul id="R_6.054.010">
        <li id="R_6.054.010ab">so 'pi sainyāni saṃkruddho vānarāṇāṃ mahaujasām</li>
        <li id="R_6.054.010cd">mamantha paramāyatto vanāny agnir ivotthitaḥ</li>
    </ul>
    <ul id="R_6.054.011">
        <li id="R_6.054.011ab">lohitārdrās tu bahavaḥ śerate vānararṣabhāḥ</li>
        <li id="R_6.054.011cd">nirastāḥ patitā bhūmau tāmrapuṣpā iva drumāḥ</li>
    </ul>
    <ul id="R_6.054.012">
        <li id="R_6.054.012ab">laṅghayantaḥ pradhāvanto vānarā nāvalokayan</li>
        <li id="R_6.054.012cd">ke cit samudre patitāḥ ke cid gaganam āśritāḥ</li>
    </ul>
    <ul id="R_6.054.013">
        <li id="R_6.054.013ab">vadhyamānās tu te vīrā rākṣasena balīyasā</li>
        <li id="R_6.054.013cd">sāgaraṃ yena te tīrṇāḥ pathā tenaiva dudruvuḥ</li>
    </ul>
    <ul id="R_6.054.014">
        <li id="R_6.054.014ab">te sthalāni tathā nimnaṃ viṣaṇṇavadanā bhayāt</li>
        <li id="R_6.054.014cd">ṛkṣā vṛkṣān samārūḍhāḥ ke cit parvatam āśritāḥ</li>
    </ul>
    <ul id="R_6.054.015">
        <li id="R_6.054.015ab">mamajjur arṇave ke cid guhāḥ ke cit samāśritāḥ</li>
        <li id="R_6.054.015cd">niṣeduḥ plavagāḥ ke cit ke cin naivāvatasthire</li>
    </ul>
    <ul id="R_6.054.016">
        <li id="R_6.054.016ab">tān samīkṣyāṅgado bhaṅgān vānarān idam abravīt</li>
        <li id="R_6.054.016cd">avatiṣṭhata yudhyāmo nivartadhvaṃ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.054.017">
        <li id="R_6.054.017ab">bhagnānāṃ vo na paśyāmi parigamya mahīm imām</li>
        <li id="R_6.054.017cd">sthānaṃ sarve nivartadhvaṃ kiṃ prāṇān parirakṣatha</li>
    </ul>
    <ul id="R_6.054.018">
        <li id="R_6.054.018ab">nirāyudhānāṃ dravatām asaṃgagatipauruṣāḥ</li>
        <li id="R_6.054.018cd">dārā hy apahasiṣyanti sa vai ghātas tu jīvitām</li>
    </ul>
    <ul id="R_6.054.019">
        <li id="R_6.054.019ab">kuleṣu jātāḥ sarve sma vistīrṇeṣu mahatsu ca</li>
        <li id="R_6.054.019cd">anāryāḥ khalu yad bhītās tyaktvā vīryaṃ pradhāvata</li>
    </ul>
    <ul id="R_6.054.020">
        <li id="R_6.054.020ab">vikatthanāni vo yāni yadā vai janasaṃsadi</li>
        <li id="R_6.054.020cd">tāni vaḥ kva ca yatāni sodagrāṇi mahānti ca</li>
    </ul>
    <ul id="R_6.054.021">
        <li id="R_6.054.021ab">bhīrupravādāḥ śrūyante yas tu jīvati dhikkṛtaḥ</li>
        <li id="R_6.054.021cd">mārgaḥ satpuruṣair juṣṭaḥ sevyatāṃ tyajyatāṃ bhayam</li>
    </ul>
    <ul id="R_6.054.022">
        <li id="R_6.054.022ab">śayāmahe vā nihatāḥ pṛthivyām alpajīvitāḥ</li>
        <li id="R_6.054.022cd">duṣprāpaṃ brahmalokaṃ vā prāpnumo yudhi sūditāḥ</li>
        <li id="R_6.054.022ef">saṃprāpnuyāmaḥ kīrtiṃ vā nihatya śatrum āhave</li>
    </ul>
    <ul id="R_6.054.023">
        <li id="R_6.054.023ab">na kumbhakarṇaḥ kākutsthaṃ dṛṣṭvā jīvan gamiṣyati</li>
        <li id="R_6.054.023cd">dīpyamānam ivāsādya pataṃgo jvalanaṃ yathā</li>
    </ul>
    <ul id="R_6.054.024">
        <li id="R_6.054.024ab">palāyanena coddiṣṭāḥ prāṇān rakṣāmahe vayam</li>
        <li id="R_6.054.024cd">ekena bahavo bhagnā yaśo nāśaṃ gamiṣyati</li>
    </ul>
    <ul id="R_6.054.025">
        <li id="R_6.054.025ab">evaṃ bruvāṇaṃ taṃ śūram aṅgadaṃ kanakāṅgadam</li>
        <li id="R_6.054.025cd">dravamāṇās tato vākyam ūcuḥ śūravigarhitam</li>
    </ul>
    <ul id="R_6.054.026">
        <li id="R_6.054.026ab">kṛtaṃ naḥ kadanaṃ ghoraṃ kumbhakarṇena rakṣasā</li>
        <li id="R_6.054.026cd">na sthānakālo gacchāmo dayitaṃ jīvitaṃ hi naḥ</li>
    </ul>
    <ul id="R_6.054.027">
        <li id="R_6.054.027ab">etāvad uktvā vacanaṃ sarve te bhejire diśaḥ</li>
        <li id="R_6.054.027cd">bhīmaṃ bhīmākṣam āyāntaṃ dṛṣṭvā vānarayūthapāḥ</li>
    </ul>
    <ul id="R_6.054.028">
        <li id="R_6.054.028ab">dravamāṇās tu te vīrā aṅgadena valīmukhāḥ</li>
        <li id="R_6.054.028cd">sāntvaiś ca bahumānaiś ca tataḥ sarve nivartitāḥ</li>
    </ul>
    <ul id="R_6.054.029">
        <li id="R_6.054.029ab">
            <seg type="pāda" n="a">ṛṣabhaśarabhamaindadhūmranīlāḥ</seg>
            <seg type="pāda" n="b">kumudasuṣeṇagavākṣarambhatārāḥ</seg>
        </li>
        <li id="R_6.054.029cd">
            <seg type="pāda" n="c">dvividapanasavāyuputramukhyās</seg>
            <seg type="pāda" n="d">tvaritatarābhimukhaṃ raṇaṃ prayātāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.001">
        <li id="R_6.055.001ab">te nivṛttā mahākāyāḥ śrutvāṅgadavacas tadā</li>
        <li id="R_6.055.001cd">naiṣṭhikīṃ buddhim āsthāya sarve saṃgrāmakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.055.002">
        <li id="R_6.055.002ab">samudīritavīryās te samāropitavikramāḥ</li>
        <li id="R_6.055.002cd">paryavasthāpitā vākyair aṅgadena valīmukhāḥ</li>
    </ul>
    <ul id="R_6.055.003">
        <li id="R_6.055.003ab">prayātāś ca gatā harṣaṃ maraṇe kṛtaniścayāḥ</li>
        <li id="R_6.055.003cd">cakruḥ sutumulaṃ yuddhaṃ vānarās tyaktajīvitāḥ</li>
    </ul>
    <ul id="R_6.055.004">
        <li id="R_6.055.004ab">atha vṛkṣān mahākāyāḥ sānūni sumahānti ca</li>
        <li id="R_6.055.004cd">vānarās tūrṇam udyamya kumbhakarṇam abhidravan</li>
    </ul>
    <ul id="R_6.055.005">
        <li id="R_6.055.005ab">sa kumbhakarṇaḥ saṃkruddho gadām udyamya vīryavān</li>
        <li id="R_6.055.005cd">ardayan sumahākāyaḥ samantād vyākṣipad ripūn</li>
    </ul>
    <ul id="R_6.055.006">
        <li id="R_6.055.006ab">śatāni sapta cāṣṭau ca sahasrāṇi ca vānarāḥ</li>
        <li id="R_6.055.006cd">prakīrṇāḥ śerate bhūmau kumbhakarṇena pothitāḥ</li>
    </ul>
    <ul id="R_6.055.007">
        <li id="R_6.055.007ab">ṣoḍaśāṣṭau ca daśa ca viṃśat triṃśat tathaiva ca</li>
        <li id="R_6.055.007cd">parikṣipya ca bāhubhyāṃ khādan viparidhāvati</li>
        <li id="R_6.055.007ef">bhakṣayan bhṛśasaṃkruddho garuḍaḥ pannagān iva</li>
    </ul>
    <ul id="R_6.055.008">
        <li id="R_6.055.008ab">hanūmāñ śailaśṛṅgāṇi vṛkṣāṃś ca vividhān bahūn</li>
        <li id="R_6.055.008cd">vavarṣa kumbhakarṇasya śirasy ambaram āsthitaḥ</li>
    </ul>
    <ul id="R_6.055.009">
        <li id="R_6.055.009ab">tāni parvataśṛṅgāṇi śūlena tu bibheda ha</li>
        <li id="R_6.055.009cd">babhañja vṛkṣavarṣaṃ ca kumbhakarṇo mahābalaḥ</li>
    </ul>
    <ul id="R_6.055.010">
        <li id="R_6.055.010ab">
            <seg type="pāda" n="a">tato harīṇāṃ tad anīkam ugraṃ</seg>
            <seg type="pāda" n="b">dudrāva śūlaṃ niśitaṃ pragṛhya</seg>
        </li>
        <li id="R_6.055.010cd">
            <seg type="pāda" n="c">tasthau tato 'syāpatataḥ purastān</seg>
            <seg type="pāda" n="d">mahīdharāgraṃ hanumān pragṛhya</seg>
        </li>
    </ul>
    <ul id="R_6.055.011">
        <li id="R_6.055.011ab">
            <seg type="pāda" n="a">sa kumbhakarṇaṃ kupito jaghāna</seg>
            <seg type="pāda" n="b">vegena śailottamabhīmakāyam</seg>
        </li>
        <li id="R_6.055.011cd">
            <seg type="pāda" n="c">sa cukṣubhe tena tadābhibūto</seg>
            <seg type="pāda" n="d">medārdragātro rudhirāvasiktaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.012">
        <li id="R_6.055.012ab">
            <seg type="pāda" n="a">sa śūlam āvidhya taḍitprakāśaṃ</seg>
            <seg type="pāda" n="b">giriṃ yathā prajvalitāgraśṛṅgam</seg>
        </li>
        <li id="R_6.055.012cd">
            <seg type="pāda" n="c">bāhvantare mārutim ājaghāna</seg>
            <seg type="pāda" n="d">guho 'calaṃ krauñcam ivograśaktyā</seg>
        </li>
    </ul>
    <ul id="R_6.055.013">
        <li id="R_6.055.013ab">
            <seg type="pāda" n="a">sa śūlanirbhinna mahābhujāntaraḥ</seg>
            <seg type="pāda" n="b">pravihvalaḥ śoṇitam udvaman mukhāt</seg>
        </li>
        <li id="R_6.055.013cd">
            <seg type="pāda" n="c">nanāda bhīmaṃ hanumān mahāhave</seg>
            <seg type="pāda" n="d">yugāntameghastanitasvanopamam</seg>
        </li>
    </ul>
    <ul id="R_6.055.014">
        <li id="R_6.055.014ab">
            <seg type="pāda" n="a">tato vineduḥ sahasā prahṛṣṭā</seg>
            <seg type="pāda" n="b">rakṣogaṇās taṃ vyathitaṃ samīkṣya</seg>
        </li>
        <li id="R_6.055.014cd">
            <seg type="pāda" n="c">plavaṃgamās tu vyathitā bhayārtāḥ</seg>
            <seg type="pāda" n="d">pradudruvuḥ saṃyati kumbhakarṇāt</seg>
        </li>
    </ul>
    <ul id="R_6.055.015">
        <li id="R_6.055.015ab">nīlaś cikṣepa śailāgraṃ kumbhakarṇāya dhīmate</li>
        <li id="R_6.055.015cd">tam āpatantaṃ saṃprekṣya muṣṭinābhijaghāna ha</li>
    </ul>
    <ul id="R_6.055.016">
        <li id="R_6.055.016ab">muṣṭiprahārābhihataṃ tac chailāgraṃ vyaśīryata</li>
        <li id="R_6.055.016cd">savisphuliṅgaṃ sajvālaṃ nipapāta mahītale</li>
    </ul>
    <ul id="R_6.055.017">
        <li id="R_6.055.017ab">ṛṣabhaḥ śarabho nīlo gavākṣo gandhamādanaḥ</li>
        <li id="R_6.055.017cd">pañcavānaraśārdūlāḥ kumbhakarṇam upādravan</li>
    </ul>
    <ul id="R_6.055.018">
        <li id="R_6.055.018ab">śailair vṛkṣais talaiḥ pādair muṣṭibhiś ca mahābalāḥ</li>
        <li id="R_6.055.018cd">kumbhakarṇaṃ mahākāyaṃ sarvato 'bhinijaghnire</li>
    </ul>
    <ul id="R_6.055.019">
        <li id="R_6.055.019ab">sparśān iva prahārāṃs tān vedayāno na vivyathe</li>
        <li id="R_6.055.019cd">ṛṣabhaṃ tu mahāvegaṃ bāhubhyāṃ pariṣasvaje</li>
    </ul>
    <ul id="R_6.055.020">
        <li id="R_6.055.020ab">kumbhakarṇabhujābhyāṃ tu pīḍito vānararṣabhaḥ</li>
        <li id="R_6.055.020cd">nipapātarṣabho bhīmaḥ pramukhāgataśoṇitaḥ</li>
    </ul>
    <ul id="R_6.055.021">
        <li id="R_6.055.021ab">muṣṭinā śarabhaṃ hatvā jānunā nīlam āhave</li>
        <li id="R_6.055.021cd">ājaghāna gavākṣaṃ ca talenendraripus tadā</li>
    </ul>
    <ul id="R_6.055.022">
        <li id="R_6.055.022ab">dattapraharavyathitā mumuhuḥ śoṇitokṣitāḥ</li>
        <li id="R_6.055.022cd">nipetus te tu medinyāṃ nikṛttā iva kiṃśukāḥ</li>
    </ul>
    <ul id="R_6.055.023">
        <li id="R_6.055.023ab">teṣu vānaramukhyeṣu patiteṣu mahātmasu</li>
        <li id="R_6.055.023cd">vānarāṇāṃ sahasrāṇi kumbhakarṇaṃ pradudruvuḥ</li>
    </ul>
    <ul id="R_6.055.024">
        <li id="R_6.055.024ab">taṃ śailam iva śailābhāḥ sarve tu plavagarṣabhāḥ</li>
        <li id="R_6.055.024cd">samāruhya samutpatya dadaṃśuś ca mahābalāḥ</li>
    </ul>
    <ul id="R_6.055.025">
        <li id="R_6.055.025ab">taṃ nakhair daśanaiś cāpi muṣṭibhir jānubhis tathā</li>
        <li id="R_6.055.025cd">kumbhakarṇaṃ mahākāyaṃ te jaghnuḥ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_6.055.026">
        <li id="R_6.055.026ab">sa vānarasahasrais tair ācitaḥ parvatopamaḥ</li>
        <li id="R_6.055.026cd">rarāja rākṣasavyāghro girir ātmaruhair iva</li>
    </ul>
    <ul id="R_6.055.027">
        <li id="R_6.055.027ab">bāhubhyāṃ vānarān sarvān pragṛhya sa mahābalaḥ</li>
        <li id="R_6.055.027cd">bhakṣayām āsa saṃkruddho garuḍaḥ pannagān iva</li>
    </ul>
    <ul id="R_6.055.028">
        <li id="R_6.055.028ab">prakṣiptāḥ kumbhakarṇena vaktre pātālasaṃnibhe</li>
        <li id="R_6.055.028cd">nāsā puṭābhyāṃ nirjagmuḥ karṇābhyāṃ caiva vānarāḥ</li>
    </ul>
    <ul id="R_6.055.029">
        <li id="R_6.055.029ab">bhakṣayan bhṛśasaṃkruddho harīn parvatasaṃnibhaḥ</li>
        <li id="R_6.055.029cd">babhañja vānarān sarvān saṃkruddho rākṣasottamaḥ</li>
    </ul>
    <ul id="R_6.055.030">
        <li id="R_6.055.030ab">māṃsaśoṇitasaṃkledāṃ bhūmiṃ kurvan sa rākṣasaḥ</li>
        <li id="R_6.055.030cd">cacāra harisainyeṣu kālāgnir iva mūrchitaḥ</li>
    </ul>
    <ul id="R_6.055.031">
        <li id="R_6.055.031ab">vajrahasto yathā śakraḥ pāśahasta ivāntakaḥ</li>
        <li id="R_6.055.031cd">śūlahasto babhau tasmin kumbhakarṇo mahābalaḥ</li>
    </ul>
    <ul id="R_6.055.032">
        <li id="R_6.055.032ab">yathā śuṣkāṇy araṇyāni grīṣme dahati pāvakaḥ</li>
        <li id="R_6.055.032cd">tathā vānarasainyāni kumbhakarṇo vinirdahat</li>
    </ul>
    <ul id="R_6.055.033">
        <li id="R_6.055.033ab">tatas te vadhyamānās tu hatayūthā vināyakāḥ</li>
        <li id="R_6.055.033cd">vānarā bhayasaṃvignā vinedur visvaraṃ bhṛśam</li>
    </ul>
    <ul id="R_6.055.034">
        <li id="R_6.055.034ab">anekaśo vadhyamānāḥ kumbhakarṇena vānarāḥ</li>
        <li id="R_6.055.034cd">rāghavaṃ śaraṇaṃ jagmur vyathitāḥ khinnacetasaḥ</li>
    </ul>
    <ul id="R_6.055.035">
        <li id="R_6.055.035ab">tam āpatantaṃ saṃprekṣya kumbhakarṇaṃ mahābalam</li>
        <li id="R_6.055.035cd">utpapāta tadā vīraḥ sugrīvo vānarādhipaḥ</li>
    </ul>
    <ul id="R_6.055.036">
        <li id="R_6.055.036ab">sa parvatāgram utkṣipya samāvidhya mahākapiḥ</li>
        <li id="R_6.055.036cd">abhidudrāva vegena kumbhakarṇaṃ mahābalam</li>
    </ul>
    <ul id="R_6.055.037">
        <li id="R_6.055.037ab">tam āpatantaṃ saṃprekṣya kumbhakarṇaḥ plavaṃgamam</li>
        <li id="R_6.055.037cd">tasthau vivṛtasarvāṅgo vānarendrasya saṃmukhaḥ</li>
    </ul>
    <ul id="R_6.055.038">
        <li id="R_6.055.038ab">kapiśoṇitadigdhāṅgaṃ bhakṣayantaṃ mahākapīn</li>
        <li id="R_6.055.038cd">kumbhakarṇaṃ sthitaṃ dṛṣṭvā sugrīvo vākyam abravīt</li>
    </ul>
    <ul id="R_6.055.039">
        <li id="R_6.055.039ab">pātitāś ca tvayā vīrāḥ kṛtaṃ karma suduṣkaram</li>
        <li id="R_6.055.039cd">bhakṣitāni ca sainyāni prāptaṃ te paramaṃ yaśaḥ</li>
    </ul>
    <ul id="R_6.055.040">
        <li id="R_6.055.040ab">tyaja tad vānarānīkaṃ prākṛtaiḥ kiṃ kariṣyasi</li>
        <li id="R_6.055.040cd">sahasvaikaṃ nipātaṃ me parvatasyāsya rākṣasa</li>
    </ul>
    <ul id="R_6.055.041">
        <li id="R_6.055.041ab">tad vākyaṃ harirājasya sattvadhairyasamanvitam</li>
        <li id="R_6.055.041cd">śrutvā rākṣasaśārdūlaḥ kumbhakarṇo 'bravīd vacaḥ</li>
    </ul>
    <ul id="R_6.055.042">
        <li id="R_6.055.042ab">prajāpates tu pautras tvaṃ tathaivarkṣarajaḥsutaḥ</li>
        <li id="R_6.055.042cd">śrutapauruṣasaṃpannas tasmād garjasi vānara</li>
    </ul>
    <ul id="R_6.055.043">
        <li id="R_6.055.043ab">
            <seg type="pāda" n="a">sa kumbhakarṇasya vaco niśamya</seg>
            <seg type="pāda" n="b">vyāvidhya śailaṃ sahasā mumoca</seg>
        </li>
        <li id="R_6.055.043cd">
            <seg type="pāda" n="c">tenājaghānorasi kumbhakarṇaṃ</seg>
            <seg type="pāda" n="d">śailena vajrāśanisaṃnibhena</seg>
        </li>
    </ul>
    <ul id="R_6.055.044">
        <li id="R_6.055.044ab">
            <seg type="pāda" n="a">tac chailaśṛṅgaṃ sahasā vikīrṇaṃ</seg>
            <seg type="pāda" n="b">bhujāntare tasya tadā viśāle</seg>
        </li>
        <li id="R_6.055.044cd">
            <seg type="pāda" n="c">tato viṣeduḥ sahasā plavaṃgamā</seg>
            <seg type="pāda" n="d">rakṣogaṇāś cāpi mudā vineduḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.045">
        <li id="R_6.055.045ab">
            <seg type="pāda" n="a">sa śailaśṛṅgābhihataś cukopa</seg>
            <seg type="pāda" n="b">nanāda kopāc ca vivṛtya vaktram</seg>
        </li>
        <li id="R_6.055.045cd">
            <seg type="pāda" n="c">vyāvidhya śūlaṃ ca taḍitprakāśaṃ</seg>
            <seg type="pāda" n="d">cikṣepa haryṛkṣapater vadhāya</seg>
        </li>
    </ul>
    <ul id="R_6.055.046">
        <li id="R_6.055.046ab">
            <seg type="pāda" n="a">tat kumbhakarṇasya bhujapraviddhaṃ</seg>
            <seg type="pāda" n="b">śūlaṃ śitaṃ kāñcanadāmajuṣṭam</seg>
        </li>
        <li id="R_6.055.046cd">
            <seg type="pāda" n="c">kṣipraṃ samutpatya nigṛhya dorbhyāṃ</seg>
            <seg type="pāda" n="d">babhañja vegena suto 'nilasya</seg>
        </li>
    </ul>
    <ul id="R_6.055.047">
        <li id="R_6.055.047ab">kṛtaṃ bhārasahasrasya śūlaṃ kālāyasaṃ mahat</li>
        <li id="R_6.055.047cd">babhañja janaum āropya prahṛṣṭaḥ plavagarṣabhaḥ</li>
    </ul>
    <ul id="R_6.055.048">
        <li id="R_6.055.048ab">
            <seg type="pāda" n="a">sa tat tadā bhagnam avekṣya śūlaṃ</seg>
            <seg type="pāda" n="b">cukopa rakṣo'dhipatir mahātmā</seg>
        </li>
        <li id="R_6.055.048cd">
            <seg type="pāda" n="c">utpāṭya laṅkāmalayāt sa śṛṅgaṃ</seg>
            <seg type="pāda" n="d">jaghāna sugrīvam upetya tena</seg>
        </li>
    </ul>
    <ul id="R_6.055.049">
        <li id="R_6.055.049ab">
            <seg type="pāda" n="a">sa śailaśṛṅgābhihato visaṃjñaḥ</seg>
            <seg type="pāda" n="b">papāta bhūmau yudhi vānarendraḥ</seg>
        </li>
        <li id="R_6.055.049cd">
            <seg type="pāda" n="c">taṃ prekṣya bhūmau patitaṃ visaṃjñaṃ</seg>
            <seg type="pāda" n="d">neduḥ prahṛṣṭā yudhi yātudhānāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.050">
        <li id="R_6.055.050ab">
            <seg type="pāda" n="a">tam abhyupetyādbhutaghoravīryaṃ</seg>
            <seg type="pāda" n="b">sa kumbhakarṇo yudhi vānarendram</seg>
        </li>
        <li id="R_6.055.050cd">
            <seg type="pāda" n="c">jahāra sugrīvam abhipragṛhya</seg>
            <seg type="pāda" n="d">yathānilo megham atipracaṇḍaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.051">
        <li id="R_6.055.051ab">
            <seg type="pāda" n="a">sa taṃ mahāmeghanikāśarūpam</seg>
            <seg type="pāda" n="b">utpāṭya gacchan yudhi kumbhakarṇaḥ</seg>
        </li>
        <li id="R_6.055.051cd">
            <seg type="pāda" n="c">rarāja merupratimānarūpo</seg>
            <seg type="pāda" n="d">merur yathātyucchritaghoraśṛṅgaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.052">
        <li id="R_6.055.052ab">
            <seg type="pāda" n="a">tataḥ samutpāṭya jagāma vīraḥ</seg>
            <seg type="pāda" n="b">saṃstūyamāno yudhi rākṣasendraiḥ</seg>
        </li>
        <li id="R_6.055.052cd">
            <seg type="pāda" n="c">śṛṇvan ninādaṃ tridaśālayānāṃ</seg>
            <seg type="pāda" n="d">plavaṃgarājagrahavismitānām</seg>
        </li>
    </ul>
    <ul id="R_6.055.053">
        <li id="R_6.055.053ab">
            <seg type="pāda" n="a">tatas tam ādāya tadā sa mene</seg>
            <seg type="pāda" n="b">harīndram indropamam indravīryaḥ</seg>
        </li>
        <li id="R_6.055.053cd">
            <seg type="pāda" n="c">asmin hṛte sarvam idaṃ hṛtaṃ syāt</seg>
            <seg type="pāda" n="d">sarāghavaṃ sainyam itīndraśatruḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.054">
        <li id="R_6.055.054ab">vidrutāṃ vāhinīṃ dṛṣṭvā vānarāṇāṃ tatas tataḥ</li>
        <li id="R_6.055.054cd">kumbhakarṇena sugrīvaṃ gṛhītaṃ cāpi vānaram</li>
    </ul>
    <ul id="R_6.055.055">
        <li id="R_6.055.055ab">hanūmāṃś cintayām āsa matimān mārutātmajaḥ</li>
        <li id="R_6.055.055cd">evaṃ gṛhīte sugrīve kiṃ kartavyaṃ mayā bhavet</li>
    </ul>
    <ul id="R_6.055.056">
        <li id="R_6.055.056ab">yad vai nyāyyaṃ mayā kartuṃ tat kariṣyāmi sarvathā</li>
        <li id="R_6.055.056cd">bhūtvā parvatasaṃkāśo nāśayiṣyāmi rākṣasaṃ</li>
    </ul>
    <ul id="R_6.055.057">
        <li id="R_6.055.057ab">
            <seg type="pāda" n="a">mayā hate saṃyati kumbhakarṇe</seg>
            <seg type="pāda" n="b">mahābale muṣṭiviśīrṇadehe</seg>
        </li>
        <li id="R_6.055.057cd">
            <seg type="pāda" n="c">vimocite vānarapārthive ca</seg>
            <seg type="pāda" n="d">bhavantu hṛṣṭāḥ pravagāḥ samagrāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.058">
        <li id="R_6.055.058ab">atha vā svayam apy eṣa mokṣaṃ prāpsyati pārthivaḥ</li>
        <li id="R_6.055.058cd">gṛhīto 'yaṃ yadi bhavet tridaśaiḥ sāsuroragaiḥ</li>
    </ul>
    <ul id="R_6.055.059">
        <li id="R_6.055.059ab">manye na tāvad ātmānaṃ budhyate vānarādhipaḥ</li>
        <li id="R_6.055.059cd">śailaprahārābhihataḥ kumbhakarṇena saṃyuge</li>
    </ul>
    <ul id="R_6.055.060">
        <li id="R_6.055.060ab">ayaṃ muhūrtāt sugrīvo labdhasaṃjño mahāhave</li>
        <li id="R_6.055.060cd">ātmano vānarāṇāṃ ca yat pathyaṃ tat kariṣyati</li>
    </ul>
    <ul id="R_6.055.061">
        <li id="R_6.055.061ab">mayā tu mokṣitasyāsya sugrīvasya mahātmanaḥ</li>
        <li id="R_6.055.061cd">aprītaś ca bhavet kaṣṭā kīrtināśaś ca śāśvataḥ</li>
    </ul>
    <ul id="R_6.055.062">
        <li id="R_6.055.062ab">tasmān muhūrtaṃ kāṅkṣiṣye vikramaṃ pārthivasya naḥ</li>
        <li id="R_6.055.062cd">bhinnaṃ ca vānarānīkaṃ tāvad āśvāsayāmy aham</li>
    </ul>
    <ul id="R_6.055.063">
        <li id="R_6.055.063ab">ity evaṃ cintayitvā tu hanūmān mārutātmajaḥ</li>
        <li id="R_6.055.063cd">bhūyaḥ saṃstambhayām āsa vānarāṇāṃ mahācamūm</li>
    </ul>
    <ul id="R_6.055.064">
        <li id="R_6.055.064ab">
            <seg type="pāda" n="a">sa kumbhakarṇo 'tha viveśa laṅkāṃ</seg>
            <seg type="pāda" n="b">sphurantam ādāya mahāhariṃ tam</seg>
        </li>
        <li id="R_6.055.064cd">
            <seg type="pāda" n="c">vimānacaryāgṛhagopurasthaiḥ</seg>
            <seg type="pāda" n="d">puṣpāgryavarṣair avakīryamāṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.065">
        <li id="R_6.055.065ab">
            <seg type="pāda" n="a">tataḥ sa saṃjñām upalabhya kṛcchrād</seg>
            <seg type="pāda" n="b">balīyasas tasya bhujāntarasthaḥ</seg>
        </li>
        <li id="R_6.055.065cd">
            <seg type="pāda" n="c">avekṣamāṇaḥ purarājamārgaṃ</seg>
            <seg type="pāda" n="d">vicintayām āsa muhur mahātmā</seg>
        </li>
    </ul>
    <ul id="R_6.055.066">
        <li id="R_6.055.066ab">
            <seg type="pāda" n="a">evaṃ gṛhītena kathaṃ nu nāma</seg>
            <seg type="pāda" n="b">śakyaṃ mayā saṃprati kartum adya</seg>
        </li>
        <li id="R_6.055.066cd">
            <seg type="pāda" n="c">tathā kariṣyāmi yathā harīṇāṃ</seg>
            <seg type="pāda" n="d">bhaviṣyatīṣṭaṃ ca hitaṃ ca kāryam</seg>
        </li>
    </ul>
    <ul id="R_6.055.067">
        <li id="R_6.055.067ab">
            <seg type="pāda" n="a">tataḥ karāgraiḥ sahasā sametya</seg>
            <seg type="pāda" n="b">rājā harīṇām amarendraśatroḥ</seg>
        </li>
        <li id="R_6.055.067cd">
            <seg type="pāda" n="c">nakhaiś ca karṇau daśanaiś ca nāsāṃ</seg>
            <seg type="pāda" n="d">dadaṃśa pārśveṣu ca kumbhakarṇam</seg>
        </li>
    </ul>
    <ul id="R_6.055.068">
        <li id="R_6.055.068ab">
            <seg type="pāda" n="a">sa kumbhakarṇau hṛtakarṇanāso</seg>
            <seg type="pāda" n="b">vidāritas tena vimarditaś ca</seg>
        </li>
        <li id="R_6.055.068cd">
            <seg type="pāda" n="c">roṣābhibhūtaḥ kṣatajārdragātraḥ</seg>
            <seg type="pāda" n="d">sugrīvam āvidhya pipeṣa bhūmau</seg>
        </li>
    </ul>
    <ul id="R_6.055.069">
        <li id="R_6.055.069ab">
            <seg type="pāda" n="a">sa bhūtale bhīmabalābhipiṣṭaḥ</seg>
            <seg type="pāda" n="b">surāribhis tair abhihanyamānaḥ</seg>
        </li>
        <li id="R_6.055.069cd">
            <seg type="pāda" n="c">jagāma khaṃ vegavad abhyupetya</seg>
            <seg type="pāda" n="d">punaś ca rāmeṇa samājagāma</seg>
        </li>
    </ul>
    <ul id="R_6.055.070">
        <li id="R_6.055.070ab">karṇanāsā vihīnasya kumbhakarṇo mahābalaḥ</li>
        <li id="R_6.055.070cd">rarāja śoṇitotsikto giriḥ prasravaṇair iva</li>
    </ul>
    <ul id="R_6.055.071">
        <li id="R_6.055.071ab">
            <seg type="pāda" n="a">tataḥ sa puryāḥ sahasā mahātmā</seg>
            <seg type="pāda" n="b">niṣkramya tad vānarasainyam ugram</seg>
        </li>
        <li id="R_6.055.071cd">
            <seg type="pāda" n="c">babhakṣa rakṣo yudhi kumbhakarṇaḥ</seg>
            <seg type="pāda" n="d">prajā yugāntāgnir iva pradīptaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.072">
        <li id="R_6.055.072ab">
            <seg type="pāda" n="a">bubhukṣitaḥ śoṇitamāṃsagṛdhnuḥ</seg>
            <seg type="pāda" n="b">praviśya tad vānarasainyam ugram</seg>
        </li>
        <li id="R_6.055.072cd">
            <seg type="pāda" n="c">cakhāda rakṣāṃsi harīn piśācān</seg>
            <seg type="pāda" n="d">ṛkṣāṃś ca mohād yudhi kumbhakarṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.073">
        <li id="R_6.055.073ab">ekaṃ dvau trīn bahūn kruddho vānarān saha rākṣasaiḥ</li>
        <li id="R_6.055.073cd">samādāyaikahastena pracikṣepa tvaran mukhe</li>
    </ul>
    <ul id="R_6.055.074">
        <li id="R_6.055.074ab">saṃprasravaṃs tadā medaḥ śoṇitaṃ ca mahābalaḥ</li>
        <li id="R_6.055.074cd">vadhyamāno nagendrāgrair bhakṣayām āsa vānarān</li>
        <li id="R_6.055.074ef">te bhakṣyamāṇā harayo rāmaṃ jagmus tadā gatim</li>
    </ul>
    <ul id="R_6.055.075">
        <li id="R_6.055.075ab">tasmin kāle sumitrāyāḥ putraḥ parabalārdanaḥ</li>
        <li id="R_6.055.075cd">cakāra lakṣmaṇaḥ kruddho yuddhaṃ parapuraṃjayaḥ</li>
    </ul>
    <ul id="R_6.055.076">
        <li id="R_6.055.076ab">sa kumbhakarṇasya śarāñ śarīre sapta vīryavān</li>
        <li id="R_6.055.076cd">nicakhānādade cānyān visasarja ca lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.055.077">
        <li id="R_6.055.077ab">atikramya ca saumitriṃ kumbhakarṇo mahābalaḥ</li>
        <li id="R_6.055.077cd">rāmam evābhidudrāva dārayann iva medinīm</li>
    </ul>
    <ul id="R_6.055.078">
        <li id="R_6.055.078ab">atha dāśarathī rāmo raudram astraṃ prayojayan</li>
        <li id="R_6.055.078cd">kumbhakarṇasya hṛdaye sasarja niśitāñ śarān</li>
    </ul>
    <ul id="R_6.055.079">
        <li id="R_6.055.079ab">tasya rāmeṇa viddhasya sahasābhipradhāvataḥ</li>
        <li id="R_6.055.079cd">aṅgāramiśrāḥ kruddhasya mukhān niścerur arciṣaḥ</li>
    </ul>
    <ul id="R_6.055.080">
        <li id="R_6.055.080ab">tasyorasi nimagnāś ca śarā barhiṇavāsasaḥ</li>
        <li id="R_6.055.080cd">hastāc cāsya paribhraṣṭā papātorvyāṃ mahāgadā</li>
    </ul>
    <ul id="R_6.055.081">
        <li id="R_6.055.081ab">sa nirāyudham ātmānaṃ yadā mene mahābalaḥ</li>
        <li id="R_6.055.081cd">muṣṭibhyāṃ cāraṇābhyāṃ ca cakāra kadanaṃ mahat</li>
    </ul>
    <ul id="R_6.055.082">
        <li id="R_6.055.082ab">sa bāṇair atividdhāṅgaḥ kṣatajena samukṣitaḥ</li>
        <li id="R_6.055.082cd">rudhiraṃ parisusrāva giriḥ prasravaṇān iva</li>
    </ul>
    <ul id="R_6.055.083">
        <li id="R_6.055.083ab">sa tīvreṇa ca kopena rudhireṇa ca mūrchitaḥ</li>
        <li id="R_6.055.083cd">vānarān rākṣasān ṛkṣān khādan viparidhāvati</li>
    </ul>
    <ul id="R_6.055.084">
        <li id="R_6.055.084ab">tasmin kāle sa dharmātmā lakṣmaṇo rāmam abravīt</li>
        <li id="R_6.055.084cd">kumbhakarṇavadhe yukto yogān parimṛśan bahūn</li>
    </ul>
    <ul id="R_6.055.085">
        <li id="R_6.055.085ab">naivāyaṃ vānarān rājan na vijānāti rākṣasān</li>
        <li id="R_6.055.085cd">mattaḥ śoṇitagandhena svān parāṃś caiva khādati</li>
    </ul>
    <ul id="R_6.055.086">
        <li id="R_6.055.086ab">sādhv enam adhirohantu sarvato vānararṣabhāḥ</li>
        <li id="R_6.055.086cd">yūthapāś ca yathāmukhyās tiṣṭhantv asya samantataḥ</li>
    </ul>
    <ul id="R_6.055.087">
        <li id="R_6.055.087ab">apy ayaṃ durmatiḥ kāle gurubhāraprapīḍitaḥ</li>
        <li id="R_6.055.087cd">prapatan rākṣaso bhūmau nānyān hanyāt plavaṃgamān</li>
    </ul>
    <ul id="R_6.055.088">
        <li id="R_6.055.088ab">tasya tad vacanaṃ śrutvā rājaputrasya dhīmataḥ</li>
        <li id="R_6.055.088cd">te samāruruhur hṛṣṭāḥ kumbhakarṇaṃ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.055.089">
        <li id="R_6.055.089ab">kumbhakarṇas tu saṃkruddhaḥ samārūḍhaḥ plavaṃgamaiḥ</li>
        <li id="R_6.055.089cd">vyadhūnayat tān vegena duṣṭahastīva hastipān</li>
    </ul>
    <ul id="R_6.055.090">
        <li id="R_6.055.090ab">tān dṛṣṭvā nirdhūtān rāmo ruṣṭo 'yam iti rākṣasaḥ</li>
        <li id="R_6.055.090cd">samutpapāta vegena dhanur uttamam ādade</li>
    </ul>
    <ul id="R_6.055.091">
        <li id="R_6.055.091ab">
            <seg type="pāda" n="a">sa cāpam ādāya bhujaṃgakalpaṃ</seg>
            <seg type="pāda" n="b">dṛḍhajyam ugraṃ tapanīyacitram</seg>
        </li>
        <li id="R_6.055.091cd">
            <seg type="pāda" n="c">harīn samāśvāsya samutpapāta</seg>
            <seg type="pāda" n="d">rāmo nibaddhottamatūṇabāṇaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.092">
        <li id="R_6.055.092ab">sa vānaragaṇais tais tu vṛtaḥ paramadurjayaḥ</li>
        <li id="R_6.055.092cd">lakṣmaṇānucaro rāmaḥ saṃpratasthe mahābalaḥ</li>
    </ul>
    <ul id="R_6.055.093">
        <li id="R_6.055.093ab">sa dadarśa mahātmānaṃ kirīṭinam ariṃdamam</li>
        <li id="R_6.055.093cd">śoṇitāplutasarvāṅgaṃ kumbhakarṇaṃ mahābalam</li>
    </ul>
    <ul id="R_6.055.094">
        <li id="R_6.055.094ab">sarvān samabhidhāvantaṃ yathāruṣṭaṃ diśā gajam</li>
        <li id="R_6.055.094cd">mārgamāṇaṃ harīn kruddhaṃ rākṣasaiḥ parivāritam</li>
    </ul>
    <ul id="R_6.055.095">
        <li id="R_6.055.095ab">vindhyamandarasaṃkāśaṃ kāñcanāṅgadabhūṣaṇam</li>
        <li id="R_6.055.095cd">sravantaṃ rudhiraṃ vaktrād varṣamegham ivotthitam</li>
    </ul>
    <ul id="R_6.055.096">
        <li id="R_6.055.096ab">jihvayā parilihyantaṃ śoṇitaṃ śoṇitokṣitam</li>
        <li id="R_6.055.096cd">mṛdnantaṃ vānarānīkaṃ kālāntakayamopamam</li>
    </ul>
    <ul id="R_6.055.097">
        <li id="R_6.055.097ab">taṃ dṛṣṭvā rākṣasaśreṣṭhaṃ pradīptānalavarcasaṃ</li>
        <li id="R_6.055.097cd">visphārayām āsa tadā kārmukaṃ puruṣarṣabhaḥ</li>
    </ul>
    <ul id="R_6.055.098">
        <li id="R_6.055.098ab">sa tasya cāpanirghoṣāt kupito nairṛtarṣabhaḥ</li>
        <li id="R_6.055.098cd">amṛṣyamāṇas taṃ ghoṣam abhidudrāva rāghavam</li>
    </ul>
    <ul id="R_6.055.099">
        <li id="R_6.055.099ab">
            <seg type="pāda" n="a">tatas tu vātoddhatameghakalpaṃ</seg>
            <seg type="pāda" n="b">bhujaṃgarājottamabhogabāhum</seg>
        </li>
        <li id="R_6.055.099cd">
            <seg type="pāda" n="c">tam āpatantaṃ dharaṇīdharābham</seg>
            <seg type="pāda" n="d">uvāca rāmo yudhi kumbhakarṇam</seg>
        </li>
    </ul>
    <ul id="R_6.055.100">
        <li id="R_6.055.100ab">
            <seg type="pāda" n="a">āgaccha rakṣo'dhipamā viṣādam</seg>
            <seg type="pāda" n="b">avasthito 'haṃ pragṛhītacāpaḥ</seg>
        </li>
        <li id="R_6.055.100cd">
            <seg type="pāda" n="c">avehi māṃ śakrasapatna rāmam</seg>
            <seg type="pāda" n="d">ayaṃ muhūrtād bhavitā vicetāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.101">
        <li id="R_6.055.101ab">rāmo 'yam iti vijñāya jahāsa vikṛtasvanam</li>
        <li id="R_6.055.101cd">pātayann iva sarveṣāṃ hṛdayāni vanaukasām</li>
    </ul>
    <ul id="R_6.055.102">
        <li id="R_6.055.102ab">prahasya vikṛtaṃ bhīmaṃ sa meghasvanitopamam</li>
        <li id="R_6.055.102cd">kumbhakarṇo mahātejā rāghavaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.055.103">
        <li id="R_6.055.103ab">nāhaṃ virādho vijñeyo na kabandhaḥ kharo na ca</li>
        <li id="R_6.055.103cd">na vālī na ca mārīcaḥ kumbhakarṇo 'ham āgataḥ</li>
    </ul>
    <ul id="R_6.055.104">
        <li id="R_6.055.104ab">paśya me mudgaraṃ ghoraṃ sarvakālāyasaṃ mahat</li>
        <li id="R_6.055.104cd">anena nirjitā devā dānavāś ca mayā purā</li>
    </ul>
    <ul id="R_6.055.105">
        <li id="R_6.055.105ab">vikarṇanāsa iti māṃ nāvajñātuṃ tvam arhasi</li>
        <li id="R_6.055.105cd">svalpāpi hi na me pīḍā karṇanāsāvināśanāt</li>
    </ul>
    <ul id="R_6.055.106">
        <li id="R_6.055.106ab">darśayekṣvākuśārdūla vīryaṃ gātreṣu me laghu</li>
        <li id="R_6.055.106cd">tatas tvāṃ bhakṣayiṣyāmi dṛṣṭapauruṣavikramam</li>
    </ul>
    <ul id="R_6.055.107">
        <li id="R_6.055.107ab">
            <seg type="pāda" n="a">sa kumbhakarṇasya vaco niśamya</seg>
            <seg type="pāda" n="b">rāmaḥ supuṅkhān visasarja bāṇān</seg>
        </li>
        <li id="R_6.055.107cd">
            <seg type="pāda" n="c">tair āhato vajrasamapravegair</seg>
            <seg type="pāda" n="d">na cukṣubhe na vyathate surāriḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.108">
        <li id="R_6.055.108ab">
            <seg type="pāda" n="a">yaiḥ sāyakaiḥ sālavarā nikṛttā</seg>
            <seg type="pāda" n="b">vālī hato vānarapuṃgavaś ca</seg>
        </li>
        <li id="R_6.055.108cd">
            <seg type="pāda" n="c">te kumbhakarṇasya tadā śarīraṃ</seg>
            <seg type="pāda" n="d">vajropamā na vyathayāṃ pracakruḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.109">
        <li id="R_6.055.109ab">
            <seg type="pāda" n="a">sa vāridhārā iva sāyakāṃs tān</seg>
            <seg type="pāda" n="b">pibañ śarīreṇa mahendraśatruḥ</seg>
        </li>
        <li id="R_6.055.109cd">
            <seg type="pāda" n="c">jaghāna rāmasya śarapravegaṃ</seg>
            <seg type="pāda" n="d">vyāvidhya taṃ mudgaram ugravegam</seg>
        </li>
    </ul>
    <ul id="R_6.055.110">
        <li id="R_6.055.110ab">
            <seg type="pāda" n="a">tatas tu rakṣaḥ kṣatajānuliptaṃ</seg>
            <seg type="pāda" n="b">vitrāsanaṃ devamahācamūnām</seg>
        </li>
        <li id="R_6.055.110cd">
            <seg type="pāda" n="c">vyāvidhya taṃ mudgaram ugravegaṃ</seg>
            <seg type="pāda" n="d">vidrāvayām āsa camūṃ harīṇām</seg>
        </li>
    </ul>
    <ul id="R_6.055.111">
        <li id="R_6.055.111ab">
            <seg type="pāda" n="a">vāyavyam ādāya tato varāstraṃ</seg>
            <seg type="pāda" n="b">rāmaḥ pracikṣepa niśācarāya</seg>
        </li>
        <li id="R_6.055.111cd">
            <seg type="pāda" n="c">samudgaraṃ tena jahāra bāhuṃ</seg>
            <seg type="pāda" n="d">sa kṛttabāhus tumulaṃ nanāda</seg>
        </li>
    </ul>
    <ul id="R_6.055.112">
        <li id="R_6.055.112ab">
            <seg type="pāda" n="a">sa tasya bāhur giriśṛṅgakalpaḥ</seg>
            <seg type="pāda" n="b">samudgaro rāghavabāṇakṛttaḥ</seg>
        </li>
        <li id="R_6.055.112cd">
            <seg type="pāda" n="c">papāta tasmin harirājasainye</seg>
            <seg type="pāda" n="d">jaghāna tāṃ vānaravāhinīṃ ca</seg>
        </li>
    </ul>
    <ul id="R_6.055.113">
        <li id="R_6.055.113ab">
            <seg type="pāda" n="a">te vānarā bhagnahatāvaśeṣāḥ</seg>
            <seg type="pāda" n="b">paryantam āśritya tadā viṣaṇṇāḥ</seg>
        </li>
        <li id="R_6.055.113cd">
            <seg type="pāda" n="c">pravepitāṅgā dadṛśuḥ sughoraṃ</seg>
            <seg type="pāda" n="d">narendrarakṣo'dhipasaṃnipātam</seg>
        </li>
    </ul>
    <ul id="R_6.055.114">
        <li id="R_6.055.114ab">
            <seg type="pāda" n="a">sa kumbhakarṇo 'stranikṛttabāhur</seg>
            <seg type="pāda" n="b">mahān nikṛttāgra ivācalendraḥ</seg>
        </li>
        <li id="R_6.055.114cd">
            <seg type="pāda" n="c">utpāṭayām āsa kareṇa vṛkṣaṃ</seg>
            <seg type="pāda" n="d">tato 'bhidudrāva raṇe narendram</seg>
        </li>
    </ul>
    <ul id="R_6.055.115">
        <li id="R_6.055.115ab">
            <seg type="pāda" n="a">taṃ tasya bāhuṃ saha sālavṛkṣaṃ</seg>
            <seg type="pāda" n="b">samudyataṃ pannagabhogakalpam</seg>
        </li>
        <li id="R_6.055.115cd">
            <seg type="pāda" n="c">aindrāstrayuktena jahāra rāmo</seg>
            <seg type="pāda" n="d">bāṇena jāmbūnadacitritena</seg>
        </li>
    </ul>
    <ul id="R_6.055.116">
        <li id="R_6.055.116ab">
            <seg type="pāda" n="a">sa kumbhakarṇasya bhujo nikṛttaḥ</seg>
            <seg type="pāda" n="b">papāta bhūmau girisaṃnikāśaḥ</seg>
        </li>
        <li id="R_6.055.116cd">
            <seg type="pāda" n="c">viveṣṭamāno nijaghāna vṛkṣāñ</seg>
            <seg type="pāda" n="d">śailāñ śilāvānararākṣasāṃś ca</seg>
        </li>
    </ul>
    <ul id="R_6.055.117">
        <li id="R_6.055.117ab">
            <seg type="pāda" n="a">taṃ chinnabāhuṃ samavekṣya rāmaḥ</seg>
            <seg type="pāda" n="b">samāpatantaṃ sahasā nadantam</seg>
        </li>
        <li id="R_6.055.117cd">
            <seg type="pāda" n="c">dvāv ardhacandrau niśitau pragṛhya</seg>
            <seg type="pāda" n="d">ciccheda pādau yudhi rākṣasasya</seg>
        </li>
    </ul>
    <ul id="R_6.055.118">
        <li id="R_6.055.118ab">
            <seg type="pāda" n="a">nikṛttabāhur vinikṛttapādo</seg>
            <seg type="pāda" n="b">vidārya vaktraṃ vaḍavāmukhābham</seg>
        </li>
        <li id="R_6.055.118cd">
            <seg type="pāda" n="c">dudrāva rāmaṃ sahasābhigarjan</seg>
            <seg type="pāda" n="d">rāhur yathā candram ivāntarikṣe</seg>
        </li>
    </ul>
    <ul id="R_6.055.119">
        <li id="R_6.055.119ab">
            <seg type="pāda" n="a">apūrayat tasya mukhaṃ śitāgrai</seg>
            <seg type="pāda" n="b">rāmaḥ śarair hemapinaddhapuṅkhaiḥ</seg>
        </li>
        <li id="R_6.055.119cd">
            <seg type="pāda" n="c">sa pūrṇavaktro na śaśāka vaktuṃ</seg>
            <seg type="pāda" n="d">cukūja kṛcchreṇa mumoha cāpi</seg>
        </li>
    </ul>
    <ul id="R_6.055.120">
        <li id="R_6.055.120ab">
            <seg type="pāda" n="a">athādade sūryamarīcikalpaṃ</seg>
            <seg type="pāda" n="b">sa brahmadaṇḍāntakakālakalpam</seg>
        </li>
        <li id="R_6.055.120cd">
            <seg type="pāda" n="c">ariṣṭam aindraṃ niśitaṃ supuṅkhaṃ</seg>
            <seg type="pāda" n="d">rāmaḥ śaraṃ mārutatulyavegam</seg>
        </li>
    </ul>
    <ul id="R_6.055.121">
        <li id="R_6.055.121ab">
            <seg type="pāda" n="a">taṃ vajrajāmbūnadacārupuṅkhaṃ</seg>
            <seg type="pāda" n="b">pradīptasūryajvalanaprakāśam</seg>
        </li>
        <li id="R_6.055.121cd">
            <seg type="pāda" n="c">mahendravajrāśanitulyavegaṃ</seg>
            <seg type="pāda" n="d">rāmaḥ pracikṣepa niśācarāya</seg>
        </li>
    </ul>
    <ul id="R_6.055.122">
        <li id="R_6.055.122ab">
            <seg type="pāda" n="a">sa sāyako rāghavabāhucodito</seg>
            <seg type="pāda" n="b">diśaḥ svabhāsā daśa saṃprakāśayan</seg>
        </li>
        <li id="R_6.055.122cd">
            <seg type="pāda" n="c">vidhūmavaiśvānaradīptadarśano</seg>
            <seg type="pāda" n="d">jagāma śakrāśanitulyavikramaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.123">
        <li id="R_6.055.123ab">
            <seg type="pāda" n="a">sa tan mahāparvatakūṭasaṃnibhaṃ</seg>
            <seg type="pāda" n="b">vivṛttadaṃṣṭraṃ calacārukuṇḍalam</seg>
        </li>
        <li id="R_6.055.123cd">
            <seg type="pāda" n="c">cakarta rakṣo'dhipateḥ śiras tadā</seg>
            <seg type="pāda" n="d">yathaiva vṛtrasya purā puraṃdaraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.124">
        <li id="R_6.055.124ab">
            <seg type="pāda" n="a">tad rāmabāṇābhihataṃ papāta</seg>
            <seg type="pāda" n="b">rakṣaḥśiraḥ parvatasaṃnikāśam</seg>
        </li>
        <li id="R_6.055.124cd">
            <seg type="pāda" n="c">babhañja caryāgṛhagopurāṇi</seg>
            <seg type="pāda" n="d">prākāram uccaṃ tam apātayac ca</seg>
        </li>
    </ul>
    <ul id="R_6.055.125">
        <li id="R_6.055.125ab">
            <seg type="pāda" n="a">tac cātikāyaṃ himavatprakāśaṃ</seg>
            <seg type="pāda" n="b">rakṣas tadā toyanidhau papāta</seg>
        </li>
        <li id="R_6.055.125cd">
            <seg type="pāda" n="c">grāhān mahāmīnacayān bhujaṃgamān</seg>
            <seg type="pāda" n="d">mamarda bhūmiṃ ca tathā viveśa</seg>
        </li>
    </ul>
    <ul id="R_6.055.126">
        <li id="R_6.055.126ab">
            <seg type="pāda" n="a">tasmir hate brāhmaṇadevaśatrau</seg>
            <seg type="pāda" n="b">mahābale saṃyati kumbhakarṇe</seg>
        </li>
        <li id="R_6.055.126cd">
            <seg type="pāda" n="c">cacāla bhūr bhūmidharāś ca sarve</seg>
            <seg type="pāda" n="d">harṣāc ca devās tumulaṃ praṇeduḥ</seg>
        </li>
    </ul>
    <ul id="R_6.055.127">
        <li id="R_6.055.127ab">
            <seg type="pāda" n="a">tatas tu devarṣimaharṣipannagāḥ</seg>
            <seg type="pāda" n="b">surāś ca bhūtāni suparṇaguhyakāḥ</seg>
        </li>
        <li id="R_6.055.127cd">
            <seg type="pāda" n="c">sayakṣagandharvagaṇā nabhogatāḥ</seg>
            <seg type="pāda" n="d">praharṣitā rāma parākrameṇa</seg>
        </li>
    </ul>
    <ul id="R_6.055.128">
        <li id="R_6.055.128ab">
            <seg type="pāda" n="a">praharṣam īyur bahavas tu vānarāḥ</seg>
            <seg type="pāda" n="b">prabuddhapadmapratimair ivānanaiḥ</seg>
        </li>
        <li id="R_6.055.128cd">
            <seg type="pāda" n="c">apūjayan rāghavam iṣṭabhāginaṃ</seg>
            <seg type="pāda" n="d">hate ripau bhīmabale durāsade</seg>
        </li>
    </ul>
    <ul id="R_6.055.129">
        <li id="R_6.055.129ab">
            <seg type="pāda" n="a">sa kumbhakarṇaṃ surasainyamardanaṃ</seg>
            <seg type="pāda" n="b">mahatsu yuddheṣv aparājitaśramam</seg>
        </li>
        <li id="R_6.055.129cd">
            <seg type="pāda" n="c">nananda hatvā bharatāgrajo raṇe</seg>
            <seg type="pāda" n="d">mahāsuraṃ vṛtram ivāmarādhipaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.056.001">
        <li id="R_6.056.001ab">kumbhakarṇaṃ hataṃ dṛṣṭvā rāghaveṇa mahātmanā</li>
        <li id="R_6.056.001cd">rākṣasā rākṣasendrāya rāvaṇāya nyavedayan</li>
    </ul>
    <ul id="R_6.056.002">
        <li id="R_6.056.002ab">śrutvā vinihataṃ saṃkhye kumbhakarṇaṃ mahābalam</li>
        <li id="R_6.056.002cd">rāvaṇaḥ śokasaṃtapto mumoha ca papāta ca</li>
    </ul>
    <ul id="R_6.056.003">
        <li id="R_6.056.003ab">pitṛvyaṃ nihataṃ śrutvā devāntakanarāntakau</li>
        <li id="R_6.056.003cd">triśirāś cātikāyaś ca ruruduḥ śokapīḍitāḥ</li>
    </ul>
    <ul id="R_6.056.004">
        <li id="R_6.056.004ab">bhrātaraṃ nihataṃ śrutvā rāmeṇākliṣṭakarmaṇā</li>
        <li id="R_6.056.004cd">mahodaramahāpārśvau śokākrāntau babhūvatuḥ</li>
    </ul>
    <ul id="R_6.056.005">
        <li id="R_6.056.005ab">tataḥ kṛcchrāt samāsādya saṃjñāṃ rākṣasapuṃgavaḥ</li>
        <li id="R_6.056.005cd">kumbhakarṇavadhād dīno vilalāpa sa rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.056.006">
        <li id="R_6.056.006ab">hā vīra ripudarpaghna kumbhakarṇa mahābala</li>
        <li id="R_6.056.006cd">śatrusainyaṃ pratāpyaikaḥ kva māṃ saṃtyajya gacchasi</li>
    </ul>
    <ul id="R_6.056.007">
        <li id="R_6.056.007ab">idānīṃ khalv ahaṃ nāsmi yasya me patito bhujaḥ</li>
        <li id="R_6.056.007cd">dakṣiṇo yaṃ samāśritya na bibhemi surāsurān</li>
    </ul>
    <ul id="R_6.056.008">
        <li id="R_6.056.008ab">katham evaṃvidho vīro devadānavadarpahā</li>
        <li id="R_6.056.008cd">kālāgnipratimo hy adya rāghaveṇa raṇe hataḥ</li>
    </ul>
    <ul id="R_6.056.009">
        <li id="R_6.056.009ab">yasya te vajraniṣpeṣo na kuryād vyasanaṃ sadā</li>
        <li id="R_6.056.009cd">sa kathaṃ rāmabāṇārtaḥ prasupto 'si mahītale</li>
    </ul>
    <ul id="R_6.056.010">
        <li id="R_6.056.010ab">ete devagaṇāḥ sārdham ṛṣibhir gagane sthitāḥ</li>
        <li id="R_6.056.010cd">nihataṃ tvāṃ raṇe dṛṣṭvā ninadanti praharṣitāḥ</li>
    </ul>
    <ul id="R_6.056.011">
        <li id="R_6.056.011ab">dhruvam adyaiva saṃhṛṣṭā labdhalakṣyāḥ plavaṃgamāḥ</li>
        <li id="R_6.056.011cd">ārokṣyantīha durgāṇi laṅkādvārāṇi sarvaśaḥ</li>
    </ul>
    <ul id="R_6.056.012">
        <li id="R_6.056.012ab">rājyena nāsti me kāryaṃ kiṃ kariṣyāmi sītayā</li>
        <li id="R_6.056.012cd">kumbhakarṇavihīnasya jīvite nāsti me ratiḥ</li>
    </ul>
    <ul id="R_6.056.013">
        <li id="R_6.056.013ab">yady ahaṃ bhrātṛhantāraṃ na hanmi yudhi rāghavam</li>
        <li id="R_6.056.013cd">nanu me maraṇaṃ śreyo na cedaṃ vyarthajīvitam</li>
    </ul>
    <ul id="R_6.056.014">
        <li id="R_6.056.014ab">adyaiva taṃ gamiṣyāmi deśaṃ yatrānujo mama</li>
        <li id="R_6.056.014cd">na hi bhrātṝn samutsṛjya kṣaṇaṃ jīvitum utsahe</li>
    </ul>
    <ul id="R_6.056.015">
        <li id="R_6.056.015ab">devā hi māṃ hasiṣyanti dṛṣṭvā pūrvāpakāriṇam</li>
        <li id="R_6.056.015cd">katham indraṃ jayiṣyāmi kumbhakarṇahate tvayi</li>
    </ul>
    <ul id="R_6.056.016">
        <li id="R_6.056.016ab">tad idaṃ mām anuprāptaṃ vibhīṣaṇavacaḥ śubham</li>
        <li id="R_6.056.016cd">yad ajñānān mayā tasya na gṛhītaṃ mahātmanaḥ</li>
    </ul>
    <ul id="R_6.056.017">
        <li id="R_6.056.017ab">vibhīṣaṇavaco yāvat kumbhakarṇaprahastayoḥ</li>
        <li id="R_6.056.017cd">vināśo 'yaṃ samutpanno māṃ vrīḍayati dāruṇaḥ</li>
    </ul>
    <ul id="R_6.056.018">
        <li id="R_6.056.018ab">tasyāyaṃ karmaṇaḥ prāto vipāko mama śokadaḥ</li>
        <li id="R_6.056.018cd">yan mayā dhārmikaḥ śrīmān sa nirasto vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.056.019">
        <li id="R_6.056.019ab">
            <seg type="pāda" n="a">iti bahuvidham ākulāntarātmā</seg>
            <seg type="pāda" n="b">kṛpaṇam atīva vilapya kumbhakarṇam</seg>
        </li>
        <li id="R_6.056.019cd">
            <seg type="pāda" n="c">nyapatad atha daśānano bhṛśārtas</seg>
            <seg type="pāda" n="d">tam anujam indraripuṃ hataṃ viditvā</seg>
        </li>
    </ul>
    <ul id="R_6.057.001">
        <li id="R_6.057.001ab">evaṃ vilapamānasya rāvaṇasya durātmanaḥ</li>
        <li id="R_6.057.001cd">śrutvā śokābhitaptasya triśirā vākyam abravīt</li>
    </ul>
    <ul id="R_6.057.002">
        <li id="R_6.057.002ab">evam eva mahāvīryo hato nas tāta madhyamaḥ</li>
        <li id="R_6.057.002cd">na tu satpuruṣā rājan vilapanti yathā bhavān</li>
    </ul>
    <ul id="R_6.057.003">
        <li id="R_6.057.003ab">nūnaṃ tribhuvaṇasyāpi paryāptas tvam asi prabho</li>
        <li id="R_6.057.003cd">sa kasmāt prākṛta iva śokasyātmānam īdṛśam</li>
    </ul>
    <ul id="R_6.057.004">
        <li id="R_6.057.004ab">brahmadattāsti te śaktiḥ kavacaḥ sāyako dhanuḥ</li>
        <li id="R_6.057.004cd">sahasrakharasaṃyukto ratho meghasamasvanaḥ</li>
    </ul>
    <ul id="R_6.057.005">
        <li id="R_6.057.005ab">tvayāsakṛd viśastreṇa viśastā devadānavāḥ</li>
        <li id="R_6.057.005cd">sa sarvāyudhasaṃpanno rāghavaṃ śāstum arhasi</li>
    </ul>
    <ul id="R_6.057.006">
        <li id="R_6.057.006ab">kāmaṃ tiṣṭha mahārājanirgamiṣyāmy ahaṃ raṇam</li>
        <li id="R_6.057.006cd">uddhariṣyāmi te śatrūn garuḍaḥ pannagān iha</li>
    </ul>
    <ul id="R_6.057.007">
        <li id="R_6.057.007ab">śambaro devarājena narako viṣṇunā yathā</li>
        <li id="R_6.057.007cd">tathādya śayitā rāmo mayā yudhi nipātitaḥ</li>
    </ul>
    <ul id="R_6.057.008">
        <li id="R_6.057.008ab">śrutvā triśiraso vākyaṃ rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.057.008cd">punar jātam ivātmānaṃ manyate kālacoditaḥ</li>
    </ul>
    <ul id="R_6.057.009">
        <li id="R_6.057.009ab">śrutvā triśiraso vākyaṃ devāntakanarāntakau</li>
        <li id="R_6.057.009cd">atikāyaś ca tejasvī babhūvur yuddhaharṣitāḥ</li>
    </ul>
    <ul id="R_6.057.010">
        <li id="R_6.057.010ab">tato 'ham aham ity evaṃ garjanto nairṛtarṣabhāḥ</li>
        <li id="R_6.057.010cd">rāvaṇasya sutā vīrāḥ śakratulyaparākramāḥ</li>
    </ul>
    <ul id="R_6.057.011">
        <li id="R_6.057.011ab">antarikṣacarāḥ sarve sarve māyā viśāradāḥ</li>
        <li id="R_6.057.011cd">sarve tridaśadarpaghnāḥ sarve ca raṇadurmadāḥ</li>
    </ul>
    <ul id="R_6.057.012">
        <li id="R_6.057.012ab">sarve 'strabalasaṃpannāḥ sarve vistīrṇa kīrtayaḥ</li>
        <li id="R_6.057.012cd">sarve samaram āsādya na śrūyante sma nirjitāḥ</li>
    </ul>
    <ul id="R_6.057.013">
        <li id="R_6.057.013ab">sarve 'straviduṣo vīrāḥ sarve yuddhaviśāradāḥ</li>
        <li id="R_6.057.013cd">sarve pravarajijñānāḥ sarve labdhavarās tathā</li>
    </ul>
    <ul id="R_6.057.014">
        <li id="R_6.057.014ab">
            <seg type="pāda" n="a">sa tais tathā bhāskaratulyavarcasaiḥ</seg>
            <seg type="pāda" n="b">sutair vṛtaḥ śatrubalapramardanaiḥ</seg>
        </li>
        <li id="R_6.057.014cd">
            <seg type="pāda" n="c">rarāja rājā maghavān yathāmarair</seg>
            <seg type="pāda" n="d">vṛto mahādānavadarpanāśanaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.057.015">
        <li id="R_6.057.015ab">sa putrān saṃpariṣvajya bhūṣayitvā ca bhūṣaṇaiḥ</li>
        <li id="R_6.057.015cd">āśīrbhiś ca praśastābhiḥ preṣayām āsa saṃyuge</li>
    </ul>
    <ul id="R_6.057.016">
        <li id="R_6.057.016ab">mahodaramahāpārśvau bhrātarau cāpi rāvaṇaḥ</li>
        <li id="R_6.057.016cd">rakṣaṇārthaṃ kumārāṇāṃ preṣayām āsa saṃyuge</li>
    </ul>
    <ul id="R_6.057.017">
        <li id="R_6.057.017ab">te 'bhivādya mahātmānaṃ rāvaṇaṃ ripurāvaṇam</li>
        <li id="R_6.057.017cd">kṛtvā pradakṣiṇaṃ caiva mahākāyāḥ pratasthire</li>
    </ul>
    <ul id="R_6.057.018">
        <li id="R_6.057.018ab">sarvauṣadhībhir gandhaiś ca samālabhya mahābalāḥ</li>
        <li id="R_6.057.018cd">nirjagmur nairṛtaśreṣṭhāḥ ṣaḍ ete yuddhakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.057.019">
        <li id="R_6.057.019ab">tataḥ sudarśanaṃ nāma nīlajīmūtasaṃnibham</li>
        <li id="R_6.057.019cd">airāvatakule jātam āruroha mahodaraḥ</li>
    </ul>
    <ul id="R_6.057.020">
        <li id="R_6.057.020ab">sarvāyudhasamāyuktaṃ tūṇībhiś ca svalaṃkṛtam</li>
        <li id="R_6.057.020cd">rarāja gajam āsthāya savitevāstamūrdhani</li>
    </ul>
    <ul id="R_6.057.021">
        <li id="R_6.057.021ab">hayottamasamāyuktaṃ sarvāyudhasamākulam</li>
        <li id="R_6.057.021cd">āruroha rathaśreṣṭhaṃ triśirā rāvaṇātmajaḥ</li>
    </ul>
    <ul id="R_6.057.022">
        <li id="R_6.057.022ab">triśirā ratham āsthāya virarāja dhanurdharaḥ</li>
        <li id="R_6.057.022cd">savidyudulkaḥ sajvālaḥ sendracāpa ivāmbudaḥ</li>
    </ul>
    <ul id="R_6.057.023">
        <li id="R_6.057.023ab">tribhiḥ kirīṭais triśirāḥ śuśubhe sa rathottame</li>
        <li id="R_6.057.023cd">himavān iva śailendras tribhiḥ kāñcanaparvataiḥ</li>
    </ul>
    <ul id="R_6.057.024">
        <li id="R_6.057.024ab">atikāyo 'pi tejasvī rākṣasendrasutas tadā</li>
        <li id="R_6.057.024cd">āruroha rathaśreṣṭhaṃ śreṣṭhaḥ sarvadhanuṣmatām</li>
    </ul>
    <ul id="R_6.057.025">
        <li id="R_6.057.025ab">sucakrākṣaṃ susaṃyuktaṃ sānukarṣaṃ sakūbaram</li>
        <li id="R_6.057.025cd">tūṇībāṇāsanair dīptaṃ prāsāsi parighākulam</li>
    </ul>
    <ul id="R_6.057.026">
        <li id="R_6.057.026ab">sa kāñcanavicitreṇa kirīṭena virājatā</li>
        <li id="R_6.057.026cd">bhūṣaṇaiś ca babhau meruḥ prabhābhir iva bhāsvaraḥ</li>
    </ul>
    <ul id="R_6.057.027">
        <li id="R_6.057.027ab">sa rarāja rathe tasmin rājasūnur mahābalaḥ</li>
        <li id="R_6.057.027cd">vṛto nairṛtaśārdūlair vajrapāṇir ivāmaraiḥ</li>
    </ul>
    <ul id="R_6.057.028">
        <li id="R_6.057.028ab">hayam uccaiḥśravaḥ prakhyaṃ śvetaṃ kanakabhūṣaṇam</li>
        <li id="R_6.057.028cd">manojavaṃ mahākāyam āruroha narāntakaḥ</li>
    </ul>
    <ul id="R_6.057.029">
        <li id="R_6.057.029ab">gṛhītvā prāsam uklābhaṃ virarāja narāntakaḥ</li>
        <li id="R_6.057.029cd">śaktim ādāya tejasvī guhaḥ śatruṣv ivāhave</li>
    </ul>
    <ul id="R_6.057.030">
        <li id="R_6.057.030ab">devāntakaḥ samādāya parighaṃ vajrabhūṣaṇam</li>
        <li id="R_6.057.030cd">parigṛhya giriṃ dorbhyāṃ vapur viṣṇor viḍambayan</li>
    </ul>
    <ul id="R_6.057.031">
        <li id="R_6.057.031ab">mahāpārśvo mahātejā gadām ādāya vīryavān</li>
        <li id="R_6.057.031cd">virarāja gadāpāṇiḥ kubera iva saṃyuge</li>
    </ul>
    <ul id="R_6.057.032">
        <li id="R_6.057.032ab">te pratasthur mahātmāno balair apratimair vṛtāḥ</li>
        <li id="R_6.057.032cd">surā ivāmarāvatyāṃ balair apratimair vṛtāḥ</li>
    </ul>
    <ul id="R_6.057.033">
        <li id="R_6.057.033ab">tān gajaiś ca turaṃgaiś ca rathaiś cāmbudanisvanaiḥ</li>
        <li id="R_6.057.033cd">anujagmur mahātmāno rākṣasāḥ pravarāyudhāḥ</li>
    </ul>
    <ul id="R_6.057.034">
        <li id="R_6.057.034ab">te virejur mahātmāno kumārāḥ sūryavarcasaḥ</li>
        <li id="R_6.057.034cd">kirīṭinaḥ śriyā juṣṭā grahā dīptā ivāmbare</li>
    </ul>
    <ul id="R_6.057.035">
        <li id="R_6.057.035ab">pragṛhītā babhau teṣāṃ chatrāṇām āvaliḥ sitā</li>
        <li id="R_6.057.035cd">śāradābhrapratīkāśāṃ haṃsāvalir ivāmbare</li>
    </ul>
    <ul id="R_6.057.036">
        <li id="R_6.057.036ab">maraṇaṃ vāpi niścitya śatrūṇāṃ vā parājayam</li>
        <li id="R_6.057.036cd">iti kṛtvā matiṃ vīrā nirjagmuḥ saṃyugārthinaḥ</li>
    </ul>
    <ul id="R_6.057.037">
        <li id="R_6.057.037ab">jagarjuś ca praṇeduś ca cikṣipuś cāpi sāyakān</li>
        <li id="R_6.057.037cd">jahṛṣuś ca mahātmāno niryānto yuddhadurmadāḥ</li>
    </ul>
    <ul id="R_6.057.038">
        <li id="R_6.057.038ab">kṣveḍitāsphoṭaninadaiḥ saṃcacāleva medinī</li>
        <li id="R_6.057.038cd">rakṣasāṃ siṃhanādaiś ca pusphoṭeva tadāmbaram</li>
    </ul>
    <ul id="R_6.057.039">
        <li id="R_6.057.039ab">te 'bhiniṣkramya muditā rākṣasendrā mahābalāḥ</li>
        <li id="R_6.057.039cd">dadṛśur vānarānīkaṃ samudyataśilānagam</li>
    </ul>
    <ul id="R_6.057.040">
        <li id="R_6.057.040ab">harayo 'pi mahātmāno dadṛśur nairṛtaṃ balam</li>
        <li id="R_6.057.040cd">hastyaśvarathasaṃbādhaṃ kiṅkiṇīśatanāditam</li>
    </ul>
    <ul id="R_6.057.041">
        <li id="R_6.057.041ab">nīlajīmūtasaṃkāśaṃ samudyatamahāyudham</li>
        <li id="R_6.057.041cd">dīptānalaraviprakhyair nairṛtaiḥ sarvato vṛtam</li>
    </ul>
    <ul id="R_6.057.042">
        <li id="R_6.057.042ab">tad dṛṣṭvā balam āyāntaṃ labdhalakṣyāḥ plavaṃgamāḥ</li>
        <li id="R_6.057.042cd">samudyatamahāśailāḥ saṃpraṇedur muhur muhuḥ</li>
    </ul>
    <ul id="R_6.057.043">
        <li id="R_6.057.043ab">
            <seg type="pāda" n="a">tataḥ samudghuṣṭaravaṃ niśamya</seg>
            <seg type="pāda" n="b">rakṣogaṇā vānarayūthapānām</seg>
        </li>
        <li id="R_6.057.043cd">
            <seg type="pāda" n="c">amṛṣyamāṇāḥ paraharṣam ugraṃ</seg>
            <seg type="pāda" n="d">mahābalā bhīmataraṃ vineduḥ</seg>
        </li>
    </ul>
    <ul id="R_6.057.044">
        <li id="R_6.057.044ab">te rākṣasabalaṃ ghoraṃ praviśya hariyūthapāḥ</li>
        <li id="R_6.057.044cd">vicerur udyataiḥ śailair nagāḥ śikhariṇo yathā</li>
    </ul>
    <ul id="R_6.057.045">
        <li id="R_6.057.045ab">ke cid ākāśam āviśya ke cid urvyāṃ plavaṃgamāḥ</li>
        <li id="R_6.057.045cd">rakṣaḥsainyeṣu saṃkruddhāś cerur drumaśilāyudhāḥ</li>
    </ul>
    <ul id="R_6.057.046">
        <li id="R_6.057.046ab">te pādapaśilāśailaiś cakrur vṛṣṭim anuttamām</li>
        <li id="R_6.057.046cd">bāṇaughair vāryamāṇāś ca harayo bhīmavikramāḥ</li>
    </ul>
    <ul id="R_6.057.047">
        <li id="R_6.057.047ab">siṃhanādān vineduś ca raṇe rākṣasavānarāḥ</li>
        <li id="R_6.057.047cd">śilābhiś cūrṇayām āsur yātudhānān plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.057.048">
        <li id="R_6.057.048ab">nijaghnuḥ saṃyuge kruddhāḥ kavacābharaṇāvṛtān</li>
        <li id="R_6.057.048cd">ke cid rathagatān vīrān gajavājigatān api</li>
    </ul>
    <ul id="R_6.057.049">
        <li id="R_6.057.049ab">nijaghnuḥ sahasāplutya yātudhānān plavaṃgamāḥ</li>
        <li id="R_6.057.049cd">śailaśṛṅganipātaiś ca muṣṭibhir vāntalocanāḥ</li>
        <li id="R_6.057.049ef">celuḥ petuś ca neduś ca tatra rākṣasapuṃgavāḥ</li>
    </ul>
    <ul id="R_6.057.050">
        <li id="R_6.057.050ab">tataḥ śailaiś ca khaḍgaiś ca visṛṣṭair harirākṣasaiḥ</li>
        <li id="R_6.057.050cd">muhūrtenāvṛtā bhūmir abhavac choṇitāplutā</li>
    </ul>
    <ul id="R_6.057.051">
        <li id="R_6.057.051ab">vikīrṇaparvatākārai rakṣobhir arimardanaiḥ</li>
        <li id="R_6.057.051cd">ākṣiptāḥ kṣipyamāṇāś ca bhagnaśūlāś ca vānaraiḥ</li>
    </ul>
    <ul id="R_6.057.052">
        <li id="R_6.057.052ab">vānarān vānarair eva jagnus te rajanīcarāḥ</li>
        <li id="R_6.057.052cd">rākṣasān rākṣasair eva jaghnus te vānarā api</li>
    </ul>
    <ul id="R_6.057.053">
        <li id="R_6.057.053ab">ākṣipya ca śilās teṣāṃ nijaghnū rākṣasā harīn</li>
        <li id="R_6.057.053cd">teṣāṃ cācchidya śastrāṇi jaghnū rakṣāṃsi vānarāḥ</li>
    </ul>
    <ul id="R_6.057.054">
        <li id="R_6.057.054ab">nijaghnuḥ śailaśūlāstrair vibhiduś ca parasparam</li>
        <li id="R_6.057.054cd">siṃhanādān vineduś ca raṇe vānararākṣasāḥ</li>
    </ul>
    <ul id="R_6.057.055">
        <li id="R_6.057.055ab">chinnavarmatanutrāṇā rākṣasā vānarair hatāḥ</li>
        <li id="R_6.057.055cd">rudhiraṃ prasrutās tatra rasasāram iva drumāḥ</li>
    </ul>
    <ul id="R_6.057.056">
        <li id="R_6.057.056ab">rathena ca rathaṃ cāpi vāraṇena ca vāraṇam</li>
        <li id="R_6.057.056cd">hayena ca hayaṃ ke cin nijaghnur vānarā raṇe</li>
    </ul>
    <ul id="R_6.057.057">
        <li id="R_6.057.057ab">kṣuraprair ardhacandraiś ca bhallaiś ca niśitaiḥ śaraiḥ</li>
        <li id="R_6.057.057cd">rākṣasā vānarendrāṇāṃ cicchiduḥ pādapāñ śilāḥ</li>
    </ul>
    <ul id="R_6.057.058">
        <li id="R_6.057.058ab">vikīrṇaiḥ parvatāgraiś ca drumaiś chinnaiś ca saṃyuge</li>
        <li id="R_6.057.058cd">hataiś ca kapirakṣobhir durgamā vasudhābhavat</li>
    </ul>
    <ul id="R_6.057.059">
        <li id="R_6.057.059ab">
            <seg type="pāda" n="a">tasmin pravṛtte tumule vimarde</seg>
            <seg type="pāda" n="b">prahṛṣyamāṇeṣu valī mukheṣu</seg>
        </li>
        <li id="R_6.057.059cd">
            <seg type="pāda" n="c">nipātyamāneṣu ca rākṣaseṣu</seg>
            <seg type="pāda" n="d">maharṣayo devagaṇāś ca neduḥ</seg>
        </li>
    </ul>
    <ul id="R_6.057.060">
        <li id="R_6.057.060ab">
            <seg type="pāda" n="a">tato hayaṃ mārutatulyavegam</seg>
            <seg type="pāda" n="b">āruhya śaktiṃ niśitāṃ pragṛhya</seg>
        </li>
        <li id="R_6.057.060cd">
            <seg type="pāda" n="c">narāntako vānararājasainyaṃ</seg>
            <seg type="pāda" n="d">mahārṇavaṃ mīna ivāviveśa</seg>
        </li>
    </ul>
    <ul id="R_6.057.061">
        <li id="R_6.057.061ab">
            <seg type="pāda" n="a">sa vānarān saptaśatāni vīraḥ</seg>
            <seg type="pāda" n="b">prāsena dīptena vinirbibheda</seg>
        </li>
        <li id="R_6.057.061cd">
            <seg type="pāda" n="c">ekaḥ kṣaṇenendraripur mahātmā</seg>
            <seg type="pāda" n="d">jaghāna sainyaṃ haripuṃgavānām</seg>
        </li>
    </ul>
    <ul id="R_6.057.062">
        <li id="R_6.057.062ab">dadṛśuś ca mahātmānaṃ hayapṛṣṭhe pratiṣṭhitam</li>
        <li id="R_6.057.062cd">carantaṃ harisainyeṣu vidyādharamaharṣayaḥ</li>
    </ul>
    <ul id="R_6.057.063">
        <li id="R_6.057.063ab">sa tasya dadṛśe mārgo māṃsaśoṇitakardamaḥ</li>
        <li id="R_6.057.063cd">patitaiḥ parvatākārair vānarair abhisaṃvṛtaḥ</li>
    </ul>
    <ul id="R_6.057.064">
        <li id="R_6.057.064ab">yāvad vikramituṃ buddhiṃ cakruḥ plavagapuṃgavāḥ</li>
        <li id="R_6.057.064cd">tāvad etān atikramya nirbibheda narāntakaḥ</li>
    </ul>
    <ul id="R_6.057.065">
        <li id="R_6.057.065ab">jvalantaṃ prāsam udyamya saṃgrāmānte narāntakaḥ</li>
        <li id="R_6.057.065cd">dadāha harisainyāni vanānīva vibhāvasuḥ</li>
    </ul>
    <ul id="R_6.057.066">
        <li id="R_6.057.066ab">yāvad utpāṭayām āsur vṛkṣāñ śailān vanaukasaḥ</li>
        <li id="R_6.057.066cd">tāvat prāsahatāḥ petur vajrakṛttā ivācalāḥ</li>
    </ul>
    <ul id="R_6.057.067">
        <li id="R_6.057.067ab">dikṣu sarvāsu balavān vicacāra narāntakaḥ</li>
        <li id="R_6.057.067cd">pramṛdnan sarvato yuddhe prāvṛṭkāle yathānilaḥ</li>
    </ul>
    <ul id="R_6.057.068">
        <li id="R_6.057.068ab">na śekur dhāvituṃ vīrā na sthātuṃ spandituṃ kutaḥ</li>
        <li id="R_6.057.068cd">utpatantaṃ sthitaṃ yāntaṃ sarvān vivyādha vīryavān</li>
    </ul>
    <ul id="R_6.057.069">
        <li id="R_6.057.069ab">ekenāntakakalpena prāsenādityatejasā</li>
        <li id="R_6.057.069cd">bhinnāni harisainyāni nipetur dharaṇītale</li>
    </ul>
    <ul id="R_6.057.070">
        <li id="R_6.057.070ab">vajraniṣpeṣasadṛśaṃ prāsasyābhinipātanam</li>
        <li id="R_6.057.070cd">na śekur vānarāḥ soḍhuṃ te vinedur mahāsvanam</li>
    </ul>
    <ul id="R_6.057.071">
        <li id="R_6.057.071ab">patatāṃ harivīrāṇāṃ rūpāṇi pracakāśire</li>
        <li id="R_6.057.071cd">vajrabhinnāgrakūṭānāṃ śailānāṃ patatām iva</li>
    </ul>
    <ul id="R_6.057.072">
        <li id="R_6.057.072ab">ye tu pūrvaṃ mahātmānaḥ kumbhakarṇena pātitāḥ</li>
        <li id="R_6.057.072cd">te 'svasthā vānaraśreṣṭhāḥ sugrīvam upatasthire</li>
    </ul>
    <ul id="R_6.057.073">
        <li id="R_6.057.073ab">viprekṣamāṇaḥ sugrīvo dadarśa harivāhinīm</li>
        <li id="R_6.057.073cd">narāntakabhayatrastāṃ vidravantīm itas tataḥ</li>
    </ul>
    <ul id="R_6.057.074">
        <li id="R_6.057.074ab">vidrutāṃ vāhinīṃ dṛṣṭvā sa dadarśa narāntakam</li>
        <li id="R_6.057.074cd">gṛhītaprāsam āyāntaṃ hayapṛṣṭhe pratiṣṭhitam</li>
    </ul>
    <ul id="R_6.057.075">
        <li id="R_6.057.075ab">athovāca mahātejāḥ sugrīvo vānarādhipaḥ</li>
        <li id="R_6.057.075cd">kumāram aṅgadaṃ vīraṃ śakratulyaparākramam</li>
    </ul>
    <ul id="R_6.057.076">
        <li id="R_6.057.076ab">gacchainaṃ rākṣasaṃ vīra yo 'sau turagam āsthitaḥ</li>
        <li id="R_6.057.076cd">kṣobhayantaṃ haribalaṃ kṣipraṃ prāṇair viyojaya</li>
    </ul>
    <ul id="R_6.057.077">
        <li id="R_6.057.077ab">sa bhartur vacanaṃ śrutvā niṣpapātāṅgadas tadā</li>
        <li id="R_6.057.077cd">anīkān meghasaṃkāśān meghānīkād ivāṃśumān</li>
    </ul>
    <ul id="R_6.057.078">
        <li id="R_6.057.078ab">śailasaṃghātasaṃkāśo harīṇām uttamo 'ṅgadaḥ</li>
        <li id="R_6.057.078cd">rarājāṅgadasaṃnaddhaḥ sadhātur iva parvataḥ</li>
    </ul>
    <ul id="R_6.057.079">
        <li id="R_6.057.079ab">nirāyudho mahātejāḥ kevalaṃ nakhadaṃṣṭravān</li>
        <li id="R_6.057.079cd">narāntakam abhikramya vāliputro 'bravīd vacaḥ</li>
    </ul>
    <ul id="R_6.057.080">
        <li id="R_6.057.080ab">tiṣṭha kiṃ prākṛtair ebhir haribhis tvaṃ kariṣyasi</li>
        <li id="R_6.057.080cd">asmin vajrasamasparśe prāsaṃ kṣipa mamorasi</li>
    </ul>
    <ul id="R_6.057.081">
        <li id="R_6.057.081ab">aṅgadasya vacaḥ śrutvā pracukrodha narāntakaḥ</li>
        <li id="R_6.057.081cd">saṃdaśya daśanair oṣṭhaṃ niśvasya ca bhujaṃgavat</li>
    </ul>
    <ul id="R_6.057.082">
        <li id="R_6.057.082ab">
            <seg type="pāda" n="a">sa prāsam āvidhya tadāṅgadāya</seg>
            <seg type="pāda" n="b">samujjvalantaṃ sahasotsasarja</seg>
        </li>
        <li id="R_6.057.082cd">
            <seg type="pāda" n="c">sa vāliputrorasi vajrakalpe</seg>
            <seg type="pāda" n="d">babhūva bhagno nyapatac ca bhūmau</seg>
        </li>
    </ul>
    <ul id="R_6.057.083">
        <li id="R_6.057.083ab">
            <seg type="pāda" n="a">taṃ prāsam ālokya tadā vibhagnaṃ</seg>
            <seg type="pāda" n="b">suparṇakṛttoragabhogakalpam</seg>
        </li>
        <li id="R_6.057.083cd">
            <seg type="pāda" n="c">talaṃ samudyamya sa vāliputras</seg>
            <seg type="pāda" n="d">turaṃgamasyābhijaghāna mūrdhni</seg>
        </li>
    </ul>
    <ul id="R_6.057.084">
        <li id="R_6.057.084ab">
            <seg type="pāda" n="a">nimagnapādaḥ sphuṭitākṣi tāro</seg>
            <seg type="pāda" n="b">niṣkrāntajihvo 'calasaṃnikāśaḥ</seg>
        </li>
        <li id="R_6.057.084cd">
            <seg type="pāda" n="c">sa tasya vājī nipapāta bhūmau</seg>
            <seg type="pāda" n="d">talaprahāreṇa vikīrṇamūrdhā</seg>
        </li>
    </ul>
    <ul id="R_6.057.085">
        <li id="R_6.057.085ab">
            <seg type="pāda" n="a">narāntakaḥ krodhavaśaṃ jagāma</seg>
            <seg type="pāda" n="b">hataṃ turagaṃ patitaṃ nirīkṣya</seg>
        </li>
        <li id="R_6.057.085cd">
            <seg type="pāda" n="c">sa muṣṭim udyamya mahāprabhāvo</seg>
            <seg type="pāda" n="d">jaghāna śīrṣe yudhi vāliputram</seg>
        </li>
    </ul>
    <ul id="R_6.057.086">
        <li id="R_6.057.086ab">
            <seg type="pāda" n="a">athāṅgado muṣṭivibhinnamūrdhā</seg>
            <seg type="pāda" n="b">susrāva tīvraṃ rudhiraṃ bhṛśoṣṇam</seg>
        </li>
        <li id="R_6.057.086cd">
            <seg type="pāda" n="c">muhur vijajvāla mumoha cāpi</seg>
            <seg type="pāda" n="d">saṃjñāṃ samāsādya visiṣmiye ca</seg>
        </li>
    </ul>
    <ul id="R_6.057.087">
        <li id="R_6.057.087ab">
            <seg type="pāda" n="a">athāṅgado vajrasamānavegaṃ</seg>
            <seg type="pāda" n="b">saṃvartya muṣṭiṃ giriśṛṅgakalpam</seg>
        </li>
        <li id="R_6.057.087cd">
            <seg type="pāda" n="c">nipātayām āsa tadā mahātmā</seg>
            <seg type="pāda" n="d">narāntakasyorasi vāliputraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.057.088">
        <li id="R_6.057.088ab">
            <seg type="pāda" n="a">sa muṣṭiniṣpiṣṭavibhinnavakṣā</seg>
            <seg type="pāda" n="b">jvālāṃ vamañ śoṇitadigdhagātraḥ</seg>
        </li>
        <li id="R_6.057.088cd">
            <seg type="pāda" n="c">narāntako bhūmitale papāta</seg>
            <seg type="pāda" n="d">yathācalo vajranipātabhagnaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.057.089">
        <li id="R_6.057.089ab">
            <seg type="pāda" n="a">athāntarikṣe tridaśottamānāṃ</seg>
            <seg type="pāda" n="b">vanaukasāṃ caiva mahāpraṇādaḥ</seg>
        </li>
        <li id="R_6.057.089cd">
            <seg type="pāda" n="c">babhūva tasmin nihate 'gryavīre</seg>
            <seg type="pāda" n="d">narāntake vālisutena saṃkhye</seg>
        </li>
    </ul>
    <ul id="R_6.057.090">
        <li id="R_6.057.090ab">
            <seg type="pāda" n="a">athāṅgado rāmamanaḥ praharṣaṇaṃ</seg>
            <seg type="pāda" n="b">suduṣkaraṃ taṃ kṛtavān hi vikramam</seg>
        </li>
        <li id="R_6.057.090cd">
            <seg type="pāda" n="c">visiṣmiye so 'py ativīrya vikramaḥ</seg>
            <seg type="pāda" n="d">punaś ca yuddhe sa babhūva harṣitaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.058.001">
        <li id="R_6.058.001ab">narāntakaṃ hataṃ dṛṣṭvā cukruśur nairṛtarṣabhāḥ</li>
        <li id="R_6.058.001cd">devāntakas trimūrdhā ca paulastyaś ca mahodaraḥ</li>
    </ul>
    <ul id="R_6.058.002">
        <li id="R_6.058.002ab">ārūḍho meghasaṃkāśaṃ vāraṇendraṃ mahodaraḥ</li>
        <li id="R_6.058.002cd">vāliputraṃ mahāvīryam abhidudrāva vīryavān</li>
    </ul>
    <ul id="R_6.058.003">
        <li id="R_6.058.003ab">bhrātṛvyasanasaṃtaptas tadā devāntako balī</li>
        <li id="R_6.058.003cd">ādāya parighaṃ dīptam aṅgadaṃ samabhidravat</li>
    </ul>
    <ul id="R_6.058.004">
        <li id="R_6.058.004ab">ratham ādityasaṃkāśaṃ yuktaṃ paramavājibhiḥ</li>
        <li id="R_6.058.004cd">āsthāya triśirā vīro vāliputram athābhyayāt</li>
    </ul>
    <ul id="R_6.058.005">
        <li id="R_6.058.005ab">sa tribhir devadarpaghnair nairṛtendrair abhidrutaḥ</li>
        <li id="R_6.058.005cd">vṛkṣam utpāṭayām āsa mahāviṭapam aṅgadaḥ</li>
    </ul>
    <ul id="R_6.058.006">
        <li id="R_6.058.006ab">devāntakāya taṃ vīraś cikṣepa sahasāṅgadaḥ</li>
        <li id="R_6.058.006cd">mahāvṛkṣaṃ mahāśākhaṃ śakro dīptam ivāśanim</li>
    </ul>
    <ul id="R_6.058.007">
        <li id="R_6.058.007ab">triśirās taṃ praciccheda śarair āśīviṣopamaiḥ</li>
        <li id="R_6.058.007cd">sa vṛkṣaṃ kṛttam ālokya utpapāta tato 'ṅgadaḥ</li>
    </ul>
    <ul id="R_6.058.008">
        <li id="R_6.058.008ab">sa vavarṣa tato vṛkṣāñ śilāś ca kapikuñjaraḥ</li>
        <li id="R_6.058.008cd">tān praciccheda saṃkruddhas triśirā niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.058.009">
        <li id="R_6.058.009ab">parighāgreṇa tān vṛkṣān babhañja ca surāntakaḥ</li>
        <li id="R_6.058.009cd">triśirāś cāṅgadaṃ vīram abhidudrāva sāyakaiḥ</li>
    </ul>
    <ul id="R_6.058.010">
        <li id="R_6.058.010ab">gajena samabhidrutya vāliputraṃ mahodaraḥ</li>
        <li id="R_6.058.010cd">jaghānorasi saṃkruddhas tomarair vajrasaṃnibhaiḥ</li>
    </ul>
    <ul id="R_6.058.011">
        <li id="R_6.058.011ab">devāntakaś ca saṃkruddhaḥ parigheṇa tadāṅgadam</li>
        <li id="R_6.058.011cd">upagamyābhihatyāśu vyapacakrāma vegavān</li>
    </ul>
    <ul id="R_6.058.012">
        <li id="R_6.058.012ab">sa tribhir nairṛtaśreṣṭhair yugapat samabhidrutaḥ</li>
        <li id="R_6.058.012cd">na vivyathe mahātejā vāliputraḥ pratāpavān</li>
    </ul>
    <ul id="R_6.058.013">
        <li id="R_6.058.013ab">talena bhṛśam utpatya jaghānāsya mahāgajam</li>
        <li id="R_6.058.013cd">petatur locane tasya vinanāda sa vāraṇaḥ</li>
    </ul>
    <ul id="R_6.058.014">
        <li id="R_6.058.014ab">viṣāṇaṃ cāsya niṣkṛṣya vāliputro mahābalaḥ</li>
        <li id="R_6.058.014cd">devāntakam abhidrutya tāḍayām āsa saṃyuge</li>
    </ul>
    <ul id="R_6.058.015">
        <li id="R_6.058.015ab">sa vihvalitasarvāṅgo vātoddhata iva drumaḥ</li>
        <li id="R_6.058.015cd">lākṣārasasavarṇaṃ ca susrāva rudhiraṃ mukhāt</li>
    </ul>
    <ul id="R_6.058.016">
        <li id="R_6.058.016ab">athāśvāsya mahātejāḥ kṛcchrād devāntako balī</li>
        <li id="R_6.058.016cd">āvidhya parighaṃ ghoram ājaghāna tadāṅgadam</li>
    </ul>
    <ul id="R_6.058.017">
        <li id="R_6.058.017ab">parighābhihataś cāpi vānarendrātmajas tadā</li>
        <li id="R_6.058.017cd">jānubhyāṃ patito bhūmau punar evotpapāta ha</li>
    </ul>
    <ul id="R_6.058.018">
        <li id="R_6.058.018ab">samutpatantaṃ triśirās tribhir āśīviṣopamaiḥ</li>
        <li id="R_6.058.018cd">ghorair haripateḥ putraṃ lalāṭe 'bhijaghāna ha</li>
    </ul>
    <ul id="R_6.058.019">
        <li id="R_6.058.019ab">tato 'ṅgadaṃ parikṣiptaṃ tribhir nairṛtapuṃgavaiḥ</li>
        <li id="R_6.058.019cd">hanūmān api vijñāya nīlaś cāpi pratasthatuḥ</li>
    </ul>
    <ul id="R_6.058.020">
        <li id="R_6.058.020ab">tataś cikṣepa śailāgraṃ nīlas triśirase tadā</li>
        <li id="R_6.058.020cd">tad rāvaṇasuto dhīmān bibheda niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.058.021">
        <li id="R_6.058.021ab">tad bāṇaśatanirbhinnaṃ vidāritaśilātalam</li>
        <li id="R_6.058.021cd">savisphuliṅgaṃ sajvālaṃ nipapāta gireḥ śiraḥ</li>
    </ul>
    <ul id="R_6.058.022">
        <li id="R_6.058.022ab">tato jṛmbhitam ālokya harṣād devāntakas tadā</li>
        <li id="R_6.058.022cd">parigheṇābhidudrāva mārutātmajam āhave</li>
    </ul>
    <ul id="R_6.058.023">
        <li id="R_6.058.023ab">tam āpatantam utpatya hanūmān mārutātmajaḥ</li>
        <li id="R_6.058.023cd">ājaghāna tadā mūrdhni vajravegena muṣṭinā</li>
    </ul>
    <ul id="R_6.058.024">
        <li id="R_6.058.024ab">
            <seg type="pāda" n="a">sa muṣṭiniṣpiṣṭavikīrṇamūrdhā</seg>
            <seg type="pāda" n="b">nirvāntadantākṣivilambijihvaḥ</seg>
        </li>
        <li id="R_6.058.024cd">
            <seg type="pāda" n="c">devāntako rākṣasarājasūnur</seg>
            <seg type="pāda" n="d">gatāsur urvyāṃ sahasā papāta</seg>
        </li>
    </ul>
    <ul id="R_6.058.025">
        <li id="R_6.058.025ab">
            <seg type="pāda" n="a">tasmin hate rākṣasayodhamukhye</seg>
            <seg type="pāda" n="b">mahābale saṃyati devaśatrau</seg>
        </li>
        <li id="R_6.058.025cd">
            <seg type="pāda" n="c">kruddhas trimūrdhā niśitāgram ugraṃ</seg>
            <seg type="pāda" n="d">vavarṣa nīlorasi bāṇavarṣam</seg>
        </li>
    </ul>
    <ul id="R_6.058.026">
        <li id="R_6.058.026ab">
            <seg type="pāda" n="a">sa taiḥ śaraughair abhivarṣyamāṇo</seg>
            <seg type="pāda" n="b">vibhinnagātraḥ kapisainyapālaḥ</seg>
        </li>
        <li id="R_6.058.026cd">
            <seg type="pāda" n="c">nīlo babhūvātha visṛṣṭagātro</seg>
            <seg type="pāda" n="d">viṣṭambhitas tena mahābalena</seg>
        </li>
    </ul>
    <ul id="R_6.058.027">
        <li id="R_6.058.027ab">
            <seg type="pāda" n="a">tatas tu nīlaḥ pratilabhya saṃjñāṃ</seg>
            <seg type="pāda" n="b">śailaṃ samutpāṭya savṛkṣaṣaṇḍam</seg>
        </li>
        <li id="R_6.058.027cd">
            <seg type="pāda" n="c">tataḥ samutpatya bhṛśogravego</seg>
            <seg type="pāda" n="d">mahodaraṃ tena jaghāna mūrdhni</seg>
        </li>
    </ul>
    <ul id="R_6.058.028">
        <li id="R_6.058.028ab">
            <seg type="pāda" n="a">tataḥ sa śailābhinipātabhagno</seg>
            <seg type="pāda" n="b">mahodaras tena saha dvipena</seg>
        </li>
        <li id="R_6.058.028cd">
            <seg type="pāda" n="c">vipothito bhūmitale gatāsuḥ</seg>
            <seg type="pāda" n="d">papāta varjābhihato yathādriḥ</seg>
        </li>
    </ul>
    <ul id="R_6.058.029">
        <li id="R_6.058.029ab">pitṛvyaṃ nihataṃ dṛṣṭvā triśirāś cāpam ādade</li>
        <li id="R_6.058.029cd">hanūmantaṃ ca saṃkruddho vivyādha niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.058.030">
        <li id="R_6.058.030ab">hanūmāṃs tu samutpatya hayāṃs triśirasas tadā</li>
        <li id="R_6.058.030cd">vidadāra nakhaiḥ kruddho gajendraṃ mṛgarāḍ iva</li>
    </ul>
    <ul id="R_6.058.031">
        <li id="R_6.058.031ab">atha śaktiṃ samādāya kālarātrim ivāntakaḥ</li>
        <li id="R_6.058.031cd">cikṣepānilaputrāya triśirā rāvaṇātmajaḥ</li>
    </ul>
    <ul id="R_6.058.032">
        <li id="R_6.058.032ab">divi kṣiptām ivolkāṃ tāṃ śaktiṃ kṣiptām asaṃgatām</li>
        <li id="R_6.058.032cd">gṛhītvā hariśārdūlo babhañja ca nanāda ca</li>
    </ul>
    <ul id="R_6.058.033">
        <li id="R_6.058.033ab">tāṃ dṛṣṭvā ghorasaṃkāśāṃ śaktiṃ bhagnāṃ hanūmatā</li>
        <li id="R_6.058.033cd">prahṛṣṭā vānaragaṇā vinedur jaladā iva</li>
    </ul>
    <ul id="R_6.058.034">
        <li id="R_6.058.034ab">tataḥ khaḍgaṃ samudyamya triśirā rākṣasottamaḥ</li>
        <li id="R_6.058.034cd">nicakhāna tadā roṣād vānarendrasya vakṣasi</li>
    </ul>
    <ul id="R_6.058.035">
        <li id="R_6.058.035ab">khaḍgaprahārābhihato hanūmān mārutātmajaḥ</li>
        <li id="R_6.058.035cd">ājaghāna trimūrdhānaṃ talenorasi vīryavān</li>
    </ul>
    <ul id="R_6.058.036">
        <li id="R_6.058.036ab">sa talabhihatas tena srastahastāmbaro bhuvi</li>
        <li id="R_6.058.036cd">nipapāta mahātejās triśirās tyaktacetanaḥ</li>
    </ul>
    <ul id="R_6.058.037">
        <li id="R_6.058.037ab">sa tasya patataḥ khaḍgaṃ samācchidya mahākapiḥ</li>
        <li id="R_6.058.037cd">nanāda girisaṃkāśas trāsayan sarvanairṛtān</li>
    </ul>
    <ul id="R_6.058.038">
        <li id="R_6.058.038ab">amṛṣyamāṇas taṃ ghoṣam utpapāta niśācaraḥ</li>
        <li id="R_6.058.038cd">utpatya ca hanūmantaṃ tāḍayām āsa muṣṭinā</li>
    </ul>
    <ul id="R_6.058.039">
        <li id="R_6.058.039ab">tena muṣṭiprahāreṇa saṃcukopa mahākapiḥ</li>
        <li id="R_6.058.039cd">kupitaś ca nijagrāha kirīṭe rākṣasarṣabham</li>
    </ul>
    <ul id="R_6.058.040">
        <li id="R_6.058.040ab">
            <seg type="pāda" n="a">sa tasya śīrṣāṇy asinā śitena</seg>
            <seg type="pāda" n="b">kirīṭajuṣṭāni sakuṇḍalāni</seg>
        </li>
        <li id="R_6.058.040cd">
            <seg type="pāda" n="c">kruddhaḥ praciccheda suto 'nilasya</seg>
            <seg type="pāda" n="d">tvaṣṭuḥ sutasyeva śirāṃsi śakraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.058.041">
        <li id="R_6.058.041ab">
            <seg type="pāda" n="a">tāny āyatākṣāṇy agasaṃnibhāni</seg>
            <seg type="pāda" n="b">pradīptavaiśvānaralocanāni</seg>
        </li>
        <li id="R_6.058.041cd">
            <seg type="pāda" n="c">petuḥ śirāṃsīndraripor dharaṇyāṃ</seg>
            <seg type="pāda" n="d">jyotīṃṣi muktāni yathārkamārgāt</seg>
        </li>
    </ul>
    <ul id="R_6.058.042">
        <li id="R_6.058.042ab">
            <seg type="pāda" n="a">tasmin hate devaripau triśīrṣe</seg>
            <seg type="pāda" n="b">hanūmata śakraparākrameṇa</seg>
        </li>
        <li id="R_6.058.042cd">
            <seg type="pāda" n="c">neduḥ plavaṃgāḥ pracacāla bhūmī</seg>
            <seg type="pāda" n="d">rakṣāṃsy atho dudruvire samantāt</seg>
        </li>
    </ul>
    <ul id="R_6.058.043">
        <li id="R_6.058.043ab">hataṃ triśirasaṃ dṛṣṭvā tathaiva ca mahodaram</li>
        <li id="R_6.058.043cd">hatau prekṣya durādharṣau devāntakanarāntakau</li>
    </ul>
    <ul id="R_6.058.044">
        <li id="R_6.058.044ab">cukopa paramāmarṣī mahāpārśvo mahābalaḥ</li>
        <li id="R_6.058.044cd">jagrāhārciṣmatīṃ cāpi gadāṃ sarvāyasīṃ śubhām</li>
    </ul>
    <ul id="R_6.058.045">
        <li id="R_6.058.045ab">hemapaṭṭaparikṣiptāṃ māṃsaśoṇitalepanām</li>
        <li id="R_6.058.045cd">virājamānāṃ vapuṣā śatruśoṇitarañjitām</li>
    </ul>
    <ul id="R_6.058.046">
        <li id="R_6.058.046ab">tejasā saṃpradīptāgrāṃ raktamālyavibhūṣitām</li>
        <li id="R_6.058.046cd">airāvatamahāpadmasārvabhauma bhayāvahām</li>
    </ul>
    <ul id="R_6.058.047">
        <li id="R_6.058.047ab">gadām ādāya saṃkruddho mahāpārśvo mahābalaḥ</li>
        <li id="R_6.058.047cd">harīn samabhidudrāva yugāntāgnir iva jvalan</li>
    </ul>
    <ul id="R_6.058.048">
        <li id="R_6.058.048ab">atharṣayaḥ samutpatya vānaro ravaṇānujam</li>
        <li id="R_6.058.048cd">mahāpārśvam upāgamya tasthau tasyāgrato balī</li>
    </ul>
    <ul id="R_6.058.049">
        <li id="R_6.058.049ab">taṃ purastāt sthitaṃ dṛṣṭvā vānaraṃ parvatopamam</li>
        <li id="R_6.058.049cd">ājaghānorasi kruddho gadayā vajrakalpayā</li>
    </ul>
    <ul id="R_6.058.050">
        <li id="R_6.058.050ab">sa tayābhihatas tena gadayā vānararṣabhaḥ</li>
        <li id="R_6.058.050cd">bhinnavakṣāḥ samādhūtaḥ susrāva rudhiraṃ bahu</li>
    </ul>
    <ul id="R_6.058.051">
        <li id="R_6.058.051ab">sa saṃprāpya cirāt saṃjñām ṛṣabho vānararṣabhaḥ</li>
        <li id="R_6.058.051cd">kruddho visphuramāṇauṣṭho mahāpārśvam udaikṣata</li>
    </ul>
    <ul id="R_6.058.052">
        <li id="R_6.058.052ab">tāṃ gṛhītvā gadāṃ bhīmām āvidhya ca punaḥ punaḥ</li>
        <li id="R_6.058.052cd">mattānīkaṃ mahāpārśvaṃ jaghāna raṇamūrdhani</li>
    </ul>
    <ul id="R_6.058.053">
        <li id="R_6.058.053ab">sa svayā gadayā bhinno vikīrṇadaśanekṣaṇaḥ</li>
        <li id="R_6.058.053cd">nipapāta mahāpārśvo vajrāhata ivācalaḥ</li>
    </ul>
    <ul id="R_6.058.054">
        <li id="R_6.058.054ab">
            <seg type="pāda" n="a">tasmin hate bhrātari rāvaṇasya</seg>
            <seg type="pāda" n="b">tan nairṛtānāṃ balam arṇavābham</seg>
        </li>
        <li id="R_6.058.054cd">
            <seg type="pāda" n="c">tyaktāyudhaṃ kevalajīvitārthaṃ</seg>
            <seg type="pāda" n="d">dudrāva bhinnārṇavasaṃnikāśam</seg>
        </li>
    </ul>
    <ul id="R_6.059.001">
        <li id="R_6.059.001ab">svabalaṃ vyathitaṃ dṛṣṭvā tumulaṃ lomaharṣaṇam</li>
        <li id="R_6.059.001cd">bhrātṝṃś ca nihatān dṛṣṭvā śakratulyaparākramān</li>
    </ul>
    <ul id="R_6.059.002">
        <li id="R_6.059.002ab">pitṛvyau cāpi saṃdṛśya samare saṃniṣūditau</li>
        <li id="R_6.059.002cd">mahodaramahāpārśvau bhrātarau rākṣasarṣabhau</li>
    </ul>
    <ul id="R_6.059.003">
        <li id="R_6.059.003ab">cukopa ca mahātejā brahmadattavaro yudhi</li>
        <li id="R_6.059.003cd">atikāyo 'drisaṃkāśo devadānavadarpahā</li>
    </ul>
    <ul id="R_6.059.004">
        <li id="R_6.059.004ab">sa bhāskarasahasrasya saṃghātam iva bhāsvaram</li>
        <li id="R_6.059.004cd">ratham āsthāya śakrārir abhidudrāva vānarān</li>
    </ul>
    <ul id="R_6.059.005">
        <li id="R_6.059.005ab">sa visphārya mahac cāpaṃ kirīṭī mṛṣṭakuṇḍalaḥ</li>
        <li id="R_6.059.005cd">nāma viśrāvayām āsa nanāda ca mahāsvanam</li>
    </ul>
    <ul id="R_6.059.006">
        <li id="R_6.059.006ab">tena siṃhapraṇādena nāmaviśrāvaṇena ca</li>
        <li id="R_6.059.006cd">jyāśabdena ca bhīmena trāsayām āsa vānarān</li>
    </ul>
    <ul id="R_6.059.007">
        <li id="R_6.059.007ab">te tasya rūpam ālokya yathā viṣṇos trivikrame</li>
        <li id="R_6.059.007cd">bhayārtā vānarāḥ sarve vidravanti diśo daśa</li>
    </ul>
    <ul id="R_6.059.008">
        <li id="R_6.059.008ab">te 'tikāyaṃ samāsādya vānarā mūḍhacetasaḥ</li>
        <li id="R_6.059.008cd">śaraṇyaṃ śaraṇaṃ jagmur lakṣmaṇāgrajam āhave</li>
    </ul>
    <ul id="R_6.059.009">
        <li id="R_6.059.009ab">tato 'tikāyaṃ kākutstho rathasthaṃ parvatopamam</li>
        <li id="R_6.059.009cd">dadarśa dhanvinaṃ dūrād garjantaṃ kālameghavat</li>
    </ul>
    <ul id="R_6.059.010">
        <li id="R_6.059.010ab">sa taṃ dṛṣṭvā mahātmānaṃ rāghavas tu suvismitaḥ</li>
        <li id="R_6.059.010cd">vānarān sāntvayitvā tu vibhīṣaṇam uvāca ha</li>
    </ul>
    <ul id="R_6.059.011">
        <li id="R_6.059.011ab">ko 'sau parvatasaṃkāśo dhanuṣmān harilocanaḥ</li>
        <li id="R_6.059.011cd">yukte hayasahasreṇa viśāle syandane sthitaḥ</li>
    </ul>
    <ul id="R_6.059.012">
        <li id="R_6.059.012ab">ya eṣa niśitaiḥ śūlaiḥ sutīkṣṇaiḥ prāsatomaraiḥ</li>
        <li id="R_6.059.012cd">arciṣmadbhir vṛto bhāti bhūtair iva maheśvaraḥ</li>
    </ul>
    <ul id="R_6.059.013">
        <li id="R_6.059.013ab">kālajihvāprakāśābhir ya eṣo 'bhivirājate</li>
        <li id="R_6.059.013cd">āvṛto rathaśaktībhir vidyudbhir iva toyadaḥ</li>
    </ul>
    <ul id="R_6.059.014">
        <li id="R_6.059.014ab">dhanūṃsi cāsya sajyāni hemapṛṣṭhāni sarvaśaḥ</li>
        <li id="R_6.059.014cd">śobhayanti rathaśreṣṭhaṃ śakrapātam ivāmbaram</li>
    </ul>
    <ul id="R_6.059.015">
        <li id="R_6.059.015ab">ka eṣa rakṣaḥ śārdūlo raṇabhūmiṃ virājayan</li>
        <li id="R_6.059.015cd">abhyeti rathināṃ śreṣṭho rathenādityatejasā</li>
    </ul>
    <ul id="R_6.059.016">
        <li id="R_6.059.016ab">dhvajaśṛṅgapratiṣṭhena rāhuṇābhivirājate</li>
        <li id="R_6.059.016cd">sūryaraśmiprabhair bāṇair diśo daśa virājayan</li>
    </ul>
    <ul id="R_6.059.017">
        <li id="R_6.059.017ab">triṇataṃ meghanirhrādaṃ hemapṛṣṭham alaṃkṛtam</li>
        <li id="R_6.059.017cd">śatakratudhanuḥprakhyaṃ dhanuś cāsya virājate</li>
    </ul>
    <ul id="R_6.059.018">
        <li id="R_6.059.018ab">sadhvajaḥ sapatākaś ca sānukarṣo mahārathaḥ</li>
        <li id="R_6.059.018cd">catuḥsādisamāyukto meghastanitanisvanaḥ</li>
    </ul>
    <ul id="R_6.059.019">
        <li id="R_6.059.019ab">viṃśatir daśa cāṣṭau ca tūṇīraratham āsthitāḥ</li>
        <li id="R_6.059.019cd">kārmukāṇi ca bhīmāni jyāś ca kāñcanapiṅgalāḥ</li>
    </ul>
    <ul id="R_6.059.020">
        <li id="R_6.059.020ab">dvau ca khaḍgau rathagatau pārśvasthau pārśvaśobhinau</li>
        <li id="R_6.059.020cd">caturhastatsarucitau vyaktahastadaśāyatau</li>
    </ul>
    <ul id="R_6.059.021">
        <li id="R_6.059.021ab">raktakaṇṭhaguṇo dhīro mahāparvatasaṃnibhaḥ</li>
        <li id="R_6.059.021cd">kālaḥ kālamahāvaktro meghastha iva bhāskaraḥ</li>
    </ul>
    <ul id="R_6.059.022">
        <li id="R_6.059.022ab">kāñcanāṅgadanaddhābhyāṃ bhujābhyām eṣa śobhate</li>
        <li id="R_6.059.022cd">śṛṅgābhyām iva tuṅgābhyāṃ himavān parvatottamaḥ</li>
    </ul>
    <ul id="R_6.059.023">
        <li id="R_6.059.023ab">kuṇḍalābhyāṃ tu yasyaitad bhāti vaktraṃ śubhekṣaṇam</li>
        <li id="R_6.059.023cd">punarvasvantaragataṃ pūrṇabimbam ivaindavam</li>
    </ul>
    <ul id="R_6.059.024">
        <li id="R_6.059.024ab">ācakṣva me mahābāho tvam enaṃ rākṣasottamam</li>
        <li id="R_6.059.024cd">yaṃ dṛṣṭvā vānarāḥ sarve bhayārtā vidrutā diśaḥ</li>
    </ul>
    <ul id="R_6.059.025">
        <li id="R_6.059.025ab">sa pṛṣṭho rājaputreṇa rāmeṇāmitatejasā</li>
        <li id="R_6.059.025cd">ācacakṣe mahātejā rāghavāya vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.059.026">
        <li id="R_6.059.026ab">daśagrīvo mahātejā rājā vaiśravaṇānujaḥ</li>
        <li id="R_6.059.026cd">bhīmakarmā mahotsāho rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_6.059.027">
        <li id="R_6.059.027ab">tasyāsīd vīryavān putro rāvaṇapratimo raṇe</li>
        <li id="R_6.059.027cd">vṛddhasevī śrutadharaḥ sarvāstraviduṣāṃ varaḥ</li>
    </ul>
    <ul id="R_6.059.028">
        <li id="R_6.059.028ab">aśvapṛṣṭhe rathe nāge khaḍge dhanuṣi karṣaṇe</li>
        <li id="R_6.059.028cd">bhede sāntve ca dāne ca naye mantre ca saṃmataḥ</li>
    </ul>
    <ul id="R_6.059.029">
        <li id="R_6.059.029ab">yasya bāhuṃ samāśritya laṅkā bhavati nirbhayā</li>
        <li id="R_6.059.029cd">tanayaṃ dhānyamālinyā atikāyam imaṃ viduḥ</li>
    </ul>
    <ul id="R_6.059.030">
        <li id="R_6.059.030ab">etenārādhito brahmā tapasā bhāvitātmanā</li>
        <li id="R_6.059.030cd">astrāṇi cāpy avāptāni ripavaś ca parājitāḥ</li>
    </ul>
    <ul id="R_6.059.031">
        <li id="R_6.059.031ab">surāsurair avadhyatvaṃ dattam asmai svayambhuvā</li>
        <li id="R_6.059.031cd">etac ca kavacaṃ divyaṃ rathaś caiṣo 'rkabhāskaraḥ</li>
    </ul>
    <ul id="R_6.059.032">
        <li id="R_6.059.032ab">etena śataśo devā dānavāś ca parājitāḥ</li>
        <li id="R_6.059.032cd">rakṣitāni ca rakṣāmi yakṣāś cāpi niṣūditāḥ</li>
    </ul>
    <ul id="R_6.059.033">
        <li id="R_6.059.033ab">vajraṃ viṣṭambhitaṃ yena bāṇair indrasya dhīmataḥ</li>
        <li id="R_6.059.033cd">pāśaḥ salilarājasya yuddhe pratihatas tathā</li>
    </ul>
    <ul id="R_6.059.034">
        <li id="R_6.059.034ab">eṣo 'tikāyo balavān rākṣasānām atharṣabhaḥ</li>
        <li id="R_6.059.034cd">rāvaṇasya suto dhīmān devadanava darpahā</li>
    </ul>
    <ul id="R_6.059.035">
        <li id="R_6.059.035ab">tad asmin kriyatāṃ yatnaḥ kṣipraṃ puruṣapuṃgava</li>
        <li id="R_6.059.035cd">purā vānarasainyāni kṣayaṃ nayati sāyakaiḥ</li>
    </ul>
    <ul id="R_6.059.036">
        <li id="R_6.059.036ab">tato 'tikāyo balavān praviśya harivāhinīm</li>
        <li id="R_6.059.036cd">visphārayām āsa dhanur nanāda ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.059.037">
        <li id="R_6.059.037ab">taṃ bhīmavapuṣaṃ dṛṣṭvā rathasthaṃ rathināṃ varam</li>
        <li id="R_6.059.037cd">abhipetur mahātmāno ye pradhānāḥ plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.059.038">
        <li id="R_6.059.038ab">kumudo dvivido maindo nīlaḥ śarabha eva ca</li>
        <li id="R_6.059.038cd">pādapair giriśṛṅgaiś ca yugapat samabhidravan</li>
    </ul>
    <ul id="R_6.059.039">
        <li id="R_6.059.039ab">teṣāṃ vṛkṣāṃś ca śailāṃś ca śaraiḥ kāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.059.039cd">atikāyo mahātejāś cicchedāstravidāṃ varaḥ</li>
    </ul>
    <ul id="R_6.059.040">
        <li id="R_6.059.040ab">tāṃś caiva sarān sa harīñ śaraiḥ sarvāyasair balī</li>
        <li id="R_6.059.040cd">vivyādhābhimukhaḥ saṃkhye bhīmakāyo niśācaraḥ</li>
    </ul>
    <ul id="R_6.059.041">
        <li id="R_6.059.041ab">te 'rditā bāṇabarṣeṇa bhinnagātrāḥ plavaṃgamāḥ</li>
        <li id="R_6.059.041cd">na śekur atikāyasya pratikartuṃ mahāraṇe</li>
    </ul>
    <ul id="R_6.059.042">
        <li id="R_6.059.042ab">tat sainyaṃ harivīrāṇāṃ trāsayām āsa rākṣasaḥ</li>
        <li id="R_6.059.042cd">mṛgayūtham iva kruddho harir yauvanam āsthitaḥ</li>
    </ul>
    <ul id="R_6.059.043">
        <li id="R_6.059.043ab">
            <seg type="pāda" n="a">sa rāṣasendro harisainyamadhye</seg>
            <seg type="pāda" n="b">nāyudhyamānaṃ nijaghāna kaṃ cit</seg>
        </li>
        <li id="R_6.059.043cd">
            <seg type="pāda" n="c">upetya rāmaṃ sadhanuḥ kalāpī</seg>
            <seg type="pāda" n="d">sagarvitaṃ vākyam idaṃ babhāṣe</seg>
        </li>
    </ul>
    <ul id="R_6.059.044">
        <li id="R_6.059.044ab">
            <seg type="pāda" n="a">rathe sthito 'haṃ śaracāpapāṇir</seg>
            <seg type="pāda" n="b">na prākṛtaṃ kaṃ cana yodhayāmi</seg>
        </li>
        <li id="R_6.059.044cd">
            <seg type="pāda" n="c">yasyāsti śaktir vyavasāya yuktā</seg>
            <seg type="pāda" n="d">dadātuṃ me kṣipram ihādya yuddham</seg>
        </li>
    </ul>
    <ul id="R_6.059.045">
        <li id="R_6.059.045ab">
            <seg type="pāda" n="a">tat tasya vākyaṃ bruvato niśamya</seg>
            <seg type="pāda" n="b">cukopa saumitrir amitrahantā</seg>
        </li>
        <li id="R_6.059.045cd">
            <seg type="pāda" n="c">amṛṣyamāṇaś ca samutpapāta</seg>
            <seg type="pāda" n="d">jagrāha cāpaṃ ca tataḥ smayitvā</seg>
        </li>
    </ul>
    <ul id="R_6.059.046">
        <li id="R_6.059.046ab">kruddhaḥ saumitrir utpatya tūṇād ākṣipya sāyakam</li>
        <li id="R_6.059.046cd">purastād atikāyasya vicakarṣa mahad dhanuḥ</li>
    </ul>
    <ul id="R_6.059.047">
        <li id="R_6.059.047ab">pūrayan sa mahīṃ śailān ākāśaṃ sāgaraṃ diśaḥ</li>
        <li id="R_6.059.047cd">jyāśabdo lakṣmaṇasyogras trāsayan rajanīcarān</li>
    </ul>
    <ul id="R_6.059.048">
        <li id="R_6.059.048ab">saumitreś cāpanirghoṣaṃ śrutvā pratibhayaṃ tadā</li>
        <li id="R_6.059.048cd">visiṣmiye mahātejā rākṣasendrātmajo balī</li>
    </ul>
    <ul id="R_6.059.049">
        <li id="R_6.059.049ab">athātikāyaḥ kupito dṛṣṭvā lakṣmaṇam utthitam</li>
        <li id="R_6.059.049cd">ādāya niśitaṃ bāṇam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.059.050">
        <li id="R_6.059.050ab">bālas tvam asi saumitre vikrameṣv avicakṣaṇaḥ</li>
        <li id="R_6.059.050cd">gaccha kiṃ kālasadṛśaṃ māṃ yodhayitum icchasi</li>
    </ul>
    <ul id="R_6.059.051">
        <li id="R_6.059.051ab">na hi madbāhusṛṣṭānām astrāṇāṃ himavān api</li>
        <li id="R_6.059.051cd">soḍhum utsahate vegam antarikṣam atho mahī</li>
    </ul>
    <ul id="R_6.059.052">
        <li id="R_6.059.052ab">sukhaprasuptaṃ kālāgniṃ prabodhayitum icchasi</li>
        <li id="R_6.059.052cd">nyasya cāpaṃ nivartasva mā prāṇāñ jahi madgataḥ</li>
    </ul>
    <ul id="R_6.059.053">
        <li id="R_6.059.053ab">atha vā tvaṃ pratiṣṭabdho na nivartitum icchasi</li>
        <li id="R_6.059.053cd">tiṣṭha prāṇān parityajya gamiṣyasi yamakṣayam</li>
    </ul>
    <ul id="R_6.059.054">
        <li id="R_6.059.054ab">paśya me niśitān bāṇān aridarpaniṣūdanān</li>
        <li id="R_6.059.054cd">īśvarāyudhasaṃkāśāṃs taptakāñcanabhūṣaṇān</li>
    </ul>
    <ul id="R_6.059.055">
        <li id="R_6.059.055ab">eṣa te sarpasaṃkāśo bāṇaḥ pāsyati śoṇitam</li>
        <li id="R_6.059.055cd">mṛgarāja iva kruddho nāgarājasya śoṇitam</li>
    </ul>
    <ul id="R_6.059.056">
        <li id="R_6.059.056ab">
            <seg type="pāda" n="a">śrutvātikāyasya vacaḥ saroṣaṃ</seg>
            <seg type="pāda" n="b">sagarvitaṃ saṃyati rājaputraḥ</seg>
        </li>
        <li id="R_6.059.056cd">
            <seg type="pāda" n="c">sa saṃcukopātibalo bṛhacchrīr</seg>
            <seg type="pāda" n="d">uvāca vākyaṃ ca tato mahārtham</seg>
        </li>
    </ul>
    <ul id="R_6.059.057">
        <li id="R_6.059.057ab">
            <seg type="pāda" n="a">na vākyamātreṇa bhavān pradhāno</seg>
            <seg type="pāda" n="b">na katthanāt satpuruṣā bhavanti</seg>
        </li>
        <li id="R_6.059.057cd">
            <seg type="pāda" n="c">mayi sthite dhanvini bāṇapāṇau</seg>
            <seg type="pāda" n="d">vidarśayasvātmabalaṃ durātman</seg>
        </li>
    </ul>
    <ul id="R_6.059.058">
        <li id="R_6.059.058ab">karmaṇā sūcayātmānaṃ na vikatthitum arhasi</li>
        <li id="R_6.059.058cd">pauruṣeṇa tu yo yuktaḥ sa tu śūra iti smṛtaḥ</li>
    </ul>
    <ul id="R_6.059.059">
        <li id="R_6.059.059ab">sarvāyudhasamāyukto dhanvī tvaṃ ratham āsthitaḥ</li>
        <li id="R_6.059.059cd">śarair vā yadi vāpy astrair darśayasva parākramam</li>
    </ul>
    <ul id="R_6.059.060">
        <li id="R_6.059.060ab">tataḥ śiras te niśitaiḥ pātayiṣyāmy ahaṃ śaraiḥ</li>
        <li id="R_6.059.060cd">mārutaḥ kālasaṃpakvaṃ vṛntāt tālaphalaṃ yathā</li>
    </ul>
    <ul id="R_6.059.061">
        <li id="R_6.059.061ab">adya te māmakā bāṇās taptakāñcanabhūṣaṇāḥ</li>
        <li id="R_6.059.061cd">pāsyanti rudhiraṃ gātrād bāṇaśalyāntarotthitam</li>
    </ul>
    <ul id="R_6.059.062">
        <li id="R_6.059.062ab">bālo 'yam iti vijñāya na māvajñātum arhasi</li>
        <li id="R_6.059.062cd">bālo vā yadi vā vṛddho mṛtyuṃ jānīhi saṃyuge</li>
    </ul>
    <ul id="R_6.059.063">
        <li id="R_6.059.063ab">lakṣmaṇasya vacaḥ śrutvā hetumat paramārthavat</li>
        <li id="R_6.059.063cd">atikāyaḥ pracukrodha bāṇaṃ cottamam ādade</li>
    </ul>
    <ul id="R_6.059.064">
        <li id="R_6.059.064ab">tato vidyādharā bhūtā devā daityā maharṣayaḥ</li>
        <li id="R_6.059.064cd">guhyakāś ca mahātmānas tad yuddhaṃ dadṛśus tadā</li>
    </ul>
    <ul id="R_6.059.065">
        <li id="R_6.059.065ab">tato 'tikāyaḥ kupitaś cāpam āropya sāyakam</li>
        <li id="R_6.059.065cd">lakṣmaṇasya pracikṣepa saṃkṣipann iva cāmbaram</li>
    </ul>
    <ul id="R_6.059.066">
        <li id="R_6.059.066ab">tam āpatantaṃ niśitaṃ śaram āśīviṣopamam</li>
        <li id="R_6.059.066cd">ardhacandreṇa ciccheda lakṣmaṇaḥ paravīrahā</li>
    </ul>
    <ul id="R_6.059.067">
        <li id="R_6.059.067ab">taṃ nikṛttaṃ śaraṃ dṛṣṭvā kṛttabhogam ivoragam</li>
        <li id="R_6.059.067cd">atikāyo bhṛśaṃ kruddhaḥ pañcabāṇān samādade</li>
    </ul>
    <ul id="R_6.059.068">
        <li id="R_6.059.068ab">tāñ śarān saṃpracikṣepa lakṣmaṇāya niśācaraḥ</li>
        <li id="R_6.059.068cd">tān aprāptāñ śarais tīkṣṇaiś ciccheda bharatānujaḥ</li>
    </ul>
    <ul id="R_6.059.069">
        <li id="R_6.059.069ab">sa tāṃś chittvā śarais tīkṣṇair lakṣmaṇaḥ paravīrahā</li>
        <li id="R_6.059.069cd">ādade niśitaṃ bāṇaṃ jvalantam iva tejasā</li>
    </ul>
    <ul id="R_6.059.070">
        <li id="R_6.059.070ab">tam ādāya dhanuḥ śreṣṭhe yojayām āsa lakṣmaṇaḥ</li>
        <li id="R_6.059.070cd">vicakarṣa ca vegena visasarja ca sāyakam</li>
    </ul>
    <ul id="R_6.059.071">
        <li id="R_6.059.071ab">pūrṇāyatavisṛṣṭena śareṇānata parvaṇā</li>
        <li id="R_6.059.071cd">lalāṭe rākṣasaśreṣṭham ājaghāna sa vīryavān</li>
    </ul>
    <ul id="R_6.059.072">
        <li id="R_6.059.072ab">sa lalāṭe śaro magnas tasya bhīmasya rakṣasaḥ</li>
        <li id="R_6.059.072cd">dadṛśe śoṇitenāktaḥ pannagendra ivāhave</li>
    </ul>
    <ul id="R_6.059.073">
        <li id="R_6.059.073ab">rākṣasaḥ pracakampe ca lakṣmaṇeṣu prakampitaḥ</li>
        <li id="R_6.059.073cd">rudrabāṇahataṃ bhīmaṃ yathā tripuragopuram</li>
    </ul>
    <ul id="R_6.059.074">
        <li id="R_6.059.074ab">cintayām āsa cāśvasya vimṛśya ca mahābalaḥ</li>
        <li id="R_6.059.074cd">sādhu bāṇanipātena śvāghanīyo 'si me ripuḥ</li>
    </ul>
    <ul id="R_6.059.075">
        <li id="R_6.059.075ab">vicāryaivaṃ vinamyāsyaṃ vinamya ca bhujāv ubhau</li>
        <li id="R_6.059.075cd">sa rathopastham āsthāya rathena pracacāra ha</li>
    </ul>
    <ul id="R_6.059.076">
        <li id="R_6.059.076ab">ekaṃ trīn pañca sapteti sāyakān rākṣasarṣabhaḥ</li>
        <li id="R_6.059.076cd">ādade saṃdadhe cāpi vicakarṣotsasarja ca</li>
    </ul>
    <ul id="R_6.059.077">
        <li id="R_6.059.077ab">te bāṇāḥ kālasaṃkāśā rākṣasendradhanuś cyutāḥ</li>
        <li id="R_6.059.077cd">hemapuṅkhā raviprakhyāś cakrur dīptam ivāmbaram</li>
    </ul>
    <ul id="R_6.059.078">
        <li id="R_6.059.078ab">tatas tān rākṣasotsṛṣṭāñ śaraughān rāvaṇānujaḥ</li>
        <li id="R_6.059.078cd">asaṃbhrāntaḥ praciccheda niśitair bahubhiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.059.079">
        <li id="R_6.059.079ab">tāñ śarān yudhi saṃprekṣya nikṛttān rāvaṇātmajaḥ</li>
        <li id="R_6.059.079cd">cukopa tridaśendrārir jagrāha niśitaṃ śaram</li>
    </ul>
    <ul id="R_6.059.080">
        <li id="R_6.059.080ab">sa saṃdhāya mahātejās taṃ bāṇaṃ sahasotsṛjat</li>
        <li id="R_6.059.080cd">tataḥ saumitrim āyāntam ājaghāna stanāntare</li>
    </ul>
    <ul id="R_6.059.081">
        <li id="R_6.059.081ab">atikāyena saumitris tāḍito yudhi vakṣasi</li>
        <li id="R_6.059.081cd">susrāva rudhiraṃ tīvraṃ madaṃ matta iva dvipaḥ</li>
    </ul>
    <ul id="R_6.059.082">
        <li id="R_6.059.082ab">sa cakāra tadātmānaṃ viśalyaṃ sahasā vibhuḥ</li>
        <li id="R_6.059.082cd">jagrāha ca śaraṃ tīṣṇam astreṇāpi samādadhe</li>
    </ul>
    <ul id="R_6.059.083">
        <li id="R_6.059.083ab">āgneyena tadāstreṇa yojayām āsa sāyakam</li>
        <li id="R_6.059.083cd">sa jajvāla tadā bāṇo dhanuś cāsya mahātmanaḥ</li>
    </ul>
    <ul id="R_6.059.084">
        <li id="R_6.059.084ab">atikāyo 'titejasvī sauram astraṃ samādade</li>
        <li id="R_6.059.084cd">tena bāṇaṃ bhujaṃgābhaṃ hemapuṅkham ayojayat</li>
    </ul>
    <ul id="R_6.059.085">
        <li id="R_6.059.085ab">tatas taṃ jvalitaṃ ghoraṃ lakṣmaṇaḥ śaram āhitam</li>
        <li id="R_6.059.085cd">atikāyāya cikṣepa kāladaṇḍam ivāntakaḥ</li>
    </ul>
    <ul id="R_6.059.086">
        <li id="R_6.059.086ab">āgneyenābhisaṃyuktaṃ dṛṣṭvā bāṇaṃ niśācaraḥ</li>
        <li id="R_6.059.086cd">utsasarja tadā bāṇaṃ dīptaṃ sūryāstrayojitam</li>
    </ul>
    <ul id="R_6.059.087">
        <li id="R_6.059.087ab">tāv ubhāv ambare bāṇāv anyonyam abhijaghnatuḥ</li>
        <li id="R_6.059.087cd">tejasā saṃpradīptāgrau kruddhāv iva bhujaṃ gamau</li>
    </ul>
    <ul id="R_6.059.088">
        <li id="R_6.059.088ab">tāv anyonyaṃ vinirdahya petatur dharaṇītale</li>
        <li id="R_6.059.088cd">nirarciṣau bhasmakṛtau na bhrājete śarottamau</li>
    </ul>
    <ul id="R_6.059.089">
        <li id="R_6.059.089ab">tato 'tikāyaḥ saṃkruddhas tv astram aiṣīkam utsṛjat</li>
        <li id="R_6.059.089cd">tat praciccheda saumitrir astram aindreṇa vīryavān</li>
    </ul>
    <ul id="R_6.059.090">
        <li id="R_6.059.090ab">aiṣīkaṃ nihataṃ dṛṣṭvā kumāro rāvaṇātmajaḥ</li>
        <li id="R_6.059.090cd">yāmyenāstreṇa saṃkruddho yojayām āsa sāyakam</li>
    </ul>
    <ul id="R_6.059.091">
        <li id="R_6.059.091ab">tatas tad astraṃ cikṣepa lakṣmaṇāya niśācaraḥ</li>
        <li id="R_6.059.091cd">vāyavyena tad astraṃ tu nijaghāna sa lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.059.092">
        <li id="R_6.059.092ab">athainaṃ śaradhārābhir dhārābhir iva toyadaḥ</li>
        <li id="R_6.059.092cd">abhyavarṣata saṃkruddho lakṣmaṇo rāvaṇātmajam</li>
    </ul>
    <ul id="R_6.059.093">
        <li id="R_6.059.093ab">te 'tikāyaṃ samāsādya kavace vajrabhūṣite</li>
        <li id="R_6.059.093cd">bhagnāgraśalyāḥ sahasā petur bāṇā mahītale</li>
    </ul>
    <ul id="R_6.059.094">
        <li id="R_6.059.094ab">tān moghān abhisaṃprekṣya lakṣmaṇaḥ paravīrahā</li>
        <li id="R_6.059.094cd">abhyavarṣata bāṇānāṃ sahasreṇa mahāyaśāḥ</li>
    </ul>
    <ul id="R_6.059.095">
        <li id="R_6.059.095ab">sa varṣyamāṇo bāṇaughair atikāyo mahābalaḥ</li>
        <li id="R_6.059.095cd">avadhyakavacaḥ saṃkhye rākṣaso naiva vivyathe</li>
    </ul>
    <ul id="R_6.059.096">
        <li id="R_6.059.096ab">na śaśāka rujaṃ kartuṃ yudhi tasya narottamaḥ</li>
        <li id="R_6.059.096cd">athainam abhyupāgamya vāyur vākyam uvāca ha</li>
    </ul>
    <ul id="R_6.059.097">
        <li id="R_6.059.097ab">brahmadattavaro hy eṣa avadhya kavacāvṛtaḥ</li>
        <li id="R_6.059.097cd">brāhmeṇāstreṇa bhindhy enam eṣa vadhyo hi nānyathā</li>
    </ul>
    <ul id="R_6.059.098">
        <li id="R_6.059.098ab">
            <seg type="pāda" n="a">tataḥ sa vāyor vacanaṃ niśamya</seg>
            <seg type="pāda" n="b">saumitrir indrapratimānavīryaḥ</seg>
        </li>
        <li id="R_6.059.098cd">
            <seg type="pāda" n="c">samādade bāṇam amoghavegaṃ</seg>
            <seg type="pāda" n="d">tad brāhmam astraṃ sahasā niyojya</seg>
        </li>
    </ul>
    <ul id="R_6.059.099">
        <li id="R_6.059.099ab">
            <seg type="pāda" n="a">tasmin varāstre tu niyujyamāne</seg>
            <seg type="pāda" n="b">saumitriṇā bāṇavare śitāgre</seg>
        </li>
        <li id="R_6.059.099cd">
            <seg type="pāda" n="c">diśaḥ sacandrārkamahāgrahāś ca</seg>
            <seg type="pāda" n="d">nabhaś ca tatrāsa rarāsa corvī</seg>
        </li>
    </ul>
    <ul id="R_6.059.100">
        <li id="R_6.059.100ab">
            <seg type="pāda" n="a">taṃ brahmaṇo 'streṇa niyujya cāpe</seg>
            <seg type="pāda" n="b">śaraṃ supuṅkhaṃ yamadūtakalpam</seg>
        </li>
        <li id="R_6.059.100cd">
            <seg type="pāda" n="c">saumitrir indrārisutasya tasya</seg>
            <seg type="pāda" n="d">sasarja bāṇaṃ yudhi vajrakalpam</seg>
        </li>
    </ul>
    <ul id="R_6.059.101">
        <li id="R_6.059.101ab">
            <seg type="pāda" n="a">taṃ lakṣmaṇotsṛṣṭam amoghavegaṃ</seg>
            <seg type="pāda" n="b">samāpatantaṃ jvalanaprakāśam</seg>
        </li>
        <li id="R_6.059.101cd">
            <seg type="pāda" n="c">suvarṇavajrottamacitrapuṅkhaṃ</seg>
            <seg type="pāda" n="d">tadātikāyaḥ samare dadarśa</seg>
        </li>
    </ul>
    <ul id="R_6.059.102">
        <li id="R_6.059.102ab">
            <seg type="pāda" n="a">taṃ prekṣamāṇaḥ sahasātikāyo</seg>
            <seg type="pāda" n="b">jaghāna bāṇair niśitair anekaiḥ</seg>
        </li>
        <li id="R_6.059.102cd">
            <seg type="pāda" n="c">sa sāyakas tasya suparṇavegas</seg>
            <seg type="pāda" n="d">tadātivegena jagāma pārśvam</seg>
        </li>
    </ul>
    <ul id="R_6.059.103">
        <li id="R_6.059.103ab">
            <seg type="pāda" n="a">tam āgataṃ prekṣya tadātikāyo</seg>
            <seg type="pāda" n="b">bāṇaṃ pradīptāntakakālakalpam</seg>
        </li>
        <li id="R_6.059.103cd">
            <seg type="pāda" n="c">jaghāna śaktyṛṣṭigadākuṭhāraiḥ</seg>
            <seg type="pāda" n="d">śūlair halaiś cāpy avipannaceṣṭaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.059.104">
        <li id="R_6.059.104ab">
            <seg type="pāda" n="a">tāny āyudhāny adbhutavigrahāṇi</seg>
            <seg type="pāda" n="b">moghāni kṛtvā sa śaro 'gnidīptaḥ</seg>
        </li>
        <li id="R_6.059.104cd">
            <seg type="pāda" n="c">prasahya tasyaiva kirīṭajuṣṭaṃ</seg>
            <seg type="pāda" n="d">tadātikāyasya śiro jahāra</seg>
        </li>
    </ul>
    <ul id="R_6.059.105">
        <li id="R_6.059.105ab">tac chiraḥ saśiras trāṇaṃ lakṣmaṇeṣuprapīḍitam</li>
        <li id="R_6.059.105cd">papāta sahasā bhūmau śṛṅgaṃ himavato yathā</li>
    </ul>
    <ul id="R_6.059.106">
        <li id="R_6.059.106ab">
            <seg type="pāda" n="a">praharṣayuktā bahavas tu vānarā</seg>
            <seg type="pāda" n="b">prabuddhapadmapratimānanās tadā</seg>
        </li>
        <li id="R_6.059.106cd">
            <seg type="pāda" n="c">apūjayaṃl lakṣmaṇam iṣṭabhāginaṃ</seg>
            <seg type="pāda" n="d">hate ripau bhīmabale durāsade</seg>
        </li>
    </ul>
    <ul id="R_6.060.001">
        <li id="R_6.060.001ab">
            <seg type="pāda" n="a">tato hatān rākṣasapuṃgavāṃs tān</seg>
            <seg type="pāda" n="b">devāntakāditriśiro 'tikāyān</seg>
        </li>
        <li id="R_6.060.001cd">
            <seg type="pāda" n="c">rakṣogaṇās tatra hatāvaśiṣṭās</seg>
            <seg type="pāda" n="d">te rāvaṇāya tvaritaṃ śaśaṃsuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.002">
        <li id="R_6.060.002ab">
            <seg type="pāda" n="a">tato hatāṃs tān sahasā niśamya</seg>
            <seg type="pāda" n="b">rājā mumohāśrupariplutākṣaḥ</seg>
        </li>
        <li id="R_6.060.002cd">
            <seg type="pāda" n="c">putrakṣayaṃ bhrātṛvadhaṃ ca ghoraṃ</seg>
            <seg type="pāda" n="d">vicintya rājā vipulaṃ pradadhyau</seg>
        </li>
    </ul>
    <ul id="R_6.060.003">
        <li id="R_6.060.003ab">
            <seg type="pāda" n="a">tatas tu rājānam udīkṣya dīnaṃ</seg>
            <seg type="pāda" n="b">śokārṇave saṃparipupluvānam</seg>
        </li>
        <li id="R_6.060.003cd">
            <seg type="pāda" n="c">atharṣabho rākṣasarājasūnur</seg>
            <seg type="pāda" n="d">athendrajid vākyam idaṃ babhāṣe</seg>
        </li>
    </ul>
    <ul id="R_6.060.004">
        <li id="R_6.060.004ab">
            <seg type="pāda" n="a">na tāta mohaṃ pratigantum arhasi</seg>
            <seg type="pāda" n="b">yatrendrajij jīvati rākṣasendra</seg>
        </li>
        <li id="R_6.060.004cd">
            <seg type="pāda" n="c">nendrāribāṇābhihato hi kaś cit</seg>
            <seg type="pāda" n="d">prāṇān samarthaḥ samare 'bhidhartum</seg>
        </li>
    </ul>
    <ul id="R_6.060.005">
        <li id="R_6.060.005ab">
            <seg type="pāda" n="a">paśyādya rāmaṃ sahalakṣmaṇena</seg>
            <seg type="pāda" n="b">madbāṇanirbhinnavikīrṇadeham</seg>
        </li>
        <li id="R_6.060.005cd">
            <seg type="pāda" n="c">gatāyuṣaṃ bhūmitale śayānaṃ</seg>
            <seg type="pāda" n="d">śaraiḥ śitair ācitasarvagātram</seg>
        </li>
    </ul>
    <ul id="R_6.060.006">
        <li id="R_6.060.006ab">
            <seg type="pāda" n="a">imāṃ pratijñāṃ śṛṇu śakraśatroḥ</seg>
            <seg type="pāda" n="b">suniścitāṃ pauruṣadaivayuktām</seg>
        </li>
        <li id="R_6.060.006cd">
            <seg type="pāda" n="c">adyaiva rāmaṃ sahalakṣmaṇena</seg>
            <seg type="pāda" n="d">saṃtāpayiṣyāmi śarair amoghaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.007">
        <li id="R_6.060.007ab">
            <seg type="pāda" n="a">adyendravaivasvataviṣṇumitra</seg>
            <seg type="pāda" n="b">sādhyāśvivaiśvānaracandrasūryāḥ</seg>
        </li>
        <li id="R_6.060.007cd">
            <seg type="pāda" n="c">drakṣyanti me vikramam aprameyaṃ</seg>
            <seg type="pāda" n="d">viṣṇor ivograṃ baliyajñavāṭe</seg>
        </li>
    </ul>
    <ul id="R_6.060.008">
        <li id="R_6.060.008ab">
            <seg type="pāda" n="a">sa evam uktvā tridaśendraśatrur</seg>
            <seg type="pāda" n="b">āpṛcchya rājānam adīnasattvaḥ</seg>
        </li>
        <li id="R_6.060.008cd">
            <seg type="pāda" n="c">samārurohānilatulyavegaṃ</seg>
            <seg type="pāda" n="d">rathaṃ kharaśreṣṭhasamādhiyuktam</seg>
        </li>
    </ul>
    <ul id="R_6.060.009">
        <li id="R_6.060.009ab">samāsthāya mahātejā rathaṃ harirathopamam</li>
        <li id="R_6.060.009cd">jagāma sahasā tatra yatra yuddham ariṃdama</li>
    </ul>
    <ul id="R_6.060.010">
        <li id="R_6.060.010ab">taṃ prasthitaṃ mahātmānam anujagmur mahābalāḥ</li>
        <li id="R_6.060.010cd">saṃharṣamāṇā bahavo dhanuḥpravarapāṇayaḥ</li>
    </ul>
    <ul id="R_6.060.011">
        <li id="R_6.060.011ab">gajaskandhagatāḥ ke cit ke cit paramavājibhiḥ</li>
        <li id="R_6.060.011cd">prāsamudgaranistriṃśa paraśvadhagadādharāḥ</li>
    </ul>
    <ul id="R_6.060.012">
        <li id="R_6.060.012ab">sa śaṅkhaninadair bhīmair bherīṇāṃ ca mahāsvanaiḥ</li>
        <li id="R_6.060.012cd">jagāma tridaśendrāriḥ stūyamāno niśācaraiḥ</li>
    </ul>
    <ul id="R_6.060.013">
        <li id="R_6.060.013ab">sa śaṅkhaśaśivarṇena chatreṇa ripusādanaḥ</li>
        <li id="R_6.060.013cd">rarāja paripūrṇena nabhaś candramasā yathā</li>
    </ul>
    <ul id="R_6.060.014">
        <li id="R_6.060.014ab">avījyata tato vīro haimair hemavibhūṣitaiḥ</li>
        <li id="R_6.060.014cd">cārucāmaramukhyaiś ca mukhyaḥ sarvadhanuṣmatām</li>
    </ul>
    <ul id="R_6.060.015">
        <li id="R_6.060.015ab">tatas tv indrajitā laṅkā sūryapratimatejasā</li>
        <li id="R_6.060.015cd">rarājāprativīryeṇa dyaur ivārkeṇa bhāsvatā</li>
    </ul>
    <ul id="R_6.060.016">
        <li id="R_6.060.016ab">sa tu dṛṣṭvā viniryāntaṃ balena mahatā vṛtam</li>
        <li id="R_6.060.016cd">rākṣasādhipatiḥ śrīmān rāvaṇaḥ putram abravīt</li>
    </ul>
    <ul id="R_6.060.017">
        <li id="R_6.060.017ab">tvam apratirathaḥ putra jitas te yudhi vāsavaḥ</li>
        <li id="R_6.060.017cd">kiṃ punar mānuṣaṃ dhṛṣyaṃ na vadhiṣyasi rāghavam</li>
    </ul>
    <ul id="R_6.060.018">
        <li id="R_6.060.018ab">tathokto rākṣasendreṇa pratigṛhya mahāśiṣaḥ</li>
        <li id="R_6.060.018cd">rathenāśvayujā vīraḥ śīghraṃ gatvā nikumbhilām</li>
    </ul>
    <ul id="R_6.060.019">
        <li id="R_6.060.019ab">sa saṃprāpya mahātejā yuddhabhūmim ariṃdamaḥ</li>
        <li id="R_6.060.019cd">sthāpayām āsa rakṣāṃsi rathaṃ prati samantataḥ</li>
    </ul>
    <ul id="R_6.060.020">
        <li id="R_6.060.020ab">tatas tu hutabhoktāraṃ hutabhuk sadṛśaprabhaḥ</li>
        <li id="R_6.060.020cd">juhuve rākṣasaśreṣṭho mantravad vidhivat tadā</li>
    </ul>
    <ul id="R_6.060.021">
        <li id="R_6.060.021ab">sa havirjālasaṃskārair mālyagandhapuraskṛtaiḥ</li>
        <li id="R_6.060.021cd">juhuve pāvakaṃ tatra rākṣasendraḥ pratāpavān</li>
    </ul>
    <ul id="R_6.060.022">
        <li id="R_6.060.022ab">śastrāṇi śarapatrāṇi samidho 'tha vibhītakaḥ</li>
        <li id="R_6.060.022cd">lohitāni ca vāsāṃsi sruvaṃ kārṣṇāyasaṃ tathā</li>
    </ul>
    <ul id="R_6.060.023">
        <li id="R_6.060.023ab">sa tatrāgniṃ samāstīrya śarapatraiḥ satomaraiḥ</li>
        <li id="R_6.060.023cd">chāgasya sarvakṛṣṇasya galaṃ jagrāha jīvataḥ</li>
    </ul>
    <ul id="R_6.060.024">
        <li id="R_6.060.024ab">sakṛd eva samiddhasya vidhūmasya mahārciṣaḥ</li>
        <li id="R_6.060.024cd">babhūvus tāni liṅgāni vijayaṃ yāny adarśayan</li>
    </ul>
    <ul id="R_6.060.025">
        <li id="R_6.060.025ab">pradakṣiṇāvartaśikhas taptakāñcanasaṃnibhaḥ</li>
        <li id="R_6.060.025cd">havis tat pratijagrāha pāvakaḥ svayam utthitaḥ</li>
    </ul>
    <ul id="R_6.060.026">
        <li id="R_6.060.026ab">so 'stram āhārayām āsa brāhmam astravidāṃ varaḥ</li>
        <li id="R_6.060.026cd">dhanuś cātmarathaṃ caiva sarvaṃ tatrābhyamantrayat</li>
    </ul>
    <ul id="R_6.060.027">
        <li id="R_6.060.027ab">tasminn āhūyamāne 'stre hūyamāne ca pāvake</li>
        <li id="R_6.060.027cd">sārkagrahendu nakṣatraṃ vitatrāsa nabhastalam</li>
    </ul>
    <ul id="R_6.060.028">
        <li id="R_6.060.028ab">
            <seg type="pāda" n="a">sa pāvakaṃ pāvakadīptatejā</seg>
            <seg type="pāda" n="b">hutvā mahendrapratimaprabhāvaḥ</seg>
        </li>
        <li id="R_6.060.028cd">
            <seg type="pāda" n="c">sacāpabāṇāsirathāśvasūtaḥ</seg>
            <seg type="pāda" n="d">khe 'ntardadha ātmānam acintyarūpaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.029">
        <li id="R_6.060.029ab">
            <seg type="pāda" n="a">sa sainyam utsṛjya sametya tūrṇaṃ</seg>
            <seg type="pāda" n="b">mahāraṇe vānaravāhinīṣu</seg>
        </li>
        <li id="R_6.060.029cd">
            <seg type="pāda" n="c">adṛśyamānaḥ śarajālam ugraṃ</seg>
            <seg type="pāda" n="d">vavarṣa nīlāmbudharo yathāmbu</seg>
        </li>
    </ul>
    <ul id="R_6.060.030">
        <li id="R_6.060.030ab">
            <seg type="pāda" n="a">te śakrajidbāṇaviśīrṇadehā</seg>
            <seg type="pāda" n="b">māyāhatā visvaram unnadantaḥ</seg>
        </li>
        <li id="R_6.060.030cd">
            <seg type="pāda" n="c">raṇe nipetur harayo 'drikalpā</seg>
            <seg type="pāda" n="d">yathendravajrābhihatā nagendrāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.031">
        <li id="R_6.060.031ab">
            <seg type="pāda" n="a">te kevalaṃ saṃdadṛśuḥ śitāgrān</seg>
            <seg type="pāda" n="b">bāṇān raṇe vānaravāhinīṣu</seg>
        </li>
        <li id="R_6.060.031cd">
            <seg type="pāda" n="c">māyā nigūḍhaṃ ca surendraśatruṃ</seg>
            <seg type="pāda" n="d">na cātra taṃ rākṣasam abhyapaśyan</seg>
        </li>
    </ul>
    <ul id="R_6.060.032">
        <li id="R_6.060.032ab">
            <seg type="pāda" n="a">tataḥ sa rakṣo'dhipatir mahātmā</seg>
            <seg type="pāda" n="b">sarvā diśo bāṇagaṇaiḥ śitāgraiḥ</seg>
        </li>
        <li id="R_6.060.032cd">
            <seg type="pāda" n="c">pracchādayām āsa raviprakāśair</seg>
            <seg type="pāda" n="d">viṣādayām āsa ca vānarendrān</seg>
        </li>
    </ul>
    <ul id="R_6.060.033">
        <li id="R_6.060.033ab">
            <seg type="pāda" n="a">sa śūlanistriṃśa paraśvadhāni</seg>
            <seg type="pāda" n="b">vyāvidhya dīptānalasaṃnibhāni</seg>
        </li>
        <li id="R_6.060.033cd">
            <seg type="pāda" n="c">savisphuliṅgojjvalapāvakāni</seg>
            <seg type="pāda" n="d">vavarṣa tīvraṃ plavagendrasainye</seg>
        </li>
    </ul>
    <ul id="R_6.060.034">
        <li id="R_6.060.034ab">tato jvalanasaṃkāśaiḥ śitair vānarayūthapāḥ</li>
        <li id="R_6.060.034cd">tāḍitāḥ śakrajidbāṇaiḥ praphullā iva kiṃśukāḥ</li>
    </ul>
    <ul id="R_6.060.035">
        <li id="R_6.060.035ab">anyonyam abhisarpanto ninadantaś ca visvaram</li>
        <li id="R_6.060.035cd">rākṣasendrāstranirbhinnā nipetur vānararṣabhāḥ</li>
    </ul>
    <ul id="R_6.060.036">
        <li id="R_6.060.036ab">udīkṣamāṇā gaganaṃ ke cin netreṣu tāḍitāḥ</li>
        <li id="R_6.060.036cd">śarair viviśur anyonyaṃ petuś ca jagatītale</li>
    </ul>
    <ul id="R_6.060.037">
        <li id="R_6.060.037ab">hanūmantaṃ ca sugrīvam aṅgadaṃ gandhamādanam</li>
        <li id="R_6.060.037cd">jāmbavantaṃ suṣeṇaṃ ca vegadarśinam eva ca</li>
    </ul>
    <ul id="R_6.060.038">
        <li id="R_6.060.038ab">maindaṃ ca dvividaṃ nīlaṃ gavākṣaṃ gajagomukhau</li>
        <li id="R_6.060.038cd">kesariṃ harilomānaṃ vidyuddaṃṣṭraṃ ca vānaram</li>
    </ul>
    <ul id="R_6.060.039">
        <li id="R_6.060.039ab">sūryānanaṃ jyotimukhaṃ tathā dadhimukhaṃ harim</li>
        <li id="R_6.060.039cd">pāvakākṣaṃ nalaṃ caiva kumudaṃ caiva vānaram</li>
    </ul>
    <ul id="R_6.060.040">
        <li id="R_6.060.040ab">prāsaiḥ śūlaiḥ śitair bāṇair indrajinmantrasaṃhitaiḥ</li>
        <li id="R_6.060.040cd">vivyādha hariśārdūlān sarvāṃs tān rākṣasottamaḥ</li>
    </ul>
    <ul id="R_6.060.041">
        <li id="R_6.060.041ab">
            <seg type="pāda" n="a">sa vai gadābhir hariyūthamukhyān</seg>
            <seg type="pāda" n="b">nirbhidya bāṇais tapanīyapuṅkhaiḥ</seg>
        </li>
        <li id="R_6.060.041cd">
            <seg type="pāda" n="c">vavarṣa rāmaṃ śaravṛṣṭijālaiḥ</seg>
            <seg type="pāda" n="d">salakṣmaṇaṃ bhāskararaśmikalpaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.042">
        <li id="R_6.060.042ab">
            <seg type="pāda" n="a">sa bāṇavarṣair abhivarṣyamāṇo</seg>
            <seg type="pāda" n="b">dhārānipātān iva tān vicintya</seg>
        </li>
        <li id="R_6.060.042cd">
            <seg type="pāda" n="c">samīkṣamāṇaḥ paramādbhutaśrī</seg>
            <seg type="pāda" n="d">rāmas tadā lakṣmaṇam ity uvāca</seg>
        </li>
    </ul>
    <ul id="R_6.060.043">
        <li id="R_6.060.043ab">
            <seg type="pāda" n="a">asau punar lakṣmaṇa rākṣasendro</seg>
            <seg type="pāda" n="b">brahmāstram āśritya surendraśatruḥ</seg>
        </li>
        <li id="R_6.060.043cd">
            <seg type="pāda" n="c">nipātayitvā harisainyam ugram</seg>
            <seg type="pāda" n="d">asmāñ śarair ardayati prasaktam</seg>
        </li>
    </ul>
    <ul id="R_6.060.044">
        <li id="R_6.060.044ab">
            <seg type="pāda" n="a">svayambhuvā dattavaro mahātmā</seg>
            <seg type="pāda" n="b">kham āsthito 'ntarhitabhīmakāyaḥ</seg>
        </li>
        <li id="R_6.060.044cd">
            <seg type="pāda" n="c">kathaṃ nu śakyo yudhi naṣṭadeho</seg>
            <seg type="pāda" n="d">nihantum adyendrajid udyatāstraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.045">
        <li id="R_6.060.045ab">
            <seg type="pāda" n="a">manye svayambhūr bhagavān acintyo</seg>
            <seg type="pāda" n="b">yasyaitad astraṃ prabhavaś ca yo 'sya</seg>
        </li>
        <li id="R_6.060.045cd">
            <seg type="pāda" n="c">bāṇāvapātāṃs tvam ihādya dhīman</seg>
            <seg type="pāda" n="d">mayā sahāvyagramanāḥ sahasva</seg>
        </li>
    </ul>
    <ul id="R_6.060.046">
        <li id="R_6.060.046ab">
            <seg type="pāda" n="a">pracchādayaty eṣa hi rākṣasendraḥ</seg>
            <seg type="pāda" n="b">sarvā diśaḥ sāyakavṛṣṭijālaiḥ</seg>
        </li>
        <li id="R_6.060.046cd">
            <seg type="pāda" n="c">etac ca sarvaṃ patitāgryavīraṃ</seg>
            <seg type="pāda" n="d">na bhrājate vānararājasainyam</seg>
        </li>
    </ul>
    <ul id="R_6.060.047">
        <li id="R_6.060.047ab">
            <seg type="pāda" n="a">āvāṃ tu dṛṣṭvā patitau visaṃjñau</seg>
            <seg type="pāda" n="b">nivṛttayuddhau hataroṣaharṣau</seg>
        </li>
        <li id="R_6.060.047cd">
            <seg type="pāda" n="c">dhruvaṃ pravekṣyaty amarārivāsaṃ</seg>
            <seg type="pāda" n="d">asau samādāya raṇāgralakṣmīm</seg>
        </li>
    </ul>
    <ul id="R_6.060.048">
        <li id="R_6.060.048ab">
            <seg type="pāda" n="a">tatas tu tāv indrajid astrajālair</seg>
            <seg type="pāda" n="b">babhūvatus tatra tadā viśastau</seg>
        </li>
        <li id="R_6.060.048cd">
            <seg type="pāda" n="c">sa cāpi tau tatra viṣādayitvā</seg>
            <seg type="pāda" n="d">nanāda harṣād yudhi rākṣasendraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.060.049">
        <li id="R_6.060.049ab">
            <seg type="pāda" n="a">sa tat tadā vānararājasainyaṃ</seg>
            <seg type="pāda" n="b">rāmaṃ ca saṃkhye sahalakṣmaṇena</seg>
        </li>
        <li id="R_6.060.049cd">
            <seg type="pāda" n="c">viṣādayitvā sahasā viveśa</seg>
            <seg type="pāda" n="d">purīṃ daśagrīvabhujābhiguptām</seg>
        </li>
    </ul>
    <ul id="R_6.061.001">
        <li id="R_6.061.001ab">
            <seg type="pāda" n="a">tayos tadā sāditayo raṇāgre</seg>
            <seg type="pāda" n="b">mumoha sainyaṃ hariyūthapānām</seg>
        </li>
        <li id="R_6.061.001cd">
            <seg type="pāda" n="c">sugrīvanīlāṅgadajāmbavanto</seg>
            <seg type="pāda" n="d">na cāpi kiṃ cit pratipedire te</seg>
        </li>
    </ul>
    <ul id="R_6.061.002">
        <li id="R_6.061.002ab">
            <seg type="pāda" n="a">tato viṣaṇṇaṃ samavekṣya sainyaṃ</seg>
            <seg type="pāda" n="b">vibhīṣaṇo buddhimatāṃ variṣṭhaḥ</seg>
        </li>
        <li id="R_6.061.002cd">
            <seg type="pāda" n="c">uvāca śākhāmṛgarājavīrān</seg>
            <seg type="pāda" n="d">āśvāsayann apratimair vacobhiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.003">
        <li id="R_6.061.003ab">
            <seg type="pāda" n="a">mā bhaiṣṭa nāsty atra viṣādakālo</seg>
            <seg type="pāda" n="b">yad āryaputrāv avaśau viṣaṇṇau</seg>
        </li>
        <li id="R_6.061.003cd">
            <seg type="pāda" n="c">svayambhuvo vākyam athodvahantau</seg>
            <seg type="pāda" n="d">yat sāditāv indrajidastrajālaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.004">
        <li id="R_6.061.004ab">
            <seg type="pāda" n="a">tasmai tu dattaṃ paramāstram etat</seg>
            <seg type="pāda" n="b">svayambhuvā brāhmam amoghavegam</seg>
        </li>
        <li id="R_6.061.004cd">
            <seg type="pāda" n="c">tan mānayantau yadi rājaputrau</seg>
            <seg type="pāda" n="d">nipātitau ko 'tra viṣādakālaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.005">
        <li id="R_6.061.005ab">brāhmam astraṃ tadā dhīmān mānayitvā tu mārutiḥ</li>
        <li id="R_6.061.005cd">vibhīṣaṇavacaḥ śrutvā hanūmāṃs tam athābravīt</li>
    </ul>
    <ul id="R_6.061.006">
        <li id="R_6.061.006ab">etasmin nihate sainye vānarāṇāṃ tarasvinām</li>
        <li id="R_6.061.006cd">yo yo dhārayate prāṇāṃs taṃ tam āśvāsayāvahe</li>
    </ul>
    <ul id="R_6.061.007">
        <li id="R_6.061.007ab">tāv ubhau yugapad vīrau hanūmad rākṣasottamau</li>
        <li id="R_6.061.007cd">ulkāhastau tadā rātrau raṇaśīrṣe viceratuḥ</li>
    </ul>
    <ul id="R_6.061.008">
        <li id="R_6.061.008ab">chinnalāṅgūlahastorupādāṅguli śiro dharaiḥ</li>
        <li id="R_6.061.008cd">sravadbhiḥ kṣatajaṃ gātraiḥ prasravadbhiḥ samantataḥ</li>
    </ul>
    <ul id="R_6.061.009">
        <li id="R_6.061.009ab">patitaiḥ parvatākārair vānarair abhisaṃkulām</li>
        <li id="R_6.061.009cd">śastraiś ca patitair dīptair dadṛśāte vasuṃdharām</li>
    </ul>
    <ul id="R_6.061.010">
        <li id="R_6.061.010ab">sugrīvam aṅgadaṃ nīlaṃ śarabhaṃ gandhamādanam</li>
        <li id="R_6.061.010cd">jāmbavantaṃ suṣeṇaṃ ca vegadarśanam āhukam</li>
    </ul>
    <ul id="R_6.061.011">
        <li id="R_6.061.011ab">maindaṃ nalaṃ jyotimukhaṃ dvividaṃ panasaṃ tathā</li>
        <li id="R_6.061.011cd">vibhīṣaṇo hanūmāṃś ca dadṛśāte hatān raṇe</li>
    </ul>
    <ul id="R_6.061.012">
        <li id="R_6.061.012ab">saptaṣaṣṭir hatāḥ koṭyo vānarāṇāṃ tarasvinām</li>
        <li id="R_6.061.012cd">ahnaḥ pañcamaśeṣeṇa vallabhena svayambhuvaḥ</li>
    </ul>
    <ul id="R_6.061.013">
        <li id="R_6.061.013ab">sāgaraughanibhaṃ bhīmaṃ dṛṣṭvā bāṇārditaṃ balam</li>
        <li id="R_6.061.013cd">mārgate jāmbavantaṃ sma hanūmān savibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.061.014">
        <li id="R_6.061.014ab">svabhāvajarayā yuktaṃ vṛddhaṃ śaraśataiś citam</li>
        <li id="R_6.061.014cd">prajāpatisutaṃ vīraṃ śāmyantam iva pāvakam</li>
    </ul>
    <ul id="R_6.061.015">
        <li id="R_6.061.015ab">dṛṣṭvā tam upasaṃgamya paulastyo vākyam abravīt</li>
        <li id="R_6.061.015cd">kaccid āryaśarais tīrṣṇair na prāṇā dhvaṃsitās tava</li>
    </ul>
    <ul id="R_6.061.016">
        <li id="R_6.061.016ab">vibhīṣaṇavacaḥ śrutvā jāmbavān ṛkṣapuṃgavaḥ</li>
        <li id="R_6.061.016cd">kṛcchrād abhyudgiran vākyam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.061.017">
        <li id="R_6.061.017ab">nairṛtendramahāvīryasvareṇa tvābhilakṣaye</li>
        <li id="R_6.061.017cd">pīḍyamānaḥ śitair bāṇair na tvāṃ paśyāmi cakṣuṣā</li>
    </ul>
    <ul id="R_6.061.018">
        <li id="R_6.061.018ab">añjanā suprajā yena mātariśvā ca nairṛta</li>
        <li id="R_6.061.018cd">hanūmān vānaraśreṣṭhaḥ prāṇān dhārayate kva cit</li>
    </ul>
    <ul id="R_6.061.019">
        <li id="R_6.061.019ab">śrutvā jāmbavato vākyam uvācedaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.061.019cd">āryaputrāv atikramya kasmāt pṛcchasi mārutim</li>
    </ul>
    <ul id="R_6.061.020">
        <li id="R_6.061.020ab">naiva rājani sugrīve nāṅgade nāpi rāghave</li>
        <li id="R_6.061.020cd">ārya saṃdarśitaḥ sneho yathā vāyusute paraḥ</li>
    </ul>
    <ul id="R_6.061.021">
        <li id="R_6.061.021ab">vibhīṣaṇavacaḥ śrutvā jāmbavān vākyam abravīt</li>
        <li id="R_6.061.021cd">śṛṇu nairṛtaśārdūla yasmāt pṛcchāmi mārutim</li>
    </ul>
    <ul id="R_6.061.022">
        <li id="R_6.061.022ab">tasmiñ jīvati vīre tu hatam apy ahataṃ balam</li>
        <li id="R_6.061.022cd">hanūmaty ujjhitaprāṇe jīvanto 'pi vayaṃ hatāḥ</li>
    </ul>
    <ul id="R_6.061.023">
        <li id="R_6.061.023ab">dhriyate mārutis tāta mārutapratimo yadi</li>
        <li id="R_6.061.023cd">vaiśvānarasamo vīrye jīvitāśā tato bhavet</li>
    </ul>
    <ul id="R_6.061.024">
        <li id="R_6.061.024ab">tato vṛddham upāgamya niyamenābhyavādayat</li>
        <li id="R_6.061.024cd">gṛhya jāmbavataḥ pādau hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_6.061.025">
        <li id="R_6.061.025ab">śrutvā hanumato vākyaṃ tathāpi vyathitendriyaḥ</li>
        <li id="R_6.061.025cd">punarjātam ivātmānaṃ sa mene ṛkṣapuṃgavaḥ</li>
    </ul>
    <ul id="R_6.061.026">
        <li id="R_6.061.026ab">tato 'bravīn mahātejā hanūmantaṃ sa jāmbavān</li>
        <li id="R_6.061.026cd">āgaccha hariśārdūlavānarāṃs trātum arhasi</li>
    </ul>
    <ul id="R_6.061.027">
        <li id="R_6.061.027ab">nānyo vikramaparyāptas tvam eṣāṃ paramaḥ sakhā</li>
        <li id="R_6.061.027cd">tvatparākramakālo 'yaṃ nānyaṃ paśyāmi kañ cana</li>
    </ul>
    <ul id="R_6.061.028">
        <li id="R_6.061.028ab">ṛkṣavānaravīrāṇām anīkāni praharṣaya</li>
        <li id="R_6.061.028cd">viśalyau kuru cāpy etau sāditau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.061.029">
        <li id="R_6.061.029ab">gatvā paramam adhvānam upary upari sāgaram</li>
        <li id="R_6.061.029cd">himavantaṃ nagaśreṣṭhaṃ hanūman gantum arhasi</li>
    </ul>
    <ul id="R_6.061.030">
        <li id="R_6.061.030ab">tataḥ kāñcanam atyugram ṛṣabhaṃ parvatottamam</li>
        <li id="R_6.061.030cd">kailāsaśikharaṃ cāpi drakṣyasy ariniṣūdana</li>
    </ul>
    <ul id="R_6.061.031">
        <li id="R_6.061.031ab">tayoḥ śikharayor madhye pradīptam atulaprabham</li>
        <li id="R_6.061.031cd">sarvauṣadhiyutaṃ vīra drakṣyasy auṣadhiparvatam</li>
    </ul>
    <ul id="R_6.061.032">
        <li id="R_6.061.032ab">tasya vānaraśārdūlacatasro mūrdhni saṃbhavāḥ</li>
        <li id="R_6.061.032cd">drakṣyasy oṣadhayo dīptā dīpayantyo diśo daśa</li>
    </ul>
    <ul id="R_6.061.033">
        <li id="R_6.061.033ab">mṛtasaṃjīvanīṃ caiva viśalyakaraṇīm api</li>
        <li id="R_6.061.033cd">sauvarṇakaraṇīṃ caiva saṃdhānīṃ ca mahauṣadhīm</li>
    </ul>
    <ul id="R_6.061.034">
        <li id="R_6.061.034ab">tāḥ sarvā hanuman gṛhya kṣipram āgantum arhasi</li>
        <li id="R_6.061.034cd">āśvāsaya harīn prāṇair yojya gandhavahātmajaḥ</li>
    </ul>
    <ul id="R_6.061.035">
        <li id="R_6.061.035ab">śrutvā jāmbavato vākyaṃ hanūmān haripuṃgavaḥ</li>
        <li id="R_6.061.035cd">āpūryata baloddharṣais toyavegair ivārṇavaḥ</li>
    </ul>
    <ul id="R_6.061.036">
        <li id="R_6.061.036ab">sa parvatataṭāgrasthaḥ pīḍayan parvatottaram</li>
        <li id="R_6.061.036cd">hanūmān dṛśyate vīro dvitīya iva parvataḥ</li>
    </ul>
    <ul id="R_6.061.037">
        <li id="R_6.061.037ab">haripādavinirbhinno niṣasāda sa parvataḥ</li>
        <li id="R_6.061.037cd">na śaśāka tadātmānaṃ soḍhuṃ bhṛśanipīḍitaḥ</li>
    </ul>
    <ul id="R_6.061.038">
        <li id="R_6.061.038ab">tasya petur nagā bhūmau harivegāc ca jajvaluḥ</li>
        <li id="R_6.061.038cd">śṛṅgāṇi ca vyakīryanta pīḍitasya hanūmatā</li>
    </ul>
    <ul id="R_6.061.039">
        <li id="R_6.061.039ab">tasmin saṃpīḍyamāne tu bhagnadrumaśilātale</li>
        <li id="R_6.061.039cd">na śekur vānarāḥ sthātuṃ ghūrṇamāne nagottame</li>
    </ul>
    <ul id="R_6.061.040">
        <li id="R_6.061.040ab">sa ghūrṇitamahādvārā prabhagnagṛhagopurā</li>
        <li id="R_6.061.040cd">laṅkā trāsākulā rātrau pranṛttevābhavat tadā</li>
    </ul>
    <ul id="R_6.061.041">
        <li id="R_6.061.041ab">pṛthivīdharasaṃkāśo nipīḍya dharaṇīdharam</li>
        <li id="R_6.061.041cd">pṛthivīṃ kṣobhayām āsa sārṇavāṃ mārutātmajaḥ</li>
    </ul>
    <ul id="R_6.061.042">
        <li id="R_6.061.042ab">padbhyāṃ tu śailam āpīḍya vaḍavāmukhavan mukham</li>
        <li id="R_6.061.042cd">vivṛtyograṃ nanādoccais trāsayann iva rākṣasān</li>
    </ul>
    <ul id="R_6.061.043">
        <li id="R_6.061.043ab">tasya nānadyamānasya śrutvā ninadam adbhutam</li>
        <li id="R_6.061.043cd">laṅkāsthā rākṣasāḥ sarve na śekuḥ spandituṃ bhayāt</li>
    </ul>
    <ul id="R_6.061.044">
        <li id="R_6.061.044ab">namaskṛtvātha rāmāya mārutir bhīmavikramaḥ</li>
        <li id="R_6.061.044cd">rāghavārthe paraṃ karma samaihata paraṃtapaḥ</li>
    </ul>
    <ul id="R_6.061.045">
        <li id="R_6.061.045ab">
            <seg type="pāda" n="a">sa puccham udyamya bhujaṃgakalpaṃ</seg>
            <seg type="pāda" n="b">vinamya pṛṣṭhaṃ śravaṇe nikuñcya</seg>
        </li>
        <li id="R_6.061.045cd">
            <seg type="pāda" n="c">vivṛtya vaktraṃ vaḍavāmukhābham</seg>
            <seg type="pāda" n="d">āpupluve vyomni sa caṇḍavegaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.046">
        <li id="R_6.061.046ab">
            <seg type="pāda" n="a">sa vṛkṣaṣaṇḍāṃs tarasā jahāra</seg>
            <seg type="pāda" n="b">śailāñ śilāḥ prākṛtavānarāṃś ca</seg>
        </li>
        <li id="R_6.061.046cd">
            <seg type="pāda" n="c">bāhūruvegoddhatasaṃpraṇunnās</seg>
            <seg type="pāda" n="d">te kṣīṇavegāḥ salile nipetuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.047">
        <li id="R_6.061.047ab">
            <seg type="pāda" n="a">sa tau prasāryoragabhogakalpau</seg>
            <seg type="pāda" n="b">bhujau bhujaṃgārinikāśavīryaḥ</seg>
        </li>
        <li id="R_6.061.047cd">
            <seg type="pāda" n="c">jagāma meruṃ nagarājam agryaṃ</seg>
            <seg type="pāda" n="d">diśaḥ prakarṣann iva vāyusūnuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.048">
        <li id="R_6.061.048ab">
            <seg type="pāda" n="a">sa sāgaraṃ ghūrṇitavīcimālaṃ</seg>
            <seg type="pāda" n="b">tadā bhṛśaṃ bhrāmitasarvasattvam</seg>
        </li>
        <li id="R_6.061.048cd">
            <seg type="pāda" n="c">samīkṣamāṇaḥ sahasā jagāma</seg>
            <seg type="pāda" n="d">cakraṃ yathā viṣṇukarāgramuktam</seg>
        </li>
    </ul>
    <ul id="R_6.061.049">
        <li id="R_6.061.049ab">
            <seg type="pāda" n="a">sa parvatān vṛkṣagaṇān sarāṃsi</seg>
            <seg type="pāda" n="b">nadīs taṭākāni purottamāni</seg>
        </li>
        <li id="R_6.061.049cd">
            <seg type="pāda" n="c">sphītāñjanāṃs tān api saṃprapaśyañ</seg>
            <seg type="pāda" n="d">jagāma vegāt pitṛtulyavegaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.050">
        <li id="R_6.061.050ab">ādityapatham āśritya jagāma sa gataśramaḥ</li>
        <li id="R_6.061.050cd">sa dadarśa hariśreṣṭho himavantaṃ nagottamam</li>
    </ul>
    <ul id="R_6.061.051">
        <li id="R_6.061.051ab">nānāprasravaṇopetaṃ bahukaṃdaranirjharam</li>
        <li id="R_6.061.051cd">śvetābhracayasaṃkāśaiḥ śikharaiś cārudarśanaiḥ</li>
    </ul>
    <ul id="R_6.061.052">
        <li id="R_6.061.052ab">
            <seg type="pāda" n="a">sa taṃ samāsādya mahānagendram</seg>
            <seg type="pāda" n="b">atipravṛddhottamaghoraśṛṅgam</seg>
        </li>
        <li id="R_6.061.052cd">
            <seg type="pāda" n="c">dadarśa puṇyāni mahāśramāṇi</seg>
            <seg type="pāda" n="d">surarṣisaṃghottamasevitāni</seg>
        </li>
    </ul>
    <ul id="R_6.061.053">
        <li id="R_6.061.053ab">
            <seg type="pāda" n="a">sa brahmakośaṃ rajatālayaṃ ca</seg>
            <seg type="pāda" n="b">śakrālayaṃ rudraśarapramokṣam</seg>
        </li>
        <li id="R_6.061.053cd">
            <seg type="pāda" n="c">hayānanaṃ brahmaśiraś ca dīptaṃ</seg>
            <seg type="pāda" n="d">dadarśa vaivasvata kiṃkarāṃś ca</seg>
        </li>
    </ul>
    <ul id="R_6.061.054">
        <li id="R_6.061.054ab">
            <seg type="pāda" n="a">vajrālayaṃ vaiśvaraṇālayaṃ ca</seg>
            <seg type="pāda" n="b">sūryaprabhaṃ sūryanibandhanaṃ ca</seg>
        </li>
        <li id="R_6.061.054cd">
            <seg type="pāda" n="c">brahmāsanaṃ śaṃkarakārmukaṃ ca</seg>
            <seg type="pāda" n="d">dadarśa nābhiṃ ca vasuṃdharāyāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.055">
        <li id="R_6.061.055ab">
            <seg type="pāda" n="a">kailāsam agryaṃ himavacchilāṃ ca</seg>
            <seg type="pāda" n="b">tatharṣabhaṃ kāñcanaśailam agryam</seg>
        </li>
        <li id="R_6.061.055cd">
            <seg type="pāda" n="c">sa dīptasarvauṣadhisaṃpradīptaṃ</seg>
            <seg type="pāda" n="d">dadarśa sarvauṣadhiparvatendram</seg>
        </li>
    </ul>
    <ul id="R_6.061.056">
        <li id="R_6.061.056ab">
            <seg type="pāda" n="a">sa taṃ samīkṣyānalaraśmidīptaṃ</seg>
            <seg type="pāda" n="b">visiṣmiye vāsavadūtasūnuḥ</seg>
        </li>
        <li id="R_6.061.056cd">
            <seg type="pāda" n="c">āplutya taṃ cauṣadhiparvatendraṃ</seg>
            <seg type="pāda" n="d">tatrauṣadhīnāṃ vicayaṃ cakāra</seg>
        </li>
    </ul>
    <ul id="R_6.061.057">
        <li id="R_6.061.057ab">sa yojanasahasrāṇi samatītya mahākapiḥ</li>
        <li id="R_6.061.057cd">divyauṣadhidharaṃ śailaṃ vyacaran mārutātmajaḥ</li>
    </ul>
    <ul id="R_6.061.058">
        <li id="R_6.061.058ab">mahauṣadhyas tu tāḥ sarvās tasmin parvatasattame</li>
        <li id="R_6.061.058cd">vijñāyārthinam āyāntaṃ tato jagmur adarśanam</li>
    </ul>
    <ul id="R_6.061.059">
        <li id="R_6.061.059ab">
            <seg type="pāda" n="a">sa tā mahātmā hanumān apaśyaṃś</seg>
            <seg type="pāda" n="b">cukopa kopāc ca bhṛśaṃ nanāda</seg>
        </li>
        <li id="R_6.061.059cd">
            <seg type="pāda" n="c">amṛṣyamāṇo 'gninikāśacakṣur</seg>
            <seg type="pāda" n="d">mahīdharendraṃ tam uvāca vākyam</seg>
        </li>
    </ul>
    <ul id="R_6.061.060">
        <li id="R_6.061.060ab">
            <seg type="pāda" n="a">kim etad evaṃ suviniścitaṃ te</seg>
            <seg type="pāda" n="b">yad rāghave nāsi kṛtānukampaḥ</seg>
        </li>
        <li id="R_6.061.060cd">
            <seg type="pāda" n="c">paśyādya madbāhubalābhibhūto</seg>
            <seg type="pāda" n="d">vikīrṇam ātmānam atho nagendra</seg>
        </li>
    </ul>
    <ul id="R_6.061.061">
        <li id="R_6.061.061ab">
            <seg type="pāda" n="a">sa tasya śṛṅgaṃ sanagaṃ sanāgaṃ</seg>
            <seg type="pāda" n="b">sakāñcanaṃ dhātusahasrajuṣṭam</seg>
        </li>
        <li id="R_6.061.061cd">
            <seg type="pāda" n="c">vikīrṇakūṭaṃ calitāgrasānuṃ</seg>
            <seg type="pāda" n="d">pragṛhya vegāt sahasonmamātha</seg>
        </li>
    </ul>
    <ul id="R_6.061.062">
        <li id="R_6.061.062ab">
            <seg type="pāda" n="a">sa taṃ samutpāṭya kham utpapāta</seg>
            <seg type="pāda" n="b">vitrāsya lokān sasurān surendrān</seg>
        </li>
        <li id="R_6.061.062cd">
            <seg type="pāda" n="c">saṃstūyamānaḥ khacarair anekair</seg>
            <seg type="pāda" n="d">jagāma vegād garuḍogravīryaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.063">
        <li id="R_6.061.063ab">
            <seg type="pāda" n="a">sa bhāskarādhvānam anuprapannas</seg>
            <seg type="pāda" n="b">tad bhāskarābhaṃ śikharaṃ pragṛhya</seg>
        </li>
        <li id="R_6.061.063cd">
            <seg type="pāda" n="c">babhau tadā bhāskarasaṃnikāśo</seg>
            <seg type="pāda" n="d">raveḥ samīpe pratibhāskarābhaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.064">
        <li id="R_6.061.064ab">
            <seg type="pāda" n="a">sa tena śailena bhṛśaṃ rarāja</seg>
            <seg type="pāda" n="b">śailopamo gandhavahātmajas tu</seg>
        </li>
        <li id="R_6.061.064cd">
            <seg type="pāda" n="c">sahasradhāreṇa sapāvakena</seg>
            <seg type="pāda" n="d">cakreṇa khe viṣṇur ivoddhṛtena</seg>
        </li>
    </ul>
    <ul id="R_6.061.065">
        <li id="R_6.061.065ab">
            <seg type="pāda" n="a">taṃ vānarāḥ prekṣya tadā vineduḥ</seg>
            <seg type="pāda" n="b">sa tān api prekṣya mudā nanāda</seg>
        </li>
        <li id="R_6.061.065cd">
            <seg type="pāda" n="c">teṣāṃ samudghuṣṭaravaṃ niśamya</seg>
            <seg type="pāda" n="d">laṅkālayā bhīmataraṃ vineduḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.066">
        <li id="R_6.061.066ab">
            <seg type="pāda" n="a">tato mahātmā nipapāta tasmiñ</seg>
            <seg type="pāda" n="b">śailottame vānarasainyamadhye</seg>
        </li>
        <li id="R_6.061.066cd">
            <seg type="pāda" n="c">haryuttamebhyaḥ śirasābhivādya</seg>
            <seg type="pāda" n="d">vibhīṣaṇaṃ tatra ca sasvaje saḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.067">
        <li id="R_6.061.067ab">
            <seg type="pāda" n="a">tāv apy ubhau mānuṣarājaputrau</seg>
            <seg type="pāda" n="b">taṃ gandham āghrāya mahauṣadhīnām</seg>
        </li>
        <li id="R_6.061.067cd">
            <seg type="pāda" n="c">babhūvatus tatra tadā viśalyāv</seg>
            <seg type="pāda" n="d">uttasthur anye ca haripravīrāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.061.068">
        <li id="R_6.061.068ab">
            <seg type="pāda" n="a">tato harir gandhavahātmajas tu</seg>
            <seg type="pāda" n="b">tam oṣadhīśailam udagravīryaḥ</seg>
        </li>
        <li id="R_6.061.068cd">
            <seg type="pāda" n="c">nināya vegād dhimavantam eva</seg>
            <seg type="pāda" n="d">punaś ca rāmeṇa samājagāma</seg>
        </li>
    </ul>
    <ul id="R_6.062.001">
        <li id="R_6.062.001ab">tato 'bravīn mahātejāḥ sugrīvo vānarādhipaḥ</li>
        <li id="R_6.062.001cd">arthyaṃ vijāpayaṃś cāpi hanūmantaṃ mahābalam</li>
    </ul>
    <ul id="R_6.062.002">
        <li id="R_6.062.002ab">yato hataḥ kumbhakarṇaḥ kumārāś ca niṣūditāḥ</li>
        <li id="R_6.062.002cd">nedānīm upanirhāraṃ rāvaṇo dātum arhati</li>
    </ul>
    <ul id="R_6.062.003">
        <li id="R_6.062.003ab">ye ye mahābalāḥ santi laghavaś ca plavaṃgamāḥ</li>
        <li id="R_6.062.003cd">laṅkām abhyutpatantv āśu gṛhyolkāḥ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_6.062.004">
        <li id="R_6.062.004ab">tato 'staṃ gata āditye raudre tasmin niśāmukhe</li>
        <li id="R_6.062.004cd">laṅkām abhimukhāḥ solkā jagmus te plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_6.062.005">
        <li id="R_6.062.005ab">ulkāhastair harigaṇaiḥ sarvataḥ samabhidrutāḥ</li>
        <li id="R_6.062.005cd">ārakṣasthā virūpākṣāḥ sahasā vipradudruvuḥ</li>
    </ul>
    <ul id="R_6.062.006">
        <li id="R_6.062.006ab">gopurāṭṭa pratolīṣu caryāsu vividhāsu ca</li>
        <li id="R_6.062.006cd">prāsādeṣu ca saṃhṛṣṭāḥ sasṛjus te hutāśanam</li>
    </ul>
    <ul id="R_6.062.007">
        <li id="R_6.062.007ab">teṣāṃ gṛhasahasrāṇi dadāha hutabhuk tadā</li>
        <li id="R_6.062.007cd">āvāsān rākṣasānāṃ ca sarveṣāṃ gṛhamedhinām</li>
    </ul>
    <ul id="R_6.062.008">
        <li id="R_6.062.008ab">hemacitratanutrāṇāṃ sragdāmāmbaradhāriṇām</li>
        <li id="R_6.062.008cd">sīdhupānacalākṣāṇāṃ madavihvalagāminām</li>
    </ul>
    <ul id="R_6.062.009">
        <li id="R_6.062.009ab">kāntālambitavastrāṇāṃ śatrusaṃjātamanyunām</li>
        <li id="R_6.062.009cd">gadāśūlāsi hastānāṃ khādatāṃ pibatām api</li>
    </ul>
    <ul id="R_6.062.010">
        <li id="R_6.062.010ab">śayaneṣu mahārheṣu prasuptānāṃ priyaiḥ saha</li>
        <li id="R_6.062.010cd">trastānāṃ gacchatāṃ tūrṇaṃ putrān ādāya sarvataḥ</li>
    </ul>
    <ul id="R_6.062.011">
        <li id="R_6.062.011ab">teṣāṃ gṛhasahasrāṇi tadā laṅkānivāsinām</li>
        <li id="R_6.062.011cd">adahat pāvakas tatra jajvāla ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.062.012">
        <li id="R_6.062.012ab">sāravanti mahārhāṇi gambhīraguṇavanti ca</li>
        <li id="R_6.062.012cd">hemacandrārdhacandrāṇi candraśālonnatāni ca</li>
    </ul>
    <ul id="R_6.062.013">
        <li id="R_6.062.013ab">ratnacitragavākṣāṇi sādhiṣṭhānāni sarvaśaḥ</li>
        <li id="R_6.062.013cd">maṇividrumacitrāṇi spṛśantīva ca bhāskaram</li>
    </ul>
    <ul id="R_6.062.014">
        <li id="R_6.062.014ab">krauñcabarhiṇavīṇānāṃ bhūṣaṇānāṃ ca nisvanaiḥ</li>
        <li id="R_6.062.014cd">nāditāny acalābhāni veśmāny agnir dadāha saḥ</li>
    </ul>
    <ul id="R_6.062.015">
        <li id="R_6.062.015ab">jvalanena parītāni toraṇāni cakāśire</li>
        <li id="R_6.062.015cd">vidyudbhir iva naddhāni meghajālāni gharmage</li>
    </ul>
    <ul id="R_6.062.016">
        <li id="R_6.062.016ab">vimāneṣu prasuptāś ca dahyamānā varāṅganāḥ</li>
        <li id="R_6.062.016cd">tyaktābharaṇasaṃyogā hāhety uccair vicukruśaḥ</li>
    </ul>
    <ul id="R_6.062.017">
        <li id="R_6.062.017ab">tatra cāgniparītāni nipetur bhavanāny api</li>
        <li id="R_6.062.017cd">vajrivajrahatānīva śikharāṇi mahāgireḥ</li>
    </ul>
    <ul id="R_6.062.018">
        <li id="R_6.062.018ab">tāni nirdahyamānāni dūrataḥ pracakāśire</li>
        <li id="R_6.062.018cd">himavacchikharāṇīva dīptauṣadhivanāni ca</li>
    </ul>
    <ul id="R_6.062.019">
        <li id="R_6.062.019ab">harmyāgrair dahyamānaiś ca jvālāprajvalitair api</li>
        <li id="R_6.062.019cd">rātrau sā dṛśyate laṅkā puṣpitair iva kiṃśukaiḥ</li>
    </ul>
    <ul id="R_6.062.020">
        <li id="R_6.062.020ab">hastyadhyakṣair gajair muktair muktaiś ca turagair api</li>
        <li id="R_6.062.020cd">babhūva laṅkā lokānte bhrāntagrāha ivārṇavaḥ</li>
    </ul>
    <ul id="R_6.062.021">
        <li id="R_6.062.021ab">aśvaṃ muktaṃ gajo dṛṣṭvā kaccid bhīto 'pasarpati</li>
        <li id="R_6.062.021cd">bhīto bhītaṃ gajaṃ dṛṣṭvā kva cid aśvo nivartate</li>
    </ul>
    <ul id="R_6.062.022">
        <li id="R_6.062.022ab">sā babhūva muhūrtena haribhir dīpitā purī</li>
        <li id="R_6.062.022cd">lokasyāsya kṣaye ghore pradīpteva vasuṃdharā</li>
    </ul>
    <ul id="R_6.062.023">
        <li id="R_6.062.023ab">nārī janasya dhūmena vyāptasyoccair vineduṣaḥ</li>
        <li id="R_6.062.023cd">svano jvalanataptasya śuśruve daśayojanam</li>
    </ul>
    <ul id="R_6.062.024">
        <li id="R_6.062.024ab">pradagdhakāyān aparān rākṣasān nirgatān bahiḥ</li>
        <li id="R_6.062.024cd">sahasābhyutpatanti sma harayo 'tha yuyutsavaḥ</li>
    </ul>
    <ul id="R_6.062.025">
        <li id="R_6.062.025ab">udghuṣṭaṃ vānarāṇāṃ ca rākṣasānāṃ ca nisvanaḥ</li>
        <li id="R_6.062.025cd">diśo daśa samudraṃ ca pṛthivīṃ cānvanādayat</li>
    </ul>
    <ul id="R_6.062.026">
        <li id="R_6.062.026ab">viśalyau tu mahātmānau tāv ubhau rāmalakṣmaṇau</li>
        <li id="R_6.062.026cd">asaṃbhrāntau jagṛhatus tāv ubhau dhanuṣī vare</li>
    </ul>
    <ul id="R_6.062.027">
        <li id="R_6.062.027ab">tato visphārayāṇasya rāmasya dhanur uttamam</li>
        <li id="R_6.062.027cd">babhūva tumulaḥ śabdo rākṣasānāṃ bhayāvahaḥ</li>
    </ul>
    <ul id="R_6.062.028">
        <li id="R_6.062.028ab">aśobhata tadā rāmo dhanur visphārayan mahat</li>
        <li id="R_6.062.028cd">bhagavān iva saṃkruddho bhavo vedamayaṃ dhanuḥ</li>
    </ul>
    <ul id="R_6.062.029">
        <li id="R_6.062.029ab">vānarodghuṣṭaghoṣaś ca rākṣasānāṃ ca nisvanaḥ</li>
        <li id="R_6.062.029cd">jyāśabdaś cāpi rāmasya trayaṃ vyāpa diśo daśa</li>
    </ul>
    <ul id="R_6.062.030">
        <li id="R_6.062.030ab">tasya kārmukamuktaiś ca śarais tatpuragopuram</li>
        <li id="R_6.062.030cd">kailāsaśṛṅgapratimaṃ vikīrṇam apatad bhuvi</li>
    </ul>
    <ul id="R_6.062.031">
        <li id="R_6.062.031ab">tato rāmaśarān dṛṣṭvā vimāneṣu gṛheṣu ca</li>
        <li id="R_6.062.031cd">saṃnāho rākṣasendrāṇāṃ tumulaḥ samapadyata</li>
    </ul>
    <ul id="R_6.062.032">
        <li id="R_6.062.032ab">teṣāṃ saṃnahyamānānāṃ siṃhanādaṃ ca kurvatām</li>
        <li id="R_6.062.032cd">śarvarī rākṣasendrāṇāṃ raudrīva samapadyata</li>
    </ul>
    <ul id="R_6.062.033">
        <li id="R_6.062.033ab">ādiṣṭā vānarendrās te sugrīveṇa mahātmanā</li>
        <li id="R_6.062.033cd">āsannā dvāram āsādya yudhyadhvaṃ plavagarṣabhāḥ</li>
    </ul>
    <ul id="R_6.062.034">
        <li id="R_6.062.034ab">yaś ca vo vitathaṃ kuryāt tatra tatra vyavasthitaḥ</li>
        <li id="R_6.062.034cd">sa hantavyo 'bhisaṃplutya rājaśāsanadūṣakaḥ</li>
    </ul>
    <ul id="R_6.062.035">
        <li id="R_6.062.035ab">teṣu vānaramukhyeṣu dīptolkojjvalapāṇiṣu</li>
        <li id="R_6.062.035cd">sthiteṣu dvāram āsādya rāvaṇaṃ manyur āviśat</li>
    </ul>
    <ul id="R_6.062.036">
        <li id="R_6.062.036ab">tasya jṛmbhitavikṣepād vyāmiśrā vai diśo daśa</li>
        <li id="R_6.062.036cd">rūpavān iva rudrasya manyur gātreṣv adṛśyata</li>
    </ul>
    <ul id="R_6.062.037">
        <li id="R_6.062.037ab">sa nikumbhaṃ ca kumbhaṃ ca kumbhakarṇātmajāv ubhau</li>
        <li id="R_6.062.037cd">preṣayām āsa saṃkruddho rākṣasair bahubhiḥ saha</li>
    </ul>
    <ul id="R_6.062.038">
        <li id="R_6.062.038ab">śaśāsa caiva tān sarvān rākṣasān rākṣaseśvaraḥ</li>
        <li id="R_6.062.038cd">rākṣasā gacchatātraiva siṃhanādaṃ ca nādayan</li>
    </ul>
    <ul id="R_6.062.039">
        <li id="R_6.062.039ab">tatas tu coditās tena rākṣasā jvalitāyudhāḥ</li>
        <li id="R_6.062.039cd">laṅkāyā niryayur vīrāḥ praṇadantaḥ punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.062.040">
        <li id="R_6.062.040ab">bhīmāśvarathamātaṃgaṃ nānāpatti samākulam</li>
        <li id="R_6.062.040cd">dīptaśūlagadākhaḍgaprāsatomarakārmukam</li>
    </ul>
    <ul id="R_6.062.041">
        <li id="R_6.062.041ab">tad rākṣasabalaṃ ghoraṃ bhīmavikramapauruṣam</li>
        <li id="R_6.062.041cd">dadṛśe jvalitaprāsaṃ kiṅkiṇīśatanāditam</li>
    </ul>
    <ul id="R_6.062.042">
        <li id="R_6.062.042ab">hemajālācitabhujaṃ vyāveṣṭitaparaśvadham</li>
        <li id="R_6.062.042cd">vyāghūrṇitamahāśastraṃ bāṇasaṃsaktakārmukam</li>
    </ul>
    <ul id="R_6.062.043">
        <li id="R_6.062.043ab">gandhamālyamadhūtsekasaṃmodita mahānilam</li>
        <li id="R_6.062.043cd">ghoraṃ śūrajanākīrṇaṃ mahāmbudharanisvanam</li>
    </ul>
    <ul id="R_6.062.044">
        <li id="R_6.062.044ab">taṃ dṛṣṭvā balam āyāntaṃ rākṣasānāṃ sudāruṇam</li>
        <li id="R_6.062.044cd">saṃcacāla plavaṃgānāṃ balam uccair nanāda ca</li>
    </ul>
    <ul id="R_6.062.045">
        <li id="R_6.062.045ab">javenāplutya ca punas tad rākṣasabalaṃ mahat</li>
        <li id="R_6.062.045cd">abhyayāt pratyaribalaṃ pataṃga iva pāvakam</li>
    </ul>
    <ul id="R_6.062.046">
        <li id="R_6.062.046ab">teṣāṃ bhujaparāmarśavyāmṛṣṭaparighāśani</li>
        <li id="R_6.062.046cd">rākṣasānāṃ balaṃ śreṣṭhaṃ bhūyastaram aśobhata</li>
    </ul>
    <ul id="R_6.062.047">
        <li id="R_6.062.047ab">tathaivāpy apare teṣāṃ kapīnām asibhiḥ śitaiḥ</li>
        <li id="R_6.062.047cd">pravīrān abhito jaghnur ghorarūpā niśācarāḥ</li>
    </ul>
    <ul id="R_6.062.048">
        <li id="R_6.062.048ab">ghnantam anyaṃ jaghānānyaḥ pātayantam apātayat</li>
        <li id="R_6.062.048cd">garhamāṇaṃ jagarhānye daśantam apare 'daśat</li>
    </ul>
    <ul id="R_6.062.049">
        <li id="R_6.062.049ab">dehīty anye dadāty anyo dadāmīty aparaḥ punaḥ</li>
        <li id="R_6.062.049cd">kiṃ kleśayasi tiṣṭheti tatrānyonyaṃ babhāṣire</li>
    </ul>
    <ul id="R_6.062.050">
        <li id="R_6.062.050ab">samudyatamahāprāsaṃ muṣṭiśūlāsisaṃkulam</li>
        <li id="R_6.062.050cd">prāvartata mahāraudraṃ yuddhaṃ vānararakṣasām</li>
    </ul>
    <ul id="R_6.062.051">
        <li id="R_6.062.051ab">vānarān daśa sapteti rākṣasā abhyapātayan</li>
        <li id="R_6.062.051cd">rākṣasān daśasapteti vānarā jaghnur āhave</li>
    </ul>
    <ul id="R_6.062.052">
        <li id="R_6.062.052ab">visrastakeśarasanaṃ vimuktakavacadhvajam</li>
        <li id="R_6.062.052cd">balaṃ rākṣasam ālambya vānarāḥ paryavārayan</li>
    </ul>
    <ul id="R_6.063.001">
        <li id="R_6.063.001ab">pravṛtte saṃkule tasmin ghore vīrajanakṣaye</li>
        <li id="R_6.063.001cd">aṅgadaḥ kampanaṃ vīram āsasāda raṇotsukaḥ</li>
    </ul>
    <ul id="R_6.063.002">
        <li id="R_6.063.002ab">āhūya so 'ṅgadaṃ kopāt tāḍayām āsa vegitaḥ</li>
        <li id="R_6.063.002cd">gadayā kampanaḥ pūrvaṃ sa cacāla bhṛśāhataḥ</li>
    </ul>
    <ul id="R_6.063.003">
        <li id="R_6.063.003ab">sa saṃjñāṃ prāpya tejasvī cikṣepa śikharaṃ gireḥ</li>
        <li id="R_6.063.003cd">arditaś ca prahāreṇa kampanaḥ patito bhuvi</li>
    </ul>
    <ul id="R_6.063.004">
        <li id="R_6.063.004ab">hatapravīrā vyathitā rākṣasendracamūs tadā</li>
        <li id="R_6.063.004cd">jagāmābhimukhī sā tu kumbhakarṇasuto yataḥ</li>
        <li id="R_6.063.004ef">āpatantīṃ ca vegena kumbhas tāṃ sāntvayac camūm</li>
    </ul>
    <ul id="R_6.063.005">
        <li id="R_6.063.005ab">sa dhanur dhanvināṃ śreṣṭhaḥ pragṛhya susamāhitaḥ</li>
        <li id="R_6.063.005cd">mumocāśīviṣaprakhyāñ śarān dehavidāraṇān</li>
    </ul>
    <ul id="R_6.063.006">
        <li id="R_6.063.006ab">tasya tac chuśubhe bhūyaḥ saśaraṃ dhanur uttamam</li>
        <li id="R_6.063.006cd">vidyudairāvatārciṣmad dvitīyendradhanur yathā</li>
    </ul>
    <ul id="R_6.063.007">
        <li id="R_6.063.007ab">ākarṇakṛṣṭamuktena jaghāna dvividaṃ tadā</li>
        <li id="R_6.063.007cd">tena hāṭakapuṅkhena patriṇā patravāsasā</li>
    </ul>
    <ul id="R_6.063.008">
        <li id="R_6.063.008ab">sahasābhihatas tena vipramuktapadaḥ sphuran</li>
        <li id="R_6.063.008cd">nipapātādrikūṭābho vihvalaḥ plavagottamaḥ</li>
    </ul>
    <ul id="R_6.063.009">
        <li id="R_6.063.009ab">maindas tu bhrātaraṃ dṛṣṭvā bhagnaṃ tatra mahāhave</li>
        <li id="R_6.063.009cd">abhidudrāva vegena pragṛhya mahatīṃ śilām</li>
    </ul>
    <ul id="R_6.063.010">
        <li id="R_6.063.010ab">tāṃ śilāṃ tu pracikṣepa rākṣasāya mahābalaḥ</li>
        <li id="R_6.063.010cd">bibheda tāṃ śilāṃ kumbhaḥ prasannaiḥ pañcabhiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.063.011">
        <li id="R_6.063.011ab">saṃdhāya cānyaṃ sumukhaṃ śaram āśīviṣopamam</li>
        <li id="R_6.063.011cd">ājaghāna mahātejā vakṣasi dvividāgrajam</li>
    </ul>
    <ul id="R_6.063.012">
        <li id="R_6.063.012ab">sa tu tena prahāreṇa maindo vānarayūthapaḥ</li>
        <li id="R_6.063.012cd">marmaṇy abhihatas tena papāta bhuvi mūrchitaḥ</li>
    </ul>
    <ul id="R_6.063.013">
        <li id="R_6.063.013ab">aṅgado mātulau dṛṣṭvā patitau tau mahābalau</li>
        <li id="R_6.063.013cd">abhidudrāva vegena kumbham udyatakārmukam</li>
    </ul>
    <ul id="R_6.063.014">
        <li id="R_6.063.014ab">tam āpatantaṃ vivyādha kumbhaḥ pañcabhir āyasaiḥ</li>
        <li id="R_6.063.014cd">tribhiś cānyaiḥ śitair bāṇair mātaṃgam iva tomaraiḥ</li>
    </ul>
    <ul id="R_6.063.015">
        <li id="R_6.063.015ab">so 'ṅgadaṃ vividhair bāṇaiḥ kumbho vivyādha vīryavān</li>
        <li id="R_6.063.015cd">akuṇṭhadhārair niśitais tīkṣṇaiḥ kanakabhūṣaṇaiḥ</li>
    </ul>
    <ul id="R_6.063.016">
        <li id="R_6.063.016ab">aṅgadaḥ pratividdhāṅgo vāliputro na kampate</li>
        <li id="R_6.063.016cd">śilāpādapavarṣāṇi tasya mūrdhni vavarṣa ha</li>
    </ul>
    <ul id="R_6.063.017">
        <li id="R_6.063.017ab">sa praciccheda tān sarvān bibheda ca punaḥ śilāḥ</li>
        <li id="R_6.063.017cd">kumbhakarṇātmajaḥ śrīmān vāliputrasamīritān</li>
    </ul>
    <ul id="R_6.063.018">
        <li id="R_6.063.018ab">āpatantaṃ ca saṃprekṣya kumbho vānarayūthapam</li>
        <li id="R_6.063.018cd">bhruvor vivyādha bāṇābhyām ulkābhyām iva kuñjaram</li>
    </ul>
    <ul id="R_6.063.019">
        <li id="R_6.063.019ab">aṅgadaḥ pāṇinā netre pidhāya rudhirokṣite</li>
        <li id="R_6.063.019cd">sālam āsannam ekena parijagrāha pāṇinā</li>
    </ul>
    <ul id="R_6.063.020">
        <li id="R_6.063.020ab">tam indraketupratimaṃ vṛkṣaṃ mandarasaṃnibham</li>
        <li id="R_6.063.020cd">samutsṛjantaṃ vegena paśyatāṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_6.063.021">
        <li id="R_6.063.021ab">sa ciccheda śitair bāṇaiḥ saptabhiḥ kāyabhedanaiḥ</li>
        <li id="R_6.063.021cd">aṅgado vivyathe 'bhīkṣṇaṃ sasāda ca mumoha ca</li>
    </ul>
    <ul id="R_6.063.022">
        <li id="R_6.063.022ab">aṅgadaṃ vyathitaṃ dṛṣṭvā sīdantam iva sāgare</li>
        <li id="R_6.063.022cd">durāsadaṃ hariśreṣṭhā rāghavāya nyavedayan</li>
    </ul>
    <ul id="R_6.063.023">
        <li id="R_6.063.023ab">rāmas tu vyathitaṃ śrutvā vāliputraṃ mahāhave</li>
        <li id="R_6.063.023cd">vyādideśa hariśreṣṭhāñ jāmbavatpramukhāṃs tataḥ</li>
    </ul>
    <ul id="R_6.063.024">
        <li id="R_6.063.024ab">te tu vānaraśārdūlāḥ śrutvā rāmasya śāsanam</li>
        <li id="R_6.063.024cd">abhipetuḥ susaṃkruddhāḥ kumbham udyatakārmukam</li>
    </ul>
    <ul id="R_6.063.025">
        <li id="R_6.063.025ab">tato drumaśilāhastāḥ kopasaṃraktalocanāḥ</li>
        <li id="R_6.063.025cd">rirakṣiṣanto 'bhyapatann aṅgadaṃ vānararṣabhāḥ</li>
    </ul>
    <ul id="R_6.063.026">
        <li id="R_6.063.026ab">jāmbavāṃś ca suṣeṇaś ca vegadarśī ca vānaraḥ</li>
        <li id="R_6.063.026cd">kumbhakarṇātmajaṃ vīraṃ kruddhāḥ samabhidudruvuḥ</li>
    </ul>
    <ul id="R_6.063.027">
        <li id="R_6.063.027ab">samīkṣyātatatas tāṃs tu vānarendrān mahābalān</li>
        <li id="R_6.063.027cd">āvavāra śaraugheṇa nageneva jalāśayam</li>
    </ul>
    <ul id="R_6.063.028">
        <li id="R_6.063.028ab">tasya bāṇacayaṃ prāpya na śoker ativartitum</li>
        <li id="R_6.063.028cd">vānarendrā mahātmāno velām iva mahodadhiḥ</li>
    </ul>
    <ul id="R_6.063.029">
        <li id="R_6.063.029ab">tāṃs tu dṛṣṭvā harigaṇāñ śaravṛṣṭibhir arditān</li>
        <li id="R_6.063.029cd">aṅgadaṃ pṛṣṭhataḥ kṛtvā bhrātṛjaṃ plavageśvaraḥ</li>
    </ul>
    <ul id="R_6.063.030">
        <li id="R_6.063.030ab">abhidudrāva vegena sugrīvaḥ kumbham āhave</li>
        <li id="R_6.063.030cd">śailasānu caraṃ nāgaṃ vegavān iva kesarī</li>
    </ul>
    <ul id="R_6.063.031">
        <li id="R_6.063.031ab">utpāṭya ca mahāśailān aśvakarṇān dhavān bahūn</li>
        <li id="R_6.063.031cd">anyāṃś ca vividhān vṛkṣāṃś cikṣepa ca mahābalaḥ</li>
    </ul>
    <ul id="R_6.063.032">
        <li id="R_6.063.032ab">tāṃ chādayantīm ākāśaṃ vṛkṣavṛṣṭiṃ durāsadām</li>
        <li id="R_6.063.032cd">kumbhakarṇātmajaḥ śrīmāṃś ciccheda niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.063.033">
        <li id="R_6.063.033ab">abhilakṣyeṇa tīvreṇa kumbhena niśitaiḥ śaraiḥ</li>
        <li id="R_6.063.033cd">ācitās te drumā rejur yathā ghorāḥ śataghnayaḥ</li>
    </ul>
    <ul id="R_6.063.034">
        <li id="R_6.063.034ab">drumavarṣaṃ tu tac chinnaṃ dṛṣṭvā kumbhena vīryavān</li>
        <li id="R_6.063.034cd">vānarādhipatiḥ śrīmān mahāsattvo na vivyathe</li>
    </ul>
    <ul id="R_6.063.035">
        <li id="R_6.063.035ab">nirbhidyamānaḥ sahasā sahamānaś ca tāñ śarān</li>
        <li id="R_6.063.035cd">kumbhasya dhanur ākṣipya babhañjendradhanuḥprabham</li>
    </ul>
    <ul id="R_6.063.036">
        <li id="R_6.063.036ab">avaplutya tataḥ śīghraṃ kṛtvā karma suduṣkaram</li>
        <li id="R_6.063.036cd">abravīt kupitaḥ kumbhaṃ bhagnaśṛṅgam iva dvipam</li>
    </ul>
    <ul id="R_6.063.037">
        <li id="R_6.063.037ab">nikumbhāgraja vīryaṃ te bāṇavegaṃ tad adbhutam</li>
        <li id="R_6.063.037cd">saṃnatiś ca prabhāvaś ca tava vā rāvaṇasya vā</li>
    </ul>
    <ul id="R_6.063.038">
        <li id="R_6.063.038ab">prahrādabalivṛtraghnakuberavaruṇopama</li>
        <li id="R_6.063.038cd">ekas tvam anujāto 'si pitaraṃ balavattaraḥ</li>
    </ul>
    <ul id="R_6.063.039">
        <li id="R_6.063.039ab">tvām evaikaṃ mahābāhuṃ śūlahastam ariṃdamam</li>
        <li id="R_6.063.039cd">tridaśā nātivartante jitendriyam ivādhayaḥ</li>
    </ul>
    <ul id="R_6.063.040">
        <li id="R_6.063.040ab">varadānāt pitṛvyas te sahate devadānavān</li>
        <li id="R_6.063.040cd">kumbhakarṇas tu vīryeṇa sahate ca surāsurān</li>
    </ul>
    <ul id="R_6.063.041">
        <li id="R_6.063.041ab">dhanuṣīndrajitas tulyaḥ pratāpe rāvaṇasya ca</li>
        <li id="R_6.063.041cd">tvam adya rakṣasāṃ loke śreṣṭho 'si balavīryataḥ</li>
    </ul>
    <ul id="R_6.063.042">
        <li id="R_6.063.042ab">mahāvimardaṃ samare mayā saha tavādbhutam</li>
        <li id="R_6.063.042cd">adya bhūtāni paśyantu śakraśambarayor iva</li>
    </ul>
    <ul id="R_6.063.043">
        <li id="R_6.063.043ab">kṛtam apratimaṃ karma darśitaṃ cāstrakauśalam</li>
        <li id="R_6.063.043cd">pātitā harivīrāś ca tvayaite bhīmavikramāḥ</li>
    </ul>
    <ul id="R_6.063.044">
        <li id="R_6.063.044ab">upālambhabhayāc cāpi nāsi vīra mayā hataḥ</li>
        <li id="R_6.063.044cd">kṛtakarmā pariśrānto viśrāntaḥ paśya me balam</li>
    </ul>
    <ul id="R_6.063.045">
        <li id="R_6.063.045ab">tena sugrīvavākyena sāvamānena mānitaḥ</li>
        <li id="R_6.063.045cd">agner ājyahutasyeva tejas tasyābhyavardhata</li>
    </ul>
    <ul id="R_6.063.046">
        <li id="R_6.063.046ab">tataḥ kumbhaḥ samutpatya sugrīvam abhipadya ca</li>
        <li id="R_6.063.046cd">ājaghānorasi kruddho vajravegena muṣṭinā</li>
    </ul>
    <ul id="R_6.063.047">
        <li id="R_6.063.047ab">tasya carma ca pusphoṭa saṃjajñe cāsya śoṇitam</li>
        <li id="R_6.063.047cd">sa ca muṣṭir mahāvegaḥ pratijaghne 'sthimaṇḍale</li>
    </ul>
    <ul id="R_6.063.048">
        <li id="R_6.063.048ab">tadā vegena tatrāsīt tejaḥ prajvālitaṃ muhuḥ</li>
        <li id="R_6.063.048cd">vajraniṣpeṣasaṃjātajvālā merau yathā girau</li>
    </ul>
    <ul id="R_6.063.049">
        <li id="R_6.063.049ab">sa tatrābhihatas tena sugrīvo vānararṣabhaḥ</li>
        <li id="R_6.063.049cd">muṣṭiṃ saṃvartayām āsa vajrakalpaṃ mahābalaḥ</li>
    </ul>
    <ul id="R_6.063.050">
        <li id="R_6.063.050ab">arciḥsahasravikacaṃ ravimaṇḍalasaprabham</li>
        <li id="R_6.063.050cd">sa muṣṭiṃ pātayām āsa kumbhasyorasi vīryavān</li>
    </ul>
    <ul id="R_6.063.051">
        <li id="R_6.063.051ab">muṣṭinābhihatas tena nipapātāśu rākṣasaḥ</li>
        <li id="R_6.063.051cd">lohitāṅga ivākāśād dīptaraśmir yadṛcchayā</li>
    </ul>
    <ul id="R_6.063.052">
        <li id="R_6.063.052ab">kumbhasya patato rūpaṃ bhagnasyorasi muṣṭinā</li>
        <li id="R_6.063.052cd">babhau rudrābhipannasya yathārūpaṃ gavāṃ pateḥ</li>
    </ul>
    <ul id="R_6.063.053">
        <li id="R_6.063.053ab">
            <seg type="pāda" n="a">tasmin hate bhīmaparākrameṇa</seg>
            <seg type="pāda" n="b">plavaṃgamānām ṛṣabheṇa yuddhe</seg>
        </li>
        <li id="R_6.063.053cd">
            <seg type="pāda" n="c">mahī saśailā savanā cacāla</seg>
            <seg type="pāda" n="d">bhayaṃ ca rakṣāṃsy adhikaṃ viveśa</seg>
        </li>
    </ul>
    <ul id="R_6.064.001">
        <li id="R_6.064.001ab">nikumbho bhrātaraṃ dṛṣṭvā sugrīveṇa nipātitam</li>
        <li id="R_6.064.001cd">pradahann iva kopena vānarendram avaikṣata</li>
    </ul>
    <ul id="R_6.064.002">
        <li id="R_6.064.002ab">tataḥ sragdāmasaṃnaddhaṃ dattapañcāṅgulaṃ śubham</li>
        <li id="R_6.064.002cd">ādade parighaṃ vīro nagendraśikharopamam</li>
    </ul>
    <ul id="R_6.064.003">
        <li id="R_6.064.003ab">hemapaṭṭaparikṣiptaṃ vajravidrumabhūṣitam</li>
        <li id="R_6.064.003cd">yamadaṇḍopamaṃ bhīmaṃ rakṣasāṃ bhayanāśanam</li>
    </ul>
    <ul id="R_6.064.004">
        <li id="R_6.064.004ab">tam āvidhya mahātejāḥ śakradhvajasamaṃ raṇe</li>
        <li id="R_6.064.004cd">vinanāda vivṛttāsyo nikumbho bhīmavikramaḥ</li>
    </ul>
    <ul id="R_6.064.005">
        <li id="R_6.064.005ab">urogatena niṣkeṇa bhujasthair aṅgadair api</li>
        <li id="R_6.064.005cd">kuṇḍalābhyāṃ ca mṛṣṭābhyāṃ mālayā ca vicitrayā</li>
    </ul>
    <ul id="R_6.064.006">
        <li id="R_6.064.006ab">nikumbho bhūṣaṇair bhāti tena sma parigheṇa ca</li>
        <li id="R_6.064.006cd">yathendradhanuṣā meghaḥ savidyutstanayitnumān</li>
    </ul>
    <ul id="R_6.064.007">
        <li id="R_6.064.007ab">parighāgreṇa pusphoṭa vātagranthir mahātmanaḥ</li>
        <li id="R_6.064.007cd">prajajvāla saghoṣaś ca vidhūma iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.064.008">
        <li id="R_6.064.008ab">nagaryā viṭapāvatyā gandharvabhavanottamaiḥ</li>
        <li id="R_6.064.008cd">saha caivāmarāvatyā sarvaiś ca bhavanaiḥ saha</li>
    </ul>
    <ul id="R_6.064.009">
        <li id="R_6.064.009ab">satārāgaṇanakṣatraṃ sacandraṃ samahāgraham</li>
        <li id="R_6.064.009cd">nikumbhaparighāghūrṇaṃ bhramatīva nabhastalam</li>
    </ul>
    <ul id="R_6.064.010">
        <li id="R_6.064.010ab">durāsadaś ca saṃjajñe parighābharaṇaprabhaḥ</li>
        <li id="R_6.064.010cd">krodhendhano nikumbhāgnir yugāntāgnir ivotthitaḥ</li>
    </ul>
    <ul id="R_6.064.011">
        <li id="R_6.064.011ab">rākṣasā vānarāś cāpi na śekuḥ spandituṃ bhayāt</li>
        <li id="R_6.064.011cd">hanūmaṃs tu vivṛtyoras tasthau pramukhato balī</li>
    </ul>
    <ul id="R_6.064.012">
        <li id="R_6.064.012ab">parighopamabāhus tu parighaṃ bhāskaraprabham</li>
        <li id="R_6.064.012cd">balī balavatas tasya pātayām āsa vakṣasi</li>
    </ul>
    <ul id="R_6.064.013">
        <li id="R_6.064.013ab">sthire tasyorasi vyūḍhe parighaḥ śatadhā kṛtaḥ</li>
        <li id="R_6.064.013cd">viśīryamāṇaḥ sahasā ulkā śatam ivāmbare</li>
    </ul>
    <ul id="R_6.064.014">
        <li id="R_6.064.014ab">sa tu tena prahāreṇa cacāla ca mahākapiḥ</li>
        <li id="R_6.064.014cd">parigheṇa samādhūto yathā bhūmicale 'calaḥ</li>
    </ul>
    <ul id="R_6.064.015">
        <li id="R_6.064.015ab">sa tathābhihatas tena hanūmān plavagottamaḥ</li>
        <li id="R_6.064.015cd">muṣṭiṃ saṃvartayām āsa balenātimahābalaḥ</li>
    </ul>
    <ul id="R_6.064.016">
        <li id="R_6.064.016ab">tam udyamya mahātejā nikumbhorasi vīryavān</li>
        <li id="R_6.064.016cd">abhicikṣepa vegena vegavān vāyuvikramaḥ</li>
    </ul>
    <ul id="R_6.064.017">
        <li id="R_6.064.017ab">tataḥ pusphoṭa carmāsya prasusrāva ca śoṇitam</li>
        <li id="R_6.064.017cd">muṣṭinā tena saṃjajñe jvālā vidyud ivotthitā</li>
    </ul>
    <ul id="R_6.064.018">
        <li id="R_6.064.018ab">sa tu tena prahāreṇa nikumbho vicacāla ha</li>
        <li id="R_6.064.018cd">svasthaś cāpi nijagrāha hanūmantaṃ mahābalam</li>
    </ul>
    <ul id="R_6.064.019">
        <li id="R_6.064.019ab">vicukruśus tadā saṃkhye bhīmaṃ laṅkānivāsinaḥ</li>
        <li id="R_6.064.019cd">nikumbhenoddhṛtaṃ dṛṣṭvā hanūmantaṃ mahābalam</li>
    </ul>
    <ul id="R_6.064.020">
        <li id="R_6.064.020ab">sa tathā hriyamāṇo 'pi kumbhakarṇātmajena hi</li>
        <li id="R_6.064.020cd">ājaghānānilasuto vajravegena muṣṭinā</li>
    </ul>
    <ul id="R_6.064.021">
        <li id="R_6.064.021ab">ātmānaṃ mocayitvātha kṣitāv abhyavapadyata</li>
        <li id="R_6.064.021cd">hanūmān unmamathāśu nikumbhaṃ mārutātmajaḥ</li>
    </ul>
    <ul id="R_6.064.022">
        <li id="R_6.064.022ab">nikṣipya paramāyatto nikumbhaṃ niṣpipeṣa ca</li>
        <li id="R_6.064.022cd">utpatya cāsya vegena papātorasi vīryavān</li>
    </ul>
    <ul id="R_6.064.023">
        <li id="R_6.064.023ab">parigṛhya ca bāhubhyāṃ parivṛtya śirodharām</li>
        <li id="R_6.064.023cd">utpāṭayām āsa śiro bhairavaṃ nadato mahat</li>
    </ul>
    <ul id="R_6.064.024">
        <li id="R_6.064.024ab">
            <seg type="pāda" n="a">atha vinadati sādite nikumbhe</seg>
            <seg type="pāda" n="b">pavanasutena raṇe babhūva yuddham</seg>
        </li>
        <li id="R_6.064.024cd">
            <seg type="pāda" n="c">daśarathasutarākṣasendracamvor</seg>
            <seg type="pāda" n="d">bhṛśataram āgataroṣayoḥ subhīmam</seg>
        </li>
    </ul>
    <ul id="R_6.065.001">
        <li id="R_6.065.001ab">nikumbhaṃ ca hataṃ śrutvā kumbhaṃ ca vinipātitam</li>
        <li id="R_6.065.001cd">rāvaṇaḥ paramāmarṣī prajajvālānalo yathā</li>
    </ul>
    <ul id="R_6.065.002">
        <li id="R_6.065.002ab">nairṛtaḥ krodhaśokābhyāṃ dvābhyāṃ tu parimūrchitaḥ</li>
        <li id="R_6.065.002cd">kharaputraṃ viśālākṣaṃ makarākṣam acodayat</li>
    </ul>
    <ul id="R_6.065.003">
        <li id="R_6.065.003ab">gaccha putra mayājñapto balenābhisamanvitaḥ</li>
        <li id="R_6.065.003cd">rāghavaṃ lakṣmaṇaṃ caiva jahi tau savanaukasau</li>
    </ul>
    <ul id="R_6.065.004">
        <li id="R_6.065.004ab">rāvaṇasya vacaḥ śrutvā śūro mānī kharātmajaḥ</li>
        <li id="R_6.065.004cd">bāḍham ity abravīd dhṛṣṭo makarākṣo niśācaraḥ</li>
    </ul>
    <ul id="R_6.065.005">
        <li id="R_6.065.005ab">so 'bhivādya daśagrīvaṃ kṛtvā cāpi pradakṣiṇam</li>
        <li id="R_6.065.005cd">nirjagāma gṛhāc chubhrād rāvaṇasyājñayā balī</li>
    </ul>
    <ul id="R_6.065.006">
        <li id="R_6.065.006ab">samīpasthaṃ balādhyakṣaṃ kharaputro 'bravīd idam</li>
        <li id="R_6.065.006cd">ratham ānīyatāṃ śīghraṃ sainyaṃ cānīyatāṃ tvarāt</li>
    </ul>
    <ul id="R_6.065.007">
        <li id="R_6.065.007ab">tasya tad vacanaṃ śrutvā balādhyakṣo niśācaraḥ</li>
        <li id="R_6.065.007cd">syandanaṃ ca balaṃ caiva samīpaṃ pratyapādayat</li>
    </ul>
    <ul id="R_6.065.008">
        <li id="R_6.065.008ab">pradakṣiṇaṃ rathaṃ kṛtvā āruroha niśācaraḥ</li>
        <li id="R_6.065.008cd">sūtaṃ saṃcodayām āsa śīghraṃ me ratham āvaha</li>
    </ul>
    <ul id="R_6.065.009">
        <li id="R_6.065.009ab">atha tān rākṣasān sarvān makarākṣo 'bravīd idam</li>
        <li id="R_6.065.009cd">yūyaṃ sarve prayudhyadhvaṃ purastān mama rākṣasāḥ</li>
    </ul>
    <ul id="R_6.065.010">
        <li id="R_6.065.010ab">ahaṃ rākṣasarājena rāvaṇena mahātmanā</li>
        <li id="R_6.065.010cd">ājñaptaḥ samare hantuṃ tāv ubhau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.065.011">
        <li id="R_6.065.011ab">adya rāmaṃ vadhiṣyāmi lakṣmaṇaṃ ca niśācarāḥ</li>
        <li id="R_6.065.011cd">śākhāmṛgaṃ ca sugrīvaṃ vānarāṃś ca śarottamaiḥ</li>
    </ul>
    <ul id="R_6.065.012">
        <li id="R_6.065.012ab">adya śūlanipātaiś ca vānarāṇāṃ mahācamūm</li>
        <li id="R_6.065.012cd">pradahiṣyāmi saṃprāptāṃ śuṣkendhanam ivānalaḥ</li>
    </ul>
    <ul id="R_6.065.013">
        <li id="R_6.065.013ab">makarākṣasya tac chrutvā vacanaṃ te niśācarāḥ</li>
        <li id="R_6.065.013cd">sarve nānāyudhopetā balavantaḥ samāhitāḥ</li>
    </ul>
    <ul id="R_6.065.014">
        <li id="R_6.065.014ab">te kāmarūpiṇaḥ śūrā daṃṣṭriṇaḥ piṅgalekṣaṇāḥ</li>
        <li id="R_6.065.014cd">mātaṃgā iva nardanto dhvastakeśā bhayānakāḥ</li>
    </ul>
    <ul id="R_6.065.015">
        <li id="R_6.065.015ab">parivārya mahākāyā mahākāyaṃ kharātmajam</li>
        <li id="R_6.065.015cd">abhijagmus tadā hṛṣṭāś cālayanto vasuṃdharām</li>
    </ul>
    <ul id="R_6.065.016">
        <li id="R_6.065.016ab">śaṅkhabherīsahasrāṇām āhatānāṃ samantataḥ</li>
        <li id="R_6.065.016cd">kṣveḍitāsphoṭitānāṃ ca tataḥ śabdo mahān abhūt</li>
    </ul>
    <ul id="R_6.065.017">
        <li id="R_6.065.017ab">prabhraṣṭo 'tha karāt tasya pratodaḥ sārathes tadā</li>
        <li id="R_6.065.017cd">papāta sahasā caiva dhvajas tasya ca rakṣasaḥ</li>
    </ul>
    <ul id="R_6.065.018">
        <li id="R_6.065.018ab">tasya te rathasaṃyuktā hayā vikramavarjitāḥ</li>
        <li id="R_6.065.018cd">caraṇair ākulair gatvā dīnāḥ sāsramukhā yayuḥ</li>
    </ul>
    <ul id="R_6.065.019">
        <li id="R_6.065.019ab">pravāti pavanas tasya sapāṃsuḥ kharadāruṇaḥ</li>
        <li id="R_6.065.019cd">niryāṇe tasya raudrasya makarākṣasya durmateḥ</li>
    </ul>
    <ul id="R_6.065.020">
        <li id="R_6.065.020ab">tāni dṛṣṭvā nimittāni rākṣasā vīryavattamāḥ</li>
        <li id="R_6.065.020cd">acintyanirgatāḥ sarve yatra tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.065.021">
        <li id="R_6.065.021ab">
            <seg type="pāda" n="a">ghanagajamahiṣāṅgatulyavarṇāḥ</seg>
            <seg type="pāda" n="b">samaramukheṣv asakṛd gadāsibhinnāḥ</seg>
        </li>
        <li id="R_6.065.021cd">
            <seg type="pāda" n="c">aham aham iti yuddhakauśalās te</seg>
            <seg type="pāda" n="d">rajanicarāḥ paribabhramur nadantaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.066.001">
        <li id="R_6.066.001ab">nirgataṃ makarākṣaṃ te dṛṣṭvā vānarapuṃgavāḥ</li>
        <li id="R_6.066.001cd">āplutya sahasā sarve yoddhukāmā vyavasthitāḥ</li>
    </ul>
    <ul id="R_6.066.002">
        <li id="R_6.066.002ab">tataḥ pravṛttaṃ sumahat tad yuddhaṃ lomaharṣaṇam</li>
        <li id="R_6.066.002cd">niśācaraiḥ plavaṃgānāṃ devānāṃ dānavair iva</li>
    </ul>
    <ul id="R_6.066.003">
        <li id="R_6.066.003ab">vṛkṣaśūlanipātaiś ca śilāparighapātanaiḥ</li>
        <li id="R_6.066.003cd">anyonyaṃ mardayanti sma tadā kapiniśācarāḥ</li>
    </ul>
    <ul id="R_6.066.004">
        <li id="R_6.066.004ab">śaktiśūlagadākhaḍgais tomaraiś ca niśācarāḥ</li>
        <li id="R_6.066.004cd">paṭṭasair bhindipālaiś ca bāṇapātaiḥ samantataḥ</li>
    </ul>
    <ul id="R_6.066.005">
        <li id="R_6.066.005ab">pāśamudgaradaṇḍaiś ca nirghātaiś cāparais tathā</li>
        <li id="R_6.066.005cd">kadanaṃ kapisiṃhānāṃ cakrus te rajanīcarāḥ</li>
    </ul>
    <ul id="R_6.066.006">
        <li id="R_6.066.006ab">bāṇaughair arditāś cāpi kharaputreṇa vānarāḥ</li>
        <li id="R_6.066.006cd">saṃbhrāntamanasaḥ sarve dudruvur bhayapīḍitāḥ</li>
    </ul>
    <ul id="R_6.066.007">
        <li id="R_6.066.007ab">tān dṛṣṭvā rākṣasāḥ sarve dravamāṇān vanaukasaḥ</li>
        <li id="R_6.066.007cd">nedus te siṃhavad dhṛṣṭā rākṣasā jitakāśinaḥ</li>
    </ul>
    <ul id="R_6.066.008">
        <li id="R_6.066.008ab">vidravatsu tadā teṣu vānareṣu samantataḥ</li>
        <li id="R_6.066.008cd">rāmas tān vārayām āsa śaravarṣeṇa rākṣasān</li>
    </ul>
    <ul id="R_6.066.009">
        <li id="R_6.066.009ab">vāritān rākṣasān dṛṣṭvā makarākṣo niśācaraḥ</li>
        <li id="R_6.066.009cd">krodhān alasam āviṣṭo vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_6.066.010">
        <li id="R_6.066.010ab">tiṣṭha rāma mayā sārdhaṃ dvandvayuddhaṃ dadāmi te</li>
        <li id="R_6.066.010cd">tyājayiṣyāmi te prāṇān dhanurmuktaiḥ śitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.066.011">
        <li id="R_6.066.011ab">yat tadā daṇḍakāraṇye pitaraṃ hatavān mama</li>
        <li id="R_6.066.011cd">madagrataḥ svakarmasthaṃ smṛtvā roṣo 'bhivardhate</li>
    </ul>
    <ul id="R_6.066.012">
        <li id="R_6.066.012ab">dahyante bhṛśam aṅgāni durātman mama rāghava</li>
        <li id="R_6.066.012cd">yan mayāsi na dṛṣṭas tvaṃ tasmin kāle mahāvane</li>
    </ul>
    <ul id="R_6.066.013">
        <li id="R_6.066.013ab">diṣṭyāsi darśanaṃ rāma mama tvaṃ prāptavān iha</li>
        <li id="R_6.066.013cd">kāṅkṣito 'si kṣudhārtasya siṃhasyevetaro mṛgaḥ</li>
    </ul>
    <ul id="R_6.066.014">
        <li id="R_6.066.014ab">adya madbāṇavegena pretarāḍ viṣayaṃ gataḥ</li>
        <li id="R_6.066.014cd">ye tvayā nihatāḥ śūrāḥ saha tais tvaṃ sameṣyasi</li>
    </ul>
    <ul id="R_6.066.015">
        <li id="R_6.066.015ab">bahunātra kim uktena śṛṇu rāma vaco mama</li>
        <li id="R_6.066.015cd">paśyantu sakalā lokās tvāṃ māṃ caiva raṇājire</li>
    </ul>
    <ul id="R_6.066.016">
        <li id="R_6.066.016ab">astrair vā gadayā vāpi bāhubhyāṃ vā mahāhave</li>
        <li id="R_6.066.016cd">abhyastaṃ yena vā rāma tena vā vartatāṃ yudhi</li>
    </ul>
    <ul id="R_6.066.017">
        <li id="R_6.066.017ab">makarākṣavacaḥ śrutvā rāmo daśarathātmajaḥ</li>
        <li id="R_6.066.017cd">abravīt prahasan vākyam uttarottaravādinam</li>
    </ul>
    <ul id="R_6.066.018">
        <li id="R_6.066.018ab">caturdaśasahasrāṇi rakṣasāṃ tvatpitā ca yaḥ</li>
        <li id="R_6.066.018cd">triśirā dūṣaṇaś cāpi daṇḍake nihatā mayā</li>
    </ul>
    <ul id="R_6.066.019">
        <li id="R_6.066.019ab">svāśitās tava māṃsena gṛdhragomāyuvāyasāḥ</li>
        <li id="R_6.066.019cd">bhaviṣyanty adya vai pāpa tīkṣṇatuṇḍanakhāṅkuśāḥ</li>
    </ul>
    <ul id="R_6.066.020">
        <li id="R_6.066.020ab">evam uktas tu rāmeṇa kharaputro niśācaraḥ</li>
        <li id="R_6.066.020cd">bāṇaughān asṛjat tasmai rāghavāya raṇājire</li>
    </ul>
    <ul id="R_6.066.021">
        <li id="R_6.066.021ab">tāñ śarāñ śaravarṣeṇa rāmaś ciccheda naikadhā</li>
        <li id="R_6.066.021cd">nipetur bhuvi te chinnā rukmapuṅkhāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_6.066.022">
        <li id="R_6.066.022ab">tad yuddham abhavat tatra sametyānyonyam ojasā</li>
        <li id="R_6.066.022cd">khara rākṣasaputrasya sūnor daśarathasya ca</li>
    </ul>
    <ul id="R_6.066.023">
        <li id="R_6.066.023ab">jīmūtayor ivākāśe śabdo jyātalayos tadā</li>
        <li id="R_6.066.023cd">dhanur muktaḥ svanotkṛṣṭaḥ śrūyate ca raṇājire</li>
    </ul>
    <ul id="R_6.066.024">
        <li id="R_6.066.024ab">devadānavagandharvāḥ kiṃnarāś ca mahoragāḥ</li>
        <li id="R_6.066.024cd">antarikṣagatāḥ sarve draṣṭukāmās tad adbhutam</li>
    </ul>
    <ul id="R_6.066.025">
        <li id="R_6.066.025ab">viddham anyonyagātreṣu dviguṇaṃ vardhate balam</li>
        <li id="R_6.066.025cd">kṛtapratikṛtānyonyaṃ kurvāte tau raṇājire</li>
    </ul>
    <ul id="R_6.066.026">
        <li id="R_6.066.026ab">rāmam uktās tu bāṇaughān rākṣasas tv acchinad raṇe</li>
        <li id="R_6.066.026cd">rakṣomuktāṃs tu rāmo vai naikadhā prācchinac charaiḥ</li>
    </ul>
    <ul id="R_6.066.027">
        <li id="R_6.066.027ab">bāṇaughavitatāḥ sarvā diśaś ca vidiśas tathā</li>
        <li id="R_6.066.027cd">saṃchannā vasudhā caiva samantān na prakāśate</li>
    </ul>
    <ul id="R_6.066.028">
        <li id="R_6.066.028ab">tataḥ kruddho mahābāhur dhanuś ciccheda rakṣasaḥ</li>
        <li id="R_6.066.028cd">aṣṭābhir atha nārācaiḥ sūtaṃ vivyādha rāghavaḥ</li>
        <li id="R_6.066.028ef">bhittvā śarai rathaṃ rāmo rathāśvān samapātayat</li>
    </ul>
    <ul id="R_6.066.029">
        <li id="R_6.066.029ab">viratho vasudhāṃ tiṣṭhan makarākṣo niśācaraḥ</li>
        <li id="R_6.066.029cd">atiṣṭhad vasudhāṃ rakṣaḥ śūlaṃ jagrāha pāṇinā</li>
        <li id="R_6.066.029ef">trāsanaṃ sarvabhūtānāṃ yugāntāgnisamaprabham</li>
    </ul>
    <ul id="R_6.066.030">
        <li id="R_6.066.030ab">vibhrāmya ca mahac chūlaṃ prajvalantaṃ niśācaraḥ</li>
        <li id="R_6.066.030cd">sa krodhāt prāhiṇot tasmai rāghavāya mahāhave</li>
    </ul>
    <ul id="R_6.066.031">
        <li id="R_6.066.031ab">tam āpatantaṃ jvalitaṃ kharaputrakarāc cyutam</li>
        <li id="R_6.066.031cd">bāṇais tu tribhir ākāśe śūlaṃ ciccheda rāghavaḥ</li>
    </ul>
    <ul id="R_6.066.032">
        <li id="R_6.066.032ab">sacchinno naikadhā śūlo divyahāṭakamaṇḍitaḥ</li>
        <li id="R_6.066.032cd">vyaśīryata mahokleva rāmabāṇārdito bhuvi</li>
    </ul>
    <ul id="R_6.066.033">
        <li id="R_6.066.033ab">tac chūlaṃ nihataṃ dṛṣṭvā rāmeṇādbhutakarmaṇā</li>
        <li id="R_6.066.033cd">sādhu sādhv iti bhūtāni vyāharanti nabhogatāḥ</li>
    </ul>
    <ul id="R_6.066.034">
        <li id="R_6.066.034ab">tad dṛṣṭvā nihataṃ śūlaṃ makarākṣo niśācaraḥ</li>
        <li id="R_6.066.034cd">muṣṭim udyamya kākutsthaṃ tiṣṭha tiṣṭheti cābravīt</li>
    </ul>
    <ul id="R_6.066.035">
        <li id="R_6.066.035ab">sa taṃ dṛṣṭvā patantaṃ vai prahasya raghunandanaḥ</li>
        <li id="R_6.066.035cd">pāvakāstraṃ tato rāmaḥ saṃdadhe svaśarāsane</li>
    </ul>
    <ul id="R_6.066.036">
        <li id="R_6.066.036ab">tenāstreṇa hataṃ rakṣaḥ kākutsthena tadā raṇe</li>
        <li id="R_6.066.036cd">saṃchinnahṛdayaṃ tatra papāta ca mamāra ca</li>
    </ul>
    <ul id="R_6.066.037">
        <li id="R_6.066.037ab">dṛṣṭvā te rākṣasāḥ sarve makarākṣasya pātanam</li>
        <li id="R_6.066.037cd">laṅkām eva pradhāvanta rāmabālārditās tadā</li>
    </ul>
    <ul id="R_6.066.038">
        <li id="R_6.066.038ab">
            <seg type="pāda" n="a">daśarathanṛpaputrabāṇavegai</seg>
            <seg type="pāda" n="b">rajanicaraṃ nihataṃ kharātmajaṃ tam</seg>
        </li>
        <li id="R_6.066.038cd">
            <seg type="pāda" n="c">dadṛśur atha ca devatāḥ prahṛṣṭā</seg>
            <seg type="pāda" n="d">girim iva vajrahataṃ yathā viśīrṇam</seg>
        </li>
    </ul>
    <ul id="R_6.067.001">
        <li id="R_6.067.001ab">makarākṣaṃ hataṃ śrutvā rāvaṇaḥ samitiṃjayaḥ</li>
        <li id="R_6.067.001cd">ādideśātha saṃkruddho raṇāyendrajitaṃ sutam</li>
    </ul>
    <ul id="R_6.067.002">
        <li id="R_6.067.002ab">jahi vīra mahāvīryau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.067.002cd">adṛśyo dṛśyamāno vā sarvathā tvaṃ balādhikaḥ</li>
    </ul>
    <ul id="R_6.067.003">
        <li id="R_6.067.003ab">tvam apratimakarmāṇam indraṃ jayasi saṃyuge</li>
        <li id="R_6.067.003cd">kiṃ punar mānuṣau dṛṣṭvā na vadhiṣyasi saṃyuge</li>
    </ul>
    <ul id="R_6.067.004">
        <li id="R_6.067.004ab">tathokto rākṣasendreṇa pratigṛhya pitur vacaḥ</li>
        <li id="R_6.067.004cd">yajñabhūmau sa vidhivat pāvakaṃ juhuve ndrajit</li>
    </ul>
    <ul id="R_6.067.005">
        <li id="R_6.067.005ab">juhvataś cāpi tatrāgniṃ raktoṣṇīṣadharāḥ striyaḥ</li>
        <li id="R_6.067.005cd">ājagmus tatra saṃbhrāntā rākṣasyo yatra rāvaṇiḥ</li>
    </ul>
    <ul id="R_6.067.006">
        <li id="R_6.067.006ab">śastrāṇi śarapatrāṇi samidho 'tha vibhītakāḥ</li>
        <li id="R_6.067.006cd">lohitāni ca vāsāṃsi sruvaṃ kārṣṇāyasaṃ tathā</li>
    </ul>
    <ul id="R_6.067.007">
        <li id="R_6.067.007ab">sarvato 'gniṃ samāstīrya śarapatraiḥ samantataḥ</li>
        <li id="R_6.067.007cd">chāgasya sarvakṛṣṇasya galaṃ jagrāha jīvataḥ</li>
    </ul>
    <ul id="R_6.067.008">
        <li id="R_6.067.008ab">caruhomasamiddhasya vidhūmasya mahārciṣaḥ</li>
        <li id="R_6.067.008cd">babhūvus tāni liṅgāni vijayaṃ darśayanti ca</li>
    </ul>
    <ul id="R_6.067.009">
        <li id="R_6.067.009ab">pradakṣiṇāvartaśikhas taptahāṭakasaṃnibhaḥ</li>
        <li id="R_6.067.009cd">havis tat pratijagrāha pāvakaḥ svayam utthitaḥ</li>
    </ul>
    <ul id="R_6.067.010">
        <li id="R_6.067.010ab">hutvāgniṃ tarpayitvātha devadānavarākṣasān</li>
        <li id="R_6.067.010cd">āruroha rathaśreṣṭham antardhānagataṃ śubham</li>
    </ul>
    <ul id="R_6.067.011">
        <li id="R_6.067.011ab">sa vājibhiś caturbhis tu bāṇaiś ca niśitair yutaḥ</li>
        <li id="R_6.067.011cd">āropitamahācāpaḥ śuśubhe syandanottame</li>
    </ul>
    <ul id="R_6.067.012">
        <li id="R_6.067.012ab">jājvalyamāno vapuṣā tapanīyaparicchadaḥ</li>
        <li id="R_6.067.012cd">śaraiś candrārdhacandraiś ca sa rathaḥ samalaṃkṛtaḥ</li>
    </ul>
    <ul id="R_6.067.013">
        <li id="R_6.067.013ab">jāmbūnadamahākambur dīptapāvakasaṃnibhaḥ</li>
        <li id="R_6.067.013cd">babhūvendrajitaḥ ketur vaidūryasamalaṃkṛtaḥ</li>
    </ul>
    <ul id="R_6.067.014">
        <li id="R_6.067.014ab">tena cādityakalpena brahmāstreṇa ca pālitaḥ</li>
        <li id="R_6.067.014cd">sa babhūva durādharṣo rāvaṇiḥ sumahābalaḥ</li>
    </ul>
    <ul id="R_6.067.015">
        <li id="R_6.067.015ab">so 'bhiniryāya nagarād indrajit samitiṃjayaḥ</li>
        <li id="R_6.067.015cd">hutvāgniṃ rākṣasair mantrair antardhānagato 'bravīt</li>
    </ul>
    <ul id="R_6.067.016">
        <li id="R_6.067.016ab">adya hatvāhave yau tau mithyā pravrajitau vane</li>
        <li id="R_6.067.016cd">jayaṃ pitre pradāsyāmi rāvaṇāya raṇādhikam</li>
    </ul>
    <ul id="R_6.067.017">
        <li id="R_6.067.017ab">kṛtvā nirvānarām urvīṃ hatvā rāmaṃ salakṣmaṇam</li>
        <li id="R_6.067.017cd">kariṣye paramāṃ prītim ity uktvāntaradhīyata</li>
    </ul>
    <ul id="R_6.067.018">
        <li id="R_6.067.018ab">āpapātātha saṃkruddho daśagrīveṇa coditaḥ</li>
        <li id="R_6.067.018cd">tīkṣṇakārmukanārācais tīkṣṇas tv indraripū raṇe</li>
    </ul>
    <ul id="R_6.067.019">
        <li id="R_6.067.019ab">sa dadarśa mahāvīryau nāgau triśirasāv iva</li>
        <li id="R_6.067.019cd">sṛjantāv iṣujālāni vīrau vānaramadhyagau</li>
    </ul>
    <ul id="R_6.067.020">
        <li id="R_6.067.020ab">imau tāv iti saṃcintya sajyaṃ kṛtvā ca kārmukam</li>
        <li id="R_6.067.020cd">saṃtatāneṣudhārābhiḥ parjanya iva vṛṣṭimān</li>
    </ul>
    <ul id="R_6.067.021">
        <li id="R_6.067.021ab">sa tu vaihāyasaṃ prāpya saratho rāmalakṣmaṇau</li>
        <li id="R_6.067.021cd">acakṣur viṣaye tiṣṭhan vivyādha niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.067.022">
        <li id="R_6.067.022ab">tau tasya śaravegena parītau rāmalakṣmaṇau</li>
        <li id="R_6.067.022cd">dhanuṣī saśare kṛtvā divyam astraṃ pracakratuḥ</li>
    </ul>
    <ul id="R_6.067.023">
        <li id="R_6.067.023ab">pracchādayantau gaganaṃ śarajālair mahābalau</li>
        <li id="R_6.067.023cd">tam astraiḥ surasaṃkāśau naiva pasparśatuḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.067.024">
        <li id="R_6.067.024ab">sa hi dhūmāndhakāraṃ ca cakre pracchādayan nabhaḥ</li>
        <li id="R_6.067.024cd">diśaś cāntardadhe śrīmān nīhāratamasāvṛtaḥ</li>
    </ul>
    <ul id="R_6.067.025">
        <li id="R_6.067.025ab">naiva jyātalanirghoṣo na ca nemikhurasvanaḥ</li>
        <li id="R_6.067.025cd">śuśruve caratas tasya na ca rūpaṃ prakāśate</li>
    </ul>
    <ul id="R_6.067.026">
        <li id="R_6.067.026ab">ghanāndhakāre timire śaravarṣam ivādbhutam</li>
        <li id="R_6.067.026cd">sa vavarṣa mahābāhur nārācaśaravṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_6.067.027">
        <li id="R_6.067.027ab">sa rāmaṃ sūryasaṃkāśaiḥ śarair dattavaro bhṛśam</li>
        <li id="R_6.067.027cd">vivyādha samare kruddhaḥ sarvagātreṣu rāvaṇiḥ</li>
    </ul>
    <ul id="R_6.067.028">
        <li id="R_6.067.028ab">tau hanyamānau nārācair dhārābhir iva parvatau</li>
        <li id="R_6.067.028cd">hemapuṅkhān naravyāghrau tigmān mumucatuḥ śarān</li>
    </ul>
    <ul id="R_6.067.029">
        <li id="R_6.067.029ab">antarikṣaṃ samāsādya rāvaṇiṃ kaṅkapatriṇaḥ</li>
        <li id="R_6.067.029cd">nikṛtya patagā bhūmau petus te śoṇitokṣitāḥ</li>
    </ul>
    <ul id="R_6.067.030">
        <li id="R_6.067.030ab">atimātraṃ śaraugheṇa pīḍyamānau narottamau</li>
        <li id="R_6.067.030cd">tān iṣūn patato bhallair anekair nicakartatuḥ</li>
    </ul>
    <ul id="R_6.067.031">
        <li id="R_6.067.031ab">yato hi dadṛśāte tau śarān nipatitāñ śitān</li>
        <li id="R_6.067.031cd">tatas tato dāśarathī sasṛjāte 'stram uttamam</li>
    </ul>
    <ul id="R_6.067.032">
        <li id="R_6.067.032ab">rāvaṇis tu diśaḥ sarvā rathenātirathaḥ patan</li>
        <li id="R_6.067.032cd">vivyādha tau dāśarathī laghv astro niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.067.033">
        <li id="R_6.067.033ab">tenātividdhau tau vīrau rukmapuṅkhaiḥ susaṃhataiḥ</li>
        <li id="R_6.067.033cd">babhūvatur dāśarathī puṣpitāv iva kiṃśukau</li>
    </ul>
    <ul id="R_6.067.034">
        <li id="R_6.067.034ab">nāsya veda gatiṃ kaś cin na ca rūpaṃ dhanuḥ śarān</li>
        <li id="R_6.067.034cd">na cānyad viditaṃ kiṃ cit sūryasyevābhrasaṃplave</li>
    </ul>
    <ul id="R_6.067.035">
        <li id="R_6.067.035ab">tena viddhāś ca harayo nihatāś ca gatāsavaḥ</li>
        <li id="R_6.067.035cd">babhūvuḥ śataśas tatra patitā dharaṇītale</li>
    </ul>
    <ul id="R_6.067.036">
        <li id="R_6.067.036ab">lakṣmaṇas tu susaṃkruddho bhrātaraṃ vākyam abravīt</li>
        <li id="R_6.067.036cd">brāhmam astraṃ prayokṣyāmi vadhārthaṃ sarvarakṣasām</li>
    </ul>
    <ul id="R_6.067.037">
        <li id="R_6.067.037ab">tam uvāca tato rāmo lakṣmaṇaṃ śubhalakṣaṇam</li>
        <li id="R_6.067.037cd">naikasya heto rakṣāṃsi pṛthivyāṃ hantum arhasi</li>
    </ul>
    <ul id="R_6.067.038">
        <li id="R_6.067.038ab">ayudhyamānaṃ pracchannaṃ prāñjaliṃ śaraṇāgatam</li>
        <li id="R_6.067.038cd">palāyantaṃ pramattaṃ vā na tvaṃ hantum ihārhasi</li>
    </ul>
    <ul id="R_6.067.039">
        <li id="R_6.067.039ab">asyaiva tu vadhe yatnaṃ kariṣyāvo mahābala</li>
        <li id="R_6.067.039cd">ādekṣyāvo mahāvegān astrān āśīviṣopamān</li>
    </ul>
    <ul id="R_6.067.040">
        <li id="R_6.067.040ab">tam enaṃ māyinaṃ kṣudram antarhitarathaṃ balāt</li>
        <li id="R_6.067.040cd">rākṣasaṃ nihaniṣyanti dṛṣṭvā vānarayūthapāḥ</li>
    </ul>
    <ul id="R_6.067.041">
        <li id="R_6.067.041ab">
            <seg type="pāda" n="a">yady eṣa bhūmiṃ viśate divaṃ vā</seg>
            <seg type="pāda" n="b">rasātalaṃ vāpi nabhastalaṃ vā</seg>
        </li>
        <li id="R_6.067.041cd">
            <seg type="pāda" n="c">evaṃ nigūḍho 'pi mamāstradagdhaḥ</seg>
            <seg type="pāda" n="d">patiṣyate bhūmitale gatāsuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.067.042">
        <li id="R_6.067.042ab">
            <seg type="pāda" n="a">ity evam uktvā vacanaṃ mahātmā</seg>
            <seg type="pāda" n="b">raghupravīraḥ plavagarṣabhair vṛtaḥ</seg>
        </li>
        <li id="R_6.067.042cd">
            <seg type="pāda" n="c">vadhāya raudrasya nṛśaṃsakarmaṇas</seg>
            <seg type="pāda" n="d">tadā mahātmā tvaritaṃ nirīkṣate</seg>
        </li>
    </ul>
    <ul id="R_6.068.001">
        <li id="R_6.068.001ab">vijñāya tu manas tasya rāghavasya mahātmanaḥ</li>
        <li id="R_6.068.001cd">saṃnivṛtyāhavāt tasmāt praviveśa puraṃ tataḥ</li>
    </ul>
    <ul id="R_6.068.002">
        <li id="R_6.068.002ab">so 'nusmṛtya vadhaṃ teṣāṃ rākṣasānāṃ tarasvinām</li>
        <li id="R_6.068.002cd">krodhatāmrekṣaṇaḥ śūro nirjagāma mahādyutiḥ</li>
    </ul>
    <ul id="R_6.068.003">
        <li id="R_6.068.003ab">sa paścimena dvāreṇa niryayau rākṣasair vṛtaḥ</li>
        <li id="R_6.068.003cd">indrajit tu mahāvīryaḥ paulastyo devakaṇṭakaḥ</li>
    </ul>
    <ul id="R_6.068.004">
        <li id="R_6.068.004ab">indrajit tu tato dṛṣṭvā bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.068.004cd">raṇāyābhyudyatau vīrau māyāṃ prāduṣkarot tadā</li>
    </ul>
    <ul id="R_6.068.005">
        <li id="R_6.068.005ab">indrajit tu rathe sthāpya sītāṃ māyāmayīṃ tadā</li>
        <li id="R_6.068.005cd">balena mahatāvṛtya tasyā vadham arocayat</li>
    </ul>
    <ul id="R_6.068.006">
        <li id="R_6.068.006ab">mohanārthaṃ tu sarveṣāṃ buddhiṃ kṛtvā sudurmatiḥ</li>
        <li id="R_6.068.006cd">hantuṃ sītāṃ vyavasito vānarābhimukho yayau</li>
    </ul>
    <ul id="R_6.068.007">
        <li id="R_6.068.007ab">taṃ dṛṣṭvā tv abhiniryāntaṃ nagaryāḥ kānanaukasaḥ</li>
        <li id="R_6.068.007cd">utpetur abhisaṃkruddhāḥ śilāhastā yuyutsavaḥ</li>
    </ul>
    <ul id="R_6.068.008">
        <li id="R_6.068.008ab">hanūmān puratas teṣāṃ jagāma kapikuñjaraḥ</li>
        <li id="R_6.068.008cd">pragṛhya sumahac chṛṅgaṃ parvatasya durāsadam</li>
    </ul>
    <ul id="R_6.068.009">
        <li id="R_6.068.009ab">sa dadarśa hatānandāṃ sītām indrajito rathe</li>
        <li id="R_6.068.009cd">ekaveṇīdharāṃ dīnām upavāsakṛśānanām</li>
    </ul>
    <ul id="R_6.068.010">
        <li id="R_6.068.010ab">parikliṣṭaikavasanām amṛjāṃ rāghavapriyām</li>
        <li id="R_6.068.010cd">rajomalābhyām āliptaiḥ sarvagātrair varastriyam</li>
    </ul>
    <ul id="R_6.068.011">
        <li id="R_6.068.011ab">tāṃ nirīkṣya muhūrtaṃ tu maithilīm adhyavasya ca</li>
        <li id="R_6.068.011cd">bāṣpaparyākulamukho hanūmān vyathito 'bhavat</li>
    </ul>
    <ul id="R_6.068.012">
        <li id="R_6.068.012ab">abravīt tāṃ tu śokārtāṃ nirānandāṃ tapasvinām</li>
        <li id="R_6.068.012cd">dṛṣṭvā rathe stitāṃ sītāṃ rākṣasendrasutāśritām</li>
    </ul>
    <ul id="R_6.068.013">
        <li id="R_6.068.013ab">kiṃ samarthitam asyeti cintayan sa mahākapiḥ</li>
        <li id="R_6.068.013cd">saha tair vānaraśreṣṭhair abhyadhāvata rāvaṇim</li>
    </ul>
    <ul id="R_6.068.014">
        <li id="R_6.068.014ab">tad vānarabalaṃ dṛṣṭvā rāvaṇiḥ krodhamūrchitaḥ</li>
        <li id="R_6.068.014cd">kṛtvā viśokaṃ nistriṃśaṃ mūrdhni sītāṃ parāmṛśat</li>
    </ul>
    <ul id="R_6.068.015">
        <li id="R_6.068.015ab">taṃ striyaṃ paśyatāṃ teṣāṃ tāḍayām āsa rāvaṇiḥ</li>
        <li id="R_6.068.015cd">krośantīṃ rāma rāmeti māyayā yojitāṃ rathe</li>
    </ul>
    <ul id="R_6.068.016">
        <li id="R_6.068.016ab">gṛhītamūrdhajāṃ dṛṣṭvā hanūmān dainyam āgataḥ</li>
        <li id="R_6.068.016cd">duḥkhajaṃ vārinetrābhyām utsṛjan mārutātmajaḥ</li>
        <li id="R_6.068.016ef">abravīt paruṣaṃ vākyaṃ krodhād rakṣo'dhipātmajam</li>
    </ul>
    <ul id="R_6.068.017">
        <li id="R_6.068.017ab">durātmann ātmanāśāya keśapakṣe parāmṛśaḥ</li>
        <li id="R_6.068.017cd">brahmarṣīṇāṃ kule jāto rākṣasīṃ yonim āśritaḥ</li>
        <li id="R_6.068.017ef">dhik tvāṃ pāpasamācāraṃ yasya te matir īdṛśī</li>
    </ul>
    <ul id="R_6.068.018">
        <li id="R_6.068.018ab">nṛśaṃsānārya durvṛtta kṣudra pāpaparākrama</li>
        <li id="R_6.068.018cd">anāryasyedṛśaṃ karma ghṛṇā te nāsti nirghṛṇa</li>
    </ul>
    <ul id="R_6.068.019">
        <li id="R_6.068.019ab">cyutā gṛhāc ca rājyāc ca rāmahastāc ca maithilī</li>
        <li id="R_6.068.019cd">kiṃ tavaiṣāparāddhā hi yad enāṃ hantum icchasi</li>
    </ul>
    <ul id="R_6.068.020">
        <li id="R_6.068.020ab">sītāṃ ca hatvā na ciraṃ jīviṣyasi kathaṃ cana</li>
        <li id="R_6.068.020cd">vadhārhakarmaṇānena mama hastagato hy asi</li>
    </ul>
    <ul id="R_6.068.021">
        <li id="R_6.068.021ab">ye ca strīghātināṃ lokā lokavadhyaiś ca kutsitāḥ</li>
        <li id="R_6.068.021cd">iha jīvitam utsṛjya pretya tān pratilapsyase</li>
    </ul>
    <ul id="R_6.068.022">
        <li id="R_6.068.022ab">iti bruvāṇo hanumān sāyudhair haribhir vṛtaḥ</li>
        <li id="R_6.068.022cd">abhyadhāvata saṃkruddho rākṣasendrasutaṃ prati</li>
    </ul>
    <ul id="R_6.068.023">
        <li id="R_6.068.023ab">āpatantaṃ mahāvīryaṃ tad anīkaṃ vanaukasām</li>
        <li id="R_6.068.023cd">rakṣasāṃ bhīmavegānām anīkena nyavārayat</li>
    </ul>
    <ul id="R_6.068.024">
        <li id="R_6.068.024ab">sa tāṃ bāṇasahasreṇa vikṣobhya harivāhinīm</li>
        <li id="R_6.068.024cd">hariśreṣṭhaṃ hanūmantam indrajit pratyuvāca ha</li>
    </ul>
    <ul id="R_6.068.025">
        <li id="R_6.068.025ab">sugrīvas tvaṃ ca rāmaś ca yannimittam ihāgatāḥ</li>
        <li id="R_6.068.025cd">tāṃ haniṣyāmi vaidehīm adyaiva tava paśyataḥ</li>
    </ul>
    <ul id="R_6.068.026">
        <li id="R_6.068.026ab">imāṃ hatvā tato rāmaṃ lakṣmaṇaṃ tvāṃ ca vānara</li>
        <li id="R_6.068.026cd">sugrīvaṃ ca vadhiṣyāmi taṃ cānāryaṃ vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.068.027">
        <li id="R_6.068.027ab">na hantavyāḥ striyaś ceti yad bravīṣi plavaṃgama</li>
        <li id="R_6.068.027cd">pīḍā karam amitrāṇāṃ yat syāt kartavyam eta tat</li>
    </ul>
    <ul id="R_6.068.028">
        <li id="R_6.068.028ab">tam evam uktvā rudatīṃ sītāṃ māyāmayīṃ tataḥ</li>
        <li id="R_6.068.028cd">śitadhāreṇa khaḍgena nijaghānendrajit svayam</li>
    </ul>
    <ul id="R_6.068.029">
        <li id="R_6.068.029ab">yajñopavītamārgeṇa chinnā tena tapasvinī</li>
        <li id="R_6.068.029cd">sā pṛthivyāṃ pṛthuśroṇī papāta priyadarśanā</li>
    </ul>
    <ul id="R_6.068.030">
        <li id="R_6.068.030ab">tām indrajitstriyaṃ hatvā hanūmantam uvāca ha</li>
        <li id="R_6.068.030cd">mayā rāmasya paśyemāṃ kopena ca niṣūditām</li>
    </ul>
    <ul id="R_6.068.031">
        <li id="R_6.068.031ab">tataḥ khaḍgena mahatā hatvā tām indrajit svayam</li>
        <li id="R_6.068.031cd">hṛṣṭaḥ sa ratham āsthāya vinanāda mahāsvanam</li>
    </ul>
    <ul id="R_6.068.032">
        <li id="R_6.068.032ab">vānarāḥ śuśruvuḥ śabdam adūre pratyavasthitāḥ</li>
        <li id="R_6.068.032cd">vyāditāsyasya nadatas tad durgaṃ saṃśritasya tu</li>
    </ul>
    <ul id="R_6.068.033">
        <li id="R_6.068.033ab">
            <seg type="pāda" n="a">tathā tu sītāṃ vinihatya durmatiḥ</seg>
            <seg type="pāda" n="b">prahṛṣṭacetāḥ sa babhūva rāvaṇiḥ</seg>
        </li>
        <li id="R_6.068.033cd">
            <seg type="pāda" n="c">taṃ hṛṣṭarūpaṃ samudīkṣya vānarā</seg>
            <seg type="pāda" n="d">viṣaṇṇarūpāḥ samabhipradudruvuḥ</seg>
        </li>
    </ul>
    <ul id="R_6.069.001">
        <li id="R_6.069.001ab">śrutvā taṃ bhīmanirhrādaṃ śakrāśanisamasvanam</li>
        <li id="R_6.069.001cd">vīkṣamāṇā diśaḥ sarvā dudruvur vānararṣabhāḥ</li>
    </ul>
    <ul id="R_6.069.002">
        <li id="R_6.069.002ab">tān uvāca tataḥ sarvān hanūmān mārutātmajaḥ</li>
        <li id="R_6.069.002cd">viṣaṇṇavadanān dīnāṃs trastān vidravataḥ pṛthak</li>
    </ul>
    <ul id="R_6.069.003">
        <li id="R_6.069.003ab">kasmād viṣaṇṇavadanā vidravadhvaṃ plavaṃgamāḥ</li>
        <li id="R_6.069.003cd">tyaktayuddhasamutsāhāḥ śūratvaṃ kva nu vo gatam</li>
    </ul>
    <ul id="R_6.069.004">
        <li id="R_6.069.004ab">pṛṣṭhato 'nuvrajadhvaṃ mām agrato yāntam āhave</li>
        <li id="R_6.069.004cd">śūrair abhijanopetair ayuktaṃ hi nivartitum</li>
    </ul>
    <ul id="R_6.069.005">
        <li id="R_6.069.005ab">evam uktāḥ susaṃkruddhā vāyuputreṇa dhīmatā</li>
        <li id="R_6.069.005cd">śailaśṛṅgān drumāṃś caiva jagṛhur hṛṣṭamānasāḥ</li>
    </ul>
    <ul id="R_6.069.006">
        <li id="R_6.069.006ab">abhipetuś ca garjanto rākṣasān vānararṣabhāḥ</li>
        <li id="R_6.069.006cd">parivārya hanūmantam anvayuś ca mahāhave</li>
    </ul>
    <ul id="R_6.069.007">
        <li id="R_6.069.007ab">sa tair vānaramukhyais tu hanūmān sarvato vṛtaḥ</li>
        <li id="R_6.069.007cd">hutāśana ivārciṣmān adahac chatruvāhinīm</li>
    </ul>
    <ul id="R_6.069.008">
        <li id="R_6.069.008ab">sa rākṣasānāṃ kadanaṃ cakāra sumahākapiḥ</li>
        <li id="R_6.069.008cd">vṛto vānarasainyena kālāntakayamopamaḥ</li>
    </ul>
    <ul id="R_6.069.009">
        <li id="R_6.069.009ab">sa tu śokena cāviṣṭaḥ krodhena ca mahākapiḥ</li>
        <li id="R_6.069.009cd">hanūmān rāvaṇi rathe mahatīṃ pātayac chilām</li>
    </ul>
    <ul id="R_6.069.010">
        <li id="R_6.069.010ab">tām āpatantīṃ dṛṣṭvaiva rathaḥ sārathinā tadā</li>
        <li id="R_6.069.010cd">vidheyāśva samāyuktaḥ sudūram apavāhitaḥ</li>
    </ul>
    <ul id="R_6.069.011">
        <li id="R_6.069.011ab">tam indrajitam aprāpya rathathaṃ sahasārathim</li>
        <li id="R_6.069.011cd">viveśa dharaṇīṃ bhittvā sā śilāvyartham udyatā</li>
    </ul>
    <ul id="R_6.069.012">
        <li id="R_6.069.012ab">patitāyāṃ śilāyāṃ tu rakṣasāṃ vyathitā camūḥ</li>
        <li id="R_6.069.012cd">tam abhyadhāvañ śataśo nadantaḥ kānanaukasaḥ</li>
    </ul>
    <ul id="R_6.069.013">
        <li id="R_6.069.013ab">te drumāṃś ca mahākāyā giriśṛṅgāṇi codyatāḥ</li>
        <li id="R_6.069.013cd">cikṣipur dviṣatāṃ madhye vānarā bhīmavikramāḥ</li>
    </ul>
    <ul id="R_6.069.014">
        <li id="R_6.069.014ab">vānarair tair mahāvīryair ghorarūpā niśācarāḥ</li>
        <li id="R_6.069.014cd">vīryād abhihatā vṛkṣair vyaveṣṭanta raṇakṣitau</li>
    </ul>
    <ul id="R_6.069.015">
        <li id="R_6.069.015ab">svasainyam abhivīkṣyātha vānarārditam indrajit</li>
        <li id="R_6.069.015cd">pragṛhītāyudhaḥ kruddhaḥ parān abhimukho yayau</li>
    </ul>
    <ul id="R_6.069.016">
        <li id="R_6.069.016ab">sa śaraughān avasṛjan svasainyenābhisaṃvṛtaḥ</li>
        <li id="R_6.069.016cd">jaghāna kapiśārdūlān subahūn dṛṣṭavikramaḥ</li>
    </ul>
    <ul id="R_6.069.017">
        <li id="R_6.069.017ab">śūlair aśanibhiḥ khaḍgaiḥ paṭṭasaiḥ kūṭamudgaraiḥ</li>
        <li id="R_6.069.017cd">te cāpy anucarāṃs tasya vānarā jaghnur āhave</li>
    </ul>
    <ul id="R_6.069.018">
        <li id="R_6.069.018ab">saskandhaviṭapaiḥ sālaiḥ śilābhiś ca mahābalaiḥ</li>
        <li id="R_6.069.018cd">hanūmān kadanaṃ cakre rakṣasāṃ bhīmakarmaṇām</li>
    </ul>
    <ul id="R_6.069.019">
        <li id="R_6.069.019ab">sa nivārya parānīkam abravīt tān vanaukasaḥ</li>
        <li id="R_6.069.019cd">hanūmān saṃnivartadhvaṃ na naḥ sādhyam idaṃ balam</li>
    </ul>
    <ul id="R_6.069.020">
        <li id="R_6.069.020ab">tyaktvā prāṇān viceṣṭanto rāma priyacikīrṣavaḥ</li>
        <li id="R_6.069.020cd">yannimittaṃ hi yudhyāmo hatā sā janakātmajā</li>
    </ul>
    <ul id="R_6.069.021">
        <li id="R_6.069.021ab">imam arthaṃ hi vijñāpya rāmaṃ sugrīvam eva ca</li>
        <li id="R_6.069.021cd">tau yat pratividhāsyete tat kariṣyāmahe vayam</li>
    </ul>
    <ul id="R_6.069.022">
        <li id="R_6.069.022ab">ity uktvā vānaraśreṣṭho vārayan sarvavānarān</li>
        <li id="R_6.069.022cd">śanaiḥ śanair asaṃtrastaḥ sabalaḥ sa nyavartata</li>
    </ul>
    <ul id="R_6.069.023">
        <li id="R_6.069.023ab">sa tu prekṣya hanūmantaṃ vrajantaṃ yatra rāghavaḥ</li>
        <li id="R_6.069.023cd">nikumbhilām adhiṣṭhāya pāvakaṃ juhuve ndrajit</li>
    </ul>
    <ul id="R_6.069.024">
        <li id="R_6.069.024ab">yajñabhūmyāṃ tu vidhivat pāvakas tena rakṣasā</li>
        <li id="R_6.069.024cd">hūyamānaḥ prajajvāla homaśoṇitabhuk tadā</li>
    </ul>
    <ul id="R_6.069.025">
        <li id="R_6.069.025ab">so 'rciḥ pinaddho dadṛśe homaśoṇitatarpitaḥ</li>
        <li id="R_6.069.025cd">saṃdhyāgata ivādityaḥ sa tīvrāgniḥ samutthitaḥ</li>
    </ul>
    <ul id="R_6.069.026">
        <li id="R_6.069.026ab">
            <seg type="pāda" n="a">athendrajid rākṣasabhūtaye tu</seg>
            <seg type="pāda" n="b">juhāva havyaṃ vidhinā vidhānavat</seg>
        </li>
        <li id="R_6.069.026cd">
            <seg type="pāda" n="c">dṛṣṭvā vyatiṣṭhanta ca rākṣasās te</seg>
            <seg type="pāda" n="d">mahāsamūheṣu nayānayajñāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.070.001">
        <li id="R_6.070.001ab">rāghavaś cāpi vipulaṃ taṃ rākṣasavanaukasām</li>
        <li id="R_6.070.001cd">śrutvā saṃgrāmanirghoṣaṃ jāmbavantam uvāca ha</li>
    </ul>
    <ul id="R_6.070.002">
        <li id="R_6.070.002ab">saumya nūnaṃ hanumatā kṛtaṃ karma suduṣkaram</li>
        <li id="R_6.070.002cd">śrūyate hi yathā bhīmaḥ sumahān āyudhasvanaḥ</li>
    </ul>
    <ul id="R_6.070.003">
        <li id="R_6.070.003ab">tad gaccha kuru sāhāyyaṃ svabalenābhisaṃvṛtaḥ</li>
        <li id="R_6.070.003cd">kṣipram ṛṣkapate tasya kapiśreṣṭhasya yudhyataḥ</li>
    </ul>
    <ul id="R_6.070.004">
        <li id="R_6.070.004ab">ṛkṣarājas tathety uktvā svenānīkena saṃvṛtaḥ</li>
        <li id="R_6.070.004cd">āgacchat paścimadvāraṃ hanūmān yatra vānaraḥ</li>
    </ul>
    <ul id="R_6.070.005">
        <li id="R_6.070.005ab">athāyāntaṃ hanūmantaṃ dadarśarkṣapatiḥ pathi</li>
        <li id="R_6.070.005cd">vānaraiḥ kṛtasaṃgrāmaiḥ śvasadbhir abhisaṃvṛtam</li>
    </ul>
    <ul id="R_6.070.006">
        <li id="R_6.070.006ab">dṛṣṭvā pathi hanūmāṃś ca tad ṛṣkabalam udyatam</li>
        <li id="R_6.070.006cd">nīlameghanibhaṃ bhīmaṃ saṃnivārya nyavartata</li>
    </ul>
    <ul id="R_6.070.007">
        <li id="R_6.070.007ab">sa tena harisainyena saṃnikarṣaṃ mahāyaśāḥ</li>
        <li id="R_6.070.007cd">śīghram āgamya rāmāya duḥkhito vākyam abravīt</li>
    </ul>
    <ul id="R_6.070.008">
        <li id="R_6.070.008ab">samare yudhyamānānām asmākaṃ prekṣatāṃ ca saḥ</li>
        <li id="R_6.070.008cd">jaghāna rudatīṃ sītām indrajid rāvaṇātmajaḥ</li>
    </ul>
    <ul id="R_6.070.009">
        <li id="R_6.070.009ab">udbhrāntacittas tāṃ dṛṣṭvā viṣaṇṇo 'ham ariṃdama</li>
        <li id="R_6.070.009cd">tad ahaṃ bhavato vṛttaṃ vijñāpayitum āgataḥ</li>
    </ul>
    <ul id="R_6.070.010">
        <li id="R_6.070.010ab">tasya tad vacanaṃ śrutvā rāghavaḥ śokamūrchitaḥ</li>
        <li id="R_6.070.010cd">nipapāta tadā bhūmau chinnamūla iva drumaḥ</li>
    </ul>
    <ul id="R_6.070.011">
        <li id="R_6.070.011ab">taṃ bhūmau devasaṃkāśaṃ patitaṃ dṛśya rāghavam</li>
        <li id="R_6.070.011cd">abhipetuḥ samutpatya sarvataḥ kapisattamāḥ</li>
    </ul>
    <ul id="R_6.070.012">
        <li id="R_6.070.012ab">asiñcan salilaiś cainaṃ padmotpalasugandhibhiḥ</li>
        <li id="R_6.070.012cd">pradahantam asahyaṃ ca sahasāgnim ivotthitam</li>
    </ul>
    <ul id="R_6.070.013">
        <li id="R_6.070.013ab">taṃ lakṣmaṇo 'tha bāhubhyāṃ pariṣvajya suduḥkhitaḥ</li>
        <li id="R_6.070.013cd">uvāca rāmam asvasthaṃ vākyaṃ hetvarthasaṃhitam</li>
    </ul>
    <ul id="R_6.070.014">
        <li id="R_6.070.014ab">śubhe vartmani tiṣṭhantaṃ tvām āryavijitendriyam</li>
        <li id="R_6.070.014cd">anarthebhyo na śaknoti trātuṃ dharmo nirarthakaḥ</li>
    </ul>
    <ul id="R_6.070.015">
        <li id="R_6.070.015ab">bhūtānāṃ sthāvarāṇāṃ ca jaṅgamānāṃ ca darśanam</li>
        <li id="R_6.070.015cd">yathāsti na tathā dharmas tena nāstīti me matiḥ</li>
    </ul>
    <ul id="R_6.070.016">
        <li id="R_6.070.016ab">yathaiva sthāvaraṃ vyaktaṃ jaṅgamaṃ ca tathāvidham</li>
        <li id="R_6.070.016cd">nāyam arthas tathā yuktas tvadvidho na vipadyate</li>
    </ul>
    <ul id="R_6.070.017">
        <li id="R_6.070.017ab">yady adharmo bhaved bhūto rāvaṇo narakaṃ vrajet</li>
        <li id="R_6.070.017cd">bhavāṃś ca dharmasaṃyukto naivaṃ vyasanam āpnuyāt</li>
    </ul>
    <ul id="R_6.070.018">
        <li id="R_6.070.018ab">tasya ca vyasanābhāvād vyasanaṃ ca gate tvayi</li>
        <li id="R_6.070.018cd">dharmeṇopalabhed dharmam adharmaṃ cāpy adharmataḥ</li>
    </ul>
    <ul id="R_6.070.019">
        <li id="R_6.070.019ab">yadi dharmeṇa yujyeran nādharmarucayo janāḥ</li>
        <li id="R_6.070.019cd">dharmeṇa caratāṃ dharmas tathā caiṣāṃ phalaṃ bhavet</li>
    </ul>
    <ul id="R_6.070.020">
        <li id="R_6.070.020ab">yasmād arthā vivardhante yeṣv adharmaḥ pratiṣṭhitaḥ</li>
        <li id="R_6.070.020cd">kliśyante dharmaśīlāś ca tasmād etau nirarthakau</li>
    </ul>
    <ul id="R_6.070.021">
        <li id="R_6.070.021ab">vadhyante pāpakarmāṇo yady adharmeṇa rāghava</li>
        <li id="R_6.070.021cd">vadhakarmahato dharmaḥ sa hataḥ kaṃ vadhiṣyati</li>
    </ul>
    <ul id="R_6.070.022">
        <li id="R_6.070.022ab">atha vā vihitenāyaṃ hanyate hanti vā param</li>
        <li id="R_6.070.022cd">vidhir ālipyate tena na sa pāpena karmaṇā</li>
    </ul>
    <ul id="R_6.070.023">
        <li id="R_6.070.023ab">adṛṣṭapratikāreṇa avyaktenāsatā satā</li>
        <li id="R_6.070.023cd">kathaṃ śakyaṃ paraṃ prāptuṃ dharmeṇārivikarśana</li>
    </ul>
    <ul id="R_6.070.024">
        <li id="R_6.070.024ab">yadi sat syāt satāṃ mukhya nāsat syāt tava kiṃ cana</li>
        <li id="R_6.070.024cd">tvayā yadīdṛśaṃ prāptaṃ tasmāt san nopapadyate</li>
    </ul>
    <ul id="R_6.070.025">
        <li id="R_6.070.025ab">atha vā durbalaḥ klībo balaṃ dharmo 'nuvartate</li>
        <li id="R_6.070.025cd">durbalo hṛtamaryādo na sevya iti me matiḥ</li>
    </ul>
    <ul id="R_6.070.026">
        <li id="R_6.070.026ab">balasya yadi ced dharmo guṇabhūtaḥ parākrame</li>
        <li id="R_6.070.026cd">dharmam utsṛjya vartasva yathā dharme tathā bale</li>
    </ul>
    <ul id="R_6.070.027">
        <li id="R_6.070.027ab">atha cet satyavacanaṃ dharmaḥ kila paraṃtapa</li>
        <li id="R_6.070.027cd">anṛtas tvayy akaruṇaḥ kiṃ na baddhas tvayā pitā</li>
    </ul>
    <ul id="R_6.070.028">
        <li id="R_6.070.028ab">yadi dharmo bhaved bhūta adharmo vā paraṃtapa</li>
        <li id="R_6.070.028cd">na sma hatvā muniṃ vajrī kuryād ijyāṃ śatakratuḥ</li>
    </ul>
    <ul id="R_6.070.029">
        <li id="R_6.070.029ab">adharmasaṃśrito dharmo vināśayati rāghava</li>
        <li id="R_6.070.029cd">sarvam etad yathākāmaṃ kākutstha kurute naraḥ</li>
    </ul>
    <ul id="R_6.070.030">
        <li id="R_6.070.030ab">mama cedaṃ mataṃ tāta dharmo 'yam iti rāghava</li>
        <li id="R_6.070.030cd">dharmamūlaṃ tvayā chinnaṃ rājyam utsṛjatā tadā</li>
    </ul>
    <ul id="R_6.070.031">
        <li id="R_6.070.031ab">arthebhyo hi vivṛddhebhyaḥ saṃvṛddhebhyas tatas tataḥ</li>
        <li id="R_6.070.031cd">kriyāḥ sarvāḥ pravartante parvatebhya ivāpagāḥ</li>
    </ul>
    <ul id="R_6.070.032">
        <li id="R_6.070.032ab">arthena hi viyuktasya puruṣasyālpatejasaḥ</li>
        <li id="R_6.070.032cd">vyucchidyante kriyāḥ sarvā grīṣme kusarito yathā</li>
    </ul>
    <ul id="R_6.070.033">
        <li id="R_6.070.033ab">so 'yam arthaṃ parityajya sukhakāmaḥ sukhaidhitaḥ</li>
        <li id="R_6.070.033cd">pāpam ārabhate kartuṃ tathā doṣaḥ pravartate</li>
    </ul>
    <ul id="R_6.070.034">
        <li id="R_6.070.034ab">yasyārthās tasya mitrāṇi yasyārthās tasya bāndhavaḥ</li>
        <li id="R_6.070.034cd">yasyārthāḥ sa pumāṃl loke yasyārthāḥ sa ca paṇḍitaḥ</li>
    </ul>
    <ul id="R_6.070.035">
        <li id="R_6.070.035ab">yasyārthāḥ sa ca vikrānto yasyārthāḥ sa ca buddhimān</li>
        <li id="R_6.070.035cd">yasyārthāḥ sa mahābhāgo yasyārthāḥ sa mahāguṇaḥ</li>
    </ul>
    <ul id="R_6.070.036">
        <li id="R_6.070.036ab">arthasyaite parityāge doṣāḥ pravyāhṛtā mayā</li>
        <li id="R_6.070.036cd">rājyam utsṛjatā vīra yena buddhis tvayā kṛtā</li>
    </ul>
    <ul id="R_6.070.037">
        <li id="R_6.070.037ab">yasyārthā dharmakāmārthās tasya sarvaṃ pradakṣiṇam</li>
        <li id="R_6.070.037cd">adhanenārthakāmena nārthaḥ śakyo vicinvatā</li>
    </ul>
    <ul id="R_6.070.038">
        <li id="R_6.070.038ab">harṣaḥ kāmaś ca darpaś ca dharmaḥ krodhaḥ śamo damaḥ</li>
        <li id="R_6.070.038cd">arthād etāni sarvāṇi pravartante narādhipa</li>
    </ul>
    <ul id="R_6.070.039">
        <li id="R_6.070.039ab">yeṣāṃ naśyaty ayaṃ lokaś caratāṃ dharmacāriṇām</li>
        <li id="R_6.070.039cd">te 'rthās tvayi na dṛśyante durdineṣu yathā grahāḥ</li>
    </ul>
    <ul id="R_6.070.040">
        <li id="R_6.070.040ab">tvayi pravrajite vīra guroś ca vacane sthite</li>
        <li id="R_6.070.040cd">rakṣasāpahṛtā bhāryā prāṇaiḥ priyatarā tava</li>
    </ul>
    <ul id="R_6.070.041">
        <li id="R_6.070.041ab">tad adya vipulaṃ vīra duḥkham indrajitā kṛtam</li>
        <li id="R_6.070.041cd">karmaṇā vyapaneṣyāmi tasmād uttiṣṭha rāghava</li>
    </ul>
    <ul id="R_6.070.042">
        <li id="R_6.070.042ab">
            <seg type="pāda" n="a">ayam anagha tavoditaḥ priyārthaṃ</seg>
            <seg type="pāda" n="b">janakasutā nidhanaṃ nirīkṣya ruṣṭaḥ</seg>
        </li>
        <li id="R_6.070.042cd">
            <seg type="pāda" n="c">sahayagajarathāṃ sarākṣasendrāṃ</seg>
            <seg type="pāda" n="d">bhṛśam iṣubhir vinipātayāmi laṅkām</seg>
        </li>
    </ul>
    <ul id="R_6.071.001">
        <li id="R_6.071.001ab">rāmam āśvāsayāne tu lakṣmaṇe bhrātṛvatsale</li>
        <li id="R_6.071.001cd">nikṣipya gulmān svasthāne tatrāgacchad vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.071.002">
        <li id="R_6.071.002ab">nānāpraharaṇair vīraiś caturbhiḥ sacivair vṛtaḥ</li>
        <li id="R_6.071.002cd">nīlāñjanacayākārair mātaṃgair iva yūthapaḥ</li>
    </ul>
    <ul id="R_6.071.003">
        <li id="R_6.071.003ab">so 'bhigamya mahātmānaṃ rāghavaṃ śokalālasaṃ</li>
        <li id="R_6.071.003cd">vānarāṃś caiva dadṛśe bāṣpaparyākulekṣaṇān</li>
    </ul>
    <ul id="R_6.071.004">
        <li id="R_6.071.004ab">rāghavaṃ ca mahātmānam ikṣvākukulanandanam</li>
        <li id="R_6.071.004cd">dadarśa moham āpannaṃ lakṣmaṇasyāṅkam āśritam</li>
    </ul>
    <ul id="R_6.071.005">
        <li id="R_6.071.005ab">vrīḍitaṃ śokasaṃtaptaṃ dṛṣṭvā rāmaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.071.005cd">antarduḥkhena dīnātmā kim etad iti so 'bravīt</li>
    </ul>
    <ul id="R_6.071.006">
        <li id="R_6.071.006ab">vibhīṣaṇa mukhaṃ dṛṣṭvā sugrīvaṃ tāṃś ca vānarān</li>
        <li id="R_6.071.006cd">uvāca lakṣmaṇo vākyam idaṃ bāṣpapariplutaḥ</li>
    </ul>
    <ul id="R_6.071.007">
        <li id="R_6.071.007ab">hatām indrajitā sītām iha śrutvaiva rāghavaḥ</li>
        <li id="R_6.071.007cd">hanūmad vacanāt saumya tato moham upāgataḥ</li>
    </ul>
    <ul id="R_6.071.008">
        <li id="R_6.071.008ab">kathayantaṃ tu saumitriṃ saṃnivārya vibhīṣaṇaḥ</li>
        <li id="R_6.071.008cd">puṣkalārtham idaṃ vākyaṃ visaṃjñaṃ rāmam abravīt</li>
    </ul>
    <ul id="R_6.071.009">
        <li id="R_6.071.009ab">manujendrārtarūpeṇa yad uktas tvaṃ hanūmatā</li>
        <li id="R_6.071.009cd">tad ayuktam ahaṃ manye sāgarasyeva śoṣaṇam</li>
    </ul>
    <ul id="R_6.071.010">
        <li id="R_6.071.010ab">abhiprāyaṃ tu jānāmi rāvaṇasya durātmanaḥ</li>
        <li id="R_6.071.010cd">sītāṃ prati mahābāho na ca ghātaṃ kariṣyati</li>
    </ul>
    <ul id="R_6.071.011">
        <li id="R_6.071.011ab">yācyamānaḥ subahuśo mayā hitacikīrṣuṇā</li>
        <li id="R_6.071.011cd">vaidehīm utsṛjasveti na ca tat kṛtavān vacaḥ</li>
    </ul>
    <ul id="R_6.071.012">
        <li id="R_6.071.012ab">naiva sāmnā na bhedena na dānena kuto yudhā</li>
        <li id="R_6.071.012cd">sā draṣṭum api śakyeta naiva cānyena kena cit</li>
    </ul>
    <ul id="R_6.071.013">
        <li id="R_6.071.013ab">vānarān mohayitvā tu pratiyātaḥ sa rākṣasaḥ</li>
        <li id="R_6.071.013cd">caityaṃ nikumbhilāṃ nāma yatra homaṃ kariṣyati</li>
    </ul>
    <ul id="R_6.071.014">
        <li id="R_6.071.014ab">hutavān upayāto hi devair api savāsavaiḥ</li>
        <li id="R_6.071.014cd">durādharṣo bhavaty eṣa saṃgrāme rāvaṇātmajaḥ</li>
    </ul>
    <ul id="R_6.071.015">
        <li id="R_6.071.015ab">tena mohayatā nūnam eṣā māyā prayojitā</li>
        <li id="R_6.071.015cd">vighnam anvicchatā tāta vānarāṇāṃ parākrame</li>
        <li id="R_6.071.015ef">sasainyās tatra gacchāmo yāvat tan na samāpyate</li>
    </ul>
    <ul id="R_6.071.016">
        <li id="R_6.071.016ab">tyajemaṃ naraśārdūlamithyā saṃtāpam āgatam</li>
        <li id="R_6.071.016cd">sīdate hi balaṃ sarvaṃ dṛṣṭvā tvāṃ śokakarśitam</li>
    </ul>
    <ul id="R_6.071.017">
        <li id="R_6.071.017ab">iha tvaṃ svastha hṛdayas tiṣṭha sattvasamucchritaḥ</li>
        <li id="R_6.071.017cd">lakṣmaṇaṃ preṣayāsmābhiḥ saha sainyānukarṣibhiḥ</li>
    </ul>
    <ul id="R_6.071.018">
        <li id="R_6.071.018ab">eṣa taṃ naraśārdūlo rāvaṇiṃ niśitaiḥ śaraiḥ</li>
        <li id="R_6.071.018cd">tyājayiṣyati tat karma tato vadhyo bhaviṣyati</li>
    </ul>
    <ul id="R_6.071.019">
        <li id="R_6.071.019ab">tasyaite niśitās tīkṣṇāḥ patripatrāṅgavājinaḥ</li>
        <li id="R_6.071.019cd">patatriṇa ivāsaumyāḥ śarāḥ pāsyanti śoṇitam</li>
    </ul>
    <ul id="R_6.071.020">
        <li id="R_6.071.020ab">tat saṃdiśa mahābāho lakṣmaṇaṃ śubhalakṣaṇam</li>
        <li id="R_6.071.020cd">rākṣasasya vināśāya vajraṃ vajradharo yathā</li>
    </ul>
    <ul id="R_6.071.021">
        <li id="R_6.071.021ab">
            <seg type="pāda" n="a">manujavara na kālaviprakarṣo</seg>
            <seg type="pāda" n="b">ripunidhanaṃ prati yat kṣamo 'dya kartum</seg>
        </li>
        <li id="R_6.071.021cd">
            <seg type="pāda" n="c">tvam atisṛja ripor vadhāya bāṇīm</seg>
            <seg type="pāda" n="d">asurapuronmathane yathā mahendraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.071.022">
        <li id="R_6.071.022ab">
            <seg type="pāda" n="a">samāptakarmā hi sa rākṣasendro</seg>
            <seg type="pāda" n="b">bhavaty adṛśyaḥ samare surāsuraiḥ</seg>
        </li>
        <li id="R_6.071.022cd">
            <seg type="pāda" n="c">yuyutsatā tena samāptakarmaṇā</seg>
            <seg type="pāda" n="d">bhavet surāṇām api saṃśayo mahān</seg>
        </li>
    </ul>
    <ul id="R_6.072.001">
        <li id="R_6.072.001ab">tasya tad vacanaṃ śrutvā rāghavaḥ śokakarśitaḥ</li>
        <li id="R_6.072.001cd">nopadhārayate vyaktaṃ yad uktaṃ tena rakṣasā</li>
    </ul>
    <ul id="R_6.072.002">
        <li id="R_6.072.002ab">tato dhairyam avaṣṭabhya rāmaḥ parapuraṃjayaḥ</li>
        <li id="R_6.072.002cd">vibhīṣaṇam upāsīnam uvāca kapisaṃnidhau</li>
    </ul>
    <ul id="R_6.072.003">
        <li id="R_6.072.003ab">nairṛtādhipate vākyaṃ yad uktaṃ te vibhīṣaṇa</li>
        <li id="R_6.072.003cd">bhūyas tac chrotum icchāmi brūhi yat te vivakṣitam</li>
    </ul>
    <ul id="R_6.072.004">
        <li id="R_6.072.004ab">rāghavasya vacaḥ śrutvā vākyaṃ vākyaviśāradaḥ</li>
        <li id="R_6.072.004cd">yat tat punar idaṃ vākyaṃ babhāṣe sa vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.072.005">
        <li id="R_6.072.005ab">yathājñaptaṃ mahābāho tvayā gulmaniveśanam</li>
        <li id="R_6.072.005cd">tat tathānuṣṭhitaṃ vīra tvadvākyasamanantaram</li>
    </ul>
    <ul id="R_6.072.006">
        <li id="R_6.072.006ab">tāny anīkāni sarvāṇi vibhaktāni samantataḥ</li>
        <li id="R_6.072.006cd">vinyastā yūthapāś caiva yathānyāyaṃ vibhāgaśaḥ</li>
    </ul>
    <ul id="R_6.072.007">
        <li id="R_6.072.007ab">bhūyas tu mama vijāpyaṃ tac chṛṇuṣva mahāyaśaḥ</li>
        <li id="R_6.072.007cd">tvayy akāraṇasaṃtapte saṃtaptahṛdayā vayam</li>
    </ul>
    <ul id="R_6.072.008">
        <li id="R_6.072.008ab">tyaja rājann imaṃ śokaṃ mithyā saṃtāpam āgatam</li>
        <li id="R_6.072.008cd">tad iyaṃ tyajyatāṃ cintā śatruharṣavivardhanī</li>
    </ul>
    <ul id="R_6.072.009">
        <li id="R_6.072.009ab">udyamaḥ kriyatāṃ vīra harṣaḥ samupasevyatām</li>
        <li id="R_6.072.009cd">prāptavyā yadi te sītā hantavyaś ca niśācarāḥ</li>
    </ul>
    <ul id="R_6.072.010">
        <li id="R_6.072.010ab">raghunandana vakṣyāmi śrūyatāṃ me hitaṃ vacaḥ</li>
        <li id="R_6.072.010cd">sādhv ayaṃ yātu saumitrir balena mahatā vṛtaḥ</li>
        <li id="R_6.072.010ef">nikumbhilāyāṃ saṃprāpya hantuṃ rāvaṇim āhave</li>
    </ul>
    <ul id="R_6.072.011">
        <li id="R_6.072.011ab">dhanurmaṇḍalanirmuktair āśīviṣaviṣopamaiḥ</li>
        <li id="R_6.072.011cd">śarair hantuṃ maheṣvāso rāvaṇiṃ samitiṃjayaḥ</li>
    </ul>
    <ul id="R_6.072.012">
        <li id="R_6.072.012ab">tena vīreṇa tapasā varadānāt svayambhutaḥ</li>
        <li id="R_6.072.012cd">astraṃ brahmaśiraḥ prāptaṃ kāmagāś ca turaṃgamāḥ</li>
    </ul>
    <ul id="R_6.072.013">
        <li id="R_6.072.013ab">nikumbhilām asaṃprāptam ahutāgniṃ ca yo ripuḥ</li>
        <li id="R_6.072.013cd">tvām ātatāyinaṃ hanyād indraśatro sa te vadhaḥ</li>
        <li id="R_6.072.013ef">ity evaṃ vihito rājan vadhas tasyaiva dhīmataḥ</li>
    </ul>
    <ul id="R_6.072.014">
        <li id="R_6.072.014ab">vadhāyendrajito rāma taṃ diśasva mahābalam</li>
        <li id="R_6.072.014cd">hate tasmin hataṃ viddhi rāvaṇaṃ sasuhṛjjanam</li>
    </ul>
    <ul id="R_6.072.015">
        <li id="R_6.072.015ab">vibhīṣaṇavacaḥ śrutva rāmo vākyam athābravīt</li>
        <li id="R_6.072.015cd">jānāmi tasya raudrasya māyāṃ satyaparākrama</li>
    </ul>
    <ul id="R_6.072.016">
        <li id="R_6.072.016ab">sa hi brahmāstravit prājño mahāmāyo mahābalaḥ</li>
        <li id="R_6.072.016cd">karoty asaṃjñān saṃgrāme devān savaruṇān api</li>
    </ul>
    <ul id="R_6.072.017">
        <li id="R_6.072.017ab">tasyāntarikṣe carato rathasthasya mahāyaśaḥ</li>
        <li id="R_6.072.017cd">na gatir jñāyate vīrasūryasyevābhrasaṃplave</li>
    </ul>
    <ul id="R_6.072.018">
        <li id="R_6.072.018ab">rāghavas tu ripor jñātvā māyāvīryaṃ durātmanaḥ</li>
        <li id="R_6.072.018cd">lakṣmaṇaṃ kīrtisaṃpannam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.072.019">
        <li id="R_6.072.019ab">yad vānarendrasya balaṃ tena sarveṇa saṃvṛtaḥ</li>
        <li id="R_6.072.019cd">hanūmatpramukhaiś caiva yūthapaiḥ sahalakṣmaṇa</li>
    </ul>
    <ul id="R_6.072.020">
        <li id="R_6.072.020ab">jāmbavenarkṣapatinā saha sainyena saṃvṛtaḥ</li>
        <li id="R_6.072.020cd">jahi taṃ rākṣasasutaṃ māyābalaviśāradam</li>
    </ul>
    <ul id="R_6.072.021">
        <li id="R_6.072.021ab">ayaṃ tvāṃ sacivaiḥ sārdhaṃ mahātmā rajanīcaraḥ</li>
        <li id="R_6.072.021cd">abhijñas tasya deśasya pṛṣṭhato 'nugamiṣyati</li>
    </ul>
    <ul id="R_6.072.022">
        <li id="R_6.072.022ab">rāghavasya vacaḥ śrutvā lakṣmaṇaḥ savibhīṣaṇaḥ</li>
        <li id="R_6.072.022cd">jagrāha kārmukaṃ śreṣṭham anyad bhīmaparākramaḥ</li>
    </ul>
    <ul id="R_6.072.023">
        <li id="R_6.072.023ab">saṃnaddhaḥ kavacī khaḍgī sa śarī hemacāpadhṛk</li>
        <li id="R_6.072.023cd">rāmapādāv upaspṛśya hṛṣṭaḥ saumitrir abravīt</li>
    </ul>
    <ul id="R_6.072.024">
        <li id="R_6.072.024ab">adya matkārmukonmukhāḥ śarā nirbhidya rāvaṇim</li>
        <li id="R_6.072.024cd">laṅkām abhipatiṣyanti haṃsāḥ puṣkariṇīm iva</li>
    </ul>
    <ul id="R_6.072.025">
        <li id="R_6.072.025ab">adyaiva tasya raudrasya śarīraṃ māmakāḥ śarāḥ</li>
        <li id="R_6.072.025cd">vidhamiṣyanti hatvā taṃ mahācāpaguṇacyutāḥ</li>
    </ul>
    <ul id="R_6.072.026">
        <li id="R_6.072.026ab">sa evam uktvā dyutimān vacanaṃ bhrātur agrataḥ</li>
        <li id="R_6.072.026cd">sa rāvaṇivadhākāṅkṣī lakṣmaṇas tvarito yayau</li>
    </ul>
    <ul id="R_6.072.027">
        <li id="R_6.072.027ab">so 'bhivādya guroḥ pādau kṛtvā cāpi pradakṣiṇam</li>
        <li id="R_6.072.027cd">nikumbhilām abhiyayau caityaṃ rāvaṇipālitam</li>
    </ul>
    <ul id="R_6.072.028">
        <li id="R_6.072.028ab">vibhīṣaṇena sahito rājaputraḥ pratāpavān</li>
        <li id="R_6.072.028cd">kṛtasvastyayano bhrātrā lakṣmaṇas tvarito yayau</li>
    </ul>
    <ul id="R_6.072.029">
        <li id="R_6.072.029ab">vānarāṇāṃ sahasrais tu hanūmān bahubhir vṛtaḥ</li>
        <li id="R_6.072.029cd">vibhīṣaṇaḥ sahāmātyas tadā lakṣmaṇam anvagāt</li>
    </ul>
    <ul id="R_6.072.030">
        <li id="R_6.072.030ab">mahatā harisainyena savegam abhisaṃvṛtaḥ</li>
        <li id="R_6.072.030cd">ṛkṣarājabalaṃ caiva dadarśa pathi viṣṭhitam</li>
    </ul>
    <ul id="R_6.072.031">
        <li id="R_6.072.031ab">sa gatvā dūram adhvānaṃ saumitrir mitranandanaḥ</li>
        <li id="R_6.072.031cd">rākṣasendrabalaṃ dūrād apaśyad vyūham āsthitam</li>
    </ul>
    <ul id="R_6.072.032">
        <li id="R_6.072.032ab">sa saṃprāpya dhanuṣpāṇir māyāyogam ariṃdama</li>
        <li id="R_6.072.032cd">tasthau brahmavidhānena vijetuṃ raghunandanaḥ</li>
    </ul>
    <ul id="R_6.072.033">
        <li id="R_6.072.033ab">
            <seg type="pāda" n="a">vividham amalaśastrabhāsvaraṃ tad</seg>
            <seg type="pāda" n="b">dhvajagahanaṃ vipulaṃ mahārathaiś ca</seg>
        </li>
        <li id="R_6.072.033cd">
            <seg type="pāda" n="c">pratibhayatamam aprameyavegaṃ</seg>
            <seg type="pāda" n="d">timiram iva dviṣatāṃ balaṃ viveśa</seg>
        </li>
    </ul>
    <ul id="R_6.073.001">
        <li id="R_6.073.001ab">atha tasyām avasthāyāṃ lakṣmaṇaṃ rāvaṇānujaḥ</li>
        <li id="R_6.073.001cd">pareṣām ahitaṃ vākyam arthasādhakam abravīt</li>
    </ul>
    <ul id="R_6.073.002">
        <li id="R_6.073.002ab">asyānīkasya mahato bhedane yatalakṣmaṇa</li>
        <li id="R_6.073.002cd">rākṣasendrasuto 'py atra bhinne dṛśyo bhaviṣyati</li>
    </ul>
    <ul id="R_6.073.003">
        <li id="R_6.073.003ab">sa tvam indrāśaniprakhyaiḥ śarair avakiran parān</li>
        <li id="R_6.073.003cd">abhidravāśu yāvad vai naitat karma samāpyate</li>
    </ul>
    <ul id="R_6.073.004">
        <li id="R_6.073.004ab">jahi vīradurātmānaṃ māyāparam adhārmikam</li>
        <li id="R_6.073.004cd">rāvaṇiṃ krūrakarmāṇaṃ sarvalokabhayāvaham</li>
    </ul>
    <ul id="R_6.073.005">
        <li id="R_6.073.005ab">vibhīṣaṇavacaḥ śrutvā lakṣmaṇaḥ śubhalakṣaṇaḥ</li>
        <li id="R_6.073.005cd">vavarṣa śaravarṣāṇi rākṣasendrasutaṃ prati</li>
    </ul>
    <ul id="R_6.073.006">
        <li id="R_6.073.006ab">ṛkṣāḥ śākhāmṛgāś caiva drumādrivarayodhinaḥ</li>
        <li id="R_6.073.006cd">abhyadhāvanta sahitās tad anīkam avasthitam</li>
    </ul>
    <ul id="R_6.073.007">
        <li id="R_6.073.007ab">rākṣasāś ca śitair bāṇair asibhiḥ śaktitomaraiḥ</li>
        <li id="R_6.073.007cd">udyataiḥ samavartanta kapisainyajighāṃsavaḥ</li>
    </ul>
    <ul id="R_6.073.008">
        <li id="R_6.073.008ab">sa saṃprahāras tumulaḥ saṃjajñe kapirakṣasām</li>
        <li id="R_6.073.008cd">śabdena mahatā laṅkāṃ nādayan vai samantataḥ</li>
    </ul>
    <ul id="R_6.073.009">
        <li id="R_6.073.009ab">śastrair bahuvidhākāraiḥ śitair bāṇaiś ca pādapaiḥ</li>
        <li id="R_6.073.009cd">udyatair giriśṛṅgaiś ca ghorair ākāśam āvṛtam</li>
    </ul>
    <ul id="R_6.073.010">
        <li id="R_6.073.010ab">te rākṣasā vānareṣu vikṛtānanabāhavaḥ</li>
        <li id="R_6.073.010cd">niveśayantaḥ śastrāṇi cakrus te sumahad bhayam</li>
    </ul>
    <ul id="R_6.073.011">
        <li id="R_6.073.011ab">tathaiva sakalair vṛkṣair giriśṛṅgaiś ca vānarāḥ</li>
        <li id="R_6.073.011cd">abhijaghnur nijaghnuś ca samare rākṣasarṣabhān</li>
    </ul>
    <ul id="R_6.073.012">
        <li id="R_6.073.012ab">ṛkṣavānaramukhyaiś ca mahākāyair mahābalaiḥ</li>
        <li id="R_6.073.012cd">rakṣasāṃ vadhyamānānāṃ mahad bhayam ajāyata</li>
    </ul>
    <ul id="R_6.073.013">
        <li id="R_6.073.013ab">svam anīkaṃ viṣaṇṇaṃ tu śrutvā śatrubhir arditam</li>
        <li id="R_6.073.013cd">udatiṣṭhata durdharṣas tat karmaṇy ananuṣṭhite</li>
    </ul>
    <ul id="R_6.073.014">
        <li id="R_6.073.014ab">vṛkṣāndhakārān niṣkramya jātakrodhaḥ sa rāvaṇiḥ</li>
        <li id="R_6.073.014cd">āruroha rathaṃ sajjaṃ pūrvayuktaṃ sa rākṣasaḥ</li>
    </ul>
    <ul id="R_6.073.015">
        <li id="R_6.073.015ab">sa bhīmakārmukaśaraḥ kṛṣṇāñjanacayopamaḥ</li>
        <li id="R_6.073.015cd">raktāsyanayanaḥ krūro babhau mṛtyur ivāntakaḥ</li>
    </ul>
    <ul id="R_6.073.016">
        <li id="R_6.073.016ab">dṛṣṭvaiva tu rathasthaṃ taṃ paryavartata tad balam</li>
        <li id="R_6.073.016cd">rakṣasāṃ bhīmavegānāṃ lakṣmaṇena yuyutsatām</li>
    </ul>
    <ul id="R_6.073.017">
        <li id="R_6.073.017ab">tasmin kāle tu hanumān udyamya sudurāsadam</li>
        <li id="R_6.073.017cd">dharaṇīdharasaṃkāśī mahāvṛkṣam ariṃdamaḥ</li>
    </ul>
    <ul id="R_6.073.018">
        <li id="R_6.073.018ab">sa rākṣasānāṃ tat sainyaṃ kālāgnir iva nirdahan</li>
        <li id="R_6.073.018cd">cakāra bahubhir vṛkṣair niḥsaṃjñaṃ yudhi vānaraḥ</li>
    </ul>
    <ul id="R_6.073.019">
        <li id="R_6.073.019ab">vidhvaṃsayantaṃ tarasā dṛṣṭvaiva pavanātmajam</li>
        <li id="R_6.073.019cd">rākṣasānāṃ sahasrāṇi hanūmantam avākiran</li>
    </ul>
    <ul id="R_6.073.020">
        <li id="R_6.073.020ab">śitaśūladharāḥ śūlair asibhiś cāsipāṇayaḥ</li>
        <li id="R_6.073.020cd">śaktibhiḥ śaktihastāś ca paṭṭasaiḥ paṭṭasāyudhāḥ</li>
    </ul>
    <ul id="R_6.073.021">
        <li id="R_6.073.021ab">parighaiś ca gadābhiś ca kuntaiś ca śubhadarśanaiḥ</li>
        <li id="R_6.073.021cd">śataśaś ca śataghnībhir āyasair api mudgaraiḥ</li>
    </ul>
    <ul id="R_6.073.022">
        <li id="R_6.073.022ab">ghoraiḥ paraśubhiś caiva bhiṇḍipālaiś ca rākṣasāḥ</li>
        <li id="R_6.073.022cd">muṣṭibhir vajravegaiś ca talair aśanisaṃnibhaiḥ</li>
    </ul>
    <ul id="R_6.073.023">
        <li id="R_6.073.023ab">abhijaghnuḥ samāsādya samantāt parvatopamam</li>
        <li id="R_6.073.023cd">teṣām api ca saṃkruddhaś cakāra kadanaṃ mahat</li>
    </ul>
    <ul id="R_6.073.024">
        <li id="R_6.073.024ab">sa dadarśa kapiśreṣṭham acalopamam indrajit</li>
        <li id="R_6.073.024cd">sūdayānam amitraghnam amitrān pavanātmajam</li>
    </ul>
    <ul id="R_6.073.025">
        <li id="R_6.073.025ab">sa sārathim uvācedaṃ yāhi yatraiṣa vānaraḥ</li>
        <li id="R_6.073.025cd">kṣayam eva hi naḥ kuryād rākṣasānām upekṣitaḥ</li>
    </ul>
    <ul id="R_6.073.026">
        <li id="R_6.073.026ab">ity uktaḥ sārathis tena yayau yatra sa mārutiḥ</li>
        <li id="R_6.073.026cd">vahan paramadurdharṣaṃ sthitam indrajitaṃ rathe</li>
    </ul>
    <ul id="R_6.073.027">
        <li id="R_6.073.027ab">so 'bhyupetya śarān khaḍgān paṭṭasāsiparaśvadhān</li>
        <li id="R_6.073.027cd">abhyavarṣata durdharṣaḥ kapimūrdhni sa rākṣasaḥ</li>
    </ul>
    <ul id="R_6.073.028">
        <li id="R_6.073.028ab">tāni śastrāṇi ghorāṇi pratigṛhya sa mārutiḥ</li>
        <li id="R_6.073.028cd">roṣeṇa mahatāviṣo vākyaṃ cedam uvāca ha</li>
    </ul>
    <ul id="R_6.073.029">
        <li id="R_6.073.029ab">yudhyasva yadi śūro 'si rāvaṇātmaja durmate</li>
        <li id="R_6.073.029cd">vāyuputraṃ samāsādya na jīvan pratiyāsyasi</li>
    </ul>
    <ul id="R_6.073.030">
        <li id="R_6.073.030ab">bāhubhyāṃ saṃprayudhyasva yadi me dvandvam āhave</li>
        <li id="R_6.073.030cd">vegaṃ sahasva durbuddhe tatas tvaṃ rakṣasāṃ varaḥ</li>
    </ul>
    <ul id="R_6.073.031">
        <li id="R_6.073.031ab">hanūmantaṃ jighāṃsantaṃ samudyataśarāsanam</li>
        <li id="R_6.073.031cd">rāvaṇātmajam ācaṣṭe lakṣmaṇāya vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.073.032">
        <li id="R_6.073.032ab">yas tu vāsavanirjetā rāvaṇasyātmasaṃbhavaḥ</li>
        <li id="R_6.073.032cd">sa eṣa ratham āsthāya hanūmantaṃ jighāṃsati</li>
    </ul>
    <ul id="R_6.073.033">
        <li id="R_6.073.033ab">tam apratimasaṃsthānaiḥ śaraiḥ śatruvidāraṇaiḥ</li>
        <li id="R_6.073.033cd">jīvitāntakarair ghoraiḥ saumitre rāvaṇiṃ jahi</li>
    </ul>
    <ul id="R_6.073.034">
        <li id="R_6.073.034ab">
            <seg type="pāda" n="a">ity evam uktas tu tadā mahātmā</seg>
            <seg type="pāda" n="b">vibhīṣaṇenārivibhīṣaṇena</seg>
        </li>
        <li id="R_6.073.034cd">
            <seg type="pāda" n="c">dadarśa taṃ parvatasaṃnikāśaṃ</seg>
            <seg type="pāda" n="d">rathasthitaṃ bhīmabalaṃ durāsadam</seg>
        </li>
    </ul>
    <ul id="R_6.074.001">
        <li id="R_6.074.001ab">evam uktvā tu saumitriṃ jātaharṣo vibhīṣaṇaḥ</li>
        <li id="R_6.074.001cd">dhanuṣpāṇinam ādāya tvaramāṇo jagāma saḥ</li>
    </ul>
    <ul id="R_6.074.002">
        <li id="R_6.074.002ab">avidūraṃ tato gatvā praviśya ca mahad vanam</li>
        <li id="R_6.074.002cd">darśayām āsa tat karma lakṣmaṇāya vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.074.003">
        <li id="R_6.074.003ab">nīlajīmūtasaṃkāśaṃ nyagrodhaṃ bhīmadarśanam</li>
        <li id="R_6.074.003cd">tejasvī rāvaṇabhrātā lakṣmaṇāya nyavedayat</li>
    </ul>
    <ul id="R_6.074.004">
        <li id="R_6.074.004ab">ihopahāraṃ bhūtānāṃ balavān rāvaṇātajaḥ</li>
        <li id="R_6.074.004cd">upahṛtya tataḥ paścāt saṃgrāmam abhivartate</li>
    </ul>
    <ul id="R_6.074.005">
        <li id="R_6.074.005ab">adṛśyaḥ sarvabhūtānāṃ tato bhavati rākṣasaḥ</li>
        <li id="R_6.074.005cd">nihanti samare śatrūn badhnāti ca śarottamaiḥ</li>
    </ul>
    <ul id="R_6.074.006">
        <li id="R_6.074.006ab">tam apraviṣṭaṃ nyagrodhaṃ balinaṃ rāvaṇātmajam</li>
        <li id="R_6.074.006cd">vidhvaṃsaya śarais tīkṣṇaiḥ sarathaṃ sāśvasārathim</li>
    </ul>
    <ul id="R_6.074.007">
        <li id="R_6.074.007ab">tathety uktvā mahātejāḥ saumitrir mitranandanaḥ</li>
        <li id="R_6.074.007cd">babhūvāvasthitas tatra citraṃ visphārayan dhanuḥ</li>
    </ul>
    <ul id="R_6.074.008">
        <li id="R_6.074.008ab">sa rathenāgnivarṇena balavān rāvaṇātmajaḥ</li>
        <li id="R_6.074.008cd">indrajit kavacī khaḍgī sadhvajaḥ pratyadṛśyata</li>
    </ul>
    <ul id="R_6.074.009">
        <li id="R_6.074.009ab">tam uvāca mahātejāḥ paulastyam aparājitam</li>
        <li id="R_6.074.009cd">samāhvaye tvāṃ samare samyag yuddhaṃ prayaccha me</li>
    </ul>
    <ul id="R_6.074.010">
        <li id="R_6.074.010ab">evam ukto mahātejā manasvī rāvaṇātmajaḥ</li>
        <li id="R_6.074.010cd">abravīt paruṣaṃ vākyaṃ tatra dṛṣṭvā vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.074.011">
        <li id="R_6.074.011ab">iha tvaṃ jātasaṃvṛddhaḥ sākṣād bhrātā pitur mama</li>
        <li id="R_6.074.011cd">kathaṃ druhyasi putrasya pitṛvyo mama rākṣasa</li>
    </ul>
    <ul id="R_6.074.012">
        <li id="R_6.074.012ab">na jñātitvaṃ na sauhārdaṃ na jātis tava durmate</li>
        <li id="R_6.074.012cd">pramāṇaṃ na ca sodaryaṃ na dharmo dharmadūṣaṇa</li>
    </ul>
    <ul id="R_6.074.013">
        <li id="R_6.074.013ab">śocyas tvam asi durbuddhe nindanīyaś ca sādhubhiḥ</li>
        <li id="R_6.074.013cd">yas tvaṃ svajanam utsṛjya parabhṛtyatvam āgataḥ</li>
    </ul>
    <ul id="R_6.074.014">
        <li id="R_6.074.014ab">naitac chithilayā buddhyā tvaṃ vetsi mahad antaram</li>
        <li id="R_6.074.014cd">kva ca svajanasaṃvāsaḥ kva ca nīcaparāśrayaḥ</li>
    </ul>
    <ul id="R_6.074.015">
        <li id="R_6.074.015ab">guṇavān vā parajanaḥ svajano nirguṇo 'pi vā</li>
        <li id="R_6.074.015cd">nirguṇaḥ svajanaḥ śreyān yaḥ paraḥ para eva saḥ</li>
    </ul>
    <ul id="R_6.074.016">
        <li id="R_6.074.016ab">niranukrośatā ceyaṃ yādṛśī te niśācara</li>
        <li id="R_6.074.016cd">svajanena tvayā śakyaṃ paruṣaṃ rāvaṇānuja</li>
    </ul>
    <ul id="R_6.074.017">
        <li id="R_6.074.017ab">ity ukto bhrātṛputreṇa pratyuvāca vibhīṣaṇaḥ</li>
        <li id="R_6.074.017cd">ajānann iva macchīlaṃ kiṃ rākṣasa vikatthase</li>
    </ul>
    <ul id="R_6.074.018">
        <li id="R_6.074.018ab">rākṣasendrasutāsādho pāruṣyaṃ tyaja gauravāt</li>
        <li id="R_6.074.018cd">kule yady apy ahaṃ jāto rakṣasāṃ krūrakarmaṇām</li>
        <li id="R_6.074.018ef">guṇo 'yaṃ prathamo nṝṇāṃ tan me śīlam arākṣasaṃ</li>
    </ul>
    <ul id="R_6.074.019">
        <li id="R_6.074.019ab">na rame dāruṇenāhaṃ na cādharmeṇa vai rame</li>
        <li id="R_6.074.019cd">bhrātrā viṣamaśīlena kathaṃ bhrātā nirasyate</li>
    </ul>
    <ul id="R_6.074.020">
        <li id="R_6.074.020ab">parasvānāṃ ca haraṇaṃ paradārābhimarśanam</li>
        <li id="R_6.074.020cd">suhṛdām atiśaṅkāṃ ca trayo doṣāḥ kṣayāvahāḥ</li>
    </ul>
    <ul id="R_6.074.021">
        <li id="R_6.074.021ab">maharṣīṇāṃ vadho ghoraḥ sarvadevaiś ca vigrahaḥ</li>
        <li id="R_6.074.021cd">abhimānaś ca kopaś ca vairitvaṃ pratikūlatā</li>
    </ul>
    <ul id="R_6.074.022">
        <li id="R_6.074.022ab">ete doṣā mama bhrātur jīvitaiśvaryanāśanāḥ</li>
        <li id="R_6.074.022cd">guṇān pracchādayām āsuḥ parvatān iva toyadāḥ</li>
    </ul>
    <ul id="R_6.074.023">
        <li id="R_6.074.023ab">doṣair etaiḥ parityakto mayā bhrātā pitā tava</li>
        <li id="R_6.074.023cd">neyam asti purī laṅkā na ca tvaṃ na ca te pitā</li>
    </ul>
    <ul id="R_6.074.024">
        <li id="R_6.074.024ab">atimānī ca bālaś ca durvinītaś ca rākṣasa</li>
        <li id="R_6.074.024cd">baddhas tvaṃ kālapāśena brūhi māṃ yad yad icchasi</li>
    </ul>
    <ul id="R_6.074.025">
        <li id="R_6.074.025ab">adya te vyasanaṃ prāptaṃ kim iha tvaṃ tu vakṣyasi</li>
        <li id="R_6.074.025cd">praveṣṭuṃ na tvayā śakyo nyagrodho rākṣasādhama</li>
    </ul>
    <ul id="R_6.074.026">
        <li id="R_6.074.026ab">dharṣayitvā tu kākutsthau na śakyaṃ jīvituṃ tvayā</li>
        <li id="R_6.074.026cd">yudhyasva naradevena lakṣmaṇena raṇe saha</li>
        <li id="R_6.074.026ef">hatas tvaṃ devatā kāryaṃ kariṣyasi yamakṣaye</li>
    </ul>
    <ul id="R_6.074.027">
        <li id="R_6.074.027ab">
            <seg type="pāda" n="a">nidarśayasvātmabalaṃ samudyataṃ</seg>
            <seg type="pāda" n="b">kuruṣva sarvāyudhasāyakavyayam</seg>
        </li>
        <li id="R_6.074.027cd">
            <seg type="pāda" n="c">na lakṣmaṇasyaitya hi bāṇagocaraṃ</seg>
            <seg type="pāda" n="d">tvam adya jīvan sabalo gamiṣyasi</seg>
        </li>
    </ul>
    <ul id="R_6.075.001">
        <li id="R_6.075.001ab">vibhīṣaṇa vacaḥ śrutvā rāvaṇiḥ krodhamūrchitaḥ</li>
        <li id="R_6.075.001cd">abravīt paruṣaṃ vākyaṃ vegenābhyutpapāta ha</li>
    </ul>
    <ul id="R_6.075.002">
        <li id="R_6.075.002ab">udyatāyudhanistriṃśo rathe tu samalaṃkṛte</li>
        <li id="R_6.075.002cd">kālāśvayukte mahati sthitaḥ kālāntakopamaḥ</li>
    </ul>
    <ul id="R_6.075.003">
        <li id="R_6.075.003ab">mahāpramāṇam udyamya vipulaṃ vegavad dṛḍham</li>
        <li id="R_6.075.003cd">dhanur bhīmaṃ parāmṛśya śarāṃś cāmitranāśanān</li>
    </ul>
    <ul id="R_6.075.004">
        <li id="R_6.075.004ab">uvācainaṃ samārabdhaḥ saumitriṃ savibhīṣaṇam</li>
        <li id="R_6.075.004cd">tāṃś ca vānaraśārdūlān paśyadhvaṃ me parākramam</li>
    </ul>
    <ul id="R_6.075.005">
        <li id="R_6.075.005ab">adya matkārmukotsṛṣṭaṃ śaravarṣaṃ durāsadam</li>
        <li id="R_6.075.005cd">muktaṃ varṣam ivākāśe vārayiṣyatha saṃyuge</li>
    </ul>
    <ul id="R_6.075.006">
        <li id="R_6.075.006ab">adya vo māmakā bāṇā mahākārmukaniḥsṛtāḥ</li>
        <li id="R_6.075.006cd">vidhamiṣyanti gātrāṇi tūlarāśim ivānalaḥ</li>
    </ul>
    <ul id="R_6.075.007">
        <li id="R_6.075.007ab">tīkṣṇasāyakanirbhinnāñ śūlaśaktyṛṣṭitomaraiḥ</li>
        <li id="R_6.075.007cd">adya vo gamayiṣyāmi sarvān eva yamakṣayam</li>
    </ul>
    <ul id="R_6.075.008">
        <li id="R_6.075.008ab">kṣipataḥ śaravarṣāṇi kṣiprahastasya me yudhi</li>
        <li id="R_6.075.008cd">jīmūtasyeva nadataḥ kaḥ sthāsyati mamāgrataḥ</li>
    </ul>
    <ul id="R_6.075.009">
        <li id="R_6.075.009ab">tac chrutvā rākṣasendrasya garjitaṃ lakṣmaṇas tadā</li>
        <li id="R_6.075.009cd">abhītavadanaḥ kruddho rāvaṇiṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.075.010">
        <li id="R_6.075.010ab">uktaś ca durgamaḥ pāraḥ kāryāṇāṃ rākṣasa tvayā</li>
        <li id="R_6.075.010cd">kāryāṇāṃ karmaṇā pāraṃ yo gacchati sa buddhimān</li>
    </ul>
    <ul id="R_6.075.011">
        <li id="R_6.075.011ab">sa tvam arthasya hīnārtho duravāpasya kena cit</li>
        <li id="R_6.075.011cd">vaco vyāhṛtya jānīṣe kṛtārtho 'smīti durmate</li>
    </ul>
    <ul id="R_6.075.012">
        <li id="R_6.075.012ab">antardhānagatenājau yas tvayācaritas tadā</li>
        <li id="R_6.075.012cd">taskarācarito mārgo naiṣa vīraniṣevitaḥ</li>
    </ul>
    <ul id="R_6.075.013">
        <li id="R_6.075.013ab">yathā bāṇapathaṃ prāpya sthito 'haṃ tava rākṣasa</li>
        <li id="R_6.075.013cd">darśayasvādya tat tejo vācā tvaṃ kiṃ vikatthase</li>
    </ul>
    <ul id="R_6.075.014">
        <li id="R_6.075.014ab">evam ukto dhanur bhīmaṃ parāmṛśya mahābalaḥ</li>
        <li id="R_6.075.014cd">sasarje niśitān bāṇān indrajit samijiṃjaya</li>
    </ul>
    <ul id="R_6.075.015">
        <li id="R_6.075.015ab">te nisṛṣṭā mahāvegāḥ śarāḥ sarpaviṣopamāḥ</li>
        <li id="R_6.075.015cd">saṃprāpya lakṣmaṇaṃ petuḥ śvasanta iva pannagāḥ</li>
    </ul>
    <ul id="R_6.075.016">
        <li id="R_6.075.016ab">śarair atimahāvegair vegavān rāvaṇātmajaḥ</li>
        <li id="R_6.075.016cd">saumitrim indrajid yuddhe vivyādha śubhalakṣaṇam</li>
    </ul>
    <ul id="R_6.075.017">
        <li id="R_6.075.017ab">sa śarair atividdhāṅgo rudhireṇa samukṣitaḥ</li>
        <li id="R_6.075.017cd">śuśubhe lakṣmaṇaḥ śrīmān vidhūma iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.075.018">
        <li id="R_6.075.018ab">indrajit tv ātmanaḥ karma prasamīkṣyādhigamya ca</li>
        <li id="R_6.075.018cd">vinadya sumahānādam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.075.019">
        <li id="R_6.075.019ab">patriṇaḥ śitadhārās te śarā matkārmukacyutāḥ</li>
        <li id="R_6.075.019cd">ādāsyante 'dya saumitre jīvitaṃ jīvitāntagāḥ</li>
    </ul>
    <ul id="R_6.075.020">
        <li id="R_6.075.020ab">adya gomāyusaṃghāś ca śyenasaṃghāś ca lakṣmaṇa</li>
        <li id="R_6.075.020cd">gṛdhrāś ca nipatantu tvāṃ gatāsuṃ nihataṃ mayā</li>
    </ul>
    <ul id="R_6.075.021">
        <li id="R_6.075.021ab">kṣatrabandhuḥ sadānāryo rāmaḥ paramadurmatiḥ</li>
        <li id="R_6.075.021cd">bhaktaṃ bhrātaram adyaiva tvāṃ drakṣyati mayā hatam</li>
    </ul>
    <ul id="R_6.075.022">
        <li id="R_6.075.022ab">viśastakavacaṃ bhūmau vyapaviddhaśarāsanam</li>
        <li id="R_6.075.022cd">hṛtottamāṅgaṃ saumitre tvām adya nihataṃ mayā</li>
    </ul>
    <ul id="R_6.075.023">
        <li id="R_6.075.023ab">iti bruvāṇaṃ saṃrabdhaṃ paruṣaṃ rāvaṇātmajam</li>
        <li id="R_6.075.023cd">hetumadvākyam atyarthaṃ lakṣmaṇaḥ pratyuvāca ha</li>
    </ul>
    <ul id="R_6.075.024">
        <li id="R_6.075.024ab">akṛtvā katthase karma kimartham iha rākṣasa</li>
        <li id="R_6.075.024cd">kuru tat karma yenāhaṃ śraddadhyāṃ tava katthanam</li>
    </ul>
    <ul id="R_6.075.025">
        <li id="R_6.075.025ab">anuktvā paruṣaṃ vākyaṃ kiṃ cid apy anavakṣipan</li>
        <li id="R_6.075.025cd">avikatthan vadhiṣyāmi tvāṃ paśya puruṣādana</li>
    </ul>
    <ul id="R_6.075.026">
        <li id="R_6.075.026ab">ity uktvā pañcanārācān ākarṇāpūritāñ śarān</li>
        <li id="R_6.075.026cd">nicakhāna mahāvegāṃl lakṣmaṇo rākṣasorasi</li>
    </ul>
    <ul id="R_6.075.027">
        <li id="R_6.075.027ab">sa śarair āhatas tena saroṣo rāvaṇātmajaḥ</li>
        <li id="R_6.075.027cd">suprayuktais tribhir bāṇaiḥ prativivyādha lakṣmaṇam</li>
    </ul>
    <ul id="R_6.075.028">
        <li id="R_6.075.028ab">sa babhūva mahābhīmo nararākṣasasiṃhayoḥ</li>
        <li id="R_6.075.028cd">vimardas tumulo yuddhe parasparavadhaiṣiṇoḥ</li>
    </ul>
    <ul id="R_6.075.029">
        <li id="R_6.075.029ab">ubhau hi balasaṃpannāv ubhau vikramaśālinau</li>
        <li id="R_6.075.029cd">ubhāv api suvikrāntau sarvaśastrāstrakovidau</li>
    </ul>
    <ul id="R_6.075.030">
        <li id="R_6.075.030ab">ubhau paramadurjeyāv atulyabalatejasau</li>
        <li id="R_6.075.030cd">yuyudhāte mahāvīrau grahāv iva nabho gatau</li>
    </ul>
    <ul id="R_6.075.031">
        <li id="R_6.075.031ab">balavṛtrāv iva hi tau yudhi vai duṣpradharṣaṇau</li>
        <li id="R_6.075.031cd">yuyudhāte mahātmānau tadā kesariṇāv iva</li>
    </ul>
    <ul id="R_6.075.032">
        <li id="R_6.075.032ab">bahūn avasṛjantau hi mārgaṇaughān avasthitau</li>
        <li id="R_6.075.032cd">nararākṣasasiṃhau tau prahṛṣṭāv abhyayudhyatām</li>
    </ul>
    <ul id="R_6.075.033">
        <li id="R_6.075.033ab">
            <seg type="pāda" n="a">susaṃprahṛṣṭau nararākṣasottamau</seg>
            <seg type="pāda" n="b">jayaiṣiṇau mārgaṇacāpadhāriṇau</seg>
        </li>
        <li id="R_6.075.033cd">
            <seg type="pāda" n="c">parasparaṃ tau pravavarṣatur bhṛśaṃ</seg>
            <seg type="pāda" n="d">śaraughavarṣeṇa balāhakāv iva</seg>
        </li>
    </ul>
    <ul id="R_6.076.001">
        <li id="R_6.076.001ab">tataḥ śaraṃ dāśarathiḥ saṃdhāyāmitrakarśanaḥ</li>
        <li id="R_6.076.001cd">sasarja rākṣasendrāya kruddhaḥ sarpa iva śvasan</li>
    </ul>
    <ul id="R_6.076.002">
        <li id="R_6.076.002ab">tasya jyātalanirghoṣaṃ sa śrutvā rāvaṇātmajaḥ</li>
        <li id="R_6.076.002cd">vivarṇavadano bhūtvā lakṣmaṇaṃ samudaikṣata</li>
    </ul>
    <ul id="R_6.076.003">
        <li id="R_6.076.003ab">taṃ viṣaṇṇamukhaṃ dṛṣṭvā rākṣasaṃ rāvaṇātmajam</li>
        <li id="R_6.076.003cd">saumitriṃ yuddhasaṃsaktaṃ pratyuvāca vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.076.004">
        <li id="R_6.076.004ab">nimittāny anupaśyāmi yāny asmin rāvaṇātmaje</li>
        <li id="R_6.076.004cd">tvara tena mahābāho bhagna eṣa na saṃśayaḥ</li>
    </ul>
    <ul id="R_6.076.005">
        <li id="R_6.076.005ab">tataḥ saṃdhāya saumitriḥ śarān agniśikhopamān</li>
        <li id="R_6.076.005cd">mumoca niśitāṃs tasmai sarvān iva viṣolbaṇān</li>
    </ul>
    <ul id="R_6.076.006">
        <li id="R_6.076.006ab">śakrāśanisamasparśair lakṣmaṇenāhataḥ śaraiḥ</li>
        <li id="R_6.076.006cd">muhūrtam abhavan mūḍhaḥ sarvasaṃkṣubhitendriyaḥ</li>
    </ul>
    <ul id="R_6.076.007">
        <li id="R_6.076.007ab">upalabhya muhūrtena saṃjñāṃ pratyāgatendriyaḥ</li>
        <li id="R_6.076.007cd">dadarśāvasthitaṃ vīraṃ vīro daśarathātmajam</li>
    </ul>
    <ul id="R_6.076.008">
        <li id="R_6.076.008ab">so 'bhicakrāma saumitriṃ roṣāt saṃraktalocanaḥ</li>
        <li id="R_6.076.008cd">abravīc cainam āsādya punaḥ sa paruṣaṃ vacaḥ</li>
    </ul>
    <ul id="R_6.076.009">
        <li id="R_6.076.009ab">kiṃ na smarasi tad yuddhe prathame matparākramam</li>
        <li id="R_6.076.009cd">nibaddhas tvaṃ saha bhrātrā yadā yudhi viceṣṭase</li>
    </ul>
    <ul id="R_6.076.010">
        <li id="R_6.076.010ab">yuvā khalu mahāyuddhe śakrāśanisamaiḥ śaraiḥ</li>
        <li id="R_6.076.010cd">śāyinau prathamaṃ bhūmau visaṃjñau sapuraḥsarau</li>
    </ul>
    <ul id="R_6.076.011">
        <li id="R_6.076.011ab">smṛtir vā nāsti te manye vyaktaṃ vā yamasādanam</li>
        <li id="R_6.076.011cd">gantum icchasi yasmāt tvaṃ māṃ dharṣayitum icchasi</li>
    </ul>
    <ul id="R_6.076.012">
        <li id="R_6.076.012ab">yadi te prathame yuddhe na dṛṣṭo matparākramaḥ</li>
        <li id="R_6.076.012cd">adya tvāṃ darśayiṣyāmi tiṣṭhedānīṃ vyavasthitaḥ</li>
    </ul>
    <ul id="R_6.076.013">
        <li id="R_6.076.013ab">ity uktvā saptabhir bāṇair abhivivyādha lakṣmaṇam</li>
        <li id="R_6.076.013cd">daśabhiś ca hanūmantaṃ tīkṣṇadhāraiḥ śarottamaiḥ</li>
    </ul>
    <ul id="R_6.076.014">
        <li id="R_6.076.014ab">tataḥ śaraśatenaiva suprayuktena vīryavān</li>
        <li id="R_6.076.014cd">krodhād dviguṇasaṃrabdho nirbibheda vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.076.015">
        <li id="R_6.076.015ab">tad dṛṣṭvendrajitaḥ karma kṛtaṃ rāmānujas tadā</li>
        <li id="R_6.076.015cd">acintayitvā prahasan naitat kiṃ cid iti bruvan</li>
    </ul>
    <ul id="R_6.076.016">
        <li id="R_6.076.016ab">mumoca sa śarān ghorān saṃgṛhya narapuṃgavaḥ</li>
        <li id="R_6.076.016cd">abhītavadanaḥ kruddho rāvaṇiṃ lakṣmaṇo yudhi</li>
    </ul>
    <ul id="R_6.076.017">
        <li id="R_6.076.017ab">naivaṃ raṇagataḥ śūrāḥ praharanti niśācara</li>
        <li id="R_6.076.017cd">laghavaś cālpavīryāś ca sukhā hīme śarās tava</li>
    </ul>
    <ul id="R_6.076.018">
        <li id="R_6.076.018ab">naivaṃ śūrās tu yudhyante samare jayakāṅkṣiṇaḥ</li>
        <li id="R_6.076.018cd">ity evaṃ taṃ bruvāṇas tu śaravarṣair avākirat</li>
    </ul>
    <ul id="R_6.076.019">
        <li id="R_6.076.019ab">tasya bāṇais tu vidhvastaṃ kavacaṃ hemabhūṣitam</li>
        <li id="R_6.076.019cd">vyaśīryata rathopasthe tārājālam ivāmbarāt</li>
    </ul>
    <ul id="R_6.076.020">
        <li id="R_6.076.020ab">vidhūtavarmā nārācair babhūva sa kṛtavraṇaḥ</li>
        <li id="R_6.076.020cd">indrajit samare śūraḥ prarūḍha iva sānumān</li>
    </ul>
    <ul id="R_6.076.021">
        <li id="R_6.076.021ab">abhīkṣṇaṃ niśvasantau hi yudhyetāṃ tumulaṃ yudhi</li>
        <li id="R_6.076.021cd">śarasaṃkṛttasarvāṅgo sarvato rudhirokṣitau</li>
    </ul>
    <ul id="R_6.076.022">
        <li id="R_6.076.022ab">astrāṇy astravidāṃ śreṣṭhau darśayantau punaḥ punaḥ</li>
        <li id="R_6.076.022cd">śarān uccāvacākārān antarikṣe babandhatuḥ</li>
    </ul>
    <ul id="R_6.076.023">
        <li id="R_6.076.023ab">vyapetadoṣam asyantau laghucitraṃ ca suṣṭhu ca</li>
        <li id="R_6.076.023cd">ubhau tu tumulaṃ ghoraṃ cakratur nararākṣasau</li>
    </ul>
    <ul id="R_6.076.024">
        <li id="R_6.076.024ab">tayoḥ pṛthakpṛthag bhīmaḥ śuśruve talanisvanaḥ</li>
        <li id="R_6.076.024cd">sughorayor niṣṭanator gagane meghayor iva</li>
    </ul>
    <ul id="R_6.076.025">
        <li id="R_6.076.025ab">te gātrayor nipatitā rukmapuṅkhāḥ śarā yudhi</li>
        <li id="R_6.076.025cd">asṛgdigdhā viniṣpetur viviśur dharaṇītalam</li>
    </ul>
    <ul id="R_6.076.026">
        <li id="R_6.076.026ab">anyaiḥ suniśitaiḥ śastrair ākāśe saṃjaghaṭṭire</li>
        <li id="R_6.076.026cd">babhañjuś cicchiduś cāpi tayor bāṇāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_6.076.027">
        <li id="R_6.076.027ab">sa babhūva raṇe ghoras tayor bāṇamayaś cayaḥ</li>
        <li id="R_6.076.027cd">agnibhyām iva dīptābhyāṃ satre kuśamayaś cayaḥ</li>
    </ul>
    <ul id="R_6.076.028">
        <li id="R_6.076.028ab">tayoḥ kṛtavraṇau dehau śuśubhāte mahātmanoḥ</li>
        <li id="R_6.076.028cd">sapuṣpāv iva niṣpatrau vane śālmalikuṃśukau</li>
    </ul>
    <ul id="R_6.076.029">
        <li id="R_6.076.029ab">cakratus tumulaṃ ghoraṃ saṃnipātaṃ muhur muhuḥ</li>
        <li id="R_6.076.029cd">indrajil lakṣmaṇaś caiva parasparajayaiṣiṇau</li>
    </ul>
    <ul id="R_6.076.030">
        <li id="R_6.076.030ab">lakṣmaṇo rāvaṇiṃ yuddhe rāvaṇiś cāpi lakṣmaṇam</li>
        <li id="R_6.076.030cd">anyonyaṃ tāv abhighnantau na śramaṃ pratyapadyatām</li>
    </ul>
    <ul id="R_6.076.031">
        <li id="R_6.076.031ab">bāṇajālaiḥ śarīrasthair avagāḍhais tarasvinau</li>
        <li id="R_6.076.031cd">śuśubhāte mahāvīrau virūḍhāv iva parvatau</li>
    </ul>
    <ul id="R_6.076.032">
        <li id="R_6.076.032ab">tayo rudhirasiktāni saṃvṛtāni śarair bhṛśam</li>
        <li id="R_6.076.032cd">babhrājuḥ sarvagātrāṇi jvalanta iva pāvakāḥ</li>
    </ul>
    <ul id="R_6.076.033">
        <li id="R_6.076.033ab">tayor atha mahān kālo vyatīyād yudhyamānayoḥ</li>
        <li id="R_6.076.033cd">na ca tau yuddhavaimukhyaṃ śramaṃ vāpy upajagmatuḥ</li>
    </ul>
    <ul id="R_6.076.034">
        <li id="R_6.076.034ab">
            <seg type="pāda" n="a">atha samarapariśramaṃ nihantuṃ</seg>
            <seg type="pāda" n="b">samaramukheṣv ajitasya lakṣmaṇasya</seg>
        </li>
        <li id="R_6.076.034cd">
            <seg type="pāda" n="c">priyahitam upapādayan mahaujāḥ</seg>
            <seg type="pāda" n="d">samaram upetya vibhīṣaṇo 'vatasthe</seg>
        </li>
    </ul>
    <ul id="R_6.077.001">
        <li id="R_6.077.001ab">yudhyamānau tu tau dṛṣṭvā prasaktau nararākṣasau</li>
        <li id="R_6.077.001cd">śūraḥ sa rāvaṇabhrātā tasthau saṃgrāmamūrdhani</li>
    </ul>
    <ul id="R_6.077.002">
        <li id="R_6.077.002ab">tato visphārayām āsa mahad dhanur avasthitaḥ</li>
        <li id="R_6.077.002cd">utsasarja ca tīkṣṇāgrān rākṣaseṣu mahāśarān</li>
    </ul>
    <ul id="R_6.077.003">
        <li id="R_6.077.003ab">te śarāḥ śikhisaṃkāśā nipatantaḥ samāhitāḥ</li>
        <li id="R_6.077.003cd">rākṣasān dārayām āsur vajrā iva mahāgirīn</li>
    </ul>
    <ul id="R_6.077.004">
        <li id="R_6.077.004ab">vibhīṣaṇasyānucarās te 'pi śūlāsipaṭṭasaiḥ</li>
        <li id="R_6.077.004cd">ciccheduḥ samare vīrān rākṣasān rākṣasottamāḥ</li>
    </ul>
    <ul id="R_6.077.005">
        <li id="R_6.077.005ab">rākṣasais taiḥ parivṛtaḥ sa tadā tu vibhīṣaṇaḥ</li>
        <li id="R_6.077.005cd">babhau madhye prahṛṣṭānāṃ kalabhānām iva dvipaḥ</li>
    </ul>
    <ul id="R_6.077.006">
        <li id="R_6.077.006ab">tataḥ saṃcodayāno vai harīn rakṣoraṇapriyān</li>
        <li id="R_6.077.006cd">uvāca vacanaṃ kāle kālajño rakṣasāṃ varaḥ</li>
    </ul>
    <ul id="R_6.077.007">
        <li id="R_6.077.007ab">eko 'yaṃ rākṣasendrasya parāyaṇam iva sthitaḥ</li>
        <li id="R_6.077.007cd">etac cheṣaṃ balaṃ tasya kiṃ tiṣṭhata harīśvarāḥ</li>
    </ul>
    <ul id="R_6.077.008">
        <li id="R_6.077.008ab">asmin vinihate pāpe rākṣase raṇamūrdhani</li>
        <li id="R_6.077.008cd">rāvaṇaṃ varjayitvā tu śeṣam asya balaṃ hatam</li>
    </ul>
    <ul id="R_6.077.009">
        <li id="R_6.077.009ab">prahasto nihato vīro nikumbhaś ca mahābalaḥ</li>
        <li id="R_6.077.009cd">kumbhakarṇaś ca kumbhaś ca dhūmrākṣaś ca niśācaraḥ</li>
    </ul>
    <ul id="R_6.077.010">
        <li id="R_6.077.010ab">akampanaḥ supārśvaś ca cakramālī ca rākṣasaḥ</li>
        <li id="R_6.077.010cd">kampanaḥ sattvavantaś ca devāntakanarāntakau</li>
    </ul>
    <ul id="R_6.077.011">
        <li id="R_6.077.011ab">etān nihatyātibalān bahūn rākṣasasattamān</li>
        <li id="R_6.077.011cd">bāhubhyāṃ sāgaraṃ tīrtvā laṅghyatāṃ goṣpadaṃ laghu</li>
    </ul>
    <ul id="R_6.077.012">
        <li id="R_6.077.012ab">etāvad iha śeṣaṃ vo jetavyam iha vānarāḥ</li>
        <li id="R_6.077.012cd">hatāḥ sarve samāgamya rākṣasā baladarpitāḥ</li>
    </ul>
    <ul id="R_6.077.013">
        <li id="R_6.077.013ab">ayuktaṃ nidhanaṃ kartuṃ putrasya janitur mama</li>
        <li id="R_6.077.013cd">ghṛṇām apāsya rāmārthe nihanyāṃ bhrātur ātmajam</li>
    </ul>
    <ul id="R_6.077.014">
        <li id="R_6.077.014ab">hantukāmasya me bāṣpaṃ cakśuś caiva nirudhyate</li>
        <li id="R_6.077.014cd">tad evaiṣa mahābāhur lakṣmaṇaḥ śamayiṣyati</li>
        <li id="R_6.077.014ef">vānarā ghnantuṃ saṃbhūya bhṛtyān asya samīpagān</li>
    </ul>
    <ul id="R_6.077.015">
        <li id="R_6.077.015ab">iti tenātiyaśasā rākṣasenābhicoditāḥ</li>
        <li id="R_6.077.015cd">vānarendrā jahṛṣire lāṅgalāni ca vivyadhuḥ</li>
    </ul>
    <ul id="R_6.077.016">
        <li id="R_6.077.016ab">tatas te kapiśārdūlāḥ kṣveḍantaś ca muhur muhuḥ</li>
        <li id="R_6.077.016cd">mumucur vividhān nādān meghān dṛṣṭveva barhiṇaḥ</li>
    </ul>
    <ul id="R_6.077.017">
        <li id="R_6.077.017ab">jāmbavān api taiḥ sarvaiḥ svayūthair abhisaṃvṛtaḥ</li>
        <li id="R_6.077.017cd">aśmabhis tāḍayām āsa nakhair dantaiś ca rākṣasān</li>
    </ul>
    <ul id="R_6.077.018">
        <li id="R_6.077.018ab">nighnantam ṛkṣādhipatiṃ rākṣasās te mahābalāḥ</li>
        <li id="R_6.077.018cd">parivavrur bhayaṃ tyaktvā tam anekavidhāyudhāḥ</li>
    </ul>
    <ul id="R_6.077.019">
        <li id="R_6.077.019ab">śaraiḥ paraśubhis tīkṣṇaiḥ paṭṭasair yaṣṭitomaraiḥ</li>
        <li id="R_6.077.019cd">jāmbavantaṃ mṛdhe jaghnur nighnantaṃ rākṣasīṃ camūm</li>
    </ul>
    <ul id="R_6.077.020">
        <li id="R_6.077.020ab">sa saṃprahāras tumulaḥ saṃjajñe kapirākṣasām</li>
        <li id="R_6.077.020cd">devāsurāṇāṃ kruddhānāṃ yathā bhīmo mahāsvanaḥ</li>
    </ul>
    <ul id="R_6.077.021">
        <li id="R_6.077.021ab">hanūmān api saṃkruddhaḥ sālam utpāṭya parvatāt</li>
        <li id="R_6.077.021cd">rakṣasāṃ kadanaṃ cakre samāsādya sahasraśaḥ</li>
    </ul>
    <ul id="R_6.077.022">
        <li id="R_6.077.022ab">sa dattvā tumulaṃ yuddhaṃ pitṛvyasyendrajid yudhi</li>
        <li id="R_6.077.022cd">lakṣmaṇaṃ paravīraghnaṃ punar evābhyadhāvata</li>
    </ul>
    <ul id="R_6.077.023">
        <li id="R_6.077.023ab">tau prayuddhau tadā vīrau mṛdhe lakṣmaṇarākṣasau</li>
        <li id="R_6.077.023cd">śaraughān abhivarṣantau jaghnatus tau parasparam</li>
    </ul>
    <ul id="R_6.077.024">
        <li id="R_6.077.024ab">abhīkṣṇam antardadhatuḥ śarajālair mahābalau</li>
        <li id="R_6.077.024cd">candrādityāv ivoṣṇānte yathā meghais tarasvinau</li>
    </ul>
    <ul id="R_6.077.025">
        <li id="R_6.077.025ab">na hy ādānaṃ na saṃdhānaṃ dhanuṣo vā parigrahaḥ</li>
        <li id="R_6.077.025cd">na vipramokṣo bāṇānāṃ na vikarṣo na vigrahaḥ</li>
    </ul>
    <ul id="R_6.077.026">
        <li id="R_6.077.026ab">na muṣṭipratisaṃdhānaṃ na lakṣyapratipādanam</li>
        <li id="R_6.077.026cd">adṛśyata tayos tatra yudhyatoḥ pāṇilāghavāt</li>
    </ul>
    <ul id="R_6.077.027">
        <li id="R_6.077.027ab">cāpavegapramuktaiś ca bāṇajālaiḥ samantataḥ</li>
        <li id="R_6.077.027cd">antarikṣe 'bhisaṃchanne na rūpāṇi cakāśire</li>
        <li id="R_6.077.027ef">tamasā pihitaṃ sarvam āsīd bhīmataraṃ mahat</li>
    </ul>
    <ul id="R_6.077.028">
        <li id="R_6.077.028ab">na tadānīṃ vavau vāyur na jajvāla ca pāvakaḥ</li>
        <li id="R_6.077.028cd">svastyas tu lokebhya iti jajalpaś ca maharṣayaḥ</li>
        <li id="R_6.077.028ef">saṃpetuś cātra saṃprāptā gandharvāḥ saha cāraṇaiḥ</li>
    </ul>
    <ul id="R_6.077.029">
        <li id="R_6.077.029ab">atha rākṣasasiṃhasya kṛṣṇān kanakabhūṣaṇān</li>
        <li id="R_6.077.029cd">śaraiś caturbhiḥ saumitrir vivyādha caturo hayān</li>
    </ul>
    <ul id="R_6.077.030">
        <li id="R_6.077.030ab">tato 'pareṇa bhallena sūtasya vicariṣyataḥ</li>
        <li id="R_6.077.030cd">lāghavād rāghavaḥ śrīmāñ śiraḥ kāyād apāharat</li>
    </ul>
    <ul id="R_6.077.031">
        <li id="R_6.077.031ab">nihataṃ sārathiṃ dṛṣṭvā samare rāvaṇātmajaḥ</li>
        <li id="R_6.077.031cd">prajahau samaroddharṣaṃ viṣaṇṇaḥ sa babhūva ha</li>
    </ul>
    <ul id="R_6.077.032">
        <li id="R_6.077.032ab">viṣaṇṇavadanaṃ dṛṣṭvā rākṣasaṃ hariyūthapāḥ</li>
        <li id="R_6.077.032cd">tataḥ paramasaṃhṛṣṭo lakṣmaṇaṃ cābhyapūjayan</li>
    </ul>
    <ul id="R_6.077.033">
        <li id="R_6.077.033ab">tataḥ pramāthī śarabho rabhaso gandhamādanaḥ</li>
        <li id="R_6.077.033cd">amṛṣyamāṇāś catvāraś cakrur vegaṃ harīśvarāḥ</li>
    </ul>
    <ul id="R_6.077.034">
        <li id="R_6.077.034ab">te cāsya hayamukhyeṣu tūrṇam utpatya vānarāḥ</li>
        <li id="R_6.077.034cd">caturṣu sumahāvīryā nipetur bhīmavikramāḥ</li>
    </ul>
    <ul id="R_6.077.035">
        <li id="R_6.077.035ab">teṣām adhiṣṭhitānāṃ tair vānaraiḥ parvatopamaiḥ</li>
        <li id="R_6.077.035cd">mukhebhyo rudhiraṃ vyaktaṃ hayānāṃ samavartata</li>
    </ul>
    <ul id="R_6.077.036">
        <li id="R_6.077.036ab">te nihatya hayāṃs tasya pramathya ca mahāratham</li>
        <li id="R_6.077.036cd">punar utpatya vegena tasthur lakṣmaṇapārśvataḥ</li>
    </ul>
    <ul id="R_6.077.037">
        <li id="R_6.077.037ab">sa hatāśvād avaplutya rathān mathitasāratheḥ</li>
        <li id="R_6.077.037cd">śaravarṣeṇa saumitrim abhyadhāvata rāvaṇiḥ</li>
    </ul>
    <ul id="R_6.077.038">
        <li id="R_6.077.038ab">
            <seg type="pāda" n="a">tato mahendrapratimaḥ sa lakṣmaṇaḥ</seg>
            <seg type="pāda" n="b">padātinaṃ taṃ niśitaiḥ śarottamaiḥ</seg>
        </li>
        <li id="R_6.077.038cd">
            <seg type="pāda" n="c">sṛjantam ādau niśitāñ śarottamān</seg>
            <seg type="pāda" n="d">bhṛśaṃ tadā bāṇagaṇair nyavārayat</seg>
        </li>
    </ul>
    <ul id="R_6.078.001">
        <li id="R_6.078.001ab">sa hatāśvo mahātejā bhūmau tiṣṭhan niśācaraḥ</li>
        <li id="R_6.078.001cd">indrajit paramakruddhaḥ saṃprajajvāla tejasā</li>
    </ul>
    <ul id="R_6.078.002">
        <li id="R_6.078.002ab">tau dhanvinau jighāṃsantāv anyonyam iṣubhir bhṛśam</li>
        <li id="R_6.078.002cd">vijayenābhiniṣkrāntau vane gajavṛṣāv iva</li>
    </ul>
    <ul id="R_6.078.003">
        <li id="R_6.078.003ab">nibarhayantaś cānyonyaṃ te rākṣasavanaukasaḥ</li>
        <li id="R_6.078.003cd">bhartāraṃ na jahur yuddhe saṃpatantas tatas tataḥ</li>
    </ul>
    <ul id="R_6.078.004">
        <li id="R_6.078.004ab">sa lakṣmaṇaṃ samuddiśya paraṃ lāghavam āsthitaḥ</li>
        <li id="R_6.078.004cd">vavarṣa śaravarṣāṇi varṣāṇīva puraṃdaraḥ</li>
    </ul>
    <ul id="R_6.078.005">
        <li id="R_6.078.005ab">muktam indrajitā tat tu śaravarṣam ariṃdamaḥ</li>
        <li id="R_6.078.005cd">avārayad asaṃbhrānto lakṣmaṇaḥ sudurāsadam</li>
    </ul>
    <ul id="R_6.078.006">
        <li id="R_6.078.006ab">abhedyakacanaṃ matvā lakṣmaṇaṃ rāvaṇātmajaḥ</li>
        <li id="R_6.078.006cd">lalāṭe lakṣmaṇaṃ bāṇaiḥ supuṅkhais tribhir indrajit</li>
        <li id="R_6.078.006ef">avidhyat paramakruddhaḥ śīghram astraṃ pradarśayan</li>
    </ul>
    <ul id="R_6.078.007">
        <li id="R_6.078.007ab">taiḥ pṛṣatkair lalāṭasthaiḥ śuśubhe raghunandanaḥ</li>
        <li id="R_6.078.007cd">raṇāgre samaraślāghī triśṛṅga iva parvataḥ</li>
    </ul>
    <ul id="R_6.078.008">
        <li id="R_6.078.008ab">sa tathāpy ardito bāṇai rākṣasena mahāmṛdhe</li>
        <li id="R_6.078.008cd">tam āśu prativivyādha lakṣmaṇaḥ panabhiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.078.009">
        <li id="R_6.078.009ab">lakṣmaṇendrajitau vīrau mahābalaśarāsanau</li>
        <li id="R_6.078.009cd">anyonyaṃ jaghnatur bāṇair viśikhair bhīmavikramau</li>
    </ul>
    <ul id="R_6.078.010">
        <li id="R_6.078.010ab">tau parasparam abhyetya sarvagātreṣu dhanvinau</li>
        <li id="R_6.078.010cd">ghorair vivyadhatur bāṇaiḥ kṛtabhāvāv ubhau jaye</li>
    </ul>
    <ul id="R_6.078.011">
        <li id="R_6.078.011ab">tasmai dṛḍhataraṃ kruddho hatāśvāya vibhīṣaṇaḥ</li>
        <li id="R_6.078.011cd">vajrasparśasamān pañca sasarjorasi mārgaṇān</li>
    </ul>
    <ul id="R_6.078.012">
        <li id="R_6.078.012ab">te tasya kāyaṃ nirbhidya rukmapuṅkhā nimittagāḥ</li>
        <li id="R_6.078.012cd">babhūvur lohitādigdhā raktā iva mahoragāḥ</li>
    </ul>
    <ul id="R_6.078.013">
        <li id="R_6.078.013ab">sa pitṛvyasya saṃkruddha indrajic charam ādade</li>
        <li id="R_6.078.013cd">uttamaṃ rakṣasāṃ madhye yamadattaṃ mahābalaḥ</li>
    </ul>
    <ul id="R_6.078.014">
        <li id="R_6.078.014ab">taṃ samīkṣya mahātejā maheṣuṃ tena saṃhitam</li>
        <li id="R_6.078.014cd">lakṣmaṇo 'py ādade bāṇam anyaṃ bhīmaparākramaḥ</li>
    </ul>
    <ul id="R_6.078.015">
        <li id="R_6.078.015ab">kubereṇa svayaṃ svapne yad dattam amitātmanā</li>
        <li id="R_6.078.015cd">durjayaṃ durviṣahyaṃ ca sendrair api surāsuraiḥ</li>
    </ul>
    <ul id="R_6.078.016">
        <li id="R_6.078.016ab">tābhyāṃ tau dhanuṣi śreṣṭhe saṃhitau sāyakottamau</li>
        <li id="R_6.078.016cd">vikṛṣyamāṇau vīrābhyāṃ bhṛśaṃ jajvalatuḥ śriyā</li>
    </ul>
    <ul id="R_6.078.017">
        <li id="R_6.078.017ab">tau bhāsayantāv ākāśaṃ dhanurbhyāṃ viśikhau cyutau</li>
        <li id="R_6.078.017cd">mukhena mukham āhatya saṃnipetatur ojasā</li>
    </ul>
    <ul id="R_6.078.018">
        <li id="R_6.078.018ab">tau mahāgrahasaṃkāśāv anyonyaṃ saṃnipatya ca</li>
        <li id="R_6.078.018cd">saṃgrāme śatadhā yātau medinyāṃ vinipetatuḥ</li>
    </ul>
    <ul id="R_6.078.019">
        <li id="R_6.078.019ab">śarau pratihatau dṛṣṭvā tāv ubhau raṇamūrdhani</li>
        <li id="R_6.078.019cd">vrīḍito jātaroṣau ca lakṣmaṇendrajitāv ubhau</li>
    </ul>
    <ul id="R_6.078.020">
        <li id="R_6.078.020ab">susaṃrabdhas tu saumitrir astraṃ vāruṇam ādade</li>
        <li id="R_6.078.020cd">raudraṃ mahedrajid yuddhe vyasṛjad yudhi viṣṭhitaḥ</li>
    </ul>
    <ul id="R_6.078.021">
        <li id="R_6.078.021ab">tayoḥ sutumulaṃ yuddhaṃ saṃbabhūvādbhutopamam</li>
        <li id="R_6.078.021cd">gaganasthāni bhūtāni lakṣmaṇaṃ paryavārayan</li>
    </ul>
    <ul id="R_6.078.022">
        <li id="R_6.078.022ab">bhairavābhirute bhīme yuddhe vānararākṣasām</li>
        <li id="R_6.078.022cd">bhūtair bahubhir ākāśaṃ vismitair āvṛtaṃ babhau</li>
    </ul>
    <ul id="R_6.078.023">
        <li id="R_6.078.023ab">ṛṣayaḥ pitaro devā gandharvā garuṇoragāḥ</li>
        <li id="R_6.078.023cd">śatakratuṃ puraskṛtya rarakṣur lakṣmaṇaṃ raṇe</li>
    </ul>
    <ul id="R_6.078.024">
        <li id="R_6.078.024ab">athānyaṃ mārgaṇaśreṣṭhaṃ saṃdadhe rāvaṇānujaḥ</li>
        <li id="R_6.078.024cd">hutāśanasamasparśaṃ rāvaṇātmajadāruṇam</li>
    </ul>
    <ul id="R_6.078.025">
        <li id="R_6.078.025ab">supatram anuvṛttāṅgaṃ suparvāṇaṃ susaṃsthitam</li>
        <li id="R_6.078.025cd">suvarṇavikṛtaṃ vīraḥ śarīrāntakaraṃ śaram</li>
    </ul>
    <ul id="R_6.078.026">
        <li id="R_6.078.026ab">durāvāraṃ durviṣahaṃ rākṣasānāṃ bhayāvaham</li>
        <li id="R_6.078.026cd">āśīviṣaviṣaprakhyaṃ devasaṃghaiḥ samarcitam</li>
    </ul>
    <ul id="R_6.078.027">
        <li id="R_6.078.027ab">yena śakro mahātejā dānavān ajayat prabhuḥ</li>
        <li id="R_6.078.027cd">purā devāsure yuddhe vīryavān harivāhanaḥ</li>
    </ul>
    <ul id="R_6.078.028">
        <li id="R_6.078.028ab">tad aindram astraṃ saumitriḥ saṃyugeṣv aparājitam</li>
        <li id="R_6.078.028cd">śaraśreṣṭhaṃ dhanuḥ śreṣṭhe naraśreṣṭho 'bhisaṃdadhe</li>
    </ul>
    <ul id="R_6.078.029">
        <li id="R_6.078.029ab">saṃdhāyāmitradalanaṃ vicakarṣa śarāsanam</li>
        <li id="R_6.078.029cd">sajyam āyamya durdharśaḥ kālo lokakṣaye yathā</li>
    </ul>
    <ul id="R_6.078.030">
        <li id="R_6.078.030ab">saṃdhāya dhanuṣi śreṣṭhe vikarṣann idam abravīt</li>
        <li id="R_6.078.030cd">lakṣmīvāṃl lakṣmaṇo vākyam arthasādhakam ātmanaḥ</li>
    </ul>
    <ul id="R_6.078.031">
        <li id="R_6.078.031ab">dharmātmā satyasaṃdhaś ca rāmo dāśarathir yadi</li>
        <li id="R_6.078.031cd">pauruṣe cāpratidvandvas tad enaṃ jahi rāvaṇim</li>
    </ul>
    <ul id="R_6.078.032">
        <li id="R_6.078.032ab">ity uktvā bāṇam ākarṇaṃ vikṛṣya tam ajihmagam</li>
        <li id="R_6.078.032cd">lakṣmaṇaḥ samare vīraḥ sasarjendrajitaṃ prati</li>
        <li id="R_6.078.032ef">aindrāstreṇa samāyujya lakṣmaṇaḥ paravīrahā</li>
    </ul>
    <ul id="R_6.078.033">
        <li id="R_6.078.033ab">tac chiraḥ saśiras trāṇaṃ śrīmaj jvalitakuṇḍalam</li>
        <li id="R_6.078.033cd">pramathyendrajitaḥ kāyāt papāta dharaṇītale</li>
    </ul>
    <ul id="R_6.078.034">
        <li id="R_6.078.034ab">tad rākṣasatanūjasya chinnaskandhaṃ śiro mahat</li>
        <li id="R_6.078.034cd">tapanīyanibhaṃ bhūmau dadṛśe rudhirokṣitam</li>
    </ul>
    <ul id="R_6.078.035">
        <li id="R_6.078.035ab">hatas tu nipapātāśu dharaṇyāṃ rāvaṇātmajaḥ</li>
        <li id="R_6.078.035cd">kavacī saśirastrāṇo vidhvastaḥ saśarāsanaḥ</li>
    </ul>
    <ul id="R_6.078.036">
        <li id="R_6.078.036ab">cukruśus te tataḥ sarve vānarāḥ savibhīṣaṇāḥ</li>
        <li id="R_6.078.036cd">hṛṣyanto nihate tasmin devā vṛtravadhe yathā</li>
    </ul>
    <ul id="R_6.078.037">
        <li id="R_6.078.037ab">athāntarikṣe bhūtānām ṛṣīṇāṃ ca mahātmanām</li>
        <li id="R_6.078.037cd">abhijajñe ca saṃnādo gandharvāpsarasām api</li>
    </ul>
    <ul id="R_6.078.038">
        <li id="R_6.078.038ab">patitaṃ samabhijñāya rākṣasī sā mahācamūḥ</li>
        <li id="R_6.078.038cd">vadhyamānā diśo bheje haribhir jitakāśibhiḥ</li>
    </ul>
    <ul id="R_6.078.039">
        <li id="R_6.078.039ab">vanarair vadhyamānās te śastrāṇy utsṛjya rākṣasāḥ</li>
        <li id="R_6.078.039cd">laṅkām abhimukhāḥ sarve naṣṭasaṃjñāḥ pradhāvitāḥ</li>
    </ul>
    <ul id="R_6.078.040">
        <li id="R_6.078.040ab">dudruvur bahudhā bhītā rākṣasāḥ śataśo diśaḥ</li>
        <li id="R_6.078.040cd">tyaktvā praharaṇān sarve paṭṭasāsiparaśvadhān</li>
    </ul>
    <ul id="R_6.078.041">
        <li id="R_6.078.041ab">ke cil laṅkāṃ paritrastāḥ praviṣṭā vānarārditāḥ</li>
        <li id="R_6.078.041cd">samudre patitāḥ ke cit ke cit parvatam āśritāḥ</li>
    </ul>
    <ul id="R_6.078.042">
        <li id="R_6.078.042ab">hatam indrajitaṃ dṛṣṭvā śayānaṃ samarakṣitau</li>
        <li id="R_6.078.042cd">rākṣasānāṃ sahasreṣu na kaś cit pratyadṛśyata</li>
    </ul>
    <ul id="R_6.078.043">
        <li id="R_6.078.043ab">yathāstaṃ gata āditye nāvatiṣṭhanti raśmayaḥ</li>
        <li id="R_6.078.043cd">tathā tasmin nipatite rākṣasās te gatā diśaḥ</li>
    </ul>
    <ul id="R_6.078.044">
        <li id="R_6.078.044ab">śāntarakśmir ivādityo nirvāṇa iva pāvakaḥ</li>
        <li id="R_6.078.044cd">sa babhūva mahātejā vyapāsta gatajīvitaḥ</li>
    </ul>
    <ul id="R_6.078.045">
        <li id="R_6.078.045ab">praśāntapīḍā bahulo vinaṣṭāriḥ praharṣavān</li>
        <li id="R_6.078.045cd">babhūva lokaḥ patite rākṣasendrasute tadā</li>
    </ul>
    <ul id="R_6.078.046">
        <li id="R_6.078.046ab">harṣaṃ ca śakro bhagavān saha sarvaiḥ surarṣabhaiḥ</li>
        <li id="R_6.078.046cd">jagāma nihate tasmin rākṣase pāpakarmaṇi</li>
    </ul>
    <ul id="R_6.078.047">
        <li id="R_6.078.047ab">śuddhā āpo nabhaś caiva jahṛṣur daityadānavāḥ</li>
        <li id="R_6.078.047cd">ājagmuḥ patite tasmin sarvalokabhayāvahe</li>
    </ul>
    <ul id="R_6.078.048">
        <li id="R_6.078.048ab">ūcuś ca sahitāḥ sarve devagandharvadānavāḥ</li>
        <li id="R_6.078.048cd">vijvarāḥ śāntakaluṣā brāhmaṇā vicarantv iti</li>
    </ul>
    <ul id="R_6.078.049">
        <li id="R_6.078.049ab">tato 'bhyanandan saṃhṛṣṭāḥ samare hariyūthapāḥ</li>
        <li id="R_6.078.049cd">tam apratibalaṃ dṛṣṭvā hataṃ nairṛtapuṃgavam</li>
    </ul>
    <ul id="R_6.078.050">
        <li id="R_6.078.050ab">vibhīṣaṇo hanūmāṃś ca jāmbavāṃś carkṣayūthapaḥ</li>
        <li id="R_6.078.050cd">vijayenābhinandantas tuṣṭuvuś cāpi lakṣmaṇam</li>
    </ul>
    <ul id="R_6.078.051">
        <li id="R_6.078.051ab">kṣveḍantaś ca nadantaś ca garjantaś ca plavaṃgamāḥ</li>
        <li id="R_6.078.051cd">labdhalakṣā raghusutaṃ parivāryopatasthire</li>
    </ul>
    <ul id="R_6.078.052">
        <li id="R_6.078.052ab">lāṅgūlāni pravidhyantaḥ sphoṭayantaś ca vānarāḥ</li>
        <li id="R_6.078.052cd">lakṣmaṇo jayatīty evaṃ vākyaṃ vyaśrāvayaṃs tadā</li>
    </ul>
    <ul id="R_6.078.053">
        <li id="R_6.078.053ab">anyonyaṃ ca samāśliṣya kapayo hṛṣṭamānasāḥ</li>
        <li id="R_6.078.053cd">cakrur uccāvacaguṇā rāghavāśrayajāḥ kathāḥ</li>
    </ul>
    <ul id="R_6.078.054">
        <li id="R_6.078.054ab">
            <seg type="pāda" n="a">tad asukaram athābhivīkṣya hṛṣṭāḥ</seg>
            <seg type="pāda" n="b">priyasuhṛdo yudhi lakṣmaṇasya karma</seg>
        </li>
        <li id="R_6.078.054cd">
            <seg type="pāda" n="c">paramam upalabhan manaḥpraharṣaṃ</seg>
            <seg type="pāda" n="d">vinihatam indraripuṃ niśamya devāḥ</seg>
        </li>
    </ul>
    <ul id="R_6.079.001">
        <li id="R_6.079.001ab">rudhiraklinnagātras tu lakṣmaṇaḥ śubhalakṣaṇaḥ</li>
        <li id="R_6.079.001cd">babhūva hṛṣṭas taṃ hatvā śakrajetāram āhave</li>
    </ul>
    <ul id="R_6.079.002">
        <li id="R_6.079.002ab">tataḥ sa jāmbavantaṃ ca hanūmantaṃ ca vīryavān</li>
        <li id="R_6.079.002cd">saṃnivartya mahātejās tāṃś ca sarvān vanaukasaḥ</li>
    </ul>
    <ul id="R_6.079.003">
        <li id="R_6.079.003ab">ājagāma tataḥ śīghraṃ yatra sugrīvarāghavau</li>
        <li id="R_6.079.003cd">vibhīṣaṇam avaṣṭabhya hanūmantaṃ ca lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.079.004">
        <li id="R_6.079.004ab">tato rāmam abhikramya saumitrir abhivādya ca</li>
        <li id="R_6.079.004cd">tasthau bhrātṛsamīpasthaḥ śakrasyendrānujo yathā</li>
        <li id="R_6.079.004ef">ācacakṣe tadā vīro ghoram indrajito vadham</li>
    </ul>
    <ul id="R_6.079.005">
        <li id="R_6.079.005ab">rāvaṇas tu śiraś chinnaṃ lakṣmaṇena mahātmanā</li>
        <li id="R_6.079.005cd">nyavedayata rāmāya tadā hṛṣṭo vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.079.006">
        <li id="R_6.079.006ab">upaveśya tam utsaṅge pariṣvajyāvapīḍitam</li>
        <li id="R_6.079.006cd">mūrdhni cainam upāghrāya bhūyaḥ saṃspṛśya ca tvaran</li>
        <li id="R_6.079.006ef">uvāca lakṣmaṇaṃ vākyam āśvāsya puruṣarṣabhaḥ</li>
    </ul>
    <ul id="R_6.079.007">
        <li id="R_6.079.007ab">kṛtaṃ paramakalyāṇaṃ karma duṣkarakāriṇā</li>
        <li id="R_6.079.007cd">niramitraḥ kṛto 'smy adya niryāsyati hi rāvaṇaḥ</li>
        <li id="R_6.079.007ef">balavyūhena mahatā śrutvā putraṃ nipātitam</li>
    </ul>
    <ul id="R_6.079.008">
        <li id="R_6.079.008ab">taṃ putravadhasaṃtaptaṃ niryāntaṃ rākṣasādhipam</li>
        <li id="R_6.079.008cd">balenāvṛtya mahatā nihaniṣyāmi durjayam</li>
    </ul>
    <ul id="R_6.079.009">
        <li id="R_6.079.009ab">tvayā lakṣmaṇa nāthena sītā ca pṛthivī ca me</li>
        <li id="R_6.079.009cd">na duṣprāpā hate tv adya śakrajetari cāhave</li>
    </ul>
    <ul id="R_6.079.010">
        <li id="R_6.079.010ab">sa taṃ bhrātaram āśvāsya pāriṣvajya ca rāghavaḥ</li>
        <li id="R_6.079.010cd">rāmaḥ suṣeṇaṃ muditaḥ samābhāṣyedam abravīt</li>
    </ul>
    <ul id="R_6.079.011">
        <li id="R_6.079.011ab">saśalyo 'yaṃ mahāprājñaḥ saumitrir mitravatsalaḥ</li>
        <li id="R_6.079.011cd">yathā bhavati susvasthas tathā tvaṃ samupācara</li>
        <li id="R_6.079.011ef">viśalyaḥ kriyatāṃ kṣipraṃ saumitriḥ savibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.079.012">
        <li id="R_6.079.012ab">kṛṣa vānarasainyānāṃ śūrāṇāṃ drumayodhinām</li>
        <li id="R_6.079.012cd">ye cānye 'tra ca yudhyantaḥ saśalyā vraṇinas tathā</li>
        <li id="R_6.079.012ef">te 'pi sarve prayatnena kriyantāṃ sukhinas tvayā</li>
    </ul>
    <ul id="R_6.079.013">
        <li id="R_6.079.013ab">evam uktaḥ sa rāmeṇa mahātmā hariyūthapaḥ</li>
        <li id="R_6.079.013cd">lakṣmaṇāya dadau nastaḥ suṣeṇaḥ paramauṣadham</li>
    </ul>
    <ul id="R_6.079.014">
        <li id="R_6.079.014ab">sa tasya gandham āghrāya viśalyaḥ samapadyata</li>
        <li id="R_6.079.014cd">tadā nirvedanaś caiva saṃrūḍhavraṇa eva ca</li>
    </ul>
    <ul id="R_6.079.015">
        <li id="R_6.079.015ab">vibhīṣaṇa mukhānāṃ ca suhṛdāṃ rāghavājñayā</li>
        <li id="R_6.079.015cd">sarvavānaramukhyānāṃ cikitsāṃ sa tadākarot</li>
    </ul>
    <ul id="R_6.079.016">
        <li id="R_6.079.016ab">tataḥ prakṛtim āpanno hṛtaśalyo gatavyathaḥ</li>
        <li id="R_6.079.016cd">saumitrir muditas tatra kṣaṇena vigatajvaraḥ</li>
    </ul>
    <ul id="R_6.079.017">
        <li id="R_6.079.017ab">
            <seg type="pāda" n="a">tathaiva rāmaḥ plavagādhipas tadā</seg>
            <seg type="pāda" n="b">vibhīṣaṇaś carkṣapatiś ca jāmbavān</seg>
        </li>
        <li id="R_6.079.017cd">
            <seg type="pāda" n="c">avekṣya saumitrim arogam utthitaṃ</seg>
            <seg type="pāda" n="d">mudā sasainyaḥ suciraṃ jaharṣire</seg>
        </li>
    </ul>
    <ul id="R_6.079.018">
        <li id="R_6.079.018ab">
            <seg type="pāda" n="a">apūjayat karma sa lakṣmaṇasya</seg>
            <seg type="pāda" n="b">suduṣkaraṃ dāśarathir mahātmā</seg>
        </li>
        <li id="R_6.079.018cd">
            <seg type="pāda" n="c">hṛṣṭā babhūvur yudhi yūthapendrā</seg>
            <seg type="pāda" n="d">niśamya taṃ śakrajitaṃ nipātitam</seg>
        </li>
    </ul>
    <ul id="R_6.080.001">
        <li id="R_6.080.001ab">tataḥ paulastya sacivāḥ śrutvā cendrajitaṃ hatam</li>
        <li id="R_6.080.001cd">ācacakṣur abhijñāya daśagrīvāya savyathāḥ</li>
    </ul>
    <ul id="R_6.080.002">
        <li id="R_6.080.002ab">yuddhe hato mahārāja lakṣmaṇena tavātmajaḥ</li>
        <li id="R_6.080.002cd">vibhīṣaṇasahāyena miṣatāṃ no mahādyute</li>
    </ul>
    <ul id="R_6.080.003">
        <li id="R_6.080.003ab">śūraḥ śūreṇa saṃgamya saṃyugeṣv aparājitaḥ</li>
        <li id="R_6.080.003cd">lakṣṇanena hataḥ śūraḥ putras te vibudhendrajit</li>
    </ul>
    <ul id="R_6.080.004">
        <li id="R_6.080.004ab">sa taṃ pratibhayaṃ śrutvā vadhaṃ putrasya dāruṇam</li>
        <li id="R_6.080.004cd">ghoram indrajitaḥ saṃkhye kaśmalaṃ prāviśan mahat</li>
    </ul>
    <ul id="R_6.080.005">
        <li id="R_6.080.005ab">upalabhya cirāt saṃjñāṃ rājā rākṣasapuṃgavaḥ</li>
        <li id="R_6.080.005cd">putraśokārdito dīno vilalāpākulendriyaḥ</li>
    </ul>
    <ul id="R_6.080.006">
        <li id="R_6.080.006ab">hā rākṣasacamūmukhya mama vatsa mahāratha</li>
        <li id="R_6.080.006cd">jitvendraṃ katham adya tvaṃ lakṣmaṇasya vaśaṃ gataḥ</li>
    </ul>
    <ul id="R_6.080.007">
        <li id="R_6.080.007ab">nanu tvam iṣubhiḥ kruddho bhindyāḥ kālāntakāv api</li>
        <li id="R_6.080.007cd">mandarasyāpi śṛṅgāṇi kiṃ punar lakṣmaṇaṃ raṇe</li>
    </ul>
    <ul id="R_6.080.008">
        <li id="R_6.080.008ab">adya vaivasvato rājā bhūyo bahumato mama</li>
        <li id="R_6.080.008cd">yenādya tvaṃ mahābāho saṃyuktaḥ kāladharmaṇā</li>
    </ul>
    <ul id="R_6.080.009">
        <li id="R_6.080.009ab">eṣa panthāḥ suyodhānāṃ sarvāmaragaṇeṣv api</li>
        <li id="R_6.080.009cd">yaḥ kṛte hanyate bhartuḥ sa pumān svargam ṛcchati</li>
    </ul>
    <ul id="R_6.080.010">
        <li id="R_6.080.010ab">adya devagaṇāḥ sarve lokapālās tatharṣayaḥ</li>
        <li id="R_6.080.010cd">hatam indrajitaṃ dṛṣṭvā sukhaṃ svapsyanti nirbhayāḥ</li>
    </ul>
    <ul id="R_6.080.011">
        <li id="R_6.080.011ab">adya lokās trayaḥ kṛtsnāḥ pṛthivī ca sakānanā</li>
        <li id="R_6.080.011cd">ekenendrajitā hīnā śūṇyeva pratibhāti me</li>
    </ul>
    <ul id="R_6.080.012">
        <li id="R_6.080.012ab">adya nairṛtakanyāyāṃ śroṣyāmy antaḥpure ravam</li>
        <li id="R_6.080.012cd">kareṇusaṃghasya yathā ninādaṃ girigahvare</li>
    </ul>
    <ul id="R_6.080.013">
        <li id="R_6.080.013ab">yauvarājyaṃ ca laṅkāṃ ca rakṣāṃsi ca paraṃtapa</li>
        <li id="R_6.080.013cd">mātaraṃ māṃ ca bhāryāṃ ca kva gato 'si vihāya naḥ</li>
    </ul>
    <ul id="R_6.080.014">
        <li id="R_6.080.014ab">mama nāma tvayā vīra gatasya yamasādanam</li>
        <li id="R_6.080.014cd">pretakāryāṇi kāryāṇi viparīte hi vartase</li>
    </ul>
    <ul id="R_6.080.015">
        <li id="R_6.080.015ab">sa tvaṃ jīvati sugrīve rāghave ca salakṣmaṇe</li>
        <li id="R_6.080.015cd">mama śalyam anuddhṛtya kva gato 'si vihāya naḥ</li>
    </ul>
    <ul id="R_6.080.016">
        <li id="R_6.080.016ab">evamādivilāpārtaṃ rāvaṇaṃ rākṣasādhipam</li>
        <li id="R_6.080.016cd">āviveśa mahān kopaḥ putravyasanasaṃbhavaḥ</li>
    </ul>
    <ul id="R_6.080.017">
        <li id="R_6.080.017ab">ghoraṃ prakṛtyā rūpaṃ tat tasya krodhāgnimūrchitam</li>
        <li id="R_6.080.017cd">babhūva rūpaṃ rudrasya kruddhasyeva durāsadam</li>
    </ul>
    <ul id="R_6.080.018">
        <li id="R_6.080.018ab">tasya kruddhasya netrābhyāṃ prāpatann asrabindavaḥ</li>
        <li id="R_6.080.018cd">dīptābhyām iva dīpābhyāṃ sārciṣaḥ snehabindavaḥ</li>
    </ul>
    <ul id="R_6.080.019">
        <li id="R_6.080.019ab">dantān vidaśatas tasya śrūyate daśanasvanaḥ</li>
        <li id="R_6.080.019cd">yantrasyāveṣṭyamānasya mahato dānavair iva</li>
    </ul>
    <ul id="R_6.080.020">
        <li id="R_6.080.020ab">kālāgnir iva saṃkruddho yāṃ yāṃ diśam avaikṣata</li>
        <li id="R_6.080.020cd">tasyāṃ tasyāṃ bhayatrastā rākṣasāḥ saṃnililyire</li>
    </ul>
    <ul id="R_6.080.021">
        <li id="R_6.080.021ab">tam antakam iva kruddhaṃ carācaracikhādiṣum</li>
        <li id="R_6.080.021cd">vīkṣamāṇaṃ diśaḥ sarvā rākṣasā nopacakramuḥ</li>
    </ul>
    <ul id="R_6.080.022">
        <li id="R_6.080.022ab">tataḥ paramasaṃkruddho rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.080.022cd">abravīd rakṣasāṃ madhye saṃstambhayiṣur āhave</li>
    </ul>
    <ul id="R_6.080.023">
        <li id="R_6.080.023ab">mayā varṣasahasrāṇi caritvā duścaraṃ tapaḥ</li>
        <li id="R_6.080.023cd">teṣu teṣv avakāśeṣu svayambhūḥ paritoṣitaḥ</li>
    </ul>
    <ul id="R_6.080.024">
        <li id="R_6.080.024ab">tasyaiva tapaso vyuṣṭyā prasādāc ca svayambhuvaḥ</li>
        <li id="R_6.080.024cd">nāsurebhyo na devebhyo bhayaṃ mama kadā cana</li>
    </ul>
    <ul id="R_6.080.025">
        <li id="R_6.080.025ab">kavacaṃ brahmadattaṃ me yad ādityasamaprabham</li>
        <li id="R_6.080.025cd">devāsuravimardeṣu na bhinnaṃ vajraśaktibhiḥ</li>
    </ul>
    <ul id="R_6.080.026">
        <li id="R_6.080.026ab">tena mām adya saṃyuktaṃ rathastham iha saṃyuge</li>
        <li id="R_6.080.026cd">pratīyāt ko 'dya mām ājau sākṣād api puraṃdaraḥ</li>
    </ul>
    <ul id="R_6.080.027">
        <li id="R_6.080.027ab">yat tadābhiprasannena saśaraṃ kārmukaṃ mahat</li>
        <li id="R_6.080.027cd">devāsuravimardeṣu mama dattaṃ svayambhuvā</li>
    </ul>
    <ul id="R_6.080.028">
        <li id="R_6.080.028ab">adya tūryaśatair bhīmaṃ dhanur utthāpyatāṃ mahat</li>
        <li id="R_6.080.028cd">rāmalakṣmaṇayor eva vadhāya paramāhave</li>
    </ul>
    <ul id="R_6.080.029">
        <li id="R_6.080.029ab">sa putravadhasaṃtaptaḥ śūraḥ krodhavaśaṃ gataḥ</li>
        <li id="R_6.080.029cd">samīkṣya rāvaṇo buddhyā sītāṃ hantuṃ vyavasyata</li>
    </ul>
    <ul id="R_6.080.030">
        <li id="R_6.080.030ab">pratyavekṣya tu tāmrākṣaḥ sughoro ghoradarśanān</li>
        <li id="R_6.080.030cd">dīno dīnasvarān sarvāṃs tān uvāca niśācarān</li>
    </ul>
    <ul id="R_6.080.031">
        <li id="R_6.080.031ab">māyayā mama vatsena vañcanārthaṃ vanaukasām</li>
        <li id="R_6.080.031cd">kiṃ cid eva hataṃ tatra sīteyam iti darśitam</li>
    </ul>
    <ul id="R_6.080.032">
        <li id="R_6.080.032ab">tad idaṃ satyam evāhaṃ kariṣye priyam ātmanaḥ</li>
        <li id="R_6.080.032cd">vaidehīṃ nāśayiṣyāmi kṣatrabandhum anuvratām</li>
        <li id="R_6.080.032ef">ity evam uktvā sacivān khaḍgam āśu parāmṛśat</li>
    </ul>
    <ul id="R_6.080.033">
        <li id="R_6.080.033ab">uddhṛtya guṇasaṃpannaṃ vimalāmbaravarcasaṃ</li>
        <li id="R_6.080.033cd">niṣpapāta sa vegena sabhāyāḥ sacivair vṛtaḥ</li>
    </ul>
    <ul id="R_6.080.034">
        <li id="R_6.080.034ab">rāvaṇaḥ putraśokena bhṛśam ākulacetanaḥ</li>
        <li id="R_6.080.034cd">saṃkruddhaḥ khaḍgam ādāya sahasā yatra maithilī</li>
    </ul>
    <ul id="R_6.080.035">
        <li id="R_6.080.035ab">vrajantaṃ rākṣasaṃ prekṣya siṃhanādaṃ pracukruśuḥ</li>
        <li id="R_6.080.035cd">ūcuś cānyonyam āśliṣya saṃkruddhaṃ prekṣya rākṣasāḥ</li>
    </ul>
    <ul id="R_6.080.036">
        <li id="R_6.080.036ab">adyainaṃ tāv ubhau dṛṣṭvā bhrātarau pravyathiṣyataḥ</li>
        <li id="R_6.080.036cd">lokapālā hi catvāraḥ kruddhenānena nirjitāḥ</li>
        <li id="R_6.080.036ef">bahavaḥ śatravaś cānye saṃyugeṣv abhipātitāḥ</li>
    </ul>
    <ul id="R_6.080.037">
        <li id="R_6.080.037ab">teṣāṃ saṃjalpamānānām aśokavanikāṃ gatām</li>
        <li id="R_6.080.037cd">abhidudrāva vaidehīṃ rāvaṇaḥ krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_6.080.038">
        <li id="R_6.080.038ab">vāryamāṇaḥ susaṃkruddhaḥ suhṛdbhir hitabuddhibhiḥ</li>
        <li id="R_6.080.038cd">abhyadhāvata saṃkruddhaḥ khe graho rohiṇīm iva</li>
    </ul>
    <ul id="R_6.080.039">
        <li id="R_6.080.039ab">maithilī rakṣyamāṇā tu rākṣasībhir aninditā</li>
        <li id="R_6.080.039cd">dadarśa rākṣasaṃ kruddhaṃ nistriṃśavaradhāriṇam</li>
    </ul>
    <ul id="R_6.080.040">
        <li id="R_6.080.040ab">taṃ niśāmya sanistriṃśaṃ vyathitā janakātmajā</li>
        <li id="R_6.080.040cd">nivāryamāṇaṃ bahuśaḥ suhṛdbhir anivartinam</li>
    </ul>
    <ul id="R_6.080.041">
        <li id="R_6.080.041ab">yathāyaṃ mām abhikruddhaḥ samabhidravati svayam</li>
        <li id="R_6.080.041cd">vadhiṣyati sanāthāṃ mām anāthām iva durmatiḥ</li>
    </ul>
    <ul id="R_6.080.042">
        <li id="R_6.080.042ab">bahuśaś codayām āsa bhartāraṃ mām anuvratām</li>
        <li id="R_6.080.042cd">bhāryā bhava ramasyeti pratyākhyāto 'bhavan mayā</li>
    </ul>
    <ul id="R_6.080.043">
        <li id="R_6.080.043ab">so 'yaṃ mām anupasthānād vyaktaṃ nairāśyam āgataḥ</li>
        <li id="R_6.080.043cd">krodhamohasamāviṣṭo nihantuṃ māṃ samudyataḥ</li>
    </ul>
    <ul id="R_6.080.044">
        <li id="R_6.080.044ab">atha vā tau naravyāghrau bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.080.044cd">mannimittam anāryeṇa samare 'dya nipātitau</li>
        <li id="R_6.080.044ef">aho dhin mannimitto 'yaṃ vināśo rājaputrayoḥ</li>
    </ul>
    <ul id="R_6.080.045">
        <li id="R_6.080.045ab">hanūmato hi tadvākyaṃ na kṛtaṃ kṣudrayā mayā</li>
        <li id="R_6.080.045cd">yady ahaṃ tasya pṛṣṭhena tadāyāsam aninditā</li>
        <li id="R_6.080.045ef">nādyaivam anuśoceyaṃ bhartur aṅkagatā satī</li>
    </ul>
    <ul id="R_6.080.046">
        <li id="R_6.080.046ab">manye tu hṛdayaṃ tasyāḥ kausalyāyāḥ phaliṣyati</li>
        <li id="R_6.080.046cd">ekaputrā yadā putraṃ vinaṣṭaṃ śroṣyate yudhi</li>
    </ul>
    <ul id="R_6.080.047">
        <li id="R_6.080.047ab">sā hi janma ca bālyaṃ ca yauvanaṃ ca mahātmanaḥ</li>
        <li id="R_6.080.047cd">dharmakāryāṇi rūpaṃ ca rudatī saṃsramiṣyati</li>
    </ul>
    <ul id="R_6.080.048">
        <li id="R_6.080.048ab">nirāśā nihate putre dattvā śrāddham acetanā</li>
        <li id="R_6.080.048cd">agnim ārokṣyate nūnam apo vāpi pravekṣyati</li>
    </ul>
    <ul id="R_6.080.049">
        <li id="R_6.080.049ab">dhig astu kubjām asatīṃ mantharāṃ pāpaniścayām</li>
        <li id="R_6.080.049cd">yannimittam idaṃ duḥkhaṃ kausalyā pratipatsyate</li>
    </ul>
    <ul id="R_6.080.050">
        <li id="R_6.080.050ab">ity evaṃ maithilīṃ dṛṣṭvā vilapantīṃ tapasvinīm</li>
        <li id="R_6.080.050cd">rohiṇīm iva candreṇa vinā grahavaśaṃ gatām</li>
    </ul>
    <ul id="R_6.080.051">
        <li id="R_6.080.051ab">supārśvo nāma medhāvī rāvaṇaṃ rākṣaseśvaram</li>
        <li id="R_6.080.051cd">nivāryamāṇaṃ sacivair idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.080.052">
        <li id="R_6.080.052ab">kathaṃ nāma daśagrīva sākṣād vaiśravaṇānuja</li>
        <li id="R_6.080.052cd">hantum icchasi vaidehīṃ krodhād dharmam apāsya hi</li>
    </ul>
    <ul id="R_6.080.053">
        <li id="R_6.080.053ab">veda vidyāvrata snātaḥ svadharmanirataḥ sadā</li>
        <li id="R_6.080.053cd">striyāḥ kasmād vadhaṃ vīra manyase rākṣaseśvara</li>
    </ul>
    <ul id="R_6.080.054">
        <li id="R_6.080.054ab">maithilīṃ rūpasaṃpannāṃ pratyavekṣasva pārthiva</li>
        <li id="R_6.080.054cd">tvam eva tu sahāsmābhī rāghave krodham utsṛja</li>
    </ul>
    <ul id="R_6.080.055">
        <li id="R_6.080.055ab">abhyutthānaṃ tvam adyaiva kṛṣṇapakṣacaturdaśīm</li>
        <li id="R_6.080.055cd">kṛtvā niryāhy amāvāsyāṃ vijayāya balair vṛtaḥ</li>
    </ul>
    <ul id="R_6.080.056">
        <li id="R_6.080.056ab">śūro dhīmān rathī khaḍgī rathapravaram āsthitaḥ</li>
        <li id="R_6.080.056cd">hatvā dāśarathiṃ rāmaṃ bhavān prāpsyati maithilīm</li>
    </ul>
    <ul id="R_6.080.057">
        <li id="R_6.080.057ab">
            <seg type="pāda" n="a">sa tad durātmā suhṛdā niveditaṃ</seg>
            <seg type="pāda" n="b">vacaḥ sudharmyaṃ pratigṛhya rāvaṇaḥ</seg>
        </li>
        <li id="R_6.080.057cd">
            <seg type="pāda" n="c">gṛhaṃ jagāmātha tataś ca vīryavān</seg>
            <seg type="pāda" n="d">punaḥ sabhāṃ ca prayayau suhṛdvṛtaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.081.001">
        <li id="R_6.081.001ab">sa praviśya sabhāṃ rājā dīnaḥ paramaduḥkhitaḥ</li>
        <li id="R_6.081.001cd">niṣasādāsane mukhye siṃhaḥ kruddha iva śvasan</li>
    </ul>
    <ul id="R_6.081.002">
        <li id="R_6.081.002ab">abravīc ca tadā sarvān balamukhyān mahābalaḥ</li>
        <li id="R_6.081.002cd">rāvaṇaḥ prāñjalīn vākyaṃ putravyasanakarśitaḥ</li>
    </ul>
    <ul id="R_6.081.003">
        <li id="R_6.081.003ab">sarve bhavantaḥ sarveṇa hastyaśvena samāvṛtāḥ</li>
        <li id="R_6.081.003cd">niryāntu rathasaṃghaiś ca pādātaiś copaśobhitāḥ</li>
    </ul>
    <ul id="R_6.081.004">
        <li id="R_6.081.004ab">ekaṃ rāmaṃ parikṣipya samare hantum arhatha</li>
        <li id="R_6.081.004cd">prahṛṣṭā śaravarṣeṇa prāvṛṭkāla ivāmbudāḥ</li>
    </ul>
    <ul id="R_6.081.005">
        <li id="R_6.081.005ab">atha vāhaṃ śarair tīkṣṇair bhinnagātraṃ mahāraṇe</li>
        <li id="R_6.081.005cd">bhavadbhiḥ śvo nihantāsmi rāmaṃ lokasya paśyataḥ</li>
    </ul>
    <ul id="R_6.081.006">
        <li id="R_6.081.006ab">ity evaṃ rākṣasendrasya vākyam ādāya rākṣasāḥ</li>
        <li id="R_6.081.006cd">niryayus te rathaiḥ śīghraṃ nāgānīkaiś ca saṃvṛtāḥ</li>
    </ul>
    <ul id="R_6.081.007">
        <li id="R_6.081.007ab">sa saṃgrāmo mahābhīmaḥ sūryasyodayanaṃ prati</li>
        <li id="R_6.081.007cd">rakṣasāṃ vānarāṇāṃ ca tumulaḥ samapadyata</li>
    </ul>
    <ul id="R_6.081.008">
        <li id="R_6.081.008ab">te gadābhir vicitrābhiḥ prāsaiḥ khaḍgaiḥ paraśvadhaiḥ</li>
        <li id="R_6.081.008cd">anyonyaṃ samare jaghnus tadā vānararākṣasāḥ</li>
    </ul>
    <ul id="R_6.081.009">
        <li id="R_6.081.009ab">mātaṃgarathakūlasya vājimatsyā dhvajadrumāḥ</li>
        <li id="R_6.081.009cd">śarīrasaṃghāṭavahāḥ prasasruḥ śoṇitāpagāḥ</li>
    </ul>
    <ul id="R_6.081.010">
        <li id="R_6.081.010ab">dhvajavarmarathān aśvān nānāpraharaṇāni ca</li>
        <li id="R_6.081.010cd">āplutyāplutya samare vānarendrā babhañjire</li>
    </ul>
    <ul id="R_6.081.011">
        <li id="R_6.081.011ab">keśān karṇalalāṭāṃś ca nāsikāś ca plavaṃgamāḥ</li>
        <li id="R_6.081.011cd">rakṣasāṃ daśanais tīkṣṇair nakhaiś cāpi vyakartayan</li>
    </ul>
    <ul id="R_6.081.012">
        <li id="R_6.081.012ab">ekaikaṃ rākṣasaṃ saṃkhye śataṃ vānarapuṃgavāḥ</li>
        <li id="R_6.081.012cd">abhyadhāvanta phalinaṃ vṛkṣaṃ śakunayo yathā</li>
    </ul>
    <ul id="R_6.081.013">
        <li id="R_6.081.013ab">tathā gadābhir gurvībhiḥ prāsaiḥ khaḍgaiḥ paraśvadhaiḥ</li>
        <li id="R_6.081.013cd">nirjaghnur vānarān ghorān rākṣasāḥ parvatopamāḥ</li>
    </ul>
    <ul id="R_6.081.014">
        <li id="R_6.081.014ab">rākṣasair vadhyamānānāṃ vānarāṇāṃ mahācamūḥ</li>
        <li id="R_6.081.014cd">śaraṇyaṃ śaraṇaṃ yātā rāmaṃ daśarathātmajam</li>
    </ul>
    <ul id="R_6.081.015">
        <li id="R_6.081.015ab">tato rāmo mahātejā dhanur ādāya vīryavān</li>
        <li id="R_6.081.015cd">praviśya rākṣasaṃ sainyaṃ śaravarṣaṃ vavarṣa ha</li>
    </ul>
    <ul id="R_6.081.016">
        <li id="R_6.081.016ab">praviṣṭaṃ tu tadā rāmaṃ meghāḥ sūryam ivāmbare</li>
        <li id="R_6.081.016cd">nābhijagmur mahāghoraṃ nirdahantaṃ śarāgninā</li>
    </ul>
    <ul id="R_6.081.017">
        <li id="R_6.081.017ab">kṛtāny eva sughorāṇi rāmeṇa rajanīcarāḥ</li>
        <li id="R_6.081.017cd">raṇe rāmasya dadṛśuḥ karmāṇy asukarāṇi ca</li>
    </ul>
    <ul id="R_6.081.018">
        <li id="R_6.081.018ab">cālayantaṃ mahānīkaṃ vidhamantaṃ mahārathān</li>
        <li id="R_6.081.018cd">dadṛśus te na vai rāmaṃ vātaṃ vanagataṃ yathā</li>
    </ul>
    <ul id="R_6.081.019">
        <li id="R_6.081.019ab">chinnaṃ bhinnaṃ śarair dagdhaṃ prabhagnaṃ śastrapīḍitam</li>
        <li id="R_6.081.019cd">balaṃ rāmeṇa dadṛśur na ramaṃ śīghrakāriṇam</li>
    </ul>
    <ul id="R_6.081.020">
        <li id="R_6.081.020ab">praharantaṃ śarīreṣu na te paśyanti rāghavam</li>
        <li id="R_6.081.020cd">indriyārtheṣu tiṣṭhantaṃ bhūtātmānam iva prajāḥ</li>
    </ul>
    <ul id="R_6.081.021">
        <li id="R_6.081.021ab">eṣa hanti gajānīkam eṣa hanti mahārathān</li>
        <li id="R_6.081.021cd">eṣa hanti śarais tīkṣṇaiḥ padātīn vājibhiḥ saha</li>
    </ul>
    <ul id="R_6.081.022">
        <li id="R_6.081.022ab">iti te rākṣasāḥ sarve rāmasya sadṛśān raṇe</li>
        <li id="R_6.081.022cd">anyonyakupitā jaghnuḥ sādṛśyād rāghavasya te</li>
    </ul>
    <ul id="R_6.081.023">
        <li id="R_6.081.023ab">na te dadṛśire rāmaṃ dahantam arivāhinīm</li>
        <li id="R_6.081.023cd">mohitāḥ paramāstreṇa gāndharveṇa mahātmanā</li>
    </ul>
    <ul id="R_6.081.024">
        <li id="R_6.081.024ab">te tu rāmasahasrāṇi raṇe paśyanti rākṣasāḥ</li>
        <li id="R_6.081.024cd">punaḥ paśyanti kākutstham ekam eva mahāhave</li>
    </ul>
    <ul id="R_6.081.025">
        <li id="R_6.081.025ab">bhramantīṃ kāñcanīṃ koṭiṃ kārmukasya mahātmanaḥ</li>
        <li id="R_6.081.025cd">alātacakrapratimāṃ dadṛśus te na rāghavam</li>
    </ul>
    <ul id="R_6.081.026">
        <li id="R_6.081.026ab">śarīranābhi sattvārciḥ śarīraṃ nemikārmukam</li>
        <li id="R_6.081.026cd">jyāghoṣatalanirghoṣaṃ tejobuddhiguṇaprabham</li>
    </ul>
    <ul id="R_6.081.027">
        <li id="R_6.081.027ab">divyāstraguṇaparyantaṃ nighnantaṃ yudhi rākṣasān</li>
        <li id="R_6.081.027cd">dadṛśū rāmacakraṃ tat kālacakram iva prajāḥ</li>
    </ul>
    <ul id="R_6.081.028">
        <li id="R_6.081.028ab">anīkaṃ daśasāhasraṃ rathānāṃ vātaraṃhasām</li>
        <li id="R_6.081.028cd">aṣṭādaśasahasrāṇi kuñjarāṇāṃ tarasvinām</li>
    </ul>
    <ul id="R_6.081.029">
        <li id="R_6.081.029ab">caturdaśasahasrāṇi sārohāṇāṃ ca vājinām</li>
        <li id="R_6.081.029cd">pūrṇe śatasahasre dve rākṣasānāṃ padātinām</li>
    </ul>
    <ul id="R_6.081.030">
        <li id="R_6.081.030ab">divasasyāṣṭame bhāge śarair agniśikhopamaiḥ</li>
        <li id="R_6.081.030cd">hatāny ekena rāmeṇa rakṣasāṃ kāmarūpiṇām</li>
    </ul>
    <ul id="R_6.081.031">
        <li id="R_6.081.031ab">te hatāśvā hatarathāḥ śrāntā vimathitadhvajāḥ</li>
        <li id="R_6.081.031cd">abhipetuḥ purīṃ laṅkāṃ hataśeṣā niśācarāḥ</li>
    </ul>
    <ul id="R_6.081.032">
        <li id="R_6.081.032ab">hatair gajapadāty aśvais tad babhūva raṇājiram</li>
        <li id="R_6.081.032cd">ākrīḍabhūmī rudrasya kruddhasyeva pinākinaḥ</li>
    </ul>
    <ul id="R_6.081.033">
        <li id="R_6.081.033ab">tato devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_6.081.033cd">sādhu sādhv iti rāmasya tat karma samapūjayan</li>
    </ul>
    <ul id="R_6.081.034">
        <li id="R_6.081.034ab">abravīc ca tadā rāmaḥ sugrīvaṃ pratyanantaram</li>
        <li id="R_6.081.034cd">etad astrabalaṃ divyaṃ mama vā tryambakasya vā</li>
    </ul>
    <ul id="R_6.081.035">
        <li id="R_6.081.035ab">
            <seg type="pāda" n="a">nihatya tāṃ rākṣasavāhinīṃ tu</seg>
            <seg type="pāda" n="b">rāmas tadā śakrasamo mahātmā</seg>
        </li>
        <li id="R_6.081.035cd">
            <seg type="pāda" n="c">astreṣu śastreṣu jitaklamaś ca</seg>
            <seg type="pāda" n="d">saṃstūyate devagaṇaiḥ prahṛṣṭaiḥ</seg>
        </li>
    </ul>
    <ul id="R_6.082.001">
        <li id="R_6.082.001ab">tāni nāgasahasrāṇi sārohāṇāṃ ca vājinām</li>
        <li id="R_6.082.001cd">rathānāṃ cāgnivarṇānāṃ sadhvajānāṃ sahasraśaḥ</li>
    </ul>
    <ul id="R_6.082.002">
        <li id="R_6.082.002ab">rākṣasānāṃ sahasrāṇi gadāparighayodhinām</li>
        <li id="R_6.082.002cd">kāñcanadhvajacitrāṇāṃ śūrāṇāṃ kāmarūpiṇām</li>
    </ul>
    <ul id="R_6.082.003">
        <li id="R_6.082.003ab">nihatāni śarais tīkṣṇais taptakāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.082.003cd">rāvaṇena prayuktāni rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_6.082.004">
        <li id="R_6.082.004ab">dṛṣṭvā śrutvā ca saṃbhrāntā hataśeṣā niśācarāḥ</li>
        <li id="R_6.082.004cd">rākṣasyaś ca samāgamya dīnāś cintāpariplutāḥ</li>
    </ul>
    <ul id="R_6.082.005">
        <li id="R_6.082.005ab">vidhavā hataputrāś ca krośantyo hatabāndhavāḥ</li>
        <li id="R_6.082.005cd">rākṣasyaḥ saha saṃgamya duḥkhārtāḥ paryadevayan</li>
    </ul>
    <ul id="R_6.082.006">
        <li id="R_6.082.006ab">kathaṃ śūrpaṇakhā vṛddhā karālā nirṇatodarī</li>
        <li id="R_6.082.006cd">āsasāda vane rāmaṃ kandarpam iva rūpiṇam</li>
    </ul>
    <ul id="R_6.082.007">
        <li id="R_6.082.007ab">sukumāraṃ mahāsattvaṃ sarvabhūtahite ratam</li>
        <li id="R_6.082.007cd">taṃ dṛṣṭvā lokavadhyā sā hīnarūpā prakāmitā</li>
    </ul>
    <ul id="R_6.082.008">
        <li id="R_6.082.008ab">kathaṃ sarvaguṇair hīnā guṇavantaṃ mahaujasaṃ</li>
        <li id="R_6.082.008cd">sumukhaṃ durmukhī rāmaṃ kāmayām āsa rākṣasī</li>
    </ul>
    <ul id="R_6.082.009">
        <li id="R_6.082.009ab">janasyāsyālpabhāgyatvāt palinī śvetamūrdhajā</li>
        <li id="R_6.082.009cd">akāryam apahāsyaṃ ca sarvalokavigarhitam</li>
    </ul>
    <ul id="R_6.082.010">
        <li id="R_6.082.010ab">rākṣasānāṃ vināśāya dūṣaṇasya kharasya ca</li>
        <li id="R_6.082.010cd">cakārāpratirūpā sā rāghavasya pradharṣaṇam</li>
    </ul>
    <ul id="R_6.082.011">
        <li id="R_6.082.011ab">tan nimittam idaṃ vairaṃ rāvaṇena kṛtaṃ mahat</li>
        <li id="R_6.082.011cd">vadhāya nītā sā sītā daśagrīveṇa rakṣasā</li>
    </ul>
    <ul id="R_6.082.012">
        <li id="R_6.082.012ab">na ca sītāṃ daśagrīvaḥ prāpnoti janakātmajām</li>
        <li id="R_6.082.012cd">baddhaṃ balavatā vairam akṣayaṃ rāghaveṇa ha</li>
    </ul>
    <ul id="R_6.082.013">
        <li id="R_6.082.013ab">vaidehīṃ prārthayānaṃ taṃ virādhaṃ prekṣya rākṣasaṃ</li>
        <li id="R_6.082.013cd">hatam ekena rāmeṇa paryāptaṃ tannidarśanam</li>
    </ul>
    <ul id="R_6.082.014">
        <li id="R_6.082.014ab">caturdaśasahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_6.082.014cd">nihatāni janasthāne śarair agniśikhopamaiḥ</li>
    </ul>
    <ul id="R_6.082.015">
        <li id="R_6.082.015ab">kharaś ca nihataḥ saṃkhye dūṣaṇas triśirās tathā</li>
        <li id="R_6.082.015cd">śarair ādityasaṃkāśaiḥ paryāptaṃ tannidarśanam</li>
    </ul>
    <ul id="R_6.082.016">
        <li id="R_6.082.016ab">hato yojanabāhuś ca kabandho rudhirāśanaḥ</li>
        <li id="R_6.082.016cd">krodhārto vinadan so 'tha paryāptaṃ tannidarśanam</li>
    </ul>
    <ul id="R_6.082.017">
        <li id="R_6.082.017ab">jaghāna balinaṃ rāmaḥ sahasranayanātmajam</li>
        <li id="R_6.082.017cd">bālinaṃ meghasaṃkāśaṃ paryāptaṃ tannidarśanam</li>
    </ul>
    <ul id="R_6.082.018">
        <li id="R_6.082.018ab">ṛśyamūke vasañ śaile dīno bhagnamanorathaḥ</li>
        <li id="R_6.082.018cd">sugrīvaḥ sthāpito rājye paryāptaṃ tannidarśanam</li>
    </ul>
    <ul id="R_6.082.019">
        <li id="R_6.082.019ab">dharmārthasahitaṃ vākyaṃ sarveṣāṃ rakṣasāṃ hitam</li>
        <li id="R_6.082.019cd">yuktaṃ vibhīṣaṇenoktaṃ mohāt tasya na rocate</li>
    </ul>
    <ul id="R_6.082.020">
        <li id="R_6.082.020ab">vibhīṣaṇavacaḥ kuryād yadi sma dhanadānujaḥ</li>
        <li id="R_6.082.020cd">śmaśānabhūtā duḥkhārtā neyaṃ laṅkā purī bhavet</li>
    </ul>
    <ul id="R_6.082.021">
        <li id="R_6.082.021ab">kumbhakarṇaṃ hataṃ śrutvā rāghaveṇa mahābalam</li>
        <li id="R_6.082.021cd">priyaṃ cendrajitaṃ putraṃ rāvaṇo nāvabudhyate</li>
    </ul>
    <ul id="R_6.082.022">
        <li id="R_6.082.022ab">mama putro mama bhrātā mama bhartā raṇe hataḥ</li>
        <li id="R_6.082.022cd">ity evaṃ śrūyate śabdo rākṣasānāṃ kule kule</li>
    </ul>
    <ul id="R_6.082.023">
        <li id="R_6.082.023ab">rathāś cāśvāś ca nāgāś ca hatāḥ śatasahasraśaḥ</li>
        <li id="R_6.082.023cd">raṇe rāmeṇa śūreṇa rākṣasāś ca padātayaḥ</li>
    </ul>
    <ul id="R_6.082.024">
        <li id="R_6.082.024ab">rudro vā yadi vā viṣṇur mahendro vā śatakratuḥ</li>
        <li id="R_6.082.024cd">hanti no rāmarūpeṇa yadi vā svayam antakaḥ</li>
    </ul>
    <ul id="R_6.082.025">
        <li id="R_6.082.025ab">hatapravīrā rāmeṇa nirāśā jīvite vayam</li>
        <li id="R_6.082.025cd">apaśyantyo bhayasyāntam anāthā vilapāmahe</li>
    </ul>
    <ul id="R_6.082.026">
        <li id="R_6.082.026ab">rāmahastād daśagrīvaḥ śūro dattavaro yudhi</li>
        <li id="R_6.082.026cd">idaṃ bhayaṃ mahāghoram utpannaṃ nāvabudhyate</li>
    </ul>
    <ul id="R_6.082.027">
        <li id="R_6.082.027ab">na devā na ca gandharvā na piśācā na rākṣasāḥ</li>
        <li id="R_6.082.027cd">upasṛṣṭaṃ paritrātuṃ śaktā rāmeṇa saṃyuge</li>
    </ul>
    <ul id="R_6.082.028">
        <li id="R_6.082.028ab">utpātāś cāpi dṛśyante rāvaṇasya raṇe raṇe</li>
        <li id="R_6.082.028cd">kathayiṣyanti rāmeṇa rāvaṇasya nibarhaṇam</li>
    </ul>
    <ul id="R_6.082.029">
        <li id="R_6.082.029ab">pitāmahena prītena devadānavarākṣasaiḥ</li>
        <li id="R_6.082.029cd">rāvaṇasyābhayaṃ dattaṃ mānuṣebhyo na yācitam</li>
    </ul>
    <ul id="R_6.082.030">
        <li id="R_6.082.030ab">tad idaṃ mānuṣān manye prāptaṃ niḥsaṃśayaṃ bhayam</li>
        <li id="R_6.082.030cd">jīvitāntakaraṃ ghoraṃ rakṣasāṃ rāvaṇasya ca</li>
    </ul>
    <ul id="R_6.082.031">
        <li id="R_6.082.031ab">pīḍyamānās tu balinā varadānena rakṣasā</li>
        <li id="R_6.082.031cd">dīptais tapobhir vibudhāḥ pitāmaham apūjayan</li>
    </ul>
    <ul id="R_6.082.032">
        <li id="R_6.082.032ab">devatānāṃ hitārthāya mahātmā vai pitāmahaḥ</li>
        <li id="R_6.082.032cd">uvāca devatāḥ sarvā idaṃ tuṣṭo mahad vacaḥ</li>
    </ul>
    <ul id="R_6.082.033">
        <li id="R_6.082.033ab">adya prabhṛti lokāṃs trīn sarve dānavarākṣasāḥ</li>
        <li id="R_6.082.033cd">bhayena prāvṛtā nityaṃ vicariṣyanti śāśvatam</li>
    </ul>
    <ul id="R_6.082.034">
        <li id="R_6.082.034ab">daivatais tu samāgamya sarvaiś cendrapurogamaiḥ</li>
        <li id="R_6.082.034cd">vṛṣadhvajas tripurahā mahādevaḥ prasāditaḥ</li>
    </ul>
    <ul id="R_6.082.035">
        <li id="R_6.082.035ab">prasannas tu mahādevo devān etad vaco 'bravīt</li>
        <li id="R_6.082.035cd">utpatsyati hitārthaṃ vo nārī rakṣaḥkṣayāvahā</li>
    </ul>
    <ul id="R_6.082.036">
        <li id="R_6.082.036ab">eṣā devaiḥ prayuktā tu kṣud yathā dānavān purā</li>
        <li id="R_6.082.036cd">bhakṣayiṣyati naḥ sītā rākṣasaghnī sarāvaṇān</li>
    </ul>
    <ul id="R_6.082.037">
        <li id="R_6.082.037ab">rāvaṇasyāpanītena durvinītasya durmateḥ</li>
        <li id="R_6.082.037cd">ayaṃ niṣṭānako ghoraḥ śokena samabhiplutaḥ</li>
    </ul>
    <ul id="R_6.082.038">
        <li id="R_6.082.038ab">taṃ na paśyāmahe loke yo naḥ śaraṇado bhavet</li>
        <li id="R_6.082.038cd">rāghaveṇopasṛṣṭānāṃ kāleneva yugakṣaye</li>
    </ul>
    <ul id="R_6.082.039">
        <li id="R_6.082.039ab">
            <seg type="pāda" n="a">itīva sarvā rajanīcarastriyaḥ</seg>
            <seg type="pāda" n="b">parasparaṃ saṃparirabhya bāhubhiḥ</seg>
        </li>
        <li id="R_6.082.039cd">
            <seg type="pāda" n="c">viṣedur ārtātibhayābhipīḍitā</seg>
            <seg type="pāda" n="d">vinedur uccaiś ca tadā sudāruṇam</seg>
        </li>
    </ul>
    <ul id="R_6.083.001">
        <li id="R_6.083.001ab">ārtānāṃ rākṣasīnāṃ tu laṅkāyāṃ vai kule kule</li>
        <li id="R_6.083.001cd">rāvaṇaḥ karuṇaṃ śabdaṃ śuśrāva pariveditam</li>
    </ul>
    <ul id="R_6.083.002">
        <li id="R_6.083.002ab">sa tu dīrghaṃ viniśvasya muhūrtaṃ dhyānam āsthitaḥ</li>
        <li id="R_6.083.002cd">babhūva paramakruddho rāvaṇo bhīmadarśanaḥ</li>
    </ul>
    <ul id="R_6.083.003">
        <li id="R_6.083.003ab">saṃdaśya daśanair oṣṭhaṃ krodhasaṃraktalocanaḥ</li>
        <li id="R_6.083.003cd">rākṣasair api durdarśaḥ kālāgnir iva mūrchitaḥ</li>
    </ul>
    <ul id="R_6.083.004">
        <li id="R_6.083.004ab">uvāca ca samīpasthān rākṣasān rākṣaseśvaraḥ</li>
        <li id="R_6.083.004cd">bhayāvyaktakathāṃs tatra nirdahann iva cakṣuṣā</li>
    </ul>
    <ul id="R_6.083.005">
        <li id="R_6.083.005ab">mahodaraṃ mahāpārśvaṃ virūpākṣaṃ ca rākṣasaṃ</li>
        <li id="R_6.083.005cd">śīghraṃ vadata sainyāni niryāteti mamājñayā</li>
    </ul>
    <ul id="R_6.083.006">
        <li id="R_6.083.006ab">tasya tad vacanaṃ śrutvā rākṣasās te bhayārditāḥ</li>
        <li id="R_6.083.006cd">codayām āsur avyagrān rākṣasāṃs tān nṛpājñayā</li>
    </ul>
    <ul id="R_6.083.007">
        <li id="R_6.083.007ab">te tu sarve tathety uktvā rākṣasā ghoradarśanāḥ</li>
        <li id="R_6.083.007cd">kṛtasvastyayanāḥ sarve rāvaṇābhimukhā yayuḥ</li>
    </ul>
    <ul id="R_6.083.008">
        <li id="R_6.083.008ab">pratipūjya yathānyāyaṃ rāvaṇaṃ te mahārathāḥ</li>
        <li id="R_6.083.008cd">tasthuḥ prāñjalayaḥ sarve bhartur vijayakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.083.009">
        <li id="R_6.083.009ab">athovāca prahasyaitān rāvaṇaḥ krodhamūrchitaḥ</li>
        <li id="R_6.083.009cd">mahodaramahāpārśvau virūpākṣaṃ ca rākṣasaṃ</li>
    </ul>
    <ul id="R_6.083.010">
        <li id="R_6.083.010ab">adya bāṇair dhanurmuktair yugāntādityasaṃnibhaiḥ</li>
        <li id="R_6.083.010cd">rāghavaṃ lakṣmaṇaṃ caiva neṣyāmi yamasādhanam</li>
    </ul>
    <ul id="R_6.083.011">
        <li id="R_6.083.011ab">kharasya kumbhakarṇasya prahastendrajitos tathā</li>
        <li id="R_6.083.011cd">kariṣyāmi pratīkāram adya śatruvadhād aham</li>
    </ul>
    <ul id="R_6.083.012">
        <li id="R_6.083.012ab">naivāntarikṣaṃ na diśo na nadyo nāpi sāgaraḥ</li>
        <li id="R_6.083.012cd">prakāśatvaṃ gamiṣyanti madbāṇajaladāvṛtāḥ</li>
    </ul>
    <ul id="R_6.083.013">
        <li id="R_6.083.013ab">adya vānarayūthānāṃ tāni yūthāni bhāgaśaḥ</li>
        <li id="R_6.083.013cd">dhanuḥsamudrād udbhūtair mathiṣyāmi śarormibhiḥ</li>
    </ul>
    <ul id="R_6.083.014">
        <li id="R_6.083.014ab">vyākośapadmacakrāṇi padmakesaravarcasām</li>
        <li id="R_6.083.014cd">adya yūthataṭākāni gajavat pramathāmy aham</li>
    </ul>
    <ul id="R_6.083.015">
        <li id="R_6.083.015ab">saśarair adya vadanaiḥ saṃkhye vānarayūthapāḥ</li>
        <li id="R_6.083.015cd">maṇḍayiṣyanti vasudhāṃ sanālair iva paṅkalaiḥ</li>
    </ul>
    <ul id="R_6.083.016">
        <li id="R_6.083.016ab">adya yuddhapracaṇḍānāṃ harīṇāṃ drumayodhinām</li>
        <li id="R_6.083.016cd">muktenaikeṣuṇā yuddhe bhetsyāmi ca śataṃśatam</li>
    </ul>
    <ul id="R_6.083.017">
        <li id="R_6.083.017ab">hato bhartā hato bhrātā yāsāṃ ca tanayā hatāḥ</li>
        <li id="R_6.083.017cd">vadhenādya ripos tāsāṃ karmomy asrapramārjanam</li>
    </ul>
    <ul id="R_6.083.018">
        <li id="R_6.083.018ab">adya madbāṇanirbhinnaiḥ prakīrṇair gatacetanaiḥ</li>
        <li id="R_6.083.018cd">karomi vānarair yuddhe yatnāvekṣyatalāṃ mahīm</li>
    </ul>
    <ul id="R_6.083.019">
        <li id="R_6.083.019ab">adya gomāyavo gṛdhrā ye ca māṃsāśino 'pare</li>
        <li id="R_6.083.019cd">sarvāṃs tāṃs tarpayiṣyāmi śatrumāṃsaiḥ śarārditaiḥ</li>
    </ul>
    <ul id="R_6.083.020">
        <li id="R_6.083.020ab">kalpyatāṃ me rathaśīghraṃ kṣipram ānīyatāṃ dhanuḥ</li>
        <li id="R_6.083.020cd">anuprayāntu māṃ yuddhe ye 'vaśiṣṭā niśācarāḥ</li>
    </ul>
    <ul id="R_6.083.021">
        <li id="R_6.083.021ab">tasya tad vacanaṃ śrutvā mahāpārśvo 'bravīd vacaḥ</li>
        <li id="R_6.083.021cd">balādhyakṣān sthitāṃs tatra balaṃ saṃtvaryatām iti</li>
    </ul>
    <ul id="R_6.083.022">
        <li id="R_6.083.022ab">balādhyakṣās tu saṃrabdhā rākṣasāṃs tān gṛhād gṛhāt</li>
        <li id="R_6.083.022cd">codayantaḥ pariyayur laṅkāṃ laghuparākramāḥ</li>
    </ul>
    <ul id="R_6.083.023">
        <li id="R_6.083.023ab">tato muhūrtān niṣpetū rākṣasā bhīmavikramāḥ</li>
        <li id="R_6.083.023cd">nardanto bhīmavadanā nānāpraharaṇair bhujaiḥ</li>
    </ul>
    <ul id="R_6.083.024">
        <li id="R_6.083.024ab">asibhiḥ paṭṭasaiḥ śūlair galābhir musalair halaiḥ</li>
        <li id="R_6.083.024cd">śaktibhis tīkṣṇadhārābhir mahadbhiḥ kūṭamudgaraiḥ</li>
    </ul>
    <ul id="R_6.083.025">
        <li id="R_6.083.025ab">yaṣṭibhir vimalaiś cakrair niśitaiś ca paraśvadhaiḥ</li>
        <li id="R_6.083.025cd">bhiṇḍipālaiḥ śataghnībhir anyaiś cāpi varāyudhaiḥ</li>
    </ul>
    <ul id="R_6.083.026">
        <li id="R_6.083.026ab">athānayan balādhyakṣāś catvāro rāvaṇājñayā</li>
        <li id="R_6.083.026cd">drutaṃ sūtasamāyuktaṃ yuktāṣṭaturagaṃ ratham</li>
    </ul>
    <ul id="R_6.083.027">
        <li id="R_6.083.027ab">āruroha rathaṃ divyaṃ dīpyamānaṃ svatejasā</li>
        <li id="R_6.083.027cd">rāvaṇaḥ sattvagāmbhīryād dārayann iva medinīm</li>
    </ul>
    <ul id="R_6.083.028">
        <li id="R_6.083.028ab">rāvaṇenābhyanujñātau mahāpārśvamahodarau</li>
        <li id="R_6.083.028cd">virūpākṣaś ca durdharṣo rathān āruruhus tadā</li>
    </ul>
    <ul id="R_6.083.029">
        <li id="R_6.083.029ab">te tu hṛṣṭā vinardanto bhindanta iva medinīm</li>
        <li id="R_6.083.029cd">nādaṃ ghoraṃ vimuñcanto niryayur jayakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.083.030">
        <li id="R_6.083.030ab">tato yuddhāya tejasvī rakṣogaṇabalair vṛtaḥ</li>
        <li id="R_6.083.030cd">niryayāv udyatadhanuḥ kālāntakayamomapaḥ</li>
    </ul>
    <ul id="R_6.083.031">
        <li id="R_6.083.031ab">tataḥ prajavanāśvena rathena sa mahārathaḥ</li>
        <li id="R_6.083.031cd">dvāreṇa niryayau tena yatra tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.083.032">
        <li id="R_6.083.032ab">tato naṣṭaprabhaḥ sūryo diśaś ca timirāvṛtāḥ</li>
        <li id="R_6.083.032cd">dvijāś ca nedur ghorāś ca saṃcacāla ca medinī</li>
    </ul>
    <ul id="R_6.083.033">
        <li id="R_6.083.033ab">vavarṣa rudhiraṃ devaś caskhaluś ca turaṃgamāḥ</li>
        <li id="R_6.083.033cd">dhvajāgre nyapatad gṛdhro vineduś cāśivaṃ śivāḥ</li>
    </ul>
    <ul id="R_6.083.034">
        <li id="R_6.083.034ab">nayanaṃ cāsphurad vāmaṃ savyo bāhur akampata</li>
        <li id="R_6.083.034cd">vivarṇavadanaś cāsīt kiṃ cid abhraśyata svaraḥ</li>
    </ul>
    <ul id="R_6.083.035">
        <li id="R_6.083.035ab">tato niṣpatato yuddhe daśagrīvasya rakṣasaḥ</li>
        <li id="R_6.083.035cd">raṇe nidhanaśaṃsīni rūpāṇy etāni jajñire</li>
    </ul>
    <ul id="R_6.083.036">
        <li id="R_6.083.036ab">antarikṣāt papātolkā nirghātasamanisvanā</li>
        <li id="R_6.083.036cd">vinedur aśivaṃ gṛdhrā vāyasair anunāditāḥ</li>
    </ul>
    <ul id="R_6.083.037">
        <li id="R_6.083.037ab">etān acintayan ghorān utpātān samupasthitān</li>
        <li id="R_6.083.037cd">niryayau rāvaṇo mohād vadhārthī kālacoditaḥ</li>
    </ul>
    <ul id="R_6.083.038">
        <li id="R_6.083.038ab">teṣāṃ tu rathaghoṣeṇa rākṣasānāṃ mahātmanām</li>
        <li id="R_6.083.038cd">vānarāṇām api camūr yuddhāyaivābhyavartata</li>
    </ul>
    <ul id="R_6.083.039">
        <li id="R_6.083.039ab">teṣāṃ sutumulaṃ yuddhaṃ babhūva kapirakṣasām</li>
        <li id="R_6.083.039cd">anyonyam āhvayānānāṃ kruddhānāṃ jayam icchatām</li>
    </ul>
    <ul id="R_6.083.040">
        <li id="R_6.083.040ab">tataḥ kruddho daśagrīvaḥ śaraiḥ kāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.083.040cd">vānarāṇām anīkeṣu cakāra kadanaṃ mahat</li>
    </ul>
    <ul id="R_6.083.041">
        <li id="R_6.083.041ab">nikṛttaśirasaḥ ke cid rāvaṇena valīmukhāḥ</li>
        <li id="R_6.083.041cd">nirucchvāsā hatāḥ ke cit ke cit pārśveṣu dāritāḥ</li>
        <li id="R_6.083.041ef">ke cid vibhinnaśirasaḥ ke cic cakṣurvivarjitāḥ</li>
    </ul>
    <ul id="R_6.083.042">
        <li id="R_6.083.042ab">
            <seg type="pāda" n="a">daśānanaḥ krodhavivṛttanetro</seg>
            <seg type="pāda" n="b">yato yato 'bhyeti rathena saṃkhye</seg>
        </li>
        <li id="R_6.083.042cd">
            <seg type="pāda" n="c">tatas tatas tasya śarapravegaṃ</seg>
            <seg type="pāda" n="d">soḍhuṃ na śekur hariyūthapās te</seg>
        </li>
    </ul>
    <ul id="R_6.084.001">
        <li id="R_6.084.001ab">tathā taiḥ kṛttagātrais tu daśagrīveṇa mārgaṇaiḥ</li>
        <li id="R_6.084.001cd">babhūva vasudhā tatra prakīrṇā haribhir vṛtā</li>
    </ul>
    <ul id="R_6.084.002">
        <li id="R_6.084.002ab">rāvaṇasyāprasahyaṃ taṃ śarasaṃpātam ekataḥ</li>
        <li id="R_6.084.002cd">na śekuḥ sahituṃ dīptaṃ pataṃgā iva pāvakam</li>
    </ul>
    <ul id="R_6.084.003">
        <li id="R_6.084.003ab">te 'rditā niśitair bāṇaiḥ krośanto vipradudruvuḥ</li>
        <li id="R_6.084.003cd">pāvakārciḥsamāviṣṭā dahyamānā yathā gajāḥ</li>
    </ul>
    <ul id="R_6.084.004">
        <li id="R_6.084.004ab">plavaṃgānām anīkāni mahābhrāṇīva mārutaḥ</li>
        <li id="R_6.084.004cd">sa yayau samare tasmin vidhaman rāvaṇaḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.084.005">
        <li id="R_6.084.005ab">kadanaṃ tarasā kṛtvā rākṣasendro vanaukasām</li>
        <li id="R_6.084.005cd">āsasāda tato yuddhe rāghavaṃ tvaritas tadā</li>
    </ul>
    <ul id="R_6.084.006">
        <li id="R_6.084.006ab">sugrīvas tān kapīn dṛṣṭvā bhagnān vidravato raṇe</li>
        <li id="R_6.084.006cd">gulme suṣeṇaṃ nikṣipya cakre yuddhe drutaṃ manaḥ</li>
    </ul>
    <ul id="R_6.084.007">
        <li id="R_6.084.007ab">ātmanaḥ sadṛśaṃ vīraṃ sa taṃ nikṣipya vānaram</li>
        <li id="R_6.084.007cd">sugrīvo 'bhimukhaḥ śatruṃ pratasthe pādapāyudhaḥ</li>
    </ul>
    <ul id="R_6.084.008">
        <li id="R_6.084.008ab">pārśvataḥ pṛṣṭhataś cāsya sarve yūthādhipāḥ svayam</li>
        <li id="R_6.084.008cd">anujahrur mahāśailān vividhāṃś ca mahādrumān</li>
    </ul>
    <ul id="R_6.084.009">
        <li id="R_6.084.009ab">sa nadan yudhi sugrīvaḥ svareṇa mahatā mahān</li>
        <li id="R_6.084.009cd">pātayan vividhāṃś cānyāñ jaghānottamarākṣasān</li>
    </ul>
    <ul id="R_6.084.010">
        <li id="R_6.084.010ab">mamarda ca mahākāyo rākṣasān vānareśvaraḥ</li>
        <li id="R_6.084.010cd">yugāntasamaye vāyuḥ pravṛddhān agamān iva</li>
    </ul>
    <ul id="R_6.084.011">
        <li id="R_6.084.011ab">rākṣasānām anīkeṣu śailavarṣaṃ vavarṣa ha</li>
        <li id="R_6.084.011cd">aśmavarṣaṃ yathā meghaḥ pakṣisaṃgheṣu kānane</li>
    </ul>
    <ul id="R_6.084.012">
        <li id="R_6.084.012ab">kapirājavimuktais taiḥ śailavarṣais tu rākṣasāḥ</li>
        <li id="R_6.084.012cd">vikīrṇaśirasaḥ petur nikṛttā iva parvatāḥ</li>
    </ul>
    <ul id="R_6.084.013">
        <li id="R_6.084.013ab">atha saṃkṣīyamāṇeṣu rākṣaseṣu samantataḥ</li>
        <li id="R_6.084.013cd">sugrīveṇa prabhagneṣu patatsu vinadatsu ca</li>
    </ul>
    <ul id="R_6.084.014">
        <li id="R_6.084.014ab">virūpākṣaḥ svakaṃ nāma dhanvī viśrāvya rākṣasaḥ</li>
        <li id="R_6.084.014cd">rathād āplutya durdharṣo gajaskandham upāruhat</li>
    </ul>
    <ul id="R_6.084.015">
        <li id="R_6.084.015ab">sa taṃ dviradam āruhya virūpākṣo mahārathaḥ</li>
        <li id="R_6.084.015cd">vinadan bhīmanirhrālaṃ vānarān abhyadhāvata</li>
    </ul>
    <ul id="R_6.084.016">
        <li id="R_6.084.016ab">sugrīve sa śarān ghorān visasarja camūmukhe</li>
        <li id="R_6.084.016cd">sthāpayām āsā codvignān rākṣasān saṃpraharṣayan</li>
    </ul>
    <ul id="R_6.084.017">
        <li id="R_6.084.017ab">so 'tividdhaḥ śitair bāṇaiḥ kapīndras tena rakṣasā</li>
        <li id="R_6.084.017cd">cukrodha ca mahākrodho vadhe cāsya mano dadhe</li>
    </ul>
    <ul id="R_6.084.018">
        <li id="R_6.084.018ab">tataḥ pādapam uddhṛtya śūraḥ saṃpradhane hariḥ</li>
        <li id="R_6.084.018cd">abhipatya jaghānāsya pramukhe taṃ mahāgajam</li>
    </ul>
    <ul id="R_6.084.019">
        <li id="R_6.084.019ab">sa tu prahārābhihataḥ sugrīveṇa mahāgajaḥ</li>
        <li id="R_6.084.019cd">apāsarpad dhanurmātraṃ niṣasāda nanāda ca</li>
    </ul>
    <ul id="R_6.084.020">
        <li id="R_6.084.020ab">gajāt tu mathitāt tūrṇam apakramya sa vīryavān</li>
        <li id="R_6.084.020cd">rākṣaso 'bhimukhaḥ śatruṃ pratyudgamya tataḥ kapim</li>
    </ul>
    <ul id="R_6.084.021">
        <li id="R_6.084.021ab">ārṣabhaṃ carmakhaḍgaṃ ca pragṛhya laghuvikramaḥ</li>
        <li id="R_6.084.021cd">bhartsayann iva sugrīvam āsasāda vyavasthitam</li>
    </ul>
    <ul id="R_6.084.022">
        <li id="R_6.084.022ab">sa hi tasyābhisaṃkruddhaḥ pragṛhya mahatīṃ śilām</li>
        <li id="R_6.084.022cd">virūpākṣāya cikṣepa sugrīvo jaladopamām</li>
    </ul>
    <ul id="R_6.084.023">
        <li id="R_6.084.023ab">sa tāṃ śilām āpatantīṃ dṛṣṭvā rākṣasapuṃgavaḥ</li>
        <li id="R_6.084.023cd">apakramya suvikrāntaḥ khaḍgena prāharat tadā</li>
    </ul>
    <ul id="R_6.084.024">
        <li id="R_6.084.024ab">tena khaḍgena saṃkruddhaḥ sugrīvasya camūmukhe</li>
        <li id="R_6.084.024cd">kavacaṃ pātayām āsa sa khaḍgābhihato 'patat</li>
    </ul>
    <ul id="R_6.084.025">
        <li id="R_6.084.025ab">sa samutthāya patitaḥ kapis tasya vyasarjayat</li>
        <li id="R_6.084.025cd">talaprahāram aśaneḥ samānaṃ bhīmanisvanam</li>
    </ul>
    <ul id="R_6.084.026">
        <li id="R_6.084.026ab">talaprahāraṃ tad rakṣaḥ sugrīveṇa samudyatam</li>
        <li id="R_6.084.026cd">naipuṇyān mocayitvainaṃ muṣṭinorasy atāḍayat</li>
    </ul>
    <ul id="R_6.084.027">
        <li id="R_6.084.027ab">tatas tu saṃkruddhataraḥ sugrīvo vānareśvaraḥ</li>
        <li id="R_6.084.027cd">mokṣitaṃ cātmano dṛṣṭvā prahāraṃ tena rakṣasā</li>
    </ul>
    <ul id="R_6.084.028">
        <li id="R_6.084.028ab">sa dadarśāntaraṃ tasya virūpākṣasya vānaraḥ</li>
        <li id="R_6.084.028cd">tato nyapātayat krodhāc chaṅkhadeśe mahātalam</li>
    </ul>
    <ul id="R_6.084.029">
        <li id="R_6.084.029ab">mahendrāśanikalpena talenābhihataḥ kṣitau</li>
        <li id="R_6.084.029cd">papāta rudhiraklinnaḥ śoṇitaṃ sa samudvaman</li>
    </ul>
    <ul id="R_6.084.030">
        <li id="R_6.084.030ab">vivṛttanayanaṃ krodhāt saphenarudhirāplutam</li>
        <li id="R_6.084.030cd">dadṛśus te virūpākṣaṃ virūpākṣataraṃ kṛtam</li>
    </ul>
    <ul id="R_6.084.031">
        <li id="R_6.084.031ab">sphurantaṃ parivarjantaṃ pārśvena rudhirokṣitam</li>
        <li id="R_6.084.031cd">karuṇaṃ ca vinardāntaṃ dadṛśuḥ kapayo ripum</li>
    </ul>
    <ul id="R_6.084.032">
        <li id="R_6.084.032ab">
            <seg type="pāda" n="a">tathā tu tau saṃyati saṃprayuktau</seg>
            <seg type="pāda" n="b">tarasvinau vānararākṣasānām</seg>
        </li>
        <li id="R_6.084.032cd">
            <seg type="pāda" n="c">balārṇavau sasvanatuḥ sabhīmaṃ</seg>
            <seg type="pāda" n="d">mahārṇavau dvāv iva bhinnavelau</seg>
        </li>
    </ul>
    <ul id="R_6.084.033">
        <li id="R_6.084.033ab">
            <seg type="pāda" n="a">vināśitaṃ prekṣya virūpanetraṃ</seg>
            <seg type="pāda" n="b">mahābalaṃ taṃ haripārthivena</seg>
        </li>
        <li id="R_6.084.033cd">
            <seg type="pāda" n="c">balaṃ samastaṃ kapirākṣasānām</seg>
            <seg type="pāda" n="d">unmattagaṅgāpratimaṃ babhūva</seg>
        </li>
    </ul>
    <ul id="R_6.085.001">
        <li id="R_6.085.001ab">hanyamāne bale tūrṇam anyonyaṃ te mahāmṛdhe</li>
        <li id="R_6.085.001cd">sarasīva mahāgharme sūpakṣīṇe babhūvatuḥ</li>
    </ul>
    <ul id="R_6.085.002">
        <li id="R_6.085.002ab">svabalasya vighātena virūpākṣavadhena ca</li>
        <li id="R_6.085.002cd">babhūva dviguṇaṃ kruddho rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_6.085.003">
        <li id="R_6.085.003ab">prakṣīṇaṃ tu balaṃ dṛṣṭvā vadhyamānaṃ valīmukhaiḥ</li>
        <li id="R_6.085.003cd">babhūvāsya vyathā yuddhe prekṣya daivaviparyayam</li>
    </ul>
    <ul id="R_6.085.004">
        <li id="R_6.085.004ab">uvāca ca samīpasthaṃ mahodaram ariṃdamam</li>
        <li id="R_6.085.004cd">asmin kāle mahābāho jayāśā tvayi me sthitā</li>
    </ul>
    <ul id="R_6.085.005">
        <li id="R_6.085.005ab">jahi śatrucamūṃ vīra darśayādya parākramam</li>
        <li id="R_6.085.005cd">bhartṛpiṇḍasya kālo 'yaṃ nirveṣṭuṃ sādhu yudhyatām</li>
    </ul>
    <ul id="R_6.085.006">
        <li id="R_6.085.006ab">evam uktas tathety uktvā rākṣasendraṃ mahodaraḥ</li>
        <li id="R_6.085.006cd">praviveśārisenāṃ sa pataṃga iva pāvakam</li>
    </ul>
    <ul id="R_6.085.007">
        <li id="R_6.085.007ab">tataḥ sa kadanaṃ cakre vānarāṇāṃ mahābalaḥ</li>
        <li id="R_6.085.007cd">bhartṛvākyena tejasvī svena vīryeṇa coditaḥ</li>
    </ul>
    <ul id="R_6.085.008">
        <li id="R_6.085.008ab">prabhagnāṃ samare dṛṣṭvā vānarāṇāṃ mahācamūm</li>
        <li id="R_6.085.008cd">abhidudrāva sugrīvo mahodaram anantaram</li>
    </ul>
    <ul id="R_6.085.009">
        <li id="R_6.085.009ab">pragṛhya vipulāṃ ghorāṃ mahīdharasamāṃ śilām</li>
        <li id="R_6.085.009cd">cikṣepa ca mahātejās tad vadhāya harīśvaraḥ</li>
    </ul>
    <ul id="R_6.085.010">
        <li id="R_6.085.010ab">tām āpatantīṃ sahasā śilāṃ dṛṣṭvā mahodaraḥ</li>
        <li id="R_6.085.010cd">asaṃbhrāntas tato bāṇair nirbibheda durāsadām</li>
    </ul>
    <ul id="R_6.085.011">
        <li id="R_6.085.011ab">rakṣasā tena bāṇaughair nikṛttā sā sahasradhā</li>
        <li id="R_6.085.011cd">nipapāta śilā bhūmau gṛdhracakram ivākulam</li>
    </ul>
    <ul id="R_6.085.012">
        <li id="R_6.085.012ab">tāṃ tu bhinnāṃ śilāṃ dṛṣṭvā sugrīvaḥ krodhamūrchitaḥ</li>
        <li id="R_6.085.012cd">sālam utpāṭya cikṣepa rakṣase raṇamūrdhani</li>
        <li id="R_6.085.012ef">śaraiś ca vidadārainaṃ śūraḥ parapuraṃjayaḥ</li>
    </ul>
    <ul id="R_6.085.013">
        <li id="R_6.085.013ab">sa dadarśa tataḥ kruddhaḥ parighaṃ patitaṃ bhuvi</li>
        <li id="R_6.085.013cd">āvidhya tu sa taṃ dīptaṃ parighaṃ tasya darśayan</li>
        <li id="R_6.085.013ef">parighāgreṇa vegena jaghānāsya hayottamān</li>
    </ul>
    <ul id="R_6.085.014">
        <li id="R_6.085.014ab">tasmād dhatahayād vīraḥ so 'vaplutya mahārathāt</li>
        <li id="R_6.085.014cd">gadāṃ jagrāha saṃkruddho rākṣaso 'tha mahodaraḥ</li>
    </ul>
    <ul id="R_6.085.015">
        <li id="R_6.085.015ab">gadāparighahastau tau yudhi vīrau samīyatuḥ</li>
        <li id="R_6.085.015cd">nardantau govṛṣaprakhyau ghanāv iva savidyutau</li>
    </ul>
    <ul id="R_6.085.016">
        <li id="R_6.085.016ab">ājaghāna gadāṃ tasya parigheṇa harīśvaraḥ</li>
        <li id="R_6.085.016cd">papāta sa gadodbhinnaḥ parighas tasya bhūtale</li>
    </ul>
    <ul id="R_6.085.017">
        <li id="R_6.085.017ab">tato jagrāha tejasvī sugrīvo vasudhātalāt</li>
        <li id="R_6.085.017cd">āyasaṃ musalaṃ ghoraṃ sarvato hemabhūṣitam</li>
    </ul>
    <ul id="R_6.085.018">
        <li id="R_6.085.018ab">taṃ samudyamya cikṣepa so 'py anyāṃ vyākṣipad gadām</li>
        <li id="R_6.085.018cd">bhinnāv anyonyam āsādya petatur dharaṇītale</li>
    </ul>
    <ul id="R_6.085.019">
        <li id="R_6.085.019ab">tato bhagnapraharaṇau muṣṭibhyāṃ tau samīyatuḥ</li>
        <li id="R_6.085.019cd">tejobalasamāviṣṭau dīptāv iva hutāśanau</li>
    </ul>
    <ul id="R_6.085.020">
        <li id="R_6.085.020ab">jaghnatus tau tadānyonyaṃ nedatuś ca punaḥ punaḥ</li>
        <li id="R_6.085.020cd">talaiś cānyonyam āhatya petatur dharaṇītale</li>
    </ul>
    <ul id="R_6.085.021">
        <li id="R_6.085.021ab">utpetatus tatas tūrṇaṃ jaghnatuś ca parasparam</li>
        <li id="R_6.085.021cd">bhujaiś cikṣepatur vīrāv anyonyam aparājitau</li>
    </ul>
    <ul id="R_6.085.022">
        <li id="R_6.085.022ab">ājahāra tadā khaḍgam adūraparivartinam</li>
        <li id="R_6.085.022cd">rākṣasaś carmaṇā sārdhaṃ mahāvego mahodaraḥ</li>
    </ul>
    <ul id="R_6.085.023">
        <li id="R_6.085.023ab">tathaiva ca mahākhaḍgaṃ carmaṇā patitaṃ saha</li>
        <li id="R_6.085.023cd">jagrāha vānaraśreṣṭhaḥ sugrīvo vegavattaraḥ</li>
    </ul>
    <ul id="R_6.085.024">
        <li id="R_6.085.024ab">tau tu roṣaparītāṅgau nardantāv abhyadhāvatām</li>
        <li id="R_6.085.024cd">udyatāsī raṇe hṛṣṭau yudhi śastraviśāradau</li>
    </ul>
    <ul id="R_6.085.025">
        <li id="R_6.085.025ab">dakṣiṇaṃ maṇḍalaṃ cobhau tau tūrṇaṃ saṃparīyatuḥ</li>
        <li id="R_6.085.025cd">anyonyam abhisaṃkruddhau jaye praṇihitāv ubhau</li>
    </ul>
    <ul id="R_6.085.026">
        <li id="R_6.085.026ab">sa tu śūro mahāvego vīryaślāghī mahodaraḥ</li>
        <li id="R_6.085.026cd">mahācarmaṇi taṃ khaḍgaṃ pātayām āsa durmatiḥ</li>
    </ul>
    <ul id="R_6.085.027">
        <li id="R_6.085.027ab">lagnam utkarṣataḥ khaḍgaṃ khaḍgena kapikuñjaraḥ</li>
        <li id="R_6.085.027cd">jahāra saśiras trāṇaṃ kuṇḍalopahitaṃ śiraḥ</li>
    </ul>
    <ul id="R_6.085.028">
        <li id="R_6.085.028ab">nikṛttaśirasas tasya patitasya mahītale</li>
        <li id="R_6.085.028cd">tad balaṃ rākṣasendrasya dṛṣṭvā tatra na tiṣṭhati</li>
    </ul>
    <ul id="R_6.085.029">
        <li id="R_6.085.029ab">hatvā taṃ vānaraiḥ sārdhaṃ nanāda mudito hariḥ</li>
        <li id="R_6.085.029cd">cukrodha ca daśagrīvo babhau hṛṣṭaś ca rāghavaḥ</li>
    </ul>
    <ul id="R_6.086.001">
        <li id="R_6.086.001ab">mahodare tu nihate mahāpārśvo mahābalaḥ</li>
        <li id="R_6.086.001cd">aṅgadasya camūṃ bhīmāṃ kṣobhayām āsa sāyakaiḥ</li>
    </ul>
    <ul id="R_6.086.002">
        <li id="R_6.086.002ab">sa vānarāṇāṃ mukhyānām uttamāṅgāni sarvaśaḥ</li>
        <li id="R_6.086.002cd">pātayām āsa kāyebhyaḥ phalaṃ vṛntād ivānilaḥ</li>
    </ul>
    <ul id="R_6.086.003">
        <li id="R_6.086.003ab">keṣāṃ cid iṣubhir bāhūn skandhāṃś cicheda rākṣasaḥ</li>
        <li id="R_6.086.003cd">vānarāṇāṃ susaṃkruddhaḥ pārśvaṃ keṣāṃ vyadārayat</li>
    </ul>
    <ul id="R_6.086.004">
        <li id="R_6.086.004ab">te 'rditā bāṇavarṣeṇa mahāpārśvena vānarāḥ</li>
        <li id="R_6.086.004cd">viṣādavimukhāḥ sarve babhūvur gatacetasaḥ</li>
    </ul>
    <ul id="R_6.086.005">
        <li id="R_6.086.005ab">nirīkṣya balam udvignam aṅgado rākṣasārditam</li>
        <li id="R_6.086.005cd">vegaṃ cakre mahābāhuḥ samudra iva parvaṇi</li>
    </ul>
    <ul id="R_6.086.006">
        <li id="R_6.086.006ab">āyasaṃ parighaṃ gṛhya sūryaraśmisamaprabham</li>
        <li id="R_6.086.006cd">samare vānaraśreṣṭho mahāpārśve nyapātayat</li>
    </ul>
    <ul id="R_6.086.007">
        <li id="R_6.086.007ab">sa tu tena prahāreṇa mahāpārśvo vicetanaḥ</li>
        <li id="R_6.086.007cd">sasūtaḥ syandanāt tasmād visaṃjñaḥ prāpatad bhuvi</li>
    </ul>
    <ul id="R_6.086.008">
        <li id="R_6.086.008ab">sarkṣarājas tu tejasvī nīlāñjanacayopamaḥ</li>
        <li id="R_6.086.008cd">niṣpatya sumahāvīryaḥ svād yūthān meghasaṃnibhāt</li>
    </ul>
    <ul id="R_6.086.009">
        <li id="R_6.086.009ab">pragṛhya giriśṛṅgābhāṃ kruddhaḥ sa vipulāṃ śilām</li>
        <li id="R_6.086.009cd">aśvāñ jaghāna tarasā syandanaṃ ca babhañja tam</li>
    </ul>
    <ul id="R_6.086.010">
        <li id="R_6.086.010ab">muhūrtāl labdhasaṃjñas tu mahāpārśvo mahābalaḥ</li>
        <li id="R_6.086.010cd">aṅgadaṃ bahubhir bāṇair bhūyas taṃ pratyavidhyata</li>
    </ul>
    <ul id="R_6.086.011">
        <li id="R_6.086.011ab">jāmbavantaṃ tribhir bāṇair ājaghāna stanāntare</li>
        <li id="R_6.086.011cd">ṛkṣarājaṃ gavākṣaṃ ca jaghāna bahubhiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.086.012">
        <li id="R_6.086.012ab">gavākṣaṃ jāmbavantaṃ ca sa dṛṣṭvā śarapīḍitau</li>
        <li id="R_6.086.012cd">jagrāha parighaṃ ghoram aṅgadaḥ krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_6.086.013">
        <li id="R_6.086.013ab">tasyāṅgadaḥ prakupito rākṣasasya tam āyasaṃ</li>
        <li id="R_6.086.013cd">dūrasthitasya parighaṃ raviraśmisamaprabham</li>
    </ul>
    <ul id="R_6.086.014">
        <li id="R_6.086.014ab">dvābhyāṃ bhujābhyāṃ saṃgṛhya bhrāmayitvā ca vegavān</li>
        <li id="R_6.086.014cd">mahāpārśvāya cikṣepa vadhārthaṃ vālinaḥ sutaḥ</li>
    </ul>
    <ul id="R_6.086.015">
        <li id="R_6.086.015ab">sa tu kṣipto balavatā parighas tasya rakṣasaḥ</li>
        <li id="R_6.086.015cd">dhanuś ca saśaraṃ hastāc chirastraṃ cāpy apātayat</li>
    </ul>
    <ul id="R_6.086.016">
        <li id="R_6.086.016ab">taṃ samāsādya vegena vāliputraḥ pratāpavān</li>
        <li id="R_6.086.016cd">talenābhyahanat kruddhaḥ karṇamūle sakuṇḍale</li>
    </ul>
    <ul id="R_6.086.017">
        <li id="R_6.086.017ab">sa tu kruddho mahāvego mahāpārśvo mahādyutiḥ</li>
        <li id="R_6.086.017cd">kareṇaikena jagrāha sumahāntaṃ paraśvadham</li>
    </ul>
    <ul id="R_6.086.018">
        <li id="R_6.086.018ab">taṃ tailadhautaṃ vimalaṃ śailasāramayaṃ dṛḍham</li>
        <li id="R_6.086.018cd">rākṣasaḥ paramakruddho vāliputre nyapātayat</li>
    </ul>
    <ul id="R_6.086.019">
        <li id="R_6.086.019ab">tena vāmāṃsaphalake bhṛśaṃ pratyavapātitam</li>
        <li id="R_6.086.019cd">aṅgado mokṣayām āsa saroṣaḥ sa paraśvadham</li>
    </ul>
    <ul id="R_6.086.020">
        <li id="R_6.086.020ab">sa vīro vajrasaṃkāśam aṅgado muṣṭim ātmanaḥ</li>
        <li id="R_6.086.020cd">saṃvartayan susaṃkruddhaḥ pitus tulyaparākramaḥ</li>
    </ul>
    <ul id="R_6.086.021">
        <li id="R_6.086.021ab">rākṣasasya stanābhyāśe marmajño hṛdayaṃ prati</li>
        <li id="R_6.086.021cd">indrāśanisamasparśaṃ sa muṣṭiṃ vinyapātayat</li>
    </ul>
    <ul id="R_6.086.022">
        <li id="R_6.086.022ab">tena tasya nipātena rākṣasasya mahāmṛdhe</li>
        <li id="R_6.086.022cd">paphāla hṛdayaṃ cāśu sa papāta hato bhuvi</li>
    </ul>
    <ul id="R_6.086.023">
        <li id="R_6.086.023ab">tasmin nipatite bhūmau tat sainyaṃ saṃpracukṣubhe</li>
        <li id="R_6.086.023cd">abhavac ca mahān krodhaḥ samare rāvaṇasya tu</li>
    </ul>
    <ul id="R_6.087.001">
        <li id="R_6.087.001ab">mahodaramahāpārśvau hatau dṛṣṭvā tu rākṣasau</li>
        <li id="R_6.087.001cd">tasmiṃś ca nihate vīre virūpākṣe mahābale</li>
    </ul>
    <ul id="R_6.087.002">
        <li id="R_6.087.002ab">āviveśa mahān krodho rāvaṇaṃ tu mahāmṛdhe</li>
        <li id="R_6.087.002cd">sūtaṃ saṃcodayām āsa vākyaṃ cedam uvāca ha</li>
    </ul>
    <ul id="R_6.087.003">
        <li id="R_6.087.003ab">nihatānām amātyānāṃ ruddhasya nagarasya ca</li>
        <li id="R_6.087.003cd">duḥkham eṣo 'paneṣyāmi hatvā tau rāmalakṣmaṇau</li>
    </ul>
    <ul id="R_6.087.004">
        <li id="R_6.087.004ab">rāmavṛkṣaṃ raṇe hanmi sītāpuṣpaphalapradam</li>
        <li id="R_6.087.004cd">praśākhā yasya sugrīvo jāmbavān kumudo nalaḥ</li>
    </ul>
    <ul id="R_6.087.005">
        <li id="R_6.087.005ab">sa diśo daśa ghoṣeṇa rathasyātiratho mahān</li>
        <li id="R_6.087.005cd">nādayan prayayau tūrṇaṃ rāghavaṃ cābhyavartata</li>
    </ul>
    <ul id="R_6.087.006">
        <li id="R_6.087.006ab">pūritā tena śabdena sanadīgirikānanā</li>
        <li id="R_6.087.006cd">saṃcacāla mahī sarvā savarāhamṛgadvipā</li>
    </ul>
    <ul id="R_6.087.007">
        <li id="R_6.087.007ab">tāmasaṃ sumahāghoraṃ cakārāstraṃ sudāruṇam</li>
        <li id="R_6.087.007cd">nirdadāha kapīn sarvāṃs te prapetuḥ samantataḥ</li>
    </ul>
    <ul id="R_6.087.008">
        <li id="R_6.087.008ab">tāny anīkāny anekāni rāvaṇasya śarottamaiḥ</li>
        <li id="R_6.087.008cd">dṛṣṭvā bhagnāni śataśo rāghavaḥ paryavasthitaḥ</li>
    </ul>
    <ul id="R_6.087.009">
        <li id="R_6.087.009ab">sa dadarśa tato rāmaṃ tiṣṭhantam aparājitam</li>
        <li id="R_6.087.009cd">lakṣmaṇena saha bhrātrā viṣṇunā vāsavaṃ yathā</li>
    </ul>
    <ul id="R_6.087.010">
        <li id="R_6.087.010ab">ālikhantam ivākāśam avaṣṭabhya mahad dhanuḥ</li>
        <li id="R_6.087.010cd">padmapatraviśālākṣaṃ dīrghabāhum ariṃdamam</li>
    </ul>
    <ul id="R_6.087.011">
        <li id="R_6.087.011ab">vānarāṃś ca raṇe bhagnān āpatantaṃ ca rāvaṇam</li>
        <li id="R_6.087.011cd">samīkṣya rāghavo hṛṣṭo madhye jagrāha kārmukam</li>
    </ul>
    <ul id="R_6.087.012">
        <li id="R_6.087.012ab">visphārayitum ārebhe tataḥ sa dhanur uttamam</li>
        <li id="R_6.087.012cd">mahāvegaṃ mahānādaṃ nirbhindann iva medinīm</li>
    </ul>
    <ul id="R_6.087.013">
        <li id="R_6.087.013ab">tayoḥ śarapathaṃ prāpya rāvaṇo rājaputrayoḥ</li>
        <li id="R_6.087.013cd">sa babhūva yathā rāhuḥ samīpe śaśisūryayoḥ</li>
    </ul>
    <ul id="R_6.087.014">
        <li id="R_6.087.014ab">rāvaṇasya ca bāṇaughai rāmavispharitena ca</li>
        <li id="R_6.087.014cd">śabdena rākṣasās tena petuś ca śataśas tadā</li>
    </ul>
    <ul id="R_6.087.015">
        <li id="R_6.087.015ab">tam icchan prathamaṃ yoddhuṃ lakṣmaṇo niśitaiḥ śaraiḥ</li>
        <li id="R_6.087.015cd">mumoca dhanur āyamya śarān agniśikhopamān</li>
    </ul>
    <ul id="R_6.087.016">
        <li id="R_6.087.016ab">tān muktamātrān ākāśe lakṣmaṇena dhanuṣmatā</li>
        <li id="R_6.087.016cd">bāṇān bāṇair mahātejā rāvaṇaḥ pratyavārayat</li>
    </ul>
    <ul id="R_6.087.017">
        <li id="R_6.087.017ab">ekam ekena bāṇena tribhis trīn daśabhir daśa</li>
        <li id="R_6.087.017cd">lakṣmaṇasya praciccheda darśayan pāṇilāghavam</li>
    </ul>
    <ul id="R_6.087.018">
        <li id="R_6.087.018ab">abhyatikramya saumitriṃ rāvaṇaḥ samitiṃjayaḥ</li>
        <li id="R_6.087.018cd">āsasāda tato rāmaṃ sthitaṃ śailam ivācalam</li>
    </ul>
    <ul id="R_6.087.019">
        <li id="R_6.087.019ab">sa saṃkhye rāmam āsādya krodhasaṃraktalocanaḥ</li>
        <li id="R_6.087.019cd">vyasṛjac charavarṣāni rāvaṇo rāghavopari</li>
    </ul>
    <ul id="R_6.087.020">
        <li id="R_6.087.020ab">śaradhārās tato rāmo rāvaṇasya dhanuścyutāḥ</li>
        <li id="R_6.087.020cd">dṛṣṭvaivāpatitāḥ śīghraṃ bhallāñ jagrāha satvaram</li>
    </ul>
    <ul id="R_6.087.021">
        <li id="R_6.087.021ab">tāñ śaraughāṃs tato bhallais tīkṣṇaiś ciccheda rāghavaḥ</li>
        <li id="R_6.087.021cd">dīpyamānān mahāvegān kruddhān āśīviṣān iva</li>
    </ul>
    <ul id="R_6.087.022">
        <li id="R_6.087.022ab">rāghavo rāvaṇaṃ tūrṇaṃ rāvaṇo rāghavaṃ tathā</li>
        <li id="R_6.087.022cd">anyonyaṃ vividhais tīkṣṇaiḥ śarair abhivavarṣatuḥ</li>
    </ul>
    <ul id="R_6.087.023">
        <li id="R_6.087.023ab">ceratuś ca ciraṃ citraṃ maṇḍalaṃ savyadakṣiṇam</li>
        <li id="R_6.087.023cd">bāṇavegān samudīkṣya samareṣv aparājitau</li>
    </ul>
    <ul id="R_6.087.024">
        <li id="R_6.087.024ab">tayor bhūtāni vitreṣur yugapat saṃprayudhyatoḥ</li>
        <li id="R_6.087.024cd">raudrayoḥ sāyakamucor yamāntakanikāśayoḥ</li>
    </ul>
    <ul id="R_6.087.025">
        <li id="R_6.087.025ab">saṃtataṃ vividhair bāṇair babhūva gaganaṃ tadā</li>
        <li id="R_6.087.025cd">ghanair ivātapāpāye vidyunmālāsamākulaiḥ</li>
    </ul>
    <ul id="R_6.087.026">
        <li id="R_6.087.026ab">gavākṣitam ivākāśaṃ babhūva śūravṛṣṭibhiḥ</li>
        <li id="R_6.087.026cd">mahāvegaiḥ sutīkṣṇāgrair gṛdhrapatraiḥ suvājitaiḥ</li>
    </ul>
    <ul id="R_6.087.027">
        <li id="R_6.087.027ab">śarāndhakāraṃ tau bhīmaṃ cakratuḥ paramaṃ tadā</li>
        <li id="R_6.087.027cd">gate 'staṃ tapane cāpi mahāmeghāv ivotthitau</li>
    </ul>
    <ul id="R_6.087.028">
        <li id="R_6.087.028ab">babhūva tumulaṃ yuddham anyonyavadhakāṅkṣiṇoḥ</li>
        <li id="R_6.087.028cd">anāsādyam acintyaṃ ca vṛtravāsavayor iva</li>
    </ul>
    <ul id="R_6.087.029">
        <li id="R_6.087.029ab">ubhau hi parameṣvāsāv ubhau śastraviśāradau</li>
        <li id="R_6.087.029cd">ubhau cāstravidāṃ mukhyāv ubhau yuddhe viceratuḥ</li>
    </ul>
    <ul id="R_6.087.030">
        <li id="R_6.087.030ab">ubhau hi yena vrajatas tena tena śarormayaḥ</li>
        <li id="R_6.087.030cd">ūrmayo vāyunā viddhā jagmuḥ sāgarayor iva</li>
    </ul>
    <ul id="R_6.087.031">
        <li id="R_6.087.031ab">tataḥ saṃsaktahastas tu rāvaṇo lokarāvaṇaḥ</li>
        <li id="R_6.087.031cd">nārācamālāṃ rāmasya lalāṭe pratyamuñcata</li>
    </ul>
    <ul id="R_6.087.032">
        <li id="R_6.087.032ab">raudracāpaprayuktāṃ tāṃ nīlotpaladalaprabhām</li>
        <li id="R_6.087.032cd">śirasā dhārayan rāmo na vyathāṃ pratyapadyata</li>
    </ul>
    <ul id="R_6.087.033">
        <li id="R_6.087.033ab">atha mantrān api japan raudram astram udīrayan</li>
        <li id="R_6.087.033cd">śarān bhūyaḥ samādāya rāmaḥ krodhasamanvitaḥ</li>
    </ul>
    <ul id="R_6.087.034">
        <li id="R_6.087.034ab">mumoca ca mahātejāś cāpam āyamya vīryavān</li>
        <li id="R_6.087.034cd">tāñ śarān rākṣasendrāya cikṣepācchinnasāyakaḥ</li>
    </ul>
    <ul id="R_6.087.035">
        <li id="R_6.087.035ab">te mahāmeghasaṃkāśe kavace patitāḥ śarāḥ</li>
        <li id="R_6.087.035cd">avadhye rākṣasendrasya na vyathāṃ janayaṃs tadā</li>
    </ul>
    <ul id="R_6.087.036">
        <li id="R_6.087.036ab">punar evātha taṃ rāmo rathasthaṃ rākṣasādhipam</li>
        <li id="R_6.087.036cd">lalāṭe paramāstreṇa sarvāstrakuśalo 'bhinat</li>
    </ul>
    <ul id="R_6.087.037">
        <li id="R_6.087.037ab">te bhittvā bāṇarūpāṇi pañcaśīrṣā ivoragāḥ</li>
        <li id="R_6.087.037cd">śvasanto viviśur bhūmiṃ rāvaṇapratikūlatāḥ</li>
    </ul>
    <ul id="R_6.087.038">
        <li id="R_6.087.038ab">nihatya rāghavasyāstraṃ rāvaṇaḥ krodhamūrchitaḥ</li>
        <li id="R_6.087.038cd">āsuraṃ sumahāghoram anyad astraṃ samādade</li>
    </ul>
    <ul id="R_6.087.039">
        <li id="R_6.087.039ab">siṃhavyāghramukhāṃś cānyān kaṅkakākamukhān api</li>
        <li id="R_6.087.039cd">gṛdhraśyenamukhāṃś cāpi sṛgālavadanāṃs tathā</li>
    </ul>
    <ul id="R_6.087.040">
        <li id="R_6.087.040ab">īhāmṛgamukhāṃś cānyān vyāditāsyān bhayāvahān</li>
        <li id="R_6.087.040cd">pañcāsyāṃl lelihānāṃś ca sasarja niśitāñ śarān</li>
    </ul>
    <ul id="R_6.087.041">
        <li id="R_6.087.041ab">śarān kharamukhāṃś cānyān varāhamukhasaṃsthitān</li>
        <li id="R_6.087.041cd">śvānakukkuṭavaktrāṃś ca makarāśīviṣānanān</li>
    </ul>
    <ul id="R_6.087.042">
        <li id="R_6.087.042ab">etāṃś cānyāṃś ca māyābhiḥ sasarja niśitāñ śarān</li>
        <li id="R_6.087.042cd">rāmaṃ prati mahātejāḥ kruddhaḥ sarpa iva śvasan</li>
    </ul>
    <ul id="R_6.087.043">
        <li id="R_6.087.043ab">āsureṇa samāviṣṭaḥ so 'streṇa raghunandanaḥ</li>
        <li id="R_6.087.043cd">sasarjāstraṃ mahotsāhaḥ pāvakaṃ pāvakopamaḥ</li>
    </ul>
    <ul id="R_6.087.044">
        <li id="R_6.087.044ab">agnidīptamukhān bāṇāṃs tathā sūryamukhān api</li>
        <li id="R_6.087.044cd">candrārdhacandravaktrāṃś ca dhūmaketumukhān api</li>
    </ul>
    <ul id="R_6.087.045">
        <li id="R_6.087.045ab">grahanakṣatravarṇāṃś ca maholkāmukhasaṃsthitān</li>
        <li id="R_6.087.045cd">vidyujjihvopamāṃś cānyān sasarja niśitāñ śarān</li>
    </ul>
    <ul id="R_6.087.046">
        <li id="R_6.087.046ab">te rāvaṇaśarā ghorā rāghavāstrasamāhatāḥ</li>
        <li id="R_6.087.046cd">vilayaṃ jagmur ākāśe jagmuś caiva sahasraśaḥ</li>
    </ul>
    <ul id="R_6.087.047">
        <li id="R_6.087.047ab">tad astraṃ nihataṃ dṛṣṭvā rāmeṇākliṣṭakarmaṇā</li>
        <li id="R_6.087.047cd">hṛṣṭā nedus tataḥ sarve kapayaḥ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_6.088.001">
        <li id="R_6.088.001ab">tasmin pratihate 'stre tu rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.088.001cd">krodhaṃ ca dviguṇaṃ cakre krodhāc cāstram anantaram</li>
    </ul>
    <ul id="R_6.088.002">
        <li id="R_6.088.002ab">mayena vihitaṃ raudram anyad astraṃ mahādyutiḥ</li>
        <li id="R_6.088.002cd">utsraṣṭuṃ rāvaṇo ghoraṃ rāghavāya pracakrame</li>
    </ul>
    <ul id="R_6.088.003">
        <li id="R_6.088.003ab">tataḥ śūlāni niścerur gadāś ca musalāni ca</li>
        <li id="R_6.088.003cd">kārmukād dīpyamānāni vajrasārāṇi sarvaśaḥ</li>
    </ul>
    <ul id="R_6.088.004">
        <li id="R_6.088.004ab">kūṭamudgarapāśāś ca dīptāś cāśanayas tathā</li>
        <li id="R_6.088.004cd">niṣpetur vividhās tīkṣṇā vātā iva yugakṣaye</li>
    </ul>
    <ul id="R_6.088.005">
        <li id="R_6.088.005ab">tad astraṃ rāghavaḥ śrīmān uttamāstravidāṃ varaḥ</li>
        <li id="R_6.088.005cd">jaghāna paramāstreṇa gandharveṇa mahādyutiḥ</li>
    </ul>
    <ul id="R_6.088.006">
        <li id="R_6.088.006ab">tasmin pratihate 'stre tu rāghaveṇa mahātmanā</li>
        <li id="R_6.088.006cd">rāvaṇaḥ krodhatāmrākṣaḥ sauram astram udīrayat</li>
    </ul>
    <ul id="R_6.088.007">
        <li id="R_6.088.007ab">tataś cakrāṇi niṣpetur bhāsvarāṇi mahānti ca</li>
        <li id="R_6.088.007cd">kārmukād bhīmavegasya daśagrīvasya dhīmataḥ</li>
    </ul>
    <ul id="R_6.088.008">
        <li id="R_6.088.008ab">tair āsīd gaganaṃ dīptaṃ saṃpatadbhir itas tataḥ</li>
        <li id="R_6.088.008cd">patadbhiś ca diśo dīptaiś candrasūryagrahair iva</li>
    </ul>
    <ul id="R_6.088.009">
        <li id="R_6.088.009ab">tāni ciccheda bāṇaughaiś cakrāṇi tu sa rāghavaḥ</li>
        <li id="R_6.088.009cd">āyudhāni vicitrāṇi rāvaṇasya camūmukhe</li>
    </ul>
    <ul id="R_6.088.010">
        <li id="R_6.088.010ab">tad astraṃ tu hataṃ dṛṣṭvā rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.088.010cd">vivyādha daśabhir bāṇai rāmaṃ sarveṣu marmasu</li>
    </ul>
    <ul id="R_6.088.011">
        <li id="R_6.088.011ab">sa viddho daśabhir bāṇair mahākārmukaniḥsṛtaiḥ</li>
        <li id="R_6.088.011cd">rāvaṇena mahātejā na prākampata rāghavaḥ</li>
    </ul>
    <ul id="R_6.088.012">
        <li id="R_6.088.012ab">tato vivyādha gātreṣu sarveṣu samitiṃjayaḥ</li>
        <li id="R_6.088.012cd">rāghavas tu susaṃkruddho rāvaṇaṃ bahubhiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.088.013">
        <li id="R_6.088.013ab">etasminn antare kruddho rāghavasyānujo balī</li>
        <li id="R_6.088.013cd">lakṣmaṇaḥ sāyakān sapta jagrāha paravīrahā</li>
    </ul>
    <ul id="R_6.088.014">
        <li id="R_6.088.014ab">taiḥ sāyakair mahāvegai rāvaṇasya mahādyutiḥ</li>
        <li id="R_6.088.014cd">dhvajaṃ manuṣyaśīrṣaṃ tu tasya ciccheda naikadhā</li>
    </ul>
    <ul id="R_6.088.015">
        <li id="R_6.088.015ab">sāratheś cāpi bāṇena śiro jvalitakuṇḍalam</li>
        <li id="R_6.088.015cd">jahāra lakṣmaṇaḥ śrīmān nairṛtasya mahābalaḥ</li>
    </ul>
    <ul id="R_6.088.016">
        <li id="R_6.088.016ab">tasya bāṇaiś ca ciccheda dhanur gajakaropamam</li>
        <li id="R_6.088.016cd">lakṣmaṇo rākṣasendrasya pañcabhir niśitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.088.017">
        <li id="R_6.088.017ab">nīlameghanibhāṃś cāsya sadaśvān parvatopamān</li>
        <li id="R_6.088.017cd">jaghānāplutya gadayā rāvaṇasya vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.088.018">
        <li id="R_6.088.018ab">hatāśvād vegavān vegād avaplutya mahārathāt</li>
        <li id="R_6.088.018cd">krodham āhārayat tīvraṃ bhrātaraṃ prati rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.088.019">
        <li id="R_6.088.019ab">tataḥ śaktiṃ mahāśaktir dīptāṃ dīptāśanīm iva</li>
        <li id="R_6.088.019cd">vibhīṣaṇāya cikṣepa rākṣasendraḥ pratāpavān</li>
    </ul>
    <ul id="R_6.088.020">
        <li id="R_6.088.020ab">aprāptām eva tāṃ bāṇais tribhiś ciccheda lakṣmaṇaḥ</li>
        <li id="R_6.088.020cd">athodatiṣṭhat saṃnādo vānarāṇāṃ tadā raṇe</li>
    </ul>
    <ul id="R_6.088.021">
        <li id="R_6.088.021ab">sā papāta tridhā chinnā śaktiḥ kāñcanamālinī</li>
        <li id="R_6.088.021cd">savisphuliṅgā jvalitā maholkeva divaś cyutā</li>
    </ul>
    <ul id="R_6.088.022">
        <li id="R_6.088.022ab">tataḥ saṃbhāvitatarāṃ kālenāpi durāsadām</li>
        <li id="R_6.088.022cd">jagrāha vipulāṃ śaktiṃ dīpyamānāṃ svatejasā</li>
    </ul>
    <ul id="R_6.088.023">
        <li id="R_6.088.023ab">sā veginā balavatā rāvaṇena durātmanā</li>
        <li id="R_6.088.023cd">jajvāla sumahāghorā śakrāśanisamaprabhā</li>
    </ul>
    <ul id="R_6.088.024">
        <li id="R_6.088.024ab">etasminn antare vīro lakṣmaṇas taṃ vibhīṣaṇam</li>
        <li id="R_6.088.024cd">prāṇasaṃśayam āpannaṃ tūrṇam evābhyapadyata</li>
    </ul>
    <ul id="R_6.088.025">
        <li id="R_6.088.025ab">taṃ vimokṣayituṃ vīraś cāpam āyamya lakṣmaṇaḥ</li>
        <li id="R_6.088.025cd">rāvaṇaṃ śaktihastaṃ taṃ śaravarṣair avākirat</li>
    </ul>
    <ul id="R_6.088.026">
        <li id="R_6.088.026ab">kīryamāṇaḥ śaraugheṇa visṛṣṭena mahātmanā</li>
        <li id="R_6.088.026cd">na prahartuṃ manaś cakre vimukhīkṛtavikramaḥ</li>
    </ul>
    <ul id="R_6.088.027">
        <li id="R_6.088.027ab">mokṣitaṃ bhrātaraṃ dṛṣṭvā lakṣmaṇena sa rāvaṇaḥ</li>
        <li id="R_6.088.027cd">lakṣmaṇābhimukhas tiṣṭhann idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_6.088.028">
        <li id="R_6.088.028ab">mokṣitas te balaślāghin yasmād evaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.088.028cd">vimucya rākṣasaṃ śaktis tvayīyaṃ vinipātyate</li>
    </ul>
    <ul id="R_6.088.029">
        <li id="R_6.088.029ab">eṣā te hṛdayaṃ bhittvā śaktir lohitalakṣaṇā</li>
        <li id="R_6.088.029cd">madbāhuparighotsṛṣṭā prāṇān ādāya yāsyati</li>
    </ul>
    <ul id="R_6.088.030">
        <li id="R_6.088.030ab">ity evam uktvā tāṃ śaktim aṣṭaghaṇṭāṃ mahāsvanām</li>
        <li id="R_6.088.030cd">mayena māyāvihitām amoghāṃ śatrughātinīm</li>
    </ul>
    <ul id="R_6.088.031">
        <li id="R_6.088.031ab">lakṣmaṇāya samuddiśya jvalantīm iva tejasā</li>
        <li id="R_6.088.031cd">rāvaṇaḥ paramakruddhaś cikṣepa ca nanāda ca</li>
    </ul>
    <ul id="R_6.088.032">
        <li id="R_6.088.032ab">sā kṣiptā bhīmavegena śakrāśanisamasvanā</li>
        <li id="R_6.088.032cd">śaktir abhyapatad vegāl lakṣmaṇaṃ raṇamūrdhani</li>
    </ul>
    <ul id="R_6.088.033">
        <li id="R_6.088.033ab">tām anuvyāharac chaktim āpatantīṃ sa rāghavaḥ</li>
        <li id="R_6.088.033cd">svastyas tu lakṣmaṇāyeti moghā bhava hatodyamā</li>
    </ul>
    <ul id="R_6.088.034">
        <li id="R_6.088.034ab">nyapatat sā mahāvegā lakṣmaṇasya mahorasi</li>
        <li id="R_6.088.034cd">jihvevoragarājasya dīpyamānā mahādyutiḥ</li>
    </ul>
    <ul id="R_6.088.035">
        <li id="R_6.088.035ab">tato rāvaṇavegena sudūram avagāḍhayā</li>
        <li id="R_6.088.035cd">śaktyā nirbhinnahṛdayaḥ papāta bhuvi lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.088.036">
        <li id="R_6.088.036ab">tadavasthaṃ samīpastho lakṣmaṇaṃ prekṣya rāghavaḥ</li>
        <li id="R_6.088.036cd">bhrātṛsnehān mahātejā viṣaṇṇahṛdayo 'bhavat</li>
    </ul>
    <ul id="R_6.088.037">
        <li id="R_6.088.037ab">sa muhūrtam anudhyāya bāṣpavyākulalocanaḥ</li>
        <li id="R_6.088.037cd">babhūva saṃrabdhataro yugānta iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.088.038">
        <li id="R_6.088.038ab">na viṣādasya kālo 'yam iti saṃcintya rāghavaḥ</li>
        <li id="R_6.088.038cd">cakre sutumulaṃ yuddhaṃ rāvaṇasya vadhe dhṛtaḥ</li>
    </ul>
    <ul id="R_6.088.039">
        <li id="R_6.088.039ab">sa dadarśa tato rāmaḥ śaktyā bhinnaṃ mahāhave</li>
        <li id="R_6.088.039cd">lakṣmaṇaṃ rudhirādigdhaṃ sapannagam ivācalam</li>
    </ul>
    <ul id="R_6.088.040">
        <li id="R_6.088.040ab">tām api prahitāṃ śaktiṃ rāvaṇena balīyasā</li>
        <li id="R_6.088.040cd">yatnatas te hariśreṣṭhā na śekur avamarditum</li>
        <li id="R_6.088.040ef">arditāś caiva bāṇaughaiḥ kṣiprahastena rakṣasā</li>
    </ul>
    <ul id="R_6.088.041">
        <li id="R_6.088.041ab">saumitriṃ sā vinirbhidya praviṣṭā dharaṇītalam</li>
        <li id="R_6.088.041cd">tāṃ karābhyāṃ parāmṛśya rāmaḥ śaktiṃ bhayāvahām</li>
        <li id="R_6.088.041ef">babhañja samare kruddho balavad vicakarṣa ca</li>
    </ul>
    <ul id="R_6.088.042">
        <li id="R_6.088.042ab">tasya niṣkarṣataḥ śaktiṃ rāvaṇena balīyasā</li>
        <li id="R_6.088.042cd">śarāḥ sarveṣu gātreṣu pātitā marmabhedinaḥ</li>
    </ul>
    <ul id="R_6.088.043">
        <li id="R_6.088.043ab">acintayitvā tān bāṇān samāśliṣya ca lakṣmaṇam</li>
        <li id="R_6.088.043cd">abravīc ca hanūmantaṃ sugrīvaṃ caiva rāghavaḥ</li>
        <li id="R_6.088.043ef">lakṣmaṇaṃ parivāryeha tiṣṭhadhvaṃ vānarottamāḥ</li>
    </ul>
    <ul id="R_6.088.044">
        <li id="R_6.088.044ab">parākramasya kālo 'yaṃ saṃprāpto me cirepsitaḥ</li>
        <li id="R_6.088.044cd">pāpātmāyaṃ daśagrīvo vadhyatāṃ pāpaniścayaḥ</li>
        <li id="R_6.088.044ef">kāṅkṣitaḥ stokakasyeva gharmānte meghadarśanam</li>
    </ul>
    <ul id="R_6.088.045">
        <li id="R_6.088.045ab">asmin muhūrte nacirāt satyaṃ pratiśṛṇomi vaḥ</li>
        <li id="R_6.088.045cd">arāvaṇam arāmaṃ vā jagad drakṣyatha vānarāḥ</li>
    </ul>
    <ul id="R_6.088.046">
        <li id="R_6.088.046ab">rājyanāśaṃ vane vāsaṃ daṇḍake paridhāvanam</li>
        <li id="R_6.088.046cd">vaidehyāś ca parāmarśaṃ rakṣobhiś ca samāgamam</li>
    </ul>
    <ul id="R_6.088.047">
        <li id="R_6.088.047ab">prāptaṃ duḥkhaṃ mahad ghoraṃ kleśaṃ ca nirayopamam</li>
        <li id="R_6.088.047cd">adya sarvam ahaṃ tyakṣye hatvā taṃ rāvaṇaṃ raṇe</li>
    </ul>
    <ul id="R_6.088.048">
        <li id="R_6.088.048ab">yadarthaṃ vānaraṃ sainyaṃ samānītam idaṃ mayā</li>
        <li id="R_6.088.048cd">sugrīvaś ca kṛto rājye nihatvā vālinaṃ raṇe</li>
    </ul>
    <ul id="R_6.088.049">
        <li id="R_6.088.049ab">yadarthaṃ sāgaraḥ krāntaḥ setur baddhaś ca sāgare</li>
        <li id="R_6.088.049cd">so 'yam adya raṇe pāpaś cakṣurviṣayam āgataḥ</li>
    </ul>
    <ul id="R_6.088.050">
        <li id="R_6.088.050ab">cakṣurviṣayam āgamya nāyaṃ jīvitum arhati</li>
        <li id="R_6.088.050cd">dṛṣṭiṃ dṛṣṭiviṣasyeva sarpasya mama rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.088.051">
        <li id="R_6.088.051ab">svasthāḥ paśyata durdharṣā yuddhaṃ vānarapuṃgavāḥ</li>
        <li id="R_6.088.051cd">āsīnāḥ parvatāgreṣu mamedaṃ rāvaṇasya ca</li>
    </ul>
    <ul id="R_6.088.052">
        <li id="R_6.088.052ab">adya rāmasya rāmatvaṃ paśyantu mama saṃyuge</li>
        <li id="R_6.088.052cd">trayo lokāḥ sagandharvāḥ sadevāḥ sarṣicāraṇāḥ</li>
    </ul>
    <ul id="R_6.088.053">
        <li id="R_6.088.053ab">adya karma kariṣyāmi yal lokāḥ sacarācarāḥ</li>
        <li id="R_6.088.053cd">sadevāḥ kathayiṣyanti yāvad bhūmir dhariṣyati</li>
    </ul>
    <ul id="R_6.088.054">
        <li id="R_6.088.054ab">evam uktvā śitair bāṇais taptakāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.088.054cd">ājaghāna daśagrīvaṃ raṇe rāmaḥ samāhitaḥ</li>
    </ul>
    <ul id="R_6.088.055">
        <li id="R_6.088.055ab">atha pradīptair nārācair musalaiś cāpi rāvaṇaḥ</li>
        <li id="R_6.088.055cd">abhyavarṣat tadā rāmaṃ dhārābhir iva toyadaḥ</li>
    </ul>
    <ul id="R_6.088.056">
        <li id="R_6.088.056ab">rāmarāvaṇamuktānām anyonyam abhinighnatām</li>
        <li id="R_6.088.056cd">śarāṇāṃ ca śarāṇāṃ ca babhūva tumulaḥ svanaḥ</li>
    </ul>
    <ul id="R_6.088.057">
        <li id="R_6.088.057ab">te bhinnāś ca vikīrṇāś ca rāmarāvaṇayoḥ śarāḥ</li>
        <li id="R_6.088.057cd">antarikṣāt pradīptāgrā nipetur dharaṇītale</li>
    </ul>
    <ul id="R_6.088.058">
        <li id="R_6.088.058ab">tayor jyātalanirghoṣo rāmarāvaṇayor mahān</li>
        <li id="R_6.088.058cd">trāsanaḥ sarvabūtānāṃ sa babhūvādbhutopamaḥ</li>
    </ul>
    <ul id="R_6.088.059">
        <li id="R_6.088.059ab">
            <seg type="pāda" n="a">sa kīryamāṇaḥ śarajālavṛṣṭibhir</seg>
            <seg type="pāda" n="b">mahātmanā dīptadhanuṣmatārditaḥ</seg>
        </li>
        <li id="R_6.088.059cd">
            <seg type="pāda" n="c">bhayāt pradudrāva sametya rāvaṇo</seg>
            <seg type="pāda" n="d">yathānilenābhihato balāhakaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.089.001">
        <li id="R_6.089.001ab">sa dattvā tumulaṃ yuddhaṃ rāvaṇasya durātmanaḥ</li>
        <li id="R_6.089.001cd">visṛjann eva bāṇaughān suṣeṇaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_6.089.002">
        <li id="R_6.089.002ab">eṣa rāvaṇavegena lakṣmaṇaḥ patitaḥ kṣitau</li>
        <li id="R_6.089.002cd">sarpavad veṣṭate vīro mama śokam udīrayan</li>
    </ul>
    <ul id="R_6.089.003">
        <li id="R_6.089.003ab">śoṇitārdram imaṃ vīraṃ prāṇair iṣṭataraṃ mama</li>
        <li id="R_6.089.003cd">paśyato mama kā śaktir yoddhuṃ paryākulātmanaḥ</li>
    </ul>
    <ul id="R_6.089.004">
        <li id="R_6.089.004ab">ayaṃ sa samaraślāghī bhrātā me śubhalakṣaṇaḥ</li>
        <li id="R_6.089.004cd">yadi pañcatvam āpannaḥ prāṇair me kiṃ sukhena vā</li>
    </ul>
    <ul id="R_6.089.005">
        <li id="R_6.089.005ab">lajjatīva hi me vīryaṃ bhraśyatīva karād dhanuḥ</li>
        <li id="R_6.089.005cd">sāyakā vyavasīdanti dṛṣṭir bāṣpavaśaṃ gatā</li>
        <li id="R_6.089.005ef">cintā me vardhate tīvrā mumūrṣā copajāyate</li>
    </ul>
    <ul id="R_6.089.006">
        <li id="R_6.089.006ab">bhrātaraṃ nihataṃ dṛṣṭvā rāvaṇena durātmanā</li>
        <li id="R_6.089.006cd">paraṃ viṣādam āpanno vilalāpākulendriyaḥ</li>
    </ul>
    <ul id="R_6.089.007">
        <li id="R_6.089.007ab">na hi yuddhena me kāryaṃ naiva prāṇair na sītayā</li>
        <li id="R_6.089.007cd">bhrātaraṃ nihataṃ dṛṣṭvā lakṣmaṇaṃ raṇapāṃsuṣu</li>
    </ul>
    <ul id="R_6.089.008">
        <li id="R_6.089.008ab">kiṃ me rājyena kiṃ prāṇair yuddhe kāryaṃ na vidyate</li>
        <li id="R_6.089.008cd">yatrāyaṃ nihataḥ śete raṇamūrdhani lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.089.009">
        <li id="R_6.089.009ab">rāmam āśvāsayan vīraḥ suṣeṇo vākyam abravīt</li>
        <li id="R_6.089.009cd">na mṛto 'yaṃ mahābāhur lakṣmaṇo lakṣmivardhanaḥ</li>
    </ul>
    <ul id="R_6.089.010">
        <li id="R_6.089.010ab">na cāsya vikṛtaṃ vaktraṃ nāpi śyāmaṃ na niṣprabham</li>
        <li id="R_6.089.010cd">suprabhaṃ ca prasannaṃ ca mukham asyābhilakṣyate</li>
    </ul>
    <ul id="R_6.089.011">
        <li id="R_6.089.011ab">padmaraktatalau hastau suprasanne ca locane</li>
        <li id="R_6.089.011cd">evaṃ na vidyate rūpaṃ gatāsūnāṃ viśāṃ pate</li>
        <li id="R_6.089.011ef">māṃ viṣādaṃ kṛthā vīra saprāṇo 'yam ariṃdama</li>
    </ul>
    <ul id="R_6.089.012">
        <li id="R_6.089.012ab">ākhyāsyate prasuptasya srastagātrasya bhūtale</li>
        <li id="R_6.089.012cd">socchvāsaṃ hṛdayaṃ vīra kampamānaṃ muhur muhuḥ</li>
    </ul>
    <ul id="R_6.089.013">
        <li id="R_6.089.013ab">evam uktvā tu vākyajñaḥ suṣeṇo rāghavaṃ vacaḥ</li>
        <li id="R_6.089.013cd">samīpastham uvācedaṃ hanūmantam abhitvaran</li>
    </ul>
    <ul id="R_6.089.014">
        <li id="R_6.089.014ab">saumya śīghram ito gatvā śailam oṣadhiparvatam</li>
        <li id="R_6.089.014cd">pūrvaṃ hi kathito yo 'sau vīra jāmbavatā śubhaḥ</li>
    </ul>
    <ul id="R_6.089.015">
        <li id="R_6.089.015ab">dakṣiṇe śikhare tasya jātām oṣadhim ānaya</li>
        <li id="R_6.089.015cd">viśalyakaraṇī nāma viśalyakaraṇīṃ śubhām</li>
    </ul>
    <ul id="R_6.089.016">
        <li id="R_6.089.016ab">sauvarṇakaraṇīṃ cāpi tathā saṃjīvanīm api</li>
        <li id="R_6.089.016cd">saṃdhānakaraṇīṃ cāpi gatvā śīghram ihānaya</li>
        <li id="R_6.089.016ef">saṃjīvanārthaṃ vīrasya lakṣmaṇasya mahātmanaḥ</li>
    </ul>
    <ul id="R_6.089.017">
        <li id="R_6.089.017ab">ity evam ukto hanumān gatvā cauṣadhiparvatam</li>
        <li id="R_6.089.017cd">cintām abhyagamac chrīmān ajānaṃs tā mahauṣadhīḥ</li>
    </ul>
    <ul id="R_6.089.018">
        <li id="R_6.089.018ab">tasya buddhiḥ samutpannā māruter amitaujasaḥ</li>
        <li id="R_6.089.018cd">idam eva gamiṣyāmi gṛhītvā śikharaṃ gireḥ</li>
    </ul>
    <ul id="R_6.089.019">
        <li id="R_6.089.019ab">agṛhya yadi gacchāmi viśalyakaraṇīm aham</li>
        <li id="R_6.089.019cd">kālātyayena doṣaḥ syād vaiklavyaṃ ca mahad bhavet</li>
    </ul>
    <ul id="R_6.089.020">
        <li id="R_6.089.020ab">iti saṃcintya hanumān gatvā kṣipraṃ mahābalaḥ</li>
        <li id="R_6.089.020cd">utpapāta gṛhītvā tu hanūmāñ śikharaṃ gireḥ</li>
    </ul>
    <ul id="R_6.089.021">
        <li id="R_6.089.021ab">oṣadhīr nāvagachāmi tā ahaṃ haripuṃgava</li>
        <li id="R_6.089.021cd">tad idaṃ śikharaṃ kṛtsnaṃ gires tasyāhṛtaṃ mayā</li>
    </ul>
    <ul id="R_6.089.022">
        <li id="R_6.089.022ab">evaṃ kathayamānaṃ taṃ praśasya pavanātmajam</li>
        <li id="R_6.089.022cd">suṣeṇo vānaraśreṣṭho jagrāhotpāṭya cauṣadhīḥ</li>
    </ul>
    <ul id="R_6.089.023">
        <li id="R_6.089.023ab">tataḥ saṃkṣodayitvā tām oṣadhiṃ vānarottamaḥ</li>
        <li id="R_6.089.023cd">lakṣmaṇasya dadau nastaḥ suṣeṇaḥ sumahādyutiḥ</li>
    </ul>
    <ul id="R_6.089.024">
        <li id="R_6.089.024ab">saśalyaḥ sa samāghrāya lakṣmaṇaḥ paravīrahā</li>
        <li id="R_6.089.024cd">viśalyo virujaḥ śīghram udatiṣṭhan mahītalāt</li>
    </ul>
    <ul id="R_6.089.025">
        <li id="R_6.089.025ab">samutthitaṃ te harayo bhūtalāt prekṣya lakṣmaṇam</li>
        <li id="R_6.089.025cd">sādhu sādhv iti suprītāḥ suṣeṇaṃ pratyapūjayan</li>
    </ul>
    <ul id="R_6.089.026">
        <li id="R_6.089.026ab">ehy ehīty abravīd rāmo lakṣmaṇaṃ paravīrahā</li>
        <li id="R_6.089.026cd">sasvaje snehagāḍhaṃ ca bāṣpaparyākulekṣaṇaḥ</li>
    </ul>
    <ul id="R_6.089.027">
        <li id="R_6.089.027ab">abravīc ca pariṣvajya saumitriṃ rāghavas tadā</li>
        <li id="R_6.089.027cd">diṣṭyā tvāṃ vīra paśyāmi maraṇāt punar āgatam</li>
    </ul>
    <ul id="R_6.089.028">
        <li id="R_6.089.028ab">na hi me jīvitenārthaḥ sītayā ca jayena vā</li>
        <li id="R_6.089.028cd">ko hi me jīvitenārthas tvayi pañcatvam āgate</li>
    </ul>
    <ul id="R_6.089.029">
        <li id="R_6.089.029ab">ity evaṃ vadatas tasya rāghavasya mahātmanaḥ</li>
        <li id="R_6.089.029cd">khinnaḥ śithilayā vācā lakṣmaṇo vākyam abravīt</li>
    </ul>
    <ul id="R_6.089.030">
        <li id="R_6.089.030ab">tāṃ pratijñāṃ pratijñāya purā satyaparākrama</li>
        <li id="R_6.089.030cd">laghuḥ kaś cid ivāsattvo naivaṃ vaktum ihārhasi</li>
    </ul>
    <ul id="R_6.089.031">
        <li id="R_6.089.031ab">na pratijñāṃ hi kurvanti vitathāṃ sādhavo 'nagha</li>
        <li id="R_6.089.031cd">lakṣaṇaṃ hi mahat tv asya pratijñāparipālanam</li>
    </ul>
    <ul id="R_6.089.032">
        <li id="R_6.089.032ab">nairāśyam upagantuṃ te tad alaṃ matkṛte 'nagha</li>
        <li id="R_6.089.032cd">vadhena rāvaṇasyādya pratijñām anupālaya</li>
    </ul>
    <ul id="R_6.089.033">
        <li id="R_6.089.033ab">na jīvan yāsyate śatrus tava bāṇapathaṃ gataḥ</li>
        <li id="R_6.089.033cd">nardatas tīkṣṇadaṃṣṭrasya siṃhasyeva mahāgajaḥ</li>
    </ul>
    <ul id="R_6.089.034">
        <li id="R_6.089.034ab">ahaṃ tu vadham icchāmi śīghram asya durātmanaḥ</li>
        <li id="R_6.089.034cd">yāvad astaṃ na yāty eṣa kṛtakarmā divākaraḥ</li>
    </ul>
    <ul id="R_6.090.001">
        <li id="R_6.090.001ab">lakṣmaṇena tu tad vākyam uktaṃ śrutvā sa rāghavaḥ</li>
        <li id="R_6.090.001cd">rāvaṇāya śarān ghorān visasarja camūmukhe</li>
    </ul>
    <ul id="R_6.090.002">
        <li id="R_6.090.002ab">daśagrīvo rathasthas tu rāmaṃ vajropamaiḥ śaraiḥ</li>
        <li id="R_6.090.002cd">ājaghāna mahāghorair dhārābhir iva toyadaḥ</li>
    </ul>
    <ul id="R_6.090.003">
        <li id="R_6.090.003ab">dīptapāvakasaṃkāśaiḥ śaraiḥ kāñcanabhūṣaṇaiḥ</li>
        <li id="R_6.090.003cd">nirbibheda raṇe rāmo daśagrīvaṃ samāhitaḥ</li>
    </ul>
    <ul id="R_6.090.004">
        <li id="R_6.090.004ab">bhūmisthitasya rāmasya rathasthasya ca rakṣasaḥ</li>
        <li id="R_6.090.004cd">na samaṃ yuddham ity āhur devagandharvadānavāḥ</li>
    </ul>
    <ul id="R_6.090.005">
        <li id="R_6.090.005ab">tataḥ kāñcanacitrāṅgaḥ kiṃkiṇīśatabhūṣitaḥ</li>
        <li id="R_6.090.005cd">taruṇādityasaṃkāśo vaidūryamayakūbaraḥ</li>
    </ul>
    <ul id="R_6.090.006">
        <li id="R_6.090.006ab">sadaśvaiḥ kāñcanāpīḍair yuktaḥ śvetaprakīrṇakaiḥ</li>
        <li id="R_6.090.006cd">haribhiḥ sūryasaṃkāśair hemajālavibhūṣitaiḥ</li>
    </ul>
    <ul id="R_6.090.007">
        <li id="R_6.090.007ab">rukmaveṇudhvajaḥ śrīmān devarājaratho varaḥ</li>
        <li id="R_6.090.007cd">abhyavartata kākutstham avatīrya triviṣṭapāt</li>
    </ul>
    <ul id="R_6.090.008">
        <li id="R_6.090.008ab">abravīc ca tadā rāmaṃ sapratodo rathe sthitaḥ</li>
        <li id="R_6.090.008cd">prāñjalir mātalir vākyaṃ sahasrākṣasya sārathiḥ</li>
    </ul>
    <ul id="R_6.090.009">
        <li id="R_6.090.009ab">sahasrākṣeṇa kākutstha ratho 'yaṃ vijayāya te</li>
        <li id="R_6.090.009cd">dattas tava mahāsattva śrīmāñ śatrunibarhaṇaḥ</li>
    </ul>
    <ul id="R_6.090.010">
        <li id="R_6.090.010ab">idam aindraṃ mahaccāpaṃ kavacaṃ cāgnisaṃnibham</li>
        <li id="R_6.090.010cd">śarāś cādityasaṃkāśāḥ śaktiś ca vimalā śitāḥ</li>
    </ul>
    <ul id="R_6.090.011">
        <li id="R_6.090.011ab">āruhyemaṃ rathaṃ vīra rākṣasaṃ jahi rāvaṇam</li>
        <li id="R_6.090.011cd">mayā sārathinā rāma mahendra iva dānavān</li>
    </ul>
    <ul id="R_6.090.012">
        <li id="R_6.090.012ab">ity uktaḥ sa parikramya rathaṃ tam abhivādya ca</li>
        <li id="R_6.090.012cd">āruroha tadā rāmo lokāṃl lakṣmyā virājayan</li>
    </ul>
    <ul id="R_6.090.013">
        <li id="R_6.090.013ab">tad babhūvādbhutaṃ yuddhaṃ dvairathaṃ lomaharṣaṇam</li>
        <li id="R_6.090.013cd">rāmasya ca mahābāho rāvaṇasya ca rakṣasaḥ</li>
    </ul>
    <ul id="R_6.090.014">
        <li id="R_6.090.014ab">sa gāndharveṇa gāndharvaṃ daivaṃ daivena rāghavaḥ</li>
        <li id="R_6.090.014cd">astraṃ rākṣasarājasya jaghāna paramāstravit</li>
    </ul>
    <ul id="R_6.090.015">
        <li id="R_6.090.015ab">astraṃ tu paramaṃ ghoraṃ rākṣasaṃ rākasādhipaḥ</li>
        <li id="R_6.090.015cd">sasarja paramakruddhaḥ punar eva niśācaraḥ</li>
    </ul>
    <ul id="R_6.090.016">
        <li id="R_6.090.016ab">te rāvaṇadhanurmuktāḥ śarāḥ kāñcanabhūṣaṇāḥ</li>
        <li id="R_6.090.016cd">abhyavartanta kākutsthaṃ sarpā bhūtvā mahāviṣāḥ</li>
    </ul>
    <ul id="R_6.090.017">
        <li id="R_6.090.017ab">te dīptavadanā dīptaṃ vamanto jvalanaṃ mukhaiḥ</li>
        <li id="R_6.090.017cd">rāmam evābhyavartanta vyāditāsyā bhayānakāḥ</li>
    </ul>
    <ul id="R_6.090.018">
        <li id="R_6.090.018ab">tair vāsukisamasparśair dīptabhogair mahāviṣaiḥ</li>
        <li id="R_6.090.018cd">diśaś ca saṃtatāḥ sarvāḥ pradiśaś ca samāvṛtāḥ</li>
    </ul>
    <ul id="R_6.090.019">
        <li id="R_6.090.019ab">tān dṛṣṭvā pannagān rāmaḥ samāpatata āhave</li>
        <li id="R_6.090.019cd">astraṃ gārutmataṃ ghoraṃ prāduścakre bhayāvaham</li>
    </ul>
    <ul id="R_6.090.020">
        <li id="R_6.090.020ab">te rāghavadhanurmuktā rukmapuṅkhāḥ śikhiprabhāḥ</li>
        <li id="R_6.090.020cd">suparṇāḥ kāñcanā bhūtvā viceruḥ sarpaśatravaḥ</li>
    </ul>
    <ul id="R_6.090.021">
        <li id="R_6.090.021ab">te tān sarvāñ śarāñ jaghnuḥ sarparūpān mahājavān</li>
        <li id="R_6.090.021cd">suparṇarūpā rāmasya viśikhāḥ kāmarūpiṇaḥ</li>
    </ul>
    <ul id="R_6.090.022">
        <li id="R_6.090.022ab">astre pratihate kruddho rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_6.090.022cd">abhyavarṣat tadā rāmaṃ ghorābhiḥ śaravṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_6.090.023">
        <li id="R_6.090.023ab">tataḥ śarasahasreṇa rāmam akliṣṭakāriṇam</li>
        <li id="R_6.090.023cd">ardayitvā śaraugheṇa mātaliṃ pratyavidhyata</li>
    </ul>
    <ul id="R_6.090.024">
        <li id="R_6.090.024ab">pātayitvā rathopasthe rathāt ketuṃ ca kāñcanam</li>
        <li id="R_6.090.024cd">aindrān abhijaghānāśvāñ śarajālena rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.090.025">
        <li id="R_6.090.025ab">viṣedur devagandharvā dānavāś cāraṇaiḥ saha</li>
        <li id="R_6.090.025cd">rāmam ārtaṃ tadā dṛṣṭvā siddhāś ca paramarṣayaḥ</li>
    </ul>
    <ul id="R_6.090.026">
        <li id="R_6.090.026ab">vyathitā vānarendrāś ca babhūvuḥ savibhīṣaṇāḥ</li>
        <li id="R_6.090.026cd">rāmacandramasaṃ dṛṣṭvā grastaṃ rāvaṇarāhuṇā</li>
    </ul>
    <ul id="R_6.090.027">
        <li id="R_6.090.027ab">prājāpatyaṃ ca nakṣatraṃ rohiṇīṃ śaśinaḥ priyām</li>
        <li id="R_6.090.027cd">samākramya budhas tasthau prajānām aśubhāvahaḥ</li>
    </ul>
    <ul id="R_6.090.028">
        <li id="R_6.090.028ab">sadhūmaparivṛttormiḥ prajvalann iva sāgaraḥ</li>
        <li id="R_6.090.028cd">utpapāta tadā kruddhaḥ spṛśann iva divākaram</li>
    </ul>
    <ul id="R_6.090.029">
        <li id="R_6.090.029ab">śastravarṇaḥ suparuṣo mandaraśmir divākaraḥ</li>
        <li id="R_6.090.029cd">adṛśyata kabandhāṅgaḥ saṃsakto dhūmaketunā</li>
    </ul>
    <ul id="R_6.090.030">
        <li id="R_6.090.030ab">kosalānāṃ ca nakṣatraṃ vyaktam indrāgnidaivatam</li>
        <li id="R_6.090.030cd">ākramyāṅgārakas tasthau viśākhām api cāmbare</li>
    </ul>
    <ul id="R_6.090.031">
        <li id="R_6.090.031ab">daśāsyo viṃśatibhujaḥ pragṛhītaśarāsanaḥ</li>
        <li id="R_6.090.031cd">adṛśyata daśagrīvo maināka iva parvataḥ</li>
    </ul>
    <ul id="R_6.090.032">
        <li id="R_6.090.032ab">nirasyamāno rāmas tu daśagrīveṇa rakṣasā</li>
        <li id="R_6.090.032cd">nāśakad abhisaṃdhātuṃ sāyakān raṇamūrdhani</li>
    </ul>
    <ul id="R_6.090.033">
        <li id="R_6.090.033ab">sa kṛtvā bhrukuṭīṃ kruddhaḥ kiṃ cit saṃraktalocanaḥ</li>
        <li id="R_6.090.033cd">jagāma sumahākrodhaṃ nirdahann iva cakṣuṣā</li>
    </ul>
    <ul id="R_6.091.001">
        <li id="R_6.091.001ab">tasya kruddhasya vadanaṃ dṛṣṭvā rāmasya dhīmataḥ</li>
        <li id="R_6.091.001cd">sarvabhūtāni vitreṣuḥ prākampata ca medinī</li>
    </ul>
    <ul id="R_6.091.002">
        <li id="R_6.091.002ab">siṃhaśārdūlavāñ śailaḥ saṃcacālācaladrumaḥ</li>
        <li id="R_6.091.002cd">babhūva cāpi kṣubhitaḥ samudraḥ saritāṃ patiḥ</li>
    </ul>
    <ul id="R_6.091.003">
        <li id="R_6.091.003ab">khagāś ca kharanirghoṣā gagane paruṣasvanāḥ</li>
        <li id="R_6.091.003cd">autpātikā vinardantaḥ samantāt paricakramuḥ</li>
    </ul>
    <ul id="R_6.091.004">
        <li id="R_6.091.004ab">rāmaṃ dṛṣṭvā susaṃkruddham utpātāṃś ca sudāruṇān</li>
        <li id="R_6.091.004cd">vitreṣuḥ sarvabhūtāni rāvaṇasyāviśad bhayam</li>
    </ul>
    <ul id="R_6.091.005">
        <li id="R_6.091.005ab">vimānasthās tadā devā gandharvāś ca mahoragāḥ</li>
        <li id="R_6.091.005cd">ṛṣidānavadaityāś ca garutmantaś ca khecarāḥ</li>
    </ul>
    <ul id="R_6.091.006">
        <li id="R_6.091.006ab">dadṛśus te tadā yuddhaṃ lokasaṃvartasaṃsthitam</li>
        <li id="R_6.091.006cd">nānāpraharaṇair bhīmaiḥ śūrayoḥ saṃprayudhyatoḥ</li>
    </ul>
    <ul id="R_6.091.007">
        <li id="R_6.091.007ab">ūcuḥ surāsurāḥ sarve tadā vigraham āgatāḥ</li>
        <li id="R_6.091.007cd">prekṣamāṇā mahāyuddhaṃ vākyaṃ bhaktyā prahṛṣṭavat</li>
    </ul>
    <ul id="R_6.091.008">
        <li id="R_6.091.008ab">daśagrīvaṃ jayety āhur asurāḥ samavasthitāḥ</li>
        <li id="R_6.091.008cd">devā rāmam athocus te tvaṃ jayeti punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.091.009">
        <li id="R_6.091.009ab">etasminn antare krodhād rāghavasya sa rāvaṇaḥ</li>
        <li id="R_6.091.009cd">prahartukāmo duṣṭātmā spṛśan praharaṇaṃ mahat</li>
    </ul>
    <ul id="R_6.091.010">
        <li id="R_6.091.010ab">vajrasāraṃ mahānādaṃ sarvaśatrunibarhaṇam</li>
        <li id="R_6.091.010cd">śailaśṛṅganibhaiḥ kūṭaiś citaṃ dṛṣṭibhayāvaham</li>
    </ul>
    <ul id="R_6.091.011">
        <li id="R_6.091.011ab">sadhūmam iva tīkṣṇāgraṃ yugāntāgnicayopamam</li>
        <li id="R_6.091.011cd">atiraudram anāsādyaṃ kālenāpi durāsadam</li>
    </ul>
    <ul id="R_6.091.012">
        <li id="R_6.091.012ab">trāsanaṃ sarvabhūtānāṃ dāraṇaṃ bhedanaṃ tathā</li>
        <li id="R_6.091.012cd">pradīpta iva roṣeṇa śūlaṃ jagrāha rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.091.013">
        <li id="R_6.091.013ab">tac chūlaṃ paramakruddho madhye jagrāha vīryavān</li>
        <li id="R_6.091.013cd">anekaiḥ samare śūrai rākṣasaiḥ parivāritaḥ</li>
    </ul>
    <ul id="R_6.091.014">
        <li id="R_6.091.014ab">samudyamya mahākāyo nanāda yudhi bhairavam</li>
        <li id="R_6.091.014cd">saṃraktanayano roṣāt svasainyam abhiharṣayan</li>
    </ul>
    <ul id="R_6.091.015">
        <li id="R_6.091.015ab">pṛthivīṃ cāntarikṣaṃ ca diśaś ca pradiśas tathā</li>
        <li id="R_6.091.015cd">prākampayat tadā śabdo rākṣasendrasya dāruṇaḥ</li>
    </ul>
    <ul id="R_6.091.016">
        <li id="R_6.091.016ab">atinādasya nādena tena tasya durātmanaḥ</li>
        <li id="R_6.091.016cd">sarvabhūtāni vitreṣuḥ sāgaraś ca pracukṣubhe</li>
    </ul>
    <ul id="R_6.091.017">
        <li id="R_6.091.017ab">sa gṛhītvā mahāvīryaḥ śūlaṃ tad rāvaṇo mahat</li>
        <li id="R_6.091.017cd">vinadya sumahānādaṃ rāmaṃ paruṣam abravīt</li>
    </ul>
    <ul id="R_6.091.018">
        <li id="R_6.091.018ab">śūlo 'yaṃ vajrasāras te rāma roṣān mayodyataḥ</li>
        <li id="R_6.091.018cd">tava bhrātṛsahāyasya sadyaḥ prāṇān hariṣyati</li>
    </ul>
    <ul id="R_6.091.019">
        <li id="R_6.091.019ab">rakṣasām adya śūrāṇāṃ nihatānāṃ camūmukhe</li>
        <li id="R_6.091.019cd">tvāṃ nihatya raṇaślāghin karomi tarasā samam</li>
    </ul>
    <ul id="R_6.091.020">
        <li id="R_6.091.020ab">tiṣṭhedānīṃ nihanmi tvām eṣa śūlena rāghava</li>
        <li id="R_6.091.020cd">evam uktvā sa cikṣepa tac chūlaṃ rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_6.091.021">
        <li id="R_6.091.021ab">āpatantaṃ śaraugheṇa vārayām āsa rāghavaḥ</li>
        <li id="R_6.091.021cd">utpatantaṃ yugāntāgniṃ jalaughair iva vāsavaḥ</li>
    </ul>
    <ul id="R_6.091.022">
        <li id="R_6.091.022ab">nirdadāha sa tān bāṇān rāmakārmukaniḥsṛtān</li>
        <li id="R_6.091.022cd">rāvaṇasya mahāśūlaḥ pataṃgān iva pāvakaḥ</li>
    </ul>
    <ul id="R_6.091.023">
        <li id="R_6.091.023ab">tān dṛṣṭvā bhasmasād bhūtāñ śūlasaṃsparśacūrṇitān</li>
        <li id="R_6.091.023cd">sāyakān antarikṣasthān rāghavaḥ krodham āharat</li>
    </ul>
    <ul id="R_6.091.024">
        <li id="R_6.091.024ab">sa tāṃ mātalinānītāṃ śaktiṃ vāsavanirmitām</li>
        <li id="R_6.091.024cd">jagrāha paramakruddho rāghavo raghunandanaḥ</li>
    </ul>
    <ul id="R_6.091.025">
        <li id="R_6.091.025ab">sā tolitā balavatā śaktir ghaṇṭākṛtasvanā</li>
        <li id="R_6.091.025cd">nabhaḥ prajvālayām āsa yugāntolkeva saprabhā</li>
    </ul>
    <ul id="R_6.091.026">
        <li id="R_6.091.026ab">sā kṣiptā rākṣasendrasya tasmiñ śūle papāta ha</li>
        <li id="R_6.091.026cd">bhinnaḥ śaktyā mahāñ śūlo nipapāta gatadyutiḥ</li>
    </ul>
    <ul id="R_6.091.027">
        <li id="R_6.091.027ab">nirbibheda tato bāṇair hayān asya mahājavān</li>
        <li id="R_6.091.027cd">rāmas tīkṣṇair mahāvegair vajrakalpaiḥ śitaiḥ śaraiḥ</li>
    </ul>
    <ul id="R_6.091.028">
        <li id="R_6.091.028ab">nirbibhedorasi tadā rāvaṇaṃ niśitaiḥ śaraiḥ</li>
        <li id="R_6.091.028cd">rāghavaḥ paramāyatto lalāṭe patribhis tribhiḥ</li>
    </ul>
    <ul id="R_6.091.029">
        <li id="R_6.091.029ab">sa śarair bhinnasarvāṅgo gātraprasrutaśoṇitaḥ</li>
        <li id="R_6.091.029cd">rākṣasendraḥ samūhasthaḥ phullāśoka ivābabhau</li>
    </ul>
    <ul id="R_6.091.030">
        <li id="R_6.091.030ab">
            <seg type="pāda" n="a">sa rāmabāṇair atividdhagātro</seg>
            <seg type="pāda" n="b">niśācarendraḥ kṣatajārdragātraḥ</seg>
        </li>
        <li id="R_6.091.030cd">
            <seg type="pāda" n="c">jagāma khedaṃ ca samājamadhye</seg>
            <seg type="pāda" n="d">krodhaṃ ca cakre subhṛśaṃ tadānīm</seg>
        </li>
    </ul>
    <ul id="R_6.092.001">
        <li id="R_6.092.001ab">sa tu tena tadā krodhāt kākutsthenārdito raṇe</li>
        <li id="R_6.092.001cd">rāvaṇaḥ samaraślāghī mahākrodham upāgamat</li>
    </ul>
    <ul id="R_6.092.002">
        <li id="R_6.092.002ab">sa dīptanayano roṣāc cāpam āyamya vīryavān</li>
        <li id="R_6.092.002cd">abhyardayat susaṃkruddho rāghavaṃ paramāhave</li>
    </ul>
    <ul id="R_6.092.003">
        <li id="R_6.092.003ab">bāṇadhārāsahasrais tu sa toyada ivāmbarāt</li>
        <li id="R_6.092.003cd">rāghavaṃ rāvaṇo bāṇais taṭākam iva pūrayat</li>
    </ul>
    <ul id="R_6.092.004">
        <li id="R_6.092.004ab">pūritaḥ śarajālena dhanurmuktena saṃyuge</li>
        <li id="R_6.092.004cd">mahāgirir ivākampyaḥ kākustho na prakampate</li>
    </ul>
    <ul id="R_6.092.005">
        <li id="R_6.092.005ab">sa śaraiḥ śarajālāni vārayan samare sthitaḥ</li>
        <li id="R_6.092.005cd">gabhastīn iva sūryasya pratijagrāha vīryavān</li>
    </ul>
    <ul id="R_6.092.006">
        <li id="R_6.092.006ab">tataḥ śarasahasrāṇi kṣiprahasto niśācaraḥ</li>
        <li id="R_6.092.006cd">nijaghānorasi kruddho rāghavasya mahātmanaḥ</li>
    </ul>
    <ul id="R_6.092.007">
        <li id="R_6.092.007ab">sa śoṇitasamādigdhaḥ samare lakṣmaṇāgrajaḥ</li>
        <li id="R_6.092.007cd">dṛṣṭaḥ phulla ivāraṇye sumahān kiṃśukadrumaḥ</li>
    </ul>
    <ul id="R_6.092.008">
        <li id="R_6.092.008ab">śarābhighātasaṃrabdhaḥ so 'pi jagrāha sāyakān</li>
        <li id="R_6.092.008cd">kākutsthaḥ sumahātejā yugāntādityavarcasaḥ</li>
    </ul>
    <ul id="R_6.092.009">
        <li id="R_6.092.009ab">tato 'nyonyaṃ susaṃrabdhāv ubhau tau rāmarāvaṇau</li>
        <li id="R_6.092.009cd">śarāndhakāre samare nopālakṣayatāṃ tadā</li>
    </ul>
    <ul id="R_6.092.010">
        <li id="R_6.092.010ab">tataḥ krodhasamāviṣṭo rāmo daśarathātmajaḥ</li>
        <li id="R_6.092.010cd">uvāca rāvaṇaṃ vīraḥ prahasya paruṣaṃ vacaḥ</li>
    </ul>
    <ul id="R_6.092.011">
        <li id="R_6.092.011ab">mama bhāryā janasthānād ajñānād rākṣasādhama</li>
        <li id="R_6.092.011cd">hṛtā te vivaśā yasmāt tasmāt tvaṃ nāsi vīryavān</li>
    </ul>
    <ul id="R_6.092.012">
        <li id="R_6.092.012ab">mayā virahitāṃ dīnāṃ vartamānāṃ mahāvane</li>
        <li id="R_6.092.012cd">vaidehīṃ prasabhaṃ hṛtvā śūro 'ham iti manyase</li>
    </ul>
    <ul id="R_6.092.013">
        <li id="R_6.092.013ab">strīṣu śūra vināthāsu paradārābhimarśake</li>
        <li id="R_6.092.013cd">kṛtvā kāpuruṣaṃ karma śūro 'ham iti manyase</li>
    </ul>
    <ul id="R_6.092.014">
        <li id="R_6.092.014ab">bhinnamaryāda nirlajja cāritreṣv anavasthita</li>
        <li id="R_6.092.014cd">darpān mṛtyum upādāya śūro 'ham iti manyase</li>
    </ul>
    <ul id="R_6.092.015">
        <li id="R_6.092.015ab">śūreṇa dhanadabhrātrā balaiḥ samuditena ca</li>
        <li id="R_6.092.015cd">ślāghanīyaṃ yaśasyaṃ ca kṛtaṃ karma mahat tvayā</li>
    </ul>
    <ul id="R_6.092.016">
        <li id="R_6.092.016ab">utsekenābhipannasya garhitasyāhitasya ca</li>
        <li id="R_6.092.016cd">karmaṇaḥ prāpnuhīdānīṃ tasyādya sumahat phalam</li>
    </ul>
    <ul id="R_6.092.017">
        <li id="R_6.092.017ab">śūro 'ham iti cātmānam avagacchasi durmate</li>
        <li id="R_6.092.017cd">naiva lajjāsti te sītāṃ coravad vyapakarṣataḥ</li>
    </ul>
    <ul id="R_6.092.018">
        <li id="R_6.092.018ab">yadi matsaṃnidhau sītā dharṣitā syāt tvayā balāt</li>
        <li id="R_6.092.018cd">bhrātaraṃ tu kharaṃ paśyes tadā matsāyakair hataḥ</li>
    </ul>
    <ul id="R_6.092.019">
        <li id="R_6.092.019ab">diṣṭyāsi mama duṣṭātmaṃś cakṣurviṣayam āgataḥ</li>
        <li id="R_6.092.019cd">adya tvāṃ sāyakais tīkṣṇair nayāmi yamasādanam</li>
    </ul>
    <ul id="R_6.092.020">
        <li id="R_6.092.020ab">adya te maccharaiś chinnaṃ śiro jvalitakuṇḍalam</li>
        <li id="R_6.092.020cd">kravyādā vyapakarṣantu vikīrṇaṃ raṇapāṃsuṣu</li>
    </ul>
    <ul id="R_6.092.021">
        <li id="R_6.092.021ab">nipatyorasi gṛdhrās te kṣitau kṣiptasya rāvaṇa</li>
        <li id="R_6.092.021cd">pibantu rudhiraṃ tarṣād bāṇaśalyāntarothitam</li>
    </ul>
    <ul id="R_6.092.022">
        <li id="R_6.092.022ab">adya madbāṇābhinnasya gatāsoḥ patitasya te</li>
        <li id="R_6.092.022cd">karṣantv antrāṇi patagā garutmanta ivoragān</li>
    </ul>
    <ul id="R_6.092.023">
        <li id="R_6.092.023ab">ity evaṃ sa vadan vīro rāmaḥ śatrunibarhaṇaḥ</li>
        <li id="R_6.092.023cd">rākṣasendraṃ samīpasthaṃ śaravarṣair avākirat</li>
    </ul>
    <ul id="R_6.092.024">
        <li id="R_6.092.024ab">babhūva dviguṇaṃ vīryaṃ balaṃ harṣaś ca saṃyuge</li>
        <li id="R_6.092.024cd">rāmasyāstrabalaṃ caiva śatror nidhanakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.092.025">
        <li id="R_6.092.025ab">prādurbabhūvur astrāṇi sarvāṇi viditātmanaḥ</li>
        <li id="R_6.092.025cd">praharṣāc ca mahātejāḥ śīghrahastataro 'bhavat</li>
    </ul>
    <ul id="R_6.092.026">
        <li id="R_6.092.026ab">śubhāny etāni cihnāni vijñāyātmagatāni saḥ</li>
        <li id="R_6.092.026cd">bhūya evārdayad rāmo rāvaṇaṃ rākṣasāntakṛt</li>
    </ul>
    <ul id="R_6.092.027">
        <li id="R_6.092.027ab">harīṇāṃ cāśmanikaraiḥ śaravarṣaiś ca rāghavāt</li>
        <li id="R_6.092.027cd">hanyamāno daśagrīvo vighūrṇahṛdayo 'bhavat</li>
    </ul>
    <ul id="R_6.092.028">
        <li id="R_6.092.028ab">yadā ca śastraṃ nārebhe na vyakarṣac charāsanam</li>
        <li id="R_6.092.028cd">nāsya pratyakarod vīryaṃ viklavenāntarātmanā</li>
    </ul>
    <ul id="R_6.092.029">
        <li id="R_6.092.029ab">kṣiptāś cāpi śarās tena śastrāṇi vividhāni ca</li>
        <li id="R_6.092.029cd">na raṇārthāya vartante mṛtyukāle 'bhivartataḥ</li>
    </ul>
    <ul id="R_6.092.030">
        <li id="R_6.092.030ab">sūtas tu rathanetāsya tadavasthaṃ nirīkṣya tam</li>
        <li id="R_6.092.030cd">śanair yuddhād asaṃbhānto rathaṃ tasyāpavāhayat</li>
    </ul>
    <ul id="R_6.093.001">
        <li id="R_6.093.001ab">sa tu mohāt susaṃkruddhaḥ kṛtāntabalacoditaḥ</li>
        <li id="R_6.093.001cd">krodhasaṃraktanayano rāvaṇo sūtam abravīt</li>
    </ul>
    <ul id="R_6.093.002">
        <li id="R_6.093.002ab">hīnavīryam ivāśaktaṃ pauruṣeṇa vivarjitam</li>
        <li id="R_6.093.002cd">bhīruṃ laghum ivāsattvaṃ vihīnam iva tejasā</li>
    </ul>
    <ul id="R_6.093.003">
        <li id="R_6.093.003ab">vimuktam iva māyābhir astrair iva bahiṣkṛtam</li>
        <li id="R_6.093.003cd">mām avajñāya durbuddhe svayā buddhyā viceṣṭase</li>
    </ul>
    <ul id="R_6.093.004">
        <li id="R_6.093.004ab">kimarthaṃ mām avajñāya macchandam anavekṣya ca</li>
        <li id="R_6.093.004cd">tvayā śatrusamakṣaṃ me ratho 'yam apavāhitaḥ</li>
    </ul>
    <ul id="R_6.093.005">
        <li id="R_6.093.005ab">tvayādya hi mamānārya cirakālasamārjitam</li>
        <li id="R_6.093.005cd">yaśo vīryaṃ ca tejaś ca pratyayaś ca vināśitaḥ</li>
    </ul>
    <ul id="R_6.093.006">
        <li id="R_6.093.006ab">śatroḥ prakhyātavīryasya rañjanīyasya vikramaiḥ</li>
        <li id="R_6.093.006cd">paśyato yuddhalubdho 'haṃ kṛtaḥ kāpuruṣas tvayā</li>
    </ul>
    <ul id="R_6.093.007">
        <li id="R_6.093.007ab">yas tvaṃ ratham imaṃ mohān na codvahasi durmate</li>
        <li id="R_6.093.007cd">satyo 'yaṃ pratitarko me pareṇa tvam upaskṛtaḥ</li>
    </ul>
    <ul id="R_6.093.008">
        <li id="R_6.093.008ab">na hīdaṃ vidyate karma suhṛdo hitakāṅkṣiṇaḥ</li>
        <li id="R_6.093.008cd">ripūṇāṃ sadṛśaṃ caitan na tvayaitat svanuṣṭhitam</li>
    </ul>
    <ul id="R_6.093.009">
        <li id="R_6.093.009ab">nivartaya rathaṃ śīghraṃ yāvan nāpaiti me ripuḥ</li>
        <li id="R_6.093.009cd">yadi vāpy uṣito 'si tvaṃ smaryante yadi vā guṇāḥ</li>
    </ul>
    <ul id="R_6.093.010">
        <li id="R_6.093.010ab">evaṃ paruṣam uktas tu hitabuddhir abuddhinā</li>
        <li id="R_6.093.010cd">abravīd rāvaṇaṃ sūto hitaṃ sānunayaṃ vacaḥ</li>
    </ul>
    <ul id="R_6.093.011">
        <li id="R_6.093.011ab">na bhīto 'smi na mūḍho 'smi nopajapto 'smi śatrubhiḥ</li>
        <li id="R_6.093.011cd">na pramatto na niḥsneho vismṛtā na ca satkriyā</li>
    </ul>
    <ul id="R_6.093.012">
        <li id="R_6.093.012ab">mayā tu hitakāmena yaśaś ca parirakṣatā</li>
        <li id="R_6.093.012cd">snehapraskannamanasā priyam ity apriyaṃ kṛtam</li>
    </ul>
    <ul id="R_6.093.013">
        <li id="R_6.093.013ab">nāsminn arthe mahārāja tvaṃ māṃ priyahite ratam</li>
        <li id="R_6.093.013cd">kaś cil laghur ivānāryo doṣato gantum arhasi</li>
    </ul>
    <ul id="R_6.093.014">
        <li id="R_6.093.014ab">śrūyatām abhidhāsyāmi yannimittaṃ mayā rathaḥ</li>
        <li id="R_6.093.014cd">nadīvega ivāmbhobhiḥ saṃyuge vinivartitaḥ</li>
    </ul>
    <ul id="R_6.093.015">
        <li id="R_6.093.015ab">śramaṃ tavāvagacchāmi mahatā raṇakarmaṇā</li>
        <li id="R_6.093.015cd">na hi te vīra saumukhyaṃ praharṣaṃ vopadhāraye</li>
    </ul>
    <ul id="R_6.093.016">
        <li id="R_6.093.016ab">rathodvahanakhinnāś ca ta ime rathavājinaḥ</li>
        <li id="R_6.093.016cd">dīnā gharmapariśrāntā gāvo varṣahatā iva</li>
    </ul>
    <ul id="R_6.093.017">
        <li id="R_6.093.017ab">nimittāni ca bhūyiṣṭhaṃ yāni prādurbhavanti naḥ</li>
        <li id="R_6.093.017cd">teṣu teṣv abhipanneṣu lakṣayāmy apradakṣiṇam</li>
    </ul>
    <ul id="R_6.093.018">
        <li id="R_6.093.018ab">deśakālau ca vijñeyau lakṣaṇānīṅgitāni ca</li>
        <li id="R_6.093.018cd">dainyaṃ harṣaś ca khedaś ca rathinaś ca balābalam</li>
    </ul>
    <ul id="R_6.093.019">
        <li id="R_6.093.019ab">sthalanimnāni bhūmeś ca samāni viṣamāṇi ca</li>
        <li id="R_6.093.019cd">yuddhakālaś ca vijñeyaḥ parasyāntaradarśanam</li>
    </ul>
    <ul id="R_6.093.020">
        <li id="R_6.093.020ab">upayānāpayāne ca sthānaṃ pratyapasarpaṇam</li>
        <li id="R_6.093.020cd">sarvam etad rathasthena jñeyaṃ rathakuṭumbinā</li>
    </ul>
    <ul id="R_6.093.021">
        <li id="R_6.093.021ab">tava viśrāmahetos tu tathaiṣāṃ rathavājinām</li>
        <li id="R_6.093.021cd">raudraṃ varjayatā khedaṃ kṣamaṃ kṛtam idaṃ mayā</li>
    </ul>
    <ul id="R_6.093.022">
        <li id="R_6.093.022ab">na mayā svecchayā vīra ratho 'yam apavāhitaḥ</li>
        <li id="R_6.093.022cd">bhartṛsnehaparītena mayedaṃ yatkṛtaṃ vibho</li>
    </ul>
    <ul id="R_6.093.023">
        <li id="R_6.093.023ab">ājñāpaya yathātattvaṃ vakṣyasy ariniṣūdana</li>
        <li id="R_6.093.023cd">tat kariṣyāmy ahaṃ vīraṃ gatānṛṇyena cetasā</li>
    </ul>
    <ul id="R_6.093.024">
        <li id="R_6.093.024ab">saṃtuṣṭas tena vākyena rāvaṇas tasya sāratheḥ</li>
        <li id="R_6.093.024cd">praśasyainaṃ bahuvidhaṃ yuddhalubdho 'bravīd idam</li>
    </ul>
    <ul id="R_6.093.025">
        <li id="R_6.093.025ab">rathaṃ śīghram imaṃ sūta rāghavābhimukhaṃ kuru</li>
        <li id="R_6.093.025cd">nāhatvā samare śatrūn nivartiṣyati rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.093.026">
        <li id="R_6.093.026ab">evam uktvā tatas tuṣṭo rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_6.093.026cd">dadau tasya śubhaṃ hy ekaṃ hastābharaṇam uttamam</li>
    </ul>
    <ul id="R_6.093.027">
        <li id="R_6.093.027ab">
            <seg type="pāda" n="a">tato drutaṃ rāvaṇavākyacoditaḥ</seg>
            <seg type="pāda" n="b">pracodayām āsa hayān sa sārathiḥ</seg>
        </li>
        <li id="R_6.093.027cd">
            <seg type="pāda" n="c">sa rākṣasendrasya tato mahārathaḥ</seg>
            <seg type="pāda" n="d">kṣaṇena rāmasya raṇāgrato 'bhavat</seg>
        </li>
    </ul>
    <ul id="R_6.094.001">
        <li id="R_6.094.001ab">tam āpatantaṃ sahasā svanavantaṃ mahādhvajam</li>
        <li id="R_6.094.001cd">rathaṃ rākṣasarājasya nararājo dadarśa ha</li>
    </ul>
    <ul id="R_6.094.002">
        <li id="R_6.094.002ab">kṛṣṇavājisamāyuktaṃ yuktaṃ raudreṇa varcasā</li>
        <li id="R_6.094.002cd">taḍitpatākāgahanaṃ darśitendrāyudhāyudham</li>
        <li id="R_6.094.002ef">śaradhārā vimuñcantaṃ dhārāsāram ivānbudam</li>
    </ul>
    <ul id="R_6.094.003">
        <li id="R_6.094.003ab">taṃ dṛṣṭvā meghasaṃkāśam āpatantaṃ rathaṃ ripoḥ</li>
        <li id="R_6.094.003cd">girer vajrābhimṛṣṭasya dīryataḥ sadṛśasvanam</li>
        <li id="R_6.094.003ef">uvāca mātaliṃ rāmaḥ sahasrākṣasya sārathim</li>
    </ul>
    <ul id="R_6.094.004">
        <li id="R_6.094.004ab">mātale paśya saṃrabdham āpatantaṃ rathaṃ ripoḥ</li>
        <li id="R_6.094.004cd">yathāpasavyaṃ patatā vegena mahatā punaḥ</li>
        <li id="R_6.094.004ef">samare hantum ātmānaṃ tathānena kṛtā matiḥ</li>
    </ul>
    <ul id="R_6.094.005">
        <li id="R_6.094.005ab">tad apramādam ātiṣṭha pratyudgaccha rathaṃ ripoḥ</li>
        <li id="R_6.094.005cd">vidhvaṃsayitum icchāmi vāyur megham ivotthitam</li>
    </ul>
    <ul id="R_6.094.006">
        <li id="R_6.094.006ab">aviklavam asaṃbhrāntam avyagrahṛdayekṣaṇam</li>
        <li id="R_6.094.006cd">raśmisaṃcāraniyataṃ pracodaya rathaṃ drutam</li>
    </ul>
    <ul id="R_6.094.007">
        <li id="R_6.094.007ab">kāmaṃ na tvaṃ samādheyaḥ puraṃdararathocitaḥ</li>
        <li id="R_6.094.007cd">yuyutsur aham ekāgraḥ smāraye tvāṃ na śikṣaye</li>
    </ul>
    <ul id="R_6.094.008">
        <li id="R_6.094.008ab">parituṣṭaḥ sa rāmasya tena vākyena mātaliḥ</li>
        <li id="R_6.094.008cd">pracodayām āsa rathaṃ surasārathisattamaḥ</li>
    </ul>
    <ul id="R_6.094.009">
        <li id="R_6.094.009ab">apasavyaṃ tataḥ kurvan rāvaṇasya mahāratham</li>
        <li id="R_6.094.009cd">cakrotkṣiptena rajasā rāvaṇaṃ vyavadhūnayat</li>
    </ul>
    <ul id="R_6.094.010">
        <li id="R_6.094.010ab">tataḥ kruddho daśagrīvas tāmravisphāritekṣaṇaḥ</li>
        <li id="R_6.094.010cd">rathapratimukhaṃ rāmaṃ sāyakair avadhūnayat</li>
    </ul>
    <ul id="R_6.094.011">
        <li id="R_6.094.011ab">dharṣaṇāmarṣito rāmo dhairyaṃ roṣeṇa laṅghayan</li>
        <li id="R_6.094.011cd">jagrāha sumahāvegam aindraṃ yudhi śarāsanam</li>
        <li id="R_6.094.011ef">śarāṃś ca sumahātejāḥ sūryaraśmisamaprabhān</li>
    </ul>
    <ul id="R_6.094.012">
        <li id="R_6.094.012ab">tad upoḍhaṃ mahad yuddham anyonyavadhakāṅkṣiṇoḥ</li>
        <li id="R_6.094.012cd">parasparābhimukhayor dṛptayor iva siṃhayoḥ</li>
    </ul>
    <ul id="R_6.094.013">
        <li id="R_6.094.013ab">tato devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_6.094.013cd">samīyur dvairathaṃ draṣṭuṃ rāvaṇakṣayakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.094.014">
        <li id="R_6.094.014ab">samutpetur athotpātā dāruṇā lomaharṣaṇāḥ</li>
        <li id="R_6.094.014cd">rāvaṇasya vināśāya rāghavasya jayāya ca</li>
    </ul>
    <ul id="R_6.094.015">
        <li id="R_6.094.015ab">vavarṣa rudhiraṃ devo rāvaṇasya rathopari</li>
        <li id="R_6.094.015cd">vātā maṇḍalinas tīvrā apasavyaṃ pracakramuḥ</li>
    </ul>
    <ul id="R_6.094.016">
        <li id="R_6.094.016ab">mahad gṛdhrakulaṃ cāsya bhramamāṇaṃ nabhastale</li>
        <li id="R_6.094.016cd">yena yena ratho yāti tena tena pradhāvati</li>
    </ul>
    <ul id="R_6.094.017">
        <li id="R_6.094.017ab">saṃdhyayā cāvṛtā laṅkā japāpuṣpanikāśayā</li>
        <li id="R_6.094.017cd">dṛśyate saṃpradīteva divase 'pi vasuṃdharā</li>
    </ul>
    <ul id="R_6.094.018">
        <li id="R_6.094.018ab">sanirghātā maholkāś ca saṃpracetur mahāsvanāḥ</li>
        <li id="R_6.094.018cd">viṣādayantyo rakṣāṃsi rāvaṇasya tadāhitāḥ</li>
    </ul>
    <ul id="R_6.094.019">
        <li id="R_6.094.019ab">rāvaṇaś ca yatas tatra pracacāla vasuṃdharā</li>
        <li id="R_6.094.019cd">rakṣasāṃ ca praharatāṃ gṛhītā iva bāhavaḥ</li>
    </ul>
    <ul id="R_6.094.020">
        <li id="R_6.094.020ab">tāmrāḥ pītāḥ sitāḥ śvetāḥ patitāḥ sūryaraśmayaḥ</li>
        <li id="R_6.094.020cd">dṛśyante rāvaṇasyāṅge parvatasyeva dhātavaḥ</li>
    </ul>
    <ul id="R_6.094.021">
        <li id="R_6.094.021ab">gṛdhrair anugatāś cāsya vamantyo jvalanaṃ mukhaiḥ</li>
        <li id="R_6.094.021cd">praṇedur mukham īkṣantyaḥ saṃrabdham aśivaṃ śivāḥ</li>
    </ul>
    <ul id="R_6.094.022">
        <li id="R_6.094.022ab">pratikūlaṃ vavau vāyū raṇe pāṃsūn samutkiran</li>
        <li id="R_6.094.022cd">tasya rākṣasarājasya kurvan dṛṣṭivilopanam</li>
    </ul>
    <ul id="R_6.094.023">
        <li id="R_6.094.023ab">nipetur indrāśanayaḥ sainye cāsya samantataḥ</li>
        <li id="R_6.094.023cd">durviṣahya svanā ghorā vinā jaladharasvanam</li>
    </ul>
    <ul id="R_6.094.024">
        <li id="R_6.094.024ab">diśaś ca pradiśaḥ sarvā babhūvus timirāvṛtāḥ</li>
        <li id="R_6.094.024cd">pāṃsuvarṣeṇa mahatā durdarśaṃ ca nabho 'bhavat</li>
    </ul>
    <ul id="R_6.094.025">
        <li id="R_6.094.025ab">kurvantyaḥ kalahaṃ ghoraṃ sārikās tadrathaṃ prati</li>
        <li id="R_6.094.025cd">nipetuḥ śataśas tatra dāruṇā dāruṇasvanāḥ</li>
    </ul>
    <ul id="R_6.094.026">
        <li id="R_6.094.026ab">jaghanebhyaḥ sphuliṅgāṃś ca netrebhyo 'śrūṇi saṃtatam</li>
        <li id="R_6.094.026cd">mumucus tasya turagās tulyam agniṃ ca vāri ca</li>
    </ul>
    <ul id="R_6.094.027">
        <li id="R_6.094.027ab">evaṃ prakārā bahavaḥ samutpātā bhayāvahāḥ</li>
        <li id="R_6.094.027cd">rāvaṇasya vināśāya dāruṇāḥ saṃprajajñire</li>
    </ul>
    <ul id="R_6.094.028">
        <li id="R_6.094.028ab">rāmasyāpi nimittāni saumyāni ca śivāni ca</li>
        <li id="R_6.094.028cd">babhūvur jayaśaṃsīni prādurbhūtāni sarvaśaḥ</li>
    </ul>
    <ul id="R_6.094.029">
        <li id="R_6.094.029ab">
            <seg type="pāda" n="a">tato nirīkṣyātmagatāni rāghavo</seg>
            <seg type="pāda" n="b">raṇe nimittāni nimittakovidaḥ</seg>
        </li>
        <li id="R_6.094.029cd">
            <seg type="pāda" n="c">jagāma harṣaṃ ca parāṃ ca nirvṛtiṃ</seg>
            <seg type="pāda" n="d">cakāra yuddhe 'bhyadhikaṃ ca vikramam</seg>
        </li>
    </ul>
    <ul id="R_6.095.001">
        <li id="R_6.095.001ab">tataḥ pravṛttaṃ sukrūraṃ rāmarāvaṇayos tadā</li>
        <li id="R_6.095.001cd">sumahad dvairathaṃ yuddhaṃ sarvalokabhayāvaham</li>
    </ul>
    <ul id="R_6.095.002">
        <li id="R_6.095.002ab">tato rākṣasasainyaṃ ca harīṇāṃ ca mahad balam</li>
        <li id="R_6.095.002cd">pragṛhītapraharaṇaṃ niśceṣṭaṃ samatiṣṭhata</li>
    </ul>
    <ul id="R_6.095.003">
        <li id="R_6.095.003ab">saṃprayuddhau tato dṛṣṭvā balavan nararākṣasau</li>
        <li id="R_6.095.003cd">vyākṣiptahṛdayāḥ sarve paraṃ vismayam āgatāḥ</li>
    </ul>
    <ul id="R_6.095.004">
        <li id="R_6.095.004ab">nānāpraharaṇair vyagrair bhujair vismitabuddhayaḥ</li>
        <li id="R_6.095.004cd">tasthuḥ prekṣya ca saṃgrāmaṃ nābhijaghnuḥ parasparam</li>
    </ul>
    <ul id="R_6.095.005">
        <li id="R_6.095.005ab">rakṣasāṃ rāvaṇaṃ cāpi vānarāṇāṃ ca rāghavam</li>
        <li id="R_6.095.005cd">paśyatāṃ vismitākṣāṇāṃ sainyaṃ citram ivābabhau</li>
    </ul>
    <ul id="R_6.095.006">
        <li id="R_6.095.006ab">tau tu tatra nimittāni dṛṣṭvā rāghavarāvaṇau</li>
        <li id="R_6.095.006cd">kṛtabuddhī sthirāmarṣau yuyudhāte abhītavat</li>
    </ul>
    <ul id="R_6.095.007">
        <li id="R_6.095.007ab">jetavyam iti kākutstho martavyam iti rāvaṇaḥ</li>
        <li id="R_6.095.007cd">dhṛtau svavīryasarvasvaṃ yuddhe 'darśayatāṃ tadā</li>
    </ul>
    <ul id="R_6.095.008">
        <li id="R_6.095.008ab">tataḥ krodhād daśagrīvaḥ śarān saṃdhāya vīryavān</li>
        <li id="R_6.095.008cd">mumoca dhvajam uddiśya rāghavasya rathe sthitam</li>
    </ul>
    <ul id="R_6.095.009">
        <li id="R_6.095.009ab">te śarās tam anāsādya puraṃdararathadhvajam</li>
        <li id="R_6.095.009cd">raktaśaktiṃ parāmṛśya nipetur dharaṇītale</li>
    </ul>
    <ul id="R_6.095.010">
        <li id="R_6.095.010ab">tato rāmo 'bhisaṃkruddhaś cāpam āyamya vīryavān</li>
        <li id="R_6.095.010cd">kṛtapratikṛtaṃ kartuṃ manasā saṃpracakrame</li>
    </ul>
    <ul id="R_6.095.011">
        <li id="R_6.095.011ab">rāvaṇadhvajam uddiśya mumoca niśitaṃ śaram</li>
        <li id="R_6.095.011cd">mahāsarpam ivāsahyaṃ jvalantaṃ svena tejasā</li>
    </ul>
    <ul id="R_6.095.012">
        <li id="R_6.095.012ab">jagāma sa mahīṃ bhittvā daśagrīvadhvajaṃ śaraḥ</li>
        <li id="R_6.095.012cd">sa nikṛtto 'patad bhūmau rāvaṇasya rathadhvajaḥ</li>
    </ul>
    <ul id="R_6.095.013">
        <li id="R_6.095.013ab">dhvajasyonmathanaṃ dṛṣṭvā rāvaṇaḥ sumahābalaḥ</li>
        <li id="R_6.095.013cd">krodhajenāgninā saṃkhye pradīpta iva cābhavat</li>
    </ul>
    <ul id="R_6.095.014">
        <li id="R_6.095.014ab">sa roṣavaśam āpannaḥ śaravarṣaṃ mahad vaman</li>
        <li id="R_6.095.014cd">rāmasya turagān divyāñ śarair vivyādha rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.095.015">
        <li id="R_6.095.015ab">te viddhā harayas tasya nāskhalan nāpi babhramuḥ</li>
        <li id="R_6.095.015cd">babhūvuḥ svasthahṛdayāḥ padmanālair ivāhatāḥ</li>
    </ul>
    <ul id="R_6.095.016">
        <li id="R_6.095.016ab">teṣām asaṃbhramaṃ dṛṣṭvā vājināṃ rāvaṇas tadā</li>
        <li id="R_6.095.016cd">bhūya eva susaṃkruddhaḥ śaravarṣaṃ mumoca ha</li>
    </ul>
    <ul id="R_6.095.017">
        <li id="R_6.095.017ab">gadāś ca parighāṃś caiva cakrāṇi musalāni ca</li>
        <li id="R_6.095.017cd">giriśṛṅgāṇi vṛkṣāṃś ca tathā śūlaparaśvadhān</li>
    </ul>
    <ul id="R_6.095.018">
        <li id="R_6.095.018ab">māyāvihitam etat tu śastravarṣam apātayat</li>
        <li id="R_6.095.018cd">sahasraśas tato bāṇān aśrāntahṛdayodyamaḥ</li>
    </ul>
    <ul id="R_6.095.019">
        <li id="R_6.095.019ab">tumulaṃ trāsajananaṃ bhīmaṃ bhīmapratisvanam</li>
        <li id="R_6.095.019cd">durdharṣam abhavad yuddhe naikaśastramayaṃ mahat</li>
    </ul>
    <ul id="R_6.095.020">
        <li id="R_6.095.020ab">vimucya rāghavarathaṃ samantād vānare bale</li>
        <li id="R_6.095.020cd">sāyakair antarikṣaṃ ca cakārāśu nirantaram</li>
        <li id="R_6.095.020ef">mumoca ca daśagrīvo niḥsaṅgenāntarātmanā</li>
    </ul>
    <ul id="R_6.095.021">
        <li id="R_6.095.021ab">vyāyacchamānaṃ taṃ dṛṣṭvā tatparaṃ rāvaṇaṃ raṇe</li>
        <li id="R_6.095.021cd">prahasann iva kākutsthaḥ saṃdadhe sāyakāñ śitān</li>
    </ul>
    <ul id="R_6.095.022">
        <li id="R_6.095.022ab">sa mumoca tato bāṇān raṇe śatasahasraśaḥ</li>
        <li id="R_6.095.022cd">tān dṛṣṭvā rāvaṇaś cakre svaśaraiḥ khaṃ nirantaram</li>
    </ul>
    <ul id="R_6.095.023">
        <li id="R_6.095.023ab">tatas tābhyāṃ prayuktena śaravarṣeṇa bhāsvatā</li>
        <li id="R_6.095.023cd">śarabaddham ivābhāti dvitīyaṃ bhāsvad ambaram</li>
    </ul>
    <ul id="R_6.095.024">
        <li id="R_6.095.024ab">nānimitto 'bhavad bāṇo nātibhettā na niṣphalaḥ</li>
        <li id="R_6.095.024cd">tathā visṛjator bāṇān rāmarāvaṇayor mṛdhe</li>
    </ul>
    <ul id="R_6.095.025">
        <li id="R_6.095.025ab">prāyudhyetām avicchinnam asyantau savyadakṣiṇam</li>
        <li id="R_6.095.025cd">cakratus tau śaraughais tu nirucchvāsam ivāmbaram</li>
    </ul>
    <ul id="R_6.095.026">
        <li id="R_6.095.026ab">rāvaṇasya hayān rāmo hayān rāmasya rāvaṇaḥ</li>
        <li id="R_6.095.026cd">jaghnatus tau tadānyonyaṃ kṛtānukṛtakāriṇau</li>
    </ul>
    <ul id="R_6.096.001">
        <li id="R_6.096.001ab">tau tathā yudhyamānau tu samare rāmarāvaṇau</li>
        <li id="R_6.096.001cd">dadṛśuḥ sarvabhūtāni vismitenāntarātmanā</li>
    </ul>
    <ul id="R_6.096.002">
        <li id="R_6.096.002ab">ardayantau tu samare tayos tau syandanottamau</li>
        <li id="R_6.096.002cd">parasparavadhe yuktau ghorarūpau babhūvatuḥ</li>
    </ul>
    <ul id="R_6.096.003">
        <li id="R_6.096.003ab">maṇḍalāni ca vīthīś ca gatapratyāgatāni ca</li>
        <li id="R_6.096.003cd">darśayantau bahuvidhāṃ sūtau sārathyajāṃ gatim</li>
    </ul>
    <ul id="R_6.096.004">
        <li id="R_6.096.004ab">ardayan rāvaṇaṃ rāmo rāghavaṃ cāpi rāvaṇaḥ</li>
        <li id="R_6.096.004cd">gativegaṃ samāpannau pravartana nivartane</li>
    </ul>
    <ul id="R_6.096.005">
        <li id="R_6.096.005ab">kṣipatoḥ śarajālāni tayos tau syandanottamau</li>
        <li id="R_6.096.005cd">ceratuḥ saṃyugamahīṃ sāsārau jaladāv iva</li>
    </ul>
    <ul id="R_6.096.006">
        <li id="R_6.096.006ab">darśayitvā tadā tau tu gatiṃ bahuvidhāṃ raṇe</li>
        <li id="R_6.096.006cd">parasparasyābhimukhau punar eva ca tasthatuḥ</li>
    </ul>
    <ul id="R_6.096.007">
        <li id="R_6.096.007ab">dhuraṃ dhureṇa rathayor vaktraṃ vaktreṇa vājinām</li>
        <li id="R_6.096.007cd">patākāś ca patākābhiḥ sameyuḥ sthitayos tadā</li>
    </ul>
    <ul id="R_6.096.008">
        <li id="R_6.096.008ab">rāvaṇasya tato rāmo dhanurmuktaiḥ śitaiḥ śaraiḥ</li>
        <li id="R_6.096.008cd">caturbhiś caturo dīptān hayān pratyapasarpayat</li>
    </ul>
    <ul id="R_6.096.009">
        <li id="R_6.096.009ab">sa krodhavaśam āpanno hayānām apasarpaṇe</li>
        <li id="R_6.096.009cd">mumoca niśitān bāṇān rāghavāya niśācaraḥ</li>
    </ul>
    <ul id="R_6.096.010">
        <li id="R_6.096.010ab">so 'tividdho balavatā daśagrīveṇa rāghavaḥ</li>
        <li id="R_6.096.010cd">jagāma na vikāraṃ ca na cāpi vyathito 'bhavat</li>
    </ul>
    <ul id="R_6.096.011">
        <li id="R_6.096.011ab">cikṣepa ca punar bāṇān vajrapātasamasvanān</li>
        <li id="R_6.096.011cd">sārathiṃ vajrahastasya samuddiśya niśācaraḥ</li>
    </ul>
    <ul id="R_6.096.012">
        <li id="R_6.096.012ab">mātales tu mahāvegāḥ śarīre patitāḥ śarāḥ</li>
        <li id="R_6.096.012cd">na sūkṣmam api saṃmohaṃ vyathāṃ vā pradadur yudhi</li>
    </ul>
    <ul id="R_6.096.013">
        <li id="R_6.096.013ab">tayā dharṣaṇayā kroddho mātaler na tathātmanaḥ</li>
        <li id="R_6.096.013cd">cakāra śarajālena rāghavo vimukhaṃ ripum</li>
    </ul>
    <ul id="R_6.096.014">
        <li id="R_6.096.014ab">viṃśatiṃ triṃśataṃ ṣaṣṭiṃ śataśo 'tha sahasraśaḥ</li>
        <li id="R_6.096.014cd">mumoca rāghavo vīraḥ sāyakān syandane ripoḥ</li>
    </ul>
    <ul id="R_6.096.015">
        <li id="R_6.096.015ab">gadānāṃ musalānāṃ ca parighāṇāṃ ca nisvanaiḥ</li>
        <li id="R_6.096.015cd">śarāṇāṃ puṅkhavātaiś ca kṣubhitāḥ saptasāgarāḥ</li>
    </ul>
    <ul id="R_6.096.016">
        <li id="R_6.096.016ab">kṣubdhānāṃ sāgarāṇāṃ ca pātālatalavāsinaḥ</li>
        <li id="R_6.096.016cd">vyathitāḥ pannagāḥ sarve dānavāś ca sahasraśaḥ</li>
    </ul>
    <ul id="R_6.096.017">
        <li id="R_6.096.017ab">cakampe medinī kṛtsnā saśailavanakānanā</li>
        <li id="R_6.096.017cd">bhāskaro niṣprabhaś cābhūn na vavau cāpi mārutaḥ</li>
    </ul>
    <ul id="R_6.096.018">
        <li id="R_6.096.018ab">tato devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_6.096.018cd">cintām āpedire sarve sakiṃnaramahoragāḥ</li>
    </ul>
    <ul id="R_6.096.019">
        <li id="R_6.096.019ab">svasti gobrāhmaṇebhyo 'stu lokās tiṣṭhantu śāśvatāḥ</li>
        <li id="R_6.096.019cd">jayatāṃ rāghavaḥ saṃkhye rāvaṇaṃ rākṣaseśvaram</li>
    </ul>
    <ul id="R_6.096.020">
        <li id="R_6.096.020ab">tataḥ kruddho mahābāhū raghūṇāṃ kīrtivardhanaḥ</li>
        <li id="R_6.096.020cd">saṃdhāya dhanuṣā rāmaḥ kṣuram āśīviṣopamam</li>
        <li id="R_6.096.020ef">rāvaṇasya śiro 'cchindac chrīmaj jvalitakuṇḍalam</li>
    </ul>
    <ul id="R_6.096.021">
        <li id="R_6.096.021ab">tac chiraḥ patitaṃ bhūmau dṛṣṭaṃ lokais tribhis tadā</li>
        <li id="R_6.096.021cd">tasyaiva sadṛśaṃ cānyad rāvaṇasyotthitaṃ śiraḥ</li>
    </ul>
    <ul id="R_6.096.022">
        <li id="R_6.096.022ab">tat kṣipraṃ kṣiprahastena rāmeṇa kṣiprakāriṇā</li>
        <li id="R_6.096.022cd">dvitīyaṃ rāvaṇaśiraś chinnaṃ saṃyati sāyakaiḥ</li>
    </ul>
    <ul id="R_6.096.023">
        <li id="R_6.096.023ab">chinnamātraṃ ca tac chīrṣaṃ punar anyat sma dṛśyate</li>
        <li id="R_6.096.023cd">tad apy aśanisaṃkāśaiś chinnaṃ rāmeṇa sāyakaiḥ</li>
    </ul>
    <ul id="R_6.096.024">
        <li id="R_6.096.024ab">evam eva śataṃ chinnaṃ śirasāṃ tulyavarcasām</li>
        <li id="R_6.096.024cd">na caiva rāvaṇasyānto dṛśyate jīvitakṣaye</li>
    </ul>
    <ul id="R_6.096.025">
        <li id="R_6.096.025ab">tataḥ sarvāstravid vīraḥ kausalyānandivardhanaḥ</li>
        <li id="R_6.096.025cd">mārgaṇair bahubhir yuktaś cintayām āsa rāghavaḥ</li>
    </ul>
    <ul id="R_6.096.026">
        <li id="R_6.096.026ab">mārīco nihato yais tu kharo yais tu sudūṣaṇaḥ</li>
        <li id="R_6.096.026cd">krañcāraṇye virādhas tu kabandho daṇḍakā vane</li>
    </ul>
    <ul id="R_6.096.027">
        <li id="R_6.096.027ab">ta ime sāyakāḥ sarve yuddhe pratyayikā mama</li>
        <li id="R_6.096.027cd">kiṃ nu tat kāraṇaṃ yena rāvaṇe mandatejasaḥ</li>
    </ul>
    <ul id="R_6.096.028">
        <li id="R_6.096.028ab">iti cintāparaś cāsīd apramattaś ca saṃyuge</li>
        <li id="R_6.096.028cd">vavarṣa śaravarṣāṇi rāghavo rāvaṇorasi</li>
    </ul>
    <ul id="R_6.096.029">
        <li id="R_6.096.029ab">rāvaṇo 'pi tataḥ kruddho rathastho rākṣaseśvaraḥ</li>
        <li id="R_6.096.029cd">gadāmusalavarṣeṇa rāmaṃ pratyardayad raṇe</li>
    </ul>
    <ul id="R_6.096.030">
        <li id="R_6.096.030ab">devadānavayakṣāṇāṃ piśācoragarakṣasām</li>
        <li id="R_6.096.030cd">paśyatāṃ tan mahad yuddhaṃ sarvarātram avartata</li>
    </ul>
    <ul id="R_6.096.031">
        <li id="R_6.096.031ab">naiva ratriṃ na divasaṃ na muhūrtaṃ na cakṣaṇam</li>
        <li id="R_6.096.031cd">rāmarāvaṇayor yuddhaṃ virāmam upagacchati</li>
    </ul>
    <ul id="R_6.097.001">
        <li id="R_6.097.001ab">atha saṃsmārayām āsa rāghavaṃ mātalis tadā</li>
        <li id="R_6.097.001cd">ajānann iva kiṃ vīra tvam enam anuvartase</li>
    </ul>
    <ul id="R_6.097.002">
        <li id="R_6.097.002ab">visṛjāsmai vadhāya tvam astraṃ paitāmahaṃ prabho</li>
        <li id="R_6.097.002cd">vināśakālaḥ kathito yaḥ suraiḥ so 'dya vartate</li>
    </ul>
    <ul id="R_6.097.003">
        <li id="R_6.097.003ab">tataḥ saṃsmārito rāmas tena vākyena mātaleḥ</li>
        <li id="R_6.097.003cd">jagrāha sa śaraṃ dīptaṃ niśvasantam ivoragam</li>
    </ul>
    <ul id="R_6.097.004">
        <li id="R_6.097.004ab">yam asmai prathamaṃ prādād agastyo bhagavān ṛṣiḥ</li>
        <li id="R_6.097.004cd">brahmadattaṃ mahad bāṇam amoghaṃ yudhi vīryavān</li>
    </ul>
    <ul id="R_6.097.005">
        <li id="R_6.097.005ab">brahmaṇā nirmitaṃ pūrvam indrārtham amitaujasā</li>
        <li id="R_6.097.005cd">dattaṃ surapateḥ pūrvaṃ trilokajayakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_6.097.006">
        <li id="R_6.097.006ab">yasya vājeṣu pavanaḥ phale pāvakabhāskarau</li>
        <li id="R_6.097.006cd">śarīram ākāśamayaṃ gaurave merumandarau</li>
    </ul>
    <ul id="R_6.097.007">
        <li id="R_6.097.007ab">jājvalyamānaṃ vapuṣā supuṅkhaṃ hemabhūṣitam</li>
        <li id="R_6.097.007cd">tejasā sarvabhūtānāṃ kṛtaṃ bhāskaravarcasaṃ</li>
    </ul>
    <ul id="R_6.097.008">
        <li id="R_6.097.008ab">sadhūmam iva kālāgniṃ dīptam āśīviṣaṃ yathā</li>
        <li id="R_6.097.008cd">rathanāgāśvavṛndānāṃ bhedanaṃ kṣiprakāriṇam</li>
    </ul>
    <ul id="R_6.097.009">
        <li id="R_6.097.009ab">dvārāṇāṃ parighāṇāṃ ca girīṇām api bhedanam</li>
        <li id="R_6.097.009cd">nānārudhirasiktāṅgaṃ medodigdhaṃ sudāruṇam</li>
    </ul>
    <ul id="R_6.097.010">
        <li id="R_6.097.010ab">vajrasāraṃ mahānādaṃ nānāsamitidāruṇam</li>
        <li id="R_6.097.010cd">sarvavitrāsanaṃ bhīmaṃ śvasantam iva pannagam</li>
    </ul>
    <ul id="R_6.097.011">
        <li id="R_6.097.011ab">kaṅkagṛdhrabalānāṃ ca gomāyugaṇarakṣasām</li>
        <li id="R_6.097.011cd">nityaṃ bhakṣapradaṃ yuddhe yamarūpaṃ bhayāvaham</li>
    </ul>
    <ul id="R_6.097.012">
        <li id="R_6.097.012ab">nandanaṃ vānarendrāṇāṃ rakṣasām avasādanam</li>
        <li id="R_6.097.012cd">vājitaṃ vividhair vājaiś cārucitrair garutmataḥ</li>
    </ul>
    <ul id="R_6.097.013">
        <li id="R_6.097.013ab">tam uttameṣuṃ lokānām ikṣvākubhayanāśanam</li>
        <li id="R_6.097.013cd">dviṣatāṃ kīrtiharaṇaṃ praharṣakaram ātmanaḥ</li>
    </ul>
    <ul id="R_6.097.014">
        <li id="R_6.097.014ab">abhimantrya tato rāmas taṃ maheṣuṃ mahābalaḥ</li>
        <li id="R_6.097.014cd">vedaproktena vidhinā saṃdadhe kārmuke balī</li>
    </ul>
    <ul id="R_6.097.015">
        <li id="R_6.097.015ab">sa rāvaṇāya saṃkruddho bhṛśam āyamya kārmukam</li>
        <li id="R_6.097.015cd">cikṣepa param āyattas taṃ śaraṃ marmaghātinam</li>
    </ul>
    <ul id="R_6.097.016">
        <li id="R_6.097.016ab">sa vajra iva durdharṣo vajrabāhuvisarjitaḥ</li>
        <li id="R_6.097.016cd">kṛtānta iva cāvāryo nyapatad rāvaṇorasi</li>
    </ul>
    <ul id="R_6.097.017">
        <li id="R_6.097.017ab">sa visṛṣṭo mahāvegaḥ śarīrāntakaraḥ śaraḥ</li>
        <li id="R_6.097.017cd">bibheda hṛdayaṃ tasya rāvaṇasya durātmanaḥ</li>
    </ul>
    <ul id="R_6.097.018">
        <li id="R_6.097.018ab">rudhirāktaḥ sa vegena jīvitāntakaraḥ śaraḥ</li>
        <li id="R_6.097.018cd">rāvaṇasya haran prāṇān viveśa dharaṇītalam</li>
    </ul>
    <ul id="R_6.097.019">
        <li id="R_6.097.019ab">sa śaro rāvaṇaṃ hatvā rudhirārdrakṛtacchaviḥ</li>
        <li id="R_6.097.019cd">kṛtakarmā nibhṛtavat svatūṇīṃ punar āviśat</li>
    </ul>
    <ul id="R_6.097.020">
        <li id="R_6.097.020ab">tasya hastād dhatasyāśu kārmukaṃ tat sasāyakam</li>
        <li id="R_6.097.020cd">nipapāta saha prāṇair bhraśyamānasya jīvitāt</li>
    </ul>
    <ul id="R_6.097.021">
        <li id="R_6.097.021ab">gatāsur bhīmavegas tu nairṛtendro mahādyutiḥ</li>
        <li id="R_6.097.021cd">papāta syandanād bhūmau vṛtro vajrahato yathā</li>
    </ul>
    <ul id="R_6.097.022">
        <li id="R_6.097.022ab">taṃ dṛṣṭvā patitaṃ bhūmau hataśeṣā niśācarāḥ</li>
        <li id="R_6.097.022cd">hatanāthā bhayatrastāḥ sarvataḥ saṃpradudruvuḥ</li>
    </ul>
    <ul id="R_6.097.023">
        <li id="R_6.097.023ab">nardantaś cābhipetus tān vānarā drumayodhinaḥ</li>
        <li id="R_6.097.023cd">daśagrīvavadhaṃ dṛṣṭvā vijayaṃ rāghavasya ca</li>
    </ul>
    <ul id="R_6.097.024">
        <li id="R_6.097.024ab">arditā vānarair hṛṣṭair laṅkām abhyapatan bhayāt</li>
        <li id="R_6.097.024cd">hatāśrayatvāt karuṇair bāṣpaprasravaṇair mukhaiḥ</li>
    </ul>
    <ul id="R_6.097.025">
        <li id="R_6.097.025ab">tato vineduḥ saṃhṛṣṭā vānarā jitakāśinaḥ</li>
        <li id="R_6.097.025cd">vadanto rāghavajayaṃ rāvaṇasya ca taṃ vadham</li>
    </ul>
    <ul id="R_6.097.026">
        <li id="R_6.097.026ab">athāntarikṣe vyanadat saumyas tridaśadundubhiḥ</li>
        <li id="R_6.097.026cd">divyagandhavahas tatra mārutaḥ susukho vavau</li>
    </ul>
    <ul id="R_6.097.027">
        <li id="R_6.097.027ab">nipapātāntarikṣāc ca puṣpavṛṣṭis tadā bhuvi</li>
        <li id="R_6.097.027cd">kirantī rāghavarathaṃ duravāpā manoharāḥ</li>
    </ul>
    <ul id="R_6.097.028">
        <li id="R_6.097.028ab">rāghavastavasaṃyuktā gagane ca viśuśruve</li>
        <li id="R_6.097.028cd">sādhu sādhv iti vāg agryā devatānāṃ mahātmanām</li>
    </ul>
    <ul id="R_6.097.029">
        <li id="R_6.097.029ab">āviveśa mahān harṣo devānāṃ cāraṇaiḥ saha</li>
        <li id="R_6.097.029cd">rāvaṇe nihate raudre sarvalokabhayaṃkare</li>
    </ul>
    <ul id="R_6.097.030">
        <li id="R_6.097.030ab">tataḥ sakāmaṃ sugrīvam aṅgadaṃ ca mahābalam</li>
        <li id="R_6.097.030cd">cakāra rāghavaḥ prīto hatvā rākṣasapuṃgavam</li>
    </ul>
    <ul id="R_6.097.031">
        <li id="R_6.097.031ab">
            <seg type="pāda" n="a">tataḥ prajagmuḥ praśamaṃ marudgaṇā</seg>
            <seg type="pāda" n="b">diśaḥ prasedur vimalaṃ nabho 'bhavat</seg>
        </li>
        <li id="R_6.097.031cd">
            <seg type="pāda" n="c">mahī cakampe na ca mārutā vavuḥ</seg>
            <seg type="pāda" n="d">sthiraprabhaś cāpy abhavad divākaraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.097.032">
        <li id="R_6.097.032ab">
            <seg type="pāda" n="a">tatas tu sugrīvavibhīṣaṇādayaḥ</seg>
            <seg type="pāda" n="b">suhṛdviśeṣāḥ sahalakṣmaṇās tadā</seg>
        </li>
        <li id="R_6.097.032cd">
            <seg type="pāda" n="c">sametya hṛṣṭā vijayena rāghavaṃ</seg>
            <seg type="pāda" n="d">raṇe 'bhirāmaṃ vidhinābhyapūjayan</seg>
        </li>
    </ul>
    <ul id="R_6.097.033">
        <li id="R_6.097.033ab">
            <seg type="pāda" n="a">sa tu nihataripuḥ sthirapratijñaḥ</seg>
            <seg type="pāda" n="b">svajanabalābhivṛto raṇe rarāja</seg>
        </li>
        <li id="R_6.097.033cd">
            <seg type="pāda" n="c">raghukulanṛpanandano mahaujās</seg>
            <seg type="pāda" n="d">tridaśagaṇair abhisaṃvṛto yathendraḥ</seg>
        </li>
    </ul>
    <ul id="R_6.098.001">
        <li id="R_6.098.001ab">rāvaṇaṃ nihataṃ śrutvā rāghaveṇa mahātmanā</li>
        <li id="R_6.098.001cd">antaḥpurād viniṣpetū rākṣasyaḥ śokakarśitāḥ</li>
    </ul>
    <ul id="R_6.098.002">
        <li id="R_6.098.002ab">vāryamāṇāḥ subahuśo vṛṣṭantyaḥ kṣitipāṃsuṣu</li>
        <li id="R_6.098.002cd">vimuktakeśyo duḥkhārtā gāvo vatsahatā yathā</li>
    </ul>
    <ul id="R_6.098.003">
        <li id="R_6.098.003ab">uttareṇa viniṣkramya dvāreṇa saha rākṣasaiḥ</li>
        <li id="R_6.098.003cd">praviśyāyodhanaṃ ghoraṃ vicinvantyo hataṃ patim</li>
    </ul>
    <ul id="R_6.098.004">
        <li id="R_6.098.004ab">āryaputreti vādinyo hā nātheti ca sarvaśaḥ</li>
        <li id="R_6.098.004cd">paripetuḥ kabandhāṅkāṃ mahīṃ śoṇitakardamām</li>
    </ul>
    <ul id="R_6.098.005">
        <li id="R_6.098.005ab">tā bāṣpaparipūrṇākṣyo bhartṛśokaparājitāḥ</li>
        <li id="R_6.098.005cd">kareṇva iva nardantyo vinedur hatayūthapāḥ</li>
    </ul>
    <ul id="R_6.098.006">
        <li id="R_6.098.006ab">dadṛśus tā mahākāyaṃ mahāvīryaṃ mahādyutim</li>
        <li id="R_6.098.006cd">rāvaṇaṃ nihataṃ bhūmau nīlāñjanacayopamam</li>
    </ul>
    <ul id="R_6.098.007">
        <li id="R_6.098.007ab">tāḥ patiṃ sahasā dṛṣṭvā śayānaṃ raṇapāṃsuṣu</li>
        <li id="R_6.098.007cd">nipetus tasya gātreṣu chinnā vanalatā iva</li>
    </ul>
    <ul id="R_6.098.008">
        <li id="R_6.098.008ab">bahumānāt pariṣvajya kā cid enaṃ ruroda ha</li>
        <li id="R_6.098.008cd">caraṇau kā cid āliṅgya kā cit kaṇṭhe 'valambya ca</li>
    </ul>
    <ul id="R_6.098.009">
        <li id="R_6.098.009ab">uddhṛtya ca bhujau kā cid bhūmau sma parivartate</li>
        <li id="R_6.098.009cd">hatasya vadanaṃ dṛṣṭvā kā cin moham upāgamat</li>
    </ul>
    <ul id="R_6.098.010">
        <li id="R_6.098.010ab">kā cid aṅke śiraḥ kṛtvā ruroda mukham īkṣatī</li>
        <li id="R_6.098.010cd">snāpayantī mukhaṃ bāṣpais tuṣārair iva paṅkajam</li>
    </ul>
    <ul id="R_6.098.011">
        <li id="R_6.098.011ab">evam ārtāḥ patiṃ dṛṣṭvā rāvaṇaṃ nihataṃ bhuvi</li>
        <li id="R_6.098.011cd">cukruśur bahudhā śokād bhūyas tāḥ paryadevayan</li>
    </ul>
    <ul id="R_6.098.012">
        <li id="R_6.098.012ab">yena vitrāsitaḥ śakro yena vitrāsito yamaḥ</li>
        <li id="R_6.098.012cd">yena vaiśravaṇo rājā puṣpakeṇa viyojitaḥ</li>
    </ul>
    <ul id="R_6.098.013">
        <li id="R_6.098.013ab">gandharvāṇām ṛṣīṇāṃ ca surāṇāṃ ca mahātmanām</li>
        <li id="R_6.098.013cd">bhayaṃ yena mahad dattaṃ so 'yaṃ śete raṇe hataḥ</li>
    </ul>
    <ul id="R_6.098.014">
        <li id="R_6.098.014ab">asurebhyaḥ surebhyo vā pannagebhyo 'pi vā tathā</li>
        <li id="R_6.098.014cd">na bhayaṃ yo vijānāti tasyedaṃ mānuṣād bhayam</li>
    </ul>
    <ul id="R_6.098.015">
        <li id="R_6.098.015ab">avadhyo devatānāṃ yas tathā dānavarakṣasām</li>
        <li id="R_6.098.015cd">hataḥ so 'yaṃ raṇe śete mānuṣeṇa padātinā</li>
    </ul>
    <ul id="R_6.098.016">
        <li id="R_6.098.016ab">yo na śakyaḥ surair hantuṃ na yakṣair nāsurais tathā</li>
        <li id="R_6.098.016cd">so 'yaṃ kaś cid ivāsattvo mṛtyuṃ martyena lambhitaḥ</li>
    </ul>
    <ul id="R_6.098.017">
        <li id="R_6.098.017ab">evaṃ vadantyo bahudhā rurudus tasya tāḥ striyaḥ</li>
        <li id="R_6.098.017cd">bhūya eva ca duḥkhārtā vilepuś ca punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.098.018">
        <li id="R_6.098.018ab">aśṛṇvatā tu suhṛdāṃ satataṃ hitavādinām</li>
        <li id="R_6.098.018cd">etāḥ samam idānīṃ te vayam ātmā ca pātitāḥ</li>
    </ul>
    <ul id="R_6.098.019">
        <li id="R_6.098.019ab">bruvāṇo 'pi hitaṃ vākyam iṣṭo bhrātā vibhīṣaṇaḥ</li>
        <li id="R_6.098.019cd">dhṛṣṭaṃ paruṣito mohāt tvayātmavadhakāṅkṣiṇā</li>
    </ul>
    <ul id="R_6.098.020">
        <li id="R_6.098.020ab">yadi niryātitā te syāt sītā rāmāya maithilī</li>
        <li id="R_6.098.020cd">na naḥ syād vyasanaṃ ghoram idaṃ mūlaharaṃ mahat</li>
    </ul>
    <ul id="R_6.098.021">
        <li id="R_6.098.021ab">vṛttakāmo bhaved bhrātā rāmo mitrakulaṃ bhavet</li>
        <li id="R_6.098.021cd">vayaṃ cāvidhavāḥ sarvāḥ sakāmā na ca śatravaḥ</li>
    </ul>
    <ul id="R_6.098.022">
        <li id="R_6.098.022ab">tvayā punar nṛśaṃsena sītāṃ saṃrundhatā balāt</li>
        <li id="R_6.098.022cd">rākṣasā vayam ātmā ca trayaṃ tulaṃ nipātitam</li>
    </ul>
    <ul id="R_6.098.023">
        <li id="R_6.098.023ab">na kāmakāraḥ kāmaṃ vā tava rākṣasapuṃgava</li>
        <li id="R_6.098.023cd">daivaṃ ceṣṭayate sarvaṃ hataṃ daivena hanyate</li>
    </ul>
    <ul id="R_6.098.024">
        <li id="R_6.098.024ab">vānarāṇāṃ vināśo 'yaṃ rākṣasānāṃ ca te raṇe</li>
        <li id="R_6.098.024cd">tava caiva mahābāho daivayogād upāgataḥ</li>
    </ul>
    <ul id="R_6.098.025">
        <li id="R_6.098.025ab">naivārthena na kāmena vikrameṇa na cājñayā</li>
        <li id="R_6.098.025cd">śakyā daivagatir loke nivartayitum udyatā</li>
    </ul>
    <ul id="R_6.098.026">
        <li id="R_6.098.026ab">vilepur evaṃ dīnās tā rākṣasādhipayoṣitaḥ</li>
        <li id="R_6.098.026cd">kurarya iva duḥkhārtā bāṣpaparyākulekṣaṇāḥ</li>
    </ul>
    <ul id="R_6.099.001">
        <li id="R_6.099.001ab">tāsāṃ vilapamānānāṃ tathā rākṣasayoṣitām</li>
        <li id="R_6.099.001cd">jyeṣṭhā patnī priyā dīnā bhartāraṃ samudaikṣata</li>
    </ul>
    <ul id="R_6.099.002">
        <li id="R_6.099.002ab">daśagrīvaṃ hataṃ dṛṣṭvā rāmeṇācintyakarmaṇā</li>
        <li id="R_6.099.002cd">patiṃ mandodarī tatra kṛpaṇā paryadevayat</li>
    </ul>
    <ul id="R_6.099.003">
        <li id="R_6.099.003ab">nanu nāma mahābāho tava vaiśravaṇānuja</li>
        <li id="R_6.099.003cd">kruddhasya pramukhe sthātuṃ trasyaty api puraṃdaraḥ</li>
    </ul>
    <ul id="R_6.099.004">
        <li id="R_6.099.004ab">ṛṣayaś ca mahīdevā gandharvāś ca yaśasvinaḥ</li>
        <li id="R_6.099.004cd">nanu nāma tavodvegāc cāraṇāś ca diśo gatāḥ</li>
    </ul>
    <ul id="R_6.099.005">
        <li id="R_6.099.005ab">sa tvaṃ mānuṣamātreṇa rāmeṇa yudhi nirjitaḥ</li>
        <li id="R_6.099.005cd">na vyapatrapase rājan kim idaṃ rākṣasarṣabha</li>
    </ul>
    <ul id="R_6.099.006">
        <li id="R_6.099.006ab">kathaṃ trailokyam ākramya śriyā vīryeṇa cānvitam</li>
        <li id="R_6.099.006cd">aviṣahyaṃ jaghāna tvāṃ mānuṣo vanagocaraḥ</li>
    </ul>
    <ul id="R_6.099.007">
        <li id="R_6.099.007ab">mānuṣāṇām aviṣaye carataḥ kāmarūpiṇaḥ</li>
        <li id="R_6.099.007cd">vināśas tava rāmeṇa saṃyuge nopapadyate</li>
    </ul>
    <ul id="R_6.099.008">
        <li id="R_6.099.008ab">na caitat karma rāmasya śraddadhāmi camūmukhe</li>
        <li id="R_6.099.008cd">sarvataḥ samupetasya tava tenābhimarśanam</li>
    </ul>
    <ul id="R_6.099.009">
        <li id="R_6.099.009ab">indriyāṇi purā jitvā jitaṃ tribhuvaṇaṃ tvayā</li>
        <li id="R_6.099.009cd">smaradbhir iva tad vairam indriyair eva nirjitaḥ</li>
    </ul>
    <ul id="R_6.099.010">
        <li id="R_6.099.010ab">atha vā rāmarūpeṇa vāsavaḥ svayam āgataḥ</li>
        <li id="R_6.099.010cd">māyāṃ tava vināśāya vidhāyāpratitarkitām</li>
    </ul>
    <ul id="R_6.099.011">
        <li id="R_6.099.011ab">yadaiva hi janasthāne rākṣasair bahubhir vṛtaḥ</li>
        <li id="R_6.099.011cd">kharas tava hato bhrātā tadaivāsau na mānuṣaḥ</li>
    </ul>
    <ul id="R_6.099.012">
        <li id="R_6.099.012ab">yadaiva nagarīṃ laṅkāṃ duṣpraveṣāṃ surair api</li>
        <li id="R_6.099.012cd">praviṣṭo hanumān vīryāt tadaiva vyathitā vayam</li>
    </ul>
    <ul id="R_6.099.013">
        <li id="R_6.099.013ab">kriyatām avirodhaś ca rāghaveṇeti yan mayā</li>
        <li id="R_6.099.013cd">ucyamāno na gṛhṇāsi tasyeyaṃ vyuṣṭir āgatā</li>
    </ul>
    <ul id="R_6.099.014">
        <li id="R_6.099.014ab">akasmāc cābhikāmo 'si sītāṃ rākṣasapuṃgava</li>
        <li id="R_6.099.014cd">aiśvaryasya vināśāya dehasya svajanasya ca</li>
    </ul>
    <ul id="R_6.099.015">
        <li id="R_6.099.015ab">arundhatyā viśiṣṭāṃ tāṃ rohiṇyāś cāpi durmate</li>
        <li id="R_6.099.015cd">sītāṃ dharṣayatā mānyāṃ tvayā hy asadṛśaṃ kṛtam</li>
    </ul>
    <ul id="R_6.099.016">
        <li id="R_6.099.016ab">na kulena na rūpeṇa na dākṣiṇyena maithilī</li>
        <li id="R_6.099.016cd">mayādhikā vā tulyā vā tvaṃ tu mohān na budhyase</li>
    </ul>
    <ul id="R_6.099.017">
        <li id="R_6.099.017ab">sarvathā sarvabhūtānāṃ nāsti mṛtyur alakṣaṇaḥ</li>
        <li id="R_6.099.017cd">tava tāvad ayaṃ mṛtyur maithilīkṛtalakṣaṇaḥ</li>
    </ul>
    <ul id="R_6.099.018">
        <li id="R_6.099.018ab">maithilī saha rāmeṇa viśokā vihariṣyati</li>
        <li id="R_6.099.018cd">alpapuṇyā tv ahaṃ ghore patitā śokasāgare</li>
    </ul>
    <ul id="R_6.099.019">
        <li id="R_6.099.019ab">kailāse mandare merau tathā caitrarathe vane</li>
        <li id="R_6.099.019cd">devodyāneṣu sarveṣu vihṛtya sahitā tvayā</li>
    </ul>
    <ul id="R_6.099.020">
        <li id="R_6.099.020ab">vimānenānurūpeṇa yā yāmy atulayā śriyā</li>
        <li id="R_6.099.020cd">paśyantī vividhān deśāṃs tāṃs tāṃś citrasragambarā</li>
        <li id="R_6.099.020ef">bhraṃśitā kāmabhogebhyaḥ sāsmi vīravadhāt tava</li>
    </ul>
    <ul id="R_6.099.021">
        <li id="R_6.099.021ab">satyavāk sa mahābhāgo devaro me yad abravīt</li>
        <li id="R_6.099.021cd">ayaṃ rākṣasamukhyānāṃ vināśaḥ paryupasthitaḥ</li>
    </ul>
    <ul id="R_6.099.022">
        <li id="R_6.099.022ab">kāmakrodhasamutthena vyasanena prasaṅginā</li>
        <li id="R_6.099.022cd">tvayā kṛtam idaṃ sarvam anāthaṃ rakṣasāṃ kulam</li>
    </ul>
    <ul id="R_6.099.023">
        <li id="R_6.099.023ab">na hi tvaṃ śocitavyo me prakhyātabalapauruṣaḥ</li>
        <li id="R_6.099.023cd">strīsvabhāvāt tu me buddhiḥ kāruṇye parivartate</li>
    </ul>
    <ul id="R_6.099.024">
        <li id="R_6.099.024ab">sukṛtaṃ duṣkṛtaṃ ca tvaṃ gṛhītvā svāṃ gatiṃ gataḥ</li>
        <li id="R_6.099.024cd">ātmānam anuśocāmi tvadviyogena duḥkhitām</li>
    </ul>
    <ul id="R_6.099.025">
        <li id="R_6.099.025ab">nīlajīmūtasaṃkāśaḥ pītāmbaraśubhāṅgadaḥ</li>
        <li id="R_6.099.025cd">sarvagātrāṇi vikṣipya kiṃ śeṣe rudhirāplutaḥ</li>
        <li id="R_6.099.025ef">prasupta iva śokārtāṃ kiṃ māṃ na pratibhāṣase</li>
    </ul>
    <ul id="R_6.099.026">
        <li id="R_6.099.026ab">mahāvīryasya dakṣasya saṃyugeṣv apalāyinaḥ</li>
        <li id="R_6.099.026cd">yātudhānasya dauhitrīṃ kiṃ tvaṃ māṃ nābhyudīkṣase</li>
    </ul>
    <ul id="R_6.099.027">
        <li id="R_6.099.027ab">yena sūdayase śatrūn samare sūryavarcasā</li>
        <li id="R_6.099.027cd">vajro vajradharasyeva so 'yaṃ te satatārcitaḥ</li>
    </ul>
    <ul id="R_6.099.028">
        <li id="R_6.099.028ab">raṇe śatrupraharaṇo hemajālapariṣkṛtaḥ</li>
        <li id="R_6.099.028cd">parigho vyavakīrṇas te bāṇaiś chinnaḥ sahasradhā</li>
    </ul>
    <ul id="R_6.099.029">
        <li id="R_6.099.029ab">dhig astu hṛdayaṃ yasyā mamedaṃ na sahasradhā</li>
        <li id="R_6.099.029cd">tvayi pañcatvam āpanne phalate śokapīḍitam</li>
    </ul>
    <ul id="R_6.099.030">
        <li id="R_6.099.030ab">etasminn antare rāmo vibhīṣaṇam uvāca ha</li>
        <li id="R_6.099.030cd">saṃskāraḥ kriyatāṃ bhrātuḥ striyaś caitā nivartaya</li>
    </ul>
    <ul id="R_6.099.031">
        <li id="R_6.099.031ab">taṃ praśritas tato rāmaṃ śrutavākyo vibhīṣaṇaḥ</li>
        <li id="R_6.099.031cd">vimṛśya buddhyā dharmajño dharmārthasahitaṃ vacaḥ</li>
        <li id="R_6.099.031ef">rāmasyaivānuvṛttyartham uttaraṃ pratyabhāṣata</li>
    </ul>
    <ul id="R_6.099.032">
        <li id="R_6.099.032ab">tyaktadharmavrataṃ krūraṃ nṛśaṃsam anṛtaṃ tathā</li>
        <li id="R_6.099.032cd">nāham arho 'smi saṃskartuṃ paradārābhimarśakam</li>
    </ul>
    <ul id="R_6.099.033">
        <li id="R_6.099.033ab">bhrātṛrūpo hi me śatrur eṣa sarvāhite rataḥ</li>
        <li id="R_6.099.033cd">rāvaṇo nārhate pūjāṃ pūjyo 'pi gurugauravāt</li>
    </ul>
    <ul id="R_6.099.034">
        <li id="R_6.099.034ab">nṛśaṃsa iti māṃ rāma vakṣyanti manujā bhuvi</li>
        <li id="R_6.099.034cd">śrutvā tasya guṇān sarve vakṣyanti sukṛtaṃ punaḥ</li>
    </ul>
    <ul id="R_6.099.035">
        <li id="R_6.099.035ab">tac chrutvā paramaprīto rāmo dharmabhṛtāṃ varaḥ</li>
        <li id="R_6.099.035cd">vibhīṣaṇam uvācedaṃ vākyajño vākyakovidam</li>
    </ul>
    <ul id="R_6.099.036">
        <li id="R_6.099.036ab">tavāpi me priyaṃ kāryaṃ tvatprabhavāc ca me jitam</li>
        <li id="R_6.099.036cd">avaśyaṃ tu kṣamaṃ vācyo mayā tvaṃ rākṣaseśvara</li>
    </ul>
    <ul id="R_6.099.037">
        <li id="R_6.099.037ab">adharmānṛtasaṃyuktaḥ kāmam eṣa niśācaraḥ</li>
        <li id="R_6.099.037cd">tejasvī balavāñ śūraḥ saṃgrāmeṣu ca nityaśaḥ</li>
    </ul>
    <ul id="R_6.099.038">
        <li id="R_6.099.038ab">śatakratumukhair devaiḥ śrūyate na parājitaḥ</li>
        <li id="R_6.099.038cd">mahātmā balasaṃpanno rāvaṇo lokarāvaṇaḥ</li>
    </ul>
    <ul id="R_6.099.039">
        <li id="R_6.099.039ab">maraṇāntāni vairāṇi nirvṛttaṃ naḥ prayojanam</li>
        <li id="R_6.099.039cd">kriyatām asya saṃskāro mamāpy eṣa yathā tava</li>
    </ul>
    <ul id="R_6.099.040">
        <li id="R_6.099.040ab">tvatsakāśān mahābāho saṃskāraṃ vidhipūrvakam</li>
        <li id="R_6.099.040cd">kṣipram arhati dharmajña tvaṃ yaśobhāg bhaviṣyasi</li>
    </ul>
    <ul id="R_6.099.041">
        <li id="R_6.099.041ab">rāghavasya vacaḥ śrutvā tvaramāṇo vibhīṣaṇaḥ</li>
        <li id="R_6.099.041cd">saṃskāreṇānurūpeṇa yojayām āsa rāvaṇam</li>
    </ul>
    <ul id="R_6.099.042">
        <li id="R_6.099.042ab">sa dadau pāvakaṃ tasya vidhiyuktaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.099.042cd">tāḥ striyo 'nunayām āsa sāntvam uktvā punaḥ punaḥ</li>
    </ul>
    <ul id="R_6.099.043">
        <li id="R_6.099.043ab">praviṣṭāsu ca sarvāsu rākṣasīṣu vibhīṣaṇaḥ</li>
        <li id="R_6.099.043cd">rāmapārśvam upāgamya tadātiṣṭhad vinītavat</li>
    </ul>
    <ul id="R_6.099.044">
        <li id="R_6.099.044ab">rāmo 'pi saha sainyena sasugrīvaḥ salakṣmaṇaḥ</li>
        <li id="R_6.099.044cd">harṣaṃ lebhe ripuṃ hatvā yathā vṛtraṃ śatakratuḥ</li>
    </ul>
    <ul id="R_6.100.001">
        <li id="R_6.100.001ab">te rāvaṇavadhaṃ dṛṣṭvā devagandharvadānavāḥ</li>
        <li id="R_6.100.001cd">jagmus tais tair vimānaiḥ svaiḥ kathayantaḥ śubhāḥ kathāḥ</li>
    </ul>
    <ul id="R_6.100.002">
        <li id="R_6.100.002ab">rāvaṇasya vadhaṃ ghoraṃ rāghavasya parākramam</li>
        <li id="R_6.100.002cd">suyuddhaṃ vānarāṇāṃ ca sugrīvasya ca mantritam</li>
    </ul>
    <ul id="R_6.100.003">
        <li id="R_6.100.003ab">anurāgaṃ ca vīryaṃ ca saumitrer lakṣmaṇasya ca</li>
        <li id="R_6.100.003cd">kathayanto mahābhāgā jagmur hṛṣṭā yathāgatam</li>
    </ul>
    <ul id="R_6.100.004">
        <li id="R_6.100.004ab">rāghavas tu rathaṃ divyam indradattaṃ śikhiprabham</li>
        <li id="R_6.100.004cd">anujñāya mahābhāgo mātaliṃ pratyapūjayat</li>
    </ul>
    <ul id="R_6.100.005">
        <li id="R_6.100.005ab">rāghaveṇābhyanujñāto mātaliḥ śakrasārathiḥ</li>
        <li id="R_6.100.005cd">divyaṃ taṃ ratham āsthāya divam evāruroha saḥ</li>
    </ul>
    <ul id="R_6.100.006">
        <li id="R_6.100.006ab">tasmiṃs tu divam ārūḍhe surasārathisattame</li>
        <li id="R_6.100.006cd">rāghavaḥ paramaprītaḥ sugrīvaṃ pariṣasvaje</li>
    </ul>
    <ul id="R_6.100.007">
        <li id="R_6.100.007ab">pariṣvajya ca sugrīvaṃ lakṣmaṇenābhivāditaḥ</li>
        <li id="R_6.100.007cd">pūjyamāno hariśreṣṭhair ājagāma balālayam</li>
    </ul>
    <ul id="R_6.100.008">
        <li id="R_6.100.008ab">abravīc ca tadā rāmaḥ samīpaparivartinam</li>
        <li id="R_6.100.008cd">saumitriṃ sattvasaṃpannaṃ lakṣmaṇaṃ dīptatejasaṃ</li>
    </ul>
    <ul id="R_6.100.009">
        <li id="R_6.100.009ab">vibhīṣaṇam imaṃ saumya laṅkāyām abhiṣecaya</li>
        <li id="R_6.100.009cd">anuraktaṃ ca bhaktaṃ ca mama caivopakāriṇam</li>
    </ul>
    <ul id="R_6.100.010">
        <li id="R_6.100.010ab">eṣa me paramaḥ kāmo yad imaṃ rāvaṇānujam</li>
        <li id="R_6.100.010cd">laṅkāyāṃ saumya paśyeyam abhiṣiktaṃ vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.100.011">
        <li id="R_6.100.011ab">evam uktas tu saumitrī rāghaveṇa mahātmanā</li>
        <li id="R_6.100.011cd">tathety uktvā tu saṃhṛṣṭaḥ sauvarṇaṃ ghaṭam ādade</li>
    </ul>
    <ul id="R_6.100.012">
        <li id="R_6.100.012ab">ghaṭena tena saumitrir abhyaṣiñcad vibhīṣaṇam</li>
        <li id="R_6.100.012cd">laṅkāyāṃ rakṣasāṃ madhye rājānaṃ rāmaśāsanāt</li>
    </ul>
    <ul id="R_6.100.013">
        <li id="R_6.100.013ab">abhyaṣiñcat sa dharmātmā śuddhātmānaṃ vibhīṣaṇam</li>
        <li id="R_6.100.013cd">tasyāmātyā jahṛṣire bhaktā ye cāsya rākṣasāḥ</li>
    </ul>
    <ul id="R_6.100.014">
        <li id="R_6.100.014ab">dṛṣṭvābhiṣiktaṃ laṅkāyāṃ rākṣasendraṃ vibhīṣaṇam</li>
        <li id="R_6.100.014cd">rāghavaḥ paramāṃ prītiṃ jagāma sahalakṣmaṇaḥ</li>
    </ul>
    <ul id="R_6.100.015">
        <li id="R_6.100.015ab">sa tad rājyaṃ mahat prāpya rāmadattaṃ vibhīṣaṇaḥ</li>
        <li id="R_6.100.015cd">prakṛtīḥ sāntvayitvā ca tato rāmam upāgamat</li>
    </ul>
    <ul id="R_6.100.016">
        <li id="R_6.100.016ab">akṣatān modakāṃl lājān divyāḥ sumanasas tathā</li>
        <li id="R_6.100.016cd">ājahrur atha saṃhṛṣṭāḥ paurās tasmai niśācarāḥ</li>
    </ul>
    <ul id="R_6.100.017">
        <li id="R_6.100.017ab">sa tān gṛhītvā durdharṣo rāghavāya nyavedayat</li>
        <li id="R_6.100.017cd">maṅgalyaṃ maṅgalaṃ sarvaṃ lakṣmaṇāya ca vīryavān</li>
    </ul>
    <ul id="R_6.100.018">
        <li id="R_6.100.018ab">kṛtakāryaṃ samṛddhārthaṃ dṛṣṭvā rāmo vibhīṣaṇam</li>
        <li id="R_6.100.018cd">pratijagrāha tat sarvaṃ tasyaiva priyakāmyayā</li>
    </ul>
    <ul id="R_6.100.019">
        <li id="R_6.100.019ab">tataḥ śailopamaṃ vīraṃ prāñjaliṃ pārśvataḥ sthitam</li>
        <li id="R_6.100.019cd">abravīd rāghavo vākyaṃ hanūmantaṃ plavaṃgamam</li>
    </ul>
    <ul id="R_6.100.020">
        <li id="R_6.100.020ab">anumānya mahārājam imaṃ saumya vibhīṣaṇam</li>
        <li id="R_6.100.020cd">praviśya rāvaṇagṛhaṃ vinayenopasṛtya ca</li>
    </ul>
    <ul id="R_6.100.021">
        <li id="R_6.100.021ab">vaidehyā māṃ kuśalinaṃ sasugrīvaṃ salakṣmaṇam</li>
        <li id="R_6.100.021cd">ācakṣva jayatāṃ śreṣṭha rāvaṇaṃ ca mayā hatam</li>
    </ul>
    <ul id="R_6.100.022">
        <li id="R_6.100.022ab">priyam etad udāhṛtya maithilyās tvaṃ harīśvara</li>
        <li id="R_6.100.022cd">pratigṛhya ca saṃdeśam upāvartitum arhasi</li>
    </ul>
    <ul id="R_6.101.001">
        <li id="R_6.101.001ab">iti pratisamādiṣṭo hanūmān mārutātmajaḥ</li>
        <li id="R_6.101.001cd">praviveśa purīṃ laṅkāṃ pūjyamāno niśācaraiḥ</li>
    </ul>
    <ul id="R_6.101.002">
        <li id="R_6.101.002ab">praviśya tu mahātejā rāvaṇasya niveśanam</li>
        <li id="R_6.101.002cd">dadarśa śaśinā hīnāṃ sātaṅkām iva rohiṇīm</li>
    </ul>
    <ul id="R_6.101.003">
        <li id="R_6.101.003ab">nibhṛtaḥ praṇataḥ prahvaḥ so 'bhigamyābhivādya ca</li>
        <li id="R_6.101.003cd">rāmasya vacanaṃ sarvam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_6.101.004">
        <li id="R_6.101.004ab">vaidehi kuśalī rāmaḥ sasugrīvaḥ salakṣmaṇaḥ</li>
        <li id="R_6.101.004cd">kuśalaṃ cāha siddhārtho hataśatrur ariṃdamaḥ</li>
    </ul>
    <ul id="R_6.101.005">
        <li id="R_6.101.005ab">vibhīṣaṇasahāyena rāmeṇa haribhiḥ saha</li>
        <li id="R_6.101.005cd">nihato rāvaṇo devi lakṣmaṇasya nayena ca</li>
    </ul>
    <ul id="R_6.101.006">
        <li id="R_6.101.006ab">pṛṣṭvā ca kuśalaṃ rāmo vīras tvāṃ raghunandanaḥ</li>
        <li id="R_6.101.006cd">abravīt paramaprītaḥ kṛtārthenāntarātmanā</li>
    </ul>
    <ul id="R_6.101.007">
        <li id="R_6.101.007ab">priyam ākhyāmi te devi tvāṃ tu bhūyaḥ sabhājaye</li>
        <li id="R_6.101.007cd">diṣṭyā jīvasi dharmajñe jayena mama saṃyuge</li>
    </ul>
    <ul id="R_6.101.008">
        <li id="R_6.101.008ab">labdho no vijayaḥ sīte svasthā bhava gatavyathā</li>
        <li id="R_6.101.008cd">rāvaṇaḥ sa hataḥ śatrur laṅkā ceyaṃ vaśe sthitā</li>
    </ul>
    <ul id="R_6.101.009">
        <li id="R_6.101.009ab">mayā hy alabdhanidreṇa dhṛtena tava nirjaye</li>
        <li id="R_6.101.009cd">pratijñaiṣā vinistīrṇā baddhvā setuṃ mahodadhau</li>
    </ul>
    <ul id="R_6.101.010">
        <li id="R_6.101.010ab">saṃbhramaś ca na kartavyo vartantyā rāvaṇālaye</li>
        <li id="R_6.101.010cd">vibhīṣaṇavidheyaṃ hi laṅkaiśvaryam idaṃ kṛtam</li>
    </ul>
    <ul id="R_6.101.011">
        <li id="R_6.101.011ab">tad āśvasihi viśvastā svagṛhe parivartase</li>
        <li id="R_6.101.011cd">ayaṃ cābhyeti saṃhṛṣṭas tvaddarśanasamutsukaḥ</li>
    </ul>
    <ul id="R_6.101.012">
        <li id="R_6.101.012ab">evam uktā samutpatya sītā śaśinibhānanā</li>
        <li id="R_6.101.012cd">praharṣeṇāvaruddhā sā vyājahāra na kiṃ cana</li>
    </ul>
    <ul id="R_6.101.013">
        <li id="R_6.101.013ab">abravīc ca hariśreṣṭhaḥ sītām apratijalpatīm</li>
        <li id="R_6.101.013cd">kiṃ tvaṃ cintayase devi kiṃ ca māṃ nābhibhāṣase</li>
    </ul>
    <ul id="R_6.101.014">
        <li id="R_6.101.014ab">evam uktā hanumatā sītā dharme vyavasthitā</li>
        <li id="R_6.101.014cd">abravīt paramaprītā harṣagadgadayā girā</li>
    </ul>
    <ul id="R_6.101.015">
        <li id="R_6.101.015ab">priyam etad upaśrutya bhartur vijayasaṃśritam</li>
        <li id="R_6.101.015cd">praharṣavaśam āpannā nirvākyāsmi kṣaṇāntaram</li>
    </ul>
    <ul id="R_6.101.016">
        <li id="R_6.101.016ab">na hi paśyāmi sadṛśaṃ cintayantī plavaṃgama</li>
        <li id="R_6.101.016cd">matpriyākhyānakasyeha tava pratyabhinandanam</li>
    </ul>
    <ul id="R_6.101.017">
        <li id="R_6.101.017ab">na ca paśyāmi tat saumya pṛthivyām api vānara</li>
        <li id="R_6.101.017cd">sadṛśaṃ matpriyākhyāne tava dātuṃ bhavet samam</li>
    </ul>
    <ul id="R_6.101.018">
        <li id="R_6.101.018ab">hiraṇyaṃ vā suvarṇaṃ vā ratnāni vividhāni ca</li>
        <li id="R_6.101.018cd">rājyaṃ vā triṣu lokeṣu naitad arhati bhāṣitum</li>
    </ul>
    <ul id="R_6.101.019">
        <li id="R_6.101.019ab">evam uktas tu vaidehyā pratyuvāca plavaṃgamaḥ</li>
        <li id="R_6.101.019cd">pragṛhītāñjalir vākyaṃ sītāyāḥ pramukhe sthitaḥ</li>
    </ul>
    <ul id="R_6.101.020">
        <li id="R_6.101.020ab">bhartuḥ priyahite yukte bhartur vijayakāṅkṣiṇi</li>
        <li id="R_6.101.020cd">snigdham evaṃvidhaṃ vākyaṃ tvam evārhasi bhāṣitum</li>
    </ul>
    <ul id="R_6.101.021">
        <li id="R_6.101.021ab">tavaitad vacanaṃ saumye sāravat snigdham eva ca</li>
        <li id="R_6.101.021cd">ratnaughād vividhāc cāpi devarājyād viśiṣyate</li>
    </ul>
    <ul id="R_6.101.022">
        <li id="R_6.101.022ab">arthataś ca mayā prāptā devarājyādayo guṇāḥ</li>
        <li id="R_6.101.022cd">hataśatruṃ vijayinaṃ rāmaṃ paśyāmi yat sthitam</li>
    </ul>
    <ul id="R_6.101.023">
        <li id="R_6.101.023ab">imās tu khalu rākṣasyo yadi tvam anumanyase</li>
        <li id="R_6.101.023cd">hantum icchāmy ahaṃ sarvā yābhis tvaṃ tarjitā purā</li>
    </ul>
    <ul id="R_6.101.024">
        <li id="R_6.101.024ab">kliśyantīṃ patidevāṃ tvām aśokavanikāṃ gatām</li>
        <li id="R_6.101.024cd">ghorarūpasamācārāḥ krūrāḥ krūratarekṣaṇāḥ</li>
    </ul>
    <ul id="R_6.101.025">
        <li id="R_6.101.025ab">rākṣasyo dāruṇakathā varam etaṃ prayaccha me</li>
        <li id="R_6.101.025cd">icchāmi vividhair ghātair hantum etāḥ sudāruṇāḥ</li>
    </ul>
    <ul id="R_6.101.026">
        <li id="R_6.101.026ab">muṣṭibhiḥ pāṇibhiś caiva caraṇaiś caiva śobhane</li>
        <li id="R_6.101.026cd">ghorair jānuprahāraiś ca daśanānāṃ ca pātanaiḥ</li>
    </ul>
    <ul id="R_6.101.027">
        <li id="R_6.101.027ab">bhakṣaṇaiḥ karṇanāsānāṃ keśānāṃ luñcanais tathā</li>
        <li id="R_6.101.027cd">bhṛśaṃ śuṣkamukhībhiś ca dāruṇair laṅghanair hataiḥ</li>
    </ul>
    <ul id="R_6.101.028">
        <li id="R_6.101.028ab">evaṃprakārair bahubhir viprakārair yaśasvini</li>
        <li id="R_6.101.028cd">hantum icchāmy ahaṃ devi tavemāḥ kṛtakilbiṣāḥ</li>
    </ul>
    <ul id="R_6.101.029">
        <li id="R_6.101.029ab">evam uktā mahumatā vaidehī janakātmajā</li>
        <li id="R_6.101.029cd">uvāca dharmasahitaṃ hanūmantaṃ yaśasvinī</li>
    </ul>
    <ul id="R_6.101.030">
        <li id="R_6.101.030ab">rājasaṃśrayavaśyānāṃ kurvatīnāṃ parājñayā</li>
        <li id="R_6.101.030cd">vidheyānāṃ ca dāsīnāṃ kaḥ kupyed vānarottama</li>
    </ul>
    <ul id="R_6.101.031">
        <li id="R_6.101.031ab">bhāgyavaiṣamyayogena purā duścaritena ca</li>
        <li id="R_6.101.031cd">mayaitat prāpyate sarvaṃ svakṛtaṃ hy upabhujyate</li>
    </ul>
    <ul id="R_6.101.032">
        <li id="R_6.101.032ab">prāptavyaṃ tu daśāyogān mayaitad iti niścitam</li>
        <li id="R_6.101.032cd">dāsīnāṃ rāvaṇasyāhaṃ marṣayāmīha durbalā</li>
    </ul>
    <ul id="R_6.101.033">
        <li id="R_6.101.033ab">ājñaptā rāvaṇenaitā rākṣasyo mām atarjayan</li>
        <li id="R_6.101.033cd">hate tasmin na kuryur hi tarjanaṃ vānarottama</li>
    </ul>
    <ul id="R_6.101.034">
        <li id="R_6.101.034ab">ayaṃ vyāghrasamīpe tu purāṇo dharmasaṃhitaḥ</li>
        <li id="R_6.101.034cd">ṛkṣeṇa gītaḥ śloko me taṃ nibodha plavaṃgama</li>
    </ul>
    <ul id="R_6.101.035">
        <li id="R_6.101.035ab">na paraḥ pāpam ādatte pareṣāṃ pāpakarmaṇām</li>
        <li id="R_6.101.035cd">samayo rakṣitavyas tu santaś cāritrabhūṣaṇāḥ</li>
    </ul>
    <ul id="R_6.101.036">
        <li id="R_6.101.036ab">pāpānāṃ vā śubhānāṃ vā vadhārhāṇāṃ plavaṃgama</li>
        <li id="R_6.101.036cd">kāryaṃ kāruṇyam āryeṇa na kaś cin nāparādhyati</li>
    </ul>
    <ul id="R_6.101.037">
        <li id="R_6.101.037ab">lokahiṃsāvihārāṇāṃ rakṣasāṃ kāmarūpiṇam</li>
        <li id="R_6.101.037cd">kurvatām api pāpāni naiva kāryam aśobhanam</li>
    </ul>
    <ul id="R_6.101.038">
        <li id="R_6.101.038ab">evam uktas tu hanumān sītayā vākyakovidaḥ</li>
        <li id="R_6.101.038cd">pratyuvāca tataḥ sītāṃ rāmapatnīṃ yaśasvinīm</li>
    </ul>
    <ul id="R_6.101.039">
        <li id="R_6.101.039ab">yuktā rāmasya bhavatī dharmapatnī yaśasvinī</li>
        <li id="R_6.101.039cd">pratisaṃdiśa māṃ devi gamiṣye yatra rāghavaḥ</li>
    </ul>
    <ul id="R_6.101.040">
        <li id="R_6.101.040ab">evam uktā hanumatā vaidehī janakātmajā</li>
        <li id="R_6.101.040cd">abravīd draṣṭum icchāmi bhartāraṃ vānarottama</li>
    </ul>
    <ul id="R_6.101.041">
        <li id="R_6.101.041ab">tasyās tad vacanaṃ śrutvā hanumān pavanātmajaḥ</li>
        <li id="R_6.101.041cd">harṣayan maithilīṃ vākyam uvācedaṃ mahādyutiḥ</li>
    </ul>
    <ul id="R_6.101.042">
        <li id="R_6.101.042ab">pūrṇacandrānanaṃ rāmaṃ drakṣyasy ārye salakṣmaṇam</li>
        <li id="R_6.101.042cd">sthiramitraṃ hatāmitraṃ śacīva tridaśeśvaram</li>
    </ul>
    <ul id="R_6.101.043">
        <li id="R_6.101.043ab">tām evam uktvā rājantīṃ sītāṃ sākṣād iva śriyam</li>
        <li id="R_6.101.043cd">ājagāma mahāvego hanūmān yatra rāghavaḥ</li>
    </ul>
    <ul id="R_6.102.001">
        <li id="R_6.102.001ab">sa uvāca mahāprajñam abhigamya plavaṃgamaḥ</li>
        <li id="R_6.102.001cd">rāmaṃ vacanam arthajño varaṃ sarvadhanuṣmatām</li>
    </ul>
    <ul id="R_6.102.002">
        <li id="R_6.102.002ab">yannimitto 'yam ārambhaḥ karmaṇāṃ ca phalodayaḥ</li>
        <li id="R_6.102.002cd">tāṃ devīṃ śokasaṃtaptāṃ maithilīṃ draṣṭum arhasi</li>
    </ul>
    <ul id="R_6.102.003">
        <li id="R_6.102.003ab">sā hi śokasamāviṣṭā bāṣpaparyākulekṣaṇā</li>
        <li id="R_6.102.003cd">maithilī vijayaṃ śrutvā tava harṣam upāgamat</li>
    </ul>
    <ul id="R_6.102.004">
        <li id="R_6.102.004ab">pūrvakāt pratyayāc cāham ukto viśvastayā tayā</li>
        <li id="R_6.102.004cd">bhartāraṃ draṣṭum icchāmi kṛtārthaṃ sahalakṣmaṇam</li>
    </ul>
    <ul id="R_6.102.005">
        <li id="R_6.102.005ab">evam ukto hanumatā rāmo dharmabhṛtāṃ varaḥ</li>
        <li id="R_6.102.005cd">agacchat sahasā dhyānam āsīd bāṣpapariplutaḥ</li>
    </ul>
    <ul id="R_6.102.006">
        <li id="R_6.102.006ab">dīrgham uṣṇaṃ ca niśvasya medinīm avalokayan</li>
        <li id="R_6.102.006cd">uvāca meghasaṃkāśaṃ vibhīṣaṇam upasthitam</li>
    </ul>
    <ul id="R_6.102.007">
        <li id="R_6.102.007ab">divyāṅgarāgāṃ vaidehīṃ divyābharaṇabhūṣitām</li>
        <li id="R_6.102.007cd">iha sītāṃ śiraḥsnātām upasthāpaya māciram</li>
    </ul>
    <ul id="R_6.102.008">
        <li id="R_6.102.008ab">evam uktas tu rāmeṇa tvaramāṇo vibhīṣaṇaḥ</li>
        <li id="R_6.102.008cd">praviśyāntaḥpuraṃ sītāṃ strībhiḥ svābhir acodayat</li>
    </ul>
    <ul id="R_6.102.009">
        <li id="R_6.102.009ab">divyāṅgarāgā vaidehī divyābharaṇabhūṣitā</li>
        <li id="R_6.102.009cd">yānam āroha bhadraṃ te bhartā tvāṃ draṣṭum icchati</li>
    </ul>
    <ul id="R_6.102.010">
        <li id="R_6.102.010ab">evam uktā tu vaidehī pratyuvāca vibhīṣaṇam</li>
        <li id="R_6.102.010cd">asnātā draṣṭum icchāmi bhartāraṃ rākṣasādhipa</li>
    </ul>
    <ul id="R_6.102.011">
        <li id="R_6.102.011ab">tasyās tad vacanaṃ śrutvā pratyuvāca vibhīṣaṇaḥ</li>
        <li id="R_6.102.011cd">yathāha rāmo bhartā te tat tathā kartum arhasi</li>
    </ul>
    <ul id="R_6.102.012">
        <li id="R_6.102.012ab">tasya tad vacanaṃ śrutvā maithilī bhartṛdevatā</li>
        <li id="R_6.102.012cd">bhartṛbhaktivratā sādhvī tatheti pratyabhāṣata</li>
    </ul>
    <ul id="R_6.102.013">
        <li id="R_6.102.013ab">tataḥ sītāṃ śiraḥsnātāṃ yuvatībhir alaṃkṛtām</li>
        <li id="R_6.102.013cd">mahārhābharaṇopetāṃ mahārhāmbaradhāriṇīm</li>
    </ul>
    <ul id="R_6.102.014">
        <li id="R_6.102.014ab">āropya śibikāṃ dīptāṃ parārdhyāmbarasaṃvṛtām</li>
        <li id="R_6.102.014cd">rakṣobhir bahubhir guptām ājahāra vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.102.015">
        <li id="R_6.102.015ab">so 'bhigamya mahātmānaṃ jñātvābhidhyānam āsthitam</li>
        <li id="R_6.102.015cd">praṇataś ca prahṛṣṭaś ca prāptāṃ sītāṃ nyavedayat</li>
    </ul>
    <ul id="R_6.102.016">
        <li id="R_6.102.016ab">tām āgatām upaśrutya rakṣogṛhaciroṣitām</li>
        <li id="R_6.102.016cd">harṣo dainyaṃ ca roṣaś ca trayaṃ rāghavam āviśat</li>
    </ul>
    <ul id="R_6.102.017">
        <li id="R_6.102.017ab">tataḥ pārśvagataṃ dṛṣṭvā savimarśaṃ vicārayan</li>
        <li id="R_6.102.017cd">vibhīṣaṇam idaṃ vākyam ahṛṣṭo rāghavo 'bravīt</li>
    </ul>
    <ul id="R_6.102.018">
        <li id="R_6.102.018ab">rākṣasādhipate saumya nityaṃ madvijaye rata</li>
        <li id="R_6.102.018cd">vaidehī saṃnikarṣaṃ me śīghraṃ samupagacchatu</li>
    </ul>
    <ul id="R_6.102.019">
        <li id="R_6.102.019ab">sa tad vacanam ājñāya rāghavasya vibhīṣaṇaḥ</li>
        <li id="R_6.102.019cd">tūrṇam utsāraṇe yatnaṃ kārayām āsa sarvataḥ</li>
    </ul>
    <ul id="R_6.102.020">
        <li id="R_6.102.020ab">kañcukoṣṇīṣiṇas tatra vetrajharjharapāṇayaḥ</li>
        <li id="R_6.102.020cd">utsārayantaḥ puruṣāḥ samantāt paricakramuḥ</li>
    </ul>
    <ul id="R_6.102.021">
        <li id="R_6.102.021ab">ṛkṣāṇāṃ vānarāṇāṃ ca rākṣasānāṃ ca sarvataḥ</li>
        <li id="R_6.102.021cd">vṛndāny utsāryamāṇāni dūram utsasṛjus tataḥ</li>
    </ul>
    <ul id="R_6.102.022">
        <li id="R_6.102.022ab">teṣām utsāryamāṇānāṃ sarveṣāṃ dhvanir utthitaḥ</li>
        <li id="R_6.102.022cd">vāyunodvartamānasya sāgarasyeva nisvanaḥ</li>
    </ul>
    <ul id="R_6.102.023">
        <li id="R_6.102.023ab">utsāryamāṇāṃs tān dṛṣṭvā samantāj jātasaṃbhramān</li>
        <li id="R_6.102.023cd">dākṣiṇyāt tadamarṣāc ca vārayām āsa rāghavaḥ</li>
    </ul>
    <ul id="R_6.102.024">
        <li id="R_6.102.024ab">saṃrabdhaś cābravīd rāmaś cakṣuṣā pradahann iva</li>
        <li id="R_6.102.024cd">vibhīṣaṇaṃ mahāprājñaṃ sopālambham idaṃ vacaḥ</li>
    </ul>
    <ul id="R_6.102.025">
        <li id="R_6.102.025ab">kimarthaṃ mām anādṛtya kṛśyate 'yaṃ tvayā janaḥ</li>
        <li id="R_6.102.025cd">nivartayainam udyogaṃ jano 'yaṃ svajano mama</li>
    </ul>
    <ul id="R_6.102.026">
        <li id="R_6.102.026ab">na gṛhāṇi na vastrāṇi na prākārās tiraskriyāḥ</li>
        <li id="R_6.102.026cd">nedṛśā rājasatkārā vṛttam āvaraṇaṃ striyaḥ</li>
    </ul>
    <ul id="R_6.102.027">
        <li id="R_6.102.027ab">vyasaneṣu na kṛcchreṣu na yuddhe na svayaṃvare</li>
        <li id="R_6.102.027cd">na kratau no vivāhe ca darśanaṃ duṣyate striyaḥ</li>
    </ul>
    <ul id="R_6.102.028">
        <li id="R_6.102.028ab">saiṣā yuddhagatā caiva kṛcchre mahati ca sthitā</li>
        <li id="R_6.102.028cd">darśane 'syā na doṣaḥ syān matsamīpe viśeṣataḥ</li>
    </ul>
    <ul id="R_6.102.029">
        <li id="R_6.102.029ab">tad ānaya samīpaṃ me śīghram enāṃ vibhīṣaṇa</li>
        <li id="R_6.102.029cd">sītā paśyatu mām eṣā suhṛdgaṇavṛtaṃ sthitam</li>
    </ul>
    <ul id="R_6.102.030">
        <li id="R_6.102.030ab">evam uktas tu rāmeṇa savimarśo vibhīṣaṇaḥ</li>
        <li id="R_6.102.030cd">rāmasyopānayat sītāṃ saṃnikarṣaṃ vinītavat</li>
    </ul>
    <ul id="R_6.102.031">
        <li id="R_6.102.031ab">tato lakṣmaṇasugrīvau hanūmāṃś ca plavaṃgamaḥ</li>
        <li id="R_6.102.031cd">niśamya vākyaṃ rāmasya babhūvur vyathitā bhṛśam</li>
    </ul>
    <ul id="R_6.102.032">
        <li id="R_6.102.032ab">kalatranirapekṣaiś ca iṅgitair asya dāruṇaiḥ</li>
        <li id="R_6.102.032cd">aprītam iva sītāyāṃ tarkayanti sma rāghavam</li>
    </ul>
    <ul id="R_6.102.033">
        <li id="R_6.102.033ab">lajjayā tv avalīyantī sveṣu gātreṣu maithilī</li>
        <li id="R_6.102.033cd">vibhīṣaṇenānugatā bhartāraṃ sābhyavartata</li>
    </ul>
    <ul id="R_6.102.034">
        <li id="R_6.102.034ab">sā vastrasaṃruddhamukhī lajjayā janasaṃsadi</li>
        <li id="R_6.102.034cd">rurodāsādya bhartāram āryaputreti bhāṣiṇī</li>
    </ul>
    <ul id="R_6.102.035">
        <li id="R_6.102.035ab">vismayāc ca praharṣāc ca snehāc ca paridevatā</li>
        <li id="R_6.102.035cd">udaikṣata mukhaṃ bhartuḥ saumyaṃ saumyatarānanā</li>
    </ul>
    <ul id="R_6.102.036">
        <li id="R_6.102.036ab">
            <seg type="pāda" n="a">atha samapanudan manaḥklamaṃ sā</seg>
            <seg type="pāda" n="b">suciram adṛṣṭam udīkṣya vai priyasya</seg>
        </li>
        <li id="R_6.102.036cd">
            <seg type="pāda" n="c">vadanam uditapūrṇacandrakāntaṃ</seg>
            <seg type="pāda" n="d">vimalaśaśāṅkanibhānanā tadāsīt</seg>
        </li>
    </ul>
    <ul id="R_6.103.001">
        <li id="R_6.103.001ab">tāṃ tu pārśve sthitāṃ prahvāṃ rāmaḥ saṃprekṣya maithilīm</li>
        <li id="R_6.103.001cd">hṛdayāntargatakrodho vyāhartum upacakrame</li>
    </ul>
    <ul id="R_6.103.002">
        <li id="R_6.103.002ab">eṣāsi nirjitā bhadre śatruṃ jitvā mayā raṇe</li>
        <li id="R_6.103.002cd">pauruṣād yad anuṣṭheyaṃ tad etad upapāditam</li>
    </ul>
    <ul id="R_6.103.003">
        <li id="R_6.103.003ab">gato 'smy antam amarṣasya dharṣaṇā saṃpramārjitā</li>
        <li id="R_6.103.003cd">avamānaś ca śatruś ca mayā yugapad uddhṛtau</li>
    </ul>
    <ul id="R_6.103.004">
        <li id="R_6.103.004ab">adya me pauruṣaṃ dṛṣṭam adya me saphalaḥ śramaḥ</li>
        <li id="R_6.103.004cd">adya tīrṇapratijñatvāt prabhavāmīha cātmanaḥ</li>
    </ul>
    <ul id="R_6.103.005">
        <li id="R_6.103.005ab">yā tvaṃ virahitā nītā calacittena rakṣasā</li>
        <li id="R_6.103.005cd">daivasaṃpādito doṣo mānuṣeṇa mayā jitaḥ</li>
    </ul>
    <ul id="R_6.103.006">
        <li id="R_6.103.006ab">saṃprāptam avamānaṃ yas tejasā na pramārjati</li>
        <li id="R_6.103.006cd">kas tasya puruṣārtho 'sti puruṣasyālpatejasaḥ</li>
    </ul>
    <ul id="R_6.103.007">
        <li id="R_6.103.007ab">laṅghanaṃ ca samudrasya laṅkāyāś cāvamardanam</li>
        <li id="R_6.103.007cd">saphalaṃ tasya tac chlāghyam adya karma hanūmataḥ</li>
    </ul>
    <ul id="R_6.103.008">
        <li id="R_6.103.008ab">yuddhe vikramataś caiva hitaṃ mantrayataś ca me</li>
        <li id="R_6.103.008cd">sugrīvasya sasainyasya saphalo 'dya pariśramaḥ</li>
    </ul>
    <ul id="R_6.103.009">
        <li id="R_6.103.009ab">nirguṇaṃ bhrātaraṃ tyaktvā yo māṃ svayam upasthitaḥ</li>
        <li id="R_6.103.009cd">vibhīṣaṇasya bhaktasya saphalo 'dya pariśramaḥ</li>
    </ul>
    <ul id="R_6.103.010">
        <li id="R_6.103.010ab">ity evaṃ bruvatas tasya sītā rāmasya tad vacaḥ</li>
        <li id="R_6.103.010cd">mṛgīvotphullanayanā babhūvāśrupariplutā</li>
    </ul>
    <ul id="R_6.103.011">
        <li id="R_6.103.011ab">paśyatas tāṃ tu rāmasya bhūyaḥ krodho 'bhyavartata</li>
        <li id="R_6.103.011cd">prabhūtājyāvasiktasya pāvakasyeva dīpyataḥ</li>
    </ul>
    <ul id="R_6.103.012">
        <li id="R_6.103.012ab">sa baddhvā bhrukuṭiṃ vaktre tiryakprekṣitalocanaḥ</li>
        <li id="R_6.103.012cd">abravīt paruṣaṃ sītāṃ madhye vānararakṣasām</li>
    </ul>
    <ul id="R_6.103.013">
        <li id="R_6.103.013ab">yat kartavyaṃ manuṣyeṇa dharṣaṇāṃ parimārjatā</li>
        <li id="R_6.103.013cd">tat kṛtaṃ sakalaṃ sīte śatruhastād amarṣaṇāt</li>
    </ul>
    <ul id="R_6.103.014">
        <li id="R_6.103.014ab">nirjitā jīvalokasya tapasā bhāvitātmanā</li>
        <li id="R_6.103.014cd">agastyena durādharṣā muninā dakṣiṇeva dik</li>
    </ul>
    <ul id="R_6.103.015">
        <li id="R_6.103.015ab">viditaś cāstu bhadraṃ te yo 'yaṃ raṇapariśramaḥ</li>
        <li id="R_6.103.015cd">sa tīrṇaḥ suhṛdāṃ vīryān na tvadarthaṃ mayā kṛtaḥ</li>
    </ul>
    <ul id="R_6.103.016">
        <li id="R_6.103.016ab">rakṣatā tu mayā vṛttam apavādaṃ ca sarvaśaḥ</li>
        <li id="R_6.103.016cd">prakhyātasyātmavaṃśasya nyaṅgaṃ ca parimārjatā</li>
    </ul>
    <ul id="R_6.103.017">
        <li id="R_6.103.017ab">prāptacāritrasaṃdehā mama pratimukhe sthitā</li>
        <li id="R_6.103.017cd">dīpo netrāturasyeva pratikūlāsi me dṛḍham</li>
    </ul>
    <ul id="R_6.103.018">
        <li id="R_6.103.018ab">tad gaccha hy abhyanujñātā yateṣṭaṃ janakātmaje</li>
        <li id="R_6.103.018cd">etā daśa diśo bhadre kāryam asti na me tvayā</li>
    </ul>
    <ul id="R_6.103.019">
        <li id="R_6.103.019ab">kaḥ pumān hi kule jātaḥ striyaṃ paragṛhoṣitām</li>
        <li id="R_6.103.019cd">tejasvi punar ādadyāt suhṛllekhena cetasā</li>
    </ul>
    <ul id="R_6.103.020">
        <li id="R_6.103.020ab">rāvaṇāṅkaparibhraṣṭāṃ dṛṣṭāṃ duṣṭena cakṣuṣā</li>
        <li id="R_6.103.020cd">kathaṃ tvāṃ punarādadyāṃ kulaṃ vyapadiśan mahat</li>
    </ul>
    <ul id="R_6.103.021">
        <li id="R_6.103.021ab">tadarthaṃ nirjitā me tvaṃ yaśaḥ pratyāhṛtaṃ mayā</li>
        <li id="R_6.103.021cd">nāsti me tvayy abhiṣvaṅgo yatheṣṭaṃ gamyatām itaḥ</li>
    </ul>
    <ul id="R_6.103.022">
        <li id="R_6.103.022ab">iti pravyāhṛtaṃ bhadre mayaitat kṛtabuddhinā</li>
        <li id="R_6.103.022cd">lakṣmaṇe bharate vā tvaṃ kuru buddhiṃ yathāsukham</li>
    </ul>
    <ul id="R_6.103.023">
        <li id="R_6.103.023ab">sugrīve vānarendre vā rākṣasendre vibhīṣaṇe</li>
        <li id="R_6.103.023cd">niveśaya manaḥ sīte yathā vā sukham ātmanaḥ</li>
    </ul>
    <ul id="R_6.103.024">
        <li id="R_6.103.024ab">na hi tvāṃ rāvaṇo dṛṣṭvā divyarūpāṃ manoramām</li>
        <li id="R_6.103.024cd">marṣayate ciraṃ sīte svagṛhe parivartinīm</li>
    </ul>
    <ul id="R_6.103.025">
        <li id="R_6.103.025ab">
            <seg type="pāda" n="a">tataḥ priyārhaśvaraṇā tad apriyaṃ</seg>
            <seg type="pāda" n="b">priyād upaśrutya cirasya maithilī</seg>
        </li>
        <li id="R_6.103.025cd">
            <seg type="pāda" n="c">mumoca bāṣpaṃ subhṛśaṃ pravepitā</seg>
            <seg type="pāda" n="d">gajendrahastābhihateva vallarī</seg>
        </li>
    </ul>
    <ul id="R_6.104.001">
        <li id="R_6.104.001ab">evam uktā tu vaidehī paruṣaṃ lomaharṣaṇam</li>
        <li id="R_6.104.001cd">rāghaveṇa saroṣeṇa bhṛśaṃ pravyathitābhavat</li>
    </ul>
    <ul id="R_6.104.002">
        <li id="R_6.104.002ab">sā tad aśrutapūrvaṃ hi jane mahati maithilī</li>
        <li id="R_6.104.002cd">śrutvā bhartṛvaco rūkṣaṃ lajjayā vrīḍitābhavat</li>
    </ul>
    <ul id="R_6.104.003">
        <li id="R_6.104.003ab">praviśantīva gātrāṇi svāny eva janakātmajā</li>
        <li id="R_6.104.003cd">vākśalyais taiḥ saśalyeva bhṛśam aśrūṇy avartayat</li>
    </ul>
    <ul id="R_6.104.004">
        <li id="R_6.104.004ab">tato bāṣpaparikliṣṭaṃ pramārjantī svam ānanam</li>
        <li id="R_6.104.004cd">śanair gadgadayā vācā bhartāram idam abravīt</li>
    </ul>
    <ul id="R_6.104.005">
        <li id="R_6.104.005ab">kiṃ mām asadṛśaṃ vākyam īdṛśaṃ śrotradāruṇam</li>
        <li id="R_6.104.005cd">rūkṣaṃ śrāvayase vīra prākṛtaḥ prākṛtām iva</li>
    </ul>
    <ul id="R_6.104.006">
        <li id="R_6.104.006ab">na tathāsmi mahābāho yathā tvam avagacchasi</li>
        <li id="R_6.104.006cd">pratyayaṃ gaccha me svena cāritreṇaiva te śape</li>
    </ul>
    <ul id="R_6.104.007">
        <li id="R_6.104.007ab">pṛthak strīṇāṃ pracāreṇa jātiṃ tvaṃ pariśaṅkase</li>
        <li id="R_6.104.007cd">parityajemāṃ śaṅkāṃ tu yadi te 'haṃ parīkṣitā</li>
    </ul>
    <ul id="R_6.104.008">
        <li id="R_6.104.008ab">yady ahaṃ gātrasaṃsparśaṃ gatāsmi vivaśā prabho</li>
        <li id="R_6.104.008cd">kāmakāro na me tatra daivaṃ tatrāparādhyati</li>
    </ul>
    <ul id="R_6.104.009">
        <li id="R_6.104.009ab">madadhīnaṃ tu yat tan me hṛdayaṃ tvayi vartate</li>
        <li id="R_6.104.009cd">parādhīneṣu gātreṣu kiṃ kariṣyāmy anīśvarā</li>
    </ul>
    <ul id="R_6.104.010">
        <li id="R_6.104.010ab">sahasaṃvṛddhabhāvāc ca saṃsargeṇa ca mānada</li>
        <li id="R_6.104.010cd">yady ahaṃ te na vijñātā hatā tenāsmi śāśvatam</li>
    </ul>
    <ul id="R_6.104.011">
        <li id="R_6.104.011ab">preṣitas te yadā vīro hanūmān avalokakaḥ</li>
        <li id="R_6.104.011cd">laṅkāsthāhaṃ tvayā vīra kiṃ tadā na visarjitā</li>
    </ul>
    <ul id="R_6.104.012">
        <li id="R_6.104.012ab">pratyakṣaṃ vānarendrasya tvadvākyasamanantaram</li>
        <li id="R_6.104.012cd">tvayā saṃtyaktayā vīra tyaktaṃ syāj jīvitaṃ mayā</li>
    </ul>
    <ul id="R_6.104.013">
        <li id="R_6.104.013ab">na vṛthā te śramo 'yaṃ syāt saṃśaye nyasya jīvitam</li>
        <li id="R_6.104.013cd">suhṛjjanaparikleśo na cāyaṃ niṣphalas tava</li>
    </ul>
    <ul id="R_6.104.014">
        <li id="R_6.104.014ab">tvayā tu naraśārdūla krodham evānuvartatā</li>
        <li id="R_6.104.014cd">laghuneva manuṣyeṇa strītvam eva puraskṛtam</li>
    </ul>
    <ul id="R_6.104.015">
        <li id="R_6.104.015ab">apadeśena janakān notpattir vasudhātalāt</li>
        <li id="R_6.104.015cd">mama vṛttaṃ ca vṛttajña bahu te na puraskṛtam</li>
    </ul>
    <ul id="R_6.104.016">
        <li id="R_6.104.016ab">na pramāṇīkṛtaḥ pāṇir bālye bālena pīḍitaḥ</li>
        <li id="R_6.104.016cd">mama bhaktiś ca śīlaṃ ca sarvaṃ te pṛṣṭhataḥ kṛtam</li>
    </ul>
    <ul id="R_6.104.017">
        <li id="R_6.104.017ab">evaṃ bruvāṇā rudatī bāṣpagadgadabhāṣiṇī</li>
        <li id="R_6.104.017cd">abravīl lakṣmaṇaṃ sītā dīnaṃ dhyānaparaṃ sthitam</li>
    </ul>
    <ul id="R_6.104.018">
        <li id="R_6.104.018ab">citāṃ me kuru saumitre vyasanasyāsya bheṣajam</li>
        <li id="R_6.104.018cd">mithyāpavādopahatā nāhaṃ jīvitum utsahe</li>
    </ul>
    <ul id="R_6.104.019">
        <li id="R_6.104.019ab">aprītasya guṇair bhartus tyaktayā janasaṃsadi</li>
        <li id="R_6.104.019cd">yā kṣamā me gatir gantuṃ pravekṣye havyavāhanam</li>
    </ul>
    <ul id="R_6.104.020">
        <li id="R_6.104.020ab">evam uktas tu vaidehyā lakṣmaṇaḥ paravīrahā</li>
        <li id="R_6.104.020cd">amarṣavaśam āpanno rāghavānanam aikṣata</li>
    </ul>
    <ul id="R_6.104.021">
        <li id="R_6.104.021ab">sa vijñāya manaśchandaṃ rāmasyākārasūcitam</li>
        <li id="R_6.104.021cd">citāṃ cakāra saumitrir mate rāmasya vīryavān</li>
    </ul>
    <ul id="R_6.104.022">
        <li id="R_6.104.022ab">adhomukhaṃ tato rāmaṃ śanaiḥ kṛtvā pradakṣiṇam</li>
        <li id="R_6.104.022cd">upāsarpata vaidehī dīpyamānaṃ hutāśanam</li>
    </ul>
    <ul id="R_6.104.023">
        <li id="R_6.104.023ab">praṇamya devatābhyaś ca brāhmaṇebhyaś ca maithilī</li>
        <li id="R_6.104.023cd">baddhāñjalipuṭā cedam uvācāgnisamīpataḥ</li>
    </ul>
    <ul id="R_6.104.024">
        <li id="R_6.104.024ab">yathā me hṛdayaṃ nityaṃ nāpasarpati rāghavāt</li>
        <li id="R_6.104.024cd">tathā lokasya sākṣī māṃ sarvataḥ pātu pāvakaḥ</li>
    </ul>
    <ul id="R_6.104.025">
        <li id="R_6.104.025ab">evam uktvā tu vaidehī parikramya hutāśanam</li>
        <li id="R_6.104.025cd">viveśa jvalanaṃ dīptaṃ niḥsaṅgenāntarātmanā</li>
    </ul>
    <ul id="R_6.104.026">
        <li id="R_6.104.026ab">janaḥ sa sumahāṃs tatra bālavṛddhasamākulaḥ</li>
        <li id="R_6.104.026cd">dadarśa maithilīṃ tatra praviśantīṃ hutāśanam</li>
    </ul>
    <ul id="R_6.104.027">
        <li id="R_6.104.027ab">tasyām agniṃ viśantyāṃ tu hāheti vipulaḥ svanaḥ</li>
        <li id="R_6.104.027cd">rakṣasāṃ vānarāṇāṃ ca saṃbabhūvādbhutopamaḥ</li>
    </ul>
    <ul id="R_6.105.001">
        <li id="R_6.105.001ab">tato vaiśravaṇo rājā yamaś cāmitrakarśanaḥ</li>
        <li id="R_6.105.001cd">sahasrākṣo mahendraś ca varuṇaś ca paraṃtapaḥ</li>
    </ul>
    <ul id="R_6.105.002">
        <li id="R_6.105.002ab">ṣaḍardhanayanaḥ śrīmān mahādevo vṛṣadhvajaḥ</li>
        <li id="R_6.105.002cd">kartā sarvasya lokasya brahmā brahmavidāṃ varaḥ</li>
    </ul>
    <ul id="R_6.105.003">
        <li id="R_6.105.003ab">ete sarve samāgamya vimānaiḥ sūryasaṃnibhaiḥ</li>
        <li id="R_6.105.003cd">āgamya nagarīṃ laṅkām abhijagmuś ca rāghavam</li>
    </ul>
    <ul id="R_6.105.004">
        <li id="R_6.105.004ab">tataḥ sahastābharaṇān pragṛhya vipulān bhujān</li>
        <li id="R_6.105.004cd">abruvaṃs tridaśaśreṣṭhāḥ prāñjaliṃ rāghavaṃ sthitam</li>
    </ul>
    <ul id="R_6.105.005">
        <li id="R_6.105.005ab">kartā sarvasya lokasya śreṣṭho jñānavatāṃ varaḥ</li>
        <li id="R_6.105.005cd">upekṣase kathaṃ sītāṃ patantīṃ havyavāhane</li>
        <li id="R_6.105.005ef">kathaṃ devagaṇaśreṣṭham ātmānaṃ nāvabudhyase</li>
    </ul>
    <ul id="R_6.105.006">
        <li id="R_6.105.006ab">ṛtadhāmā vasuḥ pūrvaṃ vasūnāṃ ca prajāpatiḥ</li>
        <li id="R_6.105.006cd">tvaṃ trayāṇāṃ hi lokānām ādikartā svayaṃprabhuḥ</li>
    </ul>
    <ul id="R_6.105.007">
        <li id="R_6.105.007ab">rudrāṇām aṣṭamo rudraḥ sādhyānām api pañcamaḥ</li>
        <li id="R_6.105.007cd">aśvinau cāpi te karṇau candrasūryau ca cakṣuṣī</li>
    </ul>
    <ul id="R_6.105.008">
        <li id="R_6.105.008ab">ante cādau ca lokānāṃ dṛśyase tvaṃ paraṃtapa</li>
        <li id="R_6.105.008cd">upekṣase ca vaidehīṃ mānuṣaḥ prākṛto yathā</li>
    </ul>
    <ul id="R_6.105.009">
        <li id="R_6.105.009ab">ity ukto lokapālais taiḥ svāmī lokasya rāghavaḥ</li>
        <li id="R_6.105.009cd">abravīt tridaśaśreṣṭhān rāmo dharmabhṛtāṃ varaḥ</li>
    </ul>
    <ul id="R_6.105.010">
        <li id="R_6.105.010ab">ātmānaṃ mānuṣaṃ manye rāmaṃ daśarathātmajam</li>
        <li id="R_6.105.010cd">yo 'haṃ yasya yataś cāhaṃ bhagavāṃs tad bravītu me</li>
    </ul>
    <ul id="R_6.105.011">
        <li id="R_6.105.011ab">iti bruvāṇaṃ kākutsthaṃ brahmā brahmavidāṃ varaḥ</li>
        <li id="R_6.105.011cd">abravīc chṛṇu me rāma satyaṃ satyaparākrama</li>
    </ul>
    <ul id="R_6.105.012">
        <li id="R_6.105.012ab">bhavān nārāyaṇo devaḥ śrīmāṃś cakrāyudho vibhuḥ</li>
        <li id="R_6.105.012cd">ekaśṛṅgo varāhas tvaṃ bhūtabhavyasapatnajit</li>
    </ul>
    <ul id="R_6.105.013">
        <li id="R_6.105.013ab">akṣaraṃ brahmasatyaṃ ca madhye cānte ca rāghava</li>
        <li id="R_6.105.013cd">lokānāṃ tvaṃ paro dharmo viṣvaksenaś caturbhujaḥ</li>
    </ul>
    <ul id="R_6.105.014">
        <li id="R_6.105.014ab">śārṅgadhanvā hṛṣīkeśaḥ puruṣaḥ puruṣottamaḥ</li>
        <li id="R_6.105.014cd">ajitaḥ khaḍgadhṛg viṣṇuḥ kṛṣṇaś caiva bṛhadbalaḥ</li>
    </ul>
    <ul id="R_6.105.015">
        <li id="R_6.105.015ab">senānīr grāmaṇīś ca tvaṃ buddhiḥ sattvaṃ kṣamā damaḥ</li>
        <li id="R_6.105.015cd">prabhavaś cāpyayaś ca tvam upendro madhusūdanaḥ</li>
    </ul>
    <ul id="R_6.105.016">
        <li id="R_6.105.016ab">indrakarmā mahendras tvaṃ padmanābho raṇāntakṛt</li>
        <li id="R_6.105.016cd">śaraṇyaṃ śaraṇaṃ ca tvām āhur divyā maharṣayaḥ</li>
    </ul>
    <ul id="R_6.105.017">
        <li id="R_6.105.017ab">sahasraśṛṅgo vedātmā śatajihvo maharṣabhaḥ</li>
        <li id="R_6.105.017cd">tvaṃ yajñas tvaṃ vaṣaṭkāras tvam oṃkāraḥ paraṃtapa</li>
    </ul>
    <ul id="R_6.105.018">
        <li id="R_6.105.018ab">prabhavaṃ nidhanaṃ vā te na viduḥ ko bhavān iti</li>
        <li id="R_6.105.018cd">dṛśyase sarvabhūteṣu brāhmaṇeṣu ca goṣu ca</li>
    </ul>
    <ul id="R_6.105.019">
        <li id="R_6.105.019ab">dikṣu sarvāsu gagane parvateṣu vaneṣu ca</li>
        <li id="R_6.105.019cd">sahasracaraṇaḥ śrīmāñ śataśīrṣaḥ sahasradhṛk</li>
    </ul>
    <ul id="R_6.105.020">
        <li id="R_6.105.020ab">tvaṃ dhārayasi bhūtāni vasudhāṃ ca saparvatām</li>
        <li id="R_6.105.020cd">ante pṛthivyāḥ salile dṛśyase tvaṃ mahoragaḥ</li>
    </ul>
    <ul id="R_6.105.021">
        <li id="R_6.105.021ab">trīṃl lokān dhārayan rāma devagandharvadānavān</li>
        <li id="R_6.105.021cd">ahaṃ te hṛdayaṃ rāma jihvā devī sarasvatī</li>
    </ul>
    <ul id="R_6.105.022">
        <li id="R_6.105.022ab">devā gātreṣu lomāni nirmitā brahmaṇā prabho</li>
        <li id="R_6.105.022cd">nimeṣas te 'bhavad rātrir unmeṣas te 'bhavad divā</li>
    </ul>
    <ul id="R_6.105.023">
        <li id="R_6.105.023ab">saṃskārās te 'bhavan vedā na tad asti tvayā vinā</li>
        <li id="R_6.105.023cd">jagat sarvaṃ śarīraṃ te sthairyaṃ te vasudhātalam</li>
    </ul>
    <ul id="R_6.105.024">
        <li id="R_6.105.024ab">agniḥ kopaḥ prasādas te somaḥ śrīvatsalakṣaṇa</li>
        <li id="R_6.105.024cd">tvayā lokās trayaḥ krāntāḥ purāṇe vikramais tribhiḥ</li>
    </ul>
    <ul id="R_6.105.025">
        <li id="R_6.105.025ab">mahendraś ca kṛto rājā baliṃ baddhvā mahāsuram</li>
        <li id="R_6.105.025cd">sītā lakṣmīr bhavān viṣṇur devaḥ kṛṣṇaḥ prajāpatiḥ</li>
    </ul>
    <ul id="R_6.105.026">
        <li id="R_6.105.026ab">vadhārthaṃ rāvaṇasyeha praviṣṭo mānuṣīṃ tanum</li>
        <li id="R_6.105.026cd">tad idaṃ naḥ kṛtaṃ kāryaṃ tvayā dharmabhṛtāṃ vara</li>
    </ul>
    <ul id="R_6.105.027">
        <li id="R_6.105.027ab">nihato rāvaṇo rāma prahṛṣṭo divam ākrama</li>
        <li id="R_6.105.027cd">amoghaṃ balavīryaṃ te amoghas te parākramaḥ</li>
    </ul>
    <ul id="R_6.105.028">
        <li id="R_6.105.028ab">amoghās te bhaviṣyanti bhaktimantaś ca ye narāḥ</li>
        <li id="R_6.105.028cd">ye tvāṃ devaṃ dhruvaṃ bhaktāḥ purāṇaṃ puruṣottamam</li>
    </ul>
    <ul id="R_6.105.029">
        <li id="R_6.105.029ab">ye narāḥ kīrtayiṣyanti nāsti teṣāṃ parābhavaḥ</li>
    </ul>
    <ul id="R_6.106.001">
        <li id="R_6.106.001ab">etac chrutvā śubhaṃ vākyaṃ pitāmahasamīritam</li>
        <li id="R_6.106.001cd">aṅkenādāya vaidehīm utpapāta vibhāvasuḥ</li>
    </ul>
    <ul id="R_6.106.002">
        <li id="R_6.106.002ab">taruṇādityasaṃkāśāṃ taptakāñcanabhūṣaṇām</li>
        <li id="R_6.106.002cd">raktāmbaradharāṃ bālāṃ nīlakuñcitamūrdhajām</li>
    </ul>
    <ul id="R_6.106.003">
        <li id="R_6.106.003ab">akliṣṭamālyābharaṇāṃ tathā rūpāṃ manasvinīm</li>
        <li id="R_6.106.003cd">dadau rāmāya vaidehīm aṅke kṛtvā vibhāvasuḥ</li>
    </ul>
    <ul id="R_6.106.004">
        <li id="R_6.106.004ab">abravīc ca tadā rāmaṃ sākṣī lokasya pāvakaḥ</li>
        <li id="R_6.106.004cd">eṣā te rāma vaidehī pāpam asyā na vidyate</li>
    </ul>
    <ul id="R_6.106.005">
        <li id="R_6.106.005ab">naiva vācā na manasā nānudhyānān na cakṣuṣā</li>
        <li id="R_6.106.005cd">suvṛttā vṛttaśauṇḍīrā na tvām aticacāra ha</li>
    </ul>
    <ul id="R_6.106.006">
        <li id="R_6.106.006ab">rāvaṇenāpanītaiṣā vīryotsiktena rakṣasā</li>
        <li id="R_6.106.006cd">tvayā virahitā dīnā vivaśā nirjanād vanāt</li>
    </ul>
    <ul id="R_6.106.007">
        <li id="R_6.106.007ab">ruddhā cāntaḥpure guptā tvakcittā tvatparāyaṇā</li>
        <li id="R_6.106.007cd">rakṣitā rākṣasī saṃghair vikṛtair ghoradarśanaiḥ</li>
    </ul>
    <ul id="R_6.106.008">
        <li id="R_6.106.008ab">pralobhyamānā vividhaṃ bhartsyamānā ca maithilī</li>
        <li id="R_6.106.008cd">nācintayata tad rakṣas tvadgatenāntarātmanā</li>
    </ul>
    <ul id="R_6.106.009">
        <li id="R_6.106.009ab">viśuddhabhāvāṃ niṣpāpāṃ pratigṛhṇīṣva rāghava</li>
        <li id="R_6.106.009cd">na kiṃ cid abhidhātavyam aham ājñāpayāmi te</li>
    </ul>
    <ul id="R_6.106.010">
        <li id="R_6.106.010ab">evam ukto mahātejā dhṛtimān dṛḍhavikramaḥ</li>
        <li id="R_6.106.010cd">abravīt tridaśaśreṣṭhaṃ rāmo dharmabhṛtāṃ varaḥ</li>
    </ul>
    <ul id="R_6.106.011">
        <li id="R_6.106.011ab">avaśyaṃ triṣu lokeṣu sītā pāvanam arhati</li>
        <li id="R_6.106.011cd">dīrghakāloṣitā ceyaṃ rāvaṇāntaḥpure śubhā</li>
    </ul>
    <ul id="R_6.106.012">
        <li id="R_6.106.012ab">bāliśaḥ khalu kāmātmā rāmo daśarathātmajaḥ</li>
        <li id="R_6.106.012cd">iti vakṣyanti māṃ santo jānakīm aviśodhya hi</li>
    </ul>
    <ul id="R_6.106.013">
        <li id="R_6.106.013ab">ananyahṛdayāṃ bhaktāṃ maccittaparirakṣaṇīm</li>
        <li id="R_6.106.013cd">aham apy avagacchāmi maithilīṃ janakātmajām</li>
    </ul>
    <ul id="R_6.106.014">
        <li id="R_6.106.014ab">pratyayārthaṃ tu lokānāṃ trayāṇāṃ satyasaṃśrayaḥ</li>
        <li id="R_6.106.014cd">upekṣe cāpi vaidehīṃ praviśantīṃ hutāśanam</li>
    </ul>
    <ul id="R_6.106.015">
        <li id="R_6.106.015ab">imām api viśālākṣīṃ rakṣitāṃ svena tejasā</li>
        <li id="R_6.106.015cd">rāvaṇo nātivarteta velām iva mahodadhiḥ</li>
    </ul>
    <ul id="R_6.106.016">
        <li id="R_6.106.016ab">na hi śaktaḥ sa duṣṭātmā manasāpi hi maithilīm</li>
        <li id="R_6.106.016cd">pradharṣayitum aprāptāṃ dīptām agniśikhām iva</li>
    </ul>
    <ul id="R_6.106.017">
        <li id="R_6.106.017ab">neyam arhati caiśvaryaṃ rāvaṇāntaḥpure śubhā</li>
        <li id="R_6.106.017cd">ananyā hi mayā sītāṃ bhāskareṇa prabhā yathā</li>
    </ul>
    <ul id="R_6.106.018">
        <li id="R_6.106.018ab">viśuddhā triṣu lokeṣu maithilī janakātmajā</li>
        <li id="R_6.106.018cd">na hi hātum iyaṃ śakyā kīrtir ātmavatā yathā</li>
    </ul>
    <ul id="R_6.106.019">
        <li id="R_6.106.019ab">avaśyaṃ ca mayā kāryaṃ sarveṣāṃ vo vaco hitam</li>
        <li id="R_6.106.019cd">snigdhānāṃ lokamānyānām evaṃ ca bruvatāṃ hitam</li>
    </ul>
    <ul id="R_6.106.020">
        <li id="R_6.106.020ab">
            <seg type="pāda" n="a">itīdam uktvā vacanaṃ mahābalaiḥ</seg>
            <seg type="pāda" n="b">praśasyamānaḥ svakṛtena karmaṇā</seg>
        </li>
        <li id="R_6.106.020cd">
            <seg type="pāda" n="c">sametya rāmaḥ priyayā mahābalaḥ</seg>
            <seg type="pāda" n="d">sukhaṃ sukhārho 'nubabhūva rāghavaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.107.001">
        <li id="R_6.107.001ab">etac chrutvā śubhaṃ vākyaṃ rāghaveṇa subhāṣitam</li>
        <li id="R_6.107.001cd">idaṃ śubhataraṃ vākyaṃ vyājahāra maheśvaraḥ</li>
    </ul>
    <ul id="R_6.107.002">
        <li id="R_6.107.002ab">puṣkarākṣa mahābāho mahāvakṣaḥ paraṃtapa</li>
        <li id="R_6.107.002cd">diṣṭyā kṛtam idaṃ karma tvayā śastrabhṛtāṃ vara</li>
    </ul>
    <ul id="R_6.107.003">
        <li id="R_6.107.003ab">diṣṭyā sarvasya lokasya pravṛddhaṃ dāruṇaṃ tamaḥ</li>
        <li id="R_6.107.003cd">apāvṛttaṃ tvayā saṃkhye rāma rāvaṇajaṃ bhayam</li>
    </ul>
    <ul id="R_6.107.004">
        <li id="R_6.107.004ab">āśvāsya bharataṃ dīnaṃ kausalyāṃ ca yaśasvinīm</li>
        <li id="R_6.107.004cd">kaikeyīṃ ca sumitrāṃ ca dṛṣṭvā lakṣmaṇamātaram</li>
    </ul>
    <ul id="R_6.107.005">
        <li id="R_6.107.005ab">prāpya rājyam ayodhyāyāṃ nandayitvā suhṛjjanam</li>
        <li id="R_6.107.005cd">ikṣvākūṇāṃ kule vaṃśaṃ sthāpayitvā mahābala</li>
    </ul>
    <ul id="R_6.107.006">
        <li id="R_6.107.006ab">iṣṭvā turagamedhena prāpya cānuttamaṃ yaśaḥ</li>
        <li id="R_6.107.006cd">brāhmaṇebhyo dhanaṃ dattvā tridivaṃ gantum arhasi</li>
    </ul>
    <ul id="R_6.107.007">
        <li id="R_6.107.007ab">eṣa rājā vimānasthaḥ pitā daśarathas tava</li>
        <li id="R_6.107.007cd">kākutstha mānuṣe loke gurus tava mahāyaśāḥ</li>
    </ul>
    <ul id="R_6.107.008">
        <li id="R_6.107.008ab">indralokaṃ gataḥ śrīmāṃs tvayā putreṇa tāritaḥ</li>
        <li id="R_6.107.008cd">lakṣmaṇena saha bhrātrā tvam enam abhivādaya</li>
    </ul>
    <ul id="R_6.107.009">
        <li id="R_6.107.009ab">mahādevavacaḥ śrutvā kākutsthaḥ sahalakṣmaṇaḥ</li>
        <li id="R_6.107.009cd">vimānaśikharasthasya praṇāmam akarot pituḥ</li>
    </ul>
    <ul id="R_6.107.010">
        <li id="R_6.107.010ab">dīpyamānaṃ svayāṃ lakṣmyā virajo'mbaradhāriṇam</li>
        <li id="R_6.107.010cd">lakṣmaṇena saha bhrātrā dadarśa pitaraṃ prabhuḥ</li>
    </ul>
    <ul id="R_6.107.011">
        <li id="R_6.107.011ab">harṣeṇa mahatāviṣṭo vimānastho mahīpatiḥ</li>
        <li id="R_6.107.011cd">prāṇaiḥ priyataraṃ dṛṣṭvā putraṃ daśarathas tadā</li>
    </ul>
    <ul id="R_6.107.012">
        <li id="R_6.107.012ab">āropyāṅkaṃ mahābāhur varāsanagataḥ prabhuḥ</li>
        <li id="R_6.107.012cd">bāhubhyāṃ saṃpariṣvajya tato vākyaṃ samādade</li>
    </ul>
    <ul id="R_6.107.013">
        <li id="R_6.107.013ab">na me svargo bahumataḥ saṃmānaś ca surarṣibhiḥ</li>
        <li id="R_6.107.013cd">tvayā rāma vihīnasya satyaṃ pratiśṛṇomi te</li>
    </ul>
    <ul id="R_6.107.014">
        <li id="R_6.107.014ab">kaikeyyā yāni coktāni vākyāni vadatāṃ vara</li>
        <li id="R_6.107.014cd">tava pravrājanārthāni sthitāni hṛdaye mama</li>
    </ul>
    <ul id="R_6.107.015">
        <li id="R_6.107.015ab">tvāṃ tu dṛṣṭvā kuśalinaṃ pariṣvajya salakṣmaṇam</li>
        <li id="R_6.107.015cd">adya duḥkhād vimukto 'smi nīhārād iva bhāskaraḥ</li>
    </ul>
    <ul id="R_6.107.016">
        <li id="R_6.107.016ab">tārito 'haṃ tvayā putra suputreṇa mahātmanā</li>
        <li id="R_6.107.016cd">aṣṭāvakreṇa dharmātmā tārito brāhmaṇo yathā</li>
    </ul>
    <ul id="R_6.107.017">
        <li id="R_6.107.017ab">idānīṃ ca vijānāmi yathā saumya sureśvaraiḥ</li>
        <li id="R_6.107.017cd">vadhārthaṃ rāvaṇasyeha vihitaṃ puruṣottamam</li>
    </ul>
    <ul id="R_6.107.018">
        <li id="R_6.107.018ab">siddhārthā khalu kausalyā yā tvāṃ rāma gṛhaṃ gatam</li>
        <li id="R_6.107.018cd">vanān nivṛttaṃ saṃhṛṣṭā drakṣyate śatrusūdana</li>
    </ul>
    <ul id="R_6.107.019">
        <li id="R_6.107.019ab">siddhārthāḥ khalu te rāma narā ye tvāṃ purīṃ gatam</li>
        <li id="R_6.107.019cd">jalārdram abhiṣiktaṃ ca drakṣyanti vasudhādhipam</li>
    </ul>
    <ul id="R_6.107.020">
        <li id="R_6.107.020ab">anuraktena balinā śucinā dharmacāriṇā</li>
        <li id="R_6.107.020cd">iccheyaṃ tvām ahaṃ draṣṭuṃ bharatena samāgatam</li>
    </ul>
    <ul id="R_6.107.021">
        <li id="R_6.107.021ab">caturdaśasamāḥ saumya vane niryāpitās tvayā</li>
        <li id="R_6.107.021cd">vasatā sītayā sārdhaṃ lakṣmaṇena ca dhīmatā</li>
    </ul>
    <ul id="R_6.107.022">
        <li id="R_6.107.022ab">nivṛttavanavāso 'si pratijñā saphalā kṛtā</li>
        <li id="R_6.107.022cd">rāvaṇaṃ ca raṇe hatvā devās te paritoṣitāḥ</li>
    </ul>
    <ul id="R_6.107.023">
        <li id="R_6.107.023ab">kṛtaṃ karma yaśaḥ ślāghyaṃ prāptaṃ te śatrusūdana</li>
        <li id="R_6.107.023cd">bhrātṛbhiḥ saha rājyastho dīrgham āyur avāpnuhi</li>
    </ul>
    <ul id="R_6.107.024">
        <li id="R_6.107.024ab">iti bruvāṇaṃ rājānaṃ rāmaḥ prāñjalir abravīt</li>
        <li id="R_6.107.024cd">kuru prasādaṃ dharmajña kaikeyyā bharatasya ca</li>
    </ul>
    <ul id="R_6.107.025">
        <li id="R_6.107.025ab">saputrāṃ tvāṃ tyajāmīti yad uktā kaikayī tvayā</li>
        <li id="R_6.107.025cd">sa śāpaḥ kaikayīṃ ghoraḥ saputrāṃ na spṛśet prabho</li>
    </ul>
    <ul id="R_6.107.026">
        <li id="R_6.107.026ab">sa tatheti mahārājo rāmam uktvā kṛtāñjalim</li>
        <li id="R_6.107.026cd">lakṣmaṇaṃ ca pariṣvajya punar vākyam uvāca ha</li>
    </ul>
    <ul id="R_6.107.027">
        <li id="R_6.107.027ab">rāmaṃ śuśrūṣatā bhaktyā vaidehyā saha sītayā</li>
        <li id="R_6.107.027cd">kṛtā mama mahāprītiḥ prāptaṃ dharmaphalaṃ ca te</li>
    </ul>
    <ul id="R_6.107.028">
        <li id="R_6.107.028ab">dharmaṃ prāpsyasi dharmajña yaśaś ca vipulaṃ bhuvi</li>
        <li id="R_6.107.028cd">rāme prasanne svargaṃ ca mahimānaṃ tathaiva ca</li>
    </ul>
    <ul id="R_6.107.029">
        <li id="R_6.107.029ab">rāmaṃ śuśrūṣa bhadraṃ te sumitrānandavardhana</li>
        <li id="R_6.107.029cd">rāmaḥ sarvasya lokasya śubheṣv abhirataḥ sadā</li>
    </ul>
    <ul id="R_6.107.030">
        <li id="R_6.107.030ab">ete sendrās trayo lokāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_6.107.030cd">abhigamya mahātmānam arcanti puruṣottamam</li>
    </ul>
    <ul id="R_6.107.031">
        <li id="R_6.107.031ab">etat tad uktam avyaktam akṣaraṃ brahmanirmitam</li>
        <li id="R_6.107.031cd">devānāṃ hṛdayaṃ saumya guhyaṃ rāmaḥ paraṃtapaḥ</li>
    </ul>
    <ul id="R_6.107.032">
        <li id="R_6.107.032ab">avāptaṃ dharmacaraṇaṃ yaśaś ca vipulaṃ tvayā</li>
        <li id="R_6.107.032cd">rāmaṃ śuśrūṣatā bhaktyā vaidehyā saha sītayā</li>
    </ul>
    <ul id="R_6.107.033">
        <li id="R_6.107.033ab">sa tathoktvā mahābāhur lakṣmaṇaṃ prāñjaliṃ sthitam</li>
        <li id="R_6.107.033cd">uvāca rājā dharmātmā vaidehīṃ vacanaṃ śubham</li>
    </ul>
    <ul id="R_6.107.034">
        <li id="R_6.107.034ab">kartavyo na tu vaidehi manyus tyāgam imaṃ prati</li>
        <li id="R_6.107.034cd">rāmeṇa tvadviśuddhyarthaṃ kṛtam etad dhitaiṣiṇā</li>
    </ul>
    <ul id="R_6.107.035">
        <li id="R_6.107.035ab">na tvaṃ subhru samādheyā patiśuśrūvaṇaṃ prati</li>
        <li id="R_6.107.035cd">avaśyaṃ tu mayā vācyam eṣa te daivataṃ param</li>
    </ul>
    <ul id="R_6.107.036">
        <li id="R_6.107.036ab">iti pratisamādiśya putrau sītāṃ tathā snuṣām</li>
        <li id="R_6.107.036cd">indralokaṃ vimānena yayau daśaratho jvalan</li>
    </ul>
    <ul id="R_6.108.001">
        <li id="R_6.108.001ab">pratiprayāte kākutsthe mahendraḥ pākaśāsanaḥ</li>
        <li id="R_6.108.001cd">abravīt paramaprīto rāghavaṃ prāñjaliṃ sthitam</li>
    </ul>
    <ul id="R_6.108.002">
        <li id="R_6.108.002ab">amoghaṃ darśanaṃ rāma tavāsmākaṃ paraṃtapa</li>
        <li id="R_6.108.002cd">prītiyukto 'smi tena tvaṃ brūhi yan manasecchasi</li>
    </ul>
    <ul id="R_6.108.003">
        <li id="R_6.108.003ab">evam uktas tu kākutsthaḥ pratyuvāca kṛtāñjaliḥ</li>
        <li id="R_6.108.003cd">lakṣmaṇena saha bhrātrā sītayā cāpi bhāryayā</li>
    </ul>
    <ul id="R_6.108.004">
        <li id="R_6.108.004ab">yadi prītiḥ samutpannā mayi sarvasureśvara</li>
        <li id="R_6.108.004cd">vakṣyāmi kuru me satyaṃ vacanaṃ vadatāṃ vara</li>
    </ul>
    <ul id="R_6.108.005">
        <li id="R_6.108.005ab">mama hetoḥ parākrāntā ye gatā yamasādanam</li>
        <li id="R_6.108.005cd">te sarve jīvitaṃ prāpya samuttiṣṭhantu vānarāḥ</li>
    </ul>
    <ul id="R_6.108.006">
        <li id="R_6.108.006ab">matpriyeṣv abhiraktāś ca na mṛtyuṃ gaṇayanti ca</li>
        <li id="R_6.108.006cd">tvatprasādāt sameyus te varam etad ahaṃ vṛṇe</li>
    </ul>
    <ul id="R_6.108.007">
        <li id="R_6.108.007ab">nīrujān nirvraṇāṃś caiva saṃpannabalapauruṣān</li>
        <li id="R_6.108.007cd">golāṅgūlāṃs tathaivarkṣān draṣṭum icchāmi mānada</li>
    </ul>
    <ul id="R_6.108.008">
        <li id="R_6.108.008ab">akāle cāpi mukhyāni mūlāni ca phalāni ca</li>
        <li id="R_6.108.008cd">nadyaś ca vimalās tatra tiṣṭheyur yatra vānarāḥ</li>
    </ul>
    <ul id="R_6.108.009">
        <li id="R_6.108.009ab">śrutvā tu vacanaṃ tasya rāghavasya mahātmanaḥ</li>
        <li id="R_6.108.009cd">mahendraḥ pratyuvācedaṃ vacanaṃ prītilakṣaṇam</li>
    </ul>
    <ul id="R_6.108.010">
        <li id="R_6.108.010ab">mahān ayaṃ varas tāta tvayokto raghunandana</li>
        <li id="R_6.108.010cd">samutthāsyanti harayaḥ suptā nidrākṣaye yathā</li>
    </ul>
    <ul id="R_6.108.011">
        <li id="R_6.108.011ab">suhṛdbhir bāndhavaiś caiva jñātibhiḥ svajanena ca</li>
        <li id="R_6.108.011cd">sarva eva sameṣyanti saṃyuktāḥ parayā mudā</li>
    </ul>
    <ul id="R_6.108.012">
        <li id="R_6.108.012ab">akāle puṣpaśabalāḥ phalavantaś ca pādapāḥ</li>
        <li id="R_6.108.012cd">bhaviṣyanti maheṣvāsa nadyaś ca salilāyutāḥ</li>
    </ul>
    <ul id="R_6.108.013">
        <li id="R_6.108.013ab">savraṇaiḥ prathamaṃ gātraiḥ saṃvṛtair nivraṇaiḥ punaḥ</li>
        <li id="R_6.108.013cd">babhūvur vānarāḥ sarve kim etad iti vismitaḥ</li>
    </ul>
    <ul id="R_6.108.014">
        <li id="R_6.108.014ab">kākutsthaṃ paripūrṇārthaṃ dṛṣṭvā sarve surottamāḥ</li>
        <li id="R_6.108.014cd">ūcus te prathamaṃ stutvā stavārhaṃ sahalakṣmaṇam</li>
    </ul>
    <ul id="R_6.108.015">
        <li id="R_6.108.015ab">gacchāyodhyām ito vīra visarjaya ca vānarān</li>
        <li id="R_6.108.015cd">maithilīṃ sāntvayasvainām anuraktāṃ tapasvinīm</li>
    </ul>
    <ul id="R_6.108.016">
        <li id="R_6.108.016ab">bhrātaraṃ paśya bharataṃ tvacchokād vratacāriṇam</li>
        <li id="R_6.108.016cd">abhiṣecaya cātmānaṃ paurān gatvā praharṣaya</li>
    </ul>
    <ul id="R_6.108.017">
        <li id="R_6.108.017ab">evam uktvā tam āmantrya rāmaṃ saumitriṇā saha</li>
        <li id="R_6.108.017cd">vimānaiḥ sūryasaṃkāśair hṛṣṭā jagmuḥ surā divam</li>
    </ul>
    <ul id="R_6.108.018">
        <li id="R_6.108.018ab">abhivādya ca kākutsthaḥ sarvāṃs tāṃs tridaśottamān</li>
        <li id="R_6.108.018cd">lakṣmaṇena saha bhrātrā vāsam ājñāpayat tadā</li>
    </ul>
    <ul id="R_6.108.019">
        <li id="R_6.108.019ab">
            <seg type="pāda" n="a">tatas tu sā lakṣmaṇarāmapālitā</seg>
            <seg type="pāda" n="b">mahācamūr hṛṣṭajanā yaśasvinī</seg>
        </li>
        <li id="R_6.108.019cd">
            <seg type="pāda" n="c">śriyā jvalantī virarāja sarvato</seg>
            <seg type="pāda" n="d">niśāpraṇīteva hi śītaraśminā</seg>
        </li>
    </ul>
    <ul id="R_6.109.001">
        <li id="R_6.109.001ab">tāṃ rātrim uṣitaṃ rāmaṃ sukhotthitam ariṃdamam</li>
        <li id="R_6.109.001cd">abravīt prāñjalir vākyaṃ jayaṃ pṛṣṭvā vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.109.002">
        <li id="R_6.109.002ab">snānāni cāṅgarāgāṇi vastrāṇy ābharaṇāni ca</li>
        <li id="R_6.109.002cd">candanāni ca divyāni mālyāni vividhāni ca</li>
    </ul>
    <ul id="R_6.109.003">
        <li id="R_6.109.003ab">alaṃkāravidaś cemā nāryaḥ padmanibhekṣaṇāḥ</li>
        <li id="R_6.109.003cd">upasthitās tvāṃ vidhivat snāpayiṣyanti rāghava</li>
    </ul>
    <ul id="R_6.109.004">
        <li id="R_6.109.004ab">evam uktas tu kākutsthaḥ pratyuvāca vibhīṣaṇam</li>
        <li id="R_6.109.004cd">harīn sugrīvamukhyāṃs tvaṃ snānenopanimantraya</li>
    </ul>
    <ul id="R_6.109.005">
        <li id="R_6.109.005ab">sa tu tāmyati dharmātmā mamahetoḥ sukhocitaḥ</li>
        <li id="R_6.109.005cd">sukumāro mahābāhuḥ kumāraḥ satyasaṃśravaḥ</li>
    </ul>
    <ul id="R_6.109.006">
        <li id="R_6.109.006ab">taṃ vinā kaikeyīputraṃ bharataṃ dharmacāriṇam</li>
        <li id="R_6.109.006cd">na me snānaṃ bahumataṃ vastrāṇy ābharaṇāni ca</li>
    </ul>
    <ul id="R_6.109.007">
        <li id="R_6.109.007ab">ita eva pathā kṣipraṃ pratigacchāma tāṃ purīm</li>
        <li id="R_6.109.007cd">ayodhyām āyato hy eṣa panthāḥ paramadurgamaḥ</li>
    </ul>
    <ul id="R_6.109.008">
        <li id="R_6.109.008ab">evam uktas tu kākutsthaṃ pratyuvāca vibhīṣaṇaḥ</li>
        <li id="R_6.109.008cd">ahnā tvāṃ prāpayiṣyāmi tāṃ purīṃ pārthivātmaja</li>
    </ul>
    <ul id="R_6.109.009">
        <li id="R_6.109.009ab">puṣpakaṃ nāma bhadraṃ te vimānaṃ sūryasaṃnibham</li>
        <li id="R_6.109.009cd">mama bhrātuḥ kuberasya rāvaṇenāhṛtaṃ balāt</li>
    </ul>
    <ul id="R_6.109.010">
        <li id="R_6.109.010ab">tad idaṃ meghasaṃkāśaṃ vimānam iha tiṣṭhati</li>
        <li id="R_6.109.010cd">tena yāsyasi yānena tvam ayodhyāṃ gajajvaraḥ</li>
    </ul>
    <ul id="R_6.109.011">
        <li id="R_6.109.011ab">ahaṃ te yady anugrāhyo yadi smarasi me guṇān</li>
        <li id="R_6.109.011cd">vasa tāvad iha prājña yady asti mayi sauhṛdam</li>
    </ul>
    <ul id="R_6.109.012">
        <li id="R_6.109.012ab">lakṣmaṇena saha bhrātrā vaidehyā cāpi bhāryayā</li>
        <li id="R_6.109.012cd">arcitaḥ sarvakāmais tvaṃ tato rāma gamiṣyasi</li>
    </ul>
    <ul id="R_6.109.013">
        <li id="R_6.109.013ab">prītiyuktas tu me rāma sasainyaḥ sasuhṛdgaṇaḥ</li>
        <li id="R_6.109.013cd">satkriyāṃ vihitāṃ tāvad gṛhāṇa tvaṃ mayodyatām</li>
    </ul>
    <ul id="R_6.109.014">
        <li id="R_6.109.014ab">praṇayād bahumānāc ca sauhṛdena ca rāghava</li>
        <li id="R_6.109.014cd">prasādayāmi preṣyo 'haṃ na khalv ājñāpayāmi te</li>
    </ul>
    <ul id="R_6.109.015">
        <li id="R_6.109.015ab">evam uktas tato rāmaḥ pratyuvāca vibhīṣaṇam</li>
        <li id="R_6.109.015cd">rakṣasāṃ vānarāṇāṃ ca sarveṣāṃ copaśṛṇvatām</li>
    </ul>
    <ul id="R_6.109.016">
        <li id="R_6.109.016ab">pūjito 'haṃ tvayā vīra sācivyena paraṃtapa</li>
        <li id="R_6.109.016cd">sarvātmanā ca ceṣṭibhiḥ sauhṛdenottamena ca</li>
    </ul>
    <ul id="R_6.109.017">
        <li id="R_6.109.017ab">na khalv etan na kuryāṃ te vacanaṃ rākṣaseśvara</li>
        <li id="R_6.109.017cd">taṃ tu me bhrātaraṃ draṣṭuṃ bharataṃ tvarate manaḥ</li>
    </ul>
    <ul id="R_6.109.018">
        <li id="R_6.109.018ab">māṃ nivartayituṃ yo 'sau citrakūṭam upāgataḥ</li>
        <li id="R_6.109.018cd">śirasā yācato yasya vacanaṃ na kṛtaṃ mayā</li>
    </ul>
    <ul id="R_6.109.019">
        <li id="R_6.109.019ab">kausalyāṃ ca sumitrāṃ ca kaikeyīṃ ca yaśasvinīm</li>
        <li id="R_6.109.019cd">gurūṃś ca suhṛdaś caiva paurāṃś ca tanayaiḥ saha</li>
    </ul>
    <ul id="R_6.109.020">
        <li id="R_6.109.020ab">upasthāpaya me kṣipraṃ vimānaṃ rākṣaseśvara</li>
        <li id="R_6.109.020cd">kṛtakāryasya me vāsaḥ kathaṃ cid iha saṃmataḥ</li>
    </ul>
    <ul id="R_6.109.021">
        <li id="R_6.109.021ab">anujānīhi māṃ saumya pūjito 'smi vibhīṣaṇa</li>
        <li id="R_6.109.021cd">manyur na khalu kartavyas tvaritas tvānumānaye</li>
    </ul>
    <ul id="R_6.109.022">
        <li id="R_6.109.022ab">tataḥ kāñcanacitrāṅgaṃ vaidūryamaṇivedikam</li>
        <li id="R_6.109.022cd">kūṭāgāraiḥ parikṣiptaṃ sarvato rajataprabham</li>
    </ul>
    <ul id="R_6.109.023">
        <li id="R_6.109.023ab">pāṇḍurābhiḥ patākābhir dhvajaiś ca samalaṃkṛtam</li>
        <li id="R_6.109.023cd">śobhitaṃ kāñcanair harmyair hemapadmavibhūṣitam</li>
    </ul>
    <ul id="R_6.109.024">
        <li id="R_6.109.024ab">prakīrṇaṃ kiṅkiṇījālair muktāmaṇigavākṣitam</li>
        <li id="R_6.109.024cd">ghaṇṭājālaiḥ parikṣiptaṃ sarvato madhurasvanam</li>
    </ul>
    <ul id="R_6.109.025">
        <li id="R_6.109.025ab">tan meruśikharākāraṃ nirmitaṃ viśvakarmaṇā</li>
        <li id="R_6.109.025cd">bahubhir bhūṣitaṃ harmyair muktārajatasaṃnibhau</li>
    </ul>
    <ul id="R_6.109.026">
        <li id="R_6.109.026ab">talaiḥ sphaṭikacitrāṅgair vaidūryaiś ca varāsanaiḥ</li>
        <li id="R_6.109.026cd">mahārhāstaraṇopetair upapannaṃ mahādhanaiḥ</li>
    </ul>
    <ul id="R_6.109.027">
        <li id="R_6.109.027ab">upasthitam anādhṛṣyaṃ tad vimānaṃ manojavam</li>
        <li id="R_6.109.027cd">nivedayitvā rāmāya tasthau tatra vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.110.001">
        <li id="R_6.110.001ab">upasthitaṃ tu taṃ dṛṣṭvā puṣpakaṃ puṣpabhūṣitam</li>
        <li id="R_6.110.001cd">avidūre sthitaṃ rāmaṃ pratyuvāca vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.110.002">
        <li id="R_6.110.002ab">sa tu baddhāñjaliḥ prahvo vinīto rākṣaseśvaraḥ</li>
        <li id="R_6.110.002cd">abravīt tvarayopetaḥ kiṃ karomīti rāghavam</li>
    </ul>
    <ul id="R_6.110.003">
        <li id="R_6.110.003ab">tam abravīn mahātejā lakṣmaṇasyopaśṛṇvataḥ</li>
        <li id="R_6.110.003cd">vimṛśya rāghavo vākyam idaṃ snehapuraskṛtam</li>
    </ul>
    <ul id="R_6.110.004">
        <li id="R_6.110.004ab">kṛtaprayatnakarmāṇo vibhīṣaṇa vanaukasaḥ</li>
        <li id="R_6.110.004cd">ratnair arthaiś ca vivibhair bhūṣaṇaiś cābhipūjaya</li>
    </ul>
    <ul id="R_6.110.005">
        <li id="R_6.110.005ab">sahaibhir arditā laṅkā nirjitā rākṣaseśvara</li>
        <li id="R_6.110.005cd">hṛṣṭaiḥ prāṇabhayaṃ tyaktvā saṃgrāmeṣv anivartibhiḥ</li>
    </ul>
    <ul id="R_6.110.006">
        <li id="R_6.110.006ab">evaṃ saṃmānitāś ceme mānārhā mānada tvayā</li>
        <li id="R_6.110.006cd">bhaviṣyanti kṛtajñena nirvṛtā hariyūthapāḥ</li>
    </ul>
    <ul id="R_6.110.007">
        <li id="R_6.110.007ab">tyāginaṃ saṃgrahītāraṃ sānukrośaṃ yaśasvinam</li>
        <li id="R_6.110.007cd">yatas tvām avagacchanti tataḥ saṃbodhayāmi te</li>
    </ul>
    <ul id="R_6.110.008">
        <li id="R_6.110.008ab">evam uktas tu rāmeṇa vānarāṃs tān vibhīṣaṇaḥ</li>
        <li id="R_6.110.008cd">ratnārthaiḥ saṃvibhāgena sarvān evānvapūjayat</li>
    </ul>
    <ul id="R_6.110.009">
        <li id="R_6.110.009ab">tatas tān pūjitān dṛṣṭvā ratnair arthaiś ca yūthapān</li>
        <li id="R_6.110.009cd">āruroha tato rāmas tad vimānam anuttamam</li>
    </ul>
    <ul id="R_6.110.010">
        <li id="R_6.110.010ab">aṅkenādāya vaidehīṃ lajjamānāṃ yaśasvinīm</li>
        <li id="R_6.110.010cd">lakṣmaṇena saha bhrātrā vikrāntena dhanuṣmatā</li>
    </ul>
    <ul id="R_6.110.011">
        <li id="R_6.110.011ab">abravīc ca vimānasthaḥ kākutsthaḥ sarvavānarān</li>
        <li id="R_6.110.011cd">sugrīvaṃ ca mahāvīryaṃ rākṣasaṃ ca vibhīṣaṇam</li>
    </ul>
    <ul id="R_6.110.012">
        <li id="R_6.110.012ab">mitrakāryaṃ kṛtam idaṃ bhavadbhir vānarottamāḥ</li>
        <li id="R_6.110.012cd">anujñātā mayā sarve yatheṣṭaṃ pratigacchata</li>
    </ul>
    <ul id="R_6.110.013">
        <li id="R_6.110.013ab">yat tu kāryaṃ vayasyena suhṛdā vā paraṃtapa</li>
        <li id="R_6.110.013cd">kṛtaṃ sugrīva tat sarvaṃ bhavatā dharmabhīruṇā</li>
        <li id="R_6.110.013ef">kiṣkindhāṃ pratiyāhy āśu svasainyenābhisaṃvṛtaḥ</li>
    </ul>
    <ul id="R_6.110.014">
        <li id="R_6.110.014ab">svarājye vasa laṅkāyāṃ mayā datte vibhīṣaṇa</li>
        <li id="R_6.110.014cd">na tvāṃ dharṣayituṃ śaktāḥ sendrā api divaukasaḥ</li>
    </ul>
    <ul id="R_6.110.015">
        <li id="R_6.110.015ab">ayodhyāṃ pratiyāsyāmi rājadhānīṃ pitur mama</li>
        <li id="R_6.110.015cd">abhyanujñātum icchāmi sarvān āmantrayāmi vaḥ</li>
    </ul>
    <ul id="R_6.110.016">
        <li id="R_6.110.016ab">evam uktās tu rāmeṇa vānarās te mahābalāḥ</li>
        <li id="R_6.110.016cd">ūcuḥ prāñjalayo rāmaṃ rākṣasaś ca vibhīṣaṇaḥ</li>
        <li id="R_6.110.016ef">ayodhyāṃ gantum icchāmaḥ sarvān nayatu no bhavān</li>
    </ul>
    <ul id="R_6.110.017">
        <li id="R_6.110.017ab">dṛṣṭvā tvām abhiṣekārdraṃ kausalyām abhivādya ca</li>
        <li id="R_6.110.017cd">acireṇāgamiṣyāmaḥ svān gṛhān nṛpateḥ suta</li>
    </ul>
    <ul id="R_6.110.018">
        <li id="R_6.110.018ab">evam uktas tu dharmātmā vānaraiḥ savibhīṣaṇaiḥ</li>
        <li id="R_6.110.018cd">abravīd rāghavaḥ śrīmān sasugrīvavibhīṣaṇān</li>
    </ul>
    <ul id="R_6.110.019">
        <li id="R_6.110.019ab">priyāt priyataraṃ labdhaṃ yad ahaṃ sasuhṛjjanaḥ</li>
        <li id="R_6.110.019cd">sarvair bhavadbhiḥ sahitaḥ prītiṃ lapsye purīṃ gataḥ</li>
    </ul>
    <ul id="R_6.110.020">
        <li id="R_6.110.020ab">kṣipram āroha sugrīva vimānaṃ vānaraiḥ saha</li>
        <li id="R_6.110.020cd">tvam adhyāroha sāmātyo rākṣasendravibhīṣaṇa</li>
    </ul>
    <ul id="R_6.110.021">
        <li id="R_6.110.021ab">tatas tat puṣpakaṃ divyaṃ sugrīvaḥ saha senayā</li>
        <li id="R_6.110.021cd">adhyārohat tvarañ śīghraṃ sāmātyaś ca vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.110.022">
        <li id="R_6.110.022ab">teṣv ārūḍheṣu sarveṣu kauberaṃ paramāsanam</li>
        <li id="R_6.110.022cd">rāghaveṇābhyanujñātam utpapāta vihāyasaṃ</li>
    </ul>
    <ul id="R_6.110.023">
        <li id="R_6.110.023ab">yayau tena vimānena haṃsayuktena bhāsvatā</li>
        <li id="R_6.110.023cd">prahṛṣṭaś ca pratītaś ca babhau rāmaḥ kuberavat</li>
    </ul>
    <ul id="R_6.111.001">
        <li id="R_6.111.001ab">anujñātaṃ tu rāmeṇa tad vimānam anuttamam</li>
        <li id="R_6.111.001cd">utpapāta mahāmeghaḥ śvasanenoddhato yathā</li>
    </ul>
    <ul id="R_6.111.002">
        <li id="R_6.111.002ab">pātayitvā tataś cakṣuḥ sarvato raghunandanaḥ</li>
        <li id="R_6.111.002cd">abravīn maithilīṃ sītāṃ rāmaḥ śaśinibhānanām</li>
    </ul>
    <ul id="R_6.111.003">
        <li id="R_6.111.003ab">kailāsaśikharākāre trikūṭaśikhare sthitām</li>
        <li id="R_6.111.003cd">laṅkām īkṣasva vaidehi nirmitāṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_6.111.004">
        <li id="R_6.111.004ab">etad āyodhanaṃ paśya māṃsaśoṇitakardamam</li>
        <li id="R_6.111.004cd">harīṇāṃ rākṣasānāṃ ca sīte viśasanaṃ mahat</li>
    </ul>
    <ul id="R_6.111.005">
        <li id="R_6.111.005ab">tavahetor viśālākṣi rāvaṇo nihato mayā</li>
        <li id="R_6.111.005cd">kumbhakarṇo 'tra nihataḥ prahastaś ca niśācaraḥ</li>
    </ul>
    <ul id="R_6.111.006">
        <li id="R_6.111.006ab">lakṣmaṇenendrajic cātra rāvaṇir nihato raṇe</li>
        <li id="R_6.111.006cd">virūpākṣaś ca duṣprekṣyo mahāpārśvamahodarau</li>
    </ul>
    <ul id="R_6.111.007">
        <li id="R_6.111.007ab">akampanaś ca nihato balino 'nye ca rākṣasāḥ</li>
        <li id="R_6.111.007cd">triśirāś cātikāyaś ca devāntakanarāntakau</li>
    </ul>
    <ul id="R_6.111.008">
        <li id="R_6.111.008ab">atra mandodarī nāma bhāryā taṃ paryadevayat</li>
        <li id="R_6.111.008cd">sapatnīnāṃ sahasreṇa sāsreṇa parivāritā</li>
    </ul>
    <ul id="R_6.111.009">
        <li id="R_6.111.009ab">etat tu dṛśyate tīrthaṃ samudrasya varānane</li>
        <li id="R_6.111.009cd">yatra sāgaram uttīrya tāṃ rātrim uṣitā vayam</li>
    </ul>
    <ul id="R_6.111.010">
        <li id="R_6.111.010ab">eṣa setur mayā baddhaḥ sāgare salilārṇave</li>
        <li id="R_6.111.010cd">tavahetor viśālākṣi nalasetuḥ suduṣkaraḥ</li>
    </ul>
    <ul id="R_6.111.011">
        <li id="R_6.111.011ab">paśya sāgaram akṣobhyaṃ vaidehi varuṇālayam</li>
        <li id="R_6.111.011cd">apāram abhigarjantaṃ śaṅkhaśuktiniṣevitam</li>
    </ul>
    <ul id="R_6.111.012">
        <li id="R_6.111.012ab">hiraṇyanābhaṃ śailendraṃ kāñcanaṃ paśya maithili</li>
        <li id="R_6.111.012cd">viśramārthaṃ hanumato bhittvā sāgaram utthitam</li>
    </ul>
    <ul id="R_6.111.013">
        <li id="R_6.111.013ab">atra rākṣasarājo 'yam ājagāma vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.111.014">
        <li id="R_6.111.014ab">eṣā sā dṛśyate sīte kiṣkindhā citrakānanā</li>
        <li id="R_6.111.014cd">sugrīvasya purī ramyā yatra vālī mayā hataḥ</li>
    </ul>
    <ul id="R_6.111.015">
        <li id="R_6.111.015ab">dṛśyate 'sau mahān sīte savidyud iva toyadaḥ</li>
        <li id="R_6.111.015cd">ṛśyamūko giriśreṣṭhaḥ kāñcanair dhātubhir vṛtaḥ</li>
    </ul>
    <ul id="R_6.111.016">
        <li id="R_6.111.016ab">atrāhaṃ vānarendreṇa sugrīveṇa samāgataḥ</li>
        <li id="R_6.111.016cd">samayaś ca kṛtaḥ sīte vadhārthaṃ vālino mayā</li>
    </ul>
    <ul id="R_6.111.017">
        <li id="R_6.111.017ab">eṣā sā dṛśyate pampā nalinī citrakānanā</li>
        <li id="R_6.111.017cd">tvayā vihīno yatrāhaṃ vilalāpa suduḥkhitaḥ</li>
    </ul>
    <ul id="R_6.111.018">
        <li id="R_6.111.018ab">asyās tīre mayā dṛṣṭā śabarī dharmacāriṇī</li>
        <li id="R_6.111.018cd">atra yojanabāhuś ca kabandho nihato mayā</li>
    </ul>
    <ul id="R_6.111.019">
        <li id="R_6.111.019ab">dṛśyate 'sau janasthāne sīte śrīmān vanaspatiḥ</li>
        <li id="R_6.111.019cd">yatra yuddhaṃ mahad vṛttaṃ tavahetor vilāsini</li>
        <li id="R_6.111.019ef">rāvaṇasya nṛśaṃsasya jaṭāyoś ca mahātmanaḥ</li>
    </ul>
    <ul id="R_6.111.020">
        <li id="R_6.111.020ab">kharaś ca nihataś saṃkhye dūṣaṇaś ca nipātitaḥ</li>
        <li id="R_6.111.020cd">triśirāś ca mahāvīryo mayā bāṇair ajihmagaiḥ</li>
    </ul>
    <ul id="R_6.111.021">
        <li id="R_6.111.021ab">parṇaśālā tathā citrā dṛśyate śubhadarśanā</li>
        <li id="R_6.111.021cd">yatra tvaṃ rākṣasendreṇa rāvaṇena hṛtā balāt</li>
    </ul>
    <ul id="R_6.111.022">
        <li id="R_6.111.022ab">eṣā godāvarī ramyā prasannasalilā śivā</li>
        <li id="R_6.111.022cd">agastyasyāśramo hy eṣa dṛśyate paśya maithili</li>
    </ul>
    <ul id="R_6.111.023">
        <li id="R_6.111.023ab">vaidehi dṛśyate cātra śarabhaṅgāśramo mahān</li>
        <li id="R_6.111.023cd">upayātaḥ sahasrākṣo yatra śakraḥ puraṃdaraḥ</li>
    </ul>
    <ul id="R_6.111.024">
        <li id="R_6.111.024ab">ete te tāpasāvāsā dṛśyante tanumadhyame</li>
        <li id="R_6.111.024cd">atriḥ kulapatir yatra sūryavaiśvānaraprabhaḥ</li>
        <li id="R_6.111.024ef">atra sīte tvayā dṛṣṭā tāpasī dharmacāriṇī</li>
    </ul>
    <ul id="R_6.111.025">
        <li id="R_6.111.025ab">asmin deśe mahākāyo virādho nihato mayā</li>
    </ul>
    <ul id="R_6.111.026">
        <li id="R_6.111.026ab">asau sutanuśailendraś citrakūṭaḥ prakāśate</li>
        <li id="R_6.111.026cd">yatra māṃ kaikayīputraḥ prasādayitum āgataḥ</li>
    </ul>
    <ul id="R_6.111.027">
        <li id="R_6.111.027ab">eṣā sā yamunā dūrād dṛśyate citrakānanā</li>
        <li id="R_6.111.027cd">bharadvājāśramo yatra śrīmān eṣa prakāśate</li>
    </ul>
    <ul id="R_6.111.028">
        <li id="R_6.111.028ab">eṣā tripathagā gaṅgā dṛśyate varavarṇini</li>
        <li id="R_6.111.028cd">śṛṅgaverapuraṃ caitad guho yatra samāgataḥ</li>
    </ul>
    <ul id="R_6.111.029">
        <li id="R_6.111.029ab">eṣā sā dṛśyate 'yodhyā rājadhānī pitur mama</li>
        <li id="R_6.111.029cd">ayodhyāṃ kuru vaidehi praṇāmaṃ punar āgatā</li>
    </ul>
    <ul id="R_6.111.030">
        <li id="R_6.111.030ab">tatas te vānarāḥ sarve rākṣasaś ca vibhīṣaṇaḥ</li>
        <li id="R_6.111.030cd">utpatyotpatya dadṛśus tāṃ purīṃ śubhadarśanām</li>
    </ul>
    <ul id="R_6.111.031">
        <li id="R_6.111.031ab">
            <seg type="pāda" n="a">tatas tu tāṃ pāṇḍuraharmyamālinīṃ</seg>
            <seg type="pāda" n="b">viśālakakṣyāṃ gajavājisaṃkulām</seg>
        </li>
        <li id="R_6.111.031cd">
            <seg type="pāda" n="c">purīm ayodhyāṃ dadṛśuḥ plavaṃgamāḥ</seg>
            <seg type="pāda" n="d">purīṃ mahendrasya yathāmarāvatīm</seg>
        </li>
    </ul>
    <ul id="R_6.112.001">
        <li id="R_6.112.001ab">pūrṇe caturdaśe varṣe pañcabhyāṃ lakṣmaṇāgrajaḥ</li>
        <li id="R_6.112.001cd">bharadvājāśramaṃ prāpya vavande niyato munim</li>
    </ul>
    <ul id="R_6.112.002">
        <li id="R_6.112.002ab">so 'pṛcchad abhivādyainaṃ bharadvājaṃ tapodhanam</li>
        <li id="R_6.112.002cd">śṛṇoṣi ka cid bhagavan subhikṣānāmayaṃ pure</li>
        <li id="R_6.112.002ef">kaccic ca yukto bharato jīvanty api ca mātaraḥ</li>
    </ul>
    <ul id="R_6.112.003">
        <li id="R_6.112.003ab">evam uktas tu rāmeṇa bharadvājo mahāmuniḥ</li>
        <li id="R_6.112.003cd">pratyuvāca raghuśreṣṭhaṃ smitapūrvaṃ prahṛṣṭavat</li>
    </ul>
    <ul id="R_6.112.004">
        <li id="R_6.112.004ab">paṅkadigdhas tu bharato jaṭilas tvāṃ pratīkṣate</li>
        <li id="R_6.112.004cd">pāduke te puraskṛtya sarvaṃ ca kuśalaṃ gṛhe</li>
    </ul>
    <ul id="R_6.112.005">
        <li id="R_6.112.005ab">tvāṃ purā cīravasanaṃ praviśantaṃ mahāvanam</li>
        <li id="R_6.112.005cd">strītṛtīyaṃ cyutaṃ rājyād dharmakāmaṃ ca kevalam</li>
    </ul>
    <ul id="R_6.112.006">
        <li id="R_6.112.006ab">padātiṃ tyaktasarvasvaṃ pitur vacanakāriṇam</li>
        <li id="R_6.112.006cd">svargabhogaiḥ parityaktaṃ svargacyutam ivāmaram</li>
    </ul>
    <ul id="R_6.112.007">
        <li id="R_6.112.007ab">dṛṣṭvā tu karuṇā pūrvaṃ mamāsīt samitiṃjaya</li>
        <li id="R_6.112.007cd">kaikeyīvacane yuktaṃ vanyamūlaphalāśanam</li>
    </ul>
    <ul id="R_6.112.008">
        <li id="R_6.112.008ab">sāmprataṃ susamṛddhārthaṃ samitragaṇabāndhavam</li>
        <li id="R_6.112.008cd">samīkṣya vijitāriṃ tvāṃ mama prītir anuttamā</li>
    </ul>
    <ul id="R_6.112.009">
        <li id="R_6.112.009ab">sarvaṃ ca sukhaduḥkhaṃ te viditaṃ mama rāghava</li>
        <li id="R_6.112.009cd">yat tvayā vipulaṃ prāptaṃ janasthānavadhādikam</li>
    </ul>
    <ul id="R_6.112.010">
        <li id="R_6.112.010ab">brāhmaṇārthe niyuktasya rakṣataḥ sarvatāpasān</li>
        <li id="R_6.112.010cd">mārīcadarśanaṃ caiva sītonmathanam eva ca</li>
    </ul>
    <ul id="R_6.112.011">
        <li id="R_6.112.011ab">kabandhadarśanaṃ caiva pampābhigamanaṃ tathā</li>
        <li id="R_6.112.011cd">sugrīveṇa ca te sakhyaṃ yac ca vālī hatas tvayā</li>
    </ul>
    <ul id="R_6.112.012">
        <li id="R_6.112.012ab">mārgaṇaṃ caiva vaidehyāḥ karma vātātmajasya ca</li>
        <li id="R_6.112.012cd">viditāyāṃ ca vaidehyāṃ nalasetur yathā kṛtaḥ</li>
        <li id="R_6.112.012ef">yathā ca dīpitā laṅkā prahṛṣṭair hariyūthapaiḥ</li>
    </ul>
    <ul id="R_6.112.013">
        <li id="R_6.112.013ab">saputrabāndhavāmātyaḥ sabalaḥ saha vāhanaḥ</li>
        <li id="R_6.112.013cd">yathā ca nihataḥ saṃkhye rāvaṇo devakaṇṭakaḥ</li>
    </ul>
    <ul id="R_6.112.014">
        <li id="R_6.112.014ab">samāgamaś ca tridaśair yathādattaś ca te varaḥ</li>
        <li id="R_6.112.014cd">sarvaṃ mamaitad viditaṃ tapasā dharmavatsala</li>
    </ul>
    <ul id="R_6.112.015">
        <li id="R_6.112.015ab">aham apy atra te dadmi varaṃ śastrabhṛtāṃ vara</li>
        <li id="R_6.112.015cd">arghyaṃ pratigṛhāṇedam ayodhyāṃ śvo gamiṣyasi</li>
    </ul>
    <ul id="R_6.112.016">
        <li id="R_6.112.016ab">tasya tac chirasā vākyaṃ pratigṛhya nṛpātmajaḥ</li>
        <li id="R_6.112.016cd">bāḍham ity eva saṃhṛṣṭaḥ śrīmān varam ayācata</li>
    </ul>
    <ul id="R_6.112.017">
        <li id="R_6.112.017ab">akālaphalino vṛkṣāḥ sarve cāpi madhusravāḥ</li>
        <li id="R_6.112.017cd">bhavantu mārge bhagavann ayodhyāṃ prati gacchataḥ</li>
    </ul>
    <ul id="R_6.112.018">
        <li id="R_6.112.018ab">niṣphalāḥ phalinaś cāsan vipuṣpāḥ puṣpaśālinaḥ</li>
        <li id="R_6.112.018cd">śuṣkāḥ samagrapatrās te nagāś caiva madhusravāḥ</li>
    </ul>
    <ul id="R_6.113.001">
        <li id="R_6.113.001ab">ayodhyāṃ tu samālokya cintayām āsa rāghavaḥ</li>
        <li id="R_6.113.001cd">cintayitvā tato dṛṣṭiṃ vānareṣu nyapātayat</li>
    </ul>
    <ul id="R_6.113.002">
        <li id="R_6.113.002ab">priyakāmaḥ priyaṃ rāmas tatas tvaritavikramam</li>
        <li id="R_6.113.002cd">uvāca dhīmāṃs tejasvī hanūmantaṃ plavaṃgamam</li>
    </ul>
    <ul id="R_6.113.003">
        <li id="R_6.113.003ab">ayodhyāṃ tvarito gaccha kṣipraṃ tvaṃ plavagottama</li>
        <li id="R_6.113.003cd">jānīhi kaccit kuśalī jano nṛpatimandire</li>
    </ul>
    <ul id="R_6.113.004">
        <li id="R_6.113.004ab">śṛṅgaverapuraṃ prāpya guhaṃ gahanagocaram</li>
        <li id="R_6.113.004cd">niṣādādhipatiṃ brūhi kuśalaṃ vacanān mama</li>
    </ul>
    <ul id="R_6.113.005">
        <li id="R_6.113.005ab">śrutvā tu māṃ kuśalinam arogaṃ vigatajvaram</li>
        <li id="R_6.113.005cd">bhaviṣyati guhaḥ prītaḥ sa mamātmasamaḥ sakhā</li>
    </ul>
    <ul id="R_6.113.006">
        <li id="R_6.113.006ab">ayodhyāyāś ca te mārgaṃ pravṛttiṃ bharatasya ca</li>
        <li id="R_6.113.006cd">nivedayiṣyati prīto niṣādādhipatir guhaḥ</li>
    </ul>
    <ul id="R_6.113.007">
        <li id="R_6.113.007ab">bharatas tu tvayā vācyaḥ kuśalaṃ vacanān mama</li>
        <li id="R_6.113.007cd">siddhārthaṃ śaṃsa māṃ tasmai sabhāryaṃ sahalakṣmaṇam</li>
    </ul>
    <ul id="R_6.113.008">
        <li id="R_6.113.008ab">haraṇaṃ cāpi vaidehyā rāvaṇena balīyasā</li>
        <li id="R_6.113.008cd">sugrīveṇa ca saṃvādaṃ vālinaś ca vadhaṃ raṇe</li>
    </ul>
    <ul id="R_6.113.009">
        <li id="R_6.113.009ab">maithilyanveṣaṇaṃ caiva yathā cādhigatā tvayā</li>
        <li id="R_6.113.009cd">laṅghayitvā mahātoyam āpagāpatim avyayam</li>
    </ul>
    <ul id="R_6.113.010">
        <li id="R_6.113.010ab">upayānaṃ samudrasya sāgarasya ca darśanam</li>
        <li id="R_6.113.010cd">yathā ca kāritaḥ setū rāvaṇaś ca yathā hataḥ</li>
    </ul>
    <ul id="R_6.113.011">
        <li id="R_6.113.011ab">varadānaṃ mahendreṇa brahmaṇā varuṇena ca</li>
        <li id="R_6.113.011cd">mahādevaprasādāc ca pitrā mama samāgamam</li>
    </ul>
    <ul id="R_6.113.012">
        <li id="R_6.113.012ab">jitvā śatrugaṇān rāmaḥ prāpya cānuttamaṃ yaśaḥ</li>
        <li id="R_6.113.012cd">upayāti samṛddhārthaḥ saha mitrair mahābalaḥ</li>
    </ul>
    <ul id="R_6.113.013">
        <li id="R_6.113.013ab">etac chrutvā yamākāraṃ bhajate bharatas tataḥ</li>
        <li id="R_6.113.013cd">sa ca te veditavyaḥ syāt sarvaṃ yac cāpi māṃ prati</li>
    </ul>
    <ul id="R_6.113.014">
        <li id="R_6.113.014ab">jñeyāḥ sarve ca vṛttāntā bharatasyeṅgitāni ca</li>
        <li id="R_6.113.014cd">tattvena mukhavarṇena dṛṣṭyā vyābhāṣaṇena ca</li>
    </ul>
    <ul id="R_6.113.015">
        <li id="R_6.113.015ab">sarvakāmasamṛddhaṃ hi hastyaśvarathasaṃkulam</li>
        <li id="R_6.113.015cd">pitṛpaitāmahaṃ rājyaṃ kasya nāvartayen manaḥ</li>
    </ul>
    <ul id="R_6.113.016">
        <li id="R_6.113.016ab">saṃgatyā bharataḥ śrīmān rājyenārthī svayaṃ bhavet</li>
        <li id="R_6.113.016cd">praśāstu vasudhāṃ sarvām akhilāṃ raghunandanaḥ</li>
    </ul>
    <ul id="R_6.113.017">
        <li id="R_6.113.017ab">tasya buddhiṃ ca vijñāya vyavasāyaṃ ca vānara</li>
        <li id="R_6.113.017cd">yāvan na dūraṃ yātāḥ smaḥ kṣipram āgantum arhasi</li>
    </ul>
    <ul id="R_6.113.018">
        <li id="R_6.113.018ab">iti pratisamādiṣṭo hanūmān mārutātmajaḥ</li>
        <li id="R_6.113.018cd">mānuṣaṃ dhārayan rūpam ayodhyāṃ tvarito yayau</li>
    </ul>
    <ul id="R_6.113.019">
        <li id="R_6.113.019ab">laṅghayitvā pitṛpathaṃ bhujagendrālayaṃ śubham</li>
        <li id="R_6.113.019cd">gaṅgāyamunayor bhīmaṃ saṃnipātam atītya ca</li>
    </ul>
    <ul id="R_6.113.020">
        <li id="R_6.113.020ab">śṛṅgaverapuraṃ prāpya guham āsādya vīryavān</li>
        <li id="R_6.113.020cd">sa vācā śubhayā hṛṣṭo hanūmān idam abravīt</li>
    </ul>
    <ul id="R_6.113.021">
        <li id="R_6.113.021ab">sakhā tu tava kākutstho rāmaḥ satyaparākramaḥ</li>
        <li id="R_6.113.021cd">sasītaḥ saha saumitriḥ sa tvāṃ kuśalam abravīt</li>
    </ul>
    <ul id="R_6.113.022">
        <li id="R_6.113.022ab">pañcamīm adya rajanīm uṣitvā vacanān muneḥ</li>
        <li id="R_6.113.022cd">bharadvājābhyanujñātaṃ drakṣyasy adyaiva rāghavam</li>
    </ul>
    <ul id="R_6.113.023">
        <li id="R_6.113.023ab">evam uktvā mahātejāḥ saṃprahṛṣṭatanūruhaḥ</li>
        <li id="R_6.113.023cd">utpapāta mahāvego vegavān avicārayan</li>
    </ul>
    <ul id="R_6.113.024">
        <li id="R_6.113.024ab">so 'paśyad rāmatīrthaṃ ca nadīṃ vālukinīṃ tathā</li>
        <li id="R_6.113.024cd">gomatīṃ tāṃ ca so 'paśyad bhīmaṃ sālavanaṃ tathā</li>
    </ul>
    <ul id="R_6.113.025">
        <li id="R_6.113.025ab">sa gatvā dūram adhvānaṃ tvaritaḥ kapikuñjaraḥ</li>
        <li id="R_6.113.025cd">āsasāda drumān phullān nandigrāmasamīpajān</li>
    </ul>
    <ul id="R_6.113.026">
        <li id="R_6.113.026ab">krośamātre tv ayodhyāyāś cīrakṛṣṇājināmbaram</li>
        <li id="R_6.113.026cd">dadarśa bharataṃ dīnaṃ kṛśam āśramavāsinam</li>
    </ul>
    <ul id="R_6.113.027">
        <li id="R_6.113.027ab">jaṭilaṃ maladigdhāṅgaṃ bhrātṛvyasanakarśitam</li>
        <li id="R_6.113.027cd">phalamūlāśinaṃ dāntaṃ tāpasaṃ dharmacāriṇam</li>
    </ul>
    <ul id="R_6.113.028">
        <li id="R_6.113.028ab">samunnatajaṭābhāraṃ valkalājinavāsasaṃ</li>
        <li id="R_6.113.028cd">niyataṃ bhāvitātmānaṃ brahmarṣisamatejasaṃ</li>
    </ul>
    <ul id="R_6.113.029">
        <li id="R_6.113.029ab">pāduke te puraskṛtya śāsantaṃ vai vasuṃdharām</li>
        <li id="R_6.113.029cd">caturvarṇyasya lokasya trātāraṃ sarvato bhayāt</li>
    </ul>
    <ul id="R_6.113.030">
        <li id="R_6.113.030ab">upasthitam amātyaiś ca śucibhiś ca purohitaiḥ</li>
        <li id="R_6.113.030cd">balamukhyaiś ca yuktaiś ca kāṣāyāmbaradhāribhiḥ</li>
    </ul>
    <ul id="R_6.113.031">
        <li id="R_6.113.031ab">na hi te rājaputraṃ taṃ cīrakṛṣṇājināmbaram</li>
        <li id="R_6.113.031cd">parimoktuṃ vyavasyanti paurā vai dharmavatsalāḥ</li>
    </ul>
    <ul id="R_6.113.032">
        <li id="R_6.113.032ab">taṃ dharmam iva dharmajñaṃ devavantam ivāparam</li>
        <li id="R_6.113.032cd">uvāca prāñjalir vākyaṃ hanūmān mārutātmajaḥ</li>
    </ul>
    <ul id="R_6.113.033">
        <li id="R_6.113.033ab">vasantaṃ daṇḍakāraṇye yaṃ tvaṃ cīrajaṭādharam</li>
        <li id="R_6.113.033cd">anuśocasi kākutsthaṃ sa tvā kuśalam abravīt</li>
    </ul>
    <ul id="R_6.113.034">
        <li id="R_6.113.034ab">priyam ākhyāmi te deva śokaṃ tyakṣyasi dāruṇam</li>
        <li id="R_6.113.034cd">asmin muhūrte bhrātrā tvaṃ rāmeṇa saha saṃgataḥ</li>
    </ul>
    <ul id="R_6.113.035">
        <li id="R_6.113.035ab">nihatya rāvaṇaṃ rāmaḥ pratilabhya ca maithilīm</li>
        <li id="R_6.113.035cd">upayāti samṛddhārthaḥ saha mitrair mahābalaiḥ</li>
    </ul>
    <ul id="R_6.113.036">
        <li id="R_6.113.036ab">lakṣmaṇaś ca mahātejā vaidehī ca yaśasvinī</li>
        <li id="R_6.113.036cd">sītā samagrā rāmeṇa mahendreṇa śacī yathā</li>
    </ul>
    <ul id="R_6.113.037">
        <li id="R_6.113.037ab">evam ukto hanumatā bharataḥ kaikayīsutaḥ</li>
        <li id="R_6.113.037cd">papāta sahasā hṛṣṭo harṣān mohaṃ jagāma ha</li>
    </ul>
    <ul id="R_6.113.038">
        <li id="R_6.113.038ab">tato muhūrtād utthāya pratyāśvasya ca rāghavaḥ</li>
        <li id="R_6.113.038cd">hanūmantam uvācedaṃ bharataḥ priyavādinam</li>
    </ul>
    <ul id="R_6.113.039">
        <li id="R_6.113.039ab">aśokajaiḥ prītimayaiḥ kapim āliṅgya saṃbhramāt</li>
        <li id="R_6.113.039cd">siṣeca bharataḥ śrīmān vipulair aśrubindubhiḥ</li>
    </ul>
    <ul id="R_6.113.040">
        <li id="R_6.113.040ab">devo vā mānuṣo vā tvam anukrośād ihāgataḥ</li>
        <li id="R_6.113.040cd">priyākhyānasya te saumya dadāmi bruvataḥ priyam</li>
    </ul>
    <ul id="R_6.113.041">
        <li id="R_6.113.041ab">gavāṃ śatasahasraṃ ca grāmāṇāṃ ca śataṃ param</li>
        <li id="R_6.113.041cd">sakuṇḍalāḥ śubhācārā bhāryāḥ kanyāś ca ṣoḍaśa</li>
    </ul>
    <ul id="R_6.113.042">
        <li id="R_6.113.042ab">hemavarṇāḥ sunāsorūḥ śaśisaumyānanāḥ striyaḥ</li>
        <li id="R_6.113.042cd">sarvābharaṇasaṃpannā saṃpannāḥ kulajātibhiḥ</li>
    </ul>
    <ul id="R_6.113.043">
        <li id="R_6.113.043ab">
            <seg type="pāda" n="a">niśamya rāmāgamanaṃ nṛpātmajaḥ</seg>
            <seg type="pāda" n="b">kapipravīrasya tadādbhutopamam</seg>
        </li>
        <li id="R_6.113.043cd">
            <seg type="pāda" n="c">praharṣito rāmadidṛkṣayābhavat</seg>
            <seg type="pāda" n="d">punaś ca harṣād idam abravīd vacaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.114.001">
        <li id="R_6.114.001ab">bahūni nāma varṣāṇi gatasya sumahad vanam</li>
        <li id="R_6.114.001cd">śṛṇomy ahaṃ prītikaraṃ mama nāthasya kīrtanam</li>
    </ul>
    <ul id="R_6.114.002">
        <li id="R_6.114.002ab">kalyāṇī bata gātheyaṃ laukikī pratibhāti me</li>
        <li id="R_6.114.002cd">eti jīvantam ānando naraṃ varṣaśatād api</li>
    </ul>
    <ul id="R_6.114.003">
        <li id="R_6.114.003ab">rāghavasya harīṇāṃ ca katham āsīt samāgamaḥ</li>
        <li id="R_6.114.003cd">kasmin deśe kim āśritya tat tvam ākhyāhi pṛcchataḥ</li>
    </ul>
    <ul id="R_6.114.004">
        <li id="R_6.114.004ab">sa pṛṣṭo rājaputreṇa bṛsyāṃ samupaveśitaḥ</li>
        <li id="R_6.114.004cd">ācacakṣe tataḥ sarvaṃ rāmasya caritaṃ vane</li>
    </ul>
    <ul id="R_6.114.005">
        <li id="R_6.114.005ab">yathā pravrajito rāmo mātur datte vare tava</li>
        <li id="R_6.114.005cd">yathā ca putraśokena rājā daśaratho mṛtaḥ</li>
    </ul>
    <ul id="R_6.114.006">
        <li id="R_6.114.006ab">yathā dūtais tvam ānītas tūrṇaṃ rājagṛhāt prabho</li>
        <li id="R_6.114.006cd">tvayāyodhyāṃ praviṣṭena yathā rājyaṃ na cepsitam</li>
    </ul>
    <ul id="R_6.114.007">
        <li id="R_6.114.007ab">citrakūṭaṃ giriṃ gatvā rājyenāmitrakarśanaḥ</li>
        <li id="R_6.114.007cd">nimantritas tvayā bhrātā dharmam ācaritā satām</li>
    </ul>
    <ul id="R_6.114.008">
        <li id="R_6.114.008ab">sthitena rājño vacane yathā rājyaṃ visarjitam</li>
        <li id="R_6.114.008cd">āryasya pāduke gṛhya yathāsi punar āgataḥ</li>
    </ul>
    <ul id="R_6.114.009">
        <li id="R_6.114.009ab">sarvam etan mahābāho yathāvad viditaṃ tava</li>
        <li id="R_6.114.009cd">tvayi pratiprayāte tu yad vṛttaṃ tan nibodha me</li>
    </ul>
    <ul id="R_6.114.010">
        <li id="R_6.114.010ab">apayāte tvayi tadā samudbhrāntamṛgadvijam</li>
        <li id="R_6.114.010cd">praviveśātha vijanaṃ sumahad daṇḍakāvanam</li>
    </ul>
    <ul id="R_6.114.011">
        <li id="R_6.114.011ab">teṣāṃ purastād balavān gacchatāṃ gahane vane</li>
        <li id="R_6.114.011cd">vinadan sumahānādaṃ virādhaḥ pratyadṛśyata</li>
    </ul>
    <ul id="R_6.114.012">
        <li id="R_6.114.012ab">tam utkṣipya mahānādam ūrdhvabāhum adhomukham</li>
        <li id="R_6.114.012cd">nikhāte prakṣipanti sma nadantam iva kuñjaram</li>
    </ul>
    <ul id="R_6.114.013">
        <li id="R_6.114.013ab">tat kṛtvā duṣkaraṃ karma bhrātarau rāmalakṣmaṇau</li>
        <li id="R_6.114.013cd">sāyāhne śarabhaṅgasya ramyam āśramam īyatuḥ</li>
    </ul>
    <ul id="R_6.114.014">
        <li id="R_6.114.014ab">śarabhaṅge divaṃ prāpte rāmaḥ satyaparākramaḥ</li>
        <li id="R_6.114.014cd">abhivādya munīn sarvāñ janasthānam upāgamat</li>
    </ul>
    <ul id="R_6.114.015">
        <li id="R_6.114.015ab">caturdaśasahasrāṇi rakṣasāṃ bhīmakarmaṇām</li>
        <li id="R_6.114.015cd">hatāni vasatā tatra rāghaveṇa mahātmanā</li>
    </ul>
    <ul id="R_6.114.016">
        <li id="R_6.114.016ab">tataḥ paścāc chūrpaṇakhā rāmapārśvam upāgatā</li>
        <li id="R_6.114.016cd">tato rāmeṇa saṃdiṣṭo lakṣmaṇaḥ sahasotthitaḥ</li>
    </ul>
    <ul id="R_6.114.017">
        <li id="R_6.114.017ab">pragṛhya khaḍgaṃ ciccheda karṇanāse mahābalaḥ</li>
        <li id="R_6.114.017cd">tatas tenārditā bālā rāvaṇaṃ samupāgatā</li>
    </ul>
    <ul id="R_6.114.018">
        <li id="R_6.114.018ab">rāvaṇānucaro ghoro mārīco nāma rākṣasaḥ</li>
        <li id="R_6.114.018cd">lobhayām āsa vaidehīṃ bhūtvā ratnamayo mṛgaḥ</li>
    </ul>
    <ul id="R_6.114.019">
        <li id="R_6.114.019ab">sā rāmam abravīd dṛṣṭvā vaidehī gṛhyatām iti</li>
        <li id="R_6.114.019cd">aho manoharaḥ kānta āśrame no bhaviṣyati</li>
    </ul>
    <ul id="R_6.114.020">
        <li id="R_6.114.020ab">tato rāmo dhanuṣpāṇir dhāvantam anudhāvati</li>
        <li id="R_6.114.020cd">sa taṃ jaghāna dhāvantaṃ śareṇānataparvaṇā</li>
    </ul>
    <ul id="R_6.114.021">
        <li id="R_6.114.021ab">atha saumyā daśagrīvo mṛgaṃ yāte tu rāghave</li>
        <li id="R_6.114.021cd">lakṣmaṇe cāpi niṣkrānte praviveśāśramaṃ tadā</li>
        <li id="R_6.114.021ef">jagrāha tarasā sītāṃ grahaḥ khe rohiṇīm iva</li>
    </ul>
    <ul id="R_6.114.022">
        <li id="R_6.114.022ab">trātukāmaṃ tato yuddhe hatvā gṛdhraṃ jaṭāyuṣam</li>
        <li id="R_6.114.022cd">pragṛhya sītāṃ sahasā jagāmāśu sa rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.114.023">
        <li id="R_6.114.023ab">tatas tv adbhutasaṃkāśāḥ sthitāḥ parvatamūrdhani</li>
        <li id="R_6.114.023cd">sītāṃ gṛhītvā gacchantaṃ vānarāḥ parvatopamāḥ</li>
        <li id="R_6.114.023ef">dadṛśur vismitās tatra rāvaṇaṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_6.114.024">
        <li id="R_6.114.024ab">praviverśa tadā laṅkāṃ rāvaṇo lokarāvaṇaḥ</li>
    </ul>
    <ul id="R_6.114.025">
        <li id="R_6.114.025ab">tāṃ suvarṇaparikrānte śubhe mahati veśmani</li>
        <li id="R_6.114.025cd">praveśya maithilīṃ vākyaiḥ sāntvayām āsa rāvaṇaḥ</li>
    </ul>
    <ul id="R_6.114.026">
        <li id="R_6.114.026ab">nivartamānaḥ kākutstho dṛṣṭvā gṛdhraṃ pravivyathe</li>
    </ul>
    <ul id="R_6.114.027">
        <li id="R_6.114.027ab">gṛdhraṃ hataṃ tadā dagdhvā rāmaḥ priyasakhaṃ pituḥ</li>
        <li id="R_6.114.027cd">godāvarīm anucaran vanoddeśāṃś ca puṣpitān</li>
        <li id="R_6.114.027ef">āsedatur mahāraṇye kabandhaṃ nāma rākṣasaṃ</li>
    </ul>
    <ul id="R_6.114.028">
        <li id="R_6.114.028ab">tataḥ kabandhavacanād rāmaḥ satyaparākramaḥ</li>
        <li id="R_6.114.028cd">ṛśyamūkaṃ giriṃ gatvā sugrīveṇa samāgataḥ</li>
    </ul>
    <ul id="R_6.114.029">
        <li id="R_6.114.029ab">tayoḥ samāgamaḥ pūrvaṃ prītyā hārdo vyajāyata</li>
        <li id="R_6.114.029cd">itaretara saṃvādāt pragāḍhaḥ praṇayas tayoḥ</li>
    </ul>
    <ul id="R_6.114.030">
        <li id="R_6.114.030ab">rāmaḥ svabāhuvīryeṇa svarājyaṃ pratyapādayat</li>
        <li id="R_6.114.030cd">vālinaṃ samare hatvā mahākāyaṃ mahābalam</li>
    </ul>
    <ul id="R_6.114.031">
        <li id="R_6.114.031ab">sugrīvaḥ sthāpito rājye sahitaḥ sarvavānaraiḥ</li>
        <li id="R_6.114.031cd">rāmāya pratijānīte rājaputryās tu mārgaṇam</li>
    </ul>
    <ul id="R_6.114.032">
        <li id="R_6.114.032ab">ādiṣṭā vānarendreṇa sugrīveṇa mahātmanā</li>
        <li id="R_6.114.032cd">daśakoṭyaḥ plavaṃgānāṃ sarvāḥ prasthāpitā diśaḥ</li>
    </ul>
    <ul id="R_6.114.033">
        <li id="R_6.114.033ab">teṣāṃ no vipranaṣṭānāṃ vindhye parvatasattame</li>
        <li id="R_6.114.033cd">bhṛśaṃ śokābhitaptānāṃ mahān kālo 'tyavartata</li>
    </ul>
    <ul id="R_6.114.034">
        <li id="R_6.114.034ab">bhrātā tu gṛdhrarājasya saṃpātir nāma vīryavān</li>
        <li id="R_6.114.034cd">samākhyāti sma vasatiṃ sītāyā rāvaṇālaye</li>
    </ul>
    <ul id="R_6.114.035">
        <li id="R_6.114.035ab">so 'haṃ duḥkhaparītānāṃ duḥkhaṃ tajjñātināṃ nudan</li>
        <li id="R_6.114.035cd">ātmavīryaṃ samāsthāya yojanānāṃ śataṃ plutaḥ</li>
    </ul>
    <ul id="R_6.114.036">
        <li id="R_6.114.036ab">tatrāham ekām adrākṣam aśokavanikāṃ gatām</li>
        <li id="R_6.114.036cd">kauśeyavastrāṃ malināṃ nirānandāṃ dṛḍhavratām</li>
    </ul>
    <ul id="R_6.114.037">
        <li id="R_6.114.037ab">tayā sametya vidhivat pṛṣṭvā sarvam aninditām</li>
        <li id="R_6.114.037cd">abhijñānaṃ maṇiṃ labdhvā caritārtho 'ham āgataḥ</li>
    </ul>
    <ul id="R_6.114.038">
        <li id="R_6.114.038ab">mayā ca punar āgamya rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_6.114.038cd">abhijñānaṃ mayā dattam arciṣmān sa mahāmaṇiḥ</li>
    </ul>
    <ul id="R_6.114.039">
        <li id="R_6.114.039ab">śrutvā tāṃ maithilīṃ hṛṣṭas tv āśaśaṃse sa jīvitam</li>
        <li id="R_6.114.039cd">jīvitāntam anuprāptaḥ pītvāmṛtam ivāturaḥ</li>
    </ul>
    <ul id="R_6.114.040">
        <li id="R_6.114.040ab">udyojayiṣyann udyogaṃ dadhre laṅkāvadhe manaḥ</li>
        <li id="R_6.114.040cd">jighāṃsur iva lokāṃs te sarvāṃl lokān vibhāvasuḥ</li>
    </ul>
    <ul id="R_6.114.041">
        <li id="R_6.114.041ab">tataḥ samudram āsādya nalaṃ setum akārayat</li>
        <li id="R_6.114.041cd">atarat kapivīrāṇāṃ vāhinī tena setunā</li>
    </ul>
    <ul id="R_6.114.042">
        <li id="R_6.114.042ab">prahastam avadhīn nīlaḥ kumbhakarṇaṃ tu rāghavaḥ</li>
        <li id="R_6.114.042cd">lakṣmaṇo rāvaṇasutaṃ svayaṃ rāmas tu rāvaṇam</li>
    </ul>
    <ul id="R_6.114.043">
        <li id="R_6.114.043ab">sa śakreṇa samāgamya yamena varuṇena ca</li>
        <li id="R_6.114.043cd">surarṣibhiś ca kākutstho varāṃl lebhe paraṃtapaḥ</li>
    </ul>
    <ul id="R_6.114.044">
        <li id="R_6.114.044ab">sa tu dattavaraḥ prītyā vānaraiś ca samāgataḥ</li>
        <li id="R_6.114.044cd">puṣpakeṇa vimānena kiṣkindhām abhyupāgamat</li>
    </ul>
    <ul id="R_6.114.045">
        <li id="R_6.114.045ab">taṃ gaṅgāṃ punar āsādya vasantaṃ munisaṃnidhau</li>
        <li id="R_6.114.045cd">avighnaṃ puṣyayogena śvo rāmaṃ draṣṭum arhasi</li>
    </ul>
    <ul id="R_6.114.046">
        <li id="R_6.114.046ab">
            <seg type="pāda" n="a">tataḥ sa satyaṃ hanumadvaco mahan</seg>
            <seg type="pāda" n="b">niśamya hṛṣṭo bharataḥ kṛtāñjaliḥ</seg>
        </li>
        <li id="R_6.114.046cd">
            <seg type="pāda" n="c">uvāca vāṇīṃ manasaḥ praharṣiṇī</seg>
            <seg type="pāda" n="d">cirasya pūrṇaḥ khalu me manorathaḥ</seg>
        </li>
    </ul>
    <ul id="R_6.115.001">
        <li id="R_6.115.001ab">śrutvā tu param ānandaṃ bharataḥ satyavikramaḥ</li>
        <li id="R_6.115.001cd">hṛṣṭam ājñāpayām āsa śatrughnaṃ paravīrahā</li>
    </ul>
    <ul id="R_6.115.002">
        <li id="R_6.115.002ab">daivatāni ca sarvāṇi caityāni nagarasya ca</li>
        <li id="R_6.115.002cd">sugandhamālyair vāditrair arcantu śucayo narāḥ</li>
    </ul>
    <ul id="R_6.115.003">
        <li id="R_6.115.003ab">rājadārās tathāmātyāḥ sainyāḥ senāgaṇāṅganāḥ</li>
        <li id="R_6.115.003cd">abhiniryāntu rāmasya draṣṭuṃ śaśinibhaṃ mukham</li>
    </ul>
    <ul id="R_6.115.004">
        <li id="R_6.115.004ab">bharatasya vacaḥ śrutvā śatrughnaḥ paravīrahā</li>
        <li id="R_6.115.004cd">viṣṭīr anekasāhasrīś codayām āsa vīryavān</li>
    </ul>
    <ul id="R_6.115.005">
        <li id="R_6.115.005ab">samīkuruta nimnāni viṣamāṇi samāni ca</li>
        <li id="R_6.115.005cd">sthānāni ca nirasyantāṃ nandigrāmād itaḥ param</li>
    </ul>
    <ul id="R_6.115.006">
        <li id="R_6.115.006ab">siñcantu pṛthivīṃ kṛtsnāṃ himaśītena vāriṇā</li>
        <li id="R_6.115.006cd">tato 'bhyavakiraṃs tv anye lājaiḥ puṣpaiś ca sarvataḥ</li>
    </ul>
    <ul id="R_6.115.007">
        <li id="R_6.115.007ab">samucchritapatākās tu rathyāḥ puravarottame</li>
        <li id="R_6.115.007cd">śobhayantu ca veśmāni sūryasyodayanaṃ prati</li>
    </ul>
    <ul id="R_6.115.008">
        <li id="R_6.115.008ab">sragdāmamuktapuṣpaiś ca sugandhaiḥ pañcavarṇakaiḥ</li>
        <li id="R_6.115.008cd">rājamārgam asaṃbādhaṃ kirantu śataśo narāḥ</li>
    </ul>
    <ul id="R_6.115.009">
        <li id="R_6.115.009ab">mattair nāgasahasraiś ca śātakumbhavibhūṣitaḥ</li>
        <li id="R_6.115.009cd">apare hemakakṣyābhiḥ sagajābhiḥ kareṇubhiḥ</li>
        <li id="R_6.115.009ef">niryayus tvarayā yuktā rathaiś ca sumahārathāḥ</li>
    </ul>
    <ul id="R_6.115.010">
        <li id="R_6.115.010ab">tato yānāny upārūḍhāḥ sarvā daśarathastriyaḥ</li>
        <li id="R_6.115.010cd">kausalyāṃ pramukhe kṛtvā sumitrāṃ cāpi niryayuḥ</li>
    </ul>
    <ul id="R_6.115.011">
        <li id="R_6.115.011ab">aśvānāṃ khuraśabdena rathanemisvanena ca</li>
        <li id="R_6.115.011cd">śaṅkhadundubhinādena saṃcacāleva medinī</li>
    </ul>
    <ul id="R_6.115.012">
        <li id="R_6.115.012ab">kṛtsnaṃ ca nagaraṃ tat tu nandigrāmam upāgamat</li>
        <li id="R_6.115.012cd">dvijātimukhyair dharmātmā śreṇīmukhyaiḥ sanaigamaiḥ</li>
    </ul>
    <ul id="R_6.115.013">
        <li id="R_6.115.013ab">mālyamodaka hastaiś ca mantribhir bharato vṛtaḥ</li>
        <li id="R_6.115.013cd">śaṅkhabherīninādaiś ca bandibhiś cābhivanditaḥ</li>
    </ul>
    <ul id="R_6.115.014">
        <li id="R_6.115.014ab">āryapādau gṛhītvā tu śirasā dharmakovidaḥ</li>
        <li id="R_6.115.014cd">pāṇḍuraṃ chatram ādāya śuklamālyopaśobhitam</li>
    </ul>
    <ul id="R_6.115.015">
        <li id="R_6.115.015ab">śukle ca vālavyajane rājārhe hemabhūṣite</li>
        <li id="R_6.115.015cd">upavāsakṛśo dīnaś cīrakṛṣṇājināmbaraḥ</li>
    </ul>
    <ul id="R_6.115.016">
        <li id="R_6.115.016ab">bhrātur āgamanaṃ śrutvā tat pūrvaṃ harṣam āgataḥ</li>
        <li id="R_6.115.016cd">pratyudyayau tadā rāmaṃ mahātmā sacivaiḥ saha</li>
    </ul>
    <ul id="R_6.115.017">
        <li id="R_6.115.017ab">samīkṣya bharato vākyam uvāca pavanātmajam</li>
        <li id="R_6.115.017cd">kaccin na khalu kāpeyī sevyate calacittatā</li>
        <li id="R_6.115.017ef">na hi paśyāmi kākutsthaṃ rāmam āryaṃ paraṃtapam</li>
    </ul>
    <ul id="R_6.115.018">
        <li id="R_6.115.018ab">athaivam ukte vacane hanūmān idam abravīt</li>
        <li id="R_6.115.018cd">arthaṃ vijñāpayann eva bharataṃ satyavikramam</li>
    </ul>
    <ul id="R_6.115.019">
        <li id="R_6.115.019ab">sadā phalān kusumitān vṛkṣān prāpya madhusravān</li>
        <li id="R_6.115.019cd">bharadvājaprasādena mattabhramaranāditān</li>
    </ul>
    <ul id="R_6.115.020">
        <li id="R_6.115.020ab">tasya caiṣa varo datto vāsavena paraṃtapa</li>
        <li id="R_6.115.020cd">sasainyasya tadātithyaṃ kṛtaṃ sarvaguṇānvitam</li>
    </ul>
    <ul id="R_6.115.021">
        <li id="R_6.115.021ab">nisvanaḥ śrūyate bhīmaḥ prahṛṣṭānāṃ vanaukasām</li>
        <li id="R_6.115.021cd">manye vānarasenā sā nadīṃ tarati gomatīm</li>
    </ul>
    <ul id="R_6.115.022">
        <li id="R_6.115.022ab">rajovarṣaṃ samudbhūtaṃ paśya vālukinīṃ prati</li>
        <li id="R_6.115.022cd">manye sālavanaṃ ramyaṃ lolayanti plavaṃgamāḥ</li>
    </ul>
    <ul id="R_6.115.023">
        <li id="R_6.115.023ab">tad etad dṛśyate dūrād vimalaṃ candrasaṃnibham</li>
        <li id="R_6.115.023cd">vimānaṃ puṣpakaṃ divyaṃ manasā brahmanirmitam</li>
    </ul>
    <ul id="R_6.115.024">
        <li id="R_6.115.024ab">rāvaṇaṃ bāndhavaiḥ sārdhaṃ hatvā labdhaṃ mahātmanā</li>
        <li id="R_6.115.024cd">dhanadasya prasādena divyam etan manojavam</li>
    </ul>
    <ul id="R_6.115.025">
        <li id="R_6.115.025ab">etasmin bhrātarau vīrau vaidehyā saha rāghavau</li>
        <li id="R_6.115.025cd">sugrīvaś ca mahātejā rākṣasendro vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.115.026">
        <li id="R_6.115.026ab">tato harṣasamudbhūto nisvano divam aspṛśat</li>
        <li id="R_6.115.026cd">strībālayuvavṛddhānāṃ rāmo 'yam iti kīrtitaḥ</li>
    </ul>
    <ul id="R_6.115.027">
        <li id="R_6.115.027ab">rathakuñjaravājibhyas te 'vatīrya mahīṃ gatāḥ</li>
        <li id="R_6.115.027cd">dadṛśus taṃ vimānasthaṃ narāḥ somam ivāmbare</li>
    </ul>
    <ul id="R_6.115.028">
        <li id="R_6.115.028ab">prāñjalir bharato bhūtvā prahṛṣṭo rāghavonmukhaḥ</li>
        <li id="R_6.115.028cd">svāgatena yathārthena tato rāmam apūjayat</li>
    </ul>
    <ul id="R_6.115.029">
        <li id="R_6.115.029ab">manasā brahmaṇā sṛṣṭe vimāne lakṣmaṇāgrajaḥ</li>
        <li id="R_6.115.029cd">rarāja pṛthudīrghākṣo vajrapāṇir ivāparaḥ</li>
    </ul>
    <ul id="R_6.115.030">
        <li id="R_6.115.030ab">tato vimānāgragataṃ bharato bhrātaraṃ tadā</li>
        <li id="R_6.115.030cd">vavande praṇato rāmaṃ merustham iva bhāskaram</li>
    </ul>
    <ul id="R_6.115.031">
        <li id="R_6.115.031ab">āropito vimānaṃ tad bharataḥ satyavikramaḥ</li>
        <li id="R_6.115.031cd">rāmam āsādya muditaḥ punar evābhyavādayat</li>
    </ul>
    <ul id="R_6.115.032">
        <li id="R_6.115.032ab">taṃ samutthāpya kākutsthaś cirasyākṣipathaṃ gatam</li>
        <li id="R_6.115.032cd">aṅke bharatam āropya muditaḥ pariṣaṣvaje</li>
    </ul>
    <ul id="R_6.115.033">
        <li id="R_6.115.033ab">tato lakṣmaṇam āsādya vaidehīṃ ca paraṃtapaḥ</li>
        <li id="R_6.115.033cd">abhyavādayata prīto bharato nāma cābravīt</li>
    </ul>
    <ul id="R_6.115.034">
        <li id="R_6.115.034ab">sugrīvaṃ kaikayī putro jāmbavantaṃ tathāṅgadam</li>
        <li id="R_6.115.034cd">maindaṃ ca dvividaṃ nīlam ṛṣabhaṃ caiva sasvaje</li>
    </ul>
    <ul id="R_6.115.035">
        <li id="R_6.115.035ab">te kṛtvā mānuṣaṃ rūpaṃ vānarāḥ kāmarūpiṇaḥ</li>
        <li id="R_6.115.035cd">kuśalaṃ paryapṛcchanta prahṛṣṭā bharataṃ tadā</li>
    </ul>
    <ul id="R_6.115.036">
        <li id="R_6.115.036ab">vibhīṣaṇaṃ ca bharataḥ sāntvayan vākyam abravīt</li>
        <li id="R_6.115.036cd">diṣṭyā tvayā sahāyena kṛtaṃ karma suduṣkaram</li>
    </ul>
    <ul id="R_6.115.037">
        <li id="R_6.115.037ab">śatrughnaś ca tadā rāmam abhivādya salakṣmaṇam</li>
        <li id="R_6.115.037cd">sītāyāś caraṇau paścād vavande vinayānvitaḥ</li>
    </ul>
    <ul id="R_6.115.038">
        <li id="R_6.115.038ab">rāmo mātaram āsādya viṣaṇṇaṃ śokakarśitām</li>
        <li id="R_6.115.038cd">jagrāha praṇataḥ pādau mano mātuḥ prasādayan</li>
    </ul>
    <ul id="R_6.115.039">
        <li id="R_6.115.039ab">abhivādya sumitrāṃ ca kaikeyīṃ ca yaśasvinīm</li>
        <li id="R_6.115.039cd">sa mātṝś ca tadā sarvāḥ purohitam upāgamat</li>
    </ul>
    <ul id="R_6.115.040">
        <li id="R_6.115.040ab">svāgataṃ te mahābāho kausalyānandavardhana</li>
        <li id="R_6.115.040cd">iti prāñjalayaḥ sarve nāgarā rāmam abruvan</li>
    </ul>
    <ul id="R_6.115.041">
        <li id="R_6.115.041ab">tany añjalisahasrāṇi pragṛhītāni nāgaraiḥ</li>
        <li id="R_6.115.041cd">ākośānīva padmāni dadarśa bharatāgrajaḥ</li>
    </ul>
    <ul id="R_6.115.042">
        <li id="R_6.115.042ab">pāduke te tu rāmasya gṛhītvā bharataḥ svayam</li>
        <li id="R_6.115.042cd">caraṇābhyāṃ narendrasya yojayām āsa dharmavit</li>
    </ul>
    <ul id="R_6.115.043">
        <li id="R_6.115.043ab">abravīc ca tadā rāmaṃ bharataḥ sa kṛtāñjaliḥ</li>
        <li id="R_6.115.043cd">etat te rakṣitaṃ rājan rājyaṃ niryātitaṃ mayā</li>
    </ul>
    <ul id="R_6.115.044">
        <li id="R_6.115.044ab">adya janma kṛtārthaṃ me saṃvṛttaś ca manorathaḥ</li>
        <li id="R_6.115.044cd">yas tvāṃ paśyāmi rājānam ayodhyāṃ punar āgatam</li>
    </ul>
    <ul id="R_6.115.045">
        <li id="R_6.115.045ab">avekṣatāṃ bhavān kośaṃ koṣṭhāgāraṃ puraṃ balam</li>
        <li id="R_6.115.045cd">bhavatas tejasā sarvaṃ kṛtaṃ daśaguṇaṃ mayā</li>
    </ul>
    <ul id="R_6.115.046">
        <li id="R_6.115.046ab">tathā bruvāṇaṃ bharataṃ dṛṣṭvā taṃ bhrātṛvatsalam</li>
        <li id="R_6.115.046cd">mumucur vānarā bāṣpaṃ rākṣasaś ca vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.115.047">
        <li id="R_6.115.047ab">tataḥ praharṣād bharatam aṅkam āropya rāghavaḥ</li>
        <li id="R_6.115.047cd">yayau tena vimānena sasainyo bharatāśramam</li>
    </ul>
    <ul id="R_6.115.048">
        <li id="R_6.115.048ab">bharatāśramam āsādya sasainyo rāghavas tadā</li>
        <li id="R_6.115.048cd">avatīrya vimānāgrād avatasthe mahītale</li>
    </ul>
    <ul id="R_6.115.049">
        <li id="R_6.115.049ab">abravīc ca tadā rāmas tadvimānam anuttamam</li>
        <li id="R_6.115.049cd">vaha vaiśravaṇaṃ devam anujānāmi gamyatām</li>
    </ul>
    <ul id="R_6.115.050">
        <li id="R_6.115.050ab">tato rāmābhyanujñātaṃ tadvimānam anuttamam</li>
        <li id="R_6.115.050cd">uttarāṃ diśam uddiśya jagāma dhanadālayam</li>
    </ul>
    <ul id="R_6.115.051">
        <li id="R_6.115.051ab">
            <seg type="pāda" n="a">purohitasyātmasamasya rāghavo</seg>
            <seg type="pāda" n="b">bṛhaspateḥ śakra ivāmarādhipaḥ</seg>
        </li>
        <li id="R_6.115.051cd">
            <seg type="pāda" n="c">nipīḍya pādau pṛthag āsane śubhe</seg>
            <seg type="pāda" n="d">sahaiva tenopaviveśa vīryavān</seg>
        </li>
    </ul>
    <ul id="R_6.116.001">
        <li id="R_6.116.001ab">śirasy añjalim ādāya kaikeyīnandivardhanaḥ</li>
        <li id="R_6.116.001cd">babhāṣe bharato jyeṣṭhaṃ rāmaṃ satyaparākramam</li>
    </ul>
    <ul id="R_6.116.002">
        <li id="R_6.116.002ab">pūjitā māmikā mātā dattaṃ rājyam idaṃ mama</li>
        <li id="R_6.116.002cd">tad dadāmi punas tubhyaṃ yathā tvam adadā mama</li>
    </ul>
    <ul id="R_6.116.003">
        <li id="R_6.116.003ab">dhuram ekākinā nyastām ṛṣabheṇa balīyasā</li>
        <li id="R_6.116.003cd">kiśoravad guruṃ bhāraṃ na voḍhum aham utsahe</li>
    </ul>
    <ul id="R_6.116.004">
        <li id="R_6.116.004ab">vārivegena mahatā bhinnaḥ setur iva kṣaran</li>
        <li id="R_6.116.004cd">durbandhanam idaṃ manye rājyacchidram asaṃvṛtam</li>
    </ul>
    <ul id="R_6.116.005">
        <li id="R_6.116.005ab">gatiṃ khara ivāśvasya haṃsasyeva ca vāyasaḥ</li>
        <li id="R_6.116.005cd">nānvetum utsahe deva tava mārgam ariṃdama</li>
    </ul>
    <ul id="R_6.116.006">
        <li id="R_6.116.006ab">yathā ca ropito vṛkṣo jātaś cāntarniveśane</li>
        <li id="R_6.116.006cd">mahāṃś ca sudurāroho mahāskandhaḥ praśākhavān</li>
    </ul>
    <ul id="R_6.116.007">
        <li id="R_6.116.007ab">śīryeta puṣpito bhūtvā na phalāni pradarśayet</li>
        <li id="R_6.116.007cd">tasya nānubhaved arthaṃ yasya hetoḥ sa ropyate</li>
    </ul>
    <ul id="R_6.116.008">
        <li id="R_6.116.008ab">eṣopamā mahābāho tvam arthaṃ vettum arhasi</li>
        <li id="R_6.116.008cd">yady asmān manujendra tvaṃ bhaktān bhṛtyān na śādhi hi</li>
    </ul>
    <ul id="R_6.116.009">
        <li id="R_6.116.009ab">jagad adyābhiṣiktaṃ tvām anupaśyatu sarvataḥ</li>
        <li id="R_6.116.009cd">pratapantam ivādityaṃ madhyāhne dīptatejasaṃ</li>
    </ul>
    <ul id="R_6.116.010">
        <li id="R_6.116.010ab">tūryasaṃghātanirghoṣaiḥ kāñcīnūpuranisvanaiḥ</li>
        <li id="R_6.116.010cd">madhurair gītaśabdaiś ca pratibudhyasva śeṣva ca</li>
    </ul>
    <ul id="R_6.116.011">
        <li id="R_6.116.011ab">yāvad āvartate cakraṃ yāvatī ca vasuṃdharā</li>
        <li id="R_6.116.011cd">tāvat tvam iha sarvasya svāmitvam abhivartaya</li>
    </ul>
    <ul id="R_6.116.012">
        <li id="R_6.116.012ab">bharatasya vacaḥ śrutvā rāmaḥ parapuraṃjayaḥ</li>
        <li id="R_6.116.012cd">tatheti pratijagrāha niṣasādāsane śubhe</li>
    </ul>
    <ul id="R_6.116.013">
        <li id="R_6.116.013ab">tataḥ śatrughnavacanān nipuṇāḥ śmaśruvardhakāḥ</li>
        <li id="R_6.116.013cd">sukhahastāḥ suśīghrāś ca rāghavaṃ paryupāsata</li>
    </ul>
    <ul id="R_6.116.014">
        <li id="R_6.116.014ab">pūrvaṃ tu bharate snāte lakṣmaṇe ca mahābale</li>
        <li id="R_6.116.014cd">sugrīve vānarendre ca rākṣasendre vibhīṣaṇe</li>
    </ul>
    <ul id="R_6.116.015">
        <li id="R_6.116.015ab">viśodhitajaṭaḥ snātaś citramālyānulepanaḥ</li>
        <li id="R_6.116.015cd">mahārhavasanopetas tasthau tatra śriyā jvalan</li>
    </ul>
    <ul id="R_6.116.016">
        <li id="R_6.116.016ab">pratikarma ca rāmasya kārayām āsa vīryavān</li>
        <li id="R_6.116.016cd">lakṣmaṇasya ca lakṣmīvān ikṣvākukulavardhanaḥ</li>
    </ul>
    <ul id="R_6.116.017">
        <li id="R_6.116.017ab">pratikarma ca sītāyāḥ sarvā daśarathastriyaḥ</li>
        <li id="R_6.116.017cd">ātmanaiva tadā cakrur manasvinyo manoharam</li>
    </ul>
    <ul id="R_6.116.018">
        <li id="R_6.116.018ab">tato rāghavapatnīnāṃ sarvāsām eva śobhanam</li>
        <li id="R_6.116.018cd">cakāra yatnāt kausalyā prahṛṣṭā putravatsalā</li>
    </ul>
    <ul id="R_6.116.019">
        <li id="R_6.116.019ab">tataḥ śatrughnavacanāt sumantro nāma sārathiḥ</li>
        <li id="R_6.116.019cd">yojayitvābhicakrāma rathaṃ sarvāṅgaśobhanam</li>
    </ul>
    <ul id="R_6.116.020">
        <li id="R_6.116.020ab">arkamaṇḍalasaṃkāśaṃ divyaṃ dṛṣṭvā rathaṃ sthitam</li>
        <li id="R_6.116.020cd">āruroha mahābāhū rāmaḥ satyaparākramaḥ</li>
    </ul>
    <ul id="R_6.116.021">
        <li id="R_6.116.021ab">ayodhyāyāṃ tu sacivā rājño daśarathasya ye</li>
        <li id="R_6.116.021cd">purohitaṃ puraskṛtya mantrayām āsur arthavat</li>
    </ul>
    <ul id="R_6.116.022">
        <li id="R_6.116.022ab">mantrayan rāmavṛddhyarthaṃ vṛttyarthaṃ nagarasya ca</li>
        <li id="R_6.116.022cd">sarvam evābhiṣekārthaṃ jayārhasya mahātmanaḥ</li>
        <li id="R_6.116.022ef">kartum arhatha rāmasya yad yan maṅgalapūrvakam</li>
    </ul>
    <ul id="R_6.116.023">
        <li id="R_6.116.023ab">iti te mantriṇaḥ sarve saṃdiśya tu purohitam</li>
        <li id="R_6.116.023cd">nagarān niryayus tūrṇaṃ rāmadarśanabuddhayaḥ</li>
    </ul>
    <ul id="R_6.116.024">
        <li id="R_6.116.024ab">hariyuktaṃ sahasrākṣo ratham indra ivānaghaḥ</li>
        <li id="R_6.116.024cd">prayayau ratham āsthāya rāmo nagaram uttamam</li>
    </ul>
    <ul id="R_6.116.025">
        <li id="R_6.116.025ab">jagrāha bharato raśmīñ śatrughnaś chatram ādade</li>
        <li id="R_6.116.025cd">lakṣmaṇo vyajanaṃ tasya mūrdhni saṃparyavījayat</li>
    </ul>
    <ul id="R_6.116.026">
        <li id="R_6.116.026ab">śvetaṃ ca vālavyajanaṃ sugrīvo vānareśvaraḥ</li>
        <li id="R_6.116.026cd">aparaṃ candrasaṃkāśaṃ rākṣasendro vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.116.027">
        <li id="R_6.116.027ab">ṛṣisaṃghair tadākāśe devaiś ca samarudgaṇaiḥ</li>
        <li id="R_6.116.027cd">stūyamānasya rāmasya śuśruve madhuradhvaniḥ</li>
    </ul>
    <ul id="R_6.116.028">
        <li id="R_6.116.028ab">tataḥ śatruṃjayaṃ nāma kuñjaraṃ parvatopamam</li>
        <li id="R_6.116.028cd">āruroha mahātejāḥ sugrīvo vānareśvaraḥ</li>
    </ul>
    <ul id="R_6.116.029">
        <li id="R_6.116.029ab">navanāgasahasrāṇi yayur āsthāya vānarāḥ</li>
        <li id="R_6.116.029cd">mānuṣaṃ vigrahaṃ kṛtvā sarvābharaṇabhūṣitāḥ</li>
    </ul>
    <ul id="R_6.116.030">
        <li id="R_6.116.030ab">śaṅkhaśabdapraṇādaiś ca dundubhīnāṃ ca nisvanaiḥ</li>
        <li id="R_6.116.030cd">prayayū puruṣavyāghras tāṃ purīṃ harmyamālinīm</li>
    </ul>
    <ul id="R_6.116.031">
        <li id="R_6.116.031ab">dadṛśus te samāyāntaṃ rāghavaṃ sapuraḥsaram</li>
        <li id="R_6.116.031cd">virājamānaṃ vapuṣā rathenātirathaṃ tadā</li>
    </ul>
    <ul id="R_6.116.032">
        <li id="R_6.116.032ab">te vardhayitvā kākutsthaṃ rāmeṇa pratinanditāḥ</li>
        <li id="R_6.116.032cd">anujagmur mahātmānaṃ bhrātṛbhiḥ parivāritam</li>
    </ul>
    <ul id="R_6.116.033">
        <li id="R_6.116.033ab">amātyair brāhmaṇaiś caiva tathā prakṛtibhir vṛtaḥ</li>
        <li id="R_6.116.033cd">śriyā viruruce rāmo nakṣatrair iva candramāḥ</li>
    </ul>
    <ul id="R_6.116.034">
        <li id="R_6.116.034ab">sa purogāmibhis tūryais tālasvastikapāṇibhiḥ</li>
        <li id="R_6.116.034cd">pravyāharadbhir muditair maṅgalāni yayau vṛtaḥ</li>
    </ul>
    <ul id="R_6.116.035">
        <li id="R_6.116.035ab">akṣataṃ jātarūpaṃ ca gāvaḥ kanyās tathā dvijāḥ</li>
        <li id="R_6.116.035cd">narā modakahastāś ca rāmasya purato yayuḥ</li>
    </ul>
    <ul id="R_6.116.036">
        <li id="R_6.116.036ab">sakhyaṃ ca rāmaḥ sugrīve prabhāvaṃ cānilātmaje</li>
        <li id="R_6.116.036cd">vānarāṇāṃ ca tat karma vyācacakṣe 'tha mantriṇām</li>
        <li id="R_6.116.036ef">śrutvā ca vismayaṃ jagmur ayodhyāpuravāsinaḥ</li>
    </ul>
    <ul id="R_6.116.037">
        <li id="R_6.116.037ab">dyutimān etad ākhyāya rāmo vānarasaṃvṛtaḥ</li>
        <li id="R_6.116.037cd">hṛṣṭapuṣṭajanākīrṇām ayodhyāṃ praviveśa ha</li>
    </ul>
    <ul id="R_6.116.038">
        <li id="R_6.116.038ab">tato hy abhyucchrayan paurāḥ patākās te gṛhe gṛhe</li>
        <li id="R_6.116.038cd">aikṣvākādhyuṣitaṃ ramyam āsasāda pitur gṛham</li>
    </ul>
    <ul id="R_6.116.039">
        <li id="R_6.116.039ab">pitur bhavanam āsādya praviśya ca mahātmanaḥ</li>
        <li id="R_6.116.039cd">kausalyāṃ ca sumitrāṃ ca kaikeyīṃ cābhyavādayat</li>
    </ul>
    <ul id="R_6.116.040">
        <li id="R_6.116.040ab">athābravīd rājaputro bharataṃ dharmiṇāṃ varam</li>
        <li id="R_6.116.040cd">athopahitayā vācā madhuraṃ raghunandanaḥ</li>
    </ul>
    <ul id="R_6.116.041">
        <li id="R_6.116.041ab">yac ca madbhavanaṃ śreṣṭhaṃ sāśokavanikaṃ mahat</li>
        <li id="R_6.116.041cd">muktāvaidūryasaṃkīrṇaṃ sugrīvasya nivedaya</li>
    </ul>
    <ul id="R_6.116.042">
        <li id="R_6.116.042ab">tasya tad vacanaṃ śrutvā bharataḥ satyavikramaḥ</li>
        <li id="R_6.116.042cd">pāṇau gṛhītvā sugrīvaṃ praviveśa tam ālayam</li>
    </ul>
    <ul id="R_6.116.043">
        <li id="R_6.116.043ab">tatas tailapradīpāṃś ca paryaṅkāstaraṇāni ca</li>
        <li id="R_6.116.043cd">gṛhītvā viviśuḥ kṣipraṃ śatrughnena pracoditāḥ</li>
    </ul>
    <ul id="R_6.116.044">
        <li id="R_6.116.044ab">uvāca ca mahātejāḥ sugrīvaṃ rāghavānujaḥ</li>
        <li id="R_6.116.044cd">abhiṣekāya rāmasya dūtān ājñāpaya prabho</li>
    </ul>
    <ul id="R_6.116.045">
        <li id="R_6.116.045ab">sauvarṇān vānarendrāṇāṃ caturṇāṃ caturo ghaṭān</li>
        <li id="R_6.116.045cd">dadau kṣipraṃ sa sugrīvaḥ sarvaratnavibhūṣitān</li>
    </ul>
    <ul id="R_6.116.046">
        <li id="R_6.116.046ab">yathā pratyūṣasamaye caturṇāṃ sāgarāmbhasām</li>
        <li id="R_6.116.046cd">pūrṇair ghaṭaiḥ pratīkṣadhvaṃ tathā kuruta vānarāḥ</li>
    </ul>
    <ul id="R_6.116.047">
        <li id="R_6.116.047ab">evam uktā mahātmāno vānarā vāraṇopamāḥ</li>
        <li id="R_6.116.047cd">utpetur gaganaṃ śīghraṃ garuḍā iva śīghragāḥ</li>
    </ul>
    <ul id="R_6.116.048">
        <li id="R_6.116.048ab">jāmbavāṃś ca hanūmāṃś ca vegadarśī ca vānaraḥ</li>
        <li id="R_6.116.048cd">ṛṣabhaś caiva kalaśāñ jalapūrṇān athānayan</li>
        <li id="R_6.116.048ef">nadīśatānāṃ pañcānāṃ jale kumbhair upāharan</li>
    </ul>
    <ul id="R_6.116.049">
        <li id="R_6.116.049ab">pūrvāt samudrāt kalaśaṃ jalapūrṇam athānayat</li>
        <li id="R_6.116.049cd">suṣeṇaḥ sattvasaṃpannaḥ sarvaratnavibhūṣitam</li>
    </ul>
    <ul id="R_6.116.050">
        <li id="R_6.116.050ab">ṛṣabho dakṣiṇāt tūrṇaṃ samudrāj jalam āharat</li>
    </ul>
    <ul id="R_6.116.051">
        <li id="R_6.116.051ab">raktacandanakarpūraiḥ saṃvṛtaṃ kāñcanaṃ ghaṭam</li>
        <li id="R_6.116.051cd">gavayaḥ paścimāt toyam ājahāra mahārṇavāt</li>
    </ul>
    <ul id="R_6.116.052">
        <li id="R_6.116.052ab">ratnakumbhena mahatā śītaṃ mārutavikramaḥ</li>
        <li id="R_6.116.052cd">uttarāc ca jalaṃ śīghraṃ garuḍānilavikramaḥ</li>
    </ul>
    <ul id="R_6.116.053">
        <li id="R_6.116.053ab">abhiṣekāya rāmasya śatrughnaḥ sacivaiḥ saha</li>
        <li id="R_6.116.053cd">purohitāya śreṣṭhāya suhṛdbhyaś ca nyavedayat</li>
    </ul>
    <ul id="R_6.116.054">
        <li id="R_6.116.054ab">tataḥ sa prayato vṛddho vasiṣṭho brāhmaṇaiḥ saha</li>
        <li id="R_6.116.054cd">rāmaṃ ratnamayo pīṭhe sahasītaṃ nyaveśayat</li>
    </ul>
    <ul id="R_6.116.055">
        <li id="R_6.116.055ab">vasiṣṭho vāmadevaś ca jābālir atha kāśyapaḥ</li>
        <li id="R_6.116.055cd">kātyāyanaḥ suyajñaś ca gautamo vijayas tathā</li>
    </ul>
    <ul id="R_6.116.056">
        <li id="R_6.116.056ab">abhyaṣiñcan naravyāghraṃ prasannena sugandhinā</li>
        <li id="R_6.116.056cd">salilena sahasrākṣaṃ vasavo vāsavaṃ yathā</li>
    </ul>
    <ul id="R_6.116.057">
        <li id="R_6.116.057ab">ṛtvigbhir brāhmaṇaiḥ pūrvaṃ kanyābhir mantribhis tathā</li>
        <li id="R_6.116.057cd">yodhaiś caivābhyaṣiñcaṃs te saṃprahṛṣṭāḥ sanaigamaiḥ</li>
    </ul>
    <ul id="R_6.116.058">
        <li id="R_6.116.058ab">sarvauṣadhirasaiś cāpi daivatair nabhasi sthitaiḥ</li>
        <li id="R_6.116.058cd">caturhir lokapālaiś ca sarvair devaiś ca saṃgataiḥ</li>
    </ul>
    <ul id="R_6.116.059">
        <li id="R_6.116.059ab">chatraṃ tasya ca jagrāha śatrughnaḥ pāṇḍuraṃ śubham</li>
        <li id="R_6.116.059cd">śvetaṃ ca vālavyajanaṃ sugrīvo vānareśvaraḥ</li>
        <li id="R_6.116.059ef">aparaṃ candrasaṃkāśaṃ rākṣasendro vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_6.116.060">
        <li id="R_6.116.060ab">mālāṃ jvalantīṃ vapuṣā kāñcanīṃ śatapuṣkarām</li>
        <li id="R_6.116.060cd">rāghavāya dadau vāyur vāsavena pracoditaḥ</li>
    </ul>
    <ul id="R_6.116.061">
        <li id="R_6.116.061ab">sarvaratnasamāyuktaṃ maṇiratnavibhūṣitam</li>
        <li id="R_6.116.061cd">muktāhāraṃ narendrāya dadau śakrapracoditaḥ</li>
    </ul>
    <ul id="R_6.116.062">
        <li id="R_6.116.062ab">prajagur devagandharvā nanṛtuś cāpsaro gaṇāḥ</li>
        <li id="R_6.116.062cd">abhiṣeke tad arhasya tadā rāmasya dhīmataḥ</li>
    </ul>
    <ul id="R_6.116.063">
        <li id="R_6.116.063ab">bhūmiḥ sasyavatī caiva phalavantaś ca pādapāḥ</li>
        <li id="R_6.116.063cd">gandhavanti ca puṣpāṇi babhūvū rāghavotsave</li>
    </ul>
    <ul id="R_6.116.064">
        <li id="R_6.116.064ab">sahasraśatam aśvānāṃ dhenūnāṃ ca gavāṃ tathā</li>
        <li id="R_6.116.064cd">dadau śataṃ vṛṣān pūrvaṃ dvijebhyo manujarṣabhaḥ</li>
    </ul>
    <ul id="R_6.116.065">
        <li id="R_6.116.065ab">triṃśatkoṭīr hiraṇyasya brāhmaṇebhyo dadau punaḥ</li>
        <li id="R_6.116.065cd">nānābharaṇavastrāṇi mahārhāṇi ca rāghavaḥ</li>
    </ul>
    <ul id="R_6.116.066">
        <li id="R_6.116.066ab">arkaraśmipratīkāśāṃ kāñcanīṃ maṇivigrahām</li>
        <li id="R_6.116.066cd">sugrīvāya srajaṃ divyāṃ prāyacchan manujarṣabhaḥ</li>
    </ul>
    <ul id="R_6.116.067">
        <li id="R_6.116.067ab">vaidūryamaṇicitre ca vajraratnavibhūṣite</li>
        <li id="R_6.116.067cd">vāliputrāya dhṛtimān aṅgadāyāṅgade dadau</li>
    </ul>
    <ul id="R_6.116.068">
        <li id="R_6.116.068ab">maṇipravarajuṣṭaṃ ca muktāhāram anuttamam</li>
        <li id="R_6.116.068cd">sītāyai pradadau rāmaś candraraśmisamaprabham</li>
    </ul>
    <ul id="R_6.116.069">
        <li id="R_6.116.069ab">araje vāsasī divye śubhāny ābharaṇāni ca</li>
        <li id="R_6.116.069cd">avekṣamāṇā vaidehī pradadau vāyusūnave</li>
    </ul>
    <ul id="R_6.116.070">
        <li id="R_6.116.070ab">avamucyātmanaḥ kaṇṭhād dhāraṃ janakanandinī</li>
        <li id="R_6.116.070cd">avaikṣata harīn sarvān bhartāraṃ ca muhur muhuḥ</li>
    </ul>
    <ul id="R_6.116.071">
        <li id="R_6.116.071ab">tām iṅgitajñaḥ saṃprekṣya babhāṣe janakātmajām</li>
        <li id="R_6.116.071cd">pradehi subhage hāraṃ yasya tuṣṭāsi bhāmini</li>
    </ul>
    <ul id="R_6.116.072">
        <li id="R_6.116.072ab">pauruṣaṃ vikramo buddhir yasminn etāni nityadā</li>
        <li id="R_6.116.072cd">dadau sā vāyuputrāya taṃ hāram asitekṣaṇā</li>
    </ul>
    <ul id="R_6.116.073">
        <li id="R_6.116.073ab">hanūmāṃs tena hāreṇa śuśubhe vānararṣabhaḥ</li>
        <li id="R_6.116.073cd">candrāṃśucayagaureṇa śvetābhreṇa yathācalaḥ</li>
    </ul>
    <ul id="R_6.116.074">
        <li id="R_6.116.074ab">tato dvivida maindābhyāṃ nīlāya ca paraṃtapaḥ</li>
        <li id="R_6.116.074cd">sarvān kāmaguṇān vīkṣya pradadau vasudhādhipaḥ</li>
    </ul>
    <ul id="R_6.116.075">
        <li id="R_6.116.075ab">sarvavānaravṛddhāś ca ye cānye vānareśvarāḥ</li>
        <li id="R_6.116.075cd">vāsobhir bhūṣaṇaiś caiva yathārhaṃ pratipūjitāḥ</li>
    </ul>
    <ul id="R_6.116.076">
        <li id="R_6.116.076ab">yathārhaṃ pūjitāḥ sarve kāmai ratnaiś ca puṣkalair</li>
        <li id="R_6.116.076cd">prahṛṣṭamanasaḥ sarve jagmur eva yathāgatam</li>
    </ul>
    <ul id="R_6.116.077">
        <li id="R_6.116.077ab">rāghavaḥ paramodāraḥ śaśāsa parayā mudā</li>
        <li id="R_6.116.077cd">uvāca lakṣmaṇaṃ rāmo dharmajñaṃ dharmavatsalaḥ</li>
    </ul>
    <ul id="R_6.116.078">
        <li id="R_6.116.078ab">
            <seg type="pāda" n="a">ātiṣṭha dharmajña mayā sahemāṃ</seg>
            <seg type="pāda" n="b">gāṃ pūrvarājādhyuṣitāṃ balena</seg>
        </li>
        <li id="R_6.116.078cd">
            <seg type="pāda" n="c">tulyaṃ mayā tvaṃ pitṛbhir dhṛtā yā</seg>
            <seg type="pāda" n="d">tāṃ yauvarājye dhuram udvahasva</seg>
        </li>
    </ul>
    <ul id="R_6.116.079">
        <li id="R_6.116.079ab">
            <seg type="pāda" n="a">sarvātmanā paryanunīyamāno</seg>
            <seg type="pāda" n="b">yadā na saumitrir upaiti yogam</seg>
        </li>
        <li id="R_6.116.079cd">
            <seg type="pāda" n="c">niyujyamāno bhuvi yauvarājye</seg>
            <seg type="pāda" n="d">tato 'bhyaṣiñcad bharataṃ mahātmā</seg>
        </li>
    </ul>
    <ul id="R_6.116.080">
        <li id="R_6.116.080ab">rāghavaś cāpi dharmātmā prāpya rājyam anuttamam</li>
        <li id="R_6.116.080cd">īje bahuvidhair yajñaiḥ sasuhṛdbhrātṛbāndhavaḥ</li>
    </ul>
    <ul id="R_6.116.081">
        <li id="R_6.116.081ab">pauṇḍarīkāśvamedhābhyāṃ vājapeyena cāsakṛt</li>
        <li id="R_6.116.081cd">anyaiś ca vividhair yajñair ayajat pārthivarṣabhaḥ</li>
    </ul>
    <ul id="R_6.116.082">
        <li id="R_6.116.082ab">rājyaṃ daśasahasrāṇi prāpya varṣāṇi rāghavaḥ</li>
        <li id="R_6.116.082cd">śatāśvamedhān ājahre sadaśvān bhūridakṣiṇān</li>
    </ul>
    <ul id="R_6.116.083">
        <li id="R_6.116.083ab">ājānulambibāhuś ca mahāskandhaḥ pratāpavān</li>
        <li id="R_6.116.083cd">lakṣmaṇānucaro rāmaḥ pṛthivīm anvapālayat</li>
    </ul>
    <ul id="R_6.116.084">
        <li id="R_6.116.084ab">na paryadevan vidhavā na ca vyālakṛtaṃ bhayam</li>
        <li id="R_6.116.084cd">na vyādhijaṃ bhayaṃ vāpi rāme rājyaṃ praśāsati</li>
    </ul>
    <ul id="R_6.116.085">
        <li id="R_6.116.085ab">nirdasyur abhaval loko nānarthaḥ kaṃ cid aspṛśat</li>
        <li id="R_6.116.085cd">na ca sma vṛddhā bālānāṃ pretakāryāṇi kurvate</li>
    </ul>
    <ul id="R_6.116.086">
        <li id="R_6.116.086ab">sarvaṃ muditam evāsīt sarvo dharmaparo 'bhavat</li>
        <li id="R_6.116.086cd">rāmam evānupaśyanto nābhyahiṃsan parasparam</li>
    </ul>
    <ul id="R_6.116.087">
        <li id="R_6.116.087ab">āsan varṣasahasrāṇi tathā putrasahasriṇaḥ</li>
        <li id="R_6.116.087cd">nirāmayā viśokāś ca rāme rājyaṃ praśāsati</li>
    </ul>
    <ul id="R_6.116.088">
        <li id="R_6.116.088ab">nityapuṣpā nityaphalās taravaḥ skandhavistṛtāḥ</li>
        <li id="R_6.116.088cd">kālavarṣī ca parjanyaḥ sukhasparśaś ca mārutaḥ</li>
    </ul>
    <ul id="R_6.116.089">
        <li id="R_6.116.089ab">svakarmasu pravartante tuṣṭhāḥ svair eva karmabhiḥ</li>
        <li id="R_6.116.089cd">āsan prajā dharmaparā rāme śāsati nānṛtāḥ</li>
    </ul>
    <ul id="R_6.116.090">
        <li id="R_6.116.090ab">sarve lakṣaṇasaṃpannāḥ sarve dharmaparāyaṇāḥ</li>
        <li id="R_6.116.090cd">daśavarṣasahasrāṇi rāmo rājyam akārayat</li>
    </ul>
	{/if}
</div>
<div class="kanda-7">
<h4>Kāṇḍa 7</h4>
		{#if kanda7}
    <ul id="R_7.001.001">
        <li id="R_7.001.001ab">prāptarājyasya rāmasya rākṣasānāṃ vadhe kṛte</li>
        <li id="R_7.001.001cd">ājagmur ṛṣayaḥ sarve rāghavaṃ pratinanditum</li>
    </ul>
    <ul id="R_7.001.002">
        <li id="R_7.001.002ab">kauśiko 'tha yavakrīto raibhyaś cyavana eva ca</li>
        <li id="R_7.001.002cd">kaṇvo medhātitheḥ putraḥ pūrvasyāṃ diśi ye śritāḥ</li>
    </ul>
    <ul id="R_7.001.003">
        <li id="R_7.001.003ab">svastyātreyaś ca bhagavān namuciḥ pramucus tathā</li>
        <li id="R_7.001.003cd">ājagmus te sahāgastyā ye śritā dakṣiṇāṃ diśam</li>
    </ul>
    <ul id="R_7.001.004">
        <li id="R_7.001.004ab">pṛṣadguḥ kavaṣo dhaumyo raudreyaś ca mahān ṛṣiḥ</li>
        <li id="R_7.001.004cd">te 'py ājagmuḥ saśiṣyā vai ye śritāḥ paścimāṃ diśam</li>
    </ul>
    <ul id="R_7.001.005">
        <li id="R_7.001.005ab">vasiṣṭhaḥ kaśyapo 'thātrir viśvāmitro 'tha gautamaḥ</li>
        <li id="R_7.001.005cd">jamadagnir bharadvājas te 'pi saptamaharṣayaḥ</li>
    </ul>
    <ul id="R_7.001.006">
        <li id="R_7.001.006ab">saṃprāpyaite mahātmāno rāghavasya niveśanam</li>
        <li id="R_7.001.006cd">viṣṭhitāḥ pratihārārthaṃ hutāśanasamaprabhāḥ</li>
    </ul>
    <ul id="R_7.001.007">
        <li id="R_7.001.007ab">pratihāras tatas tūrṇam agastyavacanād atha</li>
        <li id="R_7.001.007cd">samīpaṃ rāghavasyāśu praviveśa mahātmanaḥ</li>
    </ul>
    <ul id="R_7.001.008">
        <li id="R_7.001.008ab">sa rāmaṃ dṛśya sahasā pūrṇacandrasamadyutim</li>
        <li id="R_7.001.008cd">agastyaṃ kathayām āsa saṃprātam ṛṣibhiḥ saha</li>
    </ul>
    <ul id="R_7.001.009">
        <li id="R_7.001.009ab">śrutvā prāptān munīṃs tāṃs tu bālasūryasamaprabhān</li>
        <li id="R_7.001.009cd">tadovāca nṛpo dvāḥsthaṃ praveśaya yathāsukham</li>
    </ul>
    <ul id="R_7.001.010">
        <li id="R_7.001.010ab">dṛṣṭvā prāptān munīṃs tāṃs tu pratyutthāya kṛtāñjaliḥ</li>
        <li id="R_7.001.010cd">rāmo 'bhivādya prayata āsanāny ādideśa ha</li>
    </ul>
    <ul id="R_7.001.011">
        <li id="R_7.001.011ab">teṣu kāñcanacitreṣu svāstīrṇeṣu sukheṣu ca</li>
        <li id="R_7.001.011cd">yathārham upaviṣṭās te āsaneṣv ṛṣipuṃgavāḥ</li>
    </ul>
    <ul id="R_7.001.012">
        <li id="R_7.001.012ab">rāmeṇa kuśalaṃ pṛṣṭāḥ saśiṣyāḥ sapurogamāḥ</li>
        <li id="R_7.001.012cd">maharṣayo vedavido rāmaṃ vacanam abruvan</li>
    </ul>
    <ul id="R_7.001.013">
        <li id="R_7.001.013ab">kuśalaṃ no mahābāho sarvatra raghunandana</li>
        <li id="R_7.001.013cd">tvāṃ tu diṣṭyā kuśalinaṃ paśyāmo hataśātravam</li>
    </ul>
    <ul id="R_7.001.014">
        <li id="R_7.001.014ab">na hi bhāraḥ sa te rāma rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_7.001.014cd">sadhanus tvaṃ hi lokāṃs trīn vijayethā na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.001.015">
        <li id="R_7.001.015ab">diṣṭyā tvayā hato rāma rāvaṇaḥ putrapautravān</li>
        <li id="R_7.001.015cd">diṣṭyā vijayinaṃ tvādya paśyāmaḥ saha bhāryayā</li>
    </ul>
    <ul id="R_7.001.016">
        <li id="R_7.001.016ab">diṣṭyā prahasto vikaṭo virūpākṣo mahodaraḥ</li>
        <li id="R_7.001.016cd">akampanaś ca durdharṣo nihatās te niśācarāḥ</li>
    </ul>
    <ul id="R_7.001.017">
        <li id="R_7.001.017ab">yasya pramāṇād vipulaṃ pramāṇaṃ neha vidyate</li>
        <li id="R_7.001.017cd">diṣṭyā te samare rāma kumbhakarṇo nipātitaḥ</li>
    </ul>
    <ul id="R_7.001.018">
        <li id="R_7.001.018ab">diṣṭyā tvaṃ rākṣasendreṇa dvandvayuddham upāgataḥ</li>
        <li id="R_7.001.018cd">devatānām avadhyena vijayaṃ prāptavān asi</li>
    </ul>
    <ul id="R_7.001.019">
        <li id="R_7.001.019ab">saṃkhye tasya na kiṃ cit tu rāvaṇasya parābhavaḥ</li>
        <li id="R_7.001.019cd">dvandvayuddham anuprāpto diṣṭyā te rāvaṇir hataḥ</li>
    </ul>
    <ul id="R_7.001.020">
        <li id="R_7.001.020ab">diṣṭyā tasya mahābāho kālasyevābhidhāvataḥ</li>
        <li id="R_7.001.020cd">muktaḥ suraripor vīra prāptaś ca vijayas tvayā</li>
    </ul>
    <ul id="R_7.001.021">
        <li id="R_7.001.021ab">vismayas tv eṣa naḥ saumya saṃśrutyendrajitaṃ hatam</li>
        <li id="R_7.001.021cd">avadhyaḥ sarvabhūtānāṃ mahāmāyādharo yudhi</li>
    </ul>
    <ul id="R_7.001.022">
        <li id="R_7.001.022ab">dattvā puṇyām imāṃ vīra saumyām abhayadakṣiṇām</li>
        <li id="R_7.001.022cd">diṣṭyā vardhasi kākutstha jayenāmitrakarśana</li>
    </ul>
    <ul id="R_7.001.023">
        <li id="R_7.001.023ab">śrutvā tu vacanaṃ teṣām ṛṣīṇāṃ bhāvitātmanām</li>
        <li id="R_7.001.023cd">vismayaṃ paramaṃ gatvā rāmaḥ prāñjalir abravīt</li>
    </ul>
    <ul id="R_7.001.024">
        <li id="R_7.001.024ab">bhavantaḥ kumbhakarṇaṃ ca rāvaṇaṃ ca niśācaram</li>
        <li id="R_7.001.024cd">atikramya mahāvīryau kiṃ praśaṃsatha rāvaṇim</li>
    </ul>
    <ul id="R_7.001.025">
        <li id="R_7.001.025ab">mahodaraṃ prahastaṃ ca virūpākṣaṃ ca rākṣasaṃ</li>
        <li id="R_7.001.025cd">atikramya mahāvīryān kiṃ praśaṃsatha rāvaṇim</li>
    </ul>
    <ul id="R_7.001.026">
        <li id="R_7.001.026ab">kīdṛśo vai prabhāvo 'sya kiṃ balaṃ kaḥ parākramaḥ</li>
        <li id="R_7.001.026cd">kena vā kāraṇenaiṣa rāvaṇād atiricyate</li>
    </ul>
    <ul id="R_7.001.027">
        <li id="R_7.001.027ab">śakyaṃ yadi mayā śrotuṃ na khalv ājñāpayāmi vaḥ</li>
        <li id="R_7.001.027cd">yadi guhyaṃ na ced vaktuṃ śrotum icchāmi kathyatām</li>
        <li id="R_7.001.027ef">kathaṃ śakro jitas tena kathaṃ labdhavaraś ca saḥ</li>
    </ul>
    <ul id="R_7.002.001">
        <li id="R_7.002.001ab">tasya tad vacanaṃ śrutvā rāghavasya mahātmanaḥ</li>
        <li id="R_7.002.001cd">kumbhayonir mahātejā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.002.002">
        <li id="R_7.002.002ab">śṛṇu rājan yathāvṛttaṃ yasya tejobalaṃ mahat</li>
        <li id="R_7.002.002cd">jaghāna ca ripūn yuddhe yathāvadhyaś ca śatrubhiḥ</li>
    </ul>
    <ul id="R_7.002.003">
        <li id="R_7.002.003ab">ahaṃ te rāvaṇasyedaṃ kulaṃ janma ca rāghava</li>
        <li id="R_7.002.003cd">varapradānaṃ ca tathā tasmai dattaṃ bravīmi te</li>
    </ul>
    <ul id="R_7.002.004">
        <li id="R_7.002.004ab">purā kṛtayuge rāma prajāpatisutaḥ prabhuḥ</li>
        <li id="R_7.002.004cd">pulastyo nāma brahmarṣiḥ sākṣād iva pitāmahaḥ</li>
    </ul>
    <ul id="R_7.002.005">
        <li id="R_7.002.005ab">nānukīrtyā guṇās tasya dharmataḥ śīlatas tathā</li>
        <li id="R_7.002.005cd">prajāpateḥ putra iti vaktuṃ śakyaṃ hi nāmataḥ</li>
    </ul>
    <ul id="R_7.002.006">
        <li id="R_7.002.006ab">sa tu dharmaprasaṅgena meroḥ pārśve mahāgireḥ</li>
        <li id="R_7.002.006cd">tṛṇabindvāśramaṃ gatvā nyavasan munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.002.007">
        <li id="R_7.002.007ab">tapas tepe sa dharmātmā svādhyāyaniyatendriyaḥ</li>
        <li id="R_7.002.007cd">gatvāśramapadaṃ tasya vighnaṃ kurvanti kanyakāḥ</li>
    </ul>
    <ul id="R_7.002.008">
        <li id="R_7.002.008ab">devapannagakanyāś ca rājarṣitanayāś ca yāḥ</li>
        <li id="R_7.002.008cd">krīḍantyo 'psarasaś caiva taṃ deśam upapedire</li>
    </ul>
    <ul id="R_7.002.009">
        <li id="R_7.002.009ab">sarvartuṣūpabhogyatvād ramyatvāt kānanasya ca</li>
        <li id="R_7.002.009cd">nityaśas tās tu taṃ deśaṃ gatvā krīḍanti kanyakāḥ</li>
    </ul>
    <ul id="R_7.002.010">
        <li id="R_7.002.010ab">atha ruṣṭo mahātejā vyājahāra mahāmuniḥ</li>
        <li id="R_7.002.010cd">yā me darśanam āgacchet sā garbhaṃ dhārayiṣyati</li>
    </ul>
    <ul id="R_7.002.011">
        <li id="R_7.002.011ab">tās tu sarvāḥ pratigatāḥ śrutvā vākyaṃ mahātmanaḥ</li>
        <li id="R_7.002.011cd">brahmaśāpabhayād bhītās taṃ deśaṃ nopacakramuḥ</li>
    </ul>
    <ul id="R_7.002.012">
        <li id="R_7.002.012ab">tṛṇabindos tu rājarṣes tanayā na śṛṇoti tat</li>
        <li id="R_7.002.012cd">gatvāśramapadaṃ tasya vicacāra sunirbhayā</li>
    </ul>
    <ul id="R_7.002.013">
        <li id="R_7.002.013ab">tasminn eva tu kāle sa prājāpatyo mahān ṛṣiḥ</li>
        <li id="R_7.002.013cd">svādhyāyam akarot tatra tapasā dyotitaprabhaḥ</li>
    </ul>
    <ul id="R_7.002.014">
        <li id="R_7.002.014ab">sā tu vedadhvaniṃ śrutvā dṛṣṭvā caiva tapodhanam</li>
        <li id="R_7.002.014cd">abhavat pāṇḍudehā sā suvyañjitaśarīrajā</li>
    </ul>
    <ul id="R_7.002.015">
        <li id="R_7.002.015ab">dṛṣṭvā paramasaṃvignā sā tu tadrūpam ātmanaḥ</li>
        <li id="R_7.002.015cd">idaṃ me kiṃ nv iti jñātvā pitur gatvāgrataḥ sthitā</li>
    </ul>
    <ul id="R_7.002.016">
        <li id="R_7.002.016ab">tāṃ tu dṛṣṭvā tathā bhūtāṃ tṛṇabindur athābravīt</li>
        <li id="R_7.002.016cd">kiṃ tvam etat tv asadṛśaṃ dhārayasy ātmano vapuḥ</li>
    </ul>
    <ul id="R_7.002.017">
        <li id="R_7.002.017ab">sā tu kṛtvāñjaliṃ dīnā kanyovāca tapodhanam</li>
        <li id="R_7.002.017cd">na jāne kāraṇaṃ tāta yena me rūpam īdṛśam</li>
    </ul>
    <ul id="R_7.002.018">
        <li id="R_7.002.018ab">kiṃ tu pūrvaṃ gatāsmy ekā maharṣer bhāvitātmanaḥ</li>
        <li id="R_7.002.018cd">pulastyasyāśramaṃ divyam anveṣṭuṃ svasakhījanam</li>
    </ul>
    <ul id="R_7.002.019">
        <li id="R_7.002.019ab">na ca paśyāmy ahaṃ tatra kāṃ cid apy āgatāṃ sakhīm</li>
        <li id="R_7.002.019cd">rūpasya tu viparyāsaṃ dṛṣṭvā cāham ihāgatā</li>
    </ul>
    <ul id="R_7.002.020">
        <li id="R_7.002.020ab">tṛṇabindus tu rājarṣis tapasā dyotitaprabhaḥ</li>
        <li id="R_7.002.020cd">dhyānaṃ viveśa tac cāpi apaśyad ṛṣikarmajam</li>
    </ul>
    <ul id="R_7.002.021">
        <li id="R_7.002.021ab">sa tu vijñāya taṃ śāpaṃ maharṣer bhāvitātmanaḥ</li>
        <li id="R_7.002.021cd">gṛhītvā tanayāṃ gatvā pulastyam idam abravīt</li>
    </ul>
    <ul id="R_7.002.022">
        <li id="R_7.002.022ab">bhagavaṃs tanayāṃ me tvaṃ guṇaiḥ svair eva bhūṣitām</li>
        <li id="R_7.002.022cd">bhikṣāṃ pratigṛhāṇemāṃ maharṣe svayam udyatām</li>
    </ul>
    <ul id="R_7.002.023">
        <li id="R_7.002.023ab">tapaścaraṇayuktasya śrāmyamāṇendriyasya te</li>
        <li id="R_7.002.023cd">śuśrūṣātatparā nityaṃ bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.002.024">
        <li id="R_7.002.024ab">taṃ bruvāṇaṃ tu tadvākyaṃ rājarṣiṃ dhārmikaṃ tadā</li>
        <li id="R_7.002.024cd">jighṛkṣur abravīt kanyāṃ bāḍham ity eva sa dvijaḥ</li>
    </ul>
    <ul id="R_7.002.025">
        <li id="R_7.002.025ab">dattvā tu sa gato rājā svam āśramapadaṃ tadā</li>
        <li id="R_7.002.025cd">sāpi tatrāvasat kanyā toṣayantī patiṃ guṇaiḥ</li>
        <li id="R_7.002.025ef">prītaḥ sa tu mahātejā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.002.026">
        <li id="R_7.002.026ab">parituṣṭo 'smi bhadraṃ te guṇānāṃ saṃpadā bhṛśam</li>
        <li id="R_7.002.026cd">tasmāt te viramāmy adya putram ātmasamaṃ guṇaiḥ</li>
        <li id="R_7.002.026ef">ubhayor vaṃśakartāraṃ paulastya iti viśrutam</li>
    </ul>
    <ul id="R_7.002.027">
        <li id="R_7.002.027ab">yasmāt tu viśruto vedas tvayehābhyasyato mama</li>
        <li id="R_7.002.027cd">tasmāt sa viśravā nāma bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.002.028">
        <li id="R_7.002.028ab">evam uktā tu sā kanyā prahṛṣṭenāntarātmanā</li>
        <li id="R_7.002.028cd">acireṇaiva kālena sūtā viśravasaṃ sutam</li>
    </ul>
    <ul id="R_7.002.029">
        <li id="R_7.002.029ab">sa tu lokatraye khyātaḥ śaucadharmasamanvitaḥ</li>
        <li id="R_7.002.029cd">piteva tapasā yukto viśravā munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.003.001">
        <li id="R_7.003.001ab">atha putraḥ pulastyasya viśravā munipuṃgavaḥ</li>
        <li id="R_7.003.001cd">acireṇaiva kālena piteva tapasi sthitaḥ</li>
    </ul>
    <ul id="R_7.003.002">
        <li id="R_7.003.002ab">satyavāñ śīlavān dakṣaḥ svādhyāyanirataḥ śuciḥ</li>
        <li id="R_7.003.002cd">sarvabhogeṣv asaṃsakto nityaṃ dharmaparāyaṇaḥ</li>
    </ul>
    <ul id="R_7.003.003">
        <li id="R_7.003.003ab">jñātvā tasya tu tadvṛttaṃ bharadvājo mahān ṛṣiḥ</li>
        <li id="R_7.003.003cd">dadau viśravase bhāryāṃ svāṃ sutāṃ devavarṇinīm</li>
    </ul>
    <ul id="R_7.003.004">
        <li id="R_7.003.004ab">pratigṛhya tu dharmeṇa bharadvājasutāṃ tadā</li>
        <li id="R_7.003.004cd">mudā paramayā yukto viśravā munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.003.005">
        <li id="R_7.003.005ab">sa tasyāṃ vīryasaṃpannam apatyaṃ paramādbhutam</li>
        <li id="R_7.003.005cd">janayām āsa dharmātmā sarvair brahmaguṇair yutam</li>
    </ul>
    <ul id="R_7.003.006">
        <li id="R_7.003.006ab">tasmiñ jāte tu saṃhṛṣṭaḥ sa babhūva pitāmahaḥ</li>
        <li id="R_7.003.006cd">nāma cāsyākarot prītaḥ sārdhaṃ devarṣibhis tadā</li>
    </ul>
    <ul id="R_7.003.007">
        <li id="R_7.003.007ab">yasmād viśravaso 'patyaṃ sādṛśyād viśravā iva</li>
        <li id="R_7.003.007cd">tasmād vaiśravaṇo nāma bhaviṣyaty eṣa viśrutaḥ</li>
    </ul>
    <ul id="R_7.003.008">
        <li id="R_7.003.008ab">sa tu vaiśravaṇas tatra tapovanagatas tadā</li>
        <li id="R_7.003.008cd">avardhata mahātejā hutāhutir ivānalaḥ</li>
    </ul>
    <ul id="R_7.003.009">
        <li id="R_7.003.009ab">tasyāśramapadasthasya buddhir jajñe mahātmanaḥ</li>
        <li id="R_7.003.009cd">cariṣye niyato dharmaṃ dharmo hi paramā gatiḥ</li>
    </ul>
    <ul id="R_7.003.010">
        <li id="R_7.003.010ab">sa tu varṣasahasrāṇi tapas taptvā mahāvane</li>
        <li id="R_7.003.010cd">pūrṇe varṣasahasre tu taṃ taṃ vidhim avartata</li>
    </ul>
    <ul id="R_7.003.011">
        <li id="R_7.003.011ab">jalāśī mārutāhāro nirāhāras tathaiva ca</li>
        <li id="R_7.003.011cd">evaṃ varṣasahasrāṇi jagmus tāny eva varṣavat</li>
    </ul>
    <ul id="R_7.003.012">
        <li id="R_7.003.012ab">atha prīto mahātejāḥ sendraiḥ suragaṇaiḥ saha</li>
        <li id="R_7.003.012cd">gatvā tasyāśramapadaṃ brahmedaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_7.003.013">
        <li id="R_7.003.013ab">parituṣṭo 'smi te vatsa karmaṇānena suvrata</li>
        <li id="R_7.003.013cd">varaṃ vṛṇīṣva bhadraṃ te varārhas tvaṃ hi me mataḥ</li>
    </ul>
    <ul id="R_7.003.014">
        <li id="R_7.003.014ab">athābravīd vaiśravaṇaḥ pitāmaham upasthitam</li>
        <li id="R_7.003.014cd">bhagavaṃl lokapālatvam iccheyaṃ vittarakṣaṇam</li>
    </ul>
    <ul id="R_7.003.015">
        <li id="R_7.003.015ab">tato 'bravīd vaiśravaṇaṃ parituṣṭena cetasā</li>
        <li id="R_7.003.015cd">brahmā suragaṇaiḥ sārdhaṃ bāḍham ity eva hṛṣṭavat</li>
    </ul>
    <ul id="R_7.003.016">
        <li id="R_7.003.016ab">ahaṃ hi lokapālānāṃ caturthaṃ sraṣṭum udyataḥ</li>
        <li id="R_7.003.016cd">yamendravaruṇānāṃ hi padaṃ yat tava cepsitam</li>
    </ul>
    <ul id="R_7.003.017">
        <li id="R_7.003.017ab">tatkṛtaṃ gaccha dharmajña dhaneśatvam avāpnuhi</li>
        <li id="R_7.003.017cd">yamendravaruṇānāṃ hi caturtho 'dya bhaviṣyasi</li>
    </ul>
    <ul id="R_7.003.018">
        <li id="R_7.003.018ab">etac ca puṣpakaṃ nāma vimānaṃ sūryasaṃnibham</li>
        <li id="R_7.003.018cd">pratigṛhṇīṣva yānārthaṃ tridaśaiḥ samatāṃ vraja</li>
    </ul>
    <ul id="R_7.003.019">
        <li id="R_7.003.019ab">svasti te 'stu gamiṣyāmaḥ sarva eva yathāgatam</li>
        <li id="R_7.003.019cd">kṛtakṛtyā vayaṃ tāta dattvā tava mahāvaram</li>
    </ul>
    <ul id="R_7.003.020">
        <li id="R_7.003.020ab">gateṣu brahmapūrveṣu deveṣv atha nabhastalam</li>
        <li id="R_7.003.020cd">dhaneśaḥ pitaraṃ prāha vinayāt praṇato vacaḥ</li>
    </ul>
    <ul id="R_7.003.021">
        <li id="R_7.003.021ab">bhagavaṃl labdhavān asmi varaṃ kamalayonitaḥ</li>
        <li id="R_7.003.021cd">nivāsaṃ na tu me devo vidadhe sa prajāpatiḥ</li>
    </ul>
    <ul id="R_7.003.022">
        <li id="R_7.003.022ab">tat paśya bhagavan kaṃ cid deśaṃ vāsāya naḥ prabho</li>
        <li id="R_7.003.022cd">na ca pīḍā bhaved yatra prāṇino yasya kasya cit</li>
    </ul>
    <ul id="R_7.003.023">
        <li id="R_7.003.023ab">evam uktas tu putreṇa viśravā munipuṃgavaḥ</li>
        <li id="R_7.003.023cd">vacanaṃ prāha dharmajña śrūyatām iti dharmavit</li>
    </ul>
    <ul id="R_7.003.024">
        <li id="R_7.003.024ab">laṅkā nāma purī ramyā nirmitā viśvakarmaṇā</li>
        <li id="R_7.003.024cd">rākṣasānāṃ nivāsārthaṃ yathendrasyāmarāvatī</li>
    </ul>
    <ul id="R_7.003.025">
        <li id="R_7.003.025ab">ramaṇīyā purī sā hi rukmavaidūryatoraṇā</li>
        <li id="R_7.003.025cd">rākṣasaiḥ sā parityaktā purā viṣṇubhayārditaiḥ</li>
        <li id="R_7.003.025ef">śūnyā rakṣogaṇaiḥ sarvai rasātalatalaṃ gataiḥ</li>
    </ul>
    <ul id="R_7.003.026">
        <li id="R_7.003.026ab">sa tvaṃ tatra nivāsāya rocayasva matiṃ svakām</li>
        <li id="R_7.003.026cd">nirdoṣas tatra te vāso na ca bādhāsti kasya cit</li>
    </ul>
    <ul id="R_7.003.027">
        <li id="R_7.003.027ab">etac chrutvā tu dharmātmā dharmiṣṭhaṃ vacanaṃ pituḥ</li>
        <li id="R_7.003.027cd">niveśayām āsa tadā laṅkāṃ parvatamūrdhani</li>
    </ul>
    <ul id="R_7.003.028">
        <li id="R_7.003.028ab">nairṛtānāṃ sahasrais tu hṛṣṭaiḥ pramuditaiḥ sadā</li>
        <li id="R_7.003.028cd">acireṇaikakālena saṃpūrṇā tasya śāsanāt</li>
    </ul>
    <ul id="R_7.003.029">
        <li id="R_7.003.029ab">atha tatrāvasat prīto dharmātmā nairṛtādhipaḥ</li>
        <li id="R_7.003.029cd">samudraparidhānāyāṃ laṅkāyāṃ viśravātmajaḥ</li>
    </ul>
    <ul id="R_7.003.030">
        <li id="R_7.003.030ab">kāle kāle vinītātmā puṣpakeṇa dhaneśvaraḥ</li>
        <li id="R_7.003.030cd">abhyagacchat susaṃhṛṣṭaḥ pitaraṃ mātaraṃ ca saḥ</li>
    </ul>
    <ul id="R_7.003.031">
        <li id="R_7.003.031ab">
            <seg type="pāda" n="a">sa devagandharvagaṇair abhiṣṭutas</seg>
            <seg type="pāda" n="b">tathaiva siddhaiḥ saha cāraṇair api</seg>
        </li>
        <li id="R_7.003.031cd">
            <seg type="pāda" n="c">gabhastibhiḥ sūrya ivaujasā vṛtaḥ</seg>
            <seg type="pāda" n="d">pituḥ samīpaṃ prayayau śriyā vṛtaḥ</seg>
        </li>
    </ul>
    <ul id="R_7.004.001">
        <li id="R_7.004.001ab">śrutvāgastyeritaṃ vākyaṃ rāmo vismayam āgataḥ</li>
        <li id="R_7.004.001cd">pūrvam āsīt tu laṅkāyāṃ rakṣasām iti saṃbhavaḥ</li>
    </ul>
    <ul id="R_7.004.002">
        <li id="R_7.004.002ab">tataḥ śiraḥ kampayitvā tretāgnisamavigraham</li>
        <li id="R_7.004.002cd">agastyaṃ taṃ muhur dṛṣṭvā smayamāno 'bhyabhāṣata</li>
    </ul>
    <ul id="R_7.004.003">
        <li id="R_7.004.003ab">bhagavan pūrvam apy eṣā laṅkāsīt piśitāśinām</li>
        <li id="R_7.004.003cd">itīdaṃ bhavataḥ śrutvā vismayo janito mama</li>
    </ul>
    <ul id="R_7.004.004">
        <li id="R_7.004.004ab">pulastyavaṃśād udbhūtā rākṣasā iti naḥ śrutam</li>
        <li id="R_7.004.004cd">idānīm anyataś cāpi saṃbhavaḥ kīrtitas tvayā</li>
    </ul>
    <ul id="R_7.004.005">
        <li id="R_7.004.005ab">rāvaṇāt kumbhakarṇāc ca prahastād vikaṭād api</li>
        <li id="R_7.004.005cd">rāvaṇasya ca putrebhyaḥ kiṃ nu te balavattarāḥ</li>
    </ul>
    <ul id="R_7.004.006">
        <li id="R_7.004.006ab">ka eṣāṃ pūrvako brahman kiṃnāmā kiṃtapobalaḥ</li>
        <li id="R_7.004.006cd">aparādhaṃ ca kaṃ prāpya viṣṇunā drāvitāḥ purā</li>
    </ul>
    <ul id="R_7.004.007">
        <li id="R_7.004.007ab">etad vistarataḥ sarvaṃ kathayasva mamānagha</li>
        <li id="R_7.004.007cd">kautūhalaṃ kṛtaṃ mahyaṃ nuda bhānur yathā tamaḥ</li>
    </ul>
    <ul id="R_7.004.008">
        <li id="R_7.004.008ab">rāghavasya tu tac chrutvā saṃskārālaṃkṛtaṃ vacaḥ</li>
        <li id="R_7.004.008cd">īṣadvismayamānas tam agastyaḥ prāha rāghavam</li>
    </ul>
    <ul id="R_7.004.009">
        <li id="R_7.004.009ab">prajāpatiḥ purā sṛṣṭvā apaḥ salilasaṃbhavaḥ</li>
        <li id="R_7.004.009cd">tāsāṃ gopāyane sattvān asṛjat padmasaṃbhavaḥ</li>
    </ul>
    <ul id="R_7.004.010">
        <li id="R_7.004.010ab">te sattvāḥ sattvakartāraṃ vinītavad upasthitāḥ</li>
        <li id="R_7.004.010cd">kiṃ kurma iti bhāṣantaḥ kṣutpipāsābhayārditāḥ</li>
    </ul>
    <ul id="R_7.004.011">
        <li id="R_7.004.011ab">prajāpatis tu tāny āha sattvāni prahasann iva</li>
        <li id="R_7.004.011cd">ābhāṣya vācā yatnena rakṣadhvam iti mānadaḥ</li>
    </ul>
    <ul id="R_7.004.012">
        <li id="R_7.004.012ab">rakṣāma iti tatrānyair yakṣāmeti tathāparaiḥ</li>
        <li id="R_7.004.012cd">bhuṅkṣitābhuṅkṣitair uktas tatas tān āha bhūtakṛt</li>
    </ul>
    <ul id="R_7.004.013">
        <li id="R_7.004.013ab">rakṣāma iti yair uktaṃ rākṣasās te bhavantu vaḥ</li>
        <li id="R_7.004.013cd">yakṣāma iti yair uktaṃ te vai yakṣā bhavantu vaḥ</li>
    </ul>
    <ul id="R_7.004.014">
        <li id="R_7.004.014ab">tatra hetiḥ prahetiś ca bhrātarau rākṣasarṣabhau</li>
        <li id="R_7.004.014cd">madhukaiṭabhasaṃkāśau babhūvatur ariṃdamau</li>
    </ul>
    <ul id="R_7.004.015">
        <li id="R_7.004.015ab">prahetir dhārmikas tatra na dārān so 'bhikāṅkṣati</li>
        <li id="R_7.004.015cd">hetir dārakriyārthaṃ tu yatnaṃ param athākarot</li>
    </ul>
    <ul id="R_7.004.016">
        <li id="R_7.004.016ab">sa kālabhaginīṃ kanyāṃ bhayāṃ nāma bhayāvahām</li>
        <li id="R_7.004.016cd">udāvahad ameyātmā svayam eva mahāmatiḥ</li>
    </ul>
    <ul id="R_7.004.017">
        <li id="R_7.004.017ab">sa tasyāṃ janayām āsa hetī rākṣasapuṃgavaḥ</li>
        <li id="R_7.004.017cd">putraṃ putravatāṃ śreṣṭho vidyutkeśa iti śrutam</li>
    </ul>
    <ul id="R_7.004.018">
        <li id="R_7.004.018ab">vidyutkeśo hetiputraḥ pradīptāgnisamaprabhaḥ</li>
        <li id="R_7.004.018cd">vyavardhata mahātejās toyamadhya ivāmbujam</li>
    </ul>
    <ul id="R_7.004.019">
        <li id="R_7.004.019ab">sa yadā yauvanaṃ bhadram anuprāpto niśācaraḥ</li>
        <li id="R_7.004.019cd">tato dārakriyāṃ tasya kartuṃ vyavasitaḥ pitā</li>
    </ul>
    <ul id="R_7.004.020">
        <li id="R_7.004.020ab">saṃdhyāduhitaraṃ so 'tha saṃdhyātulyāṃ prabhāvataḥ</li>
        <li id="R_7.004.020cd">varayām āsa putrārthaṃ hetī rākṣasapuṃgavaḥ</li>
    </ul>
    <ul id="R_7.004.021">
        <li id="R_7.004.021ab">avaśyam eva dātavyā parasmai seti saṃdhyayā</li>
        <li id="R_7.004.021cd">cintayitvā sutā dattā vidyutkeśāya rāghava</li>
    </ul>
    <ul id="R_7.004.022">
        <li id="R_7.004.022ab">saṃdhyāyās tanayāṃ labdhvā vidyutkeśo niśācaraḥ</li>
        <li id="R_7.004.022cd">ramate sa tayā sārdhaṃ paulomyā maghavān iva</li>
    </ul>
    <ul id="R_7.004.023">
        <li id="R_7.004.023ab">kena cit tv atha kālena rāma sālakaṭaṃkaṭā</li>
        <li id="R_7.004.023cd">vidyutkeśād garbham āpa ghanarājir ivārṇavāt</li>
    </ul>
    <ul id="R_7.004.024">
        <li id="R_7.004.024ab">tataḥ sā rākṣasī garbhaṃ ghanagarbhasamaprabham</li>
        <li id="R_7.004.024cd">prasūtā mandaraṃ gatvā gaṅgā garbham ivāgnijam</li>
    </ul>
    <ul id="R_7.004.025">
        <li id="R_7.004.025ab">tam utsṛjya tu sā garbhaṃ vidyutkeśād ratārthinī</li>
        <li id="R_7.004.025cd">reme sā patinā sārdhaṃ vismṛtya sutam ātmajam</li>
    </ul>
    <ul id="R_7.004.026">
        <li id="R_7.004.026ab">tayotsṛṣṭaḥ sa tu śiśuḥ śaradarkasamadyutiḥ</li>
        <li id="R_7.004.026cd">pāṇim āsye samādhāya ruroda ghanarāḍ iva</li>
    </ul>
    <ul id="R_7.004.027">
        <li id="R_7.004.027ab">athopariṣṭād gacchan vai vṛṣabhastho haraḥ prabhuḥ</li>
        <li id="R_7.004.027cd">apaśyad umayā sārdhaṃ rudantaṃ rākṣasātmajam</li>
    </ul>
    <ul id="R_7.004.028">
        <li id="R_7.004.028ab">kāruṇyabhāvāt pārvatyā bhavas tripurahā tataḥ</li>
        <li id="R_7.004.028cd">taṃ rākṣasātmajaṃ cakre mātur eva vayaḥ samam</li>
    </ul>
    <ul id="R_7.004.029">
        <li id="R_7.004.029ab">amaraṃ caiva taṃ kṛtvā mahādevo 'kṣayo 'vyayaḥ</li>
        <li id="R_7.004.029cd">puram ākāśagaṃ prādāt pārvatyāḥ priyakāmyayā</li>
    </ul>
    <ul id="R_7.004.030">
        <li id="R_7.004.030ab">umayāpi varo datto rākṣasīnāṃ nṛpātmaja</li>
        <li id="R_7.004.030cd">sadyopalabdhir garbhasya prasūtiḥ sadya eva ca</li>
        <li id="R_7.004.030ef">sadya eva vayaḥprāptir mātur eva vayaḥ samam</li>
    </ul>
    <ul id="R_7.004.031">
        <li id="R_7.004.031ab">
            <seg type="pāda" n="a">tataḥ sukeśo varadānagarvitaḥ</seg>
            <seg type="pāda" n="b">śriyaṃ prabhoḥ prāpya harasya pārśvataḥ</seg>
        </li>
        <li id="R_7.004.031cd">
            <seg type="pāda" n="c">cacāra sarvatra mahāmatiḥ khagaḥ</seg>
            <seg type="pāda" n="d">khagaṃ puraṃ prāpya puraṃdaro yathā</seg>
        </li>
    </ul>
    <ul id="R_7.005.001">
        <li id="R_7.005.001ab">sukeśaṃ dhārmikaṃ dṛṣṭvā varalabdhaṃ ca rākṣasaṃ</li>
        <li id="R_7.005.001cd">grāmaṇīr nāma gandharvo viśvāvasusamaprabhaḥ</li>
    </ul>
    <ul id="R_7.005.002">
        <li id="R_7.005.002ab">tasya devavatī nāma dvitīyā śrīr ivātmajā</li>
        <li id="R_7.005.002cd">tāṃ sukeśāya dharmeṇa dadau dakṣaḥ śriyaṃ yathā</li>
    </ul>
    <ul id="R_7.005.003">
        <li id="R_7.005.003ab">varadānakṛtaiśvaryaṃ sā taṃ prāpya patiṃ priyam</li>
        <li id="R_7.005.003cd">āsīd devavatī tuṣṭā dhanaṃ prāpyeva nirdhanaḥ</li>
    </ul>
    <ul id="R_7.005.004">
        <li id="R_7.005.004ab">sa tayā saha saṃyukto rarāja rajanīcaraḥ</li>
        <li id="R_7.005.004cd">añjanād abhiniṣkrāntaḥ kareṇveva mahāgajaḥ</li>
    </ul>
    <ul id="R_7.005.005">
        <li id="R_7.005.005ab">devavatyāṃ sukeśas tu janayām āsa rāghava</li>
        <li id="R_7.005.005cd">trīṃs trinetrasamān putrān rākṣasān rākṣasādhipaḥ</li>
        <li id="R_7.005.005ef">mālyavantaṃ sumāliṃ ca māliṃ ca balināṃ varam</li>
    </ul>
    <ul id="R_7.005.006">
        <li id="R_7.005.006ab">trayo lokā ivāvyagrāḥ sthitās traya ivāgnayaḥ</li>
        <li id="R_7.005.006cd">trayo mantrā ivātyugrās trayo ghorā ivāmayāḥ</li>
    </ul>
    <ul id="R_7.005.007">
        <li id="R_7.005.007ab">trayaḥ sukeśasya sutās tretāgnisamavarcasaḥ</li>
        <li id="R_7.005.007cd">vivṛddhim agamaṃs tatra vyādhayopekṣitā iva</li>
    </ul>
    <ul id="R_7.005.008">
        <li id="R_7.005.008ab">varaprāptiṃ pitus te tu jñātvaiśvaryaṃ tato mahat</li>
        <li id="R_7.005.008cd">tapas taptuṃ gatā meruṃ bhrātaraḥ kṛtaniścayāḥ</li>
    </ul>
    <ul id="R_7.005.009">
        <li id="R_7.005.009ab">pragṛhya niyamān ghorān rākṣasā nṛpasattama</li>
        <li id="R_7.005.009cd">vicerus te tapo ghoraṃ sarvabhūtabhayāvaham</li>
    </ul>
    <ul id="R_7.005.010">
        <li id="R_7.005.010ab">satyārjavadamopetais tapobhir bhuvi duṣkaraiḥ</li>
        <li id="R_7.005.010cd">saṃtāpayantas trīṃl lokān sadevāsuramānuṣān</li>
    </ul>
    <ul id="R_7.005.011">
        <li id="R_7.005.011ab">tato vibhuś caturvaktro vimānavaram āsthitaḥ</li>
        <li id="R_7.005.011cd">sukeśaputrān āmantrya varado 'smīty abhāṣata</li>
    </ul>
    <ul id="R_7.005.012">
        <li id="R_7.005.012ab">brahmāṇaṃ varadaṃ jñātvā sendrair devagaṇair vṛtam</li>
        <li id="R_7.005.012cd">ūcuḥ prāñjalayaḥ sarve vepamānā iva drumāḥ</li>
    </ul>
    <ul id="R_7.005.013">
        <li id="R_7.005.013ab">tapasārādhito deva yadi no diśase varam</li>
        <li id="R_7.005.013cd">ajeyāḥ śatruhantāras tathaiva cirajīvinaḥ</li>
        <li id="R_7.005.013ef">prabhaviṣṇavo bhavāmeti parasparam anuvratāḥ</li>
    </ul>
    <ul id="R_7.005.014">
        <li id="R_7.005.014ab">evaṃ bhaviṣyatīty uktvā sukeśatanayān prabhuḥ</li>
        <li id="R_7.005.014cd">prayayau brahmalokāya brahmā brāhmaṇavatsalaḥ</li>
    </ul>
    <ul id="R_7.005.015">
        <li id="R_7.005.015ab">varaṃ labdhvā tataḥ sarve rāma rātriṃcarās tadā</li>
        <li id="R_7.005.015cd">surāsurān prabādhante varadānāt sunirbhayāḥ</li>
    </ul>
    <ul id="R_7.005.016">
        <li id="R_7.005.016ab">tair vadhyamānās tridaśāḥ sarṣisaṃghāḥ sacāraṇāḥ</li>
        <li id="R_7.005.016cd">trātāraṃ nādhigacchanti nirayasthā yathā narāḥ</li>
    </ul>
    <ul id="R_7.005.017">
        <li id="R_7.005.017ab">atha te viśvakarmāṇaṃ śilpināṃ varam avyayam</li>
        <li id="R_7.005.017cd">ūcuḥ sametya saṃhṛṣṭā rākṣasā raghusattama</li>
    </ul>
    <ul id="R_7.005.018">
        <li id="R_7.005.018ab">gṛhakartā bhavān eva devānāṃ hṛdayepsitam</li>
        <li id="R_7.005.018cd">asmākam api tāvat tvaṃ gṛhaṃ kuru mahāmate</li>
    </ul>
    <ul id="R_7.005.019">
        <li id="R_7.005.019ab">himavantaṃ samāśritya meruṃ mandaram eva vā</li>
        <li id="R_7.005.019cd">maheśvaragṛhaprakhyaṃ gṛhaṃ naḥ kriyatāṃ mahat</li>
    </ul>
    <ul id="R_7.005.020">
        <li id="R_7.005.020ab">viśvakarmā tatas teṣāṃ rākṣasānāṃ mahābhujaḥ</li>
        <li id="R_7.005.020cd">nivāsaṃ kathayām āsa śakrasyevāmarāvatīm</li>
    </ul>
    <ul id="R_7.005.021">
        <li id="R_7.005.021ab">dakṣiṇasyodadhes tīre trikūṭo nāma parvataḥ</li>
        <li id="R_7.005.021cd">śikhare tasya śailasya madhyame 'mbudasaṃnibhe</li>
        <li id="R_7.005.021ef">śakunair api duṣprāpe ṭaṅkacchinnacaturdiśi</li>
    </ul>
    <ul id="R_7.005.022">
        <li id="R_7.005.022ab">triṃśadyojanavistīrṇā svarṇaprākāratoraṇā</li>
        <li id="R_7.005.022cd">mayā laṅketi nagarī śakrājñaptena nirmitā</li>
    </ul>
    <ul id="R_7.005.023">
        <li id="R_7.005.023ab">tasyāṃ vasata durdharṣāḥ puryāṃ rākṣasasattamāḥ</li>
        <li id="R_7.005.023cd">amarāvatīṃ samāsādya sendrā iva divaukasaḥ</li>
    </ul>
    <ul id="R_7.005.024">
        <li id="R_7.005.024ab">laṅkādurgaṃ samāsādya rākṣasair bahubhir vṛtāḥ</li>
        <li id="R_7.005.024cd">bhaviṣyatha durādharṣāḥ śatrūṇāṃ śatrusūdanāḥ</li>
    </ul>
    <ul id="R_7.005.025">
        <li id="R_7.005.025ab">viśvakarmavacaḥ śrutvā tatas te rāma rākṣasāḥ</li>
        <li id="R_7.005.025cd">sahasrānucarā gatvā laṅkāṃ tām avasan purīm</li>
    </ul>
    <ul id="R_7.005.026">
        <li id="R_7.005.026ab">dṛḍhaprākāraparikhāṃ haimair gṛhaśatair vṛtām</li>
        <li id="R_7.005.026cd">laṅkām avāpya te hṛṣṭā viharanti niśācarāḥ</li>
    </ul>
    <ul id="R_7.005.027">
        <li id="R_7.005.027ab">narmadā nāma gandharvī nānādharmasamedhitā</li>
        <li id="R_7.005.027cd">tasyāḥ kanyātrayaṃ hy āsīd dhīśrīkīrtisamadyuti</li>
    </ul>
    <ul id="R_7.005.028">
        <li id="R_7.005.028ab">jyeṣṭhakrameṇa sā teṣāṃ rākṣasānām arākṣasī</li>
        <li id="R_7.005.028cd">kanyās tāḥ pradadau hṛṣṭā pūrṇacandranibhānanāḥ</li>
    </ul>
    <ul id="R_7.005.029">
        <li id="R_7.005.029ab">trayāṇāṃ rākṣasendrāṇāṃ tisro gandharvakanyakāḥ</li>
        <li id="R_7.005.029cd">mātrā dattā mahābhāgā nakṣatre bhagadaivate</li>
    </ul>
    <ul id="R_7.005.030">
        <li id="R_7.005.030ab">kṛtadārās tu te rāma sukeśatanayāḥ prabho</li>
        <li id="R_7.005.030cd">bhāryābhiḥ saha cikrīḍur apsarobhir ivāmarāḥ</li>
    </ul>
    <ul id="R_7.005.031">
        <li id="R_7.005.031ab">tatra mālyavato bhāryā sundarī nāma sundarī</li>
        <li id="R_7.005.031cd">sa tasyāṃ janayām āsa yad apatyaṃ nibodha tat</li>
    </ul>
    <ul id="R_7.005.032">
        <li id="R_7.005.032ab">vajramuṣṭir virūpākṣo durmukhaś caiva rākṣasaḥ</li>
        <li id="R_7.005.032cd">suptaghno yajñakopaś ca mattonmattau tathaiva ca</li>
        <li id="R_7.005.032ef">analā cābhavat kanyā sundaryāṃ rāma sundarī</li>
    </ul>
    <ul id="R_7.005.033">
        <li id="R_7.005.033ab">sumālino 'pi bhāryāsīt pūrṇacandranibhānanā</li>
        <li id="R_7.005.033cd">nāmnā ketumatī nāma prāṇebhyo 'pi garīyasī</li>
    </ul>
    <ul id="R_7.005.034">
        <li id="R_7.005.034ab">sumālī janayām āsa yad apatyaṃ niśācaraḥ</li>
        <li id="R_7.005.034cd">ketumatyāṃ mahārāja tan nibodhānupūrvaśaḥ</li>
    </ul>
    <ul id="R_7.005.035">
        <li id="R_7.005.035ab">prahasto 'kampanaiś caiva vikaṭaḥ kālakārmukaḥ</li>
        <li id="R_7.005.035cd">dhūmrākśaś cātha daṇḍaś ca supārśvaś ca mahābalaḥ</li>
    </ul>
    <ul id="R_7.005.036">
        <li id="R_7.005.036ab">saṃhrādiḥ praghasaś caiva bhāsakarṇaś ca rākṣasaḥ</li>
        <li id="R_7.005.036cd">rākā puṣpotkaṭā caiva kaikasī ca śucismitā</li>
        <li id="R_7.005.036ef">kumbhīnasī ca ity ete sumāleḥ prasavāḥ smṛtāḥ</li>
    </ul>
    <ul id="R_7.005.037">
        <li id="R_7.005.037ab">māles tu vasudā nāma gandharvī rūpaśālinī</li>
        <li id="R_7.005.037cd">bhāryāsīt padmapatrākṣī svakṣī yakṣīvaropamā</li>
    </ul>
    <ul id="R_7.005.038">
        <li id="R_7.005.038ab">sumāler anujas tasyāṃ janayām āsa yat prabho</li>
        <li id="R_7.005.038cd">apatyaṃ kathyamānaṃ tan mayā tvaṃ śṛṇu rāghava</li>
    </ul>
    <ul id="R_7.005.039">
        <li id="R_7.005.039ab">analaś cānilaś caiva haraḥ saṃpātir eva ca</li>
        <li id="R_7.005.039cd">ete vibhīṣaṇāmātyā māleyās te niśācarāḥ</li>
    </ul>
    <ul id="R_7.005.040">
        <li id="R_7.005.040ab">
            <seg type="pāda" n="a">tatas tu te rākṣasapuṃgavās trayo</seg>
            <seg type="pāda" n="b">niśācaraiḥ putraśataiś ca saṃvṛtāḥ</seg>
        </li>
        <li id="R_7.005.040cd">
            <seg type="pāda" n="c">surān sahendrān ṛṣināgadānavān</seg>
            <seg type="pāda" n="d">babādhire te balavīryadarpitāḥ</seg>
        </li>
    </ul>
    <ul id="R_7.005.041">
        <li id="R_7.005.041ab">
            <seg type="pāda" n="a">jagad bhramanto 'nilavad durāsadā</seg>
            <seg type="pāda" n="b">raṇe ca mṛtyupratimāḥ samāhitāḥ</seg>
        </li>
        <li id="R_7.005.041cd">
            <seg type="pāda" n="c">varapradānād abhigarvitā bhṛśaṃ</seg>
            <seg type="pāda" n="d">kratukriyāṇāṃ praśamaṃkarāḥ sadā</seg>
        </li>
    </ul>
    <ul id="R_7.006.001">
        <li id="R_7.006.001ab">tair vadhyamānā devāś ca ṛṣayaś ca tapodhanāḥ</li>
        <li id="R_7.006.001cd">bhayārtāḥ śaraṇaṃ jagmur devadevaṃ maheśvaram</li>
    </ul>
    <ul id="R_7.006.002">
        <li id="R_7.006.002ab">te sametya tu kāmāriṃ tripurāriṃ trilocanam</li>
        <li id="R_7.006.002cd">ūcuḥ prāñjalayo devā bhayagadgadabhāṣiṇaḥ</li>
    </ul>
    <ul id="R_7.006.003">
        <li id="R_7.006.003ab">sukeśaputrair bhagavan pitāmahavaroddhataiḥ</li>
        <li id="R_7.006.003cd">prajādhyakṣa prajāḥ sarvā bādhyante ripubādhana</li>
    </ul>
    <ul id="R_7.006.004">
        <li id="R_7.006.004ab">śaraṇyāny aśaraṇyāni āśramāṇi kṛtāni naḥ</li>
        <li id="R_7.006.004cd">svargāc ca cyāvitaḥ śakraḥ svarge krīḍanti śakravat</li>
    </ul>
    <ul id="R_7.006.005">
        <li id="R_7.006.005ab">ahaṃ viṣṇur ahaṃ rudro brahmāhaṃ devarāḍ aham</li>
        <li id="R_7.006.005cd">ahaṃ yamo 'haṃ varuṇaś candro 'haṃ ravir apy aham</li>
    </ul>
    <ul id="R_7.006.006">
        <li id="R_7.006.006ab">iti te rākṣasā deva varadānena darpitāḥ</li>
        <li id="R_7.006.006cd">bādhante samaroddharṣā ye ca teṣāṃ puraḥsarāḥ</li>
    </ul>
    <ul id="R_7.006.007">
        <li id="R_7.006.007ab">tan no devabhayārtānām abhayaṃ dātum arhasi</li>
        <li id="R_7.006.007cd">aśivaṃ vapur āsthāya jahi daivatakaṇṭakān</li>
    </ul>
    <ul id="R_7.006.008">
        <li id="R_7.006.008ab">ity uktas tu suraiḥ sarvaiḥ kapardī nīlalohitaḥ</li>
        <li id="R_7.006.008cd">sukeśaṃ prati sāpekṣa āha devagaṇān prabhuḥ</li>
    </ul>
    <ul id="R_7.006.009">
        <li id="R_7.006.009ab">nāhaṃ tān nihaniṣyāmi avadhyā mama te 'surāḥ</li>
        <li id="R_7.006.009cd">kiṃ tu mantraṃ pradāsyāmi yo vai tān nihaniṣyati</li>
    </ul>
    <ul id="R_7.006.010">
        <li id="R_7.006.010ab">evam eva samudyogaṃ puraskṛtya surarṣabhāḥ</li>
        <li id="R_7.006.010cd">gacchantu śaraṇaṃ viṣṇuṃ haniṣyati sa tān prabhuḥ</li>
    </ul>
    <ul id="R_7.006.011">
        <li id="R_7.006.011ab">tatas te jayaśabdena pratinandya maheśvaram</li>
        <li id="R_7.006.011cd">viṣṇoḥ samīpam ājagmur niśācarabhayārditāḥ</li>
    </ul>
    <ul id="R_7.006.012">
        <li id="R_7.006.012ab">śaṅkhacakradharaṃ devaṃ praṇamya bahumānya ca</li>
        <li id="R_7.006.012cd">ūcuḥ saṃbhrāntavad vākyaṃ sukeśatanayārditāḥ</li>
    </ul>
    <ul id="R_7.006.013">
        <li id="R_7.006.013ab">sukeśatanayair devatribhis tretāgnisaṃnibhaiḥ</li>
        <li id="R_7.006.013cd">ākramya varadānena sthānāny apahṛtāni naḥ</li>
    </ul>
    <ul id="R_7.006.014">
        <li id="R_7.006.014ab">laṅkā nāma purī durgā trikūṭaśikhare sthitā</li>
        <li id="R_7.006.014cd">tatra sthitāḥ prabādhante sarvān naḥ kṣaṇadācarāḥ</li>
    </ul>
    <ul id="R_7.006.015">
        <li id="R_7.006.015ab">sa tvam asmatpriyārthaṃ tu jahi tān madhusūdana</li>
        <li id="R_7.006.015cd">cakrakṛttāsyakamalān nivedaya yamāya vai</li>
    </ul>
    <ul id="R_7.006.016">
        <li id="R_7.006.016ab">bhayeṣv abhayado 'smākaṃ nānyo 'sti bhavatā samaḥ</li>
        <li id="R_7.006.016cd">nuda tvaṃ no bhayaṃ deva nīhāram iva bhāskaraḥ</li>
    </ul>
    <ul id="R_7.006.017">
        <li id="R_7.006.017ab">ity evaṃ daivatair ukto devadevo janārdanaḥ</li>
        <li id="R_7.006.017cd">abhayaṃ bhayado 'rīṇāṃ dattvā devān uvāca ha</li>
    </ul>
    <ul id="R_7.006.018">
        <li id="R_7.006.018ab">sukeśaṃ rākṣasaṃ jāne īśāna varadarpitam</li>
        <li id="R_7.006.018cd">tāṃś cāsya tanayāñ jāne yeṣāṃ jyeṣṭhaḥ sa mālyavān</li>
    </ul>
    <ul id="R_7.006.019">
        <li id="R_7.006.019ab">tān ahaṃ samatikrāntamaryādān rākṣasādhamān</li>
        <li id="R_7.006.019cd">sūdayiṣyāmi saṃgrāme surā bhavata vijvarāḥ</li>
    </ul>
    <ul id="R_7.006.020">
        <li id="R_7.006.020ab">ity uktās te surāḥ sarve viṣṇunā prabhaviṣṇunā</li>
        <li id="R_7.006.020cd">yathā vāsaṃ yayur hṛṣṭāḥ praśamanto janārdanam</li>
    </ul>
    <ul id="R_7.006.021">
        <li id="R_7.006.021ab">vibudhānāṃ samudyogaṃ mālyavān sa niśācaraḥ</li>
        <li id="R_7.006.021cd">śrutvā tau bhrātarau vīrāv idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.006.022">
        <li id="R_7.006.022ab">amarā ṛṣayaś caiva saṃhatya kila śaṃkaram</li>
        <li id="R_7.006.022cd">asmadvadhaṃ parīpsanta idam ūcus trilocanam</li>
    </ul>
    <ul id="R_7.006.023">
        <li id="R_7.006.023ab">sukeśatanayā deva varadānabaloddhatāḥ</li>
        <li id="R_7.006.023cd">bādhante 'smān samudyuktā ghorarūpāḥ pade pade</li>
    </ul>
    <ul id="R_7.006.024">
        <li id="R_7.006.024ab">rākṣasair abhibhūtāḥ sma na śaktāḥ sma umāpate</li>
        <li id="R_7.006.024cd">sveṣu veśmasu saṃsthātuṃ bhayāt teṣāṃ durātmanām</li>
    </ul>
    <ul id="R_7.006.025">
        <li id="R_7.006.025ab">tad asmākaṃ hitārthe tvaṃ jahi tāṃs tāṃs trilocana</li>
        <li id="R_7.006.025cd">rākṣasān huṃkṛtenaiva daha pradahatāṃ vara</li>
    </ul>
    <ul id="R_7.006.026">
        <li id="R_7.006.026ab">ity evaṃ tridaśair ukto niśamyāndhakasūdanaḥ</li>
        <li id="R_7.006.026cd">śiraḥ karaṃ ca dhunvāna idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.006.027">
        <li id="R_7.006.027ab">avadhyā mama te devāḥ sukeśatanayā raṇe</li>
        <li id="R_7.006.027cd">mantraṃ tu vaḥ pradāsyāmi yo vai tān nihaniṣyati</li>
    </ul>
    <ul id="R_7.006.028">
        <li id="R_7.006.028ab">yaḥ sa cakragadāpāṇiḥ pītavāsā janārdanaḥ</li>
        <li id="R_7.006.028cd">haniṣyati sa tān yuddhe śaraṇaṃ taṃ prapadyatha</li>
    </ul>
    <ul id="R_7.006.029">
        <li id="R_7.006.029ab">harān nāvāpya te kāmaṃ kāmārim abhivādya ca</li>
        <li id="R_7.006.029cd">nārāyaṇālayaṃ prāptās tasmai sarvaṃ nyavedayan</li>
    </ul>
    <ul id="R_7.006.030">
        <li id="R_7.006.030ab">tato nārāyaṇenoktā devā indrapurogamāḥ</li>
        <li id="R_7.006.030cd">surārīn sūdayiṣyāmi surā bhavata vijvarāḥ</li>
    </ul>
    <ul id="R_7.006.031">
        <li id="R_7.006.031ab">devānāṃ bhayabhītānāṃ hariṇā rākṣasarṣabhau</li>
        <li id="R_7.006.031cd">pratijñāto vadho 'smākaṃ tac cintayatha yat kṣamam</li>
    </ul>
    <ul id="R_7.006.032">
        <li id="R_7.006.032ab">hiraṇyakaśipor mṛtyur anyeṣāṃ ca suradviṣām</li>
        <li id="R_7.006.032cd">duḥkhaṃ nārāyaṇaṃ jetuṃ yo no hantum abhīpsati</li>
    </ul>
    <ul id="R_7.006.033">
        <li id="R_7.006.033ab">tataḥ sumālī mālī ca śrutvā mālyavato vacaḥ</li>
        <li id="R_7.006.033cd">ūcatur bhrātaraṃ jyeṣṭhaṃ bhagāṃśāv iva vāsavam</li>
    </ul>
    <ul id="R_7.006.034">
        <li id="R_7.006.034ab">svadhītaṃ dattam iṣṭaṃ ca aiśvaryaṃ paripālitam</li>
        <li id="R_7.006.034cd">āyur nirāmayaṃ prāptaṃ svadharmaḥ sthāpitaś ca naḥ</li>
    </ul>
    <ul id="R_7.006.035">
        <li id="R_7.006.035ab">devasāgaram akṣobhyaṃ śastraughaiḥ pravigāhya ca</li>
        <li id="R_7.006.035cd">jitā devā raṇe nityaṃ na no mṛtyukṛtaṃ bhayam</li>
    </ul>
    <ul id="R_7.006.036">
        <li id="R_7.006.036ab">nārāyaṇaś ca rudraś ca śakraś cāpi yamas tathā</li>
        <li id="R_7.006.036cd">asmākaṃ pramukhe sthātuṃ sarva eva hi bibhyati</li>
    </ul>
    <ul id="R_7.006.037">
        <li id="R_7.006.037ab">viṣṇor doṣaś ca nāsty atra kāraṇaṃ rākṣaseśvara</li>
        <li id="R_7.006.037cd">devānām eva doṣeṇa viṣṇoḥ pracalitaṃ manaḥ</li>
    </ul>
    <ul id="R_7.006.038">
        <li id="R_7.006.038ab">tasmād adya samudyuktāḥ sarvasainyasamāvṛtāḥ</li>
        <li id="R_7.006.038cd">devān eva jighāṃsāmo yebhyo doṣaḥ samutthitaḥ</li>
    </ul>
    <ul id="R_7.006.039">
        <li id="R_7.006.039ab">iti mālī sumālī ca mālyavān agrajaḥ prabhuḥ</li>
        <li id="R_7.006.039cd">udyogaṃ ghoṣayitvātha rākṣasāḥ sarva eva te</li>
        <li id="R_7.006.039ef">yuddhāya niryayuḥ kruddhā jambhavṛtrabalā iva</li>
    </ul>
    <ul id="R_7.006.040">
        <li id="R_7.006.040ab">syandanair vāraṇendraiś ca hayaiś ca girisaṃnibhaiḥ</li>
        <li id="R_7.006.040cd">kharair gobhir athoṣṭraiś ca śiṃśumārair bhujaṃ gamaiḥ</li>
    </ul>
    <ul id="R_7.006.041">
        <li id="R_7.006.041ab">makaraiḥ kacchapair mīnair vihaṃgair garuḍopamaiḥ</li>
        <li id="R_7.006.041cd">siṃhair vyāghrair varāhaiś ca sṛmaraiś camarair api</li>
    </ul>
    <ul id="R_7.006.042">
        <li id="R_7.006.042ab">tyaktvā laṅkāṃ tataḥ sarve rākṣasā balagarvitāḥ</li>
        <li id="R_7.006.042cd">prayātā devalokāya yoddhuṃ daivataśatravaḥ</li>
    </ul>
    <ul id="R_7.006.043">
        <li id="R_7.006.043ab">laṅkāviparyayaṃ dṛṣṭvā yāni laṅkālayāny atha</li>
        <li id="R_7.006.043cd">bhūtāni bhayadarśīni vimanaskāni sarvaśaḥ</li>
    </ul>
    <ul id="R_7.006.044">
        <li id="R_7.006.044ab">bhaumās tathāntarikṣāś ca kālājñaptā bhayāvahāḥ</li>
        <li id="R_7.006.044cd">utpātā rākṣasendrāṇām abhāvāyotthitā drutam</li>
    </ul>
    <ul id="R_7.006.045">
        <li id="R_7.006.045ab">asthīni meghā varṣanti uṣṇaṃ śoṇitam eva ca</li>
        <li id="R_7.006.045cd">velāṃ samudro 'py utkrāntaś calante cācalottamāḥ</li>
    </ul>
    <ul id="R_7.006.046">
        <li id="R_7.006.046ab">aṭṭahāsān vimuñcanto ghananādasamasvanān</li>
        <li id="R_7.006.046cd">bhūtāḥ paripatanti sma nṛtyamānāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_7.006.047">
        <li id="R_7.006.047ab">gṛdhracakraṃ mahac cāpi jvalanodgāribhir mukhaiḥ</li>
        <li id="R_7.006.047cd">rākṣasānām upari vai bhramate kālacakravat</li>
    </ul>
    <ul id="R_7.006.048">
        <li id="R_7.006.048ab">tān acintyamahotpātān rākṣasā balagarvitāḥ</li>
        <li id="R_7.006.048cd">yanty eva na nivartante mṛtyupāśāvapāśitāḥ</li>
    </ul>
    <ul id="R_7.006.049">
        <li id="R_7.006.049ab">mālyavāṃś ca sumālī ca mālī ca rajanīcarāḥ</li>
        <li id="R_7.006.049cd">āsan puraḥsarās teṣāṃ kratūnām iva pāvakāḥ</li>
    </ul>
    <ul id="R_7.006.050">
        <li id="R_7.006.050ab">mālyavantaṃ tu te sarve mālyavantam ivācalam</li>
        <li id="R_7.006.050cd">niśācarā āśrayante dhātāram iva dehinaḥ</li>
    </ul>
    <ul id="R_7.006.051">
        <li id="R_7.006.051ab">tad balaṃ rākṣasendrāṇāṃ mahābhraghananāditam</li>
        <li id="R_7.006.051cd">jayepsayā devalokaṃ yayau mālī vaśe sthitam</li>
    </ul>
    <ul id="R_7.006.052">
        <li id="R_7.006.052ab">rākṣasānāṃ samudyogaṃ taṃ tu nārāyaṇaḥ prabhuḥ</li>
        <li id="R_7.006.052cd">devadūtād upaśrutya dadhre yuddhe tato manaḥ</li>
    </ul>
    <ul id="R_7.006.053">
        <li id="R_7.006.053ab">
            <seg type="pāda" n="a">sa devasiddharṣimahoragaiś ca</seg>
            <seg type="pāda" n="b">gandharvamukhyāpsarasopagītaḥ</seg>
        </li>
        <li id="R_7.006.053cd">
            <seg type="pāda" n="c">samāsasādāmaraśatrusainyaṃ</seg>
            <seg type="pāda" n="d">cakrāsisīrapravarādidhārī</seg>
        </li>
    </ul>
    <ul id="R_7.006.054">
        <li id="R_7.006.054ab">
            <seg type="pāda" n="a">suparṇapakṣānilanunnapakṣaṃ</seg>
            <seg type="pāda" n="b">bhramatpatākaṃ pravikīrṇaśastram</seg>
        </li>
        <li id="R_7.006.054cd">
            <seg type="pāda" n="c">cacāla tad rākṣasarājasainyaṃ</seg>
            <seg type="pāda" n="d">calopalo nīla ivācalendraḥ</seg>
        </li>
    </ul>
    <ul id="R_7.006.055">
        <li id="R_7.006.055ab">
            <seg type="pāda" n="a">tatha śitaiḥ śoṇitamāṃsarūṣitair</seg>
            <seg type="pāda" n="b">yugāntavaiśvānaratulyavigrahaiḥ</seg>
        </li>
        <li id="R_7.006.055cd">
            <seg type="pāda" n="c">niśācarāḥ saṃparivārya mādhavaṃ</seg>
            <seg type="pāda" n="d">varāyudhair nirbibhiduḥ sahasraśaḥ</seg>
        </li>
    </ul>
    <ul id="R_7.007.001">
        <li id="R_7.007.001ab">nārāyaṇagiriṃ te tu garjanto rākṣasāmbudāḥ</li>
        <li id="R_7.007.001cd">avarṣann iṣuvarṣeṇa varṣeṇādrim ivāmbudāḥ</li>
    </ul>
    <ul id="R_7.007.002">
        <li id="R_7.007.002ab">śyāmāvadātas tair viṣṇur nīlair naktaṃcarottamaiḥ</li>
        <li id="R_7.007.002cd">vṛto 'ñjanagirīvāsīd varṣamāṇaiḥ payodharaiḥ</li>
    </ul>
    <ul id="R_7.007.003">
        <li id="R_7.007.003ab">śalabhā iva kedāraṃ maśakā iva parvatam</li>
        <li id="R_7.007.003cd">yathāmṛtaghaṭaṃ jīvā makarā iva cārṇavam</li>
    </ul>
    <ul id="R_7.007.004">
        <li id="R_7.007.004ab">tathā rakṣodhanur muktā vajrānilamanojavāḥ</li>
        <li id="R_7.007.004cd">hariṃ viśanti sma śarā lokāstam iva paryaye</li>
    </ul>
    <ul id="R_7.007.005">
        <li id="R_7.007.005ab">syandanaiḥ syandanagatā gajaiś ca gajadhūr gatāḥ</li>
        <li id="R_7.007.005cd">aśvārohāḥ sadaśvaiś ca pādātāś cāmbare carāḥ</li>
    </ul>
    <ul id="R_7.007.006">
        <li id="R_7.007.006ab">rākṣasendrā girinibhāḥ śaraśaktyṛṣṭitomaraiḥ</li>
        <li id="R_7.007.006cd">nirucchvāsaṃ hariṃ cakruḥ prāṇāyāma iva dvijam</li>
    </ul>
    <ul id="R_7.007.007">
        <li id="R_7.007.007ab">niśācarais tudyamāno mīnair iva mahātimiḥ</li>
        <li id="R_7.007.007cd">śārṅgam āyamya gātrāṇi rākṣasānāṃ mahāhave</li>
    </ul>
    <ul id="R_7.007.008">
        <li id="R_7.007.008ab">śaraiḥ pūrṇāyatotsṛṣṭair vajravaktrair manojavaiḥ</li>
        <li id="R_7.007.008cd">ciccheda tilaśo viṣṇuḥ śataśo 'tha sahasraśaḥ</li>
    </ul>
    <ul id="R_7.007.009">
        <li id="R_7.007.009ab">vidrāvya śaravarṣaṃ taṃ varṣaṃ vāyur ivotthitam</li>
        <li id="R_7.007.009cd">pāñcajanyaṃ mahāśaṅkhaṃ pradadhmau puruṣottamaḥ</li>
    </ul>
    <ul id="R_7.007.010">
        <li id="R_7.007.010ab">so 'mbujo hariṇā dhmātaḥ sarvaprāṇena śaṅkharāṭ</li>
        <li id="R_7.007.010cd">rarāsa bhīmanihrādo yugānte jalado yathā</li>
    </ul>
    <ul id="R_7.007.011">
        <li id="R_7.007.011ab">śaṅkharājaravaḥ so 'tha trāsayām āsa rākṣasān</li>
        <li id="R_7.007.011cd">mṛgarāja ivāraṇye samadān iva kuñjarān</li>
    </ul>
    <ul id="R_7.007.012">
        <li id="R_7.007.012ab">na śekur aśvāḥ saṃsthātuṃ vimadāḥ kuñjarābhavan</li>
        <li id="R_7.007.012cd">syandanebhyaś cyutā yodhāḥ śaṅkharāvitadurbalāḥ</li>
    </ul>
    <ul id="R_7.007.013">
        <li id="R_7.007.013ab">śārṅgacāpavinirmuktā vajratulyānanāḥ śarāḥ</li>
        <li id="R_7.007.013cd">vidārya tāni rakṣāṃsi supuṅkhā viviśuḥ kṣitim</li>
    </ul>
    <ul id="R_7.007.014">
        <li id="R_7.007.014ab">bhidyamānāḥ śaraiś cānye nārāyaṇadhanuścyutaiḥ</li>
        <li id="R_7.007.014cd">nipetū rākṣasā bhīmāḥ śailā vajrahatā iva</li>
    </ul>
    <ul id="R_7.007.015">
        <li id="R_7.007.015ab">vraṇair vraṇakarārīṇām adhokṣajaśarodbhavaiḥ</li>
        <li id="R_7.007.015cd">asṛk kṣaranti dhārābhiḥ svarṇadhārām ivācalāḥ</li>
    </ul>
    <ul id="R_7.007.016">
        <li id="R_7.007.016ab">śaṅkharājaravaś cāpi śārṅgacāparavas tathā</li>
        <li id="R_7.007.016cd">rākṣasānāṃ ravāṃś cāpi grasate vaiṣṇavo ravaḥ</li>
    </ul>
    <ul id="R_7.007.017">
        <li id="R_7.007.017ab">sūryād iva karā ghorā ūrmayaḥ sāgarād iva</li>
        <li id="R_7.007.017cd">parvatād iva nāgendrā vāryoghā iva cāmbudāt</li>
    </ul>
    <ul id="R_7.007.018">
        <li id="R_7.007.018ab">tathā bāṇā vinirmuktāḥ śārṅgān narāyaṇeritāḥ</li>
        <li id="R_7.007.018cd">nirdhāvantīṣavas tūrṇaṃ śataśo 'tha sahasraśaḥ</li>
    </ul>
    <ul id="R_7.007.019">
        <li id="R_7.007.019ab">śarabheṇa yathā siṃhāḥ siṃhena dviradā yathā</li>
        <li id="R_7.007.019cd">dviradena yathā vyāghrā vyāghreṇa dvīpino yathā</li>
    </ul>
    <ul id="R_7.007.020">
        <li id="R_7.007.020ab">dvīpinā ca yathā śvānaḥ śunā mārjārakā yathā</li>
        <li id="R_7.007.020cd">mārjāreṇa yathā sarpāḥ sarpeṇa ca yathākhavaḥ</li>
    </ul>
    <ul id="R_7.007.021">
        <li id="R_7.007.021ab">tathā te rākṣasā yuddhe viṣṇunā prabhaviṣṇunā</li>
        <li id="R_7.007.021cd">dravanti drāvitāś caiva śāyitāś ca mahītale</li>
    </ul>
    <ul id="R_7.007.022">
        <li id="R_7.007.022ab">rākṣasānāṃ sahasrāṇi nihatya madhusūdanaḥ</li>
        <li id="R_7.007.022cd">vārijaṃ nādayām āsa toyadaṃ surarāḍ iva</li>
    </ul>
    <ul id="R_7.007.023">
        <li id="R_7.007.023ab">nārāyaṇaśaragrastaṃ śaṅkhanādasuvihvalam</li>
        <li id="R_7.007.023cd">yayau laṅkām abhimukhaṃ prabhagnaṃ rākṣasaṃ balam</li>
    </ul>
    <ul id="R_7.007.024">
        <li id="R_7.007.024ab">prabhagne rākṣasabale nārāyaṇaśarāhate</li>
        <li id="R_7.007.024cd">sumālī śaravarṣeṇa āvavāra raṇe harim</li>
    </ul>
    <ul id="R_7.007.025">
        <li id="R_7.007.025ab">utkṣipya hemābharaṇaṃ karaṃ karam iva dvipaḥ</li>
        <li id="R_7.007.025cd">rarāsa rākṣaso harṣāt sataḍit toyado yathā</li>
    </ul>
    <ul id="R_7.007.026">
        <li id="R_7.007.026ab">sumāler nardatas tasya śiro jvalitakuṇḍalam</li>
        <li id="R_7.007.026cd">ciccheda yantur aśvāś ca bhrāntās tasya tu rakṣasaḥ</li>
    </ul>
    <ul id="R_7.007.027">
        <li id="R_7.007.027ab">tair aśvair bhrāmyate bhrāntaiḥ sumālī rākṣaseśvaraḥ</li>
        <li id="R_7.007.027cd">indriyāśvair yathā bhrāntair dhṛtihīno yathā naraḥ</li>
    </ul>
    <ul id="R_7.007.028">
        <li id="R_7.007.028ab">mālī cābhyadravad yuddhe pragṛhya saśaraṃ dhanuḥ</li>
        <li id="R_7.007.028cd">māler dhanuścyutā bāṇāḥ kārtasvaravibhūṣitāḥ</li>
        <li id="R_7.007.028ef">viviśur harim āsādya krauñcaṃ patrarathā iva</li>
    </ul>
    <ul id="R_7.007.029">
        <li id="R_7.007.029ab">ardyamānaḥ śaraiḥ so 'tha mālimuktaiḥ sahasraśaḥ</li>
        <li id="R_7.007.029cd">cukṣubhe na raṇe viṣṇur jitendriya ivādhibhiḥ</li>
    </ul>
    <ul id="R_7.007.030">
        <li id="R_7.007.030ab">atha maurvī svanaṃ kṛtvā bhagavān bhūtabhāvanaḥ</li>
        <li id="R_7.007.030cd">mālinaṃ prati bāṇaughān sasarjāsigadādharaḥ</li>
    </ul>
    <ul id="R_7.007.031">
        <li id="R_7.007.031ab">te mālideham āsādya vajravidyutprabhāḥ śarāḥ</li>
        <li id="R_7.007.031cd">pibanti rudhiraṃ tasya nāgā iva purāmṛtam</li>
    </ul>
    <ul id="R_7.007.032">
        <li id="R_7.007.032ab">mālinaṃ vimukhaṃ kṛtvā mālimauliṃ harir balāt</li>
        <li id="R_7.007.032cd">rathaṃ ca sadhvajaṃ cāpaṃ vājinaś ca nyapātayat</li>
    </ul>
    <ul id="R_7.007.033">
        <li id="R_7.007.033ab">virathas tu gadāṃ gṛhya mālī naktaṃcarottamaḥ</li>
        <li id="R_7.007.033cd">āpupluve gadāpāṇir giryagrād iva keṣarī</li>
    </ul>
    <ul id="R_7.007.034">
        <li id="R_7.007.034ab">sa tayā garuḍaṃ saṃkhye īśānam iva cāntakaḥ</li>
        <li id="R_7.007.034cd">lalāṭadeśe 'bhyahanad vajreṇendro yathācalam</li>
    </ul>
    <ul id="R_7.007.035">
        <li id="R_7.007.035ab">gadayābhihatas tena mālinā garuḍo bhṛśam</li>
        <li id="R_7.007.035cd">raṇāt parāṅmukhaṃ devaṃ kṛtavān vedanāturaḥ</li>
    </ul>
    <ul id="R_7.007.036">
        <li id="R_7.007.036ab">parāṅmukhe kṛte deve mālinā garuḍena vai</li>
        <li id="R_7.007.036cd">udatiṣṭhan mahānādo rakṣasām abhinardatām</li>
    </ul>
    <ul id="R_7.007.037">
        <li id="R_7.007.037ab">rakṣasāṃ nadatāṃ nādaṃ śrutvā harihayānujaḥ</li>
        <li id="R_7.007.037cd">parāṅmukho 'py utsasarja cakraṃ mālijighāṃsayā</li>
    </ul>
    <ul id="R_7.007.038">
        <li id="R_7.007.038ab">tat sūryamaṇḍalābhāsaṃ svabhāsā bhāsayan nabhaḥ</li>
        <li id="R_7.007.038cd">kālacakranibhaṃ cakraṃ māleḥ śīrṣam apātayat</li>
    </ul>
    <ul id="R_7.007.039">
        <li id="R_7.007.039ab">tacchiro rākṣasendrasya cakrotkṛttaṃ vibhīṣaṇam</li>
        <li id="R_7.007.039cd">papāta rudhirodgāri purā rāhuśiro yathā</li>
    </ul>
    <ul id="R_7.007.040">
        <li id="R_7.007.040ab">tataḥ suraiḥ susaṃhṛṣṭaiḥ sarvaprāṇasamīritaḥ</li>
        <li id="R_7.007.040cd">siṃhanādaravo muktaḥ sādhu deveti vādibhiḥ</li>
    </ul>
    <ul id="R_7.007.041">
        <li id="R_7.007.041ab">mālinaṃ nihataṃ dṛṣṭvā sumālī malyavān api</li>
        <li id="R_7.007.041cd">sabalau śokasaṃtaptau laṅkāṃ prati vidhāvitau</li>
    </ul>
    <ul id="R_7.007.042">
        <li id="R_7.007.042ab">garuḍas tu samāśvastaḥ saṃnivṛtya mahāmanāḥ</li>
        <li id="R_7.007.042cd">rākṣasān drāvayām āsa pakṣavātena kopitaḥ</li>
    </ul>
    <ul id="R_7.007.043">
        <li id="R_7.007.043ab">
            <seg type="pāda" n="a">nārāyaṇo 'pīṣuvarāśanībhir</seg>
            <seg type="pāda" n="b">vidārayām āsa dhanuḥpramuktaiḥ</seg>
        </li>
        <li id="R_7.007.043cd">
            <seg type="pāda" n="c">naktaṃcarān muktavidhūtakeśān</seg>
            <seg type="pāda" n="d">yathāśanībhiḥ sataḍinmahendraḥ</seg>
        </li>
    </ul>
    <ul id="R_7.007.044">
        <li id="R_7.007.044ab">
            <seg type="pāda" n="a">bhinnātapatraṃ patamānaśastraṃ</seg>
            <seg type="pāda" n="b">śarair apadhvastaviśīrṇadeham</seg>
        </li>
        <li id="R_7.007.044cd">
            <seg type="pāda" n="c">viniḥsṛtāntraṃ bhayalolanetraṃ</seg>
            <seg type="pāda" n="d">balaṃ tad unmattanibhaṃ babhūva</seg>
        </li>
    </ul>
    <ul id="R_7.007.045">
        <li id="R_7.007.045ab">
            <seg type="pāda" n="a">siṃhārditānām iva kuñjarāṇāṃ</seg>
            <seg type="pāda" n="b">niśācarāṇāṃ saha kuñjarāṇām</seg>
        </li>
        <li id="R_7.007.045cd">
            <seg type="pāda" n="c">ravāś ca vegāś ca samaṃ babhūvuḥ</seg>
            <seg type="pāda" n="d">purāṇasiṃhena vimarditānām</seg>
        </li>
    </ul>
    <ul id="R_7.007.046">
        <li id="R_7.007.046ab">
            <seg type="pāda" n="a">saṃchādyamānā haribāṇajālaiḥ</seg>
            <seg type="pāda" n="b">svabāṇajālāni samutsṛjantaḥ</seg>
        </li>
        <li id="R_7.007.046cd">
            <seg type="pāda" n="c">dhāvanti naktaṃcarakālameghā</seg>
            <seg type="pāda" n="d">vāyupraṇunnā iva kālameghāḥ</seg>
        </li>
    </ul>
    <ul id="R_7.007.047">
        <li id="R_7.007.047ab">
            <seg type="pāda" n="a">cakraprahārair vinikṛttaśīrṣāḥ</seg>
            <seg type="pāda" n="b">saṃcūrṇitāṅgāś ca gadāprahāraiḥ</seg>
        </li>
        <li id="R_7.007.047cd">
            <seg type="pāda" n="c">asiprahārair bahudhā vibhaktāḥ</seg>
            <seg type="pāda" n="d">patanti śailā iva rākṣasendrāḥ</seg>
        </li>
    </ul>
    <ul id="R_7.007.048">
        <li id="R_7.007.048ab">cakrakṛttāsyakamalā gadāsaṃcūrṇitorasaḥ</li>
        <li id="R_7.007.048cd">lāṅgalaglapitagrīvā musalair bhinnamastakāḥ</li>
    </ul>
    <ul id="R_7.007.049">
        <li id="R_7.007.049ab">ke cic caivāsinā chinnās tathānye śaratāḍitāḥ</li>
        <li id="R_7.007.049cd">nipetur ambarāt tūrṇaṃ rākṣasāḥ sāgarāmbhasi</li>
    </ul>
    <ul id="R_7.007.050">
        <li id="R_7.007.050ab">
            <seg type="pāda" n="a">tadāmbaraṃ vigalitahārakuṇḍalair</seg>
            <seg type="pāda" n="b">niśācarair nīlabalāhakopamaiḥ</seg>
        </li>
        <li id="R_7.007.050cd">
            <seg type="pāda" n="c">nipātyamānair dadṛśe nirantaraṃ</seg>
            <seg type="pāda" n="d">nipātyamānair iva nīlaparvataiḥ</seg>
        </li>
    </ul>
    <ul id="R_7.008.001">
        <li id="R_7.008.001ab">hanyamāne bale tasmin padmanābhena pṛṣṭhataḥ</li>
        <li id="R_7.008.001cd">mālyavān saṃnivṛtto 'tha velātiga ivārṇavaḥ</li>
    </ul>
    <ul id="R_7.008.002">
        <li id="R_7.008.002ab">saṃraktanayanaḥ kopāc calan maulir niśācaraḥ</li>
        <li id="R_7.008.002cd">padmanābham idaṃ prāha vacanaṃ paruṣaṃ tadā</li>
    </ul>
    <ul id="R_7.008.003">
        <li id="R_7.008.003ab">nārāyaṇa na jānīṣe kṣatradharmaṃ sanātanam</li>
        <li id="R_7.008.003cd">ayuddhamanaso bhagnān yo 'smān haṃsi yathetaraḥ</li>
    </ul>
    <ul id="R_7.008.004">
        <li id="R_7.008.004ab">parāṅmukhavadhaṃ pāpaṃ yaḥ karoti sureśvara</li>
        <li id="R_7.008.004cd">sa hantā na gataḥ svargaṃ labhate puṇyakarmaṇām</li>
    </ul>
    <ul id="R_7.008.005">
        <li id="R_7.008.005ab">yuddhaśraddhātha vā te 'sti śaṅkhacakragadādhara</li>
        <li id="R_7.008.005cd">ahaṃ sthito 'smi paśyāmi balaṃ darśaya yat tava</li>
    </ul>
    <ul id="R_7.008.006">
        <li id="R_7.008.006ab">uvāca rākṣasendraṃ taṃ devarājānujo balī</li>
        <li id="R_7.008.006cd">yuṣmatto bhayabhītānāṃ devānāṃ vai mayābhayam</li>
        <li id="R_7.008.006ef">rākṣasotsādanaṃ dattaṃ tad etad anupālyate</li>
    </ul>
    <ul id="R_7.008.007">
        <li id="R_7.008.007ab">prāṇair api priyaṃ kāryaṃ devānāṃ hi sadā mayā</li>
        <li id="R_7.008.007cd">so 'haṃ vo nihaniṣyāmi rasātalagatān api</li>
    </ul>
    <ul id="R_7.008.008">
        <li id="R_7.008.008ab">devam evaṃ bruvāṇaṃ tu raktāmburuhalocanam</li>
        <li id="R_7.008.008cd">śaktyā bibheda saṃkruddho rākṣasendro rarāsa ca</li>
    </ul>
    <ul id="R_7.008.009">
        <li id="R_7.008.009ab">mālyavad bhujanirmuktā śaktir ghaṇṭākṛtasvanā</li>
        <li id="R_7.008.009cd">harer urasi babhrāja meghastheva śatahradā</li>
    </ul>
    <ul id="R_7.008.010">
        <li id="R_7.008.010ab">tatas tām eva cotkṛṣya śaktiṃ śaktidharapriyaḥ</li>
        <li id="R_7.008.010cd">mālyavantaṃ samuddiśya cikṣepāmburuhekṣaṇaḥ</li>
    </ul>
    <ul id="R_7.008.011">
        <li id="R_7.008.011ab">skandotsṛṣṭeva sā śaktir govindakaraniḥsṛtā</li>
        <li id="R_7.008.011cd">kāṅkṣantī rākṣasaṃ prāyān maholkevāñjanācalam</li>
    </ul>
    <ul id="R_7.008.012">
        <li id="R_7.008.012ab">sā tasyorasi vistīrṇe hārabhāsāvabhāsite</li>
        <li id="R_7.008.012cd">apatad rākṣasendrasya girikūṭa ivāśaniḥ</li>
    </ul>
    <ul id="R_7.008.013">
        <li id="R_7.008.013ab">tayā bhinnatanutrāṇāḥ prāviśad vipulaṃ tamaḥ</li>
        <li id="R_7.008.013cd">mālyavān punar āśvastas tasthau girir ivācalaḥ</li>
    </ul>
    <ul id="R_7.008.014">
        <li id="R_7.008.014ab">tataḥ kārṣṇāyasaṃ śūlaṃ kaṇṭakair bahubhiś citam</li>
        <li id="R_7.008.014cd">pragṛhyābhyahanad devaṃ stanayor antare dṛḍham</li>
    </ul>
    <ul id="R_7.008.015">
        <li id="R_7.008.015ab">tathaiva raṇaraktas tu muṣṭinā vāsavānujam</li>
        <li id="R_7.008.015cd">tāḍayitvā dhanurmātram apakrānto niśācaraḥ</li>
    </ul>
    <ul id="R_7.008.016">
        <li id="R_7.008.016ab">tato 'mbare mahāñ śabdaḥ sādhu sādhv iti cotthitaḥ</li>
        <li id="R_7.008.016cd">āhatya rākṣaso viṣṇuṃ garuḍaṃ cāpy atāḍayat</li>
    </ul>
    <ul id="R_7.008.017">
        <li id="R_7.008.017ab">vainateyas tataḥ kruddhaḥ pakṣavātena rākṣasaṃ</li>
        <li id="R_7.008.017cd">vyapohad balavān vāyuḥ śuṣkaparṇacayaṃ yathā</li>
    </ul>
    <ul id="R_7.008.018">
        <li id="R_7.008.018ab">dvijendrapakṣavātena drāvitaṃ dṛśya pūrvajam</li>
        <li id="R_7.008.018cd">sumālī svabalaiḥ sārdhaṃ laṅkām abhimukho yayau</li>
    </ul>
    <ul id="R_7.008.019">
        <li id="R_7.008.019ab">pakṣavātabaloddhūto mālyavān api rākṣasaḥ</li>
        <li id="R_7.008.019cd">svabalena samāgamya yayau laṅkāṃ hriyā vṛtaḥ</li>
    </ul>
    <ul id="R_7.008.020">
        <li id="R_7.008.020ab">evaṃ te rākṣasā rāma hariṇā kamalekṣaṇa</li>
        <li id="R_7.008.020cd">bahuśaḥ saṃyuge bhagnā hatapravaranāyakāḥ</li>
    </ul>
    <ul id="R_7.008.021">
        <li id="R_7.008.021ab">aśaknuvantas te viṣṇuṃ pratiyoddhuṃ bhayārditāḥ</li>
        <li id="R_7.008.021cd">tyaktvā laṅkāṃ gatā vastuṃ pātālaṃ sahapatnayaḥ</li>
    </ul>
    <ul id="R_7.008.022">
        <li id="R_7.008.022ab">sumālinaṃ samāsādya rākṣasaṃ raghunandana</li>
        <li id="R_7.008.022cd">sthitāḥ prakhyātavīryās te vaṃśe sālakaṭaṅkaṭe</li>
    </ul>
    <ul id="R_7.008.023">
        <li id="R_7.008.023ab">ye tvayā nihatās te vai paulastyā nāma rākṣasāḥ</li>
        <li id="R_7.008.023cd">sumālī mālyavān mālī ye ca teṣāṃ puraḥsarāḥ</li>
        <li id="R_7.008.023ef">sarva ete mahābhāga rāvaṇād balavattarāḥ</li>
    </ul>
    <ul id="R_7.008.024">
        <li id="R_7.008.024ab">na cānyo rakṣasāṃ hantā sureṣv api puraṃjaya</li>
        <li id="R_7.008.024cd">ṛte nārāyaṇaṃ devaṃ śaṅkhacakragadādharam</li>
    </ul>
    <ul id="R_7.008.025">
        <li id="R_7.008.025ab">bhavān nārāyaṇo devaś caturbāhuḥ sanātanaḥ</li>
        <li id="R_7.008.025cd">rākṣasān hantum utpanno ajeyaḥ prabhur avyayaḥ</li>
    </ul>
    <ul id="R_7.009.001">
        <li id="R_7.009.001ab">kasya cit tv atha kālasya sumālī nāma rākṣasaḥ</li>
        <li id="R_7.009.001cd">rasātalān martyalokaṃ sarvaṃ vai vicacāra ha</li>
    </ul>
    <ul id="R_7.009.002">
        <li id="R_7.009.002ab">nīlajīmūtasaṃkāśas taptakāñcanakuṇḍalaḥ</li>
        <li id="R_7.009.002cd">kanyāṃ duhitaraṃ gṛhya vinā padmam iva śriyam</li>
        <li id="R_7.009.002ef">athāpaśyat sa gacchantaṃ puṣpakeṇa dhaneśvaram</li>
    </ul>
    <ul id="R_7.009.003">
        <li id="R_7.009.003ab">taṃ dṛṣṭvāmarasaṃkāśaṃ gacchantaṃ pāvakopamam</li>
        <li id="R_7.009.003cd">athābbravīt sutāṃ rakṣaḥ kaikasīṃ nāma nāmataḥ</li>
    </ul>
    <ul id="R_7.009.004">
        <li id="R_7.009.004ab">putri pradānakālo 'yaṃ yauvanaṃ te 'tivartate</li>
        <li id="R_7.009.004cd">tvatkṛte ca vayaṃ sarve yantritā dharmabuddhayaḥ</li>
    </ul>
    <ul id="R_7.009.005">
        <li id="R_7.009.005ab">tvaṃ hi sarvaguṇopetā śrīḥ sapadmeva putrike</li>
        <li id="R_7.009.005cd">pratyākhyānāc ca bhītais tvaṃ na varaiḥ pratigṛhyase</li>
    </ul>
    <ul id="R_7.009.006">
        <li id="R_7.009.006ab">kanyāpitṛtvaṃ duḥkhaṃ hi sarveṣāṃ mānakāṅkṣiṇām</li>
        <li id="R_7.009.006cd">na jñāyate ca kaḥ kanyāṃ varayed iti putrike</li>
    </ul>
    <ul id="R_7.009.007">
        <li id="R_7.009.007ab">mātuḥ kulaṃ pitṛkulaṃ yatra caiva pradīyate</li>
        <li id="R_7.009.007cd">kulatrayaṃ sadā kanyā saṃśaye sthāpya tiṣṭhati</li>
    </ul>
    <ul id="R_7.009.008">
        <li id="R_7.009.008ab">sā tvaṃ munivaraśreṣṭhaṃ prajāpatikulodbhavam</li>
        <li id="R_7.009.008cd">gaccha viśravasaṃ putri paulastyaṃ varaya svayam</li>
    </ul>
    <ul id="R_7.009.009">
        <li id="R_7.009.009ab">īdṛśās te bhaviṣyanti putrāḥ putri na saṃśayaḥ</li>
        <li id="R_7.009.009cd">tejasā bhāskarasamā yādṛśo 'yaṃ dhaneśvaraḥ</li>
    </ul>
    <ul id="R_7.009.010">
        <li id="R_7.009.010ab">etasminn antare rāma pulastyatanayo dvijaḥ</li>
        <li id="R_7.009.010cd">agnihotram upātiṣṭhac caturtha iva pāvakaḥ</li>
    </ul>
    <ul id="R_7.009.011">
        <li id="R_7.009.011ab">sā tu tāṃ dāruṇāṃ velām acintya pitṛgauravāt</li>
        <li id="R_7.009.011cd">upasṛtyāgratas tasya caraṇādhomukhī sthitā</li>
    </ul>
    <ul id="R_7.009.012">
        <li id="R_7.009.012ab">sa tu tāṃ vīkṣya suśroṇīṃ pūrṇacandranibhānanām</li>
        <li id="R_7.009.012cd">abravīt paramodāro dīpyamāna ivaujasā</li>
    </ul>
    <ul id="R_7.009.013">
        <li id="R_7.009.013ab">bhadre kasyāsi duhitā kuto vā tvam ihāgatā</li>
        <li id="R_7.009.013cd">kiṃ kāryaṃ kasya vā hetos tattvato brūhi śobhane</li>
    </ul>
    <ul id="R_7.009.014">
        <li id="R_7.009.014ab">evam uktā tu sā kanyā kṛtāñjalir athābravīt</li>
        <li id="R_7.009.014cd">ātmaprabhāvena mune jñātum arhasi me matam</li>
    </ul>
    <ul id="R_7.009.015">
        <li id="R_7.009.015ab">kiṃ tu viddhi hi māṃ brahmañ śāsanāt pitur āgatām</li>
        <li id="R_7.009.015cd">kaikasī nāma nāmnāhaṃ śeṣaṃ tvaṃ jñātum arhasi</li>
    </ul>
    <ul id="R_7.009.016">
        <li id="R_7.009.016ab">sa tu gatvā munir dhyānaṃ vākyam etad uvāca ha</li>
        <li id="R_7.009.016cd">vijñātaṃ te mayā bhadre kāraṇaṃ yan manogatam</li>
    </ul>
    <ul id="R_7.009.017">
        <li id="R_7.009.017ab">dāruṇāyāṃ tu velāyāṃ yasmāt tvaṃ mām upasthitā</li>
        <li id="R_7.009.017cd">śṛṇu tasmāt sutān bhadre yādṛśāñ janayiṣyasi</li>
    </ul>
    <ul id="R_7.009.018">
        <li id="R_7.009.018ab">dāruṇān dāruṇākārān dāruṇābhijanapriyān</li>
        <li id="R_7.009.018cd">prasaviṣyasi suśroṇi rākṣasān krūrakarmaṇaḥ</li>
    </ul>
    <ul id="R_7.009.019">
        <li id="R_7.009.019ab">sā tu tad vacanaṃ śrutvā praṇipatyābravīd vacaḥ</li>
        <li id="R_7.009.019cd">bhagavan nedṛśāḥ putrās tvatto 'rhā brahmayonitaḥ</li>
    </ul>
    <ul id="R_7.009.020">
        <li id="R_7.009.020ab">athābravīn munis tatra paścimo yas tavātmajaḥ</li>
        <li id="R_7.009.020cd">mama vaṃśānurūpaś ca dharmātmā ca bhaviṣyati</li>
    </ul>
    <ul id="R_7.009.021">
        <li id="R_7.009.021ab">evam uktā tu sā kanyā rāma kālena kena cit</li>
        <li id="R_7.009.021cd">janayām āsa bībhatsaṃ rakṣorūpaṃ sudāruṇam</li>
    </ul>
    <ul id="R_7.009.022">
        <li id="R_7.009.022ab">daśaśīrṣaṃ mahādaṃṣṭraṃ nīlāñjanacayopamam</li>
        <li id="R_7.009.022cd">tāmrauṣṭhaṃ viṃśatibhujaṃ mahāsyaṃ dīptamūrdhajam</li>
    </ul>
    <ul id="R_7.009.023">
        <li id="R_7.009.023ab">jātamātre tatas tasmin sajvālakavalāḥ śivāḥ</li>
        <li id="R_7.009.023cd">kravyādāś cāpasavyāni maṇḍalāni pracakrire</li>
    </ul>
    <ul id="R_7.009.024">
        <li id="R_7.009.024ab">vavarṣa rudhiraṃ devo meghāś ca kharanisvanāḥ</li>
        <li id="R_7.009.024cd">prababhau na ca khe sūryo maholkāś cāpatan bhuvi</li>
    </ul>
    <ul id="R_7.009.025">
        <li id="R_7.009.025ab">atha nāmākarot tasya pitāmahasamaḥ pitā</li>
        <li id="R_7.009.025cd">daśaśīrṣaḥ prasūto 'yaṃ daśagrīvo bhaviṣyati</li>
    </ul>
    <ul id="R_7.009.026">
        <li id="R_7.009.026ab">tasya tv anantaraṃ jātaḥ kumbhakarṇo mahābalaḥ</li>
        <li id="R_7.009.026cd">pramāṇād yasya vipulaṃ pramāṇaṃ neha vidyate</li>
    </ul>
    <ul id="R_7.009.027">
        <li id="R_7.009.027ab">tataḥ śūrpaṇakhā nāma saṃjajñe vikṛtānanā</li>
        <li id="R_7.009.027cd">vibhīṣaṇaś ca dharmātmā kaikasyāḥ paścimaḥ sutaḥ</li>
    </ul>
    <ul id="R_7.009.028">
        <li id="R_7.009.028ab">te tu tatra mahāraṇye vavṛdhuḥ sumahaujasaḥ</li>
        <li id="R_7.009.028cd">teṣāṃ krūro daśagrīvo lokodvegakaro 'bhavat</li>
    </ul>
    <ul id="R_7.009.029">
        <li id="R_7.009.029ab">kumbhakarṇaḥ pramattas tu maharṣīn dharmasaṃśritān</li>
        <li id="R_7.009.029cd">trailokyaṃ trāsayan duṣṭo bhakṣayan vicacāra ha</li>
    </ul>
    <ul id="R_7.009.030">
        <li id="R_7.009.030ab">vibhīṣaṇas tu dharmātmā nityaṃ dharmapathe sthitaḥ</li>
        <li id="R_7.009.030cd">svādhyāyaniyatāhāra uvāsa niyatendriyaḥ</li>
    </ul>
    <ul id="R_7.009.031">
        <li id="R_7.009.031ab">atha vitteśvaro devas tatra kālena kena cit</li>
        <li id="R_7.009.031cd">āgacchat pitaraṃ draṣṭuṃ puṣpakeṇa mahaujasaṃ</li>
    </ul>
    <ul id="R_7.009.032">
        <li id="R_7.009.032ab">taṃ dṛṣṭvā kaikasī tatra jvalantam iva tejasā</li>
        <li id="R_7.009.032cd">āsthāya rākṣasīṃ buddhiṃ daśagrīvam uvāca ha</li>
    </ul>
    <ul id="R_7.009.033">
        <li id="R_7.009.033ab">putravaiśravaṇaṃ paśya bhrātaraṃ tejasā vṛtam</li>
        <li id="R_7.009.033cd">bhrātṛbhāve same cāpi paśyātmānaṃ tvam īdṛśam</li>
    </ul>
    <ul id="R_7.009.034">
        <li id="R_7.009.034ab">daśagrīva tathā yatnaṃ kuruṣvāmitavikrama</li>
        <li id="R_7.009.034cd">yathā bhavasi me putra śīghraṃ vaiśvaraṇopamaḥ</li>
    </ul>
    <ul id="R_7.009.035">
        <li id="R_7.009.035ab">mātus tad vacanaṃ śrutvā daśagrīvaḥ pratāpavān</li>
        <li id="R_7.009.035cd">amarṣam atulaṃ lebhe pratijñāṃ cākarot tadā</li>
    </ul>
    <ul id="R_7.009.036">
        <li id="R_7.009.036ab">satyaṃ te pratijānāmi tulyo bhrātrādhiko 'pi vā</li>
        <li id="R_7.009.036cd">bhaviṣyāmy acirān mātaḥ saṃtāpaṃ tyaja hṛdgatam</li>
    </ul>
    <ul id="R_7.009.037">
        <li id="R_7.009.037ab">tataḥ krodhena tenaiva daśagrīvaḥ sahānujaḥ</li>
        <li id="R_7.009.037cd">prāpsyāmi tapasā kāmam iti kṛtvādhyavasya ca</li>
        <li id="R_7.009.037ef">āgacchad ātmasiddhyarthaṃ gokarṇasyāśramaṃ śubham</li>
    </ul>
    <ul id="R_7.010.001">
        <li id="R_7.010.001ab">athābravīd dvijaṃ rāmaḥ kathaṃ te bhrātaro vane</li>
        <li id="R_7.010.001cd">kīdṛśaṃ tu tadā brahmaṃs tapaś cerur mahāvratāḥ</li>
    </ul>
    <ul id="R_7.010.002">
        <li id="R_7.010.002ab">agastyas tv abravīt tatra rāmaṃ prayata mānasaṃ</li>
        <li id="R_7.010.002cd">tāṃs tān dharmavidhīṃs tatra bhrātaras te samāviśan</li>
    </ul>
    <ul id="R_7.010.003">
        <li id="R_7.010.003ab">kumbhakarṇas tadā yatto nityaṃ dharmaparāyaṇaḥ</li>
        <li id="R_7.010.003cd">tatāpa graiṣmike kāle pañcasv agniṣv avasthitaḥ</li>
    </ul>
    <ul id="R_7.010.004">
        <li id="R_7.010.004ab">varṣe meghodakaklinno vīrāsanam asevata</li>
        <li id="R_7.010.004cd">nityaṃ ca śaiśire kāle jalamadhyapratiśrayaḥ</li>
    </ul>
    <ul id="R_7.010.005">
        <li id="R_7.010.005ab">evaṃ varṣasahasrāṇi daśa tasyāticakramuḥ</li>
        <li id="R_7.010.005cd">dharme prayatamānasya satpathe niṣṭhitasya ca</li>
    </ul>
    <ul id="R_7.010.006">
        <li id="R_7.010.006ab">vibhīṣaṇas tu dharmātmā nityaṃ dharmaparaḥ śuciḥ</li>
        <li id="R_7.010.006cd">pañcavarṣasahasrāṇi pādenaikena tasthivān</li>
    </ul>
    <ul id="R_7.010.007">
        <li id="R_7.010.007ab">samāpte niyame tasya nanṛtuś cāpsarogaṇāḥ</li>
        <li id="R_7.010.007cd">papāta puṣpavarṣaṃ ca kṣubhitāś cāpi devatāḥ</li>
    </ul>
    <ul id="R_7.010.008">
        <li id="R_7.010.008ab">pañcavarṣasahasrāṇi sūryaṃ caivānvavartata</li>
        <li id="R_7.010.008cd">tasthau cordhvaśiro bāhuḥ svādhyāyadhṛtamānasaḥ</li>
    </ul>
    <ul id="R_7.010.009">
        <li id="R_7.010.009ab">evaṃ vibhīṣaṇasyāpi gatāni niyatātmanaḥ</li>
        <li id="R_7.010.009cd">daśavarṣasahasrāṇi svargasthasyeva nandane</li>
    </ul>
    <ul id="R_7.010.010">
        <li id="R_7.010.010ab">daśavarṣasahasraṃ tu nirāhāro daśānanaḥ</li>
        <li id="R_7.010.010cd">pūrṇe varṣasahasre tu śiraś cāgnau juhāva saḥ</li>
    </ul>
    <ul id="R_7.010.011">
        <li id="R_7.010.011ab">evaṃ varṣasahasrāṇi nava tasyāticakramuḥ</li>
        <li id="R_7.010.011cd">śirāṃsi nava cāpy asya praviṣṭāni hutāśanam</li>
    </ul>
    <ul id="R_7.010.012">
        <li id="R_7.010.012ab">atha varṣasahasre tu daśame daśamaṃ śiraḥ</li>
        <li id="R_7.010.012cd">chettukāmaḥ sa dharmātmā prāptaś cātra pitāmahaḥ</li>
    </ul>
    <ul id="R_7.010.013">
        <li id="R_7.010.013ab">pitāmahas tu suprītaḥ sārdhaṃ devair upasthitaḥ</li>
        <li id="R_7.010.013cd">vatsa vatsa daśagrīva prīto 'smīty abhyabhāṣata</li>
    </ul>
    <ul id="R_7.010.014">
        <li id="R_7.010.014ab">śīghraṃ varaya dharmajña varo yas te 'bhikāṅkṣitaḥ</li>
        <li id="R_7.010.014cd">kiṃ te kāmaṃ karomy adya na vṛthā te pariśramaḥ</li>
    </ul>
    <ul id="R_7.010.015">
        <li id="R_7.010.015ab">tato 'bravīd daśagrīvaḥ prahṛṣṭenāntarātmanā</li>
        <li id="R_7.010.015cd">praṇamya śirasā devaṃ harṣagadgadayā girā</li>
    </ul>
    <ul id="R_7.010.016">
        <li id="R_7.010.016ab">bhagavan prāṇināṃ nityaṃ nānyatra maraṇād bhayam</li>
        <li id="R_7.010.016cd">nāsti mṛtyusamaḥ śatrur amaratvam ato vṛṇe</li>
    </ul>
    <ul id="R_7.010.017">
        <li id="R_7.010.017ab">suparṇanāgayakṣāṇāṃ daityadānavarakṣasām</li>
        <li id="R_7.010.017cd">avadhyaḥ syāṃ prajādhyakṣa devatānāṃ ca śāśvatam</li>
    </ul>
    <ul id="R_7.010.018">
        <li id="R_7.010.018ab">na hi cintā mamānyeṣu prāṇiṣv amarapūjita</li>
        <li id="R_7.010.018cd">tṛṇabhūtā hi me sarve prāṇino mānuṣādayaḥ</li>
    </ul>
    <ul id="R_7.010.019">
        <li id="R_7.010.019ab">evam uktas tu dharmātmā daśagrīveṇa rakṣasā</li>
        <li id="R_7.010.019cd">uvāca vacanaṃ rāma saha devaiḥ pitāmahaḥ</li>
    </ul>
    <ul id="R_7.010.020">
        <li id="R_7.010.020ab">bhaviṣyaty evam evaitat tava rākṣasapuṃgava</li>
        <li id="R_7.010.020cd">śṛṇu cāpi vaco bhūyaḥ prītasyeha śubhaṃ mama</li>
    </ul>
    <ul id="R_7.010.021">
        <li id="R_7.010.021ab">hutāni yāni śīrṣāṇi pūrvam agnau tvayānagha</li>
        <li id="R_7.010.021cd">punas tāni bhaviṣyanti tathaiva tava rākṣasa</li>
    </ul>
    <ul id="R_7.010.022">
        <li id="R_7.010.022ab">evaṃ pitāmahoktasya daśagrīvasya rakṣasaḥ</li>
        <li id="R_7.010.022cd">agnau hutāni śīrṣāṇi yāni tāny utthitāni vai</li>
    </ul>
    <ul id="R_7.010.023">
        <li id="R_7.010.023ab">evam uktvvā tu taṃ rāma daśagrīvaṃ prajāpatiḥ</li>
        <li id="R_7.010.023cd">vibhīṣaṇam athovāca vākyaṃ lokapitāmahaḥ</li>
    </ul>
    <ul id="R_7.010.024">
        <li id="R_7.010.024ab">vibhīṣaṇa tvayā vatsa dharmasaṃhitabuddhinā</li>
        <li id="R_7.010.024cd">parituṣṭo 'smi dharmajña varaṃ varaya suvrata</li>
    </ul>
    <ul id="R_7.010.025">
        <li id="R_7.010.025ab">vibhīṣaṇas tu dharmātmā vacanaṃ prāha sāñjaliḥ</li>
        <li id="R_7.010.025cd">vṛtaḥ sarvaguṇair nityaṃ candramā iva raśmibhiḥ</li>
    </ul>
    <ul id="R_7.010.026">
        <li id="R_7.010.026ab">bhagavan kṛtakṛtyo 'haṃ yan me lokaguruḥ svayam</li>
        <li id="R_7.010.026cd">prīto yadi tvaṃ dātavyaṃ varaṃ me śṛṇu suvrata</li>
    </ul>
    <ul id="R_7.010.027">
        <li id="R_7.010.027ab">yā yā me jāyate buddhir yeṣu yeṣv āśrameṣv iha</li>
        <li id="R_7.010.027cd">sā sā bhavatu dharmiṣṭhā taṃ taṃ dharmaṃ ca pālaye</li>
    </ul>
    <ul id="R_7.010.028">
        <li id="R_7.010.028ab">eṣa me paramodāra varaḥ paramako mataḥ</li>
        <li id="R_7.010.028cd">na hi dharmābhiraktānāṃ loke kiṃ cana durlabham</li>
    </ul>
    <ul id="R_7.010.029">
        <li id="R_7.010.029ab">atha prajāpatiḥ prīto vibhīṣaṇam uvāca ha</li>
        <li id="R_7.010.029cd">dharmiṣṭhas tvaṃ yathā vatsa tathā caitad bhaviṣyati</li>
    </ul>
    <ul id="R_7.010.030">
        <li id="R_7.010.030ab">yasmād rākṣasayonau te jātasyāmitrakarṣaṇa</li>
        <li id="R_7.010.030cd">nādharme jāyate buddhir amaratvaṃ dadāmi te</li>
    </ul>
    <ul id="R_7.010.031">
        <li id="R_7.010.031ab">kumbhakarṇāya tu varaṃ prayacchantam ariṃdama</li>
        <li id="R_7.010.031cd">prajāpatiṃ surāḥ sarve vākyaṃ prāñjalayo 'bruvan</li>
    </ul>
    <ul id="R_7.010.032">
        <li id="R_7.010.032ab">na tāvat kumbhakarṇāya pradātavyo varas tvayā</li>
        <li id="R_7.010.032cd">jānīṣe hi yathā lokāṃs trāsayaty eṣa durmatiḥ</li>
    </ul>
    <ul id="R_7.010.033">
        <li id="R_7.010.033ab">nandane 'psarasaḥ sapta mahendrānucarā daśa</li>
        <li id="R_7.010.033cd">anena bhakṣitā brahman ṛṣayo mānuṣās tathā</li>
    </ul>
    <ul id="R_7.010.034">
        <li id="R_7.010.034ab">varavyājena moho 'smai dīyatām amitaprabha</li>
        <li id="R_7.010.034cd">lokānāṃ svasti caiva syād bhaved asya ca saṃnatiḥ</li>
    </ul>
    <ul id="R_7.010.035">
        <li id="R_7.010.035ab">evam uktaḥ surair brahmācintayat padmasaṃbhavaḥ</li>
        <li id="R_7.010.035cd">cintitā copatasthe 'sya pārśvaṃ devī sarasvatī</li>
    </ul>
    <ul id="R_7.010.036">
        <li id="R_7.010.036ab">prāñjaliḥ sā tu parśvasthā prāha vākyaṃ sarasvatī</li>
        <li id="R_7.010.036cd">iyam asmy āgatā devakiṃ kāryaṃ karavāṇy aham</li>
    </ul>
    <ul id="R_7.010.037">
        <li id="R_7.010.037ab">prajāpatis tu tāṃ prāptāṃ prāha vākyaṃ sarasvatīm</li>
        <li id="R_7.010.037cd">vāṇi tvaṃ rākṣasendrasya bhava yā devatepsitā</li>
    </ul>
    <ul id="R_7.010.038">
        <li id="R_7.010.038ab">tathety uktvā praviṣṭā sā prajāpatir athābravīt</li>
        <li id="R_7.010.038cd">kumbhakarṇa mahābāho varaṃ varaya yo mataḥ</li>
    </ul>
    <ul id="R_7.010.039">
        <li id="R_7.010.039ab">kumbhakarṇas tu tad vākyaṃ śrutvā vacanam abravīt</li>
        <li id="R_7.010.039cd">svaptuṃ varṣāṇy anekāni devadeva mamepsitam</li>
    </ul>
    <ul id="R_7.010.040">
        <li id="R_7.010.040ab">evam astv iti taṃ coktvā saha devaiḥ pitāmahaḥ</li>
        <li id="R_7.010.040cd">devī sarasvatī caiva muktvā taṃ prayayau divam</li>
    </ul>
    <ul id="R_7.010.041">
        <li id="R_7.010.041ab">kumbhakarṇas tu duṣṭātmā cintayām āsa duḥkhitaḥ</li>
        <li id="R_7.010.041cd">kīrdṛśaṃ kiṃ nv idaṃ vākyaṃ mamādya vadanāc cyutam</li>
    </ul>
    <ul id="R_7.010.042">
        <li id="R_7.010.042ab">evaṃ labdhavarāḥ sarve bhrātaro dīptatejasaḥ</li>
        <li id="R_7.010.042cd">śleṣmātakavanaṃ gatvā tatra te nyavasan sukham</li>
    </ul>
    <ul id="R_7.011.001">
        <li id="R_7.011.001ab">sumālī varalabdhāṃs tu jñātvā tān vai niśācarān</li>
        <li id="R_7.011.001cd">udatiṣṭhad bhayaṃ tyaktvā sānugaḥ sa rasātalāt</li>
    </ul>
    <ul id="R_7.011.002">
        <li id="R_7.011.002ab">mārīcaś ca prahastaś ca virūpākṣo mahodaraḥ</li>
        <li id="R_7.011.002cd">udatiṣṭhan susaṃrabdhāḥ sacivās tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_7.011.003">
        <li id="R_7.011.003ab">sumālī caiva taiḥ sarvair vṛto rākṣasapuṃgavaiḥ</li>
        <li id="R_7.011.003cd">abhigamya daśagrīvaṃ pariṣvajyedam abravīt</li>
    </ul>
    <ul id="R_7.011.004">
        <li id="R_7.011.004ab">diṣṭyā te putrasaṃprāptaś cintito 'yaṃ manorathaḥ</li>
        <li id="R_7.011.004cd">yas tvaṃ tribhuvaṇaśreṣṭhāl labdhavān varam īdṛśam</li>
    </ul>
    <ul id="R_7.011.005">
        <li id="R_7.011.005ab">yatkṛte ca vayaṃ laṅkāṃ tyaktvā yātā rasātalam</li>
        <li id="R_7.011.005cd">tad gataṃ no mahābāho mahad viṣṇukṛtaṃ bhayam</li>
    </ul>
    <ul id="R_7.011.006">
        <li id="R_7.011.006ab">asakṛt tena bhagnā hi parityajya svam ālayam</li>
        <li id="R_7.011.006cd">vidrutāḥ sahitāḥ sarve praviṣṭāḥ sma rasātalam</li>
    </ul>
    <ul id="R_7.011.007">
        <li id="R_7.011.007ab">asmadīyā ca laṅkeyaṃ nagarī rākṣasoṣitā</li>
        <li id="R_7.011.007cd">niveśitā tava bhrātrā dhanādhyakṣeṇa dhīmatā</li>
    </ul>
    <ul id="R_7.011.008">
        <li id="R_7.011.008ab">yadi nāmātra śakyaṃ syāt sāmnā dānena vānagha</li>
        <li id="R_7.011.008cd">tarasā vā mahābāho pratyānetuṃ kṛtaṃ bhavet</li>
    </ul>
    <ul id="R_7.011.009">
        <li id="R_7.011.009ab">tvaṃ ca laṅkeśvaras tāta bhaviṣyasi na saṃśayaḥ</li>
        <li id="R_7.011.009cd">sarveṣāṃ naḥ prabhuś caiva bhaviṣyasi mahābala</li>
    </ul>
    <ul id="R_7.011.010">
        <li id="R_7.011.010ab">athābravīd daśagrīvo mātāmaham upasthitam</li>
        <li id="R_7.011.010cd">vitteśo gurur asmākaṃ nārhasy evaṃ prabhāṣitum</li>
    </ul>
    <ul id="R_7.011.011">
        <li id="R_7.011.011ab">uktavantaṃ tathā vākyaṃ daśagrīvaṃ niśācaraḥ</li>
        <li id="R_7.011.011cd">prahastaḥ praśritaṃ vākyam idam āha sakāraṇam</li>
    </ul>
    <ul id="R_7.011.012">
        <li id="R_7.011.012ab">daśagrīva mahābāho nārhas tvaṃ vaktum īdṛśam</li>
        <li id="R_7.011.012cd">saubhrātraṃ nāsti śūrāṇāṃ śṛṇu cedaṃ vaco mama</li>
    </ul>
    <ul id="R_7.011.013">
        <li id="R_7.011.013ab">aditiś ca ditiś caiva bhaginyau sahite kila</li>
        <li id="R_7.011.013cd">bhārye paramarūpiṇyau kaśyapasya prajāpateḥ</li>
    </ul>
    <ul id="R_7.011.014">
        <li id="R_7.011.014ab">aditir janayām āsa devāṃs tribhuvaṇeśvarān</li>
        <li id="R_7.011.014cd">ditis tv ajanayad daityān kaśyapasyātmasaṃbhavān</li>
    </ul>
    <ul id="R_7.011.015">
        <li id="R_7.011.015ab">daityānāṃ kila dharmajña pureyaṃ savanārṇavā</li>
        <li id="R_7.011.015cd">saparvatā mahī vīra te 'bhavan prabhaviṣṇavaḥ</li>
    </ul>
    <ul id="R_7.011.016">
        <li id="R_7.011.016ab">nihatya tāṃs tu samare viṣṇunā prabhaviṣṇunā</li>
        <li id="R_7.011.016cd">devānāṃ vaśam ānītaṃ trailokyam idam avyayam</li>
    </ul>
    <ul id="R_7.011.017">
        <li id="R_7.011.017ab">naitad eko bhavān eva kariṣyati viparyayam</li>
        <li id="R_7.011.017cd">surair ācaritaṃ pūrvaṃ kuruṣvaitad vaco mama</li>
    </ul>
    <ul id="R_7.011.018">
        <li id="R_7.011.018ab">evam ukto daśagrīvaḥ prahastena durātmanā</li>
        <li id="R_7.011.018cd">cintayitvā muhūrtaṃ vai bāḍham ity eva so 'bravīt</li>
    </ul>
    <ul id="R_7.011.019">
        <li id="R_7.011.019ab">sa tu tenaiva harṣeṇa tasminn ahani vīryavān</li>
        <li id="R_7.011.019cd">vanaṃ gato daśagrīvaḥ saha taiḥ kṣaṇadācaraiḥ</li>
    </ul>
    <ul id="R_7.011.020">
        <li id="R_7.011.020ab">trikūṭasthaḥ sa tu tadā daśagrīvo niśācaraḥ</li>
        <li id="R_7.011.020cd">preṣayām āsa dautyena prahastaṃ vākyakovidam</li>
    </ul>
    <ul id="R_7.011.021">
        <li id="R_7.011.021ab">prahasta śīghraṃ gatvā tvaṃ brūhi nairṛtapuṃgavam</li>
        <li id="R_7.011.021cd">vacanān mama vitteśaṃ sāmapūrvam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_7.011.022">
        <li id="R_7.011.022ab">iyaṃ laṅkā purī rājan rākṣasānāṃ mahātmanām</li>
        <li id="R_7.011.022cd">tvayā niveśitā saumya naitad yuktaṃ tavānagha</li>
    </ul>
    <ul id="R_7.011.023">
        <li id="R_7.011.023ab">tad bhavān yadi sāmnaitāṃ dadyād atulavikrama</li>
        <li id="R_7.011.023cd">kṛtā bhaven mama prītir dharmaś caivānupālitaḥ</li>
    </ul>
    <ul id="R_7.011.024">
        <li id="R_7.011.024ab">ity uktaḥ sa tadā gatvā prahasto vākyakovidaḥ</li>
        <li id="R_7.011.024cd">daśagrīvavacaḥ sarvaṃ vitteśāya nyavedayat</li>
    </ul>
    <ul id="R_7.011.025">
        <li id="R_7.011.025ab">prahastād api saṃśrutya devo vaiśravaṇo vacaḥ</li>
        <li id="R_7.011.025cd">pratyuvāca prahastaṃ taṃ vākyaṃ vākyaviśāradaḥ</li>
    </ul>
    <ul id="R_7.011.026">
        <li id="R_7.011.026ab">brūhi gaccha daśagrīvaṃ purī rājyaṃ ca yan mama</li>
        <li id="R_7.011.026cd">tavāpy etan mahābāho bhuṅkṣvaitad dhatakaṇṭakam</li>
    </ul>
    <ul id="R_7.011.027">
        <li id="R_7.011.027ab">sarvaṃ kartāsmi bhadraṃ te rākṣaseśa vaco 'cirāt</li>
        <li id="R_7.011.027cd">kiṃ tu tāvat pratīkṣasva pitur yāvan nivedaye</li>
    </ul>
    <ul id="R_7.011.028">
        <li id="R_7.011.028ab">evam uktvā dhanādhyakṣo jagāma pitur antikam</li>
        <li id="R_7.011.028cd">abhivādya guruṃ prāha rāvaṇasya yadīpsitam</li>
    </ul>
    <ul id="R_7.011.029">
        <li id="R_7.011.029ab">eṣa tāta daśagrīvo dūtaṃ preṣitavān mama</li>
        <li id="R_7.011.029cd">dīyatāṃ nagarī laṅkā pūrvaṃ rakṣogaṇoṣitā</li>
        <li id="R_7.011.029ef">mayātra yad anuṣṭheyaṃ tan mamācakṣva suvrata</li>
    </ul>
    <ul id="R_7.011.030">
        <li id="R_7.011.030ab">brahmarṣis tv evam ukto 'sau viśravā munipuṃgavaḥ</li>
        <li id="R_7.011.030cd">uvāca dhanadaṃ vākyaṃ śṛṇu putra vaco mama</li>
    </ul>
    <ul id="R_7.011.031">
        <li id="R_7.011.031ab">daśagrīvo mahābāhur uktavān mama saṃnidhau</li>
        <li id="R_7.011.031cd">mayā nirbhartsitaś cāsīd bahudhoktaḥ sudurmatiḥ</li>
    </ul>
    <ul id="R_7.011.032">
        <li id="R_7.011.032ab">sa krodhena mayā cokto dhvaṃsasveti punaḥ punaḥ</li>
        <li id="R_7.011.032cd">śreyo'bhiyuktaṃ dharmyaṃ ca śṛṇu putra vaco mama</li>
    </ul>
    <ul id="R_7.011.033">
        <li id="R_7.011.033ab">varapradānasaṃmūḍho mānyāmānyaṃ sudurmatiḥ</li>
        <li id="R_7.011.033cd">na vetti mama śāpāc ca prakṛtiṃ dāruṇāṃ gataḥ</li>
    </ul>
    <ul id="R_7.011.034">
        <li id="R_7.011.034ab">tasmād gaccha mahābāho kailāsaṃ dharaṇīdharam</li>
        <li id="R_7.011.034cd">niveśaya nivāsārthaṃ tyaja laṅkāṃ sahānugaḥ</li>
    </ul>
    <ul id="R_7.011.035">
        <li id="R_7.011.035ab">tatra mandākinī ramyā nadīnāṃ pravarā nadī</li>
        <li id="R_7.011.035cd">kāñcanaiḥ sūryasaṃkāśaiḥ paṅkajaiḥ saṃvṛtodakā</li>
    </ul>
    <ul id="R_7.011.036">
        <li id="R_7.011.036ab">na hi kṣamaṃ tvayā tena vairaṃ dhanadarakṣasā</li>
        <li id="R_7.011.036cd">jānīṣe hi yathānena labdhaḥ paramako varaḥ</li>
    </ul>
    <ul id="R_7.011.037">
        <li id="R_7.011.037ab">evam ukto gṛhītvā tu tad vacaḥ pitṛgauravāt</li>
        <li id="R_7.011.037cd">sadāra pauraḥ sāmātyaḥ savāhanadhano gataḥ</li>
    </ul>
    <ul id="R_7.011.038">
        <li id="R_7.011.038ab">prahastas tu daśagrīvaṃ gatvā sarvaṃ nyavedayat</li>
        <li id="R_7.011.038cd">śūnyā sā nagarī laṅkā triṃśadyojanam āyatā</li>
        <li id="R_7.011.038ef">praviśya tāṃ sahāsmābhiḥ svadharmaṃ tatra pālaya</li>
    </ul>
    <ul id="R_7.011.039">
        <li id="R_7.011.039ab">evam uktaḥ prahastena rāvaṇo rākṣasas tadā</li>
        <li id="R_7.011.039cd">viveśa nagarīṃ laṅkāṃ sabhrātā sabalānugaḥ</li>
    </ul>
    <ul id="R_7.011.040">
        <li id="R_7.011.040ab">
            <seg type="pāda" n="a">sa cābhiṣiktaḥ kṣaṇadācarais tadā</seg>
            <seg type="pāda" n="b">niveśayām āsa purīṃ daśānanaḥ</seg>
        </li>
        <li id="R_7.011.040cd">
            <seg type="pāda" n="c">nikāmapūrṇā ca babhūva sā purī</seg>
            <seg type="pāda" n="d">niśācarair nīlabalāhakopamaiḥ</seg>
        </li>
    </ul>
    <ul id="R_7.011.041">
        <li id="R_7.011.041ab">
            <seg type="pāda" n="a">dhaneśvaras tv atha pitṛvākyagauravān</seg>
            <seg type="pāda" n="b">nyaveśayac chaśivimale girau purīm</seg>
        </li>
        <li id="R_7.011.041cd">
            <seg type="pāda" n="c">svalaṃkṛtair bhavanavarair vibhūṣitāṃ</seg>
            <seg type="pāda" n="d">puraṃdarasyeva tadāmarāvatīm</seg>
        </li>
    </ul>
    <ul id="R_7.012.001">
        <li id="R_7.012.001ab">rākṣasendro 'bhiṣiktas tu bhrātṛbhyāṃ sahitas tadā</li>
        <li id="R_7.012.001cd">tataḥ pradānaṃ rākṣasyā bhaginyāḥ samacintayat</li>
    </ul>
    <ul id="R_7.012.002">
        <li id="R_7.012.002ab">dadau tāṃ kālakeyāya dānavendrāya rākṣasīm</li>
        <li id="R_7.012.002cd">svasāṃ śūrpaṇakhāṃ nāma vidyujjihvāya nāmataḥ</li>
    </ul>
    <ul id="R_7.012.003">
        <li id="R_7.012.003ab">atha dattvā svasāraṃ sa mṛgayāṃ paryaṭan nṛpaḥ</li>
        <li id="R_7.012.003cd">tatrāpaśyat tato rāma mayaṃ nāma diteḥ sutam</li>
    </ul>
    <ul id="R_7.012.004">
        <li id="R_7.012.004ab">kanyāsahāyaṃ taṃ dṛṣṭvā daśagrīvo niśācaraḥ</li>
        <li id="R_7.012.004cd">apṛcchat ko bhavan eko nirmanuṣya mṛge vane</li>
    </ul>
    <ul id="R_7.012.005">
        <li id="R_7.012.005ab">mayas tv athābravīd rāma pṛcchantaṃ taṃ niśācaram</li>
        <li id="R_7.012.005cd">śrūyatāṃ sarvam ākhyāsye yathāvṛttam idaṃ mama</li>
    </ul>
    <ul id="R_7.012.006">
        <li id="R_7.012.006ab">hemā nāmāpsarās tāta śrutapūrvā yadi tvayā</li>
        <li id="R_7.012.006cd">daivatair mama sā dattā paulomīva śatakratoḥ</li>
    </ul>
    <ul id="R_7.012.007">
        <li id="R_7.012.007ab">tasyāṃ saktamanās tāta pañcavarṣaśatāny aham</li>
        <li id="R_7.012.007cd">sā ca daivata kāryeṇa gatā varṣaṃ caturdaśam</li>
    </ul>
    <ul id="R_7.012.008">
        <li id="R_7.012.008ab">tasyāḥ kṛte ca hemāyāḥ sarvaṃ hemapuraṃ mayā</li>
        <li id="R_7.012.008cd">vajravaidūryacitraṃ ca māyayā nirmitaṃ tadā</li>
    </ul>
    <ul id="R_7.012.009">
        <li id="R_7.012.009ab">tatrāham aratiṃ vindaṃs tayā hīnaḥ suduḥkhitaḥ</li>
        <li id="R_7.012.009cd">tasmāt purād duhitaraṃ gṛhītvā vanam āgataḥ</li>
    </ul>
    <ul id="R_7.012.010">
        <li id="R_7.012.010ab">iyaṃ mamātmajā rājaṃs tasyāḥ kukṣau vivardhitā</li>
        <li id="R_7.012.010cd">bhartāram anayā sārdham asyāḥ prāpto 'smi mārgitum</li>
    </ul>
    <ul id="R_7.012.011">
        <li id="R_7.012.011ab">kanyāpitṛtvaṃ duḥkhaṃ hi narāṇāṃ mānakāṅkṣiṇām</li>
        <li id="R_7.012.011cd">kanyā hi dve kule nityaṃ saṃśaye sthāpya tiṣṭhati</li>
    </ul>
    <ul id="R_7.012.012">
        <li id="R_7.012.012ab">dvau sutau tu mama tv asyāṃ bhāryāyāṃ saṃbabhūvatuḥ</li>
        <li id="R_7.012.012cd">māyāvī prathamas tāta dundubhis tadanantaram</li>
    </ul>
    <ul id="R_7.012.013">
        <li id="R_7.012.013ab">etat te sarvam ākhyātaṃ yāthātathyena pṛcchataḥ</li>
        <li id="R_7.012.013cd">tvām idānīṃ kathaṃ tāta jānīyāṃ ko bhavān iti</li>
    </ul>
    <ul id="R_7.012.014">
        <li id="R_7.012.014ab">evam ukto rākṣasendro vinītam idam abravīt</li>
        <li id="R_7.012.014cd">ahaṃ paulastya tanayo daśagrīvaś ca nāmataḥ</li>
    </ul>
    <ul id="R_7.012.015">
        <li id="R_7.012.015ab">brahmarṣes taṃ sutaṃ jñātvā mayo harṣam upāgataḥ</li>
        <li id="R_7.012.015cd">dātuṃ duhitaraṃ tasya rocayām āsa tatra vai</li>
    </ul>
    <ul id="R_7.012.016">
        <li id="R_7.012.016ab">prahasan prāha daityendro rākṣasendram idaṃ vacaḥ</li>
        <li id="R_7.012.016cd">iyaṃ mamātmajā rājan hemayāpsarasā dhṛtā</li>
        <li id="R_7.012.016ef">kanyā mandodarī nāma patnyarthaṃ pratigṛhyatām</li>
    </ul>
    <ul id="R_7.012.017">
        <li id="R_7.012.017ab">bāḍham ity eva taṃ rāma daśagrīvo 'bhyabhāṣata</li>
        <li id="R_7.012.017cd">prajvālya tatra caivāgnim akarot pāṇisaṃgraham</li>
    </ul>
    <ul id="R_7.012.018">
        <li id="R_7.012.018ab">na hi tasya mayo rāma śāpābhijñas tapodhanāt</li>
        <li id="R_7.012.018cd">viditvā tena sā dattā tasya paitāmahaṃ kulam</li>
    </ul>
    <ul id="R_7.012.019">
        <li id="R_7.012.019ab">amoghāṃ tasya śaktiṃ ca pradadau paramādbhutām</li>
        <li id="R_7.012.019cd">pareṇa tapasā labdhāṃ jaghnivāṃl lakṣmaṇaṃ yayā</li>
    </ul>
    <ul id="R_7.012.020">
        <li id="R_7.012.020ab">evaṃ sa kṛtadāro vai laṅkāyām īśvaraḥ prabhuḥ</li>
        <li id="R_7.012.020cd">gatvā tu nagaraṃ bhārye bhrātṛbhyāṃ samudāvahat</li>
    </ul>
    <ul id="R_7.012.021">
        <li id="R_7.012.021ab">vairocanasya dauhitrīṃ vajrajvāleti nāmataḥ</li>
        <li id="R_7.012.021cd">tāṃ bhāryāṃ kumbhakarṇasya rāvaṇaḥ samudāvahat</li>
    </ul>
    <ul id="R_7.012.022">
        <li id="R_7.012.022ab">gandharvarājasya sutāṃ śailūṣasya mahātmana</li>
        <li id="R_7.012.022cd">saramā nāma dharmajño lebhe bhāryāṃ vibhīṣaṇaḥ</li>
    </ul>
    <ul id="R_7.012.023">
        <li id="R_7.012.023ab">tīre tu sarasaḥ sā vai saṃjajñe mānasasya ca</li>
        <li id="R_7.012.023cd">mānasaṃ ca saras tāta vavṛdhe jaladāgame</li>
    </ul>
    <ul id="R_7.012.024">
        <li id="R_7.012.024ab">mātrā tu tasyāḥ kanyāyāḥ snehanākranditaṃ vacaḥ</li>
        <li id="R_7.012.024cd">saro mā vardhatety uktaṃ tataḥ sā saramābhavat</li>
    </ul>
    <ul id="R_7.012.025">
        <li id="R_7.012.025ab">evaṃ te kṛtadārā vai remire tatra rākṣasāḥ</li>
        <li id="R_7.012.025cd">svāṃ svāṃ bhāryām upādāya gandharvā iva nandane</li>
    </ul>
    <ul id="R_7.012.026">
        <li id="R_7.012.026ab">tato mandodarī putraṃ meghanādam asūyata</li>
        <li id="R_7.012.026cd">sa eṣa indrajin nāma yuṣmābhir abhidhīyate</li>
    </ul>
    <ul id="R_7.012.027">
        <li id="R_7.012.027ab">jātamātreṇa hi purā tena rākṣasasūnunā</li>
        <li id="R_7.012.027cd">rudatā sumahān mukto nādo jaladharopamaḥ</li>
    </ul>
    <ul id="R_7.012.028">
        <li id="R_7.012.028ab">jaḍīkṛtāyāṃ laṅkāyāṃ tena nādena tasya vai</li>
        <li id="R_7.012.028cd">pitā tasyākaron nāma meghanāda iti svayam</li>
    </ul>
    <ul id="R_7.012.029">
        <li id="R_7.012.029ab">so 'vardhata tadā rāma rāvaṇāntaḥpure śubhe</li>
        <li id="R_7.012.029cd">rakṣyamāṇo varastrībhiś channaḥ kāṣṭhair ivānalaḥ</li>
    </ul>
    <ul id="R_7.013.001">
        <li id="R_7.013.001ab">atha lokeśvarotsṛṣṭā tatra kālena kena cit</li>
        <li id="R_7.013.001cd">nidrā samabhavat tīvrā kumbhakarṇasya rūpiṇī</li>
    </ul>
    <ul id="R_7.013.002">
        <li id="R_7.013.002ab">tato bhrātaram āsīnaṃ kumbhakarṇo 'bravīd vacaḥ</li>
        <li id="R_7.013.002cd">nidrā māṃ bādhate rājan kārayasva mamālayam</li>
    </ul>
    <ul id="R_7.013.003">
        <li id="R_7.013.003ab">viniyuktās tato rājñā śilpino viśvakarmavat</li>
        <li id="R_7.013.003cd">akurvan kumbhakarṇasya kailāsasamam ālayam</li>
    </ul>
    <ul id="R_7.013.004">
        <li id="R_7.013.004ab">vistīrṇaṃ yojanaṃ śubhraṃ tato dviguṇam āyatam</li>
        <li id="R_7.013.004cd">darśanīyaṃ nirābādhaṃ kumbhakarṇasya cakrire</li>
    </ul>
    <ul id="R_7.013.005">
        <li id="R_7.013.005ab">sphāṭikaiḥ kāñcanaiś citraiḥ stambhaiḥ sarvatra śobhitam</li>
        <li id="R_7.013.005cd">vaidūryakṛtaśobhaṃ ca kiṅkiṇījālakaṃ tathā</li>
    </ul>
    <ul id="R_7.013.006">
        <li id="R_7.013.006ab">dantatoraṇavinyastaṃ vajrasphaṭikavedikam</li>
        <li id="R_7.013.006cd">sarvartusukhadaṃ nityaṃ meroḥ puṇyāṃ guhām iva</li>
    </ul>
    <ul id="R_7.013.007">
        <li id="R_7.013.007ab">tatra nidrāṃ samāviṣṭaḥ kumbhakarṇo niśācaraḥ</li>
        <li id="R_7.013.007cd">bahūny abdasahasrāṇi śayāno nāvabudhyate</li>
    </ul>
    <ul id="R_7.013.008">
        <li id="R_7.013.008ab">nidrābhibhūte tu tadā kumbhakarṇe daśānanaḥ</li>
        <li id="R_7.013.008cd">devarṣiyakṣagandharvān bādhate sma sa nityaśaḥ</li>
    </ul>
    <ul id="R_7.013.009">
        <li id="R_7.013.009ab">udyānāni vicitrāṇi nandanādīni yāni ca</li>
        <li id="R_7.013.009cd">tāni gatvā susaṃkruddho bhinatti sma daśānanaḥ</li>
    </ul>
    <ul id="R_7.013.010">
        <li id="R_7.013.010ab">nadīṃ gaja iva krīḍan vṛkṣān vāyur iva kṣipan</li>
        <li id="R_7.013.010cd">nagān vajra iva sṛṣṭo vidhvaṃsayati nityaśaḥ</li>
    </ul>
    <ul id="R_7.013.011">
        <li id="R_7.013.011ab">tathā vṛttaṃ tu vijñāya daśagrīvaṃ dhaneśvaraḥ</li>
        <li id="R_7.013.011cd">kulānurūpaṃ dharmajña vṛttaṃ saṃsmṛtya cātmanaḥ</li>
    </ul>
    <ul id="R_7.013.012">
        <li id="R_7.013.012ab">saubhrātradarśanārthaṃ tu dūtaṃ vaiśvaraṇas tadā</li>
        <li id="R_7.013.012cd">laṅkāṃ saṃpreṣayām āsa daśagrīvasya vai hitam</li>
    </ul>
    <ul id="R_7.013.013">
        <li id="R_7.013.013ab">sa gatvā nagarīṃ laṅkām āsasāda vibhīṣaṇam</li>
        <li id="R_7.013.013cd">mānitas tena dharmeṇa pṛṣṭhaś cāgamanaṃ prati</li>
    </ul>
    <ul id="R_7.013.014">
        <li id="R_7.013.014ab">pṛṣṭvā ca kuśalaṃ rājño jñātīn api ca bāndhavān</li>
        <li id="R_7.013.014cd">sabhāyāṃ darśayām āsa tam āsīnaṃ daśānanam</li>
    </ul>
    <ul id="R_7.013.015">
        <li id="R_7.013.015ab">sa dṛṣṭvā tatra rājānaṃ dīpyamānaṃ svatejasā</li>
        <li id="R_7.013.015cd">jayena cābhisaṃpūjya tūṣṇīm āsīn muhūrtakam</li>
    </ul>
    <ul id="R_7.013.016">
        <li id="R_7.013.016ab">tasyopanīte paryaṅke varāstaraṇasaṃvṛte</li>
        <li id="R_7.013.016cd">upaviśya daśagrīvaṃ dūto vākyam athābravīt</li>
    </ul>
    <ul id="R_7.013.017">
        <li id="R_7.013.017ab">rājan vadāmi te sarvaṃ bhrātā tava yad abravīt</li>
        <li id="R_7.013.017cd">ubhayoḥ sadṛśaṃ saumya vṛttasya ca kulasya ca</li>
    </ul>
    <ul id="R_7.013.018">
        <li id="R_7.013.018ab">sādhu paryāptam etāvat kṛtaś cāritrasaṃgrahaḥ</li>
        <li id="R_7.013.018cd">sādhu dharme vyavasthānaṃ kriyatāṃ yadi śakyate</li>
    </ul>
    <ul id="R_7.013.019">
        <li id="R_7.013.019ab">dṛṣṭaṃ me nandanaṃ bhagnam ṛṣayo nihatāḥ śrutāḥ</li>
        <li id="R_7.013.019cd">devānāṃ tu samudyogas tvatto rājañ śrutaś ca me</li>
    </ul>
    <ul id="R_7.013.020">
        <li id="R_7.013.020ab">nirākṛtaś ca bahuśas tvayāhaṃ rākṣasādhipa</li>
        <li id="R_7.013.020cd">aparāddhā hi bālyāc ca rakṣaṇīyāḥ svabāndhavāḥ</li>
    </ul>
    <ul id="R_7.013.021">
        <li id="R_7.013.021ab">ahaṃ tu himavatpṛṣṭhaṃ gato dharmam upāsitum</li>
        <li id="R_7.013.021cd">raudraṃ vrataṃ samāsthāya niyato niyatendriyaḥ</li>
    </ul>
    <ul id="R_7.013.022">
        <li id="R_7.013.022ab">tatra devo mayā dṛṣṭaḥ saha devyomayā prabhuḥ</li>
        <li id="R_7.013.022cd">savyaṃ cakṣur mayā caiva tatra devyāṃ nipātitam</li>
    </ul>
    <ul id="R_7.013.023">
        <li id="R_7.013.023ab">kā nv iyaṃ syād iti śubhā na khalv anyena hetunā</li>
        <li id="R_7.013.023cd">rūpaṃ hy anupamaṃ kṛtvā tatra krīḍati pārvatī</li>
    </ul>
    <ul id="R_7.013.024">
        <li id="R_7.013.024ab">tato devyāḥ prabhāvena dagdhaṃ savyaṃ mamekṣaṇam</li>
        <li id="R_7.013.024cd">reṇudhvastam iva jyotiḥ piṅgalatvam upāgatam</li>
    </ul>
    <ul id="R_7.013.025">
        <li id="R_7.013.025ab">tato 'ham anyad vistīrṇaṃ gatvā tasya gires taṭam</li>
        <li id="R_7.013.025cd">pūrṇaṃ varṣaśatāny aṣṭau samavāpa mahāvratam</li>
    </ul>
    <ul id="R_7.013.026">
        <li id="R_7.013.026ab">samāpte niyame tasmiṃs tatra devo maheśvaraḥ</li>
        <li id="R_7.013.026cd">prītaḥ prītena manasā prāha vākyam idaṃ prabhuḥ</li>
    </ul>
    <ul id="R_7.013.027">
        <li id="R_7.013.027ab">prīto 'smi tava dharmajña tapasānena suvrata</li>
        <li id="R_7.013.027cd">mayā caitad vrataṃ cīrṇaṃ tvayā caiva dhanādhipa</li>
    </ul>
    <ul id="R_7.013.028">
        <li id="R_7.013.028ab">tṛtīyaḥ puruṣo nāsti yaś cared vratam īdṛśam</li>
        <li id="R_7.013.028cd">vrataṃ suduścaraṃ hy etan mayaivotpāditaṃ purā</li>
    </ul>
    <ul id="R_7.013.029">
        <li id="R_7.013.029ab">tat sakhitvaṃ mayā sārdhaṃ rocayasva dhaneśvara</li>
        <li id="R_7.013.029cd">tapasā nirjitatvād dhi sakhā bhava mamānagha</li>
    </ul>
    <ul id="R_7.013.030">
        <li id="R_7.013.030ab">devyā dagdhaṃ prabhāvena yac ca sāvyaṃ tavekṣaṇam</li>
        <li id="R_7.013.030cd">ekākṣi piṅgalety eva nāma sthāsyati śāśvatam</li>
    </ul>
    <ul id="R_7.013.031">
        <li id="R_7.013.031ab">evaṃ tena sakhitvaṃ ca prāpyānujñāṃ ca śaṃkarāt</li>
        <li id="R_7.013.031cd">āgamya ca śruto 'yaṃ me tava pāpaviniścayaḥ</li>
    </ul>
    <ul id="R_7.013.032">
        <li id="R_7.013.032ab">tadadharmiṣṭhasaṃyogān nivarta kuladūṣaṇa</li>
        <li id="R_7.013.032cd">cintyate hi vadhopāyaḥ sarṣisaṃghaiḥ surais tava</li>
    </ul>
    <ul id="R_7.013.033">
        <li id="R_7.013.033ab">evam ukto daśagrīvaḥ kruddhaḥ saṃraktalocanaḥ</li>
        <li id="R_7.013.033cd">hastān dantāṃś a saṃpīḍya vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.013.034">
        <li id="R_7.013.034ab">vijñātaṃ te mayā dūta vākyaṃ yat tvaṃ prabhāṣase</li>
        <li id="R_7.013.034cd">naiva tvam asi naivāsau bhrātrā yenāsi preṣitaḥ</li>
    </ul>
    <ul id="R_7.013.035">
        <li id="R_7.013.035ab">hitaṃ na sa mamaitad dhi bravīti dhanarakṣakaḥ</li>
        <li id="R_7.013.035cd">maheśvarasakhitvaṃ tu mūḍha śrāvayase kila</li>
    </ul>
    <ul id="R_7.013.036">
        <li id="R_7.013.036ab">na hantavyo gurur jyeṣṭho mamāyam iti manyate</li>
        <li id="R_7.013.036cd">tasya tv idānīṃ śrutvā me vākyam eṣā kṛtā matiḥ</li>
    </ul>
    <ul id="R_7.013.037">
        <li id="R_7.013.037ab">trīṃl lokān api jeṣyāmi bāhuvīryam upāśritaḥ</li>
        <li id="R_7.013.037cd">etan muhūrtam eṣo 'haṃ tasyaikasya kṛte ca vai</li>
        <li id="R_7.013.037ef">caturo lokapālāṃs tān nayiṣyāmi yamakṣayam</li>
    </ul>
    <ul id="R_7.013.038">
        <li id="R_7.013.038ab">evam uktvā tu laṅkeśo dūtaṃ khaḍgena jaghnivān</li>
        <li id="R_7.013.038cd">dadau bhakṣayituṃ hy enaṃ rākṣasānāṃ durātmanām</li>
    </ul>
    <ul id="R_7.013.039">
        <li id="R_7.013.039ab">tataḥ kṛtasvastyayano ratham āruhya rāvaṇaḥ</li>
        <li id="R_7.013.039cd">trailokyavijayākāṅkṣī yayau tatra dhaneśvaraḥ</li>
    </ul>
    <ul id="R_7.014.001">
        <li id="R_7.014.001ab">tataḥ sa sacivaiḥ sārdhaṃ ṣaḍbhir nityaṃ balotkaṭaiḥ</li>
        <li id="R_7.014.001cd">mahodaraprahastābhyāṃ mārīcaśukasāraṇaiḥ</li>
    </ul>
    <ul id="R_7.014.002">
        <li id="R_7.014.002ab">dhūmrākṣeṇa ca vīreṇa nityaṃ samaragṛdhnunā</li>
        <li id="R_7.014.002cd">vṛtaḥ saṃprayayau śrīmān krodhāl lokān dahann iva</li>
    </ul>
    <ul id="R_7.014.003">
        <li id="R_7.014.003ab">purāṇi sa nadīḥ śailān vanāny upavanāni ca</li>
        <li id="R_7.014.003cd">atikramya muhūrtena kailāsaṃ girim āviśat</li>
    </ul>
    <ul id="R_7.014.004">
        <li id="R_7.014.004ab">taṃ niviṣṭaṃ girau tasmin rākṣasendraṃ niśamya tu</li>
        <li id="R_7.014.004cd">rājño bhrātāyam ity uktvā gatā yatra dhaneśvaraḥ</li>
    </ul>
    <ul id="R_7.014.005">
        <li id="R_7.014.005ab">gatvā tu sarvam ācakhyur bhrātus tasya viniścayam</li>
        <li id="R_7.014.005cd">anujñātā yayuś caiva yuddhāya dhanadena te</li>
    </ul>
    <ul id="R_7.014.006">
        <li id="R_7.014.006ab">tato balasya saṃkṣobhaḥ sāgarasyeva vardhataḥ</li>
        <li id="R_7.014.006cd">abhūn nairṛtarājasya giriṃ saṃcālayann iva</li>
    </ul>
    <ul id="R_7.014.007">
        <li id="R_7.014.007ab">tato yuddhaṃ samabhavad yakṣarākṣasasaṃkulam</li>
        <li id="R_7.014.007cd">vyathitāś cābhavaṃs tatra sacivās tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_7.014.008">
        <li id="R_7.014.008ab">taṃ dṛṣṭvā tādṛśaṃ sainyaṃ daśagrīvo niśācaraḥ</li>
        <li id="R_7.014.008cd">harṣān nādaṃ tataḥ kṛtvā roṣāt samabhivartata</li>
    </ul>
    <ul id="R_7.014.009">
        <li id="R_7.014.009ab">ye tu te rākṣasendrasya sacivā ghoravikramaḥ</li>
        <li id="R_7.014.009cd">te sahasraṃ sahasrāṇām ekaikaṃ samayodhayan</li>
    </ul>
    <ul id="R_7.014.010">
        <li id="R_7.014.010ab">tato gadābhiḥ parighair asibhiḥ śaktitomaraiḥ</li>
        <li id="R_7.014.010cd">vadhyamāno daśagrīvas tat sainyaṃ samagāhata</li>
    </ul>
    <ul id="R_7.014.011">
        <li id="R_7.014.011ab">tair nirucchvāsavat tatra vadhyamāno daśānanaḥ</li>
        <li id="R_7.014.011cd">varṣamāṇair iva ghanair yakṣendraiḥ saṃnirudhyata</li>
    </ul>
    <ul id="R_7.014.012">
        <li id="R_7.014.012ab">sa durātmā samudyamya kāladaṇḍopamāṃ gadām</li>
        <li id="R_7.014.012cd">praviveśa tataḥ sainyaṃ nayan yakṣān yamakṣayam</li>
    </ul>
    <ul id="R_7.014.013">
        <li id="R_7.014.013ab">sa kakṣam iva vistīrṇaṃ śuṣkendhanasamākulam</li>
        <li id="R_7.014.013cd">vātenāgnir ivāyatto 'dahat sainyaṃ sudāruṇam</li>
    </ul>
    <ul id="R_7.014.014">
        <li id="R_7.014.014ab">tais tu tasya mṛdhe 'mātyair mahodaraśukādibhiḥ</li>
        <li id="R_7.014.014cd">alpāvaśiṣṭās te yakṣāḥ kṛtā vātair ivāmbudāḥ</li>
    </ul>
    <ul id="R_7.014.015">
        <li id="R_7.014.015ab">ke cit tv āyudhabhagnāṅgāḥ patitāḥ samarakṣitau</li>
        <li id="R_7.014.015cd">oṣṭhān svadaśanais tīkṣṇair daṃśanto bhuvi pātitāḥ</li>
    </ul>
    <ul id="R_7.014.016">
        <li id="R_7.014.016ab">bhayād anyonyam āliṅgya bhraṣṭaśastrā raṇājire</li>
        <li id="R_7.014.016cd">niṣedus te tadā yakṣāḥ kūlā jalahatā iva</li>
    </ul>
    <ul id="R_7.014.017">
        <li id="R_7.014.017ab">hatānāṃ svargasaṃsthānāṃ yudhyatāṃ pṛthivītale</li>
        <li id="R_7.014.017cd">prekṣatām ṛṣisaṃghānāṃ na babhūvāntaraṃ divi</li>
    </ul>
    <ul id="R_7.014.018">
        <li id="R_7.014.018ab">etasminn antare rāma vistīrṇabalavāhanaḥ</li>
        <li id="R_7.014.018cd">agamat sumahān yakṣo nāmnā saṃyodhakaṇṭakaḥ</li>
    </ul>
    <ul id="R_7.014.019">
        <li id="R_7.014.019ab">tena yakṣeṇa mārīco viṣṇuneva samāhataḥ</li>
        <li id="R_7.014.019cd">patitaḥ pṛthivīṃ bheje kṣīṇapuṇya ivāmbarāt</li>
    </ul>
    <ul id="R_7.014.020">
        <li id="R_7.014.020ab">prāptasaṃjño muhūrtena viśramya ca niśācaraḥ</li>
        <li id="R_7.014.020cd">taṃ yakṣaṃ yodhayām āsa sa ca bhagnaḥ pradudruve</li>
    </ul>
    <ul id="R_7.014.021">
        <li id="R_7.014.021ab">tataḥ kāñcanacitrāṅgaṃ vaidūryarajatokṣitam</li>
        <li id="R_7.014.021cd">maryādāṃ dvārapālānāṃ toraṇaṃ tat samāviśat</li>
    </ul>
    <ul id="R_7.014.022">
        <li id="R_7.014.022ab">tato rāma daśagrīvaṃ praviśantaṃ niśācaram</li>
        <li id="R_7.014.022cd">sūryabhānur iti khyāto dvārapālo nyavārayat</li>
    </ul>
    <ul id="R_7.014.023">
        <li id="R_7.014.023ab">tatas toraṇam utpāṭya tena yakṣeṇa tāḍitaḥ</li>
        <li id="R_7.014.023cd">rākṣaso yakṣasṛṣṭena toraṇena samāhataḥ</li>
        <li id="R_7.014.023ef">na kṣitiṃ prayayau rāma varāt salilayoninaḥ</li>
    </ul>
    <ul id="R_7.014.024">
        <li id="R_7.014.024ab">sa tu tenaiva taṃ yakṣaṃ toraṇena samāhanat</li>
        <li id="R_7.014.024cd">nādṛśyata tadā yakṣo bhasma tena kṛtas tu saḥ</li>
    </ul>
    <ul id="R_7.014.025">
        <li id="R_7.014.025ab">tataḥ pradudruvuḥ sarve yakṣā dṛṣṭvā parākramam</li>
        <li id="R_7.014.025cd">tato nadīr guhāś caiva viviśur bhayapīḍitāḥ</li>
    </ul>
    <ul id="R_7.015.001">
        <li id="R_7.015.001ab">tatas tān vidrutān dṛṣṭvā yakṣāñ śatasahasraśaḥ</li>
        <li id="R_7.015.001cd">svayam eva dhanādhyakṣo nirjagāma raṇaṃ prati</li>
    </ul>
    <ul id="R_7.015.002">
        <li id="R_7.015.002ab">tatra māṇicāro nāma yakṣaḥ paramadurjayaḥ</li>
        <li id="R_7.015.002cd">vṛto yakṣasahasraiḥ sa caturbhiḥ samayodhayat</li>
    </ul>
    <ul id="R_7.015.003">
        <li id="R_7.015.003ab">te gadāmusalaprāsaśaktitomaramudgaraiḥ</li>
        <li id="R_7.015.003cd">abhighnanto raṇe yakṣā rākṣasān abhidudruvuḥ</li>
    </ul>
    <ul id="R_7.015.004">
        <li id="R_7.015.004ab">tataḥ prahastena tadā sahasraṃ nihataṃ raṇe</li>
        <li id="R_7.015.004cd">mahodareṇa gadayā sahasram aparaṃ hatam</li>
    </ul>
    <ul id="R_7.015.005">
        <li id="R_7.015.005ab">kruddhena ca tadā rāma mārīcena durātmanā</li>
        <li id="R_7.015.005cd">nimeṣāntaramātreṇa dve sahasre nipātite</li>
    </ul>
    <ul id="R_7.015.006">
        <li id="R_7.015.006ab">dhūmrākṣeṇa samāgamya māṇibhadro mahāraṇe</li>
        <li id="R_7.015.006cd">musalenorasi krodhāt tāḍito na ca kampitaḥ</li>
    </ul>
    <ul id="R_7.015.007">
        <li id="R_7.015.007ab">tato gadāṃ samāvidhya māṇibhadreṇa rākṣasaḥ</li>
        <li id="R_7.015.007cd">dhūmrākṣas tāḍito mūrdhni vihvalo nipapāta ha</li>
    </ul>
    <ul id="R_7.015.008">
        <li id="R_7.015.008ab">dhūmrākṣaṃ tāḍitaṃ dṛṣṭvā patitaṃ śoṇitokṣitam</li>
        <li id="R_7.015.008cd">abhyadhāvat susaṃkruddho māṇibhadraṃ daśānanaḥ</li>
    </ul>
    <ul id="R_7.015.009">
        <li id="R_7.015.009ab">taṃ kruddham abhidhāvantaṃ yugāntāgnim ivotthitam</li>
        <li id="R_7.015.009cd">śaktibhis tāḍayām āsa tisṛbhir yakṣapuṃgavaḥ</li>
    </ul>
    <ul id="R_7.015.010">
        <li id="R_7.015.010ab">tato rākṣasarājena tāḍito gadayā raṇe</li>
        <li id="R_7.015.010cd">tasya tena prahāreṇa mukuṭaḥ pārśvam āgataḥ</li>
        <li id="R_7.015.010ef">tadā prabhṛti yakṣo 'sau pārśvamaulir iti smṛtaḥ</li>
    </ul>
    <ul id="R_7.015.011">
        <li id="R_7.015.011ab">tasmiṃs tu vimukhe yakṣe māṇibhadre mahātmani</li>
        <li id="R_7.015.011cd">saṃnādaḥ sumahān rāma tasmiñ śaile vyavardhata</li>
    </ul>
    <ul id="R_7.015.012">
        <li id="R_7.015.012ab">tato dūrāt pradadṛśe dhanādhyakṣo gadādharaḥ</li>
        <li id="R_7.015.012cd">śukraproṣṭaḥpadābhyāṃ ca śaṅkhapadmasamāvṛtaḥ</li>
    </ul>
    <ul id="R_7.015.013">
        <li id="R_7.015.013ab">sa dṛṣṭvā bhrātaraṃ saṃkhye śāpād vibhraṣṭagauravam</li>
        <li id="R_7.015.013cd">uvāca vacanaṃ dhīmān yuktaṃ paitāmahe kule</li>
    </ul>
    <ul id="R_7.015.014">
        <li id="R_7.015.014ab">mayā tvaṃ vāryamāṇo 'pi nāvagacchasi durmate</li>
        <li id="R_7.015.014cd">paścād asya phalaṃ prāpya jñāsyase nirayaṃ gataḥ</li>
    </ul>
    <ul id="R_7.015.015">
        <li id="R_7.015.015ab">yo hi mohād viṣaṃ pītvā nāvagacchati mānavaḥ</li>
        <li id="R_7.015.015cd">pariṇāme sa vi mūḍho jānīte karmaṇaḥ phalam</li>
    </ul>
    <ul id="R_7.015.016">
        <li id="R_7.015.016ab">daivatāni hi nandanti dharmayuktena kena cit</li>
        <li id="R_7.015.016cd">yena tvam īdṛśaṃ bhāvaṃ nītas tac ca na budhyase</li>
    </ul>
    <ul id="R_7.015.017">
        <li id="R_7.015.017ab">yo hi mātṝḥ pitṝn bhrātṝn ācaryāṃś cāvamanyate</li>
        <li id="R_7.015.017cd">sa paśyati phalaṃ tasya pretarājavaśaṃ gataḥ</li>
    </ul>
    <ul id="R_7.015.018">
        <li id="R_7.015.018ab">adhruve hi śarīre yo na karoti tapo 'rjanam</li>
        <li id="R_7.015.018cd">sa paścāt tapyate mūḍho mṛto dṛṣṭvātmano gatim</li>
    </ul>
    <ul id="R_7.015.019">
        <li id="R_7.015.019ab">kasya cin na hi durbudheś chandato jāyate matiḥ</li>
        <li id="R_7.015.019cd">yādṛśaṃ kurute karma tādṛśaṃ phalam aśnute</li>
    </ul>
    <ul id="R_7.015.020">
        <li id="R_7.015.020ab">buddhiṃ rūpaṃ balaṃ vittaṃ putrān māhātmyam eva ca</li>
        <li id="R_7.015.020cd">prapnuvanti narāḥ sarvaṃ svakṛtaiḥ pūrvakarmabhiḥ</li>
    </ul>
    <ul id="R_7.015.021">
        <li id="R_7.015.021ab">evaṃ nirayagāmī tvaṃ yasya te matir īdṛśī</li>
        <li id="R_7.015.021cd">na tvāṃ samabhibhāṣiṣye durvṛttasyaiṣa nirṇayaḥ</li>
    </ul>
    <ul id="R_7.015.022">
        <li id="R_7.015.022ab">evam uktvā tatas tena tasyāmātyāḥ samāhatāḥ</li>
        <li id="R_7.015.022cd">mārīcapramukhāḥ sarve vimukhā vipradudruvuḥ</li>
    </ul>
    <ul id="R_7.015.023">
        <li id="R_7.015.023ab">tatas tena daśagrīvo yakṣendreṇa mahātmanā</li>
        <li id="R_7.015.023cd">gadayābhihato mūrdhni na ca sthānād vyakampata</li>
    </ul>
    <ul id="R_7.015.024">
        <li id="R_7.015.024ab">tatas tau rāma nighnantāv anyonyaṃ paramāhave</li>
        <li id="R_7.015.024cd">na vihvalau na ca śrāntau babhūvatur amarṣaṇaiḥ</li>
    </ul>
    <ul id="R_7.015.025">
        <li id="R_7.015.025ab">āgneyam astraṃ sa tato mumoca dhanado raṇe</li>
        <li id="R_7.015.025cd">vāruṇena daśagrīvas tad astraṃ pratyavārayat</li>
    </ul>
    <ul id="R_7.015.026">
        <li id="R_7.015.026ab">tato māyāṃ praviṣṭaḥ sa rākṣasīṃ rākṣaseśvaraḥ</li>
        <li id="R_7.015.026cd">jaghāna mūrdhni dhanadaṃ vyāvidhya mahatīṃ gadām</li>
    </ul>
    <ul id="R_7.015.027">
        <li id="R_7.015.027ab">evaṃ sa tenābhihato vihvalaḥ śoṇitokṣitaḥ</li>
        <li id="R_7.015.027cd">kṛttamūla ivāśoko nipapāta dhanādhipaḥ</li>
    </ul>
    <ul id="R_7.015.028">
        <li id="R_7.015.028ab">tataḥ padmādibhis tatra nidhibhiḥ sa dhanādhipaḥ</li>
        <li id="R_7.015.028cd">nandanaṃ vanam ānīya dhanado śvāsitas tadā</li>
    </ul>
    <ul id="R_7.015.029">
        <li id="R_7.015.029ab">tato nirjitya taṃ rāma dhanadaṃ rākṣasādhipaḥ</li>
        <li id="R_7.015.029cd">puṣpakaṃ tasya jagrāha vimānaṃ jayalakṣaṇam</li>
    </ul>
    <ul id="R_7.015.030">
        <li id="R_7.015.030ab">kāñcanastambhasaṃvītaṃ vaidūryamaṇitoraṇam</li>
        <li id="R_7.015.030cd">muktājālapraticchannaṃ sarvakāmaphaladrumam</li>
    </ul>
    <ul id="R_7.015.031">
        <li id="R_7.015.031ab">tat tu rājā samāruhya kāmagaṃ vīryanirjitam</li>
        <li id="R_7.015.031cd">jitvā vaiśravaṇaṃ devaṃ kailāsād avarohata</li>
    </ul>
    <ul id="R_7.016.001">
        <li id="R_7.016.001ab">sa jitvā bhrātaraṃ rāma dhanadaṃ rākṣasādhipaḥ</li>
        <li id="R_7.016.001cd">mahāsenaprasūtiṃ tu yayau śaravaṇaṃ tataḥ</li>
    </ul>
    <ul id="R_7.016.002">
        <li id="R_7.016.002ab">athāpaśyad daśagrīvo raukmaṃ śaravaṇaṃ tadā</li>
        <li id="R_7.016.002cd">gabhastijālasaṃvītaṃ dvitīyam iva bhāskaram</li>
    </ul>
    <ul id="R_7.016.003">
        <li id="R_7.016.003ab">parvataṃ sa samāsādya kiṃ cid ramyavanāntaram</li>
        <li id="R_7.016.003cd">apaśyat puṣpakaṃ tatra rāma viṣṭambhitaṃ divi</li>
    </ul>
    <ul id="R_7.016.004">
        <li id="R_7.016.004ab">viṣṭabdhaṃ puṣpakaṃ dṛṣṭvā kāmagaṃ hy agamaṃ kṛtam</li>
        <li id="R_7.016.004cd">rākṣasaś cintayām āsa sacivais taiḥ samāvṛtaḥ</li>
    </ul>
    <ul id="R_7.016.005">
        <li id="R_7.016.005ab">kim idaṃ yannimittaṃ me na ca gacchati puṣpakam</li>
        <li id="R_7.016.005cd">parvatasyoparisthasya kasya karma tv idaṃ bhavet</li>
    </ul>
    <ul id="R_7.016.006">
        <li id="R_7.016.006ab">tato 'bravīd daśagrīvaṃ mārīco buddhikovidaḥ</li>
        <li id="R_7.016.006cd">naitan niṣkāraṇaṃ rājan puṣpako 'yaṃ na gacchati</li>
    </ul>
    <ul id="R_7.016.007">
        <li id="R_7.016.007ab">tataḥ pārśvam upāgamya bhavasyānucaro balī</li>
        <li id="R_7.016.007cd">nandīśvara uvācedaṃ rākṣasendram aśaṅkitaḥ</li>
    </ul>
    <ul id="R_7.016.008">
        <li id="R_7.016.008ab">nivartasva daśagrīva śaile krīḍati śaṃkaraḥ</li>
    </ul>
    <ul id="R_7.016.009">
        <li id="R_7.016.009ab">suparṇanāgayakṣāṇāṃ daityadānavarakṣasām</li>
        <li id="R_7.016.009cd">prāṇinām eva sarveṣām agamyaḥ parvataḥ kṛtaḥ</li>
    </ul>
    <ul id="R_7.016.010">
        <li id="R_7.016.010ab">sa roṣāt tāmranayanaḥ puṣpakād avaruhya ca</li>
        <li id="R_7.016.010cd">ko 'yaṃ śaṃkara ity uktvā śailamūlam upāgamat</li>
    </ul>
    <ul id="R_7.016.011">
        <li id="R_7.016.011ab">nandīśvaram athāpaśyad avidūrasthitaṃ prabhum</li>
        <li id="R_7.016.011cd">dīptaṃ śūlam avaṣṭabhya dvitīyam iva śaṃkaram</li>
    </ul>
    <ul id="R_7.016.012">
        <li id="R_7.016.012ab">sa vānaramukhaṃ dṛṣṭvā tam avajñāya rākṣasaḥ</li>
        <li id="R_7.016.012cd">prahāsaṃ mumuce maurkhyāt satoya iva toyadaḥ</li>
    </ul>
    <ul id="R_7.016.013">
        <li id="R_7.016.013ab">saṃkruddho bhagavān nandī śaṃkarasyāparā tanuḥ</li>
        <li id="R_7.016.013cd">abravīd rākṣasaṃ tatra daśagrīvam upasthitam</li>
    </ul>
    <ul id="R_7.016.014">
        <li id="R_7.016.014ab">yasmād vānaramūrtiṃ māṃ dṛṣṭvā rākṣasadurmate</li>
        <li id="R_7.016.014cd">maurkhyāt tvam avajānīṣe parihāsaṃ ca muñcasi</li>
    </ul>
    <ul id="R_7.016.015">
        <li id="R_7.016.015ab">tasmān madrūpasaṃyuktā madvīryasamatejasaḥ</li>
        <li id="R_7.016.015cd">utpatsyante vadhārthaṃ hi kulasya tava vānarāḥ</li>
    </ul>
    <ul id="R_7.016.016">
        <li id="R_7.016.016ab">kiṃ tv idānīṃ mayā śakyaṃ kartuṃ yat tvāṃ niśācara</li>
        <li id="R_7.016.016cd">na hantavyo hatas tvaṃ hi pūrvam eva svakarmabhiḥ</li>
    </ul>
    <ul id="R_7.016.017">
        <li id="R_7.016.017ab">acintayitvā sa tadā nandivākyaṃ niśācaraḥ</li>
        <li id="R_7.016.017cd">parvataṃ taṃ samāsādya vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.016.018">
        <li id="R_7.016.018ab">puṣpakasya gatiś chinnā yatkṛte mama gacchataḥ</li>
        <li id="R_7.016.018cd">tad etac chailam unmūlaṃ karomi tava gopate</li>
    </ul>
    <ul id="R_7.016.019">
        <li id="R_7.016.019ab">kena prabhāvena bhavas tatra krīḍati rājavat</li>
        <li id="R_7.016.019cd">vijñātavyaṃ na jānīṣe bhayasthānam upasthitam</li>
    </ul>
    <ul id="R_7.016.020">
        <li id="R_7.016.020ab">evam uktvā tato rājan bhujān prakṣipya parvate</li>
        <li id="R_7.016.020cd">tolayām āsa taṃ śailaṃ samṛgavyālapādapam</li>
    </ul>
    <ul id="R_7.016.021">
        <li id="R_7.016.021ab">tato rāma mahādevaḥ prahasan vīkṣya tatkṛtam</li>
        <li id="R_7.016.021cd">pādāṅguṣṭhena taṃ śailaṃ pīḍayām āsa līlayā</li>
    </ul>
    <ul id="R_7.016.022">
        <li id="R_7.016.022ab">tatas te pīḍitās tasya śailasyādho gatā bhujāḥ</li>
        <li id="R_7.016.022cd">vismitāś cābhavaṃs tatra sacivās tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_7.016.023">
        <li id="R_7.016.023ab">rakṣasā tena roṣāc ca bhujānāṃ pīḍanāt tathā</li>
        <li id="R_7.016.023cd">mukto virāvaḥ sumahāṃs trailokyaṃ yena pūritam</li>
    </ul>
    <ul id="R_7.016.024">
        <li id="R_7.016.024ab">mānuṣāḥ śabdavitrastā menire lokasaṃkṣayam</li>
        <li id="R_7.016.024cd">devatāś cāpi saṃkṣubdhāś calitāḥ sveṣu karmasu</li>
    </ul>
    <ul id="R_7.016.025">
        <li id="R_7.016.025ab">tataḥ prīto mahādevaḥ śailāgre viṣṭhitas tadā</li>
        <li id="R_7.016.025cd">muktvā tasya bhujān rājan prāha vākyaṃ daśānanam</li>
    </ul>
    <ul id="R_7.016.026">
        <li id="R_7.016.026ab">prīto 'smi tava vīryāc ca śauṇḍīryāc ca niśācara</li>
        <li id="R_7.016.026cd">ravato vedanā muktaḥ svaraḥ paramadāruṇaḥ</li>
    </ul>
    <ul id="R_7.016.027">
        <li id="R_7.016.027ab">yasmāl lokatrayaṃ tv etad rāvitaṃ bhayam āgatam</li>
        <li id="R_7.016.027cd">tasmāt tvaṃ rāvaṇo nāma nāmnā tena bhaviṣyasi</li>
    </ul>
    <ul id="R_7.016.028">
        <li id="R_7.016.028ab">devatā mānuṣā yakṣā ye cānye jagatītale</li>
        <li id="R_7.016.028cd">evaṃ tvām abhidhāsyanti rāvaṇaṃ lokarāvaṇam</li>
    </ul>
    <ul id="R_7.016.029">
        <li id="R_7.016.029ab">gaccha paulastya visrabdhaḥ pathā yena tvam icchasi</li>
        <li id="R_7.016.029cd">mayā tvam abhyanujñāto rākṣasādhipa gamyatām</li>
    </ul>
    <ul id="R_7.016.030">
        <li id="R_7.016.030ab">sākṣān maheśvareṇaivaṃ kṛtanāmā sa rāvaṇaḥ</li>
        <li id="R_7.016.030cd">abhivādya mahādevaṃ vimānaṃ tat samāruhat</li>
    </ul>
    <ul id="R_7.016.031">
        <li id="R_7.016.031ab">tato mahītale rāma paricakrāma rāvaṇaḥ</li>
        <li id="R_7.016.031cd">kṣatriyān sumahāvīryān bādhamānas tatas tataḥ</li>
    </ul>
    <ul id="R_7.017.001">
        <li id="R_7.017.001ab">atha rājan mahābāhur vicaran sa mahītalam</li>
        <li id="R_7.017.001cd">himavadvanam āsādya paricakrāma rāvaṇaḥ</li>
    </ul>
    <ul id="R_7.017.002">
        <li id="R_7.017.002ab">tatrāpaśyata vai kanyāṃ kṛṣṭājinajaṭādharām</li>
        <li id="R_7.017.002cd">ārṣeṇa vidhinā yuktāṃ tapantīṃ devatām iva</li>
    </ul>
    <ul id="R_7.017.003">
        <li id="R_7.017.003ab">sa dṛṣṭvā rūpasaṃpannāṃ kanyāṃ tāṃ sumahāvratām</li>
        <li id="R_7.017.003cd">kāmamohaparītātmā papraccha prahasann iva</li>
    </ul>
    <ul id="R_7.017.004">
        <li id="R_7.017.004ab">kim idaṃ vartase bhadre viruddhaṃ yauvanasya te</li>
        <li id="R_7.017.004cd">na hi yuktā tavaitasya rūpasyeyaṃ pratikriyā</li>
    </ul>
    <ul id="R_7.017.005">
        <li id="R_7.017.005ab">kasyāsi duhitā bhadre ko vā bhartā tavānaghe</li>
        <li id="R_7.017.005cd">pṛcchataḥ śaṃsa me śīghraṃ ko vā hetus tapo'rjane</li>
    </ul>
    <ul id="R_7.017.006">
        <li id="R_7.017.006ab">evam uktā tu sā kanyā tenānāryeṇa rakṣasā</li>
        <li id="R_7.017.006cd">abravīd vidhivat kṛtvā tasyātithyaṃ tapodhanā</li>
    </ul>
    <ul id="R_7.017.007">
        <li id="R_7.017.007ab">kuśadhvajo nāma pitā brahmarṣir mama dhārmikaḥ</li>
        <li id="R_7.017.007cd">bṛhaspatisutaḥ śrīmān buddhyā tulyo bṛhaspateḥ</li>
    </ul>
    <ul id="R_7.017.008">
        <li id="R_7.017.008ab">tasyāhaṃ kurvato nityaṃ vedābhyāsaṃ mahātmanaḥ</li>
        <li id="R_7.017.008cd">saṃbhūtā vānmayī kanyā nāmnā vedavatī smṛtā</li>
    </ul>
    <ul id="R_7.017.009">
        <li id="R_7.017.009ab">tato devāḥ sagandharvā yakṣarākṣasapannagāḥ</li>
        <li id="R_7.017.009cd">te cāpi gatvā pitaraṃ varaṇaṃ rocayanti me</li>
    </ul>
    <ul id="R_7.017.010">
        <li id="R_7.017.010ab">na ca māṃ sa pitā tebhyo dattavān rākṣaseśvara</li>
        <li id="R_7.017.010cd">kāraṇaṃ tad vadiṣyāmi niśāmaya mahābhuja</li>
    </ul>
    <ul id="R_7.017.011">
        <li id="R_7.017.011ab">pitus tu mama jāmātā viṣṇuḥ kila surottamaḥ</li>
        <li id="R_7.017.011cd">abhipretas trilokeśas tasmān nānyasya me pitāḥ</li>
    </ul>
    <ul id="R_7.017.012">
        <li id="R_7.017.012ab">dātum icchati dharmātmā tac chrutvā baladarpitaḥ</li>
        <li id="R_7.017.012cd">śambhur nāma tato rājā daityānāṃ kupito 'bhavat</li>
        <li id="R_7.017.012ef">tena rātrau prasupto me pitā pāpena hiṃsitaḥ</li>
    </ul>
    <ul id="R_7.017.013">
        <li id="R_7.017.013ab">tato me jananī dīnā tac charīraṃ pitur mama</li>
        <li id="R_7.017.013cd">pariṣvajya mahābhāgā praviṣṭā dahanaṃ saha</li>
    </ul>
    <ul id="R_7.017.014">
        <li id="R_7.017.014ab">tato manorathaṃ satyaṃ pitur nārāyaṇaṃ prati</li>
        <li id="R_7.017.014cd">karomīti mamecchā ca hṛdaye sādhu viṣṭhitā</li>
    </ul>
    <ul id="R_7.017.015">
        <li id="R_7.017.015ab">ahaṃ pretagatasyāpi kariṣye kāṅkṣitaṃ pituḥ</li>
        <li id="R_7.017.015cd">iti pratijñām āruhya carāmi vipulaṃ tapaḥ</li>
    </ul>
    <ul id="R_7.017.016">
        <li id="R_7.017.016ab">etat te sarvam ākhyātaṃ mayā rākṣasapuṃgava</li>
        <li id="R_7.017.016cd">āśritāṃ viddhi māṃ dharmaṃ nārāyaṇapatīcchayā</li>
    </ul>
    <ul id="R_7.017.017">
        <li id="R_7.017.017ab">vijñātas tvaṃ hi me rājan gaccha paulastyanandana</li>
        <li id="R_7.017.017cd">jānāmi tapasā sarvaṃ trailokye yad dhi vartate</li>
    </ul>
    <ul id="R_7.017.018">
        <li id="R_7.017.018ab">so 'bravīd rāvaṇas tatra tāṃ kanyāṃ sumahāvratām</li>
        <li id="R_7.017.018cd">avaruhya vimānāgrāt kandarpaśarapīḍitaḥ</li>
    </ul>
    <ul id="R_7.017.019">
        <li id="R_7.017.019ab">avaliptāsi suśroṇi yasyās te matir īdṛśī</li>
        <li id="R_7.017.019cd">vṛddhānāṃ mṛgaśāvākṣi bhrājate dharmasaṃcayaḥ</li>
    </ul>
    <ul id="R_7.017.020">
        <li id="R_7.017.020ab">tvaṃ sarvaguṇasaṃpannā nārhase kartum īdṛśam</li>
        <li id="R_7.017.020cd">trailokyasundarī bhīru yauvane vārdhakaṃ vidhim</li>
    </ul>
    <ul id="R_7.017.021">
        <li id="R_7.017.021ab">kaś ca tāvad asau yaṃ tvaṃ viṣṇur ity abhibhāṣase</li>
        <li id="R_7.017.021cd">vīryeṇa tapasā caiva bhogena ca balena ca</li>
        <li id="R_7.017.021ef">na mayāsau samo bhadre yaṃ tvaṃ kāmayase 'ṅgane</li>
    </ul>
    <ul id="R_7.017.022">
        <li id="R_7.017.022ab">ma maivam iti sā kanyā tam uvāca niśācaram</li>
        <li id="R_7.017.022cd">mūrdhajeṣu ca tāṃ rakṣaḥ karāgreṇa parāmṛśat</li>
    </ul>
    <ul id="R_7.017.023">
        <li id="R_7.017.023ab">tato vedavatī kruddhā keśān hastena sācchinat</li>
        <li id="R_7.017.023cd">uvācāgniṃ samādhāya maraṇāya kṛtatvarā</li>
    </ul>
    <ul id="R_7.017.024">
        <li id="R_7.017.024ab">dharṣitāyās tvayānārya nedānīṃ mama jīvitam</li>
        <li id="R_7.017.024cd">rakṣas tasmāt pravekṣyāmi paśyatas te hutāśanam</li>
    </ul>
    <ul id="R_7.017.025">
        <li id="R_7.017.025ab">yasmāt tu dharṣitā cāham apāpā cāpy anāthavat</li>
        <li id="R_7.017.025cd">tasmāt tava vadhārthaṃ vai samutpatsyāmy ahaṃ punaḥ</li>
    </ul>
    <ul id="R_7.017.026">
        <li id="R_7.017.026ab">na hi śakyaḥ striyā pāpa hantuṃ tvaṃ tu viśeṣataḥ</li>
        <li id="R_7.017.026cd">śāpe tvayi mayotsṛṣṭe tapasaś ca vyayo bhavet</li>
    </ul>
    <ul id="R_7.017.027">
        <li id="R_7.017.027ab">yadi tv asti mayā kiṃ cit kṛtaṃ dattaṃ hutaṃ tathā</li>
        <li id="R_7.017.027cd">tena hy ayonijā sādhvī bhaveyaṃ dharmiṇaḥ sutā</li>
    </ul>
    <ul id="R_7.017.028">
        <li id="R_7.017.028ab">evam uktvā praviṣṭā sā jvalantaṃ vai hutāśanam</li>
        <li id="R_7.017.028cd">papāta ca divo divyā puṣpavṛṣṭiḥ samantataḥ</li>
    </ul>
    <ul id="R_7.017.029">
        <li id="R_7.017.029ab">pūrvaṃ krodhahataḥ śatrur yayāsau nihatas tvayā</li>
        <li id="R_7.017.029cd">samupāśritya śailābhaṃ tava vīryam amānuṣam</li>
    </ul>
    <ul id="R_7.017.030">
        <li id="R_7.017.030ab">evam eṣā mahābhāgā martyeṣūtpadyate punaḥ</li>
        <li id="R_7.017.030cd">kṣetre halamukhagraste vedyām agniśikhopamā</li>
    </ul>
    <ul id="R_7.017.031">
        <li id="R_7.017.031ab">eṣā vedavatī nāma pūrvam āsīt kṛte yuge</li>
        <li id="R_7.017.031cd">tretāyugam anuprāpya vadhārthaṃ tasya rakṣasaḥ</li>
        <li id="R_7.017.031ef">sītotpanneti sītaiṣā mānuṣaiḥ punar ucyate</li>
    </ul>
    <ul id="R_7.018.001">
        <li id="R_7.018.001ab">praviṣṭāyāṃ hutāśaṃ tu vedavatyāṃ sa rāvaṇaḥ</li>
        <li id="R_7.018.001cd">puṣpakaṃ tat samāruhya paricakrāma medinīm</li>
    </ul>
    <ul id="R_7.018.002">
        <li id="R_7.018.002ab">tato maruttaṃ nṛpatiṃ yajantaṃ saha daivataiḥ</li>
        <li id="R_7.018.002cd">uśīrabījam āsādya dadarśa sa tu rākṣasaḥ</li>
    </ul>
    <ul id="R_7.018.003">
        <li id="R_7.018.003ab">saṃvarto nāma brahmarṣir bhrātā sākṣād bṛhaspateḥ</li>
        <li id="R_7.018.003cd">yājayām āsa dharmajñaḥ sarvair brahmagaṇair vṛtaḥ</li>
    </ul>
    <ul id="R_7.018.004">
        <li id="R_7.018.004ab">dṛṣṭvā devās tu tad rakṣo varadānena durjayam</li>
        <li id="R_7.018.004cd">tāṃ tāṃ yoniṃ samāpannās tasya dharṣaṇabhīravaḥ</li>
    </ul>
    <ul id="R_7.018.005">
        <li id="R_7.018.005ab">indro mayūraḥ saṃvṛtto dharmarājas tu vāyasaḥ</li>
        <li id="R_7.018.005cd">kṛkalāso dhanādhyakṣo haṃso vai varuṇo 'bhavat</li>
    </ul>
    <ul id="R_7.018.006">
        <li id="R_7.018.006ab">taṃ ca rājānam āsādya rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_7.018.006cd">prāha yuddhaṃ prayacceti nirjito 'smīti vā vada</li>
    </ul>
    <ul id="R_7.018.007">
        <li id="R_7.018.007ab">tato marutto nṛpatiḥ ko bhavān ity uvāca tam</li>
        <li id="R_7.018.007cd">avahāsaṃ tato muktvā rākṣaso vākyam abravīt</li>
    </ul>
    <ul id="R_7.018.008">
        <li id="R_7.018.008ab">akutūhalabhāvena prīto 'smi tava pārthiva</li>
        <li id="R_7.018.008cd">dhanadasyānujaṃ yo māṃ nāvagacchasi rāvaṇam</li>
    </ul>
    <ul id="R_7.018.009">
        <li id="R_7.018.009ab">triṣu lokeṣu kaḥ so 'sti yo na jānāti me balam</li>
        <li id="R_7.018.009cd">bhrātaraṃ yena nirjitya vimānam idam āhṛtam</li>
    </ul>
    <ul id="R_7.018.010">
        <li id="R_7.018.010ab">tato marutto nṛpatis taṃ rākṣasam athābravīt</li>
        <li id="R_7.018.010cd">dhanyaḥ khalu bhavān yena jyeṣṭho bhrātā raṇe jitaḥ</li>
    </ul>
    <ul id="R_7.018.011">
        <li id="R_7.018.011ab">nādharmasahitaṃ ślāghyaṃ na lokapratisaṃhitam</li>
        <li id="R_7.018.011cd">karma daurātmyakaṃ kṛtvā ślāghase bhrātṛnirjayāt</li>
    </ul>
    <ul id="R_7.018.012">
        <li id="R_7.018.012ab">kiṃ tvaṃ prāk kevalaṃ dharmaṃ caritvā labdhavān varam</li>
        <li id="R_7.018.012cd">śrutapūrvaṃ hi na mayā yādṛśaṃ bhāṣase svayam</li>
    </ul>
    <ul id="R_7.018.013">
        <li id="R_7.018.013ab">tataḥ śarāsanaṃ gṛhya sāyakāṃś ca sa pārthivaḥ</li>
        <li id="R_7.018.013cd">raṇāya niryayau kruddhaḥ saṃvarto mārgam āvṛṇot</li>
    </ul>
    <ul id="R_7.018.014">
        <li id="R_7.018.014ab">so 'bravīt snehasaṃyuktaṃ maruttaṃ taṃ mahān ṛṣiḥ</li>
        <li id="R_7.018.014cd">śrotavyaṃ yadi madvākyaṃ saṃprahāro na te kṣamaḥ</li>
    </ul>
    <ul id="R_7.018.015">
        <li id="R_7.018.015ab">māheśvaram idaṃ satram asamāptaṃ kulaṃ dahet</li>
        <li id="R_7.018.015cd">dīkṣitasya kuto yuddhaṃ krūratvaṃ dīkṣite kutaḥ</li>
    </ul>
    <ul id="R_7.018.016">
        <li id="R_7.018.016ab">saṃśayaś ca raṇe nityaṃ rākṣasaś caiṣa durjayaḥ</li>
        <li id="R_7.018.016cd">sa nivṛtto guror vākyān maruttaḥ pṛthivīpatiḥ</li>
        <li id="R_7.018.016ef">visṛjya saśaraṃ cāpaṃ svastho makhamukho 'bhavat</li>
    </ul>
    <ul id="R_7.018.017">
        <li id="R_7.018.017ab">tatas taṃ nirjitaṃ matvā ghoṣayām āsa vai śukaḥ</li>
        <li id="R_7.018.017cd">rāvaṇo jitavāṃś ceti harṣān nādaṃ ca muktavān</li>
    </ul>
    <ul id="R_7.018.018">
        <li id="R_7.018.018ab">tān bhakṣayitvā tatrasthān maharṣīn yajñam āgatān</li>
        <li id="R_7.018.018cd">vitṛpto rudhirais teṣāṃ punaḥ saṃprayayau mahīm</li>
    </ul>
    <ul id="R_7.018.019">
        <li id="R_7.018.019ab">rāvaṇe tu gate devāḥ sendrāś caiva divaukasaḥ</li>
        <li id="R_7.018.019cd">tataḥ svāṃ yonim āsādya tāni sattvāny athābruvan</li>
    </ul>
    <ul id="R_7.018.020">
        <li id="R_7.018.020ab">harṣāt tadābravīd indro mayūraṃ nīlabarhiṇam</li>
        <li id="R_7.018.020cd">prīto 'smi tava dharmajña upakārād vihaṃgama</li>
    </ul>
    <ul id="R_7.018.021">
        <li id="R_7.018.021ab">mama netrasahasraṃ yat tat te barhe bhaviṣyati</li>
        <li id="R_7.018.021cd">varṣamāṇe mayi mudaṃ prāpsyase prītilakṣaṇam</li>
    </ul>
    <ul id="R_7.018.022">
        <li id="R_7.018.022ab">nīlāḥ kila purā barhā mayūrāṇāṃ narādhipa</li>
        <li id="R_7.018.022cd">surādhipād varaṃ prāpya gatāḥ sarve vicitratām</li>
    </ul>
    <ul id="R_7.018.023">
        <li id="R_7.018.023ab">dharmarājo 'bravīd rāma prāgvaṃśe vāyasaṃ sthitam</li>
        <li id="R_7.018.023cd">pakṣiṃs tavāsmi suprītaḥ prītasya ca vacaḥ śṛṇu</li>
    </ul>
    <ul id="R_7.018.024">
        <li id="R_7.018.024ab">yathānye vividhai rogaiḥ pīḍyante prāṇino mayā</li>
        <li id="R_7.018.024cd">te na te prabhaviṣyanti mayi prīte na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.018.025">
        <li id="R_7.018.025ab">mṛtyutas te bhayaṃ nāsti varān mama vihaṃgama</li>
        <li id="R_7.018.025cd">yāvat tvāṃ na vadhiṣyanti narās tāvad bhaviṣyasi</li>
    </ul>
    <ul id="R_7.018.026">
        <li id="R_7.018.026ab">ye ca madviṣayasthās tu mānavāḥ kṣudhayārditāḥ</li>
        <li id="R_7.018.026cd">tvayi bhukte tu tṛptās te bhaviṣyanti sabāndhavāḥ</li>
    </ul>
    <ul id="R_7.018.027">
        <li id="R_7.018.027ab">varuṇas tv abravīd dhaṃsaṃ gaṅgātoyavicāriṇam</li>
        <li id="R_7.018.027cd">śrūyatāṃ prītisaṃyuktaṃ vacaḥ patraratheśvara</li>
    </ul>
    <ul id="R_7.018.028">
        <li id="R_7.018.028ab">varṇo manoharaḥ saumyaś candramaṇḍalasaṃnibhaḥ</li>
        <li id="R_7.018.028cd">bhaviṣyati tavodagraḥ śuklaphenasamaprabhaḥ</li>
    </ul>
    <ul id="R_7.018.029">
        <li id="R_7.018.029ab">maccharīraṃ samāsādya kānto nityaṃ bhaviṣyasi</li>
        <li id="R_7.018.029cd">prāpsyase cātulāṃ prītim etan me prītilakṣaṇam</li>
    </ul>
    <ul id="R_7.018.030">
        <li id="R_7.018.030ab">haṃsānāṃ hi purā rāma na varṇaḥ sarvapāṇḍuraḥ</li>
        <li id="R_7.018.030cd">pakṣā nīlāgrasaṃvītāḥ kroḍāḥ śaṣpāgranirmalāḥ</li>
    </ul>
    <ul id="R_7.018.031">
        <li id="R_7.018.031ab">athābravīd vaiśravaṇaḥ kṛkalāsaṃ girau sthitam</li>
        <li id="R_7.018.031cd">hairaṇyaṃ saṃprayacchāmi varṇaṃ prītis tavāpy aham</li>
    </ul>
    <ul id="R_7.018.032">
        <li id="R_7.018.032ab">sadravyaṃ ca śiro nityaṃ bhaviṣyati tavākṣayam</li>
        <li id="R_7.018.032cd">eṣa kāñcanako varṇo matprītyā te bhaviṣyati</li>
    </ul>
    <ul id="R_7.018.033">
        <li id="R_7.018.033ab">evaṃ dattvā varāṃs tebhyas tasmin yajñotsave surāḥ</li>
        <li id="R_7.018.033cd">nivṛtte saha rājñā vai punaḥ svabhavanaṃ gatāḥ</li>
    </ul>
    <ul id="R_7.019.001">
        <li id="R_7.019.001ab">atha jitvā maruttaṃ sa prayayau rākṣasādhipaḥ</li>
        <li id="R_7.019.001cd">nagarāṇi narendrāṇāṃ yuddhakāṅkṣī daśānanaḥ</li>
    </ul>
    <ul id="R_7.019.002">
        <li id="R_7.019.002ab">sa samāsādya rājendrān mahendravaruṇopamān</li>
        <li id="R_7.019.002cd">abravīd rākṣasendras tu yuddhaṃ me dīyatām iti</li>
    </ul>
    <ul id="R_7.019.003">
        <li id="R_7.019.003ab">nirjitāḥ smeti vā brūta eṣo hi mama niścayaḥ</li>
        <li id="R_7.019.003cd">anyathā kurvatām evaṃ mokṣo vo nopapadyate</li>
    </ul>
    <ul id="R_7.019.004">
        <li id="R_7.019.004ab">tatas tu bahavaḥ prājñāḥ pārthivā dharmaṇiścayāḥ</li>
        <li id="R_7.019.004cd">nirjitāḥ smety abhāṣanta jñātvā varabalaṃ ripoḥ</li>
    </ul>
    <ul id="R_7.019.005">
        <li id="R_7.019.005ab">duṣyantaḥ suratho gādhir gayo rājā purūravāḥ</li>
        <li id="R_7.019.005cd">ete sarve 'bruvaṃs tāta nirjitāḥ smeti pārthivāḥ</li>
    </ul>
    <ul id="R_7.019.006">
        <li id="R_7.019.006ab">athāyodhyāṃ samāsādya rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_7.019.006cd">suguptām anaraṇyena śakreṇevāmarāvatīm</li>
    </ul>
    <ul id="R_7.019.007">
        <li id="R_7.019.007ab">prāha rājānam āsādya yuddhaṃ me saṃpradīyatām</li>
        <li id="R_7.019.007cd">nirjito 'smīti vā brūhi mamaitad iha śāsanam</li>
    </ul>
    <ul id="R_7.019.008">
        <li id="R_7.019.008ab">anaraṇyaḥ susaṃkruddho rākṣasendram athābravīt</li>
        <li id="R_7.019.008cd">dīyate dvandvayuddhaṃ te rākṣasādhipate mayā</li>
    </ul>
    <ul id="R_7.019.009">
        <li id="R_7.019.009ab">atha pūrvaṃ śrutārthena sajjitaṃ sumahad dhi yat</li>
        <li id="R_7.019.009cd">niṣkrāmat tan narendrasya balaṃ rakṣovadhodyatam</li>
    </ul>
    <ul id="R_7.019.010">
        <li id="R_7.019.010ab">nāgānāṃ bahusāhasraṃ vājinām ayutaṃ tathā</li>
        <li id="R_7.019.010cd">mahīṃ saṃchādya niṣkrāntaṃ sapadātirathaṃ kṣaṇāt</li>
    </ul>
    <ul id="R_7.019.011">
        <li id="R_7.019.011ab">tad rāvaṇabalaṃ prāpya balaṃ tasya mahīpateḥ</li>
        <li id="R_7.019.011cd">prāṇaśyata tadā rājan havyaṃ hutam ivānale</li>
    </ul>
    <ul id="R_7.019.012">
        <li id="R_7.019.012ab">so 'paśyata narendras tu naśyamānaṃ mahad balam</li>
        <li id="R_7.019.012cd">mahārṇavaṃ samāsādya yathā pañcāpagā jalam</li>
    </ul>
    <ul id="R_7.019.013">
        <li id="R_7.019.013ab">tataḥ śakradhanuḥprakhyaṃ dhanur visphārayan svayam</li>
        <li id="R_7.019.013cd">āsadāda narendrās taṃ rāvaṇaṃ krodhamūrchitaḥ</li>
    </ul>
    <ul id="R_7.019.014">
        <li id="R_7.019.014ab">tato bāṇaśatāny aṣṭau pātayām āsa mūrdhani</li>
        <li id="R_7.019.014cd">tasya rākṣasarājasya ikṣvākukulanandanaḥ</li>
    </ul>
    <ul id="R_7.019.015">
        <li id="R_7.019.015ab">tasya bāṇāḥ patantas te cakrire na kṣataṃ kva cit</li>
        <li id="R_7.019.015cd">vāridhārā ivābhrebhyaḥ patantyo nagamūrdhani</li>
    </ul>
    <ul id="R_7.019.016">
        <li id="R_7.019.016ab">tato rākṣasarājena kruddhena nṛpatis tadā</li>
        <li id="R_7.019.016cd">talena bhihato mūrdhni sa rathān nipapāta ha</li>
    </ul>
    <ul id="R_7.019.017">
        <li id="R_7.019.017ab">sa rājā patito bhūmau vihvalāṅgaḥ pravepitaḥ</li>
        <li id="R_7.019.017cd">vajradagdha ivāraṇye sālo nipatito mahān</li>
    </ul>
    <ul id="R_7.019.018">
        <li id="R_7.019.018ab">taṃ prahasyābravīd rakṣa ikṣvākuṃ pṛthivīpatim</li>
        <li id="R_7.019.018cd">kim idānīṃ tvayā prāptaṃ phalaṃ māṃ prati yudhyatā</li>
    </ul>
    <ul id="R_7.019.019">
        <li id="R_7.019.019ab">trailokye nāsti yo dvandvaṃ mama dadyān narādhipa</li>
        <li id="R_7.019.019cd">śaṅke pramatto bhogeṣu na śṛṇoṣi balaṃ mama</li>
    </ul>
    <ul id="R_7.019.020">
        <li id="R_7.019.020ab">tasyaivaṃ bruvato rājā mandāsur vākyam abravīt</li>
        <li id="R_7.019.020cd">kiṃ śakyam iha kartuṃ vai yat kālo duratikramaḥ</li>
    </ul>
    <ul id="R_7.019.021">
        <li id="R_7.019.021ab">na hy ahaṃ nirjito rakṣas tvayā cātmapraśaṃsinā</li>
        <li id="R_7.019.021cd">kāleneha vipanno 'haṃ hetubhūtas tu me bhavān</li>
    </ul>
    <ul id="R_7.019.022">
        <li id="R_7.019.022ab">kiṃ tv idānīṃ mayā śakyaṃ kartuṃ prāṇaparikṣaye</li>
        <li id="R_7.019.022cd">ikṣvākuparibhāvitvād vaco vakṣyāmi rākṣasa</li>
    </ul>
    <ul id="R_7.019.023">
        <li id="R_7.019.023ab">yadi dattaṃ yadi hutaṃ yadi me sukṛtaṃ tapaḥ</li>
        <li id="R_7.019.023cd">yadi guptāḥ prajāḥ samyak tathā satyaṃ vaco 'stu me</li>
    </ul>
    <ul id="R_7.019.024">
        <li id="R_7.019.024ab">utpatsyate kule hy asminn ikṣvākūṇāṃ mahātmanām</li>
        <li id="R_7.019.024cd">rājā paramatejasvī yas te prāṇān hariṣyati</li>
    </ul>
    <ul id="R_7.019.025">
        <li id="R_7.019.025ab">tato jaladharodagras tāḍito devadundubhiḥ</li>
        <li id="R_7.019.025cd">tasminn udāhṛte śāpe puṣpavṛṣṭiś ca khāc cyutā</li>
    </ul>
    <ul id="R_7.019.026">
        <li id="R_7.019.026ab">tataḥ sa rājā rājendra gataḥ sthānaṃ triviṣṭapam</li>
        <li id="R_7.019.026cd">svargate ca nṛpe rāma rākṣasaḥ sa nyavartata</li>
    </ul>
    <ul id="R_7.020.001">
        <li id="R_7.020.001ab">tato vitrāsayan martyān pṛthivyāṃ rākṣasādhipaḥ</li>
        <li id="R_7.020.001cd">āsasāda ghane tasmin nāradaṃ munisattamam</li>
    </ul>
    <ul id="R_7.020.002">
        <li id="R_7.020.002ab">nāradas tu mahātejā devarṣir amitaprabhaḥ</li>
        <li id="R_7.020.002cd">abravīn meghapṛṣṭhastho rāvaṇaṃ puṣpake sthitam</li>
    </ul>
    <ul id="R_7.020.003">
        <li id="R_7.020.003ab">rākṣasādhipate saumya tiṣṭha viśravasaḥ suta</li>
        <li id="R_7.020.003cd">prīto 'smy abhijanopeta vikramair ūrjitais tava</li>
    </ul>
    <ul id="R_7.020.004">
        <li id="R_7.020.004ab">viṣṇunā daityaghātaiś ca tārkṣyasyoragadharṣaṇaiḥ</li>
        <li id="R_7.020.004cd">tvayā samaramardaiś ca bhṛśaṃ hi paritoṣitaḥ</li>
    </ul>
    <ul id="R_7.020.005">
        <li id="R_7.020.005ab">kiṃ cid vakṣyāmi tāvat te śrotavyaṃ śroṣyase yadi</li>
        <li id="R_7.020.005cd">śrutvā cānantaraṃ kāryaṃ tvayā rākṣasapuṃgava</li>
    </ul>
    <ul id="R_7.020.006">
        <li id="R_7.020.006ab">kim ayaṃ vadhyate lokas tvayāvadhyena daivataiḥ</li>
        <li id="R_7.020.006cd">hata eva hy ayaṃ loko yadā mṛtyuvaśaṃ gataḥ</li>
    </ul>
    <ul id="R_7.020.007">
        <li id="R_7.020.007ab">paśya tāvan mahābāho rākṣaseśvaramānuṣam</li>
        <li id="R_7.020.007cd">lokam enaṃ vicitrārthaṃ yasya na jñāyate gatiḥ</li>
    </ul>
    <ul id="R_7.020.008">
        <li id="R_7.020.008ab">kva cid vāditranṛttāni sevyante muditair janaiḥ</li>
        <li id="R_7.020.008cd">rudyate cāparair ārtair dhārāśrunayanānanaiḥ</li>
    </ul>
    <ul id="R_7.020.009">
        <li id="R_7.020.009ab">mātā pitṛsutasnehair bhāryā bandhumanoramaiḥ</li>
        <li id="R_7.020.009cd">mohenāyaṃ jano dhvastaḥ kleśaṃ svaṃ nāvabudhyate</li>
    </ul>
    <ul id="R_7.020.010">
        <li id="R_7.020.010ab">tat kim evaṃ parikliśya lokaṃ mohanirākṛtam</li>
        <li id="R_7.020.010cd">jita eva tvayā saumya martyaloko na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.020.011">
        <li id="R_7.020.011ab">evam uktas tu laṅkeśo dīpyamāna ivaujasā</li>
        <li id="R_7.020.011cd">abravīn nāradaṃ tatra saṃprahasyābhivādya ca</li>
    </ul>
    <ul id="R_7.020.012">
        <li id="R_7.020.012ab">maharṣe devagandharvavihāra samarapriya</li>
        <li id="R_7.020.012cd">ahaṃ khalūdyato gantuṃ vijayārthī rasātalam</li>
    </ul>
    <ul id="R_7.020.013">
        <li id="R_7.020.013ab">tato lokatrayaṃ jitvā sthāpya nāgān surān vaśe</li>
        <li id="R_7.020.013cd">samudram amṛtārthaṃ vai mathiṣyāmi rasālayam</li>
    </ul>
    <ul id="R_7.020.014">
        <li id="R_7.020.014ab">athābravīd daśagrīvaṃ nārado bhagavān ṛṣiḥ</li>
        <li id="R_7.020.014cd">kva khalv idānīṃ mārgeṇa tvayānena gamiṣyate</li>
    </ul>
    <ul id="R_7.020.015">
        <li id="R_7.020.015ab">ayaṃ khalu sudurgamyaḥ pitṛrājñaḥ puraṃ prati</li>
        <li id="R_7.020.015cd">mārgo gacchati durdharṣo yamasyāmitrakarśana</li>
    </ul>
    <ul id="R_7.020.016">
        <li id="R_7.020.016ab">sa tu śāradameghābhaṃ muktvā hāsaṃ daśānanaḥ</li>
        <li id="R_7.020.016cd">uvāca kṛtam ity eva vacanaṃ cedam abravīt</li>
    </ul>
    <ul id="R_7.020.017">
        <li id="R_7.020.017ab">tasmād eṣa mahābrahman vaivasvatavadhodyataḥ</li>
        <li id="R_7.020.017cd">gacchāmi dakṣiṇām āśāṃ yatra sūryātmajo nṛpaḥ</li>
    </ul>
    <ul id="R_7.020.018">
        <li id="R_7.020.018ab">mayā hi bhagavan krodhāt pratijñātaṃ raṇārthinā</li>
        <li id="R_7.020.018cd">avajeṣyāmi caturo lokapālān iti prabho</li>
    </ul>
    <ul id="R_7.020.019">
        <li id="R_7.020.019ab">tenaiṣa prasthito 'haṃ vai pitṛrājapuraṃ prati</li>
        <li id="R_7.020.019cd">prāṇisaṃkleśakartāraṃ yojayiṣyāmi mṛtyunā</li>
    </ul>
    <ul id="R_7.020.020">
        <li id="R_7.020.020ab">evam uktvā daśagrīvo muniṃ tam abhivādya ca</li>
        <li id="R_7.020.020cd">prayayau dakṣiṇām āśāṃ prahṛṣṭaiḥ saha mantribhiḥ</li>
    </ul>
    <ul id="R_7.020.021">
        <li id="R_7.020.021ab">nāradas tu mahātejā muhūrtaṃ dhyānam āsthitaḥ</li>
        <li id="R_7.020.021cd">cintayām āsa viprendro vidhūma iva pāvakaḥ</li>
    </ul>
    <ul id="R_7.020.022">
        <li id="R_7.020.022ab">yena lokās trayaḥ sendrāḥ kliśyante sacarācarāḥ</li>
        <li id="R_7.020.022cd">kṣīṇe cāyuṣi dharme ca sa kālo hiṃsyate katham</li>
    </ul>
    <ul id="R_7.020.023">
        <li id="R_7.020.023ab">yasya nityaṃ trayo lokā vidravanti bhayārditāḥ</li>
        <li id="R_7.020.023cd">taṃ kathaṃ rākṣasendro 'sau svayam evābhigacchati</li>
    </ul>
    <ul id="R_7.020.024">
        <li id="R_7.020.024ab">yo vidhātā ca dhātā ca sukṛte duṣkṛte yathā</li>
        <li id="R_7.020.024cd">trailokyaṃ vijitaṃ yena taṃ kathaṃ nu vijeṣyati</li>
    </ul>
    <ul id="R_7.020.025">
        <li id="R_7.020.025ab">aparaṃ kiṃ nu kṛtvaivaṃ vidhānaṃ saṃvidhāsyati</li>
        <li id="R_7.020.025cd">kautūhalasamutpanno yāsyāmi yamasādanam</li>
    </ul>
    <ul id="R_7.021.001">
        <li id="R_7.021.001ab">evaṃ saṃcintya viprendro jagāma laghuvikramaḥ</li>
        <li id="R_7.021.001cd">ākhyātuṃ tad yathāvṛttaṃ yamasya sadanaṃ prati</li>
    </ul>
    <ul id="R_7.021.002">
        <li id="R_7.021.002ab">apaśyat sa yamaṃ tatra devam agnipuraskṛtam</li>
        <li id="R_7.021.002cd">vidhānam upatiṣṭhantaṃ prāṇino yasya yādṛśam</li>
    </ul>
    <ul id="R_7.021.003">
        <li id="R_7.021.003ab">sa tu dṛṣṭvā yamaḥ prāptaṃ maharṣiṃ tatra nāradam</li>
        <li id="R_7.021.003cd">abravīt sukham āsīnam arghyam āvedya dharmataḥ</li>
    </ul>
    <ul id="R_7.021.004">
        <li id="R_7.021.004ab">kaccit kṣemaṃ nu devarṣe kaccid dharmo na naśyati</li>
        <li id="R_7.021.004cd">kim āgamanakṛtyaṃ te devagandharvasevita</li>
    </ul>
    <ul id="R_7.021.005">
        <li id="R_7.021.005ab">abravīt tu tadā vākyaṃ nārado bhagavān ṛṣiḥ</li>
        <li id="R_7.021.005cd">śrūyatām abhidhāsyāmi vidhānaṃ ca vidhīyatām</li>
    </ul>
    <ul id="R_7.021.006">
        <li id="R_7.021.006ab">eṣa nāmnā daśagrīvaḥ pitṛrāja niśācaraḥ</li>
        <li id="R_7.021.006cd">upayāti vaśaṃ netuṃ vikramais tvāṃ sudurjayam</li>
    </ul>
    <ul id="R_7.021.007">
        <li id="R_7.021.007ab">etena kāraṇenāhaṃ tvarito 'smy āgataḥ prabho</li>
        <li id="R_7.021.007cd">daṇḍapraharaṇasyādya tava kiṃ nu kariṣyati</li>
    </ul>
    <ul id="R_7.021.008">
        <li id="R_7.021.008ab">etasminn antare dūrād aṃśumantam ivoditam</li>
        <li id="R_7.021.008cd">dadṛśe divyam āyāntaṃ vimānaṃ tasya rakṣasaḥ</li>
    </ul>
    <ul id="R_7.021.009">
        <li id="R_7.021.009ab">taṃ deśaṃ prabhayā tasya puṣpakasya mahābalaḥ</li>
        <li id="R_7.021.009cd">kṛtvā vitimiraṃ sarvaṃ samīpaṃ samavartata</li>
    </ul>
    <ul id="R_7.021.010">
        <li id="R_7.021.010ab">sa tv apaśyan mahābāhur daśagrīvas tatas tataḥ</li>
        <li id="R_7.021.010cd">prāṇinaḥ sukṛtaṃ karma bhuñjānāṃś caiva duṣkṛtam</li>
    </ul>
    <ul id="R_7.021.011">
        <li id="R_7.021.011ab">tatas tān vadhyamānāṃs tu karmabhir duṣkṛtaiḥ svakaiḥ</li>
        <li id="R_7.021.011cd">rāvaṇo mocayām āsa vikrameṇa balād balī</li>
    </ul>
    <ul id="R_7.021.012">
        <li id="R_7.021.012ab">preteṣu mucyamāneṣu rākṣasena balīyasā</li>
        <li id="R_7.021.012cd">pretagopāḥ susaṃrabdhā rākṣasendram abhidravan</li>
    </ul>
    <ul id="R_7.021.013">
        <li id="R_7.021.013ab">te prāsaiḥ parighaiḥ śūlair mudgaraiḥ śaktitomaraiḥ</li>
        <li id="R_7.021.013cd">puṣpakaṃ samavarṣanta śūrāḥ śatasahasraśaḥ</li>
    </ul>
    <ul id="R_7.021.014">
        <li id="R_7.021.014ab">tasyāsanāni prāsādān vedikāstaraṇāni ca</li>
        <li id="R_7.021.014cd">puṣpakasya babhañjus te śīghraṃ madhukarā iva</li>
    </ul>
    <ul id="R_7.021.015">
        <li id="R_7.021.015ab">devaniṣṭhānabhūtaṃ tad vimānaṃ puṣpakaṃ mṛdhe</li>
        <li id="R_7.021.015cd">bhajyamānaṃ tathaivāsīd akṣayaṃ brahmatejasā</li>
    </ul>
    <ul id="R_7.021.016">
        <li id="R_7.021.016ab">tatas te rāvaṇāmātyā yathākāmaṃ yathābalam</li>
        <li id="R_7.021.016cd">ayudhyanta mahāvīryāḥ sa ca rājā daśānanaḥ</li>
    </ul>
    <ul id="R_7.021.017">
        <li id="R_7.021.017ab">te tu śoṇitadigdhāṅgāḥ sarvaśastrasamāhatāḥ</li>
        <li id="R_7.021.017cd">amātyā rākṣasendrasya cakrur āyodhanaṃ mahat</li>
    </ul>
    <ul id="R_7.021.018">
        <li id="R_7.021.018ab">anyonyaṃ ca mahābhāgā jaghnuḥ praharaṇair yudhi</li>
        <li id="R_7.021.018cd">yamasya ca mahat sainyaṃ rākṣasasya ca mantriṇaḥ</li>
    </ul>
    <ul id="R_7.021.019">
        <li id="R_7.021.019ab">amātyāṃs tāṃs tu saṃtyajya rākṣasasya mahaujasaḥ</li>
        <li id="R_7.021.019cd">tam eva samadhāvanta śūlavarṣair daśānanam</li>
    </ul>
    <ul id="R_7.021.020">
        <li id="R_7.021.020ab">tataḥ śoṇitadigdhāṅgaḥ prahārair jarjarīkṛtaḥ</li>
        <li id="R_7.021.020cd">vimāne rākṣasaśreṣṭhaḥ phullāśoka ivābabhau</li>
    </ul>
    <ul id="R_7.021.021">
        <li id="R_7.021.021ab">sa śūlāni gadāḥ prāsāñ śaktitomarasāyakān</li>
        <li id="R_7.021.021cd">musalāni śilāvṛkṣān mumocāstrabalād balī</li>
    </ul>
    <ul id="R_7.021.022">
        <li id="R_7.021.022ab">tāṃs tu sarvān samākṣipya tad astram apahatya ca</li>
        <li id="R_7.021.022cd">jaghnus te rākṣasaṃ ghoram ekaṃ śatasahasrakaḥ</li>
    </ul>
    <ul id="R_7.021.023">
        <li id="R_7.021.023ab">parivārya ca taṃ sarve śailaṃ meghotkarā iva</li>
        <li id="R_7.021.023cd">bhindipālaiś ca śūlaiś ca nirucchvāsam akārayan</li>
    </ul>
    <ul id="R_7.021.024">
        <li id="R_7.021.024ab">vimuktakavacaḥ kruddho siktaḥ śoṇitavisravaiḥ</li>
        <li id="R_7.021.024cd">sa puṣpakaṃ parityajya pṛthivyām avatiṣṭhata</li>
    </ul>
    <ul id="R_7.021.025">
        <li id="R_7.021.025ab">tataḥ sa kārmukī bāṇī pṛthivyāṃ rākṣasādhipaḥ</li>
        <li id="R_7.021.025cd">labdhasaṃjño muhūrtena kruddhas tasthau yathāntakaḥ</li>
    </ul>
    <ul id="R_7.021.026">
        <li id="R_7.021.026ab">tataḥ pāśupataṃ divyam astraṃ saṃdhāya kārmuke</li>
        <li id="R_7.021.026cd">tiṣṭha tiṣṭheti tān uktvā tac cāpaṃ vyapakarṣata</li>
    </ul>
    <ul id="R_7.021.027">
        <li id="R_7.021.027ab">jvālāmālī sa tu śaraḥ kravyādānugato raṇe</li>
        <li id="R_7.021.027cd">mukto gulmān drumāṃś caiva bhasmakṛtvā pradhāvati</li>
    </ul>
    <ul id="R_7.021.028">
        <li id="R_7.021.028ab">te tasya tejasā dagdhāḥ sainyā vaivasvatasya tu</li>
        <li id="R_7.021.028cd">raṇe tasmin nipatitā dāvadagdhā nagā iva</li>
    </ul>
    <ul id="R_7.021.029">
        <li id="R_7.021.029ab">tataḥ sa sacivaiḥ sārdhaṃ rākṣaso bhīmavikramaḥ</li>
        <li id="R_7.021.029cd">nanāda sumahānādaṃ kampayann iva medinīm</li>
    </ul>
    <ul id="R_7.022.001">
        <li id="R_7.022.001ab">sa tu tasya mahānādaṃ śrutvā vaivasvato yamaḥ</li>
        <li id="R_7.022.001cd">śatruṃ vijayinaṃ mene svabalasya ca saṃkṣayam</li>
    </ul>
    <ul id="R_7.022.002">
        <li id="R_7.022.002ab">sa tu yodhān hatān matvā krodhaparyākulekṣaṇaḥ</li>
        <li id="R_7.022.002cd">abravīt tvaritaṃ sūtaṃ rathaḥ samupanīyatām</li>
    </ul>
    <ul id="R_7.022.003">
        <li id="R_7.022.003ab">tasya sūto rathaṃ divyam upasthāpya mahāsvanam</li>
        <li id="R_7.022.003cd">sthitaḥ sa ca mahātejā āruroha mahāratham</li>
    </ul>
    <ul id="R_7.022.004">
        <li id="R_7.022.004ab">pāśamudgarahastaś ca mṛtyus tasyāgrato sthitaḥ</li>
        <li id="R_7.022.004cd">yena saṃkṣipyate sarvaṃ trailokyaṃ sacarācaram</li>
    </ul>
    <ul id="R_7.022.005">
        <li id="R_7.022.005ab">kāladaṇḍaś ca pārśvastho mūrtimān syandane sthitaḥ</li>
        <li id="R_7.022.005cd">yamapraharaṇaṃ divyaṃ prajvalann iva tejasā</li>
    </ul>
    <ul id="R_7.022.006">
        <li id="R_7.022.006ab">tato lokās trayas trastāḥ kampante ca divaukasaḥ</li>
        <li id="R_7.022.006cd">kālaṃ kruddhaṃ tadā dṛṣṭvā lokatrayabhayāvaham</li>
    </ul>
    <ul id="R_7.022.007">
        <li id="R_7.022.007ab">dṛṣṭvā tu te taṃ vikṛtaṃ rathaṃ mṛtyusamanvitam</li>
        <li id="R_7.022.007cd">sacivā rākṣasendrasya sarvalokabhayāvaham</li>
    </ul>
    <ul id="R_7.022.008">
        <li id="R_7.022.008ab">laghusattvatayā sarve naṣṭasaṃjñā bhayārditāḥ</li>
        <li id="R_7.022.008cd">nātra yoddhuṃ samarthāḥ sma ity uktvā vipradudruvuḥ</li>
    </ul>
    <ul id="R_7.022.009">
        <li id="R_7.022.009ab">sa tu taṃ tādṛśaṃ dṛṣṭvā rathaṃ lokabhayāvaham</li>
        <li id="R_7.022.009cd">nākṣubhyata tadā rakṣo vyathā caivāsya nābhavat</li>
    </ul>
    <ul id="R_7.022.010">
        <li id="R_7.022.010ab">sa tu rāvaṇam āsādya visṛjañ śaktitomarān</li>
        <li id="R_7.022.010cd">yamo marmāṇi saṃkruddho rākṣasasya nyakṛntata</li>
    </ul>
    <ul id="R_7.022.011">
        <li id="R_7.022.011ab">rāvaṇas tu sthitaḥ svasthaḥ śaravarṣaṃ mumoca ha</li>
        <li id="R_7.022.011cd">tasmin vaivasvatarathe toyavarṣam ivāmbudaḥ</li>
    </ul>
    <ul id="R_7.022.012">
        <li id="R_7.022.012ab">tato mahāśaktiśataiḥ pātyamānair mahorasi</li>
        <li id="R_7.022.012cd">pratikartuṃ sa nāśaknod rākṣasaḥ śalyapīḍitaḥ</li>
    </ul>
    <ul id="R_7.022.013">
        <li id="R_7.022.013ab">nānāpraharaṇair evaṃ yamenāmitrakarśinā</li>
        <li id="R_7.022.013cd">saptarātraṃ kṛte saṃkhye na bhagno vijito 'pi vā</li>
    </ul>
    <ul id="R_7.022.014">
        <li id="R_7.022.014ab">tato 'bhavat punar yuddhaṃ yamarākṣasayos tadā</li>
        <li id="R_7.022.014cd">vijayākāṅkṣiṇos tatra samareṣv anivartinoḥ</li>
    </ul>
    <ul id="R_7.022.015">
        <li id="R_7.022.015ab">tato devāḥ sagandharvāḥ siddhāś ca paramarṣayaḥ</li>
        <li id="R_7.022.015cd">prajāpatiṃ puraskṛtya dadṛśus tad raṇājiram</li>
    </ul>
    <ul id="R_7.022.016">
        <li id="R_7.022.016ab">saṃvarta iva lokānām abhavad yudhyatos tayoḥ</li>
        <li id="R_7.022.016cd">rākṣasānāṃ ca mukhyasya pretānām īśvarasya ca</li>
    </ul>
    <ul id="R_7.022.017">
        <li id="R_7.022.017ab">rākṣasendras tataḥ kruddhaś cāpam āyamya saṃyuge</li>
        <li id="R_7.022.017cd">nirantaram ivākāśaṃ kurvan bāṇān mumoca ha</li>
    </ul>
    <ul id="R_7.022.018">
        <li id="R_7.022.018ab">mṛtyuṃ caturbhir viśikhaiḥ sūtaṃ saptabhir ardayat</li>
        <li id="R_7.022.018cd">yamaṃ śarasahasreṇa śīghraṃ marmasv atāḍayat</li>
    </ul>
    <ul id="R_7.022.019">
        <li id="R_7.022.019ab">tataḥ kruddhasya sahasā yamasyābhiviniḥsṛtaḥ</li>
        <li id="R_7.022.019cd">jvālāmālo viniśvāso vadanāt krodhapāvakaḥ</li>
    </ul>
    <ul id="R_7.022.020">
        <li id="R_7.022.020ab">tato 'paśyaṃs tadāścaryaṃ devadānavarākṣasāḥ</li>
        <li id="R_7.022.020cd">krodhajaṃ pāvakaṃ dīptaṃ didhakṣantaṃ ripor balam</li>
    </ul>
    <ul id="R_7.022.021">
        <li id="R_7.022.021ab">mṛtyus tu paramakruddho vaivasvatam athābravīt</li>
        <li id="R_7.022.021cd">muñca māṃ deva śīghraṃ tvaṃ nihanmi samare ripum</li>
    </ul>
    <ul id="R_7.022.022">
        <li id="R_7.022.022ab">narakaḥ śambaro vṛtraḥ śambhuḥ kārtasvaro balī</li>
        <li id="R_7.022.022cd">namucir virocanaś caiva tāv ubhau madhukaiṭabhau</li>
    </ul>
    <ul id="R_7.022.023">
        <li id="R_7.022.023ab">ete cānye ca bahavo balavanto durāsadāḥ</li>
        <li id="R_7.022.023cd">vinipannā mayā dṛṣṭāḥ kā cintāsmin niśācare</li>
    </ul>
    <ul id="R_7.022.024">
        <li id="R_7.022.024ab">muñca māṃ sādhu dharmajña yāvad enaṃ nihanmy aham</li>
        <li id="R_7.022.024cd">na hi kaś cin mayā dṛṣṭo muhūrtam api jīvati</li>
    </ul>
    <ul id="R_7.022.025">
        <li id="R_7.022.025ab">balaṃ mama na khalv etan maryādaiṣā nisargataḥ</li>
        <li id="R_7.022.025cd">saṃspṛṣṭo hi mayā kaś cin na jīved iti niścayaḥ</li>
    </ul>
    <ul id="R_7.022.026">
        <li id="R_7.022.026ab">etat tu vacanaṃ śrutvā dharmarājaḥ pratāpavān</li>
        <li id="R_7.022.026cd">abravīt tatra taṃ mṛtyumayam enaṃ nihanmy aham</li>
    </ul>
    <ul id="R_7.022.027">
        <li id="R_7.022.027ab">tataḥ saṃraktanayanaḥ kruddho vaivasvataḥ prabhuḥ</li>
        <li id="R_7.022.027cd">kāladaṇḍam amoghaṃ taṃ tolayām āsa pāṇinā</li>
    </ul>
    <ul id="R_7.022.028">
        <li id="R_7.022.028ab">yasya pārśveṣu niśchidrāḥ kālapāśāḥ pratiṣṭhitāḥ</li>
        <li id="R_7.022.028cd">pāvakasparśasaṃkāśo mudgaro mūrtimān sthitaḥ</li>
    </ul>
    <ul id="R_7.022.029">
        <li id="R_7.022.029ab">darśanād eva yaḥ prāṇān prāṇinām uparudhyati</li>
        <li id="R_7.022.029cd">kiṃ punas tāḍanād vāpi pīḍanād vāpi dehinaḥ</li>
    </ul>
    <ul id="R_7.022.030">
        <li id="R_7.022.030ab">sa jvālāparivāras tu pibann iva niśācaram</li>
        <li id="R_7.022.030cd">karaspṛṣṭo balavatā daṇḍaḥ kruddhaḥ sudāruṇaḥ</li>
    </ul>
    <ul id="R_7.022.031">
        <li id="R_7.022.031ab">tato vidudruvuḥ sarve sattvās tasmād raṇājirāt</li>
        <li id="R_7.022.031cd">surāś ca kṣubhitā dṛṣṭvā kāladaṇḍodyataṃ yamam</li>
    </ul>
    <ul id="R_7.022.032">
        <li id="R_7.022.032ab">tasmin prahartukāme tu daṇḍam udyamya rāvaṇam</li>
        <li id="R_7.022.032cd">yamaṃ pitāmahaḥ sākṣād darśayitvedam abravīt</li>
    </ul>
    <ul id="R_7.022.033">
        <li id="R_7.022.033ab">vaivasvata mahābāho na khalv atulavikrama</li>
        <li id="R_7.022.033cd">prahartavyaṃ tvayaitena daṇḍenāsmin niśācare</li>
    </ul>
    <ul id="R_7.022.034">
        <li id="R_7.022.034ab">varaḥ khalu mayā dattas tasya tridaśapuṃgava</li>
        <li id="R_7.022.034cd">tat tvayā nānṛtaṃ kāryaṃ yan mayā vyāhṛtaṃ vacaḥ</li>
    </ul>
    <ul id="R_7.022.035">
        <li id="R_7.022.035ab">amogho hy eṣa sarvāsāṃ prajānāṃ vinipātane</li>
        <li id="R_7.022.035cd">kāladaṇḍo mayā sṛṣṭaḥ pūrvaṃ mṛtyupuraskṛtaḥ</li>
    </ul>
    <ul id="R_7.022.036">
        <li id="R_7.022.036ab">tan na khalv eṣa te saumya pātyo rākṣasamūrdhani</li>
        <li id="R_7.022.036cd">na hy asmin patite kaś cin muhūrtam api jīvati</li>
    </ul>
    <ul id="R_7.022.037">
        <li id="R_7.022.037ab">yadi hy asmin nipatite na mriyetaiṣa rākṣasaḥ</li>
        <li id="R_7.022.037cd">mriyeta vā daśagrīvas tathāpy ubhayato 'nṛtam</li>
    </ul>
    <ul id="R_7.022.038">
        <li id="R_7.022.038ab">rākṣasendrān niyacchādya daṇḍam enaṃ vadhodyatam</li>
        <li id="R_7.022.038cd">satyaṃ mama kuruṣvedaṃ lokāṃs tvaṃ samavekṣya ca</li>
    </ul>
    <ul id="R_7.022.039">
        <li id="R_7.022.039ab">evam uktas tu dharmātmā pratyuvāca yamas tadā</li>
        <li id="R_7.022.039cd">eṣa vyāvartito daṇḍaḥ prabhaviṣṇur bhavān hi naḥ</li>
    </ul>
    <ul id="R_7.022.040">
        <li id="R_7.022.040ab">kiṃ tv idānīṃ mayā śakyaṃ kartuṃ raṇagatena hi</li>
        <li id="R_7.022.040cd">yan mayā yan na hantavyo rākṣaso varadarpitaḥ</li>
    </ul>
    <ul id="R_7.022.041">
        <li id="R_7.022.041ab">eṣa tasmāt praṇaśyāmi darśanād asya rakṣasaḥ</li>
        <li id="R_7.022.041cd">ity uktvā sarathaḥ sāśvas tatraivāntaradhīyata</li>
    </ul>
    <ul id="R_7.022.042">
        <li id="R_7.022.042ab">daśagrīvas tu taṃ jitvā nāma viśrāvya cātmanaḥ</li>
        <li id="R_7.022.042cd">puṣpakeṇa tu saṃhṛṣṭo niṣkrānto yamasādanāt</li>
    </ul>
    <ul id="R_7.022.043">
        <li id="R_7.022.043ab">tato vaivasvato devaiḥ saha brahmapurogamaiḥ</li>
        <li id="R_7.022.043cd">jagāma tridivaṃ hṛṣṭo nāradaś ca mahāmuniḥ</li>
    </ul>
    <ul id="R_7.023.001">
        <li id="R_7.023.001ab">sa tu jitvā daśagrīvo yamaṃ tridaśapuṃgavam</li>
        <li id="R_7.023.001cd">rāvaṇas tu jayaślāghī svasahāyān dadarśa ha</li>
    </ul>
    <ul id="R_7.023.002">
        <li id="R_7.023.002ab">jayena vardhayitvā ca mārīcapramukhās tataḥ</li>
        <li id="R_7.023.002cd">puṣpakaṃ bhejire sarve sāntvitā ravaṇena ha</li>
    </ul>
    <ul id="R_7.023.003">
        <li id="R_7.023.003ab">tato rasātalaṃ hṛṣṭaḥ praviṣṭaḥ payaso nidhim</li>
        <li id="R_7.023.003cd">daityoraga gaṇādhyuṣṭaṃ varuṇena surakṣitam</li>
    </ul>
    <ul id="R_7.023.004">
        <li id="R_7.023.004ab">sa tu bhogavatīṃ gatvā purīṃ vāsukipālitām</li>
        <li id="R_7.023.004cd">sthāpya nāgān vaśe kṛtvā yayau maṇimatīṃ purīm</li>
    </ul>
    <ul id="R_7.023.005">
        <li id="R_7.023.005ab">nivātakavacās tatra daityā labdhavarā vasan</li>
        <li id="R_7.023.005cd">rākṣasas tān samāsādya yuddhena samupāhvayat</li>
    </ul>
    <ul id="R_7.023.006">
        <li id="R_7.023.006ab">te tu sarve suvikrāntā daiteyā balaśālinaḥ</li>
        <li id="R_7.023.006cd">nānāpraharaṇās tatra prayuddhā yuddhadurmadāḥ</li>
    </ul>
    <ul id="R_7.023.007">
        <li id="R_7.023.007ab">teṣāṃ tu yudhyamānānāṃ sāgraḥ saṃvatsaro gataḥ</li>
        <li id="R_7.023.007cd">na cānyatarayos tatra vijayo vā kṣayo 'pi vā</li>
    </ul>
    <ul id="R_7.023.008">
        <li id="R_7.023.008ab">tataḥ pitāmahas tatra trailokyagatir avyayaḥ</li>
        <li id="R_7.023.008cd">ājagāma drutaṃ devo vimānavaram āsthitaḥ</li>
    </ul>
    <ul id="R_7.023.009">
        <li id="R_7.023.009ab">nivātakavacānāṃ tu nivārya raṇakarma tat</li>
        <li id="R_7.023.009cd">vṛddhaḥ pitāmaho vākyam uvāca viditārthavat</li>
    </ul>
    <ul id="R_7.023.010">
        <li id="R_7.023.010ab">na hy ayaṃ rāvaṇo yuddhe śakyo jetuṃ surāsuraiḥ</li>
        <li id="R_7.023.010cd">na bhavantaḥ kṣayaṃ netuṃ śakyāḥ sendraiḥ surāsuraiḥ</li>
    </ul>
    <ul id="R_7.023.011">
        <li id="R_7.023.011ab">rākṣasasya sakhitvaṃ vai bhavadbhiḥ saha rocate</li>
        <li id="R_7.023.011cd">avibhaktā hi sarvārthāḥ suhṛdāṃ nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_7.023.012">
        <li id="R_7.023.012ab">tato 'gnisākṣikaṃ sakhyaṃ kṛtavāṃs tatra rāvaṇaḥ</li>
        <li id="R_7.023.012cd">nivātakavacaiḥ sārdhaṃ prītimān abhavat tadā</li>
    </ul>
    <ul id="R_7.023.013">
        <li id="R_7.023.013ab">arcitas tair yathānyāyaṃ saṃvatsarasukhoṣitaḥ</li>
        <li id="R_7.023.013cd">svapurān nirviśeṣaṃ ca pūjāṃ prāpto daśānanaḥ</li>
    </ul>
    <ul id="R_7.023.014">
        <li id="R_7.023.014ab">sa tūpadhārya māyānāṃ śatam ekonam ātmavān</li>
        <li id="R_7.023.014cd">salilendrapurānveṣī sa babhrāma rasātalam</li>
    </ul>
    <ul id="R_7.023.015">
        <li id="R_7.023.015ab">tato 'śmanagaraṃ nāma kālakeyābhirakṣitam</li>
        <li id="R_7.023.015cd">taṃ vijitya muhūrtena jaghne daityāṃś catuḥśatam</li>
    </ul>
    <ul id="R_7.023.016">
        <li id="R_7.023.016ab">tataḥ pāṇḍurameghābhaṃ kailāsam iva saṃsthitam</li>
        <li id="R_7.023.016cd">varuṇasyālayaṃ divyam apaśyad rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_7.023.017">
        <li id="R_7.023.017ab">kṣarantīṃ ca payo nityaṃ surabhiṃ gām avasthitām</li>
        <li id="R_7.023.017cd">yasyāḥ payoviniṣyandāt kṣīrodo nāma sāgaraḥ</li>
    </ul>
    <ul id="R_7.023.018">
        <li id="R_7.023.018ab">yasmāc candraḥ prabhavati śītaraśmiḥ prajāhitaḥ</li>
        <li id="R_7.023.018cd">yaṃ samāsādya jīvanti phenapāḥ paramarṣayaḥ</li>
        <li id="R_7.023.018ef">amṛtaṃ yatra cotpannaṃ surā cāpi surāśinām</li>
    </ul>
    <ul id="R_7.023.019">
        <li id="R_7.023.019ab">yāṃ bruvanti narā loke surabhiṃ nāma nāmataḥ</li>
        <li id="R_7.023.019cd">pradakṣiṇaṃ tu tāṃ kṛtvā rāvaṇaḥ paramādbhutām</li>
        <li id="R_7.023.019ef">praviveśa mahāghoraṃ guptaṃ bahuvidhair balaiḥ</li>
    </ul>
    <ul id="R_7.023.020">
        <li id="R_7.023.020ab">tato dhārāśatākīrṇaṃ śāradābhranibhaṃ tadā</li>
        <li id="R_7.023.020cd">nityaprahṛṣṭaṃ dadṛśe varuṇasya gṛhottamam</li>
    </ul>
    <ul id="R_7.023.021">
        <li id="R_7.023.021ab">tato hatvā balādhyakṣān samare taiś ca tāḍitaḥ</li>
        <li id="R_7.023.021cd">abravīt kva gato yo vo rājā śīghraṃ nivedyatām</li>
    </ul>
    <ul id="R_7.023.022">
        <li id="R_7.023.022ab">yuddhārthī rāvaṇaḥ prāptas tasya yuddhaṃ pradīyatām</li>
        <li id="R_7.023.022cd">vada vā na bhayaṃ te 'sti nirjito 'smīti sāñjaliḥ</li>
    </ul>
    <ul id="R_7.023.023">
        <li id="R_7.023.023ab">etasminn antare kruddhā varuṇasya mahātmanaḥ</li>
        <li id="R_7.023.023cd">putrāḥ pautrāś ca niṣkrāman gauś ca puṣkara eva ca</li>
    </ul>
    <ul id="R_7.023.024">
        <li id="R_7.023.024ab">te tu vīryaguṇopetā balaiḥ parivṛtāḥ svakaiḥ</li>
        <li id="R_7.023.024cd">yuktvā rathān kāmagamān udyadbhāskaravarcasaḥ</li>
    </ul>
    <ul id="R_7.023.025">
        <li id="R_7.023.025ab">tato yuddhaṃ samabhavad dāruṇaṃ lomaharṣaṇam</li>
        <li id="R_7.023.025cd">salilendrasya putrāṇāṃ rāvaṇasya ca rakṣasaḥ</li>
    </ul>
    <ul id="R_7.023.026">
        <li id="R_7.023.026ab">amātyais tu mahāvīryair daśagrīvasya rakṣasaḥ</li>
        <li id="R_7.023.026cd">vāruṇaṃ tad balaṃ kṛtsnaṃ kṣaṇena vinipātitam</li>
    </ul>
    <ul id="R_7.023.027">
        <li id="R_7.023.027ab">samīkṣya svabalaṃ saṃkhye varuṇasyā sutās tadā</li>
        <li id="R_7.023.027cd">arditāḥ śarajālena nivṛttā raṇakarmaṇaḥ</li>
    </ul>
    <ul id="R_7.023.028">
        <li id="R_7.023.028ab">mahītalagatās te tu rāvaṇaṃ dṛśya puṣpake</li>
        <li id="R_7.023.028cd">ākāśam āśu viviśuḥ syandanaiḥ śīghragāmibhiḥ</li>
    </ul>
    <ul id="R_7.023.029">
        <li id="R_7.023.029ab">mahad āsīt tatas teṣāṃ tulyaṃ sthānam avāpya tat</li>
        <li id="R_7.023.029cd">ākāśayuddhaṃ tumulaṃ devadānavayor iva</li>
    </ul>
    <ul id="R_7.023.030">
        <li id="R_7.023.030ab">tatas te rāvaṇaṃ yuddhe śaraiḥ pāvakasaṃnibhaiḥ</li>
        <li id="R_7.023.030cd">vimukhīkṛtya saṃhṛṣṭā vinedur vividhān ravān</li>
    </ul>
    <ul id="R_7.023.031">
        <li id="R_7.023.031ab">tato mahodaraḥ kruddho rājānaṃ dṛśya dharṣitam</li>
        <li id="R_7.023.031cd">tyaktvā mṛtyubhayaṃ vīro yuddhakāṅkṣī vyalokayat</li>
    </ul>
    <ul id="R_7.023.032">
        <li id="R_7.023.032ab">tena teṣāṃ hayā ye ca kāmagāḥ pavanopamāḥ</li>
        <li id="R_7.023.032cd">mahodareṇa gadayā hatās te prayayuḥ kṣitim</li>
    </ul>
    <ul id="R_7.023.033">
        <li id="R_7.023.033ab">teṣāṃ varuṇasūnūnāṃ hatvā yodhān hayāṃś ca tān</li>
        <li id="R_7.023.033cd">mumocāśu mahānādaṃ virathān prekṣya tān sthitān</li>
    </ul>
    <ul id="R_7.023.034">
        <li id="R_7.023.034ab">te tu teṣāṃ rathāḥ sāśvāḥ saha sārathibhir varaiḥ</li>
        <li id="R_7.023.034cd">mahodareṇa nihatāḥ patitāḥ pṛthivītale</li>
    </ul>
    <ul id="R_7.023.035">
        <li id="R_7.023.035ab">te tu tyaktvā rathān putrā varuṇasya mahātmanaḥ</li>
        <li id="R_7.023.035cd">ākāśe viṣṭhitāḥ śūrāḥ svaprabhāvān na vivyathuḥ</li>
    </ul>
    <ul id="R_7.023.036">
        <li id="R_7.023.036ab">dhanūṃṣi kṛtvā sajyāni vinirbhidya mahodaram</li>
        <li id="R_7.023.036cd">rāvaṇaṃ samare kruddhāḥ sahitāḥ samabhidravan</li>
    </ul>
    <ul id="R_7.023.037">
        <li id="R_7.023.037ab">tataḥ kruddho daśagrīvaḥ kālāgnir iva viṣṭhitaḥ</li>
        <li id="R_7.023.037cd">śaravarṣaṃ mahāvegaṃ teṣāṃ marmasv apātayat</li>
    </ul>
    <ul id="R_7.023.038">
        <li id="R_7.023.038ab">musalāni vicitrāṇi tato bhallaśatāni ca</li>
        <li id="R_7.023.038cd">paṭṭasāṃś caiva śaktīś ca śataghnīs tomarāṃs tathā</li>
        <li id="R_7.023.038ef">pātayām āsa durdharṣas teṣām upari viṣṭhitaḥ</li>
    </ul>
    <ul id="R_7.023.039">
        <li id="R_7.023.039ab">atha viddhās tu te vīrā viniṣpetuḥ padātayaḥ</li>
    </ul>
    <ul id="R_7.023.040">
        <li id="R_7.023.040ab">tato rakṣo mahānādaṃ muktvā hanti sma vāruṇān</li>
        <li id="R_7.023.040cd">nānāpraharaṇair ghorair dhārāpātair ivāmbudaḥ</li>
    </ul>
    <ul id="R_7.023.041">
        <li id="R_7.023.041ab">tatas te vimukhāḥ sarve patitā dharaṇītale</li>
        <li id="R_7.023.041cd">raṇāt svapuruṣaiḥ śīghraṃ gṛhāṇy eva praveśitāḥ</li>
    </ul>
    <ul id="R_7.023.042">
        <li id="R_7.023.042ab">tān abravīt tato rakṣo varuṇāya nivedyatām</li>
        <li id="R_7.023.042cd">rāvaṇaṃ cābravīn mantrī prabhāso nāma vāruṇaḥ</li>
    </ul>
    <ul id="R_7.023.043">
        <li id="R_7.023.043ab">gataḥ khalu mahātejā brahmalokaṃ jaleśvaraḥ</li>
        <li id="R_7.023.043cd">gāndharvaṃ varuṇaḥ śrotuṃ yaṃ tvam āhvayase yudhi</li>
    </ul>
    <ul id="R_7.023.044">
        <li id="R_7.023.044ab">tat kiṃ tava vṛthā vīra pariśrāmya gate nṛpe</li>
        <li id="R_7.023.044cd">ye tu saṃnihitā vīrāḥ kumārās te parājitāḥ</li>
    </ul>
    <ul id="R_7.023.045">
        <li id="R_7.023.045ab">rākṣasendras tu tac chrutvā nāma viśrāvya cātmanaḥ</li>
        <li id="R_7.023.045cd">harṣān nādaṃ vimuñcan vai niṣkrānto varuṇālayāt</li>
    </ul>
    <ul id="R_7.023.046">
        <li id="R_7.023.046ab">āgatas tu pathā yena tenaiva vinivṛtya saḥ</li>
        <li id="R_7.023.046cd">laṅkām abhimukho rakṣo nabhastalagato yayau</li>
    </ul>
    <ul id="R_7.024.001">
        <li id="R_7.024.001ab">nivartamānaḥ saṃhṛṣṭo rāvaṇaḥ sa durātmavān</li>
        <li id="R_7.024.001cd">jahre pathi narendrarṣidevagandharvakanyakāḥ</li>
    </ul>
    <ul id="R_7.024.002">
        <li id="R_7.024.002ab">darśanīyāṃ hi yāṃ rakṣaḥ kanyāṃ strīṃ vātha paśyati</li>
        <li id="R_7.024.002cd">hatvā bandhujanaṃ tasyā vimāne saṃnyaveśayat</li>
    </ul>
    <ul id="R_7.024.003">
        <li id="R_7.024.003ab">tatra pannagayakṣāṇāṃ mānuṣāṇāṃ ca rakṣasām</li>
        <li id="R_7.024.003cd">daityānāṃ dānavānāṃ ca kanyā jagrāha rāvaṇaḥ</li>
    </ul>
    <ul id="R_7.024.004">
        <li id="R_7.024.004ab">dīrghakeśyaḥ sucārvaṅgyaḥ pūrṇacandranibhānanāḥ</li>
        <li id="R_7.024.004cd">śokāyattās taruṇyaś ca samastā stananamritāḥ</li>
    </ul>
    <ul id="R_7.024.005">
        <li id="R_7.024.005ab">tulyam agnyarciṣāṃ tatra śokāgnibhayasaṃbhavam</li>
        <li id="R_7.024.005cd">pravepamānā duḥkhārtā mumucur bāṣpajaṃ jalam</li>
    </ul>
    <ul id="R_7.024.006">
        <li id="R_7.024.006ab">tāsāṃ niśvasamānānāṃ niśvasaiḥ saṃpradīpitam</li>
        <li id="R_7.024.006cd">agnihotram ivābhāti saṃniruddhāgnipuṣpakam</li>
    </ul>
    <ul id="R_7.024.007">
        <li id="R_7.024.007ab">kā cid dadhyau suduḥkhārtā hanyād api hi mām ayam</li>
        <li id="R_7.024.007cd">smṛtvā mātṝḥ pitṝn bhrātṝn putrān vai śvaśurān api</li>
        <li id="R_7.024.007ef">duḥkhaśokasamāviṣṭā vilepuḥ sahitāḥ striyaḥ</li>
    </ul>
    <ul id="R_7.024.008">
        <li id="R_7.024.008ab">kathaṃ nu khalu me putraḥ kariṣyati mayā vinā</li>
        <li id="R_7.024.008cd">kathaṃ mātā kathaṃ bhrātā nimagnāḥ śokasāgare</li>
    </ul>
    <ul id="R_7.024.009">
        <li id="R_7.024.009ab">hā kathaṃ nu kariṣyāmi bhartāraṃ daivataṃ vinā</li>
        <li id="R_7.024.009cd">mṛtyo prasīda yāce tvāṃ naya māṃ yamasādanam</li>
    </ul>
    <ul id="R_7.024.010">
        <li id="R_7.024.010ab">kiṃ nu me duṣkṛtaṃ karma kṛtaṃ dehāntare purā</li>
        <li id="R_7.024.010cd">tato 'smi dharṣitānena patitā śokasāgare</li>
    </ul>
    <ul id="R_7.024.011">
        <li id="R_7.024.011ab">na khalv idānīṃ paśyāmi duḥkhasyāntam ihātmanaḥ</li>
        <li id="R_7.024.011cd">aho dhin mānuṣāṃl lokān nāsti khalv adhamaḥ paraḥ</li>
    </ul>
    <ul id="R_7.024.012">
        <li id="R_7.024.012ab">yad durbalā balavatā bāndhavā rāvaṇena me</li>
        <li id="R_7.024.012cd">uditenaiva sūryeṇa tārakā iva nāśitāḥ</li>
    </ul>
    <ul id="R_7.024.013">
        <li id="R_7.024.013ab">aho subalavad rakṣo vadhopāyeṣu rajyate</li>
        <li id="R_7.024.013cd">aho durvṛttam ātmānaṃ svayam eva na budhyate</li>
    </ul>
    <ul id="R_7.024.014">
        <li id="R_7.024.014ab">sarvathā sadṛśas tāvad vikramo 'sya durātmanaḥ</li>
        <li id="R_7.024.014cd">idaṃ tv asadṛśaṃ karma paradārābhimarśanam</li>
    </ul>
    <ul id="R_7.024.015">
        <li id="R_7.024.015ab">yasmād eṣa parakhyāsu strīṣu rajyati durmatiḥ</li>
        <li id="R_7.024.015cd">tasmād dhi strīkṛtenaiva vadhaṃ prāpsyati vāraṇaḥ</li>
    </ul>
    <ul id="R_7.024.016">
        <li id="R_7.024.016ab">śaptaḥ strībhiḥ sa tu tadā hatatejāḥ suniṣprabha</li>
        <li id="R_7.024.016cd">pativratābhiḥ sādhvībhiḥ sthitābhiḥ sādhu vartmani</li>
    </ul>
    <ul id="R_7.024.017">
        <li id="R_7.024.017ab">evaṃ vilapamānāsu rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_7.024.017cd">praviveśa purīṃ laṅkāṃ pūjyamāno niśācaraiḥ</li>
    </ul>
    <ul id="R_7.024.018">
        <li id="R_7.024.018ab">tato rākṣasarājasya svasā paramaduḥkhitā</li>
        <li id="R_7.024.018cd">pādayoḥ patitā tasya vaktum evopacakrame</li>
    </ul>
    <ul id="R_7.024.019">
        <li id="R_7.024.019ab">tataḥ svasāram utthāpya rāvaṇaḥ parisāntvayan</li>
        <li id="R_7.024.019cd">abravīt kim idaṃ bhadre vaktum arhasi me drutam</li>
    </ul>
    <ul id="R_7.024.020">
        <li id="R_7.024.020ab">sā bāṣpapariruddhākṣī rākṣasī vākyam abravīt</li>
        <li id="R_7.024.020cd">hatāsmi vidhavā rājaṃs tvayā balavatā kṛtā</li>
    </ul>
    <ul id="R_7.024.021">
        <li id="R_7.024.021ab">ete viryāt tvayā rājan daityā vinihatā raṇe</li>
        <li id="R_7.024.021cd">kālakeyā iti khyātā mahābalaparākramāḥ</li>
    </ul>
    <ul id="R_7.024.022">
        <li id="R_7.024.022ab">tatra me nihato bhartā garīyāñ jīvitād api</li>
        <li id="R_7.024.022cd">sa tvayā dayitas tatra bhrātrā śatrusamena vai</li>
    </ul>
    <ul id="R_7.024.023">
        <li id="R_7.024.023ab">yā tvayāsmi hatā rājan svayam eveha bandhunā</li>
        <li id="R_7.024.023cd">duḥkhaṃ vaidhavyaśabdaṃ ca dattaṃ bhokṣyāmy ahaṃ tvayā</li>
    </ul>
    <ul id="R_7.024.024">
        <li id="R_7.024.024ab">nanu nāma tvayā rakṣyo jāmātā samareṣv api</li>
        <li id="R_7.024.024cd">taṃ nihatya raṇe rājan svayam eva na lajjase</li>
    </ul>
    <ul id="R_7.024.025">
        <li id="R_7.024.025ab">evam uktas tayā rakṣo bhaginyā krośamānayā</li>
        <li id="R_7.024.025cd">abravīt sāntvayitvā tāṃ sāmapūrvam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_7.024.026">
        <li id="R_7.024.026ab">alaṃ vatse viṣādena na bhetavyaṃ ca sarvaśaḥ</li>
        <li id="R_7.024.026cd">mānadānaviśeṣais tvāṃ toṣayiṣyāmi nityaśaḥ</li>
    </ul>
    <ul id="R_7.024.027">
        <li id="R_7.024.027ab">yuddhe pramatto vyākṣipto jayakāṅkṣī kṣipañ śarān</li>
        <li id="R_7.024.027cd">nāvagacchāmi yuddheṣu svān parān vāpy ahaṃ śubhe</li>
        <li id="R_7.024.027ef">tenāsau nihataḥ saṃkhye mayā bhartā tava svasaḥ</li>
    </ul>
    <ul id="R_7.024.028">
        <li id="R_7.024.028ab">asmin kāle tu yat prāptaṃ tat kariṣyāmi te hitam</li>
        <li id="R_7.024.028cd">bhrātur aiśvaryasaṃsthasya kharasya bhava pārśvataḥ</li>
    </ul>
    <ul id="R_7.024.029">
        <li id="R_7.024.029ab">caturdaśānāṃ bhrātā te sahasrāṇāṃ bhaviṣyati</li>
        <li id="R_7.024.029cd">prabhuḥ prayāṇe dāne ca rākṣasānāṃ mahaujasām</li>
    </ul>
    <ul id="R_7.024.030">
        <li id="R_7.024.030ab">tatra mātṛṣvasuḥ putro bhrātā tava kharaḥ prabhuḥ</li>
        <li id="R_7.024.030cd">bhaviṣyati sadā kurvan yad vakṣyasi vacaḥ svayam</li>
    </ul>
    <ul id="R_7.024.031">
        <li id="R_7.024.031ab">śīghraṃ gacchatv ayaṃ śūro daṇḍakān parirakṣitum</li>
        <li id="R_7.024.031cd">dūṣaṇo 'sya balādhyakṣo bhaviṣyati mahābalaḥ</li>
    </ul>
    <ul id="R_7.024.032">
        <li id="R_7.024.032ab">sa hi śapto vanoddeśaḥ kruddhenośanasā purā</li>
        <li id="R_7.024.032cd">rākṣasānām ayaṃ vāso bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.024.033">
        <li id="R_7.024.033ab">evam uktvā daśagrīvaḥ sainyaṃ tasyādideśa ha</li>
        <li id="R_7.024.033cd">caturdaśa sahasrāṇi rakṣasāṃ kāmarūpiṇām</li>
    </ul>
    <ul id="R_7.024.034">
        <li id="R_7.024.034ab">sa taiḥ sarvaiḥ parivṛto rākṣasair ghoradarśanaiḥ</li>
        <li id="R_7.024.034cd">kharaḥ saṃprayayau śīghraṃ daṇḍakān akutobhayaḥ</li>
    </ul>
    <ul id="R_7.024.035">
        <li id="R_7.024.035ab">sa tatra kārayām āsa rājyaṃ nihatakaṇṭakam</li>
        <li id="R_7.024.035cd">sā ca śūrpaṇakhā prītā nyavasad daṇḍakāvane</li>
    </ul>
    <ul id="R_7.025.001">
        <li id="R_7.025.001ab">sa tu dattvā daśagrīvo vanaṃ ghoraṃ kharasya tat</li>
        <li id="R_7.025.001cd">bhaginīṃ ca samāśvāsya hṛṣṭaḥ svasthataro 'bhavat</li>
    </ul>
    <ul id="R_7.025.002">
        <li id="R_7.025.002ab">tato nikumbhilā nāma laṅkāyāḥ kānanaṃ mahat</li>
        <li id="R_7.025.002cd">mahātmā rākṣasendras tat praviveśa sahānugaḥ</li>
    </ul>
    <ul id="R_7.025.003">
        <li id="R_7.025.003ab">tatra yūpaśatākīrṇaṃ saumyacaityopaśobhitam</li>
        <li id="R_7.025.003cd">dadarśa viṣṭhitaṃ yajñaṃ saṃpradīptam iva śriyā</li>
    </ul>
    <ul id="R_7.025.004">
        <li id="R_7.025.004ab">tataḥ kṛṣṇājinadharaṃ kamaṇḍaluśikhādhvajam</li>
        <li id="R_7.025.004cd">dadarśa svasutaṃ tatra meghanādam ariṃdamam</li>
    </ul>
    <ul id="R_7.025.005">
        <li id="R_7.025.005ab">rakṣaḥpatiḥ samāsādya samāśliṣya ca bāhubhiḥ</li>
        <li id="R_7.025.005cd">abravīt kim idaṃ vatsa vartate tad bravīhi me</li>
    </ul>
    <ul id="R_7.025.006">
        <li id="R_7.025.006ab">uśanā tv abravīt tatra gurur yajñasamṛddhaye</li>
        <li id="R_7.025.006cd">rāvaṇaṃ rākṣasaśreṣṭhaṃ dvijaśreṣṭho mahātapāḥ</li>
    </ul>
    <ul id="R_7.025.007">
        <li id="R_7.025.007ab">aham ākhyāmi te rājañ śrūyatāṃ sarvam eva ca</li>
        <li id="R_7.025.007cd">yajñās te sapta putreṇa prāptāḥ subahuvistarāḥ</li>
    </ul>
    <ul id="R_7.025.008">
        <li id="R_7.025.008ab">agniṣṭomo 'śvamedhaś ca yajño bahusuvarṇakaḥ</li>
        <li id="R_7.025.008cd">rājasūyas tathā yajño gomedho vaiṣṇavas tathā</li>
    </ul>
    <ul id="R_7.025.009">
        <li id="R_7.025.009ab">māheśvare pravṛtte tu yajñe pumbhiḥ sudurlabhe</li>
        <li id="R_7.025.009cd">varāṃs te labdhavān putraḥ sākṣāt paśupater iha</li>
    </ul>
    <ul id="R_7.025.010">
        <li id="R_7.025.010ab">kāmagaṃ syandanaṃ divyam antarikṣacaraṃ dhruvam</li>
        <li id="R_7.025.010cd">māyāṃ ca tāmasīṃ nāma yayā saṃpadyate tamaḥ</li>
    </ul>
    <ul id="R_7.025.011">
        <li id="R_7.025.011ab">etayā kila saṃgrāme māyayā rākṣaseśvara</li>
        <li id="R_7.025.011cd">prayuddhasya gatiḥ śakyā na hi jñātuṃ surāsuraiḥ</li>
    </ul>
    <ul id="R_7.025.012">
        <li id="R_7.025.012ab">akṣayāv iṣudhī bāṇaiś cāpaṃ cāpi sudurjayam</li>
        <li id="R_7.025.012cd">astraṃ ca balavat saumya śatruvidhvaṃsanaṃ raṇe</li>
    </ul>
    <ul id="R_7.025.013">
        <li id="R_7.025.013ab">etān sarvān varāṃl labdhvā putras te 'yaṃ daśānana</li>
        <li id="R_7.025.013cd">adya yajñasamāptau ca tvatpratīkṣaḥ sthito aham</li>
    </ul>
    <ul id="R_7.025.014">
        <li id="R_7.025.014ab">tato 'bravīd daśagrīvo na śobhanam idaṃ kṛtam</li>
        <li id="R_7.025.014cd">pūjitāḥ śatravo yasmād dravyair indrapurogamāḥ</li>
    </ul>
    <ul id="R_7.025.015">
        <li id="R_7.025.015ab">ehīdānīṃ kṛtaṃ yad dhi tad akartuṃ na śakyate</li>
        <li id="R_7.025.015cd">āgaccha saumya gacchāmaḥ svam eva bhavanaṃ prati</li>
    </ul>
    <ul id="R_7.025.016">
        <li id="R_7.025.016ab">tato gatvā daśagrīvaḥ saputraḥ savibhīṣaṇaḥ</li>
        <li id="R_7.025.016cd">striyo 'vatārayām āsa sarvās tā bāṣpaviklavāḥ</li>
    </ul>
    <ul id="R_7.025.017">
        <li id="R_7.025.017ab">lakṣiṇyo ratnabūtāś ca devadānavarakṣasām</li>
        <li id="R_7.025.017cd">nānābhūṣaṇasaṃpannā jvalantyaḥ svena tejasā</li>
    </ul>
    <ul id="R_7.025.018">
        <li id="R_7.025.018ab">vibhīṣaṇas tu tā nārīr dṛṣṭvā śokasamākulāḥ</li>
        <li id="R_7.025.018cd">tasya tāṃ ca matiṃ jñātvā dharmātmā vākyam abravīt</li>
    </ul>
    <ul id="R_7.025.019">
        <li id="R_7.025.019ab">īdṛśais taiḥ samācārair yaśo'rthakulanāśanaiḥ</li>
        <li id="R_7.025.019cd">dharṣaṇaṃ prāṇināṃ dattvā svamatena viceṣṭase</li>
    </ul>
    <ul id="R_7.025.020">
        <li id="R_7.025.020ab">jñātīn vai dharṣayitvemās tvayānītā varāṅganāḥ</li>
        <li id="R_7.025.020cd">tvām atikramya madhunā rājan kumbhīnasī hṛtā</li>
    </ul>
    <ul id="R_7.025.021">
        <li id="R_7.025.021ab">rāvaṇas tv abravīd vākyaṃ nāvagacchāmi kiṃ tv idam</li>
        <li id="R_7.025.021cd">ko vāyaṃ yas tvayākhyāto madhur ity eva nāmataḥ</li>
    </ul>
    <ul id="R_7.025.022">
        <li id="R_7.025.022ab">vibhīṣaṇas tu saṃkruddho bhrātaraṃ vākyam abravīt</li>
        <li id="R_7.025.022cd">śrūyatām asya pāpasya karmaṇaḥ phalam āgatam</li>
    </ul>
    <ul id="R_7.025.023">
        <li id="R_7.025.023ab">mātāmahasya yo 'smākaṃ jyeṣṭho bhrātā sumālinaḥ</li>
        <li id="R_7.025.023cd">mālyavān iti vikhyāto vṛddhaprājño niśācaraḥ</li>
    </ul>
    <ul id="R_7.025.024">
        <li id="R_7.025.024ab">pitur jyeṣṭho jananyāś ca asmākaṃ tv āryako 'bhavat</li>
        <li id="R_7.025.024cd">tasya kumbhīnasī nāma duhitur duhitābhavat</li>
    </ul>
    <ul id="R_7.025.025">
        <li id="R_7.025.025ab">mātṛṣvasur athāsmākaṃ sā kanyā cānalodbhavā</li>
        <li id="R_7.025.025cd">bhavaty asmākam eṣā vai bhrātṝṇāṃ dharmataḥ svasā</li>
    </ul>
    <ul id="R_7.025.026">
        <li id="R_7.025.026ab">sā hṛtā madhunā rājan rākṣasena balīyasā</li>
        <li id="R_7.025.026cd">yajñapravṛtte putre te mayi cāntarjaloṣite</li>
    </ul>
    <ul id="R_7.025.027">
        <li id="R_7.025.027ab">nihatya rākṣasaśreṣṭhān amātyāṃs tava saṃmatān</li>
        <li id="R_7.025.027cd">dharṣayitvā hṛtā rājan guptā hy antaḥpure tava</li>
    </ul>
    <ul id="R_7.025.028">
        <li id="R_7.025.028ab">śrutvā tv etan mahārāja kṣāntam eva hato na saḥ</li>
        <li id="R_7.025.028cd">yasmād avaśyaṃ dātavyā kanyā bhartre hi dātṛbhiḥ</li>
        <li id="R_7.025.028ef">asminn evābhisaṃprāptaṃ loke viditam astu te</li>
    </ul>
    <ul id="R_7.025.029">
        <li id="R_7.025.029ab">tato 'bravīd daśagrīvaḥ kruddhaḥ saṃraktalocanaḥ</li>
        <li id="R_7.025.029cd">kalpyatāṃ me rathaḥ śīghraṃ śūrāḥ sajjībhavantu ca</li>
    </ul>
    <ul id="R_7.025.030">
        <li id="R_7.025.030ab">bhrātā me kumbhakarṇaś ca ye ca mukhyā niśācarāḥ</li>
        <li id="R_7.025.030cd">vāhanāny adhirohantu nānāpraharaṇāyudhāḥ</li>
    </ul>
    <ul id="R_7.025.031">
        <li id="R_7.025.031ab">adya taṃ samare hatvā madhuṃ rāvaṇanirbhayam</li>
        <li id="R_7.025.031cd">indralokaṃ gamiṣyāmi yuddhakāṅkṣī suhṛdvṛtaḥ</li>
    </ul>
    <ul id="R_7.025.032">
        <li id="R_7.025.032ab">tato vijitya tridivaṃ vaśe sthāpya puraṃdaram</li>
        <li id="R_7.025.032cd">nirvṛto vihariṣyāmi trailokyaiśvaryaśobhitaḥ</li>
    </ul>
    <ul id="R_7.025.033">
        <li id="R_7.025.033ab">akṣauhiṇīsahasrāṇi catvāry ugrāṇi rakṣasām</li>
        <li id="R_7.025.033cd">nānāpraharaṇāny āśu niryayur yuddhakāṅkṣiṇām</li>
    </ul>
    <ul id="R_7.025.034">
        <li id="R_7.025.034ab">indrajit tv agrataḥ sainyaṃ sainikān parigṛhya ca</li>
        <li id="R_7.025.034cd">rāvaṇo madhyataḥ śūraḥ kumbhakarṇaś ca pṛṣṭhataḥ</li>
    </ul>
    <ul id="R_7.025.035">
        <li id="R_7.025.035ab">vibhīṣaṇas tu dharmātmā laṅkāyāṃ dharmam ācarat</li>
        <li id="R_7.025.035cd">te tu sarve mahābhāgā yayur madhupuraṃ prati</li>
    </ul>
    <ul id="R_7.025.036">
        <li id="R_7.025.036ab">rathair nāgaiḥ kharair uṣṭrair hayair dīptair mahoragaiḥ</li>
        <li id="R_7.025.036cd">rākṣasāḥ prayayuḥ sarve kṛtvākāśaṃ nirantaram</li>
    </ul>
    <ul id="R_7.025.037">
        <li id="R_7.025.037ab">daityāś ca śataśas tatra kṛtavairāḥ suraiḥ saha</li>
        <li id="R_7.025.037cd">rāvaṇaṃ prekṣya gacchantam anvagacchanta pṛṣṭhataḥ</li>
    </ul>
    <ul id="R_7.025.038">
        <li id="R_7.025.038ab">sa tu gatvā madhupuraṃ praviśya ca daśānanaḥ</li>
        <li id="R_7.025.038cd">na dadarśa madhuṃ tatra bhaginīṃ tatra dṛṣṭavān</li>
    </ul>
    <ul id="R_7.025.039">
        <li id="R_7.025.039ab">sā prahvā prāñjalir bhūtvā śirasā pādayor gatā</li>
        <li id="R_7.025.039cd">tasya rākṣasarājasya trastā kumbhīnasī svasā</li>
    </ul>
    <ul id="R_7.025.040">
        <li id="R_7.025.040ab">tāṃ samutthāpayām āsa na bhetavyam iti bruvan</li>
        <li id="R_7.025.040cd">rāvaṇo rākṣasaśreṣṭhaḥ kiṃ cāpi karavāṇi te</li>
    </ul>
    <ul id="R_7.025.041">
        <li id="R_7.025.041ab">sābravīd yadi me rājan prasannas tvaṃ mahābala</li>
        <li id="R_7.025.041cd">bhartāraṃ na mamehādya hantum arhasi mānada</li>
    </ul>
    <ul id="R_7.025.042">
        <li id="R_7.025.042ab">satyavāg bhava rājendra mām avekṣasva yācatīm</li>
        <li id="R_7.025.042cd">tvayā hy uktaṃ mahābāho na bhetavyam iti svayam</li>
    </ul>
    <ul id="R_7.025.043">
        <li id="R_7.025.043ab">rāvaṇas tv abravīd dhṛṣṭaḥ svasāraṃ tatra saṃsthitām</li>
        <li id="R_7.025.043cd">kva cāsau tava bhartā vai mama śīghraṃ nivedyatām</li>
    </ul>
    <ul id="R_7.025.044">
        <li id="R_7.025.044ab">saha tena gamiṣyāmi suralokaṃ jayāya vai</li>
        <li id="R_7.025.044cd">tava kāruṇyasauhārdān nivṛtto 'smi madhor vadhāt</li>
    </ul>
    <ul id="R_7.025.045">
        <li id="R_7.025.045ab">ity uktā sā prasuptaṃ taṃ samutthāpya niśācaram</li>
        <li id="R_7.025.045cd">abravīt saṃprahṛṣṭeva rākṣasī suvipaścitam</li>
    </ul>
    <ul id="R_7.025.046">
        <li id="R_7.025.046ab">eṣa prāpto daśagrīvo mama bhrātā niśācaraḥ</li>
        <li id="R_7.025.046cd">suralokajayākāṅkṣī sāhāyye tvāṃ vṛṇoti ca</li>
    </ul>
    <ul id="R_7.025.047">
        <li id="R_7.025.047ab">tad asya tvaṃ sahāyārthaṃ sabandhur gaccha rākṣasa</li>
        <li id="R_7.025.047cd">snigdhasya bhajamānasya yuktam arthāya kalpitum</li>
    </ul>
    <ul id="R_7.025.048">
        <li id="R_7.025.048ab">tasyās tad vacanaṃ śrutvā tathety āha madhur vacaḥ</li>
        <li id="R_7.025.048cd">dadarśa rākṣasaśreṣṭhaṃ yathānyāyam upetya saḥ</li>
    </ul>
    <ul id="R_7.025.049">
        <li id="R_7.025.049ab">pūjayām āsa dharmeṇa rāvaṇaṃ rākṣasādhipam</li>
        <li id="R_7.025.049cd">prāptapūjo daśagrīvo madhuveśmani vīryavān</li>
        <li id="R_7.025.049ef">tatra caikāṃ niśām uṣya gamanāyopacakrame</li>
    </ul>
    <ul id="R_7.025.050">
        <li id="R_7.025.050ab">tataḥ kailāsam āsādya śailaṃ vaiśravaṇālayam</li>
        <li id="R_7.025.050cd">rākṣasendro mahendrābhaḥ senām upaniveśayat</li>
    </ul>
    <ul id="R_7.026.001">
        <li id="R_7.026.001ab">sa tu tatra daśagrīvaḥ saha sainyena vīryavān</li>
        <li id="R_7.026.001cd">astaṃ prāpte dinakare nivāsaṃ samarocayat</li>
    </ul>
    <ul id="R_7.026.002">
        <li id="R_7.026.002ab">udite vimale candre tulyaparvatavarcasi</li>
        <li id="R_7.026.002cd">sa dadarśa guṇāṃs tatra candrapādopaśobhitān</li>
    </ul>
    <ul id="R_7.026.003">
        <li id="R_7.026.003ab">karṇikāravanair divyaiḥ kadambagahanais tathā</li>
        <li id="R_7.026.003cd">padminībhiś ca phullābhir mandākinyā jalair api</li>
    </ul>
    <ul id="R_7.026.004">
        <li id="R_7.026.004ab">ghaṇṭānām iva saṃnādaḥ śuśruve madhurasvanaḥ</li>
        <li id="R_7.026.004cd">apsarogaṇasaṃghanāṃ gāyatāṃ dhanadālaye</li>
    </ul>
    <ul id="R_7.026.005">
        <li id="R_7.026.005ab">puṣpavarṣāṇi muñcanto nagāḥ pavanatāḍitāḥ</li>
        <li id="R_7.026.005cd">śailaṃ taṃ vāsayantīva madhumādhavagandhinaḥ</li>
    </ul>
    <ul id="R_7.026.006">
        <li id="R_7.026.006ab">madhupuṣparajaḥpṛktaṃ gandham ādāya puṣkalam</li>
        <li id="R_7.026.006cd">pravavau vardhayan kāmaṃ rāvaṇasya sukho 'nilaḥ</li>
    </ul>
    <ul id="R_7.026.007">
        <li id="R_7.026.007ab">geyāt puṣpasamṛddhyā ca śaityād vāyor guṇair gireḥ</li>
        <li id="R_7.026.007cd">pravṛttāyāṃ rajanyāṃ ca candrasyodayanena ca</li>
    </ul>
    <ul id="R_7.026.008">
        <li id="R_7.026.008ab">rāvaṇaḥ sumahāvīryaḥ kāmabāṇavaśaṃ gataḥ</li>
        <li id="R_7.026.008cd">viniśvasya viniśvasya śaśinaṃ samavaikṣata</li>
    </ul>
    <ul id="R_7.026.009">
        <li id="R_7.026.009ab">etasminn antare tatra divyapuṣpavibhūṣitā</li>
        <li id="R_7.026.009cd">sarvāpsarovarā rambhā pūrṇacandranibhānanā</li>
    </ul>
    <ul id="R_7.026.010">
        <li id="R_7.026.010ab">kṛtair viśeṣakair ārdraiḥ ṣaḍartukusumotsavaiḥ</li>
        <li id="R_7.026.010cd">nīlaṃ satoyameghābhaṃ vastraṃ samavaguṇṭhitā</li>
    </ul>
    <ul id="R_7.026.011">
        <li id="R_7.026.011ab">yasya vaktraṃ śaśinibhaṃ bhruvau cāpanibhe śubhe</li>
        <li id="R_7.026.011cd">ūrū karikarākārau karau pallavakomalau</li>
        <li id="R_7.026.011ef">sainyamadhyena gacchantī rāvaṇenopalakṣitā</li>
    </ul>
    <ul id="R_7.026.012">
        <li id="R_7.026.012ab">tāṃ samutthāya rakṣendraḥ kāmabāṇabalārditaḥ</li>
        <li id="R_7.026.012cd">kare gṛhītvā gacchantīṃ smayamāno 'bhyabhāṣata</li>
    </ul>
    <ul id="R_7.026.013">
        <li id="R_7.026.013ab">kva gacchasi varārohe kāṃ siddhiṃ bhajase svayam</li>
        <li id="R_7.026.013cd">kasyābhyudayakālo 'yaṃ yas tvāṃ samupabhokṣyate</li>
    </ul>
    <ul id="R_7.026.014">
        <li id="R_7.026.014ab">tavānanarasasyādya padmotpalasugandhinaḥ</li>
        <li id="R_7.026.014cd">sudhāmṛtarasasyeva ko 'dya tṛptiṃ gamiṣyati</li>
    </ul>
    <ul id="R_7.026.015">
        <li id="R_7.026.015ab">svarṇakumbhanibhau pīnau śubhau bhīru nirantarau</li>
        <li id="R_7.026.015cd">kasyorasthalasaṃsparśaṃ dāsyatas te kucāv imau</li>
    </ul>
    <ul id="R_7.026.016">
        <li id="R_7.026.016ab">suvarṇacakrapratimaṃ svarṇadāmacitaṃ pṛthu</li>
        <li id="R_7.026.016cd">adhyārokṣyati kas te 'dya svargaṃ jaghanarūpiṇam</li>
    </ul>
    <ul id="R_7.026.017">
        <li id="R_7.026.017ab">madviśiṣṭaḥ pumān ko 'nyaḥ śakro viṣṇur athāśvinau</li>
        <li id="R_7.026.017cd">mām atītya hi yasya tvaṃ yāsi bhīru na śobhanam</li>
    </ul>
    <ul id="R_7.026.018">
        <li id="R_7.026.018ab">viśrama tvaṃ pṛthuśroṇi śilātalam idaṃ śubham</li>
        <li id="R_7.026.018cd">trailokye yaḥ prabhuś caiva tulyo mama na vidyate</li>
    </ul>
    <ul id="R_7.026.019">
        <li id="R_7.026.019ab">tad eṣa prāñjaliḥ prahvo yācate tvāṃ daśānanaḥ</li>
        <li id="R_7.026.019cd">yaḥ prabhuś cāpi bhartā ca trailokyasya bhajasva mām</li>
    </ul>
    <ul id="R_7.026.020">
        <li id="R_7.026.020ab">evam uktābravīd rambhā vepamānā kṛtāñjaliḥ</li>
        <li id="R_7.026.020cd">prasīda nārhase vaktum īdṛśaṃ tvaṃ hi me guruḥ</li>
    </ul>
    <ul id="R_7.026.021">
        <li id="R_7.026.021ab">anyebhyo 'pi tvayā rakṣyā prāpnuyāṃ dharṣaṇaṃ yadi</li>
        <li id="R_7.026.021cd">dharmataś ca snuṣā te 'haṃ tattvam etad bravīmi te</li>
    </ul>
    <ul id="R_7.026.022">
        <li id="R_7.026.022ab">abravīt tāṃ daśagrīvaś caraṇādhomukhīṃ sthitām</li>
        <li id="R_7.026.022cd">sutasya yadi me bhāryā tatas tvaṃ me snuṣā bhaveḥ</li>
    </ul>
    <ul id="R_7.026.023">
        <li id="R_7.026.023ab">bāḍham ity eva sā rambhā prāha rāvaṇam uttaram</li>
        <li id="R_7.026.023cd">dharmatas te sutasyāhaṃ bhāryā rākṣasapuṃgava</li>
    </ul>
    <ul id="R_7.026.024">
        <li id="R_7.026.024ab">putraḥ priyataraḥ prāṇair bhrātur vaiśravaṇasya te</li>
        <li id="R_7.026.024cd">khyāto yas triṣu lokeṣu nalakūbara ity asau</li>
    </ul>
    <ul id="R_7.026.025">
        <li id="R_7.026.025ab">dharmato yo bhaved vipraḥ kṣatriyo vīryato bhavet</li>
        <li id="R_7.026.025cd">krodhād yaś ca bhaved agniḥ kṣāntyā ca vasudhāsamaḥ</li>
    </ul>
    <ul id="R_7.026.026">
        <li id="R_7.026.026ab">tasyāsmi kṛtasaṃketā lokapālasutasya vai</li>
        <li id="R_7.026.026cd">tam uddiśya ca me sarvaṃ vibhūṣaṇam idaṃ kṛtam</li>
    </ul>
    <ul id="R_7.026.027">
        <li id="R_7.026.027ab">yasya tasya hi nānyasya bhāvo māṃ prati tiṣṭhati</li>
        <li id="R_7.026.027cd">tena satyena māṃ rājan moktum arhasy ariṃdama</li>
    </ul>
    <ul id="R_7.026.028">
        <li id="R_7.026.028ab">sa hi tiṣṭhati dharmātmā sāmprataṃ matsamutsukaḥ</li>
        <li id="R_7.026.028cd">tan na vighnaṃ sutasyeha kartum arhasi muñca mām</li>
    </ul>
    <ul id="R_7.026.029">
        <li id="R_7.026.029ab">sadbhir ācaritaṃ mārgaṃ gaccha rākṣasapuṃgava</li>
        <li id="R_7.026.029cd">mānanīyo mayā hi tvaṃ lālanīyā tathāsmi te</li>
    </ul>
    <ul id="R_7.026.030">
        <li id="R_7.026.030ab">evaṃ bruvāṇāṃ rambhāṃ tāṃ dharmārthasahitaṃ vacaḥ</li>
        <li id="R_7.026.030cd">nirbhartsya rākṣaso mohāt pratigṛhya balād balī</li>
        <li id="R_7.026.030ef">kāmamohābhisaṃrabdho maithunāyopacakrame</li>
    </ul>
    <ul id="R_7.026.031">
        <li id="R_7.026.031ab">sā vimuktā tato rambhā bhraṣṭamālyavibhūṣaṇā</li>
        <li id="R_7.026.031cd">gajendrākrīḍamathitā nadīvākulatāṃ gatā</li>
    </ul>
    <ul id="R_7.026.032">
        <li id="R_7.026.032ab">sā vepamānā lajjantī bhītā karakṛtāñjaliḥ</li>
        <li id="R_7.026.032cd">nalakūbaram āsādya pādayor nipapāta ha</li>
    </ul>
    <ul id="R_7.026.033">
        <li id="R_7.026.033ab">tadavasthāṃ ca tāṃ dṛṣṭvā mahātmā nalakūbaraḥ</li>
        <li id="R_7.026.033cd">abravīt kim idaṃ bhadre pādayoḥ patitāsi me</li>
    </ul>
    <ul id="R_7.026.034">
        <li id="R_7.026.034ab">sā tu niśvasamānā ca vepamānātha sāñjaliḥ</li>
        <li id="R_7.026.034cd">tasmai sarvaṃ yathātathyam ākhyātum upacakrame</li>
    </ul>
    <ul id="R_7.026.035">
        <li id="R_7.026.035ab">eṣa deva daśagrīvaḥ prāpto gantuṃ triviṣṭapam</li>
        <li id="R_7.026.035cd">tena sainyasahāyena niśeha pariṇāmyate</li>
    </ul>
    <ul id="R_7.026.036">
        <li id="R_7.026.036ab">āyāntī tena dṛṣṭāsmi tvatsakāśam ariṃdama</li>
        <li id="R_7.026.036cd">gṛhītvā tena pṛṣṭāsmi kasya tvam iti rakṣasā</li>
    </ul>
    <ul id="R_7.026.037">
        <li id="R_7.026.037ab">mayā tu sarvaṃ yat satyaṃ tad dhi tasmai niveditam</li>
        <li id="R_7.026.037cd">kāmamohābhibhūtātmā nāśrauṣīt tad vaco mama</li>
    </ul>
    <ul id="R_7.026.038">
        <li id="R_7.026.038ab">yācyamāno mayā deva snuṣā te 'ham iti prabho</li>
        <li id="R_7.026.038cd">tat sarvaṃ pṛṣṭhataḥ kṛtvā balāt tenāsmi dharṣitā</li>
    </ul>
    <ul id="R_7.026.039">
        <li id="R_7.026.039ab">evaṃ tvam aparādhaṃ me kṣantum arhasi mānada</li>
        <li id="R_7.026.039cd">na hi tulyaṃ balaṃ saumya striyāś ca puruṣasya ca</li>
    </ul>
    <ul id="R_7.026.040">
        <li id="R_7.026.040ab">evaṃ śrutvā tu saṃkruddhas tadā vaiśvaraṇātmajaḥ</li>
        <li id="R_7.026.040cd">dharṣaṇāṃ tāṃ parāṃ śrutvā dhyānaṃ saṃpraviveśa ha</li>
    </ul>
    <ul id="R_7.026.041">
        <li id="R_7.026.041ab">tasya tat karma vijñāya tadā vaiśravaṇātmajaḥ</li>
        <li id="R_7.026.041cd">muhūrtād roṣatāmrākṣas toyaṃ jagrāha pāṇinā</li>
    </ul>
    <ul id="R_7.026.042">
        <li id="R_7.026.042ab">gṛhītvā salilaṃ divyam upaspṛśya yathāvidhi</li>
        <li id="R_7.026.042cd">utsasarja tadā śāpaṃ rākṣasendrāya dāruṇam</li>
    </ul>
    <ul id="R_7.026.043">
        <li id="R_7.026.043ab">akāmā tena yasmāt tvaṃ balād bhadre pradharṣitā</li>
        <li id="R_7.026.043cd">tasmāt sa yuvatīm anyāṃ nākāmām upayāsyati</li>
    </ul>
    <ul id="R_7.026.044">
        <li id="R_7.026.044ab">yadā tv akāmāṃ kāmārto dharṣayiṣyati yoṣitam</li>
        <li id="R_7.026.044cd">mūrdhā tu saptadhā tasya śakalībhavitā tadā</li>
    </ul>
    <ul id="R_7.026.045">
        <li id="R_7.026.045ab">tasminn udāhṛte śāpe jvalitāgnisamaprabhe</li>
        <li id="R_7.026.045cd">devadundubhayo neduḥ puṣpavṛṣṭiś ca khāc cyutā</li>
    </ul>
    <ul id="R_7.026.046">
        <li id="R_7.026.046ab">prajāpatimukhāś cāpi sarve devāḥ praharṣitāḥ</li>
        <li id="R_7.026.046cd">jñātvā lokagatiṃ sarvāṃ tasya mṛtyuṃ ca rakṣasaḥ</li>
    </ul>
    <ul id="R_7.026.047">
        <li id="R_7.026.047ab">śrutvā tu sa daśagrīvas taṃ śāpaṃ romaharṣaṇam</li>
        <li id="R_7.026.047cd">nārīṣu maithunaṃ bhāvaṃ nākāmāsv abhyarocayat</li>
    </ul>
    <ul id="R_7.027.001">
        <li id="R_7.027.001ab">kailāsaṃ laṅghayitvātha daśagrīvaḥ sarākṣasaḥ</li>
        <li id="R_7.027.001cd">āsasāda mahātejā indralokaṃ niśācaraḥ</li>
    </ul>
    <ul id="R_7.027.002">
        <li id="R_7.027.002ab">tasya rākṣasasainyasya samantād upayāsyataḥ</li>
        <li id="R_7.027.002cd">devalokaṃ yayau śabdo bhidyamānārṇavopamaḥ</li>
    </ul>
    <ul id="R_7.027.003">
        <li id="R_7.027.003ab">śrutvā tu rāvaṇaṃ prāptam indraḥ saṃcalitāsanaḥ</li>
        <li id="R_7.027.003cd">abravīt tatra tān devān sarvān eva samāgatān</li>
    </ul>
    <ul id="R_7.027.004">
        <li id="R_7.027.004ab">ādityān savasūn rudrān viśvān sādhyān marudgaṇān</li>
        <li id="R_7.027.004cd">sajjībhavata yuddhārthaṃ rāvaṇasya durātmanaḥ</li>
    </ul>
    <ul id="R_7.027.005">
        <li id="R_7.027.005ab">evam uktās tu śakreṇa devāḥ śakrasamā yudhi</li>
        <li id="R_7.027.005cd">saṃnahyanta mahāsattvā yuddhaśraddhāsamanvitāḥ</li>
    </ul>
    <ul id="R_7.027.006">
        <li id="R_7.027.006ab">sa tu dīnaḥ paritrasto mahendro rāvaṇaṃ prati</li>
        <li id="R_7.027.006cd">viṣṇoḥ samīpam āgatya vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.027.007">
        <li id="R_7.027.007ab">viṣṇo kathaṃ kariṣyāmo mahāvīryaparākrama</li>
        <li id="R_7.027.007cd">asau hi balavān rakṣo yuddhārtham abhivartate</li>
    </ul>
    <ul id="R_7.027.008">
        <li id="R_7.027.008ab">varapradānād balavān na khalv anyena hetunā</li>
        <li id="R_7.027.008cd">tac ca satyaṃ hi kartavyaṃ vākyaṃ deva prajāpateḥ</li>
    </ul>
    <ul id="R_7.027.009">
        <li id="R_7.027.009ab">tad yathā namucir vṛtro balir narakaśambarau</li>
        <li id="R_7.027.009cd">tvan mataṃ samavaṣṭabhya yathā dagdhās tathā kuru</li>
    </ul>
    <ul id="R_7.027.010">
        <li id="R_7.027.010ab">na hy anyo deva devānām āpatsu sumahābala</li>
        <li id="R_7.027.010cd">gatiḥ parāyaṇaṃ vāsti tvām ṛte puruṣottama</li>
    </ul>
    <ul id="R_7.027.011">
        <li id="R_7.027.011ab">tvaṃ hi nārāyaṇaḥ śrīmān padmanābhaḥ sanātanaḥ</li>
        <li id="R_7.027.011cd">tvayāhaṃ sthāpitaś caiva devarājye sanātane</li>
    </ul>
    <ul id="R_7.027.012">
        <li id="R_7.027.012ab">tad ākhyāhi yathātattvaṃ devadeva mama svayam</li>
        <li id="R_7.027.012cd">asicakrasahāyas tvaṃ yudhyase saṃyuge ripum</li>
    </ul>
    <ul id="R_7.027.013">
        <li id="R_7.027.013ab">evam uktaḥ sa śakreṇa devo nārāyaṇaḥ prabhuḥ</li>
        <li id="R_7.027.013cd">abravīn na paritrāsaḥ kāryas te śrūyatāṃ ca me</li>
    </ul>
    <ul id="R_7.027.014">
        <li id="R_7.027.014ab">na tāvad eṣa durvṛttaḥ śakyo daivatadānavaiḥ</li>
        <li id="R_7.027.014cd">hantuṃ yudhi samāsādya varadānena durjayaḥ</li>
    </ul>
    <ul id="R_7.027.015">
        <li id="R_7.027.015ab">sarvathā tu mahat karma kariṣyati balotkaṭaḥ</li>
        <li id="R_7.027.015cd">rakṣaḥ putrasahāyo 'sau dṛṣṭam etan nisargataḥ</li>
    </ul>
    <ul id="R_7.027.016">
        <li id="R_7.027.016ab">bravīṣi yat tu māṃ śakra saṃyuge yotsyasīti ha</li>
        <li id="R_7.027.016cd">naivāhaṃ pratiyotsye taṃ rāvaṇaṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_7.027.017">
        <li id="R_7.027.017ab">anihatya ripuṃ viṣṇur na hi pratinivartate</li>
        <li id="R_7.027.017cd">durlabhaś caiṣa kāmo 'dya varam āsādya rākṣase</li>
    </ul>
    <ul id="R_7.027.018">
        <li id="R_7.027.018ab">pratijānāmi devendra tvatsamīpaṃ śatakrato</li>
        <li id="R_7.027.018cd">rākṣasasyāham evāsya bhavitā mṛtyukāraṇam</li>
    </ul>
    <ul id="R_7.027.019">
        <li id="R_7.027.019ab">aham enaṃ vadhiṣyāmi rāvaṇaṃ sasutaṃ yudhi</li>
        <li id="R_7.027.019cd">devatās toṣayiṣyāmi jñātvā kālam upasthitam</li>
    </ul>
    <ul id="R_7.027.020">
        <li id="R_7.027.020ab">etasminn antare nādaḥ śuśruve rajanīkṣaye</li>
        <li id="R_7.027.020cd">tasya rāvaṇasainyasya prayuddhasya samantataḥ</li>
    </ul>
    <ul id="R_7.027.021">
        <li id="R_7.027.021ab">atha yuddhaṃ samabhavad devarākṣasayos tadā</li>
        <li id="R_7.027.021cd">ghoraṃ tumulanirhrādaṃ nānāpraharaṇāyudham</li>
    </ul>
    <ul id="R_7.027.022">
        <li id="R_7.027.022ab">etasminn antare śūrā rākṣasā ghoradarśanāḥ</li>
        <li id="R_7.027.022cd">yuddhārtham abhyadhāvanta sacivā rāvaṇājñayā</li>
    </ul>
    <ul id="R_7.027.023">
        <li id="R_7.027.023ab">mārīcaś ca prahastaś ca mahāpārśvamahodarau</li>
        <li id="R_7.027.023cd">akampano nikumbhaś ca śukaḥ sāraṇa eva ca</li>
    </ul>
    <ul id="R_7.027.024">
        <li id="R_7.027.024ab">saṃhrādir dhūmaketuś ca mahādaṃṣṭro mahāmukhaḥ</li>
        <li id="R_7.027.024cd">jambumālī mahāmālī virūpākṣaś ca rākṣasaḥ</li>
    </ul>
    <ul id="R_7.027.025">
        <li id="R_7.027.025ab">etaiḥ sarvair mahāvīryair vṛto rākṣasapuṃgavaḥ</li>
        <li id="R_7.027.025cd">rāvaṇasyāryakaḥ sainyaṃ sumālī praviveśa ha</li>
    </ul>
    <ul id="R_7.027.026">
        <li id="R_7.027.026ab">sa hi devagaṇān sarvān nānāpraharaṇaiḥ śitaiḥ</li>
        <li id="R_7.027.026cd">vidhvaṃsayati saṃkruddhaḥ saha taiḥ kṣaṇadācaraiḥ</li>
    </ul>
    <ul id="R_7.027.027">
        <li id="R_7.027.027ab">etasminn antare śūro vasūnām aṣṭamo vasuḥ</li>
        <li id="R_7.027.027cd">sāvitra iti vikhyātaḥ praviveśa mahāraṇam</li>
    </ul>
    <ul id="R_7.027.028">
        <li id="R_7.027.028ab">tato yuddhaṃ samabhavat surāṇāṃ rākṣasaiḥ saha</li>
        <li id="R_7.027.028cd">kruddhānāṃ rakṣasāṃ kīrtiṃ samareṣv anivartinām</li>
    </ul>
    <ul id="R_7.027.029">
        <li id="R_7.027.029ab">tatas te rākṣasāḥ śūrā devāṃs tān samare sthitān</li>
        <li id="R_7.027.029cd">nānāpraharaṇair ghorair jaghnuḥ śatasahasraśaḥ</li>
    </ul>
    <ul id="R_7.027.030">
        <li id="R_7.027.030ab">surās tu rākṣasān ghorān mahāvīryān svatejasā</li>
        <li id="R_7.027.030cd">samare vividhaiḥ śastrair anayan yamasādanam</li>
    </ul>
    <ul id="R_7.027.031">
        <li id="R_7.027.031ab">etasminn antare śūraḥ sumālī nāma rākṣasaḥ</li>
        <li id="R_7.027.031cd">nānāpraharaṇaiḥ kruddho raṇam evābhyavartata</li>
    </ul>
    <ul id="R_7.027.032">
        <li id="R_7.027.032ab">devānāṃ tad balaṃ sarvaṃ nānāpraharaṇaiḥ śitaiḥ</li>
        <li id="R_7.027.032cd">vidhvaṃsayati saṃkruddho vāyur jaladharān iva</li>
    </ul>
    <ul id="R_7.027.033">
        <li id="R_7.027.033ab">te mahābāṇavarṣaiś ca śūlaiḥ prāsaiś ca dāruṇaiḥ</li>
        <li id="R_7.027.033cd">pīḍyamānāḥ surāḥ sarve na vyatiṣṭhan samāhitāḥ</li>
    </ul>
    <ul id="R_7.027.034">
        <li id="R_7.027.034ab">tato vidrāvyamāṇeṣu tridaśeṣu sumālinā</li>
        <li id="R_7.027.034cd">vasūnām aṣṭamo devaḥ sāvitro vyavatiṣṭhata</li>
    </ul>
    <ul id="R_7.027.035">
        <li id="R_7.027.035ab">saṃvṛtaḥ svair anīkais tu praharantaṃ niśācaram</li>
        <li id="R_7.027.035cd">vikrameṇa mahātejā vārayām āsa saṃyuge</li>
    </ul>
    <ul id="R_7.027.036">
        <li id="R_7.027.036ab">sumattayos tayor āsīd yuddhaṃ loke sudāruṇam</li>
        <li id="R_7.027.036cd">sumālino vasoś caiva samareṣv anivartinoḥ</li>
    </ul>
    <ul id="R_7.027.037">
        <li id="R_7.027.037ab">tatas tasya mahābāṇair vasunā sumahātmanā</li>
        <li id="R_7.027.037cd">mahān sa pannagarathaḥ kṣaṇena vinipātitaḥ</li>
    </ul>
    <ul id="R_7.027.038">
        <li id="R_7.027.038ab">hatvā tu saṃyuge tasya rathaṃ bāṇaśataiḥ śitaiḥ</li>
        <li id="R_7.027.038cd">gadāṃ tasya vadhārthāya vasur jagrāha pāṇinā</li>
    </ul>
    <ul id="R_7.027.039">
        <li id="R_7.027.039ab">tāṃ pradīptāṃ pragṛhyāśu kāladaṇḍanibhāṃ śubhām</li>
        <li id="R_7.027.039cd">tasya mūrdhani sāvitraḥ sumāler vinipātayat</li>
    </ul>
    <ul id="R_7.027.040">
        <li id="R_7.027.040ab">tasya mūrdhani solkābhā patantī ca tadā babhau</li>
        <li id="R_7.027.040cd">sahasrākṣasamutsṛṣṭā girāv iva mahāśaniḥ</li>
    </ul>
    <ul id="R_7.027.041">
        <li id="R_7.027.041ab">tasya naivāsthi kāyo vā na māṃsaṃ dadṛśe tadā</li>
        <li id="R_7.027.041cd">gadayā bhasmasādbhūto raṇe tasmin nipātitaḥ</li>
    </ul>
    <ul id="R_7.027.042">
        <li id="R_7.027.042ab">taṃ dṛṣṭvā nihataṃ saṃkhye rākṣasās te samantataḥ</li>
        <li id="R_7.027.042cd">dudruvuḥ sahitāḥ sarve krośamānā mahāsvanam</li>
    </ul>
    <ul id="R_7.028.001">
        <li id="R_7.028.001ab">sumālinaṃ hataṃ dṛṣṭvā vasunā bhasmasātkṛtam</li>
        <li id="R_7.028.001cd">vidrutaṃ cāpi svaṃ sainyaṃ lakṣayitvārditaṃ śaraiḥ</li>
    </ul>
    <ul id="R_7.028.002">
        <li id="R_7.028.002ab">tataḥ sa balavān kruddho rāvaṇasya suto yudhi</li>
        <li id="R_7.028.002cd">nivartya rākṣasān sarvān meghanādo vyatiṣṭhata</li>
    </ul>
    <ul id="R_7.028.003">
        <li id="R_7.028.003ab">sa rathenāgnivarṇena kāmagena mahārathaḥ</li>
        <li id="R_7.028.003cd">abhidudrāva senāṃ tāṃ vanāny agnir iva jvalan</li>
    </ul>
    <ul id="R_7.028.004">
        <li id="R_7.028.004ab">tataḥ praviśatas tasya vividhāyudhadhāriṇaḥ</li>
        <li id="R_7.028.004cd">vidudruvur diśaḥ sarvā devās tasya ca darśanāt</li>
    </ul>
    <ul id="R_7.028.005">
        <li id="R_7.028.005ab">na tatrāvasthitaḥ kaś cid raṇe tasya yuyutsataḥ</li>
        <li id="R_7.028.005cd">sarvān āvidhya vitrastān dṛṣṭvā śakro 'bhyabhāṣata</li>
    </ul>
    <ul id="R_7.028.006">
        <li id="R_7.028.006ab">na bhetavyaṃ na gantavyaṃ nivartadhvaṃ raṇaṃ prati</li>
        <li id="R_7.028.006cd">eṣa gacchati me putro yuddhārtham aparājitaḥ</li>
    </ul>
    <ul id="R_7.028.007">
        <li id="R_7.028.007ab">tataḥ śakrasuto devo jayanta iti viśrutaḥ</li>
        <li id="R_7.028.007cd">rathenādbhutakalpena saṃgrāmam abhivartata</li>
    </ul>
    <ul id="R_7.028.008">
        <li id="R_7.028.008ab">tatas te tridaśāḥ sarve parivārya śacīsutam</li>
        <li id="R_7.028.008cd">rāvaṇasya sutaṃ yuddhe samāsādya vyavasthitāḥ</li>
    </ul>
    <ul id="R_7.028.009">
        <li id="R_7.028.009ab">teṣāṃ yuddhaṃ mahad abhūt sadṛśaṃ devarakṣasām</li>
        <li id="R_7.028.009cd">kṛte mahendraputrasya rākṣasendrasutasya ca</li>
    </ul>
    <ul id="R_7.028.010">
        <li id="R_7.028.010ab">tato mātaliputre tu gomukhe rākṣasātmajaḥ</li>
        <li id="R_7.028.010cd">sārathau pātayām āsa śarān kāñcanabhūṣaṇān</li>
    </ul>
    <ul id="R_7.028.011">
        <li id="R_7.028.011ab">śacīsutas tv api tathā jayantas tasya sārathim</li>
        <li id="R_7.028.011cd">taṃ caiva rāvaṇiṃ kruddhaḥ pratyavidhyad raṇājire</li>
    </ul>
    <ul id="R_7.028.012">
        <li id="R_7.028.012ab">tataḥ kruddho mahātejā rakṣo visphāritekṣaṇaḥ</li>
        <li id="R_7.028.012cd">rāvaṇiḥ śakraputraṃ taṃ śaravarṣair avākirat</li>
    </ul>
    <ul id="R_7.028.013">
        <li id="R_7.028.013ab">tataḥ pragṛhya śastrāṇi sāravanti mahānti ca</li>
        <li id="R_7.028.013cd">śataghnīs tomarān prāsān gadākhaḍgaparaśvadhān</li>
        <li id="R_7.028.013ef">sumahānty adriśṛṅgāṇi pātayām āsa rāvaṇiḥ</li>
    </ul>
    <ul id="R_7.028.014">
        <li id="R_7.028.014ab">tataḥ pravyathitā lokāḥ saṃjajñe ca tamo mahat</li>
        <li id="R_7.028.014cd">tasya rāvaṇaputrasya tadā śatrūn abhighnataḥ</li>
    </ul>
    <ul id="R_7.028.015">
        <li id="R_7.028.015ab">tatas tad daivatabalaṃ samantāt taṃ śacīsutam</li>
        <li id="R_7.028.015cd">bahuprakāram asvasthaṃ tatra tatra sma dhāvati</li>
    </ul>
    <ul id="R_7.028.016">
        <li id="R_7.028.016ab">nābhyajānaṃs tadānyonyaṃ śatrūn vā daivatāni vā</li>
        <li id="R_7.028.016cd">tatra tatra viparyastaṃ samantāt paridhāvitam</li>
    </ul>
    <ul id="R_7.028.017">
        <li id="R_7.028.017ab">etasminn antare śūraḥ pulomā nāma vīryavān</li>
        <li id="R_7.028.017cd">daiteyas tena saṃgṛhya śacīputro 'pavāhitaḥ</li>
    </ul>
    <ul id="R_7.028.018">
        <li id="R_7.028.018ab">gṛhītvā taṃ tu naptāraṃ praviṣṭaḥ sa mahodadhim</li>
        <li id="R_7.028.018cd">mātāmaho 'ryakas tasya paulomī yena sā śacī</li>
    </ul>
    <ul id="R_7.028.019">
        <li id="R_7.028.019ab">praṇāśaṃ dṛśya tu surā jayantasyātidāruṇam</li>
        <li id="R_7.028.019cd">vyathitāś cāprahṛṣṭāś ca samantād vipradudruvuḥ</li>
    </ul>
    <ul id="R_7.028.020">
        <li id="R_7.028.020ab">rāvaṇis tv atha saṃhṛṣṭo balaiḥ parivṛtaḥ svakaiḥ</li>
        <li id="R_7.028.020cd">abhyadhāvata devāṃs tān mumoca ca mahāsvanam</li>
    </ul>
    <ul id="R_7.028.021">
        <li id="R_7.028.021ab">dṛṣṭvā praṇāśaṃ putrasya rāvaṇeś cāpi vikramam</li>
        <li id="R_7.028.021cd">mātaliṃ prāha devendro rathaḥ samupanīyatām</li>
    </ul>
    <ul id="R_7.028.022">
        <li id="R_7.028.022ab">sa tu divyo mahābhīmaḥ sajja eva mahārathaḥ</li>
        <li id="R_7.028.022cd">upasthito mātalinā vāhyamāno manojavaḥ</li>
    </ul>
    <ul id="R_7.028.023">
        <li id="R_7.028.023ab">tato meghā rathe tasmiṃs taḍidvanto mahāsvanāḥ</li>
        <li id="R_7.028.023cd">agrato vāyucapalā gacchanto vyanadaṃs tadā</li>
    </ul>
    <ul id="R_7.028.024">
        <li id="R_7.028.024ab">nānāvādyāni vādyanta stutayaś ca samāhitāḥ</li>
        <li id="R_7.028.024cd">nanṛtuś cāpsaraḥsaṃghāḥ prayāte vāsave raṇam</li>
    </ul>
    <ul id="R_7.028.025">
        <li id="R_7.028.025ab">rudrair vasubhir ādityaiḥ sādhyaiś ca samarudgaṇaiḥ</li>
        <li id="R_7.028.025cd">vṛto nānāpraharaṇair niryayau tridaśādhipaḥ</li>
    </ul>
    <ul id="R_7.028.026">
        <li id="R_7.028.026ab">nirgacchatas tu śakrasya paruṣaṃ pavano vavau</li>
        <li id="R_7.028.026cd">bhāskaro niṣprabhaś cāsīn maholkāś ca prapedire</li>
    </ul>
    <ul id="R_7.028.027">
        <li id="R_7.028.027ab">etasminn antare śūro daśagrīvaḥ pratāpavān</li>
        <li id="R_7.028.027cd">āruroha rathaṃ divyaṃ nirmitaṃ viśvakarmaṇā</li>
    </ul>
    <ul id="R_7.028.028">
        <li id="R_7.028.028ab">pannagaiḥ sumahākāyair veṣṭitaṃ lomaharṣaṇaiḥ</li>
        <li id="R_7.028.028cd">yeṣāṃ niśvāsavātena pradīptam iva saṃyugam</li>
    </ul>
    <ul id="R_7.028.029">
        <li id="R_7.028.029ab">daityair niśācaraiḥ śūrai rathaḥ saṃparivāritaḥ</li>
        <li id="R_7.028.029cd">samarābhimukho divyo mahendram abhivartata</li>
    </ul>
    <ul id="R_7.028.030">
        <li id="R_7.028.030ab">putraṃ taṃ vārayitvāsau svayam eva vyavasthitaḥ</li>
        <li id="R_7.028.030cd">so 'pi yuddhād viniṣkramya rāvaṇiḥ samupāviśat</li>
    </ul>
    <ul id="R_7.028.031">
        <li id="R_7.028.031ab">tato yuddhaṃ pravṛttaṃ tu surāṇāṃ rākṣasaiḥ saha</li>
        <li id="R_7.028.031cd">śastrābhivarṣaṇaṃ ghoraṃ meghānām iva saṃyuge</li>
    </ul>
    <ul id="R_7.028.032">
        <li id="R_7.028.032ab">kumbhakarṇas tu duṣṭātmā nānāpraharaṇodyataḥ</li>
        <li id="R_7.028.032cd">nājñāyata tadā yuddhe saha kenāpy ayudhyata</li>
    </ul>
    <ul id="R_7.028.033">
        <li id="R_7.028.033ab">dantair bhujābhyāṃ padbhyāṃ ca śaktitomarasāyakaiḥ</li>
        <li id="R_7.028.033cd">yena kenaiva saṃrabdhas tāḍayām āsa vai surān</li>
    </ul>
    <ul id="R_7.028.034">
        <li id="R_7.028.034ab">tato rudrair mahābhāgaiḥ sahādityair niśācaraḥ</li>
        <li id="R_7.028.034cd">prayuddhas taiś ca saṃgrāme kṛttaḥ śastrair nirantaram</li>
    </ul>
    <ul id="R_7.028.035">
        <li id="R_7.028.035ab">tatas tad rākṣasaṃ sainyaṃ tridaśaiḥ samarudgaṇaiḥ</li>
        <li id="R_7.028.035cd">raṇe vidrāvitaṃ sarvaṃ nānāpraharaṇaiḥ śitaiḥ</li>
    </ul>
    <ul id="R_7.028.036">
        <li id="R_7.028.036ab">ke cid vinihatāḥ śastrair veṣṭanti sma mahītale</li>
        <li id="R_7.028.036cd">vāhaneṣv avasaktāś ca sthitā evāpare raṇe</li>
    </ul>
    <ul id="R_7.028.037">
        <li id="R_7.028.037ab">rathān nāgān kharān uṣṭrān pannagāṃs turagāṃs tathā</li>
        <li id="R_7.028.037cd">śiṃśumārān varāhāṃś ca piśācavadanāṃs tathā</li>
    </ul>
    <ul id="R_7.028.038">
        <li id="R_7.028.038ab">tān samāliṅgya bāhubhyāṃ viṣṭabdhāḥ ke cid ucchritāḥ</li>
        <li id="R_7.028.038cd">devais tu śastrasaṃviddhā mamrire ca niśācarāḥ</li>
    </ul>
    <ul id="R_7.028.039">
        <li id="R_7.028.039ab">citrakarma ivābhāti sa teṣāṃ raṇasaṃplavaḥ</li>
        <li id="R_7.028.039cd">nihatānāṃ pramattānāṃ rākṣasānāṃ mahītale</li>
    </ul>
    <ul id="R_7.028.040">
        <li id="R_7.028.040ab">śoṇitodaka niṣyandākaṅkagṛdhrasamākulā</li>
        <li id="R_7.028.040cd">pravṛttā saṃyugamukhe śastragrāhavatī nadī</li>
    </ul>
    <ul id="R_7.028.041">
        <li id="R_7.028.041ab">etasminn antare kruddho daśagrīvaḥ pratāpavān</li>
        <li id="R_7.028.041cd">nirīkṣya tad balaṃ sarvaṃ daivatair vinipātitam</li>
    </ul>
    <ul id="R_7.028.042">
        <li id="R_7.028.042ab">sa taṃ prativigāhyāśu pravṛddhaṃ sainyasāgaram</li>
        <li id="R_7.028.042cd">tridaśān samare nighnañ śakram evābhyavartata</li>
    </ul>
    <ul id="R_7.028.043">
        <li id="R_7.028.043ab">tataḥ śakro mahac cāpaṃ visphārya sumahāsvanam</li>
        <li id="R_7.028.043cd">yasya visphāraghoṣeṇa svananti sma diśo daśa</li>
    </ul>
    <ul id="R_7.028.044">
        <li id="R_7.028.044ab">tad vikṛṣya mahac cāpam indro rāvaṇamūrdhani</li>
        <li id="R_7.028.044cd">nipātayām āsa śarān pāvakādityavarcasaḥ</li>
    </ul>
    <ul id="R_7.028.045">
        <li id="R_7.028.045ab">tathaiva ca mahābāhur daśagrīvo vyavasthitaḥ</li>
        <li id="R_7.028.045cd">śakraṃ kārmukavibhraṣṭaiḥ śaravarṣair avākirat</li>
    </ul>
    <ul id="R_7.028.046">
        <li id="R_7.028.046ab">prayudhyator atha tayor bāṇavarṣaiḥ samantataḥ</li>
        <li id="R_7.028.046cd">nājñāyata tadā kiṃ cit sarvaṃ hi tamasā vṛtam</li>
    </ul>
    <ul id="R_7.029.001">
        <li id="R_7.029.001ab">tatas tamasi saṃjāte rākṣasā daivataiḥ saha</li>
        <li id="R_7.029.001cd">ayudhyanta balonmattāḥ sūdayantaḥ parasparam</li>
    </ul>
    <ul id="R_7.029.002">
        <li id="R_7.029.002ab">tatas tu devasainyena rākṣasānāṃ mahad balam</li>
        <li id="R_7.029.002cd">daśāṃśaṃ sthāpitaṃ yuddhe śeṣaṃ nītaṃ yamakṣayam</li>
    </ul>
    <ul id="R_7.029.003">
        <li id="R_7.029.003ab">tasmiṃs tu tamasā naddhe sarve te devarākṣasāḥ</li>
        <li id="R_7.029.003cd">anyonyaṃ nābhyajānanta yudhyamānāḥ parasparam</li>
    </ul>
    <ul id="R_7.029.004">
        <li id="R_7.029.004ab">indraś ca rāvaṇaś caiva rāvaṇiś ca mahābalaḥ</li>
        <li id="R_7.029.004cd">tasmiṃs tamojālavṛte moham īyur na te trayaḥ</li>
    </ul>
    <ul id="R_7.029.005">
        <li id="R_7.029.005ab">sa tu dṛṣṭvā balaṃ sarvaṃ nihataṃ rāvaṇo raṇe</li>
        <li id="R_7.029.005cd">krodham abhyāgamat tīvraṃ mahānādaṃ ca muktavān</li>
    </ul>
    <ul id="R_7.029.006">
        <li id="R_7.029.006ab">krodhāt sūtaṃ ca durdharṣaḥ syandanastham uvāca ha</li>
        <li id="R_7.029.006cd">parasainyasya madhyena yāvadantaṃ nayasva mām</li>
    </ul>
    <ul id="R_7.029.007">
        <li id="R_7.029.007ab">adyaitāṃs tridaśān sarvān vikramaiḥ samare svayam</li>
        <li id="R_7.029.007cd">nānāśastrair mahāsārair nāśayāmi nabhastalāt</li>
    </ul>
    <ul id="R_7.029.008">
        <li id="R_7.029.008ab">aham indraṃ vadhiṣyāmi varuṇaṃ dhanadaṃ yamam</li>
        <li id="R_7.029.008cd">tridaśān vinihatyāśu svayaṃ sthāsyāmy athopari</li>
    </ul>
    <ul id="R_7.029.009">
        <li id="R_7.029.009ab">viṣādo na ca kartavyaḥ śīghraṃ vāhaya me ratham</li>
        <li id="R_7.029.009cd">dviḥ khalu tvāṃ bravīmy adya yāvadantaṃ nayasva mām</li>
    </ul>
    <ul id="R_7.029.010">
        <li id="R_7.029.010ab">ayaṃ sa nandanoddeśo yatra vartāmahe vayam</li>
        <li id="R_7.029.010cd">naya mām adya tatra tvam udayo yatra parvataḥ</li>
    </ul>
    <ul id="R_7.029.011">
        <li id="R_7.029.011ab">tasya tad vacanaṃ śrutvā turagān sa manojavān</li>
        <li id="R_7.029.011cd">ādideśātha śatrūṇāṃ madhyenaiva ca sārathiḥ</li>
    </ul>
    <ul id="R_7.029.012">
        <li id="R_7.029.012ab">tasya taṃ niścayaṃ jñātvā śakro deveśvaras tadā</li>
        <li id="R_7.029.012cd">rathasthaḥ samarasthāṃs tān devān vākyam athābravīt</li>
    </ul>
    <ul id="R_7.029.013">
        <li id="R_7.029.013ab">surāḥ śṛṇuta madvākyaṃ yat tāvan mama rocate</li>
        <li id="R_7.029.013cd">jīvann eva daśagrīvaḥ sādhu rakṣo nigṛhyatām</li>
    </ul>
    <ul id="R_7.029.014">
        <li id="R_7.029.014ab">eṣa hy atibalaḥ sainye rathena pavanaujasā</li>
        <li id="R_7.029.014cd">gamiṣyati pravṛddhormiḥ samudra iva parvaṇi</li>
    </ul>
    <ul id="R_7.029.015">
        <li id="R_7.029.015ab">na hy eṣa hantuṃ śakyo 'dya varadānāt sunirbhayaḥ</li>
        <li id="R_7.029.015cd">tad grahīṣyāmahe rakṣo yattā bhavata saṃyuge</li>
    </ul>
    <ul id="R_7.029.016">
        <li id="R_7.029.016ab">yathā baliṃ nigṛhyaitat trailokyaṃ bhujyate mayā</li>
        <li id="R_7.029.016cd">evam etasya pāpasya nigraho mama rocate</li>
    </ul>
    <ul id="R_7.029.017">
        <li id="R_7.029.017ab">tato 'nyaṃ deśam āsthāya śakraḥ saṃtyajya rāvaṇam</li>
        <li id="R_7.029.017cd">ayudhyata mahātejā rākṣasān nāśayan raṇe</li>
    </ul>
    <ul id="R_7.029.018">
        <li id="R_7.029.018ab">uttareṇa daśagrīvaḥ praviveśānivartitaḥ</li>
        <li id="R_7.029.018cd">dakṣiṇena tu pārśvena praviveśa śatakratuḥ</li>
    </ul>
    <ul id="R_7.029.019">
        <li id="R_7.029.019ab">tataḥ sa yojanaśataṃ praviṣṭo rākṣasādhipaḥ</li>
        <li id="R_7.029.019cd">devatānāṃ balaṃ kṛtsnaṃ śaravarṣair avākirat</li>
    </ul>
    <ul id="R_7.029.020">
        <li id="R_7.029.020ab">tataḥ śakro nirīkṣyātha praviṣṭaṃ taṃ balaṃ svakam</li>
        <li id="R_7.029.020cd">nyavartayad asaṃbhrāntaḥ samāvṛtya daśānanam</li>
    </ul>
    <ul id="R_7.029.021">
        <li id="R_7.029.021ab">etasminn antare nādo mukto dānavarākṣasaiḥ</li>
        <li id="R_7.029.021cd">hā hatāḥ smeti taṃ dṛṣṭvā grastaṃ śakreṇa rāvaṇam</li>
    </ul>
    <ul id="R_7.029.022">
        <li id="R_7.029.022ab">tato rathaṃ samāruhya rāvaṇiḥ krodhamūrchitaḥ</li>
        <li id="R_7.029.022cd">tat sainyam atisaṃkruddhaḥ praviveśa sudāruṇam</li>
    </ul>
    <ul id="R_7.029.023">
        <li id="R_7.029.023ab">sa tāṃ praviśya māyāṃ tu dattāṃ gopatinā purā</li>
        <li id="R_7.029.023cd">adṛśyaḥ sarvabhūtānāṃ tat sainyaṃ samavākirat</li>
    </ul>
    <ul id="R_7.029.024">
        <li id="R_7.029.024ab">tataḥ sa devān saṃtyajya śakram evābhyayād drutam</li>
        <li id="R_7.029.024cd">mahendraś ca mahātejā na dadarśa sutaṃ ripoḥ</li>
    </ul>
    <ul id="R_7.029.025">
        <li id="R_7.029.025ab">sa mātaliṃ hayāṃś caiva tāḍayitvā śarottamaiḥ</li>
        <li id="R_7.029.025cd">mahendraṃ bāṇavarṣeṇa śīghrahasto hy avākirat</li>
    </ul>
    <ul id="R_7.029.026">
        <li id="R_7.029.026ab">tataḥ śakro rathaṃ tyaktva visṛjya ca sa mātalim</li>
        <li id="R_7.029.026cd">airāvataṃ samāruhya mṛgayām āsa rāvaṇim</li>
    </ul>
    <ul id="R_7.029.027">
        <li id="R_7.029.027ab">sa tu māyā balād rakṣaḥ saṃgrāme nābhyadṛśyata</li>
        <li id="R_7.029.027cd">kiramāṇaḥ śaraughena mahendram amitaujasaṃ</li>
    </ul>
    <ul id="R_7.029.028">
        <li id="R_7.029.028ab">sa taṃ yadā pariśrāntam indraṃ mene 'tha rāvaṇiḥ</li>
        <li id="R_7.029.028cd">tadainaṃ māyayā baddhvā svasainyam abhito 'nayat</li>
    </ul>
    <ul id="R_7.029.029">
        <li id="R_7.029.029ab">taṃ dṛṣṭvātha balāt tasmin māyayāpahṛtaṃ raṇe</li>
        <li id="R_7.029.029cd">mahendram amarāḥ sarve kiṃ nv etad iti cukruśuḥ</li>
        <li id="R_7.029.029ef">na hi dṛśyati vidyāvān māyayā yena nīyate</li>
    </ul>
    <ul id="R_7.029.030">
        <li id="R_7.029.030ab">etasminn antare cāpi sarve suragaṇās tadā</li>
        <li id="R_7.029.030cd">abhyadravan susaṃkruddhā rāvaṇaṃ śastravṛṣṭibhiḥ</li>
    </ul>
    <ul id="R_7.029.031">
        <li id="R_7.029.031ab">rāvaṇas tu samāsādya vasvādityamarudgaṇān</li>
        <li id="R_7.029.031cd">na śaśāka raṇe sthātuṃ na yoddhuṃ śastrapīḍitaḥ</li>
    </ul>
    <ul id="R_7.029.032">
        <li id="R_7.029.032ab">taṃ tu dṛṣṭvā pariśrāntaṃ prahārair jarjaracchavim</li>
        <li id="R_7.029.032cd">rāvaṇiḥ pitaraṃ yuddhe 'darśanastho 'bravīd idam</li>
    </ul>
    <ul id="R_7.029.033">
        <li id="R_7.029.033ab">āgaccha tāta gacchāvo nivṛttaṃ raṇakarma tat</li>
        <li id="R_7.029.033cd">jitaṃ te viditaṃ bho 'stu svastho bhava gatajvaraḥ</li>
    </ul>
    <ul id="R_7.029.034">
        <li id="R_7.029.034ab">ayaṃ hi surasainyasya trailokyasya ca yaḥ prabhuḥ</li>
        <li id="R_7.029.034cd">sa gṛhīto mayā śakro bhagnamānāḥ surāḥ kṛtāḥ</li>
    </ul>
    <ul id="R_7.029.035">
        <li id="R_7.029.035ab">yatheṣṭaṃ bhuṅkṣva trailokyaṃ nigṛhya ripum ojasā</li>
        <li id="R_7.029.035cd">vṛthā te kiṃ śramaṃ kṛtvā yuddhaṃ hi tava niṣphalam</li>
    </ul>
    <ul id="R_7.029.036">
        <li id="R_7.029.036ab">sa daivatabalāt tasmān nivṛtto raṇakarmaṇaḥ</li>
        <li id="R_7.029.036cd">tac chrutvā rāvaṇer vākyaṃ svasthacetā daśānanaḥ</li>
    </ul>
    <ul id="R_7.029.037">
        <li id="R_7.029.037ab">
            <seg type="pāda" n="a">atha raṇavigatajvaraḥ prabhur</seg>
            <seg type="pāda" n="b">vijayam avāpya niśācarādhipaḥ</seg>
        </li>
        <li id="R_7.029.037cd">
            <seg type="pāda" n="c">bhavanam abhi tato jagāma hṛṣṭaḥ</seg>
            <seg type="pāda" n="d">svasutam avāpya ca vākyam abravīt</seg>
        </li>
    </ul>
    <ul id="R_7.029.038">
        <li id="R_7.029.038ab">
            <seg type="pāda" n="a">atibalasadṛśaiḥ parākramais tair</seg>
            <seg type="pāda" n="b">mama kulamānavivardhanaṃ kṛtam</seg>
        </li>
        <li id="R_7.029.038cd">
            <seg type="pāda" n="c">yad amarasamavikrama tvayā</seg>
            <seg type="pāda" n="d">tridaśapatis tridaśāś ca nirjitāḥ</seg>
        </li>
    </ul>
    <ul id="R_7.029.039">
        <li id="R_7.029.039ab">
            <seg type="pāda" n="a">tvaritam upanayasva vāsavaṃ</seg>
            <seg type="pāda" n="b">nagaram ito vraja sainyasaṃvṛtaḥ</seg>
        </li>
        <li id="R_7.029.039cd">
            <seg type="pāda" n="c">aham api tava gacchato drutaṃ</seg>
            <seg type="pāda" n="d">saha sacivair anuyāmi pṛṣṭhataḥ</seg>
        </li>
    </ul>
    <ul id="R_7.029.040">
        <li id="R_7.029.040ab">
            <seg type="pāda" n="a">atha sa balavṛtaḥ savāhanas</seg>
            <seg type="pāda" n="b">tridaśapatiṃ parigṛhya rāvaṇiḥ</seg>
        </li>
        <li id="R_7.029.040cd">
            <seg type="pāda" n="c">svabhavanam upagamya rākṣaso</seg>
            <seg type="pāda" n="d">muditamanā visasarja rākṣasān</seg>
        </li>
    </ul>
    <ul id="R_7.030.001">
        <li id="R_7.030.001ab">jite mahendre 'tibale rāvaṇasya sutena vai</li>
        <li id="R_7.030.001cd">prajāpatiṃ puraskṛtya gatā laṅkāṃ surās tadā</li>
    </ul>
    <ul id="R_7.030.002">
        <li id="R_7.030.002ab">taṃ rāvaṇaṃ samāsādya putrabhrātṛbhir āvṛtam</li>
        <li id="R_7.030.002cd">abravīd gagane tiṣṭhan sāntvapūrvaṃ prajāpatiḥ</li>
    </ul>
    <ul id="R_7.030.003">
        <li id="R_7.030.003ab">vatsa rāvaṇa tuṣṭo 'smi tava putrasya saṃyuge</li>
        <li id="R_7.030.003cd">aho 'sya vikramaudāryaṃ tava tulyo 'dhiko 'pi vā</li>
    </ul>
    <ul id="R_7.030.004">
        <li id="R_7.030.004ab">jitaṃ hi bhavatā sarvaṃ trailokyaṃ svena tejasā</li>
        <li id="R_7.030.004cd">kṛtā pratijñā saphalā prīto 'smi svasutena vai</li>
    </ul>
    <ul id="R_7.030.005">
        <li id="R_7.030.005ab">ayaṃ ca putro 'tibalas tava rāvaṇarāvaṇiḥ</li>
        <li id="R_7.030.005cd">indrajit tv iti vikhyāto jagaty eṣa bhaviṣyati</li>
    </ul>
    <ul id="R_7.030.006">
        <li id="R_7.030.006ab">balavāñ śatrunirjetā bhaviṣyaty eṣa rākṣasaḥ</li>
        <li id="R_7.030.006cd">yam āśritya tvayā rājan sthāpitās tridaśā vaśe</li>
    </ul>
    <ul id="R_7.030.007">
        <li id="R_7.030.007ab">tan mucyatāṃ mahābāho mahendraḥ pākaśāsanaḥ</li>
        <li id="R_7.030.007cd">kiṃ cāsya mokṣaṇārthāya prayacchanti divaukasaḥ</li>
    </ul>
    <ul id="R_7.030.008">
        <li id="R_7.030.008ab">athābravīn mahātejā indrajit samitiṃjayaḥ</li>
        <li id="R_7.030.008cd">amaratvam ahaṃ deva vṛṇomīhāsya mokṣaṇe</li>
    </ul>
    <ul id="R_7.030.009">
        <li id="R_7.030.009ab">abravīt tu tadā devo rāvaṇiṃ kamalodbhavaḥ</li>
        <li id="R_7.030.009cd">nāsti sarvāmaratvaṃ hi keṣāṃ cit prāṇināṃ bhuvi</li>
    </ul>
    <ul id="R_7.030.010">
        <li id="R_7.030.010ab">athābravīt sa tatrastham indrajit padmasaṃbhavam</li>
        <li id="R_7.030.010cd">śrūyatāṃ yā bhavet siddhiḥ śatakratuvimokṣaṇe</li>
    </ul>
    <ul id="R_7.030.011">
        <li id="R_7.030.011ab">mameṣṭaṃ nityaśo deva havyaiḥ saṃpūjya pāvakam</li>
        <li id="R_7.030.011cd">saṃgrāmam avatartuṃ vai śatrunirjayakāṅkṣiṇaḥ</li>
    </ul>
    <ul id="R_7.030.012">
        <li id="R_7.030.012ab">tasmiṃś ced asamāpte tu japyahome vibhāvasoḥ</li>
        <li id="R_7.030.012cd">yudhyeyaṃ deva saṃgrāme tadā me syād vināśanam</li>
    </ul>
    <ul id="R_7.030.013">
        <li id="R_7.030.013ab">sarvo hi tapasā caiva vṛṇoty amaratāṃ pumān</li>
        <li id="R_7.030.013cd">vikrameṇa mayā tv etad amaratvaṃ pravartitam</li>
    </ul>
    <ul id="R_7.030.014">
        <li id="R_7.030.014ab">evam astv iti taṃ prāha vākyaṃ devaḥ prajāpatiḥ</li>
        <li id="R_7.030.014cd">muktaś cendrajitā śakro gatāś ca tridivaṃ surāḥ</li>
    </ul>
    <ul id="R_7.030.015">
        <li id="R_7.030.015ab">etasminn antare śakro dīno bhraṣṭāmbarasrajaḥ</li>
        <li id="R_7.030.015cd">rāma cintāparītātmā dhyānatatparatāṃ gataḥ</li>
    </ul>
    <ul id="R_7.030.016">
        <li id="R_7.030.016ab">taṃ tu dṛṣṭvā tathābhūtaṃ prāha devaḥ prajāpatiḥ</li>
        <li id="R_7.030.016cd">śakrakrato kim utkaṇṭhāṃ karoṣi smara duṣkṛtam</li>
    </ul>
    <ul id="R_7.030.017">
        <li id="R_7.030.017ab">amarendra mayā bahvyaḥ prajāḥ sṛṣṭāḥ purā prabho</li>
        <li id="R_7.030.017cd">ekavarṇāḥ samābhāṣā ekarūpāś ca sarvaśaḥ</li>
    </ul>
    <ul id="R_7.030.018">
        <li id="R_7.030.018ab">tāsāṃ nāsti viśeṣo hi darśane lakṣaṇe 'pi vā</li>
        <li id="R_7.030.018cd">tato 'ham ekāgramanās tāḥ prajāḥ paryacintayam</li>
    </ul>
    <ul id="R_7.030.019">
        <li id="R_7.030.019ab">so 'haṃ tāsāṃ viśeṣārthaṃ striyam ekāṃ vinirmame</li>
        <li id="R_7.030.019cd">yad yat prajānāṃ pratyaṅgaṃ viśiṣṭaṃ tat tad uddhṛtam</li>
    </ul>
    <ul id="R_7.030.020">
        <li id="R_7.030.020ab">tato mayā rūpaguṇair ahalyā strī vinirmitā</li>
        <li id="R_7.030.020cd">ahalyety eva ca mayā tasyā nāma pravartitam</li>
    </ul>
    <ul id="R_7.030.021">
        <li id="R_7.030.021ab">nirmitāyāṃ tu devendra tasyāṃ nāryāṃ surarṣabha</li>
        <li id="R_7.030.021cd">bhaviṣyatīti kasyaiṣā mama cintā tato 'bhavat</li>
    </ul>
    <ul id="R_7.030.022">
        <li id="R_7.030.022ab">tvaṃ tu śakra tadā nārīṃ jānīṣe manasā prabho</li>
        <li id="R_7.030.022cd">sthānādhikatayā patnī mamaiṣeti puraṃdara</li>
    </ul>
    <ul id="R_7.030.023">
        <li id="R_7.030.023ab">sā mayā nyāsabhūtā tu gautamasya mahātmanaḥ</li>
        <li id="R_7.030.023cd">nyastā bahūni varṣāṇi tena niryātitā ca sā</li>
    </ul>
    <ul id="R_7.030.024">
        <li id="R_7.030.024ab">tatas tasya parijñāya mayā sthairyaṃ mahāmuneḥ</li>
        <li id="R_7.030.024cd">jñātvā tapasi siddhiṃ ca patnyarthaṃ sparśitā tadā</li>
    </ul>
    <ul id="R_7.030.025">
        <li id="R_7.030.025ab">sa tayā saha dharmātmā ramate sma mahāmuniḥ</li>
        <li id="R_7.030.025cd">āsan nirāśā devās tu gautame dattayā tayā</li>
    </ul>
    <ul id="R_7.030.026">
        <li id="R_7.030.026ab">tvaṃ kruddhas tv iha kāmātmā gatvā tasyāśramaṃ muneḥ</li>
        <li id="R_7.030.026cd">dṛṣṭavāṃś ca tadā tāṃ strīṃ dīptām agniśikhām iva</li>
    </ul>
    <ul id="R_7.030.027">
        <li id="R_7.030.027ab">sā tvayā dharṣitā śakra kāmārtena samanyunā</li>
        <li id="R_7.030.027cd">dṛṣṭas tvaṃ ca tadā tena āśrame paramarṣiṇā</li>
    </ul>
    <ul id="R_7.030.028">
        <li id="R_7.030.028ab">tataḥ kruddhena tenāsi śaptaḥ paramatejasā</li>
        <li id="R_7.030.028cd">gato 'si yena devendra daśābhāgaviparyayam</li>
    </ul>
    <ul id="R_7.030.029">
        <li id="R_7.030.029ab">yasmān me dharṣitā patnī tvayā vāsava nirbhayam</li>
        <li id="R_7.030.029cd">tasmāt tvaṃ samare rājañ śatruhastaṃ gamiṣyasi</li>
    </ul>
    <ul id="R_7.030.030">
        <li id="R_7.030.030ab">ayaṃ tu bhāvo durbuddhe yas tvayeha pravartitaḥ</li>
        <li id="R_7.030.030cd">mānuṣeṣv api sarveṣu bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.030.031">
        <li id="R_7.030.031ab">tatrādharmaḥ subalavān samutthāsyati yo mahān</li>
        <li id="R_7.030.031cd">tatrārdhaṃ tasya yaḥ kartā tvayy ardhaṃ nipatiṣyati</li>
    </ul>
    <ul id="R_7.030.032">
        <li id="R_7.030.032ab">na ca te sthāvaraṃ sthānaṃ bhaviṣyati puraṃdara</li>
        <li id="R_7.030.032cd">etenādharmayogena yas tvayeha pravartitaḥ</li>
    </ul>
    <ul id="R_7.030.033">
        <li id="R_7.030.033ab">yaś ca yaś ca surendraḥ syād dhruvaḥ sa na bhaviṣyati</li>
        <li id="R_7.030.033cd">eṣa śāpo mayā mukta ity asau tvāṃ tadābravīt</li>
    </ul>
    <ul id="R_7.030.034">
        <li id="R_7.030.034ab">tāṃ tu bhāryāṃ vinirbhartsya so 'bravīt sumahātapāḥ</li>
        <li id="R_7.030.034cd">durvinīte vinidhvaṃsa mamāśramasamīpataḥ</li>
    </ul>
    <ul id="R_7.030.035">
        <li id="R_7.030.035ab">rūpayauvanasaṃpannā yasmāt tvam anavasthitā</li>
        <li id="R_7.030.035cd">tasmād rūpavatī loke na tvam ekā bhaviṣyasi</li>
    </ul>
    <ul id="R_7.030.036">
        <li id="R_7.030.036ab">rūpaṃ ca tat prajāḥ sarvā gamiṣyanti sudurlabham</li>
        <li id="R_7.030.036cd">yat tavedaṃ samāśritya vibhrame 'yam upasthitaḥ</li>
    </ul>
    <ul id="R_7.030.037">
        <li id="R_7.030.037ab">tadā prabhṛti bhūyiṣṭhaṃ prajā rūpasamanvitāḥ</li>
        <li id="R_7.030.037cd">śāpotsargād dhi tasyedaṃ muneḥ sarvam upāgatam</li>
    </ul>
    <ul id="R_7.030.038">
        <li id="R_7.030.038ab">tat smara tvaṃ mahābāho duṣkṛtaṃ yat tvayā kṛtam</li>
        <li id="R_7.030.038cd">yena tvaṃ grahaṇaṃ śatror gato nānyena vāsava</li>
    </ul>
    <ul id="R_7.030.039">
        <li id="R_7.030.039ab">śīghraṃ yajasva yajñaṃ tvaṃ vaiṣṇavaṃ susamāhitaḥ</li>
        <li id="R_7.030.039cd">pāvitas tena yajñena yāsyasi tridivaṃ tataḥ</li>
    </ul>
    <ul id="R_7.030.040">
        <li id="R_7.030.040ab">putraś ca tava devendra na vinaṣṭo mahāraṇe</li>
        <li id="R_7.030.040cd">nītaḥ saṃnihitaś caiva aryakeṇa mahodadhau</li>
    </ul>
    <ul id="R_7.030.041">
        <li id="R_7.030.041ab">etac chrutvā mahendras tu yajñam iṣṭvā ca vaiṣṇavam</li>
        <li id="R_7.030.041cd">punas tridivam ākrāmad anvaśāsac ca devatāḥ</li>
    </ul>
    <ul id="R_7.030.042">
        <li id="R_7.030.042ab">etad indrajito rāma balaṃ yat kīrtitaṃ mayā</li>
        <li id="R_7.030.042cd">nirjitas tena devendraḥ prāṇino 'nye ca kiṃ punaḥ</li>
    </ul>
    <ul id="R_7.031.001">
        <li id="R_7.031.001ab">tato rāmo mahātejā vismayāt punar eva hi</li>
        <li id="R_7.031.001cd">uvāca praṇato vākyam agastyam ṛṣisattamam</li>
    </ul>
    <ul id="R_7.031.002">
        <li id="R_7.031.002ab">bhagavan kiṃ tadā lokāḥ śūnyā āsan dvijottama</li>
        <li id="R_7.031.002cd">dharṣaṇāṃ yatra na prāpto rāvaṇo rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_7.031.003">
        <li id="R_7.031.003ab">utāho hīnavīryās te babhuvuḥ pṛthivīkṣitaḥ</li>
        <li id="R_7.031.003cd">bahiṣkṛtā varāstraiś ca bahavo nirjitā nṛpāḥ</li>
    </ul>
    <ul id="R_7.031.004">
        <li id="R_7.031.004ab">rāghavasya vacaḥ śrutvā agastyo bhagavān ṛṣiḥ</li>
        <li id="R_7.031.004cd">uvāca rāmaṃ prahasan pitāmaha iveśvaram</li>
    </ul>
    <ul id="R_7.031.005">
        <li id="R_7.031.005ab">sa evaṃ bādhamānas tu pārthivān pārthivarṣabha</li>
        <li id="R_7.031.005cd">cacāra rāvaṇo rāma pṛthivyāṃ pṛthivīpate</li>
    </ul>
    <ul id="R_7.031.006">
        <li id="R_7.031.006ab">tato māhiṣmatīṃ nāma purīṃ svargapurīprabhām</li>
        <li id="R_7.031.006cd">saṃprāpto yatra sāmnidhyaṃ paramaṃ vasuretasaḥ</li>
    </ul>
    <ul id="R_7.031.007">
        <li id="R_7.031.007ab">tulya āsīn nṛpas tasya pratāpād vasuretasaḥ</li>
        <li id="R_7.031.007cd">arjuno nāma yasyāgniḥ śarakuṇḍe śayaḥ sadā</li>
    </ul>
    <ul id="R_7.031.008">
        <li id="R_7.031.008ab">tam eva divasaṃ so 'tha haihayādhipatir balī</li>
        <li id="R_7.031.008cd">arjuno narmadāṃ rantuṃ gataḥ strībhiḥ saheśvaraḥ</li>
    </ul>
    <ul id="R_7.031.009">
        <li id="R_7.031.009ab">rāvaṇo rākṣasendras tu tasyāmātyān apṛcchata</li>
        <li id="R_7.031.009cd">kvārjuno vo nṛpaḥ so 'dya śīghram ākhyātum arhatha</li>
    </ul>
    <ul id="R_7.031.010">
        <li id="R_7.031.010ab">rāvaṇo 'ham anuprāpto yuddhepsur nṛvareṇa tu</li>
        <li id="R_7.031.010cd">mamāgamanam avyagrair yuṣmābhiḥ saṃnivedyatām</li>
    </ul>
    <ul id="R_7.031.011">
        <li id="R_7.031.011ab">ity evaṃ rāvaṇenoktās te 'mātyāḥ suvipaścitaḥ</li>
        <li id="R_7.031.011cd">abruvan rākṣasapatim asāmnidhyaṃ mahīpateḥ</li>
    </ul>
    <ul id="R_7.031.012">
        <li id="R_7.031.012ab">śrutvā viśravasaḥ putraḥ paurāṇām arjunaṃ gatam</li>
        <li id="R_7.031.012cd">apasṛtyāgato vindhyaṃ himavatsaṃnibhaṃ girim</li>
    </ul>
    <ul id="R_7.031.013">
        <li id="R_7.031.013ab">sa tam abhram ivāviṣṭam udbhrāntam iva medinīm</li>
        <li id="R_7.031.013cd">apaśyad rāvaṇo vindhyam ālikhantam ivāmbaram</li>
    </ul>
    <ul id="R_7.031.014">
        <li id="R_7.031.014ab">sahasraśikharopetaṃ siṃhādhyuṣitakandaram</li>
        <li id="R_7.031.014cd">prapāta patitaiḥ śītaiḥ sāṭṭahāsam ivāmbubhiḥ</li>
    </ul>
    <ul id="R_7.031.015">
        <li id="R_7.031.015ab">devadānavagandharvaiḥ sāpsarogaṇakiṃnaraiḥ</li>
        <li id="R_7.031.015cd">sāha strībhiḥ krīḍamānaiḥ svargabhūtaṃ mahocchrayam</li>
    </ul>
    <ul id="R_7.031.016">
        <li id="R_7.031.016ab">nadībhiḥ syandamānābhir agatipratimaṃ jalam</li>
        <li id="R_7.031.016cd">sphuṭībhiś calajihvābhir vamantam iva viṣṭhitam</li>
    </ul>
    <ul id="R_7.031.017">
        <li id="R_7.031.017ab">ulkāvantaṃ darīvantaṃ himavatsaṃnibhaṃ girim</li>
        <li id="R_7.031.017cd">paśyamānas tato vindhyaṃ rāvaṇo narmadāṃ yayau</li>
    </ul>
    <ul id="R_7.031.018">
        <li id="R_7.031.018ab">calopalajalāṃ puṇyāṃ paścimodadhigāminīm</li>
        <li id="R_7.031.018cd">mahiṣaiḥ sṛmaraiḥ siṃhaiḥ śārdūlarkṣagajottamaiḥ</li>
        <li id="R_7.031.018ef">uṣṇābhitaptais tṛṣitaiḥ saṃkṣobhitajalāśayām</li>
    </ul>
    <ul id="R_7.031.019">
        <li id="R_7.031.019ab">cakravākaiḥ sakāraṇḍaiḥ sahaṃsajalakukkuṭaiḥ</li>
        <li id="R_7.031.019cd">sārasaiś ca sadāmattaiḥ kokūjadbhiḥ samāvṛtām</li>
    </ul>
    <ul id="R_7.031.020">
        <li id="R_7.031.020ab">phulladrumakṛtottaṃsāṃ cakravākayugastanīm</li>
        <li id="R_7.031.020cd">vistīrṇapulinaśroṇīṃ haṃsāvalisumekhalām</li>
    </ul>
    <ul id="R_7.031.021">
        <li id="R_7.031.021ab">puṣpareṇvanuliptāṅgīṃ jalaphenāmalāṃśukām</li>
        <li id="R_7.031.021cd">jalāvagāhasaṃsparśāṃ phullotpalaśubhekṣaṇām</li>
    </ul>
    <ul id="R_7.031.022">
        <li id="R_7.031.022ab">puṣpakād avaruhyāśu narmadāṃ saritāṃ varām</li>
        <li id="R_7.031.022cd">iṣṭām iva varāṃ nārīm avagāhya daśānanaḥ</li>
    </ul>
    <ul id="R_7.031.023">
        <li id="R_7.031.023ab">sa tasyāḥ puline ramye nānākusumaśobhite</li>
        <li id="R_7.031.023cd">upopaviṣṭaḥ sacivaiḥ sārdhaṃ rākṣasapuṃgavaḥ</li>
        <li id="R_7.031.023ef">narmadā darśajaṃ harṣam āptavān rākṣaseśvaraḥ</li>
    </ul>
    <ul id="R_7.031.024">
        <li id="R_7.031.024ab">tataḥ salīlaṃ prahasān rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_7.031.024cd">uvāca sacivāṃs tatra mārīcaśukasāraṇān</li>
    </ul>
    <ul id="R_7.031.025">
        <li id="R_7.031.025ab">eṣa raśmisahasreṇa jagat kṛtveva kāñcanam</li>
        <li id="R_7.031.025cd">tīkṣṇatāpakaraḥ sūryo nabhaso madhyam āsthitaḥ</li>
        <li id="R_7.031.025ef">mām āsīnaṃ viditveha candrāyāti divākaraḥ</li>
    </ul>
    <ul id="R_7.031.026">
        <li id="R_7.031.026ab">narmadā jalaśītaś ca sugandhiḥ śramanāśanaḥ</li>
        <li id="R_7.031.026cd">madbhayād anilo hy eṣa vāty asau susamāhitaḥ</li>
    </ul>
    <ul id="R_7.031.027">
        <li id="R_7.031.027ab">iyaṃ cāpi saricchreṣṭhā narmadā narma vardhinī</li>
        <li id="R_7.031.027cd">līnamīnavihaṃgormiḥ sabhayevāṅganā sthitā</li>
    </ul>
    <ul id="R_7.031.028">
        <li id="R_7.031.028ab">tad bhavantaḥ kṣatāḥ śastrair nṛpair indrasamair yudhi</li>
        <li id="R_7.031.028cd">candanasya raseneva rudhireṇa samukṣitāḥ</li>
    </ul>
    <ul id="R_7.031.029">
        <li id="R_7.031.029ab">te yūyam avagāhadhvaṃ narmadāṃ śarmadāṃ nṛṇām</li>
        <li id="R_7.031.029cd">mahāpadmamukhā mattā gaṅgām iva mahāgajāḥ</li>
    </ul>
    <ul id="R_7.031.030">
        <li id="R_7.031.030ab">asyāṃ snātvā mahānadyāṃ pāpmānaṃ vipramokṣyatha</li>
    </ul>
    <ul id="R_7.031.031">
        <li id="R_7.031.031ab">aham apy atra puline śaradindusamaprabhe</li>
        <li id="R_7.031.031cd">puṣpopaharaṃ śanakaiḥ kariṣyāmi umāpateḥ</li>
    </ul>
    <ul id="R_7.031.032">
        <li id="R_7.031.032ab">rāvaṇenaivam uktās tu mārīcaśukasāraṇāḥ</li>
        <li id="R_7.031.032cd">samahodaradhūmrākṣā narmadām avagāhire</li>
    </ul>
    <ul id="R_7.031.033">
        <li id="R_7.031.033ab">rākṣasendragajais tais tu kṣobhyate narmadā nadī</li>
        <li id="R_7.031.033cd">vāmanāñjanapadmādyair gaṅgā iva mahāgajaiḥ</li>
    </ul>
    <ul id="R_7.031.034">
        <li id="R_7.031.034ab">tatas te rākṣasāḥ snātvā narmadāyā varāmbhasi</li>
        <li id="R_7.031.034cd">uttīrya puṣpāṇy ājahrur balyarthaṃ rāvaṇasya tu</li>
    </ul>
    <ul id="R_7.031.035">
        <li id="R_7.031.035ab">narmadā puline ramye śubhrābhrasadṛśaprabhe</li>
        <li id="R_7.031.035cd">rākṣasendrair muhūrtena kṛtaḥ puṣpamayo giriḥ</li>
    </ul>
    <ul id="R_7.031.036">
        <li id="R_7.031.036ab">puṣpeṣūpahṛteṣv eva rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_7.031.036cd">avatīrṇo nadīṃ snātuṃ gaṅgām iva mahāgajaḥ</li>
    </ul>
    <ul id="R_7.031.037">
        <li id="R_7.031.037ab">tatra snātvā ca vidhivaj japtvā japyam anuttamam</li>
        <li id="R_7.031.037cd">narmadā salilāt tasmād uttatāra sa rāvaṇaḥ</li>
    </ul>
    <ul id="R_7.031.038">
        <li id="R_7.031.038ab">rāvaṇaṃ prāñjaliṃ yāntam anvayuḥ saptarākṣasāḥ</li>
        <li id="R_7.031.038cd">yatra yatra sa yāti sma rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_7.031.038ef">jāmbūnadamayaṃ liṅgaṃ tatra tatra sma nīyate</li>
    </ul>
    <ul id="R_7.031.039">
        <li id="R_7.031.039ab">vālukavedimadhye tu tal liṅgaṃ sthāpya rāvaṇaḥ</li>
        <li id="R_7.031.039cd">arcayām āsa gandhaiś ca puṣpaiś cāmṛtagandhibhiḥ</li>
    </ul>
    <ul id="R_7.031.040">
        <li id="R_7.031.040ab">
            <seg type="pāda" n="a">tataḥ satām ārtiharaṃ haraṃ paraṃ</seg>
            <seg type="pāda" n="b">varapradaṃ candramayūkhabhūṣaṇam</seg>
        </li>
        <li id="R_7.031.040cd">
            <seg type="pāda" n="c">samarcayitvā sa niśācaro jagau</seg>
            <seg type="pāda" n="d">prasārya hastān praṇanarta cāyatān</seg>
        </li>
    </ul>
    <ul id="R_7.032.001">
        <li id="R_7.032.001ab">narmadā puline yatra rākṣasendraḥ sa rāvaṇaḥ</li>
        <li id="R_7.032.001cd">puṣpopahāraṃ kurute tasmād deśād adūrataḥ</li>
    </ul>
    <ul id="R_7.032.002">
        <li id="R_7.032.002ab">arjuno jayatāṃ śreṣṭho māhiṣmatyāḥ patiḥ prabhuḥ</li>
        <li id="R_7.032.002cd">krīḍite saha nārībhir narmadātoyam āśritaḥ</li>
    </ul>
    <ul id="R_7.032.003">
        <li id="R_7.032.003ab">tāsāṃ madhyagato rāja rarāja sa tato 'rjunaḥ</li>
        <li id="R_7.032.003cd">kareṇūnāṃ sahasrasya madhyastha iva kuñjaraḥ</li>
    </ul>
    <ul id="R_7.032.004">
        <li id="R_7.032.004ab">jijñāsuḥ sa tu bāhūnāṃ sahasrasyottamaṃ balam</li>
        <li id="R_7.032.004cd">rurodha narmadā vegaṃ bāhubhiḥ sa tadārjunaḥ</li>
    </ul>
    <ul id="R_7.032.005">
        <li id="R_7.032.005ab">kārtavīryabhujāsetuṃ taj jalaṃ prāpya nirmalam</li>
        <li id="R_7.032.005cd">kūlāpahāraṃ kurvāṇaṃ pratisrotaḥ pradhāvati</li>
    </ul>
    <ul id="R_7.032.006">
        <li id="R_7.032.006ab">samīnanakramakaraḥ sapuṣpakuśasaṃstaraḥ</li>
        <li id="R_7.032.006cd">sa narmadāmbhaso vegaḥ prāvṛṭkāla ivābabhau</li>
    </ul>
    <ul id="R_7.032.007">
        <li id="R_7.032.007ab">sa vegaḥ kārtavīryeṇa saṃpreṣiṭa ivāmbhasaḥ</li>
        <li id="R_7.032.007cd">puṣpopahāraṃ tat sarvaṃ rāvaṇasya jahāra ha</li>
    </ul>
    <ul id="R_7.032.008">
        <li id="R_7.032.008ab">rāvaṇo 'rdhasamāptaṃ tu utsṛjya niyamaṃ tadā</li>
        <li id="R_7.032.008cd">narmadāṃ paśyate kāntāṃ pratikūlāṃ yathā priyām</li>
    </ul>
    <ul id="R_7.032.009">
        <li id="R_7.032.009ab">paścimena tu taṃ dṛṣṭvā sāgarodgārasaṃnibham</li>
        <li id="R_7.032.009cd">vardhantam ambhaso vegaṃ pūrvām āśāṃ praviśya tu</li>
    </ul>
    <ul id="R_7.032.010">
        <li id="R_7.032.010ab">tato 'nudbhrāntaśakunāṃ svābhāvye parame sthitām</li>
        <li id="R_7.032.010cd">nirvikārāṅganābhāsāṃ paśyate rāvaṇo nadīm</li>
    </ul>
    <ul id="R_7.032.011">
        <li id="R_7.032.011ab">savyetarakarāṅgulyā saśabdaṃ ca daśānanaḥ</li>
        <li id="R_7.032.011cd">vegaprabhavam anveṣṭuṃ so 'diśac chukasāraṇau</li>
    </ul>
    <ul id="R_7.032.012">
        <li id="R_7.032.012ab">tau tu rāvaṇasaṃdiṣṭau bhrātarau śukasāraṇau</li>
        <li id="R_7.032.012cd">vyomāntaracarau vīrau prasthitau paścimonmukhau</li>
    </ul>
    <ul id="R_7.032.013">
        <li id="R_7.032.013ab">ardhayojanamātraṃ tu gatvā tau tu niśācarau</li>
        <li id="R_7.032.013cd">paśyetāṃ puruṣaṃ toye krīḍantaṃ sahayoṣitam</li>
    </ul>
    <ul id="R_7.032.014">
        <li id="R_7.032.014ab">bṛhatsālapratīkaśaṃ toyavyākulamūrdhajam</li>
        <li id="R_7.032.014cd">madaraktāntanayanaṃ madanākāravarcasaṃ</li>
    </ul>
    <ul id="R_7.032.015">
        <li id="R_7.032.015ab">nadīṃ bāhusahasreṇa rundhantam arimardanam</li>
        <li id="R_7.032.015cd">giriṃ pādasahasreṇa rundhantam iva medinīm</li>
    </ul>
    <ul id="R_7.032.016">
        <li id="R_7.032.016ab">bālānāṃ varanārīṇāṃ sahasreṇābhisaṃvṛtam</li>
        <li id="R_7.032.016cd">samadānāṃ kareṇūnāṃ sahasreṇeva kuñjaram</li>
    </ul>
    <ul id="R_7.032.017">
        <li id="R_7.032.017ab">tam adbhutatamaṃ dṛṣṭvā rākṣasau śukasāraṇau</li>
        <li id="R_7.032.017cd">saṃnivṛttāv upāgamya rāvaṇaṃ tam athocatuḥ</li>
    </ul>
    <ul id="R_7.032.018">
        <li id="R_7.032.018ab">bṛhatsālapratīkāśaḥ ko 'py asau rākṣaseśvara</li>
        <li id="R_7.032.018cd">narmadāṃ rodhavad ruddhvā krīḍāpayati yoṣitaḥ</li>
    </ul>
    <ul id="R_7.032.019">
        <li id="R_7.032.019ab">tena bāhusahasreṇa saṃniruddhajalā nadī</li>
        <li id="R_7.032.019cd">sāgarodgārasaṃkāśān udgārān sṛjate muhuḥ</li>
    </ul>
    <ul id="R_7.032.020">
        <li id="R_7.032.020ab">ity evaṃ bhāṣamāṇau tau niśamya śukasāraṇau</li>
        <li id="R_7.032.020cd">rāvaṇo 'rjuna ity uktvā uttasthau yuddhalālasaḥ</li>
    </ul>
    <ul id="R_7.032.021">
        <li id="R_7.032.021ab">arjunābhimukhe tasmin prasthite rākṣaseśvare</li>
        <li id="R_7.032.021cd">sakṛd eva kṛto rāvaḥ saraktaḥ preṣito ghanaiḥ</li>
    </ul>
    <ul id="R_7.032.022">
        <li id="R_7.032.022ab">mahodaramahāpārśvadhūmrākṣaśukasāraṇaiḥ</li>
        <li id="R_7.032.022cd">saṃvṛto rākṣasendras tu tatrāgād yatra so 'rjunaḥ</li>
    </ul>
    <ul id="R_7.032.023">
        <li id="R_7.032.023ab">nātidīrgheṇa kālena sa tato rākṣaso balī</li>
        <li id="R_7.032.023cd">taṃ narmadā hradaṃ bhīmam ājagāmāñjanaprabhaḥ</li>
    </ul>
    <ul id="R_7.032.024">
        <li id="R_7.032.024ab">sa tatra strīparivṛtaṃ vāśitābhir iva dvipam</li>
        <li id="R_7.032.024cd">narendraṃ paśyate rājā rākṣasānāṃ tadārjunam</li>
    </ul>
    <ul id="R_7.032.025">
        <li id="R_7.032.025ab">sa roṣād raktanayano rākṣasendro baloddhataḥ</li>
        <li id="R_7.032.025cd">ity evam arjunāmātyān āha gambhīrayā girā</li>
    </ul>
    <ul id="R_7.032.026">
        <li id="R_7.032.026ab">amātyāḥ kṣipram ākhyāta haihayasya nṛpasya vai</li>
        <li id="R_7.032.026cd">yuddhārthaṃ samanuprāpto rāvaṇo nāma nāmataḥ</li>
    </ul>
    <ul id="R_7.032.027">
        <li id="R_7.032.027ab">rāvaṇasya vacaḥ śrutvā mantriṇo 'thārjunasya te</li>
        <li id="R_7.032.027cd">uttasthuḥ sāyudhās taṃ ca rāvaṇaṃ vākyam abruvan</li>
    </ul>
    <ul id="R_7.032.028">
        <li id="R_7.032.028ab">yuddhasya kālo vijñātaḥ sādhu bhoḥ sādhu rāvaṇa</li>
        <li id="R_7.032.028cd">yaḥ kṣībaṃ strīvṛtaṃ caiva yoddhum icchasi no nṛpam</li>
        <li id="R_7.032.028ef">vāśitāmadhyagaṃ mattaṃ śārdūla iva kuñjaram</li>
    </ul>
    <ul id="R_7.032.029">
        <li id="R_7.032.029ab">kṣamasvādya daśagrīva uṣyatāṃ rajanī tvayā</li>
        <li id="R_7.032.029cd">yuddhaśraddhā tu yady asti śvas tāta samare 'rjunam</li>
    </ul>
    <ul id="R_7.032.030">
        <li id="R_7.032.030ab">yadi vāpi tvarā tubhyaṃ yuddhatṛṣṇāsamāvṛtā</li>
        <li id="R_7.032.030cd">nihatyāsmāṃs tato yuddham arjunenopayāsyasi</li>
    </ul>
    <ul id="R_7.032.031">
        <li id="R_7.032.031ab">tatas te rāvaṇāmātyair amātyāḥ pārthivasya tu</li>
        <li id="R_7.032.031cd">sūditāś cāpi te yuddhe bhakṣitāś ca bubhukṣitaiḥ</li>
    </ul>
    <ul id="R_7.032.032">
        <li id="R_7.032.032ab">tato halahalāśabdo narmadā tira ābabhau</li>
        <li id="R_7.032.032cd">arjunasyānuyātrāṇāṃ rāvaṇasya ca mantriṇām</li>
    </ul>
    <ul id="R_7.032.033">
        <li id="R_7.032.033ab">iṣubhis tomaraiḥ śūlair vajrakalpaiḥ sakarṣaṇaiḥ</li>
        <li id="R_7.032.033cd">sarāvaṇān ardayantaḥ samantāt samabhidrutāḥ</li>
    </ul>
    <ul id="R_7.032.034">
        <li id="R_7.032.034ab">haihayādhipayodhānāṃ vega āsīt sudāruṇaḥ</li>
        <li id="R_7.032.034cd">sanakramīnamakarasamudrasyeva nisvanaḥ</li>
    </ul>
    <ul id="R_7.032.035">
        <li id="R_7.032.035ab">rāvaṇasya tu te 'mātyāḥ prahastaśukasāraṇāḥ</li>
        <li id="R_7.032.035cd">kārtavīryabalaṃ kruddhā nirdahanty agnitejasaḥ</li>
    </ul>
    <ul id="R_7.032.036">
        <li id="R_7.032.036ab">arjunāya tu tat karma rāvaṇasya samantriṇaḥ</li>
        <li id="R_7.032.036cd">krīḍamānāya kathitaṃ puruṣair dvārarakṣibhiḥ</li>
    </ul>
    <ul id="R_7.032.037">
        <li id="R_7.032.037ab">uktvā na bhetavyam iti strījanaṃ sa tato 'rjunaḥ</li>
        <li id="R_7.032.037cd">uttatāra jalāt tasmād gaṅgātoyād ivāñjanaḥ</li>
    </ul>
    <ul id="R_7.032.038">
        <li id="R_7.032.038ab">krodhadūṣitanetras tu sa tato 'rjuna pāvakaḥ</li>
        <li id="R_7.032.038cd">prajajvāla mahāghoro yugānta iva pāvakaḥ</li>
    </ul>
    <ul id="R_7.032.039">
        <li id="R_7.032.039ab">sa tūrṇataram ādāya varahemāṅgado gadām</li>
        <li id="R_7.032.039cd">abhidravati rakṣāṃsi tamāṃsīva divākaraḥ</li>
    </ul>
    <ul id="R_7.032.040">
        <li id="R_7.032.040ab">bāhuvikṣepakaraṇāṃ samudyamya mahāgadām</li>
        <li id="R_7.032.040cd">gāruḍaṃ vegam āsthāya āpapātaiva so 'rjunaḥ</li>
    </ul>
    <ul id="R_7.032.041">
        <li id="R_7.032.041ab">tasya margaṃ samāvṛtya vindhyo 'rkasyeva parvataḥ</li>
        <li id="R_7.032.041cd">sthito vindhya ivākampyaḥ prahasto musalāyudhaḥ</li>
    </ul>
    <ul id="R_7.032.042">
        <li id="R_7.032.042ab">tato 'sya musalaṃ ghoraṃ lohabaddhaṃ madoddhataḥ</li>
        <li id="R_7.032.042cd">prahastaḥ preṣayan kruddho rarāsa ca yathāmbudaḥ</li>
    </ul>
    <ul id="R_7.032.043">
        <li id="R_7.032.043ab">tasyāgre musalasyāgnir aśokāpīḍasaṃnibhaḥ</li>
        <li id="R_7.032.043cd">prahastakaramuktasya babhūva pradahann iva</li>
    </ul>
    <ul id="R_7.032.044">
        <li id="R_7.032.044ab">ādhāvamānaṃ musalaṃ kārtavīryas tadārjunaḥ</li>
        <li id="R_7.032.044cd">nipuṇaṃ vañcayām āsa sagado gajavikramaḥ</li>
    </ul>
    <ul id="R_7.032.045">
        <li id="R_7.032.045ab">tatas tam abhidudrāva prahastaṃ haihayādhipaḥ</li>
        <li id="R_7.032.045cd">bhrāmayāṇo gadāṃ gurvīṃ pañcabāhuśatocchrayām</li>
    </ul>
    <ul id="R_7.032.046">
        <li id="R_7.032.046ab">tenāhato 'tivegena prahasto gadayā tadā</li>
        <li id="R_7.032.046cd">nipapāta sthitaḥ śailo vajrivajrahato yathā</li>
    </ul>
    <ul id="R_7.032.047">
        <li id="R_7.032.047ab">prahastaṃ patitaṃ dṛṣṭvā mārīcaśukasāraṇāḥ</li>
        <li id="R_7.032.047cd">samahodaradhūmrākṣā apasṛptā raṇājirāt</li>
    </ul>
    <ul id="R_7.032.048">
        <li id="R_7.032.048ab">apakrānteṣv amātyeṣu prahaste ca nipātite</li>
        <li id="R_7.032.048cd">rāvaṇo 'bhyadravat tūrṇam arjunaṃ nṛpasattamam</li>
    </ul>
    <ul id="R_7.032.049">
        <li id="R_7.032.049ab">sahasrabāhos tad yuddhaṃ viṃśadbāhoś ca dāruṇam</li>
        <li id="R_7.032.049cd">nṛparākṣasayos tatra ārabdhaṃ lomaharṣaṇam</li>
    </ul>
    <ul id="R_7.032.050">
        <li id="R_7.032.050ab">sāgarāv iva saṃkṣubdhau calamūlāv ivācalau</li>
        <li id="R_7.032.050cd">tejoyuktāv ivādityau pradahantāv ivānalau</li>
    </ul>
    <ul id="R_7.032.051">
        <li id="R_7.032.051ab">baloddhatau yathā nāgau vāśitārthe yathā vṛṣau</li>
        <li id="R_7.032.051cd">meghāv iva vinardantau siṃhāv iva balotkaṭau</li>
    </ul>
    <ul id="R_7.032.052">
        <li id="R_7.032.052ab">rudrakālāv iva kruddhau tau tathā rākṣasārjunau</li>
        <li id="R_7.032.052cd">parasparaṃ gadābhyāṃ tau tāḍayām āsatur bhṛśam</li>
    </ul>
    <ul id="R_7.032.053">
        <li id="R_7.032.053ab">vajraprahārān acalā yathā ghorān viṣehire</li>
        <li id="R_7.032.053cd">gadāprahārāṃs tadvat tau sahete nararākṣasau</li>
    </ul>
    <ul id="R_7.032.054">
        <li id="R_7.032.054ab">yathāśaniravebhyas tu jāyate vai pratiśrutiḥ</li>
        <li id="R_7.032.054cd">tathā tābhyāṃ gadāpātair diśaḥ sarvāḥ pratiśrutāḥ</li>
    </ul>
    <ul id="R_7.032.055">
        <li id="R_7.032.055ab">arjunasya gadā sā tu pātyamānāhitorasi</li>
        <li id="R_7.032.055cd">kāñcanābhaṃ nabhaś cakre vidyutsaudāmanī yathā</li>
    </ul>
    <ul id="R_7.032.056">
        <li id="R_7.032.056ab">tathaiva rāvaṇenāpi pātyamānā muhur muhuḥ</li>
        <li id="R_7.032.056cd">arjunorasi nirbhāti gadolkeva mahāgirau</li>
    </ul>
    <ul id="R_7.032.057">
        <li id="R_7.032.057ab">nārjunaḥ khedam āpnoti na rākṣasagaṇeśvaraḥ</li>
        <li id="R_7.032.057cd">samam āsīt tayor yuddhaṃ yathā pūrvaṃ balīndrayoḥ</li>
    </ul>
    <ul id="R_7.032.058">
        <li id="R_7.032.058ab">śṛṅgair maharṣabhau yadvad dantāgrair iva kuñjarau</li>
        <li id="R_7.032.058cd">parasparaṃ vinighnantau nararākṣasasattamau</li>
    </ul>
    <ul id="R_7.032.059">
        <li id="R_7.032.059ab">tato 'rjunena kruddhena sarvaprāṇena sā gadā</li>
        <li id="R_7.032.059cd">stanayor antare muktā rāvaṇasya mahāhave</li>
    </ul>
    <ul id="R_7.032.060">
        <li id="R_7.032.060ab">varadānakṛtatrāṇe sā gadā rāvaṇorasi</li>
        <li id="R_7.032.060cd">durbaleva yathā senā dvidhābhūtāpatat kṣitau</li>
    </ul>
    <ul id="R_7.032.061">
        <li id="R_7.032.061ab">sa tv arjunapramuktena gadāpātena rāvaṇaḥ</li>
        <li id="R_7.032.061cd">apāsarpad dhanurmātraṃ niṣasāda ca niṣṭanan</li>
    </ul>
    <ul id="R_7.032.062">
        <li id="R_7.032.062ab">sa vihvalaṃ tad ālakṣya daśagrīvaṃ tato 'rjunaḥ</li>
        <li id="R_7.032.062cd">sahasā pratijagrāha garutmān iva pannagam</li>
    </ul>
    <ul id="R_7.032.063">
        <li id="R_7.032.063ab">sa taṃ bāhusahasreṇa balād gṛhya daśānanam</li>
        <li id="R_7.032.063cd">babandha balavān rājā baliṃ nārāyaṇo yathā</li>
    </ul>
    <ul id="R_7.032.064">
        <li id="R_7.032.064ab">badhyamāne daśagrīve siddhacāraṇadevatāḥ</li>
        <li id="R_7.032.064cd">sādhvīti vādinaḥ puṣpaiḥ kiranty arjunamūrdhani</li>
    </ul>
    <ul id="R_7.032.065">
        <li id="R_7.032.065ab">vyāghro mṛgam ivādāya siṃharāḍ iva dantinam</li>
        <li id="R_7.032.065cd">rarāsa haihayo rājā harṣād ambudavan muhuḥ</li>
    </ul>
    <ul id="R_7.032.066">
        <li id="R_7.032.066ab">prahastas tu samāśvasto dṛṣṭvā baddhaṃ daśānanam</li>
        <li id="R_7.032.066cd">saha tai rākasaiḥ kruddha abhidudrāva pārthivam</li>
    </ul>
    <ul id="R_7.032.067">
        <li id="R_7.032.067ab">naktaṃcarāṇāṃ vegas tu teṣām āpatatāṃ babhau</li>
        <li id="R_7.032.067cd">uddhṛta ātapāpāye samudrāṇām ivādbhutaḥ</li>
    </ul>
    <ul id="R_7.032.068">
        <li id="R_7.032.068ab">muñca muñceti bhāṣantas tiṣṭha tiṣṭheti cāsakṛt</li>
        <li id="R_7.032.068cd">musalāni ca śūlāni utsasarjus tadārjune</li>
    </ul>
    <ul id="R_7.032.069">
        <li id="R_7.032.069ab">aprāptāny eva tāny āśu asaṃbhrāntas tadārjunaḥ</li>
        <li id="R_7.032.069cd">āyudhāny amarārīṇāṃ jagrāha ripusūdanaḥ</li>
    </ul>
    <ul id="R_7.032.070">
        <li id="R_7.032.070ab">tatas tair eva rakṣāṃsi durdharaiḥ pravarāyudhaiḥ</li>
        <li id="R_7.032.070cd">bhittvā vidrāvayām āsa vāyur ambudharān iva</li>
    </ul>
    <ul id="R_7.032.071">
        <li id="R_7.032.071ab">rākṣasāṃs trāsayitvā tu kārtavīryārjunas tadā</li>
        <li id="R_7.032.071cd">rāvaṇaṃ gṛhya nagaraṃ praviveśa suhṛdvṛtaḥ</li>
    </ul>
    <ul id="R_7.032.072">
        <li id="R_7.032.072ab">
            <seg type="pāda" n="a">sa kīryamāṇaḥ kusumākṣatotkarair</seg>
            <seg type="pāda" n="b">dvijaiḥ sapauraiḥ puruhūtasaṃnibhaḥ</seg>
        </li>
        <li id="R_7.032.072cd">
            <seg type="pāda" n="c">tadārjunaḥ saṃpraviveśa tāṃ purīṃ</seg>
            <seg type="pāda" n="d">baliṃ nigṛhyaiva sahasralocanaḥ</seg>
        </li>
    </ul>
    <ul id="R_7.033.001">
        <li id="R_7.033.001ab">rāvaṇagrahaṇaṃ tat tu vāyugrahaṇasaṃnibham</li>
        <li id="R_7.033.001cd">ṛṣiḥ pulastyaḥ śuśrāva kathitaṃ divi daivataiḥ</li>
    </ul>
    <ul id="R_7.033.002">
        <li id="R_7.033.002ab">tataḥ putrasutasnehāt kampyamāno mahādhṛtiḥ</li>
        <li id="R_7.033.002cd">māhiṣmatīpatiṃ draṣṭum ājagāma mahān ṛṣiḥ</li>
    </ul>
    <ul id="R_7.033.003">
        <li id="R_7.033.003ab">sa vāyumārgam āsthāya vāyutulyagatir dvijaḥ</li>
        <li id="R_7.033.003cd">purīṃ māhiṣmatīṃ prāpto manaḥsaṃtāpavikramaḥ</li>
    </ul>
    <ul id="R_7.033.004">
        <li id="R_7.033.004ab">so 'marāvatisaṃkāśāṃ hṛṣṭapuṣṭajanāvṛtām</li>
        <li id="R_7.033.004cd">praviveśa purīṃ brahmā indrasyevāmarāvatīm</li>
    </ul>
    <ul id="R_7.033.005">
        <li id="R_7.033.005ab">pādacāram ivādityaṃ niṣpatantaṃ sudurdṛśam</li>
        <li id="R_7.033.005cd">tatas te pratyabhijñāya arjunāya nyavedayan</li>
    </ul>
    <ul id="R_7.033.006">
        <li id="R_7.033.006ab">pulastya iti taṃ śrutvā vacanaṃ haihayādhipaḥ</li>
        <li id="R_7.033.006cd">śirasy añjalim uddhṛtya pratyudgacchad dvijottamam</li>
    </ul>
    <ul id="R_7.033.007">
        <li id="R_7.033.007ab">purohito 'sya gṛhyārghyaṃ madhuparkaṃ tathaiva ca</li>
        <li id="R_7.033.007cd">purastāt prayayau rājña indrasyeva bṛhaspatiḥ</li>
    </ul>
    <ul id="R_7.033.008">
        <li id="R_7.033.008ab">tatas tam ṛṣim āyāntam udyantam iva bhāskaram</li>
        <li id="R_7.033.008cd">arjuno dṛśya saṃprāptaṃ vavandendra iveśvaram</li>
    </ul>
    <ul id="R_7.033.009">
        <li id="R_7.033.009ab">sa tasya madhuparkaṃ ca pādyam arghyaṃ ca dāpayan</li>
        <li id="R_7.033.009cd">pulastyam āha rājendro harṣagadgadayā girā</li>
    </ul>
    <ul id="R_7.033.010">
        <li id="R_7.033.010ab">adyeyam amarāvatyā tulyā māhiṣmatī kṛtā</li>
        <li id="R_7.033.010cd">adyāhaṃ tu dvijendrendra yasmāt paśyāmi durdṛśam</li>
    </ul>
    <ul id="R_7.033.011">
        <li id="R_7.033.011ab">adya me kuśalaṃ deva adya me kulam uddhṛtam</li>
        <li id="R_7.033.011cd">yat te devagaṇair vandyau vande 'haṃ caraṇāv imau</li>
    </ul>
    <ul id="R_7.033.012">
        <li id="R_7.033.012ab">idaṃ rājyam ime putrā ime dārā ime vayam</li>
        <li id="R_7.033.012cd">brahman kiṃ kurma kiṃ kāryam ājñāpayatu no bhavān</li>
    </ul>
    <ul id="R_7.033.013">
        <li id="R_7.033.013ab">taṃ dharme 'gniṣu bhṛtyeṣu śivaṃ pṛṣṭvātha pārthivam</li>
        <li id="R_7.033.013cd">pulastyovāca rājānaṃ haihayānāṃ tadārjunam</li>
    </ul>
    <ul id="R_7.033.014">
        <li id="R_7.033.014ab">rājendrāmalapadmākṣapūrṇacandranibhānana</li>
        <li id="R_7.033.014cd">atulaṃ te balaṃ yena daśagrīvas tvayā jitaḥ</li>
    </ul>
    <ul id="R_7.033.015">
        <li id="R_7.033.015ab">bhayād yasyāvatiṣṭhetāṃ niṣpandau sāgarānilau</li>
        <li id="R_7.033.015cd">so 'yam adya tvayā baddhaḥ pautro me 'tīvadurjayaḥ</li>
    </ul>
    <ul id="R_7.033.016">
        <li id="R_7.033.016ab">tat putraka yaśaḥ sphītaṃ nāma viśrāvitaṃ tvayā</li>
        <li id="R_7.033.016cd">madvākyād yācyamāno 'dya muñca vatsa daśānanam</li>
    </ul>
    <ul id="R_7.033.017">
        <li id="R_7.033.017ab">pulastyājñāṃ sa gṛhyātha akiṃcanavaco 'rjunaḥ</li>
        <li id="R_7.033.017cd">mumoca pārthivendrendro rākṣasendraṃ prahṛṣṭavat</li>
    </ul>
    <ul id="R_7.033.018">
        <li id="R_7.033.018ab">
            <seg type="pāda" n="a">sa taṃ pramuktvā tridaśārim arjunaḥ</seg>
            <seg type="pāda" n="b">prapūjya divyābharaṇasragambaraiḥ</seg>
        </li>
        <li id="R_7.033.018cd">
            <seg type="pāda" n="c">ahiṃsākaṃ sakhyam upetya sāgnikaṃ</seg>
            <seg type="pāda" n="d">praṇamya sa brahmasutaṃ gṛhaṃ yayau</seg>
        </li>
    </ul>
    <ul id="R_7.033.019">
        <li id="R_7.033.019ab">pulastyenāpi saṃgamya rākṣasendraḥ pratāpavān</li>
        <li id="R_7.033.019cd">pariṣvaṅgakṛtātithyo lajjamāno visarjitaḥ</li>
    </ul>
    <ul id="R_7.033.020">
        <li id="R_7.033.020ab">pitāmahasutaś cāpi pulastyo munisattamaḥ</li>
        <li id="R_7.033.020cd">mocayitvā daśagrīvaṃ brahmalokaṃ jagāma saḥ</li>
    </ul>
    <ul id="R_7.033.021">
        <li id="R_7.033.021ab">evaṃ sa rāvaṇaḥ prāptaḥ kārtavīryāt tu dharṣaṇāt</li>
        <li id="R_7.033.021cd">pulastyavacanāc cāpi punar mokṣam avāptavān</li>
    </ul>
    <ul id="R_7.033.022">
        <li id="R_7.033.022ab">evaṃ balibhyo balinaḥ santi rāghavanandana</li>
        <li id="R_7.033.022cd">nāvajñā parataḥ kāryā ya icchec chreya ātmanaḥ</li>
    </ul>
    <ul id="R_7.033.023">
        <li id="R_7.033.023ab">
            <seg type="pāda" n="a">tataḥ sa rājā piśitāśanānāṃ</seg>
            <seg type="pāda" n="b">sahasrabāhor upalabhya maitrīm</seg>
        </li>
        <li id="R_7.033.023cd">
            <seg type="pāda" n="c">punar narāṇāṃ kadanaṃ cakāra</seg>
            <seg type="pāda" n="d">cacāra sarvāṃ pṛthivīṃ ca darpāt</seg>
        </li>
    </ul>
    <ul id="R_7.034.001">
        <li id="R_7.034.001ab">arjunena vimuktas tu rāvaṇo rākṣasādhipaḥ</li>
        <li id="R_7.034.001cd">cacāra pṛthivīṃ sarvām anirviṇṇas tathā kṛtaḥ</li>
    </ul>
    <ul id="R_7.034.002">
        <li id="R_7.034.002ab">rākṣasaṃ vā manuṣyaṃ vā śṛṇute yaṃ balādhikam</li>
        <li id="R_7.034.002cd">rāvaṇas taṃ samāsādya yuddhe hvayati darpitaḥ</li>
    </ul>
    <ul id="R_7.034.003">
        <li id="R_7.034.003ab">tataḥ kadā cit kiṣkindhāṃ nagarīṃ vālipālitām</li>
        <li id="R_7.034.003cd">gatvāhvayati yuddhāya vālinaṃ hemamālinam</li>
    </ul>
    <ul id="R_7.034.004">
        <li id="R_7.034.004ab">tatas taṃ vānarāmātyas tāras tārāpitā prabhuḥ</li>
        <li id="R_7.034.004cd">uvāca rāvaṇaṃ vākyaṃ yuddhaprepsum upāgatam</li>
    </ul>
    <ul id="R_7.034.005">
        <li id="R_7.034.005ab">rākṣasendra gato vālī yas te pratibalo bhavet</li>
        <li id="R_7.034.005cd">nānyaḥ pramukhataḥ sthātuṃ tava śaktaḥ plavaṃgamaḥ</li>
    </ul>
    <ul id="R_7.034.006">
        <li id="R_7.034.006ab">caturbhyo 'pi samudrebhyaḥ saṃdhyām anvāsya rāvaṇa</li>
        <li id="R_7.034.006cd">imaṃ muhūrtam āyāti vālī tiṣṭha muhūrtakam</li>
    </ul>
    <ul id="R_7.034.007">
        <li id="R_7.034.007ab">etān asthicayān paśya ya ete śaṅkhapāṇḍurāḥ</li>
        <li id="R_7.034.007cd">yuddhārthinām ime rājan vānarādhipatejasā</li>
    </ul>
    <ul id="R_7.034.008">
        <li id="R_7.034.008ab">yad vāmṛtarasaḥ pītas tvayā rāvaṇarākṣasa</li>
        <li id="R_7.034.008cd">tathā vālinam āsādya tadantaṃ tava jīvitam</li>
    </ul>
    <ul id="R_7.034.009">
        <li id="R_7.034.009ab">atha vā tvarase martuṃ gaccha dakṣiṇasāgaram</li>
        <li id="R_7.034.009cd">vālinaṃ drakṣyase tatra bhūmiṣṭham iva bhāskaram</li>
    </ul>
    <ul id="R_7.034.010">
        <li id="R_7.034.010ab">sa tu tāraṃ vinirbhartsya rāvaṇo rākṣaseśvaraḥ</li>
        <li id="R_7.034.010cd">puṣpakaṃ tat samāruhya prayayau dakṣiṇārṇavam</li>
    </ul>
    <ul id="R_7.034.011">
        <li id="R_7.034.011ab">tatra hemagiriprakhyaṃ taruṇārkanibhānanam</li>
        <li id="R_7.034.011cd">rāvaṇo vālinaṃ dṛṣṭvā saṃdhyopāsanatatparam</li>
    </ul>
    <ul id="R_7.034.012">
        <li id="R_7.034.012ab">puṣpakād avaruhyātha rāvaṇo 'ñjanasaṃnibhaḥ</li>
        <li id="R_7.034.012cd">grahītuṃ vālinaṃ tūrṇaṃ niḥśabdapadam ādravat</li>
    </ul>
    <ul id="R_7.034.013">
        <li id="R_7.034.013ab">yadṛcchayonmīlayatā vālināpi sa rāvaṇaḥ</li>
        <li id="R_7.034.013cd">pāpābhiprāyavān dṛṣṭaś cakāra na ca saṃbhramam</li>
    </ul>
    <ul id="R_7.034.014">
        <li id="R_7.034.014ab">śaśam ālakṣya siṃho vā pannagaṃ garuḍo yathā</li>
        <li id="R_7.034.014cd">na cintayati taṃ vālī rāvaṇaṃ pāpaniścayam</li>
    </ul>
    <ul id="R_7.034.015">
        <li id="R_7.034.015ab">jighṛkṣamāṇam adyainaṃ rāvaṇaṃ pāpabuddhinam</li>
        <li id="R_7.034.015cd">kakṣāvalambinaṃ kṛtvā gamiṣyāmi mahārṇavān</li>
    </ul>
    <ul id="R_7.034.016">
        <li id="R_7.034.016ab">drakṣyanty ariṃ mamāṅkasthaṃ sraṃsitorukarāmbaram</li>
        <li id="R_7.034.016cd">lambamānaṃ daśagrīvaṃ garuḍasyeva pannagam</li>
    </ul>
    <ul id="R_7.034.017">
        <li id="R_7.034.017ab">ity evaṃ matim āsthāya vālī karṇam upāśritaḥ</li>
        <li id="R_7.034.017cd">japan vai naigamān mantrāṃs tasthau parvatarāḍ iva</li>
    </ul>
    <ul id="R_7.034.018">
        <li id="R_7.034.018ab">tāv anyonyaṃ jighṛkṣantau harirākṣasapārthivau</li>
        <li id="R_7.034.018cd">prayatnavantau tat karma īhatur baladarpitau</li>
    </ul>
    <ul id="R_7.034.019">
        <li id="R_7.034.019ab">hastagrāhyaṃ tu taṃ matvā pādaśabdena rāvaṇam</li>
        <li id="R_7.034.019cd">parāṅmukho 'pi jagrāha vālī sarpam ivāṇḍajaḥ</li>
    </ul>
    <ul id="R_7.034.020">
        <li id="R_7.034.020ab">grahītukāmaṃ taṃ gṛhya rakṣasām īśvaraṃ hariḥ</li>
        <li id="R_7.034.020cd">kham utpapāta vegena kṛtvā kakṣāvalambinam</li>
    </ul>
    <ul id="R_7.034.021">
        <li id="R_7.034.021ab">sa taṃ pīḍdayamānas tu vitudantaṃ nakhair muhuḥ</li>
        <li id="R_7.034.021cd">jahāra rāvaṇaṃ vālī pavanas toyadaṃ yathā</li>
    </ul>
    <ul id="R_7.034.022">
        <li id="R_7.034.022ab">atha te rākṣasāmātyā hriyamāṇe daśānane</li>
        <li id="R_7.034.022cd">mumokṣayiṣavo ghorā ravamāṇā hy abhidravan</li>
    </ul>
    <ul id="R_7.034.023">
        <li id="R_7.034.023ab">anvīyamānas tair vālī bhrājate 'mbaramadhyagaḥ</li>
        <li id="R_7.034.023cd">anvīyamāno meghaughair ambarastha ivāṃśumān</li>
    </ul>
    <ul id="R_7.034.024">
        <li id="R_7.034.024ab">te 'śaknuvantaḥ saṃprāptaṃ vālinaṃ rākṣasottamāḥ</li>
        <li id="R_7.034.024cd">tasya bāhūruvegena pariśrāntaḥ patanti ca</li>
    </ul>
    <ul id="R_7.034.025">
        <li id="R_7.034.025ab">vālimārgād apākrāman parvatendrā hi gacchataḥ</li>
    </ul>
    <ul id="R_7.034.026">
        <li id="R_7.034.026ab">apakṣigaṇasaṃpāto vānarendro mahājavaḥ</li>
        <li id="R_7.034.026cd">kramaśaḥ sāgarān sarvān saṃdhyākālam avandata</li>
    </ul>
    <ul id="R_7.034.027">
        <li id="R_7.034.027ab">sabhājyamāno bhūtais tu khecaraiḥ khecaro hariḥ</li>
        <li id="R_7.034.027cd">paścimaṃ sāgaraṃ vālī ājagāma sarāvaṇaḥ</li>
    </ul>
    <ul id="R_7.034.028">
        <li id="R_7.034.028ab">tatra saṃdhyām upāsitvā snātvā japtvā ca vānaraḥ</li>
        <li id="R_7.034.028cd">uttaraṃ sāgaraṃ prāyād vahamāno daśānanam</li>
    </ul>
    <ul id="R_7.034.029">
        <li id="R_7.034.029ab">uttare sāgare saṃdhyām upāsitvā daśānanam</li>
        <li id="R_7.034.029cd">vahamāno 'gamad vālī pūrvam ambumahānidhim</li>
    </ul>
    <ul id="R_7.034.030">
        <li id="R_7.034.030ab">tatrāpi saṃdhyām anvāsya vāsaviḥ sa harīśvaraḥ</li>
        <li id="R_7.034.030cd">kiṣkindhābhimukho gṛhya rāvaṇaṃ punar āgamat</li>
    </ul>
    <ul id="R_7.034.031">
        <li id="R_7.034.031ab">caturṣv api samudreṣu saṃdhyām anvāsya vānaraḥ</li>
        <li id="R_7.034.031cd">rāvaṇodvahanaśrāntaḥ kiṣkindhopavane 'patat</li>
    </ul>
    <ul id="R_7.034.032">
        <li id="R_7.034.032ab">rāvaṇaṃ tu mumocātha svakakṣāt kapisattamaḥ</li>
        <li id="R_7.034.032cd">kutas tvam iti covāca prahasan rāvaṇaṃ prati</li>
    </ul>
    <ul id="R_7.034.033">
        <li id="R_7.034.033ab">vismayaṃ tu mahad gatvā śramalokanirīkṣaṇaḥ</li>
        <li id="R_7.034.033cd">rākṣaseśo harīśaṃ tam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.034.034">
        <li id="R_7.034.034ab">vānarendra mahendrābha rākṣasendro 'smi rāvaṇaḥ</li>
        <li id="R_7.034.034cd">yuddhepsur ahaṃ saṃprāptaḥ sa cādyāsāditas tvayā</li>
    </ul>
    <ul id="R_7.034.035">
        <li id="R_7.034.035ab">aho balam aho vīryam aho gambhīratā ca te</li>
        <li id="R_7.034.035cd">yenāhaṃ paśuvad gṛhya bhrāmitaś caturo 'rṇavān</li>
    </ul>
    <ul id="R_7.034.036">
        <li id="R_7.034.036ab">evam aśrāntavad vīra śīghram eva ca vānara</li>
        <li id="R_7.034.036cd">māṃ caivodvahamānas tu ko 'nyo vīraḥ kramiṣyati</li>
    </ul>
    <ul id="R_7.034.037">
        <li id="R_7.034.037ab">trayāṇām eva bhūtānāṃ gatir eṣā plavaṃgama</li>
        <li id="R_7.034.037cd">mano'nilasuparṇānāṃ tava vā nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_7.034.038">
        <li id="R_7.034.038ab">so 'haṃ dṛṣṭabalas tubhyam icchāmi haripuṃgava</li>
        <li id="R_7.034.038cd">tvayā saha ciraṃ sakhyaṃ susnigdhaṃ pāvakāgrataḥ</li>
    </ul>
    <ul id="R_7.034.039">
        <li id="R_7.034.039ab">dārāḥ putrāḥ puraṃ rāṣṭraṃ bhogācchādanabhojanam</li>
        <li id="R_7.034.039cd">sarvam evāvibhaktaṃ nau bhaviṣyati harīśvara</li>
    </ul>
    <ul id="R_7.034.040">
        <li id="R_7.034.040ab">tataḥ prajvālayitvāgniṃ tāv ubhau harirākṣasau</li>
        <li id="R_7.034.040cd">bhrātṛtvam upasaṃpannau pariṣvajya parasparam</li>
    </ul>
    <ul id="R_7.034.041">
        <li id="R_7.034.041ab">anyonyaṃ lambitakarau tatas tau harirākṣasau</li>
        <li id="R_7.034.041cd">kiṣkindhāṃ viśatur hṛṣṭau siṃhau giriguhām iva</li>
    </ul>
    <ul id="R_7.034.042">
        <li id="R_7.034.042ab">sa tatra māsam uṣitaḥ sugrīva iva rāvaṇaḥ</li>
        <li id="R_7.034.042cd">amātyair āgatair nīcas trailokyotsādanārthibhiḥ</li>
    </ul>
    <ul id="R_7.034.043">
        <li id="R_7.034.043ab">evam etat purāvṛttaṃ vālinā rāvaṇaḥ prabho</li>
        <li id="R_7.034.043cd">dharṣitaś ca kṛtaś cāpi bhrātā pāvakasaṃnidhau</li>
    </ul>
    <ul id="R_7.034.044">
        <li id="R_7.034.044ab">balam apratimaṃ rāma vālino 'bhavad uttamam</li>
        <li id="R_7.034.044cd">so 'pi tayā vinirdagdhaḥ śalabho vahninā yathā</li>
    </ul>
    <ul id="R_7.035.001">
        <li id="R_7.035.001ab">apṛcchata tato rāmo dakṣiṇāśālayaṃ munim</li>
        <li id="R_7.035.001cd">prāñjalir vinayopeta idam āha vaco 'rthavat</li>
    </ul>
    <ul id="R_7.035.002">
        <li id="R_7.035.002ab">atulaṃ balam etābhyāṃ vālino rāvaṇasya ca</li>
        <li id="R_7.035.002cd">na tv etau hanumadvīryaiḥ samāv iti matir mama</li>
    </ul>
    <ul id="R_7.035.003">
        <li id="R_7.035.003ab">śauryaṃ dākṣyaṃ balaṃ dhairyaṃ prājñatā nayasādhanam</li>
        <li id="R_7.035.003cd">vikramaś ca prabhāvaś ca hanūmati kṛtālayāḥ</li>
    </ul>
    <ul id="R_7.035.004">
        <li id="R_7.035.004ab">dṛṣṭvodadhiṃ viṣīdantīṃ tadaiṣa kapivāhinīm</li>
        <li id="R_7.035.004cd">samāśvāsya kapīn bhūyo yojanānāṃ śataṃ plutaḥ</li>
    </ul>
    <ul id="R_7.035.005">
        <li id="R_7.035.005ab">dharṣayitvā purīṃ laṅkāṃ rāvaṇāntaḥpuraṃ tathā</li>
        <li id="R_7.035.005cd">dṛṣṭvā saṃbhāṣitā cāpi sītā viśvāsitā tathā</li>
    </ul>
    <ul id="R_7.035.006">
        <li id="R_7.035.006ab">senāgragā mantrisutāḥ kiṃkarā rāvaṇātmajaḥ</li>
        <li id="R_7.035.006cd">ete hanumatā tatra ekena vinipātitāḥ</li>
    </ul>
    <ul id="R_7.035.007">
        <li id="R_7.035.007ab">bhūyo bandhād vimuktena saṃbhāṣitvā daśānanam</li>
        <li id="R_7.035.007cd">laṅkā bhasmīkṛtā tena pāvakeneva medinī</li>
    </ul>
    <ul id="R_7.035.008">
        <li id="R_7.035.008ab">na kālasya na śakrasya na viṣṇor vittapasya ca</li>
        <li id="R_7.035.008cd">karmāṇi tāni śrūyante yāni yuddhe hanūmataḥ</li>
    </ul>
    <ul id="R_7.035.009">
        <li id="R_7.035.009ab">etasya bāhuvīryeṇa laṅkā sītā ca lakṣmaṇaḥ</li>
        <li id="R_7.035.009cd">prāpto mayā jayaś caiva rājyaṃ mitrāṇi bāndhavāḥ</li>
    </ul>
    <ul id="R_7.035.010">
        <li id="R_7.035.010ab">hanūmān yadi me na syād vānarādhipateḥ sakhā</li>
        <li id="R_7.035.010cd">pravṛttam api ko vettuṃ jānakyāḥ śaktimān bhavet</li>
    </ul>
    <ul id="R_7.035.011">
        <li id="R_7.035.011ab">kimarthaṃ vālī caitena sugrīvapriyakāmyayā</li>
        <li id="R_7.035.011cd">tadā vaire samutpanne na dagdho vīrudho yathā</li>
    </ul>
    <ul id="R_7.035.012">
        <li id="R_7.035.012ab">na hi veditavān manye hanūmān ātmano balam</li>
        <li id="R_7.035.012cd">yad dṛṣṭavāñ jīviteṣṭaṃ kliśyantaṃ vānarādhipam</li>
    </ul>
    <ul id="R_7.035.013">
        <li id="R_7.035.013ab">etan me bhagavan sarvaṃ hanūmati mahāmune</li>
        <li id="R_7.035.013cd">vistareṇa yathātattvaṃ kathayāmarapūjita</li>
    </ul>
    <ul id="R_7.035.014">
        <li id="R_7.035.014ab">rāghavasya vacaḥ śrutvā hetuyuktam ṛṣis tataḥ</li>
        <li id="R_7.035.014cd">hanūmataḥ samakṣaṃ tam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.035.015">
        <li id="R_7.035.015ab">satyam etad raghuśreṣṭha yad bravīṣi hanūmataḥ</li>
        <li id="R_7.035.015cd">na bale vidyate tulyo na gatau na matau paraḥ</li>
    </ul>
    <ul id="R_7.035.016">
        <li id="R_7.035.016ab">amoghaśāpaiḥ śāpas tu datto 'sya ṛṣibhiḥ purā</li>
        <li id="R_7.035.016cd">na veditā balaṃ yena balī sann arimardanaḥ</li>
    </ul>
    <ul id="R_7.035.017">
        <li id="R_7.035.017ab">bālye 'py etena yat karma kṛtaṃ rāma mahābala</li>
        <li id="R_7.035.017cd">tan na varṇayituṃ śakyam atibālatayāsya te</li>
    </ul>
    <ul id="R_7.035.018">
        <li id="R_7.035.018ab">yadi vāsti tv abhiprāyas tac chrotuṃ tava rāghava</li>
        <li id="R_7.035.018cd">samādhāya matiṃ rāma niśāmaya vadāmy aham</li>
    </ul>
    <ul id="R_7.035.019">
        <li id="R_7.035.019ab">sūryadattavarasvarṇaḥ sumerur nāma parvataḥ</li>
        <li id="R_7.035.019cd">yatra rājyaṃ praśāsty asya keṣarī nāma vai pitā</li>
    </ul>
    <ul id="R_7.035.020">
        <li id="R_7.035.020ab">tasya bhāryā babhūveṣṭā hy añjaneti pariśrutā</li>
        <li id="R_7.035.020cd">janayām āsa tasyāṃ vai vāyur ātmajam uttamam</li>
    </ul>
    <ul id="R_7.035.021">
        <li id="R_7.035.021ab">śāliśūkasamābhāsaṃ prāsūtemaṃ tadāñjanā</li>
        <li id="R_7.035.021cd">phalāny āhartukāmā vai niṣkrāntā gahane carā</li>
    </ul>
    <ul id="R_7.035.022">
        <li id="R_7.035.022ab">eṣa mātur viyogāc ca kṣudhayā ca bhṛśārditaḥ</li>
        <li id="R_7.035.022cd">ruroda śiśur atyarthaṃ śiśuḥ śarabharāḍ iva</li>
    </ul>
    <ul id="R_7.035.023">
        <li id="R_7.035.023ab">tatodyantaṃ vivasvantaṃ japāpuṣpotkaropamam</li>
        <li id="R_7.035.023cd">dadṛśe phalalobhāc ca utpapāta raviṃ prati</li>
    </ul>
    <ul id="R_7.035.024">
        <li id="R_7.035.024ab">bālārkābhimukho bālo bālārka iva mūrtimān</li>
        <li id="R_7.035.024cd">grahītukāmo bālārkaṃ plavate 'mbaramadhyagaḥ</li>
    </ul>
    <ul id="R_7.035.025">
        <li id="R_7.035.025ab">etasmin plavamāne tu śiśubhāve hanūmati</li>
        <li id="R_7.035.025cd">devadānavasiddhānāṃ vismayaḥ sumahān abhūt</li>
    </ul>
    <ul id="R_7.035.026">
        <li id="R_7.035.026ab">nāpy evaṃ vegavān vāyur garuḍo na manas tathā</li>
        <li id="R_7.035.026cd">yathāyaṃ vāyuputras tu kramate 'mbaram uttamam</li>
    </ul>
    <ul id="R_7.035.027">
        <li id="R_7.035.027ab">yadi tāvac chiśor asya īdṛśau gativikramau</li>
        <li id="R_7.035.027cd">yauvanaṃ balam āsādya kathaṃ vego bhaviṣyati</li>
    </ul>
    <ul id="R_7.035.028">
        <li id="R_7.035.028ab">tam anuplavate vāyuḥ plavantaṃ putram ātmanaḥ</li>
        <li id="R_7.035.028cd">sūryadāhabhayād rakṣaṃs tuṣāracayaśītalaḥ</li>
    </ul>
    <ul id="R_7.035.029">
        <li id="R_7.035.029ab">bahuyojanasāhasraṃ kramaty eṣa tato 'mbaram</li>
        <li id="R_7.035.029cd">pitur balāc ca bālyāc ca bhāskarābhyāśam āgataḥ</li>
    </ul>
    <ul id="R_7.035.030">
        <li id="R_7.035.030ab">śiśur eṣa tv adoṣajña iti matvā divākaraḥ</li>
        <li id="R_7.035.030cd">kāryaṃ cātra samāyattam ity evaṃ na dadāha saḥ</li>
    </ul>
    <ul id="R_7.035.031">
        <li id="R_7.035.031ab">yam eva divasaṃ hy eṣa grahītuṃ bhāskaraṃ plutaḥ</li>
        <li id="R_7.035.031cd">tam eva divasaṃ rāhur jighṛkṣati divākaram</li>
    </ul>
    <ul id="R_7.035.032">
        <li id="R_7.035.032ab">anena ca parāmṛṣṭo rāma sūryarathopari</li>
        <li id="R_7.035.032cd">apakrāntas tatas trasto rāhuś candrārkamardanaḥ</li>
    </ul>
    <ul id="R_7.035.033">
        <li id="R_7.035.033ab">sa indrabhavanaṃ gatvā saroṣaḥ siṃhikāsutaḥ</li>
        <li id="R_7.035.033cd">abravīd bhrukuṭīṃ kṛtvā devaṃ devagaṇair vṛtam</li>
    </ul>
    <ul id="R_7.035.034">
        <li id="R_7.035.034ab">bubhukṣāpanayaṃ dattvā candrārkau mama vāsava</li>
        <li id="R_7.035.034cd">kim idaṃ tat tvayā dattam anyasya balavṛtrahan</li>
    </ul>
    <ul id="R_7.035.035">
        <li id="R_7.035.035ab">adyāhaṃ parvakāle tu jighṛkṣuḥ sūryam āgataḥ</li>
        <li id="R_7.035.035cd">athānyo rāhur āsādya jagrāha sahasā ravim</li>
    </ul>
    <ul id="R_7.035.036">
        <li id="R_7.035.036ab">sa rāhor vacanaṃ śrutvā vāsavaḥ saṃbhramānvitaḥ</li>
        <li id="R_7.035.036cd">utpapātāsanaṃ hitvā udvahan kāñcanasrajam</li>
    </ul>
    <ul id="R_7.035.037">
        <li id="R_7.035.037ab">tataḥ kailāsakūṭābhaṃ caturdantaṃ madasravam</li>
        <li id="R_7.035.037cd">śṛṅgārakāriṇaṃ prāṃśuṃ svarṇaghaṇṭāṭṭahāsinam</li>
    </ul>
    <ul id="R_7.035.038">
        <li id="R_7.035.038ab">indraḥ karīndram āruhya rāhuṃ kṛtvā puraḥsaram</li>
        <li id="R_7.035.038cd">prāyād yatrābhavat sūryaḥ sahānena hanūmatā</li>
    </ul>
    <ul id="R_7.035.039">
        <li id="R_7.035.039ab">athātirabhasenāgād rāhur utsṛjya vāsavam</li>
        <li id="R_7.035.039cd">anena ca sa vai dṛṣṭa ādhāvañ śailakūṭavat</li>
    </ul>
    <ul id="R_7.035.040">
        <li id="R_7.035.040ab">tataḥ sūryaṃ samutsṛjya rāhum evam avekṣya ca</li>
        <li id="R_7.035.040cd">utpapāta punar vyoma grahītuṃ siṃhikāsutam</li>
    </ul>
    <ul id="R_7.035.041">
        <li id="R_7.035.041ab">utsṛjyārkam imaṃ rāma ādhāvantaṃ plavaṃgamam</li>
        <li id="R_7.035.041cd">dṛṣṭvā rāhuḥ parāvṛtya mukhaśeṣaḥ parāṅmukhaḥ</li>
    </ul>
    <ul id="R_7.035.042">
        <li id="R_7.035.042ab">indram āśaṃsamānas tu trātāraṃ siṃhikāsutaḥ</li>
        <li id="R_7.035.042cd">indra indreti saṃtrāsān muhur muhur abhāṣata</li>
    </ul>
    <ul id="R_7.035.043">
        <li id="R_7.035.043ab">rāhor vikrośamānasya prāg evālakṣitaḥ svaraḥ</li>
        <li id="R_7.035.043cd">śrutvendrovāca mā bhaiṣīr ayam enaṃ nihanmy aham</li>
    </ul>
    <ul id="R_7.035.044">
        <li id="R_7.035.044ab">airāvataṃ tato dṛṣṭvā mahat tad idam ity api</li>
        <li id="R_7.035.044cd">phalaṃ taṃ hastirājānam abhidudrāva mārutiḥ</li>
    </ul>
    <ul id="R_7.035.045">
        <li id="R_7.035.045ab">tadāsya dhāvato rūpam airāvatajighṛkṣayā</li>
        <li id="R_7.035.045cd">muhūrtam abhavad ghoram indrāgnyor iva bhāsvaram</li>
    </ul>
    <ul id="R_7.035.046">
        <li id="R_7.035.046ab">evam ādhāvamānaṃ tu nātikruddhaḥ śacīpatiḥ</li>
        <li id="R_7.035.046cd">hastāntenātimuktena kuliśenābhyatāḍayat</li>
    </ul>
    <ul id="R_7.035.047">
        <li id="R_7.035.047ab">tato girau papātaiṣa indravajrābhitāḍitaḥ</li>
        <li id="R_7.035.047cd">patamānasya caitasya vāmo hanur abhajyata</li>
    </ul>
    <ul id="R_7.035.048">
        <li id="R_7.035.048ab">tasmiṃs tu patite bāle vajratāḍanavihvale</li>
        <li id="R_7.035.048cd">cukrodhendrāya pavanaḥ prajānām aśivāya ca</li>
    </ul>
    <ul id="R_7.035.049">
        <li id="R_7.035.049ab">viṇmūtrāśayam āvṛtya prajāsv antargataḥ prabhuḥ</li>
        <li id="R_7.035.049cd">rurodha sarvabhūtāni yathā varṣāṇi vāsavaḥ</li>
    </ul>
    <ul id="R_7.035.050">
        <li id="R_7.035.050ab">vāyuprakopād bhūtāni nirucchvāsāni sarvataḥ</li>
        <li id="R_7.035.050cd">saṃdhibhir bhajyamānāni kāṣṭhabhūtāni jajñire</li>
    </ul>
    <ul id="R_7.035.051">
        <li id="R_7.035.051ab">niḥsvadhaṃ nirvaṣaṭkāraṃ niṣkriyaṃ dharmavarjitam</li>
        <li id="R_7.035.051cd">vāyuprakopāt trailokyaṃ nirayastham ivābabhau</li>
    </ul>
    <ul id="R_7.035.052">
        <li id="R_7.035.052ab">tataḥ prajāḥ sagandharvāḥ sadevāsuramānuṣāḥ</li>
        <li id="R_7.035.052cd">prajāpatiṃ samādhāvann asukhārtāḥ sukhaiṣiṇaḥ</li>
    </ul>
    <ul id="R_7.035.053">
        <li id="R_7.035.053ab">ūcuḥ prāñjalayo devā darodaranibhodarāḥ</li>
        <li id="R_7.035.053cd">tvayā sma bhagavan sṛṣṭāḥ prajānātha caturvidhāḥ</li>
    </ul>
    <ul id="R_7.035.054">
        <li id="R_7.035.054ab">tvayā datto 'yam asmākam āyuṣaḥ pavanaḥ patiḥ</li>
        <li id="R_7.035.054cd">so 'smān prāṇeśvaro bhūtvā kasmād eṣo 'dya sattama</li>
    </ul>
    <ul id="R_7.035.055">
        <li id="R_7.035.055ab">rurodha duḥkhaṃ janayann antaḥpura iva striyaḥ</li>
        <li id="R_7.035.055cd">tasmāt tvāṃ śaraṇaṃ prāptā vāyunopahatā vibho</li>
    </ul>
    <ul id="R_7.035.056">
        <li id="R_7.035.056ab">vāyusaṃrodhajaṃ duḥkham idaṃ no nuda śatruhan</li>
    </ul>
    <ul id="R_7.035.057">
        <li id="R_7.035.057ab">etat prajānāṃ śrutvā tu prajānāthaḥ prajāpatiḥ</li>
        <li id="R_7.035.057cd">kāraṇād iti tān uktvā prajāḥ punar abhāṣata</li>
    </ul>
    <ul id="R_7.035.058">
        <li id="R_7.035.058ab">yasmin vaḥ kāraṇe vāyuś cukrodha ca rurodha ca</li>
        <li id="R_7.035.058cd">prajāḥ śṛṇudhvaṃ tat sarvaṃ śrotavyaṃ cātmanaḥ kṣamam</li>
    </ul>
    <ul id="R_7.035.059">
        <li id="R_7.035.059ab">putras tasyāmareśena indreṇādya nipātitaḥ</li>
        <li id="R_7.035.059cd">rāhor vacanam ājñāya rājñā vaḥ kopito 'nilaḥ</li>
    </ul>
    <ul id="R_7.035.060">
        <li id="R_7.035.060ab">aśarīraḥ śarīreṣu vāyuś carati pālayan</li>
        <li id="R_7.035.060cd">śarīraṃ hi vinā vāyuṃ samatāṃ yāti reṇubhiḥ</li>
    </ul>
    <ul id="R_7.035.061">
        <li id="R_7.035.061ab">vāyuḥ prāṇāḥ sukhaṃ vāyur vāyuḥ sarvam idaṃ jagat</li>
        <li id="R_7.035.061cd">vāyunā saṃparityaktaṃ na sukhaṃ vindate jagat</li>
    </ul>
    <ul id="R_7.035.062">
        <li id="R_7.035.062ab">adyaiva ca parityaktaṃ vāyunā jagad āyuṣā</li>
        <li id="R_7.035.062cd">adyaiveme nirucchvāsāḥ kāṣṭhakuḍyopamāḥ sthitāḥ</li>
    </ul>
    <ul id="R_7.035.063">
        <li id="R_7.035.063ab">tad yāmas tatra yatrāste māruto rukprado hi vaḥ</li>
        <li id="R_7.035.063cd">mā vināśaṃ gamiṣyāma aprasādyāditeḥ sutam</li>
    </ul>
    <ul id="R_7.035.064">
        <li id="R_7.035.064ab">
            <seg type="pāda" n="a">tataḥ prajābhiḥ sahitaḥ prajāpatiḥ</seg>
            <seg type="pāda" n="b">sadevagandharvabhujaṃgaguhyakaḥ</seg>
        </li>
        <li id="R_7.035.064cd">
            <seg type="pāda" n="c">jagāma tatrāsyati yatra mārutaḥ</seg>
            <seg type="pāda" n="d">sutaṃ surendrābhihataṃ pragṛhya saḥ</seg>
        </li>
    </ul>
    <ul id="R_7.035.065">
        <li id="R_7.035.065ab">
            <seg type="pāda" n="a">tato 'rkavaiśvānarakāñcanaprabhaṃ</seg>
            <seg type="pāda" n="b">sutaṃ tadotsaṅgagataṃ sadāgateḥ</seg>
        </li>
        <li id="R_7.035.065cd">
            <seg type="pāda" n="c">caturmukho vīkṣya kṛpām athākarot</seg>
            <seg type="pāda" n="d">sadevasiddharṣibhujaṃgarākṣasaḥ</seg>
        </li>
    </ul>
    <ul id="R_7.036.001">
        <li id="R_7.036.001ab">tataḥ pitāmahaṃ dṛṣṭvā vāyuḥ putravadhārditaḥ</li>
        <li id="R_7.036.001cd">śiśukaṃ taṃ samādāya uttasthau dhātur agrataḥ</li>
    </ul>
    <ul id="R_7.036.002">
        <li id="R_7.036.002ab">calatkuṇḍalamaulisraktapanīyavibhūṣaṇaḥ</li>
        <li id="R_7.036.002cd">pādayor nyapatad vāyus tisro 'vasthāya vedhase</li>
    </ul>
    <ul id="R_7.036.003">
        <li id="R_7.036.003ab">taṃ tu vedavidādyas tu lambābharaṇaśobhinā</li>
        <li id="R_7.036.003cd">vāyum utthāpya hastena śiśuṃ taṃ parimṛṣṭavān</li>
    </ul>
    <ul id="R_7.036.004">
        <li id="R_7.036.004ab">spṛṣṭamātras tataḥ so 'tha salīlaṃ padmajanmanā</li>
        <li id="R_7.036.004cd">jalasiktaṃ yathā sasyaṃ punar jīvitam āptavān</li>
    </ul>
    <ul id="R_7.036.005">
        <li id="R_7.036.005ab">prāṇavantam imaṃ dṛṣṭvā prāṇo gandhavaho mudā</li>
        <li id="R_7.036.005cd">cacāra sarvabhūteṣu saṃniruddhaṃ yathāpurā</li>
    </ul>
    <ul id="R_7.036.006">
        <li id="R_7.036.006ab">marudrogavinirmuktāḥ prajā vai muditābhavan</li>
        <li id="R_7.036.006cd">śītavātavinirmuktāḥ padminya iva sāmbujāḥ</li>
    </ul>
    <ul id="R_7.036.007">
        <li id="R_7.036.007ab">tatas triyugmas trikakut tridhāmā tridaśārcitaḥ</li>
        <li id="R_7.036.007cd">uvāca devatā brahmā mārutapriyakāmyayā</li>
    </ul>
    <ul id="R_7.036.008">
        <li id="R_7.036.008ab">bho mahendrāgnivaruṇadhaneśvaramaheśvarāḥ</li>
        <li id="R_7.036.008cd">jānatām api tat sarvaṃ hitaṃ vakṣyāmi śrūyatām</li>
    </ul>
    <ul id="R_7.036.009">
        <li id="R_7.036.009ab">anena śiśunā kāryaṃ kartavyaṃ vo bhaviṣyati</li>
        <li id="R_7.036.009cd">dadatāsya varān sarve mārutasyāsya tuṣṭidān</li>
    </ul>
    <ul id="R_7.036.010">
        <li id="R_7.036.010ab">tataḥ sahasranayanaḥ prītiraktaḥ śubhānanaḥ</li>
        <li id="R_7.036.010cd">kuśe śayamayīṃ mālāṃ samutkṣipyedam abravīt</li>
    </ul>
    <ul id="R_7.036.011">
        <li id="R_7.036.011ab">matkarotsṛṣṭavajreṇa hanur asya yathā kṣataḥ</li>
        <li id="R_7.036.011cd">nāmnaiṣa kapiśārdūlo bhavitā hanumān iti</li>
    </ul>
    <ul id="R_7.036.012">
        <li id="R_7.036.012ab">aham evāsya dāsyāmi paramaṃ varam uttamam</li>
        <li id="R_7.036.012cd">ataḥ prabhṛti vajrasya mamāvadhyo bhaviṣyati</li>
    </ul>
    <ul id="R_7.036.013">
        <li id="R_7.036.013ab">mārtāṇḍas tv abravīt tatra bhagavāṃs timirāpahaḥ</li>
        <li id="R_7.036.013cd">tejaso 'sya madīyasya dadāmi śatikāṃ kalām</li>
    </ul>
    <ul id="R_7.036.014">
        <li id="R_7.036.014ab">yadā tu śāstrāṇy adhyetuṃ śaktir asya bhaviṣyati</li>
        <li id="R_7.036.014cd">tadāsya śāstraṃ dāsyāmi yena vāgmī bhaviṣyati</li>
    </ul>
    <ul id="R_7.036.015">
        <li id="R_7.036.015ab">varuṇaś ca varaṃ prādān nāsya mṛtyur bhaviṣyati</li>
        <li id="R_7.036.015cd">varṣāyutaśatenāpi matpāśād udakād api</li>
    </ul>
    <ul id="R_7.036.016">
        <li id="R_7.036.016ab">yamo 'pi daṇḍāvadhyatvam arogatvaṃ ca nityaśaḥ</li>
        <li id="R_7.036.016cd">diśate 'sya varaṃ tuṣṭa aviṣādaṃ ca saṃyuge</li>
    </ul>
    <ul id="R_7.036.017">
        <li id="R_7.036.017ab">gadeyaṃ māmikā nainaṃ saṃyugeṣu vadhiṣyati</li>
        <li id="R_7.036.017cd">ity evaṃ varadaḥ prāha tadā hy ekākṣipiṅgalaḥ</li>
    </ul>
    <ul id="R_7.036.018">
        <li id="R_7.036.018ab">matto madāyudhānāṃ ca na vadhyo 'yaṃ bhaviṣyati</li>
        <li id="R_7.036.018cd">ity evaṃ śaṃkareṇāpi datto 'sya paramo varaḥ</li>
    </ul>
    <ul id="R_7.036.019">
        <li id="R_7.036.019ab">sarveṣāṃ brahmadaṇḍānām avadhyo 'yaṃ bhaviṣyati</li>
        <li id="R_7.036.019cd">dīrghāyuś ca mahātmā ca iti brahmābravīd vacaḥ</li>
    </ul>
    <ul id="R_7.036.020">
        <li id="R_7.036.020ab">viśvakarmā tu dṛṣṭvainaṃ bālasūryopamaṃ śiśum</li>
        <li id="R_7.036.020cd">śilpināṃ pravaraḥ prāha varam asya mahāmatiḥ</li>
    </ul>
    <ul id="R_7.036.021">
        <li id="R_7.036.021ab">vinirmitāni devānām āyudhānīha yāni tu</li>
        <li id="R_7.036.021cd">teṣāṃ saṃgrāmakāle tu avadhyo 'yaṃ bhaviṣyati</li>
    </ul>
    <ul id="R_7.036.022">
        <li id="R_7.036.022ab">tataḥ surāṇāṃ tu varair dṛṣṭvā hy enam alaṃkṛtam</li>
        <li id="R_7.036.022cd">caturmukhas tuṣṭamukho vāyum āha jagadguruḥ</li>
    </ul>
    <ul id="R_7.036.023">
        <li id="R_7.036.023ab">amitrāṇāṃ bhayakaro mitrāṇām abhayaṃkaraḥ</li>
        <li id="R_7.036.023cd">ajeyo bhavitā te 'tra putro mārutamārutiḥ</li>
    </ul>
    <ul id="R_7.036.024">
        <li id="R_7.036.024ab">rāvaṇotsādanārthāni rāmaprītikarāṇi ca</li>
        <li id="R_7.036.024cd">romaharṣakarāṇy eṣa kartā karmāṇi saṃyuge</li>
    </ul>
    <ul id="R_7.036.025">
        <li id="R_7.036.025ab">evam uktvā tam āmantrya mārutaṃ te 'maraiḥ saha</li>
        <li id="R_7.036.025cd">yathāgataṃ yayuḥ sarve pitāmahapurogamāḥ</li>
    </ul>
    <ul id="R_7.036.026">
        <li id="R_7.036.026ab">so 'pi gandhavahaḥ putraṃ pragṛhya gṛham ānayat</li>
        <li id="R_7.036.026cd">añjanāyāstam ākhyāya varaṃ dattaṃ viniḥsṛtaḥ</li>
    </ul>
    <ul id="R_7.036.027">
        <li id="R_7.036.027ab">prāpya rāma varān eṣa varadānabalānvitaḥ</li>
        <li id="R_7.036.027cd">balenātmani saṃsthena so 'pūryata yathārṇavaḥ</li>
    </ul>
    <ul id="R_7.036.028">
        <li id="R_7.036.028ab">balenāpūryamāṇo hi eṣa vānarapuṃgavaḥ</li>
        <li id="R_7.036.028cd">āśrameṣu maharṣīṇām aparādhyati nirbhayaḥ</li>
    </ul>
    <ul id="R_7.036.029">
        <li id="R_7.036.029ab">srugbhāṇḍān agnihotraṃ ca valkalānāṃ ca saṃcayān</li>
        <li id="R_7.036.029cd">bhagnavicchinnavidhvastān suśāntānāṃ karoty ayam</li>
    </ul>
    <ul id="R_7.036.030">
        <li id="R_7.036.030ab">sarveṣāṃ brahmadaṇḍānām avadhyaṃ brahmaṇā kṛtam</li>
        <li id="R_7.036.030cd">jānanta ṛṣayas taṃ vai kṣamante tasya nityaśaḥ</li>
    </ul>
    <ul id="R_7.036.031">
        <li id="R_7.036.031ab">yadā keṣariṇā tv eṣa vāyunā sāñjanena ca</li>
        <li id="R_7.036.031cd">pratiṣiddho 'pi maryādāṃ laṅghayaty eva vānaraḥ</li>
    </ul>
    <ul id="R_7.036.032">
        <li id="R_7.036.032ab">tato maharṣayaḥ kruddhā bhṛgvaṅgirasavaṃśajāḥ</li>
        <li id="R_7.036.032cd">śepur enaṃ raghuśreṣṭha nātikruddhātimanyavaḥ</li>
    </ul>
    <ul id="R_7.036.033">
        <li id="R_7.036.033ab">bādhase yat samāśritya balam asmān plavaṃgama</li>
        <li id="R_7.036.033cd">tad dīrghakālaṃ vettāsi nāsmākaṃ śāpamohitaḥ</li>
    </ul>
    <ul id="R_7.036.034">
        <li id="R_7.036.034ab">tatas tu hṛtatejaujā maharṣivacanaujasā</li>
        <li id="R_7.036.034cd">eṣo śramāṇi nātyeti mṛdubhāvagataś caran</li>
    </ul>
    <ul id="R_7.036.035">
        <li id="R_7.036.035ab">atha ṛkṣarajā nāma vālisugrīvayoḥ pitā</li>
        <li id="R_7.036.035cd">sarvavānararājāsīt tejasā iva bhāskaraḥ</li>
    </ul>
    <ul id="R_7.036.036">
        <li id="R_7.036.036ab">sa tu rājyaṃ ciraṃ kṛtvā vānarāṇāṃ harīśvaraḥ</li>
        <li id="R_7.036.036cd">tatas tvarkṣarajā nāma kāladharmeṇa saṃgataḥ</li>
    </ul>
    <ul id="R_7.036.037">
        <li id="R_7.036.037ab">tasminn astamite vālī mantribhir mantrakovidaiḥ</li>
        <li id="R_7.036.037cd">pitrye pade kṛto rājā sugrīvo vālinaḥ pade</li>
    </ul>
    <ul id="R_7.036.038">
        <li id="R_7.036.038ab">sugrīveṇa samaṃ tv asya advaidhaṃ chidravarjitam</li>
        <li id="R_7.036.038cd">ahāryaṃ sakhyam abhavad anilasya yathāgninā</li>
    </ul>
    <ul id="R_7.036.039">
        <li id="R_7.036.039ab">eṣa śāpavaśād eva na vedabalam ātmanaḥ</li>
        <li id="R_7.036.039cd">vālisugrīvayor vairaṃ yadā rāma samutthitam</li>
    </ul>
    <ul id="R_7.036.040">
        <li id="R_7.036.040ab">na hy eṣa rāma sugrīvo bhrāmyamāṇo 'pi vālinā</li>
        <li id="R_7.036.040cd">vedayāno na ca hy eṣa balam ātmani mārutiḥ</li>
    </ul>
    <ul id="R_7.036.041">
        <li id="R_7.036.041ab">
            <seg type="pāda" n="a">parākramotsāhamatipratāpaiḥ</seg>
            <seg type="pāda" n="b">sauśīlyamādhuryanayānayaiś ca</seg>
        </li>
        <li id="R_7.036.041cd">
            <seg type="pāda" n="c">gāmbhīryacāturyasuvīryadhairyair</seg>
            <seg type="pāda" n="d">hanūmataḥ ko 'py adhiko 'sti loke</seg>
        </li>
    </ul>
    <ul id="R_7.036.042">
        <li id="R_7.036.042ab">
            <seg type="pāda" n="a">asau purā vyākaraṇaṃ grahīṣyan</seg>
            <seg type="pāda" n="b">sūryonmukhaḥ pṛṣṭhagamaḥ kapīndraḥ</seg>
        </li>
        <li id="R_7.036.042cd">
            <seg type="pāda" n="c">udyadgirer astagiriṃ jagāma</seg>
            <seg type="pāda" n="d">granthaṃ mahad dhārayad aprameyaḥ</seg>
        </li>
    </ul>
    <ul id="R_7.036.043">
        <li id="R_7.036.043ab">
            <seg type="pāda" n="a">pravīvivikṣor iva sāgarasya</seg>
            <seg type="pāda" n="b">lokān didhakṣor iva pāvakasya</seg>
        </li>
        <li id="R_7.036.043cd">
            <seg type="pāda" n="c">lokakṣayeṣv eva yathāntakasya</seg>
            <seg type="pāda" n="d">hanūmataḥ sthāsyati kaḥ purastāt</seg>
        </li>
    </ul>
    <ul id="R_7.036.044">
        <li id="R_7.036.044ab">
            <seg type="pāda" n="a">eṣo 'pi cānye ca mahākapīndrāḥ</seg>
            <seg type="pāda" n="b">sugrīvamaindadvividāḥ sanīlāḥ</seg>
        </li>
        <li id="R_7.036.044cd">
            <seg type="pāda" n="c">satāratāreyanalāḥ sarambhās</seg>
            <seg type="pāda" n="d">tvatkāraṇād rāma surair hi sṛṣṭāḥ</seg>
        </li>
    </ul>
    <ul id="R_7.036.045">
        <li id="R_7.036.045ab">tad etat kathitaṃ sarvaṃ yan māṃ tvaṃ paripṛcchasi</li>
        <li id="R_7.036.045cd">hanūmato bālabhāve karmaitat kathitaṃ mayā</li>
    </ul>
    <ul id="R_7.036.046">
        <li id="R_7.036.046ab">dṛṣṭaḥ saṃbhāṣitaś cāsi rāma gacchamahe vayam</li>
        <li id="R_7.036.046cd">evam uktvā gatāḥ sarve ṛṣayas te yathāgatam</li>
    </ul>
    <ul id="R_7.037.001">
        <li id="R_7.037.001ab">vimṛśya ca tato rāmo vayasyam akutobhayam</li>
        <li id="R_7.037.001cd">pratardanaṃ kāśipatiṃ pariṣvajyedam abravīt</li>
    </ul>
    <ul id="R_7.037.002">
        <li id="R_7.037.002ab">darśitā bhavatā prītir darśitaṃ sauhṛdaṃ param</li>
        <li id="R_7.037.002cd">udyogaś ca kṛto rājan bharatena tvayā saha</li>
    </ul>
    <ul id="R_7.037.003">
        <li id="R_7.037.003ab">tad bhavān adya kāśeyīṃ purīṃ vārāṇasīṃ vraja</li>
        <li id="R_7.037.003cd">ramaṇīyāṃ tvayā guptāṃ suprākārāṃ sutoraṇām</li>
    </ul>
    <ul id="R_7.037.004">
        <li id="R_7.037.004ab">etāvad uktvā utthāya kākutsthaḥ paramāsanāt</li>
        <li id="R_7.037.004cd">paryaṣvajata dharmātmā nirantaram urogatam</li>
    </ul>
    <ul id="R_7.037.005">
        <li id="R_7.037.005ab">visṛjya taṃ vayasyaṃ sa svāgatān pṛthivīpatīn</li>
        <li id="R_7.037.005cd">prahasan rāghavo vākyam uvāca madhurākṣaram</li>
    </ul>
    <ul id="R_7.037.006">
        <li id="R_7.037.006ab">bhavatāṃ prītir avyagrā tejasā parirakṣitā</li>
        <li id="R_7.037.006cd">dharmaś ca niyato nityaṃ satyaṃ ca bhavatāṃ sadā</li>
    </ul>
    <ul id="R_7.037.007">
        <li id="R_7.037.007ab">yuṣmākaṃ ca prabhāvena tejasā ca mahātmanām</li>
        <li id="R_7.037.007cd">hato durātmā durbuddhī rāvaṇo rākṣasādhipaḥ</li>
    </ul>
    <ul id="R_7.037.008">
        <li id="R_7.037.008ab">hetumātram ahaṃ tatra bhavatāṃ tejasāṃ hataḥ</li>
        <li id="R_7.037.008cd">rāvaṇaḥ sagaṇo yuddhe saputraḥ sahabāndhavaḥ</li>
    </ul>
    <ul id="R_7.037.009">
        <li id="R_7.037.009ab">bhavantaś ca samānītā bharatena mahātmanā</li>
        <li id="R_7.037.009cd">śrutvā janakarājasya kānane tanayāṃ hṛtām</li>
    </ul>
    <ul id="R_7.037.010">
        <li id="R_7.037.010ab">udyuktānāṃ ca sarveṣāṃ pārthivānāṃ mahātmanām</li>
        <li id="R_7.037.010cd">kālo hy atītaḥ sumahān gamane rocatāṃ matiḥ</li>
    </ul>
    <ul id="R_7.037.011">
        <li id="R_7.037.011ab">pratyūcus taṃ ca rājāno harṣeṇa mahatānvitāḥ</li>
        <li id="R_7.037.011cd">diṣṭyā tvaṃ vijayī rāma rājyaṃ cāpi pratiṣṭhitam</li>
    </ul>
    <ul id="R_7.037.012">
        <li id="R_7.037.012ab">diṣṭyā pratyāhṛtā sītā diṣṭyā śatruḥ parājitaḥ</li>
        <li id="R_7.037.012cd">eṣa naḥ paramaḥ kāma eṣā naḥ kīrtir uttamā</li>
    </ul>
    <ul id="R_7.037.013">
        <li id="R_7.037.013ab">yat tvāṃ vijayinaṃ rāma paśyāmo hataśātravam</li>
        <li id="R_7.037.013cd">upapannaṃ ca kākutstha yat tvam asmān praśaṃsasi</li>
    </ul>
    <ul id="R_7.037.014">
        <li id="R_7.037.014ab">praśaṃsārhā hi jānanti praśaṃsāṃ vaktum īdṛśīm</li>
        <li id="R_7.037.014cd">āpṛcchāmo gamiṣyāmo hṛdistho naḥ sadā bhavān</li>
    </ul>
    <ul id="R_7.037.015">
        <li id="R_7.037.015ab">bhavec ca te mahārāja prītir asmāsu nityadā</li>
    </ul>
    <ul id="R_7.038.001">
        <li id="R_7.038.001ab">te prayātā mahātmānaḥ pārthivāḥ sarvato diśam</li>
        <li id="R_7.038.001cd">kampayanto mahīṃ vīrāḥ svapurāṇi prahṛṣṭavat</li>
    </ul>
    <ul id="R_7.038.002">
        <li id="R_7.038.002ab">akṣauhiṇī sahasrais te samavetās tv anekaśaḥ</li>
        <li id="R_7.038.002cd">hṛṣṭāḥ pratigatāḥ sarve rāghavārthe samāgatāḥ</li>
    </ul>
    <ul id="R_7.038.003">
        <li id="R_7.038.003ab">ūcuś caiva mahīpālā baladarpasamanvitāḥ</li>
        <li id="R_7.038.003cd">na nāma rāvaṇaṃ yuddhe paśyāmaḥ purataḥ sthitam</li>
    </ul>
    <ul id="R_7.038.004">
        <li id="R_7.038.004ab">bharatena vayaṃ paścāt samānītā nirarthakam</li>
        <li id="R_7.038.004cd">hatā hi rākṣasās tatra pārthivaiḥ syur na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.038.005">
        <li id="R_7.038.005ab">rāmasya bāhuvīryeṇa pālitā lakṣmaṇasya ca</li>
        <li id="R_7.038.005cd">sukhaṃ pāre samudrasya yudhyema vigatajvarāḥ</li>
    </ul>
    <ul id="R_7.038.006">
        <li id="R_7.038.006ab">etāś cānyāś ca rājānaḥ kathās tatra sahasraśaḥ</li>
        <li id="R_7.038.006cd">kathayantaḥ svarāṣṭrāṇi viviśus te mahārathāḥ</li>
    </ul>
    <ul id="R_7.038.007">
        <li id="R_7.038.007ab">yathāpurāṇi te gatvā ratnāni vividhāni ca</li>
        <li id="R_7.038.007cd">rāmāya priyakāmārtham upahārān nṛpā daduḥ</li>
    </ul>
    <ul id="R_7.038.008">
        <li id="R_7.038.008ab">aśvān ratnāni vastrāṇi hastinaś ca madotkaṭān</li>
        <li id="R_7.038.008cd">candanāni ca divyāni divyāny ābharaṇāni ca</li>
    </ul>
    <ul id="R_7.038.009">
        <li id="R_7.038.009ab">bharato lakṣmaṇaś caiva śatrughnaś ca mahārathaḥ</li>
        <li id="R_7.038.009cd">ādāya tāni ratnāni ayodhyām agaman punaḥ</li>
    </ul>
    <ul id="R_7.038.010">
        <li id="R_7.038.010ab">āgatāś ca purīṃ ramyām ayodhyāṃ puruṣarṣabhāḥ</li>
        <li id="R_7.038.010cd">daduḥ sarvāṇi ratnāni rāghavāya mahātmane</li>
    </ul>
    <ul id="R_7.038.011">
        <li id="R_7.038.011ab">pratigṛhya ca tat sarvaṃ prītiyuktaḥ sa rāghavaḥ</li>
        <li id="R_7.038.011cd">sarvāṇi tāni pradadau sugrīvāya mahātmane</li>
    </ul>
    <ul id="R_7.038.012">
        <li id="R_7.038.012ab">vibhīṣaṇāya ca dadau ye cānye ṛkṣavānarāḥ</li>
        <li id="R_7.038.012cd">hanūmatpramukhā vīrā rākṣasāś ca mahābalāḥ</li>
    </ul>
    <ul id="R_7.038.013">
        <li id="R_7.038.013ab">te sarve hṛṣṭamanaso rāmadattāni tāny atha</li>
        <li id="R_7.038.013cd">śirobhir dhārayām āsur bāhubhiś ca mahābalāḥ</li>
    </ul>
    <ul id="R_7.038.014">
        <li id="R_7.038.014ab">papuś caiva sugandhīni madhūni vividhāni ca</li>
        <li id="R_7.038.014cd">māṃsāni ca sumṛṣṭāni phalāny āsvādayanti ca</li>
    </ul>
    <ul id="R_7.038.015">
        <li id="R_7.038.015ab">evaṃ teṣāṃ nivasatāṃ māsaḥ sāgro gatas tadā</li>
        <li id="R_7.038.015cd">muhūrtam iva tat sarvaṃ rāmabhaktyā samarthayan</li>
    </ul>
    <ul id="R_7.038.016">
        <li id="R_7.038.016ab">reme rāmaḥ sa taiḥ sārdhaṃ vānaraiḥ kāmarūpibhiḥ</li>
        <li id="R_7.038.016cd">rājabhiś ca mahāvīryai rākṣasaiś ca mahābalaiḥ</li>
    </ul>
    <ul id="R_7.038.017">
        <li id="R_7.038.017ab">evaṃ teṣāṃ yayau māso dvitīyaḥ śaiśiraḥ sukham</li>
        <li id="R_7.038.017cd">vānarāṇāṃ prahṛṣṭānāṃ rākṣasānāṃ ca sarvaśaḥ</li>
    </ul>
    <ul id="R_7.039.001">
        <li id="R_7.039.001ab">tathā sma teṣāṃ vasatām ṛkṣavānararakṣasām</li>
        <li id="R_7.039.001cd">rāghavas tu mahātejāḥ sugrīvam idam abravīt</li>
    </ul>
    <ul id="R_7.039.002">
        <li id="R_7.039.002ab">gamyatāṃ saumya kiṣkindhāṃ durādharṣaṃ surāsuraiḥ</li>
        <li id="R_7.039.002cd">pālayasva sahāmātyai rājyaṃ nihatakaṇṭakam</li>
    </ul>
    <ul id="R_7.039.003">
        <li id="R_7.039.003ab">aṅgadaṃ ca mahābāho prītyā paramayānvitaḥ</li>
        <li id="R_7.039.003cd">paśya tvaṃ hanumantaṃ ca nalaṃ ca sumahābalam</li>
    </ul>
    <ul id="R_7.039.004">
        <li id="R_7.039.004ab">suṣeṇaṃ śvaśuraṃ śūraṃ tāraṃ ca balināṃ varam</li>
        <li id="R_7.039.004cd">kumudaṃ caiva durdharṣaṃ nīlaṃ ca sumahābalam</li>
    </ul>
    <ul id="R_7.039.005">
        <li id="R_7.039.005ab">vīraṃ śatabaliṃ caiva maindaṃ dvividam eva ca</li>
        <li id="R_7.039.005cd">gajaṃ gavākṣaṃ gavayaṃ śarabhaṃ ca mahābalam</li>
    </ul>
    <ul id="R_7.039.006">
        <li id="R_7.039.006ab">ṛkṣarājaṃ ca durdharṣaṃ jāmbavantaṃ mahābalam</li>
        <li id="R_7.039.006cd">paśya prītisamāyukto gandhamādanam eva ca</li>
    </ul>
    <ul id="R_7.039.007">
        <li id="R_7.039.007ab">ye cānye sumahātmāno madarthe tyaktajīvitāḥ</li>
        <li id="R_7.039.007cd">paśya tvaṃ prītisaṃyukto mā caiṣāṃ vipriyaṃ kṛthāḥ</li>
    </ul>
    <ul id="R_7.039.008">
        <li id="R_7.039.008ab">evam uktvā ca sugrīvaṃ praśasya ca punaḥ punaḥ</li>
        <li id="R_7.039.008cd">vibhīṣaṇam athovāca rāmo madhurayā girā</li>
    </ul>
    <ul id="R_7.039.009">
        <li id="R_7.039.009ab">taṅkāṃ praśādhi dharmeṇa saṃmato hy asi pārthiva</li>
        <li id="R_7.039.009cd">purasya rākṣasānāṃ ca bhrātur vaiśvaraṇasya ca</li>
    </ul>
    <ul id="R_7.039.010">
        <li id="R_7.039.010ab">mā ca buddhim adharme tvaṃ kuryā rājan kathaṃ cana</li>
        <li id="R_7.039.010cd">buddhimanto hi rājāno dhruvam aśnanti medinīm</li>
    </ul>
    <ul id="R_7.039.011">
        <li id="R_7.039.011ab">ahaṃ ca nityaśo rājan sugrīvasahitas tvayā</li>
        <li id="R_7.039.011cd">smartavyaḥ parayā prītyā gaccha tvaṃ vigatajvaraḥ</li>
    </ul>
    <ul id="R_7.039.012">
        <li id="R_7.039.012ab">rāmasya bhāṣitaṃ śrutvā ṛṣkavānararākṣasāḥ</li>
        <li id="R_7.039.012cd">sādhu sādhv iti kākutsthaṃ praśaśaṃsuḥ punaḥ punaḥ</li>
    </ul>
    <ul id="R_7.039.013">
        <li id="R_7.039.013ab">tava buddhir mahābāho vīryam adbhutam eva ca</li>
        <li id="R_7.039.013cd">mādhuryaṃ paramaṃ rāma svayambhor iva nityadā</li>
    </ul>
    <ul id="R_7.039.014">
        <li id="R_7.039.014ab">teṣām evaṃ bruvāṇānāṃ vānarāṇāṃ ca rakṣasām</li>
        <li id="R_7.039.014cd">hanūmatpraṇato bhūtvā rāghavaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_7.039.015">
        <li id="R_7.039.015ab">sneho me paramo rājaṃs tvayi nityaṃ pratiṣṭhitaḥ</li>
        <li id="R_7.039.015cd">bhaktiś ca niyatā vīra bhāvo nānyatra gacchati</li>
    </ul>
    <ul id="R_7.039.016">
        <li id="R_7.039.016ab">yāvad rāmakathāṃ vīra śroṣye 'haṃ pṛthivītale</li>
        <li id="R_7.039.016cd">tāvac charīre vatsyantu mama prāṇā na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.039.017">
        <li id="R_7.039.017ab">evaṃ bruvāṇaṃ rājendro hanūmantam athāsanāt</li>
        <li id="R_7.039.017cd">utthāya ca pariṣvajya vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.039.018">
        <li id="R_7.039.018ab">evam etat kapiśreṣṭha bhavitā nātra saṃśayaḥ</li>
        <li id="R_7.039.018cd">lokā hi yāvat sthāsyanti tāvat sthāsyati me kathā</li>
    </ul>
    <ul id="R_7.039.019">
        <li id="R_7.039.019ab">cariṣyati kathā yāval lokān eṣā hi māmikā</li>
        <li id="R_7.039.019cd">tāvac charīre vatsyanti prāṇās tava na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.039.020">
        <li id="R_7.039.020ab">tato 'sya hāraṃ candrābhaṃ mucya kaṇṭhāt sa rāghavaḥ</li>
        <li id="R_7.039.020cd">vaidūryataralaṃ snehād ābabandhe hanūmati</li>
    </ul>
    <ul id="R_7.039.021">
        <li id="R_7.039.021ab">tenorasi nibaddhena hāreṇa sa mahākapiḥ</li>
        <li id="R_7.039.021cd">rarāja hemaśailendraś candreṇākrāntamastakaḥ</li>
    </ul>
    <ul id="R_7.039.022">
        <li id="R_7.039.022ab">śrutvā tu rāghavasyaitad utthāyotthāya vānarāḥ</li>
        <li id="R_7.039.022cd">praṇamya śirasā pādau prajagmus te mahābalāḥ</li>
    </ul>
    <ul id="R_7.039.023">
        <li id="R_7.039.023ab">sugrīvaś caiva rāmeṇa pariṣvakto mahābhujaḥ</li>
        <li id="R_7.039.023cd">vibhīṣaṇaś ca dharmātmā nirantaram urogataḥ</li>
    </ul>
    <ul id="R_7.039.024">
        <li id="R_7.039.024ab">sarve ca te bāṣpagalāḥ sāśrunetrā vicetasaḥ</li>
        <li id="R_7.039.024cd">saṃmūḍhā iva duḥkhena tyajante rāghavaṃ tadā</li>
    </ul>
    <ul id="R_7.040.001">
        <li id="R_7.040.001ab">visṛjya ca mahābāhur ṛkṣavānararākṣasān</li>
        <li id="R_7.040.001cd">bhrātṛbhiḥ sahito rāmaḥ pramumoda sukhī sukham</li>
    </ul>
    <ul id="R_7.040.002">
        <li id="R_7.040.002ab">athāparāhṇasamaye bhrātṛbhiḥ saha rāghavaḥ</li>
        <li id="R_7.040.002cd">śuśrāva madhurāṃ vāṇīm antarikṣāt prabhāṣitām</li>
    </ul>
    <ul id="R_7.040.003">
        <li id="R_7.040.003ab">saumya rāma nirīkṣasva saumyena vadanena mām</li>
        <li id="R_7.040.003cd">kailāsaśikharāt prāptaṃ viddhi māṃ puṣkaraṃ prabho</li>
    </ul>
    <ul id="R_7.040.004">
        <li id="R_7.040.004ab">tava śāsanam ājñāya gato 'smi dhanadaṃ prati</li>
        <li id="R_7.040.004cd">upasthātuṃ naraśreṣṭha sa ca māṃ pratyabhāṣata</li>
    </ul>
    <ul id="R_7.040.005">
        <li id="R_7.040.005ab">nirjitas tvaṃ narendreṇa rāghaveṇa mahātmanā</li>
        <li id="R_7.040.005cd">nihatya yudhi durdharṣaṃ rāvaṇaṃ rākṣasādhipam</li>
    </ul>
    <ul id="R_7.040.006">
        <li id="R_7.040.006ab">mamāpi paramā prītir hate tasmin durātmani</li>
        <li id="R_7.040.006cd">rāvaṇe sagaṇe saumya saputrāmātyabāndhave</li>
    </ul>
    <ul id="R_7.040.007">
        <li id="R_7.040.007ab">sa tvaṃ rāmeṇa laṅkāyāṃ nirjitaḥ paramātmanā</li>
        <li id="R_7.040.007cd">vaha saumya tam eva tvam aham ājñāpayāmi te</li>
    </ul>
    <ul id="R_7.040.008">
        <li id="R_7.040.008ab">eṣa me paramaḥ kāmo yat tvaṃ rāghavanandanam</li>
        <li id="R_7.040.008cd">vaher lokasya saṃyānaṃ gacchasva vigatajvaraḥ</li>
    </ul>
    <ul id="R_7.040.009">
        <li id="R_7.040.009ab">tacchāsanam ahaṃ jñātvā dhanadasya mahātmanaḥ</li>
        <li id="R_7.040.009cd">tvatsakāśaṃ punaḥ prāptaḥ sa evaṃ pratigṛhṇa mām</li>
    </ul>
    <ul id="R_7.040.010">
        <li id="R_7.040.010ab">bāḍham ity eva kākutsthaḥ puṣpakaṃ samapūjayat</li>
        <li id="R_7.040.010cd">lājākṣataiś ca puṣpaiś ca gandhaiś ca susugandhibhiḥ</li>
    </ul>
    <ul id="R_7.040.011">
        <li id="R_7.040.011ab">gamyatāṃ ca yathākāmam āgacches tvaṃ yadā smare</li>
        <li id="R_7.040.011cd">evam astv iti rāmeṇa visṛṣṭaḥ puṣpakaḥ punaḥ</li>
        <li id="R_7.040.011ef">abhipretāṃ diśaṃ prāyāt puṣpakaḥ puṣpabhūṣitaḥ</li>
    </ul>
    <ul id="R_7.040.012">
        <li id="R_7.040.012ab">evam antarhite tasmin puṣpake vividhātmani</li>
        <li id="R_7.040.012cd">bharataḥ prāñjalir vākyam uvāca raghunandanam</li>
    </ul>
    <ul id="R_7.040.013">
        <li id="R_7.040.013ab">atyadbhutāni dṛśyante tvayi rājyaṃ praśāsati</li>
        <li id="R_7.040.013cd">amānuṣāṇāṃ sattvānāṃ vyāhṛtāni muhur muhuḥ</li>
    </ul>
    <ul id="R_7.040.014">
        <li id="R_7.040.014ab">anāmayāc ca martyānāṃ sāgro māso gato hy ayam</li>
        <li id="R_7.040.014cd">jīrṇānām api sattvānāṃ mṛtyur nāyāti rāghava</li>
    </ul>
    <ul id="R_7.040.015">
        <li id="R_7.040.015ab">putrān nāryaḥ prasūyante vapuṣmantaś ca mānavāḥ</li>
        <li id="R_7.040.015cd">harṣaś cābhyadhiko rājañ janasya puravāsinaḥ</li>
    </ul>
    <ul id="R_7.040.016">
        <li id="R_7.040.016ab">kāle ca vāsavo varṣaṃ pātayaty amṛtopamam</li>
        <li id="R_7.040.016cd">vāyavaś cāpi vāyante sparśavantaḥ sukhapradāḥ</li>
    </ul>
    <ul id="R_7.040.017">
        <li id="R_7.040.017ab">īdṛśo naś ciraṃ rājā bhavatv iti nareśvara</li>
        <li id="R_7.040.017cd">kathayanti pure paurā janā janapadeṣu ca</li>
    </ul>
    <ul id="R_7.040.018">
        <li id="R_7.040.018ab">etā vācaḥ sumadhurā bharatena samīritāḥ</li>
        <li id="R_7.040.018cd">śrutvā rāmo mudā yuktaḥ pramumoda sukhī sukham</li>
    </ul>
    <ul id="R_7.041.001">
        <li id="R_7.041.001ab">sa visṛjya tato rāmaḥ puṣpakaṃ hemabhūṣitam</li>
        <li id="R_7.041.001cd">praviveśa mahābāhur aśokavanikāṃ tadā</li>
    </ul>
    <ul id="R_7.041.002">
        <li id="R_7.041.002ab">candanāgarucūtaiś ca tuṅgakāleyakair api</li>
        <li id="R_7.041.002cd">devadāruvanaiś cāpi samantād upaśobhitām</li>
    </ul>
    <ul id="R_7.041.003">
        <li id="R_7.041.003ab">priyaṅgubhiḥ kadambaiś ca tathā kurabakair api</li>
        <li id="R_7.041.003cd">jambūbhiḥ pāṭalībhiś ca kovidāraiś ca saṃvṛtām</li>
    </ul>
    <ul id="R_7.041.004">
        <li id="R_7.041.004ab">sarvadā kusumai ramyaiḥ phalavadbhir manoramaiḥ</li>
        <li id="R_7.041.004cd">cārupallavapuṣpāḍhyair mattabhramarasaṃkulaiḥ</li>
    </ul>
    <ul id="R_7.041.005">
        <li id="R_7.041.005ab">kokilair bhṛṅgarājaiś ca nānāvarṇaiś ca pakṣibhiḥ</li>
        <li id="R_7.041.005cd">śobhitāṃ śataśaś citraiś cūtavṛkṣāvataṃsakaiḥ</li>
    </ul>
    <ul id="R_7.041.006">
        <li id="R_7.041.006ab">śātakumbhanibhāḥ ke cit ke cid agniśikhopamāḥ</li>
        <li id="R_7.041.006cd">nīlāñjananibhāś cānye bhānti tatra sma pādapāḥ</li>
    </ul>
    <ul id="R_7.041.007">
        <li id="R_7.041.007ab">dīrghikā vividhākārāḥ pūrṇāḥ paramavāriṇā</li>
        <li id="R_7.041.007cd">mahārhamaṇisopānasphaṭikāntarakuṭṭimāḥ</li>
    </ul>
    <ul id="R_7.041.008">
        <li id="R_7.041.008ab">phullapadmotpalavanāś cakravākopaśobhitāḥ</li>
        <li id="R_7.041.008cd">prākārair vividhākāraiḥ śobhitāś ca śilātalaiḥ</li>
    </ul>
    <ul id="R_7.041.009">
        <li id="R_7.041.009ab">tatra tatra vanoddeśe vaidūryamaṇisaṃnibhaiḥ</li>
        <li id="R_7.041.009cd">śādvalaiḥ paramopetāḥ puṣpitadrumasaṃyutāḥ</li>
    </ul>
    <ul id="R_7.041.010">
        <li id="R_7.041.010ab">nandanaṃ hi yathendrasya brāhmaṃ caitrarathaṃ yathā</li>
        <li id="R_7.041.010cd">tathārūpaṃ hi rāmasya kānanaṃ tan niveśitam</li>
    </ul>
    <ul id="R_7.041.011">
        <li id="R_7.041.011ab">bahvāsanagṛhopetāṃ latāgṛhasamāvṛtām</li>
        <li id="R_7.041.011cd">aśokavanikāṃ sphītāṃ praviśya raghunandanaḥ</li>
    </ul>
    <ul id="R_7.041.012">
        <li id="R_7.041.012ab">āsane tu śubhākāre puṣpastabakabhūṣite</li>
        <li id="R_7.041.012cd">kuthāstaraṇasaṃvīte rāmaḥ saṃniṣasāda ha</li>
    </ul>
    <ul id="R_7.041.013">
        <li id="R_7.041.013ab">sītāṃ saṃgṛhya bāhubhyāṃ madhumaireyam uttamam</li>
        <li id="R_7.041.013cd">pāyayām āsa kākutsthaḥ śacīm indro yathāmṛtam</li>
    </ul>
    <ul id="R_7.041.014">
        <li id="R_7.041.014ab">māṃsāni ca vicitrāṇi phalāni vividhāni ca</li>
        <li id="R_7.041.014cd">rāmasyābhyavahārārthaṃ kiṃkarās tūrṇam āharan</li>
    </ul>
    <ul id="R_7.041.015">
        <li id="R_7.041.015ab">upanṛtyanti rājānaṃ nṛtyagītaviśāradāḥ</li>
        <li id="R_7.041.015cd">bālāś ca rūpavatyaś ca striyaḥ pānavaśaṃ gatāḥ</li>
    </ul>
    <ul id="R_7.041.016">
        <li id="R_7.041.016ab">evaṃ rāmo mudā yuktaḥ sītāṃ surucirānanām</li>
        <li id="R_7.041.016cd">ramayām āsa vaidehīm ahany ahani devavat</li>
    </ul>
    <ul id="R_7.041.017">
        <li id="R_7.041.017ab">tathā tu ramamāṇasya tasyaivaṃ śiśiraḥ śubhaḥ</li>
        <li id="R_7.041.017cd">atyakrāman narendrasya rāghavasya mahātmanaḥ</li>
    </ul>
    <ul id="R_7.041.018">
        <li id="R_7.041.018ab">pūrvāhṇe paurakṛtyāni kṛtvā dharmeṇa dharmavit</li>
        <li id="R_7.041.018cd">śeṣaṃ divasabhāgārdham antaḥpuragato 'bhavat</li>
    </ul>
    <ul id="R_7.041.019">
        <li id="R_7.041.019ab">sītā ca devakāryāṇi kṛtvā paurvāhṇikāni tu</li>
        <li id="R_7.041.019cd">śvaśrūṇām aviśeṣeṇa sarvāsāṃ prāñjaliḥ sthitā</li>
    </ul>
    <ul id="R_7.041.020">
        <li id="R_7.041.020ab">tato rāmam upāgacchad vicitrabahubhūṣaṇā</li>
        <li id="R_7.041.020cd">triviṣṭape sahasrākṣam upaviṣṭaṃ yathā śacī</li>
    </ul>
    <ul id="R_7.041.021">
        <li id="R_7.041.021ab">dṛṣṭvā tu rāghavaḥ patnīṃ kalyāṇena samanvitām</li>
        <li id="R_7.041.021cd">praharṣam atulaṃ lebhe sādhu sādhv iti cābravīt</li>
    </ul>
    <ul id="R_7.041.022">
        <li id="R_7.041.022ab">apatyalābho vaidehi mamāyaṃ samupasthitaḥ</li>
        <li id="R_7.041.022cd">kim icchasi hi tad brūhi kaḥ kāmaḥ kriyatāṃ tava</li>
    </ul>
    <ul id="R_7.041.023">
        <li id="R_7.041.023ab">prahasantī tu vaidehī rāmaṃ vākyam athābravīt</li>
        <li id="R_7.041.023cd">tapovanāni puṇyāni draṣṭum icchāmi rāghava</li>
    </ul>
    <ul id="R_7.041.024">
        <li id="R_7.041.024ab">gaṅgātīre niviṣṭāni ṛṣīṇāṃ puṇyakarmaṇām</li>
        <li id="R_7.041.024cd">phalamūlāśināṃ vīra pādamūleṣu vartitum</li>
    </ul>
    <ul id="R_7.041.025">
        <li id="R_7.041.025ab">eṣa me paramaḥ kāmo yan mūlaphalabhojiṣu</li>
        <li id="R_7.041.025cd">apy ekarātraṃ kākutstha vaseyaṃ puṇyaśāliṣu</li>
    </ul>
    <ul id="R_7.041.026">
        <li id="R_7.041.026ab">tatheti ca pratijñātaṃ rāmeṇākliṣṭakarmaṇā</li>
        <li id="R_7.041.026cd">visrabdhā bhava vaidehi śvo gamiṣyasy asaṃśayam</li>
    </ul>
    <ul id="R_7.041.027">
        <li id="R_7.041.027ab">evam uktvā tu kākutstho maithilīṃ janakātmajām</li>
        <li id="R_7.041.027cd">madhyakakṣāntaraṃ rāmo nirjagāma suhṛdvṛtaḥ</li>
    </ul>
    <ul id="R_7.042.001">
        <li id="R_7.042.001ab">tatropaviṣṭaṃ rājānam upāsante vicakṣaṇāḥ</li>
        <li id="R_7.042.001cd">kathānāṃ bahurūpāṇāṃ hāsyakārāḥ samantataḥ</li>
    </ul>
    <ul id="R_7.042.002">
        <li id="R_7.042.002ab">vijayo madhumattaś ca kāśyapaḥ piṅgalaḥ kuśaḥ</li>
        <li id="R_7.042.002cd">surājiḥ kāliyo bhadro dantavakraḥ samāgadhaḥ</li>
    </ul>
    <ul id="R_7.042.003">
        <li id="R_7.042.003ab">ete kathā bahuvidhā parihāsasamanvitāḥ</li>
        <li id="R_7.042.003cd">kathayanti sma saṃhṛṣṭā rāghavasya mahātmanaḥ</li>
    </ul>
    <ul id="R_7.042.004">
        <li id="R_7.042.004ab">tataḥ kathāyāṃ kasyāṃ cid rāghavaḥ samabhāṣata</li>
        <li id="R_7.042.004cd">kāḥ kathā nagare bhadra vartante viṣayeṣu ca</li>
    </ul>
    <ul id="R_7.042.005">
        <li id="R_7.042.005ab">mām āśritāni kāny āhuḥ paurajānapadā janāḥ</li>
        <li id="R_7.042.005cd">kiṃ ca sītāṃ samāśritya bharataṃ kiṃ nu lakṣmaṇam</li>
    </ul>
    <ul id="R_7.042.006">
        <li id="R_7.042.006ab">kiṃ nu śatrughnam āśritya kaikeyīṃ mātaraṃ ca me</li>
        <li id="R_7.042.006cd">vaktavyatāṃ ca rājāno nave rājye vrajanti hi</li>
    </ul>
    <ul id="R_7.042.007">
        <li id="R_7.042.007ab">evam ukte tu rāmeṇa bhadraḥ prāñjalir abravīt</li>
        <li id="R_7.042.007cd">sthitāḥ kathāḥ śubhā rājan vartante puravāsinām</li>
    </ul>
    <ul id="R_7.042.008">
        <li id="R_7.042.008ab">ayaṃ tu vijayaḥ saumya daśagrīvavadhāśritaḥ</li>
        <li id="R_7.042.008cd">bhūyiṣṭhaṃ svapure pauraiḥ kathyate puruṣarṣabha</li>
    </ul>
    <ul id="R_7.042.009">
        <li id="R_7.042.009ab">evam uktas tu bhadreṇa rāghavo vākyam abravīt</li>
        <li id="R_7.042.009cd">kathayasva yathātathyaṃ sarvaṃ niravaśeṣataḥ</li>
    </ul>
    <ul id="R_7.042.010">
        <li id="R_7.042.010ab">śubhāśubhāni vākyāni yāny āhuḥ puravāsinaḥ</li>
        <li id="R_7.042.010cd">śrutvedānīṃ śubhaṃ kuryāṃ na kuryām aśubhāni ca</li>
    </ul>
    <ul id="R_7.042.011">
        <li id="R_7.042.011ab">kathayasva ca visrabdho nirbhayo vigatajvaraḥ</li>
        <li id="R_7.042.011cd">kathayante yathā paurā janā janapadeṣu ca</li>
    </ul>
    <ul id="R_7.042.012">
        <li id="R_7.042.012ab">rāghaveṇaivam uktas tu bhadraḥ suruciraṃ vacaḥ</li>
        <li id="R_7.042.012cd">pratyuvāca mahābāhuṃ prāñjaliḥ susamāhitaḥ</li>
    </ul>
    <ul id="R_7.042.013">
        <li id="R_7.042.013ab">śṛṇu rājan yathā paurāḥ kathayanti śubhāśubham</li>
        <li id="R_7.042.013cd">catvarāpaṇarathyāsu vaneṣūpavaneṣu ca</li>
    </ul>
    <ul id="R_7.042.014">
        <li id="R_7.042.014ab">duṣkaraṃ kṛtavān rāmaḥ samudre setubandhanam</li>
        <li id="R_7.042.014cd">akṛtaṃ pūrvakaiḥ kaiś cid devair api sadānavaiḥ</li>
    </ul>
    <ul id="R_7.042.015">
        <li id="R_7.042.015ab">rāvaṇaś ca durādharṣo hataḥ sabalavāhanaḥ</li>
        <li id="R_7.042.015cd">vānarāś ca vaśaṃ nītā ṛkṣāś ca saha rākṣasaiḥ</li>
    </ul>
    <ul id="R_7.042.016">
        <li id="R_7.042.016ab">hatvā ca rāvaṇaṃ yuddhe sītām āhṛtya rāghavaḥ</li>
        <li id="R_7.042.016cd">amarṣaṃ pṛṣṭhataḥ kṛtvā svaveśma punar ānayat</li>
    </ul>
    <ul id="R_7.042.017">
        <li id="R_7.042.017ab">kīdṛśaṃ hṛdaye tasya sītāsaṃbhogajaṃ sukham</li>
        <li id="R_7.042.017cd">aṅkam āropya hi purā rāvaṇena balād dhṛtām</li>
    </ul>
    <ul id="R_7.042.018">
        <li id="R_7.042.018ab">laṅkām api punar nītām aśokavanikāṃ gatām</li>
        <li id="R_7.042.018cd">rakṣasāṃ vaśam āpannāṃ kathaṃ rāmo na kutsate</li>
    </ul>
    <ul id="R_7.042.019">
        <li id="R_7.042.019ab">asmākam api dāreṣu sahanīyaṃ bhaviṣyati</li>
        <li id="R_7.042.019cd">yathā hi kurute rājā prajā tam anuvartate</li>
    </ul>
    <ul id="R_7.042.020">
        <li id="R_7.042.020ab">evaṃ bahuvidhā vāco vadanti puravāsinaḥ</li>
        <li id="R_7.042.020cd">nagareṣu ca sarveṣu rājañ janapadeṣu ca</li>
    </ul>
    <ul id="R_7.042.021">
        <li id="R_7.042.021ab">tasyaitad bhāṣitaṃ śrutvā rāghavaḥ paramārtavat</li>
        <li id="R_7.042.021cd">uvāca sarvān suhṛdaḥ katham etan nivedyatām</li>
    </ul>
    <ul id="R_7.042.022">
        <li id="R_7.042.022ab">sarve tu śirasā bhūmāv abhivādya praṇamya ca</li>
        <li id="R_7.042.022cd">pratyūcū rāghavaṃ dīnam evam etan na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.042.023">
        <li id="R_7.042.023ab">śrutvā tu vākyaṃ kākutsthaḥ sarveṣāṃ samudīritam</li>
        <li id="R_7.042.023cd">visarjayām āsa tadā sarvāṃs tāñ śatrutāpanaḥ</li>
    </ul>
    <ul id="R_7.043.001">
        <li id="R_7.043.001ab">visṛjya tu suhṛdvargaṃ buddhyā niścitya rāghavaḥ</li>
        <li id="R_7.043.001cd">samīpe dvāḥstham āsīnam idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.043.002">
        <li id="R_7.043.002ab">śīghram ānaya saumitriṃ lakṣmaṇaṃ śubhalakṣaṇam</li>
        <li id="R_7.043.002cd">bharataṃ ca mahābāhuṃ śatrughnaṃ cāparājitam</li>
    </ul>
    <ul id="R_7.043.003">
        <li id="R_7.043.003ab">rāmasya bhāṣitaṃ śrutvā dvāḥstho mūrdhni kṛtāñjaliḥ</li>
        <li id="R_7.043.003cd">lakṣmaṇasya gṛhaṃ gatvā praviveśānivāritaḥ</li>
    </ul>
    <ul id="R_7.043.004">
        <li id="R_7.043.004ab">uvāca ca tadā vākyaṃ vardhayitvā kṛtāñjaliḥ</li>
        <li id="R_7.043.004cd">draṣṭum icchati rājā tvāṃ gamyatāṃ tatra mā ciram</li>
    </ul>
    <ul id="R_7.043.005">
        <li id="R_7.043.005ab">bāḍham ity eva saumitriḥ śrutvā rāghavaśāsanam</li>
        <li id="R_7.043.005cd">prādravad ratham āruhya rāghavasya niveśanam</li>
    </ul>
    <ul id="R_7.043.006">
        <li id="R_7.043.006ab">prayāntaṃ lakṣmaṇaṃ dṛṣṭvā dvāḥstho bharatam antikāt</li>
        <li id="R_7.043.006cd">uvāca prāñjalir vākyaṃ rājā tvāṃ draṣṭum icchati</li>
    </ul>
    <ul id="R_7.043.007">
        <li id="R_7.043.007ab">bharatas tu vacaḥ śrutvā dvāḥsthād rāmasamīritam</li>
        <li id="R_7.043.007cd">utpapātāsanāt tūrṇaṃ padbhyām eva tato 'gamat</li>
    </ul>
    <ul id="R_7.043.008">
        <li id="R_7.043.008ab">dṛṣṭvā prayāntaṃ bharataṃ tvaramāṇaḥ kṛtāñjaliḥ</li>
        <li id="R_7.043.008cd">śatrughnabhavanaṃ gatvā tato vākyaṃ jagāda ha</li>
    </ul>
    <ul id="R_7.043.009">
        <li id="R_7.043.009ab">ehy āgaccha raghuśreṣṭha rājā tvāṃ draṣṭum icchati</li>
        <li id="R_7.043.009cd">gato hi lakṣmaṇaḥ pūrvaṃ bharataś ca mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.043.010">
        <li id="R_7.043.010ab">śrutvā tu vacanaṃ tasya śatrughno rāmaśāsanam</li>
        <li id="R_7.043.010cd">śirasā vandya dharaṇīṃ prayayau yatra rāghavaḥ</li>
    </ul>
    <ul id="R_7.043.011">
        <li id="R_7.043.011ab">kumārān āgatāñ śrutvā cintāvyākulitendriyaḥ</li>
        <li id="R_7.043.011cd">avākśirā dīnamanā dvāḥsthaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.043.012">
        <li id="R_7.043.012ab">praveśaya kumārāṃs tvaṃ matsamīpaṃ tvarānvitaḥ</li>
        <li id="R_7.043.012cd">eteṣu jīvitaṃ mahyam ete prāṇā bahiścarāḥ</li>
    </ul>
    <ul id="R_7.043.013">
        <li id="R_7.043.013ab">ājñaptās tu narendreṇa kumārāḥ śuklavāsasaḥ</li>
        <li id="R_7.043.013cd">prahvāḥ prāñjalayo bhūtvā viviśus te samāhitāḥ</li>
    </ul>
    <ul id="R_7.043.014">
        <li id="R_7.043.014ab">te tu dṛṣṭvā mukhaṃ tasya sagrahaṃ śaśinaṃ yathā</li>
        <li id="R_7.043.014cd">saṃdhyāgatam ivādityaṃ prabhayā parivarjitam</li>
    </ul>
    <ul id="R_7.043.015">
        <li id="R_7.043.015ab">bāṣpapūrṇe ca nayane dṛṣṭvā rāmasya dhīmataḥ</li>
        <li id="R_7.043.015cd">hataśobhaṃ yathā padmaṃ mukhaṃ vīkṣya ca tasya te</li>
    </ul>
    <ul id="R_7.043.016">
        <li id="R_7.043.016ab">tato 'bhivādya tvaritāḥ pādau rāmasya mūrdhabhiḥ</li>
        <li id="R_7.043.016cd">tasthuḥ samāhitāḥ sarve rāmaś cāśrūṇy avartayat</li>
    </ul>
    <ul id="R_7.043.017">
        <li id="R_7.043.017ab">tān pariṣvajya bāhubhyām utthāpya ca mahābhujaḥ</li>
        <li id="R_7.043.017cd">āsaneṣv ādhvam ity uktvā tato vākyaṃ jagāda ha</li>
    </ul>
    <ul id="R_7.043.018">
        <li id="R_7.043.018ab">bhavanto mama sarvasvaṃ bhavanto mama jīvitam</li>
        <li id="R_7.043.018cd">bhavadbhiś ca kṛtaṃ rājyaṃ pālayāmi nareśvarāḥ</li>
    </ul>
    <ul id="R_7.043.019">
        <li id="R_7.043.019ab">bhavantaḥ kṛtaśāstrārthā buddhau ca pariniṣṭhitāḥ</li>
        <li id="R_7.043.019cd">saṃbhūya ca madartho 'yam anveṣṭavyo nareśvarāḥ</li>
    </ul>
    <ul id="R_7.044.001">
        <li id="R_7.044.001ab">teṣāṃ samupaviṣṭānāṃ sarveṣāṃ dīnacetasām</li>
        <li id="R_7.044.001cd">uvāca vākyaṃ kākutstho mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_7.044.002">
        <li id="R_7.044.002ab">sarve śṛṇuta bhadraṃ vo mā kurudhvaṃ mano 'nyathā</li>
        <li id="R_7.044.002cd">paurāṇāṃ mama sītāyāṃ yādṛśī vartate kathā</li>
    </ul>
    <ul id="R_7.044.003">
        <li id="R_7.044.003ab">paurāpavādaḥ sumahāṃs tathā janapadasya ca</li>
        <li id="R_7.044.003cd">vartate mayi bībhatsaḥ sa me marmāṇi kṛntati</li>
    </ul>
    <ul id="R_7.044.004">
        <li id="R_7.044.004ab">ahaṃ kila kule jāta ikṣvākūṇāṃ mahātmanām</li>
        <li id="R_7.044.004cd">sītāṃ pāpasamācārām ānayeyaṃ kathaṃ pure</li>
    </ul>
    <ul id="R_7.044.005">
        <li id="R_7.044.005ab">jānāsi hi yathā saumya daṇḍake vijane vane</li>
        <li id="R_7.044.005cd">rāvaṇena hṛtā sītā sa ca vidhvaṃsito mayā</li>
    </ul>
    <ul id="R_7.044.006">
        <li id="R_7.044.006ab">pratyakṣaṃ tava saumitre devānāṃ havyavāhanaḥ</li>
        <li id="R_7.044.006cd">apāpāṃ maithilīm āha vāyuś cākāśagocaraḥ</li>
    </ul>
    <ul id="R_7.044.007">
        <li id="R_7.044.007ab">candrādityau ca śaṃsete surāṇāṃ saṃnidhau purā</li>
        <li id="R_7.044.007cd">ṛṣīṇāṃ caiva sarveṣām apāpāṃ janakātmajām</li>
    </ul>
    <ul id="R_7.044.008">
        <li id="R_7.044.008ab">evaṃ śuddhasamācārā devagandharvasaṃnidhau</li>
        <li id="R_7.044.008cd">laṅkādvīpe mahendreṇa mama haste niveśitā</li>
    </ul>
    <ul id="R_7.044.009">
        <li id="R_7.044.009ab">antarātmā ca me vetti sītāṃ śuddhāṃ yaśasvinīm</li>
        <li id="R_7.044.009cd">tato gṛhītvā vaidehīm ayodhyām aham āgataḥ</li>
    </ul>
    <ul id="R_7.044.010">
        <li id="R_7.044.010ab">ayaṃ tu me mahān vādaḥ śokaś ca hṛdi vartate</li>
        <li id="R_7.044.010cd">paurāpavādaḥ sumahāṃs tathā janapadasya ca</li>
    </ul>
    <ul id="R_7.044.011">
        <li id="R_7.044.011ab">akīrtir yasya gīyeta loke bhūtasya kasya cit</li>
        <li id="R_7.044.011cd">pataty evādhamāṃl lokān yāvac chabdaḥ sa kīrtyate</li>
    </ul>
    <ul id="R_7.044.012">
        <li id="R_7.044.012ab">akīrtir nindyate daivaiḥ kīrtir deveṣu pūjyate</li>
        <li id="R_7.044.012cd">kīrtyarthaṃ ca samārambhaḥ sarva eva mahātmanām</li>
    </ul>
    <ul id="R_7.044.013">
        <li id="R_7.044.013ab">apy ahaṃ jīvitaṃ jahyāṃ yuṣmān vā puruṣarṣabhāḥ</li>
        <li id="R_7.044.013cd">apavādabhayād bhītaḥ kiṃ punar janakātmajām</li>
    </ul>
    <ul id="R_7.044.014">
        <li id="R_7.044.014ab">tasmād bhavantaḥ paśyantu patitaṃ śokasāgare</li>
        <li id="R_7.044.014cd">na hi paśyāmy ahaṃ bhūyaḥ kiṃ cid duḥkham ato 'dhikam</li>
    </ul>
    <ul id="R_7.044.015">
        <li id="R_7.044.015ab">śvas tvaṃ prabhāte saumitre sumantrādhiṣṭhitaṃ ratham</li>
        <li id="R_7.044.015cd">āruhya sītām āropya viṣayānte samutsṛja</li>
    </ul>
    <ul id="R_7.044.016">
        <li id="R_7.044.016ab">gaṅgāyās tu pare pāre vālmīkeḥ sumahātmanaḥ</li>
        <li id="R_7.044.016cd">āśramo divyasaṃkāśas tamasātīram āśritaḥ</li>
    </ul>
    <ul id="R_7.044.017">
        <li id="R_7.044.017ab">tatraināṃ vijane kakṣe visṛjya raghunandana</li>
        <li id="R_7.044.017cd">śīghram āgaccha saumitre kuruṣva vacanaṃ mama</li>
    </ul>
    <ul id="R_7.044.018">
        <li id="R_7.044.018ab">na cāsmi prativaktavyaḥ sītāṃ prati kathaṃ cana</li>
        <li id="R_7.044.018cd">aprītiḥ paramā mahyaṃ bhavet tu prativārite</li>
    </ul>
    <ul id="R_7.044.019">
        <li id="R_7.044.019ab">śāpitāś ca mayā yūyaṃ bhujābhyāṃ jīvitena ca</li>
        <li id="R_7.044.019cd">ye māṃ vākyāntare brūyur anunetuṃ kathaṃ cana</li>
    </ul>
    <ul id="R_7.044.020">
        <li id="R_7.044.020ab">mānayantu bhavanto māṃ yadi macchāsane sthitāḥ</li>
        <li id="R_7.044.020cd">ito 'dya nīyatāṃ sītā kuruṣva vacanaṃ mama</li>
    </ul>
    <ul id="R_7.044.021">
        <li id="R_7.044.021ab">pūrvam ukto 'ham anayā gaṅgātīre mahāśramān</li>
        <li id="R_7.044.021cd">paśyeyam iti tasyāś ca kāmaḥ saṃvartyatām ayam</li>
    </ul>
    <ul id="R_7.044.022">
        <li id="R_7.044.022ab">evam uktvā tu kākutstho bāṣpeṇa pihitekṣaṇaḥ</li>
        <li id="R_7.044.022cd">praviveśa sa dharmātmā bhrātṛbhiḥ parivāritaḥ</li>
    </ul>
    <ul id="R_7.045.001">
        <li id="R_7.045.001ab">tato rajanyāṃ vyuṣṭāyāṃ lakṣmaṇo dīnacetanaḥ</li>
        <li id="R_7.045.001cd">sumantram abravīd vākyaṃ mukhena pariśuṣyatā</li>
    </ul>
    <ul id="R_7.045.002">
        <li id="R_7.045.002ab">sārathe turagāñ śīghraṃ yojayasva rathottame</li>
        <li id="R_7.045.002cd">svāstīrṇaṃ rājabhavanāt sītāyāś cāsanaṃ śubham</li>
    </ul>
    <ul id="R_7.045.003">
        <li id="R_7.045.003ab">sītā hi rājabhavanād āśramaṃ puṇyakarmaṇām</li>
        <li id="R_7.045.003cd">mayā neyā maharṣīṇāṃ śīghram ānīyatāṃ rathaḥ</li>
    </ul>
    <ul id="R_7.045.004">
        <li id="R_7.045.004ab">sumantras tu tathety uktvā yuktaṃ paramavājibhiḥ</li>
        <li id="R_7.045.004cd">rathaṃ suruciraprakhyaṃ svāstīrṇaṃ sukhaśayyayā</li>
    </ul>
    <ul id="R_7.045.005">
        <li id="R_7.045.005ab">ādāyovāca saumitriṃ mitrāṇāṃ harṣavardhanam</li>
        <li id="R_7.045.005cd">ratho 'yaṃ samanuprāpto yat kāryaṃ kriyatāṃ prabho</li>
    </ul>
    <ul id="R_7.045.006">
        <li id="R_7.045.006ab">evam uktaḥ sumantreṇa rājaveśma sa lakṣmaṇaḥ</li>
        <li id="R_7.045.006cd">praviśya sītām āsādya vyājahāra nararṣabhaḥ</li>
    </ul>
    <ul id="R_7.045.007">
        <li id="R_7.045.007ab">gaṅgātīre mayā devi munīnām āśrame śubhe</li>
        <li id="R_7.045.007cd">śīghraṃ gatvopaneyāsi śāsanāt pārthivasya naḥ</li>
    </ul>
    <ul id="R_7.045.008">
        <li id="R_7.045.008ab">evam uktā tu vaidehī lakṣmaṇena mahātmanā</li>
        <li id="R_7.045.008cd">praharṣam atulaṃ lebhe gamanaṃ cābhyarocayat</li>
    </ul>
    <ul id="R_7.045.009">
        <li id="R_7.045.009ab">vāsāṃsi ca mahārhāṇi ratnāni vividhāni ca</li>
        <li id="R_7.045.009cd">gṛhītvā tāni vaidehī gamanāyopacakrame</li>
    </ul>
    <ul id="R_7.045.010">
        <li id="R_7.045.010ab">imāni munipatnīnāṃ dāsyāmy ābharaṇāny aham</li>
        <li id="R_7.045.010cd">saumitris tu tathety uktvā ratham āropya maithilīm</li>
        <li id="R_7.045.010ef">prayayau śīghraturago rāmasyājñām anusmaran</li>
    </ul>
    <ul id="R_7.045.011">
        <li id="R_7.045.011ab">abravīc ca tadā sītā lakṣmaṇaṃ lakṣmivardhanam</li>
        <li id="R_7.045.011cd">aśubhāni bahūny adya paśyāmi raghunandana</li>
    </ul>
    <ul id="R_7.045.012">
        <li id="R_7.045.012ab">nayanaṃ me sphuraty adya gātrotkampaś ca jāyate</li>
        <li id="R_7.045.012cd">hṛdayaṃ caiva saumitre asvastham iva lakṣaye</li>
    </ul>
    <ul id="R_7.045.013">
        <li id="R_7.045.013ab">autsukyaṃ paramaṃ cāpi adhṛtiś ca parā mama</li>
        <li id="R_7.045.013cd">śūnyām iva ca paśyāmi pṛthivīṃ pṛthulocana</li>
    </ul>
    <ul id="R_7.045.014">
        <li id="R_7.045.014ab">api svasti bhavet tasya bhrātus te bhrātṛbhiḥ saha</li>
        <li id="R_7.045.014cd">śvaśrūṇāṃ caiva me vīra sarvāsām aviśeṣataḥ</li>
    </ul>
    <ul id="R_7.045.015">
        <li id="R_7.045.015ab">pure janapade caiva kuśalaṃ prāṇinām api</li>
        <li id="R_7.045.015cd">ity añjalikṛtā sītā devatā abhyayācata</li>
    </ul>
    <ul id="R_7.045.016">
        <li id="R_7.045.016ab">lakṣmaṇo 'rthaṃ tu taṃ śrutvā śirasā vandya maithilīm</li>
        <li id="R_7.045.016cd">śivam ity abravīd dhṛṣṭo hṛdayena viśuṣyatā</li>
    </ul>
    <ul id="R_7.045.017">
        <li id="R_7.045.017ab">tato vāsam upāgamya gomatītīra āśrame</li>
        <li id="R_7.045.017cd">prabhāte punar utthāya saumitriḥ sūtam abravīt</li>
    </ul>
    <ul id="R_7.045.018">
        <li id="R_7.045.018ab">yojayasva rathaṃ śīghram adya bhāgīrathījalam</li>
        <li id="R_7.045.018cd">śirasā dhārayiṣyāmi tryambakaḥ parvate yathā</li>
    </ul>
    <ul id="R_7.045.019">
        <li id="R_7.045.019ab">so 'śvān vicārayitvāśu rathe yuktvā manojavān</li>
        <li id="R_7.045.019cd">ārohasveti vaidehīṃ sūtaḥ prāñjalir abravīt</li>
    </ul>
    <ul id="R_7.045.020">
        <li id="R_7.045.020ab">sā tu sūtasya vacanād āruroha rathottamam</li>
        <li id="R_7.045.020cd">sītā saumitriṇā sārdhaṃ sumantreṇa ca dhīmatā</li>
    </ul>
    <ul id="R_7.045.021">
        <li id="R_7.045.021ab">athārdhadivasaṃ gatvā bhāgīrathyā jalāśayam</li>
        <li id="R_7.045.021cd">nirīkṣya lakṣmaṇo dīnaḥ praruroda mahāsvanam</li>
    </ul>
    <ul id="R_7.045.022">
        <li id="R_7.045.022ab">sītā tu paramāyattā dṛṣṭvā lakṣmaṇam āturam</li>
        <li id="R_7.045.022cd">uvāca vākyaṃ dharmajña kim idaṃ rudyate tvayā</li>
    </ul>
    <ul id="R_7.045.023">
        <li id="R_7.045.023ab">jāhnavītīram āsādya cirābhilaṣitaṃ mama</li>
        <li id="R_7.045.023cd">harṣakāle kim arthaṃ māṃ viṣādayasi lakṣmaṇa</li>
    </ul>
    <ul id="R_7.045.024">
        <li id="R_7.045.024ab">nityaṃ tvaṃ rāmapādeṣu vartase puruṣarṣabha</li>
        <li id="R_7.045.024cd">kaccid vinākṛtas tena dvirātre śokam āgataḥ</li>
    </ul>
    <ul id="R_7.045.025">
        <li id="R_7.045.025ab">mamāpi dayito rāmo jīvitenāpi lakṣmaṇa</li>
        <li id="R_7.045.025cd">na cāham evaṃ śocāmi maivaṃ tvaṃ bāliśo bhava</li>
    </ul>
    <ul id="R_7.045.026">
        <li id="R_7.045.026ab">tārayasva ca māṃ gaṅgāṃ darśayasva ca tāpasān</li>
        <li id="R_7.045.026cd">tato dhanāni vāsāṃsi dāsyāmy ābharaṇāni ca</li>
    </ul>
    <ul id="R_7.045.027">
        <li id="R_7.045.027ab">tataḥ kṛtvā maharṣīṇāṃ yathārham abhivādanam</li>
        <li id="R_7.045.027cd">tatra caikāṃ niśām uṣya yāsyāmas tāṃ purīṃ punaḥ</li>
    </ul>
    <ul id="R_7.045.028">
        <li id="R_7.045.028ab">tasyās tad vacanaṃ śrutvā pramṛjya nayane śubhe</li>
        <li id="R_7.045.028cd">titīrṣur lakṣmaṇo gaṅgāṃ śubhāṃ nāvam upāharat</li>
    </ul>
    <ul id="R_7.046.001">
        <li id="R_7.046.001ab">atha nāvaṃ suvistīrṇāṃ naiṣādīṃ rāghavānujaḥ</li>
        <li id="R_7.046.001cd">āruroha samāyuktāṃ pūrvam āropya maithilīm</li>
    </ul>
    <ul id="R_7.046.002">
        <li id="R_7.046.002ab">sumantraṃ caiva sarathaṃ sthīyatām iti lakṣmaṇaḥ</li>
        <li id="R_7.046.002cd">uvāca śokasaṃtaptaḥ prayāhīti ca nāvikam</li>
    </ul>
    <ul id="R_7.046.003">
        <li id="R_7.046.003ab">tatas tīram upāgamya bhāgīrathyāḥ sa lakṣmaṇaḥ</li>
        <li id="R_7.046.003cd">uvāca maithilīṃ vākyaṃ prāñjalir bāṣpagadgadaḥ</li>
    </ul>
    <ul id="R_7.046.004">
        <li id="R_7.046.004ab">hṛdgataṃ me mahac chalyaṃ yad asmy āryeṇa dhīmatā</li>
        <li id="R_7.046.004cd">asmin nimitte vaidehi lokasya vacanīkṛtaḥ</li>
    </ul>
    <ul id="R_7.046.005">
        <li id="R_7.046.005ab">śreyo hi maraṇaṃ me 'dya mṛtyor vā yat paraṃ bhavet</li>
        <li id="R_7.046.005cd">na cāsminn īdṛśe kārye niyojyo lokanindite</li>
    </ul>
    <ul id="R_7.046.006">
        <li id="R_7.046.006ab">prasīda na ca me roṣaṃ kartum arhasi suvrate</li>
        <li id="R_7.046.006cd">ity añjalikṛto bhūmau nipapāta sa lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_7.046.007">
        <li id="R_7.046.007ab">rudantaṃ prāñjaliṃ dṛṣṭvā kāṅkṣantaṃ mṛtyum ātmanaḥ</li>
        <li id="R_7.046.007cd">maithilī bhṛśasaṃvignā lakṣmaṇaṃ vākyam abravīt</li>
    </ul>
    <ul id="R_7.046.008">
        <li id="R_7.046.008ab">kim idaṃ nāvagacchāmi brūhi tattvena lakṣmaṇa</li>
        <li id="R_7.046.008cd">paśyāmi tvāṃ ca na svastham api kṣemaṃ mahīpateḥ</li>
    </ul>
    <ul id="R_7.046.009">
        <li id="R_7.046.009ab">śāpito 'si narendreṇa yat tvaṃ saṃtāpam ātmanaḥ</li>
        <li id="R_7.046.009cd">tad brūyāḥ saṃnidhau mahyam aham ājñāpayāmi te</li>
    </ul>
    <ul id="R_7.046.010">
        <li id="R_7.046.010ab">vaidehyā codyamānas tu lakṣmaṇo dīnacetanaḥ</li>
        <li id="R_7.046.010cd">avāṅmukho bāṣpagalo vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.046.011">
        <li id="R_7.046.011ab">śrutvā pariṣado madhye apavādaṃ sudāruṇam</li>
        <li id="R_7.046.011cd">pure janapade caiva tvatkṛte janakātmaje</li>
    </ul>
    <ul id="R_7.046.012">
        <li id="R_7.046.012ab">na tāni vacanīyāni mayā devi tavāgrataḥ</li>
        <li id="R_7.046.012cd">yāni rājñā hṛdi nyastāny amarṣaḥ pṛṣṭhataḥ kṛtaḥ</li>
    </ul>
    <ul id="R_7.046.013">
        <li id="R_7.046.013ab">sā tvaṃ tyaktā nṛpatinā nirdoṣā mama saṃnidhau</li>
        <li id="R_7.046.013cd">paurāpavādabhītena grāhyaṃ devi na te 'nyathā</li>
    </ul>
    <ul id="R_7.046.014">
        <li id="R_7.046.014ab">āśramānteṣu ca mayā tyaktavyā tvaṃ bhaviṣyasi</li>
        <li id="R_7.046.014cd">rājñaḥ śāsanam ājñāya tavaivaṃ kila daurhṛdam</li>
    </ul>
    <ul id="R_7.046.015">
        <li id="R_7.046.015ab">tad etaj jāhnavītīre brahmarṣīṇāṃ tapovanam</li>
        <li id="R_7.046.015cd">puṇyaṃ ca ramaṇīyaṃ ca mā viṣādaṃ kṛthāḥ śubhe</li>
    </ul>
    <ul id="R_7.046.016">
        <li id="R_7.046.016ab">rājño daśarathasyaiṣa pitur me munipuṃgavaḥ</li>
        <li id="R_7.046.016cd">sakhā paramako vipro vālmīkiḥ sumahāyaśāḥ</li>
    </ul>
    <ul id="R_7.046.017">
        <li id="R_7.046.017ab">pādacchāyām upāgamya sukham asya mahātmanaḥ</li>
        <li id="R_7.046.017cd">upavāsaparaikāgrā vasa tvaṃ janakātmaje</li>
    </ul>
    <ul id="R_7.046.018">
        <li id="R_7.046.018ab">pativratātvam āsthāya rāmaṃ kṛtvā sadā hṛdi</li>
        <li id="R_7.046.018cd">śreyas te paramaṃ devi tathā kṛtvā bhaviṣyati</li>
    </ul>
    <ul id="R_7.047.001">
        <li id="R_7.047.001ab">lakṣmaṇasya vacaḥ śrutvā dāruṇaṃ janakātmajā</li>
        <li id="R_7.047.001cd">paraṃ viṣādam āgamya vaidehī nipapāta ha</li>
    </ul>
    <ul id="R_7.047.002">
        <li id="R_7.047.002ab">sā muhūrtam ivāsaṃjñā bāṣpavyākulitekṣaṇā</li>
        <li id="R_7.047.002cd">lakṣmaṇaṃ dīnayā vācā uvāca janakātmajā</li>
    </ul>
    <ul id="R_7.047.003">
        <li id="R_7.047.003ab">māmikeyaṃ tanur nūnaṃ sṛṣṭā duḥkhāya lakṣmaṇa</li>
        <li id="R_7.047.003cd">dhātrā yasyās tathā me 'dya duḥkhamūrtiḥ pradṛśyate</li>
    </ul>
    <ul id="R_7.047.004">
        <li id="R_7.047.004ab">kiṃ nu pāpaṃ kṛtaṃ pūrvaṃ ko vā dārair viyojitaḥ</li>
        <li id="R_7.047.004cd">yāhaṃ śuddhasamācārā tyaktā nṛpatinā satī</li>
    </ul>
    <ul id="R_7.047.005">
        <li id="R_7.047.005ab">purāham āśrame vāsaṃ rāmapādānuvartinī</li>
        <li id="R_7.047.005cd">anurudhyāpi saumitre duḥkhe viparivartinī</li>
    </ul>
    <ul id="R_7.047.006">
        <li id="R_7.047.006ab">sā kathaṃ hy āśrame saumya vatsyāmi vijanīkṛtā</li>
        <li id="R_7.047.006cd">ākhyāsyāmi ca kasyāhaṃ duḥkhaṃ duḥkhaparāyaṇā</li>
    </ul>
    <ul id="R_7.047.007">
        <li id="R_7.047.007ab">kiṃ ca vakṣyāmi muniṣu kiṃ mayāpakṛtaṃ nṛpe</li>
        <li id="R_7.047.007cd">kasmin vā kāraṇe tyaktā rāghaveṇa mahātmanā</li>
    </ul>
    <ul id="R_7.047.008">
        <li id="R_7.047.008ab">na khalv adyaiva saumitre jīvitaṃ jāhnavījale</li>
        <li id="R_7.047.008cd">tyajeyaṃ rājavaṃśas tu bhartur me parihāsyate</li>
    </ul>
    <ul id="R_7.047.009">
        <li id="R_7.047.009ab">yathājñāṃ kuru saumitre tyaja māṃ duḥkhabhāginīm</li>
        <li id="R_7.047.009cd">nideśe sthīyatāṃ rājñaḥ śṛṇu cedaṃ vaco mama</li>
    </ul>
    <ul id="R_7.047.010">
        <li id="R_7.047.010ab">śvaśrūṇām aviśeṣeṇa prāñjaliḥ pragraheṇa ca</li>
        <li id="R_7.047.010cd">śirasā vandya caraṇau kuśalaṃ brūhi pārthivam</li>
    </ul>
    <ul id="R_7.047.011">
        <li id="R_7.047.011ab">yathā bhrātṛṣu vartethās tathā paureṣu nityadā</li>
        <li id="R_7.047.011cd">paramo hy eṣa dharmaḥ syād eṣā kīrtir anuttamā</li>
    </ul>
    <ul id="R_7.047.012">
        <li id="R_7.047.012ab">yat tvaṃ paurajanaṃ rājan dharmeṇa samavāpnuyāḥ</li>
        <li id="R_7.047.012cd">ahaṃ tu nānuśocāmi svaśarīraṃ nararṣabha</li>
        <li id="R_7.047.012ef">yathāpavādaṃ paurāṇāṃ tathaiva raghunandana</li>
    </ul>
    <ul id="R_7.047.013">
        <li id="R_7.047.013ab">evaṃ bruvantyāṃ sītāyāṃ lakṣmaṇo dīnacetanaḥ</li>
        <li id="R_7.047.013cd">śirasā dharaṇīṃ gatvā vyāhartuṃ na śaśāka ha</li>
    </ul>
    <ul id="R_7.047.014">
        <li id="R_7.047.014ab">pradakṣiṇaṃ ca kṛtvā sa rudann eva mahāsvanam</li>
        <li id="R_7.047.014cd">āruroha punar nāvaṃ nāvikaṃ cābhyacodayat</li>
    </ul>
    <ul id="R_7.047.015">
        <li id="R_7.047.015ab">sa gatvā cottaraṃ kūlaṃ śokabhārasamanvitaḥ</li>
        <li id="R_7.047.015cd">saṃmūḍha iva duḥkhena ratham adhyāruhad drutam</li>
    </ul>
    <ul id="R_7.047.016">
        <li id="R_7.047.016ab">muhur muhur apāvṛtya dṛṣṭvā sītām anāthavat</li>
        <li id="R_7.047.016cd">veṣṭantīṃ paratīrasthāṃ lakṣmaṇaḥ prayayāv atha</li>
    </ul>
    <ul id="R_7.047.017">
        <li id="R_7.047.017ab">dūrasthaṃ ratham ālokya lakṣmaṇaṃ ca muhur muhuḥ</li>
        <li id="R_7.047.017cd">nirīkṣamāṇām udvignāṃ sītāṃ śokaḥ samāviśat</li>
    </ul>
    <ul id="R_7.047.018">
        <li id="R_7.047.018ab">
            <seg type="pāda" n="a">sā duḥkhabhārāvanatā tapasvinī</seg>
            <seg type="pāda" n="b">yaśodharā nātham apaśyatī satī</seg>
        </li>
        <li id="R_7.047.018cd">
            <seg type="pāda" n="c">ruroda sā barhiṇanādite vane</seg>
            <seg type="pāda" n="d">mahāsvanaṃ duḥkhaparāyaṇā satī</seg>
        </li>
    </ul>
    <ul id="R_7.048.001">
        <li id="R_7.048.001ab">sītāṃ tu rudatīṃ dṛṣṭvā ye tatra munidārakāḥ</li>
        <li id="R_7.048.001cd">prādravan yatra bhagavān āste vālmīkir agryadhīḥ</li>
    </ul>
    <ul id="R_7.048.002">
        <li id="R_7.048.002ab">abhivādya muneḥ pādau muniputrā maharṣaye</li>
        <li id="R_7.048.002cd">sarve nivedayām āsus tasyās tu ruditasvanam</li>
    </ul>
    <ul id="R_7.048.003">
        <li id="R_7.048.003ab">adṛṣṭapūrvā bhagavan kasyāpy eṣā mahātmanaḥ</li>
        <li id="R_7.048.003cd">patnī śrīr iva saṃmohād virauti vikṛtasvarā</li>
    </ul>
    <ul id="R_7.048.004">
        <li id="R_7.048.004ab">bhagavan sādhu paśyemāṃ devatām iva khāc cyutām</li>
        <li id="R_7.048.004cd">na hy enāṃ mānuṣīṃ vidmaḥ satkriyāsyāḥ prayujyatām</li>
    </ul>
    <ul id="R_7.048.005">
        <li id="R_7.048.005ab">teṣāṃ tad vacanaṃ śrutvā buddhyā niścitya dharmavit</li>
        <li id="R_7.048.005cd">tapasā labdhacakṣuṣmān prādravad yatra maithilī</li>
    </ul>
    <ul id="R_7.048.006">
        <li id="R_7.048.006ab">taṃ tu deśam abhipretya kiṃ cit padbhyāṃ mahāmuniḥ</li>
        <li id="R_7.048.006cd">arghyam ādāya ruciraṃ jāhvanītīram āśritaḥ</li>
        <li id="R_7.048.006ef">dadarśa rāghavasyeṣṭāṃ patnīṃ sītām anāthavat</li>
    </ul>
    <ul id="R_7.048.007">
        <li id="R_7.048.007ab">tāṃ sītāṃ śokabhārārtāṃ vālmīkir munipuṃgavaḥ</li>
        <li id="R_7.048.007cd">uvāca madhurāṃ vāṇīṃ hlādayann iva tejasā</li>
    </ul>
    <ul id="R_7.048.008">
        <li id="R_7.048.008ab">snuṣā daśarathasya tvaṃ rāmasya mahiṣī satī</li>
        <li id="R_7.048.008cd">janakasya sutā rājñaḥ svāgataṃ te pativrate</li>
    </ul>
    <ul id="R_7.048.009">
        <li id="R_7.048.009ab">āyānty evāsi vijñātā mayā dharmasamādhinā</li>
        <li id="R_7.048.009cd">kāraṇaṃ caiva sarvaṃ me hṛdayenopalakṣitam</li>
    </ul>
    <ul id="R_7.048.010">
        <li id="R_7.048.010ab">apāpāṃ vedmi sīte tvāṃ tapolabdhena cakṣuṣā</li>
        <li id="R_7.048.010cd">viśuddhabhāvā vaidehi sāmprataṃ mayi vartase</li>
    </ul>
    <ul id="R_7.048.011">
        <li id="R_7.048.011ab">āśramasyāvidūre me tāpasyas tapasi sthitāḥ</li>
        <li id="R_7.048.011cd">tās tvāṃ vatse yathā vatsaṃ pālayiṣyanti nityaśaḥ</li>
    </ul>
    <ul id="R_7.048.012">
        <li id="R_7.048.012ab">idam arghyaṃ pratīccha tvaṃ visrabdhā vigatajvarā</li>
        <li id="R_7.048.012cd">yathā svagṛham abhyetya viṣādaṃ caiva mā kṛthāḥ</li>
    </ul>
    <ul id="R_7.048.013">
        <li id="R_7.048.013ab">śrutvā tu bhāṣitaṃ sītā muneḥ paramam adbhutam</li>
        <li id="R_7.048.013cd">śirasā vandya caraṇau tathety āha kṛtāñjaliḥ</li>
    </ul>
    <ul id="R_7.048.014">
        <li id="R_7.048.014ab">taṃ prayāntaṃ muniṃ sītā prāñjaliḥ pṛṣṭhato 'nvagāt</li>
        <li id="R_7.048.014cd">anvayād yatra tāpasyo dharmanityāḥ samāhitāḥ</li>
    </ul>
    <ul id="R_7.048.015">
        <li id="R_7.048.015ab">taṃ dṛṣṭvā munim āyāntaṃ vaidehyānugataṃ tadā</li>
        <li id="R_7.048.015cd">upājagmur mudā yuktā vacanaṃ cedam abruvan</li>
    </ul>
    <ul id="R_7.048.016">
        <li id="R_7.048.016ab">svāgataṃ te muniśreṣṭha cirasyāgamanaṃ prabho</li>
        <li id="R_7.048.016cd">abhivādayāmaḥ sarvās tvām ucyatāṃ kiṃ ca kurmahe</li>
    </ul>
    <ul id="R_7.048.017">
        <li id="R_7.048.017ab">tāsāṃ tad vacanaṃ śrutvā vālmīkir idam abravīt</li>
        <li id="R_7.048.017cd">sīteyaṃ samanuprāptā patnī rāmasya dhīmataḥ</li>
    </ul>
    <ul id="R_7.048.018">
        <li id="R_7.048.018ab">snuṣā daśarathasyaiṣā janakasya sutā satī</li>
        <li id="R_7.048.018cd">apāpā patinā tyaktā paripālyā mayā sadā</li>
    </ul>
    <ul id="R_7.048.019">
        <li id="R_7.048.019ab">imāṃ bhavatyaḥ paśyantu snehena parameṇa ha</li>
        <li id="R_7.048.019cd">gauravān mama vākyasya pūjyā vo 'stu viśeṣataḥ</li>
    </ul>
    <ul id="R_7.048.020">
        <li id="R_7.048.020ab">muhur muhuś ca vaidehīṃ parisāntvya mahāyaśāḥ</li>
        <li id="R_7.048.020cd">svam āśramaṃ śiṣyavṛtaḥ punar āyān mahātapāḥ</li>
    </ul>
    <ul id="R_7.049.001">
        <li id="R_7.049.001ab">dṛṣṭvā tu maithilīṃ sītām āśramaṃ saṃpraveśitām</li>
        <li id="R_7.049.001cd">saṃtāpam akarod ghoraṃ lakṣmaṇo dīnacetanaḥ</li>
    </ul>
    <ul id="R_7.049.002">
        <li id="R_7.049.002ab">abravīc ca mahātejāḥ sumantraṃ mantrasārathim</li>
        <li id="R_7.049.002cd">sītāsaṃtāpajaṃ duḥkhaṃ paśya rāmasya dhīmataḥ</li>
    </ul>
    <ul id="R_7.049.003">
        <li id="R_7.049.003ab">ato duḥkhataraṃ kiṃ nu rāghavasya bhaviṣyati</li>
        <li id="R_7.049.003cd">patnīṃ śuddhasamācārāṃ visṛjya janakātmajām</li>
    </ul>
    <ul id="R_7.049.004">
        <li id="R_7.049.004ab">vyaktaṃ daivād ahaṃ manye rāghavasya vinābhavam</li>
        <li id="R_7.049.004cd">vaidehyā sārathe sārdhaṃ daivaṃ hi duratikramam</li>
    </ul>
    <ul id="R_7.049.005">
        <li id="R_7.049.005ab">yo hi devān sagandharvān asurān saha rākṣasaiḥ</li>
        <li id="R_7.049.005cd">nihanyād rāghavaḥ kruddhaḥ sa daivam anuvartate</li>
    </ul>
    <ul id="R_7.049.006">
        <li id="R_7.049.006ab">purā mama pitur vākyair daṇḍake vijane vane</li>
        <li id="R_7.049.006cd">uṣito navavarṣāṇi pañca caiva sudāruṇe</li>
    </ul>
    <ul id="R_7.049.007">
        <li id="R_7.049.007ab">tato duḥkhataraṃ bhūyaḥ sītāyā vipravāsanam</li>
        <li id="R_7.049.007cd">paurāṇāṃ vacanaṃ śrutvā nṛśaṃsaṃ pratibhāti me</li>
    </ul>
    <ul id="R_7.049.008">
        <li id="R_7.049.008ab">ko nu dharmāśrayaḥ sūta karmaṇy asmin yaśohare</li>
        <li id="R_7.049.008cd">maithilīṃ prati saṃprāptaḥ paurair hīnārthavādibhiḥ</li>
    </ul>
    <ul id="R_7.049.009">
        <li id="R_7.049.009ab">etā bahuvidhā vācaḥ śrutvā lakṣmaṇabhāṣitāḥ</li>
        <li id="R_7.049.009cd">sumantraḥ prāñjalir bhūtvā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.049.010">
        <li id="R_7.049.010ab">na saṃtāpas tvayā kāryaḥ saumitre maithilīṃ prati</li>
        <li id="R_7.049.010cd">dṛṣṭam etat purā vipraiḥ pitus te lakṣmaṇāgrataḥ</li>
    </ul>
    <ul id="R_7.049.011">
        <li id="R_7.049.011ab">bhaviṣyati dṛḍhaṃ rāmo duḥkhaprāyo 'lpasaukhyavān</li>
        <li id="R_7.049.011cd">tvāṃ caiva maithilīṃ caiva śatrughnabharatau tathā</li>
        <li id="R_7.049.011ef">saṃtyajiṣyati dharmātmā kālena mahatā mahān</li>
    </ul>
    <ul id="R_7.049.012">
        <li id="R_7.049.012ab">na tv idaṃ tvayi vaktavyaṃ saumitre bharate 'pi vā</li>
        <li id="R_7.049.012cd">rājñā vo 'vyāhṛtaṃ vākyaṃ durvāsā yad uvāca ha</li>
    </ul>
    <ul id="R_7.049.013">
        <li id="R_7.049.013ab">mahārājasamīpe ca mama caiva nararṣabha</li>
        <li id="R_7.049.013cd">ṛṣiṇā vyāhṛtaṃ vākyaṃ vasiṣṭhasya ca saṃnidhau</li>
    </ul>
    <ul id="R_7.049.014">
        <li id="R_7.049.014ab">ṛṣes tu vacanaṃ śrutvā mām āha puruṣarṣabhaḥ</li>
        <li id="R_7.049.014cd">sūta na kva cid evaṃ te vaktavyaṃ janasaṃnidhau</li>
    </ul>
    <ul id="R_7.049.015">
        <li id="R_7.049.015ab">tasyāhaṃ lokapālasya vākyaṃ tat susamāhitaḥ</li>
        <li id="R_7.049.015cd">naiva jātv anṛtaṃ kuryām iti me saumya darśanam</li>
    </ul>
    <ul id="R_7.049.016">
        <li id="R_7.049.016ab">sarvathā nāsty avaktavyaṃ mayā saumya tavāgrataḥ</li>
        <li id="R_7.049.016cd">yadi te śravaṇe śraddhā śrūyatāṃ raghunandana</li>
    </ul>
    <ul id="R_7.049.017">
        <li id="R_7.049.017ab">yady apy ahaṃ narendreṇa rahasyaṃ śrāvitaḥ purā</li>
        <li id="R_7.049.017cd">tac cāpy udāhariṣyāmi daivaṃ hi duratikramam</li>
    </ul>
    <ul id="R_7.049.018">
        <li id="R_7.049.018ab">tac chrutvā bhāṣitaṃ tasya gambhīrārthapadaṃ mahat</li>
        <li id="R_7.049.018cd">tathyaṃ brūhīti saumitriḥ sūtaṃ vākyam athābravīt</li>
    </ul>
    <ul id="R_7.050.001">
        <li id="R_7.050.001ab">tathā saṃcoditaḥ sūto lakṣmaṇena mahātmanā</li>
        <li id="R_7.050.001cd">tad vākyam ṛṣiṇā proktaṃ vyāhartum upacakrame</li>
    </ul>
    <ul id="R_7.050.002">
        <li id="R_7.050.002ab">purā nāmnā hi durvāsā atreḥ putro mahāmuniḥ</li>
        <li id="R_7.050.002cd">vasiṣṭhasyāśrame puṇye sa vārṣikyam uvāsa ha</li>
    </ul>
    <ul id="R_7.050.003">
        <li id="R_7.050.003ab">tam āśramaṃ mahātejāḥ pitā te sumahāyaśāḥ</li>
        <li id="R_7.050.003cd">purodhasaṃ mahātmānaṃ didṛkṣur agamat svayam</li>
    </ul>
    <ul id="R_7.050.004">
        <li id="R_7.050.004ab">sa dṛṣṭvā sūryasaṃkāśaṃ jvalantam iva tejasā</li>
        <li id="R_7.050.004cd">upaviṣṭaṃ vasiṣṭhasya savye pārśve mahāmunim</li>
        <li id="R_7.050.004ef">tau munī tāpasaśreṣṭhau vinītas tv abhyavādayat</li>
    </ul>
    <ul id="R_7.050.005">
        <li id="R_7.050.005ab">sa tābhyāṃ pūjito rājā svāgatenāsanena ca</li>
        <li id="R_7.050.005cd">pādyena phalamūlaiś ca so 'py āste munibhiḥ saha</li>
    </ul>
    <ul id="R_7.050.006">
        <li id="R_7.050.006ab">teṣāṃ tatropaviṣṭānāṃ tās tāḥ sumadhurāḥ kathāḥ</li>
        <li id="R_7.050.006cd">babhūvuḥ paramarṣīṇāṃ madhyādityagate 'hani</li>
    </ul>
    <ul id="R_7.050.007">
        <li id="R_7.050.007ab">tataḥ kathāyāṃ kasyāṃ cit prāñjaliḥ pragraho nṛpaḥ</li>
        <li id="R_7.050.007cd">uvāca taṃ mahātmānam atreḥ putraṃ tapodhanam</li>
    </ul>
    <ul id="R_7.050.008">
        <li id="R_7.050.008ab">bhagavan kiṃpramāṇena mama vaṃśo bhaviṣyati</li>
        <li id="R_7.050.008cd">kimāyuś ca hi me rāmaḥ putrāś cānye kimāyuṣaḥ</li>
    </ul>
    <ul id="R_7.050.009">
        <li id="R_7.050.009ab">rāmasya ca sutā ye syus teṣām āyuḥ kiyad bhavet</li>
        <li id="R_7.050.009cd">kāmyayā bhagavan brūhi vaṃśasyāsya gatiṃ mama</li>
    </ul>
    <ul id="R_7.050.010">
        <li id="R_7.050.010ab">tac chrutvā vyāhṛtaṃ vākyaṃ rājño daśarathasya tu</li>
        <li id="R_7.050.010cd">durvāsāḥ sumahātejā vyāhartum upacakrame</li>
    </ul>
    <ul id="R_7.050.011">
        <li id="R_7.050.011ab">ayodhyāyāḥ patī rāmo dīrghakālaṃ bhaviṣyati</li>
        <li id="R_7.050.011cd">sukhinaś ca samṛddhāś ca bhaviṣyanty asya cānujāḥ</li>
    </ul>
    <ul id="R_7.050.012">
        <li id="R_7.050.012ab">kasmiṃś cit karaṇe tvāṃ ca maithilīṃ ca yaśasvinīm</li>
        <li id="R_7.050.012cd">saṃtyajiṣyati dharmātmā kālena mahatā kila</li>
    </ul>
    <ul id="R_7.050.013">
        <li id="R_7.050.013ab">daśavarṣasahasraṇi daśavarṣaśatāni ca</li>
        <li id="R_7.050.013cd">rāmo rājyam upāsitvā brahmalokaṃ gamiṣyati</li>
    </ul>
    <ul id="R_7.050.014">
        <li id="R_7.050.014ab">samṛddhair hayamedhaiś ca iṣṭvā parapuraṃjayaḥ</li>
        <li id="R_7.050.014cd">rājavaṃśāṃś ca kākutstho bahūn saṃsthāpayiṣyati</li>
    </ul>
    <ul id="R_7.050.015">
        <li id="R_7.050.015ab">sa sarvam akhilaṃ rājño vaṃśasyāsya gatāgatam</li>
        <li id="R_7.050.015cd">ākhyāya sumahātejās tūṣṇīm āsīn mahādyutiḥ</li>
    </ul>
    <ul id="R_7.050.016">
        <li id="R_7.050.016ab">tūṣṇīṃbhūte munau tasmin rājā daśarathas tadā</li>
        <li id="R_7.050.016cd">abhivādya mahātmānau punar āyāt purottamam</li>
    </ul>
    <ul id="R_7.050.017">
        <li id="R_7.050.017ab">etad vaco mayā tatra muninā vyāhṛtaṃ purā</li>
        <li id="R_7.050.017cd">śrutaṃ hṛdi ca nikṣiptaṃ nānyathā tad bhaviṣyati</li>
    </ul>
    <ul id="R_7.050.018">
        <li id="R_7.050.018ab">evaṃ gate na saṃtāpaṃ gantum arhasi rāghava</li>
        <li id="R_7.050.018cd">sītārthe rāghavārthe vā dṛḍho bhava narottama</li>
    </ul>
    <ul id="R_7.050.019">
        <li id="R_7.050.019ab">tac chrutvā vyāhṛtaṃ vākyaṃ sūtasya paramādbhutam</li>
        <li id="R_7.050.019cd">praharṣam atulaṃ lebhe sādhu sādhv iti cābravīt</li>
    </ul>
    <ul id="R_7.050.020">
        <li id="R_7.050.020ab">tayoḥ saṃvadator evaṃ sūtalakṣmaṇayoḥ pathi</li>
        <li id="R_7.050.020cd">astam arko gato vāsaṃ gomatyāṃ tāv athoṣatuḥ</li>
    </ul>
    <ul id="R_7.051.001">
        <li id="R_7.051.001ab">tatra tāṃ rajanīm uṣya gomatyāṃ raghunandanaḥ</li>
        <li id="R_7.051.001cd">prabhāte punar utthāya lakṣmaṇaḥ prayayau tadā</li>
    </ul>
    <ul id="R_7.051.002">
        <li id="R_7.051.002ab">tato 'rdhadivase prāpte praviveśa mahārathaḥ</li>
        <li id="R_7.051.002cd">ayodhyāṃ ratnasaṃpūrṇāṃ hṛṣṭapuṣṭajanāvṛtām</li>
    </ul>
    <ul id="R_7.051.003">
        <li id="R_7.051.003ab">saumitris tu paraṃ dainyaṃ jagāma sumahāmatiḥ</li>
        <li id="R_7.051.003cd">rāmapādau samāsādya vakṣyāmi kim ahaṃ gataḥ</li>
    </ul>
    <ul id="R_7.051.004">
        <li id="R_7.051.004ab">tasyaivaṃ cintayānasya bhavanaṃ śaśisaṃnibham</li>
        <li id="R_7.051.004cd">rāmasya paramodāraṃ purastāt samadṛśyata</li>
    </ul>
    <ul id="R_7.051.005">
        <li id="R_7.051.005ab">rājñas tu bhavanadvāri so 'vatīrya narottamaḥ</li>
        <li id="R_7.051.005cd">avāṅmukho dīnamanāḥ prāviveśānivāritaḥ</li>
    </ul>
    <ul id="R_7.051.006">
        <li id="R_7.051.006ab">sa dṛṣṭvā rāghavaṃ dīnam āsīnaṃ paramāsane</li>
        <li id="R_7.051.006cd">netrābhyām aśrupūrṇābhyāṃ dadarśāgrajam agrataḥ</li>
    </ul>
    <ul id="R_7.051.007">
        <li id="R_7.051.007ab">jagrāha caraṇau tasya lakṣmaṇo dīnacetanaḥ</li>
        <li id="R_7.051.007cd">uvāca dīnayā vācā prāñjaliḥ susamāhitaḥ</li>
    </ul>
    <ul id="R_7.051.008">
        <li id="R_7.051.008ab">āryasyājñāṃ puraskṛtya visṛjya janakātmajām</li>
        <li id="R_7.051.008cd">gaṅgātīre yathoddiṣṭe vālmīker āśrame śubhe</li>
        <li id="R_7.051.008ef">punar asmy āgato vīra pādamūlam upāsitum</li>
    </ul>
    <ul id="R_7.051.009">
        <li id="R_7.051.009ab">mā śucaḥ puruṣavyāghra kālasya gatir īdṛśī</li>
        <li id="R_7.051.009cd">tvadvidhā na hi śocanti sattvavanto manasvinaḥ</li>
    </ul>
    <ul id="R_7.051.010">
        <li id="R_7.051.010ab">sarve kṣayāntā nicayāḥ patanāntāḥ samucchrayāḥ</li>
        <li id="R_7.051.010cd">saṃyogā viprayogāntā maraṇāntaṃ ca jīvitam</li>
    </ul>
    <ul id="R_7.051.011">
        <li id="R_7.051.011ab">śaktas tvam ātmanātmānaṃ vijetuṃ manasaiva hi</li>
        <li id="R_7.051.011cd">lokān sarvāṃś ca kākutstha kiṃ punar duḥkham īdṛśam</li>
    </ul>
    <ul id="R_7.051.012">
        <li id="R_7.051.012ab">nedṛśeṣu vimuhyanti tvadvidhāḥ puruṣarṣabhāḥ</li>
        <li id="R_7.051.012cd">yadarthaṃ maithilī tyaktā apavādabhayān nṛpa</li>
    </ul>
    <ul id="R_7.051.013">
        <li id="R_7.051.013ab">sa tvaṃ puruṣaśārdūla dhairyeṇa susamāhitaḥ</li>
        <li id="R_7.051.013cd">tyajemāṃ durbalāṃ buddhiṃ saṃtāpaṃ mā kuruṣva ha</li>
    </ul>
    <ul id="R_7.051.014">
        <li id="R_7.051.014ab">evam uktas tu kākutstho lakṣmaṇena mahātmanā</li>
        <li id="R_7.051.014cd">uvāca parayā prītyā saumitriṃ mitravatsalam</li>
    </ul>
    <ul id="R_7.051.015">
        <li id="R_7.051.015ab">evam etan naraśreṣṭha yathā vadasi lakṣmaṇa</li>
        <li id="R_7.051.015cd">paritoṣaś ca me vīra mama kāryānuśāsane</li>
    </ul>
    <ul id="R_7.051.016">
        <li id="R_7.051.016ab">nirvṛtiś ca kṛtā saumya saṃtāpaś ca nirākṛtaḥ</li>
        <li id="R_7.051.016cd">bhavadvākyaiḥ sumadhurair anunīto 'smi lakṣmaṇa</li>
    </ul>
    <ul id="R_7.052.001">
        <li id="R_7.052.001ab">tataḥ sumantras tv āgamya rāghavaṃ vākyam abravīt</li>
        <li id="R_7.052.001cd">ete nivāritā rājan dvāri tiṣṭhanti tāpasāḥ</li>
    </ul>
    <ul id="R_7.052.002">
        <li id="R_7.052.002ab">bhārgavaṃ cyavanaṃ nāma puraskṛtya maharṣayaḥ</li>
        <li id="R_7.052.002cd">darśanaṃ te mahārāja codayanti kṛtatvarāḥ</li>
        <li id="R_7.052.002ef">prīyamāṇā naravyāghra yamunātīravāsinaḥ</li>
    </ul>
    <ul id="R_7.052.003">
        <li id="R_7.052.003ab">tasya tad vacanaṃ śrutvā rāmaḥ provāca dharmavit</li>
        <li id="R_7.052.003cd">praveśyantāṃ mahātmāno bhārgavapramukhā dvijāḥ</li>
    </ul>
    <ul id="R_7.052.004">
        <li id="R_7.052.004ab">rājñas tv ājñāṃ puraskṛtya dvāḥstho mūrdhni kṛtāñjaliḥ</li>
        <li id="R_7.052.004cd">praveśayām āsa tatas tāpasān saṃmatān bahūn</li>
    </ul>
    <ul id="R_7.052.005">
        <li id="R_7.052.005ab">śataṃ samadhikaṃ tatra dīpyamānaṃ svatejasā</li>
        <li id="R_7.052.005cd">praviṣṭaṃ rājabhavanaṃ tāpasānāṃ mahātmanām</li>
    </ul>
    <ul id="R_7.052.006">
        <li id="R_7.052.006ab">te dvijāḥ pūrṇakalaśaiḥ sarvatīrthāmbu satkṛtam</li>
        <li id="R_7.052.006cd">gṛhītvā phalamūlaṃ ca rāmasyābhyāharan bahu</li>
    </ul>
    <ul id="R_7.052.007">
        <li id="R_7.052.007ab">pratigṛhya tu tat sarvaṃ rāmaḥ prītipuraskṛtaḥ</li>
        <li id="R_7.052.007cd">tīrthodakāni sarvāṇi phalāni vividhāni ca</li>
    </ul>
    <ul id="R_7.052.008">
        <li id="R_7.052.008ab">uvāca ca mahābāhuḥ sarvān eva mahāmunīn</li>
        <li id="R_7.052.008cd">imāny āsanamukhyāni yathārham upaviśyatām</li>
    </ul>
    <ul id="R_7.052.009">
        <li id="R_7.052.009ab">rāmasya bhāṣitaṃ śrutvā sarva eva maharṣayaḥ</li>
        <li id="R_7.052.009cd">bṛsīṣu rucirākhyāsu niṣeduḥ kāñcanīṣu te</li>
    </ul>
    <ul id="R_7.052.010">
        <li id="R_7.052.010ab">upaviṣṭān ṛṣīṃs tatra dṛṣṭvā parapuraṃjayaḥ</li>
        <li id="R_7.052.010cd">prayataḥ prāñjalir bhūtvā rāghavo vākyam abravīt</li>
    </ul>
    <ul id="R_7.052.011">
        <li id="R_7.052.011ab">kim āgamanakāryaṃ vaḥ kiṃ karomi tapodhanāḥ</li>
        <li id="R_7.052.011cd">ājñāpyo 'haṃ maharṣīṇāṃ sarvakāmakaraḥ sukham</li>
    </ul>
    <ul id="R_7.052.012">
        <li id="R_7.052.012ab">idaṃ rājyaṃ ca sakalaṃ jīvitaṃ ca hṛdi sthitam</li>
        <li id="R_7.052.012cd">sarvam etad dvijārthaṃ me satyam etad bravīmi vaḥ</li>
    </ul>
    <ul id="R_7.052.013">
        <li id="R_7.052.013ab">tasya tad vacanaṃ śrutvā sādhuvādo mahān abhūt</li>
        <li id="R_7.052.013cd">ṛṣīṇām ugratapasāṃ yamunātīravāsinām</li>
    </ul>
    <ul id="R_7.052.014">
        <li id="R_7.052.014ab">ūcuś ca te mahātmāno harṣeṇa mahatānvitāḥ</li>
        <li id="R_7.052.014cd">upapannaṃ naraśreṣṭha tavaiva bhuvi nānyataḥ</li>
    </ul>
    <ul id="R_7.052.015">
        <li id="R_7.052.015ab">bahavaḥ pārthivā rājann atikrāntā mahābalāḥ</li>
        <li id="R_7.052.015cd">kāryagauravam aśrutvā pratijñāṃ nābhyarocayan</li>
    </ul>
    <ul id="R_7.052.016">
        <li id="R_7.052.016ab">
            <seg type="pāda" n="a">tvayā punar brāhmaṇagauravād iyaṃ</seg>
            <seg type="pāda" n="b">kṛtā pratijñā hy anavekṣya kāraṇam</seg>
        </li>
        <li id="R_7.052.016cd">
            <seg type="pāda" n="c">kuruṣva kartā hy asi nātra saṃśayo</seg>
            <seg type="pāda" n="d">mahābhayāt trātum ṛṣīṃs tvam arhasi</seg>
        </li>
    </ul>
    <ul id="R_7.053.001">
        <li id="R_7.053.001ab">bruvadbhir evam ṛṣibhiḥ kākutstho vākyam abravīt</li>
        <li id="R_7.053.001cd">kiṃ kāryaṃ brūta bhavatāṃ bhayaṃ nāśayitāsmi vaḥ</li>
    </ul>
    <ul id="R_7.053.002">
        <li id="R_7.053.002ab">tathā vadati kākutsthe bhārgavo vākyam abravīt</li>
        <li id="R_7.053.002cd">bhayaṃ naḥ śṛṇu yan mūlaṃ deśasya ca nareśvara</li>
    </ul>
    <ul id="R_7.053.003">
        <li id="R_7.053.003ab">pūrvaṃ kṛtayuge rāma daiteyaḥ sumahābalaḥ</li>
        <li id="R_7.053.003cd">lolāputro 'bhavaj jyeṣṭho madhur nāma mahāsuraḥ</li>
    </ul>
    <ul id="R_7.053.004">
        <li id="R_7.053.004ab">brahmaṇyaś ca śaraṇyaś ca buddhyā ca pariniṣṭhitaḥ</li>
        <li id="R_7.053.004cd">suraiś ca paramodāraiḥ prītis tasyātulābhavat</li>
    </ul>
    <ul id="R_7.053.005">
        <li id="R_7.053.005ab">sa madhur vīryasaṃpanno dharme ca susamāhitaḥ</li>
        <li id="R_7.053.005cd">bahumānāc ca rudreṇa dattas tasyādbhuto varaḥ</li>
    </ul>
    <ul id="R_7.053.006">
        <li id="R_7.053.006ab">śūlaṃ śūlād viniṣkṛṣya mahāvīryaṃ mahāprabham</li>
        <li id="R_7.053.006cd">dadau mahātmā suprīto vākyaṃ caitad uvāca ha</li>
    </ul>
    <ul id="R_7.053.007">
        <li id="R_7.053.007ab">tvayāyam atulo dharmo matprasādāt kṛtaḥ śubhaḥ</li>
        <li id="R_7.053.007cd">prītyā paramayā yukto dadāmy āyudham uttamam</li>
    </ul>
    <ul id="R_7.053.008">
        <li id="R_7.053.008ab">yāvat suraiś ca vipraiś ca na virudhyer mahāsura</li>
        <li id="R_7.053.008cd">tāvac chūlaṃ tavedaṃ syād anyathā nāśam āpnuyāt</li>
    </ul>
    <ul id="R_7.053.009">
        <li id="R_7.053.009ab">yaś ca tvām abhiyuñjīta yuddhāya vigatajvaraḥ</li>
        <li id="R_7.053.009cd">taṃ śūlaṃ bhasmasāt kṛtvā punar eṣyati te karam</li>
    </ul>
    <ul id="R_7.053.010">
        <li id="R_7.053.010ab">evaṃ rudrād varaṃ labdhvā bhūya eva mahāsuraḥ</li>
        <li id="R_7.053.010cd">praṇipatya mahādevaṃ vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.053.011">
        <li id="R_7.053.011ab">bhagavan mama vaṃśasya śūlam etad anuttamam</li>
        <li id="R_7.053.011cd">bhavet tu satataṃ deva surāṇām īśvaro hy asi</li>
    </ul>
    <ul id="R_7.053.012">
        <li id="R_7.053.012ab">taṃ bruvāṇaṃ madhuṃ devaḥ sarvabhūtapatiḥ śivaḥ</li>
        <li id="R_7.053.012cd">pratyuvāca mahādevo naitad evaṃ bhaviṣyati</li>
    </ul>
    <ul id="R_7.053.013">
        <li id="R_7.053.013ab">mā bhūt te viphalā vāṇī matprasādakṛtā śubhā</li>
        <li id="R_7.053.013cd">bhavataḥ putram ekaṃ tu śūlam etad gamiṣyati</li>
    </ul>
    <ul id="R_7.053.014">
        <li id="R_7.053.014ab">yāvat karasthaḥ śūlo 'yaṃ bhaviṣyati sutasya te</li>
        <li id="R_7.053.014cd">avadhyaḥ sarvabhūtānāṃ śūlahasto bhaviṣyati</li>
    </ul>
    <ul id="R_7.053.015">
        <li id="R_7.053.015ab">evaṃ madhur varaṃ labdhvā devāt sumahad adbhutam</li>
        <li id="R_7.053.015cd">bhavanaṃ cāsuraśreṣṭhaḥ kārayām āsa suprabham</li>
    </ul>
    <ul id="R_7.053.016">
        <li id="R_7.053.016ab">tasya patnī mahābhāgā priyā kumbhīnasī hi yā</li>
        <li id="R_7.053.016cd">viśvāvasor apatyaṃ sā hy analāyāṃ mahāprabhā</li>
    </ul>
    <ul id="R_7.053.017">
        <li id="R_7.053.017ab">tasyāḥ putro mahāvīryo lavaṇo nāma dāruṇaḥ</li>
        <li id="R_7.053.017cd">bālyāt prabhṛti duṣṭātmā pāpāny eva samācarat</li>
    </ul>
    <ul id="R_7.053.018">
        <li id="R_7.053.018ab">taṃ putraṃ durvinītaṃ tu dṛṣṭvā duḥkhasamanvitaḥ</li>
        <li id="R_7.053.018cd">madhuḥ sa śokam āpede na cainaṃ kiṃ cid abravīt</li>
    </ul>
    <ul id="R_7.053.019">
        <li id="R_7.053.019ab">sa vihāya imaṃ lokaṃ praviṣṭo varuṇālayam</li>
        <li id="R_7.053.019cd">śūlaṃ niveśya lavaṇe varaṃ tasmai nyavedayat</li>
    </ul>
    <ul id="R_7.053.020">
        <li id="R_7.053.020ab">sa prabhāvena śūlasya daurātmyenātmanas tathā</li>
        <li id="R_7.053.020cd">saṃtāpayati lokāṃs trīn viśeṣeṇa tu tāpasān</li>
    </ul>
    <ul id="R_7.053.021">
        <li id="R_7.053.021ab">evaṃprabhāvo lavaṇaḥ śūlaṃ caiva tathāvidham</li>
        <li id="R_7.053.021cd">śrutvā pramāṇaṃ kākutstha tvaṃ hi naḥ paramā gatiḥ</li>
    </ul>
    <ul id="R_7.053.022">
        <li id="R_7.053.022ab">bahavaḥ pārthivā rāma bhayārtair ṛṣibhiḥ purā</li>
        <li id="R_7.053.022cd">abhayaṃ yācitā vīra trātāraṃ na ca vidmahe</li>
    </ul>
    <ul id="R_7.053.023">
        <li id="R_7.053.023ab">te vayaṃ rāvaṇaṃ śrutvā hataṃ sabalavāhanam</li>
        <li id="R_7.053.023cd">trātāraṃ vidmahe rāma nānyaṃ bhuvi narādhipam</li>
        <li id="R_7.053.023ef">tat paritrātum icchāmo lavaṇād bhayapīḍitāḥ</li>
    </ul>
    <ul id="R_7.054.001">
        <li id="R_7.054.001ab">tathokte tān ṛṣīn rāmaḥ pratyuvāca kṛtāñjaliḥ</li>
        <li id="R_7.054.001cd">kimāhāraḥ kimācāro lavaṇaḥ kva ca vartate</li>
    </ul>
    <ul id="R_7.054.002">
        <li id="R_7.054.002ab">rāghavasya vacaḥ śrutvā ṛṣayaḥ sarva eva te</li>
        <li id="R_7.054.002cd">tato nivedayām āsur lavaṇo vavṛdhe yathā</li>
    </ul>
    <ul id="R_7.054.003">
        <li id="R_7.054.003ab">āhāraḥ sarvasattvāni viśeṣeṇa ca tāpasāḥ</li>
        <li id="R_7.054.003cd">ācāro raudratā nityaṃ vāso madhuvane sadā</li>
    </ul>
    <ul id="R_7.054.004">
        <li id="R_7.054.004ab">hatvā daśasahasrāṇi siṃhavyāghramṛgadvipān</li>
        <li id="R_7.054.004cd">mānuṣāṃś caiva kurute nityam āhāram āhnikam</li>
    </ul>
    <ul id="R_7.054.005">
        <li id="R_7.054.005ab">tato 'parāṇi sattvāni khādate sa mahābalaḥ</li>
        <li id="R_7.054.005cd">saṃhāre samanuprāpte vyāditāsya ivāntakaḥ</li>
    </ul>
    <ul id="R_7.054.006">
        <li id="R_7.054.006ab">tac chrutvā rāghavo vākyam uvāca sa mahāmunīn</li>
        <li id="R_7.054.006cd">ghātayiṣyāmi tad rakṣo vyapagacchatu vo bhayam</li>
    </ul>
    <ul id="R_7.054.007">
        <li id="R_7.054.007ab">tathā teṣāṃ pratijñāya munīnām ugratejasām</li>
        <li id="R_7.054.007cd">sa bhrātṝn sahitān sarvān uvāca raghunandanaḥ</li>
    </ul>
    <ul id="R_7.054.008">
        <li id="R_7.054.008ab">ko hantā lavaṇaṃ vīrāḥ kasyāṃśaḥ sa vidhīyatām</li>
        <li id="R_7.054.008cd">bharatasya mahābāhoḥ śatrughnasyātha vā punaḥ</li>
    </ul>
    <ul id="R_7.054.009">
        <li id="R_7.054.009ab">rāghaveṇaivam uktas tu bharato vākyam abravīt</li>
        <li id="R_7.054.009cd">aham enaṃ vadhiṣyāmi mamāṃśaḥ sa vidhīyatām</li>
    </ul>
    <ul id="R_7.054.010">
        <li id="R_7.054.010ab">bharatasya vacaḥ śrutvā śauryavīryasamanvitam</li>
        <li id="R_7.054.010cd">lakṣmaṇāvarajas tasthau hitvā sauvarṇam āsanam</li>
    </ul>
    <ul id="R_7.054.011">
        <li id="R_7.054.011ab">śatrughnas tv abravīd vākyaṃ praṇipatya narādhipam</li>
        <li id="R_7.054.011cd">kṛtakarmā mahābāhur madhyamo raghunandanaḥ</li>
    </ul>
    <ul id="R_7.054.012">
        <li id="R_7.054.012ab">āryeṇa hi purā śūnyā ayodhyā rakṣitā purī</li>
        <li id="R_7.054.012cd">saṃtāpaṃ hṛdaye kṛtvā āryasyāgamanaṃ prati</li>
    </ul>
    <ul id="R_7.054.013">
        <li id="R_7.054.013ab">duḥkhāni ca bahūnīha anubhūtāni pārthiva</li>
        <li id="R_7.054.013cd">śayāno duḥkhaśayyāsu nandigrāme mahātmanā</li>
    </ul>
    <ul id="R_7.054.014">
        <li id="R_7.054.014ab">phalamūlāśano bhūtvā jaṭācīradharas tathā</li>
        <li id="R_7.054.014cd">anubhūyedṛśaṃ duḥkham eṣa rāghavanandanaḥ</li>
        <li id="R_7.054.014ef">preṣye mayi sthite rājan na bhūyaḥ kleśam āpnuyāt</li>
    </ul>
    <ul id="R_7.054.015">
        <li id="R_7.054.015ab">tathā bruvati śatrughne rāghavaḥ punar abravīt</li>
        <li id="R_7.054.015cd">evaṃ bhavatu kākutstha kriyatāṃ mama śāsanam</li>
    </ul>
    <ul id="R_7.054.016">
        <li id="R_7.054.016ab">rājye tvām abhiṣekṣyāmi madhos tu nagare śubhe</li>
        <li id="R_7.054.016cd">niveśaya mahābāho bharataṃ yady avekṣase</li>
    </ul>
    <ul id="R_7.054.017">
        <li id="R_7.054.017ab">śūras tvaṃ kṛtavidyaś ca samarthaḥ saṃniveśane</li>
        <li id="R_7.054.017cd">nagaraṃ madhunā juṣṭaṃ tathā janapadāñ śubhān</li>
    </ul>
    <ul id="R_7.054.018">
        <li id="R_7.054.018ab">yo hi vaṃśaṃ samutpāṭya pārthivasya punaḥ kṣaye</li>
        <li id="R_7.054.018cd">na vidhatte nṛpaṃ tatra narakaṃ sa nigacchati</li>
    </ul>
    <ul id="R_7.054.019">
        <li id="R_7.054.019ab">sa tvaṃ hatvā madhusutaṃ lavaṇaṃ pāpaniścayam</li>
        <li id="R_7.054.019cd">rājyaṃ praśādhi dharmeṇa vākyaṃ me yady avekṣase</li>
    </ul>
    <ul id="R_7.054.020">
        <li id="R_7.054.020ab">uttaraṃ ca na vaktavyaṃ śūra vākyāntare mama</li>
        <li id="R_7.054.020cd">bālena pūrvajasyājñā kartavyā nātra saṃśayaḥ</li>
    </ul>
    <ul id="R_7.054.021">
        <li id="R_7.054.021ab">abhiṣekaṃ ca kākutstha pratīcchasva mayodyatam</li>
        <li id="R_7.054.021cd">vasiṣṭhapramukhair viprair vidhimantrapuraskṛtam</li>
    </ul>
    <ul id="R_7.055.001">
        <li id="R_7.055.001ab">evam uktas tu rāmeṇa parāṃ vrīḍām upāgataḥ</li>
        <li id="R_7.055.001cd">śatrughno vīryasaṃpanno mandaṃ mandam uvāca ha</li>
    </ul>
    <ul id="R_7.055.002">
        <li id="R_7.055.002ab">avaśyaṃ karaṇīyaṃ ca śāsanaṃ puruṣarṣabha</li>
        <li id="R_7.055.002cd">tava caiva mahābhāga śāsanaṃ duratikramam</li>
        <li id="R_7.055.002ef">ayaṃ kāmakaro rājaṃs tavāsmi puruṣarṣabha</li>
    </ul>
    <ul id="R_7.055.003">
        <li id="R_7.055.003ab">evam ukte tu śūreṇa śatrughnena mahātmanā</li>
        <li id="R_7.055.003cd">uvāca rāmaḥ saṃhṛṣṭo lakṣmaṇaṃ bharataṃ tathā</li>
    </ul>
    <ul id="R_7.055.004">
        <li id="R_7.055.004ab">saṃbhārān abhiṣekasya ānayadhvaṃ samāhitāḥ</li>
        <li id="R_7.055.004cd">adyaiva puruṣavyāghram abhiṣekṣyāmi durjayam</li>
    </ul>
    <ul id="R_7.055.005">
        <li id="R_7.055.005ab">purodhasaṃ ca kākutsthau naigamān ṛtvijas tathā</li>
        <li id="R_7.055.005cd">mantriṇaś caiva me sarvān ānayadhvaṃ mamājñayā</li>
    </ul>
    <ul id="R_7.055.006">
        <li id="R_7.055.006ab">rājñaḥ śāsanam ājñāya tathākurvan mahārathāḥ</li>
        <li id="R_7.055.006cd">abhiṣekasamārambhaṃ puraskṛtya purodhasaṃ</li>
        <li id="R_7.055.006ef">praviṣṭā rājabhavanaṃ puraṃdaragṛhopamam</li>
    </ul>
    <ul id="R_7.055.007">
        <li id="R_7.055.007ab">tato 'bhiṣeko vavṛdhe śatrughnasya mahātmanaḥ</li>
        <li id="R_7.055.007cd">saṃpraharṣakaraḥ śrīmān rāghavasya purasya ca</li>
    </ul>
    <ul id="R_7.055.008">
        <li id="R_7.055.008ab">tato 'bhiṣiktaṃ śatrughnam aṅkam āropya rāghavaḥ</li>
        <li id="R_7.055.008cd">uvāca madhurāṃ vāṇīṃ tejas tasyābhipūrayan</li>
    </ul>
    <ul id="R_7.055.009">
        <li id="R_7.055.009ab">ayaṃ śaras tv amoghas te divyaḥ parapuraṃjayaḥ</li>
        <li id="R_7.055.009cd">anena lavaṇaṃ saumya hantāsi raghunandana</li>
    </ul>
    <ul id="R_7.055.010">
        <li id="R_7.055.010ab">sṛṣṭaḥ śaro 'yaṃ kākutstha yadā śete mahārṇave</li>
        <li id="R_7.055.010cd">svayambhūr ajito devo yaṃ nāpaśyan surāsurāḥ</li>
    </ul>
    <ul id="R_7.055.011">
        <li id="R_7.055.011ab">adṛśyaḥ sarvabhūtānāṃ tenāyaṃ hi śarottamaḥ</li>
        <li id="R_7.055.011cd">sṛṣṭaḥ krodhābhibhūtena vināśārthaṃ durātmanoḥ</li>
        <li id="R_7.055.011ef">madhukaiṭabhayor vīra vighāte vartamānayoḥ</li>
    </ul>
    <ul id="R_7.055.012">
        <li id="R_7.055.012ab">sraṣṭukāmena lokāṃs trīṃs tau cānena hatau yudhi</li>
        <li id="R_7.055.012cd">anena śaramukhyena tato lokāṃś cakāra saḥ</li>
    </ul>
    <ul id="R_7.055.013">
        <li id="R_7.055.013ab">nāyaṃ mayā śaraḥ pūrvaṃ rāvaṇasya vadhārthinā</li>
        <li id="R_7.055.013cd">muktaḥ śatrughna bhūtānāṃ mahāṃs trāso bhaved iti</li>
    </ul>
    <ul id="R_7.055.014">
        <li id="R_7.055.014ab">yac ca tasya mahac chūlaṃ tryambakeṇa mahātmanā</li>
        <li id="R_7.055.014cd">dattaṃ śatruvināśāya madhor āyudham uttamam</li>
    </ul>
    <ul id="R_7.055.015">
        <li id="R_7.055.015ab">tat saṃnikṣipya bhavane pūjyamānaṃ punaḥ punaḥ</li>
        <li id="R_7.055.015cd">diśaḥ sarvāḥ samālokya prāpnoty āhāram ātmanaḥ</li>
    </ul>
    <ul id="R_7.055.016">
        <li id="R_7.055.016ab">yadā tu yuddham ākāṅkṣan kaś cid enaṃ samāhvayet</li>
        <li id="R_7.055.016cd">tadā śūlaṃ gṛhītvā tad bhasma rakṣaḥ karoti tam</li>
    </ul>
    <ul id="R_7.055.017">
        <li id="R_7.055.017ab">sa tvaṃ puruṣaśārdūla tam āyudhavivarjitam</li>
        <li id="R_7.055.017cd">apraviṣṭapuraṃ pūrvaṃ dvāri tiṣṭha dhṛtāyudhaḥ</li>
    </ul>
    <ul id="R_7.055.018">
        <li id="R_7.055.018ab">apraviṣṭaṃ ca bhavanaṃ yuddhāya puruṣarṣabha</li>
        <li id="R_7.055.018cd">āhvayethā mahābāho tato hantāsi rākṣasaṃ</li>
    </ul>
    <ul id="R_7.055.019">
        <li id="R_7.055.019ab">anyathā kriyamāṇe tu avadhyaḥ sa bhaviṣyati</li>
        <li id="R_7.055.019cd">yadi tv evaṃ kṛte vīra vināśam upayāsyati</li>
    </ul>
    <ul id="R_7.055.020">
        <li id="R_7.055.020ab">etat te sarvam ākhyātaṃ śūlasya ca viparyayam</li>
        <li id="R_7.055.020cd">śrīmataḥ śitikaṇṭhasya kṛtyaṃ hi duratikramam</li>
    </ul>
    <ul id="R_7.056.001">
        <li id="R_7.056.001ab">evam uktvā tu kākutsthaṃ praśasya ca punaḥ punaḥ</li>
        <li id="R_7.056.001cd">punar evāparaṃ vākyam uvāca raghunandanaḥ</li>
    </ul>
    <ul id="R_7.056.002">
        <li id="R_7.056.002ab">imāny aśvasahasrāṇi catvāri puruṣarṣabha</li>
        <li id="R_7.056.002cd">rathānāṃ ca sahasre dve gajānāṃ śatam eva ca</li>
    </ul>
    <ul id="R_7.056.003">
        <li id="R_7.056.003ab">antarāpaṇavīthyaś ca nānāpaṇyopaśobhitāḥ</li>
        <li id="R_7.056.003cd">anugacchantu śatrughna tathaiva naṭanartakāḥ</li>
    </ul>
    <ul id="R_7.056.004">
        <li id="R_7.056.004ab">hiraṇyasya suvarṇasya ayutaṃ puruṣarṣabha</li>
        <li id="R_7.056.004cd">gṛhītvā gaccha śatrughna paryāptadhanavāhanaḥ</li>
    </ul>
    <ul id="R_7.056.005">
        <li id="R_7.056.005ab">balaṃ ca subhṛtaṃ vīra hṛṣṭapuṣṭam anuttamam</li>
        <li id="R_7.056.005cd">saṃbhāṣya saṃpradānena rañjayasva narottama</li>
    </ul>
    <ul id="R_7.056.006">
        <li id="R_7.056.006ab">na hy arthās tatra tiṣṭhanti na dārā na ca bāndhavāḥ</li>
        <li id="R_7.056.006cd">suprīto bhṛtyavargas tu yatra tiṣṭhati rāghava</li>
    </ul>
    <ul id="R_7.056.007">
        <li id="R_7.056.007ab">ato hṛṣṭajanākīrṇāṃ prasthāpya mahatīṃ camūm</li>
        <li id="R_7.056.007cd">eka eva dhanuṣpānis tad gaccha tvaṃ madhor vanam</li>
    </ul>
    <ul id="R_7.056.008">
        <li id="R_7.056.008ab">yathā tvāṃ na prajānāti gacchantaṃ yuddhakāṅkṣiṇam</li>
        <li id="R_7.056.008cd">lavaṇas tu madhoḥ putras tathā gaccher aśaṅkitaḥ</li>
    </ul>
    <ul id="R_7.056.009">
        <li id="R_7.056.009ab">na tasya mṛtyur anyo 'sti kaś cid dhi puruṣarṣabha</li>
        <li id="R_7.056.009cd">darśanaṃ yo 'bhigaccheta sa vadhyo lavaṇena hi</li>
    </ul>
    <ul id="R_7.056.010">
        <li id="R_7.056.010ab">sa grīṣme vyapayāte tu varṣarātra upasthite</li>
        <li id="R_7.056.010cd">hanyās tvaṃ lavaṇaṃ saumya sa hi kālo 'sya durmateḥ</li>
    </ul>
    <ul id="R_7.056.011">
        <li id="R_7.056.011ab">maharṣīṃs tu puraskṛtya prayāntu tava sainikāḥ</li>
        <li id="R_7.056.011cd">yathā grīṣmāvaśeṣeṇa tareyur jāhnavījalam</li>
    </ul>
    <ul id="R_7.056.012">
        <li id="R_7.056.012ab">tataḥ sthāpya balaṃ sarvaṃ nadītīre samāhitaḥ</li>
        <li id="R_7.056.012cd">agrato dhanuṣā sārdhaṃ gaccha tvaṃ laghuvikrama</li>
    </ul>
    <ul id="R_7.056.013">
        <li id="R_7.056.013ab">evam uktas tu rāmeṇa śatrughnas tān mahābalān</li>
        <li id="R_7.056.013cd">senāmukhyān samānīya tato vākyam uvāca ha</li>
    </ul>
    <ul id="R_7.056.014">
        <li id="R_7.056.014ab">ete vo gaṇitā vāsā yatra yatra nivatsyatha</li>
        <li id="R_7.056.014cd">sthātavyaṃ cāvirodhena yathā bādhā na kasya cit</li>
    </ul>
    <ul id="R_7.056.015">
        <li id="R_7.056.015ab">tathā tāṃs tu samājñāpya niryāpya ca mahad balam</li>
        <li id="R_7.056.015cd">kausalyāṃ ca sumitrāṃ ca kaikeyīṃ cābhyavādayat</li>
    </ul>
    <ul id="R_7.056.016">
        <li id="R_7.056.016ab">rāmaṃ pradakṣiṇaṃ kṛtvā śirasābhipraṇamya ca</li>
        <li id="R_7.056.016cd">rāṇeṇa cābhyanujñātaḥ śatrughnaḥ śatrutāpanaḥ</li>
    </ul>
    <ul id="R_7.056.017">
        <li id="R_7.056.017ab">lakṣmaṇaṃ bharataṃ caiva praṇipatya kṛtāñjaliḥ</li>
        <li id="R_7.056.017cd">purodhasaṃ vasiṣṭhaṃ ca śatrughnaḥ prayatātmavān</li>
        <li id="R_7.056.017ef">pradakṣiṇam atho kṛtvā nirjagāma mahābalaḥ</li>
    </ul>
    <ul id="R_7.057.001">
        <li id="R_7.057.001ab">prasthāpya tad balaṃ sarvaṃ māsamātroṣitaḥ pathi</li>
        <li id="R_7.057.001cd">eka evāśu śatrughno jagāma tvaritas tadā</li>
    </ul>
    <ul id="R_7.057.002">
        <li id="R_7.057.002ab">dvirātram antare śūra uṣya rāghavanandanaḥ</li>
        <li id="R_7.057.002cd">vālmīker āśramaṃ puṇyam agacchad vāsam uttamam</li>
    </ul>
    <ul id="R_7.057.003">
        <li id="R_7.057.003ab">so 'bhivādya mahātmānaṃ vālmīkiṃ munisattamam</li>
        <li id="R_7.057.003cd">kṛtāñjalir atho bhūtvā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.057.004">
        <li id="R_7.057.004ab">bhagavan vastum icchāmi guroḥ kṛtyād ihāgataḥ</li>
        <li id="R_7.057.004cd">śvaḥ prabhāte gamiṣyāmi pratīcīṃ vāruṇīṃ diśam</li>
    </ul>
    <ul id="R_7.057.005">
        <li id="R_7.057.005ab">śatrughnasya vacaḥ śrutvā prahasya munipuṃgavaḥ</li>
        <li id="R_7.057.005cd">pratyuvāca mahātmānaṃ svāgataṃ te mahāyaśaḥ</li>
    </ul>
    <ul id="R_7.057.006">
        <li id="R_7.057.006ab">svam āśramam idaṃ saumya rāghavāṇāṃ kulasya ha</li>
        <li id="R_7.057.006cd">āsanaṃ pādyam arghyaṃ ca nirviśaṅkaḥ pratīccha me</li>
    </ul>
    <ul id="R_7.057.007">
        <li id="R_7.057.007ab">pratigṛhya tataḥ pūjāṃ phalamūlaṃ ca bhojanam</li>
        <li id="R_7.057.007cd">bhakṣayām āsa kākutsthas tṛptiṃ ca paramāṃ gataḥ</li>
    </ul>
    <ul id="R_7.057.008">
        <li id="R_7.057.008ab">sa tu bhuktvā mahābāhur maharṣiṃ tam uvāca ha</li>
        <li id="R_7.057.008cd">pūrvaṃ yajñavibhūtīyaṃ kasyāśramasamīpataḥ</li>
    </ul>
    <ul id="R_7.057.009">
        <li id="R_7.057.009ab">tasya tadbhāṣitaṃ śrutvā vālmīkir vākyam abravīt</li>
        <li id="R_7.057.009cd">śatrughna śṛṇu yasyedaṃ babhūvāyatanaṃ purā</li>
    </ul>
    <ul id="R_7.057.010">
        <li id="R_7.057.010ab">yuṣmākaṃ pūrvako rājā sudāsasya mahātmanaḥ</li>
        <li id="R_7.057.010cd">putro mitrasaho nāma vīryavān atidhārmikaḥ</li>
    </ul>
    <ul id="R_7.057.011">
        <li id="R_7.057.011ab">sa bāla eva saudāso mṛgayām upacakrame</li>
        <li id="R_7.057.011cd">cañcūryamāṇaṃ dadṛśe sa śūro rākṣasadvayam</li>
    </ul>
    <ul id="R_7.057.012">
        <li id="R_7.057.012ab">śārdūlarūpiṇau ghorau mṛgān bahusahasraśaḥ</li>
        <li id="R_7.057.012cd">bhakṣayāṇāv asaṃtuṣṭau paryāptiṃ ca na jagmatuḥ</li>
    </ul>
    <ul id="R_7.057.013">
        <li id="R_7.057.013ab">sa tu tau rākṣasau dṛṣṭvā nirmṛgaṃ ca vanaṃ kṛtam</li>
        <li id="R_7.057.013cd">krodhena mahatāviṣṭo jaghānaikaṃ maheṣuṇā</li>
    </ul>
    <ul id="R_7.057.014">
        <li id="R_7.057.014ab">vinipātya tam ekaṃ tu saudāsaḥ puruṣarṣabhaḥ</li>
        <li id="R_7.057.014cd">vijvaro vigatāmarṣo hataṃ rakṣo 'bhyavaikṣata</li>
    </ul>
    <ul id="R_7.057.015">
        <li id="R_7.057.015ab">nirīkṣamāṇaṃ taṃ dṛṣṭvā sahāyas tasya rakṣasaḥ</li>
        <li id="R_7.057.015cd">saṃtāpam akarod ghoraṃ saudāsaṃ cedam abravīt</li>
    </ul>
    <ul id="R_7.057.016">
        <li id="R_7.057.016ab">yasmād anaparāddhaṃ tvaṃ sahāyaṃ mama jaghnivān</li>
        <li id="R_7.057.016cd">tasmāt tavāpi pāpiṣṭha pradāsyāmi pratikriyām</li>
    </ul>
    <ul id="R_7.057.017">
        <li id="R_7.057.017ab">evam uktvā tu taṃ rakṣas tatraivāntaradhīyata</li>
        <li id="R_7.057.017cd">kālaparyāyayogena rājā mitrasaho 'bhavat</li>
    </ul>
    <ul id="R_7.057.018">
        <li id="R_7.057.018ab">rājāpi yajate yajñaṃ tasyāśramasamīpataḥ</li>
        <li id="R_7.057.018cd">aśvamedhaṃ mahāyajñaṃ taṃ vasiṣṭho 'bhyapālayat</li>
    </ul>
    <ul id="R_7.057.019">
        <li id="R_7.057.019ab">tatra yajño mahān āsīd bahuvarṣagaṇāyutān</li>
        <li id="R_7.057.019cd">samṛddhaḥ parayā lakṣmyā devayajñasamo 'bhavat</li>
    </ul>
    <ul id="R_7.057.020">
        <li id="R_7.057.020ab">athāvasāne yajñasya pūrvavairam anusmaran</li>
        <li id="R_7.057.020cd">vasiṣṭharūpī rājānam iti hovāca rākṣasaḥ</li>
    </ul>
    <ul id="R_7.057.021">
        <li id="R_7.057.021ab">adya yajñāvasānānte sāmiṣaṃ bhojanaṃ mama</li>
        <li id="R_7.057.021cd">dīyatām iti śīghraṃ vai nātra kāryā vicāraṇā</li>
    </ul>
    <ul id="R_7.057.022">
        <li id="R_7.057.022ab">tac chrutvā vyāhṛtaṃ vākyaṃ rakṣasā kāmarūpiṇā</li>
        <li id="R_7.057.022cd">bhakṣasaṃskārakuśalam uvāca pṛthivīpatiḥ</li>
    </ul>
    <ul id="R_7.057.023">
        <li id="R_7.057.023ab">haviṣyaṃ sāmiṣaṃ svādu yathā bhavati bhojanam</li>
        <li id="R_7.057.023cd">tathā kuruṣva śīghraṃ vai parituṣyed yathā guruḥ</li>
    </ul>
    <ul id="R_7.057.024">
        <li id="R_7.057.024ab">śāsanāt pārthivendrasya sūdaḥ saṃbhrāntamānasaḥ</li>
        <li id="R_7.057.024cd">sa ca rakṣaḥ punas tatra sūdaveṣam athākarot</li>
    </ul>
    <ul id="R_7.057.025">
        <li id="R_7.057.025ab">sa mānuṣam atho māṃsaṃ pārthivāya nyavedayat</li>
        <li id="R_7.057.025cd">idaṃ svāduhaviṣyaṃ ca sāmiṣaṃ cānnam āhṛtam</li>
    </ul>
    <ul id="R_7.057.026">
        <li id="R_7.057.026ab">sa bhojanaṃ vasiṣṭhāya patnyā sārdham upāharat</li>
        <li id="R_7.057.026cd">madayantyā naravyāghra sāmiṣaṃ rakṣasā hṛtam</li>
    </ul>
    <ul id="R_7.057.027">
        <li id="R_7.057.027ab">jñātvā tadāmiṣaṃ vipro mānuṣaṃ bhojanāhṛtam</li>
        <li id="R_7.057.027cd">krodhena mahatāviṣṭo vyāhartum upacakrame</li>
    </ul>
    <ul id="R_7.057.028">
        <li id="R_7.057.028ab">yasmāt tvaṃ bhojanaṃ rājan mamaitad dātum icchasi</li>
        <li id="R_7.057.028cd">tasmād bhojanam etat te bhaviṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.057.029">
        <li id="R_7.057.029ab">sa rājā saha patnyā vai praṇipatya muhur muhuḥ</li>
        <li id="R_7.057.029cd">punar vasiṣṭhaṃ provāca yad uktaṃ brahmarūpiṇā</li>
    </ul>
    <ul id="R_7.057.030">
        <li id="R_7.057.030ab">tac chrutā pārthivendrasya rakṣasā vikṛtaṃ ca tat</li>
        <li id="R_7.057.030cd">punaḥ provāca rājānaṃ vasiṣṭhaḥ puruṣarṣabham</li>
    </ul>
    <ul id="R_7.057.031">
        <li id="R_7.057.031ab">mayā roṣaparītena yad idaṃ vyāhṛtaṃ vacaḥ</li>
        <li id="R_7.057.031cd">naitac chakyaṃ vṛthā kartuṃ pradāsyāmi ca te varam</li>
    </ul>
    <ul id="R_7.057.032">
        <li id="R_7.057.032ab">kālo dvādaśa varṣāṇi śāpasyāsya bhaviṣyati</li>
        <li id="R_7.057.032cd">matprasādāc ca rājendra atītaṃ na smariṣyasi</li>
    </ul>
    <ul id="R_7.057.033">
        <li id="R_7.057.033ab">evaṃ sa rājā taṃ śāpam upabhujyārimardanaḥ</li>
        <li id="R_7.057.033cd">pratilebhe punā rājyaṃ prajāś caivānvapālayat</li>
    </ul>
    <ul id="R_7.057.034">
        <li id="R_7.057.034ab">tasya kalmāṣapādasya yajñasyāyatanaṃ śubham</li>
        <li id="R_7.057.034cd">āśramasya samīpe 'smin yasmin pṛcchasi rāghava</li>
    </ul>
    <ul id="R_7.057.035">
        <li id="R_7.057.035ab">tasya tāṃ pārthivendrasya kathāṃ śrutvā sudāruṇām</li>
        <li id="R_7.057.035cd">viveśa parṇaśālāyāṃ maharṣim abhivādya ca</li>
    </ul>
    <ul id="R_7.058.001">
        <li id="R_7.058.001ab">yām eva rātriṃ śatrughna parṇaśālāṃ samāviśat</li>
        <li id="R_7.058.001cd">tām eva rātriṃ sītāpi prasūtā dārakadvayam</li>
    </ul>
    <ul id="R_7.058.002">
        <li id="R_7.058.002ab">tato 'rdharātrasamaye bālakā munidārakāḥ</li>
        <li id="R_7.058.002cd">vālmīkeḥ priyam ācakhyuḥ sītāyāḥ prasavaṃ śubham</li>
        <li id="R_7.058.002ef">tasya rakṣāṃ mahātejaḥ kuru bhūtavināśinīm</li>
    </ul>
    <ul id="R_7.058.003">
        <li id="R_7.058.003ab">teṣāṃ tad vacanaṃ śrutvā munir harṣam upāgamat</li>
        <li id="R_7.058.003cd">bhūtaghnīṃ cākarot tābhyāṃ rakṣāṃ rakṣovināśinīm</li>
    </ul>
    <ul id="R_7.058.004">
        <li id="R_7.058.004ab">kuśamuṣṭim upādāya lavaṃ caiva tu sa dvijaḥ</li>
        <li id="R_7.058.004cd">vālmīkiḥ pradadau tābhyāṃ rakṣāṃ bhūtavināśinīm</li>
    </ul>
    <ul id="R_7.058.005">
        <li id="R_7.058.005ab">yas tayoḥ pūrvajo jātaḥ sa kuśair mantrasaṃskṛtaiḥ</li>
        <li id="R_7.058.005cd">nirmārjanīyas tu bhavet kuśa ity asya nāmataḥ</li>
    </ul>
    <ul id="R_7.058.006">
        <li id="R_7.058.006ab">yaś cāparo bhavet tābhyāṃ lavena susamāhitaḥ</li>
        <li id="R_7.058.006cd">nirmārjanīyo vṛddhābhir lavaś ceti sa nāmataḥ</li>
    </ul>
    <ul id="R_7.058.007">
        <li id="R_7.058.007ab">evaṃ kuśalavau nāmnā tāv ubhau yamajātakau</li>
        <li id="R_7.058.007cd">matkṛtabhyāṃ ca nāmabhyāṃ khyātiyuktau bhaviṣyataḥ</li>
    </ul>
    <ul id="R_7.058.008">
        <li id="R_7.058.008ab">te rakṣāṃ jagṛhus tāṃ ca munihastāt samāhitāḥ</li>
        <li id="R_7.058.008cd">akurvaṃś ca tato rakṣāṃ tayor vigatakalmaṣāḥ</li>
    </ul>
    <ul id="R_7.058.009">
        <li id="R_7.058.009ab">tathā tāṃ kriyamāṇāṃ tu rakṣāṃ gotraṃ ca nāma ca</li>
        <li id="R_7.058.009cd">saṃkīrtanaṃ ca rāmasya sītāyāḥ prasavau śubhau</li>
    </ul>
    <ul id="R_7.058.010">
        <li id="R_7.058.010ab">ardharātre tu śatrughnaḥ śuśrāva sumahat priyam</li>
        <li id="R_7.058.010cd">parṇaśālāṃ gato rātrau diṣṭyā diṣṭyeti cābravīt</li>
    </ul>
    <ul id="R_7.058.011">
        <li id="R_7.058.011ab">tatha tasya prahṛṣṭasya śatrughnasya mahātmanaḥ</li>
        <li id="R_7.058.011cd">vyatītā vārṣikī rātriḥ śrāvaṇī laghuvikramā</li>
    </ul>
    <ul id="R_7.058.012">
        <li id="R_7.058.012ab">prabhāte tu mahāvīryaḥ kṛtvā paurvāhṇikaṃ kramam</li>
        <li id="R_7.058.012cd">muniṃ prāñjalir āmantrya prāyāt paścānmukhaḥ punaḥ</li>
    </ul>
    <ul id="R_7.058.013">
        <li id="R_7.058.013ab">sa gatvā yamunātīraṃ saptarātroṣitaḥ pathi</li>
        <li id="R_7.058.013cd">ṛṣīṇāṃ puṇyakīrtīnām āśrame vāsam abhyayāt</li>
    </ul>
    <ul id="R_7.058.014">
        <li id="R_7.058.014ab">sa tatra munibhiḥ sārdhaṃ bhārgavapramukhair nṛpaḥ</li>
        <li id="R_7.058.014cd">kathābhir bahurūpābhir vāsaṃ cakre mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.059.001">
        <li id="R_7.059.001ab">atha rātryāṃ pravṛttāyāṃ śatrughno bhṛgunandanam</li>
        <li id="R_7.059.001cd">papraccha cyavanaṃ vipraṃ lavaṇasya balābalam</li>
    </ul>
    <ul id="R_7.059.002">
        <li id="R_7.059.002ab">śūlasya ca balaṃ brahman ke ca pūrvaṃ nipātitāḥ</li>
        <li id="R_7.059.002cd">anena śūlamukhena dvandvayuddham upāgatāḥ</li>
    </ul>
    <ul id="R_7.059.003">
        <li id="R_7.059.003ab">tasya tadbhāṣitaṃ śrutvā śatrughnasya mahātmanaḥ</li>
        <li id="R_7.059.003cd">pratyuvāca mahātejāś cyavano raghunandanam</li>
    </ul>
    <ul id="R_7.059.004">
        <li id="R_7.059.004ab">asaṃkhyeyāni karmāṇi yāny asya puruṣarṣabha</li>
        <li id="R_7.059.004cd">ikṣvākuvaṃśaprabhave yad vṛttaṃ tac chṛṇuṣva me</li>
    </ul>
    <ul id="R_7.059.005">
        <li id="R_7.059.005ab">ayodhyāyāṃ purā rājā yuvanāśvasuto balī</li>
        <li id="R_7.059.005cd">māndhātā iti vikhyātas triṣu lokeṣu vīryavān</li>
    </ul>
    <ul id="R_7.059.006">
        <li id="R_7.059.006ab">sa kṛtvā pṛthivīṃ kṛtsnāṃ śāsane pṛthivīpatiḥ</li>
        <li id="R_7.059.006cd">suralokam atho jetum udyogam akaron nṛpaḥ</li>
    </ul>
    <ul id="R_7.059.007">
        <li id="R_7.059.007ab">indrasya tu bhayaṃ tīvraṃ surāṇāṃ ca mahātmanām</li>
        <li id="R_7.059.007cd">māndhātari kṛtodyoge devalokajigīṣayā</li>
    </ul>
    <ul id="R_7.059.008">
        <li id="R_7.059.008ab">ardhāsanena śakrasya rājyārdhena ca pārthivaḥ</li>
        <li id="R_7.059.008cd">vandyamānaḥ suragaṇaiḥ pratijñām adhyarohata</li>
    </ul>
    <ul id="R_7.059.009">
        <li id="R_7.059.009ab">tasya pāpam abhiprāyaṃ viditvā pākaśāsanaḥ</li>
        <li id="R_7.059.009cd">sāntvapūrvam idaṃ vākyam uvāca yuvanāśvajam</li>
    </ul>
    <ul id="R_7.059.010">
        <li id="R_7.059.010ab">rājā tvaṃ mānuṣe loke na tāvat puruṣarṣabha</li>
        <li id="R_7.059.010cd">akṛtvā pṛthivīṃ vaśyāṃ devarājyam ihecchasi</li>
    </ul>
    <ul id="R_7.059.011">
        <li id="R_7.059.011ab">yadi vīra samagrā te medinī nikhilā vaśe</li>
        <li id="R_7.059.011cd">devarājyaṃ kuruṣveha sabhṛtyabalavāhanaḥ</li>
    </ul>
    <ul id="R_7.059.012">
        <li id="R_7.059.012ab">indram evaṃ bruvāṇaṃ tu māndhātā vākyam abravīt</li>
        <li id="R_7.059.012cd">kva me śakra pratihataṃ śāsanaṃ pṛthivītale</li>
    </ul>
    <ul id="R_7.059.013">
        <li id="R_7.059.013ab">tam uvāca sahasrākṣo lavaṇo nāma rākṣasaḥ</li>
        <li id="R_7.059.013cd">madhuputro madhuvane nājñāṃ te kurute 'nagha</li>
    </ul>
    <ul id="R_7.059.014">
        <li id="R_7.059.014ab">tac chrutvā vipriyaṃ ghoraṃ sahasrākṣeṇa bhāṣitam</li>
        <li id="R_7.059.014cd">vrīḍito 'vāṅmukho rājā vyāhartuṃ na śaśāka ha</li>
    </ul>
    <ul id="R_7.059.015">
        <li id="R_7.059.015ab">āmantrya tu sahasrākṣaṃ hriyā kiṃ cid avāṅmukhaḥ</li>
        <li id="R_7.059.015cd">punar evāgamac chrīmān imaṃ lokaṃ nareśvaraḥ</li>
    </ul>
    <ul id="R_7.059.016">
        <li id="R_7.059.016ab">sa kṛtvā hṛdaye 'marṣaṃ sabhṛtyabalavāhanaḥ</li>
        <li id="R_7.059.016cd">ājagāma madhoḥ putraṃ vaśe kartum aninditaḥ</li>
    </ul>
    <ul id="R_7.059.017">
        <li id="R_7.059.017ab">sa kāṅkṣamāṇo lavaṇaṃ yuddhāya puruṣarṣabhaḥ</li>
        <li id="R_7.059.017cd">dūtaṃ saṃpreṣayām āsa sakāśaṃ lavaṇasya saḥ</li>
    </ul>
    <ul id="R_7.059.018">
        <li id="R_7.059.018ab">sa gatvā vipriyāṇy āha bahūni madhunaḥ sutam</li>
        <li id="R_7.059.018cd">vadantam evaṃ taṃ dūtaṃ bhakṣayām āsa rākṣasaḥ</li>
    </ul>
    <ul id="R_7.059.019">
        <li id="R_7.059.019ab">cirāyamāṇe dūte tu rājā krodhasamanvitaḥ</li>
        <li id="R_7.059.019cd">ardayām āsa tad rakṣaḥ śaravṛṣṭyā samantataḥ</li>
    </ul>
    <ul id="R_7.059.020">
        <li id="R_7.059.020ab">tataḥ prahasya lavaṇaḥ śūlaṃ jagrāha pāṇinā</li>
        <li id="R_7.059.020cd">vadhāya sānubandhasya mumocāyudham uttamam</li>
    </ul>
    <ul id="R_7.059.021">
        <li id="R_7.059.021ab">tac chūlaṃ dīpyamānaṃ tu sabhṛtyabalavāhanam</li>
        <li id="R_7.059.021cd">bhasmīkṛtya nṛpaṃ bhūyo lavaṇasyāgamat karam</li>
    </ul>
    <ul id="R_7.059.022">
        <li id="R_7.059.022ab">evaṃ sa rājā sumahān hataḥ sabalavāhanaḥ</li>
        <li id="R_7.059.022cd">śūlasya ca balaṃ vīra aprameyam anuttamam</li>
    </ul>
    <ul id="R_7.059.023">
        <li id="R_7.059.023ab">śvaḥ prabhāte tu lavaṇaṃ vadhiṣyasi na saṃśayaḥ</li>
        <li id="R_7.059.023cd">agṛhītāyudhaṃ kṣipraṃ dhruvo hi vijayas tava</li>
    </ul>
    <ul id="R_7.060.001">
        <li id="R_7.060.001ab">kathāṃ kathayatāṃ teṣāṃ jayaṃ cākāṅkṣatāṃ śubham</li>
        <li id="R_7.060.001cd">vyatītā rajanī śīghraṃ śatrughnasya mahātmanaḥ</li>
    </ul>
    <ul id="R_7.060.002">
        <li id="R_7.060.002ab">tataḥ prabhāte vimale tasmin kāle sa rākṣasaḥ</li>
        <li id="R_7.060.002cd">nirgatas tu purād vīro bhakṣāhārapracoditaḥ</li>
    </ul>
    <ul id="R_7.060.003">
        <li id="R_7.060.003ab">etasminn antare śūraḥ śatrughno yamunāṃ nadīm</li>
        <li id="R_7.060.003cd">tīrtvā madhupuradvāri dhanuṣpāṇir atiṣṭhata</li>
    </ul>
    <ul id="R_7.060.004">
        <li id="R_7.060.004ab">tato 'rdhadivase prāpte krūrakarmā sa rākṣasaḥ</li>
        <li id="R_7.060.004cd">āgacchad bahusahasraṃ prāṇinām udvahan bharam</li>
    </ul>
    <ul id="R_7.060.005">
        <li id="R_7.060.005ab">tato dadarśa śatrughnaṃ sthitaṃ dvāri dhṛtāyudham</li>
        <li id="R_7.060.005cd">tam uvāca tato rakṣaḥ kim anena kariṣyasi</li>
    </ul>
    <ul id="R_7.060.006">
        <li id="R_7.060.006ab">īdṛśānāṃ sahasrāṇi sāyudhānāṃ narādhama</li>
        <li id="R_7.060.006cd">bhakṣitāni mayā roṣāt kālam ākāṅkṣase nu kim</li>
    </ul>
    <ul id="R_7.060.007">
        <li id="R_7.060.007ab">āhāraś cāpy asaṃpūrṇo mamāyaṃ puruṣādhama</li>
        <li id="R_7.060.007cd">svayaṃ praviṣṭo nu mukhaṃ katham āsādya durmate</li>
    </ul>
    <ul id="R_7.060.008">
        <li id="R_7.060.008ab">tasyaivaṃ bhāṣamāṇasya hasataś ca muhur muhuḥ</li>
        <li id="R_7.060.008cd">śatrughno vīryasaṃpanno roṣād aśrūṇy avartayat</li>
    </ul>
    <ul id="R_7.060.009">
        <li id="R_7.060.009ab">tasya roṣābhibhūtasya śatrughnasya mahātmanaḥ</li>
        <li id="R_7.060.009cd">tejomayā marīcyas tu sarvagātrair viniṣpatan</li>
    </ul>
    <ul id="R_7.060.010">
        <li id="R_7.060.010ab">uvāca ca susaṃkruddhaḥ śatrughnas taṃ niśācaram</li>
        <li id="R_7.060.010cd">yoddhum icchāmi durbuddhe dvandvayuddhaṃ tvayā saha</li>
    </ul>
    <ul id="R_7.060.011">
        <li id="R_7.060.011ab">putro daśarathasyāhaṃ bhrātā rāmasya dhīmataḥ</li>
        <li id="R_7.060.011cd">śatrughno nāma śatrughno vadhākāṅkṣī tavāgataḥ</li>
    </ul>
    <ul id="R_7.060.012">
        <li id="R_7.060.012ab">tasya me yuddhakāmasya dvandvayuddhaṃ pradīyatām</li>
        <li id="R_7.060.012cd">śatrus tvaṃ sarvajīvānāṃ na me jīvan gamiṣyasi</li>
    </ul>
    <ul id="R_7.060.013">
        <li id="R_7.060.013ab">tasmiṃs tathā bruvāṇe tu rākṣasaḥ prahasann iva</li>
        <li id="R_7.060.013cd">pratyuvāca naraśreṣṭhaṃ diṣṭyā prāpto 'si durmate</li>
    </ul>
    <ul id="R_7.060.014">
        <li id="R_7.060.014ab">mama mātṛṣvasur bhrātā rāvaṇo nāma rākṣasaḥ</li>
        <li id="R_7.060.014cd">hato rāmeṇa durbuddhe strīhetoḥ puruṣādhama</li>
    </ul>
    <ul id="R_7.060.015">
        <li id="R_7.060.015ab">tac ca sarvaṃ mayā kṣāntaṃ rāvaṇasya kulakṣayam</li>
        <li id="R_7.060.015cd">avajñāṃ purataḥ kṛtvā mayā yūyaṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_7.060.016">
        <li id="R_7.060.016ab">na hatāś ca hi me sarve paribhūtās tṛṇaṃ yathā</li>
        <li id="R_7.060.016cd">bhūtāś caiva bhaviṣyāś ca yūyaṃ ca puruṣādhamāḥ</li>
    </ul>
    <ul id="R_7.060.017">
        <li id="R_7.060.017ab">tasya te yuddhakāmasya yuddhaṃ dāsyāmi durmate</li>
        <li id="R_7.060.017cd">īpsitaṃ yādṛśaṃ tubhyaṃ sajjaye yāvad āyudham</li>
    </ul>
    <ul id="R_7.060.018">
        <li id="R_7.060.018ab">tam uvācātha śatrughnaḥ kva me jīvan gamiṣyasi</li>
        <li id="R_7.060.018cd">durbalo 'py āgataḥ śatrur na moktavyaḥ kṛtātmanā</li>
    </ul>
    <ul id="R_7.060.019">
        <li id="R_7.060.019ab">yo hi viklavayā buddhyā prasaraṃ śatrave dadau</li>
        <li id="R_7.060.019cd">sa hato mandabuddhitvād yathā kāpuruṣas tathā</li>
    </ul>
    <ul id="R_7.061.001">
        <li id="R_7.061.001ab">tac chrutvā bhāṣitaṃ tasya śatrughnasya mahātmanaḥ</li>
        <li id="R_7.061.001cd">krodham āhārayat tīvraṃ tiṣṭha tiṣṭheti cābravīt</li>
    </ul>
    <ul id="R_7.061.002">
        <li id="R_7.061.002ab">pāṇau pāṇiṃ viniṣpiṣya dantān kaṭakaṭāyya ca</li>
        <li id="R_7.061.002cd">lavaṇo raghuśārdūlam āhvayām āsa cāsakṛt</li>
    </ul>
    <ul id="R_7.061.003">
        <li id="R_7.061.003ab">taṃ bruvāṇaṃ tathā vākyaṃ lavaṇaṃ ghoravikramam</li>
        <li id="R_7.061.003cd">śatrughno deva śatrughna idaṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.061.004">
        <li id="R_7.061.004ab">śatrughno na tadā jāto yadānye nirjitās tvayā</li>
        <li id="R_7.061.004cd">tad adya bāṇābhihato vraja taṃ yamasādanam</li>
    </ul>
    <ul id="R_7.061.005">
        <li id="R_7.061.005ab">ṛṣayo 'py adya pāpātman mayā tvāṃ nihataṃ raṇe</li>
        <li id="R_7.061.005cd">paśyantu viprā vidvāṃsas tridaśā iva rāvaṇam</li>
    </ul>
    <ul id="R_7.061.006">
        <li id="R_7.061.006ab">tvayi madbāṇanirdagdhe patite 'dya niśācara</li>
        <li id="R_7.061.006cd">puraṃ janapadaṃ cāpi kṣemam etad bhaviṣyati</li>
    </ul>
    <ul id="R_7.061.007">
        <li id="R_7.061.007ab">adya madbāhuniṣkrāntaḥ śaro vajranibhānanaḥ</li>
        <li id="R_7.061.007cd">pravekṣyate te hṛdayaṃ padmam aṃśur ivārkajaḥ</li>
    </ul>
    <ul id="R_7.061.008">
        <li id="R_7.061.008ab">evam ukto mahāvṛkṣaṃ lavaṇaḥ krodhamūrchitaḥ</li>
        <li id="R_7.061.008cd">śatrughnorasi cikṣepa taṃ śūraḥ śatadhācchinat</li>
    </ul>
    <ul id="R_7.061.009">
        <li id="R_7.061.009ab">tad dṛṣṭvā viphalaṃ karma rākṣasaḥ punar eva tu</li>
        <li id="R_7.061.009cd">pādapān subahūn gṛhya śatrughne vyasṛjad balī</li>
    </ul>
    <ul id="R_7.061.010">
        <li id="R_7.061.010ab">śatrughnaś cāpi tejasvī vṛkṣān āpatato bahūn</li>
        <li id="R_7.061.010cd">tribhiś caturbhir ekaikaṃ ciccheda nataparvabhiḥ</li>
    </ul>
    <ul id="R_7.061.011">
        <li id="R_7.061.011ab">tato bāṇamayaṃ varṣaṃ vyasṛjad rākṣasor asi</li>
        <li id="R_7.061.011cd">śatrughno vīryasaṃpanno vivyathe na ca rākṣasaḥ</li>
    </ul>
    <ul id="R_7.061.012">
        <li id="R_7.061.012ab">tataḥ prahasya lavaṇo vṛkṣam utpāṭya līlayā</li>
        <li id="R_7.061.012cd">śirasy abhyahanac chūraṃ srastāṅgaḥ sa mumoha vai</li>
    </ul>
    <ul id="R_7.061.013">
        <li id="R_7.061.013ab">tasmin nipatite vīre hāhākāro mahān abhūt</li>
        <li id="R_7.061.013cd">ṛṣīṇāṃ deva saṃghānāṃ gandharvāpsarasām api</li>
    </ul>
    <ul id="R_7.061.014">
        <li id="R_7.061.014ab">tam avajñāya tu hataṃ śatrughnaṃ bhuvi pātitam</li>
        <li id="R_7.061.014cd">rakṣo labdhāntaram api na viveśa svam ālayam</li>
    </ul>
    <ul id="R_7.061.015">
        <li id="R_7.061.015ab">nāpi śūlaṃ prajagrāha taṃ dṛṣṭvā bhuvi pātitam</li>
        <li id="R_7.061.015cd">tato hata iti jñātvā tān bhakṣān samudāvahat</li>
    </ul>
    <ul id="R_7.061.016">
        <li id="R_7.061.016ab">muhūrtāl labdhasaṃjñas tu punas tasthau dhṛtāyudhaḥ</li>
        <li id="R_7.061.016cd">śatrughno rākṣasadvāri ṛṣibhiḥ saṃprapūjitaḥ</li>
    </ul>
    <ul id="R_7.061.017">
        <li id="R_7.061.017ab">tato divyam amoghaṃ taṃ jagrāha śaram uttamam</li>
        <li id="R_7.061.017cd">jvalantaṃ tejasā ghoraṃ pūrayantaṃ diśo daśa</li>
    </ul>
    <ul id="R_7.061.018">
        <li id="R_7.061.018ab">vajrānanaṃ vajravegaṃ merumandara gauravam</li>
        <li id="R_7.061.018cd">nataṃ parvasu sarveṣu saṃyugeṣv aparājitam</li>
    </ul>
    <ul id="R_7.061.019">
        <li id="R_7.061.019ab">asṛkcandanadigdhāṅgaṃ cārupatraṃ patatriṇam</li>
        <li id="R_7.061.019cd">dānavendrācalendrāṇām asurāṇāṃ ca dāruṇam</li>
    </ul>
    <ul id="R_7.061.020">
        <li id="R_7.061.020ab">taṃ dīptam iva kālāgniṃ yugānte samupasthite</li>
        <li id="R_7.061.020cd">dṛṣṭvā sarvāṇi bhūtāni paritrāsam upāgaman</li>
    </ul>
    <ul id="R_7.061.021">
        <li id="R_7.061.021ab">sadevāsuragandharvaṃ samuniṃ sāpsarogaṇam</li>
        <li id="R_7.061.021cd">jagad dhi sarvam asvasthaṃ pitāmaham upasthitam</li>
    </ul>
    <ul id="R_7.061.022">
        <li id="R_7.061.022ab">ūcuś ca devadeveśaṃ varadaṃ prapitāmaham</li>
        <li id="R_7.061.022cd">kaccil lokakṣayo deva prāpto vā yugasaṃkayaḥ</li>
    </ul>
    <ul id="R_7.061.023">
        <li id="R_7.061.023ab">nedṛśaṃ dṛṣṭapūrvaṃ na śrutaṃ vā prapitāmaha</li>
        <li id="R_7.061.023cd">devānāṃ bhayasaṃmoho lokānāṃ saṃkṣayaḥ prabho</li>
    </ul>
    <ul id="R_7.061.024">
        <li id="R_7.061.024ab">teṣāṃ tad vacanaṃ śrutvā brahmā lokapitāmanaḥ</li>
        <li id="R_7.061.024cd">bhayakāraṇam ācaṣṭe devānām abhayaṃkaraḥ</li>
    </ul>
    <ul id="R_7.061.025">
        <li id="R_7.061.025ab">vadhāya lavaṇasyājau śaraḥ śatrughnadhāritaḥ</li>
        <li id="R_7.061.025cd">tejasā yasya sarve sma saṃmūḍhāḥ surasattamāḥ</li>
    </ul>
    <ul id="R_7.061.026">
        <li id="R_7.061.026ab">eṣo hi pūrvaṃ devasya lokakartuḥ sanātanaḥ</li>
        <li id="R_7.061.026cd">śaras tejomayo vatsā yena vai bhayam āgatam</li>
    </ul>
    <ul id="R_7.061.027">
        <li id="R_7.061.027ab">eṣa vai kaiṭabhasyārthe madhunaś ca mahāśaraḥ</li>
        <li id="R_7.061.027cd">sṛṣṭo mahātmanā tena vadhārthaṃ daityayos tayoḥ</li>
    </ul>
    <ul id="R_7.061.028">
        <li id="R_7.061.028ab">evam etaṃ prajānīdhvaṃ viṣṇos tejomayaṃ śaram</li>
        <li id="R_7.061.028cd">eṣā caiva tanuḥ pūrvā viṣṇos tasya mahātmanaḥ</li>
    </ul>
    <ul id="R_7.061.029">
        <li id="R_7.061.029ab">ito gacchatā paśyadhvaṃ vadhyamānaṃ mahātmanā</li>
        <li id="R_7.061.029cd">rāmānujena vīreṇa lavaṇaṃ rākṣasottamam</li>
    </ul>
    <ul id="R_7.061.030">
        <li id="R_7.061.030ab">tasya te devadevasya niśamya madhurāṃ giram</li>
        <li id="R_7.061.030cd">ājagmur yatra yudhyete śatrughnalavaṇāv ubhau</li>
    </ul>
    <ul id="R_7.061.031">
        <li id="R_7.061.031ab">taṃ śaraṃ divyasaṃkāśaṃ śatrughnakaradhāritam</li>
        <li id="R_7.061.031cd">dadṛśuḥ sarvabhūtāni yugāntāgnim ivotthitam</li>
    </ul>
    <ul id="R_7.061.032">
        <li id="R_7.061.032ab">ākāśam āvṛtaṃ dṛṣṭvā devair hi raghunandanaḥ</li>
        <li id="R_7.061.032cd">siṃhanādaṃ muhuḥ kṛtvā dadarśa lavaṇaṃ punaḥ</li>
    </ul>
    <ul id="R_7.061.033">
        <li id="R_7.061.033ab">āhūtaś ca tatas tena śatrughnena mahātmanā</li>
        <li id="R_7.061.033cd">lavaṇaḥ krodhasaṃyukto yuddhāya samupasthitaḥ</li>
    </ul>
    <ul id="R_7.061.034">
        <li id="R_7.061.034ab">ākarṇāt sa vikṛṣyātha tad dhanur dhanvināṃ varaḥ</li>
        <li id="R_7.061.034cd">sa mumoca mahābāṇaṃ lavaṇasya mahorasi</li>
        <li id="R_7.061.034ef">uras tasya vidāryāśu praviveśa rasātalam</li>
    </ul>
    <ul id="R_7.061.035">
        <li id="R_7.061.035ab">gatvā rasātalaṃ divyaṃ śaro vibudhapūjitaḥ</li>
        <li id="R_7.061.035cd">punar evāgamat tūrṇam ikṣvākukulanandanam</li>
    </ul>
    <ul id="R_7.061.036">
        <li id="R_7.061.036ab">śatrughnaśaranirbhinno lavaṇaḥ sa niśācaraḥ</li>
        <li id="R_7.061.036cd">papāta sahasā bhūmau vajrāhata ivācalaḥ</li>
    </ul>
    <ul id="R_7.061.037">
        <li id="R_7.061.037ab">tac ca divyaṃ mahac chūlaṃ hate lavaṇarākṣase</li>
        <li id="R_7.061.037cd">paśyatāṃ sarvabhūtānāṃ rudrasya vaśam anvagāt</li>
    </ul>
    <ul id="R_7.061.038">
        <li id="R_7.061.038ab">
            <seg type="pāda" n="a">ekeṣupātena bhayaṃ nihatya</seg>
            <seg type="pāda" n="b">lokatrayasyāsya raghupravīraḥ</seg>
        </li>
        <li id="R_7.061.038cd">
            <seg type="pāda" n="c">vinirbabhāv udyatacāpabāṇas</seg>
            <seg type="pāda" n="d">tamaḥ praṇudyeva sahasraraśmiḥ</seg>
        </li>
    </ul>
    <ul id="R_7.062.001">
        <li id="R_7.062.001ab">hate tu lavaṇe devāḥ sendrāḥ sāgnipurogamāḥ</li>
        <li id="R_7.062.001cd">ūcuḥ sumadhurāṃ vāṇīṃ śatrughnāṃ śatrutāpanam</li>
    </ul>
    <ul id="R_7.062.002">
        <li id="R_7.062.002ab">diṣṭyā te vijayo vatsa diṣṭya lavaṇarākṣasaḥ</li>
        <li id="R_7.062.002cd">hataḥ puruṣaśārdūlavaraṃ varaya rāghava</li>
    </ul>
    <ul id="R_7.062.003">
        <li id="R_7.062.003ab">varadāḥ sma mahābāho sarva eva samāgatāḥ</li>
        <li id="R_7.062.003cd">vijayākāṅkṣiṇas tubhyam amoghaṃ darśanaṃ hi naḥ</li>
    </ul>
    <ul id="R_7.062.004">
        <li id="R_7.062.004ab">devānāṃ bhāṣitaṃ śrutvā śūro mūrdhni kṛtāñjaliḥ</li>
        <li id="R_7.062.004cd">pratyuvāca mahābāhuḥ śatrughnaḥ prayatātmavān</li>
    </ul>
    <ul id="R_7.062.005">
        <li id="R_7.062.005ab">imāṃ madhupurīṃ ramyāṃ madhurāṃ deva nirmitām</li>
        <li id="R_7.062.005cd">niveśaṃ prapnuyāṃ śīghram eṣa me 'stu varo mataḥ</li>
    </ul>
    <ul id="R_7.062.006">
        <li id="R_7.062.006ab">taṃ devāḥ prītamanaso bāḍham ity eva rāghavam</li>
        <li id="R_7.062.006cd">bhaviṣyati purī ramyā śūrasenā na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.062.007">
        <li id="R_7.062.007ab">te tathoktvā mahātmāno divam āruruhus tadā</li>
        <li id="R_7.062.007cd">śatrughno 'pi mahātejās tāṃ senāṃ samupānayat</li>
    </ul>
    <ul id="R_7.062.008">
        <li id="R_7.062.008ab">sā sena śīghram āgacchac chrutvā śatrughnaśāsanam</li>
        <li id="R_7.062.008cd">niveśanaṃ ca śatrughnaḥ śāsanena samārabhat</li>
    </ul>
    <ul id="R_7.062.009">
        <li id="R_7.062.009ab">sā purī divyasaṃkāśā varṣe dvādaśame śubhā</li>
        <li id="R_7.062.009cd">niviṣṭā śūrasenānāṃ viṣayaś cākutobhayaḥ</li>
    </ul>
    <ul id="R_7.062.010">
        <li id="R_7.062.010ab">kṣetrāṇi sasya yuktāni kāle varṣati vāsavaḥ</li>
        <li id="R_7.062.010cd">arogā vīrapuruṣā śatrughnabhujapālitā</li>
    </ul>
    <ul id="R_7.062.011">
        <li id="R_7.062.011ab">ardhacandrapratīkāśā yamunātīraśobhitā</li>
        <li id="R_7.062.011cd">śobhitā gṛhamukhyaiś ca śobhitā catvarāpaṇaiḥ</li>
    </ul>
    <ul id="R_7.062.012">
        <li id="R_7.062.012ab">yac ca tena mahac chūnyaṃ lavaṇena kṛtaṃ purā</li>
        <li id="R_7.062.012cd">śobhayām āsa tad vīro nānāpaṇyasamṛddhibhiḥ</li>
    </ul>
    <ul id="R_7.062.013">
        <li id="R_7.062.013ab">tāṃ samṛddhāṃ samṛddhārthaḥ śatrughno bharatānujaḥ</li>
        <li id="R_7.062.013cd">nirīkṣya paramaprītaḥ paraṃ harṣam upāgamat</li>
    </ul>
    <ul id="R_7.062.014">
        <li id="R_7.062.014ab">tasya buddhiḥ samutpannā niveśya madhurāṃ purīm</li>
        <li id="R_7.062.014cd">rāmapādau nirīkṣeyaṃ varṣe dvādaśame śubhe</li>
    </ul>
    <ul id="R_7.063.001">
        <li id="R_7.063.001ab">tato dvādaśame varṣe śatrughno rāmapālitām</li>
        <li id="R_7.063.001cd">ayodhyāṃ cakame gantum alpabhṛtyabalānugaḥ</li>
    </ul>
    <ul id="R_7.063.002">
        <li id="R_7.063.002ab">mantriṇo balamukhyāṃś ca nivartya ca purodhasaṃ</li>
        <li id="R_7.063.002cd">jagāma rathamukhyena hayayuktena bhāsvatā</li>
    </ul>
    <ul id="R_7.063.003">
        <li id="R_7.063.003ab">sa gatvā gaṇitān vāsān saptāṣṭau raghunandanaḥ</li>
        <li id="R_7.063.003cd">ayodhyām agamat tūrṇaṃ rāghavotsukadarśanaḥ</li>
    </ul>
    <ul id="R_7.063.004">
        <li id="R_7.063.004ab">sa praviśya purīṃ ramyāṃ śrīmān ikṣvākunandanaḥ</li>
        <li id="R_7.063.004cd">praviveśa mahābāhur yatra rāmo mahādyutiḥ</li>
    </ul>
    <ul id="R_7.063.005">
        <li id="R_7.063.005ab">so 'bhivādya mahātmānaṃ jvalantam iva tejasā</li>
        <li id="R_7.063.005cd">uvāca prāñjalir bhūtvā rāmaṃ satyaparākramam</li>
    </ul>
    <ul id="R_7.063.006">
        <li id="R_7.063.006ab">yad ājñaptaṃ mahārāja sarvaṃ tat kṛtavān aham</li>
        <li id="R_7.063.006cd">hataḥ sa lavaṇaḥ pāpaḥ purī sā ca niveśitā</li>
    </ul>
    <ul id="R_7.063.007">
        <li id="R_7.063.007ab">dvādaśaṃ ca gataṃ varṣaṃ tvāṃ vinā raghunandana</li>
        <li id="R_7.063.007cd">notsaheyam ahaṃ vastuṃ tvayā virahito nṛpa</li>
    </ul>
    <ul id="R_7.063.008">
        <li id="R_7.063.008ab">sa me prasādaṃ kākutstha kuruṣvāmitavikrama</li>
        <li id="R_7.063.008cd">mātṛhīno yathā vatsas tvāṃ vinā pravasāmy aham</li>
    </ul>
    <ul id="R_7.063.009">
        <li id="R_7.063.009ab">evaṃ bruvāṇaṃ śatrughnaṃ pariṣvajyedam abravīt</li>
        <li id="R_7.063.009cd">mā viṣādaṃ kṛthā vīra naitat kṣatriya ceṣṭitam</li>
    </ul>
    <ul id="R_7.063.010">
        <li id="R_7.063.010ab">nāvasīdanti rājāno vipravāseṣu rāghava</li>
        <li id="R_7.063.010cd">prajāś ca paripālyā hi kṣatradharmeṇa rāghava</li>
    </ul>
    <ul id="R_7.063.011">
        <li id="R_7.063.011ab">kāle kāle ca māṃ vīra ayodhyām avalokitum</li>
        <li id="R_7.063.011cd">āgaccha tvaṃ naraśreṣṭha gantāsi ca puraṃ tava</li>
    </ul>
    <ul id="R_7.063.012">
        <li id="R_7.063.012ab">mamāpi tvaṃ sudayitaḥ prāṇair api na saṃśayaḥ</li>
        <li id="R_7.063.012cd">avaśyaṃ karaṇīyaṃ ca rājyasya paripālanam</li>
    </ul>
    <ul id="R_7.063.013">
        <li id="R_7.063.013ab">tasmāt tvaṃ vasa kākutstha pañcarātraṃ mayā saha</li>
        <li id="R_7.063.013cd">ūrdhvaṃ gantāsi madhurāṃ sabhṛtyabalavāhanaḥ</li>
    </ul>
    <ul id="R_7.063.014">
        <li id="R_7.063.014ab">rāmasyaitad vacaḥ śrutvā dharmayuktaṃ mano'nugam</li>
        <li id="R_7.063.014cd">śatrughno dīnayā vācā bāḍham ity eva cābravīt</li>
    </ul>
    <ul id="R_7.063.015">
        <li id="R_7.063.015ab">sa pañcarātraṃ kākutstho rāghavasya yathājñayā</li>
        <li id="R_7.063.015cd">uṣya tatra maheṣvāso gamanāyopacakrame</li>
    </ul>
    <ul id="R_7.063.016">
        <li id="R_7.063.016ab">āmantrya tu mahātmānaṃ rāmaṃ satyaparākramam</li>
        <li id="R_7.063.016cd">bharataṃ lakṣmaṇaṃ caiva mahāratham upāruhat</li>
    </ul>
    <ul id="R_7.063.017">
        <li id="R_7.063.017ab">dūraṃ tābhyām anugato lakṣmaṇena mahātmanā</li>
        <li id="R_7.063.017cd">bharatena ca śatrughno jagāmāśu purīṃ tadā</li>
    </ul>
    <ul id="R_7.064.001">
        <li id="R_7.064.001ab">prasthāpya tu sa śatrughnaṃ bhrātṛbhyāṃ saha rāghavaḥ</li>
        <li id="R_7.064.001cd">pramumoda sukhī rājyaṃ dharmeṇa paripālayan</li>
    </ul>
    <ul id="R_7.064.002">
        <li id="R_7.064.002ab">tataḥ katipayāhaḥsu vṛddho jānapado dvijaḥ</li>
        <li id="R_7.064.002cd">śavaṃ bālam upādāya rājadvāram upāgamat</li>
    </ul>
    <ul id="R_7.064.003">
        <li id="R_7.064.003ab">rudan bahuvidhā vācaḥ snehākṣarasamanvitāḥ</li>
        <li id="R_7.064.003cd">asakṛt putraputreti vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.064.004">
        <li id="R_7.064.004ab">kiṃ nu me duṣkṛtaṃ karma pūrvaṃ dehāntare kṛtam</li>
        <li id="R_7.064.004cd">yad ahaṃ putram ekaṃ tvāṃ paśyāmi nidhanaṃ gatam</li>
    </ul>
    <ul id="R_7.064.005">
        <li id="R_7.064.005ab">aprāptayauvanaṃ bālaṃ pañcavarṣasamanvitam</li>
        <li id="R_7.064.005cd">akāle kālam āpannaṃ duḥkhāya mama putraka</li>
    </ul>
    <ul id="R_7.064.006">
        <li id="R_7.064.006ab">alpair ahobhir nidhanaṃ gamiṣyāmi na saṃśayaḥ</li>
        <li id="R_7.064.006cd">ahaṃ ca jananī caiva tava śokena putraka</li>
    </ul>
    <ul id="R_7.064.007">
        <li id="R_7.064.007ab">na smarāmy anṛtaṃ hy uktaṃ na ca hiṃsāṃ smarāmy aham</li>
        <li id="R_7.064.007cd">kena me duṣkṛtenādya bāla eva mamātmajaḥ</li>
        <li id="R_7.064.007ef">akṛtvā pitṛkāryāṇi nīto vaivasvatakṣayam</li>
    </ul>
    <ul id="R_7.064.008">
        <li id="R_7.064.008ab">nedṛśaṃ dṛṣṭapūrvaṃ me śrutaṃ vā ghoradarśanam</li>
        <li id="R_7.064.008cd">mṛtyur aprāptakālānāṃ rāmasya viṣaye yathā</li>
    </ul>
    <ul id="R_7.064.009">
        <li id="R_7.064.009ab">rāmasya duṣkṛtaṃ kiṃ cin mahad asti na saṃśayaḥ</li>
        <li id="R_7.064.009cd">tvaṃ rājañ jīvayasvainaṃ bālaṃ mṛtyuvaśaṃ gatam</li>
    </ul>
    <ul id="R_7.064.010">
        <li id="R_7.064.010ab">bhrātṛbhiḥ sahito rājan dīrgham āyur avāpnuhi</li>
        <li id="R_7.064.010cd">uṣitāḥ sma sukhaṃ rājye tavāsmin sumahābala</li>
    </ul>
    <ul id="R_7.064.011">
        <li id="R_7.064.011ab">saṃpraty anātho viṣaya ikṣvākūṇāṃ mahātmanām</li>
        <li id="R_7.064.011cd">rāmaṃ nātham ihāsādya bālāntakaraṇaṃ nṛpam</li>
    </ul>
    <ul id="R_7.064.012">
        <li id="R_7.064.012ab">rājadoṣair vipadyante prajā hy avidhipālitāḥ</li>
        <li id="R_7.064.012cd">asadvṛtte tu nṛpatāv akāle mriyate janaḥ</li>
    </ul>
    <ul id="R_7.064.013">
        <li id="R_7.064.013ab">yadā pureṣv ayuktāni janā janapadeśu ca</li>
        <li id="R_7.064.013cd">kurvate na ca rakṣāsti tadākālakṛtaṃ bhayam</li>
    </ul>
    <ul id="R_7.064.014">
        <li id="R_7.064.014ab">savyaktaṃ rājadoṣo 'yaṃ bhaviṣyati na saṃśayaḥ</li>
        <li id="R_7.064.014cd">pure janapade vāpi tadā bālavadho hy ayam</li>
    </ul>
    <ul id="R_7.064.015">
        <li id="R_7.064.015ab">evaṃ bahuvidhair vākyair nindayāno muhur muhuḥ</li>
        <li id="R_7.064.015cd">rājānaṃ duḥkhasaṃtaptaḥ sutaṃ tam upagūhati</li>
    </ul>
    <ul id="R_7.065.001">
        <li id="R_7.065.001ab">tathā tu karuṇaṃ tasya dvijasya paridevitam</li>
        <li id="R_7.065.001cd">śuśrāva rāghavaḥ sarvaṃ duḥkhaśokasamanvitam</li>
    </ul>
    <ul id="R_7.065.002">
        <li id="R_7.065.002ab">sa duḥkhena susaṃtapto mantriṇaḥ samupāhvayat</li>
        <li id="R_7.065.002cd">vasiṣṭhaṃ vāmadevaṃ ca bhrātṝṃś ca sahanaigamān</li>
    </ul>
    <ul id="R_7.065.003">
        <li id="R_7.065.003ab">tato dvijā vasiṣṭhena sārdham aṣṭau praveśitāḥ</li>
        <li id="R_7.065.003cd">rājānaṃ devasaṃkāśaṃ vardhasveti tato 'bruvan</li>
    </ul>
    <ul id="R_7.065.004">
        <li id="R_7.065.004ab">mārkaṇḍeyo 'tha maudgalyo vāmadevaś ca kāśyapaḥ</li>
        <li id="R_7.065.004cd">kātyāyano 'tha jābālir gautamo nāradas tathā</li>
    </ul>
    <ul id="R_7.065.005">
        <li id="R_7.065.005ab">ete dvijarṣabhāḥ sarve āganeṣūpaveśitāḥ</li>
        <li id="R_7.065.005cd">mantriṇo naigamāś caiva yathārham anukūlataḥ</li>
    </ul>
    <ul id="R_7.065.006">
        <li id="R_7.065.006ab">teṣāṃ samupaviṣṭānāṃ sarveṣāṃ dīptatejasām</li>
        <li id="R_7.065.006cd">raghavaḥ sarvam ācaṣṭe dvijo yasmāt praroditi</li>
    </ul>
    <ul id="R_7.065.007">
        <li id="R_7.065.007ab">tasya tad vacanaṃ śrutvā rājño dīnasya nāradaḥ</li>
        <li id="R_7.065.007cd">pratyuvāca śubhaṃ vākyam ṛṣīṇāṃ saṃnidhau nṛpam</li>
    </ul>
    <ul id="R_7.065.008">
        <li id="R_7.065.008ab">śṛṇu rājan yathākāle prāpto 'yaṃ bālasaṃkṣayaḥ</li>
        <li id="R_7.065.008cd">śrutvā kartavyatāṃ vīra kuruṣva raghunandana</li>
    </ul>
    <ul id="R_7.065.009">
        <li id="R_7.065.009ab">purā kṛtayuge rāma brāhmaṇā vai tapasvinaḥ</li>
        <li id="R_7.065.009cd">abrāhmaṇas tadā rājan na tapasvī kathaṃ cana</li>
    </ul>
    <ul id="R_7.065.010">
        <li id="R_7.065.010ab">tasmin yuge prajvalite brahmabhūte anāvṛte</li>
        <li id="R_7.065.010cd">amṛtyavas tadā sarve jajñire dīrghadarśinaḥ</li>
    </ul>
    <ul id="R_7.065.011">
        <li id="R_7.065.011ab">tatas tretāyugaṃ nāma mānavānāṃ vapuṣmatām</li>
        <li id="R_7.065.011cd">kṣatriyā yatra jāyante pūrveṇa tapasānvitāḥ</li>
    </ul>
    <ul id="R_7.065.012">
        <li id="R_7.065.012ab">vīryeṇa tapasā caiva te 'dhikāḥ pūrvajanmani</li>
        <li id="R_7.065.012cd">mānavā ye mahātmānas tasmiṃs tretāyuge yuge</li>
    </ul>
    <ul id="R_7.065.013">
        <li id="R_7.065.013ab">brahmakṣatraṃ tu tat sarvaṃ yat pūrvam aparaṃ ca yat</li>
        <li id="R_7.065.013cd">yugayor ubhayor āsīt samavīryasamanvitam</li>
    </ul>
    <ul id="R_7.065.014">
        <li id="R_7.065.014ab">apaśyantas tu te sarve viśeṣam adhikaṃ tataḥ</li>
        <li id="R_7.065.014cd">sthāpanaṃ cakrire tatra cāturvarṇyasya sarvataḥ</li>
    </ul>
    <ul id="R_7.065.015">
        <li id="R_7.065.015ab">adharmaḥ pādam ekaṃ tu pātayat pṛthivītale</li>
        <li id="R_7.065.015cd">adharmeṇa hi saṃyuktās tena mandābhavan dvijāḥ</li>
    </ul>
    <ul id="R_7.065.016">
        <li id="R_7.065.016ab">tataḥ prāduṣkṛtaṃ pūrvam āyuṣaḥ pariniṣṭhitam</li>
        <li id="R_7.065.016cd">śubhāny evācaraṃl lokāḥ satyadharmaparāyaṇāḥ</li>
    </ul>
    <ul id="R_7.065.017">
        <li id="R_7.065.017ab">tretāyuge tv avartanta brāhmaṇāḥ kṣatriyaś ca ye</li>
        <li id="R_7.065.017cd">tapo 'tapyanta te sarve śuśrūṣām apare janāḥ</li>
    </ul>
    <ul id="R_7.065.018">
        <li id="R_7.065.018ab">sa dharmaḥ paramas teṣāṃ vaiśyaśūdram athāgamat</li>
        <li id="R_7.065.018cd">pūjāṃ ca sarvavarṇānāṃ śūdrāś cakrur viśeṣataḥ</li>
    </ul>
    <ul id="R_7.065.019">
        <li id="R_7.065.019ab">tataḥ pādam adharmasya dvitīyam avatārayat</li>
        <li id="R_7.065.019cd">tato dvāparasaṃkhyā sā yugasya samajāyata</li>
    </ul>
    <ul id="R_7.065.020">
        <li id="R_7.065.020ab">tasmin dvāparasaṃkhye tu vartamāne yugakṣaye</li>
        <li id="R_7.065.020cd">adharmaś cānṛtaṃ caiva vavṛdhe puruṣarṣabha</li>
    </ul>
    <ul id="R_7.065.021">
        <li id="R_7.065.021ab">tasmin dvāparasaṃkhyāte tapo vaiśyān samāviśat</li>
        <li id="R_7.065.021cd">na śūdro labhate dharmam ugraṃ taptaṃ nararṣabha</li>
    </ul>
    <ul id="R_7.065.022">
        <li id="R_7.065.022ab">hīnavarṇo naraśreṣṭha tapyate sumahat tapaḥ</li>
        <li id="R_7.065.022cd">bhaviṣyā śūdrayonyāṃ hi tapaścaryā kalau yuge</li>
    </ul>
    <ul id="R_7.065.023">
        <li id="R_7.065.023ab">adharmaḥ paramo rāma dvāpare śūdradhāritaḥ</li>
        <li id="R_7.065.023cd">sa vai viṣayaparyante tava rājan mahātapāḥ</li>
        <li id="R_7.065.023ef">śūdras tapyati durbuddhis tena bālavadho hy ayam</li>
    </ul>
    <ul id="R_7.065.024">
        <li id="R_7.065.024ab">yo hy adharmam akāryaṃ vā viṣaye pārthivasya hi</li>
        <li id="R_7.065.024cd">karoti rājaśārdūla pure vā durmatir naraḥ</li>
        <li id="R_7.065.024ef">kṣipraṃ hi narakaṃ yāti sa ca rājā na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.065.025">
        <li id="R_7.065.025ab">sa tvaṃ puruṣaśārdūla mārgasva viṣayaṃ svakam</li>
        <li id="R_7.065.025cd">duṣkṛtaṃ yatra paśyethās tatra yatnaṃ samācara</li>
    </ul>
    <ul id="R_7.065.026">
        <li id="R_7.065.026ab">evaṃ te dharmavṛddhiś ca nṛṇāṃ cāyurvivardhanam</li>
        <li id="R_7.065.026cd">bhaviṣyati naraśreṣṭha bālasyāsya ca jīvitam</li>
    </ul>
    <ul id="R_7.066.001">
        <li id="R_7.066.001ab">nāradasya tu tad vākyaṃ śrutvāmṛtamayaṃ yathā</li>
        <li id="R_7.066.001cd">praharṣam atulaṃ lebhe lakṣmaṇaṃ cedam abravīt</li>
    </ul>
    <ul id="R_7.066.002">
        <li id="R_7.066.002ab">gaccha saumya dvijaśreṣṭhaṃ samāśvāsaya lakṣmaṇa</li>
        <li id="R_7.066.002cd">bālasya ca śarīraṃ tat tailadroṇyāṃ nidhāpaya</li>
    </ul>
    <ul id="R_7.066.003">
        <li id="R_7.066.003ab">gandhaiś ca paramodārais tailaiś ca susugandhibhiḥ</li>
        <li id="R_7.066.003cd">yathā na kṣīyate bālas tathā saumya vidhīyatām</li>
    </ul>
    <ul id="R_7.066.004">
        <li id="R_7.066.004ab">yathā śarīre bālasya guptasyākliṣṭakarmaṇaḥ</li>
        <li id="R_7.066.004cd">vipattiḥ paribhedo vā bhaven na ca tathā kuru</li>
    </ul>
    <ul id="R_7.066.005">
        <li id="R_7.066.005ab">tathā saṃdiśya kākutstho lakṣmaṇaṃ śubhalakṣaṇam</li>
        <li id="R_7.066.005cd">manasā puṣpakaṃ dadhyāv āgaccheti mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.066.006">
        <li id="R_7.066.006ab">iṅgitaṃ sa tu vijñāya puṣpako hemabhūṣitaḥ</li>
        <li id="R_7.066.006cd">ājagāma muhūrtena saṃpīpaṃ rāghavasya vai</li>
    </ul>
    <ul id="R_7.066.007">
        <li id="R_7.066.007ab">so 'bravīt praṇato bhūtvā ayam asmi narādhipa</li>
        <li id="R_7.066.007cd">vaśyas tava mahābāho kiṃkaraḥ samupasthitaḥ</li>
    </ul>
    <ul id="R_7.066.008">
        <li id="R_7.066.008ab">bhāṣitaṃ ruciraṃ śrutvā puṣpakasya narādhipaḥ</li>
        <li id="R_7.066.008cd">abhivādya maharṣīs tān vimānaṃ so 'dhyarohata</li>
    </ul>
    <ul id="R_7.066.009">
        <li id="R_7.066.009ab">dhanur gṛhītvā tūṇīṃ ca khagdaṃ ca ruciraprabham</li>
        <li id="R_7.066.009cd">nikṣipya nagare vīrau saumitribharatāv ubhau</li>
    </ul>
    <ul id="R_7.066.010">
        <li id="R_7.066.010ab">prāyāt pratīcīṃ sa marūn vicinvaṃś ca samantataḥ</li>
        <li id="R_7.066.010cd">uttarām agamac chrīmān diśaṃ himavadāvṛtam</li>
    </ul>
    <ul id="R_7.066.011">
        <li id="R_7.066.011ab">apaśyamānas tatrāpi svalpam apy atha duṣkṛtam</li>
        <li id="R_7.066.011cd">pūrvām api diśaṃ sarvām athāpaśyan narādhipaḥ</li>
    </ul>
    <ul id="R_7.066.012">
        <li id="R_7.066.012ab">dakṣiṇāṃ diśam ākrāmat tato rājarṣinandanaḥ</li>
        <li id="R_7.066.012cd">śaivalasyottare pārśve dadarśa sumahat saraḥ</li>
    </ul>
    <ul id="R_7.066.013">
        <li id="R_7.066.013ab">tasmin sarasi tapyantaṃ tāpasaṃ sumahat tapaḥ</li>
        <li id="R_7.066.013cd">dadarśa rāghavaḥ śrīmāṃl lambamānam adho mukham</li>
    </ul>
    <ul id="R_7.066.014">
        <li id="R_7.066.014ab">athainaṃ samupāgamya tapyantaṃ tapa uttamam</li>
        <li id="R_7.066.014cd">uvāca rāghavo vākyaṃ dhanyas tvam asi suvrata</li>
    </ul>
    <ul id="R_7.066.015">
        <li id="R_7.066.015ab">kasyāṃ yonyāṃ tapovṛddhavartase dṛḍhavikrama</li>
        <li id="R_7.066.015cd">kautūhalāt tvāṃ pṛcchāmi rāmo dāśarathir hy aham</li>
    </ul>
    <ul id="R_7.066.016">
        <li id="R_7.066.016ab">manīṣitas te ko nv arthaḥ svargalābho varāśrayaḥ</li>
        <li id="R_7.066.016cd">yam aśritya tapas taptaṃ śrotum icchāmi tāpasa</li>
    </ul>
    <ul id="R_7.066.017">
        <li id="R_7.066.017ab">brāhmaṇo vāsi bhadraṃ te kṣatriyo vāsi durjayaḥ</li>
        <li id="R_7.066.017cd">vaiśyo vā yadi vā śūdraḥ satyam etad bravīhi me</li>
    </ul>
    <ul id="R_7.067.001">
        <li id="R_7.067.001ab">tasya tad vacanaṃ śrutvā rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_7.067.001cd">avākśirās tathābhūto vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.067.002">
        <li id="R_7.067.002ab">śūdrayonyāṃ prasūto 'smi tapa ugraṃ samāsthitaḥ</li>
        <li id="R_7.067.002cd">devatvaṃ prārthaye rāma saśarīro mahāyaśaḥ</li>
    </ul>
    <ul id="R_7.067.003">
        <li id="R_7.067.003ab">na mithyāhaṃ vade rājan devalokajigīṣayā</li>
        <li id="R_7.067.003cd">śūdraṃ māṃ viddhi kākutstha śambūkaṃ nāma nāmataḥ</li>
    </ul>
    <ul id="R_7.067.004">
        <li id="R_7.067.004ab">bhāṣatas tasya śūdrasya khaḍgaṃ suruciraprabham</li>
        <li id="R_7.067.004cd">niṣkṛṣya kośād vimalaṃ śiraś ciccheda rāghavaḥ</li>
    </ul>
    <ul id="R_7.067.005">
        <li id="R_7.067.005ab">tasmin muhūrte bālo 'sau jīvena samayujyata</li>
    </ul>
    <ul id="R_7.067.006">
        <li id="R_7.067.006ab">tato 'gastyāśramapadaṃ rāmaḥ kamalalocanaḥ</li>
        <li id="R_7.067.006cd">sa gatvā vinayenaiva taṃ natvā mumude sukhī</li>
    </ul>
    <ul id="R_7.067.007">
        <li id="R_7.067.007ab">so 'bhivādya mahātmānaṃ jvalantam iva tejasā</li>
        <li id="R_7.067.007cd">ātithyaṃ paramaṃ prāpya niṣasāda narādhipaḥ</li>
    </ul>
    <ul id="R_7.067.008">
        <li id="R_7.067.008ab">tam uvāca mahātejāḥ kumbhayonir mahātapāḥ</li>
        <li id="R_7.067.008cd">svāgataṃ te naraśreṣṭha diṣṭyā prāpto 'si rāghava</li>
    </ul>
    <ul id="R_7.067.009">
        <li id="R_7.067.009ab">tvaṃ me bahumato rāma guṇair bahubhir uttamaiḥ</li>
        <li id="R_7.067.009cd">atithiḥ pūjanīyaś ca māma rājan hṛdi sthitaḥ</li>
    </ul>
    <ul id="R_7.067.010">
        <li id="R_7.067.010ab">surā hi kathayanti tvām āgataṃ śūdraghātinam</li>
        <li id="R_7.067.010cd">brāhmaṇasya tu dharmeṇa tvayā jīvāpitaḥ sutaḥ</li>
    </ul>
    <ul id="R_7.067.011">
        <li id="R_7.067.011ab">uṣyatāṃ ceha rajanīṃ sakāśe mama rāghava</li>
        <li id="R_7.067.011cd">prabhāte puṣpakeṇa tvaṃ gantā svapuram eva hi</li>
    </ul>
    <ul id="R_7.067.012">
        <li id="R_7.067.012ab">idaṃ cābharaṇaṃ saumya nirmitaṃ viśvakarmaṇā</li>
        <li id="R_7.067.012cd">divyaṃ divyena vapuṣā dīpyamānaṃ svatejasā</li>
        <li id="R_7.067.012ef">pratigṛhṇīṣva kākutstha matpriyaṃ kuru rāghava</li>
    </ul>
    <ul id="R_7.067.013">
        <li id="R_7.067.013ab">dattasya hi punar dānaṃ sumahat phalam ucyate</li>
        <li id="R_7.067.013cd">tasmāt pradāsye vidhivat tat pratīccha nararṣabha</li>
    </ul>
    <ul id="R_7.067.014">
        <li id="R_7.067.014ab">tad rāmaḥ pratijagrāha munes tasya mahātmanaḥ</li>
        <li id="R_7.067.014cd">divyam ābharaṇaṃ citraṃ pradīptam iva bhāskaram</li>
    </ul>
    <ul id="R_7.067.015">
        <li id="R_7.067.015ab">pratigṛhya tato rāmas tad ābharaṇam uttamam</li>
        <li id="R_7.067.015cd">āgamaṃ tasya divyasya praṣṭum evopacakrame</li>
    </ul>
    <ul id="R_7.067.016">
        <li id="R_7.067.016ab">atyadbhutam idaṃ brahman vapuṣā yuktam uttamam</li>
        <li id="R_7.067.016cd">kathaṃ bhagavatā prāptaṃ kuto vā kena vāhṛtam</li>
    </ul>
    <ul id="R_7.067.017">
        <li id="R_7.067.017ab">kutūhalatayā brahman pṛcchāmi tvāṃ mahāyaśaḥ</li>
        <li id="R_7.067.017cd">āścaryāṇāṃ bahūnāṃ hi nidhiḥ paramako bhavān</li>
    </ul>
    <ul id="R_7.067.018">
        <li id="R_7.067.018ab">evaṃ bruvati kākutsthe munir vākyam athābravīt</li>
        <li id="R_7.067.018cd">śṛṇu rāma yathāvṛttaṃ purā tretāyuge gate</li>
    </ul>
    <ul id="R_7.068.001">
        <li id="R_7.068.001ab">purā tretāyuge hy āsīd araṇyaṃ bahuvistaram</li>
        <li id="R_7.068.001cd">samantād yojanaśataṃ nirmṛgaṃ pakṣivarjitam</li>
    </ul>
    <ul id="R_7.068.002">
        <li id="R_7.068.002ab">tasmin nirmānuṣe 'raṇye kurvāṇas tapa uttamam</li>
        <li id="R_7.068.002cd">aham ākramituṃ śaumya tad araṇyam upāgamam</li>
    </ul>
    <ul id="R_7.068.003">
        <li id="R_7.068.003ab">tasya rūpam araṇyasya nirdeṣṭuṃ na śaśāka ha</li>
        <li id="R_7.068.003cd">phalamūlaiḥ sukhāsvādair bahurūpaiś ca pādapaiḥ</li>
    </ul>
    <ul id="R_7.068.004">
        <li id="R_7.068.004ab">tasyāraṇyasya madhye tu saro yojanam āyatam</li>
        <li id="R_7.068.004cd">padmotpalasamākīrṇaṃ samatikrāntaśaivalam</li>
    </ul>
    <ul id="R_7.068.005">
        <li id="R_7.068.005ab">tad āścaryam ivātyarthaṃ sukhāsvādam anuttamam</li>
        <li id="R_7.068.005cd">arajaskaṃ tathākṣobhyaṃ śrīmatpakṣigaṇāyutam</li>
    </ul>
    <ul id="R_7.068.006">
        <li id="R_7.068.006ab">tasmin saraḥsamīpe tu mahad adbhutam āśramam</li>
        <li id="R_7.068.006cd">purāṇaṃ puṇyam atyarthaṃ tapasvijanavarjitam</li>
    </ul>
    <ul id="R_7.068.007">
        <li id="R_7.068.007ab">tatrāham avasaṃ rātriṃ naidāghīṃ puruṣarṣabha</li>
        <li id="R_7.068.007cd">prabhāte kālyam utthāya saras tad upacakrame</li>
    </ul>
    <ul id="R_7.068.008">
        <li id="R_7.068.008ab">athāpaśyaṃ śavaṃ tatra supuṣṭam ajaraṃ kva cit</li>
        <li id="R_7.068.008cd">tiṣṭhantaṃ parayā lakṣmyā tasmiṃs toyāśaye nṛpa</li>
    </ul>
    <ul id="R_7.068.009">
        <li id="R_7.068.009ab">tam arthaṃ cintayāno 'haṃ muhūrtaṃ tatra rāghava</li>
        <li id="R_7.068.009cd">viṣṭhito 'smi sarastīre kiṃ nv idaṃ syād iti prabho</li>
    </ul>
    <ul id="R_7.068.010">
        <li id="R_7.068.010ab">athāpaśyaṃ muhūrtāt tu divyam adbhutadarśanam</li>
        <li id="R_7.068.010cd">vimānaṃ paramodāraṃ haṃsayuktaṃ manojavam</li>
    </ul>
    <ul id="R_7.068.011">
        <li id="R_7.068.011ab">atyarthaṃ svargiṇaṃ tatra vimāne raghunandana</li>
        <li id="R_7.068.011cd">upāste 'psarasāṃ vīra sahasraṃ divyabhūṣaṇam</li>
        <li id="R_7.068.011ef">gānti geyāni ramyāṇi vādayanti tathāparāḥ</li>
    </ul>
    <ul id="R_7.068.012">
        <li id="R_7.068.012ab">paśyato me tadā rāma vimānād avaruhya ca</li>
        <li id="R_7.068.012cd">taṃ śavaṃ bhakṣayām āsa sa svargī raghunandana</li>
    </ul>
    <ul id="R_7.068.013">
        <li id="R_7.068.013ab">tato bhuktvā yathākāmaṃ māṃsaṃ bahu ca suṣṭhu ca</li>
        <li id="R_7.068.013cd">avatīrya saraḥ svargī saṃspraṣṭum upacakrame</li>
    </ul>
    <ul id="R_7.068.014">
        <li id="R_7.068.014ab">upaspṛśya yathānyāyaṃ sa svargī puruṣarṣabha</li>
        <li id="R_7.068.014cd">āroḍhum upacakrāma vimānavaram uttamam</li>
    </ul>
    <ul id="R_7.068.015">
        <li id="R_7.068.015ab">tam ahaṃ devasaṃkāśam ārohantam udīkṣya vai</li>
        <li id="R_7.068.015cd">athāham abruvaṃ vākyaṃ tam eva puruṣarṣabha</li>
    </ul>
    <ul id="R_7.068.016">
        <li id="R_7.068.016ab">ko bhavān devasaṃkāśa āhāraś ca vigarhitaḥ</li>
        <li id="R_7.068.016cd">tvayāyaṃ bhujyate saumya kiṃ karthaṃ vaktum arhasi</li>
    </ul>
    <ul id="R_7.068.017">
        <li id="R_7.068.017ab">āścaryam īdṛśo bhāvo bhāsvaro devasaṃmataḥ</li>
        <li id="R_7.068.017cd">āhāro garhitaḥ saumya śrotum icchāmi tattvataḥ</li>
    </ul>
    <ul id="R_7.069.001">
        <li id="R_7.069.001ab">bhuktvā tu bhāṣitaṃ vākyaṃ mama rāma śubhākṣaram</li>
        <li id="R_7.069.001cd">prāñjaliḥ pratyuvācedaṃ sa svargī raghunandana</li>
    </ul>
    <ul id="R_7.069.002">
        <li id="R_7.069.002ab">śṛṇu brahman yathāvṛttaṃ mamaitat sukhaduḥkhayoḥ</li>
        <li id="R_7.069.002cd">duratikramaṇīyaṃ hi yathā pṛcchasi māṃ dvija</li>
    </ul>
    <ul id="R_7.069.003">
        <li id="R_7.069.003ab">purā vaidarbhako rājā pitā mama mahāyaśāḥ</li>
        <li id="R_7.069.003cd">sudeva iti vikhyātas triṣu lokeṣu vīryavān</li>
    </ul>
    <ul id="R_7.069.004">
        <li id="R_7.069.004ab">tasya putradvayaṃ brahman dvābhyāṃ strībhyām ajāyata</li>
        <li id="R_7.069.004cd">ahaṃ śveta iti khyāto yavīyān suratho 'bhavat</li>
    </ul>
    <ul id="R_7.069.005">
        <li id="R_7.069.005ab">tataḥ pitari svaryāte paurā mām abhyaṣecayan</li>
        <li id="R_7.069.005cd">tatrāhaṃ kṛtavān rājyaṃ dharmeṇa susamāhitaḥ</li>
    </ul>
    <ul id="R_7.069.006">
        <li id="R_7.069.006ab">evaṃ varṣasahasrāṇi samatītāni suvrata</li>
        <li id="R_7.069.006cd">rājyaṃ kārayato brahman prajā dharmeṇa rakṣataḥ</li>
    </ul>
    <ul id="R_7.069.007">
        <li id="R_7.069.007ab">so 'haṃ nimitte kasmiṃś cid vijñātāyur dvijottama</li>
        <li id="R_7.069.007cd">kāladharmaṃ hṛdi nyasya tato vanam upāgamam</li>
    </ul>
    <ul id="R_7.069.008">
        <li id="R_7.069.008ab">so 'haṃ vanam idaṃ durgaṃ mṛgapakṣivivarjitam</li>
        <li id="R_7.069.008cd">tapaś cartuṃ praviṣṭo 'smi samīpe sarasaḥ śubhe</li>
    </ul>
    <ul id="R_7.069.009">
        <li id="R_7.069.009ab">bhrātaraṃ surathaṃ rājye abhiṣicya narādhipam</li>
        <li id="R_7.069.009cd">idaṃ saraḥ samāsādya tapas taptaṃ mayā ciram</li>
    </ul>
    <ul id="R_7.069.010">
        <li id="R_7.069.010ab">so 'haṃ varṣasahasrāṇi tapas trīṇi mahāmune</li>
        <li id="R_7.069.010cd">taptvā suduṣkaraṃ prāpto brahmalokam anuttamam</li>
    </ul>
    <ul id="R_7.069.011">
        <li id="R_7.069.011ab">tato māṃ svargasaṃsthaṃ vai kṣutpipāse dvijottama</li>
        <li id="R_7.069.011cd">bādhete paramodāra tato 'haṃ vyathitendriyaḥ</li>
    </ul>
    <ul id="R_7.069.012">
        <li id="R_7.069.012ab">gatvā tribhuvaṇaśreṣṭhaṃ pitāmaham uvāca ha</li>
        <li id="R_7.069.012cd">bhagavan brahmaloko 'yaṃ kṣutpipāsāvivarjitaḥ</li>
    </ul>
    <ul id="R_7.069.013">
        <li id="R_7.069.013ab">kasyeyaṃ karmaṇaḥ prāptiḥ kṣutpipāsāvaśo 'smi yat</li>
        <li id="R_7.069.013cd">āhāraḥ kaś ca me deva tan me brūhi pitāmaha</li>
    </ul>
    <ul id="R_7.069.014">
        <li id="R_7.069.014ab">pitāmahas tu mām āha tavāhāraḥ sudevaja</li>
        <li id="R_7.069.014cd">svādūni svāni māṃsāni tāni bhakṣaya nityaśaḥ</li>
    </ul>
    <ul id="R_7.069.015">
        <li id="R_7.069.015ab">svaśarīraṃ tvayā puṣṭaṃ kurvatā tapa uttamam</li>
        <li id="R_7.069.015cd">anuptaṃ rohate śveta na kadā cin mahāmate</li>
    </ul>
    <ul id="R_7.069.016">
        <li id="R_7.069.016ab">dattaṃ na te 'sti sūkṣmo 'pi vane sattvaniṣevite</li>
        <li id="R_7.069.016cd">tena svargagato vatsa bādhyase kṣutpipāsayā</li>
    </ul>
    <ul id="R_7.069.017">
        <li id="R_7.069.017ab">sa tvaṃ supuṣṭam āhāraiḥ svaśarīram anuttamam</li>
        <li id="R_7.069.017cd">bhakṣayasvāmṛtarasaṃ sā te tṛptir bhaviṣyati</li>
    </ul>
    <ul id="R_7.069.018">
        <li id="R_7.069.018ab">yadā tu tad vanaṃ śveta agastyaḥ sumahān ṛṣiḥ</li>
        <li id="R_7.069.018cd">ākramiṣyati durdharṣas tadā kṛcchād vimokṣyase</li>
    </ul>
    <ul id="R_7.069.019">
        <li id="R_7.069.019ab">sa hi tārayituṃ saumya śaktaḥ suragaṇān api</li>
        <li id="R_7.069.019cd">kiṃ punas tvāṃ mahābāho kṣutpipāsāvaśaṃ gatam</li>
    </ul>
    <ul id="R_7.069.020">
        <li id="R_7.069.020ab">so 'haṃ bhagavataḥ śrutvā devadevasya niścayam</li>
        <li id="R_7.069.020cd">āhāraṃ garhitaṃ kurmi svaśarīraṃ dvijottama</li>
    </ul>
    <ul id="R_7.069.021">
        <li id="R_7.069.021ab">bahūn varṣagaṇān brahman bhujyamānam idaṃ mayā</li>
        <li id="R_7.069.021cd">kṣayaṃ nābhyeti brahmarṣe tṛptiś cāpi mamottamā</li>
    </ul>
    <ul id="R_7.069.022">
        <li id="R_7.069.022ab">tasya me kṛcchrabhūtasya kṛcchrād asmād vimokṣaya</li>
        <li id="R_7.069.022cd">anyeṣām agatir hy atra kumbhayonim ṛte dvijam</li>
    </ul>
    <ul id="R_7.069.023">
        <li id="R_7.069.023ab">idam ābharaṇaṃ saumya tāraṇārthaṃ dvijottama</li>
        <li id="R_7.069.023cd">pratigṛhṇīṣva brahmarṣe prasādaṃ kartum arhasi</li>
    </ul>
    <ul id="R_7.069.024">
        <li id="R_7.069.024ab">tasyāhaṃ svargiṇo vākyaṃ śrutvā duḥkhasamanvitam</li>
        <li id="R_7.069.024cd">tāraṇāyopajagrāha tad ābharaṇam uttamam</li>
    </ul>
    <ul id="R_7.069.025">
        <li id="R_7.069.025ab">mayā pratigṛhīte tu tasminn ābharaṇe śubhe</li>
        <li id="R_7.069.025cd">mānuṣaḥ pūrvako deho rājarṣeḥ sa nanāśa ha</li>
    </ul>
    <ul id="R_7.069.026">
        <li id="R_7.069.026ab">pranaṣṭe tu śarīre 'sau rājarṣiḥ parayā mudā</li>
        <li id="R_7.069.026cd">tṛptaḥ pramudito rājā jagāma tridivaṃ punaḥ</li>
    </ul>
    <ul id="R_7.069.027">
        <li id="R_7.069.027ab">tenedaṃ śakratulyena divyam ābharaṇaṃ mama</li>
        <li id="R_7.069.027cd">tasmin nimitte kākutstha dattam adbhutadarśanam</li>
    </ul>
    <ul id="R_7.070.001">
        <li id="R_7.070.001ab">tad adbhutatamaṃ vākyaṃ śrutvāgastyasya rāghavaḥ</li>
        <li id="R_7.070.001cd">gauravād vismayāc caiva bhūyaḥ praṣṭuṃ pracakrame</li>
    </ul>
    <ul id="R_7.070.002">
        <li id="R_7.070.002ab">bhagavaṃs tad vanaṃ ghoraṃ tapas tapyati yatra saḥ</li>
        <li id="R_7.070.002cd">śveto vaidarbhako rājā kathaṃ tad amṛgadvijam</li>
    </ul>
    <ul id="R_7.070.003">
        <li id="R_7.070.003ab">niḥsattvaṃ ca vanaṃ jātaṃ śūnyaṃ manujavarjitam</li>
        <li id="R_7.070.003cd">tapaś cartuṃ praviṣṭaḥ sa śrotum icchāmi tattvataḥ</li>
    </ul>
    <ul id="R_7.070.004">
        <li id="R_7.070.004ab">rāmasya bhāṣitaṃ śrutvā kautūhalasamanvitam</li>
        <li id="R_7.070.004cd">vākyaṃ paramatejasvī vaktum evopacakrame</li>
    </ul>
    <ul id="R_7.070.005">
        <li id="R_7.070.005ab">purā kṛtayuge rāma manur daṇḍadharaḥ prabhuḥ</li>
        <li id="R_7.070.005cd">tasya putro mahān āsīd ikṣvākuḥ kulavardhanaḥ</li>
    </ul>
    <ul id="R_7.070.006">
        <li id="R_7.070.006ab">taṃ putraṃ pūrvake rājye nikṣipya bhuvi durjayam</li>
        <li id="R_7.070.006cd">pṛthivyāṃ rājavaṃśānāṃ bhava kartety uvāca ha</li>
    </ul>
    <ul id="R_7.070.007">
        <li id="R_7.070.007ab">tatheti ca pratijñātaṃ pituḥ putreṇa rāghava</li>
        <li id="R_7.070.007cd">tataḥ paramasaṃhṛṣṭo manuḥ punar uvāca ha</li>
    </ul>
    <ul id="R_7.070.008">
        <li id="R_7.070.008ab">prīto 'smi paramodārakartā cāsi na saṃśayaḥ</li>
        <li id="R_7.070.008cd">daṇḍena ca prajā rakṣa mā ca daṇḍam akāraṇe</li>
    </ul>
    <ul id="R_7.070.009">
        <li id="R_7.070.009ab">aparādhiṣu yo daṇḍaḥ pātyate mānaveṣu vai</li>
        <li id="R_7.070.009cd">sa daṇḍo vidhivan muktaḥ svargaṃ nayati pārthivam</li>
    </ul>
    <ul id="R_7.070.010">
        <li id="R_7.070.010ab">tasmād daṇḍe mahābāho yatnavān bhava putraka</li>
        <li id="R_7.070.010cd">dharmo hi paramo loke kurvatas te bhaviṣyati</li>
    </ul>
    <ul id="R_7.070.011">
        <li id="R_7.070.011ab">iti taṃ bahu saṃdiśya manuḥ putraṃ samādhinā</li>
        <li id="R_7.070.011cd">jagāma tridivaṃ hṛṣṭo brahmalokam anuttamam</li>
    </ul>
    <ul id="R_7.070.012">
        <li id="R_7.070.012ab">prayāte tridive tasminn ikṣvākur amitaprabhaḥ</li>
        <li id="R_7.070.012cd">janayiṣye kathaṃ putrān iti cintāparo 'bhavat</li>
    </ul>
    <ul id="R_7.070.013">
        <li id="R_7.070.013ab">karmabhir bahurūpaiś ca tais tair manusutaḥ sutān</li>
        <li id="R_7.070.013cd">janayām āsa dharmātmā śataṃ devasutopamān</li>
    </ul>
    <ul id="R_7.070.014">
        <li id="R_7.070.014ab">teṣām avarajas tāta sarveṣāṃ raghunandana</li>
        <li id="R_7.070.014cd">mūḍhaś cākṛtividyaś ca na śuśrūṣati pūrvajān</li>
    </ul>
    <ul id="R_7.070.015">
        <li id="R_7.070.015ab">nāma tasya ca daṇḍeti pitā cakre 'lpatejasaḥ</li>
        <li id="R_7.070.015cd">avaśyaṃ daṇḍapatanaṃ śarīre 'sya bhaviṣyati</li>
    </ul>
    <ul id="R_7.070.016">
        <li id="R_7.070.016ab">sa paśyamānas taṃ doṣaṃ ghoraṃ putrasya rāghava</li>
        <li id="R_7.070.016cd">vindhyaśaivalayor madhye rājyaṃ prādād ariṃdama</li>
    </ul>
    <ul id="R_7.070.017">
        <li id="R_7.070.017ab">sa daṇḍas tatra rājābhūd ramye parvatarodhasi</li>
        <li id="R_7.070.017cd">puraṃ cāpratimaṃ rāma nyaveśayad anuttamam</li>
    </ul>
    <ul id="R_7.070.018">
        <li id="R_7.070.018ab">purasya cākaron nāma madhumantam iti prabho</li>
        <li id="R_7.070.018cd">purohitaṃ cośanasaṃ varayām āsa suvratam</li>
    </ul>
    <ul id="R_7.070.019">
        <li id="R_7.070.019ab">evaṃ sa rājā tad rājyaṃ kārayat sapurohitaḥ</li>
        <li id="R_7.070.019cd">prahṛṣṭamanujākīrṇaṃ devarājyaṃ yathā divi</li>
    </ul>
    <ul id="R_7.071.001">
        <li id="R_7.071.001ab">etad ākhyāya rāmāya maharṣiḥ kumbhasaṃbhavaḥ</li>
        <li id="R_7.071.001cd">asyām evāparaṃ vākyaṃ kathāyām upacakrame</li>
    </ul>
    <ul id="R_7.071.002">
        <li id="R_7.071.002ab">tataḥ sa daṇḍaḥ kākutstha bahuvarṣagaṇāyutam</li>
        <li id="R_7.071.002cd">akarot tatra mandātmā rājyaṃ nihatakaṇṭakam</li>
    </ul>
    <ul id="R_7.071.003">
        <li id="R_7.071.003ab">atha kāle tu kasmiṃś cid rājā bhārgavam āśramam</li>
        <li id="R_7.071.003cd">ramaṇīyam upākrāmac caitre māsi manorame</li>
    </ul>
    <ul id="R_7.071.004">
        <li id="R_7.071.004ab">tatra bhārgavakanyāṃ sa rūpeṇāpratimāṃ bhuvi</li>
        <li id="R_7.071.004cd">vicarantīṃ vanoddeśe daṇḍo 'paśyad anuttamām</li>
    </ul>
    <ul id="R_7.071.005">
        <li id="R_7.071.005ab">sa dṛṣṭvā tāṃ sudurmedhā anaṅgaśarapīḍitaḥ</li>
        <li id="R_7.071.005cd">abhigamya susaṃvignaḥ kanyāṃ vacanam abravīt</li>
    </ul>
    <ul id="R_7.071.006">
        <li id="R_7.071.006ab">kutas tvam asi suśroṇi kasya vāsi sutā śubhe</li>
        <li id="R_7.071.006cd">pīḍito 'ham anaṅgena pṛcchāmi tvāṃ sumadhyame</li>
    </ul>
    <ul id="R_7.071.007">
        <li id="R_7.071.007ab">tasya tv evaṃ bruvāṇasya mohonmattasya kāminaḥ</li>
        <li id="R_7.071.007cd">bhārgavī pratyuvācedaṃ vacaḥ sānunayaṃ nṛpam</li>
    </ul>
    <ul id="R_7.071.008">
        <li id="R_7.071.008ab">bhārgavasya sutāṃ viddhi devasyākliṣṭakarmaṇaḥ</li>
        <li id="R_7.071.008cd">arajāṃ nāma rājendra jyeṣṭhām āśramavāsinīm</li>
    </ul>
    <ul id="R_7.071.009">
        <li id="R_7.071.009ab">guruḥ pitā me rājendra tvaṃ ca śiṣyo mahātmanaḥ</li>
        <li id="R_7.071.009cd">vyasanaṃ sumahat kruddhaḥ sa te dadyān mahātapāḥ</li>
    </ul>
    <ul id="R_7.071.010">
        <li id="R_7.071.010ab">yadi vātra mayā kāryaṃ dharmadṛṣṭena satpathā</li>
        <li id="R_7.071.010cd">varayasva nṛpa śreṣṭha pitaraṃ me mahādyutim</li>
    </ul>
    <ul id="R_7.071.011">
        <li id="R_7.071.011ab">anyathā tu phalaṃ tubhyaṃ bhaved ghorābhisaṃhitam</li>
        <li id="R_7.071.011cd">krodhena hi pitā me 'sau trailokyam api nirdahet</li>
    </ul>
    <ul id="R_7.071.012">
        <li id="R_7.071.012ab">evaṃ bruvāṇām arajāṃ daṇḍaḥ kāmaśarārditaḥ</li>
        <li id="R_7.071.012cd">pratyuvāca madonmattaḥ śirasy ādhāya so 'ñjalim</li>
    </ul>
    <ul id="R_7.071.013">
        <li id="R_7.071.013ab">prasādaṃ kuru suśroṇi na kālaṃ kṣeptum arhasi</li>
        <li id="R_7.071.013cd">tvatkṛte hi mama prāṇā vidīryante śubhānane</li>
    </ul>
    <ul id="R_7.071.014">
        <li id="R_7.071.014ab">tvāṃ prāpya hi vadho vāpi pāpaṃ vāpi sudāruṇam</li>
        <li id="R_7.071.014cd">bhaktaṃ bhajasva māṃ bhīru bhajamānaṃ suvihvalam</li>
    </ul>
    <ul id="R_7.071.015">
        <li id="R_7.071.015ab">evam uktvā tu tāṃ kanyāṃ dorbhyāṃ gṛhya balād balī</li>
        <li id="R_7.071.015cd">visphurantīṃ yathākāmaṃ maithunāyopacakrame</li>
    </ul>
    <ul id="R_7.071.016">
        <li id="R_7.071.016ab">tam anarthaṃ mahāghoraṃ daṇḍaḥ kṛtvā sudāruṇam</li>
        <li id="R_7.071.016cd">nagaraṃ prayayau cāśu madhumantam anuttamam</li>
    </ul>
    <ul id="R_7.071.017">
        <li id="R_7.071.017ab">arajāpi rudantī sā āśramasyāvidūrataḥ</li>
        <li id="R_7.071.017cd">pratīkṣate susaṃtrastā pitaraṃ devasaṃnibham</li>
    </ul>
    <ul id="R_7.072.001">
        <li id="R_7.072.001ab">sa muhūrtād upaśrutya devarṣir amitaprabhaḥ</li>
        <li id="R_7.072.001cd">svam āśramaṃ śiṣya vṛtaḥ kṣudhārtaḥ saṃnyavartata</li>
    </ul>
    <ul id="R_7.072.002">
        <li id="R_7.072.002ab">so 'paśyad arajāṃ dīnāṃ rajasā samabhiplutām</li>
        <li id="R_7.072.002cd">jyotsnām ivāruṇagrastāṃ pratyūṣe na virājatīm</li>
    </ul>
    <ul id="R_7.072.003">
        <li id="R_7.072.003ab">tasya roṣaḥ samabhavat kṣudhārtasya viśeṣataḥ</li>
        <li id="R_7.072.003cd">nirdahann iva lokāṃs trīñ śiṣyāṃś cedam uvāca ha</li>
    </ul>
    <ul id="R_7.072.004">
        <li id="R_7.072.004ab">paśyadhvaṃ viparītasya daṇḍasyāviditātmanaḥ</li>
        <li id="R_7.072.004cd">vipattiṃ ghorasaṃkāśāṃ kruddhām agniśikhām iva</li>
    </ul>
    <ul id="R_7.072.005">
        <li id="R_7.072.005ab">kṣayo 'sya durmateḥ prāptaḥ sānugasya durātmanaḥ</li>
        <li id="R_7.072.005cd">yaḥ pradīptāṃ hutāśasya śikhāṃ vai spraṣṭum icchati</li>
    </ul>
    <ul id="R_7.072.006">
        <li id="R_7.072.006ab">yasmāt sa kṛtavān pāpam īdṛśaṃ ghoradarśanam</li>
        <li id="R_7.072.006cd">tasmāt prāpsyati durmedhāḥ phalaṃ pāpasya karmaṇaḥ</li>
    </ul>
    <ul id="R_7.072.007">
        <li id="R_7.072.007ab">saptarātreṇa rājāsau sabhṛtyabalavāhanaḥ</li>
        <li id="R_7.072.007cd">pāpakarmasamācāro vadhaṃ prāpsyati durmatiḥ</li>
    </ul>
    <ul id="R_7.072.008">
        <li id="R_7.072.008ab">samantād yojanaśataṃ viṣayaṃ cāsya durmateḥ</li>
        <li id="R_7.072.008cd">dhakṣyate pāṃsuvarṣeṇa mahatā pākaśāsanaḥ</li>
    </ul>
    <ul id="R_7.072.009">
        <li id="R_7.072.009ab">sarvasattvāni yānīha sthāvarāṇi carāṇi ca</li>
        <li id="R_7.072.009cd">mahatā pāṃsuvarṣeṇa nāśaṃ yāsyanti sarvaśaḥ</li>
    </ul>
    <ul id="R_7.072.010">
        <li id="R_7.072.010ab">daṇḍasya viṣayo yāvat tāvat sarvasamucchrayaḥ</li>
        <li id="R_7.072.010cd">pāṃsubhuta ivālakṣyaḥ saptarātrād bhaviṣyati</li>
    </ul>
    <ul id="R_7.072.011">
        <li id="R_7.072.011ab">ity uktvā krodhasaṃtapas tam āśramanivāsinam</li>
        <li id="R_7.072.011cd">janaṃ janapadānteṣu sthīyatām iti cābravīt</li>
    </ul>
    <ul id="R_7.072.012">
        <li id="R_7.072.012ab">śrutvā tūśasano vākyaṃ sa āśramāvasatho janaḥ</li>
        <li id="R_7.072.012cd">niṣkrānto viṣayāt tasya sthānaṃ cakre 'tha bāhyataḥ</li>
    </ul>
    <ul id="R_7.072.013">
        <li id="R_7.072.013ab">sa tathoktvā munijanam arajām idam abravīt</li>
        <li id="R_7.072.013cd">ihaiva vasa durmedhe āśrame susamāhitā</li>
    </ul>
    <ul id="R_7.072.014">
        <li id="R_7.072.014ab">idaṃ yojanaparyantaṃ saraḥ suruciraprabham</li>
        <li id="R_7.072.014cd">araje vijvarā bhuṅkṣva kālaś cātra pratīkṣyatām</li>
    </ul>
    <ul id="R_7.072.015">
        <li id="R_7.072.015ab">tvatsamīpe tu ye sattvā vāsam eṣyanti tāṃ niśām</li>
        <li id="R_7.072.015cd">avadhyāḥ pāṃsuvarṣeṇa te bhaviṣyanti nityadā</li>
    </ul>
    <ul id="R_7.072.016">
        <li id="R_7.072.016ab">ity uktvā bhārgavo vāsam anyatra samupākramat</li>
        <li id="R_7.072.016cd">saptāhād bhasmasādbhūtaṃ yathoktaṃ brahmavādinā</li>
    </ul>
    <ul id="R_7.072.017">
        <li id="R_7.072.017ab">tasyāsau daṇḍaviṣayo vindhyaśaivalasānuṣu</li>
        <li id="R_7.072.017cd">śapto brahmarṣiṇā tena purā vaidharmake kṛte</li>
    </ul>
    <ul id="R_7.072.018">
        <li id="R_7.072.018ab">tataḥ prabhṛti kākutstha daṇḍakāraṇyam ucyate</li>
        <li id="R_7.072.018cd">tapasvinaḥ sthitā yatra janasthānam atho 'bhavat</li>
    </ul>
    <ul id="R_7.072.019">
        <li id="R_7.072.019ab">etat te sarvam ākhyātaṃ yan māṃ pṛcchasi rāghava</li>
        <li id="R_7.072.019cd">saṃdhyām upāsituṃ vīra samayo hy ativartate</li>
    </ul>
    <ul id="R_7.072.020">
        <li id="R_7.072.020ab">ete maharṣayaḥ sarve pūrṇakumbhāḥ samantataḥ</li>
        <li id="R_7.072.020cd">kṛtodako naravyāghra ādityaṃ paryupāsate</li>
    </ul>
    <ul id="R_7.072.021">
        <li id="R_7.072.021ab">sa tair ṛṣibhir abhyastaḥ sahitair brahmasattamaiḥ</li>
        <li id="R_7.072.021cd">ravir astaṃ gato rāma gacchodakam upaspṛśa</li>
    </ul>
    <ul id="R_7.073.001">
        <li id="R_7.073.001ab">ṛṣer vacanam ājñāya rāmaḥ saṃdhyām upāsitum</li>
        <li id="R_7.073.001cd">upākrāmat saraḥ puṇyam apsarobhir niṣevitam</li>
    </ul>
    <ul id="R_7.073.002">
        <li id="R_7.073.002ab">tatrodakam upaspṛṣśya saṃdhyām anvāsya paścimām</li>
        <li id="R_7.073.002cd">āśramaṃ prāviśad rāmaḥ kumbhayoner mahātmanaḥ</li>
    </ul>
    <ul id="R_7.073.003">
        <li id="R_7.073.003ab">asyāgastyo bahuguṇaṃ phalamūlaṃ tathauṣadhīḥ</li>
        <li id="R_7.073.003cd">śākāni ca pavitrāṇi bhojanārtham akalpayat</li>
    </ul>
    <ul id="R_7.073.004">
        <li id="R_7.073.004ab">sa bhuktavān naraśreṣṭhas tad annam amṛtopamam</li>
        <li id="R_7.073.004cd">prītaś ca parituṣṭaś ca tāṃ rātriṃ samupāvasat</li>
    </ul>
    <ul id="R_7.073.005">
        <li id="R_7.073.005ab">prabhāte kālyam utthāya kṛtvāhnikam ariṃdamaḥ</li>
        <li id="R_7.073.005cd">ṛṣiṃ samabhicakrāma gamanāya raghūttamaḥ</li>
    </ul>
    <ul id="R_7.073.006">
        <li id="R_7.073.006ab">abhivādyābravīd rāmo maharṣiṃ kumbhasaṃbhavam</li>
        <li id="R_7.073.006cd">āpṛcche tvāṃ gamiṣyāmi mām anujñātum arhasi</li>
    </ul>
    <ul id="R_7.073.007">
        <li id="R_7.073.007ab">dhanyo 'smy anugṛhīto 'smi darśanena mahātmanaḥ</li>
        <li id="R_7.073.007cd">draṣṭuṃ caivāgamiṣyāmi pāvanārtham ihātmanaḥ</li>
    </ul>
    <ul id="R_7.073.008">
        <li id="R_7.073.008ab">tathā vadati kākutsthe vākyam adbhutadarśanam</li>
        <li id="R_7.073.008cd">uvāca paramaprīto dharmanetras tapodhanaḥ</li>
    </ul>
    <ul id="R_7.073.009">
        <li id="R_7.073.009ab">atyadbhutam idaṃ vākyaṃ tava rāma śubhākṣaram</li>
        <li id="R_7.073.009cd">pāvanaḥ sarvalokānāṃ tvam eva raghunandana</li>
    </ul>
    <ul id="R_7.073.010">
        <li id="R_7.073.010ab">muhūrtam api rāma tvāṃ ye nu paśyanti ke cana</li>
        <li id="R_7.073.010cd">pāvitāḥ svargabhūtās te pūjyante divi daivataiḥ</li>
    </ul>
    <ul id="R_7.073.011">
        <li id="R_7.073.011ab">ye ca tvāṃ ghoracakṣurbhir īkṣante prāṇino bhuvi</li>
        <li id="R_7.073.011cd">hatās te yamadaṇḍena sadyo nirayagāminaḥ</li>
    </ul>
    <ul id="R_7.073.012">
        <li id="R_7.073.012ab">gaccha cāriṣṭam avyagraḥ panthānam akutobhayam</li>
        <li id="R_7.073.012cd">praśādhi rājyaṃ dharmeṇa gatir hi jagato bhavān</li>
    </ul>
    <ul id="R_7.073.013">
        <li id="R_7.073.013ab">evam uktas tu muninā prāñjaliḥ prpagraho nṛpaḥ</li>
        <li id="R_7.073.013cd">abhyavādayata prājñas tam ṛṣiṃ puṇyaśīlinam</li>
    </ul>
    <ul id="R_7.073.014">
        <li id="R_7.073.014ab">abhivādya muniśreṣṭhaṃ tāṃś ca sarvāṃs tapodhanān</li>
        <li id="R_7.073.014cd">adhyārohat tad avyagraḥ puṣpakaṃ hemabhūṣitam</li>
    </ul>
    <ul id="R_7.073.015">
        <li id="R_7.073.015ab">taṃ prayāntaṃ munigaṇā āśīrvādaiḥ samantataḥ</li>
        <li id="R_7.073.015cd">apūjayan mahendrābhaṃ sahasrākṣam ivāmarāḥ</li>
    </ul>
    <ul id="R_7.073.016">
        <li id="R_7.073.016ab">svasthaḥ sa dadṛśe rāmaḥ puṣpake hemabhūṣite</li>
        <li id="R_7.073.016cd">śaśī meghasamīpastho yathā jaladharāgame</li>
    </ul>
    <ul id="R_7.073.017">
        <li id="R_7.073.017ab">tato 'rdhadivase prāpte pūjyamānas tatas tataḥ</li>
        <li id="R_7.073.017cd">ayodhyāṃ prāpya kākutstho vimānād avarohata</li>
    </ul>
    <ul id="R_7.073.018">
        <li id="R_7.073.018ab">tato visṛjya ruciraṃ puṣpakaṃ kāmagāminam</li>
        <li id="R_7.073.018cd">kakṣyāntaravinikṣiptaṃ dvāḥsthaṃ rāmo 'bravīd vacaḥ</li>
    </ul>
    <ul id="R_7.073.019">
        <li id="R_7.073.019ab">lakṣmaṇaṃ bharataṃ caiva gatvā tau laghuvikramau</li>
        <li id="R_7.073.019cd">mamāgamanam ākhyāya śabdāpaya ca māṃ ciram</li>
    </ul>
    <ul id="R_7.074.001">
        <li id="R_7.074.001ab">tac chrutvā bhāṣitaṃ tasya rāmasyākliṣṭakarmaṇaḥ</li>
        <li id="R_7.074.001cd">dvāḥsthaḥ kumārāv āhūya rāghavāya nyavedayat</li>
    </ul>
    <ul id="R_7.074.002">
        <li id="R_7.074.002ab">dṛṣṭvā tu rāghavaḥ prāptau priyau bharatalakṣmaṇau</li>
        <li id="R_7.074.002cd">pariṣvajya tato rāmo vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.074.003">
        <li id="R_7.074.003ab">kṛtaṃ mayā yathātathyaṃ dvijakāryam anuttamam</li>
        <li id="R_7.074.003cd">dharmasetumato bhūyaḥ kartum icchāmi rāghavau</li>
    </ul>
    <ul id="R_7.074.004">
        <li id="R_7.074.004ab">yuvābhyām ātmabhūtābhyāṃ rājasūyam anuttamam</li>
        <li id="R_7.074.004cd">sahito yaṣṭum icchāmi tatra dharmo hi śāśvataḥ</li>
    </ul>
    <ul id="R_7.074.005">
        <li id="R_7.074.005ab">iṣṭvā tu rājasūyena mitraḥ śatrunibarhaṇaḥ</li>
        <li id="R_7.074.005cd">suhutena suyajñena varuṇatvam upāgamat</li>
    </ul>
    <ul id="R_7.074.006">
        <li id="R_7.074.006ab">somaś ca rājasūyena iṣṭvā dharmeṇa dharmavit</li>
        <li id="R_7.074.006cd">prāptaś ca sarvalokānāṃ kīrtiṃ sthānaṃ ca śāśvatam</li>
    </ul>
    <ul id="R_7.074.007">
        <li id="R_7.074.007ab">asminn ahani yac chreyaś cintyatāṃ tan mayā saha</li>
        <li id="R_7.074.007cd">hitaṃ cāyati yuktaṃ ca prayatau vaktum arhatha</li>
    </ul>
    <ul id="R_7.074.008">
        <li id="R_7.074.008ab">śrutā tu rāghavasyaitad vākyaṃ vākyaviśāradaḥ</li>
        <li id="R_7.074.008cd">bharataḥ prāñjalir bhūtvā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.074.009">
        <li id="R_7.074.009ab">tvayi dharmaḥ paraḥ sādho tvayi sarvā vasuṃdharā</li>
        <li id="R_7.074.009cd">pratiṣṭhitā mahābāho yaśaś cāmitavikrama</li>
    </ul>
    <ul id="R_7.074.010">
        <li id="R_7.074.010ab">mahīpālāś ca sarve tvāṃ prajāpatim ivāmarāḥ</li>
        <li id="R_7.074.010cd">nirīkṣante mahātmāno lokanāthaṃ yathā vayam</li>
    </ul>
    <ul id="R_7.074.011">
        <li id="R_7.074.011ab">prajāś ca pitṛvad rājan paśyanti tvāṃ mahābala</li>
        <li id="R_7.074.011cd">pṛthivyāṃ gatibhūto 'si prāṇinām api rāghava</li>
    </ul>
    <ul id="R_7.074.012">
        <li id="R_7.074.012ab">sa tvam evaṃvidhaṃ yajñam āhartāsi kathaṃ nṛpa</li>
        <li id="R_7.074.012cd">pṛthivyāṃ rājavaṃśānāṃ vināśo yatra dṛśyate</li>
    </ul>
    <ul id="R_7.074.013">
        <li id="R_7.074.013ab">pṛthivyāṃ ye ca puruṣā rājan pauruṣam āgatāḥ</li>
        <li id="R_7.074.013cd">sarveṣāṃ bhavitā tatra kṣayaḥ sarvāntakopamaḥ</li>
    </ul>
    <ul id="R_7.074.014">
        <li id="R_7.074.014ab">sa tvaṃ puruṣaśārdūla guṇair atulavikrama</li>
        <li id="R_7.074.014cd">pṛthivīṃ nārhase hantuṃ vaśe hi tava vartate</li>
    </ul>
    <ul id="R_7.074.015">
        <li id="R_7.074.015ab">bharatasya tu tad vākyaṃ śrutvāmṛtamayaṃ yathā</li>
        <li id="R_7.074.015cd">praharṣam atulaṃ lebhe rāmaḥ satyaparākramaḥ</li>
    </ul>
    <ul id="R_7.074.016">
        <li id="R_7.074.016ab">uvāca ca śubhāṃ vāṇīṃ kaikeyyā nandivardhanam</li>
        <li id="R_7.074.016cd">prīto 'smi parituṣṭo 'smi tavādya vacanena hi</li>
    </ul>
    <ul id="R_7.074.017">
        <li id="R_7.074.017ab">idaṃ vacanam aklībaṃ tvayā dharmasamāhitam</li>
        <li id="R_7.074.017cd">vyāhṛtaṃ puruṣavyāghra pṛthivyāḥ paripālanam</li>
    </ul>
    <ul id="R_7.074.018">
        <li id="R_7.074.018ab">eṣa tasmād abhiprāyād rājasūyāt kratūttamān</li>
        <li id="R_7.074.018cd">nivartayāmi dharmajña tava suvyāhṛtena vai</li>
    </ul>
    <ul id="R_7.074.019">
        <li id="R_7.074.019ab">prajānāṃ pālanaṃ dharmo rājñāṃ yajñena saṃmitaḥ</li>
        <li id="R_7.074.019cd">tasmāc chṛṇomi te vākyaṃ sādhūktaṃ susamāhitam</li>
    </ul>
    <ul id="R_7.075.001">
        <li id="R_7.075.001ab">tathoktavati rāme tu bharate ca mahātmani</li>
        <li id="R_7.075.001cd">lakṣmaṇo 'pi śubhaṃ vākyam uvāca raghunandanam</li>
    </ul>
    <ul id="R_7.075.002">
        <li id="R_7.075.002ab">aśvamedho mahāyajñaḥ pāvanaḥ sarvapāpmanām</li>
        <li id="R_7.075.002cd">pāvanas tava durdharṣo rocatāṃ kratupuṃgavaḥ</li>
    </ul>
    <ul id="R_7.075.003">
        <li id="R_7.075.003ab">śrūyate hi purāvṛttaṃ vāsave sumahātmani</li>
        <li id="R_7.075.003cd">brahmahatyāvṛtaḥ śakro hayamedhena pāvitaḥ</li>
    </ul>
    <ul id="R_7.075.004">
        <li id="R_7.075.004ab">purā kila mahābāho devāsurasamāgame</li>
        <li id="R_7.075.004cd">vṛtro nāma mahān āsīd daiteyo lokasaṃmataḥ</li>
    </ul>
    <ul id="R_7.075.005">
        <li id="R_7.075.005ab">vistīrṇā yojanaśatam ucchritas triguṇaṃ tataḥ</li>
        <li id="R_7.075.005cd">anurāgeṇa lokāṃs trīn snehāt paśyati sarvataḥ</li>
    </ul>
    <ul id="R_7.075.006">
        <li id="R_7.075.006ab">dharmajñaś ca kṛtajñaś ca buddhyā ca pariniṣṭhitaḥ</li>
        <li id="R_7.075.006cd">śaśāsa pṛthivīṃ sarvāṃ dharmeṇa susamāhitaḥ</li>
    </ul>
    <ul id="R_7.075.007">
        <li id="R_7.075.007ab">tasmin praśāsati tadā sarvakāmadughā mahī</li>
        <li id="R_7.075.007cd">rasavanti prasūtāni mūlāni ca phalāni ca</li>
    </ul>
    <ul id="R_7.075.008">
        <li id="R_7.075.008ab">akṛṣṭapacyā pṛthivī susaṃpannā mahātmanaḥ</li>
        <li id="R_7.075.008cd">sa rājyaṃ tādṛśaṃ bhuṅkte sphītam adbhutadarśanam</li>
    </ul>
    <ul id="R_7.075.009">
        <li id="R_7.075.009ab">tasya buddhiḥ samutpannā tapaḥ kuryām anuttamam</li>
        <li id="R_7.075.009cd">tapo hi paramaṃ śreyas tapo hi paramaṃ sukham</li>
    </ul>
    <ul id="R_7.075.010">
        <li id="R_7.075.010ab">sa nikṣipya sutaṃ jyeṣṭhaṃ paureṣu parameśvaram</li>
        <li id="R_7.075.010cd">tapa ugram upātiṣṭhat tāpayan sarvadevatāḥ</li>
    </ul>
    <ul id="R_7.075.011">
        <li id="R_7.075.011ab">tapas tapyati vṛtre tu vāsavaḥ paramārtavat</li>
        <li id="R_7.075.011cd">viṣṇuṃ samupasaṃkramya vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.075.012">
        <li id="R_7.075.012ab">tapasyatā mahābāho lokā vṛtreṇa nirjitāḥ</li>
        <li id="R_7.075.012cd">balavān sa hi dharmātmā nainaṃ śakṣyāmi bādhitum</li>
    </ul>
    <ul id="R_7.075.013">
        <li id="R_7.075.013ab">yady asau tapa ātiṣṭhed bhūya eva sureśvara</li>
        <li id="R_7.075.013cd">yāval lokā dhariṣyanti tāvad asya vaśānugāḥ</li>
    </ul>
    <ul id="R_7.075.014">
        <li id="R_7.075.014ab">tvaṃ cainaṃ paramodāram upekṣasi mahābala</li>
        <li id="R_7.075.014cd">kṣaṇaṃ hi na bhaved vṛtraḥ kruddhe tvayi sureśvara</li>
    </ul>
    <ul id="R_7.075.015">
        <li id="R_7.075.015ab">yadā hi prītisaṃyogaṃ tvayā viṣṇo samāgataḥ</li>
        <li id="R_7.075.015cd">tadā prabhṛti lokānāṃ nāthatvam upalabdhavān</li>
    </ul>
    <ul id="R_7.075.016">
        <li id="R_7.075.016ab">sa tvaṃ prasādaṃ lokānāṃ kuruṣva sumahāyaśaḥ</li>
        <li id="R_7.075.016cd">tvatkṛtena hi sarvaṃ syāt praśāntam ajaraṃ jagat</li>
    </ul>
    <ul id="R_7.075.017">
        <li id="R_7.075.017ab">ime hi sarve viṣṇo tvāṃ nirīkṣante divaukasaḥ</li>
        <li id="R_7.075.017cd">vṛtraghatena mahatā eṣāṃ sāhyaṃ kuruṣva ha</li>
    </ul>
    <ul id="R_7.075.018">
        <li id="R_7.075.018ab">tvayā hi nityaśaḥ sāhyaṃ kṛtam eṣāṃ mahātmanām</li>
        <li id="R_7.075.018cd">asahyam idam anyeṣām agatīnāṃ gatir bhavān</li>
    </ul>
    <ul id="R_7.076.001">
        <li id="R_7.076.001ab">lakṣmaṇasya tu tad vākyaṃ śrutvā śatrunibarhaṇaḥ</li>
        <li id="R_7.076.001cd">vṛtraghātam aśeṣeṇa kathayety āha lakṣmaṇam</li>
    </ul>
    <ul id="R_7.076.002">
        <li id="R_7.076.002ab">rāghaveṇaivam uktas tu sumitrānandavardhanaḥ</li>
        <li id="R_7.076.002cd">bhūya eva kathāṃ divyāṃ kathayām āsa lakṣmaṇaḥ</li>
    </ul>
    <ul id="R_7.076.003">
        <li id="R_7.076.003ab">sahasrākṣavacaḥ śrutvā sarveṣāṃ ca divaukasām</li>
        <li id="R_7.076.003cd">viṣṇur devān uvācedaṃ sarvān indrapurogamān</li>
    </ul>
    <ul id="R_7.076.004">
        <li id="R_7.076.004ab">pūrvaṃ sauhṛdabaddho 'smi vṛtrasya sumahātmanaḥ</li>
        <li id="R_7.076.004cd">tena yuṣmat priyārthaṃ vai nāhaṃ hanmi mahāsuram</li>
    </ul>
    <ul id="R_7.076.005">
        <li id="R_7.076.005ab">avaśyaṃ karaṇīyaṃ ca bhavatāṃ sukham uttamam</li>
        <li id="R_7.076.005cd">tasmād upāyam ākhyāsye yena vṛtraṃ haniṣyatha</li>
    </ul>
    <ul id="R_7.076.006">
        <li id="R_7.076.006ab">tridhā bhūtaṃ kariṣye 'ham ātmānaṃ surasattamāḥ</li>
        <li id="R_7.076.006cd">tena vṛtraṃ sahasrākṣo haniṣyati na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.076.007">
        <li id="R_7.076.007ab">eko 'ṃśo vāsavaṃ yātu dvitīyo vajram eva tu</li>
        <li id="R_7.076.007cd">tṛtīyo bhūtalaṃ śakras tato vṛtraṃ haniṣyati</li>
    </ul>
    <ul id="R_7.076.008">
        <li id="R_7.076.008ab">tathā bruvati deveśe devā vākyam athābruvan</li>
        <li id="R_7.076.008cd">evam etan na saṃdeho yathā vadasi daityahan</li>
    </ul>
    <ul id="R_7.076.009">
        <li id="R_7.076.009ab">bhadraṃ te 'stu gamiṣyāmo vṛtrāsuravadhaiṣiṇaḥ</li>
        <li id="R_7.076.009cd">bhajasva paramodāravāsavaṃ svena tejasā</li>
    </ul>
    <ul id="R_7.076.010">
        <li id="R_7.076.010ab">tataḥ sarve mahātmānaḥ sahasrākṣapurogamāḥ</li>
        <li id="R_7.076.010cd">tad araṇyam upākrāman yatra vṛtro mahāsuraḥ</li>
    </ul>
    <ul id="R_7.076.011">
        <li id="R_7.076.011ab">te 'paśyaṃs tejasā bhūtaṃ tapantam asurottamam</li>
        <li id="R_7.076.011cd">pibantam iva lokāṃs trīn nirdahantam ivāmbaram</li>
    </ul>
    <ul id="R_7.076.012">
        <li id="R_7.076.012ab">dṛṣṭvaiva cāsuraśreṣṭhaṃ devās trāsam upāgaman</li>
        <li id="R_7.076.012cd">katham enaṃ vadhiṣyāmaḥ kathaṃ na syāt parājayaḥ</li>
    </ul>
    <ul id="R_7.076.013">
        <li id="R_7.076.013ab">teṣāṃ cintayatāṃ tatra sahasrākṣaḥ puraṃdaraḥ</li>
        <li id="R_7.076.013cd">vajraṃ pragṛhya bāhubhyāṃ prahiṇod vṛtramūrdhani</li>
    </ul>
    <ul id="R_7.076.014">
        <li id="R_7.076.014ab">kālāgnineva ghoreṇa dīpteneva mahārciṣā</li>
        <li id="R_7.076.014cd">prataptaṃ vṛtraśirasi jagat trāsam upāgamat</li>
    </ul>
    <ul id="R_7.076.015">
        <li id="R_7.076.015ab">asaṃbhāvyaṃ vadhaṃ tasya vṛtrasya vibudhādhipaḥ</li>
        <li id="R_7.076.015cd">cintayāno jagāmāśu lokasyāntaṃ mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.076.016">
        <li id="R_7.076.016ab">tam indraṃ brahmahatyāśu gacchantam anugacchati</li>
        <li id="R_7.076.016cd">apatac cāsya gātreṣu tam indraṃ duḥkham āviśat</li>
    </ul>
    <ul id="R_7.076.017">
        <li id="R_7.076.017ab">hatārayaḥ pranaṣṭendrā devāḥ sāgnipurogamāḥ</li>
        <li id="R_7.076.017cd">viṣṇuṃ tribhuvaṇaśreṣṭhaṃ muhur muhur apūjayan</li>
    </ul>
    <ul id="R_7.076.018">
        <li id="R_7.076.018ab">tvaṃ gatiḥ paramā deva pūrvajo jagataḥ prabhuḥ</li>
        <li id="R_7.076.018cd">rathārthaṃ sarvabhūtānāṃ viṣṇutvam upajagmivān</li>
    </ul>
    <ul id="R_7.076.019">
        <li id="R_7.076.019ab">hataś cāyaṃ tvayā vṛtro brahmahatyā ca vāsavam</li>
        <li id="R_7.076.019cd">bādhate suraśārdūla mokṣaṃ tasya vinirdiśa</li>
    </ul>
    <ul id="R_7.076.020">
        <li id="R_7.076.020ab">teṣāṃ tad vacanaṃ śrutvā devānāṃ viṣṇur abravīt</li>
        <li id="R_7.076.020cd">mām eva yajatāṃ śakraḥ pāvayiṣyāmi vajriṇam</li>
    </ul>
    <ul id="R_7.076.021">
        <li id="R_7.076.021ab">puṇyena hayamedhena mām iṣṭvā pākaśāsanaḥ</li>
        <li id="R_7.076.021cd">punar eṣyati devānām indratvam akutobhayaḥ</li>
    </ul>
    <ul id="R_7.076.022">
        <li id="R_7.076.022ab">evaṃ saṃdiśya devānāṃ tāṃ vāṇīm amṛtopamā</li>
        <li id="R_7.076.022cd">jagāma viṣṇur deveśaḥ stūyamānas triviṣṭapam</li>
    </ul>
    <ul id="R_7.077.001">
        <li id="R_7.077.001ab">tathā vṛtravadhaṃ sarvam akhilena sa lakṣmaṇaḥ</li>
        <li id="R_7.077.001cd">kathayitvā naraśreṣṭhaḥ kathāśeṣam upākramat</li>
    </ul>
    <ul id="R_7.077.002">
        <li id="R_7.077.002ab">tato hate mahāvīrye vṛtre devabhayaṃkare</li>
        <li id="R_7.077.002cd">brahmahatyāvṛtaḥ śakraḥ saṃjñāṃ lebhe na vṛtrahā</li>
    </ul>
    <ul id="R_7.077.003">
        <li id="R_7.077.003ab">so 'ntam āśritya lokānāṃ naṣṭasaṃjño vicetanaḥ</li>
        <li id="R_7.077.003cd">kālaṃ tatrāvasat kaṃ cid veṣṭamāno yathoragaḥ</li>
    </ul>
    <ul id="R_7.077.004">
        <li id="R_7.077.004ab">atha naṣṭe sahasrākṣe udvignam abhavaj jagat</li>
        <li id="R_7.077.004cd">bhūmiś ca dhvastasaṃkāśā niḥsnehā śuṣkakānanā</li>
    </ul>
    <ul id="R_7.077.005">
        <li id="R_7.077.005ab">niḥsrotasaś cāmbuvāhā hradāś ca saritas tathā</li>
        <li id="R_7.077.005cd">saṃkṣobhaś caiva sattvānām anāvṛṣṭikṛto 'bhavat</li>
    </ul>
    <ul id="R_7.077.006">
        <li id="R_7.077.006ab">kṣīyamāṇe tu loke 'smin saṃbhrāntamanasaḥ surāḥ</li>
        <li id="R_7.077.006cd">yad uktaṃ viṣṇunā pūrvaṃ taṃ yajñaṃ samupānayan</li>
    </ul>
    <ul id="R_7.077.007">
        <li id="R_7.077.007ab">tataḥ sarve suragaṇāḥ sopādhyāyāḥ saharṣibhiḥ</li>
        <li id="R_7.077.007cd">taṃ deśaṃ sahitā jagmur yatrendro bhayamohitaḥ</li>
    </ul>
    <ul id="R_7.077.008">
        <li id="R_7.077.008ab">te tu dṛṣṭvā sahasrākṣaṃ mohitaṃ brahmahatyayā</li>
        <li id="R_7.077.008cd">taṃ puraskṛtya deveśam aśvamedhaṃ pracakrire</li>
    </ul>
    <ul id="R_7.077.009">
        <li id="R_7.077.009ab">tato 'śvamedhaḥ sumahān mahendrasya mahātmanaḥ</li>
        <li id="R_7.077.009cd">vavṛdhe brahmahatyāyāḥ pāvanārthaṃ nareśvara</li>
    </ul>
    <ul id="R_7.077.010">
        <li id="R_7.077.010ab">tato yajñasamāptau tu brahmahatyā mahātmanaḥ</li>
        <li id="R_7.077.010cd">abhigamyābravīd vākyaṃ kva me sthānaṃ vidhāsyatha</li>
    </ul>
    <ul id="R_7.077.011">
        <li id="R_7.077.011ab">te tām ūcus tato devās tuṣṭāḥ prītisamanvitāḥ</li>
        <li id="R_7.077.011cd">caturdhā vibhajātmānam ātmanaiva durāsade</li>
    </ul>
    <ul id="R_7.077.012">
        <li id="R_7.077.012ab">devānāṃ bhāṣitaṃ śrutvā brahmahatyā mahātmanām</li>
        <li id="R_7.077.012cd">saṃnidhau sthānam anyatra varayām āsa durvasā</li>
    </ul>
    <ul id="R_7.077.013">
        <li id="R_7.077.013ab">ekenāṃśena vatsyāmi pūrṇodāsu nadīṣu vai</li>
        <li id="R_7.077.013cd">dvitīyena tu vṛkṣeṣu satyam etad bravīmi vaḥ</li>
    </ul>
    <ul id="R_7.077.014">
        <li id="R_7.077.014ab">yo 'yam aṃśas tṛtīyo me strīṣu yauvanaśāliṣu</li>
        <li id="R_7.077.014cd">trirātraṃ darpaparṇāsu vasiṣye darpaghātinī</li>
    </ul>
    <ul id="R_7.077.015">
        <li id="R_7.077.015ab">hantāro brāhmaṇān ye tu prekṣāpūrvam adūṣakān</li>
        <li id="R_7.077.015cd">tāṃś caturthena bhāgena saṃśrayiṣye surarṣabhāḥ</li>
    </ul>
    <ul id="R_7.077.016">
        <li id="R_7.077.016ab">pratyūcus tāṃ tato devā yathā vadasi durvase</li>
        <li id="R_7.077.016cd">tathā bhavatu tat sarvaṃ sādhayasva yathepsitam</li>
    </ul>
    <ul id="R_7.077.017">
        <li id="R_7.077.017ab">tataḥ prītyānvitā devāḥ sahasrākṣaṃ vavandire</li>
        <li id="R_7.077.017cd">vijvaraḥ pūtapāpmā ca vāsavaḥ samapadyata</li>
    </ul>
    <ul id="R_7.077.018">
        <li id="R_7.077.018ab">praśāntaṃ ca jagat sarvaṃ sahasrākṣe pratiṣṭhate</li>
        <li id="R_7.077.018cd">yajñaṃ cādbhutasaṃkāśaṃ tadā śakro 'bhyapūjayat</li>
    </ul>
    <ul id="R_7.077.019">
        <li id="R_7.077.019ab">īdṛśo hy aśvamedhasya prabhāvo raghunandana</li>
        <li id="R_7.077.019cd">yajasva sumahābhāga hayamedhena pārthiva</li>
    </ul>
    <ul id="R_7.078.001">
        <li id="R_7.078.001ab">tac chrutvā lakṣmaṇenoktaṃ vākyaṃ vākyaviśāradaḥ</li>
        <li id="R_7.078.001cd">pratyuvāca mahātejāḥ prahasan rāghavo vacaḥ</li>
    </ul>
    <ul id="R_7.078.002">
        <li id="R_7.078.002ab">evam etan naraśreṣṭha yathā vadasi lakṣmaṇa</li>
        <li id="R_7.078.002cd">vṛtraghātam aśeṣeṇa vājimedhaphalaṃ ca yat</li>
    </ul>
    <ul id="R_7.078.003">
        <li id="R_7.078.003ab">śrūyate hi purā saumya kardamasya prajāpateḥ</li>
        <li id="R_7.078.003cd">putro bāhlīśvaraḥ śrīmān ilo nāma sudhārmikaḥ</li>
    </ul>
    <ul id="R_7.078.004">
        <li id="R_7.078.004ab">sa rājā pṛthivīṃ sarvāṃ vaśe kṛtvā mahāyaśāḥ</li>
        <li id="R_7.078.004cd">rājyaṃ caiva naravyāghra putravat paryapālayat</li>
    </ul>
    <ul id="R_7.078.005">
        <li id="R_7.078.005ab">suraiś ca paramodārair daiteyaiś ca mahāsuraiḥ</li>
        <li id="R_7.078.005cd">nāgarākṣasagandharvair yakṣaiś ca sumahātmabhiḥ</li>
    </ul>
    <ul id="R_7.078.006">
        <li id="R_7.078.006ab">pūjyate nityaśaḥ saumya bhayārtai raghunandana</li>
        <li id="R_7.078.006cd">abibhyaṃś ca trayo lokāḥ saroṣasya mahātmanaḥ</li>
    </ul>
    <ul id="R_7.078.007">
        <li id="R_7.078.007ab">sa rājā tādṛśo hy āsīd dharme vīrye ca niṣṭhitaḥ</li>
        <li id="R_7.078.007cd">buddhyā ca paramodāro bāhlīkānāṃ mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.078.008">
        <li id="R_7.078.008ab">sa pracakre mahābāhur mṛgayāṃ rucire vane</li>
        <li id="R_7.078.008cd">caitre manorame māsi sabhṛtyabalavāhanaḥ</li>
    </ul>
    <ul id="R_7.078.009">
        <li id="R_7.078.009ab">prajaghne sa nṛpo 'raṇye mṛgāñ śatasahasraśaḥ</li>
        <li id="R_7.078.009cd">hatvaiva tṛptir nābhūc ca rājñas tasya mahātmanaḥ</li>
    </ul>
    <ul id="R_7.078.010">
        <li id="R_7.078.010ab">nānāmṛgāṇām ayutaṃ vadhyamānaṃ mahātmanā</li>
        <li id="R_7.078.010cd">yatra jāto mahāsenas taṃ deśam upacakrame</li>
    </ul>
    <ul id="R_7.078.011">
        <li id="R_7.078.011ab">tasmiṃs tu devadeveśaḥ śailarājasutāṃ haraḥ</li>
        <li id="R_7.078.011cd">ramayām āsa durdharṣaiḥ sarvair anucaraiḥ saha</li>
    </ul>
    <ul id="R_7.078.012">
        <li id="R_7.078.012ab">kṛtvā strībhūtam ātmānam umeśo gopatidhvajaḥ</li>
        <li id="R_7.078.012cd">devyāḥ priyacikīrṣuḥ sa tasmin parvatanirjhare</li>
    </ul>
    <ul id="R_7.078.013">
        <li id="R_7.078.013ab">ye ca tatra vanoddeśe sattvāḥ puruṣavādinaḥ</li>
        <li id="R_7.078.013cd">yac ca kiṃ cana tat sarvaṃ nārīsaṃjñaṃ babhūva ha</li>
    </ul>
    <ul id="R_7.078.014">
        <li id="R_7.078.014ab">etasminn antare rājā sa ilaḥ kardamātmajaḥ</li>
        <li id="R_7.078.014cd">nighnan mṛgasahasrāṇi taṃ deśam upacakrame</li>
    </ul>
    <ul id="R_7.078.015">
        <li id="R_7.078.015ab">sa dṛṣṭvā strīkṛtaṃ sarvaṃ savyālamṛgapakṣiṇam</li>
        <li id="R_7.078.015cd">ātmānaṃ sānugaṃ caiva strībhūtaṃ raghunandana</li>
    </ul>
    <ul id="R_7.078.016">
        <li id="R_7.078.016ab">tasya duḥkhaṃ mahat tv āsīd dṛṣṭvātmānaṃ tathā gatam</li>
        <li id="R_7.078.016cd">umāpateś ca tat karma jñātvā trāsam upāgamat</li>
    </ul>
    <ul id="R_7.078.017">
        <li id="R_7.078.017ab">tato devaṃ mahātmānaṃ śitikaṇṭhaṃ kapardinam</li>
        <li id="R_7.078.017cd">jagāma śaraṇaṃ rājā sabhṛtyabalavāhanaḥ</li>
    </ul>
    <ul id="R_7.078.018">
        <li id="R_7.078.018ab">tataḥ prahasya varadaḥ saha devyā mahāyaśāḥ</li>
        <li id="R_7.078.018cd">prajāpatisutaṃ vākyam uvāca varadaḥ svayam</li>
    </ul>
    <ul id="R_7.078.019">
        <li id="R_7.078.019ab">uttiṣṭhottiṣṭha rājarṣe kārdameya mahābala</li>
        <li id="R_7.078.019cd">puruṣatvam ṛte saumya varaṃ varaya suvrata</li>
    </ul>
    <ul id="R_7.078.020">
        <li id="R_7.078.020ab">tataḥ sa rājā śokārtāḥ pratyākhyāto mahātmanā</li>
        <li id="R_7.078.020cd">na sa jagrāha strībhūto varam anyaṃ surottamāt</li>
    </ul>
    <ul id="R_7.078.021">
        <li id="R_7.078.021ab">tataḥ śokena mahatā śailarājasutāṃ nṛpaḥ</li>
        <li id="R_7.078.021cd">praṇipatya mahādevīṃ sarveṇaivāntarātmanā</li>
    </ul>
    <ul id="R_7.078.022">
        <li id="R_7.078.022ab">īśe varāṇāṃ varade lokānām asi bhāmini</li>
        <li id="R_7.078.022cd">amoghadarśane devi bhaje saumye namo 'stu te</li>
    </ul>
    <ul id="R_7.078.023">
        <li id="R_7.078.023ab">hṛdgataṃ tasya rājarṣer vijñāya harasaṃnidhau</li>
        <li id="R_7.078.023cd">pratyuvāca śubhaṃ vākyaṃ devī rudrasya saṃmatā</li>
    </ul>
    <ul id="R_7.078.024">
        <li id="R_7.078.024ab">ardhasya devo varado varārdhasya tathā hy aham</li>
        <li id="R_7.078.024cd">tasmād ardhaṃ gṛhāṇa tvaṃ strīpuṃsor yāvad icchasi</li>
    </ul>
    <ul id="R_7.078.025">
        <li id="R_7.078.025ab">tad adbhutatamaṃ śrutvā devyā varam anuttamam</li>
        <li id="R_7.078.025cd">saṃprahṛṣṭamanā bhūtvā rājā vākyam athābravīt</li>
    </ul>
    <ul id="R_7.078.026">
        <li id="R_7.078.026ab">yadi devi prasannā me rūpeṇāpratimā bhuvi</li>
        <li id="R_7.078.026cd">māsaṃ strītvam upāsitvā māsaṃ syāṃ puruṣaḥ punaḥ</li>
    </ul>
    <ul id="R_7.078.027">
        <li id="R_7.078.027ab">īpsitaṃ tasya vijñāya devī surucirānanā</li>
        <li id="R_7.078.027cd">pratyuvāca śubhaṃ vākyam evam etad bhaviṣyati</li>
    </ul>
    <ul id="R_7.078.028">
        <li id="R_7.078.028ab">rājan puruṣabhūtas tvaṃ strībhāvaṃ na smariṣyasi</li>
        <li id="R_7.078.028cd">strībhūtaś cāparaṃ māsaṃ na smariṣyasi pauruṣam</li>
    </ul>
    <ul id="R_7.078.029">
        <li id="R_7.078.029ab">evaṃ sa rājā puruṣo māmaṃ bhūtvātha kārdamiḥ</li>
        <li id="R_7.078.029cd">trailokyasundarī nārī māsam ekam ilābhavat</li>
    </ul>
    <ul id="R_7.079.001">
        <li id="R_7.079.001ab">tāṃ kathām ilasaṃbaddhāṃ rāmeṇa samudīritām</li>
        <li id="R_7.079.001cd">lakṣmaṇo bharataś caiva śrutvā paramavismitau</li>
    </ul>
    <ul id="R_7.079.002">
        <li id="R_7.079.002ab">tau rāmaṃ prāñjalībhūtvā tasya rājño mahātmanaḥ</li>
        <li id="R_7.079.002cd">vistaraṃ tasya bhāvasya tadā papracchatuḥ punaḥ</li>
    </ul>
    <ul id="R_7.079.003">
        <li id="R_7.079.003ab">kathaṃ sa rājā strībhūto vartayām āsa durgatim</li>
        <li id="R_7.079.003cd">puruṣo vā yadā bhūtaḥ kāṃ vṛttiṃ vartayaty asau</li>
    </ul>
    <ul id="R_7.079.004">
        <li id="R_7.079.004ab">tayos tad bhāṣitaṃ śrutvā kautūhalasamanvitam</li>
        <li id="R_7.079.004cd">kathayām āsa kākutṣṭhas tasya rājño yathā gatam</li>
    </ul>
    <ul id="R_7.079.005">
        <li id="R_7.079.005ab">tam eva prathamaṃ māsaṃ strībhūtvā lokasundarī</li>
        <li id="R_7.079.005cd">tābhiḥ parivṛtā strībhir ye 'sya pūrvaṃ padānugāḥ</li>
    </ul>
    <ul id="R_7.079.006">
        <li id="R_7.079.006ab">tat kānanaṃ vigāhyāśu vijahre lokasundarī</li>
        <li id="R_7.079.006cd">drumagulmalatākīrṇaṃ padbhyāṃ padmadalekṣaṇā</li>
    </ul>
    <ul id="R_7.079.007">
        <li id="R_7.079.007ab">vāhanāni ca sarvāṇi saṃtyaktvā vai samantataḥ</li>
        <li id="R_7.079.007cd">parvatābhogavivare tasmin reme ilā tadā</li>
    </ul>
    <ul id="R_7.079.008">
        <li id="R_7.079.008ab">atha tasmin vanoddeśe parvatasyāvidūrataḥ</li>
        <li id="R_7.079.008cd">saraḥ suruciraprakhyaṃ nānāpakṣigaṇāyutam</li>
    </ul>
    <ul id="R_7.079.009">
        <li id="R_7.079.009ab">dadarśa sā ilā tasmin budhaṃ somasutaṃ tadā</li>
        <li id="R_7.079.009cd">jvalantaṃ svena vapuṣā pūrṇaṃ somam ivoditam</li>
    </ul>
    <ul id="R_7.079.010">
        <li id="R_7.079.010ab">tapantaṃ ca tapas tīvram ambhomadhye durāsadam</li>
        <li id="R_7.079.010cd">yaśak saraṃ kāmagamaṃ tāruṇye paryavasthitam</li>
    </ul>
    <ul id="R_7.079.011">
        <li id="R_7.079.011ab">sā taṃ jalāśayaṃ sarvaṃ kṣobhayām āsa vismitā</li>
        <li id="R_7.079.011cd">saha taiḥ pūra puruṣaiḥ strībhūtai raghunandana</li>
    </ul>
    <ul id="R_7.079.012">
        <li id="R_7.079.012ab">budhas tu tāṃ nirīkṣyaiva kāmabāṇābhipīḍitaḥ</li>
        <li id="R_7.079.012cd">nopalebhe tadātmānaṃ cacāla ca tadāmbhasi</li>
    </ul>
    <ul id="R_7.079.013">
        <li id="R_7.079.013ab">ilāṃ nirīkṣamāṇaḥ sa trailokyābhyadhikāṃ śubhām</li>
        <li id="R_7.079.013cd">cintāṃ samabhyatikrāmat kā nv iyaṃ devatādhikā</li>
    </ul>
    <ul id="R_7.079.014">
        <li id="R_7.079.014ab">na devīṣu na nāgīṣu nāsurīṣv apsaraḥsu ca</li>
        <li id="R_7.079.014cd">dṛṣṭapūrvā mayā kā cid rūpeṇaitena śobhitā</li>
    </ul>
    <ul id="R_7.079.015">
        <li id="R_7.079.015ab">sadṛśīyaṃ mama bhaved yadi nānyaparigrahā</li>
        <li id="R_7.079.015cd">iti buddhiṃ samāsthāya jalāt sthalam upāgamat</li>
    </ul>
    <ul id="R_7.079.016">
        <li id="R_7.079.016ab">sa āśramaṃ samupāgamya catasraḥ pramadās tataḥ</li>
        <li id="R_7.079.016cd">śabdāpayata dharmātmā tāś cainaṃ ca vavandire</li>
    </ul>
    <ul id="R_7.079.017">
        <li id="R_7.079.017ab">sa tāḥ papraccha dharmātma kasyaiṣā lokasundarī</li>
        <li id="R_7.079.017cd">kimartham āgatā ceha satyam ākhyāta māciram</li>
    </ul>
    <ul id="R_7.079.018">
        <li id="R_7.079.018ab">śubhaṃ tu tasya tadvākyaṃ madhuraṃ madhurākṣaram</li>
        <li id="R_7.079.018cd">śrutvā tu tāḥ striyaḥ sarvā ūcur madhurayā girā</li>
    </ul>
    <ul id="R_7.079.019">
        <li id="R_7.079.019ab">asmākam eṣā suśroṇī prabhutve vartate sadā</li>
        <li id="R_7.079.019cd">apatiḥ kānanānteṣu sahāsmābhir aṭaty asau</li>
    </ul>
    <ul id="R_7.079.020">
        <li id="R_7.079.020ab">tad vākyam avyaktapadaṃ tāsāṃ strīṇāṃ niśamya tu</li>
        <li id="R_7.079.020cd">vidyām āvartanīṃ puṇyām āvartayata sa dvijaḥ</li>
    </ul>
    <ul id="R_7.079.021">
        <li id="R_7.079.021ab">so 'rthaṃ viditvā nikhilaṃ tasya rājño yathāgatam</li>
        <li id="R_7.079.021cd">sarvā eva striyas tāś ca babhāṣe munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.079.022">
        <li id="R_7.079.022ab">atra kiṃ puruṣā bhadrā avasañ śailarodhasi</li>
        <li id="R_7.079.022cd">vatsyathāsmin girau yūyam avakāśo vidhīyatām</li>
    </ul>
    <ul id="R_7.079.023">
        <li id="R_7.079.023ab">mūlaputraphalaiḥ sarvā vartayiṣyatha nityadā</li>
        <li id="R_7.079.023cd">striyaḥ kimpuruṣān nāma bhartṝn samupalapsyatha</li>
    </ul>
    <ul id="R_7.079.024">
        <li id="R_7.079.024ab">tāḥ śrutvā somaputrasya vācaṃ kiṃpuruṣīkṛtāḥ</li>
        <li id="R_7.079.024cd">upāsāṃ cakrire śailaṃ bahvyas tā bahudhā tadā</li>
    </ul>
    <ul id="R_7.080.001">
        <li id="R_7.080.001ab">śrutvā kimpuruṣotpattiṃ lakṣmaṇo bharatas tadā</li>
        <li id="R_7.080.001cd">āścaryam iti cābrūtām ubhau rāmaṃ janeśvaram</li>
    </ul>
    <ul id="R_7.080.002">
        <li id="R_7.080.002ab">atha rāmaḥ kathām etāṃ bhūya eva mahāyaśāḥ</li>
        <li id="R_7.080.002cd">kathayām āsa dharmātmā prajāpatisutasya vai</li>
    </ul>
    <ul id="R_7.080.003">
        <li id="R_7.080.003ab">sarvās tā vidrutā dṛṣṭvā kiṃnarīr ṛṣisattamaḥ</li>
        <li id="R_7.080.003cd">uvāca rūpasaṃpannāṃ tāṃ striyaṃ prahasann iva</li>
    </ul>
    <ul id="R_7.080.004">
        <li id="R_7.080.004ab">somasyāhaṃ sudayitaḥ sutaḥ surucirānane</li>
        <li id="R_7.080.004cd">bhajasva māṃ varārohe bhaktyā snigdhena cakṣuṣā</li>
    </ul>
    <ul id="R_7.080.005">
        <li id="R_7.080.005ab">tasya tad vacanaṃ śrutvā śūnye svajanavarjitā</li>
        <li id="R_7.080.005cd">ilā suruciraprakhyaṃ pratyuvāca mahāgraham</li>
    </ul>
    <ul id="R_7.080.006">
        <li id="R_7.080.006ab">ahaṃ kāmakarī saumya tavāsmi vaśavartinī</li>
        <li id="R_7.080.006cd">praśādhi māṃ somasuta yathecchasi tathā kuru</li>
    </ul>
    <ul id="R_7.080.007">
        <li id="R_7.080.007ab">tasyās tad adbhutaprakhyaṃ śrutvā harṣasamanvitaḥ</li>
        <li id="R_7.080.007cd">sa vai kāmī saha tayā reme candramasaḥ sutaḥ</li>
    </ul>
    <ul id="R_7.080.008">
        <li id="R_7.080.008ab">budhasya mādhavo māsas tām ilāṃ rucirānanām</li>
        <li id="R_7.080.008cd">gato ramayato 'tyarthaṃ kṣaṇavat tasya kāminaḥ</li>
    </ul>
    <ul id="R_7.080.009">
        <li id="R_7.080.009ab">atha māse tu saṃpūrṇe pūrṇendusadṛśānanaḥ</li>
        <li id="R_7.080.009cd">prajāpatisutaḥ śrīmāñ śayane pratyabudhyata</li>
    </ul>
    <ul id="R_7.080.010">
        <li id="R_7.080.010ab">so 'paśyat somajaṃ tatra tapyantaṃ salilāśaye</li>
        <li id="R_7.080.010cd">ūrdhvabāhuṃ nirālambaṃ taṃ rājā pratyabhāṣata</li>
    </ul>
    <ul id="R_7.080.011">
        <li id="R_7.080.011ab">bhagavan parvataṃ durgaṃ praviṣṭo 'smi sahānugaḥ</li>
        <li id="R_7.080.011cd">na ca paśyāmi tat sainyaṃ kva nu te māmakā gatāḥ</li>
    </ul>
    <ul id="R_7.080.012">
        <li id="R_7.080.012ab">tac chrutvā tasya rājarṣer naṣṭasaṃjñasya bhāṣitam</li>
        <li id="R_7.080.012cd">pratyuvāca śubhaṃ vākyaṃ sāntvayan parayā girā</li>
    </ul>
    <ul id="R_7.080.013">
        <li id="R_7.080.013ab">aśmavarṣeṇa mahatā bhṛtyās te vinipātitāḥ</li>
        <li id="R_7.080.013cd">tvaṃ cāśramapade supto vātavarṣabhayārditaḥ</li>
    </ul>
    <ul id="R_7.080.014">
        <li id="R_7.080.014ab">samāśvasihi bhadraṃ te nirbhayo vigatajvaraḥ</li>
        <li id="R_7.080.014cd">phalamūlāśano vīra vasa ceha yathāsukham</li>
    </ul>
    <ul id="R_7.080.015">
        <li id="R_7.080.015ab">sa rājā tena vākyena pratyāśvasto mahāyaśāḥ</li>
        <li id="R_7.080.015cd">pratyuvāca śubhaṃ vākyaṃ dīno bhṛtyajanakṣayāt</li>
    </ul>
    <ul id="R_7.080.016">
        <li id="R_7.080.016ab">tyakṣyāmy ahaṃ svakaṃ rājyaṃ nāhaṃ bhṛtyair vinā kṛtaḥ</li>
        <li id="R_7.080.016cd">vartayeyaṃ kṣaṇaṃ brahman samanujñātum arhasi</li>
    </ul>
    <ul id="R_7.080.017">
        <li id="R_7.080.017ab">suto dharmaparo brahmañ jyeṣṭho mama mahāyaśāḥ</li>
        <li id="R_7.080.017cd">śaśabindur iti khyātaḥ sa me rājyaṃ prapatsyate</li>
    </ul>
    <ul id="R_7.080.018">
        <li id="R_7.080.018ab">na hi śakṣyāmy ahaṃ gatvā bhṛtyadārān sukhānvitān</li>
        <li id="R_7.080.018cd">prativaktuṃ mahātejaḥ kiṃ cid apy aśubhaṃ vacaḥ</li>
    </ul>
    <ul id="R_7.080.019">
        <li id="R_7.080.019ab">tathā bruvati rājendre budhaḥ paramam adbhutam</li>
        <li id="R_7.080.019cd">sāntvapūrvam athovāca vāsas ta iha rocatām</li>
    </ul>
    <ul id="R_7.080.020">
        <li id="R_7.080.020ab">na saṃtāpas tvayā kāryaḥ kārdameya mahābala</li>
        <li id="R_7.080.020cd">saṃvatsaroṣitasyeha kārayiṣyāmi te hitam</li>
    </ul>
    <ul id="R_7.080.021">
        <li id="R_7.080.021ab">tasya tad vacanaṃ śrutvā budhasyākliṣṭakarmaṇaḥ</li>
        <li id="R_7.080.021cd">vāsāya vidadhe buddhiṃ yad uktaṃ brahmavādinā</li>
    </ul>
    <ul id="R_7.080.022">
        <li id="R_7.080.022ab">māsaṃ sa strī tadā bhūtvā ramayaty aniśaṃ śubhā</li>
        <li id="R_7.080.022cd">māsaṃ puruṣabhāvena dharmabuddhiṃ cakāra saḥ</li>
    </ul>
    <ul id="R_7.080.023">
        <li id="R_7.080.023ab">tataḥ sa navame māsi ilā somasutātmajam</li>
        <li id="R_7.080.023cd">janayām āsa suśroṇī purūravasam ātmajam</li>
    </ul>
    <ul id="R_7.080.024">
        <li id="R_7.080.024ab">jātamātraṃ tu suśroṇī pitur haste nyaveśayat</li>
        <li id="R_7.080.024cd">budhasya samavarṇābham ilāputraṃ mahābalam</li>
    </ul>
    <ul id="R_7.080.025">
        <li id="R_7.080.025ab">budho 'pi puruṣībhūtaṃ samāśvāsya narādhipam</li>
        <li id="R_7.080.025cd">kathābhī ramayām āsa dharmayuktābhir ātmavān</li>
    </ul>
    <ul id="R_7.081.001">
        <li id="R_7.081.001ab">tathoktavati rāme tu tasya janma tad adbhutam</li>
        <li id="R_7.081.001cd">uvāca lakṣmaṇo bhūyo bharataś ca mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.081.002">
        <li id="R_7.081.002ab">sā priyā somaputrasya saṃvatsaram athoṣitā</li>
        <li id="R_7.081.002cd">akarot kiṃ naraśreṣṭha tat tvaṃ śaṃsitum arhasi</li>
    </ul>
    <ul id="R_7.081.003">
        <li id="R_7.081.003ab">tayos tad vākyamādhuryaṃ niśamya paripṛcchatoḥ</li>
        <li id="R_7.081.003cd">rāmaḥ punar uvācemāṃ prajāpatisute kathām</li>
    </ul>
    <ul id="R_7.081.004">
        <li id="R_7.081.004ab">puruṣatvaṃ gate śūre budhaḥ paramabuddhimān</li>
        <li id="R_7.081.004cd">saṃvartaṃ paramodāram ājuhāva mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.081.005">
        <li id="R_7.081.005ab">cyavanaṃ bhṛguputraṃ ca muniṃ cāriṣṭaneminam</li>
        <li id="R_7.081.005cd">pramodanaṃ modakaraṃ tato durvāsasaṃ munim</li>
    </ul>
    <ul id="R_7.081.006">
        <li id="R_7.081.006ab">etān sarvān samānīya vākyajñas tattvadarśinaḥ</li>
        <li id="R_7.081.006cd">uvāca sarvān suhṛdo dhairyeṇa susamāhitaḥ</li>
    </ul>
    <ul id="R_7.081.007">
        <li id="R_7.081.007ab">ayaṃ rājā mahābāhuḥ kardamasya ilaḥ sutaḥ</li>
        <li id="R_7.081.007cd">jānītainaṃ yathā bhūtaṃ śreyo hy asya vidhīyatām</li>
    </ul>
    <ul id="R_7.081.008">
        <li id="R_7.081.008ab">teṣāṃ saṃvadatām eva tam āśramam upāgamat</li>
        <li id="R_7.081.008cd">kardamaḥ sumahātejā dvijaiḥ saha mahātmabhiḥ</li>
    </ul>
    <ul id="R_7.081.009">
        <li id="R_7.081.009ab">pulastyaś ca kratuś caiva vaṣaṭkāras tathaiva ca</li>
        <li id="R_7.081.009cd">oṃkāraś ca mahātejās tam āśramam upāgaman</li>
    </ul>
    <ul id="R_7.081.010">
        <li id="R_7.081.010ab">te sarve hṛṣṭamanasaḥ parasparasamāgame</li>
        <li id="R_7.081.010cd">hitaiṣiṇo bāhli pateḥ pṛthag vākyam athābruvan</li>
    </ul>
    <ul id="R_7.081.011">
        <li id="R_7.081.011ab">kardamas tv abravīd vākyaṃ sutārthaṃ paramaṃ hitam</li>
        <li id="R_7.081.011cd">dvijāḥ śṛṇuta madvākyaṃ yac chreyaḥ pārthivasya hi</li>
    </ul>
    <ul id="R_7.081.012">
        <li id="R_7.081.012ab">nānyaṃ paśyāmi bhaiṣajyam antareṇa vṛṣadhvajam</li>
        <li id="R_7.081.012cd">nāśvamedhāt paro yajñaḥ priyaś caiva mahātmanaḥ</li>
    </ul>
    <ul id="R_7.081.013">
        <li id="R_7.081.013ab">tasmād yajāmahe sarve pārthivārthe durāsadam</li>
        <li id="R_7.081.013cd">kardamenaivam uktās tu sarva eva dvijarṣabhāḥ</li>
        <li id="R_7.081.013ef">rocayanti sma taṃ yajñaṃ rudrasyārādhanaṃ prati</li>
    </ul>
    <ul id="R_7.081.014">
        <li id="R_7.081.014ab">saṃvartasya tu rājarṣiḥ śiṣyaḥ parapuraṃjayaḥ</li>
        <li id="R_7.081.014cd">marutta iti vikhyātas taṃ yajñaṃ samupāharat</li>
    </ul>
    <ul id="R_7.081.015">
        <li id="R_7.081.015ab">tato yajño mahān āsīd budhāśramasamīpataḥ</li>
        <li id="R_7.081.015cd">rudraś ca paramaṃ toṣam ājagāma mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.081.016">
        <li id="R_7.081.016ab">atha yajñasamāptau tu prītaḥ paramayā mudā</li>
        <li id="R_7.081.016cd">umāpatir dvijān sarvān uvācedam ilāṃ prati</li>
    </ul>
    <ul id="R_7.081.017">
        <li id="R_7.081.017ab">prīto 'smi hayamedhena bhaktyā ca dvijasattamāḥ</li>
        <li id="R_7.081.017cd">asya bāhlipateś caiva kiṃ karomi priyaṃ śubham</li>
    </ul>
    <ul id="R_7.081.018">
        <li id="R_7.081.018ab">tathā vadati deveśe dvijās te susamāhitāḥ</li>
        <li id="R_7.081.018cd">prasādayanti deveśaṃ yathā syāt puruṣas tv ilā</li>
    </ul>
    <ul id="R_7.081.019">
        <li id="R_7.081.019ab">tataḥ prītamanā rudraḥ puruṣatvaṃ dadau punaḥ</li>
        <li id="R_7.081.019cd">ilāyai sumahātejā dattvā cāntaradhīyata</li>
    </ul>
    <ul id="R_7.081.020">
        <li id="R_7.081.020ab">nivṛtte hayamedhe tu gate cādarśanaṃ hare</li>
        <li id="R_7.081.020cd">yathāgataṃ dvijāḥ sarve agacchan dīrghadarśinaḥ</li>
    </ul>
    <ul id="R_7.081.021">
        <li id="R_7.081.021ab">rājā tu bāhlim utsṛjya madhyadeśe hy anuttamam</li>
        <li id="R_7.081.021cd">niveśayām āsa puraṃ pratiṣṭhānaṃ yaśaskaram</li>
    </ul>
    <ul id="R_7.081.022">
        <li id="R_7.081.022ab">śaśabindus tu rājāsīd bāhlyāṃ parapuraṃjayaḥ</li>
        <li id="R_7.081.022cd">pratiṣṭhāna ilo rājā prajāpatisuto balī</li>
    </ul>
    <ul id="R_7.081.023">
        <li id="R_7.081.023ab">sa kāle prāptavāṃl lokam ilo brāhmam anuttamam</li>
        <li id="R_7.081.023cd">ailaḥ purūravā rājā pratiṣṭhānam avāptavān</li>
    </ul>
    <ul id="R_7.081.024">
        <li id="R_7.081.024ab">īdṛśo hy aśvamedhasya prabhāvaḥ puruṣarṣabhau</li>
        <li id="R_7.081.024cd">strībhūtaḥ pauruṣaṃ lebhe yac cānyad api durlabham</li>
    </ul>
    <ul id="R_7.082.001">
        <li id="R_7.082.001ab">etad ākhyāya kākutstho bhrātṛhyām amitaprabhaḥ</li>
        <li id="R_7.082.001cd">lakṣmaṇaṃ punār evāha dharmayuktam idaṃ vacaḥ</li>
    </ul>
    <ul id="R_7.082.002">
        <li id="R_7.082.002ab">vasiṣṭhaṃ vāmadevaṃ ca jābālim atha kaśyapam</li>
        <li id="R_7.082.002cd">dvijāṃś ca sarvapravarān aśvamedhapuraskṛtān</li>
    </ul>
    <ul id="R_7.082.003">
        <li id="R_7.082.003ab">etān sarvān samāhūya mantrayitvā ca lakṣmaṇa</li>
        <li id="R_7.082.003cd">hayaṃ lakṣmaṇasaṃpannaṃ vimokṣyāmi samādhinā</li>
    </ul>
    <ul id="R_7.082.004">
        <li id="R_7.082.004ab">tad vākyaṃ rāghaveṇoktaṃ śrutvā tvaritavikramaḥ</li>
        <li id="R_7.082.004cd">dvijān sarvān samāhūya darśayām āsa rāghavam</li>
    </ul>
    <ul id="R_7.082.005">
        <li id="R_7.082.005ab">te dṛṣṭvā devasaṃkāśaṃ kṛtapādābhivandanam</li>
        <li id="R_7.082.005cd">rāghavaṃ sudurādharṣam āśīrbhiḥ samapūjayan</li>
    </ul>
    <ul id="R_7.082.006">
        <li id="R_7.082.006ab">prāñjalis tu tato bhūtvā rāghavo dvijasāttamān</li>
        <li id="R_7.082.006cd">uvāca dharmasaṃyuktam aśvamedhāśritaṃ vacaḥ</li>
    </ul>
    <ul id="R_7.082.007">
        <li id="R_7.082.007ab">sa teṣāṃ dvijamukhyānāṃ vākyam adbhutadarśanam</li>
        <li id="R_7.082.007cd">aśvamedhāśritaṃ śrutvā bhṛśaṃ prīto 'bhavat tadā</li>
    </ul>
    <ul id="R_7.082.008">
        <li id="R_7.082.008ab">vijñāya tu mataṃ teṣāṃ rāmo lakṣmaṇam abravīt</li>
        <li id="R_7.082.008cd">preṣayasva mahābāho sugrīvāya mahātmane</li>
    </ul>
    <ul id="R_7.082.009">
        <li id="R_7.082.009ab">śīghraṃ mahadbhir haribhir bahibhiś ca tadāśrayaiḥ</li>
        <li id="R_7.082.009cd">sārdham āgaccha bhadraṃ te anubhoktuṃ makhottamam</li>
    </ul>
    <ul id="R_7.082.010">
        <li id="R_7.082.010ab">vibhīṣaṇaś ca rakṣobhiḥ kāmagair bahubhir vṛtaḥ</li>
        <li id="R_7.082.010cd">aśvamedhaṃ mahābāhuḥ prāpnotu laghuvikramaḥ</li>
    </ul>
    <ul id="R_7.082.011">
        <li id="R_7.082.011ab">rājānaś ca naravyāghra ye me priyacikīrṣavaḥ</li>
        <li id="R_7.082.011cd">sānugāḥ kṣipram āyāntu yajñabhūmim anuttamām</li>
    </ul>
    <ul id="R_7.082.012">
        <li id="R_7.082.012ab">deśāntaragatā ye ca dvijā dharmaparāyaṇāḥ</li>
        <li id="R_7.082.012cd">nimantrayasva tān sarvān aśvamedhāya lakṣmaṇa</li>
    </ul>
    <ul id="R_7.082.013">
        <li id="R_7.082.013ab">ṛṣayaś cā mahābāho āhūyantāṃ tapodhanāḥ</li>
        <li id="R_7.082.013cd">deśāntaragatā ye ca sadārāś ca maharṣayaḥ</li>
    </ul>
    <ul id="R_7.082.014">
        <li id="R_7.082.014ab">yajñavāṭaś ca sumahān gomatyā naimiṣe vane</li>
        <li id="R_7.082.014cd">ājñāpyatāṃ mahābāho tad dhi puṇyam anuttamam</li>
    </ul>
    <ul id="R_7.082.015">
        <li id="R_7.082.015ab">śataṃ vāhasahasrāṇāṃ taṇḍulānāṃ vapuṣmatām</li>
        <li id="R_7.082.015cd">ayutaṃ tilamudgasya prayātv agre mahābala</li>
    </ul>
    <ul id="R_7.082.016">
        <li id="R_7.082.016ab">suvarṇakoṭyo bahulā hiraṇyasya śatottarāḥ</li>
        <li id="R_7.082.016cd">agrato bharataḥ kṛtvā gacchatv agre mahāmatiḥ</li>
    </ul>
    <ul id="R_7.082.017">
        <li id="R_7.082.017ab">antarāpaṇavīthyaś ca sarvāṃś ca naṭanartakān</li>
        <li id="R_7.082.017cd">naigamān bālavṛddhāṃś ca dvijāṃś ca susamāhitān</li>
    </ul>
    <ul id="R_7.082.018">
        <li id="R_7.082.018ab">karmāntikāṃś ca kuśalāñ śilpinaś ca supaṇḍitān</li>
        <li id="R_7.082.018cd">mātaraś caiva me sarvāḥ kumārāntaḥpurāṇi ca</li>
    </ul>
    <ul id="R_7.082.019">
        <li id="R_7.082.019ab">kāñcanīṃ mama patnīṃ ca dīkṣārhāṃ yajñakarmaṇi</li>
        <li id="R_7.082.019cd">agrato bharataḥ kṛtvā gacchatv agre mahāmatiḥ</li>
    </ul>
    <ul id="R_7.083.001">
        <li id="R_7.083.001ab">tat sarvam akhilenāśu prasthāpya bharatāgrajaḥ</li>
        <li id="R_7.083.001cd">hayaṃ lakṣmaṇasaṃpannaṃ kṛṣṇasāraṃ mumoca ha</li>
    </ul>
    <ul id="R_7.083.002">
        <li id="R_7.083.002ab">ṛtvigbhir lakṣmaṇaṃ sārdham aśve ca viniyujya saḥ</li>
        <li id="R_7.083.002cd">tato 'bhyagacchat kākutsthaḥ saha sainyena naimiṣam</li>
    </ul>
    <ul id="R_7.083.003">
        <li id="R_7.083.003ab">yajñavāṭaṃ mahābāhur dṛṣṭvā paramam adbhutam</li>
        <li id="R_7.083.003cd">praharṣam atulaṃ lebhe śrīmān iti ca so 'bravīt</li>
    </ul>
    <ul id="R_7.083.004">
        <li id="R_7.083.004ab">naimiṣe vasatas tasya sarva eva narādhipāḥ</li>
        <li id="R_7.083.004cd">ājagmuḥ sarvarāṣṭrebhyas tān rāmaḥ pratyapūjayat</li>
    </ul>
    <ul id="R_7.083.005">
        <li id="R_7.083.005ab">upakāryān mahārhāṃś ca pārthivānāṃ mahātmanām</li>
        <li id="R_7.083.005cd">sānugānāṃ naraśreṣṭho vyādideśa mahādyutiḥ</li>
    </ul>
    <ul id="R_7.083.006">
        <li id="R_7.083.006ab">annapānāni vastrāṇi sānugānāṃ mahātmanām</li>
        <li id="R_7.083.006cd">bharataḥ saṃdadāv āśu śatrughnasahitas tadā</li>
    </ul>
    <ul id="R_7.083.007">
        <li id="R_7.083.007ab">vānarāś ca mahātmānaḥ sugrīvasahitās tadā</li>
        <li id="R_7.083.007cd">viprāṇāṃ praṇatāḥ sarve cakrire pariveṣaṇam</li>
    </ul>
    <ul id="R_7.083.008">
        <li id="R_7.083.008ab">vibhīṣaṇaś ca rakṣobhiḥ sragvibhir bahubhir vṛtaḥ</li>
        <li id="R_7.083.008cd">ṛṣīṇām ugratapasāṃ kiṃkaraḥ paryupasthitaḥ</li>
    </ul>
    <ul id="R_7.083.009">
        <li id="R_7.083.009ab">evaṃ suvihito yajño hayamedho 'bhyavartata</li>
        <li id="R_7.083.009cd">lakṣmaṇenābhiguptā ca hayacaryā pravartitā</li>
    </ul>
    <ul id="R_7.083.010">
        <li id="R_7.083.010ab">nānyaḥ śabdo 'bhavat tatra hayamedhe mahātmanaḥ</li>
        <li id="R_7.083.010cd">chandato dehi visrabdho yāvat tuṣyanti yācakāḥ</li>
        <li id="R_7.083.010ef">tāvad vānararakṣobhir dattam evābhyadṛśyata</li>
    </ul>
    <ul id="R_7.083.011">
        <li id="R_7.083.011ab">na kaś cin malinas tatra dīno vāpy atha vā kṛśaḥ</li>
        <li id="R_7.083.011cd">tasmin yajñavare rājño hṛṣṭapuṣṭajanāvṛte</li>
    </ul>
    <ul id="R_7.083.012">
        <li id="R_7.083.012ab">ye ca tatra mahātmāno munayaś cirajīvinaḥ</li>
        <li id="R_7.083.012cd">nāsmaraṃs tādṛśaṃ yajñaṃ dānaughasamalaṃkṛtam</li>
    </ul>
    <ul id="R_7.083.013">
        <li id="R_7.083.013ab">rajatānāṃ suvarṇānāṃ ratnānām atha vāsasām</li>
        <li id="R_7.083.013cd">aniśaṃ dīyamānānāṃ nāntaḥ samupadṛśyate</li>
    </ul>
    <ul id="R_7.083.014">
        <li id="R_7.083.014ab">na śakrasya na somasya yamasya varuṇasya vā</li>
        <li id="R_7.083.014cd">īdṛśo dṛṣṭapūrvo na evam ūcus tapodhanāḥ</li>
    </ul>
    <ul id="R_7.083.015">
        <li id="R_7.083.015ab">sarvatra vānarās tasthuḥ sarvatraiva ca rākṣasāḥ</li>
        <li id="R_7.083.015cd">vāso dhanāni kāmibhyaḥ pūrṇahastā dadur bhṛśam</li>
    </ul>
    <ul id="R_7.083.016">
        <li id="R_7.083.016ab">īdṛśo rājasiṃhasya yajñaḥ sarvaguṇānvitaḥ</li>
        <li id="R_7.083.016cd">saṃvatsaram atho sāgraṃ vartate na ca hīyate</li>
    </ul>
    <ul id="R_7.084.001">
        <li id="R_7.084.001ab">vartamāne tathābhūte yajñe paramake 'dbhute</li>
        <li id="R_7.084.001cd">saśiṣya ājagāmāśu vālmīkir munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.084.002">
        <li id="R_7.084.002ab">sa dṛṣṭvā divyasaṃkāśaṃ yajñam adbhutadarśanam</li>
        <li id="R_7.084.002cd">ekānte ṛṣivāṭānāṃ cakāra uṭajāñ śubhān</li>
    </ul>
    <ul id="R_7.084.003">
        <li id="R_7.084.003ab">sa śiṣyāv abravīd dhṛṣṭo yuvāṃ gatvā samāhitau</li>
        <li id="R_7.084.003cd">kṛtsnaṃ rāmāyaṇaṃ kāvyaṃ gāyatāṃ parayā mudā</li>
    </ul>
    <ul id="R_7.084.004">
        <li id="R_7.084.004ab">ṛṣivāṭeṣu puṇyeṣu brāhmaṇāvasatheṣu ca</li>
        <li id="R_7.084.004cd">rathyāsu rājamārgeṣu pārthivānāṃ gṛheṣu ca</li>
    </ul>
    <ul id="R_7.084.005">
        <li id="R_7.084.005ab">rāmasya bhavanadvāri yatra karma ca vartate</li>
        <li id="R_7.084.005cd">ṛtvijām agrataś caiva tatra geyaṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_7.084.006">
        <li id="R_7.084.006ab">imāni ca phalāny atra svādūni vividhāni ca</li>
        <li id="R_7.084.006cd">jātāni parvatāgreṣu āsvādyāsvādya gīyatām</li>
    </ul>
    <ul id="R_7.084.007">
        <li id="R_7.084.007ab">na yāsyathaḥ śramaṃ vatsau bhakṣayitvā phalāni vai</li>
        <li id="R_7.084.007cd">mūlāni ca sumṛṣṭāni nagarāt parihāsyatha</li>
    </ul>
    <ul id="R_7.084.008">
        <li id="R_7.084.008ab">yadi śabdāpayed rāmaḥ śravaṇāya mahīpatiḥ</li>
        <li id="R_7.084.008cd">ṛṣīṇām upaviṣṭānāṃ tato geyaṃ pravartatām</li>
    </ul>
    <ul id="R_7.084.009">
        <li id="R_7.084.009ab">divase viṃśatiḥ sargā geyā vai parayā mudā</li>
        <li id="R_7.084.009cd">pramāṇair bahubhis tatra yathoddiṣṭaṃ mayā purā</li>
    </ul>
    <ul id="R_7.084.010">
        <li id="R_7.084.010ab">lobhaś cāpi na kartavyaḥ svalpo 'pi dhanakāṅkṣayā</li>
        <li id="R_7.084.010cd">kiṃ dhanenāśramasthānāṃ phalamūlopabhoginām</li>
    </ul>
    <ul id="R_7.084.011">
        <li id="R_7.084.011ab">yadi pṛcchet sa kākutstho yuvāṃ kasyeti dārakau</li>
        <li id="R_7.084.011cd">vālmīker atha śiṣyau hi brūtām evaṃ narādhipam</li>
    </ul>
    <ul id="R_7.084.012">
        <li id="R_7.084.012ab">imās tantrīḥ sumadhurāḥ sthānaṃ vā pūrvadarśitam</li>
        <li id="R_7.084.012cd">mūrchayitvā sumadhuraṃ gāyetāṃ vigatajvarau</li>
    </ul>
    <ul id="R_7.084.013">
        <li id="R_7.084.013ab">ādiprabhṛti geyaṃ syān na cāvajñāya pārthivam</li>
        <li id="R_7.084.013cd">pitā hi sarvabhūtānāṃ rājā bhavati dharmataḥ</li>
    </ul>
    <ul id="R_7.084.014">
        <li id="R_7.084.014ab">tad yuvāṃ hṛṣṭamanasau śvaḥ prabhāte samādhinā</li>
        <li id="R_7.084.014cd">gāyetāṃ madhuraṃ geyaṃ tantrīlayasamanvitam</li>
    </ul>
    <ul id="R_7.084.015">
        <li id="R_7.084.015ab">iti saṃdiśya bahuśo muniḥ prācetasas tadā</li>
        <li id="R_7.084.015cd">vālmīkiḥ paramodāras tūṣṇīm āsīn mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.084.016">
        <li id="R_7.084.016ab">
            <seg type="pāda" n="a">tām adbhutāṃ tau hṛdaye kumārau</seg>
            <seg type="pāda" n="b">niveśya vāṇīm ṛṣibhāṣitāṃ śubhām</seg>
        </li>
        <li id="R_7.084.016cd">
            <seg type="pāda" n="c">samutsukau tau sukham ūṣatur niśāṃ</seg>
            <seg type="pāda" n="d">yathāśvinau bhārgavanītisaṃskṛtau</seg>
        </li>
    </ul>
    <ul id="R_7.085.001">
        <li id="R_7.085.001ab">tau rajanyāṃ prabhātāyāṃ snātau hutahutāśanau</li>
        <li id="R_7.085.001cd">yathoktam ṛṣiṇā pūrvaṃ tatra tatrābhyagāyatām</li>
    </ul>
    <ul id="R_7.085.002">
        <li id="R_7.085.002ab">tāṃ sa śuśrāva kākutsthaḥ pūrvacaryāṃ tatas tataḥ</li>
        <li id="R_7.085.002cd">apūrvāṃ pāṭhya jātiṃ ca geyena samalaṃkṛtām</li>
    </ul>
    <ul id="R_7.085.003">
        <li id="R_7.085.003ab">pramāṇair bahubhir baddhāṃ tantrīlayasamanvitām</li>
        <li id="R_7.085.003cd">bālābhyāṃ rāghavaḥ śrutvā kautūhalaparo 'bhavat</li>
    </ul>
    <ul id="R_7.085.004">
        <li id="R_7.085.004ab">atha karmāntare rājā samānīya mahāmunīn</li>
        <li id="R_7.085.004cd">pārthivāṃś ca naravyāghraḥ paṇḍitān naigamāṃs tathā</li>
    </ul>
    <ul id="R_7.085.005">
        <li id="R_7.085.005ab">paurāṇikāñ śabdavito ye ca vṛddhā dvijātayaḥ</li>
        <li id="R_7.085.005cd">etān sarvān samānīya gātārau samaveśayat</li>
    </ul>
    <ul id="R_7.085.006">
        <li id="R_7.085.006ab">hṛṣṭā ṛṣigaṇās tatra pārthivāś ca mahaujasaḥ</li>
        <li id="R_7.085.006cd">pibanta iva cakṣurbhyāṃ rājānaṃ gāyakau ca tau</li>
    </ul>
    <ul id="R_7.085.007">
        <li id="R_7.085.007ab">parasparam athocus te sarva eva samaṃ tataḥ</li>
        <li id="R_7.085.007cd">ubhau rāmasya sadṛśau bimbād bimbam ivoddhṛtau</li>
    </ul>
    <ul id="R_7.085.008">
        <li id="R_7.085.008ab">jaṭilau yadi na syātāṃ na valkaladharau yadi</li>
        <li id="R_7.085.008cd">viśeṣaṃ nādhigacchāmo gāyato rāghavasya ca</li>
    </ul>
    <ul id="R_7.085.009">
        <li id="R_7.085.009ab">teṣāṃ saṃvadatām evaṃ śrotṝṇāṃ harṣavardhanam</li>
        <li id="R_7.085.009cd">geyaṃ pracakratus tatra tāv ubhau munidārakau</li>
    </ul>
    <ul id="R_7.085.010">
        <li id="R_7.085.010ab">tataḥ pravṛttaṃ madhuraṃ gāndharvam atimānuṣam</li>
        <li id="R_7.085.010cd">na ca tṛptiṃ yayuḥ sarve śrotāro geya saṃpadā</li>
    </ul>
    <ul id="R_7.085.011">
        <li id="R_7.085.011ab">pravṛttam āditaḥ pūrvaṃ sargān nāradadarśanāt</li>
        <li id="R_7.085.011cd">tataḥ prabhṛti sargāṃś ca yāvadviṃśaty agāyatām</li>
    </ul>
    <ul id="R_7.085.012">
        <li id="R_7.085.012ab">tato 'parāhṇasamaye rāghavaḥ samabhāṣata</li>
        <li id="R_7.085.012cd">śrutvā viṃśatisargāṃs tān bharataṃ bhrātṛvatsalaḥ</li>
    </ul>
    <ul id="R_7.085.013">
        <li id="R_7.085.013ab">aṣṭādaśa sahasrāṇi suvarṇasya mahātmanoḥ</li>
        <li id="R_7.085.013cd">dadasva śīghraṃ kākutstha bālayor mā vṛthā śramaḥ</li>
    </ul>
    <ul id="R_7.085.014">
        <li id="R_7.085.014ab">dīyamānaṃ suvarṇaṃ tan nāgṛhṇītāṃ kuśīlavau</li>
        <li id="R_7.085.014cd">ūcatuś ca mahātmānau kim aneneti vismitau</li>
    </ul>
    <ul id="R_7.085.015">
        <li id="R_7.085.015ab">vanyena phalamūlena niratu svo vanaukasau</li>
        <li id="R_7.085.015cd">suvarṇena hiraṇyena kiṃ kariṣyāvahe vane</li>
    </ul>
    <ul id="R_7.085.016">
        <li id="R_7.085.016ab">tathā tayoḥ prabruvatoḥ kautūhalasamanvitāḥ</li>
        <li id="R_7.085.016cd">śrotāraś caiva rāmaś ca sarva eva suvismitāḥ</li>
    </ul>
    <ul id="R_7.085.017">
        <li id="R_7.085.017ab">tasya caivāgamaṃ rāmaḥ kāvyasya śrotum utsukaḥ</li>
        <li id="R_7.085.017cd">papraccha tau mahātejās tāv ubhau munidārakau</li>
    </ul>
    <ul id="R_7.085.018">
        <li id="R_7.085.018ab">kiṃpramāṇam idaṃ kāvyaṃ kā pratiṣṭhā mahātmanaḥ</li>
        <li id="R_7.085.018cd">kartā kāvyasya mahataḥ ko vāsau munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.085.019">
        <li id="R_7.085.019ab">pṛcchantaṃ rāghavaṃ vākyam ūcatur munidārakau</li>
        <li id="R_7.085.019cd">vālmīkir bhagavān kartā saṃprāpto yajñasaṃnidhim</li>
        <li id="R_7.085.019ef">yenedaṃ caritaṃ tubhyam aśeṣaṃ saṃpradarśitam</li>
    </ul>
    <ul id="R_7.085.020">
        <li id="R_7.085.020ab">ādiprabhṛti rājendra pañcasarga śatāni ca</li>
        <li id="R_7.085.020cd">pratiṣṭhā jīvitaṃ yāvat tāvad rājañ śubhāśubham</li>
    </ul>
    <ul id="R_7.085.021">
        <li id="R_7.085.021ab">yadi buddhiḥ kṛtā rājañ śravaṇāya mahāratha</li>
        <li id="R_7.085.021cd">karmāntare kṣaṇī hūtas tac chṛṇuṣva sahānujaḥ</li>
    </ul>
    <ul id="R_7.085.022">
        <li id="R_7.085.022ab">bāḍham ity abravīd rāmas tau cānujñāpya rāghavam</li>
        <li id="R_7.085.022cd">prahṛṣṭau jagmatur vāsaṃ yatrāsau munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.085.023">
        <li id="R_7.085.023ab">rāmo 'pi munibhiḥ sārdhaṃ pārthivaiś ca mahātmabhiḥ</li>
        <li id="R_7.085.023cd">śrutvā tad gītamādhuryaṃ karmaśālām upāgamat</li>
    </ul>
    <ul id="R_7.086.001">
        <li id="R_7.086.001ab">rāmo bahūny ahāny eva tad gītaṃ paramādbhutam</li>
        <li id="R_7.086.001cd">śuśrāva munibhiḥ sārdhaṃ rājabhiḥ saha vānaraiḥ</li>
    </ul>
    <ul id="R_7.086.002">
        <li id="R_7.086.002ab">tasmin gīte tu vijñāya sītāputrau kuśīlavau</li>
        <li id="R_7.086.002cd">tasyāḥ pariṣado madhye rāmo vacanam abravīt</li>
    </ul>
    <ul id="R_7.086.003">
        <li id="R_7.086.003ab">madvaco brūta gacchadhvam iti bhagavato 'ntikam</li>
    </ul>
    <ul id="R_7.086.004">
        <li id="R_7.086.004ab">yadi śuddhasamācārā yadi vā vītakalmaṣā</li>
        <li id="R_7.086.004cd">karotv ihātmanaḥ śuddhim anumānya mahāmunim</li>
    </ul>
    <ul id="R_7.086.005">
        <li id="R_7.086.005ab">chandaṃ munes tu vijñāya sītāyāś ca manogatam</li>
        <li id="R_7.086.005cd">pratyayaṃ dātukāmāyās tataḥ śaṃsata me laghu</li>
    </ul>
    <ul id="R_7.086.006">
        <li id="R_7.086.006ab">śvaḥ prabhāte tu śapathaṃ maithilī janakātmajā</li>
        <li id="R_7.086.006cd">karotu pariṣanmadhye śodhanārthaṃ mameha ca</li>
    </ul>
    <ul id="R_7.086.007">
        <li id="R_7.086.007ab">śrutvā tu rāghavasyaitad vacaḥ paramam adbhutam</li>
        <li id="R_7.086.007cd">dūtāḥ saṃprayayur vāṭaṃ yatrāste munipuṃgavaḥ</li>
    </ul>
    <ul id="R_7.086.008">
        <li id="R_7.086.008ab">te praṇamya mahātmānaṃ jvalantam amitaprabham</li>
        <li id="R_7.086.008cd">ūcus te rāma vākyāni mṛdūni madhurāṇi ca</li>
    </ul>
    <ul id="R_7.086.009">
        <li id="R_7.086.009ab">teṣāṃ tad bhāṣitaṃ śrutvā rāmasya ca manogatam</li>
        <li id="R_7.086.009cd">vijñāya sumahātejā munir vākyam athābravīt</li>
    </ul>
    <ul id="R_7.086.010">
        <li id="R_7.086.010ab">evaṃ bhavatu bhadraṃ vo yathā tuṣyati rāghavaḥ</li>
        <li id="R_7.086.010cd">tathā kariṣyate sītā daivataṃ hi patiḥ striyāḥ</li>
    </ul>
    <ul id="R_7.086.011">
        <li id="R_7.086.011ab">tathoktā muninā sarve rāmadūtā mahaujasaḥ</li>
        <li id="R_7.086.011cd">pratyetya rāghavaṃ sarve munivākyaṃ babhāṣire</li>
    </ul>
    <ul id="R_7.086.012">
        <li id="R_7.086.012ab">tataḥ prahṛṣṭaḥ kākutsthaḥ śrutvā vākyaṃ mahātmanaḥ</li>
        <li id="R_7.086.012cd">ṛṣīṃs tatra sametāṃś ca rājñaś caivābhyabhāṣata</li>
    </ul>
    <ul id="R_7.086.013">
        <li id="R_7.086.013ab">bhagavantaḥ saśiṣyā vai sānugaś ca narādhipāḥ</li>
        <li id="R_7.086.013cd">paśyantu sītāśapathaṃ yaś caivānyo 'bhikāṅkṣate</li>
    </ul>
    <ul id="R_7.086.014">
        <li id="R_7.086.014ab">tasya tad vacanaṃ śrutvā rāghavasya mahātmanaḥ</li>
        <li id="R_7.086.014cd">sarveṣam ṛṣimukhyānāṃ sādhuvādo mahān abhūt</li>
    </ul>
    <ul id="R_7.086.015">
        <li id="R_7.086.015ab">rājānaś ca mahātmānaḥ praśaṃsanti sma rāghavam</li>
        <li id="R_7.086.015cd">upapannaṃ naraśreṣṭha tvayy eva bhuvi nānyataḥ</li>
    </ul>
    <ul id="R_7.086.016">
        <li id="R_7.086.016ab">evaṃ viniścayaṃ kṛtvā śvobhūta iti rāghavaḥ</li>
        <li id="R_7.086.016cd">visarjayām āsa tadā sarvāṃs tāñ śatrusūdanaḥ</li>
    </ul>
    <ul id="R_7.087.001">
        <li id="R_7.087.001ab">tasyāṃ rajanyāṃ vyuṣṭāyāṃ yajñavāṭagato nṛpaḥ</li>
        <li id="R_7.087.001cd">ṛṣīn sarvān mahātejāḥ śabdāpayati rāghavaḥ</li>
    </ul>
    <ul id="R_7.087.002">
        <li id="R_7.087.002ab">vasiṣṭho vāmadevaś ca jābālir atha kāśyapaḥ</li>
        <li id="R_7.087.002cd">viśvāmitro dīrghatapā durvāsāś ca mahātapāḥ</li>
    </ul>
    <ul id="R_7.087.003">
        <li id="R_7.087.003ab">agastyo 'tha tathāśaktir bhārgavaś caiva vāmanaḥ</li>
        <li id="R_7.087.003cd">mārkaṇḍeyaś ca dīrghāyur maudgalyaś ca mahātapāḥ</li>
    </ul>
    <ul id="R_7.087.004">
        <li id="R_7.087.004ab">bhārgavaś cyavanaś caiva śatānandaś ca dharmavit</li>
        <li id="R_7.087.004cd">bharadvājaś ca tejasvī agniputraś ca suprabhaḥ</li>
    </ul>
    <ul id="R_7.087.005">
        <li id="R_7.087.005ab">ete cānye ca munayo bahavaḥ saṃśitavratāḥ</li>
        <li id="R_7.087.005cd">rājānaś ca naravyāghrāḥ sarva eva samāgatāḥ</li>
    </ul>
    <ul id="R_7.087.006">
        <li id="R_7.087.006ab">rākṣasāś ca mahāvīryā vānarāś ca mahābalāḥ</li>
        <li id="R_7.087.006cd">samājagmur mahātmānaḥ sarva eva kutūhalāt</li>
    </ul>
    <ul id="R_7.087.007">
        <li id="R_7.087.007ab">kṣatriyāś caiva vaiśyāś ca śūdrāś caiva sahasraśaḥ</li>
        <li id="R_7.087.007cd">sītāśapathavīkṣārthaṃ sarva eva samāgatāḥ</li>
    </ul>
    <ul id="R_7.087.008">
        <li id="R_7.087.008ab">tathā samāgataṃ sarvam aśvabhūtam ivācalam</li>
        <li id="R_7.087.008cd">śrutvā munivaras tūrṇaṃ sasītaḥ samupāgamat</li>
    </ul>
    <ul id="R_7.087.009">
        <li id="R_7.087.009ab">tam ṛṣiṃ pṛṣṭhataḥ sītā sānvagacchad avāṅmukhī</li>
        <li id="R_7.087.009cd">kṛtāñjalir bāṣpagalā kṛtvā rāmaṃ manogatam</li>
    </ul>
    <ul id="R_7.087.010">
        <li id="R_7.087.010ab">tāṃ dṛṣṭvā śrīm ivāyāntīṃ brahmāṇam anugāminīm</li>
        <li id="R_7.087.010cd">vālmīkeḥ pṛṣṭhataḥ sītāṃ sādhukāro mahān abhūt</li>
    </ul>
    <ul id="R_7.087.011">
        <li id="R_7.087.011ab">tato halahalā śabdaḥ sarveṣām evam ābabhau</li>
        <li id="R_7.087.011cd">duḥkhajena viśālena śokenākulitātmanām</li>
    </ul>
    <ul id="R_7.087.012">
        <li id="R_7.087.012ab">sādhu sīteti ke cit tu sādhu rāmeti cāpare</li>
        <li id="R_7.087.012cd">ubhāv eva tu tatrānye sādhu sādhv iti cābruvan</li>
    </ul>
    <ul id="R_7.087.013">
        <li id="R_7.087.013ab">tato madhyaṃ janaughānāṃ praviśya munipuṃgavaḥ</li>
        <li id="R_7.087.013cd">sītāsahāyo vālmīkir iti hovāca rāghavam</li>
    </ul>
    <ul id="R_7.087.014">
        <li id="R_7.087.014ab">iyaṃ dāśarathe sītā suvratā dharmacāriṇī</li>
        <li id="R_7.087.014cd">apāpā te parityaktā mamāśramasamīpataḥ</li>
    </ul>
    <ul id="R_7.087.015">
        <li id="R_7.087.015ab">lokāpavādabhītasya tava rāma mahāvrata</li>
        <li id="R_7.087.015cd">pratyayaṃ dāsyate sītā tām anujñātum arhasi</li>
    </ul>
    <ul id="R_7.087.016">
        <li id="R_7.087.016ab">imau ca jānakī putrāv ubhau ca yamajātakau</li>
        <li id="R_7.087.016cd">sutau tavaiva durdharṣo satyam etad bravīmi te</li>
    </ul>
    <ul id="R_7.087.017">
        <li id="R_7.087.017ab">pracetaso 'haṃ daśamaḥ putro rāghavanandana</li>
        <li id="R_7.087.017cd">na smarāmy anṛtaṃ vākyaṃ tathemau tava putrakau</li>
    </ul>
    <ul id="R_7.087.018">
        <li id="R_7.087.018ab">bahuvarṣasahasrāṇi tapaścaryā mayā kṛtā</li>
        <li id="R_7.087.018cd">tasyāḥ phalam upāśnīyām apāpā maithilī yathā</li>
    </ul>
    <ul id="R_7.087.019">
        <li id="R_7.087.019ab">ahaṃ pañcasu bhūteṣu manaḥṣaṣṭheṣu rāghava</li>
        <li id="R_7.087.019cd">vicintya sītāṃ śuddheti nyagṛhṇāṃ vananirjhare</li>
    </ul>
    <ul id="R_7.087.020">
        <li id="R_7.087.020ab">iyaṃ śuddhasamācārā apāpā patidevatā</li>
        <li id="R_7.087.020cd">lokāpavādabhītasya dāsyati pratyayaṃ tava</li>
    </ul>
    <ul id="R_7.088.001">
        <li id="R_7.088.001ab">vālmīkinaivam uktas tu rāghavaḥ pratyabhāṣata</li>
        <li id="R_7.088.001cd">prāñjalir jagato madhye dṛṣṭvā tāṃ devavarṇinīm</li>
    </ul>
    <ul id="R_7.088.002">
        <li id="R_7.088.002ab">evam etan mahābhāga yathā vadasi dharmavit</li>
        <li id="R_7.088.002cd">pratyayo hi mama brahmaṃs tava vākyair akalmaṣaiḥ</li>
    </ul>
    <ul id="R_7.088.003">
        <li id="R_7.088.003ab">pratyayo hi purā datto vaidehyā surasaṃnidhau</li>
        <li id="R_7.088.003cd">seyaṃ lokabhayād brahmann apāpety abhijānatā</li>
        <li id="R_7.088.003ef">parityaktā mayā sītā tad bhavān kṣantum arhati</li>
    </ul>
    <ul id="R_7.088.004">
        <li id="R_7.088.004ab">jānāmi cemau putrau me yamajātau kuśīlavau</li>
        <li id="R_7.088.004cd">śuddhāyāṃ jagato madhye maithilyāṃ prītir astu me</li>
    </ul>
    <ul id="R_7.088.005">
        <li id="R_7.088.005ab">abhiprāyaṃ tu vijñāya rāmasya surasattamāḥ</li>
        <li id="R_7.088.005cd">pitāmahaṃ puraskṛtya sarva eva samāgatāḥ</li>
    </ul>
    <ul id="R_7.088.006">
        <li id="R_7.088.006ab">ādityā vasavo rudrā viśve deśā marudgaṇāḥ</li>
        <li id="R_7.088.006cd">aśvināv ṛṣigandharvā apsarāṇāṃ gaṇās tathā</li>
        <li id="R_7.088.006ef">sādhyāś ca devāḥ sarve te sarve ca paramarṣayaḥ</li>
    </ul>
    <ul id="R_7.088.007">
        <li id="R_7.088.007ab">tato vāyuḥ śubhaḥ puṇyo divyagandho manoramaḥ</li>
        <li id="R_7.088.007cd">taṃ janaughaṃ suraśreṣṭho hlādayām āsa sarvataḥ</li>
    </ul>
    <ul id="R_7.088.008">
        <li id="R_7.088.008ab">tad adbhutam ivācintyaṃ nirīkṣante samāhitāḥ</li>
        <li id="R_7.088.008cd">mānavāḥ sarvarāṣṭrebhyaḥ pūrvaṃ kṛtayuge yathā</li>
    </ul>
    <ul id="R_7.088.009">
        <li id="R_7.088.009ab">sarvān samāgatān dṛṣṭvā sītā kāṣāyavāsinī</li>
        <li id="R_7.088.009cd">abravīt prāñjalir vākyam adhodṛṣṭir avānmukhī</li>
    </ul>
    <ul id="R_7.088.010">
        <li id="R_7.088.010ab">yathāhaṃ rāghavād anyaṃ manasāpi na cintaye</li>
        <li id="R_7.088.010cd">tathā me mādhavī devī vivaraṃ dātum arhati</li>
    </ul>
    <ul id="R_7.088.011">
        <li id="R_7.088.011ab">tathā śapantyāṃ vaidehyāṃ prādurāsīt tad adbhutam</li>
        <li id="R_7.088.011cd">bhūtalād utthitaṃ divyaṃ siṃhāsanam anuttamam</li>
    </ul>
    <ul id="R_7.088.012">
        <li id="R_7.088.012ab">dhriyamāṇaṃ śirobhis tan nāgair amitavikramaiḥ</li>
        <li id="R_7.088.012cd">divyaṃ divyena vapuṣā sarvaratnavibhūṣitam</li>
    </ul>
    <ul id="R_7.088.013">
        <li id="R_7.088.013ab">tasmiṃs tu dharaṇī devī bāhubhyāṃ gṛhya maithilīm</li>
        <li id="R_7.088.013cd">svāgatenābhinandyainām āsane copaveṣayat</li>
    </ul>
    <ul id="R_7.088.014">
        <li id="R_7.088.014ab">tām āsanagatāṃ dṛṣṭvā praviśantīṃ rasātalam</li>
        <li id="R_7.088.014cd">puṇyavṛṣṭir avicchinnā divyā sītām avākirat</li>
    </ul>
    <ul id="R_7.088.015">
        <li id="R_7.088.015ab">sādhukāraś ca sumahān devānāṃ sahasotthitaḥ</li>
        <li id="R_7.088.015cd">sādhu sādhv iti vai sīte yasyās te śīlam īdṛśam</li>
    </ul>
    <ul id="R_7.088.016">
        <li id="R_7.088.016ab">evaṃ bahuvidhā vāco hy antarikṣagatāḥ surāḥ</li>
        <li id="R_7.088.016cd">vyājahrur hṛṣṭamanaso dṛṣṭvā sītāpraveśanam</li>
    </ul>
    <ul id="R_7.088.017">
        <li id="R_7.088.017ab">yajñavāṭagatāś cāpi munayaḥ sarva eva te</li>
        <li id="R_7.088.017cd">rājānaś ca naravyāghrā vismayān noparemire</li>
    </ul>
    <ul id="R_7.088.018">
        <li id="R_7.088.018ab">antarikṣe ca bhūmau ca sarve sthāvarajaṅgamāḥ</li>
        <li id="R_7.088.018cd">dānavāś ca mahākāyāḥ pātāle pannagādhipāḥ</li>
    </ul>
    <ul id="R_7.088.019">
        <li id="R_7.088.019ab">ke cid vineduḥ saṃhṛṣṭāḥ ke cid dhyānaparāyaṇāḥ</li>
        <li id="R_7.088.019cd">ke cid rāmaṃ nirīkṣante ke cit sītām acetanāḥ</li>
    </ul>
    <ul id="R_7.088.020">
        <li id="R_7.088.020ab">sītāpraveśanaṃ dṛṣṭvā teṣām āsīt samāgamaḥ</li>
        <li id="R_7.088.020cd">taṃ muhūrtam ivātyarthaṃ sarvaṃ saṃmohitaṃ jagat</li>
    </ul>
    <ul id="R_7.089.001">
        <li id="R_7.089.001ab">tadāvasāne yajñasya rāmaḥ paramadurmanāḥ</li>
        <li id="R_7.089.001cd">apaśyamāno vaidehīṃ mene śūnyam idaṃ jagat</li>
        <li id="R_7.089.001ef">śokena paramāyatto na śāntiṃ manasāgamat</li>
    </ul>
    <ul id="R_7.089.002">
        <li id="R_7.089.002ab">visṛjya pārthivān sarvān ṛkṣavānararākṣasān</li>
        <li id="R_7.089.002cd">janaughaṃ brahmamukhyānāṃ vittapūrṇaṃ vyasarjayat</li>
    </ul>
    <ul id="R_7.089.003">
        <li id="R_7.089.003ab">tato visṛjya tān sarvān rāmo rājīvalocanaḥ</li>
        <li id="R_7.089.003cd">hṛdi kṛtvā tadā sītām ayodhyāṃ praviveśa saḥ</li>
    </ul>
    <ul id="R_7.089.004">
        <li id="R_7.089.004ab">na sītāyāḥ parāṃ bhāryāṃ vavre sa raghunandanaḥ</li>
        <li id="R_7.089.004cd">yajñe yajñe ca patnyarthaṃ jānakī kāñcanī bhavat</li>
    </ul>
    <ul id="R_7.089.005">
        <li id="R_7.089.005ab">daśavarṣasahasrāṇi vājimedham upākarot</li>
        <li id="R_7.089.005cd">vājapeyān daśaguṇāṃs tathā bahusuvarṇakān</li>
    </ul>
    <ul id="R_7.089.006">
        <li id="R_7.089.006ab">agniṣṭomātirātrābhyāṃ gosavaiś ca mahādhanaiḥ</li>
        <li id="R_7.089.006cd">īje kratubhir anyaiś ca sa śrīmān āptadakṣiṇaiḥ</li>
    </ul>
    <ul id="R_7.089.007">
        <li id="R_7.089.007ab">evaṃ sa kālaḥ sumahān rājyasthasya mahātmanaḥ</li>
        <li id="R_7.089.007cd">dharme prayatamānasya vyatīyād rāghavasya tu</li>
    </ul>
    <ul id="R_7.089.008">
        <li id="R_7.089.008ab">ṛkṣavānararakṣāṃsi sthitā rāmasya śāsane</li>
        <li id="R_7.089.008cd">anurajyanti rājāno ahany ahani rāghavam</li>
    </ul>
    <ul id="R_7.089.009">
        <li id="R_7.089.009ab">kāle varṣati parjanyaḥ subhikṣaṃ vimalā diśaḥ</li>
        <li id="R_7.089.009cd">hṛṣṭapuṣṭajanākīrṇaṃ puraṃ janapadas tathā</li>
    </ul>
    <ul id="R_7.089.010">
        <li id="R_7.089.010ab">nākāle mriyate kaś cin na vyādhiḥ prāṇināṃ tadā</li>
        <li id="R_7.089.010cd">nādharmaś cābhavat kaś cid rāme rājyaṃ praśāsati</li>
    </ul>
    <ul id="R_7.089.011">
        <li id="R_7.089.011ab">atha dīrghasya kālasya rāmamātā yaśasvinī</li>
        <li id="R_7.089.011cd">putrapautraiḥ parivṛtā kāladharmam upāgamat</li>
    </ul>
    <ul id="R_7.089.012">
        <li id="R_7.089.012ab">anviyāya sumitrāpi kaikeyī ca yaśasvinī</li>
        <li id="R_7.089.012cd">dharmaṃ kṛtvā bahuvidhaṃ tridive paryavasthitā</li>
    </ul>
    <ul id="R_7.089.013">
        <li id="R_7.089.013ab">sarvāḥ pratiṣṭhitāḥ svarge rājñā daśarathena ca</li>
        <li id="R_7.089.013cd">samāgatā mahābhāgāḥ saha dharmaṃ ca lebhire</li>
    </ul>
    <ul id="R_7.089.014">
        <li id="R_7.089.014ab">tāsāṃ rāmo mahādānaṃ kāle kāle prayacchati</li>
        <li id="R_7.089.014cd">mātṝṇām aviśeṣeṇa brāhmaṇeṣu tapasviṣu</li>
    </ul>
    <ul id="R_7.089.015">
        <li id="R_7.089.015ab">pitryāṇi bahuratnāni yajñān paramadustarān</li>
        <li id="R_7.089.015cd">cakāra rāmo dharmātmā pitṝn devān vivardhayan</li>
    </ul>
    <ul id="R_7.090.001">
        <li id="R_7.090.001ab">kasya cit tv atha kālasya yudhājit kekayo nṛpaḥ</li>
        <li id="R_7.090.001cd">svaguruṃ preṣayām āsa rāghavāya mahātmane</li>
    </ul>
    <ul id="R_7.090.002">
        <li id="R_7.090.002ab">gārgyam aṅgirasaḥ putraṃ brahmarṣim amitaprabham</li>
        <li id="R_7.090.002cd">daśa cāśvasahasrāṇi prītidānam anuttamam</li>
    </ul>
    <ul id="R_7.090.003">
        <li id="R_7.090.003ab">kambalāni ca ratnāni citravastram athottamam</li>
        <li id="R_7.090.003cd">rāmāya pradadau rājā bahūny ābharaṇāni ca</li>
    </ul>
    <ul id="R_7.090.004">
        <li id="R_7.090.004ab">śrutvā tu rāghavo gārgyaṃ maharṣiṃ samupāgatam</li>
        <li id="R_7.090.004cd">mātulasyāśvapatinaḥ priyaṃ dūtam upāgatam</li>
    </ul>
    <ul id="R_7.090.005">
        <li id="R_7.090.005ab">pratyudgamya ca kākutsthaḥ krośamātraṃ sahānugaḥ</li>
        <li id="R_7.090.005cd">gārgyaṃ saṃpūjayām āsa dhanaṃ tat pratigṛhya ca</li>
    </ul>
    <ul id="R_7.090.006">
        <li id="R_7.090.006ab">pṛṣṭvā ca prītidaṃ sarvaṃ kuśalaṃ mātulasya ca</li>
        <li id="R_7.090.006cd">upaviṣṭaṃ mahābhāgaṃ rāmaḥ praṣṭuṃ pracakrame</li>
    </ul>
    <ul id="R_7.090.007">
        <li id="R_7.090.007ab">kim āha matulo vākyaṃ yadarthaṃ bhagavān iha</li>
        <li id="R_7.090.007cd">prāpto vākyavidāṃ śreṣṭha sākṣād iva bṛhaspatiḥ</li>
    </ul>
    <ul id="R_7.090.008">
        <li id="R_7.090.008ab">rāmasya bhāṣitaṃ śrutvā brahmarṣiḥ kāryavistaram</li>
        <li id="R_7.090.008cd">vaktum adbhutasaṃkāśaṃ rāghavāyopacakrame</li>
    </ul>
    <ul id="R_7.090.009">
        <li id="R_7.090.009ab">mātulas te mahābāho vākyam āha nararṣabha</li>
        <li id="R_7.090.009cd">yudhājit prītisaṃyuktaṃ śrūyatāṃ yadi rocate</li>
    </ul>
    <ul id="R_7.090.010">
        <li id="R_7.090.010ab">ayaṃ gandharvaviṣayaḥ phalamūlopaśobhitaḥ</li>
        <li id="R_7.090.010cd">sindhor ubhayataḥ pārśve deśaḥ paramaśobhanaḥ</li>
    </ul>
    <ul id="R_7.090.011">
        <li id="R_7.090.011ab">taṃ ca rakṣanti gandharvāḥ sāyudhā yuddhakovidāḥ</li>
        <li id="R_7.090.011cd">śailūṣasya sutā vīrās tisraḥ koṭyo mahābalāḥ</li>
    </ul>
    <ul id="R_7.090.012">
        <li id="R_7.090.012ab">tān vinirjitya kākutstha gandharvaviṣayaṃ śubham</li>
        <li id="R_7.090.012cd">niveśaya mahābāho dve pure susamāhitaḥ</li>
    </ul>
    <ul id="R_7.090.013">
        <li id="R_7.090.013ab">anyasya na gatis tatra deśaś cāyaṃ suśobhanaḥ</li>
        <li id="R_7.090.013cd">rocatāṃ te mahābāho nāhaṃ tvām anṛtaṃ vade</li>
    </ul>
    <ul id="R_7.090.014">
        <li id="R_7.090.014ab">tac chrutvā rāghavaḥ prīto maharṣer mātulasya ca</li>
        <li id="R_7.090.014cd">uvāca bāḍham ity evaṃ bharataṃ cānvavaikṣata</li>
    </ul>
    <ul id="R_7.090.015">
        <li id="R_7.090.015ab">so 'bravīd rāghavaḥ prītaḥ prāñjalipragraho dvijam</li>
        <li id="R_7.090.015cd">imau kumārau taṃ deśaṃ brahmarṣe vijayiṣyataḥ</li>
    </ul>
    <ul id="R_7.090.016">
        <li id="R_7.090.016ab">bharatasyātmajau vīrau takṣaḥ puṣkala eva ca</li>
        <li id="R_7.090.016cd">mātulena suguptau tau dharmeṇa ca samāhitau</li>
    </ul>
    <ul id="R_7.090.017">
        <li id="R_7.090.017ab">bharataṃ cāgrataḥ kṛtvā kumārau sabalānugau</li>
        <li id="R_7.090.017cd">nihatya gandharvasutān dve pure vibhajiṣyataḥ</li>
    </ul>
    <ul id="R_7.090.018">
        <li id="R_7.090.018ab">niveśya te puravare ātmājau saṃniveśya ca</li>
        <li id="R_7.090.018cd">āgamiṣyati me bhūyaḥ sakāśam atidhārmikaḥ</li>
    </ul>
    <ul id="R_7.090.019">
        <li id="R_7.090.019ab">brahmarṣim evam uktvā tu bharataṃ sabalānugam</li>
        <li id="R_7.090.019cd">ājñāpayām āsa tadā kumārau cābhyaṣecayat</li>
    </ul>
    <ul id="R_7.090.020">
        <li id="R_7.090.020ab">nakṣatreṇa ca saumyena puraskṛtyāṅgiraḥ sutam</li>
        <li id="R_7.090.020cd">bharataḥ saha sainyena kumārābhyāṃ ca niryayau</li>
    </ul>
    <ul id="R_7.090.021">
        <li id="R_7.090.021ab">sā senā śakrayukteva naragān niryayāv atha</li>
        <li id="R_7.090.021cd">rāghavānugatā dūraṃ durādharṣā surāsuraiḥ</li>
    </ul>
    <ul id="R_7.090.022">
        <li id="R_7.090.022ab">māṃsāśīni ca sattvāni rakṣāṃsi sumahānti ca</li>
        <li id="R_7.090.022cd">anujagmuś ca bharataṃ rudhirasya pipāsayā</li>
    </ul>
    <ul id="R_7.090.023">
        <li id="R_7.090.023ab">bhūtagrāmāś ca bahavo māṃsabhakṣāḥ sudāruṇāḥ</li>
        <li id="R_7.090.023cd">gandharvaputramāṃsāni bhoktukāmāḥ sahasraśaḥ</li>
    </ul>
    <ul id="R_7.090.024">
        <li id="R_7.090.024ab">siṃhavyāghrasṛgālānāṃ khecarāṇāṃ ca pakṣiṇām</li>
        <li id="R_7.090.024cd">bahūni vai sahasrāṇi senāyā yayur agrataḥ</li>
    </ul>
    <ul id="R_7.090.025">
        <li id="R_7.090.025ab">adhyardhamāsam uṣitā pathi senā nirāmayā</li>
        <li id="R_7.090.025cd">hṛṣṭapuṣṭajanākīrṇā kekayaṃ samupāgamat</li>
    </ul>
    <ul id="R_7.091.001">
        <li id="R_7.091.001ab">śrutvā senāpatiṃ prāptaṃ bharataṃ kekayādhipaḥ</li>
        <li id="R_7.091.001cd">yudhājid gārgyasahitaṃ parāṃ prītim upāgamat</li>
    </ul>
    <ul id="R_7.091.002">
        <li id="R_7.091.002ab">sa niryayau janaughena mahatā kekayādhipaḥ</li>
        <li id="R_7.091.002cd">tvaramāṇo 'bhicakrāma gandharvān devarūpiṇaḥ</li>
    </ul>
    <ul id="R_7.091.003">
        <li id="R_7.091.003ab">bharataś ca yudhājic ca sametau laghuvikramau</li>
        <li id="R_7.091.003cd">gandharvanagaraṃ prāptau sabalau sapadānugau</li>
    </ul>
    <ul id="R_7.091.004">
        <li id="R_7.091.004ab">śrutvā tu bharataṃ prāptaṃ gandharvās te samāgatāḥ</li>
        <li id="R_7.091.004cd">yoddhukāmā mahāvīryā vinadantaḥ samantataḥ</li>
    </ul>
    <ul id="R_7.091.005">
        <li id="R_7.091.005ab">tataḥ samabhavad yuddhaṃ tumulaṃ lomaharṣaṇam</li>
        <li id="R_7.091.005cd">saptarātraṃ mahābhīmaṃ na cānyatarayor jayaḥ</li>
    </ul>
    <ul id="R_7.091.006">
        <li id="R_7.091.006ab">tato rāmānujaḥ kruddhaḥ kālasyāstraṃ sudāruṇam</li>
        <li id="R_7.091.006cd">saṃvartaṃ nāma bharato gandharveṣv abhyayojayat</li>
    </ul>
    <ul id="R_7.091.007">
        <li id="R_7.091.007ab">te baddhāḥ kālapāśena saṃvartena vidāritāḥ</li>
        <li id="R_7.091.007cd">kṣaṇenābhihatās tisras tatra koṭyo mahātmanā</li>
    </ul>
    <ul id="R_7.091.008">
        <li id="R_7.091.008ab">taṃ ghātaṃ ghorasaṃkāśaṃ na smaranti divaukasaḥ</li>
        <li id="R_7.091.008cd">nimeṣāntaramātreṇa tādṛśānāṃ mahātmanām</li>
    </ul>
    <ul id="R_7.091.009">
        <li id="R_7.091.009ab">hateṣu teṣu vīreṣu bharataḥ kaikayīsutaḥ</li>
        <li id="R_7.091.009cd">niveśayām āsa tadā samṛddhe dve purottame</li>
        <li id="R_7.091.009ef">takṣaṃ takṣaśilāyāṃ tu puṣkaraṃ puṣkarāvatau</li>
    </ul>
    <ul id="R_7.091.010">
        <li id="R_7.091.010ab">gandharvadeśo ruciro gāndhāraviṣayaś ca saḥ</li>
        <li id="R_7.091.010cd">varṣaiḥ pañcabhir ākīrṇo viṣayair nāgarais tathā</li>
    </ul>
    <ul id="R_7.091.011">
        <li id="R_7.091.011ab">dhanaratnaughasaṃpūrṇo kānanair upaśobhite</li>
        <li id="R_7.091.011cd">anyonyasaṃgharṣakṛte spardhayā guṇavistare</li>
    </ul>
    <ul id="R_7.091.012">
        <li id="R_7.091.012ab">ubhe suruciraprakhye vyavahārair akalmaṣaiḥ</li>
        <li id="R_7.091.012cd">udyānayānaughavṛte suvibhaktāntarāpaṇe</li>
    </ul>
    <ul id="R_7.091.013">
        <li id="R_7.091.013ab">ubhe puravare ramye vistarair upaśobhite</li>
        <li id="R_7.091.013cd">gṛhamukhyaiḥ surucirair vimānaiḥ samavarṇibhiḥ</li>
    </ul>
    <ul id="R_7.091.014">
        <li id="R_7.091.014ab">śobhite śobhanīyaiś ca devāyatanavistaraiḥ</li>
        <li id="R_7.091.014cd">niveśya pañcabhir varṣair bharato rāghavānujaḥ</li>
        <li id="R_7.091.014ef">punar āyān mahābāhur ayodhyāṃ kaikayīsutaḥ</li>
    </ul>
    <ul id="R_7.091.015">
        <li id="R_7.091.015ab">so 'bhivādya mahātmānaṃ sākṣād dharmam ivāparam</li>
        <li id="R_7.091.015cd">rāghavaṃ bharataḥ śrīmān brahmāṇam iva vāsavaḥ</li>
    </ul>
    <ul id="R_7.091.016">
        <li id="R_7.091.016ab">śaśaṃsa ca yathāvṛttaṃ gandharvavadham uttamam</li>
        <li id="R_7.091.016cd">niveśanaṃ ca deśasya śrutvā prīto 'sya rāghavaḥ</li>
    </ul>
    <ul id="R_7.092.001">
        <li id="R_7.092.001ab">tac chrutvā harṣam āpede rāghavo bhrātṛbhiḥ saha</li>
        <li id="R_7.092.001cd">vākyaṃ cādbhutasaṃkāśaṃ bhrātṝn provāca rāghavaḥ</li>
    </ul>
    <ul id="R_7.092.002">
        <li id="R_7.092.002ab">imau kumārau saumitre tava dharmaviśāradau</li>
        <li id="R_7.092.002cd">aṅgadaś candraketuś ca rājyārhau dṛḍhadhanvinau</li>
    </ul>
    <ul id="R_7.092.003">
        <li id="R_7.092.003ab">imau rājye 'bhiṣekṣyāmi deśaḥ sādhu vidhīyatām</li>
        <li id="R_7.092.003cd">ramaṇīyo hy asaṃbādho rametāṃ yatra dhanvinau</li>
    </ul>
    <ul id="R_7.092.004">
        <li id="R_7.092.004ab">na rājñāṃ yatra pīdā syān nāśramāṇāṃ vināśanam</li>
        <li id="R_7.092.004cd">sa deśo dṛśyatāṃ saumya nāparādhyāmahe yathā</li>
    </ul>
    <ul id="R_7.092.005">
        <li id="R_7.092.005ab">tathoktavati rāme tu bharataḥ pratyuvāca ha</li>
        <li id="R_7.092.005cd">ayaṃ kārāpatho deśaḥ suramaṇyo nirāmayaḥ</li>
    </ul>
    <ul id="R_7.092.006">
        <li id="R_7.092.006ab">niveśyatāṃ tatra puram aṅgadasya mahātmanaḥ</li>
        <li id="R_7.092.006cd">candraketoś ca ruciraṃ candrakāntaṃ nirāmayam</li>
    </ul>
    <ul id="R_7.092.007">
        <li id="R_7.092.007ab">tad vākyaṃ bharatenoktaṃ pratijagrāha rāghavaḥ</li>
        <li id="R_7.092.007cd">taṃ ca kṛtā vaśe deśam aṅgadasya nyaveśayat</li>
    </ul>
    <ul id="R_7.092.008">
        <li id="R_7.092.008ab">aṅgadīyā purī ramyā aṅgadasya niveśitā</li>
        <li id="R_7.092.008cd">ramaṇīyā suguptā ca rāmeṇākliṣṭakarmaṇā</li>
    </ul>
    <ul id="R_7.092.009">
        <li id="R_7.092.009ab">candraketus tu mallasya mallabhūmyāṃ niveśitā</li>
        <li id="R_7.092.009cd">candrakānteti vikhyātā divyā svargapurī yathā</li>
    </ul>
    <ul id="R_7.092.010">
        <li id="R_7.092.010ab">tato rāmaḥ parāṃ prītiṃ bharato lakṣmaṇas tathā</li>
        <li id="R_7.092.010cd">yayur yudhi durādharṣā abhiṣekaṃ ca cakrire</li>
    </ul>
    <ul id="R_7.092.011">
        <li id="R_7.092.011ab">abhiṣicya kumārau dvau prasthāpya sabalānugau</li>
        <li id="R_7.092.011cd">aṅgadaṃ paścimā bhūmiṃ candraketum udaṅmukham</li>
    </ul>
    <ul id="R_7.092.012">
        <li id="R_7.092.012ab">aṅgadaṃ cāpi saumitrir lakṣmaṇo 'nujagāma ha</li>
        <li id="R_7.092.012cd">candraketos tu bharataḥ pārṣṇigrāho babhūva ha</li>
    </ul>
    <ul id="R_7.092.013">
        <li id="R_7.092.013ab">lakṣmaṇas tv aṅgadīyāyāṃ saṃvatsaram athoṣitaḥ</li>
        <li id="R_7.092.013cd">putre sthite durādharṣe ayodhyāṃ punar āgamat</li>
    </ul>
    <ul id="R_7.092.014">
        <li id="R_7.092.014ab">bharato 'pi tathaivoṣya saṃvatsaram athādhikam</li>
        <li id="R_7.092.014cd">ayodhyāṃ punar agamya rāmapādāv upāgamat</li>
    </ul>
    <ul id="R_7.092.015">
        <li id="R_7.092.015ab">ubhau saumitribharatau rāmapādāv anuvratau</li>
        <li id="R_7.092.015cd">kālaṃ gatam api snehān na jajñāte 'tidhārmikau</li>
    </ul>
    <ul id="R_7.092.016">
        <li id="R_7.092.016ab">evaṃ varṣasahasrāṇi daśateṣāṃ yayus tadā</li>
        <li id="R_7.092.016cd">dharme prayatamānānāṃ paurakāryeṣu nityadā</li>
    </ul>
    <ul id="R_7.092.017">
        <li id="R_7.092.017ab">
            <seg type="pāda" n="a">vihṛtya lākaṃ paripūrṇamānasāḥ</seg>
            <seg type="pāda" n="b">śriyā vṛtā dharmapathe pare sthitāḥ</seg>
        </li>
        <li id="R_7.092.017cd">
            <seg type="pāda" n="c">trayaḥ samiddhā iva dīptatejasā</seg>
            <seg type="pāda" n="d">hutāgnayaḥ sādhu mahādhvare trayaḥ</seg>
        </li>
    </ul>
    <ul id="R_7.093.001">
        <li id="R_7.093.001ab">kasya cit tv atha kālasya rāme dharmapathe sthite</li>
        <li id="R_7.093.001cd">kālas tāpasarūpeṇa rājadvāram upāgamat</li>
    </ul>
    <ul id="R_7.093.002">
        <li id="R_7.093.002ab">so 'bravīl lakṣmaṇaṃ vākyaṃ dhṛtimantaṃ yaśasvinam</li>
        <li id="R_7.093.002cd">māṃ nivedaya rāmāya saṃprāptaṃ kāryagauravāt</li>
    </ul>
    <ul id="R_7.093.003">
        <li id="R_7.093.003ab">dūto hy atibalasyāhaṃ maharṣer amitaujasaḥ</li>
        <li id="R_7.093.003cd">rāmaṃ didṛkṣur āyātaḥ kāryeṇa hi mahābala</li>
    </ul>
    <ul id="R_7.093.004">
        <li id="R_7.093.004ab">tasya tad vacanaṃ śrutvā saumitris tvarayānvitaḥ</li>
        <li id="R_7.093.004cd">nyavedayata rāmāya tāpasasya vivakṣitam</li>
    </ul>
    <ul id="R_7.093.005">
        <li id="R_7.093.005ab">jayasva rājan dharmeṇa ubhau lokau mahādyute</li>
        <li id="R_7.093.005cd">dūtas tvāṃ draṣṭum āyātas tapasvī bhāskaraprabhaḥ</li>
    </ul>
    <ul id="R_7.093.006">
        <li id="R_7.093.006ab">tad vākyaṃ lakṣmaṇenoktaṃ śrutvā rāma uvāca ha</li>
        <li id="R_7.093.006cd">praveśyatāṃ munis tāta mahaujās tasya vākyadhṛk</li>
    </ul>
    <ul id="R_7.093.007">
        <li id="R_7.093.007ab">saumitris tu tathety uktvā prāveśayata taṃ munim</li>
        <li id="R_7.093.007cd">jvalantam iva tejobhiḥ pradahantam ivāṃśubhiḥ</li>
    </ul>
    <ul id="R_7.093.008">
        <li id="R_7.093.008ab">so 'bhigamya raghuśreṣṭhaṃ dīpyamānaṃ svatejasā</li>
        <li id="R_7.093.008cd">ṛṣir madhurayā vācā vardhasvety āha rāghavam</li>
    </ul>
    <ul id="R_7.093.009">
        <li id="R_7.093.009ab">tasmai rāmo mahātejāḥ pūjām arghya purogamām</li>
        <li id="R_7.093.009cd">dadau kuśalam avyagraṃ praṣṭuṃ caivopacakrame</li>
    </ul>
    <ul id="R_7.093.010">
        <li id="R_7.093.010ab">pṛṣṭhaś ca kuśalaṃ tena rāmeṇa vadatāṃ varaḥ</li>
        <li id="R_7.093.010cd">āsane kāñcane divye niṣasāda mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.093.011">
        <li id="R_7.093.011ab">tam uvāca tato rāmaḥ svāgataṃ te mahāmune</li>
        <li id="R_7.093.011cd">prāpayasva ca vākyāni yato dūtas tvam āgataḥ</li>
    </ul>
    <ul id="R_7.093.012">
        <li id="R_7.093.012ab">codito rājasiṃhena munir vākyam udīrayat</li>
        <li id="R_7.093.012cd">dvandvam etat pravaktavyaṃ na ca cakṣur hataṃ vacaḥ</li>
    </ul>
    <ul id="R_7.093.013">
        <li id="R_7.093.013ab">yaḥ śṛṇoti nirīkṣed vā sa vadhyas tava rāghava</li>
        <li id="R_7.093.013cd">bhaved vai munimukhyasya vacanaṃ yady avekṣase</li>
    </ul>
    <ul id="R_7.093.014">
        <li id="R_7.093.014ab">tatheti ca pratijñāya rāmo lakṣmaṇam abravīt</li>
        <li id="R_7.093.014cd">dvāri tiṣṭha mahābāho pratihāraṃ visarjaya</li>
    </ul>
    <ul id="R_7.093.015">
        <li id="R_7.093.015ab">sa me vadhyaḥ khalu bhavet kathāṃ dvandvasamīritām</li>
        <li id="R_7.093.015cd">ṛṣer mama ca saumitre paśyed vā śṛṇuyā ca yaḥ</li>
    </ul>
    <ul id="R_7.093.016">
        <li id="R_7.093.016ab">tato nikṣipya kākutstho lakṣmaṇaṃ dvārasaṃgrahe</li>
        <li id="R_7.093.016cd">tam uvāca muniṃ vākyaṃ kathayasveti rāghavaḥ</li>
    </ul>
    <ul id="R_7.093.017">
        <li id="R_7.093.017ab">yat te manīṣitaṃ vākyaṃ yena vāsi samāhitaḥ</li>
        <li id="R_7.093.017cd">kathayasva viśaṅkas tvaṃ mamāpi hṛdi vartate</li>
    </ul>
    <ul id="R_7.094.001">
        <li id="R_7.094.001ab">śṛṇu rāma mahābāho yadartham aham āhataḥ</li>
        <li id="R_7.094.001cd">pitāmahena devena preṣito 'smi mahābala</li>
    </ul>
    <ul id="R_7.094.002">
        <li id="R_7.094.002ab">tavāhaṃ pūrvake bhāve putraḥ parapuraṃjaya</li>
        <li id="R_7.094.002cd">māyāsaṃbhāvito vīra kālaḥ sarvasamāharaḥ</li>
    </ul>
    <ul id="R_7.094.003">
        <li id="R_7.094.003ab">pitāmahaś ca bhagavān āha lokapatiḥ prabhuḥ</li>
        <li id="R_7.094.003cd">samayas te mahābāho svarlokān parirakṣitum</li>
    </ul>
    <ul id="R_7.094.004">
        <li id="R_7.094.004ab">saṃkṣipya ca purā lokān māyayā svayam eva hi</li>
        <li id="R_7.094.004cd">mahārṇave śayāno 'psu māṃ tvaṃ pūrvam ajījanaḥ</li>
    </ul>
    <ul id="R_7.094.005">
        <li id="R_7.094.005ab">bhogavantaṃ tato nāgam anantam udake śayam</li>
        <li id="R_7.094.005cd">māyayā janayitvā tvaṃ dvau ca sattvau mahābalau</li>
    </ul>
    <ul id="R_7.094.006">
        <li id="R_7.094.006ab">madhuṃ ca kaiṭabhaṃ caiva yayor asthicayair vṛtā</li>
        <li id="R_7.094.006cd">iyaṃ parvatasaṃbādhā medinī cābhavan mahī</li>
    </ul>
    <ul id="R_7.094.007">
        <li id="R_7.094.007ab">padme divyārkasaṃkāśe nābhyām utpādya mām api</li>
        <li id="R_7.094.007cd">prājāpatyaṃ tvayā karma sarvaṃ mayi niveśitam</li>
    </ul>
    <ul id="R_7.094.008">
        <li id="R_7.094.008ab">so 'haṃ saṃnyastabhāro hi tvām upāse jagatpatim</li>
        <li id="R_7.094.008cd">rakṣāṃ vidhatsva bhūteṣu mama tejaḥ karo bhavān</li>
    </ul>
    <ul id="R_7.094.009">
        <li id="R_7.094.009ab">tatas tvam api durdharṣas tasmād bhāvāt sanātanāt</li>
        <li id="R_7.094.009cd">rakṣārthaṃ sarvabhūtānāṃ viṣṇutvam upajagmivān</li>
    </ul>
    <ul id="R_7.094.010">
        <li id="R_7.094.010ab">adityāṃ vīryavān putro bhrātṝṇāṃ harṣavardhanaḥ</li>
        <li id="R_7.094.010cd">samutpanneṣu kṛtyeṣu lokasāhyāya kalpase</li>
    </ul>
    <ul id="R_7.094.011">
        <li id="R_7.094.011ab">sa tvaṃ vitrāsyamānāsu prajāsu jagatāṃ vara</li>
        <li id="R_7.094.011cd">rāvaṇasya vadhākāṅkṣī mānuṣeṣu mano 'dadhāḥ</li>
    </ul>
    <ul id="R_7.094.012">
        <li id="R_7.094.012ab">daśavarṣasahasrāṇi daśavarṣaśatāni ca</li>
        <li id="R_7.094.012cd">kṛtvā vāsasya niyatiṃ svayam evātmanaḥ purā</li>
    </ul>
    <ul id="R_7.094.013">
        <li id="R_7.094.013ab">sa tvaṃ manomayaḥ putraḥ pūrṇāyur mānuṣeṣv iha</li>
        <li id="R_7.094.013cd">kālo naravaraśreṣṭha samīpam upavartitum</li>
    </ul>
    <ul id="R_7.094.014">
        <li id="R_7.094.014ab">yadi bhūyo mahārāja prajā icchasy upāsitum</li>
        <li id="R_7.094.014cd">vasa vā vīra bhadraṃ te evam āha pitāmahaḥ</li>
    </ul>
    <ul id="R_7.094.015">
        <li id="R_7.094.015ab">atha vā vijigīṣā te suralokāya rāghava</li>
        <li id="R_7.094.015cd">sanāthā viṣṇunā devā bhavantu vigatajvarāḥ</li>
    </ul>
    <ul id="R_7.094.016">
        <li id="R_7.094.016ab">śrutvā pitāmahenoktaṃ vākyaṃ kālasamīritam</li>
        <li id="R_7.094.016cd">rāghavaḥ prahasan vākyaṃ sarvasaṃhāram abravīt</li>
    </ul>
    <ul id="R_7.094.017">
        <li id="R_7.094.017ab">śrutaṃ me devadevasya vākyaṃ paramam adbhutam</li>
        <li id="R_7.094.017cd">prītir hi mahatī jātā tavāgamanasaṃbhavā</li>
    </ul>
    <ul id="R_7.094.018">
        <li id="R_7.094.018ab">bhadraṃ te 'stu gamiṣyāmi yata evāham āgataḥ</li>
        <li id="R_7.094.018cd">hṛd gato hy asi saṃprāpto na me 'sty atra vicāraṇā</li>
    </ul>
    <ul id="R_7.094.019">
        <li id="R_7.094.019ab">mayā hi sarvakṛtyeṣu devānāṃ vaśavartinām</li>
        <li id="R_7.094.019cd">sthātavyaṃ sarvasaṃhāre yathā hy āha pitāmahaḥ</li>
    </ul>
    <ul id="R_7.095.001">
        <li id="R_7.095.001ab">tathā tayoḥ kathayator durvāsā bhagavān ṛṣiḥ</li>
        <li id="R_7.095.001cd">rāmasya darśanākāṅkṣī rājadvāram upāgamat</li>
    </ul>
    <ul id="R_7.095.002">
        <li id="R_7.095.002ab">so 'bhigamya ca saumitrim uvāca ṛṣisattamaḥ</li>
        <li id="R_7.095.002cd">rāmaṃ darśaya me śīghraṃ purā me 'rtho 'tivartate</li>
    </ul>
    <ul id="R_7.095.003">
        <li id="R_7.095.003ab">munes tu bhāṣitaṃ śrutvā lakṣmaṇaḥ paravīrahā</li>
        <li id="R_7.095.003cd">abhivādya mahātmānaṃ vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.095.004">
        <li id="R_7.095.004ab">kiṃ kāryaṃ brūhi bhagavan ko vārthaḥ kiṃ karomy aham</li>
        <li id="R_7.095.004cd">vyagro hi rāghavo brahman muhūrtaṃ vā pratīkṣatām</li>
    </ul>
    <ul id="R_7.095.005">
        <li id="R_7.095.005ab">tac chrutvā ṛṣiśārdūlaḥ krodhena kaluṣīkṛtaḥ</li>
        <li id="R_7.095.005cd">uvāca lakṣmaṇaṃ vākyaṃ nirdahann iva cakṣuṣā</li>
    </ul>
    <ul id="R_7.095.006">
        <li id="R_7.095.006ab">asmin kṣaṇe māṃ saumitre rāmāya prativedaya</li>
        <li id="R_7.095.006cd">viṣayaṃ tvāṃ puraṃ caiva śapiṣye rāghavaṃ tathā</li>
    </ul>
    <ul id="R_7.095.007">
        <li id="R_7.095.007ab">bharataṃ caiva saumitre yuṣmākaṃ yā ca saṃtatiḥ</li>
        <li id="R_7.095.007cd">na hi śakṣyāmy ahaṃ bhūyo manyuṃ dhārayituṃ hṛdi</li>
    </ul>
    <ul id="R_7.095.008">
        <li id="R_7.095.008ab">tac chrutvā ghorasaṃkāśaṃ vākyaṃ tasya mahātmanaḥ</li>
        <li id="R_7.095.008cd">cintayām āsa manasā tasya vākyasya niścayam</li>
    </ul>
    <ul id="R_7.095.009">
        <li id="R_7.095.009ab">ekasya maraṇaṃ me 'stu mā bhūt sarvavināśanam</li>
        <li id="R_7.095.009cd">iti buddhyā viniścitya rāghavāya nyavedayat</li>
    </ul>
    <ul id="R_7.095.010">
        <li id="R_7.095.010ab">lakṣmaṇasya vacaḥ śrutvā rāmaḥ kālaṃ visṛjya ca</li>
        <li id="R_7.095.010cd">niṣpatya tvaritaṃ rājā atreḥ putraṃ dadarśa ha</li>
    </ul>
    <ul id="R_7.095.011">
        <li id="R_7.095.011ab">so 'bhivādya mahātmānaṃ jvalantam iva tejasā</li>
        <li id="R_7.095.011cd">kiṃ kāryam iti kākutsthaḥ kṛtāñjalir abhāṣata</li>
    </ul>
    <ul id="R_7.095.012">
        <li id="R_7.095.012ab">tad vākyaṃ rāghaveṇṇoktaṃ śrutvā munivaraḥ prabhuḥ</li>
        <li id="R_7.095.012cd">pratyāha rāmaṃ durvāsāḥ śrūyatāṃ dharmavatsala</li>
    </ul>
    <ul id="R_7.095.013">
        <li id="R_7.095.013ab">adya varṣasahasrasya samāptir mama rāghava</li>
        <li id="R_7.095.013cd">so 'haṃ bhojanam icchāmi yathāsiddhaṃ tavānagha</li>
    </ul>
    <ul id="R_7.095.014">
        <li id="R_7.095.014ab">tac chrutvā vacanaṃ rāmo harṣeṇa mahatānvitaḥ</li>
        <li id="R_7.095.014cd">bhojanaṃ munimukhyāya yathāsiddham upāharat</li>
    </ul>
    <ul id="R_7.095.015">
        <li id="R_7.095.015ab">sa tu bhuktvā muniśreṣṭhas tad annam amṛtopamam</li>
        <li id="R_7.095.015cd">sādhu rāmeti saṃbhāṣya svam āśramam upāgamat</li>
    </ul>
    <ul id="R_7.095.016">
        <li id="R_7.095.016ab">tasmin gate mahātejā rāghavaḥ prītamānasaḥ</li>
        <li id="R_7.095.016cd">saṃsmṛtya kālavākyāni tato duḥkham upeyivān</li>
    </ul>
    <ul id="R_7.095.017">
        <li id="R_7.095.017ab">duḥkhena ca susaṃtaptaḥ smṛtvā tad ghoradarśanam</li>
        <li id="R_7.095.017cd">avānmukho dīnamanā vyāhartuṃ na śaśāka ha</li>
    </ul>
    <ul id="R_7.095.018">
        <li id="R_7.095.018ab">tato buddhyā viniścitya kālavākyāni rāghavaḥ</li>
        <li id="R_7.095.018cd">naitad astīti coktvā sa tūṣṇīm āsīn mahāyaśāḥ</li>
    </ul>
    <ul id="R_7.096.001">
        <li id="R_7.096.001ab">avāṅmukham atho dīnaṃ dṛṣṭvā somam ivāplutam</li>
        <li id="R_7.096.001cd">rāghavaṃ lakṣmaṇo vākyaṃ hṛṣṭo madhuram abravīt</li>
    </ul>
    <ul id="R_7.096.002">
        <li id="R_7.096.002ab">na saṃtāpaṃ mahābāho madarthaṃ kartum arhasi</li>
        <li id="R_7.096.002cd">pūrvanirmāṇabaddhā hi kālasya gatir īdṛśī</li>
    </ul>
    <ul id="R_7.096.003">
        <li id="R_7.096.003ab">jahi māṃ saumya visrabdaḥ pratijñāṃ paripālaya</li>
        <li id="R_7.096.003cd">hīnapratijñāḥ kākutstha prayānti narakaṃ narāḥ</li>
    </ul>
    <ul id="R_7.096.004">
        <li id="R_7.096.004ab">yadi prītir mahārāja yady anugrāhyatā mayi</li>
        <li id="R_7.096.004cd">jahi māṃ nirviśaṅkas tvaṃ dharmaṃ vardhaya rāghava</li>
    </ul>
    <ul id="R_7.096.005">
        <li id="R_7.096.005ab">lakṣmaṇena tathoktas tu rāmaḥ pracalitendriyaḥ</li>
        <li id="R_7.096.005cd">mantriṇaḥ samupānīya tathaiva ca purodhasaṃ</li>
    </ul>
    <ul id="R_7.096.006">
        <li id="R_7.096.006ab">abravīc ca yathāvṛttaṃ teṣāṃ madhye narādhipaḥ</li>
        <li id="R_7.096.006cd">durvāso'bhigamaṃ caiva pratijñāṃ tāpasasya ca</li>
    </ul>
    <ul id="R_7.096.007">
        <li id="R_7.096.007ab">tac chrutvā mantriṇaḥ sarve sopādhyāyāḥ samāsata</li>
        <li id="R_7.096.007cd">vasiṣṭhas tu mahātejā vākyam etad uvāca ha</li>
    </ul>
    <ul id="R_7.096.008">
        <li id="R_7.096.008ab">dṛṣṭam etan mahābāho kṣayaṃ te lomaharṣaṇam</li>
        <li id="R_7.096.008cd">lakṣmaṇena viyogaś ca tava rāma mahāyaśaḥ</li>
    </ul>
    <ul id="R_7.096.009">
        <li id="R_7.096.009ab">tyajainaṃ balavān kālo mā pratijñāṃ vṛthā kṛthāḥ</li>
        <li id="R_7.096.009cd">vinaṣṭāyāṃ pratijñāyāṃ dharmo hi vilayaṃ vrajet</li>
    </ul>
    <ul id="R_7.096.010">
        <li id="R_7.096.010ab">tato dharme vinaṣṭe tu trailokye sacarācaram</li>
        <li id="R_7.096.010cd">sadevarṣigaṇaṃ sarvaṃ vinaśyeta na saṃśayaḥ</li>
    </ul>
    <ul id="R_7.096.011">
        <li id="R_7.096.011ab">sa tvaṃ puruṣaśārdūla trailokyasyābhipālanam</li>
        <li id="R_7.096.011cd">lakṣmaṇasya vadhenādya jagat svasthaṃ kuruṣva ha</li>
    </ul>
    <ul id="R_7.096.012">
        <li id="R_7.096.012ab">teṣāṃ tat samavetānāṃ vākyaṃ dharmārthasaṃhitam</li>
        <li id="R_7.096.012cd">śrutvā pariṣado madhye rāmo lakṣmaṇam abravīt</li>
    </ul>
    <ul id="R_7.096.013">
        <li id="R_7.096.013ab">visarjaye tvāṃ saumitre mā bhūd dharmaviparyayaḥ</li>
        <li id="R_7.096.013cd">tyāgo vadho vā vihitaḥ sādhūnām ubhayaṃ samam</li>
    </ul>
    <ul id="R_7.096.014">
        <li id="R_7.096.014ab">rāmeṇa bhāṣite vākye bāṣpavyākulitekṣaṇaḥ</li>
        <li id="R_7.096.014cd">lakṣmaṇas tvaritaḥ prāyāt svagṛhaṃ na viveśa ha</li>
    </ul>
    <ul id="R_7.096.015">
        <li id="R_7.096.015ab">sa gatvā sarayūtīram upaspṛśya kṛtāñjaliḥ</li>
        <li id="R_7.096.015cd">nigṛhya sarvasrotāṃsi niḥśvāsaṃ na mumoca ha</li>
    </ul>
    <ul id="R_7.096.016">
        <li id="R_7.096.016ab">anucchvasantaṃ yuktaṃ taṃ saśakrāḥ sāpsarogaṇāḥ</li>
        <li id="R_7.096.016cd">devāḥ sarṣigaṇāḥ sarve puṣpair avakiraṃs tadā</li>
    </ul>
    <ul id="R_7.096.017">
        <li id="R_7.096.017ab">adṛśyaṃ sarvam anujaiḥ saśarīraṃ mahābalam</li>
        <li id="R_7.096.017cd">pragṛhya lakṣmaṇaṃ śakro divaṃ saṃpraviveśa ha</li>
    </ul>
    <ul id="R_7.096.018">
        <li id="R_7.096.018ab">tato viṣṇoś caturbhāgam āgataṃ surasattamāḥ</li>
        <li id="R_7.096.018cd">hṛṣṭāḥ pramuditāḥ sarve 'pūjayan ṛṣibhiḥ saha</li>
    </ul>
    <ul id="R_7.097.001">
        <li id="R_7.097.001ab">visṛjya lakṣmaṇaṃ rāmo duḥkhaśokasamanvitaḥ</li>
        <li id="R_7.097.001cd">purodhasaṃ mantriṇaś ca naigamāṃś cedam abravīt</li>
    </ul>
    <ul id="R_7.097.002">
        <li id="R_7.097.002ab">adya rājye 'bhiṣekṣyāmi bharataṃ dharmavatsalam</li>
        <li id="R_7.097.002cd">ayodhyāyāṃ patiṃ vīraṃ tato yāsyāmy ahaṃ vanam</li>
    </ul>
    <ul id="R_7.097.003">
        <li id="R_7.097.003ab">praveśayata saṃbhārān mā bhūt kālātyayo yathā</li>
        <li id="R_7.097.003cd">adyaivāhaṃ gamiṣyāmi lakṣmaṇena gatāṃ gatim</li>
    </ul>
    <ul id="R_7.097.004">
        <li id="R_7.097.004ab">tac chrutvā rāghaveṇoktaṃ sarvāḥ prakṛtayo bhṛśam</li>
        <li id="R_7.097.004cd">mūrdhabhiḥ praṇatā bhūmau gatasattvā ivābhavan</li>
    </ul>
    <ul id="R_7.097.005">
        <li id="R_7.097.005ab">bharataś ca visaṃjño 'bhūc chrutvā rāmasya bhāṣitam</li>
        <li id="R_7.097.005cd">rājyaṃ vigarhayām āsa rāghavaṃ cedam abravīt</li>
    </ul>
    <ul id="R_7.097.006">
        <li id="R_7.097.006ab">satyena hi śape rājan svargaloke na caiva hi</li>
        <li id="R_7.097.006cd">na kāmaye yathā rājyaṃ tvāṃ vinā raghunandana</li>
    </ul>
    <ul id="R_7.097.007">
        <li id="R_7.097.007ab">imau kuśīlavau rājann abhiṣiñca narādhipa</li>
        <li id="R_7.097.007cd">kosaleṣu kuśaṃ vīram uttareṣu tathā lavam</li>
    </ul>
    <ul id="R_7.097.008">
        <li id="R_7.097.008ab">śatrughnasya tu gacchantu dūtās tvaritavikramāḥ</li>
        <li id="R_7.097.008cd">idaṃ gamanam asmākaṃ svargāyākhyāntu māciram</li>
    </ul>
    <ul id="R_7.097.009">
        <li id="R_7.097.009ab">tac chrutvā bharatenoktaṃ dṛṣṭvā cāpi hy adho mukhān</li>
        <li id="R_7.097.009cd">paurān duḥkhena saṃtaptān vasiṣṭho vākyam abravīt</li>
    </ul>
    <ul id="R_7.097.010">
        <li id="R_7.097.010ab">vatsa rāma imāḥ paśya dharaṇīṃ prakṛtīr gatāḥ</li>
        <li id="R_7.097.010cd">jñātvaiṣām īpsitaṃ kāryaṃ mā caiṣāṃ vipriyaṃ kṛthāḥ</li>
    </ul>
    <ul id="R_7.097.011">
        <li id="R_7.097.011ab">vasiṣṭhasya tu vākyena utthāpya prakṛtījanam</li>
        <li id="R_7.097.011cd">kiṃ karomīti kākutsthaḥ sarvān vacanam abravīt</li>
    </ul>
    <ul id="R_7.097.012">
        <li id="R_7.097.012ab">tataḥ sarvāḥ prakṛtayo rāmaṃ vacanam abruvan</li>
        <li id="R_7.097.012cd">gacchantam anugacchāmo yato rāma gamiṣyasi</li>
    </ul>
    <ul id="R_7.097.013">
        <li id="R_7.097.013ab">eṣā naḥ paramā prītir eṣa dharmaḥ paro mataḥ</li>
        <li id="R_7.097.013cd">hṛdgatā naḥ sadā tuṣṭis tavānugamane dṛḍhā</li>
    </ul>
    <ul id="R_7.097.014">
        <li id="R_7.097.014ab">paureṣu yadi te prītir yadi sneho hy anuttamaḥ</li>
        <li id="R_7.097.014cd">saputradārāḥ kākutstha samaṃ gacchāma satpatham</li>
    </ul>
    <ul id="R_7.097.015">
        <li id="R_7.097.015ab">tapovanaṃ vā durgaṃ vā nadīm ambhonidhiṃ tathā</li>
        <li id="R_7.097.015cd">vayaṃ te yadi na tyājyāḥ sarvān no naya īśvara</li>
    </ul>
    <ul id="R_7.097.016">
        <li id="R_7.097.016ab">sa teṣāṃ niścayaṃ jñātvā kṛtāntaṃ ca nirīkṣyaca</li>
        <li id="R_7.097.016cd">paurāṇāṃ dṛḍhabhaktiṃ ca bāḍham ity eva so 'bravīt</li>
    </ul>
    <ul id="R_7.097.017">
        <li id="R_7.097.017ab">evaṃ viniścayaṃ kṛtvā tasminn ahani rāghavaḥ</li>
        <li id="R_7.097.017cd">kosaleṣu kuśaṃ vīram uttareṣu tathā lavam</li>
    </ul>
    <ul id="R_7.097.018">
        <li id="R_7.097.018ab">abhiṣiñcan mahātmānāv ubhāv eva kuśīlavau</li>
        <li id="R_7.097.018cd">rathānāṃ tu sahasrāṇi trīṇi nāgāyutāni ca</li>
    </ul>
    <ul id="R_7.097.019">
        <li id="R_7.097.019ab">daśa cāśvasahasrāṇi ekaikasya dhanaṃ dadau</li>
        <li id="R_7.097.019cd">bahuratnau bahudhanau hṛṣṭapuṣṭajanāvṛtau</li>
    </ul>
    <ul id="R_7.097.020">
        <li id="R_7.097.020ab">abhiṣicya tu tau vīrau prasthāpya svapure tathā</li>
        <li id="R_7.097.020cd">dūtān saṃpreṣayām āsa śatrughnāya mahātmane</li>
    </ul>
    <ul id="R_7.098.001">
        <li id="R_7.098.001ab">te dūtā rāmavākyena coditā laghuvikramāḥ</li>
        <li id="R_7.098.001cd">prajagmur madhurāṃ śīghraṃ cakrur vāsaṃ na cādhvani</li>
    </ul>
    <ul id="R_7.098.002">
        <li id="R_7.098.002ab">tatas tribhir aho rātraiḥ saṃprāpya madhurām atha</li>
        <li id="R_7.098.002cd">śatrughnāya yathāvṛttam ācakhyuḥ sarvam eva tat</li>
    </ul>
    <ul id="R_7.098.003">
        <li id="R_7.098.003ab">lakṣmaṇasya parityāgaṃ pratijñāṃ rāghavasya ca</li>
        <li id="R_7.098.003cd">putrayor abhiṣekaṃ ca paurānugamanaṃ tathā</li>
    </ul>
    <ul id="R_7.098.004">
        <li id="R_7.098.004ab">kuśasya nagarī ramyā vindhyaparvatarodhasi</li>
        <li id="R_7.098.004cd">kuśāvatīti nāmnā sā kṛtā rāmeṇa dhīmatā</li>
    </ul>
    <ul id="R_7.098.005">
        <li id="R_7.098.005ab">śrāvitā ca purī ramyā śrāvatīti lavasya ca</li>
        <li id="R_7.098.005cd">ayodhyāṃ vijanāṃ caiva bharataṃ rāghavānugam</li>
    </ul>
    <ul id="R_7.098.006">
        <li id="R_7.098.006ab">evaṃ sarvaṃ nivedyāśu śatrughnāya mahātmane</li>
        <li id="R_7.098.006cd">viremus te tato dūtās tvara rājann iti bruvan</li>
    </ul>
    <ul id="R_7.098.007">
        <li id="R_7.098.007ab">śrutvā taṃ ghorasaṃkāśaṃ kulakṣayam upasthitam</li>
        <li id="R_7.098.007cd">prakṛtīs tu samānīya kāñcanaṃ ca purohitam</li>
    </ul>
    <ul id="R_7.098.008">
        <li id="R_7.098.008ab">teṣāṃ sarvaṃ yathāvṛttam ākhyāya raghunandanaḥ</li>
        <li id="R_7.098.008cd">ātmanaś ca viparyāsaṃ bhaviṣyaṃ bhrātṛbhiḥ saha</li>
    </ul>
    <ul id="R_7.098.009">
        <li id="R_7.098.009ab">tataḥ putradvayaṃ vīraḥ so 'bhyaṣiñcan narādhipaḥ</li>
        <li id="R_7.098.009cd">subāhur madhurāṃ lebhe śatrughātī ca vaidiśam</li>
    </ul>
    <ul id="R_7.098.010">
        <li id="R_7.098.010ab">dvidhākṛtvā tu tāṃ senāṃ mādhurīṃ putrayor dvayoḥ</li>
        <li id="R_7.098.010cd">dhanadhānyasamāyuktau sthāpayām āsa pārthivau</li>
    </ul>
    <ul id="R_7.098.011">
        <li id="R_7.098.011ab">tato visṛjya rājānaṃ vaidiśe śatrughātinam</li>
        <li id="R_7.098.011cd">jagāma tvarito 'yodhyāṃ rathenaikena rāghavaḥ</li>
    </ul>
    <ul id="R_7.098.012">
        <li id="R_7.098.012ab">sa dadarśa mahātmānaṃ jvalantam iva pāvakam</li>
        <li id="R_7.098.012cd">kṣaumasūkṣmāmbaradharaṃ munibhiḥ sārdham akṣayaiḥ</li>
    </ul>
    <ul id="R_7.098.013">
        <li id="R_7.098.013ab">so 'bhivādya tato rāmaṃ prāñjaliḥ prayatendriyaḥ</li>
        <li id="R_7.098.013cd">uvāca vākyaṃ dharmajño dharmam evānucintayan</li>
    </ul>
    <ul id="R_7.098.014">
        <li id="R_7.098.014ab">kṛtvābhiṣekaṃ sutayor yuktaṃ rāghavayor dhanaiḥ</li>
        <li id="R_7.098.014cd">tavānugamane rājan viddhi māṃ kṛtaniścayam</li>
    </ul>
    <ul id="R_7.098.015">
        <li id="R_7.098.015ab">na cānyad atra vaktavyaṃ dustaraṃ tava śāsanam</li>
        <li id="R_7.098.015cd">tyaktuṃ nārhasi māṃ vīra bhaktimantaṃ viśeṣataḥ</li>
    </ul>
    <ul id="R_7.098.016">
        <li id="R_7.098.016ab">tasya tāṃ buddhim aklībāṃ vijñāya raghunandanaḥ</li>
        <li id="R_7.098.016cd">bāḍham ity eva śatrughnaṃ rāmo vacanam abravīt</li>
    </ul>
    <ul id="R_7.098.017">
        <li id="R_7.098.017ab">tasya vākyasya vākyānte vānarāḥ kāmarūpiṇaḥ</li>
        <li id="R_7.098.017cd">ṛkṣarākṣasasaṃghāś ca samāpetur anekaśaḥ</li>
    </ul>
    <ul id="R_7.098.018">
        <li id="R_7.098.018ab">devaputrā ṛṣisutā gandharvāṇāṃ sutās tathā</li>
        <li id="R_7.098.018cd">rāma kṣayaṃ viditvā te sarva eva samāgatāḥ</li>
    </ul>
    <ul id="R_7.098.019">
        <li id="R_7.098.019ab">te rāmam abhivādyāhuḥ sarva eva samāgatāḥ</li>
        <li id="R_7.098.019cd">tavānugamane rājan saṃprāptāḥ sma mahāyaśaḥ</li>
    </ul>
    <ul id="R_7.098.020">
        <li id="R_7.098.020ab">yadi rāma vināsmābhir gacches tvaṃ puruṣarṣabha</li>
        <li id="R_7.098.020cd">yamadaṇḍam ivodyamya tvayā sma vinipātitāḥ</li>
    </ul>
    <ul id="R_7.098.021">
        <li id="R_7.098.021ab">evaṃ teṣāṃ vacaḥ śrutvā ṛṣkavānararakṣasām</li>
        <li id="R_7.098.021cd">vibhīṣaṇam athovāca madhuraṃ ślakṣṇayā girā</li>
    </ul>
    <ul id="R_7.098.022">
        <li id="R_7.098.022ab">yāvat prajā dhariṣyanti tāvat tvaṃ vai vibhīṣaṇa</li>
        <li id="R_7.098.022cd">rākṣasendra mahāvīrya laṅkāsthaḥ svaṃ dhariṣyasi</li>
    </ul>
    <ul id="R_7.098.023">
        <li id="R_7.098.023ab">prajāḥ saṃrakṣa dharmeṇa nottaraṃ vaktum arhasi</li>
    </ul>
    <ul id="R_7.098.024">
        <li id="R_7.098.024ab">tam evam uktvā kākutstho hanūmantam athābravīt</li>
        <li id="R_7.098.024cd">jīvite kṛtabuddhis tvaṃ mā pratijñāṃ vilopaya</li>
    </ul>
    <ul id="R_7.098.025">
        <li id="R_7.098.025ab">matkathāḥ pracariṣyanti yāval loke harīśvara</li>
        <li id="R_7.098.025cd">tāvat tvaṃ dhārayan prāṇān pratijñām anupālaya</li>
    </ul>
    <ul id="R_7.098.026">
        <li id="R_7.098.026ab">tathaivam uktvā kākutsthaḥ sarvāṃs tān ṛkṣavānarān</li>
        <li id="R_7.098.026cd">mayā sārdhaṃ prayāteti tadā tān rāghavo 'bravīt</li>
    </ul>
    <ul id="R_7.099.001">
        <li id="R_7.099.001ab">prabhātāyāṃ tu śarvaryāṃ pṛthuvakṣā mahāyaśāḥ</li>
        <li id="R_7.099.001cd">rāmaḥ kamalapatrākṣaḥ purodhasam athābravīt</li>
    </ul>
    <ul id="R_7.099.002">
        <li id="R_7.099.002ab">agnihotraṃ vrajatv agre sarpir jvalitapāvakam</li>
        <li id="R_7.099.002cd">vājapeyātapatraṃ ca śobhayānaṃ mahāpatham</li>
    </ul>
    <ul id="R_7.099.003">
        <li id="R_7.099.003ab">tato vasiṣṭhas tejasvī sarvaṃ niravaśeṣataḥ</li>
        <li id="R_7.099.003cd">cakāra vidhivad dharmyaṃ mahāprāsthānikaṃ vidhim</li>
    </ul>
    <ul id="R_7.099.004">
        <li id="R_7.099.004ab">tataḥ kṣaumāmbaradharo brahma cāvartayan param</li>
        <li id="R_7.099.004cd">kuśān gṛhītvā pāṇibhyāṃ prasajya prayayāv atha</li>
    </ul>
    <ul id="R_7.099.005">
        <li id="R_7.099.005ab">avyāharan kva cit kiṃ cin niśceṣṭo niḥsukhaḥ pathi</li>
        <li id="R_7.099.005cd">nirjagāma gṛhāt tasmād dīpyamāno yathāṃśumān</li>
    </ul>
    <ul id="R_7.099.006">
        <li id="R_7.099.006ab">rāmasya pārśve savye tu padmā śrīḥ susamāhitā</li>
        <li id="R_7.099.006cd">dakṣiṇe hrīr viśālākṣī vyavasāyas tathāgrataḥ</li>
    </ul>
    <ul id="R_7.099.007">
        <li id="R_7.099.007ab">śarā nānāvidhāś cāpi dhanur āyatavigraham</li>
        <li id="R_7.099.007cd">anuvrajanti kākutsthaṃ sarve puruṣavigrahāḥ</li>
    </ul>
    <ul id="R_7.099.008">
        <li id="R_7.099.008ab">vedā brāhmaṇarūpeṇa sāvitrī sarvarakṣiṇī</li>
        <li id="R_7.099.008cd">oṃkāro 'tha vaṣaṭkāraḥ sarve rāmam anuvratāḥ</li>
    </ul>
    <ul id="R_7.099.009">
        <li id="R_7.099.009ab">ṛṣayaś ca mahātmānaḥ sarva eva mahīsurāḥ</li>
        <li id="R_7.099.009cd">anvagacchanta kākutsthaṃ svargadvāram upāgatam</li>
    </ul>
    <ul id="R_7.099.010">
        <li id="R_7.099.010ab">taṃ yāntam anuyānti sma antaḥpuracarāḥ striyaḥ</li>
        <li id="R_7.099.010cd">savṛddhabāladāsīkāḥ savarṣavarakiṃkarāḥ</li>
    </ul>
    <ul id="R_7.099.011">
        <li id="R_7.099.011ab">sāntaḥpuraś ca bharataḥ śatrughnasahito yayau</li>
        <li id="R_7.099.011cd">rāmavratam upāgamya rāghavaṃ samanuvratāḥ</li>
    </ul>
    <ul id="R_7.099.012">
        <li id="R_7.099.012ab">tato viprā mahātmānaḥ sāgnihotrāḥ samāhitāḥ</li>
        <li id="R_7.099.012cd">saputradārāḥ kākutstham anvagacchan mahāmatim</li>
    </ul>
    <ul id="R_7.099.013">
        <li id="R_7.099.013ab">mantriṇo bhṛtyavargāś ca saputrāḥ sahabāndhavāḥ</li>
        <li id="R_7.099.013cd">sānugā rāghavaṃ sarve anvagacchan prahṛṣṭavat</li>
    </ul>
    <ul id="R_7.099.014">
        <li id="R_7.099.014ab">tataḥ sarvāḥ prakṛtayo hṛṣṭapuṣṭajanāvṛtāḥ</li>
        <li id="R_7.099.014cd">anujagmuḥ pragacchantaṃ rāghavaṃ guṇarañjitāḥ</li>
    </ul>
    <ul id="R_7.099.015">
        <li id="R_7.099.015ab">snātaṃ pramuditaṃ sarvaṃ hṛṣṭapuṣpam anuttamam</li>
        <li id="R_7.099.015cd">dṛptaṃ kilikilāśabdaiḥ sarvaṃ rāmam anuvratam</li>
    </ul>
    <ul id="R_7.099.016">
        <li id="R_7.099.016ab">na tatra kaś cid dīno 'bhūd vrīḍito vāpi duḥkhitaḥ</li>
        <li id="R_7.099.016cd">hṛṣṭaṃ pramuditaṃ sarvaṃ babhūva paramādbhutam</li>
    </ul>
    <ul id="R_7.099.017">
        <li id="R_7.099.017ab">draṣṭukāmo 'tha niryāṇaṃ rājño jānapado janaḥ</li>
        <li id="R_7.099.017cd">saṃprāptaḥ so 'pi dṛṣṭvaiva saha sarvair anuvrataḥ</li>
    </ul>
    <ul id="R_7.099.018">
        <li id="R_7.099.018ab">ṛkṣavānararakṣāṃsi janāś ca puravāsinaḥ</li>
        <li id="R_7.099.018cd">agachan parayā bhaktyā pṛṣṭhataḥ susamāhitāḥ</li>
    </ul>
    <ul id="R_7.100.001">
        <li id="R_7.100.001ab">adhyardhayojanaṃ gatvā nadīṃ paścān mukhāśritām</li>
        <li id="R_7.100.001cd">sarayūṃ puṇyasalilāṃ dadarśa raghunandanaḥ</li>
    </ul>
    <ul id="R_7.100.002">
        <li id="R_7.100.002ab">atha tasmin muhūrte tu brahmā lokapitāmahaḥ</li>
        <li id="R_7.100.002cd">sarvaiḥ parivṛto devair ṛṣibhiś ca mahātmabhiḥ</li>
    </ul>
    <ul id="R_7.100.003">
        <li id="R_7.100.003ab">āyayau yatra kākutsthaḥ svargāya samupasthitaḥ</li>
        <li id="R_7.100.003cd">vimānaśatakoṭībhir divyābhir abhisaṃvṛtaḥ</li>
    </ul>
    <ul id="R_7.100.004">
        <li id="R_7.100.004ab">papāta puṣpavṛṣṭiś ca vāyumuktā mahaughavat</li>
    </ul>
    <ul id="R_7.100.005">
        <li id="R_7.100.005ab">tasmiṃs tūryaśatākīrṇe gandharvāpsarasaṃkule</li>
        <li id="R_7.100.005cd">sarayūsalilaṃ rāmaḥ padbhyāṃ samupacakrame</li>
    </ul>
    <ul id="R_7.100.006">
        <li id="R_7.100.006ab">tataḥ pitāmaho vāṇīm antarikṣād abhāṣata</li>
        <li id="R_7.100.006cd">āgaccha viṣṇo bhadraṃ te diṣṭyā prāpto 'si rāghava</li>
    </ul>
    <ul id="R_7.100.007">
        <li id="R_7.100.007ab">bhrātṛbhiḥ saha devābhaiḥ praviśasva svakāṃ tanum</li>
        <li id="R_7.100.007cd">vaiṣṇavīṃ tāṃ mahātejas tad ākāśaṃ sanātanam</li>
    </ul>
    <ul id="R_7.100.008">
        <li id="R_7.100.008ab">tvaṃ hi lokagatir deva na tvāṃ ke cit prajānate</li>
        <li id="R_7.100.008cd">ṛte māyāṃ viśālākṣa tava pūrvaparigrahām</li>
    </ul>
    <ul id="R_7.100.009">
        <li id="R_7.100.009ab">tvam acintyaṃ mahad bhūtam akṣayaṃ sarvasaṃgraham</li>
        <li id="R_7.100.009cd">yām icchasi mahātejas tāṃ tanuṃ praviśa svayam</li>
    </ul>
    <ul id="R_7.100.010">
        <li id="R_7.100.010ab">pitāmahavacaḥ śrutvā viniścitya mahāmatiḥ</li>
        <li id="R_7.100.010cd">viveśa vaiṣṇavaṃ tejaḥ saśarīraḥ sahānujaḥ</li>
    </ul>
    <ul id="R_7.100.011">
        <li id="R_7.100.011ab">tato viṣṇugataṃ devaṃ pūjayanti sma devatāḥ</li>
        <li id="R_7.100.011cd">sādhyā marudgaṇāś caiva sendrāḥ sāgnipurogamāḥ</li>
    </ul>
    <ul id="R_7.100.012">
        <li id="R_7.100.012ab">ye ca divyā ṛṣigaṇā gandharvāpsarasaś ca yāḥ</li>
        <li id="R_7.100.012cd">suparṇanāgayakṣāś ca daityadānavarākṣasāḥ</li>
    </ul>
    <ul id="R_7.100.013">
        <li id="R_7.100.013ab">sarvaṃ hṛṣṭaṃ pramuditaṃ sarvaṃ pūrṇamanoratham</li>
        <li id="R_7.100.013cd">sādhu sādhv iti tat sarvaṃ tridivaṃ gatakalmaṣam</li>
    </ul>
    <ul id="R_7.100.014">
        <li id="R_7.100.014ab">atha viṣṇur mahātejāḥ pitāmaham uvāca ha</li>
        <li id="R_7.100.014cd">eṣāṃ lokāñ janaughānāṃ dātum arhasi suvrata</li>
    </ul>
    <ul id="R_7.100.015">
        <li id="R_7.100.015ab">ime hi sarve snehān mām anuyātā manasvinaḥ</li>
        <li id="R_7.100.015cd">bhaktā bhājayitavyāś ca tyaktātmānaś ca matkṛte</li>
    </ul>
    <ul id="R_7.100.016">
        <li id="R_7.100.016ab">tac chrutvā viṣṇuvacanaṃ brahmā lokaguruḥ prabhuḥ</li>
        <li id="R_7.100.016cd">lokān sāntānikān nāma yāsyantīme samāgatāḥ</li>
    </ul>
    <ul id="R_7.100.017">
        <li id="R_7.100.017ab">yac ca tiryaggataṃ kiṃ cid rāmam evānucintayat</li>
        <li id="R_7.100.017cd">prāṇāṃs tyakṣyati bhaktyā vai saṃtāne tu nivatsyati</li>
        <li id="R_7.100.017ef">sarvair eva guṇair yukte brahmalokād anantare</li>
    </ul>
    <ul id="R_7.100.018">
        <li id="R_7.100.018ab">vānarāś ca svakāṃ yonim ṛkṣāś caiva tathā yayuḥ</li>
    </ul>
    <ul id="R_7.100.019">
        <li id="R_7.100.019ab">yebhyo viniḥsṛtā ye ye surādibhyaḥ susaṃbhavāḥ</li>
        <li id="R_7.100.019cd">ṛṣibhyo nāgayakṣebhyas tāṃs tān eva prapedire</li>
    </ul>
    <ul id="R_7.100.020">
        <li id="R_7.100.020ab">tathoktavati deveśe gopratāram upāgatāḥ</li>
        <li id="R_7.100.020cd">bhejire sarayūṃ sarve harṣapūrṇāśruviklavāḥ</li>
    </ul>
    <ul id="R_7.100.021">
        <li id="R_7.100.021ab">avagāhya jalaṃ yo yaḥ prāṇī hy āsīt prahṛṣṭavat</li>
        <li id="R_7.100.021cd">mānuṣaṃ deham utsṛjya vimānaṃ so 'dhyarohata</li>
    </ul>
    <ul id="R_7.100.022">
        <li id="R_7.100.022ab">tiryagyonigatāś cāpi saṃprāptāḥ sarayūjalam</li>
        <li id="R_7.100.022cd">divyā divyena vapuṣā devā dīptā ivābhavan</li>
    </ul>
    <ul id="R_7.100.023">
        <li id="R_7.100.023ab">gatvā tu sarayūtoyaṃ sthāvarāṇi carāṇi ca</li>
        <li id="R_7.100.023cd">prāpya tat toyavikledaṃ devalokam upāgaman</li>
    </ul>
    <ul id="R_7.100.024">
        <li id="R_7.100.024ab">devānāṃ yasya yā yonir vānarā ṛṣkarākṣasāḥ</li>
        <li id="R_7.100.024cd">tām eva viviśuḥ sarve dehān nikṣipya cāmbhasi</li>
    </ul>
    <ul id="R_7.100.025">
        <li id="R_7.100.025ab">tathā svargagataṃ sarvaṃ kṛtvā lokagurur divam</li>
        <li id="R_7.100.025cd">jagāma tridaśaiḥ sārdhaṃ hṛṣṭair hṛṣṭo mahāmatiḥ</li>
    </ul>
    <ul id="R_7.100.026">
        <li id="R_7.100.026ab">etāvad eva ākhyānaṃ sottaraṃ brahmapūjitam</li>
        <li id="R_7.100.026cd">rāmāyaṇam iti khyātaṃ mukhyaṃ vālmīkinā kṛtam</li>
    </ul>
	{/if}
</div>

